INSERT INTO words (word, word_type, description)VALUES  (    'Magnificoes',    'pl. ',    'of Magnifico');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magnifico', 'n.', 'A grandee or nobleman of Venice; -- so called in\n   courtesy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magnifico', 'n.', 'A rector of a German university.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magnifier', 'n.', 'One who, or that which, magnifies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magnified', 'imp. & p. p.', 'of Magnify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magnifying', 'p. pr. & vb. n.', 'of Magnify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magnify', 'v. t.', 'To make great, or greater; to increase the dimensions\n   of; to amplify; to enlarge, either in fact or in appearance; as, the\n   microscope magnifies the object by a thousand diameters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magnify', 'v. t.', 'To increase the importance of; to augment the esteem or\n   respect in which one is held.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magnify', 'v. t.', 'To praise highly; to land; to extol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magnify', 'v. t.', 'To exaggerate; as, to magnify a loss or a difficulty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magnify', 'v. i.', 'To have the power of causing objects to appear larger\n   than they really are; to increase the apparent dimensions of objects;\n   as, some lenses magnify but little.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magnify', 'v. i.', 'To have effect; to be of importance or significance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magniloquence', 'n.', 'The quality of being magniloquent; pompous\n   discourse; grandiloquence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magniloquent', 'a.', 'Speaking pompously; using swelling discourse;\n   bombastic; tumid in style; grandiloquent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magniloquous', 'a.', 'Magniloquent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magnitude', 'n.', 'Extent of dimensions; size; -- applied to things that\n   have length, breath, and thickness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magnitude', 'n.', 'That which has one or more of the three dimensions,\n   length, breadth, and thickness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magnitude', 'n.', 'Anything of which greater or less can be predicated, as\n   time, weight, force, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magnitude', 'n.', 'Greatness; grandeur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magnitude', 'n.', 'Greatness, in reference to influence or effect;\n   importance; as, an affair of magnitude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magnolia', 'n.', 'A genus of American and Asiatic trees, with aromatic bark\n   and large sweet-scented whitish or reddish flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magnoliaceous', 'a.', 'Pertaining to a natural order (Magnoliaceae) of\n   trees of which the magnolia, the tulip tree, and the star anise are\n   examples.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magnum', 'n.', 'A large wine bottle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magnum', 'n.', 'A bone of the carpus at the base of the third metacarpal\n   bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magot', 'n.', 'The Barbary ape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magot-pie', 'n.', 'A magpie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magpie', 'n.', 'Any one of numerous species of the genus Pica and related\n   genera, allied to the jays, but having a long graduated tail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maguari', 'n.', 'A South American stork (Euxenara maguari), having a forked\n   tail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maguey', 'n.', 'The century plant, a species of Agave (A. Americana). See\n   Agave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magyar', 'n.', 'One of the dominant people of Hungary, allied to the Finns;\n   a Hungarian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magyar', 'n.', 'The language of the Magyars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maha', 'n.', 'A kind of baboon; the wanderoo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mahabarata', 'n.', 'Alt. of Mahabharatam');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mahabharatam', 'n.', 'A celebrated epic poem of the Hindoos. It is of great\n   length, and is chiefly devoted to the history of a civil war between\n   two dynasties of ancient India.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mahaled', 'n.', 'A cherry tree (Prunus Mahaleb) of Southern Europe. The\n   wood is prized by cabinetmakers, the twigs are used for pipe stems, the\n   flowers and leaves yield a perfume, and from the fruit a violet dye and\n   a fermented liquor (like kirschwasser) are prepared.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maharajah', 'n.', 'A sovereign prince in India; -- a title given also to\n   other persons of high rank.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maharif', 'n.', 'An African antelope (Hippotragus Bakeri). Its face is\n   striped with black and white.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maharmah', 'n.', 'A muslin wrapper for the head and the lower part of the\n   face, worn by Turkish and Armenian women when they go abroad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mahdi', 'n.', 'Among Mohammedans, the last imam or leader of the faithful.\n   The Sunni, the largest sect of the Mohammedans, believe that he is yet\n   to appear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mahl-stick', 'n.', 'See Maul-stick.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mahoe', 'n.', 'A name given to several malvaceous trees (species of\n   Hibiscus, Ochroma, etc.), and to their strong fibrous inner bark, which\n   is used for strings and cordage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mahogany', 'n.', 'A large tree of the genus Swietenia (S. Mahogoni), found\n   in tropical America.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mahogany', 'n.', 'The wood of the Swietenia Mahogoni. It is of a reddish\n   brown color, beautifully veined, very hard, and susceptible of a fine\n   polish. It is used in the manufacture of furniture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mahogany', 'n.', 'A table made of mahogany wood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maholi', 'n.', 'A South African lemur (Galago maholi), having very large\n   ears.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mahomedan', 'n.', 'Alt. of Mahometan');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mahometan', 'n.', 'See Mohammedan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mahometanism', 'n.', 'See Mohammedanism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mahometanized', 'imp. & p. p.', 'of Mahometanize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mahometanizing', 'p. pr. & vb. n.', 'of Mahometanize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mahometanize', 'v. t.', 'To convert to the religion of Mohammed; to\n   Mohammedanize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mahometism', 'n.', 'See Mohammedanism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mahometist', 'n.', 'A Mohammedan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mahometry', 'n.', 'Mohammedanism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mahone', 'n.', 'A large Turkish ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mahonia', 'n.', 'The Oregon grape, a species of barberry (Berberis\n   Aquifolium), often cultivated for its hollylike foliage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mahon stock', '', 'An annual cruciferous plant with reddish purple or white\n   flowers (Malcolmia maritima). It is called in England Virginia stock,\n   but the plant comes from the Mediterranean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mahoohoo', 'n.', 'The African white two-horned rhinoceros (Atelodus simus).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mahori', 'n.', 'One of the dark race inhabiting principally the islands of\n   Eastern Polynesia. Also used adjectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mahound', 'n.', 'A contemptuous name for Mohammed; hence, an evil spirit; a\n   devil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mahout', 'n.', 'The keeper and driver of an elephant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mahovo', 'n.', 'A device for saving power in stopping and starting a\n   railroad car, by means of a heavy fly wheel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mahrati', 'n.', 'The language of the Mahrattas; the language spoken in the\n   Deccan and Concan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mahratta', 'n.', 'One of a numerous people inhabiting the southwestern part\n   of India. Also, the language of the Mahrattas; Mahrati. It is closely\n   allied to Sanskrit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mahratta', 'a.', 'Of or pertaining to the Mahrattas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mahumetan', 'n.', 'Alt. of Mahumetanism');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mahumetanism', 'n.', 'See Mohammedan, Mohammedanism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mahwa tree', '', 'An East Indian sapotaceous tree (Bassia latifolia, and\n   also B. butyracea), whose timber is used for wagon wheels, and the\n   flowers for food and in preparing an intoxicating drink. It is one of\n   the butter trees. The oil, known as mahwa and yallah, is obtained from\n   the kernels of the fruit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maia', 'n.', 'A genus of spider crabs, including the common European\n   species (Maia squinado).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maia', 'n.', 'A beautiful American bombycid moth (Eucronia maia).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maian', 'n.', 'Any spider crab of the genus Maia, or family Maiadae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maid', 'n.', 'An unmarried woman; usually, a young unmarried woman; esp., a\n   girl; a virgin; a maiden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maid', 'n.', 'A man who has not had sexual intercourse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maid', 'n.', 'A female servant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maid', 'n.', 'The female of a ray or skate, esp. of the gray skate (Raia\n   batis), and of the thornback (R. clavata).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maiden', 'n.', 'An unmarried woman; a girl or woman who has not experienced\n   sexual intercourse; a virgin; a maid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maiden', 'n.', 'A female servant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maiden', 'n.', 'An instrument resembling the guillotine, formerly used in\n   Scotland for beheading criminals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maiden', 'n.', 'A machine for washing linen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maiden', 'a.', 'Of or pertaining to a maiden, or to maidens; suitable to,\n   or characteristic of, a virgin; as, maiden innocence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maiden', 'a.', 'Never having been married; not having had sexual\n   intercourse; virgin; -- said usually of the woman, but sometimes of the\n   man; as, a maiden aunt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maiden', 'a.', 'Fresh; innocent; unpolluted; pure; hitherto unused.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maiden', 'a.', 'Used of a fortress, signifying that it has never been\n   captured, or violated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maiden', 'v. t.', 'To act coyly like a maiden; -- with it as an indefinite\n   object.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maidenhair', 'n.', 'A fern of the genus Adiantum (A. pedatum), having very\n   slender graceful stalks. It is common in the United States, and is\n   sometimes used in medicine. The name is also applied to other species\n   of the same genus, as to the Venus-hair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maidenhead', 'n.', 'The state of being a maiden; maidenhood; virginity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maidenhead', 'n.', 'The state of being unused or uncontaminated; freshness;\n   purity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maidenhead', 'n.', 'The hymen, or virginal membrane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maidenhood', 'n.', 'The state of being a maid or a virgin; virginity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maidenhood', 'n.', 'Newness; freshness; uncontaminated state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maidenlike', 'a.', 'Like a maiden; modest; coy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maidenliness', 'n.', 'The quality of being maidenly; the behavior that\n   becomes a maid; modesty; gentleness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maidenly', 'a.', 'Like a maid; suiting a maid; maiden-like; gentle, modest,\n   reserved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maidenly', 'adv.', 'In a maidenlike manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maidenship', 'n.', 'Maidenhood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maidhood', 'n.', 'Maidenhood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maidmarian', 'n.', 'The lady of the May games; one of the characters in a\n   morris dance; a May queen. Afterward, a grotesque character personated\n   in sports and buffoonery by a man in womans clothes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maidmarian', 'n.', 'A kind of dance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maidpale', 'a.', 'Pale, like a sick girl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maidservant', 'n.', 'A female servant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maids hair', '', 'The yellow bedstraw (Galium verum).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maieutic', 'a.', 'Alt. of Maieutical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maieutical', 'a.', 'Serving to assist childbirth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maieutical', 'a.', 'Fig. : Aiding, or tending to, the definition and\n   interpretation of thoughts or language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maieutics', 'n.', 'The art of giving birth (i. e., clearness and\n   conviction) to ideas, which are conceived as struggling for birth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maiger', 'n.', 'The meagre.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maigre', 'a.', 'Belonging to a fast day or fast; as, a maigre day.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maihem', 'n.', 'See Maim, and Mayhem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maikel', 'n.', 'A South American carnivore of the genus Conepatus, allied\n   to the skunk, but larger, and having a longer snout. The tail is not\n   bushy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maikong', 'n.', 'A South American wild dog (Canis cancrivorus); the\n   crab-eating dog.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mail', 'n.', 'A spot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mail', 'n.', 'A small piece of money; especially, an English silver\n   half-penny of the time of Henry V.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mail', 'n.', 'Rent; tribute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mail', 'n.', 'A flexible fabric made of metal rings interlinked. It was\n   used especially for defensive armor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mail', 'n.', 'Hence generally, armor, or any defensive covering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mail', 'n.', 'A contrivance of interlinked rings, for rubbing off the loose\n   hemp on lines and white cordage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mail', 'n.', 'Any hard protective covering of an animal, as the scales and\n   plates of reptiles, shell of a lobster, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mail', 'v. t.', 'To arm with mail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mail', 'v. t.', 'To pinion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mail', 'n.', 'A bag; a wallet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mail', 'n.', 'The bag or bags with the letters, papers, papers, or other\n   matter contained therein, conveyed under public authority from one post\n   office to another; the whole system of appliances used by government in\n   the conveyance and delivery of mail matter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mail', 'n.', 'That which comes in the mail; letters, etc., received through\n   the post office.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mail', 'n.', 'A trunk, box, or bag, in which clothing, etc., may be\n   carried.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mailed', 'imp. & p. p.', 'of Mail');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mailing', 'p. pr. & vb. n.', 'of Mail');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mail', 'v. t.', 'To deliver into the custody of the postoffice officials,\n   or place in a government letter box, for transmission by mail; to post;\n   as, to mail a letter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mailable', 'a.', 'Admissible lawfully into the mail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mailclad', 'a.', 'Protected by a coat of mail; clad in armor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mailed', 'a.', 'Protected by an external coat, or covering, of scales or\n   plates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mailed', 'a.', 'Spotted; speckled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mailing', 'n.', 'A farm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mail-shell', 'n.', 'A chiton.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maimed', 'imp. & p. p.', 'of Maim');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maiming', 'p. pr. & vb. n.', 'of Maim');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maim', 'v. t.', 'To deprive of the use of a limb, so as to render a person\n   on fighting less able either to defend himself or to annoy his\n   adversary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maim', 'v. t.', 'To mutilate; to cripple; to injure; to disable; to impair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maim', 'v.', 'The privation of the use of a limb or member of the body, by\n   which one is rendered less able to defend himself or to annoy his\n   adversary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maim', 'v.', 'The privation of any necessary part; a crippling; mutilation;\n   injury; deprivation of something essential. See Mayhem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maimedly', 'adv.', 'In a maimed manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maimedness', 'n.', 'State of being maimed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Main', 'n.', 'A hand or match at dice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Main', 'n.', 'A stake played for at dice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Main', 'n.', 'The largest throw in a match at dice; a throw at dice within\n   given limits, as in the game of hazard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Main', 'n.', 'A match at cockfighting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Main', 'n.', 'A main-hamper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Main', 'v.', 'Strength; force; might; violent effort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Main', 'v.', 'The chief or principal part; the main or most important\n   thing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Main', 'v.', 'The great sea, as distinguished from an arm, bay, etc. ; the\n   high sea; the ocean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Main', 'v.', 'The continent, as distinguished from an island; the mainland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Main', 'v.', 'principal duct or pipe, as distinguished from lesser ones;\n   esp. (Engin.), a principal pipe leading to or from a reservoir; as, a\n   fire main.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Main', 'a.', 'Very or extremely strong.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Main', 'a.', 'Vast; huge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Main', 'a.', 'Unqualified; absolute; entire; sheer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Main', 'a.', 'Principal; chief; first in size, rank, importance, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Main', 'a.', 'Important; necessary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Main', 'a.', 'Very; extremely; as, main heavy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maine', 'n.', 'One of the New England States.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Main-gauche', 'n.', 'The dagger held in the left hand, while the rapier is\n   held in the right; -- used to parry thrusts of the adversarys rapier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Main-hamper', 'n.', 'A hamper to be carried in the hand; a hand basket used\n   in carrying grapes to the press.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mainland', 'n.', 'The continent; the principal land; -- opposed to island,\n   or peninsula.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mainly', 'adv.', 'Very strongly; mightily; to a great degree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mainly', 'adv.', 'Principally; chiefly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mainmast', 'n.', 'The principal mast in a ship or other vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mainor', 'n.', 'A thing stolen found on the person of the thief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mainpernable', 'a.', 'Capable of being admitted to give surety by\n   mainpernors; able to be mainprised.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mainpernor', 'n.', 'A surety, under the old writ of mainprise, for a\n   prisoners appearance in court at a day.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mainpin', 'n.', 'A kingbolt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mainprise', 'n.', 'A writ directed to the sheriff, commanding him to take\n   sureties, called mainpernors, for the prisoners appearance, and to let\n   him go at large. This writ is now obsolete.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mainprise', 'n.', 'Deliverance of a prisoner on security for his appearance\n   at a day.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mainprised', 'imp. & p. p.', 'of Mainprise');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mainprising', 'p. pr. & vb. n.', 'of Mainprise');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mainprise', 'v. t.', 'To suffer to go at large, on his finding sureties, or\n   mainpernors, for his appearance at a day; -- said of a prisoner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mains', 'n.', 'The farm attached to a mansion house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mainsail', 'n.', 'The principal sail in a ship or other vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mainsheet', 'n.', 'One of the ropes by which the mainsail is hauled aft and\n   trimmed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mainspring', 'n.', 'The principal or most important spring in a piece of\n   mechanism, especially the moving spring of a watch or clock or the\n   spring in a gunlock which impels the hammer. Hence: The chief or most\n   powerful motive; the efficient cause of action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mainstay', 'n.', 'The stay extending from the foot of the foremast to the\n   maintop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mainstay', 'n.', 'Main support; principal dependence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mainswear', 'v. i.', 'To swear falsely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maintained', 'imp. & p. p.', 'of Maintain');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maintaining', 'p. pr. & vb. n.', 'of Maintain');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maintain', 'v. t.', 'To hold or keep in any particular state or condition;\n   to support; to sustain; to uphold; to keep up; not to suffer to fail or\n   decline; as, to maintain a certain degree of heat in a furnace; to\n   maintain a fence or a railroad; to maintain the digestive process or\n   powers of the stomach; to maintain the fertility of soil; to maintain\n   present reputation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maintain', 'v. t.', 'To keep possession of; to hold and defend; not to\n   surrender or relinquish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maintain', 'v. t.', 'To continue; not to suffer to cease or fail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maintain', 'v. t.', 'To bear the expense of; to support; to keep up; to\n   supply with what is needed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maintain', 'v. t.', 'To affirm; to support or defend by argument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maintainable', 'a.', 'That maybe maintained.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maintainer', 'n.', 'One who maintains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maintainor', 'n.', 'One who, not being interested, maintains a cause\n   depending between others, by furnishing money, etc., to either party.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maintenance', 'n.', 'The act of maintaining; sustenance; support; defense;\n   vindication.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maintenance', 'n.', 'That which maintains or supports; means of sustenance;\n   supply of necessaries and conveniences.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maintenance', 'n.', 'An officious or unlawful intermeddling in a cause\n   depending between others, by assisting either party with money or means\n   to carry it on. See Champerty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maintop', 'n.', 'The platform about the head of the mainmast in\n   square-rigged vessels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Main yard', '', 'The yard on which the mainsail is extended, supported by\n   the mainmast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maioid', 'a.', 'Of or pertaining to the genus Maia, or family Maiadeae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maister', 'n.', 'Master.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maister', 'a.', 'Principal; chief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maistre', 'n.', 'Alt. of Maistry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maistrie', 'n.', 'Alt. of Maistry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maistry', 'n.', 'Mastery; superiority; art. See Mastery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maistress', 'n.', 'Mistress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maithes', 'n.', 'Same as Maghet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maize', 'n.', 'A large species of American grass of the genus Zea (Z.\n   Mays), widely cultivated as a forage and food plant; Indian corn. Also,\n   its seed, growing on cobs, and used as food for men animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Majestatic', 'a.', 'Alt. of Majestatal');
  INSERT INTO words (word, word_type, description) VALUES  (    'Majestatal', 'a.', 'Majestic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Majestic', 'a.', 'Possessing or exhibiting majesty; of august dignity,\n   stateliness, or imposing grandeur; lofty; noble; grand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Majestical', 'a.', 'Majestic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Majesticness', 'n.', 'The quality or state of being majestic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Majesties', 'pl. ', 'of Majesty');
  INSERT INTO words (word, word_type, description) VALUES  (    'Majesty', 'n.', 'The dignity and authority of sovereign power; quality or\n   state which inspires awe or reverence; grandeur; exalted dignity,\n   whether proceeding from rank, character, or bearing; imposing\n   loftiness; stateliness; -- usually applied to the rank and dignity of\n   sovereigns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Majesty', 'n.', 'Hence, used with the possessive pronoun, the title of an\n   emperor, king or queen; -- in this sense taking a plural; as, their\n   majesties attended the concert.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Majesty', 'n.', 'Dignity; elevation of manner or style.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Majolica', 'n.', 'A kind of pottery, with opaque glazing and showy, which\n   reached its greatest perfection in Italy in the 16th century.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Major', 'a.', 'Greater in number, quantity, or extent; as, the major part\n   of the assembly; the major part of the revenue; the major part of the\n   territory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Major', 'a.', 'Of greater dignity; more important.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Major', 'a.', 'Of full legal age.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Major', 'a.', 'Greater by a semitone, either in interval or in difference\n   of pitch from another tone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Major', 'a.', 'An officer next in rank above a captain and next below a\n   lieutenant colonel; the lowest field officer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Major', 'a.', 'A person of full age.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Major', 'a.', 'That premise which contains the major term. It its the first\n   proposition of a regular syllogism; as: No unholy person is qualified\n   for happiness in heaven [the major]. Every man in his natural state is\n   unholy [minor]. Therefore, no man in his natural state is qualified for\n   happiness in heaven [conclusion or inference].');
  INSERT INTO words (word, word_type, description) VALUES  (    'Major', 'a.', 'A mayor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Majorat', 'a.', 'The right of succession to property according to age; --\n   so termed in some of the countries of continental Europe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Majorat', 'a.', 'Property, landed or funded, so attached to a title of\n   honor as to descend with it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Majorate', 'n.', 'The office or rank of a major.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Majorate', 'a.', 'To augment; to increase.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Majoration', 'n.', 'Increase; enlargement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Majorcan', 'a.', 'Of or pertaining to Majorca.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Majorcan', 'n.', 'A native or inhabitant of Majorca.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Major-domo', 'n.', 'A man who has authority to act, within certain limits,\n   as master of the house; a steward; also, a chief minister or officer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Major general', '', 'An officer of the army holding a rank next above that\n   of brigadier general and next below that of lieutenant general, and who\n   usually commands a division or a corps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Majorities', 'pl. ', 'of Majority');
  INSERT INTO words (word, word_type, description) VALUES  (    'Majority', 'n.', 'The quality or condition of being major or greater;\n   superiority.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Majority', 'n.', 'The military rank of a major.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Majority', 'n.', 'The condition of being of full age, or authorized by law\n   to manage ones own affairs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Majority', 'n.', 'The greater number; more than half; as, a majority of\n   mankind; a majority of the votes cast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Majority', 'n.', 'Ancestors; ancestry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Majority', 'n.', 'The amount or number by which one aggregate exceeds all\n   other aggregates with which it is contrasted; especially, the number by\n   which the votes for a successful candidate exceed those for all other\n   candidates; as, he is elected by a majority of five hundred votes. See\n   Plurality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Majorship', 'n.', 'The office of major.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Majoun', 'n.', 'See Madjoun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Majusculae', 'n. pl.', 'Capital letters, as found in manuscripts of the\n   sixth century and earlier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Majuscule', 'n.', 'A capital letter; especially, one used in ancient\n   manuscripts. See Majusculae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Makable', 'a.', 'Capable of being made.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Makaron', 'n.', 'See Macaroon, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Make', 'n.', 'A companion; a mate; often, a husband or a wife.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Made', 'imp. & p. p.', 'of Make');
  INSERT INTO words (word, word_type, description) VALUES  (    'Making', 'p. pr. & vb. n.', 'of Make');
  INSERT INTO words (word, word_type, description) VALUES  (    'Make', 'v. t.', 'To cause to exist; to bring into being; to form; to\n   produce; to frame; to fashion; to create.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Make', 'v. t.', 'To form of materials; to cause to exist in a certain form;\n   to construct; to fabricate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Make', 'v. t.', 'To produce, as something artificial, unnatural, or false;\n   -- often with up; as, to make up a story.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Make', 'v. t.', 'To bring about; to bring forward; to be the cause or agent\n   of; to effect, do, perform, or execute; -- often used with a noun to\n   form a phrase equivalent to the simple verb that corresponds to such\n   noun; as, to make complaint, for to complain; to make record of, for to\n   record; to make abode, for to abide, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Make', 'v. t.', 'To execute with the requisite formalities; as, to make a\n   bill, note, will, deed, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Make', 'v. t.', 'To gain, as the result of ones efforts; to get, as\n   profit; to make acquisition of; to have accrue or happen to one; as, to\n   make a large profit; to make an error; to make a loss; to make money.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Make', 'v. t.', 'To find, as the result of calculation or computation; to\n   ascertain by enumeration; to find the number or amount of, by\n   reckoning, weighing, measurement, and the like; as, he made the\n   distance of; to travel over; as, the ship makes ten knots an hour; he\n   made the distance in one day.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Make', 'v. t.', 'To put a desired or desirable condition; to cause to\n   thrive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Make', 'v. t.', 'To cause to be or become; to put into a given state verb,\n   or adjective; to constitute; as, to make known; to make public; to make\n   fast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Make', 'v. t.', 'To cause to appear to be; to constitute subjectively; to\n   esteem, suppose, or represent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Make', 'v. t.', 'To require; to constrain; to compel; to force; to cause;\n   to occasion; -- followed by a noun or pronoun and infinitive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Make', 'v. t.', 'To become; to be, or to be capable of being, changed or\n   fashioned into; to do the part or office of; to furnish the material\n   for; as, he will make a good musician; sweet cider makes sour vinegar;\n   wool makes warm clothing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Make', 'v. t.', 'To compose, as parts, ingredients, or materials; to\n   constitute; to form; to amount to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Make', 'v. t.', 'To be engaged or concerned in.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Make', 'v. t.', 'To reach; to attain; to arrive at or in sight of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Make', 'v. i.', 'To act in a certain manner; to have to do; to manage; to\n   interfere; to be active; -- often in the phrase to meddle or make.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Make', 'v. i.', 'To proceed; to tend; to move; to go; as, he made toward\n   home; the tiger made at the sportsmen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Make', 'v. i.', 'To tend; to contribute; to have effect; -- with for or\n   against; as, it makes for his advantage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Make', 'v. i.', 'To increase; to augment; to accrue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Make', 'v. i.', 'To compose verses; to write poetry; to versify.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Make', 'n.', 'Structure, texture, constitution of parts; construction;\n   shape; form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Makebate', 'n.', 'One who excites contentions and quarrels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Make-belief', 'n.', 'A feigning to believe; make believe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Make-believe', 'n.', 'A feigning to believe, as in the play of children; a\n   mere pretense; a fiction; an invention.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Make-believe', 'a.', 'Feigned; insincere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maked', 'p. p.', 'Made.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Make-game', 'n.', 'An object of ridicule; a butt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Makeless', 'a.', 'Matchless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Makeless', 'a.', 'Without a mate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Make-peace', 'n.', 'A peacemaker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maker', 'n.', 'One who makes, forms, or molds; a manufacturer;\n   specifically, the Creator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maker', 'n.', 'The person who makes a promissory note.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maker', 'n.', 'One who writes verses; a poet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Makeshift', 'n.', 'That with which one makes shift; a temporary expedient.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Make-up', 'n.', 'The way in which the parts of anything are put together;\n   often, the way in which an actor is dressed, painted, etc., in\n   personating a character.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Makeweight', 'n.', 'That which is thrown into a scale to make weight;\n   something of little account added to supply a deficiency or fill a gap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maki', 'n.', 'A lemur. See Lemur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Making', 'n.', 'The act of one who makes; workmanship; fabrication;\n   construction; as, this is cloth of your own making; the making of peace\n   or war was in his power.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Making', 'n.', 'Composition, or structure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Making', 'n.', 'a poem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Making', 'n.', 'That which establishes or places in a desirable state or\n   condition; the material of which something may be made; as, early\n   misfortune was the making of him.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Making', 'n.', 'External appearance; from.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Making-iron', 'n.', 'A tool somewhat like a chisel with a groove in it,\n   used by calkers of ships to finish the seams after the oakum has been\n   driven in.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Making-up', 'n.', 'The act of bringing spirits to a certain degree of\n   strength, called proof.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Making-up', 'n.', 'The act of becoming reconciled or friendly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mal-', '', 'A prefix in composition denoting ill,or evil, F. male, adv.,\n   fr. malus, bad, ill. In some words it has the form male-, as in\n   malediction, malevolent. See Malice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mala', 'n.', 'Evils; wrongs; offenses against right and law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malabar', 'n.', 'A region in the western part of the Peninsula of India,\n   between the mountains and the sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malacatune', 'n.', 'See Melocoton.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malacca', 'n.', 'A town and district upon the seacoast of the Malay\n   Peninsula.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malachite', 'n.', 'Native hydrous carbonate of copper, usually occurring in\n   green mammillary masses with concentric fibrous structure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malacissant', 'a.', 'Softening; relaxing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malacissation', 'n.', 'The act of making soft or supple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malacobdella', 'n.', 'A genus of nemertean worms, parasitic in the gill\n   cavity of clams and other bivalves. They have a large posterior sucker,\n   like that of a leech. See Illust. of Bdellomorpha.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malacoderm', 'n.', 'One of a tribe of beetles (Malacodermata), with a soft\n   and flexible body, as the fireflies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malacolite', 'n.', 'A variety of pyroxene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malacologist', 'n.', 'One versed in the science of malacology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malacology', 'n.', 'The science which relates to the structure and habits\n   of mollusks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malacopoda', 'n. pl.', 'A class of air-breathing Arthropoda; -- called also\n   Protracheata, and Onychophora.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malacopterygian', 'n.', 'One of the Malacopterygii.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malacopterygii', 'n. pl.', 'An order of fishes in which the fin rays,\n   except the anterior ray of the pectoral and dorsal fins, are closely\n   jointed, and not spiny. It includes the carp, pike, salmon, shad, etc.\n   Called also Malacopteri.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malacopterygious', 'a.', 'Belonging to the Malacopterygii.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malacosteon', 'n.', 'A peculiar disease of the bones, in consequence of\n   which they become softened and capable of being bent without breaking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malacostomous', 'a.', 'Having soft jaws without teeth, as certain fishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malacostraca', 'n. pl.', 'A subclass of Crustacea, including Arthrostraca\n   and Thoracostraca, or all those higher than the Entomostraca.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malacostracan', 'n.', 'One of the Malacostraca.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malacostracology', 'n.', 'That branch of zoological science which relates\n   to the crustaceans; -- called also carcinology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malacostracous', 'a.', 'Belonging to the Malacostraca.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malacotoon', 'n.', 'See Melocoton.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malacozoa', 'n. pl.', 'An extensive group of Invertebrata, including the\n   Mollusca, Brachiopoda, and Bryozoa. Called also Malacozoaria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malacozoic', 'a.', 'Of or pertaining to the Malacozoa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maladdress', 'n.', 'Bad address; an awkward, tactless, or offensive way of\n   accosting one or talking with one.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maladjustment', 'n.', 'A bad adjustment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maladministration', 'n.', 'Bad administration; bad management of any\n   business, especially of public affairs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maladroit', 'a.', 'Of a quality opposed to adroitness; clumsy; awkward;\n   unskillful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maladies', 'pl. ', 'of Malady');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malady', 'n.', 'Any disease of the human body; a distemper, disorder, or\n   indisposition, proceeding from impaired, defective, or morbid organic\n   functions; especially, a lingering or deep-seated disorder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malady', 'n.', 'A moral or mental defect or disorder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malaga', 'n.', 'A city and a province of Spain, on the Mediterranean.\n   Hence, Malaga grapes, Malaga raisins, Malaga wines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malagash', 'n.', 'Same as Malagasy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malagasy', 'n. sing. & pl.', 'A native or natives of Madagascar; also\n   (sing.), the language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malaise', 'n.', 'An indefinite feeling of uneasiness, or of being sick or\n   ill at ease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malamate', 'n.', 'A salt of malamic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malambo', 'n.', 'A yellowish aromatic bark, used in medicine and perfumery,\n   said to be from the South American shrub Croton Malambo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malamethane', 'n.', 'A white crystalline substance forming the ethyl salt\n   of malamic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malamic', 'a.', 'Of or pertaining an acid intermediate between malic acid\n   and malamide, and known only by its salts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malamide', 'n.', 'The acid amide derived from malic acid, as a white\n   crystalline substance metameric with asparagine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malanders', 'n. pl.', 'A scurfy eruption in the bend of the knee of the\n   fore leg of a horse. See Sallenders.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malapert', 'a.', 'Bold; forward; impudent; saucy; pert.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malapert', 'n.', 'A malapert person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malapropism', 'n.', 'A grotesque misuse of a word; a word so used.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malapropos', 'a. & adv.', 'Unseasonable or unseasonably; unsuitable or\n   unsuitably.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malapterurus', 'n.', 'A genus of African siluroid fishes, including the\n   electric catfishes. See Electric cat, under Electric.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malar', 'a.', 'Of or pertaining to the region of the cheek bone, or to the\n   malar bone; jugal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malar', 'n.', 'The cheek bone, which forms a part of the lower edge of the\n   orbit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malaria', 'n.', 'Air infected with some noxious substance capable of\n   engendering disease; esp., an unhealthy exhalation from certain soils,\n   as marshy or wet lands, producing fevers; miasma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malaria', 'n.', 'A morbid condition produced by exhalations from decaying\n   vegetable matter in contact with moisture, giving rise to fever and\n   ague and many other symptoms characterized by their tendency to recur\n   at definite and usually uniform intervals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malarial', 'a.', 'Alt. of Malarious');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malarian', 'a.', 'Alt. of Malarious');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malarious', 'a.', 'Of or pertaining, to or infected by, malaria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malashaganay', 'n.', 'The fresh-water drumfish (Haploidonotus grunniens).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malassimilation', 'n.', 'Imperfect digestion of the several leading\n   constituents of the food.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malassimilation', 'n.', 'An imperfect elaboration by the tissues of the\n   materials brought to them by the blood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malate', 'n.', 'A salt of malic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malax', 'v. t.', 'Alt. of Malaxate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malaxate', 'v. t.', 'To soften by kneading or stirring with some thinner\n   substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malaxation', 'n.', 'The act of softening by mixing with a thinner\n   substance; the formation of ingredients into a mass for pills or\n   plasters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malaxator', 'n.', 'One who, or that which, malaxates; esp., a machine for\n   grinding, kneading, or stirring into a pasty or doughy mass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malay', 'n.', 'One of a race of a brown or copper complexion in the Malay\n   Peninsula and the western islands of the Indian Archipelago.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malay', 'a.', 'Alt. of Malayan');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malayan', 'a.', 'Of or pertaining to the Malays or their country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malayan', 'n.', 'The Malay language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malayalam', 'n.', 'The name given to one the cultivated Dravidian\n   languages, closely related to the Tamil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malbrouck', 'n.', 'A West African arboreal monkey (Cercopithecus\n   cynosurus).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malconformation', 'n.', 'Imperfect, disproportionate, or abnormal\n   formation; ill form; disproportion of parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malcontent', 'a.', 'discontented; uneasy; dissatisfied; especially,\n   dissatisfied with the government.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malcontent', 'n.', 'One who discontented; especially, a discontented\n   subject of a government; one who express his discontent by words or\n   overt acts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malcontented', 'a.', 'Malcontent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maldanian', 'n.', 'Any species of marine annelids of the genus Maldane, or\n   family Maldanidae. They have a slender, round body, and make tubes in\n   the sand or mud.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Male-', '', 'See Mal-.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Male', 'a.', 'Evil; wicked; bad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Male', 'n.', 'Same as Mail, a bag.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Male', 'v. t.', 'Of or pertaining to the sex that begets or procreates\n   young, or (in a wider sense) to the sex that produces spermatozoa, by\n   which the ova are fertilized; not female; as, male organs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Male', 'v. t.', 'Capable of producing fertilization, but not of bearing\n   fruit; -- said of stamens and antheridia, and of the plants, or parts\n   of plants, which bear them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Male', 'v. t.', 'Suitable to the male sex; characteristic or suggestive of\n   a male; masculine; as, male courage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Male', 'v. t.', 'Consisting of males; as, a male choir.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Male', 'v. t.', 'Adapted for entering another corresponding piece (the\n   female piece) which is hollow and which it fits; as, a male gauge, for\n   gauging the size or shape of a hole; a male screw, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Male', 'n.', 'An animal of the male sex.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Male', 'n.', 'A plant bearing only staminate flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naleadministration', 'n.', 'Maladministration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maleate', 'n.', 'A salt of maleic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malebranchism', 'n.', 'The philosophical system of Malebranche, an eminent\n   French metaphysician. The fundamental doctrine of his system is that\n   the mind can not have knowledge of anything external to itself except\n   in its relation to God.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maleconformation', 'n.', 'Malconformation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malecontent', 'a.', 'Malcontent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maledicency', 'n.', 'Evil speaking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maledicent', 'a.', 'Speaking reproachfully; slanderous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maledict', 'a.', 'Accursed; abominable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malediction', 'n.', 'A proclaiming of evil against some one; a cursing;\n   imprecation; a curse or execration; -- opposed to benediction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malefaction', 'n.', 'A crime; an offense; an evil deed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malefactor', 'n.', 'An evil doer; one who commits a crime; one subject to\n   public prosecution and punishment; a criminal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malefactor', 'n.', 'One who does wrong by injuring another, although not a\n   criminal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malefactress', 'n.', 'A female malefactor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malefeasance', 'n.', 'See Malfeasance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malefic', 'a.', 'Doing mischief; causing harm or evil; nefarious; hurtful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malefice', 'n.', 'An evil deed; artifice; enchantment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maleficence', 'n.', 'Evil doing, esp. to others.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maleficent', 'a.', 'Doing evil to others; harmful; mischievous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maleficial', 'a.', 'Injurious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maleficiate', 'v. t.', 'To bewitch; to harm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maleficiation', 'n.', 'A bewitching.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maleficience', 'n.', 'The doing of evil, harm, or mischief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maleficient', 'a.', 'Doing evil, harm, or mischief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maleformation', 'n.', 'See Malformation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maleic', 'a.', 'Pertaining to, or designating, an acid of the ethylene\n   series, metameric with fumaric acid and obtained by heating malic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malengine', 'n.', 'Evil machination; guile; deceit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maleo', 'n.', 'A bird of Celebes (megacephalon maleo), allied to the brush\n   turkey. It makes mounds in which to lay its eggs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Male-odor', 'n.', 'See Malodor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malepractice', 'n.', 'See Malpractice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Male-spirited', 'a.', 'Having the spirit of a male; vigorous; courageous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malet', 'n.', 'A little bag or budget.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maletreat', 'v. t.', 'See Maltreat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malevolence', 'n.', 'The quality or state of being malevolent; evil\n   disposition toward another; inclination to injure others; ill will. See\n   Synonym of Malice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malevolent', 'a.', 'Wishing evil; disposed to injure others; rejoicing in\n   anothers misfortune.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malevolently', 'adv.', 'In a malevolent manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malevolous', 'a.', 'Malevolent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malexecution', 'n.', 'Bad execution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maleyl', 'n.', 'A hypothetical radical derived from maleic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malfeasance', 'n.', 'The doing of an act which a person ought not to do;\n   evil conduct; an illegal deed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malformation', 'n.', 'Ill formation; irregular or anomalous formation;\n   abnormal or wrong conformation or structure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malgracious', 'a.', 'Not graceful; displeasing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malgre', 'prep.', 'See Mauger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malic', 'a.', 'Pertaining to, or obtained from, apples; as, malic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malice', 'n.', 'Enmity of heart; malevolence; ill will; a spirit delighting\n   in harm or misfortune to another; a disposition to injure another; a\n   malignant design of evil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malice', 'n.', 'Any wicked or mischievous intention of the mind; a depraved\n   inclination to mischief; an intention to vex, annoy, or injure another\n   person, or to do a wrongful act without just cause or cause or excuse;\n   a wanton disregard of the rights or safety of others; willfulness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malice', 'v. t.', 'To regard with extreme ill will.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malicho', 'n.', 'Mischief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malicious', 'a.', 'Indulging or exercising malice; harboring ill will or\n   enmity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malicious', 'a.', 'Proceeding from hatred or ill will; dictated by malice;\n   as, a malicious report; malicious mischief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malicious', 'a.', 'With wicked or mischievous intentions or motives;\n   wrongful and done intentionally without just cause or excuse; as, a\n   malicious act.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malign', 'a.', 'Having an evil disposition toward others; harboring violent\n   enmity; malevolent; malicious; spiteful; -- opposed to benign.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malign', 'a.', 'Unfavorable; unpropitious; pernicious; tending to injure;\n   as, a malign aspect of planets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malign', 'a.', 'Malignant; as, a malign ulcer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maligned', 'imp. & p. p.', 'of Malign');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maligning', 'p. pr. & vb. n.', 'of Malign');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malign', 'a.', 'To treat with malice; to show hatred toward; to abuse; to\n   wrong; to injure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malign', 'a.', 'To speak great evil of; to traduce; to defame; to slander;\n   to vilify; to asperse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malign', 'v. i.', 'To entertain malice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malignance', 'n.', 'Alt. of Malignancy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malignancy', 'n.', 'The state or quality of being malignant; extreme\n   malevolence; bitter enmity; malice; as, malignancy of heart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malignancy', 'n.', 'Unfavorableness; evil nature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malignancy', 'n.', 'Virulence; tendency to a fatal issue; as, the\n   malignancy of an ulcer or of a fever.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malignancy', 'n.', 'The state of being a malignant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malignant', 'a.', 'Disposed to do harm, inflict suffering, or cause\n   distress; actuated by extreme malevolence or enmity; virulently\n   inimical; bent on evil; malicious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malignant', 'a.', 'Characterized or caused by evil intentions; pernicious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malignant', 'a.', 'Tending to produce death; threatening a fatal issue;\n   virulent; as, malignant diphtheria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malignant', 'n.', 'A man of extrems enmity or evil intentions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malignant', 'n.', 'One of the adherents of Charles L. or Charles LL.; -- so\n   called by the opposite party.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malignantly', 'adv.', 'In a malignant manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maligner', 'n.', 'One who maligns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malignified', 'imp. & p. p.', 'of Malignify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malignifying', 'p. pr. & vb. n.', 'of Malignify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malignify', 'v. t.', 'To make malign or malignant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malignity', 'n.', 'The state or quality of being malignant; disposition to\n   do evil; virulent enmity; malignancy; malice; spite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malignity', 'n.', 'Virulence; deadly quality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malignity', 'n.', 'Extreme evilness of nature or influence; perniciousness;\n   heinousness; as, the malignity of fraud.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malignly', 'adv.', 'In a malign manner; with malignity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'MAlingered', 'imp. & p. p.', 'of Malinger');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malingering', 'p. pr. & vb. n.', 'of Malinger');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malinger', 'v. i.', 'To act the part of a malingerer; to feign illness or\n   inability.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malingerer', 'n.', 'In the army, a soldier who feigns himself sick, or who\n   induces or protracts an illness, in order to avoid doing his duty;\n   hence, in general, one who shirks his duty by pretending illness or\n   inability.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malingery', 'n.', 'The spirit or practices of a malingerer; malingering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malison', 'n.', 'Malediction; curse; execration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malkin', 'n.', 'Originally, a kitchenmaid; a slattern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malkin', 'n.', 'A mop made of clouts, used by the kitchen servant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malkin', 'n.', 'A scarecrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malkin', 'n.', 'A mop or sponge attached to a jointed staff for swabbing\n   out a cannon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mall', 'n.', 'A large heavy wooden beetle; a mallet for driving anything\n   with force; a maul.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mall', 'n.', 'A heavy blow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mall', 'n.', 'An old game played with malls or mallets and balls. See\n   Pall-mall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mall', 'n.', 'A place where the game of mall was played. Hence: A public\n   walk; a level shaded walk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malled', 'imp. & p. p.', 'of Mall');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malling', 'p. pr. & vb. n.', 'of Mall');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mall', 'v. t.', 'To beat with a mall; to beat with something heavy; to\n   bruise; to maul.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mall', 'n.', 'Formerly, among Teutonic nations, a meeting of the notables\n   of a state for the transaction of public business, such meeting being a\n   modification of the ancient popular assembly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mall', 'n.', 'A court of justice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mall', 'n.', 'A place where justice is administered.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mall', 'n.', 'A place where public meetings are held.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mallard', 'a.', 'A drake; the male of Anas boschas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mallard', 'a.', 'A large wild duck (Anas boschas) inhabiting both America\n   and Europe. The domestic duck has descended from this species. Called\n   also greenhead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malleability', 'n.', 'The quality or state of being malleable; -- opposed\n   to friability and brittleness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malleable', 'a.', 'Capable of being extended or shaped by beating with a\n   hammer, or by the pressure of rollers; -- applied to metals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malleableize', 'v. t.', 'To make malleable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malleableness', 'n.', 'Quality of being malleable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malleal', 'a.', 'Pertaining to the malleus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malleated', 'imp. & p. p.', 'of Malleate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malleating', 'p. pr. & vb. n.', 'of Malleate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malleate', 'v. t.', 'To hammer; to beat into a plate or leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malleation', 'n.', 'The act or process of beating into a plate, sheet, or\n   leaf, as a metal; extension by beating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mallecho', 'n.', 'Same as Malicho.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mallee bird', '', 'The leipoa. See Leipoa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mallemock', 'n.', 'Alt. of Mallemoke');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mallemoke', 'n.', 'See Mollemoke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mallenders', 'n. pl.', 'Same as Malanders.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malleolar', 'a.', 'Of or pertaining to the malleolus; in the region of the\n   malleoli of the ankle joint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malleoli', 'pl. ', 'of Malleolus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malleolus', 'n.', 'A projection at the distal end of each bone of the leg\n   at the ankle joint. The malleolus of the tibia is the internal\n   projection, that of the fibula the external.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malleolus', 'n.', '\" A layer, \" a shoot partly buried in the ground, and\n   there cut halfway through.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mallet', 'n.', 'A small maul with a short handle, -- used esp. for driving\n   a tool, as a chisel or the like; also, a light beetle with a long\n   handle, -- used in playing croquet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mallei', 'pl. ', 'of Malleus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malleus', 'n.', 'The outermost of the three small auditory bones, ossicles;\n   the hammer. It is attached to the tympanic membrane by a long process,\n   the handle or manubrium. See Illust. of Far.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malleus', 'n.', 'One of the hard lateral pieces of the mastax of Rotifera.\n   See Mastax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malleus', 'n.', 'A genus of bivalve shells; the hammer shell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mallophaga', 'n. pl.', 'An extensive group of insects which are parasitic\n   on birds and mammals, and feed on the feathers and hair; -- called also\n   bird lice. See Bird louse, under Bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mallotus', 'n.', 'A genus of small Arctic fishes. One American species, the\n   capelin (Mallotus villosus), is extensively used as bait for cod.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mallow', 'n.', 'Alt. of Mallows');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mallows', 'n.', 'A genus of plants (Malva) having mucilaginous qualities.\n   See Malvaceous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mallowwort', 'n.', 'Any plant of the order Malvaceae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malm', 'n.', 'Alt. of Malmbrick');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malmbrick', 'n.', 'A kind of brick of a light brown or yellowish color,\n   made of sand, clay, and chalk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malma', 'n.', 'A spotted trout (Salvelinus malma), inhabiting Northern\n   America, west of the Rocky Mountains; -- called also Dolly Varden\n   trout, bull trout, red-spotted trout, and golet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malmag', 'n.', 'The tarsius, or spectral lemur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malmsey', 'n.', 'A kind of sweet wine from Crete, the Canary Islands, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malnutrition', 'n.', 'Faulty or imperfect nutrition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malobservation', 'n.', 'Erroneous observation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malodor', 'n.', 'An Offensive to the sense of smell; ill-smelling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malonate', 'a.', 'At salt of malonic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malonic', 'a.', 'Pertaining to, or designating, an acid produced\n   artifically as a white crystalline substance, CH2.(CO2H)2, and so\n   called because obtained by the oxidation of malic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malonyl', 'n.', 'A hydrocarbon radical, CH2.(CO)2, from malonic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malpighia', 'n.', 'A genus of tropical American shrubs with opposite leaves\n   and small white or reddish flowers. The drupes of Malpighia urens are\n   eaten under the name of Barbadoes cherries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malpighiaceous', 'a.', 'Of, pertaining to, or resembling, a natural order\n   of tropical trees and shrubs (Malpighiaceae), some of them climbing\n   plants, and their stems forming many of the curious lianes of South\n   American forests.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malpighian', 'a.', 'Of, pertaining to, or discovered by, Marcello Malpighi,\n   an Italian anatomist of the 17th century.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malposition', 'n.', 'A wrong position.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malpractice', 'n.', 'Evil practice; illegal or immoral conduct; practice\n   contrary to established rules; specifically, the treatment of a case by\n   a surgeon or physician in a manner which is contrary to accepted rules\n   and productive of unfavorable results.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malt', 'n.', 'Barley or other grain, steeped in water and dried in a kiln,\n   thus forcing germination until the saccharine principle has been\n   evolved. It is used in brewing and in the distillation of whisky.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malt', 'a.', 'Relating to, containing, or made with, malt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malted', 'imp. & p. p.', 'of Malt');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malting', 'p. pr. & vb. n.', 'of Malt');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malt', 'v. t.', 'To make into malt; as, to malt barley.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malt', 'v. i.', 'To become malt; also, to make grain into malt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maltalent', 'n.', 'Ill will; malice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maltese', 'a.', 'Of or pertaining to Malta or to its inhabitants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maltese', 'n. sing. & pl.', 'A native or inhabitant of Malta; the people of\n   Malta.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maltha', 'n.', 'A variety of bitumen, viscid and tenacious, like pitch,\n   unctuous to the touch, and exhaling a bituminous odor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maltha', 'n.', 'Mortar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malthusian', 'a.', 'Of or pertaining to the political economist, the Rev.\n   T. R. Malthus, or conforming to his views; as, Malthusian theories.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mathusian', 'n.', 'A follower of Malthus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malthusianism', 'n.', 'The system of Malthusian doctrines relating to\n   population.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maltin', 'n.', 'Alt. of Maltine');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maltine', 'n.', 'The fermentative principle of malt; malt diastase; also, a\n   name given to various medicinal preparations made from or containing\n   malt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malting', 'n.', 'The process of making, or of becoming malt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maltmen', 'pl. ', 'of Maltman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maltman', 'n.', 'A man whose occupation is to make malt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maltonic', 'a.', 'Of, pertaining to, or derived from, maltose; specif.,\n   designating an acid called also gluconic or dextronic acid. See\n   Gluconic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maltose', 'n.', 'A crystalline sugar formed from starch by the action of\n   distance of malt, and the amylolytic ferment of saliva and pancreatic\n   juice. It resembles dextrose, but rotates the plane of polarized light\n   further to the right and possesses a lower cupric oxide reducing power.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maltreated', 'imp. & p. p.', 'of Maltreat');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maltreating', 'p. pr. & vb. n.', 'of Maltreat');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maltreat', 'v. t.', 'To treat ill; to abuse; to treat roughly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maltreament', 'n.', 'Ill treatment; ill usage; abuse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maltster', 'n.', 'A maltman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maltworm', 'n.', 'A tippler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malty', 'a.', 'Consisting, or like, malt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mala', 'pl. ', 'of Malum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malum', 'n.', 'An evil. See Mala.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malvaceous', 'a.', 'Pertaining to, or resembling, a natural order of plants\n   (Malvaceae), of which the mallow is the type. The cotton plant,\n   hollyhock, and abutilon are of this order, and the baobab and the\n   silk-cotton trees are now referred to it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malversation', 'n.', 'Evil conduct; fraudulent practices; misbehavior,\n   corruption, or extortion in office.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Malvesie', 'n.', 'Malmsey wine. See Malmsey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mam', 'n.', 'Mamma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mama', 'n.', 'See Mamma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mamaluke', 'n.', 'Same as Mameluke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mamelon', 'n.', 'A rounded hillock; a rounded elevation or protuberance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mameluco', 'n.', 'A child born of a white father and Indian mother.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mameluke', 'n.', 'One of a body of mounted soldiers recruited from slaves\n   converted to Mohammedanism, who, during several centuries, had more or\n   less control of the government of Egypt, until exterminated or\n   dispersed by Mehemet Ali in 1811.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mamillated', 'a.', 'See Mammillated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mamma', 'n.', 'Mother; -- word of tenderness and familiarity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mammae', 'pl. ', 'of Mamma');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mamma', 'n.', 'A glandular organ for secreting milk, characteristic of all\n   mammals, but usually rudimentary in the male; a mammary gland; a\n   breast; under; bag.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mammals', 'pl. ', 'of Mammal');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mammal', 'n.', 'One of the Mammalia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mammalia', 'n. pl.', 'The highest class of Vertebrata. The young are\n   nourished for a time by milk, or an analogous fluid, secreted by the\n   mammary glands of the mother.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mammalian', 'a.', 'Of or pertaining to the Mammalia or mammals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mammaliferous', 'a.', 'Containing mammalian remains; -- said of certain\n   strata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mammalogical', 'a.', 'Of or pertaining to mammalogy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mammalogist', 'n.', 'One versed in mammalogy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mammalogy', 'n.', 'The science which relates to mammals or the Mammalia.\n   See Mammalia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mammary', 'a.', 'Of or pertaining to the mammae or breasts; as, the mammary\n   arteries and veins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mammee', 'n.', 'A fruit tree of tropical America, belonging to the genus\n   Mammea (M. Americana); also, its fruit. The latter is large, covered\n   with a thick, tough ring, and contains a bright yellow pulp of a\n   pleasant taste and fragrant scent. It is often called mammee apple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mammer', 'v. i.', 'To hesitate; to mutter doubtfully.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mammet', 'n.', 'An idol; a puppet; a doll.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mammetry', 'n.', 'See Mawmetry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mammifer', 'n.', 'A mammal. See Mammalia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mammiferous', 'a.', 'Having breasts; of, pertaining to, or derived from,\n   the Mammalia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mammiform', 'a.', 'Having the form of a mamma (breast) or mammae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mammilae', 'pl. ', 'of Mammilla');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mammilla', 'n.', 'The nipple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mammillary', 'a.', 'Of or pertaining to the mammilla, or nipple, or to the\n   breast; resembling a mammilla; mammilloid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mammillary', 'a.', 'Composed of convex convex concretions, somewhat\n   resembling the breasts in form; studded with small mammiform\n   protuberances.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mammillate', 'a.', 'Alt. of Mammillated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mammillated', 'a.', 'Having small nipples, or small protuberances like\n   nipples or mammae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mammillated', 'a.', 'Bounded like a nipple; -- said of the apex of some\n   shells.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mammilliform', 'a.', 'Having the form of a mammilla.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mammilloid', 'a.', 'Like a mammilla or nipple; mammilliform.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mammock', 'n.', 'A shapeless piece; a fragment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mammock', 'v. t.', 'To tear to pieces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mammodis', 'n.', 'Coarse plain India muslins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mammology', 'n.', 'Mastology. See Mammalogy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mammon', 'n.', 'Riches; wealth; the god of riches; riches, personified.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mammonish', 'a.', 'Actuated or prompted by a devotion to money getting or\n   the service of Mammon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mammonism', 'n.', 'Devotion to the pursuit of wealth; worldliness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mammonist', 'n.', 'A mammonite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mammonite', 'n.', 'One devoted to the acquisition of wealth or the service\n   of Mammon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mammonization', 'n.', 'The process of making mammonish; the state of being\n   under the influence of mammonism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mammonize', 'v. t.', 'To make mammonish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mammose', 'a.', 'Having the form of the breast; breast-shaped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mammoth', 'n.', 'An extinct, hairy, maned elephant (Elephas primigenius),\n   of enormous size, remains of which are found in the northern parts of\n   both continents. The last of the race, in Europe, were coeval with\n   prehistoric man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mammoth', 'a.', 'Resembling the mammoth in size; very large; gigantic; as,\n   a mammoth ox.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mammothrept', 'n.', 'A child brought up by its grandmother; a spoiled\n   child.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mammies', 'pl. ', 'of Mammy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mammy', 'n.', 'A childs name for mamma, mother.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mamzer', 'n.', 'A person born of relations between whom marriage was\n   forbidden by the Mosaic law; a bastard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Men', 'pl. ', 'of Man');
  INSERT INTO words (word, word_type, description) VALUES  (    'Man', 'n.', 'A human being; -- opposed tobeast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Man', 'n.', 'Especially: An adult male person; a grown-up male person, as\n   distinguished from a woman or a child.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Man', 'n.', 'The human race; mankind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Man', 'n.', 'The male portion of the human race.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Man', 'n.', 'One possessing in a high degree the distinctive qualities of\n   manhood; one having manly excellence of any kind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Man', 'n.', 'An adult male servant; also, a vassal; a subject.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Man', 'n.', 'A term of familiar address often implying on the part of the\n   speaker some degree of authority, impatience, or haste; as, Come, man,\n   we ve no time to lose!');
  INSERT INTO words (word, word_type, description) VALUES  (    'Man', 'n.', 'A married man; a husband; -- correlative to wife.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Man', 'n.', 'One, or any one, indefinitely; -- a modified survival of the\n   Saxon use of man, or mon, as an indefinite pronoun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Man', 'n.', 'One of the piece with which certain games, as chess or\n   draughts, are played.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manned', 'imp. & p. p.', 'of Man');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manning', 'p. pr. & vb. n.', 'of Man');
  INSERT INTO words (word, word_type, description) VALUES  (    'Man', 'v. t.', 'To supply with men; to furnish with a sufficient force or\n   complement of men, as for management, service, defense, or the like; to\n   guard; as, to man a ship, boat, or fort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Man', 'v. t.', 'To furnish with strength for action; to prepare for\n   efficiency; to fortify.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Man', 'v. t.', 'To tame, as a hawk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Man', 'v. t.', 'To furnish with a servants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Man', 'v. t.', 'To wait on as a manservant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manable', 'a.', 'Marriageable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manace', 'n. & v.', 'Same as Menace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manacle', 'n.', 'A handcuff; a shackle for the hand or wrist; -- usually in\n   the plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manacled', 'imp. & p. p.', 'of Manacle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manacling', 'p. pr. & vb. n.', 'of Manacle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manacle', 'v. t.', 'To put handcuffs or other fastening upon, for confining\n   the hands; to shackle; to confine; to restrain from the use of the\n   limbs or natural powers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manage', 'n.', 'The handling or government of anything, but esp. of a\n   horse; management; administration. See Manege.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Managed', 'imp. & p. p.', 'of Manage');
  INSERT INTO words (word, word_type, description) VALUES  (    'Managing', 'p. pr. & vb. n.', 'of Manage');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manage', 'n.', 'To have under control and direction; to conduct; to guide;\n   to administer; to treat; to handle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manage', 'n.', 'Hence: Esp., to guide by careful or delicate treatment; to\n   wield with address; to make subservient by artful conduct; to bring\n   around cunningly to ones plans.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manage', 'n.', 'To train in the manege, as a horse; to exercise in graceful\n   or artful action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manage', 'n.', 'To treat with care; to husband.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manage', 'n.', 'To bring about; to contrive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manage', 'v. i.', 'To direct affairs; to carry on business or affairs; to\n   administer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manageability', 'n.', 'The state or quality of being manageable;\n   manageableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manageable', 'a.', 'Such as can be managed or used; suffering control;\n   governable; tractable; subservient; as, a manageable horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manageless', 'a.', 'Unmanageable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Management', 'v.', 'The act or art of managing; the manner of treating,\n   directing, carrying on, or using, for a purpose; conduct;\n   administration; guidance; control; as, the management of a family or of\n   a farm; the management of state affairs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Management', 'v.', 'Business dealing; negotiation; arrangement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Management', 'v.', 'Judicious use of means to accomplish an end; conduct\n   directed by art or address; skillful treatment; cunning practice; --\n   often in a bad sense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Management', 'v.', 'The collective body of those who manage or direct any\n   enterprise or interest; the board of managers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manager', 'n.', 'One who manages; a conductor or director; as, the manager\n   of a theater.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manager', 'n.', 'A person who conducts business or household affairs with\n   economy and frugality; a good economist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manager', 'n.', 'A contriver; an intriguer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Managerial', 'a.', 'Of or pertaining to management or a manager; as,\n   managerial qualities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Managership', 'n.', 'The office or position of a manager.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Managery', 'n.', 'Management; manner of using; conduct; direction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Managery', 'n.', 'Husbandry; economy; frugality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manakin', 'n.', 'Any one of numerous small birds belonging to Pipra,\n   Manacus, and other genera of the family Pipridae. They are mostly\n   natives of Central and South America. some are bright-colored, and\n   others have the wings and tail curiously ornamented. The name is\n   sometimes applied to related birds of other families.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manakin', 'n.', 'A dwarf. See Manikin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manatee', 'n.', 'Any species of Trichechus, a genus of sirenians; -- called\n   alsosea cow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manation', 'n.', 'The act of issuing or flowing out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manbote', 'n.', 'A sum paid to a lord as a pecuniary compensation for\n   killing his man (that is, his vassal, servant, or tenant).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manca', 'n.', 'See Mancus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manche', 'n.', 'A sleeve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manchet', 'n.', 'Fine white bread; a loaf of fine bread.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manchineel', 'n.', 'A euphorbiaceous tree (Hippomane Mancinella) of\n   tropical America, having a poisonous and blistering milky juice, and\n   poisonous acrid fruit somewhat resembling an apple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manchu', 'a.', 'Of or pertaining to Manchuria or its inhabitants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manchu', 'n.', 'A native or inhabitant of Manchuria; also, the language\n   spoken by the Manchus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mancipate', 'v. t.', 'To enslave; to bind; to restrict.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mancipation', 'n.', 'Slavery; involuntary servitude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manciple', 'n.', 'A steward; a purveyor, particularly of a college or Inn\n   of Court.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mancona bark', '', 'See Sassy bark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mancus', 'n.', 'An old Anglo Saxon coin both of gold and silver, and of\n   variously estimated values. The silver mancus was equal to about one\n   shilling of modern English money.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-mancy', '', 'A combining form denoting divination; as, aleuromancy,\n   chiromancy, necromancy, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mand', 'n.', 'A demand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mandamus', 'n.', 'A writ issued by a superior court and directed to some\n   inferior tribunal, or to some corporation or person exercising\n   authority, commanding the performance of some specified duty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mandarin', 'n.', 'A Chinese public officer or nobleman; a civil or military\n   official in China and Annam.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mandarin', 'n.', 'A small orange, with easily separable rind. It is thought\n   to be of Chinese origin, and is counted a distinct species (Citrus\n   nobilis)mandarin orange; tangerine --.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mandarinate', 'n.', 'The collective body of officials or persons of rank in\n   China.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mandarinic', 'a.', 'Appropriate or peculiar to a mandarin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mandarining', 'n.', 'The process of giving an orange color to goods formed\n   of animal tissue, as silk or wool, not by coloring matter, but by\n   producing a certain change in the fiber by the action of dilute nitric\n   acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mandarinism', 'n.', 'A government mandarins; character or spirit of the\n   mandarins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mandatary', 'n.', 'One to whom a command or charge is given; hence,\n   specifically, a person to whom the pope has, by his prerogative, given\n   a mandate or order for his benefice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mandatary', 'n.', 'One who undertakes to discharge a specific business\n   commission; a mandatory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mandate', 'n.', 'An official or authoritative command; an order or\n   injunction; a commission; a judicial precept.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mandate', 'n.', 'A rescript of the pope, commanding an ordinary collator to\n   put the person therein named in possession of the first vacant benefice\n   in his collation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mandate', 'n.', 'A contract by which one employs another to manage any\n   business for him. By the Roman law, it must have been gratuitous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mandator', 'n.', 'A director; one who gives a mandate or order.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mandator', 'n.', 'The person who employs another to perform a mandate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mandatory', 'a.', 'Containing a command; preceptive; directory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mandatory', 'n.', 'Same as Mandatary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mandelate', 'n.', 'A salt of mandelic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mandelic', 'a.', 'Pertaining to an acid first obtained from benzoic\n   aldehyde (oil of better almonds), as a white crystalline substance; --\n   called also phenyl glycolic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mander', 'v. t. & i.', 'See Maunder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manderil', 'n.', 'A mandrel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mandible', 'n.', 'The bone, or principal bone, of the lower jaw; the\n   inferior maxilla; -- also applied to either the upper or the lower jaw\n   in the beak of birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mandible', 'n.', 'The anterior pair of mouth organs of insects,\n   crustaceaus, and related animals, whether adapted for biting or not.\n   See Illust. of Diptera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mandibular', 'a.', 'Of or pertaining to a mandible; like a mandible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mandibular', 'n.', 'The principal mandibular bone; the mandible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mandibulate', 'a.', 'Alt. of Mandibulated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mandibulated', 'a.', 'Provided with mandibles adapted for biting, as many\n   insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mandibulate', 'n.', 'An insect having mandibles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mandibuliform', 'a.', 'Having the form of a mandible; -- said especially of\n   the maxillae of an insect when hard and adapted for biting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mandibulohyoid', 'a.', 'Pertaining both to the mandibular and the hyoid\n   arch, or situated between them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mandil', 'n.', 'A loose outer garment worn the 16th and 17th centuries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mandilion', 'n.', 'See Mandil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mandingos', 'n. pl.', '; sing. Mandingo. (Ethnol.) An extensive and\n   powerful tribe of West African negroes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mandioc', 'n.', 'Alt. of Mandioca');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mandioca', 'n.', 'See Manioc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mandlestone', 'n.', 'Amygdaloid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mandment', 'n.', 'Commandment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mandolin', 'n.', 'Alt. of Mandoline');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mandoline', 'n.', 'A small and beautifully shaped instrument resembling the\n   lute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mandore', 'n.', 'A kind of four-stringed lute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mandragora', 'n.', 'A genus of plants; the mandrake. See Mandrake, 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mandragorite', 'n.', 'One who habitually intoxicates himself with a\n   narcotic obtained from mandrake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mandrake', 'n.', 'A low plant (Mandragora officinarum) of the Nightshade\n   family, having a fleshy root, often forked, and supposed to resemble a\n   man. It was therefore supposed to have animal life, and to cry out when\n   pulled up. All parts of the plant are strongly narcotic. It is found in\n   the Mediterranean region.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mandrake', 'n.', 'The May apple (Podophyllum peltatum). See May apple under\n   May, and Podophyllum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mandrel', 'n.', 'A bar of metal inserted in the work to shape it, or to\n   hold it, as in a lathe, during the process of manufacture; an arbor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mandrel', 'n.', 'The live spindle of a turning lathe; the revolving arbor\n   of a circular saw. It is usually driven by a pulley.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mandrill', 'n.', 'a large West African baboon (Cynocephalus, / Papio,\n   mormon). The adult male has, on the sides of the nose, large, naked,\n   grooved swellings, conspicuously striped with blue and red.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manducable', 'a.', 'Such as can be chewed; fit to be eaten.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manducated', 'imp. & p. p.', 'of Manducate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manducating', 'p. pr. & vb. n.', 'of Manducate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manducate', 'v. t.', 'To masticate; to chew; to eat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manducation', 'n.', 'The act of chewing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manducatory', 'a.', 'Pertaining to, or employed in, chewing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manducus', 'n.', 'A grotesque mask, representing a person chewing or\n   grimacing, worn in processions and by comic actors on the stage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mane', 'n.', 'The long and heavy hair growing on the upper side of, or\n   about, the neck of some quadrupedal animals, as the horse, the lion,\n   etc. See Illust. of Horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Man-eater', 'n.', 'One who, or that which, has an appetite for human flesh;\n   specifically, one of certain large sharks (esp. Carcharodon Rondeleti);\n   also, a lion or a tiger which has acquired the habit of feeding upon\n   human flesh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maned', 'a.', 'Having a mane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manege', 'n.', 'Art of horsemanship, or of training horses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manege', 'n.', 'A school for teaching horsemanship, and for training\n   horses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maneh', 'n.', 'A Hebrew weight for gold or silver, being one hundred\n   shekels of gold and sixty shekels of silver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maneless', 'a.', 'Having no mane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manequin', 'n.', 'An artists model of wood or other material.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manerial', 'a.', 'See Manorial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manes', 'n. pl.', 'The benevolent spirits of the dead, especially of dead\n   ancestors, regarded as family deities and protectors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manesheet', 'n.', 'A covering placed over the upper part of a horses head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maneuver', 'n.', 'Alt. of Manoeuvre');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manoeuvre', 'n.', 'Management; dexterous movement; specif., a military or\n   naval evolution, movement, or change of position.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manoeuvre', 'n.', 'Management with address or artful design; adroit\n   proceeding; stratagem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maneuvered', 'imp. & p. p.', 'of Manoeuvre');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manoeuvred', '', 'of Manoeuvre');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maneuvering', 'p. pr. & vb. n.', 'of Manoeuvre');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manoeuvring', '', 'of Manoeuvre');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maneuver', 'n.', 'Alt. of Manoeuvre');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manoeuvre', 'n.', 'To perform a movement or movements in military or naval\n   tactics; to make changes in position with reference to getting\n   advantage in attack or defense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manoeuvre', 'n.', 'To manage with address or art; to scheme.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maneuver', 'v. t.', 'Alt. of Manoeuvre');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manoeuvre', 'v. t.', 'To change the positions of, as of troops of ships.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maneuverer', 'n.', 'Alt. of Manoeuvrer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manoeuvrer', 'n.', 'One who maneuvers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manful', 'a.', 'Showing manliness, or manly spirit; hence, brave,\n   courageous, resolute, noble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mamgabey', 'n.', 'Any one of several African monkeys of the genus\n   Cercocebus, as the sooty mangabey (C. fuliginosus), which is sooty\n   black.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mangan', 'n.', 'See Mangonel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manganate', 'n.', 'A salt of manganic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manganesate', 'n.', 'A manganate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manganese', 'n.', 'An element obtained by reduction of its oxide, as a\n   hard, grayish white metal, fusible with difficulty, but easily\n   oxidized. Its ores occur abundantly in nature as the minerals\n   pyrolusite, manganite, etc. Symbol Mn. Atomic weight 54.8.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manganesian', 'a.', 'Manganic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manganesic', 'a.', 'Manganic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manganesious', 'a.', 'Manganous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manganesium', 'n.', 'Manganese.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manganesous', 'a.', 'Manganous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manganic', 'a.', 'Of, pertaining to resembling, or containing, manganese;\n   specif., designating compounds in which manganese has a higher valence\n   as contrasted with manganous compounds. Cf. Manganous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manganiferous', 'a.', 'Containing manganese.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manganite', 'n.', 'One of the oxides of manganese; -- called also gray\n   manganese ore. It occurs in brilliant steel-gray or iron-black\n   crystals, also massive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manganite', 'n.', 'A compound of manganese dioxide with a metallic oxide;\n   so called as though derived from the hypothetical manganous acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manganium', 'n.', 'Manganese.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manganous', 'a.', 'Of, pertaining to, designating, those compounds of\n   manganese in which the element has a lower valence as contrasted with\n   manganic compounds; as, manganous oxide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mangcorn', 'n.', 'A mixture of wheat and rye, or other species of grain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mange', 'n.', 'The scab or itch in cattle, dogs, and other beasts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mangel-wurzel', 'n.', 'A kind of large field beet (B. macrorhiza), used as\n   food for cattle, -- by some considered a mere variety of the ordinary\n   beet. See Beet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manger', 'n.', 'A trough or open box in which fodder is placed for horses\n   or cattle to eat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manger', 'n.', 'The fore part of the deck, having a bulkhead athwart ships\n   high enough to prevent water which enters the hawse holes from running\n   over it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mangily', 'adv.', 'In a mangy manner; scabbily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manginess', 'n.', 'The condition or quality of being mangy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mangled', 'imp. & p. p.', 'of Mangle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mangling', 'p. pr. & vb. n.', 'of Mangle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mangle', 'v. t.', 'To cut or bruise with repeated blows or strokes, making\n   a ragged or torn wound, or covering with wounds; to tear in cutting; to\n   cut in a bungling manner; to lacerate; to mutilate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mangle', 'v. t.', 'To mutilate or injure, in making, doing, or pertaining;\n   as, to mangle a piece of music or a recitation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mangle', 'n.', 'A machine for smoothing linen or cotton cloth, as sheets,\n   tablecloths, napkins, and clothing, by roller pressure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mangle', 'n.', 'To smooth with a mangle, as damp linen or cloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mangler', 'n.', 'One who mangles or tears in cutting; one who mutilates any\n   work in doing it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mangler', 'n.', 'One who smooths with a mangle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mangoes', 'pl. ', 'of Mango');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mango', 'n.', 'The fruit of the mango tree. It is rather larger than an\n   apple, and of an ovoid shape. Some varieties are fleshy and luscious,\n   and others tough and tasting of turpentine. The green fruit is pickled\n   for market.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mango', 'n.', 'A green muskmelon stuffed and pickled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mangoldwurzel', 'n.', 'See Mangel-wurzel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mangonel', 'n.', 'A military engine formerly used for throwing stones and\n   javelins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mangonism', 'n.', 'The art of mangonizing, or setting off to advantage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mangonist', 'n.', 'One who mangonizes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mangonist', 'n.', 'A slave dealer; also, a strumpet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mangonize', 'v. t.', 'To furbish up for sale; to set off to advantage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mangosteen', 'n.', 'Alt. of Mangostan');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mangostan', 'n.', 'A tree of the East Indies of the genus Garcinia (G.\n   Mangostana). The tree grows to the height of eighteen feet, and bears\n   fruit also called mangosteen, of the size of a small apple, the pulp of\n   which is very delicious food.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mangrove', 'n.', 'The name of one or two trees of the genus Rhizophora (R.\n   Mangle, and R. mucronata, the last doubtfully distinct) inhabiting\n   muddy shores of tropical regions, where they spread by emitting aerial\n   roots, which fasten in the saline mire and eventually become new stems.\n   The seeds also send down a strong root while yet attached to the parent\n   plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mangrove', 'n.', 'The mango fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mangue', 'n.', 'The kusimanse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mangy', 'superl.', 'Infected with the mange; scabby.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manhaden', 'n.', 'See Menhaden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manhead', 'n.', 'Manhood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manhole', 'n.', 'A hole through which a man may descend or creep into a\n   drain, sewer, steam boiler, parts of machinery, etc., for cleaning or\n   repairing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manhood', 'n.', 'The state of being man as a human being, or man as\n   distinguished from a child or a woman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manhood', 'n.', 'Manly quality; courage; bravery; resolution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mania', 'n.', 'Violent derangement of mind; madness; insanity. Cf.\n   Delirium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mania', 'n.', 'Excessive or unreasonable desire; insane passion affecting\n   one or many people; as, the tulip mania.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maniable', 'a.', 'Manageable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maniac', 'a.', 'Raving with madness; raging with disordered intellect;\n   affected with mania; mad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maniac', 'n.', 'A raving lunatic; a madman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maniacal', 'a.', 'Affected with, or characterized by, madness; maniac.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manicate', 'a.', 'Covered with hairs or pubescence so platted together and\n   interwoven as to form a mass easily removed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manichaean', 'n.', 'Alt. of Manichee');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manichean', 'n.', 'Alt. of Manichee');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manichee', 'n.', 'A believer in the doctrines of Manes, a Persian of the\n   third century A. D., who taught a dualism in which Light is regarded as\n   the source of Good, and Darkness as the source of Evil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manichaean', 'a.', 'Alt. of Manichean');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manichean', 'a.', 'Of or pertaining to the Manichaeans.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manichaeism', 'n.', 'Alt. of Manicheism');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manicheism', 'n.', 'The doctrines taught, or system of principles\n   maintained, by the Manichaeans.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manicheist', 'n.', 'Manichaean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manichord', '', 'Alt. of Manichordon');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manichordon', '', 'The clavichord or clarichord; -- called also dumb\n   spinet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manicure', 'n.', 'A person who makes a business of taking care of peoples\n   hands, especially their nails.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manid', 'n.', 'Any species of the genus Manis, or family Manidae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manie', 'n.', 'Mania; insanity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manifest', 'a.', 'Evident to the senses, esp. to the sight; apparent;\n   distinctly perceived; hence, obvious to the understanding; apparent to\n   the mind; easily apprehensible; plain; not obscure or hidden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manifest', 'a.', 'Detected; convicted; -- with of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manifests', 'pl. ', 'of Manifest');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manifest', 'a.', 'A public declaration; an open statement; a manifesto. See\n   Manifesto.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manifest', 'a.', 'A list or invoice of a ships cargo, containing a\n   description by marks, numbers, etc., of each package of goods, to be\n   exhibited at the customhouse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manifested', 'imp. & p. p.', 'of Manifest');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manifesting', 'p. pr. & vb. n.', 'of Manifest');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manifest', 'v. t.', 'To show plainly; to make to appear distinctly, --\n   usually to the mind; to put beyond question or doubt; to display; to\n   exhibit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manifest', 'v. t.', 'To exhibit the manifests or prepared invoices of; to\n   declare at the customhouse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manifestable', 'a.', 'Such as can be manifested.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manifestation', 'n.', 'The act of manifesting or disclosing, or the state\n   of being manifested; discovery to the eye or to the understanding;\n   also, that which manifests; exhibition; display; revelation; as, the\n   manifestation of Gods power in creation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manifestible', 'a.', 'Manifestable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manifestly', 'adv.', 'In a manifest manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manifestness', 'n.', 'The quality or state of being manifest; obviousness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manifestoes', 'pl. ', 'of Manifesto');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manifesto', 'n. & a.', 'A public declaration, usually of a prince,\n   sovereign, or other person claiming large powers, showing his\n   intentions, or proclaiming his opinions and motives in reference to\n   some act done or contemplated by him; as, a manifesto declaring the\n   purpose of a prince to begin war, and explaining his motives.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manifold', 'a.', 'Various in kind or quality; many in number; numerous;\n   multiplied; complicated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manifold', 'a.', 'Exhibited at divers times or in various ways; -- used to\n   qualify nouns in the singular number.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manifold', 'n.', 'A copy of a writing made by the manifold process.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manifold', 'n.', 'A cylindrical pipe fitting, having a number of lateral\n   outlets, for connecting one pipe with several others.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manifold', 'n.', 'The third stomach of a ruminant animal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manifolded', 'imp. & p. p.', 'of Manifold');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manifolding', 'p. pr. & vb. n.', 'of Manifold');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manifold', 'v. t.', 'To take copies of by the process of manifold writing;\n   as, to manifold a letter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manifolded', 'a.', 'Having many folds, layers, or plates; as, a manifolded\n   shield.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manifoldly', 'adv.', 'In a manifold manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manifoldness', 'n.', 'Multiplicity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manifoldness', 'n.', 'A generalized concept of magnitude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maniform', 'a.', 'Shaped like the hand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maniglion', 'n.', 'Either one of two handles on the back of a piece of\n   ordnance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manihoc', 'n.', 'Alt. of Manihot');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manihot', 'n.', 'See Manioc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manikin', 'n.', 'A little man; a dwarf; a pygmy; a manakin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manikin', 'n.', 'A model of the human body, made of papier-mache or other\n   material, commonly in detachable pieces, for exhibiting the different\n   parts and organs, their relative position, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manila', 'a.', 'Alt. of Manilla');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manilla', 'a.', 'Of or pertaining to Manila or Manilla, the capital of the\n   Philippine Islands; made in, or exported from, that city.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manilio', 'n.', 'See Manilla, 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manilla', 'n.', 'A ring worn upon the arm or leg as an ornament, especially\n   among the tribes of Africa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manilla', 'n.', 'A piece of copper of the shape of a horseshoe, used as\n   money by certain tribes of the west coast of Africa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manilla', 'a.', 'Same as Manila.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manille', 'n.', 'See 1st Manilla, 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manioc', 'n.', 'The tropical plants (Manihot utilissima, and M. Aipi), from\n   which cassava and tapioca are prepared; also, cassava.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maniple', 'a.', 'A handful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maniple', 'a.', 'A division of the Roman army numbering sixty men exclusive\n   of officers, any small body of soldiers; a company.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maniple', 'a.', 'Originally, a napkin; later, an ornamental band or scarf\n   worn upon the left arm as a part of the vestments of a priest in the\n   Roman Catholic Church. It is sometimes worn in the English Church\n   service.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manipular', 'a.', 'Of or pertaining to the maniple, or company.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manipular', 'a.', 'Manipulatory; as, manipular operations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manipulated', 'imp. & p. p.', 'of Manipulate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manipulating', 'p. pr. & vb. n.', 'of Manipulate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manipulate', 'v. t.', 'To treat, work, or operate with the hands,\n   especially when knowledge and dexterity are required; to manage in hand\n   work; to handle; as, to manipulate scientific apparatus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manipulate', 'v. t.', 'To control the action of, by management; as, to\n   manipulate a convention of delegates; to manipulate the stock market;\n   also, to manage artfully or fraudulently; as, to manipulate accounts,\n   or election returns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manipulate', 'v. i.', 'To use the hands in dexterous operations; to do hand\n   work; specifically, to manage the apparatus or instruments used in\n   scientific work, or in artistic or mechanical processes; also,\n   specifically, to use the hand in mesmeric operations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manipulation', 'n.', 'The act or process of manipulating, or the state of\n   being manipulated; the act of handling work by hand; use of the hands,\n   in an artistic or skillful manner, in science or art.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manipulation', 'n.', 'The use of the hands in mesmeric operations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manipulation', 'n.', 'Artful management; as, the manipulation of political\n   bodies; sometimes, a management or treatment for purposes of deception\n   or fraud.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manipulative', 'a.', 'Of or pertaining to manipulation; performed by\n   manipulation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manipulator', 'n.', 'One who manipulates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manipulatory', 'a.', 'Of or pertaining to manipulation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manis', 'n.', 'A genus of edentates, covered with large, hard, triangular\n   scales, with sharp edges that overlap each other like tiles on a roof.\n   They inhabit the warmest parts of Asia and Africa, and feed on ants.\n   Called also Scaly anteater. See Pangolin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manito', 'n.', 'Alt. of Manitu');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manitou', 'n.', 'Alt. of Manitu');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manitu', 'n.', 'A name given by tribes of American Indians to a great\n   spirit, whether good or evil, or to any object of worship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manitrunk', 'n.', 'The anterior segment of the thorax in insects. See\n   Insect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mankind', 'n.', 'The human race; man, taken collectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mankind', 'n.', 'Men, as distinguished from women; the male portion of\n   human race.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mankind', 'n.', 'Human feelings; humanity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mankind', 'a.', 'Manlike; not womanly; masculine; bold; cruel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manks', 'a.', 'Of or pertaining to the language or people of the of Man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manks', 'n.', 'The language spoken in the Isle of Man. See Manx.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manless', 'a.', 'Destitute of men.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manless', 'a.', 'Unmanly; inhuman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manlessly', 'adv.', 'Inhumanly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manlike', 'a.', 'Like man, or like a man, in form or nature; having the\n   qualities of a man, esp. the nobler qualities; manly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manliness', 'n.', 'The quality or state of being manly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manling', 'n.', 'A little man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manly', 'superl.', 'Having qualities becoming to a man; not childish or\n   womanish; manlike, esp. brave, courageous, resolute, noble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manly', 'adv.', 'In a manly manner; with the courage and fortitude of a\n   manly man; as, to act manly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manna', 'n.', 'The food supplied to the Israelites in their journey through\n   the wilderness of Arabia; hence, divinely supplied food.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manna', 'n.', 'A name given to lichens of the genus Lecanora, sometimes\n   blown into heaps in the deserts of Arabia and Africa, and gathered and\n   used as food.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manna', 'n.', 'A sweetish exudation in the form of pale yellow friable\n   flakes, coming from several trees and shrubs and used in medicine as a\n   gentle laxative, as the secretion of Fraxinus Ornus, and F.\n   rotundifolia, the manna ashes of Southern Europe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manna croup', '', 'The portions of hard wheat kernels not ground into flour\n   by the millstones: a kind of semolina prepared in Russia and used for\n   puddings, soups, etc. -- called also manna groats.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manna croup', '', 'The husked grains of manna grass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manner', 'n.', 'Mode of action; way of performing or effecting anything;\n   method; style; form; fashion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manner', 'n.', 'Characteristic mode of acting, conducting, carrying ones\n   self, or the like; bearing; habitual style.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manner', 'n.', 'Customary method of acting; habit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manner', 'n.', 'Carriage; behavior; deportment; also, becoming behavior;\n   well-bred carriage and address.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manner', 'n.', 'The style of writing or thought of an author;\n   characteristic peculiarity of an artist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manner', 'n.', 'Certain degree or measure; as, it is in a manner done\n   already.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manner', 'n.', 'Sort; kind; style; -- in this application sometimes having\n   the sense of a plural, sorts or kinds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mannered', 'a.', 'Having a certain way, esp. a polite way, of carrying and\n   conducting ones self.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mannered', 'a.', 'Affected with mannerism; marked by excess of some\n   characteristic peculiarity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mannerism', 'n.', 'Adherence to a peculiar style or manner; a\n   characteristic mode of action, bearing, or treatment, carried to\n   excess, especially in literature or art.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mannerist', 'n.', 'One addicted to mannerism; a person who, in action,\n   bearing, or treatment, carries characteristic peculiarities to excess.\n   See citation under Mannerism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mannerliness', 'n.', 'The quality or state of being mannerly; civility;\n   complaisance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mannerly', 'a.', 'Showing good manners; civil; respectful; complaisant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mannerly', 'adv.', 'With good manners.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mannheim gold', '', 'A kind of brass made in imitation of gold. It contains\n   eighty per cent of copper and twenty of zinc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mannide', 'n.', 'A white amorphous or crystalline substance, obtained by\n   dehydration of mannite, and distinct from, but convertible into,\n   mannitan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mannish', 'a.', 'Resembling a human being in form or nature; human.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mannish', 'a.', 'Resembling, suitable to, or characteristic of, a man,\n   manlike, masculine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mannish', 'a.', 'Fond of men; -- said of a woman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mannitan', 'n.', 'A white amorphous or crystalline substance obtained by\n   the partial dehydration of mannite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mannitate', 'n.', 'A salt of mannitic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mannite', 'n.', 'A white crystalline substance of a sweet taste obtained\n   from a so-called manna, the dried sap of the flowering ash (Fraxinus\n   ornus); -- called also mannitol, and hydroxy hexane. Cf. Dulcite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mannite', 'n.', 'A sweet white efflorescence from dried fronds of kelp,\n   especially from those of the Laminaria saccharina, or devils apron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mannitic', 'a.', 'Of, pertaining to, resembling, or derived from, mannite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mannitol', 'n.', 'The technical name of mannite. See Mannite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mannitose', 'n.', 'A variety of sugar obtained by the partial oxidation of\n   mannite, and closely resembling levulose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manoeuvre', 'n. & v.', 'See Maneuver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Men-of-war', 'pl. ', 'of Manofwar');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manofwar', 'n', 'A government vessel employed for the purposes of war, esp.\n   one of large size; a ship of war.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manometer', 'n.', 'An instrument for measuring the tension or elastic force\n   of gases, steam, etc., constructed usually on the principle of allowing\n   the gas to exert its elastic force in raising a column of mercury in an\n   open tube, or in compressing a portion of air or other gas in a closed\n   tube with mercury or other liquid intervening, or in bending a metallic\n   or other spring so as to set in motion an index; a pressure gauge. See\n   Pressure, and Illust. of Air pump.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manometric', 'a.', 'Alt. of Manometrical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manometrical', 'a.', 'Of or pertaining to the manometer; made by the\n   manometer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manor', 'n.', 'The land belonging to a lord or nobleman, or so much land as\n   a lord or great personage kept in his own hands, for the use and\n   subsistence of his family.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manor', 'n.', 'A tract of land occupied by tenants who pay a free-farm rent\n   to the proprietor, sometimes in kind, and sometimes by performing\n   certain stipulated services.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manorial', 'a.', 'Of or pertaining to a manor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manoscope', 'n.', 'Same as Manometer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manoscopy', 'n.', 'The science of the determination of the density of\n   vapors and gases.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manovery', 'n.', 'A contrivance or maneuvering to catch game illegally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manqueller', 'n.', 'A killer of men; a manslayer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manred', 'n.', 'Alt. of Manrent');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manrent', 'n.', 'Homage or service rendered to a superior, as to a lord;\n   vassalage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manrope', 'n.', 'One of the side ropes to the gangway of a ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mansard roof', '', 'A hipped curb roof; that is, a roof having on all sides\n   two slopes, the lower one being steeper than the upper one.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manse', 'n.', 'A dwelling house, generally with land attached.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manse', 'n.', 'The parsonage; a clergymans house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manservant', 'n.', 'A male servant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mansion', 'n.', 'A dwelling place, -- whether a part or whole of a house or\n   other shelter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mansion', 'n.', 'The house of the lord of a manor; a manor house; hence:\n   Any house of considerable size or pretension.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mansion', 'n.', 'A twelfth part of the heavens; a house. See 1st House, 8.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mansion', 'n.', 'The place in the heavens occupied each day by the moon in\n   its monthly revolution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mansion', 'v. i.', 'To dwell; to reside.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mansionary', 'a.', 'Resident; residentiary; as, mansionary canons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mansionry', 'n.', 'The state of dwelling or residing; occupancy as a\n   dwelling place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manslaughter', 'n.', 'The slaying of a human being; destruction of men.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manslaughter', 'n.', 'The unlawful killing of a man, either in negligenc/\n   or incidentally to the commission of some unlawful act, but without\n   specific malice, or upon a sudden excitement of anger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manslayer', 'n.', 'One who kills a human being; one who commits\n   manslaughter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manstealer', 'n.', 'A person who steals or kidnaps a human being or beings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manstealing', 'n.', 'The act or business of stealing or kidnaping human\n   beings, especially with a view to e/slave them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mansuete', 'a.', 'Tame; gentle; kind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mansuetude', 'n.', 'Tameness; gentleness; mildness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manswear', 'v. i.', 'To swear falsely. Same as Mainswear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manta', 'n.', 'See Coleoptera and Sea devil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mantchoo', 'a. & n.', 'Same as Manchu.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manteaux', 'pl. ', 'of Manteau');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manteaus', 'pl. ', 'of Manteau');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manteau', 'n.', 'A womans cloak or mantle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manteau', 'n.', 'A gown worn by women.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mantel', 'n.', 'The finish around a fireplace, covering the chimney-breast\n   in front and sometimes on both sides; especially, a shelf above the\n   fireplace, and its supports.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mantelet', 'n.', 'A short cloak formerly worn by knights.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mantelet', 'n.', 'A short cloak or mantle worn by women.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mantelet', 'n.', 'A musket-proof shield of rope, wood, or metal, which is\n   sometimes used for the protection of sappers or riflemen while\n   attacking a fortress, or of gunners at embrasures; -- now commonly\n   written mantlet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mantelpiece', 'n.', 'Same as Mantel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mantelshelf', 'n.', 'The shelf of a mantel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manteltree', 'n.', 'The lintel of a fireplace when of wood, as frequently\n   in early houses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mantic', 'a.', 'Of or pertaining to divination, or to the condition of one\n   inspired, or supposed to be inspired, by a deity; prophetic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mantilla', 'n.', 'A ladys light cloak of cape of silk, velvet, lace, or\n   the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mantilla', 'n.', 'A kind of veil, covering the head and falling down upon\n   the shoulders; -- worn in Spain, Mexico, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mantis', 'n.', 'Any one of numerous species of voracious orthopterous\n   insects of the genus Mantis, and allied genera. They are remarkable for\n   their slender grotesque forms, and for holding their stout anterior\n   legs in a manner suggesting hands folded in prayer. The common American\n   species is M. Carolina.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mantispid', 'n.', 'Any neuropterous insect of the genus Mantispa, and\n   allied genera. The larvae feed on plant lice. Also used adjectively.\n   See Illust. under Neuroptera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mantissa', 'n.', 'The decimal part of a logarithm, as distinguished from\n   the integral part, or characteristic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mantle', 'n.', 'A loose garment to be worn over other garments; an\n   enveloping robe; a cloak. Hence, figuratively, a covering or concealing\n   envelope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mantle', 'n.', 'Same as Mantling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mantle', 'n.', 'The external fold, or folds, of the soft, exterior membrane\n   of the body of a mollusk. It usually forms a cavity inclosing the\n   gills. See Illusts. of Buccinum, and Byssus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mantle', 'n.', 'Any free, outer membrane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mantle', 'n.', 'The back of a bird together with the folded wings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mantle', 'n.', 'A mantel. See Mantel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mantle', 'n.', 'The outer wall and casing of a blast furnace, above the\n   hearth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mantle', 'n.', 'A penstock for a water wheel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mantled', 'imp. & p. p.', 'of Mantle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mantling', 'p. pr. & vb. n.', 'of Mantle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mantle', 'v. t.', 'To cover or envelop, as with a mantle; to cloak; to\n   hide; to disguise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mantle', 'v. i.', 'To unfold and spread out the wings, like a mantle; --\n   said of hawks. Also used figuratively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mantle', 'v. i.', 'To spread out; -- said of wings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mantle', 'v. i.', 'To spread over the surface as a covering; to overspread;\n   as, the scum mantled on the pool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mantle', 'v. i.', 'To gather, assume, or take on, a covering, as froth,\n   scum, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mantlet', 'n.', 'See Mantelet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mantling', 'n.', 'The representation of a mantle, or the drapery behind and\n   around a coat of arms: -- called also lambrequin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manto', 'n.', 'See Manteau.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mantologist', 'n.', 'One who is skilled in mantology; a diviner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mantology', 'n.', 'The act or art of divination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mantra', 'n.', 'A prayer; an invocation; a religious formula; a charm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mantrap', 'n.', 'A trap for catching trespassers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mantrap', 'n.', 'A dangerous place, as an open hatch, into which one may\n   fall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mantua', 'n.', 'A superior kind of rich silk formerly exported from Mantua\n   in Italy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mantua', 'n.', 'A womans cloak or mantle; also, a womans gown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mantuamaker', 'n.', 'One who makes dresses, cloaks, etc., for women; a\n   dressmaker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mantuan', 'a.', 'Of or pertaining to Mantua.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mantuan', 'n.', 'A native or inhabitant of Mantua.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manu', 'n.', 'One of a series of progenitors of human beings, and authors\n   of human wisdom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manual', 'a.', 'Of or pertaining to the hand; done or made by the hand; as,\n   manual labor; the kings sign manual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manual', 'a.', 'A small book, such as may be carried in the hand, or\n   conveniently handled; a handbook; specifically, the service book of the\n   Roman Catholic Church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manual', 'a.', 'A keyboard of an organ or harmonium for the fingers, as\n   distinguished from the pedals; a clavier, or set of keys.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manual', 'a.', 'A prescribed exercise in the systematic handing of a\n   weapon; as, the manual of arms; the manual of the sword; the manual of\n   the piece (cannon, mortar, etc.).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manualist', 'n.', 'One who works with the hands; an artificer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manually', 'adv.', 'By hand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manuary', 'a.', 'Manual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manuary', 'n.', 'An artificer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manubial', 'a.', 'Belonging to spoils; taken in war.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manubrial', 'a.', 'Of or pertaining to a manubrium; shaped like a\n   manubrium; handlelike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manubria', 'pl. ', 'of Manubrium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manubriums', 'pl. ', 'of Manubrium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manubrium', 'n.', 'A handlelike process or part; esp., the anterior segment\n   of the sternum, or presternum, and the handlelike process of the\n   malleus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manubrium', 'n.', 'The proboscis of a jellyfish; -- called also hypostoma.\n   See Illust. of Hydromedusa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manucode', 'n.', 'Any bird of the genus Manucodia, of Australia and New\n   Guinea. They are related to the bird of paradise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manuducent', 'n.', 'One who leads by the hand; a manuductor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manuduction', 'n.', 'Guidance by the hand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manductor', 'n.', 'A conductor; an officer in the ancient church who gave\n   the signal for the choir to sing, and who beat time with the hand, and\n   regulated the music.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-ries', 'pl. ', 'of Manufactory');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manufactory', 'n.', 'Manufacture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manufactory', 'n.', 'A building or place where anything is manufactured; a\n   factory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manufactory', 'a.', 'Pertaining to manufacturing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manufactural', 'a.', 'Of or pertaining to manufactures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manufacture', 'n.', 'The operation of making wares or any products by hand,\n   by machinery, or by other agency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manufacture', 'n.', 'Anything made from raw materials by the hand, by\n   machinery, or by art, as cloths, iron utensils, shoes, machinery,\n   saddlery, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manufactured', 'imp. & p. p.', 'of Manufacture');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manufacturing', 'p. pr. & vb. n.', 'of Manufacture');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manufacture', 'v. t.', 'To make (wares or other products) by hand, by\n   machinery, or by other agency; as, to manufacture cloth, nails, glass,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manufacture', 'v. t.', 'To work, as raw or partly wrought materials, into\n   suitable forms for use; as, to manufacture wool, cotton, silk, or iron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manufacture', 'v. i.', 'To be employed in manufacturing something.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manufacturer', 'n.', 'One who manufactures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manufacturing', 'a.', 'Employed, or chiefly employed, in manufacture; as, a\n   manufacturing community; a manufacturing town.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manufacturing', 'a.', 'Pertaining to manufacture; as, manufacturing\n   projects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manul', 'n.', 'A wild cat (Felis manul), having long, soft, light-colored\n   fur. It is found in the mountains of Central Asia, and dwells among\n   rocks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manumise', 'v. t.', 'To manumit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manumission', 'n.', 'The act of manumitting, or of liberating a slave from\n   bondage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manumitted', 'imp. & p. p.', 'of Manumit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manumitting', 'p. pr. & vb. n.', 'of Manumit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manumit', 'v. t.', 'To release from slavery; to liberate from personal\n   bondage or servitude; to free, as a slave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manumotive', 'a.', 'Movable by hand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manumotor', 'n.', 'A small wheel carriage, so constructed that a person\n   sitting in it may move it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manurable', 'a.', 'Capable of cultivation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manurable', 'a.', 'Capable of receiving a fertilizing substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manurage', 'n.', 'Cultivation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manurance', 'n.', 'Cultivation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manured', 'imp. & p. p.', 'of Manure');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manuring', 'p. pr. & vb. n.', 'of Manure');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manure', 'v. t.', 'To cultivate by manual labor; to till; hence, to develop\n   by culture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manure', 'v. t.', 'To apply manure to; to enrich, as land, by the\n   application of a fertilizing substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manure', 'n.', 'Any matter which makes land productive; a fertilizing\n   substance, as the contents of stables and barnyards, dung, decaying\n   animal or vegetable substances, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manurement', 'n.', 'Cultivation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manurer', 'n.', 'One who manures land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manurial', 'a.', 'Relating to manures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manuring', 'n.', 'The act of process of applying manure; also, the manure\n   applied.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manus', 'pl. ', 'of Manus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manus', 'n.', 'The distal segment of the fore limb, including the carpus\n   and fore foot or hand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manuscript', 'a.', 'Written with or by the hand; not printed; as, a\n   manuscript volume.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manuscript', 'a.', 'A literary or musical composition written with the\n   hand, as distinguished from a printed copy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manuscript', 'a.', 'Writing, as opposed to print; as, the book exists only\n   in manuscript.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manuscriptal', 'a.', 'Manuscript.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manutenency', 'n.', 'Maintenance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manway', 'n.', 'A small passageway, as in a mine, that a man may pass\n   through.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manx', 'a.', 'Of or pertaining to the Isle of Man, or its inhabitants; as,\n   the Manx language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manx', 'n.', 'The language of the inhabitants of the Isle of Man, a dialect\n   of the Celtic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Many', 'n.', 'A retinue of servants; a household.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Many', 'a. / pron.', 'Consisting of a great number; numerous; not few.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Many', 'a.', 'The populace; the common people; the majority of people, or\n   of a community.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Many', 'a.', 'A large or considerable number.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Many-minded', 'a.', 'Having many faculties; versatile; many-sided.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manyplies', 'n.', 'The third division, or that between the reticulum, or\n   honeycomb stomach, and the abomasum, or rennet stomach, in the stomach\n   of ruminants; the omasum; the psalterium. So called from the numerous\n   folds in its mucous membrane. See Illust of Ruminant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Many-sided', 'a.', 'Having many sides; -- said of figures. Hence,\n   presenting many questions or subjects for consideration; as, a\n   many-sided topic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Many-sided', 'a.', 'Interested in, and having an aptitude for, many unlike\n   pursuits or objects of attention; versatile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manyways', 'adv.', 'Alt. of Manywise');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manywise', 'adv.', 'In many different ways; variously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Manzanita', 'n.', 'A name given to several species of Arctostaphylos, but\n   mostly to A. glauca and A. pungens, shrubs of California, Oregon, etc.,\n   with reddish smooth bark, ovate or oval coriaceous evergreen leaves,\n   and bearing clusters of red berries, which are said to be a favorite\n   food of the grizzly bear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maoris', 'pl. ', 'of Maori');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maori', 'n.', 'One of the aboriginal inhabitants of New Zealand; also, the\n   original language of New Zealand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maori', 'a.', 'Of or pertaining to the Maoris or to their language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Map', 'n.', 'A representation of the surface of the earth, or of some\n   portion of it, showing the relative position of the parts represented;\n   -- usually on a flat surface. Also, such a representation of the\n   celestial sphere, or of some part of it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Map', 'n.', 'Anything which represents graphically a succession of events,\n   states, or acts; as, an historical map.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mapped', 'imp. & p. p.', 'of Map');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mapping', 'p. pr. & vb. n.', 'of Map');
  INSERT INTO words (word, word_type, description) VALUES  (    'Map', 'v. t.', 'To represent by a map; -- often with out; as, to survey and\n   map, or map out, a county. Hence, figuratively: To represent or\n   indicate systematically and clearly; to sketch; to plan; as, to map, or\n   map out, a journey; to map out business.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mapach', 'n.', 'The raccoon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maple', 'n.', 'A tree of the genus Acer, including about fifty species. A.\n   saccharinum is the rock maple, or sugar maple, from the sap of which\n   sugar is made, in the United States, in great quantities, by\n   evaporation; the red or swamp maple is A. rubrum; the silver maple, A.\n   dasycarpum, having fruit wooly when young; the striped maple, A.\n   Pennsylvanium, called also moosewood. The common maple of Europe is A.\n   campestre, the sycamore maple is A. Pseudo-platanus, and the Norway\n   maple is A. platanoides.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maplike', 'a.', 'Having or consisting of lines resembling a map; as, the\n   maplike figures in which certain lichens grow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mappery', 'n.', 'The making, or study, of maps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maqui', 'n.', 'A Chilian shrub (Aristotelia Maqui). Its bark furnishes\n   strings for musical instruments, and a medicinal wine is made from its\n   berries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mar', 'n.', 'A small lake. See Mere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marred', 'imp. & p. p.', 'of Mar');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marring', 'p. pr. & vb. n.', 'of Mar');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mar', 'v.', 'To make defective; to do injury to, esp. by cutting off or\n   defacing a part; to impair; to disfigure; to deface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mar', 'v.', 'To spoil; to ruin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mar', 'n.', 'A mark or blemish made by bruising, scratching, or the like; a\n   disfigurement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mara', 'n.', 'The principal or ruling evil spirit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mara', 'n.', 'A female demon who torments people in sleep by crouching on\n   their chests or stomachs, or by causing terrifying visions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mara', 'n.', 'The Patagonian cavy (Dolichotis Patagonicus).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marabou', 'n.', 'A large stork of the genus Leptoptilos (formerly Ciconia),\n   esp. the African species (L. crumenifer), which furnishes plumes worn\n   as ornaments. The Asiatic species (L. dubius, or L. argala) is the\n   adjutant. See Adjutant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marabou', 'n.', 'One having five eighths negro blood; the offspring of a\n   mulatto and a griffe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marabout', 'n.', 'A Mohammedan saint; especially, one who claims to work\n   cures supernaturally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maracan', 'n.', 'A macaw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marai', 'n.', 'A sacred inclosure or temple; -- so called by the islanders\n   of the Pacific Ocean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maranatha', 'n.', '\"Our Lord cometh;\" -- an expression used by St. Paul at\n   the conclusion of his first Epistle to the Corinthians (xvi. 22). This\n   word has been used in anathematizing persons for great crimes; as much\n   as to say, \"May the Lord come quickly to take vengeance of thy crimes.\"\n   See Anathema maranatha, under Anathema.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maranta', 'n.', 'A genus of endogenous plants found in tropical America,\n   and some species also in India. They have tuberous roots containing a\n   large amount of starch, and from one species (Maranta arundinacea)\n   arrowroot is obtained. Many kinds are cultivated for ornament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maraschino', 'n.', 'A liqueur distilled from fermented cherry juice, and\n   flavored with the pit of a variety of cherry which grows in Dalmatia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marasmus', 'n.', 'A wasting of flesh without fever or apparent disease; a\n   kind of consumption; atrophy; phthisis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marauded', 'imp. & p. p.', 'of Maraud');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marauding', 'p. pr. & vb. n.', 'of Maraud');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maraud', 'v. i.', 'To rove in quest of plunder; to make an excursion for\n   booty; to plunder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maraud', 'n.', 'An excursion for plundering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marauder', 'v.', 'A rover in quest of booty or plunder; a plunderer; one\n   who pillages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maravedi', 'n.', 'A small copper coin of Spain, equal to three mils\n   American money, less than a farthing sterling. Also, an ancient Spanish\n   gold coin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marble', 'n.', 'A massive, compact limestone; a variety of calcite, capable\n   of being polished and used for architectural and ornamental purposes.\n   The color varies from white to black, being sometimes yellow, red, and\n   green, and frequently beautifully veined or clouded. The name is also\n   given to other rocks of like use and appearance, as serpentine or verd\n   antique marble, and less properly to polished porphyry, granite, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marble', 'n.', 'A thing made of, or resembling, marble, as a work of art,\n   or record, in marble; or, in the plural, a collection of such works;\n   as, the Arundel or Arundelian marbles; the Elgin marbles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marble', 'n.', 'A little ball of marble, or of some other hard substance,\n   used as a plaything by children; or, in the plural, a childs game\n   played with marbles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marble', 'a.', 'Made of, or resembling, marble; as, a marble mantel; marble\n   paper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marble', 'a.', 'Cold; hard; unfeeling; as, a marble breast or heart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marbled', 'imp. & p. p.', 'of Marble');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marbling', 'p. pr. & vb. n.', 'of Marble');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marble', 'n.', 'To stain or vein like marble; to variegate in color; as, to\n   marble the edges of a book, or the surface of paper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marbled', 'a.', 'Made of, or faced with, marble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marbled', 'a.', 'Made to resemble marble; veined or spotted like marble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marbled', 'a.', 'Varied with irregular markings, or witch a confused\n   blending of irregular spots and streaks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marble-edged', 'a.', 'Having the edge veined or spotted with different\n   colors like marble, as a book.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marbleized', 'imp. & p. p.', 'of Marbleize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marbleizing', 'p. pr. & vb. n.', 'of Marbleize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marbleize', 'v. t.', 'To stain or grain in imitation of marble; to cover\n   with a surface resembling marble; as, to marbleize slate, wood, or\n   iron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marbler', 'n.', 'One who works upon marble or other stone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marbler', 'n.', 'One who colors or stains in imitation of marble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marbling', 'n.', 'The art or practice of variegating in color, in imitation\n   of marble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marbling', 'n.', 'An intermixture of fat and lean in meat, giving it a\n   marbled appearance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marbling', 'n.', 'Distinct markings resembling the variegations of marble,\n   as on birds and insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marbly', 'a.', 'Containing, or resembling, marble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marbrinus', 'n.', 'A cloth woven so as to imitate the appearance of marble;\n   -- much used in the 15th and 16th centuries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marc', 'n.', 'The refuse matter which remains after the pressure of fruit,\n   particularly of grapes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marc', 'n.', 'A weight of various commodities, esp. of gold and silver,\n   used in different European countries. In France and Holland it was\n   equal to eight ounces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marc', 'n.', 'A coin formerly current in England and Scotland, equal to\n   thirteen shillings and four pence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marc', 'n.', 'A German coin and money of account. See Mark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marcantant', 'n.', 'A merchant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marcasite', 'n.', 'A sulphide of iron resembling pyrite or common iron\n   pyrites in composition, but differing in form; white iron pyrites.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marcasitic', 'a.', 'Alt. of Marcasitical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marcasitical', 'a.', 'Containing, or having the nature of, marcasite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marcassin', 'n.', 'A young wild boar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marcato', 'a.', 'In a marked emphatic manner; -- used adverbially as a\n   direction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marceline', 'n.', 'A thin silk fabric used for linings, etc., in ladies\n   dresses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marcescent', 'a.', 'Withering without/ falling off; fading; decaying.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marcescible', 'a.', 'Li/ble to wither or decay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'March', 'n.', 'The third month of the year, containing thirty-one days.');
  INSERT INTO words (word, word_type, description) VALUES  (    'March', 'n.', 'A territorial border or frontier; a region adjacent to a\n   boundary line; a confine; -- used chiefly in the plural, and in English\n   history applied especially to the border land on the frontiers between\n   England and Scotland, and England and Wales.');
  INSERT INTO words (word, word_type, description) VALUES  (    'March', 'v. i.', 'To border; to be contiguous; to lie side by side.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marched', 'imp. & p. p.', 'of March');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marching', 'p. pr. & vb. n.', 'of March');
  INSERT INTO words (word, word_type, description) VALUES  (    'March', 'v. i.', 'To move with regular steps, as a soldier; to walk in a\n   grave, deliberate, or stately manner; to advance steadily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'March', 'v. i.', 'To proceed by walking in a body or in military order; as,\n   the German army marched into France.');
  INSERT INTO words (word, word_type, description) VALUES  (    'March', 'v. t.', 'TO cause to move with regular steps in the manner of a\n   soldier; to cause to move in military array, or in a body, as troops;\n   to cause to advance in a steady, regular, or stately manner; to cause\n   to go by peremptory command, or by force.');
  INSERT INTO words (word, word_type, description) VALUES  (    'March', 'n.', 'The act of marching; a movement of soldiers from one\n   stopping place to another; military progress; advance of troops.');
  INSERT INTO words (word, word_type, description) VALUES  (    'March', 'n.', 'Hence: Measured and regular advance or movement, like that\n   of soldiers moving in order; stately or deliberate walk; steady onward\n   movement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'March', 'n.', 'The distance passed over in marching; as, an hours march; a\n   march of twenty miles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'March', 'n.', 'A piece of music designed or fitted to accompany and guide\n   the movement of troops; a piece of music in the march form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macher', 'n.', 'One who marches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marcher', 'n.', 'The lord or officer who defended the marches or borders of\n   a territory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marchet', 'n.', 'Alt. of Merchet');
  INSERT INTO words (word, word_type, description) VALUES  (    'Merchet', 'n.', 'In old English and in Scots law, a fine paid to the lord\n   of the soil by a tenant upon the marriage of one the tenants\n   daughters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marching', '', 'a. & n., fr. March, v.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marchioness', 'n.', 'The wife or the widow of a marquis; a woman who has\n   the rank and dignity of a marquis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'March-mad', 'a.', 'Extremely rash; foolhardy. See under March, the month.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marchman', 'n.', 'A person living in the marches between England and\n   Scotland or Wales.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marchpane', 'n.', 'A kind of sweet bread or biscuit; a cake of pounded\n   almonds and sugar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'March-ward', 'n.', 'A warden of the marches; a marcher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marcian', 'a.', 'Under the influence of Mars; courageous; bold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marcid', 'a.', 'Pining; lean; withered.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marcid', 'a.', 'Characterized by emaciation, as a fever.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marcidity', 'n.', 'The state or quality of being withered or lean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marcionite', 'n.', 'A follower of Marcion, a Gnostic of the second century,\n   who adopted the Oriental notion of the two conflicting principles, and\n   imagined that between them there existed a third power, neither wholly\n   good nor evil, the Creator of the world and of man, and the God of the\n   Jewish dispensation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marcobrunner', 'n.', 'A celebrated Rhine wine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marcor', 'n.', 'A wasting away of flesh; decay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marcosian', 'n.', 'One of a Gnostic sect of the second century, so called\n   from Marcus, an Egyptian, who was reputed to be a margician.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mardi gras', 'n.', 'The last day of Carnival; Shrove Tuesday; -- in some\n   cities a great day of carnival and merrymaking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mare', 'n.', 'The female of the horse and other equine quadrupeds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mare', 'n.', 'Sighing, suffocative panting, intercepted utterance, with a\n   sense of pressure across the chest, occurring during sleep; the\n   incubus; -- obsolete, except in the compound nightmare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marechal Niel', '', 'A kind of large yellow rose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mareis', 'n.', 'A Marsh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marena', 'n.', 'A European whitefish of the genus Coregonus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mareschal', 'n.', 'A military officer of high rank; a marshal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mares-nest', 'n.', 'A supposed discovery which turns out to be a hoax;\n   something grosaly absurd.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mares-tail', 'n.', 'A long streaky cloud, spreading out like a horses\n   tail, and believed to indicate rain; a cirrus cloud. See Cloud.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mares-tail', 'n.', 'An aquatic plant of the genus Hippuris (H. vulgaris),\n   having narrow leaves in whorls.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Margarate', 'n.', 'A compound of the so-called margaric acid with a base.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Margaric', 'a.', 'Pertaining to, or resembling, pearl; pearly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Margarin', 'n.', 'A fatty substance, extracted from animal fats and certain\n   vegetable oils, formerly supposed to be a definite compound of glycerin\n   and margaric acid, but now known to be simply a mixture or combination\n   of tristearin and teipalmitin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marasritaceous', 'a.', 'Pertaining to, or resembling, pearl; pearly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Margarite', 'n.', 'A pearl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Margarite', 'n.', 'A mineral related to the micas, but low in silica and\n   yielding brittle folia with pearly luster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Margaritic', 'a.', 'Margaric.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Margaritiferous', 'a.', 'Producing pearls.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Margarodite', 'n.', 'A hidrous potash mica related to muscovite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Margarone', 'n.', 'The ketone of margaric acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Margarous', 'a.', 'Margaric; -- formerly designating a supposed acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Margate fish', '', 'A sparoid fish (Diabasis aurolineatus) of the Gulf of\n   Mexico, esteemed as a food fish; -- called also red-mouth grunt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Margay', 'n.', 'An American wild cat (Felis tigrina), ranging from Mexico\n   to Brazil. It is spotted with black. Called also long-tailed cat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marge', 'n.', 'Border; margin; edge; verge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Margent', 'n.', 'A margin; border; brink; edge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Margent', 'v. t.', 'To enter or note down upon the margin of a page; to\n   margin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Margin', 'n.', 'A border; edge; brink; verge; as, the margin of a river or\n   lake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Margin', 'n.', 'Specifically: The part of a page at the edge left uncovered\n   in writing or printing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Margin', 'n.', 'The difference between the cost and the selling price of an\n   article.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Margin', 'n.', 'Something allowed, or reserved, for that which can not be\n   foreseen or known with certainty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Margin', 'n.', 'Collateral security deposited with a broker to secure him\n   from loss on contracts entered into by him on behalf of his principial,\n   as in the speculative buying and selling of stocks, wheat, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Margined', 'imp. & p. p.', 'of Margin');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marginging', 'p. pr. & vb. n.', 'of Margin');
  INSERT INTO words (word, word_type, description) VALUES  (    'Margin', 'v. t.', 'To furnish with a margin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Margin', 'v. t.', 'To enter in the margin of a page.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marginal', 'a.', 'Of or pertaining to a margin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marginal', 'a.', 'Written or printed in the margin; as, a marginal note or\n   gloss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marginalia', 'n. pl.', 'Marginal notes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marginally', 'adv.', 'In the margin of a book.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marginate', 'n.', 'Having a margin distinct in appearance or structure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marginate', 'v. t.', 'To furnish with a distinct margin; to margin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marginated', 'a.', 'Same as Marginate, a.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Margined', 'a.', 'Having a margin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Margined', 'a.', 'Bordered with a distinct line of color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marginella', 'n.', 'A genus of small, polished, marine univalve shells,\n   native of all warm seas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marginicidal', 'a.', 'Dehiscent by the separation of united carpels; --\n   said of fruits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Margosa', 'n.', 'A large tree of genus Melia (M. Azadirachta) found in\n   India. Its bark is bitter, and used as a tonic. A valuable oil is\n   expressed from its seeds, and a tenacious gum exudes from its trunk.\n   The M. Azedarach is a much more showy tree, and is cultivated in the\n   Southern United States, where it is known as Pride of India, Pride of\n   China, or bead tree. Various parts of the tree are considered\n   anthelmintic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Margravate', 'n.', 'Alt. of Margraviate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Margraviate', 'n.', 'The territory or jurisdiction of a margrave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Margrave', 'n.', 'Originally, a lord or keeper of the borders or marches in\n   Germany.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Margrave', 'n.', 'The English equivalent of the German title of nobility,\n   markgraf; a marquis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Margravine', 'n.', 'The wife of a margrave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marguerite', 'n.', 'The daisy (Bellis perennis). The name is often applied\n   also to the ox-eye daisy and to the China aster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marian', 'a.', 'Pertaining to the Virgin Mary, or sometimes to Mary, Queen\n   of England, daughter of Henry VIII.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marie', 'interj.', 'Marry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mariet', 'n.', 'A kind of bellflower, Companula Trachelium, once called\n   Viola Mariana; but it is not a violet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marigenous', 'a.', 'Produced in or by the sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marigold', 'n.', 'A name for several plants with golden yellow blossoms,\n   especially the Calendula officinalis (see Calendula), and the\n   cultivated species of Tagetes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marikina', 'n.', 'A small marmoset (Midas rosalia); the silky tamarin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marimba', 'n.', 'A musical istrument of percussion, consisting of bars\n   yielding musical tones when struck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marimonda', 'n.', 'A spider monkey (Ateles belzebuth) of Central and South\n   America.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marinade', 'n.', 'A brine or pickle containing wine and spices, for\n   enriching the flavor of meat and fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marinate', 'v. t.', 'To salt or pickle, as fish, and then preserve in oil\n   or vinegar; to prepare by the use of marinade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marine', 'a.', 'Of or pertaining to the sea; having to do with the ocean,\n   or with navigation or naval affairs; nautical; as, marine productions\n   or bodies; marine shells; a marine engine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marine', 'a.', 'Formed by the action of the currents or waves of the sea;\n   as, marine deposits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marine', 'a.', 'A solider serving on shipboard; a sea soldier; one of a\n   body of troops trained to do duty in the navy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marine', 'a.', 'The sum of naval affairs; naval economy; the department of\n   navigation and sea forces; the collective shipping of a country; as,\n   the mercantile marine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marine', 'a.', 'A picture representing some marine subject.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marined', 'a.', 'Having the lower part of the body like a fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mariner', 'n.', 'One whose occupation is to assist in navigating ships; a\n   seaman or sailor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marinership', 'n.', 'Seamanship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marinorama', 'n.', 'A representation of a sea view.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mariolater', 'n.', 'One who worships the Virgin Mary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mariolatry', 'n.', 'The worship of the Virgin Mary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marionette', 'n.', 'A puppet moved by strings, as in a puppet show.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marionette', 'n.', 'The buffel duck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mariottes law', '', 'See Boyles law, under Law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mariposa lily', '', 'One of a genus (Calochortus) of tuliplike bulbous\n   herbs with large, and often gaycolored, blossoms. Called also butterfly\n   lily. Most of them are natives of California.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mariput', 'n.', 'A species of civet; the zoril.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marish', 'n.', 'Low, wet ground; a marsh; a fen; a bog; a moor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marish', 'a.', 'Moory; fenny; boggy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marish', 'a.', 'Growing in marshes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marital', 'v.', 'Of or pertaining to a husband; as, marital rights, duties,\n   authority.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maritated', 'a.', 'Having a husband; married.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maritimal', 'a.', 'Alt. of Maritimale');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maritimale', 'a.', 'See Maritime.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maritime', 'a.', 'Bordering on, or situated near, the ocean; connected with\n   the sea by site, interest, or power; having shipping and commerce or a\n   navy; as, maritime states.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maritime', 'a.', 'Of or pertaining to the ocean; marine; pertaining to\n   navigation and naval affairs, or to shipping and commerce by sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marjoram', 'n.', 'A genus of mintlike plants (Origanum) comprising about\n   twenty-five species. The sweet marjoram (O. Majorana) is pecularly\n   aromatic and fragrant, and much used in cookery. The wild marjoram of\n   Europe and America is O. vulgare, far less fragrant than the other.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mark', 'n.', 'A license of reprisals. See Marque.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mark', 'n.', 'An old weight and coin. See Marc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mark', 'n.', 'The unit of monetary account of the German Empire, equal to\n   23.8 cents of United States money; the equivalent of one hundred\n   pfennigs. Also, a silver coin of this value.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mark', 'n.', 'A visible sign or impression made or left upon anything;\n   esp., a line, point, stamp, figure, or the like, drawn or impressed, so\n   as to attract the attention and convey some information or intimation;\n   a token; a trace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mark', 'n.', 'A character or device put on an article of merchandise by the\n   maker to show by whom it was made; a trade-mark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mark', 'n.', 'A character (usually a cross) made as a substitute for a\n   signature by one who can not write.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mark', 'n.', 'A fixed object serving for guidance, as of a ship, a\n   traveler, a surveyor, etc.; as, a seamark, a landmark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mark', 'n.', 'A trace, dot, line, imprint, or discoloration, although not\n   regarded as a token or sign; a scratch, scar, stain, etc.; as, this\n   pencil makes a fine mark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mark', 'n.', 'An evidence of presence, agency, or influence; a\n   significative token; a symptom; a trace; specifically, a permanent\n   impression of ones activity or character.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mark', 'n.', 'That toward which a missile is directed; a thing aimed at;\n   what one seeks to hit or reach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mark', 'n.', 'Attention, regard, or respect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mark', 'n.', 'Limit or standard of action or fact; as, to be within the\n   mark; to come up to the mark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mark', 'n.', 'Badge or sign of honor, rank, or official station.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mark', 'n.', 'Preeminence; high position; as, particians of mark; a fellow\n   of no mark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mark', 'n.', 'A characteristic or essential attribute; a differential.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mark', 'n.', 'A number or other character used in registring; as,\n   examination marks; a mark for tardiness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mark', 'n.', 'Image; likeness; hence, those formed in ones image;\n   children; descendants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mark', 'n.', 'One of the bits of leather or colored bunting which are\n   placed upon a sounding line at intervals of from two to five fathoms.\n   The unmarked fathoms are called \"deeps.\"');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marked', 'imp. & p. p.', 'of Mark');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marking', 'p. pr. & vb. n.', 'of Mark');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mark', 'v. t.', 'To put a mark upon; to affix a significant mark to; to\n   make recognizable by a mark; as, to mark a box or bale of merchandise;\n   to mark clothing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mark', 'v. t.', 'To be a mark upon; to designate; to indicate; -- used\n   literally and figuratively; as, this monument marks the spot where\n   Wolfe died; his courage and energy marked him for a leader.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mark', 'v. t.', 'To leave a trace, scratch, scar, or other mark, upon, or\n   any evidence of action; as, a pencil marks paper; his hobnails marked\n   the floor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mark', 'v. t.', 'To keep account of; to enumerate and register; as, to mark\n   the points in a game of billiards or cards.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mark', 'v. t.', 'To notice or observe; to give attention to; to take note\n   of; to remark; to heed; to regard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mark', 'v. i.', 'To take particular notice; to observe critically; to note;\n   to remark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Markable', 'a.', 'Remarkable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marked', 'a.', 'Designated or distinguished by, or as by, a mark; hence;\n   noticeable; conspicuous; as, a marked card; a marked coin; a marked\n   instance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Markee', 'n.', 'See Marquee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marker', 'n.', 'One who or that which marks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marker', 'n.', 'One who keeps account of a game played, as of billiards.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marker', 'n.', 'A counter used in card playing and other games.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marker', 'n.', 'The soldier who forms the pilot of a wheeling column, or\n   marks the direction of an alignment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marker', 'n.', 'An attachment to a sewing machine for marking a line on the\n   fabric by creasing it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Market', 'n.', 'A meeting together of people, at a stated time and place,\n   for the purpose of traffic (as in cattle, provisions, wares, etc.) by\n   private purchase and sale, and not by auction; as, a market is held in\n   the town every week.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Market', 'n.', 'A public place (as an open space in a town) or a large\n   building, where a market is held; a market place or market house; esp.,\n   a place where provisions are sold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Market', 'n.', 'An opportunity for selling anything; demand, as shown by\n   price offered or obtainable; a town, region, or country, where the\n   demand exists; as, to find a market for ones wares; there is no market\n   for woolen cloths in that region; India is a market for English goods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Market', 'n.', 'Exchange, or purchase and sale; traffic; as, a dull market;\n   a slow market.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Market', 'n.', 'The price for which a thing is sold in a market; market\n   price. Hence: Value; worth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Market', 'n.', 'The privelege granted to a town of having a public market.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marketed', 'imp. & p. p.', 'of Market');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marketing', 'p. pr. & vb. n.', 'of Market');
  INSERT INTO words (word, word_type, description) VALUES  (    'Market', 'v. i.', 'To deal in a market; to buy or sell; to make bargains\n   for provisions or goods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Market', 'v. t.', 'To expose for sale in a market; to traffic in; to sell\n   in a market, and in an extended sense, to sell in any manner; as, most\n   of the farmes have marketed their crops.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marketable', 'a.', 'Fit to be offered for sale in a market; such as may be\n   justly and lawfully sold; as, dacaye/ provisions are not marketable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marketable', 'a.', 'Current in market; as, marketable value.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marketable', 'a.', 'Wanted by purchasers; salable; as, furs are not\n   marketable in that country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marketableness', 'n.', 'Quality of being marketable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marketer', 'n.', 'One who attends a market to buy or sell; one who carries\n   goods to market.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marketing', 'n.', 'The act of selling or of purchasing in, or as in, a\n   market.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marketing', 'n.', 'Articles in, or from, a market; supplies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marketstead', 'n.', 'A market place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Markhoor', 'n.', 'A large wild goat (Capra megaceros), having huge\n   flattened spiral horns. It inhabits the mountains of Northern India and\n   Cashmere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marking', 'n.', 'The act of one who, or that which, marks; the mark or\n   marks made; arrangement or disposition of marks or coloring; as, the\n   marking of a birds plumage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Markis', 'n.', 'A marquis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Markisesse', 'n.', 'A marchioness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Markman', 'n.', 'A marksman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marksmen', 'pl. ', 'of Marksman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marksman', 'n.', 'One skillful to hit a mark with a missile; one who shoots\n   well.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marksman', 'n.', 'One who makes his mark, instead of writing his name, in\n   signing documents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marksmanship', 'n.', 'Skill of a marksman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marl', 'v. t.', 'To cover, as part of a rope, with marline, marking a\n   pecular hitch at each turn to prevent unwinding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marl', 'n.', 'A mixed earthy substance, consisting of carbonate of lime,\n   clay, and sand, in very varivble proportions, and accordingly\n   designated as calcareous, clayey, or sandy. See Greensand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marled', 'imp. & p. p.', 'of Marl');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marling', 'p. pr. & vb. n.', 'of Marl');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marl', 'n.', 'To overspread or manure with marl; as, to marl a field.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marlaceous', 'a.', 'Resembling marl; partaking of the qualities of marl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marlin', 'n.', 'The American great marbled godwit (Limosa fedoa). Applied\n   also to the red-breasted godwit (Limosa haematica).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marline', 'v.', 'A small line composed of two strands a little twisted,\n   used for winding around ropes and cables, to prevent their being\n   weakened by fretting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marline', 'v. t.', 'To wind marline around; as, to marline a rope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marlite', 'n.', 'A variety of marl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marlitic', 'a.', 'Partaking of the qualites of marlite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marlpit', 'n.', 'Apit where marl is dug.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marlstone', 'n.', 'A sandy calcareous straum, containing, or impregnated\n   with, iron, and lying between the upper and lower Lias of England.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marly', 'superl.', 'Consisting or partaking of marl; resembling marl;\n   abounding with marl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marmalade', 'n.', 'A preserve or confection made of the pulp of fruit, as\n   the quince, pear, apple, orange, etc., boiled with sugar, and brought\n   to a jamlike consistence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marmalet', 'n.', 'See Marmalade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marmatite', 'n.', 'A ferruginous variety of shalerite or zinc blende,\n   nearly black in color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marmolite', 'n.', 'A thin, laminated variety of serpentine, usually of a\n   pale green color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marmoraceous', 'a.', 'Pertaining to, or like, marble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marmorate', 'a.', 'Alt. of Marmorated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marmorated', 'a.', 'Variegated like marble; covered or overlaid with\n   marble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marmoration', 'n.', 'A covering or incrusting with marble; a casing of\n   marble; a variegating so as to resemble marble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marmoratum opus', '', 'A kind of hard finish for plasterwork, made of\n   plaster of Paris and marble dust, and capable of taking a high polish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marmoreal', 'a.', 'Alt. of Marmorean');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marmorean', 'a.', 'Pertaining to, or resembling, marble; made of marble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marmorosis', 'n.', 'The metamorphism of limestone, that is, its conversion\n   into marble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marmose', 'n.', 'A species of small opossum (Didelphus murina) ranging from\n   Mexico to Brazil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marmoset', 'n.', 'Any one of numerous species of small South American\n   monkeys of the genera Hapale and Midas, family Hapalidae. They have\n   long soft fur, and a hairy, nonprehensile tail. They are often kept as\n   pets. Called also squirrel monkey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marmot', 'n.', 'Any rodent of the genus Arctomys. The common European\n   marmot (A. marmotta) is about the size of a rabbit, and inhabits the\n   higher regions of the Alps and Pyrenees. The bobac is another European\n   species. The common American species (A. monax) is the woodchuck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marmot', 'n.', 'Any one of several species of ground squirrels or gophers\n   of the genus Spermophilus; also, the prairie dog.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marmottes oil', '', 'A fine oil obtained from the kernel of Prunus\n   brigantiaca. It is used instead of olive or almond oil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marmozet', 'n.', 'See Marmoset.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marone', 'n.', 'See Maroon, the color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maronites', 'pl. ', 'of Maronite');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maronite', 'n.', 'One of a body of nominal Christians, who speak the Arabic\n   language, and reside on Mount Lebanon and in different parts of Syria.\n   They take their name from one Maron of the 6th century.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maroon', 'n.', 'In the West Indies and Guiana, a fugitive slave, or a free\n   negro, living in the mountains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marooned', 'imp. & p. p.', 'of Maroon');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marooning', 'p. pr. & vb. n.', 'of Maroon');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maroon', 'v. t.', 'To put (a person) ashore on a desolate island or coast\n   and leave him to his fate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maroon', 'a.', 'Having the color called maroon. See 4th Maroon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maroon', 'n.', 'A brownish or dull red of any description, esp. of a\n   scarlet cast rather than approaching crimson or purple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maroon', 'n.', 'An explosive shell. See Marron, 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marplot', 'n.', 'One who, by his officious /nterference, mars or frustrates\n   a design or plot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marque', 'n.', 'A license to pass the limits of a jurisdiction, or boundary\n   of a country, for the purpose of making reprisals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marquee', 'n.', 'A large field tent; esp., one adapted to the use of an\n   officer of high rank.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marquess', 'n.', 'A marquis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marquetry', 'n.', 'Inlaid work; work inlaid with pieces of wood, shells,\n   ivory, and the like, of several colors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marquis', 'n.', 'A nobleman in England, France, and Germany, of a rank next\n   below that of duke. Originally, the marquis was an officer whose duty\n   was to guard the marches or frontiers of the kingdom. The office has\n   ceased, and the name is now a mere title conferred by patent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marquisate', 'n.', 'The seigniory, dignity, or lordship of a marquis; the\n   territory governed by a marquis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marquisdom', 'n.', 'A marquisate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marquise', 'n.', 'The wife of a marquis; a marchioness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marquisship', 'n.', 'A marquisate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marram', 'n.', 'A coarse grass found on sandy beaches (Ammophila\n   arundinacea). See Beach grass, under Beach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marrer', 'n.', 'One who mars or injures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marriable', 'a.', 'Marriageable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marriage', 'v. t.', 'The act of marrying, or the state of being married;\n   legal union of a man and a woman for life, as husband and wife;\n   wedlock; matrimony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marriage', 'v. t.', 'The marriage vow or contract.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marriage', 'v. t.', 'A feast made on the occasion of a marriage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marriage', 'v. t.', 'Any intimate or close union.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marriageability', 'n.', 'The quality or state of being marriageable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marriageable', 'a.', 'Fit for, or capable of, marriage; of an age at which\n   marriage is allowable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marrried', 'a.', 'Being in the state of matrimony; wedded; as, a married\n   man or woman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marrried', 'a.', 'Of or pertaining to marriage; connubial; as, the married\n   state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marrier', 'n.', 'One who marries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marron', 'a.', 'A large chestnut.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marron', 'a.', 'A chestnut color; maroon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marron', 'a.', 'A paper or pasteboard box or shell, wound about with strong\n   twine, filled with an explosive, and ignited with a fuse, -- used to\n   make a noise like a cannon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marroon', 'n. & a.', 'Same as 1st Maroon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marrot', 'n.', 'The razor-billed auk. See Auk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marrot', 'n.', 'The common guillemot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marrot', 'n.', 'The puffin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marrow', 'n.', 'The tissue which fills the cavities of most bones; the\n   medulla. In the larger cavities it is commonly very fatty, but in the\n   smaller cavities it is much less fatty, and red or reddish in color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marrow', 'n.', 'The essence; the best part.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marrow', 'n.', 'One of a pair; a match; a companion; an intimate associate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marrowed', 'imp. & p. p.', 'of Marrow');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marrowing', 'p. pr. & vb. n.', 'of Marrow');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marrow', 'v. t.', 'To fill with, or as with, marrow of fat; to glut.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marrowbone', 'n.', 'A bone containing marrow; pl. ludicrously, knee bones\n   or knees; as, to get down on ones marrowbones, i. e., to kneel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marrowfat', 'n.', 'A rich but late variety of pea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marrowish', 'a.', 'Of the nature of, or like, marrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marrowless', 'a.', 'Destitute of marrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marrowy', 'a.', 'Full of marrow; pithy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marrubium', 'n.', 'A genus of bitter aromatic plants, sometimes used in\n   medicine; hoarhound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Married', 'imp. & p. p.', 'of Marry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marrying', 'p. pr. & vb. n.', 'of Marry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marry', 'v. t.', 'To unite in wedlock or matrimony; to perform the ceremony\n   of joining, as a man and a woman, for life; to constitute (a man and a\n   woman) husband and wife according to the laws or customs of the place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marry', 'v. t.', 'To join according to law, (a man) to a woman as his wife,\n   or (a woman) to a man as her husband. See the Note to def. 4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marry', 'v. t.', 'To dispose of in wedlock; to give away as wife.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marry', 'v. t.', 'To take for husband or wife. See the Note below.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marry', 'v. t.', 'Figuratively, to unite in the closest and most endearing\n   relation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marry', 'v. i.', 'To enter into the conjugal or connubial state; to take a\n   husband or a wife.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marry', 'interj.', 'Indeed ! in truth ! -- a term of asseveration said to\n   have been derived from the practice of swearing by the Virgin Mary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mars', 'n.', 'The god of war and husbandry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mars', 'n.', 'One of the planets of the solar system, the fourth in order\n   from the sun, or the next beyond the earth, having a diameter of about\n   4,200 miles, a period of 687 days, and a mean distance of 141,000,000\n   miles. It is conspicuous for the redness of its light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mars', 'n.', 'The metallic element iron, the symbol of which / was the same\n   as that of the planet Mars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marsala', 'n.', 'A kind of wine exported from Marsala in Sicily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marsdenia', 'n.', 'A genus of plants of the Milkweed family, mostly woody\n   climbers with fragrant flowers, several species of which furnish\n   valuable fiber, and one species (Marsdenia tinctoria) affords indigo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marseillais', 'a. f.', 'Alt. of Marseillaise');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marseillaise', 'a. f.', 'Of or pertaining to Marseilles, in France, or to\n   its inhabitants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marseillais', 'n. f.', 'Alt. of Marseillaise');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marseillaise', 'n. f.', 'A native or inhabitant of Marseilles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marseilles', 'n.', 'A general term for certain kinds of fabrics, which are\n   formed of two series of threads interlacing each other, thus forming\n   double cloth, quilted in the loom; -- so named because first made in\n   Marseilles, France.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marsh', 'n.', 'A tract of soft wet land, commonly covered partially or\n   wholly with water; a fen; a swamp; a morass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marshal', 'n.', 'Originally, an officer who had the care of horses; a\n   groom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marshal', 'n.', 'An officer of high rank, charged with the arrangement of\n   ceremonies, the conduct of operations, or the like');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marshal', 'n.', 'One who goes before a prince to declare his coming and\n   provide entertainment; a harbinger; a pursuivant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marshal', 'n.', 'One who regulates rank and order at a feast or any other\n   assembly, directs the order of procession, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marshal', 'n.', 'The chief officer of arms, whose duty it was, in ancient\n   times, to regulate combats in the lists.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marshal', 'n.', 'The highest military officer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marshal', 'n.', 'A ministerial officer, appointed for each judicial\n   district of the United States, to execute the process of the courts of\n   the United States, and perform various duties, similar to those of a\n   sheriff. The name is also sometimes applied to certain police officers\n   of a city.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marshaled', 'imp. & p. p.', 'of Marshal');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marshalled', '', 'of Marshal');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marshaling', 'p. pr. & vb. n.', 'of Marshal');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marshalling', '', 'of Marshal');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marshal', 'v. t.', 'To dispose in order; to arrange in a suitable manner;\n   as, to marshal troops or an army.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marshal', 'v. t.', 'To direct, guide, or lead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marshal', 'v. t.', 'To dispose in due order, as the different quarterings\n   on an escutcheon, or the different crests when several belong to an\n   achievement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marshaler', 'n.', 'One who marshals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marshaling', 'n.', 'The act of arranging in due order.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marshaling', 'n.', 'The arrangement of an escutcheon to exhibit the\n   alliances of the owner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marshalsea', 'n.', 'The court or seat of a marshal; hence, the prison in\n   Southwark, belonging to the marshal of the kings household.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marshalship', 'n.', 'The office of a marshal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marshbanker', 'n.', 'Alt. of Marsebanker');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marsebanker', 'n.', 'The menhaden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marshiness', 'n.', 'The state or condition of being marshy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marsh marigold', '', 'A perennial plant of the genus Caltha (C. palustris),\n   growing in wet places and bearing bright yellow flowers. In the United\n   States it is used as a pot herb under the name of cowslip. See Cowslip.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marshy', 'a.', 'Resembling a marsh; wet; boggy; fenny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marshy', 'a.', 'Pertaining to, or produced in, marshes; as, a marshy weed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marsipobranch', 'n.', 'One of the Marsipobranchia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marsipobranchia', 'n. pl.', 'A class of Vertebrata, lower than fishes,\n   characterized by their purselike gill cavities, cartilaginous\n   skeletons, absence of limbs, and a suckerlike mouth destitute of jaws.\n   It includes the lampreys and hagfishes. See Cyclostoma, and Lamprey.\n   Called also Marsipobranchiata, and Marsipobranchii.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marsupial', 'a.', 'Having a pouch for carrying the immature young; of or\n   pertaining to the Marsupialia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marsupial', 'a.', 'Of or pertaining to a marsupium; as, the marsupial\n   bones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marsupial', 'n.', 'One of the Marsupialia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marsupialia', 'n. pl.', 'A subclass of Mammalia, including nearly all the\n   mammals of Australia and the adjacent islands, together with the\n   opossums of America. They differ from ordinary mammals in having the\n   corpus callosum very small, in being implacental, and in having their\n   young born while very immature. The female generally carries the young\n   for some time after birth in an external pouch, or marsupium. Called\n   also Marsupiata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marsupialian', 'n.', 'Alt. of Marsupian');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marsupian', 'n.', 'One of the Marsupialia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marsupiate', 'a.', 'Related to or resembling the marsupials; furnished with\n   a pouch for the young, as the marsupials, and also some fishes and\n   Crustacea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marsupion', 'n.', 'Same as Marsupium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marsupite', 'n.', 'A fossil crinoid of the genus Marsupites, resembling a\n   purse in form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marsupia', 'pl. ', 'of Marsupium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marsupium', 'n.', 'The pouch, formed by a fold of the skin of the abdomen,\n   in which marsupials carry their young; also, a pouch for similar use in\n   other animals, as certain Crustacea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marsupium', 'n.', 'The pecten in the eye of birds and reptiles. See Pecten.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mart', 'n.', 'A market.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mart', 'n.', 'A bargain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mart', 'v. t.', 'To buy or sell in, or as in, a mart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mart', 'v. t.', 'To traffic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mart', 'n.', 'The god Mars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mart', 'n.', 'Battle; contest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Martagon', 'n.', 'A lily (Lilium Martagon) with purplish red flowers, found\n   in Europe and Asia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Martel', 'v. i.', 'To make a blow with, or as with, a hammer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Martel de fer', '', 'A weapon resembling a hammer, often having one side of\n   the head pointed; -- used by horsemen in the Middle Ages to break\n   armor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marteline', 'n.', 'A small hammer used by marble workers and sculptors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Martello tower', '', 'A building of masonry, generally circular, usually\n   erected on the seacoast, with a gun on the summit mounted on a\n   traversing platform, so as to be fired in any direction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marten', 'n.', 'A bird. See Martin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marten', 'n.', 'Any one of several fur-bearing carnivores of the genus\n   Mustela, closely allied to the sable. Among the more important species\n   are the European beech, or stone, marten (Mustela foina); the pine\n   marten (M. martes); and the American marten, or sable (M. Americana),\n   which some zoologists consider only a variety of the Russian sable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marten', 'n.', 'The fur of the marten, used for hats, muffs, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Martern', 'n.', 'Same as Marten.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mar-text', 'n.', 'A blundering preacher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Martial', 'a.', 'Of, pertaining to, or suited for, war; military; as,\n   martial music; a martial appearance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Martial', 'a.', 'Practiced in, or inclined to, war; warlike; brave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Martial', 'a.', 'Belonging to war, or to an army and navy; -- opposed to\n   civil; as, martial law; a court-martial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Martial', 'a.', 'Pertaining to, or resembling, the god, or the planet,\n   Mars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Martial', 'a.', 'Pertaining to, or containing, iron; chalybeate; as,\n   martial preparations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Martialism', 'n.', 'The quality of being warlike; exercises suitable for\n   war.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Martialist', 'n.', 'A warrior.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Martialized', 'imp. & p. p.', 'of Martialize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Martializing', 'p. pr. & vb. n.', 'of Martialize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Martialize', 'v. t.', 'To render warlike; as, to martialize a people.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Martially', 'adv.', 'In a martial manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Martialness', 'n.', 'The quality of being martial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Martin', 'n.', 'A perforated stone-faced runner for grinding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Martin', 'n.', 'One of several species of swallows, usually having the tail\n   less deeply forked than the tail of the common swallows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Martinet', 'n.', 'In military language, a strict disciplinarian; in\n   general, one who lays stress on a rigid adherence to the details of\n   discipline, or to forms and fixed methods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Martinet', 'n.', 'The martin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Martineta', 'n.', 'A species of tinamou (Calopezus elegans), having a long\n   slender crest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Martinetism', 'n.', 'The principles or practices of a martinet; rigid\n   adherence to discipline, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Martingale', 'n.', 'Alt. of Martingal');
  INSERT INTO words (word, word_type, description) VALUES  (    'Martingal', 'n.', 'A strap fastened to a horses girth, passing between his\n   fore legs, and fastened to the bit, or now more commonly ending in two\n   rings, through which the reins pass. It is intended to hold down the\n   head of the horse, and prevent him from rearing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Martingal', 'n.', 'A lower stay of rope or chain for the jib boom or flying\n   jib boom, fastened to, or reeved through, the dolphin striker. Also,\n   the dolphin striker itself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Martingal', 'n.', 'The act of doubling, at each stake, that which has been\n   lost on the preceding stake; also, the sum so risked; -- metaphorically\n   derived from the bifurcation of the martingale of a harness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Martinmas', 'n.', 'The feast of St. Martin, the eleventh of November; --\n   often called martlemans.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Martite', 'n.', 'Iron sesquioxide in isometric form, probably a pseudomorph\n   after magnetite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Martlemas', 'n.', 'See Martinmas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Martlet', 'n.', 'The European house martin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Martlet', 'n.', 'A bird without beak or feet; -- generally assumed to\n   represent a martin. As a mark of cadency it denotes the fourth son.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Martyr', 'n.', 'One who, by his death, bears witness to the truth of the\n   gospel; one who is put to death for his religion; as, Stephen was the\n   first Christian martyr.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Martyr', 'n.', 'Hence, one who sacrifices his life, his station, or what is\n   of great value to him, for the sake of principle, or to sustain a\n   cause.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Martyred', 'imp. & p. p.', 'of Martyr');
  INSERT INTO words (word, word_type, description) VALUES  (    'Martyring', 'p. pr. & vb. n.', 'of Martyr');
  INSERT INTO words (word, word_type, description) VALUES  (    'Martyr', 'v. t.', 'To put to death for adhering to some belief, esp.\n   Christianity; to sacrifice on account of faith or profession.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Martyr', 'v. t.', 'To persecute; to torment; to torture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Martyrdom', 'n.', 'The condition of a martyr; the death of a martyr; the\n   suffering of death on account of adherence to the Christian faith, or\n   to any cause.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Martyrdom', 'n.', 'Affliction; torment; torture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Martyrization', 'n.', 'Act of martyrizing, or state of being martyrized;\n   torture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Martyrize', 'v. t.', 'To make a martyr of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Martyrly', 'adv.', 'In the manner of a martyr.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Martyrologe', 'n.', 'A martyrology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Martyrologic', 'a.', 'Alt. of Martyrological');
  INSERT INTO words (word, word_type, description) VALUES  (    'Martyrological', 'a.', 'Pertaining to martyrology or martyrs; registering,\n   or registered in, a catalogue of martyrs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Martyrologist', 'n.', 'A writer of martyrology; an historian of martyrs.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-gies', 'pl. ', 'of Martyrology');
  INSERT INTO words (word, word_type, description) VALUES  (    'Martyrology', 'n.', 'A history or account of martyrs; a register of\n   martyrs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Martyrship', 'n.', 'Martyrdom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marvel', 'n.', 'That which causes wonder; a prodigy; a miracle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marvel', 'n.', 'Wonder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marveled', 'imp. & p. p.', 'of Marvel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marvelled', '', 'of Marvel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marveling', 'p. pr. & vb. n.', 'of Marvel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marvelling', '', 'of Marvel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marvel', 'v. i.', 'To be struck with surprise, astonishment, or wonder; to\n   wonder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marvel', 'v. t.', 'To marvel at.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marvel', 'v. t.', 'To cause to marvel, or be surprised; -- used\n   impersonally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marvelous', 'n.', 'Exciting wonder or surprise; astonishing; wonderful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marvelous', 'n.', 'Partaking of the character of miracle, or supernatural\n   power; incredible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marvelously', 'adv.', 'In a marvelous manner; wonderfully; strangely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marvelousness', 'n.', 'The quality or state of being marvelous;\n   wonderfulness; strangeness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marver', 'n.', 'A stone, or cast-iron plate, or former, on which hot glass\n   is rolled to give it shape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mary', 'n.', 'Marrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mary', 'interj.', 'See Marry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mary-bud', 'n.', 'The marigold; a blossom of the marigold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maryolatry', 'n.', 'Mariolatry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Marysole', 'n.', 'A large British fluke, or flounder (Rhombus megastoma);\n   -- called also carter, and whiff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mascagnin', 'n.', 'Alt. of Mascagnite');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mascagnite', 'n.', 'Native sulphate of ammonia, found in volcanic\n   districts; -- so named from Mascagni, who discovered it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mascle', 'n.', 'A lozenge voided.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mascled', 'a.', 'Composed of, or covered with, lozenge-shaped scales;\n   having lozenge-shaped divisions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mascot', 'n.', 'Alt. of Mascotte');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mascotte', 'n.', 'A person who is supposed to bring good luck to the\n   household to which he or she belongs; anything that brings good luck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Masculate', 'v. t.', 'To make strong.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Masculine', 'a.', 'Of the male sex; not female.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Masculine', 'a.', 'Having the qualities of a man; suitable to, or\n   characteristic of, a man; virile; not feminine or effeminate; strong;\n   robust.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Masculine', 'a.', 'Belonging to males; appropriated to, or used by, males.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Masculine', 'a.', 'Having the inflections of, or construed with, words\n   pertaining especially to male beings, as distinguished from feminine\n   and neuter. See Gender.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Masculinity', 'n.', 'The state or quality of being masculine;\n   masculineness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mase', 'n. & v.', 'See Maze.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maselyn', 'n.', 'A drinking cup. See 1st Maslin, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maser', 'n.', 'Same as Mazer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mash', 'n.', 'A mesh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mash', 'n.', 'A mass of mixed ingredients reduced to a soft pulpy state by\n   beating or pressure; a mass of anything in a soft pulpy state.\n   Specifically (Brewing), ground or bruised malt, or meal of rye, wheat,\n   corn, or other grain (or a mixture of malt and meal) steeped and\n   stirred in hot water for making the wort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mash', 'n.', 'A mixture of meal or bran and water fed to animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mash', 'n.', 'A mess; trouble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mashed', 'imp. & p. p.', 'of Mash');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mashing', 'p. pr. & vb. n.', 'of Mash');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mash', 'v. t.', 'To convert into a mash; to reduce to a soft pulpy state by\n   beating or pressure; to bruise; to crush; as, to mash apples in a mill,\n   or potatoes with a pestle. Specifically (Brewing), to convert, as malt,\n   or malt and meal, into the mash which makes wort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Masher', 'n.', 'One who, or that which, mashes; also (Brewing), a machine\n   for making mash.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Masher', 'n.', 'A charmer of women.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mashlin', 'n.', 'See Maslin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mashy', 'a.', 'Produced by crushing or bruising; resembling, or consisting\n   of, a mash.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mask', 'n.', 'A cover, or partial cover, for the face, used for disguise or\n   protection; as, a dancers mask; a fencers mask; a ball players mask.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mask', 'n.', 'That which disguises; a pretext or subterfuge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mask', 'n.', 'A festive entertainment of dancing or other diversions, where\n   all wear masks; a masquerade; hence, a revel; a frolic; a delusive\n   show.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mask', 'n.', 'A dramatic performance, formerly in vogue, in which the\n   actors wore masks and represented mythical or allegorical characters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mask', 'n.', 'A grotesque head or face, used to adorn keystones and other\n   prominent parts, to spout water in fountains, and the like; -- called\n   also mascaron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mask', 'n.', 'In a permanent fortification, a redoubt which protects the\n   caponiere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mask', 'n.', 'A screen for a battery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mask', 'n.', 'The lower lip of the larva of a dragon fly, modified so as to\n   form a prehensile organ.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Masked', 'imp. & p. p.', 'of Mask');
  INSERT INTO words (word, word_type, description) VALUES  (    'Masking', 'p. pr. & vb. n.', 'of Mask');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mask', 'v. t.', 'To cover, as the face, by way of concealment or defense\n   against injury; to conceal with a mask or visor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mask', 'v. t.', 'To disguise; to cover; to hide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mask', 'v. t.', 'To conceal; also, to intervene in the line of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mask', 'v. t.', 'To cover or keep in check; as, to mask a body of troops or\n   a fortess by a superior force, while some hostile evolution is being\n   carried out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mask', 'v. i.', 'To take part as a masker in a masquerade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mask', 'v. i.', 'To wear a mask; to be disguised in any way.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Masked', 'a.', 'Wearing a mask or masks; characterized by masks; cincealed;\n   hidden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Masked', 'a.', 'Same as Personate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Masked', 'a.', 'Having the anterior part of the head differing decidedly in\n   color from the rest of the plumage; -- said of birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Masker', 'n.', 'One who wears a mask; one who appears in disguise at a\n   masquerade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Masker', 'v. t.', 'To confuse; to stupefy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maskery', 'n.', 'The dress or disguise of a maske/; masquerade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maskinonge', 'n.', 'The muskellunge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mask shell', '', 'Any spiral marine shell of the genus Persona, having a\n   curiously twisted aperture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maslach', 'n.', 'An excitant containing opium, much used by the Turks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maslin', 'n.', 'A mixture composed of different materials');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maslin', 'n.', 'A mixture of metals resembling brass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maslin', 'n.', 'A mixture of different sorts of grain, as wheat and rye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maslin', 'n.', 'A vessel made of maslin, 1 (a).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maslin', 'a.', 'Composed of different sorts; as, maslin bread, which is\n   made of rye mixed with a little wheat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mason', 'n.', 'One whose occupation is to build with stone or brick; also,\n   one who prepares stone for building purposes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mason', 'n.', 'A member of the fraternity of Freemasons. See Freemason.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mason', 'v. t.', 'To build stonework or brickwork about, under, in, over,\n   etc.; to construct by masons; -- with a prepositional suffix; as, to\n   mason up a well or terrace; to mason in a kettle or boiler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Masonic', 'a.', 'Of or pertaining to Freemasons or to their craft or\n   mysteries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Masonry', 'n.', 'The art or occupation of a mason.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Masonry', 'n.', 'The work or performance of a mason; as, good or bad\n   masonry; skillful masonry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Masonry', 'n.', 'That which is built by a mason; anything constructed of\n   the materials used by masons, such as stone, brick, tiles, or the like.\n   Dry masonry is applied to structures made without mortar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Masonry', 'n.', 'The craft, institution, or mysteries of Freemasons;\n   freemasonry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Masoola boat', '', 'A kind of boat used on the coast of Madras, India. The\n   planks are sewed together with strands of coir which cross over a\n   wadding of the same material, so that the shock on taking the beach\n   through surf is much reduced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Masora', 'n.', 'A Jewish critical work on the text of the Hebrew\n   Scriptures, composed by several learned rabbis of the school of\n   Tiberias, in the eighth and ninth centuries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Masoret', 'n.', 'A Masorite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Masoretic', 'a.', 'Alt. of Masoretical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Masoretical', 'a.', 'Of or relating to the Masora, or to its authors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Masorite', 'n.', 'One of the writers of the Masora.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Masque', 'n.', 'A mask; a masquerade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Masquerade', 'n.', 'An assembly of persons wearing masks, and amusing\n   themselves with dancing, conversation, or other diversions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Masquerade', 'n.', 'A dramatic performance by actors in masks; a mask. See\n   1st Mask, 4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Masquerade', 'n.', 'Acting or living under false pretenses; concealment of\n   something by a false or unreal show; pretentious show; disguise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Masquerade', 'n.', 'A Spanish diversion on horseback.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Masqueraded', 'imp. & p. p.', 'of Masquerade');
  INSERT INTO words (word, word_type, description) VALUES  (    'Masquerading', 'p. pr. & vb. n.', 'of Masquerade');
  INSERT INTO words (word, word_type, description) VALUES  (    'Masquerade', 'v. i.', 'To assemble in masks; to take part in a masquerade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Masquerade', 'v. i.', 'To frolic or disport in disquise; to make a\n   pretentious show of being what one is not.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Masquerade', 'v. t.', 'To conceal with masks; to disguise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Masquerader', 'n.', 'One who masquerades; a person wearing a mask; one\n   disguised.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mass', 'n.', 'The sacrifice in the sacrament of the Eucharist, or the\n   consecration and oblation of the host.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mass', 'n.', 'The portions of the Mass usually set to music, considered as\n   a musical composition; -- namely, the Kyrie, the Gloria, the Credo, the\n   Sanctus, and the Agnus Dei, besides sometimes an Offertory and the\n   Benedictus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Massed', 'imp. & p. p.', 'of Mass');
  INSERT INTO words (word, word_type, description) VALUES  (    'Massing', 'p. pr. & vb. n.', 'of Mass');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mass', 'v. i.', 'To celebrate Mass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mass', 'n.', 'A quantity of matter cohering together so as to make one\n   body, or an aggregation of particles or things which collectively make\n   one body or quantity, usually of considerable size; as, a mass of ore,\n   metal, sand, or water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mass', 'n.', 'A medicinal substance made into a cohesive, homogeneous lump,\n   of consistency suitable for making pills; as, blue mass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mass', 'n.', 'A large quantity; a sum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mass', 'n.', 'Bulk; magnitude; body; size.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mass', 'n.', 'The principal part; the main body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mass', 'n.', 'The quantity of matter which a body contains, irrespective of\n   its bulk or volume.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mass', 'v. t.', 'To form or collect into a mass; to form into a collective\n   body; to bring together into masses; to assemble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Massacre', 'n.', 'The killing of a considerable number of human beings\n   under circumstances of atrocity or cruelty, or contrary to the usages\n   of civilized people; as, the massacre on St. Bartholomews Day.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Massacre', 'n.', 'Murder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Massacred', 'imp. & p. p.', 'of Massacre');
  INSERT INTO words (word, word_type, description) VALUES  (    'Massacring', 'p. pr. & vb. n.', 'of Massacre');
  INSERT INTO words (word, word_type, description) VALUES  (    'Massacre', 'n.', 'To kill in considerable numbers where much resistance can\n   not be made; to kill with indiscriminate violence, without necessity,\n   and contrary to the usages of nations; to butcher; to slaughter; --\n   limited to the killing of human beings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Massacrer', 'n.', 'One who massacres.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Massage', 'n.', 'A rubbing or kneading of the body, especially when\n   performed as a hygienic or remedial measure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Massasauga', 'n.', 'The black rattlesnake (Crotalus, / Caudisona,\n   tergemina), found in the Mississippi Valley.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Masse', 'n.', 'Alt. of Masse shot');
  INSERT INTO words (word, word_type, description) VALUES  (    'Masse shot', 'n.', 'A stroke made with the cue held vertically.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Masser', 'n.', 'A priest who celebrates Mass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Masseter', 'n.', 'The large muscle which raises the under jaw, and assists\n   in mastication.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Masseteric', 'a.', 'Of or pertaining to the masseter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Masseterine', 'a.', 'Masseteric.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Masseur', 'n. f.', 'Alt. of Masseuse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Masseuse', 'n. f.', 'One who performs massage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Massicot', 'n.', 'Lead protoxide, PbO, obtained as a yellow amorphous\n   powder, the fused and crystalline form of which is called litharge;\n   lead ocher. It is used as a pigment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Massiness', 'n.', 'The state or quality of being massy; ponderousness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Massive', 'a.', 'Forming, or consisting of, a large mass; compacted;\n   weighty; heavy; massy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Massive', 'a.', 'In mass; not necessarily without a crystalline structure,\n   but having no regular form; as, a mineral occurs massive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Massively', 'adv.', 'In a heavy mass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Massiveness', 'n.', 'The state or quality of being massive; massiness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Massoola boat', '', 'See Masoola boat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Massora', 'n.', 'Same as Masora.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Massoret', 'n.', 'Same as Masorite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Massy', 'superl.', 'Compacted into, or consisting of, a mass; having bulk\n   and weight ot substance; ponderous; bulky and heavy; weight; heavy; as,\n   a massy shield; a massy rock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mast', 'n.', 'The fruit of the oak and beech, or other forest trees; nuts;\n   acorns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mast', 'n.', 'A pole, or long, strong, round piece of timber, or spar, set\n   upright in a boat or vessel, to sustain the sails, yards, rigging, etc.\n   A mast may also consist of several pieces of timber united by iron\n   bands, or of a hollow pillar of iron or steel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mast', 'n.', 'The vertical post of a derrick or crane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Masted', 'imp. & p. p.', 'of Mast');
  INSERT INTO words (word, word_type, description) VALUES  (    'Masting', 'p. pr. & vb. n.', 'of Mast');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mast', 'v. t.', 'To furnish with a mast or masts; to put the masts of in\n   position; as, to mast a ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mastax', 'n.', 'The pharynx of a rotifer. It usually contains four horny\n   pieces. The two central ones form the incus, against which the mallei,\n   or lateral ones, work so as to crush the food.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mastax', 'n.', 'The lore of a bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Masted', 'a.', 'Furnished with a mast or masts; -- chiefly in composition;\n   as, a three-masted schooner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Master', 'n.', 'A vessel having (so many) masts; -- used only in compounds;\n   as, a two-master.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Master', 'n.', 'A male person having another living being so far subject to\n   his will, that he can, in the main, control his or its actions; --\n   formerly used with much more extensive application than now. (a) The\n   employer of a servant. (b) The owner of a slave. (c) The person to whom\n   an apprentice is articled. (d) A sovereign, prince, or feudal noble; a\n   chief, or one exercising similar authority. (e) The head of a\n   household. (f) The male head of a school or college. (g) A male\n   teacher. (h) The director of a number of persons performing a ceremony\n   or sharing a feast. (i) The owner of a docile brute, -- especially a\n   dog or horse. (j) The controller of a familiar spirit or other\n   supernatural being.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Master', 'n.', 'One who uses, or controls at will, anything inanimate; as,\n   to be master of ones time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Master', 'n.', 'One who has attained great skill in the use or application\n   of anything; as, a master of oratorical art.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Master', 'n.', 'A title given by courtesy, now commonly pronounced mister,\n   except when given to boys; -- sometimes written Mister, but usually\n   abbreviated to Mr.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Master', 'n.', 'A young gentleman; a lad, or small boy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Master', 'n.', 'The commander of a merchant vessel; -- usually called\n   captain. Also, a commissioned officer in the navy ranking next above\n   ensign and below lieutenant; formerly, an officer on a man-of-war who\n   had immediate charge, under the commander, of sailing the vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Master', 'n.', 'A person holding an office of authority among the\n   Freemasons, esp. the presiding officer; also, a person holding a\n   similar office in other civic societies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mastered', 'imp. & p. p.', 'of Master');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mastering', 'p. pr. vb. n.', 'of Master');
  INSERT INTO words (word, word_type, description) VALUES  (    'Master', 'v. t.', 'To become the master of; to subject to ones will,\n   control, or authority; to conquer; to overpower; to subdue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Master', 'v. t.', 'To gain the command of, so as to understand or apply; to\n   become an adept in; as, to master a science.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Master', 'v. t.', 'To own; to posses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Master', 'v. i.', 'To be skillful; to excel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Masterdom', 'n.', 'Dominion; rule; command.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Masterful', 'a.', 'Inclined to play the master; domineering; imperious;\n   arbitrary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Masterful', 'a.', 'Having the skill or power of a master; indicating or\n   expressing power or mastery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Masterfully', 'adv.', 'In a masterful manner; imperiously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Masterhood', 'n.', 'The state of being a master; hence, disposition to\n   command or hector.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Masterless', 'a.', 'Destitute of a master or owner; ungoverned or\n   ungovernable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Masterliness', 'n.', 'The quality or state of being masterly; ability to\n   control wisely or skillfully.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Masterly', 'a.', 'Suitable to, or characteristic of, a master; indicating\n   thorough knowledge or superior skill and power; showing a masters\n   hand; as, a masterly design; a masterly performance; a masterly policy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Masterly', 'a.', 'Imperious; domineering; arbitrary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Masterly', 'adv.', 'With the skill of a master.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Masterous', 'a.', 'Masterly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Masterpiece', 'n.', 'Anything done or made with extraordinary skill; a\n   capital performance; a chef-doeuvre; a supreme achievement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mastership', 'n.', 'The state or office of a master.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mastership', 'n.', 'Mastery; dominion; superior skill; superiority.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mastership', 'n.', 'Chief work; masterpiece.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mastership', 'n.', 'An ironical title of respect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mastersinger', 'n.', 'One of a class of poets which flourished in Nuremberg\n   and some other cities of Germany in the 15th and 16th centuries. They\n   bound themselves to observe certain arbitrary laws of rhythm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Masterwort', 'n.', 'A tall and coarse European umbelliferous plant\n   (Peucedanum Ostruthium, formerly Imperatoria).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Masterwort', 'n.', 'The Astrantia major, a European umbelliferous plant\n   with a showy colored involucre.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Masterwort', 'n.', 'Improperly, the cow parsnip (Heracleum lanatum).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Masteries', 'pl. ', 'of Mastery');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mastery', 'n.', 'The position or authority of a master; dominion; command;\n   supremacy; superiority.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mastery', 'n.', 'Superiority in war or competition; victory; triumph;\n   preeminence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mastery', 'n.', 'Contest for superiority.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mastery', 'n.', 'A masterly operation; a feat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mastery', 'n.', 'Specifically, the philosophers stone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mastery', 'n.', 'The act process of mastering; the state of having\n   mastered.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mastful', 'a.', 'Abounding in mast; producing mast in abundance; as, the\n   mastful forest; a mastful chestnut.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Masthead', 'n.', 'The top or head of a mast; the part of a mast above the\n   hounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Masthead', 'v. t.', 'To cause to go to the masthead as a punishment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Masthouse', 'n.', 'A building in which vessels masts are shaped, fitted,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mastic', 'n.', 'A low shrubby tree of the genus Pistacia (P. Lentiscus),\n   growing upon the islands and coasts of the Mediterranean, and producing\n   a valuable resin; -- called also, mastic tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mastic', 'n.', 'A resin exuding from the mastic tree, and obtained by\n   incision. The best is in yellowish white, semitransparent tears, of a\n   faint smell, and is used as an astringent and an aromatic, also as an\n   ingredient in varnishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mastic', 'n.', 'A kind of cement composed of burnt clay, litharge, and\n   linseed oil, used for plastering walls, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Masticable', 'a.', 'Capable of being masticated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Masticador', 'n.', 'A part of a bridle, the slavering bit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Masticated', 'imp. & p. p.', 'of Masticate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Masticating', 'p. pr. & vb. n.', 'of Masticate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Masticate', 'v. t.', 'To grind or crush with, or as with, the teeth and\n   prepare for swallowing and digestion; to chew; as, to masticate food.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Masticater', 'n.', 'One who masticates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mastication', 'n.', 'The act or operation of masticating; chewing, as of\n   food.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Masticator', 'n.', 'One who masticates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Masticator', 'n.', 'A machine for cutting meat into fine pieces for\n   toothless people; also, a machine for cutting leather, India rubber, or\n   similar tough substances, into fine pieces, in some processes of\n   manufacture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Masticatory', 'a.', 'Chewing; adapted to perform the office o/ chewing\n   food.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-ries', 'pl. ', 'of Masticatory');
  INSERT INTO words (word, word_type, description) VALUES  (    'Masticatory', 'n.', 'A substance to be chewed to increase the saliva.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mastich', 'n.', 'See Mastic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Masticin', 'n.', 'A white, amorphous, tenacious substance resembling\n   caoutchouc, and obtained as an insoluble residue of mastic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Masticot', 'n.', 'Massicot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mastiffs', 'pl. ', 'of Mastiff');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mastives', 'pl. ', 'of Mastiff');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mastiff', 'n.', 'A breed of large dogs noted for strength and courage.\n   There are various strains, differing in form and color, and\n   characteristic of different countries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mastigopod', 'n.', 'One of the Mastigopoda.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mastigopoda', 'n. pl.', 'The Infusoria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mastigure', 'n.', 'Any one of several large spiny-tailed lizards of the\n   genus Uromastix. They inhabit Southern Asia and North Africa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Masting', 'n.', 'The act or process of putting a mast or masts into a\n   vessel; also, the scientific principles which determine the position of\n   masts, and the mechanical methods of placing them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mastitis', 'n.', 'Inflammation of the breast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mastless', 'a.', 'Bearing no mast; as, a mastless oak or beech.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mastless', 'a.', 'Having no mast; as, a mastless vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mastlin', 'n.', 'See Maslin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mastodon', 'n.', 'An extinct genus of mammals closely allied to the\n   elephant, but having less complex molar teeth, and often a pair of\n   lower, as well as upper, tusks, which are incisor teeth. The species\n   were mostly larger than elephants, and their romains occur in nearly\n   all parts of the world in deposits ranging from Miocene to late\n   Quaternary time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mastodonsaurus', 'n.', 'A large extinct genus of labyrinthodonts, found in\n   the European Triassic rocks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mastodontic', 'a.', 'Pertaining to, or resembling, a mastodon; as,\n   mastodontic dimensions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mastodynia', 'n.', 'Alt. of Mastodyny');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mastodyny', 'n.', 'Pain occuring in the mamma or female breast, -- a form\n   of neuralgia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mastoid', 'a.', 'Resembling the nipple or the breast; -- applied\n   specifically to a process of the temporal bone behind the ear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mastoid', 'a.', 'Pertaining to, or in the region of, the mastoid process;\n   mastoidal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mastoidal', 'a.', 'Same as Mastoid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mastology', 'n.', 'The natural history of Mammalia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mastress', 'n.', 'Mistress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Masturbation', 'n.', 'Onanism; self-pollution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Masty', 'a.', 'Full of mast; abounding in acorns, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Masula boat', '', 'Same as Masoola boat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mat', 'n.', 'A name given by coppersmiths to an alloy of copper, tin, iron,\n   etc., usually called white metal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mat', 'a.', 'Cast down; dejected; overthrown; slain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mat', 'n.', 'A fabric of sedge, rushes, flags, husks, straw, hemp, or\n   similar material, used for wiping and cleaning shoes at the door, for\n   covering the floor of a hall or room, and for other purposes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mat', 'n.', 'Any similar fabric for various uses, as for covering plant\n   houses, putting beneath dishes or lamps on a table, securing rigging\n   from friction, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mat', 'n.', 'Anything growing thickly, or closely interwoven, so as to\n   resemble a mat in form or texture; as, a mat of weeds; a mat of hair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mat', 'n.', 'An ornamental border made of paper, pasterboard, metal, etc.,\n   put under the glass which covers a framed picture; as, the mat of a\n   daguerreotype.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matted', 'imp. & p. p.', 'of Mat');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matting', 'p. pr. & vb. n.', 'of Mat');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mat', 'v. t.', 'To cover or lay with mats.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mat', 'v. t.', 'To twist, twine, or felt together; to interweave into, or\n   like, a mat; to entangle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mat', 'v. i.', 'To grow thick together; to become interwoven or felted\n   together like a mat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matachin', 'n.', 'An old dance with swords and bucklers; a sword dance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mataco', 'n.', 'The three-banded armadillo (Tolypeutis tricinctus). See\n   Illust. under Loricata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matadore', 'n.', 'Alt. of Matador');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matador', 'n.', 'The killer; the man appointed to kill the bull in\n   bullfights.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matador', 'n.', 'In the game of quadrille or omber, the three principal\n   trumps, the ace of spades being the first, the ace of clubs the third,\n   and the second being the deuce of a black trump or the seven of a red\n   one.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matagasse', 'n.', 'A shrike or butcher bird; -- called also mattages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matamata', 'n.', 'The bearded tortoise (Chelys fimbriata) of South American\n   rivers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matanza', 'n.', 'A place where animals are slaughtered for their hides and\n   tallow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Match', 'n.', 'Anything used for catching and retaining or communicating\n   fire, made of some substance which takes fire readily, or remains\n   burning some time; esp., a small strip or splint of wood dipped at one\n   end in a substance which can be easily ignited by friction, as a\n   preparation of phosphorus or chlorate of potassium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Match', 'v.', 'A person or thing equal or similar to another; one able to\n   mate or cope with another; an equal; a mate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Match', 'v.', 'A bringing together of two parties suited to one another, as\n   for a union, a trial of skill or force, a contest, or the like');
  INSERT INTO words (word, word_type, description) VALUES  (    'Match', 'v.', 'A contest to try strength or skill, or to determine\n   superiority; an emulous struggle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Match', 'v.', 'A matrimonial union; a marriage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Match', 'v.', 'An agreement, compact, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Match', 'v.', 'A candidate for matrimony; one to be gained in marriage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Match', 'v.', 'Equality of conditions in contest or competition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Match', 'v.', 'Suitable combination or bringing together; that which\n   corresponds or harmonizes with something else; as, the carpet and\n   curtains are a match.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Match', 'v.', 'A perforated board, block of plaster, hardened sand, etc.,\n   in which a pattern is partly imbedded when a mold is made, for giving\n   shape to the surfaces of separation between the parts of the mold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matched', 'imp. & p. p.', 'of Match');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matching', 'p. pr. & vb. n.', 'of Match');
  INSERT INTO words (word, word_type, description) VALUES  (    'Match', 'v. t.', 'To be a mate or match for; to be able to complete with;\n   to rival successfully; to equal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Match', 'v. t.', 'To furnish with its match; to bring a match, or equal,\n   against; to show an equal competitor to; to set something in\n   competition with, or in opposition to, as equal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Match', 'v. t.', 'To oppose as equal; to contend successfully against.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Match', 'v. t.', 'To make or procure the equal of, or that which is exactly\n   similar to, or corresponds with; as, to match a vase or a horse; to\n   match cloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Match', 'v. t.', 'To make equal, proportionate, or suitable; to adapt, fit,\n   or suit (one thing to another).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Match', 'v. t.', 'To marry; to give in marriage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Match', 'v. t.', 'To fit together, or make suitable for fitting together;\n   specifically, to furnish with a tongue and a groove, at the edges; as,\n   to match boards.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Match', 'v. i.', 'To be united in marriage; to mate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Match', 'v. i.', 'To be of equal, or similar, size, figure, color, or\n   quality; to tally; to suit; to correspond; as, these vases match.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matchable', 'a.', 'Capable of being matched; comparable on equal\n   conditions; adapted to being joined together; correspondent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Match-cloth', 'n.', 'A coarse cloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Match-coat', 'n.', 'A coat made of match-cloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matcher', 'n.', 'One who, or that which, matches; a matching machine. See\n   under 3d Match.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matchless', 'a.', 'Having no equal; unequaled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matchless', 'a.', 'Unlike each other; unequal; unsuited.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matchlock', 'n.', 'An old form of gunlock containing a match for firing the\n   priming; hence, a musket fired by means of a match.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matchmaker', 'n.', 'One who makes matches for burning or kinding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matchmaker', 'n.', 'One who tries to bring about marriages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matchmaking', 'n.', 'The act or process of making matches for kindling or\n   burning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matchmaking', 'n.', 'The act or process of trying to bring about a marriage\n   for others.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matchmaking', 'a.', 'Busy in making or contriving marriages; as, a\n   matchmaking woman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mate', 'n.', 'The Paraguay tea, being the dried leaf of the Brazilian holly\n   (Ilex Paraguensis). The infusion has a pleasant odor, with an agreeable\n   bitter taste, and is much used for tea in South America.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mate', 'n.', 'Same as Checkmate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mate', 'a.', 'See 2d Mat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mate', 'v. t.', 'To confuse; to confound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mate', 'v. t.', 'To checkmate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mate', 'n.', 'One who customarily associates with another; a companion; an\n   associate; any object which is associated or combined with a similar\n   object.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mate', 'n.', 'Hence, specifically, a husband or wife; and among the lower\n   animals, one of a pair associated for propagation and the care of their\n   young.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mate', 'n.', 'A suitable companion; a match; an equal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mate', 'n.', 'An officer in a merchant vessel ranking next below the\n   captain. If there are more than one bearing the title, they are called,\n   respectively, first mate, second mate, third mate, etc. In the navy, a\n   subordinate officer or assistant; as, masters mate; surgeons mate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mated', 'imp. & p. p.', 'of Mate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mating', 'p. pr. & vb. n.', 'of Mate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mate', 'v. t.', 'To match; to marry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mate', 'v. t.', 'To match ones self against; to oppose as equal; to\n   compete with.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mate', 'v. i.', 'To be or become a mate or mates, especially in sexual\n   companionship; as, some birds mate for life; this bird will not mate\n   with that one.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mateless', 'a.', 'Having no mate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matelote', 'n.', 'A dish of food composed of many kinds of fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mateology', 'n.', 'A vain, unprofitable discourse or inquiry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mateotechny', 'n.', 'Any unprofitable science.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mater', 'n.', 'See Alma mater, Dura mater, and Pia mater.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Material', 'a.', 'Consisting of matter; not spiritual; corporeal; physical;\n   as, material substance or bodies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Material', 'a.', 'Hence: Pertaining to, or affecting, the physical nature\n   of man, as distinguished from the mental or moral nature; relating to\n   the bodily wants, interests, and comforts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Material', 'a.', 'Of solid or weighty character; not insubstantial; of\n   cinsequence; not be dispensed with; important.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Material', 'a.', 'Pertaining to the matter, as opposed to the form, of a\n   thing. See Matter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Material', 'n.', 'The substance or matter of which anything is made or may\n   be made.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Material', 'v. t.', 'To form from matter; to materialize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Materialism', 'n.', 'The doctrine of materialists; materialistic views and\n   tenets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Materialism', 'n.', 'The tendency to give undue importance to material\n   interests; devotion to the material nature and its wants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Materialism', 'n.', 'Material substances in the aggregate; matter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Materialist', 'n.', 'One who denies the existence of spiritual substances\n   or agents, and maintains that spiritual phenomena, so called, are the\n   result of some peculiar organization of matter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Materialist', 'n.', 'One who holds to the existence of matter, as\n   distinguished from the idealist, who denies it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Materialistic', 'a.', 'Alt. of Materialistical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Materialistical', 'a.', 'Of or pertaining to materialism or materialists;\n   of the nature of materialism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Materiality', 'n.', 'The quality or state of being material; material\n   existence; corporeity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Materiality', 'n.', 'Importance; as, the materiality of facts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Materialization', 'n.', 'The act of materializing, or the state of being\n   materialized.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Materialized', 'imp. & p. p.', 'of Materialize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Materializing', 'p. pr. & vb. n.', 'of Materialize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Materialize', 'v. t.', 'To invest with material characteristics; to make\n   perceptible to the senses; hence, to present to the mind through the\n   medium of material objects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Materialize', 'v. t.', 'To regard as matter; to consider or explain by the\n   laws or principles which are appropriate to matter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Materialize', 'v. t.', 'To cause to assume a character appropriate to\n   material things; to occupy with material interests; as, to materialize\n   thought.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Materialize', 'v. t.', 'To make visable in, or as in, a material form; --\n   said of spirits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Materialize', 'v. i.', 'To appear as a material form; to take substantial\n   shape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Materially', 'adv.', 'In the state of matter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Materially', 'adv.', 'In its essence; substantially.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Materially', 'adv.', 'In an important manner or degree; essentaily; as, it\n   materially concern us to know the real motives of our actions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Materialness', 'n.', 'The state of being material.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Materia medica', '', 'Material or substance used in the composition of\n   remedies; -- a general term for all substances used as curative agents\n   in medicine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Materia medica', '', 'That branch of medical science which treats of the\n   nature and properties of all the substances that are employed for the\n   cure of diseases.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Materiarian', 'n.', 'See Materialist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Materiate', 'a.', 'Alt. of Materiated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Materiated', 'a.', 'Consisting of matter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Materiation', 'n.', 'Act of forming matter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Materiel', 'n.', 'That in a complex system which constitutes the materials,\n   or instruments employed, in distinction from the personnel, or men; as,\n   the baggage, munitions, provisions, etc., of an army; or the buildings,\n   libraries, and apparatus of a college, in distinction from its\n   officers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Materious', 'a.', 'See Material.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maternal', 'a.', 'Of or pertaining to a mother; becoming to a mother;\n   motherly; as, maternal love; maternal tenderness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maternally', 'adv.', 'In a motherly manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maternity', 'n.', 'The state of being a mother; the character or relation\n   of a mother.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matfelon', 'n.', 'The knapweed (Centaurea nigra).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Math', 'n.', 'A mowing, or that which is gathered by mowing; -- chiefly\n   used in composition; as, an aftermath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mathematic', 'a.', 'See Mathematical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mathematical', 'a.', 'Of or pertaining to mathematics; according to\n   mathematics; hence, theoretically precise; accurate; as, mathematical\n   geography; mathematical instruments; mathematical exactness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mathematician', 'n.', 'One versed in mathematics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mathematics', 'n.', 'That science, or class of sciences, which treats of\n   the exact relations existing between quantities or magnitudes, and of\n   the methods by which, in accordance with these relations, quantities\n   sought are deducible from other quantities known or supposed; the\n   science of spatial and quantitative relations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mather', 'n.', 'See Madder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mathes', 'n.', 'The mayweed. Cf. Maghet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mathesis', 'n.', 'Learning; especially, mathematics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mathurin', 'n.', 'See Trinitarian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matico', 'n.', 'A Peruvian plant (Piper, / Artanthe, elongatum), allied to\n   the pepper, the leaves of which are used as a styptic and astringent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matie', 'n.', 'A fat herring with undeveloped roe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'M/tin', 'n.', 'A French mastiff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matin', 'n.', 'Morning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matin', 'n.', 'Morning worship or service; morning prayers or songs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matin', 'n.', 'Time of morning service; the first canonical hour in the\n   Roman Catholic Church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matin', 'a.', 'Of or pertaining to the morning, or to matins; used in the\n   morning; matutinal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matinal', 'a.', 'Relating to the morning, or to matins; matutinal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matinee', 'n.', 'A reception, or a musical or dramatic entertainment, held\n   in the daytime. See SoirEe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matrass', 'n.', 'A round-bottomed glass flask having a long neck; a\n   bolthead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matress', 'n.', 'See Matress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matriarch', 'n.', 'The mother and ruler of a family or of her descendants;\n   a ruler by maternal right.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matriarchal', 'a.', 'Of or pertaining to a matriarch; governed by a\n   matriarch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matriarchate', 'n.', 'The office or jurisdiction of a matriarch; a\n   matriarchal form of government.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matrice', 'n.', 'See Matrix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matricidal', 'a.', 'Of or pertaining to matricide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matricide', 'n.', 'The murder of a mother by her son or daughter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matricide', 'n.', 'One who murders ones own mother.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matriculated', 'imp. & p. p.', 'of Matriculate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matriculating', 'p. pr. & vb. n.', 'of Matriculate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matriculate', 'v. t.', 'To enroll; to enter in a register; specifically, to\n   enter or admit to membership in a body or society, particularly in a\n   college or university, by enrolling the name in a register.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matriculate', 'v. i.', 'To go though the process of admission to\n   membership, as by examination and enrollment, in a society or college.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matriculate', 'a.', 'Matriculated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matriculate', 'n.', 'One who is matriculated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matriculation', 'n.', 'The act or process of matriculating; the state of\n   being matriculated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matrimoine', 'n.', 'Matrimony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matrimonial', 'a.', 'Of or pertaining to marriage; derived from marriage;\n   connubial; nuptial; hymeneal; as, matrimonial rights or duties.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matrimonially', 'adv.', 'In a matrimonial manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matrimonious', 'a.', 'Matrimonial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matrimony', 'n.', 'The union of man and woman as husband and wife; the\n   nuptial state; marriage; wedlock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matrimony', 'n.', 'A kind of game at cards played by several persons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matrices', 'pl. ', 'of Matrix');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matrix', 'n.', 'The womb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matrix', 'n.', 'Hence, that which gives form or origin to anything');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matrix', 'n.', 'The cavity in which anything is formed, and which gives it\n   shape; a die; a mold, as for the face of a type.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matrix', 'n.', 'The earthy or stony substance in which metallic ores or\n   crystallized minerals are found; the gangue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matrix', 'n.', 'The five simple colors, black, white, blue, red, and\n   yellow, of which all the rest are composed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matrix', 'n.', 'The lifeless portion of tissue, either animal or vegetable,\n   situated between the cells; the intercellular substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matrix', 'n.', 'A rectangular arrangement of symbols in rows and columns.\n   The symbols may express quantities or operations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matron', 'n.', 'A wife or a widow, especially, one who has borne children;\n   a woman of staid or motherly manners.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matron', 'n.', 'A housekeeper; esp., a woman who manages the domestic\n   economy of a public instution; a head nurse in a hospital; as, the\n   matron of a school or hospital.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matronage', 'n.', 'The state of a matron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matronage', 'n.', 'The collective body of matrons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matronal', 'a.', 'Of or pertaining to a matron; suitable to an elderly lady\n   or to a married woman; grave; motherly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matronhood', 'n.', 'The state of being a matron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matronized', 'imp. & p. p.', 'of Matronize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matronizing', 'p. pr. & vb. n.', 'of Matronize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matronize', 'v. t.', 'To make a matron of; to make matronlike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matronize', 'v. t.', 'To act the part of a marton toward; to superintend;\n   to chaperone; as, to matronize an assembly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matronlike', 'a.', 'Like a matron; sedate; grave; matronly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matronly', 'a.', 'Advanced in years; elderly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matronly', 'a.', 'Like, or befitting, a matron; grave; sedate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matronymic', 'n.', 'See Metronymic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matross', 'n.', 'Formerly, in the British service, a gunner or a gunners\n   mate; one of the soldiers in a train of artillery, who assisted the\n   gunners in loading, firing, and sponging the guns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matt', 'n.', 'See Matte.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mattages', 'n.', 'A shrike or butcher bird; -- written also matagasse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mattamore', 'n.', 'A subterranean repository for wheat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matte', 'n.', 'A partly reduced copper sulphide, obtained by alternately\n   roasting and melting copper ore in separating the metal from associated\n   iron ores, and called coarse metal, fine metal, etc., according to the\n   grade of fineness. On the exterior it is dark brown or black, but on a\n   fresh surface is yellow or bronzy in color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matte', 'n.', 'A dead or dull finish, as in gilding where the gold leaf is\n   not burnished, or in painting where the surface is purposely deprived\n   of gloss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matted', 'a.', 'Having a dull surface; unburnished; as, matted gold leaf or\n   gilding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matted', 'a.', 'Covered with a mat or mats; as, a matted floor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matted', 'a.', 'Tangled closely together; having its parts adhering closely\n   together; as, matted hair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matter', 'n.', 'That of which anything is composed; constituent substance;\n   material; the material or substantial part of anything; the constituent\n   elements of conception; that into which a notion may be analyzed; the\n   essence; the pith; the embodiment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matter', 'n.', 'That of which the sensible universe and all existent bodies\n   are composed; anything which has extension, occupies space, or is\n   perceptible by the senses; body; substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matter', 'n.', 'That with regard to, or about which, anything takes place\n   or is done; the thing aimed at, treated of, or treated; subject of\n   action, discussion, consideration, feeling, complaint, legal action, or\n   the like; theme.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matter', 'n.', 'That which one has to treat, or with which one has to do;\n   concern; affair; business.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matter', 'n.', 'Affair worthy of account; thing of consequence; importance;\n   significance; moment; -- chiefly in the phrases what matter ? no\n   matter, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matter', 'n.', 'Inducing cause or occasion, especially of anything\n   disagreeable or distressing; difficulty; trouble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matter', 'n.', 'Amount; quantity; portion; space; -- often indefinite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matter', 'n.', 'Substance excreted from living animal bodies; that which is\n   thrown out or discharged in a tumor, boil, or abscess; pus; purulent\n   substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matter', 'n.', 'That which is permanent, or is supposed to be given, and in\n   or upon which changes are effected by psychological or physical\n   processes and relations; -- opposed to form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matter', 'n.', 'Written manuscript, or anything to be set in type; copy;\n   also, type set up and ready to be used, or which has been used, in\n   printing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mattered', 'imp. & p. p.', 'of Matter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mattering', 'p. pr. & vb. n.', 'of Matter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matter', 'v. i.', 'To be of importance; to import; to signify.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matter', 'v. i.', 'To form pus or matter, as an abscess; to maturate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matter', 'v. t.', 'To regard as important; to take account of; to care for.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matterless', 'a.', 'Not being, or having, matter; as, matterless spirits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matterless', 'a.', 'Unimportant; immaterial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matter-of-fact', 'a.', 'Adhering to facts; not turning aside from absolute\n   realities; not fanciful or imaginative; commonplace; dry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mattery', 'a.', 'Generating or containing pus; purulent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mattery', 'a.', 'Full of substance or matter; important.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matting', 'v. t. & i.', 'The act of interweaving or tangling together so as\n   to make a mat; the process of becoming matted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matting', 'v. t. & i.', 'Mats, in general, or collectively; mat work; a\n   matlike fabric, for use in covering floors, packing articles, and the\n   like; a kind of carpeting made of straw, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matting', 'v. t. & i.', 'Materials for mats.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matting', 'v. t. & i.', 'An ornamental border. See 3d Mat, 4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matting', 'n.', 'A dull, lusterless surface in certain of the arts, as\n   gilding, metal work, glassmaking, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mattock', 'n.', 'An implement for digging and grubbing. The head has two\n   long steel blades, one like an adz and the other like a narrow ax or\n   the point of a pickax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mattowacca', 'n.', 'An American clupeoid fish (Clupea mediocris), similar\n   to the shad in habits and appearance, but smaller and less esteemed for\n   food; -- called also hickory shad, tailor shad, fall herring, and shad\n   herring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mattress', 'n.', 'A quilted bed; a bed stuffed with hair, moss, or other\n   suitable material, and quilted or otherwise fastened.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mattress', 'n.', 'A mass of interwoven brush, poles, etc., to protect a\n   bank from being worn away by currents or waves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maturant', 'n.', 'A medicine, or application, which promotes suppuration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maturated', 'imp. & p. p.', 'of Maturate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maturating', 'p. pr. & vb. n.', 'of Maturate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maturate', 'a.', 'To bring to ripeness or maturity; to ripen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maturate', 'a.', 'To promote the perfect suppuration of (an abscess).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maturate', 'v. i.', 'To ripen; to become mature; specif/cally, to\n   suppurate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maturation', 'n.', 'The process of bringing, or of coming, to maturity;\n   hence, specifically, the process of suppurating perfectly; the\n   formation of pus or matter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maturative', 'a.', 'Conducing to ripeness or maturity; hence, conducing to\n   suppuration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maturative', 'n.', 'A remedy promoting maturation; a maturant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mature', 'superl.', 'Brought by natural process to completeness of growth\n   and development; fitted by growth and development for any function,\n   action, or state, appropriate to its kind; full-grown; ripe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mature', 'superl.', 'Completely worked out; fully digested or prepared;\n   ready for action; made ready for destined application or use;\n   perfected; as, a mature plan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mature', 'superl.', 'Of or pertaining to a condition of full development;\n   as, a man of mature years.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mature', 'superl.', 'Come to, or in a state of, completed suppuration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matured', 'imp. & p. p.', 'of Mature');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maturing', 'p. pr. & vb. n.', 'of Mature');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mature', 'v. t.', 'To bring or hasten to maturity; to promote ripeness in;\n   to ripen; to complete; as, to mature ones plans.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mature', 'v. i.', 'To advance toward maturity; to become ripe; as, wine\n   matures by age; the judgment matures by age and experience.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mature', 'v. i.', 'Hence, to become due, as a note.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maturely', 'adv.', 'In a mature manner; with ripeness; completely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maturely', 'adv.', 'With caution; deliberately.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maturely', 'adv.', 'Early; soon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matureness', 'n.', 'The state or quality of being mature; maturity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maturer', 'n.', 'One who brings to maturity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maturescent', 'a.', 'Approaching maturity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maturing', 'a.', 'Approaching maturity; as, maturing fruits; maturing notes\n   of hand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maturity', 'n.', 'The state or quality of being mature; ripeness; full\n   development; as, the maturity of corn or of grass; maturity of\n   judgment; the maturity of a plan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maturity', 'n.', 'Arrival of the time fixed for payment; a becoming due;\n   termination of the period a note, etc., has to run.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matutinal', 'a.', 'Of or pertaining to the morning; early.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matutinary', 'a.', 'Matutinal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matutine', 'a.', 'Matutinal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matweed', 'n.', 'A name of several maritime grasses, as the sea sand-reed\n   (Ammophila arundinacea) which is used in Holland to bind the sand of\n   the seacoast dikes (see Beach grass, under Beach); also, the Lygeum\n   Spartum, a Mediterranean grass of similar habit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maty', 'n.', 'A native house servant in India.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Matzoth', 'n.', 'A cake of unleavened bread eaten by the Jews at the feast\n   of the Passover.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maucaco', 'n.', 'A lemur; -- applied to several species, as the\n   White-fronted, the ruffed, and the ring-tailed lemurs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maud', 'n.', 'A gray plaid; -- used by shepherds in Scotland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maudle', 'v. t.', 'To throw onto confusion or disorder; to render maudlin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maudlin', 'a.', 'Tearful; easily moved to tears; exciting to tears;\n   excessively sentimental; weak and silly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maudlin', 'a.', 'Drunk, or somewhat drunk; fuddled; given to drunkenness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maudlin', 'n.', 'Alt. of Maudeline');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maudeline', 'n.', 'An aromatic composite herb, the costmary; also, the\n   South European Achillea Ageratum, a kind of yarrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muadlinism', 'n.', 'A maudlin state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maudlinwort', 'n.', 'The oxeye daisy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mauger', 'prep.', 'Alt. of Maugre');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maugre', 'prep.', 'In spite of; in opposition to; notwithstanding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maugre', 'v. t.', 'To defy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maukin', 'n.', 'See Malkin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maukin', 'n.', 'A hare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maul', 'n.', 'A heavy wooden hammer or beetle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mauled', 'imp. & p. p.', 'of Maul');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mauling', 'p. pr. & vb. n.', 'of Maul');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maul', 'v. t.', 'To beat and bruise with a heavy stick or cudgel; to wound\n   in a coarse manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maul', 'v. t.', 'To injure greatly; to do much harm to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maule', 'n.', 'The common mallow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mauling', 'n.', 'A severe beating with a stick, cudgel, or the fist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maul-stick', 'n.', 'A stick used by painters as a rest for the hand while\n   working.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maumet', 'n.', 'See Mawmet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maunch', 'v. t.', 'To munch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maunch', 'n.', 'See Manche.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maund', 'n.', 'A hand basket.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maund', 'n.', 'An East Indian weight, varying in different localities from\n   25 to about 82 pounds avoirdupois.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maund', 'v. i.', 'Alt. of Maunder');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maunder', 'v. i.', 'To beg.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maunder', 'v. i.', 'To mutter; to mumble; to grumble; to speak indistinctly\n   or disconnectedly; to talk incoherently.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maunder', 'v. t.', 'To utter in a grumbling manner; to mutter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maunder', 'n.', 'A beggar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maunderer', 'n.', 'One who maunders.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maundril', 'n.', 'A pick with two prongs, to pry with.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maundy Thursday', '', 'The Thursday in Passion week, or next before Good\n   Friday.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maungy', 'a.', 'Mangy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mauresque', 'a. & n.', 'See Moresque.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maurist', 'n.', 'A member of the Congregation of Saint Maur, an offshoot of\n   the Benedictines, originating in France in the early part of the\n   seventeenth century. The Maurists have been distinguished for their\n   interest in literature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mausolean', 'a.', 'Pertaining to a mausoleum; monumental.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mausoleums', 'pl. ', 'of Mausoleum');
  INSERT INTO words (word, word_type, description) VALUES  (    '-lea', 'pl. ', 'of Mausoleum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mausoleum', 'n.', 'A magnificent tomb, or stately sepulchral monument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mauther', 'n.', 'A girl; esp., a great, awkward girl; a wench.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mauvaniline', 'n.', 'See Mauve aniline, under Mauve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mauve', 'n.', 'A color of a delicate purple, violet, or lilac.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mauveine', 'n.', 'An artificial organic base, obtained by oxidizing a\n   mixture of aniline and toluidine, and valuable for the dyestuffs it\n   forms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mauvine', 'a.', 'Mauve-colored.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maverick', 'n.', 'In the southwestern part of the united States, a bullock\n   or heifer that has not been branded, and is unclaimed or wild; -- said\n   to be from Maverick, the name of a cattle owner in Texas who neglected\n   to brand his cattle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mavis', 'n.', 'The European throstle or song thrush (Turdus musicus).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maw', 'n.', 'A gull.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maw', 'n.', 'A stomach; the receptacle into which food is taken by\n   swallowing; in birds, the craw; -- now used only of the lower animals,\n   exept humorously or in contempt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maw', 'n.', 'Appetite; inclination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maw', 'n.', 'An old game at cards.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mawk', 'n.', 'A maggot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mawk', 'n.', 'A slattern; a mawks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mawkin', 'n.', 'See Malkin, and Maukin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mawkingly', 'adv.', 'Slatternly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mawkish', 'a.', 'Apt to cause satiety or loathing; nauseous; disgusting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mawkish', 'a.', 'Easily disgusted; squeamish; sentimentally fastidious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mawkishly', 'adv.', 'In a mawkish way.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mawkishness', 'n.', 'The quality or state of being mawkish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mawks', 'n.', 'A slattern; a mawk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mawky', 'a.', 'Maggoty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mawmet', 'n.', 'A puppet; a doll; originally, an idol, because in the\n   Middle Ages it was generally believed that the Mohammedans worshiped\n   images representing Mohammed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mawmetry', 'n.', 'The religion of Mohammed; also, idolatry. See Mawmet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mawmish', 'a.', 'Nauseous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mawseed', 'n.', 'The seed of the opium poppy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mawworm', 'n.', 'Any intestinal worm found in the stomach, esp. the common\n   round worm (Ascaris lumbricoides), and allied species.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mawworm', 'n.', 'One of the larvae of botflies of horses; a bot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maxillae', 'pl. ', 'of Maxilla');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maxilla', 'n.', 'The bone of either the upper or the under jaw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maxilla', 'n.', 'The bone, or principal bone, of the upper jaw, the bone of\n   the lower jaw being the mandible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maxilla', 'n.', 'One of the lower or outer jaws of arthropods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maxillar', 'a.', 'Alt. of Maxillary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maxillary', 'a.', 'Pertaining to either the upper or the lower jaw, but now\n   usually applied to the upper jaw only.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maxillary', 'n.', 'The principal maxillary bone; the maxilla.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maxillary', 'n.', 'Of or pertaining to a maxilla.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maxilliform', 'a.', 'Having the form, or structure, of a maxilla.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maxilliped', 'n.', 'One of the mouth appendages of Crustacea, situated next\n   behind the maxillae. Crabs have three pairs, but many of the lower\n   Crustacea have but one pair of them. Called also jawfoot, and foot jaw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maxillo-mandibular', 'a.', 'Pertaining to the maxilla and mandible; as, the\n   maxillo-mandibular nerve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maxillo-palatine', 'a.', 'Pertaining to the maxillary and palatine regions\n   of the skull; as, the maxillo-palatine process of the maxilla. Also\n   used as n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maxilloturbinal', 'a.', 'Pertaining to the maxillary and turbinal regions\n   of the skull.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maxilloturbinal', 'n.', 'The maxillo-turbinal, or inferior turbinate, bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maxim', 'n.', 'An established principle or proposition; a condensed\n   proposition of important practical truth; an axiom of practical wisdom;\n   an adage; a proverb; an aphorism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maxim', 'n.', 'The longest note formerly used, equal to two longs, or four\n   breves; a large.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maximilian', 'n.', 'A gold coin of Bavaria, of the value of about 13s. 6d.\n   sterling, or about three dollars and a quarter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maximization', 'n.', 'The act or process of increasing to the highest\n   degree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maximize', 'v. t.', 'To increase to the highest degree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maxima', 'pl. ', 'of Maximum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maximum', 'n.', 'The greatest quantity or value attainable in a given case;\n   or, the greatest value attained by a quantity which first increases and\n   then begins to decrease; the highest point or degree; -- opposed to\n   minimum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maximum', 'a.', 'Greatest in quantity or highest in degree attainable or\n   attained; as, a maximum consumption of fuel; maximum pressure; maximum\n   heat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Might', 'imp.', 'of May');
  INSERT INTO words (word, word_type, description) VALUES  (    'May', 'v.', 'An auxiliary verb qualifyng the meaning of another verb, by\n   expressing: (a) Ability, competency, or possibility; -- now oftener\n   expressed by can.');
  INSERT INTO words (word, word_type, description) VALUES  (    'May', 'n.', 'A maiden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'May', 'n.', 'The fifth month of the year, containing thirty-one days.');
  INSERT INTO words (word, word_type, description) VALUES  (    'May', 'n.', 'The early part or springtime of life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'May', 'n.', 'The flowers of the hawthorn; -- so called from their time of\n   blossoming; also, the hawthorn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'May', 'n.', 'The merrymaking of May Day.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maya', 'n.', 'The name for the doctrine of the unreality of matter, called,\n   in English, idealism; hence, nothingness; vanity; illusion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maybe', 'adv.', 'Perhaps; possibly; peradventure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maybe', 'a.', 'Possible; probable, but not sure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maybe', 'n.', 'Possibility; uncertainty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maybird', 'n.', 'The whimbrel; -- called also May fowl, May curlew, and May\n   whaap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maybird', 'n.', 'The knot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maybird', 'n.', 'The bobolink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maybloom', 'n.', 'The hawthorn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maybush', 'n.', 'The hawthorn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mayduke', 'n.', 'A large dark-red cherry of excellent quality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mayfish', 'n.', 'A common American minnow (Fundulus majalis). See Minnow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mayflower', 'n.', 'In England, the hawthorn; in New England, the trailing\n   arbutus (see Arbutus); also, the blossom of these plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mayhap', 'adv.', 'Perhaps; peradventure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mayhem', 'n.', 'The maiming of a person by depriving him of the use of any\n   of his members which are necessary for defense or protection. See Maim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maying', 'n.', 'The celebrating of May Day.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mayonnaise', 'n.', 'A sauce compounded of raw yolks of eggs beaten up with\n   olive oil to the consistency of a sirup, and seasoned with vinegar,\n   pepper, salt, etc.; -- used in dressing salads, fish, etc. Also, a dish\n   dressed with this sauce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mayor', 'n.', 'The chief magistrate of a city or borough; the chief officer\n   of a municipal corporation. In some American cities there is a city\n   court of which the major is chief judge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mayoral', 'n.', 'The conductir of a mule team; also, a head shepherd.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mayoralty', 'n.', 'The office, or the term of office, of a mayor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mayoress', 'n.', 'The wife of a mayor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mayorship', 'n.', 'The office of a mayor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maypole', 'n.', 'A tall pole erected in an open place and wreathed with\n   flowers, about which the rustic May-day sports were had.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maypop', 'n.', 'The edible fruit of a passion flower, especially that of\n   the North American Passiflora incarnata, an oval yellowish berry as\n   large as a small apple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mayweed', 'n.', 'A composite plant (Anthemis Cotula), having a strong odor;\n   dogs fennel. It is a native of Europe, now common by the roadsides in\n   the United States.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mayweed', 'n.', 'The feverfew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mazama', 'n.', 'Alt. of Mazame');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mazame', 'n.', 'A goatlike antelope (Haplocerus montanus) which inhabits\n   the Rocky Mountains, frequenting the highest parts; -- called also\n   mountain goat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mazard', 'n.', 'A kind of small black cherry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mazard', 'n.', 'The jaw; the head or skull.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mazard', 'v. t.', 'To knock on the head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mazarine', 'a.', 'Of or pertaining to Cardinal Mazarin, prime minister of\n   France, 1643-1661.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mazarine', 'n.', 'Mazarine blue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mazdean', 'a.', 'Of or pertaining to Ahura-Mazda, or Ormuzd, the beneficent\n   deity in the Zoroastrian dualistic system; hence, Zoroastrian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mazdeism', 'n.', 'The Zoroastrian religion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maze', 'n.', 'A wild fancy; a confused notion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maze', 'n.', 'Confusion of thought; perplexity; uncertainty; state of\n   bewilderment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maze', 'n.', 'A confusing and baffling network, as of paths or passages; an\n   intricacy; a labyrinth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mazed', 'imp. & p. p.', 'of Maze');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mazing', 'p. pr. & vb. n.', 'of Maze');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maze', 'v. t.', 'To perplex greatly; to bewilder; to astonish and confuse;\n   to amaze.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maze', 'v. i.', 'To be bewildered.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mazedness', 'n.', 'The condition of being mazed; confusion; astonishment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mazeful', 'a.', 'Mazy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mazer', 'n.', 'A large drinking bowl; -- originally made of maple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mazily', 'adv.', 'In a mazy manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maziness', 'n.', 'The state or quality of being mazy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mazological', 'a.', 'Of or pertaining to mazology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mazologist', 'n.', 'One versed in mazology or mastology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mazology', 'n.', 'Same as Mastology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mazourka', 'n.', 'Alt. of Mazurka');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mazurka', 'n.', 'A Polish dance, or the music which accompanies it, usually\n   in 3-4 or 3-8 measure, with a strong accent on the second beat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mazy', 'a.', 'Perplexed with turns and windings; winding; intricate;\n   confusing; perplexing; embarrassing; as, mazy error.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Me', 'pron.', 'One. See Men, pron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Me', 'pers. pron.', 'The person speaking, regarded as an object; myself; a\n   pronoun of the first person used as the objective and dative case of\n   the pronoum I; as, he struck me; he gave me the money, or he gave the\n   money to me; he got me a hat, or he got a hat for me.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meach', 'v. i.', 'To skulk; to cower. See Mich.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meacock', 'n.', 'An uxorious, effeminate, or spiritless man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mead', 'n.', 'A fermented drink made of water and honey with malt, yeast,\n   etc.; metheglin; hydromel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mead', 'n.', 'A drink composed of sirup of sarsaparilla or other flavoring\n   extract, and water. It is sometimes charged with carbonic acid gas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mead', 'n.', 'A meadow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meadow', 'n.', 'A tract of low or level land producing grass which is mown\n   for hay; any field on which grass is grown for hay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meadow', 'n.', 'Low land covered with coarse grass or rank herbage near\n   rives and in marshy places by the sea; as, the salt meadows near Newark\n   Bay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meadow', 'a.', 'Of or pertaining to a meadow; of the nature of a meadow;\n   produced, growing, or living in, a meadow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meadowsweet', 'n.', 'Alt. of Meadowwort');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meadowwort', 'n.', 'The name of several plants of the genus Spiraea,\n   especially the white- or pink-flowered S. salicifolia, a low European\n   and American shrub, and the herbaceous S. Ulmaria, which has fragrant\n   white flowers in compound cymes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meadowy', 'a.', 'Of or pertaining to meadows; resembling, or consisting of,\n   meadow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meager', 'a.', 'Alt. of Meagre');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meagre', 'a.', 'Destitue of, or having little, flesh; lean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meagre', 'a.', 'Destitute of richness, fertility, strength, or the like;\n   defective in quantity, or poor in quality; poor; barren; scanty in\n   ideas; wanting strength of diction or affluence of imagery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meagre', 'a.', 'Dry and harsh to the touch, as chalk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meager', 'v. t.', 'Alt. of Meagre');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meagre', 'v. t.', 'To make lean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meagerly', 'adv.', 'Alt. of Meagrely');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meagrely', 'adv.', 'Poorly; thinly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meagerness', 'n.', 'Alt. of Meagreness');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meagreness', 'n.', 'The state or quality of being meager; leanness;\n   scantiness; barrenness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meagre', 'n.', 'A large European sciaenoid fish (Sciaena umbra or S.\n   aquila), having white bloodless flesh. It is valued as a food fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meak', 'n.', 'A hook with a long handle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meaking', 'n.', 'The process of picking out the oakum from the seams of a\n   vessel which is to be recalked.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meal', 'n.', 'A part; a fragment; a portion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meal', 'n.', 'The portion of food taken at a particular time for the\n   satisfaction of appetite; the quantity usually taken at one time with\n   the purpose of satisfying hunger; a repast; the act or time of eating a\n   meal; as, the traveler has not eaten a good meal for a week; there was\n   silence during the meal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meal', 'n.', 'Grain (esp. maize, rye, or oats) that is coarsely ground and\n   unbolted; also, a kind of flour made from beans, pease, etc.;\n   sometimes, any flour, esp. if coarse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meal', 'n.', 'Any substance that is coarsely pulverized like meal, but not\n   granulated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meal', 'v. t.', 'To sprinkle with, or as with, meal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meal', 'v. t.', 'To pulverize; as, mealed powder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mealies', 'n. pl.', 'Maize or Indian corn; -- the common name in South\n   Africa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mealiness', 'n.', 'The quality or state of being mealy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meal-mouthed', 'a.', 'See Mealy-mouthed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mealtime', 'n.', 'The usual time of eating a meal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mealy', 'superl.', 'Having the qualities of meal; resembling meal; soft,\n   dry, and friable; easily reduced to a condition resembling meal; as, a\n   mealy potato.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mealy', 'superl.', 'Overspread with something that resembles meal; as, the\n   mealy wings of an insect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mealy-mouthed', 'a.', 'Using soft words; plausible; affectedly or timidly\n   delicate of speech; unwilling to tell the truth in plain language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meant', 'imp. & p. p.', 'of Mean');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meaning', 'p. pr. & vb. n.', 'of Mean');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mean', 'v. t.', 'To have in the mind, as a purpose, intention, etc.; to\n   intend; to purpose; to design; as, what do you mean to do ?');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mean', 'v. t.', 'To signify; to indicate; to import; to denote.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mean', 'v. i.', 'To have a purpose or intention.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mean', 'superl.', 'Destitute of distinction or eminence; common; low;\n   vulgar; humble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mean', 'superl.', 'Wanting dignity of mind; low-minded; base; destitute of\n   honor; spiritless; as, a mean motive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mean', 'superl.', 'Of little value or account; worthy of little or no\n   regard; contemptible; despicable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mean', 'superl.', 'Of poor quality; as, mean fare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mean', 'superl.', 'Penurious; stingy; close-fisted; illiberal; as, mean\n   hospitality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mean', 'a.', 'Occupying a middle position; middle; being about midway\n   between extremes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mean', 'a.', 'Intermediate in excellence of any kind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mean', 'a.', 'Average; having an intermediate value between two extremes,\n   or between the several successive values of a variable quantity during\n   one cycle of variation; as, mean distance; mean motion; mean solar day.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mean', 'n.', 'That which is mean, or intermediate, between two extremes of\n   place, time, or number; the middle point or place; middle rate or\n   degree; mediocrity; medium; absence of extremes or excess; moderation;\n   measure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mean', 'n.', 'A quantity having an intermediate value between several\n   others, from which it is derived, and of which it expresses the\n   resultant value; usually, unless otherwise specified, it is the simple\n   average, formed by adding the quantities together and dividing by their\n   number, which is called an arithmetical mean. A geometrical mean is the\n   square root of the product of the quantities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mean', 'n.', 'That through which, or by the help of which, an end is\n   attained; something tending to an object desired; intermediate agency\n   or measure; necessary condition or coagent; instrument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mean', 'n.', 'Hence: Resources; property, revenue, or the like, considered\n   as the condition of easy livelihood, or an instrumentality at command\n   for effecting any purpose; disposable force or substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mean', 'n.', 'A part, whether alto or tenor, intermediate between the\n   soprano and base; a middle part.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mean', 'n.', 'Meantime; meanwhile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mean', 'n.', 'A mediator; a go-between.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meander', 'n.', 'A winding, crooked, or involved course; as, the meanders\n   of the veins and arteries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meander', 'n.', 'A tortuous or intricate movement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meander', 'n.', 'Fretwork. See Fret.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meander', 'v. t.', 'To wind, turn, or twist; to make flexuous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meandered', 'imp. & p. p.', 'of Meander');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meandering', 'p. pr. & vb. n.', 'of Meander');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meander', 'v. i.', 'To wind or turn in a course or passage; to be\n   intricate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meandrian', 'a.', 'Winding; having many turns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meandrina', 'n.', 'A genus of corals with meandering grooves and ridges,\n   including the brain corals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meandrous', 'a.', 'Alt. of Meandry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meandry', 'a.', 'Winding; flexuous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meaning', 'n.', 'That which is meant or intended; intent; purpose; aim;\n   object; as, a mischievous meaning was apparent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meaning', 'n.', 'That which is signified, whether by act lanquage;\n   signification; sence; import; as, the meaning of a hint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meaning', 'n.', 'Sense; power of thinking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meanly', 'adv.', 'Moderately.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meanly', 'adv.', 'In a mean manner; unworthily; basely; poorly;\n   ungenerously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meanness', 'n.', 'The condition, or quality, of being mean; want of\n   excellence; poorness; lowness; baseness; sordidness; stinginess.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meanness', 'n.', 'A mean act; as, to be guilty of meanness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mean-spirited', 'a.', 'Of a mean spirit; base; groveling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meant', '', 'imp. & p. p. of Mean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meantime', 'n.', 'Alt. of Meanwhile');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meanwhile', 'n.', 'The intervening time; as, in the meantime (or mean\n   time).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meantime', 'adv.', 'Alt. of Meanwhile');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meanwhile', 'adv.', 'In the intervening time; during the interval.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mear', 'n.', 'A boundary. See Mere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mease', 'n.', 'Five hundred; as, a mease of herrings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Measelry', 'n.', 'Leprosy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Measle', 'n.', 'A leper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Measle', 'n.', 'A tapeworm larva. See 2d Measles, 4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Measled', 'a.', 'Infected or spotted with measles, as pork.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Measles', 'n.', 'Leprosy; also, a leper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Measles', 'n.', 'A contagious febrile disorder commencing with catarrhal\n   symptoms, and marked by the appearance on the third day of an eruption\n   of distinct red circular spots, which coalesce in a crescentic form,\n   are slightly raised above the surface, and after the fourth day of the\n   eruption gradually decline; rubeola.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Measles', 'n.', 'A disease of cattle and swine in which the flesh is filled\n   with the embryos of different varieties of the tapeworm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Measles', 'n.', 'A disease of trees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Measles', 'n.', 'The larvae of any tapeworm (Taenia) in the cysticerus\n   stage, when contained in meat. Called also bladder worms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Measly', 'a.', 'Infected with measles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Measly', 'a.', 'Containing larval tapeworms; -- said of pork and beef.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Measurable', 'a.', 'Capable of being measured; susceptible of mensuration\n   or computation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Measurable', 'a.', 'Moderate; temperate; not excessive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Measure', 'n.', 'A standard of dimension; a fixed unit of quantity or\n   extent; an extent or quantity in the fractions or multiples of which\n   anything is estimated and stated; hence, a rule by which anything is\n   adjusted or judged.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Measure', 'n.', 'An instrument by means of which size or quantity is\n   measured, as a graduated line, rod, vessel, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Measure', 'n.', 'The dimensions or capacity of anything, reckoned according\n   to some standard; size or extent, determined and stated; estimated\n   extent; as, to take ones measure for a coat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Measure', 'n.', 'The contents of a vessel by which quantity is measured; a\n   quantity determined by a standard; a stated or limited quantity or\n   amount.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Measure', 'n.', 'Extent or degree not excessive or beyong bounds;\n   moderation; due restraint; esp. in the phrases, in measure; with\n   measure; without or beyond measure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Measure', 'n.', 'Determined extent, not to be exceeded; limit; allotted\n   share, as of action, influence, ability, or the like; due proportion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Measure', 'n.', 'The quantity determined by measuring, especially in buying\n   and selling; as, to give good or full measure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Measure', 'n.', 'Undefined quantity; extent; degree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Measure', 'n.', 'Regulated division of movement');
  INSERT INTO words (word, word_type, description) VALUES  (    'Measure', 'n.', 'A regulated movement corresponding to the time in which\n   the accompanying music is performed; but, especially, a slow and\n   stately dance, like the minuet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Measure', 'n.', 'The group or grouping of beats, caused by the regular\n   recurrence of accented beats.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Measure', 'n.', 'The space between two bars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Measure', 'a.', 'The manner of ordering and combining the quantities, or\n   long and short syllables; meter; rhythm; hence, a foot; as, a poem in\n   iambic measure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Measure', 'a.', 'A number which is contained in a given number a number of\n   times without a remainder; as in the phrases, the common measure, the\n   greatest common measure, etc., of two or more numbers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Measure', 'a.', 'A step or definite part of a progressive course or policy;\n   a means to an end; an act designed for the accomplishment of an object;\n   as, political measures; prudent measures; an inefficient measure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Measure', 'a.', 'The act of measuring; measurement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Measure', 'a.', 'Beds or strata; as, coal measures; lead measures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Measured', 'imp. & p. p.', 'of Measure');
  INSERT INTO words (word, word_type, description) VALUES  (    'Measuring', 'p. pr. & vb. n.', 'of Measure');
  INSERT INTO words (word, word_type, description) VALUES  (    'Measure', 'n.', 'To ascertain by use of a measuring instrument; to compute\n   or ascertain the extent, quantity, dimensions, or capacity of, by a\n   certain rule or standard; to take the dimensions of; hence, to\n   estimate; to judge of; to value; to appraise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Measure', 'n.', 'To serve as the measure of; as, the thermometer measures\n   changes of temperature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Measure', 'n.', 'To pass throught or over in journeying, as if laying off\n   and determining the distance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Measure', 'n.', 'To adjust by a rule or standard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Measure', 'n.', 'To allot or distribute by measure; to set off or apart by\n   measure; -- often with out or off.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Measure', 'v. i.', 'To make a measurement or measurements.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Measure', 'v. i.', 'To result, or turn out, on measuring; as, the grain\n   measures well; the pieces measure unequally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Measure', 'v. i.', 'To be of a certain size or quantity, or to have a\n   certain length, breadth, or thickness, or a certain capacity according\n   to a standard measure; as, cloth measures three fourths of a yard; a\n   tree measures three feet in diameter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Measured', 'a.', 'Regulated or determined by a standard; hence, equal;\n   uniform; graduated; limited; moderated; as, he walked with measured\n   steps; he expressed himself in no measured terms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Measureless', 'a.', 'Without measure; unlimited; immeasurable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Measurement', 'n.', 'The act or result of measuring; mensuration; as,\n   measurement is required.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Measurement', 'n.', 'The extent, size, capacity, amount. or quantity\n   ascertained by measuring; as, its measurement is five acres.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Measurer', 'n.', 'One who measures; one whose occupation or duty is to\n   measure commondities in market.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Measuring', 'a.', 'Used in, or adapted for, ascertaining measurements, or\n   dividing by measure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meat', 'n.', 'Food, in general; anything eaten for nourishment, either by\n   man or beast. Hence, the edible part of anything; as, the meat of a\n   lobster, a nut, or an egg.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meat', 'n.', 'The flesh of animals used as food; esp., animal muscle; as, a\n   breakfast of bread and fruit without meat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meat', 'n.', 'Specifically, dinner; the chief meal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meat', 'v. t.', 'To supply with food.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meatal', 'a.', 'Of or pertaining to a meatus; resembling a meatus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meated', 'a.', 'Fed; fattened.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meated', 'a.', 'Having (such) meat; -- used chiefly in composition; as,\n   thick-meated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meath', 'n.', 'Alt. of Meathe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meathe', 'n.', 'A sweet liquor; mead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meatiness', 'n.', 'Quality of being meaty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meatless', 'a.', 'Having no meat; without food.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meatoscope', 'n.', 'A speculum for examining a natural passage, as the\n   urethra.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meatotome', 'n.', 'An instrument for cutting into the urethra so as to\n   enlarge its orifice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meatuses', 'pl. ', 'of Meatus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meatus', 'n. sing. & pl.', 'A natural passage or canal; as, the external\n   auditory meatus. See Illust. of Ear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meaty', 'a.', 'Abounding in meat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meaw', 'n.', 'The sea mew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meaw', 'v. i.', 'See Mew, to cry as a cat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meawl', 'v. i.', 'See Mewl, and Miaul.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meazel', 'n.', 'See 1st Measle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meazling', 'a.', 'Falling in small drops; mistling; mizzing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mebles', 'n. pl.', 'See Moebles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mecate', 'n.', 'A rope of hair or of maguey fiber, for tying horses, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meccawee', 'a.', 'Of or pertaining to Mecca, in Arabia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meccawee', 'n.', 'A native or inhabitant of Mecca.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mechanic', 'a.', 'The art of the application of the laws of motion or force\n   to construction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mechanic', 'a.', 'A mechanician; an artisan; an artificer; one who\n   practices any mechanic art; one skilled or employed in shaping and\n   uniting materials, as wood, metal, etc., into any kind of structure,\n   machine, or other object, requiring the use of tools, or instruments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mechanic', 'a.', 'Having to do with the application of the laws of motion\n   in the art of constructing or making things; of or pertaining to\n   mechanics; mechanical; as, the mechanic arts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mechanic', 'a.', 'Of or pertaining to a mechanic or artificer, or to the\n   class of artisans; hence, rude; common; vulgar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mechanic', 'a.', 'Base.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mechanical', 'a.', 'Pertaining to, governed by, or in accordance with,\n   mechanics, or the laws of motion; pertaining to the quantitative\n   relations of force and matter, as distinguished from mental, vital,\n   chemical, etc.; as, mechanical principles; a mechanical theory;\n   mechanical deposits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mechanical', 'a.', 'Of or pertaining to a machine or to machinery or tools;\n   made or formed by a machine or with tools; as, mechanical precision;\n   mechanical products.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mechanical', 'a.', 'Done as if by a machine; uninfluenced by will or\n   emotion; proceeding automatically, or by habit, without special\n   intention or reflection; as, mechanical singing; mechanical verses;\n   mechanical service.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mechanical', 'a.', 'Made and operated by interaction of forces without a\n   directing intelligence; as, a mechanical universe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mechanical', 'a.', 'Obtained by trial, by measurements, etc.; approximate;\n   empirical. See the 2d Note under Geometric.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mechanical', 'n.', 'A mechanic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mechanicalize', 'v. t.', 'To cause to become mechanical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mechanically', 'adv.', 'In a mechanical manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mechanicalness', 'n.', 'The state or quality of being mechanical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mechanician', 'n.', 'One skilled in the theory or construction of machines;\n   a machinist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mechanico-chemical', 'a.', 'Pertaining to, connected with, or dependent\n   upon, both mechanics and chemistry; -- said especially of those\n   sciences which treat of such phenomena as seem to depend on the laws\n   both of mechanics and chemistry, as electricity and magnetism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mechanics', 'n.', 'That science, or branch of applied mathematics, which\n   treats of the action of forces on bodies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mechanism', 'n.', 'The arrangement or relation of the parts of a machine;\n   the parts of a machine, taken collectively; the arrangement or relation\n   of the parts of anything as adapted to produce an effect; as, the\n   mechanism of a watch; the mechanism of a sewing machine; the mechanism\n   of a seed pod.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mechanism', 'n.', 'Mechanical operation or action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mechanism', 'n.', 'An ideal machine; a combination of movable bodies\n   constituting a machine, but considered only with regard to relative\n   movements.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mechanist', 'n.', 'A maker of machines; one skilled in mechanics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mechanist', 'n.', 'One who regards the phenomena of nature as the effects\n   of forces merely mechanical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mechanized', 'imp. & p. p.', 'of Mechanize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mechanizing', 'p. pr. & vb. n.', 'of Mechanize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mechanize', 'v. t.', 'To cause to be mechanical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mechanograph', 'n.', 'One of a number of copies of anything multiplied\n   mechanically.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mechanographic', 'a.', 'Treating of mechanics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mechanographic', 'a.', 'Written, copied, or recorded by machinery; produced\n   by mechanography; as, a mechanographic record of changes of\n   temperature; mechanographic prints.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mechanographist', 'n.', 'An artist who, by mechanical means, multiplies\n   copies of works of art.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mechanography', 'n.', 'The art of mechanically multiplying copies of a\n   writing, or any work of art.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mechanurgy', 'n.', 'That branch of science which treats of moving machines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mechitarist', 'n.', 'One of a religious congregation of the Roman Catholic\n   Church devoted to the improvement of Armenians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mechlin', 'n.', 'A kind of lace made at, or originating in, Mechlin, in\n   Belgium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mechoacan', 'n.', 'A species of jalap, of very feeble properties, said to\n   be obtained from the root of a species of Convolvulus (C. Mechoacan);\n   -- so called from Michoacan, in Mexico, whence it is obtained.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meckelian', 'a.', 'Pertaining to, or discovered by, J. F. Meckel, a German\n   anatomist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meconate', 'n.', 'A salt of meconic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meconic', 'a.', 'Pertaining to, or obtained from, the poppy or opium;\n   specif. (Chem.), designating an acid related to aconitic acid, found in\n   opium and extracted as a white crystalline substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meconidine', 'n.', 'An alkaloid found in opium, and extracted as a yellow\n   amorphous substance which is easily decomposed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meconidium', 'n.', 'A kind of gonophore produced by hydroids of the genus\n   Gonothyraea. It has tentacles, and otherwise resembles a free medusa,\n   but remains attached by a pedicel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meconin', 'n.', 'A substance regarded as an anhydride of meconinic acid,\n   existing in opium and extracted as a white crystalline substance. Also\n   erroneously called meconina, meconia, etc., as though it were an\n   alkaloid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meconinic', 'a.', 'Pertaining to, or designating, an acid which occurs in\n   opium, and which may be obtained by oxidizing narcotine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meconium', 'n.', 'Opium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meconium', 'n.', 'The contents of the fetal intestine; hence, first\n   excrement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Medal', 'n.', 'A piece of metal in the form of a coin, struck with a\n   device, and intended to preserve the remembrance of a notable event or\n   an illustrious person, or to serve as a reward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Medaled', 'imp. & p. p.', 'of Medal');
  INSERT INTO words (word, word_type, description) VALUES  (    'Medalled', '', 'of Medal');
  INSERT INTO words (word, word_type, description) VALUES  (    'Medaling', 'p. pr. & vb. n.', 'of Medal');
  INSERT INTO words (word, word_type, description) VALUES  (    'Medalling', '', 'of Medal');
  INSERT INTO words (word, word_type, description) VALUES  (    'Medal', 'v. t.', 'To honor or reward with a medal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Medalet', 'n.', 'A small medal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Medalist', 'n.', 'A person that is skilled or curious in medals; a\n   collector of medals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Medalist', 'n.', 'A designer of medals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Medalist', 'n.', 'One who has gained a medal as the reward of merit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Medallic', 'a.', 'Of or pertaining to a medal, or to medals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Medallion', 'n.', 'A large medal or memorial coin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Medallion', 'n.', 'A circular or oval (or, sometimes, square) tablet\n   bearing a figure or figures represented in relief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Medalurgy', 'n.', 'The art of making and striking medals and coins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meddled', 'imp. & p. p.', 'of Meddle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meddling', 'p. pr. & vb. n.', 'of Meddle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meddle', 'v. i.', 'To mix; to mingle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meddle', 'v. i.', 'To interest or engage ones self; to have to do; -- / a\n   good sense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meddle', 'v. i.', 'To interest or engage ones self unnecessarily or\n   impertinently, to interfere or busy ones self improperly with\n   anothers affairs; specifically, to handle or distrub anothers\n   property without permission; -- often followed by with or in.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meddle', 'v. t.', 'To mix; to mingle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meddler', 'n.', 'One who meddles; one who interferes or busies himself with\n   things in which he has no concern; an officious person; a busybody.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meddlesome', 'a.', 'Given to meddling; apt to interpose in the affairs of\n   others; officiously intrusive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meddling', 'a.', 'Meddlesome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meddlingly', 'adv.', 'In a meddling manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mede', 'n.', 'A native or inhabitant of Media in Asia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mede', 'n.', 'See lst & 2d Mead, and Meed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Media', 'n.', 'pl. of Medium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mediae', 'pl. ', 'of Media');
  INSERT INTO words (word, word_type, description) VALUES  (    'Media', 'n.', 'One of the sonant mutes /, /, / (b, d, g), in Greek, or of\n   their equivalents in other languages, so named as intermediate between\n   the tenues, /, /, / (p, t, k), and the aspiratae (aspirates) /, /, /\n   (ph or f, th, ch). Also called middle mute, or medial, and sometimes\n   soft mute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mediacy', 'n.', 'The state or quality of being mediate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mediaeval', 'a.', 'Of or relating to the Middle Ages; as, mediaeval\n   architecture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mediaevalism', 'n.', 'The method or spirit of the Middle Ages; devotion to\n   the institutions and practices of the Middle Ages; a survival from the\n   Middle Ages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mediaevalist', 'n.', 'One who has a taste for, or is versed in, the history\n   of the Middle Ages; one in sympathy with the spirit or forms of the\n   Middle Ages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mediaevally', 'adv.', 'In the manner of the Middle Ages; in accordance with\n   mediaevalism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mediaevals', 'n. pl.', 'The people who lived in the Middle Ages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Medial', 'a.', 'Of or pertaining to a mean or average; mean; as, medial\n   alligation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Medial', 'n.', 'See 2d Media.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Medialuna', 'n.', 'See Half-moon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Median', 'a.', 'Being in the middle; running through the middle; as, a\n   median groove.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Median', 'a.', 'Situated in the middle; lying in a plane dividing a\n   bilateral animal into right and left halves; -- said of unpaired organs\n   and parts; as, median coverts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Median', 'n.', 'A median line or point.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mediant', 'n.', 'The third above the keynote; -- so called because it\n   divides the interval between the tonic and dominant into two thirds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mediastinal', 'a.', 'Of or pertaining to a mediastinum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mediastine', 'n.', 'Alt. of Mediastinum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mediastinum', 'n.', 'A partition; a septum; specifically, the folds of the\n   pleura (and the space included between them) which divide the thorax\n   into a right and left cavity. The space included between these folds of\n   the pleura, called the mediastinal space, contains the heart and gives\n   passage to the esophagus and great blood vessels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mediate', 'a.', 'Being between the two extremes; middle; interposed;\n   intervening; intermediate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mediate', 'a.', 'Acting by means, or by an intervening cause or instrument;\n   not direct or immediate; acting or suffering through an intervening\n   agent or condition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mediate', 'a.', 'Gained or effected by a medium or condition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mediated', 'imp. & p. p.', 'of Mediate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mediating', 'p. pr. & vb. n.', 'of Mediate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mediate', 'a.', 'To be in the middle, or between two; to intervene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mediate', 'a.', 'To interpose between parties, as the equal friend of each,\n   esp. for the purpose of effecting a reconciliation or agreement; as, to\n   mediate between nations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mediate', 'v. t.', 'To effect by mediation or interposition; to bring about\n   as a mediator, instrument, or means; as, to mediate a peace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mediate', 'v. t.', 'To divide into two equal parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mediately', 'adv.', 'In a mediate manner; by a secondary cause or agent;\n   not directly or primarily; by means; -- opposed to immediately.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mediateness', 'n.', 'The state of being mediate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mediation', 'a.', 'The act of mediating; action or relation of anything\n   interposed; action as a necessary condition, means, or instrument;\n   interposition; intervention.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mediation', 'a.', 'Hence, specifically, agency between parties at variance,\n   with a view to reconcile them; entreaty for another; intercession.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mediative', 'a.', 'Pertaining to mediation; used in mediation; as,\n   mediative efforts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mediatization', 'n.', 'The act of mediatizing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mediatized', 'imp. & p. p.', 'of Mediatize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mediatizing', 'p. pr. & vb. n.', 'of Mediatize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mediatize', 'v. t.', 'To cause to act through an agent or to hold a\n   subordinate position; to annex; -- specifically applied to the\n   annexation during the former German empire of a smaller German state to\n   a larger, while allowing it a nominal sovereignty, and its prince his\n   rank.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mediator', 'n.', 'One who mediates; especially, one who interposes between\n   parties at variance for the purpose of reconciling them; hence, an\n   intercessor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mediatorial', 'a.', 'Of or pertaining to a mediator, or to mediation;\n   mediatory; as, a mediatorial office.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mediatorship', 'n.', 'The office or character of a mediator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mediatory', 'a.', 'Mediatorial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mediatress', 'n.', 'Alt. of Mediatrix');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mediatrix', 'n.', 'A female mediator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Medic', 'n.', 'A leguminous plant of the genus Medicago. The black medic is\n   the Medicago lupulina; the purple medic, or lucern, is M. sativa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Medic', 'a.', 'Medical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Medicable', 'a.', 'Capable of being medicated; admitting of being cured or\n   healed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Medical', 'a.', 'Of, pertaining to, or having to do with, the art of\n   healing disease, or the science of medicine; as, the medical\n   profession; medical services; a medical dictionary; medical\n   jurisprudence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Medical', 'a.', 'Containing medicine; used in medicine; medicinal; as, the\n   medical properties of a plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Medically', 'adv.', 'In a medical manner; with reference to healing, or to\n   the principles of the healing art.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Medicament', 'n.', 'Anything used for healing diseases or wounds; a\n   medicine; a healing application.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Medicamental', 'a.', 'Of or pertaining to medicaments or healing\n   applications; having the qualities of medicaments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Medicaster', 'n.', 'A quack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Medicated', 'imp. & p. p.', 'of Medicate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Medicating', 'p. pr. & vb. n.', 'of Medicate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Medicate', 'v. t.', 'To tincture or impregnate with anything medicinal; to\n   drug.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Medicate', 'v. t.', 'To treat with medicine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Medication', '', 'The act or process of medicating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Medicative', 'a.', 'Medicinal; acting like a medicine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Medicean', 'a.', 'Of or relating to the Medici, a noted Italian family; as,\n   the Medicean Venus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Medicinable', 'a.', 'Medicinal; having the power of healing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Medicinal', 'a.', 'Having curative or palliative properties; used for the\n   cure or alleviation of bodily disorders; as, medicinal tinctures,\n   plants, or springs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Medicinal', 'a.', 'Of or pertaining to medicine; medical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Medicinally', 'adv.', 'In a medicinal manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Medicine', 'n.', 'The science which relates to the prevention, cure, or\n   alleviation of disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Medicine', 'n.', 'Any substance administered in the treatment of disease; a\n   remedial agent; a remedy; physic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Medicine', 'n.', 'A philter or love potion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Medicine', 'n.', 'A physician.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Medicine', 'v. t.', 'To give medicine to; to affect as a medicine does; to\n   remedy; to cure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Medico-legal', 'a.', 'Of or pertaining to law as affected by medical facts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Medicommissure', 'n.', 'A large transverse commissure in the third\n   ventricle of the brain; the middle or soft commissure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Medicornua', 'pl. ', 'of Medicornu');
  INSERT INTO words (word, word_type, description) VALUES  (    'Medicornu', 'n.', 'The middle or inferior horn of each lateral ventricle of\n   the brain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Medics', 'n.', 'Science of medicine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mediety', 'n.', 'The middle part; half; moiety.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Medieval', '', 'Alt. of Medievalist');
  INSERT INTO words (word, word_type, description) VALUES  (    'Medievalism', '', 'Alt. of Medievalist');
  INSERT INTO words (word, word_type, description) VALUES  (    'Medievalist', '', 'Same as Medi/val, Medi/valism, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Medina epoch', '', 'A subdivision of the Niagara period in the American\n   upper Silurian, characterized by the formations known as the Oneida\n   conglomerate, and the Medina sandstone. See the Chart of Geology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Medino', 'n.', 'Same as Para.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mediocral', 'a.', 'Mediocre.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mediocre', 'a.', 'Of a middle quality; of but a moderate or low degree of\n   excellence; indifferent; ordinary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mediocre', 'n.', 'A mediocre person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mediocre', 'n.', 'A young monk who was excused from performing a portion of\n   a monks duties.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mediocrist', 'n.', 'A mediocre person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mediocrity', 'n.', 'The quality of being mediocre; a middle state or\n   degree; a moderate degree or rate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mediocrity', 'n.', 'Moderation; temperance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mediostapedial', 'a.', 'Pertaining to that part of the columella of the ear\n   which, in some animals, connects the stapes with the other parts of the\n   columella.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mediostapedial', 'n.', 'The mediostapedial part of the columella.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Medioxumous', 'a.', 'Intermediate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meditance', 'n.', 'Meditation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meditated', 'imp. & p. p.', 'of Meditate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meditating', 'p. pr. & vb. n.', 'of Meditate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meditate', 'v. i.', 'To keep the mind in a state of contemplation; to dwell\n   on anything in thought; to think seriously; to muse; to cogitate; to\n   reflect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meditate', 'v. t.', 'To contemplate; to keep the mind fixed upon; to study.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meditate', 'v. t.', 'To purpose; to intend; to design; to plan by revolving\n   in the mind; as, to meditate a war.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meditation', 'n.', 'The act of meditating; close or continued thought; the\n   turning or revolving of a subject in the mind; serious contemplation;\n   reflection; musing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meditation', 'n.', 'Thought; -- without regard to kind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meditatist', 'n.', 'One who is given to meditation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meditative', 'a.', 'Disposed to meditate, or to meditation; as, a\n   meditative man; a meditative mood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mediterranean', 'a.', 'Inclosed, or nearly inclosed, with land; as, the\n   Mediterranean Sea, between Europe and Africa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mediterranean', 'a.', 'Inland; remote from the ocean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mediterranean', 'a.', 'Of or pertaining to the Mediterranean Sea; as,\n   Mediterranean trade; a Mediterranean voyage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mediterraneous', 'a.', 'Inland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Media', 'pl. ', 'of Medium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mediums', 'pl. ', 'of Medium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Medium', 'n.', 'That which lies in the middle, or between other things;\n   intervening body or quantity. Hence, specifically: (a) Middle place or\n   degree; mean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Medium', 'n.', 'See Mean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Medium', 'n.', 'The mean or middle term of a syllogism; that by which the\n   extremes are brought into connection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Medium', 'n.', 'A substance through which an effect is transmitted from one\n   thing to another; as, air is the common medium of sound. Hence: The\n   condition upon which any event or action occurs; necessary means of\n   motion or action; that through or by which anything is accomplished,\n   conveyed, or carried on; specifically, in animal magnetism,\n   spiritualism, etc., a person through whom the action of another being\n   is said to be manifested and transmitted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Medium', 'n.', 'An average.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Medium', 'n.', 'A trade name for printing and writing paper of certain\n   sizes. See Paper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Medium', 'n.', 'The liquid vehicle with which dry colors are ground and\n   prepared for application.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Medium', 'a.', 'Having a middle position or degree; mean; intermediate;\n   medial; as, a horse of medium size; a decoction of medium strength.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Medium-sized', 'a.', 'Having a medium size; as, a medium-sized man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Medii', 'pl. ', 'of Medius');
  INSERT INTO words (word, word_type, description) VALUES  (    'Medius', 'n.', 'The third or middle finger; the third digit, or that which\n   corresponds to it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Medlar', 'n.', 'A tree of the genus Mespilus (M. Germanica); also, the\n   fruit of the tree. The fruit is something like a small apple, but has a\n   bony endocarp. When first gathered the flesh is hard and austere, and\n   it is not eaten until it has begun to decay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Medle', 'v. t.', 'To mix; to mingle; to meddle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Medleys', 'pl. ', 'of Medley');
  INSERT INTO words (word, word_type, description) VALUES  (    'Medley', 'n.', 'A mixture; a mingled and confused mass of ingredients,\n   usually inharmonious; a jumble; a hodgepodge; -- often used\n   contemptuously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Medley', 'n.', 'The confusion of a hand to hand battle; a brisk, hand to\n   hand engagement; a melee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Medley', 'n.', 'A composition of passages detached from several different\n   compositions; a potpourri.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Medley', 'n.', 'A cloth of mixed colors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Medley', 'a.', 'Mixed; of mixed material or color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Medley', 'a.', 'Mingled; confused.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Medly', 'v. t.', 'See Medle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Medoc', 'n.', 'A class of claret wines, including several varieties, from\n   the district of Medoc in the department of Gironde.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Medregal', 'n.', 'See Bonito, 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Medrick', 'n.', 'A species of gull or tern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Medulla', 'n.', 'Marrow; pith; hence, essence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Medulla', 'n.', 'The marrow of bones; the deep or inner portion of an organ\n   or part; as, the medulla, or medullary substance, of the kidney;\n   specifically, the medula oblongata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Medulla', 'n.', 'A soft tissue, occupying the center of the stem or branch\n   of a plant; pith.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Medullar', 'a.', 'See Medullary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Medullary', 'a.', 'Pertaining to, consisting of, or resembling, marrow or\n   medulla.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Medullary', 'a.', 'Pertaining to the medula oblongata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Medullary', 'a.', 'Filled with spongy pith; pithy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Medullated', 'a.', 'Furnished with a medulla or marrow, or with a medullary\n   sheath; as, a medullated nerve fiber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Medullin', 'n.', 'A variety of lignin or cellulose found in the medulla, or\n   pith, of certain plants. Cf. Lignin, and Cellulose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Medusa', 'n.', 'The Gorgon; or one of the Gorgons whose hair was changed\n   into serpents, after which all who looked upon her were turned into\n   stone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Medusae', 'pl. ', 'of Medusa');
  INSERT INTO words (word, word_type, description) VALUES  (    'Medusa', 'n.', 'Any free swimming acaleph; a jellyfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Medusian', 'n.', 'A medusa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Medusiform', 'a.', 'Resembling a medusa in shape or structure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Medusoid', 'a.', 'Like a medusa; having the fundamental structure of a\n   medusa, but without a locomotive disk; -- said of the sessile\n   gonophores of hydroids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Medusoid', 'n.', 'A sessile gonophore. See Illust. under Gonosome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meech', 'v. i.', 'See Mich.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meed', 'n.', 'That which is bestowed or rendered in consideration of merit;\n   reward; recompense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meed', 'n.', 'Merit or desert; worth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meed', 'n.', 'A gift; also, a bride.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meed', 'v. t.', 'To reward; to repay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meed', 'v. t.', 'To deserve; to merit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meedful', 'a.', 'Worthy of meed, reward, or recompense; meritorious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meedfully', 'adv.', 'According to merit; suitably.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meek', 'superl.', 'Mild of temper; not easily provoked or orritated;\n   patient under injuries; not vain, or haughty, or resentful; forbearing;\n   submissive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meek', 'superl.', 'Evincing mildness of temper, or patience; characterized\n   by mildness or patience; as, a meek answer; a meek face.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meek', 'v. t.', 'Alt. of Meeken');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meeken', 'v. t.', 'To make meek; to nurture in gentleness and humility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meekly', 'adv.', 'In a meek manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meekness', 'n.', 'The quality or state of being meek.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meer', 'a.', 'Simple; unmixed. See Mere, a.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meer', 'n.', 'See Mere, a lake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meer', 'n.', 'A boundary. See Mere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meerkat', 'n.', 'A South African carnivore (Cynictis penicillata), allied\n   to the ichneumons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meerschaum', 'n.', 'A fine white claylike mineral, soft, and light enough\n   when in dry masses to float in water. It is a hydrous silicate of\n   magnesia, and is obtained chiefly in Asia Minor. It is manufacturd into\n   tobacco pipes, cigar holders, etc. Also called sepiolite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meerschaum', 'n.', 'A tobacco pipe made of this mineral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Met', 'imp. & p. p.', 'of Meet');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meeting', 'p. pr. & vb. n.', 'of Meet');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meet', 'v. t.', 'To join, or come in contact with; esp., to come in contact\n   with by approach from an opposite direction; to come upon or against,\n   front to front, as distinguished from contact by following and\n   overtaking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meet', 'v. t.', 'To come in collision with; to confront in conflict; to\n   encounter hostilely; as, they met the enemy and defeated them; the ship\n   met opposing winds and currents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meet', 'v. t.', 'To come into the presence of without contact; to come\n   close to; to intercept; to come within the perception, influence, or\n   recognition of; as, to meet a train at a junction; to meet carriages or\n   persons in the street; to meet friends at a party; sweet sounds met the\n   ear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meet', 'v. t.', 'To perceive; to come to a knowledge of; to have personal\n   acquaintance with; to experience; to suffer; as, the eye met a horrid\n   sight; he met his fate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meet', 'v. t.', 'To come up to; to be even with; to equal; to match; to\n   satisfy; to ansver; as, to meet ones expectations; the supply meets\n   the demand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meet', 'v. t.', 'To come together by mutual approach; esp., to come in\n   contact, or into proximity, by approach from opposite directions; to\n   join; to come face to face; to come in close relationship; as, we met\n   in the street; two lines meet so as to form an angle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meet', 'v. t.', 'To come together with hostile purpose; to have an\n   encounter or conflict.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meet', 'v. t.', 'To assemble together; to congregate; as, Congress meets on\n   the first Monday of December.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meet', 'v. t.', 'To come together by mutual concessions; hence, to agree;\n   to harmonize; to unite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meet', 'n.', 'An assembling together; esp., the assembling of huntsmen for\n   the hunt; also, the persons who so assemble, and the place of meeting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meet', 'a.', 'Suitable; fit; proper; appropriate; qualified; convenient.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meet', 'adv.', 'Meetly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meeten', 'v. t.', 'To render fit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meeter', 'n.', 'One who meets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meeth', 'n.', 'Mead. See Meathe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meeting', 'n.', 'A coming together; an assembling; as, the meeting of\n   Congress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meeting', 'n.', 'A junction, crossing, or union; as, the meeting of the\n   roads or of two rivers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meeting', 'n.', 'A congregation; a collection of people; a convention; as,\n   a large meeting; an harmonius meeting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meeting', 'n.', 'An assembly for worship; as, to attend meeting on Sunday;\n   -- in England, applied distinctively and disparagingly to the\n   worshiping assemblies of Dissenters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meetinghouse', 'n.', 'A house used as a place of worship; a church; -- in\n   England, applied only to a house so used by Dissenters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meetly', 'adv.', 'Fitly; suitably; properly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meetness', 'n.', 'Fitness; suitableness; propriety.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meg-', '', 'Alt. of Megalo-');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mega', '', 'Alt. of Megalo-');
  INSERT INTO words (word, word_type, description) VALUES  (    'Megalo-', '', 'Combining forms signifying: (a) Great, extended, powerful;\n   as, megascope, megacosm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Megalo-', '', 'A million times, a million of; as, megameter, a million\n   meters; megafarad, a million farads; megohm, a million ohms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Megacephalic', 'a.', 'Alt. of Megacephalous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Megacephalous', 'a.', 'Large headed; -- applied to animals, and to plants\n   when they have large flower heads.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Megaceros', 'n.', 'The Irish elk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Megachile', 'n.', 'A leaf-cutting bee of the genus Megachilus. See Leaf\n   cutter, under Leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Megacosm', 'n.', 'See Macrocosm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Megacoulomb', 'n.', 'A million coulombs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Megaderm', 'n.', 'Any one of several species of Old World blood-sucking\n   bats of the genus Megaderma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Megadyne', 'n.', 'One of the larger measures of force, amounting to one\n   million dynes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Megafarad', 'n.', 'One of the larger measures of electrical capacity,\n   amounting to one million farads; a macrofarad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Megalerg', 'n.', 'A million ergs; a megerg.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Megalesian', 'a.', 'Pertaining to, or in honor of, Cybele; as, the\n   Megalesian games at Rome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Megalethoscope', 'n.', 'An optical apparatus in which pictures are viewed\n   through a large lens with stereoptical effects. It is often combined\n   with the stereoscope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Megalith', 'n.', 'A large stone; especially, a large stone used in ancient\n   building.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Megalo-', '', 'See Meg-.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Megalocyte', 'n.', 'A large, flattened corpuscle, twice the diameter of the\n   ordinary red corpuscle, found in considerable numbers in the blood in\n   profound anaemia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Megalomania', 'n.', 'A form of mental alienation in which the patient has\n   grandiose delusions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Megalonyx', 'n.', 'An extinct quaternary mammal, of great size, allied to\n   the sloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Megalophonous', 'a.', 'Having a loud voice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Megalopolis', 'n.', 'A chief city; a metropolis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Megalops', 'n.', 'A larva, in a stage following the zoea, in the\n   development of most crabs. In this stage the legs and abdominal\n   appendages have appeared, the abdomen is relatively long, and the eyes\n   are large. Also used adjectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Megalops', 'n.', 'A large fish; the tarpum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Megalopsychy', 'n.', 'Greatness of soul.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Megalosaur', 'n.', 'Alt. of Megalosaurus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Megalosaurus', 'n.', 'A gigantic carnivorous dinosaur, whose fossil remains\n   have been found in England and elsewhere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Megameter', 'n.', 'An instrument for determining longitude by observation\n   of the stars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Megameter', 'n.', 'A micrometer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Megameter', 'n.', 'Alt. of Megametre');
  INSERT INTO words (word, word_type, description) VALUES  (    'Megametre', 'n.', 'In the metric system, one million meters, or one\n   thousand kilometers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Megampere', 'n.', 'A million amperes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Megaphone', 'n.', 'A device to magnify sound, or direct it in a given\n   direction in a greater volume, as a very large funnel used as an ear\n   trumpet or as a speaking trumpet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Megaphyton', 'n.', 'An extinct genus of tree ferns with large, two-ranked\n   leaves, or fronds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Megapode', 'n.', 'Any one of several species of large-footed, gallinaceous\n   birds of the genera Megapodius and Leipoa, inhabiting Australia and\n   other Pacific islands. See Jungle fowl (b) under Jungle, and Leipoa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Megapolis', 'n.', 'A metropolis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Megarian', 'a.', 'Alt. of Megaric');
  INSERT INTO words (word, word_type, description) VALUES  (    'Megaric', 'a.', 'Belonging, or pertaining, to Megara, a city of ancient\n   Greece.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Megascope', 'n.', 'A modification of the magic lantern, used esp. for\n   throwing a magnified image of an opaque object on a screen, solar or\n   artificial light being used.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Megaseme', 'a.', 'Having the orbital index relatively large; having the\n   orbits narrow transversely; -- opposed to microseme.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Megass', 'n.', 'Alt. of Megasse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Megasse', 'n.', 'See Bagasse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Megasthene', 'n.', 'One of a group which includes the higher orders of\n   mammals, having a large size as a typical characteristic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Megasthenic', 'a.', 'Having a typically large size; belonging to the\n   megasthenes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Megastome', 'n.', 'One of a group of univalve shells, having a large\n   aperture or mouth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Megathere', 'n.', 'Alt. of Megatherium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Megatherium', 'n.', 'An extinct gigantic quaternary mammal, allied to the\n   ant-eaters and sloths. Its remains are found in South America.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Megatheroid', 'n.', 'One of a family of extinct edentates found in America.\n   The family includes the megatherium, the megalonyx, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Megavolt', 'n.', 'One of the larger measures of electro-motive force,\n   amounting to one million volts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Megaweber', 'n.', 'A million webers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Megerg', 'n.', 'One of the larger measures of work, amounting to one\n   million ergs; -- called also megalerg.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Megilp', 'n.', 'Alt. of Megilph');
  INSERT INTO words (word, word_type, description) VALUES  (    'Megilph', 'n.', 'A gelatinous compound of linseed oil and mastic varnish,\n   used by artists as a vehicle for colors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Megohm', 'n.', 'One of the larger measures of electrical resistance,\n   amounting to one million ohms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Megrim', 'n.', 'A kind of sick or nevrous headache, usually periodical and\n   confined to one side of the head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Megrim', 'n.', 'A fancy; a whim; a freak; a humor; esp., in the plural,\n   lowness of spirits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Megrim', 'n.', 'A sudden vertigo in a horse, succeeded sometimes by\n   unconsciousness, produced by an excess of blood in the brain; a mild\n   form of apoplexy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Megrim', 'n.', 'The British smooth sole, or scaldfish (Psetta arnoglossa).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meibomian', 'a.', 'Of, pertaining to, or discovered by, Meibomius.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meine', 'v. t.', 'See Menge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meine', 'n.', 'Alt. of Meiny');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meiny', 'n.', 'A family, including servants, etc.; household; retinue;\n   train.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meiny', 'n.', 'Company; band; army.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meiocene', 'a.', 'See Miocene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meionite', 'n.', 'A member of the scapolite, group, occuring in glassy\n   crystals on Monte Somma, near Naples.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meiosis', 'n.', 'Diminution; a species of hyperbole, representing a thing\n   as being less than it really is.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melostemonous', 'a.', 'Having fever stamens than the parts of the corolla.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meistersinger', 'n.', 'See Mastersinger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mekhitarist', 'n.', 'See Mechitarist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melaconite', 'n.', 'An earthy black oxide of copper, arising from the\n   decomposition of other ores.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melada', 'n.', 'Alt. of Melado');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melado', 'n.', 'A mixture of sugar and molasses; crude sugar as it comes\n   from the pans without being drained.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melaena', 'n.', 'A discharge from the bowels of black matter, consisting of\n   altered blood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melain', 'n.', 'The dark coloring matter of the liquid of the cuttlefish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melainotype', 'n.', 'See Melanotype.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melam', 'n.', 'A white or buff-colored granular powder, C6H9N11, obtained\n   by heating ammonium sulphocyanate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melamine', 'n.', 'A strong nitrogenous base, C3H6N6, produced from several\n   cyanogen compounds, and obtained as a white crystalline substance, --\n   formerly supposed to be produced by the decomposition of melam. Called\n   also cyanuramide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melampode', 'n.', 'The black hellebore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melampyrin', 'n.', 'Alt. of Melampyrite');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melampyrite', 'n.', 'The saccharine substance dulcite; -- so called because\n   found in the leaves of cowwheat (Melampyrum). See Dulcite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melanaemia', 'n.', 'A morbid condition in which the blood contains black\n   pigment either floating freely or imbedded in the white blood\n   corpuscles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melanagogue', 'n.', 'A medicine supposed to expel black bile or choler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melancholia', 'n.', 'A kind of mental unsoundness characterized by extreme\n   depression of spirits, ill-grounded fears, delusions, and brooding over\n   one particular subject or train of ideas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melancholian', 'n.', 'A person affected with melancholy; a melancholic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melancholic', 'a.', 'Given to melancholy; depressed; melancholy; dejected;\n   unhappy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melancholic', 'n.', 'One affected with a gloomy state of mind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melancholic', 'n.', 'A gloomy state of mind; melancholy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melancholily', 'adv.', 'In a melancholy manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melancholiness', 'n.', 'The state or quality of being melancholy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melancholious', 'a.', 'Melancholy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melancholist', 'n.', 'One affected with melancholy or dejection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melancholize', 'v. i.', 'To become gloomy or dejected in mind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melancholize', 'v. t.', 'To make melancholy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melancholy', 'n.', 'Depression of spirits; a gloomy state continuing a\n   considerable time; deep dejection; gloominess.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melancholy', 'n.', 'Great and continued depression of spirits, amounting to\n   mental unsoundness; melancholia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melancholy', 'n.', 'Pensive maditation; serious thoughtfulness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melancholy', 'n.', 'Ill nature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melancholy', 'a.', 'Depressed in spirits; dejected; gloomy dismal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melancholy', 'a.', 'Producing great evil and grief; causing dejection;\n   calamitous; afflictive; as, a melancholy event.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melancholy', 'a.', 'Somewhat deranged in mind; having the jugment impaired.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melancholy', 'a.', 'Favorable to meditation; somber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melanesian', 'a.', 'Of or pertaining to Melanesia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melange', 'n.', 'A mixture; a medley.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melanian', 'n.', 'One of a family of fresh-water pectinibranchiate\n   mollusks, having a turret-shaped shell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melanic', 'a.', 'Melanotic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melanic', 'a.', 'Of or pertaining to the black-haired races.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melaniline', 'n.', 'A complex nitrogenous hydrocarbon obtained artificially\n   (as by the action of cyanogen chloride on aniline) as a white,\n   crystalline substance; -- called also diphenyl guanidin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melanin', 'n.', 'A black pigment found in the pigment-bearing cells of the\n   skin (particularly in the skin of the negro), in the epithelial cells\n   of the external layer of the retina (then called fuscin), in the outer\n   layer of the choroid, and elsewhere. It is supposed to be derived from\n   the decomposition of hemoglobin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melanism', 'n.', 'An undue development of dark-colored pigment in the skin\n   or its appendages; -- the opposite of albinism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melanism', 'n.', 'A disease; black jaundice. See Mel/na.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melanistic', 'a.', 'Affected with melanism; of the nature of melanism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melanite', 'n.', 'A black variety of garnet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melanochroi', 'n. pl.', 'A group of the human race, including the dark\n   whites.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melanochroic', 'a.', 'Having a dark complexion; of or pertaining to the\n   Melanochroi.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melanochroite', 'n.', 'A mineral of a red, or brownish or yellowish red\n   color. It is a chromate of lead; -- called also phoenicocroite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melanocomous', 'a.', 'Having very dark or black hair; black-haired.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melanorrhoea', 'n.', 'An East Indian genus of large trees. Melanorrh/a\n   usitatissima is the lignum-vitae of Pegu, and yelds a valuable black\n   varnish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melanoscope', 'n.', 'An instrument containing a combination of colored\n   glasses such that they transmit only red light, so that objects of\n   other colors, as green leaves, appear black when seen through it. It is\n   used for viewing colored flames, to detect the presence of potassium,\n   lithium, etc., by the red light which they emit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melanosis', '', 'The morbid deposition of black matter, often of a\n   malignant character, causing pigmented tumors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melanosperm', 'n.', 'An alga of any kind that produces blackish spores, or\n   seed dust. The melanosperms include the rockweeds and all kinds of\n   kelp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melanotic', 'a.', 'Melanistic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melanotype', 'n.', 'A positive picture produced with sensitized collodion\n   on a smooth surface of black varnish, coating a thin plate of iron;\n   also, the process of making such a picture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melanterite', 'n.', 'A hydrous sulphate of iron of a green color and\n   vitreous luster; iron vitriol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melanure', 'n.', 'A small fish of the Mediterranean; a gilthead. See\n   Gilthead (a).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melanuric', 'a.', 'Pertaining to, or designating, a complex nitrogenous\n   acid obtained by decomposition of melam, or of urea, as a white\n   crystalline powder; -- called also melanurenic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melaphyre', 'n.', 'Any one of several dark-colored augitic, eruptive rocks\n   allied to basalt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melasma', 'n.', 'A dark discoloration of the skin, usually local; as,\n   Addisons melasma, or Addisons disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melasses', 'n.', 'See Molasses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melassic', 'a.', 'Pertaining to, or designating, an acid obtained from\n   molasses or glucose, and probably identical with saccharic acid. See\n   Saccharic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melastoma', 'n.', 'A genus of evergreen tropical shrubs; -- so called from\n   the black berries of some species, which stain the mouth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melastomaceous', 'a.', 'Belonging to the order of which Melastoma is the\n   type.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melchite', 'n.', 'One of a sect, chiefly in Syria and Egypt, which\n   acknowledges the authority of the pope, but adheres to the liturgy and\n   ceremonies of the Eastern Church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meleagrine', 'a.', 'Of or pertaining to the genus Meleagris.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meleagris', 'n.', 'A genus of American gallinaceous birds, including the\n   common and the wild turkeys.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melee', 'n.', 'A fight in which the combatants are mingled in one confused\n   mass; a hand to hand conflict; an affray.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melena', 'n.', 'See Melaena.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melene', 'n.', 'An unsaturated hydrocarbon, C30H60, of the ethylene series,\n   obtained from beeswax as a white, scaly, crystalline wax; -- called\n   also melissene, and melissylene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melenite', 'n.', 'An explosive of great destructive power; -- so called\n   from its color, which resembles honey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meletin', 'n.', 'See Quercitin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melezitose', 'n.', 'A variety of sugar, isomeric with sucrose, extracted\n   from the manna of the larch (Larix).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meliaceous', 'a.', 'Pertaining to a natural order (Meliacae) of plants of\n   which the genus Melia is the type. It includes the mahogany and the\n   Spanish cedar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melibean', 'a.', 'Alt. of Melib/an');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melib/an', 'a.', 'Alternately responsive, as verses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melic', '', 'Of or pertaining to song; lyric; tuneful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melicerous', 'a.', 'Consisting of or containing matter like honey; -- said\n   of certain encysted tumors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melic grass', '', 'A genus of grasses (Melica) of little agricultural\n   importance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melicotoon', 'n.', 'See Melocoton.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melicratory', 'n.', 'A meadlike drink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melilite', 'n.', 'A mineral occurring in small yellow crystals, found in\n   the lavas (melilite basalt) of Vesuvius, and elsewhere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melilot', 'n.', 'Any species of Melilotus, a genus of leguminous herbs\n   having a vanillalike odor; sweet clover; harts clover. The blue\n   melilot (Melilotus caerulea) is used in Switzerland to give color and\n   flavor to sapsago cheese.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melilotic', 'a.', 'Of, pertaining to, or obtained from, sweet clover or\n   melilot; specifically, designating an acid of the aromatic series,\n   obtained from melilot as a white crystalline substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meliorated', 'imp. & p. p.', 'of Meliorate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meliorating', 'p. pr. & vb. n.', 'of Meliorate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meliorate', 'v. t.', 'To make better; to improve; to ameliorate; to soften;\n   to make more tolerable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meliorate', 'v. i.', 'To grow better.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meliorater', 'n.', 'Same as Meliorator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melioration', 'n.', 'The act or operation of meliorating, or the state of\n   being meliorated; improvement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meliorator', 'n.', 'One who meliorates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meliorism', 'n.', 'The doctrine that there is a tendency throughout nature\n   toward improvement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meliority', 'n.', 'The state or quality of being better; melioration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meliphagan', 'a.', 'Belonging to the genus Meliphaga.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meliphagan', 'n.', 'Any bird of the genus Meliphaga and allied genera; a\n   honey eater; -- called also meliphagidan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meliphagous', 'a.', 'Eating, or feeding upon, honey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melismata', 'pl. ', 'of Melisma');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melisma', 'n.', 'A piece of melody; a song or tune, -- as opposed to\n   recitative or musical declamation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melisma', 'n.', 'A grace or embellishment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melissa', 'n.', 'A genus of labiate herbs, including the balm, or bee balm\n   (Melissa officinalis).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melissic', 'a.', 'Pertaining to, or derived from, beeswax; specif.,\n   denoting an acid obtained by oxidation of myricin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melissyl', 'n.', 'See Myricyl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melissylene', 'n.', 'See Melene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melitose', 'n.', 'A variety of sugar isomeric with sucrose, extracted from\n   cotton seeds and from the so-called Australian manna (a secretion of\n   certain species of Eucalyptus).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mell', 'v. i. & t.', 'To mix; to meddle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mell', 'n.', 'Honey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mell', 'n.', 'A mill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mellate', 'n.', 'A mellitate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mellay', 'n.', 'A melee; a conflict.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mellic', 'a.', 'See Mellitic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melliferous', 'a.', 'Producing honey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mellific', 'a.', 'Producing honey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mellification', 'n.', 'The making or production of honey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mellifluence', 'n.', 'A flow of sweetness, or a sweet, smooth flow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mellifluent', 'a.', 'Flowing as with honey; smooth; mellifluous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mellifluently', 'adv.', 'In a mellifluent manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mellifluous', 'a.', 'Flowing as with honey; smooth; flowing sweetly or\n   smoothly; as, a mellifluous voice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melligenous', 'a.', 'Having the qualities of honey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melligo', 'n.', 'Honeydew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melliloquent', 'a.', 'Speaking sweetly or harmoniously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melliphagan', 'n.', 'See Meliphagan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melliphagous', 'a.', 'See Meliphagous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mellitate', 'n.', 'A salt of mellitic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mellite', 'n.', 'A mineral of a honey color, found in brown coal, and\n   partly the result of vegetable decomposition; honeystone. It is a\n   mellitate of alumina.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mellitic', 'a.', 'Containing saccharine matter; marked by saccharine\n   secretions; as, mellitic diabetes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mellitic', 'a.', 'Pertaining to, or derived from, the mineral mellite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mellone', 'n.', 'A yellow powder, C6H3N9, obtained from certain\n   sulphocyanates. It has acid properties and forms compounds called\n   mellonides.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mellonide', 'n.', 'See Mellone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mellow', 'superl.', 'Soft or tender by reason of ripeness; having a tender\n   pulp; as, a mellow apple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mellow', 'superl.', 'Easily worked or penetrated; not hard or rigid; as, a\n   mellow soil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mellow', 'superl.', 'Not coarse, rough, or harsh; subdued; soft; rich;\n   delicate; -- said of sound, color, flavor, style, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mellow', 'superl.', 'Well matured; softened by years; genial; jovial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mellow', 'superl.', 'Warmed by liquor; slightly intoxicated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mellowed', 'imp. & p. p.', 'of Mellow');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mellowing', 'p. pr. & vb. n.', 'of Mellow');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mellow', 'v. t.', 'To make mellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mellow', 'v. i.', 'To become mellow; as, ripe fruit soon mellows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mellowly', 'adv.', 'In a mellow manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mellowness', 'n.', 'Quality or state of being mellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mellowy', 'a.', 'Soft; unctuous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melluco', 'n.', 'A climbing plant (Ullucus officinalis) of the Andes,\n   having tuberous roots which are used as a substitute for potatoes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melne', 'n.', 'A mill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melocoton', 'n.', 'Alt. of Melocotoon');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melocotoon', 'n.', 'A quince.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melocotoon', 'n.', 'A kind of peach having one side deep red, and the flesh\n   yellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melodeon', 'n.', 'A kind of small reed organ; -- a portable form of the\n   seraphine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melodeon', 'n.', 'A music hall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melodic', 'a.', 'Of the nature of melody; relating to, containing, or made\n   up of, melody; melodious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melodics', 'n.', 'The department of musical science which treats of the\n   pitch of tones, and of the laws of melody.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melodiograph', 'n.', 'A contrivance for preserving a record of music, by\n   recording the action of the keys of a musical instrument when played\n   upon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melodious', 'a.', 'Containing, or producing, melody; musical; agreeable to\n   the ear by a sweet succession of sounds; as, a melodious voice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melodist', 'n.', 'A composer or singer of melodies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melodized', 'imp. & p. p.', 'of Melodize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melodizing', 'p. pr. & vb. n.', 'of Melodize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melodize', 'v. t.', 'To make melodious; to form into, or set to, melody.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melodize', 'v. i.', 'To make melody; to compose melodies; to harmonize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melodrama', 'n.', 'Formerly, a kind of drama having a musical accompaniment\n   to intensify the effect of certain scenes. Now, a drama abounding in\n   romantic sentiment and agonizing situations, with a musical\n   accompaniment only in parts which are especially thrilling or pathetic.\n   In opera, a passage in which the orchestra plays a somewhat descriptive\n   accompaniment, while the actor speaks; as, the melodrama in the\n   gravedigging scene of Beethovens \"Fidelio\".');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melodramatic', 'a.', 'Of or pertaining to melodrama; like or suitable to a\n   melodrama; unnatural in situation or action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melodramatist', 'n.', 'One who acts in, or writes, melodramas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melodrame', 'n.', 'Melodrama.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melodies', 'pl. ', 'of Melody');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melody', 'n.', 'A sweet or agreeable succession of sounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melody', 'n.', 'A rhythmical succession of single tones, ranging for the\n   most part within a given key, and so related together as to form a\n   musical whole, having the unity of what is technically called a musical\n   thought, at once pleasing to the ear and characteristic in expression.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melody', 'n.', 'The air or tune of a musical piece.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meloe', '', 'A genus of beetles without wings, but having short oval\n   elytra; the oil beetles. These beetles are sometimes used instead of\n   cantharides for raising blisters. See Oil beetle, under Oil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melograph', 'n.', 'Same as Melodiograph.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melolonthidian', 'n.', 'A beetle of the genus Melolontha, and allied\n   genera. See May beetle, under May.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melon', 'n.', 'The juicy fruit of certain cucurbitaceous plants, as the\n   muskmelon, watermelon, and citron melon; also, the plant that produces\n   the fruit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melon', 'n.', 'A large, ornamental, marine, univalve shell of the genus\n   Melo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melopiano', 'n.', 'A piano having a mechanical attachment which enables the\n   player to prolong the notes at will.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meloplastic', 'a.', 'Of or pertaining to meloplasty, or the artificial\n   formation of a new cheek.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meloplasty', 'n.', 'The process of restoring a cheek which has been\n   destroyed wholly or in part.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melop/ia', 'n.', 'The art of forming melody; melody; -- now often used for\n   a melodic passage, rather than a complete melody.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melotype', 'n.', 'A picture produced by a process in which development\n   after exposure may be deferred indefinitely, so as to permit\n   transportation of exposed plates; also, the process itself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melpomene', 'n.', 'The Muse of tragedy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melpomene', 'n.', 'The eighteenth asteroid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melrose', 'n.', 'Honey of roses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melt', 'n.', 'See 2d Milt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melted', 'imp. & p. p.', 'of Melt');
  INSERT INTO words (word, word_type, description) VALUES  (    'Molten', 'p. p.', 'of Melt');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melting', 'p. pr. & vb. n.', 'of Melt');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melt', 'v.', 'To reduce from a solid to a liquid state, as by heat; to\n   liquefy; as, to melt wax, tallow, or lead; to melt ice or snow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melt', 'v.', 'Hence: To soften, as by a warming or kindly influence; to\n   relax; to render gentle or susceptible to mild influences; sometimes,\n   in a bad sense, to take away the firmness of; to weaken.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melt', 'v. i.', 'To be changed from a solid to a liquid state under the\n   influence of heat; as, butter and wax melt at moderate temperatures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melt', 'v. i.', 'To dissolve; as, sugar melts in the mouth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melt', 'v. i.', 'Hence: To be softened; to become tender, mild, or gentle;\n   also, to be weakened or subdued, as by fear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melt', 'v. i.', 'To lose distinct form or outline; to blend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melt', 'v. i.', 'To disappear by being dispersed or dissipated; as, the fog\n   melts away.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meltable', 'a.', 'Capable of being melted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melter', 'n.', 'One who, or that which, melts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melting', 'n.', 'Liquefaction; the act of causing (something) to melt, or\n   the process of becoming melted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melting', 'a.', 'Causing to melt; becoming melted; -- used literally or\n   figuratively; as, a melting heat; a melting appeal; a melting mood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Melton', 'n.', 'A kind of stout woolen cloth with unfinished face and\n   without raised nap. A commoner variety has a cotton warp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Member', 'v. t.', 'To remember; to cause to remember; to mention.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Member', 'n.', 'A part of an animal capable of performing a distinct\n   office; an organ; a limb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Member', 'n.', 'Hence, a part of a whole; an independent constituent of a\n   body');
  INSERT INTO words (word, word_type, description) VALUES  (    'Member', 'n.', 'A part of a discourse or of a period or sentence; a clause;\n   a part of a verse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Member', 'n.', 'Either of the two parts of an algebraic equation, connected\n   by the sign of equality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Member', 'n.', 'Any essential part, as a post, tie rod, strut, etc., of a\n   framed structure, as a bridge truss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Member', 'n.', 'Any part of a building, whether constructional, as a pier,\n   column, lintel, or the like, or decorative, as a molding, or group of\n   moldings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Member', 'n.', 'One of the persons composing a society, community, or the\n   like; an individual forming part of an association; as, a member of the\n   society of Friends.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Membered', 'a.', 'Having limbs; -- chiefly used in composition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Membered', 'a.', 'Having legs of a different tincture from that of the\n   body; -- said of a bird in heraldic representations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Membership', 'n.', 'The state of being a member.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Membership', 'n.', 'The collective body of members, as of a society.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Membral', 'a.', 'Relating to a member.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Membranaceous', 'a.', 'Same as Membranous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Membranaceous', 'a.', 'Thin and rather soft or pliable, as the leaves of\n   the rose, peach tree, and aspen poplar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Membrane', 'n.', 'A thin layer or fold of tissue, usually supported by a\n   fibrous network, serving to cover or line some part or organ, and often\n   secreting or absorbing certain fluids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Membraneous', 'a.', 'See Membranous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Membraniferous', 'a.', 'Having or producing membranes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Membraniform', 'a.', 'Having the form of a membrane or of parchment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Membranology', 'n.', 'The science which treats of membranes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Membranous', 'a.', 'Pertaining to, consisting of, or resembling, membrane;\n   as, a membranous covering or lining.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Membranous', 'a.', 'Membranaceous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mementos', 'pl. ', 'of Memento');
  INSERT INTO words (word, word_type, description) VALUES  (    'Memento', 'n.', 'A hint, suggestion, token, or memorial, to awaken memory;\n   that which reminds or recalls to memory; a souvenir.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meminna', 'n.', 'A small deerlet, or chevrotain, of India.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Memnon', 'n.', 'A celebrated Egyptian statue near Thebes, said to have the\n   property of emitting a harplike sound at sunrise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Memoir', 'n.', 'Alt. of Memoirs');
  INSERT INTO words (word, word_type, description) VALUES  (    'Memoirs', 'n.', 'A memorial account; a history composed from personal\n   experience and memory; an account of transactions or events (usually\n   written in familiar style) as they are remembered by the writer. See\n   History, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Memoirs', 'n.', 'A memorial of any individual; a biography; often, a\n   biography written without special regard to method and completeness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Memoirs', 'n.', 'An account of something deemed noteworthy; an essay; a\n   record of investigations of any subject; the journals and proceedings\n   of a society.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Memoirist', 'n.', 'A writer of memoirs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Memorabilia', 'n. pl.', 'Things remarkable and worthy of remembrance or\n   record; also, the record of them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Memorability', 'n.', 'The quality or state of being memorable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Memorable', 'a.', 'Worthy to be remembered; very important or remarkable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Memorandums', 'pl. ', 'of Memorandum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Memoranda', 'pl. ', 'of Memorandum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Memorandum', 'n.', 'A record of something which it is desired to remember;\n   a note to help the memory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Memorandum', 'n.', 'A brief or informal note in writing of some\n   transaction, or an outline of an intended instrument; an instrument\n   drawn up in a brief and compendious form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Memorate', 'v. t.', 'To commemorate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Memorative', 'a.', 'Commemorative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Memoria', 'n.', 'Memory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Memorial', 'a.', 'Serving to preserve remembrance; commemorative; as, a\n   memorial building.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Memorial', 'a.', 'Mnemonic; assisting the memory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Memorial', 'n.', 'Anything intended to preserve the memory of a person or\n   event; something which serves to keep something else in remembrance; a\n   monument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Memorial', 'n.', 'A memorandum; a record.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Memorial', 'n.', 'A written representation of facts, addressed to the\n   government, or to some branch of it, or to a society, etc., -- often\n   accompanied with a petition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Memorial', 'n.', 'Memory; remembrance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Memorial', 'n.', 'A species of informal state paper, much used in\n   negotiation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Memorialist', 'n.', 'One who writes or signs a memorial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Memorialized', 'imp. & p. p.', 'of Memorialize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Memorializing', 'p. pr. & vb. n.', 'of Memorialize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Memorialize', 'v. t.', 'To address or petition by a memorial; to present a\n   memorial to; as, to memorialize the legislature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Memorializer', 'n.', 'One who petitions by a memorial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Memorist', 'n.', 'One who, or that which, causes to be remembered.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Memoriter', 'adv.', 'By, or from, memory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Memorized', 'imp. & p. p.', 'of Memorize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Memorizing', 'p. pr. & vb. n.', 'of Memorize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Memorize', 'v. t.', 'To cause to be remembered ; hence, to record.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Memorize', 'v. t.', 'To commit to memory; to learn by heart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Memories', 'pl. ', 'of Memory');
  INSERT INTO words (word, word_type, description) VALUES  (    'Memory', 'n.', 'The faculty of the mind by which it retains the knowledge\n   of previous thoughts, impressions, or events.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Memory', 'n.', 'The reach and positiveness with which a person can\n   remember; the strength and trustworthiness of ones power to reach and\n   represent or to recall the past; as, his memory was never wrong.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Memory', 'n.', 'The actual and distinct retention and recognition of past\n   ideas in the mind; remembrance; as, in memory of youth; memories of\n   foreign lands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Memory', 'n.', 'The time within which past events can be or are remembered;\n   as, within the memory of man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Memory', 'n.', 'Something, or an aggregate of things, remembered; hence,\n   character, conduct, etc., as preserved in remembrance, history, or\n   tradition; posthumous fame; as, the war became only a memory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Memory', 'n.', 'A memorial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Memphian', 'a.', 'Of or pertaining to the ancient city of Memphis in Egypt;\n   hence, Egyptian; as, Memphian darkness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Men', 'n.', 'pl. of Man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Men', 'pron.', 'A man; one; -- used with a verb in the singular, and\n   corresponding to the present indefinite one or they.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Menaccanite', 'n.', 'An iron-black or steel-gray mineral, consisting\n   chiefly of the oxides of iron and titanium. It is commonly massive, but\n   occurs also in rhombohedral crystals. Called also titanic iron ore, and\n   ilmenite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Menace', 'n.', 'The show of an intention to inflict evil; a threat or\n   threatening; indication of a probable evil or catastrophe to come.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Menaced', 'imp. & p. p.', 'of Menace');
  INSERT INTO words (word, word_type, description) VALUES  (    'Menacing', 'p. pr. & vb. n.', 'of Menace');
  INSERT INTO words (word, word_type, description) VALUES  (    'Menace', 'n.', 'To express or show an intention to inflict, or to hold out\n   a prospect of inflicting, evil or injury upon; to threaten; -- usually\n   followed by with before the harm threatened; as, to menace a country\n   with war.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Menace', 'n.', 'To threaten, as an evil to be inflicted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Menace', 'v. i.', 'To act in threatening manner; to wear a threatening\n   aspect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Menacer', 'n.', 'One who menaces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Menacingly', 'adv.', 'In a threatening manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Menage', 'n.', 'See Manage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Menage', 'n.', 'A collection of animals; a menagerie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Menagerie', 'n.', 'A piace where animals are kept and trained.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Menagerie', 'n.', 'A collection of wild or exotic animals, kept for\n   exhibition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Menagogue', 'n.', 'Emmenagogue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Menaia', 'pl. ', 'of Menaion');
  INSERT INTO words (word, word_type, description) VALUES  (    'Menaion', 'n.', 'A work of twelve volumes, each containing the offices in\n   the Greek Church for a month; also, each volume of the same.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Menald', 'a.', 'Alt. of Menild');
  INSERT INTO words (word, word_type, description) VALUES  (    'Menild', 'a.', 'Covered with spots; speckled; variegated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mended', 'imp. & p. p.', 'of Mend');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mending', 'p. pr. & vb. n.', 'of Mend');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mend', 'v. t.', 'To repair, as anything that is torn, broken, defaced,\n   decayed, or the like; to restore from partial decay, injury, or\n   defacement; to patch up; to put in shape or order again; to re-create;\n   as, to mend a garment or a machine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mend', 'v. t.', 'To alter for the better; to set right; to reform; hence,\n   to quicken; as, to mend ones manners or pace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mend', 'v. t.', 'To help, to advance, to further; to add to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mend', 'v. i.', 'To grow better; to advance to a better state; to become\n   improved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mendable', 'a.', 'Capable of being mended.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mendacious', 'a.', 'Given to deception or falsehood; lying; as, a\n   mendacious person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mendacious', 'a.', 'False; counterfeit; containing falsehood; as, a\n   mendacious statement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mendacities', 'pl. ', 'of Mendacity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mendacity', 'n.', 'The quality or state of being mendacious; a habit of\n   lying.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mendacity', 'n.', 'A falsehood; a lie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mender', 'n.', 'One who mends or repairs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mendiant', 'n.', 'See Mendinant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mendicancy', 'n.', 'The condition of being mendicant; beggary; begging.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mendicant', 'a.', 'Practicing beggary; begging; living on alms; as,\n   mendicant friars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mendicant', 'n.', 'A beggar; esp., one who makes a business of begging;\n   specifically, a begging friar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mendicate', 'v. t.& i.', 'To beg.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mendication', 'n.', 'The act or practice of begging; beggary; mendicancy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mendicity', 'n.', 'The practice of begging; the life of a beggar;\n   mendicancy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mendinant', 'n.', 'A mendicant or begging friar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mendment', 'n.', 'Amendment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mendole', 'n.', 'The cackerel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mendregal', 'n.', 'Medregal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mends', 'n.', 'See Amends.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mente', 'imp.', 'of Menge');
