  INSERT INTO words (word, word_type, description) VALUES  (    'Meint', '', 'of Menge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Menge', 'v. i.', 'To mix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Menhaden', 'n.', 'An American marine fish of the Herring familt (Brevoortia\n   tyrannus), chiefly valuable for its oil and as a component of\n   fertilizers; -- called also mossbunker, bony fish, chebog, pogy,\n   hardhead, whitefish, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Menhir', 'n.', 'A large stone set upright in olden times as a memorial or\n   monument. Many, of unknown date, are found in Brittany and throughout\n   Northern Europe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Menial', 'n.', 'Belonging to a retinue or train of servants; performing\n   servile office; serving.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Menial', 'n.', 'Pertaining to servants, esp. domestic servants; servile;\n   low; mean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Menial', 'n.', 'A domestic servant or retainer, esp. one of humble rank;\n   one employed in low or servile offices.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Menial', 'n.', 'A person of a servile character or disposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Menieres disease', '', 'A disease characterized by deafness and vertigo,\n   resulting in incoordination of movement. It is supposed to depend upon\n   a morbid condition of the semicircular canals of the internal ear.\n   Named after Meniere, a French physician.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Menilite', 'n.', 'See Opal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meningeal', 'a.', 'Of or pertaining to the meninges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meninges', 'n. pl.', 'The three membranes that envelop the brain and spinal\n   cord; the pia mater, dura mater, and arachnoid membrane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meningitis', 'n.', 'Inflammation of the membranes of the brain or spinal\n   cord.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meniscal', 'a.', 'Pertaining to, or having the form of, a meniscus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meniscoid', 'a.', 'Concavo-convex, like a meniscus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Menisci', 'pl. ', 'of Meniscus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meniscuses', 'pl. ', 'of Meniscus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meniscus', 'n.', 'A crescent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meniscus', 'n.', 'A lens convex on one side and concave on the other.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meniscus', 'n.', 'An interarticular synovial cartilage or membrane; esp.,\n   one of the intervertebral synovial disks in some parts of the vertebral\n   column of birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Menispermaceous', 'a.', 'Pertaining to a natural order (Menispermace/) of\n   climbing plants of which moonseed (Menispermum) is the type.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Menispermic', 'a.', 'Pertaining to, or obtained from, moonseed\n   (Menispermum), or other plants of the same family, as the Anamirta\n   Cocculus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Menispermine', 'n.', 'An alkaloid distinct from picrotoxin and obtained\n   from the cocculus indicus (the fruit of Anamirta Cocculus, formerly\n   Menispermum Cocculus) as a white, crystalline, tasteless powder; --\n   called also menispermina.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meniver', 'a.', 'Same as Miniver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mennonist', 'n.', 'Alt. of Mennonite');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mennonite', 'n.', 'One of a small denomination of Christians, so called\n   from Menno Simons of Friesland, their founder. They believe that the\n   New Testament is the only rule of faith, that there is no original sin,\n   that infants should not be baptized, and that Christians ought not to\n   take oath, hold office, or render military service.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Menobranch', 'n.', 'Alt. of Menobranchus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Menobranchus', 'n.', 'A large aquatic American salamander of the genus\n   Necturus, having permanent external gills.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Menologia', 'pl. ', 'of Menology');
  INSERT INTO words (word, word_type, description) VALUES  (    'Menologies', 'pl. ', 'of Menology');
  INSERT INTO words (word, word_type, description) VALUES  (    'Menologium', 'n.', 'Alt. of Menology');
  INSERT INTO words (word, word_type, description) VALUES  (    'Menology', 'n.', 'A register of months.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Menology', 'n.', 'A brief calendar of the lives of the saints for each day\n   in the year, or a simple remembrance of those whose lives are not\n   written.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Menopause', 'n.', 'The period of natural cessation of menstruation. See\n   Change of life, under Change.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Menopoma', 'n.', 'Alt. of Menopome');
  INSERT INTO words (word, word_type, description) VALUES  (    'Menopome', 'n.', 'The hellbender.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Menorrhagia', 'n.', 'Profuse menstruation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Menorrhagia', 'n.', 'Any profuse bleeding from the uterus; Metrorrhagia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Menostasis', 'n.', 'Stoppage of the mences.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Menostation', 'n.', 'Same as Menostasis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Menow', 'n.', 'A minnow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Men-pleaser', 'n.', 'One whose motive is to please men or the world, rather\n   than God.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mensal', 'a.', 'Belonging to the table; transacted at table; as, mensal\n   conversation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mensal', 'a.', 'Occurring once in a month; monthly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mense', 'n.', 'Manliness; dignity; comeliness; civility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mense', 'v. t.', 'To grace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Menses', 'n. pl.', 'The catamenial or menstrual discharge, a periodic flow\n   of blood or bloody fluid from the uterus or female generative organs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Menstrual', 'a.', 'Recurring once a month; monthly; gone through in a\n   month; as, the menstrual revolution of the moon; pertaining to monthly\n   changes; as, the menstrual equation of the suns place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Menstrual', 'a.', 'Of or pertaining to the menses; as, menstrual\n   discharges; the menstrual period.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Menstrual', 'a.', 'Of or pertaining to a menstruum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Menstruant', 'a.', 'Subject to monthly flowing or menses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Menstruate', 'a.', 'Menstruous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Menstruated', 'imp. & p. p.', 'of Menstruate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Menstruating', 'p. pr. & vb. n.', 'of Menstruate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Menstruate', 'v. i.', 'To discharge the menses; to have the catamenial\n   flow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Menstruation', 'n.', 'The discharge of the menses; also, the state or the\n   period of menstruating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Menstrue', 'n.', 'The menstrual flux; menses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Menstruous', 'a.', 'Having the monthly flow or discharge; menstruating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Menstruous', 'a.', 'Of or pertaining tj the monthly flow; catamenial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Menstruums', 'pl. ', 'of Menstruum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Menstrua', 'pl. ', 'of Menstruum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Menstruum', 'n.', 'Any substance which dissolves a solid body; a solvent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mensurability', 'n.', 'The quality of being mensurable.');
