  INSERT INTO words (word, word_type, description) VALUES  (    'Mensurableness', 'n.', 'The quality or state of being mensurable;\n   measurableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mensural', 'a.', 'Of or pertaining to measure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mensurate', 'v.', 'To measure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mensuration', 'n.', 'The act, process, or art, of measuring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mensuration', 'n.', 'That branch of applied geometry which gives rules for\n   finding the length of lines, the areas of surfaces, or the volumes of\n   solids, from certain simple data of lines and angles.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-ment', '', 'A suffix denoting that which does a thing; an act or process;\n   the result of an act or process; state or condition; as, aliment, that\n   which nourishes, ornament, increment; fragment, piece broken, segment;\n   abridgment, act of abridging, imprisonment, movement, adjournment;\n   amazement, state of being amazed, astonishment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ment', '', 'p. p. of Menge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mentagra', 'n.', 'Sycosis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mental', 'a.', 'Of or pertaining to the chin; genian; as, the mental nerve;\n   the mental region.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mental', 'n.', 'A plate or scale covering the mentum or chin of a fish or\n   reptile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mental', 'a.', 'Of or pertaining to the mind; intellectual; as, mental\n   faculties; mental operations, conditions, or exercise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mentality', 'n.', 'Quality or state of mind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mentally', 'adv.', 'In the mind; in thought or meditation; intellectually;\n   in idea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mentha', 'n.', 'A widely distributed genus of fragrant herbs, including the\n   peppermint, spearmint, etc. The plants have small flowers, usually\n   arranged in dense axillary clusters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Menthene', 'n.', 'A colorless liquid hydrocarbon resembling oil of\n   turpentine, obtained by dehydrating menthol. It has an agreeable odor\n   and a cooling taste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Menthol', 'n.', 'A white, crystalline, aromatic substance resembling\n   camphor, extracted from oil of peppermint (Mentha); -- called also mint\n   camphor or peppermint camphor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Menthyl', 'n.', 'A compound radical forming the base of menthol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Menticultural', 'a.', 'Of or pertaining to mental culture; serving to\n   improve or strengthen the mind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mention', 'n.', 'A speaking or notice of anything, -- usually in a brief or\n   cursory manner. Used especially in the phrase to make mention of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mentioned', 'imp. & p. p.', 'of Mention');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mentioning', 'p. pr. & vb. n.', 'of Mention');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mention', 'v. t.', 'To make mention of; to speak briefly of; to name.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mentionable', 'a.', 'Fit to be mentioned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mentomeckelian', 'a.', 'Of or pertaining to the chin and lower jaw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mentomeckelian', 'n.', 'The bone or cartilage forming the anterior\n   extremity of the lower jaw in some adult animals and the young of\n   others.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mentor', 'n.', 'A wise and faithful counselor or monitor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mentorial', 'a.', 'Containing advice or admonition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mentum', 'n.', 'The front median plate of the labium in insects. See\n   Labium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Menu', 'n.', 'The details of a banquet; a bill of fare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Menuse', 'v. i.', 'See Amenuse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meow', 'v. i. & n.', 'See 6th and 7th Mew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mephistophelian', 'a.', 'Pertaining to, or resembling, the devil\n   Mephistopheles, \"a crafty, scoffing, relentless fiend;\" devilish;\n   crafty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mephitic', 'a.', 'Alt. of Mephitical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mephitical', 'a.', 'Tending to destroy life; poisonous; noxious; as,\n   mephitic exhalations; mephitic regions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mephitical', 'a.', 'Offensive to the smell; as, mephitic odors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mephitis', 'n.', 'Noxious, pestilential, or foul exhalations from\n   decomposing substances, filth, or other source.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mephitis', 'n.', 'A genus of mammals, including the skunks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mephitism', 'n.', 'Same as Mephitis, 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meracious', 'a.', 'Being without mixture or adulteration; hence, strong;\n   racy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mercable', 'a.', 'Capable of being bought or sold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mercantile', 'a.', 'Of or pertaining to merchants, or the business of\n   merchants; having to do with trade, or the buying and selling of\n   commodities; commercial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mercaptal', 'n.', 'Any one of a series of compounds of mercaptans with\n   aldehydes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mercaptan', 'n.', 'Any one of series of compounds, hydrosulphides of\n   alcohol radicals, in composition resembling the alcohols, but\n   containing sulphur in place of oxygen, and hence called also the\n   sulphur alcohols. In general, they are colorless liquids having a\n   strong, repulsive, garlic odor. The name is specifically applied to\n   ethyl mercaptan, C2H5SH. So called from its avidity for mercury, and\n   other metals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mercaptide', 'n.', 'A compound of mercaptan formed by replacing its sulphur\n   hydrogen by a metal; as, potassium mercaptide, C2H5SK.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mercat', 'n.', 'Market; trade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mercatante', 'n.', 'A foreign trader.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mercators chart', '', 'See under Chart, and see Mercators projection,\n   under Projection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mercature', 'n.', 'Commerce; traffic; trade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Merce', 'v. t.', 'To subject to fine or amercement; to mulct; to amerce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mercenaria', 'n.', 'The quahog.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mercenarian', 'n.', 'A mercenary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mercenarily', 'adv.', 'In a mercenary manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mercenariness', 'n.', 'The quality or state of being mercenary; venality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mercenary', 'a.', 'Acting for reward; serving for pay; paid; hired;\n   hireling; venal; as, mercenary soldiers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mercenary', 'a.', 'Hence: Moved by considerations of pay or profit; greedy\n   of gain; sordid; selfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mercenaries', 'pl. ', 'of Mercenary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mercenary', 'n.', 'One who is hired; a hireling; especially, a soldier\n   hired into foreign service.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mercer', 'n.', 'Originally, a dealer in any kind of goods or wares; now\n   restricted to a dealer in textile fabrics, as silks or woolens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mercership', 'n.', 'The business of a mercer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mercery', 'n.', 'The trade of mercers; the goods in which a mercer deals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Merchand', 'v. i.', 'To traffic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Merchandisable', 'a.', 'Such as can be used or transferred as merchandise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Merchandise', 'n.', 'The objects of commerce; whatever is usually bought or\n   sold in trade, or market, or by merchants; wares; goods; commodities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Merchandise', 'n.', 'The act or business of trading; trade; traffic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Merchandised', 'imp. & p. p.', 'of Merchandise');
  INSERT INTO words (word, word_type, description) VALUES  (    'Merchandising', 'p. pr. & vb. n.', 'of Merchandise');
  INSERT INTO words (word, word_type, description) VALUES  (    'Merchandise', 'v. i.', 'To trade; to carry on commerce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Merchandise', 'v. t.', 'To make merchandise of; to buy and sell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Merchandiser', 'n.', 'A trader.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Merchandry', 'n.', 'Trade; commerce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Merchant', 'n.', 'One who traffics on a large scale, especially with\n   foreign countries; a trafficker; a trader.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Merchant', 'n.', 'A trading vessel; a merchantman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Merchant', 'n.', 'One who keeps a store or shop for the sale of goods; a\n   shopkeeper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Merchant', 'a.', 'Of, pertaining to, or employed in, trade or merchandise;\n   as, the merchant service.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Merchant', 'v. i.', 'To be a merchant; to trade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Merchantable', 'a.', 'Fit for market; such as is usually sold in market, or\n   such as will bring the ordinary price; as, merchantable wheat;\n   sometimes, a technical designation for a particular kind or class.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Merchantly', 'a.', 'Merchantlike; suitable to the character or business of\n   a merchant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Merchantmen', 'pl. ', 'of Merchantman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Merchantman', 'n.', 'A merchant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Merchantman', 'n.', 'A trading vessel; a ship employed in the\n   transportation of goods, as, distinguished from a man-of-war.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Merchantry', 'n.', 'The body of merchants taken collectively; as, the\n   merchantry of a country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Merchantry', 'n.', 'The business of a merchant; merchandise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Merciable', 'a.', 'Merciful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Merciful', 'a.', 'Full of mercy; having or exercising mercy; disposed to\n   pity and spare offenders; unwilling to punish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Merciful', 'a.', 'Unwilling to give pain; compassionate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mercify', 'v. t.', 'To pity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Merciless', 'a.', 'Destitute of mercy; cruel; unsparing; -- said of animate\n   beings, and also, figuratively, of things; as, a merciless tyrant;\n   merciless waves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mercurammonium', 'n.', 'A radical regarded as derived from ammonium by the\n   substitution of mercury for a portion of the hydrogen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mercurial', 'a.', 'Having the qualities fabled to belong to the god\n   Mercury; swift; active; sprightly; fickle; volatile; changeable; as, a\n   mercurial youth; a mercurial temperament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mercurial', 'a.', 'Having the form or image of Mercury; -- applied to\n   ancient guideposts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mercurial', 'a.', 'Of or pertaining to Mercury as the god of trade; hence,\n   money-making; crafty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mercurial', 'a.', 'Of or pertaining to, or containing, mercury; as,\n   mercurial preparations, barometer. See Mercury, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mercurial', 'a.', 'Caused by the use of mercury; as, mercurial sore mouth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mercurial', 'n.', 'A person having mercurial qualities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mercurial', 'n.', 'A preparation containing mercury.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mercurialist', 'n.', 'One under the influence of Mercury; one resembling\n   Mercury in character.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mercurialist', 'n.', 'A physician who uses much mercury, in any of its\n   forms, in his practice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mercurialized', 'imp. & p. p.', 'of Mercurialize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mercurializing', 'p. pr. & vb. n.', 'of Mercurialize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mercurialize', 'v. t.', 'To affect with mercury.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mercurialize', 'v. t.', 'To treat with mercury; to expose to the vapor of\n   mercury.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mercurialize', 'v. i.', 'To be sprightly, fantastic, or capricious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mercurially', 'adv.', 'In a mercurial manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mercuric', 'a.', 'Of, pertaining to, or derived from, mercury; containing\n   mercury; -- said of those compounds of mercury into which this element\n   enters in its lowest proportion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mercurification', 'n.', 'The process or operation of obtaining the mercury,\n   in its fluid form, from mercuric minerals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mercurification', 'n.', 'The act or process of compounding, or the state of\n   being compounded, with mercury.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mercurified', 'imp. & p. p.', 'of Mercurify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mercurifying', 'p. pr. & vb. n.', 'of Mercurify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mercurify', 'v. t.', 'To obtain mercury from, as mercuric minerals, which\n   may be done by any application of intense heat that expels the mercury\n   in fumes, which are afterward condensed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mercurify', 'v. t.', 'To combine or mingle mercury with; to impregnate with\n   mercury; to mercurialize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mercurism', 'n.', 'A communication of news; an announcement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mercurous', 'a.', 'Of, pertaining to, or derived from, mercury; containing\n   mercury; -- said of those compounds of mercury in which it is present\n   in its highest proportion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mercury', 'n.', 'A Latin god of commerce and gain; -- treated by the poets\n   as identical with the Greek Hermes, messenger of the gods, conductor of\n   souls to the lower world, and god of eloquence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mercury', 'n.', 'A metallic element mostly obtained by reduction from\n   cinnabar, one of its ores. It is a heavy, opaque, glistening liquid\n   (commonly called quicksilver), and is used in barometers, thermometers,\n   ect. Specific gravity 13.6. Symbol Hg (Hydrargyrum). Atomic weight\n   199.8. Mercury has a molecule which consists of only one atom. It was\n   named by the alchemists after the god Mercury, and designated by his\n   symbol, /.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mercury', 'n.', 'One of the planets of the solar system, being the one\n   nearest the sun, from which its mean distance is about 36,000,000\n   miles. Its period is 88 days, and its diameter 3,000 miles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mercury', 'n.', 'A carrier of tidings; a newsboy; a messenger; hence, also,\n   a newspaper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mercury', 'n.', 'Sprightly or mercurial quality; spirit; mutability;\n   fickleness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mercury', 'n.', 'A plant (Mercurialis annua), of the Spurge family, the\n   leaves of which are sometimes used for spinach, in Europe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mercury', 'v. t.', 'To wash with a preparation of mercury.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mercies', 'pl. ', 'of Mercy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mercy', 'n.', 'Forbearance to inflict harm under circumstances of\n   provocation, when one has the power to inflict it; compassionate\n   treatment of an offender or adversary; clemency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mercy', 'n.', 'Compassionate treatment of the unfortunate and helpless;\n   sometimes, favor, beneficence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mercy', 'n.', 'Disposition to exercise compassion or favor; pity;\n   compassion; willingness to spare or to help.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mercy', 'n.', 'A blessing regarded as a manifestation of compassion or\n   favor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Merd', 'n.', 'Ordure; dung.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-mere', '', 'A combining form meaning part, portion; as, blastomere,\n   epimere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mere', 'n.', 'A pool or lake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mere', 'n.', 'A boundary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mere', 'v. t.', 'To divide, limit, or bound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mere', 'n.', 'A mare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mere', 'Superl.', 'Unmixed; pure; entire; absolute; unqualified.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mere', 'Superl.', 'Only this, and nothing else; such, and no more; simple;\n   bare; as, a mere boy; a mere form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Merely', 'adv.', 'Purely; unmixedly; absolutely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Merely', 'adv.', 'Not otherwise than; simply; barely; only.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Merenchyma', 'n.', 'Tissue composed of spheroidal cells.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meresman', 'n.', 'An officer who ascertains meres or boundaries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Merestead', 'n.', 'The land within the boundaries of a farm; a farmstead or\n   farm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Merestone', 'n.', 'A stone designating a limit or boundary; a landmark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meretricious', 'a.', 'Of or pertaining to prostitutes; having to do with\n   harlots; lustful; as, meretricious traffic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meretricious', 'a.', 'Resembling the arts of a harlot; alluring by false\n   show; gaudily and deceitfully ornamental; tawdry; as, meretricious\n   dress or ornaments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Merganser', 'n.', 'Any bird of the genus Merganser, and allied genera. They\n   are allied to the ducks, but have a sharply serrated bill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Merged', 'imp. & p. p.', 'of Merge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Merging', 'p. pr. & vb. n.', 'of Merge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Merge', 'v. t.', 'To cause to be swallowed up; to immerse; to sink; to\n   absorb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Merge', 'v. i.', 'To be sunk, swallowed up, or lost.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Merger', 'n.', 'One who, or that which, merges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Merger', 'n.', 'An absorption of one estate, or one contract, in another,\n   or of a minor offense in a greater.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mericarp', 'n.', 'One carpel of an umbelliferous fruit. See Cremocarp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meride', 'n.', 'A permanent colony of cells or plastids which may remain\n   isolated, like Rotifer, or may multiply by gemmation to form higher\n   aggregates, termed zoides.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meridian', 'a.', 'Being at, or pertaining to, midday; belonging to, or\n   passing through, the highest point attained by the sun in his diurnal\n   course.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meridian', 'a.', 'Pertaining to the highest point or culmination; as,\n   meridian splendor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meridian', 'a.', 'Midday; noon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meridian', 'a.', 'Hence: The highest point, as of success, prosperity, or\n   the like; culmination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meridian', 'a.', 'A great circle of the sphere passing through the poles of\n   the heavens and the zenith of a given place. It is crossed by the sun\n   at midday.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meridian', 'a.', 'A great circle on the surface of the earth, passing\n   through the poles and any given place; also, the half of such a circle\n   included between the poles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meridional', 'a.', 'Of or pertaining to the meridian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meridional', 'a.', 'Having a southern aspect; southern; southerly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meridionality', 'n.', 'The state of being in the meridian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meridionality', 'n.', 'Position in the south; aspect toward the south.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meridionally', 'adv.', 'In the direction of the meridian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Merils', 'n.', 'A boys play, called also fivepenny morris. See Morris.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meringue', 'n.', 'A delicate pastry made of powdered sugar and the whites\n   of eggs whipped up, -- with jam or cream added.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Merino', 'a.', 'Of or pertaining to a variety of sheep with very fine wool,\n   originally bred in Spain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Merino', 'a.', 'Made of the wool of the merino sheep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Merinos', 'pl. ', 'of Merino');
  INSERT INTO words (word, word_type, description) VALUES  (    'Merino', 'n.', 'A breed of sheep originally from Spain, noted for the\n   fineness of its wool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Merino', 'n.', 'A fine fabric of merino wool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Merismatic', 'a.', 'Dividing into cells or segments; characterized by\n   separation into two or more parts or sections by the formation of\n   internal partitions; as, merismatic growth, where one cell divides into\n   many.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meristem', 'n.', 'A tissue of growing cells, or cells capable of further\n   division.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Merit', 'n.', 'The quality or state of deserving well or ill; desert.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Merit', 'n.', 'Esp. in a good sense: The quality or state of deserving\n   well; worth; excellence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Merit', 'n.', 'Reward deserved; any mark or token of excellence or\n   approbation; as, his teacher gave him ten merits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Merited', 'imp. & p. p.', 'of Merit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meriting', 'p. pr. & vb. n.', 'of Merit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Merit', 'n.', 'To earn by service or performance; to have a right to claim\n   as reward; to deserve; sometimes, to deserve in a bad sense; as, to\n   merit punishment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Merit', 'n.', 'To reward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Merit', 'v. i.', 'To acquire desert; to gain value; to receive benefit; to\n   profit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meritable', 'a.', 'Deserving of reward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meritedly', 'adv.', 'By merit; deservedly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Merithal', 'n.', 'Alt. of Merithallus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Merithallus', 'n.', 'Same as Internode.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meritmonger', 'n.', 'One who depends on merit for salvation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meritorious', 'a.', 'Possessing merit; deserving of reward or honor; worthy\n   of recompense; valuable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meritory', 'a.', 'Meritorious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meritot', 'n.', 'A play of children, in swinging on ropes, or the like,\n   till they are dizzy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Merk', 'n.', 'An old Scotch silver coin; a mark or marc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Merk', 'n.', 'A mark; a sign.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Merke', 'a.', 'Murky.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Merkin', 'n.', 'Originally, a wig; afterwards, a mop for cleaning cannon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Merl', 'n.', 'Alt. of Merle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Merle', 'n.', 'The European blackbird. See Blackbird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Merlin', 'n.', 'A small European falcon (Falco lithofalco, or F. aesalon).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Merling', 'n.', 'The European whiting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Merlon', 'n.', 'One of the solid parts of a battlemented parapet; a\n   battlement. See Illust. of Battlement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Merluce', 'n.', 'The European hake; -- called also herring hake and sea\n   pike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mermaid', 'n.', 'A fabled marine creature, typically represented as having\n   the upper part like that of a woman, and the lower like a fish; a sea\n   nymph, sea woman, or woman fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mermen', 'pl. ', 'of Merman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Merman', 'n.', 'The male corresponding to mermaid; a sea man, or man fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meroblast', 'n.', 'An ovum, as that of a mammal, only partially composed of\n   germinal matter, that is, consisting of both a germinal portion and an\n   albuminous or nutritive one; -- opposed to holoblast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meroblastic', 'a.', 'Consisting only in part of germinal matter;\n   characterized by partial segmentation only; as, meroblastic ova, in\n   which a portion of the yolk only undergoes fission; meroblastic\n   segmentation; -- opposed to holoblastic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Merocele', 'n.', 'Hernia in the thigh; femoral hernia .');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meroistic', 'a.', 'Applied to the ovaries of insects when they secrete\n   vitelligenous cells, as well as ova.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meropidan', 'n.', 'One of a family of birds (Meropidae), including the\n   bee-eaters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meropodite', 'n.', 'The fourth joint of a typical appendage of Crustacea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Merorganization', 'n.', 'Organization in part.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meros', 'n.', 'The plain surface between the channels of a triglyph.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meros', 'n.', 'The proximal segment of the hind limb; the thigh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Merosome', 'n.', 'One of the serial segments, or metameres, of which the\n   bodies of vertebrate and articulate animals are composed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Merostomata', 'n. pl.', 'A class of Arthropoda, allied to the Crustacea. It\n   includes the trilobites, Eurypteroidea, and Limuloidea. All are extinct\n   except the horseshoe crabs of the last group. See Limulus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Merou', 'n.', 'See Jack, 8 (c).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Merovingian', 'a.', 'Of or pertaining to the first Frankish dynasty in Gaul\n   or France.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Merovingian', 'n.', 'One of the kings of this dynasty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Merrily', 'adv.', 'In a merry manner; with mirth; with gayety and laughter;\n   jovially. See Mirth, and Merry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Merrimake', 'n.', 'See Merrymake, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Merrimake', 'v. i.', 'See Merrymake, v.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Merriment', 'n.', 'Gayety, with laughter; mirth; frolic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Merriness', 'n.', 'The quality or state of being merry; merriment; mirth;\n   gayety, with laughter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Merry', 'superl.', 'Laughingly gay; overflowing with good humor and good\n   spirits; jovial; inclined to laughter or play ; sportive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Merry', 'superl.', 'Cheerful; joyous; not sad; happy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Merry', 'superl.', 'Causing laughter, mirth, gladness, or delight; as, /\n   merry jest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Merry', 'n.', 'A kind of wild red cherry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Merry-andrew', 'n.', 'One whose business is to make sport for others; a\n   buffoon; a zany; especially, one who attends a mountebank or quack\n   doctor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Merry-go-round', 'n.', 'Any revolving contrivance for affording amusement;\n   esp., a ring of flying hobbyhorses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Merrymake', 'n.', 'Mirth; frolic; a meeting for mirth; a festival.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Merrymake', 'v. i.', 'To make merry; to be jolly; to feast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Merrymaker', 'n.', 'One who makes merriment or indulges in conviviality; a\n   jovial comrade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Merrymaking', 'a.', 'Making or producing mirth; convivial; jolly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Merrymaking', 'n.', 'The act of making merry; conviviality; merriment;\n   jollity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Merrymeeting', 'n.', 'A meeting for mirth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Merrythought', 'n.', 'The forked bone of a fowls breast; -- called also\n   wishbone. See Furculum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mersion', 'n.', 'Immersion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Merulidan', 'n.', 'A bird of the Thrush family.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Merus', 'n.', 'See Meros.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mervaille', 'n.', 'Marvel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mes-', '', 'See Meso-.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesa', '/.', 'A high tableland; a plateau on a hill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesaconate', 'n.', 'A salt of mesaconic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesaconic', 'a.', 'Pertaining to, or designating, one of several isomeric\n   acids obtained from citric acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesad', 'adv.', 'Same as Mesiad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesal', 'a.', 'Same as Mesial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesalliance', 'n.', 'A marriage with a person of inferior social position;\n   a misalliance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesally', 'adv.', 'Same as Mesially.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesam/boid', 'n.', 'One of a class of independent, isolated cells found in\n   the mesoderm, while the germ layers are undergoing differentiation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesaraic', 'a.', 'Mesenteric.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesaticephalic', 'a.', 'Having the ratio of the length to the breadth of\n   the cranium a medium one; neither brachycephalic nor dolichocephalic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesaticephalous', 'a.', 'Mesaticephalic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mescal', 'n.', 'A distilled liquor prepared in Mexico from a species of\n   agave. See Agave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesdames', 'n.', 'pl. of Madame and Madam.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meseemed', 'imp.', 'of Meseems');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meseems', 'v. impers.', 'It seems to me.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesel', 'n.', 'A leper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meselry', 'n.', 'Leprosy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesembryanthemum', 'n.', 'A genus of herbaceous or suffruticose plants,\n   chiefly natives of South Africa. The leaves are opposite, thick, and\n   f/eshy. The flowers usually open about midday, whence the name.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesencephalic', 'a.', 'Of or pertaining to the mesencephalon or midbrain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesencephalon', 'n.', 'The middle segment of the brain; the midbrain.\n   Sometimes abbreviated to mesen. See Brain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesenchyma', 'n.', 'The part of the mesoblast which gives rise to the\n   connective tissues and blood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesenteric', 'a.', 'Pertaining to a mesentery; mesaraic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesenteron', 'n.', 'All that part of the alimentary canal which is\n   developed from the primitive enteron and is lined with hypoblast. It is\n   distinguished from the stomod/um, a part at the anterior end of the\n   canal, including the cavity of the mouth, and the proctod/um, a part at\n   the posterior end, which are formed by invagination and are lined with\n   epiblast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesentery', 'n.', 'The membranes, or one of the membranes (consisting of a\n   fold of the peritoneum and inclosed tissues), which connect the\n   intestines and their appendages with the dorsal wall of the abdominal\n   cavity. The mesentery proper is connected with the jejunum and ilium,\n   the other mesenteries being called mesocaecum, mesocolon, mesorectum,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesentery', 'n.', 'One of the vertical muscular radiating partitions which\n   divide the body cavity of Anthozoa into chambers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meseraic', 'a.', 'Mesaraic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesethmoid', 'a.', 'Of or pertaining to the middle of the ethmoid region or\n   ethmoid bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesethmoid', 'n.', 'The median vertical plate, or median element, of the\n   ethmoid bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesh', 'n.', 'The opening or space inclosed by the threads of a net between\n   knot and knot, or the threads inclosing such a space; network; a net.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesh', 'n.', 'The engagement of the teeth of wheels, or of a wheel and\n   rack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meshed', 'imp. & p. p.', 'of Mesh');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meshing', 'p. pr. & vb. n.', 'of Mesh');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesh', 'v. t.', 'To catch in a mesh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesh', 'v. i.', 'To engage with each other, as the teeth of wheels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meshed', 'a.', 'Mashed; brewed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meshy', 'a.', 'Formed with meshes; netted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesiad', 'adv.', 'Toward, or on the side toward, the mesial plane;\n   mesially; -- opposed to laterad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesial', 'a.', 'Middle; median; in, or in the region of, the mesial plane;\n   internal; -- opposed to lateral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesially', 'adv.', 'In, near, or toward, the mesial plane; mesiad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesityl', 'n.', 'A hypothetical radical formerly supposed to exist in\n   mesityl oxide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesitylenate', 'n.', 'A salt of mesitylenic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesitylene', 'n.', 'A colorless, fragrant liquid, C6H3(CH3)3, of the\n   benzene series of hydrocarbons, obtained by distilling acetone with\n   sulphuric acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesitylol', 'n.', 'A crystalline substance obtained from mesitylene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meslin', 'n.', 'See Maslin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesmeree', 'n.', 'A person subjected to mesmeric influence; one who is\n   mesmerized.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesmeric', 'a.', 'Alt. of Mesmerical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesmerical', 'a.', 'Of, pertaining to, or induced by, mesmerism; as,\n   mesmeric sleep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesmerism', 'n.', 'The art of inducing an extraordinary or abnormal state\n   of the nervous system, in which the actor claims to control the\n   actions, and communicate directly with the mind, of the recipient. See\n   Animal magnetism, under Magnetism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesmerist', 'n.', 'One who practices, or believes in, mesmerism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesmerization', 'n.', 'The act of mesmerizing; the state of being\n   mesmerized.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesmerized', 'imp. & p. p.', 'of Mesmerize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesmerizing', 'p. pr. & vb. n.', 'of Mesmerize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesmerize', 'v. t.', 'To bring into a state of mesmeric sleep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesmerizer', 'n.', 'One who mesmerizes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesne', 'a.', 'Middle; intervening; as, a mesne lord, that is, a lord who\n   holds land of a superior, but grants a part of it to another person, in\n   which case he is a tenant to the superior, but lord or superior to the\n   second grantee, and hence is called the mesne lord.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meso-', '', 'Alt. of Mes-');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mes-', '', 'A combining form denoting in the middle, intermediate;');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mes-', '', 'denoting a type of hydrocarbons which are regarded as methenyl\n   derivatives. Also used adjectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesoarium', 'n.', 'The fold of peritoneum which suspends the ovary from the\n   dorsal wall of the body cavity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesoblast', 'n.', 'The mesoderm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesoblast', 'n.', 'The cell nucleus; mesoplast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesoblastic', 'a.', 'Relating to the mesoblast; as, the mesoblastic layer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesobranchial', 'a.', 'Of or pertaining to a region of the carapace of a\n   crab covering the middle branchial region.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesobronchia', 'pl. ', 'of Mesobronchium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesobronchium', 'n.', 'The main bronchus of each lung.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesocaecum', 'n.', 'The fold of peritoneum attached to the caecum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesocarp', 'n.', 'The middle layer of a pericarp which consists of three\n   distinct or dissimilar layers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesocephalic', 'a.', 'Of or pertaining to, or in the region of, the middle\n   of the head; as, the mesocephalic flexure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesocephalic', 'a.', 'Having the cranial cavity of medium capacity; neither\n   megacephalic nor microcephalic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesocephalic', 'a.', 'Having the ratio of the length to the breadth of the\n   cranium a medium one; mesaticephalic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesocephalon', 'n.', 'The pons Varolii.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesocephalous', 'a.', 'Mesocephalic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesocoele', 'n.', 'Alt. of Mesocoelia');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesocoelia', 'n.', 'The cavity of the mesencephalon; the iter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesocolon', 'n.', 'The fold of peritoneum, or mesentery, attached to the\n   colon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesocoracoid', 'n.', 'A process from the middle of the coracoid in some\n   animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesocuneiform', 'n.', 'Alt. of Mesocuniform');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesocuniform', 'n.', 'One of the bones of the tarsus. See 2d Cuneiform.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesoderm', 'n.', 'The layer of the blastoderm, between the ectoderm and\n   endoderm; mesoblast. See Illust. of Blastoderm and Ectoderm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesoderm', 'n.', 'The middle body layer in some invertebrates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesoderm', 'n.', 'The middle layer of tissue in some vegetable structures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesodermal', 'a.', 'Pertaining to, or derived from, the mesoderm; as,\n   mesodermal tissues.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesodermic', 'a.', 'Same as Mesodermal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesodont', 'a.', 'Having teeth of moderate size.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesogaster', 'n.', 'The fold of peritoneum connecting the stomach with the\n   dorsal wall of the abdominal cavity; the mesogastrium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesogastric', 'a.', 'Of or pertaining to the middle region of the abdomen,\n   or of the stomach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesogastric', 'a.', 'Of or pertaining to the mesogaster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesogastric', 'a.', 'Of or pertaining to the middle gastric lobe of the\n   carapace of a crab.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesogastrium', 'n.', 'The umbilical region.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesogastrium', 'n.', 'The mesogaster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesogl/a', 'n.', 'A thin gelatinous tissue separating the ectoderm and\n   endoderm in certain coelenterates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesognathous', 'a.', 'Having the jaws slightly projecting; between\n   prognathous and orthognathous. See Gnathic index, under Gnathic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesohepar', 'n.', 'A fold of the peritoneum connecting the liver with the\n   dorsal wall of the abdominal cavity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesohippus', 'n.', 'An extinct mammal of the Horse family, but not larger\n   than a sheep, and having three toes on each foot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesolabe', 'n.', 'An instrument of the ancients for finding two mean\n   proportionals between two given lines, required in solving the problem\n   of the duplication of the cube.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesole', 'n.', 'Same as Thomsonite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesolite', 'n.', 'A zeolitic mineral, grayish white or yellowish, occuring\n   in delicate groups of crystals, also fibrous massive. It is a hydrous\n   silicate of alumina, lime, and soda.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesologarithm', 'n.', 'A logarithm of the cosine or cotangent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesometrium', 'n.', 'The fold of the peritoneum supporting the oviduct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesomyodian', 'n.', 'A bird having a mesomyodous larynx.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesomyodous', 'a.', 'Having the intrinsic muscles of the larynx attached to\n   the middle of the semirings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meson', 'n.', 'The mesial plane dividing the body of an animal into similar\n   right and left halves. The line in which it meets the dorsal surface\n   has been called the dorsimeson, and the corresponding ventral edge the\n   ventrimeson.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesonasal', 'a.', 'Of or pertaining to the middle portion of the nasal\n   region.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesonephric', 'a.', 'Of or pertaining to the mesonephros; as, the\n   mesonephric, or Wolffian, duct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesonephros', 'n.', 'The middle one of the three pairs of embryonic renal\n   organs developed in most vertebrates; the Wolffian body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesonotum', 'n.', 'The dorsal portion of the mesothorax of insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesophl/um', 'n.', 'The middle bark of a tree; the green layer of bark,\n   usually soon covered by the outer or corky layer, and obliterated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesophryon', 'n.', 'See Glabella.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesophyllum', 'n.', 'The parenchyma of a leaf between the skin of the two\n   surfaces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesoplast', 'n.', 'The nucleus of a cell; mesoblast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesopodial', 'a.', 'Of or pertaining to the mesopodialia or to the parts of\n   the limbs to which they belong.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesopodialia', 'pl. ', 'of Mesopodiale');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesopodiale', 'n.', 'One of the bones of either the carpus or tarsus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesopodium', 'n.', 'The middle portion of the foot in the Gastropoda and\n   Pteropoda.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesopterygium', 'n.', 'The middle one of the three principal basal\n   cartilages in the fins of fishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesorchium', 'n.', 'The fold of peritoneum which attaches the testis to the\n   dorsal wall of the body cavity or scrotal sac.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesorectum', 'n.', 'The fold of peritoneum, or mesentery, attached to the\n   rectum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesorhine', 'a.', 'Having the nose of medium width; between leptorhine and\n   platyrhine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesosauria', 'n.', 'Same as Mosasauria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesoscapula', 'n.', 'A process from the middle of the scapula in some\n   animals; the spine of the scapula.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesoscapular', 'a.', 'Of or pertaining to the mesoscapula.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesoscutum', 'n.', 'The scutum or dorsal plate of the middle thoracic\n   segment of an insect. See Illust. of Butterfly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesoseme', 'a.', 'Having a medium orbital index; having orbits neither\n   broad nor narrow; between megaseme and microseme.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesosiderite', 'n.', 'See the Note under Meteorite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesosperm', 'n.', 'A membrane of a seed. See Secundine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesostate', 'n.', 'A product of metabolic action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesosternal', 'a.', 'Of or pertaining to the mesosternum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesosternum', 'n.', 'The middle portion, or body, of the sternum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesosternum', 'n.', 'The ventral piece of the middle segment of the thorax\n   in insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesotartaric', 'a.', 'Pertaining to, or designating, an acid called also\n   inactive tartaric acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesotheca', 'n.', 'The middle layer of the gonophore in the Hydrozoa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesothelium', 'n.', 'Epithelial mesoderm; a layer of cuboidal epithelium\n   cells, formed from a portion of the mesoderm during the differetiation\n   of the germ layers. It constitutes the boundary of the c/lum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesothoracic', 'a.', 'Of or pertaining to the mesothorax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesothorax', 'n.', 'The middle segment of the thorax in insects. See\n   Illust. of Coleoptera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesotrochal', 'a.', 'Having the middle of the body surrounded by bands of\n   cilia; -- said of the larvae of certain marine annelids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesotype', 'n.', 'An old term covering natrolite or soda mesolite,\n   scolecite or lime mesotype, and mesolite or lime-soda mesotype.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesovarium', 'n.', 'The fold of peritoneum connecting the ovary with the\n   wall of the abdominal cavity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesoxalate', 'n.', 'A salt of mesoxalic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesoxalic', 'a.', 'Pertaining to, or designating, an acid, CH2O2(CO2H)2,\n   obtained from amido malonic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesozoa', 'n. pl.', 'A group of very lowly organized, wormlike parasites,\n   including the Dicyemata. They are found in cephalopods. See Dicyemata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesozoic', 'a.', 'Belonging, or relating, to the secondary or reptilian\n   age, or the era between the Paleozoic and Cenozoic. See Chart of\n   Geology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesozoic', 'n.', 'The Mesozoic age or formation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesprise', 'n.', 'Contempt; scorn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesprise', 'n.', 'Misadventure; ill-success.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesquite', 'n.', 'Alt. of Mesquit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesquit', 'n.', 'A name for two trees of the southwestern part of North\n   America, the honey mesquite, and screw-pod mesquite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mess', 'n.', 'Mass; church service.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mess', 'n.', 'A quantity of food set on a table at one time; provision of\n   food for a person or party for one meal; as, a mess of pottage; also,\n   the food given to a beast at one time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mess', 'n.', 'A number of persons who eat together, and for whom food is\n   prepared in common; especially, persons in the military or naval\n   service who eat at the same table; as, the wardroom mess.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mess', 'n.', 'A set of four; -- from the old practice of dividing companies\n   into sets of four at dinner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mess', 'n.', 'The milk given by a cow at one milking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mess', 'n.', 'A disagreeable mixture or confusion of things; hence, a\n   situation resulting from blundering or from misunderstanding; as, he\n   made a mess of it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Messed', 'imp. & p. p.', 'of Mess');
  INSERT INTO words (word, word_type, description) VALUES  (    'Messing', 'p. pr. & vb. n.', 'of Mess');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mess', 'v. i.', 'To take meals with a mess; to belong to a mess; to eat\n   (with others); as, I mess with the wardroom officers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mess', 'v. t.', 'To supply with a mess.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Message', 'n.', 'Any notice, word, or communication, written or verbal,\n   sent from one person to another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Message', 'n.', 'Hence, specifically, an official communication, not made\n   in person, but delivered by a messenger; as, the Presidents message.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Message', 'v. t.', 'To bear as a message.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Message', 'n.', 'A messenger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Messager', 'n.', 'A messenger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Messenger', 'n.', 'One who bears a message; the bearer of a verbal or\n   written communication, notice, or invitation, from one person to\n   another, or to a public body; specifically, an office servant who bears\n   messages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Messenger', 'n.', 'One who, or that which, foreshows, or foretells.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Messenger', 'n.', 'A hawser passed round the capstan, and having its two\n   ends lashed together to form an endless rope or chain; -- formerly used\n   for heaving in the cable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Messenger', 'n.', 'A person appointed to perform certain ministerial duties\n   under bankrupt and insolvent laws, such as to take charge og the estate\n   of the bankrupt or insolvent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Messet', 'n.', 'A dog.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Messiad', 'n.', 'A German epic poem on the Messiah, by Klopstock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Messiah', 'n.', 'The expected king and deliverer of the Hebrews; the\n   Savior; Christ.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Messiahship', 'n.', 'The state or office of the Messiah.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Messianic', 'a.', 'Of or relating to the Messiah; as, the Messianic office\n   or character.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Messias', 'n.', 'The Messiah.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Messidor', 'n.', 'The tenth month of the French republican calendar dating\n   from September 22, 1792. It began June 19, and ended July 18. See\n   VendEmiaire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Messieurs', 'n. pl.', 'Sirs; gentlemen; -- abbreviated to Messrs., which is\n   used as the plural of Mr.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Messinese', 'a.', 'Of or pertaining to Messina, or its inhabitans.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Messmate', 'n.', 'An associate in a mess.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Messuage', 'n.', 'A dwelling house, with the adjacent buildings and\n   curtilage, and the adjoining lands appropriated to the use of the\n   household.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mest', 'a.', 'Most.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mestee', 'n.', 'The offspring of a white person and a quadroon; -- so\n   called in the West Indies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mester', 'n.', 'See Mister, a trade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mestinos', 'pl. ', 'of Mestino');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mestino', 'n.', 'See Mestizo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mestizos', 'pl. ', 'of Mestizo');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mestizo', 'n.', 'The offspring of an Indian or a negro and a European or\n   person of European stock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mestling', 'n.', 'A kind of brass. See Maslin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesymnicum', 'n.', 'A repetition at the end of a stanza.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Met', '', 'imp. & p. p. of Meet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Met', '', 'imp. & p. p. of Mete, to measure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Met', '', 'p. p. of Mete, to dream.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meta-', '', 'Alt. of Met-');
  INSERT INTO words (word, word_type, description) VALUES  (    'Met-', '', 'A prefix meaning between, with, after, behind, over, about,\n   reversely; as, metachronism, the error of placing after the correct\n   time; metaphor, lit., a carrying over; metathesis, a placing reversely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Met-', '', 'Other; duplicate, corresponding to; resembling; hence,\n   metameric; as, meta-arabinic, metaldehyde.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Met-', '', 'That two replacing radicals, in the benzene nucleus, occupy the\n   relative positions of 1 and 3, 2 and 4, 3 and 5, 4 and 6, 5 and 1, or 6\n   and 2; as, metacresol, etc. See Ortho-, and Para-.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Met-', '', 'Having less than the highest number of hydroxyl groups; -- said\n   of acids; as, metaphosphoric acid. Also used adjectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metabases', 'pl. ', 'of Metabasis');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metabasis', 'n.', 'A transition from one subject to another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metabasis', 'n.', 'Same as Metabola.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metabola', 'n.', 'Alt. of Metabole');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metabole', 'n.', 'A change or mutation; a change of disease, symptoms, or\n   treatment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metabola', 'n. pl.', 'Alt. of Metabolia');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metabolia', 'n. pl.', 'A comprehensive group of insects, including those\n   that undegro a metamorphosis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metabolian', 'n.', 'An insect which undergoes a metamorphosis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metabolic', 'a.', 'Of or pertaining to metamorphosis; pertaining to, or\n   involving, change.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metabolic', 'a.', 'Of or pertaining to metabolism; as, metabolic activity;\n   metabolic force.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metabolisis', 'n.', 'Metabolism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metabolism', 'n.', 'The act or process, by which living tissues or cells\n   take up and convert into their own proper substance the nutritive\n   material brought to them by the blood, or by which they transform their\n   cell protoplasm into simpler substances, which are fitted either for\n   excretion or for some special purpose, as in the manufacture of the\n   digestive ferments. Hence, metabolism may be either constructive\n   (anabolism), or destructive (katabolism).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metabolite', 'n.', 'A product of metabolism; a substance produced by\n   metabolic action, as urea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metabolize', 'v. t. & i.', 'To change by a metabolic process. See\n   Metabolism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metabranchial', 'a.', 'Of or pertaining to the lobe of the carapace of\n   crabs covering the posterior branchiae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metacarpal', 'a.', 'Of or pertaining to the metacarpus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metacarpal', 'n.', 'A metacarpal bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metacarpus', 'n.', 'That part of the skeleton of the hand or forefoot\n   between the carpus and phalanges. In man it consists of five bones. See\n   Illust. of Artiodactyla.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metacenter', 'n.', 'Alt. of -tre');
  INSERT INTO words (word, word_type, description) VALUES  (    '-tre', 'n.', 'The point of intersection of a vertical line through the\n   center of gravity of the fluid displaced by a floating body which is\n   tipped through a small angle from its position of equilibrium, and the\n   inclined line which was vertical through the center of gravity of the\n   body when in equilibrium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metacetone', 'n.', 'A colorless liquid of an agreeable odor, C6H10O,\n   obtained by distilling a mixture of sugar and lime; -- so called\n   because formerly regarded as a polymeric modification of acetone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metachloral', 'n.', 'A white, amorphous, insoluble substance regarded as a\n   polymeric variety of chloral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metachronism', 'n.', 'An error committed in chronology by placing an event\n   after its real time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metachrosis', 'n.', 'The power og changing color at will by the expansion\n   of special pigment cells, under nerve influence, as seen in many\n   reptiles, fishes, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metacinnabarite', 'n.', 'Sulphide of mercury in isometric form and black in\n   color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metacism', 'n.', 'A defect in pronouncing the letter m, or a too frequent\n   use of it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metacrolein', 'n.', 'A polymeric modification of acrolein obtained by\n   heating it with caustic potash. It is a crystalline substance having an\n   aromatic odor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metacromion', 'n.', 'A process projecting backward and downward from the\n   acromion of the scapula of some mammals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metadiscoidal', 'a.', 'Discoidal by derivation; -- applied especially to\n   the placenta of man and apes, because it is supposed to have been\n   derived from a diffused placenta.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metagastric', 'a.', 'Of or pertaining to the two posterior gastric lobes of\n   the carapace of crabs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metage', 'v.', 'Measurement, especially of coal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metage', 'v.', 'Charge for, or price of, measuring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metagenesis', 'n.', 'The change of form which one animal species undergoes\n   in a series of successively produced individuals, extending from the\n   one developed from the ovum to the final perfected individual. Hence,\n   metagenesis involves the production of sexual individuals by nonsexual\n   means, either directly or through intervening sexless generations.\n   Opposed to monogenesis. See Alternate generation, under Generation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metagenesis', 'n.', 'Alternation of sexual and asexual or gemmiparous\n   generations; -- in distinction from heterogamy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metagenetic', 'a.', 'Of or pertaining to metagenesis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metagenic', 'a.', 'Metagenetic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metagnathous', 'a.', 'Cross-billed; -- said of certain birds, as the\n   crossbill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metagrammatism', 'n.', 'Anagrammatism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metagraphic', 'a.', 'By or pertaining to metagraphy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metagraphy', 'n.', 'The art or act of rendering the letters of the alphabet\n   of one language into the possible equivalents of another;\n   transliteration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metal', 'n.', 'An elementary substance, as sodium, calcium, or copper,\n   whose oxide or hydroxide has basic rather than acid properties, as\n   contrasted with the nonmetals, or metalloids. No sharp line can be\n   drawn between the metals and nonmetals, and certain elements partake of\n   both acid and basic qualities, as chromium, manganese, bismuth, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metal', 'n.', 'Ore from which a metal is derived; -- so called by miners.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metal', 'n.', 'A mine from which ores are taken.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metal', 'n.', 'The substance of which anything is made; material; hence,\n   constitutional disposition; character; temper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metal', 'n.', 'Courage; spirit; mettle. See Mettle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metal', 'n.', 'The broken stone used in macadamizing roads and ballasting\n   railroads.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metal', 'n.', 'The effective power or caliber of guns carried by a vessel\n   of war.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metal', 'n.', 'Glass in a state of fusion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metal', 'n.', 'The rails of a railroad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metaled', 'imp. & p. p.', 'of Metal');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metalled', '', 'of Metal');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metaling', 'p. pr. & vb. n.', 'of Metal');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metalling', '', 'of Metal');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metal', 'v. t.', 'To cover with metal; as, to metal a ships bottom; to\n   metal a road.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metalammonium', 'n.', 'A hypothetical radical derived from ammonium by the\n   substitution of metallic atoms in place of hydrogen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metalbumin', 'n.', 'A form of albumin found in ascitic and certain serous\n   fluids. It is sometimes regarded as a mixture of albumin and mucin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metaldehyde', 'n.', 'A white crystalline substance isomeric with, and\n   obtained from, acetic aldehyde by polymerization, and reconvertible\n   into the same.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metalepses', 'pl. ', 'of Metalepsis');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metalepsis', 'n.', 'The continuation of a trope in one word through a\n   succession of significations, or the union of two or more tropes of a\n   different kind in one word.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metalepsy', 'n.', 'Exchange; replacement; substitution; metathesis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metaleptic', 'a.', 'Of or pertaining to a metalepsis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metaleptic', 'a.', 'Transverse; as, the metaleptic motion of a muscle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metaleptic', 'a.', 'Of, pertaining to, concerned in, or occurring by,\n   metalepsy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metaleptical', 'a.', 'Metaleptic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metallic', 'a.', 'Of or pertaining to a metal; of the nature of metal;\n   resembling metal; as, a metallic appearance; a metallic alloy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metallic', 'a.', 'Of, pertaining to, or characterized by, the essential and\n   implied properties of a metal, as contrasted with a nonmetal or\n   metalloid; basic; antacid; positive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metallical', 'a.', 'See Metallic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metallicly', 'adv.', 'In a metallic manner; by metallic means.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metallifacture', 'n.', 'The production and working or manufacture of\n   metals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metalliferous', 'a.', 'Producing metals; yielding metals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metalliform', 'a.', 'Having the form or structure of a metal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metalline', 'a.', 'Pertaining to, or resembling, a metal; metallic; as,\n   metalline properties.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metalline', 'a.', 'Impregnated with metallic salts; chalybeate; as,\n   metalline water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metalline', 'n.', 'A substance of variable composition, but resembling a\n   soft, dark-colored metal, used in the bearings of machines for\n   obviating friction, and as a substitute for lubricants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metallist', 'n.', 'A worker in metals, or one skilled in metals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metallization', 'n.', 'The act or process of metallizing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metallized', 'imp. & p. p.', 'of Metallize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metallizing', 'p. pr. & vb. n.', 'of Metallize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metallize', 'v. t.', 'To impart metallic properties to; to impregnate with\n   a metal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metallochrome', 'n.', 'A coloring produced by the deposition of some\n   metallic compound; specifically, the prismatic tints produced by\n   depositing a film of peroxide of lead on polished steel by electricity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metallochromy', 'n.', 'The art or process of coloring metals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metallograph', 'n.', 'A print made by metallography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metallographic', 'a.', 'Pertaining to, or by means of, metallography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metallographist', 'n.', 'One who writes on the subject of metals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metallography', 'n.', 'The science or art of metals and metal working;\n   also, a treatise on metals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metallography', 'n.', 'A method of transferring impressions of the grain of\n   wood to metallic surfaces by chemical action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metallography', 'n.', 'A substitute for lithography, in which metallic\n   plates are used instead of stone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metalloid', 'n.', 'Formerly, the metallic base of a fixed alkali, or\n   alkaline earth; -- applied by Sir H. Davy to sodium, potassium, and\n   some other metallic substances whose metallic character was supposed to\n   be not well defined.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metalloid', 'n.', 'Now, one of several elementary substances which in the\n   free state are unlike metals, and whose compounds possess or produce\n   acid, rather than basic, properties; a nonmetal; as, boron, carbon,\n   phosphorus, nitrogen, oxygen, sulphur, chlorine, bromine, etc., are\n   metalloids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metalloid', 'a.', 'Having the appearance of a metal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metalloid', 'a.', 'Having the properties of a nonmetal; nonmetallic; acid;\n   negative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metalloidal', 'a.', 'Metalloid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metallorganic', 'a.', 'Metalorganic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metallotherapy', 'n.', 'Treatment of disease by applying metallic plates to\n   the surface of the body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metallurgic', 'a.', 'Alt. of Metallurgical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metallurgical', 'a.', 'Of or pertaining to metallurgy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metallurgist', 'n.', 'One who works in metals, or prepares them for use;\n   one who is skilled in metallurgy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metallurgy', 'n.', 'The art of working metals, comprehending the whole\n   process of separating them from other matters in the ore, smelting,\n   refining, and parting them; sometimes, in a narrower sense, only the\n   process of extracting metals from their ores.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metalmen', 'pl. ', 'of Metalman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metalman', 'n.', 'A worker in metals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metalogical', 'a.', 'Beyond the scope or province of logic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metalorganic', 'a.', 'Pertaining to, or denoting, any one of a series of\n   compounds of certain metallic elements with organic radicals; as, zinc\n   methyl, sodium ethyl, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metamer', 'n.', 'Any one of several metameric forms of the same substance,\n   or of different substances having the same composition; as, xylene has\n   three metamers, viz., orthoxylene, metaxylene, and paraxylene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metamere', 'n.', 'One of successive or homodynamous parts in animals and\n   plants; one of a series of similar parts that follow one another in a\n   vertebrate or articulate animal, as in an earthworm; a segment; a\n   somite. See Illust. of Loevens larva.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metameric', 'a.', 'Having the same elements united in the same proportion\n   by weight, and with the same molecular weight, but possessing a\n   different structure and different properties; as, methyl ether and\n   ethyl alcohol are metameric compounds. See Isomeric.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metameric', 'a.', 'Of or pertaining to a metamere or its formation; as,\n   metameric segmentation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metamerically', 'adv.', 'In a metameric manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metamerism', 'n.', 'The symmetry of a metameric structure; serial symmetry;\n   the state of being made up of metameres.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metamerism', 'n.', 'The state or quality of being metameric; also, the\n   relation or condition of metameric compounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metamorphic', 'a.', 'Subject to change; changeable; variable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metamorphic', 'a.', 'Causing a change of structure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metamorphic', 'a.', 'Pertaining to, produced by, or exhibiting, certain\n   changes which minerals or rocks may have undergone since their original\n   deposition; -- especially applied to the recrystallization which\n   sedimentary rocks have undergone through the influence of heat and\n   pressure, after which they are called metamorphic rocks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metamorphism', 'n.', 'The state or quality of being metamorphic; the\n   process by which the material of rock masses has been more or less\n   recrystallized by heat, pressure, etc., as in the change of sedimentary\n   limestone to marble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metamorphist', 'n.', 'One who believes that the body of Christ was merged\n   into the Deity when he ascended.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metamorphize', 'v. t.', 'To metamorphose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metamorphosed', 'imp. & p. p.', 'of Metamorphose');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metamorphosing', 'p. pr. & vb. n.', 'of Metamorphose');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metamorphose', 'v. t.', 'To change into a different form; to transform; to\n   transmute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metamorphose', 'n.', 'Same as Metamorphosis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metamorphoser', 'n.', 'One who metamorphoses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metamorphosic', 'a.', 'Changing the form; transforming.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metamorphoses', 'pl. ', 'of Metamorphosis');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metamorphosis', 'n.', 'Change of form, or structure; transformation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metamorphosis', 'n.', 'A change in the form or function of a living\n   organism, by a natural process of growth or development; as, the\n   metamorphosis of the yolk into the embryo, of a tadpole into a frog, or\n   of a bud into a blossom. Especially, that form of sexual reproduction\n   in which an embryo undergoes a series of marked changes of external\n   form, as the chrysalis stage, pupa stage, etc., in insects. In these\n   intermediate stages sexual reproduction is usually impossible, but they\n   ultimately pass into final and sexually developed forms, from the union\n   of which organisms are produced which pass through the same cycle of\n   changes. See Transformation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metamorphosis', 'n.', 'The change of material of one kind into another\n   through the agency of the living organism; metabolism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metanauplius', 'n.', 'A larval crustacean in a stage following the\n   nauplius, and having about seven pairs of appendages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metanephritic', 'a.', 'Of or pertaining to the metanephros.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metanephros', 'n.', 'The most posterior of the three pairs of embryonic\n   renal organs developed in many vertebrates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metanotum', 'n.', 'The dorsal portion of the metaphorax of insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metantimonate', 'n.', 'A salt of metantimonic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metantimonic', 'a.', 'Pertaining to, or designating, an acid (formerly\n   called antimonic acid) analogous to metaphosphoric acid, and obtained\n   as a white amorphous insoluble substance, (HSbO3).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metantimonic', 'a.', 'Formerly, designating an acid, which is now properly\n   called pyroantimonic acid, and analogous to pyrophosphoric acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metapectic', 'a.', 'Pertaining to, or designating, a supposed acid obtained\n   from pectin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metapectin', 'n.', 'A substance obtained from, and resembling, pectin, and\n   occurring in overripe fruits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metapeptone', 'n.', 'An intermediate product formed in the gastric\n   digestion of albuminous matter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metaphor', 'n.', 'The transference of the relation between one set of\n   objects to another set for the purpose of brief explanation; a\n   compressed simile; e. g., the ship plows the sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metaphoric', 'a.', 'Alt. of Metaphorical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metaphorical', 'a.', 'Of or pertaining to metaphor; comprising a metaphor;\n   not literal; figurative; tropical; as, a metaphorical expression; a\n   metaphorical sense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metaphorist', 'n.', 'One who makes metaphors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metaphosphate', 'n.', 'A salt of metaphosphoric acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metaphosphoric', 'a.', 'Pertaining to, or designating, a monobasic acid,\n   HPO3, analogous to nitric acid, and, by heating phosphoric acid,\n   obtained as a crystalline substance, commonly called glacial phosphoric\n   acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metaphrase', 'n.', 'A verbal translation; a version or translation from one\n   language into another, word for word; -- opposed to paraphrase.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metaphrase', 'n.', 'An answering phrase; repartee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metaphrased', 'a.', 'Translated literally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metaphrasis', 'n.', 'Metaphrase.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metaphrast', 'n.', 'A literal translator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metaphrastic', 'a.', 'Alt. of Metaphrastical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metaphrastical', 'a.', 'Close, or literal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metaphysic', 'n.', 'See Metaphysics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metaphysic', 'a.', 'Metaphysical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metaphysical', 'a.', 'Of or pertaining to metaphysics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metaphysical', 'a.', 'According to rules or principles of metaphysics; as,\n   metaphysical reasoning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metaphysical', 'a.', 'Preternatural or supernatural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metaphysically', 'adv.', 'In the manner of metaphysical science, or of a\n   metaphysician.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metaphysician', 'n.', 'One who is versed in metaphysics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metaphysics', 'n.', 'The science of real as distinguished from phenomenal\n   being; ontology; also, the science of being, with reference to its\n   abstract and universal conditions, as distinguished from the science of\n   determined or concrete being; the science of the conceptions and\n   relations which are necessarily implied as true of every kind of being;\n   phylosophy in general; first principles, or the science of first\n   principles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metaphysics', 'n.', 'Hence: The scientific knowledge of mental phenomena;\n   mental philosophy; psychology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metaphysis', 'n.', 'Change of form; transformation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metaplasm', 'n.', 'A change in the letters or syllables of a word.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metaplast', 'n.', 'A word having more than one form of the root.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metapode', 'n.', 'The posterior division of the foot in the Gastropoda and\n   Pteropoda.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metapodial', 'a.', 'Of or pertaining to the metapodialia, or to the parts\n   of the limbs to which they belong.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metapodialia', 'pl. ', 'of Metapodiale');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metapodiale', 'n.', 'One of the bones of either the metacarpus or\n   metatarsus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metapodia', 'pl. ', 'of Metapodium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metapodium', 'n.', 'Same as Metapode.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metapophyses', 'pl. ', 'of Metapophysis');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metapophysis', 'n.', 'A tubercle projecting from the anterior articular\n   processes of some vertebr/; a mammillary process.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metapterygium', 'n.', 'The posterior of the three principal basal\n   cartilages in the fins of fishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metasilicate', 'n.', 'A salt of metasilicic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metasilicic', 'a.', 'Designating an acid derived from silicic acid by the\n   removal of water; of or pertaining to such an acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metasomatism', 'n.', 'An alteration in a mineral or rock mass when\n   involving a chemical change of the substance, as of chrysolite to\n   serpentine; -- opposed to ordinary metamorphism, as implying simply a\n   recrystallization.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metasome', 'n.', 'One of the component segments of the body of an animal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metastannate', 'n.', 'A salt of metastannic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metastannic', 'a.', 'Pertaining to, or designating, a compound of tin\n   (metastannic acid), obtained, as an isomeric modification of stannic\n   acid, in the form of a white amorphous substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metastases', 'pl. ', 'of Metastasis');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metastasis', 'n.', 'A spiritual change, as during baptism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metastasis', 'n.', 'A change in the location of a disease, as from one part\n   to another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metastasis', 'n.', 'The act or process by which matter is taken up by cells\n   or tissues and is transformed into other matter; in plants, the act or\n   process by which are produced all of those chemical changes in the\n   constituents of the plant which are not accompanied by a production of\n   organic matter; metabolism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metastatic', 'a.', 'Of, pertaining to, or caused by, metastasis; as, a\n   metastatic abscess; the metastatic processes of growth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metasternal', 'a.', 'Of or pertaining to the metasternum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metasternum', 'n.', 'The most posterior element of the sternum; the\n   ensiform process; xiphisternum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metasternum', 'n.', 'The ventral plate of the third or last segment of the\n   thorax of insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metastoma', 'n.', 'Alt. of Metastome');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metastome', 'n.', 'A median elevation behind the mouth in the arthropods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metatarsal', 'a.', 'Of or pertaining to the metatarsus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metatarsal', 'n.', 'A metatarsal bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metatarse', 'n.', 'Metatarsus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metatarsi', 'pl. ', 'of Metatarsus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metatarsus', 'n.', 'That part of the skeleton of the hind or lower limb\n   between the tarsus and phalanges; metatarse. It consists, in the human\n   foot, of five bones. See Illustration in Appendix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metatheses', 'pl. ', 'of Metathesis');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metathesis', 'n.', 'Transposition, as of the letters or syllables of a\n   word; as, pistris for pristis; meagre for meager.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metathesis', 'n.', 'A mere change in place of a morbid substance, without\n   removal from the body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metathesis', 'n.', 'The act, process, or result of exchange, substitution,\n   or replacement of atoms and radicals; thus, by metathesis an acid gives\n   up all or part of its hydrogen, takes on an equivalent amount of a\n   metal or base, and forms a salt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metathetic', 'a.', 'Alt. of Metathetical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metathetical', 'a.', 'Of or pertaining to metathesis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metathoracic', 'a.', 'Of or pertaining to the metathorax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metathorax', 'n.', 'The last or posterior segment of the thorax in insects.\n   See Illust. of Coleoptera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metatitanic', 'a.', 'Of, pertaining to, or designating, an acid of titanium\n   analogous to metasilicic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metatungstate', 'n.', 'A salt of metatungstic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metatungstic', 'a.', 'Of, pertaining to, or designating, an acid known only\n   in its salts (the metatungstates) and properly called polytungstic, or\n   pyrotungstic, acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metavanadate', 'n.', 'A salt of metavanadic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metavanadic', 'a.', 'Of, pertaining to, or designating, a vanadic acid\n   analogous to metaphosphoric acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metaxylene', 'n.', 'That variety of xylene, or dimethyl benzene, in which\n   the two methyl groups occupy the meta position with reference to each\n   other. It is a colorless inf/ammable liquid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metayage', 'n.', 'A system of farming on halves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metayer', 'a.', 'One who cultivates land for a share (usually one half) of\n   its yield, receiving stock, tools, and seed from the landlord.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metazoa', 'n. pl.', 'Those animals in which the protoplasmic mass,\n   constituting the egg, is converted into a multitude of cells, which are\n   metamorphosed into the tissues of the body. A central cavity is\n   commonly developed, and the cells around it are at first arranged in\n   two layers, -- the ectoderm and endoderm. The group comprises nearly\n   all animals except the Protozoa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metazoans', 'pl. ', 'of Metazoan');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metazoan', 'n.', 'One of the Metazoa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metazoic', 'a.', 'Of or pertaining to the Metazoa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metazoon', 'n.', 'One of the Metazoa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mete', 'n.', 'Meat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mete', 'v. t. & i.', 'To meet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mette', 'imp.', 'of Mete');
  INSERT INTO words (word, word_type, description) VALUES  (    'Met', 'p. p.', 'of Mete');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mete', 'v. i. & t.', 'To dream; also impersonally; as, me mette, I dreamed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meted', 'imp. & p. p.', 'of Mete');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meting', 'p. pr. & vb. n.', 'of Mete');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mete', 'a.', 'To find the quantity, dimensions, or capacity of, by any rule\n   or standard; to measure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mete', 'v. i.', 'To measure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mete', 'n.', 'Measure; limit; boundary; -- used chiefly in the plural, and\n   in the phrase metes and bounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metecorn', 'n.', 'A quantity of corn formerly given by the lord to his\n   customary tenants, as an encouragement to, or reward for, labor and\n   faithful service.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metely', 'a.', 'According to measure or proportion; proportionable;\n   proportionate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metempiric', 'a.', 'Alt. of Metempirical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metempirical', 'a.', 'Related, or belonging, to the objects of knowledge\n   within the province of metempirics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metempiricism', 'n.', 'The science that is concerned with metempirics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metempirics', 'n.', 'The concepts and relations which are conceived as\n   beyond, and yet as related to, the knowledge gained by experience.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metempsychosed', 'imp. & p. p.', 'of Metempsychose');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metempsychosing', 'p. pr. & vb. n.', 'of Metempsychose');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metempsychose', 'v. t.', 'To translate or transfer, as the soul, from one\n   body to another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metempsychosis', 'n.', 'The passage of the soul, as an immortal essence, at\n   the death of the animal body it had inhabited, into another living\n   body, whether of a brute or a human being; transmigration of souls.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metemptosis', 'n.', 'The suppression of a day in the calendar to prevent\n   the date of the new moon being set a day too late, or the suppression\n   of the bissextile day once in 134 years. The opposite to this is the\n   proemptosis, or the addition of a day every 330 years, and another\n   every 2,400 years.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metencephalon', 'n.', 'The posterior part of the brain, including the\n   medulla; the afterbrain. Sometimes abbreviated to meten.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metensomatosis', 'n.', 'The assimilation by one body or organism of the\n   elements of another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meteor', 'n.', 'Any phenomenon or appearance in the atmosphere, as clouds,\n   rain, hail, snow, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meteor', 'n.', 'Specif.: A transient luminous body or appearance seen in\n   the atmosphere, or in a more elevated region.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meteoric', 'a.', 'Of or pertaining to a meteor, or to meteors; atmospheric,\n   as, meteoric phenomena; meteoric stones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meteoric', 'a.', 'Influenced by the weather; as, meteoric conditions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meteoric', 'a.', 'Flashing; brilliant; transient; like a meteor; as,\n   meteoric fame.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meteorical', 'a.', 'Meteoric.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meteorism', 'n.', 'Flatulent distention of the abdomen; tympanites.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meteorite', 'n.', 'A mass of stone or iron which has fallen to the earth\n   from space; an aerolite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meteorize', 'v. i.', 'To ascend in vapors; to take the form of a meteor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meteorograph', 'n.', 'An instrument which registers meteorologic phases or\n   conditions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meteorographic', 'a.', 'Of or pertaining to meteorography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meteorography', 'n.', 'The registration of meteorological phenomena.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meteoroid', 'n.', 'A small body moving through space, or revolving about\n   the sun, which on entering the earths atmosphere would be deflagrated\n   and appear as a meteor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meteoroidal', 'a.', 'Of or pertaining to a meteoroid or to meteoroids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meteorolite', 'n.', 'A meteoric stone; an aerolite; a meteorite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meteoroligic', 'a.', 'Alt. of Meteorological');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meteorological', 'a.', 'Of or pertaining to the atmosphere and its\n   phenomena, or to meteorology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meteorologist', 'n.', 'A person skilled in meteorology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meteorology', 'n.', 'The science which treats of the atmosphere and its\n   phenomena, particularly of its variations of heat and moisture, of its\n   winds, storms, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meteoromancy', 'n.', 'A species of divination by meteors, chiefly by\n   thunder and lightning, which was held in high estimation by the Romans.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meteorometer', 'n.', 'An apparatus which transmits automatically to a\n   central station atmospheric changes as marked by the anemometer,\n   barometer, thermometer, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meteoroscope', 'n.', 'An astrolabe; a planisphere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meteoroscope', 'n.', 'An instrument for measuring the position, length, and\n   direction, of the apparent path of a shooting star.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meteorous', 'a.', 'Of the nature or appearance of a meteor.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-meter', '', 'A suffix denoting that by which anything is measured; as,\n   barometer, chronometer, dynamometer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meter', 'n.', 'One who, or that which, metes or measures. See Coal-meter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meter', 'n.', 'An instrument for measuring, and usually for recording\n   automatically, the quantity measured.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meter', 'n.', 'A line above or below a hanging net, to which the net is\n   attached in order to strengthen it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meter', 'n.', 'Alt. of Metre');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metre', 'n.', 'Rhythmical arrangement of syllables or words into verses,\n   stanzas, strophes, etc.; poetical measure, depending on number,\n   quantity, and accent of syllables; rhythm; measure; verse; also, any\n   specific rhythmical arrangements; as, the Horatian meters; a dactylic\n   meter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metre', 'n.', 'A poem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metre', 'n.', 'A measure of length, equal to 39.37 English inches, the\n   standard of linear measure in the metric system of weights and\n   measures. It was intended to be, and is very nearly, the ten millionth\n   part of the distance from the equator to the north pole, as ascertained\n   by actual measurement of an arc of a meridian. See Metric system, under\n   Metric.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meterage', 'n.', 'The act of measuring, or the cost of measuring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metergram', 'n.', 'A measure of energy or work done; the power exerted in\n   raising one gram through the distance of one meter against gravitation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metewand', 'n.', 'A measuring rod.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meteyard', 'n.', 'A yard, staff, or rod, used as a measure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meth', 'n.', 'See Meathe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Methaemoglobin', 'n.', 'A stable crystalline compound obtained by the\n   decomposition of hemoglobin. It is found in old blood stains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Methal', 'n.', 'A white waxy substance, found in small quantities in\n   spermaceti as an ethereal salt of several fatty acids, and regarded as\n   an alcohol of the methane series.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Methane', 'n.', 'A light, colorless, gaseous, inflammable hydrocarbon, CH4;\n   marsh gas. See Marsh gas, under Gas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metheglin', 'n.', 'A fermented beverage made of honey and water; mead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Methene', 'n.', 'See Methylene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Methenyl', 'n.', 'The hypothetical hydrocarbon radical CH, regarded as an\n   essential residue of certain organic compounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Methide', 'n.', 'A binary compound of methyl with some element; as,\n   aluminium methide, Al2(CH3)6.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Methought', 'imp.', 'of Methinks');
  INSERT INTO words (word, word_type, description) VALUES  (    'Methinks', 'v. impers.', 'It seems to me; I think. See Me.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Methionate', 'n.', 'A salt of methionic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Methionic', 'a.', 'Of, pertaining to, or designating, a sulphonic (thionic)\n   acid derivative of methane, obtained as a stable white crystalline\n   substance, CH2.(SO3H)2, which forms well defined salts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Method', 'n.', 'An orderly procedure or process; regular manner of doing\n   anything; hence, manner; way; mode; as, a method of teaching languages;\n   a method of improving the mind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Method', 'n.', 'Orderly arrangement, elucidation, development, or\n   classification; clear and lucid exhibition; systematic arrangement\n   peculiar to an individual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Method', 'n.', 'Classification; a mode or system of classifying natural\n   objects according to certain common characteristics; as, the method of\n   Theophrastus; the method of Ray; the Linnaean method.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Methodic', 'a.', 'Alt. of Methodical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Methodical', 'a.', 'Arranged with regard to method; disposed in a suitable\n   manner, or in a manner to illustrate a subject, or to facilitate\n   practical observation; as, the methodical arrangement of arguments; a\n   methodical treatise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Methodical', 'a.', 'Proceeding with regard to method; systematic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Methodical', 'a.', 'Of or pertaining to the ancient school of physicians\n   called methodists.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Methodios', 'n.', 'The art and principles of method.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Methodism', 'n.', 'The system of doctrines, polity, and worship, of the\n   sect called Methodists.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Methodist', 'n.', 'One who observes method.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Methodist', 'n.', 'One of an ancient school of physicians who rejected\n   observation and founded their practice on reasoning and theory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Methodist', 'n.', 'One of a sect of Christians, the outgrowth of a small\n   association called the \"Holy Club,\" formed at Oxford University, A.D.\n   1729, of which the most conspicuous members were John Wesley and his\n   brother Charles; -- originally so called from the methodical strictness\n   of members of the club in all religious duties.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Methodist', 'n.', 'A person of strict piety; one who lives in the exact\n   observance of religious duties; -- sometimes so called in contempt or\n   ridicule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Methodist', 'a.', 'Of or pertaining to the sect of Methodists; as,\n   Methodist hymns; a Methodist elder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Methodistic', 'a.', 'Alt. of Methodistical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Methodistical', 'a.', 'Of or pertaining to methodists, or to the\n   Methodists.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Methodization', 'n.', 'The act or process of methodizing, or the state of\n   being methodized.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Methodized', 'imp. & p. p.', 'of Methodize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Methodizing', 'p. pr. & vb. n.', 'of Methodize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Methodize', 'v. t.', 'To reduce to method; to dispose in due order; to\n   arrange in a convenient manner; as, to methodize ones work or\n   thoughts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Methodizer', 'n.', 'One who methodizes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Methodological', 'a.', 'Of or pertaining to methodology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Methodology', 'n.', 'The science of method or arrangement; a treatise on\n   method.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Methol', 'n.', 'The technical name of methyl alcohol or wood spirit; also,\n   by extension, the class name of any of the series of alcohols of the\n   methane series of which methol proper is the type. See Methyl alcohol,\n   under Methyl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Methoxyl', 'n.', 'A hypothetical radical, CH3O, analogous to hydroxyl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Methyl', 'n.', 'A hydrocarbon radical, CH3, not existing alone but regarded\n   as an essential residue of methane, and appearing as a component part\n   of many derivatives; as, methyl alcohol, methyl ether, methyl amine,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Methylal', 'n.', 'A light, volatile liquid, H2C(OCH3)2, regarded as a\n   complex ether, and having a pleasant ethereal odor. It is obtained by\n   the partial oxidation of methyl alcohol. Called also formal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Methylamine', 'n.', 'See Methyl amine, under Methyl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Methylate', 'n.', 'An alcoholate of methyl alcohol in which the hydroxyl\n   hydrogen is replaced by a metal, after the analogy of a hydrate; as,\n   sodium methylate, CH3ONa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Methylate', 'v. t.', 'To impregnate or mix with methyl or methyl alcohol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Methylated', 'a.', 'Impregnated with, or containing, methyl alcohol or wood\n   spirit; as, methylated spirits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Methylene', 'n.', 'A hydrocarbon radical, CH2, not known in the free state,\n   but regarded as an essential residue and component of certain\n   derivatives of methane; as, methylene bromide, CH2Br2; -- formerly\n   called also methene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Methylic', 'a.', 'Pertaining to, derived from, or containing, methyl;\n   specifically, designating methyl alcohol. See under Methyl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Methysticin', 'n.', 'A white, silky, crystalline substance extracted from\n   the thick rootstock of a species of pepper (Piper methysticum) of the\n   South Sea Islands; -- called also kanakin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metic', 'n.', 'A sojourner; an immigrant; an alien resident in a Grecian\n   city, but not a citizen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meticulous', 'a.', 'Timid; fearful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metif', 'n. f.', 'Alt. of Metive');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metive', 'n. f.', 'See Metis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metis', 'n. f.', 'Alt. of Metisse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metisse', 'n. f.', 'The offspring of a white person and an American Indian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metisse', 'n. f.', 'The offspring of a white person and a quadroon; an\n   octoroon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metoche', 'n.', 'The space between two dentils.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metoche', 'n.', 'The space between two triglyphs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metonic', 'a.', 'Pertaining to, or discovered by, Meton, the Athenian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metonymic', 'a.', 'Alt. of Metonymical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metonymical', 'a.', 'Used by way of metonymy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metonymy', 'n.', 'A trope in which one word is put for another that\n   suggests it; as, we say, a man keeps a good table instead of good\n   provisions; we read Virgil, that is, his poems; a man has a warm heart,\n   that is, warm affections.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metope', 'n.', 'The space between two triglyphs of the Doric frieze, which,\n   among the ancients, was often adorned with carved work. See Illust. of\n   Entablature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metope', 'n.', 'The face of a crab.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metopic', 'a.', 'Of or pertaining to the forehead or frontal bones;\n   frontal; as, the metopic suture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metopomancy', 'n.', 'Fortune telling by physiognomy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metoposcopic', 'a.', 'Alt. of Metoposcopical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metoposcopical', 'a.', 'Of or relating to metoposcopy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metoposcopist', 'n.', 'One versed in metoposcopy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metoposcopy', 'n.', 'The study of physiognomy; the art of discovering the\n   character of persons by their features, or the lines of the face.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metosteon', 'n.', 'The postero-lateral ossification in the sternum of\n   birds; also, the part resulting from such ossification.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metre', 'n.', 'See Meter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metric', 'a.', 'Relating to measurement; involving, or proceeding by,\n   measurement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metric', 'a.', 'Of or pertaining to the meter as a standard of measurement;\n   of or pertaining to the decimal system of measurement of which a meter\n   is the unit; as, the metric system; a metric measurement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metrical', 'a.', 'Of or pertaining to the meter; arranged in meter;\n   consisting of verses; as, metrical compositions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metrical', 'a.', 'Of or pertaining to measurement; as, the inch, foot,\n   yard, etc., are metrical terms; esp., of or pertaining to the metric\n   system.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metrically', 'adv.', 'In a metrical manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metrician', 'n.', 'A composer of verses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metric system', '', 'See Metric, a.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metrification', 'n.', 'Composition in metrical form; versification.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metrify', 'v. i.', 'To make verse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metrist', 'n.', 'A maker of verses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metritis', 'n.', 'Inflammation of the womb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metrochrome', 'n.', 'An instrument for measuring colors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metrograph', 'n.', 'An instrument attached to a locomotive for recording\n   its speed and the number and duration of its stops.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metrological', 'a.', 'Of or pertaining to metrology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metrology', 'n.', 'The science of, or a system of, weights and measures;\n   also, a treatise on the subject.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metromania', 'n.', 'A mania for writing verses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metromaniac', 'n.', 'One who has metromania.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metrometer', 'n.', 'An instrument for measuring the size of the womb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metronome', 'n.', 'An instrument consisting of a short pendulum with a\n   sliding weight. It is set in motion by clockwork, and serves to measure\n   time in music.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metronomy', 'n.', 'Measurement of time by an instrument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metronymic', 'a.', 'Derived from the name of ones mother, or other female\n   ancestor; as, a metronymic name or appellation. -- A metronymic\n   appellation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metropole', 'n.', 'A metropolis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metropolis', 'n.', 'The mother city; the chief city of a kingdom, state, or\n   country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metropolis', 'n.', 'The seat, or see, of the metropolitan, or highest\n   church dignitary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metropolitan', 'a.', 'Of or pertaining to the capital or principal city of\n   a country; as, metropolitan luxury.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metropolitan', 'a.', 'Of, pertaining to, or designating, a metropolitan or\n   the presiding bishop of a country or province, his office, or his\n   dignity; as, metropolitan authority.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metropolitan', 'n.', 'The superior or presiding bishop of a country or\n   province.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metropolitan', 'n.', 'An archbishop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metropolitan', 'n.', 'A bishop whose see is civil metropolis. His rank is\n   intermediate between that of an archbishop and a patriarch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metropolitanate', 'n.', 'The see of a metropolitan bishop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metropolite', 'n.', 'A metropolitan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metropolitical', 'a.', 'Of or pertaining to a metropolis; being a\n   metropolis; metropolitan; as, the metropolitical chair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metrorrhagia', 'n.', 'Profuse bleeding from the womb, esp. such as does not\n   occur at the menstrual period.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metroscope', 'n.', 'A modification of the stethoscope, for directly\n   auscultating the uterus from the vagina.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metrosideros', 'n.', 'A myrtaceous genus of trees or shrubs, found in\n   Australia and the South Sea Islands, and having very hard wood.\n   Metrosideros vera is the true ironwood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metrotome', 'n.', 'An instrument for cutting or scarifying the uterus or\n   the neck of the uterus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Metrotomy', 'n.', 'The operation of cutting into the uterus; hysterotomy;\n   the Caesarean section.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-metry', '', 'A suffix denoting the art, process, or science, of measuring;\n   as, acidmetry, chlorometry, chronometry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mette', '', 'imp. of Mete, to dream.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mettle', 'n.', 'Substance or quality of temperament; spirit, esp. as\n   regards honor, courage, fortitude, ardor, etc.; disposition; -- usually\n   in a good sense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mettled', 'a.', 'Having mettle; high-spirited; ardent; full of fire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mettlesome', 'a.', 'Full of spirit; possessing constitutional ardor; fiery;\n   as, a mettlesome horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meute', 'n.', 'A cage for hawks; a mew. See 4th Mew, 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Meve', 'v. t. & i.', 'To move.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mew', 'n.', 'A gull, esp. the common British species (Larus canus); called\n   also sea mew, maa, mar, mow, and cobb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mewed', 'imp. & p. p.', 'of Mew');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mewing', 'p. pr. & vb. n.', 'of Mew');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mew', 'v. t.', 'To shed or cast; to change; to molt; as, the hawk mewed his\n   feathers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mew', 'v. i.', 'To cast the feathers; to molt; hence, to change; to put on\n   a new appearance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mew', 'n.', 'A cage for hawks while mewing; a coop for fattening fowls;\n   hence, any inclosure; a place of confinement or shelter; -- in the\n   latter sense usually in the plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mew', 'n.', 'A stable or range of stables for horses; -- compound used in\n   the plural, and so called from the royal stables in London, built on\n   the site of the kings mews for hawks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mew', 'v. t.', 'To shut up; to inclose; to confine, as in a cage or other\n   inclosure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mew', 'v. i.', 'To cry as a cat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mew', 'n.', 'The common cry of a cat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mewled', 'imp. & p. p.', 'of Mewl');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mewling', 'p. pr. & vb. n.', 'of Mewl');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mewl', 'v. i.', 'To cry, as a young child; to squall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mewler', 'n.', 'One that mewls.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mews', 'n. sing. & pl.', 'An alley where there are stables; a narrow\n   passage; a confined place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mexal', 'mexcal.', 'Alt. of Mexical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mexical', 'mexcal.', 'See Mescal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mexican', 'a.', 'Of or pertaining to Mexico or its people.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mexican', 'n.', 'A native or inhabitant of Mexico.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mexicanize', 'v. t.', 'To cause to be like the Mexicans, or their country,\n   esp. in respect of frequent revolutions of government.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mexicanize', 'v. i.', 'To become like the Mexicans, or their country or\n   government.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neyne', 'n.', 'Same as Meine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mezcal', 'n.', 'Same as Mescal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mezereon', 'n.', 'A small European shrub (Daphne Mezereum), whose acrid\n   bark is used in medicine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mezquita', 'n.', 'A mosque.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mezuzoth', 'n.', 'A piece of parchment bearing the Decalogue and attached\n   to the doorpost; -- in use among orthodox Hebrews.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mezzanine', 'n.', 'Same as Entresol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mezzanine', 'n.', 'A partial story which is not on the same level with the\n   story of the main part of the edifice, as of a back building, where the\n   floors are on a level with landings of the staircase of the main house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mezza voce', '', 'With a medium fullness of sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mezzo', 'a.', 'Mean; not extreme.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mezzo-relievo', 'n.', 'Mezzo-rilievo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mezzo-rilievo', 'n.', 'A middle degree of relief in figures, between high\n   and low relief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mezzo-rilievo', 'n.', 'Sculpture in this kind of relief. See under\n   Alto-rilievo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mezzo-soprano', 'a.', 'Having a medium compass between the soprano and\n   contralto; -- said of the voice of a female singer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mezzo-soprano', 'n.', 'A mezzo-soprano voice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mezzo-soprano', 'n.', 'A person having such a voice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mezzotint', 'n.', 'A manner of engraving on copper or steel by drawing upon\n   a surface previously roughened, and then removing the roughness in\n   places by scraping, burnishing, etc., so as to produce the requisite\n   light and shade. Also, an engraving so produced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mezzotint', 'v. t.', 'To engrave in mezzotint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mezzotinter', 'n.', 'One who engraves in mezzotint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mezzotinto', 'n.', 'Mezzotint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mezzotintoed', 'imp. & p. p.', 'of Mezzotinto');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mezzotintoing', 'p. pr. & vb. n.', 'of Mezzotinto');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mezzotinto', 'v. t.', 'To engrave in mezzotint; to represent by mezzotint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mhorr', 'n.', 'See Mohr.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mi', 'n.', 'A syllable applied to the third tone of the scale of C, i. e.,\n   to E, in European solmization, but to the third tone of any scale in\n   the American system.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Miamis', 'n. pl.', 'A tribe of Indians that formerly occupied the country\n   between the Wabash and Maumee rivers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Miargyrite', 'n.', 'A mineral of an iron-black color, and very sectile,\n   consisting principally of sulphur, antimony, and silver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mias', 'n.', 'The orang-outang.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Miascite', 'n.', 'A granitoid rock containing feldspar, biotite, elaeolite,\n   and sodalite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Miasm', 'n.', 'Miasma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Miasmata', 'pl. ', 'of Miasma');
  INSERT INTO words (word, word_type, description) VALUES  (    'Miasma', 'n.', 'Infectious particles or germs floating in the air; air made\n   noxious by the presence of such particles or germs; noxious effluvia;\n   malaria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Miasmal', 'a.', 'Containing miasma; miasmatic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Miasmatic', 'a.', 'Alt. of Miasmatical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Miasmatical', 'a.', 'Containing, or relating to, miasma; caused by miasma;\n   as, miasmatic diseases.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Miasmatist', 'n.', 'One who has made a special study of miasma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Miasmology', 'n.', 'That department of medical science which treats of\n   miasma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Miauled', 'imp. & p. p.', 'of Miaul');
  INSERT INTO words (word, word_type, description) VALUES  (    'Miauling', 'p. pr. & vb. n.', 'of Miaul');
  INSERT INTO words (word, word_type, description) VALUES  (    'Miaul', 'v. i.', 'To cry as a cat; to mew; to caterwaul.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Miaul', 'n.', 'The crying of a cat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mica', 'n.', 'The name of a group of minerals characterized by highly\n   perfect cleavage, so that they readily separate into very thin leaves,\n   more or less elastic. They differ widely in composition, and vary in\n   color from pale brown or yellow to green or black. The transparent\n   forms are used in lanterns, the doors of stoves, etc., being popularly\n   called isinglass. Formerly called also cat-silver, and glimmer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Micaceo-calcareous', 'a.', 'Partaking of the nature of, or consisting of,\n   mica and lime; -- applied to a mica schist containing carbonate of\n   lime.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Micaceous', 'a.', 'Pertaining to, or containing, mica; splitting into\n   laminae or leaves like mica.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mice', 'n.', 'pl of Mouse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Micellae', 'pl. ', 'of Micella');
  INSERT INTO words (word, word_type, description) VALUES  (    'Micella', 'n.', 'A theoretical aggregation of molecules constituting a\n   structural particle of protoplasm, capable of increase or diminution\n   without change in chemical nature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mich', 'v. i.', 'Alt. of Miche');
  INSERT INTO words (word, word_type, description) VALUES  (    'Miche', 'v. i.', 'To lie hid; to skulk; to act, or carry ones self,\n   sneakingly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Michaelmas', 'n.', 'The feat of the archangel Michael, a church festival,\n   celebrated on the 29th of September. Hence, colloquially, autumn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Micher', 'n.', 'One who skulks, or keeps out of sight; hence, a truant; an\n   idler; a thief, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Michery', 'n.', 'Theft; cheating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Miching', 'a.', 'Hiding; skulking; cowardly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mickle', 'a.', 'Much; great.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Micmacs', 'n. pl.', 'A tribe of Indians inhabiting Nova Scotia and New\n   Brunswick.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mico', 'n.', 'A small South American monkey (Mico melanurus), allied to the\n   marmoset. The name was originally applied to an albino variety.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Micracoustic', 'a.', 'Same as Microustic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Micraster', 'n.', 'A genus of sea urchins, similar to Spatangus, abounding\n   in the chalk formation; -- from the starlike disposal of the ambulacral\n   furrows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Micrencephalous', '', 'Having a small brain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Micro-', '', 'Alt. of Micr-');
  INSERT INTO words (word, word_type, description) VALUES  (    'Micr-', '', 'A combining form');
  INSERT INTO words (word, word_type, description) VALUES  (    'Micr-', '', 'Small, little, trivial, slight; as, microcosm, microscope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Micr-', '', 'A millionth part of; as, microfarad, microohm, micrometer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Microampere', 'n.', 'One of the smaller measures of electrical currents;\n   the millionth part of one ampere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Microbacteria', 'n. pl.', 'In the classification of Cohn, one of the four\n   tribes of Bacteria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Microbe', 'n.', 'Alt. of Microbion');
  INSERT INTO words (word, word_type, description) VALUES  (    'Microbion', 'n.', 'A microscopic organism; -- particularly applied to\n   bacteria and especially to pathogenic forms; as, the microbe of fowl\n   cholera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Microbian', 'a.', 'Of, pertaining to, or caused by, microbes; as, the\n   microbian theory; a microbian disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Microbic', 'a.', 'Of or pertaining to a microbe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Microbicide', 'n.', 'Any agent detrimental to, or destructive of, the life\n   of microbes or bacterial organisms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Microcephalic', 'a.', 'Alt. of Microcephalous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Microcephalous', 'a.', 'Having a small head; having the cranial cavity\n   small; -- opposed to megacephalic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Micro-chemical', 'a.', 'Of or pertaining to micro-chemistry; as, a\n   micro-chemical test.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Micro-chemistry', 'n.', 'The application of chemical tests to minute\n   objects or portions of matter, magnified by the use of the microscopy;\n   -- distinguished from macro-chemistry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Microchronometer', 'n.', 'A chronoscope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Microcline', 'n.', 'A mineral of the feldspar group, like orthoclase or\n   common feldspar in composition, but triclinic in form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Micrococcal', 'a.', 'Of or pertaining to micrococci; caused by micrococci.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Micrococci', 'pl. ', 'of Micrococcus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Micrococcus', 'n.', 'A genus of Spherobacteria, in the form of very small\n   globular or oval cells, forming, by transverse division, filaments, or\n   chains of cells, or in some cases single organisms shaped like\n   dumb-bells (Diplococcus), all without the power of motion. See Illust.\n   of Ascoccus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Microcosm', 'n.', 'A little world; a miniature universe. Hence (so called\n   by Paracelsus), a man, as a supposed epitome of the exterior universe\n   or great world. Opposed to macrocosm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Microcosmic', 'a.', 'Alt. of Microcosmical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Microcosmical', 'a.', 'Of or pertaining to the microcosm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Microcosmography', 'n.', 'Description of man as a microcosm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Microcoulomb', 'n.', 'A measure of electrical quantity; the millionth part\n   of one coulomb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Microcoustic', 'a.', 'Pertaining, or suited, to the audition of small\n   sounds; fitted to assist hearing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Microcoustic', 'n.', 'An instrument for making faint sounds audible, as to\n   a partially deaf person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Microcrith', 'n.', 'The weight of the half hydrogen molecule, or of the\n   hydrogen atom, taken as the standard in comparing the atomic weights of\n   the elements; thus, an atom of oxygen weighs sixteen microcriths. See\n   Crith.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Microcrystalline', 'a.', 'Crystalline on a fine, or microscopic, scale;\n   consisting of fine crystals; as, the ground mass of certain porphyrics\n   is microcrystalline.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Microcyte', 'n.', 'One of the elementary granules found in blood. They are\n   much smaller than an ordinary corpuscle, and are particularly\n   noticeable in disease, as in anaemia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Microdont', 'a.', 'Having small teeth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Microfarad', 'n.', 'The millionth part of a farad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Microform', 'n.', 'A microscopic form of life; an animal or vegetable\n   organism microscopic size.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Micro-geological', 'a.', 'Of or pertaining to micro-geology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Micro-geology', 'n.', 'The part of geology relating to structure and\n   organisms which require to be studied with a microscope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Micrograph', 'n.', 'An instrument for executing minute writing or\n   engraving.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Micrographic', 'a.', 'Of or pertaining to micrography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Micrography', 'n.', 'The description of microscopic objects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Microhm', 'n.', 'The millionth part of an ohm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Microlepidoptera', 'n. pl.', 'A tribe of Lepidoptera, including a vast\n   number of minute species, as the plume moth, clothes moth, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Microlestes', 'n.', 'An extinct genus of small Triassic mammals, the oldest\n   yet found in European strata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Microlite', 'n.', 'A rare mineral of resinous luster and high specific\n   gravity. It is a tantalate of calcium, and occurs in octahedral\n   crystals usually very minute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Microlite', 'n.', 'A minute inclosed crystal, often observed when minerals\n   or rocks are examined in thin sections under the microscope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Microlith', 'n.', 'Same as Microlite, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Microlithic', 'a.', 'Formed of small stones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Micrologic', 'a.', 'Alt. of Micrological');
  INSERT INTO words (word, word_type, description) VALUES  (    'Micrological', 'a.', 'Of or pertaining to micrology; very minute; as,\n   micrologic examination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Micrology', 'n.', 'That part of science which treats of microscopic\n   objects, or depends on microscopic observation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Micrology', 'n.', 'Attention to petty items or differences.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Micromere', 'n.', 'One of the smaller cells, or blastomeres, resulting from\n   the complete segmentation of a telolecithal ovum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Micrometer', 'n.', 'An instrument, used with a telescope or microscope, for\n   measuring minute distances, or the apparent diameters of objects which\n   subtend minute angles. The measurement given directly is that of the\n   image of the object formed at the focus of the object glass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Micrometric', 'a.', 'Alt. of Micrometrical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Micrometrical', 'a.', 'Belonging to micrometry; made by the micrometer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Micrometry', 'n.', 'The art of measuring with a micrometer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Micromillimeter', 'n.', 'The millionth part of a meter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Micron', 'n.', 'A measure of length; the thousandth part of one millimeter;\n   the millionth part of a meter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Micronesian', 'a.', 'Of or pertaining to Micronesia, a collective\n   designation of the islands in the western part of the Pacific Ocean,\n   embracing the Marshall and Gilbert groups, the Ladrones, the Carolines,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Micronesians', 'n. pl.', 'A dark race inhabiting the Micronesian Islands.\n   They are supposed to be a mixed race, derived from Polynesians and\n   Papuans.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Micronometer', 'n.', 'An instrument for noting minute portions of time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Microorganism', 'n.', 'Any microscopic form of life; -- particularly\n   applied to bacteria and similar organisms, esp. such are supposed to\n   cause infectious diseases.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Micropantograph', 'n.', 'A kind of pantograph which produces copies\n   microscopically minute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Micropegmatite', 'n.', 'A rock showing under the microscope the structure\n   of a graphic granite (pegmatite).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Microphone', 'n.', 'An instrument for intensifying and making audible very\n   feeble sounds. It produces its effects by the changes of intensity in\n   an electric current, occasioned by the variations in the contact\n   resistance of conducting bodies, especially of imperfect conductors,\n   under the action of acoustic vibrations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Microphonics', 'n.', 'The science which treats of the means of increasing\n   the intensity of low or weak sounds, or of the microphone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Microphonous', 'a.', 'Serving to augment the intensity of weak sounds;\n   microcoustic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Microphotograph', 'n.', 'A microscopically small photograph of a picture,\n   writing, printed page, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Microphotograph', 'n.', 'An enlarged representation of a microscopic\n   object, produced by throwing upon a sensitive plate the magnified image\n   of an object formed by a microscope or other suitable combination of\n   lenses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Microphotography', 'n.', 'The art of making microphotographs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Microphthalmia', 'n.', 'Alt. of Microphthalmy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Microphthalmy', 'n.', 'An unnatural smallness of the eyes, occurring as the\n   result of disease or of imperfect development.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Microphyllous', 'a.', 'Small-leaved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Microphytal', 'a.', 'Pertaining to, or of the nature of, microphytes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Microphyte', 'n.', 'A very minute plant, one of certain unicellular algae,\n   such as the germs of various infectious diseases are believed to be.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Micropyle', 'n.', 'An opening in the membranes surrounding the ovum, by\n   which nutrition is assisted and the entrance of the spermatozoa\n   permitted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Micropyle', 'n.', 'An opening in the outer coat of a seed, through which\n   the fecundating pollen enters the ovule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Microscopal', 'a.', 'Pertaining to microscopy, or to the use of the\n   microscope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Microscope', 'n.', 'An optical instrument, consisting of a lens, or\n   combination of lenses, for making an enlarged image of an object which\n   is too minute to be viewed by the naked eye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Microscopial', 'a.', 'Microscopic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Microscopic', 'a.', 'Alt. of Microscopical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Microscopical', 'a.', 'Of or pertaining to the microscope or to microscopy;\n   made with a microscope; as, microscopic observation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Microscopical', 'a.', 'Able to see extremely minute objects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Microscopical', 'a.', 'Very small; visible only by the aid of a microscope;\n   as, a microscopic insect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Microscopically', 'adv.', 'By the microscope; with minute inspection; in a\n   microscopic manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Microscopist', 'n.', 'One skilled in, or given to, microscopy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Microscopy', 'n.', 'The use of the microscope; investigation with the\n   microscope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Microseme', 'a.', 'Having the orbital index relatively small; having the\n   orbits broad transversely; -- opposed to megaseme.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Microspectroscope', 'n.', 'A spectroscope arranged for attachment to a\n   microscope, for observation of the spectrum of light from minute\n   portions of any substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Microsporangium', 'n.', 'A sporangium or conceptacle containing only very\n   minute spores. Cf. Macrosporangium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Microspore', 'n.', 'One of the exceedingly minute spores found in certain\n   flowerless plants, as Selaginella and Isoetes, which bear two kinds of\n   spores, one very much smaller than the other. Cf. Macrospore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Microsporic', 'a.', 'Of or pertaining to microspores.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Microsthene', 'n.', 'One of a group of mammals having a small size as a\n   typical characteristic. It includes the lower orders, as the\n   Insectivora, Cheiroptera, Rodentia, and Edentata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Microsthenic', 'a.', 'Having a typically small size; of or pertaining to\n   the microsthenes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Microtasimeter', 'n.', 'A tasimeter, especially when arranged for measuring\n   very small extensions. See Tasimeter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Microtome', 'n.', 'An instrument for making very thin sections for\n   microscopical examination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Microtomist', 'n.', 'One who is skilled in or practices microtomy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Microtomy', 'n.', 'The art of using the microtome; investigation carried on\n   with the microtome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Microvolt', 'n.', 'A measure of electro-motive force; the millionth part of\n   one volt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Microweber', 'n.', 'The millionth part of one weber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Microzoa', 'n. pl.', 'The Infusoria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Microzoospore', 'n.', 'A small motile spore furnished with two vibratile\n   cilia, found in certain green algae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Microzyme', 'n.', 'A microorganism which is supposed to act like a ferment\n   in causing or propagating certain infectious or contagious diseases; a\n   pathogenic bacterial organism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Micturition', 'n.', 'The act of voiding urine; also, a morbidly frequent\n   passing of the urine, in consequence of disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mid', 'superl.', 'Denoting the middle part; as, in mid ocean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mid', 'superl.', 'Occupying a middle position; middle; as, the mid finger;\n   the mid hour of night.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mid', 'superl.', 'Made with a somewhat elevated position of some certain\n   part of the tongue, in relation to the palate; midway between the high\n   and the low; -- said of certain vowel sounds; as, a (ale), / (/ll), /\n   (/ld). See Guide to Pronunciation, // 10, 11.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mid', 'n.', 'Middle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mid', 'prep.', 'See Amid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mida', 'n.', 'The larva of the bean fly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Midas', 'n.', 'A genus of longeared South American monkeys, including\n   numerous species of marmosets. See Marmoset.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Midass ear', '', 'A pulmonate mollusk (Auricula, / Ellobium, aurismidae);\n   -- so called from resemblance to a human ear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Midbrain', 'n.', 'The middle segment of the brain; the mesencephalon. See\n   Brain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Midday', 'a.', 'The middle part of the day; noon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Midday', 'a.', 'Of or pertaining to noon; meridional; as, the midday sun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Midden', 'n.', 'A dunghill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Midden', 'n.', 'An accumulation of refuse about a dwelling place;\n   especially, an accumulation of shells or of cinders, bones, and other\n   refuse on the supposed site of the dwelling places of prehistoric\n   tribes, -- as on the shores of the Baltic Sea and in many other places.\n   See Kitchen middens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Midden crow', '', 'The common European crow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Middest', 'superl.', 'Situated most nearly in the middle; middlemost;\n   midmost.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Middest', 'n.', 'Midst; middle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Midding', 'n.', 'Same as Midden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Middle', 'a.', 'Equally distant from the extreme either of a number of\n   things or of one thing; mean; medial; as, the middle house in a row; a\n   middle rank or station in life; flowers of middle summer; men of middle\n   age.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Middle', 'a.', 'Intermediate; intervening.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Middle', 'a.', 'The point or part equally distant from the extremities or\n   exterior limits, as of a line, a surface, or a solid; an intervening\n   point or part in space, time, or order of series; the midst; central\n   portion');
  INSERT INTO words (word, word_type, description) VALUES  (    'Middle', 'a.', 'the waist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Middle-age', '', 'Of or pertaining to the Middle Ages; mediaeval.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Middle-aged', 'a.', 'Being about the middle of the ordinary age of man;\n   between 30 and 50 years old.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Middle-earth', 'n.', 'The world, considered as lying between heaven and\n   hell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Middle-ground', 'n.', 'That part of a picture between the foreground and\n   the background.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Middlemen', 'pl. ', 'of Middleman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Middleman', 'n.', 'An agent between two parties; a broker; a go-between;\n   any dealer between the producer and the consumer; in Ireland, one who\n   takes land of the proprietors in large tracts, and then rents it out in\n   small portions to the peasantry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Middleman', 'n.', 'A person of intermediate rank; a commoner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Middleman', 'n.', 'The man who occupies a central position in a file of\n   soldiers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Middlemost', 'a.', 'Being in the middle, or nearest the middle; midmost.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Middler', 'n.', 'One of a middle or intermediate class in some schools and\n   seminaries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Middling', 'a.', 'Of middle rank, state, size, or quality; about equally\n   distant from the extremes; medium; moderate; mediocre; ordinary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Middlings', 'n. pl.', 'A combination of the coarser parts of ground wheat\n   the finest bran, separated from the fine flour and coarse bran in\n   bolting; -- formerly regarded as valuable only for feed; but now, after\n   separation of the bran, used for making the best quality of flour.\n   Middlings contain a large proportion of gluten.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Middlings', 'n. pl.', 'In the southern and western parts of the United\n   States, the portion of the hog between the ham and the shoulder; bacon;\n   -- called also middles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Middies', 'pl. ', 'of Middy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Middy', 'n.', 'A colloquial abbreviation of midshipman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Midfeather', 'n.', 'A vertical water space in a fire box or combustion\n   chamber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Midfeather', 'n.', 'A support for the center of a tunnel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Midgard', 'n.', 'The middle space or region between heaven and hell; the\n   abode of human beings; the earth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Midge', 'n.', 'Any one of many small, delicate, long-legged flies of the\n   Chironomus, and allied genera, which do not bite. Their larvae are\n   usually aquatic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Midge', 'n.', 'A very small fly, abundant in many parts of the United\n   States and Canada, noted for the irritating quality of its bite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Midget', 'n.', 'A minute bloodsucking fly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Midget', 'n.', 'A very diminutive person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Midgut', 'n.', 'The middle part of the alimentary canal from the stomach,\n   or entrance of the bile duct, to, or including, the large intestine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Midheaven', 'n.', 'The midst or middle of heaven or the sky.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Midheaven', 'n.', 'The meridian, or middle line of the heavens; the point\n   of the ecliptic on the meridian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Midland', 'a.', 'Being in the interior country; distant from the coast or\n   seashore; as, midland towns or inhabitants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Midland', 'a.', 'Surrounded by the land; mediterranean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Midland', 'n.', 'The interior or central region of a country; -- usually in\n   the plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Midmain', 'n.', 'The middle part of the main or sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Midmost', 'a.', 'Middle; middlemost.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Midnight', 'n.', 'The middle of the night; twelve oclock at night.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Midnight', 'a.', 'Being in, or characteristic of, the middle of the night;\n   as, midnight studies; midnight gloom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Midrashim', 'pl. ', 'of Midrash');
  INSERT INTO words (word, word_type, description) VALUES  (    'Midrashoth', 'pl. ', 'of Midrash');
  INSERT INTO words (word, word_type, description) VALUES  (    'Midrash', 'n.', 'A talmudic exposition of the Hebrew law, or of some part\n   of it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Midrib', 'n.', 'A continuation of the petiole, extending from the base to\n   the apex of the lamina of a leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Midriff', 'n.', 'See Diaphragm, n., 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mid sea', '', 'Alt. of Mid-sea');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mid-sea', '', 'The middle part of the sea or ocean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Midship', 'a.', 'Of or pertaining to, or being in, the middle of a ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Midshipmen', 'pl. ', 'of Midshipman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Midshipman', 'n.', 'Formerly, a kind of naval cadet, in a ship of war,\n   whose business was to carry orders, messages, reports, etc., between\n   the officers of the quarter-deck and those of the forecastle, and\n   render other services as required.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Midshipman', 'n.', 'In the English naval service, the second rank attained\n   by a combatant officer after a term of service as naval cadet. Having\n   served three and a half years in this rank, and passed an examination,\n   he is eligible to promotion to the rank of lieutenant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Midshipman', 'n.', 'In the United States navy, the lowest grade of officers\n   in line of promotion, being graduates of the Naval Academy awaiting\n   promotion to the rank of ensign.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Midshipman', 'n.', 'An American marine fish of the genus Porichthys, allied\n   to the toadfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Midships', 'adv.', 'In the middle of a ship; -- properly amidships.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Midships', 'n. pl.', 'The timbers at the broadest part of the vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Midst', 'n.', 'The interior or central part or place; the middle; -- used\n   chiefly in the objective case after in; as, in the midst of the forest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Midst', 'n.', 'Hence, figuratively, the condition of being surrounded or\n   beset; the press; the burden; as, in the midst of official duties; in\n   the midst of secular affairs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Midst', 'prep.', 'In the midst of; amidst.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Midst', 'adv.', 'In the middle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Midsummer', 'n.', 'The middle of summer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Midward', 'a.', 'Situated in the middle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Midward', 'adv.', 'In or toward the midst.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Midway', 'n.', 'The middle of the way or distance; a middle way or course.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Midway', 'a.', 'Being in the middle of the way or distance; as, the midway\n   air.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Midway', 'adv.', 'In the middle of the way or distance; half way.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Midweek', 'n.', 'The middle of the week. Also used adjectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Midwives', 'pl. ', 'of Midwife');
  INSERT INTO words (word, word_type, description) VALUES  (    'Midwife', 'n.', 'A woman who assists other women in childbirth; a female\n   practitioner of the obstetric art.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Midwife', 'v. t.', 'To assist in childbirth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Midwife', 'v. i.', 'To perform the office of midwife.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Midwifery', 'n.', 'The art or practice of assisting women in childbirth;\n   obstetrics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Midwifery', 'n.', 'Assistance at childbirth; help or cooperation in\n   production.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Midwinter', 'n.', 'The middle of winter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Midwive', 'v. t.', 'To midwife.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mien', 'n.', 'Aspect; air; manner; demeanor; carriage; bearing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Miff', 'n.', 'A petty falling out; a tiff; a quarrel; offense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Miff', 'v. t.', 'To offend slightly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Might', '', 'imp. of May.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Might', 'v.', 'Force or power of any kind, whether of body or mind; energy\n   or intensity of purpose, feeling, or action; means or resources to\n   effect an object; strength; force; power; ability; capacity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mightful', 'a.', 'Mighty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mightily', 'adv.', 'In a mighty manner; with might; with great earnestness;\n   vigorously; powerfully.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mightily', 'adv.', 'To a great degree; very much.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mightiness', 'n.', 'The quality of being mighty; possession of might;\n   power; greatness; high dignity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mightiness', 'n.', 'Highness; excellency; -- with a possessive pronoun, a\n   title of dignity; as, their high mightinesses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mightless', 'a.', 'Without; weak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mighty', 'n.', 'Possessing might; having great power or authority.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mighty', 'n.', 'Accomplished by might; hence, extraordinary; wonderful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mighty', 'n.', 'Denoting and extraordinary degree or quality in respect of\n   size, character, importance, consequences, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mighties', 'pl. ', 'of Mighty');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mighty', 'n.', 'A warrior of great force and courage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mighty', 'adv.', 'In a great degree; very.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Migniard', 'a.', 'Soft; dainty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Migniardise', 'n.', 'Delicate fondling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mignon', 'a.', 'See 3d Minion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mignon', 'v. t.', 'To flatter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mignonette', 'n.', 'A plant (Reseda odorata) having greenish flowers with\n   orange-colored stamens, and exhaling a delicious fragrance. In Africa\n   it is a low shrub, but further north it is usually an annual herb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Migraine', 'n.', 'Same as Megrim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Migrant', 'a.', 'Migratory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Migrant', 'n.', 'A migratory bird or other animal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Migrated', 'imp. & p. p.', 'of Migrate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Migrating', 'p. pr. & vb. n.', 'of Migrate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Migrate', 'v. i.', 'To remove from one country or region to another, with a\n   view to residence; to change ones place of residence; to remove; as,\n   the Moors who migrated from Africa into Spain; to migrate to the West.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Migrate', 'v. i.', 'To pass periodically from one region or climate to\n   another for feeding or breeding; -- said of certain birds, fishes, and\n   quadrupeds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Migration', 'n.', 'The act of migrating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Migratory', 'a.', 'Removing regularly or occasionally from one region or\n   climate to another; as, migratory birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Migratory', 'a.', 'Hence, roving; wandering; nomad; as, migratory habits; a\n   migratory life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mikado', 'n.', 'The popular designation of the hereditary sovereign of\n   Japan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mikmaks', 'n.', 'Same as Micmacs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Milage', 'n.', 'Same as Mileage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Milanese', 'a.', 'Of or pertaining to Milan in Italy, or to its\n   inhabitants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Milanese', 'n. sing. & pl.', 'A native or inhabitant of Milan; people of\n   Milan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Milch', 'a.', 'Giving milk; -- now applied only to beasts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Milch', 'a.', 'Tender; pitiful; weeping.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mild', 'superl.', 'Gentle; pleasant; kind; soft; bland; clement; hence,\n   moderate in degree or quality; -- the opposite of harsh, severe,\n   irritating, violent, disagreeable, etc.; -- applied to persons and\n   things; as, a mild disposition; a mild eye; a mild air; a mild\n   medicine; a mild insanity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Milden', 'v. t.', 'To make mild, or milder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mildew', 'n.', 'A growth of minute powdery or webby fungi, whitish or of\n   different colors, found on various diseased or decaying substances.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mildewed', 'imp. & p. p.', 'of Mildew');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mildewing', 'p. pr. & vb. n.', 'of Mildew');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mildew', 'v. t.', 'To taint with mildew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mildew', 'v. i.', 'To become tainted with mildew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mildly', 'adv.', 'In a mild manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mildness', 'n.', 'The quality or state of being mild; as, mildness of\n   temper; the mildness of the winter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mile', 'n.', 'A certain measure of distance, being equivalent in England\n   and the United States to 320 poles or rods, or 5,280 feet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mileage', 'n.', 'An allowance for traveling expenses at a certain rate per\n   mile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mileage', 'n.', 'Aggregate length or distance in miles; esp., the sum of\n   lengths of tracks or wires of a railroad company, telegraph company,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Milepost', 'n.', 'A post, or one of a series of posts, set up to indicate\n   spaces of a mile each or the distance in miles from a given place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Milesian', 'a.', 'Of or pertaining to Miletus, a city of Asia Minor, or to\n   its inhabitants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Milesian', 'a.', 'Descended from King Milesius of Spain, whose two sons are\n   said to have conquered Ireland about 1300 b. c.; or pertaining to the\n   descendants of King Milesius; hence, Irish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Milesian', 'n.', 'A native or inhabitant of Miletus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Milesian', 'n.', 'A native or inhabitant of Ireland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Milestone', 'n.', 'A stone serving the same purpose as a milepost.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Milfoil', 'n.', 'A common composite herb (Achillea Millefolium) with white\n   flowers and finely dissected leaves; yarrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Miliaria', 'n.', 'A fever accompanied by an eruption of small, isolated,\n   red pimples, resembling a millet seed in form or size; miliary fever.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Miliary', 'a.', 'Like millet seeds; as, a miliary eruption.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Miliary', 'a.', 'Accompanied with an eruption like millet seeds; as, a\n   miliary fever.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Miliary', 'a.', 'Small and numerous; as, the miliary tubercles of Echini.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Miliary', 'n.', 'One of the small tubercles of Echini.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Milice', 'n.', 'Militia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Miliola', 'n.', 'A genus of Foraminifera, having a porcelanous shell with\n   several longitudinal chambers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Miliolite', 'n.', 'A fossil shell of, or similar to, the genus Miliola.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Miliolite', 'a.', 'The same Milliolitic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Miliolitic', 'a.', 'Of or pertaining to the genus Miliola; containing\n   miliolites.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Militancy', 'n.', 'The state of being militant; warfare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Militancy', 'n.', 'A military spirit or system; militarism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Militant', 'a.', 'Engaged in warfare; fighting; combating; serving as a\n   soldier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Militar', 'a.', 'Military.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Militarily', 'adv.', 'In a military manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Militarism', 'n.', 'A military state or condition; reliance on military\n   force in administering government; a military system.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Militarism', 'n.', 'The spirit and traditions of military life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Militarist', 'n.', 'A military man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Military', 'a.', 'Of or pertaining to soldiers, to arms, or to war;\n   belonging to, engaged in, or appropriate to, the affairs of war; as, a\n   military parade; military discipline; military bravery; military\n   conduct; military renown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Military', 'a.', 'Performed or made by soldiers; as, a military election; a\n   military expedition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Military', 'n.', 'The whole body of soldiers; soldiery; militia; troops;\n   the army.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Militated', 'imp. & p. p.', 'of Militate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Militating', 'p. pr. & vb. n.', 'of Militate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Militate', 'v. i.', 'To make war; to fight; to contend; -- usually followed\n   by against and with.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Militia', 'n.', 'In the widest sense, the whole military force of a nation,\n   including both those engaged in military service as a business, and\n   those competent and available for such service; specifically, the body\n   of citizens enrolled for military instruction and discipline, but not\n   subject to be called into actual service except in emergencies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Militia', 'n.', 'Military service; warfare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Militiamen', 'pl. ', 'of Militiaman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Militiaman', 'n.', 'One who belongs to the militia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Militiate', 'v. i.', 'To carry on, or prepare for, war.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Milk', 'n.', 'A white fluid secreted by the mammary glands of female\n   mammals for the nourishment of their young, consisting of minute\n   globules of fat suspended in a solution of casein, albumin, milk sugar,\n   and inorganic salts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Milk', 'n.', 'A kind of juice or sap, usually white in color, found in\n   certain plants; latex. See Latex.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Milk', 'n.', 'An emulsion made by bruising seeds; as, the milk of almonds,\n   produced by pounding almonds with sugar and water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Milk', 'n.', 'The ripe, undischarged spat of an oyster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Milked', 'imp. & p. p.', 'of Milk');
  INSERT INTO words (word, word_type, description) VALUES  (    'Milking', 'p. pr. & vb. n.', 'of Milk');
  INSERT INTO words (word, word_type, description) VALUES  (    'Milk', 'v. t.', 'To draw or press milk from the breasts or udder of, by the\n   hand or mouth; to withdraw the milk of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Milk', 'v. t.', 'To draw from the breasts or udder; to extract, as milk;\n   as, to milk wholesome milk from healthy cows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Milk', 'v. t.', 'To draw anything from, as if by milking; to compel to\n   yield profit or advantage; to plunder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Milk', 'v. i.', 'To draw or to yield milk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Milken', 'a.', 'Consisting of milk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Milker', 'n.', 'One who milks; also, a mechanical apparatus for milking\n   cows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Milker', 'n.', 'A cow or other animal that gives milk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Milkful', 'a.', 'Full of milk; abounding with food.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Milkily', 'adv.', 'In a milky manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Milkiness', 'n.', 'State or quality of being milky.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Milk-livered', 'a.', 'White-livered; cowardly; timorous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Milkmaid', 'n.', 'A woman who milks cows or is employed in the dairy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Milkmen', 'pl. ', 'of Milkman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Milkman', 'n.', 'A man who sells milk or delivers is to customers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Milksop', 'n.', 'A piece of bread sopped in milk; figuratively, an\n   effeminate or weak-minded person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Milk vetch', '', 'A leguminous herb (Astragalus glycyphyllos) of Europe and\n   Asia, supposed to increase the secretion of milk in goats.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Milkweed', 'n.', 'Any plant of the genera Asclepias and Acerates, abounding\n   in a milky juice, and having its seed attached to a long silky down;\n   silkweed. The name is also applied to several other plants with a milky\n   juice, as to several kinds of spurge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Milkwort', 'n.', 'A genus of plants (Polygala) of many species. The common\n   European P. vulgaris was supposed to have the power of producing a flow\n   of milk in nurses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Milky', 'a.', 'Consisting of, or containing, milk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Milky', 'a.', 'Like, or somewhat like, milk; whitish and turbid; as, the\n   water is milky. \"Milky juice.\"');
  INSERT INTO words (word, word_type, description) VALUES  (    'Milky', 'a.', 'Yielding milk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Milky', 'a.', 'Mild; tame; spiritless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mill', 'n.', 'A money of account of the United States, having the value of\n   the tenth of a cent, or the thousandth of a dollar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mill', 'n.', 'A machine for grinding or comminuting any substance, as\n   grain, by rubbing and crushing it between two hard, rough, or intented\n   surfaces; as, a gristmill, a coffee mill; a bone mill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mill', 'n.', 'A machine used for expelling the juice, sap, etc., from\n   vegetable tissues by pressure, or by pressure in combination with a\n   grinding, or cutting process; as, a cider mill; a cane mill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mill', 'n.', 'A machine for grinding and polishing; as, a lapidary mill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mill', 'n.', 'A common name for various machines which produce a\n   manufactured product, or change the form of a raw material by the\n   continuous repetition of some simple action; as, a sawmill; a stamping\n   mill, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mill', 'n.', 'A building or collection of buildings with machinery by which\n   the processes of manufacturing are carried on; as, a cotton mill; a\n   powder mill; a rolling mill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mill', 'n.', 'A hardened steel roller having a design in relief, used for\n   imprinting a reversed copy of the design in a softer metal, as copper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mill', 'n.', 'An excavation in rock, transverse to the workings, from which\n   material for filling is obtained.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mill', 'n.', 'A passage underground through which ore is shot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mill', 'n.', 'A milling cutter. See Illust. under Milling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mill', 'n.', 'A pugilistic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Milled', 'imp. & p. p.', 'of Mill');
  INSERT INTO words (word, word_type, description) VALUES  (    'Milling', 'p. pr. & vb. n.', 'of Mill');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mill', 'n.', 'To reduce to fine particles, or to small pieces, in a mill;\n   to grind; to comminute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mill', 'n.', 'To shape, finish, or transform by passing through a machine;\n   specifically, to shape or dress, as metal, by means of a rotary cutter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mill', 'n.', 'To make a raised border around the edges of, or to cut fine\n   grooves or indentations across the edges of, as of a coin, or a screw\n   head; also, to stamp in a coining press; to coin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mill', 'n.', 'To pass through a fulling mill; to full, as cloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mill', 'n.', 'To beat with the fists.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mill', 'n.', 'To roll into bars, as steel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mill', 'v. i.', 'To swim under water; -- said of air-breathing creatures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Millboard', 'n.', 'A kind of stout pasteboard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mill-cake', 'n.', 'The incorporated materials for gunpowder, in the form of\n   a dense mass or cake, ready to be subjected to the process of\n   granulation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Milldam', 'n.', 'A dam or mound to obstruct a water course, and raise the\n   water to a height sufficient to turn a mill wheel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Milled', 'a.', 'Having been subjected to some process of milling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Millefiore glass', '', 'Slender rods or tubes of colored glass fused\n   together and embedded in clear glass; -- used for paperweights and\n   other small articles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Millenarian', 'a.', 'Consisting of a thousand years; of or pertaining to\n   the millennium, or to the Millenarians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Millenarian', 'n.', 'One who believes that Christ will personally reign on\n   earth a thousand years; a Chiliast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Millenarianism', 'n.', 'Alt. of Millenarism');
  INSERT INTO words (word, word_type, description) VALUES  (    'Millenarism', 'n.', 'The doctrine of Millenarians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Millenary', 'a.', 'Consisting of a thousand; millennial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Millenary', 'n.', 'The space of a thousand years; a millennium; also, a\n   Millenarian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Millennial', 'a.', 'Of or pertaining to the millennium, or to a thousand\n   years; as, a millennial period; millennial happiness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Millennialist', 'n.', 'One who believes that Christ will reign personally\n   on earth a thousand years; a Chiliast; also, a believer in the\n   universal prevalence of Christianity for a long period.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Millennialism', 'n.', 'Alt. of Millenniarism');
  INSERT INTO words (word, word_type, description) VALUES  (    'Millenniarism', 'n.', 'Belief in, or expectation of, the millennium;\n   millenarianism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Millennist', 'n.', 'One who believes in the millennium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Millennium', 'n.', 'A thousand years; especially, the thousand years\n   mentioned in the twentieth chapter in the twentieth chapter of\n   Revelation, during which holiness is to be triumphant throughout the\n   world. Some believe that, during this period, Christ will reign on\n   earth in person with his saints.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Milleped', 'n.', 'A myriapod with many legs, esp. a chilognath, as the\n   galleyworm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Millepora', 'n.', 'A genus of Hydrocorallia, which includes the millipores.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Millepore', 'n.', 'Any coral of the genus Millepora, having the surface\n   nearly smooth, and perforated with very minute unequal pores, or cells.\n   The animals are hydroids, not Anthozoa. See Hydrocorallia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Milleporite', 'n.', 'A fossil millepore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Miller', 'n.', 'One who keeps or attends a flour mill or gristmill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Miller', 'n.', 'A milling machine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Miller', 'n.', 'A moth or lepidopterous insect; -- so called because the\n   wings appear as if covered with white dust or powder, like a millers\n   clothes. Called also moth miller.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Miller', 'n.', 'The eagle ray.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Miller', 'n.', 'The hen harrier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Millerite', 'n.', 'A believer in the doctrine of William Miller (d. 1849),\n   who taught that the end of the world and the second coming of Christ\n   were at hand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Millerite', 'n.', 'A sulphide of nickel, commonly occurring in delicate\n   capillary crystals, also in incrustations of a bronze yellow; --\n   sometimes called hair pyrites.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Millesimal', 'a.', 'Thousandth; consisting of thousandth parts; as,\n   millesimal fractions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Millet', 'n.', 'The name of several cereal and forage grasses which bear an\n   abundance of small roundish grains. The common millets of Germany and\n   Southern Europe are Panicum miliaceum, and Setaria Italica.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Milli-', '', 'A prefix denoting a thousandth part of; as, millimeter,\n   milligram, milliampere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Milliampere', 'n.', 'The thousandth part of one ampere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Milliard', 'n.', 'A thousand millions; -- called also billion. See Billion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Milliary', 'a.', 'Of or pertaining to a mile, or to distance by miles;\n   denoting a mile or miles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Milliaries', 'pl. ', 'of Milliary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Milliary', 'a.', 'A milestone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Millier', 'n.', 'A weight of the metric system, being one million grams; a\n   metric ton.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Millifold', 'a.', 'Thousandfold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Milligram', 'n.', 'Alt. of Milligramme');
  INSERT INTO words (word, word_type, description) VALUES  (    'Milligramme', 'n.', 'A measure of weight, in the metric system, being the\n   thousandth part of a gram, equal to the weight of a cubic millimeter of\n   water, or .01543 of a grain avoirdupois.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Milliliter', 'n.', 'Alt. of Millilitre');
  INSERT INTO words (word, word_type, description) VALUES  (    'Millilitre', 'n.', 'A measure of capacity in the metric system, containing\n   the thousandth part of a liter. It is a cubic centimeter, and is equal\n   to .061 of an English cubic inch, or to .0338 of an American fluid\n   ounce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Millimeter', 'n.', 'Alt. of Millimetre');
  INSERT INTO words (word, word_type, description) VALUES  (    'Millimetre', 'n.', 'A lineal measure in the metric system, containing the\n   thousandth part of a meter; equal to .03937 of an inch. See 3d Meter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Milliner', 'n.', 'Formerly, a man who imported and dealt in small articles\n   of a miscellaneous kind, especially such as please the fancy of women.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Milliner', 'n.', 'A person, usually a woman, who makes, trims, or deals in\n   hats, bonnets, headdresses, etc., for women.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Millinery', 'n.', 'The articles made or sold by milliners, as headdresses,\n   hats or bonnets, laces, ribbons, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Millinery', 'n.', 'The business of work of a milliner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Millinet', 'n.', 'A stiff cotton fabric used by milliners for lining\n   bonnets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Milling', 'n.', 'The act or employment of grinding or passing through a\n   mill; the process of fulling; the process of making a raised or\n   intented edge upon coin, etc.; the process of dressing surfaces of\n   various shapes with rotary cutters. See Mill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Million', 'n.', 'The number of ten hundred thousand, or a thousand\n   thousand, -- written 1,000, 000. See the Note under Hundred.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Million', 'n.', 'A very great number; an indefinitely large number.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Million', 'n.', 'The mass of common people; -- with the article the.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Millionaire', 'n.', 'One whose wealth is counted by millions of francs,\n   dollars, or pounds; a very rich person; a person worth a million or\n   more.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Millionairess', 'n.', 'A woman who is a millionaire, or the wife of a\n   millionaire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Millionary', 'a.', 'Of or pertaining to millions; consisting of millions;\n   as, the millionary chronology of the pundits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Millioned', 'a.', 'Multiplied by millions; innumerable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Millionnaire', 'n.', 'Millionaire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Millionth', 'a.', 'Being the last one of a million of units or objects\n   counted in regular order from the first of a series or succession;\n   being one of a million.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Millionth', 'n.', 'The quotient of a unit divided by one million; one of a\n   million equal parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Milliped', 'n.', 'The same Milleped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Millistere', 'n.', 'A liter, or cubic decimeter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Milliweber', 'n.', 'The thousandth part of one weber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Millrea', 'n.', 'Alt. of Millreis');
  INSERT INTO words (word, word_type, description) VALUES  (    'Millree', 'n.', 'Alt. of Millreis');
  INSERT INTO words (word, word_type, description) VALUES  (    'Millreis', 'n.', 'See Milreis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Millrind', 'n.', 'Alt. of Millrynd');
  INSERT INTO words (word, word_type, description) VALUES  (    'Millrynd', 'n.', 'A figure supposed to represent the iron which holds a\n   millstone by being set into its center.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mill-sixpence', 'n.', 'A milled sixpence; -- the sixpence being one of the\n   first English coins milled (1561).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Millstone', 'n.', 'One of two circular stones used for grinding grain or\n   other substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Millwork', 'n.', 'The shafting, gearing, and other driving machinery of\n   mills.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Millwork', 'n.', 'The business of setting up or of operating mill\n   machinery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Millwright', 'n.', 'A mechanic whose occupation is to build mills, or to\n   set up their machinery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Milreis', 'n.', 'A Portuguese money of account rated in the treasury\n   department of the United States at one dollar and eight cents; also, a\n   Brazilian money of account rated at fifty-four cents and six mills.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Milt', 'n.', 'The spleen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Milt', 'n.', 'The spermatic fluid of fishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Milt', 'n.', 'The testes, or spermaries, of fishes when filled with\n   spermatozoa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Milt', 'v. t.', 'To impregnate (the roe of a fish) with milt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Milter', 'n.', 'A male fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Miltonian', 'a.', 'Miltonic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Miltonic', 'a.', 'Of, pertaining to, or resembling, Milton, or his\n   writings; as, Miltonic prose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Miltwaste', '', 'A small European fern (Asplenium Ceterach) formerly used\n   in medicine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Milvine', 'a.', 'Of or resembling birds of the kite kind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Milvine', 'n.', 'A bird related to the kite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Milvus', 'n.', 'A genus of raptorial birds, including the European kite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mime', 'n.', 'A kind of drama in which real persons and events were\n   generally represented in a ridiculous manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mime', 'n.', 'An actor in such representations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mime', 'v. i.', 'To mimic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mimeograph', 'n.', 'An autographic stencil copying device invented by\n   Edison.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mimesis', 'n.', 'Imitation; mimicry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mimetene', 'n.', 'See Mimetite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mimetic', '', 'Alt. of Mimetical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mimetical', '', 'Apt to imitate; given to mimicry; imitative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mimetical', '', 'Characterized by mimicry; -- applied to animals and\n   plants; as, mimetic species; mimetic organisms. See Mimicry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mimetism', 'n.', 'Same as Mimicry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mimetite', 'n.', 'A mineral occurring in pale yellow or brownish hexagonal\n   crystals. It is an arseniate of lead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mimic', 'a.', 'Alt. of Mimical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mimical', 'a.', 'Imitative; mimetic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mimical', 'a.', 'Consisting of, or formed by, imitation; imitated; as,\n   mimic gestures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mimical', 'a.', 'Imitative; characterized by resemblance to other forms; --\n   applied to crystals which by twinning resemble simple forms of a higher\n   grade of symmetry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mimic', 'n.', 'One who imitates or mimics, especially one who does so for\n   sport; a copyist; a buffoon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mimicked', 'imp. & p. p.', 'of Mimic');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mimicking', 'p. pr. & vb. n.', 'of Mimic');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mimic', 'v. t.', 'To imitate or ape for sport; to ridicule by imitation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mimic', 'v. t.', 'To assume a resemblance to (some other organism of a\n   totally different nature, or some surrounding object), as a means of\n   protection or advantage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mimically', 'adv.', 'In an imitative manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mimicker', 'n.', 'One who mimics; a mimic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mimicker', 'n.', 'An animal which imitates something else, in form or\n   habits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mimicry', 'n.', 'The act or practice of one who mimics; ludicrous imitation\n   for sport or ridicule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mimicry', 'n.', 'Protective resemblance; the resemblance which certain\n   animals and plants exhibit to other animals and plants or to the\n   natural objects among which they live, -- a characteristic which serves\n   as their chief means of protection against enemies; imitation; mimesis;\n   mimetism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mimographer', 'n.', 'A writer of mimes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mimosa', 'n.', 'A genus of leguminous plants, containing many species, and\n   including the sensitive plants (Mimosa sensitiva, and M. pudica).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mimotannic', 'a.', 'Pertaining to, or designating, a variety of tannin or\n   tannic acid found in Acacia, Mimosa, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minae', 'pl. ', 'of Mina');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minas', 'pl. ', 'of Mina');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mina', 'n.', 'An ancient weight or denomination of money, of varying value.\n   The Attic mina was valued at a hundred drachmas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mina', 'n.', 'See Myna.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minable', 'a.', 'Such as can be mined; as, minable earth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minacious', 'a.', 'Threatening; menacing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minacity', 'n.', 'Disposition to threaten.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minaret', 'n.', 'A slender, lofty tower attached to a mosque and surrounded\n   by one or more projecting balconies, from which the summon to prayer is\n   cried by the muezzin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minargent', 'n.', 'An alloy consisting of copper, nickel, tungsten, and\n   aluminium; -- used by jewelers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minatorially', 'adv.', 'Alt. of Minatorily');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minatorily', 'adv.', 'In a minatory manner; with threats.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minatory', 'a.', 'Threatening; menacing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minaul', 'n.', 'Same as Manul.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minced', 'imp. & p. p.', 'of Mince');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minging', 'p. pr. & vb. n.', 'of Mince');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mince', 'v. t.', 'To cut into very small pieces; to chop fine; to hash; as,\n   to mince meat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mince', 'v. t.', 'To suppress or weaken the force of; to extenuate; to\n   palliate; to tell by degrees, instead of directly and frankly; to clip,\n   as words or expressions; to utter half and keep back half of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mince', 'v. t.', 'To affect; to make a parade of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mince', 'v. i.', 'To walk with short steps; to walk in a prim, affected\n   manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mince', 'v. i.', 'To act or talk with affected nicety; to affect delicacy\n   in manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mince', 'n.', 'A short, precise step; an affected manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mince-meat', 'n.', 'Minced meat; meat chopped very fine; a mixture of\n   boiled meat, suet, apples, etc., chopped very fine, to which spices and\n   raisins are added; -- used in making mince pie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mince pie', '', 'A pie made of mince-meat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mincer', 'n.', 'One who minces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mincing', 'a.', 'That minces; characterized by primness or affected nicety.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mincingly', 'adv.', 'In a mincing manner; not fully; with affected nicety.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mind', 'v.', 'The intellectual or rational faculty in man; the\n   understanding; the intellect; the power that conceives, judges, or\n   reasons; also, the entire spiritual nature; the soul; -- often in\n   distinction from the body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mind', 'v.', 'The state, at any given time, of the faculties of thinking,\n   willing, choosing, and the like; psychical activity or state; as: (a)\n   Opinion; judgment; belief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mind', 'v.', 'Choice; inclination; liking; intent; will.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mind', 'v.', 'Courage; spirit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mind', 'v.', 'Memory; remembrance; recollection; as, to have or keep in\n   mind, to call to mind, to put in mind, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minded', 'imp. & p. p.', 'of Mind');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minding', 'p. pr. & vb. n.', 'of Mind');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mind', 'n.', 'To fix the mind or thoughts on; to regard with attention; to\n   treat as of consequence; to consider; to heed; to mark; to note.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mind', 'n.', 'To occupy ones self with; to employ ones self about; to\n   attend to; as, to mind ones business.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mind', 'n.', 'To obey; as, to mind parents; the dog minds his master.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mind', 'n.', 'To have in mind; to purpose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mind', 'n.', 'To put in mind; to remind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mind', 'v. i.', 'To give attention or heed; to obey; as, the dog minds\n   well.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minded', 'a.', 'Disposed; inclined; having a mind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minder', 'n.', 'One who minds, tends, or watches something, as a child, a\n   machine, or cattle; as, a minder of a loom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minder', 'n.', 'One to be attended; specif., a pauper child intrusted to\n   the care of a private person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mindful', 'a.', 'Bearing in mind; regardful; attentive; heedful; observant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minding', 'n.', 'Regard; mindfulness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mindless', 'a.', 'Not indued with mind or intellectual powers; stupid;\n   unthinking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mindless', 'a.', 'Unmindful; inattentive; heedless; careless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mine', 'n.', 'See Mien.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mine', 'pron. & a.', 'Belonging to me; my. Used as a pronominal to me; my.\n   Used as a pronominal adjective in the predicate; as, \"Vengeance is\n   mine; I will repay.\" Rom. xii. 19. Also, in the old style, used\n   attributively, instead of my, before a noun beginning with a vowel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mine', 'v. i.', 'To dig a mine or pit in the earth; to get ore, metals,\n   coal, or precious stones, out of the earth; to dig in the earth for\n   minerals; to dig a passage or cavity under anything in order to\n   overthrow it by explosives or otherwise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mine', 'v. i.', 'To form subterraneous tunnel or hole; to form a burrow or\n   lodge in the earth; as, the mining cony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mined', 'imp. & p. p.', 'of Mine');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mining', 'p. pr. & vb. n.', 'of Mine');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mine', 'v. t.', 'To dig away, or otherwise remove, the substratum or\n   foundation of; to lay a mine under; to sap; to undermine; hence, to\n   ruin or destroy by slow degrees or secret means.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mine', 'v. t.', 'To dig into, for ore or metal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mine', 'v. t.', 'To get, as metals, out of the earth by digging.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mine', 'v. i.', 'A subterranean cavity or passage');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mine', 'v. i.', 'A pit or excavation in the earth, from which metallic\n   ores, precious stones, coal, or other mineral substances are taken by\n   digging; -- distinguished from the pits from which stones for\n   architectural purposes are taken, and which are called quarries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mine', 'v. i.', 'A cavity or tunnel made under a fortification or other\n   work, for the purpose of blowing up the superstructure with some\n   explosive agent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mine', 'v. i.', 'Any place where ore, metals, or precious stones are got by\n   digging or washing the soil; as, a placer mine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mine', 'v. i.', 'Fig.: A rich source of wealth or other good.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Miner', 'n.', 'One who mines; a digger for metals, etc.; one engaged in the\n   business of getting ore, coal, or precious stones, out of the earth;\n   one who digs military mines; as, armies have sappers and miners.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Miner', 'n.', 'Any of numerous insects which, in the larval state, excavate\n   galleries in the parenchyma of leaves. They are mostly minute moths and\n   dipterous flies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Miner', 'n.', 'The chattering, or garrulous, honey eater of Australia\n   (Myzantha garrula).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mineral', 'v. i.', 'An inorganic species or substance occurring in nature,\n   having a definite chemical composition and usually a distinct\n   crystalline form. Rocks, except certain glassy igneous forms, are\n   either simple minerals or aggregates of minerals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mineral', 'v. i.', 'A mine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mineral', 'v. i.', 'Anything which is neither animal nor vegetable, as in\n   the most general classification of things into three kingdoms (animal,\n   vegetable, and mineral).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mineral', 'a.', 'Of or pertaining to minerals; consisting of a mineral or\n   of minerals; as, a mineral substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mineral', 'a.', 'Impregnated with minerals; as, mineral waters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mineralist', 'n.', 'One versed in minerals; mineralogist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mineralization', 'n.', 'The process of mineralizing, or forming a mineral\n   by combination of a metal with another element; also, the process of\n   converting into a mineral, as a bone or a plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mineralization', 'n.', 'The act of impregnating with a mineral, as water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mineralization', 'n.', 'The conversion of a cell wall into a material of a\n   stony nature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mineralized', 'imp. & p. p.', 'of Mineralize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mineralizing', 'p. pr. & vb. n.', 'of Mineralize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mineralize', 'v. t.', 'To transform into a mineral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mineralize', 'v. t.', 'To impregnate with a mineral; as, mineralized water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mineralize', 'v. i.', 'To go on an excursion for observing and collecting\n   minerals; to mineralogize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mineralizer', 'n.', 'An element which is combined with a metal, thus\n   forming an ore. Thus, in galena, or lead ore, sulphur is a mineralizer;\n   in hematite, oxygen is a mineralizer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mineralogical', 'a.', 'Of or pertaining to mineralogy; as, a mineralogical\n   table.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mineralogically', 'adv.', 'According to the principles of, or with\n   reference to, mineralogy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mineralogist', 'n.', 'One versed in mineralogy; one devoted to the study of\n   minerals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mineralogist', 'n.', 'A carrier shell (Phorus).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mineralogize', 'v. i.', 'To study mineralogy by collecting and examining\n   minerals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mineralogies', 'pl. ', 'of Mineralogy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mineralogy', 'n.', 'The science which treats of minerals, and teaches how\n   to describe, distinguish, and classify them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mineralogy', 'n.', 'A treatise or book on this science.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minerva', 'n.', 'The goddess of wisdom, of war, of the arts and sciences,\n   of poetry, and of spinning and weaving; -- identified with the Grecian\n   Pallas Athene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minette', 'n.', 'The smallest of regular sizes of portrait photographs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minever', 'n.', 'Same as Miniver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minge', 'v. t.', 'To mingle; to mix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minge', 'n.', 'A small biting fly; a midge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mingled', 'imp. & p. p.', 'of Mingle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mingling', 'p. pr. & vb. n.', 'of Mingle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mingle', 'v. t.', 'To mix; intermix; to combine or join, as an individual\n   or part, with other parts, but commonly so as to be distinguishable in\n   the product; to confuse; to confound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mingle', 'v. t.', 'To associate or unite in society or by ties of\n   relationship; to cause or allow to intermarry; to intermarry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mingle', 'v. t.', 'To deprive of purity by mixture; to contaminate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mingle', 'v. t.', 'To put together; to join.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mingle', 'v. t.', 'To make or prepare by mixing the ingredients of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mingle', 'v. i.', 'To become mixed or blended.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mingle', 'n.', 'A mixture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mingleable', 'a.', 'That can be mingled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mingledly', 'adv.', 'Confusedly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mingle-mangle', 'v. t.', 'To mix in a disorderly way; to make a mess of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mingle-mangle', 'n.', 'A hotchpotch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minglement', 'n.', 'The act of mingling, or the state of being mixed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mingler', 'n.', 'One who mingles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minglingly', 'adv.', 'In a mingling manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minaceous', 'a.', 'Of the color of minium or red lead; miniate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Miniard', 'a.', 'Migniard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Miniardize', 'v. t.', 'To render delicate or dainty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Miniated', 'imp. & p. p.', 'of Miniate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Miniating', 'p. pr. & vb. n.', 'of Miniate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Miniate', 'v. t.', 'To paint or tinge with red lead or vermilion; also, to\n   decorate with letters, or the like, painted red, as the page of a\n   manuscript.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Miniate', 'a.', 'Of or pertaining to the color of red lead or vermilion;\n   painted with vermilion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Miniature', 'v.', 'Originally, a painting in colors such as those in\n   mediaeval manuscripts; in modern times, any very small painting,\n   especially a portrait.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Miniature', 'v.', 'Greatly diminished size or form; reduced scale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Miniature', 'v.', 'Lettering in red; rubric distinction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Miniature', 'v.', 'A particular feature or trait.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Miniature', 'a.', 'Being on a small; much reduced from the reality; as, a\n   miniature copy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Miniature', 'v. t.', 'To represent or depict in a small compass, or on a\n   small scale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Miniaturist', 'n.', 'A painter of miniatures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minibus', 'n.', 'A kind of light passenger vehicle, carrying four persons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minie ball', '', 'A conical rifle bullet, with a cavity in its base plugged\n   with a piece of iron, which, by the explosion of the charge, is driven\n   farther in, expanding the sides to fit closely the grooves of the\n   barrel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minie rifle', '', 'A rifle adapted to minie balls.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minified', 'imp. & p. p.', 'of Minify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minifying', 'p. pr. & vb. n.', 'of Minify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minify', 'v. t.', 'To make small, or smaller; to diminish the apparent\n   dimensions of; to lessen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minify', 'v. t.', 'To degrade by speech or action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minikin', 'n.', 'A little darling; a favorite; a minion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minikin', 'n.', 'A little pin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minikin', 'a.', 'Small; diminutive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minim', 'n.', 'Anything very minute; as, the minims of existence; --\n   applied to animalcula; and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minim', 'n.', 'The smallest liquid measure, equal to about one drop; the\n   sixtieth part of a fluid drachm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minim', 'n.', 'A small fish; a minnow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minim', 'n.', 'A little man or being; a dwarf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minim', 'n.', 'One of an austere order of mendicant hermits of friars\n   founded in the 15th century by St. Francis of Paola.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minim', 'n.', 'A time note, formerly the shortest in use; a half note,\n   equal to half a semibreve, or two quarter notes or crotchets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minim', 'n.', 'A short poetical encomium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minim', 'a.', 'Minute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Miniment', 'n.', 'A trifle; a trinket; a token.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minimization', 'n.', 'The act or process of minimizing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minimized', 'imp. & p. p.', 'of Minimize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minimizimg', 'p. pr. & vb. n.', 'of Minimize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minimize', 'v. t.', 'To reduce to the smallest part or proportion possible;\n   to reduce to a minimum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minima', 'pl. ', 'of Minimum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minimum', 'n.', 'The least quantity assignable, admissible, or possible, in\n   a given case; hence, a thing of small consequence; -- opposed to\n   maximum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minimi', 'pl. ', 'of Minimus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minimus', 'n.', 'A being of the smallest size.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minimus', 'n.', 'The little finger; the fifth digit, or that corresponding\n   to it, in either the manus or pes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mining', 'v. i.', 'The act or business of making mines or of working them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mining', 'a.', 'Of or pertaining to mines; as, mining engineer; mining\n   machinery; a mining region.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minion', 'n.', 'Minimum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minion', 'n.', 'A loved one; one highly esteemed and favored; -- in a good\n   sense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minion', 'n.', 'An obsequious or servile dependent or agent of another; a\n   fawning favorite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minion', 'n.', 'A small kind of type, in size between brevier and\n   nonpareil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minion', 'n.', 'An ancient form of ordnance, the caliber of which was about\n   three inches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minion', 'a.', 'Fine; trim; dainty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minionette', 'a.', 'Small; delicate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minionette', 'n.', 'A size of type between nonpareil and minion; -- used in\n   ornamental borders, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minioning', 'n.', 'Kind treatment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minionize', 'v. t.', 'To flavor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minionlike', 'a. & adv.', 'Alt. of Minionly');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minionly', 'a. & adv.', 'Like a minion; daintily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minionship', 'n.', 'State of being a minion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minious', 'a.', 'Of the color of red or vermilion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minish', 'a.', 'To diminish; to lessen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minishment', 'n.', 'The act of diminishing, or the state of being\n   diminished; diminution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minister', 'n.', 'A servant; a subordinate; an officer or assistant of\n   inferior rank; hence, an agent, an instrument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minister', 'n.', 'An officer of justice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minister', 'n.', 'One to whom the sovereign or executive head of a\n   government intrusts the management of affairs of state, or some\n   department of such affairs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minister', 'n.', 'A representative of a government, sent to the court, or\n   seat of government, of a foreign nation to transact diplomatic\n   business.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minister', 'n.', 'One who serves at the altar; one who performs sacerdotal\n   duties; the pastor of a church duly authorized or licensed to preach\n   the gospel and administer the sacraments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ministered', 'imp. & p. p.', 'of Minister');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ministering', 'p. pr. & vb. n.', 'of Minister');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minister', 'n.', 'To furnish or apply; to afford; to supply; to administer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minister', 'v. i.', 'To act as a servant, attendant, or agent; to attend\n   and serve; to perform service in any office, sacred or secular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minister', 'v. i.', 'To supply or to things needful; esp., to supply\n   consolation or remedies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ministerial', 'a.', 'Of or pertaining to ministry or service; serving;\n   attendant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ministerial', 'a.', 'Of or pertaining to the office of a minister or to the\n   ministry as a body, whether civil or sacerdotal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ministerial', 'a.', 'Tending to advance or promote; contributive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ministerialist', 'n.', 'A supporter of the ministers, or the party in\n   power.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ministerially', 'adv.', 'In a ministerial manner; in the character or\n   capacity of a minister.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ministery', 'n.', 'See Ministry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ministracy', 'n.', 'Ministration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ministral', 'a.', 'Ministerial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ministrant', 'a.', 'Performing service as a minister; attendant on service;\n   acting under command; subordinate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ministrant', 'n.', 'One who ministers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ministration', 'n.', 'The act of ministering; service; ministry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ministrative', 'a.', 'Serving to aid; ministering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ministress', 'n.', 'A woman who ministers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ministry', 'n.', 'The act of ministering; ministration; service.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ministry', 'n.', 'Agency; instrumentality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ministry', 'n.', 'The office, duties, or functions of a minister, servant,\n   or agent; ecclesiastical, executive, or ambassadorial function or\n   profession.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ministry', 'n.', 'The body of ministers of state; also, the clergy, as a\n   body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ministry', 'n.', 'Administration; rule; term in power; as, the ministry of\n   Pitt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ministryship', 'n.', 'The office of a minister.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minium', 'n.', 'A heavy, brilliant red pigment, consisting of an oxide of\n   lead, Pb3O4, obtained by exposing lead or massicot to a gentle and\n   continued heat in the air. It is used as a cement, as a paint, and in\n   the manufacture of flint glass. Called also red lead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Miniver', 'n.', 'A fur esteemed in the Middle Ages as a part of costume. It\n   is uncertain whether it was the fur of one animal only or of different\n   animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minivet', 'n.', 'A singing bird of India of the family Campephagidae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mink', 'n.', 'A carnivorous mammal of the genus Putorius, allied to the\n   weasel. The European mink is Putorius lutreola. The common American\n   mink (P. vison) varies from yellowish brown to black. Its fur is highly\n   valued. Called also minx, nurik, and vison.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minnesinger', 'n.', 'A love-singer; specifically, one of a class of German\n   poets and musicians who flourished from about the middle of the twelfth\n   to the middle of the fourteenth century. They were chiefly of noble\n   birth, and made love and beauty the subjects of their verses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minnow', 'n.', 'A small European fresh-water cyprinoid fish (Phoxinus\n   laevis, formerly Leuciscus phoxinus); sometimes applied also to the\n   young of larger kinds; -- called also minim and minny. The name is also\n   applied to several allied American species, of the genera Phoxinus,\n   Notropis, or Minnilus, and Rhinichthys.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minnow', 'n.', 'Any of numerous small American cyprinodont fishes of the\n   genus Fundulus, and related genera. They live both in fresh and in salt\n   water. Called also killifish, minny, and mummichog.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minny', 'n.', 'A minnow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mino bird', '', 'An Asiatic bird (Gracula musica), allied to the starlings.\n   It is black, with a white spot on the wings, and a pair of flat yellow\n   wattles on the head. It is often tamed and taught to pronounce words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minor', 'a.', 'Inferior in bulk, degree, importance, etc.; less; smaller;\n   of little account; as, minor divisions of a body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minor', 'a.', 'Less by a semitone in interval or difference of pitch; as, a\n   minor third.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minor', 'n.', 'A person of either sex who has not attained the age at which\n   full civil rights are accorded; an infant; in England and the United\n   States, one under twenty-one years of age.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minor', 'n.', 'The minor term, that is, the subject of the conclusion;\n   also, the minor premise, that is, that premise which contains the minor\n   term; in hypothetical syllogisms, the categorical premise. It is the\n   second proposition of a regular syllogism, as in the following: Every\n   act of injustice partakes of meanness; to take money from another by\n   gaming is an act of injustice; therefore, the taking of money from\n   another by gaming partakes of meanness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minor', 'n.', 'A Minorite; a Franciscan friar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minorate', 'v. t.', 'To diminish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minoration', 'n.', 'A diminution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minoress', 'n.', 'See Franciscan Nuns, under Franciscan, a.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minorite', 'n.', 'A Franciscan friar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minorities', 'pl. ', 'of Minority');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minority', 'a. & n.', 'The state of being a minor, or under age.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minority', 'a. & n.', 'State of being less or small.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minority', 'a. & n.', 'The smaller number; -- opposed to majority; as, the\n   minority must be ruled by the majority.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minos', 'n.', 'A king and lawgiver of Crete, fabled to be the son of\n   Jupiter and Europa. After death he was made a judge in the Lower\n   Regions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minotaur', 'n.', 'A fabled monster, half man and half bull, confined in the\n   labyrinth constructed by Daedalus in Crete.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minow', 'n.', 'See Minnow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minster', 'n.', 'A church of a monastery. The name is often retained and\n   applied to the church after the monastery has ceased to exist (as\n   Beverly Minster, Southwell Minster, etc.), and is also improperly used\n   for any large church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minstrel', 'n.', 'In the Middle Ages, one of an order of men who subsisted\n   by the arts of poetry and music, and sang verses to the accompaniment\n   of a harp or other instrument; in modern times, a poet; a bard; a\n   singer and harper; a musician.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minstrelsy', 'n.', 'The arts and occupation of minstrels; the singing and\n   playing of a minstrel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minstrelsy', 'n.', 'Musical instruments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minstrelsy', 'n.', 'A collective body of minstrels, or musicians; also, a\n   collective body of minstrels songs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mint', 'n.', 'The name of several aromatic labiate plants, mostly of the\n   genus Mentha, yielding odoriferous essential oils by distillation. See\n   Mentha.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mint', 'n.', 'A place where money is coined by public authority.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mint', 'n.', 'Any place regarded as a source of unlimited supply; the\n   supply itself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minted', 'imp. & p. p.', 'of Mint');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minting', 'p. pr. & vb. n.', 'of Mint');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mint', 'v. t.', 'To make by stamping, as money; to coin; to make and stamp\n   into money.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mint', 'v. t.', 'To invent; to forge; to fabricate; to fashion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mintage', 'n.', 'The coin, or other production, made in a mint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mintage', 'n.', 'The duty paid to the mint for coining.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minter', 'n.', 'One who mints.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mintmen', 'pl. ', 'of Mintman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mintman', 'n.', 'One skilled in coining, or in coins; a coiner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mint-master', 'n.', 'The master or superintendent of a mint. Also used\n   figuratively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minuend', 'n.', 'The number from which another number is to be subtracted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minuet', 'n.', 'A slow graceful dance consisting of a coupee, a high step,\n   and a balance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minuet', 'n.', 'A tune or air to regulate the movements of the dance so\n   called; a movement in suites, sonatas, symphonies, etc., having the\n   dance form, and commonly in 3-4, sometimes 3-8, measure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minum', 'n.', 'A small kind of printing type; minion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minum', 'n.', 'A minim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minus', 'a.', 'Less; requiring to be subtracted; negative; as, a minus\n   quantity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minuscule', 'n.', 'Any very small, minute object.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minuscule', 'n.', 'A small Roman letter which is neither capital nor\n   uncial; a manuscript written in such letters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minuscule', 'a.', 'Of the size and style of minuscules; written in\n   minuscules.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minutary', 'a.', 'Pertaining to, or consisting of, minutes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minute', 'n.', 'The sixtieth part of an hour; sixty seconds. (Abbrev. m.;\n   as, 4 h. 30 m.)');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minute', 'n.', 'The sixtieth part of a degree; sixty seconds (Marked thus\n   (); as, 10 20).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minute', 'n.', 'A nautical or a geographic mile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minute', 'n.', 'A coin; a half farthing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minute', 'n.', 'A very small part of anything, or anything very small; a\n   jot; a tittle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minute', 'n.', 'A point of time; a moment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minute', 'n.', 'The memorandum; a record; a note to preserve the memory of\n   anything; as, to take minutes of a contract; to take minutes of a\n   conversation or debate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minute', 'n.', 'A fixed part of a module. See Module.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minute', 'a.', 'Of or pertaining to a minute or minutes; occurring at or\n   marking successive minutes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minute', 'p. pr. & vb. n.', 'To set down a short sketch or note of; to jot\n   down; to make a minute or a brief summary of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minute', 'a.', 'Very small; little; tiny; fine; slight; slender;\n   inconsiderable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minute', 'a.', 'Attentive to small things; paying attention to details;\n   critical; particular; precise; as, a minute observer; minute\n   observation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minute-jack', 'n.', 'A figure which strikes the hour on the bell of some\n   fanciful clocks; -- called also jack of the clock house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minute-jack', 'n.', 'A timeserver; an inconstant person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minutely', 'adv.', 'In a minute manner; with minuteness; exactly; nicely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minutely', 'a.', 'Happening every minute; continuing; unceasing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minutely', 'adv.', 'At intervals of a minute; very often and regularly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minutemen', 'pl. ', 'of Minuteman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minuteman', 'n.', 'A militiaman who was to be ready to march at a moments\n   notice; -- a term used in the American Revolution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minuteness', 'n.', 'The quality of being minute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minutiae', 'pl. ', 'of Minutia');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minutia', 'n.', 'A minute particular; a small or minor detail; -- used\n   chiefly in the plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minx', 'n.', 'A pert or a wanton girl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minx', 'n.', 'A she puppy; a pet dog.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Minx', 'n.', 'The mink; -- called also minx otter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Miny', 'a.', 'Abounding with mines; like a mine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Miocene', 'a.', 'Of or pertaining to the middle division of the Tertiary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Miocene', 'n.', 'The Miocene period. See Chart of Geology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Miohippus', 'n.', 'An extinct Miocene mammal of the Horse family, closely\n   related to the genus Anhithecrium, and having three usable hoofs on\n   each foot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Miquelet', 'n.', 'An irregular or partisan soldier; a bandit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mir', 'n.', 'A Russian village community.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mir', 'n.', 'Same as Emir.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mira', 'n.', 'A remarkable variable star in the constellation Cetus (/\n   Ceti).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mirabilaries', 'pl. ', 'of Mirabilary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mirabilary', 'n.', 'One who, or a work which, narrates wonderful things;\n   one who writes of wonders.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mirabilis', 'n.', 'A genus of plants. See Four-oclock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mirabilite', 'n.', 'Native sodium sulphate; Glaubers salt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mirable', 'a.', 'Wonderful; admirable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Miracle', 'n.', 'A wonder or wonderful thing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Miracle', 'n.', 'Specifically: An event or effect contrary to the\n   established constitution and course of things, or a deviation from the\n   known laws of nature; a supernatural event, or one transcending the\n   ordinary laws by which the universe is governed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Miracle', 'n.', 'A miracle play.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Miracle', 'n.', 'A story or legend abounding in miracles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Miracle', 'v. t.', 'To make wonderful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Miraculize', 'v. t.', 'To cause to seem to be a miracle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Miraculous', 'a.', 'Of the nature of a miracle; performed by supernatural\n   power; effected by the direct agency of almighty power, and not by\n   natural causes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Miraculous', 'a.', 'Supernatural; wonderful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Miraculous', 'a.', 'Wonder-working.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mirador', 'n.', 'Same as Belvedere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mirage', 'n.', 'An optical effect, sometimes seen on the ocean, but more\n   frequently in deserts, due to total reflection of light at the surface\n   common to two strata of air differently heated. The reflected image is\n   seen, commonly in an inverted position, while the real object may or\n   may not be in sight. When the surface is horizontal, and below the eye,\n   the appearance is that of a sheet of water in which the object is seen\n   reflected; when the reflecting surface is above the eye, the image is\n   seen projected against the sky. The fata Morgana and looming are\n   species of mirage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mirbane', 'n.', 'See Nitrobenzene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mire', 'n.', 'An ant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mire', 'n.', 'Deep mud; wet, spongy earth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mired', 'imp. & p. p.', 'of Mire');
  INSERT INTO words (word, word_type, description) VALUES  (    'Miring', 'p. pr. & vb. n.', 'of Mire');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mire', 'v. t.', 'To cause or permit to stick fast in mire; to plunge or fix\n   in mud; as, to mire a horse or wagon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mire', 'v. t.', 'To soil with mud or foul matter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mire', 'v. i.', 'To stick in mire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mirific', 'a.', 'Alt. of Mirifical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mirifical', 'a.', 'Working wonders; wonderful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mirificent', 'a.', 'Wonderful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Miriness', 'n.', 'The quality of being miry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mirk', 'a.', 'Dark; gloomy; murky.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mirk', 'n.', 'Darkness; gloom; murk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mirksome', 'a.', 'Dark; gloomy; murky.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mirky', 'a.', 'Dark; gloomy. See Murky.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mirror', 'n.', 'A looking-glass or a speculum; any glass or polished\n   substance that forms images by the reflection of rays of light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mirror', 'n.', 'That which gives a true representation, or in which a true\n   image may be seen; hence, a pattern; an exemplar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mirror', 'n.', 'See Speculum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mirrored', 'imp. & p. p.', 'of Mirror');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mirroring', 'p. pr. & vb. n.', 'of Mirror');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mirror', 'v. t.', 'To reflect, as in a mirror.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mirth', 'n.', 'Merriment; gayety accompanied with laughter; jollity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mirth', 'n.', 'That which causes merriment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mirthful', 'a.', 'Full of mirth or merriment; merry; as, mirthful children.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mirthful', 'a.', 'Indicating or inspiring mirth; as, a mirthful face.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mirthless', 'a.', 'Without mirth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Miry', 'a.', 'Abounding with deep mud; full of mire; muddy; as, a miry\n   road.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mirza', 'n.', 'The common title of honor in Persia, prefixed to the surname\n   of an individual. When appended to the surname, it signifies Prince.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mis-', '', 'A prefix used adjectively and adverbially in the sense of\n   amiss, wrong, ill, wrongly, unsuitably; as, misdeed, mislead, mischief,\n   miscreant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mis', 'a. & adv.', 'Wrong; amiss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Misacceptation', 'n.', 'Wrong acceptation; understanding in a wrong sense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Misaccompt', 'v. t.', 'To account or reckon wrongly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Misadjust', 'v. t.', 'To adjust wrongly of unsuitably; to throw of\n   adjustment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Misadjustment', 'n.', 'Wrong adjustment; unsuitable arrangement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Misadventure', 'n.', 'Mischance; misfortune; ill lick; unlucky accident;\n   ill adventure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Misadventured', 'a.', 'Unfortunate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Misadventurous', 'a.', 'Unfortunate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Misadvertence', 'n.', 'Inadvertence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Misadvice', 'n.', 'Bad advice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Misadvise', 'v. t.', 'To give bad counsel to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Misadvised', 'a.', 'Ill advised.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Misaffect', 'v. t.', 'To dislike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Misaffected', 'a.', 'Ill disposed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Misaffection', 'n.', 'An evil or wrong affection; the state of being ill\n   affected.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Misaffirm', 'v. t.', 'To affirm incorrectly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Misaimed', 'a.', 'Not rightly aimed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Misallegation', 'n.', 'A erroneous statement or allegation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Misallege', 'v. t.', 'To state erroneously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Misalliance', 'n.', 'A marriage with a person of inferior rank or social\n   station; an improper alliance; a mesalliance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Misallied', 'a.', 'Wrongly allied or associated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Misallotment', 'n.', 'A wrong allotment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Misalter', 'v. t.', 'To alter wrongly; esp., to alter for the worse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Misanthrope', 'n.', 'A hater of mankind; a misanthropist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Misanthropic', 'a.', 'Alt. of Misanthropical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Misanthropical', 'a.', 'Hating or disliking mankind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Misanthropist', 'n.', 'A misanthrope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Misanthropos', 'n.', 'A misanthrope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Misanthropy', 'n.', 'Hatred of, or dislike to, mankind; -- opposed to\n   philanthropy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Misapplication', 'n.', 'A wrong application.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Misapplied', 'imp. & p. p.', 'of Misapply');
  INSERT INTO words (word, word_type, description) VALUES  (    'Misapplying', 'p. pr. & vb. n.', 'of Misapply');
  INSERT INTO words (word, word_type, description) VALUES  (    'Misapply', 'v. t.', 'To apply wrongly; to use for a wrong purpose; as, to\n   misapply a name or title; to misapply public money.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Misappreciated', 'a.', 'Improperly appreciated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Misapprehend', 'v. t.', 'To take in a wrong sense; to misunderstand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Misapprehension', 'n.', 'A mistaking or mistake; wrong apprehension of\n   ones meaning of a fact; misconception; misunderstanding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Misapprehensively', 'adv.', 'By, or with, misapprehension.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Misappropriate', 'v. t.', 'To appropriate wrongly; to use for a wrong\n   purpose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Misappropriation', 'n.', 'Wrong appropriation; wrongful use.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Misarranged', 'imp. & p. p.', 'of Misarrange');
