  INSERT INTO words (word, word_type, description) VALUES  (    'Mistigris', 'n.', 'Alt. of Mistigri');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mistigri', 'n.', 'A variety of the game of poker in which the joker is\n   used, and called mistigris or mistigri.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mistihead', 'n.', 'Mistiness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mistily', 'adv.', 'With mist; darkly; obscurely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mistime', 'v. t.', 'To time wrongly; not to adapt to the time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mistiness', 'n.', 'State of being misty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mistion', 'n.', 'Mixture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mistitle', 'v. t.', 'To call by a wrong title.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mistle', 'v. i.', 'To fall in very fine drops, as rain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mistletoe', 'n.', 'A parasitic evergreen plant of Europe (Viscum album),\n   bearing a glutinous fruit. When found upon the oak, where it is rare,\n   it was an object of superstitious regard among the Druids. A bird lime\n   is prepared from its fruit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mistonusk', 'n.', 'The American badger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mistook', '', 'imp. & obs. p. p. of Mistake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mistradition', 'n.', 'A wrong tradition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mistrain', 'v. t.', 'To train amiss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mistral', 'n.', 'A violent and cold northwest wind experienced in the\n   Mediterranean provinces of France, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mistranslate', 'v. t.', 'To translate erroneously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mistranslation', 'n.', 'Wrong translation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mistransport', 'v. t.', 'To carry away or mislead wrongfully, as by\n   passion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mistreading', 'n.', 'Misstep; misbehavior.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mistreat', 'v. t.', 'To treat amiss; to abuse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mistreatment', 'n.', 'Wrong treatment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mistress', 'n.', 'A woman having power, authority, or ownership; a woman\n   who exercises authority, is chief, etc.; the female head of a family, a\n   school, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mistress', 'n.', 'A woman well skilled in anything, or having the mastery\n   over it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mistress', 'n.', 'A woman regarded with love and devotion; she who has\n   command over ones heart; a beloved object; a sweetheart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mistress', 'n.', 'A woman filling the place, but without the rights, of a\n   wife; a concubine; a loose woman with whom one consorts habitually.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mistress', 'n.', 'A title of courtesy formerly prefixed to the name of a\n   woman, married or unmarried, but now superseded by the contracted\n   forms, Mrs., for a married, and Miss, for an unmarried, woman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mistress', 'n.', 'A married woman; a wife.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mistress', 'n.', 'The old name of the jack at bowls.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mistress', 'v. i.', 'To wait upon a mistress; to be courting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mistressship', 'n.', 'Female rule or dominion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mistressship', 'n.', 'Ladyship, a style of address; -- with the personal\n   pronoun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mistrial', 'n.', 'A false or erroneous trial; a trial which has no result.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mistrist', 'v. t.', 'To mistrust.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mistrow', 'v. i.', 'To think wrongly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mistrust', 'n.', 'Want of confidence or trust; suspicion; distrust.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mistrust', 'v. t.', 'To regard with jealousy or suspicion; to suspect; to\n   doubt the integrity of; to distrust.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mistrust', 'v. t.', 'To forebode as near, or likely to occur; to surmise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mistruster', 'n.', 'One who mistrusts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mistrustful', 'a.', 'Having or causing mistrust, suspicions, or\n   forebodings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mistrustingly', 'adv.', 'With distrust or suspicion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mistrustless', 'a.', 'Having no mistrust or suspicion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mistune', 'v. t.', 'To tune wrongly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mistura', 'n.', 'A mingled compound in which different ingredients are\n   contained in a liquid state; a mixture. See Mixture, n., 4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mistura', 'n.', 'Sometimes, a liquid medicine containing very active\n   substances, and which can only be administered by drops.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Misturn', 'v. t.', 'To turn amiss; to pervert.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mistutor', 'v. t.', 'To instruct amiss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Misty', 'superl.', 'Accompained with mist; characterized by the presence of\n   mist; obscured by, or overspread with, mist; as, misty weather; misty\n   mountains; a misty atmosphere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Misty', 'superl.', 'Obscured as if by mist; dim; obscure; clouded; as,\n   misty sight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Misunderstood', 'imp. & p. p.', 'of Misunderstand');
  INSERT INTO words (word, word_type, description) VALUES  (    'Misunderstanding', 'p. pr. & vb. n.', 'of Misunderstand');
  INSERT INTO words (word, word_type, description) VALUES  (    'Misunderstand', 'v. t.', 'To misconceive; to mistake; to miscomprehend; to\n   take in a wrong sense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Misunderstander', 'n.', 'One who misunderstands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Misunderstanding', 'n.', 'Mistake of the meaning; error; misconception.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Misunderstanding', 'n.', 'Disagreement; difference of opinion; dissension;\n   quarrel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Misurato', 'a.', 'Measured; -- a direction to perform a passage in strict\n   or measured time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Misusage', 'n.', 'Bad treatment; abuse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Misuse', 'v. t.', 'To treat or use improperly; to use to a bad purpose; to\n   misapply; as, to misuse ones talents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Misuse', 'v. t.', 'To abuse; to treat ill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Misuse', 'n.', 'Wrong use; misapplication; erroneous or improper use.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Misuse', 'n.', 'Violence, or its effects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Misusement', 'n.', 'Misuse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Misuser', 'n.', 'One who misuses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Misuser', 'n.', 'Unlawful use of a right; use in excess of, or varying\n   from, ones right.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Misvalue', 'v. t.', 'To value wrongly or too little; to undervalue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Misvouch', 'v. t.', 'To vouch falsely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Miswander', 'v. i.', 'To wander in a wrong path; to stray; to go astray.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Misway', 'n.', 'A wrong way.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Miswear', 'v. t.', 'To wear ill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Miswed', 'v. t.', 'To wed improperly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Misween', 'v. i.', 'To ween amiss; to misjudge; to distrust; to be\n   mistaken.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Miswend', 'v. i.', 'To go wrong; to go astray.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Misword', 'v. t.', 'To word wrongly; as, to misword a message, or a\n   sentence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Misword', 'n.', 'A word wrongly spoken; a cross word.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Misworship', 'n.', 'Wrong or false worship; mistaken practices in religion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Misworship', 'v. t.', 'To worship wrongly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Misworshiper', 'n.', 'One who worships wrongly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Miswrite', 'v. t.', 'To write incorrectly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Miswrought', 'a.', 'Badly wrought.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Misy', 'n.', 'An impure yellow sulphate of iron; yellow copperas or\n   copiapite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Misyoke', 'v. t.', 'To yoke improperly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Miszealous', 'a.', 'Mistakenly zealous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mite', 'n.', 'A minute arachnid, of the order Acarina, of which there are\n   many species; as, the cheese mite, sugar mite, harvest mite, etc. See\n   Acarina.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mite', 'n.', 'A small coin formerly circulated in England, rated at about a\n   third of a farthing. The name is also applied to a small coin used in\n   Palestine in the time of Christ.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mite', 'n.', 'A small weight; one twentieth of a grain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mite', 'n.', 'Anything very small; a minute object; a very little quantity\n   or particle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Miter', 'n.', 'Alt. of Mitre');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mitre', 'n.', 'A covering for the head, worn on solemn occasions by church\n   dignitaries. It has been made in many forms, the present form being a\n   lofty cap with two points or peaks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mitre', 'n.', 'The surface forming the beveled end or edge of a piece where\n   a miter joint is made; also, a joint formed or a junction effected by\n   two beveled ends or edges; a miter joint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mitre', 'n.', 'A sort of base money or coin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mitered', 'imp. & p. p.', 'of Mitre');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mitred', '', 'of Mitre');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mitering', 'p. pr. & vb. n.', 'of Mitre');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mitring', '', 'of Mitre');
  INSERT INTO words (word, word_type, description) VALUES  (    'Miter', 'v. t.', 'Alt. of Mitre');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mitre', 'v. t.', 'To place a miter upon; to adorn with a miter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mitre', 'v. t.', 'To match together, as two pieces of molding or brass rule\n   on a line bisecting the angle of junction; to bevel the ends or edges\n   of, for the purpose of matching together at an angle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Miter', 'v. i.', 'Alt. of Mitre');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mitre', 'v. i.', 'To meet and match together, as two pieces of molding, on\n   a line bisecting the angle of junction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Miterwort', 'n.', 'Any plant of the genus Mitella, -- slender, perennial\n   herbs with a pod slightly resembling a bishops miter; bishops cap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mithic', 'a.', 'See Mythic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mithras', 'n.', 'The sun god of the Persians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mithridate', 'n.', 'An antidote against poison, or a composition in form of\n   an electuary, supposed to serve either as a remedy or a preservative\n   against poison; an alexipharmic; -- so called from King Mithridates,\n   its reputed inventor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mithridatic', 'a.', 'Of or pertaining to King Mithridates, or to a\n   mithridate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mitigable', 'a.', 'Admitting of mitigation; that may be mitigated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mitigant', 'a.', 'Tending to mitigate; mitigating; lentitive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mitigated', 'imp. & p. p.', 'of Mitigate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mitigating', 'p. pr. & vb. n.', 'of Mitigate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mitigate', 'v. t.', 'To make less severe, intense, harsh, rigorous,\n   painful, etc.; to soften; to meliorate; to alleviate; to diminish; to\n   lessen; as, to mitigate heat or cold; to mitigate grief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mitigate', 'v. t.', 'To make mild and accessible; to mollify; -- applied to\n   persons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mitigation', 'n.', 'The act of mitigating, or the state of being mitigated;\n   abatement or diminution of anything painful, harsh, severe, afflictive,\n   or calamitous; as, the mitigation of pain, grief, rigor, severity,\n   punishment, or penalty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mitigative', 'a.', 'Tending to mitigate; alleviating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mitigator', 'n.', 'One who, or that which, mitigates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mitigatory', 'a.', 'Tending to mitigate or alleviate; mitigative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Miting', 'n.', 'A little one; -- used as a term of endearment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mitome', 'n.', 'The denser part of the protoplasm of a cell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mitosis', 'n.', 'See Karyokinesis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mitraille', 'n.', 'Shot or bits of iron used sometimes in loading cannon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mitrailleur', 'n.', 'One who serves a mitrailleuse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mitrailleuse', 'n.', 'A breech-loading machine gun consisting of a number\n   of barrels fitted together, so arranged that the barrels can be fired\n   simultaneously, or successively, and rapidly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mitral', 'a.', 'Pertaining to a miter; resembling a miter; as, the mitral\n   valve between the left auricle and left ventricle of the heart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mitre', 'n. & v.', 'See Miter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mitriform', 'a.', 'Having the form of a miter, or a peaked cap; as, a\n   mitriform calyptra.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mitt', 'n.', 'A mitten; also, a covering for the wrist and hand and not for\n   the fingers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mitten', 'n.', 'A covering for the hand, worn to defend it from cold or\n   injury. It differs from a glove in not having a separate sheath for\n   each finger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mitten', 'n.', 'A cover for the wrist and forearm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mittened', 'a.', 'Covered with a mitten or mittens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mittent', 'a.', 'Sending forth; emitting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mittimus', 'n.', 'A precept or warrant granted by a justice for committing\n   to prison a party charged with crime; a warrant of commitment to\n   prison.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mittimus', 'n.', 'A writ for removing records from one court to another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mitters green', '', 'A pigment of a green color, the chief constituent of\n   which is oxide of chromium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mitty', 'n.', 'The stormy petrel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mitu', 'n.', 'A South American curassow of the genus Mitua.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mity', 'a.', 'Having, or abounding with, mites.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mixed', 'imp. & p. p.', 'of Mix');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mixt', '', 'of Mix');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mixing', 'p. pr. & vb. n.', 'of Mix');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mix', 'v. t.', 'To cause a promiscuous interpenetration of the parts of, as\n   of two or more substances with each other, or of one substance with\n   others; to unite or blend into one mass or compound, as by stirring\n   together; to mingle; to blend; as, to mix flour and salt; to mix wines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mix', 'v. t.', 'To unite with in company; to join; to associate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mix', 'v. t.', 'To form by mingling; to produce by the stirring together of\n   ingredients; to compound of different parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mix', 'v. i.', 'To become united into a compound; to be blended\n   promiscuously together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mix', 'v. i.', 'To associate; to mingle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mixable', 'a.', 'Capable of being mixed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mixed', 'a.', 'Formed by mixing; united; mingled; blended. See Mix, v. t. &\n   i.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mixedly', 'adv.', 'In a mixed or mingled manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mixen', 'n.', 'A compost heap; a dunghill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mixer', 'n.', 'One who, or that which, mixes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mixogamous', 'a.', 'Pairing with several males; -- said of certain fishes\n   of which several males accompany each female during spawning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mixolydian mode', '', 'The seventh ecclesiastical mode, whose scale\n   commences on G.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mixtilineal', 'a.', 'Alt. of Mixtilinear');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mixtilinear', 'a.', 'Containing, or consisting of, lines of different\n   kinds, as straight, curved, and the like; as, a mixtilinear angle, that\n   is, an angle contained by a straight line and a curve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mixtion', 'n.', 'Mixture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mixtion', 'n.', 'A kind of cement made of mastic, amber, etc., used as a\n   mordant for gold leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mixtly', 'adv.', 'With mixture; in a mixed manner; mixedly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mixture', 'n.', 'The act of mixing, or the state of being mixed; as, made\n   by a mixture of ingredients.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mixture', 'n.', 'That which results from mixing different ingredients\n   together; a compound; as, to drink a mixture of molasses and water; --\n   also, a medley.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mixture', 'n.', 'An ingredient entering into a mixed mass; an additional\n   ingredient.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mixture', 'n.', 'A kind of liquid medicine made up of many ingredients;\n   esp., as opposed to solution, a liquid preparation in which the solid\n   ingredients are not completely dissolved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mixture', 'n.', 'A mass of two or more ingredients, the particles of which\n   are separable, independent, and uncompounded with each other, no matter\n   how thoroughly and finely commingled; -- contrasted with a compound;\n   thus, gunpowder is a mechanical mixture of carbon, sulphur, and niter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mixture', 'n.', 'An organ stop, comprising from two to five ranges of\n   pipes, used only in combination with the foundation and compound stops;\n   -- called also furniture stop. It consists of high harmonics, or\n   overtones, of the ground tone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mizmaze', 'n.', 'A maze or labyrinth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mizzen', 'a.', 'Hindmost; nearest the stern; as, the mizzen shrouds, sails,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mizzen', 'n.', 'The hindmost of the fore and aft sails of a three-masted\n   vessel; also, the spanker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mizzenmast', 'n.', 'The hindmost mast of a three-masted vessel, or of a\n   yawl-rigged vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mizzled', 'imp. & p. p.', 'of Mizzle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mizzling', 'p. pr. & vb. n.', 'of Mizzle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mizzle', 'v. i.', 'To rain in very fine drops.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mizzle', 'v. i.', 'To take ones self off; to go.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mizzle', 'n.', 'Mist; fine rain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mizzy', 'n.', 'A bog or quagmire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mnemonic', 'a.', 'Alt. of Mnemonical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mnemonical', 'a.', 'Assisting in memory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mnemonician', 'n.', 'One who instructs in the art of improving or using the\n   memory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mnemonics', 'n.', 'The art of memory; a system of precepts and rules\n   intended to assist the memory; artificial memory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mnemosyne', 'n.', 'The goddess of memory and the mother of the Muses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mnemotechny', 'n.', 'Mnemonics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mo', 'a., adv., & n.', 'More; -- usually, more in number.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-mo', '', 'A suffix added to the names of certain numerals or to the\n   numerals themselves, to indicate the number of leaves made by folding a\n   sheet of paper; as, sixteenmo or 16mo; eighteenmo or 18mo. It is taken\n   from the Latin forms similarly used; as, duodecimo, sextodecimo, etc. A\n   small circle, placed after the number and near its top, is often used\n   for -mo; as, 16, 18, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moa', 'n.', 'Any one of several very large extinct species of wingless\n   birds belonging to Dinornis, and other related genera, of the suborder\n   Dinornithes, found in New Zealand. They are allied to the apteryx and\n   the ostrich. They were probably exterminated by the natives before New\n   Zealand was discovered by Europeans. Some species were much larger than\n   the ostrich.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moabite', 'n.', 'One of the posterity of Moab, the son of Lot. (Gen. xix.\n   37.) Also used adjectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moabitess', 'n.', 'A female Moabite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moabitish', 'a.', 'Moabite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moaned', 'imp. & p. p.', 'of Moan');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moaning', 'p. pr. & vb. n.', 'of Moan');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moan', 'v. i.', 'To make a low prolonged sound of grief or pain, whether\n   articulate or not; to groan softly and continuously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moan', 'v. i.', 'To emit a sound like moan; -- said of things inanimate;\n   as, the wind moans.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moan', 'v. t.', 'To bewail audibly; to lament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moan', 'v. t.', 'To afflict; to distress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moan', 'v. i.', 'A low prolonged sound, articulate or not, indicative of\n   pain or of grief; a low groan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moan', 'v. i.', 'A low mournful or murmuring sound; -- of things.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moanful', 'a.', 'Full of moaning; expressing sorrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moat', 'n.', 'A deep trench around the rampart of a castle or other\n   fortified place, sometimes filled with water; a ditch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moat', 'v. t.', 'To surround with a moat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moate', 'v. i.', 'To void the excrement, as a bird; to mute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mob', 'n.', 'A mobcap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mob', 'v. t.', 'To wrap up in, or cover with, a cowl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mob', 'n.', 'The lower classes of a community; the populace, or the lowest\n   part of it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mob', 'n.', 'A throng; a rabble; esp., an unlawful or riotous assembly; a\n   disorderly crowd.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mobbed', 'imp. & p. p.', 'of Mob');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mobbing', 'p. pr. & vb. n.', 'of Mob');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mob', 'v. t.', 'To crowd about, as a mob, and attack or annoy; as, to mob a\n   house or a person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mobbish', 'a.', 'Like a mob; tumultuous; lawless; as, a mobbish act.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mobcap', 'n.', 'A plain cap or headdress for women or girls; especially,\n   one tying under the chin by a very broad band, generally of the same\n   material as the cap itself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mobile', 'a.', 'Capable of being moved; not fixed in place or condition;\n   movable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mobile', 'a.', 'Characterized by an extreme degree of fluidity; moving or\n   flowing with great freedom; as, benzine and mercury are mobile liquids;\n   -- opposed to viscous, viscoidal, or oily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mobile', 'a.', 'Easily moved in feeling, purpose, or direction; excitable;\n   changeable; fickle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mobile', 'a.', 'Changing in appearance and expression under the influence\n   of the mind; as, mobile features.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mobile', 'a.', 'Capable of being moved, aroused, or excited; capable of\n   spontaneous movement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mobile', 'a.', 'The mob; the populace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mobility', 'n.', 'The quality or state of being mobile; as, the mobility of\n   a liquid, of an army, of the populace, of features, of a muscle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mobility', 'n.', 'The mob; the lower classes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mobilization', 'n.', 'The act of mobilizing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mobilized', 'imp. & p. p.', 'of Mobilize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mobilizing', 'p. pr. & vb. n.', 'of Mobilize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mobilize', 'v. t.', 'To put in a state of readiness for active service in\n   war, as an army corps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moble', 'v. t.', 'To wrap the head of in a hood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mobles', 'n. pl.', 'See Moebles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mobocracy', 'n.', 'A condition in which the lower classes of a nation\n   control public affairs without respect to law, precedents, or vested\n   rights.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mobocrat', 'n.', 'One who favors a form of government in which the\n   unintelligent populace rules without restraint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mobocratic', 'a.', 'Of, or relating to, a mobocracy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moccasin', 'n.', 'A shoe made of deerskin, or other soft leather, the sole\n   and upper part being one piece. It is the customary shoe worn by the\n   American Indians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moccasin', 'n.', 'A poisonous snake of the Southern United States. The\n   water moccasin (Ancistrodon piscivorus) is usually found in or near\n   water. Above, it is olive brown, barred with black; beneath, it is\n   brownish yellow, mottled with darker. The upland moccasin is\n   Ancistrodon atrofuscus. They resemble rattlesnakes, but are without\n   rattles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moccasined', 'a.', 'Covered with, or wearing, a moccasin or moccasins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mocha', 'n.', 'A seaport town of Arabia, on the Red Sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mocha', 'n.', 'A variety of coffee brought from Mocha.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mocha', 'n.', 'An Abyssinian weight, equivalent to a Troy grain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moche', 'n.', 'A bale of raw silk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moche', 'a.', 'Much.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mochel', 'a. & adv.', 'Much.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mochila', 'n.', 'A large leather flap which covers the saddletree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mocked', 'imp. & p. p.', 'of Mock');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mocking', 'p. pr. & vb. n.', 'of Mock');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mock', 'v. t.', 'To imitate; to mimic; esp., to mimic in sport, contempt,\n   or derision; to deride by mimicry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mock', 'v. t.', 'To treat with scorn or contempt; to deride.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mock', 'v. t.', 'To disappoint the hopes of; to deceive; to tantalize; as,\n   to mock expectation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mock', 'v. i.', 'To make sport contempt or in jest; to speak in a scornful\n   or jeering manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mock', 'n.', 'An act of ridicule or derision; a scornful or contemptuous\n   act or speech; a sneer; a jibe; a jeer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mock', 'n.', 'Imitation; mimicry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mock', 'a.', 'Imitating reality, but not real; false; counterfeit; assumed;\n   sham.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mockable', 'a.', 'Such as can be mocked.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mockado', 'n.', 'A stuff made in imitation of velvet; -- probably the same\n   as mock velvet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mockadour', 'n.', 'See Mokadour.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mockage', 'n.', 'Mockery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mockbird', 'n.', 'The European sedge warbler (Acrocephalus phragmitis).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mocker', 'n.', 'One who, or that which, mocks; a scorner; a scoffer; a\n   derider.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mocker', 'n.', 'A deceiver; an impostor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mocker', 'n.', 'A mocking bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mockeries', 'pl. ', 'of Mockery');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mockery', 'n.', 'The act of mocking, deriding, and exposing to contempt, by\n   mimicry, by insincere imitation, or by a false show of earnestness; a\n   counterfeit appearance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mockery', 'n.', 'Insulting or contemptuous action or speech; contemptuous\n   merriment; derision; ridicule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mockery', 'n.', 'Subject of laughter, derision, or sport.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mocking', 'a.', 'Imitating, esp. in derision, or so as to cause derision;\n   mimicking; derisive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mockingly', 'adv.', 'By way of derision; in a contemptuous or mocking\n   manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mockingstock', 'n.', 'A butt of sport; an object of derision.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mockish', 'a.', 'Mock; counterfeit; sham.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mockle', 'a.', 'See Mickle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moco', 'n.', 'A South American rodent (Cavia rupestris), allied to the\n   Guinea pig, but larger; -- called also rock cavy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Modal', 'a.', 'Of or pertaining to a mode or mood; consisting in mode or\n   form only; relating to form; having the form without the essence or\n   reality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Modal', 'a.', 'Indicating, or pertaining to, some mode of conceiving\n   existence, or of expressing thought.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Modalist', 'n.', 'One who regards Father, Son, and Spirit as modes of\n   being, and not as persons, thus denying personal distinction in the\n   Trinity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Modality', 'n.', 'The quality or state of being modal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Modality', 'n.', 'A modal relation or quality; a mode or point of view\n   under which an object presents itself to the mind. According to Kant,\n   the quality of propositions, as assertory, problematical, or\n   apodeictic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Modally', 'adv.', 'In a modal manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mode', 'n.', 'Manner of doing or being; method; form; fashion; custom; way;\n   style; as, the mode of speaking; the mode of dressing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mode', 'n.', 'Prevailing popular custom; fashion, especially in the phrase\n   the mode.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mode', 'n.', 'Variety; gradation; degree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mode', 'n.', 'Any combination of qualities or relations, considered apart\n   from the substance to which they belong, and treated as entities; more\n   generally, condition, or state of being; manner or form of arrangement\n   or manifestation; form, as opposed to matter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mode', 'n.', 'The form in which the proposition connects the predicate and\n   subject, whether by simple, contingent, or necessary assertion; the\n   form of the syllogism, as determined by the quantity and quality of the\n   constituent proposition; mood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mode', 'n.', 'Same as Mood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mode', 'n.', 'The scale as affected by the various positions in it of the\n   minor intervals; as, the Dorian mode, the Ionic mode, etc., of ancient\n   Greek music.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mode', 'n.', 'A kind of silk. See Alamode, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Model', 'n.', 'A miniature representation of a thing, with the several\n   parts in due proportion; sometimes, a facsimile of the same size.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Model', 'n.', 'Something intended to serve, or that may serve, as a pattern\n   of something to be made; a material representation or embodiment of an\n   ideal; sometimes, a drawing; a plan; as, the clay model of a sculpture;\n   the inventors model of a machine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Model', 'n.', 'Anything which serves, or may serve, as an example for\n   imitation; as, a government formed on the model of the American\n   constitution; a model of eloquence, virtue, or behavior.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Model', 'n.', 'That by which a thing is to be measured; standard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Model', 'n.', 'Any copy, or resemblance, more or less exact.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Model', 'n.', 'A person who poses as a pattern to an artist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Model', 'a.', 'Suitable to be taken as a model or pattern; as, a model\n   house; a model husband.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Modeled', 'imp. & p. p.', 'of Model');
  INSERT INTO words (word, word_type, description) VALUES  (    'Modelled', '', 'of Model');
  INSERT INTO words (word, word_type, description) VALUES  (    'Modeling', 'p. pr. & vb. n.', 'of Model');
  INSERT INTO words (word, word_type, description) VALUES  (    'Modelling', '', 'of Model');
  INSERT INTO words (word, word_type, description) VALUES  (    'Model', 'v. t.', 'To plan or form after a pattern; to form in model; to\n   form a model or pattern for; to shape; to mold; to fashion; as, to\n   model a house or a government; to model an edifice according to the\n   plan delineated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Model', 'v. i.', 'To make a copy or a pattern; to design or imitate forms;\n   as, to model in wax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Modeler', 'n.', 'One who models; hence, a worker in plastic art.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Modeling', 'n.', 'The act or art of making a model from which a work of art\n   is to be executed; the formation of a work of art from some plastic\n   material. Also, in painting, drawing, etc., the expression or\n   indication of solid form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Modelize', 'v. t.', 'To model.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Modena', 'n.', 'A certain crimsonlike color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Modenese', 'a.', 'Of or pertaining to Modena or its inhabitants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Modenese', 'n. sing. & pl.', 'A native or inhabitant of Modena; the people\n   of Modena.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moder', 'n.', 'A mother.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moder', 'n.', 'The principal piece of an astrolabe, into which the others\n   are fixed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moder', 'v. t.', 'To moderate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moderable', 'a.', 'Modeate; temperate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moderance', 'n.', 'Moderation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moderate', 'a.', 'Kept within due bounds; observing reasonable limits; not\n   excessive, extreme, violent, or rigorous; limited; restrained');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moderate', 'a.', 'Limited in quantity; sparing; temperate; frugal; as,\n   moderate in eating or drinking; a moderate table.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moderate', 'a.', 'Limited in degree of activity, energy, or excitement;\n   reasonable; calm; slow; as, moderate language; moderate endeavors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moderate', 'a.', 'Not extreme in opinion, in partisanship, and the like;\n   as, a moderate Calvinist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moderate', 'a.', 'Not violent or rigorous; temperate; mild; gentle; as, a\n   moderate winter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moderate', 'a.', 'Limited as to degree of progress; as, to travel at\n   moderate speed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moderate', 'a.', 'Limited as to the degree in which a quality, principle,\n   or faculty appears; as, an infusion of moderate strength; a man of\n   moderate abilities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moderate', 'a.', 'Limited in scope or effects; as, a reformation of a\n   moderate kind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moderate', 'n.', 'One of a party in the Church of Scotland in the 18th\n   century, and part of the 19th, professing moderation in matters of\n   church government, in discipline, and in doctrine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moderated', 'imp. & p. p.', 'of Moderate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moderating', 'p. pr. & vb. n.', 'of Moderate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moderate', 'v. t.', 'To restrain from excess of any kind; to reduce from a\n   state of violence, intensity, or excess; to keep within bounds; to make\n   temperate; to lessen; to allay; to repress; to temper; to qualify; as,\n   to moderate rage, action, desires, etc.; to moderate heat or wind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moderate', 'v. t.', 'To preside over, direct, or regulate, as a public\n   meeting; as, to moderate a synod.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moderate', 'v. i.', 'To become less violent, severe, rigorous, or intense;\n   as, the wind has moderated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moderate', 'v. i.', 'To preside as a moderator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moderately', 'adv.', 'In a moderate manner or degree; to a moderate extent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moderateness', 'n.', 'The quality or state of being moderate;\n   temperateness; moderation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moderation', 'n.', 'The act of moderating, or of imposing due restraint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moderation', 'n.', 'The state or quality of being mmoderate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moderation', 'n.', 'Calmness of mind; equanimity; as, to bear adversity\n   with moderation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moderation', 'n.', 'The first public examinations for degrees at the\n   University of Oxford; -- usually contracted to mods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moderatism', 'n.', 'Moderation in doctrines or opinion, especially in\n   politics or religion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moderato', 'a. & adv.', 'With a moderate degree of quickness; moderately.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moderator', 'n.', 'One who, or that which, moderates, restrains, or\n   pacifies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moderator', 'n.', 'The officer who presides over an assembly to preserve\n   order, propose questions, regulate the proceedings, and declare the\n   votes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moderator', 'n.', 'In the University of Oxford, an examiner for\n   moderations; at Cambridge, the superintendant of examinations for\n   degrees; at Dublin, either the first (senior) or second (junior) in\n   rank in an examination for the degree of Bachelor of Arts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moderator', 'n.', 'A mechamical arrangement for regulating motion in a\n   machine, or producing equality of effect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moderatorship', 'n.', 'The office of a moderator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moderatress', 'n.', 'A female moderator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moderatrix', 'n.', 'A female moderator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Modern', 'a.', 'Of or pertaining to the present time, or time not long\n   past; late; not ancient or remote in past time; of recent period; as,\n   modern days, ages, or time; modern authors; modern fashions; modern\n   taste; modern practice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Modern', 'a.', 'New and common; trite; commonplace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Modern', 'n.', 'A person of modern times; -- opposed to ancient.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Modernism', 'n.', 'Modern practice; a thing of recent date; esp., a modern\n   usage or mode of expression.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Modernist', 'n.', 'One who admires the moderns, or their ways and fashions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Modernity', 'n.', 'Modernness; something modern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Modernization', 'n.', 'The act of rendering modern in style; the act or\n   process of causing to conform to modern of thinking or acting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Modernized', 'imp. & p. p.', 'of Modernize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Modernizing', 'p. pr. & vb. n.', 'of Modernize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Modernize', 'v. t.', 'To render modern; to adapt to modern person or\n   things; to cause to conform to recent or present usage or taste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Modernizer', 'n.', 'One who modernizes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Modernly', 'adv.', 'In modern times.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Modernness', 'n.', 'The quality or state of being modern; recentness;\n   novelty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Modest', 'a.', 'Restraining within due limits of propriety; not forward,\n   bold, boastful, or presumptious; rather retiring than pushing ones\n   self forward; not obstructive; as, a modest youth; a modest man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Modest', 'a.', 'Observing the proprieties of the sex; not unwomanly in act\n   or bearing; free from undue familiarity, indecency, or lewdness; decent\n   in speech and demeanor; -- said of a woman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Modest', 'a.', 'Evincing modestly in the actor, author, or speaker; not\n   showing presumption; not excessive or extreme; moderate; as, a modest\n   request; modest joy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Modestly', 'adv.', 'In a modest manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Modesty', 'n.', 'The quality or state of being modest; that lowly temper\n   which accompanies a moderate estimate of ones own worth and\n   importance; absence of self-assertion, arrogance, and presumption;\n   humility respecting ones own merit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Modesty', 'n.', 'Natural delicacy or shame regarding personal charms and\n   the sexual relation; purity of thought and manners; due regard for\n   propriety in speech or action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Modicity', 'n.', 'Moderateness; smallness; meanness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Modicum', 'n.', 'A little; a small quantity; a measured simply.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Modifiability', 'n.', 'Capability of being modified; state or quality of\n   being modifiable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Modifiable', 'a.', 'Capable of being modified; liable to modification.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Modificable', 'a.', 'Modifiable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Modificate', 'v. t.', 'To qualify.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Modification', 'n.', 'The act of modifying, or the state of being modified;\n   a modified form or condition; state as modified; a change; as, the\n   modification of an opinion, or of a machine; the various modifications\n   of light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Modificative', 'n.', 'That which modifies or qualifies, as a word or\n   clause.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Modificatory', 'a.', 'Tending or serving to modify; modifying.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Modifier', 'n.', 'One who, or that which, modifies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Modified', 'imp. & p. p.', 'of Modify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Modifying', 'p. pr. & vb. n.', 'of Modify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Modify', 'v. t.', 'To change somewhat the form or qualities of; to alter\n   somewhat; as, to modify a contrivance adapted to some mechanical\n   purpose; to modify the terms of a contract.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Modify', 'v. t.', 'To limit or reduce in extent or degree; to moderate; to\n   qualify; to lower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Modillion', 'n.', 'The enriched block or horizontal bracket generally found\n   under the cornice of the Corinthian and Composite entablature, and\n   sometimes, less ornamented, in the Ionic and other orders; -- so called\n   because of its arrangement at regulated distances.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Modiolar', 'a.', 'Shaped like a bushel measure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Modioli', 'pl. ', 'of Modiolus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Modiolus', 'n.', 'The central column in the osseous cochlea of the ear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Modish', 'a.', 'According to the mode, or customary manner; conformed to\n   the fashion; fashionable; hence, conventional; as, a modish dress; a\n   modish feast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Modist', 'n.', 'One who follows the fashion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Modiste', 'n.', 'A female maker of, or dealer in, articles of fashion,\n   especially of the fashionable dress of ladies; a woman who gives\n   direction to the style or mode of dress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Modii', 'pl. ', 'of Modius');
  INSERT INTO words (word, word_type, description) VALUES  (    'Modius', 'n.', 'A dry measure, containing about a peck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Modocs', 'n. pl.', 'A tribe of warlike Indians formerly inhabiting Northern\n   California. They are nearly extinct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Modular', 'a.', 'Of or pertaining to mode, modulation, module, or modius;\n   as, modular arrangement; modular accent; modular measure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Modulated', 'imp. & p. p.', 'of Modulate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Modulating', 'p. pr. & vb. n.', 'of Modulate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Modulate', 'v. t.', 'To form, as sound, to a certain key, or to a certain\n   portion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Modulate', 'v. t.', 'To vary or inflect in a natural, customary, or musical\n   manner; as, the organs of speech modulate the voice in reading or\n   speaking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Modulate', 'v. i.', 'To pass from one key into another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Modulation', 'n.', 'The act of modulating, or the state of being modulated;\n   as, the modulation of the voice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Modulation', 'n.', 'Sound modulated; melody.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Modulation', 'n.', 'A change of key, whether transient, or until the music\n   becomes established in the new key; a shifting of the tonality of a\n   piece, so that the harmonies all center upon a new keynote or tonic;\n   the art of transition out of the original key into one nearly related,\n   and so on, it may be, by successive changes, into a key quite remote.\n   There are also sudden and unprepared modulations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Modulator', 'n.', 'One who, or that which, modulates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Module', 'n.', 'A model or measure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Module', 'n.', 'The size of some one part, as the diameter of semi-diameter\n   of the base of a shaft, taken as a unit of measure by which the\n   proportions of the other parts of the composition are regulated.\n   Generally, for columns, the semi-diameter is taken, and divided into a\n   certain number of parts, called minutes (see Minute), though often the\n   diameter is taken, and any dimension is said to be so many modules and\n   minutes in height, breadth, or projection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Module', 'n.', 'To model; also, to modulate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moduli', 'pl. ', 'of Modulus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Modulus', 'n.', 'A quantity or coefficient, or constant, which expresses\n   the measure of some specified force, property, or quality, as of\n   elasticity, strength, efficiency, etc.; a parameter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Modi', 'pl. ', 'of Modus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Modus', 'n.', 'The arrangement of, or mode of expressing, the terms of a\n   contract or conveyance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Modus', 'n.', 'A qualification involving the idea of variation or departure\n   from some general rule or form, in the way of either restriction or\n   enlargement, according to the circumstances of the case, as in the will\n   of a donor, an agreement between parties, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Modus', 'n.', 'A fixed compensation or equivalent given instead of payment\n   of tithes in kind, expressed in full by the phrase modus decimandi.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mody', 'a.', 'Fashionable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moe', 'n.', 'A wry face or mouth; a mow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moe', 'v. i.', 'To make faces; to mow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moe', 'a., adv., & n.', 'More. See Mo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moebles', 'n. pl.', 'Movables; furniture; -- also used in the singular\n   (moeble).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moelline', 'n.', 'An unguent for the hair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moellon', 'n.', 'Rubble masonry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moesogothic', 'a.', 'Belonging to the Moesogoths, a branch of the Goths who\n   settled in Moesia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moesogothic', 'n.', 'The language of the Moesogoths; -- also called Gothic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moeve', 'v. t. & i.', 'To move.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moff', 'n.', 'A thin silk stuff made in Caucasia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moggan', 'n.', 'A closely fitting knit sleeve; also, a legging of knitted\n   material.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mogul', 'n.', 'A person of the Mongolian race.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mogul', 'n.', 'A heavy locomotive for freight traffic, having three pairs\n   of connected driving wheels and a two-wheeled truck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moha', 'n.', 'A kind of millet (Setaria Italica); German millet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mohair', 'n.', 'The long silky hair or wool of the Angora goat of Asia\n   Minor; also, a fabric made from this material, or an imitation of such\n   fabric.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mohammedan', 'a.', 'Of or pertaining to Mohammed, or the religion and\n   institutions founded by Mohammed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mohammedan', 'n.', 'A follower of Mohammed, the founder of Islamism; one\n   who professes Mohammedanism or Islamism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mohammedanism', 'n.', 'Alt. of Mohammedism');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mohammedism', 'n.', 'The religion, or doctrines and precepts, of Mohammed,\n   contained in the Koran; Islamism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mohammedanize', 'v. t.', 'Alt. of Mohammedize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mohammedize', 'v. t.', 'To make conformable to the principles, or customs\n   and rites, of Mohammedanism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mohawk', 'n.', 'One of a tribe of Indians who formed part of the Five\n   Nations. They formerly inhabited the valley of the Mohawk River.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mohawk', 'n.', 'One of certain ruffians who infested the streets of London\n   in the time of Addison, and took the name from the Mohawk Indians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mohicans', 'n. pl.', 'A tribe of Lenni-Lenape Indians who formerly\n   inhabited Western Connecticut and Eastern New York.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moho', 'n.', 'A gallinule (Notornis Mantelli) formerly inhabiting New\n   Zealand, but now supposed to be extinct. It was incapable of flight.\n   See Notornis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mohock', 'n.', 'See Mohawk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moholi', 'n.', 'See Maholi.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mohr', 'n.', 'A West African gazelle (Gazella mohr), having horns on which\n   are eleven or twelve very prominent rings. It is one of the species\n   which produce bezoar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mohur', 'n.', 'A British Indian gold coin, of the value of fifteen silver\n   rupees, or $7.21.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mohurrum', 'n.', 'Alt. of Muharram');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muharram', 'n.', 'The first month of the Mohammedan year.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muharram', 'n.', 'A festival of the Shiah sect of the Mohammedans held\n   during the first ten days of the month Mohurrum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moider', 'v. i.', 'To toil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moidore', 'n.', 'A gold coin of Portugal, valued at about 27s. sterling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moieties', 'pl. ', 'of Moiety');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moiety', 'a.', 'One of two equal parts; a half; as, a moiety of an estate,\n   of goods, or of profits; the moiety of a jury, or of a nation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moiety', 'a.', 'An indefinite part; a small part.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moiled', 'imp. & p. p.', 'of Moil');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moiling', 'p. pr. & vb. n.', 'of Moil');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moil', 'v. t.', 'To daub; to make dirty; to soil; to defile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moil', 'v. i.', 'To soil ones self with severe labor; to work with painful\n   effort; to labor; to toil; to drudge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moil', 'n.', 'A spot; a defilement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moile', 'n.', 'A kind of high shoe anciently worn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moineau', 'n.', 'A small flat bastion, raised in the middle of an overlong\n   curtain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moira', 'n.', 'The deity who assigns to every man his lot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moire', 'n.', 'Originally, a fine textile fabric made of the hair of an\n   Asiatic goat; afterwards, any textile fabric to which a watered\n   appearance is given in the process of calendering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moire', 'n.', 'A watered, clouded, or frosted appearance produced upon\n   either textile fabrics or metallic surfaces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moire metallique', '', 'A crystalline or frosted appearance produced by\n   some acids on tin plate; also, the tin plate thus treated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moist', 'a.', 'Moderately wet; damp; humid; not dry; as, a moist atmosphere\n   or air.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moist', 'a.', 'Fresh, or new.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moist', 'v. t.', 'To moisten.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moistened', 'imp. & p. p.', 'of Moisten');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moistening', 'p. pr. & vb. n.', 'of Moisten');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moisten', 'v. t.', 'To make damp; to wet in a small degree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moisten', 'v. t.', 'To soften by making moist; to make tender.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moistener', 'n.', 'One who, or that which, moistens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moistful', 'a.', 'Full of moisture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moistless', 'a.', 'Without moisture; dry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moistness', 'n.', 'The quality or state of being moist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moisture', 'n.', 'A moderate degree of wetness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moisture', 'n.', 'That which moistens or makes damp or wet; exuding fluid;\n   liquid in small quantity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moistureless', 'a.', 'Without moisture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moisty', 'a.', 'Moist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moither', 'v. t.', 'To perplex; to confuse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moither', 'v. i.', 'To toil; to labor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mokadour', 'n.', 'A handkerchief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moke', 'n.', 'A donkey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moke', 'n.', 'A mesh of a net, or of anything resembling a net.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moky', 'a.', 'Misty; dark; murky; muggy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mola', 'n.', 'See Sunfish, 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Molar', 'a.', 'Of or pertaining to a mass of matter; -- said of the\n   properties or motions of masses, as distinguished from those of\n   molecules or atoms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Molar', 'a.', 'Having power to grind; grinding; as, the molar teeth; also,\n   of or pertaining to the molar teeth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Molar', 'n.', 'Any one of the teeth back of the incisors and canines. The\n   molar which replace the deciduous or milk teeth are designated as\n   premolars, and those which are not preceded by deciduous teeth are\n   sometimes called true molars. See Tooth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Molary', 'a.', 'Same as 2d Molar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Molasse', 'n.', 'A soft Tertiary sandstone; -- applied to a rock occurring\n   in Switzerland. See Chart of Geology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Molasses', 'n.', 'The thick, brown or dark colored, viscid,\n   uncrystallizable sirup which drains from sugar, in the process of\n   manufacture; any thick, viscid, sweet sirup made from vegetable juice\n   or sap, as of the sorghum or maple. See Treacle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mold', 'n.', 'A spot; a blemish; a mole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mold', 'v.', 'Alt. of Mould');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mould', 'v.', 'Crumbling, soft, friable earth; esp., earth containing the\n   remains or constituents of organic matter, and suited to the growth of\n   plants; soil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mould', 'v.', 'Earthy material; the matter of which anything is formed;\n   composing substance; material.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Molded', 'imp. & p. p.', 'of Mould');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moulded', '', 'of Mould');
  INSERT INTO words (word, word_type, description) VALUES  (    'Molding', 'p. pr. & vb. n.', 'of Mould');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moulding', '', 'of Mould');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mold', 'v. t.', 'Alt. of Mould');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mould', 'v. t.', 'To cover with mold or soil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mold', 'n.', 'Alt. of Mould');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mould', 'n.', 'A growth of minute fungi of various kinds, esp. those of the\n   great groups Hyphomycetes, and Physomycetes, forming on damp or\n   decaying organic matter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mold', 'v. t.', 'Alt. of Mould');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mould', 'v. t.', 'To cause to become moldy; to cause mold to grow upon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mold', 'v. i.', 'Alt. of Mould');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mould', 'v. i.', 'To become moldy; to be covered or filled, in whole or in\n   part, with a mold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mold', 'n.', 'Alt. of Mould');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mould', 'n.', 'The matrix, or cavity, in which anything is shaped, and from\n   which it takes its form; also, the body or mass containing the cavity;\n   as, a sand mold; a jelly mold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mould', 'n.', 'That on which, or in accordance with which, anything is\n   modeled or formed; anything which serves to regulate the size, form,\n   etc., as the pattern or templet used by a shipbuilder, carpenter, or\n   mason.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mould', 'n.', 'Cast; form; shape; character.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mould', 'n.', 'A group of moldings; as, the arch mold of a porch or\n   doorway; the pier mold of a Gothic pier, meaning the whole profile,\n   section, or combination of parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mould', 'n.', 'A fontanel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mould', 'n.', 'A frame with a wire cloth bottom, on which the pump is\n   drained to form a sheet, in making paper by hand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mold', 'v. t.', 'Alt. of Mould');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mould', 'v. t.', 'To form into a particular shape; to shape; to model; to\n   fashion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mould', 'v. t.', 'To ornament by molding or carving the material of; as, a\n   molded window jamb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mould', 'v. t.', 'To knead; as, to mold dough or bread.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mould', 'v. t.', 'To form a mold of, as in sand, in which a casting may be\n   made.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moldable', 'a.', 'Alt. of Mouldable');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mouldable', 'a.', 'Capable of being molded or formed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moldboard', 'n.', 'Alt. of Mouldboard');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mouldboard', 'n.', 'A curved plate of iron (originally of wood) back of the\n   share of a plow, which turns over the earth in plowing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mouldboard', 'n.', 'A follow board.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Molder', 'n.', 'Alt. of Moulder');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moulder', 'n.', 'One who, or that which, molds or forms into shape;\n   specifically (Founding), one skilled in the art of making molds for\n   castings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moldered', 'imp. & p. p.', 'of Moulder');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mouldered', '', 'of Moulder');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moldering', 'p. pr. & vb. n.', 'of Moulder');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mouldering', '', 'of Moulder');
  INSERT INTO words (word, word_type, description) VALUES  (    'Molder', 'v. i.', 'Alt. of Moulder');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moulder', 'v. i.', 'To crumble into small particles; to turn to dust by\n   natural decay; to lose form, or waste away, by a gradual separation of\n   the component particles, without the presence of water; to crumble\n   away.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Molder', 'v. t.', 'Alt. of Moulder');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moulder', 'v. t.', 'To turn to dust; to cause to crumble; to cause to waste\n   away.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moldery', 'a.', 'Alt. of Mouldery');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mouldery', 'a.', 'Covered or filled with mold; consisting of, or\n   resembling, mold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moldiness', 'n.', 'Alt. of Mouldiness');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mouldiness', 'n.', 'The state of being moldy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Molding', 'n.', 'Alt. of Moulding');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moulding', 'n.', 'The act or process of shaping in or on a mold, or of\n   making molds; the art or occupation of a molder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moulding', 'n.', 'Anything cast in a mold, or which appears to be so, as\n   grooved or ornamental bars of wood or metal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moulding', 'n.', 'A plane, or curved, narrow surface, either sunk or\n   projecting, used for decoration by means of the lights and shades upon\n   its surface. Moldings vary greatly in pattern, and are generally used\n   in groups, the different members of each group projecting or\n   retreating, one beyond another. See Cable, n., 3, and Crenelated\n   molding, under Crenelate, v. t.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Molding', 'p.a.', 'Alt. of Moulding');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moulding', 'p.a.', 'Used in making a mold or moldings; used in shaping\n   anything according to a pattern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moldwarp', 'n.', 'Alt. of Mouldwarp');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mouldwarp', 'n.', 'See Mole the animal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moldy', 'superl.', 'Alt. of Mouldy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mouldy', 'superl.', 'Overgrown with, or containing, mold; as, moldy cheese\n   or bread.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mole', 'n.', 'A spot; a stain; a mark which discolors or disfigures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mole', 'n.', 'A spot, mark, or small permanent protuberance on the human\n   body; esp., a spot which is dark-colored, from which commonly issue one\n   or more hairs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mole', 'n.', 'A mass of fleshy or other more or less solid matter generated\n   in the uterus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mole', 'n.', 'A mound or massive work formed of masonry or large stones,\n   etc., laid in the sea, often extended either in a right line or an arc\n   of a circle before a port which it serves to defend from the violence\n   of the waves, thus protecting ships in a harbor; also, sometimes, the\n   harbor itself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mole', 'n.', 'Any insectivore of the family Talpidae. They have minute eyes\n   and ears, soft fur, and very large and strong fore feet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mole', 'n.', 'A plow of peculiar construction, for forming underground\n   drains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moled', 'imp. & p. p.', 'of Mole');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moling', 'p. pr. & vb. n.', 'of Mole');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mole', 'v. t.', 'To form holes in, as a mole; to burrow; to excavate; as,\n   to mole the earth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mole', 'v. t.', 'To clear of molehills.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Molebut', 'n.', 'The sunfish (Orthagoriscus, or Mola).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Molecast', 'n.', 'A little elevation of earth made by a mole; a molehill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Molech', 'n.', 'The fire god of the Ammonites, to whom human sacrifices\n   were offered; Moloch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Molecular', 'a.', 'Pertaining to, connected with, produced by, or\n   consisting of, molecules; as, molecular forces; molecular groups of\n   atoms, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Molecularity', 'n.', 'The state of consisting of molecules; the state or\n   quality of being molecular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Molecularly', 'adv.', 'With molecules; in the manner of molecules.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Molecule', 'n.', 'One of the very small invisible particles of which all\n   matter is supposed to consist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Molecule', 'n.', 'The smallest part of any substance which possesses the\n   characteristic properties and qualities of that substance, and which\n   can exist alone in a free state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Molecule', 'n.', 'A group of atoms so united and combined by chemical\n   affinity that they form a complete, integrated whole, being the\n   smallest portion of any particular compound that can exist in a free\n   state; as, a molecule of water consists of two atoms of hydrogen and\n   one of oxygen. Cf. Atom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mole-eyed', 'a.', 'Having eyes like those of the mole; having imperfect\n   sight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Molehill', 'n.', 'A little hillock of earth thrown up by moles working\n   under ground; hence, a very small hill, or an insignificant obstacle or\n   difficulty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Molendinaceous', 'a.', 'Alt. of Molendinarious');
  INSERT INTO words (word, word_type, description) VALUES  (    'Molendinarious', 'a.', 'Resembling the sails of a windmill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moleskin', 'n.', 'Any fabric having a thick soft shag, like the fur of a\n   mole; esp., a kind of strong twilled fustian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Molested', 'imp. & p. p.', 'of Molest');
  INSERT INTO words (word, word_type, description) VALUES  (    'Molesting', 'p. pr. & vb. n.', 'of Molest');
  INSERT INTO words (word, word_type, description) VALUES  (    'Molest', 'v. t.', 'To trouble; to disturb; to render uneasy; to interfere\n   with; to vex.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Molest', 'n.', 'Molestation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Molestation', 'n.', 'The act of molesting, or the state of being molested;\n   disturbance; annoyance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Molester', 'n.', 'One who molests.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Molestful', 'a.', 'Troublesome; vexatious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Molestie', 'n.', 'Alt. of Molesty');
  INSERT INTO words (word, word_type, description) VALUES  (    'Molesty', 'n.', 'Molestation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Molewarp', 'n.', 'See Moldwarp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moliminous', 'a.', 'Of great bulk or consequence; very important.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moline', 'n.', 'The crossed iron that supports the upper millstone by\n   resting on the spindle; a millrind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Molinism', 'n.', 'The doctrines of the Molinists, somewhat resembling the\n   tenets of the Arminians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Molinist', 'n.', 'A follower of the opinions of Molina, a Spanish Jesuit\n   (in respect to grace); an opposer of the Jansenists.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moll', 'a.', 'Minor; in the minor mode; as, A moll, that is, A minor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mollah', 'n.', 'One of the higher order of Turkish judges; also, a Turkish\n   title of respect for a religious and learned man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Molle', 'a.', 'Lower by a semitone; flat; as, E molle, that is, E flat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mollebart', 'n.', 'An agricultural implement used in Flanders, consisting\n   of a kind of large shovel drawn by a horse and guided by a man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mollemoke', 'n.', 'Any one of several species of large pelagic petrels and\n   fulmars, as Fulmarus glacialis, of the North Atlantic, and several\n   species of Aestrelata, of the Southern Ocean. See Fulmar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mollient', 'a.', 'Serving to soften; assuaging; emollient.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Molliently', 'adv.', 'Assuagingly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mollifiable', 'a.', 'Capable of being mollified.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mollification', 'n.', 'The act of mollifying, or the state of being\n   mollified; a softening.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mollifier', 'n.', 'One who, or that which, mollifies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mollified', 'imp. & p. p.', 'of Mollify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mollifying', 'p. pr. & vb. n.', 'of Mollify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mollify', 'v. t.', 'To soften; to make tender; to reduce the hardness,\n   harshness, or asperity of; to qualify; as, to mollify the ground.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mollify', 'v. t.', 'To assuage, as pain or irritation, to appease, as\n   excited feeling or passion; to pacify; to calm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mollinet', 'n.', 'A little mill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mollipilose', 'a.', 'Having soft hairs; downy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mollities', 'n.', 'Unnatural softness of any organ or part.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mollitude', 'n.', 'Softness; effeminacy; weakness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mollusc', 'n.', 'Same as Mollusk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mollusca', 'n. pl.', 'One of the grand divisions of the animal kingdom,\n   including the classes Cephalopoda, Gastropoda, PteropodaScaphopoda, and\n   Lamellibranchiata, or Conchifera. These animals have an unsegmented\n   bilateral body, with most of the organs and parts paired, but not\n   repeated longitudinally. Most of them develop a mantle, which incloses\n   either a branchial or a pulmonary cavity. They are generally more or\n   less covered and protected by a calcareous shell, which may be\n   univalve, bivalve, or multivalve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Molluscan', 'a.', 'Of or pertaining to mollusks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Molluscan', 'n.', 'A mollusk; one of the Mollusca.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Molluscoid', 'a.', 'Resembling the true mollusks; belonging to the\n   Molluscoidea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Molluscoid', 'n.', 'One of the Molluscoidea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Molluscoidal', 'a.', 'Molluscoid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Molluscoidea', 'n. pl.', 'A division of Invertebrata which includes the\n   classes Brachiopoda and Bryozoa; -- called also Anthoid Mollusca.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Molluscous', 'a.', 'Molluscan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Molluscum', 'n.', 'A cutaneous disease characterized by numerous tumors, of\n   various forms, filled with a thick matter; -- so called from the\n   resemblance of the tumors to some molluscous animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mollusk', 'n.', 'One of the Mollusca.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Molly', 'n.', 'Same as Mollemoke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Molly', 'n.', 'A pet or colloquial name for Mary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Molly-mawk', 'n.', 'See Mollemoke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moloch', 'n.', 'The fire god of the Ammonites in Canaan, to whom human\n   sacrifices were offered; Molech. Also applied figuratively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moloch', 'n.', 'A spiny Australian lizard (Moloch horridus). The horns on\n   the head and numerous spines on the body give it a most formidable\n   appearance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Molosse', 'n.', 'See Molossus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Molosses', 'n.', 'Molasses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Molossine', 'n.', 'A bat of the genus Molossus, as the monk bat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Molossus', 'n.', 'A foot of three long syllables.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Molt', '', 'imp. of Melt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Molted', 'imp. & p. p.', 'of Moult');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moulted', '', 'of Moult');
  INSERT INTO words (word, word_type, description) VALUES  (    'Molting', 'p. pr. & vb. n.', 'of Moult');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moulting', '', 'of Moult');
  INSERT INTO words (word, word_type, description) VALUES  (    'Molt', 'v. t.', 'Alt. of Moult');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moult', 'v. t.', 'To shed or cast the hair, feathers, skin, horns, or the\n   like, as an animal or a bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Molt', 'v. t.', 'Alt. of Moult');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moult', 'v. t.', 'To cast, as the hair, skin, feathers, or the like; to\n   shed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Molt', 'n.', 'Alt. of Moult');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moult', 'n.', 'The act or process of changing the feathers, hair, skin,\n   etc.; molting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moltable', 'a.', 'Capable of assuming a molten state; meltable; fusible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Molten', 'a.', 'Melted; being in a state of fusion, esp. when the liquid\n   state is produced by a high degree of heat; as, molten iron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Molten', 'a.', 'Made by melting and casting the substance or metal of which\n   the thing is formed; as, a molten image.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Molto', 'adv.', 'Much; very; as, molto adagio, very slow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moly', 'n.', 'A fabulous herb of occult power, having a black root and\n   white blossoms, said by Homer to have been given by Hermes to Ulysses\n   to counteract the spells of Circe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moly', 'n.', 'A kind of garlic (Allium Moly) with large yellow flowers; --\n   called also golden garlic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Molybdate', 'n.', 'A salt of molybdic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Molybdena', 'n.', 'See Molybdenite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Molybdenite', 'n.', 'A mineral occurring in soft, lead-gray, foliated\n   masses or scales, resembling graphite; sulphide of molybdenum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Molybdenous', 'a.', 'See Molybdous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Molybdenum', 'n.', 'A rare element of the chromium group, occurring in\n   nature in the minerals molybdenite and wulfenite, and when reduced\n   obtained as a hard, silver-white, difficulty fusible metal. Symbol Mo.\n   Atomic weight 95.9.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Molybdic', 'a.', 'Of, pertaining to, or containing, molybdenum; specif.,\n   designating those compounds in which the element has a higher valence,\n   as contrasted with molybdous compounds; as, molybdic oxide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Molybdite', 'n.', 'Molybdic ocher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Molybdous', 'a.', 'Of, pertaining to, or containing, molybdenum; specif.,\n   designating those compounds in which molybdenum has a lower valence as\n   contrasted with molybdic compounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mome', 'n.', 'A dull, silent person; a blockhead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moment', 'n.', 'A minute portion of time; a point of time; an instant; as,\n   at thet very moment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moment', 'n.', 'Impulsive power; force; momentum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moment', 'n.', 'Importance, as in influence or effect; consequence; weight\n   or value; consideration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moment', 'n.', 'An essential element; a deciding point, fact, or\n   consideration; an essential or influential circumstance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moment', 'n.', 'An infinitesimal change in a varying quantity; an increment\n   or decrement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moment', 'n.', 'Tendency, or measure of tendency, to produce motion, esp.\n   motion about a fixed point or axis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Momental', 'a.', 'Lasting but a moment; brief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Momental', 'a.', 'Important; momentous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Momental', 'a.', 'Of or pertaining to moment or momentum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Momentally', 'adv.', 'For a moment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Momentaneous', 'a.', 'Alt. of Momentany');
  INSERT INTO words (word, word_type, description) VALUES  (    'Momentany', 'a.', 'Momentary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Momentarily', 'adv.', 'Every moment; from moment to moment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Momentariness', 'n.', 'The state or quality of being momentary; shortness\n   of duration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Momentary', 'a.', 'Done in a moment; continuing only a moment; lasting a\n   very short time; as, a momentary pang.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Momently', 'adv.', 'For a moment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Momently', 'adv.', 'In a moment; every moment; momentarily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Momentous', 'a.', 'Of moment or consequence; very important; weighty; as, a\n   momentous decision; momentous affairs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Momenta', 'pl. ', 'of Momentum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Momentums', 'pl. ', 'of Momentum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Momentum', 'n.', 'The quantity of motion in a moving body, being always\n   proportioned to the quantity of matter multiplied into the velocity;\n   impetus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Momentum', 'n.', 'Essential element, or constituent element.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Momier', 'n.', 'A name given in contempt to strict Calvinists in\n   Switzerland, France, and some parts of Germany, in the early part of\n   the 19th century.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mommery', 'n.', 'See Mummery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Momot', 'n.', 'See Motmot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Momus', 'n.', 'The god of mockery and censure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mon-', '', 'Same as Mono-.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mona', 'n.', 'A small, handsome, long-tailed West American monkey\n   (Cercopithecus mona). The body is dark olive, with a spot of white on\n   the haunches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monachal', 'a.', 'Of or pertaining to monks or a monastic life; monastic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monachism', 'n.', 'The system and influences of a monastic life;\n   monasticism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monacid', 'a.', 'Having one hydrogen atom replaceable by a negative or acid\n   atom or radical; capable of neutralizing a monobasic acid; -- said of\n   bases, and of certain metals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monad', 'n.', 'An ultimate atom, or simple, unextended point; something\n   ultimate and indivisible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monad', 'n.', 'The elementary and indestructible units which were conceived\n   of as endowed with the power to produce all the changes they undergo,\n   and thus determine all physical and spiritual phenomena.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monad', 'n.', 'One of the smallest flangellate Infusoria; esp., the species\n   of the genus Monas, and allied genera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monad', 'n.', 'A simple, minute organism; a primary cell, germ, or plastid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monad', 'n.', 'An atom or radical whose valence is one, or which can\n   combine with, be replaced by, or exchanged for, one atom of hydrogen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monadaria', 'n. pl.', 'The Infusoria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monadelphia', 'n. pl.', 'A Linnaean class of plants having the stamens\n   united into a tube, or ring, by the filaments, as in the Mallow family.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monadelphian', 'a.', 'Alt. of Monadelphous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monadelphous', 'a.', 'Of or pertaining to the Monadelphia; having the\n   stamens united in one body by the filaments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monadic', 'a.', 'Alt. of Monadical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monadical', 'a.', 'Of, pertaining to, or like, a monad, in any of its\n   senses. See Monad, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monadiform', 'a.', 'Having the form of a monad; resembling a monad in\n   having one or more filaments of vibratile protoplasm; as, monadiform\n   young.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monadology', 'n.', 'The doctrine or theory of monads.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monal', 'n.', 'Any Asiatic pheasant of the genus Lophophorus, as the\n   Impeyan pheasant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monamide', 'n.', 'An amido compound with only one amido group.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monamine', 'n.', 'A basic compound containing one amido group; as, methyl\n   amine is a monamine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monander', 'n.', 'One of the Monandria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monandria', 'n. pl.', 'A Linnaean class of plants embracing those having\n   but a single stamen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monandrian', 'a.', 'Same as Monandrous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monandric', 'a.', 'Of or pertaining to monandry; practicing monandry as a\n   system of marriage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monandrous', 'a.', 'Of or pertaining to the monandria; having but one\n   stamen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monandry', 'n.', 'The possession by a woman of only one husband at the same\n   time; -- contrasted with polyandry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monanthous', 'a.', 'Having but one flower; one-flowered.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monarch', 'n.', 'A sole or supreme ruler; a sovereign; the highest ruler;\n   an emperor, king, queen, prince, or chief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monarch', 'n.', 'One superior to all others of the same kind; as, an oak is\n   called the monarch of the forest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monarch', 'n.', 'A patron deity or presiding genius.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monarch', 'n.', 'A very large red and black butterfly (Danais Plexippus);\n   -- called also milkweed butterfly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monarch', 'a.', 'Superior to others; preeminent; supreme; ruling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monarchal', 'a.', 'Pertaining to a monarch; suiting a monarch; sovoreign;\n   regal; imperial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monarchess', 'n.', 'A female monarch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monarchial', 'a.', 'Monarchic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monarchian', 'n.', 'One of a sect in the early Christian church which\n   rejected the doctrine of the Trinity; -- called also patripassian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monarchic', 'a.', 'Alt. of Monarchical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monarchical', 'a.', 'Of or pertaining to a monarch, or to monarchy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monarchism', 'n.', 'The principles of, or preference for, monarchy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monarchist', 'n.', 'An advocate of, or believer in, monarchy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monarchized', 'imp. & p. p.', 'of Monarchize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monarchizing', 'p. pr. & vb. n.', 'of Monarchize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monarchize', 'v. i.', 'To play the sovereign; to act the monarch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monarchize', 'v. t.', 'To rule; to govern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monarchizer', 'n.', 'One who monarchizes; also, a monarchist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monarcho', 'n.', 'The nickname of a crackbrained Italian who fancied\n   himself an emperor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monarchies', 'pl. ', 'of Monarchy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monarchy', 'n.', 'A state or government in which the supreme power is\n   lodged in the hands of a monarch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monarchy', 'n.', 'A system of government in which the chief ruler is a\n   monarch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monarchy', 'n.', 'The territory ruled over by a monarch; a kingdom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monas', 'n.', 'A genus of minute flagellate Infusoria of which there are\n   many species, both free and attached. See Illust. under Monad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monasterial', 'a.', 'Of or pertaining to monastery, or to monastic life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monasteries', 'pl. ', 'of Monastery');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monastery', 'n.', 'A house of religious retirement, or of secusion from\n   ordinary temporal concerns, especially for monks; -- more rarely\n   applied to such a house for females.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monastic', 'n.', 'A monk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monastic', 'a.', 'Alt. of Monastical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monastical', 'a.', 'Of or pertaining to monasteries, or to their occupants,\n   rules, etc., as, monastic institutions or rules.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monastical', 'a.', 'Secluded from temporal concerns and devoted to\n   religion; recluse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monastically', 'adv.', 'In a monastic manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monasticism', 'n.', 'The monastic life, system, or condition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monasticon', 'n.', 'A book giving an account of monasteries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monatomic', 'adv.', 'Consisting of, or containing, one atom; as, the\n   molecule of mercury is monatomic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monatomic', 'adv.', 'Having the equivalence or replacing power of an atom\n   of hydrogen; univalent; as, the methyl radical is monatomic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monaxial', 'a.', 'Having only one axis; developing along a single line or\n   plane; as, monaxial development.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monazite', 'n.', 'A mineral occurring usually in small isolated crystals,\n   -- a phosphate of the cerium metals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monday', 'n.', 'The second day of the week; the day following Sunday.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monde', 'n.', 'The world; a globe as an ensign of royalty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mone', 'n.', 'The moon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mone', 'n.', 'A moan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monecian', 'a.', 'Alt. of Monecious');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monecious', 'a.', 'See Monoecian, and Monoecious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monembryony', 'n.', 'The condition of an ovule having but a single embryo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moner', 'n.', 'One of the Monera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monera', 'n. pl.', 'The lowest division of rhizopods, including those which\n   resemble the amoebas, but are destitute of a nucleus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moneral', 'a.', 'Of or pertaining to the Monera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moneran', 'a.', 'Of or pertaining to the Monera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moneran', 'n.', 'One of the Monera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monera', 'pl. ', 'of Moneron');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monerons', 'pl. ', 'of Moneron');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moneron', 'n.', 'One of the Monera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monerula', 'n.', 'A germ in that stage of development in which its form is\n   simply that of a non-nucleated mass of protoplasm. It precedes the\n   one-celled germ. So called from its likeness to a moner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monesia', 'n.', 'The bark, or a vegetable extract brought in solid cakes\n   from South America and believed to be derived from the bark, of the\n   tree Chrysophyllum glycyphloeum. It is used as an alterative and\n   astringent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monesin', 'n.', 'The acrid principle of Monesia, sometimes used as a\n   medicine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monest', 'v. t.', 'To warn; to admonish; to advise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monetary', 'a.', 'Of or pertaining to money, or consisting of money;\n   pecuniary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moneth', 'n.', 'A month.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monetization', 'n.', 'The act or process of converting into money, or of\n   adopting as money; as, the monetization of silver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monetize', 'v. t.', 'To convert into money; to adopt as current money; as,\n   to monetize silver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moneys', 'pl. ', 'of Money');
  INSERT INTO words (word, word_type, description) VALUES  (    'Money', 'n.', 'A piece of metal, as gold, silver, copper, etc., coined, or\n   stamped, and issued by the sovereign authority as a medium of exchange\n   in financial transactions between citizens and with government; also,\n   any number of such pieces; coin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Money', 'n.', 'Any written or stamped promise, certificate, or order, as a\n   government note, a bank note, a certificate of deposit, etc., which is\n   payable in standard coined money and is lawfully current in lieu of it;\n   in a comprehensive sense, any currency usually and lawfully employed in\n   buying and selling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Money', 'n.', 'In general, wealth; property; as, he has much money in land,\n   or in stocks; to make, or lose, money.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Money', 'v. t.', 'To supply with money.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moneyage', 'n.', 'A tax paid to the first two Norman kings of England to\n   prevent them from debashing the coin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moneyage', 'n.', 'Mintage; coinage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moneyed', 'adv.', 'Supplied with money; having money; wealthy; as, moneyey\n   men.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moneyed', 'adv.', 'Converted into money; coined.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moneyed', 'adv.', 'Consisting in, or composed of, money.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moneyer', 'n.', 'A person who deals in money; banker or broker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moneyer', 'n.', 'An authorized coiner of money.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moneyless', 'a.', 'Destitute of money; penniless; impecunious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Money-maker', 'n.', 'One who coins or prints money; also, a counterfeiter\n   of money.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Money-maker', 'n.', 'One who accumulates money or wealth; specifically, one\n   who makes money-getting his governing motive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Money-making', 'n.', 'The act or process of making money; the acquisition\n   and accumulation of wealth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Money-making', 'a.', 'Affording profitable returns; lucrative; as, a\n   money-making business.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Money-making', 'a.', 'Sussessful in gaining money, and devoted to that aim;\n   as, a money-making man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moneywort', 'n.', 'A trailing plant (Lysimachia Nummularia), with rounded\n   opposite leaves and solitary yellow flowers in their axils.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mongcorn', 'n.', 'See Mangcorn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monger', 'n.', 'A trader; a dealer; -- now used chiefly in composition; as,\n   fishmonger, ironmonger, newsmonger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monger', 'n.', 'A small merchant vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monger', 'v. t.', 'To deal in; to make merchandise of; to traffic in; --\n   used chiefly of discreditable traffic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mongol', 'n.', 'One of the Mongols.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mongol', 'a.', 'Of or pertaining to Mongolia or the Mongols.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mongolian', 'a.', 'Of or pertaining to Mongolia or the Mongols.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mongolian', 'n.', 'One of the Mongols.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mongolic', 'a.', 'See Mongolian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mongoloid', 'a.', 'Resembling a Mongol or the Mongols; having race\n   characteristics, such as color, hair, and features, like those of the\n   Mongols.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mongols', 'n. pl.', 'Alt. of Mongolians');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mongolians', 'n. pl.', 'One of the great races of man, including the\n   greater part of the inhabitants of China, Japan, and the interior of\n   Asia, with branches in Northern Europe and other parts of the world. By\n   some American Indians are considered a branch of the Mongols. In a more\n   restricted sense, the inhabitants of Mongolia and adjacent countries,\n   including the Burats and the Kalmuks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mongoose', 'n.', 'Alt. of Mongoos');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mongoos', 'n.', 'A species of ichneumon (Herpestes griseus), native of\n   India. Applied also to other allied species, as the African banded\n   mongoose (Crossarchus fasciatus).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mongrel', 'n.', 'The progeny resulting from a cross between two breeds, as\n   of domestic animals; anything of mixed breed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mongrel', 'a.', 'Not of a pure breed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mongrel', 'a.', 'Of mixed kinds; as, mongrel language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mongrelize', 'v. t. & i.', 'To cause to be mongrel; to cross breeds, so as\n   to produce mongrels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mongst', 'prep.', 'See Amongst.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monied', 'a.', 'See Moneyed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monifier', 'n.', 'A fossil fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moniliform', 'a.', 'Joined or constricted, at regular intervals, so as to\n   resemble a string of beads; as, a moniliform root; a moniliform\n   antenna. See Illust. of Antenna.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moniment', 'n.', 'Something to preserve memory; a reminder; a monument;\n   hence, a mark; an image; a superscription; a record.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monish', 'v. t.', 'To admonish; to warn. See Admonish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monisher', 'n.', 'One who monishes; an admonisher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monishment', 'n.', 'Admonition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monism', 'n.', 'That doctrine which refers all phenomena to a single\n   ultimate constituent or agent; -- the opposite of dualism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monism', 'n.', 'See Monogenesis, 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monist', 'n.', 'A believer in monism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monistic', 'a.', 'Of, pertaining to, or involving, monism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monition', 'n.', 'Instruction or advice given by way of caution; an\n   admonition; a warning; a caution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monition', 'n.', 'Information; indication; notice; advice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monition', 'n.', 'A process in the nature of a summons to appear and\n   answer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monition', 'n.', 'An order monishing a party complained against to obey\n   under pain of the law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monitive', 'a.', 'Conveying admonition; admonitory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monitor', 'n.', 'One who admonishes; one who warns of faults, informs of\n   duty, or gives advice and instruction by way of reproof or caution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monitor', 'n.', 'Hence, specifically, a pupil selected to look to the\n   school in the absence of the instructor, to notice the absence or\n   faults of the scholars, or to instruct a division or class.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monitor', 'n.', 'Any large Old World lizard of the genus Varanus; esp., the\n   Egyptian species (V. Niloticus), which is useful because it devours the\n   eggs and young of the crocodile. It is sometimes five or six feet long.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monitor', 'n.', 'An ironclad war vessel, very low in the water, and having\n   one or more heavily-armored revolving turrets, carrying heavy guns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monitor', 'n.', 'A tool holder, as for a lathe, shaped like a low turret,\n   and capable of being revolved on a vertical pivot so as to bring\n   successively the several tools in holds into proper position for\n   cutting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monitorial', 'a.', 'Of or pertaining to a monitor or monitors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monitorial', 'a.', 'Done or performed by a monitor; as, monitorial work;\n   conducted or taught by monitors; as, a monitorial school; monitorial\n   instruction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monitorially', 'adv.', 'In a monitorial manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monitorship', 'n.', 'The post or office of a monitor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monitory', 'a.', 'Giving admonition; instructing by way of caution;\n   warning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monitory', 'n.', 'Admonition; warning; especially, a monition proceeding\n   from an ecclesiastical court, but not addressed to any one person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monitress', 'n.', 'Alt. of Monitrix');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monitrix', 'n.', 'A female monitor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monk', 'n.', 'A man who retires from the ordinary temporal concerns of the\n   world, and devotes himself to religion; one of a religious community of\n   men inhabiting a monastery, and bound by vows to a life of chastity,\n   obedience, and poverty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monk', 'n.', 'A blotch or spot of ink on a printed page, caused by the ink\n   not being properly distributed. It is distinguished from a friar, or\n   white spot caused by a deficiency of ink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monk', 'n.', 'A piece of tinder made of agaric, used in firing the powder\n   hose or train of a mine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monk', 'n.', 'A South American monkey (Pithecia monachus); also applied to\n   other species, as Cebus xanthocephalus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monk', 'n.', 'The European bullfinch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monkeries', 'pl. ', 'of Monkery');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monkery', 'n.', 'The life of monks; monastic life; monastic usage or\n   customs; -- now usually applied by way of reproach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monkery', 'n.', 'A collective body of monks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monkeys', 'pl. ', 'of Monkey');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monkey', 'n.', 'In the most general sense, any one of the Quadrumana,\n   including apes, baboons, and lemurs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monkey', 'n.', 'Any species of Quadrumana, except the lemurs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monkey', 'n.', 'Any one of numerous species of Quadrumana (esp. such as\n   have a long tail and prehensile feet) exclusive of apes and baboons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monkey', 'n.', 'A term of disapproval, ridicule, or contempt, as for a\n   mischievous child.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monkey', 'n.', 'The weight or hammer of a pile driver, that is, a very\n   heavy mass of iron, which, being raised on high, falls on the head of\n   the pile, and drives it into the earth; the falling weight of a drop\n   hammer used in forging.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monkey', 'n.', 'A small trading vessel of the sixteenth century.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monkey', 'v. t. & i.', 'To act or treat as a monkey does; to ape; to act in\n   a grotesque or meddlesome manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monkey-bread', 'n.', 'The fruit of the Adansonia digitata; also, the tree.\n   See Adansonia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monkey-cup', 'n.', 'See Nepenthes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monkey-pot', 'n.', 'The fruit of two South American trees (Lecythis\n   Ollaria, and L. Zabucajo), which have for their fruit large,\n   pot-shaped, woody capsules containing delicious nuts, and opening\n   almost explosively by a circular lid at the top. Vases and pots are\n   made of this capsule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monkeys puzzle', '', 'A lofty coniferous Chilian tree (Araucaria\n   imbricata), the branches of which are so crowded and intertwisted \"as\n   to puzzle a monkey to climb.\" The edible nuts are over an inch long,\n   and are called pion by the Chilians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monkeytail', 'n.', 'A short, round iron bar or lever used in naval gunnery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monkfish', 'n.', 'The angel fish (Squatina).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monkfish', 'n.', 'The angler (Lophius).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monkflower', 'n.', 'A name of certain curious orchids which bear three\n   kinds of flowers formerly referred to three genera, but now ascertained\n   to be sexually different forms of the same genus (Catasetum\n   tridentatum, etc.).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monkhood', 'n.', 'The character or condition of a monk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monkhood', 'n.', 'Monks, regarded collectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monking', 'a.', 'Monkish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monkish', 'a.', 'Like a monk, or pertaining to monks; monastic; as, monkish\n   manners; monkish dress; monkish solitude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monkly', 'a.', 'Like, or suitable to, a monk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monkshood', 'n.', 'A plant of the genus Aconitum; aconite. See Aconite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monks seam', '', 'An extra middle seam made at the junction of two\n   breadths of canvas, ordinarily joined by only two rows of stitches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mono-', '', 'Alt. of Mon-');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mon-', '', 'A prefix signifying one, single, alone; as, monocarp, monopoly;\n   (Chem.) indicating that a compound contains one atom, radical, or group\n   of that to the name of which it is united; as, monoxide, monosulphide,\n   monatomic, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mono', 'n.', 'The black howler of Central America (Mycetes villosus).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monobasic', 'a.', 'Capable of being neutralized by a univalent base or\n   basic radical; having but one acid hydrogen atom to be replaced; --\n   said of acids; as, acetic, nitric, and hydrochloric acids are\n   monobasic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monocarbonic', 'a.', 'Containing one carboxyl group; as, acetic acid is a\n   monocarbonic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monocardian', 'a.', 'Having a single heart, as fishes and amphibians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monocardian', 'n.', 'An animal having a single heart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monocarp', 'n.', 'A monocarpic plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monocarpellary', 'a.', 'Consisting of a single carpel, as the fruit of the\n   pea, cherry, and almond.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monocarpic', 'a.', 'Alt. of Monocarpous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monocarpous', 'a.', 'Bearing fruit but once, and dying after\n   fructification, as beans, maize, mustard, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monocephalous', 'a.', 'Having a solitary head; -- said of unbranched\n   composite plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monoceros', 'n.', 'A one-horned creature; a unicorn; a sea monster with one\n   horn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monoceros', 'n.', 'The Unicorn, a constellation situated to the east Orion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monochlamydeous', 'a.', 'Having a single floral envelope, that is, a calyx\n   without a corolla, or, possibly, in rare cases, a corolla without a\n   calyx.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monochord', 'n.', 'An instrument for experimenting upon the mathematical\n   relations of musical sounds. It consists of a single string stretched\n   between two bridges, one or both of which are movable, and which stand\n   upon a graduated rule for the purpose of readily changing and measuring\n   the length of the part of the string between them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monochromatic', 'a.', 'Consisting of one color, or presenting rays of light\n   of one color only.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monochrome', 'n.', 'A painting or drawing in a single color; a picture made\n   with a single color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monochromic', 'a.', 'Made, or done, with a single color; as, a monochromic\n   picture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monochromy', 'n.', 'The art of painting or drawing in monochrome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monochronic', 'a.', 'Existing at the same time; contemporaneous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monociliated', 'a.', 'Having but one cilium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monocle', 'n.', 'An eyeglass for one eye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monoclinal', 'a.', 'Having one oblique inclination; -- applied to strata\n   that dip in only one direction from the axis of elevation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monocline', 'n.', 'A monoclinal fold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monoclinic', 'a.', 'Having one oblique intersection; -- said of that system\n   of crystallization in which the vertical axis is inclined to one, but\n   at right angles to the other, lateral axis. See Crystallization.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monoclinous', 'a.', 'Hermaphrodite, or having both stamens and pistils in\n   every flower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monocondyla', 'n. pl.', 'A group of vertebrates, including the birds and\n   reptiles, or those that have only one occipital condyle; the\n   Sauropsida.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monocotyl', 'n.', 'Any monocotyledonous plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monocotyle', 'a.', 'Monocotyledonous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monocotyledon', 'n.', 'A plant with only one cotyledon, or seed lobe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monocotyledonous', 'a.', 'Having only one cotyledon, seed lobe, or seminal\n   leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monocracy', 'n.', 'Government by a single person; undivided rule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monocrat', 'n.', 'One who governs alone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monocrotic', 'a.', 'Of, pertaining to, or showing, monocrotism; as, a\n   monocrotic pulse; a pulse of the monocrotic type.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monocrotism', 'n.', 'That condition of the pulse in which the pulse curve\n   or sphygmogram shows but a single crest, the dicrotic elevation\n   entirely disappearing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monocular', 'a.', 'Having only one eye; with one eye only; as, monocular\n   vision.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monocular', 'a.', 'Adapted to be used with only one eye at a time; as, a\n   monocular microscope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monocule', 'n.', 'A small crustacean with one median eye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monoculous', 'a.', 'Monocular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monocystic', 'a.', 'Of or pertaining to a division (Monocystidea) of\n   Gregarinida, in which the body consists of one sac.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monodactylous', 'a.', 'Having but one finger or claw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monodelph', 'n.', 'Alt. of Monodelphian');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monodelphian', 'n.', 'One of the Monodelphia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monodelphia', 'n. pl.', 'The group that includes all ordinary or placental\n   mammals; the Placentalia. See Mammalia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monodelphic', 'a.', 'Alt. of Monodelphous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monodelphous', 'a.', 'Of or pertaining to the Monodelphia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monodic', 'a.', 'Alt. of Monodical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monodical', 'a.', 'Belonging to a monody.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monodical', 'a.', 'For one voice; monophonic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monodical', 'a.', 'Homophonic; -- applied to music in which the melody is\n   confined to one part, instead of being shared by all the parts as in\n   the style called polyphonic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monodimetric', 'a.', 'Dimetric.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monodist', 'n.', 'A writer of a monody.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monodrama', 'n.', 'Alt. of Monodrame');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monodrame', 'n.', 'A drama acted, or intended to be acted, by a single\n   person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monodramatic', 'a.', 'Pertaining to a monodrama.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monodies', 'pl. ', 'of Monody');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monody', 'n.', 'A species of poem of a mournful character, in which a\n   single mourner expresses lamentation; a song for one voice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monodynamic', 'a.', 'Possessing but one capacity or power.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monodynamism', 'n.', 'The theory that the various forms of activity in\n   nature are manifestations of the same force.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monoecia', 'n. pl.', 'A Linnaean class of plants, whose stamens and pistils\n   are in distinct flowers in the same plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monoecian', 'a.', 'Of or pertaining to the Monoecia; monoecious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monoecian', 'n.', 'One of the Monoecia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monoecian', 'n.', 'A monoecious animal, as certain mollusks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monoecious', 'a.', 'Having the sexes united in one individual, as when male\n   and female flowers grow upon the same individual plant; hermaphrodite;\n   -- opposed to dioecious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monoecism', 'n.', 'The state or condition of being monoecious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monogam', 'n.', 'One of the Monogamia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monogamia', 'n. pl.', 'A Linnaean order of plants, having solitary flowers\n   with united anthers, as in the genus Lobelia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monogamian', 'a.', 'Alt. of Monogamic');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monogamic', 'a.', 'Pertaining to, or involving, monogamy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monogamic', 'a.', 'Of or pertaining to the Monogamia; having a simple\n   flower with united anthers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monogamist', 'n.', 'One who practices or upholds monogamy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monogamous', 'a.', 'Upholding, or practicing, monogamy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monogamous', 'a.', 'Same as Monogamian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monogamous', 'a.', 'Mating with but one of the opposite sex; -- said of\n   birds and mammals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monogamy', 'n.', 'Single marriage; marriage with but one person, husband or\n   wife, at the same time; -- opposed to polygamy. Also, one marriage only\n   during life; -- opposed to deuterogamy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monogamy', 'n.', 'State of being paired with a single mate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monogastric', 'a.', 'Having but a single stomach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monogenesis', 'n.', 'Oneness of origin; esp. (Biol.), development of all\n   beings in the universe from a single cell; -- opposed to polygenesis.\n   Called also monism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monogenesis', 'n.', 'That form of reproduction which requires but one\n   parent, as in reproduction by fission or in the formation of buds,\n   etc., which drop off and form new individuals; asexual reproduction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monogenesis', 'n.', 'The direct development of an embryo, without\n   metamorphosis, into an organism similar to the parent organism; --\n   opposed to metagenesis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monogenetic', 'a.', 'One in genesis; resulting from one process of\n   formation; -- used of a mountain range.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monogenetic', 'a.', 'Relating to, or involving, monogenesis; as, the\n   monogenetic school of physiologists, who admit but one cell as the\n   source of all beings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monogenic', 'a.', 'Of or pertaining to monogenesis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monogenic', 'a.', 'Producing only one kind of germs, or young; developing\n   only in one way.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monogenism', 'n.', 'The theory or doctrine that the human races have a\n   common origin, or constitute a single species.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monogenist', 'n.', 'One who maintains that the human races are all of one\n   species; -- opposed to polygenist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monogenistic', 'a.', 'Monogenic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monogenous', 'a.', 'Of or pertaining to monogenesis; as, monogenous, or\n   asexual, reproduction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monogeny', 'n.', 'Monogenesis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monogeny', 'n.', 'The doctrine that the members of the human race have all\n   a common origin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monogoneutic', 'a.', 'Having but one brood in a season.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monogram', 'n.', 'A character or cipher composed of two or more letters\n   interwoven or combined so as to represent a name, or a part of it\n   (usually the initials). Monograms are often used on seals, ornamental\n   pins, rings, buttons, and by painters, engravers, etc., to distinguish\n   their works.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monogram', 'n.', 'A picture in lines; a sketch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monogram', 'n.', 'An arbitrary sign for a word.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monogrammal', 'a.', 'See Monogrammic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monogrammatic', 'a.', 'Monogrammic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monogrammic', 'a.', 'Of, pertaining to, or resembling, a monogram.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monogrammous', 'a.', 'Monogrammic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monograph', 'n.', 'A written account or description of a single thing, or\n   class of things; a special treatise on a particular subject of limited\n   range.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monographer', 'n.', 'A writer of a monograph.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monographic', 'a.', 'Alt. of Monographical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monographical', 'a.', 'Of or pertaining to a monograph, or to a monography;\n   as, a monographic writing; a monographic picture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monographist', 'n.', 'One who writes a monograph.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monographous', 'a.', 'Monographic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monography', 'n.', 'Representation by lines without color; an outline\n   drawing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monography', 'n.', 'A monograph.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monogyn', 'n.', 'One of the Monogynia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monogynia', 'n. pl.', 'A Linnaean order of plants, including those which\n   have only one style or stigma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monogynian', 'a.', 'Pertaining to the Monogynia; monogynous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monogynian', 'n.', 'One of the Monogynia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monogynous', 'a.', 'Of or pertaining to Monogynia; having only one style or\n   stigma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monogyny', 'n.', 'Marriage with the one woman only.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monogyny', 'n.', 'The state or condition of being monogynous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monohemerous', 'a.', 'Lasting but one day.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monoicous', 'a.', 'Monoecious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monolatry', 'n.', 'Worship of a single deity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monolith', 'n.', 'A single stone, especially one of large size, shaped into\n   a pillar, statue, or monument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monolithal', 'a.', 'Monolithic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monolithic', 'a.', 'Of or pertaining to a monolith; consisting of a single\n   stone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monologist', 'n.', 'One who soliloquizes; esp., one who monopolizes\n   conversation in company.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monologue', 'n.', 'A speech uttered by a person alone; soliloquy; also,\n   talk or discourse in company, in the strain of a soliloquy; as, an\n   account in monologue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monologue', 'n.', 'A dramatic composition for a single performer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monology', 'n.', 'The habit of soliloquizing, or of monopolizing\n   conversation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monomachia', 'n.', 'Alt. of Monomachy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monomachy', 'n.', 'A duel; single combat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monomachist', 'n.', 'One who fights in single combat; a duelist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monomane', 'n.', 'A monomaniac.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monomania', 'n.', 'Derangement of the mind in regard of a single subject\n   only; also, such a concentration of interest upon one particular\n   subject or train of ideas to show mental derangement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monomaniac', 'n.', 'A person affected by monomania.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monomaniac', 'a.', 'Alt. of Monomaniacal');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monomaniacal', 'a.', 'Affected with monomania, or partial derangement of\n   intellect; caused by, or resulting from, monomania; as, a monomaniacal\n   delusion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monome', 'n.', 'A monomial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monomerous', 'a.', 'Composed of solitary parts, as a flower with one sepal,\n   one petal, one stamen, and one pistil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monomerous', 'a.', 'Having but one joint; -- said of the foot of certain\n   insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monometallic', 'a.', 'Consisting of one metal; of or pertaining to\n   monometallism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monometallism', 'n.', 'The legalized use of one metal only, as gold, or\n   silver, in the standard currency of a country, or as a standard of\n   money values. See Bimetallism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monometallist', 'n.', 'One who believes in monometallism as opposed to\n   bimetallism, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monometer', 'n.', 'A rhythmic series, consisting of a single meter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monometric', 'a.', 'Same as Isometric.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monomial', 'n.', 'A single algebraic expression; that is, an expression\n   unconnected with any other by the sign of addition, substraction,\n   equality, or inequality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monomial', 'a.', 'Consisting of but a single term or expression.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monomorphic', 'a.', 'Alt. of Monomorphous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monomorphous', 'a.', 'Having but a single form; retaining the same form\n   throughout the various stages of development; of the same or of an\n   essentially similar type of structure; -- opposed to dimorphic,\n   trimorphic, and polymorphic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monomphalus', 'n.', 'A form of double monster, in which two individuals are\n   united by a common umbilicus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monomya', 'n.pl.', 'Alt. of Monomyaria');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monomyaria', 'n.pl.', 'An order of lamellibranchs having but one muscle for\n   closing the shell, as the oyster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monomyarian', 'a.', 'Alt. of Monomyary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monomyary', 'a.', 'Of or pertaining to the Monomya.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monomyary', 'n.', 'One of the Monomya.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mononomial', 'n. & a.', 'Monomyal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monoousian', 'a.', 'Alt. of Monoousious');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monoousious', 'a.', 'Having but one and the same nature or essence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monopathy', 'n.', 'Suffering or sensibility in a single organ or function.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monopersonal', 'a.', 'Having but one person, or form of existence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monopetalous', 'a.', 'Having only one petal, or the corolla in one piece,\n   or composed of petals cohering so as to form a tube or bowl;\n   gamopetalous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monophanous', 'a.', 'Having one and the same appearance; having a mutual\n   resemblance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monophonic', 'a.', 'Single-voiced; having but one part; as, a monophonic\n   composition; -- opposed to polyphonic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monophthong', 'n.', 'A single uncompounded vowel sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monophthong', 'n.', 'A combination of two written vowels pronounced as one;\n   a digraph.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monophthongal', 'a.', 'Consisting of, or pertaining to, a monophthong.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monophyletic', 'a.', 'Of or pertaining to a single family or stock, or to\n   development from a single common parent form; -- opposed to\n   polyphyletic; as, monophyletic origin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monophyllous', 'a.', 'One-leaved; composed of a single leaf; as, a\n   monophyllous involucre or calyx.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monophyodont', 'a.', 'Having but one set of teeth; -- opposed to\n   diphyodont.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monophysite', 'n.', 'One of a sect, in the ancient church, who maintained\n   that the human and divine in Jesus Christ constituted but one composite\n   nature. Also used adjectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monophysitical', 'a.', 'Of or pertaining to Monophysites, or their\n   doctrines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monoplast', 'n.', 'A monoplastic element.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monoplastic', 'a.', 'That has one form, or retains its primary form, as, a\n   monoplastic element.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monoplegia', 'n.', 'Paralysis affecting a single limb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monopneumona', 'n. pl.', 'A suborder of Dipnoi, including the Ceratodus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monopode', 'n.', 'One of a fabulous tribe or race of Ethiopians having but\n   one leg and foot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monopode', 'n.', 'A monopodium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monopodial', 'a.', 'Having a monopodium or a single and continuous axis, as\n   a birchen twig or a cornstalk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monopodia', 'pl. ', 'of Monopodium');
  INSERT INTO words (word, word_type, description) VALUES  (    '-ums', 'pl. ', 'of Monopodium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monopodium', 'n.', 'A single and continuous vegetable axis; -- opposed to\n   sympodium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monopody', 'n.', 'A measure of but a single foot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monopoler', 'n.', 'A monopolist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monopolist', 'n.', 'One who monopolizes; one who has a monopoly; one who\n   favors monopoly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monopolistic', 'a.', 'Of or pertaining to a monopolist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monopolite', 'n.', 'A monopolist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monopolized', 'imp. & p. p.', 'of Monopolize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monopolizing', 'p. pr. & vb. n.', 'of Monopolize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monopolize', 'v. t.', 'To acquire a monopoly of; to have or get the\n   exclusive privilege or means of dealing in, or the exclusive possession\n   of; to engross the whole of; as, to monopolize the coffee trade; to\n   monopolize land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monopolizer', 'n.', 'One who monopolizes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monopolies', 'pl. ', 'of Monopoly');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monopoly', 'n.', 'The exclusive power, or privilege of selling a commodity;\n   the exclusive power, right, or privilege of dealing in some article, or\n   of trading in some market; sole command of the traffic in anything,\n   however obtained; as, the proprietor of a patented article is given a\n   monopoly of its sale for a limited time; chartered trading companies\n   have sometimes had a monopoly of trade with remote regions; a\n   combination of traders may get a monopoly of a particular product.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monopoly', 'n.', 'Exclusive possession; as, a monopoly of land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monopoly', 'n.', 'The commodity or other material thing to which the\n   monopoly relates; as, tobacco is a monopoly in France.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monopolylogue', 'n.', 'An exhibition in which an actor sustains many\n   characters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monopsychism', 'n.', 'The doctrine that there is but one immortal soul or\n   intellect with which all men are endowed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monopteral', 'a.', 'Round and without a cella; consisting of a single ring\n   of columns supporting a roof; -- said esp. of a temple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monoptera', 'pl. ', 'of Monopteron');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monopteron', 'n.', 'A circular temple consisting of a roof supported on\n   columns, without a cella.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monoptote', 'n.', 'A noun having only one case.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monoptote', 'n.', 'A noun having only one ending for the oblique cases.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monopyrenous', 'a.', 'Having but a single stone or kernel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monorganic', 'a.', 'Belonging to, or affecting, a single organ, or set of\n   organs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monorhina', 'n. pl.', 'The Marsipobranchiata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monorhyme', 'n.', 'A composition in verse, in which all the lines end with\n   the same rhyme.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monosepalous', 'a.', 'Having only one sepal, or the calyx in one piece or\n   composed of the sepals united into one piece; gamosepalous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monosperm', 'n.', 'A monospermous plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monospermal', 'a.', 'Alt. of Monospermous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monospermous', 'a.', 'Having only one seed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monospherical', 'a.', 'Consisting of one sphere only.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monostich', 'n.', 'A composition consisting of one verse only.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monostichous', 'a.', 'Arranged in a single row on one side of an axis, as\n   the flowers in grasses of the tribe Chloridae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monostrophe', 'n.', 'A metrical composition consisting of a single strophe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monostrophic', 'a.', 'Having one strophe only; not varied in measure;\n   written in unvaried measure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monosulphide', 'n.', 'A sulphide containing one atom of sulphur, and\n   analogous to a monoxide; -- contrasted with a polysulphide; as, galena\n   is a monosulphide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monosulphuret', 'n.', 'See Monosulphide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monosyllabic', 'a.', 'Being a monosyllable, or composed of monosyllables;\n   as, a monosyllabic word; a monosyllabic language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monosyllabism', 'n.', 'The state of consisting of monosyllables, or having\n   a monosyllabic form; frequent occurrence of monosyllables.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monosyllable', 'n.', 'A word of one syllable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monosyllabled', 'a.', 'Formed into, or consisting of, monosyllables.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monosymmetric', 'a.', 'Alt. of Monosymmetrical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monosymmetrical', 'a.', 'Same as Monoclinic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monotessaron', 'n.', 'A single narrative framed from the statements of the\n   four evangelists; a gospel harmony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monothalama', 'n. pl.', 'A division of Foraminifera including those that\n   have only one chamber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monothalaman', 'n.', 'A foraminifer having but one chamber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monothalamous', 'a.', 'One-chambered.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monothalmic', 'a.', 'Formed from one pistil; -- said of fruits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monothecal', 'a.', 'Having a single loculament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monotheism', 'n.', 'The doctrine or belief that there is but one God.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monotheist', 'n.', 'One who believes that there is but one God.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monotheistic', 'a.', 'Of or pertaining to monotheism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monothelism', 'n.', 'Alt. of Monothelitism');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monothelitism', 'n.', 'The doctrine of the Monothelites.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monothelite', 'n.', 'One of an ancient sect who held that Christ had but\n   one will as he had but one nature. Cf. Monophysite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monothelitic', 'a.', 'Of or pertaining to the Monothelites, or their\n   doctrine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monotocous', 'a.', 'Bearing fruit but once; monocarpic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monotocous', 'a.', 'Uniparous; laying a single egg.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monotomous', 'a.', 'Having a distinct cleavage in a single direction only.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monotone', 'n.', 'A single unvaried tone or sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monotone', 'n.', 'The utterance of successive syllables, words, or\n   sentences, on one unvaried key or line of pitch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monotonic', 'a.', 'Alt. of Monotonical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monotonical', 'a.', 'Of, pertaining to, or uttered in, a monotone;\n   monotonous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monotonist', 'n.', 'One who talks in the same strain or on the same subject\n   until weariness is produced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monotonous', 'a.', 'Uttered in one unvarying tone; continued with dull\n   uniformity; characterized by monotony; without change or variety;\n   wearisome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monotony', 'n.', 'A frequent recurrence of the same tone or sound,\n   producing a dull uniformity; absence of variety, as in speaking or\n   singing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monotony', 'n.', 'Any irksome sameness, or want of variety.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monotremata', 'n. pl.', 'A subclass of Mammalia, having a cloaca in which\n   the ducts of the urinary, genital, and alimentary systems terminate, as\n   in birds. The female lays eggs like a bird. See Duck mole, under Duck,\n   and Echidna.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monotrematous', 'a.', 'Of or pertaining to the Monotremata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monotreme', 'n.', 'One of the Monotremata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monotriglyph', 'n.', 'A kind of intercolumniation in an entablature, in\n   which only one triglyph and two metopes are introduced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monotropa', 'n.', 'A genus of parasitic or saprophytic plants including the\n   Indian pipe and pine sap. The name alludes to the dropping end of the\n   stem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monotype', 'a.', 'Alt. of Monotypic');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monotypic', 'a.', 'Having but one type; containing but one representative;\n   as, a monotypic genus, which contains but one species.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monovalent', 'a.', 'Having a valence of one; univalent. See Univalent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monoxide', 'n.', 'An oxide containing one atom of oxygen in each molecule;\n   as, barium monoxide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monoxylon', 'n.', 'A canoe or boat made from one piece of timber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monoxylous', 'a.', 'Made of one piece of wood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monozoa', 'n. pl.', 'A division of Radiolaria; -- called also Monocyttaria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monroe doctrine', '', 'See under Doctrine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Messeigneurs', 'pl. ', 'of Monseigneur');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monseigneur', 'n.', 'My lord; -- a title in France of a person of high\n   birth or rank; as, Monseigneur the Prince, or Monseigneur the\n   Archibishop. It was given, specifically, to the dauphin, before the\n   Revolution of 1789. (Abbrev. Mgr.)');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monsels salt', '', 'A basic sulphate of iron; -- so named from Monsel, a\n   Frenchman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monsels solution', '', 'An aqueous solution of Monsels salt, having\n   valuable styptic properties.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Messieurs', 'pl. ', 'of Monsieur');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monsieur', 'n.', 'The common title of civility in France in speaking to, or\n   of, a man; Mr. or Sir.');
  INSERT INTO words (word, word_type, description) VALUES  (    'MM.', 'pl. ', 'of Monsieur');
  INSERT INTO words (word, word_type, description) VALUES  (    'Messrs.', 'pl. ', 'of Monsieur');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monsieur', 'n.', 'The oldest brother of the king of France.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monsieur', 'n.', 'A Frenchman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monsignors', 'pl. ', 'of Monsignore');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monsignore', 'n.', 'My lord; -- an ecclesiastical dignity bestowed by the\n   pope, entitling the bearer to social and domestic rank at the papal\n   court. (Abbrev. Mgr.)');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monsoon', 'n.', 'A wind blowing part of the year from one direction,\n   alternating with a wind from the opposite direction; -- a term applied\n   particularly to periodical winds of the Indian Ocean, which blow from\n   the southwest from the latter part of May to the middle of September,\n   and from the northeast from about the middle of October to the middle\n   of December.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monster', 'n.', 'Something of unnatural size, shape, or quality; a prodigy;\n   an enormity; a marvel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monster', 'n.', 'Specifically , an animal or plant departing greatly from\n   the usual type, as by having too many limbs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monster', 'n.', 'Any thing or person of unnatural or excessive ugliness,\n   deformity, wickedness, or cruelty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monster', 'a.', 'Monstrous in size.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monster', 'v. t.', 'To make monstrous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monstrance', 'n.', 'A transparent pyx, in which the consecrated host is\n   exposed to view.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monstration', 'n.', 'The act of demonstrating; proof.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monstrosities', 'pl. ', 'of Monstrosity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monstrosity', 'n.', 'The state of being monstrous, or out of the common\n   order of nature; that which is monstrous; a monster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monstrous', 'a.', 'Marvelous; strange.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monstrous', 'a.', 'Having the qualities of a monster; deviating greatly\n   from the natural form or character; abnormal; as, a monstrous birth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monstrous', 'a.', 'Extraordinary in a way to excite wonder, dislike,\n   apprehension, etc.; -- said of size, appearance, color, sound, etc.;\n   as, a monstrous height; a monstrous ox; a monstrous story.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monstrous', 'a.', 'Extraordinary on account of ugliness, viciousness, or\n   wickedness; hateful; horrible; dreadful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monstrous', 'a.', 'Abounding in monsters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monstrous', 'adv.', 'Exceedingly; very; very much.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monstrously', 'adv.', 'In a monstrous manner; unnaturally; extraordinarily;\n   as, monstrously wicked.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monstrousness', 'n.', 'The state or quality of being monstrous, unusual,\n   extraordinary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monstruosity', 'n.', 'Monstrosity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monstruous', 'a.', 'Monstrous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mont', 'n.', 'Mountain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Montaigne', 'n.', 'A mountain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Montanic', 'n.', 'Of or pertaining to mountains; consisting of mountains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Montanist', 'n.', 'A follower of Mintanus, a Phrygian enthusiast of the\n   second century, who claimed that the Holy Spirit, the Paraclete, dwelt\n   in him, and employed him as an instrument for purifying and guiding men\n   in the Christian life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Montant', 'n.', 'An upward thrust or blow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Montant', 'n.', 'An upright piece in any framework; a mullion or muntin; a\n   stile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mont de piete', '', 'One of certain public pawnbroking establishments which\n   originated in Italy in the 15th century, the object of which was to\n   lend money at a low rate of interest to poor people in need; -- called\n   also mount of piety. The institution has been adopted in other\n   countries, as in Spain and France. See Lombard-house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monte', 'n.', 'A favorite gambling game among Spaniards, played with dice\n   or cards.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monte-acid', 'n.', 'An acid elevator, as a tube through which acid is\n   forced to some height in a sulphuric acid manufactory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monteith', 'n.', 'See Monteth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Montem', 'n.', 'A custom, formerly practiced by the scholars at Eton\n   school, England, of going every third year, on Whittuesday, to a\n   hillock near the Bath road, and exacting money from all passers-by, to\n   support at the university the senior scholar of the school.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Montero', 'n.', 'An ancient kind of cap worn by horsemen or huntsmen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monteth', 'n.', 'Alt. of Monteith');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monteith', 'n.', 'A vessel in which glasses are washed; -- so called from\n   the name of the inventor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Montgolfier', 'n.', 'A balloon which ascends by the buoyancy of air heated\n   by a fire; a fire balloon; -- so called from two brothers, Stephen and\n   Joseph Montgolfier, of France, who first constructed and sent up a fire\n   balloon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Month', 'n.', 'One of the twelve portions into which the year is divided;\n   the twelfth part of a year, corresponding nearly to the length of a\n   synodic revolution of the moon, -- whence the name. In popular use, a\n   period of four weeks is often called a month.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monthling', 'n.', 'That which is a month old, or which lives for a month.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monthly', 'a.', 'Continued a month, or a performed in a month; as, the\n   monthly revolution of the moon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monthly', 'a.', 'Done, happening, payable, published, etc., once a month,\n   or every month; as, a monthly visit; monthly charges; a monthly\n   installment; a monthly magazine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monthlies', 'pl. ', 'of Monthly');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monthly', 'n.', 'A publication which appears regularly once a month.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monthly', 'adv.', 'Once a month; in every month; as, the moon changes\n   monthly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monthly', 'adv.', 'As if under the influence of the moon; in the manner of\n   a lunatic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monticle', 'n.', 'A little mount; a hillock; a small elevation or\n   prominence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monticulate', 'a.', 'Furnished with monticles or little elevations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monticule', 'n.', 'See Monticle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monticulous', 'a.', 'Monticulate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Montiform', 'a.', 'Resembling a mountain in form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Montigenous', 'a.', 'Produced on a mountain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Montoir', 'n.', 'A stone used in mounting a horse; a horse block.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monton', 'n.', 'A heap of ore; a mass undergoing the process of\n   amalgamation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Montross', 'n.', 'See Matross.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Montrue', 'n.', 'That on which anything is mounted; a setting; hence, a\n   saddle horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monument', 'n.', 'Something which stands, or remains, to keep in\n   remembrance what is past; a memorial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monument', 'n.', 'A building, pillar, stone, or the like, erected to\n   preserve the remembrance of a person, event, action, etc.; as, the\n   Washington monument; the Bunker Hill monument. Also, a tomb, with\n   memorial inscriptions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monument', 'n.', 'A stone or other permanent object, serving to indicate a\n   limit or to mark a boundary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monument', 'n.', 'A saying, deed, or example, worthy of record.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monumental', 'a.', 'Of, pertaining to, or suitable for, a monument; as, a\n   monumental inscription.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monumental', 'a.', 'Serving as a monument; memorial; preserving memory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monumentally', 'adv.', 'By way of memorial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monumentally', 'adv.', 'By means of monuments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Monureid', 'n.', 'Any one of a series of complex nitrogenous substances\n   regarded as derived from one molecule of urea; as, alloxan is a\n   monureid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moo', 'adv., & n.', 'See Mo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mooed', 'imp. & p. p.', 'of Moo');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mooing', 'p. pr. & vb. n.', 'of Moo');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moo', 'v. i.', 'To make the noise of a cow; to low; -- childs word.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moo', 'n.', 'The lowing of a cow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mood', 'n.', 'Manner; style; mode; logical form; musical style; manner of\n   action or being. See Mode which is the preferable form).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mood', 'n.', 'Manner of conceiving and expressing action or being, as\n   positive, possible, hypothetical, etc., without regard to other\n   accidents, such as time, person, number, etc.; as, the indicative mood;\n   the infinitive mood; the subjunctive mood. Same as Mode.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mood', 'n.', 'Temper of mind; temporary state of the mind in regard to\n   passion or feeling; humor; as, a melancholy mood; a suppliant mood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mooder', 'n.', 'Mother.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moodily', 'adv.', 'In a moody manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moodiness', 'n.', 'The quality or state of being moody; specifically,\n   liability to strange or violent moods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moodir', 'n.', 'The governor of a province in Egypt, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moodish', 'a.', 'Moody.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moodishly', 'adv.', 'Moodily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moody', 'superl.', 'Subject to varying moods, especially to states of mind\n   which are unamiable or depressed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moody', 'superl.', 'Hence: Out of humor; peevish; angry; fretful; also,\n   abstracted and pensive; sad; gloomy; melancholy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moolah', 'n.', 'Alt. of Moollah');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moollah', 'n.', 'See Mollah.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moolley', 'n.', 'Same as Mulley.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moon', 'n.', 'The celestial orb which revolves round the earth; the\n   satellite of the earth; a secondary planet, whose light, borrowed from\n   the sun, is reflected to the earth, and serves to dispel the darkness\n   of night. The diameter of the moon is 2,160 miles, its mean distance\n   from the earth is 240,000 miles, and its mass is one eightieth that of\n   the earth. See Lunar month, under Month.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moon', 'n.', 'A secondary planet, or satellite, revolving about any member\n   of the solar system; as, the moons of Jupiter or Saturn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moon', 'n.', 'The time occupied by the moon in making one revolution in her\n   orbit; a month.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moon', 'n.', 'A crescentlike outwork. See Half-moon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mooned', 'imp. & p. p.', 'of Moon');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mooning', 'p. pr. & vb. n.', 'of Moon');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moon', 'v. t.', 'To expose to the rays of the moon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moon', 'v. i.', 'To act if moonstruck; to wander or gaze about in an\n   abstracted manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moonbeam', 'n.', 'A ray of light from the moon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moonblind', 'a.', 'Dim-sighted; purblind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moonblink', 'n.', 'A temporary blindness, or impairment of sight, said to\n   be caused by sleeping in the moonlight; -- sometimes called nyctalopia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mooncalf', 'n.', 'A monster; a false conception; a mass of fleshy matter,\n   generated in the uterus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mooncalf', 'n.', 'A dolt; a stupid fellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moon-culminating', 'a.', 'Culminating, or coming to the meredian, at or\n   about the same time with the moon; -- said of a star or stars, esp. of\n   certain stars selected beforehand, and named in an ephemeris (as the\n   Nautical Almanac), as suitable to be observed in connection with the\n   moon at culmination, for determining terrestrial longitude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mooned', 'a.', 'Of or resembling the moon; symbolized by the moon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mooner', 'n.', 'One who abstractedly wanders or gazes about, as if\n   moonstruck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moonery', 'n.', 'Conduct of one who moons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moonet', 'n.', 'A little moon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moon-eye', 'n.', 'A eye affected by the moon; also, a disease in the eye of\n   a horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moon-eye', 'n.', 'Any species of American fresh-water fishes of the genus\n   Hyodon, esp. H. tergisus of the Great Lakes and adjacent waters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moon-eye', 'n.', 'The cisco.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moon-eyed', 'a.', 'Having eyes affected by the moon; moonblind; dim-eyed;\n   purblind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moon-faced', 'a.', 'Having a round, full face.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moonfish', 'n.', 'An American marine fish (Vomer setipennis); -- called\n   also bluntnosed shiner, horsefish, and sunfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moonfish', 'n.', 'A broad, thin, silvery marine fish (Selene vomer); --\n   called also lookdown, and silver moonfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moonfish', 'n.', 'The mola. See Sunfish, 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moonflower', 'n.', 'The oxeye daisy; -- called also moon daisy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moonflower', 'n.', 'A kind of morning glory (Ipomoea Bona-nox) with large\n   white flowers opening at night.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moong', 'n.', 'Same as Mung.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moonglade', 'n.', 'The bright reflection of the moons light on an expanse\n   of water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moonie', 'n.', 'The European goldcrest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moonish', 'a.', 'Like the moon; variable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moonless', 'a.', 'Being without a moon or moonlight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moonlight', 'n.', 'The light of the moon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moonlight', 'a.', 'Occurring during or by moonlight; characterized by\n   moonlight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moonling', 'n.', 'A simpleton; a lunatic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moonlit', 'a.', 'Illumined by the moon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moonraker', 'n.', 'Same as Moonsail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moonrise', 'n.', 'The rising of the moon above the horizon; also, the time\n   of its rising.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moonsail', 'n.', 'A sail sometimes carried in light winds, above a skysail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moonseed', 'n.', 'A climbing plant of the genus Menispermum; -- so called\n   from the crescentlike form of the seeds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moonset', 'n.', 'The descent of the moon below the horizon; also, the time\n   when the moon sets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moonshee', 'n.', 'A Mohammedan professor or teacher of language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moonshine', 'n.', 'The light of the moon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moonshine', 'n.', 'Hence, show without substance or reality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moonshine', 'n.', 'A month.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moonshine', 'n.', 'A preparation of eggs for food.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moonshine', 'a.', 'Moonlight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moonshiner', 'n.', 'A person engaged in illicit distilling; -- so called\n   because the work is largely done at night.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moonshiny', 'a.', 'Moonlight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moonstone', 'n.', 'A nearly pellucid variety of feldspar, showing pearly or\n   opaline reflections from within. It is used as a gem. The best\n   specimens come from Ceylon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moonsticken', 'a.', 'See Moonstruck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moonstruck', 'a.', 'Mentally affected or deranged by the supposed influence\n   of the moon; lunatic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moonstruck', 'a.', 'Produced by the supposed influence of the moon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moonstruck', 'a.', 'Made sick by the supposed influence of the moon, as a\n   human being; made unsuitable for food, as fishes, by such supposed\n   influence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moonwort', 'n.', 'The herb lunary or honesty. See Honesty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moonwort', 'n.', 'Any fern of the genus Botrychium, esp. B. Lunaria; -- so\n   named from the crescent-shaped segments of its frond.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moony', 'a.', 'Of or pertaining to the moon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moony', 'a.', 'Furnished with a moon; bearing a crescent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moony', 'a.', 'Silly; weakly sentimental.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moor', 'n.', 'One of a mixed race inhabiting Morocco, Algeria, Tunis, and\n   Tripoli, chiefly along the coast and in towns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moor', 'n.', 'Any individual of the swarthy races of Africa or Asia which\n   have adopted the Mohammedan religion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moor', 'n.', 'An extensive waste covered with patches of heath, and having\n   a poor, light soil, but sometimes marshy, and abounding in peat; a\n   heath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moor', 'n.', 'A game preserve consisting of moorland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moored', 'imp. & p. p.', 'of Moor');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mooring', 'p. pr. & vb. n.', 'of Moor');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moor', 'v. t.', 'To fix or secure, as a vessel, in a particular place by\n   casting anchor, or by fastening with cables or chains; as, the vessel\n   was moored in the stream; they moored the boat to the wharf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moor', 'v. t.', 'Fig.: To secure, or fix firmly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moor', 'v. i.', 'To cast anchor; to become fast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moorage', 'n.', 'A place for mooring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moorball', 'n.', 'A fresh-water alga (Cladophora Aegagropila) which forms a\n   globular mass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moorband', 'n.', 'See Moorpan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mooress', 'n.', 'A female Moor; a Moorish woman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mooring', 'n.', 'The act of confining a ship to a particular place, by\n   means of anchors or fastenings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mooring', 'n.', 'That which serves to confine a ship to a place, as\n   anchors, cables, bridles, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mooring', 'n.', 'The place or condition of a ship thus confined.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moorish', 'a.', 'Having the characteristics of a moor or heath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moorish', 'a.', 'Of or pertaining to Morocco or the Moors; in the style of\n   the Moors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moorland', 'n.', 'Land consisting of a moor or moors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moorpan', 'n.', 'A clayey layer or pan underlying some moors, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moorstone', 'n.', 'A species of English granite, used as a building stone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mooruk', 'n.', 'A species of cassowary (Casuarius Bennetti) found in New\n   Britain, and noted for its agility in running and leaping. It is\n   smaller and has stouter legs than the common cassowary. Its crest is\n   biloted; the neck and breast are black; the back, rufous mixed with\n   black; and the naked skin of the neck, blue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moory', 'a.', 'Of or pertaining to moors; marshy; fenny; boggy; moorish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moory', 'n.', 'A kind of blue cloth made in India.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moose', 'n.', 'A large cervine mammal (Alces machlis, or A. Americanus),\n   native of the Northern United States and Canada. The adult male is\n   about as large as a horse, and has very large, palmate antlers. It\n   closely resembles the European elk, and by many zoologists is\n   considered the same species. See Elk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moosewood', 'n.', 'The striped maple (Acer Pennsylvanicum).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moosewood', 'n.', 'Leatherwood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moot', 'v.', 'See 1st Mot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moot', 'n.', 'A ring for gauging wooden pins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mooted', 'imp. & p. p.', 'of Moot');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mooting', 'p. pr. & vb. n.', 'of Moot');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moot', 'v. t.', 'To argue for and against; to debate; to discuss; to\n   propose for discussion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moot', 'v. t.', 'Specifically: To discuss by way of exercise; to argue for\n   practice; to propound and discuss in a mock court.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moot', 'v. i.', 'To argue or plead in a supposed case.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moot', 'n.', 'A meeting for discussion and deliberation; esp., a meeting of\n   the people of a village or district, in Anglo-Saxon times, for the\n   discussion and settlement of matters of common interest; -- usually in\n   composition; as, folk-moot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moot', 'v.', 'A discussion or debate; especially, a discussion of\n   fictitious causes by way of practice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moot', 'a.', 'Subject, or open, to argument or discussion; undecided;\n   debatable; mooted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mootable', 'a.', 'Capable of being mooted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mooter', 'n.', 'A disputer of a mooted case.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moot-hall', 'n.', 'Alt. of Moot-house');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moot-house', 'n.', 'A hall for public meetings; a hall of judgment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moot-hill', 'n.', 'A hill of meeting or council; an elevated place in the\n   open air where public assemblies or courts were held by the Saxons; --\n   called, in Scotland, mute-hill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mootmen', 'pl. ', 'of Mootman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mootman', 'n.', 'One who argued moot cases in the inns of court.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mop', 'n.', 'A made-up face; a grimace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mop', 'v. i.', 'To make a wry mouth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mop', 'n.', 'An implement for washing floors, or the like, made of a piece\n   of cloth, or a collection of thrums, or coarse yarn, fastened to a\n   handle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mop', 'n.', 'A fair where servants are hired.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mop', 'n.', 'The young of any animal; also, a young girl; a moppet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mopped', 'imp. & p. p.', 'of Mop');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mopping', 'p. pr. & vb. n.', 'of Mop');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mop', 'v. t.', 'To rub or wipe with a mop, or as with a mop; as, to mop a\n   floor; to mop ones face with a handkerchief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mopboard', 'n.', 'A narrow board nailed against the wall of a room next to\n   the floor; skirting board; baseboard. See Baseboard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moped', 'imp. & p. p.', 'of Mope');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moping', 'p. pr. & vb. n.', 'of Mope');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mope', 'v. i.', 'To be dull and spiritless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mope', 'v. t.', 'To make spiritless and stupid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mope', 'n.', 'A dull, spiritless person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mope-eyed', 'a.', 'Shortsighted; purblind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mopeful', 'a.', 'Mopish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mopish', 'a.', 'Dull; spiritless; dejected.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moplah', 'n.', 'One of a class of Mohammedans in Malabar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moppet', 'n.', 'A rag baby; a puppet made of cloth; hence, also, in\n   fondness, a little girl, or a woman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moppet', 'n.', 'A long-haired pet dog.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mopsey', 'n.', 'Alt. of Mopsy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mopsy', 'n.', 'A moppet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mopsy', 'n.', 'A slatternly, untidy woman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mopsical', 'a.', 'Shortsighted; mope-eyed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mopstick', 'n.', 'The long handle of a mop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mopus', 'n.', 'A mope; a drone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moquette', 'n.', 'A kind of carpet having a short velvety pile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mora', 'n.', 'A game of guessing the number of fingers extended in a quick\n   movement of the hand, -- much played by Italians of the lower classes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mora', 'n.', 'A leguminous tree of Guiana and Trinidad (Dimorphandra\n   excelsa); also, its timber, used in shipbuilding and making furniture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mora', 'n.', 'Delay; esp., culpable delay; postponement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moraine', 'n.', 'An accumulation of earth and stones carried forward and\n   deposited by a glacier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morainic', 'a.', 'Of or pertaining to a moranie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moral', 'a.', 'Relating to duty or obligation; pertaining to those\n   intentions and actions of which right and wrong, virtue and vice, are\n   predicated, or to the rules by which such intentions and actions ought\n   to be directed; relating to the practice, manners, or conduct of men as\n   social beings in relation to each other, as respects right and wrong,\n   so far as they are properly subject to rules.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moral', 'a.', 'Conformed to accepted rules of right; acting in conformity\n   with such rules; virtuous; just; as, a moral man. Used sometimes in\n   distinction from religious; as, a moral rather than a religious life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moral', 'a.', 'Capable of right and wrong action or of being governed by a\n   sense of right; subject to the law of duty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moral', 'a.', 'Acting upon or through ones moral nature or sense of right,\n   or suited to act in such a manner; as, a moral arguments; moral\n   considerations. Sometimes opposed to material and physical; as, moral\n   pressure or support.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moral', 'a.', 'Supported by reason or probability; practically sufficient;\n   -- opposed to legal or demonstrable; as, a moral evidence; a moral\n   certainty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moral', 'a.', 'Serving to teach or convey a moral; as, a moral lesson;\n   moral tales.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moral', 'n.', 'The doctrine or practice of the duties of life; manner of\n   living as regards right and wrong; conduct; behavior; -- usually in the\n   plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moral', 'n.', 'The inner meaning or significance of a fable, a narrative,\n   an occurrence, an experience, etc.; the practical lesson which anything\n   is designed or fitted to teach; the doctrine meant to be inculcated by\n   a fiction; a maxim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moral', 'n.', 'A morality play. See Morality, 5.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moral', 'v. i.', 'To moralize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morale', 'a.', 'The moral condition, or the condition in other respects, so\n   far as it is affected by, or dependent upon, moral considerations, such\n   as zeal, spirit, hope, and confidence; mental state, as of a body of\n   men, an army, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moraler', 'n.', 'A moralizer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moralism', 'n.', 'A maxim or saying embodying a moral truth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moralist', 'n.', 'One who moralizes; one who teaches or animadverts upon\n   the duties of life; a writer of essays intended to correct vice and\n   inculcate moral duties.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moralist', 'n.', 'One who practices moral duties; a person who lives in\n   conformity with moral rules; one of correct deportment and dealings\n   with his fellow-creatures; -- sometimes used in contradistinction to\n   one whose life is controlled by religious motives.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moralities', 'pl. ', 'of Morality');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morality', 'n.', 'The relation of conformity or nonconformity to the moral\n   standard or rule; quality of an intention, a character, an action, a\n   principle, or a sentiment, when tried by the standard of right.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morality', 'n.', 'The quality of an action which renders it good; the\n   conformity of an act to the accepted standard of right.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morality', 'n.', 'The doctrines or rules of moral duties, or the duties of\n   men in their social character; ethics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morality', 'n.', 'The practice of the moral duties; rectitude of life;\n   conformity to the standard of right; virtue; as, we often admire the\n   politeness of men whose morality we question.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morality', 'n.', 'A kind of allegorical play, so termed because it\n   consisted of discourses in praise of morality between actors\n   representing such characters as Charity, Faith, Death, Vice, etc. Such\n   plays were occasionally exhibited as late as the reign of Henry VIII.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morality', 'n.', 'Intent; meaning; moral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moralization', 'n.', 'The act of moralizing; moral reflections or\n   discourse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moralization', 'n.', 'Explanation in a moral sense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moralized', 'imp. & p. p.', 'of Moralize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moralizing', 'p. pr. & vb. n.', 'of Moralize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moralize', 'v. t.', 'To apply to a moral purpose; to explain in a moral\n   sense; to draw a moral from.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moralize', 'v. t.', 'To furnish with moral lessons, teachings, or examples;\n   to lend a moral to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moralize', 'v. t.', 'To render moral; to correct the morals of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moralize', 'v. t.', 'To give a moral quality to; to affect the moral\n   quality of, either for better or worse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moralize', 'v. i.', 'To make moral reflections; to regard acts and events\n   as involving a moral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moralizer', 'n.', 'One who moralizes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morally', 'adv.', 'In a moral or ethical sense; according to the rules of\n   morality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morally', 'adv.', 'According to moral rules; virtuously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morally', 'adv.', 'In moral qualities; in disposition and character; as,\n   one who physically and morally endures hardships.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morally', 'adv.', 'In a manner calculated to serve as the basis of action;\n   according to the usual course of things and human judgment; according\n   to reason and probability.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morass', 'n.', 'A tract of soft, wet ground; a marsh; a fen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'morassy', 'a.', 'Marshy; fenny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morate', 'n.', 'A salt of moric acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moration', 'n.', 'A delaying tarrying; delay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moravian', 'a.', 'Of or pertaining to Moravia, or to the United Brethren.\n   See Moravian, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moravian', 'n.', 'One of a religious sect called the United Brethren (an\n   offshoot of the Hussites in Bohemia), which formed a separate church of\n   Moravia, a northern district of Austria, about the middle of the 15th\n   century. After being nearly extirpated by persecution, the society,\n   under the name of The Renewed Church of the United Brethren, was\n   reestablished in 1722-35 on the estates of Count Zinzendorf in Saxony.\n   Called also Herrnhuter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moravianism', 'n.', 'The religious system of the Moravians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moray', 'n.', 'A muraena.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morbid', 'a.', 'Not sound and healthful; induced by a diseased or abnormal\n   condition; diseased; sickly; as, morbid humors; a morbid constitution;\n   a morbid state of the juices of a plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morbid', 'a.', 'Of or pertaining to disease or diseased parts; as, morbid\n   anatomy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morbidezza', 'n.', 'Delicacy or softness in the representation of flesh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morbidezza', 'n.', 'A term used as a direction in execution, signifying,\n   with extreme delicacy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morbidity', 'n.', 'The quality or state of being morbid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morbidity', 'n.', 'Morbid quality; disease; sickness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morbidity', 'n.', 'Amount of disease; sick rate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morbidly', 'adv.', 'In a morbid manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morbidness', 'n.', 'The quality or state of being morbid; morbidity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morbific', 'a.', 'Alt. of Morbifical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morbifical', 'a.', 'Causing disease; generating a sickly state; as, a\n   morbific matter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morbillous', 'a.', 'Pertaining to the measles; partaking of the nature of\n   measels, or resembling the eruptions of that disease; measly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morbose', 'a.', 'Proceeding from disease; morbid; unhealthy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morbosity', 'n.', 'A diseased state; unhealthiness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morceau', 'n.', 'A bit; a morsel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mordacious', 'a.', 'Biting; given to biting; hence, figuratively,\n   sarcastic; severe; scathing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mordacity', 'n.', 'The quality of being mordacious; biting severity, or\n   sarcastic quality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mordant', 'a.', 'Biting; caustic; sarcastic; keen; severe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mordant', 'a.', 'Serving to fix colors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mordant', 'n.', 'Any corroding substance used in etching.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mordant', 'n.', 'Any substance, as alum or copperas, which, having a\n   twofold attraction for organic fibers and coloring matter, serves as a\n   bond of union, and thus gives fixity to, or bites in, the dyes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mordant', 'n.', 'Any sticky matter by which the gold leaf is made to\n   adhere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mordanted', 'imp. & p. p.', 'of Mordant');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mordanting', 'p. pr. & vb. n.', 'of Mordant');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mordant', 'v. t.', 'To subject to the action of, or imbue with, a mordant;\n   as, to mordant goods for dyeing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mordantly', 'adv.', 'In the manner of a mordant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mordente', 'n.', 'An embellishment resembling a trill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mordicancy', 'n.', 'A biting quality; corrosiveness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mordicant', 'a.', 'Biting; acrid; as, the mordicant quality of a body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mordication', 'n.', 'The act of biting or corroding; corrosion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mordicative', 'a.', 'Biting; corrosive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'More', 'n.', 'A hill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'More', 'n.', 'A root.');
  INSERT INTO words (word, word_type, description) VALUES  (    'More', 'superl.', 'Greater; superior; increased');
  INSERT INTO words (word, word_type, description) VALUES  (    'More', 'superl.', 'Greater in quality, amount, degree, quality, and the\n   like; with the singular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'More', 'superl.', 'Greater in number; exceeding in numbers; -- with the\n   plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'More', 'superl.', 'Additional; other; as, he wept because there were no\n   more words to conquer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'More', 'n.', 'A greater quantity, amount, or number; that which exceeds or\n   surpasses in any way what it is compared with.');
  INSERT INTO words (word, word_type, description) VALUES  (    'More', 'n.', 'That which is in addition; something other and further; an\n   additional or greater amount.');
  INSERT INTO words (word, word_type, description) VALUES  (    'More', 'adv.', 'In a greater quantity; in or to a greater extent or degree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'More', 'adv.', 'With a verb or participle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'More', 'adv.', 'With an adjective or adverb (instead of the suffix -er) to\n   form the comparative degree; as, more durable; more active; more\n   sweetly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'More', 'adv.', 'In addition; further; besides; again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'More', 'v. t.', 'To make more; to increase.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moreen', 'n.', 'A thick woolen fabric, watered or with embossed figures; --\n   used in upholstery, for curtains, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morel', 'n.', 'An edible fungus (Morchella esculenta), the upper part of\n   which is covered with a reticulated and pitted hymenium. It is used as\n   food, and for flavoring sauces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morel', 'n.', 'Nightshade; -- so called from its blackish purple berries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morel', 'n.', 'A kind of cherry. See Morello.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moreland', 'n.', 'Moorland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morelle', 'n.', 'Nightshade. See 2d Morel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morello', 'n.', 'A kind of nearly black cherry with dark red flesh and\n   juice, -- used chiefly for preserving.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morendo', 'a. & n.', 'Dying; a gradual decrescendo at the end of a strain\n   or cadence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moreness', 'n.', 'Greatness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moreover', 'adv.', 'Beyond what has been said; further; besides; in\n   addition; furthermore; also; likewise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morepork', 'n.', 'The Australian crested goatsucker (Aegotheles\n   Novae-Hollandiae). Also applied to other allied birds, as Podargus\n   Cuveiri.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moresk', 'a. & n.', 'Moresque.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moresque', 'a.', 'Of or pertaining to, or in the manner or style of, the\n   Moors; Moorish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moresque', 'n.', 'The Moresque style of architecture or decoration. See\n   Moorish architecture, under Moorish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morganatic', 'a.', 'Pertaining to, in the manner of, or designating, a kind\n   of marriage, called also left-handed marriage, between a man of\n   superior rank and a woman of inferior, in which it is stipulated that\n   neither the latter nor her children shall enjoy the rank or inherit the\n   possessions of her husband.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morgay', 'n.', 'The European small-spotted dogfish, or houndfish. See the\n   Note under Houndfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morglay', 'n.', 'A sword.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morgue', 'n.', 'A place where the bodies of persons found dead are exposed,\n   that they may be identified, or claimed by their friends; a deadhouse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moria', 'n.', 'Idiocy; imbecility; fatuity; foolishness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morian', 'n.', 'A Moor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moribund', 'a.', 'In a dying state; dying; at the point of death.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moribund', 'n.', 'A dying person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moric', 'a.', 'Pertaining to, or derived from, fustic (see Morin); as,\n   moric acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morice', 'n.', 'See Morisco.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morigerate', 'a.', 'Obedient.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morigeration', 'n.', 'Obsequiousness; obedience.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morigerous', 'a.', 'Obedient; obsequious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moril', 'n.', 'An edible fungus. Same as 1st Morel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morin', 'n.', 'A yellow crystalline substance of acid properties extracted\n   from fustic (Maclura tinctoria, formerly called Morus tinctoria); --\n   called also moric acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morinda', 'n.', 'A genus of rubiaceous trees and shrubs, mostly East\n   Indian, many species of which yield valuable red and yellow dyes. The\n   wood is hard and beautiful, and used for gunstocks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morindin', 'n.', 'A yellow dyestuff extracted from the root bark of an East\n   Indian plant (Morinda citrifolia).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morinel', 'n.', 'The dotterel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moringa', 'n.', 'A genus of trees of Southern India and Northern Africa.\n   One species (Moringa pterygosperma) is the horse-radish tree, and its\n   seeds, as well as those of M. aptera, are known in commerce as ben or\n   ben nuts, and yield the oil called oil of ben.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moringic', 'a.', 'Designating an organic acid obtained from oil of ben. See\n   Moringa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morintannic', 'a.', 'Pertaining to, or designating, a variety of tannic\n   acid extracted from fustic (Maclura, formerly Morus, tinctoria) as a\n   yellow crystalline substance; -- called also maclurin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morion', 'n.', 'A kind of open helmet, without visor or beaver, and\n   somewhat resembling a hat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morion', 'n.', 'A dark variety of smoky quartz.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morioplasty', 'n.', 'The restoration of lost parts of the body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morisco', 'a.', 'Moresque.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morisco', 'n.', 'A thing of Moorish origin; as: (a) The Moorish language.\n   (b) A Moorish dance, now called morris dance. Marston. (c) One who\n   dances the Moorish dance. Shak. (d) Moresque decoration or\n   architecture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morisk', 'n.', 'Same as Morisco.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morkin', 'n.', 'A beast that has died of disease or by mischance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morland', 'n.', 'Moorland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morling', 'n.', 'Mortling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mormal', 'n.', 'A bad sore; a gangrene; a cancer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mormo', 'n.', 'A bugbear; false terror.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mormon', 'n.', 'A genus of sea birds, having a large, thick bill; the\n   puffin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mormon', 'n.', 'The mandrill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mormon', 'n.', 'One of a sect in the United States, followers of Joseph\n   Smith, who professed to have found an addition to the Bible, engraved\n   on golden plates, called the Book of Mormon, first published in 1830.\n   The Mormons believe in polygamy, and their hierarchy of apostles, etc.,\n   has control of civil and religious matters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mormon', 'a.', 'Of or pertaining to the Mormons; as, the Mormon religion;\n   Mormon practices.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mormondom', 'n.', 'The country inhabited by the Mormons; the Mormon people.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mormonism', 'n.', 'The doctrine, system, and practices of the Mormons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mormonite', 'n.', 'A Mormon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mormonite', 'a.', 'Mormon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morn', 'n.', 'The first part of the day; the morning; -- used chiefly in\n   poetry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morne', 'a.', 'Of or pertaining to the morn; morning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morne', 'n.', 'A ring fitted upon the head of a lance to prevent wounding\n   an adversary in tilting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morne', 'a.', 'Without teeth, tongue, or claws; -- said of a lion\n   represented heraldically.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morne', 'n.', 'The first or early part of the day, variously understood as\n   the earliest hours of light, the time near sunrise; the time from\n   midnight to noon, from rising to noon, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morne', 'n.', 'The first or early part; as, the morning of life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morne', 'n.', 'The goddess Aurora.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morning', 'a.', 'Pertaining to the first part or early part of the day;\n   being in the early part of the day; as, morning dew; morning light;\n   morning service.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morning-glory', 'n.', 'A climbing plant (Ipomoea purpurea) having handsome,\n   funnel-shaped flowers, usually red, pink, purple, white, or variegated,\n   sometimes pale blue. See Dextrorsal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morningtide', 'n.', 'Morning time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mornward', 'adv.', 'Towards the morn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moro', 'n.', 'A small abscess or tumor having a resemblance to a mulberry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moroccan', 'a.', 'Of or pertaining to Morocco, or its inhabitants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morocco', 'n.', 'A fine kind of leather, prepared commonly from goatskin\n   (though an inferior kind is made of sheepskin), and tanned with sumac\n   and dyed of various colors; -- said to have been first made by the\n   Moors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morology', 'n.', 'Foolish talk; nonsense; folly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morone', 'n.', 'Maroon; the color of an unripe black mulberry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morosaurus', 'n.', 'An extinct genus of large herbivorous dinosaurs, found\n   in Jurassic strata in America.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morose', 'a.', 'Of a sour temper; sullen and austere; ill-humored; severe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morose', 'a.', 'Lascivious; brooding over evil thoughts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morosely', 'adv.', 'Sourly; with sullen austerity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moroseness', 'n.', 'Sourness of temper; sulenness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morosis', 'n.', 'Idiocy; fatuity; stupidity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morosity', 'n.', 'Moroseness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moroshop', 'n.', 'A philosophical or learned fool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morosous', 'a.', 'Morose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moroxite', 'n.', 'A variety of apatite of a greenish blue color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moroxylate', 'n.', 'A morate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moroxylic', 'a.', 'Of, pertaining to, or derived from, the mulberry; moric.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morphean', 'a.', 'Of or relating to Morpheus, to dreams, or to sleep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morpheus', 'n.', 'The god of dreams.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morphew', 'n.', 'A scurfy eruption.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morphew', 'v. t.', 'To cover with a morphew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morphia', 'n.', 'Morphine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morphine', 'n.', 'A bitter white crystalline alkaloid found in opium,\n   possessing strong narcotic properties, and much used as an anodyne; --\n   called also morphia, and morphina.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morphinism', 'n.', 'A morbid condition produced by the excessive or\n   prolonged use of morphine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morpho', 'n.', 'Any one of numerous species of large, handsome, tropical\n   American butterflies, of the genus Morpho. They are noted for the very\n   brilliant metallic luster and bright colors (often blue) of the upper\n   surface of the wings. The lower surface is usually brown or gray, with\n   eyelike spots.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morphogeny', 'n.', 'History of the evolution of forms; that part of\n   ontogeny that deals with the germ history of forms; -- distinguished\n   from physiogeny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morphologic', 'a.', 'Alt. of Morphological');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morphological', 'a.', 'Of, pertaining to, or according to, the principles\n   of morphology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morphologist', 'n.', 'One who is versed in the science of morphology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morphology', 'n.', 'That branch of biology which deals with the structure\n   of animals and plants, treating of the forms of organs and describing\n   their varieties, homologies, and metamorphoses. See Tectology, and\n   Promorphology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morphon', 'n.', 'A morphological individual, characterized by definiteness\n   of form bion, a physiological individual. See Tectology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morphonomy', 'n.', 'The laws of organic formation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morphophyly', 'n.', 'The tribal history of forms; that part of phylogeny\n   which treats of the tribal history of forms, in distinction from the\n   tribal history of functions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morphosis', 'n.', 'The order or mode of development of an organ or part.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morphotic', 'a.', 'Connected with, or becoming an integral part of, a\n   living unit or of the morphological framework; as, morphotic, or\n   tissue, proteids.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-morphous', '', 'A combining form denoting form, shape; as, isomorphous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morpion', 'n.', 'A louse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morrice', 'n.', 'Same as 1st Morris.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morrice', 'a.', 'Dancing the morrice; dancing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morricer', 'n.', 'A morris dancer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morrimal', 'n. & a.', 'See Mormal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morris', 'n.', 'A Moorish dance, usually performed by a single dancer, who\n   accompanies the dance with castanets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morris', 'n.', 'A dance formerly common in England, often performed in\n   pagenats, processions, and May games. The dancers, grotesquely dressed\n   and ornamented, took the parts of Robin Hood, Maidmarian, and other\n   fictious characters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morris', 'n.', 'An old game played with counters, or men, which are placed\n   angles of a figure drawn on a board or on the ground; also, the board\n   or ground on which the game is played.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morris', 'n.', 'A marine fish having a very slender, flat, transparent\n   body. It is now generally believed to be the young of the conger eel or\n   some allied fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morris-pike', 'n.', 'A Moorish pike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morrot', 'n.', 'See Marrot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morrow', 'n.', 'Morning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morrow', 'n.', 'The next following day; the day subsequent to any day\n   specified or understood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morrow', 'n.', 'The day following the present; to-morrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morse', 'n.', 'The walrus. See Walrus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morse', 'n.', 'A clasp for fastening garments in front.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morse alphabet', '', 'A telegraphic alphabet in very general use, inventing\n   by Samuel F.B.Morse, the inventor of Morses telegraph. The letters are\n   represented by dots and dashes impressed or printed on paper, as, .-\n   (A), - . . . (B), -.. (D), . (E), .. (O), . . . (R), -- (T), etc., or\n   by sounds, flashes of light, etc., with greater or less intervals\n   between them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morsel', 'n.', 'A little bite or bit of food.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morsel', 'n.', 'A small quantity; a little piece; a fragment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morsing horn', '', 'A horn or flask for holding powder, as for priming.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morsitation', 'n.', 'The act of biting or gnawing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morsure', 'n.', 'The act of biting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mort', 'n.', 'A great quantity or number.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mort', 'n.', 'A woman; a female.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mort', 'n.', 'A salmon in its third year.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mort', 'n.', 'Death; esp., the death of game in the chase.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mort', 'n.', 'A note or series of notes sounded on a horn at the death of\n   game.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mort', 'n.', 'The skin of a sheep or lamb that has died of disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mortal', 'a.', 'Subject to death; destined to die; as, man is mortal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mortal', 'a.', 'Destructive to life; causing or occasioning death;\n   terminating life; exposing to or deserving death; deadly; as, a mortal\n   wound; a mortal sin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mortal', 'a.', 'Fatally vulnerable; vital.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mortal', 'a.', 'Of or pertaining to the time of death.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mortal', 'a.', 'Affecting as if with power to kill; deathly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mortal', 'a.', 'Human; belonging to man, who is mortal; as, mortal wit or\n   knowledge; mortal power.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mortal', 'a.', 'Very painful or tedious; wearisome; as, a sermon lasting\n   two mortal hours.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mortal', 'n.', 'A being subject to death; a human being; man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mortality', 'n.', 'The condition or quality of being mortal; subjection to\n   death or to the necessity of dying.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mortality', 'n.', 'Human life; the life of a mortal being.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mortality', 'n.', 'Those who are, or that which is, mortal; the human cace;\n   humanity; human nature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mortality', 'n.', 'Death; destruction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mortality', 'n.', 'The whole sum or number of deaths in a given time or a\n   given community; also, the proportion of deaths to population, or to a\n   specific number of the population; death rate; as, a time of great, or\n   low, mortality; the mortality among the settlers was alarming.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mortalized', 'imp. & p. p.', 'of Mortalize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mortalizing', 'p. pr. & vb. n.', 'of Mortalize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mortalize', 'v. t.', 'To make mortal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mortally', 'adv.', 'In a mortal manner; so as to cause death; as, mortally\n   wounded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mortally', 'adv.', 'In the manner of a mortal or of mortal beings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mortally', 'adv.', 'In an extreme degree; to the point of dying or causing\n   death; desperately; as, mortally jealous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mortalness', 'n.', 'Quality of being mortal; mortality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mortar', 'n.', 'A strong vessel, commonly in form of an inverted bell, in\n   which substances are pounded or rubbed with a pestle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mortar', 'n.', 'A short piece of ordnance, used for throwing bombs,\n   carcasses, shells, etc., at high angles of elevation, as 45, and even\n   higher; -- so named from its resemblance in shape to the utensil above\n   described.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mortar', 'n.', 'A building material made by mixing lime, cement, or plaster\n   of Paris, with sand, water, and sometimes other materials; -- used in\n   masonry for joining stones, bricks, etc., also for plastering, and in\n   other ways.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mortar', 'v. t.', 'To plaster or make fast with mortar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mortar', 'n.', 'A chamber lamp or light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mortgage', 'n.', 'A conveyance of property, upon condition, as security for\n   the payment of a debt or the preformance of a duty, and to become void\n   upon payment or performance according to the stipulated terms; also,\n   the written instrument by which the conveyance is made.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mortgage', 'n.', 'State of being pledged; as, lands given in mortgage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mortgaged', 'imp. & p. p.', 'of Mortgage');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mortgaging', 'p. pr. & vb. n.', 'of Mortgage');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mortgage', 'v. t.', 'To grant or convey, as property, for the security of a\n   debt, or other engagement, upon a condition that if the debt or\n   engagement shall be discharged according to the contract, the\n   conveyance shall be void, otherwise to become absolute, subject,\n   however, to the right of redemption.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mortgage', 'v. t.', 'Hence: To pledge, either literally or figuratively; to\n   make subject to a claim or obligation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mortgagee', 'n.', 'The person to whom property is mortgaged, or to whom a\n   mortgage is made or given.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mortgageor', 'n.', 'Alt. of Mortgagor');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mortgagor', 'n.', 'One who gives a mortgage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mortgager', 'n.', 'gives a mortgage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mortiferous', 'a.', 'Bringing or producing death; deadly; destructive; as,\n   a mortiferous herb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mortification', 'n.', 'The act of mortifying, or the condition of being\n   mortified');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mortification', 'n.', 'The death of one part of an animal body, while the\n   rest continues to live; loss of vitality in some part of a living\n   animal; gangrene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mortification', 'n.', 'Destruction of active qualities; neutralization.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mortification', 'n.', 'Subjection of the passions and appetites, by\n   penance, absistence, or painful severities inflicted on the body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mortification', 'n.', 'Hence: Deprivation or depression of self-approval;\n   abatement or pride; humiliation; chagrin; vexation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mortification', 'n.', 'That which mortifies; the cause of humiliation,\n   chagrin, or vexation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mortification', 'n.', 'A gift to some charitable or religious institution;\n   -- nearly synonymous with mortmain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mortified', '', 'imp. & p. p. of Mortify.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mortifiedness', 'n.', 'The state of being mortified; humiliation;\n   subjection of the passions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mortifier', 'n.', 'One who, or that which, mortifies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mortified', 'imp. & p. p.', 'of Mortify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mortifying', 'p. pr. & vb. n.', 'of Mortify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mortify', 'v. t.', 'To destroy the organic texture and vital functions of;\n   to produce gangrene in.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mortify', 'v. t.', 'To destroy the active powers or essential qualities of;\n   to change by chemical action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mortify', 'v. t.', 'To deaden by religious or other discipline, as the\n   carnal affections, bodily appetites, or worldly desires; to bring into\n   subjection; to abase; to humble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mortify', 'v. t.', 'To affect with vexation, chagrin, or humiliation; to\n   humble; to depress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mortify', 'v. i.', 'To lose vitality and organic structure, as flesh of a\n   living body; to gangrene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mortify', 'v. i.', 'To practice penance from religious motives; to deaden\n   desires by religious discipline.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mortify', 'v. i.', 'To be subdued; to decay, as appetites, desires, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mortifying', 'a.', 'Tending to mortify; affected by, or having symptoms of,\n   mortification; as, a mortifying wound; mortifying flesh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mortifying', 'a.', 'Subduing the appetites, desires, etc.; as, mortifying\n   penances.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mortifying', 'a.', 'Tending to humble or abase; humiliating; as, a\n   mortifying repulse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mortifyingly', 'adv.', 'In a mortifying manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mortise', 'n.', 'A cavity cut into a piece of timber, or other material, to\n   receive something (as the end of another piece) made to fit it, and\n   called a tenon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mortised', 'imp. & p. p.', 'of Mortise');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mortising', 'p. pr. & vb. n.', 'of Mortise');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mortise', 'v. t.', 'To cut or make a mortisein.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mortise', 'v. t.', 'To join or fasten by a tenon and mortise; as, to\n   mortise a beam into a post, or a joist into a girder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mortling', 'n.', 'An animal, as a sheep, dead of disease or privation; a\n   mortling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mortling', 'n.', 'Wool plucked from a dead sheep; morling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mortmain', 'n.', 'Possession of lands or tenements in, or conveyance to,\n   dead hands, or hands that cannot alienate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mortmal', 'n.', 'See Mormal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mortpay', 'n.', 'Dead pay; the crime of taking pay for the service of dead\n   soldiers, or for services not actually rendered by soldiers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mortress', 'n.', 'Alt. of Mortrew');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mortrew', 'n.', 'A dish of meats and other ingredients, cooked together; an\n   ollapodrida.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mortuaries', 'pl. ', 'of Mortuary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mortuary', 'a.', 'A sort of ecclesiastical heriot, a customary gift claimed\n   by, and due to, the minister of a parish on the death of a parishioner.\n   It seems to have been originally a voluntary bequest or donation,\n   intended to make amends for any failure in the payment of tithes of\n   which the deceased had been guilty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mortuary', 'a.', 'A burial place; a place for the dead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mortuary', 'a.', 'A place for the reception of the dead before burial; a\n   deadhouse; a morgue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mortuary', 'a.', 'Of or pertaining to the dead; as, mortuary monuments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morulae', 'pl. ', 'of Morula');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morula', 'n.', 'The sphere or globular mass of cells (blastomeres), formed\n   by the clevage of the ovum or egg in the first stages of its\n   development; -- called also mulberry mass, segmentation sphere, and\n   blastosphere. See Segmentation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morulation', 'n.', 'The process of cleavage, or segmentation, of the ovum,\n   by which a morula is formed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morus', 'n.', 'A genus of trees, some species of which produce edible\n   fruit; the mulberry. See Mulberry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morwe', 'n.', 'See Morrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Morwening', 'n.', 'Morning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mosaic', 'n.', 'A surface decoration made by inlaying in patterns small\n   pieces of variously colored glass, stone, or other material; -- called\n   also mosaic work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mosaic', 'n.', 'A picture or design made in mosaic; an article decorated in\n   mosaic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mosaic', 'a.', 'Of or pertaining to the style of work called mosaic; formed\n   by uniting pieces of different colors; variegated; tessellated; also,\n   composed of various materials or ingredients.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mosaic', 'a.', 'Of or pertaining to Moses, the leader of the Israelites, or\n   established through his agency; as, the Mosaic law, rites, or\n   institutions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mosaical', 'a.', 'Mosaic (in either sense).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mosaically', 'adv.', 'In the manner of a mosaic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mosaism', 'n.', 'Attachment to the system or doctrines of Moses; that which\n   is peculiar to the Mosaic system or doctrines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mosasaur', 'n.', 'Alt. of Mosasaurian');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mosasaurian', 'n.', 'One of an extinct order of reptiles, including\n   Mosasaurus and allied genera. See Mosasauria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mosasauria', 'n. pl.', 'An order of large, extinct, marine reptiles, found\n   in the Cretaceous rocks, especially in America. They were serpentlike\n   in form and in having loosely articulated and dilatable jaws, with\n   large recurved tteth, but they had paddlelike feet. Some of them were\n   over fifty feet long. They are, essentially, fossil sea serpents with\n   paddles. Called also Pythonomarpha, and Mosasauria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mosasaurus', 'n.', 'A genus of extinct marine reptiles allied to the\n   lizards, but having the body much elongated, and the limbs in the form\n   of paddles. The first known species, nearly fifty feet in length, was\n   discovered in Cretaceous beds near Maestricht, in the Netherlands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moschatel', 'n.', 'A plant of the genus Adoxa (A. moschatellina), the\n   flowers of which are pale green, and have a faint musky smell. It is\n   found in woods in all parts of Europe, and is called also hollow root\n   and musk crowfoot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moschine', 'a.', 'Of or pertaining to Moschus, a genus including the musk\n   deer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mosel', 'n. & v.', 'See Muzzle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moselle', 'n.', 'A light wine, usually white, produced in the vicinity of\n   the river Moselle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moses', 'n.', 'A large flatboat, used in the West Indies for taking freight\n   from shore to ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mosk', 'n.', 'See Mosque.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moslems', 'pl. ', 'of Moslem');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moslem', 'pl. ', 'of Moslem');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moslem', 'n.', 'A Mussulman; an orthodox Mohammedan. [Written also muslim.]');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moslem', 'a.', 'Of or pertaining to the Mohammedans; Mohammedan; as, Moslem\n   lands; the Moslem faith.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moslings', 'n. pl.', 'Thin shreds of leather shaved off in dressing skins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mososaurus', 'n.', 'Same as Mosasaurus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mosque', 'n.', 'A Mohammedan church or place of religious worship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mosquitoes', 'pl. ', 'of Mosquito');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mosquito', 'n.', 'Any one of various species of gnats of the genus Culex\n   and allied genera. The females have a proboscis containing, within the\n   sheathlike labium, six fine, sharp, needlelike organs with which they\n   puncture the skin of man and animals to suck the blood. These bites,\n   when numerous, cause, in many persons, considerable irritation and\n   swelling, with some pain. The larvae and pupae, called wigglers, are\n   aquatic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moss', 'n.', 'A cryptogamous plant of a cellular structure, with distinct\n   stem and simple leaves. The fruit is a small capsule usually opening by\n   an apical lid, and so discharging the spores. There are many species,\n   collectively termed Musci, growing on the earth, on rocks, and trunks\n   of trees, etc., and a few in running water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moss', 'n.', 'A bog; a morass; a place containing peat; as, the mosses of\n   the Scottish border.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mossed', 'imp. & p. p.', 'of Moss');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mossing', 'p. pr. & vb. n.', 'of Moss');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moss', 'v. t.', 'To cover or overgrow with moss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mossback', 'n.', 'A veteran partisan; one who is so conservative in opinion\n   that he may be likened to a stone or old tree covered with moss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mossbanker', 'n.', 'Alt. of Mossbunker');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mossbunker', 'n.', 'The menhaded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moss-grown', 'a.', 'Overgrown with moss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mossiness', 'n.', 'The state of being mossy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mosstrooper', 'n.', 'One of a class of marauders or bandits that formerly\n   infested the border country between England and Scotland; -- so called\n   in allusion to the mossy or boggy character of much of the border\n   country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mossy', 'superl.', 'Overgrown with moss; abounding with or edged with moss;\n   as, mossy trees; mossy streams.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mossy', 'superl.', 'Resembling moss; as, mossy green.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Most', 'a.', 'Consisting of the greatest number or quantity; greater in\n   number or quantity than all the rest; nearly all.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Most', 'a.', 'Greatest in degree; as, he has the most need of it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Most', 'a.', 'Highest in rank; greatest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Most', 'a.', 'In the greatest or highest degree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mostahiba', 'n.', 'See Mustaiba.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moste', '', 'imp. of Mote.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mostic', 'n.', 'Alt. of Mostick');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mostick', 'n.', 'A painters maul-stick.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mostly', 'adv.', 'For the greatest part; for the most part; chiefly; in the\n   main.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mostra', 'n.', 'See Direct, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mostwhat', 'adv.', 'For the most part.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mot', 'Sing. pres. ind.', 'of Mot');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mote', '', 'of Mot');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moot', '', 'of Mot');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mot', 'pl.', 'of Mot');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mote', '', 'of Mot');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moote', '', 'of Mot');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mote', 'pres. subj.', 'of Mot');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moste', 'imp.', 'of Mot');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mot', 'v.', 'May; must; might.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mot', 'n.', 'A word; hence, a motto; a device.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mot', 'n.', 'A pithy or witty saying; a witticism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mot', 'n.', 'A note or brief strain on a bugle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Motacil', 'n.', 'Any singing bird of the genus Motacilla; a wagtail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Motation', 'n.', 'The act of moving; motion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mote', 'v.', 'See 1st Mot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mote', 'n.', 'A meeting of persons for discussion; as, a wardmote in the\n   city of London.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mote', 'n.', 'A body of persons who meet for discussion, esp. about the\n   management of affairs; as, a folkmote.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mote', 'n.', 'A place of meeting for discussion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mote', 'n.', 'The flourish sounded on a horn by a huntsman. See Mot, n., 3,\n   and Mort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mote', 'n.', 'A small particle, as of floating dust; anything proverbially\n   small; a speck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moted', 'a.', 'Filled with motes, or fine floating dust; as, the air.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Motet', 'n.', 'A composition adapted to sacred words in the elaborate\n   polyphonic church style; an anthem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moth', 'n.', 'A mote.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moths', 'pl. ', 'of Moth');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moth', 'n.', 'Any nocturnal lepidopterous insect, or any not included among\n   the butterflies; as, the luna moth; Io moth; hawk moth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moth', 'n.', 'Any lepidopterous insect that feeds upon garments, grain,\n   etc.; as, the clothes moth; grain moth; bee moth. See these terms under\n   Clothes, Grain, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moth', 'n.', 'Any one of various other insects that destroy woolen and fur\n   goods, etc., esp. the larvae of several species of beetles of the\n   genera Dermestes and Anthrenus. Carpet moths are often the larvae of\n   Anthrenus. See Carpet beetle, under Carpet, Dermestes, Anthrenus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moth', 'n.', 'Anything which gradually and silently eats, consumes, or\n   wastes any other thing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moth-eat', 'v. t.', 'To eat or prey upon, as a moth eats a garment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mothen', 'a.', 'Full of moths.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mother', 'n.', 'A female parent; especially, one of the human race; a woman\n   who has borne a child.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mother', 'n.', 'That which has produced or nurtured anything; source of\n   birth or origin; generatrix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mother', 'n.', 'An old woman or matron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mother', 'n.', 'The female superior or head of a religious house, as an\n   abbess, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mother', 'n.', 'Hysterical passion; hysteria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mother', 'a.', 'Received by birth or from ancestors; native, natural; as,\n   mother language; also acting the part, or having the place of a mother;\n   producing others; originating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mothered', 'imp. & p. p.', 'of Mother');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mothering', 'p. pr. & vb. n.', 'of Mother');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mother', 'v. t.', 'To adopt as a son or daughter; to perform the duties of\n   a mother to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mother', 'n.', 'A film or membrane which is developed on the surface of\n   fermented alcoholic liquids, such as vinegar, wine, etc., and acts as a\n   means of conveying the oxygen of the air to the alcohol and other\n   combustible principles of the liquid, thus leading to their oxidation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mother', 'v. i.', 'To become like, or full of, mother, or thick matter, as\n   vinegar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mothered', 'a.', 'Thick, like mother; viscid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Motherhood', 'n.', 'The state of being a mother; the character or office of\n   a mother.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mothering', 'n.', 'A rural custom in England, of visiting ones parents on\n   Midlent Sunday, -- supposed to have been originally visiting the mother\n   church to make offerings at the high altar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mother-in-law', 'n.', 'The mother of ones husband or wife.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Motherland', 'n.', 'The country of ones ancestors; -- same as fatherland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Motherless', 'a.', 'Destitute of a mother; having lost a mother; as,\n   motherless children.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Motherliness', 'n.', 'The state or quality of being motherly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Motherly', 'a.', 'Of or pertaining to a mother; like, or suitable for, a\n   mother; tender; maternal; as, motherly authority, love, or care.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Motherly', 'adv.', 'In a manner of a mother.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mother-naked', 'a.', 'Naked as when born.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mother-of-pearl', 'n.', 'The hard pearly internal layer of several kinds of\n   shells, esp. of pearl oysters, river mussels, and the abalone shells;\n   nacre. See Pearl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mother-of-thyme', 'n.', 'An aromatic plant (Thymus Serphyllum); -- called\n   also wild thyme.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Motherwort', 'n.', 'A labiate herb (Leonurus Cardiaca), of a bitter taste,\n   used popularly in medicine; lions tail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Motherwort', 'n.', 'The mugwort. See Mugwort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mothery', 'a.', 'Consisting of, containing, or resembling, mother (in\n   vinegar).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mothy', 'a.', 'Infested with moths; moth-eaten.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Motif', 'n.', 'Motive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Motific', 'a.', 'Producing motion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Motile', 'a.', 'Having powers of self-motion, though unconscious; as, the\n   motile spores of certain seaweeds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Motile', 'a.', 'Producing motion; as, motile powers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Motility', 'n.', 'Capability of motion; contractility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Motion', 'n.', 'The act, process, or state of changing place or position;\n   movement; the passing of a body from one place or position to another,\n   whether voluntary or involuntary; -- opposed to rest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Motion', 'n.', 'Power of, or capacity for, motion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Motion', 'n.', 'Direction of movement; course; tendency; as, the motion of\n   the planets is from west to east.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Motion', 'n.', 'Change in the relative position of the parts of anything;\n   action of a machine with respect to the relative movement of its parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Motion', 'n.', 'Movement of the mind, desires, or passions; mental act, or\n   impulse to any action; internal activity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Motion', 'n.', 'A proposal or suggestion looking to action or progress;\n   esp., a formal proposal made in a deliberative assembly; as, a motion\n   to adjourn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Motion', 'n.', 'An application made to a court or judge orally in open\n   court. Its object is to obtain an order or rule directing some act to\n   be done in favor of the applicant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Motion', 'n.', 'Change of pitch in successive sounds, whether in the same\n   part or in groups of parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Motion', 'n.', 'A puppet show or puppet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Motioned', 'imp. & p. p.', 'of Motion');
  INSERT INTO words (word, word_type, description) VALUES  (    'Motioning', 'p. pr. & vb. n.', 'of Motion');
  INSERT INTO words (word, word_type, description) VALUES  (    'Motion', 'v. i.', 'To make a significant movement or gesture, as with the\n   hand; as, to motion to one to take a seat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Motion', 'v. i.', 'To make proposal; to offer plans.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Motion', 'v. t.', 'To direct or invite by a motion, as of the hand or head;\n   as, to motion one to a seat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Motion', 'v. t.', 'To propose; to move.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Motioner', 'n.', 'One who makes a motion; a mover.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Motionist', 'n.', 'A mover.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Motionless', 'a.', 'Without motion; being at rest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Motive', 'n.', 'That which moves; a mover.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Motive', 'n.', 'That which incites to action; anything prompting or\n   exciting to choise, or moving the will; cause; reason; inducement;\n   object.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Motive', 'n.', 'The theme or subject; a leading phrase or passage which is\n   reproduced and varied through the course of a comor a movement; a short\n   figure, or melodic germ, out of which a whole movement is develpoed.\n   See also Leading motive, under Leading.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Motive', 'n.', 'That which produces conception, invention, or creation in\n   the mind of the artist in undertaking his subject; the guiding or\n   controlling idea manifested in a work of art, or any part of one.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Motive', 'a.', 'Causing motion; having power to move, or tending to move;\n   as, a motive argument; motive power.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Motive', 'v. t.', 'To prompt or incite by a motive or motives; to move.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Motiveless', 'a.', 'Destitute of a motive; not incited by a motive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Motivity', 'n.', 'The power of moving or producing motion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Motivity', 'n.', 'The quality of being influenced by motives.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Motivo', 'n.', 'See Motive, n., 3, 4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Motley', 'a.', 'Variegated in color; consisting of different colors;\n   dappled; party-colored; as, a motley coat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Motley', 'a.', 'Wearing motley or party-colored clothing. See Motley, n.,\n   1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Motley', 'n.', 'Composed of different or various parts; heterogeneously\n   made or mixed up; discordantly composite; as, motley style.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Motley', 'n.', 'A combination of distinct colors; esp., the party-colored\n   cloth, or clothing, worn by the professional fool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Motley', 'n.', 'Hence, a jester, a fool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Motley-minded', 'a.', 'Having a mind of a jester; foolish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Motmot', 'n.', 'Any one of several species of long-tailed, passerine birds\n   of the genus Momotus, having a strong serrated beak. In most of the\n   species the two long middle tail feathers are racket-shaped at the tip,\n   when mature. The bird itself is said by some writers to trim them into\n   this shape. They feed on insects, reptiles, and fruit, and are found\n   from Mexico to Brazil. The name is derived from its note.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moto', 'n.', 'Movement; manner of movement; particularly, movement with\n   increased rapidity; -- used especially in the phrase con moto,\n   directing to a somewhat quicker movement; as, andante con moto, a\n   little more rapidly than andante, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moton', 'n.', 'A small plate covering the armpit in armor of the 14th\n   century and later.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Motor', 'n.', 'One who, or that which, imparts motion; a source of\n   mechanical power.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Motor', 'n.', 'A prime mover; a machine by means of which a source of\n   power, as steam, moving water, electricity, etc., is made available for\n   doing mechanical work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Motor', 'n.', 'Alt. of Motorial');
  INSERT INTO words (word, word_type, description) VALUES  (    'Motory', 'n.', 'Alt. of Motorial');
  INSERT INTO words (word, word_type, description) VALUES  (    'Motorial', 'n.', 'Causing or setting up motion; pertaining to organs of\n   motion; -- applied especially in physiology to those nerves or nerve\n   fibers which only convey impressions from a nerve center to muscles,\n   thereby causing motion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Motorman', 'n.', 'A man who controls a motor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Motorpathic', 'a.', 'Of or pertaining to motorpathy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Motorpathy', 'n.', 'Kinesiatrics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Motte', 'n.', 'A clump of trees in a prairie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mottled', 'imp. & p. p.', 'of Mottle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mottling', 'p. pr. & vb. n.', 'of Mottle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mottle', 'v. t.', 'To mark with spots of different color, or shades of\n   color, as if stained; to spot; to maculate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mottle', 'n.', 'A mottled appearance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mottled', 'a.', 'Marked with spots of different colors; variegated;\n   spotted; as, mottled wood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mottoes', 'pl. ', 'of Motto');
  INSERT INTO words (word, word_type, description) VALUES  (    'Motto', 'n.', 'A sentence, phrase, or word, forming part of an heraldic\n   achievment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Motto', 'n.', 'A sentence, phrase, or word, prefixed to an essay,\n   discourse, chapter, canto, or the like, suggestive of its subject\n   matter; a short, suggestive expression of a guiding principle; a maxim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mottoed', 'a.', 'Bearing or having a motto; as, a mottoed coat or device.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Motty', 'a.', 'Full of, or consisting of, motes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mouchoir', 'n.', 'A handkerchief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mouazzin', 'n.', 'See Muezzin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mouflon', 'n.', 'A wild sheep (Ovis musimon), inhabiting the mountains of\n   Sardinia, Corsica, etc. Its horns are very large, with a triangular\n   base and rounded angles. It is supposed by some to be the original of\n   the domestic sheep. Called also musimon or musmon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mought', 'imp.', 'Might.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mouillation', 'n.', 'The act of uttering the sound of a mouille letter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mouille', 'a.', 'Applied to certain consonants having a \"liquid\" or\n   softened sound; e.g., in French, l or ll and gn (like the lli in\n   million and ni in minion); in Italian, gl and gn; in Spanish, ll and ;\n   in Portuguese, lh and nh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mould', '', 'Alt. of Mouldy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moulder', '', 'Alt. of Mouldy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mouldy', '', 'See Mold, Molder, Moldy, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moule', 'v. i.', 'To contract mold; to grow moldy; to mold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mouline', 'n.', 'Alt. of Moulinet');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moulinet', 'n.', 'The drum upon which the rope is wound in a capstan,\n   crane, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moulinet', 'n.', 'A machine formerly used for bending a crossbow by winding\n   it up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moulinet', 'n.', 'In sword and saber exercises, a circular swing of the\n   weapon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moult', 'v. & n.', 'See Molt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moulten', 'a.', 'Having molted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moun', 'v.', 'pl. of Mow, may.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mounch', 'v. t.', 'To munch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mound', 'n.', 'A ball or globe forming part of the regalia of an emperor or\n   other sovereign. It is encircled with bands, enriched with precious\n   stones, and surmounted with a cross; -- called also globe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mound', 'n.', 'An artificial hill or elevation of earth; a raised bank; an\n   embarkment thrown up for defense; a bulwark; a rampart; also, a natural\n   elevation appearing as if thrown up artificially; a regular and\n   isolated hill, hillock, or knoll.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mounded', 'imp. & p. p.', 'of Mound');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mounding', 'p. pr. & vb. n.', 'of Mound');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mound', 'v. t.', 'To fortify or inclose with a mound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mount', 'v.', 'A mass of earth, or earth and rock, rising considerably\n   above the common surface of the surrounding land; a mountain; a high\n   hill; -- used always instead of mountain, when put before a proper\n   name; as, Mount Washington; otherwise, chiefly in poetry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mount', 'v.', 'A bulwark for offense or defense; a mound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mount', 'v.', 'A bank; a fund.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mounted', 'imp. & p. p.', 'of Mount');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mounting', 'p. pr. & vb. n.', 'of Mount');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mount', 'n.', 'To rise on high; to go up; to be upraised or uplifted; to\n   tower aloft; to ascend; -- often with up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mount', 'n.', 'To get up on anything, as a platform or scaffold;\n   especially, to seat ones self on a horse for riding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mount', 'n.', 'To attain in value; to amount.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mount', 'v. t.', 'To get upon; to ascend; to climb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mount', 'v. t.', 'To place ones self on, as a horse or other animal, or\n   anything that one sits upon; to bestride.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mount', 'v. t.', 'To cause to mount; to put on horseback; to furnish with\n   animals for riding; to furnish with horses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mount', 'v. t.', 'Hence: To put upon anything that sustains and fits for\n   use, as a gun on a carriage, a map or picture on cloth or paper; to\n   prepare for being worn or otherwise used, as a diamond by setting, or a\n   sword blade by adding the hilt, scabbard, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mount', 'v. t.', 'To raise aloft; to lift on high.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mount', 'v.', 'That upon which a person or thing is mounted');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mount', 'v.', 'A horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mount', 'v.', 'The cardboard or cloth on which a drawing, photograph, or\n   the like is mounted; a mounting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mountable', 'a.', 'Such as can be mounted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mountain', 'n.', 'A large mass of earth and rock, rising above the common\n   level of the earth or adjacent land; earth and rock forming an isolated\n   peak or a ridge; an eminence higher than a hill; a mount.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mountain', 'n.', 'A range, chain, or group of such elevations; as, the\n   White Mountains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mountain', 'n.', 'A mountainlike mass; something of great bulk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mountain', 'a.', 'Of or pertaining to a mountain or mountains; growing or\n   living on a mountain; found on or peculiar to mountains; among\n   mountains; as, a mountain torrent; mountain pines; mountain goats;\n   mountain air; mountain howitzer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mountain', 'a.', 'Like a mountain; mountainous; vast; very great.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mountaineer', 'n.', 'An inhabitant of a mountain; one who lives among\n   mountains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mountaineer', 'n.', 'A rude, fierce person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mountaineer', 'v. i.', 'To lie or act as a mountaineer; to climb mountains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mountainer', 'n.', 'A mountaineer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mountainet', 'n.', 'A small mountain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mountainous', 'a.', 'Full of, or containing, mountains; as, the mountainous\n   country of the Swiss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mountainous', 'a.', 'Inhabiting mountains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mountainous', 'a.', 'Large as, or resembling, a mountain; huge; of great\n   bulk; as, a mountainous heap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mountainousness', 'n.', 'The state or quality of being mountainous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mountance', 'n.', 'Amount; sum; quantity; extent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mountant', 'a.', 'Raised; high.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mountebank', 'n.', 'One who mounts a bench or stage in the market or other\n   public place, boasts of his skill in curing diseases, and vends\n   medicines which he pretends are infalliable remedies; a quack doctor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mountebank', 'n.', 'Any boastful or false pretender; a charlatan; a quack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mountebank', 'v. t.', 'To cheat by boasting and false pretenses; to gull.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mountebank', 'v. i.', 'To play the mountebank.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mountebankery', 'n.', 'The practices of a mountebank; quackery; boastful\n   and vain pretenses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mountebankish', 'a.', 'Like a mountebank or his quackery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mountebankism', 'n.', 'The practices of a mountebank; mountebankery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mounted', 'a.', 'Seated or serving on horseback or similarly; as, mounted\n   police; mounted infantry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mounted', 'a.', 'Placed on a suitable support, or fixed in a setting; as, a\n   mounted gun; a mounted map; a mounted gem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mountenaunce', 'n.', 'Mountance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mounter', 'n.', 'One who mounts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mounter', 'n.', 'An animal mounted; a monture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mounting', 'n.', 'The act of one that mounts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mounting', 'n.', 'That by which anything is prepared for use, or set off to\n   advantage; equipment; embellishment; setting; as, the mounting of a\n   sword or diamond.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mountingly', 'adv.', 'In an ascending manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mountlet', 'n.', 'A small or low mountain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mounty', 'v.', 'The rise of a hawk after prey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mourned', 'imp. & p. p.', 'of Mourn');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mourning', 'p. pr. & vb. n.', 'of Mourn');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mourn', 'v. i.', 'To express or to feel grief or sorrow; to grieve; to be\n   sorrowful; to lament; to be in a state of grief or sadness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mourn', 'v. i.', 'To wear the customary garb of a mourner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mourn', 'v. t.', 'To grieve for; to lament; to deplore; to bemoan; to\n   bewail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mourn', 'v. t.', 'To utter in a mournful manner or voice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mourne', 'n.', 'The armed or feruled end of a staff; in a sheephook, the\n   end of the staff to which the hook is attached.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mourner', 'n.', 'One who mourns or is grieved at any misfortune, as the\n   death of a friend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mourner', 'n.', 'One who attends a funeral as a hired mourner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mournful', 'a.', 'Full of sorrow; expressing, or intended to express,\n   sorrow; mourning; grieving; sad; also, causing sorrow; saddening;\n   grievous; as, a mournful person; mournful looks, tones, loss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mourning', 'n.', 'The act of sorrowing or expressing grief; lamentation;\n   sorrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mourning', 'n.', 'Garb, drapery, or emblems indicative of grief, esp.\n   clothing or a badge of somber black.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mourning', 'a.', 'Grieving; sorrowing; lamenting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mourning', 'a.', 'Employed to express sorrow or grief; worn or used as\n   appropriate to the condition of one bereaved or sorrowing; as, mourning\n   garments; a mourning ring; a mourning pin, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mourningly', 'adv.', 'In a mourning manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mournival', 'n.', 'See Murnival.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mice', 'pl. ', 'of Mouse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mouse', 'n.', 'Any one of numerous species of small rodents belonging to\n   the genus Mus and various related genera of the family Muridae. The\n   common house mouse (Mus musculus) is found in nearly all countries. The\n   American white-footed, or deer, mouse (Hesperomys leucopus) sometimes\n   lives in houses. See Dormouse, Meadow mouse, under Meadow, and Harvest\n   mouse, under Harvest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mouse', 'n.', 'A knob made on a rope with spun yarn or parceling to prevent\n   a running eye from slipping.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mouse', 'n.', 'Same as 2d Mousing, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mouse', 'n.', 'A familiar term of endearment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mouse', 'n.', 'A dark-colored swelling caused by a blow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mouse', 'n.', 'A match used in firing guns or blasting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moused', 'imp. & p. p.', 'of Mouse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mousing', 'p. pr. & vb. n.', 'of Mouse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mouse', 'v. i.', 'To watch for and catch mice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mouse', 'v. i.', 'To watch for or pursue anything in a sly manner; to pry\n   about, on the lookout for something.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mouse', 'v. t.', 'To tear, as a cat devours a mouse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mouse', 'v. t.', 'To furnish with a mouse; to secure by means of a mousing.\n   See Mouse, n., 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mouse-ear', 'n.', 'The forget-me-not (Myosotis palustris) and other species\n   of the same genus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mouse-ear', 'n.', 'A European species of hawkweed (Hieracium Pilosella).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mousefish', 'n.', 'See Frogfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mousehole', 'n.', 'A hole made by a mouse, for passage or abode, as in a\n   wall; hence, a very small hole like that gnawed by a mouse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mousekin', 'n.', 'A little mouse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mouser', 'n.', 'A cat that catches mice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mouser', 'n.', 'One who pries about on the lookout for something.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mousetail', 'n.', 'A genus of ranunculaceous plants (Myosurus), in which\n   the prolonged receptacle is covered with imbricating achenes, and so\n   resembles the tail of a mouse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mousie', 'n.', 'Diminutive for Mouse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mousing', 'a.', 'Impertinently inquisitive; prying; meddlesome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mousing', 'n.', 'The act of hunting mice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mousing', 'n.', 'A turn or lashing of spun yarn or small stuff, or a\n   metallic clasp or fastening, uniting the point and shank of a hook to\n   prevent its unhooking or straighening out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mousing', 'n.', 'A ratchet movement in a loom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mousle', 'v. t.', 'To sport with roughly; to rumple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mousseline', 'n.', 'Muslin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moustache', 'n.', 'Mustache.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mousy', 'a.', 'Infested with mice; smelling of mice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moutan', 'n.', 'The Chinese tree peony (Paeonia Mountan), a shrub with\n   large flowers of various colors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mouths', 'pl. ', 'of Mouth');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mouth', 'n.', 'The opening through which an animal receives food; the\n   aperture between the jaws or between the lips; also, the cavity,\n   containing the tongue and teeth, between the lips and the pharynx; the\n   buccal cavity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mouth', 'n.', 'An opening affording entrance or exit; orifice; aperture;');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mouth', 'n.', 'The opening of a vessel by which it is filled or emptied,\n   charged or discharged; as, the mouth of a jar or pitcher; the mouth of\n   the lacteal vessels, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mouth', 'n.', 'The opening or entrance of any cavity, as a cave, pit, well,\n   or den.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mouth', 'n.', 'The opening of a piece of ordnance, through which it is\n   discharged.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mouth', 'n.', 'The opening through which the waters of a river or any\n   stream are discharged.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mouth', 'n.', 'The entrance into a harbor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mouth', 'n.', 'The crosspiece of a bridle bit, which enters the mouth of an\n   animal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mouth', 'n.', 'A principal speaker; one who utters the common opinion; a\n   mouthpiece.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mouth', 'n.', 'Cry; voice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mouth', 'n.', 'Speech; language; testimony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mouth', 'n.', 'A wry face; a grimace; a mow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mouthed', 'imp. & p. p.', 'of Mouth');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mouthing', 'p. pr. & vb. n.', 'of Mouth');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mouth', 'v. t.', 'To take into the mouth; to seize or grind with the mouth\n   or teeth; to chew; to devour.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mouth', 'v. t.', 'To utter with a voice affectedly big or swelling; to\n   speak in a strained or unnaturally sonorous manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mouth', 'v. t.', 'To form or cleanse with the mouth; to lick, as a bear her\n   cub.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mouth', 'v. t.', 'To make mouths at.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mouth', 'v. i.', 'To speak with a full, round, or loud, affected voice; to\n   vociferate; to rant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mouth', 'v. i.', 'To put mouth to mouth; to kiss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mouth', 'v. i.', 'To make grimaces, esp. in ridicule or contempt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mouthed', 'a.', 'Furnished with a mouth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mouthed', 'a.', 'Having a mouth of a particular kind; using the mouth,\n   speech, or voice in a particular way; -- used only in composition; as,\n   wide-mouthed; hard-mouthed; foul-mouthed; mealy-mouthed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mouther', 'n.', 'One who mouths; an affected speaker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mouth-footed', 'a.', 'Having the basal joints of the legs converted into\n   jaws.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mouthfuls', 'pl. ', 'of Mouthful');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mouthful', 'n.', 'As much as is usually put into the mouth at one time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mouthful', 'n.', 'Hence, a small quantity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mouthless', 'a.', 'Destitute of a mouth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mouth-made', 'a.', 'Spoken without sincerity; not heartfelt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mouthpiece', 'n.', 'The part of a musical or other instrument to which the\n   mouth is applied in using it; as, the mouthpiece of a bugle, or of a\n   tobacco pipe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mouthpiece', 'n.', 'An appendage to an inlet or outlet opening of a pipe or\n   vessel, to direct or facilitate the inflow or outflow of a fluid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mouthpiece', 'n.', 'One who delivers the opinion of others or of another; a\n   spokesman; as, the mouthpiece of his party.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Movability', 'n.', 'Movableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Movable', 'a.', 'Capable of being moved, lifted, carried, drawn, turned, or\n   conveyed, or in any way made to change place or posture; susceptible of\n   motion; not fixed or stationary; as, a movable steam engine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Movable', 'a.', 'Changing from one time to another; as, movable feasts, i.\n   e., church festivals, the date of which varies from year to year.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Movables', 'pl. ', 'of Movable');
  INSERT INTO words (word, word_type, description) VALUES  (    'Movable', 'n.', 'An article of wares or goods; a commodity; a piece of\n   property not fixed, or not a part of real estate; generally, in the\n   plural, goods; wares; furniture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Movable', 'n.', 'Property not attached to the soil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Movableness', 'n.', 'The quality or state of being movable; mobility;\n   susceptibility of motion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Movably', 'adv.', 'In a movable manner or condition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moved', 'imp. & p. p.', 'of Move');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moving', 'p. pr. & vb. n.', 'of Move');
  INSERT INTO words (word, word_type, description) VALUES  (    'Move', 'v. t.', 'To cause to change place or posture in any manner; to set\n   in motion; to carry, convey, draw, or push from one place to another;\n   to impel; to stir; as, the wind moves a vessel; the horse moves a\n   carriage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Move', 'v. t.', 'To transfer (a piece or man) from one space or position to\n   another, according to the rules of the game; as, to move a king.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Move', 'v. t.', 'To excite to action by the presentation of motives; to\n   rouse by representation, persuasion, or appeal; to influence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Move', 'v. t.', 'To arouse the feelings or passions of; especially, to\n   excite to tenderness or compassion; to touch pathetically; to excite,\n   as an emotion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Move', 'v. t.', 'To propose; to recommend; specifically, to propose\n   formally for consideration and determination, in a deliberative\n   assembly; to submit, as a resolution to be adopted; as, to move to\n   adjourn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Move', 'v. t.', 'To apply to, as for aid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Move', 'v. i.', 'To change place or posture; to stir; to go, in any manner,\n   from one place or position to another; as, a ship moves rapidly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Move', 'v. i.', 'To act; to take action; to stir; to begin to act; as, to\n   move in a matter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Move', 'v. i.', 'To change residence; to remove, as from one house, town,\n   or state, to another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Move', 'v. i.', 'To change the place of a piece in accordance with the\n   rules of the game.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Move', 'n.', 'The act of moving; a movement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Move', 'n.', 'The act of moving one of the pieces, from one position to\n   another, in the progress of the game.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Move', 'n.', 'An act for the attainment of an object; a step in the\n   execution of a plan or purpose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moveless', 'a.', 'Motionless; fixed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Movement', 'n.', 'The act of moving; change of place or posture;\n   transference, by any means, from one situation to another; natural or\n   appropriate motion; progress; advancement; as, the movement of an army\n   in marching or maneuvering; the movement of a wheel or a machine; the\n   party of movement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Movement', 'n.', 'Motion of the mind or feelings; emotion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Movement', 'n.', 'Manner or style of moving; as, a slow, or quick, or\n   sudden, movement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Movement', 'n.', 'The rhythmical progression, pace, and tempo of a piece.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Movement', 'n.', 'One of the several strains or pieces, each complete in\n   itself, with its own time and rhythm, which make up a larger work; as,\n   the several movements of a suite or a symphony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Movement', 'n.', 'A system of mechanism for transmitting motion of a\n   definite character, or for transforming motion; as, the wheelwork of a\n   watch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Movent', 'a.', 'Moving.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Movent', 'n.', 'That which moves anything.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mover', 'n.', 'A person or thing that moves, stirs, or changes place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mover', 'n.', 'A person or thing that imparts motion, or causes change of\n   place; a motor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mover', 'n.', 'One who, or that which, excites, instigates, or causes\n   movement, change, etc.; as, movers of sedition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mover', 'n.', 'A proposer; one who offers a proposition, or recommends\n   anything for consideration or adoption; as, the mover of a resolution\n   in a legislative body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moving', 'a.', 'Changing place or posture; causing motion or action; as, a\n   moving car, or power.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moving', 'a.', 'Exciting movement of the mind; adapted to move the\n   sympathies, passions, or affections; touching; pathetic; as, a moving\n   appeal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moving', 'n.', 'The act of changing place or posture; esp., the act of\n   changing ones dwelling place or place of business.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Movingly', 'adv.', 'In a moving manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Movingness', 'n.', 'The power of moving.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mow', 'n.', 'A wry face.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mow', 'v. i.', 'To make mouths.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mow', 'n.', 'Same as Mew, a gull.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mow', 'pres. sing.', 'of Mow');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mowe', 'pl.', 'of Mow');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mowen', '', 'of Mow');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moun', '', 'of Mow');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mow', 'v.', 'May; can.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mowed', 'imp.', 'of Mow');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mowed', 'p. p.', 'of Mow');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mown', '', 'of Mow');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mowing', 'p. pr. & vb. n.', 'of Mow');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mow', 'v. t.', 'To cut down, as grass, with a scythe or machine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mow', 'v. t.', 'To cut the grass from; as, to mow a meadow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mow', 'v. t.', 'To cut down; to cause to fall in rows or masses, as in\n   mowing grass; -- with down; as, a discharge of grapeshot mows down\n   whole ranks of men.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mow', 'v. i.', 'To cut grass, etc., with a scythe, or with a machine; to\n   cut grass for hay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mow', 'n.', 'A heap or mass of hay or of sheaves of grain stowed in a barn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mow', 'n.', 'The place in a barn where hay or grain in the sheaf is stowed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mow', 'v. t.', 'To lay, as hay or sheaves of grain, in a heap or mass in a\n   barn; to pile and stow away.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mowburn', 'v. i.', 'To heat and ferment in the mow, as hay when housed too\n   green.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mowe', 'v.', 'See 4th Mow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mowe', 'n. & v.', 'See 1st & 2d Mow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mower', 'n.', 'One who, or that which, mows; a mowing machine; as, a lawn\n   mower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mowing', 'n.', 'The act of one who, or the operation of that which, mows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mowing', 'n.', 'Land from which grass is cut; meadow land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mown', 'p. p. & a.', 'Cut down by mowing, as grass; deprived of grass by\n   mowing; as, a mown field.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mowyer', 'n.', 'A mower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moxa', 'n.', 'A soft woolly mass prepared from the young leaves of\n   Artemisia Chinensis, and used as a cautery by burning it on the skin;\n   hence, any substance used in a like manner, as cotton impregnated with\n   niter, amadou.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moxa', 'n.', 'A plant from which this substance is obtained, esp. Artemisia\n   Chinensis, and A. moxa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moxie', 'n.', 'energy; pep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moxie', 'n.', 'courage, determination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moxie', 'n.', 'Know-how, expertise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moya', 'n.', 'Mud poured out from volcanoes during eruptions; -- so called\n   in South America.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moyle', 'n. & v.', 'See Moil, and Moile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mozarab', '', 'Alt. of Mozarabic');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mozarabic', '', 'Same as Muzarab, Muzarabic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mozetta', 'n.', 'Alt. of Mozzetta');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mozzetta', 'n.', 'A cape, with a small hood; -- worn by the pope and other\n   dignitaries of the Roman Catholic Church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mr.', '', 'The customary abbreviation of Mister in writing and printing.\n   See Master, 4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mrs.', '', 'The customary abbreviation of Mistress when used as a title of\n   courtesy, in writing and printing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mucamide', 'n.', 'The acid amide of mucic acid, obtained as a white\n   crystalline substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mucate', 'n.', 'A salt of mucic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muce', 'n.', 'See Muse, and Muset.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mucedin', 'n.', 'A yellowish white, amorphous, nitrogenous substance found\n   in wheat, rye, etc., and resembling gluten; -- formerly called also\n   mucin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Much', 'Compar. & superl. wa', 'Great in quantity;\n   long in duration; as, much rain has fallen; much time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Much', 'Compar. & superl. wa', 'Many in number.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Much', 'Compar. & superl. wa', 'High in rank or\n   position.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Much', 'n.', 'A great quantity; a great deal; also, an indefinite quantity;\n   as, you have as much as I.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Much', 'n.', 'A thing uncommon, wonderful, or noticeable; something\n   considerable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Much', 'a.', 'To a great degree or extent; greatly; abundantly; far;\n   nearly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muchel', 'a.', 'Much.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muchness', 'n.', 'Greatness; extent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muchwhat', 'adv.', 'Nearly; almost; much.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mucic', 'a.', 'Pertaining to, or derived from, gums and micilaginous\n   substances; specif., denoting an acid obtained by the oxidation of\n   gums, dulcite, etc., as a white crystalline substance isomeric with\n   saccharic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mucid', 'a.', 'Musty; moldy; slimy; mucous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mucific', 'a.', 'Inducing or stimulating the secretion of mucus;\n   blennogenous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mucific', 'a.', 'Secreting mucus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muciform', 'a.', 'Resembling mucus; having the character or appearance of\n   mucus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mucigen', 'n.', 'A substance which is formed in mucous epithelial cells,\n   and gives rise to mucin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mucigenous', 'a.', 'Connected with the formation of mucin; resembling\n   mucin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mucilage', 'n.', 'A gummy or gelatinous substance produced in certain\n   plants by the action of water on the cell wall, as in the seeds of\n   quinces, of flax, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mucilage', 'n.', 'An aqueous solution of gum, or of substances allied to\n   it; as, medicinal mucilage; mucilage for fastening envelopes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mucilaginous', 'a.', 'Partaking of the nature of, or resembling, mucilage;\n   moist, soft, and viscid; slimy; ropy; as, a mucilaginous liquid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mucilaginous', 'a.', 'Of, pertaining to, or secreting, mucilage; as, the\n   mucilaginous glands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mucilaginous', 'a.', 'Soluble in water, but not in alcohol; yielding\n   mucilage; as, mucilaginous gums or plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mucin', 'n.', 'See Mucedin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mucin', 'n.', 'An albuminoid substance which is contained in mucus, and\n   gives to the latter secretion its peculiar ropy character. It is found\n   in all the secretions from mucous glands, and also between the fibers\n   of connective tissue, as in tendons. See Illust. of Demilune.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mucinogen', 'n.', 'Same as Mucigen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muciparous', 'a.', 'Secreting, or producing, mucus or mucin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mucivore', 'n.', 'An insect which feeds on mucus, or the sap of plants, as\n   certain Diptera, of the tribe Mucivora.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muck', '', 'abbreviation of Amuck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muck', 'n.', 'Dung in a moist state; manure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muck', 'n.', 'Vegetable mold mixed with earth, as found in low, damp places\n   and swamps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muck', 'n.', 'Anything filthy or vile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muck', 'n.', 'Money; -- in contempt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muck', 'a.', 'Like muck; mucky; also, used in collecting or distributing\n   muck; as, a muck fork.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muck', 'v. t.', 'To manure with muck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muckender', 'n.', 'A handkerchief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mucker', 'n.', 'A term of reproach for a low or vulgar labor person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mucker', 'v. t.', 'To scrape together, as money, by mean labor or shifts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muckerer', 'n.', 'A miser; a niggard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muckiness', 'n.', 'The quality of being mucky.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muckle', 'a.', 'Much.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muckmidden', 'n.', 'A dunghill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mucksy', 'a.', 'Somewhat mucky; soft, sticky, and dirty; muxy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muckworm', 'n.', 'A larva or grub that lives in muck or manure; -- applied\n   to the larvae of the tumbledung and allied beetles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muckworm', 'n.', 'One who scrapes together money by mean labor and devices;\n   a miser.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mucky', 'a.', 'Filthy with muck; miry; as, a mucky road.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mucky', 'a.', 'Vile, in a moral sense; sordid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mucocele', 'n.', 'An enlargement or protrusion of the mucous membrane of\n   the lachrymal passages, or dropsy of the lachrymal sac, dependent upon\n   catarrhal inflammation of the latter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mucoid', 'a.', 'Resembling mucus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muconate', 'n.', 'A salt of muconic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muconic', 'a.', 'Of, pertaining to, or designating, an organic acid,\n   obtained indirectly from mucic acid, and somewhat resembling itaconic\n   acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mucopurulent', 'a.', 'Having the character or appearance of both mucus and\n   pus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mucor', 'n.', 'A genus of minute fungi. The plants consist of slender\n   threads with terminal globular sporangia; mold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mucosity', 'n.', 'The quality or state of being mucous or slimy;\n   mucousness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mucous', 'a.', 'Of, pertaining to, or resembling, mucus; slimy, ropy, or\n   stringy, and lubricous; as, a mucous substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mucous', 'a.', 'Secreting a slimy or mucigenous substance; as, the mucous\n   membrane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mucousness', 'n.', 'The quality or state of being mucous; sliminess.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mucro', 'n.', 'A minute abrupt point, as of a leaf; any small, sharp point\n   or process, terminating a larger part or organ.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mucronate', 'a.', 'Alt. of Mucronated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mucronated', 'a.', 'Ending abruptly in a sharp point; abruptly tipped with\n   a short and sharp point; as, a mucronate leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mucronulate', 'a.', 'Having, or tipped with, a small point or points.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muculent', 'a.', 'Slimy; moist, and moderately viscous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mucus', 'n.', 'A viscid fluid secreted by mucous membranes, which it serves\n   to moisten and protect. It covers the lining membranes of all the\n   cavities which open externally, such as those of the mouth, nose,\n   lungs, intestinal canal, urinary passages, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mucus', 'n.', 'Any other animal fluid of a viscid quality, as the synovial\n   fluid, which lubricates the cavities of the joints; -- improperly so\n   used.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mucus', 'n.', 'A gelatinous or slimy substance found in certain algae and\n   other plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mucusin', 'n.', 'Mucin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mud', 'n.', 'Earth and water mixed so as to be soft and adhesive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mud', 'v. t.', 'To bury in mud.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mud', 'v. t.', 'To make muddy or turbid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mudar', 'n.', 'Either one of two asclepiadaceous shrubs (Calotropis\n   gigantea, and C. procera), which furnish a strong and valuable fiber.\n   The acrid milky juice is used medicinally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mudarin', 'n.', 'A brown, amorphous, bitter substance having a strong\n   emetic action, extracted from the root of the mudar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muddily', 'adv.', 'In a muddy manner; turbidly; without mixture; cloudily;\n   obscurely; confusedly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muddiness', 'n.', 'The condition or quality of being muddy; turbidness;\n   foulness caused by mud, dirt, or sediment; as, the muddiness of a\n   stream.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muddiness', 'n.', 'Obscurity or confusion, as in treatment of a subject;\n   intellectual dullness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muddled', 'imp. & p. p.', 'of Muddle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muddling', 'p. pr. & vb. n.', 'of Muddle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muddle', 'v. t.', 'To make turbid, or muddy, as water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muddle', 'v. t.', 'To cloud or stupefy; to render stupid with liquor; to\n   intoxicate partially.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muddle', 'v. t.', 'To waste or misuse, as one does who is stupid or\n   intoxicated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muddle', 'v. t.', 'To mix confusedly; to confuse; to make a mess of; as, to\n   muddle matters; also, to perplex; to mystify.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muddle', 'v. i.', 'To dabble in mud.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muddle', 'v. i.', 'To think and act in a confused, aimless way.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muddle', 'n.', 'A state of being turbid or confused; hence, intellectual\n   cloudiness or dullness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muddlehead', 'n.', 'A stupid person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muddler', 'n.', 'One who, or that which, muddles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muddy', 'superl.', 'Abounding in mud; besmeared or dashed with mud; as, a\n   muddy road or path; muddy boots.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muddy', 'superl.', 'Turbid with mud; as, muddy water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muddy', 'superl.', 'Consisting of mud or earth; gross; impure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muddy', 'superl.', 'Confused, as if turbid with mud; cloudy in mind; dull;\n   stupid; also, immethodical; incoherent; vague.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muddy', 'superl.', 'Not clear or bright.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muddied', 'imp. & p. p.', 'of Muddy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muddying', 'p. pr. & vb. n.', 'of Muddy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muddy', 'v. t.', 'To soil with mud; to dirty; to render turbid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muddy', 'v. t.', 'Fig.: To cloud; to make dull or heavy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muddy-headed', 'a.', 'Dull; stupid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muddy-mettled', 'a.', 'Dull-spirited.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mudfish', 'n.', 'The European loach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mudfish', 'n.', 'The bowfin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mudfish', 'n.', 'The South American lipedosiren, and the allied African\n   species (Protopterus annectens). See Lipedosiren.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mudfish', 'n.', 'The mud minnow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mudhole', 'n.', 'A hole, or hollow place, containing mud, as in a road.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mudhole', 'n.', 'A hole near the bottom, through which the sediment is\n   withdrawn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mudir', 'n.', 'Same as Moodir.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mudsill', 'n.', 'The lowest sill of a structure, usually embedded in the\n   soil; the lowest timber of a house; also, that sill or timber of a\n   bridge which is laid at the bottom of the water. See Sill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mudsucker', 'n.', 'A woodcock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mudwall', 'n.', 'The European bee-eater. See Bee-eater.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mudwort', 'n.', 'A small herbaceous plant growing on muddy shores\n   (Limosella aquatica).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mue', 'v. i.', 'To mew; to molt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muezzin', 'n.', 'A Mohammedan crier of the hour of prayer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muff', 'n.', 'A soft cover of cylindrical form, usually of fur, worn by\n   women to shield the hands from cold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muff', 'n.', 'A short hollow cylinder surrounding an object, as a pipe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muff', 'n.', 'A blown cylinder of glass which is afterward flattened out to\n   make a sheet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muff', 'n.', 'A stupid fellow; a poor-spirited person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muff', 'n.', 'A failure to hold a ball when once in the hands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muff', 'n.', 'The whitethroat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muffed', 'imp. & p. p.', 'of Muff');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muffing', 'p. pr. & vb. n.', 'of Muff');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muff', 'v. t.', 'To handle awkwardly; to fumble; to fail to hold, as a\n   ball, in catching it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muffetee', 'n.', 'A small muff worn over the wrist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muffin', 'n.', 'A light, spongy, cylindrical cake, used for breakfast and\n   tea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muffineer', 'n.', 'A dish for keeping muffins hot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muffish', 'a.', 'Stupid; awkward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muffle', 'n.', 'The bare end of the nose between the nostrils; -- used esp.\n   of ruminants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muffled', 'imp. & p. p.', 'of Muffle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muffling', 'p. pr. & vb. n.', 'of Muffle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muffle', 'v. t.', 'To wrap up in something that conceals or protects; to\n   wrap, as the face and neck, in thick and disguising folds; hence, to\n   conceal or cover the face of; to envelop; to inclose; -- often with up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muffle', 'v. t.', 'To prevent seeing, or hearing, or speaking, by wraps\n   bound about the head; to blindfold; to deafen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muffle', 'v. t.', 'To wrap with something that dulls or deadens the sound\n   of; as, to muffle the strings of a drum, or that part of an oar which\n   rests in the rowlock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muffle', 'v. i.', 'To speak indistinctly, or without clear articulation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muffle', 'v. t.', 'Anything with which another thing, as an oar or drum, is\n   muffled; also, a boxing glove; a muff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muffle', 'v. t.', 'An earthenware compartment or oven, often shaped like a\n   half cylinder, used in furnaces to protect objects heated from the\n   direct action of the fire, as in scorification of ores, cupellation of\n   ore buttons, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muffle', 'v. t.', 'A small oven for baking and fixing the colors of painted\n   or printed pottery, without exposing the pottery to the flames of the\n   furnace or kiln.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muffle', 'v. t.', 'A pulley block containing several sheaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muffler', 'n.', 'Anything used in muffling; esp., a scarf for protecting\n   the head and neck in cold weather; a tippet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muffler', 'n.', 'A cushion for terminating or softening a note made by a\n   stringed instrument with a keyboard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muffler', 'n.', 'A kind of mitten or boxing glove, esp. when stuffed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muffler', 'n.', 'One who muffles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muflon', 'n.', 'See Mouflon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muftis', 'pl. ', 'of Mufti');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mufti', 'n.', 'An official expounder of Mohammedan law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mufti', 'n.', 'Citizens dress when worn by a naval or military officer; --\n   a term derived from the British service in India.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mug', 'n.', 'A kind of earthen or metal drinking cup, with a handle, --\n   usually cylindrical and without a lip.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mug', 'n.', 'The face or mouth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muggard', 'a.', 'Sullen; displeased.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mugget', 'n.', 'The small entrails of a calf or a hog.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mugginess', 'n.', 'The condition or quality of being muggy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muggish', 'a.', 'See Muggy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muggletonian', 'n.', 'One of an extinct sect, named after Ludovic\n   Muggleton, an English journeyman tailor, who (about 1657) claimed to be\n   inspired.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muggy', 'superl.', 'Moist; damp; moldy; as, muggy straw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muggy', 'superl.', 'Warm, damp, and close; as, muggy air, weather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mughouse', 'n.', 'An alehouse; a pothouse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mugiency', 'n.', 'A bellowing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mugient', 'a.', 'Lowing; bellowing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mugil', 'n.', 'A genus of fishes including the gray mullets. See Mullet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mugiloid', 'a.', 'Like or pertaining to the genus Mugil, or family\n   Mugilidae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mugweed', 'n.', 'A slender European weed (Galium Cruciata); -- called also\n   crossweed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mugwort', 'n.', 'A somewhat aromatic composite weed (Artemisia vulgaris),\n   at one time used medicinally; -- called also motherwort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mugwump', 'n.', 'A bolter from the Republican party in the national\n   election of 1884; an Independent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mugwumpery', 'n.', 'Alt. of Mugwumpism');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mugwumpism', 'n.', 'The acts and views of the mugwumps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muhammadan', 'a. & n.', 'Alt. of Muhammedan');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muhammedan', 'a. & n.', 'Mohammedan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muhammadanism', 'n.', 'Mohammedanism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mulada', 'n.', 'A moor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mulada', 'n.', 'A drove of mules.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mulattoes', 'pl. ', 'of Mulatto');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mulatto', 'n.', 'The offspring of a negress by a white man, or of a white\n   woman by a negro, -- usually of a brownish yellow complexion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mulattress', 'n.', 'A female mulatto.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mulberries', 'pl. ', 'of Mulberry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mulberry', 'n.', 'The berry or fruit of any tree of the genus Morus; also,\n   the tree itself. See Morus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mulberry', 'n.', 'A dark pure color, like the hue of a black mulberry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mulberry-faced', 'a.', 'Having a face of a mulberry color, or blotched as\n   if with mulberry stains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mulch', 'n.', 'Half-rotten straw, or any like substance strewn on the\n   ground, as over the roots of plants, to protect from heat, drought,\n   etc., and to preserve moisture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mulched', 'imp. & p. p.', 'of Mulch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mulching', 'p. pr. & vb. n.', 'of Mulch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mulch', 'v. t.', 'To cover or dress with mulch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mulct', 'n.', 'A fine or penalty, esp. a pecuniary punishment or penalty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mulct', 'n.', 'A blemish or defect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mulcted', 'imp. & p. p.', 'of Mulct');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mulcting', 'p. pr. & vb. n.', 'of Mulct');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mulct', 'v. t.', 'To punish for an offense or misdemeanor by imposing a\n   fine or forfeiture, esp. a pecuniary fine; to fine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mulct', 'v. t.', 'Hence, to deprive of; to withhold by way of punishment or\n   discipline.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mulctary', 'a.', 'Alt. of Mulctuary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mulctuary', 'a.', 'Imposing a pecuniary penalty; consisting of, or paid as,\n   a fine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mule', 'n.', 'A hybrid animal; specifically, one generated between an ass\n   and a mare, sometimes a horse and a she-ass. See Hinny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mule', 'n.', 'A plant or vegetable produced by impregnating the pistil of\n   one species with the pollen or fecundating dust of another; -- called\n   also hybrid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mule', 'n.', 'A very stubborn person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mule', 'n.', 'A machine, used in factories, for spinning cotton, wool,\n   etc., into yarn or thread and winding it into cops; -- called also\n   jenny and mule-jenny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mule-jenny', 'n.', 'See Mule, 4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muleteer', 'n.', 'One who drives mules.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mulewort', 'n.', 'A fern of the genus Hemionitis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muley', 'n.', 'A stiff, long saw, guided at the ends but not stretched in a\n   gate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muley', 'n.', 'See Mulley.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muliebrity', 'n.', 'The state of being a woman or of possessing full\n   womanly powers; womanhood; -- correlate of virility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muliebrity', 'n.', 'Hence: Effeminancy; softness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mulier', 'n.', 'A woman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mulier', 'n.', 'Lawful issue born in wedlock, in distinction from an elder\n   brother born of the same parents before their marriage; a lawful son.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mulier', 'n.', 'A woman; a wife; a mother.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mulierly', 'adv.', 'In the manner or condition of a mulier; in wedlock;\n   legitimately.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mulierose', 'a.', 'Fond of woman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mulierosity', 'n.', 'A fondness for women.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mulierty', 'n.', 'Condition of being a mulier; position of one born in\n   lawful wedlock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mulish', 'a.', 'Like a mule; sullen; stubborn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mull', 'n.', 'A thin, soft kind of muslin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mull', 'n.', 'A promontory; as, the Mull of Cantyre.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mull', 'n.', 'A snuffbox made of the small end of a horn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mull', 'n.', 'Dirt; rubbish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mull', 'v. t.', 'To powder; to pulverize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mull', 'v. i.', 'To work (over) mentally; to cogitate; to ruminate; --\n   usually with over; as, to mull over a thought or a problem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mull', 'n.', 'An inferior kind of madder prepared from the smaller roots or\n   the peelings and refuse of the larger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mulled', 'imp. & p. p.', 'of Mull');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mulling', 'p. pr. & vb. n.', 'of Mull');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mull', 'v. t.', 'To heat, sweeten, and enrich with spices; as, to mull\n   wine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mull', 'v. t.', 'To dispirit or deaden; to dull or blunt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mulla', 'n.', 'Same as Mollah.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mullagatawny', 'n.', 'An East Indian curry soup.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mullah', 'n.', 'See Mollah.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mullar', 'n.', 'A die, cut in intaglio, for stamping an ornament in relief,\n   as upon metal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mullein', 'n.', 'Any plant of the genus Verbascum. They are tall herbs\n   having coarse leaves, and large flowers in dense spikes. The common\n   species, with densely woolly leaves, is Verbascum Thapsus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mullen', 'n.', 'See Mullein.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muller', 'n.', 'One who, or that which, mulls.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muller', 'n.', 'A vessel in which wine, etc., is mulled over a fire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muller', 'n.', 'A stone or thick lump of glass, or kind of pestle, flat at\n   the bottom, used for grinding pigments or drugs, etc., upon a slab of\n   similar material.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mullerian', 'a.', 'Of, pertaining to, or discovered by, Johannes Muller.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mullet', 'n.', 'Any one of numerous fishes of the genus Mugil; -- called\n   also gray mullets. They are found on the coasts of both continents, and\n   are highly esteemed as food. Among the most valuable species are Mugil\n   capito of Europe, and M. cephalus which occurs both on the European and\n   American coasts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mullet', 'n.', 'Any species of the genus Mullus, or family Mullidae; called\n   also red mullet, and surmullet, esp. the plain surmullet (Mullus\n   barbatus), and the striped surmullet (M. surmulletus) of Southern\n   Europe. The former is the mullet of the Romans. It is noted for the\n   brilliancy of its colors. See Surmullet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mullet', 'n.', 'A star, usually five pointed and pierced; -- when used as a\n   difference it indicates the third son.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mullet', 'n.', 'Small pinchers for curling the hair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mulley', 'n.', 'Alt. of Moolley');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moolley', 'n.', 'A mulley or polled animal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moolley', 'n.', 'A cow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mulley', 'a.', 'Alt. of Moolley');
  INSERT INTO words (word, word_type, description) VALUES  (    'Moolley', 'a.', 'Destitute of horns, although belonging to a species of\n   animals most of which have horns; hornless; polled; as, mulley cattle;\n   a mulley (or moolley) cow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mulligatawny', 'n.', 'See Mullagatawny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mulligrubs', 'n.', 'A griping of the intestines; colic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mulligrubs', 'n.', 'Hence, sullenness; the sulks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mullingong', 'n.', 'See Duck mole, under Duck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mullion', 'n.', 'A slender bar or pier which forms the division between the\n   lights of windows, screens, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mullion', 'n.', 'An upright member of a framing. See Stile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mullion', 'v. t.', 'To furnish with mullions; to divide by mullions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mullock', 'n.', 'Rubbish; refuse; dirt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mulliod', 'a.', 'Like or pertaining to the genus Mullus, which includes the\n   surmullet, or red mullet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mulmul', 'n.', 'A fine, soft muslin; mull.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mulse', 'n.', 'Wine boiled and mingled with honey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mult-', '', 'See Multi-.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multangular', 'a.', 'Having many angles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multanimous', 'a.', 'Many-minded; many-sided.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multarticulate', 'a.', 'Having many articulations or joints.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multeity', 'n.', 'Multiplicity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multi-', '', 'Alt. of Mult-');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mult-', '', 'A prefix signifying much or many; several; more than one; as,\n   multiaxial, multocular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multiaxial', 'a.', 'Having more than one axis; developing in more than a\n   single line or plain; -- opposed to monoaxial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multicapsular', 'a.', 'Having many, or several, capsules.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multicarinate', 'a.', 'Many-keeled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multicavous', 'a.', 'Having many cavities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multicellular', 'a.', 'Consisting of, or having, many cells or more than\n   one cell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multicentral', 'a.', 'Having many, or several, centers; as, a multicentral\n   cell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multicipital', 'a.', 'Having many heads or many stems from one crown or\n   root.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multicolor', 'a.', 'Having many, or several, colors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multicostate', 'a.', 'Having numerous ribs, or costae, as the leaf of a\n   plant, or as certain shells and corals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multicuspid', 'a.', 'Multicuspidate; -- said of teeth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multicuspidate', 'a.', 'Having many cusps or points.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multidentate', 'a.', 'Having many teeth, or toothlike processes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multidigitate', 'a.', 'Having many fingers, or fingerlike processes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multifaced', 'a.', 'Having many faces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multifarious', 'a.', 'Having multiplicity; having great diversity or\n   variety; of various kinds; diversified; made up of many differing\n   parts; manifold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multifarious', 'a.', 'Having parts, as leaves, arranged in many vertical\n   rows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multifariously', 'adv.', 'With great multiplicity and diversity; with\n   variety of modes and relations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multifariousness', 'n.', 'Multiplied diversity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multifariousness', 'n.', 'The fault of improperly uniting in one bill\n   distinct and independent matters, and thereby confounding them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multiferous', 'a.', 'Bearing or producing much or many.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multifid', 'a.', 'Having many segments; cleft into several parts by linear\n   sinuses; as, a multifid leaf or corolla.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multiflorous', 'a.', 'Having many flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multiflue', 'a.', 'Having many flues; as, a multiflue boiler. See Boiler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multifoil', 'n.', 'An ornamental foliation consisting of more than five\n   divisions or foils.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multifoil', 'a.', 'Having more than five divisions or foils.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multifold', 'a.', 'Many times doubled; manifold; numerous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multiform', 'a.', 'Having many forms, shapes, or appearances.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multiformity', 'n.', 'The quality of being multiform; diversity of forms;\n   variety of appearances in the same thing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multiformous', 'a.', 'Multiform.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multigenerous', 'a.', 'Having many kinds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multigranulate', 'a.', 'Having, or consisting of, many grains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multijugate', 'a.', 'Having many pairs of leaflets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multijugous', 'a.', 'Consisting of many parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multijugous', 'a.', 'Same as Multijugate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multilateral', 'a.', 'Having many sides; many-sided.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multilineal', 'a.', 'Having many lines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multilobar', 'a.', 'Consisting of, or having, many lobes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multilocular', 'a.', 'Having many or several cells or compartments; as, a\n   multilocular shell or capsule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multiloquence', 'n.', 'Quality of being multiloquent; use of many words;\n   talkativeness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multiloquent', 'a.', 'Alt. of Multiloquous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multiloquous', 'a.', 'Speaking much; very talkative; loquacious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multiloquy', 'n.', 'Excess of words or talk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multinodate', 'a.', 'Having many knots or nodes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multinodous', 'a.', 'Same as Multinodate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multinomial', 'n. & a.', 'Same as Polynomial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multinominal', 'a.', 'Alt. of Multinominous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multinominous', 'a.', 'Having many names or terms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multinuclear', 'a.', 'Containing many nuclei; as, multinuclear cells.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multinucleate', 'a.', 'Alt. of Multinucleated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multinucleated', 'a.', 'Multinuclear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multiparous', 'a.', 'Producing many, or more than one, at a birth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multipartite', 'a.', 'Divided into many parts; having several parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multiped', 'n.', 'An insect having many feet, as a myriapod.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multiped', 'a.', 'Having many feet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multiple', 'a.', 'Containing more than once, or more than one; consisting\n   of more than one; manifold; repeated many times; having several, or\n   many, parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multiple', 'n.', 'A quantity containing another quantity a number of times\n   without a remainder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multiplex', 'a.', 'Manifold; multiple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multipliable', 'a.', 'Capable of being multiplied.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multiplicable', 'a.', 'Capable of being multiplied; multipliable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multiplicand', 'n.', 'The number which is to be multiplied by another\n   number called the multiplier. See Note under Multiplication.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multiplicate', 'a.', 'Consisting of many, or of more than one; multiple;\n   multifold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multiplication', 'n.', 'The act or process of multiplying, or of increasing\n   in number; the state of being multiplied; as, the multiplication of the\n   human species by natural generation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multiplication', 'n.', 'The process of repeating, or adding to itself, any\n   given number or quantity a certain number of times; commonly, the\n   process of ascertaining by a briefer computation the result of such\n   repeated additions; also, the rule by which the operation is performed;\n   -- the reverse of division.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multiplication', 'n.', 'An increase above the normal number of parts,\n   especially of petals; augmentation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multiplication', 'n.', 'The art of increasing gold or silver by magic, --\n   attributed formerly to the alchemists.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multiplicative', 'a.', 'Tending to multiply; having the power to multiply,\n   or incease numbers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multiplicatively', 'adv.', 'So as to multiply.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multiplicator', 'n.', 'The number by which another number is multiplied; a\n   multiplier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multiplicious', 'a.', 'Manifold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multiplicity', 'n.', 'The quality of being multiple, manifold, or various;\n   a state of being many; a multitude; as, a multiplicity of thoughts or\n   objects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multiplier', 'n.', 'One who, or that which, multiplies or increases number.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multiplier', 'n.', 'The number by which another number is multiplied. See\n   the Note under Multiplication.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multiplier', 'n.', 'An instrument for multiplying or increasing by\n   repetition or accumulation the intensity of a force or action, as heat\n   or electricity. It is particularly used to render such a force or\n   action appreciable or measurable when feeble. See Thermomultiplier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multiplied', 'imp. & p. p.', 'of Multiply');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multiplying', 'p. pr. & vb. n.', 'of Multiply');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multiply', 'v. t.', 'To increase in number; to make more numerous; to add\n   quantity to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multiply', 'v. t.', 'To add (any given number or quantity) to itself a\n   certain number of times; to find the product of by multiplication; thus\n   7 multiplied by 8 produces the number 56; to multiply two numbers. See\n   the Note under Multiplication.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multiply', 'v. t.', 'To increase (the amount of gold or silver) by the arts\n   of alchemy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multiply', 'v. i.', 'To become greater in number; to become numerous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multiply', 'v. i.', 'To increase in extent and influence; to spread.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multiply', 'v. i.', 'To increase amount of gold or silver by the arts of\n   alchemy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multipolar', 'a.', 'Having many poles; -- applied especially to those\n   ganglionic nerve cells which have several radiating processes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multipotent', 'a.', 'Having manifold power, or power to do many things.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multipresence', 'n.', 'The state or power of being multipresent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multipresent', 'a.', 'Being, or having the power to be, present in two or\n   more places at once.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multiradiate', 'a.', 'Having many rays.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multiramified', 'a.', 'Divided into many branches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multiramose', 'a.', 'Having many branches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multiscious', 'a.', 'Having much or varied knowledge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multisect', 'a.', 'Divided into many similar segments; -- said of an insect\n   or myriapod.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multiseptate', 'a.', 'Divided into many chambers by partitions, as the pith\n   of the pokeweed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multiserial', 'a.', 'Arranged in many rows, or series, as the scales of a\n   pine cone, or the leaves of the houseleek.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multisiliquous', 'a.', 'Having many pods or seed vessels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multisonous', 'a.', 'Having many sounds, or sounding much.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multispiral', 'a.', 'Having numerous spiral coils round a center or\n   nucleus; -- said of the opercula of certain shells.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multistriate', 'a.', 'Having many streaks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multisulcate', 'a.', 'Having many furrows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multisyllable', 'n.', 'A word of many syllables; a polysyllable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multititular', 'a.', 'Having many titles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multitubular', 'a.', 'Having many tubes; as, a multitubular boiler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multitude', 'n.', 'A great number of persons collected together; a numerous\n   collection of persons; a crowd; an assembly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multitude', 'n.', 'A great number of persons or things, regarded\n   collectively; as, the book will be read by a multitude of people; the\n   multitude of stars; a multitude of cares.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multitude', 'n.', 'The state of being many; numerousness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multitudinary', 'a.', 'Multitudinous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multitudinous', 'a.', 'Consisting of a multitude; manifold in number or\n   condition; as, multitudinous waves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multitudinous', 'a.', 'Of or pertaining to a multitude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multivagant', 'a.', 'Alt. of Multivagous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multivagous', 'a.', 'Wandering much.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multivalence', 'n.', 'Quality, state, or degree, of a multivalent element,\n   atom, or radical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multivalent', 'a.', 'Having a valence greater than one, as silicon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multivalent', 'a.', 'Having more than one degree of valence, as sulphur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multivalve', 'n.', 'Any mollusk which has a shell composed of more than two\n   pieces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multivalve', 'a.', 'Alt. of Multivalvular');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multivalvular', 'a.', 'Having many valves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multivalvular', 'a.', 'Many-valved; having more than two valves; -- said of\n   certain shells, as the chitons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multiversant', 'a.', 'Turning into many shapes; assuming many forms;\n   protean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multivious', 'a. & adv.', 'Having many ways or roads; by many ways.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multivocal', 'a.', 'Signifying many different things; of manifold meaning;\n   equivocal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multivocal', 'n.', 'A multivocal word.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multocular', 'a.', 'Having many eyes, or more than two.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multum', 'n.', 'An extract of quassia licorice, fraudulently used by\n   brewers in order to economize malt and hops.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Multungulate', 'a.', 'Having many hoofs.');
