  INSERT INTO words (word, word_type, description) VALUES  (    'Murrain', 'a.', 'Having, or afflicted with, murrain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Murrayin', 'n.', 'A glucoside found in the flowers of a plant (Murraya\n   exotica) of South Asia, and extracted as a white amorphous slightly\n   bitter substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Murre', 'n.', 'Any one of several species of sea birds of the genus Uria,\n   or Catarractes; a guillemot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Murrelet', 'n.', 'One of several species of sea birds of the genera\n   Synthliboramphus and Brachyramphus, inhabiting the North Pacific. They\n   are closely related to the murres.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Murrey', 'n.', 'A dark red color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Murrey', 'a.', 'Of a dark red color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Murrhine', 'a.', 'Made of the stone or material called by the Romans\n   murrha; -- applied to certain costly vases of great beauty and delicacy\n   used by the luxurious in Rome as wine cups; as, murrhine vases, cups,\n   vessels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Murrion', 'a.', 'Infected with or killed by murrain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Murrion', 'n.', 'A morion. See Morion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Murry', 'n.', 'See Muraena.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Murth', 'n.', 'Plenty; abundance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Murther', 'n. & v.', 'Murder, n. & v.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Murtherer', 'n.', 'A murderer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Murza', 'n.', 'One of the hereditary nobility among the Tatars, esp. one of\n   the second class.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mures', 'pl. ', 'of Mus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mus', 'n.', 'A genus of small rodents, including the common mouse and rat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Musae', 'pl. ', 'of Musa');
  INSERT INTO words (word, word_type, description) VALUES  (    'Musa', 'n.', 'A genus of perennial, herbaceous, endogenous plants of great\n   size, including the banana (Musa sapientum), the plantain (M.\n   paradisiaca of Linnaeus, but probably not a distinct species), the\n   Abyssinian (M. Ensete), the Philippine Island (M. textilis, which\n   yields Manila hemp), and about eighteen other species. See Illust. of\n   Banana and Plantain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Musaceous', 'a.', 'Of, pertaining to, or resembling, plants of the genus\n   Musa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Musal', 'a.', 'Of or pertaining to the Muses, or to Poetry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Musang', 'n.', 'A small animal of Java (Paradoxirus fasciatus), allied to\n   the civets. It swallows, but does not digest, large quantities of ripe\n   coffee berries, thus serving to disseminate the coffee plant; hence it\n   is called also coffee rat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Musar', 'n.', 'An itinerant player on the musette, an instrument formerly\n   common in Europe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Musard', 'v. i.', 'A dreamer; an absent-minded person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muscae', 'pl. ', 'of Musca');
  INSERT INTO words (word, word_type, description) VALUES  (    'Musca', 'n.', 'A genus of dipterous insects, including the common house\n   fly, and numerous allied species.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Musca', 'n.', 'A small constellation situated between the Southern Cross\n   and the Pole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muscadel', 'n.', 'See Muscatel, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muscadine', 'n.', 'A name given to several very different kinds of grapes,\n   but in America used chiefly for the scuppernong, or southern fox grape,\n   which is said to be the parent stock of the Catawba. See Grapevine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muscadine', 'n.', 'A fragrant and delicious pear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muscadine', 'n.', 'See Muscardin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muscales', 'n. pl.', 'An old name for mosses in the widest sense, including\n   the true mosses and also hepaticae and sphagna.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muscallonge', 'n.', 'See Muskellunge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muscardin', 'n.', 'The common European dormouse; -- so named from its odor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muscardine', 'n.', 'A disease which is very destructive to silkworms, and\n   which sometimes extends to other insects. It is attended by the\n   development of a fungus (provisionally called Botrytis bassiana). Also,\n   the fungus itself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muscariform', 'a.', 'Having the form of a brush.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muscarin', 'n.', 'A solid crystalline substance, C5H13NO2, found in the\n   toadstool (Agaricus muscarius), and in putrid fish. It is a typical\n   ptomaine, and a violent poison.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muscat', 'n.', 'A name given to several varieties of Old World grapes,\n   differing in color, size, etc., but all having a somewhat musky flavor.\n   The muscat of Alexandria is a large oval grape of a pale amber color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muscatel', 'a.', 'Of, pertaining to, or designating, or derived from, a\n   muscat grapes or similar grapes; a muscatel grapes; muscatel wine, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muscatel', 'n.', 'A common name for several varieties of rich sweet wine,\n   made in Italy, Spain, and France.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muscatel', 'n.', 'Finest raisins, dried on the vine; \"sun raisins.\"');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muschelkalk', 'n.', 'A kind of shell limestone, whose strata form the\n   middle one of the three divisions of the Triassic formation in Germany.\n   See Chart, under Geology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Musci', 'n. pl.', 'An order or subclass of cryptogamous plants; the mosses.\n   See Moss, and Cryptogamia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muscicapine', 'a.', 'Of or pertaining to the Muscicapidae, a family of\n   birds that includes the true flycatchers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muscid', 'n.', 'Any fly of the genus Musca, or family Muscidae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Musciform', 'a.', 'Having the form or structure of flies of the genus\n   Musca, or family Muscidae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Musciform', 'a.', 'Having the appearance or form of a moss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muscle', 'n.', 'An organ which, by its contraction, produces motion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muscle', 'n.', 'The contractile tissue of which muscles are largely made\n   up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muscle', 'n.', 'Muscular strength or development; as, to show ones muscle\n   by lifting a heavy weight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muscle', 'n.', 'See Mussel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muscled', 'a.', 'Furnished with muscles; having muscles; as, things well\n   muscled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muscling', 'n.', 'Exhibition or representation of the muscles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muscogees', 'n. pl.', 'See Muskogees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muscoid', 'a.', 'Mosslike; resembling moss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muscoid', 'n.', 'A term formerly applied to any mosslike flowerless plant,\n   with a distinct stem, and often with leaves, but without any vascular\n   system.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muscology', 'n.', 'Bryology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muscosity', 'n.', 'Mossiness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muscovado', 'a.', 'Pertaining to, or of the nature of, unrefined or raw\n   sugar, obtained from the juice of the sugar cane by evaporating and\n   draining off the molasses. Muscovado sugar contains impurities which\n   render it dark colored and moist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muscovado', 'n.', 'Unrefined or raw sugar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muscovite', 'n.', 'A native or inhabitant of Muscovy or ancient Russia;\n   hence, a Russian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muscovite', 'n.', 'Common potash mica. See Mica.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muscovy duck', '', 'A duck (Cairina moschata), larger than the common duck,\n   often raised in poultry yards. Called also musk duck. It is native of\n   tropical America, from Mexico to Southern Brazil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muscovy glass', '', 'Mica; muscovite. See Mica.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muscular', 'a.', 'Of or pertaining to a muscle, or to a system of muscles;\n   consisting of, or constituting, a muscle or muscles; as, muscular\n   fiber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muscular', 'a.', 'Performed by, or dependent on, a muscle or the muscles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muscular', 'a.', 'Well furnished with muscles; having well-developed\n   muscles; brawny; hence, strong; powerful; vigorous; as, a muscular body\n   or arm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muscularity', 'n.', 'The state or quality of being muscular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muscularize', 'v. t.', 'To make muscular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muscularly', 'adv.', 'In a muscular manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Musculation', 'n.', 'The muscular system of an animal, or of any of its\n   parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Musculature', 'n.', 'Musculation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muscule', 'n.', 'A long movable shed used by besiegers in ancient times in\n   attacking the walls of a fortified town.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Musculin', 'n.', 'See Syntonin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Musculocutaneous', 'a.', 'Pertaining both to muscles and skin; as, the\n   musculocutaneous nerve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Musculophrenic', 'a.', 'Pertaining to the muscles and the diaphragm; as,\n   the musculophrenic artery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Musculosity', 'n.', 'The quality or state of being musculous; muscularity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Musculospiral', 'a.', 'Of or pertaining to the muscles, and taking a spiral\n   course; -- applied esp. to a large nerve of the arm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Musculous', 'a.', 'Muscular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muse', 'n.', 'A gap or hole in a hedge, hence, wall, or the like, through\n   which a wild animal is accustomed to pass; a muset.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muse', 'n.', 'One of the nine goddesses who presided over song and the\n   different kinds of poetry, and also the arts and sciences; -- often\n   used in the plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muse', 'n.', 'A particular power and practice of poetry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muse', 'n.', 'A poet; a bard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mused', 'imp. & p. p.', 'of Muse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Musing', 'p. pr. & vb. n.', 'of Muse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muse', 'n.', 'To think closely; to study in silence; to meditate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muse', 'n.', 'To be absent in mind; to be so occupied in study or\n   contemplation as not to observe passing scenes or things present; to be\n   in a brown study.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muse', 'n.', 'To wonder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muse', 'v. t.', 'To think on; to meditate on.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muse', 'v. t.', 'To wonder at.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muse', 'n.', 'Contemplation which abstracts the mind from passing scenes;\n   absorbing thought; hence, absence of mind; a brown study.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muse', 'n.', 'Wonder, or admiration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Museful', 'a.', 'Meditative; thoughtfully silent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Museless', 'a.', 'Unregardful of the Muses; disregarding the power of\n   poetry; unpoetical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muser', 'n.', 'One who muses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muset', 'n.', 'A small hole or gap through which a wild animal passes; a\n   muse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Musette', 'n.', 'A small bagpipe formerly in use, having a soft and sweet\n   tone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Musette', 'n.', 'An air adapted to this instrument; also, a kind of rustic\n   dance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Museum', 'n.', 'A repository or a collection of natural, scientific, or\n   literary curiosities, or of works of art.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mush', 'n.', 'Meal (esp. Indian meal) boiled in water; hasty pudding;\n   supawn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mush', 'v. t.', 'To notch, cut, or indent, as cloth, with a stamp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mushroom', 'n.', 'An edible fungus (Agaricus campestris), having a white\n   stalk which bears a convex or oven flattish expanded portion called the\n   pileus. This is whitish and silky or somewhat scaly above, and bears on\n   the under side radiating gills which are at first flesh-colored, but\n   gradually become brown. The plant grows in rich pastures and is\n   proverbial for rapidity of growth and shortness of duration. It has a\n   pleasant smell, and is largely used as food. It is also cultivated from\n   spawn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mushroom', 'n.', 'Any large fungus, especially one of the genus Agaricus; a\n   toadstool. Several species are edible; but many are very poisonous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mushroom', 'n.', 'One who rises suddenly from a low condition in life; an\n   upstart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mushroom', 'a.', 'Of or pertaining to mushrooms; as, mushroom catchup.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mushroom', 'a.', 'Resembling mushrooms in rapidity of growth and shortness\n   of duration; short-lived; ephemerial; as, mushroom cities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mushroom-headed', 'a.', 'Having a cylindrical body with a convex head of\n   larger diameter; having a head like that of a mushroom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mushy', 'a.', 'Soft like mush; figuratively, good-naturedly weak and\n   effusive; weakly sentimental.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Music', 'n.', 'The science and the art of tones, or musical sounds, i. e.,\n   sounds of higher or lower pitch, begotten of uniform and synchronous\n   vibrations, as of a string at various degrees of tension; the science\n   of harmonical tones which treats of the principles of harmony, or the\n   properties, dependences, and relations of tones to each other; the art\n   of combining tones in a manner to please the ear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Music', 'n.', 'Melody; a rhythmical and otherwise agreeable succession of\n   tones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Music', 'n.', 'Harmony; an accordant combination of simultaneous tones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Music', 'n.', 'The written and printed notation of a musical composition;\n   the score.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Music', 'n.', 'Love of music; capacity of enjoying music.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Music', 'n.', 'A more or less musical sound made by many of the lower\n   animals. See Stridulation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Musical', 'a.', 'Of or pertaining to music; having the qualities of music;\n   or the power of producing music; devoted to music; melodious;\n   harmonious; as, musical proportion; a musical voice; musical\n   instruments; a musical sentence; musical persons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Musical', 'n.', 'Music.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Musical', 'n.', 'A social entertainment of which music is the leading\n   feature; a musical party.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Musicale', 'n.', 'A social musical party.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Musically', 'adv.', 'In a musical manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Musicalness', 'n.', 'The quality of being musical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Musician', 'n.', 'One skilled in the art or science of music; esp., a\n   skilled singer, or performer on a musical instrument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Musicomania', 'n.', 'A kind of monomania in which the passion for music\n   becomes so strong as to derange the intellectual faculties.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Musimon', 'n.', 'See Mouflon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Musingly', 'adv.', 'In a musing manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Musit', 'n.', 'See Muset.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Musk', 'n.', 'A substance of a reddish brown color, and when fresh of the\n   consistence of honey, obtained from a bag being behind the navel of the\n   male musk deer. It has a slightly bitter taste, but is specially\n   remarkable for its powerful and enduring odor. It is used in medicine\n   as a stimulant antispasmodic. The term is also applied to secretions of\n   various other animals, having a similar odor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Musk', 'n.', 'The musk deer. See Musk deer (below).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Musk', 'n.', 'The perfume emitted by musk, or any perfume somewhat similar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Musk', 'n.', 'The musk plant (Mimulus moschatus).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Musk', 'n.', 'A plant of the genus Erodium (E. moschatum); -- called also\n   musky herons-bill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Musk', 'n.', 'A plant of the genus Muscari; grape hyacinth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Musk', 'v. t.', 'To perfume with musk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muskadel', 'n.', 'See Muscadel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muskat', 'n.', 'See Muscat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muskellunge', 'n.', 'A large American pike (Esox nobilitor) found in the\n   Great Lakes, and other Northern lakes, and in the St. Lawrence River.\n   It is valued as a food fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Musket', 'n.', 'The male of the sparrow hawk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Musket', 'n.', 'A species of firearm formerly carried by the infantry of an\n   army. It was originally fired by means of a match, or matchlock, for\n   which several mechanical appliances (including the flintlock, and\n   finally the percussion lock) were successively substituted. This arm\n   has been generally superseded by the rifle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Musketeer', 'n.', 'A soldier armed with a musket.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Musketo', 'n.', 'See Mosquito.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Musketoon', 'n.', 'A short musket.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Musketoon', 'n.', 'One who is armed with such a musket.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Musketry', 'n.', 'Muskets, collectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Musketry', 'n.', 'The fire of muskets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muskiness', 'n.', 'The quality or state of being musky; the scent of musk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muskmelon', 'n.', 'The fruit of a cucubritaceous plant (Cicumis Melo),\n   having a peculiar aromatic flavor, and cultivated in many varieties,\n   the principal sorts being the cantaloupe, of oval form and yellowish\n   flesh, and the smaller nutmeg melon with greenish flesh. See Illust. of\n   Melon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muskogees', 'n. pl.', 'A powerful tribe of North American Indians that\n   formerly occupied the region of Georgia, Florida, and Alabama. They\n   constituted a large part of the Creek confederacy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muskrat', 'n.', 'A North American aquatic fur-bearing rodent (Fiber\n   zibethicus). It resembles a rat in color and having a long scaly tail,\n   but the tail is compressed, the bind feet are webbed, and the ears are\n   concealed in the fur. It has scent glands which secrete a substance\n   having a strong odor of musk. Called also musquash, musk beaver, and\n   ondatra.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muskrat', 'n.', 'The musk shrew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muskrat', 'n.', 'The desman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muskwood', 'n.', 'The wood of a West Indian tree of the Mahogany family\n   (Moschoxylum Swartzii).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muskwood', 'n.', 'The wood of an Australian tree (Eurybia argophylla).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Musky', 'a.', 'Having an odor of musk, or somewhat the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muslim', 'n.', 'See Moslem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muslin', 'n.', 'A thin cotton, white, dyed, or printed. The name is also\n   applied to coarser and heavier cotton goods; as, shirting and sheeting\n   muslins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muslinet', 'n.', 'A sort of coarse or light cotton cloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Musmon', 'n.', 'See Mouflon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Musomania', 'n.', 'See Musicomania.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Musquash', 'n.', 'See Muskrat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Musquaw', 'n.', 'The American black bear. See Bear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Musquet', 'n.', 'See Musket.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Musquito', 'n.', 'See Mosquito.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Musrole', 'n.', 'Alt. of Musrol');
  INSERT INTO words (word, word_type, description) VALUES  (    'Musrol', 'n.', 'The nose band of a horses bridle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muss', 'n.', 'A scramble, as when small objects are thrown down, to be\n   taken by those who can seize them; a confused struggle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muss', 'n.', 'A state of confusion or disorder; -- prob. variant of mess,\n   but influenced by muss, a scramble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mussed', 'imp. & p. p.', 'of Muss');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mussing', 'p. pr. & vb. n.', 'of Muss');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muss', 'v. t.', 'To disarrange, as clothing; to rumple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muss', 'n.', 'A term of endearment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mussel', 'n.', 'Any one of many species of marine bivalve shells of the\n   genus Mytilus, and related genera, of the family Mytidae. The common\n   mussel (Mytilus edulis; see Illust. under Byssus), and the larger, or\n   horse, mussel (Modiola modiolus), inhabiting the shores both of Europe\n   and America, are edible. The former is extensively used as food in\n   Europe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mussel', 'n.', 'Any one of numerous species of Unio, and related\n   fresh-water genera; -- called also river mussel. See Naiad, and Unio.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mussitation', 'n.', 'A speaking in a low tone; mumbling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mussite', 'n.', 'A variety of pyroxene, from the Mussa Alp in Piedmont;\n   diopside.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mussulmans', 'pl. ', 'of Mussulman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mussulman', 'n.', 'A Mohammedan; a Moslem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mussulmanic', 'a.', 'Of, pertaining to, or like, the Mussulmans, or their\n   customs: Mohammedan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mussulmanish', 'a.', 'Mohammedan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mussulmanism', 'n.', 'Mohammedanism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mussulmanly', 'adv.', 'In the manner of Moslems.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mussy', 'a.', 'Disarranged; rumpled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Must', 'v. i. / auxiliary', 'To be obliged; to be necessitated; --\n   expressing either physical or moral necessity; as, a man must eat for\n   nourishment; we must submit to the laws.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Must', 'v. i. / auxiliary', 'To be morally required; to be necessary or\n   essential to a certain quality, character, end, or result; as, he must\n   reconsider the matter; he must have been insane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Must', 'n.', 'The expressed juice of the grape, or other fruit, before\n   fermentation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Must', 'n.', 'Mustiness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Must', 'v. t. & i.', 'To make musty; to become musty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mustac', 'n.', 'A small tufted monkey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mustaches', 'pl. ', 'of Mustache');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mustache', 'n.', 'That part of the beard which grows on the upper lip; hair\n   left growing above the mouth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mustache', 'n.', 'A West African monkey (Cercopithecus cephus). It has\n   yellow whiskers, and a triangular blue mark on the nose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mustache', 'n.', 'Any conspicuous stripe of color on the side of the head,\n   beneath the eye of a bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mustachios', 'pl. ', 'of Mustacho');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mustacho', 'n.', 'A mustache.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mustachoed', 'a.', 'Having mustachios.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mustaiba', 'n.', 'A close-grained, neavy wood of a brownish color, brought\n   from Brazil, and used in turning, for making the handles of tools, and\n   the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mustang', 'n.', 'The half-wild horse of the plains in Mexico, California,\n   etc. It is small, hardy, and easily sustained.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mustard', 'n.', 'The name of several cruciferous plants of the genus\n   Brassica (formerly Sinapis), as white mustard (B. alba), black mustard\n   (B. Nigra), wild mustard or charlock (B. Sinapistrum).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mustard', 'n.', 'A powder or a paste made from the seeds of black or white\n   mustard, used as a condiment and a rubefacient. Taken internally it is\n   stimulant and diuretic, and in large doses is emetic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mustee', 'n.', 'See Mestee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Musteline', 'a.', 'Like or pertaining to the family Mustelidae, or the\n   weasels and martens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muster', 'v. t.', 'Something shown for imitation; a pattern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muster', 'v. t.', 'A show; a display.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muster', 'v. t.', 'An assembling or review of troops, as for parade,\n   verification of numbers, inspection, exercise, or introduction into\n   service.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muster', 'v. t.', 'The sum total of an army when assembled for review and\n   inspection; the whole number of effective men in an army.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muster', 'v. t.', 'Any assemblage or display; a gathering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mustered', 'imp. & p. p.', 'of Muster');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mustering', 'p. pr. & vb. n.', 'of Muster');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muster', 'v. t.', 'To collect and display; to assemble, as troops for\n   parade, inspection, exercise, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muster', 'v. t.', 'Hence: To summon together; to enroll in service; to get\n   together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muster', 'v. i.', 'To be gathered together for parade, inspection,\n   exercise, or the like; to come together as parts of a force or body;\n   as, his supporters mustered in force.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mustily', 'a.', 'In a musty state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mustiness', 'n.', 'The quality or state of being musty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Musty', 'n.', 'Having the rank, pungent, offencive odor and taste which\n   substances of organic origin acquire during warm, moist weather; foul\n   or sour and fetid; moldy; as, musty corn; musty books.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Musty', 'n.', 'Spoiled by age; rank; stale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Musty', 'n.', 'Dull; heavy; spiritless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mutability', 'n.', 'The quality of being mutable, or subject to change or\n   alteration, either in form, state, or essential character;\n   susceptibility of change; changeableness; inconstancy; variation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mutable', 'a.', 'Capable of alteration; subject to change; changeable in\n   form, qualities, or nature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mutable', 'a.', 'Changeable; inconstant; unsettled; unstable; fickle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mutableness', 'n.', 'The quality of being mutable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mutably', 'adv.', 'Changeably.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mutacism', 'n.', 'See Mytacism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mutage', 'n.', 'A process for checking the fermentation of the must of\n   grapes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mutanda', 'pl. ', 'of Mutandum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mutandum', 'n.', 'A thing which is to be changed; something which must be\n   altered; -- used chiefly in the plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mutation', 'n.', 'Change; alteration, either in form or qualities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mutch', 'n.', 'The close linen or muslin cap of an old woman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muchkin', 'n.', 'A liquid measure equal to four gills, or an imperial pint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mute', 'v. t.', 'To cast off; to molt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mute', 'v. t. & i.', 'To eject the contents of the bowels; -- said of\n   birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mute', 'n.', 'The dung of birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mute', 'a.', 'Not speaking; uttering no sound; silent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mute', 'a.', 'Incapable of speaking; dumb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mute', 'a.', 'Not uttered; unpronounced; silent; also, produced by complete\n   closure of the mouth organs which interrupt the passage of breath; --\n   said of certain letters. See 5th Mute, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mute', 'a.', 'Not giving a ringing sound when struck; -- said of a metal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mute', 'n.', 'One who does not speak, whether from physical inability,\n   unwillingness, or other cause.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mute', 'n.', 'One who, from deafness, either congenital or from early life,\n   is unable to use articulate language; a deaf-mute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mute', 'n.', 'A person employed by undertakers at a funeral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mute', 'n.', 'A person whose part in a play does not require him to speak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mute', 'n.', 'Among the Turks, an officer or attendant who is selected for\n   his place because he can not speak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mute', 'n.', 'A letter which represents no sound; a silent letter; also, a\n   close articulation; an element of speech formed by a position of the\n   mouth organs which stops the passage of the breath; as, p, b, d, k, t.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mute', 'n.', 'A little utensil made of brass, ivory, or other material, so\n   formed that it can be fixed in an erect position on the bridge of a\n   violin, or similar instrument, in order to deaden or soften the tone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mute-hill', 'n.', 'See Moot-hill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mutely', 'adv.', 'Without uttering words or sounds; in a mute manner;\n   silently.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muteness', 'n.', 'The quality or state of being mute; speechlessness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mutic', 'a.', 'Alt. of Muticous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muticous', 'a.', 'Without a point or pointed process; blunt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mutilate', 'a.', 'Deprived of, or having lost, an important part;\n   mutilated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mutilate', 'a.', 'Having finlike appendages or flukes instead of legs, as a\n   cetacean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mutilate', 'n.', 'A cetacean, or a sirenian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mutilated', 'imp. & p. p.', 'of Mutilate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mutilating', 'p. pr. & vb. n.', 'of Mutilate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mutilate', 'v. t.', 'To cut off or remove a limb or essential part of; to\n   maim; to cripple; to hack; as, to mutilate the body, a statue, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mutilate', 'v. t.', 'To destroy or remove a material part of, so as to\n   render imperfect; as, to mutilate the orations of Cicero.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mutilation', 'n.', 'The act of mutilating, or the state of being mutilated;\n   deprivation of a limb or of an essential part.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mutilator', 'n.', 'One who mutilates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mutilous', 'a.', 'Mutilated; defective; imperfect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mutine', 'n.', 'A mutineer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mutine', 'v. i.', 'To mutiny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mutineer', 'n.', 'One guilty of mutiny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muting', 'n.', 'Dung of birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mutinous', 'a.', 'Disposed to mutiny; in a state of mutiny; characterized\n   by mutiny; seditious; insubordinate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mutinies', 'pl. ', 'of Mutiny');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mutiny', 'n.', 'Insurrection against constituted authority, particularly\n   military or naval authority; concerted revolt against the rules of\n   discipline or the lawful commands of a superior officer; hence,\n   generally, forcible resistance to rightful authority; insubordination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mutiny', 'n.', 'Violent commotion; tumult; strife.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mutinied', 'imp. & p. p.', 'of Mutiny');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mutinying', 'p. pr. & vb. n.', 'of Mutiny');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mutiny', 'v. i.', 'To rise against, or refuse to obey, lawful authority in\n   military or naval service; to excite, or to be guilty of, mutiny or\n   mutinous conduct; to revolt against ones superior officer, or any\n   rightful authority.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mutiny', 'v. i.', 'To fall into strife; to quarrel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mutism', 'n.', 'The condition, state, or habit of being mute, or without\n   speech.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muttered', 'imp. & p. p.', 'of Mutter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muttering', 'p. pr. & vb. n.', 'of Mutter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mutter', 'v. i.', 'To utter words indistinctly or with a low voice and lips\n   partly closed; esp., to utter indistinct complaints or angry\n   expressions; to grumble; to growl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mutter', 'v. i.', 'To sound with a low, rumbling noise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mutter', 'v. t.', 'To utter with imperfect articulations, or with a low\n   voice; as, to mutter threats.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mutter', 'n.', 'Repressed or obscure utterance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mutterer', 'n.', 'One who mutters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mutteringly', 'adv.', 'With a low voice and indistinct articulation; in a\n   muttering manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mutton', 'n.', 'A sheep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mutton', 'n.', 'The flesh of a sheep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mutton', 'n.', 'A loose woman; a prostitute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muttony', 'a.', 'Like mutton; having a flavor of mutton.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mutual', 'a.', 'Reciprocally acting or related; reciprocally receiving and\n   giving; reciprocally given and received; reciprocal; interchanged; as,\n   a mutual love, advantage, assistance, aversion, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mutual', 'a.', 'Possessed, experienced, or done by two or more persons or\n   things at the same time; common; joint; as, mutual happiness; a mutual\n   effort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mutualism', 'n.', 'The doctrine of mutual dependence as the condition of\n   individual and social welfare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mutuality', 'n.', 'The quality of correlation; reciprocation; interchange;\n   interaction; interdependence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mutuality', 'n.', 'Reciprocity of consideration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mutually', 'adv.', 'In a mutual manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mutuary', 'n.', 'One who borrows personal chattels which are to be consumed\n   by him, and which he is to return or repay in kind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mutuation', 'n.', 'The act of borrowing or exchanging.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mutule', 'n.', 'A projecting block worked under the corona of the Doric\n   corice, in the same situation as the modillion of the Corinthian and\n   Composite orders. See Illust. of Gutta.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mux', 'n.', 'Dirt; filth; muck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mux', 'v. t.', 'To mix in an unitidy and offensive way; to make a mess of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muxy', 'a.', 'Soft; sticky, and dirty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muzarab', 'n.', 'One of a denomination of Christians formerly living under\n   the government of the Moors in Spain, and having a liturgy and ritual\n   of their own.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muzarabic', 'a.', 'Of or pertaining to Muzarabs; as, the Muzarabic liturgy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muzziness', 'n.', 'The state or quality of being muzzy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muzzle', 'v. i.', 'The projecting mouth and nose of a quadruped, as of a\n   horse; a snout.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muzzle', 'v. i.', 'The mouth of a thing; the end for entrance or discharge;\n   as, the muzzle of a gun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muzzle', 'v. i.', 'A fastening or covering (as a band or cage) for the\n   mouth of an animal, to prevent eating or vicious biting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muzzled', 'imp. & p. p.', 'of Muzzle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muzzling', 'p. pr. & vb. n.', 'of Muzzle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muzzle', 'v. t.', 'To bind the mouth of; to fasten the mouth of, so as to\n   prevent biting or eating; hence, figuratively, to bind; to sheathe; to\n   restrain from speech or action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muzzle', 'v. t.', 'To fondle with the closed mouth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muzzle', 'v. i.', 'To bring the mouth or muzzle near.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muzzle-loader', 'n.', 'A firearm which receives its charge through the\n   muzzle, as distinguished from one which is loaded at the breech.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muzzle-loading', 'a.', 'Receiving its charge through the muzzle; as, a\n   muzzle-loading rifle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Muzzy', 'a.', 'Absent-minded; dazed; muddled; stupid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'My', 'a.', 'Of or belonging to me; -- used always attributively; as, my\n   body; my book; -- mine is used in the predicate; as, the book is mine.\n   See Mine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mya', 'n.', 'A genus of bivalve mollusks, including the common long, or\n   soft-shelled, clam.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myalgia', 'n.', 'Pain in the muscles; muscular rheumatism or neuralgia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myall wood', '', 'A durable, fragrant, and dark-colored Australian wood,\n   used by the natives for spears. It is obtained from the small tree\n   Acacia homolophylla.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myaria', 'n. pl.', 'A division of bivalve mollusks of which the common clam\n   (Mya) is the type.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mycelium', 'n.', 'The white threads or filamentous growth from which a\n   mushroom or fungus is developed; the so-called mushroom spawn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myceloid', 'a.', 'Resembling mycelium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mycetes', 'n.', 'A genus of South American monkeys, including the howlers.\n   See Howler, 2, and Illust.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mycetoid', '', 'Resembling a fungus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mycoderma', 'n.', 'One of the forms in which bacteria group themselves; a\n   more or less thick layer of motionless but living bacteria, formed by\n   the bacteria uniting on the surface of the fluid in which they are\n   developed. This production differs from the zooloea stage of bacteria\n   by not having the intermediary mucous substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mycoderma', 'n.', 'A genus of microorganisms of which the acetic ferment\n   (Mycoderma aceti), which converts alcoholic fluids into vinegar, is a\n   representative. Cf. Mother.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mycologic', 'a.', 'Alt. of Mycological');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mycological', 'a.', 'Of or relating to mycology, or the fungi.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mycologist', 'n.', 'One who is versed in, or who studies, mycology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mycology', 'n.', 'That branch of botanical science which relates to the\n   musgrooms and other fungi.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mycomelic', 'a.', 'Pertaining to, or designating, a complex nitrogenous\n   acid of the alloxan group, obtained as a honey-yellow powder. Its\n   solutions have a gelatinous consistency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mycoprotein', 'n.', 'The protoplasmic matter of which bacteria are\n   composed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mycose', 'n.', 'A variety of sugar, isomeric with sucrose and obtained from\n   certain lichens and fungi. Called also trehalose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mycothrix', 'n.', 'The chain of micrococci formed by the division of the\n   micrococci in multiplication.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mydaleine', 'n.', 'A toxic alkaloid (ptomaine) obtained from putrid flesh\n   and from herring brines. As a poison it is said to execute profuse\n   diarrhoea, vomiting, and intestinal inflammation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mydatoxin', 'n.', 'A poisonous amido acid, C6H13NO2, separated by Brieger\n   from decaying horseflesh. In physiological action, it is similar to\n   curare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mydaus', 'n.', 'The teledu.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mydriasis', 'n.', 'A long-continued or excessive dilatation of the pupil of\n   the eye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mydriatic', 'a.', 'Causing dilatation of the pupil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mydriatic', 'n.', 'A mydriatic medicine or agent, as belladonna.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myelencephala', 'n. pl.', 'Same as Vertebrata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myelencephalic', 'a.', 'Of or pertaining to the myelencephalon;\n   cerebro-spinal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myelencephalon', 'n.', 'The brain and spinal cord; the cerebro-spinal axis;\n   the neuron. Sometimes abbreviated to myelencephal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myelencephalon', 'n.', 'The metencephalon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myelencephalous', 'a.', 'Of or pertaining to the Myelencephala.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myelin', 'n.', 'A soft white substance constituting the medullary sheats of\n   nerve fibers, and composed mainly of cholesterin, lecithin, cerebrin,\n   albumin, and some fat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myelin', 'n.', 'One of a group of phosphorized principles occurring in\n   nerve tissue, both in the brain and nerve fibers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myelitis', 'n.', 'Inflammation of the spinal marrow or its membranes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myelocoele', 'n.', 'The central canal of the spinal cord.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myelogenic', 'a.', 'Derived from, or pertaining to, the bone marrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myeloid', 'a.', 'Resembling marrow in appearance or consistency; as, a\n   myeloid tumor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myeloidin', 'n.', 'A substance, present in the protoplasm of the retinal\n   epithelium cells, and resembling, if not identical with, the substance\n   (myelin) forming the medullary sheaths of nerve fibers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myelon', 'n.', 'The spinal cord. (Sometimes abbrev. to myel.)');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myelonal', 'a.', 'Of or pertaining to the myelon; as, the myelonal, or\n   spinal, nerves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myeloneura', 'n. pl.', 'The Vertebrata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myeloplaxes', 'pl. ', 'of Myeloplax');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myeloplaces', 'pl. ', 'of Myeloplax');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myeloplax', 'n.', 'One of the huge multinucleated cells found in the marrow\n   of bone and occasionally in other parts; a giant cell. See Osteoclast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mygale', 'n.', 'A genus of very large hairy spiders having four lungs and\n   only four spinnerets. They do not spin webs, but usually construct\n   tubes in the earth, which are often furnished with a trapdoor. The\n   South American bird spider (Mygale avicularia), and the crab spider, or\n   matoutou (M. cancerides) are among the largest species. Some of the\n   species are erroneously called tarantulas, as the Texas tarantula (M.\n   Hentzii).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mylodon', 'n.', 'An extinct genus of large slothlike American edentates,\n   allied to Megatherium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mylohyoid', 'a.', 'Pertaining to, or in the region of, the lower jaw and\n   the hyoid apparatus; as, the mylohyoid nerve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myna', 'n.', 'Any one of numerous species of Asiatic starlings of the\n   genera Acridotheres, Sturnopastor, Sturnia, Gracula, and allied genera.\n   In habits they resemble the European starlings, and like them are often\n   caged and taught to talk. See Hill myna, under Hill, and Mino bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mynchen', 'n.', 'A nun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mynchery', 'n.', 'A nunnery; -- a term still applied to the ruins of\n   certain nunneries in England.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mynheer', 'n.', 'The Dutch equivalent of Mr. or Sir; hence, a Dutchman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myo-', '', 'A combining form of Gr. /, /, a muscle; as, myograph,\n   myochrome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myocarditis', 'n.', 'Inflammation of the myocardium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myocardium', 'n.', 'The main substance of the muscular wall of the heart\n   inclosed between the epicardium and endocardium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myochrome', 'n.', 'A colored albuminous substance in the serum from\n   red-colored muscles. It is identical with hemoglobin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myocommata', 'pl. ', 'of Myocomma');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myocommas', 'pl. ', 'of Myocomma');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myocomma', 'n.', 'A myotome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myodynamics', 'n.', 'The department of physiology which deals with the\n   principles of muscular contraction; the exercise of muscular force or\n   contraction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myodynamiometer', 'n.', 'A myodynamometer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myodynamometer', 'n.', 'An instrument for measuring the muscular strength\n   of man or of other animals; a dynamometer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myoepithelial', 'a.', 'Derived from epithelial cells and destined to become\n   a part of the muscular system; -- applied to structural elements in\n   certain embryonic forms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myoepithelial', 'a.', 'Having the characteristics of both muscle and\n   epithelium; as, the myoepithelial cells of the hydra.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myogalid', 'n.', 'One of the Myogalodae, a family of Insectivora, including\n   the desman, and allied species.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myogram', 'n.', 'See Muscle curve, under Muscle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myograph', 'n.', 'An instrument for determining and recording the different\n   phases, as the intensity, velocity, etc., of a muscular contraction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myographic', 'a.', 'Alt. of Myographical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myographical', 'a.', 'Of or pertaining to myography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myography', 'n.', 'The description of muscles, including the study of\n   muscular contraction by the aid of registering apparatus, as by some\n   form of myograph; myology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myohaematin', 'n.', 'A red-colored respiratory pigment found associated\n   with hemoglobin in the muscle tissue of a large number of animals, both\n   vertebrate and invertebrate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myoid', 'a.', 'Composed of, or resembling, muscular fiber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myolemma', 'n.', 'Sarcolemma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myolin', 'n.', 'The essential material of muscle fibers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myologic', 'a.', 'Alt. of Myological');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myological', 'a.', 'Of or pertaining to myology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myologist', 'n.', 'One skilled in myology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myology', 'n.', 'That part of anatomy which treats of muscles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myoma', 'n.', 'A tumor consisting of muscular tissue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myomancy', 'n.', 'Divination by the movements of mice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myomorph', 'n.', 'One of the Myomorpha.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myomorpha', 'n. pl.', 'An extensive group of rodents which includes the\n   rats, mice, jerboas, and many allied forms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myopathia', 'n.', 'Any affection of the muscles or muscular system.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myopathic', 'a.', 'Of or pertaining to myopathia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myopathy', 'n.', 'Same as Myopathia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myope', 'n.', 'A person having myopy; a myops.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myophan', 'n.', 'A contractile striated layer found in the bodies and stems\n   of certain Infusoria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myopia', 'n.', 'Nearsightedness; shortsightedness; a condition of the eye\n   in which the rays from distant object are brought to a focus before\n   they reach the retina, and hence form an indistinct image; while the\n   rays from very near objects are normally converged so as to produce a\n   distinct image. It is corrected by the use of a concave lens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myopic', 'a.', 'Pertaining to, or affected with, or characterized by,\n   myopia; nearsighted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myips', 'n.', 'See Myope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myopsis', 'n.', 'The appearance of muscae volitantes. See Muscae\n   volitantes, under Musca.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myopy', 'n.', 'Myopia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myosin', 'n.', 'An albuminous body present in dead muscle, being formed in\n   the process of coagulation which takes place in rigor mortis; the clot\n   formed in the coagulation of muscle plasma. See Muscle plasma, under\n   Plasma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myosis', 'n.', 'Long-continued contraction of the pupil of the eye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myositic', 'a.', 'Myotic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myositis', 'n.', 'Inflammation of the muscles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myosotis', 'n.', 'A genus of plants. See Mouse-ear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myotic', 'a.', 'Producing myosis, or contraction of the pupil of the eye,\n   as opium, calabar bean, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myotic', 'n.', 'A myotic agent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myotome', 'n.', 'A muscular segment; one of the zones into which the\n   muscles of the trunk, especially in fishes, are divided; a myocomma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myotome', 'n.', 'One of the embryonic muscular segments arising from the\n   protovertebrae; also, one of the protovertebrae themselves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myotome', 'n.', 'The muscular system of one metamere of an articulate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myotomic', 'a.', 'Of or pertaining to a myotome or myotomes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myotomy', 'n.', 'The dissection, or that part of anatomy which treats of\n   the dissection, of muscles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myrcia', 'n.', 'A large genus of tropical American trees and shrubs, nearly\n   related to the true myrtles (Myrtus), from which they differ in having\n   very few seeds in each berry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myria-', '', 'A prefix, esp. in the metric system, indicating ten thousand,\n   ten thousand times; as, myriameter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myriacanthous', 'a.', 'Having numerous spines, as certain fishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myriad', 'n.', 'The number of ten thousand; ten thousand persons or things.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myriad', 'n.', 'An immense number; a very great many; an indefinitely large\n   number.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myriad', 'a.', 'Consisting of a very great, but indefinite, number; as,\n   myriad stars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myriagram', 'n.', 'Alt. of Myriagramme');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myriagramme', 'n.', 'A metric weight, consisting of ten thousand grams or\n   ten kilograms. It is equal to 22.046 lbs. avoirdupois.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myrialiter', 'n.', 'Alt. of Myrialitre');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myrialitre', 'n.', 'A metric measure of capacity, containing ten thousand\n   liters. It is equal to 2641.7 wine gallons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myriameter', 'n.', 'Alt. of Myriametre');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myriametre', 'n.', 'A metric measure of length, containing ten thousand\n   meters. It is equal to 6.2137 miles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myriapod', 'n.', 'One of the Myriapoda.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myriapoda', 'n. pl.', 'A class, or subclass, of arthropods, related to the\n   hexapod insects, from which they differ in having the body made up of\n   numerous similar segments, nearly all of which bear true jointed legs.\n   They have one pair of antennae, three pairs of mouth organs, and\n   numerous trachaae, similar to those of true insects. The larvae, when\n   first hatched, often have but three pairs of legs. See Centiped,\n   Galleyworm, Milliped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myriarch', 'n.', 'A captain or commander of ten thousand men.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myriare', 'n.', 'A measure of surface in the metric system containing ten\n   thousand ares, or one million square meters. It is equal to about 247.1\n   acres.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myrica', 'n.', 'A widely dispersed genus of shrubs and trees, usually with\n   aromatic foliage. It includes the bayberry or wax myrtle, the sweet\n   gale, and the North American sweet fern, so called.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myricin', 'n.', 'A silky, crystalline, waxy substance, forming the less\n   soluble part of beeswax, and regarded as a palmitate of a higher\n   alcohol of the paraffin series; -- called also myricyl alcohol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myricyl', 'n.', 'A hypothetical radical regarded as the essential residue\n   of myricin; -- called also melissyl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myriological', 'a.', 'Of or relating to a myriologue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myriologist', 'n.', 'One who composes or sings a myriologue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myriologue', 'n.', 'An extemporaneous funeral song, composed and sung by a\n   woman on the death of a friend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myriophyllous', 'a.', 'Having an indefinitely great or countless number of\n   leaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myriopoda', 'n. pl.', 'See Myriapoda.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myriorama', 'n.', 'A picture made up of several smaller pictures, drawn\n   upon separate pieces in such a manner as to admit of combination in\n   many different ways, thus producing a great variety of scenes or\n   landscapes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myrioscope', 'n.', 'A form of kaleidoscope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myristate', 'n.', 'A salt of myristic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myristic', 'a.', 'Pertaining to, or derived from, the nutmeg (Myristica).\n   Specifically, designating an acid found in nutmeg oil and otoba fat,\n   and extracted as a white crystalline waxy substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myristin', 'n.', 'The myristate of glycerin, -- found as a vegetable fat in\n   nutmeg butter, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myristone', 'n.', 'The ketone of myristic acid, obtained as a white\n   crystalline substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myrmicine', 'a.', 'Of or pertaining to Myrmica, a genus of ants including\n   the small house ant (M. molesta), and many others.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myrmidon', 'n.', 'One of a fierce tribe or troop who accompained Achilles,\n   their king, to the Trojan war.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myrmidon', 'n.', 'A soldier or a subordinate civil officer who executes\n   cruel orders of a superior without protest or pity; -- sometimes\n   applied to bailiffs, constables, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myrmidonian', 'a.', 'Consisting of, or like, myrmidons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myrmotherine', 'a.', 'Feeding upon ants; -- said of certain birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myrobalan', 'n.', 'Alt. of Myrobolan');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myrobolan', 'n.', 'A dried astringent fruit much resembling a prune. It\n   contains tannin, and was formerly used in medicine, but is now chiefly\n   used in tanning and dyeing. Myrobolans are produced by various species\n   of Terminalia of the East Indies, and of Spondias of South America.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myronic', 'a.', 'Pertaining to, or obtained from, mustard; -- used\n   specifically to designate a glucoside called myronic acid, found in\n   mustard seed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myropolist', 'n.', 'One who sells unguents or perfumery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myrosin', 'n.', 'A ferment, resembling diastase, found in mustard seeds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myroxylon', 'n.', 'A genus of leguminous trees of tropical America, the\n   different species of which yield balsamic products, among which are\n   balsam of Peru, and balsam of Tolu. The species were formerly referred\n   to Myrospermum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myrrh', 'n.', 'A gum resin, usually of a yellowish brown or amber color, of\n   an aromatic odor, and a bitter, slightly pungent taste. It is valued\n   for its odor and for its medicinal properties. It exudes from the bark\n   of a shrub of Abyssinia and Arabia, the Balsamodendron Myrrha. The\n   myrrh of the Bible is supposed to have been partly the gum above named,\n   and partly the exudation of species of Cistus, or rockrose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myrrhic', 'a.', 'Of, pertaining to, or obtained from, myrrh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myrrhine', 'a.', 'Murrhine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myrtaceous', 'a.', 'Of, pertaining to, or resembling, a large and important\n   natural order of trees and shrubs (Myrtaceae), of which the myrtle is\n   the type. It includes the genera Eucalyptus, Pimenta, Lechythis, and\n   about seventy more.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myrtiform', 'a.', 'Resembling myrtle or myrtle berries; having the form of\n   a myrtle leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myrtle', 'n.', 'A species of the genus Myrtus, especially Myrtus communis.\n   The common myrtle has a shrubby, upright stem, eight or ten feet high.\n   Its branches form a close, full head, thickly covered with ovate or\n   lanceolate evergreen leaves. It has solitary axillary white or rosy\n   flowers, followed by black several-seeded berries. The ancients\n   considered it sacred to Venus. The flowers, leaves, and berries are\n   used variously in perfumery and as a condiment, and the beautifully\n   mottled wood is used in turning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ourselves', 'pl. ', 'of Myself');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myself', 'pron.', 'I or me in person; -- used for emphasis, my own self or\n   person; as I myself will do it; I have done it myself; -- used also\n   instead of me, as the object of the first person of a reflexive verb,\n   without emphasis; as, I will defend myself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myselven', 'pron.', 'Myself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mysis', 'n.', 'A genus of small schizopod shrimps found both in fresh and\n   salt water; the opossum shrimps. One species inhabits the Great Lakes\n   of North America, and is largely eaten by the whitefish. The marine\n   species form part of the food of right whales.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mystacal', 'a.', 'Of or pertaining to the upper lip, or mustache.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mystagogic', 'a.', 'Alt. of Mystagogical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mystagogical', 'a.', 'Of or pertaining to interpretation of mysteries or to\n   mystagogue; of the nature of mystagogy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mystagogue', 'n.', 'interprets mysteries, especially of a religious kind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mystagogue', 'n.', 'One who keeps and shows church relics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mystagogy', 'n.', 'The doctrines, principles, or practice of a mystagogue;\n   interpretation of mysteries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mysterial', 'a.', 'Mysterious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mysteriarch', 'n.', 'One presiding over mysteries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mysterious', 'a.', 'Of or pertaining to mystery; containing a mystery;\n   difficult or impossible to understand; obscure not revealed or\n   explained; enigmatical; incomprehensible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mysteriously', 'adv.', 'In a mysterious manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mysteriousness', 'n.', 'The state or quality of being mysterious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mysteriousness', 'n.', 'Something mysterious; a mystery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mysterized', 'imp. & p. p.', 'of Mysterize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mysterizing', 'p. pr. & vb. n.', 'of Mysterize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mysterize', 'v. t.', 'To make mysterious; to make a mystery of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mysteries', 'pl. ', 'of Mystery');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mystery', 'a.', 'A profound secret; something wholly unknown, or something\n   kept cautiously concealed, and therefore exciting curiosity or wonder;\n   something which has not been or can not be explained; hence,\n   specifically, that which is beyond human comprehension.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mystery', 'a.', 'A kind of secret religious celebration, to which none were\n   admitted except those who had been initiated by certain preparatory\n   ceremonies; -- usually plural; as, the Eleusinian mysteries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mystery', 'a.', 'The consecrated elements in the eucharist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mystery', 'a.', 'Anything artfully made difficult; an enigma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mysteries', 'pl. ', 'of Mystery');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mystery', 'n.', 'A trade; a handicraft; hence, any business with which one\n   is usually occupied.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mystery', 'n.', 'A dramatic representation of a Scriptural subject, often\n   some event in the life of Christ; a dramatic composition of this\n   character; as, the Chester Mysteries, consisting of dramas acted by\n   various craft associations in that city in the early part of the 14th\n   century.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mystic', 'a.', 'Alt. of Mystical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mystical', 'a.', 'Remote from or beyond human comprehension; baffling human\n   understanding; unknowable; obscure; mysterious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mystical', 'a.', 'Importing or implying mysticism; involving some secret\n   meaning; allegorical; emblematical; as, a mystic dance; mystic Babylon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mystic', 'n.', 'One given to mysticism; one who holds mystical views,\n   interpretations, etc.; especially, in ecclesiastical history, one who\n   professed mysticism. See Mysticism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mysticete', 'n.', 'Any right whale, or whalebone whale. See Cetacea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mysticism', 'n.', 'Obscurity of doctrine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mysticism', 'n.', 'The doctrine of the Mystics, who professed a pure,\n   sublime, and wholly disinterested devotion, and maintained that they\n   had direct intercourse with the divine Spirit, and aquired a knowledge\n   of God and of spiritual things unattainable by the natural intellect,\n   and such as can not be analyzed or explained.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mysticism', 'n.', 'The doctrine that the ultimate elements or principles of\n   knowledge or belief are gained by an act or process akin to feeling or\n   faith.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mystification', 'n.', 'The act of mystifying, or the state of being\n   mystied; also, something designed to, or that does, mystify.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mystificator', 'n.', 'One who mystifies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mystified', 'imp. & p. p.', 'of Mystify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mystifying', 'p. pr. & vb. n.', 'of Mystify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mystify', 'v. t.', 'To involve in mystery; to make obscure or difficult to\n   understand; as, to mystify a passage of Scripture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mystify', 'v. t.', 'To perplex the mind of; to puzzle; to impose upon the\n   credulity of ; as, to mystify an opponent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mytacism', 'n.', 'Too frequent use of the letter m, or of the sound\n   represented by it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myth', 'n.', 'A story of great but unknown age which originally embodied a\n   belief regarding some fact or phenomenon of experience, and in which\n   often the forces of nature and of the soul are personified; an ancient\n   legend of a god, a hero, the origin of a race, etc.; a wonder story of\n   prehistoric origin; a popular fable which is, or has been, received as\n   historical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myth', 'n.', 'A person or thing existing only in imagination, or whose\n   actual existence is not verifiable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mythe', 'n.', 'See Myth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mythic', 'a.', 'Alt. of Mythical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mythical', 'a.', 'Of or relating to myths; described in a myth; of the\n   nature of a myth; fabulous; imaginary; fanciful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mythographer', 'n.', 'A composer of fables.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mythologer', 'n.', 'A mythologist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mythologian', 'n.', 'A mythologist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mythologic', 'a.', 'Alt. of Mythological');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mythological', 'a.', 'Of or pertaining to mythology or to myths; mythical;\n   fabulous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mythologist', 'n.', 'One versed in, or who writes on, mythology or myths.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mythologize', 'v. i.', 'To relate, classify, and explain, or attempt to\n   explain, myths; to write upon myths.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mythologize', 'v. i.', 'To construct and propagate myths.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mythologizer', 'n.', 'One who, or that which, mythologizes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mythologue', 'n.', 'A fabulous narrative; a myth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mythologies', 'pl. ', 'of Mythology');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mythology', 'n.', 'The science which treats of myths; a treatise on myths.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mythology', 'n.', 'A body of myths; esp., the collective myths which\n   describe the gods of a heathen people; as, the mythology of the Greeks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mythoplasm', 'n.', 'A narration of mere fable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mythopoeic', 'a.', 'Making or producing myths; giving rise to mythical\n   narratives.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mythopoetic', 'a.', 'Making or producing myths or mythical tales.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mytiloid', 'a.', 'Like, or pertaining to, the genus Mytilus, or family\n   Mytilidae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mytilotoxine', 'n.', 'A poisonous base (leucomaine) found in the common\n   mussel. It either causes paralysis of the muscles, or gives rise to\n   convulsions, including death by an accumulation of carbonic acid in the\n   blood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mytilus', 'n.', 'A genus of marine bivalve shells, including the common\n   mussel. See Illust. under Byssus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myxa', 'n.', 'The distal end of the mandibles of a bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myxine', 'n.', 'A genus of marsipobranchs, including the hagfish. See Hag,\n   4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myxinoid', 'a.', 'Like, or pertaining to, the genus Myxine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myxinoid', 'n.', 'A hagfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myxocystodea', 'n. pl.', 'A division of Infusoria including the Noctiluca.\n   See Noctiluca.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myxomata', 'pl. ', 'of Myxoma');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myxoma', 'n.', 'A tumor made up of a gelatinous tissue resembling that\n   found in the umbilical cord.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myxopod', 'n.', 'A rhizopod or moneran. Also used adjectively; as, a\n   myxopod state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myzontes', 'n. pl.', 'The Marsipobranchiata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Myzostomata', 'n. pl.', 'An order of curious parasitic worms found on\n   crinoids. The body is short and disklike, with four pairs of suckers\n   and five pairs of hook-bearing parapodia on the under side.\n   N () the fourteenth letter of English alphabet, is a vocal consonent,\n   and, in allusion to its mode of formation, is called the dentinasal or\n   linguanasal consonent. Its commoner sound is that heard in ran, done;\n   but when immediately followed in the same word by the sound of g hard\n   or k (as in single, sink, conquer), it usually represents the same\n   sound as the digraph ng in sing, bring, etc. This is a simple but\n   related sound, and is called the gutturo-nasal consonent. See Guide to\n   Pronunciation, // 243-246.');
  INSERT INTO words (word, word_type, description) VALUES  (    'N', 'n.', 'A measure of space equal to half an M (or em); an en.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Na', 'a. & adv.', 'No, not. See No.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nab', 'n.', 'The summit of an eminence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nab', 'n.', 'The cock of a gunlock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nab', 'n.', 'The keeper, or box into which the lock is shot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nabbed', 'imp. & p. p.', 'of Nab');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nabbing', 'p. pr. & vb. n.', 'of Nab');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nab', 'v. t.', 'To catch or seize suddenly or unexpectedly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nabit', 'n.', 'Pulverized sugar candy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nabk', 'n.', 'The edible berries of the Zizyphys Lotus, a tree of Northern\n   Africa, and Southwestern Europe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nabob', 'n.', 'A deputy or viceroy in India; a governor of a province of\n   the ancient Mogul empire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nabob', 'n.', 'One who returns to Europe from the East with immense riches:\n   hence, any man of great wealth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nacarat', 'n.', 'A pale red color, with a cast of orange.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nacarat', 'n.', 'Fine linen or crape dyed of this color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nacker', 'n.', 'See Nacre.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nacre', 'n.', 'A pearly substance which lines the interior of many shells,\n   and is most perfect in the mother-of-pearl. [Written also nacker and\n   naker.] See Pearl, and Mother-of-pearl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nacreous', 'a.', 'Consisting of, or resembling, nacre; pearly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nad', '', 'Alt. of Nadde');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nadde', '', 'Had not.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nadder', 'n.', 'An adder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nadir', 'n.', 'That point of the heavens, or lower hemisphere, directly\n   opposite the zenith; the inferior pole of the horizon; the point of the\n   celestial sphere directly under the place where we stand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nadir', 'n.', 'The lowest point; the time of greatest depression.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naenia', 'n.', 'See Nenia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naeve', 'n.', 'A naevus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naevoid', 'a.', 'Resembling a naevus or naevi; as, naevoid elephantiasis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naevose', 'a.', 'Spotted; frecled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naevi', 'pl. ', 'of Navus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Navus', 'n.', 'A spot or mark on the skin of children when born; a\n   birthmark; -- usually applied to vascular tumors, i. e., those\n   consisting mainly of blood vessels, as dilated arteries, veins, or\n   capillaries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nag', 'n.', 'A small horse; a pony; hence, any horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nag', 'n.', 'A paramour; -- in contempt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nagged', 'imp. & p. p.', 'of Nag');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nagging', 'p. pr. & vb. n.', 'of Nag');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nag', 'v. t. & i.', 'To tease in a petty way; to scold habitually; to\n   annoy; to fret pertinaciously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nagging', 'a.', 'Fault-finding; teasing; persistently annoying; as, a\n   nagging toothache.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naggy', 'a.', 'Irritable; touchy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nagor', 'n.', 'A West African gazelle (Gazella redunca).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nagyagite', 'n.', 'A mineral of blackish lead-gray color and metallic\n   luster, generally of a foliated massive structure; foliated tellurium.\n   It is a telluride of lead and gold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naiad', 'n.', 'A water nymph; one of the lower female divinities, fabled to\n   preside over some body of fresh water, as a lake, river, brook, or\n   fountain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naiad', 'n.', 'Any species of a tribe (Naiades) of freshwater bivalves,\n   including Unio, Anodonta, and numerous allied genera; a river mussel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naiad', 'n.', 'One of a group of butterflies. See Nymph.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naiad', 'n.', 'Any plant of the order Naiadaceae, such as eelgrass,\n   pondweed, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naiant', 'a.', 'See Natant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naid', 'n.', 'Any one of numerous species of small, fresh-water, chaetopod\n   annelids of the tribe Naidina. They belong to the Oligochaeta.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naif', 'a.', 'Having a true natural luster without being cut; -- applied by\n   jewelers to a precious stone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naif', 'a.', 'Naive; as, a naif remark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naik', 'n.', 'A chief; a leader; a Sepoy corporal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nail', 'n.', 'the horny scale of plate of epidermis at the end of the\n   fingers and toes of man and many apes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nail', 'n.', 'The basal thickened portion of the anterior wings of certain\n   hemiptera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nail', 'n.', 'The terminal horny plate on the beak of ducks, and other\n   allied birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nail', 'n.', 'A slender, pointed piece of metal, usually with a head, used\n   for fastening pieces of wood or other material together, by being\n   driven into or through them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nail', 'a.', 'A measure of length, being two inches and a quarter, or the\n   sixteenth of a yard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nailed', 'imp. & p. p.', 'of Nail');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nailing', 'p. pr. & vb. n.', 'of Nail');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nail', 'n.', 'To fasten with a nail or nails; to close up or secure by\n   means of nails; as, to nail boards to the beams.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nail', 'n.', 'To stud or boss with nails, or as with nails.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nail', 'n.', 'To fasten, as with a nail; to bind or hold, as to a bargain\n   or to acquiescence in an argument or assertion; hence, to catch; to\n   trap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nail', 'n.', 'To spike, as a cannon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nailbrush', 'n.', 'A brush for cleaning the nails.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nailer', 'n.', 'One whose occupation is to make nails; a nail maker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nailer', 'n.', 'One who fastens with, or drives, nails.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naileress', 'n.', 'A women who makes nailes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naileries', 'pl. ', 'of Nailery');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nailery', '', 'A manufactory where nails are made.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nail-headed', 'a.', 'Having a head like that of a nail; formed so as to\n   resemble the head of a nail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nailless', 'a.', 'Without nails; having no nails.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nainsook', 'n.', 'A thick sort of jaconet muslin, plain or striped,\n   formerly made in India.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nais', 'n.', 'See Naiad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naissant', 'a.', 'Same as Jessant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naive', 'a.', 'Having native or unaffected simplicity; ingenuous; artless;\n   frank; as, naive manners; a naive person; naive and unsophisticated\n   remarks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naively', 'adv.', 'In a naive manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naivete', 'n.', 'Native simplicity; unaffected plainness or ingenuousness;\n   artlessness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naivety', 'n.', 'Naivete.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nake', 'v.t.', 'To make naked.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naked', 'a.', 'Having no clothes on; uncovered; nude; bare; as, a naked\n   body; a naked limb; a naked sword.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naked', 'a.', 'Having no means of defense or protection; open; unarmed;\n   defenseless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naked', 'a.', 'Unprovided with needful or desirable accessories, means of\n   sustenance, etc.; destitute; unaided; bare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naked', 'a.', 'Without addition, exaggeration, or excuses; not concealed or\n   disguised; open to view; manifest; plain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naked', 'a.', 'Mere; simple; plain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naked', 'a.', 'Without pubescence; as, a naked leaf or stem; bare, or not\n   covered by the customary parts, as a flower without a perianth, a stem\n   without leaves, seeds without a pericarp, buds without bud scales.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naked', 'a.', 'Not having the full complement of tones; -- said of a chord\n   of only two tones, which requires a third tone to be sounded with them\n   to make the combination pleasing to the ear; as, a naked fourth or\n   fifth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nakedly', 'adv.', 'In a naked manner; without covering or disguise;\n   manifestly; simply; barely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nakedness', 'n.', 'The condition of being naked.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nakedness', 'n.', 'The privy parts; the genitals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naker', 'n.', 'Same as Nacre.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naker', 'n.', 'A kind of kettledrum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nakoo', 'n.', 'The gavial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nale', 'n.', 'Ale; also, an alehouse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nall', 'n.', 'An awl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nam', '', 'Am not.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nam', '', 'imp. of Nim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Namable', 'a.', 'Capable of being named.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Namation', 'n.', 'A distraining or levying of a distress; an impounding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Namaycush', 'n.', 'A large North American lake trout (Salvelinus\n   namaycush). It is usually spotted with red, and sometimes weighs over\n   forty pounds. Called also Mackinaw trout, lake trout, lake salmon,\n   salmon trout, togue, and tuladi.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Namby-pamby', 'n.', 'Talk or writing which is weakly sentimental or\n   affectedly pretty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Namby-pamby', 'a.', 'Affectedly pretty; weakly sentimental; finical;\n   insipid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Name', 'n.', 'The title by which any person or thing is known or\n   designated; a distinctive specific appellation, whether of an\n   individual or a class.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Name', 'n.', 'A descriptive or qualifying appellation given to a person or\n   thing, on account of a character or acts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Name', 'n.', 'Reputed character; reputation, good or bad; estimation; fame;\n   especially, illustrious character or fame; honorable estimation;\n   distinction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Name', 'n.', 'Those of a certain name; a race; a family.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Name', 'n.', 'A person, an individual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Named', 'imp. & p. p.', 'of Name');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naming', 'p. pr. & vb. n.', 'of Name');
  INSERT INTO words (word, word_type, description) VALUES  (    'Name', 'n.', 'To give a distinctive name or appellation to; to entitle; to\n   denominate; to style; to call.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Name', 'n.', 'To mention by name; to utter or publish the name of; to refer\n   to by distinctive title; to mention.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Name', 'n.', 'To designate by name or specifically for any purpose; to\n   nominate; to specify; to appoint; as, to name a day for the wedding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Name', 'n.', 'To designate (a member) by name, as the Speaker does by way\n   of reprimand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nameless', 'a.', 'Without a name; not having been given a name; as, a\n   nameless star.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nameless', 'a.', 'Undistinguished; not noted or famous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nameless', 'a.', 'Not known or mentioned by name; anonymous; as, a nameless\n   writer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nameless', 'a.', 'Unnamable; indescribable; inexpressible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Namelessly', 'adv.', 'In a nameless manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Namely', 'adv.', 'By name; by particular mention; specifically; especially;\n   expressly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Namely', 'adv.', 'That is to say; to wit; videlicet; -- introducing a\n   particular or specific designation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Namer', 'n.', 'One who names, or calls by name.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Namesake', 'n.', 'One that has the same name as another; especially, one\n   called after, or named out of regard to, another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Namo', 'adv.', 'No more.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nan', 'inerj.', 'Anan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nandine', 'n.', 'An African carnivore (Nandinia binotata), allied to the\n   civets. It is spotted with black.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nandou', 'n.', 'Alt. of Nandu');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nandu', 'n.', 'Any one of three species of South American ostriches of the\n   genera Rhea and Pterocnemia. See Rhea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nankeen', 'n.', 'A species of cloth, of a firm texture, originally brought\n   from China, made of a species of cotton (Gossypium religiosum) that is\n   naturally of a brownish yellow color quite indestructible and\n   permanent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nankeen', 'n.', 'An imitation of this cloth by artificial coloring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nankeen', 'n.', 'Trousers made of nankeen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nanny', 'n.', 'A diminutive of Ann or Anne, the proper name.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nannyberry', 'n.', 'See Sheepberry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nanpie', 'n.', 'The magpie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naos', 'n.', 'A term used by modern archaeologists instead of cella. See\n   Cella.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Napped', 'imp. & p. p.', 'of Nap');
  INSERT INTO words (word, word_type, description) VALUES  (    'Napping', 'p. pr. & vb. n.', 'of Nap');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nap', 'v. i.', 'To have a short sleep; to be drowsy; to doze.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nap', 'v. i.', 'To be in a careless, secure state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nap', 'n.', 'A short sleep; a doze; a siesta.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nap', 'n.', 'Woolly or villous surface of felt, cloth, plants, etc.; an\n   external covering of down, of short fine hairs or fibers forming part\n   of the substance of anything, and lying smoothly in one direction; the\n   pile; -- as, the nap of cotton flannel or of broadcloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nap', 'n.', 'The loops which are cut to make the pile, in velvet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nap', 'v. t.', 'To raise, or put, a nap on.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nape', 'n.', 'The back part of the neck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nape-crest', 'n.', 'An African bird of the genus Schizorhis, related to the\n   plantain eaters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naperies', 'pl. ', 'of Napery');
  INSERT INTO words (word, word_type, description) VALUES  (    'Napery', 'n.', 'Table linen; also, linen clothing, or linen in general.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Napha water', '', 'A perfume distilled from orange flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naphew', 'n.', 'See Navew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naphtha', 'n.', 'The complex mixture of volatile, liquid, inflammable\n   hydrocarbons, occurring naturally, and usually called crude petroleum,\n   mineral oil, or rock oil. Specifically: That portion of the distillate\n   obtained in the refinement of petroleum which is intermediate between\n   the lighter gasoline and the heavier benzine, and has a specific\n   gravity of about 0.7, -- used as a solvent for varnishes, as a\n   carburetant, illuminant, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naphtha', 'n.', 'One of several volatile inflammable liquids obtained by\n   the distillation of certain carbonaceous materials and resembling the\n   naphtha from petroleum; as, Boghead naphtha, from Boghead coal\n   (obtained at Boghead, Scotland); crude naphtha, or light oil, from coal\n   tar; wood naphtha, from wood, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naphthalate', 'n.', 'A salt of naphthalic acid; a phthalate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naphthalene', 'n.', 'A white crystalline aromatic hydrocarbon, C10H8,\n   analogous to benzene, and obtained by the distillation of certain\n   bituminous materials, such as the heavy oil of coal tar. It is the type\n   and basis of a large number of derivatives among organic compounds.\n   Formerly called also naphthaline.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naphthalenic', 'a.', 'Pertaining to , or derived from, naphthalene; -- used\n   specifically to designate a yellow crystalline substance, called\n   naphthalenic acid and also hydroxy quinone, and obtained from certain\n   derivatives of naphthol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naphthalic', 'a.', 'Pertaining to, derived from, or related to,\n   naphthalene; -- used specifically to denote any one of a series of\n   acids derived from naphthalene, and called naphthalene acids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naphthalic', 'a.', 'Formerly, designating an acid probably identical with\n   phthalic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naphthalidine', 'n.', 'Same as Naphthylamine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naphthalin', 'n.', 'Alt. of Naphthaline');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naphthaline', 'n.', 'See Naphthalene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naphthalize', 'v. t.', 'To mingle, saturate, or impregnate, with naphtha.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naphthazarin', 'n.', 'A dyestuff, resembling alizarin, obtained from\n   naphthoquinone as a red crystalline substance with a bright green,\n   metallic luster; -- called also naphthalizarin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naphthene', 'n.', 'A peculiar hydrocarbon occuring as an ingredient of\n   Caucasian petroleum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naphthide', 'n.', 'A compound of naphthalene or its radical with a metallic\n   element; as, mercuric naphthide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naphthoic', 'a.', 'Pertaining to, derived from, or related to, naphthalene;\n   -- used specifically to designate any one of a series of carboxyl\n   derivatives, called naphthoic acids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naphthol', 'n.', 'Any one of a series of hydroxyl derivatives of\n   naphthalene, analogous to phenol. In general they are crystalline\n   substances with a phenol (carbolic) odor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naphthoquinone', 'n.', 'A yellow crystalline substance, C10H6O2, analogous\n   to quinone, obtained by oxidizing naphthalene with chromic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naphthyl', 'n.', 'A hydrocarbon radical regarded as the essential residue\n   of naphthalene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naphthylamine', 'n.', 'One of two basic amido derivatives of naphthalene,\n   C10H7.NH2, forming crystalline solids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Napierian', 'a.', 'Alt. of Naperian');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naperian', 'a.', 'Of, pertaining to, or discovered by, Napier, or Naper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Napiers bones', '', 'Alt. of Napiers rods');
  INSERT INTO words (word, word_type, description) VALUES  (    'Napiers rods', '', 'A set of rods, made of bone or other material, each\n   divided into nine spaces, and containing the numbers of a column of the\n   multiplication table; -- a contrivance of Baron Napier, the inventor of\n   logarithms, for facilitating the operations of multiplication and\n   division.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Napiform', 'a.', 'Turnip-shaped; large and round in the upper part, and\n   very slender below.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Napkin', 'n.', 'A little towel, or small cloth, esp. one for wiping the\n   fingers and mouth at table.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Napkin', 'n.', 'A handkerchief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Napless', 'a.', 'Without nap; threadbare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naples yellow', '', 'See under Yellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Napoleon', 'n.', 'A French gold coin of twenty francs, or about $3.86.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Napoleonic', 'a.', 'Of or pertaining to Napoleon I., or his family;\n   resembling, or having the qualities of, Napoleon I.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Napoleonist', 'n.', 'A supporter of the dynasty of the Napoleons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nappe', 'n.', 'Sheet; surface; all that portion of a surface that is\n   continuous in such a way that it is possible to pass from any one point\n   of the portion to any other point of the portion without leaving the\n   surface. Thus, some hyperboloids have one nappe, and some have two.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nappiness', 'n.', 'The quality of having a nap; abundance of nap, as on\n   cloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Napping', 'n.', 'The act or process of raising a nap, as on cloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Napping', 'n.', 'A sheet of partially felted fur before it is united to the\n   hat body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nappy', 'a.', 'Inclined to sleep; sleepy; as, to feel nappy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nappy', 'a.', 'Tending to cause sleepiness; serving to make sleepy; strong;\n   heady; as, nappy ale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nappy', 'a.', 'Having a nap or pile; downy; shaggy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nappies', 'pl. ', 'of Nappy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nappy', 'n.', 'A round earthen dish, with a flat bottom and sloping sides.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nap-taking', 'n.', 'A taking by surprise; an unexpected onset or attack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Napu', 'n.', 'A very small chevrotain (Tragulus Javanicus), native of Java.\n   It is about the size of a hare, and is noted for its agility in\n   leaping. Called also Java musk deer, pygmy musk deer, and deerlet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Napus', 'n.', 'A kind of turnip. See Navew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Narceine', 'n.', 'An alkaloid found in small quantities in opium, and\n   extracted as a white crystalline substance of a bitter astringent\n   taste. It is a narcotic. Called also narceia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Narcissine', 'a.', 'Of or pertaining to Narcissus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Narcissuses', 'pl. ', 'of Narcissus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Narcissus', 'n.', 'A genus of endogenous bulbous plants with handsome\n   flowers, having a cup-shaped crown within the six-lobed perianth, and\n   comprising the daffodils and jonquils of several kinds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Narcissus', 'n.', 'A beautiful youth fabled to have been enamored of his\n   own image as seen in a fountain, and to have been changed into the\n   flower called Narcissus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Narcosis', 'n.', 'Privation of sense or consciousness, due to a narcotic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Narcotic', 'a.', 'Having the properties of a narcotic; operating as a\n   narcotic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Narcotic', 'n.', 'A drug which, in medicinal doses, generally allays morbid\n   susceptibility, relieves pain, and produces sleep; but which, in\n   poisonous doses, produces stupor, coma, or convulsions, and, when given\n   in sufficient quantity, causes death. The best examples are opium (with\n   morphine), belladonna (with atropine), and conium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Narcotical', 'a.', 'Narcotic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Narcotine', 'n.', 'An alkaloid found in opium, and extracted as a white\n   crystalline substance, tasteless and less poisonous than morphine; --\n   called also narcotia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Narcotinic', 'a.', 'Pertaining to narcotine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Narcotism', 'n.', 'Narcosis; the state of being narcotized.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Narcotized', 'imp. & p. p.', 'of Narcotize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Narcotizing', 'p. pr. & vb. n.', 'of Narcotize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Narcotize', 'v. t.', 'To imbue with, or subject to the influence of, a\n   narcotic; to put into a state of narcosis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nard', 'n.', 'An East Indian plant (Nardostachys Jatamansi) of the Valerian\n   family, used from remote ages in Oriental perfumery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nard', 'n.', 'An ointment prepared partly from this plant. See Spikenard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nard', 'n.', 'A kind of grass (Nardus stricta) of little value, found in\n   Europe and Asia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nardine', 'a.', 'Of or pertaining to nard; having the qualities of nard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nardoo', 'n.', 'An Australian name for Marsilea Drummondii, a four-leaved\n   cryptogamous plant, sometimes used for food.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nare', 'n.', 'A nostril.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nares', 'n. pl.', 'The nostrils or nasal openings, -- the anterior nares\n   being the external or proper nostrils, and the posterior nares, the\n   openings of the nasal cavities into the mouth or pharynx.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nargile', 'n.', 'Alt. of Nargileh');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nargileh', 'n.', 'An apparatus for smoking tobacco. It has a long flexible\n   tube, and the smoke is drawn through water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Narica', 'n.', 'The brown coati. See Coati.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nariform', 'a.', 'Formed like the nose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Narine', 'a.', 'Of or belonging to the nostrils.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Narrable', 'a.', 'Capable of being narrated or told.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Narragansetts', 'n. pl.', 'A tribe of Indians who formerly inhabited the\n   shores of Narragansett Bay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Narrated', 'imp. & p. p.', 'of Narrate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Narrating', 'p. pr. & vb. n.', 'of Narrate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Narrate', 'v. t.', 'To tell, rehearse, or recite, as a story; to relate the\n   particulars of; to go through with in detail, as an incident or\n   transaction; to give an account of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Narration', 'n.', 'The act of telling or relating the particulars of an\n   event; rehearsal; recital.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Narration', 'n.', 'That which is related; the relation in words or writing\n   of the particulars of any transaction or event, or of any series of\n   transactions or events; story; history.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Narration', 'n.', 'That part of a discourse which recites the time, manner,\n   or consequences of an action, or simply states the facts connected with\n   the subject.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Narrative', 'a.', 'Of or pertaining to narration; relating to the\n   particulars of an event or transaction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Narrative', 'a.', 'Apt or inclined to relate stories, or to tell\n   particulars of events; story-telling; garrulous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Narrative', 'n.', 'That which is narrated; the recital of a story; a\n   continuous account of the particulars of an event or transaction; a\n   story.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Narratively', 'adv.', 'In the style of narration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Narrator', 'n.', 'One who narrates; one who relates a series of events or\n   transactions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Narratory', 'a.', 'Giving an account of events; narrative; as, narratory\n   letters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Narre', 'a.', 'Nearer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Narrow', 'superl.', 'Of little breadth; not wide or broad; having little\n   distance from side to side; as, a narrow board; a narrow street; a\n   narrow hem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Narrow', 'superl.', 'Of little extent; very limited; circumscribed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Narrow', 'superl.', 'Having but a little margin; having barely sufficient\n   space, time, or number, etc.; close; near; -- with special reference to\n   some peril or misfortune; as, a narrow shot; a narrow escape; a narrow\n   majority.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Narrow', 'superl.', 'Limited as to means; straitened; pinching; as, narrow\n   circumstances.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Narrow', 'superl.', 'Contracted; of limited scope; illiberal; bigoted; as,\n   a narrow mind; narrow views.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Narrow', 'superl.', 'Parsimonious; niggardly; covetous; selfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Narrow', 'superl.', 'Scrutinizing in detail; close; accurate; exact.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Narrow', 'superl.', 'Formed (as a vowel) by a close position of some part\n   of the tongue in relation to the palate; or (according to Bell) by a\n   tense condition of the pharynx; -- distinguished from wide; as e (eve)\n   and / (f/d), etc., from i (ill) and / (f/t), etc. See Guide to\n   Pronunciation, / 13.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Narrows', 'pl. ', 'of Narrow');
  INSERT INTO words (word, word_type, description) VALUES  (    'Narrow', 'n.', 'A narrow passage; esp., a contracted part of a stream,\n   lake, or sea; a strait connecting two bodies of water; -- usually in\n   the plural; as, The Narrows of New York harbor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Narrowed', 'imp. & p. p.', 'of Narrow');
  INSERT INTO words (word, word_type, description) VALUES  (    'Narrowing', 'p. pr. & vb. n.', 'of Narrow');
  INSERT INTO words (word, word_type, description) VALUES  (    'Narrow', 'v. t.', 'To lessen the breadth of; to contract; to draw into a\n   smaller compass; to reduce the width or extent of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Narrow', 'v. t.', 'To contract the reach or sphere of; to make less liberal\n   or more selfish; to limit; to confine; to restrict; as, to narrow ones\n   views or knowledge; to narrow a question in discussion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Narrow', 'v. t.', 'To contract the size of, as a stocking, by taking two\n   stitches into one.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Narrow', 'v. i.', 'To become less broad; to contract; to become narrower;\n   as, the sea narrows into a strait.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Narrow', 'v. i.', 'Not to step out enough to the one hand or the other; as,\n   a horse narrows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Narrow', 'v. i.', 'To contract the size of a stocking or other knit\n   article, by taking two stitches into one.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Narrower', 'n.', 'One who, or that which, narrows or contracts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Narrowing', 'n.', 'The act of contracting, or of making or becoming less in\n   breadth or extent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Narrowing', 'n.', 'The part of a stocking which is narrowed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Narrowly', 'adv.', 'With little breadth; in a narrow manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Narrowly', 'adv.', 'Without much extent; contractedly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Narrowly', 'adv.', 'With minute scrutiny; closely; as, to look or watch\n   narrowly; to search narrowly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Narrowly', 'adv.', 'With a little margin or space; by a small distance;\n   hence, closely; hardly; barely; only just; -- often with reference to\n   an avoided danger or misfortune; as, he narrowly escaped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Narrowly', 'adv.', 'Sparingly; parsimoniously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Narrow-minded', 'a.', 'Of narrow mental scope; illiberal; mean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Narrowness', 'n.', 'The condition or quality of being narrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nart', '', 'Art not.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Narthex', 'n.', 'A tall umbelliferous plant (Ferula communis). See Giant\n   fennel, under Fennel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Narthex', 'n.', 'The portico in front of ancient churches; sometimes, the\n   atrium or outer court surrounded by ambulatories; -- used, generally,\n   for any vestibule, lobby, or outer porch, leading to the nave of a\n   church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Narwal', 'n.', 'See Narwhal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Narwe', 'a.', 'Narrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Narwhal', 'n.', 'An arctic cetacean (Monodon monocerous), about twenty feet\n   long. The male usually has one long, twisted, pointed canine tooth, or\n   tusk projecting forward from the upper jaw like a horn, whence it is\n   called also sea unicorn, unicorn fish, and unicorn whale. Sometimes two\n   horns are developed, side by side.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nas', '', 'Was not.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nas', '', 'Has not.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nasal', 'a.', 'Of or pertaining to the nose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nasal', 'a.', 'Having a quality imparted by means of the nose; and\n   specifically, made by lowering the soft palate, in some cases with\n   closure of the oral passage, the voice thus issuing (wholly or\n   partially) through the nose, as in the consonants m, n, ng (see Guide\n   to Pronunciation, // 20, 208); characterized by resonance in the nasal\n   passage; as, a nasal vowel; a nasal utterance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nasal', 'n.', 'An elementary sound which is uttered through the nose, or\n   through both the nose and the mouth simultaneously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nasal', 'n.', 'A medicine that operates through the nose; an errhine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nasal', 'n.', 'Part of a helmet projecting to protect the nose; a nose\n   guard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nasal', 'n.', 'One of the nasal bones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nasal', 'n.', 'A plate, or scale, on the nose of a fish, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nasality', 'n.', 'The quality or state of being nasal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nasalization', 'n.', 'The act of nasalizing, or the state of being\n   nasalized.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nasalized', 'imp. & p. p.', 'of Nasalize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nasalizing', 'p. pr. & vb. n.', 'of Nasalize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nasalize', 'v. t.', 'To render nasal, as sound; to insert a nasal or sound\n   in.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nasalize', 'v. t.', 'To utter words or letters with a nasal sound; to speak\n   through the nose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nasally', 'adv.', 'In a nasal manner; by the nose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nascal', 'n.', 'A kind of pessary of medicated wool or cotton, formerly\n   used.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nascency', 'n.', 'State of being nascent; birth; beginning; origin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nascent', 'a.', 'Commencing, or in process of development; beginning to\n   exist or to grow; coming into being; as, a nascent germ.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nascent', 'a.', 'Evolving; being evolved or produced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naseberry', 'n.', 'A tropical fruit. See Sapodilla.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nash', 'a.', 'Firm; stiff; hard; also, chilly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nasicornous', 'a.', 'Bearing a horn, or horns, on the nose, as the\n   rhinoceros.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nasiform', 'a.', 'Having the shape of a nose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nasion', 'n.', 'The middle point of the nasofrontal suture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naso-', '', 'A combining form denoting pertaining to, or connected with,\n   the nose; as, nasofrontal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nasobuccal', 'a.', 'Connected with both the nose and the mouth; as, the\n   nasobuccal groove in the skate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nasofrontal', 'a.', 'Of or pertaining to the nose and the front of the\n   head; as, the embryonic nasofrontal process which forms the anterior\n   boundary of the mouth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nasolachrymal', 'a.', 'Connected with the lachrymal apparatus and the nose;\n   as, the nasolachrymal, or lachrymal duct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nasopalatal', 'a.', 'Alt. of Nasopalatine');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nasopalatine', 'a.', 'Connected with both the nose and the palate; as, the\n   nasopalatine or incisor, canal connecting the mouth and the nasal\n   chamber in some animals; the nasopalatine nerve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nasopharyngeal', 'a.', 'Of or pertaining to both throat and nose; as, a\n   nasopharyngeal polypus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nasoseptal', 'a.', 'Of or pertaining to the internasal septum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nasoturbinal', 'a.', 'Connected with, or near, both the turbinal and the\n   nasal bones; as, the nasalturbinal bone, made up of the uppermost\n   lammelae of the ethmoturbinal, and sometimes united with the nasal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nasoturbinal', 'n.', 'The nasoturbinal bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nassas', 'pl. ', 'of Nassa');
  INSERT INTO words (word, word_type, description) VALUES  (    'NassAe', 'pl. ', 'of Nassa');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nassa', 'n.', 'Any species of marine gastropods, of the genera Nassa,\n   Tritia, and other allied genera of the family Nassidae; a dog whelk.\n   See Illust. under Gastropoda.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nastily', 'adv.', 'In a nasty manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nastiness', 'n.', 'The quality or state of being nasty; extreme filthness;\n   dirtiness; also, indecency; obscenity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nasturtion', 'n.', 'Same as Nasturtium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nasturtium', 'n.', 'A genus of cruciferous plants, having white or\n   yellowish flowers, including several species of cress. They are found\n   chiefly in wet or damp grounds, and have a pungent biting taste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nasturtium', 'n.', 'Any plant of the genus Tropaeolum, geraniaceous herbs,\n   having mostly climbing stems, peltate leaves, and spurred flowers, and\n   including the common Indian cress (Tropaeolum majus), the canary-bird\n   flower (T. peregrinum), and about thirty more species, all natives of\n   South America. The whole plant has a warm pungent flavor, and the\n   fleshy fruits are used as a substitute for capers, while the leaves and\n   flowers are sometimes used in salads.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nasty', 'superl.', 'Offensively filthy; very dirty, foul, or defiled;\n   disgusting; nauseous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nasty', 'superl.', 'Hence, loosely: Offensive; disagreeable; unpropitious;\n   wet; drizzling; as, a nasty rain, day, sky.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nasty', 'superl.', 'Characterized by obcenity; indecent; indelicate; gross;\n   filthy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nasute', 'a.', 'Having a nice sense of smell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nasute', 'a.', 'Critically nice; captious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nasutness', 'n.', 'Quickness of scent; hence, nice discernment; acuteness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nat', 'adv.', 'Not.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nat', '', 'Not at; nor at.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Natal', 'a.', 'Of or pertaining to ones birth; accompying or dating from\n   ones birth; native.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Natal', 'a.', 'Presiding over nativity; as, natal Jove.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Natalitial', 'a.', 'Alt. of Natalitious');
  INSERT INTO words (word, word_type, description) VALUES  (    'Natalitious', 'a.', 'Of or pertaining to ones birth or birthday, or ones\n   nativity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nataloin', 'n.', 'A bitter crystalline substance constituting the essential\n   principle of Natal aloes. Cf. Aloon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Natal plum', '', 'The drupaceous fruit of two South African shrubs of the\n   genus Arduina (A. bispinosa and A. grandiflora).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Natals', 'n. pl.', 'Ones birth, or the circumstances attending it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Natant', 'a.', 'Floating in water, as the leaves of water lilies, or\n   submersed, as those of many aquatic plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Natant', 'a.', 'Placed horizontally across the field, as if swimmimg toward\n   the dexter side; said of all sorts of fishes except the flying fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Natantly', 'adv.', 'In a floating manner; swimmingly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Natation', 'n.', 'The act of floating on the water; swimming.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Natatores', 'n. pl.', 'The swimming birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Natatorial', 'a.', 'Inclined or adapted to swim; swimming; as, natatorial\n   birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Natatorious', 'a.', 'Adapted for swimming; -- said of the legs of certain\n   insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Natatorium', 'n.', 'A swimming bath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Natatory', 'a.', 'Adapted for swimming or floating; as, natatory organs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Natch', 'n.', 'The rump of beef; esp., the lower and back part of the rump.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Natchez', 'n. pl.', 'A tribe of Indians who formerly lived near the site of\n   the city of Natchez, Mississippi. In 1729 they were subdued by the\n   French; the survivors joined the Creek Confederacy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Natchnee', 'n.', 'An annual grass (Eleusine coracona), cultivated in India\n   as a food plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nates', 'n. pl.', 'The buttocks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nates', 'n. pl.', 'The two anterior of the four lobes on the dorsal side of\n   the midbrain of most mammals; the anterior optic lobes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nates', 'n. pl.', 'The umbones of a bivalve shell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nath', '', 'hath not.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nathless', 'conj.', 'Nevertheless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nathmore', 'adv.', 'Not the more; never the more.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naticas', 'pl. ', 'of Natica');
  INSERT INTO words (word, word_type, description) VALUES  (    'NaticAe', 'pl. ', 'of Natica');
  INSERT INTO words (word, word_type, description) VALUES  (    'Natica', 'n.', 'Any one of numerous species of marine gastropods belonging\n   to Natica, Lunatia, Neverita, and other allied genera (family\n   Naticidae.) They burrow beneath the sand, or mud, and drill other\n   shells.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naticoid', 'a.', 'Like or belonging to Natica, or the family Naticidae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nation', 'n.', 'A part, or division, of the people of the earth,\n   distinguished from the rest by common descent, language, or\n   institutions; a race; a stock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nation', 'n.', 'The body of inhabitants of a country, united under an\n   independent government of their own.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nation', 'n.', 'Family; lineage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nation', 'n.', 'One of the divisions of university students in a\n   classification according to nativity, formerly common in Europe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nation', 'n.', 'One of the four divisions (named from the parts of\n   Scotland) in which students were classified according to their\n   nativity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nation', 'n.', 'A great number; a great deal; -- by way of emphasis; as, a\n   nation of herbs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'National', 'a.', 'Of or pertaining to a nation; common to a whole people or\n   race; public; general; as, a national government, language, dress,\n   custom, calamity, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'National', 'a.', 'Attached to ones own country or nation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nationalism', 'n.', 'The state of being national; national attachment;\n   nationality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nationalism', 'n.', 'An idiom, trait, or character peculiar to any nation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nationalism', 'n.', 'National independence; the principles of the\n   Nationalists.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nationalist', 'n.', 'One who advocates national unity and independence; one\n   of a party favoring Irish independence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nationalities', 'pl. ', 'of Nationality');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nationality', 'n.', 'The quality of being national, or strongly attached to\n   ones own nation; patriotism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nationality', 'n.', 'The sum of the qualities which distinguish a nation;\n   national character.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nationality', 'n.', 'A race or people, as determined by common language and\n   character, and not by political bias or divisions; a nation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nationality', 'n.', 'Existence as a distinct or individual nation; national\n   unity and integrity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nationality', 'n.', 'The state or quality of belonging to or being\n   connected with a nation or government by nativity, character,\n   ownership, allegiance, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nationalization', 'n.', 'The act of nationalizing, or the state of being\n   nationalized.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nationalized', 'imp. & p. p.', 'of Nationalize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nationalizing', 'p. pr. & vb. n.', 'of Nationalize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nationalize', 'v. t.', 'To make national; to make a nation of; to endow\n   with the character and habits of a nation, or the peculiar sentiments\n   and attachment of citizens of a nation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nationally', 'adv.', 'In a national manner or way; as a nation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nationalness', 'n.', 'The quality or state of being national; nationality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Native', 'a.', 'Arising by birth; having an origin; born.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Native', 'a.', 'Of or pertaining to ones birth; natal; belonging to the\n   place or the circumstances in which one is born; -- opposed to foreign;\n   as, native land, language, color, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Native', 'a.', 'Born in the region in which one lives; as, a native\n   inhabitant, race; grown or originating in the region where used or\n   sold; not foreign or imported; as, native oysters, or strawberries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Native', 'a.', 'Original; constituting the original substance of anything;\n   as, native dust.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Native', 'a.', 'Conferred by birth; derived from origin; born with one;\n   inherent; inborn; not acquired; as, native genius, cheerfulness,\n   simplicity, rights, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Native', 'a.', 'Naturally related; cognate; connected (with).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Native', 'a.', 'Found in nature uncombined with other elements; as, native\n   silver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Native', 'a.', 'Found in nature; not artificial; as native sodium chloride.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Native', 'n.', 'One who, or that which, is born in a place or country\n   referred to; a denizen by birth; an animal, a fruit, or vegetable,\n   produced in a certain region; as, a native of France.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Native', 'n.', 'Any of the live stock found in a region, as distinguished\n   from such as belong to pure and distinct imported breeds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Natively', 'adv.', 'By natural or original condition; naturally;\n   originally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nativeness', 'n.', 'The quality or state of being native.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nativism', 'n.', 'The disposition to favor the native inhabitants of a\n   country, in preference to immigrants from foreign countries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nativism', 'n.', 'The doctrine of innate ideas, or that the mind possesses\n   forms of thought independent of sensation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nativist', 'n.', 'An advocate of nativism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nativistic', 'a.', 'Relating to nativism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nativies', 'pl. ', 'of Nativity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nativity', 'n.', 'The coming into life or into the world; birth; also, the\n   circumstances attending birth, as time, place, manner, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nativity', 'n.', 'A picture representing or symbolizing the early infancy\n   of Christ. The simplest form is the babe in a rude cradle, and the\n   heads of an ox and an ass to express the stable in which he was born.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nativity', 'n.', 'A representation of the positions of the heavenly bodies\n   as the moment of ones birth, supposed to indicate his future\n   destinies; a horoscope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Natka', 'a.', 'A species of shrike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Natrium', 'n.', 'The technical name for sodium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Natrolite', 'n.', 'A zeolite occuring in groups of glassy acicular\n   crystals, and in masses which often have a radiated structure. It is a\n   hydrous silicate of alumina and soda.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Natron', 'n.', 'Native sodium carbonate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Natter', 'v. i.', 'To find fault; to be peevish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Natterjack', 'n.', 'A European toad (Bufo calamita), having a yellow line\n   along its back.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Natty', 'a.', 'Neat; tidy; spruce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Natural', 'a.', 'Fixed or determined by nature; pertaining to the\n   constitution of a thing; belonging to native character; according to\n   nature; essential; characteristic; not artifical, foreign, assumed, put\n   on, or acquired; as, the natural growth of animals or plants; the\n   natural motion of a gravitating body; natural strength or disposition;\n   the natural heat of the body; natural color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Natural', 'a.', 'Conformed to the order, laws, or actual facts, of nature;\n   consonant to the methods of nature; according to the stated course of\n   things, or in accordance with the laws which govern events, feelings,\n   etc.; not exceptional or violent; legitimate; normal; regular; as, the\n   natural consequence of crime; a natural death.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Natural', 'a.', 'Having to do with existing system to things; dealing with,\n   or derived from, the creation, or the world of matter and mind, as\n   known by man; within the scope of human reason or experience; not\n   supernatural; as, a natural law; natural science; history, theology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Natural', 'a.', 'Conformed to truth or reality');
  INSERT INTO words (word, word_type, description) VALUES  (    'Natural', 'a.', 'Springing from true sentiment; not artifical or\n   exaggerated; -- said of action, delivery, etc.; as, a natural gesture,\n   tone, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Natural', 'a.', 'Resembling the object imitated; true to nature; according\n   to the life; -- said of anything copied or imitated; as, a portrait is\n   natural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Natural', 'a.', 'Having the character or sentiments properly belonging to\n   ones position; not unnatural in feelings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Natural', 'a.', 'Connected by the ties of consanguinity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Natural', 'a.', 'Begotten without the sanction of law; born out of wedlock;\n   illegitimate; bastard; as, a natural child.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Natural', 'a.', 'Of or pertaining to the lower or animal nature, as\n   contrasted with the higher or moral powers, or that which is spiritual;\n   being in a state of nature; unregenerate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Natural', 'a.', 'Belonging to, to be taken in, or referred to, some system,\n   in which the base is 1; -- said or certain functions or numbers; as,\n   natural numbers, those commencing at 1; natural sines, cosines, etc.,\n   those taken in arcs whose radii are 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Natural', 'a.', 'Produced by natural organs, as those of the human throat,\n   in distinction from instrumental music.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Natural', 'a.', 'Of or pertaining to a key which has neither a flat nor a\n   sharp for its signature, as the key of C major.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Natural', 'a.', 'Applied to an air or modulation of harmony which moves by\n   easy and smooth transitions, digressing but little from the original\n   key.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Natural', 'n.', 'A native; an aboriginal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Natural', 'n.', 'Natural gifts, impulses, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Natural', 'n.', 'One born without the usual powers of reason or\n   understanding; an idiot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Natural', 'n.', 'A character [/] used to contradict, or to remove the\n   effect of, a sharp or flat which has preceded it, and to restore the\n   unaltered note.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naturalism', 'n.', 'A state of nature; conformity to nature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naturalism', 'n.', 'The doctrine of those who deny a supernatural agency in\n   the miracles and revelations recorded in the Bible, and in spiritual\n   influences; also, any system of philosophy which refers the phenomena\n   of nature to a blind force or forces acting necessarily or according to\n   fixed laws, excluding origination or direction by one intelligent will.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naturalist', 'n.', 'One versed in natural science; a student of natural\n   history, esp. of the natural history of animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naturalist', 'n.', 'One who holds or maintains the doctrine of naturalism\n   in religion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naturalistic', 'a.', 'Belonging to the doctrines of naturalism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naturalistic', 'a.', 'Closely resembling nature; realistic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naturality', 'n.', 'Nature; naturalness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naturalization', 'n.', 'The act or process of naturalizing, esp. of\n   investing an alien with the rights and privileges of a native or\n   citizen; also, the state of being naturalized.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naturalized', 'imp. & p. p.', 'of Naturalize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naturalizing', 'p. pr. & vb. n.', 'of Naturalize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naturalize', 'v. t.', 'To make natural; as, custom naturalizes labor or\n   study.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naturalize', 'v. t.', 'To confer the rights and privileges of a native\n   subject or citizen on; to make as if native; to adopt, as a foreigner\n   into a nation or state, and place in the condition of a native subject.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naturalize', 'v. t.', 'To receive or adopt as native, natural, or\n   vernacular; to make ones own; as, to naturalize foreign words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naturalize', 'v. t.', 'To adapt; to accustom; to habituate; to acclimate;\n   to cause to grow as under natural conditions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naturalize', 'v. i.', 'To become as if native.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naturalize', 'v. i.', 'To explain phenomena by natural agencies or laws, to\n   the exclusion of the supernatural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naturally', 'adv.', 'In a natural manner or way; according to the usual\n   course of things; spontaneously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naturalness', 'n.', 'The state or quality of being natural; conformity to\n   nature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nature', 'n.', 'The existing system of things; the world of matter, or of\n   matter and mind; the creation; the universe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nature', 'n.', 'The personified sum and order of causes and effects; the\n   powers which produce existing phenomena, whether in the total or in\n   detail; the agencies which carry on the processes of creation or of\n   being; -- often conceived of as a single and separate entity, embodying\n   the total of all finite agencies and forces as disconnected from a\n   creating or ordering intelligence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nature', 'n.', 'The established or regular course of things; usual order of\n   events; connection of cause and effect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nature', 'n.', 'Conformity to that which is natural, as distinguished from\n   that which is artifical, or forced, or remote from actual experience.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nature', 'n.', 'The sum of qualities and attributes which make a person or\n   thing what it is, as distinct from others; native character; inherent\n   or essential qualities or attributes; peculiar constitution or quality\n   of being.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nature', 'n.', 'Hence: Kind, sort; character; quality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nature', 'n.', 'Physical constitution or existence; the vital powers; the\n   natural life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nature', 'n.', 'Natural affection or reverence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nature', 'n.', 'Constitution or quality of mind or character.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nature', 'v. t.', 'To endow with natural qualities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Natured', 'a.', 'Having (such) a nature, temper, or disposition; disposed;\n   -- used in composition; as, good-natured, ill-natured, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Natureless', 'a.', 'Not in accordance with nature; unnatural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naturism', 'n.', 'The belief or doctrine that attributes everything to\n   nature as a sanative agent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naturist', 'n.', 'One who believes in, or conforms to, the theory of\n   naturism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naturity', 'n.', 'The quality or state of being produced by nature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naturize', 'v. t.', 'To endow with a nature or qualities; to refer to\n   nature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naufrage', 'n.', 'Shipwreck; ruin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naufragous', 'a.', 'causing shipwreck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naught', 'adv.', 'Nothing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naught', 'adv.', 'The arithmetical character 0; a cipher. See Cipher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naught', 'adv.', 'In no degree; not at all.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naught', 'a.', 'Of no value or account; worthless; bad; useless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naught', 'a.', 'Hence, vile; base; naughty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naughtily', 'adv.', 'In a naughty manner; wickedly; perversely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naughtiness', 'n.', 'The quality or state of being naughty; perverseness;\n   badness; wickedness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naughtly', 'adv.', 'Naughtily; wrongly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naughty', 'superl.', 'Having little or nothing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naughty', 'superl.', 'Worthless; bad; good for nothing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naughty', 'superl.', 'hence, corrupt; wicked.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naughty', 'superl.', 'Mischievous; perverse; froward; guilty of disobedient\n   or improper conduct; as, a naughty child.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naumachy', 'n.', 'A naval battle; esp., a mock sea fight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naumachy', 'n.', 'A show or spectacle representing a sea fight; also, a\n   place for such exhibitions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nauplii', 'pl. ', 'of Nauplius');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nauplius', 'n.', 'A crustacean larva having three pairs of locomotive\n   organs (corresponding to the antennules, antennae, and mandibles), a\n   median eye, and little or no segmentation of the body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nauropometer', 'n.', 'An instrument for measuring the amount which a ship\n   heels at sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nauscopy', 'n.', 'The power or act of discovering ships or land at\n   considerable distances.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nausea', 'n.', 'Seasickness; hence, any similar sickness of the stomach\n   accompanied with a propensity to vomit; qualm; squeamishness of the\n   stomach; loathing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nauseant', 'n.', 'A substance which produces nausea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nauseated', 'imp. & p. p.', 'of Nauseate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nauseating', 'p. pr. & vb. n.', 'of Nauseate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nauseate', 'v. i.', 'To become squeamish; to feel nausea; to turn away with\n   disgust.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nauseate', 'v. t.', 'To affect with nausea; to sicken; to cause to feel\n   loathing or disgust.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nauseate', 'v. t.', 'To sicken at; to reject with disgust; to loathe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nauseation', 'n.', 'The act of nauseating, or the state of being nauseated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nauseative', 'a.', 'Causing nausea; nauseous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nauseous', 'a.', 'Causing, or fitted to cause, nausea; sickening;\n   loathsome; disgusting; exciting abhorrence; as, a nauseous drug or\n   medicine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nautch', 'n.', 'An entertainment consisting chiefly of dancing by\n   professional dancing (or Nautch) girls.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nautic', 'a.', 'Nautical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nautical', 'a.', 'Of or pertaining to seamen, to the art of navigation, or\n   to ships; as, nautical skill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nautically', 'adv.', 'In a nautical manner; with reference to nautical\n   affairs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nautiform', 'a.', 'Shaped like the hull of a ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nautilite', 'n.', 'A fossil nautilus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nautiloid', 'a.', 'Like or pertaining to the nautilus; shaped like a\n   nautilus shell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nautiloid', 'n.', 'A mollusk, or shell, of the genus Nautilus or family\n   Nautilidae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nautiluses', 'pl. ', 'of Nautilus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nautili', 'pl. ', 'of Nautilus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nautilus', 'n.', 'The only existing genus of tetrabranchiate cephalopods.\n   About four species are found living in the tropical Pacific, but many\n   other species are found fossil. The shell is spiral, symmetrical, and\n   chambered, or divided into several cavities by simple curved\n   partitions, which are traversed and connected together by a continuous\n   and nearly central tube or siphuncle. See Tetrabranchiata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nautilus', 'n.', 'The argonaut; -- also called paper nautilus. See\n   Argonauta, and Paper nautilus, under Paper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nautilus', 'n.', 'A variety of diving bell, the lateral as well as vertical\n   motions of which are controlled, by the occupants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Navajoes', 'n. pl.', 'A tribe of Indians inhabiting New Mexico and Arizona,\n   allied to the Apaches. They are now largely engaged in agriculture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naval', 'a.', 'Having to do with shipping; of or pertaining to ships or a\n   navy; consisting of ships; as, naval forces, successes, stores, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Navals', 'n.pl.', 'Naval affairs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Navarch', 'n.', 'The commander of a fleet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Navarchy', 'n.', 'Nautical skill or experience.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Navarrese', 'a.', 'Of or pertaining to Navarre.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Navarrese', 'n. sing. & pl.', 'A native or inhabitant of Navarre; the\n   people of Navarre.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nave', 'n.', 'The block in the center of a wheel, from which the spokes\n   radiate, and through which the axle passes; -- called also hub or hob.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nave', 'n.', 'The navel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nave', 'n.', 'The middle or body of a church, extending from the transepts\n   to the principal entrances, or, if there are no transepts, from the\n   choir to the principal entrance, but not including the aisles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Navel', 'n.', 'A mark or depression in the middle of the abdomen; the\n   umbilicus. See Umbilicus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Navel', 'n.', 'The central part or point of anything; the middle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Navel', 'n.', 'An eye on the under side of a carronade for securing it to a\n   carriage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Navel-string', 'n.', 'The umbilical cord.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Navelwort', 'n.', 'A European perennial succulent herb (Cotyledon\n   umbilicus), having round, peltate leaves with a central depression; --\n   also called pennywort, and kidneywort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Navew', 'n.', 'A kind of small turnip, a variety of Brassica campestris.\n   See Brassica.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Navicular', 'a.', 'Of, pertaining to, or resembling, a boat or ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Navicular', 'a.', 'Shaped like a boat; cymbiform; scaphoid; as, the\n   navicular glumes of most grasses; the navicular bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Navicular', 'n.', 'The navicular bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Navigability', 'n.', 'The quality or condition of being navigable;\n   navigableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Navigable', 'a.', 'Capable of being navigated; deep enough and wide enough\n   to afford passage to vessels; as, a navigable river.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Navigated', 'imp. & p. p.', 'of Navigate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Navigating', 'p. pr. & vb. n.', 'of Navigate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Navigate', 'v. i.', 'To joirney by water; to go in a vessel or ship; to\n   perform the duties of a navigator; to use the waters as a highway or\n   channel for commerce or communication; to sail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Navigate', 'v. t.', 'To pass over in ships; to sail over or on; as, to\n   navigate the Atlantic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Navigate', 'v. t.', 'To steer, direct, or manage in sailing; to conduct\n   (ships) upon the water by the art or skill of seamen; as, to navigate a\n   ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Navigation', 'n.', 'The act of navigating; the act of passing on water in\n   ships or other vessels; the state of being navigable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Navigation', 'n.', 'the science or art of conducting ships or vessels from\n   one place to another, including, more especially, the method of\n   determining a ships position, course, distance passed over, etc., on\n   the surface of the globe, by the principles of geometry and astronomy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Navigation', 'n.', 'The management of sails, rudder, etc.; the mechanics of\n   traveling by water; seamanship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Navigation', 'n.', 'Ships in general.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Navigator', 'n.', 'One who navigates or sails; esp., one who direct the\n   course of a ship, or one who is skillful in the art of navigation;\n   also, a book which teaches the art of navigation; as, Bowditchs\n   Navigator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Navigerous', 'a.', 'Bearing ships; capable of floating vessels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Navies', 'pl. ', 'of Navvy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Navvy', 'n.', 'Originally, a laborer on canals for internal navigation;\n   hence, a laborer on other public works, as in building railroads,\n   embankments, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Navies', 'pl. ', 'of Navy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Navy', 'n.', 'A fleet of ships; an assemblage of merchantmen, or so many as\n   sail in company.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Navy', 'n.', 'The whole of the war vessels belonging to a nation or ruler,\n   considered collectively; as, the navy of Italy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Navy', 'n.', 'The officers and men attached to the war vessels of a nation;\n   as, he belongs to the navy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nawab', 'n.', 'A deputy ruler or viceroy in India; also, a title given by\n   courtesy to other persons of high rank in the East.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nawl', 'n.', 'An awl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nay', 'adv.', 'No; -- a negative answer to a question asked, or a request\n   made, now superseded by no. See Yes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nay', 'adv.', 'Not this merely, but also; not only so, but; -- used to mark\n   the addition or substitution of a more explicit or more emphatic\n   phrase.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nays', 'pl. ', 'of Nay');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nay', 'n.', 'Denial; refusal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nay', 'n.', 'a negative vote; one who votes in the negative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nay', 'v. t. & i.', 'To refuse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nayaur', 'n.', 'A specied of wild sheep (Ovis Hodgsonii), native of Nepaul\n   and Thibet. It has a dorsal mane and a white ruff beneath the neck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nayt', 'v. t.', 'To refuse; to deny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nayward', 'n.', 'The negative side.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nayword', 'n.', 'A byword; a proverb; also, a watchword.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nazarene', 'n.', 'A native or inhabitant of Nazareth; -- a term of contempt\n   applied to Christ and the early Christians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nazarene', 'n.', 'One of a sect of Judaizing Christians in the first and\n   second centuries, who observed the laws of Moses, and held to certain\n   heresies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nazarite', 'n.', 'A Jew bound by a vow to lave the hair uncut, to abstain\n   from wine and strong drink, and to practice extraordinary purity of\n   life and devotion, the obligation being for life, or for a certain\n   time. The word is also used adjectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nazariteship', 'n.', 'The state of a Nazarite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nazaritic', 'a.', 'Of or pertaining to a Nazarite, or to Nazarites.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nazaritism', 'n.', 'The vow and practice of a Nazarite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Naze', 'n.', 'A promotory or headland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nazirite', 'n.', 'A Nazarite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ne', 'adv.', 'Not; never.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ne', 'adv.', 'Nor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neaf', 'n.', 'See 2d Neif.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neal', 'v. t.', 'To anneal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neal', 'v. i.', 'To be tempered by heat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neap', 'n.', 'The tongue or pole of a cart or other vehicle drawn by two\n   animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neap', 'a.', 'Low.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neap', 'n.', 'A neap tide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neaped', 'a.', 'Left aground on the height of a spring tide, so that it\n   will not float till the next spring tide; -- called also beneaped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neapolitan', 'a.', 'Of of pertaining to Naples in Italy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neapolitan', 'n.', 'A native or citizen of Naples.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Near', 'adv.', 'At a little distance, in place, time, manner, or degree;\n   not remote; nigh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Near', 'adv.', 'Nearly; almost; well-nigh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Near', 'adv.', 'Closely; intimately.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Near', 'adv.', 'Not far distant in time, place, or degree; not remote;\n   close at hand; adjacent; neighboring; nigh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Near', 'adv.', 'Closely connected or related.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Near', 'adv.', 'Close to ones interests, affection, etc.; touching, or\n   affecting intimately; intimate; dear; as, a near friend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Near', 'adv.', 'Close to anything followed or imitated; not free, loose, or\n   rambling; as, a version near to the original.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Near', 'adv.', 'So as barely to avoid or pass injury or loss; close;\n   narrow; as, a near escape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Near', 'adv.', 'Next to the driver, when he is on foot; in the Unted\n   States, on the left of an animal or a team; as, the near ox; the near\n   leg. See Off side, under Off, a.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Near', 'a', 'Immediate; direct; close; short.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Near', 'a', 'Close-fisted; parsimonious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Near', 'prep.', 'Adjacent to; close by; not far from; nigh; as, the ship\n   sailed near the land. See the Note under near, a.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neared', 'imp. & p. p.', 'of Near');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nearing', 'p. pr. & vb. n', 'of Near');
  INSERT INTO words (word, word_type, description) VALUES  (    'Near', 'adv.', 'To approach; to come nearer; as, the ship neared the land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Near', 'v. i.', 'To draw near; to approach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nearctic', 'a.', 'Of or pertaining to a region of the earths surface\n   including all of temperate and arctic North America and Greenland. In\n   the geographical distribution of animals, this region is marked off as\n   the habitat certain species.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nearhand', 'a. & adv.', 'Near; near at hand; closely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Near-legged', 'a.', 'Having the feet so near together that they interfere\n   in traveling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nearly', 'adv.', 'In a near manner; not remotely; closely; intimately;\n   almost.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nearness', 'n.', 'The state or quality of being near; -- used in the\n   various senses of the adjective.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nearsighted', 'a.', 'Seeing distinctly at short distances only;\n   shortsighted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nearsightedness', 'n.', 'See Myopic, and Myopia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neat', 'n. sing. & pl.', 'Cattle of the genus Bos, as distinguished from\n   horses, sheep, and goats; an animal of the genus Bos; as, a neats\n   tongue; a neats foot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neat', 'n.', 'Of or pertaining to the genus Bos, or to cattle of that\n   genus; as, neat cattle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neat', 'a.', 'Free from that which soils, defiles, or disorders; clean;\n   cleanly; tidy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neat', 'a.', 'Free from what is unbecoming, inappropriate, or tawdry;\n   simple and becoming; pleasing with simplicity; tasteful; chaste; as, a\n   neat style; a neat dress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neat', 'a.', 'Free from admixture or adulteration; good of its kind; as,\n   neat brandy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neat', 'a.', 'Excellent in character, skill, or performance, etc.; nice;\n   finished; adroit; as, a neat design; a neat thief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neat', 'a.', 'With all deductions or allowances made; net. [In this sense\n   usually written net. See Net, a., 3.]');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neath', 'prep. & adv.', 'An abbreviation of Beneath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neatherd', 'n.', 'A person who has the care of neat cattle; a cowherd.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neathouse', 'n.', 'A building for the shelter of neat cattle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neatify', 'v. t.', 'To make neat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neatly', 'adv.', 'In a neat manner; tidily; tastefully.');
  INSERT INTO words (word, word_type, description) VALUES  (    'neatness', 'n.', 'The state or quality of being neat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neatress', 'n.', 'A woman who takes care of cattle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neb', 'n.', 'The nose; the snout; the mouth; the beak of a bird; a nib, as\n   of a pen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nebalia', 'n.', 'A genus of small marine Crustacea, considered the type of\n   a distinct order (Nebaloidea, or Phyllocarida.)');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neb-neb', 'n.', 'Same as Bablh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nebulae', 'pl. ', 'of Nebula');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nebula', 'n.', 'A faint, cloudlike, self-luminous mass of matter situated\n   beyond the solar system among the stars. True nebulae are gaseous; but\n   very distant star clusters often appear like them in the telescope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nebula', 'n.', 'A white spot or a slight opacity of the cornea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nebula', 'n.', 'A cloudy appearance in the urine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nebular', 'a.', 'Of or pertaining to nebulae; of the nature of, or\n   resembling, a nebula.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nebulated', 'a.', 'Clouded with indistinct color markings, as an animal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'nebulation', 'n.', 'The condition of being nebulated; also, a clouded, or\n   ill-defined, color mark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nebule', 'n.', 'A little cloud; a cloud.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nebule', 'a.', 'Alt. of Nebuly');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nebuly', 'a.', 'Composed of successive short curves supposed to resemble a\n   cloud; -- said of a heraldic line by which an ordinary or subordinary\n   may be bounded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nebulization', 'n.', 'The act or process of nebulizing; atomization.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nebulize', 'v. t.', 'To reduce (as a liquid) to a fine spray or vapor; to\n   atomize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nebulizer', 'n.', 'An atomizer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nebulose', 'a.', 'Nebulous; cloudy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nebulosity', 'n.', 'The state or quality of being nebulous; cloudiness;\n   hazeness; mistiness; nebulousness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nebulosity', 'n.', 'The stuff of which a nebula is formed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nebulosity', 'n.', 'A nebula.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nebulous', 'a.', 'Cloudy; hazy; misty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nebulous', 'a.', 'Of, pertaining to, or having the appearance of, a nebula;\n   nebular; cloudlike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nebuly', 'n.', 'A line or a direction composed of successive short curves\n   or waves supposed to resembe a cloud. See NEbulE');
  INSERT INTO words (word, word_type, description) VALUES  (    'Necessarian', 'n.', 'An advocate of the doctrine of philosophical\n   necessity; a nacessitarian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Necessarian', 'a.', 'Of or pertaining to necessarianism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Necessarianism', 'n.', 'The doctrine of philosophical necessity;\n   necessitarianism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Necessarily', 'adv.', 'In a necessary manner; by necessity; unavoidably;\n   indispensably.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Necessariness', 'n.', 'The quality of being necessary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Necessary', 'a.', 'Such as must be; impossible to be otherwise; not to be\n   avoided; inevitable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Necessary', 'a.', 'Impossible to be otherwise, or to be dispensed with,\n   without preventing the attainment of a desired result; indispensable;\n   requiste; essential.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Necessary', 'a.', 'Acting from necessity or compulsion; involuntary; --\n   opposed to free; as, whether man is a necessary or a free agent is a\n   question much discussed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Necessaries', 'pl. ', 'of Necessary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Necessary', 'n.', 'A thing that is necessary or indispensable to some\n   purpose; something that one can not do without; a requisite; an\n   essential; -- used chiefly in the plural; as, the necessaries of life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Necessary', 'n.', 'A privy; a water-closet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Necessary', 'n.', 'Such things, in respect to infants, lunatics, and\n   married women, as are requisite for support suitable to station.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Necessitarian', 'a.', 'Of or pertaining to the doctrine of philosophical\n   necessity in regard to the origin and existence of things, especially\n   as applied to the actings or choices of the will; -- opposed to\n   libertarian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Necessitarian', 'n.', 'One who holds to the doctrine of necessitarianism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Necessitarianism', 'n.', 'The doctrine of philosophical necessity; the\n   doctrine that results follow by invariable sequence from causes, and\n   esp. that the will is not free, but that human actions and choices\n   result inevitably from motives; deteminism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Necessitated', 'imp. & p. p.', 'of Necessitate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Necessitating', 'p. pr. & vb. n.', 'of Necessitate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Necessitate', 'v. t.', 'To make necessary or indispensable; to render\n   unaviolable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Necessitate', 'v. t.', 'To reduce to the necessity of; to force; to compel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Necessitattion', 'n.', 'The act of making necessary, or the state of being\n   made necessary; compulsion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Necessitied', 'a.', 'In a state of want; necessitous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Necessitous', 'a.', 'Very needy or indigent; pressed with poverty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Necessitous', 'a.', 'Narrow; destitute; pinching; pinched; as, necessitous\n   circumstances.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Necessitude', 'n.', 'Necessitousness; want.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Necessitude', 'n.', 'Necessary connection or relation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Necessities', 'pl. ', 'of Necessity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Necessity', 'n.', 'The quality or state of being necessary, unavoidable, or\n   absolutely requisite; inevitableness; indispensableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Necessity', 'n.', 'The condition of being needy or necessitous; pressing\n   need; indigence; want.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Necessity', 'n.', 'That which is necessary; a necessary; a requisite;\n   something indispensable; -- often in the plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Necessity', 'n.', 'That which makes an act or an event unavoidable;\n   irresistible force; overruling power; compulsion, physical or moral;\n   fate; fatality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Necessity', 'n.', 'The negation of freedom in voluntary action; the\n   subjection of all phenomena, whether material or spiritual, to\n   inevitable causation; necessitarianism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neck', 'n.', 'The part of an animal which connects the head and the trunk,\n   and which, in man and many other animals, is more slender than the\n   trunk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neck', 'n.', 'Any part of an inanimate object corresponding to or\n   resembling the neck of an animal');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neck', 'n.', 'The long slender part of a vessel, as a retort, or of a\n   fruit, as a gourd.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neck', 'n.', 'A long narrow tract of land projecting from the main body, or\n   a narrow tract connecting two larger tracts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neck', 'n.', 'That part of a violin, guitar, or similar instrument, which\n   extends from the head to the body, and on which is the finger board or\n   fret board.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neck', 'n.', 'A reduction in size near the end of an object, formed by a\n   groove around it; as, a neck forming the journal of a shaft.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neck', 'n.', 'the point where the base of the stem of a plant arises from\n   the root.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Necked', 'imp. & p. p.', 'of Neck');
  INSERT INTO words (word, word_type, description) VALUES  (    'Necking', 'p. pr. & vb. n.', 'of Neck');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neck', 'v. t.', 'To reduce the diameter of (an object) near its end, by\n   making a groove around it; -- used with down; as, to neck down a shaft.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neck', 'v. t. & i.', 'To kiss and caress amorously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neckar nut', '', 'See Nicker nut.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neckband', 'n.', 'A band which goes around the neck; often, the part at the\n   top of a garment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neckcloth', 'n.', 'A piece of any fabric worn around the neck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Necked', 'a.', 'Having (such) a neck; -- chiefly used in composition; as,\n   stiff-necked.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Necked', 'a.', 'Cracked; -- said of a treenail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neckerchief', 'n.', 'A kerchief for the neck; -- called also neck\n   handkerchief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Necking', 'n.', 'Same as Neckmold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Necklace', 'n.', 'A string of beads, etc., or any continuous band or chain,\n   worn around the neck as an ornament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Necklace', 'n.', 'A rope or chain fitted around the masthead to hold\n   hanging blocks for jibs and stays.');
  INSERT INTO words (word, word_type, description) VALUES  (    'necklaced', 'a.', 'Wearing a necklace; marked as with a necklace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'neckland', 'n.', 'A neck of land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'necklet', 'n.', 'A necklace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neckmold', 'n.', 'Alt. of Neckmould');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neckmould', 'n.', 'A small convex molding surrounding a column at the\n   jinction of the shaft and capital.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neckplate', 'n.', 'See Gorget, 1 and 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Necktie', 'n.', 'A scarf, band, or kerchief of silk, etc., passing around\n   the neck or collar and tied in front; a bow of silk, etc., fastened in\n   front of the neck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neckwear', 'n.', 'A collective term for cravats, collars, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neckweed', 'n.', 'An American annual weed (veronica peregrina), with small\n   white flowers and a roundish pod.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neckweed', 'n.', 'The hemp; -- so called as furnishing ropes for hanging\n   criminals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Necrobiosis', 'n.', 'The death of a part by molecular disintegration and\n   without loss of continuity, as in the processes of degeneration and\n   atrophy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Necrobiotic', 'a.', 'Of or pertaining to necrobiosis; as, a necrobiotic\n   metamorphosis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Necrolatry', 'n.', 'The worship of the dead; manes worship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Necrolite', 'n.', 'Same as Necronite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Necrologic', 'a.', 'Alt. of Necrological');
  INSERT INTO words (word, word_type, description) VALUES  (    'Necrological', 'a.', 'Of or pertaining to necrology; of the nature of\n   necrology; relating to, or giving, an account of the dead, or of\n   deaths.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Necrologist', 'n.', 'One who gives an account of deaths.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Necrologies', 'pl. ', 'of Necrology');
  INSERT INTO words (word, word_type, description) VALUES  (    'Necrology', 'n.', 'An account of deaths, or of the dead; a register of\n   deaths; a collection of obituary notices.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Necromancer', 'n.', 'One who practices necromancy; a sorcerer; a wizard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Necromancy', 'n.', 'The art of revealing future events by means of a\n   pretended communication with the dead; the black art; hence, magic in\n   general; conjuration; enchantment. See Black art.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Necromantic', 'n.', 'Conjuration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Necromantic', 'a.', 'Alt. of Necromantical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Necromantical', 'a.', 'Of or pertaining to necromancy; performed by\n   necromancy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Necronite', 'n.', 'Fetid feldspar, a mineral which, when struck, exhales a\n   fetid odor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Necrophagan', 'a.', 'Eating carrion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Necrophagan', 'n.', 'Any species of a tribe (Necrophaga) of beetles which,\n   in the larval state, feed on carrion; a burying beetle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Necrophagous', 'a.', 'Of or pertaining to the Necrophaga; eating carrion.\n   See Necrophagan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Necrophobia', 'n.', 'An exaggerated fear of death or horror of dead bodies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Necrophore', 'n.', 'Any one of numerous species of beetles of the genus\n   Necrophorus and allied genera; -- called also burying beetle, carrion\n   beetle, sexton beetle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Necropolises', 'pl. ', 'of Necropolis');
  INSERT INTO words (word, word_type, description) VALUES  (    'Necropolis', 'n.', 'A city of the dead; a name given by the ancients to\n   their cemeteries, and sometimes applied to modern burial places; a\n   graveyard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Necropsy', 'n.', 'A post-mortem examination or inspection; an autopsy. See\n   Autopsy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Necroscopic', 'a.', 'Alt. of Necroscopical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Necroscopical', 'a.', 'Or or relating to post-mortem examinations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Necrose', 'v. t. & i.', 'To affect with necrosis; to unergo necrosis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Necrosed', 'a.', 'Affected by necrosis; dead; as, a necrosed bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Necrosis', 'n.', 'Mortification or gangrene of bone, or the death of a bone\n   or portion of a bone in mass, as opposed to its death by molecular\n   disintegration. See Caries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Necrosis', 'n.', 'A disease of trees, in which the branches gradually dry\n   up from the bark to the center.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Necrotic', 'a.', 'Affected with necrosis; as, necrotic tissue;\n   characterized by, or producing, necrosis; as, a necrotic process.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nectar', 'n.', 'The drink of the gods (as ambrosia was their food); hence,\n   any delicious or inspiring beverage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nectar', 'n.', 'A sweetish secretion of blossoms from which bees make\n   honey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nectareal', 'a.', 'Nectareous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nectareal', 'a.', 'Of or pertaining to a nectary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nectarean', 'a.', 'Resembling nectar; very sweet and pleasant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nectared', 'a.', 'Imbued with nectar; mingled with nectar; abounding with\n   nectar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nectareous', 'a.', 'Of, pertaining to, containing, or resembling nectar;\n   delicious; nectarean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nectarial', 'a.', 'Of or pertaining to the nectary of a plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nectaried', 'a.', 'Having a nectary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nectariferous', 'a.', 'Secreting nectar; -- said of blossoms or their\n   parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nectarine', 'a.', 'Nectareous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nectarine', 'n.', 'A smooth-skinned variety of peach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nectarized', 'imp. & p. p.', 'of Nectarize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nectarizing', 'p. pr. & vb. n.', 'of Nectarize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nectarize', 'v. t.', 'To mingle or infuse with nectar; to sweeten.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nectarous', 'a.', 'Nectareous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nectaries', 'pl. ', 'of Nectary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nectary', 'n.', 'That part of a blossom which secretes nectar, usually the\n   base of the corolla or petals; also, the spur of such flowers as the\n   larkspur and columbine, whether nectariferous or not. See the\n   Illustration of Nasturtium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nectocalyces', 'pl. ', 'of Nectocalyx');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nectocalyx', 'n.', 'The swimming bell or umbrella of a jellyfish of medusa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nectocalyx', 'n.', 'One of the zooids of certain Siphonophora, having\n   somewhat the form, and the essential structure, of the bell of a\n   jellyfish, and acting as a swimming organ.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nectosac', 'n.', 'Alt. of Nectosack');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nectosack', 'n.', 'The cavity of a nectocalyx.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nectostem', 'n.', 'That portion of the axis which bears the nectocalyces in\n   the Siphonophora.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nedder', 'n.', 'An adder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neddies', 'pl. ', 'of Neddy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neddy', 'n.', 'A pet name for a donkey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nee', 'p. p., fem.', 'Born; -- a term sometimes used in introducing the\n   name of the family to which a married woman belongs by birth; as,\n   Madame de Stael, nee Necker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Need', 'n.', 'A state that requires supply or relief; pressing occasion for\n   something; necessity; urgent want.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Need', 'n.', 'Want of the means of subsistence; poverty; indigence;\n   destitution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Need', 'n.', 'That which is needful; anything necessary to be done; (pl.)\n   necessary things; business.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Need', 'n.', 'Situation of need; peril; danger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Needed', 'imp. & p. p.', 'of Need');
  INSERT INTO words (word, word_type, description) VALUES  (    'Needing', 'p. pr. & vb. n.', 'of Need');
  INSERT INTO words (word, word_type, description) VALUES  (    'Need', 'n.', 'To be in want of; to have cause or occasion for; to lack; to\n   require, as supply or relief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Need', 'v. i.', 'To be wanted; to be necessary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Need', 'adv.', 'Of necessity. See Needs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Needer', 'n.', 'One who needs anything.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Needful', 'a.', 'Full of need; in need or want; needy; distressing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Needful', 'a.', 'Necessary for supply or relief; requisite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Needily', 'adv.', 'In a needy condition or manner; necessarily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neediness', 'n.', 'The state or quality of being needy; want; poverty;\n   indigence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Needle', 'n.', 'A small instrument of steel, sharply pointed at one end,\n   with an eye to receive a thread, -- used in sewing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Needle', 'n.', 'See Magnetic needle, under Magnetic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Needle', 'n.', 'A slender rod or wire used in knitting; a knitting needle;\n   also, a hooked instrument which carries the thread or twine, and by\n   means of which knots or loops are formed in the process of netting,\n   knitting, or crocheting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Needle', 'n.', 'One of the needle-shaped secondary leaves of pine trees.\n   See Pinus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Needle', 'n.', 'Any slender, pointed object, like a needle, as a pointed\n   crystal, a sharp pinnacle of rock, an obelisk, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Needle', 'v. t.', 'To form in the shape of a needle; as, to needle\n   crystals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Needle', 'v. i.', 'To form needles; to crystallize in the form of needles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Needlebook', 'n.', 'A book-shaped needlecase, having leaves of cloth into\n   which the needles are stuck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Needlecase', 'n.', 'A case to keep needles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Needlefish', 'n.', 'The European great pipefich (Siphostoma, / Syngnathus,\n   acus); -- called also earl, and tanglefish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Needlefish', 'n.', 'The garfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'needlefuls', 'pl. ', 'of needleful');
  INSERT INTO words (word, word_type, description) VALUES  (    'needleful', 'n.', 'As much thread as is used in a needle at one time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Needle-pointed', 'a.', 'Pointed as needles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Needler', 'n.', 'One who makes or uses needles; also, a dealer in needles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Needless', 'a.', 'Having no need.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Needless', 'a.', 'Not wanted; unnecessary; not requiste; as, needless\n   labor; needless expenses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Needless', 'a.', 'Without sufficient cause; groundless; cuseless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Needlestone', 'n.', 'Natrolite; -- called also needle zeolite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Needlewomen', 'pl. ', 'of Needlewoman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Needlewoman', 'n.', 'A woman who does needlework; a seamstress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Needlework', 'n.', 'Work executed with a needle; sewed work; sewing;\n   embroidery; also, the business of a seamstress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Needlework', 'n.', 'The combination of timber and plaster making the\n   outside framework of some houses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Needly', 'a.', 'Like a needle or needles; as, a needly horn; a needly\n   beard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Needly', 'adv.', 'Necessarily; of necessity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Needment', 'n.', 'Something needed or wanted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Needment', 'n.', 'Outfit; necessary luggage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Needs', 'adv.', 'Of necessity; necessarily; indispensably; -- often with\n   must, and equivalent to of need.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Needscost', 'adv.', 'Of necessity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Needsly', 'adv.', 'Of necessity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Needy', 'superl.', 'Distressed by want of the means of living; very por;\n   indigent; necessitous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Needy', 'superl.', 'Necessary; requiste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neeld', 'n.', 'Alt. of Neele');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neele', 'n.', 'A needle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neelghau', 'n.', 'See Nylghau.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neem tree', '', 'An Asiatic name for Melia Azadirachta, and M. Azedarach.\n   See Margosa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neer', 'adv. & a.', 'Nearer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neer', 'adv.', 'a contraction of Never.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neesed', 'imp. & p. p.', 'of Neese');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neesing', 'p. pr. & vb. n.', 'of Neese');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neese', 'v. i.', 'To sneeze.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neesing', 'n.', 'Sneezing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ne exeat', '', 'A writ to restrain a person from leaving the country, or\n   the jurisdiction of the court. The writ was originally applicable to\n   purposes of state, but is now an ordinary process of courts of equity,\n   resorted to for the purpose of obtaining bail, or security to abide a\n   decree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nef', 'n.', 'The nave of a church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nefand', 'a.', 'Alt. of Nefandous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nefandous', 'a.', 'Unfit to speak of; unmentionable; impious; execrable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nefarious', 'adv.', 'Wicked in the extreme; abominable; iniquitous;\n   atrociously villainous; execrable; detestably vile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nefasch', 'n.', 'Any fish of the genus Distichodus. Several large species\n   inhabit the Nile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nefast', 'a.', 'Wicked.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Negation', 'adv.', 'The act of denying; assertion of the nonreality or\n   untruthfulness of anything; declaration that something is not, or has\n   not been, or will not be; denial; -- the opposite of affirmation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Negation', 'adv.', 'Description or definition by denial, exclusion, or\n   exception; statement of what a thing is not, or has not, from which may\n   be inferred what it is or has.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Negative', 'a.', 'Denying; implying, containing, or asserting denial,\n   negation or refusal; returning the answer no to an inquiry or request;\n   refusing assent; as, a negative answer; a negative opinion; -- opposed\n   to affirmative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Negative', 'a.', 'Not positive; without affirmative statement or\n   demonstration; indirect; consisting in the absence of something;\n   privative; as, a negative argument; a negative morality; negative\n   criticism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Negative', 'a.', 'Asserting absence of connection between a subject and a\n   predicate; as, a negative proposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Negative', 'a.', 'Of or pertaining to a picture upon glass or other\n   material, in which the lights and shades of the original, and the\n   relations of right and left, are reversed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Negative', 'a.', 'Metalloidal; nonmetallic; -- contracted with positive or\n   basic; as, the nitro group is negative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Negative', 'n.', 'A proposition by which something is denied or forbidden;\n   a conception or term formed by prefixing the negative particle to one\n   which is positive; an opposite or contradictory term or conception.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Negative', 'n.', 'A word used in denial or refusal; as, not, no.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Negative', 'n.', 'The refusal or withholding of assents; veto.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Negative', 'n.', 'That side of a question which denies or refuses, or which\n   is taken by an opposing or denying party; the relation or position of\n   denial or opposition; as, the question was decided in the negative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Negative', 'n.', 'A picture upon glass or other material, in which the\n   light portions of the original are represented in some opaque material\n   (usually reduced silver), and the dark portions by the uncovered and\n   transparent or semitransparent ground of the picture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Negative', 'n.', 'The negative plate of a voltaic or electrolytic cell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Negatived', 'imp. & p. p.', 'of Negative');
  INSERT INTO words (word, word_type, description) VALUES  (    'Negativing', 'p. pr. & vb. n.', 'of Negative');
  INSERT INTO words (word, word_type, description) VALUES  (    'Negative', 'v. t.', 'To prove unreal or intrue; to disprove.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Negative', 'v. t.', 'To reject by vote; to refuse to enact or sanction; as,\n   the Senate negatived the bill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Negative', 'v. t.', 'To neutralize the force of; to counteract.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Negatively', 'adv.', 'In a negative manner; with or by denial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Negatively', 'adv.', 'In the form of speech implying the absence of\n   something; -- opposed to positively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Negativeness', 'n.', 'Alt. of Negativity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Negativity', 'n.', 'The quality or state of being negative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Negatory', 'a.', 'Expressing denial; belonging to negation; negative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neginoth', 'n. pl.', 'Stringed instruments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neglected', 'imp. & p. p.', 'of Neglect');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neglecting', 'p. pr. & vb. n.', 'of Neglect');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neglect', 'adv.', 'Not to attend to with due care or attention; to forbear\n   ones duty in regard to; to suffer to pass unimproved, unheeded,\n   undone, etc.; to omit; to disregard; to slight; as, to neglect duty or\n   business; to neglect to pay debts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neglect', 'adv.', 'To omit to notice; to forbear to treat with attention or\n   respect; to slight; as, to neglect strangers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neglect', 'v.', 'Omission of proper attention; avoidance or disregard of\n   duty, from heedlessness, indifference, or willfulness; failure to do,\n   use, or heed anything; culpable disregard; as, neglect of business, of\n   health, of economy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neglect', 'v.', 'Omission if attention or civilities; slight; as, neglect\n   of strangers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neglect', 'v.', 'Habitual carelessness; negligence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neglect', 'v.', 'The state of being disregarded, slighted, or neglected.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neglectedness', 'n.', 'The state of being neglected.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neglecter', 'n.', 'One who neglects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neglectful', 'a.', 'Full of neglect; heedless; careless; negligent;\n   inattentive; indifferent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neglectingly', 'adv.', 'Carelessly; heedlessly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neglection', 'n.', 'The state of being negligent; negligence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neglective', 'a.', 'Neglectful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Negligee', 'n.', 'An easy, unceremonious attire; undress; also, a kind of\n   easy robe or dressing gown worn by women.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Negligence', 'n.', 'The quality or state of being negligent; lack of due\n   diligence or care; omission of duty; habitual neglect; heedlessness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Negligence', 'n.', 'An act or instance of negligence or carelessness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Negligence', 'n.', 'The omission of the care usual under the circumstances,\n   being convertible with the Roman culpa. A specialist is bound to higher\n   skill and diligence in his specialty than one who is not a specialist,\n   and liability for negligence varies acordingly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Negligent', 'a.', 'Apt to neglect; customarily neglectful; characterized by\n   negligence; careless; heedless; culpably careless; showing lack of\n   attention; as, disposed in negligent order.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Negligently', 'adv.', 'In a negligent manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Negligible', 'a.', 'That may neglicted, disregarded, or left out of\n   consideration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Negoce', 'n.', 'Business; occupation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Negotiability', 'n.', 'The quality of being negotiable or transferable by\n   indorsement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Negotiable', 'a.', 'Capable of being negotiated; transferable by assigment\n   or indorsement to another person; as, a negotiable note or bill of\n   exchange.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Negotiant', 'n.', 'A negotiator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Negotiate', 'v. i.', 'To transact business; to carry on trade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Negotiate', 'v. i.', 'To treat with another respecting purchase and sale or\n   some business affair; to bargain or trade; as, to negotiate with a man\n   for the purchase of goods or a farm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Negotiate', 'v. i.', 'To hold intercourse respecting a treaty, league, or\n   convention; to treat with, respecting peace or commerce; to conduct\n   communications or conferences.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Negotiate', 'v. i.', 'To intrigue; to scheme.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Negotiated', 'imp. & p. p.', 'of Negotiate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Negotiating', 'p. pr. & vb. n.', 'of Negotiate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Negotiate', 'v. t.', 'To carry on negotiations concerning; to procure or\n   arrange for by negotiation; as, to negotiate peace, or an exchange.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Negotiate', 'v. t.', 'To transfer for a valuable consideration under rules\n   of commercial law; to sell; to pass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Negotiation', 'n.', 'The act or process of negotiating; a treating with\n   another respecting sale or purchase. etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Negotiation', 'n.', 'Hence, mercantile business; trading.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Negotiation', 'n.', 'The transaction of business between nations; the\n   mutual intercourse of governments by diplomatic agents, in making\n   treaties, composing difference, etc.; as, the negotiations at Ghent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Negotiator', 'n.', 'One who negotiates; a person who treats with others,\n   either as principal or agent, in respect to purchase and sale, or\n   public compacts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Negotiatory', 'a.', 'Of or pertaining to negotiation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Negotiatrix', 'n.', 'A woman who negotiates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Negotiosity', 'n.', 'The state of being busy; multitude of business.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Negotious', 'a.', 'Very busy; attentive to business; active.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Negotiousness', 'n.', 'The state of being busily occupied; activity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Negresses', 'pl. ', 'of Negress');
  INSERT INTO words (word, word_type, description) VALUES  (    'Negress', 'n.', 'A black woman; a female negro.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Negrita', 'n.', 'A blackish fish (Hypoplectrus nigricans), of the Sea-bass\n   family. It is a native of the West Indies and Florida.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Negritic', 'a.', 'Of or pertaining to negroes; composed of negroes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Negritos', 'n. pl.', 'A degraded Papuan race, inhabiting Luzon and some of\n   the other east Indian Islands. They resemble negroes, but are smaller\n   in size. They are mostly nomads.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Negroes', 'pl. ', 'of Negro');
  INSERT INTO words (word, word_type, description) VALUES  (    'Negro', 'n.', 'A black man; especially, one of a race of black or very dark\n   persons who inhabit the greater part of tropical Africa, and are\n   distinguished by crisped or curly hair, flat noses, and thick\n   protruding lips; also, any black person of unmixed African blood,\n   wherever found.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Negro', 'a.', 'Of or pertaining to negroes; black.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Negroid', 'a.', 'Characteristic of the negro.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Negroid', 'a.', 'Resembling the negro or negroes; of or pertaining to those\n   who resemble the negro.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Negroloid', 'a.', 'See Negroid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Negus', 'n.', 'A beverage made of wine, water, sugar, nutmeg, and lemon\n   juice; -- so called, it is said, from its first maker, Colonel Negus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nehiloth', 'n. pl.', 'A term supposed to mean, perforated wind instruments\n   of music, as pipes or flutes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nehushtan', 'n.', 'A thing of brass; -- the name under which the Israelites\n   worshiped the brazen serpent made by Moses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neif', 'n.', 'Alt. of Neife');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neife', 'n.', 'A woman born in the state of villeinage; a female serf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neif', 'n.', 'Alt. of Neaf');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neaf', 'n.', 'The fist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neighed', 'imp. & p. p.', 'of Neigh');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neighing', 'p. pr. & vb. n.', 'of Neigh');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neigh', 'v. i.', 'To utter the cry of the horse; to whinny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neigh', 'v. i.', 'To scoff or sneer; to jeer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neigh', 'n.', 'The cry of a horse; a whinny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neighbor', 'n.', 'A person who lives near another; one whose abode is not\n   far off.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neighbor', 'n.', 'One who is near in sympathy or confidence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neighbor', 'n.', 'One entitled to, or exhibiting, neighborly kindness;\n   hence, one of the human race; a fellow being.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neighbor', 'a.', 'Near to another; adjoining; adjacent; next; neighboring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neighbored', 'imp. & p. p.', 'of neighbor');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neighboring', 'p. pr. & vb. n', 'of neighbor');
  INSERT INTO words (word, word_type, description) VALUES  (    'neighbor', 'v. t.', 'To adjoin; to border on; tobe near to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'neighbor', 'v. t.', 'To associate intimately with.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neighbor', 'v. i.', 'To dwell in the vicinity; to be a neighbor, or in the\n   neighborhood; to be near.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neighborhood', 'n.', 'The quality or condition of being a neighbor; the\n   state of being or dwelling near; proximity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neighborhood', 'n.', 'A place near; vicinity; adjoining district; a region\n   the inhabitants of which may be counted as neighbors; as, he lives in\n   my neighborhood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neighborhood', 'n.', 'The inhabitants who live in the vicinity of each\n   other; as, the fire alarmed all the neiborhood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neighborhood', 'n.', 'The disposition becoming a neighbor; neighborly\n   kindness or good will.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neighboring', 'a.', 'Living or being near; adjacent; as, the neighboring\n   nations or countries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neighborliness', 'n.', 'The quality or state of being neighborly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neighborly', 'a.', 'Apropriate to the relation of neighbors; having\n   frequent or familiar intercourse; kind; civil; social; friendly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neighborly', 'adv.', 'In a neigborly manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neighborship', 'n.', 'The state of being neighbors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neishout', 'n.', 'The mahogany-like wood of the South African tree\n   Pteroxylon utile, the sawdust of which causes violent sneezing (whence\n   the name). Also called sneezewood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neither', 'a.', 'Not either; not the one or the other.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neither', 'conj.', 'not either; generally used to introduce the first of\n   two or more coordinate clauses of which those that follow begin with\n   nor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nelumbo', 'n.', 'A genus of great water lilies. The North American species\n   is Nelumbo lutea, the Asiatic is the sacred lotus, N. speciosa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nemaline', 'a.', 'Having the form of threads; fibrous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nemalite', 'n.', 'A fibrous variety of brucite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nematelmia', 'n. pl.', 'Same as Nemathelminthes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nemathecia', 'pl. ', 'of Nemathecium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nemathecium', 'n.', 'A peculiar kind of fructification on certain red\n   algae, consisting of an external mass of filaments at length separating\n   into tetraspores.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nemthelminthes', 'n. pl.', 'Alt. of Nematelminthes');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nematelminthes', 'n. pl.', 'An ordr of helminths, including the Nematoidea\n   and Gordiacea; the roundworms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nemato-', '', 'A combining form from Gr. nh^ma, nh`matos, a thread.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nematoblast', 'n.', 'A spermatocyte or spermoblast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nematocalyces', 'pl. ', 'of Nematocalyx');
  INSERT INTO words (word, word_type, description) VALUES  (    'Calyxes', 'pl. ', 'of Nematocalyx');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nematocalyx', 'n.', 'One of a peculiar kind of cups, or calicles, found\n   upon hydroids of the family Plumularidae. They contain nematocysts. See\n   Plumularia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nematocera', 'n. pl.', 'A suborder of dipterous insects, having long\n   antennae, as the mosquito, gnat, and crane fly; -- called also\n   Nemocera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nematocyst', 'n.', 'A lasso cell, or thread cell. See Lasso cell, under\n   Lasso.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nematode', 'a. & n.', 'Same as Nematoid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nematogene', 'n.', 'One of the dimorphic forms of the species of Dicyemata,\n   which produced vermiform embryos; -- opposed to rhombogene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nematognath', 'n.', 'one of the Nematognathi.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nematognathi', 'n. pl.', 'An order of fishes having barbels on the jaws. It\n   includes the catfishes, or siluroids. See Siluroid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nematoid', 'a.', 'Of or pertaining to the Nematoidea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nematoid', 'n.', 'One of the Nematoidea. see Illustration in Appendix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nematoidea', 'n. pl.', 'An order of worms, having a long, round, and\n   generally smooth body; the roundworms. they are mostly parasites.\n   Called also Nematodea, and Nematoda.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nematoidean', 'a. & n.', 'Nematoid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nematophora', 'n. pl.', 'Same as Coelenterata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nemean', 'a.', 'Of or pertaining to Nemea, in Argolis, where the ancient\n   Greeks celebrated games, and Hercules killed a lion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nemetean', 'a.', 'Of or pertaining to the Nemertina.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nemetean', 'n.', 'One of the Nemertina.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nemertes', 'n.', 'A genus of nemertina.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nemertian', 'a. & n.', 'Nemertean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nemertid', 'a. & n.', 'Nemertean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nemertida', 'n. pl.', 'Nemertina.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nemertina', 'n. pl.', 'An order of helminths usually having a long,\n   slender, smooth, often bright-colored body, covered with minute\n   vibrating cilia; -- called also Nemertea, Nemertida, and Rhynchocoela.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nemesis', 'n.', 'The goddess of retribution or vengeance; hence,\n   retributive justice personified; divine vengeance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nemophilist', 'n.', 'One who is fond of forest or forest scenery; a haunter\n   of the woods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nemophily', 'n.', 'Fondness for forest scenery; love of the woods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nemoral', 'a.', 'Of or pertaining to a wood or grove.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nemorous', 'a.', 'Woody.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nempne', 'v.', 'To name or call.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nempt', 'p. p.', 'Called; named.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nems', 'n.', 'The ichneumon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nenia', 'n.', 'A funeral song; an elegy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nenuphar', 'n.', 'The great white water lily of Europe; the Nymphaea alba.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neo-', '', 'A prefix meaning new, recent, late; and in chemistry\n   designating specifically that variety of metameric hydrocarbons which,\n   when the name was applied, had been recently classified, and in which\n   at least one carbon atom in connected directly with four other carbon\n   atoms; -- contrasted with normal and iso-; as, neopentane; the\n   neoparaffins. Also used adjectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neocarida', 'n. pl.', 'The modern, or true, Crustacea, as distinguished\n   from the Merostomata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neocene', 'a.', 'More recent than the Eocene, that is, including both the\n   Miocene and Pliocene divisions of the Tertiary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neo-Christianity', 'n.', 'Rationalism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neocomian', 'n.', 'A term applied to the lowest deposits of the Cretaceous\n   or chalk formation of Europe, being the lower greensand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neocomian', 'a.', 'Of or pertaining to the lower greensand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neocosmic', 'a.', 'Of or pertaining to the universe in its present state;\n   specifically, pertaining to the races of men known to history.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neocracy', 'n.', 'Government by new or inexperienced hands; upstart rule;\n   raw or untried officials.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neodamode', 'n.', 'In ancient Sparta, one of those Helots who were freed by\n   the state in reward for military service.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neodymium', 'n.', 'An elementary substance which forms one of the\n   constituents of didymium. Symbol Nd. Atomic weight 140.8.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neogaean', 'a.', 'Of or pertaining to the New World, or Western Hemisphere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neogamist', 'n.', 'A person recently married.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neogen', 'n.', 'An alloy resembling silver, and consisting chiefly of\n   copper, zinc, and nickel, with small proportions of tin, aluminium, and\n   bismuth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neography', 'n.', 'A new method or system of writing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neo-Latin', 'a.', 'Applied to the Romance languages, as being mostly of\n   Latin origin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neolithic', 'a.', 'Of or pertaining to, or designating, an era\n   characterized by late remains in stone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neologian', 'a.', 'Neologic; neological.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neologian', 'n.', 'A neologist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neologianism', 'n.', 'Neologism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neologic', 'a.', 'Alt. of Neological');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neological', 'a.', 'Of or pertaining to neology; employing new words; of\n   the nature of, or containing, new words or new doctrines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neologically', 'adv.', 'In a neological manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neologism', 'n.', 'The introduction of new words, or the use of old words\n   in a new sense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neologism', 'n.', 'A new word, phrase, or expression.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neologism', 'n.', 'A new doctrine; specifically, rationalism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neologist', 'n.', 'One who introduces new words or new senses of old words\n   into a language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neologist', 'n.', 'An innovator in any doctrine or system of belief,\n   especially in theology; one who introduces or holds doctrines\n   subversive of supernatural or revealed religion; a rationalist,\n   so-called.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neologistic', 'a.', 'Alt. of Neologistical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neologistical', 'a.', 'Of or pertaining to neology; neological.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neologization', 'n.', 'The act or process of neologizing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neologize', 'v. i.', 'To introduce or use new words or terms or new uses of\n   old words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neologize', 'v. i.', 'To introduce innovations in doctrine, esp. in\n   theological doctrine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neology', 'n.', 'The introduction of a new word, or of words or\n   significations, into a language; as, the present nomenclature of\n   chemistry is a remarkable instance of neology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neology', 'n.', 'A new doctrine; esp. (Theol.), a doctrine at variance with\n   the received interpretation of revealed truth; a new method of\n   theological interpretation; rationalism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neomenia', 'n.', 'The time of the new moon; the beginning of the month in\n   the lunar calendar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neomenoidea', 'n. pl.', 'A division of vermiform gastropod mollusks,\n   without a shell, belonging to the Isopleura.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neomorph', 'n.', 'A structure, part, or organ developed independently, that\n   is, not derived from a similar structure, part, or organ, in a pre\n   existing form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neonism', 'n.', 'Neologism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neonomian', 'n.', 'One who advocates adheres to new laws; esp. one who\n   holds or believes that the gospel is a new law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neonomian', 'a.', 'Of or pertaining to the Neonomians, or in accordance\n   with their doctrines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neonomianism', 'n.', 'The doctrines or belief of the neonomians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neophyte', 'n.', 'A new convert or proselyte; -- a name given by the early\n   Christians, and still given by the Roman Catholics, to such as have\n   recently embraced the Christian faith, and been admitted to baptism,\n   esp. to converts from heathenism or Judaism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neophyte', 'n.', 'A novice; a tyro; a beginner in anything.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neoplasia', 'n.', 'Growth or development of new material; neoplasty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neoplasm', 'n.', 'A new formation or tissue, the product of morbid action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neoplastic', 'a.', 'Of or pertaining to neoplasty, or neoplasia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neoplasty', 'n.', 'Restoration of a part by granulation, adhesive\n   inflammation, or autoplasty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neoplatonic', 'a.', 'Of, pertaining to, or resembling, Neoplatonism or the\n   Neoplatonists.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neoplatonician', 'n.', 'A neoplatonist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neoplatonism', 'n.', 'A pantheistic eclectic school of philosophy, of which\n   Plotinus was the chief (A. D. 205-270), and which sought to reconcile\n   the Platonic and Aristotelian systems with Oriental theosophy. It\n   tended to mysticism and theurgy, and was the last product of Greek\n   philosophy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neoplatonist', 'n.', 'One who held to Neoplatonism; a member of the\n   Neoplatonic school.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neorama', 'n.', 'A panorama of the interior of a building, seen from\n   within.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neossine', 'n.', 'The substance constituting the edible birds nest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neossology', 'n.', 'The study of young birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neoteric', 'a.', 'Alt. of Neoterical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neoterical', 'a.', 'Recent in origin; modern; new.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neoteric', 'n.', 'One of modern times; a modern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neoterically', 'adv.', 'Recently; newly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neoterism', 'n.', 'An innovation or novelty; a neoteric word or phrase.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neoterist', 'n.', 'One ho introduces new word/ or phrases.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neoterized', 'imp. & p. p.', 'of Neoterize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neoterized', 'p. pr. & vb. n.', 'of Neoterize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neoterize', 'v. i.', 'To innovate; to coin or introduce new words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neotropical', 'a.', 'Belonging to, or designating, a region of the earths\n   surface which comprises most of South America, the Antilles, and\n   tropical North America.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neozoic', 'a.', 'More recent than the Paleozoic, -- that is, including the\n   Mesozoic and Cenozoic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nep', 'n.', 'Catnip.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nepa', 'n.', 'A genus of aquatic hemipterus insects. The species feed upon\n   other insects and are noted for their voracity; -- called also scorpion\n   bug and water scorpion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nepaulese', 'a.', 'Of or pertaining to Nepaul, a kingdom in Northern\n   Hindostan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nepaulese', 'n. sing. & pl.', 'A native or natives of Nepaul.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nepenthe', 'n.', 'A drug used by the ancients to give relief from pain and\n   sorrow; -- by some supposed to have been opium or hasheesh. Hence,\n   anything soothing and comforting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nepenthes', 'n.', 'Same as Nepenthe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nepenthes', 'n.', 'A genus of climbing plants found in India, Malaya, etc.,\n   which have the leaves prolonged into a kind of stout tendril\n   terminating in a pitcherlike appendage, whence the plants are often\n   called pitcher plants and monkey-cups. There are about thirty species,\n   of which the best known is Nepenthes distillatoria. See Pitcher plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nepeta', 'n.', 'A genus of labiate plants, including the catnip and ground\n   ivy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nephalism', 'n.', 'Total abstinence from spirituous liquor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nephalist', 'n.', 'One who advocates or practices nephalism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nepheline', 'n.', 'Alt. of Nephelite');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nephelite', 'n.', 'A mineral occuring at Vesuvius, in glassy agonal\n   crystals; also elsewhere, in grayish or greenish masses having a greasy\n   luster, as the variety elaeolite. It is a silicate of aluminia, soda,\n   and potash.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nephelodometer', 'n.', 'An instrument for reckoning the distances or\n   velocities of clouds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nephelometer', 'n.', 'An instrument for measuring or registering the amount\n   of cloudiness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nephew', 'n.', 'A grandson or grandchild, or remoter lineal descendant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nephew', 'n.', 'A cousin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nephew', 'n.', 'The son of a brother or a sister, or of a brother-in-law or\n   sister-in-law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nephilim', 'n. pl.', 'Giants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nephoscope', 'n.', 'An instrument for observing the clouds and their\n   velocity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nephralgia', 'n.', 'Alt. of Nephralgy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nephralgy', 'n.', 'Neuralgia of the kidneys; a disease characterized by\n   pain in the region of the kidneys without any structural lesion of the\n   latter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nephridial', 'a.', 'Of or pertaining to a nephridium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nephridia', 'pl. ', 'of Nephridium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nephridium', 'n.', 'A segmental tubule; one of the tubules of the primitive\n   urinogenital organs; a segmental organ. See Illust. under Loevens\n   larva.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nephrite', 'n.', 'A hard compact mineral, of a dark green color, formerly\n   worn as a remedy for diseases of the kidneys, whence its name; kidney\n   stone; a kind of jade. See Jade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nephritic', 'a.', 'Alt. of Nephritical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nephritical', 'a.', 'Of or pertaining to the kidneys or urinary organs;\n   renal; as, a nephritic disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nephritical', 'a.', 'Affected with a disease of the kidneys; as, a\n   nephritic patient.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nephritical', 'a.', 'Relieving disorders of the kidneys; affecting the\n   kidneys; as, a nephritic medicine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nephritic', 'n.', 'A medicine adapted to relieve or cure disease of the\n   kidneys.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nephritis', 'n.', 'An inflammation of the kidneys.');
  INSERT INTO words (word, word_type, description) VALUES  (    'nephrolithic', 'a.', 'Of or pertaining to gravel, or renal calculi.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nephrology', 'n.', 'A treatise on, or the science which treats of, the\n   kidneys, and their structure and functions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nephrostome', 'n.', 'The funnelshaped opening of a nephridium into the body\n   cavity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nephrotomy', 'n.', 'Extraction of stone from the kidney by cutting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nepotal', 'a.', 'Of or relating to a nephew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nepotic', 'a.', 'Of or pertaining to npotism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nepotism', 'n.', 'Undue attachment to relations; favoritism shown to\n   members of ones family; bestowal of patronage in consideration of\n   relationship, rather than of merit or of legal claim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nepotist', 'n.', 'One who practices nepotism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neptune', 'n.', 'The son of Saturn and Ops, the god of the waters,\n   especially of the sea. He is represented as bearing a trident for a\n   scepter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neptune', 'n.', 'The remotest known planet of our system, discovered -- as\n   a result of the computations of Leverrier, of Paris -- by Galle, of\n   Berlin, September 23, 1846. Its mean distance from the sun is about\n   2,775,000,000 miles, and its period of revolution is about 164,78\n   years.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neptunian', 'a.', 'Of or pertaining to the ocean or sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neptunian', 'a.', 'Formed by water or aqueous solution; as, Neptunian\n   rocks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neptunian', 'n.', 'Alt. of Neptunist');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neptunist', 'n.', 'One who adopts the neptunian theory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neptunicentric', 'a.', 'As seen from Neptune, or having Neptune as a\n   center; as, Neptunicentric longitude or force.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neptunium', 'n.', 'A new metallic element, of doubtful genuineness and\n   uncertain indentification, said to exist in certain minerals, as\n   columbite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ner', 'adv. & a.', 'nearer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nere', '', 'Were not.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nereids', 'pl. ', 'of Nereid');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nereides', 'pl. ', 'of Nereid');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nereid', 'n.', 'A sea nymph, one of the daughters of Nereus, who were\n   attendants upon Neptune, and were represented as riding on sea horses,\n   sometimes with the human form entire, and sometimes with the tail of a\n   fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nereid', 'n.', 'Any species of Nereis. The word is sometimes used for\n   similar annelids of other families.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nereidian', 'n.', 'Any annelid resembling Nereis, or of the family\n   Lycoridae or allied families.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nereides', 'pl. ', 'of Nereis');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nereis', 'n.', 'A Nereid. See Nereid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nereis', 'n.', 'A genus, including numerous species, of marine chaetopod\n   annelids, having a well-formed head, with two pairs of eyes, antennae,\n   four pairs of tentacles, and a protrusile pharynx, armed with a pair of\n   hooked jaws.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nereites', 'n. pl.', 'Fossil tracks of annelids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nereocystis', 'n.', 'A genus of gigantic seaweeds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nerfling', 'n.', 'The id.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nerita', 'n.', 'A genus of marine gastropods, mostly natives of warm\n   climates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nerite', 'n.', 'Any mollusk of the genus Nerita.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neritina', 'n.', 'A genus including numerous species of shells resembling\n   Nerita in form. They mostly inhabit brackish water, and are often\n   delicately tinted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nero', 'n.', 'A Roman emperor notorius for debauchery and barbarous\n   cruelty; hence, any profligate and cruel ruler or merciless tyrant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neroli', 'n.', 'An essential oil obtained by distillation from the flowers\n   of the orange. It has a strong odor, and is used in perfumery, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nerre', 'adv. & a.', 'Nearer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nervate', 'a.', 'Nerved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nervation', 'n.', 'The arrangement of nerves and veins, especially those of\n   leaves; neuration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nerve', 'n.', 'One of the whitish and elastic bundles of fibers, with the\n   accompanying tissues, which transmit nervous impulses between nerve\n   centers and various parts of the animal body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nerve', 'n.', 'A sinew or a tendon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nerve', 'n.', 'Physical force or steadiness; muscular power and control;\n   constitutional vigor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nerve', 'n.', 'Steadiness and firmness of mind; self-command in personal\n   danger, or under suffering; unshaken courage and endurance; coolness;\n   pluck; resolution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nerve', 'n.', 'Audacity; assurance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nerve', 'n.', 'One of the principal fibrovascular bundles or ribs of a\n   leaf, especially when these extend straight from the base or the midrib\n   of the leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nerve', 'n.', 'One of the nervures, or veins, in the wings of insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nerved', 'imp. & p. p.', 'of Nerve');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nerving', 'p. pr. & vb. n.', 'of Nerve');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nerve', 'v. t.', 'To give strength or vigor to; to supply with force; as,\n   fear nerved his arm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nerved', 'a.', 'Having nerves of a special character; as, weak-nerved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nerved', 'a.', 'Having nerves, or simple and parallel ribs or veins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nerveless', 'a.', 'Destitute of nerves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nerveless', 'a.', 'Destitute of strength or of courage; wanting vigor;\n   weak; powerless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nervelessness', 'n.', 'The state of being nerveless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nerve-shaken', 'a.', 'Affected by a tremor, or by a nervous disease;\n   weakened; overcome by some violent influence or sensation; shoked.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nervimotion', 'n.', 'The movement caused in the sensory organs by external\n   agents and transmitted to the muscles by the nerves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nervimotor', 'n.', 'Any agent capable of causing nervimotion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nervine', 'a.', 'Having the quality of acting upon or affecting the nerves;\n   quieting nervous excitement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nervine', 'n.', 'A nervine agent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nervomuscular', 'a.', 'Of or pertaining to both nerves and muscles; of the\n   nature of nerves and muscles; as, nervomuscular energy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nervose', 'a.', 'Same as Nerved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nervosity', 'n.', 'Nervousness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nervous', 'a.', 'possessing nerve; sinewy; strong; vigorous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nervous', 'a.', 'Possessing or manifesting vigor of mind; characterized by\n   strength in sentiment or style; forcible; spirited; as, a nervous\n   writer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nervous', 'a.', 'Of or pertaining to the nerves; seated in the nerves; as,\n   nervous excitement; a nervous fever.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nervous', 'a.', 'Having the nerves weak, diseased, or easily excited;\n   subject to, or suffering from, undue excitement of the nerves; easily\n   agitated or annoyed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nervous', 'a.', 'Sensitive; excitable; timid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nervously', 'adv.', 'In a nervous manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nervousness', 'n.', 'State or quality of being nervous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nervure', 'n.', 'One of the nerves of leaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nervure', 'n.', 'One of the chitinous supports, or veins, in the wings of\n   incests.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nervy', 'superl. -', 'Strong; sinewy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nescience', 'n.', 'Want of knowledge; ignorance; agnosticism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nese', 'n.', 'Nose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nesh', 'a.', 'Soft; tender; delicate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ness', 'n.', 'A promontory; a cape; a headland.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-ness', '', 'A suffix used to form abstract nouns expressive of quality or\n   state; as, goodness, greatness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nesslerize', 'v. t.', 'To treat or test, as a liquid, with a solution of\n   mercuric iodide in potassium iodide and potassium hydroxide, which is\n   called Nesslers solution or Nesslers test, and is used to detect the\n   presence of ammonia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nest', 'n.', 'The bed or receptacle prepared by a fowl for holding her eggs\n   and for hatching and rearing her young.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nest', 'n.', 'Hence: the place in which the eggs of other animals, as\n   insects, turtles, etc., are laid and hatched; a snug place in which\n   young animals are reared.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nest', 'n.', 'A snug, comfortable, or cozy residence or situation; a\n   retreat, or place of habitual resort; hence, those who occupy a nest,\n   frequent a haunt, or are associated in the same pursuit; as, a nest of\n   traitors; a nest of bugs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nest', 'n.', 'An aggregated mass of any ore or mineral, in an isolated\n   state, within a rock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nest', 'n.', 'A collection of boxes, cases, or the like, of graduated size,\n   each put within the one next larger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nest', 'n.', 'A compact group of pulleys, gears, springs, etc., working\n   together or collectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nest', 'v. i.', 'To build and occupy a nest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nest', 'v. t.', 'To put into a nest; to form a nest for.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nestfuls', 'pl. ', 'of Nestful');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nestful', 'n.', 'As much or many as will fill a nest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nestled', 'imp. & p. p.', 'of Nestle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nestling', 'p. pr. & vb. n.', 'of Nestle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nestle', 'v. i.', 'To make and occupy a nest; to nest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nestle', 'v. i.', 'To lie close and snug, as a bird in her nest; to cuddle\n   up; to settle, as in a nest; to harbor; to take shelter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nestle', 'v. i.', 'To move about in ones place, like a bird when shaping\n   the interior of her nest or a young bird getting close to the parent;\n   as, a child nestles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nestle', 'v. t.', 'To house, as in a nest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nestle', 'v. t.', 'To cherish, as a bird her young.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nestling', 'n.', 'A young bird which has not abandoned the nest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nestling', 'n.', 'A nest; a receptacle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nestling', 'a.', 'Newly hatched; being yet in the nest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nestor', 'n.', 'A genus of parrots with gray heads. of New Zeland and\n   papua, allied to the cockatoos. See Kaka.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nestorian', 'n.', 'An adherent of Nestorius, patriarch of Constantinople to\n   the fifth century, who has condemned as a heretic for maintaining that\n   the divine and the human natures were not merged into one nature in\n   Christ (who was God in man), and, hence, that it was improper to call\n   Mary the mother of Christ; also, one of the sect established by the\n   followers of Nestorius in Persia, india, and other Oriental countries,\n   and still in existence. opposed to Eutychian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nestorian', 'a.', 'Of or relating to the Nestorians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nestorian', 'a.', 'relating to, or resembling, Nestor, the aged warior and\n   counselor mentioned by Homer; hence, wise; experienced; aged; as,\n   Nestorian caution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nestorianism', 'n.', 'The doctrines of the nestorian Christians, or of\n   Nestorius.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ney', 'n.', 'A fabric of twine, thread, or the like, wrought or woven into\n   meshes, and used for catching fish, birds, butterflies, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ney', 'n.', 'Anything designed or fitted to entrap or catch; a snare; any\n   device for catching and holding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ney', 'n.', 'Anything wrought or woven in meshes; as, a net for the hair; a\n   mosquito net; a tennis net.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ney', 'n.', 'A figure made up of a large number of straight lines or\n   curves, which are connected at certain points and related to each other\n   by some specified law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Netted', 'imp. & p. p.', 'of Net');
  INSERT INTO words (word, word_type, description) VALUES  (    'Netting', 'p. pr. & vb. n.', 'of Net');
  INSERT INTO words (word, word_type, description) VALUES  (    'Net', 'v. t.', 'To make into a net; to make n the style of network; as, to\n   net silk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Net', 'v. t.', 'To take in a net; to capture by stratagem or wile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Net', 'v. t.', 'To inclose or cover with a net; as, to net a tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Net', 'v. i.', 'To form network or netting; to knit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Net', 'a.', 'Without spot; pure; shining.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Net', 'a.', 'Free from extraneous substances; pure; unadulterated; neat;\n   as, net wine, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Net', 'a.', 'Not including superfluous, incidental, or foreign matter, as\n   boxes, coverings, wraps, etc.; free from charges, deductions, etc; as,\n   net profit; net income; net weight, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Netted', 'imp. & p. p.', 'of Net');
  INSERT INTO words (word, word_type, description) VALUES  (    'Netting', 'p. pr. & vb. n.', 'of Net');
  INSERT INTO words (word, word_type, description) VALUES  (    'Net', 'v. t.', 'To produce or gain as clear profit; as, he netted a\n   thousand dollars by the operation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Netfish', 'n.', 'An astrophyton.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nether', 'a.', 'Situated down or below; lying beneath, or in the lower\n   part; having a lower position; belonging to the region below; lower;\n   under; -- opposed to upper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neithermore', 'a.', 'Lower, nether.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nethermost', 'a.', 'Lowest; as, the nethermost abyss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nethinim', 'n. pl.', 'Servants of the priests and Levites in the menial\n   services about the tabernacle and temple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Netify', 'v. t.', 'To render neat; to clean; to put in order.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Netting', 'n.', 'The act or process of making nets or network, or of\n   forming meshes, as for fancywork, fishing nets, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Netting', 'n.', 'A piece of network; any fabric, made of cords, threads,\n   wires, or the like, crossing one another with open spaces between.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Netting', 'n.', 'A network of ropes used for various purposes, as for\n   holding the hammocks when not in use, also for stowing sails, and for\n   hoisting from the gunwale to the rigging to hinder an enemy from\n   boarding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Netting', 'n.', 'Urine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nettle', 'n.', 'A plant of the genus Urtica, covered with minute sharp\n   hairs containing a poison that produces a stinging sensation. Urtica\n   gracitis is common in the Northern, and U. chamaedryoides in the\n   Southern, United States. the common European species, U. urens and U.\n   dioica, are also found in the Eastern united States. U. pilulifera is\n   the Roman nettle of England.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nettled', 'imp. & p. p.', 'of Nettle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nettling', 'p. pr. & vb. n.', 'of Nettle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nettle', 'v. t.', 'To fret or sting; to irritate or vex; to cause to\n   experience sensations of displeasure or uneasiness not amounting to\n   violent anger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nettlebird', 'n.', 'the European whitethroat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nettler', 'n.', 'One who nettles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nettles', 'n. pl.', 'The halves of yarns in the unlaid end of a rope\n   twisted for pointing or grafting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nettles', 'n. pl.', 'Small lines used to sling hammocks under the deck\n   beams.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nettles', 'n. pl.', 'Reef points.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nettling', 'n.', 'A process (resembling splicing) by which two ropes are\n   jointed end so as to form one rope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nettling', 'n.', 'The process of tying together the ends of yarns in pairs,\n   to prevent tangling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nettling', 'p. pr. & a.', 'Stinging; irritating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Netty', 'a.', 'Like a net, or network; netted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Net-veined', 'a.', 'Having veins, or nerves, reticulated or netted; as, a\n   net-veined wing or leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Network', 'n.', 'A fabric of threads, cords, or wires crossing each other\n   at certain intervals, and knotted or secured at the crossings, thus\n   leaving spaces or meshes between them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Network', 'n.', 'Any system of lines or channels interlacing or crossing\n   like the fabric of a net; as, a network of veins; a network of\n   railroads.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neurad', 'adv.', 'Toward the neural side; -- opposed to haemad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neural', 'a.', 'relating to the nerves or nervous system; taining to,\n   situated in the region of, or on the side with, the neural, or\n   cerebro-spinal, axis; -- opposed to hemal. As applied to vertebrates,\n   neural is the same as dorsal; as applied to invertebrates it is usually\n   the same as ventral. Cf. Hemal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neuralgia', 'n.', 'A disease, the chief symptom of which is a very acute\n   pain, exacerbating or intermitting, which follows the course of a\n   nervous branch, extends to its ramifications, and seems therefore to be\n   seated in the nerve. It seems to be independent of any structural\n   lesion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neuralgic', 'a.', 'Of or pertaining to, or having the character of,\n   neuralgia; as, a neuralgic headache.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neuralgy', 'n.', 'Neuralgia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neurapophysial', 'a.', 'Of or pertaining to a neurapophysis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neurapophyses', 'pl. ', 'of Neurapophysis');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neurapophysis', 'n.', 'One of the two lateral processes or elements which\n   form the neural arch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neurapophysis', 'n.', 'The dorsal process of the neural arch; neural spine;\n   spinous process.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neurasthenia', 'n.', 'A condition of nervous debility supposed to be\n   dependent upon impairment in the functions of the spinal cord.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neuration', 'n.', 'The arrangement or distribution of nerves, as in the\n   leaves of a plant or the wings of an insect; nervation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neuraxis', 'n.', 'See Axis cylinder, under Axis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neurenteric', 'a.', 'Of or pertaining to both the neuron and the enteron;\n   as, the neurenteric canal, which, in embroys of many vertebrates,\n   connects the medullary tube and the primitive intestine. See Illust. of\n   Ectoderm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neuridin', 'n.', 'a nontoxic base, C5H14N2, found in the putrescent matters\n   of flesh, fish, decaying cheese, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neurilemma', 'n.', 'The delicate outer sheath of a nerve fiber; the\n   primitive sheath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neurilemma', 'n.', 'The perineurium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neurility', 'n.', 'The special properties and functions of the nerves; that\n   capacity for transmitting a stimulus which belongs to nerves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neurine', 'n.', 'A poisonous organic base (a ptomaine) formed in the\n   decomposition of protagon with boiling baryta water, and in the\n   putrefraction of proteid matter. It was for a long time considered\n   identical with choline, a crystalline body originally obtained from\n   bile. Chemically, however, choline is oxyethyl-trimethyl-ammonium\n   hydroxide, while neurine is vinyl-trimethyl-ammonium hydroxide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neurism', 'n.', 'Nerve force. See Vital force, under Vital.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neuritis', 'n.', 'Inflammation of a nerve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neuro-', '', 'A combining denoting a nerve, of / pertaining to a nerve /\n   the nervous system.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neuro-central', 'a.', 'Between the neural arch and the centrum of a\n   vertebra; as, the neurocentral suture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neurochord', 'a.', 'Alt. of Neurochordal');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neurochordal', 'a.', 'See Neurocord.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neurocity', 'n.', 'Nerve force.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neurocoele', 'n.', 'The central canal and ventricles of the spinal cord and\n   brain; the myelencephalic cavity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neurocord', 'n.', 'A cordlike organ composed of elastic fibers situated\n   above the ventral nervous cord of annelids, like the earthworm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neuro-epidermal', 'a.', 'Pertaining to, or giving rise to, the central\n   nervous system and epiderms; as, the neuroepidermal, or epiblastic,\n   layer of the blastoderm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neuroglia', 'n.', 'The delicate connective tissue framework which supports\n   the nervous matter and blood vessels of the brain and spinal cord.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neurography', 'n.', 'A description of the nerves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neurokeratin', 'n.', 'A substance, resembling keratin, present in nerve\n   tissue, as in the sheath of the axis cylinder of medullated nerve\n   fibers. Like keratin it resists the action of most chemical agents, and\n   by decomposition with sulphuric acid yields leucin and tyrosin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neurological', 'a.', 'Of or pertaining to neurolgy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neurologist', 'n.', 'One who is versed in neurology; also, one skilled in\n   the treatment of nervous diseases.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neurology', 'n.', 'The branch of science which treats of the nervous\n   system.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neuroma', 'n.', 'A tumor developed on, or connected with, a nerve, esp. one\n   consisting of new-formed nerve fibers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neuromere', 'n.', 'A metameric segment of the cerebro-spinal nervous\n   system.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neuromuscular', 'a.', 'Nervomuscular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neura', 'pl. ', 'of Neuron');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neuron', 'n.', 'The brain and spinal cord; the cerebro-spinal axis;\n   myelencephalon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neuropathic', 'a.', 'Of or pertaining to neuropathy; of the nature of, or\n   suffering from, nervous disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neuropathy', 'n.', 'An affection of the nervous system or of a nerve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neuropod', 'n.', 'A neuropodous animal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neuropodium', 'n.', 'The ventral lobe or branch of a parapodium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neuropodous', 'a.', 'Having the limbs on, or directed toward, the neural\n   side, as in most invertebrates; -- opposed to haemapodous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neuropore', 'n.', 'An opening at either end of the embryonic neural canal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neuropter', 'n.', 'One of the Neuroptera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neuroptera', 'n. pl.', 'An order of hexapod insects having two pairs of\n   large, membranous, net-veined wings. The mouth organs are adapted for\n   chewing. They feed upon other insects, and undergo a complete\n   metamorphosis. The ant-lion, hellgamite, and lacewing fly are examples.\n   Formerly, the name was given to a much more extensive group, including\n   the true Neuroptera and the Pseudoneuroptera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neropteral', 'a.', 'Of or pertaining to the Neuroptera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neuropteran', 'n.', 'A neuropter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neuropteris', 'n.', 'An extensive genus of fossil ferns, of which species\n   have been found from the Devonian to the Triassic formation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neuropterous', 'a.', 'Neuropteral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neurosensiferous', 'a.', 'Pertaining to, or forming, both nerves and sense\n   organs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neurosis', 'n.', 'A functional nervous affection or disease, that is, a\n   disease of the nerves without any appreciable change of nerve\n   structure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neuroskeletal', 'a.', 'Of or pertaining to the neuroskeleton.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neuroskeleton', 'n.', 'The deep-seated parts of the vertebrate skeleton\n   which are relation with the nervous axis and locomation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neurospast', 'n.', 'A puppet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neurotic', 'a.', 'Of or pertaining to the nerves; seated in the nerves;\n   nervous; as, a neurotic disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neurotic', 'a.', 'Uself in disorders of, or affecting, the nerves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neurotic', 'n.', 'A disease seated in the nerves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neurotic', 'n.', 'Any toxic agent whose action is mainly directed to the\n   great nerve centers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neurotome', 'n.', 'An instrument for cutting or dissecting nerves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neurotome', 'n.', 'A neuromere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neurotomical', 'a.', 'Of or pertaining to neurotomy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neurotomist', 'n.', 'One who skilled in or practices neurotomy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neurotomy', 'n.', 'The dissection, or anatomy, of the nervous system.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neurotomy', 'n.', 'The division of a nerve, for the relief of neuralgia, or\n   for other purposes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neurula', 'n.', 'An embryo or certain invertebrates in the stage when the\n   primitive band is first developed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neuter', 'a.', 'Neither the one thing nor the other; on neither side;\n   impartial; neutral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neuter', 'a.', 'Having a form belonging more especially to words which are\n   not appellations of males or females; expressing or designating that\n   which is of neither sex; as, a neuter noun; a neuter termination; the\n   neuter gender.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neuter', 'a.', 'Intransitive; as, a neuter verb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neuter', 'a.', 'Having no generative organs, or imperfectly developed ones;\n   sexless. See Neuter, n., 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neuter', 'n.', 'A person who takes no part in a contest; one who is either\n   indifferent to a cause or forbears to interfere; a neutral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neuter', 'n.', 'A noun of the neuter gender; any one of those words which\n   have the terminations usually found in neuter words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neuter', 'n.', 'An intransitive verb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neuter', 'n.', 'An organism, either vegetable or animal, which at its\n   maturity has no generative organs, or but imperfectly developed ones,\n   as a plant without stamens or pistils, as the garden Hydrangea; esp.,\n   one of the imperfectly developed females of certain social insects, as\n   of the ant and the common honeybee, which perform the labors of the\n   community, and are called workers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neutral', 'a.', 'Not engaged on either side; not taking part with or\n   assisting either of two or more contending parties; neuter;\n   indifferent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neutral', 'a.', 'Neither good nor bad; of medium quality; middling; not\n   decided or pronounced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neutral', 'a.', 'Neuter. See Neuter, a., 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neutral', 'a.', 'Having neither acid nor basic properties; unable to turn\n   red litmus blue or blue litmus red; -- said of certain salts or other\n   compounds. Contrasted with acid, and alkaline.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neutral', 'n.', 'A person or a nation that takes no part in a contest\n   between others; one who is neutral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neutralist', 'n.', 'A neutral; one who professes or practices neutrality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neutrality', 'n.', 'The state or quality of being neutral; the condition of\n   being unengaged in contests between others; state of taking no part on\n   either side; indifference.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neutrality', 'n.', 'Indifference in quality; a state neither very good nor\n   bad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neutrality', 'n.', 'The quality or state of being neutral. See Neutral, a.,\n   4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neutrality', 'a.', 'The condition of a nation or government which refrains\n   from taking part, directly or indirectly, in a war between other\n   powers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neutrality', 'a.', 'Those who are neutral; a combination of neutral powers\n   or states.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neutralization', 'n.', 'The act or process of neutralizing, or the state of\n   being neutralized.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neutralization', 'n.', 'The act or process by which an acid and a base are\n   combined in such proportions that the resulting compound is neutral.\n   See Neutral, a., 4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neutralized', 'imp. & p. p.', 'of Neutralize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neutralizing', 'p. pr. & vb. n.', 'of Neutralize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neutralize', 'v. t.', 'To render neutral; to reduce to a state of\n   neutrality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neutralize', 'v. t.', 'To render inert or imperceptible the peculiar\n   affinities of, as a chemical substance; to destroy the effect of; as,\n   to neutralize an acid with a base.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neutralize', 'v. t.', 'To destroy the peculiar or opposite dispositions of;\n   to reduce to a state of indifference inefficience; to counteract; as,\n   to neutralize parties in government; to neutralize efforts, opposition,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neutralizer', 'n.', 'One who, or that which, neutralizes; that which\n   destroys, disguises, or renders inert the peculiar properties of a\n   body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neutrally', 'adv.', 'In a neutral manner; without taking part with either\n   side; indifferently.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neuvaines', 'n. pl.', 'Prayers offered up for nine successive days.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nevadite', 'n.', 'A grantitoid variety of rhyolite, common in Nevada.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neve', 'n.', 'The upper part of a glacier, above the limit or perpetual\n   snow. See Galcier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neven', 'v. t.', 'To name; to mention; to utter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Never', 'adv.', 'Not ever; not at any time; at no time, whether past,\n   present, or future.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Never', 'adv.', 'In no degree; not in the least; not.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nevermore', 'adv.', 'Never again; at no time hereafter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Neverthelater', 'adv. / conj.', 'Nevertheless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nevertheless', 'adv. / conj.', 'Not the less; notwithstanding; in spite of\n   that; yet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nevew', 'n.', 'Nephew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'New', 'superl.', 'Having existed, or having been made, but a short time;\n   having originated or occured lately; having recently come into\n   existence, or into ones possession; not early or long in being; of\n   late origin; recent; fresh; modern; -- opposed to old, as, a new coat;\n   a new house; a new book; a new fashion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'New', 'superl.', 'Not before seen or known, although existing before;\n   lately manifested; recently discovered; as, a new metal; a new planet;\n   new scenes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'New', 'superl.', 'Newly beginning or recurring; starting anew; now\n   commencing; different from has been; as, a new year; a new course or\n   direction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'New', 'superl.', 'As if lately begun or made; having the state or quality\n   of original freshness; also, changed for the better; renovated; unworn;\n   untried; unspent; as, rest and travel made him a new man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'New', 'superl.', 'Not of ancient extraction, or of a family of ancient\n   descent; not previously kniwn or famous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'New', 'superl.', 'Not habituated; not familiar; unaccustomed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'New', 'superl.', 'Fresh from anything; newly come.');
  INSERT INTO words (word, word_type, description) VALUES  (    'New', 'adv.', 'Newly; recently.');
  INSERT INTO words (word, word_type, description) VALUES  (    'New', 'v. t. & i.', 'To make new; to renew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Newborn', 'a.', 'Recently born.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Newcome', 'a.', 'Recently come.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Newcomer', 'n.', 'One who has lately come.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Newel', 'n.', 'A novelty; a new thing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Newel', 'n.', 'The upright post about which the steps of a circular\n   staircase wind; hence, in stairs having straight flights, the principal\n   post at the foot of a staircase, or the secondary ones at the landings.\n   See Hollow newel, under Hollow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Newfangle', 'a.', 'Eager for novelties; desirous of changing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Newfangle', 'v. t.', 'To change by introducing novelties.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Newfangled', 'a.', 'Newmade; formed with the affectation of novelty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Newfangled', 'a.', 'Disposed to change; inclined to novelties; given to new\n   theories or fashions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Newfangledness', 'n.', 'Affectation of, or fondness for, novelty; vain or\n   affected fashion or form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Newfangleness', 'n.', 'Newfangledness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Newfanglist', 'n.', 'One who is eager for novelties or desirous of change.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Newfangly', 'adv.', 'In a newfangled manner; with eagerness for novelty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Newfashioned', 'a.', 'Made in a new form, or lately come into fashion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Newfoundland', 'n.', 'An island on the coast of British North America,\n   famed for the fishing grounds in its vicinity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Newfoundland', 'n.', 'A Newfoundland dog.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Newing', 'v. t.', 'Yeast; barm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Newish', 'a.', 'Somewhat new; nearly new.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Newly', 'adv.', 'Lately; recently.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Newly', 'adv.', 'Anew; afresh; freshly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Newmarket', 'n.', 'A long, closely fitting cloak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'New-model', 'v. t.', 'To remodel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Newness', 'n.', 'The quality or state of being new; as, the newness of a\n   system; the newness of a scene; newness of life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'News', 'n', 'A report of recent occurences; information of something that\n   has lately taken place, or of something before unknown; fresh tindings;\n   recent intelligence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'News', 'n', 'Something strange or newly happened.');
  INSERT INTO words (word, word_type, description) VALUES  (    'News', 'n', 'A bearer of news; a courier; a newspaper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'News-book', 'n.', 'A newspaper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'newsboy', 'n.', 'A boy who distributes or sells newspaper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'News-letter', 'n.', 'A circular letter, written or printed for the purpose\n   of disseminating news. This was the name given to the earliest English\n   newspapers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Newsmen', 'pl. ', 'of Newsman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Newsman', 'n.', 'One who brings news.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Newsman', 'n.', 'A man who distributes or sells newspapers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Newsmonger', 'n.', 'One who deals in news; one who is active in hearing and\n   telling news.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Newspaper', 'n.', 'A sheet of paper printed and distributed, at stated\n   intervals, for conveying intelligence of passing events, advocating\n   opinions, etc.; a public print that circulates news, advertisements,\n   proceedings of legislative bodies, public announcements, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Newsroom', 'n.', 'A room where news is collected and disseminated, or\n   periodicals sold; a reading room supplied with newspapers, magazines,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'News-vnder', 'n.', 'A seller of newspapers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'News-writer', 'n.', 'One who gathered news for, and wrote, news-letters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Newsy', 'a.', 'Full of news; abounding in information as to current events.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Newt', 'n.', 'Any one of several species of small aquatic salamanders. The\n   common British species are the crested newt (Triton cristatus) and the\n   smooth newt (Lophinus punctatus). In America, Diemictylus viridescens\n   is one of the most abundant species.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Newtonian', 'a.', 'Of or pertaining to Sir Isaac Newton, or his\n   discoveries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Newtonian', 'n.', 'A follower of Newton.');
  INSERT INTO words (word, word_type, description) VALUES  (    'New-year', 'a.', 'Of or pertaining to, or suitable for, the commencement of\n   the year; as, New-year gifts or odes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'New Years Day', '', 'the first day of a calendar year; the first day of\n   January. Often colloquially abbreviated to New years or new year.');
  INSERT INTO words (word, word_type, description) VALUES  (    'New Zealand', '', 'A group of islands in the South Pacific Ocean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nexible', 'a.', 'That may be knit together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Next', 'superl.', 'Nearest in place; having no similar object intervening.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Next', 'superl.', 'Nearest in time; as, the next day or hour.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Next', 'superl.', 'Adjoining in a series; immediately preceding or\n   following in order.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Next', 'superl.', 'Nearest in degree, quality, rank, right, or relation;\n   as, the next heir was an infant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Next', 'adv.', 'In the time, place, or order nearest or immediately\n   suceeding; as, this man follows next.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nexus', 'n.', 'Connection; tie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nez Perces', '', 'A tribe of Indians, mostly inhabiting Idaho.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ngina', 'n.', 'The gorilla.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Niagara period', '', 'A subdivision or the American Upper Silurian system,\n   embracing the Medina, Clinton, and Niagara epoch. The rocks of the\n   Niagara epoch, mostly limestones, are extensively distributed, and at\n   Niagara Falls consist of about eighty feet of shale supporting a\n   greater thickness of limestone, which is gradually undermined by the\n   removal of the shale. See Chart of Geology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nias', 'n.', 'A young hawk; an eyas; hence, an unsophisticated person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nib', 'n.', 'A small and pointed thing or part; a point; a prong.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nib', 'n.', 'The bill or beak of a bird; the neb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nib', 'n.', 'The points of a pen; also, the pointed part of a pen; a short\n   pen adapted for insertion in a holder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nib', 'n.', 'One of the handles which project from a scythe snath; also,\n   [Prov. Eng.], the shaft of a wagon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nebbed', 'imp. & p. p.', 'of Nib');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nibbing', 'p. pr. & vb. n.', 'of Nib');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nib', 'v. t.', 'To furnish with a nib; to point; to mend the point of; as,\n   to nib a pen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nibbed', 'a.', 'Having a nib or point.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nibbled', 'imp. & p. p.', 'of Nibble');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nibbling', 'p. pr. & vb. n.', 'of Nibble');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nibble', 'v. t.', 'To bite by little at a time; to seize gently with the\n   mouth; to eat slowly or in small bits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nibble', 'v. t.', 'To bite upon something gently or cautiously; to eat a\n   little of a thing, as by taking small bits cautiously; as, fishes\n   nibble at the bait.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nibble', 'n.', 'A small or cautious bite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nibbler', 'n.', 'One who, or that which, nibbles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nibblingly', 'adv.', 'In a nibbling manner; cautiously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Niblick', 'n.', 'A kind of golf stick used to lift the ball out of holes,\n   ruts, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nicagua', 'n.', 'The laughing falcon. See under laughing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nicaragua wood', '', 'Brazil wood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Niccolite', 'n.', 'A mineral of a copper-red color and metallic luster; an\n   arsenide of nickel; -- called also coppernickel, kupfernickel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nice', 'superl.', 'Foolish; silly; simple; ignorant; also, weak;\n   effeminate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nice', 'superl.', 'Of trifling moment; nimportant; trivial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nice', 'superl.', 'Overscrupulous or exacting; hard to please or satisfy;\n   fastidious in small matters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nice', 'superl.', 'Delicate; refined; dainty; pure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nice', 'superl.', 'Apprehending slight differences or delicate\n   distinctions; distinguishing accurately or minutely; carefully\n   discriminating; as, a nice taste or judgment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nice', 'superl.', 'Done or made with careful labor; suited to excite\n   admiration on account of exactness; evidencing great skill; exact;\n   fine; finished; as, nice proportions, nice workmanship, a nice\n   application; exactly or fastidiously discriminated; requiring close\n   discrimination; as, a nice point of law, a nice distinction in\n   philosophy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nice', 'superl.', 'Pleasing; agreeable; gratifying; delightful; good; as, a\n   nice party; a nice excursion; a nice person; a nice day; a nice sauce,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nicely', 'adv.', 'In a nice manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nicene', 'a.', 'Of or pertaining to Nice, a town of Asia Minor, or to the\n   ecumenial council held there A. D. 325.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Niceness', 'n.', 'Quality or state of being nice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nicery', 'n.', 'Nicety.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Niceties', 'pl. ', 'of Nicety');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nicety', 'n.', 'The quality or state of being nice (in any of the senses of\n   that word.).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nicety', 'n.', 'Delicacy or exactness of perception; minuteness of\n   observation or of discrimination; precision.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nicety', 'n.', 'A delicate expression, act, mode of treatment, distinction,\n   or the like; a minute distinction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Niche', 'n.', 'A cavity, hollow, or recess, generally within the thickness\n   of a wall, for a statue, bust, or other erect ornament. hence, any\n   similar position, literal or figurative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Niched', 'a.', 'Placed in a niche.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nick', 'n.', 'An evil spirit of the waters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nick', 'n.', 'A notch cut into something');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nick', 'n.', 'A score for keeping an account; a reckoning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nick', 'n.', 'A notch cut crosswise in the shank of a type, to assist a\n   compositor in placing it properly in the stick, and in distribution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nick', 'n.', 'A broken or indented place in any edge or surface; nicks in\n   china.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nick', 'n.', 'A particular point or place considered as marked by a nick;\n   the exact point or critical moment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nicked', 'imp. & p. p.', 'of Nick');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nicking', 'p. pr. & vb. n.', 'of Nick');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nick', 'v. t.', 'To make a nick or nicks in; to notch; to keep count of or\n   upon by nicks; as, to nick a stick, tally, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nick', 'v. t.', 'To mar; to deface; to make ragged, as by cutting nicks or\n   notches in.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nick', 'v. t.', 'To suit or fit into, as by a correspondence of nicks; to\n   tally with.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nick', 'v. t.', 'To hit at, or in, the nick; to touch rightly; to strike at\n   the precise point or time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nick', 'v. t.', 'To make a cross cut or cuts on the under side of (the tail\n   of a horse, in order to make him carry ir higher).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nick', 'v. t.', 'To nickname; to style.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nickar nut', '', 'Alt. of Nickar tree');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nickar tree', '', 'Same as Nicker nut, Nicker tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nickel', 'n.', 'A bright silver-white metallic element. It is of the iron\n   group, and is hard, malleable, and ductile. It occurs combined with\n   sulphur in millerite, with arsenic in the mineral niccolite, and with\n   arsenic and sulphur in nickel glance. Symbol Ni. Atomic weight 58.6.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nickel', 'n.', 'A small coin made of or containing nickel; esp., a\n   five-cent piece.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nickelic', 'a.', 'Pertaining to, or containing, nickel; specifically,\n   designating compounds in which, as contrasted with the nickelous\n   compounds, the metal has a higher valence; as nickelic oxide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nickeliferous', 'a.', 'Containing nickel; as, nickelferous iron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nickeline', 'n.', 'An alloy of nickel, a variety of German silver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nickeline', 'n.', 'Niccolite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nickelous', 'a.', 'Of, pertaining to, or designating, those compounds of\n   nickel in which, as contrasted with the nickelic compounds, the metal\n   has a lower valence; as, nickelous oxide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nicker', 'v. t.', 'One of the night brawlers of London formerly noted for\n   breaking windows with half-pence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nicker', 'v. t.', 'The cutting lip which projects downward at the edge of a\n   boring bit and cuts a circular groove in the wood to limit the size of\n   the hole that is bored.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nicker nut', '', 'A rounded seed, rather smaller than a nutmeg, having a\n   hard smooth shell, and a yellowish or bluish color. The seeds grow in\n   the prickly pods of tropical, woody climbers of the genus Caesalpinia.\n   C. Bonduc has yellowish seeds; C. Bonducella, bluish gray.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nicker tree', '', 'The plant producing nicker nuts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nicking', 'v. t.', 'The cutting made by the hewer at the side of the face.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nicking', 'v. t.', 'Small coal produced in making the nicking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nickle', 'n.', 'The European woodpecker, or yaffle; -- called also nicker\n   pecker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nicknack', 'n.', 'See Knickknack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nicknackery', 'n.', 'See Knickknackery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nickname', 'n.', 'A name given in contempt, derision, or sportive\n   familiarity; a familiar or an opprobrious appellation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nicknamed', 'imp. & p. p.', 'of Nickname');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nicknaming', 'p. pr. & vb. n.', 'of Nickname');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nickname', 'v. t.', 'To give a nickname to; to call by a nickname.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nicolaitan', 'n.', 'One of certain corrupt persons in the early church at\n   Ephesus, who are censured in rev. ii. 6, 15.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nicotian', 'n.', 'Tobacco.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nicotian', 'a.', 'Pertaining to, or derived from, tobacco.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nicotiana', 'n.', 'A genus of American and Asiatic solanaceous herbs, with\n   viscid foliage and funnel-shaped blossoms. Several species yield\n   tobacco. See Tobacco.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nicotianine', 'n.', 'A white waxy substance having a hot, bitter taste,\n   extracted from tobacco leaves and called also tobacco camphor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nicotic', 'a.', 'Nicotinic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nicotidine', 'n.', 'A complex, oily, nitrogenous base, isomeric with\n   nicotine, and obtained by the reduction of certain derivatives of the\n   pyridine group.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nicotine', 'n.', 'An alkaloid which is the active principle of tobacco. It\n   is a colorless, transparent, oily liquid, having an acrid odor, and an\n   acrid burning taste. It is intensely poisonous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nicotinic', 'a.', 'Pertaining to, or derived from, nicotine; nicotic; --\n   used specifically to designate an acid related to pyridine, obtained by\n   the oxidation of nicotine, and called nicotinic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nictate', 'v. i.', 'To wink; to nictitate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nictation', 'n.', 'the act of winking; nictitation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nictitate', 'v. i.', 'To wink; to nictate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nictitation', 'n.', 'The act of winking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nidamental', 'a.', 'Of, pertaining to, or baring, eggs or egg capsules; as,\n   the nidament capsules of certain gastropods; nidamental glands. See\n   Illust. of Dibranchiata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nidary', 'n.', 'A collection of nests.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nide', 'n.', 'A nestful; a brood; as, a nide of pheasants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nidering', 'a.', 'Infamous; dastardly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nidgery', 'n.', 'A trifle; a piece of foolery.');
