  INSERT INTO words (word, word_type, description) VALUES  (    'nidi', 'pl. ', 'of Nidus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nidus', 'n.', 'A nest: a repository for the eggs of birds, insects, etc.; a\n   breeding place; esp., the place or substance where parasites or the\n   germs of a disease effect lodgment or are developed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Niece', 'n.', 'A relative, in general; especially, a descendant, whether\n   male or female; a granddaughter or a grandson.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Niece', 'n.', 'A daughter of ones brother or sister, or of ones\n   brother-in-law or sister-in-law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nief', 'n.', 'See Neif, the fist.');
