  INSERT INTO words (word, word_type, description) VALUES  (    'Niello', 'n.', 'The art, process, or method of decorating metal with\n   incised designs filled with the black alloy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Niello', 'n.', 'A piece of metal, or any other object, so decorated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Niello', 'n.', 'An impression on paper taken from an ancient incised\n   decoration or metal plate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nifle', 'n.', 'A trifle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Niggard', 'n.', 'A person meanly close and covetous; one who spends\n   grudgingly; a stingy, parsimonous fellow; a miser.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Niggard', 'a.', 'Like a niggard; meanly covetous or parsimonious;\n   niggardly; miserly; stingy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Niggard', 'v. t. & i.', 'To act the niggard toward; to be niggardly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Niggardise', 'n.', 'Niggardliness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Niggardish', 'a.', 'Somewhat niggard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Niggardliness', 'n.', 'The quality or state of being niggard; meanness in\n   giving or spending; parsimony; stinginess.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Niggardly', 'a.', 'Meanly covetous or avarcious in dealing with others;\n   stingy; niggard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Niggardly', 'adv.', 'In a niggard manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Niggardness', 'n.', 'Niggardliness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Niggardous', 'a.', 'Niggardly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Niggardship', 'n.', 'Niggardliness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Niggardy', 'n.', 'Niggardliness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'nigged', 'n.', 'Hammer-dressed; -- said of building stone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nigger', 'n.', 'A negro; -- in vulgar derision or depreciation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Niggish', 'a.', 'Niggardly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Niggled', 'imp. & p. p.', 'of Niggle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Niggling', 'p. pr. & vb. n.', 'of Niggle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Niggle', 'v. t.', 'To trifle with; to deceive; to mock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Niggle', 'v. t.', 'To trifle or play.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Niggle', 'v. t.', 'To act or walk mincingly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Niggle', 'v. t.', 'To fret and snarl about trifles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Niggler', 'n.', 'One who niggles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nigh', 'superl.', 'Not distant or remote in place or time; near.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nigh', 'superl.', 'Not remote in degree, kindred, circumstances, etc.;\n   closely allied; intimate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nigh', 'a.', 'In a situation near in place or time, or in the course of\n   events; near.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nigh', 'a.', 'Almost; nearly; as, he was nigh dead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nigh', 'v. t. & i.', 'To draw nigh (to); to approach; to come near.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nigh', 'prep.', 'Near to; not remote or distant from.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nighly', 'adv.', 'In a near relation in place, time, degree, etc.; within a\n   little; almost.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nighness', 'n.', 'The quality or state of being nigh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Night', 'n.', 'That part of the natural day when the sun is beneath the\n   horizon, or the time from sunset to sunrise; esp., the time between\n   dusk and dawn, when there is no light of the sun, but only moonlight,\n   starlight, or artificial light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Night', 'n.', 'Darkness; obscurity; concealment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Night', 'n.', 'Intellectual and moral darkness; ignorance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Night', 'n.', 'A state of affliction; adversity; as, a dreary night of\n   sorrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Night', 'n.', 'The period after the close of life; death.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Night', 'n.', 'A lifeless or unenlivened period, as when nature seems to\n   sleep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Night-blooming', 'a.', 'Blooming in the night.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nightcap', 'n.', 'A cap worn in bed to protect the head, or in undress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nightcap', 'n.', 'A potion of spirit drank at bedtime.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nightdress', 'n.', 'A nightgown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nighted', 'a.', 'Darkness; clouded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nighted', 'a.', 'Overtaken by night; belated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nightertale', 'n.', 'period of night; nighttime.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Night-eyed', 'a.', 'Capable of seeing at night; sharp-eyed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nightfall', 'n.', 'The close of the day.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Night-faring', 'a.', 'Going or traveling in the night.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nightgown', 'n.', 'A loose gown used for undress; also, a gown used for a\n   sleeping garnment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nightingale', 'n.', 'A small, plain, brown and gray European song bird\n   (Luscinia luscinia). It sings at night, and is celebrated for the\n   sweetness of its song.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nightingale', 'n.', 'A larger species (Lucinia philomela), of Eastern\n   Europe, having similar habits; the thrush nightingale. The name is also\n   applied to other allied species.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nightish', 'a.', 'Of or pertaining to night.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nightjar', 'n.', 'A goatsucker, esp. the European species. See Illust. of\n   Goatsucker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nightless', 'a.', 'Having no night.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nightlong', 'a.', 'Lasting all night.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nightly', 'a.', 'Of or pertaining to the night, or to every night;\n   happening or done by night, or every night; as, nightly shades; he kept\n   nightly vigils.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nightly', 'adv.', 'At night; every night.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nightmen', 'pl. ', 'of Nightman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nightman', 'n.', 'One whose business is emptying privies by night.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nightmare', 'n.', 'A fiend or incubus formerly supposed to cause trouble in\n   sleep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nightmare', 'n.', 'A condition in sleep usually caused by improper eating\n   or by digestive or nervous troubles, and characterized by a sense of\n   extreme uneasiness or discomfort (as of weight on the chest or stomach,\n   impossibility of motion or speech, etc.), or by frightful or oppressive\n   dreams, from which one wakes after extreme anxiety, in a troubled state\n   of mind; incubus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nightmare', 'n.', 'Hence, any overwhelming, oppressive, or stupefying\n   influence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nightshade', 'n.', 'A common name of many species of the genus Solanum,\n   given esp. to the Solanum nigrum, or black nightshade, a low, branching\n   weed with small white flowers and black berries reputed to be\n   poisonous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nightshirt', 'n.', 'A kind of nightgown for men.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nighttime', 'n.', 'The time from dusk to dawn; -- opposed to daytime.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nightward', 'a.', 'Approaching toward night.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nigraniline', 'n.', 'The complex, nitrogenous, organic base and dyestuff\n   called also aniline black.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nigrescent', 'a.', 'Growing black; changing to a black color; approaching\n   to blackness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nigrification', 'n.', 'The act or process of making black.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nigrine', 'n.', 'A ferruginous variety of rutile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nigritude', 'n.', 'Blackness; the state of being black.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nigromancie', 'n.', 'Necromancy.');
