  INSERT INTO words (word, word_type, description) VALUES  (    'Nigrosine', 'n.', 'A dark blue dyestuff, of the induline group; -- called\n   also azodiphenyl blue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nigua', 'n.', 'The chigoe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nihil', 'n.', 'Nothing.');
