  INSERT INTO words (word, word_type, description) VALUES  (    'Nihilism', 'n.', 'The doctrine that nothing can be known; scepticism as to\n   all knowledge and all reality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nihilism', 'n.', 'The theories and practices of the Nihilists.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nihilist', 'n.', 'One who advocates the doctrine of nihilism; one who\n   believes or teaches that nothing can be known, or asserted to exist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nihilist', 'n.', 'A member of a secret association (esp. in Russia), which\n   is devoted to the destruction of the present political, religious, and\n   social institutions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nihilistic', 'a.', 'Of, pertaining to, or characterized by, nihilism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nihility', 'n.', 'Nothingness; a state of being nothing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nil', 'v. t.', 'Will not.');
