  INSERT INTO words (word, word_type, description) VALUES  (    'Nile', 'n.', 'The great river of Egypt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nilgau', 'n.', 'see Nylghau.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nilled', 'imp. & p. p.', 'of Nill');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nilling', 'p. pr. & vb. n.', 'of Nill');
