  INSERT INTO words (word, word_type, description) VALUES  (    'Nimbly', 'adv.', 'In a nimble manner; with agility; with light, quick\n   motion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nimbose', 'a.', 'Cloudy; stormy; tempestuous.');
