  INSERT INTO words (word, word_type, description) VALUES  (    'Nimbuses', 'pl. ', 'of Nimbus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nimbus', 'n.', 'A circle, or disk, or any indication of radiant light\n   around the heads of divinities, saints, and sovereigns, upon medals,\n   pictures, etc.; a halo. See Aureola, and Glory, n., 5.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nimbus', 'n.', 'A rain cloud; one of the four principal varieties of\n   clouds. See Cloud.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nimiety', 'n.', 'State of being in excess.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nimious', 'a.', 'Excessive; extravagant; inordinate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nimmer', 'n.', 'A thief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nin', '', 'Not in.');
