  INSERT INTO words (word, word_type, description) VALUES  (    'Nine', 'a.', 'Eight and one more; one less than ten; as, nine miles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nine', 'n.', 'The number greater than eight by a unit; nine units or\n   objects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nine', 'n.', 'A symbol representing nine units, as 9 or ix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nine-bark', 'n.', 'A white-flowered rosaceous shrub (Neillia, / Spiraea,\n   opulifolia), common in the Northern United States. The bark separates\n   into many thin layers, whence the name.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nine-eyes', 'n.', 'The lamprey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ninefold', 'a.', 'Nine times repeated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nineholes', 'n. pl.', 'A game in which nine holes are made in the ground,\n   into which a ball is bowled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nine-killer', 'n.', 'The northern butcher bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ninepences', 'pl. ', 'of Ninepence');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ninepence', 'n.', 'An old English silver coin, worth nine pence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ninepence', 'n.', 'A New England name for the Spanish real, a coin formerly\n   current in the United States, as valued at twelve and a half cents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ninepins', 'n. pl.', 'A game played with nine pins, or pieces of wood, set\n   on end, at which a wooden ball is bowled to knock them down; bowling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ninescore', 'a.', 'Nine times twenty, or one hundred and eighty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ninescore', 'n.', 'The product of nine times twenty; ninescore units or\n   objects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nineteen', 'a.', 'Nine and ten; eighteen and one more; one less than\n   twenty; as, nineteen months.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nineteen', 'n.', 'The number greater than eighteen by a unit; the sum of\n   ten and nine; nineteen units or objects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nineteen', 'n.', 'A symbol for nineteen units, as 19 or xix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nineteenth', 'a.', 'Following the eighteenth and preceding the twentieth;\n   coming after eighteen others.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nineteenth', 'a.', 'Constituting or being one of nineteen equal parts into\n   which anything is divided.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nineteenth', 'n.', 'The quotient of a unit divided by nineteen; one of\n   nineteen equal parts of anything.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nineteenth', 'n.', 'The next in order after the eighteenth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nineteenth', 'n.', 'An interval of two octaves and a fifth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ninetieth', 'a.', 'Next in order after the eighty-ninth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ninetieth', 'a.', 'Constituting or being one of ninety equal parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ninetieth', 'n.', 'The quotient of a unit divided by ninety; one of ninety\n   equal parts of anything.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ninetieth', 'n.', 'The next in order after the eighty-ninth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ninety', 'a.', 'Nine times ten; eighty-nine and one more; as, ninety men.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nineties', 'pl. ', 'of Ninety');
