  INSERT INTO words (word, word_type, description) VALUES  (    'Ninety', 'n.', 'A symbol representing ninety units, as 90 or xc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ninnies', 'pl. ', 'of Ninny');
