  INSERT INTO words (word, word_type, description) VALUES  (    'Ninnyhammer', 'n.', 'A simpleton; a silly person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ninth', 'a.', 'Following the eight and preceding the tenth; coming after\n   eight others.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ninth', 'a.', 'Constituting or being one of nine equal parts into which\n   anything is divided.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ninth', 'n.', 'The quotient of one divided by nine; one of nine equal parts\n   of a thing; the next after the eighth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ninth', 'n.', 'An interval containing an octave and a second.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ninth', 'n.', 'A chord of the dominant seventh with the ninth added.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ninthly', 'adv.', 'In the ninth place.');
