  INSERT INTO words (word, word_type, description) VALUES  (    'Niobic', 'a.', 'Same as Columbic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Niobite', 'n.', 'Same as Columbite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Niobium', 'n.', 'A later name of columbium. See Columbium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Niopo', 'n.', 'A kind of snuff prepared by the natives of Venezuela from\n   the roasted seeds of a leguminous tree (Piptadenia peregrina), thence\n   called niopo tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nip', 'n.', 'A sip or small draught; esp., a draught of intoxicating\n   liquor; a dram.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nipped', 'imp. & p. p.', 'of Nip');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nipt', '', 'of Nip');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nipping', 'p. pr. & vb. n.', 'of Nip');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nip', 'v. t.', 'To catch and inclose or compress tightly between two\n   surfaces, or points which are brought together or closed; to pinch; to\n   close in upon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nip', 'v. t.', 'To remove by pinching, biting, or cutting with two meeting\n   edges of anything; to clip.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nip', 'v. t.', 'Hence: To blast, as by frost; to check the growth or vigor\n   of; to destroy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nip', 'v. t.', 'To vex or pain, as by nipping; hence, to taunt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nip', 'n.', 'A seizing or closing in upon; a pinching; as, in the northern\n   seas, the nip of masses of ice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nip', 'n.', 'A pinch with the nails or teeth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nip', 'n.', 'A small cut, or a cutting off the end.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nip', 'n.', 'A blast; a killing of the ends of plants by frost.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nip', 'n.', 'A biting sarcasm; a taunt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nip', 'n.', 'A short turn in a rope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nipper', 'n.', 'One who, or that which, nips.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nipper', 'n.', 'A fore tooth of a horse. The nippers are four in number.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nipper', 'n.', 'A satirist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nipper', 'n.', 'A pickpocket; a young or petty thief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nipper', 'n.', 'The cunner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nipper', 'n.', 'A European crab (Polybius Henslowii).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nipperkin', 'n.', 'A small cup.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nippers', 'n. pl.', 'Small pinchers for holding, breaking, or cutting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nippers', 'n. pl.', 'A device with fingers or jaws for seizing an object\n   and holding or conveying it; as, in a printing press, a clasp for\n   catching a sheet and conveying it to the form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nippers', 'n. pl.', 'A number of rope-yarns wound together, used to secure\n   a cable to the messenger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nipping', 'a.', 'Biting; pinching; painful; destructive; as, a nipping\n   frost; a nipping wind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nippingly', 'adv.', 'In a nipping manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nippitate', 'a.', 'Peculiary strong and good; -- said of ale or liquor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nippitato', 'n.', 'Strong liquor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nipple', 'n.', 'The protuberance through which milk is drawn from the\n   breast or mamma; the mammilla; a teat; a pap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nipple', 'n.', 'The orifice at which any animal liquid, as the oil from an\n   oil bag, is discharged.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nipple', 'n.', 'Any small projection or article in which there is an\n   orifice for discharging a fluid, or for other purposes; as, the nipple\n   of a nursing bottle; the nipple of a percussion lock, or that part on\n   which the cap is put and through which the fire passes to the charge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nipple', 'n.', 'A pipe fitting, consisting of a short piece of pipe,\n   usually provided with a screw thread at each end, for connecting two\n   other fittings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nipplewort', 'n.', 'A yellow-flowered composite herb (Lampsana communis),\n   formerly used as an external application to the nipples of women; --\n   called also dock-cress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nirvana', 'n.', 'In the Buddhist system of religion, the final emancipation\n   of the soul from transmigration, and consequently a beatific\n   enfrachisement from the evils of wordly existence, as by annihilation\n   or absorption into the divine. See Buddhism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nis', '', 'Is not.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nisan', 'n.', 'The first month of the jewish ecclesiastical year, formerly\n   answering nearly to the month of April, now to March, of the Christian\n   calendar. See Abib.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nyseys', 'pl. ', 'of Nisey');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nisey', 'n.', 'A simpleton.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nisi', 'conj.', 'Unless; if not.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Niste', '', 'Wist not; knew not.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nisus', 'n.', 'A striving; an effort; a conatus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nit', 'n.', 'The egg of a louse or other small insect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nitency', 'n.', 'Brightness; luster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nitency', 'n.', 'Endeavor; rffort; tendency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Niter', 'n.', 'Alt. of Nitre');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nitre', 'n.', 'A white crystalline semitransparent salt; potassium nitrate;\n   saltpeter. See Saltpeter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nitre', 'n.', 'Native sodium carbonate; natron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nithing', 'n.', 'See Niding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nitid', 'a.', 'Bright; lustrous; shining.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nitid', 'a.', 'Gay; spruce; fine; -- said of persons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nitranilic', 'a.', 'Of, pertaining to, or designating, a complex organic\n   acid produced as a white crystalline substance by the action of nitrous\n   acid on hydroquinone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nitraniline', 'n.', 'Any one of a series of nitro derivatives of aniline.\n   In general they are yellow crystalline substances.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nitrate', 'n.', 'A salt of nitric acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nitrated', 'a.', 'Combined, or impregnated, with nitric acid, or some of\n   its compounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nitrated', 'a.', 'Prepared with nitrate of silver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nitratine', 'n.', 'A mineral occurring in transparent crystals, usually of\n   a white, sometimes of a reddish gray, or lemon-yellow, color; native\n   sodium nitrate. It is used in making nitric acid and for manure. Called\n   also soda niter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nitre', 'n.', 'See Niter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nitriary', 'n.', 'An artificial bed of animal matter for the manufacture of\n   niter by nitrification. See Nitrification, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nitric', 'a.', 'Of, pertaining to, or containing, nitrogen; specifically,\n   designating any one of those compounds in which, as contrasted with\n   nitrous compounds, the element has a higher valence; as, nitric oxide;\n   nitric acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nitride', 'n.', 'A binary compound of nitrogen with a more metallic element\n   or radical; as, boric nitride.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nitriferous', 'a.', 'Bearing niter; yielding, or containing, niter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nitrification', 'n.', 'The act, process, or result of combining with\n   nitrogen or some of its compounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nitrification', 'n.', 'The act or process of oxidizing nitrogen or its\n   compounds so as to form nitrous or nitric acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nitrification', 'n.', 'A process of oxidation, in which nitrogenous\n   vegetable and animal matter in the presence of air, moisture, and some\n   basic substances, as lime or alkali carbonate, is converted into\n   nitrates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nitrifier', 'n.', 'An agent employed in nitrification.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nitrified', 'imp. & p. p.', 'of Nitrify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nitrifying', 'p. pr. & vb. n.', 'of Nitrify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nitrify', 'v. t.', 'To combine or impregnate with nitrogen; to convert, by\n   oxidation, into nitrous or nitric acid; to subject to, or produce by,\n   nitrification.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nitrile', 'n.', 'Any one of a series of cyanogen compounds; particularly,\n   one of those cyanides of alcohol radicals which, by boiling with acids\n   or alkalies, produce a carboxyl acid, with the elimination of the\n   nitrogen as ammonia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nitrite', 'n.', 'A salt of nitrous acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nitro-', '', 'A combining form or an adjective denoting the presence of\n   niter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nitro-', '', 'A combining form (used also adjectively) designating certain\n   compounds of nitrogen or of its acids, as nitrohydrochloric,\n   nitrocalcite; also, designating the group or radical NO2, or its\n   compounds, as nitrobenzene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nitrobenzene', 'n.', 'A yellow aromatic liquid (C6H5.NO2), produced by the\n   action of nitric acid on benzene, and called from its odor imitation\n   oil of bitter almonds, or essence of mirbane. It is used in perfumery,\n   and is manufactured in large quantities in the preparation of aniline.\n   Fornerly called also nitrobenzol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nitrobenzol', 'n.', 'Alt. of Nitrobenzole');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nitrobenzole', 'n.', 'See Nitrobenzene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nitrocalcite', 'n.', 'Nitrate of calcium, a substance having a grayish\n   white color, occuring in efforescences on old walls, and in limestone\n   caves, especially where there exists decaying animal matter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nitrocarbol', 'n.', 'See Nitromethane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nitrocellulose', 'n.', 'See Gun cotton, under Gun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nitro-chloroform', 'n.', 'Same as Chlorpicrin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nitroform', 'n.', 'A nitro derivative of methane, analogous to chloroform,\n   obtained as a colorless oily or crystalline substance, CH.(NO2)3, quite\n   explosive, and having well-defined acid properties.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nitrogelatin', 'n.', 'An explosive consisting of gun cotton and camphor\n   dissolved in nitroglycerin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nitrogen', 'n.', 'A colorless nonmetallic element, tasteless and odorless,\n   comprising four fifths of the atmosphere by volume. It is chemically\n   very inert in the free state, and as such is incapable of supporting\n   life (hence the name azote still used by French chemists); but it forms\n   many important compounds, as ammonia, nitric acid, the cyanides, etc,\n   and is a constituent of all organized living tissues, animal or\n   vegetable. Symbol N. Atomic weight 14. It was formerly regarded as a\n   permanent noncondensible gas, but was liquefied in 1877 by Cailletet of\n   Paris, and Pictet of Geneva.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nitrogenized', 'imp. & p. p.', 'of Nitrogenize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nitrogenizing', 'p. pr. & vb. n.', 'of Nitrogenize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nitrogenize', 'v. t.', 'To combine, or impregnate, with nitrogen or its\n   compounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nitrogenous', 'a.', 'Of, pertaining to, or resembling, nitrogen; as, a\n   nitrogenous principle; nitrogenous compounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nitroglycerin', 'n.', 'A liquid appearing like a heavy oil, colorless or\n   yellowish, and consisting of a mixture of several glycerin salts of\n   nitric acid, and hence more properly called glycerin nitrate. It is\n   made by the action of nitric acid on glycerin in the presence of\n   sulphuric acid. It is extremely unstable and terribly explosive. A very\n   dilute solution is used in medicine as a neurotic under the name of\n   glonion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nitrohydrochloric', 'a.', 'Of, pertaining to, or containing, nitric and\n   hydrochloric acids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nitrol', 'n.', 'Any one of a series of hydrocarbons containing the nitro\n   and the nitroso or isonitroso group united to the same carbon atom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nitroleum', 'n.', 'Nitroglycerin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nitrolic', 'a.', 'Of, derived from, or designating, a nitrol; as, a\n   nitrolic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nitromagnesite', 'n.', 'Nitrate of magnesium, a saline efflorescence\n   closely resembling nitrate of calcium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nitrometer', 'n.', 'An apparatus for determining the amount of nitrogen or\n   some of its compounds in any substance subjected to analysis; an\n   azotometer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nitromethane', 'n.', 'A nitro derivative of methane obtained as a mobile\n   liquid; -- called also nitrocarbol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nitromuriatic', 'a.', 'Of, pertaining to, or composed of, nitric acid and\n   muriatic acid; nitrohydrochloric. See Nitrohydrochloric.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nitrophnol', 'n.', 'Any one of a series of nitro derivatives of phenol.\n   They are yellow oily or crystalline substances and have well-defined\n   acid properties, as picric acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nitroprussic', 'a.', 'Pertaining to, derived from, or designating, a\n   complex acid called nitroprussic acid, obtained indirectly by the\n   action of nitric acid on potassium ferrocyanide (yellow prussiate), as\n   a red crystalline unstable substance. It forms salts called\n   nitroprussides, which give a rich purple color with alkaline subphides.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nitroprusside', 'n.', 'See Nitroprussic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nitroquinol', 'n.', 'A hypothetical nitro derivative of quinol or\n   hydroquinone, not known in the free state, but forming a well defined\n   series of derivatives.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Niteosaccharin', 'n.', 'An explosive nitro derivative of certain sugars,\n   analogous to nitroglycerin, gun cotton, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nitrosalicylic', 'a.', 'Of, pertaining to, or designating, a nitro\n   derivative of salicylic acid, called also anilic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nitrose', 'a.', 'See Nitrous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nitroso-', '', '(/ / /). (Chem.) A prefix (also used adjectively)\n   designating the group or radical NO, called the nitroso group, or its\n   compounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nitrosyl', 'n.', 'the radical NO, called also the nitroso group. The term\n   is sometimes loosely used to designate certain nitro compounds; as,\n   nitrosyl sulphuric acid. Used also adjectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nitrosylic', 'a.', 'Of, pertaining to, or containing, nitrosyl; as,\n   nitrosylic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nitrous', 'a.', 'Of, pertaining to, or containing, niter; of the quality of\n   niter, or resembling it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nitrous', 'a.', 'Of, pertaining to, or designating, any one of those\n   compounds in which nitrogen has a relatively lower valence as\n   contrasted with nitric compounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nitroxyl', 'n.', 'The group NO2, usually called the nitro group.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nitrum', 'n.', 'Niter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nitry', 'a.', 'Nitrous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nitryl', 'n.', 'A name sometimes given to the nitro group or radical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nitter', 'n.', 'The horselouse; an insect that deposits nits on horses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nittily', 'adv.', 'Lousily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nittings', 'n. pl.', 'The refuse of good ore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nitty', 'a.', 'Full of nits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nitty', 'a.', 'Shining; elegant; spruce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nival', 'a.', 'Abounding with snow; snowy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Niveous', 'a.', 'Snowy; resembling snow; partaking of the qualities of\n   snow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nivose', 'n.', 'The fourth month of the French republican calendar\n   [1792-1806]. It commenced December 21, and ended January 19. See\n   VendEmiaire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nix', 'fem.', 'One of a class of water spirits, commonly described as of a\n   mischievous disposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nixie', 'n.', 'See Nix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nizam', 'n.', 'The title of the native sovereigns of Hyderabad, in India,\n   since 1719.');
  INSERT INTO words (word, word_type, description) VALUES  (    'No', 'a.', 'Not any; not one; none.');
  INSERT INTO words (word, word_type, description) VALUES  (    'No', 'adv.', 'Nay; not; not at all; not in any respect or degree; -- a word\n   expressing negation, denial, or refusal. Before or after another\n   negative, no is emphatic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noes', 'pl. ', 'of No');
  INSERT INTO words (word, word_type, description) VALUES  (    'No', 'n.', 'A refusal by use of the wordd no; a denial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'No', 'n.', 'A negative vote; one who votes in the negative; as, to call for\n   the ayes and noes; the noes have it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noachian', 'a.', 'Of or pertaining to the patriarch Noah, or to his time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noah', 'n.', 'A patriarch of Biblical history, in the time of the Deluge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nob', 'n.', 'The head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nob', 'n.', 'A person in a superior position in life; a nobleman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nobbily', 'adv.', 'In a nobby manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nobbler', 'n.', 'A dram of spirits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nobby', 'a.', 'Stylish; modish; elegant; showy; aristocratic; fashionable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nobiliary', 'a.', 'Of or pertaining to the nobility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nobiliary', 'n.', 'A history of noble families.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nobilify', 'v. t.', 'To make noble; to nobiliate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nobilitate', 'v. t.', 'To make noble; to ennoble; to exalt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nobilitation', 'n.', 'The act of making noble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nobility', 'n.', 'The quality or state of being noble; superiority of mind\n   or of character; commanding excellence; eminence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nobility', 'n.', 'The state of being of high rank or noble birth; patrician\n   dignity; antiquity of family; distinction by rank, station, or title,\n   whether inherited or conferred.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nobility', 'n.', 'Those who are noble; the collictive body of nobles or\n   titled persons in a stste; the aristocratic and patrician class; the\n   peerage; as, the English nobility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noble', 'superl.', 'Possessing eminence, elevation, dignity, etc.; above\n   whatever is low, mean, degrading, or dishonorable; magnanimous; as, a\n   noble nature or action; a noble heart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noble', 'superl.', 'Grand; stately; magnificent; splendid; as, a noble\n   edifice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noble', 'superl.', 'Of exalted rank; of or pertaining to the nobility;\n   distinguished from the masses by birth, station, or title; highborn;\n   as, noble blood; a noble personage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noble', 'n.', 'A person of rank above a commoner; a nobleman; a peer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noble', 'n.', 'An English money of account, and, formerly, a gold coin, of\n   the value of 6 s. 8 d. sterling, or about $1.61.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noble', 'n.', 'A European fish; the lyrie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noble', 'v. t.', 'To make noble; to ennoble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noblemen', 'pl. ', 'of Nobleman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nobleman', 'n.', 'One of the nobility; a noble; a peer; one who enjoys rank\n   above a commoner, either by virtue of birth, by office, or by patent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noble-minded', 'a.', 'Having a noble mind; honorable; magnanimous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nobleness', 'n.', 'The quality or state of being noble; greatness; dignity;\n   magnanimity; elevation of mind, character, or station; nobility;\n   grandeur; stateliness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nobless', 'n.', 'Alt. of Noblesse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noblesse', 'n.', 'Dignity; greatness; noble birth or condition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noblesse', 'n.', 'The nobility; persons of noble rank collectively,\n   including males and females.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noblewomen', 'pl. ', 'of Noblewoman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noblewoman', 'n.', 'A female of noble rank; a peeress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nobley', 'n.', 'The body of nobles; the nobility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nobley', 'n.', 'Noble birth; nobility; dignity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nobly', 'adv.', 'Of noble extraction; as, nobly born or descended.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nobly', 'adv.', 'In a noble manner; with greatness of soul; heroically;\n   with magnanimity; as, a deed nobly done.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nobly', 'adv.', 'Splendidly; magnificently.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nobodies', 'pl. ', 'of Nobody');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nobody', 'n.', 'No person; no one; not anybody.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nobody', 'n.', 'A person of no influence or importance; an insignificant or\n   contemptible person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nocake', 'n.', 'Indian corn parched, and beaten to powder, -- used for food\n   by the Northern American Indians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nocent', 'a.', 'Doing hurt, or having a tendency to hurt; hurtful;\n   mischievous; noxious; as, nocent qualities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nocent', 'a.', 'Guilty; -- the opposite of innocent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nocent', 'n.', 'A criminal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nocently', 'adv.', 'Hurtfully; injuriosly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nocive', 'a.', 'Hurtful; injurious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nock', 'n.', 'A notch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nock', 'n.', 'The upper fore corner of a boom sail or of a trysail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nock', 'v. t.', 'To notch; to fit to the string, as an arrow; to string, as\n   a bow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noctambulation', 'n.', 'Somnambulism; walking in sleep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noctambulism', 'n.', 'Somnambulism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noctambulist', 'n.', 'A somnambulist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noctambulo', 'n.', 'A noctambulist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noctidial', 'a.', 'Comprising a night and a day; a noctidial day.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noctiferous', 'a.', 'Bringing night.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noctilionid', 'n.', 'A South American bat of the genus Noctilio, having\n   cheek pouches and large incisor teeth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'NoctilucAe', 'pl. ', 'of Noctiluca');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noctiluca', 'n.', 'That which shines at night; -- a fanciful name for\n   phosphorus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noctiluca', 'n.', 'A genus of marine flagellate Infusoria, remarkable for\n   their unusually large size and complex structure, as well as for their\n   phosphorescence. The brilliant diffuse phosphorescence of the sea is\n   often due to myriads of Noctilucae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noctilucin', 'n.', 'A fatlike substance in certain marine animals, to which\n   they owe their phosphorescent properties.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noctilucine', 'a.', 'Of or pertaining to Noctiluca.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noctilucous', 'a.', 'Shining in the night.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noctivagant', 'a.', 'Going about in the night; night-wandering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noctivagation', 'n.', 'A roving or going about in the night.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noctivagous', 'a.', 'Noctivagant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noctograph', 'n.', 'A kind of writing frame for the blind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noctograph', 'n.', 'An instrument or register which records the presence of\n   watchmen on their beats.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noctuary', 'n.', 'A record of what passes in the night; a nightly journal;\n   -- distinguished from diary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noctuid', 'n.', 'Any one of numerous moths of the family Noctuidae, or\n   Noctuaelitae, as the cutworm moths, and armyworm moths; -- so called\n   because they fly at night.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noctuid', 'a.', 'Of or pertaining to the noctuids, or family Noctuidae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noctule', 'n.', 'A large European bat (Vespertilio, / Noctulina,\n   altivolans).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nocturn', 'n.', 'An office of devotion, or act of religious service, by\n   night.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nocturn', 'n.', 'One of the portions into which the Psalter was divided,\n   each consisting of nine psalms, designed to be used at a night service.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nocturnal', 'a.', 'Of, pertaining to, done or occuring in, the night; as,\n   nocturnal darkness, cries, expedition, etc.; -- opposed to diurnal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nocturnal', 'a.', 'Having a habit of seeking food or moving about at night;\n   as, nocturnal birds and insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nocturnal', 'n.', 'An instrument formerly used for taking the altitude of\n   the stars, etc., at sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nocturnally', 'adv.', 'By night; nightly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nocturne', 'n.', 'A night piece, or serenade. The name is now used for a\n   certain graceful and expressive form of instrumental composition, as\n   the nocturne for orchestra in Mendelsohns \"Midsummer-Nights Dream\"\n   music.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nocument', 'n.', 'Harm; injury; detriment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nocuous', 'a.', 'Hurtful; noxious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nod', 'v. i.', 'To bend or incline the upper part, with a quick motion; as,\n   nodding plumes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nod', 'v. i.', 'To incline the head with a quick motion; to make a slight\n   bow; to make a motion of assent, of salutation, or of drowsiness, with\n   the head; as, to nod at one.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nod', 'v. i.', 'To be drowsy or dull; to be careless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nodded', 'imp. & p. p.', 'of Nod');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nodding', 'p. pr. & vb. n.', 'of Nod');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nod', 'v. t.', 'To incline or bend, as the head or top; to make a motion of\n   assent, of salutation, or of drowsiness with; as, to nod the head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nod', 'v. t.', 'To signify by a nod; as, to nod approbation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nod', 'v. t.', 'To cause to bend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nod', 'n.', 'A dropping or bending forward of the upper oart or top of\n   anything.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nod', 'n.', 'A quick or slight downward or forward motion of the head, in\n   assent, in familiar salutation, in drowsiness, or in giving a signal,\n   or a command.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nodal', 'a.', 'Of the nature of, or relating to, a node; as, a nodal point.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nodated', 'a.', 'Knotted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nodation', 'n.', 'Act of making a knot, or state of being knotted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nodder', 'n.', 'One who nods; a drowsy person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nodding', 'a.', 'Curved so that the apex hangs down; having the top bent\n   downward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noddle', 'n.', 'The head; -- used jocosely or contemptuously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noddle', 'n.', 'The back part of the head or neck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noddies', 'pl. ', 'of Noddy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noddy', 'n.', 'A simpleton; a fool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noddy', 'n.', 'Any tern of the genus Anous, as A. stolidus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noddy', 'n.', 'The arctic fulmar (Fulmarus glacialis). Sometimes also\n   applied to other sea birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noddy', 'n.', 'An old game at cards.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noddy', 'n.', 'A small two-wheeled one-horse vehicle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noddy', 'n.', 'An inverted pendulum consisting of a short vertical flat\n   spring which supports a rod having a bob at the top; -- used for\n   detecting and measuring slight horizontal vibrations of a body to which\n   it is attached.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Node', 'n.', 'A knot, a knob; a protuberance; a swelling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Node', 'n.', 'One of the two points where the orbit of a planet, or comet,\n   intersects the ecliptic, or the orbit of a satellite intersects the\n   plane of the orbit of its primary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Node', 'n.', 'The joint of a stem, or the part where a leaf or several\n   leaves are inserted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Node', 'n.', 'A hole in the gnomon of a dial, through which passes the ray\n   of light which marks the hour of the day, the parallels of the suns\n   declination, his place in the ecliptic, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Node', 'n.', 'The point at which a curve crosses itself, being a double\n   point of the curve. See Crunode, and Acnode.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Node', 'n.', 'The point at which the lines of a funicular machine meet from\n   different angular directions; -- called also knot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Node', 'n.', 'The knot, intrigue, or plot of a piece.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Node', 'n.', 'A hard concretion or incrustation which forms upon bones\n   attacked with rheumatism, gout, or syphilis; sometimes also, a swelling\n   in the neighborhood of a joint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Node', 'n.', 'One of the fixed points of a sonorous string, when it\n   vibrates by aliquot parts, and produces the harmonic tones; nodal line\n   or point.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Node', 'n.', 'A swelling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nodical', 'a.', 'Of or pertaining to the nodes; from a node to the same\n   node again; as, the nodical revolutions of the moon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nodosarine', 'a.', 'Resembling in form or structure a foraminiferous shell\n   of the genus Nodosaria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nodosarine', 'n.', 'A foraminifer of the genus Nodosaria or of an allied\n   genus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nodose', 'a.', 'Knotty; having numerous or conspicuous nodes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nodose', 'a.', 'Having nodes or prominences; having the alternate joints\n   enlarged, as the antennae of certain insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nodosity', 'n.', 'The quality of being knotty or nodose; resemblance to a\n   node or swelling; knottiness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nodosity', 'n.', 'A knot; a node.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nodosous', 'a.', 'Alt. of Nodous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nodous', 'a.', 'Nodose; knotty; knotted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nodular', 'a.', 'Of, pertaining to, or in the form of, a nodule or knot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nodule', 'n.', 'A rounded mass or irregular shape; a little knot or lump.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noduled', 'a.', 'Having little knots or lumps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nodulose', 'a.', 'Alt. of Nodulous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nodulous', 'a.', 'Having small nodes or knots; diminutively nodose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noel', 'n.', 'Same as Nowel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noematachograph', 'n.', 'An instrument for determining and registering the\n   duration of more or less complex operations of the mind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noematic', 'a.', 'Alt. of Noematical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noematical', 'a.', 'Of or pertaining to the understanding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noemics', 'n.', 'The science of the understanding; intellectual science.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noetian', 'n.', 'One of the followers of Noetus, who lived in the third\n   century. He denied the distinct personality of the Father, Son, and\n   Holy Ghost.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noetic', 'a.', 'Alt. of Noetical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noetical', 'a.', 'Of or pertaining to the intellect; intellectual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nof', '', 'Not of; nor of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nog', 'n.', 'A noggin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nog', 'n.', 'A kind of strong ale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nog', 'n.', 'A wooden block, of the size of a brick, built into a wall, as\n   a hold for the nails of woodwork.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nog', 'n.', 'One of the square logs of wood used in a pile to support the\n   roof of a mine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nog', 'n.', 'A treenail to fasten the shores.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nog', 'v. t.', 'To fill in, as between scantling, with brickwork.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nog', 'v. t.', 'To fasten, as shores, with treenails.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noggen', 'a.', 'Made of hemp; hence, hard; rough; harsh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noggin', 'n.', 'A small mug or cup.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noggin', 'n.', 'A measure equivalent to a gill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nogging', 'v. t.', 'Rough brick masonry used to fill in the interstices of\n   a wooden frame, in building.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noght', 'adv.', 'Not.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noiance', 'n.', 'Annoyance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noie', 'v. t.', 'To annoy. See Noy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noier', 'n.', 'An annoyer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noils', 'n. pl.', 'Waste and knots of wool removed by the comb; combings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noint', 'v. t.', 'To anoint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noious', 'a.', 'Annoying; troublesome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noise', 'n.', 'Sound of any kind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noise', 'n.', 'Especially, loud, confused, or senseless sound; clamor; din.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noise', 'n.', 'Loud or continuous talk; general talk or discussion; rumor;\n   report.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noise', 'n.', 'Music, in general; a concert; also, a company of musicians;\n   a band.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noise', 'v. i.', 'To sound; to make a noise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noised', 'imp. & p. p.', 'of Noise');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noising', 'p pr. & vb. n.', 'of Noise');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noise', 'v. t.', 'To spread by rumor or report.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noise', 'v. t.', 'To disturb with noise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noiseful', 'a.', 'Loud; clamorous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noiseless', 'a.', 'Making, or causing, no noise or bustle; without noise;\n   silent; as, the noiseless foot of time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noisette', 'n.', 'A hybrid rose produced in 1817, by a French gardener,\n   Noisette, of Charleston, South Carolina, from the China rose and the\n   musk rose. It has given rise to many fine varieties, as the Lamarque,\n   the Marechal (or Marshal) Niel, and the Cloth of gold. Most roses of\n   this class have clustered flowers and are of vigorous growth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noisily', 'adv.', 'In a noisy manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noisiness', 'n.', 'The state or quality of being noisy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noisome', 'a.', 'Noxious to health; hurtful; mischievous; unwholesome;\n   insalubrious; destructive; as, noisome effluvia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noisome', 'a.', 'Offensive to the smell or other senses; disgusting; fetid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noisy', 'superl.', 'Making a noise, esp. a loud sound; clamorous;\n   vociferous; turbulent; boisterous; as, the noisy crowd.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noisy', 'superl.', 'Full of noise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nolde', '', 'Would not.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nole', 'n.', 'The head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noli-me-tangere', 'n.', 'Any plant of a genus of herbs (Impatiens) having\n   capsules which, if touched when ripe, discharge their seeds. -- See\n   Impatiens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noli-me-tangere', 'n.', 'The squirting cucumber. See under Cucumber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noli-me-tangere', 'n.', 'A name formerly applied to several varieties of\n   ulcerous cutaneous diseases, but now restricted to Lupus exedens, an\n   ulcerative affection of the nose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nolition', 'n.', 'Adverse action of will; unwillingness; -- opposed to\n   volition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noll', 'n.', 'The head; the noddle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nolleity', 'n.', 'The state of being unwilling; nolition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nolle prosequi', '', 'Will not prosecute; -- an entry on the record,\n   denoting that a plaintiff discontinues his suit, or the attorney for\n   the public a prosecution; either wholly, or as to some count, or as to\n   some of several defendants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nolo contendere', '', 'A plea, by the defendant, in a criminal prosecution,\n   which, without admitting guilt, subjects him to all the consequences of\n   a plea of quilty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nol. pros.', '', 'An abbrev. of Nolle prosequi.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-prossed', 'imp. & p. p.', 'of Nol-pros');
  INSERT INTO words (word, word_type, description) VALUES  (    '-prossing', 'p. pr. & vb. n.', 'of Nol-pros');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nol-pros', 'v. t.', 'To discontinue by entering a nolle prosequi; to\n   decline to prosecute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nolt', 'n. sing. & pl.', 'Neat cattle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nom', 'n.', 'Name.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noma', 'n.', 'See Canker, n., 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nomad', 'n.', 'One of a race or tribe that has no fixed location, but\n   wanders from place to place in search of pasture or game.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nomad', 'a.', 'Roving; nomadic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nomade', 'n.', 'See Nomad, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nomadian', 'n.', 'A nomad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nomadic', 'a.', 'Of or pertaining to nomads, or their way of life;\n   wandering; moving from place to place for subsistence; as, a nomadic\n   tribe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nomadism', 'n.', 'The state of being a nomad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nomadized', 'imp. & p. p.', 'of Nomadize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nomadizing', 'p. pr. & vb. n.', 'of Nomadize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nomadize', 'v. i.', 'To lead the life of a nomad; to wander with flocks and\n   herds for the sake of finding pasturage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nomancy', 'n.', 'The art or practice of divining the destiny of persons by\n   the letters which form their names.');
  INSERT INTO words (word, word_type, description) VALUES  (    'No-mans land', '', 'A space amidships used to keep blocks, ropes, etc.; a\n   space on a ship belonging to no one in particular to care for.');
  INSERT INTO words (word, word_type, description) VALUES  (    'No-mans land', '', 'Fig.: An unclaimed space or time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nomarch', 'n.', 'The chief magistrate of a nome or nomarchy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nomarchies', 'pl. ', 'of Nomarchy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nomarchy', 'n.', 'A province or territorial division of a kingdom, under\n   the rule of a nomarch, as in modern Greece; a nome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nombles', 'n. pl.', 'The entrails of a deer; the umbles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nombril', 'n.', 'A point halfway between the fess point and the middle base\n   point of an escutcheon; -- called also navel point. See Escutcheon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nome', 'n.', 'A province or political division, as of modern Greece or\n   ancient Egypt; a nomarchy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nome', 'n.', 'Any melody determined by inviolable rules.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nome', 'n.', 'See Term.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nome', '', 'Alt. of Nomen');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nomen', '', 'p. p. of Nim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nomenclator', 'n.', 'One who calls persons or things by their names.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nomenclator', 'n.', 'One who gives names to things, or who settles and\n   adjusts the nomenclature of any art or science; also, a list or\n   vocabulary of technical names.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nomenclatress', 'n.', 'A female nomenclator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nomenclatural', 'a.', 'Pertaining or according to a nomenclature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nomenclature', 'n.', 'A name.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nomenclature', 'n.', 'A vocabulary, dictionary, or glossary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nomenclature', 'n.', 'The technical names used in any particular branch of\n   science or art, or by any school or individual; as, the nomenclature of\n   botany or of chemistry; the nomenclature of Lavoisier and his\n   associates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nomial', 'n.', 'A name or term.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nomic', 'a.', 'Customary; ordinary; -- applied to the usual English\n   spelling, in distinction from strictly phonetic methods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nomic', 'n.', 'Nomic spelling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nominal', 'a.', 'Of or pertaining to a name or names; having to do with the\n   literal meaning of a word; verbal; as, a nominal definition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nominal', 'a.', 'Existing in name only; not real; as, a nominal difference.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nominal', 'n.', 'A nominalist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nominal', 'n.', 'A verb formed from a noun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nominal', 'n.', 'A name; an appellation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nominalism', 'n.', 'The principles or philosophy of the Nominalists.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nominalist', 'n.', 'One of a sect of philosophers in the Middle Ages, who\n   adopted the opinion of Roscelin, that general conceptions, or\n   universals, exist in name only.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nominalistic', 'a.', 'Of or pertaining to the Nominalists.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nominalize', 'v. t.', 'To convert into a noun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nominally', 'adv.', 'In a nominal manner; by name; in name only; not in\n   reality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nominated', 'imp. & p. p.', 'of Nominate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nominating', 'p. pr. & vb. n.', 'of Nominate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nominate', 'v. t.', 'To mention by name; to name.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nominate', 'v. t.', 'To call; to entitle; to denominate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nominate', 'v. t.', 'To set down in express terms; to state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nominate', 'v. t.', 'To name, or designate by name, for an office or place;\n   to appoint; esp., to name as a candidate for an election, choice, or\n   appointment; to propose by name, or offer the name of, as a candidate\n   for an office or place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nominately', 'adv.', 'By name; particularly; namely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nomination', 'n.', 'The act of naming or nominating; designation of a\n   person as a candidate for office; the power of nominating; the state of\n   being nominated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nomination', 'n.', 'The denomination, or name.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nominatival', 'a.', 'Of or pertaining to the nominative case.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nominative', 'a.', 'Giving a name; naming; designating; -- said of that\n   case or form of a noun which stands as the subject of a finite verb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nominative', 'n.', 'The nominative case.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nominatively', 'adv.', 'In the manner of a nominative; as a nominative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nominator', 'n.', 'One who nominates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nominee', 'n.', 'A person named, or designated, by another, to any office,\n   duty, or position; one nominated, or proposed, by others for office or\n   for election to office.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nominor', 'n.', 'A nominator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nomocracy', 'n.', 'Government in accordance with a system of law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nomography', 'n.', 'A treatise on laws; an exposition of the form proper\n   for laws.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nomology', 'n.', 'The science of law; legislation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nomology', 'n.', 'The science of the laws of the mind; rational psychology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nomopelmous', 'a.', 'Having a separate and simple tendon to flex the first\n   toe, or hallux, as do passerine birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nomothete', 'n.', 'A lawgiver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nomothetic', 'a.', 'Alt. of Nomothetical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nomothetical', 'a.', 'Legislative; enacting laws; as, a nomothetical power.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Non', 'a.', 'No; not. See No, a.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Non-', '', 'A prefix used in the sense of not; un-; in-; as in\n   nonattention, or non-attention, nonconformity, nonmetallic, nonsuit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonability', 'n.', 'Want of ability.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonability', 'n.', 'An exception taken against a plaintiff in a cause, when\n   he is unable legally to commence a suit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonacceptance', 'n.', 'A neglect or refusal to accept.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonacid', 'a.', 'Destitute of acid properties; hence, basic; metallic;\n   positive; -- said of certain atoms and radicals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonacquaintance', 'n.', 'Want of acquaintance; the state of being\n   unacquainted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonacquiescence', 'n.', 'Refusal of acquiescence; failure to yield or\n   comply.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonadmission', 'n.', 'Failure to be admitted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonadult', 'a.', 'Not adult; immature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonaerobiotic', 'a.', 'Capable of living without atmospheric oxygen;\n   anaerobiotic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonage', 'n.', 'The ninth part of movable goods, formerly payable to the\n   clergy on the death of persons in their parishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonage', 'n.', 'Time of life before a person becomes of age; legal\n   immaturity; minority.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonaged', 'a.', 'Having the quality of nonage; being a minor; immature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonagenarian', 'n.', 'A person ninety years old.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonagesimal', 'a.', 'Of or pertaining to the ninetieth degree or to a\n   nonagesimal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonagesimal', 'n.', 'The middle or highest point of the part of the\n   ecliptic which is at any given moment above the horizon. It is the\n   ninetieth degree of the ecliptic, reckoned from the points in which it\n   is intersected by the horizon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonagon', 'n.', 'A figure or polygon having nine sides and nine angles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonagrian', 'n.', 'Any moth of the genus Nonagria and allied genera, as the\n   spindleworm and stalk borer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonalienation', 'n.', 'Failure to alienate; also, the state of not being\n   alienated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonane', 'n.', 'One of a group of metameric hydrocarbons C9H20 of the\n   paraffin series; -- so called because of the nine carbon atoms in the\n   molecule. Normal nonane is a colorless volatile liquid, an ingredient\n   of ordinary kerosene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonappearance', 'n.', 'Default of apperance, as in court, to prosecute or\n   defend; failure to appear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonappointment', 'n.', 'Neglect of making appointment; failure to receive\n   an appointment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonarrival', 'n.', 'Failure to arrive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Non assumpsit', '', 'The general plea or denial in an action of assumpsit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonattendance', 'n.', 'A failure to attend; omission of attendance;\n   nonappearance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonattention', 'n.', 'Inattention.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonbituminous', 'a.', 'Containing no bitumen; not bituminous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonce', 'n.', 'The one or single occasion; the present call or purpose; --\n   chiefly used in the phrase for the nonce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonchalance', 'n.', 'Indifference; carelessness; coolness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonchalant', 'a.', 'Indifferent; careless; cool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonchalantly', 'adv.', 'In a nonchalant, indifferent, or careless manner;\n   coolly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonclaim', 'n.', 'A failure to make claim within the time limited by law;\n   omission of claim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noncohesion', 'n.', 'Want of cohesion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noncoincidence', 'n.', 'Lack of coincidence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noncoincident', 'a.', 'Not coincident.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noncombatant', 'n.', 'Any person connected with an army, or within the\n   lines of an army, who does not make it his business to fight, as any\n   one of the medical officers and their assistants, chaplains, and\n   others; also, any of the citizens of a place occupied by an army; also,\n   any one holding a similar position with respect to the navy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noncommissioned', 'a.', 'Not having a commission.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noncommittal', 'n.', 'A state of not being committed or pledged;\n   forbearance or refusal to commit ones self. Also used adjectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noncommunion', 'n.', 'Neglect or failure of communion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noncompletion', 'n.', 'Lack of completion; failure to complete.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noncompliance', 'n.', 'Neglect of compliance; failure to comply.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noncomplying', 'a.', 'Neglecting or refusing to comply.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Non compos', '', 'Alt. of Non compos mentis');
  INSERT INTO words (word, word_type, description) VALUES  (    'Non compos mentis', '', 'Not of sound mind; not having the regular use of\n   reason; hence, also, as a noun, an idiot; a lunatic; one devoid of\n   reason, either by nature or from accident.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noncon.', 'n.', 'See Noncontent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonconcluding', 'a.', 'Not concluding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonconcur', 'v. i.', 'To dissent or refuse to concur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonconcurrence', 'n.', 'Refusal to concur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noncondensible', 'a.', 'Not condensible; incapable of being liquefied; --\n   said of gases.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noncondensing', 'a.', 'Not condensing; discharging the steam from the\n   cylinder at a pressure nearly equal to or above that of the atmosphere\n   and not into a condenser.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonconducting', 'a.', 'Not conducting; not transmitting a fluid or force;\n   thus, in electricity, wax is a nonconducting substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonconduction', 'n.', 'The quality of not being able to conduct or\n   transmit; failure to conduct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonconductor', 'n.', 'A substance which does not conduct, that is, convey\n   or transmit, heat, electricity, sound, vibration, or the like, or which\n   transmits them with difficulty; an insulator; as, wool is a\n   nonconductor of heat; glass and dry wood are nonconductors of\n   electricity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonconforming', 'a.', 'Not conforming; declining conformity; especially,\n   not conforming to the established church of a country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonconformist', 'n.', 'One who does not conform to an established church;\n   especially, one who does not conform to the established church of\n   England; a dissenter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonconformity', 'n.', 'Neglect or failure of conformity; especially, in\n   England, the neglect or refusal to unite with the established church in\n   its rites and modes of worship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonconstat', 'n.', 'It does not appear; it is not plain or clear; it does\n   not follow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noncontagious', 'a.', 'Not contagious; not catching; not communicable by\n   contact.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noncontent', 'n.', 'One who gives a negative vote; -- sometimes abridged\n   into noncon. or non con.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noncontributing', 'a.', 'Alt. of Noncontributory');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noncontributory', 'a.', 'Not contributing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonda', 'n.', 'The edible plumlike fruit of the Australian tree, Parinarium\n   Nonda.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nondecane', 'n.', 'A hydrocarbon of the paraffin series, a white waxy\n   substance, C19H40; -- so called from the number of carbon atoms in the\n   molecule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nondeciduate', 'a.', 'Characterized by the absence of a decidua;\n   indeciduate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nondelivery', 'n.', 'A neglect or failure of delivery; omission of\n   delivery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nondeposition', 'n.', 'A failure to deposit or throw down.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nondescript', 'a.', 'Not hitherto described; novel; hence, odd; abnormal;\n   unclassifiable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nondescript', 'n.', 'A thing not yet described; that of which no account or\n   explanation has been given; something abnormal, or hardly classifiable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nondevelopment', 'n.', 'Failure or lack of development.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nondiscovery', 'n.', 'Want or failure of discovery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nondo', 'n.', 'A coarse umbelliferous plant (Ligusticum actaeifolium) with\n   a large aromatic root. It is found chiefly in the Alleghany region.\n   Also called Angelico.');
  INSERT INTO words (word, word_type, description) VALUES  (    'None', 'a.', 'No one; not one; not anything; -- frequently used also\n   partitively, or as a plural, not any.');
  INSERT INTO words (word, word_type, description) VALUES  (    'None', 'a.', 'No; not any; -- used adjectively before a vowel, in old\n   style; as, thou shalt have none assurance of thy life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'None', 'n.', 'Same as Nones, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noneffective', 'a.', 'Not effective.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noneffective', 'a.', 'Not fit or available for duty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Non-ego', 'n.', 'The union of being and relation as distinguished from, and\n   contrasted with, the ego. See Ego.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonelastic', 'a.', 'Not having elasticity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonelect', 'n. sing. & pl.', 'A person or persons not elected, or chosen,\n   to salvation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonelection', 'n.', 'Failure of election.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonelectric', 'a.', 'Alt. of Nonelectrical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonelectrical', 'a.', 'Not electric; conducting electricity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonelectric', 'n.', 'A substance that is not an electric; that which\n   transmits electricity, as a metal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonemphatic', 'a.', 'Alt. of Nonemphatical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonemphatical', 'a.', 'Having no emphasis; unemphatic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonentities', 'pl. ', 'of Nonentity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonentity', 'n.', 'Nonexistence; the negation of being.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonentity', 'n.', 'A thing not existing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonentity', 'n.', 'A person or thing of little or no account.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Non-Episcopal', 'a.', 'Not Episcopal; not pertaining to the Episcopal\n   church or system.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nones', 'n. pl.', 'The fifth day of the months January, February, April,\n   June, August, September, November, and December, and the seventh day of\n   March, May, July, and October. The nones were nine days before the\n   ides, reckoning inclusively, according to the Roman method.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nones', 'n. pl.', 'The canonical office, being a part of the Breviary,\n   recited at noon (formerly at the ninth hour, 3 P. M.) in the Roman\n   Catholic Church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nones', 'n. pl.', 'The hour of dinner; the noonday meal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonessential', 'a.', 'Not essential.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonessential', 'n.', 'A thing not essential.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Non est factum', '', 'The plea of the general issue in an action of debt on\n   bond.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Non est inventus', '', 'The return of a sheriff on a writ, when the\n   defendant is not found in his county.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonesuch', 'n.', 'A person or thing of a sort that there is no other such;\n   something extraordinary; a thing that has not its equal. It is given as\n   a name to various objects, as to a choice variety of apple, a species\n   of medic (Medicago lupulina), a variety of pottery clay, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonet', 'n.', 'Alt. of Nonetto');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonetto', 'n.', 'A composition for nine instruments, rarely for nine\n   voices.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonett', 'n.', 'The titmouse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonexecution', 'n.', 'Neglect or failure of execution; nonperformance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonexistence', 'n.', 'Absence of existence; the negation of being;\n   nonentity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonexistence', 'n.', 'A thing that has no existence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonexistent', 'a.', 'Not having existence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonexportation', 'n.', 'A failure of exportation; a not exporting of\n   commodities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonextensile', 'a.', 'Not extensile; incapable of being stretched.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Non-feasance', 'n.', 'An omission or neglect to do something, esp. that\n   which ought to have been done. Cf. Malfeasance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonfulfillment', 'n.', 'Neglect or failure to fulfill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonillion', 'n.', 'According to the French and American notation, a\n   thousand octillions, or a unit with thirty ciphers annexed; according\n   to the English notation, a million octillions, or a unit with\n   fifty-four ciphers annexed. See the Note under Numeration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonimportation', 'n.', 'Want or failure of importation; a not importing of\n   commodities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonimporting', 'a.', 'Not importing; not bringing from foreign countries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noninflectional', 'a.', 'Not admitting of, or characterized by, inflection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noninhabitant', 'n.', 'One who is not an inhabitant; a stranger; a\n   foreigner; a nonresident.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonintervention', 'n.', 'The state or habit of not intervening or\n   interfering; as, the nonintervention of one state in the affairs of\n   another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonius', 'n.', 'A vernier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonjoinder', 'n.', 'The omission of some person who ought to have been made\n   a plaintiff or defendant in a suit, or of some cause of action which\n   ought to be joined.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonjurant', 'a.', 'Nonjuring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonjuring', 'a.', 'Not swearing allegiance; -- applied to the party in\n   Great Britain that would not swear allegiance to William and Mary, or\n   their successors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonjuror', 'n.', 'One of those adherents of James II. who refused to take\n   the oath of allegiance to William and Mary, or to their successors,\n   after the revolution of 1688; a Jacobite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonjurorism', 'n.', 'The doctrines, or action, of the Nonjurors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonlimitation', 'n.', 'Want of limitation; failure to limit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Non liquet', '', 'It is not clear; -- a verdict given by a jury when a\n   matter is to be deferred to another day of trial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonmalignant', 'a.', 'Not malignant, as a disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonmanufacturing', 'a.', 'Not carrying on manufactures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonmedullated', 'a.', 'Not medullated; (Anat.) without a medulla or marrow,\n   or without a medullary sheath; as, a nonmedullated nerve fiber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonmember', 'n.', 'One who is not a member.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonmembership', 'n.', 'State of not being a member.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonmetal', 'n.', 'Any one of the set of elements which, as contrasted with\n   the metals, possess, produce, or receive, acid rather than basic\n   properties; a metalloid; as, oxygen, sulphur, and chlorine are\n   nonmetals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonmetallic', 'a.', 'Not metallic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonmetallic', 'a.', 'Resembling, or possessing the properties of, a\n   nonmetal or metalloid; as, sulphur is a nonmetallic element.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonnatural', 'a.', 'Not natural; unnatural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonne', 'n.', 'A nun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonnecessity', 'n.', 'Absence of necessity; the quality or state of being\n   unnecessary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonnitrognous', 'a.', 'Devoid of nitrogen; as, a nonnitrogenous principle;\n   a nonnitrogenous food. See the Note under Food, n., 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonnucleated', 'a.', 'Without a nucleus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonny', 'n.', 'A silly fellow; a ninny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonobedience', 'n.', 'Neglect of obedience; failure to obey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonobservance', 'n.', 'Neglect or failure to observe or fulfill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Non obstante', '', 'Notwithstanding; in opposition to, or in spite of, what\n   has been stated, or is to be stated or admitted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Non obstante', '', 'A clause in old English statutes and letters patent,\n   importing a license from the crown to do a thing notwithstanding any\n   statute to the contrary. This dispensing power was abolished by the\n   Bill of Rights.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonoic', 'a.', 'Pertaining to, derived from, or resembling, nonane; as,\n   nonoic acid, which is also called pelargonic acid. Cf. Pelargonic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonone', 'n.', 'Any one of several metameric unsaturated hydrocarbons\n   (C9H14) of the valylene series.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonoxygenous', 'a.', 'Without oxygen; characterized by the absence of\n   oxygen; as, a nonoxygenous alkaloid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonpareil', 'a.', 'Something of unequaled excellence; a peerless thing or\n   person; a nonesuch; -- often used as a name.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonpareil', 'a.', 'A size of type next smaller than minion and next larger\n   than agate (or ruby).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonpareil', 'a.', 'A beautifully colored finch (Passerina ciris), native of\n   the Southern United States. The male has the head and neck deep blue,\n   rump and under parts bright red, back and wings golden green, and the\n   tail bluish purple. Called also painted finch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonpareil', 'a.', 'Any other similar bird of the same genus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonpareil', 'a.', 'Having no equal; peerless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonpayment', 'n.', 'Neglect or failure to pay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonperformance', 'n.', 'Neglect or failure to perform.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonphotobiotic', 'a.', 'Capable of living without light; as, nonphotobiotic\n   plant cells, or cells which habitually live in darkness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonplane', 'a.', 'Not lying in one plane; -- said of certain curves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonplus', 'n.', 'A state or condition which daffles reason or confounds\n   judgment; insuperable difficalty; inability to proceed or decide;\n   puzzle; quandary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonplused', 'imp. & p. p.', 'of Nonplus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonplussed', '', 'of Nonplus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonplusing', 'p. pr. & vb. n.', 'of Nonplus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonplussing', '', 'of Nonplus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonplus', 'v. t.', 'To puzzle; to confound; to perplex; to cause to stop by\n   embarrassment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonpreparation', 'n.', 'Neglect or failure to prepare; want of preparation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonpresentation', 'n.', 'Neglect or failure to present; state of not being\n   presented.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonproduction', 'n.', 'A failure to produce or exhibit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonprofessional', 'a.', 'Not belonging to a profession; not done by, or\n   proceeding from, professional men; contrary to professional usage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonproficiency', 'n.', 'Want of proficiency; failure to make progress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonproficient', 'n.', 'One who has failed to become proficient.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Non pros.', '', 'An abbreviation of Non prosequitur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonprossed', 'imp. & p. p.', 'of Non-pros');
  INSERT INTO words (word, word_type, description) VALUES  (    'Non-prossing', 'p. pr. & vb. n.', 'of Non-pros');
  INSERT INTO words (word, word_type, description) VALUES  (    'Non-pros', 'v. t.', 'To decline or fail to prosecute; to allow to be\n   dropped (said of a suit); to enter judgment against (a plaintiff who\n   fails to prosecute); as, the plaintiff was non-prossed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Non prosequitur', '', 'A judgment entered against the plaintiff in a suit\n   where he does not appear to prosecute. See Nolle prosequi.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonrecurrent', 'a.', 'Not recurring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonrecurring', 'a.', 'Nonrecurrent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonregardance', 'n.', 'Want of due regard; disregard; slight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonregent', 'n.', 'A master of arts whose regency has ceased. See Regent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonrendition', 'n.', 'Neglect of rendition; the not rendering what is due.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonresemblance', 'n.', 'Want of resemblance; unlikeness; dissimilarity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonresidence', 'n.', 'The state or condition of being nonresident,');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonresident', 'a.', 'Not residing in a particular place, on ones own\n   estate, or in ones proper place; as, a nonresident clergyman or\n   proprietor of lands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonresident', 'n.', 'A nonresident person; one who does not reside in the\n   State or jurisdiction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonresistance', 'n.', 'The principles or practice of a nonresistant;\n   passive obedience; submission to authority, power, oppression, or\n   violence without opposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonresistant', 'a.', 'Making no resistance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonresistant', 'n.', 'One who maintains that no resistance should be made\n   to constituted authority, even when unjustly or oppressively exercised;\n   one who advocates or practices absolute submission; also, one who holds\n   that violence should never be resisted by force.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonresisting', 'a.', 'Not making resistance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonruminant', 'a.', 'Not ruminating; as, a nonruminant animal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonsane', 'a.', 'Unsound; not perfect; as, a person of nonsane memory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonsense', 'n.', 'That which is not sense, or has no sense; words, or\n   language, which have no meaning, or which convey no intelligible ideas;\n   absurdity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonsense', 'n.', 'Trifles; things of no importance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonsensical', 'a.', 'Without sense; unmeaning; absurb; foolish; irrational;\n   preposterous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonsensitive', 'a.', 'Not sensitive; wanting sense or perception; not\n   easily affected.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Non sequitur', '', 'An inference which does not follow from the premises.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonsexual', 'a.', 'Having no distinction of sex; sexless; neuter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonslaveholding', 'a.', 'Not possessing or holding slaves; as, a\n   nonslaveholding State.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonsolution', 'n.', 'Failure of solution or explanation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonsolvency', 'n.', 'Inability to pay debts; insolvency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonsolvent', 'a.', 'Not solvent; insolvent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonsolvent', 'n.', 'An insolvent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonsonant', 'a.', 'Not sonant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonsonant', 'n.', 'A nonsonant or nonvocal consonant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonsparing', 'a.', 'Sparing none.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonstriated', 'a.', 'Without striations; unstriped; as, nonstriated muscle\n   fibers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonsubmission', 'n.', 'Want of submission; failure or refusal to submit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonsubmissive', 'a.', 'Not submissive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonsuch', 'n.', 'See Nonesuch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonsuit', 'n.', 'A neglect or failure by the plaintiff to follow up his\n   suit; a stopping of the suit; a renunciation or withdrawal of the cause\n   by the plaintiff, either because he is satisfied that he can not\n   support it, or upon the judges expressing his opinion. A compulsory\n   nonsuit is a nonsuit ordered by the court on the ground that the\n   plaintiff on his own showing has not made out his case.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonsuited', 'imp. & p. p.', 'of Nonsuit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonsuiting', 'p. pr. & vb. n.', 'of Nonsuit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonsuit', 'v. t.', 'To determine, adjudge, or record (a plaintiff) as\n   having dropped his suit, upon his withdrawal or failure to follow it\n   up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonsuit', 'a.', 'Nonsuited.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonsurety', 'n.', 'Insecurity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nontenure', 'n.', 'A plea of a defendant that he did not hold the land, as\n   affirmed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonterm', 'n.', 'A vacation between two terms of a court.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nontoxic', 'a.', 'Not toxic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nontronite', 'n.', 'A greenish yellow or green mineral, consisting chiefly\n   of the hydrous silicate of iron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonuniformist', 'n.', 'One who believes that past changes in the structure\n   of the earth have proceeded from cataclysms or causes more violent than\n   are now operating; -- called also nonuniformitarian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonunionist', 'n.', 'One who does not belong, or refuses to belong, to a\n   trades union.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonusance', 'n.', 'Neglect of using; failure to use.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonuser', '', 'A not using; failure to use.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonuser', '', 'Neglect or omission to use an easement or franchise or to\n   assert a right.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonvascular', 'a.', 'Destitute of vessels; extravascular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonvernacular', 'a.', 'Not vernacular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonvocal', 'a.', 'Not vocal; destitute of tone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonvocal', 'n.', 'A nonvocal consonant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonyl', 'n.', 'The hydrocarbon radical, C9H19, derived from nonane and\n   forming many compounds. Used also adjectively; as, nonyl alcohol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonylene', 'n.', 'Any one of a series of metameric, unsaturated\n   hydrocarbons C9H18 of the ethylene series.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonylenic', 'a.', 'Of, pertaining to, related to, or designating, nonylene\n   or its compounds; as, nonylenic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nonylic', 'a.', 'Of, pertaining to, or designating, nonyl or its compounds;\n   as, nonylic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noodle', 'n.', 'A simpleton; a blockhead; a stupid person; a ninny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noodle', 'n.', 'A thin strip of dough, made with eggs, rolled up, cut into\n   small pieces, and used in soup.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nook', 'n.', 'A narrow place formed by an angle in bodies or between\n   bodies; a corner; a recess; a secluded retreat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nook-shotten', 'a.', 'Full of nooks, angles, or corners.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noological', 'a.', 'Of or pertaining to noology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noologist', 'n.', 'One versed in noology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noology', 'n.', 'The science of intellectual phenomena.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noon', 'a.', 'No. See the Note under No.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noon', 'n.', 'The middle of the day; midday; the time when the sun is in\n   the meridian; twelve oclock in the daytime.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noon', 'n.', 'Hence, the highest point; culmination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noon', 'a.', 'Belonging to midday; occurring at midday; meridional.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noon', 'v. i.', 'To take rest and refreshment at noon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noonday', 'n.', 'Midday; twelve oclock in the day; noon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noonday', 'a.', 'Of or pertaining to midday; meridional; as, the noonday\n   heat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noon-flower', 'n.', 'The goats beard, whose flowers close at midday.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nooning', 'n.', 'A rest at noon; a repast at noon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noonshun', 'n.', 'See Nunchion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noonstead', 'n.', 'The position of the sun at noon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noontide', 'n.', 'The time of noon; midday.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noose', 'n.', 'A running knot, or loop, which binds the closer the more it\n   is drawn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noosed', 'imp. & p. p.', 'of Noose');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noosing', 'p. pr. & vb. n.', 'of Noose');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noose', 'v. t.', 'To tie in a noose; to catch in a noose; to entrap; to\n   insnare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noot', '', 'See lst Not.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nopal', 'n.', 'A cactaceous plant (Nopalea cochinellifera), originally\n   Mexican, on which the cochineal insect feeds, and from which it is\n   collected. The name is sometimes given to other species of Cactaceae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nopalries', 'pl. ', 'of Nopalry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nopalry', 'n.', 'A plantation of the nopal for raising the cochineal\n   insect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nope', 'n.', 'A bullfinch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nor', 'conj.', 'A negative connective or particle, introducing the second\n   member or clause of a negative proposition, following neither, or not,\n   in the first member or clause (as or in affirmative propositions\n   follows either). Nor is also used sometimes in the first member for\n   neither, and sometimes the neither is omitted and implied by the use of\n   nor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Norbertine', 'n.', 'See Premonstrant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noria', 'n.', 'A large water wheel, turned by the action of a stream\n   against its floats, and carrying at its circumference buckets, by which\n   water is raised and discharged into a trough; used in Arabia, China,\n   and elsewhere for irrigating land; a Persian wheel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Norian', 'a.', 'Pertaining to the upper portion of the Laurentian rocks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Norice', 'n.', 'Nurse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Norie', 'n.', 'The cormorant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Norimons', 'pl. ', 'of Norimon');
  INSERT INTO words (word, word_type, description) VALUES  (    'Norimon', 'n.', 'A Japanese covered litter, carried by men.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Norite', 'n.', 'A granular crystalline rock consisting essentially of a\n   triclinic feldspar (as labradorite) and hypersthene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Norium', 'n.', 'A supposed metal alleged to have been discovered in zircon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Norm', 'a.', 'A rule or authoritative standard; a model; a type.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Norm', 'a.', 'A typical, structural unit; a type.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Norma', 'n.', 'A norm; a principle or rule; a model; a standard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Norma', 'n.', 'A masons or a carpenters square or rule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Norma', 'n.', 'A templet or gauge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Normal', 'a.', 'According to an established norm, rule, or principle;\n   conformed to a type, standard, or regular form; performing the proper\n   functions; not abnormal; regular; natural; analogical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Normal', 'a.', 'According to a square or rule; perpendicular; forming a\n   right angle. Specifically: Of or pertaining to a normal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Normal', 'a.', 'Standard; original; exact; typical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Normal', 'a.', 'Denoting a solution of such strength that every cubic\n   centimeter contains the same number of milligrams of the element in\n   question as the number of its molecular weight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Normal', 'a.', 'Denoting certain hypothetical compounds, as acids from\n   which the real acids are obtained by dehydration; thus, normal\n   sulphuric acid and normal nitric acid are respectively S(OH)6, and\n   N(OH)5.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Normal', 'a.', 'Denoting that series of hydrocarbons in which no carbon\n   atom is united with more than two other carbon atoms; as, normal\n   pentane, hexane, etc. Cf. Iso-.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Normal', 'a.', 'Any perpendicular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Normal', 'a.', 'A straight line or plane drawn from any point of a curve or\n   surface so as to be perpendicular to the curve or surface at that\n   point.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Normalcy', 'n.', 'The quality, state, or fact of being normal; as, the\n   point of normalcy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Normalization', 'n.', 'Reduction to a standard or normal state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Normally', 'adv.', 'In a normal manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Norman', 'n.', 'A wooden bar, or iron pin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Norman', 'a.', 'Of or pertaining to Normandy or to the Normans; as, the\n   Norman language; the Norman conquest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Norman', 'n.', 'A native or inhabitant of Normandy; originally, one of the\n   Northmen or Scandinavians who conquered Normandy in the 10th century;\n   afterwards, one of the mixed (Norman-French) race which conquered\n   England, under William the Conqueror.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Normanism', 'n.', 'A Norman idiom; a custom or expression peculiar to the\n   Normans.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Norn', 'n.', 'Alt. of Norna');
  INSERT INTO words (word, word_type, description) VALUES  (    'Norna', 'n.', 'One of the three Fates, Past, Present, and Future. Their\n   names were Urd, Verdandi, and Skuld.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Norna', 'n.', 'A tutelary deity; a genius.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Noropianic', 'a.', 'Of, pertaining to, or designating, an acid of the\n   aromatic series obtained from opianic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Norroy', 'n.', 'The most northern of the English Kings-at-arms. See\n   King-at-arms, under King.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Norse', 'a.', 'Of or pertaining to ancient Scandinavia, or to the language\n   spoken by its inhabitants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Norse', 'n.', 'The Norse language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Norsemen', 'pl. ', 'of Norseman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Norseman', 'n.', 'One of the ancient Scandinavians; a Northman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nortelry', 'n.', 'Nurture; education; culture; bringing up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'North', 'n.', 'That one of the four cardinal points of the compass, at any\n   place, which lies in the direction of the true meridian, and to the\n   left hand of a person facing the east; the direction opposite to the\n   south.');
  INSERT INTO words (word, word_type, description) VALUES  (    'North', 'n.', 'Any country or region situated farther to the north than\n   another; the northern section of a country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'North', 'n.', 'Specifically: That part of the United States lying north of\n   Mason and Dixons line. See under Line.');
  INSERT INTO words (word, word_type, description) VALUES  (    'North', 'a.', 'Lying toward the north; situated at the north, or in a\n   northern direction from the point of observation or reckoning;\n   proceeding toward the north, or coming from the north.');
  INSERT INTO words (word, word_type, description) VALUES  (    'North', 'v. i.', 'To turn or move toward the north; to veer from the east\n   or west toward the north.');
  INSERT INTO words (word, word_type, description) VALUES  (    'North', 'adv.', 'Northward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Northeast', 'n.', 'The point between the north and east, at an equal\n   distance from each; the northeast part or region.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Northeast', 'a.', 'Of or pertaining to the northeast; proceeding toward the\n   northeast, or coming from that point; as, a northeast course; a\n   northeast wind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Northeast', 'adv.', 'Toward the northeast.');
