  INSERT INTO words (word, word_type, description) VALUES  (    'Nubian', 'n.', 'A native of Nubia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nubiferous', 'a.', 'Bringing, or producing, clouds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nubigenous', 'a.', 'Born of, or produced from, clouds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nubilate', 'v. t.', 'To cloud.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nubile', 'a.', 'Of an age suitable for marriage; marriageable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nubility', 'n.', 'The state of being marriageable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nubilose', 'a.', 'Alt. of Nubilous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nubilous', 'a.', 'Cloudy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nucament', 'n.', 'A catkin or ament; the flower cluster of the hazel, pine,\n   willow, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nucamentaceous', 'a.', 'Like a nut either in structure or in being\n   indehiscent; bearing one-seeded nutlike fruits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nucelli', 'pl. ', 'of Nucellus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nucellus', 'n.', 'See Nucleus, 3 (a).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nuch/', 'pl. ', 'of Nucha');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nucha', 'n.', 'The back or upper part of the neck; the nape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nuchal', 'a.', 'Of, pertaining to, or in the region of, the back, or nape,\n   of the neck; -- applied especially to the anterior median plate in the\n   carapace of turtles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nuciferous', 'a.', 'Bearing, or producing, nuts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nuciform', 'a.', 'Shaped like a nut; nut-shaped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nucin', 'n.', 'See Juglone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nucleal', 'a.', 'Alt. of Nuclear');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nuclear', 'a.', 'Of or pertaining to a nucleus; as, the nuclear spindle\n   (see Illust. of Karyokinesis) or the nuclear fibrils of a cell; the\n   nuclear part of a comet, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nucleate', 'a.', 'Having a nucleus; nucleated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nucleate', 'v. t.', 'To gather, as about a nucleus or center.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nucleated', 'a.', 'Having a nucleus; nucleate; as, nucleated cells.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nucleiform', 'a.', 'Formed like a nucleus or kernel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nuclein', 'n.', 'A constituent of the nuclei of all cells. It is a\n   colorless amorphous substance, readily soluble in alkaline fluids and\n   especially characterized by its comparatively large content of\n   phosphorus. It also contains nitrogen and sulphur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nucleobranch', 'a.', 'Belonging to the Nucleobranchiata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nucleobranch', 'n.', 'One of the Nucleobranchiata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nucleobranchiata', 'n. pl.', 'See Heteropoda.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nucleoidioplasma', 'n.', 'Hyaline plasma contained in the nucleus of\n   vegetable cells.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nucleolar', 'a.', 'Of or pertaining to the nucleolus of a cell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nucleolated', 'a.', 'Having a nucleole, or second inner nucleus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nucleole', 'n.', 'The nucleus within a nucleus; nucleolus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nucleoli', 'pl. ', 'of Nucleolus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nucleolus', 'n.', 'A little nucleus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nucleolus', 'n.', 'A small rounded body contained in the nucleus of a cell\n   or a protozoan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nucleoplasm', 'n.', 'The matter composing the nucleus of a cell; the\n   protoplasm of the nucleus; karyoplasma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nucleoplasmic', 'a.', 'Of or pertaining to nucleoplasm; -- esp. applied to\n   a body formed in the developing ovum from the plasma of the nucleus of\n   the germinal vesicle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nucleuses', 'pl. ', 'of Nucleus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nuclei', 'pl. ', 'of Nucleus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nucleus', 'n.', 'A kernel; hence, a central mass or point about which\n   matter is gathered, or to which accretion is made; the central or\n   material portion; -- used both literally and figuratively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nucleus', 'n.', 'The body or the head of a comet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nucleus', 'n.', 'An incipient ovule of soft cellular tissue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nucleus', 'n.', 'A whole seed, as contained within the seed coats.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nucleus', 'n.', 'A body, usually spheroidal, in a cell or a protozoan,\n   distinguished from the surrounding protoplasm by a difference in\n   refrangibility and in behavior towards chemical reagents. It is more or\n   less protoplasmic, and consists of a clear fluid (achromatin) through\n   which extends a network of fibers (chromatin) in which may be suspended\n   a second rounded body, the nucleolus (see Nucleoplasm). See Cell\n   division, under Division.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nucleus', 'n.', 'The tip, or earliest part, of a univalve or bivalve shell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nucleus', 'n.', 'The central part around which additional growths are\n   added, as of an operculum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nucleus', 'n.', 'A visceral mass, containing the stomach and other organs,\n   in Tunicata and some mollusks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nucula', 'n.', 'A genus of small marine bivalve shells, having a pearly\n   interior.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nucle', 'n.', 'Same as Nutlet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nucumentaceous', 'a.', 'See Nucamentaceous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nudation', 'n.', 'The act of stripping, or making bare or naked.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nuddle', 'v. i.', 'To walk quickly with the head bent forward; -- often\n   with along.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nude', 'a.', 'Bare; naked; unclothed; undraped; as, a nude statue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nude', 'a.', 'Naked; without consideration; void; as, a nude contract. See\n   Nudum pactum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nudge/', 'imp. & p. p.', 'of Nudge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nudging', 'p. pr. & vb. n.', 'of Nudge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nudge', 'v. t.', 'To touch gently, as with the elbow, in order to call\n   attention or convey intimation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nudge', 'n.', 'A gentle push, or jog, as with the elbow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nudibrachiate', 'a.', 'Having tentacles without vibratile cilia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nudibranch', 'a.', 'Of or pertaining to the Nudibranchiata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nudibranch', 'n.', 'One of the Nudibranchiata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nudibranchiata', 'n. pl.', 'A division of opisthobranchiate mollusks,\n   having no shell except while very young. The gills are naked and\n   situated upon the back or sides. See Ceratobranchia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nudibranchiate', 'a. & n.', 'Same as Nudibranch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nudicaul', 'a.', 'Having the stems leafless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nudification', 'n.', 'The act of making nude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nudities', 'pl. ', 'of Nudity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nudity', 'n.', 'The quality or state of being nude; nakedness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nudity', 'n.', 'That which is nude or naked; naked part; undraped or\n   unclothed portion; esp. (Fine Arts), the human figure represented\n   unclothed; any representation of nakedness; -- chiefly used in the\n   plural and in a bad sense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nudum pactum', '', 'A bare, naked contract, without any consideration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nugacity', 'n.', 'Futility; trifling talk or behavior; drollery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nugae', 'n. pl.', 'Trifles; jests.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nugation', 'n.', 'The act or practice of trifling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nugatory', 'a.', 'Trifling; vain; futile; insignificant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nugatory', 'a.', 'Of no force; inoperative; ineffectual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nugget', 'n.', 'A lump; a mass, esp. a native lump of a precious metal; as,\n   a nugget of gold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nugify', 'v. t.', 'To render trifling or futile; to make silly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nuisance', 'n.', 'That which annoys or gives trouble and vexation; that\n   which is offensive or noxious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nuisancer', 'n.', 'One who makes or causes a nuisance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nul', 'a.', 'No; not any; as, nul disseizin; nul tort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Null', 'a.', 'Of no legal or binding force or validity; of no efficacy;\n   invalid; void; nugatory; useless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Null', 'n.', 'Something that has no force or meaning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Null', 'n.', 'That which has no value; a cipher; zero.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Null', 'v. t.', 'To annul.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Null', 'n.', 'One of the beads in nulled work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nulled', 'a.', 'Turned so as to resemble nulls.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nullibiety', 'n.', 'The state or condition of being nowhere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nullification', 'n.', 'The act of nullifying; a rendering void and of no\n   effect, or of no legal effect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nullifidian', 'a.', 'Of no faith; also, not trusting to faith for\n   salvation; -- opposed to solifidian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nullifidian', 'n.', 'An unbeliever.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nullifier', 'n.', 'One who nullifies or makes void; one who maintains the\n   right to nullify a contract by one of the parties.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nullified', 'imp. & p. p.', 'of Nullify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nullifying', 'p. pr. & vb. n.', 'of Nullify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nullify', 'a.', 'To make void; to render invalid; to deprive of legal force\n   or efficacy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nullipore', 'n.', 'A name for certain crustaceous marine algae which\n   secrete carbonate of lime on their surface, and were formerly thought\n   to be of animal nature. They are now considered corallines of the\n   genera Melobesia and Lithothamnion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nullities', 'pl. ', 'of Nullity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nullity', 'n.', 'The quality or state of being null; nothingness; want of\n   efficacy or force.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nullity', 'n.', 'Nonexistence; as, a decree of nullity of marriage is a\n   decree that no legal marriage exists.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nullity', 'n.', 'That which is null.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Numb', 'a.', 'Enfeebled in, or destitute of, the power of sensation and\n   motion; rendered torpid; benumbed; insensible; as, the fingers or limbs\n   are numb with cold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Numb', 'a.', 'Producing numbness; benumbing; as, the numb, cold night.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Numbed', 'imp. & p. p.', 'of Numb');
  INSERT INTO words (word, word_type, description) VALUES  (    'Numbing', 'p. pr. & vb. n.', 'of Numb');
  INSERT INTO words (word, word_type, description) VALUES  (    'Numb', 'v. t.', 'To make numb; to deprive of the power of sensation or\n   motion; to render senseless or inert; to deaden; to benumb; to stupefy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Numbedness', 'n.', 'Numbness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Number', 'n.', 'That which admits of being counted or reckoned; a unit, or\n   an aggregate of units; a numerable aggregate or collection of\n   individuals; an assemblage made up of distinct things expressible by\n   figures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Number', 'n.', 'A collection of many individuals; a numerous assemblage; a\n   multitude; many.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Number', 'n.', 'A numeral; a word or character denoting a number; as, to\n   put a number on a door.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Number', 'n.', 'Numerousness; multitude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Number', 'n.', 'The state or quality of being numerable or countable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Number', 'n.', 'Quantity, regarded as made up of an aggregate of separate\n   things.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Number', 'n.', 'That which is regulated by count; poetic measure, as\n   divisions of time or number of syllables; hence, poetry, verse; --\n   chiefly used in the plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Number', 'n.', 'The distinction of objects, as one, or more than one (in\n   some languages, as one, or two, or more than two), expressed (usually)\n   by a difference in the form of a word; thus, the singular number and\n   the plural number are the names of the forms of a word indicating the\n   objects denoted or referred to by the word as one, or as more than one.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Number', 'n.', 'The measure of the relation between quantities or things of\n   the same kind; that abstract species of quantity which is capable of\n   being expressed by figures; numerical value.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Numbered', 'imp. & p. p.', 'of Number');
  INSERT INTO words (word, word_type, description) VALUES  (    'Numbering', 'p. pr & vb. n.', 'of Number');
  INSERT INTO words (word, word_type, description) VALUES  (    'Number', 'n.', 'To count; to reckon; to ascertain the units of; to\n   enumerate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Number', 'n.', 'To reckon as one of a collection or multitude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Number', 'n.', 'To give or apply a number or numbers to; to assign the\n   place of in a series by order of number; to designate the place of by a\n   number or numeral; as, to number the houses in a street, or the\n   apartments in a building.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Number', 'n.', 'To amount; to equal in number; to contain; to consist of;\n   as, the army numbers fifty thousand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Numberer', 'n.', 'One who numbers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Numberful', 'a.', 'Numerous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Numberless', 'a.', 'Innumerable; countless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Numberous', 'a.', 'Numerous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Numbers', 'n.', 'pl. of Number. The fourth book of the Pentateuch,\n   containing the census of the Hebrews.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Numbfish', 'n.', 'The torpedo, which numbs by the electric shocks which it\n   gives.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Numbless', 'n. pl.', 'See Nombles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Numbness', 'n.', 'The condition of being numb; that state of a living body\n   in which it loses, wholly or in part, the power of feeling or motion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Numerable', 'v. t.', 'Capable of being numbered or counted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Numeral', 'n.', 'Of or pertaining to number; consisting of number or\n   numerals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Numeral', 'n.', 'Expressing number; representing number; as, numeral\n   letters or characters, as X or 10 for ten.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Numeral', 'n.', 'A figure or character used to express a number; as, the\n   Arabic numerals, 1, 2, 3, etc.; the Roman numerals, I, V, X, L, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Numeral', 'n.', 'A word expressing a number.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Numerally', 'adv.', 'According to number; in number; numerically.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Numerary', 'a.', 'Belonging to a certain number; counting as one of a\n   collection or body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Numerated', 'imp. & p. p.', 'of Numerate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Numerating', 'p. pr. & vb. n.', 'of Numerate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Numerate', 'v.', 'To divide off and read according to the rules of\n   numeration; as, to numerate a row of figures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Numeration', 'n.', 'The act or art of numbering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Numeration', 'n.', 'The act or art of reading numbers when expressed by\n   means of numerals. The term is almost exclusively applied to the art of\n   reading numbers written in the scale of tens, by the Arabic method.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Numerative', 'a.', 'Of or pertaining to numeration; as, a numerative\n   system.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Numerator', 'n.', 'One who numbers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Numerator', 'n.', 'The term in a fraction which indicates the number of\n   fractional units that are taken.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Numeric', 'n.', 'Alt. of Numerical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Numerical', 'n.', 'Belonging to number; denoting number; consisting in\n   numbers; expressed by numbers, and not letters; as, numerical\n   characters; a numerical equation; a numerical statement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Numerical', 'n.', 'The same in number; hence, identically the same;\n   identical; as, the same numerical body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Numeric', 'n.', 'Any number, proper or improper fraction, or\n   incommensurable ratio. The term also includes any imaginary expression\n   like m + n-1, where m and n are real numerics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Numerically', 'adv.', 'In a numerical manner; in numbers; with respect to\n   number, or sameness in number; as, a thing is numerically the same, or\n   numerically different.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Numerist', 'n.', 'One who deals in numbers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Numero', 'n.', 'Number; -- often abbrev. No.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Numerosity', 'n.', 'The state of being numerous; numerousness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Numerosity', 'n.', 'Rhythm; harmony; flow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Numerous', 'a.', 'Consisting of a great number of units or individual\n   objects; being many; as, a numerous army.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Numerous', 'a.', 'Consisting of poetic numbers; rhythmical; measured and\n   counted; melodious; musical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Numidian', 'a.', 'Of or pertaining to ancient Numidia in Northern Africa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Numismatic', 'a.', 'Alt. of Numismatical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Numismatical', 'a.', 'Of or pertaining to coins; relating to the science of\n   coins or medals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Numismatics', 'n.', 'The science of coins and medals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Numismatist', 'n.', 'One skilled in numismatics; a numismatologist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Numismatography', 'n.', 'A treatise on, or description of, coins and\n   medals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Numismatologist', 'n.', 'One versed in numismatology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Numismatology', 'n.', 'The science which treats of coins and medals, in\n   their relation to history; numismatics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nummary', 'a.', 'Of or relating to coins or money.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nummular', 'a.', 'Alt. of Nummulary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nummulary', 'a.', 'Of or pertaining to coin or money; pecuniary; as, the\n   nummulary talent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nummulary', 'a.', 'Having the appearance or form of a coin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nummulation', 'n.', 'The arrangement of the red blood corpuscles in\n   rouleaux, like piles of coins, as when a drop of human blood is\n   examined under the microscope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nummulite', 'n.', 'A fossil of the genus Nummulites and allied genera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nummulites', 'n.', 'A genus of extinct Tertiary Foraminifera, having a\n   thin, flat, round shell, containing a large number of small chambers\n   arranged spirally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nummulitic', 'a.', 'Of, like, composed of, containing, nummulites; as,\n   nummulitic beds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Numps', 'n.', 'A dolt; a blockhead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Numskull', 'n.', 'A dunce; a dolt; a stupid fellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Numskulled', 'a.', 'Stupid; doltish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nun', 'n.', 'A woman devoted to a religious life, who lives in a convent,\n   under the three vows of poverty, chastity, and obedience.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nun', 'n.', 'A white variety of domestic pigeons having a veil of feathers\n   covering the head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nun', 'n.', 'The smew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nun', 'n.', 'The European blue titmouse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nunchion', 'v. i.', 'A portion of food taken at or after noon, usually\n   between full meals; a luncheon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nunciate', 'n.', 'One who announces; a messenger; a nuncio.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nunciature', 'n.', 'The office of a nuncio.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nuncios', 'pl. ', 'of Nuncio');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nuncio', 'n.', 'A messenger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nuncio', 'n.', 'The permanent official representative of the pope at a\n   foreign court or seat of government. Distinguished from a legate a\n   latere, whose mission is temporary in its nature, or for some special\n   purpose. Nuncios are of higher rank than internuncios.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nuncii', 'pl. ', 'of Nuncius');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nuncius', 'n.', 'A messenger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nuncius', 'n.', 'The information communicated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nuncupate', 'v. t.', 'To declare publicly or solemnly; to proclaim\n   formally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nuncupate', 'v. t.', 'To dedicate by declaration; to inscribe; as, to\n   nuncupate a book.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nuncupation', 'n.', 'The act of nuncupating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nuncupative', 'a.', 'Publicly or solemnly declaratory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nuncupative', 'a.', 'Nominal; existing only in name.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nuncupative', 'a.', 'Oral; not written.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nuncupatory', 'a.', 'Nuncupative; oral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nundinal', 'n.', 'A nundinal letter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nundinal', 'a.', 'Alt. of Nundinary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nundinary', 'a.', 'Of or pertaining to a fair, or to a market day.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nundinate', 'a.', 'To buy and sell at fairs or markets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nundination', 'n.', 'Traffic at fairs; marketing; buying and selling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nunnation', 'n.', 'The pronunciation of n at the end of words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nunneries', 'pl. ', 'of Nunnery');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nunnery', 'n.', 'A house in which nuns reside; a cloister or convent in\n   which women reside for life, under religious vows. See Cloister, and\n   Convent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nunnish', 'a.', 'Of, pertaining to, or resembling a nun; characteristic of\n   a nun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nup', 'n.', 'Same as Nupson.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nuphar', 'n.', 'A genus of plants found in the fresh-water ponds or lakes\n   of Europe, Asia, and North America; the yellow water lily. Cf.\n   Nymphaea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nupson', 'n.', 'A simpleton; a fool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nuptial', 'a.', 'Of or pertaining to marriage; done or used at a wedding;\n   as, nuptial rites and ceremonies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nuptials', 'pl. ', 'of Nuptial');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nuptial', 'n.', 'Marriage; wedding; nuptial ceremony; -- now only in the\n   plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nur', 'n.', 'A hard knot in wood; also, a hard knob of wood used by boys in\n   playing hockey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nurled', 'imp. & p. p.', 'of Nurl');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nurling', 'p. pr. & vb. n.', 'of Nurl');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nurl', 'v. t.', 'To cut with reeding or fluting on the edge of, as coins,\n   the heads of screws, etc.; to knurl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nurse', 'n.', 'One who nourishes; a person who supplies food, tends, or\n   brings up; as: (a) A woman who has the care of young children;\n   especially, one who suckles an infant not her own. (b) A person,\n   especially a woman, who has the care of the sick or infirm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nurse', 'n.', 'One who, or that which, brings up, rears, causes to grow,\n   trains, fosters, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nurse', 'n.', 'A lieutenant or first officer, who is the real commander\n   when the captain is unfit for his place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nurse', 'n.', 'A peculiar larva of certain trematodes which produces\n   cercariae by asexual reproduction. See Cercaria, and Redia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nurse', 'n.', 'Either one of the nurse sharks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nursed', 'imp. & p. p.', 'of Nurse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nursing', 'p. pr. & vb. n.', 'of Nurse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nurse', 'v. t.', 'To nourish; to cherish; to foster');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nurse', 'v. t.', 'To nourish at the breast; to suckle; to feed and tend, as\n   an infant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nurse', 'v. t.', 'To take care of or tend, as a sick person or an invalid;\n   to attend upon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nurse', 'v. t.', 'To bring up; to raise, by care, from a weak or invalid\n   condition; to foster; to cherish; -- applied to plants, animals, and to\n   any object that needs, or thrives by, attention.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nurse', 'v. t.', 'To manage with care and economy, with a view to increase;\n   as, to nurse our national resources.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nurse', 'v. t.', 'To caress; to fondle, as a nurse does.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nursehound', 'n.', 'See Houndfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nursemaid', 'n.', 'A girl employed to attend children.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nursepond', 'n.', 'A pond where fish are fed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nurser', 'n.', 'One who nurses; a nurse; one who cherishes or encourages\n   growth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nurseries', 'pl. ', 'of Nursery');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nursery', 'n.', 'The act of nursing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nursery', 'n.', 'The place where nursing is carried on');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nursery', 'n.', 'The place, or apartment, in a house, appropriated to the\n   care of children.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nursery', 'n.', 'A place where young trees, shrubs, vines, etc., are\n   propagated for the purpose of transplanting; a plantation of young\n   trees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nursery', 'n.', 'The place where anything is fostered and growth promoted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nursery', 'n.', 'That which forms and educates; as, commerce is the nursery\n   of seamen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nursery', 'n.', 'That which is nursed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nurserymen', 'pl. ', 'of Nurseryman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nurseryman', 'n.', 'One who cultivates or keeps a nursery, or place for\n   rearing trees, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nursing', 'a.', 'Supplying or taking nourishment from, or as from, the\n   breast; as, a nursing mother; a nursing infant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nursling', 'n.', 'One who, or that which, is nursed; an infant; a fondling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nurstle', 'v. t.', 'To nurse. See Noursle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nurture', 'n.', 'The act of nourishing or nursing; thender care; education;\n   training.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nurture', 'n.', 'That which nourishes; food; diet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nurtured', 'imp. & p. p.', 'of Nurture');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nurturing', 'p. pr. & vb. n.', 'of Nurture');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nurture', 'v. t.', 'To feed; to nourish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nurture', 'v. t.', 'To educate; to bring or train up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nustle', 'v. t.', 'To fondle; to cherish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nut', 'n.', 'The fruit of certain trees and shrubs (as of the almond,\n   walnut, hickory, beech, filbert, etc.), consisting of a hard and\n   indehiscent shell inclosing a kernel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nut', 'n.', 'A perforated block (usually a small piece of metal), provided\n   with an internal or female screw thread, used on a bolt, or screw, for\n   tightening or holding something, or for transmitting motion. See\n   Illust. of lst Bolt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nut', 'n.', 'The tumbler of a gunlock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nut', 'n.', 'A projection on each side of the shank of an anchor, to secure\n   the stock in place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nutted', 'imp. & p. p.', 'of Nut');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nutting', 'p. pr. & vb. n.', 'of Nut');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nut', 'v. i.', 'To gather nuts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nutant', 'a.', 'Nodding; having the top bent downward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nutation', 'n.', 'The act of nodding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nutation', 'n.', 'A very small libratory motion of the earths axis, by\n   which its inclination to the plane of the ecliptic is constantly\n   varying by a small amount.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nutation', 'n.', 'The motion of a flower in following the apparent movement\n   of the sun, from the east in the morning to the west in the evening.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nutation', 'n.', 'Circumnutation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nutbreaker', 'n.', 'The European nuthatch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nutbreaker', 'n.', 'The nutcracker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nut-brown', 'a.', 'Brown as a nut long kept and dried.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nutcracker', 'n.', 'An instrument for cracking nuts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nutcracker', 'n.', 'A European bird (Nucifraga caryocatactes), allied to\n   the magpie and crow. Its color is dark brown, spotted with white. It\n   feeds on nuts, seeds, and insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nutcracker', 'n.', 'The American, or Clarkes, nutcracker (Picicorvus\n   Columbianus) of Western North America.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nutgall', 'n.', 'A more or less round gall resembling a nut, esp. one of\n   those produced on the oak and used in the arts. See Gall, Gallnut.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nuthatch', 'n.', 'Any one of several species of birds of the genus Sitta,\n   as the European species (Sitta Europaea). The white-breasted nuthatch\n   (S. Carolinensis), the red-breasted nuthatch (S. Canadensis), the pygmy\n   nuthatch (S. pygmaea), and others, are American.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nuthook', 'n.', 'A hook at the end of a pole to pull down boughs for\n   gathering the nuts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nuthook', 'n.', 'A thief who steals by means of a hook; also, a bailiff who\n   hooks or seizes malefactors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nutjobber', 'n.', 'The nuthatch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nutlet', 'n.', 'A small nut; also, the stone of a drupe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nutmeg', 'n.', 'The kernel of the fruit of the nutmeg tree (Myristica\n   fragrans), a native of the Molucca Islands, but cultivated elsewhere in\n   the tropics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nutmegged', 'a.', 'Seasoned with nutmeg.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nutpecker', 'n.', 'The nuthatch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nutria', 'n.', 'The fur of the coypu. See Coypu.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nutrication', 'n.', 'The act or manner of feeding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nutrient', 'a.', 'Nutritious; nourishing; promoting growth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nutrient', 'n.', 'Any substance which has nutritious qualities, i. e.,\n   which nourishes or promotes growth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nutriment', 'n.', 'That which nourishes; anything which promotes growth and\n   repairs the natural waste of animal or vegetable life; food; aliment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nutriment', 'n.', 'That which promotes development or growth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nutrimental', 'a.', 'Nutritious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nutritial', 'a.', 'Pertaining to, or connected with, nutrition; nutritious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nutrition', 'n.', 'In the broadest sense, a process or series of processes\n   by which the living organism as a whole (or its component parts or\n   organs) is maintained in its normal condition of life and growth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nutrition', 'n.', 'In a more limited sense, the process by which the living\n   tissues take up, from the blood, matters necessary either for their\n   repair or for the performance of their healthy functions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nutrition', 'n.', 'That which nourishes; nutriment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nutritional', 'a.', 'Of or pertaining to nutrition; as, nutritional\n   changes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nutritious', 'a.', 'Nourishing; promoting growth, or preventing decay;\n   alimental.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nutritive', 'a.', 'Of or pertaining to nutrition; as, the nutritive\n   functions; having the quality of nourishing; nutritious; nutrimental;\n   alimental; as, nutritive food or berries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nutriture', 'n.', 'Nutrition; nourishment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nutshell', 'n.', 'The shell or hard external covering in which the kernel\n   of a nut is inclosed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nutshell', 'n.', 'Hence, a thing of little compass, or of little value.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nutshell', 'n.', 'A shell of the genus Nucula.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nutter', 'n.', 'A gatherer of nuts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nutting', 'n.', 'The act of gathering nuts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nutty', 'a.', 'Abounding in nuts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nutty', 'a.', 'Having a flavor like that of nuts; as, nutty wine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nux vomica', '', 'The seed of Strychnos Nuxvomica, a tree which abounds on\n   the Malabar and Coromandel coasts of the East Indies. From this seed\n   the deadly poisons known as strychnine and brucine are obtained. The\n   seeds are sometimes called Quaker buttons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nuzzied', 'imp. & p. p.', 'of Nuzzle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nuzzling', 'p. pr. & vb. n.', 'of Nuzzle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nuzzle', 'v. t.', 'To noursle or nurse; to foster; to bring up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nuzzle', 'v. t.', 'To nestle; to house, as in a nest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nuzzle', 'v. i.', 'To work with the nose, like a swine in the mud.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nuzzle', 'v. i.', 'To go with head poised like a swine, with nose down.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nuzzle', 'v. t.', 'To hide the head, as a child in the mothers bosom; to\n   nestle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nuzzle', 'v. t.', 'To loiter; to idle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ny', '', 'Not I; nor I.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ny', 'a. & adv.', 'Alt. of Nye');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nye', 'a. & adv.', 'Nigh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nyas', 'n.', 'See Nias.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nyctalopia', 'n.', 'A disease of the eye, in consequence of which the\n   patient can see well in a faint light or at twilight, but is unable to\n   see during the day or in a strong light; day blindness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nyctalopia', 'n.', 'See Moonblink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nyctalops', 'n.', 'One afflicted with nyctalopia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nyctalopy', 'n.', 'Same as Nyctalopia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nycthemeron', 'n.', 'The natural day and night, or space of twenty-four\n   hours.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nyctibune', 'n.', 'A South American bird of the genus Nyctibius, allied to\n   the goatsuckers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nyctitropic', 'a.', 'Turning or bending at night into special positions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nyctophile', 'n.', 'Any Australian bat of the genus Nyctophilus, having a\n   very simple nasal appendage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nye', 'n.', 'A brood or flock of pheasants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nyentek', 'n.', 'A carnivorous mannual (Helictis moscatus, or H.\n   orientalis), native of Eastern Asia and the Indies. It has a dorsal\n   white stripe, and another one across the shoulders. It has a strong\n   musky odor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nylghau', 'n.', 'Alt. of Nylgau');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nylgau', 'n.', 'A large Asiatic antelope (Boselaphus, / Portax,\n   tragocamelus), found in Northern India. It has short horns, a black\n   mane, and a bunch of long hair on the throat. The general color is\n   grayish brown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nymph', 'n.', 'A goddess of the mountains, forests, meadows, or waters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nymph', 'n.', 'A lovely young girl; a maiden; a damsel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nymph', 'n.', 'The pupa of an insect; a chrysalis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nymph', 'n.', 'Any one of a subfamily (Najades) of butterflies including\n   the purples, the fritillaries, the peacock butterfly, etc.; -- called\n   also naiad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nymph/', 'pl. ', 'of Nympha');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nympha', 'n.', 'Same as Nymph, 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nympha', 'n.', 'Two folds of mucous membrane, within the labia, at the\n   opening of the vulva.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nymphaea', 'n.', 'A genus of aquatic plants having showy flowers (white,\n   blue, pink, or yellow, often fragrant), including the white water lily\n   and the Egyptia lotus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nymphal', 'a.', 'Of or pertaining to a nymph or nymphs; nymphean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nymphales', 'n. pl.', 'An extensive family of butterflies including the\n   nymphs, the satyrs, the monarchs, the heliconias, and others; -- called\n   also brush-footed butterflies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nymphean', 'a.', 'Of, pertaining to, or appropriate to, nymphs; inhabited\n   by nymphs; as, a nymphean cave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nymphet', 'n.', 'A little or young nymph.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nymphic', 'a.', 'Alt. of Nymphical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nymphical', 'a.', 'Of or pertaining to nymphs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nymphiparous', 'a.', 'Producing pupas or nymphs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nymphish', 'a.', 'Relating to nymphs; ladylike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nymphlike', 'a.', 'Alt. of Nymphly');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nymphly', 'a.', 'Resembling, or characteristic of, a nymph.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nympholepsy', 'n.', 'A species of demoniac enthusiasm or possession coming\n   upon one who had accidentally looked upon a nymph; ecstasy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nympholeptic', 'a.', 'Under the influence of nympholepsy; ecstatic;\n   frenzied.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nymphomania', 'n.', 'Morbid and uncontrollable sexual desire in women,\n   constituting a true disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nymphomany', 'n.', 'Same as Nymphomania.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nymphotomy', 'n.', 'Excision of the nymphae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nys', '', 'Is not. See Nis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nystagmus', 'n.', 'A rapid involuntary oscillation of the eyeballs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Nyula', 'n.', 'A species of ichneumon (Herpestes nyula). Its fur is\n   beautifully variegated by closely set zigzag markings.\n   O () O, the fifteenth letter of the English alphabet, derives its form,\n   value, and name from the Greek O, through the Latin. The letter came\n   into the Greek from the Ph/nician, which possibly derived it ultimately\n   from the Egyptian. Etymologically, the letter o is most closely related\n   to a, e, and u; as in E. bone, AS. ban; E. stone, AS. stan; E. broke,\n   AS. brecan to break; E. bore, AS. beran to bear; E. dove, AS. d/fe; E.\n   toft, tuft; tone, tune; number, F. nombre.');
  INSERT INTO words (word, word_type, description) VALUES  (    'O', '', 'Among the ancients, O was a mark of triple time, from the notion\n   that the ternary, or number 3, is the most perfect of numbers, and\n   properly expressed by a circle, the most perfect figure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Os', 'pl. ', 'of O');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oes', 'pl. ', 'of O');
  INSERT INTO words (word, word_type, description) VALUES  (    'O', 'n.', 'The letter O, or its sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'O', 'n.', 'Something shaped like the letter O; a circle or oval.');
  INSERT INTO words (word, word_type, description) VALUES  (    'O', 'n.', 'A cipher; zero.');
  INSERT INTO words (word, word_type, description) VALUES  (    'O', '', 'A prefix to Irish family names, which signifies grandson or\n   descendant of, and is a character of dignity; as, ONeil, OCarrol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'O', 'prep.', 'A shortened form of of or on.');
  INSERT INTO words (word, word_type, description) VALUES  (    'O', 'a.', 'One.');
  INSERT INTO words (word, word_type, description) VALUES  (    'O', 'interj.', 'An exclamation used in calling or directly addressing a\n   person or personified object; also, as an emotional or impassioned\n   exclamation expressing pain, grief, surprise, desire, fear, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oad', 'n.', 'See Woad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oaf', 'n.', 'Originally, an elfs child; a changeling left by fairies or\n   goblins; hence, a deformed or foolish child; a simpleton; an idiot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oafish', 'a.', 'Like an oaf; simple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oak', 'n.', 'Any tree or shrub of the genus Quercus. The oaks have\n   alternate leaves, often variously lobed, and staminate flowers in\n   catkins. The fruit is a smooth nut, called an acorn, which is more or\n   less inclosed in a scaly involucre called the cup or cupule. There are\n   now recognized about three hundred species, of which nearly fifty occur\n   in the United States, the rest in Europe, Asia, and the other parts of\n   North America, a very few barely reaching the northern parts of South\n   America and Africa. Many of the oaks form forest trees of grand\n   proportions and live many centuries. The wood is usually hard and\n   tough, and provided with conspicuous medullary rays, forming the silver\n   grain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oak', 'n.', 'The strong wood or timber of the oak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oaken', 'a.', 'Made or consisting of oaks or of the wood of oaks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oaker', 'n.', 'See Ocher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oakling', 'n.', 'A young oak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oakum', 'n.', 'The material obtained by untwisting and picking into loose\n   fiber old hemp ropes; -- used for calking the seams of ships, stopping\n   leaks, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oakum', 'n.', 'The coarse portion separated from flax or hemp in nackling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oaky', 'n.', 'Resembling oak; strong.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oar', 'n', 'An implement for impelling a boat, being a slender piece of\n   timber, usually ash or spruce, with a grip or handle at one end and a\n   broad blade at the other. The part which rests in the rowlock is called\n   the loom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oar', 'n', 'An oarsman; a rower; as, he is a good oar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oar', 'n', 'An oarlike swimming organ of various invertebrates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oared', 'imp. & p. p.', 'of Oar');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oaring', 'p. pr. & vb. n.', 'of Oar');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oar', 'v. t. & i.', 'To row.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oared', 'a.', 'Furnished with oars; -- chiefly used in composition; as, a\n   four-oared boat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oared', 'a.', 'Having feet adapted for swimming.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oared', 'a.', 'Totipalmate; -- said of the feet of certain birds. See\n   Illust. of Aves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oarfish', 'n.', 'The ribbon fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oarfoot', 'n.', 'Any crustacean of the genus Remipes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oar-footed', 'a.', 'Having feet adapted for swimming.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oarless', 'a.', 'Without oars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oarlock', 'n.', 'The notch, fork, or other device on the gunwale of a boat,\n   in which the oar rests in rowing. See Rowlock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oarsmen', 'pl. ', 'of Oarsman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oarsman', 'n.', 'One who uses, or is skilled in the use of, an oar; a\n   rower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oarsweed', 'n.', 'Any large seaweed of the genus Laminaria; tangle; kelp.\n   See Kelp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oary', 'a.', 'Having the form or the use of an oar; as, the swans oary\n   feet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oases', 'pl. ', 'of Oasis');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oasis', 'n.', 'A fertile or green spot in a waste or desert, esp. in a\n   sandy desert.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oast', 'n.', 'A kiln to dry hops or malt; a cockle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oats', 'pl. ', 'of Oat');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oat', 'n.', 'A well-known cereal grass (Avena sativa), and its edible\n   grain; -- commonly used in the plural and in a collective sense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oat', 'n.', 'A musical pipe made of oat straw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oatcake', 'n.', 'A cake made of oatmeal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oaten', 'a.', 'Consisting of an oat straw or stem; as, an oaten pipe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oaten', 'a.', 'Made of oatmeal; as, oaten cakes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oaths', 'pl. ', 'of Oath');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oath', 'n.', 'A solemn affirmation or declaration, made with a reverent\n   appeal to God for the truth of what is affirmed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oath', 'n.', 'A solemn affirmation, connected with a sacred object, or one\n   regarded as sacred, as the temple, the altar, the blood of Abel, the\n   Bible, the Koran, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oath', 'n.', 'An appeal (in verification of a statement made) to a superior\n   sanction, in such a form as exposes the party making the appeal to an\n   indictment for perjury if the statement be false.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oath', 'n.', 'A careless and blasphemous use of the name of the divine\n   Being, or anything divine or sacred, by way of appeal or as a profane\n   exclamation or ejaculation; an expression of profane swearing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oathable', 'a.', 'Capable of having an oath administered to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oathbreaking', 'n.', 'The violation of an oath; perjury.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oatmeal', 'n.', 'Meal made of oats.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oatmeal', 'n.', 'A plant of the genus Panicum; panic grass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ob-', '', 'A prefix signifying to, toward, before, against, reversely,\n   etc.; also, as a simple intensive; as in oblige, to bind to; obstacle,\n   something standing before; object, lit., to throw against; obovate,\n   reversely, ovate. Ob- is commonly assimilated before c, f, g, and p, to\n   oc-, of-, og-, and op-.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obcompressed', 'a.', 'Compressed or flattened antero-posteriorly, or in a\n   way opposite to the usual one.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obconic', 'a.', 'Alt. of Obconical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obconical', 'a.', 'Conical, but having the apex downward; inversely\n   conical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obcordate', 'a.', 'Heart-shaped, with the attachment at the pointed end;\n   inversely cordate: as, an obcordate petal or leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obdiplostemonous', 'a.', 'Having twice as many stamens as petals, those of\n   the outer set being opposite the petals; -- said of flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obdiplostemony', 'n.', 'The condition of being obdiplostemonous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obdormition', 'n.', 'Sleep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obduce', 'v. t.', 'To draw over, as a covering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obduct', 'v. t.', 'To draw over; to cover.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obduction', 'n.', 'The act of drawing or laying over, as a covering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obduracy', 'n.', 'The duality or state of being obdurate; invincible\n   hardness of heart; obstinacy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obdurate', 'a.', 'Hardened in feelings, esp. against moral or mollifying\n   influences; unyielding; hard-hearted; stubbornly wicked.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obdurate', 'a.', 'Hard; harsh; rugged; rough; intractable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obdurate', 'v. t.', 'To harden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obduration', 'n.', 'A hardening of the heart; hardness of heart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obdure', 'v. t.', 'To harden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obdure', 'a.', 'Alt. of Obdured');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obdured', 'a.', 'Obdurate; hard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obdureness', 'n.', 'Alt. of Obduredness');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obduredness', 'n.', 'Hardness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obbe', 'n.', 'See Obi.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obeah', 'n.', 'Same as Obi.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obeah', 'a.', 'Of or pertaining to obi; as, the obeah man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obedible', 'a.', 'Obedient.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obedience', 'n.', 'The act of obeying, or the state of being obedient;\n   compliance with that which is required by authority; subjection to\n   rightful restraint or control.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obedience', 'n.', 'Words or actions denoting submission to authority;\n   dutifulness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obedience', 'n.', 'A following; a body of adherents; as, the Roman Catholic\n   obedience, or the whole body of persons who submit to the authority of\n   the pope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obedience', 'n.', 'A cell (or offshoot of a larger monastery) governed by a\n   prior.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obedience', 'n.', 'One of the three monastic vows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obedience', 'n.', 'The written precept of a superior in a religious order\n   or congregation to a subject.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obedienciary', 'n.', 'One yielding obedience.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obedient', 'a.', 'Subject in will or act to authority; willing to obey;\n   submissive to restraint, control, or command.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obediential', 'a.', 'According to the rule of obedience.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obediently', 'adv.', 'In an obedient manner; with obedience.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obeisance', 'n.', 'Obedience.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obeisance', 'n.', 'A manifestation of obedience; an expression of\n   difference or respect; homage; a bow; a courtesy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obeisancy', 'n.', 'See Obeisance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obeisant', 'a.', 'Ready to obey; reverent; differential; also, servilely\n   submissive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obelion', 'n.', 'The region of the skull between the two parietal foramina\n   where the closure of the sagittal suture usually begins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obeliscal', 'a.', 'Formed like an obelisk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obelisk', 'n.', 'An upright, four-sided pillar, gradually tapering as it\n   rises, and terminating in a pyramid called pyramidion. It is ordinarily\n   monolithic. Egyptian obelisks are commonly covered with hieroglyphic\n   writing from top to bottom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obelisk', 'n.', 'A mark of reference; -- called also dagger [/]. See\n   Dagger, n., 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obelisked', 'imp. & p. p.', 'of Obelisk');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obelisking', 'p. pr. & vb. n.', 'of Obelisk');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obelisk', 'v. t.', 'To mark or designate with an obelisk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obelized', 'imp. & p. p.', 'of Obelize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obelizing', 'p. pr. & vb. n.', 'of Obelize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obelize', 'v. t.', 'To designate with an obelus; to mark as doubtful or\n   spirituous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obeli', 'pl. ', 'of Obelus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obelus', 'n.', 'A mark [thus /, or  ]; -- so called as resembling a\n   needle. In old MSS. or editions of the classics, it marks suspected\n   passages or readings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obequitate', 'v. i.', 'To ride about.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oberon', 'n.', 'The king of the fairies, and husband of Titania or Queen\n   Mab.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oberration', 'n.', 'A wandering about.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obese', 'a.', 'Excessively corpulent; fat; fleshy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obeseness', 'n.', 'Quality of being obese; obesity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obesity', 'n.', 'The state or quality of being obese; incumbrance of flesh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obeyed', 'imp. & p. p.', 'of Obey');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obeying', 'p. pr. & vb. n.', 'of Obey');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obey', 'v. t.', 'To give ear to; to execute the commands of; to yield\n   submission to; to comply with the orders of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obey','v. t.','To submit to the authority of; to be ruled by.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obey','v. t.','To yield to the impulse, power, or operation of; as, a\n   ship obeys her helm.');
