INSERT INTO words (word, word_type, description)VALUES  (    'Obey',    'v. i.',    'To give obedience.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obeyer', 'n.', 'One who yields obedience.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obeyingly', 'adv.', 'Obediently; submissively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obfirm', 'v. t.', 'Alt. of Obfirmate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obfirmate', 'v. t.', 'To make firm; to harden in resolution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obfirmation', 'n.', 'Hardness of heart; obduracy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obfuscate', 'a.', 'Obfuscated; darkened; obscured.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obfuscated', 'imp. & p. p.', 'of Obfuscate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obfuscating', 'p. pr. & vb. n.', 'of Obfuscate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obfuscate', 'v. t.', 'To darken; to obscure; to becloud; hence, to confuse;\n   to bewilder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obfuscation', 'n.', 'The act of darkening or bewildering; the state of\n   being darkened.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obi', 'n.', 'A species of sorcery, probably of African origin, practiced\n   among the negroes of the West Indies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obi', 'n.', 'A charm or fetich.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obimbricate', 'a.', 'Imbricated, with the overlapping ends directed\n   downward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obit', 'n.', 'Death; decease; the date of ones death.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obit', 'n.', 'A funeral solemnity or office; obsequies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obit', 'n.', 'A service for the soul of a deceased person on the\n   anniversary of the day of his death.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obiter', 'adv.', 'In passing; incidentally; by the way.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obitual', 'a.', 'Of or pertaining to obits, or days when obits are\n   celebrated; as, obitual days.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obituarily', 'adv.', 'In the manner of an obituary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obiyuary', 'a.', 'Of or pertaining to the death of a person or persons; as,\n   an obituary notice; obituary poetry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obituaries', 'pl. ', 'of Obituary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obituary', 'n.', 'That which pertains to, or is called forth by, the obit\n   or death of a person; esp., an account of a deceased person; a notice\n   of the death of a person, accompanied by a biographical sketch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obituary', 'n.', 'A list of the dead, or a register of anniversary days\n   when service is performed for the dead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Objected', 'imp. & p. p.', 'of Object');
  INSERT INTO words (word, word_type, description) VALUES  (    'Objecting', 'p. pr. & vb. n.', 'of Object');
  INSERT INTO words (word, word_type, description) VALUES  (    'Object', 'v. t.', 'To set before or against; to bring into opposition; to\n   oppose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Object', 'v. t.', 'To offer in opposition as a criminal charge or by way of\n   accusation or reproach; to adduce as an objection or adverse reason.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Object', 'v. i.', 'To make opposition in words or argument; -- usually\n   followed by to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Object', 'v. t.', 'That which is put, or which may be regarded as put, in\n   the way of some of the senses; something visible or tangible; as, he\n   observed an object in the distance; all the objects in sight; he\n   touched a strange object in the dark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Object', 'v. t.', 'That which is set, or which may be regarded as set,\n   before the mind so as to be apprehended or known; that of which the\n   mind by any of its activities takes cognizance, whether a thing\n   external in space or a conception formed by the mind itself; as, an\n   object of knowledge, wonder, fear, thought, study, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Object', 'v. t.', 'That by which the mind, or any of its activities, is\n   directed; that on which the purpose are fixed as the end of action or\n   effort; that which is sought for; end; aim; motive; final cause.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Object', 'v. t.', 'Sight; show; appearance; aspect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Object', 'v. t.', 'A word, phrase, or clause toward which an action is\n   directed, or is considered to be directed; as, the object of a\n   transitive verb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Object', 'a.', 'Opposed; presented in opposition; also, exposed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Objectable', 'a.', 'Such as can be presented in opposition; that may be put\n   forward as an objection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Objectify', 'v. t.', 'To cause to become an object; to cause to assume the\n   character of an object; to render objective.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Objection', 'n.', 'The act of objecting; as, to prevent agreement, or\n   action, by objection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Objection', 'n.', 'That which is, or may be, presented in opposition; an\n   adverse reason or argument; a reason for objecting; obstacle;\n   impediment; as, I have no objection to going; unreasonable objections.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Objection', 'n.', 'Cause of trouble; sorrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Objectionable', 'a.', 'Liable to objection; likely to be objected to or\n   disapproved of; offensive; as, objectionable words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Objectist', 'n.', 'One who adheres to, or is skilled in, the objective\n   philosophy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Objectivate', 'v. t.', 'To objectify.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Objectivation', 'n.', 'Converting into an object.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Objective', 'a.', 'Of or pertaining to an object.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Objective', 'a.', 'Of or pertaining to an object; contained in, or having\n   the nature or position of, an object; outward; external; extrinsic; --\n   an epithet applied to whatever ir exterior to the mind, or which is\n   simply an object of thought or feeling, and opposed to subjective.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Objective', 'a.', 'Pertaining to, or designating, the case which follows a\n   transitive verb or a preposition, being that case in which the direct\n   object of the verb is placed. See Accusative, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Objective', 'n.', 'The objective case.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Objective', 'n.', 'An object glass. See under Object, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Objective', 'n.', 'Same as Objective point, under Objective, a.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Objectively', 'adv.', 'In the manner or state of an object; as, a\n   determinate idea objectively in the mind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Objectiveness', 'n.', 'Objectivity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Objectivity', 'n.', 'The state, quality, or relation of being objective;\n   character of the object or of the objective.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obectize', 'v. t.', 'To make an object of; to regard as an object; to place\n   in the position of an object.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Objectless', 'a.', 'Having no object; purposeless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Objector', 'n.', 'One who objects; one who offers objections to a\n   proposition or measure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Objibways', 'n.pl.', 'See Chippeways.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Objicient', 'n.', 'One who makes objection; an objector.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Objuration', 'n.', 'A binding by oath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Objurgated', 'imp. & p. p.', 'of Objurgate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Objurgating', 'p. pr. & vb. n.', 'of Objurgate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Objurgate', 'v. t.', 'To chide; to reprove.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Objurgation', 'n.', 'The act of objurgating; reproof.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Objurgatory', 'a.', 'Designed to objurgate or chide; containing or\n   expressing reproof; culpatory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oblanceolate', 'a.', 'Lanceolate in the reversed order, that is, narrowing\n   toward the point of attachment more than toward the apex.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oblate', 'a.', 'Flattened or depressed at the poles; as, the earth is an\n   oblate spheroid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oblate', 'a.', 'Offered up; devoted; consecrated; dedicated; -- used\n   chiefly or only in the titles of Roman Catholic orders. See Oblate, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oblate', 'a.', 'One of an association of priests or religious women who\n   have offered themselves to the service of the church. There are three\n   such associations of priests, and one of women, called oblates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oblate', 'a.', 'One of the Oblati.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oblateness', 'n.', 'The quality or state of being oblate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oblati', 'n. pl.', 'Children dedicated in their early years to the monastic\n   state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oblati', 'n. pl.', 'A class of persons, especially in the Middle Ages, who\n   offered themselves and their property to a monastery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oblation', 'n.', 'The act of offering, or of making an offering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oblation', 'n.', 'Anything offered or presented in worship or sacred\n   service; an offering; a sacrifice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oblation', 'n.', 'A gift or contribution made to a church, as for the\n   expenses of the eucharist, or for the support of the clergy and the\n   poor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oblationer', 'n.', 'One who makes an offering as an act worship or\n   reverence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oblatrate', 'v. i.', 'To bark or snarl, as a dog.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oblatration', 'n.', 'The act of oblatrating; a barking or snarling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oblata', 'pl. ', 'of Oblatum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oblatum', 'n.', 'An oblate spheroid; a figure described by the revolution\n   of an ellipse about its minor axis. Cf. Oblongum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oblectate', 'v. t.', 'To delight; to please greatly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oblectation', 'n.', 'The act of pleasing highly; the state of being greatly\n   pleased; delight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obligable', 'a.', 'Acknowledging, or complying with, obligation;\n   trustworthy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obligated', 'imp. & p. p.', 'of Obligate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obligating', 'p. pr. & vb. n.', 'of Obligate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obligate', 'v. t.', 'To bring or place under obligation, moral or legal; to\n   hold by a constraining motive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obligate', 'v. t.', 'To bind or firmly hold to an act; to compel; to\n   constrain; to bind to any act of duty or courtesy by a formal pledge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obligation', 'n.', 'The act of obligating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obligation', 'n.', 'That which obligates or constrains; the binding power\n   of a promise, contract, oath, or vow, or of law; that which constitutes\n   legal or moral duty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obligation', 'n.', 'Any act by which a person becomes bound to do something\n   to or for anouther, or to forbear something; external duties imposed by\n   law, promise, or contract, by the relations of society, or by courtesy,\n   kindness, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obligation', 'n.', 'The state of being obligated or bound; the state of\n   being indebted for an act of favor or kindness; as, to place others\n   under obligations to one.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obligation', 'n.', 'A bond with a condition annexed, and a penalty for\n   nonfulfillment. In a larger sense, it is an acknowledgment of a duty to\n   pay a certain sum or do a certain things.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obligato', 'a.', 'See Obbligato.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obligatorily', 'adv.', 'In an obligatory manner; by reason of obligation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obligatoriness', 'n.', 'The quality or state of being obligatory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obligatory', 'a.', 'Binding in law or conscience; imposing duty or\n   obligation; requiring performance or forbearance of some act; -- often\n   followed by on or upon; as, obedience is obligatory on a soldier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obliged', 'imp. & p. p.', 'of Oblige');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obliging', 'p. pr. & vb. n.', 'of Oblige');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oblige', 'v. t.', 'To attach, as by a bond.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oblige', 'v. t.', 'To constrain by physical, moral, or legal force; to put\n   under obligation to do or forbear something.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oblige', 'v. t.', 'To bind by some favor rendered; to place under a debt;\n   hence, to do a favor to; to please; to gratify; to accommodate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obligee', 'n.', 'The person to whom another is bound, or the person to whom\n   a bond is given.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obligement', 'n.', 'Obligation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obliger', 'n.', 'One who, or that which, obliges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obliging', 'a.', 'Putting under obligation; disposed to oblige or do\n   favors; hence, helpful; civil; kind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obligor', 'n.', 'The person who binds himself, or gives his bond to\n   another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obliquation', 'n.', 'The act of becoming oblique; a turning to one side;\n   obliquity; as, the obliquation of the eyes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obliquation', 'n.', 'Deviation from moral rectitude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oblique', 'a.', 'Not erect or perpendicular; neither parallel to, nor at\n   right angles from, the base; slanting; inclined.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oblique', 'a.', 'Not straightforward; indirect; obscure; hence,\n   disingenuous; underhand; perverse; sinister.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oblique', 'a.', 'Not direct in descent; not following the line of father\n   and son; collateral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oblique', 'n.', 'An oblique line.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obliqued', 'imp. & p. p.', 'of Oblique');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obliquing', 'p. pr. & vb. n.', 'of Oblique');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oblique', 'v. i.', 'To deviate from a perpendicular line; to move in an\n   oblique direction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oblique', 'v. i.', 'To march in a direction oblique to the line of the\n   column or platoon; -- formerly accomplished by oblique steps, now by\n   direct steps, the men half-facing either to the right or left.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oblique-angled', 'a.', 'Having oblique angles; as, an oblique-angled\n   triangle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obliquely', 'adv.', 'In an oblique manner; not directly; indirectly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obliqueness', 'n.', 'Quality or state of being oblique.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obliquities', 'pl. ', 'of Obliquity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obliquity', 'n.', 'The condition of being oblique; deviation from a right\n   line; deviation from parallelism or perpendicularity; the amount of\n   such deviation; divergence; as, the obliquity of the ecliptic to the\n   equator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obliquity', 'n.', 'Deviation from ordinary rules; irregularity; deviation\n   from moral rectitude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oblite', 'a.', 'Indistinct; slurred over.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obliterated', 'imp. & p. p.', 'of Obliterate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obliterating', 'p. pr. & vb. n.', 'of Obliterate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obliterate', 'v. t.', 'To erase or blot out; to efface; to render\n   undecipherable, as a writing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obliterate', 'v. t.', 'To wear out; to remove or destroy utterly by any\n   means; to render imperceptible; as. to obliterate ideas; to obliterate\n   the monuments of antiquity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obliterate', 'a.', 'Scarcely distinct; -- applied to the markings of\n   insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obliteration', 'n.', 'The act of obliterating, or the state of being\n   obliterated; extinction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obliterative', 'a.', 'Tending or serving to obliterate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oblivion', 'n.', 'The act of forgetting, or the state of being forgotten;\n   cessation of remembrance; forgetfulness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oblivion', 'n.', 'Official ignoring of offenses; amnesty, or general\n   pardon; as, an act of oblivion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oblivious', 'a.', 'Promoting oblivion; causing forgetfulness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oblivious', 'a.', 'Evincing oblivion; forgetful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oblocutor', 'n.', 'A disputer; a gainsayer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oblong', 'a.', 'Having greater length than breadth, esp. when rectangular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oblong', 'n.', 'A rectangular figure longer than it is broad; hence, any\n   figure longer than it is broad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oblongata', 'n.', 'The medulla oblongata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oblongatal', 'a.', 'Of or pertaining to the medulla oblongata; medullar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oblongish', 'a.', 'Somewhat oblong.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oblongly', 'adv.', 'In an oblong form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oblongness', 'n.', 'State or quality of being oblong.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oblong-ovate', 'a.', 'Between oblong and ovate, but inclined to the latter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oblonga', 'pl. ', 'of Oblongum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oblongum', 'n.', 'A prolate spheroid; a figure described by the revolution\n   of an ellipse about its greater axis. Cf. Oblatum, and see Ellipsoid of\n   revolution, under Ellipsoid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obloquious', 'a.', 'Containing obloquy; reproachful');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obloquy', 'n.', 'Censorious speech; defamatory language; language that\n   casts contempt on men or their actions; blame; reprehension.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obloquy', 'n.', 'Cause of reproach; disgrace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obluctation', 'n.', 'A struggle against; resistance; opposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obmutescence', 'n.', 'A becoming dumb; loss of speech.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obmutescence', 'n.', 'A keeping silent or mute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obnoxious', 'a.', 'Subject; liable; exposed; answerable; amenable; -- with\n   to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obnoxious', 'a.', 'Liable to censure; exposed to punishment; reprehensible;\n   blameworthy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obnoxious', 'a.', 'Offensive; odious; hateful; as, an obnoxious statesman;\n   a minister obnoxious to the Whigs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obnubilate', 'v. t.', 'To cloud; to obscure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oboe', 'n.', 'One of the higher wind instruments in the modern orchestra,\n   yet of great antiquity, having a penetrating pastoral quality of tone,\n   somewhat like the clarinet in form, but more slender, and sounded by\n   means of a double reed; a hautboy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oboist', 'n.', 'A performer on the oboe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obolary', 'a.', 'Possessing only small coins; impoverished.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obole', 'n.', 'A weight of twelve grains; or, according to some, of ten\n   grains, or half a scruple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obolize', 'v. t.', 'See Obelize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obolo', 'n.', 'A copper coin, used in the Ionian Islands, about one cent in\n   value.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oboli', 'pl. ', 'of Obolus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obolus', 'n.', 'A small silver coin of Athens, the sixth part of a drachma,\n   about three cents in value.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obolus', 'n.', 'An ancient weight, the sixth part of a drachm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obomegoid', 'a.', 'Obversely omegoid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oboval', 'a.', 'Obovate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obovate', 'a.', 'Inversely ovate; ovate with the narrow end downward; as,\n   an obovate leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obreption', 'n.', 'The act of creeping upon with secrecy or by surprise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obreption', 'n.', 'The obtaining gifts of escheat by fraud or surprise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obreptitious', 'a.', 'Done or obtained by surprise; with secrecy, or by\n   concealment of the truth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obrogate', 'v. t.', 'To annul indirectly by enacting a new and contrary\n   law, instead of by expressly abrogating or repealing the old one.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obrok', 'n.', 'A rent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obrok', 'n.', 'A poll tax paid by peasants absent from their lords estate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obscene', 'a/', 'Offensive to chastity or modesty; expressing of presenting\n   to the mind or view something which delicacy, purity, and decency\n   forbid to be exposed; impure; as, obscene language; obscene pictures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obscene', 'a/', 'Foul; fifthy; disgusting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obscene', 'a/', 'Inauspicious; ill-omened.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obscenities', 'pl. ', 'of Obscenity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obscenity', 'n.', 'That quality in words or things which presents what is\n   offensive to chasity or purity of mind; obscene or impure lanquage or\n   acts; moral impurity; lewdness; obsceneness; as, the obscenity of a\n   speech, or a picture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obscurant', 'n.', 'One who obscures; one who prevents enlightenment or\n   hinders the progress of knowledge and wisdom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obscurantism', 'n.', 'The system or the principles of the obscurants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obscurantist', 'n.', 'Same as Obscurant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obscuration', 'v. t.', 'The act or operation of obscuring; the state of\n   being obscured; as, the obscuration of the moon in an eclipse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obscure', 'superl.', 'Covered over, shaded, or darkened; destitute of\n   light; imperfectly illuminated; dusky; dim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obscure', 'superl.', 'Of or pertaining to darkness or night; inconspicuous\n   to the sight; indistinctly seen; hidden; retired; remote from\n   observation; unnoticed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obscure', 'superl.', 'Not noticeable; humble; mean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obscure', 'superl.', 'Not easily understood; not clear or legible; abstruse\n   or blind; as, an obscure passage or inscription.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obscure', 'superl.', 'Not clear, full, or distinct; clouded; imperfect; as,\n   an obscure view of remote objects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obscured', 'imp. & p. p.', 'of Obscure');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obscuring', 'p. pr. & vb. n.', 'of Obscure');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obscure', 'a.', 'To render obscure; to darken; to make dim; to keep in the\n   dark; to hide; to make less visible, intelligible, legible, glorious,\n   beautiful, or illustrious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obscure', 'v. i.', 'To conceal ones self; to hide; to keep dark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obscure', 'n.', 'Obscurity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obscurely', 'adv.', 'In an obscure manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obscurement', 'n.', 'The act of obscuring, or the state of being obscured;\n   obscuration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obscureness', 'n.', 'Obscurity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obscurer', 'n.', 'One who, or that which, obscures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obscurity', 'n.', 'The quality or state of being obscure; darkness;\n   privacy; inconspicuousness; unintelligibleness; uncertainty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obsecrated', 'imp. & p. p.', 'of Obsecrate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obsecrating', 'p. pr. & vb, n.', 'of Obsecrate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obsecrate', 'v. t.', 'To beseech; to supplicate; to implore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obsecration', 'n.', 'The act of obsecrating or imploring; as, the\n   obsecrations of the Litany, being those clauses beginning with \"By.\"');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obsecration', 'n.', 'A figure of speech in which the orator implores the\n   assistance of God or man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obsecratory', 'a.', 'Expressing, or used in, entreaty; supplicatory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obsequent', 'a.', 'Obedient; submissive; obsequious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obsequience', 'n.', 'Obsequiousness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obsequies', 'n.pl.', 'See Obsequy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obsequious', 'a.', 'Promptly obedient, or submissive, to the will of\n   another; compliant; yielding to the desires of another; devoted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obsequious', 'a.', 'Servilely or meanly attentive; compliant to excess;\n   cringing; fawning; as, obsequious flatterer, parasite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obsequious', 'a.', 'Of or pertaining to obsequies; funereal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obsequiously', 'adv.', 'In an obsequious manner; compliantly; fawningly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obsequiously', 'adv.', 'In a manner appropriate to obsequies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obsequiousness', 'n.', 'The quality or state of being obsequious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obsequies', 'pl. ', 'of Obsequy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obsequy', 'n.', 'The last duty or service to a person, rendered after his\n   death; hence, a rite or ceremony pertaining to burial; -- now used only\n   in the plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obsequy', 'n.', 'Obsequiousness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Observable', 'a.', 'Worthy or capable of being observed; discernible;\n   noticeable; remarkable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Observance', 'n.', 'The act or practice of observing or noticing with\n   attention; a heeding or keeping with care; performance; -- usually with\n   a sense of strictness and fidelity; as, the observance of the Sabbath\n   is general; the strict observance of duties.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Observance', 'n.', 'An act, ceremony, or rite, as of worship or respect;\n   especially, a customary act or service of attention; a form; a\n   practice; a rite; a custom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Observance', 'n.', 'Servile attention; sycophancy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Observancy', 'n.', 'Observance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Observanda', 'pl. ', 'of Observandum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Observandum', 'n.', 'A thing to be observed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Observant', 'a.', 'Taking notice; viewing or noticing attentively;\n   watchful; attentive; as, an observant spectator; observant habits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Observant', 'a.', 'Submissively attentive; obediently watchful; regardful;\n   mindful; obedient (to); -- with of, as, to be observant of rules.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Observant', 'n.', 'One who observes forms and rules.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Observant', 'n.', 'A sycophantic servant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Observant', 'n.', 'An Observantine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Observantine', 'n.', 'One of a branch of the Order of Franciscans, who\n   profess to adhere more strictly than the Conventuals to the intention\n   of the founder, especially as to poverty; -- called also Observants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Observantly', 'adv.', 'In an observant manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Observation', 'n.', 'The act or the faculty of observing or taking notice;\n   the act of seeing, or of fixing the mind upon, anything.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Observation', 'n.', 'The result of an act, or of acts, of observing; view;\n   reflection; conclusion; judgment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Observation', 'n.', 'Hence: An expression of an opinion or judgment upon\n   what one has observed; a remark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Observation', 'n.', 'Performance of what is prescribed; adherence in\n   practice; observance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Observation', 'n.', 'The act of recognizing and noting some fact or\n   occurrence in nature, as an aurora, a corona, or the structure of an\n   animal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Observation', 'n.', 'Specifically, the act of measuring, with suitable\n   instruments, some magnitude, as the time of an occultation, with a\n   clock; the right ascension of a star, with a transit instrument and\n   clock; the suns altitude, or the distance of the moon from a star,\n   with a sextant; the temperature, with a thermometer, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Observation', 'n.', 'The information so acquired.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Observational', 'a.', 'Of a pertaining to observation; consisting of, or\n   containing, observations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Observative', 'a.', 'Observing; watchful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Observator', 'n.', 'One who observes or takes notice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Observator', 'n.', 'One who makes a remark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Observatories', 'pl. ', 'of Observatory');
  INSERT INTO words (word, word_type, description) VALUES  (    'Observatory', 'n.', 'A place or building for making observations on the\n   heavenly bodies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Observatory', 'n.', 'A building fitted with instruments for making\n   systematic observations of any particular class or series of natural\n   phenomena.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Observatory', 'n.', 'A place, as an elevated chamber, from which a view may\n   be observed or commanded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Observatory', 'n.', 'A lookout on a flank of a battery whence an officer\n   can note the range and effect of the fire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Observed', 'imp. & p. p.', 'of Observe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Observing', 'p. pr. & vb. n.', 'of Observe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Observe', 'v. t.', 'To take notice of by appropriate conduct; to conform\n   ones action or practice to; to keep; to heed; to obey; to comply with;\n   as, to observe rules or commands; to observe civility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Observe', 'v. t.', 'To be on the watch respecting; to pay attention to; to\n   notice with care; to see; to perceive; to discover; as, to observe an\n   eclipse; to observe the color or fashion of a dress; to observe the\n   movements of an army.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Observe', 'v. t.', 'To express as what has been noticed; to utter as a\n   remark; to say in a casual or incidental way; to remark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Observe', 'v. i.', 'To take notice; to give attention to what one sees or\n   hears; to attend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Observe', 'v. i.', 'To make a remark; to comment; -- generally with on or\n   upon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Observer', 'n.', 'One who observes, or pays attention to, anything;\n   especially, one engaged in, or trained to habits of, close and exact\n   observation; as, an astronomical observer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Observer', 'n.', 'One who keeps any law, custom, regulation, rite, etc.;\n   one who conforms to anything in practice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Observer', 'n.', 'One who fulfills or performs; as, an observer of his\n   promises.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Observer', 'n.', 'A sycophantic follower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Observership', 'n.', 'The office or work of an observer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Observing', 'a.', 'Giving particular attention; habitually attentive to\n   what passes; as, an observing person; an observing mind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obsess', 'v. t.', 'To besiege; to beset.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obsession', 'n.', 'The act of besieging.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obsession', 'n.', 'The state of being besieged; -- used specifically of a\n   person beset by a spirit from without.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obsidian', 'n.', 'A kind of glass produced by volcanoes. It is usually of a\n   black color, and opaque, except in thin splinters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obsidional', 'a.', 'Of or pertaining to a siege.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obsigillation', 'n.', 'A sealing up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obsign', 'v. t.', 'To seal; to confirm, as by a seal or stamp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obsignate', 'v. t.', 'To seal; to ratify.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obsignation', 'n.', 'The act of sealing or ratifying; the state of being\n   sealed or confirmed; confirmation, as by the Holy Spirit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obsignatory', 'a.', 'Ratifying; confirming by sealing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obsolesce', 'v. i.', 'To become obsolescent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obsolescence', 'n.', 'The state of becoming obsolete.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obsolescent', 'a.', 'Going out of use; becoming obsolete; passing into\n   desuetude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obsolete', 'a.', 'No longer in use; gone into disuse; disused; neglected;\n   as, an obsolete word; an obsolete statute; -- applied chiefly to words,\n   writings, or observances.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obsolete', 'a.', 'Not very distinct; obscure; rudimental; imperfectly\n   developed; abortive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obsolete', 'v. i.', 'To become obsolete; to go out of use.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obsoletely', 'adv.', 'In an obsolete manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obsoleteness', 'n.', 'The state of being obsolete, or no longer used; a\n   state of desuetude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obsoleteness', 'n.', 'Indistinctness; want of development.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obsoletism', 'n.', 'A disused word or phrase; an archaism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obstacle', 'v.', 'That which stands in the way, or opposes; anything that\n   hinders progress; a hindrance; an obstruction, physical or moral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obstancy', 'n.', 'Opposition; impediment; obstruction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obstetric', 'a.', 'Alt. of Obstetrical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obstetrical', 'a.', 'Of or pertaining to midwifery, or the delivery of\n   women in childbed; as, the obstetric art.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obstetricate', 'v. i.', 'To perform the office of midwife.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obstetricate', 'v. t.', 'To assist as a midwife.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obstetrication', 'n.', 'The act of assisting as a midwife; delivery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obstetrician', 'n.', 'One skilled in obstetrics; an accoucheur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obstetricious', 'a.', 'Serving to assist childbirth; obstetric; hence,\n   facilitating any bringing forth or deliverance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obstetrics', 'n.', 'The science of midwifery; the art of assisting women in\n   parturition, or in the trouble incident to childbirth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obstetricy', 'n.', 'Obstetrics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obstinacy', 'n.', 'A fixedness in will, opinion, or resolution that can not\n   be shaken at all, or only with great difficulty; firm and usually\n   unreasonable adherence to an opinion, purpose, or system; unyielding\n   disposition; stubborness; pertinacity; persistency; contumacy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obstinacy', 'n.', 'The quality or state of being difficult to remedy,\n   relieve, or subdue; as, the obstinacy of a disease or evil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obstinate', 'a.', 'Pertinaciously adhering to an opinion, purpose, or\n   course; persistent; not yielding to reason, arguments, or other means;\n   stubborn; pertinacious; -- usually implying unreasonableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obstinate', 'a.', 'Not yielding; not easily subdued or removed; as,\n   obstinate fever; obstinate obstructions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obstination', 'n.', 'Obstinacy; stubbornness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obstipation', 'n.', 'The act of stopping up, as a passage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obstipation', 'n.', 'Extreme constipation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obstreperous', 'a.', 'Attended by, or making, a loud and tumultuous noise;\n   clamorous; noisy; vociferous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obstriction', 'n.', 'The state of being constrained, bound, or obliged;\n   that which constrains or obliges; obligation; bond.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obstringe', 'v. t.', 'To constrain; to put under obligation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obstructed', 'imp. & p. p.', 'of Obstruct');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obstructing', 'p. pr. & vb. n.', 'of Obstruct');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obstruct', 'v. t.', 'To block up; to stop up or close, as a way or passage;\n   to place an obstacle in, or fill with obstacles or impediments that\n   prevent or hinder passing; as, to obstruct a street; to obstruct the\n   channels of the body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obstruct', 'v. t.', 'To be, or come, in the way of; to hinder from passing;\n   to stop; to impede; to retard; as, the bar in the harbor obstructs the\n   passage of ships; clouds obstruct the light of the sun; unwise rules\n   obstruct legislation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obstructer', 'n.', 'One who obstructs or hinders.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obstruction', 'n.', 'The act of obstructing, or state of being obstructed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obstruction', 'n.', 'That which obstructs or impedes; an obstacle; an\n   impediment; a hindrance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obstruction', 'n.', 'The condition of having the natural powers obstructed\n   in their usual course; the arrest of the vital functions; death.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obstructionism', 'n.', 'The act or the policy of obstructing progress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obstructionist', 'n.', 'One who hinders progress; one who obstructs\n   business, as in a legislative body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obstructionist', 'a.', 'Of or pertaining to obstructionists.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obstructive', 'a.', 'Tending to obstruct; presenting obstacles; hindering;\n   causing impediment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obstructive', 'n.', 'An obstructive person or thing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obstruent', 'a.', 'Causing obstruction; blocking up; hindering; as, an\n   obstruent medicine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obstruent', 'n.', 'Anything that obstructs or closes a passage; esp., that\n   which obstructs natural passages in the body; as, a medicine which acts\n   as an obstruent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obstupefaction', 'n.', 'See Stupefaction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obstupefactive', 'a.', 'Stupefactive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obstupefy', 'v. t.', 'See Stupefy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obtained', 'imp. & p. p.', 'of Obtain');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obtaining', 'p. pr. & vb. n.', 'of Obtain');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obtain', 'v. t.', 'To hold; to keep; to possess.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obtain', 'v. t.', 'To get hold of by effort; to gain possession of; to\n   procure; to acquire, in any way.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obtain', 'v. i.', 'To become held; to gain or have a firm footing; to be\n   recognized or established; to subsist; to become prevalent or general;\n   to prevail; as, the custom obtains of going to the seashore in summer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obtain', 'v. i.', 'To prevail; to succeed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obtainable', 'a.', 'Capable of being obtained.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obtainer', 'n.', 'One who obtains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obtainment', 'n.', 'The act or process of obtaining; attainment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obtected', 'a.', 'Covered; protected.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obtected', 'a.', 'Covered with a hard chitinous case, as the pupa of\n   certain files.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obtemper', 'v. t. & i.', 'To obey (a judgment or decree).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obtemperate', 'v. t.', 'To obey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obtended', 'imp. & p. p.', 'of Obtend');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obtending', 'p. pr. & vb. n.', 'of Obtend');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obtend', 'v. t.', 'To oppose; to hold out in opposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obtend', 'v. t.', 'To offer as the reason of anything; to pretend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obtenebration', 'n.', 'The act of darkening; the state of being darkened;\n   darkness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obtension', 'n.', 'The act of obtending.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obtested', 'imp. & p. p.', 'of Obtest');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obtesting', 'p. pr. & vb. n.', 'of Obtest');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obtest', 'v. t.', 'To call to witness; to invoke as a witness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obtest', 'v. t.', 'To beseech; to supplicate; to beg for.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obtest', 'v. i.', 'To protest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obtestation', 'n.', 'The act of obtesting; supplication; protestation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obtrectation', 'n.', 'Slander; detraction; calumny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obtruded', 'imp. & p. p.', 'of Obtrude');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obtruding', 'p. pr. & vb. n.', 'of Obtrude');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obtrude', 'v. t.', 'To thrust impertinently; to present without warrant or\n   solicitation; as, to obtrude ones self upon a company.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obtrude', 'v. t.', 'To offer with unreasonable importunity; to urge unduly\n   or against the will.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obtrude', 'v. i.', 'To thrust ones self upon a company or upon attention;\n   to intrude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obtruder', 'n.', 'One who obtrudes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obtruncate', 'v. t.', 'To deprive of a limb; to lop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obtruncation', 'n.', 'The act of lopping or cutting off.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obtrusion', 'n.', 'The act of obtruding; a thrusting upon others by force\n   or unsolicited; as, the obtrusion of crude opinions on the world.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obtrusion', 'n.', 'That which is obtruded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obtrusionist', 'n.', 'One who practices or excuses obtrusion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obtrusive', 'a.', 'Disposed to obtrude; inclined to intrude or thrust ones\n   self or ones opinions upon others, or to enter uninvited; forward;\n   pushing; intrusive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obtunded', 'imp. & p. p.', 'of Obtund');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obtunding', 'p. pr. & vb. n.', 'of Obtund');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obtund', 'v. t.', 'To reduce the edge, pungency, or violent action of; to\n   dull; to blunt; to deaden; to quell; as, to obtund the acrimony of the\n   gall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obtundent', 'n.', 'A substance which sheathes a part, or blunts irritation,\n   usually some bland, oily, or mucilaginous matter; -- nearly the same as\n   demulcent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obtunder', 'n.', 'That which obtunds or blunts; especially, that which\n   blunts sensibility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obturation', 'n.', 'The act of stopping up, or closing, an opening.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obturator', 'n.', 'That which closes or stops an opening.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obturator', 'n.', 'An apparatus designed to close an unnatural opening, as\n   a fissure of the palate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obturator', 'a.', 'Serving as an obturator; closing an opening; pertaining\n   to, or in the region of, the obturator foramen; as, the obturator\n   nerve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obtusangular', 'a.', 'See Obstuseangular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obtuse', 'superl.', 'Not pointed or acute; blunt; -- applied esp. to angles\n   greater than a right angle, or containing more than ninety degrees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obtuse', 'superl.', 'Not having acute sensibility or perceptions; dull;\n   stupid; as, obtuse senses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obtuse', 'superl.', 'Dull; deadened; as, obtuse sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obtuse-angled', 'a.', 'Alt. of obtuse-angular');
  INSERT INTO words (word, word_type, description) VALUES  (    'obtuse-angular', 'a.', 'Having an obtuse angle; as, an obtuse-angled\n   triangle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obtusely', 'adv.', 'In an obtuse manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obtuseness', 'n.', 'State or quality of being obtuse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obtusion', 'n.', 'The act or process of making obtuse or blunt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obtusion', 'n.', 'The state of being dulled or blunted; as, the obtusion of\n   the senses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obtusity', 'n.', 'Obtuseness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obumbrant', 'a.', 'Overhanging; as, obumbrant feathers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obumbrate', 'v. t.', 'To shade; to darken; to cloud.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obumbration', 'n.', 'Act of darkening or obscuring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obuncous', 'a.', 'Hooked or crooked in an extreme degree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obvention', 'n.', 'The act of happening incidentally; that which happens\n   casually; an incidental advantage; an occasional offering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obversant', 'a.', 'Conversant; familiar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obverse', 'a.', 'Having the base, or end next the attachment, narrower than\n   the top, as a leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obverse', 'a.', 'The face of a coin which has the principal image or\n   inscription upon it; -- the other side being the reverse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obverse', 'a.', 'Anything necessarily involved in, or answering to,\n   another; the more apparent or conspicuous of two possible sides, or of\n   two corresponding things.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obversely', 'adv.', 'In an obverse manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obversion', 'n.', 'The act of turning toward or downward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obversion', 'n.', 'The act of immediate inference, by which we deny the\n   opposite of anything which has been affirmed; as, all men are mortal;\n   then, by obversion, no men are immortal. This is also described as\n   \"immediate inference by privative conception.\"');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obverted', 'imp. & p. p.', 'of Obvert');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obverting', 'p. pr. & vb. n.', 'of Obvert');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obvert', 'v. t.', 'To turn toward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obviated', 'imp. & p. p.', 'of Obviate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obviating', 'p. pr. & vb. n.', 'of Obviate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obviate', 'v. t.', 'To meet in the way.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obviate', 'v. t.', 'To anticipate; to prevent by interception; to remove\n   from the way or path; to make unnecessary; as, to obviate the necessity\n   of going.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obviation', 'n.', 'The act of obviating, or the state of being obviated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obvious', 'a.', 'Opposing; fronting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obvious', 'a.', 'Exposed; subject; open; liable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obvious', 'a.', 'Easily discovered, seen, or understood; readily perceived\n   by the eye or the intellect; plain; evident; apparent; as, an obvious\n   meaning; an obvious remark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obvolute', 'a.', 'Alt. of Obvoluted');
  INSERT INTO words (word, word_type, description) VALUES  (    'Obvoluted', 'a.', 'Overlapping; contorted; convolute; -- applied primarily,\n   in botany, to two opposite leaves, each of which has one edge\n   overlapping the nearest edge of the other, and secondarily to a circle\n   of several leaves or petals which thus overlap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oby', 'n.', 'See Obi.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oca', 'n.', 'A Peruvian name for certain species of Oxalis (O. crenata, and\n   O. tuberosa) which bear edible tubers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Occamy', 'n.', 'An alloy imitating gold or silver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Occasion', 'n.', 'A falling out, happening, or coming to pass; hence, that\n   which falls out or happens; occurrence; incident.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Occasion', 'n.', 'A favorable opportunity; a convenient or timely chance;\n   convenience.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Occasion', 'n.', 'An occurrence or condition of affairs which brings with\n   it some unlooked-for event; that which incidentally brings to pass an\n   event, without being its efficient cause or sufficient reason;\n   accidental or incidental cause.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Occasion', 'n.', 'Need; exigency; requirement; necessity; as, I have no\n   occasion for firearms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Occasion', 'n.', 'A reason or excuse; a motive; a persuasion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Occasioned', 'imp. & p. p.', 'of Occasion');
  INSERT INTO words (word, word_type, description) VALUES  (    'Occasioning', 'p. pr. & vb. n.', 'of Occasion');
  INSERT INTO words (word, word_type, description) VALUES  (    'Occasion', 'v. t.', 'To give occasion to; to cause; to produce; to induce;\n   as, to occasion anxiety.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Occasionable', 'a.', 'Capable of being occasioned or caused.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Occasional', 'a.', 'Of or pertaining to an occasion or to occasions;\n   occuring at times, but not constant, regular, or systematic; made or\n   happening as opportunity requires or admits; casual; incidental; as,\n   occasional remarks, or efforts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Occasional', 'a.', 'Produced by accident; as, the occasional origin of a\n   thing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Occasionalism', 'n.', 'The system of occasional causes; -- a name given to\n   certain theories of the Cartesian school of philosophers, as to the\n   intervention of the First Cause, by which they account for the apparent\n   reciprocal action of the soul and the body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Occasionality', 'n.', 'Quality or state of being occasional; occasional\n   occurrence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Occasionally', 'adv.', 'In an occasional manner; on occasion; at times, as\n   convenience requires or opportunity offers; not regularly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Occasionate', 'v. t.', 'To occasion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Occasioner', 'n.', 'One who, or that which, occasions, causes, or produces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Occasive', 'a.', 'Of or pertaining to the setting sun; falling; descending;\n   western.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Occecation', 'n.', 'The act of making blind, or the state of being blind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Occident', 'n.', 'The part of the horizon where the sun last appears in the\n   evening; that part of the earth towards the sunset; the west; --\n   opposed to orient. Specifically, in former times, Europe as opposed to\n   Asia; now, also, the Western hemisphere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Occidental', 'a.', 'Of, pertaining to, or situated in, the occident, or\n   west; western; -- opposed to oriental; as, occidental climates, or\n   customs; an occidental planet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Occidental', 'a.', 'Possessing inferior hardness, brilliancy, or beauty; --\n   used of inferior precious stones and gems, because those found in the\n   Orient are generally superior.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Occidentals', 'n.pl.', 'Western Christians of the Latin rite. See\n   Orientals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Occiduous', 'a.', 'Western; occidental.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Occipital', 'a.', 'Of or pertaining to the occiput, or back part of the\n   head, or to the occipital bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Occipital', 'n.', 'The occipital bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Occipito-', '', 'A combining form denoting relation to, or situation near,\n   the occiput; as, occipito-axial; occipito-mastoid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Occipitoaxial', 'a.', 'Of or pertaining to the occipital bone and second\n   vertebra, or axis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Occipita', 'pl. ', 'of Occiput');
  INSERT INTO words (word, word_type, description) VALUES  (    'Occiputs', 'pl. ', 'of Occiput');
  INSERT INTO words (word, word_type, description) VALUES  (    'Occiput', 'n.', 'The back, or posterior, part of the head or skull; the\n   region of the occipital bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Occiput', 'n.', 'A plate which forms the back part of the head of insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Occision', 'n.', 'A killing; the act of killing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Occlude', 'v. t.', 'To shut up; to close.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Occlude', 'v. t.', 'To take in and retain; to absorb; -- said especially\n   with respect to gases; as iron, platinum, and palladium occlude large\n   volumes of hydrogen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Occludent', 'a.', 'Serving to close; shutting up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Occludent', 'n.', 'That which closes or shuts up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Occluse', 'a.', 'Shut; closed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Occlusion', 'n.', 'The act of occluding, or the state of being occluded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Occlusion', 'n.', 'The transient approximation of the edges of a natural\n   opening; imperforation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Occrustate', 'v. t.', 'To incrust; to harden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Occult', 'a.', 'Hidden from the eye or the understanding; inviable; secret;\n   concealed; unknown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Occult', 'v. t.', 'To eclipse; to hide from sight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Occultation', 'n.', 'The hiding of a heavenly body from sight by the\n   intervention of some other of the heavenly bodies; -- applied\n   especially to eclipses of stars and planets by the moon, and to the\n   eclipses of satellites of planets by their primaries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Occultation', 'n.', 'Fig.: The state of being occult.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Occulted', 'a.', 'Hidden; secret.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Occulted', 'a.', 'Concealed by the intervention of some other heavenly\n   body, as a star by the moon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Occulting', 'n.', 'Same as Occultation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Occultism', 'n.', 'A certain Oriental system of theosophy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Occultist', 'n.', 'An adherent of occultism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Occultly', 'adv.', 'In an occult manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Occultness', 'n.', 'State or quality of being occult.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Occupancy', 'n.', 'The act of taking or holding possession; possession;\n   occupation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Occupant', 'n.', 'One who occupies, or takes possession; one who has the\n   actual use or possession, or is in possession, of a thing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Occupant', 'n.', 'A prostitute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Occupate', 'v. t.', 'To occupy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Occupation', 'n.', 'The act or process of occupying or taking possession;\n   actual possession and control; the state of being occupied; a holding\n   or keeping; tenure; use; as, the occupation of lands by a tenant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Occupation', 'n.', 'That which occupies or engages the time and attention;\n   the principal business of ones life; vocation; employment; calling;\n   trade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Occupier', 'n.', 'One who occupies, or has possession.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Occupier', 'n.', 'One who follows an employment; hence, a tradesman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Occupied', 'imp. & p. p.', 'of Occupy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Occupying', 'p. pr. & vb. n.', 'of Occupy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Occupy', 'v. t.', 'To take or hold possession of; to hold or keep for use;\n   to possess.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Occupy', 'v. t.', 'To hold, or fill, the dimensions of; to take up the room\n   or space of; to cover or fill; as, the camp occupies five acres of\n   ground.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Occupy', 'v. t.', 'To possess or use the time or capacity of; to engage the\n   service of; to employ; to busy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Occupy', 'v. t.', 'To do business in; to busy ones self with.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Occupy', 'v. t.', 'To use; to expend; to make use of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Occupy', 'v. t.', 'To have sexual intercourse with.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Occupy', 'v. i.', 'To hold possession; to be an occupant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Occupy', 'v. i.', 'To follow business; to traffic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Occurred', 'imp. & p. p.', 'of Occur');
  INSERT INTO words (word, word_type, description) VALUES  (    'Occurring', 'p. pr. & vb. n.', 'of Occur');
  INSERT INTO words (word, word_type, description) VALUES  (    'Occur', 'v. i.', 'To meet; to clash.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Occur', 'v. i.', 'To go in order to meet; to make reply.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Occur', 'v. i.', 'To meet ones eye; to be found or met with; to present\n   itself; to offer; to appear; to happen; to take place; as, I will write\n   if opportunity occurs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Occur', 'v. i.', 'To meet or come to the mind; to suggest itself; to be\n   presented to the imagination or memory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Occurrence', 'n.', 'A coming or happening; as, the occurence of a railway\n   collision.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Occurrence', 'n.', 'Any incident or event; esp., one which happens without\n   being designed or expected; as, an unusual occurrence, or the ordinary\n   occurrences of life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Occurrent', 'a.', 'Occurring or happening; hence, incidental; accidental.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Occurrent', 'n.', 'One who meets; hence, an adversary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Occurrent', 'n.', 'Anything that happens; an occurrence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Occurse', 'n.', 'Same as Occursion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Occursion', 'n.', 'A meeting; a clash; a collision.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ocean', 'n.', 'The whole body of salt water which covers more than three\n   fifths of the surface of the globe; -- called also the sea, or great\n   sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ocean', 'n.', 'One of the large bodies of water into which the great ocean\n   is regarded as divided, as the Atlantic, Pacific, Indian, Arctic and\n   Antarctic oceans.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ocean', 'n.', 'An immense expanse; any vast space or quantity without\n   apparent limits; as, the boundless ocean of eternity; an ocean of\n   affairs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ocean', 'a.', 'Of or pertaining to the main or great sea; as, the ocean\n   waves; an ocean stream.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oceanic', 'a.', 'Of or pertaining to the ocean; found or formed in or\n   about, or produced by, the ocean; frequenting the ocean, especially\n   mid-ocean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oceanic', 'a.', 'Of or pertaining to Oceania or its inhabitants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oceanography', 'n.', 'A description of the ocean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oceanology', 'n.', 'That branch of science which relates to the ocean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oceanus', 'n.', 'The god of the great outer sea, or the river which was\n   believed to flow around the whole earth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ocellary', 'a.', 'Of or pertaining to ocelli.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ocellate', 'a.', 'Same as Ocellated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ocellated', 'a.', 'Resembling an eye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ocellated', 'a.', 'Marked with eyelike spots of color; as, the ocellated\n   blenny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ocelli', 'pl. ', 'of Ocellus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ocellus', 'n.', 'A little eye; a minute simple eye found in many\n   invertebrates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ocellus', 'n.', 'An eyelike spot of color, as those on the tail of the\n   peacock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oceloid', 'a.', 'Resembling the ocelot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ocelot', 'n.', 'An American feline carnivore (Felis pardalis). It ranges\n   from the Southwestern United States to Patagonia. It is covered with\n   blackish ocellated spots and blotches, which are variously arranged.\n   The ground color varies from reddish gray to tawny yellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ocher', 'n.', 'Alt. of Ochre');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ochre', 'n.', 'A impure earthy ore of iron or a ferruginous clay, usually\n   red (hematite) or yellow (limonite), -- used as a pigment in making\n   paints, etc. The name is also applied to clays of other colors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ochre', 'n.', 'A metallic oxide occurring in earthy form; as, tungstic\n   ocher or tungstite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ocherous', 'a.', 'Alt. of Ochreous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ochreous', 'a.', 'Of or pertaining to ocher; containing or resembling\n   ocher; as, ocherous matter; ocherous soil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ochery', 'a.', 'Ocherous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ochimy', 'n.', 'See Occamy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ochlesis', 'n.', 'A general morbid condition induced by the crowding\n   together of many persons, esp. sick persons, under one roof.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ochlocracy', 'n.', 'A form of government by the multitude; a mobocracy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ochlocratic', 'a.', 'Alt. of Ochlocratical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ochlocratical', 'a.', 'Of or pertaining to ochlocracy; having the form or\n   character of an ochlocracy; mobocratic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ochraceous', 'a.', 'Ocherous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ochre', 'n.', 'See Ocher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ochreaee', 'pl. ', 'of Ochrea');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ochrea', 'n.', 'A greave or legging.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ochrea', 'n.', 'A kind of sheath formed by two stipules united round a\n   stem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ochreate', 'a.', 'Alt. of Ochreated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ochreated', 'a.', 'Wearing or furnished with an ochrea or legging; wearing\n   boots; booted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ochreated', 'a.', 'Provided with ochrea, or sheathformed stipules, as the\n   rhubarb, yellow dock, and knotgrass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ochreous', 'a.', 'See Ocherous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ochrey', 'a.', 'See Ochery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ochroleucous', 'a.', 'Yellowish white; having a faint tint of dingy yellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ochry', 'a.', 'See Ochery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ochymy', 'n.', 'See Occamy.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-ock', '', 'A suffix used to form diminutives; as, bullock, hillock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ocra', 'n.', 'See Okra.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ocrea', 'n.', 'See Ochrea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ocreate', 'a.', 'Alt. of Ocreated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ocreated', 'a.', 'Same as Ochreate, Ochreated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Octa-', '', 'A prefix meaning eight. See Octo-.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Octachord', 'n.', 'An instrument of eight strings; a system of eight tones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Octad', 'n.', 'An atom or radical which has a valence of eight, or is\n   octavalent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Octaedral', 'a.', 'See Octahedral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Octaemeron', 'n.', 'A fast of eight days before a great festival.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Octagon', 'n.', 'A plane figure of eight sides and eight angles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Octagon', 'n.', 'Any structure (as a fortification) or place with eight\n   sides or angles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Octagonal', 'a.', 'Having eight sides and eight angles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Octagynous', 'a.', 'Having eight pistils or styles; octogynous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Octahedral', 'a.', 'Having eight faces or sides; of, pertaining to, or\n   formed in, octahedrons; as, octahedral cleavage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Octahedrite', 'n.', 'Titanium dioxide occurring in acute octahedral\n   crystals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Octahedron', 'n.', 'A solid bounded by eight faces. The regular octahedron\n   is contained by eight equal equilateral triangles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Octamerous', 'a.', 'Having the parts in eights; as, an octamerous flower;\n   octamerous mesenteries in polyps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Octameter', 'n.', 'A verse containing eight feet; as, --//Deep# in|to# the\n   | dark#ness | peer#ing, | long# I | stood# there | wond#ring, |\n   fear#ing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Octander', 'n.', 'One of the Octandria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Octandria', 'n.pl.', 'A Linnaean class of plants, in which the flowers have\n   eight stamens not united to one another or to the pistil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Octandrian', 'a.', 'Alt. of Octandrous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Octandrous', 'a.', 'Of or pertaining to the Octandria; having eight\n   distinct stamens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Octane', 'n.', 'Any one of a group of metametric hydrocarcons (C8H18) of\n   the methane series. The most important is a colorless, volatile,\n   inflammable liquid, found in petroleum, and a constituent of benzene or\n   ligroin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Octangular', 'a.', 'Having eight angles; eight-angled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Octant', 'n.', 'The eighth part of a circle; an arc of 45 degrees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Octant', 'n.', 'The position or aspect of a heavenly body, as the moon or a\n   planet, when half way between conjunction, or opposition, and\n   quadrature, or distant from another body 45 degrees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Octant', 'n.', 'An instrument for measuring angles (generally called a\n   quadrant), having an arc which measures up to 9O, but being itself the\n   eighth part of a circle. Cf. Sextant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Octant', 'n.', 'One of the eight parts into which a space is divided by\n   three coordinate planes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Octapla', 'sing.', 'A portion of the Old Testament prepared by Origen in\n   the 3d century, containing the Hebrew text and seven Greek versions of\n   it, arranged in eight parallel columns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Octaroon', 'n.', 'See Octoroon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Octastyle', 'a.', 'See Octostyle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Octateuch', 'n.', 'A collection of eight books; especially, the first eight\n   books of the Old Testament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Octavalent', 'a.', 'Having a valence of eight; capable of being combined\n   with, exchanged for, or compared with, eight atoms of hydrogen; -- said\n   of certain atoms or radicals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Octave', 'n.', 'The eighth day after a church festival, the festival day\n   being included; also, the week following a church festival.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Octave', 'n.', 'The eighth tone in the scale; the interval between one and\n   eight of the scale, or any interval of equal length; an interval of\n   five tones and two semitones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Octave', 'n.', 'The whole diatonic scale itself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Octave', 'n.', 'The first two stanzas of a sonnet, consisting of four\n   verses each; a stanza of eight lines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Octave', 'n.', 'A small cask of wine, the eighth part of a pipe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Octave', 'a.', 'Consisting of eight; eight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Octavos', 'pl. ', 'of Octavo');
  INSERT INTO words (word, word_type, description) VALUES  (    'Octavo', 'n.', 'A book composed of sheets each of which is folded into\n   eight leaves; hence, indicating more or less definitely a size of book\n   so made; -- usually written 8vo or 8.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Octavo', 'a.', 'Having eight leaves to a sheet; as, an octavo form, book,\n   leaf, size, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Octene', 'n.', 'Same as Octylene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Octennial', 'a.', 'Happening every eighth year; also, lasting a period of\n   eight years.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Octet', 'n.', 'A composition for eight parts, usually for eight solo\n   instruments or voices.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Octic', 'a.', 'Of the eighth degree or order.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Octic', 'n.', 'A quantic of the eighth degree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Octile', 'n.', 'Same as Octant, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Octillion', 'n.', 'According to the French method of numeration (which\n   method is followed also in the United States) the number expressed by a\n   unit with twenty-seven ciphers annexed. According to the English\n   method, the number expressed by a unit with forty-eight ciphers\n   annexed. See Numeration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Octo-', '', 'Alt. of Octa-');
  INSERT INTO words (word, word_type, description) VALUES  (    'Octa-', '', 'A combining form meaning eight; as in octodecimal,\n   octodecimal, octolocular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Octoate', 'n.', 'A salt of an octoic acid; a caprylate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'October', 'n.', 'The tenth month of the year, containing thirty-one days.');
  INSERT INTO words (word, word_type, description) VALUES  (    'October', 'n.', 'Ale or cider made in that month.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Octocera', 'n.pl.', 'Octocerata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Octocerata', 'n.pl.', 'A suborder of Cephalopoda including Octopus,\n   Argonauta, and allied genera, having eight arms around the head; --\n   called also Octopoda.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Octochord', 'n.', 'See Octachord.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Octodecimo', 'a.', 'Having eighteen leaves to a sheet; as, an octodecimo\n   form, book, leaf, size, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Octodecimos', 'pl. ', 'of Octodecimo');
  INSERT INTO words (word, word_type, description) VALUES  (    'Octodecimo', 'n.', 'A book composed of sheets each of which is folded into\n   eighteen leaves; hence; indicating more or less definitely a size of\n   book, whose sheets are so folded; -- usually written 18mo or 18, and\n   called eighteenmo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Octodentate', 'a.', 'Having eight teeth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Octodont', 'a.', 'Of or pertaining to the Octodontidae, a family of rodents\n   which includes the coypu, and many other South American species.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Octoedrical', 'a.', 'See Octahedral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Octofid', 'a.', 'Cleft or separated into eight segments, as a calyx.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Octogamy', 'n.', 'A marrying eight times.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Octogenarian', 'n.', 'A person eighty years, or more, of age.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Octogenary', 'a.', 'Of eighty years of age.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Octogild', 'n.', 'A pecuniary compensation for an injury, of eight times\n   the value of the thing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Octogonal', 'a.', 'See Octagonal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Octogynia', 'n.pl.', 'A Linnaean order of plants having eight pistils.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Octogynian', 'a.', 'Alt. of Octogynous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Octogynous', 'a.', 'Having eight pistils; octagynous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Octoic', 'a.', 'Pertaining to, derived from, or resembling, octane; -- used\n   specifically, to designate any one of a group of acids, the most\n   important of which is called caprylic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Octolocular', 'a.', 'Having eight cells for seeds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Octonaphthene', 'n.', 'A colorless liquid hydrocarbon of the octylene\n   series, occurring in Caucasian petroleum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Octonary', 'a.', 'Of or pertaining to the number eight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Octonocular', 'a.', 'Having eight eyes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Octopede', 'n.', 'An animal having eight feet, as a spider.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Octopetalous', 'a.', 'Having eight petals or flower leaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Octopod', 'n.', 'One of the Octocerata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Octopoda', 'n.pl.', 'Same as Octocerata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Octopoda', 'n.pl.', 'Same as Arachnida.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Octopodia', 'n.pl.', 'Same as Octocerata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Octopus', 'n.', 'A genus of eight-armed cephalopods, including numerous\n   species, some of them of large size. See Devilfish,');
  INSERT INTO words (word, word_type, description) VALUES  (    'Octoradiated', 'a.', 'Having eight rays.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Octoroon', 'n.', 'The offspring of a quadroon and a white person; a mestee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Octospermous', 'a.', 'Containing eight seeds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Octostichous', 'a.', 'In eight vertical ranks, as leaves on a stem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Octostyle', 'a.', 'Having eight columns in the front; -- said of a temple\n   or portico. The Parthenon is octostyle, but most large Greek temples\n   are hexastele. See Hexastyle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Octostyle', 'n.', 'An octostyle portico or temple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Octosyllabic', 'a.', 'Alt. of Octosyllabical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Octosyllabical', 'a.', 'Consisting of or containing eight syllables.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Octosyllable', 'a.', 'Octosyllabic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Octosyllable', 'n.', 'A word of eight syllables.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Octoyl', 'n.', 'A hypothetical radical (C8H15O), regarded as the essential\n   residue of octoic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Octroi', 'n.', 'A privilege granted by the sovereign authority, as the\n   exclusive right of trade granted to a guild or society; a concession.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Octroi', 'n.', 'A tax levied in money or kind at the gate of a French city\n   on articles brought within the walls.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Octuor', 'n.', 'See Octet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Octuple', 'a.', 'Eightfold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Octyl', 'n.', 'A hypothetical hydrocarbon radical regarded as an essential\n   residue of octane, and as entering into its derivatives; as, octyl\n   alcohol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Octylene', 'n.', 'Any one of a series of metameric hydrocarbons (C8H16) of\n   the ethylene series. In general they are combustible, colorless\n   liquids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Octylic', 'a.', 'Pertaining to, derived from, or containing, octyl; as,\n   octylic ether.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ocular', 'a.', 'Depending on, or perceived by, the eye; received by actual\n   sight; personally seeing or having seen; as, ocular proof.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ocular', 'a.', 'Of or pertaining to the eye; optic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ocular', 'n.', 'The eyepiece of an optical instrument, as of a telescope or\n   microscope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ocularly', 'adv.', 'By the eye, or by actual sight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oculary', 'a.', 'Of or pertaining to the eye; ocular; optic; as, oculary\n   medicines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oculate', 'a.', 'Alt. of Oculated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oculated', 'a.', 'Furnished with eyes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oculated', 'a.', 'Having spots or holes resembling eyes; ocellated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oculiform', 'a.', 'In the form of an eye; resembling an eye; as, an\n   oculiform pebble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oculina', 'n.', 'A genus of tropical corals, usually branched, and having a\n   very volid texture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oculinacea', 'n.pl.', 'A suborder of corals including many reef-building\n   species, having round, starlike calicles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oculist', 'n.', 'One skilled in treating diseases of the eye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oculo-', '', 'A combining form from L. oculus the eye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oculomotor', 'a.', 'Of or pertaining to the movement of the eye; -- applied\n   especially to the common motor nerves (or third pair of cranial nerves)\n   which supply many of the muscles of the orbit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oculomotor', 'n.', 'The oculomotor nerve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oculonasal', 'a.', 'Of or pertaining to the region of the eye and the nose;\n   as, the oculonasal, or nasal, nerve, one of the branches of the\n   ophthalmic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oculi', 'pl. ', 'of Oculus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oculus', 'n.', 'An eye; (Bot.) a leaf bud.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oculus', 'n.', 'A round window, usually a small one.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ocypodian', 'n.', 'One of a tribe of crabs which live in holes in the sand\n   along the seashore, and run very rapidly, -- whence the name.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Od', 'n.', 'An alleged force or natural power, supposed, by Reichenbach and\n   others, to produce the phenomena of mesmerism, and to be developed by\n   various agencies, as by magnets, heat, light, chemical or vital action,\n   etc.; -- called also odyle or the odylic force.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Odalisque', 'n.', 'A female slave or concubine in the harem of the Turkish\n   sultan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Odd', 'superl.', 'Not paired with another, or remaining over after a\n   pairing; without a mate; unmatched; single; as, an odd shoe; an odd\n   glove.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Odd', 'superl.', 'Not divisible by 2 without a remainder; not capable of\n   being evenly paired, one unit with another; as, 1, 3, 7, 9, 11, etc.,\n   are odd numbers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Odd', 'superl.', 'Left over after a definite round number has been taken or\n   mentioned; indefinitely, but not greatly, exceeding a specified number;\n   extra.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Odd', 'superl.', 'Remaining over; unconnected; detached; fragmentary;\n   hence, occasional; inconsiderable; as, odd jobs; odd minutes; odd\n   trifles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Odd', 'superl.', 'Different from what is usual or common; unusual;\n   singular; peculiar; unique; strange.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Odd Fellow', '', 'A member of a secret order, or fraternity, styled the\n   Independent Order of Odd Fellows, established for mutual aid and social\n   enjoyment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oddities', 'pl. ', 'of Oddity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oddity', 'n.', 'The quality or state of being odd; singularity; queerness;\n   peculiarity; as, oddity of dress, manners, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oddity', 'n.', 'That which is odd; as, a collection of oddities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oddly', 'adv.', 'In an odd manner; unevently.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oddly', 'adv.', 'In a peculiar manner; strangely; queerly; curiously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oddly', 'adv.', 'In a manner measured by an odd number.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oddness', 'n.', 'The state of being odd, or not even.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oddness', 'n.', 'Singularity; strangeness; eccentricity; irregularity;\n   uncouthness; as, the oddness of dress or shape; the oddness of an\n   event.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Odds', 'a.', 'Difference in favor of one and against another; excess of one\n   of two things or numbers over the other; inequality; advantage;\n   superiority; hence, excess of chances; probability.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Odds', 'a.', 'Quarrel; dispute; debate; strife; -- chiefly in the phrase at\n   odds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ode', 'n.', 'A short poetical composition proper to be set to music or\n   sung; a lyric poem; esp., now, a poem characterized by sustained noble\n   sentiment and appropriate dignity of style.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Odelet', 'n.', 'A little or short ode.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Odeon', 'n.', 'A kind of theater in ancient Greece, smaller than the\n   dramatic theater and roofed over, in which poets and musicians\n   submitted their works to the approval of the public, and contended for\n   prizes; -- hence, in modern usage, the name of a hall for musical or\n   dramatic performances.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Odeum', 'n.', 'See Odeon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Odible', 'a.', 'Fitted to excite hatred; hateful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Odic', 'a.', 'Of or pertaining to od. See Od.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Odin', 'n.', 'The supreme deity of the Scandinavians; -- the same as Woden,\n   of the German tribes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Odinic', 'a.', 'Of or pertaining to Odin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Odious', 'a.', 'Hateful; deserving or receiving hatred; as, an odious name,\n   system, vice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Odious', 'a.', 'Causing or provoking hatred, repugnance, or disgust;\n   offensive; disagreeable; repulsive; as, an odious sight; an odious\n   smell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Odist', 'n.', 'A writer of an ode or odes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Odium', 'n.', 'Hatred; dislike; as, his conduct brought him into odium, or,\n   brought odium upon him.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Odium', 'n.', 'The quality that provokes hatred; offensiveness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Odized', 'imp. & p. p.', 'of Odize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Odizing', 'p. pr. & vb. n.', 'of Odize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Odize', 'v. t.', 'To charge with od. See Od.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Odmyl', 'n.', 'A volatile liquid obtained by boiling sulphur with linseed\n   oil. It has an unpleasant garlic odor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Odometer', 'n.', 'An instrument attached to the wheel of a vehicle, to\n   measure the distance traversed; also, a wheel used by surveyors, which\n   registers the miles and rods traversed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Odometrical', 'a.', 'Of or pertaining to the odometer, or to measurements\n   made with it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Odometrous', 'a.', 'Serving to measure distance on a road.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Odometry', 'n.', 'Measurement of distances by the odometer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Odonata', 'n. pl.', 'The division of insects that includes the dragon\n   flies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Odontalgia', 'n.', 'Toothache.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Odontalgic', 'a.', 'Of or pertaining to odontalgia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Odontalgic', 'n.', 'A remedy for the toothache.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Odontalgy', 'n.', 'Same as Odontalgia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Odontiasis', 'n.', 'Cutting of the teeth; dentition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Odonto-', '', 'A combining form from Gr. odoy`s, odo`ntos, a tooth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Odontoblast', 'n.', 'One of the more or less columnar cells on the outer\n   surface of the pulp of a tooth; an odontoplast. They are supposed to be\n   connected with the formation of dentine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Odontoblast', 'n.', 'One of the cells which secrete the chitinous teeth of\n   Mollusca.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Odontocete', 'n.pl.', 'A subdivision of Cetacea, including the sperm whale,\n   dolphins, etc.; the toothed whales.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Odontogeny', 'n.', 'Generetion, or mode of development, of the teeth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Odontograph', 'n.', 'An instrument for marking or laying off the outlines\n   of teeth of gear wheels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Odontographic', 'a.', 'Of or pertaining to odontography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Odontography', 'n.', 'A description of the teeth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Odontoid', 'a.', 'Having the form of a tooth; toothlike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Odontoid', 'a.', 'Of or pertaining to the odontoid bone or to the odontoid\n   process.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Odontolcae', 'n. pl.', 'An extinct order of ostrichlike aquatic birds\n   having teeth, which are set in a groove in the jaw. It includes\n   Hesperornis, and allied genera. See Hesperornis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Odontolite', 'n.', 'A fossil tooth colored a bright blue by phosphate of\n   iron. It is used as an imitation of turquoise, and hence called bone\n   turquoise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Odontology', 'n.', 'The science which treats of the teeth, their structure\n   and development.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Odontophora', 'n.pl.', 'Same as Cephalophora.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Odontophore', 'n.', 'A special structure found in the mouth of most\n   mollusks, except bivalves. It consists of several muscles and a\n   cartilage which supports a chitinous radula, or lingual ribbon, armed\n   with teeth. Also applied to the radula alone. See Radula.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Odontophorous', 'a.', 'Having an odontophore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Odontoplast', 'n.', 'An odontoblast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Odontopteryx', 'n.', 'An extinct Eocene bird having the jaws strongly\n   serrated, or dentated, but destitute of true teeth. It was found near\n   London.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Odontornithes', 'n. pl.', 'A group of Mesozoic birds having the jaws armed\n   with teeth, as in most other vertebrates. They have been divided into\n   three orders: Odontolcae, Odontotormae, and Saururae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Odontostomatous', 'a.', 'Having toothlike mandibles; -- applied to certain\n   insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Odontotormae', 'n.pl.', 'An order of extinct toothed birds having the teeth\n   in sockets, as in the genus Ichthyornis. See Ichthyornis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Odor', 'n.', 'Any smell, whether fragrant or offensive; scent; perfume.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Odorament', 'n.', 'A perfume; a strong scent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Odorant', 'a.', 'Yielding odors; fragrant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Odorate', 'a.', 'Odorous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Odorating', 'a.', 'Diffusing odor or scent; fragrant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Odoriferous', 'a.', 'Bearing or yielding an odor; perfumed; usually, sweet\n   of scent; fragrant; as, odoriferous spices, particles, fumes, breezes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Odorline', 'n.', 'A pungent oily substance obtained by redistilling bone\n   oil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Odorless', 'a.', 'Free from odor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Odorous', 'a.', 'Having or emitting an odor or scent, esp. a sweet odor;\n   fragrant; sweet-smelling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ods', 'interj.', 'A corruption of Gods; -- formerly used in oaths and\n   ejaculatory phrases.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Odyl', 'n.', 'Alt. of Odyle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Odyle', 'n.', 'See Od. [Archaic].');
  INSERT INTO words (word, word_type, description) VALUES  (    'Odylic', 'a.', 'Of or pertaining to odyle; odic; as, odylic force.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Odyssey', 'n.', 'An epic poem attributed to Homer, which describes the\n   return of Ulysses to Ithaca after the siege of Troy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oe', '', 'a diphthong, employed in the Latin language, and thence in the\n   English language, as the representative of the Greek diphthong oi. In\n   many words in common use, e alone stands instead of /. Classicists\n   prefer to write the diphthong oe separate in Latin words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oecoid', 'n.', 'The colorless porous framework, or stroma, of red blood\n   corpuscles from which the zooid, or hemoglobin and other substances of\n   the corpuscles, may be dissolved out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oecology', 'n.', 'The various relations of animals and plants to one\n   another and to the outer world.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oeconomical', 'a.', 'See Economical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oeconomics', 'n.', 'See Economics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oeconomy', 'n.', 'See Economy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oecumenical', 'a.', 'See Ecumenical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oedema', 'n.', 'A swelling from effusion of watery fluid in the cellular\n   tissue beneath the skin or mucous membrance; dropsy of the subcutaneous\n   cellular tissue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oedematous', 'a.', 'Pertaining to, or of the nature of, edema; affected\n   with edema.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oeiliad', 'n.', 'Alt. of Oeillade');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oeillade', 'n.', 'A glance of the eye; an amorous look.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oelet', 'n.', 'An eye, bud, or shoot, as of a plant; an oilet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oenanthate', 'n.', 'A salt of the supposed /nanthic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oenanthic', 'a.', 'Having, or imparting, the odor characteristic of the\n   bouquet of wine; specifically used, formerly, to designate an acid\n   whose ethereal salts were supposed to occasion the peculiar bouquet, or\n   aroma, of old wine. Cf. Oenanthylic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oenanthol', 'n.', 'An oily substance obtained by the distillation of castor\n   oil, recognized as the aldehyde of oenanthylic acid, and hence called\n   also oenanthaldehyde.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oenanthone', 'n.', 'The ketone of oenanthic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oenanthyl', 'n.', 'A hydrocarbon radical formerly supposed to exist in\n   oenanthic acid, now known to be identical with heptyl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oenanthylate', 'n.', 'A salt of /nanthylic acid; as, potassium\n   oenanthylate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oenanthylic', 'a.', 'Pertaining to, derived from, or containing, oenanthyl;\n   specifically, designating an acid formerly supposed to be identical\n   with the acid in oenanthic ether, but now known to be identical with\n   heptoic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oenanthylidene', 'n.', 'A colorless liquid hydrocarbon, having a garlic\n   odor; heptine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oenanthylous', 'a.', 'Of, pertaining to, or designating, an acid formerly\n   supposed to be the acid of oenanthylic ether, but now known to be a\n   mixture of higher acids, especially capric acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oenocyan', 'n.', 'The coloring matter of red wines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oenology', 'n.', 'Knowledge of wine, scientific or practical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oenomania', 'n.', 'Delirium tremens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oenomania', 'n.', 'Dipsomania.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oenomel', 'n.', 'Wine mixed with honey; mead,');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oenometer', 'n.', 'See Alcoholometer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oenophilist', 'n.', 'A lover of wine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oenothionic', 'a.', 'Pertaining to an acid now called sulphovinic, / ethyl\n   sulphuric, acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oer', 'prep. & adv.', 'A contr. of Over.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oesophagus', 'a.', 'Alt. of Oesophageal');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oesophageal', 'a.', 'Same as Esophagus, Esophageal, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oestrian', 'a.', 'Of or pertaining to the gadflies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oestrian', 'n.', 'A gadfly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oestrual', 'a.', 'Of or pertaining to sexual desire; -- mostly applied to\n   brute animals; as, the oestrual period; oestrual influence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oestruation', 'n.', 'The state of being under oestrual influence, or of\n   having sexual desire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oestrus', 'n.', 'A genus of gadflies. The species which deposits its larvae\n   in the nasal cavities of sheep is oestrus ovis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oestrus', 'n.', 'A vehement desire; esp. (Physiol.), the periodical sexual\n   impulse of animals; heat; rut.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Of', 'prep.', 'In a general sense, from, or out from; proceeding from;\n   belonging to; relating to; concerning; -- used in a variety of\n   applications; as:');
  INSERT INTO words (word, word_type, description) VALUES  (    'Of', 'prep.', 'Denoting that from which anything proceeds; indicating\n   origin, source, descent, and the like; as, he is of a race of kings; he\n   is of noble blood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Of', 'prep.', 'Denoting possession or ownership, or the relation of subject\n   to attribute; as, the apartment of the consul: the power of the king; a\n   man of courage; the gate of heaven.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Of', 'prep.', 'Denoting the material of which anything is composed, or that\n   which it contains; as, a throne of gold; a sword of steel; a wreath of\n   mist; a cup of water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Of', 'prep.', 'Denoting part of an aggregate or whole; belonging to a\n   number or quantity mentioned; out of; from amongst; as, of this little\n   he had some to spare; some of the mines were unproductive; most of the\n   company.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Of', 'prep.', 'Denoting that by which a person or thing is actuated or\n   impelled; also, the source of a purpose or action; as, they went of\n   their own will; no body can move of itself; he did it of necessity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Of', 'prep.', 'Denoting reference to a thing; about; concerning; relating\n   to; as, to boast of ones achievements.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Of', 'prep.', 'Denoting nearness or distance, either in space or time;\n   from; as, within a league of the town; within an hour of the appointed\n   time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Of', 'prep.', 'Denoting identity or equivalence; -- used with a name or\n   appellation, and equivalent to the relation of apposition; as, the\n   continent of America; the city of Rome; the Island of Cuba.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Of', 'prep.', 'Denoting the agent, or person by whom, or thing by which,\n   anything is, or is done; by.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Of', 'prep.', 'Denoting relation to place or time; belonging to, or\n   connected with; as, men of Athens; the people of the Middle Ages; in\n   the days of Herod.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Of', 'prep.', 'Denoting passage from one state to another; from.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Of', 'prep.', 'During; in the course of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Off', 'adv.', 'In a general sense, denoting from or away from; as:');
  INSERT INTO words (word, word_type, description) VALUES  (    'Off', 'adv.', 'Denoting distance or separation; as, the house is a mile\n   off.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Off', 'adv.', 'Denoting the action of removing or separating; separation;\n   as, to take off the hat or cloak; to cut off, to pare off, to clip off,\n   to peel off, to tear off, to march off, to fly off, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Off', 'adv.', 'Denoting a leaving, abandonment, departure, abatement,\n   interruption, or remission; as, the fever goes off; the pain goes off;\n   the game is off; all bets are off.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Off', 'adv.', 'Denoting a different direction; not on or towards: away; as,\n   to look off.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Off', 'adv.', 'Denoting opposition or negation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Off', 'interj.', 'Away; begone; -- a command to depart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Off', 'prep.', 'Not on; away from; as, to be off ones legs or off the bed;\n   two miles off the shore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Off', 'a.', 'On the farther side; most distant; on the side of an animal or\n   a team farthest from the driver when he is on foot; in the United\n   States, the right side; as, the off horse or ox in a team, in\n   distinction from the nigh or near horse or ox; the off leg.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Off', 'a.', 'Designating a time when one is not strictly attentive to\n   business or affairs, or is absent from his post, and, hence, a time\n   when affairs are not urgent; as, he took an off day for fishing: an off\n   year in politics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Off', 'n.', 'The side of the field that is on the right of the wicket\n   keeper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Offal', 'n.', 'The rejected or waste parts of a butchered animal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Offal', 'n.', 'A dead body; carrion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Offal', 'n.', 'That which is thrown away as worthless or unfit for use;\n   refuse; rubbish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Offcut', 'n.', 'That which is cut off.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Offcut', 'n.', 'A portion ofthe printed sheet, in certain sizes of books,\n   that is cut off before folding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Offence', 'n.', 'See Offense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Offended', 'imp. & p. p.', 'of Offend');
  INSERT INTO words (word, word_type, description) VALUES  (    'Offending', 'p. pr. & vb. n.', 'of Offend');
  INSERT INTO words (word, word_type, description) VALUES  (    'Offend', 'v. t.', 'To strike against; to attack; to assail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Offend', 'v. t.', 'To displease; to make angry; to affront.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Offend', 'v. t.', 'To be offensive to; to harm; to pain; to annoy; as,\n   strong light offends the eye; to offend the conscience.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Offend', 'v. t.', 'To transgress; to violate; to sin against.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Offend', 'v. t.', 'To oppose or obstruct in duty; to cause to stumble; to\n   cause to sin or to fall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Odfend', 'v. i.', 'To transgress the moral or divine law; to commit a\n   crime; to stumble; to sin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Odfend', 'v. i.', 'To cause dislike, anger, or vexation; to displease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Offendant', 'n.', 'An offender.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Offender', 'n.', 'One who offends; one who violates any law, divine or\n   human; a wrongdoer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Offendress', 'n.', 'A woman who offends.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Offense', 'n.', 'Alt. of Offence');
  INSERT INTO words (word, word_type, description) VALUES  (    'Offence', 'n.', 'The act of offending in any sense; esp., a crime or a sin,\n   an affront or an injury.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Offence', 'n.', 'The state of being offended or displeased; anger;\n   displeasure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Offence', 'n.', 'A cause or occasion of stumbling or of sin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Offenseful', 'a.', 'Causing offense; displeasing; wrong; as, an offenseful\n   act.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Offenseless', 'a.', 'Unoffending; inoffensive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Offensible', 'a.', 'That may give offense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Offension', 'n.', 'Assault; attack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Offensive', 'a.', 'Giving offense; causing displeasure or resentment;\n   displeasing; annoying; as, offensive words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Offensive', 'a.', 'Giving pain or unpleasant sensations; disagreeable;\n   revolting; noxious; as, an offensive smell; offensive sounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Offensive', 'a.', 'Making the first attack; assailant; aggressive; hence,\n   used in attacking; -- opposed to defensive; as, an offensive war;\n   offensive weapons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Offensive', 'n.', 'The state or posture of one who offends or makes attack;\n   aggressive attitude; the act of the attacking party; -- opposed to\n   defensive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Offered', 'imp. & p. p.', 'of Offer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Offering', 'p. pr. & vb. n.', 'of Offer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Offer', 'v. t.', 'To present, as an act of worship; to immolate; to\n   sacrifice; to present in prayer or devotion; -- often with up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Offer', 'v. t.', 'To bring to or before; to hold out to; to present for\n   acceptance or rejection; as, to offer a present, or a bribe; to offer\n   ones self in marriage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Offer', 'v. t.', 'To present in words; to proffer; to make a proposal of;\n   to suggest; as, to offer an opinion. With the infinitive as an\n   objective: To make an offer; to declare ones willingness; as, he\n   offered to help me.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Offer', 'v. t.', 'To attempt; to undertake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Offer', 'v. t.', 'To bid, as a price, reward, or wages; as, to offer a\n   guinea for a ring; to offer a salary or reward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Offer', 'v. t.', 'To put in opposition to; to manifest in an offensive way;\n   to threaten; as, to offer violence, attack, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Offer', 'v. i.', 'To present itself; to be at hand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Offer', 'v. i.', 'To make an attempt; to make an essay or a trial; -- used\n   with at.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Offer', 'v. t.', 'The act of offering, bringing forward, proposing, or\n   bidding; a proffer; a first advance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Offer', 'v. t.', 'That which is offered or brought forward; a proposal to\n   be accepted or rejected; a sum offered; a bid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Offer', 'v. t.', 'Attempt; endeavor; essay; as, he made an offer to catch\n   the ball.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Offerable', 'a.', 'Capable of being offered; suitable or worthy to be\n   offered.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Offerer', 'n.', 'One who offers; esp., one who offers something to God in\n   worship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Offering', 'n.', 'The act of an offerer; a proffering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Offering', 'n.', 'That which is offered, esp. in divine service; that which\n   is presented as an expiation or atonement for sin, or as a free gift; a\n   sacrifice; an oblation; as, sin offering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Offering', 'n.', 'A sum of money offered, as in church service; as, a\n   missionary offering. Specif.: (Ch. of Eng.) Personal tithes payable\n   according to custom, either at certain seasons as Christmas or Easter,\n   or on certain occasions as marriages or christenings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Offertories', 'pl. ', 'of Offertory');
  INSERT INTO words (word, word_type, description) VALUES  (    'Offertory', 'n.', 'The act of offering, or the thing offered.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Offertory', 'n.', 'An anthem chanted, or a voluntary played on the organ,\n   during the offering and first part of the Mass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Offertory', 'n.', 'That part of the Mass which the priest reads before\n   uncovering the chalice to offer up the elements for consecration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Offertory', 'n.', 'The oblation of the elements.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Offertory', 'n.', 'The Scripture sentences said or sung during the\n   collection of the offerings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Offertory', 'n.', 'The offerings themselves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Offerture', 'n.', 'Offer; proposal; overture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Offhand', 'a.', 'Instant; ready; extemporaneous; as, an offhand speech;\n   offhand excuses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Offhand', 'adv.', 'In an offhand manner; as, he replied offhand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Office', 'n.', 'That which a person does, either voluntarily or by\n   appointment, for, or with reference to, others; customary duty, or a\n   duty that arises from the relations of man to man; as, kind offices,\n   pious offices.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Office', 'n.', 'A special duty, trust, charge, or position, conferred by\n   authority and for a public purpose; a position of trust or authority;\n   as, an executive or judical office; a municipal office.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Office', 'n.', 'A charge or trust, of a sacred nature, conferred by God\n   himself; as, the office of a priest under the old dispensation, and\n   that of the apostles in the new.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Office', 'n.', 'That which is performed, intended, or assigned to be done,\n   by a particular thing, or that which anything is fitted to perform; a\n   function; -- answering to duty in intelligent beings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Office', 'n.', 'The place where a particular kind of business or service\n   for others is transacted; a house or apartment in which public officers\n   and others transact business; as, the registers office; a lawyers\n   office.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Office', 'n.', 'The company or corporation, or persons collectively, whose\n   place of business is in an office; as, I have notified the office.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Office', 'n.', 'The apartments or outhouses in which the domestics\n   discharge the duties attached to the service of a house, as kitchens,\n   pantries, stables, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Office', 'n.', 'Any service other than that of ordination and the Mass; any\n   prescribed religious service.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Office', 'v. t.', 'To perform, as the duties of an office; to discharge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Officeholder', 'n.', 'An officer, particularly one in the civil service; a\n   placeman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Officer', 'n.', 'One who holds an office; a person lawfully invested with\n   an office, whether civil, military, or ecclesiastical; as, a church\n   officer; a police officer; a staff officer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Officer', 'n.', 'Specifically, a commissioned officer, in distinction from\n   a warrant officer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Officered', 'imp. & p. p.', 'of Officer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Officering', 'p. pr. & vb. n.', 'of Officer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Officer', 'v. t.', 'To furnish with officers; to appoint officers over.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Officer', 'v. t.', 'To command as an officer; as, veterans from old\n   regiments officered the recruits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Official', 'n.', 'Of or pertaining to an office or public trust; as,\n   official duties, or routine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Official', 'n.', 'Derived from the proper office or officer, or from the\n   proper authority; made or communicated by virtue of authority; as, an\n   official statement or report.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Official', 'n.', 'Approved by authority; sanctioned by the pharmacopoeia;\n   appointed to be used in medicine; as, an official drug or preparation.\n   Cf. Officinal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Official', 'n.', 'Discharging an office or function.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Official', 'a.', 'One who holds an office; esp., a subordinate executive\n   officer or attendant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Official', 'a.', 'An ecclesiastical judge appointed by a bishop, chapter,\n   archdeacon, etc., with charge of the spiritual jurisdiction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Officialism', 'n.', 'The state of being official; a system of official\n   government; also, adherence to office routine; red-tapism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Officialily', 'n.', 'See Officialty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Officially', 'adv.', 'By the proper officer; by virtue of the proper\n   authority; in pursuance of the special powers vested in an officer or\n   office; as, accounts or reports officially vertified or rendered;\n   letters officially communicated; persons officially notified.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Officialty', 'n.', 'The charge, office, court, or jurisdiction of an\n   official.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Officiant', 'n.', 'The officer who officiates or performs an office, as the\n   burial office.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Officiary', 'a.', 'Of or pertaining to an office or an officer; official.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Officiated', 'imp. & p. p.', 'of Officiate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Officiating', 'p. pr. & vb. n.', 'of Officiate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Officiate', 'v. i.', 'To act as an officer in performing a duty; to\n   transact the business of an office or public trust; to conduct a public\n   service.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Officiate', 'v. t.', 'To discharge, perform, or supply, as an official duty\n   or function.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Officiator', 'n.', 'One who officiates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Officinal', 'a.', 'Used in a shop, or belonging to it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Officinal', 'a.', 'Kept in stock by apothecaries; -- said of such drugs and\n   medicines as may be obtained without special preparation or\n   compounding; not magistral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Officious', 'a.', 'Pertaining to, or being in accordance with, duty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Officious', 'a.', 'Disposed to serve; kind; obliging.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Officious', 'a.', 'Importunately interposing services; intermeddling in\n   affairs in which one has no concern; meddlesome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Offing', 'n.', 'That part of the sea at a good distance from the shore, or\n   where there is deep water and no need of a pilot; also, distance from\n   the shore; as, the ship had ten miles offing; we saw a ship in the\n   offing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Offish', 'a.', 'Shy or distant in manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Offlet', 'n.', 'A pipe to let off water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Offscouring', 'n.', 'That which is scoured off; hence, refuse; rejected\n   matter; that which is vile or despised.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Offscum', 'n.', 'Removed scum; refuse; dross.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Offset', 'n.', 'In general, that which is set off, from, before, or\n   against, something');
  INSERT INTO words (word, word_type, description) VALUES  (    'Offset', 'n.', 'A short prostrate shoot, which takes root and produces a\n   tuft of leaves, etc. See Illust. of Houseleek.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Offset', 'n.', 'A sum, account, or value set off against another sum or\n   account, as an equivalent; hence, anything which is given in exchange\n   or retaliation; a set-off.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Offset', 'n.', 'A spur from a range of hills or mountains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Offset', 'n.', 'A horizontal ledge on the face of a wall, formed by a\n   diminution of its thickness, or by the weathering or upper surface of a\n   part built out from it; -- called also set-off.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Offset', 'n.', 'A short distance measured at right angles from a line\n   actually run to some point in an irregular boundary, or to some object.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Offset', 'n.', 'An abrupt bend in an object, as a rod, by which one part is\n   turned aside out of line, but nearly parallel, with the rest; the part\n   thus bent aside.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Offset', 'n.', 'A more or less distinct transfer of a printed page or\n   picture to the opposite page, when the pages are pressed together\n   before the ink is dry or when it is poor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Offset', 'imp. & p. p.', 'of Offset');
  INSERT INTO words (word, word_type, description) VALUES  (    'Offsetting', 'p. pr. & vb. n.', 'of Offset');
  INSERT INTO words (word, word_type, description) VALUES  (    'Offset', 'v. t.', 'To set off; to place over against; to balance; as, to\n   offset one account or charge against another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Offset', 'v. t.', 'To form an offset in, as in a wall, rod, pipe, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Offset', 'v. i.', 'To make an offset.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Offshoot', 'n.', 'That which shoots off or separates from a main stem,\n   channel, family, race, etc.; as, the offshoots of a tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Offshore', 'a.', 'From the shore; as, an offshore wind; an offshore signal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Offskip', 'n.', 'That part of a landscape which recedes from the spectator\n   into distance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Offspring', 'n.sing. & pl.', 'The act of production; generation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Offspring', 'n.sing. & pl.', 'That which is produced; a child or children;\n   a descendant or descendants, however remote from the stock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Offspring', 'n.sing. & pl.', 'Origin; lineage; family.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Offuscate', '', 'Alt. of Offuscation');
  INSERT INTO words (word, word_type, description) VALUES  (    'Offuscation', '', 'See Obfuscate, Obfuscation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oft', 'adv.', 'Often; frequently; not rarely; many times.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oft', 'a.', 'Frequent; often; repeated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Often', 'adv.', 'Frequently; many times; not seldom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Often', 'a.', 'Frequent; common; repeated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oftenness', 'n.', 'Frequency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oftensith', 'adv.', 'Frequently; often.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oftentide', 'adv.', 'Frequently; often.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oftentimes', 'adv.', 'Frequently; often; many times.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ofter', 'adv.', 'Compar. of Oft.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ofttimes', 'adv.', 'Frequently; often.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ogam', 'n.', 'Same as Ogham.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ogdoad', 'n.', 'A thing made up of eight parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ogdoastich', 'n.', 'A poem of eight lines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ogee', 'n.', 'A molding, the section of which is the form of the letter S,\n   with the convex part above; cyma reversa. See Illust. under Cyma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ogee', 'n.', 'Hence, any similar figure used for any purpose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ogeechee lime', '', 'The acid, olive-shaped, drupaceous fruit of a species\n   of tupelo (Nyssa capitata) which grows in swamps in Georgia and\n   Florida.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ogeechee lime', '', 'The tree which bears this fruit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ogganition', 'n.', 'Snarling; grumbling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ogham', 'n.', 'A particular kind of writing practiced by the ancient Irish,\n   and found in inscriptions on stones, metals, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ogive', 'n.', 'The arch or rib which crosses a Gothic vault diagonally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ogled', 'imp. & p. p.', 'of Ogle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ogling', 'p. pr. & vb. n.', 'of Ogle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ogle', 'v. t.', 'To view or look at with side glances, as in fondness, or\n   with a design to attract notice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ogle', 'n.', 'An amorous side glance or look.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ogler', 'n.', 'One who ogles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oglio', 'n.', 'See Olio.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ogre', 'n.', 'An imaginary monster, or hideous giant of fairy tales, who\n   lived on human beings; hence, any frightful giant; a cruel monster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ogreish', 'a.', 'Resembling an ogre; having the character or appearance of\n   an ogre; suitable for an ogre.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ogress', 'n.', 'A female ogre.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ogreism', 'n.', 'Alt. of Ogrism');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ogrism', 'n.', 'The character or manners of an ogre.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ogygian', 'a.', 'Of or pertaining to Ogyges, a mythical king of ancient\n   Attica, or to a great deluge in Attica in his days; hence, primeval; of\n   obscure antiquity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oh', 'interj.', 'An exclamation expressing various emotions, according to\n   the tone and manner, especially surprise, pain, sorrow, anxiety, or a\n   wish. See the Note under O.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ohm', 'n.', 'The standard unit in the measure of electrical resistance,\n   being the resistance of a circuit in which a potential difference of\n   one volt produces a current of one ampere. As defined by the\n   International Electrical Congress in 1893, and by United States\n   Statute, it is a resistance substantially equal to 109 units of\n   resistance of the C.G.S. system of electro-magnetic units, and is\n   represented by the resistance offered to an unvarying electric current\n   by a column of mercury at the temperature of melting ice 14.4521 grams\n   in mass, of a constant cross-sectional area, and of the length of 106.3\n   centimeters. As thus defined it is called the international ohm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oho', 'interj.', 'An exclamation of surprise, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-oid', '', 'A suffix or combining form meaning like, resembling, in the\n   form of; as in anthropoid, asteroid, spheroid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oidium', 'n.', 'A genus of minute fungi which form a floccose mass of\n   filaments on decaying fruit, etc. Many forms once referred to this\n   genus are now believed to be temporary conditions of fungi of other\n   genera, among them the vine mildew (Oidium Tuckeri), which has caused\n   much injury to grapes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oil', 'n.', 'Any one of a great variety of unctuous combustible substances,\n   not miscible with water; as, olive oil, whale oil, rock oil, etc. They\n   are of animal, vegetable, or mineral origin and of varied composition,\n   and they are variously used for food, for solvents, for anointing,\n   lubrication, illumination, etc. By extension, any substance of an oily\n   consistency; as, oil of vitriol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oiled', 'imp. & p. p.', 'of Oil');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oiling', 'p. pr. & vb. n.', 'of Oil');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oil', 'v. t.', 'To smear or rub over with oil; to lubricate with oil; to\n   anoint with oil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oilbird', 'n.', 'See Guacharo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oilcloth', 'n.', 'Cloth treated with oil or paint, and used for marking\n   garments, covering floors, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oiled', 'a.', 'Covered or treated with oil; dressed with, or soaked in,\n   oil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oiler', 'n.', 'One who deals in oils.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oiler', 'n.', 'One who, or that which, oils.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oilery', 'n.', 'The business, the place of business, or the goods, of a\n   maker of, or dealer in, oils.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oiliness', 'n.', 'The quality of being oily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oillet', 'n.', 'A small opening or loophole, sometimes circular, used in\n   mediaeval fortifications.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oillet', 'n.', 'A small circular opening, and ring of moldings surrounding\n   it, used in window tracery in Gothic architecture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oilmen', 'pl. ', 'of Oilman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oilman', 'n.', 'One who deals in oils; formerly, one who dealt in oils and\n   pickles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oilnut', 'n.', 'The buffalo nut. See Buffalo nut, under Buffalo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oilseed', 'n.', 'Seed from which oil is expressed, as the castor bean;\n   also, the plant yielding such seed. See Castor bean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oilseed', 'n.', 'A cruciferous herb (Camelina sativa).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oilseed', 'n.', 'The sesame.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oilskin', 'n.', 'Cloth made waterproof by oil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oilstone', 'n.', 'A variety of hone slate, or whetstone, used for whetting\n   tools when lubricated with oil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oily', 'superl.', 'Consisting of oil; containing oil; having the nature or\n   qualities of oil; unctuous; oleaginous; as, oily matter or substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oily', 'superl.', 'Covered with oil; greasy; hence, resembling oil; as, an\n   oily appearance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oily', 'superl.', 'Smoothly subservient; supple; compliant; plausible;\n   insinuating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oinement', 'n.', 'Ointment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oinomania', 'n.', 'See oenomania.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ointed', 'imp. & p. p.', 'of Oint');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ointing', 'p. pr & vb. n.', 'of Oint');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oint', 'v. t.', 'To anoint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ointment', 'n.', 'That which serves to anoint; any soft unctuous substance\n   used for smearing or anointing; an unguent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ojibways', 'n. pl.', 'Same as Chippeways.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ojo', 'n.', 'A spring, surrounded by rushes or rank grass; an oasis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oke', 'n.', 'A Turkish and Egyptian weight, equal to about 2/ pounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oke', 'n.', 'An Hungarian and Wallachian measure, equal to about 2/ pints.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Okenite', 'n.', 'A massive and fibrous mineral of a whitish color, chiefly\n   hydrous silicate of lime.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oker', 'n.', 'See Ocher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Okra', 'n.', 'An annual plant (Abelmoschus, / Hibiscus, esculentus), whose\n   green pods, abounding in nutritious mucilage, are much used for soups,\n   stews, or pickles; gumbo.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-ol', '', 'A suffix denoting that the substance in the name of which it\n   appears belongs to the series of alcohols or hydroxyl derivatives, as\n   carbinol, glycerol, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Olay', 'n. pl.', 'Palm leaves, prepared for being written upon with a style\n   pointed with steel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Old', 'n.', 'Open country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Old', 'superl.', 'Not young; advanced far in years or life; having lived\n   till toward the end of the ordinary term of living; as, an old man; an\n   old age; an old horse; an old tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Old', 'superl.', 'Not new or fresh; not recently made or produced; having\n   existed for a long time; as, old wine; an old friendship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Old', 'superl.', 'Formerly existing; ancient; not modern; preceding;\n   original; as, an old law; an old custom; an old promise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Old', 'superl.', 'Continued in life; advanced in the course of existence;\n   having (a certain) length of existence; -- designating the age of a\n   person or thing; as, an infant a few hours old; a cathedral centuries\n   old.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Old', 'superl.', 'Long practiced; hence, skilled; experienced; cunning; as,\n   an old offender; old in vice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Old', 'superl.', 'Long cultivated; as, an old farm; old land, as opposed to\n   new land, that is, to land lately cleared.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Old', 'superl.', 'Worn out; weakened or exhausted by use; past usefulness;\n   as, old shoes; old clothes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Old', 'superl.', 'More than enough; abundant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Old', 'superl.', 'Aged; antiquated; hence, wanting in the mental vigor or\n   other qualities belonging to youth; -- used disparagingly as a term of\n   reproach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Old', 'superl.', 'Old-fashioned; wonted; customary; as of old; as, the good\n   old times; hence, colloquially, gay; jolly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Old', 'superl.', 'Used colloquially as a term of cordiality and\n   familiarity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Olden', 'a.', 'Old; ancient; as, the olden time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Olden', 'v. i.', 'To grow old; to age.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Old-fashioned', 'a.', 'Formed according to old or obsolete fashion or\n   pattern; adhering to old customs or ideas; as, an old-fashioned dress,\n   girl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Old-gentlemanly', 'a.', 'Pertaining to an old gentleman, or like one.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oldish', 'a.', 'Somewhat old.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Old lang syne', '', 'See Auld lang syne.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Old-maidish', 'a.', 'Like an old maid; prim; precise; particular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Old-maidism', 'n.', 'The condition or characteristics of an old maid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oldness', 'n.', 'The state or quality of being old; old age.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oldster', 'n.', 'An old person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Old-womanish', 'a.', 'Like an old woman; anile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Olea', 'n.', 'A genus of trees including the olive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oleaceous', 'a.', 'Of, pertaining to, or resembling, a natural order of\n   plants (Oleaceae), mostly trees and shrubs, of which the olive is the\n   type. It includes also the ash, the lilac, the true jasmine, and fringe\n   tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oleaginous', 'a.', 'Having the nature or qualities of oil; oily; unctuous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oleaginousness', 'n.', 'Oiliness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oleamen', 'n.', 'A soft ointment prepared from oil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oleander', 'n.', 'A beautiful evergreen shrub of the Dogbane family, having\n   clusters of fragrant red or white flowers. It is native of the East\n   Indies, but the red variety has become common in the south of Europe.\n   Called also rosebay, rose laurel, and South-sea rose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oleandrine', 'n.', 'One of several alkaloids found in the leaves of the\n   oleander.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oleaster', 'n.', 'The wild olive tree (Olea Europea, var. sylvestris).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oleaster', 'n.', 'Any species of the genus Elaeagus. See Eleagnus. The\n   small silvery berries of the common species (Elaeagnus hortensis) are\n   called Trebizond dates, and are made into cakes by the Arabs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oleate', 'n.', 'A salt of oleic acid. Some oleates, as the oleate of\n   mercury, are used in medicine by way of inunction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Olecranal', 'a.', 'Of or pertaining to the olecranon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Olecranon', 'n.', 'The large process at the proximal end of the ulna which\n   projects behind the articulation with the humerus and forms the bony\n   prominence of the elbow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Olefiant', 'a.', 'Forming or producing an oil; specifically, designating a\n   colorless gaseous hydrocarbon called ethylene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Olefine', 'n.', 'Olefiant gas, or ethylene; hence, by extension, any one of\n   the series of unsaturated hydrocarbons of which ethylene is a type. See\n   Ethylene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oleic', 'a.', 'Pertaining to, derived from, or contained in, oil; as, oleic\n   acid, an acid of the acrylic acid series found combined with glyceryl\n   in the form of olein in certain animal and vegetable fats and oils,\n   such as sperm oil, olive oil, etc. At low temperatures the acid is\n   crystalline, but melts to an oily liquid above 14/ C.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oleiferous', 'a.', 'Producing oil; as, oleiferous seeds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Olein', 'n.', 'A fat, liquid at ordinary temperatures, but solidifying at\n   temperatures below 0 C., found abundantly in both the animal and\n   vegetable kingdoms (see Palmitin). It dissolves solid fats, especially\n   at 30-40 C. Chemically, olein is a glyceride of oleic acid; and, as\n   three molecules of the acid are united to one molecule of glyceryl to\n   form the fat, it is technically known as triolein. It is also called\n   elain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Olent', 'a.', 'Scented.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oleograph', 'n.', 'The form or figure assumed by a drop of oil when placed\n   upon water or some other liquid with which it does not mix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oleograph', 'n.', 'A picture produced in oils by a process analogous to\n   that of lithographic printing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oleomargarine', 'n.', 'A liquid oil made from animal fats (esp. beef fat)\n   by separating the greater portion of the solid fat or stearin, by\n   crystallization. It is mainly a mixture of olein and palmitin with some\n   little stearin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oleomargarine', 'n.', 'An artificial butter made by churning this oil with\n   more or less milk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oleometer', 'n.', 'An instrument for ascertaining the weight and purity of\n   oil; an elaiometer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oleone', 'n.', 'An oily liquid, obtained by distillation of calcium oleate,\n   and probably consisting of the ketone of oleic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oleoptene', 'n.', 'See Eleoptene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oleoresin', 'n.', 'A natural mixture of a terebinthinate oil and a resin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oleoresin', 'n.', 'A liquid or semiliquid preparation extracted (as from\n   capsicum, cubebs, or ginger) by means of ether, and consisting of fixed\n   or volatile oil holding resin in solution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oleose', 'a.', 'Alt. of Oleous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oleous', 'a.', 'Oily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oleosity', 'n.', 'The state or quality of being oily or fat; fatness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oleraceous', 'a.', 'Pertaining to pot herbs; of the nature or having the\n   qualities of herbs for cookery; esculent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Olf', 'n.', 'The European bullfinch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Olfaction', 'n.', 'The sense by which the impressions made on the olfactory\n   organs by the odorous particles in the atmosphere are perceived.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Olfactive', 'a.', 'See Olfactory, a.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Olfactor', 'n.', 'A smelling organ; a nose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Olfactory', 'a.', 'Of, pertaining to, or connected with, the sense of\n   smell; as, the olfactory nerves; the olfactory cells.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Olfactories', 'pl. ', 'of Olfactory');
  INSERT INTO words (word, word_type, description) VALUES  (    'Olfactory', 'n.', 'An olfactory organ; also, the sense of smell; -- usually\n   in the plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oliban', 'n.', 'See Olibanum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Olibanum', 'n.', 'The fragrant gum resin of various species of Boswellia;\n   Oriental frankincense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Olibene', 'n.', 'A colorless mobile liquid of a pleasant aromatic odor\n   obtained by the distillation of olibanum, or frankincense, and regarded\n   as a terpene; -- called also conimene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Olid', 'a.', 'Alt. of Olidous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Olidous', 'a.', 'Having a strong, disagreeable smell; fetid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Olifant', 'n.', 'An elephant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Olifant', 'n.', 'An ancient horn, made of ivory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oligandrous', 'a.', 'Having few stamens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oliganthous', 'a.', 'Having few flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oligarch', 'n.', 'A member of an oligarchy; one of the rulers in an\n   oligarchical government.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oligarchal', 'a.', 'Oligarchic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oligarchic', 'a.', 'Alt. of Oligarchical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oligarchical', 'a.', 'Of or pertaining to oligarchy, or government by a\n   few.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oligarchist', 'n.', 'An advocate or supporter of oligarchy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oligarchies', 'pl. ', 'of Oligarchy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oligarchy', 'n.', 'A form of government in which the supreme power is\n   placed in the hands of a few persons; also, those who form the ruling\n   few.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oligist', 'a.', 'Hematite or specular iron ore; -- prob. so called in\n   allusion to its feeble magnetism, as compared with magnetite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oligist', 'a.', 'Alt. of Oligistic');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oligistic', 'a.', 'Of or pertaining to hematite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oligo-', '', 'A combining form from Gr. /, few, little, small.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oligocene', 'a.', 'Of, pertaining to, or designating, certain strata which\n   occupy an intermediate position between the Eocene and Miocene periods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oligocene', 'n.', 'The Oligocene period. See the Chart of Geology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oligochaeta', 'n. pl.', 'An order of Annelida which includes the earthworms\n   and related species.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oligochete', 'a.', 'Of or pertaining to the Oligochaeta.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oligoclase', 'n.', 'A triclinic soda-lime feldspar. See Feldspar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oligomerous', 'a.', 'Having few members in each set of organs; as, an\n   oligomerous flower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oligomyold', 'a.', 'Having few or imperfect syringeal muscles; -- said of\n   some passerine birds (Oligomyodi).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oligopetalous', 'a.', 'Having few petals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oligosepalous', 'a.', 'Having few sepals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oligosiderite', 'n.', 'A meteorite characterized by the presence of but a\n   small amount of metallic iron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oligospermous', 'a.', 'Having few seeds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oligotokous', 'a.', 'Producing few young.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Olio', 'n.', 'A dish of stewed meat of different kinds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Olio', 'n.', 'A mixture; a medley.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Olio', 'n.', 'A collection of miscellaneous pieces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Olitory', 'a.', 'Of or pertaining to, or produced in, a kitchen garden;\n   used for kitchen purposes; as, olitory seeds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oliva', 'n.', 'A genus of polished marine gastropod shells, chiefly\n   tropical, and often beautifully colored.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Olivaceous', 'a.', 'Resembling the olive; of the color of the olive;\n   olive-green.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Olivary', 'a.', 'Like an olive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Olivaster', 'a.', 'Of the color of the olive; tawny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Olive', 'n.', 'A tree (Olea Europaea) with small oblong or elliptical\n   leaves, axillary clusters of flowers, and oval, one-seeded drupes. The\n   tree has been cultivated for its fruit for thousands of years, and its\n   branches are the emblems of peace. The wood is yellowish brown and\n   beautifully variegated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Olive', 'n.', 'The fruit of the olive. It has been much improved by\n   cultivation, and is used for making pickles. Olive oil is pressed from\n   its flesh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Olive', 'n.', 'Any shell of the genus Oliva and allied genera; -- so called\n   from the form. See Oliva.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Olive', 'n.', 'The oyster catcher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Olive', 'n.', 'The color of the olive, a peculiar dark brownish, yellowish,\n   or tawny green.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Olive', 'n.', 'One of the tertiary colors, composed of violet and green\n   mixed in equal strength and proportion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Olive', 'n.', 'An olivary body. See under Olivary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Olive', 'n.', 'A small slice of meat seasoned, rolled up, and cooked; as,\n   olives of beef or veal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Olive', 'a.', 'Approaching the color of the olive; of a peculiar dark\n   brownish, yellowish, or tawny green.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Olived', 'a.', 'Decorated or furnished with olive trees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Olivenite', 'n.', 'An olive-green mineral, a hydrous arseniate of copper;\n   olive ore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oliver', 'n.', 'An olive grove.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oliver', 'n.', 'An olive tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oliver', 'n.', 'A small tilt hammer, worked by the foot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oliverian', 'n.', 'An adherent of Oliver Cromwell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Olivewood', 'n.', 'The wood of the olive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Olivewood', 'n.', 'An Australian name given to the hard white wood of\n   certain trees of the genus Elaeodendron, and also to the trees\n   themselves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Olivil', 'n.', 'A white crystalline substance, obtained from an exudation\n   from the olive, and having a bitter-sweet taste and acid proporties.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Olivin', 'n.', 'A complex bitter gum, found on the leaves of the olive\n   tree; -- called also olivite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Olivine', 'n.', 'A common name of the yellowish green mineral chrysolite,\n   esp. the variety found in eruptive rocks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Olivite', 'n.', 'See Olivin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Olla', 'n.', 'A pot or jar having a wide mouth; a cinerary urn, especially\n   one of baked clay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Olla', 'n.', 'A dish of stewed meat; an olio; an olla-podrida.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Olla-podrida', 'n.', 'A favorite Spanish dish, consisting of a mixture of\n   several kinds of meat chopped fine, and stewed with vegetables.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Olla-podrida', 'n.', 'Any incongruous mixture or miscellaneous collection;\n   an olio.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ology', 'n.', 'A colloquial or humorous name for any science or branch of\n   knowledge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Olpe', 'n.', 'Originally, a leather flask or vessel for oils or liquids;\n   afterward, an earthenware vase or pitcher without a spout.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Olusatrum', 'n.', 'An umbelliferous plant, the common Alexanders of Western\n   Europe (Smyrnium Olusatrum).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Olympiad', 'n.', 'A period of four years, by which the ancient Greeks\n   reckoned time, being the interval from one celebration of the Olympic\n   games to another, beginning with the victory of Cor/bus in the foot\n   race, which took place in the year 776 b.c.; as, the era of the\n   olympiads.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Olympian', 'a.', 'Alt. of Olympic');
  INSERT INTO words (word, word_type, description) VALUES  (    'Olympic', 'a.', 'Of or pertaining to Olympus, a mountain of Thessaly,\n   fabled as the seat of the gods, or to Olympia, a small plain in Elis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Olympionic', 'n.', 'An ode in honor of a victor in the Olympic games.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-oma', '', 'A suffix used in medical terms to denote a morbid condition of\n   some part, usually some kind of tumor; as in fibroma, glaucoma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Omagra', 'n.', 'Gout in the shoulder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Omahas', 'n. pl.', 'A tribe of Indians who inhabited the south side of the\n   Missouri River. They are now partly civilized and occupy a reservation\n   in Nebraska.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Omander wood', '', 'The wood of Diospyros ebenaster, a kind of ebony found\n   in Ceylon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Omasum', 'n.', 'The third division of the stomach of ruminants. See\n   Manyplies, and Illust. under Ruminant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Omber', 'n.', 'Alt. of Ombre');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ombre', 'n.', 'A game at cards, borrowed from the Spaniards, and usually\n   played by three persons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ombre', 'n.', 'A large Mediterranean food fish (Umbrina cirrhosa): --\n   called also umbra, and umbrine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ombrometer', 'n.', 'An instrument for measuring the rain that falls; a rain\n   gauge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Omega', 'n.', 'The last letter of the Greek alphabet. See Alpha.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Omega', 'n.', 'The last; the end; hence, death.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Omegoid', 'a.', 'Having the form of the Greek capital letter Omega (/).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Omelet', 'n.', 'Eggs beaten up with a little flour, etc., and cooked in a\n   frying pan; as, a plain omelet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Omen', 'n.', 'An occurrence supposed to portend, or show the character of,\n   some future event; any indication or action regarded as a foreshowing;\n   a foreboding; a presage; an augury.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Omened', 'imp. & p. p.', 'of Omen');
  INSERT INTO words (word, word_type, description) VALUES  (    'Omening', 'p. pr. & vb. n.', 'of Omen');
  INSERT INTO words (word, word_type, description) VALUES  (    'Omen', 'v. t.', 'To divine or to foreshow by signs or portents; to have\n   omens or premonitions regarding; to predict; to augur; as, to omen ill\n   of an enterprise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Omened', 'a.', 'Attended by, or containing, an omen or omens; as,\n   happy-omened day.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Omental', 'a.', 'Of or pertaining to an omentum or the omenta.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Omenta', 'pl. ', 'of Omentum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Omentum', 'n.', 'A free fold of the peritoneum, or one serving to connect\n   viscera, support blood vessels, etc.; an epiploon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Omer', 'n.', 'A Hebrew measure, the tenth of an ephah. See Ephah.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Omiletical', 'a.', 'Homiletical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ominate', 'v. t. & i.', 'To presage; to foreshow; to foretoken.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Omination', 'n.', 'The act of ominating; presaging.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ominous', 'a.', 'Of or pertaining to an omen or to omens; being or\n   exhibiting an omen; significant; portentous; -- formerly used both in a\n   favorable and unfavorable sense; now chiefly in the latter; foreboding\n   or foreshowing evil; inauspicious; as, an ominous dread.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Omissible', 'a.', 'Capable of being omitted; that may be omitted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Omission', 'n.', 'The act of omitting; neglect or failure to do something\n   required by propriety or duty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Omission', 'n.', 'That which is omitted or is left undone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Omissive', 'a.', 'Leaving out; omitting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Omitted', 'imp. & p. p.', 'of Omit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Omitting', 'p. pr. & vb. n.', 'of Omit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Omit', 'v. t.', 'To let go; to leave unmentioned; not to insert or name; to\n   drop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Omit', 'v. t.', 'To pass by; to forbear or fail to perform or to make use\n   of; to leave undone; to neglect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Omittance', 'n.', 'The act of omitting, or the state of being omitted;\n   forbearance; neglect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Omitter', 'n.', 'One who omits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ommateal', 'a.', 'Of or pertaining to an ommateum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ommatea', 'pl. ', 'of Ommateum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ommateum', 'n.', 'A compound eye, as of insects and crustaceans.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ommatidia', 'pl. ', 'of Ommatidium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ommatidium', 'n.', 'One of the single eyes forming the compound eyes of\n   crustaceans, insects, and other invertebrates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Omni-', '', 'A combining form denoting all, every, everywhere; as in\n   omnipotent, all-powerful; omnipresent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Omnibus', 'n.', 'A long four-wheeled carriage, having seats for many\n   people; especially, one with seats running lengthwise, used in\n   conveying passengers short distances.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Omnibus', 'n.', 'A sheet-iron cover for articles in a leer or annealing\n   arch, to protect them from drafts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Omnicorporeal', 'a.', 'Comprehending or including all bodies; embracing all\n   substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Omniety', 'n.', 'That which is all-pervading or all-comprehensive; hence,\n   the Deity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Omnifarious', 'a.', 'Of all varieties, forms, or kinds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Omniferous', 'a.', 'All-bearing; producing all kinds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Omnific', 'a.', 'All-creating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Omniform', 'a.', 'Having every form or shape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Omniformity', 'n.', 'The condition or quality of having every form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Omnify', 'v. t.', 'To render universal; to enlarge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Omnigenous', 'a.', 'Consisting of all kinds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Omnigraph', 'n.', 'A pantograph.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Omniparient', 'a.', 'Producing or bringing forth all things; all-producing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Omniparity', 'n.', 'Equality in every part; general equality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Omniparous', 'a.', 'Producing all things; omniparient.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Omnipatient', 'a.', 'Capable of enduring all things.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Omnipercipience', 'n.', 'Alt. of Omnipercipiency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Omnipercipiency', 'n.', 'Perception of everything.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Omnipercipient', 'a.', 'Perceiving everything.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Omnipotence', 'n.', 'Alt. of Omnipotency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Omnipotency', 'n.', 'The state of being omnipotent; almighty power; hence,\n   one who is omnipotent; the Deity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Omnipotency', 'n.', 'Unlimited power of a particular kind; as, loves\n   omnipotence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Omnipotent', 'a.', 'Able in every respect and for every work; unlimited in\n   ability; all-powerful; almighty; as, the Being that can create worlds\n   must be omnipotent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Omnipotent', 'a.', 'Having unlimited power of a particular kind; as,\n   omnipotent love.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Omnipotently', 'adv.', 'In an omnipotent manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Omnipresence', 'n.', 'Presence in every place at the same time; unbounded\n   or universal presence; ubiquity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Omnipresency', 'n.', 'Omnipresence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Omnipresent', 'a.', 'Present in all places at the same time; ubiquitous;\n   as, the omnipresent Jehovah.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Omnipresential', 'a.', 'Implying universal presence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Omniprevalent', 'a.', 'Prevalent everywhere or in all things.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Omniscience', 'n.', 'The quality or state of being omniscient; -- an\n   attribute peculiar to God.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Omnisciency', 'n.', 'Omniscience.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Omniscient', 'a.', 'Having universal knowledge; knowing all things;\n   infinitely knowing or wise; as, the omniscient God.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Omniscious', 'a.', 'All-knowing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Omnispective', 'a.', 'Beholding everything; capable of seeing all things;\n   all-seeing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Omnium', 'n.', 'The aggregate value of the different stocks in which a loan\n   to government is now usually funded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Omnium-gatherum', 'n.', 'A miscellaneous collection of things or persons; a\n   confused mixture; a medley.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Omnivagant', 'a.', 'Wandering anywhere and everywhere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Omnivora', 'n. pl.', 'A group of ungulate mammals including the hog and the\n   hippopotamus. The term is also sometimes applied to the bears, and to\n   certain passerine birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Omnivorous', 'a.', 'All-devouring; eating everything indiscriminately; as,\n   omnivorous vanity; esp. (Zool.), eating both animal and vegetable food.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Omo-', '', 'A combining form used in anatomy to indicate connection with,\n   or relation to, the shoulder or the scapula.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Omohyoid', 'a.', 'Of or pertaining to the shoulder and the hyoid bone; as,\n   the omohyoid muscle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Omophagic', 'a.', 'Eating raw flesh; using uncooked meat as food; as,\n   omophagic feasts, rites.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Omoplate', 'n.', 'The shoulder blade, or scapula.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Omostegite', 'n.', 'The part of the carapace of a crustacean situated\n   behind the cervical groove.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Omosternal', 'a.', 'Of or pertaining to the omosternum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Omosternum', 'n.', 'The anterior element of the sternum which projects\n   forward from between the clavicles in many batrachians and is usually\n   tipped with cartilage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Omosternum', 'n.', 'In many mammals, an interarticular cartilage, or bone,\n   between the sternum and the clavicle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Omphacine', 'a.', 'Of, pertaining to, or expressed from, unripe fruit; as,\n   omphacine oil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Omphalic', 'a.', 'Of or pertaining to the umbilicus, or navel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Omphalo-', '', 'A combining form indicating connection with, or relation\n   to, the umbilicus, or navel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Omphalocele', 'n.', 'A hernia at the navel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Omphalode', 'n.', 'The central part of the hilum of a seed, through which\n   the nutrient vessels pass into the rhaphe or the chalaza; -- called\n   also omphalodium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Omphalomancy', 'n.', 'Divination by means of a childs navel, to learn how\n   many children the mother may have.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Omphalomesaraic', 'a.', 'Omphalomesenteric.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Omphalomesenteric', 'a.', 'Of or pertaining to the umbilicus and mesentery;\n   omphalomesaraic; as, the omphalomesenteric arteries and veins of a\n   fetus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Omphalopsychite', 'n.', 'A name of the Hesychasts, from their habit of\n   gazing upon the navel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Omphalopter', 'n.', 'Alt. of Omphaloptic');
  INSERT INTO words (word, word_type, description) VALUES  (    'Omphaloptic', 'n.', 'An optical glass that is convex on both sides.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Omphalos', 'n.', 'The navel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Omphalotomy', 'n.', 'The operation of dividing the navel-string.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Omy', 'a.', 'Mellow, as land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'On', 'prep.', 'The general signification of on is situation, motion, or\n   condition with respect to contact or support beneath');
  INSERT INTO words (word, word_type, description) VALUES  (    'On', 'prep.', 'At, or in contact with, the surface or upper part of a\n   thing, and supported by it; placed or lying in contact with the\n   surface; as, the book lies on the table, which stands on the floor of a\n   house on an island.');
  INSERT INTO words (word, word_type, description) VALUES  (    'On', 'prep.', 'To or against the surface of; -- used to indicate the motion\n   of a thing as coming or falling to the surface of another; as, rain\n   falls on the earth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'On', 'prep.', 'Denoting performance or action by contact with the surface,\n   upper part, or outside of anything; hence, by means of; with; as, to\n   play on a violin or piano. Hence, figuratively, to work on ones\n   feelings; to make an impression on the mind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'On', 'prep.', 'At or near; adjacent to; -- indicating situation, place, or\n   position; as, on the one hand, on the other hand; the fleet is on the\n   American coast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'On', 'prep.', 'In addition to; besides; -- indicating multiplication or\n   succession in a series; as, heaps on heaps; mischief on mischief; loss\n   on loss; thought on thought.');
  INSERT INTO words (word, word_type, description) VALUES  (    'On', 'prep.', 'Indicating dependence or reliance; with confidence in; as,\n   to depend on a person for assistance; to rely on; hence, indicating the\n   ground or support of anything; as, he will promise on certain\n   conditions; to bet on a horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'On', 'prep.', 'At or in the time of; during; as, on Sunday we abstain from\n   labor. See At (synonym).');
  INSERT INTO words (word, word_type, description) VALUES  (    'On', 'prep.', 'At the time of, conveying some notion of cause or motive;\n   as, on public occasions, the officers appear in full dress or uniform.\n   Hence, in consequence of, or following; as, on the ratification of the\n   treaty, the armies were disbanded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'On', 'prep.', 'Toward; for; -- indicating the object of some passion; as,\n   have pity or compassion on him.');
  INSERT INTO words (word, word_type, description) VALUES  (    'On', 'prep.', 'At the peril of, or for the safety of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'On', 'prep.', 'By virtue of; with the pledge of; -- denoting a pledge or\n   engagement, and put before the thing pledged; as, he affirmed or\n   promised on his word, or on his honor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'On', 'prep.', 'To the account of; -- denoting imprecation or invocation, or\n   coming to, falling, or resting upon; as, on us be all the blame; a\n   curse on him.');
  INSERT INTO words (word, word_type, description) VALUES  (    'On', 'prep.', 'In reference or relation to; as, on our part expect\n   punctuality; a satire on society.');
  INSERT INTO words (word, word_type, description) VALUES  (    'On', 'prep.', 'Of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'On', 'prep.', 'Occupied with; in the performance of; as, only three\n   officers are on duty; on a journey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'On', 'prep.', 'In the service of; connected with; of the number of; as, he\n   is on a newspaper; on a committee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'On', 'prep.', 'Forward, in progression; onward; -- usually with a verb of\n   motion; as, move on; go on.');
  INSERT INTO words (word, word_type, description) VALUES  (    'On', 'prep.', 'Forward, in succession; as, from father to son, from the son\n   to the grandson, and so on.');
  INSERT INTO words (word, word_type, description) VALUES  (    'On', 'prep.', 'In continuance; without interruption or ceasing; as, sleep\n   on, take your ease; say on; sing on.');
  INSERT INTO words (word, word_type, description) VALUES  (    'On', 'prep.', 'Adhering; not off; as in the phrase, \"He is neither on nor\n   off,\" that is, he is not steady, he is irresolute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'On', 'prep.', 'Attached to the body, as clothing or ornament, or for use.');
  INSERT INTO words (word, word_type, description) VALUES  (    'On', 'prep.', 'In progress; proceeding; as, a game is on.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Onagri', 'pl. ', 'of Onager');
  INSERT INTO words (word, word_type, description) VALUES  (    'Onagers', 'pl. ', 'of Onager');
  INSERT INTO words (word, word_type, description) VALUES  (    'Onager', 'n.', 'A military engine acting like a sling, which threw stones\n   from a bag or wooden bucket, and was operated by machinery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Onager', 'n.', 'A wild ass, especially the koulan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Onagga', 'n.', 'The dauw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Onagraceous', 'a.', 'Alt. of Onagrarieous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Onagrarieous', 'a.', 'Pertaining to, or resembling, a natural order of\n   plants (Onagraceae or Onagrarieae), which includes the fuchsia, the\n   willow-herb (Epilobium), and the evening primrose (/nothera).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Onanism', 'n.', 'Self-pollution; masturbation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Onappo', 'n.', 'A nocturnal South American monkey (Callithrix discolor),\n   noted for its agility; -- called also ventriloquist monkey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ince', 'n.', 'The ounce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Once', 'adv.', 'By limitation to the number one; for one time; not twice\n   nor any number of times more than one.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Once', 'adv.', 'At some one period of time; -- used indefinitely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Once', 'adv.', 'At any one time; -- often nearly equivalent to ever, if\n   ever, or whenever; as, once kindled, it may not be quenched.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oncidium', 'n.', 'A genus of tropical orchidaceous plants, the flower of\n   one species of which (O. Papilio) resembles a butterfly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oncograph', 'n.', 'An instrument for registering the changes observable\n   with an oncometer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oncometer', 'n.', 'An instrument for measuring the variations in size of\n   the internal organs of the body, as the kidney, spleen, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oncotomy', 'n.', 'The opening of an abscess, or the removal of a tumor,\n   with a cutting instrument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Onde', 'n.', 'Hatred; fury; envy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'On dit', '', 'They say, or it is said.');
  INSERT INTO words (word, word_type, description) VALUES  (    'On dit', 'n.', 'A flying report; rumor; as, it is a mere on dit.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-one', '', 'A suffix indicating that the substance, in the name of which it\n   appears, is a ketone; as, acetone.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-one', '', 'A termination indicating that the hydrocarbon to the name of\n   which it is affixed belongs to the fourth series of hydrocarbons, or\n   the third series of unsaturated hydrocarbonsl as, nonone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'One', 'a.', 'Being a single unit, or entire being or thing, and no more;\n   not multifold; single; individual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'One', 'a.', 'Denoting a person or thing conceived or spoken of\n   indefinitely; a certain. \"I am the sister of one Claudio\" [Shak.], that\n   is, of a certain man named Claudio.');
  INSERT INTO words (word, word_type, description) VALUES  (    'One', 'a.', 'Pointing out a contrast, or denoting a particular thing or\n   person different from some other specified; -- used as a correlative\n   adjective, with or without the.');
  INSERT INTO words (word, word_type, description) VALUES  (    'One', 'a.', 'Closely bound together; undivided; united; constituting a\n   whole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'One', 'a.', 'Single in kind; the same; a common.');
  INSERT INTO words (word, word_type, description) VALUES  (    'One', 'a.', 'Single; inmarried.');
  INSERT INTO words (word, word_type, description) VALUES  (    'One', 'n.', 'A single unit; as, one is the base of all numbers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'One', 'n.', 'A symbol representing a unit, as 1, or i.');
  INSERT INTO words (word, word_type, description) VALUES  (    'One', 'n.', 'A single person or thing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'One', 'indef. pron.', 'Any person, indefinitely; a person or body; as, what\n   one would have well done, one should do ones self.');
  INSERT INTO words (word, word_type, description) VALUES  (    'One', 'v. t.', 'To cause to become one; to gather into a single whole; to\n   unite; to assimilite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oneberry', 'n.', 'The herb Paris. See Herb Paris, under Herb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'One-hand', 'a.', 'Employing one hand; as, the one-hand alphabet. See\n   Dactylology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'One-horse', 'a.', 'Drawn by one horse; having but a single horse; as, a\n   one-horse carriage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'One-horse', 'a.', 'Second-rate; inferior; small.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oneidas', 'n. pl.', 'A tribe of Indians formerly inhabiting the region near\n   Oneida Lake in the State of New York, and forming part of the Five\n   Nations. Remnants of the tribe now live in New York, Canada, and\n   Wisconsin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oneirocritic', 'a.', 'An interpreter of dreams.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oneirocritic', 'a.', 'Alt. of Oneirocritical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oneirocritical', 'a.', 'Of or pertaining to the interpretation of dreams.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oneirocriticism', 'n.', 'Alt. of Oneirocritics');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oneirocritics', 'n.', 'The art of interpreting dreams.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oneiromancy', 'n.', 'Divination by means of dreams.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oneiroscopist', 'n.', 'One who interprets dreams.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oneiroscopy', 'n.', 'The interpretation of dreams.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oneliness', 'n.', 'The state of being one or single.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Onely', 'a.', 'See Only.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Onement', 'n.', 'The state of being at one or reconciled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oneness', 'n.', 'The state of being one; singleness in number;\n   individuality; unity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Onerary', 'a.', 'Fitted for, or carrying, a burden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Onerated', 'imp. & p. p.', 'of Onerate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Onerating', 'p. pr. & vb. n.', 'of Onerate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Onerate', 'v. t.', 'To load; to burden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oneration', 'n.', 'The act of loading.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Onerous', 'a.', 'Burdensome; oppressive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Onerously', 'adv.', 'In an onerous manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ones', 'adv.', 'Once.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oneself', 'pron.', 'A reflexive form of the indefinite pronoun one.\n   Commonly writen as two words, ones self.');
  INSERT INTO words (word, word_type, description) VALUES  (    'One-sided', 'a.', 'Having one side only, or one side prominent; hence,\n   limited to one side; partial; unjust; unfair; as, a one-sided view or\n   statement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'One-sided', 'a.', 'Growing on one side of a stem; as, one-sided flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Onethe', 'adv.', 'Scarcely. See Unnethe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ongoing', 'n.', 'The act of going forward; progress; (pl.) affairs;\n   business; current events.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Onguent', 'n.', 'An unguent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'On-hanger', 'n.', 'A hanger-on.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Onion', 'n.', 'A liliaceous plant of the genus Allium (A. cepa), having a\n   strong-flavored bulb and long hollow leaves; also, its bulbous root,\n   much used as an article of food. The name is often extended to other\n   species of the genus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Onirocritic', 'a.', 'See Oneirocritic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Onliness', 'n.', 'The state of being alone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Onloft', 'adv.', 'Aloft; above ground.');
  INSERT INTO words (word, word_type, description) VALUES  (    'On-looker', 'n.', 'A looker-on.');
  INSERT INTO words (word, word_type, description) VALUES  (    'On-looking', 'a.', 'Looking on or forward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Only', 'a.', 'One alone; single; as, the only man present; his only\n   occupation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Only', 'a.', 'Alone in its class; by itself; not associated with others of\n   the same class or kind; as, an only child.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Only', 'a.', 'Hence, figuratively: Alone, by reason of superiority;\n   preeminent; chief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Only', 'a.', 'In one manner or degree; for one purpose alone; simply;\n   merely; barely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Only', 'a.', 'So and no otherwise; no other than; exclusively; solely;\n   wholly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Only', 'a.', 'Singly; without more; as, only-begotten.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Only', 'a.', 'Above all others; particularly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Only', 'conj.', 'Save or except (that); -- an adversative used elliptically\n   with or without that, and properly introducing a single fact or\n   consideration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Onocerin', 'n.', 'A white crystalline waxy substance extracted from the\n   root of the leguminous plant Ononis spinosa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Onology', 'n.', 'Foolish discourse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Onomancy', 'n.', 'Divination by the letters of a name; nomancy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Onomantic', 'a.', 'Alt. of Onomantical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Onomantical', 'a.', 'Of or pertaining to onomancy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Onomastic', 'a.', 'Applied to a signature when the body of the instrument\n   is in anothers handwriting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Onomasticon', 'n.', 'A collection of names and terms; a dictionary;\n   specif., a collection of Greek names, with explanatory notes, made by\n   Julius Pollux about A.D.180.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Onomatechny', 'n.', 'Prognostication by the letters of a name.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Onomatologist', 'n.', 'One versed in the history of names.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Onomatology', 'n.', 'The science of names or of their classification.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Onomatope', 'n.', 'An imitative word; an onomatopoetic word.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Onomatopoeia', 'n.', 'The formation of words in imitation of sounds; a\n   figure of speech in which the sound of a word is imitative of the sound\n   of the thing which the word represents; as, the buzz of bees; the hiss\n   of a goose; the crackle of fire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Onomatopoeic', 'a.', 'Onomatopoetic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Onomatopoetic', 'a.', 'Of or pertaining to onomatopoeia; characterized by\n   onomatopoeia; imitative; as, an onomatopoetic writer or word.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Onomatopy', 'n.', 'Onomatopoeia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Onomomancy', 'n.', 'See Onomancy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Onondagas', 'n. pl.', 'A tribe of Indians formerly inhabiting what is now a\n   part of the State of New York. They were the central or head tribe of\n   the Five Nations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Onrush', 'n.', 'A rushing onward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Onset', 'n.', 'A rushing or setting upon; an attack; an assault; a\n   storming; especially, the assault of an army.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Onset', 'n.', 'A setting about; a beginning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Onset', 'n.', 'Anything set on, or added, as an ornament or as a useful\n   appendage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Onset', 'v. t.', 'To assault; to set upon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Onset', 'v. t.', 'To set about; to begin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Onslaught', 'n.', 'An attack; an onset; esp., a furious or murderous attack\n   or assault.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Onslaught', 'n.', 'A bloody fray or battle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Onstead', 'n.', 'A single farmhouse; a steading.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Onto', 'prep.', 'On the top of; upon; on. See On to, under On, prep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ontogenesis', 'n.', 'Alt. of Ontogeny');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ontogeny', 'n.', 'The history of the individual development of an organism;\n   the history of the evolution of the germ; the development of an\n   individual organism, -- in distinction from phylogeny, or evolution of\n   the tribe. Called also henogenesis, henogeny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ontogenetic', 'a.', 'Of or pertaining to ontogenesis; as, ontogenetic\n   phenomena.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ontogenic', 'a.', 'Ontogenetic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ontologic', 'a.', 'Ontological.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ontological', 'a.', 'Of or pertaining to ontology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ontologically', 'adv.', 'In an ontological manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ontologist', 'n.', 'One who is versed in or treats of ontology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ontology', 'n.', 'That department of the science of metaphysics which\n   investigates and explains the nature and essential properties and\n   relations of all beings, as such, or the principles and causes of\n   being.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Onus', 'n.', 'A burden; an obligation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Onward', 'a.', 'Moving in a forward direction; tending toward a\n   contemplated or desirable end; forward; as, an onward course, progress,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Onward', 'a.', 'Advanced in a forward direction or toward an end.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Onward', 'adv.', 'Toward a point before or in front; forward;\n   progressively; as, to move onward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Onwardness', 'n.', 'Progress; advancement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Onwards', 'adv.', 'Onward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ony', 'a.', 'Any.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Onycha', 'n.', 'An ingredient of the Mosaic incense, probably the operculum\n   of some kind of strombus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Onycha', 'n.', 'The precious stone called onyx.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Onychia', 'n.', 'A whitlow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Onychia', 'n.', 'An affection of a finger or toe, attended with ulceration\n   at the base of the nail, and terminating in the destruction of the\n   nail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Onychomancy', 'n.', 'Divination by the nails.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Onychophora', 'n. pl.', 'Malacopoda.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Onyx', 'n.', 'Chalcedony in parallel layers of different shades of color.\n   It is used for making cameos, the figure being cut in one layer with\n   the next as a ground.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oo', 'a.', 'One.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oo', 'n.', 'A beautiful bird (Moho nobilis) of the Hawaiian Islands. It\n   yields the brilliant yellow feathers formerly used in making the royal\n   robes. Called also yellow-tufted honeysucker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ooecia', 'pl. ', 'of Ooecium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ooecium', 'n.', 'One of the special zooids, or cells, of Bryozoa, destined\n   to receive and develop ova; an ovicell. See Bryozoa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oogenesis', 'n.', 'The development, or mode of origin, of the ova.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oogonia', 'pl. ', 'of Oogonium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oogoniums', 'pl. ', 'of Oogonium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oogonium', 'n.', 'A special cell in certain cryptogamous plants containing\n   oospheres, as in the rockweeds (Fucus), and the orders Vaucherieae and\n   Peronosporeae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ooidal', 'a.', 'Shaped like an egg.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ook', 'n.', 'Oak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oolite', 'n.', 'A variety of limestone, consisting of small round grains,\n   resembling the roe of a fish. It sometimes constitutes extensive beds,\n   as in the European Jurassic. See the Chart of Geology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oolitic', 'a.', 'Of or pertaining to oolite; composed of, or resembling,\n   oolite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oological', 'a.', 'Of or pertaining to oology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oologist', 'n.', 'One versed in oology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oology', 'n.', 'The science of eggs in relation to their coloring, size,\n   shape, and number.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oolong', 'n.', 'A fragrant variety of black tea having somewhat the flavor\n   of green tea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oomiac', 'n.', 'Alt. of Oomiak');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oomiak', 'n.', 'A long, broad boat used by the Eskimos.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oon', 'a.', 'One.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oones', 'adv.', 'Once.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oop', 'v. t.', 'To bind with a thread or cord; to join; to unite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oopack', 'n.', 'Alt. of Oopak');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oopak', 'n.', 'A kind of black tea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oophore', 'n.', 'An alternately produced form of certain cryptogamous\n   plants, as ferns, mosses, and the like, which bears antheridia and\n   archegonia, and so has sexual fructification, as contrasted with the\n   sporophore, which is nonsexual, but produces spores in countless\n   number. In ferns the oophore is a minute prothallus; in mosses it is\n   the leafy plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oophorectomy', 'n.', 'Ovariotomy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oophoric', 'a.', 'Having the nature of, or belonging to, an oophore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oophorida', 'pl. ', 'of Oophoridium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oophoridiums', 'pl. ', 'of Oophoridium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oophoridium', 'n.', 'The macrosporangium or case for the larger kind of\n   spores in heterosporous flowerless plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oophoritis', 'n.', 'Ovaritis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oophyte', 'n.', 'Any plant of a proposed class or grand division\n   (collectively termed oophytes or Oophyta), which have their sexual\n   reproduction accomplished by motile antherozoids acting on oospheres,\n   either while included in their oogonia or after exclusion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oophytic', 'a.', 'Of or pertaining to an oophyte.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oorial', 'n.', 'A wild, bearded sheep inhabiting the Ladakh mountains. It\n   is reddish brown, with a dark beard from the chin to the chest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oosperm', 'n.', 'The ovum, after fusion with the spermatozoon in\n   impregnation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oospere', 'n.', 'An unfertilized, rounded mass of protoplasm, produced in\n   an oogonium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oospere', 'n.', 'An analogous mass of protoplasm in the ovule of a\n   flowering plant; an embryonic vesicle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oosporangia', 'pl. ', 'of Oosporangium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oosporangiums', 'pl. ', 'of Oosporangium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oosporangium', 'n.', 'An oogonium; also, a case containing oval or rounded\n   spores of some other kind than oospores.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oospore', 'n.', 'A special kind of spore resulting from the fertilization\n   of an oosphere by antherozoids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oospore', 'n.', 'A fertilized oosphere in the ovule of a flowering plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oosporic', 'a.', 'Of or pertaining to an oospore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oostegite', 'n.', 'One of the plates which in some Crustacea inclose a\n   cavity wherein the eggs are hatched.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oothecae', 'pl. ', 'of Ootheca');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ootheca', 'n.', 'An egg case, especially those of many kinds of mollusks,\n   and of some insects, as the cockroach. Cf. Ooecium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ootooid', 'n.', 'Alt. of Ootocoid');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ootocoid', 'n.', 'A half oviparous, or an oviparous, mammal; a marsupial or\n   monotreme.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ootype', 'n.', 'The part of the oviduct of certain trematode worms in which\n   the ova are completed and furnished with a shell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ooze', 'n.', 'Soft mud or slime; earth so wet as to flow gently, or easily\n   yield to pressure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ooze', 'n.', 'Soft flow; spring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ooze', 'n.', 'The liquor of a tan vat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oozed', 'imp. & p. p.', 'of Ooze');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oozing', 'p. pr. & vb. n.', 'of Ooze');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ooze', 'n.', 'To flow gently; to percolate, as a liquid through the pores\n   of a substance or through small openings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ooze', 'n.', 'Fig.: To leak (out) or escape slowly; as, the secret oozed\n   out; his courage oozed out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ooze', 'v. t.', 'To cause to ooze.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oozoa', 'n. pl.', 'Same as Acrita.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oozy', 'a.', 'Miry; containing soft mud; resembling ooze; as, the oozy bed\n   of a river.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opacate', 'v. t.', 'To darken; to cloud.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opacity', 'n.', 'The state of being opaque; the quality of a body which\n   renders it impervious to the rays of light; want of transparency;\n   opaqueness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opacity', 'n.', 'Obscurity; want of clearness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opacous', 'a.', 'Opaque.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opacular', 'a.', 'Opaque.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opah', 'n.', 'A large oceanic fish (Lampris quttatus), inhabiting the\n   Atlantic Ocean. It is remarkable for its brilliant colors, which are\n   red, green, and blue, with tints of purple and gold, covered with round\n   silvery spots. Called also king of the herrings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opake', 'a.', 'See Opaque.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opal', 'n.', 'A mineral consisting, like quartz, of silica, but inferior to\n   quartz in hardness and specific gravity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opalesced', 'imp. & p. p.', 'of Opalesce');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opalescing', 'p. pr. & vb. n.', 'of Opalesce');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opalesce', 'v. i.', 'To give forth a play of colors, like the opal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opalescence', 'n.', 'A reflection of a milky or pearly light from the\n   interior of a mineral, as in the moonstone; the state or quality of\n   being opalescent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opalescent', 'a.', 'Reflecting a milky or pearly light from the interior;\n   having an opaline play of colors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opaline', 'a.', 'Of, pertaining to, or like, opal in appearance; having\n   changeable colors like those of the opal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opalized', 'imp. & p. p.', 'of Opalize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opalizing', 'p. pr. & vb. n.', 'of Opalize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opalize', 'v. t.', 'To convert into opal, or a substance like opal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opalotype', 'n.', 'A picture taken on \"milky\" glass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opaque', 'a.', 'Impervious to the rays of light; not transparent; as, an\n   opaque substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opaque', 'a.', 'Obscure; not clear; unintelligible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opaque', 'n.', 'That which is opaque; opacity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opaqueness', 'n.', 'The state or quality of being impervious to light;\n   opacity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ope', 'a.', 'Open.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ope', 'v. t. & i.', 'To open.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opeidoscope', 'n.', 'An instrument, consisting of a tube having one end\n   open and the other end covered with a thin flexible membrance to the\n   center of which is attached a small mirror. It is used for exhibiting\n   upon a screen, by means of rays reflected from the mirror, the\n   vibratory motions caused by sounds produced at the open end of the\n   tube, as by speaking or singing into it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opelet', 'n.', 'A bright-colored European actinian (Anemonia, / Anthea,\n   sulcata); -- so called because it does not retract its tentacles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Open', 'a.', 'Free of access; not shut up; not closed; affording\n   unobstructed ingress or egress; not impeding or preventing passage; not\n   locked up or covered over; -- applied to passageways; as, an open door,\n   window, road, etc.; also, to inclosed structures or objects; as, open\n   houses, boxes, baskets, bottles, etc.; also, to means of communication\n   or approach by water or land; as, an open harbor or roadstead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Open', 'a.', 'Free to be used, enjoyed, visited, or the like; not private;\n   public; unrestricted in use; as, an open library, museum, court, or\n   other assembly; liable to the approach, trespass, or attack of any one;\n   unprotected; exposed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Open', 'a.', 'Free or cleared of obstruction to progress or to view;\n   accessible; as, an open tract; the open sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Open', 'a.', 'Not drawn together, closed, or contracted; extended;\n   expanded; as, an open hand; open arms; an open flower; an open\n   prospect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Open', 'a.', 'Without reserve or false pretense; sincere; characterized by\n   sincerity; unfeigned; frank; also, generous; liberal; bounteous; --\n   applied to personal appearance, or character, and to the expression of\n   thought and feeling, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Open', 'a.', 'Not concealed or secret; not hidden or disguised; exposed to\n   view or to knowledge; revealed; apparent; as, open schemes or plans;\n   open shame or guilt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Open', 'a.', 'Not of a quality to prevent communication, as by closing\n   water ways, blocking roads, etc.; hence, not frosty or inclement; mild;\n   -- used of the weather or the climate; as, an open season; an open\n   winter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Open', 'a.', 'Not settled or adjusted; not decided or determined; not\n   closed or withdrawn from consideration; as, an open account; an open\n   question; to keep an offer or opportunity open.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Open', 'a.', 'Free; disengaged; unappropriated; as, to keep a day open for\n   any purpose; to be open for an engagement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Open', 'a.', 'Uttered with a relatively wide opening of the articulating\n   organs; -- said of vowels; as, the an far is open as compared with the\n   a in say.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Open', 'a.', 'Uttered, as a consonant, with the oral passage simply\n   narrowed without closure, as in uttering s.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Open', 'a.', 'Not closed or stopped with the finger; -- said of the string\n   of an instrument, as of a violin, when it is allowed to vibrate\n   throughout its whole length.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Open', 'a.', 'Produced by an open string; as, an open tone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Open', 'n.', 'Open or unobstructed space; clear land, without trees or\n   obstructions; open ocean; open water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opened', 'imp. & p. p.', 'of Open');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opening', 'p. pr. & vb. n.', 'of Open');
  INSERT INTO words (word, word_type, description) VALUES  (    'Open', 'v. t.', 'To make or set open; to render free of access; to unclose;\n   to unbar; to unlock; to remove any fastening or covering from; as, to\n   open a door; to open a box; to open a room; to open a letter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Open', 'v. t.', 'To spread; to expand; as, to open the hand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Open', 'v. t.', 'To disclose; to reveal; to interpret; to explain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Open', 'v. t.', 'To make known; to discover; also, to render available or\n   accessible for settlements, trade, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Open', 'v. t.', 'To enter upon; to begin; as, to open a discussion; to open\n   fire upon an enemy; to open trade, or correspondence; to open a case in\n   court, or a meeting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Open', 'v. t.', 'To loosen or make less compact; as, to open matted cotton\n   by separating the fibers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Open', 'v. i.', 'To unclose; to form a hole, breach, or gap; to be\n   unclosed; to be parted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Open', 'v. i.', 'To expand; to spread out; to be disclosed; as, the harbor\n   opened to our view.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Open', 'v. i.', 'To begin; to commence; as, the stock opened at par; the\n   battery opened upon the enemy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Open', 'v. i.', 'To bark on scent or view of the game.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Open-air', 'a.', 'Taking place in the open air; outdoor; as, an open-air\n   game or meeting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Openbill', 'n.', 'A bird of the genus Anastomus, allied to the stork; -- so\n   called because the two parts of the bill touch only at the base and\n   tip. One species inhabits India, another Africa. Called also open-beak.\n   See Illust. (m), under Beak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opener', 'n.', 'One who, or that which, opens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Open-eyed', 'a.', 'With eyes widely open; watchful; vigilant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Open-handed', 'a.', 'Generous; liberal; munificent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Open-headed', 'a.', 'Bareheaded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Open-hearted', 'a.', 'Candid; frank; generous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opening', 'n.', 'The act or process of opening; a beginning; commencement;\n   first appearance; as, the opening of a speech.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opening', 'n.', 'A place which is open; a breach; an aperture; a gap;\n   cleft, or hole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opening', 'n.', 'Hence: A vacant place; an opportunity; as, an opening for\n   business.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opening', 'n.', 'A thinly wooded space, without undergrowth, in the midst\n   of a forest; as, oak openings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Openly', 'adv.', 'In an open manner; publicly; not in private; without\n   secrecy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Openly', 'adv.', 'Without reserve or disguise; plainly; evidently.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Open-mouthed', 'a.', 'Having the mouth open; gaping; hence, greedy;\n   clamorous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Openness', 'n.', 'The quality or state of being open.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Openwork', 'n.', 'Anything so constructed or manufactured (in needlework,\n   carpentry, metal work, etc.) as to show openings through its substance;\n   work that is perforated or pierced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Openwork', 'n.', 'A quarry; an open cut.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opera', 'n.', 'A drama, either tragic or comic, of which music forms an\n   essential part; a drama wholly or mostly sung, consisting of\n   recitative, arials, choruses, duets, trios, etc., with orchestral\n   accompaniment, preludes, and interludes, together with appropriate\n   costumes, scenery, and action; a lyric drama.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opera', 'n.', 'The score of a musical drama, either written or in print; a\n   play set to music.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opera', 'n.', 'The house where operas are exhibited.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Operable', 'a.', 'Practicable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Operameter', 'n.', 'An instrument or machine for measuring work done,\n   especially for ascertaining the number of rotations made by a machine\n   or wheel in manufacturing cloth; a counter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Operance', 'n.', 'Alt. of Operancy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Operancy', 'n.', 'The act of operating or working; operation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Operand', 'n.', 'The symbol, quantity, or thing upon which a mathematical\n   operation is performed; -- called also faciend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Operant', 'a.', 'Operative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Operant', 'n.', 'An operative person or thing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Operated', 'imp. & p. p.', 'of Operate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Operating', 'p. pr. & vb. n.', 'of Operate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Operate', 'v. i.', 'To perform a work or labor; to exert power or strengh,\n   physical or mechanical; to act.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Operate', 'v. i.', 'To produce an appropriate physical effect; to issue in\n   the result designed by nature; especially (Med.), to take appropriate\n   effect on the human system.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Operate', 'v. i.', 'To act or produce effect on the mind; to exert moral\n   power or influence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Operate', 'v. i.', 'To perform some manual act upon a human body in a\n   methodical manner, and usually with instruments, with a view to restore\n   soundness or health, as in amputation, lithotomy, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Operate', 'v. i.', 'To deal in stocks or any commodity with a view to\n   speculative profits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Operate', 'v. t.', 'To produce, as an effect; to cause.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Operate', 'v. t.', 'To put into, or to continue in, operation or activity;\n   to work; as, to operate a machine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Operatic', 'a.', 'Alt. of Operatical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Operatical', 'a.', 'Of or pertaining to the opera or to operas;\n   characteristic of, or resembling, the opera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Operation', 'n.', 'The act or process of operating; agency; the exertion of\n   power, physical, mechanical, or moral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Operation', 'n.', 'The method of working; mode of action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Operation', 'n.', 'That which is operated or accomplished; an effect\n   brought about in accordance with a definite plan; as, military or naval\n   operations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Operation', 'n.', 'Effect produced; influence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Operation', 'n.', 'Something to be done; some transformation to be made\n   upon quantities, the transformation being indicated either by rules or\n   symbols.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Operation', 'n.', 'Any methodical action of the hand, or of the hand with\n   instruments, on the human body, to produce a curative or remedial\n   effect, as in amputation, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Operative', 'a.', 'Having the power of acting; hence, exerting force,\n   physical or moral; active in the production of effects; as, an\n   operative motive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Operative', 'a.', 'Producing the appropriate or designed effect;\n   efficacious; as, an operative dose, rule, or penalty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Operative', 'a.', 'Based upon, or consisting of, an operation or\n   operations; as, operative surgery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Operative', 'n.', 'A skilled worker; an artisan; esp., one who operates a\n   machine in a mill or manufactory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Operatively', 'adv.', 'In an operative manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Operator', 'n.', 'One who, or that which, operates or produces an effect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Operator', 'n.', 'One who performs some act upon the human body by means of\n   the hand, or with instruments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Operator', 'n.', 'A dealer in stocks or any commodity for speculative\n   purposes; a speculator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Operator', 'n.', 'The symbol that expresses the operation to be performed;\n   -- called also facient.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Operatory', 'n.', 'A laboratory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opercle', 'n.', 'Any one of the bony plates which support the gill covers\n   of fishes; an opercular bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opercle', 'n.', 'An operculum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opercula', 'n. pl.', 'See Operculum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oparcular', 'a.', 'Of, pertaining to, or like, an operculum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opercular', 'n.', 'The principal opercular bone or operculum of fishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Operculate', 'a.', 'Alt. of Operculated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Operculated', 'a.', 'Closed by a lid or cover, as the capsules of the\n   mosses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Operculated', 'a.', 'Having an operculum, or an apparatus for protecting\n   the gills; -- said of shells and of fishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Operculiferous', 'a.', 'Bearing an operculum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Operculiform', 'a.', 'Having the form of a lid or cover.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Operculigenous', 'a.', 'Producing an operculum; -- said of the foot, or\n   part of the foot, of certain mollusks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opercula', 'pl. ', 'of Operculum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Operculums', 'pl. ', 'of Operculum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Operculum', 'n.', 'The lid of a pitcherform leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Operculum', 'n.', 'The lid of the urnlike capsule of mosses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Operculum', 'n.', 'Any lidlike or operculiform process or part; as, the\n   opercula of a dental follicle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Operculum', 'n.', 'The fold of integument, usually supported by bony\n   plates, which protects the gills of most fishes and some amphibians;\n   the gill cover; the gill lid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Operculum', 'n.', 'The principal opercular bone in the upper and posterior\n   part of the gill cover.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Operculum', 'n.', 'The lid closing the aperture of various species of\n   shells, as the common whelk. See Illust. of Gastropoda.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Operculum', 'n.', 'Any lid-shaped structure closing the aperture of a tube\n   or shell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Operetta', 'n.', 'A short, light, musical drama.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Operose', 'a.', 'Wrought with labor; requiring labor; hence, tedious;\n   wearisome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Operosity', 'n.', 'Laboriousness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Operous', 'a.', 'Operose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opertaneous', 'a.', 'Concealed; private.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opetide', 'n.', 'Open time; -- applied to different things');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opetide', 'n.', 'The early spring, or the time when flowers begin opening.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opetide', 'n.', 'The time between Epiphany and Ash Wednesday wherein\n   marriages were formerly solemnized publicly in churches. [Eng.]');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opetide', 'n.', 'The time after harvest when the common fields are open to\n   all kinds of stock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ophelic', 'a.', 'Of, pertaining to, or designating, a substance (called\n   ophelic acid) extracted from a plant (Ophelia) of the Gentian family as\n   a bitter yellowish sirup, used in India as a febrifuge and tonic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ophicleide', 'n.', 'A large brass wind instrument, formerly used in the\n   orchestra and in military bands, having a loud tone, deep pitch, and a\n   compass of three octaves; -- now generally supplanted by bass and\n   contrabass tubas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ophidia', 'n. pl.', 'The order of reptiles which includes the serpents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ophidian', 'n.', 'One of the Ophidia; a snake or serpent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ophidian', 'a.', 'Of or pertaining to the Ophidia; belonging to serpents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ophidioid', 'a.', 'Of or pertaining to the Ophidiidae, a family of fishes\n   which includes many slender species.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ophidioid', 'n.', 'One of the Ophidiidae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ophidia', 'pl. ', 'of Ophidion');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ophidion', 'n.', 'The typical genus of ophidioid fishes. [Written also\n   Ophidium.] See Illust. under Ophidioid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ophidious', 'a.', 'Ophidian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ophiolatry', 'n.', 'The worship of serpents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ophiologic', 'a.', 'Alt. of Ophiological');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ophiological', 'a.', 'Of or pertaining to ophiology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ophiologist', 'n.', 'One versed in the natural history of serpents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ophiology', 'n.', 'That part of natural history which treats of the\n   ophidians, or serpents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ophiomancy', 'n.', 'Divination by serpents, as by their manner of eating,\n   or by their coils.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ophiomorpha', 'n. pl.', 'An order of tailless amphibians having a slender,\n   wormlike body with regular annulations, and usually with minute scales\n   imbedded in the skin. The limbs are rudimentary or wanting. It includes\n   the caecilians. Called also Gymnophiona and Ophidobatrachia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ophiomorphite', 'n.', 'An ammonite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ophiomorphous', 'a.', 'Having the form of a serpent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ophiophagous', 'a.', 'Feeding on serpents; -- said of certain birds and\n   reptiles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ophiophagus', 'n.', 'A genus of venomous East Indian snakes, which feed on\n   other snakes. Ophiophagus elaps is said to be the largest and most\n   deadly of poisonous snakes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ophite', 'a.', 'Of or pertaining to a serpent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ophite', 'n.', 'A greenish spotted porphyry, being a diabase whose pyroxene\n   has been altered to uralite; -- first found in the Pyreness. So called\n   from the colored spots which give it a mottled appearance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ophite', 'a.', 'A mamber of a Gnostic serpent-worshiping sect of the second\n   century.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ophiuchus', 'n.', 'A constellation in the Northern Hemisphere, delineated\n   as a man holding a serpent in his hands; -- called also Serpentarius.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ophiura', 'n.', 'A genus of ophiurioid starfishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ophiuran', 'a.', 'Of or pertaining to the Ophiurioidea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ophiuran', 'n.', 'One of the Ophiurioidea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ophiurid', 'n.', 'Same as Ophiurioid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ophiurida', 'n. pl.', 'Same as Ophiurioidea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ophiurioid', 'a.', 'Of or pertaining to the Ophiurioidea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ophiurioid', 'n.', 'One of the Ophiurioidea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ophiurioidea', 'n. pl.', 'Alt. of Ophiuroidea');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ophiuroidea', 'n. pl.', 'A class of star-shaped echinoderms having a\n   disklike body, with slender, articulated arms, which are not grooved\n   beneath and are often very fragile; -- called also Ophiuroida and\n   Ophiuridea. See Illust. under Brittle star.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ophryon', 'n.', 'The supraorbital point.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ophthalmia', 'n.', 'An inflammation of the membranes or coats of the eye or\n   of the eyeball.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ophthalmic', 'a.', 'Of, pertaining to, or in the region of, the eye;\n   ocular; as the ophthalmic, or orbitonasal, nerve, a division of the\n   trigeminal, which gives branches to the lachrymal gland, eyelids, nose,\n   and forehead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ophthalmite', 'n.', 'An eyestalk; the organ which bears the compound eyes\n   of decapod Crustacea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ophthalmological', 'a.', 'Of or pertaining to ophthalmology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ophthalmologist', 'n.', 'One skilled in ophthalmology; an oculist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ophthalmology', 'n.', 'The science which treats of the structure,\n   functions, and diseases of the eye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ophthalmometer', 'n.', 'An instrument devised by Helmholtz for measuring\n   the size of a reflected image on the convex surface of the cornea and\n   lens of the eye, by which their curvature can be ascertained.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ophthalmoscope', 'n.', 'An instrument for viewing the interior of the eye,\n   particularly the retina. Light is thrown into the eye by a mirror\n   (usually concave) and the interior is then examined with or without the\n   aid of a lens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ophthalmoscopy', 'n.', 'A branch of physiognomy which deduces the knowledge\n   of a persons temper and character from the appearance of the eyes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ophthalmoscopy', 'n.', 'Examination of the eye with the ophthalmoscope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ophthalmy', 'n.', 'Same as Ophthalmia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opianic', 'a.', 'Of, pertaining to, or designating, an organic acid\n   obtained by the oxidation of narcotine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opianine', 'n.', 'An alkaloid found in small quantity in opium. It is\n   identical with narcotine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opianyl', 'n.', 'Same as Meconin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opiate', 'n.', 'Originally, a medicine of a thicker consistence than sirup,\n   prepared with opium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opiate', 'n.', 'Any medicine that contains opium, and has the quality of\n   inducing sleep or repose; a narcotic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opiate', 'n.', 'Anything which induces rest or inaction; that which quiets\n   uneasiness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opiate', 'a.', 'Inducing sleep; somniferous; narcotic; hence, anodyne;\n   causing rest, dullness, or inaction; as, the opiate rod of Hermes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opiate', 'v. t.', 'To subject to the influence of an opiate; to put to\n   sleep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opiated', 'a.', 'Mixed with opiates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opiated', 'a.', 'Under the influence of opiates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opie', 'n.', 'Opium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opiferous', 'a.', 'Bringing help.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opifice', 'n.', 'Workmanship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opificer', 'n.', 'An artificer; a workman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opinable', 'a.', 'Capable of being opined or thought.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opination', 'n.', 'The act of thinking; a supposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opinative', 'a.', 'Obstinate in holding opinions; opinionated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opinator', 'n.', 'One fond of his own opinious; one who holds an opinion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opined', 'imp. & p. p.', 'of Opine');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opining', 'p. pr. & vb. n.', 'of Opine');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opine', 'v. t. & i.', 'To have an opinion; to judge; to think; to suppose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opiner', 'n.', 'One who opines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opiniaster', 'a.', 'Alt. of Opiniatre');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opiniatre', 'a.', 'Opinionated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opiniastrous', 'a.', 'See Opiniaster. [Obs.].');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opinlate', 'v. t.', 'To hold or maintain persistently.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opiniated', 'a.', 'Opinionated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opiniative', 'a.', 'Opinionative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opiniator', 'n.', 'Alt. of Opiniatre');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opiniatre', 'n.', 'One who is opinionated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opiniatre', 'a.', 'See Opiniaster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opiniatrety', 'n.', 'Obstinacy in opinious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opinicus', 'n.', 'An imaginary animal borne as a charge, having wings, an\n   eagles head, and a short tail; -- sometimes represented without wings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opining', 'n.', 'Opinion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opinion', 'n.', 'That which is opined; a notion or conviction founded on\n   probable evidence; belief stronger than impression, less strong than\n   positive knowledge; settled judgment in regard to any point of\n   knowledge or action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opinion', 'n.', 'The judgment or sentiment which the mind forms of persons\n   or things; estimation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opinion', 'n.', 'Favorable estimation; hence, consideration; reputation;\n   fame; public sentiment or esteem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opinion', 'n.', 'Obstinacy in holding to ones belief or impression;\n   opiniativeness; conceitedness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opinion', 'n.', 'The formal decision, or expression of views, of a judge,\n   an umpire, a counselor, or other party officially called upon to\n   consider and decide upon a matter or point submitted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opinion', 'v. t.', 'To opine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opinionable', 'a.', 'Being, or capable of being, a matter of opinion; that\n   can be thought; not positively settled; as, an opinionable doctrine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opinionate', 'a.', 'Opinionated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opinionated', 'a.', 'Stiff in opinion; firmly or unduly adhering to ones\n   own opinion or to preconceived notions; obstinate in opinion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opinionately', 'adv.', 'Conceitedly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opinionatist', 'n.', 'An opinionist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opinionative', 'a.', 'Unduly attached to ones own opinions; opinionated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opinionative', 'a.', 'Of the nature of an opinion; conjectured.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opinionator', 'n.', 'An opinionated person; one given to conjecture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opinioned', 'a.', 'Opinionated; conceited.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opinionist', 'n.', 'One fond of his own notions, or unduly attached to his\n   own opinions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opiparous', 'a.', 'Sumptuous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opisometer', 'n.', 'An instrument with a revolving wheel for measuring a\n   curved line, as on a map.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opisthion', 'n.', 'The middle of the posterior, or dorsal, margin of the\n   great foramen of the skull.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opisthobranchia', 'n. pl.', 'Alt. of Opisthobranchiata');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opisthobranchiata', 'n. pl.', 'A division of gastropod Mollusca, in which\n   the breathing organs are usually situated behind the heart. It includes\n   the tectibranchs and nudibranchs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opisthobranchiate', 'a.', 'Of or pertaining to the Opisthobranchiata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opisthobranchiate', 'n.', 'One of the Opisthobranchiata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opisthocoelian', 'a.', 'Alt. of Opisthocoelous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opisthocoelous', 'a.', 'Concave behind; -- applied especially to vertebrae\n   in which the anterior end of the centrum is convex and the posterior\n   concave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opisthodome', 'n.', 'A back chamber; especially, that part of the naos, or\n   cella, farthest from the main entrance, sometimes having an entrance of\n   its own, and often used as a treasury.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opisthoglypha', 'n. pl.', 'A division of serpents which have some of the\n   posterior maxillary teeth grooved for fangs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opisthography', 'n.', 'A writing upon the back of anything, as upon the\n   back of a leaf or sheet already written upon on one side.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opisthomi', 'n. pl.', 'An order of eellike fishes having the scapular arch\n   attached to the vertebrae, but not connected with the skull.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opisthopulmonate', 'a.', 'Having the pulmonary sac situated posteriorly; --\n   said of certain air-breathing Mollusca.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opisthotic', 'n.', 'The inferior and posterior of the three elements\n   forming the periotic bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opisthotonos', 'n.', 'A tetanic spasm in which the body is bent backwards\n   and stiffened.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opitulation', 'n.', 'The act of helping or aiding; help.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opium', 'n.', 'The inspissated juice of the Papaver somniferum, or white\n   poppy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ople tree', '', 'The witch-hazel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opobalsam', 'n.', 'Alt. of Opobalsamum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opobalsamum', 'n.', 'The old name of the aromatic resinous juice of the\n   Balsamodendron opobalsamum, now commonly called balm of Gilead. See\n   under Balm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opodeldoc', 'n.', 'A kind of plaster, said to have been invented by\n   Mindererus, -- used for external injuries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opodeldoc', 'n.', 'A saponaceous, camphorated liniment; a solution of soap\n   in alcohol, with the addition of camphor and essential oils; soap\n   liniment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opopanax', 'n.', 'The inspissated juice of an umbelliferous plant (the\n   Opoponax Chironum), brought from Turkey and the East Indies in loose\n   granules, or sometimes in larger masses, of a reddish yellow color,\n   with specks of white. It has a strong smell and acrid taste, and was\n   formerly used in medicine as an emmenagogue and antispasmodic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opossum', 'n.', 'Any American marsupial of the genera Didelphys and\n   Chironectes. The common species of the United States is Didelphys\n   Virginiana.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oppidan', 'a.', 'Of or pertaining to a town.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oppidan', 'n.', 'An inhabitant of a town.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oppidan', 'n.', 'A student of Eton College, England, who is not a Kings\n   scholar, and who boards in a private family.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oppignerate', 'v. i.', 'To pledge; to pawn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oppilated', 'imp. & p. p.', 'of Oppilate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oppilating', 'p. pr. & vb. n.', 'of Oppilate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oppilate', 'v. t.', 'To crowd together; to fill with obstructions; to block\n   up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oppilation', 'n.', 'The act of filling or crowding together; a stopping by\n   redundant matter; obstruction, particularly in the lower intestines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oppilative', 'a.', 'Obstructive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opplete', 'a.', 'Alt. of Oppleted');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oppleted', 'a.', 'Filled; crowded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oppletion', 'n.', 'The act of filling up, or the state of being filled up;\n   fullness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oppone', 'v. t.', 'To oppose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opponency', 'n.', 'The act of opening an academical disputation; the\n   proposition of objections to a tenet, as an exercise for a degree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opponent', 'a.', 'Situated in front; opposite; hence, opposing; adverse;\n   antagonistic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opponent', 'n.', 'One who opposes; an adversary; an antagonist; a foe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opponent', 'n.', 'One who opposes in a disputation, argument, or other\n   verbal controversy; specifically, one who attacks some theirs or\n   proposition, in distinction from the respondent, or defendant, who\n   maintains it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opportune', 'a.', 'Convenient; ready; hence, seasonable; timely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opportune', 'v. t.', 'To suit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opportunism', 'n.', 'The art or practice of taking advantage of\n   opportunities or circumstances, or of seeking immediate advantage with\n   little regard for ultimate consequences.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opportunist', 'n.', 'One who advocates or practices opportunism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opportunities', 'pl. ', 'of Opportunity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opportunity', 'n.', 'Fit or convenient time; a time or place favorable for\n   executing a purpose; a suitable combination of conditions; suitable\n   occasion; chance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opportunity', 'n.', 'Convenience of situation; fitness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opportunity', 'n.', 'Importunity; earnestness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opposability', 'n.', 'The condition or quality of being opposable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opposable', 'a.', 'Capable of being opposed or resisted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opposable', 'a.', 'Capable of being placed opposite something else; as, the\n   thumb is opposable to the forefinger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opposal', 'n.', 'Opposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opposed', 'imp. & p. p.', 'of Oppose');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opposing', 'p. pr. & vb. n.', 'of Oppose');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oppose', 'n.', 'To place in front of, or over against; to set opposite; to\n   exhibit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oppose', 'n.', 'To put in opposition, with a view to counterbalance or\n   countervail; to set against; to offer antagonistically.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oppose', 'n.', 'To resist or antagonize by physical means, or by arguments,\n   etc.; to contend against; to confront; to resist; to withstand; as, to\n   oppose the king in battle; to oppose a bill in Congress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oppose', 'n.', 'To compete with; to strive against; as, to oppose a rival\n   for a prize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oppose', 'v. i.', 'To be set opposite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oppose', 'v. i.', 'To act adversely or in opposition; -- with against or\n   to; as, a servant opposed against the act.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oppose', 'v. i.', 'To make objection or opposition in controversy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opposeless', 'a.', 'Not to be effectually opposed; irresistible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opposer', 'n.', 'One who opposes; an opponent; an antagonist; an adversary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opposite', 'a.', 'Placed over against; standing or situated over against or\n   in front; facing; -- often with to; as, a house opposite to the\n   Exchange.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opposite', 'a.', 'Applied to the other of two things which are entirely\n   different; other; as, the opposite sex; the opposite extreme.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opposite', 'a.', 'Extremely different; inconsistent; contrary; repugnant;\n   antagonistic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opposite', 'a.', 'Set over against each other, but separated by the whole\n   diameter of the stem, as two leaves at the same node.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opposite', 'a.', 'Placed directly in front of another part or organ, as a\n   stamen which stands before a petal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opposite', 'n.', 'One who opposes; an opponent; an antagonist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opposite', 'n.', 'That which is opposed or contrary; as, sweetness and its\n   opposite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oppositely', 'adv.', 'In a situation to face each other; in an opposite\n   manner or direction; adversely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oppositeness', 'n.', 'The quality or state of being opposite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oppositifolious', 'a.', 'Placed at the same node with a leaf, but separated\n   from it by the whole diameter of the stem; as, an oppositifolious\n   peduncle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opposition', 'n.', 'The act of opposing; an attempt to check, restrain, or\n   defeat; resistance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opposition', 'n.', 'The state of being placed over against; situation so as\n   to front something else.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opposition', 'n.', 'Repugnance; contrariety of sentiment, interest, or\n   purpose; antipathy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opposition', 'n.', 'That which opposes; an obstacle; specifically, the\n   aggregate of persons or things opposing; hence, in politics and\n   parliamentary practice, the party opposed to the party in power.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opposition', 'n.', 'The situation of a heavenly body with respect to\n   another when in the part of the heavens directly opposite to it;\n   especially, the position of a planet or satellite when its longitude\n   differs from that of the sun 180; -- signified by the symbol /; as, /\n   / /, opposition of Jupiter to the sun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opposition', 'n.', 'The relation between two propositions when, having the\n   same subject and predicate, they differ in quantity, or in quality, or\n   in both; or between two propositions which have the same matter but a\n   different form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oppositionist', 'n.', 'One who belongs to the opposition party.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oppositipetalous', 'a.', 'Placed in front of a petal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oppositisepalous', 'a.', 'Placed in front of a sepal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oppositive', 'a.', 'Capable of being put in opposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oppressed', 'imp. & p. p.', 'of Oppress');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oppressing', 'p. pr. & vb. n.', 'of Oppress');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oppress', 'v. t.', 'To impose excessive burdens upon; to overload; hence,\n   to treat with unjust rigor or with cruelty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oppress', 'v. t.', 'To ravish; to violate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oppress', 'v. t.', 'To put down; to crush out; to suppress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oppress', 'v. t.', 'To produce a sensation of weight in (some part of the\n   body); as, my lungs are oppressed by the damp air; excess of food\n   oppresses the stomach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oppression', 'n.', 'The act of oppressing, or state of being oppressed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oppression', 'n.', 'That which oppresses; a hardship or injustice; cruelty;\n   severity; tyranny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oppression', 'n.', 'A sense of heaviness or obstruction in the body or\n   mind; depression; dullness; lassitude; as, an oppression of spirits; an\n   oppression of the lungs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oppression', 'n.', 'Ravishment; rape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oppressive', 'a.', 'Unreasonably burdensome; unjustly severe, rigorous, or\n   harsh; as, oppressive taxes; oppressive exactions of service; an\n   oppressive game law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oppressive', 'a.', 'Using oppression; tyrannical; as, oppressive authority\n   or commands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oppressive', 'a.', 'Heavy; overpowering; hard to be borne; as, oppressive\n   grief or woe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oppressor', 'n.', 'One who oppresses; one who imposes unjust burdens on\n   others; one who harasses others with unjust laws or unreasonable\n   severity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oppressure', 'n.', 'Oppression.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opprobrious', 'a.', 'Expressive of opprobrium; attaching disgrace;\n   reproachful; scurrilous; as, opprobrious language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opprobrious', 'a.', 'Infamous; despised; rendered hateful; as, an\n   opprobrious name.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opprobrium', 'n.', 'Disgrace; infamy; reproach mingled with contempt;\n   abusive language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opprobry', 'n.', 'Opprobrium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oppugned', 'imp. & p. p.', 'of Oppugn');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oppugning', 'p pr. & vb. n.', 'of Oppugn');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oppugn', 'v. t.', 'To fight against; to attack; to be in conflict with; to\n   oppose; to resist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oppugnancy', 'n.', 'The act of oppugning; opposition; resistance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oppugnant', 'a.', 'Tending to awaken hostility; hostile; opposing; warring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oppugnant', 'n.', 'An opponent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oppugnation', 'n.', 'Opposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oppugner', 'n.', 'One who opposes or attacks; that which opposes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opsimathy', 'n.', 'Education late in life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opsiometer', 'n.', 'An instrument for measuring the limits of distincts\n   vision in different individuals, and thus determiming the proper focal\n   length of a lens for correcting imperfect sight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opsonation', 'n.', 'A catering; a buying of provisions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Optable', 'a.', 'That may be chosen; desirable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Optate', 'v. i.', 'To choose; to wish for; to desire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Optation', 'n.', 'The act of optating; a wish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Optative', 'a.', 'Expressing desire or wish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Optative', 'n.', 'Something to be desired.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Optative', 'n.', 'The optative mood; also, a verb in the optative mood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Optatively', 'adv.', 'In an optative manner; with the expression of desire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Optic', 'a.', 'The organ of sight; an eye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Optic', 'a.', 'An eyeglass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Optic', 'a.', 'Alt. of Optical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Optical', 'a.', 'Of or pertaining to vision or sight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Optical', 'a.', 'Of or pertaining to the eye; ocular; as, the optic nerves\n   (the first pair of cranial nerves) which are distributed to the retina.\n   See Illust. of Brain, and Eye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Optical', 'a.', 'Relating to the science of optics; as, optical works.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Optically', 'adv.', 'By optics or sight; with reference to optics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Optician', 'a.', 'One skilled in optics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Optician', 'a.', 'One who deals in optical glasses and instruments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Optics', 'n.', 'That branch of physical science which treats of the nature\n   and properties of light, the laws of its modification by opaque and\n   transparent bodies, and the phenomena of vision.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Optigraph', 'a.', 'A telescope with a diagonal eyepiece, suspended\n   vertically in gimbals by the object end beneath a fixed diagonal plane\n   mirror. It is used for delineating landscapes, by means of a pencil at\n   the eye end which leaves the delineation on paper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Optimacy', 'n.', 'Government by the nobility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Optimacy', 'n.', 'Collectively, the nobility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Optimate', 'a.', 'Of or pertaining to the nobility or aristocracy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Optimate', 'n.', 'A nobleman or aristocrat; a chief man in a state or city.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Optimates', 'n. pl.', 'The nobility or aristocracy of ancient Rome, as\n   opposed to the populares.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Optime', 'n.', 'One of those who stand in the second rank of honors,\n   immediately after the wranglers, in the University of Cambridge,\n   England. They are divided into senior and junior optimes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Optimism', 'n.', 'The opinion or doctrine that everything in nature, being\n   the work of God, is ordered for the best, or that the ordering of\n   things in the universe is such as to produce the highest good.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Optimism', 'n.', 'A disposition to take the most hopeful view; -- opposed\n   to pessimism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Optimist', 'n.', 'One who holds the opinion that all events are ordered for\n   the best.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Optimist', 'n.', 'One who looks on the bright side of things, or takes\n   hopeful views; -- opposed to pessimist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Optimistic', 'a.', 'Of or pertaining to optimism; tending, or conforming,\n   to the opinion that all events are ordered for the best.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Optimistic', 'a.', 'Hopeful; sanguine; as, an optimistic view.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Optimity', 'n.', 'The state of being best.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Option', 'n.', 'The power of choosing; the right of choice or election; an\n   alternative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Option', 'n.', 'The exercise of the power of choice; choice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Option', 'n.', 'A wishing; a wish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Option', 'n.', 'A right formerly belonging to an archbishop to select any\n   one dignity or benefice in the gift of a suffragan bishop consecrated\n   or confirmed by him, for bestowal by himself when next vacant; --\n   annulled by Parliament in 1845.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Option', 'n.', 'A stipulated privilege, given to a party in a time\n   contract, of demanding its fulfillment on any day within a specified\n   limit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Optional', 'a.', 'Involving an option; depending on the exercise of an\n   option; left to ones discretion or choice; not compulsory; as,\n   optional studies; it is optional with you to go or stay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Optional', 'n.', 'See Elective, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Optionally', 'adv.', 'In an optional manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Optocoele', 'n.', 'Alt. of Optocoelia');
  INSERT INTO words (word, word_type, description) VALUES  (    'Optocoelia', 'n.', 'The cavity of one of the optic lobes of the brain in\n   many animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Optogram', 'n.', 'An image of external objects fixed on the retina by the\n   photochemical action of light on the visual purple. See Optography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Optography', 'n.', 'The production of an optogram on the retina by the\n   photochemical action of light on the visual purple; the fixation of an\n   image in the eye. The object so photographed shows white on a purple or\n   red background. See Visual purple, under Visual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Optometer', 'n.', 'An instrument for measuring the distance of distinct\n   vision, mainly for the selection of eveglasses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opulence', 'n.', 'Wealth; riches; affluence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opulency', 'n.', 'See Opulence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opulent', 'a.', 'Having a large estate or property; wealthy; rich;\n   affluent; as, an opulent city; an opulent citizen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opuntia', 'n.', 'A genus of cactaceous plants; the prickly pear, or Indian\n   fig.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opera', 'pl. ', 'of Opus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opus', 'n.', 'A work; specif. (Mus.), a musical composition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opuscle', 'n.', 'Alt. of Opuscule');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opuscule', 'n.', 'A small or petty work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opuscula', 'pl. ', 'of Opusculum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opusculum', 'n.', 'An opuscule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Opye', 'n.', 'Opium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oquassa', 'n.', 'A small, handsome trout (Salvelinus oquassa), found in\n   some of the lakes in Maine; -- called also blueback trout.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-or', '', 'A noun suffix denoting an act; a state or quality; as in error,\n   fervor, pallor, candor, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-or', '', 'A noun suffix denoting an agent or doer; as in auditor, one who\n   hears; donor, one who gives; obligor, elevator. It is correlative to\n   -ee. In general -or is appended to words of Latin, and -er to those of\n   English, origin. See -er.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Or', 'conj.', 'A particle that marks an alternative; as, you may read or\n   may write, -- that is, you may do one of the things at your pleasure,\n   but not both. It corresponds to either. You may ride either to London\n   or to Windsor. It often connects a series of words or propositions,\n   presenting a choice of either; as, he may study law, or medicine, or\n   divinity, or he may enter into trade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Or', 'prep. & adv.', 'Ere; before; sooner than.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Or', 'n.', 'Yellow or gold color, -- represented in drawing or engraving by\n   small dots.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ora', 'n.', 'A money of account among the Anglo-Saxons, valued, in the\n   Domesday Book, at twenty pence sterling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orabassu', 'n.', 'A South American monkey of the genus Callithrix, esp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orach', 'n.', 'Alt. of Orache');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orache', 'n.', 'A genus (Atriplex) of herbs or low shrubs of the Goosefoot\n   family, most of them with a mealy surface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oracle', 'n.', 'The answer of a god, or some person reputed to be a god, to\n   an inquiry respecting some affair or future event, as the success of an\n   enterprise or battle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oracle', 'n.', 'Hence: The deity who was supposed to give the answer; also,\n   the place where it was given.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oracle', 'n.', 'The communications, revelations, or messages delivered by\n   God to the prophets; also, the entire sacred Scriptures -- usually in\n   the plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oracle', 'n.', 'The sanctuary, or Most Holy place in the temple; also, the\n   temple itself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oracle', 'n.', 'One who communicates a divine command; an angel; a prophet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oracle', 'n.', 'Any person reputed uncommonly wise; one whose decisions are\n   regarded as of great authority; as, a literary oracle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oracle', 'n.', 'A wise sentence or decision of great authority.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oracled', 'imp. & p. p.', 'of Oracle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oracling', 'p. pr. & vb. n.', 'of Oracle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oracle', 'v. i.', 'To utter oracles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oracular', 'a.', 'Of or pertaining to an oracle; uttering oracles;\n   forecasting the future; as, an oracular tongue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oracular', 'a.', 'Resembling an oracle in some way, as in solemnity,\n   wisdom, authority, obscurity, ambiguity, dogmatism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oraculous', 'a.', 'Oracular; of the nature of an oracle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oragious', 'a.', 'Stormy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oraison', 'n.', 'See Orison.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oral', 'a.', 'Uttered by the mouth, or in words; spoken, not written;\n   verbal; as, oral traditions; oral testimony; oral law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oral', 'a.', 'Of or pertaining to the mouth; surrounding or lining the\n   mouth; as, oral cilia or cirri.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orally', 'adv.', 'In an oral manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orally', 'adv.', 'By, with, or in, the mouth; as, to receive the sacrament\n   orally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orang', 'n.', 'See Orang-outang.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orange', 'n.', 'The fruit of a tree of the genus Citrus (C. Aurantium). It\n   is usually round, and consists of pulpy carpels, commonly ten in\n   number, inclosed in a leathery rind, which is easily separable, and is\n   reddish yellow when ripe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orange', 'n.', 'The tree that bears oranges; the orange tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orange', 'n.', 'The color of an orange; reddish yellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orange', 'a.', 'Of or pertaining to an orange; of the color of an orange;\n   reddish yellow; as, an orange ribbon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orangeade', 'n.', 'A drink made of orange juice and water, corresponding to\n   lemonade; orange sherbet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orangeat', 'n.', 'Candied orange peel; also, orangeade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orangeism', 'n.', 'Attachment to the principles of the society of\n   Orangemen; the tenets or practices of the Orangemen.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-men', 'pl. ', 'of Orangeman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orangeman', 'n.', 'One of a secret society, organized in the north of\n   Ireland in 1795, the professed objects of which are the defense of the\n   regning sovereign of Great Britain, the support of the Protestant\n   religion, the maintenance of the laws of the kingdom, etc.; -- so\n   called in honor of William, Prince of Orange, who became William III.\n   of England.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orangeroot', 'n.', 'An American ranunculaceous plant (Hidrastis\n   Canadensis), having a yellow tuberous root; -- also called yellowroot,\n   golden seal, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orangery', 'n.', 'A place for raising oranges; a plantation of orange\n   trees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orangetawny', 'a. & n.', 'Deep orange-yellow; dark yellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orangite', '', 'An orange-yellow variety of the mineral thorite, found in\n   Norway.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orang-outang', 'n.', 'An arboreal anthropoid ape (Simia satyrus), which\n   inhabits Borneo and Sumatra. Often called simply orang.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orarian', 'a.', 'Of or pertaining to a coast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oration', 'n.', 'An elaborate discourse, delivered in public, treating an\n   important subject in a formal and dignified manner; especially, a\n   discourse having reference to some special occasion, as a funeral, an\n   anniversary, a celebration, or the like; -- distinguished from an\n   argument in court, a popular harangue, a sermon, a lecture, etc.; as,\n   Websters oration at Bunker Hill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oration', 'v. i.', 'To deliver an oration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orator', 'n.', 'A public speaker; one who delivers an oration; especially,\n   one distinguished for his skill and power as a public speaker; one who\n   is eloquent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orator', 'n.', 'In equity proceedings, one who prays for relief; a\n   petitioner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orator', 'n.', 'A plaintiff, or complainant, in a bill in chancery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orator', 'n.', 'An officer who is the voice of the university upon all\n   public occasions, who writes, reads, and records all letters of a\n   public nature, presents, with an appropriate address, those persons on\n   whom honorary degrees are to be conferred, and performs other like\n   duties; -- called also public orator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oratorial', 'a.', 'Oratorical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oratorian', 'a.', 'Oratorical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oratorian', 'n.', 'See Fathers of the Oratory, under Oratory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oratorical', 'a.', 'Of or pertaining to an orator or to oratory;\n   characterized by oratory; rhetorical; becoming to an orator; as, an\n   oratorical triumph; an oratorical essay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oratorio', 'n.', 'A more or less dramatic text or poem, founded on some\n   Scripture nerrative, or great divine event, elaborately set to music,\n   in recitative, arias, grand choruses, etc., to be sung with an\n   orchestral accompaniment, but without action, scenery, or costume,\n   although the oratorio grew out of the Mysteries and the Miracle and\n   Passion plays, which were acted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oratorio', 'n.', 'Performance or rendering of such a composition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oratorious', 'a.', 'Oratorical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oratorize', 'v. i.', 'To play the orator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oratories', 'pl. ', 'of Oratory');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oratory', 'n.', 'A place of orisons, or prayer; especially, a chapel or\n   small room set apart for private devotions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oratory', 'n.', 'The art of an orator; the art of public speaking in an\n   eloquent or effective manner; the exercise of rhetorical skill in oral\n   discourse; eloquence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oratress', 'n.', 'A woman who makes public addresses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oratrix', 'n.', 'A woman plaintiff, or complainant, in equity pleading.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orb', 'n.', 'A blank window or panel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orb', 'n.', 'A spherical body; a globe; especially, one of the celestial\n   spheres; a sun, planet, or star.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orb', 'n.', 'One of the azure transparent spheres conceived by the ancients\n   to be inclosed one within another, and to carry the heavenly bodies in\n   their revolutions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orb', 'n.', 'A circle; esp., a circle, or nearly circular orbit, described\n   by the revolution of a heavenly body; an orbit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orb', 'n.', 'A period of time marked off by the revolution of a heavenly\n   body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orb', 'n.', 'The eye, as luminous and spherical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orb', 'n.', 'A revolving circular body; a wheel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orb', 'n.', 'A sphere of action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orb', 'n.', 'Same as Mound, a ball or globe. See lst Mound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orb', 'n.', 'A body of soldiers drawn up in a circle, as for defense, esp.\n   infantry to repel cavalry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orbed', 'imp. & p. p.', 'of Orb');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orbing', 'p. pr. & vb. n.', 'of Orb');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orb', 'v. t.', 'To form into an orb or circle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orb', 'v. t.', 'To encircle; to surround; to inclose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orb', 'v. i.', 'To become round like an orb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orbate', 'a.', 'Bereaved; fatherless; childless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orbation', 'n.', 'The state of being orbate, or deprived of parents or\n   children; privation, in general; bereavement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orbed', 'a.', 'Having the form of an orb; round.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orbic', 'a.', 'Alt. of Orbical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orbical', 'a.', 'Spherical; orbicular; orblike; circular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orbicle', 'n.', 'A small orb, or sphere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orbicula', 'n.', 'Same as Discina.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orbicular', 'a.', 'Resembling or having the form of an orb; spherical;\n   circular; orbiculate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orbiculate', 'n.', 'That which is orbiculate; especially, a solid the\n   vertical section of which is oval, and the horizontal section circular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orbiculate', 'a.', 'Alt. of Orbiculated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orbiculated', 'a.', 'Made, or being, in the form of an orb; having a\n   circular, or nearly circular, or a spheroidal, outline.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orbiculation', 'n.', 'The state or quality of being orbiculate;\n   orbicularness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orbit', 'n.', 'The path described by a heavenly body in its periodical\n   revolution around another body; as, the orbit of Jupiter, of the earth,\n   of the moon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orbit', 'n.', 'An orb or ball.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orbit', 'n.', 'The cavity or socket of the skull in which the eye and its\n   appendages are situated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orbit', 'n.', 'The skin which surrounds the eye of a bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orbital', 'a.', 'Of or pertaining to an orbit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orbitar', 'a.', 'Orbital.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orbitary', 'a.', 'Situated around the orbit; as, the orbitary feathers of a\n   bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orbitelae', 'n. pl.', 'A division of spiders, including those that make\n   geometrical webs, as the garden spider, or Epeira.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orbitolites', 'n.', 'A genus of living Foraminifera, forming broad, thin,\n   circular disks, containing numerous small chambers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orbitonasal', 'a.', 'Of or pertaining to the orbit and the nose; as, the\n   orbitonasal, or ophthalmic, nerve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orbitosphenoid', 'a.', 'Of or pertaining to the sphenoid bone and the\n   orbit, or to the orbitosphenoid bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orbitosphenoid', 'n.', 'The orbitosphenoid bone, which is situated in the\n   orbit on either side of the presphenoid. It generally forms a part of\n   the sphenoid in the adult.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orbitosphenoidal', 'a.', 'Of or pertaining to the orbitosphenoid bone;\n   orbitosphenoid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orbituary', 'a.', 'Orbital.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orbitude', 'n.', 'Alt. of Orbity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orbity', 'n.', 'Orbation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orbulina', 'n.', 'A genus of minute living Foraminifera having a globular\n   shell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orby', 'a.', 'Orblike; having the course of an orb; revolving.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orc', 'n.', 'The grampus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orcadian', 'a.', 'Of or pertaining to the Orkney Islands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orcein', 'n.', 'A reddish brown amorphous dyestuff, /, obtained from orcin,\n   and forming the essential coloring matter of cudbear and archil. It is\n   closely related to litmus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orchal', 'n.', 'See Archil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orchanet', 'n.', 'Same as Alkanet, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orchard', 'n.', 'A garden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orchard', 'n.', 'An inclosure containing fruit trees; also, the fruit\n   trees, collectively; -- used especially of apples, peaches, pears,\n   cherries, plums, or the like, less frequently of nutbearing trees and\n   of sugar maple trees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orcharding', 'n.', 'The cultivation of orchards.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orcharding', 'n.', 'Orchards, in general.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orchardist', 'n.', 'One who cultivates an orchard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orchel', 'n.', 'Archil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orchesography', 'n.', 'A treatise upon dancing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orchester', 'n.', 'See Orchestra.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orchestian', 'n.', 'Any species of amphipod crustacean of the genus\n   Orchestia, or family Orchestidae. See Beach flea, under Beach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orchestra', 'n.', 'The space in a theater between the stage and the\n   audience; -- originally appropriated by the Greeks to the chorus and\n   its evolutions, afterward by the Romans to persons of distinction, and\n   by the moderns to a band of instrumental musicians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orchestra', 'n.', 'The place in any public hall appropriated to a band of\n   instrumental musicians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orchestra', 'n.', 'Loosely: A band of instrumental musicians performing in\n   a theater, concert hall, or other place of public amusement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orchestra', 'n.', 'Strictly: A band suitable for the performance of\n   symphonies, overtures, etc., as well as for the accompaniment of\n   operas, oratorios, cantatas, masses, and the like, or of vocal and\n   instrumental solos.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orchestra', 'n.', 'A band composed, for the largest part, of players of the\n   various viol instruments, many of each kind, together with a proper\n   complement of wind instruments of wood and brass; -- as distinguished\n   from a military or street band of players on wind instruments, and from\n   an assemblage of solo players for the rendering of concerted pieces,\n   such as septets, octets, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orchestra', 'n.', 'The instruments employed by a full band, collectively;\n   as, an orchestra of forty stringed instruments, with proper complement\n   of wind instruments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orchestral', 'a.', 'Of or pertaining to an orchestra; suitable for, or\n   performed in or by, an orchestra.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orchestration', 'n.', 'The arrangement of music for an orchestra;\n   orchestral treatment of a composition; -- called also instrumentation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orchestre', 'n.', 'See Orchestra.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orchestric', 'a.', 'Orchestral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orchestrion', 'n.', 'A large music box imitating a variety of orchestral\n   instruments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orchid', 'n.', 'Any plant of the order Orchidaceae. See Orchidaceous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orchidaceous', 'a.', 'Pertaining to, or resembling, a natural order\n   (Orchidaceae) of endogenous plants of which the genus Orchis is the\n   type. They are mostly perennial herbs having the stamens and pistils\n   united in a single column, and normally three petals and three sepals,\n   all adherent to the ovary. The flowers are curiously shaped, often\n   resembling insects, the odd or lower petal (called the lip) being\n   unlike the others, and sometimes of a strange and unexpected\n   appearance. About one hundred species occur in the United States, but\n   several thousand in the tropics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orchidean', 'a.', 'Orchidaceous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orchideous', 'a.', 'Same as Orchidaceous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orchidologist', 'n.', 'One versed in orchidology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orchidology', 'n.', 'The branch of botany which treats of orchids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orchil', 'n.', 'See Archil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orchilla weed', '', 'The lichen from which archil is obtained. See Archil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orchises', 'pl. ', 'of Orchis');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orchis', 'n.', 'A genus of endogenous plants growing in the North Temperate\n   zone, and consisting of about eighty species. They are perennial herbs\n   growing from a tuber (beside which is usually found the last years\n   tuber also), and are valued for their showy flowers. See Orchidaceous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orchis', 'n.', 'Any plant of the same family with the orchis; an orchid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orchitis', 'n.', 'Inflammation of the testicles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orchotomy', 'n.', 'The operation of cutting out or removing a testicle by\n   the knife; castration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orcin', 'n.', 'A colorless crystalline substance, C6H3.CH3.(OH)2, which is\n   obtained from certain lichens (Roccella, Lecanora, etc.), also from\n   extract of aloes, and artificially from certain derivatives of toluene.\n   It changes readily into orcein.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ord', 'n.', 'An edge or point; also, a beginning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ordained', 'imp. & p. p.', 'of Ordain');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ordaining', 'p. pr. & vb. n.', 'of Ordain');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ordain', 'v. t.', 'To set in order; to arrange according to rule; to\n   regulate; to set; to establish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ordain', 'v. t.', 'To regulate, or establish, by appointment, decree, or\n   law; to constitute; to decree; to appoint; to institute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ordain', 'v. t.', 'To set apart for an office; to appoint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ordain', 'v. t.', 'To invest with ministerial or sacerdotal functions; to\n   introduce into the office of the Christian ministry, by the laying on\n   of hands, or other forms; to set apart by the ceremony of ordination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ordainable', 'a.', 'Capable of being ordained; worthy to be ordained or\n   appointed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ordainer', 'n.', 'One who ordains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ordainment', 'n.', 'Ordination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ordal', 'n.', 'Ordeal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ordalian', 'a.', 'Of or pertaining to trial by ordeal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ordeal', 'n.', 'An ancient form of test to determine guilt or innocence, by\n   appealing to a supernatural decision, -- once common in Europe, and\n   still practiced in the East and by savage tribes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ordeal', 'n.', 'Any severe trial, or test; a painful experience.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ordeal', 'a.', 'Of or pertaining to trial by ordeal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Order', 'n.', 'Regular arrangement; any methodical or established\n   succession or harmonious relation; method; system');
  INSERT INTO words (word, word_type, description) VALUES  (    'Order', 'n.', 'Of material things, like the books in a library.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Order', 'n.', 'Of intellectual notions or ideas, like the topics of a\n   discource.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Order', 'n.', 'Of periods of time or occurrences, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Order', 'n.', 'Right arrangement; a normal, correct, or fit condition; as,\n   the house is in order; the machinery is out of order.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Order', 'n.', 'The customary mode of procedure; established system, as in\n   the conduct of debates or the transaction of business; usage; custom;\n   fashion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Order', 'n.', 'Conformity with law or decorum; freedom from disturbance;\n   general tranquillity; public quiet; as, to preserve order in a\n   community or an assembly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Order', 'n.', 'That which prescribes a method of procedure; a rule or\n   regulation made by competent authority; as, the rules and orders of the\n   senate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Order', 'n.', 'A command; a mandate; a precept; a direction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Order', 'n.', 'Hence: A commission to purchase, sell, or supply goods; a\n   direction, in writing, to pay money, to furnish supplies, to admit to a\n   building, a place of entertainment, or the like; as, orders for\n   blankets are large.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Order', 'n.', 'A number of things or persons arranged in a fixed or\n   suitable place, or relative position; a rank; a row; a grade;\n   especially, a rank or class in society; a group or division of men in\n   the same social or other position; also, a distinct character, kind, or\n   sort; as, the higher or lower orders of society; talent of a high\n   order.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Order', 'n.', 'A body of persons having some common honorary distinction or\n   rule of obligation; esp., a body of religious persons or aggregate of\n   convents living under a common rule; as, the Order of the Bath; the\n   Franciscan order.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Order', 'n.', 'An ecclesiastical grade or rank, as of deacon, priest, or\n   bishop; the office of the Christian ministry; -- often used in the\n   plural; as, to take orders, or to take holy orders, that is, to enter\n   some grade of the ministry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Order', 'n.', 'The disposition of a column and its component parts, and of\n   the entablature resting upon it, in classical architecture; hence (as\n   the column and entablature are the characteristic features of classical\n   architecture) a style or manner of architectural designing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Order', 'n.', 'An assemblage of genera having certain important characters\n   in common; as, the Carnivora and Insectivora are orders of Mammalia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Order', 'n.', 'The placing of words and members in a sentence in such a\n   manner as to contribute to force and beauty or clearness of expression.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Order', 'n.', 'Rank; degree; thus, the order of a curve or surface is the\n   same as the degree of its equation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ordered', 'imp. & p. p.', 'of Order');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ordering', 'p pr. & vb. n.', 'of Order');
  INSERT INTO words (word, word_type, description) VALUES  (    'Order', 'n.', 'To put in order; to reduce to a methodical arrangement; to\n   arrange in a series, or with reference to an end. Hence, to regulate;\n   to dispose; to direct; to rule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Order', 'n.', 'To give an order to; to command; as, to order troops to\n   advance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Order', 'n.', 'To give an order for; to secure by an order; as, to order a\n   carriage; to order groceries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Order', 'n.', 'To admit to holy orders; to ordain; to receive into the\n   ranks of the ministry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Order', 'v. i.', 'To give orders; to issue commands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orderable', 'a.', 'Capable of being ordered; tractable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orderer', 'n.', 'One who puts in order, arranges, methodizes, or regulates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orderer', 'n.', 'One who gives orders.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ordering', 'n.', 'Disposition; distribution; management.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orderless', 'a.', 'Being without order or regularity; disorderly; out of\n   rule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orderliness', 'n.', 'The state or quality of being orderly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orderly', 'a.', 'Conformed to order; in order; regular; as, an orderly\n   course or plan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orderly', 'a.', 'Observant of order, authority, or rule; hence, obedient;\n   quiet; peaceable; not unruly; as, orderly children; an orderly\n   community.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orderly', 'a.', 'Performed in good or established order; well-regulated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orderly', 'a.', 'Being on duty; keeping order; conveying orders.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orderly', 'adv.', 'According to due order; regularly; methodically; duly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orderlies', 'pl. ', 'of Orderly');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orderly', 'n.', 'A noncommissioned officer or soldier who attends a\n   superior officer to carry his orders, or to render other service.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orderly', 'n.', 'A street sweeper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ordinability', 'n.', 'Capability of being ordained or appointed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ordinable', 'a.', 'Capable of being ordained or appointed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ordinal', 'a.', 'Indicating order or succession; as, the ordinal numbers,\n   first, second, third, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ordinal', 'a.', 'Of or pertaining to an order.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ordinal', 'n.', 'A word or number denoting order or succession.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ordinal', 'n.', 'The book of forms for making, ordaining, and consecrating\n   bishops, priests, and deacons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ordinal', 'n.', 'A book containing the rubrics of the Mass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ordinalism', 'n.', 'The state or quality of being ordinal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ordinance', 'n.', 'Orderly arrangement; preparation; provision.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ordinance', 'n.', 'A rule established by authority; a permanent rule of\n   action; a statute, law, regulation, rescript, or accepted usage; an\n   edict or decree; esp., a local law enacted by a municipal government;\n   as, a municipal ordinance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ordinance', 'n.', 'An established rite or ceremony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ordinance', 'n.', 'Rank; order; station.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ordinance', 'n.', 'Ordnance; cannon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ordinand', 'n.', 'One about to be ordained.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ordinant', 'a.', 'Ordaining; decreeing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ordinant', 'n.', 'One who ordains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ordinarily', 'adv.', 'According to established rules or settled method; as\n   a rule; commonly; usually; in most cases; as, a winter more than\n   ordinarily severe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ordinary', 'a.', 'According to established order; methodical; settled;\n   regular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ordinary', 'a.', 'Common; customary; usual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ordinary', 'a.', 'Of common rank, quality, or ability; not distinguished by\n   superior excellence or beauty; hence, not distinguished in any way;\n   commonplace; inferior; of little merit; as, men of ordinary judgment;\n   an ordinary book.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ordinaries', 'pl. ', 'of Ordinary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ordinary', 'n.', 'An officer who has original jurisdiction in his own\n   right, and not by deputation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ordinary', 'n.', 'One who has immediate jurisdiction in matters\n   ecclesiastical; an ecclesiastical judge; also, a deputy of the bishop,\n   or a clergyman appointed to perform divine service for condemned\n   criminals and assist in preparing them for death.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ordinary', 'n.', 'A judicial officer, having generally the powers of a\n   judge of probate or a surrogate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ordinary', 'n.', 'The mass; the common run.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ordinary', 'n.', 'That which is so common, or continued, as to be\n   considered a settled establishment or institution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ordinary', 'n.', 'Anything which is in ordinary or common use.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ordinary', 'n.', 'A dining room or eating house where a meal is prepared\n   for all comers, at a fixed price for the meal, in distinction from one\n   where each dish is separately charged; a table dhote; hence, also, the\n   meal furnished at such a dining room.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ordinary', 'n.', 'A charge or bearing of simple form, one of nine or ten\n   which are in constant use. The bend, chevron, chief, cross, fesse,\n   pale, and saltire are uniformly admitted as ordinaries. Some\n   authorities include bar, bend sinister, pile, and others. See\n   Subordinary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ordinaryship', 'n.', 'The state of being an ordinary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ordinate', 'a.', 'Well-ordered; orderly; regular; methodical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ordinate', 'n.', 'The distance of any point in a curve or a straight line,\n   measured on a line called the axis of ordinates or on a line parallel\n   to it, from another line called the axis of abscissas, on which the\n   corresponding abscissa of the point is measured.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ordinate', 'v. t.', 'To appoint, to regulate; to harmonize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ordinately', 'adv.', 'In an ordinate manner; orderly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ordination', 'n.', 'The act of ordaining, appointing, or setting apart; the\n   state of being ordained, appointed, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ordination', 'n.', 'The act of setting apart to an office in the Christian\n   ministry; the conferring of holy orders.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ordination', 'n.', 'Disposition; arrangement; order.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ordinative', 'a.', 'Tending to ordain; directing; giving order.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ordinator', 'n.', 'One who ordains or establishes; a director.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ordnance', 'n.', 'Heavy weapons of warfare; cannon, or great guns, mortars,\n   and howitzers; artillery; sometimes, a general term for all weapons and\n   appliances used in war.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ordonnance', 'n.', 'The disposition of the parts of any composition with\n   regard to one another and the whole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ordonnant', 'a.', 'Of or pertaining to ordonnance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ordovian', 'a. & n.', 'Ordovician.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ordovician', 'a.', 'Of or pertaining to a division of the Silurian\n   formation, corresponding in general to the Lower Silurian of most\n   authors, exclusive of the Cambrian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ordovician', 'n.', 'The Ordovician formation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ordure', 'n.', 'Dung; excrement; faeces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ordure', 'n.', 'Defect; imperfection; fault.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ordurous', 'a.', 'Of or pertaining to ordure; filthy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ore', 'n.', 'Honor; grace; favor; mercy; clemency; happy augry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ore', 'n.', 'The native form of a metal, whether free and uncombined, as\n   gold, copper, etc., or combined, as iron, lead, etc. Usually the ores\n   contain the metals combined with oxygen, sulphur, arsenic, etc. (called\n   mineralizers).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ore', 'n.', 'A native metal or its compound with the rock in which it\n   occurs, after it has been picked over to throw out what is worthless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ore', 'n.', 'Metal; as, the liquid ore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oread', 'n.', 'One of the nymphs of mountains and grottoes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oreades', 'n. pl.', 'A group of butterflies which includes the satyrs. See\n   Satyr, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orectic', 'a.', 'Of or pertaining to the desires; hence, impelling to\n   gratification; appetitive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oregon grape', '', 'An evergreen species of barberry (Berberis Aquifolium),\n   of Oregon and California; also, its roundish, blue-black berries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oreide', 'n.', 'See Oroide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oreodon', 'n.', 'A genus of extinct herbivorous mammals, abundant in the\n   Tertiary formation of the Rocky Mountains. It is more or less related\n   to the camel, hog, and deer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oreodont', 'a.', 'Resembling, or allied to, the genus Oreodon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oreographic', 'a.', 'Of or pertaining to oreography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oreography', 'n.', 'The science of mountains; orography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oreoselin', 'n.', 'A white crystalline substance which is obtained\n   indirectly from the root of an umbelliferous plant (Imperatoria\n   Oreoselinum), and yields resorcin on decomposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oreosoma', 'n. pl.', 'A genus of small oceanic fishes, remarkable for the\n   large conical tubercles which cover the under surface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oreweed', 'n.', 'Same as Oarweed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orewood', 'n.', 'Same as Oarweed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orf', 'n.', 'Alt. of Orfe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orfe', 'n.', 'A bright-colored domesticated variety of the id. See Id.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orfgild', 'n.', 'Restitution for cattle; a penalty for taking away cattle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orfray', 'n.', 'The osprey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orfrays', 'n.', 'See Orphrey. [Obs.] Rom. of R.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orgal', 'n.', 'See Argol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Organ', 'n.', 'An instrument or medium by which some important action is\n   performed, or an important end accomplished; as, legislatures, courts,\n   armies, taxgatherers, etc., are organs of government.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Organ', 'n.', 'A natural part or structure in an animal or a plant, capable\n   of performing some special action (termed its function), which is\n   essential to the life or well-being of the whole; as, the heart, lungs,\n   etc., are organs of animals; the root, stem, foliage, etc., are organs\n   of plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Organ', 'n.', 'A component part performing an essential office in the\n   working of any complex machine; as, the cylinder, valves, crank, etc.,\n   are organs of the steam engine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Organ', 'n.', 'A medium of communication between one person or body and\n   another; as, the secretary of state is the organ of communication\n   between the government and a foreign power; a newspaper is the organ of\n   its editor, or of a party, sect, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Organ', 'n.', 'A wind instrument containing numerous pipes of various\n   dimensions and kinds, which are filled with wind from a bellows, and\n   played upon by means of keys similar to those of a piano, and sometimes\n   by foot keys or pedals; -- formerly used in the plural, each pipe being\n   considired an organ.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Organ', 'v. t.', 'To supply with an organ or organs; to fit with organs; to\n   organize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Organdie', 'n.', 'Alt. of Organdy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Organdy', 'n.', 'A kind of transparent light muslin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Organic', 'a.', 'Of or pertaining to an organ or its functions, or to\n   objects composed of organs; consisting of organs, or containing them;\n   as, the organic structure of animals and plants; exhibiting characters\n   peculiar to living organisms; as, organic bodies, organic life, organic\n   remains. Cf. Inorganic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Organic', 'a.', 'Produced by the organs; as, organic pleasure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Organic', 'a.', 'Instrumental; acting as instruments of nature or of art to\n   a certain destined function or end.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Organic', 'a.', 'Forming a whole composed of organs. Hence: Of or\n   pertaining to a system of organs; inherent in, or resulting from, a\n   certain organization; as, an organic government; his love of truth was\n   not inculcated, but organic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Organic', 'a.', 'Pertaining to, or denoting, any one of the large series of\n   substances which, in nature or origin, are connected with vital\n   processes, and include many substances of artificial production which\n   may or may not occur in animals or plants; -- contrasted with\n   inorganic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Organical', 'a.', 'Organic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Organically', 'adv.', 'In an organic manner; by means of organs or with\n   reference to organic functions; hence, fundamentally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Organicalness', 'n.', 'The quality or state of being organic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Organicism', 'n.', 'The doctrine of the localization of disease, or which\n   refers it always to a material lesion of an organ.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Organific', 'a.', 'Making an organic or organized structure; producing an\n   organism; acting through, or resulting from, organs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Organism', 'n.', 'Organic structure; organization.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Organism', 'n.', 'An organized being; a living body, either vegetable or\n   animal, compozed of different organs or parts with functions which are\n   separate, but mutually dependent, and essential to the life of the\n   individual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Organist', 'n.', 'One who plays on the organ.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Organist', 'n.', 'One of the priests who organized or sung in parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Organista', 'n.', 'Any one of several South American wrens, noted for the\n   sweetness of their song.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Organity', 'n.', 'Organism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Organizability', 'n.', 'Quality of being organizable; capability of being\n   organized.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Organizable', 'a.', 'Capable of being organized; esp. (Biol.), capable of\n   being formed into living tissue; as, organizable matter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Organization', 'n.', 'The act of organizing; the act of arranging in a\n   systematic way for use or action; as, the organization of an army, or\n   of a deliberative body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Organization', 'n.', 'The state of being organized; also, the relations\n   included in such a state or condition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Organization', 'n.', 'That which is organized; an organized existence; an\n   organism');
  INSERT INTO words (word, word_type, description) VALUES  (    'Organization', 'n.', 'an arrangement of parts for the performance of the\n   functions necessary to life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Organized', 'imp. & p. p.', 'of Organize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Organizing', 'p. pr. & vb. n.', 'of Organize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Organize', 'v. t.', 'To furnish with organs; to give an organic structure\n   to; to endow with capacity for the functions of life; as, an organized\n   being; organized matter; -- in this sense used chiefly in the past\n   participle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Organize', 'v. t.', 'To arrange or constitute in parts, each having a\n   special function, act, office, or relation; to systematize; to get into\n   working order; -- applied to products of the human intellect, or to\n   human institutions and undertakings, as a science, a government, an\n   army, a war, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Organize', 'v. t.', 'To sing in parts; as, to organize an anthem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Organizer', 'n.', 'One who organizes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Organling', 'n.', 'A large kind of sea fish; the orgeis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Organo-', '', 'A combining form denoting relation to, or connection with,\n   an organ or organs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Organogen', 'n.', 'A name given to any one of the four elements, carbon,\n   hydrogen, oxygen, and nitrogen, which are especially characteristic\n   ingredients of organic compounds; also, by extension, to other elements\n   sometimes found in the same connection; as sulphur, phosphorus, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Organogenesis', 'n.', 'The origin and development of organs in animals and\n   plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Organogenesis', 'n.', 'The germ history of the organs and systems of\n   organs, -- a branch of morphogeny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Organogenic', 'a.', 'Of or pertaining to organogenesis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Organogeny', 'n.', 'Organogenesis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Organographic', 'a.', 'Alt. of Organographical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Organographical', 'a.', 'Of or pertaining to organography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Organographist', 'n.', 'One versed in organography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Organography', 'n.', 'A description of the organs of animals or plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Organoleptic', 'a.', 'Making an impression upon an organ; plastic; -- said\n   of the effect or impression produced by any substance on the organs of\n   touch, taste, or smell, and also on the organism as a whole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Organological', 'a.', 'Of or relating to organology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Organology', 'n.', 'The science of organs or of anything considered as an\n   organic structure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Organology', 'n.', 'That branch of biology which treats, in particular, of\n   the organs of animals and plants. See Morphology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Organometallic', 'a.', 'Metalorganic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Organon', 'n.', 'Alt. of Organum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Organum', 'n.', 'An organ or instrument; hence, a method by which\n   philosophical or scientific investigation may be conducted; -- a term\n   adopted from the Aristotelian writers by Lord Bacon, as the title\n   (\"Novum Organon\") of part of his treatise on philosophical method.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Organonymy', 'n.', 'The designation or nomenclature of organs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Organophyly', 'n.', 'The tribal history of organs, -- a branch of\n   morphophyly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Organoplastic', 'a.', 'Having the property of producing the tissues or\n   organs of animals and plants; as, the organoplastic cells.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Organoscopy', 'n.', 'Phrenology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Organotrophic', 'a.', 'Relating to the creation, organization, and\n   nutrition of living organs or parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Organule', 'n.', 'One of the essential cells or elements of an organ. See\n   Sense organule, under Sense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Organy', 'n.', 'See Origan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Organzine', 'n.', 'A kind of double thrown silk of very fine texture, that\n   is, silk twisted like a rope with different strands, so as to increase\n   its strength.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orgasm', 'n.', 'Eager or immoderate excitement or action; the state of\n   turgescence of any organ; erethism; esp., the height of venereal\n   excitement in sexual intercourse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orgeat', 'n.', 'A sirup in which, formerly, a decoction of barley entered,\n   but which is now prepared with an emulsion of almonds, -- used to\n   flavor beverages or edibles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orgeis', 'n.', 'See Organling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orgiastic', 'a.', 'Pertaining to, or of the nature of, orgies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orgies', 'n. pl.', 'A sacrifice accompanied by certain ceremonies in honor\n   of some pagan deity; especially, the ceremonies observed by the Greeks\n   and Romans in the worship of Dionysus, or Bacchus, which were\n   characterized by wild and dissolute revelry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orgies', 'n. pl.', 'Drunken revelry; a carouse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orgillous', 'a.', 'Proud; haughty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orgue', 'n.', 'Any one of a number of long, thick pieces of timber, pointed\n   and shod with iron, and suspended, each by a separate rope, over a\n   gateway, to be let down in case of attack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orgue', 'n.', 'A piece of ordnance, consisting of a number of musket\n   barrels arranged so that a match or train may connect with all their\n   touchholes, and a discharge be secured almost or quite simultaneously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orgulous', 'a.', 'See Orgillous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orgies', 'pl. ', 'of Orgy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orgy', 'n.', 'A frantic revel; drunken revelry. See Orgies');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orgyia', 'n.', 'A genus of bombycid moths whose caterpillars (esp. those of\n   Orgyia leucostigma) are often very injurious to fruit trees and shade\n   trees. The female is wingless. Called also vaporer moth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oricalche', 'n.', 'See Orichalch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orichalceous', 'a.', 'Pertaining to, or resembling, orichalch; having a\n   color or luster like that of brass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orichalch', 'n.', 'A metallic substance, resembling gold in color, but\n   inferior in value; a mixed metal of the ancients, resembling brass; --\n   called also aurichalcum, orichalcum, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oriel', 'n.', 'A gallery for minstrels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oriel', 'n.', 'A small apartment next a hall, where certain persons were\n   accustomed to dine; a sort of recess.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oriel', 'n.', 'A bay window. See Bay window.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oriency', 'n.', 'Brightness or strength of color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orient', 'a.', 'Rising, as the sun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orient', 'a.', 'Eastern; oriental.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orient', 'a.', 'Bright; lustrous; superior; pure; perfect; pellucid; --\n   used of gems and also figuratively, because the most perfect jewels are\n   found in the East.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orient', 'n.', 'The part of the horizon where the sun first appears in the\n   morning; the east.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orient', 'n.', 'The countries of Asia or the East.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orient', 'n.', 'A pearl of great luster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orient', 'v. t.', 'To define the position of, in relation to the orient or\n   east; hence, to ascertain the bearings of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orient', 'v. t.', 'Fig.: To correct or set right by recurring to first\n   principles; to arrange in order; to orientate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oriental', 'a.', 'Of or pertaining to the orient or east; eastern;\n   concerned with the East or Orientalism; -- opposed to occidental; as,\n   Oriental countries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oriental', 'n.', 'A native or inhabitant of the Orient or some Eastern part\n   of the world; an Asiatic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oriental', 'n.', 'Eastern Christians of the Greek rite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orientalism', 'n.', 'Any system, doctrine, custom, expression, etc.,\n   peculiar to Oriental people.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orientalism', 'n.', 'Knowledge or use of Oriental languages, history,\n   literature, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orientalist', 'n.', 'An inhabitant of the Eastern parts of the world; an\n   Oriental.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orientalist', 'n.', 'One versed in Eastern languages, literature, etc.; as,\n   the Paris Congress of Orientalists.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orientality', 'n.', 'The quality or state of being oriental or eastern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orientalized', 'imp. & p. p.', 'of Orientalize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orientalizing', 'p. pr. & vb. n.', 'of Orientalize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orientalize', 'v. t.', 'to render Oriental; to cause to conform to Oriental\n   manners or conditions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orientated', 'imp. & p. p.', 'of Orientate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orientating', 'p. pr. & vb. n.', 'of Orientate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orientate', 'v. t.', 'To place or turn toward the east; to cause to assume\n   an easterly direction, or to veer eastward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orientate', 'v. t.', 'To arrange in order; to dispose or place (a body) so\n   as to show its relation to other bodies, or the relation of its parts\n   among themselves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orientate', 'v. i.', 'To move or turn toward the east; to veer from the\n   north or south toward the east.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orientation', 'n.', 'The act or process of orientating; determination of\n   the points of the compass, or the east point, in taking bearings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orientation', 'n.', 'The tendency of a revolving body, when suspended in a\n   certain way, to bring the axis of rotation into parallelism with the\n   earths axis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orientation', 'n.', 'An aspect or fronting to the east; especially (Arch.),\n   the placing of a church so that the chancel, containing the altar\n   toward which the congregation fronts in worship, will be on the east\n   end.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orientation', 'n.', 'Fig.: A return to first principles; an orderly\n   arrangement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orientness', 'n.', 'The quality or state of being orient or bright;\n   splendor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orifice', 'n.', 'A mouth or aperture, as of a tube, pipe, etc.; an opening;\n   as, the orifice of an artery or vein; the orifice of a wound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oriflamb', 'n.', 'Alt. of Oriflamme');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oriflamme', 'n.', 'The ancient royal standard of France.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oriflamme', 'n.', 'A standard or ensign, in battle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Origan', 'n.', 'Alt. of Origanum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Origanum', 'n.', 'A genus of aromatic labiate plants, including the sweet\n   marjoram (O. Marjorana) and the wild marjoram (O. vulgare).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Origenism', 'n.', 'The opinions of Origen of Alexandria, who lived in the\n   3d century, one of the most learned of the Greek Fathers. Prominent in\n   his teaching was the doctrine that all created beings, including Satan,\n   will ultimately be saved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Origenist', 'n.', 'A follower of Origen of Alexandria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Origin', 'n.', 'The first existence or beginning of anything; the birth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Origin', 'n.', 'That from which anything primarily proceeds; the fountain;\n   the spring; the cause; the occasion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Origin', 'n.', 'The point of attachment or end of a muscle which is fixed\n   during contraction; -- in contradistinction to insertion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Originable', 'a.', 'Capable of being originated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Original', 'a.', 'Pertaining to the origin or beginning; preceding all\n   others; first in order; primitive; primary; pristine; as, the original\n   state of man; the original laws of a country; the original inventor of\n   a process.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Original', 'a.', 'Not copied, imitated, or translated; new; fresh; genuine;\n   as, an original thought; an original process; the original text of\n   Scripture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Original', 'a.', 'Having the power to suggest new thoughts or combinations\n   of thought; inventive; as, an original genius.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Original', 'a.', 'Before unused or unknown; new; as, a book full of\n   original matter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Original', 'n.', 'Origin; commencement; source.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Original', 'n.', 'That which precedes all others of its class; archetype;\n   first copy; hence, an original work of art, manuscript, text, and the\n   like, as distinguished from a copy, translation, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Original', 'n.', 'An original thinker or writer; an originator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Original', 'n.', 'A person of marked eccentricity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Original', 'n.', 'The natural or wild species from which a domesticated or\n   cultivated variety has been derived; as, the wolf is thought by some to\n   be the original of the dog, the blackthorn the original of the plum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Originalist', 'n.', 'One who is original.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Originality', 'n.', 'The quality or state of being original.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Originally', 'adv.', 'In the original time, or in an original manner;\n   primarily; from the beginning or origin; not by derivation, or\n   imitation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Originally', 'adv.', 'At first; at the origin; at the time of formation or\n   costruction; as, a book originally written by another hand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Originalness', 'n.', 'The quality of being original; originality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Originant', 'a.', 'Originating; original.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Originary', 'a.', 'Causing existence; productive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Originary', 'a.', 'Primitive; primary; original.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Originated', 'imp. & p. p.', 'of Originate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Originating', 'p. pr. & vb. n.', 'of Originate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Originate', 'v. t.', 'To give an origin or beginning to; to cause to be; to\n   bring into existence; to produce as new.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Originate', 'v. i.', 'To take first existence; to have origin or beginning;\n   to begin to exist or act; as, the scheme originated with the governor\n   and council.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Origination', 'n.', 'The act or process of bringing or coming into\n   existence; first production.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Origination', 'n.', 'Mode of production, or bringing into being.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Originative', 'a.', 'Having power, or tending, to originate, or bring into\n   existence; originating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Originator', 'n.', 'One who originates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orillon', 'n.', 'A semicircular projection made at the shoulder of a\n   bastion for the purpose of covering the retired flank, -- found in old\n   fortresses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oriol', 'n.', 'See Oriel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oriole', 'n.', 'Any one of various species of Old World singing birds of\n   the family Oriolidae. They are usually conspicuously colored with\n   yellow and black. The European or golden oriole (Oriolus galbula, or O.\n   oriolus) has a very musical flutelike note.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oriole', 'n.', 'In America, any one of several species of the genus\n   Icterus, belonging to the family Icteridae. See Baltimore oriole, and\n   Orchard oriole, under Orchard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orion', 'n.', 'A large and bright constellation on the equator, between the\n   stars Aldebaran and Sirius. It contains a remarkable nebula visible to\n   the naked eye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oriskany', 'a.', 'Designating, or pertaining to, certain beds, chiefly\n   limestone, characteristic of the latest period of the Silurian age.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orismological', 'a.', 'Of or pertaining to orismology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orismology', 'n.', 'That departament of natural history which treats of\n   technical terms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orison', 'n.', 'A prayer; a supplication.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orisont', 'n.', 'Horizon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ork', 'n.', 'See Orc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orkneyan', 'a.', 'Of or pertaining to the Orkney islands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orle', 'n.', 'A bearing, in the form of a fillet, round the shield, within,\n   but at some distance from, the border.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orle', 'n.', 'The wreath, or chaplet, surmounting or encircling the helmet\n   of a knight and bearing the crest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orleans', 'n.', 'A cloth made of worsted and cotton, -- used for wearing\n   apparel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orleans', 'n.', 'A variety of the plum. See under Plum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orlo', 'n.', 'A wind instrument of music in use among the Spaniards.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orlop', 'n.', 'The lowest deck of a vessel, esp. of a ship of war,\n   consisting of a platform laid over the beams in the hold, on which the\n   cables are coiled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ormer', 'n.', 'An abalone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ormolu', 'n.', 'A variety of brass made to resemble gold by the use of less\n   zinc and more copper in its composition than ordinary brass contains.\n   Its golden color is often heightened by means of lacquer of some sort,\n   or by use of acids. Called also mosaic gold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ormuzd', 'n.', 'The good principle, or being, of the ancient Persian\n   religion. See Ahriman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orn', 'v. t.', 'To ornament; to adorn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ornament', 'n.', 'That which embellishes or adorns; that which adds grace\n   or beauty; embellishment; decoration; adornment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ornamented', 'imp. & p. p.', 'of Ornament');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ornamenting', 'p. pr. & vb. n.', 'of Ornament');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ornament', 'v. t.', 'To adorn; to deck; to embellish; to beautify; as, to\n   ornament a room, or a city.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ornamental', 'a.', 'Serving to ornament; characterized by ornament;\n   beautifying; embellishing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ornamentally', 'adv.', 'By way of ornament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ornamentation', 'n.', 'The act or art of ornamenting, or the state of being\n   ornamented.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ornamentation', 'n.', 'That which ornaments; ornament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ornamenter', 'n.', 'One who ornaments; a decorator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ornate', 'a.', 'Adorned; decorated; beautiful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ornate', 'a.', 'Finely finished, as a style of composition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ornate', 'v. t.', 'To adorn; to honor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ornately', 'adv.', 'In an ornate manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ornateness', 'n.', 'The quality of being ornate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ornature', 'n.', 'Decoration; ornamentation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ornithic', 'a.', 'Of or pertaining to birds; as, ornithic fossils.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ornithichnite', 'n.', 'The footmark of a bird occurring in strata of stone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ornithichnology', 'n.', 'The branch of science which treats of\n   ornithichnites.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ornitho-', '', 'A combining form fr. Gr. /, /, a bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ornithodelphia', 'n. pl.', 'Same as Monotremata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ornithoidichnite', 'n.', 'A fossil track resembling that of a bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ornitholite', 'n.', 'The fossil remains of a bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ornitholite', 'n.', 'A stone of various colors bearing the figures of\n   birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ornithologic', 'a.', 'Alt. of Ornithological');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ornithological', 'a.', 'Of or pertaining to ornithology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ornithologist', 'n.', 'One skilled in ornithology; a student of\n   ornithology; one who describes birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ornithology', 'n.', 'That branch of zoology which treats of the natural\n   history of birds and their classification.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ornithology', 'n.', 'A treatise or book on this science.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ornithomancy', 'n.', 'Divination by means of birds, their flight, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ornithon', 'n.', 'An aviary; a poultry house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ornithopappi', 'n. pl.', 'An extinct order of birds. It includes only the\n   Archaeopteryx.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ornithopoda', 'n. pl.', 'An order of herbivorous dinosaurs with birdlike\n   characteristics in the skeleton, esp. in the pelvis and hind legs,\n   which in some genera had only three functional toes, and supported the\n   body in walking as in Iguanodon. See Illust. in Appendix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ornithorhynchus', 'n.', 'See Duck mole, under Duck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ornithosauria', 'n. pl.', 'An order of extinct flying reptiles; -- called\n   also Pterosauria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ornithoscelida', 'n. pl.', 'A group of extinct Reptilia, intermediate in\n   structure (especially with regard to the pelvis) between reptiles and\n   birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ornithoscopy', 'n.', 'Observation of birds and their habits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ornithotomical', 'a.', 'Of or pertaining to ornithotomy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ornithotomist', 'n.', 'One who is skilled in ornithotomy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ornithotomy', 'n.', 'The anatomy or dissection of birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orographic', 'a.', 'Alt. of Orographical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orographical', 'a.', 'Of or pertaining to orography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orography', 'n.', 'That branch of science which treats of mountains and\n   mountain systems; orology; as, the orography of Western Europe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orohippus', 'n.', 'A genus of American Eocene mammals allied to the horse,\n   but having four toes in front and three behind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oroide', 'n.', 'An alloy, chiefly of copper and zinc or tin, resembling\n   gold in color and brilliancy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orological', 'a.', 'Of or pertaining to orology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orologist', 'n.', 'One versed in orology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orology', 'n.', 'The science or description of mountains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orotund', 'a.', 'Characterized by fullness, clearness, strength, and\n   smoothness; ringing and musical; -- said of the voice or manner of\n   utterance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orotund', 'n.', 'The orotund voice or utterance');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orotundity', 'n.', 'The orotund mode of intonation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orphaline', 'n.', 'See Orpheline.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orphan', 'n.', 'A child bereaved of both father and mother; sometimes,\n   also, a child who has but one parent living.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orphan', 'a.', 'Bereaved of parents, or (sometimes) of one parent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orphaned', 'imp. & p. p.', 'of Orphan');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orphaning', 'p. pr. & vb. n.', 'of Orphan');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orphan', 'v. t.', 'To cause to become an orphan; to deprive of parents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orphanage', 'n.', 'The state of being an orphan; orphanhood; orphans,\n   collectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orphanage', 'n.', 'An institution or asylum for the care of orphans.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orphancy', 'n.', 'Orphanhood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orphanet', 'n.', 'A little orphan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orphanhood', 'n.', 'The state or condition of being an orphan; orphanage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orphanism', 'n.', 'Orphanhood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orphanotrophism', 'n.', 'The care and support of orphans.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orphanotrophy', 'n.', 'A hospital for orphans.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orphanotrophy', 'n.', 'The act of supporting orphans.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orpharion', 'n.', 'An old instrument of the lute or cittern kind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orphean', 'a.', 'Of or pertaining to Orpheus, the mythic poet and musician;\n   as, Orphean strains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orpheline', 'n.', 'An orphan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orpheus', 'n.', 'The famous mythic Thracian poet, son of the Muse Calliope,\n   and husband of Eurydice. He is reputed to have had power to entrance\n   beasts and inanimate objects by the music of his lyre.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orphic', 'a.', 'Pertaining to Orpheus; Orphean; as, Orphic hymns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orphrey', 'n.', 'A band of rich embroidery, wholly or in part of gold,\n   affixed to vestments, especially those of ecclesiastics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orpiment', 'n.', 'Arsenic sesquisulphide, produced artificially as an\n   amorphous lemonyellow powder, and occurring naturally as a yellow\n   crystalline mineral; -- formerly called auripigment. It is used in\n   kings yellow, in white Indian fire, and in certain technical\n   processes, as indigo printing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orpin', 'n.', 'A yellow pigment of various degrees of intensity,\n   approaching also to red.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orpin', 'n.', 'The orpine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orpine', 'n.', 'A low plant with fleshy leaves (Sedum telephium), having\n   clusters of purple flowers. It is found on dry, sandy places, and on\n   old walls, in England, and has become naturalized in America. Called\n   also stonecrop, and live-forever.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orrach', 'n.', 'See Orach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orreries', 'pl. ', 'of Orrery');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orrery', 'n.', 'An apparatus which illustrates, by the revolution of balls\n   moved by wheelwork, the relative size, periodic motions, positions,\n   orbits, etc., of bodies in the solar system.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orris', 'n.', 'A plant of the genus Iris (I. Florentina); a kind of\n   flower-de-luce. Its rootstock has an odor resembling that of violets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orris', 'n.', 'A sort of gold or silver lace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orris', 'n.', 'A peculiar pattern in which gold lace or silver lace is\n   worked; especially, one in which the edges are ornamented with conical\n   figures placed at equal distances, with spots between them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orsedew', 'n.', 'Alt. of Orsedue');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orsedue', 'n.', 'Leaf metal of bronze; Dutch metal. See under Dutch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orseille', 'n.', 'See Archil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orsellic', 'a.', 'Pertaining to, or designating, an acid found in certain\n   lichens, and called also lecanoric acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orsellinic', 'a.', 'Pertaining to, or designating, an organic acid obtained\n   by a partial decomposition of orsellic acid as a white crystalline\n   substance, and related to protocatechuic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orts', 'pl. ', 'of Ort');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ort', 'n.', 'A morsel left at a meal; a fragment; refuse; -- commonly used\n   in the plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ortalidian', 'n.', 'Any one of numerous small two-winged flies of the\n   family Ortalidae. The larvae of many of these flies live in fruit;\n   those of others produce galls on various plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orthid', 'n.', 'A brachiopod shell of the genus Orthis, and allied genera,\n   of the family Orthidae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orthis', 'n.', 'An extinct genus of Brachiopoda, abundant in the Paleozoic\n   rocks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orthite', 'n.', 'A variety of allanite occurring in slender prismatic\n   crystals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ortho-', '', 'A combining form signifying straight, right, upright,\n   correct, regular; as, orthodromy, orthodiagonal, orthodox,\n   orthographic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ortho-', '', 'A combining form (also used adjectively)');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ortho-', '', 'The one of several acids of the same element (as the\n   phosphoric acids), which actually occurs with the greatest number of\n   hydroxyl groups; as, orthophosphoric acid. Cf. Normal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ortho-', '', 'Connection with, or affinity to, one variety of isomerism,\n   characteristic of the benzene compounds; -- contrasted with meta- or\n   para-; as, the ortho position; hence, designating any substance showing\n   such isomerism; as, an ortho compound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orthocarbonic', 'a.', 'Designating a complex ether, C.(OC2H5)4, which is\n   obtained as a liquid of a pleasant ethereal odor by means of\n   chlorpicrin, and is believed to be a derivative of the hypothetical\n   normal carbonic acid, C.(OH)4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orthocenter', 'n.', 'That point in which the three perpendiculars let fall\n   from the angles of a triangle upon the opposite sides, or the sides\n   produced, mutually intersect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orthoceras', 'n.', 'An extinct genus of Paleozoic Cephalopoda, having a\n   long, straight, conical shell. The interior is divided into numerous\n   chambers by transverse septa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orthoceratite', 'n.', 'An orthoceras; also, any fossil shell allied to\n   Orthoceras.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orthoclase', 'n.', 'Common or potash feldspar crystallizing in the\n   monoclinic system and having two cleavages at right angles to each\n   other. See Feldspar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orthoclastic', 'a.', 'Breaking in directions at right angles to each other;\n   -- said of the monoclinic feldspars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orthodiagonal', 'n.', 'The diagonal or lateral axis in a monoclinic crystal\n   which is at right angles with the vertical axis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orthodome', 'n.', 'See the Note under Dome, 4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orthodox', 'a.', 'Sound in opinion or doctrine, especially in religious\n   doctrine; hence, holding the Christian faith; believing the doctrines\n   taught in the Scriptures; -- opposed to heretical and heterodox; as, an\n   orthodox Christian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orthodox', 'a.', 'According or congruous with the doctrines of Scripture,\n   the creed of a church, the decree of a council, or the like; as, an\n   orthodox opinion, book, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orthodox', 'a.', 'Approved; conventional.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orthodoxal', 'a.', 'Pertaining to, or evincing, orthodoxy; orthodox.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orthodoxality', 'n.', 'Orthodoxness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orthodoxally', 'adv.', 'Orthodoxly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orthodoxastical', 'a.', 'Orthodox.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orthodoxical', 'a.', 'Pertaining to, or evincing, orthodoxy; orthodox.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orthodoxly', 'adv.', 'In an orthodox manner; with soundness of faith.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orthodoxness', 'n.', 'The quality or state of being orthodox; orthodoxy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orthodoxy', 'n.', 'Soundness of faith; a belief in the doctrines taught in\n   the Scriptures, or in some established standard of faith; -- opposed to\n   heterodoxy or to heresy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orthodoxy', 'n.', 'Consonance to genuine Scriptural doctrines; -- said of\n   moral doctrines and beliefs; as, the orthodoxy of a creed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orthodoxy', 'n.', 'By extension, said of any correct doctrine or belief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orthodromic', 'a.', 'Of or pertaining to orthodromy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orthodromics', 'n.', 'The art of sailing in a direct course, or on the arc\n   of a great circle, which is the shortest distance between any two\n   points on the surface of the globe; great-circle sailing; orthodromy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orthodromy', 'n.', 'The act or art of sailing on a great circle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orthoepic', 'a.', 'Alt. of Orthoepical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orthoepical', 'a.', 'Of or pertaining to orthoepy, or correct\n   pronunciation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orthoepist', 'n.', 'One who is skilled in orthoepy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orthoepy', 'n.', 'The art of uttering words correctly; a correct\n   pronunciation of words; also, mode of pronunciation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orthogamy', 'n.', 'Direct fertilization in plants, as when the pollen\n   fertilizing the ovules comes from the stamens of the same blossom; --\n   opposed to heterogamy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orthognathic', 'a.', 'Orthognathous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orthognathism', 'n.', 'The quality or state of being orthognathous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orthognathous', 'a.', 'Having the front of the head, or the skull, nearly\n   perpendicular, not retreating backwards above the jaws; -- opposed to\n   prognathous. See Gnathic index, under Gnathic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orthogon', 'n.', 'A rectangular figure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orthogonal', 'a.', 'Right-angled; rectangular; as, an orthogonal\n   intersection of one curve with another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orthogonally', 'adv.', 'Perpendicularly; at right angles; as, a curve cuts\n   a set of curves orthogonally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orthographer', 'n.', 'One versed in orthography; one who spells words\n   correctly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orthographic', 'a.', 'Alt. of Orthographical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orthographical', 'a.', 'Of or pertaining to orthography, or right spelling;\n   also, correct in spelling; as, orthographical rules; the letter was\n   orthographic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orthographical', 'a.', 'Of or pertaining to right lines or angles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orthographically', 'adv.', 'In an orthographical manner');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orthographically', 'adv.', 'according to the rules of proper spelling');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orthographically', 'adv.', 'according to orthographic projection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orthographist', 'n.', 'One who spells words correctly; an orthographer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orthographize', 'v. t.', 'To spell correctly or according to usage; to\n   correct in regard to spelling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orthography', 'n.', 'The art or practice of writing words with the proper\n   letters, according to standard usage; conventionally correct spelling;\n   also, mode of spelling; as, his orthography is vicious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orthography', 'n.', 'The part of grammar which treats of the letters, and\n   of the art of spelling words correctly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orthography', 'n.', 'A drawing in correct projection, especially an\n   elevation or a vertical section.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orthology', 'n.', 'The right description of things.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orthometric', 'a.', 'Having the axes at right angles to one another; --\n   said of crystals or crystalline forms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orthometry', 'n.', 'The art or practice of constructing verses correctly;\n   the laws of correct versification.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orthomorphic', 'a.', 'Having the right form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orthopedic', 'a.', 'Alt. of Orthopedical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orthopedical', 'a.', 'Pertaining to, or employed in, orthopedy; relating to\n   the prevention or cure of deformities of children, or, in general, of\n   the human body at any age; as, orthopedic surgery; an orthopedic\n   hospital.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orthopedist', 'n.', 'One who prevents, cures, or remedies deformities, esp.\n   in children.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orthopedy', 'n.', 'The art or practice of curing the deformities of\n   children, or, by extension, any deformities of the human body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orthophony', 'n.', 'The art of correct articulation; voice training.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orthopinacoid', 'n.', 'A name given to the two planes in the monoclinic\n   system which are parallel to the vertical and orthodiagonal axes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orthopn/a', 'n.', 'Alt. of Orthopny');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orthopny', 'n.', 'Specifically, a morbid condition in which respiration can\n   be performed only in an erect posture; by extension, any difficulty of\n   breathing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orthopoda', 'n. pl.', 'An extinct order of reptiles which stood erect on\n   the hind legs, and resembled birds in the structure of the feet,\n   pelvis, and other parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orthopraxy', 'n.', 'The treatment of deformities in the human body by\n   mechanical appliances.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orthoptera', 'n. pl.', 'An order of mandibulate insects including\n   grasshoppers, locusts, cockroaches, etc. See Illust. under Insect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orthopteran', 'n.', 'One of the Orthoptera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orthopterous', 'a.', 'Of or pertaining to the Orthoptera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orthorhombic', 'a.', 'Noting the system of crystallization which has three\n   unequal axes at right angles to each other; trimetric. See\n   Crystallization.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orthoscope', 'n.', 'An instrument designed to show the condition of the\n   superficial portions of the eye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orthoscopic', 'a.', 'Giving an image in correct or normal proportions;\n   giving a flat field of view; as, an orthoscopic eyepiece.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orthosilicic', 'a.', 'Designating the form of silicic acid having the\n   normal or highest number of hydroxyl groups.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orthospermous', 'a.', 'Having the seeds straight, as in the fruits of some\n   umbelliferous plants; -- opposed to coelospermous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orthostade', 'n.', 'A chiton, or loose, ungirded tunic, falling in straight\n   folds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orthostichies', 'pl. ', 'of Orthostichy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orthostichy', 'n.', 'A longitudinal rank, or row, of leaves along a stem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orthotomic', 'a.', 'Cutting at right angles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orthotomous', 'a.', 'Having two cleavages at right angles with one another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orthotomy', 'n.', 'The property of cutting at right angles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orthotone', 'a.', 'Retaining the accent; not enclitic; -- said of certain\n   indefinite pronouns and adverbs when used interrogatively, which, when\n   not so used, are ordinarilly enclitic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orthotropal', 'a.', 'Alt. of Orthotropous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orthotropous', 'a.', 'Having the axis of an ovule or seed straight from the\n   hilum and chalaza to the orifice or the micropyle; atropous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orthotropic', 'a.', 'Having the longer axis vertical; -- said of erect\n   stems.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orthoxylene', 'n.', 'That variety of xylene in which the two methyl groups\n   are in the ortho position; a colorless, liquid, combustible hydrocarbon\n   resembling benzene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ortive', 'a.', 'Of or relating to the time or act of rising; eastern; as,\n   the ortive amplitude of a planet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ortolan', 'n.', 'A European singing bird (Emberiza hortulana), about the\n   size of the lark, with black wings. It is esteemed delicious food when\n   fattened. Called also bunting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ortolan', 'n.', 'In England, the wheatear (Saxicola oenanthe).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ortolan', 'n.', 'In America, the sora, or Carolina rail (Porzana Carolina).\n   See Sora.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ortygan', 'n.', 'One of several species of East Indian birds of the genera\n   Ortygis and Hemipodius. They resemble quails, but lack the hind toe.\n   See Turnix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orval', 'n.', 'A kind of sage (Salvia Horminum).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orvet', 'n.', 'The blindworm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orvietan', 'n.', 'A kind of antidote for poisons; a counter poison formerly\n   in vogue.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-ory', '', 'An adjective suffix meaning of or pertaining to, serving for;\n   as in auditory, pertaining to or serving for hearing; prohibitory,\n   amendatory, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-ory', '', 'A noun suffix denoting that which pertains to, or serves for;\n   as in ambulatory, that which serves for walking; consistory, factory,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oryal', 'n.', 'Alt. of Oryall');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oryall', 'n.', 'See Oriel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oryctere', 'n.', 'The aard-vark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Orycterope', 'n.', 'Same as Oryctere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oryctognosy', 'n.', 'Mineralogy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oryctography', 'n.', 'Description of fossils.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oryctological', 'a.', 'Of or pertaining to oryctology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oryctologist', 'n.', 'One versed in oryctology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oryctology', 'n.', 'An old name for paleontology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oryctology', 'n.', 'An old name for mineralogy and geology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oryx', 'n.', 'A genus of African antelopes which includes the gemsbok, the\n   leucoryx, the bisa antelope (O. beisa), and the beatrix antelope (O.\n   beatrix) of Arabia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oryza', 'n.', 'A genus of grasses including the rice plant; rice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ossa', 'pl. ', 'of Os');
  INSERT INTO words (word, word_type, description) VALUES  (    'Os', 'n.', 'A bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ora', 'pl. ', 'of Os');
  INSERT INTO words (word, word_type, description) VALUES  (    'Os', 'n.', 'A mouth; an opening; an entrance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osar', 'pl. ', 'of Os');
  INSERT INTO words (word, word_type, description) VALUES  (    'Os', 'n.', 'One of the ridges of sand or gravel found in Sweden, etc.,\n   supposed by some to be of marine origin, but probably formed by\n   subglacial waters. The osar are similar to the kames of Scotland and\n   the eschars of Ireland. See Eschar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osage orange', '', 'An ornamental tree of the genus Maclura (M.\n   aurantiaca), closely allied to the mulberry (Morus); also, its fruit.\n   The tree was first found in the country of the Osage Indians, and bears\n   a hard and inedible fruit of an orangelike appearance. See Bois darc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osages', 'n. pl.', 'A tribe of southern Sioux Indians, now living in the\n   Indian Territory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osanne', 'n.', 'Hosanna.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osar', 'n. pl.', 'See 3d Os.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oscan', 'a.', 'Of or pertaining to the Osci, a primitive people of\n   Campania, a province of ancient Italy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oscan', 'n.', 'The language of the Osci.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oscillancy', 'n.', 'The state of oscillating; a seesaw kind of motion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oscillaria', 'n.', 'A genus of dark green, or purplish black, filamentous,\n   fresh-water algae, the threads of which have an automatic swaying or\n   crawling motion. Called also Oscillatoria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oscillated', 'imp. & p. p.', 'of Oscillate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oscillating', 'p. pr. & vb. n.', 'of Oscillate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oscillate', 'v. i.', 'To move backward and forward; to vibrate like a\n   pendulum; to swing; to sway.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oscillate', 'v. i.', 'To vary or fluctuate between fixed limits; to act or\n   move in a fickle or fluctuating manner; to change repeatedly, back and\n   forth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oscillating', 'a.', 'That oscillates; vibrating; swinging.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oscillation', 'n.', 'The act of oscillating; a swinging or moving backward\n   and forward, like a pendulum; vibration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oscillation', 'n.', 'Fluctuation; variation; change back and forth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oscillative', 'a.', 'Tending to oscillate; vibratory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oscillatoria', 'n. pl.', 'Same as Oscillaria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oscillatory', 'a.', 'Moving, or characterized by motion, backward and\n   forward like a pendulum; swinging; oscillating; vibratory; as,\n   oscillatory motion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oscine', 'a.', 'Relating to the Oscines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oscines', 'n. pl.', 'Singing birds; a group of the Passeres, having\n   numerous syringeal muscles, conferring musical ability.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oscinian', 'n.', 'One of the Oscines, or singing birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oscinian', 'n.', 'Any one of numerous species of dipterous files of the\n   family Oscinidae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oscinine', 'a.', 'Of or pertaining to the Oscines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oscitancy', 'n.', 'The act of gaping or yawning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oscitancy', 'n.', 'Drowsiness; dullness; sluggishness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oscitant', 'a.', 'Yawning; gaping.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oscitant', 'a.', 'Sleepy; drowsy; dull; sluggish; careless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oscitantly', 'adv.', 'In an oscitant manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oscitate', 'v. i.', 'To gape; to yawn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oscitation', 'n.', 'The act of yawning or gaping.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osculant', 'a.', 'Kissing; hence, meeting; clinging.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osculant', 'a.', 'Adhering closely; embracing; -- applied to certain\n   creeping animals, as caterpillars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osculant', 'a.', 'Intermediate in character, or on the border, between two\n   genera, groups, families, etc., of animals or plants, and partaking\n   somewhat of the characters of each, thus forming a connecting link;\n   interosculant; as, the genera by which two families approximate are\n   called osculant genera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osculated', 'imp. & p. p.', 'of Osculate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osculating', 'p. pr. & vb. n.', 'of Osculate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osculate', 'v. t.', 'To kiss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osculate', 'v. t.', 'To touch closely, so as to have a common curvature at\n   the point of contact. See Osculation, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osculate', 'v. i.', 'To kiss one another; to kiss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osculate', 'v. i.', 'To touch closely. See Osculation, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osculate', 'v. i.', 'To have characters in common with two genera or\n   families, so as to form a connecting link between them; to\n   interosculate. See Osculant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osculation', 'n.', 'The act of kissing; a kiss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osculation', 'n.', 'The contact of one curve with another, when the number\n   of consecutive points of the latter through which the former passes\n   suffices for the complete determination of the former curve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osculatory', 'a.', 'Of or pertaining to kissing; kissing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osculatory', 'a.', 'Pertaining to, or having the properties of, an\n   osculatrix; capable of osculation; as, a circle may be osculatory with\n   a curve, at a given point.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osculatory', 'n.', 'Same as Pax, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osculatrixes', 'pl. ', 'of Osculatrix');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osculatrix', 'n.', 'A curve whose contact with a given curve, at a given\n   point, is of a higher order (or involves the equality of a greater\n   number of successive differential coefficients of the ordinates of the\n   curves taken at that point) than that of any other curve of the same\n   kind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oscule', 'n.', 'One of the excurrent apertures of sponges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oscula', 'pl. ', 'of Osculum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osculum', 'n.', 'Same as Oscule.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-ose', '', 'A suffix denoting full of, containing, having the qualities of,\n   like; as in verbose, full of words; pilose, hairy; globose, like a\n   globe.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-ose', '', 'A suffix indicating that the substance to the name of which it\n   is affixed is a member of the carbohydrate group; as in cellulose,\n   sucrose, dextrose, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osier', 'n.', 'A kind of willow (Salix viminalis) growing in wet places in\n   Europe and Asia, and introduced into North America. It is considered\n   the best of the willows for basket work. The name is sometimes given to\n   any kind of willow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osier', 'n.', 'One of the long, pliable twigs of this plant, or of other\n   similar plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osier', 'a.', 'Made of osiers; composed of, or containing, osiers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osiered', 'a.', 'Covered or adorned with osiers; as, osiered banks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osiery', 'n.', 'An osier bed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osiris', 'n.', 'One of the principal divinities of Egypt, the brother and\n   husband of Isis. He was figured as a mummy wearing the royal cap of\n   Upper Egypt, and was symbolized by the sacred bull, called Apis. Cf.\n   Serapis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osmanlis', 'pl. ', 'of Osmanli');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osmanli', 'n.', 'A Turkish official; one of the dominant tribe of Turks;\n   loosely, any Turk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osmate', 'n.', 'A salt of osmic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osmateria', 'pl. ', 'of Osmaterium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osmaterium', 'n.', 'One of a pair of scent organs which the larvae of\n   certain butterflies emit from the first body segment, either above or\n   below.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osmazome', 'n.', 'A substance formerly supposed to give to soup and broth\n   their characteristic odor, and probably consisting of one or several of\n   the class of nitrogenous substances which are called extractives.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osmiamate', 'n.', 'A salt of osmiamic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osmiamic', 'a.', 'Of, pertaining to, or designating, a nitrogenous acid of\n   osmium, H2N2Os2O5, forming a well-known series of yellow salts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osmic', 'a.', 'Pertaining to, derived from, or containing, osmium;\n   specifically, designating those compounds in which it has a valence\n   higher than in other lower compounds; as, osmic oxide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osmidrosis', 'n.', 'The secretion of fetid sweat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osmious', 'a.', 'Denoting those compounds of osmium in which the element\n   has a valence relatively lower than in the osmic compounds; as, osmious\n   chloride.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osmite', 'n.', 'A salt of osmious acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osmium', 'n.', 'A rare metallic element of the platinum group, found native\n   as an alloy in platinum ore, and in iridosmine. It is a hard,\n   infusible, bluish or grayish white metal, and the heaviest substance\n   known. Its tetroxide is used in histological experiments to stain\n   tissues. Symbol Os. Atomic weight 191.1. Specific gravity 22.477.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osmometer', 'n.', 'An instrument for measuring the amount of osmotic action\n   in different liquids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osmometry', 'n.', 'The study of osmose by means of the osmometer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osmose', 'n.', 'The tendency in fluids to mix, or become equably diffused,\n   when in contact. It was first observed between fluids of differing\n   densities, and as taking place through a membrane or an intervening\n   porous structure. The more rapid flow from the thinner to the thicker\n   fluid was then called endosmose, and the opposite, slower current,\n   exosmose. Both are, however, results of the same force. Osmose may be\n   regarded as a form of molecular attraction, allied to that of adhesion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osmose', 'n.', 'The action produced by this tendency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osmosis', 'n.', 'Osmose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osmotic', 'a.', 'Pertaining to, or having the property of, osmose; as,\n   osmotic force.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osmund', 'n.', 'A fern of the genus Osmunda, or flowering fern. The most\n   remarkable species is the osmund royal, or royal fern (Osmunda\n   regalis), which grows in wet or boggy places, and has large bipinnate\n   fronds, often with a panicle of capsules at the top. The rootstock\n   contains much starch, and has been used in stiffening linen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osnaburg', 'n.', 'A species of coarse linen, originally made in Osnaburg,\n   Germany.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oso-berry', 'n.', 'The small, blueblack, drupelike fruit of the Nuttallia\n   cerasiformis, a shrub of Oregon and California, belonging to the Cherry\n   tribe of Rosaceae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osphradia', 'pl. ', 'of Osphradium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osphradium', 'n.', 'The olfactory organ of some Mollusca. It is connected\n   with the organ of respiration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osprey', 'n.', 'Alt. of Ospray');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ospray', 'n.', 'The fishhawk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oss', 'n.', 'To prophesy; to presage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osse', 'n.', 'A prophetic or ominous utterance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ossean', 'n.', 'A fish having a bony skeleton; a teleost.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ossein', 'n.', 'The organic basis of bone tissue; the residue after removal\n   of the mineral matters from bone by dilute acid; in embryonic tissue,\n   the substance in which the mineral salts are deposited to form bone; --\n   called also ostein. Chemically it is the same as collagen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osselet', 'n.', 'A little bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osselet', 'n.', 'The internal bone, or shell, of a cuttlefish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osseous', 'a.', 'Composed of bone; resembling bone; capable of forming\n   bone; bony; ossific.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osseter', 'n.', 'A species of sturgeon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ossianic', 'a.', 'Of or pertaining to, or characteristic of, Ossian, a\n   legendary Erse or Celtic bard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ossicle', 'n.', 'A little bone; as, the auditory ossicles in the tympanum\n   of the ear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ossicle', 'n.', 'One of numerous small calcareous structures forming the\n   skeleton of certain echinoderms, as the starfishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ossiculated', 'a.', 'Having small bones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ossicula', 'pl. ', 'of Ossiculum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ossiculum', 'n.', 'Same as Ossicle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ossiferous', 'a.', 'Containing or yielding bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ossific', 'a.', 'Capable of producing bone; having the power to change\n   cartilage or other tissue into bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ossification', 'n.', 'The formation of bone; the process, in the growth of\n   an animal, by which inorganic material (mainly lime salts) is deposited\n   in cartilage or membrane, forming bony tissue; ostosis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ossification', 'n.', 'The state of being changed into a bony substance;\n   also, a mass or point of ossified tissue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ossified', 'a.', 'Changed to bone or something resembling bone; hardened by\n   deposits of mineral matter of any kind; -- said of tissues.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ossifrage', 'n.', 'The lammergeir.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ossifrage', 'n.', 'The young of the sea eagle or bald eagle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ossifragous', 'a.', 'Serving to break bones; bone-breaking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ossified', 'imp. & p. p.', 'of Ossify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ossifying', 'p. pr. & vb. n.', 'of Ossify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ossify', 'v. t.', 'To form into bone; to change from a soft animal\n   substance into bone, as by the deposition of lime salts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ossify', 'v. t.', 'Fig.: To harden; as, to ossify the heart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ossify', 'v. i.', 'To become bone; to change from a soft tissue to a hard\n   bony tissue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ossifying', 'a.', 'Changing into bone; becoming bone; as, the ossifying\n   process.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ossivorous', 'a.', 'Feeding on bones; eating bones; as, ossivorous\n   quadrupeds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osspringer', 'n.', 'The osprey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ossuarium', 'n.', 'A charnel house; an ossuary.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-ries', 'pl. ', 'of Ossuary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ossuary', 'n.', 'A place where the bones of the dead are deposited; a\n   charnel house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ost', 'n.', 'See Oast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osteal', 'a.', 'Osseous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ostein', 'n.', 'Ossein.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osteitis', 'n.', 'Inflammation of bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osteler', 'n.', 'Same as Hosteler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ostend', 'v. t.', 'To exhibit; to manifest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ostensibility', 'n.', 'The quality or state of being ostensible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ostensible', 'a.', 'Capable of being shown; proper or intended to be shown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ostensible', 'a.', 'Shown; exhibited; declared; avowed; professed;\n   apparent; -- often used as opposed to real or actual; as, an ostensible\n   reason, motive, or aim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ostensibly', 'adv.', 'In an ostensible manner; avowedly; professedly;\n   apparently.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ostension', 'n.', 'The showing of the sacrament on the altar in order that\n   it may receive the adoration of the communicants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ostensive', 'a.', 'Showing; exhibiting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ostensively', 'adv.', 'In an ostensive manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-soria', 'pl. ', 'of Ostensory');
  INSERT INTO words (word, word_type, description) VALUES  (    '-sories', 'pl. ', 'of Ostensory');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ostensorium', 'n.', 'Alt. of Ostensory');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ostensory', 'n.', 'Same as Monstrance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ostent', 'n.', 'Appearance; air; mien.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ostent', 'n.', 'Manifestation; token; portent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ostentate', 'v. t.', 'To make an ambitious display of; to show or exhibit\n   boastingly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ostentation', 'n.', 'The act of ostentating or of making an ambitious\n   display; unnecessary show; pretentious parade; -- usually in a\n   detractive sense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ostentation', 'n.', 'A show or spectacle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ostentatious', 'a.', 'Fond of, or evincing, ostentation; unduly\n   conspicuous; pretentious; boastful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ostentator', 'n.', 'One fond of display; a boaster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ostentive', 'a.', 'Ostentatious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ostentous', 'a.', 'Ostentatious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osteo-', '', 'A combining form of Gr. / a bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osteoblast', 'n.', 'One of the protoplasmic cells which occur in the\n   osteogenetic layer of the periosteum, and from or around which the\n   matrix of the bone is developed; an osteoplast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osteoclasis', 'n.', 'The operation of breaking a bone in order to correct\n   deformity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osteoclast', 'n.', 'A myeloplax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osteoclast', 'n.', 'An instrument for performing osteoclasis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osteocolla', 'n.', 'A kind of glue obtained from bones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osteocolla', 'n.', 'A cellular calc tufa, which in some places forms\n   incrustations on the stems of plants, -- formerly supposed to have the\n   quality of uniting fractured bones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osteocommata', 'pl. ', 'of Osteocomma');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osteocommas', 'pl. ', 'of Osteocomma');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osteocomma', 'n.', 'A metamere of the vertebrate skeleton; an osteomere; a\n   vertebra.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osteocope', 'n.', 'Pain in the bones; a violent fixed pain in any part of a\n   bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osteocranium', 'n.', 'The bony cranium, as distinguished from the\n   cartilaginous cranium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osteodentine', 'n.', 'A hard substance, somewhat like bone, which is\n   sometimes deposited within the pulp cavity of teeth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osteogen', 'n.', 'The soft tissue, or substance, which, in developing bone,\n   ultimately undergoes ossification.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osteogenesis', 'n.', 'Alt. of Osteogeny');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osteogeny', 'n.', 'The formation or growth of bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osteogenetic', 'a.', 'Connected with osteogenesis, or the formation of\n   bone; producing bone; as, osteogenetic tissue; the osteogenetic layer\n   of the periosteum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osteogenic', 'a.', 'Osteogenetic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osteographer', 'n.', 'An osteologist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osteography', 'n.', 'The description of bones; osteology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osteoid', 'a.', 'Resembling bone; bonelike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osteolite', 'n.', 'A massive impure apatite, or calcium phosphate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osteologer', 'n.', 'One versed in osteology; an osteologist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osteologic', 'a.', 'Alt. of Osteological');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osteological', 'a.', 'Of or pertaining to osteology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osteologist', 'n.', 'One who is skilled in osteology; an osteologer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osteology', 'n.', 'The science which treats of the bones of the vertebrate\n   skeleton.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osteomata', 'pl. ', 'of Osteoma');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osteoma', 'n.', 'A tumor composed mainly of bone; a tumor of a bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osteomalacia', 'n.', 'A disease of the bones, in which they lose their\n   earthy material, and become soft, flexible, and distorted. Also called\n   malacia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osteomanty', 'n.', 'Divination by means of bones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osteomere', 'n.', 'An osteocomma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osteophone', 'n.', 'An instrument for transmission of auditory vibrations\n   through the bones of the head, so as to be appreciated as sounds by\n   persons deaf from causes other than those affecting the nervous\n   apparatus of hearing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osteoplast', 'n.', 'An osteoblast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osteoplastic', 'a.', 'Producing bone; as, osteoplastic cells.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osteoplastic', 'a.', 'Of or pertaining to the replacement of bone; as, an\n   osteoplastic operation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osteoplasty', 'n.', 'An operation or process by which the total or partial\n   loss of a bone is remedied.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osteopterygious', 'a.', 'Having bones in the fins, as certain fishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osteosarcomata', 'pl. ', 'of Osteosarcoma');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osteosarcoma', 'n.', 'A tumor having the structure of a sacroma in which\n   there is a deposit of bone; sarcoma connected with bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osteotome', 'n.', 'Strong nippers or a chisel for dividing bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osteotomist', 'n.', 'One skilled in osteotomy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osteotomy', 'n.', 'The dissection or anatomy of bones; osteology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osteotomy', 'n.', 'The operation of dividing a bone or of cutting a piece\n   out of it, -- done to remedy deformity, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Osteozoa', 'n. pl.', 'Same as Vertebrata.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-ries', 'pl. ', 'of Ostiary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ostiary', 'n.', 'The mouth of a river; an estuary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ostiary', 'n.', 'One who keeps the door, especially the door of a church; a\n   porter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ostic', 'a.', 'Pertaining to, or applied to, the language of the\n   Tuscaroras, Iroquois, Wyandots, Winnebagoes, and a part of the Sioux\n   Indians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ostiole', 'n.', 'The exterior opening of a stomate. See Stomate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ostiole', 'n.', 'Any small orifice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ostitis', 'n.', 'See Osteitis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ostia', 'pl. ', 'of Ostium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ostium', 'n.', 'An opening; a passage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ostler', 'n.', 'See Hostler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ostleress', 'n.', 'A female ostler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ostlery', 'n.', 'See Hostelry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ostmen', 'n. pl.', 'East men; Danish settlers in Ireland, formerly so\n   called.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ostosis', 'n.', 'Bone formation; ossification. See Ectostosis, and\n   Endostosis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ostracea', 'n. pl.', 'A division of bivalve mollusks including the oysters\n   and allied shells.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ostracean', 'n.', 'Any one of a family of bivalves, of which the oyster is\n   the type.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ostracion', 'n.', 'A genus of plectognath fishes having the body covered\n   with solid, immovable, bony plates. It includes the trunkfishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ostraciont', 'n.', 'A fish of the genus Ostracion and allied genera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ostracism', 'n.', 'Banishment by popular vote, -- a means adopted at Athens\n   to rid the city of a person whose talent and influence gave umbrage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ostracism', 'n.', 'Banishment; exclusion; as, social ostracism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ostracite', 'n.', 'A fossil oyster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ostracized', 'imp. & p. p.', 'of Ostracize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ostracizing', 'p. pr. & vb. n.', 'of Ostracize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ostracize', 'v. t.', 'To exile by ostracism; to banish by a popular vote,\n   as at Athens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ostracize', 'v. t.', 'To banish from society; to put under the ban; to cast\n   out from social, political, or private favor; as, he was ostracized by\n   his former friends.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ostracoda', 'n. pl.', 'Ostracoidea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ostracodermi', 'n. pl.', 'A suborder of fishes of which Ostracion is the\n   type.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ostracoid', 'a.', 'Of or pertaining to the Ostracoidea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ostracoid', 'n.', 'One of the Ostracoidea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ostracoidea', 'n. pl.', 'An order of Entomostraca possessing hard bivalve\n   shells. They are of small size, and swim freely about.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ostrea', 'n.', 'A genus of bivalve Mollusca which includes the true\n   oysters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ostreaceous', 'a.', 'Of or pertaining to an oyster, or to a shell; shelly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ostreaculture', 'n.', 'The artificial cultivation of oysters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ostreophagist', 'n.', 'One who feeds on oysters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ostrich', 'n.', 'A large bird of the genus Struthio, of which Struthio\n   camelus of Africa is the best known species. It has long and very\n   strong legs, adapted for rapid running; only two toes; a long neck,\n   nearly bare of feathers; and short wings incapable of flight. The adult\n   male is about eight feet high.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ostriferous', 'a.', 'Producing oysters; containing oysters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ostrogoth', 'n.', 'One of the Eastern Goths. See Goth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ostrogothic', 'a.', 'Of or pertaining to the Ostrogoths.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oswego tea', '', 'An American aromatic herb (Monarda didyma), with showy,\n   bright red, labiate flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Otacoustic', 'a.', 'Assisting the sense of hearing; as, an otacoustic\n   instrument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Otacoustic', 'n.', 'Alt. of Otacousticon');
  INSERT INTO words (word, word_type, description) VALUES  (    'Otacousticon', 'n.', 'An instrument to facilitate hearing, as an ear\n   trumpet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Otaheite apple', '', 'The fruit of a Polynesian anacardiaceous tree\n   (Spondias dulcis), also called vi-apple. It is rather larger than an\n   apple, and the rind has a flavor of turpentine, but the flesh is said\n   to taste like pineapples.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Otaheite apple', '', 'A West Indian name for a myrtaceous tree (Jambosa\n   Malaccensis) which bears crimson berries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Otalgia', 'n.', 'Pain in the ear; earache.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Otalgic', 'a.', 'Of or pertaining to otalgia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Otalgic', 'n.', 'A remedy for otalgia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Otalgy', 'n.', 'Pain in the ear; otalgia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Otaries', 'pl. ', 'of Otary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Otary', 'n.', 'Any eared seal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Otheoscope', 'n.', 'An instrument for exhibiting the repulsive action\n   produced by light or heat in an exhausted vessel; a modification of the\n   radoimeter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Other', 'conj.', 'Either; -- used with other or or for its correlative (as\n   either . . . or are now used).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Other', 'pron. & a.', 'Different from that which, or the one who, has been\n   specified; not the same; not identical; additional; second of two.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Other', 'pron. & a.', 'Not this, but the contrary; opposite; as, the other\n   side of a river.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Other', 'pron. & a.', 'Alternate; second; -- used esp. in connection with\n   every; as, every other day, that is, each alternate day, every second\n   day.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Other', 'pron. & a.', 'Left, as opposed to right.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Other', 'adv.', 'Otherwise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Othergates', 'adv.', 'In another manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Otherguise', 'a. & adv.', 'Alt. of Otherguess');
  INSERT INTO words (word, word_type, description) VALUES  (    'Otherguess', 'a. & adv.', 'Of another kind or sort; in another way.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Otherness', 'n.', 'The quality or state of being other or different;\n   alterity; oppositeness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Otherways', 'adv.', 'See Otherwise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Otherwhere', 'adv.', 'In or to some other place, or places; elsewhere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Otherwhile', 'adv.', 'Alt. of Otherwhiles');
  INSERT INTO words (word, word_type, description) VALUES  (    'Otherwhiles', 'adv.', 'At another time, or other times; sometimes;\n   /ccasionally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Otherwise', 'adv.', 'In a different manner; in another way, or in other\n   ways; differently; contrarily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Otherwise', 'adv.', 'In other respects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Otherwise', 'adv.', 'In different circumstances; under other conditions;\n   as, I am engaged, otherwise I would accept.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Othman', 'n. & a.', 'See Ottoman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Otic', 'a.', 'Of, pertaining to, or in the region of, the ear; auricular;\n   auditory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Otiose', 'a.', 'Being at leisure or ease; unemployed; indolent; idle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Otiosity', 'n.', 'Leisure; indolence; idleness; ease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Otis', 'n.', 'A genus of birds including the bustards.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Otitis', 'n.', 'Inflammation of the ear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oto-', '', 'A combining form denoting relation to, or situation near or in,\n   the ear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Otoba fat', '', 'A colorless buttery substance obtained from the fruit of\n   Myristica otoba, a species of nutmeg tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Otoconite', 'n.', 'A mass of otoliths.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Otoconite', 'n.', 'An otolith.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Otocrane', 'n.', 'The cavity in the skull in which the parts of the\n   internal ear are lodged.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Otocranial', 'a.', 'Of or pertaining to the otocrane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Otocyst', 'n.', 'An auditory cyst or vesicle; one of the simple auditory\n   organs of many invertebrates, containing a fluid and otoliths; also,\n   the embryonic vesicle from which the parts of the internal ear of\n   vertebrates are developed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Otography', 'n.', 'A description of the ear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Otolith', 'n.', 'Alt. of Otolite');
  INSERT INTO words (word, word_type, description) VALUES  (    'Otolite', 'n.', 'One of the small bones or particles of calcareous or other\n   hard substance in the internal ear of vertebrates, and in the auditory\n   organs of many invertebrates; an ear stone. Collectively, the otoliths\n   are called ear sand and otoconite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Otolithic', 'a.', 'Alt. of Otolitic');
  INSERT INTO words (word, word_type, description) VALUES  (    'Otolitic', 'a.', 'Of or pertaining to otoliths.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Otological', 'a.', 'Of or pertaining tootology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Otologist', 'n.', 'One skilled in otology; an aurist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Otology', 'n.', 'The branch of science which treats of the ear and its\n   diseases.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Otopathy', 'n.', 'A diseased condition of the ear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Otorrh/a', 'n.', 'A flow or running from the ear, esp. a purulent\n   discharge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Otoscope', 'n.', 'An instrument for examining the condition of the ear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Otoscopeic', 'a.', 'Of or pertaining to the otoscope or to otoscopy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Otoscopy', 'n.', 'The examination of the ear; the art of using the\n   otoscope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Otosteal', 'n.', 'An auditory ossicle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Otozoum', 'n.', 'An extinct genus of huge vertebrates, probably dinosaurs,\n   known only from four-toed tracks in Triassic sandstones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ottar', 'n.', 'See Attar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ottawas', 'n. pl.', 'A tribe of Indians who, when first known, lived on the\n   Ottawa River. Most of them subsequently migrated to the southwestern\n   shore of Lake Superior.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Otter', 'n.', 'Any carnivorous animal of the genus Lutra, and related\n   genera. Several species are described. They have large, flattish heads,\n   short ears, and webbed toes. They are aquatic, and feed on fish. Their\n   fur is soft and valuable. The common otter of Europe is Lutra vulgaris;\n   the American otter is L. Canadensis; other species inhabit South\n   America and Asia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Otter', 'n.', 'The larva of the ghost moth. It is very injurious to hop\n   vines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Otter', 'n.', 'A corruption of Annotto.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Otto', 'n.', 'See Attar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ottoman', 'a.', 'Of or pertaining to the Turks; as, the Ottoman power or\n   empire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ottomans', 'pl. ', 'of Ottoman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ottoman', 'n.', 'A Turk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ottoman', 'n.', 'A stuffed seat without a back, originally used in Turkey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ottomite', 'n.', 'An Ottoman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ottrelite', 'n.', 'A micaceous mineral occurring in small scales. It is\n   characteristic of certain crystalline schists.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ouakari', 'n.', 'Any South American monkey of the genus Brachyurus,\n   especially B. ouakari.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ouanderoo', 'n.', 'The wanderoo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ouarine', 'n.', 'A Brazilian monkey of the genus Mycetes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oubliette', 'n.', 'A dungeon with an opening only at the top, found in some\n   old castles and other strongholds, into which persons condemned to\n   perpetual imprisonment, or to perish secretly, were thrust, or lured to\n   fall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ouch', 'n.', 'A socket or bezel holding a precious stone; hence, a jewel or\n   ornament worn on the person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oughne', 'a.', 'Own.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ought', 'n. & adv.', 'See Aught.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ought', 'imp., p. p., or auxi', 'Was or were under obligation to pay;\n   owed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ought', 'imp., p. p., or auxi', 'Owned; possessed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ought', 'imp., p. p., or auxi', 'To be bound in duty or by moral\n   obligation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ought', 'imp., p. p., or auxi', 'To be necessary, fit, becoming, or\n   expedient; to behoove; -- in this sense formerly sometimes used\n   impersonally or without a subject expressed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oughtness', 'n.', 'The state of being as a thing ought to be; rightness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oughwhere', 'adv.', 'Anywhere; somewhere. See Owher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ouistiti', 'n.', 'See Wistit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oul', 'n.', 'An awl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oul', 'n.', 'An owl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oulachan', 'n.', 'Same as Eulachon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ounce', 'n.', 'A weight, the sixteenth part of a pound avoirdupois, and\n   containing 437/ grains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ounce', 'n.', 'The twelfth part of a troy pound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ounce', 'n.', 'Fig.: A small portion; a bit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ounce', 'n.', 'A feline quadruped (Felis irbis, / uncia) resembling the\n   leopard in size, and somewhat in color, but it has longer and thicker\n   fur, which forms a short mane on the back. The ounce is pale yellowish\n   gray, with irregular dark spots on the neck and limbs, and dark rings\n   on the body. It inhabits the lofty mountain ranges of Asia. Called also\n   once.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ounded', 'a.', 'Alt. of Oundy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oundy', 'a.', 'Wavy; waving/ curly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ounding', 'vb. n.', 'Waving.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ouphe', 'n.', 'A fairy; a goblin; an elf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ouphen', 'a.', 'Elfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Our', 'possessive pron.', 'Of or pertaining to us; belonging to us; as, our\n   country; our rights; our troops; our endeavors. See I.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-our', '', 'See -or.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ourang', 'n.', 'The orang-outang.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ourang-outang', 'n.', 'See Orang-outang.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ouranographist', 'n.', 'See Uranographist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ouranography', 'n.', 'See Uranography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ourebi', 'n.', 'A small, graceful, and swift African antelope, allied to\n   the klipspringer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ouretic', 'a.', 'Uric.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ourology', 'n.', 'See Urology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ouroscopy', 'n.', 'Ourology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ours', 'possessive pron.', 'See Note under Our.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ourselves', 'pron.', '; sing. Ourself (/). An emphasized form of the\n   pronoun of the first person plural; -- used as a subject, usually with\n   we; also, alone in the predicate, in the nominative or the objective\n   case.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-ous', '', 'An adjective suffix meaning full of, abounding in, having,\n   possessing the qualities of, like; as in gracious, abounding in grace;\n   arduous, full of ardor; bulbous, having bulbs, bulblike; riotous,\n   poisonous, piteous, joyous, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-ous', '', 'A suffix denoting that the element indicated by the name\n   bearing it, has a valence lower than that denoted by the termination\n   -ic; as, nitrous, sulphurous, etc., as contrasted with nitric,\n   sulphuric, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ouse', 'n. & v.', 'See Ooze.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ousel', 'n.', 'One of several species of European thrushes, especially the\n   blackbird (Merula merula, or Turdus merula), and the mountain or ring\n   ousel (Turdus torquatus).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oust', 'n.', 'See Oast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ousted', 'imp. & p. p.', 'of Oust');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ousting', 'p. pr. & vb. n.', 'of Oust');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oust', 'v. t.', 'To take away; to remove.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oust', 'v. t.', 'To eject; to turn out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ouster', 'n.', 'A putting out of possession; dispossession; ejection;\n   disseizin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Out', 'a.', 'In its original and strict sense, out means from the interior\n   of something; beyond the limits or boundary of somethings; in a\n   position or relation which is exterior to something; -- opposed to in\n   or into. The something may be expressed after of, from, etc. (see Out\n   of, below); or, if not expressed, it is implied; as, he is out; or, he\n   is out of the house, office, business, etc.; he came out; or, he came\n   out from the ship, meeting, sect, party, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Out', 'a.', 'Away; abroad; off; from home, or from a certain, or a usual,\n   place; not in; not in a particular, or a usual, place; as, the\n   proprietor is out, his team was taken out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Out', 'a.', 'Beyond the limits of concealment, confinement, privacy,\n   constraint, etc., actual of figurative; hence, not in concealment,\n   constraint, etc., in, or into, a state of freedom, openness,\n   disclosure, publicity, etc.; as, the sun shines out; he laughed out, to\n   be out at the elbows; the secret has leaked out, or is out; the disease\n   broke out on his face; the book is out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Out', 'a.', 'Beyond the limit of existence, continuance, or supply; to the\n   end; completely; hence, in, or into, a condition of extinction,\n   exhaustion, completion; as, the fuel, or the fire, has burned out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Out', 'a.', 'Beyond possession, control, or occupation; hence, in, or into,\n   a state of want, loss, or deprivation; -- used of office, business,\n   property, knowledge, etc.; as, the Democrats went out and the Whigs\n   came in; he put his money out at interest.');
