  INSERT INTO words (word, word_type, description) VALUES  (    'Outswell', 'v. t.', 'To swell beyond; to overflow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Outtake', 'prep.', 'Except.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Outtaken', 'p. p.', 'or prep. Excepted; save.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Outtalk', 'v. t.', 'To overpower by talking; to exceed in talking; to talk\n   down.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Outtell', 'v. t.', 'To surpass in telling, counting, or reckoning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Outterm', 'n.', 'An external or superficial thing; outward manner;\n   superficial remark, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Outthrow', 'v. t.', 'To throw out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Outthrow', 'v. t.', 'To excel in throwing, as in ball playing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Outtoil', 'v. t.', 'To exceed in toiling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Outtongue', 'v. t.', 'To silence by talk, clamor, or noise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Outtop', 'v. t.', 'To overtop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Outtravel', 'v. t.', 'To exceed in speed o/ distance traveled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Outtwine', 'v. t.', 'To disentangle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Outvalue', 'v. t.', 'To exceed in value.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Outvenom', 'v. t.', 'To exceed in venom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Outvie', 'v. t.', 'To exceed in vying.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Outvillain', 'v. t.', 'To exceed in villainy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Outvoice', 'v. t.', 'To exceed in noise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Outvote', 'v. t.', 'To exceed in the number of votes given; to defeat by\n   votes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Outwalk', 'v. t.', 'To excel in walking; to leave behind in walking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Outwall', 'n.', 'The exterior wall; the outside surface, or appearance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Outward', 'adv.', 'Alt. of Outwards');
  INSERT INTO words (word, word_type, description) VALUES  (    'Outwards', 'adv.', 'From the interior part; in a direction from the\n   interior toward the exterior; out; to the outside; beyond; off; away;\n   as, a ship bound outward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Outward', 'a.', 'Forming the superficial part; external; exterior; --\n   opposed to inward; as, an outward garment or layer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Outward', 'a.', 'Of or pertaining to the outer surface or to what is\n   external; manifest; public.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Outward', 'a.', 'Foreign; not civil or intestine; as, an outward war.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Outward', 'a.', 'Tending to the exterior or outside.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Outward', 'n.', 'External form; exterior.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Outwards', 'adv.', 'See Outward, adv.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Outwatch', 'v. t.', 'To exceed in watching.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Outway', 'n.', 'A way out; exit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Outwear', 'v. t.', 'To wear out; to consume or destroy by wearing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Outwear', 'v. t.', 'To last longer than; to outlast; as, this cloth will\n   outwear the other.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Outweary', 'v. t.', 'To weary out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Outweed', 'v. t.', 'To weed out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Outweep', 'v. t.', 'To exceed in weeping.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Outweigh', 'v. t.', 'To exceed in weight or value.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Outwell', 'v. t.', 'To pour out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Outwell', 'v. i.', 'To issue forth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Outwent', '', 'imp. of Outgo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Outwhore', 'v. t.', 'To exceed in lewdness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Outwin', 'v. t.', 'To win a way out of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Outwind', 'v. t.', 'To extricate by winding; to unloose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Outwing', 'v. t.', 'To surpass, exceed, or outstrip in flying.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Outwit', 'v. t.', 'To surpass in wisdom, esp. in cunning; to defeat or\n   overreach by superior craft.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Outwit', 'n.', 'The faculty of acquiring wisdom by observation and\n   experience, or the wisdom so acquired; -- opposed to inwit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Outwoe', 'v. t.', 'To exceed in woe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Outwork', 'v. t.', 'To exceed in working; to work more or faster than.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Outwork', 'n.', 'A minor defense constructed beyond the main body of a\n   work, as a ravelin, lunette, hornwork, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Outworth', 'v. t.', 'To exceed in worth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Outwrest', 'v. t.', 'To extort; to draw from or forth by violence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Outwrite', 'v. t.', 'To exceed or excel in writing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Outzany', 'v. t.', 'To exceed in buffoonery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ouvarovite', 'n.', 'Chrome garnet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ouze', 'n. & v.', 'See Ooze.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ouzel', 'n.', 'Same as Ousel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ova', 'n. pl.', 'See Ovum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oval', 'a.', 'Of or pertaining to eggs; done in the egg, or inception; as,\n   oval conceptions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oval', 'a.', 'Having the figure of an egg; oblong and curvilinear, with one\n   end broader than the other, or with both ends of about the same\n   breadth; in popular usage, elliptical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oval', 'a.', 'Broadly elliptical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oval', 'n.', 'A body or figure in the shape of an egg, or popularly, of an\n   ellipse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ovalbumin', 'n.', 'Alt. of Ovalbumen');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ovalbumen', 'n.', 'The albumin from white of eggs; egg albumin; -- in\n   distinction from serum albumin. See Albumin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ovaliform', 'a.', 'Having the form of an egg; having a figure such that any\n   section in the direction of the shorter diameter will be circular, and\n   any in the direction of the longer diameter will be oval.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ovally', 'adv.', 'In an oval form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ovant', 'a.', 'Exultant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ovarian', 'a.', 'Alt. of Ovarial');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ovarial', 'a.', 'Of or pertaining to an ovary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ovariole', 'n.', 'One of the tubes of which the ovaries of most insects are\n   composed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ovariotomist', 'n.', 'One who performs, or is skilled in, ovariotomy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ovariotomy', 'n.', 'The operation of removing one or both of the ovaries;\n   oophorectomy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ovarious', 'a.', 'Consisting of eggs; as, ovarious food.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ovaritis', 'n.', 'Inflammation of the ovaries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ovaria', 'pl. ', 'of Ovarium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ovariums', 'pl. ', 'of Ovarium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ovarium', 'n.', 'An ovary. See Ovary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ovaries', 'pl. ', 'of Ovary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ovary', 'n.', 'That part of the pistil which contains the seed, and in most\n   flowering plants develops into the fruit. See Illust. of Flower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ovary', 'n.', 'The essential female reproductive organ in which the ova are\n   produced. See Illust. of Discophora.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ovate', 'a.', 'Shaped like an egg, with the lower extremity broadest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ovate', 'a.', 'Having the shape of an egg, or of the longitudinal sectior\n   of an egg, with the broader end basal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ovate-acuminate', 'a.', 'Having an ovate form, but narrowed at the end into\n   a slender point.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ovate-cylindraceous', 'a.', 'Having a form intermediate between ovate and\n   cylindraceous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ovated', 'a.', 'Ovate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ovate-lanceolate', 'a.', 'Having a form intermediate between ovate and\n   lanceolate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ovate-oblong', 'a.', 'Oblong. with one end narrower than the other;\n   ovato-oblong.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ovate-rotundate', 'a.', 'Having a form intermediate between that of an egg\n   and a sphere; roundly ovate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ovate-subulate', 'a.', 'Having an ovate form, but with a subulate tip or\n   extremity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ovation', 'n.', 'A lesser kind of triumph allowed to a commander for an\n   easy, bloodless victory, or a victory over slaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ovation', 'n.', 'Hence: An expression of popular homage; the tribute of the\n   multitude to a public favorite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ovato-acuminate', 'a.', 'Same as Ovate-acuminate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ovato-cylindraceous', 'a.', 'Same as Ovate-cylindraceous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ovato-oblong', 'a.', 'Same as Ovate-oblong.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ovato-rotundate', 'a.', 'Same as Ovate-rotundate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oven', 'n.', 'A place arched over with brick or stonework, and used for\n   baking, heating, or drying; hence, any structure, whether fixed or\n   portable, which may be heated for baking, drying, etc.; esp., now, a\n   chamber in a stove, used for baking or roasting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ovenbird', 'n.', 'Any species of the genus Furnarius, allied to the\n   creepers. They inhabit South America and the West Indies, and construct\n   curious oven-shaped nests.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ovenbird', 'n.', 'In the United States, Seiurus aurocapillus; -- called\n   also golden-crowned thrush.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ovenbird', 'n.', 'In England, sometimes applied to the willow warbler, and\n   to the long-tailed titmouse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Over', 'prep.', 'Above, or higher than, in place or position, with the idea\n   of covering; -- opposed to under; as, clouds are over our heads; the\n   smoke rises over the city.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Over', 'prep.', 'Across; from side to side of; -- implying a passing or\n   moving, either above the substance or thing, or on the surface of it;\n   as, a dog leaps over a stream or a table.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Over', 'prep.', 'Upon the surface of, or the whole surface of; hither and\n   thither upon; throughout the whole extent of; as, to wander over the\n   earth; to walk over a field, or over a city.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Over', 'prep.', 'Above; -- implying superiority in excellence, dignity,\n   condition, or value; as, the advantages which the Christian world has\n   over the heathen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Over', 'prep.', 'Above in authority or station; -- implying government,\n   direction, care, attention, guard, responsibility, etc.; -- opposed to\n   under.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Over', 'prep.', 'Across or during the time of; from beginning to end of;\n   as, to keep anything over night; to keep corn over winter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Over', 'prep.', 'Above the perpendicular height or length of, with an idea\n   of measurement; as, the water, or the depth of water, was over his\n   head, over his shoes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Over', 'prep.', 'Beyond; in excess of; in addition to; more than; as, it\n   cost over five dollars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Over', 'prep.', 'Above, implying superiority after a contest; in spite of;\n   notwithstanding; as, he triumphed over difficulties; the bill was\n   passed over the veto.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Over', 'adv.', 'From one side to another; from side to side; across;\n   crosswise; as, a board, or a tree, a foot over, i. e., a foot in\n   diameter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Over', 'adv.', 'From one person or place to another regarded as on the\n   opposite side of a space or barrier; -- used with verbs of motion; as,\n   to sail over to England; to hand over the money; to go over to the\n   enemy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Over', 'adv.', 'Also, with verbs of being: At, or on, the opposite side;\n   as, the boat is over.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Over', 'adv.', 'From beginning to end; throughout the course, extent, or\n   expanse of anything; as, to look over accounts, or a stock of goods; a\n   dress covered over with jewels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Over', 'adv.', 'From inside to outside, above or across the brim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Over', 'adv.', 'Beyond a limit; hence, in excessive degree or quantity;\n   superfluously; with repetition; as, to do the whole work over.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Over', 'adv.', 'In a manner to bring the under side to or towards the top;\n   as, to turn (ones self) over; to roll a stone over; to turn over the\n   leaves; to tip over a cart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Over', 'adv.', 'At an end; beyond the limit of continuance; completed;\n   finished.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Over', 'a.', 'Upper; covering; higher; superior; also, excessive; too much\n   or too great; -- chiefly used in composition; as, overshoes, overcoat,\n   over-garment, overlord, overwork, overhaste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Over', 'n.', 'A certain number of balls (usually four) delivered\n   successively from behind one wicket, after which the ball is bowled\n   from behind the other wicket as many times, the fielders changing\n   places.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overabound', 'v. i.', 'To be exceedingly plenty or superabundant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overact', 'v. t.', 'To act or perform to excess; to exaggerate in acting;\n   as, he overacted his part.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overact', 'v. t.', 'To act upon, or influence, unduly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overact', 'v. i.', 'To act more than is necessary; to go to excess in\n   action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overaction', 'n.', 'Per/ormance to excess; exaggerated or excessive action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overaffect', 'v. t.', 'To affect or care for unduly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overagitate', 'v. t.', 'To agitate or discuss beyond what is expedient.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overall', 'adv.', 'Everywhere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overalls', 'n. pl.', 'A kind of loose trousers worn over others to protect\n   them from soiling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overalls', 'n. pl.', 'Waterproof leggings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overanxiety', 'n.', 'The state of being overanxious; excessive anxiety.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overanxious', 'a.', 'Anxious in an excessive or needless degree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overarch', 'v. t. & i.', 'To make or place an arch over; to hang over like\n   an arch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Over-arm', 'a.', 'Done (as bowling or pitching) with the arm raised above\n   the shoulder. See Overhard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overawed', 'imp. & p. p.', 'of Overawe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overawing', 'p. pr. & vb. n.', 'of Overawe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overawe', 'v. t.', 'To awe exceedingly; to subjugate or restrain by awe or\n   great fear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overawful', 'a.', 'Awful, or reverential, in an excessive degree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overbalance', 'v. t.', 'To exceed equality with; to outweigh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overbalance', 'v. t.', 'To cause to lose balance or equilibrium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overbalance', 'n.', 'Excess of weight or value; something more than an\n   equivalent; as, an overbalance of exports.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overbarren', 'a.', 'Excessively barren.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overbattle', 'a.', 'Excessively fertile; bearing rank or noxious growths.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overbear', 'v. t.', 'To bear down or carry down, as by excess of weight,\n   power, force, etc.; to overcome; to suppress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overbear', 'v. t.', 'To domineer over; to overcome by insolence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overbear', 'v. i.', 'To bear fruit or offspring to excess; to be too\n   prolific.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overbearing', 'a.', 'Overpowering; subduing; repressing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overbearing', 'a.', 'Aggressively haughty; arrogant; domineering;\n   tyrannical; dictatorial; insolent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overbend', 'v. t.', 'To bend to excess.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overbend', 'v. i.', 'To bend over.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overbid', 'v. t.', 'To bid or offer beyond, or in excess of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overbide', 'v. t.', 'To outlive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overblow', 'v. i.', 'To blow over, or be subdued.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overblow', 'v. i.', 'To force so much wind into a pipe that it produces an\n   overtone, or a note higher than the natural note; thus, the upper\n   octaves of a flute are produced by overblowing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overblow', 'v. t.', 'To blow away; to dissipate by wind, or as by wind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overboard', 'adv.', 'Over the side of a ship; hence, from on board of a\n   ship, into the water; as, to fall overboard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overboil', 'v. i.', 'To boil over or unduly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overbold', 'a.', 'Excessively or presumptuously bold; impudent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overbookish', 'a.', 'Excessively bookish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overbounteous', 'a.', 'Bounteous to excess.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overbow', 'v. t.', 'To bend or bow over; to bend in a contrary direction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overbreed', 'v. t.', 'To breed to excess.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overbrim', 'v. i.', 'To flow over the brim; to be so full as to overflow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overbrow', 'v. t.', 'To hang over like a brow; to impend over.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overbuild', 'v. t.', 'To build over.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overbuild', 'v. t.', 'To build too much; to build beyond the demand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overbuilt', 'a.', 'Having too many buildings; as, an overbuilt part of a\n   town.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overbulk', 'v. t.', 'To oppress by bulk; to overtower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overburden', 'v. t.', 'To load with too great weight or too much care, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overburden', 'n.', 'The waste which overlies good stone in a quarry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overburdensome', 'a.', 'Too burdensome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overburn', 'v. t. & i.', 'To burn too much; to be overzealous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Over-busy', 'a.', 'Too busy; officious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overbuy', 'v. t.', 'To buy too much.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overbuy', 'v. t.', 'To buy at too dear a rate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overcanopy', 'v. t.', 'To cover as with a canopy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overcapable', 'a.', 'Too capable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overcare', 'n.', 'Excessive care.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overcareful', 'a.', 'Too careful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overcarking', 'a.', 'Too anxious; too full of care.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overcarry', 'v. t. & i.', 'To carry too far; to carry beyond the proper\n   point.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overcast', 'v. t.', 'To cast or cover over; hence, to cloud; to darken.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overcast', 'v. t.', 'To compute or rate too high.');
