  INSERT INTO words (word, word_type, description) VALUES  (    'Overtitle', 'v. t.', 'To give too high a title to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overtly', 'adv.', 'Publicly; openly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overtoil', 'v. t.', 'To overwork.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overtoil', 'v. t.', 'To weary excessively; to exhaust.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overtone', 'n.', 'One of the harmonics faintly heard with and above a tone\n   as it dies away, produced by some aliquot portion of the vibrating\n   sting or column of air which yields the fundamental tone; one of the\n   natural harmonic scale of tones, as the octave, twelfth, fifteenth,\n   etc.; an aliquot or \"partial\" tone; a harmonic. See Harmonic, and Tone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overtopped', 'imp. & p. p.', 'of Overtop');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overtopping', 'p. pr. & vb. n.', 'of Overtop');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overtop', 'v. t.', 'To rise above the top of; to exceed in height; to tower\n   above.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overtop', 'v. t.', 'To go beyond; to transcend; to transgress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overtop', 'v. t.', 'To make of less importance, or throw into the\n   background, by superior excellence; to dwarf; to obscure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overtower', 'v. t.', 'To tower over or above.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overtower', 'v. i.', 'To soar too high.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overtrade', 'v. i.', 'To trade beyond ones capital; to buy goods beyond\n   the means of paying for or seleng them; to overstock the market.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overtrading', 'n.', 'The act or practice of buying goods beyond the means\n   of payment; a glutting of the market.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overtread', 'v. t.', 'To tread over or upon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overtrip', 'v. t.', 'To trip over nimbly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overtroubled', 'a.', 'Excessively troubled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overtrow', 'v. i.', 'To be too trustful or confident; to trust too much.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overtrust', 'n.', 'Excessive confidence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overtrust', 'v. t. & i.', 'To trust too much.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overture', '', 'An opening or aperture; a recess; a recess; a chamber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overture', '', 'Disclosure; discovery; revelation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overture', '', 'A proposal; an offer; a proposition formally submitted for\n   consideration, acceptance, or rejection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overture', '', 'A composition, for a full orchestra, designed as an\n   introduction to an oratorio, opera, or ballet, or as an independent\n   piece; -- called in the latter case a concert overture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overture', 'v. t.', 'To make an overture to; as, to overture a religious\n   body on some subject.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overturned', 'imp. & p. p.', 'of Overturn');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overturning', 'p. pr. & vb. n.', 'of Overturn');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overturn', 'v. t.', 'To turn or throw from a basis, foundation, or\n   position; to overset; as, to overturn a carriage or a building.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overturn', 'v. t.', 'To subvert; to destroy; to overthrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overturn', 'v. t.', 'To overpower; to conquer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overturn', 'n.', 'The act off overturning, or the state of being overturned\n   or subverted; overthrow; as, an overturn of parties.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overturnable', 'a.', 'Capable of being, or liable to be, overturned or\n   subverted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overturner', 'n.', 'One who overturns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overvail', 'v. t.', 'See Overveil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overvaluation', 'n.', 'Excessive valuation; overestimate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overvalued', 'imp. & p. p.', 'of Overvalue');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overvaluing', 'p. pr. & vb. n.', 'of Overvalue');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overvalue', 'v. t.', 'To value excessively; to rate at too high a price.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overvalue', 'v. t.', 'To exceed in value.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overveil', 'v. t.', 'To veil or cover.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overview', 'n.', 'An inspection or overlooking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overvote', 'v. t.', 'To outvote; to outnumber in votes given.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overwalk', 'v. t.', 'To walk over or upon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overwar', 'v. t.', 'To defeat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overwary', 'a.', 'Too wary; too cautious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overwash', 'v. t.', 'To overflow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overwasted', 'a.', 'Wasted or worn out; /onsumed; spent');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overwatch', 'v. t.', 'To watch too much.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overwatch', 'v. t.', 'To weary or exhaust by watching.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overwax', 'v. i.', 'To wax or grow too rapindly or too much.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overweak', 'a.', 'Too weak; too feeble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overwear', 'v. t.', 'To wear too much; to wear out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overweary', 'v. t.', 'To weary too much; to tire out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overweather', 'v. t.', 'To expose too long to the influence of the weather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overween', 'v. t.', 'To think too highly or arrogantly; to regard ones own\n   thinking or conclusions too highly; hence, to egotistic, arrogant, or\n   rash, in opinion; to think conceitedly; to presume.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overweener', 'n.', 'One who overweens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overweening', 'a.', 'Unduly confident; arrogant; presumptuous; conceited.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overweening', 'n.', 'Conceit; arrogance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overweigh', 'v. t.', 'To exceed in weight; to overbalance; to weigh down.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overweight', 'n.', 'Weight over and above what is required by law or\n   custom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overweight', 'n.', 'Superabundance of weight; preponderance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overweight', 'a.', 'Overweighing; excessive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overwell', 'v. t.', 'To overflow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overwet', 'n.', 'Excessive wetness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overwhelmed', 'imp. & p. p.', 'of Overwhelm');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overwhelming', 'p. pr. & vb. n.', 'of Overwhelm');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overwhelm', 'v. t.', 'To cover over completely, as by a great wave; to\n   overflow and bury beneath; to ingulf; hence, figuratively, to immerse\n   and bear down; to overpower; to crush; to bury; to oppress, etc.,\n   overpoweringly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overwhelm', 'v. t.', 'To project or impend over threateningly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overwhelm', 'v. t.', 'To cause to surround, to cover.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overwhelm', 'n.', 'The act of overwhelming.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overwhelming', 'a.', 'Overpowering; irresistible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overwind', 'v. t.', 'To wind too tightly, as a spring, or too far, as a\n   hoisting rope on a drum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overwing', 'v. t.', 'To outflank.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overwise', 'a.', 'Too wise; affectedly wise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overwit', 'v. t.', 'To outwit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overword', 'v. t.', 'To say in too many words; to express verbosely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overworked', 'imp. & p. p.', 'of Overwork');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overwrought', '', 'of Overwork');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overworking', 'p. pr. & vb. n.', 'of Overwork');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overwork', 'v. t.', 'To work beyond the strength; to cause to labor too\n   much or too long; to tire excessively; as, to overwork a horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overwork', 'v. t.', 'To fill too full of work; to crowd with labor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overwork', 'v. t.', 'To decorate all over.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overwork', 'v. t.', 'To work too much, or beyond ones strength.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overwork', 'n.', 'Work in excess of the usual or stipulated time or\n   quantity; extra work; also, excessive labor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overworn', 'v. t.', 'Worn out or subdued by toil; worn out so as to be\n   trite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overwrest', 'v. t.', 'To wrest or force from the natural or proper\n   position.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overwrestle', 'v. t.', 'To subdue by wrestling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overwrought', 'p. p. & a.', 'Wrought upon excessively; overworked;\n   overexcited.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overzeal', 'n.', 'Excess of zeal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Overzealous', 'a.', 'Too zealous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ovicapsule', 'n.', 'The outer layer of a Graafian follicle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ovicapsule', 'n.', 'Same as Ootheca.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ovicell', 'n.', 'One of the dilatations of the body wall of Bryozoa in\n   which the ova sometimes undegro the first stages of their development.\n   See Illust. of Chilostoma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ovioular', 'a.', 'Of or pertaining to an egg.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ovicyst', 'n.', 'The pouch in which incubation takes place in some\n   Tunicata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ovidian', 'a.', 'Of or pertaining to the Latin poet Ovid; resembling the\n   style of Ovid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oviducal', 'a.', 'Of or pertaining to oviducts; as, oviducal glands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oviduct', 'n.', 'A tube, or duct, for the passage of ova from the ovary to\n   the exterior of the animal or to the part where further development\n   takes place. In mammals the oviducts are also called Fallopian tubes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oviferous', 'a.', 'Egg-bearing; -- applied particularly to certain\n   receptacles, as in Crustacea, that retain the eggs after they have been\n   excluded from the formative organs, until they are hatched.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oviform', 'a.', 'Having the form or figure of an egg; egg-shaped; as, an\n   oviform leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ovigerons', 'a.', 'Bearing eggs; oviferous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ovile', 'a.', 'See Ovine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ovine', 'a.', 'Of or pertaining to sheep; consisting of sheep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ovipara', 'n. pl.', 'An artifical division of vertebrates, including those\n   that lay eggs; -- opposed to Vivipara.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oviparity', 'n.', 'Generation by means of ova. See Generation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oviparous', 'a.', 'Producing young from rggs; as, an oviparous animal, in\n   which the egg is generally separated from the animal, and hatched after\n   exclusion; -- opposed to viviparous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oviposited', 'imp. & p. p.', 'of Oviposit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ovipositing', 'p. pr. & vb. n.', 'of Oviposit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oviposit', 'v. i.', 'To lay or deposit eggs; -- said esp. of insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oviposit', 'v. t.', 'To deposit or lay (an egg).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ovipositing', 'n.', 'Alt. of Oviposition');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oviposition', 'n.', 'The depositing of eggs, esp. by insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ovipositor', 'n.', 'The organ with which many insects and some other\n   animals deposit their eggs. Some ichneumon files have a long ovipositor\n   fitted to pierce the eggs or larvae of other insects, in order to lay\n   their own eggs within the same.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ovisac', 'n.', 'A Graafian follicle; any sac containing an ovum or ova.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ovisac', 'n.', 'The inner layer of the fibrous wall of a Graafian follicle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ovist', 'n.', 'Same as Ovulist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ovococci', 'pl. ', 'of Ovococcus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ovococcus', 'n.', 'A germinal vesicle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ovoid', 'a.', 'Alt. of Ovoidal');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ovoidal', 'a.', 'Resembling an egg in shape; egg-shaped; ovate; as, an\n   ovoidal apple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ovoid', 'n.', 'A solid resembling an egg in shape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ovolo', 'n.', 'A round, convex molding. See Illust. of Column.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ovology', 'n.', 'That branch of natural history which treats of the origin\n   and functions of eggs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ovoplasma', 'n.', 'Yolk; egg yolk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ovotesttis', 'n.', 'An organ which produces both ova and spermatozoids; an\n   hermaphrodite gland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ovoviviparous', 'a.', 'Oviparous, but hatching the egg while it is within\n   the body, as some fishes and reptiles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ovular', 'a.', 'Relating or belonging to an ovule; as, an ovular growth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ovulary', 'a.', 'Pertaining to ovules.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ovulate', 'a.', 'Containing an ovule or ovules.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ovulation', 'n.', 'The formation of ova or eggs in the ovary, and the\n   discharge of the same. In the mammalian female the discharge occurs\n   during menstruation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ovule', 'n.', 'The rudiment of a seed. It grows from a placenta, and\n   consists of a soft nucleus within two delicate coatings. The attached\n   base of the ovule is the hilum, the coatings are united with the\n   nucleus at the chalaza, and their minute orifice is the foramen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ovule', 'n.', 'An ovum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ovuliferous', 'a.', 'Producing ovules.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ovulist', 'n.', 'A believer in the theory (called encasement theory),\n   current during the last century, that the egg was the real animal germ,\n   and that at the time of fecundation the spermatozoa simply gave the\n   impetus which caused the unfolding of the egg, in which all generations\n   were inclosed one within the other. Also called ovist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ovulite', 'n.', 'A fossil egg.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ovula', 'pl. ', 'of Ovulum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ovulum', 'n.', 'An ovule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ova', 'pl. ', 'of Ovum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ovums', 'pl. ', 'of Ovum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ovum', 'n.', 'A more or less spherical and transparent mass of granular\n   protoplasm, which by a process of multiplication and growth develops\n   into a mass of cells, constituting a new individual like the parent; an\n   egg, spore, germ, or germ cell. See Illust. of Mycropyle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ovum', 'n.', 'One of the series of egg-shaped ornaments into which the\n   ovolo is often carved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Owch', 'n.', 'See Ouch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Owed', 'imp. & p. p.', 'of Owe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ought', '', 'of Owe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Owing', 'p. pr. & vb. n.', 'of Owe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Owe', 'v.', 'To possess; to have, as the rightful owner; to own.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Owe', 'v.', 'To have or possess, as something derived or bestowed; to be\n   obliged to ascribe (something to some source); to be indebted or\n   obliged for; as, he owed his wealth to his father; he owed his victory\n   to his lieutenants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Owe', 'v.', 'Hence: To have or be under an obigation to restore, pay, or\n   render (something) in return or compensation for something received; to\n   be indebted in the sum of; as, the subject owes allegiance; the\n   fortunate owe assistance to the unfortunate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Owe', 'v.', 'To have an obligation to (some one) on account of something\n   done or received; to be indebted to; as, to iwe the grocer for\n   supplies, or a laborer for services.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Owel', 'a.', 'Equal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Owelty', 'n.', 'Equality; -- sometimes written ovelty and ovealty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Owen', 'a.', 'Own.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Owenite', 'n.', 'A follower of Robert Owen, who tried to reorganize society\n   on a socialistic basis, and established an industrial community on the\n   Clyde, Scotland, and, later, a similar one in Indiana.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Owher', 'adv.', 'Anywhere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Owing', 'P. p. & a.', 'Had or held under obligation of paying; due.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Owing', 'P. p. & a.', 'Had or experienced as a consequence, result, issue,\n   etc.; ascribable; -- with to; as, misfortunes are often owing to vices;\n   his failure was owing to speculations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Owl', 'n.', 'Any species of raptorial birds of the family Strigidae. They\n   have large eyes and ears, and a conspicuous circle of feathers around\n   each eye. They are mostly nocturnal in their habits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Owl', 'n.', 'A variety of the domestic pigeon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Owled', 'imp. & p. p.', 'of Owl');
  INSERT INTO words (word, word_type, description) VALUES  (    'Owling', 'p. pr. & vb. n.', 'of Owl');
  INSERT INTO words (word, word_type, description) VALUES  (    'Owl', 'v. i.', 'To pry about; to prowl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Owl', 'v. i.', 'To carry wool or sheep out of England.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Owl', 'v. i.', 'Hence, to carry on any contraband trade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Owler', 'v. i.', 'One who owls; esp., one who conveys contraband goods. See\n   Owling, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Owleries', 'pl. ', 'of Owlery');
  INSERT INTO words (word, word_type, description) VALUES  (    'Owlery', 'n.', 'An abode or a haunt of owls.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Owlet', 'n.', 'A small owl; especially, the European species (Athene\n   noctua), and the California flammulated owlet (Megascops flammeolus).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Owl-eyed', 'a.', 'Having eyes like an owls.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Owling', 'v. i.', 'The offense of transporting wool or sheep out of England\n   contrary to the statute formerly existing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Owlish', 'a.', 'Resembling, or characteristic of, an owl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Owlism', 'n.', 'Affected wisdom; pompous dullness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Owllight', 'n.', 'Glimmering or imperfect light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Own', 'v. t.', 'To grant; to acknowledge; to admit to be true; to confess;\n   to recognize in a particular character; as, we own that we have\n   forfeited your love.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Own', 'a.', 'Belonging to; belonging exclusively or especially to;\n   peculiar; -- most frequently following a possessive pronoun, as my,\n   our, thy, your, his, her, its, their, in order to emphasize or\n   intensify the idea of property, peculiar interest, or exclusive\n   ownership; as, my own father; my own composition; my own idea; at my\n   own price.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Owned', 'imp. & p. p.', 'of Own');
  INSERT INTO words (word, word_type, description) VALUES  (    'Owning', 'p. pr. & vb. n.', 'of Own');
  INSERT INTO words (word, word_type, description) VALUES  (    'Own', 'a.', 'To hold as property; to have a legal or rightful title to; to\n   be the proprietor or possessor of; to possess; as, to own a house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Owner', 'n.', 'One who owns; a rightful proprietor; one who has the legal\n   or rightful title, whether he is the possessor or not.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ownerless', 'a.', 'Without an owner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ownership', 'n.', 'The state of being an owner; the right to own; exclusive\n   right of possession; legal or just claim or title; proprietorship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Owre', 'n.', 'The aurochs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Owse', 'n.', 'Alt. of Owser');
  INSERT INTO words (word, word_type, description) VALUES  (    'Owser', 'n.', 'Tanners ooze. See Ooze, 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxen', 'pl. ', 'of Ox');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ox', 'n.', 'The male of bovine quadrupeds, especially the domestic animal\n   when castrated and grown to its full size, or nearly so. The word is\n   also applied, as a general name, to any species of bovine animals, male\n   and female.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxacid', 'n.', 'See Oxyacid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxalan', 'n.', 'A complex nitrogenous substance C3N3H5O3 obtained from\n   alloxan (or when urea is fused with ethyl oxamate), as a stable white\n   crystalline powder; -- called also oxaluramide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxalantin', 'n.', 'A white crystalline nitrogenous substance (C6H4N4O5)\n   obtained by the reduction of parabanic acid; -- called also leucoturic\n   acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxalate', 'n.', 'A salt of oxalic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxaldehyde', 'n.', 'Same as Glyoxal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxalethyline', 'n.', 'A poisonous nitrogenous base (C6H10N2) obtained\n   indirectly from oxamide as a thick transparent oil which has a strong\n   narcotic odor, and a physiological action resembling that of atropine.\n   It is probably related to pyridine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxalic', 'a.', 'Pertaining to, derived from, or contained in, sorrel, or\n   oxalis; specifically, designating an acid found in, and characteristic\n   of, oxalis, and also certain plant of the Buckwheat family.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxaline', 'n.', 'See Glyoxaline.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxalis', 'n.', 'A genus of plants, mostly herbs, with acid-tasting\n   trifoliolate or multifoliolate leaves; -- called also wood sorrel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxalite', 'n.', 'A yellow mineral consisting of oxalate of iron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxaluramide', 'n.', 'Same as Oxalan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxalurate', 'n.', 'A salt of oxaluric acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxaluric', 'a.', 'Pertaining to, or designating, a complex nitrogenous acid\n   related to the ureids, and obtained from parabanic acid as a white\n   silky crystalline substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxalyl', 'n.', 'A hydrocarbon radical (C2O2) regarded as a residue of\n   oxalic acid and occurring in derivatives of it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxalyl', 'n.', 'An old name for carbonyl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxalyl', 'n.', 'An old name for carboxyl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxamate', 'n.', 'A salt of oxamic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxamethane', 'n.', 'Ethyl oxamate, obtained as a white scaly crystalline\n   powder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxamethylane', 'n.', 'Methyl oxamate, obtained as a pearly white\n   crystalline substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxamic', 'a.', 'Pertaining to, or designating, an acid NH2.C2O2.HO obtained\n   as a fine crystalline powder, intermediate between oxalic acid and\n   oxamide. Its ammonium salt is obtained by boiling oxamide with ammonia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxamide', 'n', 'A white crystalline neutral substance (C2O2(NH2)2) obtained\n   by treating ethyl oxalate with ammonia. It is the acid amide of oxalic\n   acid. Formerly called also oxalamide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxamidine', 'n.', 'One of a series of bases containing the amido and the\n   isonitroso groups united to the same carbon atom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxanillamide', 'n.', 'A white crystalline nitrogenous substance, obtained\n   indirectly by the action of cyanogen on aniline, and regarded as an\n   anilide of oxamic acid; -- called also phenyl oxamide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxanilate', 'n.', 'A salt of oxanilic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxanilic', 'a.', 'Pertaining to, or derived from, oxalic acid and aniline;\n   -- used to designate an acid obtained in white crystalline scales by\n   heating these substances together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxanilide', 'n.', 'a white crystalline substance, resembling oxanilamide,\n   obtained by heating aniline oxalate, and regarded as a double anilide\n   of oxalic acid; -- called also diphenyl oxamide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxbane', 'n.', 'A poisonous bulbous plant (Buphane toxicaria) of the Cape\n   of Good Hope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxbird', 'n.', 'The dunlin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxbird', 'n.', 'The sanderling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxbird', 'n.', 'An African weaver bird (Textor alector).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxbiter', 'n.', 'The cow blackbird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxbow', 'n.', 'A frame of wood, bent into the shape of the letter U, and\n   embracing an oxs neck as a kind of collar, the upper ends passing\n   through the bar of the yoke; also, anything so shaped, as a bend in a\n   river.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxeye', 'n.', 'The oxeye daisy. See under Daisy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxeye', 'n.', 'The corn camomile (Anthemis arvensis).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxeye', 'n.', 'A genus of composite plants (Buphthalmum) with large yellow\n   flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxeye', 'n.', 'A titmouse, especially the great titmouse (Parus major) and\n   the blue titmouse (P. coeruleus).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxeye', 'n.', 'The dunlin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxeye', 'n.', 'A fish; the bogue, or box.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxeyed', 'a.', 'Having large, full eyes, like those of an ox.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxfly', 'n.', 'The gadfly of cattle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxford', 'a.', 'Of or pertaining to the city or university of Oxford,\n   England.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxgang', 'n.', 'See Bovate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxgoad', 'n.', 'A goad for driving oxen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxhead', 'n.', 'Literally, the head of an ox (emblem of cuckoldom); hence,\n   a dolt; a blockhead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxheal', 'n.', 'Same as Bears-foot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxheart', 'n.', 'A large heart-shaped cherry, either black, red, or white.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxhide', 'n.', 'The skin of an ox, or leather made from it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxhide', 'n.', 'A measure of land. See 3d Hide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxid', 'n.', 'See Oxide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxidability', 'n.', 'Capability of being converted into an oxide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxidable', 'a.', 'Capable of being converted into an oxide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxidated', 'imp. & p. p.', 'of Oxidate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxidating', 'p. pr. & vb. n.', 'of Oxidate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxidate', 'v. t.', 'To oxidize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxidation', 'n.', 'The act or process of oxidizing, or the state or result\n   of being oxidized.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxidator', 'n.', 'An oxidizer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxidator', 'n.', 'A contrivance for causing a current of air to impinge on\n   the flame of the Argand lamp; -- called also oxygenator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxide', 'n.', 'A binary compound of oxygen with an atom or radical, or a\n   compound which is regarded as binary; as, iron oxide, ethyl oxide,\n   nitrogen oxide, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxidizable', 'a.', 'Capable of being oxidized.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxidized', 'imp. & p. p.', 'of Oxidize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxidizing', 'p. pr. & vb. n.', 'of Oxidize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxidize', 'v. t.', 'To combine with oxygen, or subject to the action of\n   oxygen, or of an oxidizing agent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxidize', 'v. t.', 'To combine with oxygen or with more oxygen; to add\n   oxygen to; as, to oxidize nitrous acid so as to form nitric acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxidize', 'v. t.', 'To remove hydrogen from (anything), as by the action of\n   oxygen; as, to oxidize alcohol so as to form aldehyde.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxidize', 'v. t.', 'To subject to the action of oxygen or of an oxidizing\n   agent, so as to bring to a higher grade, as an -ous compound to an -ic\n   compound; as, to oxidize mercurous chloride to mercuric chloride.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxidizement', 'n.', 'Oxidation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxidizer', 'n.', 'An agent employed in oxidation, or which facilitates or\n   brings about combination with oxygen; as, nitric acid, chlorine,\n   bromine, etc., are strong oxidizers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxidulated', 'a.', 'Existing in the state of a protoxide; -- said of an\n   oxide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxime', 'n.', 'One of a series of isonitroso derivatives obtained by the\n   action of hydroxylamine on aldehydes or ketones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxindol', 'n.', 'A white crystalline nitrogenous substance (C8H7NO) of the\n   indol group, obtained by the reduction of dioxindol. It is a so-called\n   lactam compound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxiodic', 'a.', 'Pertaining to, or designating, certain compounds of iodine\n   and oxygen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxlike', 'a.', 'Characteristic of, or like, an ox.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxlip', 'n.', 'The great cowslip (Primula veris, var. elatior).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxonate', 'n.', 'A salt of oxonic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxonian', 'a.', 'Of or relating to the city or the university of Oxford,\n   England.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxonian', 'n.', 'A student or graduate of Oxford University, in England.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxonic', 'a.', 'Pertaining to, or designating, a complex nitrogenous acid\n   (C4H5N3O4) not known in the free state, but obtained, in combination\n   with its salts, by a slow oxidation of uric acid, to which it is\n   related.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxpecker', 'n.', 'An African bird of the genus Buphaga; the beefeater.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxshoe', 'n.', 'A shoe for oxen, consisting of a flat piece of iron nailed\n   to the hoof.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxter', 'n.', 'The armpit; also, the arm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxtongue', 'n.', 'A name given to several plants, from the shape and\n   roughness of their leaves; as, Anchusa officinalis, a kind of bugloss,\n   and Helminthia echioides, both European herbs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxy-', '', 'A prefix, also used adjectively');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxy-', '', 'A compound containing oxygen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxy-', '', 'A compound containing the hydroxyl group, more properly\n   designated by hydroxy-. See Hydroxy-.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxyacetic', 'a.', 'Hydroxyacetic; designating an acid called also glycolic\n   acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxyacid', 'n.', 'An acid containing oxygen, as chloric acid or sulphuric\n   acid; -- contrasted with the hydracids, which contain no oxygen, as\n   hydrochloric acid. See Acid, and Hydroxy-.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxyammonia', 'n.', 'Same as Hydroxylamine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxybenzene', 'n.', 'Hydroxy benzene. Same as Phenol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxybenzoic', 'a.', 'Hydroxybenzoic; pertaining to, or designating, any one\n   of several hydroxyl derivatives of benzonic acid, of which the\n   commonest is salicylic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxybromic', 'a.', 'Pertaining to, or designating, certain compounds of\n   oxygen and bromine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxybutyric', 'a.', 'Hydroxybutyric; designating any one of a group of\n   metameric acids (C3H6.OH.CO2H).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxycalcium', 'a.', 'Of or pertaining to oxygen and calcium; as, the\n   oxycalcium light. See Drummond light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxycaproic', 'a.', 'See Leucic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxychloric', 'a.', 'Of, pertaining to, or designating in general, certain\n   compounds containing oxygen and chlorine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxychloric', 'a.', 'Formerly designating an acid now called perchloric\n   acid. See Perchloric.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxychloride', 'n.', 'A ternary compound of oxygen and chlorine; as, plumbic\n   oxychloride.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxycrate', 'n.', 'A Mixture of water and vinegar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxycymene', 'n.', 'Hydroxy cymene. Same as Carvacrol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxygen', 'n.', 'A colorless, tasteless, odorless, gaseous element occurring\n   in the free state in the atmosphere, of which it forms about 23 per\n   cent by weight and about 21 per cent by volume, being slightly heavier\n   than nitrogen. Symbol O. Atomic weight 15.96.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxygen', 'n.', 'Chlorine used in bleaching.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxygenated', 'imp. & p. p.', 'of Oxygenate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxygenating', 'p. pr. & vb. n.', 'of Oxygenate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxygenate', 'v. t.', 'To unite, or cause to combine, with oxygen; to treat\n   with oxygen; to oxidize; as, oxygenated water (hydrogen dioxide).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxygenation', 'n.', 'The act or process of combining or of treating with\n   oxygen; oxidation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxygenator', 'n.', 'An oxidizer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxygenic', 'a.', 'Pertaining to, containing, or resembling, oxygen;\n   producing oxygen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxygenium', 'n.', 'The technical name of oxygen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxygenizable', 'a.', 'Oxidizable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxygenized', 'imp. & p. p.', 'of Oxygenize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxygenizing', 'p pr. & vb. n.', 'of Oxygenize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxygenize', 'v. t.', 'To oxidize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxygenizement', 'n.', 'Oxidation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxygenous', 'a.', 'Oxygenic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxygon', 'n.', 'A triangle having three acute angles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxygonal', 'a.', 'Alt. of Oxygonial');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxygonial', 'a.', 'Having acute angles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'OxYhaemacyanin', 'n.', 'Alt. of Oxyhaemocyanin');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxyhaemocyanin', 'n.', 'See Haemacyanin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxyhaemoglobin', 'n.', 'Alt. of Oxyhemoglobin');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxyhemoglobin', 'n.', 'See Hemoglobin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxyhydrogen', 'a.', 'Of or pertaining to a mixture of oxygen and hydrogen;\n   as, oxyhydrogen gas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxymel', 'n.', 'A mixture of honey, water, vinegar, and spice, boiled to a\n   sirup.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxymethylene', 'n.', 'Formic aldehyde, regarded as a methylene derivative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxymoron', 'n.', 'A figure in which an epithet of a contrary signification\n   is added to a word; e. g., cruel kindness; laborious idleness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxymuriate', 'n.', 'A salt of the supposed oxymuriatic acid; a chloride.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxymuriatic', 'a.', 'Pertaining to, or consisting of, oxygen and muriatic\n   acid, that is, hydrochloric acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxyneurine', 'n.', 'See Betaine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxyntic', 'a.', 'Acid; producing acid; -applied especially to certain\n   glands and cells in the stomach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxyopia', 'n.', 'Alt. of Oxyopy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxyopy', 'n.', 'Excessive acuteness of sight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxyphenic', 'a.', 'Pertaining to, or designating, the phenol formerly\n   called oxyphenic acid, and now oxyphenol and pyrocatechin. See\n   Pyrocatechin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxyphenol', 'n.', 'A phenol, /////, produced by the distillation of\n   catechin; called also oxyphenic acid, and now pyrocatechin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxyphony', 'n.', 'Acuteness or shrillness of voice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxyquinoline', 'n.', 'Hydroxy quinoline; a phenol derivative of quinoline,\n   -- called also carbostyril.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxyrhyncha', 'n. pl.', 'The maioid crabs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxyrrhodine', 'n.', 'A mixture of two parts of the oil of roses with one of\n   the vinegar of roses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxysalt', 'n.', 'A salt of an oxyacid, as a sulphate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxysulphide', 'n.', 'A ternary compound of oxygen and sulphur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxysulphuret', 'n.', 'An oxysulphide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxytocic', 'a.', 'Promoting uterine contractions, or parturition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxytocic', 'n.', 'An oxytocic medicine or agent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxytoluene', 'n.', 'One of three hydroxy derivatives of toluene, called the\n   cresols. See Cresol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxytone', 'a.', 'Having an acute sound; (Gr. Gram.), having an acute accent\n   on the last syllable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxytone', 'n.', 'An acute sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxytone', 'n.', 'A word having the acute accent on the last syllable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oxytonical', 'a.', 'Oxytone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oyer', 'n.', 'A hearing or an inspection, as of a deed, bond, etc., as when\n   a defendant in court prays oyer of a writing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oyez', 'interj.', 'Hear; attend; -- a term used by criers of courts to\n   secure silence before making a proclamation. It is repeated three\n   times.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oylet', 'n.', 'See Eyelet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oylet', 'n.', 'Same as Oillet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oynoun', 'n.', 'Onion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oyster', 'n.', 'Any marine bivalve mollusk of the genus Ostrea. They are\n   usually found adhering to rocks or other fixed objects in shallow water\n   along the seacoasts, or in brackish water in the mouth of rivers. The\n   common European oyster (Ostrea edulis), and the American oyster (Ostrea\n   Virginiana), are the most important species.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oyster', 'n.', 'A name popularly given to the delicate morsel contained in\n   a small cavity of the bone on each side of the lower part of the back\n   of a fowl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oyster-green', 'n.', 'A green membranous seaweed (Ulva) often found growing\n   on oysters but common on stones, piles, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oystering', 'n.', 'Gathering, or dredging for, oysters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oysterling', 'n.', 'A young oyster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ozena', 'n.', 'A discharge of fetid matter from the nostril, particularly\n   if associated with ulceration of the soft parts and disease of the\n   bones of the nose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ozocerite', 'n.', 'A waxlike mineral resin; -- sometimes called native\n   paraffin, and mineral wax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ozonation', 'n.', 'The act of treating with ozone; also, the act of\n   converting into, or producing, ozone; ozonization.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ozone', 'n.', 'A colorless gaseous substance (O/) obtained (as by the\n   silent discharge of electricity in oxygen) as an allotropic form of\n   oxygen, containing three atoms in the molecule. It is a streng\n   oxidizer, and probably exists in the air, though by he ordinary tests\n   it is liable to be confused with certain other substances, as hydrogen\n   dioxide, or certain oxides of nitrogen. It derives its name from its\n   peculiar odor, which resembles that of weak chlorine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ozonic', 'a.', 'Pertaining to, resembling, or containing, ozone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ozonification', 'n.', 'The act or process of producing, or of subjecting to\n   the action of, ozone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ozonization', 'n.', 'Ozonation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ozonized', 'imp. & p. p.', 'of Ozonize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ozonizing', 'p. pr. & vb. n.', 'of Ozonize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ozonize', 'v. t.', 'To convert into ozone, as oxygen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ozonize', 'v. t.', 'To treat with ozone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ozonizer', 'n.', 'An apparatus or agent for the production or application\n   of ozone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ozonometer', 'n.', 'An instrument for ascertaining the amount of ozone in\n   the atmosphere, or in any gaseous mixture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ozonometric', 'a.', 'Pertaining to, or used for, the determination of the\n   amount of ozone; of or relating to ozonometry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ozonometry', 'n.', 'The measurement or determination of the quantity of\n   ozone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ozonoscope', 'n.', 'An apparatus employed to indicate the presence, or the\n   amount, of ozone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ozonoscopic', 'a.', 'Serving to indicate the presence or the amount of\n   ozone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ozonous', 'a.', 'Pertaining to or containing, ozone.\n   P () the sixteenth letter of the English alphabet, is a nonvocal\n   consonant whose form and value come from the Latin, into which language\n   the letter was brought, through the ancient Greek, from the Phoenician,\n   its probable origin being Egyptian. Etymologically P is most closely\n   related to b, f, and v; as hobble, hopple; father, paternal; recipient,\n   receive. See B, F, and M.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pa', 'n.', 'A shortened form of Papa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paage', 'n.', 'A toll for passage over another persons grounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paard', 'n.', 'The zebra.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paas', 'n.', 'Pace');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paas', 'n.', 'The Easter festival.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pabular', 'a.', 'Of, pertaining to, or fit for, pabulum or food; affording\n   food.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pabulation', 'n.', 'The act of feeding, or providing food.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pabulation', 'n.', 'Food; fodder; pabulum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pabulous', 'a.', 'Affording pabulum, or food; alimental.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pabulum', 'n.', 'The means of nutriment to animals or plants; food;\n   nourishment; hence, that which feeds or sustains, as fuel for a fire;\n   that upon which the mind or soul is nourished; as, intellectual\n   pabulum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pac', 'n.', 'A kind of moccasin, having the edges of the sole turned up and\n   sewed to the upper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paca', 'n.', 'A small South American rodent (Coelogenys paca), having\n   blackish brown fur, with four parallel rows of white spots along its\n   sides; the spotted cavy. It is nearly allied to the agouti and the\n   Guinea pig.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pacable', 'a.', 'Placable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pacane', 'n.', 'A species of hickory. See Pecan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pacate', 'a.', 'Appeased; pacified; tranquil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pacated', 'a.', 'Pacified; pacate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pacation', 'n.', 'The act of pacifying; a peacemaking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pace', 'n.', 'A single movement from one foot to the other in walking; a\n   step.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pace', 'n.', 'The length of a step in walking or marching, reckoned from\n   the heel of one foot to the heel of the other; -- used as a unit in\n   measuring distances; as, he advanced fifty paces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pace', 'n.', 'Manner of stepping or moving; gait; walk; as, the walk, trot,\n   canter, gallop, and amble are paces of the horse; a swaggering pace; a\n   quick pace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pace', 'n.', 'A slow gait; a footpace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pace', 'n.', 'Specifically, a kind of fast amble; a rack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pace', 'n.', 'Any single movement, step, or procedure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pace', 'n.', 'A broad step or platform; any part of a floor slightly raised\n   above the rest, as around an altar, or at the upper end of a hall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pace', 'n.', 'A device in a loom, to maintain tension on the warp in pacing\n   the web.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paced', 'imp. & p. p.', 'of Pace');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pacing', 'p. pr. & vb. n.', 'of Pace');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pace', 'v. i.', 'To go; to walk; specifically, to move with regular or\n   measured steps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pace', 'v. i.', 'To proceed; to pass on.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pace', 'v. i.', 'To move quickly by lifting the legs on the same side\n   together, as a horse; to amble with rapidity; to rack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pace', 'v. i.', 'To pass away; to die.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pace', 'v. t.', 'To walk over with measured tread; to move slowly over or\n   upon; as, the guard paces his round.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pace', 'v. t.', 'To measure by steps or paces; as, to pace a piece of\n   ground.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pace', 'v. t.', 'To develop, guide, or control the pace or paces of; to\n   teach the pace; to break in.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paced', 'a.', 'Having, or trained in, [such] a pace or gait; trained; --\n   used in composition; as, slow-paced; a thorough-paced villain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pacer', 'n.', 'One who, or that which, paces; especially, a horse that\n   paces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pacha', 'n.', 'See Pasha.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pachacamac', 'n.', 'A divinity worshiped by the ancient Peruvians as the\n   creator of the universe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pachak', 'n.', 'The fragrant roots of the Saussurea Costus, exported from\n   India to China, and used for burning as incense. It is supposed to be\n   the costus of the ancients.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pachalic', 'a. & n.', 'See Pashalic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pachisi', 'n.', 'Alt. of Parchesi');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parchesi', 'n.', 'A game, somewhat resembling backgammon, originating in\n   India.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pachometer', 'n.', 'An instrument for measuring thickness, as of the glass\n   of a mirror, or of paper; a pachymeter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pachonta', 'n.', 'A substance resembling gutta-percha, and used to\n   adulterate it, obtained from the East Indian tree Isonandra acuminata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pachy-', '', 'A combining form meaning thick; as, pachyderm, pachydactyl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pachycarpous', 'a.', 'Having the pericarp thick.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pachydactyl', 'n.', 'A bird or other animal having thick toes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pachydactylous', 'a.', 'Having thick toes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pachyderm', 'n.', 'One of the Pachydermata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pachydermal', 'a.', 'Of or relating to the pachyderms; as, pachydermal\n   dentition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pachydermata', 'n. pl.', 'A group of hoofed mammals distinguished for the\n   thickness of their skins, including the elephant, hippopotamus,\n   rhinoceros, tapir, horse, and hog. It is now considered an artificial\n   group.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pachydermatous', 'a.', 'Of or pertaining to the pachyderms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pachydermatous', 'a.', 'Thick-skinned; not sensitive to ridicule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pachydermoid', 'a.', 'Related to the pachyderms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pachyglossal', 'a.', 'Having a thick tongue; -- applied to a group of\n   lizards (Pachyglossae), including the iguanas and agamas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pachymeningitis', 'n.', 'Inflammation of the dura mater or outer membrane\n   of the brain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pachymeter', 'n.', 'Same as Pachometer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pachyote', 'n.', 'One of a family of bats, including those which have thick\n   external ears.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pacifiable', 'a.', 'Capable of being pacified or appeased; placable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pacific', 'a.', 'Of or pertaining to peace; suited to make or restore\n   peace; of a peaceful character; not warlike; not quarrelsome;\n   conciliatory; as, pacific words or acts; a pacific nature or condition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pacificable', 'a.', 'Placable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pacifical', 'a.', 'Of or pertaining to peace; pacific.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pacification', 'n.', 'The act or process of pacifying, or of making peace\n   between parties at variance; reconciliation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pacificator', 'n.', 'One who, or that which, pacifies; a peacemaker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pacificatory', 'a.', 'Tending to make peace; conciliatory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pacfier', 'n.', 'One who pacifies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pacified', 'imp. & p. p.', 'of Pacify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pacifying', 'p. pr. & vb. n.', 'of Pacify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pacify', 'v. t.', 'To make to be at peace; to appease; to calm; to still;\n   to quiet; to allay the agitation, excitement, or resentment of; to\n   tranquillize; as, to pacify a man when angry; to pacify pride,\n   appetite, or importunity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pacinian', 'a.', 'Of, pertaining to, or discovered by, Filippo Pacini, an\n   Italian physician of the 19th century.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pack', 'n.', 'A pact.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pack', 'n.', 'A bundle made up and prepared to be carried; especially, a\n   bundle to be carried on the back; a load for an animal; a bale, as of\n   goods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pack', 'n.', 'A number or quantity equal to the contents of a pack; hence,\n   a multitude; a burden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pack', 'n.', 'A number or quantity of connected or similar things');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pack', 'n.', 'A full set of playing cards; also, the assortment used in a\n   particular game; as, a euchre pack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pack', 'n.', 'A number of hounds or dogs, hunting or kept together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pack', 'n.', 'A number of persons associated or leagued in a bad design or\n   practice; a gang; as, a pack of thieves or knaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pack', 'n.', 'A shook of cask staves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pack', 'n.', 'A bundle of sheet-iron plates for rolling simultaneously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pack', 'n.', 'A large area of floating pieces of ice driven together more\n   or less closely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pack', 'n.', 'An envelope, or wrapping, of sheets used in hydropathic\n   practice, called dry pack, wet pack, cold pack, etc., according to the\n   method of treatment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pack', 'n.', 'A loose, lewd, or worthless person. See Baggage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Packed', 'imp. & p. p.', 'of Pack');
  INSERT INTO words (word, word_type, description) VALUES  (    'Packing', 'p. pr. & vb. n.', 'of Pack');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pack', 'n.', 'To make a pack of; to arrange closely and securely in a pack;\n   hence, to place and arrange compactly as in a pack; to press into close\n   order or narrow compass; as to pack goods in a box; to pack fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pack', 'n.', 'To fill in the manner of a pack, that is, compactly and\n   securely, as for transportation; hence, to fill closely or to\n   repletion; to stow away within; to cause to be full; to crowd into; as,\n   to pack a trunk; the play, or the audience, packs the theater.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pack', 'n.', 'To sort and arrange (the cards) in a pack so as to secure the\n   game unfairly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pack', 'n.', 'Hence: To bring together or make up unfairly and\n   fraudulently, in order to secure a certain result; as, to pack a jury\n   or a causes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pack', 'n.', 'To contrive unfairly or fraudulently; to plot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pack', 'n.', 'To load with a pack; hence, to load; to encumber; as, to pack\n   a horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pack', 'n.', 'To cause to go; to send away with baggage or belongings;\n   esp., to send away peremptorily or suddenly; -- sometimes with off; as,\n   to pack a boy off to school.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pack', 'n.', 'To transport in a pack, or in the manner of a pack (i. e., on\n   the backs of men or beasts).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pack', 'n.', 'To envelop in a wet or dry sheet, within numerous coverings.\n   See Pack, n., 5.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pack', 'n.', 'To render impervious, as by filling or surrounding with\n   suitable material, or to fit or adjust so as to move without giving\n   passage to air, water, or steam; as, to pack a joint; to pack the\n   piston of a steam engine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pack', 'v. i.', 'To make up packs, bales, or bundles; to stow articles\n   securely for transportation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pack', 'v. i.', 'To admit of stowage, or of making up for transportation or\n   storage; to become compressed or to settle together, so as to form a\n   compact mass; as, the goods pack conveniently; wet snow packs well.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pack', 'v. i.', 'To gather in flocks or schools; as, the grouse or the\n   perch begin to pack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pack', 'v. i.', 'To depart in haste; -- generally with off or away.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pack', 'v. i.', 'To unite in bad measures; to confederate for ill purposes;\n   to join in collusion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Package', 'n.', 'Act or process of packing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Package', 'n.', 'A bundle made up for transportation; a packet; a bale; a\n   parcel; as, a package of goods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Package', 'n.', 'A charge made for packing goods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Package', 'n.', 'A duty formerly charged in the port of London on goods\n   imported or exported by aliens, or by denizens who were the sons of\n   aliens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Packer', 'n.', 'A person whose business is to pack things; especially, one\n   who packs food for preservation; as, a pork packer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Packet', 'n.', 'A small pack or package; a little bundle or parcel; as, a\n   packet of letters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Packet', 'n.', 'Originally, a vessel employed by government to convey\n   dispatches or mails; hence, a vessel employed in conveying dispatches,\n   mails, passengers, and goods, and having fixed days of sailing; a mail\n   boat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Packeted', 'imp. & p. p.', 'of Packet');
  INSERT INTO words (word, word_type, description) VALUES  (    'Packeting', 'p. pr. & vb. n.', 'of Packet');
  INSERT INTO words (word, word_type, description) VALUES  (    'Packet', 'v. t.', 'To make up into a packet or bundle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Packet', 'v. t.', 'To send in a packet or dispatch vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Packet', 'v. i.', 'To ply with a packet or dispatch boat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Packfong', 'n.', 'A Chinese alloy of nickel, zinc, and copper, resembling\n   German silver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pack herse', '', 'See under 2d Pack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Packhouse', 'n.', 'Warehouse for storing goods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Packing', 'n.', 'The act or process of one who packs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Packing', 'n.', 'Any material used to pack, fill up, or make close.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Packing', 'n.', 'A substance or piece used to make a joint impervious');
  INSERT INTO words (word, word_type, description) VALUES  (    'Packing', 'n.', 'A thin layer, or sheet, of yielding or elastic material\n   inserted between the surfaces of a flange joint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Packing', 'n.', 'The substance in a stuffing box, through which a piston\n   rod slides.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Packing', 'n.', 'A yielding ring, as of metal, which surrounds a piston and\n   maintains a tight fit, as inside a cylinder, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Packing', 'n.', 'Same as Filling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Packing', 'n.', 'A trick; collusion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Packmen', 'pl. ', 'of Packman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Packman', 'n.', 'One who bears a pack; a peddler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pack saddle', '', 'Alt. of Pack thread');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pack thread', '', 'See under 2d Pack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Packwax', 'n.', 'Same as Paxwax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Packway', 'n.', 'A path, as over mountains, followed by pack animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paco', 'n.', 'Alt. of Pacos');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pacos', 'n.', 'Same as Alpaca.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pacos', 'n.', 'An earthy-looking ore, consisting of brown oxide of iron\n   with minute particles of native silver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pact', 'v.', 'An agreement; a league; a compact; a covenant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paction', 'n.', 'An agreement; a compact; a bargain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pactional', 'a.', 'Of the nature of, or by means of, a paction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pactitious', 'a.', 'Setted by a pact, or agreement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pactolian', 'a.', 'Pertaining to the Pactolus, a river in ancient Lydia\n   famous for its golden sands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pacu', 'n.', 'A South American freah-water fish (Myleies pacu), of the\n   family Characinidae. It is highly esteemed as food.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pad', 'n.', 'A footpath; a road.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pad', 'n.', 'An easy-paced horse; a padnag.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pad', 'n.', 'A robber that infests the road on foot; a highwayman; --\n   usually called a footpad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pad', 'n.', 'The act of robbing on the highway.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pad', 'v. t.', 'To travel upon foot; to tread.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pad', 'v. i.', 'To travel heavily or slowly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pad', 'v. i.', 'To rob on foot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pad', 'v. i.', 'To wear a path by walking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pad', 'n.', 'A soft, or small, cushion; a mass of anything soft; stuffing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pad', 'n.', 'A kind of cushion for writing upon, or for blotting; esp., one\n   formed of many flat sheets of writing paper, or layers of blotting\n   paper; a block of paper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pad', 'n.', 'A cushion used as a saddle without a tree or frame.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pad', 'n.', 'A stuffed guard or protection; esp., one worn on the legs of\n   horses to prevent bruising.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pad', 'n.', 'A cushionlike thickening of the skin one the under side of the\n   toes of animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pad', 'n.', 'A floating leaf of a water lily or similar plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pad', 'n.', 'A soft bag or cushion to relieve pressure, support a part,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pad', 'n.', 'A piece of timber fixed on a beam to fit the curve of the\n   deck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pad', 'n.', 'A measure for fish; as, sixty mackerel go to a pad; a basket\n   of soles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Padded', 'imp. & p. p.', 'of Pad');
  INSERT INTO words (word, word_type, description) VALUES  (    'Padding', 'p. pr. & vb. n.', 'of Pad');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pad', 'v. t.', 'To stuff; to furnish with a pad or padding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pad', 'v. t.', 'To imbue uniformly with a mordant; as, to pad cloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Padar', 'n.', 'Groats; coarse flour or meal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Padder', 'n.', 'One who, or that which, pads.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Padder', 'n.', 'A highwayman; a footpad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Padding', 'n.', 'The act or process of making a pad or of inserting\n   stuffing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Padding', 'n.', 'The material with which anything is padded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Padding', 'n.', 'Material of inferior value, serving to extend a book,\n   essay, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Padding', 'n.', 'The uniform impregnation of cloth with a mordant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paddle', 'v. i.', 'To use the hands or fingers in toying; to make caressing\n   strokes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paddle', 'v. i.', 'To dabble in water with hands or feet; to use a paddle,\n   or something which serves as a paddle, in swimming, in paddling a boat,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paddled', 'imp. & p. p.', 'of Paddle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paddling', 'p. pr. & vb. n.', 'of Paddle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paddle', 'v. t.', 'To pat or stroke amorously, or gently.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paddle', 'v. t.', 'To propel with, or as with, a paddle or paddles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paddle', 'v. t.', 'To pad; to tread upon; to trample.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paddle', 'v. i.', 'An implement with a broad blade, which is used without a\n   fixed fulcrum in propelling and steering canoes and boats.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paddle', 'v. i.', 'The broad part of a paddle, with which the stroke is\n   made; hence, any short, broad blade, resembling that of a paddle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paddle', 'v. i.', 'One of the broad boards, or floats, at the circumference\n   of a water wheel, or paddle wheel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paddle', 'v. i.', 'A small gate in sluices or lock gates to admit or let\n   off water; -- also called clough.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paddle', 'v. i.', 'A paddle-shaped foot, as of the sea turtle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paddle', 'v. i.', 'A paddle-shaped implement for string or mixing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paddle', 'v. i.', 'See Paddle staff (b), below.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paddlecock', 'n.', 'The lumpfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paddlefish', 'n.', 'A large ganoid fish (Polyodon spathula) found in the\n   rivers of the Mississippi Valley. It has a long spatula-shaped snout.\n   Called also duck-billed cat, and spoonbill sturgeon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Padder', 'n.', 'One who, or that which, paddles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paddlewood', 'n.', 'The light elastic wood of the Aspidosperma excelsum, a\n   tree of Guiana having a fluted trunk readily split into planks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paddock', 'n.', 'A toad or frog.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paddock', 'n.', 'A small inclosure or park for sporting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paddock', 'n.', 'A small inclosure for pasture; esp., one adjoining a\n   stable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paddy', 'a.', 'Low; mean; boorish; vagabond.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paddies', 'pl. ', 'of Paddy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paddy', 'n.', 'A jocose or contemptuous name for an Irishman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paddy', 'n.', 'Unhusked rice; -- commonly so called in the East Indies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Padelion', 'n.', 'A plant with pedately lobed leaves; the ladys mantle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Padella', 'n.', 'A large cup or deep saucer, containing fatty matter in\n   which a wick is placed, -- used for public illuminations, as at St.\n   Peters, in Rome. Called also padelle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pademelon', 'n.', 'See Wallaby.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Padesoy', 'n.', 'See Paduasoy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Padge', 'n.', 'The barn owl; -- called also pudge, and pudge owl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Padishah', 'n.', 'Chief ruler; monarch; sovereign; -- a title of the Sultan\n   of Turkey, and of the Shah of Persia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Padlock', 'n.', 'A portable lock with a bow which is usually jointed or\n   pivoted at one end so that it can be opened, the other end being\n   fastened by the bolt, -- used for fastening by passing the bow through\n   a staple over a hasp or through the links of a chain, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Padlock', 'n.', 'Fig.: A curb; a restraint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Padlocked', 'imp. & p. p.', 'of Padlock');
  INSERT INTO words (word, word_type, description) VALUES  (    'Padlocking', 'p. pr. & vb. n.', 'of Padlock');
  INSERT INTO words (word, word_type, description) VALUES  (    'Padlock', 'v. t.', 'To fasten with, or as with, a padlock; to stop; to\n   shut; to confine as by a padlock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Padnag', 'n.', 'An ambling nag.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Padow', 'n.', 'A paddock, or toad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Padroni', 'pl. ', 'of Padrone');
  INSERT INTO words (word, word_type, description) VALUES  (    'Padrones', 'pl. ', 'of Padrone');
  INSERT INTO words (word, word_type, description) VALUES  (    'Padrone', 'n.', 'A patron; a protector.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Padrone', 'n.', 'The master of a small coaster in the Mediterranean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Padrone', 'n.', 'A man who imports, and controls the earnings of, Italian\n   laborers, street musicians, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paduasoy', 'n.', 'A rich and heavy silk stuff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paducahs', 'n. pl.', 'See Comanches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paean', 'n.', 'An ancient Greek hymn in honor of Apollo as a healing deity,\n   and, later, a song addressed to other deities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paean', 'n.', 'Any loud and joyous song; a song of triumph.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paean', 'n.', 'See Paeon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paedobaptism', 'n.', 'Pedobaptism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paedogenesis', 'n.', 'Reproduction by young or larval animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paedogenetic', 'a.', 'Producing young while in the immature or larval\n   state; -- said of certain insects, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paeon', 'n.', 'A foot of four syllables, one long and three short,\n   admitting of four combinations, according to the place of the long\n   syllable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paeonine', 'n.', 'An artifical red nitrogenous dyestuff, called also red\n   coralline.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paeony', 'n.', 'See Peony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pagan', 'n.', 'One who worships false gods; an idolater; a heathen; one who\n   is neither a Christian, a Mohammedan, nor a Jew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pagan', 'n.', 'Of or pertaining to pagans; relating to the worship or the\n   worshipers of false goods; heathen; idolatrous, as, pagan tribes or\n   superstitions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pagandom', 'n.', 'The pagan lands; pagans, collectively; paganism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paganic', 'a.', 'Alt. of Paganical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paganical', 'a.', 'Of or pertaining to pagans or paganism; heathenish;\n   paganish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paganish', 'a.', 'Of or pertaining to pagans; heathenish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paganism', 'n.', 'The state of being pagan; pagan characteristics; esp.,\n   the worship of idols or false gods, or the system of religious opinions\n   and worship maintained by pagans; heathenism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paganity', 'n.', 'The state of being a pagan; paganism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paganized', 'imp. & p. p.', 'of Paganize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paganizing', 'p. pr. & vb. n.', 'of Paganize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paganize', 'v. t.', 'To render pagan or heathenish; to convert to paganism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paganize', 'v. i.', 'To behave like pagans.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paganly', 'adv.', 'In a pagan manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Page', 'n.', 'A serving boy; formerly, a youth attending a person of high\n   degree, especially at courts, as a position of honor and education; now\n   commonly, in England, a youth employed for doing errands, waiting on\n   the door, and similar service in households; in the United States, a\n   boy employed to wait upon the members of a legislative body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Page', 'n.', 'A boy child.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Page', 'n.', 'A contrivance, as a band, pin, snap, or the like, to hold the\n   skirt of a womans dress from the ground.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Page', 'n.', 'A track along which pallets carrying newly molded bricks are\n   conveyed to the hack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Page', 'n.', 'Any one of several species of beautiful South American moths\n   of the genus Urania.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Page', 'v. t.', 'To attend (one) as a page.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Page', 'n.', 'One side of a leaf of a book or manuscript.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Page', 'n.', 'Fig.: A record; a writing; as, the page of history.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Page', 'n.', 'The type set up for printing a page.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paged', 'imp. & p. p.', 'of Page');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paging', 'p. pr. & vb. n.', 'of Page');
  INSERT INTO words (word, word_type, description) VALUES  (    'Page', 'v. t.', 'To mark or number the pages of, as a book or manuscript;\n   to furnish with folios.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pageant', 'n.', 'A theatrical exhibition; a spectacle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pageant', 'n.', 'An elaborate exhibition devised for the entertainmeut of a\n   distinguished personage, or of the public; a show, spectacle, or\n   display.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pageant', 'a.', 'Of the nature of a pageant; spectacular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pageant', 'v. t.', 'To exhibit in show; to represent; to mimic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pageantry', 'n.', 'Scenic shows or spectacles, taken collectively;\n   spectacular quality; splendor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pagehood', 'n.', 'The state of being a page.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paginae', 'pl. ', 'of Pagina');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pagina', 'n.', 'The surface of a leaf or of a flattened thallus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paginal', 'a.', 'Consisting of pages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pagination', 'n.', 'The act or process of paging a book; also, the\n   characters used in numbering the pages; page number.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paging', 'n.', 'The marking or numbering of the pages of a book.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pagod', 'n.', 'A pagoda. [R.] \"Or some queer pagod.\"');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pagod', 'n.', 'An idol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pagoda', 'n.', 'A term by which Europeans designate religious temples and\n   tower-like buildings of the Hindoos and Buddhists of India, Farther\n   India, China, and Japan, -- usually but not always, devoted to idol\n   worship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pagoda', 'n.', 'An idol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pagoda', 'n.', 'A gold or silver coin, of various kinds and values,\n   formerly current in India. The Madras gold pagoda was worth about three\n   and a half rupees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pagodite', 'n.', 'Agalmatolite; -- so called because sometimes carved by\n   the Chinese into the form of pagodas. See Agalmatolite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paguma', 'n.', 'Any one of several species of East Indian viverrine mammals\n   of the genus Paguma. They resemble a weasel in form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pagurian', 'n.', 'Any one of a tribe of anomuran crustaceans, of which\n   Pagurus is a type; the hermit crab. See Hermit crab, under Hermit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pah', 'interj.', 'An exclamation expressing disgust or contempt. See Bah.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pah', 'n.', 'A kind of stockaded intrenchment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pahi', 'n.', 'A large war canoe of the Society Islands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pahlevi', 'n.', 'Same as Pehlevi.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pahoehoe', 'n.', 'A name given in the Sandwich Islands to lava having a\n   relatively smooth surface, in distinction from the rough-surfaced lava,\n   called a-a.');
  INSERT INTO words (word, word_type, description) VALUES  (    'PahUtes', 'n. pl.', 'See Utes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paid', 'imp., p. p., & a.', 'Receiving pay; compensated; hired; as, a paid\n   attorney.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paid', 'imp., p. p., & a.', 'Satisfied; contented.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paideutics', 'n.', 'The science or art of teaching.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paien', 'n. & a.', 'Pagan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paigle', 'n.', 'A species of Primula, either the cowslip or the primrose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paijama', 'n.', 'Pyjama.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pail', 'n.', 'A vessel of wood or tin, etc., usually cylindrical and having\n   a bail, -- used esp. for carrying liquids, as water or milk, etc.; a\n   bucket. It may, or may not, have a cover.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pailfuls', 'pl. ', 'of Pailful');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pailful', 'n.', 'The quantity that a pail will hold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paillasse', 'n.', 'An under bed or mattress of straw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pailmall', 'n. & a.', 'See Pall-mall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pain', 'n.', 'Punishment suffered or denounced; suffering or evil inflicted\n   as a punishment for crime, or connected with the commission of a crime;\n   penalty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pain', 'n.', 'Any uneasy sensation in animal bodies, from slight uneasiness\n   to extreme distress or torture, proceeding from a derangement of\n   functions, disease, or injury by violence; bodily distress; bodily\n   suffering; an ache; a smart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pain', 'n.', 'Specifically, the throes or travail of childbirth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pain', 'n.', 'Uneasiness of mind; mental distress; disquietude; anxiety;\n   grief; solicitude; anguish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pain', 'n.', 'See Pains, labor, effort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pained', 'imp. & p. p.', 'of Pain');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paining', 'p. pr. & vb. n.', 'of Pain');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pain', 'n.', 'To inflict suffering upon as a penalty; to punish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pain', 'n.', 'To put to bodily uneasiness or anguish; to afflict with\n   uneasy sensations of any degree of intensity; to torment; to torture;\n   as, his dinner or his wound pained him; his stomach pained him.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pain', 'n.', 'To render uneasy in mind; to disquiet; to distress; to\n   grieve; as a childs faults pain his parents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Painable', 'a.', 'Causing pain; painful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Painful', 'a.', 'Full of pain; causing uneasiness or distress, either\n   physical or mental; afflictive; disquieting; distressing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Painful', 'a.', 'Requiring labor or toil; difficult; executed with\n   laborious effort; as a painful service; a painful march.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Painful', 'a.', 'Painstaking; careful; industrious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Painim', 'n.', 'A pagan; an infidel; -- used also adjectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Painless', 'a.', 'Free from pain; without pain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pains', 'n.', 'Labor; toilsome effort; care or trouble taken; -- plural in\n   form, but used with a singular or plural verb, commonly the former.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Painstaker', 'n.', 'One who takes pains; one careful and faithful in all\n   work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Painstaking', 'a.', 'Careful in doing; diligent; faithful; attentive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Painstaking', 'n.', 'The act of taking pains; carefulness and fidelity in\n   performance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Painsworthy', 'a.', 'Worth the pains or care bestowed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Painted', 'imp. & p. p.', 'of Paint');
  INSERT INTO words (word, word_type, description) VALUES  (    'Painting', 'p. pr. & vb. n.', 'of Paint');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paint', 'v. t.', 'To cover with coloring matter; to apply paint to; as, to\n   paint a house, a signboard, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paint', 'v. t.', 'Fig.: To color, stain, or tinge; to adorn or beautify\n   with colors; to diversify with colors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paint', 'v. t.', 'To form in colors a figure or likeness of on a flat\n   surface, as upon canvas; to represent by means of colors or hues; to\n   exhibit in a tinted image; to portray with paints; as, to paint a\n   portrait or a landscape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paint', 'v. t.', 'Fig.: To represent or exhibit to the mind; to describe\n   vividly; to delineate; to image; to depict.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paint', 'v. t.', 'To practice the art of painting; as, the artist paints\n   well.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paint', 'v. t.', 'To color ones face by way of beautifying it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paint', 'n.', 'A pigment or coloring substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paint', 'n.', 'The same prepared with a vehicle, as oil, water with gum, or\n   the like, for application to a surface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paint', 'n.', 'A cosmetic; rouge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Painted', 'a.', 'Covered or adorned with paint; portrayed in colors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Painted', 'a.', 'Marked with bright colors; as, the painted turtle; painted\n   bunting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Painter', 'n.', 'A rope at the bow of a boat, used to fasten it to\n   anything.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Painter', 'n.', 'The panther, or puma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Painter', 'n.', 'One whose occupation is to paint');
  INSERT INTO words (word, word_type, description) VALUES  (    'Painter', 'n.', 'One who covers buildings, ships, ironwork, and the like,\n   with paint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Painter', 'n.', 'An artist who represents objects or scenes in color on a\n   flat surface, as canvas, plaster, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Painterly', 'a.', 'Like a painters work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paintership', 'n.', 'The state or position of being a painter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Painting', 'n.', 'The act or employment of laying on, or adorning with,\n   paints or colors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Painting', 'n.', 'The work of the painter; also, any work of art in which\n   objects are represented in color on a flat surface; a colored\n   representation of any object or scene; a picture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Painting', 'n.', 'Color laid on; paint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Painting', 'n.', 'A depicting by words; vivid representation in words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paintless', 'a.', 'Not capable of being painted or described.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Painture', 'v. t.', 'The art of painting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Painty', 'a.', 'Unskillfully painted, so that the painters method of work\n   is too obvious; also, having too much pigment applied to the surface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pair', 'n.', 'A number of things resembling one another, or belonging\n   together; a set; as, a pair or flight of stairs. \"A pair of beads.\"\n   Chaucer. Beau. & Fl. \"Four pair of stairs.\" Macaulay. [Now mostly or\n   quite disused, except as to stairs.]');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pair', 'n.', 'Two things of a kind, similar in form, suited to each other,\n   and intended to be used together; as, a pair of gloves or stockings; a\n   pair of shoes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pair', 'n.', 'Two of a sort; a span; a yoke; a couple; a brace; as, a pair\n   of horses; a pair of oxen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pair', 'n.', 'A married couple; a man and wife.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pair', 'n.', 'A single thing, composed of two pieces fitted to each other\n   and used together; as, a pair of scissors; a pair of tongs; a pair of\n   bellows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pair', 'n.', 'Two members of opposite parties or opinion, as in a\n   parliamentary body, who mutually agree not to vote on a given question,\n   or on issues of a party nature during a specified time; as, there were\n   two pairs on the final vote.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pair', 'n.', 'In a mechanism, two elements, or bodies, which are so applied\n   to each other as to mutually constrain relative motion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pairs Royal', 'pl. ', 'of Pair');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paired', 'imp. & p. p.', 'of Pair');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pairing', 'p. pr. & vb. n.', 'of Pair');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pair', 'v. i.', 'To be joined in paris; to couple; to mate, as for\n   breeding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pair', 'v. i.', 'To suit; to fit, as a counterpart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pair', 'v. i.', 'Same as To pair off. See phrase below.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pair', 'v. t.', 'To unite in couples; to form a pair of; to bring together,\n   as things which belong together, or which complement, or are adapted to\n   one another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pair', 'v. t.', 'To engage (ones self) with another of opposite opinions\n   not to vote on a particular question or class of questions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pair', 'v. t.', 'To impair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pairer', 'n.', 'One who impairs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pairing', 'v. i.', 'The act or process of uniting or arranging in pairs or\n   couples.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pairing', 'v. i.', 'See To pair off, under Pair, v. i.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pairment', 'n.', 'Impairment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pais', 'n.', 'The country; the people of the neighborhood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paisano', 'n.', 'The chaparral cock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paise', 'n.', 'See Poise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pajock', 'n.', 'A peacock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pakfong', 'n.', 'See Packfong.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pal', 'n.', 'A mate; a partner; esp., an accomplice or confederate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palace', 'n.', 'The residence of a sovereign, including the lodgings of\n   high officers of state, and rooms for business, as well as halls for\n   ceremony and reception.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palace', 'n.', 'The official residence of a bishop or other distinguished\n   personage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palace', 'n.', 'Loosely, any unusually magnificent or stately house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palacious', 'a.', 'Palatial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paladin', 'n.', 'A knight-errant; a distinguished champion; as, the\n   paladins of Charlemagne.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palaeo-', '', 'See Paleo-.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palaeographer', 'a.', 'Alt. of Palaeographic');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palaeographic', 'a.', 'See Paleographer, Paleographic, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palaeotype', 'n.', 'A system of representing all spoken sounds by means of\n   the printing types in common use.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palaestra', 'n.', 'See Palestra.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palaestric', 'a.', 'See Palestric.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palaetiologist', 'n.', 'One versed in palaetiology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palaetiology', 'n.', 'The science which explains, by the law of causation,\n   the past condition and changes of the earth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palamme', 'pl. ', 'of Palama');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palama', 'n.', 'A membrane extending between the toes of a bird, and\n   uniting them more or less closely together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palamedeae', 'n. pl.', 'An order, or suborder, including the kamichi, and\n   allied South American birds; -- called also screamers. In many\n   anatomical characters they are allied to the Anseres, but they\n   externally resemble the wading birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palampore', 'n.', 'See Palempore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palanka', 'n.', 'A camp permanently intrenched, attached to Turkish\n   frontier fortresses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palanquin', 'n.', 'An inclosed carriage or litter, commonly about eight\n   feet long, four feet wide, and four feet high, borne on the shoulders\n   of men by means of two projecting poles, -- used in India, China, etc.,\n   for the conveyance of a single person from place to place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palapteryx', 'n.', 'A large extinct ostrichlike bird of New Zealand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palatability', 'n.', 'Palatableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palatable', 'a.', 'Agreeable to the palate or taste; savory; hence,\n   acceptable; pleasing; as, palatable food; palatable advice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palatableness', 'n.', 'The quality or state of being agreeable to the\n   taste; relish; acceptableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palatably', 'adv.', 'In a palatable manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palatal', 'a.', 'Of or pertaining to the palate; palatine; as, the palatal\n   bones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palatal', 'a.', 'Uttered by the aid of the palate; -- said of certain\n   sounds, as the sound of k in kirk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palatal', 'n.', 'A sound uttered, or a letter pronounced, by the aid of the\n   palate, as the letters k and y.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palatalize', 'v. t.', 'To palatize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palate', 'n.', 'The roof of the mouth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palate', 'n.', 'Relish; taste; liking; -- a sense originating in the\n   mistaken notion that the palate is the organ of taste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palate', 'n.', 'Fig.: Mental relish; intellectual taste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palate', 'n.', 'A projection in the throat of such flowers as the\n   snapdragon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palate', 'v. t.', 'To perceive by the taste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palatial', 'a.', 'Of or pertaining to a palace; suitable for a palace;\n   resembling a palace; royal; magnificent; as, palatial structures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palatial', 'a.', 'Palatal; palatine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palatial', 'n.', 'A palatal letter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palatic', 'a.', 'Palatal; palatine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palatic', 'n.', 'A palatal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palatinate', 'n.', 'The province or seigniory of a palatine; the dignity of\n   a palatine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palatinate', 'v. t.', 'To make a palatinate of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palatine', 'a.', 'Of or pertaining to a palace, or to a high officer of a\n   palace; hence, possessing royal privileges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palatine', 'n.', 'One invested with royal privileges and rights within his\n   domains; a count palatine. See Count palatine, under 4th Count.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palatine', 'n.', 'The Palatine hill in Rome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palatine', 'a.', 'Of or pertaining to the palate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palatine', 'n.', 'A palatine bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palative', 'a.', 'Pleasing to the taste; palatable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palatize', 'v. t.', 'To modify, as the tones of the voice, by means of the\n   palate; as, to palatize a letter or sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palato-', '', 'A combining form used in anatomy to indicate relation to, or\n   connection with, the palate; as in palatolingual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palatonares', 'n. pl.', 'The posterior nares. See Nares.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palatopterygoid', 'a.', 'Pertaining to the palatine and pterygoid region of\n   the skull; as, the palatopterygoid cartilage, or rod, from which the\n   palatine and pterygoid bones are developed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palaver', 'n.', 'Talk; conversation; esp., idle or beguiling talk; talk\n   intended to deceive; flattery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palaver', 'n.', 'In Africa, a parley with the natives; a talk; hence, a\n   public conference and deliberation; a debate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palavered', 'imp. & p. p.', 'of Palaver');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palavering', 'p. pr. & vb. n.', 'of Palaver');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palaver', 'v. t. & i.', 'To make palaver with, or to; to used palaver;to\n   talk idly or deceitfully; to employ flattery; to cajole; as, to palaver\n   artfully.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palaverer', 'n.', 'One who palavers; a flatterer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pale', 'v. i.', 'Wanting in color; not ruddy; dusky white; pallid; wan; as,\n   a pale face; a pale red; a pale blue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pale', 'v. i.', 'Not bright or brilliant; of a faint luster or hue; dim;\n   as, the pale light of the moon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pale', 'n.', 'Paleness; pallor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paled', 'imp. & p. p.', 'of Pale');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paling', 'p. pr. & vb. n.', 'of Pale');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pale', 'v. i.', 'To turn pale; to lose color or luster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pale', 'v. t.', 'To make pale; to diminish the brightness of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pale', 'n.', 'A pointed stake or slat, either driven into the ground, or\n   fastened to a rail at the top and bottom, for fencing or inclosing; a\n   picket.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pale', 'n.', 'That which incloses or fences in; a boundary; a limit; a\n   fence; a palisade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pale', 'n.', 'A space or field having bounds or limits; a limited region or\n   place; an inclosure; -- often used figuratively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pale', 'n.', 'A stripe or band, as on a garment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pale', 'n.', 'One of the greater ordinaries, being a broad perpendicular\n   stripe in an escutcheon, equally distant from the two edges, and\n   occupying one third of it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pale', 'n.', 'A cheese scoop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pale', 'n.', 'A shore for bracing a timber before it is fastened.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pale', 'v. t.', 'To inclose with pales, or as with pales; to encircle; to\n   encompass; to fence off.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paleae', 'pl. ', 'of Palea');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palea', 'n.', 'The interior chaff or husk of grasses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palea', 'n.', 'One of the chaffy scales or bractlets growing on the\n   receptacle of many compound flowers, as the Coreopsis, the sunflower,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palea', 'n.', 'A pendulous process of the skin on the throat of a bird, as\n   in the turkey; a dewlap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paleaceous', 'a.', 'Chaffy; resembling or consisting of paleae, or chaff;\n   furnished with chaff; as, a paleaceous receptacle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palearctic', 'a.', 'Belonging to a region of the earths surface which\n   includes all Europe to the Azores, Iceland, and all temperate Asia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paled', 'a.', 'Striped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paled', 'a.', 'Inclosed with a paling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paleechinoidea', 'n. pl.', 'An extinct order of sea urchins found in the\n   Paleozoic rocks. They had more than twenty vertical rows of plates.\n   Called also Palaeechini.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paleface', 'n.', 'A white person; -- an appellation supposed to have been\n   applied to the whites by the American Indians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paleichthyes', 'n. pl.', 'A comprehensive division of fishes which includes\n   the elasmobranchs and ganoids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palely', 'a.', 'In a pale manner; dimly; wanly; not freshly or ruddily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palempore', 'n.', 'A superior kind of dimity made in India, -- used for bed\n   coverings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paleness', 'n.', 'The quality or condition of being pale; want of freshness\n   or ruddiness; a sickly whiteness; lack of color or luster; wanness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palenque', 'n. pl.', 'A collective name for the Indians of Nicaragua and\n   Honduras.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paleo-', '', 'A combining form meaning old, ancient; as, palearctic,\n   paleontology, paleothere, paleography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paleobotanist', 'n.', 'One versed in paleobotany.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paleobotany', 'n.', 'That branch of paleontology which treats of fossil\n   plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paleocarida', 'n. pl.', 'Same as Merostomata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paleocrinoidea', 'n. pl.', 'A suborder of Crinoidea found chiefly in the\n   Paleozoic rocks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paleocrystic', 'a.', 'Of, pertaining to, or derived from, a former glacial\n   formation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paleogaean', 'a.', 'Of or pertaining to the Eastern hemisphere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paleograph', 'n.', 'An ancient manuscript.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paleographer', 'n.', 'One skilled in paleography; a paleographist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paleographic', 'a.', 'Alt. of Paleographical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paleographical', 'a.', 'Of or pertaining to paleography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paleographist', 'n.', 'One versed in paleography; a paleographer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paleography', 'n.', 'An ancient manner of writing; ancient writings,\n   collectively; as, Punic paleography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paleography', 'n.', 'The study of ancient inscriptions and modes of\n   writing; the art or science of deciphering ancient writings, and\n   determining their origin, period, etc., from external characters;\n   diplomatics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paleolae', 'pl. ', 'of Paleola');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paleola', 'n.', 'A diminutive or secondary palea; a lodicule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paleolith', 'n.', 'A relic of the Paleolithic era.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paleolithic', 'a.', 'Of or pertaining to an era marked by early stone\n   implements. The Paleolithic era (as proposed by Lubbock) includes the\n   earlier half of the \"Stone Age;\" the remains belonging to it are for\n   the most part of extinct animals, with relics of human beings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paleologist', 'n.', 'One versed in paleology; a student of antiquity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paleology', 'n.', 'The study or knowledge of antiquities, esp. of\n   prehistoric antiquities; a discourse or treatise on antiquities;\n   archaeology .');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paleontographical', 'a.', 'Of or pertaining to the description of fossil\n   remains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paleontography', 'n.', 'The description of fossil remains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paleontological', 'a.', 'Of or pertaining to paleontology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paleontologist', 'n.', 'One versed in paleontology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paleontology', 'n.', 'The science which treats of the ancient life of the\n   earth, or of fossils which are the remains of such life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paleophytologist', 'n.', 'A paleobotanist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paleophytology', 'n.', 'Paleobotany.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paleornithology', 'n.', 'The branch of paleontology which treats of fossil\n   birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paleosaurus', 'n.', 'A genus of fossil saurians found in the Permian\n   formation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paleotechnic', 'a.', 'Belonging to, or connected with, ancient art.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paleothere', 'n.', 'Any species of Paleotherium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paleotherian', 'a.', 'Of or pertaining to Paleotherium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paleotherium', 'n.', 'An extinct genus of herbivorous Tertiary mammals,\n   once supposed to have resembled the tapir in form, but now known to\n   have had a more slender form, with a long neck like that of a llama.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paleotheroid', '', 'Resembling Paleotherium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paleotheroid', 'n.', 'An animal resembling, or allied to, the paleothere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paleotype', 'n.', 'See Palaeotype.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paleous', 'a.', 'Chaffy; like chaff; paleaceous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paleozoic', 'a.', 'Of or pertaining to, or designating, the older division\n   of geological time during which life is known to have existed,\n   including the Silurian, Devonian, and Carboniferous ages, and also to\n   the life or rocks of those ages. See Chart of Geology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paleozoology', 'n.', 'The Paleozoic time or strata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paleozooogy', 'n.', 'The science of extinct animals, a branch of\n   paleontology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palesie', 'n.', 'Alt. of Palesy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palesy', 'n.', 'Palsy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palestinian', 'a.', 'Alt. of Palestinean');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palestinean', 'a.', 'Of or pertaining to Palestine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palestrae', 'pl. ', 'of Palestra');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palestras', 'pl. ', 'of Palestra');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palestra', 'n.', 'A wrestling school; hence, a gymnasium, or place for\n   athletic exercise in general.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palestra', 'n.', 'A wrestling; the exercise of wrestling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palestrian', 'a.', 'Alt. of Palestrical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palestric', 'a.', 'Alt. of Palestrical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palestrical', 'a.', 'Of or pertaining to the palestra, or to wrestling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palet', 'n.', 'Same as Palea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paletot', 'n.', 'An overcoat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paletot', 'n.', 'A ladys outer garment, -- of varying fashion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palette', 'n.', 'A thin, oval or square board, or tablet, with a thumb hole\n   at one end for holding it, on which a painter lays and mixes his\n   pigments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palette', 'n.', 'One of the plates covering the points of junction at the\n   bend of the shoulders and elbows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palette', 'n.', 'A breastplate for a breast drill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palewise', 'adv.', 'In the manner of a pale or pales; by perpendicular\n   lines or divisions; as, to divide an escutcheon palewise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palfrey', 'n.', 'A saddle horse for the road, or for state occasions, as\n   distinguished from a war horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palfrey', 'n.', 'A small saddle horse for ladies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palfreyed', 'a.', 'Mounted on a palfrey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palgrave', 'n.', 'See Palsgrave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pali', 'n.', 'pl. of Palus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pali', 'n.', 'A dialect descended from Sanskrit, and like that, a dead\n   language, except when used as the sacred language of the Buddhist\n   religion in Farther India, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palification', 'n.', 'The act or practice of driving piles or posts into\n   the ground to make it firm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paliform', 'a.', 'Resembling a palus; as, the paliform lobes of the septa\n   in corals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palilogy', 'n.', 'The repetition of a word, or part of a sentence, for the\n   sake of greater emphasis; as, \"The living, the living, he shall praise\n   thee.\"');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palimpsest', 'n.', 'A parchment which has been written upon twice, the\n   first writing having been erased to make place for the second.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palindrome', 'n.', 'A word, verse, or sentence, that is the same when read\n   backward or forward; as, madam; Hannah; or Lewd did I live, & evil I\n   did dwel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palindromic', 'a.', 'Alt. of Palindromical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palindromical', 'a.', 'Of, pertaining to, or like, a palindrome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palindromist', 'n.', 'A writer of palindromes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paling', 'n.', 'Pales, in general; a fence formed with pales or pickets; a\n   limit; an inclosure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paling', 'n.', 'The act of placing pales or stripes on cloth; also, the\n   stripes themselves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palingenesia', 'n.', 'See Palingenesis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palingenesis', 'n.', 'Alt. of Palingenesy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palingenesy', 'n.', 'A new birth; a re-creation; a regeneration; a\n   continued existence in different manner or form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palingenesy', 'n.', 'That form of evolution in which the truly ancestral\n   characters conserved by heredity are reproduced in development;\n   original simple descent; -- distinguished from kenogenesis. Sometimes,\n   in zoology, the abrupt metamorphosis of insects, crustaceans, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palingenetic', 'a.', 'Of or pertaining to palingenesis: as, a palingenetic\n   process.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palinode', 'n.', 'An ode recanting, or retracting, a former one; also, a\n   repetition of an ode.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palinode', 'n.', 'A retraction; esp., a formal retraction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palinodial', 'a.', 'Of or pertaining to a palinode, or retraction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palinody', 'n.', 'See Palinode.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palinurus', 'n.', 'An instrument for obtaining directly, without\n   calculation, the true bearing of the sun, and thence the variation of\n   the compass');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palisade', 'n.', 'A strong, long stake, one end of which is set firmly in\n   the ground, and the other is sharpened; also, a fence formed of such\n   stakes set in the ground as a means of defense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palisade', 'n.', 'Any fence made of pales or sharp stakes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palisaded', 'imp. & p. p.', 'of Palisade');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palisading', 'p. pr. & vb. n.', 'of Palisade');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palisade', 'v. t.', 'To surround, inclose, or fortify, with palisades.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palisading', 'n.', 'A row of palisades set in the ground.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palisadoes', 'pl. ', 'of Palisado');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palisado', 'n.', 'A palisade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palisado', 'v. t.', 'To palisade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palish', 'a.', 'Somewhat pale or wan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palissander', 'n.', 'Violet wood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palissander', 'n.', 'Rosewood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palissy', 'a.', 'Designating, or of the nature of, a kind of pottery made\n   by Bernard Palissy, in France, in the 16th centry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palkee', 'n.', 'A palanquin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pall', 'n.', 'Same as Pawl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pall', 'n.', 'An outer garment; a cloak mantle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pall', 'n.', 'A kind of rich stuff used for garments in the Middle Ages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pall', 'n.', 'Same as Pallium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pall', 'n.', 'A figure resembling the Roman Catholic pallium, or pall, and\n   having the form of the letter Y.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pall', 'n.', 'A large cloth, esp., a heavy black cloth, thrown over a\n   coffin at a funeral; sometimes, also, over a tomb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pall', 'n.', 'A piece of cardboard, covered with linen and embroidered on\n   one side; -- used to put over the chalice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pall', 'v. t.', 'To cloak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palled', 'imp. & p. p.', 'of Pall');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palling', 'p. pr. & vb. n.', 'of Pall');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pall', 'a.', 'To become vapid, tasteless, dull, or insipid; to lose\n   strength, life, spirit, or taste; as, the liquor palls.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pall', 'v. t.', 'To make vapid or insipid; to make lifeless or spiritless;\n   to dull; to weaken.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pall', 'v. t.', 'To satiate; to cloy; as, to pall the appetite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pall', 'n.', 'Nausea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palla', 'n.', 'An oblong rectangular piece of cloth, worn by Roman ladies,\n   and fastened with brooches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palladian', 'a.', 'Of, pertaining to, or designating, a variety of the\n   revived classic style of architecture, founded on the works of Andrea\n   Palladio, an Italian architect of the 16th century.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palladic', 'a.', 'Of, pertaining to, or derived from, palladium; -- used\n   specifically to designate those compounds in which the element has a\n   higher valence as contrasted with palladious compounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palladious', 'a.', 'Of, pertaining to, or containing, palladium; -- used\n   specifically to designate those compounds in which palladium has a\n   lower valence as compared with palladic compounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palladium', 'n.', 'Any statue of the goddess Pallas; esp., the famous\n   statue on the preservation of which depended the safety of ancient\n   Troy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palladium', 'n.', 'Hence: That which affords effectual protection or\n   security; a sateguard; as, the trial by jury is the palladium of our\n   civil rights.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palladium', 'n.', 'A rare metallic element of the light platinum group,\n   found native, and also alloyed with platinum and gold. It is a\n   silver-white metal resembling platinum, and like it permanent and\n   untarnished in the air, but is more easily fusible. It is unique in its\n   power of occluding hydrogen, which it does to the extent of nearly a\n   thousand volumes, forming the alloy Pd2H. It is used for graduated\n   circles and verniers, for plating certain silver goods, and somewhat in\n   dentistry. It was so named in 1804 by Wollaston from the asteroid\n   Pallas, which was discovered in 1802. Symbol Pd. Atomic weight, 106.2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palladiumized', 'imp. & p. p.', 'of Paladiumize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palladiumizing', 'p. pr. & vb. n.', 'of Paladiumize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paladiumize', 'v. t.', 'To cover or coat with palladium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pallah', 'n.', 'A large South African antelope (Aepyceros melampus). The\n   male has long lyrate and annulated horns. The general color is bay,\n   with a black crescent on the croup. Called also roodebok.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pallas', 'n.', 'Pallas Athene, the Grecian goddess of wisdom, called also\n   Athene, and identified, at a later period, with the Roman Minerva.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pallbearer', 'n.', 'One of those who attend the coffin at a funeral; -- so\n   called from the pall being formerly carried by them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pallet', 'n.', 'A small and mean bed; a bed of straw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palet', 'n.', 'A perpendicular band upon an escutcheon, one half the\n   breadth of the pale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pallet', 'n.', 'Same as Palette.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pallet', 'n.', 'A wooden implement used by potters, crucible makers, etc.,\n   for forming, beating, and rounding their works. It is oval, round, and\n   of other forms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pallet', 'n.', 'A potters wheel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pallet', 'n.', 'An instrument used to take up gold leaf from the pillow,\n   and to apply it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pallet', 'n.', 'A tool for gilding the backs of books over the bands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pallet', 'n.', 'A board on which a newly molded brick is conveyed to the\n   hack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pallet', 'n.', 'A click or pawl for driving a ratchet wheel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pallet', 'n.', 'One of the series of disks or pistons in the chain pump.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pallet', 'n.', 'One of the pieces or levers connected with the pendulum of\n   a clock, or the balance of a watch, which receive the immediate impulse\n   of the scape-wheel, or balance wheel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pallet', 'n.', 'In the organ, a valve between the wind chest and the mouth\n   of a pipe or row of pipes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pallet', 'n.', 'One of a pair of shelly plates that protect the siphon\n   tubes of certain bivalves, as the Teredo. See Illust. of Teredo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pallet', 'n.', 'A cup containing three ounces, -- /ormerly used by\n   surgeons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pallial', 'a.', 'Of or pretaining to a mantle, especially to the mantle of\n   mollusks; produced by the mantle; as, the pallial line, or impression,\n   which marks the attachment of the mantle on the inner surface of a\n   bivalve shell. See Illust. of Bivalve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palliament', 'n.', 'A dress; a robe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palliard', 'n.', 'A born beggar; a vagabond.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palliard', 'n.', 'A lecher; a lewd person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palliasse', 'n.', 'See Paillasse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palliate', 'a.', 'Covered with a mant/e; cloaked; disguised.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palliate', 'a.', 'Eased; mitigated; alleviated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palliated', 'imp. & p. p.', 'of Palliate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palliating', 'p. pr. & vb. n.', 'of Palliate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palliate', 'v. t.', 'To cover with a mantle or cloak; to cover up; to hide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palliate', 'v. t.', 'To cover with excuses; to conceal the enormity of, by\n   excuses and apologies; to extenuate; as, to palliate faults.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palliate', 'v. t.', 'To reduce in violence; to lessen or abate; to\n   mitigate; to ease withhout curing; as, to palliate a disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palliation', 'n.', 'The act of palliating, or state of being palliated;\n   extenuation; excuse; as, the palliation of faults, offenses, vices.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palliation', 'n.', 'Mitigation; alleviation, as of a disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palliation', 'n.', 'That which cloaks or covers; disguise; also, the state\n   of being covered or disguised.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palliative', 'a.', 'Serving to palliate; serving to extenuate or mitigate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palliative', 'n.', 'That which palliates; a palliative agent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palliatory', 'a.', 'Palliative; extenuating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pallid', 'a.', 'Deficient in color; pale; wan; as, a pallid countenance;\n   pallid blue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pallidity', 'n.', 'Pallidness; paleness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pallidly', 'adv.', 'In a pallid manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pallidness', 'n.', 'The quality or state of being pallid; paleness; pallor;\n   wanness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palliobranchiata', 'n. pl.', 'Same as Brachiopoda.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palliobranchiate', 'a.', 'Having the pallium, or mantle, acting as a gill,\n   as in brachiopods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pallia', 'pl. ', 'of Pallium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palliums', 'pl. ', 'of Pallium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pallium', 'n.', 'A large, square, woolen cloak which enveloped the whole\n   person, worn by the Greeks and by certain Romans. It is the Roman name\n   of a Greek garment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pallium', 'n.', 'A band of white wool, worn on the shoulders, with four\n   purple crosses worked on it; a pall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pallium', 'n.', 'The mantle of a bivalve. See Mantle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pallium', 'n.', 'The mantle of a bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pall-mall', 'n.', 'A game formerly common in England, in which a wooden\n   ball was driven with a mallet through an elevated hoop or ring of iron.\n   The name was also given to the mallet used, to the place where the game\n   was played, and to the street, in London, still called Pall Mall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pallone', 'n.', 'An Italian game, played with a large leather ball.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pallor', 'a.', 'Paleness; want of color; pallidity; as, pallor of the\n   complexion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palm', 'n.', 'The inner and somewhat concave part of the hand between the\n   bases of the fingers and the wrist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palm', 'n.', 'A lineal measure equal either to the breadth of the hand or\n   to its length from the wrist to the ends of the fingers; a hand; --\n   used in measuring a horses height.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palm', 'n.', 'A metallic disk, attached to a strap, and worn the palm of\n   the hand, -- used to push the needle through the canvas, in sewing\n   sails, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palm', 'n.', 'The broad flattened part of an antler, as of a full-grown\n   fallow deer; -- so called as resembling the palm of the hand with its\n   protruding fingers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palm', 'n.', 'The flat inner face of an anchor fluke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palm', 'n.', 'Any endogenous tree of the order Palmae or Palmaceae; a palm\n   tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palm', 'n.', 'A branch or leaf of the palm, anciently borne or worn as a\n   symbol of victory or rejoicing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palm', 'n.', 'Any symbol or token of superiority, success, or triumph;\n   also, victory; triumph; supremacy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palmed', 'imp. & p. p.', 'of Palm');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palming', 'p. pr. & vb. n.', 'of Palm');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palm', 'v. t.', 'To handle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palm', 'v. t.', 'To manipulate with, or conceal in, the palm of the hand;\n   to juggle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palm', 'v. t.', 'To impose by fraud, as by sleight of hand; to put by\n   unfair means; -- usually with off.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palmaceous', 'a.', 'Of or pertaining to palms; of the nature of, or\n   resembling, palms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palma Christi', '', 'A plant (Ricinus communis) with ornamental peltate and\n   palmately cleft foliage, growing as a woody perennial in the tropics,\n   and cultivated as an herbaceous annual in temperate regions; -- called\n   also castor-oil plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palmacite', 'n.', 'A fossil palm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palmar', 'a.', 'Pertaining to, or corresponding with, the palm of the hand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palmar', 'a.', 'Of or pertaining to the under side of the wings of birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palmaria', 'pl. ', 'of Palmarium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palmarium', 'n.', 'One of the bifurcations of the brachial plates of a\n   crinoid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palmary', 'a.', 'Palmar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palmary', 'a.', 'Worthy of the palm; palmy; preeminent; superior;\n   principal; chief; as, palmary work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palmate', 'n.', 'A salt of palmic acid; a ricinoleate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palmate', 'a.', 'Alt. of Palmated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palmated', 'a.', 'Having the shape of the hand; resembling a hand with the\n   fingers spread.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palmated', 'a.', 'Spreading from the apex of a petiole, as the divisions of\n   a leaf, or leaflets, so as to resemble the hand with outspread fingers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palmated', 'a.', 'Having the anterior toes united by a web, as in most\n   swimming birds; webbed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palmated', 'a.', 'Having the distal portion broad, flat, and more or less\n   divided into lobes; -- said of certain corals, antlers, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palmately', 'adv.', 'In a palmate manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palmatifid', 'a.', 'Palmate, with the divisions separated but little more\n   than halfway to the common center.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palmatilobed', 'a.', 'Palmate, with the divisions separated less than\n   halfway to the common center.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palmatisect', 'a.', 'Alt. of Palmatisected');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palmatisected', 'a.', 'Divided, as a palmate leaf, down to the midrib, so\n   that the parenchyma is interrupted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palmcrist', 'n.', 'The palma Christi. (Jonah iv. 6, margin, and Douay\n   version, note.)');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palmed', 'a.', 'Having or bearing a palm or palms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palmer', 'v. t.', 'One who palms or cheats, as at cards or dice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palmer', 'n.', 'A wandering religious votary; especially, one who bore a\n   branch of palm as a token that he had visited the Holy Land and its\n   sacred places.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palmer', 'n.', 'A palmerworm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palmer', 'n.', 'Short for Palmer fly, an artificial fly made to imitate a\n   hairy caterpillar; a hackle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palmerworm', 'n.', 'Any hairy caterpillar which appears in great numbers,\n   devouring herbage, and wandering about like a palmer. The name is\n   applied also to other voracious insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palmerworm', 'n.', 'In America, the larva of any one of several moths,\n   which destroys the foliage of fruit and forest trees, esp. the larva of\n   Ypsolophus pometellus, which sometimes appears in vast numbers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palmette', 'n.', 'A floral ornament, common in Greek and other ancient\n   architecture; -- often called the honeysuckle ornament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palmetto', 'n.', 'A name given to palms of several genera and species\n   growing in the West Indies and the Southern United States. In the\n   United States, the name is applied especially to the Chamaerops, /\n   Sabal, Palmetto, the cabbage tree of Florida and the Carolinas. See\n   Cabbage tree, under Cabbage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palmic', 'a.', 'Of, pertaining to, or derived from, the castor-oil plant\n   (Ricinus communis, or Palma Christi); -- formerly used to designate an\n   acid now called ricinoleic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palmidactyles', 'n. pl.', 'A group of wading birds having the toes webbed,\n   as the avocet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palmiferous', 'a.', 'Bearing palms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palmigrade', 'a.', 'Putting the whole foot upon the ground in walking, as\n   some mammals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palmin', 'n.', 'A white waxy or fatty substance obtained from castor oil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palmin', 'n.', 'Ricinolein.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palmiped', 'a.', 'Web-footed, as a water fowl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palmiped', 'n.', 'A swimming bird; a bird having webbed feet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palmipedes', 'n. pl.', 'Same as Natatores.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palmister', 'n.', 'One who practices palmistry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palmistry', 'n.', 'The art or practice of divining or telling fortunes, or\n   of judging of character, by the lines and marks in the palm of the\n   hand; chiromancy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palmistry', 'n.', 'A dexterous use or trick of the hand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palmitate', 'n.', 'A salt of palmitic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palmite', 'n.', 'A South African plant (Prionium Palmita) of the Rush\n   family, having long serrated leaves. The stems have been used for\n   making brushes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palmitic', 'a.', 'Pertaining to, or obtained from, palmitin or palm oil;\n   as, palmitic acid, a white crystalline body belonging to the fatty acid\n   series. It is readily soluble in hot alcohol, and melts to a liquid oil\n   at 62 C.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palmitin', 'n.', 'A solid crystallizable fat, found abundantly in animals\n   and in vegetables. It occurs mixed with stearin and olein in the fat of\n   animal tissues, with olein and butyrin in butter, with olein in olive\n   oil, etc. Chemically, it is a glyceride of palmitic acid, three\n   molecules of palmitic acid being united to one molecule of glyceryl,\n   and hence it is technically called tripalmitin, or glyceryl\n   tripalmitate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palmitolic', 'a.', 'Pertaining to, or designating, an artificial acid of\n   the oleic acid series, isomeric with linoleic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palmitone', 'n.', 'The ketone of palmitic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palm Sunday', '', 'The Sunday next before Easter; -- so called in\n   commemoration of our Saviors triumphal entry into Jerusalem, when the\n   multitude strewed palm branches in the way.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palmy', 'a.', 'Bearing palms; abounding in palms; derived from palms; as, a\n   palmy shore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palmy', 'a.', 'Worthy of the palm; flourishing; prosperous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palmyra', 'n.', 'A species of palm (Borassus flabelliformis) having a\n   straight, black, upright trunk, with palmate leaves. It is found native\n   along the entire northern shores of the Indian Ocean, from the mouth of\n   the Tigris to New Guinea. More than eight hundred uses to which it is\n   put are enumerated by native writers. Its wood is largely used for\n   building purposes; its fruit and roots serve for food, its sap for\n   making toddy, and its leaves for thatching huts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palola', 'n.', 'An annelid (Palola viridis) which, at certain seasons of\n   the year, swarms at the surface of the sea about some of the Pacific\n   Islands, where it is collected for food.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pallometa', 'n.', 'A pompano.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palp', 'n.', 'Same as Palpus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palp', 'v. t.', 'To have a distinct touch or feeling of; to feel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palpability', 'n.', 'The quality of being palpable, or perceptible by the\n   touch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palpable', 'a.', 'Capable of being touched and felt; perceptible by the\n   touch; as, a palpable form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palpable', 'a.', 'Easily perceptible; plain; distinct; obvious; readily\n   perceived and detected; gross; as, palpable imposture; palpable\n   absurdity; palpable errors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palpation', 'n.', 'Act of touching or feeling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palpation', 'n.', 'Examination of a patient by touch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palpator', 'n.', 'One of a family of clavicorn beetles, including those\n   which have very long maxillary palpi.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palpebrae', 'pl. ', 'of Palpebra');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palpebra', 'n.', 'The eyelid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palpebral', 'a.', 'Of or pertaining to the eyelids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palprbrate', 'a.', 'Having eyelids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palped', 'a.', 'Having a palpus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palpi', 'n.', 'pl. of Palpus. (Zool.) See Palpus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palpicorn', 'n.', 'One of a group of aquatic beetles (Palpicornia) having\n   short club-shaped antennae, and long maxillary palpi.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palpifer', 'n.', 'Same as Palpiger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palpiform', 'a.', 'Having the form of a palpus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palpiger', 'n.', 'That portion of the labium which bears the palpi in\n   insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palpigerous', 'a.', 'Bearing a palpus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palpitant', 'a.', 'Palpitating; throbbing; trembling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palpitated', 'imp. & p. p.', 'of Palpitate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palpitating', 'p. pr. & vb. n.', 'of Palpitate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palpitate', 'v. i.', 'To beat rapidly and more strongly than usual; to\n   throb; to bound with emotion or exertion; to pulsate violently; to\n   flutter; -- said specifically of the heart when its action is abnormal,\n   as from excitement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palpitation', 'n.', 'A rapid pulsation; a throbbing; esp., an abnormal,\n   rapid beating of the heart as when excited by violent exertion, strong\n   emotion, or by disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palpless', 'a.', 'Without a palpus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palpocil', 'n.', 'A minute soft filamentary process springing from the\n   surface of certain hydroids and sponges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palpi', 'pl. ', 'of Palpus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palpus', 'n.', 'A feeler; especially, one of the jointed sense organs\n   attached to the mouth organs of insects, arachnids, crustaceans, and\n   annelids; as, the mandibular palpi, maxillary palpi, and labial palpi.\n   The palpi of male spiders serve as sexual organs. Called also palp. See\n   Illust. of Arthrogastra and Orthoptera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palsgrave', 'n.', 'A count or earl who presided in the domestic court, and\n   had the superintendence, of a royal household in Germany.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palsgravine', 'n.', 'The consort or widow of a palsgrave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palsical', 'a.', 'Affected with palsy; palsied; paralytic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palsied', 'a.', 'Affected with palsy; paralyzed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palstave', 'n.', 'A peculiar bronze adz, used in prehistoric Europe about\n   the middle of the bronze age.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palster', 'n.', 'A pilgrims staff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palsies', 'pl. ', 'of Palsy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palsy', 'n.', 'Paralysis, complete or partial. See Paralysis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palsied', 'imp. & p. p.', 'of Palsy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palsying', 'p. pr. & vb. n.', 'of Palsy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palsy', 'v. t.', 'To affect with palsy, or as with palsy; to deprive of\n   action or energy; to paralyze.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palsywort', 'n.', 'The cowslip (Primula veris); -- so called from its\n   supposed remedial powers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paltered', 'imp. & p. p.', 'of Palter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paltering', 'p. pr. & vb. n.', 'of Palter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palter', 'v. i.', 'To haggle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palter', 'v. i.', 'To act in insincere or deceitful manner; to play false;\n   to equivocate; to shift; to dodge; to trifle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palter', 'v. i.', 'To babble; to chatter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palter', 'v. t.', 'To trifle with; to waste; to squander in paltry ways or\n   on worthless things.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palterer', 'n.', 'One who palters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palterly', 'a. & adv.', 'Paltry; shabby; shabbily; paltrily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paltock', 'n.', 'A kind of doublet; a jacket.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paltrily', 'adv.', 'In a paltry manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paltriness', 'n.', 'The state or quality of being paltry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paltry', 'superl.', 'Mean; vile; worthless; despicable; contemptible;\n   pitiful; trifling; as, a paltry excuse; paltry gold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paludal', 'a.', 'Of or pertaining to marshes or fens; marshy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paludament', 'n.', 'See Paludamentum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paladumenta', 'pl. ', 'of Paludamentum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paludamentum', 'n.', 'A military cloak worn by a general and his principal\n   officers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paludicolae', 'n. pl.', 'A division of birds, including the cranes, rails,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paludicole', 'a.', 'Marsh-inhabiting; belonging to the Paludicolae');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paludinae', 'pl. ', 'of Paludina');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paludinas', 'pl. ', 'of Paludina');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paludina', 'n.', 'Any one of numerous species of freshwater\n   pectinibranchiate mollusks, belonging to Paludina, Melantho, and allied\n   genera. They have an operculated shell which is usually green, often\n   with brown bands. See Illust. of Pond snail, under Pond.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paludinal', 'a.', 'Inhabiting ponds or swamps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paludine', 'a.', 'Of or pertaining to a marsh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paludinous', 'a.', 'Paludinal. (b) Like or pertaining to the genus\n   Paludina.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paludinous', 'a.', 'Of or pertaining to a marsh or fen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paludism', 'n.', 'The morbid phenomena produced by dwelling among marshes;\n   malarial disease or disposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paludose', 'a.', 'Growing or living in marshy places; marshy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palule', 'n.', 'See Palulus or Palus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paluli', 'pl. ', 'of Palulus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palulus', 'n.', 'Same as Palus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pali', 'pl. ', 'of Palus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palus', 'n.', 'One of several upright slender calcareous processes which\n   surround the central part of the calicle of certain corals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palustral', 'a.', 'Of or pertaining to a bog or marsh; boggy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Palustrine', 'a.', 'Of, pertaining to, or living in, a marsh or swamp;\n   marshy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paly', 'a.', 'Pale; wanting color; dim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paly', 'a.', 'Divided into four or more equal parts by perpendicular lines,\n   and of two different tinctures disposed alternately.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pam', 'n.', 'The knave of clubs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pament', 'n.', 'A pavement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pampano', 'n.', 'Same as Pompano.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pampas', 'n. pl.', 'Vast plains in the central and southern part of the\n   Argentine Republic in South America. The term is sometimes used in a\n   wider sense for the plains extending from Bolivia to Southern\n   Patagonia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pampered', 'imp. & p. p.', 'of Pamper');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pampering', 'p. pr. & vb. n.', 'of Pamper');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pamper', 'v. t.', 'To feed to the full; to feed luxuriously; to glut; as,\n   to pamper the body or the appetite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pamper', 'v. t.', 'To gratify inordinately; to indulge to excess; as, to\n   pamper pride; to pamper the imagination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pampered', 'a.', 'Fed luxuriously; indulged to the full; hence, luxuriant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pamperer', 'n.', 'One who, or that which, pampers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pamperize', 'v. t.', 'To pamper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pampero', 'n.', 'A violent wind from the west or southwest, which sweeps\n   over the pampas of South America and the adjacent seas, often doing\n   great damage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pamperos', 'n. pl.', 'A tribe of Indians inhabiting the pampas of South\n   America.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pamphlet', 'n.', 'A writing; a book.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pamphlet', 'n.', 'A small book consisting of a few sheets of printed paper,\n   stitched together, often with a paper cover, but not bound; a short\n   essay or written discussion, usually on a subject of current interest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pamphlet', 'v. i.', 'To write a pamphlet or pamphlets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pamphleteer', 'n.', 'A writer of pamphlets; a scribbler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pamphleteer', 'v. i.', 'To write or publish pamphlets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pampiniform', 'a.', 'In the form of tendrils; -- applied especially to the\n   spermatic and ovarian veins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pampre', 'n.', 'An ornament, composed of vine leaves and bunches of grapes,\n   used for decorating spiral columns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pamprodactylous', 'a.', 'Having all the toes turned forward, as the colies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pan-', '', 'Alt. of Panto-');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panta-', '', 'Alt. of Panto-');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panto-', '', 'Combining forms signifying all, every; as, panorama,\n   pantheism, pantagraph, pantograph. Pan- becomes pam- before b or p, as\n   pamprodactylous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pan', 'n.', 'A part; a portion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pan', 'n.', 'The distance comprised between the angle of the epaule and the\n   flanked angle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pan', 'n.', 'A leaf of gold or silver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pan', 'v. t. & i.', 'To join or fit together; to unite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pan', 'n.', 'The betel leaf; also, the masticatory made of the betel leaf,\n   etc. See /etel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pan', 'n.', 'The god of shepherds, guardian of bees, and patron of fishing\n   and hunting. He is usually represented as having the head and trunk of\n   a man, with the legs, horns, and tail of a goat, and as playing on the\n   shepherds pipe, which he is said to have invented.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pan', 'n.', 'A shallow, open dish or vessel, usually of metal, employed for\n   many domestic uses, as for setting milk for cream, for frying or baking\n   food, etc.; also employed for various uses in manufacturing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pan', 'n.', 'A closed vessel for boiling or evaporating. See Vacuum pan,\n   under Vacuum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pan', 'n.', 'The part of a flintlock which holds the priming.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pan', 'n.', 'The skull, considered as a vessel containing the brain; the\n   upper part of the head; the brainpan; the cranium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pan', 'n.', 'A recess, or bed, for the leaf of a hinge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pan', 'n.', 'The hard stratum of earth that lies below the soil. See Hard\n   pan, under Hard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pan', 'n.', 'A natural basin, containing salt or fresh water, or mud.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panned', 'imp. & p. p.', 'of Pan');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panning', 'p. pr. & vb. n.', 'of Pan');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pan', 'v. t.', 'To separate, as gold, from dirt or sand, by washing in a\n   kind of pan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pan', 'v. i.', 'To yield gold in, or as in, the process of panning; --\n   usually with out; as, the gravel panned out richly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pan', 'v. i.', 'To turn out (profitably or unprofitably); to result; to\n   develop; as, the investigation, or the speculation, panned out poorly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panabase', 'n.', 'Same as Tetrahedrite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panacea', 'n.', 'A remedy for all diseases; a universal medicine; a\n   cure-all; catholicon; hence, a relief or solace for affliction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panacea', 'n.', 'The herb allheal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panacean', 'a.', 'Having the properties of a panacea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panache', 'n.', 'A plume or bunch of feathers, esp. such a bunch worn on\n   the helmet; any military plume, or ornamental group of feathers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panada', 'n.', 'Alt. of Panade');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panade', 'n.', 'Bread boiled in water to the consistence of pulp, and\n   sweetened or flavored.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panade', 'n.', 'A dagger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panama hat', '', 'A fine plaited hat, made in Central America of the young\n   leaves of a plant (Carludovica palmata).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pan-American', 'a.', 'Of or pertaining to both North and South America.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pan-Anglican', 'a.', 'Belonging to, or representing, the whole Church of\n   England; used less strictly, to include the Protestant Episcopal Church\n   of the United States; as, the Pan-Anglican Conference at Lambeth, in\n   1888.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panary', 'a.', 'Of or pertaining to bread or to breadmaking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panary', 'n.', 'A storehouse for bread.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pancake', 'n.', 'A thin cake of batter fried in a pan or on a griddle; a\n   griddlecake; a flapjack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pancarte', 'n.', 'A royal charter confirming to a subject all his\n   possessions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pance', 'n.', 'The pansy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panch', 'n.', 'See Paunch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panchway', 'n.', 'A Bengalese four-oared boat for passengers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pancratian', 'a.', 'Pancratic; athletic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pancratiast', 'n.', 'One who engaged in the contests of the pancratium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pancratiastic', 'a.', 'Of or pertaining to the pancratium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pancratic', 'a.', 'Having all or many degrees of power; having a great\n   range of power; -- said of an eyepiece made adjustable so as to give a\n   varying magnifying power.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pancratic', 'a.', 'Alt. of Pancratical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pancratical', 'a.', 'Of or pertaining to the pancratium; athletic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pancratist', 'n.', 'An athlete; a gymnast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pancratium', 'n.', 'An athletic contest involving both boxing and\n   wrestling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pancratium', 'n.', 'A genus of Old World amaryllideous bulbous plants,\n   having a funnel-shaped perianth with six narrow spreading lobes. The\n   American species are now placed in the related genus Hymenocallis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pancreas', 'n.', 'The sweetbread, a gland connected with the intestine of\n   nearly all vertebrates. It is usually elongated and light-colored, and\n   its secretion, called the pancreatic juice, is discharged, often\n   together with the bile, into the upper part of the intestines, and is a\n   powerful aid in digestion. See Illust. of Digestive apparatus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pancreatic', 'a.', 'Of or pertaining to the pancreas; as, the pancreatic\n   secretion, digestion, ferments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pancreatin', 'n.', 'One of the digestive ferments of the pancreatic juice;\n   also, a preparation containing such a ferment, made from the pancreas\n   of animals, and used in medicine as an aid to digestion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pancy', 'n.', 'See Pansy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panda', 'n.', 'A small Asiatic mammal (Ailurus fulgens) having fine soft\n   fur. It is related to the bears, and inhabits the mountains of Northern\n   India.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pandanus', 'n.', 'A genus of endogenous plants. See Screw pine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pandar', 'n.', 'Same as Pander.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pandarism', 'n.', 'Same as Panderism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pandarize', 'v. i.', 'To pander.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pandarous', 'a.', 'Panderous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pandean', 'a.', 'Of or relating to the god Pan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pandect', 'n.', 'A treatise which comprehends the whole of any science.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pandect', 'n.', 'The digest, or abridgment, in fifty books, of the\n   decisions, writings, and opinions of the old Roman jurists, made in the\n   sixth century by direction of the emperor Justinian, and forming the\n   leading compilation of the Roman civil law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pandemic', 'a.', 'Affecting a whole people or a number of countries;\n   everywhere epidemic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pandemic', 'n.', 'A pandemic disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pandemonium', 'n.', 'The great hall or council chamber of demons or evil\n   spirits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pandemonium', 'n.', 'An utterly lawless, riotous place or assemblage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pander', 'n.', 'A male bawd; a pimp; a procurer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pander', 'n.', 'Hence, one who ministers to the evil designs and passions\n   of another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pandered', 'imp. & p. p.', 'of Pander');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pandering', 'p. pr. & vb. n.', 'of Pander');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pander', 'v. t.', 'To play the pander for.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pander', 'v. i.', 'To act the part of a pander.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panderage', 'n.', 'The act of pandering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panderism', 'n.', 'The employment, arts, or practices of a pander.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panderly', 'a.', 'Having the quality of a pander.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pandermite', 'n.', 'A hydrous borate of lime, near priceite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panderous', 'a.', 'Of or relating to a pander; characterizing a pander.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pandiculated', 'a.', 'Extended; spread out; stretched.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pandiculation', 'n.', 'A stretching and stiffening of the trunk and\n   extremities, as when fatigued and drowsy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pandit', 'n.', 'See Pundit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pandoor', 'n.', 'Same as Pandour.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pandora', 'n.', 'A beautiful woman (all-gifted), whom Jupiter caused Vulcan\n   to make out of clay in order to punish the human race, because\n   Prometheus had stolen the fire from heaven. Jupiter gave Pandora a box\n   containing all human ills, which, when the box was opened, escaped and\n   spread over the earth. Hope alone remained in the box. Another version\n   makes the box contain all the blessings of the gods, which were lost to\n   men when Pandora opened it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pandora', 'n.', 'A genus of marine bivalves, in which one valve is flat,\n   the other convex.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pandore', 'n.', 'An ancient musical instrument, of the lute kind; a\n   bandore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pandour', 'n.', 'One of a class of Hungarian mountaineers serving in the\n   Austrian army; -- so called from Pandur, a principal town in the region\n   from which they originally came.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pandowdy', 'n.', 'A deep pie or pudding made of baked apples, or of sliced\n   bread and apples baked together, with no bottom crust.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pandurate', 'a.', 'Alt. of Panduriform');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panduriform', 'a.', 'Obovate, with a concavity in each side, like the body\n   of a violin; fiddle-shaped; as, a panduriform leaf; panduriform color\n   markings of an animal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pane', 'n.', 'The narrow edge of a hammer head. See Peen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pane', 'n.', 'A division; a distinct piece, limited part, or compartment of\n   any surface; a patch; hence, a square of a checkered or plaided\n   pattern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pane', 'n.', 'One of the openings in a slashed garment, showing the bright\n   colored silk, or the like, within; hence, the piece of colored or other\n   stuff so shown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pane', 'n.', 'A compartment of a surface, or a flat space; hence, one side\n   or face of a building; as, an octagonal tower is said to have eight\n   panes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pane', 'n.', 'Especially, in modern use, the glass in one compartment of a\n   window sash.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pane', 'n.', 'In irrigating, a subdivision of an irrigated surface between\n   a feeder and an outlet drain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pane', 'n.', 'One of the flat surfaces, or facets, of any object having\n   several sides.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pane', 'n.', 'One of the eight facets surrounding the table of a brilliant\n   cut diamond.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paned', 'a.', 'Having panes; provided with panes; also, having openings;\n   as, a paned window; paned window sash.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paned', 'a.', 'Having flat sides or surfaces; as, a six/paned nut.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panegyric', 'a.', 'An oration or eulogy in praise of some person or\n   achievement; a formal or elaborate encomium; a laudatory discourse;\n   laudation. See Synonym of Eulogy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panegyric', 'a.', 'Alt. of Panegyrical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panegyrical', 'a.', 'Containing praise or eulogy; encomiastic; laudatory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panegyris', 'n.', 'A festival; a public assembly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panegyrist', 'n.', 'One who delivers a panegyric; a eulogist; one who\n   extols or praises, either by writing or speaking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panegyrized', 'imp. & p. p.', 'of Panegyrize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panegyrizing', 'p. pr. & vb. n.', 'of Panegyrize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panegyrize', 'v. t.', 'To praise highly; to extol in a public speech; to\n   write or deliver a panegyric upon; to eulogize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panegyrize', 'v. i.', 'To indulge in panegyrics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panegyry', 'n.', 'A panegyric.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panel', 'n.', 'A sunken compartment with raised margins, molded or\n   otherwise, as in ceilings, wainscotings, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panel', 'n.', 'A piece of parchment or a schedule, containing the names of\n   persons summoned as jurors by the sheriff; hence, more generally, the\n   whole jury.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panel', 'n.', 'A prisoner arraigned for trial at the bar of a criminal\n   court.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panel', 'n.', 'Formerly, a piece of cloth serving as a saddle; hence, a\n   soft pad beneath a saddletree to prevent chafing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panel', 'n.', 'A board having its edges inserted in the groove of a\n   surrounding frame; as, the panel of a door.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panel', 'n.', 'One of the faces of a hewn stone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panel', 'n.', 'A slab or plank of wood upon which, instead of canvas, a\n   picture is painted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panel', 'n.', 'A heap of dressed ore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panel', 'n.', 'One of the districts divided by pillars of extra size, into\n   which a mine is laid off in one system of extracting coal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panel', 'n.', 'A plain strip or band, as of velvet or plush, placed at\n   intervals lengthwise on the skirt of a dress, for ornament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panel', 'n.', 'A portion of a framed structure between adjacent posts or\n   struts, as in a bridge truss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paneled', 'imp. & p. p.', 'of Panel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panelled', '', 'of Panel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paneling', 'p. pr. & vb. n.', 'of Panel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panelling', '', 'of Panel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panel', 'v. t.', 'To form in or with panels; as, to panel a wainscot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panelation', 'n.', 'The act of impaneling a jury.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paneless', 'a.', 'Without panes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paneling', 'n.', 'A forming in panels; panelwork.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panelwork', 'n.', 'Wainscoting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paneulogism', 'n.', 'Eulogy of everything; indiscriminate praise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panfuls', 'pl. ', 'of Panful');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panful', 'n.', 'Enough to fill a pan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pang', 'n.', 'A paroxysm of extreme pain or anguish; a sudden and\n   transitory agony; a throe; as, the pangs of death.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pang', 'v. t.', 'To torture; to cause to have great pain or suffering; to\n   torment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pangenesis', 'n.', 'An hypothesis advanced by Darwin in explanation of\n   heredity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pangenetic', 'a.', 'Of or pertaining to pangenesis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pangful', 'a.', 'Full of pangs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pangless', 'a.', 'Without a pang; painless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pangolin', 'n.', 'Any one of several species of Manis, Pholidotus, and\n   related genera, found in Africa and Asia. They are covered with\n   imbricated scales, and feed upon ants. Called also scaly ant-eater.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pangothic', 'a.', 'Of, pertaining to, or including, all the Gothic races.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panhellenic', 'a.', 'Of or pertaining to all Greece, or to Panhellenism;\n   including all Greece, or all the Greeks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panhellenism', 'n.', 'A scheme to unite all the Greeks in one political\n   body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panhellenist', 'n.', 'An advocate of Panhellenism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panhellenium', 'n.', 'An assembly or association of Greeks from all the\n   states of Greece.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panic', 'n.', 'A plant of the genus Panicum; panic grass; also, the edible\n   grain of some species of panic grass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panic', 'a.', 'Extreme or sudden and causeless; unreasonable; -- said of\n   fear or fright; as, panic fear, terror, alarm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panic', 'a.', 'A sudden, overpowering fright; esp., a sudden and groundless\n   fright; terror inspired by a trifling cause or a misapprehension of\n   danger; as, the troops were seized with a panic; they fled in a panic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panic', 'a.', 'By extension: A sudden widespread fright or apprehension\n   concerning financial affairs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panical', 'a.', 'See Panic, a.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panicle', 'n.', 'A pyramidal form of inflorescence, in which the cluster is\n   loosely branched below and gradually simpler toward the end.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panicled', 'a.', 'Furnished with panicles; arranged in, or like, panicles;\n   paniculate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panic-stricken', 'a.', 'Alt. of Panic-struck');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panic-struck', 'a.', 'Struck with a panic, or sudden fear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paniculate', 'a.', 'Alt. of Paniculated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paniculated', 'a.', 'Same as Panicled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panicum', 'n.', 'A genus of grasses, including several hundred species,\n   some of which are valuable; panic grass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panidiomorphic', 'a.', 'Having a completely idiomorphic structure; -- said\n   of certain rocks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panier', 'n.', 'See Pannier, 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panification', 'n.', 'The act or process of making bread.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panim', 'n.', 'See Painim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panislamism', 'n.', 'A desire or plan for the union of all Mohammedan\n   nations for the conquest of the world.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panivorous', 'a.', 'Eating bread; subsisting on bread.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pannade', 'n.', 'The curvet of a horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pannage', 'n.', 'The food of swine in the woods, as beechnuts, acorns,\n   etc.; -- called also pawns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pannage', 'n.', 'A tax paid for the privilege of feeding swine in the\n   woods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pannary', 'a.', 'See Panary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pannel', 'n.', 'A kind of rustic saddle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pannel', 'n.', 'The stomach of a hawk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pannel', 'n.', 'A carriage for conveying a mortar and its bed, on a march.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pannier', 'n.', 'A bread basket; also, a wicker basket (used commonly in\n   pairs) for carrying fruit or other things on a horse or an ass');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pannier', 'n.', 'A shield of basket work formerly used by archers as a\n   shelter from the enemys missiles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pannier', 'n.', 'A table waiter at the Inns of Court, London.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pannier', 'n.', 'A framework of steel or whalebone, worn by women to expand\n   their dresses; a kind of bustle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panniered', 'a.', 'Bearing panniers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pannikel', 'n.', 'The brainpan, or skull; hence, the crest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pannikin', 'n.', 'A small pan or cup.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pannose', 'a.', 'Similar in texture or appearance to felt or woolen cloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pannus', 'n.', 'A very vascular superficial opacity of the cornea, usually\n   caused by granulation of the eyelids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panoistic', 'a.', 'Producing ova only; -- said of the ovaries of certain\n   insects which do not produce vitelligenous cells.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panomphean', 'a.', 'Uttering ominous or prophetic voices; divining.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panoplied', 'a.', 'Dressed in panoply.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panoply', 'n.', 'Defensive armor in general; a full suit of defensive\n   armor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panopticon', 'n.', 'A prison so contructed that the inspector can see each\n   of the prisoners at all times, without being seen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panopticon', 'n.', 'A room for the exhibition of novelties.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panorama', 'n.', 'A complete view in every direction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panorama', 'n.', 'A picture presenting a view of objects in every\n   direction, as from a central point.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panorama', 'n.', 'A picture representing scenes too extended to be beheld\n   at once, and so exhibited a part at a time, by being unrolled, and made\n   to pass continuously before the spectator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panoramic', 'a.', 'Alt. of Panoramical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panoramical', 'a.', 'Of, pertaining to, or like, a panorama.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panorpian', 'a.', 'Like, or pertaining to, the genus Panorpa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panorpian', 'n.', 'Same as Panorpid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panorpid', 'n.', 'Any neuropterous insect of the genus Panorpa, and allied\n   genera. The larvae feed on plant lice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panpharmacon', 'n.', 'A medicine for all diseases; a panacea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panpresbyterian', 'a.', 'Belonging to, or representative of, those who hold\n   Presbyterian views in all parts of the world; as, a Panpresbyterian\n   council.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pansclavic', '', 'Alt. of Pansclavonian');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pansclavism', '', 'Alt. of Pansclavonian');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pansclavist', '', 'Alt. of Pansclavonian');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pansclavonian', '', 'See Panslavic, Panslavism, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panshon', 'n.', 'An earthen vessel wider at the top than at the bottom, --\n   used for holding milk and for various other purposes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pansied', 'a.', 'Covered or adorned with pansies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panslavic', 'a.', 'Pertaining to all the Slavic races.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panslavism', 'n.', 'A scheme or desire to unite all the Slavic races into\n   one confederacy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panslavist', 'n.', 'One who favors Panslavism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panslavonian', 'a.', 'See Panslavic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pansophical', 'a.', 'All-wise; claiming universal knowledge; as,\n   pansophical pretenders.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pansophy', 'n.', 'Universal wisdom; esp., a system of universal knowledge\n   proposed by Comenius (1592 -- 1671), a Moravian educator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panspermatist', 'n.', 'Alt. of Panspermist');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panspermist', 'n.', 'A believer in panspermy; one who rejects the theory of\n   spontaneous generation; a biogenist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panspermic', 'a.', 'Of or pertaining to panspermy; as, the panspermic\n   hypothesis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panspermy', 'n.', 'The doctrine of the widespread distribution of germs,\n   from which under favorable circumstances bacteria, vibrios, etc., may\n   develop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panspermy', 'n.', 'The doctrine that all organisms must come from living\n   parents; biogenesis; -- the opposite of spontaneous generation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panstereorama', 'n.', 'A model of a town or country, in relief, executed in\n   wood, cork, pasteboard, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pansies', 'pl. ', 'of Pansy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pansy', 'n.', 'A plant of the genus Viola (V. tricolor) and its blossom,\n   originally purple and yellow. Cultivated varieties have very large\n   flowers of a great diversity of colors. Called also hearts-ease,\n   love-in-idleness, and many other quaint names.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panted', 'imp. & p. p.', 'of Pant');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panting', 'p. pr. & vb. n.', 'of Pant');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pant', 'v. i.', 'To breathe quickly or in a labored manner, as after\n   exertion or from eagerness or excitement; to respire with heaving of\n   the breast; to gasp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pant', 'v. i.', 'Hence: To long eagerly; to desire earnestly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pant', 'v. i.', 'To beat with unnatural violence or rapidity; to palpitate,\n   or throb; -- said of the heart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pant', 'v. i.', 'To sigh; to flutter; to languish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pant', 'v. t.', 'To breathe forth quickly or in a labored manner; to gasp\n   out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pant', 'v. t.', 'To long for; to be eager after.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pant', 'n.', 'A quick breathing; a catching of the breath; a gasp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pant', 'n.', 'A violent palpitation of the heart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panta-', '', 'See Pan-.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pantable', 'n.', 'See Pantofle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pantacosm', 'n.', 'See Cosmolabe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pantagraph', 'n.', 'See Pantograph.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pantagruelism', 'n.', 'The theory or practice of the medical profession; --\n   used in burlesque or ridicule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pantagruelism', 'n.', 'An assumption of buffoonery to cover some serious\n   purpose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pantalet', 'n.', 'One of the legs of the loose drawers worn by children and\n   women; particularly, the lower part of such a garment, coming below the\n   knee, often made in a separate piece; -- chiefly in the plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pantaloon', 'n.', 'A ridiculous character, or an old dotard, in the Italian\n   comedy; also, a buffoon in pantomimes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pantaloon', 'n.', 'A bifurcated garment for a man, covering the body from\n   the waist downwards, and consisting of breeches and stockings in one.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pantaloon', 'n.', 'In recent times, same as Trousers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pantaloonery', 'n.', 'The character or performances of a pantaloon;\n   buffoonery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pantaloonery', 'n.', 'Materials for pantaloons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pantamorph', 'n.', 'That which assumes, or exists in, all forms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pantamorphic', 'a.', 'Taking all forms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pantascope', 'n.', 'A pantascopic camera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pantascopic', 'a.', 'Viewing all; taking a view of the whole. See under\n   Camera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pantastomata', 'n. pl.', 'One of the divisions of Flagellata, including the\n   monads and allied forms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pantechnicon', 'n.', 'A depository or place where all sorts of manufactured\n   articles are collected for sale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pantelegraph', 'n.', 'See under Telegraph.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panter', 'n.', 'One who pants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panter', 'n.', 'A keeper of the pantry; a pantler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panter', 'n.', 'A net; a noose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panteutonic', 'a.', 'Of or pertaining to all the Teutonic races.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pantheism', 'n.', 'The doctrine that the universe, taken or conceived of as\n   a whole, is God; the doctrine that there is no God but the combined\n   force and laws which are manifested in the existing universe;\n   cosmotheism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pantheist', 'n.', 'One who holds to pantheism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pantheistic', 'a.', 'Alt. of Pantheistical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pantheistical', 'a.', 'Of or pertaining to pantheism; founded in, or\n   leading to, pantheism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pantheologist', 'n.', 'One versed in pantheology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pantheology', 'n.', 'A system of theology embracing all religions; a\n   complete system of theology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pantheon', 'n.', 'A temple dedicated to all the gods; especially, the\n   building so called at Rome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pantheon', 'n.', 'The collective gods of a people, or a work treating of\n   them; as, a divinity of the Greek pantheon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panther', 'n.', 'A large dark-colored variety of the leopard, by some\n   zoologists considered a distinct species. It is marked with large\n   ringlike spots, the centers of which are darker than the color of the\n   body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panther', 'n.', 'In America, the name is applied to the puma, or cougar,\n   and sometimes to the jaguar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pantheress', 'n.', 'A female panther.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pantherine', 'a.', 'Like a panther, esp. in color; as, the pantherine snake\n   (Ptyas mucosus) of Brazil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pantile', 'n.', 'A roofing tile, of peculiar form, having a transverse\n   section resembling an elongated S laid on its side (/).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pantingly', 'adv.', 'With palpitation or rapid breathing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pantisocracy', 'n.', 'A Utopian community, in which all should rule\n   equally, such as was devised by Coleridge, Lovell, and Southey, in\n   their younger days.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pantisocrat', 'n.', 'A pantisocratist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pantisocratic', 'a.', 'Of or pertaining to a pantisocracy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pantisocratist', 'n.', 'One who favors or supports the theory of a\n   pantisocracy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pantler', 'n.', 'The servant or officer, in a great family, who has charge\n   of the bread and the pantry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panto-', '', 'See Pan-.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pantochronometer', 'n.', 'An instrument combining a compass, sundial, and\n   universal time dial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pantofle', 'n.', 'A slipper for the foot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pantograph', 'n.', 'An instrument for copying plans, maps, and other\n   drawings, on the same, or on a reduced or an enlarged, scale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pantographic', 'a.', 'Alt. of Pantographical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pantographical', 'a.', 'Of or pertaining to a pantograph; relating to\n   pantography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pantography', 'n.', 'A general description; entire view of an object.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pantological', 'a.', 'Of or pertaining to pantology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pantologist', 'n.', 'One versed in pantology; a writer of pantology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pantology', 'n.', 'A systematic view of all branches of human knowledge; a\n   work of universal information.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pantometer', 'n.', 'An instrument for measuring angles for determining\n   elevations, distances, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pantometry', 'n.', 'Universal measurement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pantomime', 'n.', 'A universal mimic; an actor who assumes many parts;\n   also, any actor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pantomime', 'n.', 'One who acts his part by gesticulation or dumb show\n   only, without speaking; a pantomimist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pantomime', 'n.', 'A dramatic representation by actors who use only dumb\n   show; hence, dumb show, generally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pantomime', 'n.', 'A dramatic and spectacular entertainment of which dumb\n   acting as well as burlesque dialogue, music, and dancing by Clown,\n   Harlequin, etc., are features.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pantomime', 'a.', 'Representing only in mute actions; pantomimic; as, a\n   pantomime dance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pantomimic', 'a.', 'Alt. of Pantomimical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pantomimical', 'a.', 'Of or pertaining to the pantomime; representing by\n   dumb show.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pantomimist', 'n.', 'An actor in pantomime; also, a composer of pantomimes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panton', 'n.', 'A horseshoe to correct a narrow, hoofbound heel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pantophagist', 'n.', 'A person or an animal that has the habit of eating\n   all kinds of food.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pantophagous', 'a.', 'Eating all kinds of food.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pantophagy', 'n.', 'The habit or power of eating all kinds of food.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pantopoda', 'n. pl.', 'Same as Pycnogonida.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pantoscopic', 'a.', 'Literally, seeing everything; -- a term applied to\n   eyeglasses or spectacles divided into two segments, the upper being\n   designed for distant vision, the lower for vision of near objects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pantries', 'pl. ', 'of Pantry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pantry', 'n.', 'An apartment or closet in which bread and other provisions\n   are kept.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panurgic', 'a.', 'Skilled in all kinds of work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panurgy', 'n.', 'Skill in all kinds of work or business; craft.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panyard', 'n.', 'See Pannier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panym', 'n. & a.', 'See Panim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Panzoism', 'n.', 'A term used to denote all of the elements or factors\n   which constitute vitality or vital energy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paolo', 'n.', 'An old Italian silver coin, worth about ten cents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pap', 'n.', 'A nipple; a mammilla; a teat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pap', 'n.', 'A rounded, nipplelike hill or peak; anything resembling a\n   nipple in shape; a mamelon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pap', 'n.', 'A soft food for infants, made of bread boiled or softtened in\n   milk or water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pap', 'n.', 'Nourishment or support from official patronage; as, treasury\n   pap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pap', 'n.', 'The pulp of fruit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pap', 'v. t.', 'To feed with pap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Papa', 'n.', 'A childs word for father.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Papa', 'n.', 'A parish priest in the Greek Church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Papabote', 'n.', 'The upland plover.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Papacy', 'n.', 'The office and dignity of the pope, or pontiff, of Rome;\n   papal jurisdiction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Papacy', 'n.', 'The popes, collectively; the succession of popes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Papacy', 'n.', 'The Roman Catholic religion; -- commonly used by the\n   opponents of the Roman Catholics in disparagement or in an opprobrious\n   sense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Papagay', 'n.', 'See Popinjay, 1 (b).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Papain', 'n.', 'A proteolytic ferment, like trypsin, present in the juice\n   of the green fruit of the papaw (Carica Papaya) of tropical America.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Papal', 'a.', 'Of or pertaining to the pope of Rome; proceeding from the\n   pope; ordered or pronounced by the pope; as, papal jurisdiction; a\n   papal edict; the papal benediction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Papal', 'a.', 'Of or pertaining to the Roman Catholic Church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Papalist', 'n.', 'A papist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Papality', 'n.', 'The papacy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Papalize', 'v. t.', 'To make papal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Papalize', 'v. i.', 'To conform to popery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Papally', 'adv.', 'In a papal manner; popishly');
  INSERT INTO words (word, word_type, description) VALUES  (    'Papalty', 'n.', 'The papacy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Papaphobia', 'n.', 'Intense fear or dread of the pope, or of the Roman\n   Catholic Church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paparchy', 'n.', 'Government by a pope; papal rule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Papaver', 'n.', 'A genus of plants, including the poppy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Papaveraceous', 'a.', 'Of, pertaining to, or resembling, a natural order of\n   plants (Papaveraceae) of which the poppy, the celandine, and the\n   bloodroot are well-known examples.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Papaverine', 'n.', 'An alkaloid found in opium. It has a weaker therapeutic\n   action than morphine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Papaverous', 'a.', 'Of or pertaining to the poppy; of the nature of the\n   poppy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Papaw', 'n.', 'A tree (Carica Papaya) of tropical America, belonging to the\n   order Passifloreae. It has a soft, spongy stem, eighteen or twenty feet\n   high, crowned with a tuft of large, long-stalked, palmately lobed\n   leaves. The milky juice of the plant is said to have the property of\n   making meat tender. Also, its dull orange-colored, melon-shaped fruit,\n   which is eaten both raw and cooked or pickled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Papaw', 'n.', 'A tree of the genus Asimina (A. triloba), growing in the\n   western and southern parts of the United States, and producing a sweet\n   edible fruit; also, the fruit itself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Papboat', 'n.', 'A kind of sauce boat or dish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Papboat', 'n.', 'A large spiral East Indian marine shell (Turbinella\n   rapha); -- so called because used by native priests to hold the oil for\n   anointing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pape', 'n.', 'A spiritual father; specifically, the pope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Papejay', 'n.', 'A popinjay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paper', 'n.', 'A substance in the form of thin sheets or leaves intended to\n   be written or printed on, or to be used in wrapping. It is made of\n   rags, straw, bark, wood, or other fibrous material, which is first\n   reduced to pulp, then molded, pressed, and dried.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paper', 'n.', 'A sheet, leaf, or piece of such substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paper', 'n.', 'A printed or written instrument; a document, essay, or the\n   like; a writing; as, a paper read before a scientific society.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paper', 'n.', 'A printed sheet appearing periodically; a newspaper; a\n   journal; as, a daily paper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paper', 'n.', 'Negotiable evidences of indebtedness; notes; bills of\n   exchange, and the like; as, the bank holds a large amount of his paper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paper', 'n.', 'Decorated hangings or coverings for walls, made of paper.\n   See Paper hangings, below.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paper', 'n.', 'A paper containing (usually) a definite quantity; as, a\n   paper of pins, tacks, opium, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paper', 'n.', 'A medicinal preparation spread upon paper, intended for\n   external application; as, cantharides paper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paper', 'a.', 'Of or pertaining to paper; made of paper; resembling paper;\n   existing only on paper; unsubstantial; as, a paper box; a paper army.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Papered', 'imp. & p. p.', 'of Paper');
  INSERT INTO words (word, word_type, description) VALUES  (    'Papering', 'p. pr. & vb. n.', 'of Paper');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paper', 'v. t.', 'To cover with paper; to furnish with paper hangings; as,\n   to paper a room or a house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paper', 'v. t.', 'To fold or inclose in paper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paper', 'v. t.', 'To put on paper; to make a memorandum of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paperweight', 'n.', 'See under Paper, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Papery', 'a.', 'Like paper; having the thinness or consistence of paper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Papescent', 'a.', 'Containing or producing pap; like pap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Papess', 'n.', 'A female pope; i. e., the fictitious pope Joan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Papeterie', 'n.', 'A case or box containing paper and materials for\n   writing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paphian', 'a.', 'Of or pertaining to Paphos, an ancient city of Cyprus,\n   having a celebrated temple of Venus; hence, pertaining to Venus, or her\n   rites.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paphian', 'n.', 'A native or inhabitant of Paphos.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Papier-mache', 'n.', 'A hard and strong substance made of a pulp from\n   paper, mixed with sise or glue, etc. It is formed into various\n   articles, usually by means of molds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Papilio', 'n.', 'A genus of butterflies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Papilionaceous', 'a.', 'Resembling the butterfly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Papilionaceous', 'a.', 'Having a winged corolla somewhat resembling a\n   butterfly, as in the blossoms of the bean and pea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Papilionaceous', 'a.', 'Belonging to that suborder of leguminous plants\n   (Papilionaceae) which includes the bean, pea, vetch, clover, and\n   locust.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Papiliones', 'n. pl.', 'The division of Lepidoptera which includes the\n   butterflies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Papilionides', 'n. pl.', 'The typical butterflies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Papillae', 'pl. ', 'of Papilla');
  INSERT INTO words (word, word_type, description) VALUES  (    'Papilla', 'n.', 'Any minute nipplelike projection; as, the papillae of the\n   tongue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Papillar', 'a.', 'Same as Papillose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Papillary', 'a.', 'Of, pertaining to, or resembling, a papilla or papillae;\n   bearing, or covered with, papillae; papillose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Papillate', 'v. t. & i.', 'To cover with papillae; to take the form of a\n   papilla, or of papillae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Papillate', 'a.', 'Same as Papillose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Papilliform', 'a.', 'Shaped like a papilla; mammilliform.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Papillomata', 'pl. ', 'of Papilloma');
  INSERT INTO words (word, word_type, description) VALUES  (    'Papilloma', 'n.', 'A tumor formed by hypertrophy of the papillae of the\n   skin or mucous membrane, as a corn or a wart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Papillomatous', 'a.', 'Of, pertaining to, or consisting of, papillomata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Papillose', 'a.', 'Covered with, or bearing, papillae; resembling papillae;\n   papillate; papillar; papillary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Papillote', 'n.', 'a small piece of paper on which women roll up their hair\n   to make it curl; a curl paper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Papillous', 'a.', 'Papillary; papillose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Papillulate', 'a.', 'Having a minute papilla in the center of a larger\n   elevation or depression.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Papion', 'n.', 'A West African baboon (Cynocephalus sphinx), allied to the\n   chacma. Its color is generally chestnut, varying in tint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Papism', 'n.', 'Popery; -- an offensive term.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Papist', 'n.', 'A Roman catholic; one who adheres to the Church of Rome and\n   the authority of the pope; -- an offensive designation applied to Roman\n   Catholics by their opponents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Papistic', 'a.', 'Alt. of Papistical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Papistical', 'a.', 'Of or pertaining to the Church of Rome and its\n   doctrines and ceremonies; pertaining to popery; popish; -- used\n   disparagingly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Papistry', 'n.', 'The doctrine and ceremonies of the Church of Rome;\n   popery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Papized', 'a.', 'Conformed to popery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Papoose', 'n.', 'A babe or young child of Indian parentage in North\n   America.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pappiform', 'a.', 'Resembling the pappus of composite plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pappoose', 'n.', 'Same as Papoose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pappose', 'a.', 'Furnished with a pappus; downy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pappous', 'a.', 'Pappose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pappus', 'n.', 'The hairy or feathery appendage of the achenes of thistles,\n   dandelions, and most other plants of the order Compositae; also, the\n   scales, awns, or bristles which represent the calyx in other plants of\n   the same order.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pappy', 'a.', 'Like pap; soft; succulent; tender.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Papuan', 'a.', 'Of or pertaining to Papua.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Papuars', 'n. pl.', 'The native black race of Papua or New Guinea, and the\n   adjacent islands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Papulae', 'pl. ', 'of Papula');
  INSERT INTO words (word, word_type, description) VALUES  (    'Papula', 'n.', 'A pimple; a small, usually conical, elevation of the\n   cuticle, produced by congestion, accumulated secretion, or hypertrophy\n   of tissue; a papule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Papula', 'n.', 'One of the numerous small hollow processes of the\n   integument between the plates of starfishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Papular', 'a.', 'Covered with papules.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Papular', 'a.', 'Consisting of papules; characterized by the presence of\n   papules; as, a papular eruption.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Papules', 'pl. ', 'of Papule');
  INSERT INTO words (word, word_type, description) VALUES  (    'Papule', 'n.', 'Same as Papula.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Papulose', 'a.', 'Having papulae; papillose; as, a papulose leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Papulous', 'a.', 'Covered with, or characterized by, papulae; papulose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Papyraceous', 'a.', 'Made of papyrus; of the consistency of paper; papery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Papyrean', 'a.', 'Of or pertaining to papyrus, or to paper; papyraceous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Papyrine', 'n.', 'Imitation parchment, made by soaking unsized paper in\n   dilute sulphuric acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Papyrograph', 'n.', 'An apparatus for multiplying writings, drawings, etc.,\n   in which a paper stencil, formed by writing or drawing with corrosive\n   ink, is used. The word is also used of other means of multiplying\n   copies of writings, drawings, etc. See Copygraph, Hectograph, Manifold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Papyrography', 'n.', 'The process of multiplying copies of writings, etc.,\n   by means of the papyrograph.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Papyri', 'pl. ', 'of Papyrus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Papyrus', 'n.', 'A tall rushlike plant (Cyperus Papyrus) of the Sedge\n   family, formerly growing in Egypt, and now found in Abyssinia, Syria,\n   Sicily, etc. The stem is triangular and about an inch thick.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Papyrus', 'n.', 'The material upon which the ancient Egyptians wrote. It\n   was formed by cutting the stem of the plant into thin longitudinal\n   slices, which were gummed together and pressed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Papyrus', 'n.', 'A manuscript written on papyrus; esp., pl., written\n   scrolls made of papyrus; as, the papyri of Egypt or Herculaneum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paque', 'n.', 'See Pasch and Easter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Par', 'n.', 'See Parr.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Par', 'prep.', 'By; with; -- used frequently in Early English in phrases\n   taken from the French, being sometimes written as a part of the word\n   which it governs; as, par amour, or paramour; par cas, or parcase; par\n   fay, or parfay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Par', 'n.', 'Equal value; equality of nominal and actual value; the value\n   expressed on the face or in the words of a certificate of value, as a\n   bond or other commercial paper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Par', 'n.', 'Equality of condition or circumstances.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Para-', '', 'A prefix signifying alongside of, beside, beyond, against,\n   amiss; as parable, literally, a placing beside; paradox, that which is\n   contrary to opinion; parachronism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Para-', '', 'A prefix denoting: (a) Likeness, similarity, or connection, or\n   that the substance resembles, but is distinct from, that to the name of\n   which it is prefixed; as paraldehyde, paraconine, etc.; also, an\n   isomeric modification. (b) Specifically: (Organ. Chem.) That two groups\n   or radicals substituted in the benzene nucleus are opposite, or in the\n   respective positions 1 and 4; 2 and 5; or 3 and 6, as paraxylene;\n   paroxybenzoic acid. Cf. Ortho-, and Meta-. Also used adjectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Para', 'n.', 'A piece of Turkish money, usually copper, the fortieth part\n   of a piaster, or about one ninth of a cent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parabanic', 'a.', 'Pertaining to, or designating, a nitrogenous acid which\n   is obtained by the oxidation of uric acid, as a white crystalline\n   substance (C3N2H2O3); -- also called oxalyl urea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parablast', 'n.', 'A portion of the mesoblast (of peripheral origin) of the\n   developing embryo, the cells of which are especially concerned in\n   forming the first blood and blood vessels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parablastic', 'a.', 'Of or pertaining to the parablast; as, the parablastic\n   cells.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parable', 'a.', 'Procurable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parable', 'n.', 'A comparison; a similitude; specifically, a short\n   fictitious narrative of something which might really occur in life or\n   nature, by means of which a moral is drawn; as, the parables of Christ.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parable', 'v. t.', 'To represent by parable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parabolas', 'pl. ', 'of Parabola');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parabola', 'n.', 'A kind of curve; one of the conic sections formed by the\n   intersection of the surface of a cone with a plane parallel to one of\n   its sides. It is a curve, any point of which is equally distant from a\n   fixed point, called the focus, and a fixed straight line, called the\n   directrix. See Focus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parabola', 'n.', 'One of a group of curves defined by the equation y = axn\n   where n is a positive whole number or a positive fraction. For the\n   cubical parabola n = 3; for the semicubical parabola n = /. See under\n   Cubical, and Semicubical. The parabolas have infinite branches, but no\n   rectilineal asymptotes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parabole', 'n.', 'Similitude; comparison.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parabolic', 'a.', 'Alt. of Parabolical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parabolical', 'a.', 'Of the nature of a parable; expressed by a parable or\n   figure; allegorical; as, parabolical instruction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parabolical', 'a.', 'Having the form or nature of a parabola; pertaining\n   to, or resembling, a parabola; as, a parabolic curve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parabolical', 'a.', 'Generated by the revolution of a parabola, or by a\n   line that moves on a parabola as a directing curve; as, a parabolic\n   conoid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parabolically', 'adv.', 'By way of parable; in a parabolic manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parabolically', 'adv.', 'In the form of a parabola.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paraboliform', 'a.', 'Resembling a parabola in form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parabolism', 'n.', 'The division of the terms of an equation by a known\n   quantity that is involved in the first term.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parabolist', 'n.', 'A narrator of parables.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paraboloid', 'n.', 'The solid generated by the rotation of a parabola about\n   its axis; any surface of the second order whose sections by planes\n   parallel to a given line are parabolas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paraboloidal', 'a.', 'Of, pertaining to, or resembling, a paraboloid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parabronchia', 'pl. ', 'of Parabronchium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parabronchium', 'n.', 'One of the branches of an ectobronchium or\n   entobronchium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paracelsian', 'a.', 'Of, pertaining to, or in conformity with, the practice\n   of Paracelsus, a Swiss physician of the 15th century.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paracelsian', 'n.', 'A follower of Paracelsus or his practice or teachings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paracelsist', 'n.', 'A Paracelsian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paracentesis', 'n.', 'The perforation of a cavity of the body with a\n   trocar, aspirator, or other suitable instrument, for the evacuation of\n   effused fluid, pus, or gas; tapping.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paracentric', 'a.', 'Alt. of Paracentrical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paracentrical', 'a.', 'Deviating from circularity; changing the distance\n   from a center.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parachordal', 'a.', 'Situated on either side of the notochord; -- applied\n   especially to the cartilaginous rudiments of the skull on each side of\n   the anterior part of the notochord.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parachordal', 'n.', 'A parachordal cartilage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parachronism', 'n.', 'An error in chronology, by which the date of an event\n   is set later than the time of its occurrence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parachrose', 'a.', 'Changing color by exposure');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parachute', 'n.', 'A contrivance somewhat in the form of an umbrella, by\n   means of which a descent may be made from a balloon, or any eminence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parachute', 'n.', 'A web or fold of skin which extends between the legs of\n   certain mammals, as the flying squirrels, colugo, and phalangister.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paraclete', 'n.', 'An advocate; one called to aid or support; hence, the\n   Consoler, Comforter, or Intercessor; -- a term applied to the Holy\n   Spirit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paraclose', 'n.', 'See Parclose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paracmastic', 'a.', 'Gradually decreasing; past the acme, or crisis, as a\n   distemper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paraconic', 'a.', 'Pertaining to, or designating, an organic acid obtained\n   as a deliquescent white crystalline substance, and isomeric with\n   itaconic, citraconic, and mesaconic acids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paraconine', 'n.', 'A base resembling and isomeric with conine, and\n   obtained as a colorless liquid from butyric aldehyde and ammonia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paracorolla', 'n.', 'A secondary or inner corolla; a corona, as of the\n   Narcissus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paracrostic', 'n.', 'A poetical composition, in which the first verse\n   contains, in order, the first letters of all the verses of the poem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paracyanogen', 'n.', 'A polymeric modification of cyanogen, obtained as a\n   brown or black amorphous residue by heating mercuric cyanide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paracymene', 'n.', 'Same as Cymene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paradactyla', 'pl. ', 'of Paradactylum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paradactylum', 'n.', 'The side of a toe or finger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parade', 'v. t.', 'The ground where a military display is held, or where\n   troops are drilled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parade', 'v. t.', 'An assembly and orderly arrangement or display of\n   troops, in full equipments, for inspection or evolutions before some\n   superior officer; a review of troops. Parades are general, regimental,\n   or private (troop, battery, or company), according to the force\n   assembled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parade', 'v. t.', 'Pompous show; formal display or exhibition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parade', 'v. t.', 'That which is displayed; a show; a spectacle; an\n   imposing procession; the movement of any body marshaled in military\n   order; as, a parade of firemen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parade', 'v. t.', 'Posture of defense; guard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parade', 'v. t.', 'A public walk; a promenade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paraded', 'imp. & p. p.', 'of Parade');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parading', 'p. pr. & vb. n.', 'of Parade');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parade', 'v. t.', 'To exhibit in a showy or ostentatious manner; to show\n   off.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parade', 'v. t.', 'To assemble and form; to marshal; to cause to maneuver\n   or march ceremoniously; as, to parade troops.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parade', 'v. i.', 'To make an exhibition or spectacle of ones self, as by\n   walking in a public place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parade', 'v. i.', 'To assemble in military order for evolutions and\n   inspection; to form or march, as in review.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paradigm', 'n.', 'An example; a model; a pattern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paradigm', 'n.', 'An example of a conjugation or declension, showing a word\n   in all its different forms of inflection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paradigm', 'n.', 'An illustration, as by a parable or fable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paradigmatic', 'a.', 'Alt. of Paradigmatical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paradigmatical', 'a.', 'Exemplary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paradigmatic', 'n.', 'A writer of memoirs of religious persons, as examples\n   of Christian excellence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paradigmatized', 'imp. & p. p.', 'of Paradigmatize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paradigmatizing', 'p. pr. & vb. n.', 'of Paradigmatize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paradigmatize', 'v. t.', 'To set forth as a model or example.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paradisaic', 'a.', 'Alt. of Paradisaical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paradisaical', 'a.', 'Of or pertaining to, or resembling, paradise;\n   paradisiacal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paradisal', 'a.', 'Paradisiacal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paradise', 'n.', 'The garden of Eden, in which Adam and Eve were placed\n   after their creation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paradise', 'n.', 'The abode of sanctified souls after death.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paradise', 'n.', 'A place of bliss; a region of supreme felicity or\n   delight; hence, a state of happiness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paradise', 'n.', 'An open space within a monastery or adjoining a church,\n   as the space within a cloister, the open court before a basilica, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paradise', 'n.', 'A churchyard or cemetery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paradise', 'v. t.', 'To affect or exalt with visions of felicity; to\n   entrance; to bewitch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paradisean', 'a.', 'Paradisiacal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paradised', 'a.', 'Placed in paradise; enjoying delights as of paradise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paradisiac', 'a.', 'Alt. of Paradisiacal');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paradisiacal', 'a.', 'Of or pertaining to paradise; suitable to, or like,\n   paradise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paradisial', 'a.', 'Alt. of Paradisian');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paradisian', 'a.', 'Paradisiacal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paradisic', 'a.', 'Paradisiacal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paradisical', 'a.', 'Paradisiacal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paradoses', 'pl. ', 'of Parados');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parados', 'n.', 'An intercepting mound, erected in any part of a\n   fortification to protect the defenders from a rear or ricochet fire; a\n   traverse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paradoxes', 'pl. ', 'of Paradox');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paradox', 'n.', 'A tenet or proposition contrary to received opinion; an\n   assertion or sentiment seemingly contradictory, or opposed to common\n   sense; that which in appearance or terms is absurd, but yet may be true\n   in fact.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paradoxal', 'a.', 'Paradoxical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paradoxical', 'a.', 'Of the nature of a paradox.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paradoxical', 'a.', 'Inclined to paradoxes, or to tenets or notions\n   contrary to received opinions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paradoxer', 'n.', 'Alt. of Paradoxist');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paradoxist', 'n.', 'One who proposes a paradox.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paradoxides', 'n.', 'A genus of large trilobites characteristic of the\n   primordial formations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paradoxology', 'n.', 'The use of paradoxes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paradoxure', 'n.', 'Any species of Paradoxurus, a genus of Asiatic\n   viverrine mammals allied to the civet, as the musang, and the luwack or\n   palm cat (Paradoxurus hermaphroditus). See Musang.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paradoxy', 'n.', 'A paradoxical statement; a paradox.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paradoxy', 'n.', 'The quality or state of being paradoxical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paraffin', 'n.', 'Alt. of Paraffine');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paraffine', 'n.', 'A white waxy substance, resembling spermaceti, tasteless\n   and odorless, and obtained from coal tar, wood tar, petroleum, etc., by\n   distillation. It is used as an illuminant and lubricant. It is very\n   inert, not being acted upon by most of the strong chemical reagents. It\n   was formerly regarded as a definite compound, but is now known to be a\n   complex mixture of several higher hydrocarbons of the methane or\n   marsh-gas series; hence, by extension, any substance, whether solid,\n   liquid, or gaseous, of the same chemical series; thus coal gas and\n   kerosene consist largely of paraffins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parage', 'n.', 'Equality of condition, blood, or dignity; also, equality in\n   the partition of an inheritance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parage', 'n.', 'Equality of condition between persons holding unequal\n   portions of a fee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parage', 'n.', 'Kindred; family; birth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paragenesis', 'n.', 'The science which treats of minerals with special\n   reference to their origin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paragenic', 'a.', 'Originating in the character of the germ, or at the\n   first commencement of an individual; -- said of peculiarities of\n   structure, character, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paraglobulin', 'n.', 'An albuminous body in blood serum, belonging to the\n   group of globulins. See Fibrinoplastin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paraglossae', 'pl. ', 'of Paraglossa');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paraglossa', 'n.', 'One of a pair of small appendages of the lingua or\n   labium of certain insects. See Illust. under Hymenoptera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paragnath', 'n.', 'Same as Paragnathus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paragnathous', 'a.', 'Having both mandibles of equal length, the tips\n   meeting, as in certain birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paragnathi', 'pl. ', 'of Paragnathus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paragnathus', 'n.', 'One of the two lobes which form the lower lip, or\n   metastome, of Crustacea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paragnathus', 'n.', 'One of the small, horny, toothlike jaws of certain\n   annelids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paragoge', 'n.', 'The addition of a letter or syllable to the end of a\n   word, as withouten for without.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paragoge', 'n.', 'Coaptation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paragogic', 'a.', 'Alt. of Paragogical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paragogical', 'a.', 'Of, pertaining to, or constituting, a paragoge; added\n   to the end of, or serving to lengthen, a word.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paragon', 'n.', 'A companion; a match; an equal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paragon', 'n.', 'Emulation; rivalry; competition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paragon', 'n.', 'A model or pattern; a pattern of excellence or perfection;\n   as, a paragon of beauty or eloquence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paragon', 'n.', 'A size of type between great primer and double pica. See\n   the Note under Type.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paragon', 'v. t.', 'To compare; to parallel; to put in rivalry or emulation\n   with.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paragon', 'v. t.', 'To compare with; to equal; to rival.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paragon', 'v. t.', 'To serve as a model for; to surpass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paragon', 'v. i.', 'To be equal; to hold comparison.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paragonite', 'n.', 'A kind of mica related to muscovite, but containing\n   soda instead of potash. It is characteristic of the paragonite schist\n   of the Alps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paragram', 'n.', 'A pun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paragrammatist', 'n.', 'A punster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paragrandine', 'n.', 'An instrument to avert the occurrence of hailstorms.\n   See Paragr/le.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paragraph', 'n.', 'Originally, a marginal mark or note, set in the margin\n   to call attention to something in the text, e. g., a change of subject;\n   now, the character /, commonly used in the text as a reference mark to\n   a footnote, or to indicate the place of a division into sections.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paragraph', 'n.', 'A distinct part of a discourse or writing; any section\n   or subdivision of a writing or chapter which relates to a particular\n   point, whether consisting of one or many sentences. The division is\n   sometimes noted by the mark /, but usually, by beginning the first\n   sentence of the paragraph on a new line and at more than the usual\n   distance from the margin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paragraph', 'n.', 'A brief composition complete in one typographical\n   section or paragraph; an item, remark, or quotation comprised in a few\n   lines forming one paragraph; as, a column of news paragraphs; an\n   editorial paragraph.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paragraphed', 'imp. & p. p.', 'of Paragraph');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paragraphing', 'p. pr. & vb. n.', 'of Paragraph');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paragraph', 'v. t.', 'To divide into paragraphs; to mark with the character\n   /.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paragraph', 'v. t.', 'To express in the compass of a paragraph; as, to\n   paragraph an article.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paragraph', 'v. t.', 'To mention in a paragraph or paragraphs');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paragrapher', 'n.', 'A writer of paragraphs; a paragraphist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paragraphic', 'a.', 'Alt. of Paragraphical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paragraphical', 'a.', 'Pertaining to, or consisting of, a paragraph or\n   paragraphs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paragraphist', 'n.', 'A paragrapher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paragraphistical', 'a.', 'Of or relating to a paragraphist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Para grass', '', 'A valuable pasture grass (Panicum barbinode) introduced\n   into the Southern United States from Brazil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paragrele', 'n.', 'A lightning conductor erected, as in a vineyard, for\n   drawing off the electricity in the atmosphere in order to prevent\n   hailstorms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paraguayan', 'a.', 'Of or pertaining to Paraguay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paraguayan', 'n.', 'A native or inhabitant of Paraguay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paraguay tea', '', 'See Mate, the leaf of the Brazilian holly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parail', 'n.', 'See Apparel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parakeet', 'n.', 'Same as Parrakeet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paralactic', 'a.', 'Designating an acid called paralactic acid. See Lactic\n   acid, under Lactic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paralbumin', 'n.', 'A proteidlike body found in the fluid from ovarian\n   cysts and elsewhere. It is generally associated with a substance\n   related to, if not identical with, glycogen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paraldehyde', 'n.', 'A polymeric modification of aldehyde obtained as a\n   white crystalline substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paraleipsis', 'n.', 'A pretended or apparent omission; a figure by which a\n   speaker artfully pretends to pass by what he really mentions; as, for\n   example, if an orator should say, \"I do not speak of my adversarys\n   scandalous venality and rapacity, his brutal conduct, his treachery and\n   malice.\"');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paralepsis', 'n.', 'See Paraleipsis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paralian', 'n.', 'A dweller by the sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paralipomenon', 'n. pl.', 'A title given in the Douay Bible to the Books of\n   Chronicles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paralipsis', 'n.', 'See Paraleipsis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parallactic', 'a.', 'Alt. of Parallactical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parallactical', 'a.', 'Of or pertaining to a parallax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parallax', 'n.', 'The apparent displacement, or difference of position, of\n   an object, as seen from two different stations, or points of view.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parallax', 'n.', 'The apparent difference in position of a body (as the\n   sun, or a star) as seen from some point on the earths surface, and as\n   seen from some other conventional point, as the earths center or the\n   sun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parallel', 'a.', 'Extended in the same direction, and in all parts equally\n   distant; as, parallel lines; parallel planes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parallel', 'a.', 'Having the same direction or tendency; running side by\n   side; being in accordance (with); tending to the same result; -- used\n   with to and with.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parallel', 'a.', 'Continuing a resemblance through many particulars;\n   applicable in all essential parts; like; similar; as, a parallel case;\n   a parallel passage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parallel', 'n.', 'A line which, throughout its whole extent, is equidistant\n   from another line; a parallel line, a parallel plane, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parallel', 'n.', 'Direction conformable to that of another line,');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parallel', 'n.', 'Conformity continued through many particulars or in all\n   essential points; resemblance; similarity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parallel', 'n.', 'A comparison made; elaborate tracing of similarity; as,\n   Johnsons parallel between Dryden and Pope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parallel', 'n.', 'Anything equal to, or resembling, another in all\n   essential particulars; a counterpart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parallel', 'n.', 'One of the imaginary circles on the surface of the earth,\n   parallel to the equator, marking the latitude; also, the corresponding\n   line on a globe or map.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parallel', 'n.', 'One of a series of long trenches constructed before a\n   besieged fortress, by the besieging force, as a cover for troops\n   supporting the attacking batteries. They are roughly parallel to the\n   line of outer defenses of the fortress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parallel', 'n.', 'A character consisting of two parallel vertical lines\n   (thus, ) used in the text to direct attention to a similarly marked\n   note in the margin or at the foot of a page.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paralleled', 'imp. & p. p.', 'of Parallel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paralleling', 'p. pr. & vb. n.', 'of Parallel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parallel', 'v. t.', 'To place or set so as to be parallel; to place so as\n   to be parallel to, or to conform in direction with, something else.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parallel', 'v. t.', 'Fig.: To make to conform to something else in\n   character, motive, aim, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parallel', 'v. t.', 'To equal; to match; to correspond to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parallel', 'v. t.', 'To produce or adduce as a parallel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parallel', 'v. i.', 'To be parallel; to correspond; to be like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parallelable', 'a.', 'Capable of being paralleled, or equaled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parallelism', 'n.', 'The quality or state of being parallel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parallelism', 'n.', 'Resemblance; correspondence; similarity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parallelism', 'n.', 'Similarity of construction or meaning of clauses\n   placed side by side, especially clauses expressing the same sentiment\n   with slight modifications, as is common in Hebrew poetry; e. g.: --//At\n   her feet he bowed, he fell:/Where he bowed, there he fell down dead.\n   Judg. v. 27.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parallelistic', 'a.', 'Of the nature of a parallelism; involving\n   parallelism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parallelize', 'v. t.', 'To render parallel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parallelless', 'a.', 'Matchless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parallelly', 'adv.', 'In a parallel manner; with parallelism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parallelogram', 'n.', 'A right-lined quadrilateral figure, whose opposite\n   sides are parallel, and consequently equal; -- sometimes restricted in\n   popular usage to a rectangle, or quadrilateral figure which is longer\n   than it is broad, and with right angles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parallelogrammatic', 'a.', 'Of or pertaining to a parallelogram;\n   parallelogrammic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parallelogrammic', 'a.', 'Alt. of Parallelogrammical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parallelogrammical', 'a.', 'Having the properties of a parallelogram.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parallelopiped', 'n.', 'A solid, the faces of which are six parallelograms,\n   the opposite pairs being parallel, and equal to each other; a prism\n   whose base is a parallelogram.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parallelopipedon', 'n.', 'A parallelopiped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paralogical', 'a.', 'Containing paralogism; illogical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paralogism', 'n.', 'A reasoning which is false in point of form, that is,\n   which is contrary to logical rules or formulae; a formal fallacy, or\n   pseudo-syllogism, in which the conclusion does not follow from the\n   premises.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paralogized', 'imp. & p. p.', 'of Paralogize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paralogizing', 'p. pr. & vb. n.', 'of Paralogize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paralogize', 'v. i.', 'To reason falsely; to draw conclusions not warranted\n   by the premises.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paralogy', 'n.', 'False reasoning; paralogism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paralyse', 'v. t.', 'Same as Paralyze.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paralysis', 'n.', 'Abolition of function, whether complete or partial;\n   esp., the loss of the power of voluntary motion, with or without that\n   of sensation, in any part of the body; palsy. See Hemiplegia, and\n   Paraplegia. Also used figuratively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paralytic', 'a.', 'Of or pertaining to paralysis; resembling paralysis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paralytic', 'a.', 'Affected with paralysis, or palsy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paralytic', 'a.', 'Inclined or tending to paralysis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paralytic', 'n.', 'A person affected with paralysis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paralytical', 'a.', 'See Paralytic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paralyzation', 'n.', 'The act or process of paralyzing, or the state of\n   being paralyzed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paralyzed', 'imp. & p. p.', 'of Paralyze');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paralyzing', 'p. pr. & vb. n.', 'of Paralyze');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paralyze', 'v. t.', 'To affect or strike with paralysis or palsy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paralyze', 'v. t.', 'Fig.: To unnerve; to destroy or impair the energy of;\n   to render ineffective; as, the occurrence paralyzed the community;\n   despondency paralyzed his efforts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Param', 'n.', 'A white crystalline nitrogenous substance (C2H4N4); --\n   called also dicyandiamide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paramagnetic', 'a.', 'Magnetic, as opposed to diamagnetic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paramagnetic', 'n.', 'A paramagnetic substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paramagnetism', 'n.', 'Magnetism, as opposed to diamagnetism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paramaleic', 'a.', 'Pertaining to, or designating, an acid obtained from\n   malic acid, and now called fumaric acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paramalic', 'a.', 'Pertaining to, or designating, an organic acid metameric\n   with malic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paramastoid', 'a.', 'Situated beside, or near, the mastoid portion of the\n   temporal bone; paroccipital; -- applied especially to a process of the\n   skull in some animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paramatta', 'n.', 'A light fabric of cotton and worsted, resembling\n   bombazine or merino.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parament', 'n.', 'Ornamental hangings, furniture, etc., as of a state\n   apartment; rich and elegant robes worn by men of rank; -- chiefly in\n   the plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paramento', 'n.', 'Ornament; decoration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paramere', 'n.', 'One of the symmetrical halves of any one of the radii, or\n   spheromeres, of a radiate animal, as a starfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parameter', 'n.', 'A term applied to some characteristic magnitude whose\n   value, invariable as long as one and the same function, curve, surface,\n   etc., is considered, serves to distinguish that function, curve,\n   surface, etc., from others of the same kind or family.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parameter', 'n.', 'Specifically (Conic Sections), in the ellipse and\n   hyperbola, a third proportional to any diameter and its conjugate, or\n   in the parabola, to any abscissa and the corresponding ordinate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parameter', 'n.', 'The ratio of the three crystallographic axes which\n   determines the position of any plane; also, the fundamental axial ratio\n   for a given species.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parametritis', 'n.', 'Inflammation of the cellular tissue in the vicinity\n   of the uterus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paramiographer', 'n.', 'A collector or writer of proverbs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paramitome', 'n.', 'The fluid portion of the protoplasm of a cell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paramos', 'pl. ', 'of Paramo');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paramo', 'n.', 'A high, bleak plateau or district, with stunted trees, and\n   cold, damp atmosphere, as in the Andes, in South America.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paramorph', 'n.', 'A kind of pseudomorph, in which there has been a change\n   of physical characters without alteration of chemical composition, as\n   the change of aragonite to calcite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paramorphism', 'n.', 'The change of one mineral species to another, so as\n   to involve a change in physical characters without alteration of\n   chemical composition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paramorphous', 'a.', 'Relating to paramorphism; exhibiting paramorphism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paramount', 'a.', 'Having the highest rank or jurisdiction; superior to all\n   others; chief; supreme; preeminent; as, a paramount duty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paramount', 'n.', 'The highest or chief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paramountly', 'adv.', 'In a paramount manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paramour', 'n.', 'A lover, of either sex; a wooer or a mistress (formerly\n   in a good sense, now only in a bad one); one who takes the place,\n   without possessing the rights, of a husband or wife; -- used of a man\n   or a woman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paramour', 'n.', 'Love; gallantry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paramour', 'adv.', 'Alt. of Paramours');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paramours', 'adv.', 'By or with love, esp. the love of the sexes; --\n   sometimes written as two words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paramylum', 'n.', 'A substance resembling starch, found in the green frothy\n   scum formed on the surface of stagnant water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paranaphthalene', 'n.', 'Anthracene; -- called also paranaphthaline.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paranoia', 'n.', 'Mental derangement; insanity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paranthracene', 'n.', 'An inert isomeric modification of anthracene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paranucleus', 'n.', 'Some as Nucleolus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Para nut', '', 'The Brazil nut.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paranymph', 'n.', 'A friend of the bridegroom who went with him in his\n   chariot to fetch home the bride.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paranymph', 'n.', 'The bridesmaid who conducted the bride to the\n   bridegroom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paranymph', 'n.', 'An ally; a supporter or abettor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paranymphal', 'a.', 'Bridal; nuptial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parapectin', 'n.', 'A gelatinous modification of pectin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parapegm', 'n.', 'An engraved tablet, usually of brass, set up in a public\n   place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parapeptone', 'n.', 'An albuminous body formed in small quantity by the\n   peptic digestion of proteids. It can be converted into peptone by\n   pancreatic juice, but not by gastric juice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parapet', 'n.', 'A low wall, especially one serving to protect the edge of\n   a platform, roof, bridge, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parapet', 'n.', 'A wall, rampart, or elevation of earth, for covering\n   soldiers from an enemys fire; a breastwork. See Illust. of Casemate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parapetalous', 'a.', 'Growing by the side of a petal, as a stamen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parapeted', 'a.', 'Having a parapet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paraph', 'n.', 'A flourish made with the pen at the end of a signature. In\n   the Middle Ages, this formed a sort of rude safeguard against forgery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paraphed', 'imp. & p. p.', 'of Paraph');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paraphing', 'p. pr. & vb. n.', 'of Paraph');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paraph', 'v. t.', 'To add a paraph to; to sign, esp. with the initials.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parapherna', 'n. pl.', 'The property of a woman which, on her marriage, was\n   not made a part of her dower, but remained her own.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paraphernal', 'a.', 'Of or pertaining to paraphernalia; as, paraphernal\n   property.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paraphernalia', 'n. pl.', 'Something reserved to a wife, over and above her\n   dower, being chiefly apparel and ornaments suited to her degree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paraphernalia', 'n. pl.', 'Appendages; ornaments; finery; equipments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paraphimosis', 'n.', 'A condition in which the prepuce, after being\n   retracted behind the glans penis, is constricted there, and can not be\n   brought forward into place again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paraphosphoric', 'a.', 'Pyrophosphoric.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paraphragmata', 'pl. ', 'of Paraphagma');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paraphagma', 'n.', 'One of the outer divisions of an endosternite of\n   Crustacea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paraphrase', 'n.', 'A restatement of a text, passage, or work, expressing\n   the meaning of the original in another form, generally for the sake of\n   its clearer and fuller exposition; a setting forth the signification of\n   a text in other and ampler terms; a free translation or rendering; --\n   opposed to metaphrase.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paraphrased', 'imp. & p. p.', 'of Paraphrase');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paraphrasing', 'p. pr. & vb. n.', 'of Paraphrase');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paraphrase', 'v. t.', 'To express, interpret, or translate with latitude;\n   to give the meaning of a passage in other language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paraphrase', 'v. i.', 'To make a paraphrase.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paraphraser', 'n.', 'One who paraphrases.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paraphrasian', 'n.', 'A paraphraser.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paraphrast', 'n.', 'A paraphraser.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paraphrastic', 'a.', 'Alt. of Paraphrastical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paraphrastical', 'a.', 'Paraphrasing; of the nature of paraphrase;\n   explaining, or translating in words more clear and ample than those of\n   the author; not literal; free.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paraphyses', 'pl. ', 'of Paraphysis');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paraphysis', 'n.', 'A minute jointed filament growing among the archegonia\n   and antheridia of mosses, or with the spore cases, etc., of other\n   flowerless plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paraplegia', 'n.', 'Alt. of Paraplegy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paraplegy', 'n.', 'Palsy of the lower half of the body on both sides,\n   caused usually by disease of the spinal cord.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parapleurae', 'pl. ', 'of Parapleura');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parapleura', 'n.', 'A chitinous piece between the metasternum and the\n   pleuron of certain insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parapodia', 'pl. ', 'of Parapodium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parapodium', 'n.', 'One of the lateral appendages of an annelid; -- called\n   also foot tubercle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parapophyses', 'pl. ', 'of Parapophysis');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parapophysis', 'n.', 'The ventral transverse, or capitular, process of a\n   vertebra. See Vertebra.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paraptera', 'pl. ', 'of Parapterum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parapterum', 'n.', 'A special plate situated on the sides of the mesothorax\n   and metathorax of certain insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paraquet', 'n.', 'Alt. of Paraquito');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paraquito', 'n.', 'See Parrakeet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parasang', 'n.', 'A Persian measure of length, which, according to\n   Herodotus and Xenophon, was thirty stadia, or somewhat more than three\n   and a half miles. The measure varied in different times and places,\n   and, as now used, is estimated at from three and a half to four English\n   miles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parascenia', 'pl. ', 'of Parascenium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parascenium', 'n.', 'One of two apartments adjoining the stage, probably\n   used as robing rooms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parasceve', 'n.', 'Among the Jews, the evening before the Sabbath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parasceve', 'n.', 'A preparation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paraschematic', 'a.', 'Of or pertaining to a change from the right form, as\n   in the formation of a word from another by a change of termination,\n   gender, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paraselenae', 'pl. ', 'of Paraselene');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paraselene', 'n.', 'A mock moon; an image of the moon which sometimes\n   appears at the point of intersection of two lunar halos. Cf. Parhelion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parasita', 'n. pl.', 'An artificial group formerly made for parasitic\n   insects, as lice, ticks, mites, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parasita', 'n. pl.', 'A division of copepod Crustacea, having a sucking\n   mouth, as the lerneans. They are mostly parasites on fishes. Called\n   also Siphonostomata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parasital', 'a.', 'Of or pertaining to parasites; parasitic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parasite', 'n.', 'One who frequents the tables of the rich, or who lives at\n   anothers expense, and earns his welcome by flattery; a hanger-on; a\n   toady; a sycophant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parasite', 'n.', 'A plant obtaining nourishment immediately from other\n   plants to which it attaches itself, and whose juices it absorbs; --\n   sometimes, but erroneously, called epiphyte.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parasite', 'n.', 'A plant living on or within an animal, and supported at\n   its expense, as many species of fungi of the genus Torrubia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parasite', 'n.', 'An animal which lives during the whole or part of its\n   existence on or in the body of some other animal, feeding upon its\n   food, blood, or tissues, as lice, tapeworms, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parasite', 'n.', 'An animal which steals the food of another, as the\n   parasitic jager.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parasite', 'n.', 'An animal which habitually uses the nest of another, as\n   the cowbird and the European cuckoo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parasitic', 'a.', 'Alt. of Parasitical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parasitical', 'a.', 'Of the nature of a parasite; fawning for food or\n   favors; sycophantic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parasitical', 'a.', 'Of or pertaining to parasites; living on, or deriving\n   nourishment from, some other living animal or plant. See Parasite, 2 &\n   3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parasiticide', 'n.', 'Anything used to destroy parasites.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parasitism', 'n.', 'The state or behavior of a parasite; the act of a\n   parasite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parasitism', 'n.', 'The state of being parasitic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parasol', 'n.', 'A kind of small umbrella used by women as a protection\n   from the sun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parasol', 'v. t.', 'To shade as with a parasol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parasolette', 'n.', 'A small parasol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parasphenoid', 'a.', 'Near the sphenoid bone; -- applied especially to a\n   bone situated immediately beneath the sphenoid in the base of the skull\n   in many animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parasphenoid', 'n.', 'The parasphenoid bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parastichy', 'n.', 'A secondary spiral in phyllotaxy, as one of the evident\n   spirals in a pine cone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parasynaxis', 'n.', 'An unlawful meeting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parasynthetic', 'a.', 'Formed from a compound word.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paratactic', 'a.', 'Of pertaining to, or characterized by, parataxis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parataxis', 'n.', 'The mere ranging of propositions one after another,\n   without indicating their connection or interdependence; -- opposed to\n   syntax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paratheses', 'pl. ', 'of Parathesis');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parathesis', 'n.', 'The placing of two or more nouns in the same case;\n   apposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parathesis', 'n.', 'A parenthetical notice, usually of matter to be\n   afterward expanded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parathesis', 'n.', 'The matter contained within brackets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parathesis', 'n.', 'A commendatory prayer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parathetic', 'a.', 'Of or pertaining to parathesis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paratonnerre', 'n.', 'A conductor of lightning; a lightning rod.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paraunter', 'adv.', 'Peradventure. See Paraventure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parauque', 'n.', 'A bird (Nyctidromus albicollis) ranging from Texas to\n   South America. It is allied to the night hawk and goatsucker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paravail', 'a.', 'At the bottom; lowest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paravant', 'adv.', 'Alt. of Paravant');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paravant', 'adv.', 'In front; publicly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paravant', 'adv.', 'Beforehand; first.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paraventure', 'adv.', 'Peradventure; perchance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paraxanthin', 'n.', 'A crystalline substance closely related to xanthin,\n   present in small quantity in urine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paraxial', 'a.', 'On either side of the axis of the skeleton.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paraxylene', 'n.', 'A hydrocarbon of the aromatic series obtained as a\n   colorless liquid by the distillation of camphor with zinc chloride. It\n   is one of the three metamers of xylene. Cf. Metamer, and Xylene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parboiled', 'imp. & p. p.', 'of Parboil');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parboiling', 'p. pr. & vb. n.', 'of Parboil');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parboil', 'v. t.', 'To boil or cook thoroughly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parboil', 'v. t.', 'To boil in part; to cook partially by boiling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parbreak', 'v. i. & t.', 'To throw out; to vomit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parbreak', 'n.', 'Vomit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parbuckle', 'n.', 'A kind of purchase for hoisting or lowering a\n   cylindrical burden, as a cask. The middle of a long rope is made fast\n   aloft, and both parts are looped around the object, which rests in the\n   loops, and rolls in them as the ends are hauled up or payed out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parbuckle', 'n.', 'A double sling made of a single rope, for slinging a\n   cask, gun, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parbuckled', 'imp. & p. p.', 'of Parbuckle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parbuckling', 'p. pr. & vb. n.', 'of Parbuckle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parbuckle', 'v. t.', 'To hoist or lower by means of a parbuckle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parcae', 'n. pl.', 'The Fates. See Fate, 4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parcase', 'adv.', 'Perchance; by chance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parcel', 'n.', 'A portion of anything taken separately; a fragment of a\n   whole; a part.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parcel', 'n.', 'A part; a portion; a piece; as, a certain piece of land is\n   part and parcel of another piece.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parcel', 'n.', 'An indiscriminate or indefinite number, measure, or\n   quantity; a collection; a group.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parcel', 'n.', 'A number or quantity of things put up together; a bundle; a\n   package; a packet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parceled', 'imp. & p. p.', 'of Parcel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parcelled', '', 'of Parcel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parceling', 'p. pr. & vb. n.', 'of Parcel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parcelling', '', 'of Parcel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parcel', 'v. t.', 'To divide and distribute by parts or portions; -- often\n   with out or into.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parcel', 'v. t.', 'To add a parcel or item to; to itemize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parcel', 'v. t.', 'To make up into a parcel; as, to parcel a customers\n   purchases; the machine parcels yarn, wool, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parcel', 'a. & adv.', 'Part or half; in part; partially. Shak. [Sometimes\n   hyphened with the word following.]');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parceling', 'n.', 'The act of dividing and distributing in portions or\n   parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parceling', 'n.', 'Long, narrow slips of canvas daubed with tar and wound\n   about a rope like a bandage, before it is served; used, also, in\n   mousing on the stayes, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parcel-mele', 'adv.', 'By parcels or parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parcenary', 'n.', 'The holding or occupation of an inheritable estate which\n   descends from the ancestor to two or more persons; coheirship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parcener', 'n.', 'A coheir, or one of two or more persons to whom an estate\n   of inheritance descends jointly, and by whom it is held as one estate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parched', 'imp. & p. p.', 'of Parch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parching', 'p. pr. & vb. n.', 'of Parch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parch', 'v. t.', 'To burn the surface of; to scorch; to roast over the\n   fire, as dry grain; as, to parch the skin; to parch corn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parch', 'v. t.', 'To dry to extremity; to shrivel with heat; as, the mouth\n   is parched from fever.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parch', 'v. i.', 'To become scorched or superficially burnt; to be very\n   dry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parchedness', 'n.', 'The state of being parched.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parchesi', 'n.', 'See Pachisi.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parching', 'a.', 'Scorching; burning; drying.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parchment', 'n.', 'The skin of a lamb, sheep, goat, young calf, or other\n   animal, prepared for writing on. See Vellum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parchment', 'n.', 'The envelope of the coffee grains, inside the pulp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parcity', 'n.', 'Sparingless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parclose', 'n.', 'A screen separating a chapel from the body of the church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pard', 'n.', 'A leopard; a panther.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pardale', 'n.', 'A leopard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parde', 'adv. / interj.', 'Alt. of Pardie');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pardie', 'adv. / interj.', 'Certainly; surely; truly; verily; -- originally\n   an oath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pardine', 'a.', 'Spotted like a pard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pardo', 'n.', 'A money of account in Goa, India, equivalent to about 2s.\n   6d. sterling. or 60 cts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pardon', 'v. t.', 'The act of pardoning; forgiveness, as of an offender, or\n   of an offense; release from penalty; remission of punishment;\n   absolution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pardon', 'v. t.', 'An official warrant of remission of penalty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pardon', 'v. t.', 'The state of being forgiven.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pardon', 'v. t.', 'A release, by a sovereign, or officer having\n   jurisdiction, from the penalties of an offense, being distinguished\n   from amenesty, which is a general obliteration and canceling of a\n   particular line of past offenses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pardoned', 'imp. & p. p.', 'of Pardon');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pardoning', 'p. pr. & vb. n.', 'of Pardon');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pardon', 'v. t.', 'To absolve from the consequences of a fault or the\n   punishment of crime; to free from penalty; -- applied to the offender.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pardon', 'v. t.', 'To remit the penalty of; to suffer to pass without\n   punishment; to forgive; -- applied to offenses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pardon', 'v. t.', 'To refrain from exacting as a penalty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pardon', 'v. t.', 'To give leave (of departure) to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pardonable', 'a.', 'Admitting of pardon; not requiring the excution of\n   penalty; venial; excusable; -- applied to the offense or to the\n   offender; as, a pardonable fault, or culprit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pardonableness', 'n.', 'The quality or state of being pardonable; as, the\n   pardonableness of sin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pardonably', 'adv.', 'In a manner admitting of pardon; excusably.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pardoner', 'n.', 'One who pardons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pardoner', 'n.', 'A seller of indulgences.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pardoning', 'a.', 'Relating to pardon; having or exercising the right to\n   pardon; willing to pardon; merciful; as, the pardoning power; a\n   pardoning God.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pared', 'imp. & p. p.', 'of Pare');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paring', 'p. pr. & vb. n.', 'of Pare');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pare', 'v. t.', 'To cut off, or shave off, the superficial substance or\n   extremities of; as, to pare an apple; to pare a horses hoof.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pare', 'v. t.', 'To remove; to separate; to cut or shave, as the skin,\n   ring, or outside part, from anything; -- followed by off or away; as;\n   to pare off the ring of fruit; to pare away redundancies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pare', 'v. t.', 'Fig.: To diminish the bulk of; to reduce; to lessen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paregoric', 'a.', 'Mitigating; assuaging or soothing pain; as, paregoric\n   elixir.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paregoric', 'n.', 'A medicine that mitigates pain; an anodyne;\n   specifically, camphorated tincture of opium; -- called also paregoric\n   elexir.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parelcon', 'n.', 'The addition of a syllable or particle to the end of a\n   pronoun, verb, or adverb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parelectronomic', 'a.', 'Of or relating to parelectronomy; as, the\n   parelectronomic part of a muscle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parelectronomy', 'n.', 'A condition of the muscles induced by exposure to\n   severe cold, in which the electrical action of the muscle is reversed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parella', 'n.', 'Alt. of Parelle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parelle', 'n.', 'A name for two kinds of dock (Rumex Patientia and R.\n   Hydrolapathum).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parelle', 'n.', 'A kind of lichen (Lecanora parella) once used in dyeing\n   and in the preparation of litmus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parembole', 'n.', 'A kind of parenthesis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parement', 'n.', 'See Parament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paremptosis', 'n.', 'Same as Parembole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parenchyma', 'n.', 'The soft celluar substance of the tissues of plants and\n   animals, like the pulp of leaves, to soft tissue of glands, and the\n   like.');
