  INSERT INTO words (word, word_type, description) VALUES  (    'Parenchymatous', 'a.', 'Alt. of Parenchymous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parenchymous', 'a.', 'Of, pertaining to, or connected with, the parenchyma\n   of a tissue or an organ; as, parenchymatous degeneration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parenesis', 'n.', 'Exhortation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parenetic', 'a.', 'Alt. of Parenetioal');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parenetioal', 'a.', 'Hortatory; encouraging; persuasive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parent', 'n.', 'One who begets, or brings forth, offspring; a father or a\n   mother.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parent', 'n.', 'That which produces; cause; source; author; begetter; as,\n   idleness is the parent of vice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parentage', 'n.', 'Descent from parents or ancestors; parents or ancestors\n   considered with respect to their rank or character; extraction; birth;\n   as, a man of noble parentage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parental', 'a.', 'Of or pertaining to a parent or to parents; as, parental\n   authority; parental obligations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parental', 'a.', 'Becoming to, or characteristic of, parents; tender;\n   affectionate; devoted; as, parental care.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parentally', 'adv.', 'In a parental manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parentation', 'n.', 'Something done or said in honor of the dead;\n   obsequies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parentele', 'n.', 'Kinship; parentage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parentheses', 'pl. ', 'of Parenthesis');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parenthesis', 'n.', 'A word, phrase, or sentence, by way of comment or\n   explanation, inserted in, or attached to, a sentence which would be\n   grammatically complete without it. It is usually inclosed within curved\n   lines (see def. 2 below), or dashes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parenthesis', 'n.', 'One of the curved lines () which inclose a parenthetic\n   word or phrase.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parenthesize', 'v. t.', 'To make a parenthesis of; to include within\n   parenthetical marks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parenthetic', 'a.', 'Alt. of Parenthetical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parenthetical', 'a.', 'Of the nature of a parenthesis; pertaining to, or\n   expressed in, or as in, a parenthesis; as, a parenthetical clause; a\n   parenthetic remark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parenthetical', 'a.', 'Using or containing parentheses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parenthetically', 'adv.', 'In a parenthetical manner; by way of\n   parenthesis; by parentheses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parenthood', 'n.', 'The state of a parent; the office or character of a\n   parent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parentticide', 'n.', 'The act of one who kills ones own parent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parentticide', 'n.', 'One who kills ones own parent; a parricide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parentless', 'a.', 'Deprived of parents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parepididymis', 'n.', 'A small body containing convoluted tubules, situated\n   near the epididymis in man and some other animals, and supposed to be a\n   remnant of the anterior part of the Wolffian body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parer', 'v. t.', 'One who, or that which, pares; an instrument for paring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parergon', 'n.', 'See Parergy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parergy', 'n.', 'Something unimportant, incidental, or superfluous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paresis', 'n.', 'Incomplete paralysis, affecting motion but not sensation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parethmoid', 'a.', 'Near or beside the ethmoid bone or cartilage; --\n   applied especially to a pair of bones in the nasal region of some\n   fishes, and to the ethmoturbinals in some higher animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parethmoid', 'n.', 'A parethmoid bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paretic', 'a.', 'Of or pertaining to paresis; affected with paresis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parfay', 'interj.', 'By my faith; verily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parfit', 'a.', 'Perfect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parfitly', 'adv.', 'Perfectly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parforn', 'v. t.', 'Alt. of Parfourn');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parfourn', 'v. t.', 'To perform.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pargasite', 'n.', 'A dark green aluminous variety of amphibole, or\n   hornblende.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pargeboard', 'n.', 'See Bargeboard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pargeted', 'imp. & p. p.', 'of Parget');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pargeting', 'p. pr. & vb. n.', 'of Parget');
