  INSERT INTO words (word, word_type, description) VALUES  (    'Parget', 'v. i.', 'To lay on plaster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parget', 'v. i.', 'To paint, as the face.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parget', 'n.', 'Gypsum or plaster stone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parget', 'n.', 'Plaster, as for lining the interior of flues, or for\n   stuccowork.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parget', 'n.', 'Paint, especially for the face.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pargeter', 'n.', 'A plasterer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pargeting', 'n.', 'Plasterwork; esp.: (a) A kind of decorative plasterwork\n   in raised ornamental figures, formerly used for the internal and\n   external decoration of houses. (b) In modern architecture, the\n   plastering of the inside of flues, intended to give a smooth surface\n   and help the draught.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pargetory', 'n.', 'Something made of, or covered with, parget, or plaster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parhelic', 'a.', 'Of or pertaining to parhelia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parhelia', 'pl. ', 'of Parhelion');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parhelion', 'n.', 'A mock sun appearing in the form of a bright light,\n   sometimes near the sun, and tinged with colors like the rainbow, and\n   sometimes opposite to the sun. The latter is usually called an\n   anthelion. Often several mock suns appear at the same time. Cf.\n   Paraselene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parhelium', 'n.', 'See Parhelion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pari-', '', 'A combining form signifying equal; as, paridigitate,\n   paripinnate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pariah', 'n.', 'One of an aboriginal people of Southern India, regarded by\n   the four castes of the Hindoos as of very low grade. They are usually\n   the serfs of the Sudra agriculturalists. See Caste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pariah', 'n.', 'An outcast; one despised by society.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parial', 'n.', 'See Pair royal, under Pair, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parian', 'a.', 'Of or pertaining to Paros, an island in the Aegean Sea\n   noted for its excellent statuary marble; as, Parian marble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parian', 'n.', 'A native or inhabitant of Paros.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parian', 'n.', 'A ceramic ware, resembling unglazed porcelain biscuit, of\n   which are made statuettes, ornaments, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paridigitata', 'n. pl.', 'Same as Artiodactyla.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parjdigitate', 'a.', 'Having an evennumber of digits on the hands or the\n   feet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parietes', 'pl. ', 'of Paries');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paries', 'n.', 'The triangular middle part of each segment of the shell of\n   a barnacle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parietal', 'a.', 'Of or pertaining to a wall; hence, pertaining to\n   buildings or the care of them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parietal', 'a.', 'Resident within the walls or buildings of a college.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parietal', 'a.', 'Of pertaining to the parietes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parietal', 'a.', 'Of, pertaining to, or in the region of, the parietal\n   bones, which form the upper and middle part of the cranium, between the\n   frontals and occipitals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parietal', 'a.', 'Attached to the main wall of the ovary, and not to the\n   axis; -- said of a placenta.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parietal', 'n.', 'One of the parietal bones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parietal', 'n.', 'One of the special scales, or plates, covering the back\n   of the head in certain reptiles and fishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parietary', 'a.', 'See Parietal, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parietary', 'n.', 'Any one of several species of Parietaria. See 1st\n   Pellitory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parietes', 'n. pl.', 'The walls of a cavity or an organ; as, the abdominal\n   parietes; the parietes of the cranium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parietes', 'n. pl.', 'The sides of an ovary or of a capsule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parietic', 'a.', 'Pertaining to, or designating, an acid found in the\n   lichen Parmelia parietina, and called also chrysophanic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parietine', 'n.', 'A piece of a fallen wall; a ruin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parieto-', '', 'A combining form used to indicate connection with, or\n   relation to, the parietal bones or the parietal segment of the skull;\n   as, the parieto-mastoid suture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parigenin', 'n.', 'A curdy white substance, obtained by the decomposition\n   of parillin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parillin', 'n.', 'A glucoside resembling saponin, found in the root of\n   sarsaparilla, smilax, etc., and extracted as a bitter white crystalline\n   substance; -- called also smilacin, sarsaparilla saponin, and\n   sarsaparillin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paring', 'v. t.', 'The act of cutting off the surface or extremites of\n   anything.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paring', 'v. t.', 'That which is pared off.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paripinnate', 'a.', 'Pinnate with an equal number of leaflets on each side;\n   having no odd leaflet at the end.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paris', 'n.', 'A plant common in Europe (Paris quadrifolia); herb Paris;\n   truelove. It has been used as a narcotic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paris', 'n.', 'The chief city of France.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parish', 'n.', 'That circuit of ground committed to the charge of one\n   parson or vicar, or other minister having cure of souls therein.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parish', 'n.', 'The same district, constituting a civil jurisdiction, with\n   its own officers and regulations, as respects the poor, taxes, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parish', 'n.', 'An ecclesiastical society, usually not bounded by\n   territorial limits, but composed of those persons who choose to unite\n   under the charge of a particular priest, clergyman, or minister; also,\n   loosely, the territory in which the members of a congregation live.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parish', 'n.', 'In Louisiana, a civil division corresponding to a county in\n   other States.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parish', 'a.', 'Of or pertaining to a parish; parochial; as, a parish\n   church; parish records; a parish priest; maintained by the parish; as,\n   parish poor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parishen', 'n.', 'A parishioner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parishional', 'a.', 'Of or pertaining to a parish; parochial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parishioner', 'n.', 'One who belongs to, or is connected with, a parish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parisian', 'n.', 'A native or inhabitant of Paris, the capital of France.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parisian', 'a.', 'Of or pertaining to Paris.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parisienne', 'n.', 'A female native or resident of Paris.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parisology', 'n.', 'The use of equivocal or ambiguous words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parisyllabic', 'a.', 'Alt. of Parisyllabical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parisyllabical', 'a.', 'Having the same number of syllables in all its\n   inflections.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paritor', 'n.', 'An apparitor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paritory', 'n.', 'Pellitory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parity', 'n.', 'The quality or condition of being equal or equivalent; A\n   like state or degree; equality; close correspondence; analogy; as,\n   parity of reasoning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Park', 'n.', 'A piece of ground inclosed, and stored with beasts of the\n   chase, which a man may have by prescription, or the kings grant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Park', 'n.', 'A tract of ground kept in its natural state, about or\n   adjacent to a residence, as for the preservation of game, for walking,\n   riding, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Park', 'n.', 'A piece of ground, in or near a city or town, inclosed and\n   kept for ornament and recreation; as, Hyde Park in London; Central Park\n   in New York.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Park', 'n.', 'A space occupied by the animals, wagons, pontoons, and\n   materials of all kinds, as ammunition, ordnance stores, hospital\n   stores, provisions, etc., when brought together; also, the objects\n   themselves; as, a park of wagons; a park of artillery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Park', 'n.', 'A partially inclosed basin in which oysters are grown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parked', 'imp. & p. p.', 'of Park');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parking', 'p. pr. & vb. n.', 'of Park');
  INSERT INTO words (word, word_type, description) VALUES  (    'Park', 'v. t.', 'To inclose in a park, or as in a park.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Park', 'v. t.', 'To bring together in a park, or compact body; as, to park\n   the artillery, the wagons, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parker', 'n.', 'The keeper of a park.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parkeria', 'n.', 'A genus of large arenaceous fossil Foraminifera found in\n   the Cretaceous rocks. The species are globular, or nearly so, and are\n   of all sizes up to that of a tennis ball.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parkesine', 'n.', 'A compound, originally made from gun cotton and castor\n   oil, but later from different materials, and used as a substitute for\n   vulcanized India rubber and for ivory; -- called also xylotile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parkleaves', 'n.', 'A European species of Saint Johns-wort; the tutsan.\n   See Tutsan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parlance', 'n.', 'Conversation; discourse; talk; diction; phrase; as, in\n   legal parlance; in common parlance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parlando', 'a. & adv.', 'Alt. of Parlante');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parlante', 'a. & adv.', 'Speaking; in a speaking or declamatory manner; to\n   be sung or played in the style of a recitative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parle', 'v. i.', 'To talk; to converse; to parley.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parle', 'n.', 'Conversation; talk; parley.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parleys', 'pl. ', 'of Parley');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parley', 'n.', 'Mutual discourse or conversation; discussion; hence, an\n   oral conference with an enemy, as with regard to a truce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parleyed', 'imp. & p. p.', 'of Parley');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parleying', 'p. pr. & vb. n.', 'of Parley');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parley', 'v. i.', 'To speak with another; to confer on some point of mutual\n   concern; to discuss orally; hence, specifically, to confer orally with\n   an enemy; to treat with him by words, as on an exchange of prisoners,\n   an armistice, or terms of peace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parliament', 'n.', 'A parleying; a discussion; a conference.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parliament', 'n.', 'A formal conference on public affairs; a general\n   council; esp., an assembly of representatives of a nation or people\n   having authority to make laws.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parliament', 'n.', 'The assembly of the three estates of the United Kingdom\n   of Great Britain and Ireland, viz., the lords spiritual, lords\n   temporal, and the representatives of the commons, sitting in the House\n   of Lords and the House of Commons, constituting the legislature, when\n   summoned by the royal authority to consult on the affairs of the\n   nation, and to enact and repeal laws.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parliament', 'n.', 'In France, before the Revolution of 1789, one of the\n   several principal judicial courts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parliamental', 'a.', 'Parliamentary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parliamentarian', 'a.', 'Of or pertaining to Parliament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parliamentarian', 'n.', 'One who adhered to the Parliament, in opposition\n   to King Charles I.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parliamentarian', 'n.', 'One versed in the rules and usages of Parliament\n   or similar deliberative assemblies; as, an accomplished\n   parliamentarian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parliamentarily', 'adv.', 'In a parliamentary manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parliamentary', 'a.', 'Of or pertaining to Parliament; as, parliamentary\n   authority.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parliamentary', 'a.', 'Enacted or done by Parliament; as, a parliamentary\n   act.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parliamentary', 'a.', 'According to the rules and usages of Parliament or\n   of deliberative bodies; as, a parliamentary motion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parlor', 'n.', 'A room for business or social conversation, for the\n   reception of guests, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parlor', 'n.', 'The apartment in a monastery or nunnery where the inmates\n   are permitted to meet and converse with each other, or with visitors\n   and friends from without.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parlor', 'n.', 'In large private houses, a sitting room for the family and\n   for familiar guests, -- a room for less formal uses than the\n   drawing-room. Esp., in modern times, the dining room of a house having\n   few apartments, as a London house, where the dining parlor is usually\n   on the ground floor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parlor', 'n.', 'Commonly, in the United States, a drawing-room, or the room\n   where visitors are received and entertained.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parlous', 'a.', 'Attended with peril; dangerous; as, a parlous cough.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parlous', 'a.', 'Venturesome; bold; mischievous; keen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parmesan', 'a.', 'Of or pertaining to Parma in Italy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parnassia', 'n.', 'A genus of herbs growing in wet places, and having white\n   flowers; grass of Parnassus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parnassian', 'a.', 'Of or pertaining to Parnassus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parnassian', 'n.', 'Any one of numerous species of butterflies belonging to\n   the genus Parnassius. They inhabit the mountains, both in the Old World\n   and in America.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parnassus', 'n.', 'A mountain in Greece, sacred to Apollo and the Muses,\n   and famous for a temple of Apollo and for the Castalian spring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paroccipital', 'a.', 'Situated near or beside the occipital condyle or the\n   occipital bone; paramastoid; -- applied especially to a process of the\n   skull in some animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parochial', 'a.', 'Of or pertaining to a parish; restricted to a parish;\n   as, parochial duties.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parochialism', 'n.', 'The quality or state of being parochial in form or\n   nature; a system of management peculiar to parishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parochiality', 'n.', 'The state of being parochial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parochialize', 'v. t.', 'To render parochial; to form into parishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parochially', 'adv.', 'In a parochial manner; by the parish, or by\n   parishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parochian', 'a.', 'Parochial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parochian', 'n.', 'A parishioner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parodic', 'a.', 'Alt. of Parodical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parodical', 'a.', 'Having the character of parody.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parodist', 'n.', 'One who writes a parody; one who parodies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parodies', 'pl. ', 'of Parody');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parody', 'n.', 'A writing in which the language or sentiment of an author\n   is mimicked; especially, a kind of literary pleasantry, in which what\n   is written on one subject is altered, and applied to another by way of\n   burlesque; travesty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parody', 'n.', 'A popular maxim, adage, or proverb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parodied', 'imp. & p. p.', 'of Parody');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parodying', 'p. pr. & vb. n.', 'of Parody');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parody', 'v. t.', 'To write a parody upon; to burlesque.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paroket', 'n.', 'See Paroquet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parol', 'n.', 'A word; an oral utterance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parol', 'n.', 'Oral declaration; word of mouth; also, a writing not under\n   seal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parol', 'a.', 'Given or done by word of mouth; oral; also, given by a\n   writing not under seal; as, parol evidence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parole', 'n.', 'A word; an oral utterance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parole', 'n.', 'Word of promise; word of honor; plighted faith; especially\n   (Mil.), promise, upon ones faith and honor, to fulfill stated\n   conditions, as not to bear arms against ones captors, to return to\n   custody, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parole', 'n.', 'A watchword given only to officers of guards; --\n   distinguished from countersign, which is given to all guards.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parole', 'n.', 'Oral declaration. See lst Parol, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parole', 'a.', 'See 2d Parol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paroled', 'imp. & p. p.', 'of Parole');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paroling', 'p. pr. & vb. n.', 'of Parole');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parole', 'v. t.', 'To set at liberty on parole; as, to parole prisoners.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paromology', 'n.', 'A concession to an adversary in order to strengthen\n   ones own argument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paronomasia', 'n.', 'A play upon words; a figure by which the same word is\n   used in different senses, or words similar in sound are set in\n   opposition to each other, so as to give antithetical force to the\n   sentence; punning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paronomastic', 'a.', 'Alt. of Paronomastical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paronomastical', 'a.', 'Of or pertaining to paronomasia; consisting in a\n   play upon words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paronomasy', 'n.', 'Paronomasia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paronychia', 'n.', 'A whitlow, or felon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paronym', 'n.', 'A paronymous word.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paronymous', 'a.', 'Having the same derivation; allied radically;\n   conjugate; -- said of certain words, as man, mankind, manhood, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paronymous', 'a.', 'Having a similar sound, but different orthography and\n   different meaning; -- said of certain words, as al/ and awl; hair and\n   hare, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paronymy', 'n.', 'The quality of being paronymous; also, the use of\n   paronymous words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paroophoron', 'n.', 'A small mass of tubules near the ovary in some\n   animals, and corresponding with the parepididymis of the male.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paroquet', 'n.', 'Same as Parrakeet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parorchis', 'n.', 'The part of the epididymis; or the corresponding part of\n   the excretory duct of the testicle, which is derived from the Wolffian\n   body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parosteal', '', 'Of or pertaining to parostosis; as, parosteal\n   ossification.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parostosis', 'n.', 'Ossification which takes place in purely fibrous\n   tracts; the formation of bone outside of the periosteum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parostotic', 'a.', 'Pertaining to parostosis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parotic', 'a.', 'On the side of the auditory capsule; near the external\n   ear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parotid', 'a.', 'Situated near the ear; -- applied especially to the\n   salivary gland near the ear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parotid', 'a.', 'Of, pertaining to, or in the region of, the parotid gland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parotid', 'n.', 'The parotid gland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parotitis', 'n.', 'Inflammation of the parotid glands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parotoid', 'a.', 'Resembling the parotid gland; -- applied especially to\n   cutaneous glandular elevations above the ear in many toads and frogs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parotoid', 'n.', 'A parotoid gland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parousia', 'n.', 'The nativity of our Lord.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parousia', 'n.', 'The last day.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parovarium', 'n.', 'A group of tubules, a remnant of the Wolffian body,\n   often found near the ovary or oviduct; the epoophoron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paroxysm', 'n.', 'The fit, attack, or exacerbation, of a disease that\n   occurs at intervals, or has decided remissions or intermissions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paroxysm', 'n.', 'Any sudden and violent emotion; spasmodic passion or\n   action; a convulsion; a fit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paroxysmal', 'a.', 'Of the nature of a paroxysm; characterized or\n   accompanied by paroxysms; as, a paroxysmal pain; paroxysmal temper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paroxytone', 'a.', 'A word having an acute accent on the penultimate\n   syllable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parquet', 'n.', 'A body of seats on the floor of a music hall or theater\n   nearest the orchestra; but commonly applied to the whole lower floor of\n   a theater, from the orchestra to the dress circle; the pit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parquet', 'n.', 'Same as Parquetry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parquetage', 'n.', 'See Parquetry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parqueted', 'a.', 'Formed in parquetry; inlaid with wood in small and\n   differently colored figures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parquetry', 'n.', 'A species of joinery or cabinet-work consisting of an\n   inlay of geometric or other patterns, generally of different colors, --\n   used especially for floors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parquette', 'n.', 'See Parquet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parr', 'n.', 'A young salmon in the stage when it has dark transverse\n   bands; -- called also samlet, skegger, and fingerling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parr', 'n.', 'A young leveret.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parrakeet', 'n.', 'Alt. of Parakeet');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parakeet', 'n.', 'Any one of numerous species of small parrots having a\n   graduated tail, which is frequently very long; -- called also paroquet\n   and paraquet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parral', 'n.', 'Alt. of Parrel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parrel', 'n.', 'The rope or collar by which a yard or spar is held to the\n   mast in such a way that it may be hoisted or lowered at pleasure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parrel', 'n.', 'A chimney-piece.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parraqua', 'n.', 'A curassow of the genus Ortalida, allied to the guan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parrhesia', 'n.', 'Boldness or freedom of speech.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parricidal', 'a.', 'Of or pertaining to parricide; guilty of parricide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parricide', 'n.', 'Properly, one who murders ones own father; in a wider\n   sense, one who murders ones father or mother or any ancestor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parricide', 'n.', 'The act or crime of murdering ones own father or any\n   ancestor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parricidious', 'a.', 'Parricidal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parrock', 'n.', 'A croft, or small field; a paddock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parrot', 'n.', 'In a general sense, any bird of the order Psittaci.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parrot', 'n.', 'Any species of Psittacus, Chrysotis, Pionus, and other\n   genera of the family Psittacidae, as distinguished from the parrakeets,\n   macaws, and lories. They have a short rounded or even tail, and often a\n   naked space on the cheeks. The gray parrot, or jako (P. erithacus) of\n   Africa (see Jako), and the species of Amazon, or green, parrots\n   (Chrysotis) of America, are examples. Many species, as cage birds,\n   readily learn to imitate sounds, and to repeat words and phrases.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parrot', 'v. t.', 'To repeat by rote, as a parrot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parrot', 'v. i.', 'To chatter like a parrot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parroter', 'n.', 'One who simply repeats what he has heard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parrotry', 'n.', 'Servile imitation or repetition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parrots-bill', 'n.', 'The glory pea. See under Glory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parried', 'imp. & p. p.', 'of Parry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parrying', 'p. pr. & vb. n.', 'of Parry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parry', 'v. t.', 'To ward off; to stop, or to turn aside; as, to parry a\n   thrust, a blow, or anything that means or threatens harm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parry', 'v. t.', 'To avoid; to shift or put off; to evade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parry', 'v. i.', 'To ward off, evade, or turn aside something, as a blow,\n   argument, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parries', 'pl. ', 'of Parry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parry', 'n.', 'A warding off of a thrust or blow, as in sword and bayonet\n   exercises or in boxing; hence, figuratively, a defensive movement in\n   debate or other intellectual encounter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parsed', 'imp. & p. p.', 'of Parse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parsing', 'p. pr. & vb. n.', 'of Parse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parse', 'n.', 'To resolve into its elements, as a sentence, pointing out\n   the several parts of speech, and their relation to each other by\n   government or agreement; to analyze and describe grammatically.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parsee', 'n.', 'One of the adherents of the Zoroastrian or ancient Persian\n   religion, descended from Persian refugees settled in India; a fire\n   worshiper; a Gheber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parsee', 'n.', 'The Iranian dialect of much of the religious literature of\n   the Parsees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parseeism', 'n.', 'The religion and customs of the Parsees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parser', 'n.', 'One who parses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parsimonious', 'a.', 'Exhibiting parsimony; sparing in expenditure of\n   money; frugal to excess; penurious; niggardly; stingy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parsimony', 'n.', 'Closeness or sparingness in the expenditure of money; --\n   generally in a bad sense; excessive frugality; niggardliness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parsley', 'n.', 'An aromatic umbelliferous herb (Carum Petroselinum),\n   having finely divided leaves which are used in cookery and as a\n   garnish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parsnip', 'n.', 'The aromatic and edible spindle-shaped root of the\n   cultivated form of the Pastinaca sativa, a biennial umbelliferous plant\n   which is very poisonous in its wild state; also, the plant itself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parson', 'n.', 'A person who represents a parish in its ecclesiastical and\n   corporate capacities; hence, the rector or incumbent of a parochial\n   church, who has full possession of all the rights thereof, with the\n   cure of souls.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parson', 'n.', 'Any clergyman having ecclesiastical preferment; one who is\n   in orders, or is licensed to preach; a preacher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parsonage', 'n.', 'A certain portion of lands, tithes, and offerings, for\n   the maintenance of the parson of a parish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parsonage', 'n.', 'The glebe and house, or the house only, owned by a\n   parish or ecclesiastical society, and appropriated to the maintenance\n   or use of the incumbent or settled pastor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parsonage', 'n.', 'Money paid for the support of a parson.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parsoned', 'a.', 'Furnished with a parson.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parsonic', 'a.', 'Alt. of Parsonical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parsonical', 'a.', 'Of or pertaining to a parson; clerical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parsonish', 'a.', 'Appropriate to, or like, a parson; -- used in\n   disparagement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Part', 'n.', 'One of the portions, equal or unequal, into which anything is\n   divided, or regarded as divided; something less than a whole; a number,\n   quantity, mass, or the like, regarded as going to make up, with others,\n   a larger number, quantity, mass, etc., whether actually separate or\n   not; a piece; a fragment; a fraction; a division; a member; a\n   constituent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Part', 'n.', 'An equal constituent portion; one of several or many like\n   quantities, numbers, etc., into which anything is divided, or of which\n   it is composed; proportional division or ingredient.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Part', 'n.', 'A constituent portion of a living or spiritual whole; a\n   member; an organ; an essential element.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Part', 'n.', 'A constituent of character or capacity; quality; faculty;\n   talent; -- usually in the plural with a collective sense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Part', 'n.', 'Quarter; region; district; -- usually in the plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Part', 'n.', 'Such portion of any quantity, as when taken a certain number\n   of times, will exactly make that quantity; as, 3 is a part of 12; --\n   the opposite of multiple. Also, a line or other element of a\n   geometrical figure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Part', 'n.', 'That which belongs to one, or which is assumed by one, or\n   which falls to one, in a division or apportionment; share; portion;\n   lot; interest; concern; duty; office.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Part', 'n.', 'One of the opposing parties or sides in a conflict or a\n   controversy; a faction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Part', 'n.', 'A particular character in a drama or a play; an assumed\n   personification; also, the language, actions, and influence of a\n   character or an actor in a play; or, figuratively, in real life. See To\n   act a part, under Act.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Part', 'n.', 'One of the different melodies of a concerted composition,\n   which heard in union compose its harmony; also, the music for each\n   voice or instrument; as, the treble, tenor, or bass part; the violin\n   part, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parted', 'imp. & p. p.', 'of Part');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parting', 'p. pr. & vb. n.', 'of Part');
  INSERT INTO words (word, word_type, description) VALUES  (    'Part', 'n.', 'To divide; to separate into distinct parts; to break into two\n   or more parts or pieces; to sever.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Part', 'n.', 'To divide into shares; to divide and distribute; to allot; to\n   apportion; to share.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Part', 'n.', 'To separate or disunite; to cause to go apart; to remove from\n   contact or contiguity; to sunder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Part', 'n.', 'Hence: To hold apart; to stand between; to intervene betwixt,\n   as combatants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Part', 'n.', 'To separate by a process of extraction, elimination, or\n   secretion; as, to part gold from silver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Part', 'n.', 'To leave; to quit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Part', 'v. i.', 'To be broken or divided into parts or pieces; to break; to\n   become separated; to go asunder; as, rope parts; his hair parts in the\n   middle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Part', 'v. i.', 'To go away; to depart; to take leave; to quit each other;\n   hence, to die; -- often with from.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Part', 'v. i.', 'To perform an act of parting; to relinquish a connection\n   of any kind; -- followed by with or from.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Part', 'v. i.', 'To have a part or share; to partake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Part', 'adv.', 'Partly; in a measure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Partable', 'a.', 'See Partible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Partage', 'n.', 'Division; the act of dividing or sharing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Partage', 'n.', 'Part; portion; share.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Partook', 'imp.', 'of Partake');
  INSERT INTO words (word, word_type, description) VALUES  (    'Partaken', 'p. p.', 'of Partake');
  INSERT INTO words (word, word_type, description) VALUES  (    'Partaking', 'p. pr. & vb. n.', 'of Partake');
  INSERT INTO words (word, word_type, description) VALUES  (    'Partake', 'v. i.', 'To take a part, portion, lot, or share, in common with\n   others; to have a share or part; to participate; to share; as, to\n   partake of a feast with others.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Partake', 'v. i.', 'To have something of the properties, character, or\n   office; -- usually followed by of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Partake', 'v. t.', 'To partake of; to have a part or share in; to share.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Partake', 'v. t.', 'To admit to a share; to cause to participate; to give a\n   part to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Partake', 'v. t.', 'To distribute; to communicate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Partaker', 'n.', 'One who partakes; a sharer; a participator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Partaker', 'n.', 'An accomplice; an associate; a partner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Partan', 'n.', 'An edible British crab.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parted', 'a.', 'Separated; devided.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parted', 'a.', 'Endowed with parts or abilities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parted', 'a.', 'Cleft so that the divisions reach nearly, but not quite, to\n   the midrib, or the base of the blade; -- said of a leaf, and used\n   chiefly in composition; as, three-parted, five-parted, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parter', 'n.', 'One who, or which, parts or separates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parterre', 'n.', 'An ornamental and diversified arrangement of beds or\n   plots, in which flowers are cultivated, with intervening spaces of\n   gravel or turf for walking on.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parterre', 'n.', 'The pit of a theater; the parquet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Partheniad', 'n.', 'A poem in honor of a virgin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parthenic', 'a.', 'Of or pertaining to the Spartan Partheniae, or sons of\n   unmarried women.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parthenogenesis', 'n.', 'The production of new individuals from virgin\n   females by means of ova which have the power of developing without the\n   intervention of the male element; the production, without\n   fertilization, of cells capable of germination. It is one of the\n   phenomena of alternate generation. Cf. Heterogamy, and Metagenesis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parthenogenesis', 'n.', 'The production of seed without fertilization,\n   believed to occur through the nonsexual formation of an embryo\n   extraneous to the embrionic vesicle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parthenogenetic', 'a.', 'Of, pertaining to, or produced by,\n   parthenogenesis; as, parthenogenetic forms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parthenogenitive', 'a.', 'Parthenogenetic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parthenogeny', 'n.', 'Same as Parthenogenesis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parthenon', 'n.', 'A celebrated marble temple of Athene, on the Acropolis\n   at Athens. It was of the pure Doric order, and has had an important\n   influence on art.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Partenope', 'n.', 'One of the Sirens, who threw herself into the sea, in\n   despair at not being able to beguile Ulysses by her songs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Partenope', 'n.', 'One of the asteroids between Mars and Jupiter,\n   descovered by M. de Gasparis in 1850.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parthian', 'a.', 'Of or pertaining to ancient Parthia, in Asia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parthian', 'n.', 'A native Parthia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Partial', 'n.', 'Of, pertaining to, or affecting, a part only; not general\n   or universal; not total or entire; as, a partial eclipse of the moon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Partial', 'n.', 'Inclined to favor one party in a cause, or one side of a\n   question, more then the other; baised; not indifferent; as, a judge\n   should not be partial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Partial', 'n.', 'Having a predelection for; inclined to favor unreasonably;\n   foolishly fond.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Partial', 'n.', 'Pertaining to a subordinate portion; as, a compound umbel\n   is made up of a several partial umbels; a leaflet is often supported by\n   a partial petiole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Partialism', 'n.', 'Partiality; specifically (Theol.), the doctrine of the\n   Partialists.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Partialist', 'n.', 'One who is partial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Partialist', 'n.', 'One who holds that the atonement was made only for a\n   part of mankind, that is, for the elect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Partiality', 'n.', 'The quality or state of being partial; inclination to\n   favor one party, or one side of a question, more than the other; undue\n   bias of mind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Partiality', 'n.', 'A predilection or inclination to one thing rather than\n   to others; special taste or liking; as, a partiality for poetry or\n   painting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Partialize', 'v. t. & i.', 'To make or be partial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Partially', 'adv.', 'In part; not totally; as, partially true; the sun\n   partially eclipsed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Partially', 'adv.', 'In a partial manner; with undue bias of mind; with\n   unjust favor or dislike; as, to judge partially.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Partibility', 'n.', 'The quality or state of being partible; divisibility;\n   separability; as, the partibility of an inherttance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Partible', 'a.', 'Admitting of being parted; divisible; separable;\n   susceptible of severance or partition; as, an estate of inheritance may\n   be partible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Participable', 'a.', 'Capable of being participated or shared.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Participant', 'a.', 'Sharing; participating; having a share of part.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Participant', 'n.', 'A participator; a partaker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Participantly', 'adv.', 'In a participant manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Participate', 'a.', 'Acting in common; participating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Participated', 'imp. & p. p.', 'of Participate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Participating', 'p. pr. & vb. n.', 'of Participate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Participate', 'v. i.', 'To have a share in common with others; to take a\n   part; to partake; -- followed by in, formely by of; as, to participate\n   in a debate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Participate', 'v. t.', 'To partake of; to share in; to receive a part of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Participate', 'v. t.', 'To impart, or give, or share of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Participation', 'n.', 'The act or state of participating, or sharing in\n   common with others; as, a participation in joy or sorrows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Participation', 'n.', 'Distribution; division into shares.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Participation', 'n.', 'community; fellowship; association.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Participative', 'a.', 'Capable of participating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Participator', 'n.', 'One who participates, or shares with another; a\n   partaker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Participial', 'a.', 'Having, or partaking of, the nature and use of a\n   participle; formed from a participle; as, a participial noun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Participial', 'n.', 'A participial word.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Participialized', 'imp. & p. p.', 'of Participialize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Participializing', 'p. pr. & vb. n.', 'of Participialize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Participialize', 'v. t.', 'To form into, or put in the form of, a\n   participle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Participially', 'adv.', 'In the sense or manner of a participle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Participle', 'n.', 'A part of speech partaking of the nature both verb and\n   adjective; a form of a verb, or verbal adjective, modifying a noun, but\n   taking the adjuncts of the verb from which it is derived. In the\n   sentences: a letter is written; being asleep he did not hear; exhausted\n   by toil he will sleep soundly, -- written, being, and exhaustedare\n   participles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Participle', 'a.', 'Anything that partakes of the nature of different\n   things.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Particle', 'n.', 'A minute part or portion of matter; a morsel; a little\n   bit; an atom; a jot; as, a particle of sand, of wood, of dust.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Particle', 'n.', 'Any very small portion or part; the smallest portion; as,\n   he has not a particle of patriotism or virtue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Particle', 'n.', 'A crumb or little piece of concecrated host.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Particle', 'n.', 'The smaller hosts distributed in the communion of the\n   laity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Particle', 'n.', 'A subordinate word that is never inflected (a\n   preposition, conjunction, interjection); or a word that can not be used\n   except in compositions; as, ward in backward, ly in lovely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Particolored', 'a.', 'Same as Party-colored.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Particular', 'a.', 'Relating to a part or portion of anything; concerning a\n   part separated from the whole or from others of the class; separate;\n   sole; single; individual; specific; as, the particular stars of a\n   constellation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Particular', 'a.', 'Of or pertaining to a single person, class, or thing;\n   belonging to one only; not general; not common; hence, personal;\n   peculiar; singular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Particular', 'a.', 'Separate or distinct by reason of superiority;\n   distinguished; important; noteworthy; unusual; special; as, he brought\n   no particular news; she was the particular belle of the party.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Particular', 'a.', 'Concerned with, or attentive to, details; minute;\n   circumstantial; precise; as, a full and particular account of an\n   accident; hence, nice; fastidious; as, a man particular in his dress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Particular', 'a.', 'Containing a part only; limited; as, a particular\n   estate, or one precedent to an estate in remainder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Particular', 'a.', 'Holding a particular estate; as, a particular tenant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Particular', 'a.', 'Forming a part of a genus; relatively limited in\n   extension; affirmed or denied of a part of a subject; as, a particular\n   proposition; -- opposed to universal: e. g. (particular affirmative)\n   Some men are wise; (particular negative) Some men are not wise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Particular', 'n.', 'A separate or distinct member of a class, or part of a\n   whole; an individual fact, point, circumstance, detail, or item, which\n   may be considered separately; as, the particulars of a story.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Particular', 'n.', 'Special or personal peculiarity, trait, or character;\n   individuality; interest, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Particular', 'n.', 'One of the details or items of grounds of claim; --\n   usually in the pl.; also, a bill of particulars; a minute account; as,\n   a particular of premises.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Particularism', 'n.', 'A minute description; a detailed statement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Particularism', 'n.', 'The doctrine of particular election.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Particularism', 'n.', 'Devotion to the interests of ones own kingdom or\n   province rather than to those of the empire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Particularist', 'n.', 'One who holds to particularism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Particularities', 'pl. ', 'of Particularity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Particularity', 'n.', 'The state or quality of being particular;\n   distinctiveness; circumstantiality; minuteness in detail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Particularity', 'n.', 'That which is particular');
  INSERT INTO words (word, word_type, description) VALUES  (    'Particularity', 'n.', 'Peculiar quality; individual characteristic;\n   peculiarity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Particularity', 'n.', 'Special circumstance; minute detail; particular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Particularity', 'n.', 'Something of special or private concern or interest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Particularization', 'n.', 'The act of particularizing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Particularized', 'imp. & p. p.', 'of Particularize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Particularizing', 'p. pr. & vb. n.', 'of Particularize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Particularize', 'v. t.', 'To give as a particular, or as the particulars;\n   to mention particularly; to give the particulars of; to enumerate or\n   specify in detail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Particularize', 'v. i.', 'To mention or attend to particulars; to give\n   minute details; to be circumstantial; as, to particularize in a\n   narrative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Particularly', 'adv.', 'In a particular manner; expressly; with a specific\n   reference or interest; in particular; distinctly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Particularly', 'adv.', 'In an especial manner; in a high degree; as, a\n   particularly fortunate man; a particularly bad failure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Particularment', 'n.', 'A particular; a detail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Particulate', 'v. t. & i.', 'To particularize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Particulate', 'a.', 'Having the form of a particle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Particulate', 'a.', 'Referring to, or produced by, particles, such as dust,\n   minute germs, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parting', 'v.', 'Serving to part; dividing; separating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parting', 'v.', 'Given when departing; as, a parting shot; a parting\n   salute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parting', 'v.', 'Departing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parting', 'v.', 'Admitting of being parted; partible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parting', 'n.', 'The act of parting or dividing; the state of being parted;\n   division; separation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parting', 'n.', 'A separation; a leave-taking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parting', 'n.', 'A surface or line of separation where a division occurs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parting', 'n.', 'The surface of the sand of one section of a mold where it\n   meets that of another section.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parting', 'n.', 'The separation and determination of alloys; esp., the\n   separation, as by acids, of gold from silver in the assay button.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parting', 'n.', 'A joint or fissure, as in a coal seam.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parting', 'n.', 'The breaking, as of a cable, by violence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parting', 'n.', 'Lamellar separation in a crystallized mineral, due to some\n   other cause than cleavage, as to the presence of twinning lamellae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Partisan', 'n.', 'An adherent to a party or faction; esp., one who is\n   strongly and passionately devoted to a party or an interest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Partisan', 'n.', 'The commander of a body of detached light troops engaged\n   in making forays and harassing an enemy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Partisan', 'n.', 'Any member of such a corps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Partisan', 'a.', 'Adherent to a party or faction; especially, having the\n   character of blind, passionate, or unreasonable adherence to a party;\n   as, blinded by partisan zeal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Partisan', 'a.', 'Serving as a partisan in a detached command; as, a\n   partisan officer or corps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Partisan', 'n.', 'A kind of halberd or pike; also, a truncheon; a staff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Partisanship', 'n.', 'The state of being a partisan, or adherent to a\n   party; feelings or conduct appropriate to a partisan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Partita', 'n.', 'A suite; a set of variations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Partite', 'a.', 'Divided nearly to the base; as, a partite leaf is a simple\n   separated down nearly to the base.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Partition', 'v.', 'The act of parting or dividing; the state of being\n   parted; separation; division; distribution; as, the partition of a\n   kingdom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Partition', 'v.', 'That which divides or separates; that by which different\n   things, or distinct parts of the same thing, are separated; separating\n   boundary; dividing line or space; specifically, an interior wall\n   dividing one part or apartment of a house, an inclosure, or the like,\n   from another; as, a brick partition; lath and plaster partitions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Partition', 'v.', 'A part divided off by walls; an apartment; a\n   compartment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Partition', 'v.', 'The servance of common or undivided interests,\n   particularly in real estate. It may be effected by consent of parties,\n   or by compulsion of law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Partition', 'v.', 'A score.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Partitioned', 'imp. & p. p.', 'of Partition');
  INSERT INTO words (word, word_type, description) VALUES  (    'Partitioning', 'p. pr. & vb. n.', 'of Partition');
  INSERT INTO words (word, word_type, description) VALUES  (    'Partition', 'v. t.', 'To divide into parts or shares; to divide and\n   distribute; as, to partition an estate among various heirs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Partition', 'v. t.', 'To divide into distinct parts by lines, walls, etc.;\n   as, to partition a house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Partitionment', 'n.', 'The act of partitioning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Partitive', 'a.', 'Denoting a part; as, a partitive genitive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Partitive', 'n.', 'A word expressing partition, or denoting a part.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Partitively', 'adv.', 'In a partitive manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Partlet', 'n.', 'A covering for the neck, and sometimes for the shoulders\n   and breast; originally worn by both sexes, but laterby women alone; a\n   ruff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Partlet', 'n.', 'A hen; -- so called from the ruffing of her neck feathers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Partly', 'adv.', 'In part; in some measure of degree; not wholly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Partner', 'n.', 'One who has a part in anything with an other; a partaker;\n   an associate; a sharer. \"Partner of his fortune.\" Shak. Hence: (a) A\n   husband or a wife. (b) Either one of a couple who dance together. (c)\n   One who shares as a member of a partnership in the management, or in\n   the gains and losses, of a business.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Partner', 'n.', 'An associate in any business or occupation; a member of a\n   partnership. See Partnership.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Partner', 'n.', 'A framework of heavy timber surrounding an opening in a\n   deck, to strengthen it for the support of a mast, pump, capstan, or the\n   like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Partner', 'v. t.', 'To associate, to join.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Partnership', 'n.', 'The state or condition of being a partner; as, to be\n   in partnership with another; to have partnership in the fortunes of a\n   family or a state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Partnership', 'n.', 'A division or sharing among partners; joint possession\n   or interest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Partnership', 'n.', 'An alliance or association of persons for the\n   prosecution of an undertaking or a business on joint account; a\n   company; a firm; a house; as, to form a partnership.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Partnership', 'n.', 'A contract between two or more competent persons for\n   joining together their money, goods, labor, and skill, or any or all of\n   them, under an understanding that there shall be a communion of profit\n   between them, and for the purpose of carrying on a legal trade,\n   business, or adventure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Partnership', 'n.', 'See Fellowship, n., 6.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Partook', '', 'imp. of Partake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Partridge', 'n.', 'Any one of numerous species of small gallinaceous birds\n   of the genus Perdix and several related genera of the family\n   Perdicidae, of the Old World. The partridge is noted as a game bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Partridge', 'n.', 'Any one of several species of quail-like birds belonging\n   to Colinus, and allied genera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Partridge', 'n.', 'The ruffed grouse (Bonasa umbellus).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parture', 'n.', 'Departure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parturiate', 'v. i.', 'To bring forth young.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parturiency', 'n.', 'Parturition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parturient', 'a.', 'Bringing forth, or about to bring forth, young;\n   fruitful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parturifacient', 'n.', 'A medicine tending to cause parturition, or to give\n   relief in childbearing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parturious', 'a.', 'Parturient.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parturition', 'n.', 'The act of bringing forth, or being delivered of,\n   young; the act of giving birth; delivery; childbirth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parturition', 'n.', 'That which is brought forth; a birth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parturitive', 'a.', 'Pertaining to parturition; obstetric.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parties', 'pl. ', 'of Party');
  INSERT INTO words (word, word_type, description) VALUES  (    'Party', 'v.', 'A part or portion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Party', 'v.', 'A number of persons united in opinion or action, as\n   distinguished from, or opposed to, the rest of a community or\n   association; esp., one of the parts into which a people is divided on\n   questions of public policy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Party', 'v.', 'A part of a larger body of company; a detachment; especially\n   (Mil.), a small body of troops dispatched on special service.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Party', 'v.', 'A number of persons invited to a social entertainment; a\n   select company; as, a dinner party; also, the entertainment itself; as,\n   to give a party.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Party', 'v.', 'One concerned or interested in an affair; one who takes part\n   with others; a participator; as, he was a party to the plot; a party to\n   the contract.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Party', 'v.', 'The plaintiff or the defendant in a lawsuit, whether an\n   individual, a firm, or corporation; a litigant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Party', 'v.', 'Hence, any certain person who is regarded as being opposed\n   or antagonistic to another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Party', 'v.', 'Cause; side; interest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Party', 'v.', 'A person; as, he is a queer party.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Party', 'v.', 'Parted or divided, as in the direction or form of one of the\n   ordinaries; as, an escutcheon party per pale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Party', 'v.', 'Partial; favoring one party.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Party', 'adv.', 'Partly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Party-coated', 'a.', 'Having a motley coat, or coat of divers colors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Party-colored', 'a.', 'Alt. of Parti-colored');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parti-colored', 'a.', 'Colored with different tints; variegated; as, a\n   party-colored flower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Partyism', 'n.', 'Devotion to party.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parumbilical', 'a.', 'Near the umbilicus; -- applied especially to one or\n   more small veins which, in man, connect the portal vein with the\n   epigastric veins in the front wall of the abdomen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parusia', 'n.', 'A figure of speech by which the present tense is used\n   instead of the past or the future, as in the animated narration of\n   past, or in the prediction of future, events.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parvanimity', 'n.', 'The state or quality of having a little or ignoble\n   mind; pettiness; meanness; -- opposed to magnanimity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parvenu', 'n.', 'An upstart; a man newly risen into notice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parvis', 'n.', 'Alt. of Parvise');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parvise', 'n.', 'a court of entrance to, or an inclosed space before, a\n   church; hence, a church porch; -- sometimes formerly used as place of\n   meeting, as for lawyers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parvitude', 'n.', 'Alt. of Parvity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parvity', 'n.', 'Littleness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parvolin', 'n.', 'A nonoxygenous ptomaine, formed in the putrefaction of\n   albuminous matters, especially of horseflesh and mackerel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Parvoline', 'n.', 'A liquid base, C/H/N, of the pyridine group, found in\n   coal tar; also, any one of the series of isometric substances of which\n   it is the type.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pas', 'n.', 'A pace; a step, as in a dance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pas', 'n.', 'Right of going foremost; precedence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pasan', 'n.', 'The gemsbok.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pasch', 'n.', 'Alt. of Pascha');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pascha', 'n.', 'The passover; the feast of Easter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paschal', 'a.', 'Of or pertaining to the passover, or to Easter; as, a\n   paschal lamb; paschal eggs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paseng', 'n.', 'The wild or bezoar goat. See Goat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pash', 'v. t.', 'To strike; to crush; to smash; to dash in pieces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pash', 'v. t.', 'The head; the poll.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pash', 'v. t.', 'A crushing blow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pash', 'v. t.', 'A heavy fall of rain or snow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pasha', 'n.', 'An honorary title given to officers of high rank in Turkey,\n   as to governers of provinces, military commanders, etc. The earlier\n   form was bashaw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pashalic', 'n.', 'The jurisdiction of a pasha.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pashaw', 'n.', 'See Pasha.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pasigraphic', 'a.', 'Alt. of Pasigraphical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pasigraphical', 'a.', 'Of or pertaining to pasigraphy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pasigraphy', 'n.', 'A system of universal writing, or a manner of writing\n   that may be understood and used by all nations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pasilaly', 'n.', 'A form of speech adapted to be used by all mankind;\n   universal language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pask', 'n.', 'See Pasch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paspy', 'n.', 'A kind of minuet, in triple time, of French origin, popular\n   in the reign of Queen Elizabeth and for some time after; -- called also\n   passing measure, and passymeasure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pasque', 'n.', 'See Pasch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pasquil', 'n.', 'See Pasquin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pasquil', 'v. t.', 'See Pasquin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pasquilant', 'n.', 'A lampooner; a pasquiler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pasquiler', 'n.', 'A lampooner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pasquin', 'n.', 'A lampooner; also, a lampoon. See Pasquinade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pasquin', 'v. t.', 'To lampoon; to satiraze.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pasquinade', 'n.', 'A lampoon or satirical writing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pasquinade', 'v. t.', 'To lampoon, to satirize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passed', 'imp. & p. p.', 'of Pass');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passing', 'p. pr. & vb. n.', 'of Pass');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pass', 'v. i.', 'To go; to move; to proceed; to be moved or transferred\n   from one point to another; to make a transit; -- usually with a\n   following adverb or adverbal phrase defining the kind or manner of\n   motion; as, to pass on, by, out, in, etc.; to pass swiftly, directly,\n   smoothly, etc.; to pass to the rear, under the yoke, over the bridge,\n   across the field, beyond the border, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pass', 'v. i.', 'To move or be transferred from one state or condition to\n   another; to change possession, condition, or circumstances; to undergo\n   transition; as, the business has passed into other hands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pass', 'v. i.', 'To move beyond the range of the senses or of knowledge; to\n   pass away; hence, to disappear; to vanish; to depart; specifically, to\n   depart from life; to die.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pass', 'v. i.', 'To move or to come into being or under notice; to come and\n   go in consciousness; hence, to take place; to occur; to happen; to\n   come; to occur progressively or in succession; to be present\n   transitorily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pass', 'v. i.', 'To go by or glide by, as time; to elapse; to be spent; as,\n   their vacation passed pleasantly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pass', 'v. i.', 'To go from one person to another; hence, to be given and\n   taken freely; as, clipped coin will not pass; to obtain general\n   acceptance; to be held or regarded; to circulate; to be current; --\n   followed by for before a word denoting value or estimation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pass', 'v. i.', 'To advance through all the steps or stages necessary to\n   validity or effectiveness; to be carried through a body that has power\n   to sanction or reject; to receive legislative sanction; to be enacted;\n   as, the resolution passed; the bill passed both houses of Congress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pass', 'v. i.', 'To go through any inspection or test successfully; to be\n   approved or accepted; as, he attempted the examination, but did not\n   expect to pass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pass', 'v. i.', 'To be suffered to go on; to be tolerated; hence, to\n   continue; to live along.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pass', 'v. i.', 'To go unheeded or neglected; to proceed without hindrance\n   or opposition; as, we let this act pass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pass', 'v. i.', 'To go beyond bounds; to surpass; to be in excess.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pass', 'v. i.', 'To take heed; to care.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pass', 'v. i.', 'To go through the intestines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pass', 'v. i.', 'To be conveyed or transferred by will, deed, or other\n   instrument of conveyance; as, an estate passes by a certain clause in a\n   deed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pass', 'v. i.', 'To make a lunge or pass; to thrust.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pass', 'v. i.', 'To decline to take an optional action when it is ones\n   turn, as to decline to bid, or to bet, or to play a card; in euchre, to\n   decline to make the trump.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pass', 'v. i.', 'In football, hockey, etc., to make a pass; to transfer the\n   ball, etc., to another player of ones own side.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pass', 'v. t.', 'To go by, beyond, over, through, or the like; to proceed\n   from one side to the other of; as, to pass a house, a stream, a\n   boundary, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pass', 'v. t.', 'To go from one limit to the other of; to spend; to live\n   through; to have experience of; to undergo; to suffer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pass', 'v. t.', 'To go by without noticing; to omit attention to; to take\n   no note of; to disregard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pass', 'v. t.', 'To transcend; to surpass; to excel; to exceed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pass', 'v. t.', 'To go successfully through, as an examination, trail,\n   test, etc.; to obtain the formal sanction of, as a legislative body;\n   as, he passed his examination; the bill passed the senate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pass', 'v. t.', 'To cause to move or go; to send; to transfer from one\n   person, place, or condition to another; to transmit; to deliver; to\n   hand; to make over; as, the waiter passed bisquit and cheese; the torch\n   was passed from hand to hand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pass', 'v. t.', 'To cause to pass the lips; to utter; to pronounce; hence,\n   to promise; to pledge; as, to pass sentence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pass', 'v. t.', 'To cause to advance by stages of progress; to carry on\n   with success through an ordeal, examination, or action; specifically,\n   to give legal or official sanction to; to ratify; to enact; to approve\n   as valid and just; as, he passed the bill through the committee; the\n   senate passed the law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pass', 'v. t.', 'To put in circulation; to give currency to; as, to pass\n   counterfeit money.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pass', 'v. t.', 'To cause to obtain entrance, admission, or conveyance; as,\n   to pass a person into a theater, or over a railroad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pass', 'v. t.', 'To emit from the bowels; to evacuate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pass', 'v. t.', 'To take a turn with (a line, gasket, etc.), as around a\n   sail in furling, and make secure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pass', 'v. t.', 'To make, as a thrust, punto, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pass', 'v. i.', 'An opening, road, or track, available for passing;\n   especially, one through or over some dangerous or otherwise\n   impracticable barrier; a passageway; a defile; a ford; as, a mountain\n   pass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pass', 'v. i.', 'A thrust or push; an attempt to stab or strike an\n   adversary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pass', 'v. i.', 'A movement of the hand over or along anything; the\n   manipulation of a mesmerist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pass', 'v. i.', 'A single passage of a bar, rail, sheet, etc., between the\n   rolls.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pass', 'v. i.', 'State of things; condition; predicament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pass', 'v. i.', 'Permission or license to pass, or to go and come; a\n   psssport; a ticket permitting free transit or admission; as, a railroad\n   or theater pass; a military pass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pass', 'v. i.', 'Fig.: a thrust; a sally of wit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pass', 'v. i.', 'Estimation; character.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pass', 'v. i.', 'A part; a division.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passable', 'a.', 'Capable of being passed, traveled, navigated, traversed,\n   penetrated, or the like; as, the roads are not passable; the stream is\n   passablein boats.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passable', 'a.', 'Capable of being freely circulated or disseminated;\n   acceptable; generally receivable; current.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passable', 'a.', 'Such as may be allowed to pass without serious objection;\n   tolerable; admissable; moderate; mediocre.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passableness', 'n.', 'The quality of being passable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passably', 'adv.', 'Tolerably; moderately.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passacaglia', 'n.', 'Alt. of Passacaglio');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passacaglio', 'n.', 'An old Italian or Spanish dance tune, in slow\n   three-four measure, with divisions on a ground bass, resembling a\n   chaconne.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passade', 'v. i.', 'Alt. of Passado');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passado', 'v. i.', 'A pass or thrust.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passado', 'v. i.', 'A turn or course of a horse backward or forward on the\n   same spot of ground.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passage', 'v. i.', 'The act of passing; transit from one place to another;\n   movement from point to point; a going by, over, across, or through; as,\n   the passage of a man or a carriage; the passage of a ship or a bird;\n   the passage of light; the passage of fluids through the pores or\n   channels of the body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passage', 'v. i.', 'Transit by means of conveyance; journey, as by water,\n   carriage, car, or the like; travel; right, liberty, or means, of\n   passing; conveyance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passage', 'v. i.', 'Price paid for the liberty to pass; fare; as, to pay\n   ones passage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passage', 'v. i.', 'Removal from life; decease; departure; death.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passage', 'v. i.', 'Way; road; path; channel or course through or by which\n   one passes; way of exit or entrance; way of access or transit. Hence, a\n   common avenue to various apartments in a building; a hall; a corridor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passage', 'v. i.', 'A continuous course, process, or progress; a connected\n   or continuous series; as, the passage of time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passage', 'v. i.', 'A separate part of a course, process, or series; an\n   occurrence; an incident; an act or deed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passage', 'v. i.', 'A particular portion constituting a part of something\n   continuous; esp., a portion of a book, speech, or musical composition;\n   a paragraph; a clause.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passage', 'v. i.', 'Reception; currency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passage', 'v. i.', 'A pass or en encounter; as, a passage at arms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passage', 'v. i.', 'A movement or an evacuation of the bowels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passage', 'v. i.', 'In parliamentary proceedings: (a) The course of a\n   proposition (bill, resolution, etc.) through the several stages of\n   consideration and action; as, during its passage through Congress the\n   bill was amended in both Houses. (b) The advancement of a bill or other\n   proposition from one stage to another by an affirmative vote; esp., the\n   final affirmative action of the body upon a proposition; hence,\n   adoption; enactment; as, the passage of the bill to its third reading\n   was delayed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passager', 'n.', 'A passenger; a bird or boat of passage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passageway', 'n.', 'A way for passage; a hall. See Passage, 5.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passant', 'v. i.', 'Passing from one to another; in circulation; current.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passant', 'v. i.', 'Curs/ry, careless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passant', 'v. i.', 'Surpassing; excelling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passant', 'v. i.', 'Walking; -- said of any animal on an escutcheon, which\n   is represented as walking with the dexter paw raised.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passe', 'a.', 'Alt. of Passee');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passee', 'a.', 'Past; gone by; hence, past ones prime; worn; faded; as, a\n   passee belle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passegarde', 'n.', 'A ridge or projecting edge on a shoulder piece to turn\n   the blow of a lance or other weapon from the joint of the armor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passement', 'n.', 'Lace, gimp, braid etc., sewed on a garment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passementerie', 'n.', 'Beaded embroidery for womens dresses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passenger', 'n.', 'A passer or passer-by; a wayfarer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passenger', 'n.', 'A traveler by some established conveyance, as a coach,\n   steamboat, railroad train, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passe partout', 'n.', 'That by which one can pass anywhere; a safe-conduct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passe partout', 'n.', 'A master key; a latchkey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passe partout', 'n.', 'A light picture frame or mat of cardboard, wood, or\n   the like, usually put between the picture and the glass, and sometimes\n   serving for several pictures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passer', 'n.', 'One who passes; a passenger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passer-by', 'n.', 'One who goes by; a passer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passeres', 'n. pl.', 'An order, or suborder, of birds, including more that\n   half of all the known species. It embraces all singing birds (Oscines),\n   together with many other small perching birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passeriform', 'a.', 'Like or belonging to the Passeres.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passerine', 'a.', 'Of or pertaining to the Passeres.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passerine', 'n.', 'One of the Passeres.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passibility', 'n.', 'The quality or state of being passible; aptness to\n   feel or suffer; sensibility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passible', 'a.', 'Susceptible of feeling or suffering, or of impressions\n   from external agents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passibleness', 'n.', 'Passibility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passiflora', 'n.', 'A genus of plants, including the passion flower. It is\n   the type of the order Passifloreae, which includes about nineteen\n   genera and two hundred and fifty species.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passim', 'adv.', 'Here and there; everywhere; as, this word occurs passim\n   in the poem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passing', 'n.', 'The act of one who, or that which, passes; the act of\n   going by or away.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passing', 'a.', 'Relating to the act of passing or going; going by, beyond,\n   through, or away; departing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passing', 'a.', 'Exceeding; surpassing, eminent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passing', 'adv.', 'Exceedingly; excessively; surpassingly; as, passing\n   fair; passing strange.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passingly', 'adv.', 'Exceedingly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passion', 'n.', 'A suffering or enduring of imposed or inflicted pain; any\n   suffering or distress (as, a cardiac passion); specifically, the\n   suffering of Christ between the time of the last supper and his death,\n   esp. in the garden upon the cross.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passion', 'n.', 'The state of being acted upon; subjection to an external\n   agent or influence; a passive condition; -- opposed to action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passion', 'n.', 'Capacity of being affected by external agents;\n   susceptibility of impressions from external agents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passion', 'n.', 'The state of the mind when it is powerfully acted upon and\n   influenced by something external to itself; the state of any particular\n   faculty which, under such conditions, becomes extremely sensitive or\n   uncontrollably excited; any emotion or sentiment (specifically, love or\n   anger) in a state of abnormal or controlling activity; an extreme or\n   inordinate desire; also, the capacity or susceptibility of being so\n   affected; as, to be in a passion; the passions of love, hate,\n   jealously, wrath, ambition, avarice, fear, etc.; a passion for war, or\n   for drink; an orator should have passion as well as rhetorical skill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passion', 'n.', 'Disorder of the mind; madness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passion', 'n.', 'Passion week. See Passion week, below.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passioned', 'imp. & p. p.', 'of Passion');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passioning', 'p. pr & vb. n.', 'of Passion');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passion', 'v. t.', 'To give a passionate character to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passion', 'v. i.', 'To suffer pain or sorrow; to experience a passion; to\n   be extremely agitated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passional', 'a.', 'Of or pertaining to passion or the passions; exciting,\n   influenced by, or ministering to, the passions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passional', 'n.', 'A passionary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passionary', 'n.', 'A book in which are described the sufferings of saints\n   and martyrs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passionate', 'a.', 'Capable or susceptible of passion, or of different\n   passions; easily moved, excited or agitated; specifically, easily moved\n   to anger; irascible; quick-tempered; as, a passionate nature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passionate', 'a.', 'Characterized by passion; expressing passion; ardent in\n   feeling or desire; vehement; warm; as, a passionate friendship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passionate', 'a.', 'Suffering; sorrowful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passionate', 'v. i.', 'To affect with passion; to impassion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passionate', 'v. i.', 'To express feelingly or sorrowfully.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passionately', 'adv.', 'In a passionate manner; with strong feeling;\n   ardently.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passionately', 'adv.', 'Angrily; irascibly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passionateness', 'n.', 'The state or quality of being passionate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passionist', 'n.', 'A member of a religious order founded in Italy in 1737,\n   and introduced into the United States in 1852. The members of the order\n   unite the austerities of the Trappists with the activity and zeal of\n   the Jesuits and Lazarists. Called also Barefooted Clerks of the Most\n   Holy Cross.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passionless', 'a.', 'Void of passion; without anger or emotion; not easily\n   excited; calm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passiontide', 'n.', 'The last fortnight of Lent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passive', 'a.', 'Not active, but acted upon; suffering or receiving\n   impressions or influences; as, they were passive spectators, not actors\n   in the scene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passive', 'a.', 'Receiving or enduring without either active sympathy or\n   active resistance; without emotion or excitement; patient; not\n   opposing; unresisting; as, passive obedience; passive submission.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passive', 'a.', 'Inactive; inert; not showing strong affinity; as, red\n   phosphorus is comparatively passive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passive', 'a.', 'Designating certain morbid conditions, as hemorrhage or\n   dropsy, characterized by relaxation of the vessels and tissues, with\n   deficient vitality and lack of reaction in the affected tissues.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passively', 'adv.', 'In a passive manner; inertly; unresistingly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passively', 'adv.', 'As a passive verb; in the passive voice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passiveness', 'n.', 'The quality or state of being passive; unresisting\n   submission.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passivity', 'n.', 'Passiveness; -- opposed to activity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passivity', 'n.', 'The tendency of a body to remain in a given state,\n   either of motion or rest, till disturbed by another body; inertia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passivity', 'n.', 'The quality or condition of any substance which has no\n   inclination to chemical activity; inactivity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pass-key', 'n.', 'A key for opening more locks than one; a master key.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passless', 'a.', 'Having no pass; impassable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passmen', 'pl. ', 'of Passman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passman', 'n.', 'One who passes for a degree, without honors. See Classman,\n   2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passover', 'n.', 'A feast of the Jews, instituted to commemorate the\n   sparing of the Hebrews in Egypt, when God, smiting the firstborn of the\n   Egyptians, passed over the houses of the Israelites which were marked\n   with the blood of a lamb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passover', 'n.', 'The sacrifice offered at the feast of the passover; the\n   paschal lamb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pass-parole', 'n.', 'An order passed from front to rear by word of mouth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passport', 'n.', 'Permission to pass; a document given by the competent\n   officer of a state, permitting the person therein named to pass or\n   travel from place to place, without molestation, by land or by water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passport', 'n.', 'A document carried by neutral merchant vessels in time of\n   war, to certify their nationality and protect them from belligerents; a\n   sea letter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passport', 'n.', 'A license granted in time of war for the removal of\n   persons and effects from a hostile country; a safe-conduct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passport', 'n.', 'Figuratively: Anything which secures advancement and\n   general acceptance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passus', 'pl. ', 'of Passus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passuses', 'pl. ', 'of Passus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passus', 'n.', 'A division or part; a canto; as, the passus of Piers\n   Plowman. See 2d Fit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Password', 'n.', 'A word to be given before a person is allowed to pass; a\n   watchword; a countersign.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Passymeasure', 'n.', 'See Paspy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Past', 'v.', 'Of or pertaining to a former time or state; neither present\n   nor future; gone by; elapsed; ended; spent; as, past troubles; past\n   offences.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Past', 'n.', 'A former time or state; a state of things gone by.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Past', 'prep.', 'Beyond, in position, or degree; further than; beyond the\n   reach or influence of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Past', 'prep.', 'Beyond, in time; after; as, past the hour.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Past', 'prep.', 'Above; exceeding; more than.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Past', 'adv.', 'By; beyond; as, he ran past.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paste', 'n.', 'A soft composition, as of flour moistened with water or\n   milk, or of earth moistened to the consistence of dough, as in making\n   potters ware.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paste', 'n.', 'Specifically, in cookery, a dough prepared for the crust of\n   pies and the like; pastry dough.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paste', 'n.', 'A kind of cement made of flour and water, starch and water,\n   or the like, -- used for uniting paper or other substances, as in\n   bookbinding, etc., -- also used in calico printing as a vehicle for\n   mordant or color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paste', 'n.', 'A highly refractive vitreous composition, variously colored,\n   used in making imitations of precious stones or gems. See Strass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paste', 'n.', 'A soft confection made of the inspissated juice of fruit,\n   licorice, or the like, with sugar, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paste', 'n.', 'The mineral substance in which other minerals are imbedded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pasted', 'imp. & p. p.', 'of Paste');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pasting', 'p. pr. & vb. n.', 'of Paste');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paste', 'v. t.', 'To unite with paste; to fasten or join by means of paste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pasteboard', 'n.', 'A stiff thick kind of paper board, formed of several\n   single sheets pasted one upon another, or of paper macerated and\n   pressed into molds, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pasteboard', 'n.', 'A board on which pastry dough is rolled; a molding\n   board.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pastel', 'n.', 'A crayon made of a paste composed of a color ground with\n   gum water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pastel', 'n.', 'A plant affording a blue dye; the woad (Isatis tinctoria);\n   also, the dye itself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paster', 'n.', 'One who pastes; as, a paster in a government department.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paster', 'n.', 'A slip of paper, usually bearing a name, intended to be\n   pasted by the voter, as a substitute, over another name on a printed\n   ballot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pastern', 'n.', 'The part of the foot of the horse, and allied animals,\n   between the fetlock and the coffin joint. See Illust. of Horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pastern', 'n.', 'A shackle for horses while pasturing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pastern', 'n.', 'A patten.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pasteurism', 'n.', 'A method of treatment, devised by Pasteur, for\n   preventing certain diseases, as hydrophobia, by successive inoculations\n   with an attenuated virus of gradually increasing strength.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pasteurism', 'n.', 'Pasteurization.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pasteurization', 'n.', 'A process devised by Pasteur for preventing or\n   checking fermentation in fluids, such as wines, milk, etc., by exposure\n   to a temperature of 140 F., thus destroying the vitality of the\n   contained germs or ferments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pasteurize', 'v. t.', 'To subject to pasteurization.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pasteurize', 'v. t.', 'To treat by pasteurism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pasticcio', 'n.', 'A medley; an olio.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pasticcio', 'n.', 'A work of art imitating directly the work of another\n   artist, or of more artists than one.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pasticcio', 'n.', 'A falsified work of art, as a vase or statue made up of\n   parts of original works, with missing parts supplied.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pastil', 'n.', 'Alt. of Pastille');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pastille', 'n.', 'A small cone or mass made of paste of gum, benzoin,\n   cinnamon, and other aromatics, -- used for fumigating or scenting the\n   air of a room.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pastille', 'n.', 'An aromatic or medicated lozenge; a troche.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pastille', 'n.', 'See Pastel, a crayon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pastime', 'n.', 'That which amuses, and serves to make time pass agreeably;\n   sport; amusement; diversion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pastime', 'v. i.', 'To sport; to amuse ones self.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pastor', 'n.', 'A shepherd; one who has the care of flocks and herds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pastor', 'n.', 'A guardian; a keeper; specifically (Eccl.), a minister\n   having the charge of a church and parish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pastor', 'n.', 'A species of starling (Pastor roseus), native of the plains\n   of Western Asia and Eastern Europe. Its head is crested and glossy\n   greenish black, and its back is rosy. It feeds largely upon locusts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pastorage', 'n.', 'The office, jurisdiction, or duty, of a pastor;\n   pastorate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pastoral', 'a.', 'Of or pertaining to shepherds; hence, relating to rural\n   life and scenes; as, a pastoral life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pastoral', 'a.', 'Relating to the care of souls, or to the pastor of a\n   church; as, pastoral duties; a pastoral letter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pastoral', 'n.', 'A poem describing the life and manners of shepherds; a\n   poem in which the speakers assume the character of shepherds; an idyl;\n   a bucolic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pastoral', 'n.', 'A cantata relating to rural life; a composition for\n   instruments characterized by simplicity and sweetness; a lyrical\n   composition the subject of which is taken from rural life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pastoral', 'n.', 'A letter of a pastor to his charge; specifically, a\n   letter addressed by a bishop to his diocese; also (Prot. Epis. Ch.), a\n   letter of the House of Bishops, to be read in each parish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pastorale', 'n.', 'A composition in a soft, rural style, generally in 6-8\n   or 12-8 time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pastorale', 'n.', 'A kind of dance; a kind of figure used in a dance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pastorally', 'adv.', 'In a pastoral or rural manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pastorally', 'adv.', 'In the manner of a pastor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pastorate', 'n.', 'The office, state, or jurisdiction of a pastor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pastorless', 'a.', 'Having no pastor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pastorling', 'n.', 'An insignificant pastor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pastorly', 'a.', 'Appropriate to a pastor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pastorship', 'n.', 'Pastorate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pastries', 'pl. ', 'of Pastry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pastry', 'n.', 'The place where pastry is made.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pastry', 'n.', 'Articles of food made of paste, or having a crust made of\n   paste, as pies, tarts, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pasturable', 'a.', 'Fit for pasture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pasturage', 'n.', 'Grazing ground; grass land used for pasturing; pasture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pasturage', 'n.', 'Grass growing for feed; grazing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pasturage', 'n.', 'The business of feeding or grazing cattle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pasture', 'n.', 'Food; nourishment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pasture', 'n.', 'Specifically: Grass growing for the food of cattle; the\n   food of cattle taken by grazing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pasture', 'n.', 'Grass land for cattle, horses, etc.; pasturage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pastured', 'imp. & p. p.', 'of Pasture');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pasturing', 'p. pr. & vb. n.', 'of Pasture');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pasture', 'v. t.', 'To feed, esp. to feed on growing grass; to supply grass\n   as food for; as, the farmer pastures fifty oxen; the land will pasture\n   forty cows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pasture', 'v. i.', 'To feed on growing grass; to graze.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pastureless', 'a.', 'Destitute of pasture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pasturer', 'n.', 'One who pastures; one who takes cattle to graze. See\n   Agister.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pasty', 'a.', 'Like paste, as in color, softness, stickness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pasties', 'pl. ', 'of Pasty');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pasty', 'n.', 'A pie consisting usually of meat wholly surrounded with a\n   crust made of a sheet of paste, and often baked without a dish; a meat\n   pie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patted', 'imp. & p. p.', 'of Pat');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patting', 'p. pr. & vb. n.', 'of Pat');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pat', 'v. t.', 'To strike gently with the fingers or hand; to stroke\n   lightly; to tap; as, to pat a dog.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pat', 'n.', 'A light, quik blow or stroke with the fingers or hand; a tap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pat', 'n.', 'A small mass, as of butter, shaped by pats.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pat', 'a.', 'Exactly suitable; fit; convenient; timely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pat', 'adv.', 'In a pat manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pataca', 'n.', 'The Spanish dollar; -- called also patacoon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patache', 'n.', 'A tender to a fleet, formerly used for conveying men,\n   orders, or treasure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patacoon', 'n.', 'See Pataca.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patagia', 'pl. ', 'of Patagium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patagium', 'n.', 'In bats, an expansion of the integument uniting the fore\n   limb with the body and extending between the elongated fingers to form\n   the wing; in birds, the similar fold of integument uniting the fore\n   limb with the body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patagium', 'n.', 'One of a pair of small vesicular organs situated at the\n   bases of the anterior wings of lepidopterous insects. See Illust. of\n   Butterfly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patagonian', 'a.', 'Of or pertaining to Patagonia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patagonian', 'n.', 'A native of Patagonia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patamar', 'n.', 'A vessel resembling a grab, used in the coasting trade of\n   Bombay and Ceylon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patas', 'n.', 'A West African long-tailed monkey (Cercopithecus ruber); the\n   red monkey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patavinity', 'n.', 'The use of local or provincial words, as in the\n   peculiar style or diction of Livy, the Roman historian; -- so called\n   from Patavium, now Padua, the place of Livys nativity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patch', 'n.', 'A piece of cloth, or other suitable material, sewed or\n   otherwise fixed upon a garment to repair or strengthen it, esp. upon an\n   old garment to cover a hole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patch', 'n.', 'A small piece of anything used to repair a breach; as, a\n   patch on a kettle, a roof, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patch', 'n.', 'A small piece of black silk stuck on the face, or neck, to\n   hide a defect, or to heighten beauty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patch', 'n.', 'A piece of greased cloth or leather used as wrapping for a\n   rifle ball, to make it fit the bore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patch', 'n.', 'Fig.: Anything regarded as a patch; a small piece of ground;\n   a tract; a plot; as, scattered patches of trees or growing corn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patch', 'n.', 'A block on the muzzle of a gun, to do away with the effect\n   of dispart, in sighting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patch', 'n.', 'A paltry fellow; a rogue; a ninny; a fool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patched', 'imp. & p. p.', 'of Patch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patching', 'p. pr. & vb. n.', 'of Patch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patch', 'v. t.', 'To mend by sewing on a piece or pieces of cloth, leather,\n   or the like; as, to patch a coat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patch', 'v. t.', 'To mend with pieces; to repair with pieces festened on;\n   to repair clumsily; as, to patch the roof of a house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patch', 'v. t.', 'To adorn, as the face, with a patch or patches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patch', 'v. t.', 'To make of pieces or patches; to repair as with patches;\n   to arrange in a hasty or clumsy manner; -- generally with up; as, to\n   patch up a truce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patcher', 'n.', 'One who patches or botches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patchery', 'n.', 'Botchery; covering of defects; bungling; hypocrisy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patchingly', 'adv.', 'Knavishy; deceitfully.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patchouli', 'n.', 'Alt. of Patchouly');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patchouly', 'n.', 'A mintlike plant (Pogostemon Patchouli) of the East\n   Indies, yielding an essential oil from which a highly valued perfume is\n   made.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patchouly', 'n.', 'The perfume made from this plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patchwork', 'n.', 'Work composed of pieces sewed together, esp. pieces of\n   various colors and figures; hence, anything put together of incongruous\n   or ill-adapted parts; something irregularly clumsily composed; a thing\n   putched up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patchy', 'a.', 'Full of, or covered with, patches; abounding in patches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pate', 'a.', 'See Patte.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pate', 'n.', 'A pie. See Patty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pate', 'n.', 'A kind of platform with a parapet, usually of an oval form,\n   and generally erected in marshy grounds to cover a gate of a fortified\n   place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pate', 'n.', 'The head of a person; the top, or crown, of the head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pate', 'n.', 'The skin of a calfs head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pated', 'a.', 'Having a pate; -- used only in composition; as, long-pated;\n   shallow-pated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patee', 'n.', 'See Pattee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patefaction', 'n.', 'The act of opening, disclosing, or manifesting; open\n   declaration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patela', 'n.', 'A large flat-bottomed trading boat peculiar to the river\n   Ganges; -- called also puteli.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patellae', 'pl. ', 'of Patella');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patella', 'n.', 'A small dish, pan, or vase.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patella', 'n.', 'The kneepan; the cap of the knee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patella', 'n.', 'A genus of marine gastropods, including many species of\n   limpets. The shell has the form of a flattened cone. The common\n   European limpet (Patella vulgata) is largely used for food.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patella', 'n.', 'A kind of apothecium in lichens, which is orbicular, flat,\n   and sessile, and has a special rim not a part of the thallus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patellar', 'a.', 'Of or pertaining to the patella, or kneepan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patelliform', 'a.', 'Having the form of a patella.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patelliform', 'a.', 'Resembling a limpet of the genus Patella.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patellulae', 'pl. ', 'of Patellula');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patellula', 'n.', 'A cuplike sucker on the feet of certain insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paten', 'n.', 'A plate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paten', 'n.', 'The place on which the consecrated bread is placed in the\n   Eucharist, or on which the host is placed during the Mass. It is\n   usually small, and formed as to fit the chalice, or cup, as a cover.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patena', 'n.', 'A paten.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patena', 'n.', 'A grassy expanse in the hill region of Ceylon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patency', 'n.', 'The condition of being open, enlarged, or spread.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patency', 'n.', 'The state of being patent or evident.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patent', 'a.', 'Open; expanded; evident; apparent; unconcealed; manifest;\n   public; conspicuous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patent', 'a.', 'Open to public perusal; -- said of a document conferring\n   some right or privilege; as, letters patent. See Letters patent, under\n   3d Letter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patent', 'a.', 'Appropriated or protected by letters patent; secured by\n   official authority to the exclusive possession, control, and disposal\n   of some person or party; patented; as, a patent right; patent\n   medicines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patent', 'a.', 'Spreading; forming a nearly right angle with the steam or\n   branch; as, a patent leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patent', 'a.', 'A letter patent, or letters patent; an official document,\n   issued by a sovereign power, conferring a right or privilege on some\n   person or party.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patent', 'a.', 'A writing securing to an invention.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patent', 'a.', 'A document making a grant and conveyance of public lands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patent', 'a.', 'The right or privilege conferred by such a document; hence,\n   figuratively, a right, privilege, or license of the nature of a patent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patented', 'imp. & p. p.', 'of Patent');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patenting', 'p. pr. & vb. n.', 'of Patent');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patent', 'v. t.', 'To grant by patent; to make the subject of a patent; to\n   secure or protect by patent; as, to patent an invention; to patent\n   public lands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patentable', 'a.', 'Suitable to be patented; capable of being patented.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patentee', 'n.', 'One to whom a grant is made, or a privilege secured, by\n   patent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patent-hammered', 'a.', 'Having a surface dressed by cutting with a hammer\n   the head of which consists of broad thin chisels clamped together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patently', 'adv.', 'Openly; evidently.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paterae', 'pl. ', 'of Patera');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patera', 'n.', 'A saucerlike vessel of earthenware or metal, used by the\n   Greeks and Romans in libations and sacrificies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patera', 'n.', 'A circular ornament, resembling a dish, often worked in\n   relief on friezes, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paterero', 'n.', 'See Pederero.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pateresfamilias', 'pl. ', 'of Paterfamilias');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paterfamilias', 'n.', 'The head of a family; in a large sense, the\n   proprietor of an estate; one who is his own master.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paternal', 'a.', 'Of or pertaining to a father; fatherly; showing the\n   disposition of a father; guiding or instructing as a father; as,\n   paternal care.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paternal', 'a.', 'Received or derived from a father; hereditary; as, a\n   paternal estate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paternalism', 'n.', 'The theory or practice of paternal government. See\n   Paternal government, under Paternal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paternally', 'adv.', 'In a paternal manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paternity', 'n.', 'The relation of a father to his child; fathership;\n   fatherhood; family headship; as, the divine paternity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paternity', 'n.', 'Derivation or descent from a father; male parentage; as,\n   the paternity of a child.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paternity', 'n.', 'Origin; authorship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paternoster', 'n.', 'The Lords prayer, so called from the first two words\n   of the Latin version.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paternoster', 'n.', 'A beadlike ornament in moldings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paternoster', 'n.', 'A line with a row of hooks and bead/shaped sinkers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paths', 'pl. ', 'of Path');
  INSERT INTO words (word, word_type, description) VALUES  (    'Path', 'n.', 'A trodden way; a footway.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Path', 'n.', 'A way, course, or track, in which anything moves or has\n   moved; route; passage; an established way; as, the path of a meteor, of\n   a caravan, of a storm, of a pestilence. Also used figuratively, of a\n   course of life or action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pathed', 'imp. & p. p.', 'of Path');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pathing', 'pr.p. & vb. n.', 'of Path');
  INSERT INTO words (word, word_type, description) VALUES  (    'Path', 'v. t.', 'To make a path in, or on (something), or for (some one).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Path', 'v. i.', 'To walk or go.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pathematic', 'a.', 'Of, pertaining to, or designating, emotion or\n   suffering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pathetic', 'a.', 'Expressing or showing anger; passionate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pathetic', 'a.', 'Affecting or moving the tender emotions, esp. pity or\n   grief; full of pathos; as, a pathetic song or story.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pathetical', 'a.', 'Pathetic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pathetism', 'n.', 'See Mesmerism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pathfinder', 'n.', 'One who discovers a way or path; one who explores\n   untraversed regions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pathic', 'n.', 'A male who submits to the crime against nature; a catamite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pathic', 'a.', 'Passive; suffering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pathless', 'a.', 'Having no beaten path or way; untrodden; impenetrable;\n   as, pathless woods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pathmaker', 'n.', 'One who, or that which, makes a way or path.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pathogene', 'n.', 'One of a class of virulent microorganisms or bacteria\n   found in the tissues and fluids in infectious diseases, and supposed to\n   be the cause of the disease; a pathogenic organism; a pathogenic\n   bacterium; -- opposed to zymogene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pathogenesis', 'n.', 'Pathogeny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pathogenetic', 'a.', 'Pathogenic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pathogenic', 'a.', 'Of or pertaining to pathogeny; producting disease; as,\n   a pathogenic organism; a pathogenic bacterium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pathogeny', 'n.', 'The generation, and method of development, of disease;\n   as, the pathogeny of yellow fever is unsettled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pathogeny', 'n.', 'That branch of pathology which treats of the generation\n   and development of disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pathognomonic', 'a.', 'Specially or decisively characteristic of a disease;\n   indicating with certainty a disease; as, a pathognomonic symptom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pathognomy', 'n.', 'Expression of the passions; the science of the signs by\n   which human passions are indicated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pathologic', 'a.', 'Alt. of Pathological');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pathological', 'a.', 'Of or pertaining to pathology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pathologist', 'n.', 'One skilled in pathology; an investigator in\n   pathology; as, the pathologist of a hospital, whose duty it is to\n   determine the causes of the diseases.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pathologies', 'pl. ', 'of Pathology');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pathology', 'n.', 'The science which treats of diseases, their nature,\n   causes, progress, symptoms, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-ias', 'pl. ', 'of Pathopoela');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pathopoela', 'n.', 'A speech, or figure of speech, designed to move the\n   passion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pathos', 'n.', 'That quality or property of anything which touches the\n   feelings or excites emotions and passions, esp., that which awakens\n   tender emotions, such as pity, sorrow, and the like; contagious warmth\n   of feeling, action, or expression; pathetic quality; as, the pathos of\n   a picture, of a poem, or of a cry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pathway', 'n.', 'A footpath; a beaten track; any path or course. Also used\n   figuratively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patible', 'a.', 'Sufferable; tolerable; endurable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patibulary', 'a.', 'Of or pertaining to the gallows, or to execution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patibulated', 'a.', 'Hanged on a gallows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patience', 'n.', 'The state or quality of being patient; the power of\n   suffering with fortitude; uncomplaining endurance of evils or wrongs,\n   as toil, pain, poverty, insult, oppression, calamity, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patience', 'n.', 'The act or power of calmly or contentedly waiting for\n   something due or hoped for; forbearance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patience', 'n.', 'Constancy in labor or application; perseverance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patience', 'n.', 'Sufferance; permission.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patience', 'n.', 'A kind of dock (Rumex Patientia), less common in America\n   than in Europe; monks rhubarb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patience', 'n.', 'Solitaire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patient', 'a.', 'Having the quality of enduring; physically able to suffer\n   or bear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patient', 'a.', 'Undergoing pains, trails, or the like, without murmuring\n   or fretfulness; bearing up with equanimity against trouble;\n   long-suffering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patient', 'a.', 'Constant in pursuit or exertion; persevering; calmly\n   diligent; as, patient endeavor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patient', 'a.', 'Expectant with calmness, or without discontent; not hasty;\n   not overeager; composed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patient', 'a.', 'Forbearing; long-suffering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patient', 'n.', 'ONe who, or that which, is passively affected; a passive\n   recipient.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patient', 'n.', 'A person under medical or surgical treatment; --\n   correlative to physician or nurse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patient', 'v. t.', 'To compose, to calm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patiently', 'adv.', 'In a patient manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patin', 'n.', 'Alt. of Patine');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patine', 'n.', 'A plate. See Paten.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patina', 'n.', 'A dish or plate of metal or earthenware; a patella.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patina', 'n.', 'The color or incrustation which age gives to works of art;\n   especially, the green rust which covers ancient bronzes, coins, and\n   medals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patio', 'n.', 'A paved yard or floor where ores are cleaned and sorted, or\n   where ore, salt, mercury, etc., are trampled by horses, to effect\n   intermixture and amalgamation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patly', 'adv.', 'Fitly; seasonably.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patness', 'n.', 'Fitness or appropriateness; striking suitableness;\n   convenience.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patois', 'n.', 'A dialect peculiar to the illiterate classes; a provincial\n   form of speech.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patonce', 'a.', 'Having the arms growing broader and floriated toward the\n   end; -- said of a cross. See Illust. 9 of Cross.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patrial', 'a.', 'Derived from the name of a country, and designating an\n   inhabitant of the country; gentile; -- said of a noun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patrial', 'n.', 'A patrial noun. Thus Romanus, a Roman, and Troas, a woman\n   of Troy, are patrial nouns, or patrials.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patriarch', 'n.', 'The father and ruler of a family; one who governs his\n   family or descendants by paternal right; -- usually applied to heads of\n   families in ancient history, especially in Biblical and Jewish history\n   to those who lived before the time of Moses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patriarch', 'n.', 'A dignitary superior to the order of archbishops; as,\n   the patriarch of Constantinople, of Alexandria, or of Antioch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patriarch', 'n.', 'A venerable old man; an elder. Also used figuratively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patriarchal', 'a.', 'Of or pertaining to a patriarch or to patriarchs;\n   possessed by, or subject to, patriarchs; as, patriarchal authority or\n   jurisdiction; a patriarchal see; a patriarchal church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patriarchal', 'a.', 'Characteristic of a patriarch; venerable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patriarchal', 'a.', 'Having an organization of society and government in\n   which the head of the family exercises authority over all its\n   generations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patriarchate', 'n.', 'The office, dignity, or jurisdiction of a patriarch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patriarchate', 'n.', 'The residence of an ecclesiastic patriarch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patriarchate', 'n.', 'A patriarchal form of government or society. See\n   Patriarchal, a., 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patriarchdom', 'n.', 'The office or jurisdiction of a patriarch;\n   patriarchate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patriarchic', 'a.', 'Patriarchal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patriarchism', 'n.', 'Government by a patriarch, or the head of a family.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patriarchship', 'n.', 'A patriarchate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patriarchy', 'n.', 'The jurisdiction of a patriarch; patriarchship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patriarchy', 'n.', 'Government by a patriarch; patriarchism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patrician', 'a.', 'Of or pertaining to the Roman patres (fathers) or\n   senators, or patricians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patrician', 'a.', 'Of, pertaining to, or appropriate to, a person of high\n   birth; noble; not plebeian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patrician', 'n.', 'Originally, a member of any of the families constituting\n   the populus Romanus, or body of Roman citizens, before the development\n   of the plebeian order; later, one who, by right of birth or by special\n   privilege conferred, belonged to the nobility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patrician', 'n.', 'A person of high birth; a nobleman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patrician', 'n.', 'One familiar with the works of the Christian Fathers;\n   one versed in patristic lore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patricianism', 'n.', 'The rank or character of patricians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patriciate', 'n.', 'The patrician class; the aristocracy; also, the office\n   of patriarch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patricidal', 'a.', 'Of or pertaining to patricide; parricidal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patricide', 'n.', 'The murderer of his father.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patricide', 'n.', 'The crime of one who murders his father. Same as\n   Parricide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patrimonial', 'a.', 'Of or pertaining to a patrimony; inherited from\n   ancestors; as, a patrimonial estate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patrimonially', 'adv.', 'By inheritance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patrimonies', 'pl. ', 'of Patrimony');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patrimony', 'n.', 'A right or estate inherited from ones father; or, in a\n   larger sense, from any ancestor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patrimony', 'n.', 'Formerly, a church estate or endowment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patriot', 'n.', 'One who loves his country, and zealously supports its\n   authority and interests.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patriot', 'a.', 'Becoming to a patriot; patriotic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patriotic', 'a.', 'Inspired by patriotism; actuated by love of ones\n   country; zealously and unselfishly devoted to the service of ones\n   country; as, a patriotic statesman, vigilance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patriotical', 'a.', 'Patriotic; that pertains to a patriot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patriotism', 'n.', 'Love of country; devotion to the welfare of ones\n   country; the virtues and actions of a patriot; the passion which\n   inspires one to serve ones country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patripassian', 'n.', 'One of a body of believers in the early church who\n   denied the independent preexistent personality of Christ, and who,\n   accordingly, held that the Father suffered in the Son; a monarchian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patrist', 'n.', 'One versed in patristics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patristic', 'a.', 'Alt. of Patristical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patristical', 'a.', 'Of or pertaining to the Fathers of the Christian\n   church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patristics', 'n.', 'That departnent of historical theology which treats of\n   the lives and doctrines of the Fathers of the church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patrizate', 'v. i.', 'To imitate ones father.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patrocinate', 'v. t.', 'To support; to patronize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patrocination', 'n.', 'The act of patrocinating or patronizing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patrociny', 'n.', 'See Patrocination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patrolled', 'imp. & p. p.', 'of Patrol');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patrolling', 'p. pr. & vb. n.', 'of Patrol');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patrol', 'v. i.', 'To go the rounds along a chain of sentinels; to traverse\n   a police district or beat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patrol', 'v.', 't To go the rounds of, as a sentry, guard, or policeman;\n   as, to patrol a frontier; to patrol a beat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patrol', 'v. i.', 'A going of the rounds along the chain of sentinels and\n   between the posts, by a guard, usually consisting of three or four men,\n   to insure greater security from attacks on the outposts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patrol', 'v. i.', 'A movement, by a small body of troops beyond the line of\n   outposts, to explore the country and gain intelligence of the enemys\n   whereabouts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patrol', 'v. i.', 'The guard or men who go the rounds for observation; a\n   detachment whose duty it is to patrol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patrol', 'v. i.', 'Any perambulation of a particular line or district to\n   guard it; also, the men thus guarding; as, a customs patrol; a fire\n   patrol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patrole', 'n. & v.', 'See Patrol, n. & v.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patrolmen', 'pl. ', 'of Patrolman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patrolman', 'n.', 'One who patrols; a watchman; especially, a policeman who\n   patrols a particular precinct of a town or city.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patron', 'n.', 'One who protects, supports, or countenances; a defender.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patron', 'n.', 'A master who had freed his slave, but still retained some\n   paternal rights over him.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patron', 'n.', 'A man of distinction under whose protection another person\n   placed himself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patron', 'n.', 'An advocate or pleader.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patron', 'n.', 'One who encourages or helps a person, a cause, or a work; a\n   furtherer; a promoter; as, a patron of art.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patron', 'n.', 'One who has gift and disposition of a benefice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patron', 'n.', 'A guardian saint. -- called also patron saint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patron', 'n.', 'See Padrone, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patron', 'v. t.', 'To be a patron of; to patronize; to favor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patron', 'a.', 'Doing the duty of a patron; giving aid or protection;\n   tutelary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patronage', 'n.', 'Special countenance or support; favor, encouragement, or\n   aid, afforded to a person or a work; as, the patronage of letters;\n   patronage given to an author.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patronage', 'n.', 'Business custom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patronage', 'n.', 'Guardianship, as of a saint; tutelary care.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patronage', 'n.', 'The right of nomination to political office; also, the\n   offices, contracts, honors, etc., which a public officer may bestow by\n   favor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patronage', 'n.', 'The right of presentation to church or ecclesiastical\n   benefice; advowson.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patronage', 'v. t.', 'To act as a patron of; to maintain; to defend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patronal', 'a.', 'Patron; protecting; favoring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patronate', 'n.', 'The right or duty of a patron; patronage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patroness', 'n.', 'A female patron or helper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patronization', 'n.', 'The act of patronizing; patronage; support.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patronized', 'imp. & p. p.', 'of Patronize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patronizing', 'p. pr. & vb. n.', 'of Patronize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patronize', 'v. t.', 'To act as patron toward; to support; to countenance;\n   to favor; to aid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patronize', 'v. t.', 'To trade with customarily; to frequent as a customer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patronize', 'v. t.', 'To assume the air of a patron, or of a superior and\n   protector, toward; -- used in an unfavorable sense; as, to patronize\n   ones equals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patronizer', 'n.', 'One who patronizes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patronizing', 'a.', 'Showing condescending favor; assuming the manner of\n   airs of a superior toward another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patronless', 'a.', 'Destitute of a patron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patronomayology', 'n.', 'That branch of knowledge which deals with personal\n   names and their origin; the study of patronymics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patronymic', 'a.', 'Derived from ancestors; as, a patronymic denomination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patronymic', 'n.', 'A modification of the fathers name borne by the son; a\n   name derived from that of a parent or ancestor; as, Pelides, the son of\n   Peleus; Johnson, the son of John; Macdonald, the son of Donald;\n   Paulowitz, the son of Paul; also, the surname of a family; the family\n   name.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patronymical', 'a.', 'Same as Patronymic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patroon', 'n.', 'One of the proprietors of certain tracts of land with\n   manorial privileges and right of entail, under the old Dutch\n   governments of New York and New Jersey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patroonship', 'n.', 'The office of a patroon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patte', 'a.', 'Alt. of Pattee');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pattee', 'a.', 'Narrow at the inner, and very broad at the other, end, or\n   having its arms of that shape; -- said of a cross. See Illust. (8) of\n   Cross.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pattemar', 'n.', 'See Patamar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patten', 'n.', 'A clog or sole of wood, usually supported by an iron ring,\n   worn to raise the feet from the wet or the mud.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patten', 'n.', 'A stilt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pattened', 'a.', 'Wearing pattens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pattered', 'imp. & p. p.', 'of Patter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pattering', 'p. pr. & vb. n.', 'of Patter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patter', 'v. i.', 'To strike with a quick succession of slight, sharp\n   sounds; as, pattering rain or hail; pattering feet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patter', 'v. i.', 'To mutter; to mumble; as, to patter with the lips.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patter', 'v. i.', 'To talk glibly; to chatter; to harangue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patter', 'v. t.', 'To spatter; to sprinkle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patter', 'v. i.', 'To mutter; as prayers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patter', 'n.', 'A quick succession of slight sounds; as, the patter of\n   rain; the patter of little feet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patter', 'n.', 'Glib and rapid speech; a voluble harangue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patter', 'n.', 'The cant of a class; patois; as, thievess patter; gypsies\n   patter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patterer', 'n.', 'One who patters, or talks glibly; specifically, a street\n   peddler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pattern', 'n.', 'Anything proposed for imitation; an archetype; an\n   exemplar; that which is to be, or is worthy to be, copied or imitated;\n   as, a pattern of a machine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pattern', 'n.', 'A part showing the figure or quality of the whole; a\n   specimen; a sample; an example; an instance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pattern', 'n.', 'Stuff sufficient for a garment; as, a dress pattern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pattern', 'n.', 'Figure or style of decoration; design; as, wall paper of a\n   beautiful pattern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pattern', 'n.', 'Something made after a model; a copy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pattern', 'n.', 'Anything cut or formed to serve as a guide to cutting or\n   forming objects; as, a dressmakers pattern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pattern', 'n.', 'A full-sized model around which a mold of sand is made, to\n   receive the melted metal. It is usually made of wood and in several\n   parts, so as to be removed from the mold without injuring it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patterned', 'imp. & p. p.', 'of Pattern');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patterning', 'p. pr. & vb. n.', 'of Pattern');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pattern', 'v. t.', 'To make or design (anything) by, from, or after,\n   something that serves as a pattern; to copy; to model; to imitate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pattern', 'v. t.', 'To serve as an example for; also, to parallel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patties', 'pl. ', 'of Patty');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patty', 'n.', 'A little pie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pattypan', 'n.', 'A pan for baking patties.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pattypan', 'n.', 'A patty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Patulous', 'a.', 'Open; expanded; slightly spreading; having the parts\n   loose or dispersed; as, a patulous calyx; a patulous cluster of\n   flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pau', 'n.', 'See Pah.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pauciloquent', 'a.', 'Uttering few words; brief in speech.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pauciloquy', 'n.', 'Brevity in speech.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paucispiral', 'a.', 'Having few spirals, or whorls; as, a paucispiral\n   operculum or shell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paucity', 'n.', 'Fewness; smallness of number; scarcity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paucity', 'n.', 'Smallnes of quantity; exiguity; insufficiency; as, paucity\n   of blood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paugies', 'pl. ', 'of Paugy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paugie', 'n.', 'Alt. of Paugy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paugy', 'n.', 'The scup. See Porgy, and Scup.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pauhaugen', 'n.', 'The menhaden; -- called also poghaden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paul', 'n.', 'See Pawl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paul', 'n.', 'An Italian silver coin. See Paolo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pauldron', 'n.', 'A piece of armor covering the shoulder at the junction of\n   the body piece and arm piece.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paulian', 'n.', 'Alt. of Paulianist');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paulianist', 'n.', 'A follower of Paul of Samosata, a bishop of Antioch in\n   the third century, who was deposed for denying the divinity of Christ.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paulician', 'n.', 'One of a sect of Christian dualists originating in\n   Armenia in the seventh century. They rejected the Old Testament and the\n   part of the New.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paulin', 'n.', 'See Tarpaulin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pauline', 'a.', 'Of or pertaining to the apostle Paul, or his writings;\n   resembling, or conforming to, the writings of Paul; as, the Pauline\n   epistles; Pauline doctrine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paulist', 'n.', 'A member of The Institute of the Missionary Priests of St.\n   Paul the Apostle, founded in 1858 by the Rev. I. T. Hecker of New York.\n   The majority of the members were formerly Protestants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paulownia', 'n.', 'A genus of trees of the order Scrophulariaceae,\n   consisting of one species, Paulownia imperialis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paum', 'v. t. & i.', 'To palm off by fraud; to cheat at cards.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paunce', 'n.', 'The pansy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paunch', 'n.', 'The belly and its contents; the abdomen; also, the first\n   stomach, or rumen, of ruminants. See Rumen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paunch', 'n.', 'A paunch mat; -- called also panch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paunch', 'n.', 'The thickened rim of a bell, struck by the clapper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paunched', 'imp. & p. p.', 'of Paunch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paunching', 'p. pr. & vb. n.', 'of Paunch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paunch', 'v. t.', 'To pierce or rip the belly of; to eviscerate; to\n   disembowel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paunch', 'v. t.', 'To stuff with food.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paunchy', 'a.', 'Pot-bellied.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paune', 'n.', 'A kind of bread. See Pone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pauper', 'n.', 'A poor person; especially, one development on private or\n   public charity. Also used adjectively; as, pouper immigrants, pouper\n   labor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pauperism', 'n.', 'The state of being a pauper; the state of indigent\n   persons requiring support from the community.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pauperization', 'n.', 'The act or process of reducing to pauperism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pauperized', 'imp. & p. p.', 'of Pauperize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pauperizing', 'p. pr. & vb. n.', 'of Pauperize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pauperize', 'v. t.', 'To reduce to pauperism; as, to pauperize the\n   peasantry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pauropoda', 'n. pl.', 'An order of small myriapods having only nine pairs\n   of legs and destitute of tracheae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pause', 'n.', 'A temporary stop or rest; an intermission of action;\n   interruption; suspension; cessation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pause', 'n.', 'Temporary inaction or waiting; hesitation; suspence; doubt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pause', 'n.', 'In speaking or reading aloud, a brief arrest or suspension\n   of voice, to indicate the limits and relations of sentences and their\n   parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pause', 'n.', 'In writing and printing, a mark indicating the place and\n   nature of an arrest of voice in reading; a punctuation point; as, teach\n   the pupil to mind the pauses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pause', 'n.', 'A break or paragraph in writing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pause', 'n.', 'A hold. See 4th Hold, 7.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paused', 'imp. & p. p.', 'of Pause');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pausing', 'p. pr. & vb. n.', 'of Pause');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pause', 'n.', 'To make a short stop; to cease for a time; to intermit\n   speaking or acting; to stop; to wait; to rest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pause', 'n.', 'To be intermitted; to cease; as, the music pauses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pause', 'n.', 'To hesitate; to hold back; to delay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pause', 'n.', 'To stop in order to consider; hence, to consider; to\n   reflect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pause', 'v. t.', 'To cause to stop or rest; -- used reflexively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pauser', 'n.', 'One who pauses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pausingly', 'adv.', 'With pauses; haltingly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pauxi', 'n.', 'A curassow (Ourax pauxi), which, in South America, is often\n   domesticated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pavage', 'n.', 'See Pavage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pavan', 'n.', 'A stately and formal Spanish dance for which full state\n   costume is worn; -- so called from the resemblance of its movements to\n   those of the peacock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pave', 'n.', 'The pavement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paved', 'imp. & p. p.', 'of Pave');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paving', 'p. pr. & vb. n.', 'of Pave');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pave', 'v. t.', 'To lay or cover with stone, brick, or other material, so\n   as to make a firm, level, or convenient surface for horses, carriages,\n   or persons on foot, to travel on; to floor with brick, stone, or other\n   solid material; as, to pave a street; to pave a court.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pave', 'v. t.', 'Fig.: To make smooth, easy, and safe; to prepare, as a\n   path or way; as, to pave the way to promotion; to pave the way for an\n   enterprise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pavement', 'n.', 'That with which anythingis paved; a floor or covering of\n   solid material, laid so as to make a hard and convenient surface for\n   travel; a paved road or sidewalk; a decorative interior floor of tiles\n   or colored bricks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pavement', 'v. t.', 'To furnish with a pavement; to pave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paven', 'n.', 'See Pavan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paver', 'n.', 'One who paves; one who lays a pavement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pavesade', 'n.', 'A canvas screen, formerly sometimes extended along the\n   side of a vessel in a naval engagement, to conceal from the enemy the\n   operations on board.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pavese', 'n.', 'Alt. of Pavesse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pavesse', 'n.', 'Pavise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paviage', 'n.', 'A contribution or a tax for paving streets or highways.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pavian', 'n.', 'See Pavan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pavid', 'a.', 'Timid; fearful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pavidity', 'n.', 'Timidity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pavier', 'n.', 'A paver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paviin', 'n.', 'A glucoside found in species of the genus Pavia of the\n   Horse-chestnut family.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pavilion', 'n.', 'A temporary movable habitation; a large tent; a marquee;\n   esp., a tent raised on posts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pavilion', 'n.', 'A single body or mass of building, contained within\n   simple walls and a single roof, whether insulated, as in the park or\n   garden of a larger edifice, or united with other parts, and forming an\n   angle or central feature of a large pile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pavilion', 'n.', 'A flag, colors, ensign, or banner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pavilion', 'n.', 'Same as Tent (Her.)');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pavilion', 'n.', 'That part of a brilliant which lies between the girdle\n   and collet. See Illust. of Brilliant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pavilion', 'n.', 'The auricle of the ear; also, the fimbriated extremity of\n   the Fallopian tube.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pavilion', 'n.', 'A covering; a canopy; figuratively, the sky.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pavilioned', 'imp. & p. p.', 'of Pavilion');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pavilioning', 'p. pr. & vb. n.', 'of Pavilion');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pavilion', 'v. t.', 'To furnish or cover with, or shelter in, a tent or\n   tents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pavin', 'n.', 'See Pavan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paving', 'n.', 'The act or process of laying a pavement, or covering some\n   place with a pavement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paving', 'n.', 'A pavement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pavior', 'n.', 'One who paves; a paver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pavior', 'n.', 'A rammer for driving paving stones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pavior', 'n.', 'A brick or slab used for paving.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pavise', 'n.', 'A large shield covering the whole body, carried by a\n   pavisor, who sometimes screened also an archer with it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pavisor', 'n.', 'A soldier who carried a pavise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pavo', 'n.', 'A genus of birds, including the peacocks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pavo', 'n.', 'The Peacock, a constellation of the southern hemisphere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pavon', 'n.', 'A small triangular flag, esp. one attached to a knights\n   lance; a pennon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pavone', 'n.', 'A peacock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pavonian', 'a.', 'Of or pertaining to a peacock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pavonine', 'a.', 'Like, or pertaining to, the genus Pavo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pavonine', 'a.', 'Characteristic of a peacock; resembling the tail of a\n   peacock, as in colors; iridescent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paw', 'n.', 'The foot of a quadruped having claws, as the lion, dog, cat,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paw', 'n.', 'The hand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paw', 'v. i.', 'To draw the forefoot along the ground; to beat or scrape\n   with the forefoot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pawed', 'imp. & p. p.', 'of Paw');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pawing', 'p. pr. & vb. n.', 'of Paw');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paw', 'v. t.', 'To pass the paw over; to stroke or handle with the paws;\n   hence, to handle fondly or rudely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paw', 'v. t.', 'To scrape or beat with the forefoot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pawk', 'n.', 'A small lobster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pawky', 'a.', 'Arch; cunning; sly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pawl', 'n.', 'A pivoted tongue, or sliding bolt, on one part of a machine,\n   adapted to fall into notches, or interdental spaces, on another part,\n   as a ratchet wheel, in such a manner as to permit motion in one\n   direction and prevent it in the reverse, as in a windlass; a catch,\n   click, or detent. See Illust. of Ratchet Wheel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pawl', 'v. t.', 'To stop with a pawl; to drop the pawls off.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pawn', 'n.', 'See Pan, the masticatory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pawn', 'n.', 'A man or piece of the lowest rank.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pawn', 'n.', 'Anything delivered or deposited as security, as for the\n   payment of money borrowed, or of a debt; a pledge. See Pledge, n., 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pawn', 'n.', 'State of being pledged; a pledge for the fulfillment of a\n   promise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pawn', 'n.', 'A stake hazarded in a wager.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pawned', 'imp. & p. p.', 'of Pawn');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pawning', 'p. pr. & vb. n.', 'of Pawn');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pawn', 'v. t.', 'To give or deposit in pledge, or as security for the\n   payment of money borrowed; to put in pawn; to pledge; as, to pawn ones\n   watch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pawn', 'v. t.', 'To pledge for the fulfillment of a promise; to stake; to\n   risk; to wager; to hazard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pawnable', 'a.', 'Capable of being pawned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pawnbroker', 'n.', 'One who makes a business of lending money on the\n   security of personal property pledged or deposited in his keeping.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pawnbroking', 'n.', 'The business of a pawnbroker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pawnee', 'n.', 'One or two whom a pledge is delivered as security; one who\n   takes anything in pawn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pawnees', 'n. pl.', 'A tribe of Indians (called also Loups) who formerly\n   occupied the region of the Platte river, but now live mostly in the\n   Indian Territory. The term is often used in a wider sense to include\n   also the related tribes of Rickarees and Wichitas. Called also Pani.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pawner', 'n.', 'Alt. of Pawnor');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pawnor', 'n.', 'One who pawns or pledges anything as security for the\n   payment of borrowed money or of a debt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pawpaw', 'n.', 'See Papaw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pax', 'n.', 'The kiss of peace; also, the embrace in the sanctuary now\n   substituted for it at High Mass in Roman Catholic churches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pax', 'n.', 'A tablet or board, on which is a representation of Christ, of\n   the Virgin Mary, or of some saint and which, in the Mass, was kissed by\n   the priest and then by the people, in mediaeval times; an osculatory.\n   It is still used in communities, confraternities, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paxillose', 'a.', 'Resembling a little stake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paxilli', 'pl. ', 'of Paxillus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paxillus', 'n.', 'One of a peculiar kind of spines covering the surface of\n   certain starfishes. They are pillarlike, with a flattened summit which\n   is covered with minute spinules or granules. See Illustration in\n   Appendix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paxwax', 'n.', 'The strong ligament of the back of the neck in quadrupeds.\n   It connects the back of the skull with dorsal spines of the cervical\n   vertebrae, and helps to support the head. Called also paxywaxy and\n   packwax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paxywaxy', 'n.', 'See Paxwax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pay', 'v. t.', 'To cover, as bottom of a vessel, a seam, a spar, etc., with\n   tar or pitch, or waterproof composition of tallow, resin, etc.; to\n   smear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paid', 'imp. & p. p.', 'of Pay');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paying', 'p. pr. & vb. n.', 'of Pay');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pay', 'v. t.', 'To satisfy, or content; specifically, to satisfy (another\n   person) for service rendered, property delivered, etc.; to discharge\n   ones obligation to; to make due return to; to compensate; to\n   remunerate; to recompense; to requite; as, to pay workmen or servants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pay', 'v. t.', 'Hence, figuratively: To compensate justly; to requite\n   according to merit; to reward; to punish; to retort or retaliate upon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pay', 'v. t.', 'To discharge, as a debt, demand, or obligation, by giving\n   or doing what is due or required; to deliver the amount or value of to\n   the person to whom it is owing; to discharge a debt by delivering\n   (money owed).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pay', 'v. t.', 'To discharge or fulfill, as a duy; to perform or render\n   duty, as that which has been promised.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pay', 'v. t.', 'To give or offer, without an implied obligation; as, to pay\n   attention; to pay a visit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pay', 'v. i.', 'To give a recompense; to make payment, requital, or\n   satisfaction; to discharge a debt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pay', 'v. i.', 'Hence, to make or secure suitable return for expense or\n   trouble; to be remunerative or profitable; to be worth the effort or\n   pains required; as, it will pay to ride; it will pay to wait;\n   politeness always pays.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pay', 'n.', 'Satisfaction; content.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pay', 'n.', 'An equivalent or return for money due, goods purchased, or\n   services performed; salary or wages for work or service; compensation;\n   recompense; payment; hire; as, the pay of a clerk; the pay of a\n   soldier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Payable', 'a.', 'That may, can, or should be paid; suitable to be paid;\n   justly due.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Payable', 'a.', 'That may be discharged or settled by delivery of value.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Payable', 'a.', 'Matured; now due.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Payee', 'n.', 'The person to whom money is to be, or has been, paid; the\n   person named in a bill or note, to whom, or to whose order, the amount\n   is promised or directed to be paid. See Bill of exchange, under Bill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Payen', 'n. & a.', 'Pagan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Payer', 'n.', 'One who pays; specifically, the person by whom a bill or\n   note has been, or should be, paid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paymaster', 'n.', 'One who pays; one who compensates, rewards, or requites;\n   specifically, an officer or agent of a government, a corporation, or an\n   employer, whose duty it is to pay salaries, wages, etc., and keep\n   account of the same.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Payment', 'n.', 'The act of paying, or giving compensation; the discharge\n   of a debt or an obligation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Payment', 'n.', 'That which is paid; the thing given in discharge of a\n   debt, or an obligation, or in fulfillment of a promise; reward;\n   recompense; requital; return.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Payment', 'n.', 'Punishment; chastisement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Payn', 'n.', 'Bread. Having');
  INSERT INTO words (word, word_type, description) VALUES  (    'Payndemain', 'n.', 'The finest and whitest bread made in the Middle Ages;\n   -- called also paynemain, payman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paynim', 'n. & a.', 'See Painim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paynize', 'v. t.', 'To treat or preserve, as wood, by a process resembling\n   kyanizing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Payor', 'n.', 'See Payer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Payse', 'v. t.', 'To poise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Paytine', 'n.', 'An alkaloid obtained from a white bark resembling that of\n   the cinchona, first brought from Payta, in Peru.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pea', 'n.', 'The sliding weight on a steelyard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pea', 'n.', 'See Peak, n., 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peas', 'pl. ', 'of Pea');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pease', 'pl. ', 'of Pea');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pea', 'n.', 'A plant, and its fruit, of the genus Pisum, of many varieties,\n   much cultivated for food. It has a papilionaceous flower, and the\n   pericarp is a legume, popularly called a pod.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pea', 'n.', 'A name given, especially in the Southern States, to the seed\n   of several leguminous plants (species of Dolichos, Cicer, Abrus, etc.)\n   esp. those having a scar (hilum) of a different color from the rest of\n   the seed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peabird', 'n.', 'The wryneck; -- so called from its note.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peabody bird', '', 'An American sparrow (Zonotrichia albicollis) having a\n   conspicuous white throat. The name is imitative of its note. Called\n   also White-throated sparrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peace', 'v.', 'A state of quiet or tranquillity; freedom from disturbance\n   or agitation; calm; repose');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peace', 'v.', 'Exemption from, or cessation of, war with public enemies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peace', 'v.', 'Public quiet, order, and contentment in obedience to law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peace', 'v.', 'Exemption from, or subjection of, agitating passions;\n   tranquillity of mind or conscience.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peace', 'v.', 'Reconciliation; agreement after variance; harmony; concord.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peace', 'v. t. & i.', 'To make or become quiet; to be silent; to stop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peaceable', 'a.', 'Begin in or at peace; tranquil; quiet; free from, or not\n   disposed to, war, disorder, or excitement; not quarrelsome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peacebreaker', 'n.', 'One who disturbs the public peace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peaceful', 'a.', 'Possessing or enjoying peace; not disturbed by war,\n   tumult, agitation, anxiety, or commotion; quiet; tranquil; as, a\n   peaceful time; a peaceful country; a peaceful end.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peaceful', 'a.', 'Not disposed or tending to war, tumult or agitation;\n   pacific; mild; calm; peaceable; as, peaceful words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peaceless', 'a.', 'Without peace; disturbed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peacemaker', 'n.', 'One who makes peace by reconciling parties that are at\n   variance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peach', 'v. t.', 'To accuse of crime; to inform against.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peach', 'v. i.', 'To turn informer; to betray ones accomplice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peach', 'n.', 'A well-known high-flavored juicy fruit, containing one or\n   two seeds in a hard almond-like endocarp or stone; also, the tree which\n   bears it (Prunus, / Amygdalus Persica). In the wild stock the fruit is\n   hard and inedible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peach-colored', 'a.', 'Of the color of a peach blossom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peacher', 'n.', 'One who peaches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peachick', 'n.', 'The chicken of the peacock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peachy', 'a.', 'Resembling a peach or peaches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peacock', 'n.', 'The male of any pheasant of the genus Pavo, of which at\n   least two species are known, native of Southern Asia and the East\n   Indies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peacock', 'n.', 'In common usage, the species in general or collectively; a\n   peafowl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peafowl', 'n.', 'The peacock or peahen; any species of Pavo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peage', 'n.', 'See Paage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peagrit', 'n.', 'A coarse pisolitic limestone. See Pisolite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peahen', 'n.', 'The hen or female peafowl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pea-jacket', 'n.', 'A thick loose woolen jacket, or coat, much worn by\n   sailors in cold weather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peak', 'n.', 'A point; the sharp end or top of anything that terminates in\n   a point; as, the peak, or front, of a cap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peak', 'n.', 'The top, or one of the tops, of a hill, mountain, or range,\n   ending in a point; often, the whole hill or mountain, esp. when\n   isolated; as, the Peak of Teneriffe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peak', 'n.', 'The upper aftermost corner of a fore-and-aft sail; -- used in\n   many combinations; as, peak-halyards, peak-brails, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peak', 'n.', 'The narrow part of a vessels bow, or the hold within it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peak', 'n.', 'The extremity of an anchor fluke; the bill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peaked', 'imp. & p. p.', 'of Peak');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peaking', 'p. pr. & vb. n.', 'of Peak');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peak', 'v. i.', 'To rise or extend into a peak or point; to form, or appear\n   as, a peak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peak', 'v. i.', 'To acquire sharpness of figure or features; hence, to look\n   thin or sicky.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peak', 'v. i.', 'To pry; to peep slyly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peak', 'v. t.', 'To raise to a position perpendicular, or more nearly so;\n   as, to peak oars, to hold them upright; to peak a gaff or yard, to set\n   it nearer the perpendicular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peaked', 'a.', 'Pointed; ending in a point; as, a peaked roof.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peaked', 'a.', 'Sickly; not robust.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peaking', 'a.', 'Mean; sneaking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peaking', 'a.', 'Pining; sickly; peakish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peakish', 'a.', 'Of or relating to a peak; or to peaks; belonging to a\n   mountainous region.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peakish', 'a.', 'Having peaks; peaked.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peakish', 'a.', 'Having features thin or sharp, as from sickness; hence,\n   sickly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peaky', 'a.', 'Having a peak or peaks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peaky', 'a.', 'Sickly; peaked.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peal', 'n.', 'A small salmon; a grilse; a sewin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peal', 'v. i.', 'To appeal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peal', 'n.', 'A loud sound, or a succession of loud sounds, as of bells,\n   thunder, cannon, shouts, of a multitude, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peal', 'n.', 'A set of bells tuned to each other according to the diatonic\n   scale; also, the changes rung on a set of bells.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pealed', 'imp. & p. p.', 'of Peal');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pealing', 'p. pr. & vb. n.', 'of Peal');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peal', 'v. i.', 'To utter or give out loud sounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peal', 'v. i.', 'To resound; to echo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peal', 'v. t.', 'To utter or give forth loudly; to cause to give out loud\n   sounds; to noise abroad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peal', 'v. t.', 'To assail with noise or loud sounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peal', 'v. t.', 'To pour out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pean', 'n.', 'One of the furs, the ground being sable, and the spots or\n   tufts or.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pean', 'n.', 'A song of praise and triumph. See Paean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peanism', 'n.', 'The song or shout of praise, of battle, or of triumph.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peanut', 'n.', 'The fruit of a trailing leguminous plant (Arachis\n   hypogaea); also, the plant itself, which is widely cultivated for its\n   fruit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pear', 'n.', 'The fleshy pome, or fruit, of a rosaceous tree (Pyrus\n   communis), cultivated in many varieties in temperate climates; also,\n   the tree which bears this fruit. See Pear family, below.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pearch', 'n.', 'See Perch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pearl', 'n.', 'A fringe or border.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pearl', 'v. t. ', 'To fringe; to border.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pearl', 'n.', 'A shelly concretion, usually rounded, and having a brilliant\n   luster, with varying tints, found in the mantle, or between the mantle\n   and shell, of certain bivalve mollusks, especially in the pearl oysters\n   and river mussels, and sometimes in certain univalves. It is usually\n   due to a secretion of shelly substance around some irritating foreign\n   particle. Its substance is the same as nacre, or mother-of-pearl.\n   Pearls which are round, or nearly round, and of fine luster, are highly\n   esteemed as jewels, and compare in value with the precious stones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pearl', 'n.', 'Hence, figuratively, something resembling a pearl; something\n   very precious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pearl', 'n.', 'Nacre, or mother-of-pearl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pearl', 'n.', 'A fish allied to the turbot; the brill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pearl', 'n.', 'A light-colored tern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pearl', 'n.', 'One of the circle of tubercles which form the bur on a\n   deers antler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pearl', 'n.', 'A whitish speck or film on the eye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pearl', 'n.', 'A capsule of gelatin or similar substance containing some\n   liquid for medicinal application, as ether.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pearl', 'n.', 'A size of type, between agate and diamond.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pearl', 'a.', 'Of or pertaining to pearl or pearls; made of pearls, or of\n   mother-of-pearl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pearl', 'v. t.', 'To set or adorn with pearls, or with mother-of-pearl.\n   Used also figuratively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pearl', 'v. t.', 'To cause to resemble pearls; to make into small round\n   grains; as, to pearl barley.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pearl', 'v. i.', 'To resemble pearl or pearls.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pearl', 'v. i.', 'To give or hunt for pearls; as, to go pearling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pearlaceous', 'a.', 'Resembling pearl or mother-of-pearl; pearly in quality\n   or appearance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pearlash', 'n.', 'A white amorphous or granular substance which consists\n   principally of potassium carbonate, and has a strong alkaline reaction.\n   It is obtained by lixiviating wood ashes, and evaporating the lye, and\n   has been an important source of potassium compounds. It is used in\n   making soap, glass, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pearl-eyed', 'a.', 'Having a pearly speck in the eye; afflicted with the\n   cataract.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pearlfish', 'n.', 'Any fish whose scales yield a pearl-like pigment used in\n   manufacturing artificial pearls, as the bleak, and whitebait.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pearlins', 'n. pl.', 'Alt. of Pearlings');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pearlings', 'n. pl.', 'A kind of lace of silk or thread.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pearlite', 'n.', 'Alt. of Pearlstone');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pearlstone', 'n.', 'A glassy volcanic rock of a grayish color and pearly\n   luster, often having a spherulitic concretionary structure due to the\n   curved cracks produced by contraction in cooling. See Illust. under\n   Perlitic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pearlwort', 'n.', 'A name given to several species of Sagina, low and\n   inconspicuous herbs of the Chickweed family.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pearly', 'a.', 'Containing pearls; abounding with, or yielding, pearls; as,\n   pearly shells.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pearly', 'a.', 'Resembling pearl or pearls; clear; pure; transparent;\n   iridescent; as, the pearly dew or flood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pearmain', 'n.', 'The name of several kinds of apples; as, the blue\n   pearmain, winter pearmain, and red pearmain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pear-shaped', 'a.', 'Of the form of a pear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peart', 'a.', 'Active; lively; brisk; smart; -- often applied to\n   convalescents; as, she is quite peart to-day.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peasant', 'n.', 'A countryman; a rustic; especially, one of the lowest\n   class of tillers of the soil in European countries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peasant', 'a.', 'Rustic, rural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peasantlike', 'a.', 'Rude; clownish; illiterate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peasantly', 'a.', 'Peasantlike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peasantry', 'n.', 'Peasants, collectively; the body of rustics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peasantry', 'n.', 'Rusticity; coarseness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peascod', 'n.', 'The legume or pericarp, or the pod, of the pea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peases', 'pl. ', 'of Pease');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peasen', 'pl. ', 'of Pease');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pease', 'n.', 'A pea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pease', 'n.', 'A plural form of Pea. See the Note under Pea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peastone', 'n.', 'Pisolite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peasweep', 'n.', 'The pewit, or lapwing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peasweep', 'n.', 'The greenfinch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peat', 'n.', 'A small person; a pet; -- sometimes used contemptuously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peat', 'n.', 'A substance of vegetable origin, consisting of roots and\n   fibers, moss, etc., in various stages of decomposition, and found, as a\n   kind of turf or bog, usually in low situations, where it is always more\n   or less saturated with water. It is often dried and used for fuel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peaty', 'a.', 'Composed of peat; abounding in peat; resembling peat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peba', 'n.', 'An armadillo (Tatusia novemcincta) which is found from Texas\n   to Paraguay; -- called also tatouhou.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pebble', 'n.', 'A small roundish stone or bowlder; especially, a stone worn\n   and rounded by the action of water; a pebblestone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pebble', 'n.', 'Transparent and colorless rock crystal; as, Brazilian\n   pebble; -- so called by opticians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pebbled', 'imp. & p. p.', 'of Pebble');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pebbling', 'p. pr. & vb. n.', 'of Pebble');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pebble', 'v. t.', 'To grain (leather) so as to produce a surface covered\n   with small rounded prominences.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pebbled', 'a.', 'Abounding in pebbles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pebblestone', '', 'A pebble; also, pebbles collectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pebbly', 'a.', 'Full of pebbles; pebbled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pebrine', 'n.', 'An epidemic disease of the silkworm, characterized by the\n   presence of minute vibratory corpuscles in the blood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pecan', 'n.', 'A species of hickory (Carya olivaeformis), growing in North\n   America, chiefly in the Mississippi valley and in Texas, where it is\n   one of the largest of forest trees; also, its fruit, a smooth, oblong\n   nut, an inch or an inch and a half long, with a thin shell and\n   well-flavored meat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pecary', 'n.', 'See Peccary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peccability', 'n.', 'The state or quality of being peccable; lability to\n   sin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peccable', 'a.', 'Liable to sin; subject to transgress the divine law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peccadillos', 'pl. ', 'of Peccadillo');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peccadillo', 'n.', 'A slight trespass or offense; a petty crime or fault.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peccancy', 'n.', 'The quality or state of being peccant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peccancy', 'n.', 'A sin; an offense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peccant', 'a.', 'Sinning; guilty of transgression; criminal; as, peccant\n   angels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peccant', 'a.', 'Morbid; corrupt; as, peccant humors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peccant', 'a.', 'Wrong; defective; faulty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peccant', 'n.', 'An offender.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peccantly', 'adv.', 'In a peccant manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peccaries', 'pl. ', 'of Peccary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peccary', 'n.', 'A pachyderm of the genus Dicotyles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peccavi', '', 'I have sinned; -- used colloquially to express confession or\n   acknowledgment of an offense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pecco', 'n.', 'See Pekoe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peck', 'n.', 'The fourth part of a bushel; a dry measure of eight quarts;\n   as, a peck of wheat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peck', 'n.', 'A great deal; a large or excessive quantity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pecked', 'imp. & p. p.', 'of Peck');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pecking', 'p. pr. & vb. n.', 'of Peck');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peck', 'v.', 'To strike with the beak; to thrust the beak into; as, a bird\n   pecks a tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peck', 'v.', 'Hence: To strike, pick, thrust against, or dig into, with a\n   pointed instrument; especially, to strike, pick, etc., with repeated\n   quick movements.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peck', 'v.', 'To seize and pick up with the beak, or as with the beak; to\n   bite; to eat; -- often with up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peck', 'v.', 'To make, by striking with the beak or a pointed instrument;\n   as, to peck a hole in a tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peck', 'v. i.', 'To make strokes with the beak, or with a pointed\n   instrument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peck', 'v. i.', 'To pick up food with the beak; hence, to eat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peck', 'n.', 'A quick, sharp stroke, as with the beak of a bird or a\n   pointed instrument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pecker', 'n.', 'One who, or that which, pecks; specif., a bird that pecks\n   holes in trees; a woodpecker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pecker', 'n.', 'An instrument for pecking; a pick.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peckish', 'a.', 'Inclined to eat; hungry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peckled', 'a.', 'Speckled; spotted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pecopteris', 'n.', 'An extensive genus of fossil ferns; -- so named from\n   the regular comblike arrangement of the leaflets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pecora', 'n. pl.', 'An extensive division of ruminants, including the\n   antelopes, deer, and cattle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pectate', 'n.', 'A salt of pectic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pecten', 'n.', 'A vascular pigmented membrane projecting into the vitreous\n   humor within the globe of the eye in birds, and in many reptiles and\n   fishes; -- also called marsupium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pecten', 'n.', 'The pubic bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pecten', 'n.', 'Any species of bivalve mollusks of the genus Pecten, and\n   numerous allied genera (family Pectinidae); a scallop. See Scallop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pecten', 'n.', 'The comb of a scorpion. See Comb, 4 (b).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pectic', 'a.', 'Of or pertaining to pectin; specifically, designating an\n   acid obtained from ordinary vegetable jelly (pectin) as an amorphous\n   substance, tough and horny when dry, but gelatinous when moist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pectin', 'n.', 'One of a series of carbohydrates, commonly called vegetable\n   jelly, found very widely distributed in the vegetable kingdom,\n   especially in ripe fleshy fruits, as apples, cranberries, etc. It is\n   extracted as variously colored, translucent substances, which are\n   soluble in hot water but become viscous on cooling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pectinal', 'a.', 'Of or pertaining to a comb; resembling a comb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pectinal', 'n.', 'A fish whose bone/ resemble comb teeth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pectinate', 'a.', 'Alt. of Pectinated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pectinated', 'a.', 'Resembling the teeth of a comb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pectinated', 'a.', 'Having very narrow, close divisions, in arrangement and\n   regularity resembling those of a comb; comblike; as, a pectinate leaf;\n   pectinated muscles. See Illust. (e) of Antennae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pectinated', 'a.', 'Interlaced, like two combs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pectinately', 'adv.', 'In a pectinate manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pectination', 'n.', 'The state of being pectinated; that which is\n   pectinated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pectination', 'n.', 'The act of combing; the combing of the head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pectination', 'n.', 'Comblike toothing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pectineal', 'a.', 'Of or pertaining to the pecten.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pectineal', 'a.', 'Relating to, or connected with, the pubic bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pectinibranch', 'n.', 'One of the Pectinibranchiata. Also used adjectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pectinibranchiata', 'n. pl.', 'A division of Gastropoda, including those\n   that have a comblike gill upon the neck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pectinibranchiate', 'a.', 'Having pectinated gills.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pectiniform', 'a.', 'Comblike in form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pectize', 'v. i.', 'To congeal; to change into a gelatinous mass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pectolite', 'n.', 'A whitish mineral occurring in radiated or fibrous\n   crystalline masses. It is a hydrous silicate of lime and soda.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pectoral', 'a.', 'Of or pertaining to the breast, or chest; as, the\n   pectoral muscles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pectoral', 'a.', 'Relating to, or good for, diseases of the chest or lungs;\n   as, a pectoral remedy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pectoral', 'a.', 'Having the breast conspicuously colored; as, the pectoral\n   sandpiper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pectoral', 'n.', 'A covering or protecting for the breast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pectoral', 'n.', 'A breastplate, esp. that worn by the Jewish high person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pectoral', 'n.', 'A clasp or a cross worn on the breast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pectoral', 'n.', 'A medicine for diseases of the chest organs, especially\n   the lungs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pectorally', 'adv.', 'As connected with the breast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pectoriloquial', 'a.', 'Pertaining to, or of the nature of, pectoriloquy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pectoriloquism', 'n.', 'Pectoriloquy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pectoriloquous', 'a.', 'Pectoriloquial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pectoriloquy', 'n.', 'The distinct articulation of the sounds of a\n   patients voice, heard on applying the ear to the chest in\n   auscultation. It usually indicates some morbid change in the lungs or\n   pleural cavity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pectose', 'n.', 'An amorphous carbohydrate found in the vegetable kingdom,\n   esp. in unripe fruits. It is associated with cellulose, and is\n   converted into substances of the pectin group.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pectosic', 'a.', 'Of, pertaining to, resembling, or derived from, pectose;\n   specifically, designating an acid supposed to constitute largely\n   ordinary pectin or vegetable jelly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pectostraca', 'n. pl.', 'A degenerate order of Crustacea, including the\n   Rhizocephala and Cirripedia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pectous', 'a.', 'Of, pertaining to, or consisting of, pectose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pectora', 'pl. ', 'of Pectus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pectus', 'n.', 'The breast of a bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pecul', 'n.', 'See Picul.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peculated', 'imp. & p. p.', 'of Peculate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peculating', 'p. pr. & vb. n.', 'of Peculate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peculate', 'v. i.', 'To appropriate to ones own use the property of the\n   public; to steal public moneys intrusted to ones care; to embezzle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peculation', 'n.', 'The act or practice of peculating, or of defrauding the\n   public by appropriating to ones own use the money or goods intrusted\n   to ones care for management or disbursement; embezzlement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peculator', 'n.', 'One who peculates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peculiar', 'a.', 'Ones own; belonging solely or especially to an\n   individual; not possessed by others; of private, personal, or\n   characteristic possession and use; not owned in common or in\n   participation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peculiar', 'a.', 'Particular; individual; special; appropriate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peculiar', 'a.', 'Unusual; singular; rare; strange; as, the sky had a\n   peculiarappearance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peculiar', 'n.', 'That which is peculiar; a sole or exclusive property; a\n   prerogative; a characteristic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peculiar', 'n.', 'A particular parish or church which is exempt from the\n   jurisdiction of the ordinary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peculiarities', 'pl. ', 'of Peculiarity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peculiarity', 'n.', 'The quality or state of being peculiar; individuality;\n   singularity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peculiarity', 'n.', 'That which is peculiar; a special and distinctive\n   characteristic or habit; particularity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peculiarity', 'n.', 'Exclusive possession or right.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pecularized', 'imp. & p. p.', 'of Peculiarize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pecularizing', 'p. pr. & vb. n.', 'of Peculiarize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peculiarize', 'v. t.', 'To make peculiar; to set appart or assign, as an\n   exclusive possession.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peculiarly', 'adv.', 'In a peculiar manner; particulary; in a rare and\n   striking degree; unusually.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peculiarness', 'n.', 'The quality or state of being peculiar; peculiarity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peculium', 'n.', 'The saving of a son or a slave with the fathers or\n   masters consent; a little property or stock of ones own; any\n   exclusive personal or separate property.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peculium', 'n.', 'A special fund for private and personal uses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pecunial', 'a.', 'Pecuniary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pecuniarily', 'adv.', 'In a pecuniary manner; as regards money.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pecuniary', 'a.', 'Relating to money; monetary; as, a pecuniary penalty; a\n   pecuniary reward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pecunious', 'a.', 'Abounding in money; wealthy; rich.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ped', 'n.', 'A basket; a hammer; a pannier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pedage', 'n.', 'A toll or tax paid by passengers, entitling them to\n   safe-conduct and protection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pedagog', 'n.', 'Pedagogue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pedagogic', 'a.', 'See Pedagogics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pedagogic', 'a.', 'Alt. of Pedagogical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pedagogical', 'a.', 'Of or pertaining to a pedagogue; suited to, or\n   characteristic of, a pedagogue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pedagogics', 'n.', 'The science or art of teaching; the principles and\n   rules of teaching; pedagogy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pedagogism', 'n.', 'The system, occupation, character, or manner of\n   pedagogues.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pedagogue', 'n.', 'A slave who led his masters children to school, and had\n   the charge of them generally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pedagogue', 'n.', 'A teacher of children; one whose occupation is to teach\n   the young; a schoolmaster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pedagogue', 'n.', 'One who by teaching has become formal, positive, or\n   pedantic in his ways; one who has the manner of a schoolmaster; a\n   pedant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pedagogue', 'v. t.', 'To play the pedagogue toward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pedagogy', 'n.', 'Pedagogics; pedagogism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pedal', 'a.', 'Of or pertaining to the foot, or to feet, literally or\n   figuratively; specifically (Zool.), pertaining to the foot of a\n   mollusk; as, the pedal ganglion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pedal', 'a.', 'Of or pertaining to a pedal; having pedals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pedal', 'a.', 'A lever or key acted on by the foot, as in the pianoforte to\n   raise the dampers, or in the organ to open and close certain pipes; a\n   treadle, as in a lathe or a bicycle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pedal', 'a.', 'A pedal curve or surface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pedalian', 'a.', 'Relating to the foot, or to a metrical foot; pedal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pedality', 'n.', 'The act of measuring by paces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pedaneous', 'a.', 'Going on foot; pedestrian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pedant', 'n.', 'A schoolmaster; a pedagogue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pedant', 'n.', 'One who puts on an air of learning; one who makes a vain\n   display of learning; a pretender to superior knowledge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pedantic', 'a.', 'Alt. of Pedantical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pedantical', 'a.', 'Of or pertaining to a pedant; characteristic of, or\n   resembling, a pedant; ostentatious of learning; as, a pedantic writer;\n   a pedantic description; a pedantical affectation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pedantically', 'adv.', 'In a pedantic manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pedanticly', 'adv.', 'Pedantically.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pedantism', 'n.', 'The office, disposition, or act of a pedant; pedantry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pedantize', 'v. i.', 'To play the pedant; to use pedantic expressions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pedantocracy', 'n.', 'The sway of pedants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pedantry', 'n.', 'The act, character, or manners of a pedant; vain\n   ostentation of learning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pedanty', 'n.', 'An assembly or clique of pedants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pedarian', 'n.', 'One of a class eligible to the office of senator, but not\n   yet chosen, who could sit and speak in the senate, but could not vote;\n   -- so called because he might indicate his opinion by walking over to\n   the side of the party he favored when a vote was taken.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pedaries', 'pl. ', 'of Pedary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pedary', 'n.', 'A sandal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pedata', 'n. pl.', 'An order of holothurians, including those that have\n   ambulacral suckers, or feet, and an internal gill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pedate', 'a.', 'Palmate, with the lateral lobes cleft into two or more\n   segments; -- said of a leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pedatifid', 'a.', 'Cleft in a pedate manner, but having the lobes\n   distinctly connected at the base; -- said of a leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peddle', 'v. i.', 'To travel about with wares for sale; to go from place to\n   place, or from house to house, for the purpose of retailing goods; as,\n   to peddle without a license.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peddle', 'v. i.', 'To do a small business; to be busy about trifles; to\n   piddle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peddled', 'imp. & p. p.', 'of Peddle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peddling', 'p. pr. & vb. n.', 'of Peddle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peddle', 'v. t.', 'To sell from place to place; to retail by carrying\n   around from customer to customer; to hawk; hence, to retail in very\n   small quantities; as, to peddle vegetables or tinware.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peddler', 'n.', 'One who peddles; a traveling trader; one who travels\n   about, retailing small wares; a hawker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peddlery', 'n.', 'The trade, or the goods, of a peddler; hawking; small\n   retail business, like that of a peddler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peddlery', 'n.', 'Trifling; trickery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peddling', 'a.', 'Hawking; acting as a peddler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peddling', 'a.', 'Petty; insignificant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pederast', 'n.', 'One guilty of pederasty; a sodomite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pederastic', 'a.', 'Of or pertaining to pederasty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pederasty', 'n.', 'The crime against nature; sodomy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pederero', 'n.', 'A term formerly applied to a short piece of chambered\n   ordnance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pedesis', 'n.', 'Same as Brownian movement, under Brownian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pedestal', 'n.', 'The base or foot of a column, statue, vase, lamp, or the\n   like; the part on which an upright work stands. It consists of three\n   parts, the base, the die or dado, and the cornice or surbase molding.\n   See Illust. of Column.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pedestal', 'n.', 'A casting secured to the frame of a truck and forming a\n   jaw for holding a journal box.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pedestal', 'n.', 'A pillow block; a low housing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pedestal', 'n.', 'An iron socket, or support, for the foot of a brace at\n   the end of a truss where it rests on a pier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pedestaled', 'a.', 'Placed on, or supported by, a pedestal; figuratively,\n   exalted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pedestrial', 'a.', 'Of or pertaining to the feet; employing the foot or\n   feet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pedestrially', 'adv.', 'In a pedestrial manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pedestrian', 'a.', 'Going on foot; performed on foot; as, a pedestrian\n   journey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pedestrian', 'n.', 'A walker; one who journeys on foot; a foot traveler;\n   specif., a professional walker or runner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pedestrianism', 'n.', 'The act, art, or practice of a pedestrian; walking\n   or running; traveling or racing on foot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pedestrianized', 'imp. & p. p.', 'of Pedestrianize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pedestrianizing', 'p. pr. & vb. n.', 'of Pedestrianize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pedestrianize', 'v. i.', 'To practice walking; to travel on foot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pedestrious', 'a.', 'Going on foot; not winged.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pedetentous', 'a.', 'Proceeding step by step; advancing cautiously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pedi-', '', 'Alt. of Pedo-');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pedo-', '', 'Combining forms from L. pes, pedis, foot, as pedipalp,\n   pedireme, pedometer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pedial', 'a.', 'Pertaining to the foot, or to any organ called a foot;\n   pedal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pedicel', 'n.', 'A stalk which supports one flower or fruit, whether\n   solitary or one of many ultimate divisions of a common peduncle. See\n   Peduncle, and Illust. of Flower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pedicel', 'n.', 'A slender support of any special organ, as that of a\n   capsule in mosses, an air vesicle in algae, or a sporangium in ferns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pedicel', 'n.', 'A slender stem by which certain of the lower animals or\n   their eggs are attached. See Illust. of Aphis lion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pedicel', 'n.', 'The ventral part of each side of the neural arch\n   connecting with the centrum of a vertebra.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pedicel', 'n.', 'An outgrowth of the frontal bones, which supports the\n   antlers or horns in deer and allied animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pediceled', 'a.', 'Pedicellate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pedicellariae', 'pl. ', 'of Pedicellaria');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pedicellaria', 'n.', 'A peculiar forcepslike organ which occurs in large\n   numbers upon starfishes and echini. Those of starfishes have two\n   movable jaws, or blades, and are usually nearly, or quite, sessile;\n   those of echini usually have three jaws and a pedicel. See Illustration\n   in Appendix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pedicellate', 'a.', 'Having a pedicel; supported by a pedicel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pedicellina', 'n.', 'A genus of Bryozoa, of the order Entoprocta, having a\n   bell-shaped body supported on a slender pedicel. See Illust. under\n   Entoprocta.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pedicle', 'n.', 'Same as Pedicel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pedicular', 'a.', 'Of or pertaining to lice; having the lousy distemper\n   (phthiriasis); lousy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pediculate', 'a.', 'Of or pertaining to the Pediculati.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pediculati', 'n. pl.', 'An order of fishes including the anglers. See\n   Illust. of Angler and Batfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pediculation', 'n.', 'Phthiriasis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pedicule', 'n.', 'A pedicel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pediculina', 'n. pl.', 'A division of parasitic hemipterous insects,\n   including the true lice. See Illust. in Appendix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pediculous', 'a.', 'Pedicular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pediculi', 'pl. ', 'of Pediculus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pediculus', 'n.', 'A genus of wingless parasitic Hemiptera, including the\n   common lice of man. See Louse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pediform', 'a.', 'Shaped like a foot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pedigerous', 'a.', 'Bearing or having feet or legs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pedigree', 'n.', 'A line of ancestors; descent; lineage; genealogy; a\n   register or record of a line of ancestors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pedigree', 'n.', 'A record of the lineage or strain of an animal, as of a\n   horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pediluvy', 'n.', 'The bathing of the feet, a bath for the feet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pedimana', 'n. pl.', 'A division of marsupials, including the opossums.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pedimane', 'n.', 'A pedimanous marsupial; an opossum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pedimanous', 'a.', 'Having feet resembling hands, or with the first toe\n   opposable, as the opossums and monkeys.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pediment', 'n.', 'Originally, in classical architecture, the triangular\n   space forming the gable of a simple roof; hence, a similar form used as\n   a decoration over porticoes, doors, windows, etc.; also, a rounded or\n   broken frontal having a similar position and use. See Temple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pedimental', 'a.', 'Of or pertaining to a pediment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pedipalp', 'n.', 'One of the Pedipalpi.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pedipalpi', 'n pl.', 'A division of Arachnida, including the whip scorpions\n   (Thelyphonus) and allied forms. Sometimes used in a wider sense to\n   include also the true scorpions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pedipalpous', 'a.', 'Pertaining to, or resembling, the pedipalps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pedipalpi', 'pl. ', 'of Pedipalpus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pedipalpus', 'n.', 'One of the second pair of mouth organs of arachnids. In\n   some they are leglike, but in others, as the scorpion, they terminate\n   in a claw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pedireme', 'n.', 'A crustacean, some of whose feet serve as oars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pedlar', 'n.', 'Alt. of Pedler');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pedler', 'n.', 'See Peddler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pedobaptism', 'n.', 'The baptism of infants or of small children.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pedobaptist', 'n.', 'One who advocates or practices infant baptism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pedomancy', 'n.', 'Divination by examining the soles of the feet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pedometer', 'n.', 'An instrument for including the number of steps in\n   walking, and so ascertaining the distance passed over. It is usually in\n   the form of a watch; an oscillating weight by the motion of the body\n   causes the index to advance a certain distance at each step.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pedometric', 'a.', 'Alt. of Pedometrical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pedometrical', 'a.', 'Pertaining to, or measured by, a pedometer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pedomotive', 'a.', 'Moved or worked by the action of the foot or feet on a\n   pedal or treadle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pedotrophy', 'n.', 'The art of nourishing children properly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pedregal', 'n.', 'A lava field.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peduncle', 'n.', 'The stem or stalk that supports the flower or fruit of a\n   plant, or a cluster of flowers or fruits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peduncle', 'n.', 'A sort of stem by which certain shells and barnacles are\n   attached to other objects. See Illust. of Barnacle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peduncle', 'n.', 'A band of nervous or fibrous matter connecting different\n   parts of the brain; as, the peduncles of the cerebellum; the peduncles\n   of the pineal gland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peduncled', 'a.', 'Having a peduncle; supported on a peduncle; pedunculate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peduncular', 'a.', 'Of or pertaining to a peduncle; growing from a\n   peduncle; as, a peduncular tendril.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pedunculata', 'n. pl.', 'A division of Cirripedia, including the stalked or\n   goose barnacles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pedunculate', 'a.', 'Alt. of Pedunculated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pedunculated', 'a.', 'Having a peduncle; growing on a peduncle; as, a\n   pedunculate flower; a pedunculate eye, as in a lobster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pee', 'n.', 'See 1st Pea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pee', 'n.', 'Bill of an anchor. See Peak, 3 (c).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peece', 'n. & v.', 'See Piece.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peechi', 'n.', 'The dauw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peek', 'v. i.', 'To look slyly, or with the eyes half closed, or through a\n   crevice; to peep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peekaboo', 'n.', 'A childs game; bopeep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peel', 'n.', 'A small tower, fort, or castle; a keep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peel', 'n.', 'A spadelike implement, variously used, as for removing loaves\n   of bread from a bakers oven; also, a T-shaped implement used by\n   printers and bookbinders for hanging wet sheets of paper on lines or\n   poles to dry. Also, the blade of an oar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peel', 'v. t.', 'To plunder; to pillage; to rob.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peeled', 'imp. & p. p.', 'of Peel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peeling', 'p. pr. & vb. n.', 'of Peel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peel', 'v. t.', 'To strip off the skin, bark, or rind of; to strip by\n   drawing or tearing off the skin, bark, husks, etc.; to flay; to\n   decorticate; as, to peel an orange.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peel', 'v. t.', 'To strip or tear off; to remove by stripping, as the skin\n   of an animal, the bark of a tree, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peel', 'v. i.', 'To lose the skin, bark, or rind; to come off, as the skin,\n   bark, or rind does; -- often used with an adverb; as, the bark peels\n   easily or readily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peel', 'n.', 'The skin or rind; as, the peel of an orange.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peele', 'n.', 'A graceful and swift South African antelope (Pelea\n   capreola). The hair is woolly, and ash-gray on the back and sides. The\n   horns are black, long, slender, straight, nearly smooth, and very\n   sharp. Called also rheeboc, and rehboc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peeler', 'n.', 'One who peels or strips.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peeler', 'n.', 'A pillager.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peeler', 'n.', 'A nickname for a policeman; -- so called from Sir Robert\n   Peel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peelhouse', 'n.', 'See 1st Peel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peen', 'n.', 'A round-edged, or hemispherical, end to the head of a hammer\n   or sledge, used to stretch or bend metal by indentation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peen', 'n.', 'The sharp-edged end of the head of a masons hammer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peen', 'v. t.', 'To draw, bend, or straighten, as metal, by blows with the\n   peen of a hammer or sledge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peenge', 'v. i.', 'To complain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peeped', 'imp. & p. p.', 'of Peep');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peeping', 'p. pr. & vb. n.', 'of Peep');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peep', 'v. i.', 'To cry, as a chicken hatching or newly hatched; to chirp;\n   to cheep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peep', 'v. i.', 'To begin to appear; to look forth from concealment; to\n   make the first appearance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peep', 'v. i.', 'To look cautiously or slyly; to peer, as through a\n   crevice; to pry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peep', 'n.', 'The cry of a young chicken; a chirp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peep', 'n.', 'First outlook or appearance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peep', 'n.', 'A sly look; a look as through a crevice, or from a place of\n   concealment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peep', 'n.', 'Any small sandpiper, as the least sandpiper (Trigna\n   minutilla).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peep', 'n.', 'The European meadow pipit (Anthus pratensis).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peeper', 'n.', 'A chicken just breaking the shell; a young bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peeper', 'n.', 'One who peeps; a prying person; a spy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peeper', 'n.', 'The eye; as, to close the peepers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peephole', 'n.', 'A hole, or crevice, through which one may peep without\n   being discovered.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peeping hole', '', 'See Peephole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peepul tree', '', 'A sacred tree (Ficus religiosa) of the Buddhists, a kind\n   of fig tree which attains great size and venerable age. See Bo tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peered', 'imp. & p. p.', 'of Peer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peering', 'p. pr. & vb. n.', 'of Peer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peer', 'v. i.', 'To come in sight; to appear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peer', 'v. i.', 'To look narrowly or curiously or intently; to peep; as,\n   the peering day.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peer', 'n.', 'One of the same rank, quality, endowments, character, etc.;\n   an equal; a match; a mate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peer', 'n.', 'A comrade; a companion; a fellow; an associate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peer', 'n.', 'A nobleman; a member of one of the five degrees of the\n   British nobility, namely, duke, marquis, earl, viscount, baron; as, a\n   peer of the realm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peer', 'v. t.', 'To make equal in rank.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peer', 'v. t.', 'To be, or to assume to be, equal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peerage', 'n.', 'The rank or dignity of a peer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peerage', 'n.', 'The body of peers; the nobility, collectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peerdom', 'n.', 'Peerage; also, a lordship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peeress', 'n.', 'The wife of a peer; a woman ennobled in her own right, or\n   by right of marriage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peerie', 'a.', 'Alt. of Peery');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peery', 'a.', 'Inquisitive; suspicious; sharp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peerless', 'a.', 'Having no peer or equal; matchless; superlative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peert', 'a.', 'Same as Peart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peerweet', 'n.', 'Same as Pewit (a & b).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peevish', 'a.', 'Habitually fretful; easily vexed or fretted; hard to\n   please; apt to complain; querulous; petulant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peevish', 'a.', 'Expressing fretfulness and discontent, or unjustifiable\n   dissatisfaction; as, a peevish answer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peevish', 'a.', 'Silly; childish; trifling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peevishly', 'adv.', 'In a peevish manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peevishness', 'n.', 'The quality of being peevish; disposition to murmur;\n   sourness of temper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peevit', 'n.', 'Alt. of Peewit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peewit', 'n.', 'See Pewit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peg', 'n.', 'A small, pointed piece of wood, used in fastening boards\n   together, in attaching the soles of boots or shoes, etc.; as, a shoe\n   peg.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peg', 'n.', 'A wooden pin, or nail, on which to hang things, as coats, etc.\n   Hence, colloquially and figuratively: A support; a reason; a pretext;\n   as, a peg to hang a claim upon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peg', 'n.', 'One of the pins of a musical instrument, on which the strings\n   are strained.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peg', 'n.', 'One of the pins used for marking points on a cribbage board.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peg', 'n.', 'A step; a degree; esp. in the slang phrase \"To take one down\n   peg.\"');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pegged', 'imp. & p. p.', 'of Peg');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pegging', 'p. pr. & vb. n.', 'of Peg');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peg', 'v. t.', 'To put pegs into; to fasten the parts of with pegs; as, to\n   peg shoes; to confine with pegs; to restrict or limit closely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peg', 'v. t.', 'To score with a peg, as points in the game; as, she pegged\n   twelwe points.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peg', 'v. i.', 'To work diligently, as one who pegs shoes; -- usually with\n   on, at, or away; as, to peg away at a task.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pegador', 'n.', 'A species of remora (Echeneis naucrates). See Remora.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pegasean', 'a.', 'Of or pertaining to Pegasus, or, figuratively, to poetry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pegasoid', 'a.', 'Like or pertaining to Pegasus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pegasus', 'n.', 'A winged horse fabled to have sprung from the body of\n   Medusa when she was slain. He is noted for causing, with a blow of his\n   hoof, Hippocrene, the inspiring fountain of the Muses, to spring from\n   Mount Helicon. On this account he is, in modern times, associated with\n   the Muses, and with ideas of poetic inspiration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pegasus', 'n.', 'A northen constellation near the vernal equinoctial point.\n   Its three brightest stars, with the brightest star of Andromeda, form\n   the square of Pegasus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pegasus', 'n.', 'A genus of small fishes, having large pectoral fins, and\n   the body covered with hard, bony plates. Several species are known from\n   the East Indies and China.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pegger', 'n.', 'One who fastens with pegs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pegging', 'n.', 'The act or process of fastening with pegs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pegm', 'n.', 'A sort of moving machine employed in the old pageants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pegmatite', 'n.', 'Graphic granite. See under Granite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pegmatite', 'n.', 'More generally, a coarse granite occurring as vein\n   material in other rocks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pegmatitic', 'a.', 'Of, pertaining to, or resembling, pegmatite; as, the\n   pegmatic structure of certain rocks resembling graphic granite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pegtatoid', 'a.', 'Resembling pegmatite; pegmatic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pegomancy', 'n.', 'Divination by fountains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pegroots', 'n.', 'Same as Setterwort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pehlevi', 'n.', 'An ancient Persian dialect in which words were partly\n   represented by their Semitic equivalents. It was in use from the 3d\n   century (and perhaps earlier) to the middle of the 7th century, and\n   later in religious writings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pein', 'n.', 'See Peen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peirameter', 'n.', 'A dynamometer for measuring the force required to draw\n   wheel carriages on roads of different constructions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peirastic', 'a.', 'Fitted for trail or test; experimental; tentative;\n   treating of attempts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peise', 'n.', 'A weight; a poise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peise', 'v. t.', 'To poise or weight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peitrel', 'n.', 'See Peytrel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pejorative', 'a.', 'Implying or imputing evil; depreciatory; disparaging;\n   unfavorable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pekan', 'n.', 'See Fisher, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pekoe', 'n.', 'A kind of black tea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pela', 'n.', 'See Wax insect, under Wax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pelage', 'n.', 'The covering, or coat, of a mammal, whether of wool, fur,\n   or hair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pelagian', 'a.', 'Of or pertaining to the sea; marine; pelagic; as,\n   pelagian shells.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pelagian', 'n.', 'A follower of Pelagius, a British monk, born in the later\n   part of the 4th century, who denied the doctrines of hereditary sin, of\n   the connection between sin and death, and of conversion through grace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pelagian', 'a.', 'Of or pertaining to Pelagius, or to his doctrines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pelagianism', 'n.', 'The doctrines of Pelagius.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pelagic', 'a.', 'Of or pertaining to the ocean; -- applied especially to\n   animals that live at the surface of the ocean, away from the coast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pelargonic', 'a.', 'Pertaining to, or designating, an organic acid (called\n   also nonoic acid) found in the leaves of the geranium (Pelargonium) and\n   allied plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pelargonium', 'n.', 'A large genus of plants of the order Geraniaceae,\n   differing from Geranium in having a spurred calyx and an irregular\n   corolla.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pelasgian', 'a.', 'Alt. of Pelasgic');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pelasgic', 'a.', 'Of or pertaining to the Pelasgians, an ancient people of\n   Greece, of roving habits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pelasgic', 'a.', 'Wandering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pelecan', 'n.', 'See Pelican.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pelecaniformes', 'n. pl.', 'Those birds that are related to the pelican;\n   the Totipalmi.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pelecoid', 'n.', 'A figure, somewhat hatched-shaped, bounded by a\n   semicircle and two inverted quadrants, and equal in area to the square\n   ABCD inclosed by the chords of the four quadrants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pelecypoda', 'n. pl.', 'Same as Lamellibranchia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pelegrine', 'a.', 'See Peregrine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pelerine', 'n.', 'A womans cape; especially, a fur cape that is longer in\n   front than behind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pelf', 'n.', 'Money; riches; lucre; gain; -- generally conveying the idea\n   of something ill-gotten or worthless. It has no plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pelfish', 'a.', 'Of or pertaining to pelf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pelfray', 'n.', 'Alt. of Pelfry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pelfry', 'n.', 'Pelf; also, figuratively, rubbish; trash.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pelican', 'n.', 'Any large webfooted bird of the genus Pelecanus, of which\n   about a dozen species are known. They have an enormous bill, to the\n   lower edge of which is attached a pouch in which captured fishes are\n   temporarily stored.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pelican', 'n.', 'A retort or still having a curved tube or tubes leading\n   back from the head to the body for continuous condensation and\n   redistillation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pelick', 'n.', 'The American coot (Fulica).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pelicoid', 'n.', 'See Pelecoid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pelicosauria', 'n. pl.', 'A suborder of Theromorpha, including terrestrial\n   reptiles from the Permian formation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peliom', 'n.', 'A variety of iolite, of a smoky blue color; pelioma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pelioma', 'n.', 'A livid ecchymosis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pelioma', 'n.', 'See Peliom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pelisse', 'n.', 'An outer garment for men or women, originally of fur, or\n   lined with fur; a ladys outer garment, made of silk or other fabric.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pell', 'v. t.', 'To pelt; to knock about.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pell', 'n.', 'A skin or hide; a pelt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pell', 'n.', 'A roll of parchment; a parchment record.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pellack', 'n.', 'A porpoise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pellage', 'n.', 'A customs duty on skins of leather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pellagra', 'n.', 'An erythematous affection of the skin, with severe\n   constitutional and nervous symptoms, endemic in Northern Italy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pellagrin', 'n.', 'One who is afficted with pellagra.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pellet', 'n.', 'A little ball; as, a pellet of wax / paper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pellet', 'n.', 'A bullet; a ball for firearms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pellet', 'v./.', 'To form into small balls.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pelleted', 'a.', 'Made of, or like, pellets; furnished with pellets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pellibranchiata', 'n. pl.', 'A division of Nudibranchiata, in which the\n   mantle itself serves as a gill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pellicle', 'n.', 'A thin skin or film.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pellicle', 'n.', 'A thin film formed on the surface of an evaporating\n   solution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pellicular', 'a.', 'Of or pertaining to a pellicle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pellile', 'n.', 'The redshank; -- so called from its note.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pellitory', 'n.', 'The common name of the several species of the genus\n   Parietaria, low, harmless weeds of the Nettle family; -- also called\n   wall pellitory, and lichwort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pellitory', 'n.', 'A composite plant (Anacyclus Pyrethrum) of the\n   Mediterranean region, having finely divided leaves and whitish flowers.\n   The root is the officinal pellitory, and is used as an irritant and\n   sialogogue. Called also bertram, and pellitory of Spain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pellitory', 'n.', 'The feverfew (Chrysanthemum Parthenium); -- so called\n   because it resembles the above.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pell-mell', 'n.', 'See Pall-mall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pellmell', 'adv.', 'In utter confusion; with confused violence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pellucid', 'a.', 'Transparent; clear; limpid; translucent; not opaque.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pellucidity', 'n.', 'Alt. of Pellucidness');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pellucidness', 'n.', 'The quality or state of being pellucid; transparency;\n   translucency; clearness; as, the pellucidity of the air.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pellucidly', 'adv.', 'In a pellucid manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pelmata', 'pl. ', 'of Pelma');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pelma', 'n.', 'The under surface of the foot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pelopium', 'n.', 'A supposed new metal found in columbite, afterwards shown\n   to be identical with columbium, or niobium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peloponnesian', 'a.', 'Of or pertaining to the Peloponnesus, or southern\n   peninsula of Greece.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peloponnesian', 'n.', 'A native or an inhabitant of the Peloponnesus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peloria', 'n.', 'Abnormal regularity; the state of certain flowers, which,\n   being naturally irregular, have become regular through a symmetrical\n   repetition of the special irregularity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peloric', 'a.', 'Abnormally regular or symmetrical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pelotage', 'n.', 'Packs or bales of Spanish wool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pelt', 'n.', 'The skin of a beast with the hair on; a raw or undressed\n   hide; a skin preserved with the hairy or woolly covering on it. See 4th\n   Fell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pelt', 'n.', 'The human skin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pelt', 'n.', 'The body of any quarry killed by the hawk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pelted', 'imp. & p. p.', 'of Pelt');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pelting', 'p. pr. & vb. n.', 'of Pelt');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pelt', 'v. t.', 'To strike with something thrown or driven; to assail with\n   pellets or missiles, as, to pelt with stones; pelted with hail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pelt', 'v. t.', 'To throw; to use as a missile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pelt', 'v. i.', 'To throw missiles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pelt', 'v. i.', 'To throw out words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pelt', 'n.', 'A blow or stroke from something thrown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peltae', 'pl. ', 'of Pelta');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pelta', 'n.', 'A small shield, especially one of an approximately elliptic\n   form, or crescent-shaped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pelta', 'n.', 'A flat apothecium having no rim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peltate', 'a.', 'Alt. of Peltated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peltated', 'a.', 'Shield-shaped; scutiform; (Bot.) having the stem or\n   support attached to the lower surface, instead of at the base or\n   margin; -- said of a leaf or other organ.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pelter', 'n.', 'One who pelts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pelter', 'n.', 'A pinchpenny; a mean, sordid person; a miser; a skinflint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peltiform', 'a.', 'Shieldlike, with the outline nearly circular; peltate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pelting', 'a.', 'Mean; paltry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peltry', 'n.', 'Pelts or skins, collectively; skins with the fur on them;\n   furs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peltryware', 'n.', 'Peltry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peludo', 'n.', 'The South American hairy armadillo (Dasypus villosus).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pelusiac', 'a.', 'Of or pertaining to Pelusium, an ancient city of Egypt;\n   as, the Pelusiac (or former eastern) outlet of the Nile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pelvic', 'a.', 'Of, pertaining to, or in the region of, the pelvis; as,\n   pelvic cellulitis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pelvimeter', 'n.', 'An instrument for measuring the dimensions of the\n   pelvis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pelvis', 'n.', 'The pelvic arch, or the pelvic arch together with the\n   sacrum. See Pelvic arch, under Pelvic, and Sacrum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pelvis', 'n.', 'The calyx of a crinoid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pemmican', 'n.', 'Among the North American Indians, meat cut in thin\n   slices, divested of fat, and dried in the sun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pemmican', 'n.', 'Meat, without the fat, cut in thin slices, dried in the\n   sun, pounded, then mixed with melted fat and sometimes dried fruit, and\n   compressed into cakes or in bags. It contains much nutriment in small\n   compass, and is of great use in long voyages of exploration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pemphigus', 'n.', 'A somewhat rare skin disease, characterized by the\n   development of blebs upon different part of the body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pen', 'n.', 'A feather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pen', 'n.', 'A wing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pen', 'n.', 'An instrument used for writing with ink, formerly made of a\n   reed, or of the quill of a goose or other bird, but now also of other\n   materials, as of steel, gold, etc. Also, originally, a stylus or other\n   instrument for scratching or graving.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pen', 'n.', 'Fig.: A writer, or his style; as, he has a sharp pen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pen', 'n.', 'The internal shell of a squid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pen', 'n.', 'A female swan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penned', 'imp. & p. p.', 'of Pen');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penning', 'p. pr. & vb. n.', 'of Pen');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pen', 'v. t.', 'To write; to compose and commit to paper; to indite; to\n   compose; as, to pen a sonnet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penned', 'imp. & p. p.', 'of Pen');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pent', '', 'of Pen');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penning', 'p. pr. & vb. n.', 'of Pen');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pen', 'n. & v.', 'To shut up, as in a pen or cage; to confine in a small\n   inclosure or narrow space; to coop up, or shut in; to inclose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pen', 'n.', 'A small inclosure; as, a pen for sheep or for pigs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penal', 'a.', 'Of or pertaining to punishment, to penalties, or to crimes\n   and offenses; pertaining to criminal jurisprudence');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penal', 'a.', 'Enacting or threatening punishment; as, a penal statue; the\n   penal code.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penal', 'a.', 'Incurring punishment; subject to a penalty; as, a penalact\n   of offense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penal', 'a.', 'Inflicted as punishment; used as a means of punishment; as,\n   a penal colony or settlement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penality', 'n.', 'The quality or state of being penal; lability to\n   punishment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penalize', 'v. t.', 'To make penal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penalize', 'v. t.', 'To put a penalty on. See Penalty, 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penally', 'adv.', 'In a penal manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penalties', 'pl. ', 'of Penalty');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penalty', 'n.', 'Penal retribution; punishment for crime or offense; the\n   suffering in person or property which is annexed by law or judicial\n   decision to the commission of a crime, offense, or trespass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penalty', 'n.', 'The suffering, or the sum to be forfeited, to which a\n   person subjects himself by covenant or agreement, in case of\n   nonfulfillment of stipulations; forfeiture; fine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penalty', 'n.', 'A handicap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penance', 'n.', 'Repentance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penance', 'n.', 'Pain; sorrow; suffering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penance', 'n.', 'A means of repairing a sin committed, and obtaining pardon\n   for it, consisting partly in the performance of expiatory rites, partly\n   in voluntary submission to a punishment corresponding to the\n   transgression. Penance is the fourth of seven sacraments in the Roman\n   Catholic Church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penanced', 'imp. & p. p.', 'of Penance');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penance', 'v. t.', 'To impose penance; to punish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penanceless', 'a.', 'Free from penance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penang nut', '', 'The betel nut.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penannular', 'a.', 'Nearly annular; having nearly the form of a ring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penary', 'a.', 'Penal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penates', 'n. pl.', 'The household gods of the ancient Romans. They\n   presided over the home and the family hearth. See Lar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penaunt', 'n.', 'A penitent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pence', 'n.', 'pl. of Penny. See Penny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pencel', 'n.', 'A small, narrow flag or streamer borne at the top of a\n   lance; -- called also pennoncel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penchant', 'n.', 'Inclination; decided taste; bias; as, a penchant for art.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penchute', 'n.', 'See Penstock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pencil', 'n.', 'A small, fine brush of hair or bristles used by painters\n   for laying on colors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pencil', 'n.', 'A slender cylinder or strip of black lead, colored chalk,\n   slate etc., or such a cylinder or strip inserted in a small wooden rod\n   intended to be pointed, or in a case, which forms a handle, -- used for\n   drawing or writing. See Graphite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pencil', 'n.', 'Hence, figuratively, an artists ability or peculiar\n   manner; also, in general, the act or occupation of the artist,\n   descriptive writer, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pencil', 'n.', 'An aggregate or collection of rays of light, especially\n   when diverging from, or converging to, a point.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pencil', 'n.', 'A number of lines that intersect in one point, the point of\n   intersection being called the pencil point.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pencil', 'n.', 'A small medicated bougie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penciled', 'imp. & p. p.', 'of Pencil');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pencilled', '', 'of Pencil');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penciling', 'p. pr. & vb. n.', 'of Pencil');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pencilling', '', 'of Pencil');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pencil', 'v. t.', 'To write or mark with a pencil; to paint or to draw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penciled', 'a.', 'Painted, drawn, sketched, or marked with a pencil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penciled', 'a.', 'Radiated; having pencils of rays.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penciled', 'a.', 'Marked with parallel or radiating lines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penciling', 'n.', 'The work of the pencil or bruch; as, delicate penciling\n   in a picture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penciling', 'n.', 'Lines of white or black paint drawn along a mortar joint\n   in a brick wall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pencillate', 'a.', 'Alt. of Pencillated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pencillated', 'a.', 'Shaped like a pencil; penicillate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pencraft', 'n.', 'Penmanship; skill in writing; chirography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pencraft', 'n.', 'The art of composing or writing; authorship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pend', 'n.', 'Oil cake; penock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pended', 'imp. & p. p.', 'of Pend');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pending', 'p. pr. & vb. n.', 'of Pend');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pend', 'v. i.', 'To hang; to depend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pend', 'v. i.', 'To be undecided, or in process of adjustment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pend', 'v. t.', 'To pen; to confine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pendant', 'n.', 'Something which hangs or depends; something suspended; a\n   hanging appendage, especially one of an ornamental character; as to a\n   chandelier or an eardrop; also, an appendix or addition, as to a book.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pendant', 'n.', 'A hanging ornament on roofs, ceilings, etc., much used in\n   the later styles of Gothic architecture, where it is of stone, and an\n   important part of the construction. There are imitations in plaster and\n   wood, which are mere decorative features.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pendant', 'n.', 'One of a pair; a counterpart; as, one vase is the pendant\n   to the other vase.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pendant', 'n.', 'A pendulum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pendant', 'n.', 'The stem and ring of a watch, by which it is suspended.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pendence', 'n.', 'Slope; inclination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pendency', 'n.', 'The quality or state of being pendent or suspended.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pendency', 'n.', 'The quality or state of being undecided, or in\n   continuance; suspense; as, the pendency of a suit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pendent', 'a.', 'Supported from above; suspended; depending; pendulous;\n   hanging; as, a pendent leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pendent', 'a.', 'Jutting over; projecting; overhanging.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pendentive', 'n.', 'The portion of a vault by means of which the square\n   space in the middle of a building is brought to an octagon or circle to\n   receive a cupola.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pendentive', 'n.', 'The part of a groined vault which is supported by, and\n   springs from, one pier or corbel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pendently', 'adv.', 'In a pendent manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pendice', 'n.', 'A sloping roof; a lean-to; a penthouse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pendicle', 'n.', 'An appendage; something dependent on another; an\n   appurtenance; a pendant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pendicler', 'n.', 'An inferior tenant; one who rents a pendicle or croft.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pending', 'a.', 'Not yet decided; in continuance; in suspense; as, a\n   pending suit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pending', 'prep.', 'During; as, pending the trail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pendragon', 'n.', 'A chief leader or a king; a head; a dictator; -- a title\n   assumed by the ancient British chiefs when called to lead other chiefs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pendular', 'a.', 'Pendulous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pendulate', 'v. i.', 'To swing as a pendulum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pendule', 'n.', 'A pendulum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penduline', 'n.', 'A European titmouse (Parus, / Aegithalus, pendulinus).\n   It is noted for its elegant pendulous purselike nest, made of the down\n   of willow trees and lined with feathers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pendulosity', 'n.', 'The state or quality of being pendulous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pendulous', 'a.', 'Depending; pendent loosely; hanging; swinging.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pendulous', 'a.', 'Wavering; unstable; doubtful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pendulous', 'a.', 'Inclined or hanging downwards, as a flower on a recurved\n   stalk, or an ovule which hangs from the upper part of the ovary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pendulously', 'adv.', 'In a pendulous manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pendulousness', 'n.', 'The quality or state of being pendulous; the state\n   of hanging loosely; pendulosity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pendulums', 'pl. ', 'of Pendulum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pendulum', 'n.', 'A body so suspended from a fixed point as to swing freely\n   to and fro by the alternate action of gravity and momentum. It is used\n   to regulate the movements of clockwork and other machinery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penelope', 'n.', 'A genus of curassows, including the guans.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penetrability', 'n.', 'The quality of being penetrable; susceptibility of\n   being penetrated, entered, or pierced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penetrable', 'a.', 'Capable of being penetrated, entered, or pierced. Used\n   also figuratively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penetrail', 'n.', 'Penetralia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penetralia', 'n. pl.', 'The recesses, or innermost parts, of any thing or\n   place, especially of a temple or palace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penetralia', 'n. pl.', 'Hidden things or secrets; privacy; sanctuary; as,\n   the sacred penetralia of the home.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penetrance', 'n.', 'Alt. of Penetrancy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penetrancy', 'n.', 'The quality or state of being penetrant; power of\n   entering or piercing; penetrating power of quality; as, the penetrancy\n   of subtile effluvia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penetrant', 'a.', 'Having power to enter or pierce; penetrating; sharp;\n   subtile; as, penetrant cold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penetrated', 'imp. & p. p.', 'of Penetrate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penetrating', 'p. pr. & vb. n.', 'of Penetrate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penetrate', 'v. t.', 'To enter into; to make way into the interior of; to\n   effect an entrance into; to pierce; as, light penetrates darkness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penetrate', 'v. t.', 'To affect profoundly through the senses or feelings;\n   to touch with feeling; to make sensible; to move deeply; as, to\n   penetrate ones heart with pity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penetrate', 'v. t.', 'To pierce into by the mind; to arrive at the inner\n   contents or meaning of, as of a mysterious or difficult subject; to\n   comprehend; to understand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penetrate', 'v. i.', 'To pass; to make way; to pierce. Also used\n   figuratively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penetrating', 'a.', 'Having the power of entering, piercing, or pervading;\n   sharp; subtile; penetrative; as, a penetrating odor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penetrating', 'a.', 'Acute; discerning; sagacious; quick to discover; as, a\n   penetrating mind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penetratingly', 'adv.', 'In a penetrating manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penetration', 'n.', 'The act or process of penetrating, piercing, or\n   entering; also, the act of mentally penetrating into, or comprehending,\n   anything difficult.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penetration', 'n.', 'Acuteness; insight; sharp discoverment; sagacity; as,\n   a person of singular penetration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penetrative', 'a.', 'Tending to penetrate; of a penetrating quality;\n   piercing; as, the penetrative sun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penetrative', 'a.', 'Having the power to affect or impress the mind or\n   heart; impressive; as, penetrative shame.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penetrative', 'a.', 'Acute; discerning; sagacious; as, penetrative wisdom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penetrativeness', 'n.', 'The quality of being penetrative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penfish', 'n.', 'A squid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penfold', 'n.', 'See Pinfold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pengolin', 'n.', 'The pangolin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penguin', 'n.', 'Any bird of the order Impennes, or Ptilopteri. They are\n   covered with short, thick feathers, almost scalelike on the wings,\n   which are without true quills. They are unable to fly, but use their\n   wings to aid in diving, in which they are very expert. See King\n   penguin, under Jackass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penguin', 'n.', 'The egg-shaped fleshy fruit of a West Indian plant\n   (Bromelia Pinguin) of the Pineapple family; also, the plant itself,\n   which has rigid, pointed, and spiny-toothed leaves, and is used for\n   hedges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penguinery', 'n.', 'A breeding place, or rookery, of penguins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penholder', 'n.', 'A handle for a pen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penhouse', 'n.', 'A penthouse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penible', 'a.', 'Painstaking; assidous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penicil', 'n.', 'A tent or pledget for wounds or ulcers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penicillate', 'a.', 'Having the form of a pencil; furnished with a pencil\n   of fine hairs; ending in a tuft of hairs like a camels-hair brush, as\n   the stigmas of some grasses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penicilliform', 'a.', 'Penicillate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peninsula', 'n.', 'A portion of land nearly surrounded by water, and\n   connected with a larger body by a neck, or isthmus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peninsular', 'a.', 'Of or pertaining to a peninsula; as, a peninsular form;\n   peninsular people; the peninsular war.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peninsulated', 'imp. & p. p.', 'of Peninsulate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peninsulating', 'p. pr. & vb. n.', 'of Peninsulate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peninsulate', 'v. t.', 'To form into a peninsula.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penis', 'n.', 'The male member, or organ of generation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penitence', 'n.', 'The quality or condition of being penitent; the\n   disposition of a penitent; sorrow for sins or faults; repentance;\n   contrition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penitencer', 'n.', 'A priest who heard confession and enjoined penance in\n   extraordinary cases.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penitency', 'n.', 'Penitence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penitent', 'a.', 'Feeling pain or sorrow on account of sins or offenses;\n   repentant; contrite; sincerely affected by a sense of guilt, and\n   resolved on amendment of life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penitent', 'a.', 'Doing penance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penitent', 'n.', 'One who repents of sin; one sorrowful on account of his\n   transgressions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penitent', 'n.', 'One under church censure, but admitted to penance; one\n   undergoing penance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penitent', 'n.', 'One under the direction of a confessor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penitential', 'a.', 'Of or pertaining to penitence, or to penance;\n   expressing penitence; of the nature of penance; as, the penitential\n   book; penitential tears.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penitential', 'n.', 'A book formerly used by priests hearing confessions,\n   containing rules for the imposition of penances; -- called also\n   penitential book.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penitentially', 'adv.', 'In a penitential manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penitentiary', 'a.', 'Relating to penance, or to the rules and measures of\n   penance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penitentiary', 'a.', 'Expressive of penitence; as, a penitentiary letter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penitentiary', 'a.', 'Used for punishment, discipline, and reformation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penitentiaries', 'pl. ', 'of Penitentiary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penitentiary', 'n.', 'One who prescribes the rules and measures of penance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penitentiary', 'n.', 'One who does penance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penitentiary', 'n.', 'A small building in a monastery where penitents\n   confessed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penitentiary', 'n.', 'That part of a church to which penitents were\n   admitted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penitentiary', 'n.', 'An office of the papal court which examines cases of\n   conscience, confession, absolution from vows, etc., and delivers\n   decisions, dispensations, etc. Its chief is a cardinal, called the\n   Grand Penitentiary, appointed by the pope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penitentiary', 'n.', 'An officer in some dioceses since A. D. 1215, vested\n   with power from the bishop to absolve in cases reserved to him.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penitentiary', 'n.', 'A house of correction, in which offenders are\n   confined for punishment, discipline, and reformation, and in which they\n   are generally compelled to labor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penitentiaryship', 'n.', 'The office or condition of a penitentiary of the\n   papal court.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penitently', 'adv.', 'In a penitent manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penk', 'n.', 'A minnow. See Pink, n., 4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penknives', 'pl. ', 'of Penknife');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penknife', 'n.', 'A small pocketknife; formerly, a knife used for making\n   and mending quill pens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penmen', 'pl. ', 'of Penman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penman', 'n.', 'One who uses the pen; a writer; esp., one skilled in the\n   use of the pen; a calligrapher; a writing master.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penman', 'n.', 'An author; a composer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penmanship', 'n.', 'The use of the pen in writing; the art of writing;\n   style or manner of writing; chirography; as, good or bad penmanship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pennae', 'pl. ', 'of Penna');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penna', 'n.', 'A perfect, or normal, feather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pennaceous', 'a.', 'Like or pertaining to a normal feather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pennach', 'n.', 'A bunch of feathers; a plume.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pennached', 'a.', 'Variegated; striped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pennage', 'n.', 'Feathery covering; plumage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pennant', 'n.', 'A small flag; a pennon. The narrow, / long, pennant\n   (called also whip or coach whip) is a long, narrow piece of bunting,\n   carried at the masthead of a government vessel in commission. The board\n   pennant is an oblong, nearly square flag, carried at the masthead of a\n   commodores vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pennant', 'n.', 'A rope or strap to which a purchase is hooked.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pennate', 'a.', 'Alt. of Pennated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pennated', 'a.', 'Winged; plume-shaped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pennated', 'a.', 'Same as Pinnate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pennatulae', 'pl. ', 'of Pennatula');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pennatulas', 'pl. ', 'of Pennatula');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pennatula', 'n.', 'Any one of numerous species of Pennatula, Pteroides, and\n   allied genera of Alcyonaria, having a featherlike form; a sea-pen. The\n   zooids are situated along one edge of the side branches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pennatulacea', 'n. pl.', 'A division of alcyonoid corals, including the\n   seapens and related kinds. They are able to move about by means of the\n   hollow muscular peduncle, which also serves to support them upright in\n   the mud. See Pennatula, and Illust. under Alcyonaria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penned', 'a.', 'Winged; having plumes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penned', 'a.', 'Written with a pen; composed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penner', 'n.', 'One who pens; a writer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penner', 'n.', 'A case for holding pens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penniform', 'a.', 'Having the form of a feather or plume.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pennigerous', 'a.', 'Bearing feathers or quills.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penniless', 'a.', 'Destitute of money; impecunious; poor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penninerved', 'a.', 'Pinnately veined or nerved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pennipotent', 'a.', 'Strong of wing; strong on the wing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pennon', 'n.', 'A wing; a pinion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pennon', 'n.', 'A pennant; a flag or streamer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pennoncel', 'n.', 'Alt. of Pennoncelle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pennoncelle', 'n.', 'See Pencel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penny', 'a.', 'Denoting pound weight for one thousand; -- used in\n   combination, with respect to nails; as, tenpenny nails, nails of which\n   one thousand weight ten pounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pennies', 'pl. ', 'of Penny');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pence', 'pl. ', 'of Penny');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penny', 'n.', 'An English coin, formerly of copper, now of bronze, the\n   twelfth part of an English shilling in account value, and equal to four\n   farthings, or about two cents; -- usually indicated by the abbreviation\n   d. (the initial of denarius).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penny', 'n.', 'Any small sum or coin; a groat; a stiver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penny', 'n.', 'Money, in general; as, to turn an honest penny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penny', 'n.', 'See Denarius.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penny', 'a.', 'Worth or costing one penny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penny-a-liner', 'n.', 'One who furnishes matter to public journals at so\n   much a line; a poor writer for hire; a hack writer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pennyroyal', 'n.', 'An aromatic herb (Mentha Pulegium) of Europe; also, a\n   North American plant (Hedeoma pulegioides) resembling it in flavor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pennyweight', 'n.', 'A troy weight containing twenty-four grains, or the\n   twentieth part of an ounce; as, a pennyweight of gold or of arsenic. It\n   was anciently the weight of a silver penny, whence the name.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pennywort', 'n.', 'A European trailing herb (Linaria Cymbalaria) with\n   roundish, reniform leaves. It is often cultivated in hanging baskets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pennyworth', 'n.', 'A pennys worth; as much as may be bought for a penny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pennyworth', 'n.', 'Hence: The full value of ones penny expended; due\n   return for money laid out; a good bargain; a bargain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pennyworth', 'n.', 'A small quantity; a trifle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penock', 'n.', 'See Pend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penological', 'a.', 'Of or pertaining to penology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penologist', 'n.', 'One versed in, or a student of, penology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penology', 'n.', 'The science or art of punishment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penrack', 'n.', 'A rack for pens not in use.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pens', 'n.', 'pl. of Penny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pensative', 'a.', 'Pensive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pensel', 'n.', 'A pencel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pensible', 'a.', 'Held aloft.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pensile', 'a.', 'Hanging; suspended; pendent; pendulous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pensileness', 'n.', 'State or quality of being pensile; pendulousness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pension', 'n.', 'A payment; a tribute; something paid or given.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pension', 'n.', 'A stated allowance to a person in consideration of past\n   services; payment made to one retired from service, on account of age,\n   disability, or other cause; especially, a regular stipend paid by a\n   government to retired public officers, disabled soldiers, the families\n   of soldiers killed in service, or to meritorious authors, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pension', 'n.', 'A certain sum of money paid to a clergyman in lieu of\n   tithes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pension', 'n.', 'A boarding house or boarding school in France, Belgium,\n   Switzerland, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pensioned', 'imp. & p. p.', 'of Pension');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pensioning', 'p. pr. & vb. n.', 'of Pension');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pension', 'v. t.', 'To grant a pension to; to pay a regular stipend to; in\n   consideration of service already performed; -- sometimes followed by\n   off; as, to pension off a servant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pensionary', 'a.', 'Maintained by a pension; receiving a pension; as,\n   pensionary spies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pensionary', 'a.', 'Consisting of a pension; as, a pensionary provision for\n   maintenance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pensionaries', 'pl. ', 'of Pensionary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pensionary', 'n.', 'One who receives a pension; a pensioner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pensionary', 'n.', 'One of the chief magistrates of towns in Holland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pensioner', 'n.', 'One in receipt of a pension; hence, figuratively, a\n   dependent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pensioner', 'n.', 'One of an honorable band of gentlemen who attend the\n   sovereign of England on state occasions, and receive an annual pension,\n   or allowance, of 150 and two horses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pensioner', 'n.', 'In the university of Cambridge, England, one who pays\n   for his living in commons; -- corresponding to commoner at Oxford.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pensive', 'a.', 'Thoughtful, sober, or sad; employed in serious reflection;\n   given to, or favorable to, earnest or melancholy musing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pensive', 'a.', 'Expressing or suggesting thoughtfulness with sadness; as,\n   pensive numbers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pensived', 'a.', 'Made pensive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pensively', 'adv.', 'In a pensive manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pensiveness', 'n.', 'The state of being pensive; serious thoughtfulness;\n   seriousness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penstock', 'n.', 'A close conduit or pipe for conducting water, as, to a\n   water wheel, or for emptying a pond, or for domestic uses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penstock', 'n.', 'The barrel of a wooden pump.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pent', 'v. t.', 'Penned or shut up; confined; -- often with up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penta-', '', 'A combining form denoting five; as, pentacapsular; pentagon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penta-', '', 'Denoting the degree of five, either as regards quality,\n   property, or composition; as, pentasulphide; pentoxide, etc. Also used\n   adjectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentabasic', 'a.', 'Capable of uniting with five molecules of a monacid\n   base; having five acid hydrogen atoms capable of substitution by a\n   basic radical; -- said of certain acids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentacapsular', 'a.', 'Having five capsules.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentachenium', 'n.', 'A dry fruit composed of five carpels, which are\n   covered by an epigynous calyx and separate at maturity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentachloride', 'n.', 'A chloride having five atoms of chlorine in each\n   molecule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentachord', 'n.', 'An ancient instrument of music with five strings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentachord', 'n.', 'An order or system of five sounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentacid', 'a.', 'Capable of neutralizing, or combining with, five\n   molecules of a monobasic acid; having five hydrogen atoms capable of\n   substitution by acid residues; -- said of certain complex bases.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentacle', 'n.', 'A figure composed of two equilateral triangles\n   intersecting so as to form a six-pointed star, -- used in early\n   ornamental art, and also with superstitious import by the astrologers\n   and mystics of the Middle Ages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentacoccous', 'a.', 'Composed of five united carpels with one seed in\n   each, as certain fruits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentaconter', 'n.', 'See Penteconter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentacrinin', 'n.', 'A red and purple pigment found in certain crinoids of\n   the genus Pentacrinus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentacrinite', 'n.', 'Any species of Pentacrinus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentacrinoid', 'n.', 'An immature comatula when it is still attached by a\n   stem, and thus resembles a Pentacrinus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentacrinus', 'n.', 'A genus of large, stalked crinoids, of which several\n   species occur in deep water among the West Indies and elsewhere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentacra', 'pl. ', 'of Pentacron');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentacrons', 'pl. ', 'of Pentacron');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentacron', 'n.', 'A solid having five summits or angular points.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentacrostic', 'n.', 'A set of verses so disposed that the name forming the\n   subject of the acrostic occurs five times -- the whole set of verses\n   being divided into five different parts from top to bottom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentad', 'n.', 'Any element, atom, or radical, having a valence of five, or\n   which can be combined with, substituted for, or compared with, five\n   atoms of hydrogen or other monad; as, nitrogen is a pentad in the\n   ammonium compounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentad', 'a.', 'Having the valence of a pentad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentadactyl', 'a.', 'Alt. of Pentadactyle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentadactyle', 'a.', 'Having five digits to the hand or foot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentadactyle', 'a.', 'Having five appendages resembling fingers or toes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentadactyloid', 'a.', 'Having the form of, or a structure modified from, a\n   pentadactyl limb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentadecane', 'n.', 'A hydrocarbon of the paraffin series, (C15H32) found\n   in petroleum, tar oil, etc., and obtained as a colorless liquid; -- so\n   called from the fifteen carbon atoms in the molecule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentadecatoic', 'a.', 'Of, pertaining to, or derived from, pentadecane, or\n   designating an acid related to it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentadecylic', 'a.', 'Same as Quindecylic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentadelphous', 'a.', 'Having the stamens arranged in five clusters, those\n   of each cluster having their filaments more or less united, as the\n   flowers of the linden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentafid', 'a.', 'Divided or cleft into five parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentaglot', 'n.', 'A work in five different tongues.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentagon', 'n.', 'A plane figure having five angles, and, consequently,\n   five sides; any figure having five angles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentagonal', 'a.', 'Having five corners or angles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentagonally', 'adv.', 'In the form of a pentagon; with five angles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentagonous', 'a.', 'Pentagonal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentagram', 'n.', 'A pentacle or a pentalpha.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentagraphic', 'a.', 'Alt. of Pentagraphical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentagraphical', 'a.', 'Pantographic. See Pantograph.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentagynia', 'n. pl.', 'A Linnaean order of plants, having five styles or\n   pistils.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentagynian', 'a.', 'Alt. of Pentagynous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentagynous', 'a.', 'Of or pertaining to plants of the order Pentagyna;\n   having five styles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentahedral', 'a.', 'Having five sides; as, a pentahedral figure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentahedrical', 'a.', 'Pentahedral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentahedron', 'n.', 'A solid figure having five sides.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentahedrous', 'a.', 'Pentahedral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentail', 'n.', 'A peculiar insectivore (Ptilocercus Lowii) of Borneo; --\n   so called from its very long, quill-shaped tail, which is scaly at the\n   base and plumose at the tip.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentalpha', 'n.', 'A five-pointed star, resembling five alphas joined at\n   their bases; -- used as a symbol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentamera', 'n. pl.', 'An extensive division of Coleoptera, including those\n   that normally have five-jointed tarsi. It embraces about half of all\n   the known species of the Coleoptera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentameran', 'n.', 'One of the Pentamera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentamerous', 'a.', 'Divided into, or consisting of, five parts; also,\n   arranged in sets, with five parts in each set, as a flower with five\n   sepals, five petals, five, or twice five, stamens, and five pistils.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentamerous', 'a.', 'Belonging to the Pentamera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentamerus', 'n.', 'A genus of extinct Paleozoic brachiopods, often very\n   abundant in the Upper Silurian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentameter', 'n.', 'A verse of five feet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentameter', 'a.', 'Having five metrical feet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentamethylene', 'n.', 'A hypothetical hydrocarbon, C5H10, metameric with\n   the amylenes, and the nucleus of a large number of derivatives; -- so\n   named because regarded as composed of five methylene residues. Cf.\n   Trimethylene, and Tetramethylene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentandria', 'n. pl.', 'A Linnaean class of plants having five separate\n   stamens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentandrian', 'a.', 'Alt. of Pentandrous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentandrous', 'a.', 'Of or pertaining to the class Pentadria; having five\n   stamens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentane', 'n.', 'Any one of the three metameric hydrocarbons, C5H12, of the\n   methane or paraffin series. They are colorless, volatile liquids, two\n   of which occur in petroleum. So called because of the five carbon atoms\n   in the molecule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentangle', 'n.', 'A pentagon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentangular', 'a.', 'Having five corners or angles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentapetalous', 'a.', 'Having five petals, or flower leaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentaphyllous', 'a.', 'Having five leaves or leaflets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentapody', 'n.', 'A measure or series consisting of five feet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentaptote', 'n.', 'A noun having five cases.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentaptych', 'n.', 'A picture, or combination of pictures, consisting of a\n   centerpiece and double folding doors or wings, as for an altarpiece.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentarchy', 'n.', 'A government in the hands of five persons; five joint\n   rulers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentaspast', 'n.', 'A purchase with five pulleys.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentaspermous', 'a.', 'Containing five seeds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentastich', 'n.', 'A composition consisting of five verses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentastichous', 'a.', 'Having, or arranged in, five vertical ranks, as the\n   leaves of an apple tree or a cherry tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentastomida', 'n. pl.', 'Same as Linguatulina.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentastyle', 'a.', 'Having five columns in front; -- said of a temple or\n   portico in classical architecture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentastyle', 'n.', 'A portico having five columns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentateuch', 'n.', 'The first five books of the Old Testament,\n   collectively; -- called also the Law of Moses, Book of the Law of\n   Moses, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentateuchal', 'a.', 'Of or pertaining to the Pentateuch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentathionic', 'a.', 'Pertaining to, or designating, an acid of sulphur\n   obtained by leading hydrogen sulphide into a solution of sulphur\n   dioxide; -- so called because it contains five atoms of sulphur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentathlon', 'n.', 'A fivefold athletic performance peculiar to the great\n   national games of the Greeks, including leaping, foot racing,\n   wrestling, throwing the discus, and throwing the spear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentatomic', 'a.', 'Having five atoms in the molecule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentatomic', 'a.', 'Having five hydrogen atoms capable of substitution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentavalent', 'a.', 'Having a valence of five; -- said of certain atoms and\n   radicals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penteconter', 'n.', 'A Grecian vessel with fifty oars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentecost', 'n.', 'A solemn festival of the Jews; -- so called because\n   celebrated on the fiftieth day (seven weeks) after the second day of\n   the Passover (which fell on the sixteenth of the Jewish month Nisan);\n   -- hence called, also, the Feast of Weeks. At this festival an offering\n   of the first fruits of the harvest was made. By the Jews it was\n   generally regarded as commemorative of the gift of the law on the\n   fiftieth day after the departure from Egypt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentecost', 'n.', 'A festival of the Roman Catholic and other churches in\n   commemoration of the descent of the Holy Spirit on the apostles; which\n   occurred on the day of Pentecost; -- called also Whitsunday.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentecostal', 'a.', 'Of or pertaining to Pentecost or to Whitsuntide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentecostals', 'n. pl.', 'Offerings formerly made to the parish priest, or\n   to the mother church, at Pentecost.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentecoster', 'n.', 'An officer in the Spartan army commanding fifty men.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentecosties', 'pl. ', 'of Pentecosty');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentecosty', 'n.', 'A troop of fifty soldiers in the Spartan army; --\n   called also pentecostys.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentelic', 'a.', 'Alt. of Pentelican');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentelican', 'a.', 'Of or pertaining to Mount Pentelicus, near Athens,\n   famous for its fine white marble quarries; obtained from Mount\n   Pentelicus; as, the Pentelic marble of which the Parthenon is built.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentene', 'n.', 'Same as Amylene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penthouse', 'n.', 'A shed or roof sloping from the main wall or building,\n   as over a door or window; a lean-to. Also figuratively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penthouse', 'a.', 'Leaning; overhanging.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentice', 'n.', 'A penthouse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentile', 'n.', 'See Pantile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentine', 'n.', 'An unsaturated hydrocarbon, C5H8, of the acetylene series.\n   Same as Valerylene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentoic', 'a.', 'Pertaining to, or desingating, an acid (called also\n   valeric acid) derived from pentane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentone', 'n.', 'Same as Valylene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentoxide', 'n.', 'An oxide containing five atoms of oxygen in each\n   molecule; as, phosphorus pentoxide, P2O5.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentremite', 'n.', 'Any species of Pentremites.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentremites', 'n.', 'A genus of crinoids belonging to the Blastoidea. They\n   have five petal-like ambulacra.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentroof', 'n.', 'See Lean-to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentrough', 'n.', 'A penstock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentyl', 'n.', 'The hypothetical radical, C5H11, of pentane and certain of\n   its derivatives. Same as Amyl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pentylic', 'a.', 'Pertaining to, derived from, or containing, pentyl; as,\n   pentylic alcohol');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penuchle', 'n.', 'Alt. of Pinocle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinocle', 'n.', 'A game at cards, played with forty-eight cards, being all\n   the cards above the eight spots in two packs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penult', 'n.', 'The last syllable but one of a word; the syllable preceding\n   the final one.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penultima', 'n.', 'Same as Penult.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penultimate', 'a.', 'Last but one; as, the penultimate syllable, the last\n   syllable but one of a word.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penultimate', 'n.', 'The penult.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penumbra', 'n.', 'An incomplete or partial shadow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penumbra', 'n.', 'The shadow cast, in an eclipse, where the light is\n   partly, but not wholly, cut off by the intervening body; the space of\n   partial illumination between the umbra, or perfect shadow, on all\n   sides, and the full light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penumbra', 'n.', 'The part of a picture where the shade imperceptibly\n   blends with the light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penumbrala', '', 'Of or pertaining to a penumbra; resembling a penumbra;\n   partially illuminated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penurious', 'a.', 'Excessively sparing in the use of money; sordid; stingy;\n   miserly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penurious', 'a.', 'Not bountiful or liberal; scanty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penurious', 'a.', 'Destitute of money; suffering extreme want.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penury', 'n.', 'Absence of resources; want; privation; indigence; extreme\n   poverty; destitution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penury', 'n.', 'Penuriousness; miserliness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penwiper', 'n.', 'A cloth, or other material, for wiping off or cleaning\n   ink from a pen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penwomen', 'pl. ', 'of Penwoman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Penwoman', 'n.', 'A female writer; an authoress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peon', 'n.', 'See Poon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peon', 'n.', 'A foot soldier; a policeman; also, an office attendant; a\n   messenger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peon', 'n.', 'A day laborer; a servant; especially, in some of the Spanish\n   American countries, debtor held by his creditor in a form of qualified\n   servitude, to work out a debt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peon', 'n.', 'See 2d Pawn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peonage', 'n.', 'The condition of a peon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peonism', 'n.', 'Same as Peonage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peonies', 'pl. ', 'of Peony');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peony', 'n.', 'A plant, and its flower, of the ranunculaceous genus\n   Paeonia. Of the four or five species, one is a shrub; the rest are\n   perennial herbs with showy flowers, often double in cultivation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'People', 'n.', 'The body of persons who compose a community, tribe, nation,\n   or race; an aggregate of individuals forming a whole; a community; a\n   nation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'People', 'n.', 'Persons, generally; an indefinite number of men and women;\n   folks; population, or part of population; as, country people; --\n   sometimes used as an indefinite subject or verb, like on in French, and\n   man in German; as, people in adversity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'People', 'n.', 'The mass of comunity as distinguished from a special class;\n   the commonalty; the populace; the vulgar; the common crowd; as, nobles\n   and people.');
  INSERT INTO words (word, word_type, description) VALUES  (    'People', 'n.', 'Ones ancestors or family; kindred; relations; as, my\n   people were English.');
  INSERT INTO words (word, word_type, description) VALUES  (    'People', 'n.', 'Ones subjects; fellow citizens; companions; followers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peopled', 'imp. & p. p.', 'of People');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peopling', 'p. pr. & vb. n.', 'of People');
  INSERT INTO words (word, word_type, description) VALUES  (    'People', 'v. t.', 'To stock with people or inhabitants; to fill as with\n   people; to populate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peopled', 'a.', 'Stocked with, or as with, people; inhabited.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peopleless', 'a.', 'Destitute of people.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peopler', 'n.', 'A settler; an inhabitant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peoplish', 'a.', 'Vulgar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peorias', 'n. pl.', 'An Algonquin tribe of Indians who formerly inhabited a\n   part of Illinois.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pepastic', 'a. & n.', 'Same as Maturative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peperine', 'n.', 'Alt. of Peperino');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peperino', 'n.', 'A volcanic rock, formed by the cementing together of\n   sand, scoria, cinders, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peplis', 'n.', 'A genus of plants including water purslane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peplus', 'n.', 'An upper garment worn by Grecian and Roman women.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peplus', 'n.', 'A kind of kerchief formerly worn by Englishwomen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pepo', 'n.', 'Any fleshy fruit with a firm rind, as a pumpkin, melon, or\n   gourd. See Gourd.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pepper', 'n.', 'A well-known, pungently aromatic condiment, the dried\n   berry, either whole or powdered, of the Piper nigrum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pepper', 'n.', 'The plant which yields pepper, an East Indian woody climber\n   (Piper nigrum), with ovate leaves and apetalous flowers in spikes\n   opposite the leaves. The berries are red when ripe. Also, by extension,\n   any one of the several hundred species of the genus Piper, widely\n   dispersed throughout the tropical and subtropical regions of the earth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pepper', 'n.', 'Any plant of the genus Capsicum, and its fruit; red pepper;\n   as, the bell pepper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peppered', 'imp. & p. p.', 'of Pepper');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peppering', 'p. pr. & vb. n.', 'of Pepper');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pepper', 'v. t.', 'To sprinkle or season with pepper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pepper', 'v. t.', 'Figuratively: To shower shot or other missiles, or\n   blows, upon; to pelt; to fill with shot, or cover with bruises or\n   wounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pepper', 'v. i.', 'To fire numerous shots (at).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pepperbrand', 'n.', 'See 1st Bunt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peppercorn', 'n.', 'A dried berry of the black pepper (Piper nigrum).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peppercorn', 'n.', 'Anything insignificant; a particle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pepper dulse', '', 'A variety of edible seaweed (Laurencia pinnatifida)\n   distinguished for its pungency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pepperer', 'n.', 'A grocer; -- formerly so called because he sold pepper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peppergrass', 'n.', 'Any herb of the cruciferous genus Lepidium, especially\n   the garden peppergrass, or garden cress, Lepidium sativum; -- called\n   also pepperwort. All the species have a pungent flavor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peppergrass', 'n.', 'The common pillwort of Europe (Pilularia globulifera).\n   See Pillwort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pepperidge', 'n.', 'A North American tree (Nyssa multiflora) with very\n   tough wood, handsome oval polished leaves, and very acid berries, --\n   the sour gum, or common tupelo. See Tupelo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peppering', 'a.', 'Hot; pungent; peppery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peppermint', 'n.', 'An aromatic and pungent plant of the genus Mentha (M.\n   piperita), much used in medicine and confectionery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peppermint', 'n.', 'A volatile oil (oil of peppermint) distilled from the\n   fresh herb; also, a well-known essence or spirit (essence of\n   peppermint) obtained from it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peppermint', 'n.', 'A lozenge of sugar flavored with peppermint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pepperwort', 'n.', 'See Peppergrass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peppery', 'a.', 'Of or pertaining to pepper; having the qualities of\n   pepper; hot; pungent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peppery', 'a.', 'Fig.: Hot-tempered; passionate; choleric.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pepsin', 'n.', 'An unorganized proteolytic ferment or enzyme contained in\n   the secretory glands of the stomach. In the gastric juice it is united\n   with dilute hydrochloric acid (0.2 per cent, approximately) and the two\n   together constitute the active portion of the digestive fluid. It is\n   the active agent in the gastric juice of all animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pepsinhydrochloric', 'a.', 'Same as Peptohydrochloric.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pepsinogen', 'n.', 'The antecedent of the ferment pepsin. A substance\n   contained in the form of granules in the peptic cells of the gastric\n   glands. It is readily convertible into pepsin. Also called propepsin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peptic', 'a.', 'Relating to digestion; promoting digestion; digestive; as,\n   peptic sauces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peptic', 'a.', 'Able to digest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peptic', 'a.', 'Pertaining to pepsin; resembling pepsin in its power of\n   digesting or dissolving albuminous matter; containing or yielding\n   pepsin, or a body of like properties; as, the peptic glands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peptic', 'n.', 'An agent that promotes digestion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peptic', 'n.', 'The digestive organs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peptics', 'n.', 'The science of digestion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peptogen', 'n.', 'A substance convertible into peptone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peptogenic', 'a.', 'Same as Peptogenous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peptogenous', 'a.', 'Capable of yielding, or being converted into, peptone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peptohydrochloric', 'a.', 'Designating a hypothetical acid (called\n   peptohydrochloric acid, pepsinhydrochloric acid, and chloropeptic acid)\n   which is supposed to be formed when pepsin and dilute (0.1-0.4 per\n   cent) hydrochloric acid are mixed together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peptone', 'n.', 'The soluble and diffusible substance or substances into\n   which albuminous portions of the food are transformed by the action of\n   the gastric and pancreatic juices. Peptones are also formed from\n   albuminous matter by the action of boiling water and boiling dilute\n   acids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peptone', 'n.', 'Collectively, in a broader sense, all the products\n   resulting from the solution of albuminous matter in either gastric or\n   pancreatic juice. In this case, however, intermediate products\n   (albumose bodies), such as antialbumose, hemialbumose, etc., are mixed\n   with the true peptones. Also termed albuminose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peptonize', 'v. t.', 'To convert into peptone; to digest or dissolve by\n   means of a proteolytic ferment; as, peptonized food.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peptonoid', 'n.', 'A substance related to peptone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peptonuria', 'n.', 'The presence of peptone, or a peptonelike body, in the\n   urine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peptotoxine', 'n.', 'A toxic alkaloid found occasionally associated with\n   the peptones formed from fibrin by pepsinhydrochloric acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pequots', 'n. pl.', 'A tribe of Indians who formerly inhabited Eastern\n   Connecticut.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Per-', '', 'A prefix used to signify through, throughout, by, for, or as an\n   intensive as perhaps, by hap or chance; perennial, that lasts\n   throughout the year; perforce, through or by force; perfoliate,\n   perforate; perspicuous, evident throughout or very evident; perplex,\n   literally, to entangle very much.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Per-', '', 'Originally, denoting that the element to the name of which it\n   is prefixed in the respective compounds exercised its highest valence;\n   now, only that the element has a higher valence than in other similar\n   compounds; thus, barium peroxide is the highest oxide of barium; while\n   nitrogen and manganese peroxides, so-called, are not the highest oxides\n   of those elements.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Per', 'prep.', 'Through; by means of; through the agency of; by; for; for\n   each; as, per annum; per capita, by heads, or according to individuals;\n   per curiam, by the court; per se, by itself, of itself. Per is also\n   sometimes used with English words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peract', 'v. t.', 'To go through with; to perform.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peracute', 'a.', 'Very sharp; very violent; as, a peracute fever.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peradventure', 'adv. & conj.', 'By chance; perhaps; it may be; if;\n   supposing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peradventure', 'n.', 'Chance; hap; hence, doubt; question; as, proved\n   beyond peradventure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peraeopod', 'n.', 'One of the thoracic legs of a crustacean. See Illust. of\n   Crustacea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peragrate', 'v. t.', 'To travel over or through.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peragration', 'n.', 'The act or state of passing through any space; as, the\n   peragration of the moon in her monthly revolution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perambulated', 'imp. & p. p.', 'of Perambulate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perambulating', 'p. pr. & vb. n.', 'of Perambulate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perambulate', 'v. t.', 'To walk through or over; especially, to travel over\n   for the purpose of surveying or examining; to inspect by traversing;\n   specifically, to inspect officially the boundaries of, as of a town or\n   parish, by walking over the whole line.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perambulate', 'v. i.', 'To walk about; to ramble; to stroll; as, he\n   perambulated in the park.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perambulation', 'n.', 'The act of perambulating; traversing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perambulation', 'n.', 'An annual survey of boundaries, as of town, a\n   parish, a forest, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perambulation', 'n.', 'A district within which one is authorized to make a\n   tour of inspection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perambulator', 'n.', 'One who perambulates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perambulator', 'n.', 'A surveyors instrument for measuring distances. It\n   consists of a wheel arranged to roll along over the ground, with an\n   apparatus of clockwork, and a dial plate upon which the distance\n   traveled is shown by an index. See Odometer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perambulator', 'n.', 'A low carriage for a child, propelled by pushing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perameles', 'n.', 'Any marsupial of the genus Perameles, which includes\n   numerous species found in Australia. They somewhat resemble rabbits in\n   size and form. See Illust. under Bandicoot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perbend', 'n.', 'See Perpender.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perbreak', 'n.', 'See Parbreak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perbromate', 'n.', 'A salt of perbromic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perbromic', 'a.', 'Pertaining to, or designating, the highest oxygen acid,\n   HBrO4, of bromine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perbromide', 'n.', 'A bromide having a higher proportion of bromine than\n   any other bromide of the same substance or series.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perca', 'n.', 'A genus of fishes, including the fresh-water perch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Percale', 'n.', 'A fine cotton fabric, having a linen finish, and often\n   printed on one side, -- used for womens and childrens wear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Percaline', 'n.', 'A fine kind of French cotton goods, usually of one\n   color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Percarbide', 'n.', 'A compound containing a relatively large amount of\n   carbon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Percarburet', 'n.', 'A percarbide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Percarbureted', 'a.', 'Combined with a relatively large amount of carbon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Percase', 'adv.', 'Perhaps; perchance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perce', 'v. t.', 'To pierce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perceivable', 'a.', 'Capable of being perceived; perceptible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perceivance', 'n.', 'Power of perceiving.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perceived', 'imp. & p. p.', 'of Perceive');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perceiving', 'p. pr. & vb. n.', 'of Perceive');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perceive', 'v. t.', 'To obtain knowledge of through the senses; to receive\n   impressions from by means of the bodily organs; to take cognizance of\n   the existence, character, or identity of, by means of the senses; to\n   see, hear, or feel; as, to perceive a distant ship; to perceive a\n   discord.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perceive', 'v. t.', 'To take intellectual cognizance of; to apprehend by\n   the mind; to be convinced of by direct intuition; to note; to remark;\n   to discern; to see; to understand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perceive', 'v. t.', 'To be affected of influented by.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perceiver', 'n.', 'One who perceives (in any of the senses of the verb).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Percely', 'n.', 'Parsley.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Percentage', 'n.', 'A certain rate per cent; the allowance, duty, rate of\n   interest, discount, or commission, on a hundred.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Percept', 'n.', 'That which is perceived.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perceptibility', 'n.', 'The quality or state of being perceptible; as, the\n   perceptibility of light or color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perceptibility', 'n.', 'Perception.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perceptible', 'a.', 'Capable of being perceived; cognizable; discernible;\n   perceivable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perception', 'n.', 'The act of perceiving; cognizance by the senses or\n   intellect; apperhension by the bodily organs, or by the mind, of what\n   is presented to them; discernment; apperhension; cognition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perception', 'n.', 'The faculty of perceiving; the faculty, or peculiar\n   part, of mans constitution by which he has knowledge through the\n   medium or instrumentality of the bodily organs; the act of apperhending\n   material objects or qualities through the senses; -- distinguished from\n   conception.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perception', 'n.', 'The quality, state, or capability, of being affected by\n   something external; sensation; sensibility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perception', 'n.', 'An idea; a notion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perceptive', 'a.', 'Of or pertaining to the act or power of perceiving;\n   having the faculty or power of perceiving; used in perception.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perceptivity', 'n.', 'The quality or state of being perceptive; power of\n   perception.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Percesoces', 'n. pl.', 'An order of fishes including the gray mullets\n   (Mugil), the barracudas, the silversides, and other related fishes. So\n   called from their relation both to perches and to pikes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perch', 'n.', 'Any fresh-water fish of the genus Perca and of several other\n   allied genera of the family Percidae, as the common American or yellow\n   perch (Perca flavescens, / Americana), and the European perch (P.\n   fluviatilis).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perch', 'n.', 'Any one of numerous species of spiny-finned fishes belonging\n   to the Percidae, Serranidae, and related families, and resembling, more\n   or less, the true perches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perch', 'n.', 'A pole; a long staff; a rod; esp., a pole or other support\n   for fowls to roost on or to rest on; a roost; figuratively, any\n   elevated resting place or seat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perch', 'n.', 'A measure of length containing five and a half yards; a rod,\n   or pole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perch', 'n.', 'In land or square measure: A square rod; the 160th part of\n   an acre.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perch', 'n.', 'In solid measure: A mass 16/ feet long, 1 foot in height,\n   and 1/ feet in breadth, or 24/ cubic feet (in local use, from 22 to 25\n   cubic feet); -- used in measuring stonework.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perch', 'n.', 'A pole connecting the fore gear and hind gear of a spring\n   carriage; a reach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perched', 'imp. & p. p.', 'of Perch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perching', 'p. pr. & vb. n.', 'of Perch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perch', 'v. i.', 'To alight or settle, as a bird; to sit or roost.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perch', 'v. t.', 'To place or to set on, or as on, a perch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perch', 'v. t.', 'To occupy as a perch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perchance', 'adv.', 'By chance; perhaps; peradventure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perchant', 'n.', 'A bird tied by the foot, to serve as decoy to other birds\n   by its fluttering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Percher', 'v. i.', 'One who, or that which, perches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Percher', 'v. i.', 'One of the Insessores.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Percher', 'v. i.', 'A Paris candle anciently used in England; also, a large\n   wax candle formerly set upon the altar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Percheron', 'n.', 'One of a breed of draught horses originating in Perche,\n   an old district of France; -- called also Percheron-Norman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perchlorate', 'n.', 'A salt of perchloric acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perchloric', 'a.', 'Pertaining to, or designating, the highest oxygen acid\n   (HClO4), of chlorine; -- called also hyperchloric.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perchloride', 'n.', 'A chloride having a higher proportion of chlorine than\n   any other chloride of the same substance or series.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perchromic', 'a.', 'Pertaining to, or designating, a certain one of the\n   highly oxidized compounds of chromium, which has a deep blue color, and\n   is produced by the action of hydrogen peroxide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perciform', 'a.', 'Pertaining to the Perciformes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perciformes', 'n. pl.', 'An extensive tribe or suborder of fishes,\n   including the true perches (Percidae); the pondfishes (Centrarchidae);\n   the sciaenoids (Sciaenidae); the sparoids (Sparidae); the serranoids\n   (Serranidae), and some other related families.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Percipience', 'n.', 'Alt. of Percipiency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Percipiency', 'n.', 'The faculty, act or power of perceiving; perception.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Percipient', 'a.', 'Having the faculty of perception; perceiving; as, a\n   percipient being.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Percipient', 'n.', 'One who, or that which, is percipient.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perclose', 'n.', 'Same as Parclose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perclose', 'n.', 'Conclusion; end.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Percoid', 'a.', 'Belonging to, or resembling, the perches, or family\n   Percidae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Percoid', 'n.', 'Any fish of the genus Perca, or allied genera of the\n   family Percidae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Percoidea', 'n. pl.', 'Same as Perciformes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Percolated', 'imp. & p. p.', 'of Percolate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Percolating', 'p. pr. & vb. n.', 'of Percolate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Percolate', 'v. t.', 'To cause to pass through fine interstices, as a\n   liquor; to filter; to strain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Percolate', 'v. i.', 'To pass through fine interstices; to filter; as,\n   water percolates through porous stone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Percolation', 'n.', 'The act or process of percolating, or filtering;\n   filtration; straining. Specifically (Pharm.), the process of exhausting\n   the virtues of a powdered drug by letting a liquid filter slowly\n   through it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Percolator', 'n.', 'One who, or that which, filters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Percomorphi', 'n. pl.', 'A division of fishes including the perches and\n   related kinds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perculaced', 'a.', 'Latticed. See Lattice, n., 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Percurrent', 'a.', 'Running through the entire length.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Percursory', 'a.', 'Running over slightly or in haste; cursory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Percussed', 'imp. & p. p.', 'of Percuss');
  INSERT INTO words (word, word_type, description) VALUES  (    'Percussing', 'p. pr. & vb. n.', 'of Percuss');
  INSERT INTO words (word, word_type, description) VALUES  (    'Percuss', 'v. t.', 'To strike smartly; to strike upon or against; as, to\n   percuss the chest in medical examination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Percuss', 'v. i.', 'To strike or tap in an examination by percussion. See\n   Percussion, 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Percussion', 'n.', 'The act of percussing, or striking one body against\n   another; forcible collision, esp. such as gives a sound or report.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Percussion', 'n.', 'Hence: The effect of violent collision; vibratory\n   shock; impression of sound on the ear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Percussion', 'n.', 'The act of tapping or striking the surface of the body\n   in order to learn the condition of the parts beneath by the sound\n   emitted or the sensation imparted to the fingers. Percussion is said to\n   be immediate if the blow is directly upon the body; if some\n   interventing substance, as a pleximeter, is, used, it is called\n   mediate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Percussive', 'a.', 'Striking against; percutient; as, percussive force.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Percutient', 'a.', 'Striking; having the power of striking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Percutient', 'n.', 'That which strikes, or has power to strike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perdicine', 'a.', 'Of or pertaining to the family Perdicidae, or\n   partridges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perdie', 'adv.', 'See Parde.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perdifoil', 'n.', 'A deciduous plant; -- opposed to evergreen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perdition', 'n.', 'Entire loss; utter destruction; ruin; esp., the utter\n   loss of the soul, or of final happiness in a future state; future\n   misery or eternal death.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perdition', 'n.', 'Loss of diminution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perditionable', 'a.', 'Capable of being ruined; worthy of perdition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perdix', 'n.', 'A genus of birds including the common European partridge.\n   Formerly the word was used in a much wider sense to include many allied\n   genera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perdu', 'a.', 'One placed on watch, or in ambush.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perdu', 'a.', 'A soldier sent on a forlorn hope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perdu', 'a.', 'Alt. of Perdue');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perdue', 'a.', 'Lost to view; in concealment or ambush; close.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perdue', 'a.', 'Accustomed to, or employed in, desperate enterprises;\n   hence, reckless; hopeless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perduellion', 'n.', 'Treason.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perdulous', 'a.', 'Lost; thrown away.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perdurability', 'n.', 'Durability; lastingness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perdurable', 'n.', 'Very durable; lasting; continuing long.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perdurance', 'n.', 'Alt. of Perduration');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perduration', 'n.', 'Long continuance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perdure', 'v. i.', 'To last or endure for a long time; to be perdurable or\n   lasting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perdy', 'adv.', 'Truly. See Parde.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pere', 'n.', 'A peer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peregal', 'a.', 'Fully equal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peregrinate', 'v. i.', 'To travel from place to place, or from one country\n   to another; hence, to sojourn in foreign countries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peregrinate', 'a.', 'Having traveled; foreign.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peregrination', 'n.', 'A traveling from one country to another; a\n   wandering; sojourn in foreign countries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peregrinator', 'n.', 'One who peregrinates; one who travels about.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peregrine', 'a.', 'Foreign; not native; extrinsic or from without; exotic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peregrine', 'n.', 'The peregrine falcon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peregrinity', 'n.', 'Foreignness; strangeness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peregrinity', 'n.', 'Travel; wandering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perel', 'n.', 'Apparel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perempt', 'v. t.', 'To destroy; to defeat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peremption', 'n.', 'A quashing; a defeating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peremptorily', 'adv.', 'In a peremptory manner; absolutely; positively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peremptoriness', 'n.', 'The quality of being peremptory; positiveness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peremptory', 'a.', 'Precluding debate or expostulation; not admitting of\n   question or appeal; positive; absolute; decisive; conclusive; final.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peremptory', 'a.', 'Positive in opinion or judgment; decided; dictatorial;\n   dogmatical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peremptory', 'a.', 'Firmly determined; unawed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perennial', 'a.', 'ing or continuing through the year; as, perennial\n   fountains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perennial', 'a.', 'Continuing without cessation or intermission; perpetual;\n   unceasing; never failing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perennial', 'a.', 'Continuing more than two years; as, a perennial steam,\n   or root, or plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perennial', 'n.', 'A perennial plant; a plant which lives or continues more\n   than two years, whether it retains its leaves in winter or not.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perennially', 'adv.', 'In a perennial manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perennibranchiata', 'n. pl.', 'Those Batrachia which retain their gills\n   through life, as the menobranchus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perennibranchiate', 'a.', 'Having branchae, or gills, through life; -- said\n   especially of certain Amphibia, like the menobranchus. Opposed to\n   caducibranchiate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perennibranchiate', 'a.', 'Belonging to the Perennibranchiata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perennity', 'n.', 'The quality of being perennial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pererration', 'n.', 'A wandering, or rambling, through various places.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perfect', 'a.', 'Brought to consummation or completeness; completed; not\n   defective nor redundant; having all the properties or qualities\n   requisite to its nature and kind; without flaw, fault, or blemish;\n   without error; mature; whole; pure; sound; right; correct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perfect', 'a.', 'Well informed; certain; sure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perfect', 'a.', 'Hermaphrodite; having both stamens and pistils; -- said of\n   flower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perfect', 'n.', 'The perfect tense, or a form in that tense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perfected', 'imp. & p. p.', 'of Perfect');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perfecting', 'p. pr. & vb. n.', 'of Perfect');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perfect', 'a.', 'To make perfect; to finish or complete, so as to leave\n   nothing wanting; to give to anything all that is requisite to its\n   nature and kind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perfecter', 'n.', 'One who, or that which, makes perfect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perfectibilian', 'n.', 'A perfectionist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perfectibilist', 'n.', 'A perfectionist. See also Illuminati, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perfectibility', 'n.', 'The quality or state of being perfectible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perfectible', 'a.', 'Capable of becoming, or being made, perfect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perfection', 'n.', 'The quality or state of being perfect or complete, so\n   that nothing requisite is wanting; entire development; consummate\n   culture, skill, or moral excellence; the highest attainable state or\n   degree of excellence; maturity; as, perfection in an art, in a science,\n   or in a system; perfection in form or degree; fruits in perfection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perfection', 'n.', 'A quality, endowment, or acquirement completely\n   excellent; an ideal faultlessness; especially, the divine attribute of\n   complete excellence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perfection', 'v. t.', 'To perfect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perfectional', 'a.', 'Of or pertaining to perfection; characterized by\n   perfection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perfectionate', 'v. t.', 'To perfect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perfectionism', 'n.', 'The doctrine of the Perfectionists.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perfectionist', 'n.', 'One pretending to perfection; esp., one pretending\n   to moral perfection; one who believes that persons may and do attain to\n   moral perfection and sinlessness in this life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perfectionment', 'n.', 'The act of bringing to perfection, or the state of\n   having attained to perfection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perfective', 'a.', 'Tending or conducing to make perfect, or to bring to\n   perfection; -- usually followed by of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perfectively', 'adv.', 'In a perfective manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perfectly', 'adv.', 'In a perfect manner or degree; in or to perfection;\n   completely; wholly; throughly; faultlessly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perfectness', 'n.', 'The quality or state of being perfect; perfection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perfervid', 'a.', 'Very fervid; too fervid; glowing; ardent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perficient', 'a.', 'Making or doing throughly; efficient; effectual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perficient', 'n.', 'One who performs or perfects a work; especially, one\n   who endows a charity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perfidious', 'a.', 'Guilty of perfidy; violating good faith or vows; false\n   to trust or confidence reposed; teacherous; faithless; as, a perfidious\n   friend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perfidious', 'a.', 'Involving, or characterized by, perfidy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perfidiously', 'adv.', 'In a perfidious manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perfidiousness', 'n.', 'The quality of being perfidious; perfidy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perfidies', 'pl. ', 'of Perfidy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perfidy', 'n.', 'The act of violating faith or allegiance; violation of a\n   promise or vow, or of trust reposed; faithlessness; treachery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perfit', 'a.', 'Perfect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perfix', 'v. t.', 'To fix surely; to appoint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perflable', 'a.', 'Capable of being blown through.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perflate', 'v. t.', 'To blow through.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perflation', 'n.', 'The act of perflating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perfoliate', 'a.', 'Having the basal part produced around the stem; -- said\n   of leaves which the stem apparently passes directory through.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perfoliate', 'a.', 'Surrounded by a circle of hairs, or projections of any\n   kind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perforata', 'n. pl.', 'A division of corals including those that have a\n   porous texture, as Porites and Madrepora; -- opposed to Aporosa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perforata', 'n. pl.', 'A division of Foraminifera, including those having\n   perforated shells.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perforated', 'imp. & p. p.', 'of Perforate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perforating', 'p. pr. & vb. n.', 'of Perforate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perforate', 'v.', 'To bore through; to pierce through with a pointed\n   instrument; to make a hole or holes through by boring or piercing; to\n   pierce or penetrate the surface of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perforate', 'a.', 'Alt. of Perforated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perforated', 'a.', 'Pierced with a hole or holes, or with pores; having\n   transparent dots resembling holes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perforation', 'n.', 'The act of perforating, or of boring or piercing\n   through.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perforation', 'n.', 'A hole made by boring or piercing; an aperture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perforative', 'a.', 'Having power to perforate or pierce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perforator', 'n.', 'One who, or that which, perforates; esp., a\n   cephalotome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perforce', 'adv.', 'By force; of necessary; at any rate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perforce', 'v. t.', 'To force; to compel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Performed', 'imp. & p. p.', 'of Perform');
  INSERT INTO words (word, word_type, description) VALUES  (    'Performing', 'p. pr. & vb. n.', 'of Perform');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perform', 'v. t.', 'To carry through; to bring to completion; to achieve;\n   to accomplish; to execute; to do.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perform', 'v. t.', 'To discharge; to fulfill; to act up to; as, to perform\n   a duty; to perform a promise or a vow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perform', 'v. t.', 'To represent; to act; to play; as in drama.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perform', 'v. i.', 'To do, execute, or accomplish something; to acquit\n   ones self in any business; esp., to represent sometimes by action; to\n   act a part; to play on a musical instrument; as, the players perform\n   poorly; the musician performs on the organ.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Performable', 'a.', 'Admitting of being performed, done, or executed;\n   practicable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Performance', 'n.', 'The act of performing; the carrying into execution or\n   action; execution; achievement; accomplishment; representation by\n   action; as, the performance of an undertaking of a duty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Performance', 'n.', 'That which is performed or accomplished; a thing done\n   or carried through; an achievement; a deed; an act; a feat; esp., an\n   action of an elaborate or public character.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Performer', 'n.', 'One who performs, accomplishes, or fulfills; as, a good\n   promiser, but a bad performer; especially, one who shows skill and\n   training in any art; as, a performer of the drama; a performer on the\n   harp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perfricate', 'v. t.', 'To rub over.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perfumatory', 'a.', 'Emitting perfume; perfuming.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perfumed', 'imp. & p. p.', 'of Perfume');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perfuming', 'p. pr. & vb. n.', 'of Perfume');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perfume', 'v. t.', 'To fill or impregnate with a perfume; to scent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perfume', 'v.', 'The scent, odor, or odoriferous particles emitted from a\n   sweet-smelling substance; a pleasant odor; fragrance; aroma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perfume', 'v.', 'A substance that emits an agreeable odor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perfumer', 'n.', 'One who, oe that which, perfumes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perfumer', 'n.', 'One whose trade is to make or sell perfumes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perfumery', 'n.', 'Perfumes, in general.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perfumery', 'n.', 'The art of preparing perfumes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perfunctorily', 'adv.', 'In a perfunctory manner; formally; carelessly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perfunctoriness', 'n.', 'The quality or state of being perfunctory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perfunctory', 'a.', 'Done merely to get rid of a duty; performed\n   mechanically and as a thing of rote; done in a careless and superficial\n   manner; characterized by indifference; as, perfunctory admonitions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perfunctory', 'a.', 'Hence: Mechanical; indifferent; listless; careless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perfuncturate', 'v. t.', 'To perform in a perfunctory manner; to do\n   negligently.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perfused', 'imp. & p. p.', 'of Perfuse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perfusing', 'p. pr. & vb. n.', 'of Perfuse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perfuse', 'v. t.', 'To suffuse; to fill full or to excess.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perfusion', 'n.', 'The act of perfusing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perfusive', 'a.', 'Of a nature to flow over, or to spread through.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pergamenous', 'a.', 'Alt. of Pergamentaceous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pergamentaceous', 'a.', 'Like parchment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perhaps', 'adv.', 'By chance; peradventure; perchance; it may be.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peri-', '', 'A prefix used to signify around, by, near, over, beyond, or to\n   give an intensive sense; as, perimeter, the measure around; perigee,\n   point near the earth; periergy, work beyond what is needed;\n   perispherical, quite spherical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peris', 'pl. ', 'of Peri');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peri', 'n.', 'An imaginary being, male or female, like an elf or fairy,\n   represented as a descendant of fallen angels, excluded from paradise\n   till penance is accomplished.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Periagua', 'n.', 'See Pirogue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perianth', 'n.', 'The leaves of a flower generally, especially when the\n   calyx and corolla are not readily distinguished.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perianth', 'n.', 'A saclike involucre which incloses the young fruit in\n   most hepatic mosses. See Illust. of Hepatica.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perianthium', 'n.', 'The perianth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Periapt', 'n.', 'A charm worn as a protection against disease or mischief;\n   an amulet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Periastral', 'a.', 'Among or around the stars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Periastron', 'n.', 'That point, in the real or apparent orbit of one star\n   revolving around another, at which the former is nearest to the latter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Periauger', 'n.', 'See Pirogue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Periblast', 'a.', 'The protoplasmic matter which surrounds the entoblast,\n   or cell nucleus, and undergoes segmentation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Periblem', 'n.', 'Nascent cortex, or immature cellular bark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peribolos', 'n.', 'In ancient architecture, an inclosed court, esp., one\n   surrounding a temple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peribranchial', 'a.', 'Surrounding the branchiae; as, a peribranchial\n   cavity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peribranchial', 'a.', 'Around the bronchi or bronchial tubes; as, the\n   peribronchial lymphatics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pericambium', 'n.', 'A layer of thin-walled young cells in a growing stem,\n   in which layer certain new vessels originate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pericardiac', 'a.', 'Alt. of Pericardial');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pericardial', 'a.', 'Of or pertaining to pericardium; situated around the\n   heart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pericardian', 'a.', 'Pericardiac.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pericardic', 'a.', 'Pericardiac.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pericarditus', 'n.', 'Inflammation of the pericardium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pericardium', 'n.', 'The double baglike fold of serous membrane which\n   incloses the heart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pericarp', 'n.', 'The ripened ovary; the walls of the fruit. See Illusts.\n   of Capsule, Drupe, and Legume.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pericarpial', 'a.', 'Alt. of Pericarpic');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pericarpic', 'a.', 'Of or pertaining to a pericarp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pericellular', 'a.', 'Surrounding a cell; as, the pericellular lymph spaces\n   surrounding ganglion cells.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perichaeth', 'n.', 'The leafy involucre surrounding the fruit stalk of\n   mosses; perichaetium; perichete.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perichaetial', 'a.', 'Of or pertaining to the perichaeth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perichaetia', 'pl. ', 'of Perichaetium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perichaetium', 'n.', 'Same as Perichaeth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perichaetous', 'a.', 'Surrounded by setae; -- said of certain earthworms\n   (genus Perichaetus).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perichete', 'n.', 'Same as Perichaeth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perichondrial', 'a.', 'Of or pertaining to the perichondrium; situated\n   around cartilage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perichondritis', 'n.', 'Inflammation of the perichondrium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perichondrium', 'n.', 'The membrane of fibrous connective tissue which\n   closely invests cartilage, except where covering articular surfaces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perichordal', 'a.', 'Around the notochord; as, a perichordal column. See\n   Epichordal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Periclase', 'n.', 'Alt. of Periclasite');
  INSERT INTO words (word, word_type, description) VALUES  (    'Periclasite', 'n.', 'A grayish or dark green mineral, consisting\n   essentially of magnesia (magnesium oxide), occurring in granular forms\n   or in isometric crystals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Periclinia', 'pl. ', 'of Periclinium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Periclinium', 'n.', 'The involucre which surrounds the common receptacle in\n   composite flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Periclitate', 'v. t.', 'To endanger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Periclitation', 'n.', 'Trial; experiment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Periclitation', 'n.', 'The state of being in peril.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pericope', 'n.', 'A selection or extract from a book; especially (Theol.),\n   a selection from the Bible, appointed to be read in the churches or\n   used as a text for a sermon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pericranial', 'a.', 'Of or pertaining to the pericranium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pericranium', 'n.', 'The periosteum which covers the cranium externally;\n   the region around the cranium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Periculous', 'a.', 'Dangerous; full of peril.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pericula', 'pl. ', 'of Periculum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Periculum', 'n.', 'Danger; risk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Periculum', 'n.', 'In a narrower, judicial sense: Accident or casus, as\n   distinguished from dolus and culpa, and hence relieving one from the\n   duty of performing an obligation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Periderm', 'n.', 'The outer layer of bark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Periderm', 'n.', 'The hard outer covering of hydroids and other marine\n   animals; the perisarc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peridiastole', 'n.', 'The almost inappreciable time which elapses between\n   the systole and the diastole of the heart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peridia', 'pl. ', 'of Peridium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peridium', 'n.', 'The envelope or coat of certain fungi, such as the\n   puffballs and earthstars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peridot', 'n.', 'Chrysolite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peridotite', 'n.', 'An eruptive rock characterized by the presence of\n   chrysolite (peridot). It also usually contains pyroxene, enstatite,\n   chromite, etc. It is often altered to serpentine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peridrome', 'n.', 'The space between the columns and the wall of the cella,\n   in a Greek or a Roman temple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Periecians', 'n. pl.', 'See Perioecians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perienteron', 'n.', 'The primitive perivisceral cavity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Periergy', 'n.', 'Excessive care or diligence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Periergy', 'n.', 'A bombastic or labored style.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Periganglionic', 'a.', 'Surrounding a ganglion; as, the periganglionic\n   glands of the frog.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perigastric', 'a.', 'Surrounding the stomach; -- applied to the body cavity\n   of Bryozoa and various other Invertebrata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perigean', 'a.', 'Pertaining to the perigee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perigee', 'n.', 'Alt. of Perigeum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perigeum', 'n.', 'That point in the orbit of the moon which is nearest to\n   the earth; -- opposed to apogee. It is sometimes, but rarely, used of\n   the nearest points of other orbits, as of a comet, a planet, etc.\n   Called also epigee, epigeum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perigenesis', 'n.', 'A theory which explains inheritance by the\n   transmission of the type of growth force possessed by one generation to\n   another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perigenetic', 'a.', 'Of or pertaining to perigenesis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perigone', 'n.', 'Any organ inclosing the essential organs of a flower; a\n   perianth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perigone', 'n.', 'In mosses, the involucral bracts of a male flower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perigone', 'n.', 'A sac which surrounds the generative bodies in the\n   gonophore of a hydroid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perigonia', 'pl. ', 'of Perigonium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perigonium', 'n.', 'Same as Perigone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perigord pie', '', 'A pie made of truffles, much esteemed by epicures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perigraph', 'n.', 'A careless or inaccurate delineation of anything.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perigynia', 'pl. ', 'of Perigynium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perigynium', 'n.', 'Some unusual appendage about the pistil, as the\n   bottle-shaped body in the sedges, and the bristles or scales in some\n   other genera of the Sedge family, or Cyperaceae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perigynous', 'a.', 'Having the ovary free, but the petals and stamens borne\n   on the calyx; -- said of flower such as that of the cherry or peach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perihelia', 'pl. ', 'of Perihelium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perihelion', 'n.', 'Alt. of Perihelium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perihelium', 'n.', 'That point of the orbit of a planet or comet which is\n   nearest to the sun; -- opposed to aphelion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peril', 'n.', 'Danger; risk; hazard; jeopardy; exposure of person or\n   property to injury, loss, or destruction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Periled', 'imp. & p. p.', 'of Peril');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perilled', '', 'of Peril');
  INSERT INTO words (word, word_type, description) VALUES  (    'Periling', 'p. pr. & vb. n.', 'of Peril');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perilling', '', 'of Peril');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peril', 'v. t.', 'To expose to danger; to hazard; to risk; as, to peril\n   ones life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peril', 'v. i.', 'To be in danger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perilla', 'n.', 'A genus of labiate herbs, of which one species (Perilla\n   ocimoides, or P. Nankinensis) is often cultivated for its purple or\n   variegated foliage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perilous', 'a.', 'Full of, attended with, or involving, peril; dangerous;\n   hazardous; as, a perilous undertaking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perilous', 'a.', 'Daring; reckless; dangerous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perilymph', 'n.', 'The fluid which surrounds the membranous labyrinth of\n   the internal ear, and separates it from the walls of the chambers in\n   which the labyrinth lies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perilymphangial', 'a.', 'Around, or at the side of, a lymphatic vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perilymphatic', 'a.', 'Pertaining to, or containing, perilymph.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perilymphatic', 'a.', 'Perilymphangial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perimeter', 'n.', 'The outer boundary of a body or figure, or the sum of\n   all the sides.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perimeter', 'n.', 'An instrument for determining the extent and shape of\n   the field of vision.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perimetric', 'a.', 'Alt. of Perimetrical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perimetrical', 'a.', 'Of or pertaining to the perimeter, or to perimetry;\n   as, a perimetric chart of the eye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perimetry', 'n.', 'The art of using the perimeter; measurement of the field\n   of vision.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perimorph', 'n.', 'A crystal of one species inclosing one of another\n   species. See Endomorph.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perimysial', 'a.', 'Surrounding a muscle or muscles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perimysial', 'a.', 'Of or pertaining to the perimysium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perimysium', 'n.', 'The connective tissue sheath which surrounds a muscle,\n   and sends partitions inwards between the bundles of muscular fibers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perinaeum', 'n.', 'See Perineum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perineal', 'a.', 'Of or pertaining to the perineum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perineoplasty', 'n.', 'The act or process of restoring an injured perineum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perineorrhaphy', 'n.', 'The operation of sewing up a ruptured perineum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perinephritis', 'n.', 'Inflammation of the cellular tissue around the\n   kidney.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perineum', 'n.', 'The region which is included within the outlet of the\n   pelvis, and is traversed by the urinogenital canal and the rectum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perineurial', 'a.', 'Surrounding nerves or nerve fibers; of or pertaining\n   to the perineurium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perineurium', 'n.', 'The connective tissue sheath which surrounds a bundle\n   of nerve fibers. See Epineurium, and Neurilemma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perinuclear', 'a.', 'Of or pertaining to a nucleus; situated around a\n   nucleus; as, the perinuclear protoplasm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Period', 'n.', 'A portion of time as limited and determined by some\n   recurring phenomenon, as by the completion of a revolution of one of\n   the heavenly bodies; a division of time, as a series of years, months,\n   or days, in which something is completed, and ready to recommence and\n   go on in the same order; as, the period of the sun, or the earth, or a\n   comet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Period', 'n.', 'A stated and recurring interval of time; more generally, an\n   interval of time specified or left indefinite; a certain series of\n   years, months, days, or the like; a time; a cycle; an age; an epoch;\n   as, the period of the Roman republic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Period', 'n.', 'One of the great divisions of geological time; as, the\n   Tertiary period; the Glacial period. See the Chart of Geology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Period', 'n.', 'The termination or completion of a revolution, cycle,\n   series of events, single event, or act; hence, a limit; a bound; an\n   end; a conclusion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Period', 'n.', 'A complete sentence, from one full stop to another; esp., a\n   well-proportioned, harmonious sentence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Period', 'n.', 'The punctuation point [.] that marks the end of a complete\n   sentence, or of an abbreviated word.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Period', 'n.', 'One of several similar sets of figures or terms usually\n   marked by points or commas placed at regular intervals, as in\n   numeration, in the extraction of roots, and in circulating decimals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Period', 'n.', 'The time of the exacerbation and remission of a disease, or\n   of the paroxysm and intermission.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Period', 'n.', 'A complete musical sentence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Period', 'v. t.', 'To put an end to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Period', 'v. i.', 'To come to a period; to conclude. [Obs.] \"You may period\n   upon this, that,\" etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Periodate', 'n.', 'A salt of periodic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Periodic', 'a.', 'Pertaining to, derived from, or designating, the highest\n   oxygen acid (HIO/) of iodine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Periodic', 'a.', 'Alt. of Periodical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Periodical', 'a.', 'Of or pertaining to a period or periods, or to division\n   by periods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Periodical', 'a.', 'Performed in a period, or regular revolution;\n   proceeding in a series of successive circuits; as, the periodical\n   motion of the planets round the sun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Periodical', 'a.', 'Happening, by revolution, at a stated time; returning\n   regularly, after a certain period of time; acting, happening, or\n   appearing, at fixed intervals; recurring; as, periodical epidemics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Periodical', 'a.', 'Of or pertaining to a period; constituting a complete\n   sentence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Periodical', 'n.', 'A magazine or other publication which appears at stated\n   or regular intervals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Periodicalist', 'n.', 'One who publishes, or writes for, a periodical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Periodically', 'adv.', 'In a periodical manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Periodicalness', 'n.', 'Periodicity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Periodicities', 'pl. ', 'of Periodicity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Periodicity', 'n.', 'The quality or state of being periodical, or regularly\n   recurrent; as, the periodicity in the vital phenomena of plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Periodide', 'n.', 'An iodide containing a higher proportion of iodine than\n   any other iodide of the same substance or series.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Periodontal', 'a.', 'Surrounding the teeth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Periodoscope', 'n.', 'A table or other means for calculating the periodical\n   functions of women.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perioeci', 'n. pl.', 'Alt. of Perioecians');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perioecians', 'n. pl.', 'Those who live on the same parallel of latitude\n   but on opposite meridians, so that it is noon in one place when it is\n   midnight in the other. Compare Antoeci.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Periople', 'n.', 'The external smooth horny layer of the hoof of the horse\n   and allied animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perioplic', 'a.', 'Of or pertaining to the periople; connected with the\n   periople.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Periosteal', 'a.', 'Situated around bone; of or pertaining to the\n   periosteum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Periosteum', 'n.', 'The membrane of fibrous connective tissue which closely\n   invests all bones except at the articular surfaces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Periostitis', 'n.', 'Inflammation of the periosteum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Periostraca', 'pl. ', 'of Periostracum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Periostracum', 'n.', 'A chitinous membrane covering the exterior of many\n   shells; -- called also epidermis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Periotic', 'a.', 'Surrounding, or pertaining to the region surrounding, the\n   internal ear; as, the periotic capsule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Periotic', 'n.', 'A periotic bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peripatecian', 'n.', 'A peripatetic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peripatetic', 'a.', 'Walking about; itinerant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peripatetic', 'a.', 'Of or pertaining to the philosophy taught by Aristotle\n   (who gave his instructions while walking in the Lyceum at Athens), or\n   to his followers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peripatetic', 'n.', 'One who walks about; a pedestrian; an itinerant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peripatetic', 'n.', 'A disciple of Aristotle; an Aristotelian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peripatetical', 'a.', 'Peripatetic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peripateticism', 'n.', 'The doctrines or philosophical system of the\n   peripatetics. See Peripatetic, n., 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peripatus', 'n.', 'A genus of lowly organized arthropods, found in South\n   Africa, Australia, and tropical America. It constitutes the order\n   Malacopoda.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peripetalous', 'a.', 'Surrounding, or situated about, the petals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peripheral', 'a.', 'Of or pertaining to a periphery; constituting a\n   periphery; peripheric.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peripheral', 'a.', 'External; away from the center; as, the peripheral\n   portion of the nervous system.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peripheric', 'a.', 'Alt. of Peripherical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peripherical', 'a.', 'See Peripheral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peripheries', 'pl. ', 'of Periphery');
  INSERT INTO words (word, word_type, description) VALUES  (    'Periphery', 'n.', 'The outside or superficial portions of a body; the\n   surface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Periphery', 'n.', 'The circumference of a circle, ellipse, or other figure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Periphrase', 'n.', 'The use of more words than are necessary to express the\n   idea; a roundabout, or indirect, way of speaking; circumlocution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Periphrased', 'imp. & p. p.', 'of Periphrase');
  INSERT INTO words (word, word_type, description) VALUES  (    'Periphrasing', 'p. pr. & vb. n.', 'of Periphrase');
  INSERT INTO words (word, word_type, description) VALUES  (    'Periphrase', 'v. t.', 'To express by periphrase or circumlocution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Periphrase', 'v. i.', 'To use circumlocution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Periphrases', 'pl. ', 'of Periphrasis');
  INSERT INTO words (word, word_type, description) VALUES  (    'Periphrasis', 'n.', 'See Periphrase.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Periphrastic', 'a.', 'Alt. of Periphrastical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Periphrastical', 'a.', 'Expressing, or expressed, in more words than are\n   necessary; characterized by periphrase; circumlocutory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Periphrastically', 'adv.', 'With circumlocution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Periplast', 'n.', 'Same as Periblast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peripneumonia', 'n.', 'Alt. of Peripneumony');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peripneumony', 'n.', 'Pneumonia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peripneumonic', 'a.', 'Of or pertaining to peripneumonia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Periproct', 'n.', 'The region surrounding the anus, particularly of\n   echinoderms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Periproctitis', 'n.', 'Inflammation of the tissues about the rectum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peripteral', 'a.', 'Having columns on all sides; -- said of an edifice. See\n   Apteral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peripterous', 'a.', 'Peripteral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peripterous', 'a.', 'Feathered all around.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perisarc', 'n.', 'The outer, hardened integument which covers most\n   hydroids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Periscian', 'a.', 'Having the shadow moving all around.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Periscians', 'n. pl.', 'Alt. of Periscii');
  INSERT INTO words (word, word_type, description) VALUES  (    'Periscii', 'n. pl.', 'Those who live within a polar circle, whose shadows,\n   during some summer days, will move entirely round, falling toward every\n   point of the compass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Periscope', 'n.', 'A general or comprehensive view.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Periscope', 'n.', 'an optical instrument of tubular shape containing an\n   arrangement of lenses and mirrors (or prisms), allowing a person to\n   observe a field of view otherwise obstructed, as beyond an obstructing\n   object or (as in submarines) above the surface of the water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Periscopic', 'a.', 'Viewing all around, or on all sides.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Periscopic', 'a.', 'of or relating to a periscope{2}.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perished', 'imp. & p. p.', 'of Perish');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perishing', 'p. pr. & vb. n.', 'of Perish');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perish', 'v. i.', 'To be destroyed; to pass away; to become nothing; to be\n   lost; to die; hence, to wither; to waste away.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perish', 'v. t.', 'To cause perish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perishability', 'n.', 'Perishableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perishable', 'a.', 'Liable to perish; subject to decay, destruction, or\n   death; as, perishable goods; our perishable bodies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perishableness', 'n.', 'The quality or state of being perishable; liability\n   to decay or destruction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perishably', 'adv.', 'In a perishable degree or manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perishment', 'n.', 'The act of perishing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perisomata', 'pl. ', 'of Perisoma');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perisoma', 'n.', 'Same as Perisome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perisome', 'n.', 'The entire covering of an invertebrate animal, as\n   echinoderm or coelenterate; the integument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perisperm', 'n.', 'The albumen of a seed, especially that portion which is\n   formed outside of the embryo sac.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perispheric', 'a.', 'Alt. of Perispherical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perispherical', 'a.', 'Exactly spherical; globular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perispomena', 'pl. ', 'of Perispomenon');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perispomenon', 'n.', 'A word which has the circumflex accent on the last\n   syllable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perispore', 'n.', 'The outer covering of a spore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perissad', 'a.', 'Odd; not even; -- said of elementary substances and of\n   radicals whose valence is not divisible by two without a remainder.\n   Contrasted with artiad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perisse', 'v. i.', 'To perish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perissodactyl', 'n.', 'One of the Perissodactyla.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perissodactyla', 'n. pl.', 'A division of ungulate mammals, including those\n   that have an odd number of toes, as the horse, tapir, and rhinoceros;\n   -- opposed to Artiodactyla.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perissological', 'a.', 'Redundant or excessive in words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perissology', 'n.', 'Superfluity of words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peristalsis', 'n.', 'Peristaltic contraction or action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peristaltic', 'a.', 'Applied to the peculiar wormlike wave motion of the\n   intestines and other similar structures, produced by the successive\n   contraction of the muscular fibers of their walls, forcing their\n   contents onwards; as, peristaltic movement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peristeria', 'n.', 'A genus of orchidaceous plants. See Dove plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peristerion', 'n.', 'The herb vervain (Verbena officinalis).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peristerite', 'n.', 'A variety of albite, whitish and slightly iridescent\n   like a pigeons neck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peristeromorphous', 'a.', 'Like or pertaining to the pigeons or Columbae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peristeropodous', 'a.', 'Having pigeonlike feet; -- said of those\n   gallinaceous birds that rest on all four toes, as the curassows and\n   megapods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peristole', 'n.', 'Peristaltic action, especially of the intestines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peristomata', 'pl. ', 'of Peristoma');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peristoma', 'n.', 'Same as Peristome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peristome', 'n.', 'The fringe of teeth around the orifice of the capsule of\n   mosses. It consists of 4, 8, 16, 32, or 64 teeth, and may be either\n   single or double.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peristome', 'n.', 'The lip, or edge of the aperture, of a spiral shell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peristome', 'n.', 'The membrane surrounding the mouth of an invertebrate\n   animal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peristomial', 'a.', 'Of or pertaining to a peristome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peristomium', 'n.', 'Same as Peristome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peristrephic', 'a.', 'Turning around; rotatory; revolving; as, a\n   peristrephic painting (of a panorama).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peristyle', 'n.', 'A range of columns with their entablature, etc.;\n   specifically, a complete system of columns, whether on all sides of a\n   court, or surrounding a building, such as the cella of a temple. Used\n   in the former sense, it gives name to the larger and inner court of a\n   Roman dwelling, the peristyle. See Colonnade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perisystole', 'n.', 'The interval between the diastole and systole of the\n   heart. It is perceptible only in the dying.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perite', 'a.', 'Skilled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perithecium', 'n.', 'An organ in certain fungi and lichens, surrounding and\n   enveloping the masses of fructification.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peritomous', 'a.', 'Cleaving in more directions than one, parallel to the\n   axis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peritonaeum', 'n.', 'Same as Peritoneum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peritoneal', 'a.', 'Of or pertaining to the peritoneum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peritoneum', 'n.', 'The smooth serous membrane which lines the cavity of\n   the abdomen, or the whole body cavity when there is no diaphragm, and,\n   turning back, surrounds the viscera, forming a closed, or nearly\n   closed, sac.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peritonitis', 'n.', 'Inflammation of the peritoneum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peritracheal', 'a.', 'Surrounding the tracheae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peritreme', 'n.', 'That part of the integument of an insect which surrounds\n   the spiracles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peritreme', 'n.', 'The edge of the aperture of a univalve shell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peritricha', 'n. pl.', 'A division of ciliated Infusoria having a circle of\n   cilia around the oral disk and sometimes another around the body. It\n   includes the vorticellas. See Vorticella.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peritrochium', 'n.', 'The wheel which, together with the axle, forms the\n   axis in peritrochio, which see under Axis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peritropal', 'a.', 'Rotatory; circuitous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peritropal', 'a.', 'Having the axis of the seed perpendicular to the axis\n   of the pericarp to which it is attached.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peritropous', 'a.', 'Peritropal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perityphlitis', 'n.', 'Inflammation of the connective tissue about the\n   caecum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Periuterine', 'a.', 'Surrounding the uterus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perivascular', 'a.', 'Around the blood vessels; as, perivascular\n   lymphatics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perivertebral', 'a.', 'Surrounding the vertebrae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perivisceral', 'a.', 'Around the viscera; as, the perivisceral cavity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perivitelline', 'a.', 'Situated around the vitellus, or between the\n   vitellus and zona pellucida of an ovum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Periwig', 'n.', 'A headdress of false hair, usually covering the whole\n   head, and representing the natural hair; a wig.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perwigged', 'imp. & p. p.', 'of Periwig');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perwigging', 'p. pr. & vb. n.', 'of Periwig');
  INSERT INTO words (word, word_type, description) VALUES  (    'Periwig', 'v. t.', 'To dress with a periwig, or with false hair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Periwinkle', 'n.', 'Any small marine gastropod shell of the genus\n   Littorina. The common European species (Littorina littorea), in Europe\n   extensively used as food, has recently become naturalized abundantly on\n   the American coast. See Littorina.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Periwinkle', 'n.', 'A trailing herb of the genus Vinca.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perjenet', 'n.', 'A kind of pear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perjured', 'imp. & p. p.', 'of Perjure');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perjuring', 'p. pr. & vb. n.', 'of Perjure');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perjure', 'v. t.', 'To cause to violate an oath or a vow; to cause to make\n   oath knowingly to what is untrue; to make guilty of perjury; to\n   forswear; to corrupt; -- often used reflexively; as, he perjured\n   himself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perjure', 'v. t.', 'To make a false oath to; to deceive by oaths and\n   protestations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perjure', 'n.', 'A perjured person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perjured', 'a.', 'Guilty of perjury; having sworn falsely; forsworn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perjurer', 'n.', 'One who is guilty of perjury; one who perjures or\n   forswears, in any sense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perjurious', 'a.', 'Alt. of Perjurous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perjurous', 'a.', 'Guilty of perjury; containing perjury.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perjuries', 'pl. ', 'of Perjury');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perjury', 'v.', 'False swearing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perjury', 'v.', 'At common law, a willfully false statement in a fact\n   material to the issue, made by a witness under oath in a competent\n   judicial proceeding. By statute the penalties of perjury are imposed on\n   the making of willfully false affirmations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perked', 'imp. & p. p.', 'of Perk');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perking', 'p. pr. & vb. n.', 'of Perk');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perk', 'v. t.', 'To make trim or smart; to straighten up; to erect; to make\n   a jaunty or saucy display of; as, to perk the ears; to perk up ones\n   head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perk', 'v. i.', 'To exalt ones self; to bear ones self loftily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perk', 'a.', 'Smart; trim; spruce; jaunty; vain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perk', 'v. i.', 'To peer; to look inquisitively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perkin', 'n.', 'A kind of weak perry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perkinism', 'n.', 'A remedial treatment, by drawing the pointed extremities\n   of two rods, each of a different metal, over the affected part;\n   tractoration, -- first employed by Dr. Elisha Perkins of Norwich, Conn.\n   See Metallotherapy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perky', 'a.', 'Perk; pert; jaunty; trim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perlaceous', 'a.', 'Pearly; resembling pearl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perlid', 'n.', 'Any insect of the genus Perla, or family Perlidae. See\n   Stone fly, under Stone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perlite', 'n.', 'Same as Pearlite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perlitic', 'a.', 'Relating to or resembling perlite, or pearlstone; as, the\n   perlitic structure of certain rocks. See Pearlite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perlous', 'a.', 'Perilous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perlustration', 'n.', 'The act of viewing all over.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Permanable', 'a.', 'Permanent; durable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Permanence', 'n.', 'Alt. of Permanency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Permanency', 'n.', 'The quality or state of being permanent; continuance in\n   the same state or place; duration; fixedness; as, the permanence of\n   institutions; the permanence of nature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Permanent', 'a.', 'Continuing in the same state, or without any change that\n   destroys form or character; remaining unaltered or unremoved; abiding;\n   durable; fixed; stable; lasting; as, a permanent impression.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Permanently', 'adv.', 'In a permanent manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Permanganate', 'n.', 'A salt of permanganic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Permanganic', 'a.', 'Pertaining to, or designating, one of the higher acids\n   of manganese, HMnO4, which forms salts called permanganates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Permansion', 'n.', 'Continuance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Permeability', 'n.', 'The quality or state of being permeable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Permeable', 'a.', 'Capable of being permeated, or passed through; yielding\n   passage; passable; penetrable; -- used especially of substances which\n   allow the passage of fluids; as, wood is permeable to oil; glass is\n   permeable to light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Permeably', 'adv.', 'In a permeable manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Permeant', 'a.', 'Passing through; permeating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Permeated', 'imp. & p. p.', 'of Permeate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Permeating', 'p. pr. & vb. n.', 'of Permeate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Permeate', 'v. t.', 'To pass through the pores or interstices of; to\n   penetrate and pass through without causing rupture or displacement; --\n   applied especially to fluids which pass through substances of loose\n   texture; as, water permeates sand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Permeate', 'v. t.', 'To enter and spread through; to pervade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Permeation', 'n.', 'The act of permeating, passing through, or spreading\n   throughout, the pores or interstices of any substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Permian', 'a.', 'Belonging or relating to the period, and also to the\n   formation, next following the Carboniferous, and regarded as closing\n   the Carboniferous age and Paleozoic era.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Permian', 'n.', 'The Permian period. See Chart of Geology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Permians', 'n. pl.', 'A tribe belonging to the Finnic race, and inhabiting\n   a portion of Russia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Permiscible', 'a.', 'Capable of being mixed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Permiss', 'n.', 'A permitted choice; a rhetorical figure in which a thing\n   is committed to the decision of ones opponent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Permissibility', 'n.', 'The quality of being permissible; permissibleness;\n   allowableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Permissible', 'a.', 'That may be permitted; allowable; admissible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Permission', 'n.', 'The act of permitting or allowing; formal consent;\n   authorization; leave; license or liberty granted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Permissive', 'a.', 'Permitting; granting leave or liberty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Permissive', 'a.', 'Permitted; tolerated; suffered.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Permissively', 'adv.', 'In a permissive manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Permistion', 'n.', 'The act of mixing; the state of being mingled; mixture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Permitted', 'imp. & p. p.', 'of Permit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Permitting', 'p. pr. & vb. n.', 'of Permit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Permit', 'v. t.', 'To consent to; to allow or suffer to be done; to\n   tolerate; to put up with.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Permit', 'v. t.', 'To grant (one) express license or liberty to do an act;\n   to authorize; to give leave; -- followed by an infinitive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Permit', 'v. t.', 'To give over; to resign; to leave; to commit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Permit', 'v. i.', 'To grant permission; to allow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Permit', 'n.', 'Warrant; license; leave; permission; specifically, a\n   written license or permission given to a person or persons having\n   authority; as, a permit to land goods subject to duty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Permittance', 'n.', 'The act of permitting; allowance; permission; leave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Permittee', 'n.', 'One to whom a permission or permit is given.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Permitter', 'n.', 'One who permits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Permix', 'v. t.', 'To mix; to mingle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Permixtion', 'n.', 'See Permission.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Permutable', 'a.', 'Capable of being permuted; exchangeable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Permutation', 'n.', 'The act of permuting; exchange of the thing for\n   another; mutual transference; interchange.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Permutation', 'n.', 'The arrangement of any determinate number of things,\n   as units, objects, letters, etc., in all possible orders, one after the\n   other; -- called also alternation. Cf. Combination, n., 4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Permutation', 'n.', 'Any one of such possible arrangements.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Permutation', 'n.', 'Barter; exchange.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Permute', 'v. t.', 'To interchange; to transfer reciprocally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Permute', 'v. t.', 'To exchange; to barter; to traffic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Permuter', 'n.', 'One who permutes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pern', 'v. t.', 'To take profit of; to make profitable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pern', 'n.', 'The honey buzzard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pernancy', 'n.', 'A taking or reception, as the receiving of rents or\n   tithes in kind, the receiving of profits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pernel', 'n.', 'See Pimpernel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pernicion', 'n.', 'Destruction; perdition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pernicious', 'a.', 'Quick; swift (to burn).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pernicious', 'a.', 'Having the quality of injuring or killing; destructive;\n   very mischievous; baleful; malicious; wicked.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pernicity', 'n.', 'Swiftness; celerity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pernio', 'n.', 'A chilblain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pernoctalian', 'n.', 'One who watches or keeps awake all night.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pernoctation', 'n.', 'The act or state of passing the whole night; a\n   remaining all night.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pernor', 'v.', 'One who receives the profits, as of an estate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pernot furnace', '', 'A reverberatory furnace with a circular revolving\n   hearth, -- used in making steel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pernyi moth', '', 'A silk-producing moth (Attacus Pernyi) which feeds upon\n   the oak. It has been introduced into Europe and America from China.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perofskite', 'n.', 'A titanate of lime occurring in octahedral or cubic\n   crystals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perogue', 'n.', 'See Pirogue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peronate', 'a.', 'A term applied to the stipes or stalks of certain fungi\n   which are covered with a woolly substance which at length becomes\n   powdery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peroneal', 'a.', 'Of or pertaining to the fibula; in the region of the\n   fibula.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perorate', 'v. i.', 'To make a peroration; to harangue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peroration', 'n.', 'The concluding part of an oration; especially, a final\n   summing up and enforcement of an argument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peroxidation', 'n.', 'Act, process, or result of peroxidizing; oxidation to\n   a peroxide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peroxide', 'n.', 'An oxide containing more oxygen than some other oxide of\n   the same element. Formerly peroxides were regarded as the highest\n   oxides. Cf. Per-, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peroxidized', 'imp. & p. p.', 'of Peroxidize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peroxidizing', 'p. pr. & vb. n.', 'of Peroxidize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peroxidize', 'v. t.', 'To oxidize to the utmost degree, so as to form a\n   peroxide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perpend', 'v. t.', 'To weight carefully in the mind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perpend', 'v. i.', 'To attend; to be attentive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perpender', 'n.', 'A large stone reaching through a wall so as to appear on\n   both sides of it, and acting as a binder; -- called also perbend,\n   perpend stone, and perpent stone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perpendicle', 'n.', 'Something hanging straight down; a plumb line.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perpendicular', 'a.', 'Exactly upright or vertical; pointing to the zenith;\n   at right angles to the plane of the horizon; extending in a right line\n   from any point toward the center of the earth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perpendicular', 'a.', 'At right angles to a given line or surface; as, the\n   line ad is perpendicular to the line bc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perpendicular', 'n.', 'A line at right angles to the plane of the horizon;\n   a vertical line or direction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perpendicular', 'n.', 'A line or plane falling at right angles on another\n   line or surface, or making equal angles with it on each side.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perpendicularity', 'n.', 'The quality or state of being perpendicular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perpendicularly', 'adv.', 'In a perpendicular manner; vertically.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perpend stone', '', 'See Perpender.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perpension', 'n.', 'Careful consideration; pondering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perpensity', 'n.', 'Perpension.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perpent stone', '', 'See Perpender.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perpession', 'n.', 'Suffering; endurance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perpetrable', 'a.', 'Capable of being perpetrated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perpetrated', 'imp. & p. p.', 'of Perpetrate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perpetrating', 'p. pr. & vb. n.', 'of Perpetrate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perpetrate', 'v. t.', 'To do or perform; to carry through; to execute,\n   commonly in a bad sense; to commit (as a crime, an offense); to be\n   guilty of; as, to perpetrate a foul deed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perpetration', 'n.', 'The act of perpetrating; a doing; -- commonly used of\n   doing something wrong, as a crime.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perpetration', 'n.', 'The thing perpetrated; an evil action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perpetrator', 'n.', 'One who perpetrates; esp., one who commits an offense\n   or crime.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perpetuable', 'a.', 'Capable of being perpetuated or continued.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perpetual', 'a.', 'Neverceasing; continuing forever or for an unlimited\n   time; unfailing; everlasting; continuous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perpetually', 'adv.', 'In a perpetual manner; constantly; continually.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perpetualty', 'n.', 'The state or condition of being perpetual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perpetuance', 'n.', 'Perpetuity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perpetuated', 'imp. & p. p.', 'of Perpetuate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perpetuating', 'p. pr. & vb. n.', 'of Perpetuate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perpetuate', 'v. t.', 'To make perpetual; to cause to endure, or to be\n   continued, indefinitely; to preserve from extinction or oblivion; to\n   eternize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perpetuate', 'a.', 'Made perpetual; perpetuated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perpetuation', 'n.', 'The act of making perpetual, or of preserving from\n   extinction through an endless existence, or for an indefinite period of\n   time; continuance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perpetuity', 'n.', 'The quality or state of being perpetual; as, the\n   perpetuity of laws.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perpetuity', 'n.', 'Something that is perpetual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perpetuity', 'n.', 'Endless time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perpetuity', 'n.', 'The number of years in which the simple interest of any\n   sum becomes equal to the principal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perpetuity', 'n.', 'The number of years purchase to be given for an\n   annuity to continue forever.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perpetuity', 'n.', 'A perpetual annuity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perpetuity', 'n.', 'Duration without limitations as to time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perpetuity', 'n.', 'The quality or condition of an estate by which it\n   becomes inalienable, either perpetually or for a very long period;\n   also, the estate itself so modified or perpetuated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perplexed', 'imp. & p. p.', 'of Perplex');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perplexing', 'p. pr. & vb. n.', 'of Perplex');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perplex', 'a.', 'To involve; to entangle; to make intricate or complicated,\n   and difficult to be unraveled or understood; as, to perplex one with\n   doubts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perplex', 'a.', 'To embarrass; to puzzle; to distract; to bewilder; to\n   confuse; to trouble with ambiguity, suspense, or anxiety.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perplex', 'a.', 'To plague; to vex; to tormen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perplex', 'a.', 'Intricate; difficult.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perplexed', 'a.', 'Entangled, involved, or confused; hence, embarrassd;\n   puzzled; doubtful; anxious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perplexing', 'a.', 'Embarrassing; puzzling; troublesome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perplexities', 'pl. ', 'of Perplexity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perplexity', 'n.', 'The quality or state of being perplexed or puzzled;\n   complication; intricacy; entanglement; distraction of mind through\n   doubt or difficulty; embarrassment; bewilderment; doubt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perplexiveness', 'n.', 'The quality of being perplexing; tendency to\n   perplex.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perplexly', 'adv.', 'Perplexedly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perpotation', 'n.', 'The act of drinking excessively; a drinking bout.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perquisite', 'n.', 'Something gained from a place or employment over and\n   above the ordinary salary or fixed wages for services rendered;\n   especially, a fee allowed by law to an officer for a specific service.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perquisite', 'n.', 'Things gotten by a mans own industry, or purchased\n   with his own money, as opposed to things which come to him by descent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perquisited', 'a.', 'Supplied with perquisites.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perquisition', 'n.', 'A thorough inquiry of search.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perradial', 'a.', 'Situated around the radii, or radial tubes, of a\n   radiate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perrie', 'n.', 'Precious stones; jewels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perrier', 'n.', 'A short mortar used formerly for throwing stone shot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perroquet', 'n.', 'See Paroquet, Parakeet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perruque', 'n.', 'See Peruke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perruquier', 'n.', 'A marker of perukes or wigs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perry', 'n.', 'A fermented liquor made from pears; pear cider.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perry', 'n.', 'A suddent squall. See Pirry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pers', 'a.', 'Light blue; grayish blue; -- a term applied to different\n   shades at different periods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pers', 'n.', 'A cloth of sky-blue color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Persalt', 'n.', 'A term formerly given to the salts supposed to be formed\n   respectively by neutralizing acids with certain peroxides.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Persant', 'a.', 'Piercing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perscrutation', 'n.', 'A thorough searching; a minute inquiry or scrutiny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Persecot', 'n.', 'See Persicot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Persecuted', 'imp. & p. p.', 'of Persecute');
  INSERT INTO words (word, word_type, description) VALUES  (    'Persecuting', 'p. pr. & vb. n.', 'of Persecute');
  INSERT INTO words (word, word_type, description) VALUES  (    'Persecute', 'v. t.', 'To pursue in a manner to injure, grieve, or afflict;\n   to beset with cruelty or malignity; to harass; especially, to afflict,\n   harass, punish, or put to death, for adherence to a particular\n   religious creed or mode of worship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Persecute', 'v. t.', 'To harass with importunity; to pursue with persistent\n   solicitations; to annoy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Persecution', 'n.', 'The act or practice of persecuting; especially, the\n   infliction of loss, pain, or death for adherence to a particular creed\n   or mode of worship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Persecution', 'n.', 'The state or condition of being persecuted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Persecution', 'n.', 'A carrying on; prosecution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Persecutor', 'n.', 'One who persecutes, or harasses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Persecutrix', 'n.', 'A woman who persecutes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perseid', 'n.', 'One of a group of shooting stars which appear yearly about\n   the 10th of August, and cross the heavens in paths apparently radiating\n   from the constellation Perseus. They are beleived to be fragments once\n   connected with a comet visible in 1862.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perseus', 'n.', 'A Grecian legendary hero, son of Jupiter and Danae, who\n   slew the Gorgon Medusa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perseus', 'n.', 'A consellation of the northern hemisphere, near Taurus and\n   Cassiopea. It contains a star cluster visible to the naked eye as a\n   nebula.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Persever', 'v. i.', 'To persevere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perseverance', 'n.', 'The act of persevering; persistence in anything\n   undertaken; continued pursuit or prosecution of any business, or\n   enterprise begun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perseverance', 'n.', 'Discrimination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perseverance', 'n.', 'Continuance in a state of grace until it is succeeded\n   by a state of glory; sometimes called final perseverance, and the\n   perseverance of the saints. See Calvinism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perseverant', 'a.', 'Persevering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Persevered', 'imp. & p. p.', 'of Persevere');
  INSERT INTO words (word, word_type, description) VALUES  (    'Persevering', 'p. pr. & vb. n.', 'of Persevere');
  INSERT INTO words (word, word_type, description) VALUES  (    'Persevere', 'v. i.', 'To persist in any business or enterprise undertaken;\n   to pursue steadily any project or course begun; to maintain a purpose\n   in spite of counter influences, opposition, or discouragement; not to\n   give or abandon what is undertaken.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Persevering', 'a.', 'Characterized by perseverance; persistent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Persian', 'a.', 'Of or pertaining to Persia, to the Persians, or to their\n   language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Persian', 'n.', 'A native or inhabitant of Persia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Persian', 'n.', 'The language spoken in Persia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Persian', 'n.', 'A thin silk fabric, used formerly for linings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Persian', 'n.', 'See Persian columns, under Persian, a.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Persic', 'a.', 'Of or relating to Persia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Persic', 'n.', 'The Persian language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Persicaria', 'n.', 'See Ladys thumb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Persicot', 'n.', 'A cordial made of the kernels of apricots, nectarines,\n   etc., with refined spirit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Persiflage', 'n.', 'Frivolous or bantering talk; a frivolous manner of\n   treating any subject, whether serious or otherwise; light raillery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Persifleur', 'n.', 'One who indulges in persiflage; a banterer; a quiz.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Persimmon', 'n.', 'An American tree (Diospyros Virginiana) and its fruit,\n   found from New York southward. The fruit is like a plum in appearance,\n   but is very harsh and astringent until it has been exposed to frost,\n   when it becomes palatable and nutritious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Persis', 'n.', 'A kind of coloring matter obtained from lichens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Persism', 'n.', 'A Persian idiom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Persisted', 'imp. & p. p.', 'of Persist');
  INSERT INTO words (word, word_type, description) VALUES  (    'Persisting', 'p. pr. & vb. n.', 'of Persist');
  INSERT INTO words (word, word_type, description) VALUES  (    'Persist', 'v. i.', 'To stand firm; to be fixed and unmoved; to stay; to\n   continue steadfastly; especially, to continue fixed in a course of\n   conduct against opposing motives; to persevere; -- sometimes conveying\n   an unfavorable notion, as of doggedness or obstinacy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Persistence', 'n.', 'Alt. of Persistency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Persistency', 'n.', 'The quality or state of being persistent; staying or\n   continuing quality; hence, in an unfavorable sense, doggedness;\n   obstinacy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Persistency', 'n.', 'The continuance of an effect after the cause which\n   first gave rise to it is removed');
  INSERT INTO words (word, word_type, description) VALUES  (    'Persistency', 'n.', 'The persistence of motion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Persistency', 'n.', 'Visual persistence, or persistence of the visual\n   impression; auditory persistence, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Persistent', 'a.', 'Inclined to persist; having staying qualities;\n   tenacious of position or purpose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Persistent', 'a.', 'Remaining beyond the period when parts of the same kind\n   sometimes fall off or are absorbed; permanent; as, persistent teeth or\n   gills; a persistent calyx; -- opposed to deciduous, and caducous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Persistently', 'adv.', 'In a persistent manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Persisting', 'a.', 'Inclined to persist; tenacious of purpose; persistent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Persistive', 'a.', 'See Persistent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Persolve', 'v. t.', 'To pay wholly, or fully.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Person', 'n.', 'A character or part, as in a play; a specific kind or\n   manifestation of individual character, whether in real life, or in\n   literary or dramatic representation; an assumed character.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Person', 'n.', 'The bodily form of a human being; body; outward appearance;\n   as, of comely person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Person', 'n.', 'A living, self-conscious being, as distinct from an animal\n   or a thing; a moral agent; a human being; a man, woman, or child.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Person', 'n.', 'A human being spoken of indefinitely; one; a man; as, any\n   person present.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Person', 'n.', 'A parson; the parish priest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Person', 'n.', 'Among Trinitarians, one of the three subdivisions of the\n   Godhead (the Father, the Son, and the Holy Ghost); an hypostasis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Person', 'n.', 'One of three relations or conditions (that of speaking,\n   that of being spoken to, and that of being spoken of) pertaining to a\n   noun or a pronoun, and thence also to the verb of which it may be the\n   subject.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Person', 'n.', 'A shoot or bud of a plant; a polyp or zooid of the compound\n   Hydrozoa Anthozoa, etc.; also, an individual, in the narrowest sense,\n   among the higher animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Person', 'v. t.', 'To represent as a person; to personify; to impersonate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Personae', 'pl. ', 'of Persona');
  INSERT INTO words (word, word_type, description) VALUES  (    'Persona', 'n.', 'Same as Person, n., 8.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Personable', 'a.', 'Having a well-formed body, or person; graceful; comely;\n   of good appearance; presentable; as, a personable man or woman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Personable', 'a.', 'Enabled to maintain pleas in court.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Personable', 'a.', 'Having capacity to take anything granted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Personage', 'n.', 'Form, appearance, or belongings of a person; the\n   external appearance, stature, figure, air, and the like, of a person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Personage', 'n.', 'Character assumed or represented.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Personage', 'n.', 'A notable or distinguished person; a conspicious or\n   peculiar character; as, an illustrious personage; a comely personage of\n   stature tall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Personal', 'a.', 'Pertaining to human beings as distinct from things.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Personal', 'a.', 'Of or pertaining to a particular person; relating to, or\n   affecting, an individual, or each of many individuals; peculiar or\n   proper to private concerns; not public or general; as, personal\n   comfort; personal desire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Personal', 'a.', 'Pertaining to the external or bodily appearance;\n   corporeal; as, personal charms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Personal', 'a.', 'Done in person; without the intervention of another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Personal', 'a.', 'Relating to an individual, his character, conduct,\n   motives, or private affairs, in an invidious and offensive manner; as,\n   personal reflections or remarks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Personal', 'a.', 'Denoting person; as, a personal pronoun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Personal', 'n.', 'A movable; a chattel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Personalism', 'n.', 'The quality or state of being personal; personality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Personalities', 'pl. ', 'of Personality');
  INSERT INTO words (word, word_type, description) VALUES  (    'Personality', 'n.', 'That which constitutes distinction of person;\n   individuality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Personality', 'n.', 'Something said or written which refers to the person,\n   conduct, etc., of some individual, especially something of a\n   disparaging or offensive nature; personal remarks; as, indulgence in\n   personalities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Personality', 'n.', 'That quality of a law which concerns the condition,\n   state, and capacity of persons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Personalized', 'imp. & p. p.', 'of Personalize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Personalizing', 'p. pr. & vb. n.', 'of Personalize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Personalize', 'v. t.', 'To make personal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Personally', 'adv.', 'In a personal manner; by bodily presence; in person;\n   not by representative or substitute; as, to deliver a letter\n   personally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Personally', 'adv.', 'With respect to an individual; as regards the person;\n   individually; particularly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Personally', 'adv.', 'With respect to ones individuality; as regards ones\n   self; as, personally I have no feeling in the matter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Personalty', 'n.', 'The state of being a person; personality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Personalty', 'n.', 'Personal property, as distinguished from realty or real\n   property.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Personated', 'imp. & p. p.', 'of Personate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Personating', 'p. pr. & vb. n.', 'of Personate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Personate', 'v. t.', 'To celebrate loudly; to extol; to praise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Personate', 'v. t.', 'To assume the character of; to represent by a\n   fictitious appearance; to act the part of; hence, to counterfeit; to\n   feign; as, he tried to personate his brother; a personated devotion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Personate', 'v. t.', 'To set forth in an unreal character; to disguise; to\n   mask.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Personate', 'v. t.', 'To personify; to typify; to describe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Personate', 'v. i.', 'To play or assume a character.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Personate', 'a.', 'Having the throat of a bilabiate corolla nearly closed\n   by a projection of the base of the lower lip; masked, as in the flower\n   of the snapdragon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Personation', 'n.', 'The act of personating, or conterfeiting the person or\n   character of another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Personator', 'n.', 'One who personates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Personeity', 'n.', 'Personality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Personification', 'n.', 'The act of personifying; impersonation;\n   embodiment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Personification', 'n.', 'A figure of speech in which an inanimate object or\n   abstract idea is represented as animated, or endowed with personality;\n   prosopop/ia; as, the floods clap their hands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Personifier', 'n.', 'One who personifies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Personified', 'imp. & p. p.', 'of Personify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Personifying', 'p. pr. & vb. n.', 'of Personify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Personify', 'v. t.', 'To regard, treat, or represent as a person; to\n   represent as a rational being.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Personify', 'v. t.', 'To be the embodiment or personification of; to\n   impersonate; as, he personifies the law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Personize', 'v. t.', 'To personify.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Personnel', 'n.', 'The body of persons employed in some public service, as\n   the army, navy, etc.; -- distinguished from materiel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perspective', 'n.', 'Of or pertaining to the science of vision; optical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perspective', 'n.', 'Pertaining to the art, or in accordance with the laws,\n   of perspective.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perspective', 'a.', 'A glass through which objects are viewed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perspective', 'a.', 'That which is seen through an opening; a view; a\n   vista.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perspective', 'a.', 'The effect of distance upon the appearance of objects,\n   by means of which the eye recognized them as being at a more or less\n   measurable distance. Hence, aerial perspective, the assumed greater\n   vagueness or uncertainty of outline in distant objects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perspective', 'a.', 'The art and the science of so delineating objects that\n   they shall seem to grow smaller as they recede from the eye; -- called\n   also linear perspective.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perspective', 'a.', 'A drawing in linear perspective.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perspectively', 'adv.', 'Optically; as through a glass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perspectively', 'adv.', 'According to the rules of perspective.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perspectograph', 'n.', 'An instrument for obtaining, and transferring to a\n   picture, the points and outlines of objects, so as to represent them in\n   their proper geometrical relations as viewed from some one point.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perspectography', 'n.', 'The science or art of delineating objects\n   according to the laws of perspective; the theory of perspective.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perspicable', 'a.', 'Discernible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perspicacious', 'a.', 'Having the power of seeing clearly; quick-sighted;\n   sharp of sight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perspicacious', 'a.', 'Fig.: Of acute discernment; keen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perspicacity', 'n.', 'The state of being perspicacious; acuteness of sight\n   or of intelligence; acute discernment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perspicacy', 'n.', 'Perspicacity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perspicience', 'n.', 'The act of looking sharply.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perspicil', 'n.', 'An optical glass; a telescope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perspicuity', 'n.', 'The quality or state of being transparent or\n   translucent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perspicuity', 'n.', 'The quality of being perspicuous to the understanding;\n   clearness of expression or thought.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perspicuity', 'n.', 'Sagacity; perspicacity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perspicuous', 'a.', 'Capable of being through; transparent; translucent;\n   not opaque.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perspicuous', 'a.', 'Clear to the understanding; capable of being clearly\n   understood; clear in thought or in expression; not obscure or\n   ambiguous; as, a perspicuous writer; perspicuous statements.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perspirability', 'n.', 'The quality or state of being perspirable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perspirable', 'a.', 'Capable of being perspired.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perspirable', 'a.', 'Emitting perspiration; perspiring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perspiration', 'n.', 'The act or process of perspiring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perspiration', 'n.', 'That which is excreted through the skin; sweat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perspirative', 'a.', 'Performing the act of perspiration; perspiratory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perspiratory', 'a.', 'Of, pertaining to, or producing, perspiration; as,\n   the perspiratory ducts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perspired', 'imp. & p. p.', 'of Perspire');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perspiring', 'p. pr. & vb. n.', 'of Perspire');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perspire', 'v. i.', 'To excrete matter through the skin; esp., to excrete\n   fluids through the pores of the skin; to sweat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perspire', 'v. i.', 'To be evacuated or excreted, or to exude, through the\n   pores of the skin; as, a fluid perspires.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perspire', 'v. t.', 'To emit or evacuate through the pores of the skin; to\n   sweat; to excrete through pores.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perstreperous', 'a.', 'Noisy; obstreperous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perstringe', 'v. t.', 'To touch; to graze; to glance on.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perstringe', 'v. t.', 'To criticise; to touch upon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Persuadable', 'a.', 'That may be persuaded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Persuaded', 'imp. & p. p.', 'of Persuade');
  INSERT INTO words (word, word_type, description) VALUES  (    'Persuading', 'p. pr. & vb. n.', 'of Persuade');
  INSERT INTO words (word, word_type, description) VALUES  (    'Persuade', 'v. t.', 'To influence or gain over by argument, advice,\n   entreaty, expostulation, etc.; to draw or incline to a determination by\n   presenting sufficient motives.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Persuade', 'v. t.', 'To try to influence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Persuade', 'v. t.', 'To convince by argument, or by reasons offered or\n   suggested from reflection, etc.; to cause to believe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Persuade', 'v. t.', 'To inculcate by argument or expostulation; to advise;\n   to recommend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Persuade', 'v. i.', 'To use persuasion; to plead; to prevail by persuasion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Persuade', 'n.', 'Persuasion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Persuaded', 'p. p. & a.', 'Prevailed upon; influenced by argument or\n   entreaty; convinced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Persuader', 'n.', 'One who, or that which, persuades or influences.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Persuasibility', 'n.', 'Capability of being persuaded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Persuasible', 'a.', 'Capable of being persuaded; persuadable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Persuasible', 'a.', 'Persuasive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Persuasion', 'n.', 'The act of persuading; the act of influencing the mind\n   by arguments or reasons offered, or by anything that moves the mind or\n   passions, or inclines the will to a determination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Persuasion', 'n.', 'The state of being persuaded or convinced; settled\n   opinion or conviction, which has been induced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Persuasion', 'n.', 'A creed or belief; a sect or party adhering to a\n   certain creed or system of opinions; as, of the same persuasion; all\n   persuasions are agreed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Persuasion', 'n.', 'The power or quality of persuading; persuasiveness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Persuasion', 'n.', 'That which persuades; a persuasive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Persuasive', 'a.', 'Tending to persuade; having the power of persuading;\n   as, persuasive eloquence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Persuasive', 'n.', 'That which persuades; an inducement; an incitement; an\n   exhortation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Persuasory', 'a.', 'Persuasive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Persulphate', 'n.', 'A sulphate of the peroxide of any base.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Persulphide', 'n.', 'A sulphide containing more sulphur than some other\n   compound of the same elements; as, iron pyrites is a persulphide; --\n   formerly called persulphuret.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Persulphocyanate', 'n.', 'A salt of persulphocyanic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Persulphocyanic', 'a.', 'Pertaining to, or designating, a yellow\n   crystalline substance (called also perthiocyanic acid), analogous to\n   sulphocyanic acid, but containing more sulphur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Persulphocyanogen', 'n.', 'An orange-yellow substance, produced by the\n   action of chlorine or boiling dilute nitric acid and sulphocyanate of\n   potassium; -- called also pseudosulphocyanogen, perthiocyanogen, and\n   formerly sulphocyanogen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Persulphuret', 'n.', 'A persulphide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pert', 'a.', 'Open; evident; apert.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pert', 'a.', 'Lively; brisk; sprightly; smart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pert', 'a.', 'Indecorously free, or presuming; saucy; bold; impertinent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pert', 'v. i.', 'To behave with pertness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pertained', 'imp. & p. p.', 'of Pertain');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pertaining', 'p. pr. & vb. n.', 'of Pertain');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pertain', 'v. i.', 'To belong; to have connection with, or dependence on,\n   something, as an appurtenance, attribute, etc.; to appertain; as,\n   saltness pertains to the ocean; flowers pertain to plant life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pertain', 'v. i.', 'To have relation or reference to something.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perterebration', 'n.', 'The act of boring through.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perthiocyanogen', 'n.', 'Same as Persulphocyanogen.');
