  INSERT INTO words (word, word_type, description) VALUES  (    'Pertinacious', 'a.', 'Holding or adhering to any opinion, purpose, or\n   design, with obstinacy; perversely persistent; obstinate; as,\n   pertinacious plotters; a pertinacious beggar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pertinacious', 'a.', 'Resolute; persevering; constant; steady.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pertinacity', 'n.', 'The quality or state of being pertinacious; obstinacy;\n   perseverance; persistency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pertinacy', 'n.', 'The quality or state of being pertinent; pertinence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pertinacy', 'n.', 'Pertinacity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pertinate', 'a.', 'Pertinacious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pertinately', 'adv.', 'Pertinaciously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pertinence', 'n.', 'Alt. of Pertinency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pertinency', 'n.', 'The quality or state of being pertinent; justness of\n   relation to the subject or matter in hand; fitness; appositeness;\n   relevancy; suitableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pertinent', 'a.', 'Belonging or related to the subject or matter in hand;\n   fit or appropriate in any way; adapted to the end proposed; apposite;\n   material; relevant; as, pertinent illustrations or arguments; pertinent\n   evidence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pertinent', 'a.', 'Regarding; concerning; belonging; pertaining.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pertly', 'adv.', 'In a pert manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pertness', 'n.', 'The quality or state of being pert.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pertransient', 'a.', 'Passing through or over.');
