  INSERT INTO words (word, word_type, description) VALUES  (    'Perturb', 'v. t.', 'To disorder; to confuse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perturbability', 'n.', 'The quality or state of being perturbable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perturbable', 'a.', 'Liable to be perturbed or agitated; liable to be\n   disturbed or disquieted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perturbance', 'n.', 'Disturbance; perturbation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perturbate', 'v. t.', 'To perturb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perturbate', 'a.', 'Perturbed; agitated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perturbation', 'n.', 'The act of perturbing, or the state of being\n   perturbed; esp., agitation of mind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perturbation', 'n.', 'A disturbance in the regular elliptic or other motion\n   of a heavenly body, produced by some force additional to that which\n   causes its regular motion; as, the perturbations of the planets are\n   caused by their attraction on each other.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perturbational', 'a.', 'Of or pertaining to perturbation, esp. to the\n   perturbations of the planets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perturbative', 'a.', 'Tending to cause perturbation; disturbing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perturbator', 'n.', 'A perturber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perturbed', 'a.', 'Agitated; disturbed; troubled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perturber', 'n.', 'One who, or that which, perturbs, or cause perturbation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pertusate', 'a.', 'Pierced at the apex.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pertuse', 'a.', 'Alt. of Pertused');
