  INSERT INTO words (word, word_type, description) VALUES  (    'Peruvian', 'n.', 'A native or an inhabitant of Peru.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pervaded', 'imp. & p. p.', 'of Pervade');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pervading', 'p. pr. & vb. n.', 'of Pervade');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pervade', 'v. t.', 'To pass or flow through, as an aperture, pore, or\n   interstice; to permeate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pervade', 'v. t.', 'To pass or spread through the whole extent of; to be\n   diffused throughout.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pervasion', 'n.', 'The act of pervading, passing, or spreading through the\n   whole extent of a thing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pervasive', 'a.', 'Tending to pervade, or having power to spread\n   throughout; of a pervading quality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perverse', 'a.', 'Turned aside; hence, specifically, turned away from the\n   right; willfully erring; wicked; perverted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perverse', 'a.', 'Obstinate in the wrong; stubborn; intractable; hence,\n   wayward; vexing; contrary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perversed', 'a.', 'Turned aside.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perversedly', 'adv.', 'Perversely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perversely', 'adv.', 'In a perverse manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perverseness', 'n.', 'The quality or state of being perverse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perversion', 'n.', 'The act of perverting, or the state of being perverted;\n   a turning from truth or right; a diverting from the true intent or\n   object; a change to something worse; a turning or applying to a wrong\n   end or use.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perversity', 'n.', 'The quality or state of being perverse; perverseness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perversive', 'a.', 'Tending to pervert.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perverted', 'imp. & p. p.', 'of Pervert');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perverting', 'p. pr. & vb. n.', 'of Pervert');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pervert', 'v. t.', 'To turnanother way; to divert.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pervert', 'v. t.', 'To turn from truth, rectitude, or propriety; to divert\n   from a right use, end, or way; to lead astray; to corrupt; also, to\n   misapply; to misinterpret designedly; as, to pervert ones words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pervert', 'v. i.', 'To become perverted; to take the wrong course.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pervert', 'n.', 'One who has been perverted; one who has turned to error,\n   especially in religion; -- opposed to convert. See the Synonym of\n   Convert.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perverter', 'n.', 'One who perverts (a person or thing).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pervertible', 'a.', 'Capable of being perverted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pervestigate', 'v. t.', 'To investigate thoroughly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pervestigation', 'n.', 'Thorough investigation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pervial', 'a.', 'Pervious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pervicacious', 'a.', 'Obstinate; willful; refractory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pervicacity', 'n.', 'Obstinacy; pervicaciousness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pervicacy', 'n.', 'Pervicacity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pervigilation', 'n.', 'Careful watching.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pervious', 'a.', 'Admitting passage; capable of being penetrated by another\n   body or substance; permeable; as, a pervious soil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pervious', 'a.', 'Capable of being penetrated, or seen through, by physical\n   or mental vision.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pervious', 'a.', 'Capable of penetrating or pervading.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pervious', 'a.', 'Open; -- used synonymously with perforate, as applied to\n   the nostrils or birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Perviousness', 'n.', 'The quality or state of being pervious; as, the\n   perviousness of glass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pervis', 'n.', 'See Parvis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pery', 'n.', 'A pear tree. See Pirie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pedes', 'pl. ', 'of Pes');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pes', 'n.', 'The distal segment of the hind limb of vertebrates, including\n   the tarsus and foot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pesade', 'n.', 'The motion of a horse when, raising his fore quarters, he\n   keeps his hind feet on the ground without advancing; rearing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pesage', 'n.', 'A fee, or toll, paid for the weighing of merchandise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pesane', 'n.', 'See Pusane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pesanted', 'a.', 'Made heavy or dull; debased.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peschito', 'n.', 'See Peshito.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pese', 'n.', 'A pea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peseta', 'n.', 'A Spanish silver coin, and money of account, equal to about\n   nineteen cents, and divided into 100 centesimos.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peshito', 'n.', 'Alt. of Peshitto');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peshitto', 'n.', 'The earliest Syriac version of the Old Testament,\n   translated from Hebrew; also, the incomplete Syriac version of the New\n   Testament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pesky', 'a.', 'Pestering; vexatious; troublesome. Used also as an\n   intensive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peso', 'n.', 'A Spanish dollar; also, an Argentine, Chilian, Colombian,\n   etc., coin, equal to from 75 cents to a dollar; also, a pound weight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pessaries', 'pl. ', 'of Pessary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pessary', 'n.', 'An instrument or device to be introduced into and worn in\n   the vagina, to support the uterus, or remedy a malposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pessary', 'n.', 'A medicinal substance in the form of a bolus or mass,\n   designed for introduction into the vagina; a vaginal suppository.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pessimism', 'n.', 'The opinion or doctrine that everything in nature is\n   ordered for or tends to the worst, or that the world is wholly evil; --\n   opposed to optimism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pessimism', 'n.', 'A disposition to take the least hopeful view of things.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pessimist', 'n.', 'One who advocates the doctrine of pessimism; -- opposed\n   to optimist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pessimist', 'n.', 'One who looks on the dark side of things.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pessimist', 'a.', 'Alt. of Pessimistic');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pessimistic', 'a.', 'Of or pertaining to pessimism; characterized by\n   pessimism; gloomy; foreboding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pessimistical', 'a.', 'Pessimistic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pessimize', 'v. i.', 'To hold or advocate the doctrine of pessimism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pessuli', 'pl. ', 'of Pessulus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pessulus', 'n.', 'A delicate bar of cartilage connecting the dorsal and\n   ventral extremities of the first pair of bronchial cartilages in the\n   syrinx of birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pest', 'n.', 'A fatal epidemic disease; a pestilence; specif., the plague.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pest', 'n.', 'Anything which resembles a pest; one who, or that which, is\n   troublesome, noxious, mischievous, or destructive; a nuisance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pestalozzian', 'a.', 'Belonging to, or characteristic of, a system of\n   elementary education which combined manual training with other\n   instruction, advocated and practiced by Jean Henri Pestalozzi\n   (1746-1827), a Swiss teacher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pestalozzian', 'n.', 'An advocate or follower of the system of Pestalozzi.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pestalozzianism', 'n.', 'The system of education introduced by Pestalozzi.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pestered', 'imp. & p. p.', 'of Pester');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pestering', 'p. pr. & vb. n.', 'of Pester');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pester', 'v. t.', 'To trouble; to disturb; to annoy; to harass with petty\n   vexations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pester', 'v. t.', 'To crowd together in an annoying way; to overcrowd; to\n   infest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pesterer', 'n.', 'One who pesters or harasses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pesterment', 'n.', 'The act of pestering, or the state of being pestered;\n   vexation; worry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pesterous', 'a.', 'Inclined to pester. Also, vexatious; encumbering;\n   burdensome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pestful', 'a.', 'Pestiferous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pesthouse', 'n.', 'A house or hospital for persons who are infected with\n   any pestilential disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pestiduct', 'n.', 'That which conveys contagion or infection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pestiferous', 'a.', 'Pest-bearing; pestilential; noxious to health;\n   malignant; infectious; contagious; as, pestiferous bodies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pestiferous', 'a.', 'Noxious to peace, to morals, or to society; vicious;\n   hurtful; destructive; as, a pestiferous demagogue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pestiferously', 'adv.', 'In a pestiferuos manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pestilence', 'n.', 'Specifically, the disease known as the plague; hence,\n   any contagious or infectious epidemic disease that is virulent and\n   devastating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pestilence', 'n.', 'Fig.: That which is pestilent, noxious, or pernicious\n   to the moral character of great numbers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pestilent', 'a.', 'Pestilential; noxious; pernicious; mischievous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pestilential', 'a.', 'Having the nature or qualities of a pestilence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pestilential', 'a.', 'Hence: Mischievous; noxious; pernicious; morally\n   destructive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pestilentially', 'adv.', 'Pestilently.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pestilentious', 'a.', 'Pestilential.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pestilently', 'adv.', 'In a pestilent manner; mischievously; destructively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pestilentness', 'n.', 'The quality of being pestilent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pestilation', 'n.', 'The act of pounding and bruising with a pestle in a\n   mortar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pestle', 'n.', 'An implement for pounding and breaking or braying\n   substances in a mortar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pestle', 'n.', 'A constables or bailiffs staff; -- so called from its\n   shape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pestle', 'n.', 'The leg and leg bone of an animal, especially of a pig; as,\n   a pestle of pork.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pestled', 'imp. & p. p.', 'of Pestle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pestling', 'p. pr. & vb. n.', 'of Pestle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pestle', 'v. t. & i.', 'To pound, pulverize, bray, or mix with a pestle, or\n   as with a pestle; to use a pestle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pet', 'n.', 'A cade lamb; a lamb brought up by hand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pet', 'n.', 'Any person or animal especially cherished and indulged; a\n   fondling; a darling; often, a favorite child.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pet', 'n.', 'A slight fit of peevishness or fretfulness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pet', 'a.', 'Petted; indulged; admired; cherished; as, a pet child; a pet\n   lamb; a pet theory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petted', 'imp. & p. p.', 'of Pet');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petting', 'p. pr. & vb. n.', 'of Pet');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pet', 'v. t.', 'To treat as a pet; to fondle; to indulge; as, she was\n   petted and spoiled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pet', 'v. i.', 'To be a pet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petal', 'n.', 'One of the leaves of the corolla, or the colored leaves of a\n   flower. See Corolla, and Illust. of Flower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petal', 'n.', 'One of the expanded ambulacra which form a rosette on the\n   black of certain Echini.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petaled', 'a.', 'Having petals; as, a petaled flower; -- opposed to\n   apetalous, and much used in compounds; as, one-petaled, three-petaled,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petaliferous', 'a.', 'Bearing petals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petaliform', 'a.', 'Having the form of a petal; petaloid; petal-shaped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petaline', 'a.', 'Pertaining to a petal; attached to, or resembling, a\n   petal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petalism', 'n.', 'A form of sentence among the ancient Syracusans by which\n   they banished for five years a citizen suspected of having dangerous\n   influence or ambition. It was similar to the ostracism in Athens; but\n   olive leaves were used instead of shells for ballots.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petalite', 'n.', 'A rare mineral, occurring crystallized and in cleavable\n   masses, usually white, or nearly so, in color. It is a silicate of\n   aluminia and lithia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petalody', 'n.', 'The metamorphosis of various floral organs, usually\n   stamens, into petals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petaloid', 'a.', 'Petaline.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petaloideous', 'a.', 'Having the whole or part of the perianth petaline.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petalosticha', 'n. pl.', 'An order of Echini, including the irregular sea\n   urchins, as the spatangoids. See Spatangoid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petalous', 'a.', 'Having petals; petaled; -- opposed to apetalous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petala', 'pl. ', 'of Petalum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petalum', 'n.', 'A petal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petar', 'n.', 'See Petard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petard', 'n.', 'A case containing powder to be exploded, esp. a conical or\n   cylindrical case of metal filled with powder and attached to a plank,\n   to be exploded against and break down gates, barricades, drawbridges,\n   etc. It has been superseded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petardeer', 'n.', 'Alt. of Petardier');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petardier', 'n.', 'One who managed a petard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petasus', 'n.', 'The winged cap of Mercury; also, a broad-brimmed,\n   low-crowned hat worn by Greeks and Romans.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petaurist', 'n.', 'Any flying marsupial of the genera Petaurus,\n   Phalangista, Acrobata, and allied genera. See Flying mouse, under\n   Flying, and Phalangister.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petechiae', 'n. pl.', 'Small crimson, purple, or livid spots, like\n   flea-bites, due to extravasation of blood, which appear on the skin in\n   malignant fevers, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petechial', 'a.', 'Characterized by, or pertaining to, petechiae; spotted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peter', 'n.', 'A common baptismal name for a man. The name of one of the\n   apostles,');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petered', 'imp. & p. p.', 'of Peter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petering', 'p. pr. & vb. n.', 'of Peter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peter', 'v. i.', 'To become exhausted; to run out; to fail; -- used\n   generally with out; as, that mine has petered out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peterel', 'n.', 'See Petrel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peterero', 'n.', 'See Pederero.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petermen', 'pl. ', 'of Peterman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peterman', 'n.', 'A fisherman; -- so called after the apostle Peter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petersham', 'n.', 'A rough, knotted woolen cloth, used chiefly for mens\n   overcoats; also, a coat of that material.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peterwort', 'n.', 'See Saint Peters-wort, under Saint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petiolar', 'a.', 'Alt. of Petiolary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petiolary', 'a.', 'Of or pertaining to petiole, or proceeding from it; as,\n   a petiolar tendril; growing or supported upon a petiole; as, a petiolar\n   gland; a petiolar bud.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petiolate', 'a.', 'Alt. of Petiolated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petiolated', 'a.', 'Having a stalk or petiole; as, a petioleate leaf; the\n   petiolated abdomen of certain Hymenoptera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petiole', 'n.', 'A leafstalk; the footstalk of a leaf, connecting the blade\n   with the stem. See Illust. of Leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petiole', 'n.', 'A stalk or peduncle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petioled', 'a.', 'Petiolate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petiolulate', 'a.', 'Supported by its own petiolule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petiolule', 'n.', 'A small petiole, or the petiole of a leaflet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petit', 'a.', 'Small; little; insignificant; mean; -- Same as Petty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petition', 'n.', 'A prayer; a supplication; an imploration; an entreaty;\n   especially, a request of a solemn or formal kind; a prayer to the\n   Supreme Being, or to a person of superior power, rank, or authority;\n   also, a single clause in such a prayer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petition', 'n.', 'A formal written request addressed to an official person,\n   or to an organized body, having power to grant it; specifically (Law),\n   a supplication to government, in either of its branches, for the\n   granting of a particular grace or right; -- in distinction from a\n   memorial, which calls certain facts to mind; also, the written\n   document.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petitioned', 'imp. & p. p.', 'of Petition');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petitioning', 'p. pr. & vb. n.', 'of Petition');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petition', 'v. t.', 'To make a prayer or request to; to ask from; to\n   solicit; to entreat; especially, to make a formal written supplication,\n   or application to, as to any branch of the government; as, to petition\n   the court; to petition the governor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petition', 'v. i.', 'To make a petition or solicitation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petitionarily', 'adv.', 'By way of begging the question; by an assumption.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petitionary', 'a.', 'Supplicatory; making a petition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petitionary', 'a.', 'Containing a petition; of the nature of a petition;\n   as, a petitionary epistle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petitionee', 'n.', 'A person cited to answer, or defend against, a\n   petition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petitioner', 'n.', 'One who presents a petition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petitioning', 'n.', 'The act of presenting apetition; a supplication.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petitor', 'n.', 'One who seeks or asks; a seeker; an applicant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petitory', 'a.', 'Petitioning; soliciting; supplicating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petong', 'n.', 'See Packfong.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petralogy', 'n.', 'See Petrology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petrary', 'n.', 'An ancient war engine for hurling stones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petrean', 'a.', 'Of or pertaining to to rock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petre', 'n.', 'See Saltpeter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petrel', 'n.', 'Any one of numerous species of longwinged sea birds\n   belonging to the family Procellaridae. The small petrels, or Mother\n   Careys chickens, belong to Oceanites, Oceanodroma, Procellaria, and\n   several allied genera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petrescence', 'n.', 'The process of changing into stone; petrification.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petrescent', 'a.', 'Petrifying; converting into stone; as, petrescent\n   water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petrifaction', 'n.', 'The process of petrifying, or changing into stone;\n   conversion of any organic matter (animal or vegetable) into stone, or a\n   substance of stony hardness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petrifaction', 'n.', 'The state or condition of being petrified.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petrifaction', 'n.', 'That which is petrified; popularly, a body incrusted\n   with stony matter; an incrustation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petrifaction', 'n.', 'Fig.: Hardness; callousness; obduracy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petrifactive', 'a.', 'Having the quality of converting organic matter into\n   stone; petrifying.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petrifactive', 'a.', 'Pertaining to, or characterized by, petrifaction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petrific', 'a.', 'Petrifying; petrifactive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petrificate', 'v. t.', 'To petrify.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petrification', 'n.', 'See Petrifaction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petrification', 'n.', 'Fig.: Obduracy; callousness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petrified', 'imp. & p. p.', 'of Petrify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petrifying', 'p. pr. & vb. n.', 'of Petrify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petrify', 'v. t.', 'To convert, as any animal or vegetable matter, into\n   stone or stony substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petrify', 'v. t.', 'To make callous or obdurate; to stupefy; to paralyze;\n   to transform; as by petrifaction; as, to petrify the heart. Young.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petrify', 'v. i.', 'To become stone, or of a stony hardness, as organic\n   matter by calcareous deposits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petrify', 'v. i.', 'Fig.: To become stony, callous, or obdurate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petrine', 'a.', 'Of or pertaining to St.Peter; as, the Petrine Epistles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petro-', '', 'A combining form from Gr. / a rock, / a stone; as, petrology,\n   petroglyphic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petrogale', 'n.', 'Any Australian kangaroo of the genus Petrogale, as the\n   rock wallaby (P. penicillata).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petroglyphic', 'a.', 'Of or pertaining to petroglyphy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petroglyphy', 'n.', 'The art or operation of carving figures or\n   inscriptions on rock or stone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petrographic', 'a.', 'Alt. of Petrographical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petrographical', 'a.', 'Pertaining to petrography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petrography', 'n.', 'The art of writing on stone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petrography', 'n.', 'The scientific description of rocks; that department\n   of science which investigates the constitution of rocks; petrology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petrohyoid', 'a.', 'Pertaining to petrous, oe periotic, portion of the\n   skull and the hyoid arch; as, the petrohyoid muscles of the frog.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petrol', 'n.', 'Petroleum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petrolatum', 'n.', 'A semisolid unctuous substance, neutral, and without\n   taste or odor, derived from petroleum by distilling off the lighter\n   portions and purifying the residue. It is a yellowish, fatlike mass,\n   transparent in thin layers, and somewhat fluorescent. It is used as a\n   bland protective dressing, and as a substitute for fatty materials in\n   ointments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petroleum', 'n.', 'Rock oil, mineral oil, or natural oil, a dark brown or\n   greenish inflammable liquid, which, at certain points, exists in the\n   upper strata of the earth, from whence it is pumped, or forced by\n   pressure of the gas attending it. It consists of a complex mixture of\n   various hydrocarbons, largely of the methane series, but may vary much\n   in appearance, composition, and properties. It is refined by\n   distillation, and the products include kerosene, benzine, gasoline,\n   paraffin, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petroleur', 'n. f.', 'Alt. of Petroleuse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petroleuse', 'n. f.', 'One who makes use of petroleum for incendiary\n   purposes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petroline', 'n.', 'A paraffin obtained from petroleum from Rangoon in\n   India, and practically identical with ordinary paraffin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petrologic', 'a.', 'Alt. of Petrological');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petrological', 'a.', 'Of or pertaining to petrology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petrologically', 'adv.', 'According to petrology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petrologist', 'n.', 'One who is versed in petrology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petrology', 'n.', 'The department of science which is concerned with the\n   mineralogical and chemical composition of rocks, and with their\n   classification: lithology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petrology', 'n.', 'A treatise on petrology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petromastoid', 'a.', 'Of or pertaining to the petrous and mastoid parts of\n   the temporal bone, periotic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petromyzont', 'n.', 'A lamprey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petronel', 'n.', 'A sort of hand cannon, or portable firearm, used in\n   France in the 15th century.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petrosal', 'a.', 'Hard; stony; petrous; as, the petrosal bone; petrosal\n   part of the temporal bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petrosal', 'a.', 'Of, pertaining to, or in the region of, the petrous, or\n   petrosal, bone, or the corresponding part of the temporal bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petrosal', 'n.', 'A petrosal bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petrosal', 'n.', 'The auditory capsule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petrosilex', 'n.', 'Felsite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petrosilicious', 'a.', 'Containing, or consisting of, petrosilex.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petrostearine', 'n.', 'A solid unctuous material, of which candles are\n   made.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petrous', 'a.', 'Like stone; hard; stony; rocky; as, the petrous part of\n   the temporal bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petrous', 'a.', 'Same as Petrosal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pettichaps', 'n.', 'See Pettychaps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petticoat', 'n.', 'A loose under-garment worn by women, and covering the\n   body below the waist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pettifogged', 'imp. & p. p.', 'of Pettifog');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pettifogging', 'p. pr. & vb. n.', 'of Pettifog');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pettifog', 'v. i.', 'To do a petty business as a lawyer; also, to do law\n   business in a petty or tricky way.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pettifog', 'v. t.', 'To advocate like a pettifogger; to argue trickily; as,\n   to pettifog a claim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pettifogger', 'n.', 'A lawyer who deals in petty cases; an attorney whose\n   methods are mean and tricky; an inferior lawyer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'ies', 'pl. ', 'of Pettifoggery');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pettifoggery', 'n.', 'The practice or arts of a pettifogger; disreputable\n   tricks; quibbles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pettifogging', 'a.', 'Paltry; quibbling; mean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pettifogging', 'n.', 'Pettifoggery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pettifogulize', 'v. i.', 'To act as a pettifogger; to use contemptible\n   tricks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pettily', 'adv.', 'In a petty manner; frivolously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pettiness', 'n.', 'The quality or state of being petty or paltry;\n   littleness; meanness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pettish', 'a.', 'Fretful; peevish; moody; capricious; inclined to ill\n   temper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pettitoes', 'n. pl.', 'The toes or feet of a pig, -- often used as food;\n   sometimes, in contempt, the human feet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petto', 'n.', 'The breast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petty', 'superl.', 'Little; trifling; inconsiderable; also, inferior;\n   subordinate; as, a petty fault; a petty prince.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pettychaps', 'n.', 'Any one of several species of small European singing\n   birds of the subfamily Sylviinae, as the willow warbler, the\n   chiff-chaff, and the golden warbler (Sylvia hortensis).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pettywhin', 'n.', 'The needle furze. See under Needle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petulance', 'n.', 'Alt. of Petulancy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petulancy', 'n.', 'The quality or state of being petulant; temporary\n   peevishness; pettishness; capricious ill humor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petulant', 'a.', 'Forward; pert; insolent; wanton.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petulant', 'a.', 'Capriciously fretful; characterized by ill-natured\n   freakishness; irritable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petulantly', 'adv.', 'In a petulant manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petulcity', 'n.', 'Wantonness; friskiness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petulcous', 'a.', 'Wanton; frisky; lustful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petunia', 'n.', 'A genus of solanaceous herbs with funnelform or\n   salver-shaped corollas. Two species are common in cultivation, Petunia\n   violacera, with reddish purple flowers, and P. nyctaginiflora, with\n   white flowers. There are also many hybrid forms with variegated\n   corollas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petunse', 'n.', 'Alt. of Petuntze');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petuntse', 'n.', 'Alt. of Petuntze');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petuntze', 'n.', 'Powdered fledspar, kaolin, or quartz, used in the\n   manufacture of porcelain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petworth marble', '', 'A kind of shell marble occurring in the Wealden clay\n   at Petworth, in Sussex, England; -- called also Sussex marble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Petzite', 'n.', 'A telluride of silver and gold, related to hessite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peucedanin', 'n.', 'A tasteless white crystalline substance, extracted from\n   the roots of the sulphurwort (Peucedanum), masterwort (Imperatoria),\n   and other related plants; -- called also imperatorin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peucil', 'n.', 'A liquid resembling camphene, obtained by treating\n   turpentine hydrochloride with lime.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pew', 'n.', 'One of the compartments in a church which are separated by low\n   partitions, and have long seats upon which several persons may sit; --\n   sometimes called slip. Pews were originally made square, but are now\n   usually long and narrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pew', 'n.', 'Any structure shaped like a church pew, as a stall, formerly\n   used by money lenders, etc.; a box in theater; a pen; a sheepfold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pew', 'v. t.', 'To furnish with pews.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pewee', 'n.', 'A common American tyrant flycatcher (Sayornis phoebe, or S.\n   fuscus). Called also pewit, and phoebe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pewee', 'n.', 'The woodcock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pewet', 'n.', 'Same as Pewit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pewfellow', 'n.', 'One who occupies the same pew with another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pewfellow', 'n.', 'An intimate associate; a companion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pewit', 'n.', 'The lapwing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pewit', 'n.', 'The European black-headed, or laughing, gull (Xema\n   ridibundus). See under Laughing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pewit', 'n.', 'The pewee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pewter', 'n.', 'A hard, tough, but easily fusible, alloy, originally\n   consisting of tin with a little lead, but afterwards modified by the\n   addition of copper, antimony, or bismuth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pewter', 'n.', 'Utensils or vessels made of pewter, as dishes, porringers,\n   drinking vessels, tankards, pots.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pewterer', 'n.', 'One whose occupation is to make utensils of pewter; a\n   pewtersmith.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pewtery', 'a.', 'Belonging to, or resembling, pewter; as, a pewtery taste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pexity', 'n.', 'Nap of cloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peyers glands', '', 'Patches of lymphoid nodules, in the walls of the\n   small intestiness; agminated glands; -- called also Peyers patches. In\n   typhoid fever they become the seat of ulcers which are regarded as the\n   characteristic organic lesion of that disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peytrel', 'n.', 'The breastplate of a horses armor or harness. [Spelt also\n   peitrel.] See Poitrel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Peziza', 'n.', 'A genus of fungi embracing a great number of species, some\n   of which are remarkable for their regular cuplike form and deep colors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pezizoid', 'a.', 'Resembling a fungus of the genus Peziza; having a cuplike\n   form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pfennigs', 'pl. ', 'of Pfennig');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pfennige', 'pl. ', 'of Pfennig');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pfennig', 'n.', 'A small copper coin of Germany. It is the hundredth part\n   of a mark, or about a quarter of a cent in United States currency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phacelli', 'pl. ', 'of Phacellus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phacellus', 'n.', 'One of the filaments on the inner surface of the gastric\n   cavity of certain jellyfishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phacochere', 'n.', 'The wart hog.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phacoid', 'a.', 'Resembling a lentil; lenticular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phacolite', 'n.', 'A colorless variety of chabazite; the original was from\n   Leipa, in Bohemia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phacops', 'n.', 'A genus of trilobites found in the Silurian and Devonian\n   formations. Phacops bufo is one of the most common species.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phaeacian', 'a.', 'Of or pertaining to the Phaeacians, a fabulous seafaring\n   people fond of the feast, the lyre, and the dance, mentioned by Homer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phaenogam', 'n.', 'Any plant of the class Phaenogamia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phaenogamia', 'n. pl.', 'The class of flowering plants including all which\n   have true flowers with distinct floral organs; phanerogamia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phaenogamian', 'a.', 'Alt. of Phaenogamic');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phaenogamic', 'a.', 'Same as Phaenogamous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phaenogamous', 'a.', 'Having true flowers with with distinct floral organs;\n   flowering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phaenomenon', 'n.', 'See Phenomenon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phaeospore', 'n.', 'A brownish zoospore, characteristic of an order\n   (Phaeosporeae) of dark green or olive-colored algae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phaethon', 'n.', 'The son of Helios (Phoebus), that is, the son of light,\n   or of the sun. He is fabled to have obtained permission to drive the\n   chariot of the sun, in doing which his want of skill would have set the\n   world on fire, had he not been struck with a thunderbolt by Jupiter,\n   and hurled headlong into the river Po.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phaethon', 'n.', 'A genus of oceanic birds including the tropic birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phaeton', 'n.', 'A four-wheeled carriage (with or without a top), open, or\n   having no side pieces, in front of the seat. It is drawn by one or two\n   horses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phaeton', 'n.', 'See Phaethon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phaeton', 'n.', 'A handsome American butterfly (Euphydryas, / Melitaea,\n   Phaeton). The upper side of the wings is black, with orange-red spots\n   and marginal crescents, and several rows of cream-colored spots; --\n   called also Baltimore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phagedena', 'n.', 'A canine appetite; bulimia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phagedena', 'n.', 'Spreading, obstinate ulceration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phagedenic', 'a.', 'Alt. of PhagedenicAL');
  INSERT INTO words (word, word_type, description) VALUES  (    'PhagedenicAL', 'a.', 'Of, like, or pertaining to, phagedena; used in the\n   treatment of phagedena; as, a phagedenic ulcer or medicine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'PhagedenicAL', 'n.', 'A phagedenic medicine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phagedenous', 'a.', 'Phagedenic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phagocyte', 'n.', 'A leucocyte which plays a part in retrogressive\n   processes by taking up (eating), in the form of fine granules, the\n   parts to be removed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phainopepla', 'n.', 'A small crested passerine bird (Phainopepla nitens),\n   native of Mexico and the Southern United States. The adult male is of a\n   uniform glossy blue-black; the female is brownish. Called also black\n   flycatcher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phakoscope', 'n.', 'An instrument for studying the mechanism of\n   accommodation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phalaena', 'n.', 'A linnaean genus which included the moths in general.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phalaenid', 'n.', 'Any moth of the family Phalaenidae, of which the\n   cankerworms are examples; a geometrid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phalangeal', 'a.', 'Alt. of Phalangal');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phalangal', 'a.', 'Of or pertaining to the phalanges. See Phalanx, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phalanger', 'n.', 'Any marsupial belonging to Phalangista, Cuscus,\n   Petaurus, and other genera of the family Phalangistidae. They are\n   arboreal, and the species of Petaurus are furnished with lateral\n   parachutes. See Flying phalanger, under Flying.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phalanges', 'n.', 'pl. of Phalanx.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phalangial', 'a.', 'Alt. of Phalangian');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phalangian', 'a.', 'Phalangeal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phalangides', 'pl. ', 'of Phalangid');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phalangid', 'n.', 'One of the Phalangoidea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phalangious', 'a.', 'Of or pertaining to Phalangoidea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phalangist', 'n.', 'Any arboreal marsupial of the genus Phalangista. The\n   vulpine phalangist (P. vulpina) is the largest species, the full grown\n   male being about two and a half feet long. It has a large bushy tail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phalangister', 'n.', 'Alt. of Phalangistine');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phalangistine', 'n.', 'Same as Phalangist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phalangite', 'n.', 'A soldier belonging to a phalanx.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phalangoidea', 'n. pl.', 'A division of Arachnoidea, including the daddy\n   longlegs or harvestman (Phalangium) and many similar kinds. They have\n   long, slender, many-jointed legs; usually a rounded, segmented abdomen;\n   and chelate jaws. They breathe by tracheae. Called also Phalangides,\n   Phalangidea, Phalangiida, and Opilionea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phalanstere', 'n.', 'A phalanstery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phalansterian', 'a.', 'Of or pertaining to phalansterianism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phalansterian', 'n.', 'One who favors the system of phalansteries proposed\n   by Fourier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phalansterism', 'n.', 'Alt. of Phalansterianism');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phalansterianism', 'n.', 'A system of phalansteries proposed by Fourier;\n   Fourierism.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-ies', 'pl. ', 'of Phalanstery');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phalanstery', 'n.', 'An association or community organized on the plan of\n   Fourier. See Fourierism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phalanstery', 'n.', 'The dwelling house of a Fourierite community.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phalanxes', 'pl. ', 'of Phalanx');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phalanges', 'pl. ', 'of Phalanx');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phalanx', 'n.', 'A body of heavy-armed infantry formed in ranks and files\n   close and deep. There were several different arrangements, the phalanx\n   varying in depth from four to twenty-five or more ranks of men.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phalanx', 'n.', 'Any body of troops or men formed in close array, or any\n   combination of people distinguished for firmness and solidity of a\n   union.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phalanx', 'n.', 'A Fourierite community; a phalanstery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phalanx', 'n.', 'One of the digital bones of the hand or foot, beyond the\n   metacarpus or metatarsus; an internode.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phalanx', 'n.', 'A group or bundle of stamens, as in polyadelphous flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phalarope', 'n.', 'Any species of Phalaropus and allied genera of small\n   wading birds (Grallae), having lobate toes. They are often seen far\n   from land, swimming in large flocks. Called also sea goose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phallic', 'a.', 'Of or pertaining to the phallus, or to phallism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phallicism', 'n.', 'See Phallism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phallism', 'n.', 'The worship of the generative principle in nature,\n   symbolized by the phallus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phalli', 'pl. ', 'of Phallus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phallus', 'n.', 'The emblem of the generative power in nature, carried in\n   procession in the Bacchic orgies, or worshiped in various ways.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phallus', 'n.', 'The penis or clitoris, or the embryonic or primitive organ\n   from which either may be derived.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phallus', 'n.', 'A genus of fungi which have a fetid and disgusting odor;\n   the stinkhorn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phane', 'n.', 'See Fane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phanerite', 'a.', 'Evident; visible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phanerocarpae', 'n. pl.', 'Same as Acraspeda.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phanerocodonic', 'a.', 'Having an umbrella-shaped or bell-shaped body, with\n   a wide, open cavity beneath; -- said of certain jellyfishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phanerocrystalline', 'a.', 'Distinctly crystalline; -- used of rocks.\n   Opposed to cryptocrystalline.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phanerodactyla', 'n. pl.', 'Same as Saururae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phanerogamia', 'n. pl.', 'That one of the two primary divisions of the\n   vegetable kingdom which contains the phanerogamic, or flowering,\n   plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phanerogamian', 'a.', 'Phanerogamous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phanerogamic', 'a.', 'Alt. of Phanerogamous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phanerogamous', 'a.', 'Having visible flowers containing distinct stamens\n   and pistils; -- said of plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phaneroglossal', 'a.', 'Having a conspicious tongue; -- said of certain\n   reptiles and insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phantascope', 'n.', 'An optical instrument or toy, resembling the\n   phenakistoscope, and illustrating the same principle; -- called also\n   phantasmascope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phantasm', 'n.', 'An image formed by the mind, and supposed to be real or\n   material; a shadowy or airy appearance; sometimes, an optical illusion;\n   a phantom; a dream.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phantasm', 'n.', 'A mental image or representation of a real object; a\n   fancy; a notion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phantasma', 'n.', 'A phantasm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phantasmagoria', 'n.', 'An optical effect produced by a magic lantern. The\n   figures are painted in transparent colors, and all the rest of the\n   glass is opaque black. The screen is between the spectators and the\n   instrument, and the figures are often made to appear as in motion, or\n   to merge into one another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phantasmagoria', 'n.', 'The apparatus by which such an effect is produced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phantasmagoria', 'n.', 'Fig.: A medley of figures; illusive images.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phantasmagorial', 'a.', 'Of, relating to, or resembling phantasmagoria;\n   phantasmagoric.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phantasmagoric', 'a.', 'Of or pertaining to phantasmagoria;\n   phantasmagorial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phantasmagory', 'n.', 'See Phantasmagoria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phantasmal', 'a.', 'Pertaining to, of the nature of, or resembling, a\n   phantasm; spectral; illusive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phantasmascope', 'n.', 'See Phantascope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phantasmatical', 'a.', 'Phantasmal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phantasmatography', 'n.', 'A description of celestial phenomena, as\n   rainbows, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phantastic', 'a.', 'Alt. of Phantastical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phantastical', 'a.', 'See Fantastic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phantasy', 'n.', 'See Fantasy, and Fancy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phantom', 'n.', 'That which has only an apparent existence; an apparition;\n   a specter; a phantasm; a sprite; an airy spirit; an ideal image.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phantomatic', 'a.', 'Phantasmal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pharaoh', 'n.', 'A title by which the sovereigns of ancient Egypt were\n   designated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pharaoh', 'n.', 'See Faro.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pharaon', 'n.', 'See Pharaoh, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pharaonic', 'a.', 'Of or pertaining to the Pharaohs, or kings of ancient\n   Egypt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phare', 'n.', 'A beacon tower; a lighthouse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phare', 'n.', 'Hence, a harbor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pharisaic', 'a.', 'Alt. of Pharisaical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pharisaical', 'a.', 'Of or pertaining to the Pharisees; resembling the\n   Pharisees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pharisaical', 'a.', 'Addicted to external forms and ceremonies; making a\n   show of religion without the spirit of it; ceremonial; formal;\n   hypocritical; self-righteous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pharisaism', 'n.', 'The notions, doctrines, and conduct of the Pharisees,\n   as a sect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pharisaism', 'n.', 'Rigid observance of external forms of religion, without\n   genuine piety; hypocrisy in religion; a censorious, self-righteous\n   spirit in matters of morals or manners.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pharisean', 'a.', 'Following the practice of Pharisees; Pharisaic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pharisee', 'n.', 'One of a sect or party among the Jews, noted for a strict\n   and formal observance of rites and ceremonies and of the traditions of\n   the elders, and whose pretensions to superior sanctity led them to\n   separate themselves from the other Jews.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phariseeism', 'n.', 'See Pharisaism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pharmaceutic', 'a.', 'Alt. of Pharmaceutical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pharmaceutical', 'a.', 'Of or pertaining to the knowledge or art of\n   pharmacy, or to the art of preparing medicines according to the rules\n   or formulas of pharmacy; as, pharmaceutical preparations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pharmaceutics', 'n.', 'The science of preparing medicines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pharmaceutist', 'n.', 'One skilled in pharmacy; a druggist. See the Note\n   under Apothecary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pharmacist', 'n.', 'One skilled in pharmacy; a pharmaceutist; a druggist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pharmacodynamics', 'n.', 'That branch of pharmacology which considers the\n   mode of action, and the effects, of medicines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pharmacognosis', 'n.', 'That branch of pharmacology which treats of\n   unprepared medicines or simples; -- called also pharmacography, and\n   pharmacomathy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pharmacognosy', 'n.', 'Pharmacognosis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pharmacography', 'n.', 'See Pharmacognosis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pharmacolite', 'n.', 'A hydrous arsenate of lime, usually occurring in\n   silky fibers of a white or grayish color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pharmacologist', 'n.', 'One skilled in pharmacology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pharmacology', 'n.', 'Knowledge of drugs or medicines; the art of preparing\n   medicines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pharmacology', 'n.', 'A treatise on the art of preparing medicines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pharmacomathy', 'n.', 'See Pharmacognosis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pharmacon', 'n.', 'A medicine or drug; also, a poison.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pharmacopoeia', 'n.', 'A book or treatise describing the drugs,\n   preparations, etc., used in medicine; especially, one that is issued by\n   official authority and considered as an authoritative standard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pharmacopoeia', 'n.', 'A chemical laboratory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pharmacopolist', 'n.', 'One who sells medicines; an apothecary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pharmacosiderite', 'n.', 'A hydrous arsenate of iron occurring in green or\n   yellowish green cubic crystals; cube ore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pharmacy', 'n.', 'The art or practice of preparing and preserving drugs,\n   and of compounding and dispensing medicines according to prescriptions\n   of physicians; the occupation of an apothecary or a pharmaceutical\n   chemist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pharmacy', 'n.', 'A place where medicines are compounded; a drug store; an\n   apothecarys shop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pharo', 'n.', 'A pharos; a lighthouse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pharo', 'n.', 'See Faro.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pharology', 'n.', 'The art or science which treats of lighthouses and\n   signal lights.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pharos', 'n.', 'A lighthouse or beacon for the guidance of seamen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pharyngal', 'a.', 'Pharyngeal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pharyngeal', 'a.', 'Of or pertaining to the pharynx; in the region of the\n   pharynx.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pharyngeal', 'n.', 'A pharyngeal bone or cartilage; especially, one of the\n   lower pharyngeals, which belong to the rudimentary fifth branchial arch\n   in many fishes, or one of the upper pharyngeals, or pharyngobranchials,\n   which are the dorsal elements in the complete branchial arches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pharyngitis', 'n.', 'Inflammation of the pharynx.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pharyngobranchial', 'a.', 'Of or pertaining to the pharynx and the\n   branchiae; -- applied especially to the dorsal elements in the\n   branchial arches of fishes. See Pharyngeal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pharyngobranchial', 'n.', 'A pharyngobranchial, or upper pharyngeal, bone\n   or cartilage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pharyngobranchii', 'n. pl.', 'Same as Leptocardia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pharyngognathi', 'n. pl.', 'A division of fishes in which the lower\n   pharyngeal bones are united. It includes the scaroid, labroid, and\n   embioticoid fishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pharyngolaryngeal', 'a.', 'Of or pertaining both to pharynx and the larynx.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pharyngopneusta', 'n. pl.', 'A group of invertebrates including the\n   Tunicata and Enteropneusta.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pharyngotome', 'n.', 'An instrument for incising or scarifying the tonsils,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pharyngotomy', 'n.', 'The operation of making an incision into the pharynx,\n   to remove a tumor or anything that obstructs the passage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pharyngotomy', 'n.', 'Scarification or incision of the tonsils.');
  INSERT INTO words (word, word_type, description) VALUES  (    'pharynges', 'pl. ', 'of Pharynx');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pharynx', 'n.', 'The part of the alimentary canal between the cavity of the\n   mouth and the esophagus. It has one or two external openings through\n   the nose in the higher vertebrates, and lateral branchial openings in\n   fishes and some amphibias.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phascolome', 'n.', 'A marsupial of the genus Phascolomys; a wombat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phases', 'pl. ', 'of Phase');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phase', 'n.', 'That which is exhibited to the eye; the appearance which\n   anything manifests, especially any one among different and varying\n   appearances of the same object.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phase', 'n.', 'Any appearance or aspect of an object of mental apprehension\n   or view; as, the problem has many phases.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phase', 'n.', 'A particular appearance or state in a regularly recurring\n   cycle of changes with respect to quantity of illumination or form of\n   enlightened disk; as, the phases of the moon or planets. See Illust.\n   under Moon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phase', 'n.', 'Any one point or portion in a recurring series of changes,\n   as in the changes of motion of one of the particles constituting a wave\n   or vibration; one portion of a series of such changes, in distinction\n   from a contrasted portion, as the portion on one side of a position of\n   equilibrium, in contrast with that on the opposite side.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phasel', 'n.', 'The French bean, or kidney bean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phaseless', 'a.', 'Without a phase, or visible form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phaseolus', 'n.', 'A genus of leguminous plants, including the Lima bean,\n   the kidney bean, the scarlet runner, etc. See Bean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phaseomannite', 'n.', 'Same as Inosite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phases', 'pl. ', 'of Phasis');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phasis', 'n.', 'See Phase.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phasm', 'n.', 'Alt. of Phasma');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phasma', 'n.', 'An apparition; a phantom; an appearance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phasmid', 'n.', 'Any orthopterous insect of the family Phasmidae, as a leaf\n   insect or a stick insect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phassachate', 'n.', 'The lead-colored agate; -- so called in reference to\n   its color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phatagin', 'n.', 'The long-tailed pangolin (Manis tetradactyla); -- called\n   also ipi.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pheasant', 'n.', 'Any one of numerous species of large gallinaceous birds\n   of the genus Phasianus, and many other genera of the family\n   Phasianidae, found chiefly in Asia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pheasant', 'n.', 'The ruffed grouse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pheasantry', 'n.', 'A place for keeping and rearing pheasants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phebe', 'n.', 'See Phoebe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pheer', 'n.', 'See 1st Fere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pheese', 'v. t.', 'To comb; also, to beat; to worry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pheese', 'n.', 'Fretful excitement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phelloderm', 'n.', 'A layer of green parenchimatous cells formed on the\n   inner side of the phellogen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phellogen', 'n.', 'The tissue of young cells which produces cork cells.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phelloplastics', 'n.', 'Art of modeling in cork.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phenacite', 'n.', 'A glassy colorless mineral occurring in rhombohedral\n   crystals, sometimes used as a gem. It is a silicate of glucina, and\n   receives its name from its deceptive similarity to quartz.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phenakistoscope', 'n.', 'A revolving disk on which figures drawn in\n   different relative attitudes are seen successively, so as to produce\n   the appearance of an object in actual motion, as an animal leaping,\n   etc., in consequence of the persistence of the successive visual\n   impressions of the retina. It is often arranged so that the figures may\n   be projected upon a screen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phenanthrene', 'n.', 'A complex hydrocarbon, C14H10, found in coal tar, and\n   obtained as a white crystalline substance with a bluish fluorescence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phenanthridine', 'n.', 'A nitrogenous hydrocarbon base, C13H9N, analogous\n   to phenanthrene and quinoline.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phenanthroline', 'n.', 'Either of two metameric nitrogenous hydrocarbon\n   bases, C12H8N2, analogous to phenanthridine, but more highly\n   nitrogenized.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phene', 'n.', 'Benzene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phenetol', 'n.', 'The ethyl ether of phenol, obtained as an aromatic\n   liquid, C6H5.O.C2H5.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phenic', 'a.', 'Of, pertaining to, derived from, or resembling, phenyl or\n   phenol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phenician', 'a. & n.', 'See Phoenician.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phenicine', 'n.', 'A purple powder precipitated when a sulphuric solution\n   of indigo is diluted with water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phenicine', 'n.', 'A coloring matter produced by the action of a mixture of\n   strong nitric and sulphuric acids on phenylic alcohol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phenicious', 'a.', 'Of a red color with a slight mixture of gray.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phenicopter', 'n.', 'A flamingo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phenixes', 'pl. ', 'of Phenix');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phenix', 'n.', 'A bird fabled to exist single, to be consumed by fire by\n   its own act, and to rise again from its ashes. Hence, an emblem of\n   immortality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phenix', 'n.', 'A southern constellation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phenix', 'n.', 'A marvelous person or thing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phenogamia', 'n. pl.', 'Same as Phaenogamia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phenogamian', 'a.', 'Alt. of Phenogamous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phenogamic', 'a.', 'Alt. of Phenogamous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phenogamous', 'a.', 'Same as Phaenogamian, Phaenogamic, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phenol', 'n.', 'A white or pinkish crystalline substance, C6H5OH, produced\n   by the destructive distillation of many organic bodies, as wood, coal,\n   etc., and obtained from the heavy oil from coal tar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phenol', 'n.', 'Any one of the series of hydroxyl derivatives of which\n   phenol proper is the type.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phenolate', 'n.', 'A compound of phenol analogous to a salt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phenomenal', 'a.', 'Relating to, or of the nature of, a phenomenon; hence,\n   extraordinary; wonderful; as, a phenomenal memory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phenomenalism', 'n.', 'That theory which limits positive or scientific\n   knowledge to phenomena only, whether material or spiritual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phenomenist', 'n.', 'One who believes in the theory of phenomenalism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phenomenology', 'n.', 'A description, history, or explanation of phenomena.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phenomena', 'pl. ', 'of Phenomenon');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phenomenon', 'n.', 'An appearance; anything visible; whatever, in matter or\n   spirit, is apparent to, or is apprehended by, observation; as, the\n   phenomena of heat, light, or electricity; phenomena of imagination or\n   memory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phenomenon', 'n.', 'That which strikes one as strange, unusual, or\n   unaccountable; an extraordinary or very remarkable person, thing, or\n   occurrence; as, a musical phenomenon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phenose', 'n.', 'A sweet amorphous deliquescent substance obtained\n   indirectly from benzene, and isometric with, and resembling, dextrose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phenyl', 'n.', 'A hydrocarbon radical (C6H5) regarded as the essential\n   residue of benzene, and the basis of an immense number of aromatic\n   derivatives.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phenylamine', 'n.', 'Any one of certain class of organic bases regarded as\n   formed from ammonia by the substitution of phenyl for hydrogen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phenylene', 'n.', 'A hypothetic radical (C6H4) occurring in certain\n   derivatives of benzene; as, phenylene diamine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phenylic', 'a.', 'Pertaining to, derived from, or containing, phenyl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pheon', 'n.', 'A bearing representing the head of a dart or javelin, with\n   long barbs which are engrailed on the inner edge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phial', 'n.', 'A glass vessel or bottle, especially a small bottle for\n   medicines; a vial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phialed', 'imp. & p. p.', 'of Phial');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phialing', 'p. pr. & vb. n.', 'of Phial');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phial', 'v. t.', 'To put or keep in, or as in, a phial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philabeg', 'n.', 'See Filibeg.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philadelphian', 'a.', 'Of or pertaining to Ptolemy Philadelphus, or to one\n   of the cities named Philadelphia, esp. the modern city in Pennsylvania.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philadelphian', 'n.', 'A native or an inhabitant of Philadelphia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philadelphian', 'n.', 'One of a society of mystics of the seventeenth\n   century, -- called also the Family of Love.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philalethist', 'n.', 'A lover of the truth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philander', 'v. i.', 'To make love to women; to play the male flirt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philander', 'n.', 'A lover.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philander', 'n.', 'A South American opossum (Didelphys philander).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philander', 'n.', 'An Australian bandicoot (Perameles lagotis).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philanderer', 'n.', 'One who hangs about women; a male flirt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philanthrope', 'n.', 'A philanthropist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philanthropic', 'a.', 'Alt. of Philanthropical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philanthropical', 'a.', 'Of or pertaining to philanthropy; characterized by\n   philanthropy; loving or helping mankind; as, a philanthropic\n   enterprise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philanthropinism', 'n.', 'A system of education on so-called natural\n   principles, attempted in Germany in the last century by Basedow, of\n   Dessau.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philanthropinist', 'n.', 'An advocate of, or believer in, philanthropinism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philanthropist', 'n.', 'One who practices philanthropy; one who loves\n   mankind, and seeks to promote the good of others.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philanthropistic', 'a.', 'Pertaining to, or characteristic of, a\n   philanthropist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philanthropy', 'n.', 'Love to mankind; benevolence toward the whole human\n   family; universal good will; desire and readiness to do good to all\n   men; -- opposed to misanthropy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philatelic', 'a.', 'Of or pertaining to philately.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philatelist', 'n.', 'One versed in philately; one who collects postage\n   stamps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philately', 'n.', 'The collection of postage stamps of various issues.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philatory', 'n.', 'A kind of transparent reliquary with an ornamental top.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philauty', 'n.', 'Self-love; selfishness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philharmonic', 'a.', 'Loving harmony or music.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philhellene', 'n.', 'A friend of Greece, or of the Greeks; a philhellenist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philhellenic', 'a.', 'Of or pertaining to philhellenism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philhellenism', 'n.', 'Love of Greece.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philhellenist', 'n.', 'A friend of Greece; one who supports the cause of\n   the Greeks; particularly, one who supported them in their struggle for\n   independence against the Turks; a philhellene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philibeg', 'n.', 'See Filibeg.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philip', 'n.', 'The European hedge sparrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philip', 'n.', 'The house sparrow. Called also phip.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philippian', 'a.', 'Of or pertaining to Philippi, a city of ancient\n   Macedonia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philippian', 'n.', 'A native or an inhabitant of Philippi.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philippic', 'n.', 'Any one of the series of famous orations of Demosthenes,\n   the Grecian orator, denouncing Philip, king of Macedon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philippic', 'n.', 'Hence: Any discourse or declamation abounding in\n   acrimonious invective.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philippium', 'n.', 'A rare and doubtful metallic element said to have been\n   discovered in the mineral samarskite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philippized', 'imp. & p. p.', 'of Philippize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philippizing', 'p. pr. & vb. n.', 'of Philippize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philippize', 'v. i.', 'To support or advocate the cause of Philip of\n   Macedon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philippize', 'v. i.', 'To write or speak in the style of a philippic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philister', 'n.', 'A Philistine; -- a cant name given to townsmen by\n   students in German universities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philistine', 'n.', 'A native or an inhabitant of ancient Philistia, a coast\n   region of southern Palestine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philistine', 'n.', 'A bailiff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philistine', 'n.', 'A person deficient in liberal culture and refinement;\n   one without appreciation of the nobler aspirations and sentiments of\n   humanity; one whose scope is limited to selfish and material interests.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philistine', 'a.', 'Of or pertaining to the Philistines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philistine', 'a.', 'Uncultured; commonplace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philistinism', 'n.', 'The condition, character, aims, and habits of the\n   class called Philistines. See Philistine, 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phillipsite', 'n.', 'A hydrous silicate of aluminia, lime, and soda, a\n   zeolitic mineral commonly occurring in complex twin crystals, often\n   cruciform in shape; -- called also christianite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phillygenin', 'n.', 'A pearly crystalline substance obtained by the\n   decomposition of phillyrin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phillyrea', 'n.', 'A genus of evergreen plants growing along the shores of\n   the Mediterranean, and breading a fruit resembling that of the olive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phillyrin', 'n.', 'A glucoside extracted from Phillyrea as a bitter white\n   crystalline substance. It is sometimes used as a febrifuge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philo-', '', 'A combining form from Gr. fi`los loving, fond of, attached\n   to; as, philosophy, philotechnic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philogynist', 'n.', 'A lover or friend of women; one who esteems woman as\n   the higher type of humanity; -- opposed to misogynist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philogyny', 'n.', 'Fondness for women; uxoriousness; -- opposed to\n   misogyny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philohellenian', 'n.', 'A philhellenist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philologer', 'n.', 'A philologist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philologian', 'n.', 'A philologist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philological', 'a.', 'Alt. of Philologic');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philologic', 'a.', 'Of or pertaining to philology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philologist', 'n.', 'One versed in philology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philologize', 'v. i.', 'To study, or make critical comments on, language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philologue', 'n.', 'A philologist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philology', 'n.', 'Criticism; grammatical learning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philology', 'n.', 'The study of language, especially in a philosophical\n   manner and as a science; the investigation of the laws of human speech,\n   the relation of different tongues to one another, and historical\n   development of languages; linguistic science.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philology', 'n.', 'A treatise on the science of language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philomath', 'n.', 'A lover of learning; a scholar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philomathematic', 'n.', 'A philomath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philomathic', 'a.', 'Of or pertaining to philomathy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philomathic', 'a.', 'Having love of learning or letters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philomathy', 'n.', 'The love of learning or letters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philomel', 'n.', 'Same as Philomela, the nightingale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philomela', 'n.', 'The nightingale; philomel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philomela', 'n.', 'A genus of birds including the nightingales.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philomene', 'n.', 'The nightingale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philomot', 'a.', 'Of the color of a dead leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philomusical', 'a.', 'Loving music. [R.]Busby.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philopena', 'n.', 'A present or gift which is made as a forfeit in a social\n   game that is played in various ways; also, the game itself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philopolemic', 'a.', 'Alt. of Philopolemical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philopolemical', 'a.', 'Fond of polemics or controversy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philoprogenitive', 'a.', 'Having the love of offspring; fond of children.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philoprogenitiveness', 'n.', 'The love of offspring; fondness for children.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philosophaster', 'n.', 'A pretender to philosophy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philosophate', 'v. i.', 'To play the philosopher; to moralize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philosophation', 'n.', 'Philosophical speculation and discussion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philosophe', 'n.', 'A philosophaster; a philosopher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philosopheme', 'n.', 'A philosophical proposition, doctrine, or principle\n   of reasoning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philosopher', 'n.', 'One who philosophizes; one versed in, or devoted to,\n   philosophy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philosopher', 'n.', 'One who reduces the principles of philosophy to\n   practice in the conduct of life; one who lives according to the rules\n   of practical wisdom; one who meets or regards all vicissitudes with\n   calmness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philosopher', 'n.', 'An alchemist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philosophic', 'a.', 'Alt. of Philosophical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philosophical', 'a.', 'Of or pertaining to philosophy; versed in, or imbued\n   with, the principles of philosophy; hence, characterizing a\n   philosopher; rational; wise; temperate; calm; cool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philosophism', 'n.', 'Spurious philosophy; the love or practice of\n   sophistry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philosophist', 'n.', 'A pretender in philosophy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philosophistic', 'a.', 'Alt. of Philosophistical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philosophistical', 'a.', 'Of or pertaining to the love or practice of\n   sophistry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philosophized', 'imp. & p. p.', 'of Philosophize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philosophizing', 'p. pr. & vb. n.', 'of Philosophize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philosophize', 'v. i.', 'To reason like a philosopher; to search into the\n   reason and nature of things; to investigate phenomena, and assign\n   rational causes for their existence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philosophizer', 'n.', 'One who philosophizes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philosophies', 'pl. ', 'of Philosophy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philosophy', 'n.', 'Literally, the love of, including the search after,\n   wisdom; in actual usage, the knowledge of phenomena as explained by,\n   and resolved into, causes and reasons, powers and laws.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philosophy', 'n.', 'A particular philosophical system or theory; the\n   hypothesis by which particular phenomena are explained.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philosophy', 'n.', 'Practical wisdom; calmness of temper and judgment;\n   equanimity; fortitude; stoicism; as, to meet misfortune with\n   philosophy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philosophy', 'n.', 'Reasoning; argumentation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philosophy', 'n.', 'The course of sciences read in the schools.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philosophy', 'n.', 'A treatise on philosophy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philostorgy', 'n.', 'Natural affection, as of parents for their children.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philotechnic', 'a.', 'Alt. of Philotechnical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philotechnical', 'a.', 'Fond of the arts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philter', 'n.', 'A potion or charm intended to excite the passion of love.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philtered', 'imp. & p. p.', 'of Philter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philtering', 'p. pr. & vb. n.', 'of Philter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philter', 'v. t.', 'To impregnate or mix with a love potion; as, to philter\n   a draught.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Philter', 'v. t.', 'To charm to love; to excite to love or sexual desire by\n   a potion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phimosis', 'n.', 'A condition of the penis in which the prepuce can not be\n   drawn back so as to uncover the glans penis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phitoness', 'n.', 'Pythoness; witch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phizes', 'pl. ', 'of Phiz');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phiz', 'n.', 'The face or visage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phlebitis', 'n.', 'Inflammation of a vein.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phlebogram', 'n.', 'A tracing (with the sphygmograph) of the movements of a\n   vein, or of the venous pulse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phlebolite', 'n.', 'Alt. of Phlebolith');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phlebolith', 'n.', 'A small calcareous concretion formed in a vein; a vein\n   stone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phlebology', 'n.', 'A branch of anatomy which treats of the veins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phlebotomist', 'n.', 'One who practiced phlebotomy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phlebotomized', 'imp. & p. p.', 'of Phlebotomize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phlebotomizing', 'p. pr. & vb. n.', 'of Phlebotomize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phlebotomize', 'v. t.', 'To let blood from by opening a vein; to bleed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phlebotomy', 'n.', 'The act or practice of opening a vein for letting\n   blood, in the treatment of disease; venesection; bloodletting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phlegm', 'a.', 'One of the four humors of which the ancients supposed the\n   blood to be composed. See Humor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phlegm', 'a.', 'Viscid mucus secreted in abnormal quantity in the\n   respiratory and digestive passages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phlegm', 'a.', 'A watery distilled liquor, in distinction from a spirituous\n   liquor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phlegm', 'a.', 'Sluggishness of temperament; dullness; want of interest;\n   indifference; coldness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phlegmagogue', 'n.', 'A medicine supposed to expel phlegm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phlegmasia', 'n.', 'An inflammation; more particularly, an inflammation of\n   the internal organs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phlegmatic', 'a.', 'Watery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phlegmatic', 'a.', 'Abounding in phlegm; as, phlegmatic humors; a\n   phlegmatic constitution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phlegmatic', 'a.', 'Generating or causing phlegm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phlegmatic', 'a.', 'Not easily excited to action or passion; cold; dull;\n   sluggish; heavy; as, a phlegmatic person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phlegmatical', 'a.', 'Phlegmatic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phlegmatically', 'adv.', 'In a phlegmatic manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phlegmaticly', 'a.', 'Phlegmatically.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phlegmon', 'n.', 'Purulent inflammation of the cellular or areolar tissue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phlegmonous', 'a.', 'Having the nature or properties of phlegmon; as,\n   phlegmonous pneumonia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phleme', 'n.', 'See Fleam.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phleum', 'n.', 'A genus of grasses, including the timothy (Phleum\n   pratense), which is highly valued for hay; cats-tail grass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phloem', 'n.', 'That portion of fibrovascular bundles which corresponds to\n   the inner bark; the liber tissue; -- distinguished from xylem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phlogistian', 'n.', 'A believer in the existence of phlogiston.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phlogistic', 'a.', 'Of or pertaining to phlogiston, or to belief in its\n   existence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phlogistic', 'a.', 'Inflammatory; belonging to inflammations and fevers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phlogistical', 'a.', 'Phlogistic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phlogisticated', 'imp. & p. p.', 'of Phlogisticate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phlogisticating', 'p. pr. & vb. n.', 'of Phlogisticate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phlogisticate', 'v. t.', 'To combine phlogiston with; -- usually in the\n   form and sense of the p. p. or the adj.; as, highly phlogisticated\n   substances.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phlogistication', 'n.', 'The act or process of combining with phlogiston.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phlogiston', 'n.', 'The hypothetical principle of fire, or inflammability,\n   regarded by Stahl as a chemical element.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phlogogenous', 'a.', 'Causing inflammation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phlogopite', 'n.', 'A kind of mica having generally a peculiar bronze-red\n   or copperlike color and a pearly luster. It is a silicate of aluminia,\n   with magnesia, potash, and some fluorine. It is characteristic of\n   crystalline limestone or dolomite and serpentine. See Mica.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phlogosis', 'n.', 'Inflammation of external parts of the body;\n   erysipelatous inflammation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phlogotic', 'n.', 'Of or pertaining to phlogisis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phloramine', 'n.', 'A basic amido derivative of phloroglucin, having an\n   astringent taste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phloretic', 'a.', 'Pertaining to, or derived from, or designating, an\n   organic acid obtained by the decomposition of phloretin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phloretin', 'n.', 'A bitter white crystalline substance obtained by the\n   decomposition of phlorizin, and formerly used to some extent as a\n   substitute for quinine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phlorizin', 'n.', 'A bitter white crystalline glucoside extracted from the\n   root bark of the apple, pear, cherry, plum, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phloroglucin', 'n.', 'A sweet white crystalline substance, metameric with\n   pyrogallol, and obtained by the decomposition of phloretin, and from\n   certain gums, as catechu, kino, etc. It belongs to the class of\n   phenols. [Called also phloroglucinol.]');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phlorol', 'n.', 'A liquid metameric with xylenol, belonging to the class of\n   phenols, and obtained by distilling certain salts of phloretic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phlorone', 'n.', 'A yellow crystalline substance having a peculiar\n   unpleasant odor, resembling the quinones, and obtained from beechwood\n   tar and coal tar, as also by the oxidation of xylidine; -- called also\n   xyloquinone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phlox', 'n.', 'A genus of American herbs, having showy red, white, or\n   purple flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phlyctenular', 'a.', 'Characterized by the presence of small pustules, or\n   whitish elevations resembling pustules; as, phlyctenular ophthalmia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phoca', 'n.', 'A genus of seals. It includes the common harbor seal and\n   allied species. See Seal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phocacean', 'n.', 'Any species of Phoca; a seal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phocal', 'a.', 'Pertaining to seals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phocenic', 'a.', 'Of or pertaining to dolphin oil or porpoise oil; -- said\n   of an acid (called also delphinic acid) subsequently found to be\n   identical with valeric acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phocenin', 'n.', 'See Delphin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phocine', 'a.', 'Of or pertaining to the seal tribe; phocal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phocodont', 'n.', 'One of the Phocodontia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phocodontia', 'n. pl.', 'A group of extinct carnivorous whales. Their teeth\n   had compressed and serrated crowns. It includes Squalodon and allied\n   genera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phoebe', 'n.', 'The pewee, or pewit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phoebus', 'n.', 'Apollo; the sun god.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phoebus', 'n.', 'The sun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phoenician', 'a.', 'Of or pertaining to Phoenica.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phoenician', 'n.', 'A native or inhabitant of Phoenica.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phoenicious', 'a.', 'See Phenicious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phoenicopterus', 'n.', 'A genus of birds which includes the flamingoes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phoenix', 'n.', 'Same as Phenix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phoenix', 'n.', 'A genus of palms including the date tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pholad', 'n.', 'Any species of Pholas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pholadean', 'n.', 'Pholad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pholades', 'pl. ', 'of Pholas');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pholas', 'n.', 'Any one of numerous species of marine bivalve mollusks of\n   the genus Pholas, or family Pholadidae. They bore holes for themselves\n   in clay, peat, and soft rocks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phonal', 'a.', 'Of or relating to the voice; as, phonal structure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phonascetics', 'n.', 'Treatment for restoring or improving the voice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phonation', 'n.', 'The act or process by which articulate sounds are\n   uttered; the utterance of articulate sounds; articulate speech.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phonautograph', 'n.', 'An instrument by means of which a sound can be made\n   to produce a visible trace or record of itself. It consists essentially\n   of a resonant vessel, usually of paraboloidal form, closed at one end\n   by a flexible membrane. A stylus attached to some point of the membrane\n   records the movements of the latter, as it vibrates, upon a moving\n   cylinder or plate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phoneidoscope', 'n.', 'An instrument for studying the motions of sounding\n   bodies by optical means. It consists of a tube across the end of which\n   is stretched a film of soap solution thin enough to give colored bands,\n   the form and position of which are affected by sonorous vibrations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phonetic', 'a.', 'Of or pertaining to the voice, or its use.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phonetic', 'a.', 'Representing sounds; as, phonetic characters; -- opposed\n   to ideographic; as, a phonetic notation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phonetically', 'adv.', 'In a phonetic manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phonetician', 'n.', 'One versed in phonetics; a phonetist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phonetics', 'n.', 'The doctrine or science of sounds; especially those of\n   the human voice; phonology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phonetics', 'n.', 'The art of representing vocal sounds by signs and\n   written characters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phonetism', 'n.', 'The science which treats of vocal sounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phonetist', 'n.', 'One versed in phonetics; a phonologist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phonetist', 'n.', 'One who advocates a phonetic spelling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phonetization', 'n.', 'The act, art, or process of representing sounds by\n   phonetic signs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phonetize', 'v. t.', 'To represent by phonetic signs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phonic', 'a.', 'Of or pertaining to sound; of the nature of sound;\n   acoustic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phonics', 'n.', 'See Phonetics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phono-', '', 'A combining form from Gr. / sound, tone; as, phonograph,\n   phonology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phono', 'n.', 'A South American butterfly (Ithonia phono) having nearly\n   transparent wings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phonocamptic', 'a.', 'Reflecting sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phonogram', 'n.', 'A letter, character, or mark used to represent a\n   particular sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phonogram', 'n.', 'A record of sounds made by a phonograph.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phonograph', 'n.', 'A character or symbol used to represent a sound, esp.\n   one used in phonography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phonograph', 'n.', 'An instrument for the mechanical registration and\n   reproduction of audible sounds, as articulate speech, etc. It consists\n   of a rotating cylinder or disk covered with some material easily\n   indented, as tinfoil, wax, paraffin, etc., above which is a thin plate\n   carrying a stylus. As the plate vibrates under the influence of a\n   sound, the stylus makes minute indentations or undulations in the soft\n   material, and these, when the cylinder or disk is again turned, set the\n   plate in vibration, and reproduce the sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phonographer', 'n.', 'One versed or skilled in phonography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phonographer', 'n.', 'One who uses, or is skilled in the use of, the\n   phonograph. See Phonograph, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phonographic', 'a.', 'Alt. of Phonographical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phonographical', 'a.', 'Of or pertaining to phonography; based upon\n   phonography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phonographical', 'a.', 'Of or pertaining to phonograph; done by the\n   phonograph.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phonographically', 'adv.', 'In a phonographic manner; by means of\n   phonograph.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phonographist', 'n.', 'Phonographer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phonography', 'n.', 'A description of the laws of the human voice, or\n   sounds uttered by the organs of speech.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phonography', 'n.', 'A representation of sounds by distinctive characters;\n   commonly, a system of shorthand writing invented by Isaac Pitman, or a\n   modification of his system, much used by reporters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phonography', 'n.', 'The art of constructing, or using, the phonograph.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phonolite', 'n.', 'A compact, feldspathic, igneous rock containing\n   nephelite, hauynite, etc. Thin slabs give a ringing sound when struck;\n   -- called also clinkstone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phonologer', 'n.', 'A phonologist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phonologic', 'a.', 'Alt. of Phonological');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phonological', 'a.', 'Of or pertaining to phonology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phonologist', 'n.', 'One versed in phonology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phonology', 'n.', 'The science or doctrine of the elementary sounds uttered\n   by the human voice in speech, including the various distinctions,\n   modifications, and combinations of tones; phonetics. Also, a treatise\n   on sounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phonometer', 'n.', 'An instrument for measuring sounds, as to their\n   intensity, or the frequency of the vibrations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phonomotor', 'n.', 'An instrument in which motion is produced by the\n   vibrations of a sounding body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phonorganon', 'n.', 'A speaking machine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phonoscope', 'n.', 'An instrument for observing or exhibiting the motions\n   or properties of sounding bodies; especially, an apparatus invented by\n   Konig for testing the quality of musical strings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phonoscope', 'n.', 'An instrument for producing luminous figures by the\n   vibrations of sounding bodies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phonotypr', 'n.', 'A type or character used in phonotypy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phonotypic', 'a.', 'Alt. of Phonotypical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phonotypical', 'a.', 'Of or pertaining to phonotypy; as, a phonotypic\n   alphabet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phonotypist', 'n.', 'One versed in phonotypy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phonotypy', 'n.', 'A method of phonetic printing of the English language,\n   as devised by Mr. Pitman, in which nearly all the ordinary letters and\n   many new forms are employed in order to indicate each elementary sound\n   by a separate character.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phorminx', 'n.', 'A kind of lyre used by the Greeks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phormium', 'n.', 'A genus of liliaceous plants, consisting of one species\n   (Phormium tenax). See Flax-plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phorone', 'n.', 'A yellow crystalline substance, having a geraniumlike\n   odor, regarded as a complex derivative of acetone, and obtained from\n   certain camphor compounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phoronis', 'n.', 'A remarkable genus of marine worms having tentacles\n   around the mouth. It is usually classed with the gephyreans. Its larva\n   (Actinotrocha) undergoes a peculiar metamorphosis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phoronomia', 'n.', 'See Phoronomics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phoronomics', 'n.', 'The science of motion; kinematics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phosgene', 'a.', 'Producing, or produced by, the action of light; --\n   formerly used specifically to designate a gas now called carbonyl\n   chloride. See Carbonyl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phosgenite', 'n.', 'A rare mineral occurring in tetragonal crystals of a\n   white, yellow, or grayish color and adamantine luster. It is a\n   chlorocarbonate of lead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phospham', 'n.', 'An inert amorphous white powder, PN2H, obtained by\n   passing ammonia over heated phosphorus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phosphate', 'n.', 'A salt of phosphoric acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phosphatic', 'a.', 'Pertaining to, or containing, phosphorus, phosphoric\n   acid, or phosphates; as, phosphatic nodules.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phosphaturia', 'n.', 'The excessive discharge of phosphates in the urine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phosphene', 'n.', 'A luminous impression produced through excitation of the\n   retina by some cause other than the impingement upon it of rays of\n   light, as by pressure upon the eyeball when the lids are closed. Cf.\n   After-image.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phosphide', 'n.', 'A binary compound of phosphorus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phosphine', 'n.', 'A colorless gas, PH3, analogous to ammonia, and having a\n   disagreeable odor resembling that of garlic. Called also hydrogen\n   phosphide, and formerly, phosphureted hydrogen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phosphinic', 'a.', 'Pertaining to, or designating, certain acids analogous\n   to the phosphonic acids, but containing two hydrocarbon radicals, and\n   derived from the secondary phosphines by oxidation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phosphite', 'n.', 'A salt of phosphorous acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phosphonic', 'a.', 'Pertaining to, or designating, certain derivatives of\n   phosphorous acid containing a hydrocarbon radical, and analogous to the\n   sulphonic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phosphonium', 'n.', 'The hypothetical radical PH4, analogous to ammonium,\n   and regarded as the nucleus of certain derivatives of phosphine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phosphor', 'n.', 'Phosphorus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phosphor', 'n.', 'The planet Venus, when appearing as the morning star;\n   Lucifer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phosphorated', 'imp. & p. p.', 'of Phosphorate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phosphorating', 'p. pr. & vb. n.', 'of Phosphorate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phosphorate', 'v. t.', 'To impregnate, or combine, with phosphorus or its\n   compounds; as, phosphorated oil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phosphor-bronze', 'n.', 'A variety of bronze possessing great hardness,\n   elasticity, and toughness, obtained by melting copper with tin\n   phosphide. It contains one or two per cent of phosphorus and from five\n   to fifteen per cent of tin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phosphoreous', 'a.', 'Phosphorescent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phosphoresced', 'imp. & p. p.', 'of Phosphoresce');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phosphorescing', 'p. pr. & vb. n.', 'of Phosphoresce');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phosphoresce', 'v. i.', 'To shine as phosphorus; to be phosphorescent; to\n   emit a phosphoric light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phosphorescence', 'n.', 'The quality or state of being phosphorescent; or\n   the act of phosphorescing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phosphorescence', 'n.', 'A phosphoric light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phosphorescent', 'a.', 'Shining with a phosphoric light; luminous without\n   sensible heat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phosphorescent', 'n.', 'A phosphorescent substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phosphoric', 'a.', 'Of or pertaining to phosphorus; resembling, or\n   containing, from us; specifically, designating those compounds in which\n   phosphorus has a higher valence as contrasted with the phosphorous\n   compounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phosphoric', 'a.', 'Phosphorescent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phosphorical', 'a.', 'Phosphoric.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phosphorite', 'n.', 'A massive variety of apatite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phosphoritic', 'a.', 'Pertaining to phosphorite; resembling, or of the\n   nature of, phosphorite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phosphorize', 'v. t.', 'To phosphorate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phosphorized', 'a.', 'Containing, or impregnated with, phosphorus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phosphorogenic', 'a.', 'Generating phosphorescence; as, phosphorogenic\n   rays.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phosphoroscope', 'n.', 'An apparatus for observing the phosphorescence\n   produced in different bodies by the action of light, and for measuring\n   its duration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phosphorous', 'a.', 'Of or pertaining to phosphorus; resembling or\n   containing phosphorus; specifically, designating those compounds in\n   which phosphorus has a lower valence as contrasted with phosphoric\n   compounds; as, phosphorous acid, H3PO3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phosphori', 'pl. ', 'of Phosphorus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phosphorus', 'n.', 'The morning star; Phosphor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phosphorus', 'n.', 'A poisonous nonmetallic element of the nitrogen group,\n   obtained as a white, or yellowish, translucent waxy substance, having a\n   characteristic disagreeable smell. It is very active chemically, must\n   be preserved under water, and unites with oxygen even at ordinary\n   temperatures, giving a faint glow, -- whence its name. It always occurs\n   compined, usually in phosphates, as in the mineral apatite, in bones,\n   etc. It is used in the composition on the tips of friction matches, and\n   for many other purposes. The molecule contains four atoms. Symbol P.\n   Atomic weight 31.0.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phosphorus', 'n.', 'Hence, any substance which shines in the dark like\n   phosphorus, as certain phosphorescent bodies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phosphoryl', 'n.', 'The radical PO, regarded as the typical nucleus of\n   certain compounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phosphuret', 'n.', 'A phosphide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phosphureted', 'a.', 'Impregnated, or combined, with phosphorus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Photic', 'a.', 'Relating to the production of light by the lower animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Photics', 'n.', 'The science of light; -- a general term sometimes employed\n   when optics is restricted to light as a producing vision.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Photos', 'pl. ', 'of Photo');
  INSERT INTO words (word, word_type, description) VALUES  (    'Photo', 'n.', 'A contraction of Photograph.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Photo-', '', 'A combining form from Gr. fw^s, fwto`s, light; as,\n   photography, phototype, photometer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Photobiotic', 'a.', 'Requiring light to live; incapable of living without\n   light; as, photobiotic plant cells.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Photochemical', 'a.', 'Of or pertaining to chemical action of light, or\n   produced by it; as, the photochemical changes of the visual purple of\n   the retina.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Photochemistry', 'n.', 'The branch of chemistry which relates to the effect\n   of light in producing chemical changes, as in photography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Photochromic', 'a.', 'Alt. of Photochromatic');
  INSERT INTO words (word, word_type, description) VALUES  (    'Photochromatic', 'a.', 'Of or pertaining to photochromy; produced by\n   photochromy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Photochromy', 'n.', 'The art or process of reproducing colors by\n   photography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Photodrome', 'n.', 'An apparatus consisting of a large wheel with spokes,\n   which when turning very rapidly is illuminated by momentary flashes of\n   light passing through slits in a rotating disk. By properly timing the\n   succession of flashes the wheel is made to appear to be motionless, or\n   to rotate more or less slowly in either direction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Photo-electric', 'a.', 'Acting by the operation of both light and\n   electricity; -- said of apparatus for producing pictures by electric\n   light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Photo-electrotype', 'n.', 'An electrotype plate formed in a mold made by\n   photographing on prepared gelatine, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Photo-engraving', 'n.', 'The process of obtaining an etched or engraved\n   plate from the photographic image, to be used in printing; also, a\n   picture produced by such a process.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Photo-epinasty', 'n.', 'A disproportionately rapid growth of the upper\n   surface of dorsiventral organs, such as leaves, through the stimulus of\n   exposure to light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Photogalvanography', 'n.', 'The art or process of making\n   photo-electrotypes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Photogen', 'n.', 'A light hydrocarbon oil resembling kerosene. It is\n   obtained by distilling coal, paraffin, etc., and is used as a\n   lubricant, illuminant, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Photogene', 'n.', 'A photograph.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Photogene', 'n.', 'A more or less continued impression or image on the\n   retina.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Photogenic', 'a.', 'Of or pertaining to photogeny; producing or generating\n   light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Photogeny', 'n.', 'See Photography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Photoglyphic', 'a.', 'Pertaining to the art of engraving by the action of\n   light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Photoglyphy', 'n.', 'Photoglyphic engraving. See under Photoglyphic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Photoglyptic', 'a.', 'Same as Photoglyphic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Photogram', 'n.', 'A photograph.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Photograph', 'n.', 'A picture or likeness obtained by photography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Photographed', 'imp. & p. p.', 'of Photograph');
  INSERT INTO words (word, word_type, description) VALUES  (    'Photographing', 'p. pr. & vb. n.', 'of Photograph');
  INSERT INTO words (word, word_type, description) VALUES  (    'Photograph', 'v. t.', 'To take a picture or likeness of by means of\n   photography; as, to photograph a view; to photograph a group.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Photograph', 'v. i.', 'To practice photography; to take photographs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Photographer', 'n.', 'One who practices, or is skilled in, photography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Photographic', 'a.', 'Alt. of Photographical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Photographical', 'a.', 'Of or pertaining to photography; obtained by\n   photography; used ib photography; as a photographic picture; a\n   photographic camera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Photographist', 'n.', 'A photographer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Photographometer', 'n.', 'An instrument for determining the sensibility of\n   the plates employed in photographic processes to luminous rays.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Photography', 'n.', 'The science which relates to the action of light on\n   sensitive bodies in the production of pictures, the fixation of images,\n   and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Photography', 'n.', 'The art or process of producing pictures by this\n   action of light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Photogravure', 'n.', 'A photoengraving; also, the process by which such a\n   picture is produced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Photoheliograph', 'n.', 'A modified kind of telescope adapted to taking\n   photographs of the sun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Photolithograph', 'n.', 'A lithographic picture or copy from a stone\n   prepared by the aid of photography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Photolithograph', 'v. t.', 'To produce (a picture, a copy) by the process\n   of photolithography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Photolithographer', 'n.', 'One who practices, or one who employs,\n   photolithography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Photolithographic', 'n.', 'Of or pertaining to photolithography; produced\n   by photolithography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Photolithography', 'n.', 'The art or process of producing photolithographs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Photologic', 'a.', 'Alt. of Photological');
  INSERT INTO words (word, word_type, description) VALUES  (    'Photological', 'a.', 'Pertaining to photology, or the doctrine of light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Photologist', 'n.', 'One who studies or expounds the laws of light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Photology', 'n.', 'The doctrine or science of light, explaining its nature\n   and phenomena; optics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Photomagnetic', 'a.', 'Of or pertaining to photomagnetism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Photomagnetism', 'n.', 'The branch of science which treats of the relation\n   of magnetism to light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Photomechanical', 'a.', 'Pertaining to, or designating, any photographic\n   process in which a printing surface is obtained without the\n   intervention of hand engraving.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Photometer', 'n.', 'An instrument for measuring the intensity of light, or,\n   more especially, for comparing the relative intensities of different\n   lights, or their relative illuminating power.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Photometric', 'a.', 'Alt. of Photometrical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Photometrical', 'a.', 'Of or pertaining to photometry, or to a photometer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Photometrician', 'n.', 'One engaged in the scientific measurement of light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Photometry', 'n.', 'That branch of science which treats of the measurement\n   of the intensity of light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Photomicrograph', 'n.', 'An enlarged or macroscopic photograph of a\n   microscopic object. See Microphotograph.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Photomicrograph', 'n.', 'A microscopically small photograph of an object.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Photomicrography', 'n.', 'The art of producing photomicrographs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Photophobia', 'n.', 'A dread or intolerance of light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Photophone', 'n.', 'An apparatus for the production of sound by the action\n   of rays of light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Photophonic', 'a.', 'Of or pertaining to photophone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Photophony', 'n.', 'The art or practice of using the photophone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Photopsia', 'n.', 'An affection of the eye, in which the patient perceives\n   luminous rays, flashes, coruscations, etc. See phosphene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Photopsy', 'n.', 'Same as Photopsia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Photorelief', 'n.', 'A printing surface in relief, obtained by photographic\n   means and subsequent manipulations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Photoscope', 'n.', 'Anything employed for the observation of light or\n   luminous effects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Photoscopic', 'a.', 'Of or pertaining to the photoscope or its uses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Photosculpture', 'n.', 'A process in which, by means of a number of\n   photographs simultaneously taken from different points of view on the\n   same level, rough models of the figure or bust of a person or animal\n   may be made with great expedition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Photosphere', 'n.', 'A sphere of light; esp., the luminous envelope of the\n   sun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Photospheric', 'a.', 'Of or pertaining to the photosphere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phototonus', 'n.', 'A motile condition in plants resulting from exposure to\n   light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phototropic', 'a.', 'Same as Heliotropic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phototype', 'n.', 'A plate or block with a printing surface (usually in\n   relief) obtained from a photograph; also, any one of the many methods\n   of processes by which such a printing surface is obtained.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phototypic', 'a.', 'Of or pertaining to a phototype or phototypy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phototypography', 'n.', 'Same as Phototypy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phototypy', 'n.', 'The art or process of producing phototypes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Photoxylography', 'n.', 'The process of producing a representation of an\n   object on wood, by photography, for the use of the wood engraver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Photozincograph', 'n.', 'A print made by photozincography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Photozincography', 'n.', 'A process, analogous to photolithography, for\n   reproducing photographed impressions transferred to zinc plate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phragmocone', 'n.', 'The thin chambered shell attached to the anterior end\n   of a belemnite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phragmosiphon', 'n.', 'The siphon of a phragmocone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phrasal', 'a.', 'Of the nature of a phrase; consisting of a phrase; as, a\n   phrasal adverb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phrase', 'n.', 'A brief expression, sometimes a single word, but usually\n   two or more words forming an expression by themselves, or being a\n   portion of a sentence; as, an adverbial phrase.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phrase', 'n.', 'A short, pithy expression; especially, one which is often\n   employed; a peculiar or idiomatic turn of speech; as, to err is human.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phrase', 'n.', 'A mode or form of speech; the manner or style in which any\n   one expreses himself; diction; expression.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phrase', 'n.', 'A short clause or portion of a period.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phrased', 'imp. & p. p.', 'of Phrase');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phrasing', 'p. pr. & vb. n.', 'of Phrase');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phrase', 'v. t.', 'To express in words, or in peculiar words; to call; to\n   style.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phrase', 'v. i.', 'To use proper or fine phrases.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phrase', 'v. i.', 'To group notes into phrases; as, he phrases well. See\n   Phrase, n., 4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phraseless', 'a.', 'Indescribable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phraseogram', 'n.', 'A symbol for a phrase.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phraseologic', 'a.', 'Alt. of Phraseological');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phraseological', 'a.', 'Of or pertaining to phraseology; consisting of a\n   peculiar form of words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phraseologist', 'n.', 'A collector or coiner of phrases.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phraseology', 'n.', 'Manner of expression; peculiarity of diction; style.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phraseology', 'n.', 'A collection of phrases; a phrase book.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phrasing', 'n.', 'Method of expression; association of words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phrasing', 'n.', 'The act or method of grouping the notes so as to form\n   distinct musical phrases.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phratries', 'pl. ', 'of Phratry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phratry', 'n.', 'A subdivision of a phyle, or tribe, in Athens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phreatic', 'a.', 'Subterranean; -- applied to sources supplying wells.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phrenetic', 'a.', 'Alt. of Phrenetical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phrenetical', 'a.', 'Relating to phrenitis; suffering from frenzy;\n   delirious; mad; frantic; frenetic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phrenetic', 'n.', 'One who is phrenetic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phrenic', 'a.', 'Of or pertaining to the diaphragm; diaphragmatic; as, the\n   phrenic nerve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phrenics', 'n.', 'That branch of science which relates to the mind; mental\n   philosophy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phrenism', 'n.', 'See Vital force, under Vital.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phrenitis', 'n.', 'Inflammation of the brain, or of the meninges of the\n   brain, attended with acute fever and delirium; -- called also\n   cephalitis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phrenitis', 'n.', 'See Frenzy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phrenograph', 'n.', 'An instrument for registering the movements of the\n   diaphragm, or midriff, in respiration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phrenologer', 'n.', 'A phrenologist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phrenologic', 'a.', 'Phrenological.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phrenological', 'a.', 'Of or pertaining to phrenology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phrenologist', 'n.', 'One versed in phrenology; a craniologist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phrenology', 'n.', 'The science of the special functions of the several\n   parts of the brain, or of the supposed connection between the various\n   faculties of the mind and particular organs in the brain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phrenology', 'n.', 'In popular usage, the physiological hypothesis of Gall,\n   that the mental faculties, and traits of character, are shown on the\n   surface of the head or skull; craniology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phrenomagnetism', 'n.', 'The power of exciting the organs of the brain by\n   magnetic or mesmeric influence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phrenosin', 'n.', 'A nitrogenous body, related to cerebrin, supposed to\n   exist in the brain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phrensied', 'p. p. & a.', 'See Frenzied.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phrensy', 'n.', 'Violent and irrational excitement; delirium. See Frenzy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phrensy', 'v. t.', 'To render frantic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phrentic', 'n. & a.', 'See Phrenetic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phryganeid', 'n.', 'Any insect belonging to the Phryganeides.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phryganeides', 'n. pl.', 'A tribe of neuropterous insects which includes\n   the caddice flies; -- called also Trichoptera. See Trichoptera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phrygian', 'a.', 'Of or pertaining to Phrygia, or to its inhabitants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phrygian', 'n.', 'A native or inhabitant of Phrygia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phrygian', 'n.', 'A Montanist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phthalate', 'n.', 'A salt of phthalic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phthalein', 'n.', 'One of a series of artificial organic dyes made as\n   condensation products of the phenols with phthalic acid, and well\n   represented by phenol phthalein. Their alkaline solutions are\n   fluorescent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phthalic', 'a.', 'Pertaining to, or designating, a dibasic acid obtained by\n   the oxidation of naphthalene and allied substances.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phthalide', 'n.', 'A lactone obtained by reduction of phthalyl chloride, as\n   a white crystalline substance; hence, by extension, any one of the\n   series of which phthalide proper is the type.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phthalimide', 'n.', 'An imido derivative of phthalic acid, obtained as a\n   white crystalline substance, C6H4.(CO)2NH, which has itself (like\n   succinimide) acid properties, and forms a series of salts. Cf. Imido\n   acid, under Imido.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phthalin', 'n.', 'A colorless crystalline substance obtained by reduction\n   from phthalein, into which it is easily converted by oxidation; hence,\n   any one of the series of which phthalin proper is the type.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phthalyl', 'n.', 'The hypothetical radical of phthalic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phthiriasis', 'n.', 'A disease (morbus pediculous) consisting in the\n   excessive multiplication of lice on the human body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phthisic', 'n.', 'Same as Phthisis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phthisical', 'a.', 'Of or pertaining to phthisis; affected with phthisis;\n   wasting; consumptive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phthisicky', 'a.', 'Having phthisis, or some symptom of it, as difficulty\n   in breathing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phthisiology', 'n.', 'A treatise on phthisis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phthisipneumonia', 'n.', 'Alt. of Phthisipneumony');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phthisipneumony', 'n.', 'Pulmonary consumption.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phthisis', 'n.', 'A wasting or consumption of the tissues. The term was\n   formerly applied to many wasting diseases, but is now usually\n   restricted to pulmonary phthisis, or consumption. See Consumption.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phthongal', 'a.', 'Formed into, or characterized by, voice; vocalized; --\n   said of all the vowels and the semivowels, also of the vocal or sonant\n   consonants g, d, b, l, r, v, z, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phthongal', 'n.', 'A vocalized element or letter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phthongometer', 'n.', 'An instrument for measuring vocal sounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phthor', 'n.', 'Fluorine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phycite', 'n.', 'See Erythrite, 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phycochrome', 'n.', 'A bluish green coloring matter of certain algae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phycocyanin', 'n.', 'Alt. of Phycocyanine');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phycocyanine', 'n.', 'A blue coloring matter found in certain algae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phycoerythrin', 'n.', 'Alt. of Phycoerythrine');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phycoerythrine', 'n.', 'A red coloring matter found in algae of the\n   subclass Florideae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phycography', 'n.', 'A description of seaweeds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phycology', 'n.', 'The science of algae, or seaweeds; algology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phycomater', 'n.', 'A gelatin in which the algae spores have been supposed\n   to vegetate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phycophaeine', 'n.', 'A brown coloring matter found in certain algae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phycoxanthin', 'n.', 'Alt. of Phycoxanthine');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phycoxanthine', 'n.', 'A yellowish coloring matter found in certain algae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phylacter', 'n.', 'A phylactery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phylactered', 'a.', 'Wearing a phylactery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phylacteric', 'a.', 'Alt. of Phylacterical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phylacterical', 'a.', 'Of or pertaining to phylacteries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phylacteries', 'pl. ', 'of Phylactery');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phylactery', 'n.', 'Any charm or amulet worn as a preservative from danger\n   or disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phylactery', 'n.', 'A small square box, made either of parchment or of\n   black calfskin, containing slips of parchment or vellum on which are\n   written the scriptural passages Exodus xiii. 2-10, and 11-17, Deut. vi.\n   4-9, 13-22. They are worn by Jews on the head and left arm, on week-day\n   mornings, during the time of prayer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phylactery', 'n.', 'Among the primitive Christians, a case in which the\n   relics of the dead were inclosed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phylactocarp', 'n.', 'A branch of a plumularian hydroid specially modified\n   in structure for the protection of the gonothecae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phylactolaema', 'n. pl.', 'Alt. of Phylactolaemata');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phylactolaemata', 'n. pl.', 'An order of fresh-water Bryozoa in which the\n   tentacles are arranged on a horseshoe-shaped lophophore, and the mouth\n   is covered by an epistome. Called also Lophopoda, and hippocrepians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phylactolaematous', 'a.', 'Of or pertaining to the Phylactolaema.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phylactolema', 'n. pl.', 'Alt. of Phylactolemata');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phylactolemata', 'n. pl.', 'Same as Phylactolaema.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phylarch', 'n.', 'The chief of a phyle, or tribe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phylarchy', 'n.', 'The office of a phylarch; government of a class or\n   tribe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phylae', 'pl. ', 'of Phyle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phyle', 'n.', 'A local division of the people in ancient Athens; a clan; a\n   tribe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phyllite', 'n.', 'A mineral related to ottrelite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phyllite', 'n.', 'Clay slate; argillaceous schist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phyllo-', '', 'A combining form from Gr. / a leaf; as, phyllopod,\n   phyllotaxy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phyllobranciae', 'pl. ', 'of Phyllobranchia');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phyllobranchia', 'n.', 'A crustacean gill composed of lamellae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phyllocladia', 'pl. ', 'of Phyllocladium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phyllocladium', 'n.', 'A flattened stem or branch which more or less\n   resembles a leaf, and performs the function of a leaf as regards\n   respiration and assimilation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phyllocyanin', 'n.', 'A blue coloring matter extracted from chlorophyll.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phyllocyst', 'n.', 'The cavity of a hydrophyllium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phyllode', 'n.', 'Same as Phyllodium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phyllodineous', 'a.', 'Having phyllodia; relating to phyllodia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phyllodia', 'pl. ', 'of Phyllodium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phyllodium', 'n.', 'A petiole dilated into the form of a blade, and usually\n   with vertical edges, as in the Australian acacias.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phyllody', 'n.', 'A retrograde metamorphosis of the floral organs to the\n   condition of leaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phylloid', 'a.', 'Resembling a leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phyllomania', 'n.', 'An abnormal or excessive production of leaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phyllome', 'n.', 'A foliar part of a plant; any organ homologous with a\n   leaf, or produced by metamorphosis of a leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phyllomorphosis', 'n.', 'The succession and variation of leaves during\n   different seasons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phyllophagan', 'n.', 'One of a group of marsupials including the\n   phalangists.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phyllophagan', 'n.', 'One of a tribe of beetles which feed upon the leaves\n   of plants, as the chafers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phyllophagous', 'a.', 'Substituting on leaves; leaf-eating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phyllophorous', 'a.', 'Leaf-bearing; producing leaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phyllopod', 'n.', 'One of the Phyllopoda. [Also used adjectively.]');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phyllopoda', 'n. pl.', 'An order of Entomostraca including a large number\n   of species, most of which live in fresh water. They have flattened or\n   leaflike legs, often very numerous, which they use in swimming. Called\n   also Branchiopoda.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phyllopodous', 'a.', 'Of or pertaining to the Phyllopoda.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phyllorhine', 'a.', 'Of or pertaining to Phyllorhina and other related\n   genera of bats that have a leaflike membrane around the nostrils.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phyllosoma', 'n.', 'The larva of the spiny lobsters (Palinurus and allied\n   genera). Its body is remarkably thin, flat, and transparent; the legs\n   are very long. Called also glass-crab, and glass-shrimp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phyllostome', 'n.', 'Any bat of the genus Phyllostoma, or allied genera,\n   having large membranes around the mouth and nose; a nose-leaf bat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phylloltomid', 'n.', 'A phyllostome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phyllotactic', 'a.', 'Of or pertaining to phyllotaxy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phyllotaxy', 'n.', 'Alt. of Phyllotaxis');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phyllotaxis', 'n.', 'The order or arrangement of leaves on the stem; the\n   science of the relative position of leaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phyllous', 'a.', 'Homologous with a leaf; as, the sepals, petals, stamens,\n   and pistils are phyllous organs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phylloxanthin', 'n.', 'A yellow coloring matter extracted from chlorophyll.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phylloxera', 'n.', 'A small hemipterous insect (Phylloxera vastatrix)\n   allied to the aphids. It attacks the roots and leaves of the grapevine,\n   doing great damage, especially in Europe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phylloxera', 'n.', 'The diseased condition of a vine caused by the insect\n   just described.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phylogenesis', 'n.', 'Alt. of Phylogeny');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phylogeny', 'n.', 'The history of genealogical development; the race\n   history of an animal or vegetable type; the historic exolution of the\n   phylon or tribe, in distinction from ontogeny, or the development of\n   the individual organism, and from biogenesis, or life development\n   generally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phylogenetic', 'a.', 'Relating to phylogenesis, or the race history of a\n   type of organism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phyla', 'pl. ', 'of Phylon');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phylon', 'n.', 'A tribe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phyla', 'pl. ', 'of Phylum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phylum', 'n.', 'One of the larger divisions of the animal kingdom; a\n   branch; a grand division.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phymata', 'pl. ', 'of Phyma');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phyma', 'n.', 'A tubercle on any external part of the body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Physa', 'n.', 'A genus of fresh-water Pulmonifera, having reversed spiral\n   shells. See Pond snail, under Pond.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Physalia', 'n.', 'A genus of large oceanic Siphonophora which includes the\n   Portuguese man-of-war.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Physaliae', 'n. pl.', 'An order of Siphonophora which includes Physalia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Physemaria', 'n. pl.', 'A group of simple marine organisms, usually classed\n   as the lowest of the sponges. They have inflated hollow bodies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Physeter', 'n.', 'The genus that includes the sperm whale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Physeter', 'n.', 'A filtering machine operated by air pressure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Physianthropy', 'n.', 'The philosophy of human life, or the doctrine of the\n   constitution and diseases of man, and their remedies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Physic', 'n.', 'The art of healing diseases; the science of medicine; the\n   theory or practice of medicine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Physic', 'n.', 'A specific internal application for the cure or relief of\n   sickness; a remedy for disease; a medicine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Physic', 'n.', 'Specifically, a medicine that purges; a cathartic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Physic', 'n.', 'A physician.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Physiced', 'imp. & p. p.', 'of Physic');
  INSERT INTO words (word, word_type, description) VALUES  (    'Physicking', 'p. pr. & vb. n.', 'of Physic');
  INSERT INTO words (word, word_type, description) VALUES  (    'Physic', 'v. t.', 'To treat with physic or medicine; to administer medicine\n   to, esp. a cathartic; to operate on as a cathartic; to purge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Physic', 'v. t.', 'To work on as a remedy; to heal; to cure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Physical', 'a.', 'Of or pertaining to nature (as including all created\n   existences); in accordance with the laws of nature; also, of or\n   relating to natural or material things, or to the bodily structure, as\n   opposed to things mental, moral, spiritual, or imaginary; material;\n   natural; as, armies and navies are the physical force of a nation; the\n   body is the physical part of man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Physical', 'a.', 'Of or pertaining to physics, or natural philosophy;\n   treating of, or relating to, the causes and connections of natural\n   phenomena; as, physical science; physical laws.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Physical', 'a.', 'Perceptible through a bodily or material organization;\n   cognizable by the senses; external; as, the physical, opposed to\n   chemical, characters of a mineral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Physical', 'a.', 'Of or pertaining to physic, or the art of medicine;\n   medicinal; curative; healing; also, cathartic; purgative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Physically', 'adv.', 'In a physical manner; according to the laws of nature\n   or physics; by physical force; not morally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Physically', 'adv.', 'According to the rules of medicine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Physician', 'n.', 'A person skilled in physic, or the art of healing; one\n   duty authorized to prescribe remedies for, and treat, diseases; a\n   doctor of medicine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Physician', 'n.', 'Hence, figuratively, one who ministers to moral\n   diseases; as, a physician of the soul.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Physicianed', 'a.', 'Licensed as a physician.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Physicism', 'n.', 'The tendency of the mind toward, or its preoccupation\n   with, physical phenomena; materialism in philosophy and religion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Physicist', 'n.', 'One versed in physics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Physicist', 'n.', 'A believer in the theory that the fundamental phenomena\n   of life are to be explained upon purely chemical and physical\n   principles; -- opposed to vitalist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Physicking', '', 'p. pr. & vb. n. fr. Physic, v. t.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Physico-', '', 'A combining form, denoting relation to, or dependence upon,\n   natural causes, or the science of physics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Physicochemical', 'a.', 'Involving the principles of both physics and\n   chemistry; dependent on, or produced by, the joint action of physical\n   and chemical agencies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Physicologic', 'n.', 'Logic illustrated by physics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Physicological', 'a.', 'Of or pertaining to physicologic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Physicology', 'n.', 'Physics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Physico-mathematics', 'n.', 'Mixed mathematics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Physico-philosophy', 'n.', 'The philosophy of nature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Physico-theology', 'n.', 'Theology or divinity illustrated or enforced by\n   physics or natural philosophy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Physics', 'n.', 'The science of nature, or of natural objects; that branch\n   of science which treats of the laws and properties of matter, and the\n   forces acting upon it; especially, that department of natural science\n   which treats of the causes (as gravitation, heat, light, magnetism,\n   electricity, etc.) that modify the general properties of bodies;\n   natural philosophy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Physiocrat', 'n.', 'One of the followers of Quesnay of France, who, in the\n   18th century, founded a system of political economy based upon the\n   supremacy of natural order.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Physiogeny', 'n.', 'The germ history of the functions, or the history of\n   the development of vital activities, in the individual, being one of\n   the branches of ontogeny. See Morphogeny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Physiognomer', 'n.', 'Physiognomist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Physiognomic', 'a.', 'Alt. of Physiognomical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Physiognomical', 'a.', 'Of or pertaining to physiognomy; according with the\n   principles of physiognomy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Physiognomist', 'n.', 'Same as Physiognomy, 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Physiognomist', 'n.', 'One skilled in physiognomy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Physiognomist', 'n.', 'One who tells fortunes by physiognomy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Physiognomize', 'v. t.', 'To observe and study the physiognomy of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Physiognommonic', 'a.', 'Physiognomic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Physiognomies', 'pl. ', 'of Physiognomy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Physiognomy', 'n.', 'The art and science of discovering the predominant\n   temper, and other characteristic qualities of the mind, by the outward\n   appearance, especially by the features of the face.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Physiognomy', 'n.', 'The face or countenance, with respect to the temper of\n   the mind; particular configuration, cast, or expression of countenance,\n   as denoting character.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Physiognomy', 'n.', 'The art telling fortunes by inspection of the\n   features.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Physiognomy', 'n.', 'The general appearance or aspect of a thing, without\n   reference to its scientific characteristics; as, the physiognomy of a\n   plant, or of a meteor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Physiogony', 'n.', 'The birth of nature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Physiographic', 'a.', 'Alt. of Physiographical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Physiographical', 'a.', 'Of or pertaining to physiography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Physiography', 'n.', 'The science which treats of the earths exterior\n   physical features, climate, life, etc., and of the physical movements\n   or changes on the earths surface, as the currents of the atmosphere\n   and ocean, the secular variations in heat, moisture, magnetism, etc.;\n   physical geography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Physiolatry', 'n.', 'The worship of the powers or agencies of nature;\n   materialism in religion; nature worship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Physiologer', 'n.', 'A physiologist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Physiologic', 'a.', 'Physiological.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Physiological', 'a.', 'Of or pertaining to physiology; relating to the\n   science of the functions of living organism; as, physiological botany\n   or chemistry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Physiologically', 'adv.', 'In a physiological manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Physiologist', 'n.', 'One who is versed in the science of physiology; a\n   student of the properties and functions of animal and vegetable organs\n   and tissues.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Physiologize', 'v. i.', 'To speculate in physiology; to make physiological\n   investigations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Physiologies', 'pl. ', 'of Physiology');
  INSERT INTO words (word, word_type, description) VALUES  (    'Physiology', 'n.', 'The science which treats of the phenomena of living\n   organisms; the study of the processes incidental to, and characteristic\n   of, life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Physiology', 'n.', 'A treatise on physiology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Physiophyly', 'n.', 'The tribal history of the functions, or the history of\n   the paleontological development of vital activities, -- being a branch\n   of phylogeny. See Morphophyly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Physique', 'n.', 'The natural constitution, or physical structure, of a\n   person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Physnomy', 'n.', 'Physiogmony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Physoclist', 'n.', 'One of the Physoclisti.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Physoclisti', 'n. pl.', 'An order of teleost in which the air bladder has\n   no opening.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Physograde', 'n.', 'Any siphonophore which has an air sac for a float, as\n   the Physalia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Physophorae', 'n. pl.', 'An order of Siphonophora, furnished with an air\n   sac, or float, and a series of nectocalyces. See Illust. under\n   Nectocalyx.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Physopod', 'n.', 'One of the Physopoda; a thrips.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Physopoda', 'n. pl.', 'Same as Thysanoptera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Physostigmine', 'n.', 'An alkaloid found in the Calabar bean (the seed of\n   Physostigma venenosum), and extracted as a white, tasteless, substance,\n   amorphous or crystalline; -- formerly called eserine, with which it was\n   regarded as identical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Physostomi', 'n. pl.', 'An order of fishes in which the air bladder is\n   provided with a duct, and the ventral fins, when present, are\n   abdominal. It includes the salmons, herrings, carps, catfishes, and\n   others.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Physostomous', 'a.', 'Having a duct to the air bladder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Physostomous', 'a.', 'Pertaining to the Physostomi.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phytelephas', 'n.', 'A genus of South American palm trees, the seeds of\n   which furnish the substance called vegetable ivory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phytivorous', 'a.', 'Feeding on plants or herbage; phytophagous; as,\n   phytivorous animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phyto-', '', 'A combining form from Gr. fyto`n a plant; as, phytochemistry,\n   phytography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phytochemical', 'a.', 'Relating to phytochemistry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phytochemistry', 'n.', 'Chemistry in its relation to vegetable bodies;\n   vegetable chemistry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phytochimy', 'n.', 'Phytochemistry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phytogenesis', 'n.', 'Alt. of Phytogeny');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phytogeny', 'n.', 'The doctrine of the generation of plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phytogeographical', 'a.', 'Of or pertaining to phytogeography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phytogeography', 'n.', 'The geographical distribution of plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phytoglyphic', 'a.', 'Relating to phytoglyphy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phytoglyphy', 'n.', 'See Nature printing, under Nature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phytographical', 'a.', 'Of or pertaining to phytography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phytography', 'n.', 'The science of describing plants in a systematic\n   manner; also, a description of plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phytoid', 'a.', 'Resembling a plant; plantlike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phytolacca', 'n.', 'A genus of herbaceous plants, some of them having\n   berries which abound in intensely red juice; poke, or pokeweed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phytolite', 'n.', 'An old name for a fossil plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phytolithologist', 'n.', 'One versed in phytolithology; a paleobotanist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phytolithology', 'n.', 'The branch of science which treats of fossil\n   plants; -- usually called paleobotany, sometimes paleophytology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phytological', 'a.', 'Of or pertaining to phytology; botanical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phytologist', 'n.', 'One skilled in phytology; a writer on plants; a\n   botanist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phytology', 'n.', 'The science of plants; a description of the kinds and\n   properties of plants; botany.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phytomer', 'n.', 'Alt. of Phytomeron');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phytomeron', 'n.', 'An organic element of a flowering plant; a phyton.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phytons', 'pl. ', 'of Phyton');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phyton', 'n.', 'One of the parts which by their repetition make up a\n   flowering plant, each being a single joint of a stem with its leaf or\n   leaves; a phytomer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phytonomy', 'n.', 'The science of the origin and growth of plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phytopathologist', 'n.', 'One skilled in diseases of plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phytopathology', 'n.', 'The science of diseases to which plants are liable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phytophaga', 'n. pl.', 'A division of Hymenoptera; the sawflies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phytophagic', 'a.', 'Phytophagous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phytophagous', 'a.', 'Feeding on plants; herbivorous; as, a phytophagous\n   animal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phytophagy', 'n.', 'The eating of plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phytophysiology', 'n.', 'Vegetable physiology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phytotomist', 'n.', 'One versed in phytotomy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phytotomy', 'n.', 'The dissection of plants; vegetable anatomy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phytozoaria', 'n. pl.', 'Same as Infusoria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phytozoa', 'pl. ', 'of Phytozoon');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phytozoon', 'n.', 'A plantlike animal. The term is sometimes applied to\n   zoophytes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Phyz', 'n.', 'See Phiz.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pi', 'n.', 'A mass of type confusedly mixed or unsorted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pied', 'imp. & p. p.', 'of Pi');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pieing', 'p. pr. & vb. n.', 'of Pi');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pi', 'v. t.', 'To put into a mixed and disordered condition, as type; to\n   mix and disarrange the type of; as, to pi a form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piacaba', 'n.', 'See Piassava.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piacle', 'n.', 'A heinous offense which requires expiation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piacular', 'a.', 'Expiatory; atoning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piacular', 'a.', 'Requiring expiation; criminal; atrociously bad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piacularity', 'n.', 'The quality or state of being piacular; criminality;\n   wickedness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piaculous', 'a.', 'Same as Piacular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pial', 'a.', 'Pertaining to the pia mater.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pia mater', '', 'The delicate and highly vascular membrane immediately\n   investing the brain and spinal cord.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pian', 'n.', 'The yaws. See Yaws.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pianet', 'n.', 'The magpie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pianet', 'n.', 'The lesser woodpecker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pianette', 'n.', 'A small piano; a pianino.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pianino', 'n.', 'A pianette, or small piano.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pianissimo', 'a.', 'Very soft; -- a direction to execute a passage as\n   softly as possible. (Abbrev. pp.)');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pianist', 'n.', 'A performer, esp. a skilled performer, on the piano.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piano', 'a. & adv.', 'Soft; -- a direction to the performer to execute a\n   certain passage softly, and with diminished volume of tone. (Abbrev.\n   p.)');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piano', 'a.', 'Alt. of Pianoforte');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pianoforte', 'a.', 'A well-known musical instrument somewhat resembling the\n   harpsichord, and consisting of a series of wires of graduated length,\n   thickness, and tension, struck by hammers moved by keys.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pianograph', 'n.', 'A form of melodiograph applied to a piano.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piapec', 'n.', 'A West African pie (Ptilostomus Senegalensis).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piarist', 'n.', 'One of a religious order who are the regular clerks of the\n   Scuole Pie (religious schools), an institute of secondary education,\n   founded at Rome in the last years of the 16th century.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piassava', 'n.', 'A fibrous product of two Brazilian palm trees (Attalea\n   funifera and Leopoldinia Piassaba), -- used in making brooms, and for\n   other purposes. Called also piacaba and piasaba.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piaster', 'n.', 'A silver coin of Spain and various other countries. See\n   Peso. The Spanish piaster (commonly called peso, or peso duro) is of\n   about the value of the American dollar. The Italian piaster, or scudo,\n   was worth from 80 to 100 cents. The Turkish and Egyptian piasters are\n   now worth about four and a half cents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piastre', 'n.', 'See Piaster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piation', 'n.', 'The act of making atonement; expiation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piatti', 'n. pl.', 'Cymbals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piazzas', 'pl. ', 'of Piazza');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piazza', 'n.', 'An open square in a European town, especially an Italian\n   town; hence (Arch.), an arcaded and roofed gallery; a portico. In the\n   United States the word is popularly applied to a veranda.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pibcorn', 'n.', 'A wind instrument or pipe, with a horn at each end, --\n   used in Wales.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pibroch', 'n.', 'A Highland air, suited to the particular passion which the\n   musician would either excite or assuage; generally applied to those\n   airs that are played on the bagpipe before the Highlanders when they go\n   out to battle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pic', 'n.', 'A Turkish cloth measure, varying from 18 to 28 inches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pica', 'n.', 'The genus that includes the magpies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pica', 'n.', 'A vitiated appetite that craves what is unfit for food, as\n   chalk, ashes, coal, etc.; chthonophagia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pica', 'n.', 'A service-book. See Pie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pica', 'n.', 'A size of type next larger than small pica, and smaller than\n   English.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Picador', 'n.', 'A horseman armed with a lance, who in a bullfight receives\n   the first attack of the bull, and excites him by picking him without\n   attempting to kill him.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Picamar', 'n.', 'An oily liquid hydrocarbon extracted from the creosote of\n   beechwood tar. It consists essentially of certain derivatives of\n   pyrogallol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Picapare', 'n.', 'The finfoot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Picard', 'n.', 'One of a sect of Adamites in the fifteenth century; -- so\n   called from one Picard of Flanders. See Adamite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Picaresque', 'a.', 'Applied to that class of literature in which the\n   principal personage is the Spanish picaro, meaning a rascal, a knave, a\n   rogue, an adventurer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Picariae', 'n. pl.', 'An extensive division of birds which includes the\n   woodpeckers, toucans, trogons, hornbills, kingfishers, motmots,\n   rollers, and goatsuckers. By some writers it is made to include also\n   the cuckoos, swifts, and humming birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Picarian', 'a.', 'Of or pertaining to Picariae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Picarian', 'n.', 'One of the Picariae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Picaroon', 'n.', 'One who plunders; especially, a plunderer of wrecks; a\n   pirate; a corsair; a marauder; a sharper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Picayune', 'n.', 'A small coin of the value of six and a quarter cents. See\n   Fippenny bit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Picayunish', 'a.', 'Petty; paltry; mean; as, a picayunish business.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piccadil', 'n.', 'Alt. of Piccadilly');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piccadilly', 'n.', 'A high, stiff collar for the neck; also, a hem or band\n   about the skirt of a garment, -- worn by men in the 17th century.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piccage', 'n.', 'Money paid at fairs for leave to break ground for booths.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piccalilli', 'n.', 'A pickle of various vegetables with pungent species, --\n   originally made in the East Indies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piccolo', 'n.', 'A small, shrill flute, the pitch of which is an octave\n   higher than the ordinary flute; an octave flute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piccolo', 'n.', 'A small upright piano.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piccolo', 'n.', 'An organ stop, with a high, piercing tone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pice', 'n.', 'A small copper coin of the East Indies, worth less than a\n   cent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Picea', 'n.', 'A genus of coniferous trees of the northen hemisphere,\n   including the Norway spruce and the American black and white spruces.\n   These trees have pendent cones, which do not readily fall to pieces, in\n   this and other respects differing from the firs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Picene', 'n.', 'A hydrocarbon (C/H/) extracted from the pitchy residue of\n   coal tar and petroleum as a bluish fluorescent crystalline substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piceous', 'a.', 'Of or pertaining to pitch; resembling pitch in color or\n   quality; pitchy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pichey', 'n.', 'A Brazilian armadillo (Dasypus minutus); the little\n   armadillo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pichiciago', 'n.', 'A small, burrowing, South American edentate\n   (Chlamyphorus truncatus), allied to the armadillos. The shell is\n   attached only along the back.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pichurim bean', '', 'The seed of a Brazilian lauraceous tree (Nectandra\n   Puchury) of a taste and smell between those of nutmeg and of sassafras,\n   -- sometimes used medicinally. Called also sassafras nut.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pici', 'n. pl.', 'A division of birds including the woodpeckers and\n   wrynecks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piciform', 'a.', 'Of or pertaining to Piciformes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piciformes', 'n. pl.', 'A group of birds including the woodpeckers,\n   toucans, barbets, colies, kingfishes, hornbills, and some other related\n   groups.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Picine', 'a.', 'Of or pertaining to the woodpeckers (Pici), or to the\n   Piciformes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Picked', 'imp. & p. p.', 'of Pick');
  INSERT INTO words (word, word_type, description) VALUES  (    'Picking', 'p. pr. & vb. n.', 'of Pick');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pick', 'v.', 'To throw; to pitch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pick', 'v.', 'To peck at, as a bird with its beak; to strike at with\n   anything pointed; to act upon with a pointed instrument; to pierce; to\n   prick, as with a pin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pick', 'v.', 'To separate or open by means of a sharp point or points; as,\n   to pick matted wool, cotton, oakum, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pick', 'v.', 'To open (a lock) as by a wire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pick', 'v.', 'To pull apart or away, especially with the fingers; to pluck;\n   to gather, as fruit from a tree, flowers from the stalk, feathers from\n   a fowl, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pick', 'v.', 'To remove something from with a pointed instrument, with the\n   fingers, or with the teeth; as, to pick the teeth; to pick a bone; to\n   pick a goose; to pick a pocket.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pick', 'v.', 'To choose; to select; to separate as choice or desirable; to\n   cull; as, to pick ones company; to pick ones way; -- often with out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pick', 'v.', 'To take up; esp., to gather from here and there; to collect;\n   to bring together; as, to pick rags; -- often with up; as, to pick up a\n   ball or stones; to pick up information.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pick', 'v.', 'To trim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pick', 'v. i.', 'To eat slowly, sparingly, or by morsels; to nibble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pick', 'v. i.', 'To do anything nicely or carefully, or by attending to\n   small things; to select something with care.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pick', 'v. i.', 'To steal; to pilfer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pick', 'n.', 'A sharp-pointed tool for picking; -- often used in\n   composition; as, a toothpick; a picklock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pick', 'n.', 'A heavy iron tool, curved and sometimes pointed at both ends,\n   wielded by means of a wooden handle inserted in the middle, -- used by\n   quarrymen, roadmakers, etc.; also, a pointed hammer used for dressing\n   millstones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pick', 'n.', 'A pike or spike; the sharp point fixed in the center of a\n   buckler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pick', 'n.', 'Choice; right of selection; as, to have ones pick.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pick', 'n.', 'That which would be picked or chosen first; the best; as, the\n   pick of the flock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pick', 'n.', 'A particle of ink or paper imbedded in the hollow of a\n   letter, filling up its face, and occasioning a spot on a printed sheet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pick', 'n.', 'That which is picked in, as with a pointed pencil, to correct\n   an unevenness in a picture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pick', 'n.', 'The blow which drives the shuttle, -- the rate of speed of a\n   loom being reckoned as so many picks per minute; hence, in describing\n   the fineness of a fabric, a weft thread; as, so many picks to an inch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pickaback', 'adv.', 'On the back or shoulders; as, to ride pickback.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pickaninnies', 'pl. ', 'of Pickaninny');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pickaninny', 'n.', 'A small child; especially, a negro or mulatto infant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pickapack', 'adv.', 'Pickaback.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pickax', 'n.', 'Alt. of Pickaxe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pickaxe', 'n.', 'A pick with a point at one end, a transverse edge or blade\n   at the other, and a handle inserted at the middle; a hammer with a\n   flattened end for driving wedges and a pointed end for piercing as it\n   strikes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pickback', 'adv.', 'On the back.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Picked', 'a.', 'Pointed; sharp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Picked', 'a.', 'Having a pike or spine on the back; -- said of certain\n   fishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Picked', 'a.', 'Carefully selected; chosen; as, picked men.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Picked', 'a.', 'Fine; spruce; smart; precise; dianty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pickedness', 'n.', 'The state of being sharpened; pointedness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pickedness', 'n.', 'Fineness; spruceness; smartness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pickeered', 'imp. & p. p.', 'of Pickeer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pickeering', 'p. pr. & vb. n.', 'of Pickeer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pickeer', 'v. i.', 'To make a raid for booty; to maraud; also, to skirmish\n   in advance of an army. See Picaroon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pickeerer', 'n.', 'One who pickeers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Picker', 'n.', 'One who, or that which, picks, in any sense, -- as, one who\n   uses a pick; one who gathers; a thief; a pick; a pickax; as, a cotton\n   picker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Picker', 'n.', 'A machine for picking fibrous materials to pieces so as to\n   loosen and separate the fiber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Picker', 'n.', 'The piece in a loom which strikes the end of the shuttle,\n   and impels it through the warp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Picker', 'n.', 'A priming wire for cleaning the vent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pickerel', 'n.', 'A young or small pike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pickerel', 'n.', 'Any one of several species of freshwater fishes of the\n   genus Esox, esp. the smaller species.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pickerel', 'n.', 'The glasseye, or wall-eyed pike. See Wall-eye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pickering', 'n.', 'The sauger of the St.Lawrence River.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pickery', 'n.', 'Petty theft.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Picket', 'n.', 'A stake sharpened or pointed, especially one used in\n   fortification and encampments, to mark bounds and angles; or one used\n   for tethering horses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Picket', 'n.', 'A pointed pale, used in marking fences.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Picket', 'n.', 'A detached body of troops serving to guard an army from\n   surprise, and to oppose reconnoitering parties of the enemy; -- called\n   also outlying picket.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Picket', 'n.', 'By extension, men appointed by a trades union, or other\n   labor organization, to intercept outsiders, and prevent them from\n   working for employers with whom the organization is at variance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Picket', 'n.', 'A military punishment, formerly resorted to, in which the\n   offender was forced to stand with one foot on a pointed stake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Picket', 'n.', 'A game at cards. See Piquet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Picketed', 'imp. & p. p.', 'of Picket');
  INSERT INTO words (word, word_type, description) VALUES  (    'Picketing', 'p. pr. & vb. n.', 'of Picket');
  INSERT INTO words (word, word_type, description) VALUES  (    'Picket', 'v. t.', 'To fortify with pointed stakes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Picket', 'v. t.', 'To inclose or fence with pickets or pales.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Picket', 'v. t.', 'To tether to, or as to, a picket; as, to picket a horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Picket', 'v. t.', 'To guard, as a camp or road, by an outlying picket.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Picket', 'v. t.', 'To torture by compelling to stand with one foot on a\n   pointed stake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Picketee', 'n.', 'See Picotee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pick-fault', 'n.', 'One who seeks out faults.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Picking', 'n.', 'The act of digging or breaking up, as with a pick.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Picking', 'n.', 'The act of choosing, plucking, or gathering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Picking', 'n.', 'That which is, or may be, picked or gleaned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Picking', 'n.', 'Pilfering; also, that which is pilfered.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Picking', 'n.', 'The pulverized shells of oysters used in making walks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Picking', 'n.', 'Rough sorting of ore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Picking', 'n.', 'Overburned bricks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Picking', 'a.', 'Done or made as with a pointed tool; as, a picking sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Picking', 'a.', 'Nice; careful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pickle', 'n.', 'See Picle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pickle', 'v. t.', 'A solution of salt and water, in which fish, meat, etc.,\n   may be preserved or corned; brine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pickle', 'v. t.', 'Vinegar, plain or spiced, used for preserving\n   vegetables, fish, eggs, oysters, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pickle', 'v. t.', 'Any article of food which has been preserved in brine or\n   in vinegar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pickle', 'v. t.', 'A bath of dilute sulphuric or nitric acid, etc., to\n   remove burnt sand, scale rust, etc., from the surface of castings, or\n   other articles of metal, or to brighten them or improve their color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pickle', 'v. t.', 'A troublesome child; as, a little pickle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pickled', 'imp. & p. p.', 'of Pickle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pickling', 'p. pr. & vb. n.', 'of Pickle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pickle', 'v. t.', 'To preserve or season in pickle; to treat with some kind\n   of pickle; as, to pickle herrings or cucumbers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pickle', 'v. t.', 'To give an antique appearance to; -- said of copies or\n   imitations of paintings by the old masters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pickled', 'a.', 'Preserved in a pickle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pickle-herring', 'n.', 'A herring preserved in brine; a pickled herring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pickle-herring', 'n.', 'A merry-andrew; a buffoon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pickler', 'n.', 'One who makes pickles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Picklock', 'n.', 'An instrument for picking locks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Picklock', 'n.', 'One who picks locks; a thief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pickmire', 'n.', 'The pewit, or black-headed gull.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Picknick', 'n.', 'See Picnic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pickpack', 'adv.', 'Pickaback.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pickpennies', 'pl. ', 'of Pickpenny');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pickpenny', 'n.', 'A miser; also, a sharper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pickpocket', 'n.', 'One who steals purses or other articles from pockets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pickpurse', 'n.', 'One who steals purses, or money from purses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Picksy', 'n.', 'See Pixy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pickthank', 'n.', 'One who strives to put another under obligation; an\n   officious person; hence, a flatterer. Used also adjectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Picktooth', 'n.', 'A toothpick.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Picke', 'n.', 'A small piece of land inclosed with a hedge; a close.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Picnic', 'v.', 'Formerly, an entertainment at which each person contributed\n   some dish to a common table; now, an excursion or pleasure party in\n   which the members partake of a collation or repast (usually in the open\n   air, and from food carried by themselves).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Picnicked', 'imp. & p. p.', 'of Picnic');
  INSERT INTO words (word, word_type, description) VALUES  (    'Picnicking', 'p. pr. & vb. n.', 'of Picnic');
  INSERT INTO words (word, word_type, description) VALUES  (    'Picnic', 'v. i.', 'To go on a picnic, or pleasure excursion; to eat in\n   public fashion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Picnicker', 'n.', 'One who takes part in a picnic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Picoid', 'a.', 'Like or pertaining to the Pici.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Picoline', 'n.', 'Any one of three isometric bases (C6H7N) related to\n   pyridine, and obtained from bone oil, acrolein ammonia, and coal-tar\n   naphtha, as colorless mobile liquids of strong odor; -- called also\n   methyl pyridine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Picotee', 'n.', 'Alt. of Picotine');
  INSERT INTO words (word, word_type, description) VALUES  (    'Picotine', 'n.', 'A variety of carnation having petals of a light color\n   variously dotted and spotted at the edges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Picquet', 'n.', 'See Piquet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Picra', 'n.', 'The powder of aloes with canella, formerly officinal,\n   employed as a cathartic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Picrate', 'n.', 'A salt of picric acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Picric', 'a.', 'Pertaining to, or designating, a strong organic acid\n   (called picric acid), intensely bitter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Picrite', 'n.', 'A dark green igneous rock, consisting largely of\n   chrysolite, with hornblende, augite, biotite, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Picrolite', 'n.', 'A fibrous variety of serpentine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Picromel', 'n.', 'A colorless viscous substance having a bitter-sweet\n   taste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Picrotoxin', 'n.', 'A bitter white crystalline substance found in the\n   cocculus indicus. It is a peculiar poisonous neurotic and intoxicant,\n   and consists of a mixture of several neutral substances.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Picryl', 'n.', 'The hypothetical radical of picric acid, analogous to\n   phenyl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pictish', 'a.', 'Of or pertaining to Picts; resembling the Picts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pictograph', 'n.', 'A picture or hieroglyph representing and expressing an\n   idea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pictorial', 'a.', 'Of or pertaining to pictures; illustrated by pictures;\n   forming pictures; representing with the clearness of a picture; as, a\n   pictorial dictionary; a pictorial imagination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pictoric', 'a.', 'Alt. of Pictorical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pictorical', 'a.', 'Pictorial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Picts', 'n. pl.', 'A race of people of uncertain origin, who inhabited\n   Scotland in early times.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pictura', 'n.', 'Pattern of coloration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Picturable', 'a.', 'Capable of being pictured, or represented by a picture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pictural', 'a.', 'Pictorial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pictural', 'n.', 'A picture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Picture', 'n.', 'The art of painting; representation by painting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Picture', 'n.', 'A representation of anything (as a person, a landscape, a\n   building) upon canvas, paper, or other surface, produced by means of\n   painting, drawing, engraving, photography, etc.; a representation in\n   colors. By extension, a figure; a model.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Picture', 'n.', 'An image or resemblance; a representation, either to the\n   eye or to the mind; that which, by its likeness, brings vividly to mind\n   some other thing; as, a child is the picture of his father; the man is\n   the picture of grief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pictured', 'imp. & p. p.', 'of Picture');
  INSERT INTO words (word, word_type, description) VALUES  (    'Picturing', 'p. pr. & vb. n.', 'of Picture');
  INSERT INTO words (word, word_type, description) VALUES  (    'Picture', 'v. t.', 'To draw or paint a resemblance of; to delineate; to\n   represent; to form or present an ideal likeness of; to bring before the\n   mind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pictured', 'a.', 'Furnished with pictures; represented by a picture or\n   pictures; as, a pictured scene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Picturer', 'n.', 'One who makes pictures; a painter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Picturesque', 'a.', 'Forming, or fitted to form, a good or pleasing\n   picture; representing with the clearness or ideal beauty appropriate to\n   a picture; expressing that peculiar kind of beauty which is agreeable\n   in a picture, natural or artificial; graphic; vivid; as, a picturesque\n   scene or attitude; picturesque language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Picturesquish', 'a.', 'Somewhat picturesque.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Picturized', 'imp. & p. p.', 'of Picturize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Picturizing', 'p. pr. & vb. n.', 'of Picturize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Picturize', 'v. t.', 'To picture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Picturize', 'v. t.', 'To adorn with pictures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Picul', 'n.', 'A commercial weight varying in different countries and for\n   different commodities. In Borneo it is 135/ lbs.; in China and Sumatra,\n   133/ lbs.; in Japan, 133/ lbs.; but sometimes 130 lbs., etc. Called\n   also, by the Chinese, tan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piculet', 'n.', 'Any species of very small woodpeckers of the genus\n   Picumnus and allied genera. Their tail feathers are not stiff and sharp\n   at the tips, as in ordinary woodpeckers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pici', 'pl. ', 'of Picus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Picus', 'n.', 'A genus of woodpeckers, including some of the common\n   American and European species.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piddled', 'imp. & p. p.', 'of Piddle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piddling', 'p. pr. & vb. n.', 'of Piddle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piddle', 'v. i.', 'To deal in trifles; to concern ones self with trivial\n   matters rather than with those that are important.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piddle', 'v. i.', 'To be squeamishly nice about ones food.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piddle', 'v. i.', 'To urinate; -- childs word.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piddler', 'n.', 'One who piddles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piddling', 'a.', 'Trifling; trivial; frivolous; paltry; -- applied to\n   persons and things.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piddock', 'n.', 'Any species of Pholas; a pholad. See Pholas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pie', 'n.', 'An article of food consisting of paste baked with something in\n   it or under it; as, chicken pie; venison pie; mince pie; apple pie;\n   pumpkin pie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pie', 'n.', 'See Camp, n., 5.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pie', 'n.', 'A magpie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pie', 'n.', 'Any other species of the genus Pica, and of several allied\n   genera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pie', 'n.', 'The service book.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pie', 'n.', 'Type confusedly mixed. See Pi.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pie', 'v. t.', 'See Pi.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piebald', 'a.', 'Having spots and patches of black and white, or other\n   colors; mottled; pied.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piebald', 'a.', 'Fig.: Mixed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piece', 'n.', 'A fragment or part of anything separated from the whole, in\n   any manner, as by cutting, splitting, breaking, or tearing; a part; a\n   portion; as, a piece of sugar; to break in pieces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piece', 'n.', 'A definite portion or quantity, as of goods or work; as, a\n   piece of broadcloth; a piece of wall paper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piece', 'n.', 'Any one thing conceived of as apart from other things of the\n   same kind; an individual article; a distinct single effort of a series;\n   a definite performance');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piece', 'n.', 'A literary or artistic composition; as, a piece of poetry,\n   music, or statuary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piece', 'n.', 'A musket, gun, or cannon; as, a battery of six pieces; a\n   following piece.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piece', 'n.', 'A coin; as, a sixpenny piece; -- formerly applied\n   specifically to an English gold coin worth 22 shillings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piece', 'n.', 'A fact; an item; as, a piece of news; a piece of knowledge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piece', 'n.', 'An individual; -- applied to a person as being of a certain\n   nature or quality; often, but not always, used slightingly or in\n   contempt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piece', 'n.', 'One of the superior men, distinguished from a pawn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piece', 'n.', 'A castle; a fortified building.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pieced', 'imp. & p. p.', 'of Piece');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piecing', 'p. pr. & vb. n.', 'of Piece');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piece', 'v. t.', 'To make, enlarge, or repair, by the addition of a piece\n   or pieces; to patch; as, to piece a garment; -- often with out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piece', 'v. t.', 'To unite; to join; to combine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piece', 'v. i.', 'To unite by a coalescence of parts; to fit together; to\n   join.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pieceless', 'a.', 'Not made of pieces; whole; entire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piecely', 'adv.', 'In pieces; piecemeal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piecemeal', 'adv.', 'In pieces; in parts or fragments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piecemeal', 'adv.', 'Piece by piece; by little and little in succession.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piecemeal', 'a.', 'Made up of parts or pieces; single; separate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piecemeal', 'n.', 'A fragment; a scrap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piecemealed', 'a.', 'Divided into pieces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piecener', 'n.', 'One who supplies rolls of wool to the slubbing machine in\n   woolen mills.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piecener', 'n.', 'Same as Piecer, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piecer', 'n.', 'One who pieces; a patcher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piecer', 'n.', 'A child employed in spinning mill to tie together broken\n   threads.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piecework', 'n.', 'Work done by the piece or job; work paid for at a rate\n   based on the amount of work done, rather than on the time employed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pied', '', 'imp. & p. p. of Pi, or Pie, v.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pied', 'a.', 'Variegated with spots of different colors; party-colored;\n   spotted; piebald.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piedmont', 'a.', 'Noting the region of foothills near the base of a\n   mountain chain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piedmontite', 'n.', 'A manganesian kind of epidote, from Piedmont. See\n   Epidote.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piedness', 'n.', 'The state of being pied.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piedouche', 'n.', 'A pedestal of small size, used to support small objects,\n   as busts, vases, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piedstall', 'n.', 'See Pedestal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piemen', 'pl. ', 'of Pieman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pieman', 'n.', 'A man who makes or sells pies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piend', 'n.', 'See Peen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pieno', 'a.', 'Full; having all the instruments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pieplant', 'n.', 'A plant (Rheum Rhaponticum) the leafstalks of which are\n   acid, and are used in making pies; the garden rhubarb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piepoudre', 'n.', 'Alt. of Piepowder');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piepowder', 'n.', 'An ancient court of record in England, formerly incident\n   to every fair and market, of which the steward of him who owned or had\n   the toll was the judge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pier', 'n.', 'Any detached mass of masonry, whether insulated or supporting\n   one side of an arch or lintel, as of a bridge; the piece of wall\n   between two openings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pier', 'n.', 'Any additional or auxiliary mass of masonry used to stiffen a\n   wall. See Buttress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pier', 'n.', 'A projecting wharf or landing place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pierage', 'n.', 'Same as Wharfage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pierced', 'imp. & p. p.', 'of Pierce');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piercing', 'p. pr. & vb. n.', 'of Pierce');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pierce', 'v. t.', 'To thrust into, penetrate, or transfix, with a pointed\n   instrument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pierce', 'v. t.', 'To penetrate; to enter; to force a way into or through;\n   to pass into or through; as, to pierce the enemys line; a shot pierced\n   the ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pierce', 'v. t.', 'Fig.: To penetrate; to affect deeply; as, to pierce a\n   mystery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pierce', 'v. i.', 'To enter; to penetrate; to make a way into or through\n   something, as a pointed instrument does; -- used literally and\n   figuratively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pierceable', 'a.', 'That may be pierced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pierced', 'a.', 'Penetrated; entered; perforated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piercel', 'n.', 'A kind of gimlet for making vents in casks; -- called also\n   piercer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piercer', 'n.', 'One who, or that which, pierces or perforates');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piercer', 'n.', 'An instrument used in forming eyelets; a stiletto.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piercer', 'n.', 'A piercel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piercer', 'n.', 'The ovipositor, or sting, of an insect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piercer', 'n.', 'An insect provided with an ovipositor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piercing', 'a.', 'Forcibly entering, or adapted to enter, at or by a point;\n   perforating; penetrating; keen; -- used also figuratively; as, a\n   piercing instrument, or thrust.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pierian', 'a.', 'Of or pertaining to Pierides or Muses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pierid', 'n.', 'Any butterfly of the genus Pieris and related genera. See\n   Cabbage butterfly, under Cabbage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pierides', 'n. pl.', 'The Muses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piet', 'n.', 'The dipper, or water ouzel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piet', 'n.', 'The magpie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pieta', 'n.', 'A representation of the dead Christ, attended by the Virgin\n   Mary or by holy women and angels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pietism', 'n.', 'The principle or practice of the Pietists.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pietism', 'n.', 'Strict devotion; also, affectation of devotion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pietist', 'n.', 'One of a class of religious reformers in Germany in the\n   17th century who sought to revive declining piety in the Protestant\n   churches; -- often applied as a term of reproach to those who make a\n   display of religious feeling. Also used adjectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pietistic', 'a.', 'Alt. of Pietistical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pietistical', 'a.', 'Of or pertaining to the Pietists; hence, in contempt,\n   affectedly or demonstratively religious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pietra dura', '', 'Hard and fine stones in general, such as are used for\n   inlay and the like, as distinguished from the softer stones used in\n   building; thus, a Florentine mosaic is a familiar instance of work in\n   pietra dura, though the ground may be soft marble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piety', 'n.', 'Veneration or reverence of the Supreme Being, and love of\n   his character; loving obedience to the will of God, and earnest\n   devotion to his service.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piety', 'n.', 'Duty; dutifulness; filial reverence and devotion;\n   affectionate reverence and service shown toward parents, relatives,\n   benefactors, country, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piewipe', 'n.', 'The lapwing, or pewit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piezometer', 'n.', 'An instrument for measuring the compressibility of\n   liquids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piezometer', 'n.', 'A gauge connected with a water main to show the\n   pressure at that point.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piffero', 'n.', 'Alt. of Piffara');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piffara', 'n.', 'A fife; also, a rude kind of oboe or a bagpipe with an\n   inflated skin for reservoir.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pig', 'n.', 'A piggin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pig', 'n.', 'The young of swine, male or female; also, any swine; a hog.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pig', 'n.', 'Any wild species of the genus Sus and related genera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pig', 'n.', 'An oblong mass of cast iron, lead, or other metal. See Mine\n   pig, under Mine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pig', 'n.', 'One who is hoggish; a greedy person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pigged', 'imp. & p. p.', 'of Pig');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pigging', 'p. pr. & vb. n.', 'of Pig');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pig', 'v. t. & i.', 'To bring forth (pigs); to bring forth in the manner of\n   pigs; to farrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pig', 'v. t. & i.', 'To huddle or lie together like pigs, in one bed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pigeon', 'n.', 'Any bird of the order Columbae, of which numerous species\n   occur in nearly all parts of the world.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pigeon', 'n.', 'An unsuspected victim of sharpers; a gull.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pigeon', 'v. t.', 'To pluck; to fleece; to swindle by tricks in gambling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pigeon-breasted', 'a.', 'Having a breast like a pigeon, -- the sternum\n   being so prominent as to constitute a deformity; chicken-breasted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pigeonfoot', 'n.', 'The doves-foot geranium (Geranium molle).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pigeon-hearted', 'a.', 'Timid; easily frightened; chicken-hearted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pigeonhole', 'n.', 'A small compartment in a desk or case for the keeping\n   of letters, documents, etc.; -- so called from the resemblance of a row\n   of them to the compartments in a dovecote.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pigeonhole', 'v. t.', 'To place in the pigeonhole of a case or cabinet;\n   hence, to put away; to lay aside indefinitely; as, to pigeonhole a\n   letter or a report.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pigeon-livered', 'a.', 'Pigeon-hearted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pigeonry', 'n.', 'A place for pigeons; a dovecote.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pigeontoed', 'a.', 'Having the toes turned in.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pig-eyed', 'a.', 'Having small, deep-set eyes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pigfish', 'n.', 'Any one of several species of salt-water grunts; -- called\n   also hogfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pigfish', 'n.', 'A sculpin. The name is also applied locally to several\n   other fishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pigfoot', 'n.', 'A marine fish (Scorpaena porcus), native of Europe. It is\n   reddish brown, mottled with dark brown and black.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pigg', 'n.', 'A piggin. See 1st Pig.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piggeries', 'pl. ', 'of Piggery');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piggery', 'n.', 'A place where swine are kept.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piggin', 'n.', 'A small wooden pail or tub with an upright stave for a\n   handle, -- often used as a dipper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piggish', 'a.', 'Relating to, or like, a pig; greedy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pig-headed', 'a.', 'Having a head like a pig; hence, figuratively:\n   stupidity obstinate; perverse; stubborn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pight', 'imp. & p. p.', 'Pitched; fixed; determined.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pightel', 'n.', 'A small inclosure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pig-jawed', 'a.', 'Having the upper jaw projecting beyond the lower, with\n   the upper incisors in advance of the lower; -- said of dogs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pigmean', 'a.', 'See Pygmean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pigment', 'n.', 'Any material from which a dye, a paint, or the like, may\n   be prepared; particularly, the refined and purified coloring matter\n   ready for mixing with an appropriate vehicle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pigment', 'n.', 'Any one of the colored substances found in animal and\n   vegetable tissues and fluids, as bilirubin, urobilin, chlorophyll, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pigment', 'n.', 'Wine flavored with species and honey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pigmental', 'a.', 'Alt. of Pigmentary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pigmentary', 'a.', 'Of or pertaining to pigments; furnished with pigments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pigmentation', 'n.', 'A deposition, esp. an excessive deposition, of\n   coloring matter; as, pigmentation of the liver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pigmented', 'a.', 'Colored; specifically (Biol.), filled or imbued with\n   pigment; as, pigmented epithelial cells; pigmented granules.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pigmentous', 'a.', 'Pigmental.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pigmy', 'n.', 'See Pygmy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pignerate', 'v. t.', 'To pledge or pawn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pignerate', 'v. t.', 'to receive in pawn, as a pawnbroker does.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pignoration', 'n.', 'The act of pledging or pawning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pignoration', 'n.', 'The taking of cattle doing damage, by way of pledge,\n   till satisfaction is made.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pignorative', 'a.', 'Pledging, pawning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pignora', 'pl. ', 'of Pignus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pignus', 'n.', 'A pledge or pawn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pignut', 'n.', 'See Groundnut (d).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pignut', 'n.', 'The bitter-flavored nut of a species of hickory (Carya\n   glabra, / porcina); also, the tree itself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pigpen', 'n.', 'A pen, or sty, for pigs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pigskin', 'n.', 'The skin of a pig, -- used chiefly for making saddles;\n   hence, a colloquial or slang term for a saddle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pigsney', 'n.', 'A word of endearment for a girl or woman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pig-sticking', 'n.', 'Boar hunting; -- so called by Anglo-Indians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pigsties', 'pl. ', 'of Pigsty');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pigsty', 'n.', 'A pigpen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pigtail', 'n.', 'The tail of a pig.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pigtail', 'n.', 'A cue, or queue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pigtail', 'n.', 'A kind of twisted chewing tobacco.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pigtailed', 'a.', 'Having a tail like a pigs; as, the pigtailed baboon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pigweed', 'n.', 'A name of several annual weeds. See Goosefoot, and\n   Lambs-quarters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pigwidgeon', 'n.', 'A cant word for anything petty or small. It is used by\n   Drayton as the name of a fairy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pika', 'n.', 'Any one of several species of rodents of the genus Lagomys,\n   resembling small tailless rabbits. They inhabit the high mountains of\n   Asia and America. Called also calling hare, and crying hare. See Chief\n   hare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pike', 'n. & v.', 'A foot soldiers weapon, consisting of a long wooden\n   shaft or staff, with a pointed steel head. It is now superseded by the\n   bayonet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pike', 'n. & v.', 'A pointed head or spike; esp., one in the center of a\n   shield or target.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pike', 'n. & v.', 'A hayfork.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pike', 'n. & v.', 'A pick.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pike', 'n. & v.', 'A pointed or peaked hill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pike', 'n. & v.', 'A large haycock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pike', 'n. & v.', 'A turnpike; a toll bar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pike', 'sing. & pl.', 'A large fresh-water fish (Esox lucius), found in\n   Europe and America, highly valued as a food fish; -- called also\n   pickerel, gedd, luce, and jack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piked', 'a.', 'Furnished with a pike; ending in a point; peaked; pointed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pike-devant', 'n.', 'A pointed beard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pikelet', 'n.', 'Alt. of Pikelin');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pikelin', 'n.', 'A light, thin cake or muffin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pikeman', 'pl. ', 'of Pikeman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pikeman', 'n.', 'A soldier armed with a pike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pikeman', 'n.', 'A miner who works with a pick.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pikeman', 'n.', 'A keeper of a turnpike gate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pikestaff', 'n.', 'The staff, or shaft, of a pike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pikestaff', 'n.', 'A staff with a spike in the lower end, to guard against\n   slipping.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piketail', 'n.', 'See Pintail, 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pikrolite', 'n.', 'See Picrolite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pilage', 'n.', 'See Pelage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pilaster', 'n.', 'An upright architectural member right-angled in plan,\n   constructionally a pier (See Pier, 1 (b)), but architecturally\n   corresponding to a column, having capital, shaft, and base to agree\n   with those of the columns of the same order. In most cases the\n   projection from the wall is one third of its width, or less.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pilastered', 'a.', 'Furnished with pilasters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pilau', 'n.', 'See Pillau.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pilch', 'n.', 'A gown or case of skin, or one trimmed or lined with fur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pilchard', 'n.', 'A small European food fish (Clupea pilchardus) resembling\n   the herring, but thicker and rounder. It is sometimes taken in great\n   numbers on the coast of England.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pilcher', 'n.', 'A scabbard, as of a sword.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pilcher', 'n.', 'The pilchard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pilcrow', 'n.', 'a paragraph mark, /.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pile', 'n.', 'A hair; hence, the fiber of wool, cotton, and the like; also,\n   the nap when thick or heavy, as of carpeting and velvet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pile', 'n.', 'A covering of hair or fur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pile', 'n.', 'The head of an arrow or spear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pile', 'n.', 'A large stake, or piece of timber, pointed and driven into\n   the earth, as at the bottom of a river, or in a harbor where the ground\n   is soft, for the support of a building, a pier, or other\n   superstructure, or to form a cofferdam, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pile', 'n.', 'One of the ordinaries or subordinaries having the form of a\n   wedge, usually placed palewise, with the broadest end uppermost.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pile', 'v. t.', 'To drive piles into; to fill with piles; to strengthen\n   with piles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pile', 'n.', 'A mass of things heaped together; a heap; as, a pile of\n   stones; a pile of wood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pile', 'n.', 'A mass formed in layers; as, a pile of shot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pile', 'n.', 'A funeral pile; a pyre.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pile', 'n.', 'A large building, or mass of buildings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pile', 'n.', 'Same as Fagot, n., 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pile', 'n.', 'A vertical series of alternate disks of two dissimilar\n   metals, as copper and zinc, laid up with disks of cloth or paper\n   moistened with acid water between them, for producing a current of\n   electricity; -- commonly called Voltas pile, voltaic pile, or galvanic\n   pile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pile', 'n.', 'The reverse of a coin. See Reverse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piled', 'imp. & p. p.', 'of Pile');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piling', 'p. pr. & vb. n.', 'of Pile');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pile', 'v. t.', 'To lay or throw into a pile or heap; to heap up; to\n   collect into a mass; to accumulate; to amass; -- often with up; as, to\n   pile up wood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pile', 'v. t.', 'To cover with heaps; or in great abundance; to fill or\n   overfill; to load.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pileate', 'a.', 'Alt. of Pileated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pileated', 'a.', 'Having the form of a cap for the head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pileated', 'a.', 'Having a crest covering the pileus, or whole top of the\n   head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piled', 'a.', 'Having a pile or point; pointed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piled', 'a.', 'Having a pile or nap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piled', 'a.', 'Formed from a pile or fagot; as, piled iron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pileiform', 'a.', 'Having the form of a pileus or cap; pileate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pilement', 'n.', 'An accumulation; a heap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pilenta', 'pl. ', 'of Pilentum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pilentum', 'n.', 'An easy chariot or carriage, used by Roman ladies, and in\n   which the vessels, etc., for sacred rites were carried.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pilorhizae', 'pl. ', 'of Pileorhiza');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pileorhiza', 'n.', 'A cap of cells which covers the growing extremity of a\n   root; a rootcap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pileous', 'a.', 'Consisting of, or covered with, hair; hairy; pilose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piler', 'n.', 'One who places things in a pile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piles', 'n. pl.', 'The small, troublesome tumors or swellings about the\n   anus and lower part of the rectum which are technically called\n   hemorrhoids. See Hemorrhoids. [The singular pile is sometimes used.]');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pilei', 'pl. ', 'of Pileus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pileus', 'n.', 'A kind of skull cap of felt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pileus', 'n.', 'The expanded upper portion of many of the fungi. See\n   Mushroom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pileus', 'n.', 'The top of the head of a bird, from the bill to the nape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pileworm', 'n.', 'The teredo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pile-worn', 'a.', 'Having the pile worn off; threadbare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pilewort', 'n.', 'A plant (Ranunculus Ficaria of Linnaeus) whose tuberous\n   roots have been used in poultices as a specific for the piles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pilfered', 'imp. & p. p.', 'of Pilfer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pilfering', 'p. pr. & vb. n.', 'of Pilfer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pilfer', 'v. i.', 'To steal in small quantities, or articles of small\n   value; to practice petty theft.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pilfer', 'v. t.', 'To take by petty theft; to filch; to steal little by\n   little.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pilferer', 'n.', 'One who pilfers; a petty thief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pilfering', 'a.', 'Thieving in a small way.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pilfering', 'n.', 'Petty theft.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pilfery', 'n.', 'Petty theft.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pilgarlic', 'n.', 'One who has lost his hair by disease; a sneaking fellow,\n   or one who is hardly used.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pilgrim', 'n.', 'A wayfarer; a wanderer; a traveler; a stranger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pilgrim', 'n.', 'One who travels far, or in strange lands, to visit some\n   holy place or shrine as a devotee; as, a pilgrim to Loretto; Canterbury\n   pilgrims. See Palmer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pilgrim', 'a.', 'Of or pertaining to a pilgrim, or pilgrims; making\n   pilgrimages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pilgrim', 'v. i.', 'To journey; to wander; to ramble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pilgrimage', 'n.', 'The journey of a pilgrim; a long journey; especially, a\n   journey to a shrine or other sacred place. Fig., the journey of human\n   life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pilgrimage', 'n.', 'A tedious and wearisome time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pilgrimize', 'v. i.', 'To wander as a pilgrim; to go on a pilgrimage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pildia', 'pl. ', 'of Pilidium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pilidium', 'n.', 'The free-swimming, hat-shaped larva of certain nemertean\n   worms. It has no resemblance to its parent, and the young worm develops\n   in its interior.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pilifera', 'n. pl.', 'Same as Mammalia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piliferous', 'a.', 'Bearing a single slender bristle, or hair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piliferous', 'a.', 'Beset with hairs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piliform', 'a.', 'Resembling hairs or down.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piligerous', 'a.', 'Bearing hair; covered with hair or down; piliferous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piling', 'n.', 'The act of heaping up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piling', 'n.', 'The process of building up, heating, and working, fagots,\n   or piles, to form bars, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piling', 'n.', 'A series of piles; piles considered collectively; as, the\n   piling of a bridge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pill', 'n.', 'The peel or skin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pill', 'v. i.', 'To be peeled; to peel off in flakes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pill', 'v. t.', 'To deprive of hair; to make bald.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pill', 'v. t.', 'To peel; to make by removing the skin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pilled', 'imp. & p. p.', 'of Pill');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pilling', 'p. pr. & vb. n.', 'of Pill');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pill', 'v. t. & i.', 'To rob; to plunder; to pillage; to peel. See Peel, to\n   plunder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pill', 'n.', 'A medicine in the form of a little ball, or small round mass,\n   to be swallowed whole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pill', 'n.', 'Figuratively, something offensive or nauseous which must be\n   accepted or endured.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pillage', 'n.', 'The act of pillaging; robbery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pillage', 'n.', 'That which is taken from another or others by open force,\n   particularly and chiefly from enemies in war; plunder; spoil; booty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pillaged', 'imp. & p. p.', 'of Pillage');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pillaging', 'p. pr. & vb. n.', 'of Pillage');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pillage', 'v. i.', 'To strip of money or goods by open violence; to\n   plunder; to spoil; to lay waste; as, to pillage the camp of an enemy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pillage', 'v. i.', 'To take spoil; to plunder; to ravage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pillager', 'n.', 'One who pillages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pillar', 'n.', 'The general and popular term for a firm, upright, insulated\n   support for a superstructure; a pier, column, or post; also, a column\n   or shaft not supporting a superstructure, as one erected for a monument\n   or an ornament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pillar', 'n.', 'Figuratively, that which resembles such a pillar in\n   appearance, character, or office; a supporter or mainstay; as, the\n   Pillars of Hercules; a pillar of the state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pillar', 'n.', 'A portable ornamental column, formerly carried before a\n   cardinal, as emblematic of his support to the church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pillar', 'n.', 'The center of the volta, ring, or manege ground, around\n   which a horse turns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pillar', 'a.', 'Having a support in the form of a pillar, instead of legs;\n   as, a pillar drill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pillar-block', 'n.', 'See under Pillow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pillared', 'a.', 'Supported or ornamented by pillars; resembling a pillar,\n   or pillars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pillaret', 'n.', 'A little pillar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pillarist', 'n.', 'See Stylite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pillau', 'n.', 'An Oriental dish consisting of rice boiled with mutton,\n   fat, or butter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pilled', 'a.', 'Stripped of hair; scant of hair; bald.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pilled-garlic', 'n.', 'See Pilgarlic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piller', 'n.', 'One who pills or plunders.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pilleries', 'pl. ', 'of Pillery');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pillery', 'n.', 'Plunder; pillage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pillion', 'n.', 'A panel or cushion saddle; the under pad or cushion of\n   saddle; esp., a pad or cushion put on behind a mans saddle, on which a\n   woman may ride.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pillorize', 'v. t.', 'To set in, or punish with, the pillory; to pillory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pillories', 'pl. ', 'of Pillory');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pillory', 'n.', 'A frame of adjustable boards erected on a post, and having\n   holes through which the head and hands of an offender were thrust so as\n   to be exposed in front of it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pilloried', 'imp. & p. p.', 'of Pillory');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pillorying', 'p. pr. & vb. n.', 'of Pillory');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pillory', 'v. t.', 'To set in, or punish with, the pillory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pillory', 'v. t.', 'Figuratively, to expose to public scorn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pillow', 'n.', 'Anything used to support the head of a person when\n   reposing; especially, a sack or case filled with feathers, down, hair,\n   or other soft material.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pillow', 'n.', 'A piece of metal or wood, forming a support to equalize\n   pressure; a brass; a pillow block.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pillow', 'n.', 'A block under the inner end of a bowsprit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pillow', 'n.', 'A kind of plain, coarse fustian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pillowed', 'imp. & p. p.', 'of Pillow');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pillowing', 'p. pr. & vb. n.', 'of Pillow');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pillow', 'v. t.', 'To rest or lay upon, or as upon, a pillow; to support;\n   as, to pillow the head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pillowcase', 'n.', 'A removable case or covering for a pillow, usually of\n   white linen or cotton cloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pillowed', 'a.', 'Provided with a pillow or pillows; having the head\n   resting on, or as on, a pillow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pillowy', 'a.', 'Like a pillow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pill-willet', 'n.', 'The willet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pillworm', 'n.', 'Any myriapod of the genus Iulus and allied genera which\n   rolls up spirally; a galleyworm. See Illust. under Myriapod.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pillwort', 'n.', 'Any plant of the genus Pilularia; minute aquatic\n   cryptograms, with small pill-shaped fruit; -- sometimes called\n   peppergrass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pilocarpine', 'n.', 'An alkaloid extracted from jaborandi (Pilocarpus\n   pennatifolius) as a white amorphous or crystalline substance which has\n   a peculiar effect on the vasomotor system.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pilose', 'a.', 'Hairy; full of, or made of, hair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pilose', 'a.', 'Clothed thickly with pile or soft down.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pilose', 'a.', 'Covered with long, slender hairs; resembling long hairs;\n   hairy; as, pilose pubescence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pilosity', 'n.', 'The quality or state of being pilose; hairiness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pilot', 'n.', 'One employed to steer a vessel; a helmsman; a steersman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pilot', 'n.', 'Specifically, a person duly qualified, and licensed by\n   authority, to conduct vessels into and out of a port, or in certain\n   waters, for a fixed rate of fees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pilot', 'n.', 'Figuratively: A guide; a director of another through a\n   difficult or unknown course.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pilot', 'n.', 'An instrument for detecting the compass error.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pilot', 'n.', 'The cowcatcher of a locomotive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piloted', 'imp. & p. p.', 'of Pilot');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piloting', 'p. pr. & vb. n.', 'of Pilot');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pilot', 'v. t.', 'To direct the course of, as of a ship, where navigation\n   is dangerous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pilot', 'v. t.', 'Figuratively: To guide, as through dangers or\n   difficulties.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pilotage', 'n.', 'The pilots skill or knowledge, as of coasts, rocks,\n   bars, and channels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pilotage', 'n.', 'The compensation made or allowed to a pilot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pilotage', 'n.', 'Guidance, as by a pilot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pilotism', 'n.', 'Alt. of Pilotry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pilotry', 'n.', 'Pilotage; skill in the duties of a pilot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pilour', 'n.', 'A piller; a plunderer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pilous', 'a.', 'See Pilose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pilser', 'n.', 'An insect that flies into a flame.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pilular', 'a.', 'Of or pertaining to pills; resembling a pill or pills; as,\n   a pilular mass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pilulous', 'a.', 'Like a pill; small; insignificant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pilwe', 'n.', 'A pillow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pily', 'a.', 'Like pile or wool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pimaric', 'a.', 'Pertaining to, or designating, an acid found in galipot,\n   and isomeric with abietic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pimelic', 'a.', 'Pertaining to, or designating, a substance obtained from\n   certain fatty substances, and subsequently shown to be a mixture of\n   suberic and adipic acids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pimelic', 'a.', 'Designating the acid proper (C5H10(CO2/H)2) which is\n   obtained from camphoric acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pimelite', 'n.', 'An apple-green mineral having a greasy feel. It is a\n   hydrous silicate of nickel, magnesia, aluminia, and iron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piment', 'n.', 'Wine flavored with spice or honey. See Pigment, 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pimenta', 'n.', 'Same as Pimento.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pimento', 'n.', 'Allspice; -- applied both to the tree and its fruit. See\n   Allspice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pimlico', 'n.', 'The friar bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pimp', 'n.', 'One who provides gratification for the lust of others; a\n   procurer; a pander.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pimped', 'imp. & p. p.', 'of Pimp');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pimping', 'p. pr. & vb. n.', 'of Pimp');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pimp', 'v. i.', 'To procure women for the gratification of others lusts;\n   to pander.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pimpernel', 'n.', 'A plant of the genus Anagallis, of which one species (A.\n   arvensis) has small flowers, usually scarlet, but sometimes purple,\n   blue, or white, which speedily close at the approach of bad weather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pimpillo', 'n.', 'A West Indian name for the prickly pear (Opuntia); --\n   called also pimploes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pimpinel', 'n.', 'The burnet saxifrage. See under Saxifrage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pimping', 'a.', 'Little; petty; pitiful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pimping', 'a.', 'Puny; sickly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pimple', 'n.', 'Any small acuminated elevation of the cuticle, whether\n   going on to suppuration or not.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pimple', 'n.', 'Fig.: A swelling or protuberance like a pimple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pimpled', 'a.', 'Having pimples.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pimply', 'a.', 'Pimpled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pimpship', 'n.', 'The office, occupation, or persom of a pimp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pin', 'v. t.', 'To peen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pin', 'v. t.', 'To inclose; to confine; to pen; to pound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pin', 'n.', 'A piece of wood, metal, etc., generally cylindrical, used for\n   fastening separate articles together, or as a support by which one\n   article may be suspended from another; a peg; a bolt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pin', 'n.', 'Especially, a small, pointed and headed piece of brass or\n   other wire (commonly tinned), largely used for fastening clothes,\n   attaching papers, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pin', 'n.', 'Hence, a thing of small value; a trifle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pin', 'n.', 'That which resembles a pin in its form or use');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pin', 'n.', 'A peg in musical instruments, for increasing or relaxing the\n   tension of the strings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pin', 'n.', 'A linchpin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pin', 'n.', 'A rolling-pin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pin', 'n.', 'A clothespin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pin', 'n.', 'A short shaft, sometimes forming a bolt, a part of which\n   serves as a journal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pin', 'n.', 'The tenon of a dovetail joint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pin', 'n.', 'One of a row of pegs in the side of an ancient drinking cup to\n   mark how much each man should drink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pin', 'n.', 'The bulls eye, or center, of a target; hence, the center.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pin', 'n.', 'Mood; humor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pin', 'n.', 'Caligo. See Caligo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pin', 'n.', 'An ornament, as a brooch or badge, fastened to the clothing by\n   a pin; as, a Masonic pin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pin', 'n.', 'The leg; as, to knock one off his pins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinned', 'imp. & p. p.', 'of Pin');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinning', 'p. pr. & vb. n.', 'of Pin');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pin', 'n.', 'To fasten with, or as with, a pin; to join; as, to pin a\n   garment; to pin boards together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pia cloth', '', 'A fine material for ladies shawls, scarfs, handkerchiefs,\n   etc., made from the fiber of the pineapple leaf, and perhaps from other\n   fibrous tropical leaves. It is delicate, soft, and transparent, with a\n   slight tinge of pale yellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinacoid', 'n.', 'A plane parallel to two of the crystalline axes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinacolin', 'n.', 'A colorless oily liquid related to the ketones, and\n   obtained by the decomposition of pinacone; hence, by extension, any one\n   of the series of which pinacolin proper is the type.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinacone', 'n.', 'A white crystalline substance related to the glycols, and\n   made from acetone; hence, by extension, any one of a series of\n   substances of which pinacone proper is the type.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinacotheca', 'n.', 'A picture gallery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinafore', 'n.', 'An apron for a child to protect the front part of dress;\n   a tier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinakothek', 'n.', 'Pinacotheca.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinaster', 'n.', 'A species of pine (Pinus Pinaster) growing in Southern\n   Europe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinaces', 'pl. ', 'of Pinax');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinax', 'n.', 'A tablet; a register; hence, a list or scheme inscribed on a\n   tablet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pince-nez', 'n.', 'Eyeglasses kept on the nose by a spring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pincers', 'n. pl.', 'See Pinchers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinched', 'imp. & p. p.', 'of Pinch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinching', 'p. pr. & vb. n.', 'of Pinch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinch', 'v. t.', 'To press hard or squeeze between the ends of the fingers,\n   between teeth or claws, or between the jaws of an instrument; to\n   squeeze or compress, as between any two hard bodies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinch', 'v. t.', 'o seize; to grip; to bite; -- said of animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinch', 'v. t.', 'To plait.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinch', 'v. t.', 'Figuratively: To cramp; to straiten; to oppress; to\n   starve; to distress; as, to be pinched for money.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinch', 'v. t.', 'To move, as a railroad car, by prying the wheels with a\n   pinch. See Pinch, n., 4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinch', 'v. i.', 'To act with pressing force; to compress; to squeeze; as,\n   the shoe pinches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinch', 'v. i.', 'To take hold; to grip, as a dog does.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinch', 'v. i.', 'To spare; to be niggardly; to be covetous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinch', 'n.', 'A close compression, as with the ends of the fingers, or\n   with an instrument; a nip.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinch', 'n.', 'As much as may be taken between the finger and thumb; any\n   very small quantity; as, a pinch of snuff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinch', 'n.', 'Pian; pang.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinch', 'n.', 'A lever having a projection at one end, acting as a fulcrum,\n   -- used chiefly to roll heavy wheels, etc. Called also pinch bar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinchbeck', 'n.', 'An alloy of copper and zinc, resembling gold; a yellow\n   metal, composed of about three ounces of zinc to a pound of copper. It\n   is much used as an imitation of gold in the manufacture of cheap\n   jewelry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinchbeck', 'a.', 'Made of pinchbeck; sham; cheap; spurious; unreal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinchcock', 'n.', 'A clamp on a flexible pipe to regulate the flow of a\n   fluid through the pipe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinchem', 'n.', 'The European blue titmouse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pincher', 'n.', 'One who, or that which, pinches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinchers', 'n. pl.', 'An instrument having two handles and two grasping\n   jaws working on a pivot; -- used for griping things to be held fast,\n   drawing nails, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinchfist', 'n.', 'A closefisted person; a miser.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinching', 'a.', 'Compressing; nipping; griping; niggardly; as, pinching\n   cold; a pinching parsimony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinchingly', 'adv.', 'In a pinching way.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinchpenny', 'n.', 'A miserly person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pincoffin', 'n.', 'A commercial preparation of garancin, yielding fine\n   violet tints.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pincpinc', 'n.', 'An African wren warbler. (Drymoica textrix).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pincushion', 'n.', 'A small cushion, in which pins may be stuck for use.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pindal', 'n.', 'Alt. of Pindar');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pindar', 'n.', 'The peanut (Arachis hypogaea); -- so called in the West\n   Indies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pindaric', 'a.', 'Of or pertaining to Pindar, the Greek lyric poet; after\n   the style and manner of Pindar; as, Pindaric odes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pindaric', 'n.', 'A Pindaric ode.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pindarical', 'a.', 'Pindaric.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pindarism', 'n.', 'Imitation of Pindar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pindarist', 'n.', 'One who imitates Pindar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinder', 'n.', 'One who impounds; a poundkeeper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pine', 'n.', 'Woe; torment; pain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pined', 'imp. & p. p.', 'of Pine');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pining', 'p. pr. & vb. n.', 'of Pine');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pine', 'v.', 'To inflict pain upon; to torment; to torture; to afflict.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pine', 'v.', 'To grieve or mourn for.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pine', 'v. i.', 'To suffer; to be afflicted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pine', 'v. i.', 'To languish; to lose flesh or wear away, under any\n   distress or anexiety of mind; to droop; -- often used with away.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pine', 'v. i.', 'To languish with desire; to waste away with longing for\n   something; -- usually followed by for.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pine', 'n.', 'Any tree of the coniferous genus Pinus. See Pinus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pine', 'n.', 'The wood of the pine tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pine', 'n.', 'A pineapple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pineal', 'a.', 'Of or pertaining to a pine cone; resembling a pine cone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pineapple', 'n.', 'A tropical plant (Ananassa sativa); also, its fruit; --\n   so called from the resemblance of the latter, in shape and external\n   appearance, to the cone of the pine tree. Its origin is unknown, though\n   conjectured to be American.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pineaster', 'n.', 'See Pinaster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pine-clad', 'a.', 'Alt. of Pine-crowned');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pine-crowned', 'a.', 'Clad or crowned with pine trees; as, pine-clad hills.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinedrops', 'n.', 'A reddish herb (Pterospora andromedea) of the United\n   States, found parasitic on the roots of pine trees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinefinch', 'n.', 'A small American bird (Spinus, / Chrysomitris, spinus);\n   -- called also pine siskin, and American siskin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinefinch', 'n.', 'The pine grosbeak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinenchyma', 'n.', 'Tabular parenchyma, a form of cellular tissue in which\n   the cells are broad and flat, as in some kinds of epidermis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pineries', 'pl. ', 'of Pinery');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinery', 'n.', 'A pine forest; a grove of pines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinery', 'n.', 'A hothouse in which pineapples are grown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinesap', 'n.', 'A reddish fleshy herb of the genus Monotropa (M.\n   hypopitys), formerly thought to be parasitic on the roots of pine\n   trees, but more probably saprophytic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinetum', 'n.', 'A plantation of pine trees; esp., a collection of living\n   pine trees made for ornamental or scientific purposes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pineweed', 'n.', 'A low, bushy, nearly leafless herb (Hypericum Sarothra),\n   common in sandy soil in the Eastern United States.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piney', 'a.', 'See Piny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piney', 'a.', 'A term used in designating an East Indian tree (the Vateria\n   Indica or piney tree, of the order Dipterocarpeae, which grows in\n   Malabar, etc.) or its products.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pin-eyed', 'a.', 'Having the stigma visible at the throad of a gamopetalous\n   corolla, while the stamens are concealed in the tube; -- said of\n   dimorphous flowers. The opposite of thrum-eyed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinfeather', 'n.', 'A feather not fully developed; esp., a rudimentary\n   feather just emerging through the skin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinfeathered', 'a.', 'Having part, or all, of the feathers imperfectly\n   developed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinfish', 'n.', 'The sailors choice (Diplodus, / Lagodon, rhomboides).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinfish', 'n.', 'The salt-water bream (Diplodus Holbrooki).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinfold', 'n.', 'A place in which stray cattle or domestic animals are\n   confined; a pound; a penfold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ping', 'n.', 'The sound made by a bullet in striking a solid object or in\n   passing through the air.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinged', 'imp. & p. p.', 'of Ping');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinging', 'p. pr. & vb. n.', 'of Ping');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ping', 'v. i.', 'To make the sound called ping.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pingle', 'n.', 'A small piece of inclosed ground.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pingster', 'n.', 'See Pinkster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinguicula', 'n.', 'See Butterwort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinguid', 'a.', 'Fat; unctuous; greasy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinguidinous', 'a.', 'Containing fat; fatty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinguitude', 'n.', 'Fatness; a growing fat; obesity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinhold', 'n.', 'A place where a pin is fixed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinic', 'a.', 'Of or pertaining to the pine; obtained from the pine;\n   formerly, designating an acid which is the chief constituent of common\n   resin, -- now called abietic, or sylvic, acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pining', 'a.', 'Languishing; drooping; wasting away, as with longing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pining', 'a.', 'Wasting; consuming.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piningly', 'adv.', 'In a pining manner; droopingly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinion', 'n.', 'A moth of the genus Lithophane, as L. antennata, whose\n   larva bores large holes in young peaches and apples.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinion', 'n.', 'A feather; a quill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinion', 'n.', 'A wing, literal or figurative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinion', 'n.', 'The joint of birds wing most remote from the body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinion', 'n.', 'A fetter for the arm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinion', 'n.', 'A cogwheel with a small number of teeth, or leaves, adapted\n   to engage with a larger wheel, or rack (see Rack); esp., such a wheel\n   having its leaves formed of the substance of the arbor or spindle which\n   is its axis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinioned', 'imp. & p. p.', 'of Pinion');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinioning', 'p. pr. & vb. n.', 'of Pinion');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinion', 'v. t.', 'To bind or confine the wings of; to confine by binding\n   the wings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinion', 'v. t.', 'To disable by cutting off the pinion joint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinion', 'v. t.', 'To disable or restrain, as a person, by binding the\n   arms, esp. by binding the arms to the body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinion', 'v. t.', 'Hence, generally, to confine; to bind; to tie up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinioned', 'a.', 'Having wings or pinions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinionist', 'n.', 'Any winged creature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinite', 'n.', 'A compact granular cryptocrystalline mineral of a dull\n   grayish or greenish white color. It is a hydrous alkaline silicate, and\n   is derived from the alteration of other minerals, as iolite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinite', 'n.', 'Any fossil wood which exhibits traces of having belonged to\n   the Pine family.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinite', 'n.', 'A sweet white crystalline substance extracted from the gum\n   of a species of pine (Pinus Lambertina). It is isomeric with, and\n   resembles, quercite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pink', 'n.', 'A vessel with a very narrow stern; -- called also pinky.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pink', 'v. i.', 'To wink; to blink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pink', 'a.', 'Half-shut; winking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinked', 'imp. & p. p.', 'of Pink');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinking', 'p. pr. & vb. n.', 'of Pink');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pink', 'v. t.', 'To pierce with small holes; to cut the edge of, as cloth\n   or paper, in small scallops or angles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pink', 'v. t.', 'To stab; to pierce as with a sword.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pink', 'v. t.', 'To choose; to cull; to pick out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pink', 'n.', 'A stab.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pink', 'v. t.', 'A name given to several plants of the caryophyllaceous\n   genus Dianthus, and to their flowers, which are sometimes very fragrant\n   and often double in cultivated varieties. The species are mostly\n   perennial herbs, with opposite linear leaves, and handsome five-petaled\n   flowers with a tubular calyx.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pink', 'v. t.', 'A color resulting from the combination of a pure vivid red\n   with more or less white; -- so called from the common color of the\n   flower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pink', 'v. t.', 'Anything supremely excellent; the embodiment or perfection\n   of something.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pink', 'v. t.', 'The European minnow; -- so called from the color of its\n   abdomen in summer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pink', 'a.', 'Resembling the garden pink in color; of the color called pink\n   (see 6th Pink, 2); as, a pink dress; pink ribbons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinked', 'a.', 'Pierced with small holes; worked in eyelets; scalloped on\n   the edge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pink-eyed', 'a.', 'Having small eyes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinking', 'n.', 'The act of piercing or stabbing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinking', 'n.', 'The act or method of decorating fabrics or garments with a\n   pinking iron; also, the style of decoration; scallops made with a\n   pinking iron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinkish', 'a.', 'Somewhat pink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinkness', 'n.', 'Quality or state of being pink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinkroot', 'n.', 'The root of Spigelia Marilandica, used as a powerful\n   vermifuge; also, that of S. Anthelmia. See definition 2 (below).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinkroot', 'n.', 'A perennial North American herb (Spigelia Marilandica),\n   sometimes cultivated for its showy red blossoms. Called also Carolina\n   pink, Maryland pinkroot, and worm grass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinkroot', 'n.', 'An annual South American and West Indian plant (Spigelia\n   Anthelmia).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinkster', 'n.', 'Whitsuntide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pink stern', '', 'See Chebacco, and 1st Pink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pink-sterned', 'a.', 'Having a very narrow stern; -- said of a vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinky', 'n.', 'See 1st Pink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinnae', 'pl. ', 'of Pinna');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinnas', 'pl. ', 'of Pinna');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinna', 'n.', 'A leaflet of a pinnate leaf. See Illust. of Bipinnate leaf,\n   under Bipinnate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinna', 'n.', 'One of the primary divisions of a decompound leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinna', 'n.', 'One of the divisions of a pinnate part or organ.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinna', 'n.', 'Any species of Pinna, a genus of large bivalve mollusks\n   found in all warm seas. The byssus consists of a large number of long,\n   silky fibers, which have been used in manufacturing woven fabrics, as a\n   curiosity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinna', 'n.', 'The auricle of the ear. See Ear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinnace', 'n.', 'A small vessel propelled by sails or oars, formerly\n   employed as a tender, or for coast defence; -- called originally,\n   spynace or spyne.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinnace', 'n.', 'A man-of-wars boat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinnace', 'n.', 'A procuress; a pimp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinnacle', 'n.', 'An architectural member, upright, and generally ending in\n   a small spire, -- used to finish a buttress, to constitute a part in a\n   proportion, as where pinnacles flank a gable or spire, and the like.\n   Pinnacles may be considered primarily as added weight, where it is\n   necessary to resist the thrust of an arch, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinnacle', 'n.', 'Anything resembling a pinnacle; a lofty peak; a pointed\n   summit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinnacled', 'imp. & p. p.', 'of Pinnacle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinnacling', 'p. pr. & vb. n.', 'of Pinnacle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinnacle', 'v. t.', 'To build or furnish with a pinnacle or pinnacles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinnage', 'n.', 'Poundage of cattle. See Pound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinnate', 'a.', 'Alt. of Pinnated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinnated', 'a.', 'Consisting of several leaflets, or separate portions,\n   arranged on each side of a common petiole, as the leaves of a rosebush,\n   a hickory, or an ash. See Abruptly pinnate, and Illust., under\n   Abruptly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinnated', 'a.', 'Having a winglike tuft of long feathers on each side of\n   the neck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinnately', 'adv.', 'In a pinnate manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinnatifid', 'a.', 'Divided in a pinnate manner, with the divisions not\n   reaching to the midrib.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinnatilobate', 'a.', 'Having lobes arranged in a pinnate manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinnatiped', 'a.', 'Having the toes bordered by membranes; fin-footed, as\n   certain birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinnatiped', 'n.', 'Any bird which has the toes bordered by membranes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinner', 'n.', 'One who, or that which, pins or fastens, as with pins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinner', 'n.', 'A headdress like a cap, with long lappets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinner', 'n.', 'An apron with a bib; a pinafore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinner', 'n.', 'A cloth band for a gown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinner', 'n.', 'A pin maker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinner', 'n.', 'One who pins or impounds cattle. See Pin, v. t.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinnet', 'n.', 'A pinnacle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinniform', 'a.', 'Shaped like a fin or feather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinnigrada', 'n. pl.', 'Same as Pinnipedia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinnigrade', 'n.', 'An animal of the seal tribe, moving by short feet that\n   serve as paddles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinniped', 'n.', 'One of the Pinnipedia; a seal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinniped', 'n.', 'One of the Pinnipedes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinnipedes', 'n. pl.', 'Same as Steganopodes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinnipedia', 'n. pl.', 'A suborder of aquatic carnivorous mammals including\n   the seals and walruses; -- opposed to Fissipedia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinnock', 'n.', 'The hedge sparrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinnock', 'n.', 'The tomtit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinnothere', 'n.', 'A crab of the genus pinnotheres. See Oyster crab, under\n   Oyster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinnulae', 'pl. ', 'of Pinnula');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinnula', 'n.', 'Same as Pinnule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinnulate', 'a.', 'Having each pinna subdivided; -- said of a leaf, or of\n   its pinnae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinnulated', 'a.', 'Having pinnules.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinnule', 'n.', 'One of the small divisions of a decompound frond or leaf.\n   See Illust. of Bipinnate leaf, under Bipinnate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinnule', 'n.', 'Any one of a series of small, slender organs, or parts,\n   when arranged in rows so as to have a plumelike appearance; as, a\n   pinnule of a gorgonia; the pinnules of a crinoid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinnywinkles', 'n. pl.', 'An instrument of torture, consisting of a board\n   with holes into which the fingers were pressed, and fastened with pegs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinocle', 'n.', 'See Penuchle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinole', 'n.', 'An aromatic powder used in Italy in the manufacture of\n   chocolate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinole', 'n.', 'Parched maize, ground, and mixed with sugar, etc. Mixed\n   with water, it makes a nutritious beverage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pion', 'n.', 'The edible seed of several species of pine; also, the tree\n   producing such seeds, as Pinus Pinea of Southern Europe, and P.\n   Parryana, cembroides, edulis, and monophylla, the nut pines of Western\n   North America.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pion', 'n.', 'See Monkeys puzzle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinpatch', 'n.', 'The common English periwinkle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pint', 'n.', 'A measure of capacity, equal to half a quart, or four gills,\n   -- used in liquid and dry measures. See Quart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pint', 'n.', 'The laughing gull.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pintados', 'pl. ', 'of Pintado');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pintado', 'n.', 'Any bird of the genus Numida. Several species are found in\n   Africa. The common pintado, or Guinea fowl, the helmeted, and the\n   crested pintados, are the best known. See Guinea fowl, under Guinea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pintail', 'n.', 'A northern duck (Dafila acuta), native of both continents.\n   The adult male has a long, tapering tail. Called also gray duck,\n   piketail, piket-tail, spike-tail, split-tail, springtail, sea pheasant,\n   and gray widgeon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pintail', 'n.', 'The sharp-tailed grouse of the great plains and Rocky\n   Mountains (Pediocaetes phasianellus); -- called also pintailed grouse,\n   pintailed chicken, springtail, and sharptail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pin-tailed', 'a.', 'Having a tapered tail, with the middle feathers\n   longest; -- said of birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pintle', 'n.', 'A little pin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pintle', 'n.', 'An upright pivot pin');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pintle', 'n.', 'The pivot pin of a hinge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pintle', 'n.', 'A hook or pin on which a rudder hangs and turns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pintle', 'n.', 'A pivot about which the chassis swings, in some kinds of\n   gun carriages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pintle', 'n.', 'A kingbolt of a wagon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pintos', 'n. pl.', 'A mountain tribe of Mexican Indians living near\n   Acapulco. They are remarkable for having the dark skin of the face\n   irregularly spotted with white. Called also speckled Indians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinule', 'n.', 'One of the sights of an astrolabe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinus', 'n.', 'A large genus of evergreen coniferous trees, mostly found in\n   the northern hemisphere. The genus formerly included the firs, spruces,\n   larches, and hemlocks, but is now limited to those trees which have the\n   primary leaves of the branchlets reduced to mere scales, and the\n   secondary ones (pine needles) acicular, and usually in fascicles of two\n   to seven. See Pine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinweed', 'n.', 'Any plant of the genus Lechea, low North American herbs\n   with branching stems, and very small and abundant leaves and flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinworm', 'n.', 'A small nematoid worm (Oxyurus vermicularis), which is\n   parasitic chiefly in the rectum of man. It is most common in children\n   and aged persons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinxit', '', 'A word appended to the artists name or initials on a\n   painting, or engraved copy of a painting; as, Rubens pinxit, Rubens\n   painted (this).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pinxter', 'n.', 'See Pinkster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piny', 'a.', 'Abounding with pines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pioned', 'a.', 'A Shakespearean word of disputed meaning; perh., \"abounding\n   in marsh marigolds.\"');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pioneer', 'n.', 'A soldier detailed or employed to form roads, dig\n   trenches, and make bridges, as an army advances.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pioneer', 'n.', 'One who goes before, as into the wilderness, preparing the\n   way for others to follow; as, pioneers of civilization; pioneers of\n   reform.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pioneered', 'imp. & p. p.', 'of Pioneer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pioneering', 'p. pr. & vb. n.', 'of Pioneer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pioneer', 'v. t. & i.', 'To go before, and prepare or open a way for; to\n   act as pioneer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pioner', 'n.', 'A pioneer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piony', 'n.', 'See Peony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piot', 'n.', 'The magpie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pious', 'a.', 'Of or pertaining to piety; exhibiting piety; reverential;\n   dutiful; religious; devout; godly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pious', 'a.', 'Practiced under the pretext of religion; prompted by\n   mistaken piety; as, pious errors; pious frauds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piously', 'adv.', 'In a pious manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pip', 'n.', 'A contagious disease of fowls, characterized by hoarseness,\n   discharge from the nostrils and eyes, and an accumulation of mucus in\n   the mouth, forming a \"scale\" on the tongue. By some the term pip is\n   restricted to this last symptom, the disease being called roup by them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pip', 'n.', 'A seed, as of an apple or orange.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pip', 'n.', 'One of the conventional figures or \"spots\" on playing cards,\n   dominoes, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pipped', 'imp. & p. p.', 'of Pip');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pipping', 'p. pr. & vb. n.', 'of Pip');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pip', 'v. i.', 'To cry or chirp, as a chicken; to peep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pipas', 'pl. ', 'of Pipa');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pipa', 'n.', 'The Surinam toad (Pipa Americana), noted for its peculiar\n   breeding habits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pipage', 'n.', 'Transportation, as of petroleum oil, by means of a pipe\n   conduit; also, the charge for such transportation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pipal tree', '', 'Same as Peepul tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pipe', 'n.', 'A wind instrument of music, consisting of a tube or tubes of\n   straw, reed, wood, or metal; any tube which produces musical sounds;\n   as, a shepherds pipe; the pipe of an organ.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pipe', 'n.', 'Any long tube or hollow body of wood, metal, earthenware, or\n   the like: especially, one used as a conductor of water, steam, gas,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pipe', 'n.', 'A small bowl with a hollow steam, -- used in smoking tobacco,\n   and, sometimes, other substances.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pipe', 'n.', 'A passageway for the air in speaking and breathing; the\n   windpipe, or one of its divisions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pipe', 'n.', 'The key or sound of the voice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pipe', 'n.', 'The peeping whistle, call, or note of a bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pipe', 'n.', 'The bagpipe; as, the pipes of Lucknow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pipe', 'n.', 'An elongated body or vein of ore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pipe', 'n.', 'A roll formerly used in the English exchequer, otherwise\n   called the Great Roll, on which were taken down the accounts of debts\n   to the king; -- so called because put together like a pipe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pipe', 'n.', 'A boatswains whistle, used to call the crew to their duties;\n   also, the sound of it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pipe', 'n.', 'A cask usually containing two hogsheads, or 126 wine gallons;\n   also, the quantity which it contains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pipe', 'v. i.', 'To play on a pipe, fife, flute, or other tubular wind\n   instrument of music.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pipe', 'v. i.', 'To call, convey orders, etc., by means of signals on a\n   pipe or whistle carried by a boatswain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pipe', 'v. i.', 'To emit or have a shrill sound like that of a pipe; to\n   whistle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pipe', 'v. i.', 'To become hollow in the process of solodifying; -- said of\n   an ingot, as of steel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piped', 'imp. & p. p.', 'of Pipe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piping', 'p. pr. & vb. n.', 'of Pipe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pipe', 'v. t.', 'To perform, as a tune, by playing on a pipe, flute, fife,\n   etc.; to utter in the shrill tone of a pipe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pipe', 'v. t.', 'To call or direct, as a crew, by the boatswains whistle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pipe', 'v. t.', 'To furnish or equip with pipes; as, to pipe an engine, or\n   a building.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pipe clay', '', 'A plastic, unctuous clay of a grayish white color, -- used\n   in making tobacco pipes and various kinds of earthenware, in scouring\n   cloth, and in cleansing soldiers equipments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pipeclay', 'v. t.', 'To whiten or clean with pipe clay, as a soldiers\n   accouterments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pipeclay', 'v. t.', 'To clear off; as, to pipeclay accounts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piped', 'a.', 'Formed with a pipe; having pipe or pipes; tubular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pipefish', 'n.', 'Any lophobranch fish of the genus Siphostoma, or\n   Syngnathus, and allied genera, having a long and very slender angular\n   body, covered with bony plates. The mouth is small, at the end of a\n   long, tubular snout. The male has a pouch on his belly, in which the\n   incubation of the eggs takes place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pipelayer', '', 'Alt. of Pipe layer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pipe layer', '', 'One who lays conducting pipes in the ground, as for\n   water, gas, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pipe layer', '', 'A politician who works in secret; -- in this sense,\n   usually written as one word.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pipelaying', '', 'Alt. of Pipe laying');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pipe laying', '', 'The laying of conducting pipes underground, as for\n   water, gas, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pipe laying', '', 'The act or method of making combinations for personal\n   advantage secretly or slyly; -- in this sense, usually written as one\n   word.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pipemouth', 'n.', 'Any fish of the genus Fistularia; -- called also tobacco\n   pipefish. See Fistularia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piper', 'n.', 'See Pepper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piper', 'n.', 'One who plays on a pipe, or the like, esp. on a bagpipe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piper', 'n.', 'A common European gurnard (Trigla lyra), having a large\n   head, with prominent nasal projection, and with large, sharp, opercular\n   spines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piper', 'n.', 'A sea urchin (Goniocidaris hystrix) having very long spines,\n   native of both the American and European coasts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piperaceous', 'a.', 'Of or pertaining to the order of plants (Piperaceae)\n   of which the pepper (Piper nigrum) is the type. There are about a dozen\n   genera and a thousand species, mostly tropical plants with pungent and\n   aromatic qualities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piperic', 'a.', 'Pertaining to, or derived from, or designating, a complex\n   organic acid found in the products of different members of the Pepper\n   family, and extracted as a yellowish crystalline substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piperidge', 'n.', 'Same as Pepperidge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piperidine', 'n.', 'An oily liquid alkaloid, C5H11N, having a hot, peppery,\n   ammoniacal odor. It is related to pyridine, and is obtained by the\n   decomposition of piperine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piperine', 'n.', 'A white crystalline compound of piperidine and piperic\n   acid. It is obtained from the black pepper (Piper nigrum) and other\n   species.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piperonal', 'n.', 'A white crystalline substance obtained by oxidation of\n   piperic acid, and regarded as a complex aldehyde.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piperylene', 'n.', 'A hydrocarbon obtained by decomposition of certain\n   piperidine derivatives.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pipestem', 'n.', 'The hollow stem or tube of a pipe used for smoking\n   tobacco, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pipestone', 'n.', 'A kind of clay slate, carved by the Indians into tobacco\n   pipes. Cf. Catlinite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pipette', 'n.', 'A small glass tube, often with an enlargement or bulb in\n   the middle, and usually graduated, -- used for transferring or\n   delivering measured quantities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pipevine', 'n.', 'The Dutchmans pipe. See under Dutchman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pipewort', 'n.', 'Any plant of a genus (Eriocaulon) of aquatic or marsh\n   herbs with soft grass-like leaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piping', 'v.', 'Playing on a musical pipe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piping', 'v.', 'Peaceful; favorable to, or characterized by, the music of\n   the pipe rather than of the drum and fife.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piping', 'v.', 'Emitting a high, shrill sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piping', 'v.', 'Simmering; boiling; sizzling; hissing; -- from the sound of\n   boiling fluids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piping', 'n.', 'A small cord covered with cloth, -- used as trimming for\n   womens dresses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piping', 'n.', 'Pipes, collectively; as, the piping of a house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piping', 'n.', 'The act of playing on a pipe; the shrill noted of birds,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piping', 'n.', 'A piece cut off to be set or planted; a cutting; also,\n   propagation by cuttings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pipistrel', 'n.', 'Alt. of Pipistrelle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pipistrelle', 'n.', 'A small European bat (Vesperugo pipistrellus); --\n   called also flittermouse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pipit', 'n.', 'Any one of numerous species of small singing birds belonging\n   to Anthus and allied genera, of the family Motacillidae. They strongly\n   resemble the true larks in habits, colors, and the great length of the\n   hind claw. They are, therefore, often called titlarks, and pipit larks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pipkin', 'n.', 'A small earthen boiler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pippin', 'n.', 'An apple from a tree raised from the seed and not grafted;\n   a seedling apple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pippin', 'n.', 'A name given to apples of several different kinds, as\n   Newtown pippin, summer pippin, fall pippin, golden pippin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pippul tree', '', 'Same as Peepul tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pipras', 'pl. ', 'of Pipra');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pipra', 'n.', 'Any one of numerous species of small clamatorial birds\n   belonging to Pipra and allied genera, of the family Pipridae. The male\n   is usually glossy black, varied with scarlet, yellow, or sky blue. They\n   chiefly inhabit South America.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piprine', 'a.', 'Of or pertaining to the pipras, or the family Pipridae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pipsissewa', 'n.', 'A low evergreen plant (Chimaphila umbellata), with\n   narrow, wedge-lanceolate leaves, and an umbel of pretty nodding\n   fragrant blossoms. It has been used in nephritic diseases. Called also\n   princes pine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pipy', 'a.', 'Like a pipe; hollow-stemmed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piquancy', 'n.', 'The quality or state of being piquant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piquant', 'a.', 'Stimulating to the taste; giving zest; tart; sharp;\n   pungent; as, a piquant anecdote.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piquantly', 'adv.', 'In a piquant manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pique', 'n.', 'A cotton fabric, figured in the loom, -- used as a dress\n   goods for women and children, and for vestings, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pique', 'n.', 'The jigger. See Jigger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pique', 'n.', 'A feeling of hurt, vexation, or resentment, awakened by a\n   social slight or injury; irritation of the feelings, as through wounded\n   pride; stinging vexation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pique', 'n.', 'Keenly felt desire; a longing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pique', 'n.', 'In piquet, the right of the elder hand to count thirty in\n   hand, or to play before the adversary counts one.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piqued', 'imp. & p. p.', 'of Pique');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piquing', 'p. pr. & vb. n.', 'of Pique');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pique', 'v. t.', 'To wound the pride of; to sting; to nettle; to irritate;\n   to fret; to offend; to excite to anger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pique', 'v. t.', 'To excite to action by causing resentment or jealousy; to\n   stimulate; to prick; as, to pique ambition, or curiosity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pique', 'v. t.', 'To pride or value; -- used reflexively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pique', 'v. i.', 'To cause annoyance or irritation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piqueer', 'v. i.', 'See Pickeer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piqueerer', 'n.', 'See Pickeerer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piquet', 'n.', 'See Picket.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piquet', 'n.', 'A game at cards played between two persons, with thirty-two\n   cards, all the deuces, threes, fours, fives, and sixes, being set\n   aside.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piracies', 'pl. ', 'of Piracy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piracy', 'n.', 'The act or crime of a pirate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piracy', 'n.', 'Robbery on the high seas; the taking of property from\n   others on the open sea by open violence; without lawful authority, and\n   with intent to steal; -- a crime answering to robbery on land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piracy', 'n.', '');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piragua', 'n.', 'See Pirogue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pirai', 'n.', 'Same as Piraya.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pirameter', 'n.', 'A dynamometer for ascertaining the power required to\n   draw carriages over roads.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pirarucu', 'n.', 'Same as Arapaima.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pirate', 'n.', 'A robber on the high seas; one who by open violence takes\n   the property of another on the high seas; especially, one who makes it\n   his business to cruise for robbery or plunder; a freebooter on the\n   seas; also, one who steals in a harbor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pirate', 'n.', 'An armed ship or vessel which sails without a legal\n   commission, for the purpose of plundering other vessels on the high\n   seas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pirate', 'n.', 'One who infringes the law of copyright, or publishes the\n   work of an author without permission.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pirated', 'imp. & p. p.', 'of Pirate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pirating', 'p. pr. & vb. n.', 'of Pirate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pirate', 'v. i.', 'To play the pirate; to practice robbery on the high\n   seas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pirate', 'v. t.', 'To publish, as books or writings, without the permission\n   of the author.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piratic', 'a.', 'Piratical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piratical', 'a.', 'Of or pertaining to a pirate; acquired by, or\n   practicing, piracy; as, a piratical undertaking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piraya', 'n.', 'A large voracious fresh-water fish (Serrasalmo piraya) of\n   South America, having lancet-shaped teeth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pirie', 'n.', 'See Pirry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pirie', 'n.', 'A pear tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piririgua', 'n.', 'A South American bird (Guira guira) allied to the\n   cuckoos.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pirl', 'v. t.', 'To spin, as a top.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pirl', 'v. t.', 'To twist or twine, as hair in making fishing lines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pirn', 'n.', 'A quill or reed on which thread or yarn is wound; a bobbin;\n   also, the wound yarn on a weavers shuttle; also, the reel of a fishing\n   rod.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pirogue', 'n.', 'A dugout canoe; by extension, any small boat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pirouette', 'n.', 'A whirling or turning on the toes in dancing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pirouette', 'n.', 'The whirling about of a horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pirouetted', 'imp. & p. p.', 'of Pirouette');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pirouetting', 'p. pr. & vb. n.', 'of Pirouette');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pirouette', 'v. i.', 'To perform a pirouette; to whirl, like a dancer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pirry', 'n.', 'Alt. of Pirrie');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pirrie', 'n.', 'A rough gale of wind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pisasphaltum', 'n.', 'See Pissasphalt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pisay', 'n.', 'See Pise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piscary', 'n.', 'The right or privilege of fishing in another mans waters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piscation', 'n.', 'Fishing; fishery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piscator', 'n.', 'A fisherman; an angler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piscatorial', 'a.', 'Alt. of Piscatory');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piscatory', 'a.', 'Of or pertaining to fishes or fishing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pisces', 'n. pl.', 'The twelfth sign of the zodiac, marked / in almanacs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pisces', 'n. pl.', 'A zodiacal constellation, including the first point of\n   Aries, which is the vernal equinoctial point; the Fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pisces', 'n. pl.', 'The class of Vertebrata that includes the fishes. The\n   principal divisions are Elasmobranchii, Ganoidei, and Teleostei.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piscicapture', 'n.', 'Capture of fishes, as by angling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piscicultural', 'a.', 'Relating to pisciculture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pisciculture', 'n.', 'Fish culture. See under Fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pisciculturist', 'n.', 'One who breeds fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pisciform', 'a.', 'Having the form of a fish; resembling a fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piscina', 'n.', 'A niche near the altar in a church, containing a small\n   basin for rinsing altar vessels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piscinal', 'a.', 'Belonging to a fishpond or a piscina.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piscine', 'a.', 'Of or pertaining to a fish or fishes; as, piscine remains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piscivorous', 'a.', 'Feeding or subsisting on fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pise', 'n.', 'A species of wall made of stiff earth or clay rammed in\n   between molds which are carried up as the wall rises; -- called also\n   pise work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pish', 'interj.', 'An exclamation of contempt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pish', 'v. i.', 'To express contempt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pishu', 'n.', 'The Canada lynx.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pisiform', 'a.', 'Resembling a pea or peas in size and shape; as, a\n   pisiform iron ore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pisiform', 'n.', 'A small bone on the ulnar side of the carpus in man and\n   many mammals. See Illust. of Artiodactyla.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pismire', 'n.', 'An ant, or emmet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pisolite', 'n.', 'A variety of calcite, or calcium carbonate, consisting of\n   aggregated globular concretions about the size of a pea; -- called also\n   peastone, peagrit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pisolitic', 'a.', 'Composed of, containing, or resembling, pisolite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pisophalt', 'n.', 'Pissasphalt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piss', 'v. t. & i.', 'To discharge urine, to urinate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piss', 'n.', 'Urine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pissabed', 'n.', 'A name locally applied to various wild plants, as\n   dandelion, bluet, oxeye daisy, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pissasphalt', 'n.', 'Earth pitch; a soft, black bitumen of the consistence\n   of tar, and of a strong smell. It is inflammable, and intermediate\n   between petroleum and asphalt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pist', 'n.', 'See Piste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pistachio', 'n.', 'The nut of the Pistacia vera, a tree of the order\n   Anacardiaceae, containing a kernel of a pale greenish color, which has\n   a pleasant taste, resembling that of the almond, and yields an oil of\n   agreeable taste and odor; -- called also pistachio nut. It is wholesome\n   and nutritive. The tree grows in Arabia, Persia, Syria, and Sicily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pistacia', 'n.', 'The name of a genus of trees, including the tree which\n   bears the pistachio, the Mediterranean mastic tree (Pistacia\n   Lentiscus), and the species (P. Terebinthus) which yields Chian or\n   Cyprus turpentine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pistacite', 'n.', 'Epidote.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pistareen', 'n.', 'An old Spanish silver coin of the value of about twenty\n   cents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pistazite', 'n.', 'Same as Pistacite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piste', 'n.', 'The track or tread a horseman makes upon the ground he goes\n   over.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pistel', 'n.', 'Alt. of Pistil');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pistil', 'n.', 'An epistle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pistic', 'a.', 'Pure; genuine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pistil', 'n.', 'The seed-bearing organ of a flower. It consists of an\n   ovary, containing the ovules or rudimentary seeds, and a stigma, which\n   is commonly raised on an elongated portion called a style. When\n   composed of one carpel a pistil is simple; when composed of several, it\n   is compound. See Illust. of Flower, and Ovary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pistillaceous', 'a.', 'Growing on, or having nature of, the pistil; of or\n   pertaining to a pistil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pistillate', 'a.', 'Having a pistil or pistils; -- usually said of flowers\n   having pistils but no stamens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pistillation', 'n.', 'The act of pounding or breaking in a mortar;\n   pestillation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pistillida', 'pl. ', 'of Pistillidium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pistillidium', 'n.', 'Same as Archegonium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pistilliferous', 'a.', 'Pistillate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pistillody', 'n.', 'The metamorphosis of other organs into pistils.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pistol', 'n.', 'The smallest firearm used, intended to be fired from one\n   hand, -- now of many patterns, and bearing a great variety of names.\n   See Illust. of Revolver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pistoled', 'imp. & p. p.', 'of Pistol');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pistoling', 'p. pr. & vb. n.', 'of Pistol');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pistol', 'v. t.', 'To shoot with a pistol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pistolade', 'n.', 'A pistol shot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pistole', 'n.', 'The name of certain gold coins of various values formerly\n   coined in some countries of Europe. In Spain it was equivalent to a\n   quarter doubloon, or about $3.90, and in Germany and Italy nearly the\n   same. There was an old Italian pistole worth about $5.40.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pistoleer', 'n.', 'One who uses a pistol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pistolet', 'n.', 'A small pistol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piston', 'n.', 'A sliding piece which either is moved by, or moves against,\n   fluid pressure. It usually consists of a short cylinder fitting within\n   a cylindrical vessel along which it moves, back and forth. It is used\n   in steam engines to receive motion from the steam, and in pumps to\n   transmit motion to a fluid; also for other purposes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pit', 'n.', 'A large cavity or hole in the ground, either natural or\n   artificial; a cavity in the surface of a body; an indentation');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pit', 'n.', 'The shaft of a coal mine; a coal pit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pit', 'n.', 'A large hole in the ground from which material is dug or\n   quarried; as, a stone pit; a gravel pit; or in which material is made\n   by burning; as, a lime pit; a charcoal pit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pit', 'n.', 'A vat sunk in the ground; as, a tan pit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pit', 'n.', 'Any abyss; especially, the grave, or hades.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pit', 'n.', 'A covered deep hole for entrapping wild beasts; a pitfall;\n   hence, a trap; a snare. Also used figuratively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pit', 'n.', 'A depression or hollow in the surface of the human body');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pit', 'n.', 'The hollow place under the shoulder or arm; the axilla, or\n   armpit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pit', 'n.', 'See Pit of the stomach (below).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pit', 'n.', 'The indentation or mark left by a pustule, as in smallpox.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pit', 'n.', 'Formerly, that part of a theater, on the floor of the house,\n   below the level of the stage and behind the orchestra; now, in England,\n   commonly the part behind the stalls; in the United States, the parquet;\n   also, the occupants of such a part of a theater.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pit', 'n.', 'An inclosed area into which gamecocks, dogs, and other animals\n   are brought to fight, or where dogs are trained to kill rats.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pit', 'n.', 'The endocarp of a drupe, and its contained seed or seeds; a\n   stone; as, a peach pit; a cherry pit, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pit', 'n.', 'A depression or thin spot in the wall of a duct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pitted', 'imp. & p. p.', 'of Pit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pitting', 'p. pr. & vb. n.', 'of Pit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pit', 'v. t.', 'To place or put into a pit or hole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pit', 'v. t.', 'To mark with little hollows, as by various pustules; as, a\n   face pitted by smallpox.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pit', 'v. t.', 'To introduce as an antagonist; to set forward for or in a\n   contest; as, to pit one dog against another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pita', 'n.', 'A fiber obtained from the Agave Americana and other related\n   species, -- used for making cordage and paper. Called also pita fiber,\n   and pita thread.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pita', 'n.', 'The plant which yields the fiber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pitahaya', 'n.', 'A cactaceous shrub (Cereus Pitajaya) of tropical America,\n   which yields a delicious fruit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pitapat', 'adv.', 'In a flutter; with palpitation or quick succession of\n   beats.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pitapat', 'n.', 'A light, repeated sound; a pattering, as of the rain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pitch', 'n.', 'A thick, black, lustrous, and sticky substance obtained by\n   boiling down tar. It is used in calking the seams of ships; also in\n   coating rope, canvas, wood, ironwork, etc., to preserve them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pitch', 'n.', 'See Pitchstone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pitched', 'imp. & p. p.', 'of Pitch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pitching', 'p. pr. & vb. n.', 'of Pitch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pitch', 'n.', 'To cover over or smear with pitch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pitch', 'n.', 'Fig.: To darken; to blacken; to obscure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pitch', 'v. t.', 'To throw, generally with a definite aim or purpose; to\n   cast; to hurl; to toss; as, to pitch quoits; to pitch hay; to pitch a\n   ball.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pitch', 'v. t.', 'To thrust or plant in the ground, as stakes or poles;\n   hence, to fix firmly, as by means of poles; to establish; to arrange;\n   as, to pitch a tent; to pitch a camp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pitch', 'v. t.', 'To set, face, or pave with rubble or undressed stones, as\n   an embankment or a roadway.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pitch', 'v. t.', 'To fix or set the tone of; as, to pitch a tune.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pitch', 'v. t.', 'To set or fix, as a price or value.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pitch', 'v. i.', 'To fix or place a tent or temporary habitation; to\n   encamp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pitch', 'v. i.', 'To light; to settle; to come to rest from flight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pitch', 'v. i.', 'To fix ones choise; -- with on or upon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pitch', 'v. i.', 'To plunge or fall; esp., to fall forward; to decline or\n   slope; as, to pitch from a precipice; the vessel pitches in a heavy\n   sea; the field pitches toward the east.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pitch', 'n.', 'A throw; a toss; a cast, as of something from the hand; as,\n   a good pitch in quoits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pitch', 'n.', 'That point of the ground on which the ball pitches or lights\n   when bowled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pitch', 'n.', 'A point or peak; the extreme point or degree of elevation or\n   depression; hence, a limit or bound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pitch', 'n.', 'Height; stature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pitch', 'n.', 'A descent; a fall; a thrusting down.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pitch', 'n.', 'The point where a declivity begins; hence, the declivity\n   itself; a descending slope; the degree or rate of descent or slope;\n   slant; as, a steep pitch in the road; the pitch of a roof.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pitch', 'n.', 'The relative acuteness or gravity of a tone, determined by\n   the number of vibrations which produce it; the place of any tone upon a\n   scale of high and low.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pitch', 'n.', 'The limit of ground set to a miner who receives a share of\n   the ore taken out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pitch', 'n.', 'The distance from center to center of any two adjacent teeth\n   of gearing, measured on the pitch line; -- called also circular pitch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pitch', 'n.', 'The length, measured along the axis, of a complete turn of\n   the thread of a screw, or of the helical lines of the blades of a screw\n   propeller.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pitch', 'n.', 'The distance between the centers of holes, as of rivet holes\n   in boiler plates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pitch-black', 'a.', 'Black as pitch or tar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pitchblende', 'n.', 'A pitch-black mineral consisting chiefly of the oxide\n   of uranium; uraninite. See Uraninite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pitch-dark', 'a.', 'Dark as a pitch; pitch-black.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pitcher', 'n.', 'One who pitches anything, as hay, quoits, a ball, etc.;\n   specifically (Baseball), the player who delivers the ball to the\n   batsman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pitcher', 'n.', 'A sort of crowbar for digging.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pitcher', 'n.', 'A wide-mouthed, deep vessel for holding liquids, with a\n   spout or protruding lip and a handle; a water jug or jar with a large\n   ear or handle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pitcher', 'n.', 'A tubular or cuplike appendage or expansion of the leaves\n   of certain plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pitcherfuls', 'pl. ', 'of Pitcherful');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pitcherful', 'n.', 'The quantity a pitcher will hold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pitch-faced', 'a.', 'Having the arris defined by a line beyond which the\n   rock is cut away, so as to give nearly true edges; -- said of squared\n   stones that are otherwise quarry-faced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pitchfork', 'n.', 'A fork, or farming utensil, used in pitching hay,\n   sheaves of grain, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pitchfork', 'v. t.', 'To pitch or throw with, or as with, a pitchfork.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pitchiness', 'n.', 'Blackness, as of pitch; darkness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pitching', 'n.', 'The act of throwing or casting; a cast; a pitch; as, wild\n   pitching in baseball.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pitching', 'n.', 'The rough paving of a street to a grade with blocks of\n   stone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pitching', 'n.', 'A facing of stone laid upon a bank to prevent wear by\n   tides or currents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pitch-ore', 'n.', 'Pitchblende.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pitchstone', 'n.', 'An igneous rock of semiglassy nature, having a luster\n   like pitch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pitchwork', 'n.', 'The work of a coal miner who is paid by a share of his\n   product.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pitchy', 'a.', 'Partaking of the qualities of pitch; resembling pitch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pitchy', 'a.', 'Smeared with pitch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pitchy', 'a.', 'Black; pitch-dark; dismal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piteous', 'a.', 'Pious; devout.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piteous', 'a.', 'Evincing pity, compassion, or sympathy; compassionate;\n   tender.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piteous', 'a.', 'Fitted to excite pity or sympathy; wretched; miserable;\n   lamentable; sad; as, a piteous case.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piteous', 'a.', 'Paltry; mean; pitiful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pitfall', 'n.', 'A pit deceitfully covered to entrap wild beasts or men; a\n   trap of any kind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pitfalling', 'a.', 'Entrapping; insnaring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pith', 'n.', 'The soft spongy substance in the center of the stems of many\n   plants and trees, especially those of the dicotyledonous or exogenous\n   classes. It consists of cellular tissue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pith', 'n.', 'The spongy interior substance of a feather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pith', 'n.', 'The spinal cord; the marrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pith', 'n.', 'Hence: The which contains the strength of life; the vital or\n   essential part; concentrated force; vigor; strength; importance; as,\n   the speech lacked pith.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pith', 'v. t.', 'To destroy the central nervous system of (an animal, as a\n   frog), as by passing a stout wire or needle up and down the vertebral\n   canal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pitheci', 'n. pl.', 'A division of mammals including the apes and monkeys.\n   Sometimes used in the sense of Primates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pithecoid', 'a.', 'Of or pertaining to the genus Pithecia, or subfamily\n   Pithecinae, which includes the saki, ouakari, and other allied South\n   American monkeys.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pithecoid', 'a.', 'Of or pertaining to the anthropoid apes in particular,\n   or to the higher apes of the Old World, collectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pithful', 'a.', 'Full of pith.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pithily', 'adv.', 'In a pithy manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pithiness', 'n.', 'The quality or state of being pithy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pithless', 'a.', 'Destitute of pith, or of strength; feeble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pit-hole', 'n.', 'A pit; a pockmark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pithsome', 'a.', 'Pithy; robust.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pithy', 'superl.', 'Consisting wholly, or in part, of pith; abounding in\n   pith; as, a pithy stem; a pithy fruit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pithy', 'superl.', 'Having nervous energy; forceful; cogent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pitiable', 'a.', 'Deserving pity; wworthy of, or exciting, compassion;\n   miserable; lamentable; piteous; as, pitiable persons; a pitiable\n   condition; pitiable wretchedness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pitier', 'n.', 'One who pities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pitiful', 'a.', 'Full of pity; tender-hearted; compassionate; kind;\n   merciful; sympathetic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pitiful', 'a.', 'Piteous; lamentable; eliciting compassion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pitiful', 'a.', 'To be pitied for littleness or meanness; miserable;\n   paltry; contemptible; despicable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pitiless', 'a.', 'Destitute of pity; hard-hearted; merciless; as, a\n   pitilessmaster; pitiless elements.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pitiless', 'a.', 'Exciting no pity; as, a pitiless condition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pitmen', 'pl. ', 'of Pitman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pitman', 'n.', 'One who works in a pit, as in mining, in sawing timber,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pitman', 'n.', 'The connecting rod in a sawmill; also, sometimes, a\n   connecting rod in other machinery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pitots tube', '', 'A bent tube used to determine the velocity of running\n   water, by placing the curved end under water, and observing the height\n   to which the fluid rises in the tube; a kind of current meter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pitpan', 'n.', 'A long, flat-bottomed canoe, used for the navigation of\n   rivers and lagoons in Central America.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pitpat', 'n. & adv.', 'See Pitapat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pitta', 'n.', 'Any one of a large group of bright-colored clamatorial birds\n   belonging to Pitta, and allied genera of the family Pittidae. Most of\n   the species are varied with three or more colors, such as blue, green,\n   crimson, yellow, purple, and black. They are called also ground\n   thrushes, and Old World ant thrushes; but they are not related to the\n   true thrushes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pittacal', 'n.', 'A dark blue substance obtained from wood tar. It consists\n   of hydrocarbons which when oxidized form the orange-yellow eupittonic\n   compounds, the salts of which are dark blue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pittance', 'n.', 'An allowance of food bestowed in charity; a mess of\n   victuals; hence, a small charity gift; a dole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pittance', 'n.', 'A meager portion, quantity, or allowance; an\n   inconsiderable salary or compensation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pitted', 'a.', 'Marked with little pits, as in smallpox. See Pit, v. t., 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pitted', 'v. t.', 'Having minute thin spots; as, pitted ducts in the\n   vascular parts of vegetable tissue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pitter', 'n.', 'A contrivance for removing the pits from peaches, plums,\n   and other stone fruit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pitter', 'v. i.', 'To make a pattering sound; to murmur; as, pittering\n   streams.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pittle-pattle', 'v. i.', 'To talk unmeaningly; to chatter or prattle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pituitary', 'a.', 'Secreting mucus or phlegm; as, the pituitary membrane,\n   or the mucous membrane which lines the nasal cavities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pituitary', 'a.', 'Of or pertaining to the pituitary body; as, the\n   pituitary fossa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pituite', 'n.', 'Mucus, phlegm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pituitous', 'a.', 'Consisting of, or resembling, pituite or mucus; full of\n   mucus; discharging mucus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pities', 'pl. ', 'of Pity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pity', 'n.', 'Piety.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pity', 'n.', 'A feeling for the sufferings or distresses of another or\n   others; sympathy with the grief or misery of another; compassion;\n   fellow-feeling; commiseration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pity', 'n.', 'A reason or cause of pity, grief, or regret; a thing to be\n   regretted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pitied', 'imp. & p. p.', 'of Pity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pitying', 'p. pr. & vb. n.', 'of Pity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pity', 'v. t.', 'To feel pity or compassion for; to have sympathy with; to\n   compassionate; to commiserate; to have tender feelings toward (any\n   one), awakened by a knowledge of suffering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pity', 'v. t.', 'To move to pity; -- used impersonally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pity', 'v. i.', 'To be compassionate; to show pity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pitying', 'a.', 'Expressing pity; as, a pitying eye, glance, or word.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pityriasis', 'n.', 'A superficial affection of the skin, characterized by\n   irregular patches of thin scales which are shed in branlike particles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pityroid', 'a.', 'Having the form of, or resembling, bran.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Piu', 'adv.', 'A little more; as, piu allegro, a little more briskly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pivot', 'n.', 'A fixed pin or short axis, on the end of which a wheel or\n   other body turns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pivot', 'n.', 'The end of a shaft or arbor which rests and turns in a\n   support; as, the pivot of an arbor in a watch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pivot', 'n.', 'Hence, figuratively: A turning point or condition; that on\n   which important results depend; as, the pivot of an enterprise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pivot', 'n.', 'The officer or soldier who simply turns in his place whike\n   the company or line moves around him in wheeling; -- called also pivot\n   man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pivoted', 'imp. & p. p.', 'of Pivot');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pivoting', 'p. pr. & vb. n.', 'of Pivot');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pivot', 'v. t.', 'To place on a pivot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pivotal', 'a.', 'Of or pertaining to a pivot or turning point; belonging\n   to, or constituting, a pivot; of the nature of a pivot; as, the\n   pivotalopportunity of a career; the pivotal position in a battle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pix', 'n. & v.', 'See Pyx.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pixies', 'pl. ', 'of Pixie');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pixy', 'n.', 'Alt. of Pixie');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pixie', 'n.', 'An old English name for a fairy; an elf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pixie', 'n.', 'A low creeping evergreen plant (Pyxidanthera barbulata),\n   with mosslike leaves and little white blossoms, found in New Jersey and\n   southward, where it flowers in earliest spring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pixy-led', 'a.', 'Led by pixies; bewildered.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pizzicato', '', 'A direction to violinists to pluck the string with the\n   finger, instead of using the bow. (Abrev. pizz.)');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pizzle', 'n.', 'The penis; -- so called in some animals, as the bull.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Placability', 'n.', 'The quality or state of being placable or appeasable;\n   placable disposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Placable', 'a.', 'Capable of being appeased or pacified; ready or willing\n   to be pacified; willing to forgive or condone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Placableness', 'n.', 'The quality of being placable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Placard', 'n.', 'A public proclamation; a manifesto or edict issued by\n   authority.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Placard', 'n.', 'Permission given by authority; a license; as, to give a\n   placard to do something.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Placard', 'n.', 'A written or printed paper, as an advertisement or a\n   declaration, posted, or to be posted, in a public place; a poster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Placard', 'n.', 'An extra plate on the lower part of the breastplate or\n   backplate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Placard', 'n.', 'A kind of stomacher, often adorned with jewels, worn in\n   the fifteenth century and later.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Placarded', 'imp. & p. p.', 'of Placard');
  INSERT INTO words (word, word_type, description) VALUES  (    'Placarding', 'p. pr. & vb. n.', 'of Placard');
  INSERT INTO words (word, word_type, description) VALUES  (    'Placard', 'v. t.', 'To post placards upon or within; as, to placard a wall,\n   to placard the city.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Placard', 'v. t.', 'To announce by placards; as, to placard a sale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Placate', 'n.', 'Same as Placard, 4 & 5.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Placated', 'imp. & p. p.', 'of Placate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Placating', 'p. pr. & vb. n.', 'of Placate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Placate', 'v. t.', 'To appease; to pacify; to concilate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Placation', 'n.', 'The act of placating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Place', 'n.', 'Any portion of space regarded as measured off or distinct\n   from all other space, or appropriated to some definite object or use;\n   position; ground; site; spot; rarely, unbounded space.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Place', 'n.', 'A broad way in a city; an open space; an area; a court or\n   short part of a street open only at one end.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Place', 'n.', 'A position which is occupied and held; a dwelling; a\n   mansion; a village, town, or city; a fortified town or post; a\n   stronghold; a region or country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Place', 'n.', 'Rank; degree; grade; order of priority, advancement,\n   dignity, or importance; especially, social rank or position; condition;\n   also, official station; occupation; calling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Place', 'n.', 'Vacated or relinquished space; room; stead (the departure or\n   removal of another being or thing being implied).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Place', 'n.', 'A definite position or passage of a document.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Place', 'n.', 'Ordinal relation; position in the order of proceeding; as,\n   he said in the first place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Place', 'n.', 'Reception; effect; -- implying the making room for.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Place', 'n.', 'Position in the heavens, as of a heavenly body; -- usually\n   defined by its right ascension and declination, or by its latitude and\n   longitude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Placed', 'imp. & p. p.', 'of Place');
  INSERT INTO words (word, word_type, description) VALUES  (    'Placing', 'p. pr. & vb. n.', 'of Place');
  INSERT INTO words (word, word_type, description) VALUES  (    'Place', 'n.', 'To assign a place to; to put in a particular spot or place,\n   or in a certain relative position; to direct to a particular place; to\n   fix; to settle; to locate; as, to place a book on a shelf; to place\n   balls in tennis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Place', 'n.', 'To put or set in a particular rank, office, or position; to\n   surround with particular circumstances or relations in life; to appoint\n   to certain station or condition of life; as, in whatever sphere one is\n   placed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Place', 'n.', 'To put out at interest; to invest; to loan; as, to place\n   money in a bank.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Place', 'n.', 'To set; to fix; to repose; as, to place confidence in a\n   friend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Place', 'n.', 'To attribute; to ascribe; to set down.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Placebo', 'n.', 'The first antiphon of the vespers for the dead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Placebo', 'n.', 'A prescription intended to humor or satisfy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Placeful', 'a.', 'In the appointed place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Placeless', 'a.', 'Having no place or office.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Placemen', 'pl. ', 'of Placeman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Placeman', 'n.', 'One who holds or occupies a place; one who has office\n   under government.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Placement', 'n.', 'The act of placing, or the state of being placed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Placement', 'n.', 'Position; place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Placentae', 'pl. ', 'of Placenta');
  INSERT INTO words (word, word_type, description) VALUES  (    'Placentas', 'pl. ', 'of Placenta');
  INSERT INTO words (word, word_type, description) VALUES  (    'Placenta', 'n.', 'The vascular appendage which connects the fetus with the\n   parent, and is cast off in parturition with the afterbirth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Placenta', 'n.', 'The part of a pistil or fruit to which the ovules or\n   seeds are attached.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Placental', 'a.', 'Of or pertaining to the placenta; having, or\n   characterized by having, a placenta; as, a placental mammal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Placental', 'a.', 'Of or pertaining to the Placentalia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Placental', 'n.', 'One of the Placentalia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Placentalia', 'n. pl.', 'A division of Mammalia including those that have a\n   placenta, or all the orders above the marsupials.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Placentary', 'a.', 'Having reference to the placenta; as, the placentary\n   system of classification.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Placentation', 'n.', 'The mode of formation of the placenta in different\n   animals; as, the placentation of mammals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Placentation', 'n.', 'The mode in which the placenta is arranged or\n   composed; as, axile placentation; parietal placentation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Placentiferous', 'a.', 'Having or producing a placenta.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Placentiform', 'a.', 'Having the shape of a placenta, or circular thickened\n   disk somewhat thinner about the middle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Placentious', 'a.', 'Pleasing; amiable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Place-proud', 'a.', 'Proud of rank or office.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Placer', 'n.', 'One who places or sets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Placer', 'n.', 'A deposit of earth, sand, or gravel, containing valuable\n   mineral in particles, especially by the side of a river, or in the bed\n   of a mountain torrent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Placet', 'n.', 'A vote of assent, as of the governing body of a university,\n   of an ecclesiastical council, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Placet', 'n.', 'The assent of the civil power to the promulgation of an\n   ecclesiastical ordinance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Placid', 'a.', 'Pleased; contented; unruffied; undisturbed; serene;\n   peaceful; tranquil; quiet; gentle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Placidity', 'n.', 'The quality or state of being placid; calmness;\n   serenity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Placidly', 'adv.', 'In a placid manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Placidness', 'n.', 'The quality or state of being placid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Placit', 'n.', 'A decree or determination; a dictum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Placitory', 'a.', 'Of or pertaining to pleas or pleading, in courts of law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Placita', 'pl. ', 'of Placitum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Placitum', 'n.', 'A public court or assembly in the Middle Ages, over which\n   the sovereign president when a consultation was held upon affairs of\n   state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Placitum', 'n.', 'A court, or cause in court.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Placitum', 'n.', 'A plea; a pleading; a judicial proceeding; a suit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plack', 'n.', 'A small copper coin formerly current in Scotland, worth less\n   than a cent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Placket', 'n.', 'A petticoat, esp. an under petticoat; hence, a cant term\n   for a woman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Placket', 'n.', 'The opening or slit left in a petticoat or skirt for\n   convenience in putting it on; -- called also placket hole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Placket', 'n.', 'A womans pocket.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Placoderm', 'n.', 'One of the Placodermi.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Placodermal', 'a.', 'Of or pertaining to the placoderms; like the\n   placoderms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Placodermata', 'n. pl.', 'Same as Placodermi.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Placodermi', 'n. pl.', 'An extinct group of fishes, supposed to be ganoids.\n   The body and head were covered with large bony plates. See Illust.\n   under Pterichthys, and Coccosteus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Placoganoid', 'a.', 'Pertaining to the Placoganoidei.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Placoganoidei', 'n. pl.', 'A division of ganoid fishes including those that\n   have large external bony plates and a cartilaginous skeleton.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Placoid', 'a.', 'Platelike; having irregular, platelike, bony scales, often\n   bearing spines; pertaining to the placoids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Placoid', 'n.', 'Any fish having placoid scales, as the sharks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Placoid', 'n.', 'One of the Placoides.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Placoides', 'n. pl.', 'A group of fishes including the sharks and rays; the\n   Elasmobranchii; -- called also Placoidei.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Placoidian', 'n.', 'One of the placoids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Placophora', 'n. pl.', 'A division of gastropod Mollusca, including the\n   chitons. The back is covered by eight shelly plates. Called also\n   Polyplacophora. See Illust. under Chiton, and Isopleura.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plagae', 'pl. ', 'of Plaga');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plaga', 'n.', 'A stripe of color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plagal', 'a.', 'Having a scale running from the dominant to its octave; --\n   said of certain old church modes or tunes, as opposed to those called\n   authentic, which ran from the tonic to its octave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plagate', 'a.', 'Having plagae, or irregular enlongated color spots.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plage', 'n.', 'A region; country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plagiarism', 'n.', 'The act or practice of plagiarizing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plagiarism', 'n.', 'That which plagiarized.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plagiarist', 'n.', 'One who plagiarizes; or purloins the words, writings,\n   or ideas of another, and passes them off as his own; a literary thief;\n   a plagiary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plagiarized', 'imp. & p. p.', 'of Plagiarize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plagiarizing', 'p. pr. & vb. n.', 'of Plagiarize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plagiarize', 'v. t.', 'To steal or purloin from the writings of another; to\n   appropriate without due acknowledgement (the ideas or expressions of\n   another).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plagiary', 'v. i.', 'To commit plagiarism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plagiaries', 'pl. ', 'of Plagiary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plagiary', 'n.', 'A manstealer; a kidnaper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plagiary', 'n.', 'One who purloins anothers expressions or ideas, and\n   offers them as his own; a plagiarist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plagiary', 'n.', 'Plagiarism; literary thief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plagiary', 'a.', 'Kidnaping.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plagiary', 'a.', 'Practicing plagiarism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plagihedral', 'a.', 'Having an oblique spiral arrangement of planes, as\n   levogyrate and dextrogyrate crystals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plagiocephalic', 'a.', 'Having an oblique lateral deformity of the skull.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plagiocephaly', 'n.', 'Oblique lateral deformity of the skull.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plagioclase', 'n.', 'A general term used of any triclinic feldspar. See the\n   Note under Feldspar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plagionite', 'n.', 'A sulphide of lead and antimony, of a blackish\n   lead-gray color and metallic luster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plagiostomatous', 'a.', 'Same as Plagiostomous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plagiostome', 'n.', 'One of the Plagiostomi.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plagiostomi', 'n. pl.', 'An order of fishes including the sharks and rays;\n   -- called also Plagiostomata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plagiostomous', 'a.', 'Of or pertaining to the Plagiostomi.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plagiotremata', 'n. pl.', 'Same as Lepidosauria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plagiotropic', 'a.', 'Having the longer axis inclined away from the\n   vertical line.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plagium', 'n.', 'Manstealing; kidnaping.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plagose', 'a.', 'Fond of flogging; as, a plagose master.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plague', 'n.', 'That which smites, wounds, or troubles; a blow; a calamity;\n   any afflictive evil or torment; a great trail or vexation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plague', 'n.', 'An acute malignant contagious fever, that often prevails in\n   Egypt, Syria, and Turkey, and has at times visited the large cities of\n   Europe with frightful mortality; hence, any pestilence; as, the great\n   London plague.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plagued', 'imp. & p. p.', 'of Plague');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plaguing', 'p. pr. & vb. n.', 'of Plague');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plague', 'v. t.', 'To infest or afflict with disease, calamity, or natural\n   evil of any kind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plague', 'v. t.', 'Fig.: To vex; to tease; to harass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plagueful', 'a.', 'Abounding, or infecting, with plagues; pestilential; as,\n   plagueful exhalations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plagueless', 'a.', 'Free from plagues or the plague.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plaguer', 'n.', 'One who plagues or annoys.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plaguily', 'adv.', 'In a plaguing manner; vexatiously; extremely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plaguy', 'a.', 'Vexatious; troublesome; tormenting; as, a plaguy horse.\n   [Colloq.] Also used adverbially; as, \"He is so plaguy proud.\"');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plaice', 'n.', 'A European food fish (Pleuronectes platessa), allied to the\n   flounder, and growing to the weight of eight or ten pounds or more.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plaice', 'n.', 'A large American flounder (Paralichthys dentatus; called\n   also brail, puckermouth, and summer flounder. The name is sometimes\n   applied to other allied species.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plaid', 'n.', 'A rectangular garment or piece of cloth, usually made of the\n   checkered material called tartan, but sometimes of plain gray, or gray\n   with black stripes. It is worn by both sexes in Scotland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plaid', 'n.', 'Goods of any quality or material of the pattern of a plaid\n   or tartan; a checkered cloth or pattern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plaid', 'a.', 'Having a pattern or colors which resemble a Scotch plaid;\n   checkered or marked with bars or stripes at right angles to one\n   another; as, plaid muslin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plaided', 'a.', 'Of the material of which plaids are made; tartan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plaided', 'a.', 'Wearing a plaid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plaiding', 'n.', 'Plaid cloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plain', 'v. i.', 'To lament; to bewail; to complain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plain', 'v. t.', 'To lament; to mourn over; as, to plain a loss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plain', 'superl.', 'Without elevations or depressions; flat; level; smooth;\n   even. See Plane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plain', 'superl.', 'Open; clear; unencumbered; equal; fair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plain', 'superl.', 'Not intricate or difficult; evident; manifest; obvious;\n   clear; unmistakable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plain', 'superl.', 'Void of extraneous beauty or ornament; without\n   conspicious embellishment; not rich; simple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plain', 'superl.', 'Not highly cultivated; unsophisticated; free from show\n   or pretension; simple; natural; homely; common.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plain', 'superl.', 'Free from affectation or disguise; candid; sincere;\n   artless; honest; frank.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plain', 'superl.', 'Not luxurious; not highly seasoned; simple; as, plain\n   food.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plain', 'superl.', 'Without beauty; not handsome; homely; as, a plain\n   woman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plain', 'superl.', 'Not variegated, dyed, or figured; as, plain muslin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plain', 'superl.', 'Not much varied by modulations; as, a plain tune.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plain', 'adv.', 'In a plain manner; plainly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plain', 'a.', 'Level land; usually, an open field or a broad stretch of\n   land with an even surface, or a surface little varied by inequalities;\n   as, the plain of Jordan; the American plains, or prairies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plain', 'a.', 'A field of battle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plained', 'imp. & p. p.', 'of Plain');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plaining', 'p. pr. & vb. n.', 'of Plain');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plain', 'v.', 'To plane or level; to make plain or even on the surface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plain', 'v.', 'To make plain or manifest; to explain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plainant', 'n.', 'One who makes complaint; the plaintiff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plain-dealing', 'a.', 'Practicing plain dealing; artless. See Plain\n   dealing, under Dealing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plain-hearted', 'a.', 'Frank; sincere; artless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plaining', 'n.', 'Complaint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plaining', 'a.', 'Complaining.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plain-laid', 'a.', 'Consisting of strands twisted together in the ordinary\n   way; as, a plain-laid rope. See Illust. of Cordage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plainly', 'adv.', 'In a plain manner; clearly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plainness', 'n.', 'The quality or state of being plain.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-men', 'pl. ', 'of Plainsman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plainsman', 'n.', 'One who lives in the plains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plain-spoken', 'a.', 'Speaking with plain, unreserved sincerity; also,\n   spoken sincerely; as, plain-spoken words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plaint', 'n.', 'Audible expression of sorrow; lamentation; complaint;\n   hence, a mournful song; a lament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plaint', 'n.', 'An accusation or protest on account of an injury.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plaint', 'n.', 'A private memorial tendered to a court, in which a person\n   sets forth his cause of action; the exhibiting of an action in writing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plaintful', 'a.', 'Containing a plaint; complaining; expressing sorrow with\n   an audible voice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plaintiff', 'n.', 'One who commences a personal action or suit to obtain a\n   remedy for an injury to his rights; -- opposed to defendant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plaintiff', 'a.', 'See Plaintive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plaintive', 'n.', 'Repining; complaining; lamenting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plaintive', 'n.', 'Expressive of sorrow or melancholy; mournful; sad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plaintless', 'a.', 'Without complaint; unrepining.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plaisance', 'n.', 'See Pleasance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plaise', 'n.', 'See Plaice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plaister', 'n.', 'See Plaster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plait', 'n.', 'A flat fold; a doubling, as of cloth; a pleat; as, a box\n   plait.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plait', 'n.', 'A braid, as of hair or straw; a plat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plaited', 'imp. & p. p.', 'of Plait');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plaiting', 'p. pr. & vb. n.', 'of Plait');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plait', 'v. t.', 'To fold; to double in narrow folds; to pleat; as, to\n   plait a ruffle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plait', 'v. t.', 'To interweave the strands or locks of; to braid; to plat;\n   as, to plait hair; to plait rope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plaited', 'a.', 'Folded; doubled over; braided; figuratively, involved;\n   intricate; artful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plaiter', 'n.', 'One who, or that which, plaits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plan', 'a.', 'A draught or form; properly, a representation drawn on a\n   plane, as a map or a chart; especially, a top view, as of a machine, or\n   the representation or delineation of a horizontal section of anything,\n   as of a building; a graphic representation; a diagram.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plan', 'a.', 'A scheme devised; a method of action or procedure expressed\n   or described in language; a project; as, the plan of a constitution;\n   the plan of an expedition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plan', 'a.', 'A method; a way of procedure; a custom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Planned', 'imp. & p. p.', 'of Plan');
  INSERT INTO words (word, word_type, description) VALUES  (    'Planning', 'p. pr. & vb. n.', 'of Plan');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plan', 'v. t.', 'To form a delineation of; to draught; to represent, as by\n   a diagram.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plan', 'v. t.', 'To scheme; to devise; to contrive; to form in design; as,\n   to plan the conquest of a country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Planariae', 'pl. ', 'of Planaria');
  INSERT INTO words (word, word_type, description) VALUES  (    '-rias', 'pl. ', 'of Planaria');
  INSERT INTO words (word, word_type, description) VALUES  (    'Planaria', 'n.', 'Any species of turbellarian worms belonging to Planaria,\n   and many allied genera. The body is usually flat, thin, and smooth.\n   Some species, in warm countries, are terrestrial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Planarian', 'n.', 'One of the Planarida, or Dendrocoela; any turbellarian\n   worm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Planarida', 'n. pl.', 'A division of Turbellaria; the Dendrocoela.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Planarioid', 'a.', 'Like the planarians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Planary', 'a.', 'Of or pertaining to a plane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Planch', 'n.', 'A plank.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Planched', 'imp. & p. p.', 'of Planch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Planching', 'p. pr. & vb. n.', 'of Planch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Planch', 'v. t.', 'To make or cover with planks or boards; to plank.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plancher', 'n.', 'A floor of wood; also, a plank.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plancher', 'n.', 'The under side of a cornice; a soffit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plancher', 'v. t.', 'To form of planks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Planchet', 'n.', 'A flat piece of metal; especially, a disk of metal ready\n   to be stamped as a coin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Planchette', 'n.', 'A circumferentor. See Circumferentor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Planchette', 'n.', 'A small tablet of wood supported on casters and having\n   a pencil attached. The characters produced by the pencil on paper,\n   while the hand rests on the instrument and it is allowed to move, are\n   sometimes translated as of oracular or supernatural import.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Planching', 'n.', 'The laying of floors in a building; also, a floor of\n   boards or planks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plane', 'n.', 'Any tree of the genus Platanus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plane', 'a.', 'Without elevations or depressions; even; level; flat; lying\n   in, or constituting, a plane; as, a plane surface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plane', 'a.', 'A surface, real or imaginary, in which, if any two points\n   are taken, the straight line which joins them lies wholly in that\n   surface; or a surface, any section of which by a like surface is a\n   straight line; a surface without curvature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plane', 'a.', 'An ideal surface, conceived as coinciding with, or\n   containing, some designated astronomical line, circle, or other curve;\n   as, the plane of an orbit; the plane of the ecliptic, or of the\n   equator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plane', 'a.', 'A block or plate having a perfectly flat surface, used as a\n   standard of flatness; a surface plate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plane', 'a.', 'A tool for smoothing boards or other surfaces of wood, for\n   forming moldings, etc. It consists of a smooth-soled stock, usually of\n   wood, from the under side or face of which projects slightly the steel\n   cutting edge of a chisel, called the iron, which inclines backward,\n   with an apperture in front for the escape of shavings; as, the jack\n   plane; the smoothing plane; the molding plane, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Planed', 'imp. & p. p.', 'of Plane');
  INSERT INTO words (word, word_type, description) VALUES  (    'Planing', 'p. pr. & vb. n.', 'of Plane');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plane', 'a.', 'To make smooth; to level; to pare off the inequalities of\n   the surface of, as of a board or other piece of wood, by the use of a\n   plane; as, to plane a plank.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plane', 'a.', 'To efface or remove.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plane', 'a.', 'Figuratively, to make plain or smooth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plane-parallel', 'a.', 'Having opposite surfaces exactly plane and\n   parallel, as a piece of glass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Planer', 'n.', 'One who, or that which, planes; a planing machine; esp., a\n   machine for planing wood or metals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Planer', 'n.', 'A wooden block used for forcing down the type in a form,\n   and making the surface even.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Planer tree', '', 'A small-leaved North American tree (Planera aquatica)\n   related to the elm, but having a wingless, nutlike fruit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Planet', 'n.', 'A celestial body which revolves about the sun in an orbit\n   of a moderate degree of eccentricity. It is distinguished from a comet\n   by the absence of a coma, and by having a less eccentric orbit. See\n   Solar system.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Planet', 'n.', 'A star, as influencing the fate of a men.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plane table', '', 'See under Plane, a.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Planetarium', 'n.', 'An orrery. See Orrery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Planetary', 'a.', 'Of or pertaining to the planets; as, planetary\n   inhabitants; planetary motions; planetary year.');
