INSERT INTO words (word, word_type, description)VALUES  (    'Planetary',    'a.',    'Consisting of planets; as, a planetary system.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Planetary', 'a.', 'Under the dominion or influence of a planet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Planetary', 'a.', 'Caused by planets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Planetary', 'a.', 'Having the nature of a planet; erratic; revolving;\n   wandering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Planeted', 'a.', 'Belonging to planets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Planetic', 'a.', 'Alt. of Planetical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Planetical', 'a.', 'Of or pertaining to planets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Planetoid', 'n.', 'A body resembling a planet; an asteroid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Planetoidal', 'a.', 'Pertaining to a planetoid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plane tree', '', 'Same as 1st Plane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Planet-stricken', 'a.', 'Alt. of Planet-struck');
  INSERT INTO words (word, word_type, description) VALUES  (    'Planet-struck', 'a.', 'Affected by the influence of planets; blasted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Planetule', 'n.', 'A little planet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plangency', 'n.', 'The quality or state of being plangent; a beating sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plangent', 'a.', 'Beating; dashing, as a wave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plani-', 'a.', 'Alt. of Plano-');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plano-', 'a.', 'Combining forms signifying flat, level, plane; as\n   planifolious, planimetry, plano-concave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Planifolious', 'a.', 'Flat-leaved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Planiform', 'a.', 'Having a plane surface; as, a planiform, gliding, or\n   arthrodial articulation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Planimeter', 'n.', 'An instrument for measuring the area of any plane\n   figure, however irregular, by passing a tracer around the bounding\n   line; a platometer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Planimetric', 'a.', 'Alt. of Planimetrical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Planimetrical', 'a.', 'Of or pertaining to planimetry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Planimetry', 'n.', 'The mensuration of plane surfaces; -- distinguished\n   from stereometry, or the mensuration of volumes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Planing', '', 'a. & vb. n. fr. Plane, v. t.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Planipennate', 'a.', 'Of or pertaining to Planipennia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Planipennia', 'n. pl.', 'A suborder of Neuroptera, including those that\n   have broad, flat wings, as the ant-lion, lacewing, etc. Called also\n   Planipennes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Planipetalous', 'a.', 'Having flat petals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Planished', 'imp. & p. p.', 'of Planish');
  INSERT INTO words (word, word_type, description) VALUES  (    'Planishing', 'p. pr. & vb. n.', 'of Planish');
  INSERT INTO words (word, word_type, description) VALUES  (    'Planish', 'v.', 'To make smooth or plane, as a metallic surface; to\n   condense, toughen, and polish by light blows with a hammer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Planisher', 'n.', 'One who, or that which, planishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Planishing', '', 'a. & vb. n. from Planish, v. t.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Planisphere', 'n.', 'The representation of the circles of the sphere upon a\n   plane; especially, a representation of the celestial sphere upon a\n   plane with adjustable circles, or other appendages, for showing the\n   position of the heavens, the time of rising and setting of stars, etc.,\n   for any given date or hour.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Planispheric', 'a.', 'Of or pertaining to a planisphere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plank', 'n.', 'A broad piece of sawed timber, differing from a board only\n   in being thicker. See Board.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plank', 'n.', 'Fig.: That which supports or upholds, as a board does a\n   swimmer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plank', 'n.', 'One of the separate articles in a declaration of the\n   principles of a party or cause; as, a plank in the national platform.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Planked', 'imp. & p. p.', 'of Plank');
  INSERT INTO words (word, word_type, description) VALUES  (    'Planking', 'p. pr. & vb. n.', 'of Plank');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plank', 'v. t.', 'To cover or lay with planks; as, to plank a floor or a\n   ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plank', 'v. t.', 'To lay down, as on a plank or table; to stake or pay\n   cash; as, to plank money in a wager.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plank', 'v. t.', 'To harden, as hat bodies, by felting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plank', 'v. t.', 'To splice together the ends of slivers of wool, for\n   subsequent drawing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Planking', 'n.', 'The act of laying planks; also, planks, collectively; a\n   series of planks in place, as the wooden covering of the frame of a\n   vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Planking', 'n.', 'The act of splicing slivers. See Plank, v. t., 4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plank-sheer', 'n.', 'The course of plank laid horizontally over the\n   timberheads of a vessels frame.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Planless', 'a.', 'Having no plan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Planner', 'n.', 'One who plans; a projector.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plano-', '', 'See Plani-.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Planoblast', 'n.', 'Any free-swimming gonophore of a hydroid; a hydroid\n   medusa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plano-concave', 'a.', 'Plane or flat on one side, and concave on the other;\n   as, a plano-concave lens. See Lens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plano-conical', 'a.', 'Plane or flat on one side, and conical on the other.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plano-convex', 'a.', 'Plane or flat on one side, and convex on the other;\n   as, a plano-convex lens. See Convex, and Lens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plano-horizontal', 'a.', 'Having a level horizontal surface or position.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Planometer', 'n.', 'An instrument for gauging or testing a plane surface.\n   See Surface gauge, under Surface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Planometry', 'n.', 'The art or process of producing or gauging a plane\n   surface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plano-orbicular', 'a.', 'Plane or flat on one side, and spherical on the\n   other.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Planorbis', 'n.', 'Any fresh-water air-breathing mollusk belonging to\n   Planorbis and other allied genera, having shells of a discoidal form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plano-subulate', 'a.', 'Smooth and awl-shaped. See Subulate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plant', 'n.', 'A vegetable; an organized living being, generally without\n   feeling and voluntary motion, and having, when complete, a root, stem,\n   and leaves, though consisting sometimes only of a single leafy\n   expansion, or a series of cellules, or even a single cellule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plant', 'n.', 'A bush, or young tree; a sapling; hence, a stick or staff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plant', 'n.', 'The sole of the foot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plant', 'n.', 'The whole machinery and apparatus employed in carrying on a\n   trade or mechanical business; also, sometimes including real estate,\n   and whatever represents investment of capital in the means of carrying\n   on a business, but not including material worked upon or finished\n   products; as, the plant of a foundry, a mill, or a railroad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plant', 'n.', 'A plan; an artifice; a swindle; a trick.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plant', 'n.', 'An oyster which has been bedded, in distinction from one of\n   natural growth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plant', 'n.', 'A young oyster suitable for transplanting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Planted', 'imp. & p. p.', 'of Plant');
  INSERT INTO words (word, word_type, description) VALUES  (    'Planting', 'p. pr. & vb. n.', 'of Plant');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plant', 'n.', 'To put in the ground and cover, as seed for growth; as, to\n   plant maize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plant', 'n.', 'To set in the ground for growth, as a young tree, or a\n   vegetable with roots.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plant', 'n.', 'To furnish, or fit out, with plants; as, to plant a garden,\n   an orchard, or a forest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plant', 'n.', 'To engender; to generate; to set the germ of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plant', 'n.', 'To furnish with a fixed and organized population; to settle;\n   to establish; as, to plant a colony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plant', 'n.', 'To introduce and establish the principles or seeds of; as,\n   to plant Christianity among the heathen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plant', 'n.', 'To set firmly; to fix; to set and direct, or point; as, to\n   plant cannon against a fort; to plant a standard in any place; to plant\n   ones feet on solid ground; to plant ones fist in anothers face.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plant', 'n.', 'To set up; to install; to instate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plant', 'v. i.', 'To perform the act of planting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plantable', 'a.', 'Capable of being planted; fit to be planted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plantage', 'n.', 'A word used once by Shakespeare to designate plants in\n   general, or anything that is planted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plantain', 'n.', 'A treelike perennial herb (Musa paradisiaca) of tropical\n   regions, bearing immense leaves and large clusters of the fruits called\n   plantains. See Musa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plantain', 'n.', 'The fruit of this plant. It is long and somewhat\n   cylindrical, slightly curved, and, when ripe, soft, fleshy, and covered\n   with a thick but tender yellowish skin. The plantain is a staple\n   article of food in most tropical countries, especially when cooked.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plantain', 'n.', 'Any plant of the genus Plantago, but especially the P.\n   major, a low herb with broad spreading radical leaves, and slender\n   spikes of minute flowers. It is a native of Europe, but now found near\n   the abode of civilized man in nearly all parts of the world.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plantal', 'a.', 'Belonging to plants; as, plantal life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plantar', 'a.', 'Of or pertaining to the sole of the foot; as, the plantar\n   arteries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plantation', 'n.', 'The act or practice of planting, or setting in the\n   earth for growth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plantation', 'n.', 'The place planted; land brought under cultivation; a\n   piece of ground planted with trees or useful plants; esp., in the\n   United States and West Indies, a large estate appropriated to the\n   production of the more important crops, and cultivated by laborers who\n   live on the estate; as, a cotton plantation; a coffee plantation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plantation', 'n.', 'An original settlement in a new country; a colony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plant-cane', 'n.', 'A stalk or shoot of sugar cane of the first growth from\n   the cutting. The growth of the second and following years is of\n   inferior quality, and is called rattoon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plant-eating', 'a.', 'Eating, or subsisting on, plants; as, a plant-eating\n   beetle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Planted', 'a.', 'Fixed in place, as a projecting member wrought on a\n   separate piece of stuff; as, a planted molding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Planter', 'n.', 'One who, or that which, plants or sows; as, a planterof\n   corn; a machine planter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Planter', 'n.', 'One who owns or cultivates a plantation; as, a sugar\n   planter; a coffee planter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Planter', 'n.', 'A colonist in a new or uncultivated territory; as, the\n   first planters in Virginia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plantership', 'n.', 'The occupation or position of a planter, or the\n   management of a plantation, as in the United States or the West Indies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Planticle', 'n.', 'A young plant, or plant in embryo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plantigrada', 'n. pl.', 'A subdivision of Carnivora having plantigrade\n   feet. It includes the bears, raccoons, and allied species.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plantigrade', 'a.', 'Walking on the sole of the foot; pertaining to the\n   plantigrades.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plantigrade', 'a.', 'Having the foot so formed that the heel touches the\n   ground when the leg is upright.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plantigrade', 'n.', 'A plantigrade animal, or one that walks or steps on\n   the sole of the foot, as man, and the bears.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Planting', 'n.', 'The act or operation of setting in the ground for\n   propagation, as seeds, trees, shrubs, etc.; the forming of plantations,\n   as of trees; the carrying on of plantations, as of sugar, coffee, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Planting', 'n.', 'That which is planted; a plantation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Planting', 'n.', 'The laying of the first courses of stone in a foundation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plantless', 'a.', 'Without plants; barren of vegetation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plantlet', 'n.', 'A little plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plantocracy', 'n.', 'Government by planters; planters, collectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plantule', 'n.', 'The embryo which has begun its development in the act of\n   germination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Planulae', 'pl. ', 'of Planula');
  INSERT INTO words (word, word_type, description) VALUES  (    'Planula', 'n.', 'In embryonic development, a vesicle filled with fluid,\n   formed from the morula by the divergence of its cells in such a manner\n   as to give rise to a central space, around which the cells arrange\n   themselves as an envelope; an embryonic form intermediate between the\n   morula and gastrula. Sometimes used as synonymous with gastrula.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Planula', 'n.', 'The very young, free-swimming larva of the coelenterates.\n   It usually has a flattened oval or oblong form, and is entirely covered\n   with cilia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Planxty', 'n.', 'An Irish or Welsh melody for the harp, sometimes of a\n   mournful character.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plaque', 'n.', 'Any flat, thin piece of metal, clay, ivory, or the like,\n   used for ornament, or for painting pictures upon, as a slab, plate,\n   dish, or the like, hung upon a wall; also, a smaller decoration worn on\n   the person, as a brooch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plash', 'v.', 'A small pool of standing water; a puddle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plash', 'v.', 'A dash of water; a splash.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plashed', 'imp. & p. p.', 'of Plash');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plashing', 'p. pr. & vb. n.', 'of Plash');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plash', 'v. i.', 'To dabble in water; to splash.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plash', 'v. t.', 'To splash, as water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plash', 'v. t.', 'To splash or sprinkle with coloring matter; as, to plash\n   a wall in imitation of granite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plashed', 'imp. & p. p.', 'of Plash');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plashing', 'p. pr. & vb. n.', 'of Plash');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plash', 'v. t.', 'To cut partly, or to bend and intertwine the branches of;\n   as, to plash a hedge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plash', 'n.', 'The branch of a tree partly cut or bent, and bound to, or\n   intertwined with, other branches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plashet', 'n.', 'A small pond or pool; a puddle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plashing', 'n.', 'The cutting or bending and intertwining the branches of\n   small trees, as in hedges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plashing', 'n.', 'The dashing or sprinkling of coloring matter on the walls\n   of buildings, to imitate granite, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plashoot', 'n.', 'A hedge or fence formed of branches of trees interlaced,\n   or plashed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plashy', 'a.', 'Watery; abounding with puddles; splashy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plashy', 'a.', 'Specked, as if plashed with color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plasm', 'n.', 'A mold or matrix in which anything is cast or formed to a\n   particular shape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plasm', 'n.', 'Same as Plasma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plasma', 'n.', 'A variety of quartz, of a color between grass green and\n   leek green, which is found associated with common chalcedony. It was\n   much esteemed by the ancients for making engraved ornaments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plasma', 'n.', 'The viscous material of an animal or vegetable cell, out of\n   which the various tissues are formed by a process of differentiation;\n   protoplasm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plasma', 'n.', 'Unorganized material; elementary matter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plasma', 'n.', 'A mixture of starch and glycerin, used as a substitute for\n   ointments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plasmatic', 'a.', 'Alt. of Plasmatical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plasmatical', 'a.', 'Forming; shaping; molding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plasmatical', 'a.', 'Of or pertaining to plasma; having the character of\n   plasma; containing, or conveying, plasma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plasmation', 'n.', 'The act of forming or molding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plasmator', 'n.', 'A former; a fashioner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plasmature', 'n.', 'Form; mold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plasmic', 'a.', 'Of, pertaining to, or connected with, plasma; plasmatic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plasmid', 'n.', 'A piece of DNA, usually circular, functioning as part of\n   the genetic material of a cell, not integrated with the chromosome and\n   replicating independently of the chromosome, but transferred, like the\n   chromosome, to subsequent generations. In bacteria, plasmids often\n   carry the genes for antibiotic resistance; they are exploited in\n   genetic engineering as the vehicles for introduction of extraneous DNA\n   into cells, to alter the genetic makeup of the cell. The cells thus\n   altered may produce desirable proteins which are extracted and used; in\n   the case of genetically altered plant cells, the altered cells may grow\n   into complete plants with changed properties, as for example, increased\n   resistance to disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plasmin', 'n.', 'A proteid body, separated by some physiologists from blood\n   plasma. It is probably identical with fibrinogen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plasmodial', 'a.', 'Of or pertaining to, or like, a plasmodium; as, the\n   plasmodial form of a life cycle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plasmodia', 'pl. ', 'of Plasmodium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plasmodium', 'n.', 'A jellylike mass of free protoplasm, without any union\n   of amoeboid cells, and endowed with life and power of motion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plasmodium', 'n.', 'A naked mobile mass of protoplasm, formed by the union\n   of several amoebalike young, and constituting one of the stages in the\n   life cycle of Mycetozoa and other low organisms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plasmogen', 'n.', 'The important living portion of protoplasm, considered a\n   chemical substance of the highest elaboration. Germ plasm and idioplasm\n   are forms of plasmogen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plasson', 'n.', 'The albuminous material composing the body of a cytode.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plaster', 'n.', 'An external application of a consistency harder than\n   ointment, prepared for use by spreading it on linen, leather, silk, or\n   other material. It is adhesive at the ordinary temperature of the body,\n   and is used, according to its composition, to produce a medicinal\n   effect, to bind parts together, etc.; as, a porous plaster; sticking\n   plaster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plaster', 'n.', 'A composition of lime, water, and sand, with or without\n   hair as a bond, for coating walls, ceilings, and partitions of houses.\n   See Mortar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plaster', 'n.', 'Calcined gypsum, or plaster of Paris, especially when\n   ground, as used for making ornaments, figures, moldings, etc.; or\n   calcined gypsum used as a fertilizer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plastered', 'imp. & p. p.', 'of Plaster');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plastering', 'p. pr. & vb. n.', 'of Plaster');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plaster', 'v. t.', 'To cover with a plaster, as a wound or sore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plaster', 'v. t.', 'To overlay or cover with plaster, as the ceilings and\n   walls of a house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plaster', 'v. t.', 'Fig.: To smooth over; to cover or conceal the defects\n   of; to hide, as with a covering of plaster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plasterer', 'n.', 'One who applies plaster or mortar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plasterer', 'n.', 'One who makes plaster casts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plastering', 'n.', 'Same as Plaster, n., 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plastering', 'n.', 'The act or process of overlaying with plaster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plastering', 'n.', 'A covering of plaster; plasterwork.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plasterly', 'a.', 'Resembling plaster of Paris.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plasterwork', 'n.', 'Plastering used to finish architectural constructions,\n   exterior or interior, especially that used for the lining of rooms.\n   Ordinarly, mortar is used for the greater part of the work, and pure\n   plaster of Paris for the moldings and ornaments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plastery', 'a.', 'Of the nature of plaster.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-plastic', '', 'A combining form signifying developing, forming, growing;\n   as, heteroplastic, monoplastic, polyplastic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plastic', 'a.', 'Having the power to give form or fashion to a mass of\n   matter; as, the plastic hand of the Creator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plastic', 'a.', 'Capable of being molded, formed, or modeled, as clay or\n   plaster; -- used also figuratively; as, the plastic mind of a child.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plastic', 'a.', 'Pertaining or appropriate to, or characteristic of,\n   molding or modeling; produced by, or appearing as if produced by,\n   molding or modeling; -- said of sculpture and the kindred arts, in\n   distinction from painting and the graphic arts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'plastic', 'n.', 'a substance composed predominantly of a synthetic organic\n   high polymer capable of being cast or molded; many varieties of plastic\n   are used to produce articles of commerce (after 1900). [MW10 gives\n   origin of word as 1905]');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plastical', 'a.', 'See Plastic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plastically', 'adv.', 'In a plastic manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plasticity', 'n.', 'The quality or state of being plastic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plasticity', 'n.', 'Plastic force.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plastid', 'n.', 'Alt. of Plastide');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plastide', 'n.', 'A formative particle of albuminous matter; a monad; a\n   cytode. See the Note under Morphon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plastide', 'n.', 'One of the many minute granules found in the protoplasm\n   of vegetable cells. They are divided by their colors into three\n   classes, chloroplastids, chromoplastids, and leucoplastids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plastidozoa', 'n. pl.', 'Same as Protoza.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plastidule', 'n.', 'One of the small particles or organic molecules of\n   protoplasm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plastin', 'n.', 'A substance associated with nuclein in cell nuclei, and by\n   some considered as the fundamental substance of the nucleus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plastography', 'n.', 'The art of forming figures in any plastic material.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plastography', 'n.', 'Imitation of handwriting; forgery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plastron', 'n.', 'A piece of leather stuffed or padded, worn by fencers to\n   protect the breast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plastron', 'n.', 'An iron breastplate, worn under the hauberk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plastron', 'n.', 'The ventral shield or shell of tortoises and turtles. See\n   Testudinata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plastron', 'n.', 'A trimming for the front of a womans dress, made of a\n   different material, and narrowing from the shoulders to the waist.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-plasty', '', 'A combining form denoting the act or process of forming,\n   development, growth; as, autoplasty, perineoplasty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Platted', 'imp. & p. p.', 'of Plat');
  INSERT INTO words (word, word_type, description) VALUES  (    'Platting', 'p. pr. & vb. n.', 'of Plat');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plat', 'v. t.', 'To form by interlaying interweaving; to braid; to plait.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plat', 'n.', 'Work done by platting or braiding; a plait.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plat', 'n.', 'A small piece or plot of ground laid out with some design, or\n   for a special use; usually, a portion of flat, even ground.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plat', 'v. t.', 'To lay out in plats or plots, as ground.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plat', 'n.', 'Plain; flat; level.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plat', 'adv.', 'Plainly; flatly; downright.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plat', 'adv.', 'Flatly; smoothly; evenly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plat', 'n.', 'The flat or broad side of a sword.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plat', 'n.', 'A plot; a plan; a design; a diagram; a map; a chart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Platan', 'n.', 'The plane tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Platanist', 'n.', 'The soosoo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Platanus', 'n.', 'A genus of trees; the plane tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Platband', 'n.', 'A border of flowers in a garden, along a wall or a\n   parterre; hence, a border.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Platband', 'n.', 'A flat molding, or group of moldings, the width of which\n   much exceeds its projection, as the face of an architrave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Platband', 'n.', 'A list or fillet between the flutings of a column.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plate', 'n.', 'A flat, or nearly flat, piece of metal, the thickness of\n   which is small in comparison with the other dimensions; a thick sheet\n   of metal; as, a steel plate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plate', 'n.', 'Metallic armor composed of broad pieces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plate', 'n.', 'Domestic vessels and utensils, as flagons, dishes, cups,\n   etc., wrought in gold or silver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plate', 'n.', 'Metallic ware which is plated, in distinction from that\n   which is genuine silver or gold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plate', 'n.', 'A small, shallow, and usually circular, vessel of metal or\n   wood, or of earth glazed and baked, from which food is eaten at table.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plate', 'n.', 'A piece of money, usually silver money.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plate', 'n.', 'A piece of metal on which anything is engraved for the\n   purpose of being printed; hence, an impression from the engraved metal;\n   as, a book illustrated with plates; a fashion plate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plate', 'n.', 'A page of stereotype, electrotype, or the like, for printing\n   from; as, publishers plates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plate', 'n.', 'That part of an artificial set of teeth which fits to the\n   mouth, and holds the teeth in place. It may be of gold, platinum,\n   silver, rubber, celluloid, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plate', 'n.', 'A horizontal timber laid upon a wall, or upon corbels\n   projecting from a wall, and supporting the ends of other timbers; also\n   used specifically of the roof plate which supports the ends of the roof\n   trusses or, in simple work, the feet of the rafters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plate', 'n.', 'A roundel of silver or tinctured argent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plate', 'n.', 'A sheet of glass, porcelain, metal, etc., with a coating\n   that is sensitive to light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plate', 'n.', 'A prize giving to the winner in a contest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plated', 'imp. & p. p.', 'of Plate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plating', 'p. pr. & vb. n.', 'of Plate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plate', 'v. t.', 'To cover or overlay with gold, silver, or other metals,\n   either by a mechanical process, as hammering, or by a chemical process,\n   as electrotyping.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plate', 'v. t.', 'To cover or overlay with plates of metal; to arm with\n   metal for defense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plate', 'v. t.', 'To adorn with plated metal; as, a plated harness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plate', 'v. t.', 'To beat into thin, flat pieces, or laminae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plate', 'v. t.', 'To calender; as, to plate paper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plateaux', 'pl. ', 'of Plateau');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plateaus', 'pl. ', 'of Plateau');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plateau', 'n.', 'A flat surface; especially, a broad, level, elevated area\n   of land; a table-land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plateau', 'n.', 'An ornamental dish for the table; a tray or salver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Platefuls', 'pl. ', 'of Plateful');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plateful', 'n.', 'Enough to fill a plate; as much as a plate will hold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plate-gilled', 'a.', 'Having flat, or leaflike, gills, as the bivalve\n   mollusks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Platel', 'n.', 'A small dish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Platen', 'n.', 'The part of a printing press which presses the paper\n   against the type and by which the impression is made.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Platen', 'n.', 'Hence, an analogous part of a typewriter, on which the\n   paper rests to receive an impression.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Platen', 'n.', 'The movable table of a machine tool, as a planer, on which\n   the work is fastened, and presented to the action of the tool; -- also\n   called table.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plater', 'n.', 'One who plates or coats articles with gold or silver; as, a\n   silver plater.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plater', 'n.', 'A machine for calendering paper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plateresque', 'a.', 'Resembling silver plate; -- said of certain\n   architectural ornaments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Platetrope', 'n.', 'One of a pair of a paired organs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Platform', 'n.', 'A plat; a plan; a sketch; a model; a pattern. Used also\n   figuratively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Platform', 'n.', 'A place laid out after a model.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Platform', 'n.', 'Any flat or horizontal surface; especially, one that is\n   raised above some particular level, as a framework of timber or boards\n   horizontally joined so as to form a roof, or a raised floor, or portion\n   of a floor; a landing; a dais; a stage, for speakers, performers, or\n   workmen; a standing place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Platform', 'n.', 'A declaration of the principles upon which a person, a\n   sect, or a party proposes to stand; a declared policy or system; as,\n   the Saybrook platform; a political platform.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Platform', 'n.', 'A light deck, usually placed in a section of the hold or\n   over the floor of the magazine. See Orlop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Platform', 'v. t.', 'To place on a platform.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Platform', 'v. t.', 'To form a plan of; to model; to lay out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plathelminth', 'n.', 'One of the Platyelminthes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plathelminthes', 'n. pl.', 'Same as Platyelminthes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Platin', 'n.', 'See Platen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Platina', 'n.', 'Platinum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plating', 'n.', 'The art or process of covering anything with a plate or\n   plates, or with metal, particularly of overlaying a base or dull metal\n   with a thin plate of precious or bright metal, as by mechanical means\n   or by electro-magnetic deposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plating', 'n.', 'A thin coating of metal laid upon another metal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plating', 'n.', 'A coating or defensive armor of metal (usually steel)\n   plates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Platinic', 'a.', 'Of, pertaining to, or containing, platinum; -- used\n   specifically to designate those compounds in which the element has a\n   higher valence, as contrasted with the platinous compounds; as,\n   platinic chloride (PtCl4).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Platinichloric', 'a.', 'Of, pertaining to, or designating, an acid\n   consisting of platinic chloride and hydrochloric acid, and obtained as\n   a brownish red crystalline substance, called platinichloric, or\n   chloroplatinic, acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Platiniferous', 'a.', 'Yielding platinum; as, platiniferous sand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Platiniridium', 'n.', 'A natural alloy of platinum and iridium occurring in\n   grayish metallic rounded or cubical grains with platinum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Platinized', 'imp. & p. p.', 'of Platinize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Platinizing', 'p. pr. & vb. n.', 'of Platinize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Platinize', 'v. t.', 'To cover or combine with platinum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Platinochloric', 'a.', 'Pertaining to, derived from, or designating, an\n   acid consisting of platinous chloride and hydrochloric acid, called\n   platinochloric, / chloroplatinous, acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Platinochloride', 'n.', 'A double chloride of platinum and some other metal\n   or radical; a salt of platinochloric acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Platinocyanic', 'a.', 'Pertaining to, derived from, or designating, an acid\n   compound of platinous cyanide and hydrocyanic acid. It is obtained as a\n   cinnaber-red crystalline substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Platinocyanide', 'n.', 'A double cyanide of platinum and some other metal\n   or radical; a salt of platinocyanic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Platinode', 'n.', 'A cathode.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Platinoid', 'a.', 'Resembling platinum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Platinoid', 'n.', 'An alloy of German silver containing tungsten; -- used\n   for forming electrical resistance coils and standards.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Platinotype', 'n.', 'A permanent photographic picture or print in platinum\n   black.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Platinotype', 'n.', 'The process by which such pictures are produced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Platinous', 'a.', 'Of, pertaining to, or containing, platinum; -- used\n   specifically to designate those compounds in which the element has a\n   lower valence, as contrasted with the platinic compounds; as, platinous\n   chloride (PtCl2).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Platinum', 'n.', 'A metallic element, intermediate in value between silver\n   and gold, occurring native or alloyed with other metals, also as the\n   platinum arsenide (sperrylite). It is heavy tin-white metal which is\n   ductile and malleable, but very infusible, and characterized by its\n   resistance to strong chemical reagents. It is used for crucibles, for\n   stills for sulphuric acid, rarely for coin, and in the form of foil and\n   wire for many purposes. Specific gravity 21.5. Atomic weight 194.3.\n   Symbol Pt. Formerly called platina.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Platitude', 'n.', 'The quality or state of being flat, thin, or insipid;\n   flat commonness; triteness; staleness of ideas of language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Platitude', 'n.', 'A thought or remark which is flat, dull, trite, or weak;\n   a truism; a commonplace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Platitudinarian', 'n.', 'One addicted to uttering platitudes, or stale and\n   insipid truisms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Platitudinize', 'v. i.', 'To utter platitudes or truisms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Platitudinous', 'a.', 'Abounding in platitudes; of the nature of\n   platitudes; uttering platitudes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Platly', 'a.', 'Flatly. See Plat, a.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Platness', 'n.', 'Flatness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Platometer', 'n.', 'See Planimeter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Platonic', 'a.', 'Alt. of Platonical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Platonical', 'a.', 'Of or pertaining to Plato, or his philosophy, school,\n   or opinions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Platonical', 'a.', 'Pure, passionless; nonsexual; philosophical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Platonic', 'n.', 'A follower of Plato; a Platonist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Platonically', 'adv.', 'In a Platonic manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Platonism', 'n.', 'The doctrines or philosophy by Plato or of his\n   followers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Platonism', 'n.', 'An elevated rational and ethical conception of the laws\n   and forces of the universe; sometimes, imaginative or fantastic\n   philosophical notions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Platonist', 'n.', 'One who adheres to the philosophy of Plato; a follower\n   of Plato.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Platonized', 'imp. & p. p.', 'of Platonize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Platonizing', 'p. pr. & vb. n.', 'of Platonize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Platonize', 'v. i.', 'To adopt the opinion of Plato or his followers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Platonize', 'v. t.', 'To explain by, or accomodate to, the Platonic\n   philosophy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Platonizer', 'n.', 'One who Platonizes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Platoon', 'n.', 'Formerly, a body of men who fired together; also, a small\n   square body of soldiers to strengthen the angles of a hollow square.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Platoon', 'n.', 'Now, in the United States service, half of a company.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Platt', 'n.', 'See Lodge, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plattdeutsch', 'n.', 'The modern dialects spoken in the north of Germany,\n   taken collectively; modern Low German. See Low German, under German.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Platten', 'a.', 'To flatten and make into sheets or plates; as, to platten\n   cylinder glass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Platter', 'n.', 'One who plats or braids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Platter', 'n.', 'A large plate or shallow dish on which meat or other food\n   is brought to the table.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Platter-faced', 'a.', 'Having a broad, flat face.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Platting', 'n.', 'Plaited strips or bark, cane, straw, etc., used for\n   making hats or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Platy', 'a.', 'Like a plate; consisting of plates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Platy-', '', 'A combining form from Gr. platy`s broad, wide, flat; as,\n   platypus, platycephalous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Platycephalic', 'a.', 'Alt. of Platycephalous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Platycephalous', 'a.', 'Broad-headed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Platycnemic', 'a.', 'Of, relating to, or characterized by, platycnemism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Platycnemism', 'n.', 'Lateral flattening of the tibia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Platycoelian', 'a.', 'Flat at the anterior and concave at the posterior\n   end; -- said of the centra of the vertebrae of some extinct dinouaurs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Platyelminthes', 'n. pl.', 'A class of helminthes including the cestodes,\n   or tapeworms, the trematodes, and the turbellarians. Called also\n   flatworms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Platyhelmia', 'n. pl.', 'Same as Platyelminthes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Platymeter', 'n.', 'An apparatus for measuring the capacity of condensers,\n   or the inductive capacity of dielectrics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Platypod', 'n.', 'An animal having broad feet, or a broad foot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Platypoda', 'n. pl.', 'Same as Prosobranchiata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Platyptera', 'n. pl.', 'A division of Pseudoneuroptera including the\n   species which have four broad, flat wings, as the termites, or\n   white-ants, and the stone flies (Perla).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Platypus', 'n.', 'The duck mole. See under Duck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Platyrhine', 'a.', 'Having the nose broad; -- opposed to leptorhine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Platyrhine', 'n.', 'One of the Platyrhini.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Platyrhini', 'n. pl.', 'A division of monkeys, including the American\n   species, which have a broad nasal septum, thirty-six teeth, and usually\n   a prehensile tail. See Monkey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plaud', 'v. t.', 'To applaud.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plaudit', 'n.', 'A mark or expression of applause; praise bestowed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plauditory', 'a.', 'Applauding; commending.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plausibility', 'n.', 'Something worthy of praise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plausibility', 'n.', 'The quality of being plausible; speciousness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plausibility', 'n.', 'Anything plausible or specious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plausible', 'a.', 'Worthy of being applauded; praiseworthy; commendable;\n   ready.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plausible', 'a.', 'Obtaining approbation; specifically pleasing; apparently\n   right; specious; as, a plausible pretext; plausible manners; a\n   plausible delusion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plausible', 'a.', 'Using specious arguments or discourse; as, a plausible\n   speaker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plausibleize', 'v. t.', 'To render plausible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plausibleness', 'n.', 'Quality of being plausible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plausibly', 'adv.', 'In a plausible manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plausibly', 'adv.', 'Contentedly, readily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plausive', 'a.', 'Applauding; manifesting praise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plausive', 'a.', 'Plausible, specious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Played', 'imp. & p. p.', 'of Play');
  INSERT INTO words (word, word_type, description) VALUES  (    'Playing', 'p. pr. & vb. n.', 'of Play');
  INSERT INTO words (word, word_type, description) VALUES  (    'Play', 'n.', 'To engage in sport or lively recreation; to exercise for the\n   sake of amusement; to frolic; to spot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Play', 'n.', 'To act with levity or thoughtlessness; to trifle; to be\n   careless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Play', 'n.', 'To contend, or take part, in a game; as, to play ball; hence,\n   to gamble; as, he played for heavy stakes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Play', 'n.', 'To perform on an instrument of music; as, to play on a flute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Play', 'n.', 'To act; to behave; to practice deception.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Play', 'n.', 'To move in any manner; especially, to move regularly with\n   alternate or reciprocating motion; to operate; to act; as, the fountain\n   plays.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Play', 'n.', 'To move gayly; to wanton; to disport.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Play', 'n.', 'To act on the stage; to personate a character.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Play', 'v. t.', 'To put in action or motion; as, to play cannon upon a\n   fortification; to play a trump.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Play', 'v. t.', 'To perform music upon; as, to play the flute or the organ.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Play', 'v. t.', 'To perform, as a piece of music, on an instrument; as, to\n   play a waltz on the violin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Play', 'v. t.', 'To bring into sportive or wanton action; to exhibit in\n   action; to execute; as, to play tricks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Play', 'v. t.', 'To act or perform (a play); to represent in music action;\n   as, to play a comedy; also, to act in the character of; to represent by\n   acting; to simulate; to behave like; as, to play King Lear; to play the\n   woman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Play', 'v. t.', 'To engage in, or go together with, as a contest for\n   amusement or for a wager or prize; as, to play a game at baseball.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Play', 'v. t.', 'To keep in play, as a hooked fish, in order to land it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Play', 'n.', 'Amusement; sport; frolic; gambols.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Play', 'n.', 'Any exercise, or series of actions, intended for amusement or\n   diversion; a game.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Play', 'n.', 'The act or practice of contending for victory, amusement, or\n   a prize, as at dice, cards, or billiards; gaming; as, to lose a fortune\n   in play.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Play', 'n.', 'Action; use; employment; exercise; practice; as, fair play;\n   sword play; a play of wit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Play', 'n.', 'A dramatic composition; a comedy or tragedy; a composition in\n   which characters are represented by dialogue and action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Play', 'n.', 'The representation or exhibition of a comedy or tragedy; as,\n   he attends ever play.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Play', 'n.', 'Performance on an instrument of music.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Play', 'n.', 'Motion; movement, regular or irregular; as, the play of a\n   wheel or piston; hence, also, room for motion; free and easy action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Play', 'n.', 'Hence, liberty of acting; room for enlargement or display;\n   scope; as, to give full play to mirth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Playa', 'n.', 'A beach; a strand; in the plains and deserts of Texas, New\n   Mexico, and Arizona, a broad, level spot, on which subsequently becomes\n   dry by evaporation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Playbill', 'n.', 'A printed programme of a play, with the parts assigned to\n   the actors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Playbook', 'n.', 'A book of dramatic compositions; a book of the play.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Playday', 'n.', 'A day given to play or diversion; a holiday.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Player', 'n.', 'One who plays, or amuses himself; one without serious aims;\n   an idler; a trifler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Player', 'n.', 'One who plays any game.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Player', 'n.', 'A dramatic actor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Player', 'n.', 'One who plays on an instrument of music.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Player', 'n.', 'A gamester; a gambler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Playfellow', 'n.', 'A companion in amusements or sports; a playmate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Playfere', 'n.', 'A playfellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Playful', 'a.', 'Sportive; gamboling; frolicsome; indulging a sportive\n   fancy; humorous; merry; as, a playful child; a playful writer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Playgame', 'n.', 'Play of children.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Playgoer', 'n.', 'One who frequents playhouses, or attends dramatic\n   performances.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Playgoing', 'a.', 'Frequenting playhouses; as, the playgoing public.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Playgoing', 'n.', 'The practice of going to plays.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Playground', 'n.', 'A piece of ground used for recreation; as, the\n   playground of a school.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Playhouse', 'n.', 'A building used for dramatic exhibitions; a theater.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Playhouse', 'n.', 'A house for children to play in; a toyhouse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Playing', '', 'a. & vb. n. of Play.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Playmaker', 'n.', 'A playwright.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Playmate', 'n.', 'A companion in diversions; a playfellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Playsome', 'a.', 'Playful; wanton; sportive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Playte', 'n.', 'See Pleyt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plaything', 'n.', 'A thing to play with; a toy; anything that serves to\n   amuse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Playtime', 'n.', 'Time for play or diversion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Playwright', 'n.', 'A maker or adapter of plays.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Playwriter', 'n.', 'A writer of plays; a dramatist; a playwright.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plaza', 'n.', 'A public square in a city or town.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plea', 'n.', 'That which is alleged by a party in support of his cause; in\n   a stricter sense, an allegation of fact in a cause, as distinguished\n   from a demurrer; in a still more limited sense, and in modern practice,\n   the defendants answer to the plaintiffs declaration and demand. That\n   which the plaintiff alleges in his declaration is answered and repelled\n   or justified by the defendants plea. In chancery practice, a plea is a\n   special answer showing or relying upon one or more things as a cause\n   why the suit should be either dismissed, delayed, or barred. In\n   criminal practice, the plea is the defendants formal answer to the\n   indictment or information presented against him.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plea', 'n.', 'A cause in court; a lawsuit; as, the Court of Common Pleas.\n   See under Common.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plea', 'n.', 'That which is alleged or pleaded, in defense or in\n   justification; an excuse; an apology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plea', 'n.', 'An urgent prayer or entreaty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleached', 'imp. & p. p.', 'of Pleach');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleaching', 'p. pr. & vb. n.', 'of Pleach');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleach', 'v. t.', 'To unite by interweaving, as branches of trees; to\n   plash; to interlock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleaded', 'imp. & p. p.', 'of Plead');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plead', '', 'of Plead');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pled', '', 'of Plead');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleading', 'p. pr. & vb. n.', 'of Plead');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plead', 'v. t.', 'To argue in support of a claim, or in defense against the\n   claim of another; to urge reasons for or against a thing; to attempt to\n   persuade one by argument or supplication; to speak by way of\n   persuasion; as, to plead for the life of a criminal; to plead with a\n   judge or with a father.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plead', 'v. t.', 'To present an answer, by allegation of fact, to the\n   declaration of a plaintiff; to deny the plaintiffs declaration and\n   demand, or to allege facts which show that ought not to recover in the\n   suit; in a less strict sense, to make an allegation of fact in a cause;\n   to carry on the allegations of the respective parties in a cause; to\n   carry on a suit or plea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plead', 'v. t.', 'To contend; to struggle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plead', 'v. t.', 'To discuss, defend, and attempt to maintain by arguments\n   or reasons presented to a tribunal or person having uthority to\n   determine; to argue at the bar; as, to plead a cause before a court or\n   jury.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plead', 'v. t.', 'To allege or cite in a legal plea or defense, or for\n   repelling a demand in law; to answer to an indictment; as, to plead\n   usury; to plead statute of limitations; to plead not guilty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plead', 'v. t.', 'To allege or adduce in proof, support, or vendication; to\n   offer in excuse; as, the law of nations may be pleaded in favor of the\n   rights of ambassadors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleadable', 'a.', 'Capable of being pleaded; capable of being alleged in\n   proof, defense, or vindication; as, a right or privilege pleadable at\n   law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleader', 'n.', 'One who pleads; one who argues for or against; an\n   advotate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleader', 'n.', 'One who draws up or forms pleas; the draughtsman of pleas\n   or pleadings in the widest sense; as, a special pleader.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleading', 'n.', 'The act of advocating, defending, or supporting, a cause\n   by arguments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleadingly', 'adv.', 'In a pleading manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleadings', 'n. pl.', 'The mutual pleas and replies of the plaintiff and\n   defendant, or written statements of the parties in support of their\n   claims, proceeding from the declaration of the plaintiff, until issue\n   is joined, and the question made to rest on some single point.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleasance', 'n.', 'Pleasure; merriment; gayety; delight; kindness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleasance', 'n.', 'A secluded part of a garden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleasant', 'a.', 'Pleasing; grateful to the mind or to the senses;\n   agreeable; as, a pleasant journey; pleasant weather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleasant', 'a.', 'Cheerful; enlivening; gay; sprightly; humorous; sportive;\n   as, pleasant company; a pleasant fellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleasant', 'n.', 'A wit; a humorist; a buffoon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleasantly', 'adv.', 'In a pleasant manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleasantness', 'n.', 'The state or quality of being pleasant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleasantries', 'pl. ', 'of Pleasantry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleasantry', 'n.', 'That which denotes or promotes pleasure or good humor;\n   cheerfulness; gayety; merriment; especially, an agreeable playfulness\n   in conversation; a jocose or humorous remark; badinage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleasant-tongued', 'a.', 'Of pleasing speech.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleased', 'imp. & p. p.', 'of Please');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleasing', 'p. pr. & vb. n.', 'of Please');
  INSERT INTO words (word, word_type, description) VALUES  (    'Please', 'v. t.', 'To give pleasure to; to excite agreeable sensations or\n   emotions in; to make glad; to gratify; to content; to satisfy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Please', 'v. t.', 'To have or take pleasure in; hence, to choose; to wish;\n   to desire; to will.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Please', 'v. t.', 'To be the will or pleasure of; to seem good to; -- used\n   impersonally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Please', 'v. i.', 'To afford or impart pleasure; to excite agreeable\n   emotions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Please', 'v. i.', 'To have pleasure; to be willing, as a matter of\n   affording pleasure or showing favor; to vouchsafe; to consent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleased', 'a.', 'Experiencing pleasure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleaseman', 'n.', 'An officious person who courts favor servilely; a\n   pickthank.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleaser', 'n.', 'One who pleases or gratifies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleasing', 'a.', 'Giving pleasure or satisfaction; causing agreeable\n   emotion; agreeable; delightful; as, a pleasing prospect; pleasing\n   manners.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleasing', 'n.', 'An object of pleasure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleasurable', 'a.', 'Capable of affording pleasure or satisfaction;\n   gratifying; abounding in pleasantness or pleasantry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleasure', 'n.', 'The gratification of the senses or of the mind; agreeable\n   sensations or emotions; the excitement, relish, or happiness produced\n   by the expectation or the enjoyment of something good, delightful, or\n   satisfying; -- opposed to pain, sorrow, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleasure', 'n.', 'Amusement; sport; diversion; self-indulgence; frivolous\n   or dissipating enjoyment; hence, sensual gratification; -- opposed to\n   labor, service, duty, self-denial, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleasure', 'n.', 'What the will dictates or prefers as gratifying or\n   satisfying; hence, will; choice; wish; purpose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleasure', 'n.', 'That which pleases; a favor; a gratification.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleasured', 'imp. & p. p.', 'of Pleasure');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleasuring', 'p. pr. & vb. n.', 'of Pleasure');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleasure', 'v. t.', 'To give or afford pleasure to; to please; to gratify.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleasure', 'v. i.', 'To take pleasure; to seek pursue pleasure; as, to go\n   pleasuring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleasureful', 'a.', 'Affording pleasure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleasureless', 'a.', 'Devoid of pleasure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleasurer', 'n.', 'A pleasure seeker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleasurist', 'n.', 'A person devoted to worldly pleasure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleat', 'n. & v. t.', 'See Plait.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plebe', 'n.', 'The common people; the mob.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plebe', 'n.', 'A member of the lowest class in the military academy at West\n   Point.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plebeian', 'a.', 'Of or pertaining to the Roman plebs, or common people.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plebeian', 'a.', 'Of or pertaining to the common people; vulgar; common;\n   as, plebeian sports; a plebeian throng.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plebeian', 'n.', 'One of the plebs, or common people of ancient Rome, in\n   distinction from patrician.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plebeian', 'n.', 'One of the common people, or lower rank of men.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plebeiance', 'n.', 'Plebeianism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plebeiance', 'n.', 'Plebeians, collectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plebeianism', 'n.', 'The quality or state of being plebeian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plebeianism', 'n.', 'The conduct or manners of plebeians; vulgarity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plebeianized', 'imp. & p. p.', 'of Plebeianize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plebeianizing', 'p. pr. & vb. n.', 'of Plebeianize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plebeianize', 'v. t.', 'To render plebeian, common, or vulgar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plebicolist', 'n.', 'One who flatters, or courts the favor of, the common\n   people; a demagogue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plebification', 'n.', 'A rendering plebeian; the act of vulgarizing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plebiscitary', 'a.', 'Of or pertaining to plebiscite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plebiscite', 'n.', 'A vote by universal male suffrage; especially, in\n   France, a popular vote, as first sanctioned by the National\n   Constitution of 1791.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plebiscitum', 'n.', 'A law enacted by the common people, under the\n   superintendence of a tribune or some subordinate plebeian magistrate,\n   without the intervention of the senate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plectile', 'a.', 'Woven; plaited.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plectognath', 'a.', 'Of or pertaining to the Plectognathi.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plectognath', 'n.', 'One of the Plectognathi.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plectognathi', 'n. pl.', 'An order of fishes generally having the maxillary\n   bone united with the premaxillary, and the articular united with the\n   dentary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plectognathic', 'a.', 'Alt. of Plec-tognathous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plec-tognathous', 'a.', 'Of or pertaining to the Plectognathi.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plectospondyli', 'n. pl.', 'An extensive suborder of fresh-water\n   physostomous fishes having the anterior vertebrae united and much\n   modified; the Eventognathi.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plectospondylous', 'a.', 'Of or pertaining to the Plectospondyli.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plectra', 'pl. ', 'of Plectrum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plectrum', 'n.', 'A small instrument of ivory, wood, metal, or quill, used\n   in playing upon the lyre and other stringed instruments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pled', '', 'imp. & p. p. of Plead');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pledge', 'n.', 'The transfer of possession of personal property from a\n   debtor to a creditor as security for a debt or engagement; also, the\n   contract created between the debtor and creditor by a thing being so\n   delivered or deposited, forming a species of bailment; also, that which\n   is so delivered or deposited; something put in pawn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pledge', 'n.', 'A person who undertook, or became responsible, for another;\n   a bail; a surety; a hostage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pledge', 'n.', 'A hypothecation without transfer of possession.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pledge', 'n.', 'Anything given or considered as a security for the\n   performance of an act; a guarantee; as, mutual interest is the best\n   pledge for the performance of treaties.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pledge', 'n.', 'A promise or agreement by which one binds ones self to do,\n   or to refrain from doing, something; especially, a solemn promise in\n   writing to refrain from using intoxicating liquors or the like; as, to\n   sign the pledge; the mayor had made no pledges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pledge', 'n.', 'A sentiment to which assent is given by drinking ones\n   health; a toast; a health.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pledged', 'imp. & p. p.', 'of Pledge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pledging', 'p. pr. & vb. n.', 'of Pledge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pledge', 'n.', 'To deposit, as a chattel, in pledge or pawn; to leave in\n   possession of another as security; as, to pledge ones watch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pledge', 'n.', 'To give or pass as a security; to guarantee; to engage; to\n   plight; as, to pledge ones word and honor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pledge', 'n.', 'To secure performance of, as by a pledge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pledge', 'n.', 'To bind or engage by promise or declaration; to engage\n   solemnly; as, to pledge ones self.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pledge', 'n.', 'To invite another to drink, by drinking of the cup first,\n   and then handing it to him, as a pledge of good will; hence, to drink\n   the health of; to toast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pledgee', 'n.', 'The one to whom a pledge is given, or to whom property\n   pledged is delivered.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pledgeless', 'a.', 'Having no pledge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pledgeor', 'n.', 'Alt. of Pledgor');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pledgor', 'n.', 'One who pledges, or delivers anything in pledge; a\n   pledger; -- opposed to pledgee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pledger', 'n.', 'One who pledges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pledgery', 'n.', 'A pledging; suretyship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pledget', 'n.', 'A small plug.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pledget', 'n.', 'A string of oakum used in calking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pledget', 'n.', 'A compress, or small flat tent of lint, laid over a wound,\n   ulcer, or the like, to exclude air, retain dressings, or absorb the\n   matter discharged.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plegepoda', 'n. pl.', 'Same as Infusoria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleiad', 'n.', 'One of the Pleiades.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleiades', 'n. pl.', 'The seven daughters of Atlas and the nymph Pleione,\n   fabled to have been made by Jupiter a constellation in the sky.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleiades', 'n. pl.', 'A group of small stars in the neck of the\n   constellation Taurus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plein', 'a.', 'Plan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plein', 'v. i. & t.', 'To complain. See Plain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plein', 'a.', 'Full; complete.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleiocene', 'a.', 'See Pliocene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleiophyllous', 'a.', 'Having several leaves; -- used especially when\n   several leaves or leaflets appear where normally there should be only\n   one.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleiosaurus', 'n.', 'Same as Pliosaurus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleistocene', 'a.', 'Of or pertaining to the epoch, or the deposits,\n   following the Tertiary, and immediately preceding man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleistocene', 'n.', 'The Pleistocene epoch, or deposits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plenal', 'a.', 'Full; complete; as, a plenal view or act.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plenarily', 'adv.', 'In a plenary manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plenariness', 'n.', 'Quality or state of being plenary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plenarty', 'n.', 'The state of a benefice when occupied.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plenary', 'a.', 'Full; entire; complete; absolute; as, a plenary license;\n   plenary authority.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plenary', 'n.', 'Decisive procedure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plene', 'ae.', 'Full; complete; plenary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plenicorn', 'n.', 'A ruminant having solid horns or antlers, as the deer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plenilunary', 'a.', 'Of or pertaining to the full moon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plenilune', 'n.', 'The full moon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plenipotence', 'n.', 'Alt. of Plenipotency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plenipotency', 'n.', 'The quality or state of being plenipotent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plenipotent', 'a.', 'Possessing full power.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plenipotentiaries', 'pl. ', 'of Plenipotentiary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plenipotentiary', 'n.', 'A person invested with full power to transact any\n   business; especially, an ambassador or envoy to a foreign court, with\n   full power to negotiate a treaty, or to transact other business.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plenipotentiary', 'a.', 'Containing or conferring full power; invested with\n   full power; as, plenipotentiary license; plenipotentiary ministers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plenish', 'v. t.', 'To replenish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plenish', 'v. t.', 'To furnish; to stock, as a house or farm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plenishing', 'n.', 'Household furniture; stock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plenist', 'n.', 'One who holds that all space is full of matter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plenitude', 'n.', 'The quality or state of being full or complete;\n   fullness; completeness; abundance; as, the plenitude of space or power.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plenitude', 'n.', 'Animal fullness; repletion; plethora.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plenitudinarian', 'n.', 'A plenist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plenitudinary', 'a.', 'Having plenitude; full; complete; thorough.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plenteous', 'a.', 'Containing plenty; abundant; copious; plentiful;\n   sufficient for every purpose; as, a plenteous supply.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plenteous', 'a.', 'Yielding abundance; productive; fruitful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plenteous', 'a.', 'Having plenty; abounding; rich.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plentevous', 'a.', 'Plenteous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plentiful', 'a.', 'Containing plenty; copious; abundant; ample; as, a\n   plentiful harvest; a plentiful supply of water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plentiful', 'a.', 'Yielding abundance; prolific; fruitful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plentiful', 'a.', 'Lavish; profuse; prodigal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plenties', 'pl. ', 'of Plenty');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plenty', 'a.', 'Full or adequate supply; enough and to spare; sufficiency;\n   specifically, abundant productiveness of the earth; ample supply for\n   human wants; abundance; copiousness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plenty', 'a.', 'Plentiful; abundant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plenum', 'n.', 'That state in which every part of space is supposed to be\n   full of matter; -- opposed to vacuum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleochroic', 'a.', 'Having the property of pleochroism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleochroism', 'n.', 'The property possessed by some crystals, of showing\n   different colors when viewed in the direction of different axes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleochromatic', 'a.', 'Pleochroic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleochromatism', 'n.', 'Pleochroism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleochroous', 'a.', 'Pleochroic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleomorphic', 'a.', 'Pertaining to pleomorphism; as, the pleomorphic\n   character of bacteria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleomorphism', 'n.', 'The property of crystallizing under two or more\n   distinct fundamental forms, including dimorphism and trimorphism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleomorphism', 'n.', 'The theory that the various genera of bacteria are\n   phases or variations of growth of a number of Protean species, each of\n   which may exhibit, according to undetermined conditions, all or some of\n   the forms characteristic of the different genera and species.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleomorphous', 'a.', 'Having the property of pleomorphism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleonasm', 'n.', 'Redundancy of language in speaking or writing; the use of\n   more words than are necessary to express the idea; as, I saw it with my\n   own eyes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleonast', 'n.', 'One who is addicted to pleonasm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleonaste', 'n.', 'A black variety of spinel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleonastic', 'a.', 'Alt. of Pleonastical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleonastical', 'a.', 'Of or pertaining to pleonasm; of the nature of\n   pleonasm; redundant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleonastically', 'adv.', 'In a pleonastic manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleopods', 'pl. ', 'of Pleopod');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleopoda', 'pl. ', 'of Pleopod');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleopod', 'n.', 'One of the abdominal legs of a crustacean. See Illust.\n   under Crustacea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plerome', 'n.', 'The central column of parenchyma in a growing stem or\n   root.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plerophory', 'n.', 'Fullness; full persuasion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plesance', 'n.', 'Pleasance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plesh', 'n.', 'A pool; a plash.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plesimorphism', 'n.', 'The property possessed by some substances of\n   crystallizing in closely similar forms while unlike in chemical\n   composition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plesiomorphous', 'a.', 'Nearly alike in form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plesiosaur', 'n.', 'One of the Plesiosauria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plesiosauria', 'n. pl.', 'An extinct order of Mesozoic marine reptiles\n   including the genera Plesiosaurus, and allied forms; -- called also\n   Sauropterygia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plesiosaurian', 'n.', 'A plesiosaur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plesiosauri', 'pl. ', 'of Plesiosaurus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plesiosaurus', 'n.', 'A genus of large extinct marine reptiles, having a\n   very long neck, a small head, and paddles for swimming. It lived in the\n   Mesozoic age.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plessimeter', 'n.', 'See Pleximeter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plete', 'v. t. & i.', 'To plead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plethora', 'n.', 'Overfullness; especially, excessive fullness of the blood\n   vessels; repletion; that state of the blood vessels or of the system\n   when the blood exceeds a healthy standard in quantity; hyperaemia; --\n   opposed to anaemia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plethora', 'n.', 'State of being overfull; excess; superabundance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plethoretic', 'a.', 'Plethoric.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plethoric', 'a.', 'Haeving a full habit of body; characterized by plethora\n   or excess of blood; as, a plethoric constitution; -- used also\n   metaphorically.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plethorical', 'a.', 'Plethoric.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plethory', 'n.', 'Plethora.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plethra', 'pl. ', 'of Plethrum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plethron', 'n.', 'Alt. of Plethrum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plethrum', 'n.', 'A long measure of 100 Greek, or 101 English, feet; also,\n   a square measure of 10,000 Greek feet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plethysmograph', 'n.', 'An instrument for determining and registering the\n   variations in the size or volume of a limb, as the arm or leg, and\n   hence the variations in the amount of blood in the limb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plethysmography', 'n.', 'The study, by means of the plethysmograph, of the\n   variations in size of a limb, and hence of its blood supply.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleura', 'n.', 'pl. of Pleuron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleurae', 'pl. ', 'of Pleura');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleuras', 'pl. ', 'of Pleura');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleura', 'n. fem.', 'The smooth serous membrane which closely covers the\n   lungs and the adjacent surfaces of the thorax; the pleural membrane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleura', 'n. fem.', 'The closed sac formed by the pleural membrane about\n   each lung, or the fold of membrane connecting each lung with the body\n   wall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleura', 'n. fem.', 'Same as Pleuron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleural', 'a.', 'Of or pertaining to the pleura or pleurae, or to the sides\n   of the thorax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleuralgia', 'n.', 'Pain in the side or region of the ribs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleurapophyses', 'pl. ', 'of Pleurapophysis');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleurapophysis', 'n.', 'One of the ventral processes of a vertebra, or the\n   dorsal element in each half of a hemal arch, forming, or corresponding\n   to, a vertebral rib.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleurenchyma', 'n.', 'A tissue consisting of long and slender tubular\n   cells, of which wood is mainly composed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleuric', 'a.', 'Pleural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleurisy', 'n.', 'An inflammation of the pleura, usually accompanied with\n   fever, pain, difficult respiration, and cough, and with exudation into\n   the pleural cavity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleurite', 'n.', 'Same as Pleuron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleuritic', 'a.', 'Alt. of Pleuritical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleuritical', 'a.', 'Of or pertaining to pleurisy; as, pleuritic symptoms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleuritical', 'a.', 'Suffering from pleurisy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleuritis', 'n.', 'Pleurisy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleuro-', '', 'A combining form denoting relation to a side; specif.,\n   connection with, or situation in or near, the pleura; as,\n   pleuroperitoneum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleurobrachia', 'n.', 'A genus of ctenophores having an ovate body and two\n   long plumose tentacles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleurobranch', 'n.', 'Any one of the gills of a crustacean that is attached\n   to the side of the thorax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleuroeranchiae', 'pl. ', 'of Pleurobranchia');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleurobranchia', 'n.', 'Same as Pleurobranch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleurocarp', 'n.', 'Any pleurocarpic moss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleurocarpic', 'a.', 'Alt. of Pleurocarpous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleurocarpous', 'a.', 'Side-fruited; -- said of those true mosses in which\n   the pedicels or the capsules are from lateral archegonia; -- opposed to\n   acrocarpous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleurocentrum', 'n.', 'One of the lateral elements in the centra of the\n   vertebrae in some fossil batrachians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pluroderes', 'n. pl.', 'A group of fresh-water turtles in which the neck\n   can not be retracted, but is bent to one side, for protection. The\n   matamata is an example.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleurodont', 'a.', 'Having the teeth consolidated with the inner edge of\n   the jaw, as in some lizards.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleurodont', 'n.', 'Any lizard having pleurodont teeth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleurodynia', 'n.', 'A painful affection of the side, simulating pleurisy,\n   usually due to rheumatism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleura', 'pl. ', 'of Pleuron');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleuron', 'n.', 'One of the sides of an animal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleuron', 'n.', 'One of the lateral pieces of a somite of an insect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleuron', 'n.', 'One of lateral processes of a somite of a crustacean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleuronectoid', 'a.', 'Pertaining to the Pleuronectidae, or Flounder\n   family.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleuropericardial', 'a.', 'Of or pertaining to the pleura and pericardium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleuroperipneumony', 'n.', 'Pleuropneumonia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleuroperitoneal', 'a.', 'Of or pertaining to the pleural and peritoneal\n   membranes or cavities, or to the pleuroperitoneum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleuroperitoneum', 'n.', 'The pleural and peritoneal membranes, or the\n   membrane lining the body cavity and covering the surface of the\n   inclosed viscera; the peritoneum; -- used especially in the case of\n   those animals in which the body cavity is not divided.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleuropneumonia', 'n.', 'Inflammation of the pleura and lungs; a\n   combination of pleurisy and pneumonia, esp. a kind of contagions and\n   fatal lung plague of cattle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleuroptera', 'n. pl', 'A group of Isectivora, including the colugo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleurosigma', 'n.', 'A genus of diatoms of elongated elliptical shape, but\n   having the sides slightly curved in the form of a letter S. Pleurosigma\n   angulatum has very fine striations, and is a favorite object for\n   testing the high powers of microscopes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleurostea', 'pl. ', 'of Pleurosteon');
  INSERT INTO words (word, word_type, description) VALUES  (    '-ons', 'pl. ', 'of Pleurosteon');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleurosteon', 'n.', 'The antero-lateral piece which articulates the sternum\n   of birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleurothotonus', 'n.', 'A species of tetanus, in which the body is curved\n   laterally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleurotomae', 'pl. ', 'of Pleurotoma');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleurotomas', 'pl. ', 'of Pleurotoma');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleurotoma', 'n.', 'Any marine gastropod belonging to Pleurotoma, and ether\n   allied genera of the family Pleurotmidae. The species are very\n   numerous, especially in tropical seas. The outer lip has usually a\n   posterior notch or slit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plevin', 'n.', 'A warrant or assurance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plexiform', 'a.', 'Like network; complicated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleximeter', 'n.', 'A small, hard, elastic plate, as of ivory, bone, or\n   rubber, placed in contact with body to receive the blow, in examination\n   by mediate percussion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plexure', 'n.', 'The act or process of weaving together, or interweaving;\n   that which is woven together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plexus', 'pl. ', 'of Plexus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plexuses', 'pl. ', 'of Plexus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plexus', 'n.', 'A network of vessels, nerves, or fibers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plexus', 'n.', 'The system of equations required for the complete\n   expression of the relations which exist between a set of quantities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pley', 'v. & n.', 'See Play.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pley', 'a.', 'Full See Plein.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pleyt', 'n.', 'An old term for a river boat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pliability', 'n.', 'The quality or state of being pliable; flexibility; as,\n   pliability of disposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pliable', 'v.', 'Capable of being plied, turned, or bent; easy to be bent;\n   flexible; pliant; supple; limber; yielding; as, willow is a pliable\n   plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pliable', 'v.', 'Flexible in disposition; readily yielding to influence,\n   arguments, persuasion, or discipline; easy to be persuaded; --\n   sometimes in a bad sense; as, a pliable youth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pliancy', 'n.', 'The quality or state of being pliant in sense; as, the\n   pliancy of a rod.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pliant', 'v.', 'Capable of plying or bending; readily yielding to force or\n   pressure without breaking; flexible; pliable; lithe; limber; plastic;\n   as, a pliant thread; pliant wax. Also used figuratively: Easily\n   influenced for good or evil; tractable; as, a pliant heart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pliant', 'v.', 'Favorable to pliancy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plica', 'v.', 'A disease of the hair (Plica polonica), in which it becomes\n   twisted and matted together. The disease is of Polish origin, and is\n   hence called also Polish plait.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plica', 'v.', 'A diseased state in plants in which there is an excessive\n   development of small entangled twigs, instead of ordinary branches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plica', 'v.', 'The bend of the wing of a bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plicate', 'a.', 'Alt. of Plicated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plicated', 'a.', 'Plaited; folded like a fan; as, a plicate leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plication', 'n.', 'A folding or fold; a plait.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plicature', 'n.', 'A fold; a doubling; a plication.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plicidentine', 'n.', 'A form of dentine which shows sinuous lines of\n   structure in a transverse section of the tooth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plied', '', 'imp. & p. p. of Ply.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pliers', 'n. pl.', 'A kind of small pinchers with long jaws, -- used for\n   bending or cutting metal rods or wire, for handling small objects such\n   as the parts of a watch, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pliform', 'a.', 'In the form of a ply, fold, or doubling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plight', '', 'imp. & p. p. of Plight, to pledge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plight', '', 'imp. & p. p. of Pluck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plight', 'v. t.', 'To weave; to braid; to fold; to plait.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plight', 'n.', 'A network; a plait; a fold; rarely a garment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plight', 'n.', 'That which is exposed to risk; that which is plighted or\n   pledged; security; a gage; a pledge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plight', 'n.', 'Condition; state; -- risk, or exposure to danger, often\n   being implied; as, a luckless plight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plighted', 'imp. & p. p.', 'of Plight');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plighting', 'p. pr. & vb. n.', 'of Plight');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plight', 'n.', 'To pledge; to give as a pledge for the performance of some\n   act; as, to plight faith, honor, word; -- never applied to property or\n   goods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plight', 'n.', 'To promise; to engage; to betroth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plighter', 'n.', 'One who, or that which, plights.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plim', 'v. i.', 'To swell, as grain or wood with water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plimsolls mark', '', 'A mark conspicuously painted on the port side of all\n   British sea-going merchant vessels, to indicate the limit of\n   submergence allowed by law; -- so called from Samuel Plimsoll, by whose\n   efforts the act of Parliament to prevent overloading was procured.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plinth', 'n.', 'In classical architecture, a vertically faced member\n   immediately below the circular base of a column; also, the lowest\n   member of a pedestal; hence, in general, the lowest member of a base; a\n   sub-base; a block upon which the moldings of an architrave or trim are\n   stopped at the bottom. See Illust. of Column.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pliocene', 'a.', 'Of, pertaining to, or characterizing, the most recent\n   division of the Tertiary age.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pliocene', 'n.', 'The Pliocene period or deposits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pliohippus', 'n.', 'An extinct genus of horses from the Pliocene deposits.\n   Each foot had a single toe (or hoof), as in the common horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pliosaurus', 'n.', 'An extinct genus of marine reptiles allied to\n   Plesiosaurus, but having a much shorter neck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plitt', 'n.', 'An instrument of punishment or torture resembling the knout,\n   used in Russia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ploc', 'n.', 'A mixture of hair and tar for covering the bottom of a ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ploce', 'n.', 'A figure in which a word is separated or repeated by way of\n   emphasis, so as not only to signify the individual thing denoted by it,\n   but also its peculiar attribute or quality; as, \"His wifes a wife\n   indeed.\"');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plodded', 'imp. & p. p.', 'of Plod');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plodding', 'p. pr. & vb. n.', 'of Plod');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plod', 'v. i.', 'To travel slowly but steadily; to trudge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plod', 'v. i.', 'To toil; to drudge; especially, to study laboriously and\n   patiently.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plod', 'v. t.', 'To walk on slowly or heavily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plodder', 'n.', 'One who plods; a drudge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plodding', 'a.', 'Progressing in a slow, toilsome manner; characterized by\n   laborious diligence; as, a plodding peddler; a plodding student; a man\n   of plodding habits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plonge', 'v. t.', 'To cleanse, as open drains which are entered by the\n   tide, by stirring up the sediment when the tide ebbs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plongee', 'n.', 'A slope or sloping toward the front; as, the plongee of a\n   parapet; the plongee of a shell in its course.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plot', 'n.', 'A small extent of ground; a plat; as, a garden plot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plot', 'n.', 'A plantation laid out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plot', 'n.', 'A plan or draught of a field, farm, estate, etc., drawn to a\n   scale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plotted', 'imp. & p. p.', 'of Plot');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plotting', 'p. pr. & vb. n.', 'of Plot');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plot', 'v. t.', 'To make a plot, map, pr plan, of; to mark the position of\n   on a plan; to delineate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plot', 'n.', 'Any scheme, stratagem, secret design, or plan, of a\n   complicated nature, adapted to the accomplishment of some purpose,\n   usually a treacherous and mischievous one; a conspiracy; an intrigue;\n   as, the Rye-house Plot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plot', 'n.', 'A share in such a plot or scheme; a participation in any\n   stratagem or conspiracy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plot', 'n.', 'Contrivance; deep reach of thought; ability to plot or\n   intrigue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plot', 'n.', 'A plan; a purpose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plot', 'n.', 'In fiction, the story of a play, novel, romance, or poem,\n   comprising a complication of incidents which are gradually unfolded,\n   sometimes by unexpected means.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plot', 'v. i.', 'To form a scheme of mischief against another, especially\n   against a government or those who administer it; to conspire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plot', 'v. i.', 'To contrive a plan or stratagem; to scheme.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plot', 'v. t.', 'To plan; to scheme; to devise; to contrive secretly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plotful', 'a.', 'Abounding with plots.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pletinian', 'a.', 'Of pertaining to the Plotinists or their doctrines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plotinist', 'n.', 'A disciple of Plotinus, a celebrated Platonic\n   philosopher of the third century, who taught that the human soul\n   emanates from the divine Being, to whom it reunited at death.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plot-proof', 'a.', 'Secure against harm by plots.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plotter', 'n.', 'One who plots or schemes; a contriver; a conspirator; a\n   schemer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plough', 'n. & v.', 'See Plow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plover', 'n.', 'Any one of numerous species of limicoline birds belonging\n   to the family Charadridae, and especially those belonging to the\n   subfamily Charadrinsae. They are prized as game birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plover', 'n.', 'Any grallatorial bird allied to, or resembling, the true\n   plovers, as the crab plover (Dromas ardeola); the American upland,\n   plover (Bartramia longicauda); and other species of sandpipers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plow', 'n.', 'Alt. of Plough');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plough', 'n.', 'A well-known implement, drawn by horses, mules, oxen, or\n   other power, for turning up the soil to prepare it for bearing crops;\n   also used to furrow or break up the soil for other purposes; as, the\n   subsoil plow; the draining plow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plough', 'n.', 'Fig.: Agriculture; husbandry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plough', 'n.', 'A carucate of land; a plowland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plough', 'n.', 'A joiners plane for making grooves; a grooving plane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plough', 'n.', 'An implement for trimming or shaving off the edges of\n   books.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plough', 'n.', 'Same as Charless Wain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plowed', 'imp. & p. p.', 'of Plough');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ploughed', '', 'of Plough');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plowing', 'p. pr. & vb. n.', 'of Plough');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ploughing', '', 'of Plough');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plow', 'v. t.', 'Alt. of Plough');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plough', 'v. t.', 'To turn up, break up, or trench, with a plow; to till\n   with, or as with, a plow; as, to plow the ground; to plow a field.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plough', 'v. t.', 'To furrow; to make furrows, grooves, or ridges in; to\n   run through, as in sailing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plough', 'v. t.', 'To trim, or shave off the edges of, as a book or paper,\n   with a plow. See Plow, n., 5.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plough', 'n.', 'To cut a groove in, as in a plank, or the edge of a board;\n   especially, a rectangular groove to receive the end of a shelf or\n   tread, the edge of a panel, a tongue, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plow', 'v. i.', 'Alt. of Plough');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plough', 'v. i.', 'To labor with, or as with, a plow; to till or turn up\n   the soil with a plow; to prepare the soil or bed for anything.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plowable', 'a.', 'Alt. of Ploughable');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ploughable', 'a.', 'Capable of being plowed; arable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plowbote', 'n.', 'Alt. of Ploughbote');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ploughbote', 'n.', 'Wood or timber allowed to a tenant for the repair of\n   instruments of husbandry. See Bote.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plowboy', 'n.', 'Alt. of Ploughboy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ploughboy', 'n.', 'A boy that drives or guides a team in plowing; a young\n   rustic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plower', 'n.', 'Alt. of Plougher');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plougher', 'n.', 'One who plows; a plowman; a cultivator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plowfoot', 'n.', 'Alt. of Ploughfoot');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ploughfoot', 'n.', 'An adjustable staff formerly attached to the plow beam\n   to determine the depth of the furrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plowgang', 'n.', 'Alt. of Ploughgang');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ploughgang', 'n.', 'Same as Plowgate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plowgate', 'n.', 'Alt. of Ploughgate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ploughgate', 'n.', 'The Scotch equivalent of the English word plowland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plowhead', 'n.', 'Alt. of Ploughhead');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ploughhead', 'n.', 'The clevis or draught iron of a plow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plowland', 'n.', 'Alt. of Plougland');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plougland', 'n.', 'Land that is plowed, or suitable for tillage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plougland', 'n.', 'the quantity of land allotted for the work of one plow;\n   a hide.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-men', 'pl. ', 'of Ploughman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plowman', 'n.', 'Alt. of Ploughman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ploughman', 'n.', 'One who plows, or who holds and guides a plow; hence, a\n   husbandman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ploughman', 'n.', 'A rustic; a countryman; a field laborer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plowpoint', 'n.', 'Alt. of Ploughpoint');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ploughpoint', 'n.', 'A detachable share at the extreme front end of the\n   plow body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plowshare', 'n.', 'Alt. of Ploughshare');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ploughshare', 'n.', 'The share of a plow, or that part which cuts the slice\n   of earth or sod at the bottom of the furrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plowtail', 'n.', 'Alt. of Ploughtail');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ploughtail', 'n.', 'The hind part or handle of a plow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plowwright', 'n.', 'Alt. of Ploughwright');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ploughwright', 'n.', 'One who makes or repairs plows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ploy', 'n.', 'Sport; frolic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ploy', 'v. i.', 'To form a column from a line of troops on some designated\n   subdivision; -- the opposite of deploy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ployment', 'n.', 'The act or movement of forming a column from a line of\n   troops on some designated subdivision; -- the opposite of deployment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plucked', 'imp. & p. p.', 'of Pluck');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plucking', 'p. pr. & vb. n.', 'of Pluck');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pluck', 'v. t.', 'To pull; to draw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pluck', 'v. t.', 'Especially, to pull with sudden force or effort, or to\n   pull off or out from something, with a twitch; to twitch; also, to\n   gather, to pick; as, to pluck feathers from a fowl; to pluck hair or\n   wool from a skin; to pluck grapes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pluck', 'v. t.', 'To strip of, or as of, feathers; as, to pluck a fowl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pluck', 'v. t.', 'To reject at an examination for degrees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pluck', 'v. i.', 'To make a motion of pulling or twitching; -- usually with\n   at; as, to pluck at ones gown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pluck', 'n.', 'The act of plucking; a pull; a twitch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pluck', 'n.', 'The heart, liver, and lights of an animal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pluck', 'n.', 'Spirit; courage; indomitable resolution; fortitude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pluck', 'n.', 'The act of plucking, or the state of being plucked, at\n   college. See Pluck, v. t., 4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pluck', 'v. t.', 'The lyrie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plucked', 'a.', 'Having courage and spirit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plucker', 'n.', 'One who, or that which, plucks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plucker', 'n.', 'A machine for straightening and cleaning wool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pluckily', 'adv.', 'In a plucky manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pluckiness', 'n.', 'The quality or state of being plucky.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pluckless', 'a.', 'Without pluck; timid; faint-hearted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plucky', 'superl.', 'Having pluck or courage; characterized by pluck;\n   displaying pluck; courageous; spirited; as, a plucky race.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pluff', 'v. t.', 'To throw out, as smoke, dust, etc., in puffs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pluff', 'n.', 'A puff, as of smoke from a pipe, or of dust from a puffball;\n   a slight explosion, as of a small quantity of gunpowder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pluff', 'n.', 'A hairdressers powder puff; also, the act of using it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plug', 'n.', 'Any piece of wood, metal, or other substance used to stop or\n   fill a hole; a stopple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plug', 'n.', 'A flat oblong cake of pressed tobacco.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plug', 'n.', 'A high, tapering silk hat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plug', 'n.', 'A worthless horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plug', 'n.', 'A block of wood let into a wall, to afford a hold for nails.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plugged', 'imp. & p. p.', 'of Plug');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plugging', 'p. pr. & vb. n.', 'of Plug');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plug', 'v. t.', 'To stop with a plug; to make tight by stopping a hole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plugger', 'n.', 'One who, or that which, plugs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plugging', 'n.', 'The act of stopping with a plug.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plugging', 'n.', 'The material of which a plug or stopple is made.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plum', 'n.', 'The edible drupaceous fruit of the Prunus domestica, and of\n   several other species of Prunus; also, the tree itself, usually called\n   plum tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plum', 'n.', 'A grape dried in the sun; a raisin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plum', 'n.', 'A handsome fortune or property; formerly, in cant language,\n   the sum of 100,000 sterling; also, the person possessing it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plumae', 'pl. ', 'of Pluma');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pluma', 'n.', 'A feather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plumage', 'n.', 'The entire clothing of a bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plumassary', 'n.', 'A plume or collection of ornamental feathers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plumassier', 'n.', 'One who prepares or deals in ornamental plumes or\n   feathers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plumb', 'n.', 'A little mass or weight of lead, or the like, attached to a\n   line, and used by builders, etc., to indicate a vertical direction; a\n   plummet; a plumb bob. See Plumb line, below.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plumb', 'a.', 'Perpendicular; vertical; conforming the direction of a line\n   attached to a plumb; as, the wall is plumb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plumb', 'adv.', 'In a plumb direction; perpendicularly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plumbed', 'imp. & p. p.', 'of Plumb');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plumbing', 'p. pr. & vb. n.', 'of Plumb');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plumb', 'v. t.', 'To adjust by a plumb line; to cause to be perpendicular;\n   as, to plumb a building or a wall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plumb', 'v. t.', 'To sound with a plumb or plummet, as the depth of water;\n   hence, to examine by test; to ascertain the depth, quality, dimension,\n   etc.; to sound; to fathom; to test.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plumb', 'v. t.', 'To seal with lead; as, to plumb a drainpipe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plumb', 'v. t.', 'To supply, as a building, with a system of plumbing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plumbage', 'n.', 'Leadwork');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plumbagin', 'n.', 'A crystalline substance said to be found in the root of\n   a certain plant of the Leadwort (Plumbago) family.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plumbagineous', 'a.', 'Pertaining to natural order (Plumbagineae) of\n   gamopetalous herbs, of which Plumbago is the type. The order includes\n   also the marsh rosemary, the thrift, and a few other genera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plumbaginous', 'a.', 'Resembling plumbago; consisting of, or containing,\n   plumbago; as, a plumbaginous slate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plumbago', 'n.', 'Same as Graphite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plumbago', 'n.', 'A genus of herbaceous plants with pretty salver-shaped\n   corollas, usually blue or violet; leadwort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plumbean', 'a.', 'Alt. of Plumbeous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plumbeous', 'a.', 'Consisting of, or resembling, lead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plumbeous', 'a.', 'Dull; heavy; stupid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plumber', 'n.', 'One who works in lead; esp., one who furnishes, fits, and\n   repairs lead, iron, or glass pipes, and other apparatus for the\n   conveyance of water, gas, or drainage in buildings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plumber block', '', 'A pillow block.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plumbery', 'n.', 'The business of a plumber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plumbery', 'n.', 'A place where plumbing is carried on; lead works.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plumbic', 'a.', 'Of, pertaining to, resembling, or containing, lead; --\n   used specifically to designate those compounds in which it has a higher\n   valence as contrasted with plumbous compounds; as, plumbic oxide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plumbiferous', 'a.', 'Producing or containing lead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plumbing', 'n.', 'The art of casting and working in lead, and applying it\n   to building purposes; especially, the business of furnishing, fitting,\n   and repairing pipes for conducting water, sewage, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plumbing', 'n.', 'The lead or iron pipes, and other apparatus, used in\n   conveying water, sewage, etc., in a building.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plumbism', 'n.', 'A diseased condition, produced by the absorption of lead,\n   common among workers in this metal or in its compounds, as among\n   painters, typesetters, etc. It is characterized by various symptoms, as\n   lead colic, lead line, and wrist drop. See under Colic, Lead, and\n   Wrist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plumbous', 'a.', 'Of, pertaining to, or containing, lead; -- used\n   specifically to designate those compounds in which it has a lower\n   valence as contrasted with plumbic compounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plumbum', 'n.', 'The technical name of lead. See Lead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plume', 'v.', 'A feather; esp., a soft, downy feather, or a long,\n   conspicuous, or handsome feather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plume', 'v.', 'An ornamental tuft of feathers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plume', 'v.', 'A feather, or group of feathers, worn as an ornament; a\n   waving ornament of hair, or other material resembling feathers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plume', 'v.', 'A token of honor or prowess; that on which one prides\n   himself; a prize or reward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plume', 'v.', 'A large and flexible panicle of inflorescence resembling a\n   feather, such as is seen in certain large ornamental grasses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plumed', 'imp. & p. p.', 'of Plume');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pluming', 'p. pr. & vb. n.', 'of Plume');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plume', 'v. t.', 'To pick and adjust the plumes or feathers of; to dress or\n   prink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plume', 'v. t.', 'To strip of feathers; to pluck; to strip; to pillage;\n   also, to peel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plume', 'v. t.', 'To adorn with feathers or plumes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plume', 'v. t.', 'To pride; to vaunt; to boast; -- used reflexively; as, he\n   plumes himself on his skill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plumeless', 'a.', 'Without plumes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plumelet', 'n.', 'A small plume.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plumery', 'n.', 'Plumes, collectively or in general; plumage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plumicorn', 'n.', 'An ear tuft of feathers, as in the horned owls.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plumigerous', 'a.', 'Feathered; having feathers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plumiliform', 'a.', 'Having the of a plume or feather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plumiped', 'a.', 'Having feet covered with feathers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plumiped', 'n.', 'A plumiped bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plummet', 'n.', 'A piece of lead attached to a line, used in sounding the\n   depth of water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plummet', 'n.', 'A plumb bob or a plumb line. See under Plumb, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plummet', 'n.', 'Hence, any weight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plummet', 'n.', 'A piece of lead formerly used by school children to rule\n   paper for writing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plumming', 'n.', 'The operation of finding, by means of a mine dial, the\n   place where to sink an air shaft, or to bring an adit to the work, or\n   to find which way the lode inclines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plummy', 'a.', 'Of the nature of a plum; desirable; profitable;\n   advantageous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plumose', 'a.', 'Alt. of Plumous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plumous', 'a.', 'Having feathers or plumes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plumous', 'a.', 'Having hairs, or other parts, arranged along an axis like\n   a feather; feathery; plumelike; as, a plumose leaf; plumose tentacles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plumosite', 'n.', 'Same as Jamesonite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plumosity', 'n.', 'The quality or state of being plumose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plump', 'adv.', 'Well rounded or filled out; full; fleshy; fat; as, a plump\n   baby; plump cheeks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plump', 'n.', 'A knot; a cluster; a group; a crowd; a flock; as, a plump of\n   trees, fowls, or spears.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plump', 'a.', 'To grow plump; to swell out; as, her cheeks have plumped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plump', 'a.', 'To drop or fall suddenly or heavily, all at once.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plump', 'a.', 'To give a plumper. See Plumper, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plumped', 'imp. & p. p.', 'of Plump');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plumping', 'p. pr. & vb. n.', 'of Plump');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plump', 'v. t.', 'To make plump; to fill (out) or support; -- often with\n   up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plump', 'v. t.', 'To cast or let drop all at once, suddenly and heavily;\n   as, to plump a stone into water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plump', 'v. t.', 'To give (a vote), as a plumper. See Plumper, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plump', 'a. & v.', 'Directly; suddenly; perpendicularly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plumper', 'n.', 'One who, or that which, plumps or swells out something\n   else; hence, something carried in the mouth to distend the cheeks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plumper', 'n.', 'A vote given to one candidate only, when two or more are\n   to be elected, thus giving him the advantage over the others. A person\n   who gives his vote thus is said to plump, or to plump his vote.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plumper', 'n.', 'A voter who plumps his vote.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plumper', 'n.', 'A downright, unqualified lie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plumply', 'adv.', 'Fully; roundly; plainly; without reserve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plumpness', 'n.', 'The quality or state of being plump.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plumpy', 'a.', 'Plump; fat; sleek.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plumule', 'pl. ', 'of Plumula');
  INSERT INTO words (word, word_type, description) VALUES  (    '-las', 'pl. ', 'of Plumula');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plumula', 'n.', 'A plumule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plumula', 'n.', 'A down feather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plumulaceous', 'a.', 'Downy; bearing down.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plumular', 'a.', 'Relating to a plumule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plumularlae', 'pl. ', 'of Plumularia');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plumularias', 'pl. ', 'of Plumularia');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plumularia', 'n.', 'Any hydroid belonging to Plumularia and other genera of\n   the family Plumularidae. They generally grow in plumelike forms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plumularian', 'n.', 'Any Plumularia. Also used adjectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plumule', 'n.', 'The first bud, or gemmule, of a young plant; the bud, or\n   growing point, of the embryo, above the cotyledons. See Illust. of\n   Radicle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plumule', 'n.', 'A down feather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plumule', 'n.', 'The aftershaft of a feather. See Illust. under Feather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plumule', 'n.', 'One of the featherlike scales of certain male butterflies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plumulose', 'a.', 'Having hairs branching out laterally, like the parts of\n   a feather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plumy', 'a.', 'Covered or adorned with plumes, or as with plumes; feathery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plundered', 'imp. & p. p.', 'of Plunder');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plundering', 'p. pr. & vb. n.', 'of Plunder');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plunder', 'v. t.', 'To take the goods of by force, or without right; to\n   pillage; to spoil; to sack; to strip; to rob; as, to plunder travelers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plunder', 'v. t.', 'To take by pillage; to appropriate forcibly; as, the\n   enemy plundered all the goods they found.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plunder', 'n.', 'The act of plundering or pillaging; robbery. See Syn. of\n   Pillage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plunder', 'n.', 'That which is taken by open force from an enemy; pillage;\n   spoil; booty; also, that which is taken by theft or fraud.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plunder', 'n.', 'Personal property and effects; baggage or luggage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plunderage', 'n.', 'The embezzlement of goods on shipboard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plunderer', 'n.', 'One who plunders or pillages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plunged', 'imp. & p. p.', 'of Plunge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plunging', 'p. pr. & vb. n.', 'of Plunge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plunge', 'v. t.', 'To thrust into water, or into any substance that is\n   penetrable; to immerse; to cause to penetrate or enter quickly and\n   forcibly; to thrust; as, to plunge the body into water; to plunge a\n   dagger into the breast. Also used figuratively; as, to plunge a nation\n   into war.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plunge', 'v. t.', 'To baptize by immersion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plunge', 'v. t.', 'To entangle; to embarrass; to overcome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plunge', 'v. i.', 'To thrust or cast ones self into water or other fluid;\n   to submerge ones self; to dive, or to rush in; as, he plunged into the\n   river. Also used figuratively; as, to plunge into debt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plunge', 'v. i.', 'To pitch or throw ones self headlong or violently\n   forward, as a horse does.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plunge', 'v. i.', 'To bet heavily and with seeming recklessness on a race,\n   or other contest; in an extended sense, to risk large sums in hazardous\n   speculations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plunge', 'n.', 'The act of thrusting into or submerging; a dive, leap,\n   rush, or pitch into, or as into, water; as, to take the water with a\n   plunge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plunge', 'n.', 'Hence, a desperate hazard or act; a state of being\n   submerged or overwhelmed with difficulties.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plunge', 'n.', 'The act of pitching or throwing ones self headlong or\n   violently forward, like an unruly horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plunge', 'n.', 'Heavy and reckless betting in horse racing; hazardous\n   speculation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plunger', 'n.', 'One who, or that which, plunges; a diver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plunger', 'n.', 'A long solid cylinder, used, instead of a piston or\n   bucket, as a forcer in pumps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plunger', 'n.', 'One who bets heavily and recklessly on a race; a reckless\n   speculator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plunger', 'n.', 'A boiler in which clay is beaten by a wheel to a creamy\n   consistence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plunger', 'n.', 'The firing pin of a breechloader.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plunket', 'n.', 'A kind of blue color; also, anciently, a kind of cloth,\n   generally blue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pluperfect', 'a.', 'More than perfect; past perfect; -- said of the tense\n   which denotes that an action or event was completed at or before the\n   time of another past action or event.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pluperfect', 'n.', 'The pluperfect tense; also, a verb in the pluperfect\n   tense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plural', 'a.', 'Relating to, or containing, more than one; designating two\n   or more; as, a plural word.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plural', 'n.', 'The plural number; that form of a word which expresses or\n   denotes more than one; a word in the plural form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pluralism', 'n.', 'The quality or state of being plural, or in the plural\n   number.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pluralism', 'n.', 'The state of a pluralist; the holding of more than one\n   ecclesiastical living at a time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pluralist', 'n.', 'A clerk or clergyman who holds more than one\n   ecclesiastical benefice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'pluralities', 'pl. ', 'of Plurality');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plurality', 'n.', 'The state of being plural, or consisting of more than\n   one; a number consisting of two or more of the same kind; as, a\n   plurality of worlds; the plurality of a verb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plurality', 'n.', 'The greater number; a majority; also, the greatest of\n   several numbers; in elections, the excess of the votes given for one\n   candidate over those given for another, or for any other, candidate.\n   When there are more than two candidates, the one who receives the\n   plurality of votes may have less than a majority. See Majority.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plurality', 'n.', 'See Plurality of benefices, below.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pluralization', 'n.', 'The act of pluralizing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pluralized', 'imp. & p. p.', 'of Pluralize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pluralizing', 'p. pr. & vb. n.', 'of Pluralize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pluralize', 'v. t.', 'To make plural by using the plural termination; to\n   attribute plurality to; to express in the plural form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pluralize', 'v. t.', 'To multiply; to make manifold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pluralize', 'v. i.', 'To take a plural; to assume a plural form; as, a noun\n   pluralizes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pluralize', 'v. i.', 'To hold more than one benefice at the same time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pluralizer', 'n.', 'A pluralist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plurally', 'adv.', 'In a plural manner or sense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pluri-', '', 'A combining form from L. plus, pluris, more, many; as\n   pluriliteral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pluries', 'n.', 'A writ issued in the third place, after two former writs\n   have been disregarded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plurifarious', 'a.', 'Of many kinds or fashions; multifarious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plurifoliolate', 'a.', 'Having several or many leaflets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pluriliteral', 'a.', 'Consisting of more letters than three.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pluriliteral', 'n.', 'A pluriliteral word.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plurilocular', 'a.', 'Having several cells or loculi');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plurilocular', 'a.', 'having several divisions containing seeds; as, the\n   lemon and the orange are plurilocular fruits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pluriparous', 'a.', 'Producing several young at a birth; as, a pluriparous\n   animal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pluripartite', 'a.', 'Deeply divided into several portions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pluripresence', 'n.', 'Presence in more places than one.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plurisy', 'n.', 'Superabundance; excess; plethora.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plus', 'a.', 'More, required to be added; positive, as distinguished from\n   negative; -- opposed to minus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plus', 'a.', 'Hence, in a literary sense, additional; real; actual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plush', 'n.', 'A textile fabric with a nap or shag on one side, longer and\n   softer than the nap of velvet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plushy', 'a.', 'Like plush; soft and shaggy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plutarchy', 'n.', 'Plutocracy; the rule of wealth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pluteal', 'a.', 'Of or pertaining to a pluteus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plutei', 'pl. ', 'of Pluteus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pluteuses', 'pl. ', 'of Pluteus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pluteus', 'n.', 'The free-swimming larva of sea urchins and ophiurans,\n   having several long stiff processes inclosing calcareous rods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pluto', 'n.', 'The son of Saturn and Rhea, brother of Jupiter and Neptune;\n   the dark and gloomy god of the Lower World.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plutocracy', 'n.', 'A form of government in which the supreme power is\n   lodged in the hands of the wealthy classes; government by the rich;\n   also, a controlling or influential class of rich men.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plutocrat', 'n.', 'One whose wealth gives him power or influence; one of\n   the plutocracy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plutocratic', 'a.', 'Of or pertaining to plutocracy; as, plutocratic ideas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plutology', 'n.', 'The science which treats of wealth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plutonian', 'a.', 'Plutonic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plutonian', 'n.', 'A Plutonist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plutonic', 'a.', 'Of or pertaining to Pluto; Plutonian; hence, pertaining\n   to the interior of the earth; subterranean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plutonic', 'a.', 'Of, pertaining to, or designating, the system of the\n   Plutonists; igneous; as, the Plutonic theory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plutonism', 'n.', 'The theory, early advanced in geology, that the\n   successive rocks of the earths crust were formed by igneous fusion; --\n   opposed to the Neptunian theory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plutonist', 'n.', 'One who adopts the geological theory of igneous fusion;\n   a Plutonian. See Plutonism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plutus', 'n.', 'The son of Jason and Ceres, and the god of wealth. He was\n   represented as bearing a cornucopia, and as blind, because his gifts\n   were bestowed without discrimination of merit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pluvial', 'a.', 'Of or pertaining to rain; rainy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pluvial', 'a.', 'Produced by the action of rain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pluvial', 'n.', 'A priests cope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pluviameter', 'n.', 'See Pluviometer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pluviametrical', 'a.', 'See Pluviometrical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pluvian', 'n.', 'The crocodile bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pluviometer', 'n.', 'An instrument for ascertaining the amount of rainfall\n   at any place in a given time; a rain gauge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pluviometrical', 'a.', 'Of or pertaining to a pluviometer; determined by a\n   pluviometer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pluviose', 'n.', 'The fifth month of the French republican calendar adopted\n   in 1793. It began January 20, and ended February 18. See Vendemiaire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pluvious', 'a.', 'Abounding in rain; rainy; pluvial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plied', 'imp. & p. p.', 'of Ply');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plying', 'p. pr. & vb. n.', 'of Ply');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ply', 'v. t.', 'To bend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ply', 'v. t.', 'To lay on closely, or in folds; to work upon steadily, or\n   with repeated acts; to press upon; to urge importunately; as, to ply\n   one with questions, with solicitations, or with drink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ply', 'v. t.', 'To employ diligently; to use steadily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ply', 'v. t.', 'To practice or perform with diligence; to work at.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ply', 'v. i.', 'To bend; to yield.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ply', 'v. i.', 'To act, go, or work diligently and steadily; especially, to\n   do something by repeated actions; to go back and forth; as, a steamer\n   plies between certain ports.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ply', 'v. i.', 'To work to windward; to beat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ply', 'v.', 'A fold; a plait; a turn or twist, as of a cord.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ply', 'v.', 'Bent; turn; direction; bias.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plyer', 'n.', 'One who, or that which, plies');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plyer', 'n.', 'A kind of balance used in raising and letting down a\n   drawbridge. It consists of timbers joined in the form of a St. Andrews\n   cross.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plyer', 'n.', 'See Pliers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plyght', 'v. & n.', 'See Plight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Plymouth Brethren', '', 'The members of a religious sect which first\n   appeared at Plymouth, England, about 1830. They protest against\n   sectarianism, and reject all official ministry or clergy. Also called\n   Brethren, Christian Brethren, Plymouthists, etc. The Darbyites are a\n   division of the Brethren.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pneometer', 'n.', 'A spirometer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pneumatic', 'a.', 'Alt. of Pneumatical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pneumatical', 'a.', 'Consisting of, or resembling, air; having the\n   properties of an elastic fluid; gaseous; opposed to dense or solid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pneumatical', 'a.', 'Of or pertaining to air, or to elastic fluids or their\n   properties; pertaining to pneumatics; as, pneumatic experiments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pneumatical', 'a.', 'Moved or worked by pressure or flow of air; as, a\n   pneumatic instrument; a pneumatic engine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pneumatical', 'a.', 'Fitted to contain air; Having cavities filled with\n   air; as, pneumatic cells; pneumatic bones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pneumaticity', 'n.', 'The state of being pneumatic, or of having a cavity\n   or cavities filled with air; as, the pneumaticity of the bones of\n   birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pneumatics', 'n.', 'That branch of science which treats of the mechanical\n   properties of air and other elastic fluids, as of their weight,\n   pressure, elasticity, etc. See Mechanics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pneumatics', 'n.', 'The scientific study or knowledge of spiritual beings\n   and their relations to God, angels, and men.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pneumato-', '', 'A combining form from Gr. pney^ma, pney`matos, wind, air,\n   breath, respiration; as, pneumatograph, pneumatology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pneumatocele', 'n.', 'A distention of the scrotum by air; also, hernia of\n   the lungs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pneumatocyst', 'n.', 'A cyst or sac of a siphonophore, containing air, and\n   serving as a float, as in Physalia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pneumatogarm', 'n.', 'A tracing of the respiratory movements, obtained by a\n   pneumatograph or stethograph.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pneumatograph', 'n.', 'An instrument for recording the movements of the\n   thorax or chest wall during respiration; -- also called stethograph.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pneumatological', 'a.', 'Of or pertaining to pneumatology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pneumatologist', 'n.', 'One versed in pneumatology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pneumatology', 'n.', 'The doctrine of, or a treatise on, air and other\n   elastic fluids. See Pneumatics, 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pneumatology', 'n.', 'The science of spiritual being or phenomena of any\n   description.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pneumatometer', 'n.', 'An instrument for measuring the amount of force\n   exerted by the lungs in respiration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pneumatometry', 'n.', 'See Spirometry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pneumatophore', 'n.', 'One of the Pneumonophora.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pneumatothorax', 'n.', 'See Pneumothorax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pneumo-', '', 'A combining form from Gr. pney`mwn, pney`monos, a lung; as,\n   pneumogastric, pneumology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pneumococcus', 'n.', 'A form of micrococcus found in the sputum (and\n   elsewhere) of persons suffering with pneumonia, and thought to be the\n   cause of this disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pneumogastric', 'a.', 'Of or pertaining to the lungs and the stomach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pneumogastric', 'n.', 'The pneumogastric nerve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pneumograph', 'n.', 'Same as Pneumatograph.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pneumography', 'n.', 'A description of the lungs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pneumology', 'n.', 'The science which treats of the lungs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pneumometer', 'n.', 'A spirometer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pneumometry', 'n.', 'Measurement of the capacity of the lungs for air.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pneumonia', 'n.', 'Inflammation of the lungs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pneumonic', 'a.', 'Of or pertaining to the lungs; pulmonic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pneumonic', 'a.', 'Of or pertaining to pneumonia; as, pneumonic symptoms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pneumonic', 'n.', 'A medicine for affections of the lungs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pneumonitic', 'a.', 'Of or pertaining to pneumonitis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pneumonitis', 'n.', 'Inflammation of the lungs; pneumonia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pneumonometer', 'n.', 'A spirometer; a pneumometer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pneumonophora', 'n. pl.', 'The division of Siphonophora which includes the\n   Physalia and allied genera; -- called also Pneumatophorae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pneumony', 'n.', 'See Pneumonia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pneumootoka', 'n. pl.', 'Same as Sauropsida.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pneumophora', 'n. pl.', 'A division of holothurians having an internal\n   gill, or respiratory tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pneumoskeleton', 'n.', 'A chitinous structure which supports the gill in\n   some invertebrates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pneumotherapy', 'n.', 'The treatment of disease by inhalations of\n   compressed or rarefied air.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pneumothorax', 'n.', 'A condition in which air or other gas is present in\n   the cavity of the chest; -- called also pneumatothorax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pnigalion', 'n.', 'Nightmare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pnyx', 'n.', 'The place at Athens where the meetings of the people were\n   held for making decrees, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poa', 'n.', 'A genus of grasses, including a great number of species, as\n   the kinds called meadow grass, Kentucky blue grass, June grass, and\n   spear grass (which see).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poached', 'imp. & p. p.', 'of Poach');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poaching', 'p. pr. & vb. n.', 'of Poach');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poach', 'v. & n.', 'To cook, as eggs, by breaking them into boiling water;\n   also, to cook with butter after breaking in a vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poach', 'v. & n.', 'To rob of game; to pocket and convey away by stealth,\n   as game; hence, to plunder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poach', 'v. i.', 'To steal or pocket game, or to carry it away privately,\n   as in a bag; to kill or destroy game contrary to law, especially by\n   night; to hunt or fish unlawfully; as, to poach for rabbits or for\n   salmon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poach', 'v. t.', 'To stab; to pierce; to spear, \\as fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poach', 'v. t.', 'To force, drive, or plunge into anything.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poach', 'v. t.', 'To make soft or muddy by trampling');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poach', 'v. t.', 'To begin and not complete.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poach', 'v. i.', 'To become soft or muddy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poachard', 'n.', 'A common European duck (Aythya ferina); -- called also\n   goldhead, poker, and fresh-water, / red-headed, widgeon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poachard', 'n.', 'The American redhead, which is closely allied to the\n   European poachard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poacher', 'n.', 'One who poaches; one who kills or catches game or fish\n   contrary to law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poacher', 'n.', 'The American widgeon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poachiness', 'n.', 'The state of being poachy; marshiness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poachy', 'a.', 'Wet and soft; easily penetrated by the feet of cattle; --\n   said of land');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poak', 'n.', 'Alt. of Poake');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poake', 'n.', 'Waste matter from the preparation of skins, consisting of\n   hair, lime, oil, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pocan', 'n.', 'The poke (Phytolacca decandra); -- called also pocan bush.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pochard', 'n.', 'See Poachard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pock', 'n.', 'A pustule raised on the surface of the body in variolous and\n   vaccine diseases.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pockarred', 'a.', 'See Pockmarked.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pock-broken', 'a.', 'Broken out, or marked, with smallpox; pock-fretten.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pocket', 'n.', 'A bag or pouch; especially; a small bag inserted in a\n   garment for carrying small articles, particularly money; hence,\n   figuratively, money; wealth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pocket', 'n.', 'One of several bags attached to a billiard table, into\n   which the balls are driven.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pocket', 'n.', 'A large bag or sack used in packing various articles, as\n   ginger, hops, cowries, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pocket', 'n.', 'A hole or space covered by a movable piece of board, as in\n   a floor, boxing, partitions, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pocket', 'n.', 'A cavity in a rock containing a nugget of gold, or other\n   mineral; a small body of ore contained in such a cavity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pocket', 'n.', 'A hole containing water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pocket', 'n.', 'A strip of canvas, sewn upon a sail so that a batten or a\n   light spar can placed in the interspace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pocket', 'n.', 'Same as Pouch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pocketed', 'imp. & p. p.', 'of Pocket');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pocketing', 'p. pr. & vb. n.', 'of Pocket');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pocket', 'v. t.', 'To put, or conceal, in the pocket; as, to pocket the\n   change.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pocket', 'v. t.', 'To take clandestinely or fraudulently.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pocketbook', 'n.', 'A small book or case for carrying papers, money, etc.,\n   in the pocket; also, a notebook for the pocket.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pocketfuls', 'pl. ', 'of Pocketful');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pocketful', 'n.', 'As much as a pocket will hold; enough to fill a pocket;\n   as, pocketfuls of chestnuts.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-knives', 'pl. ', 'of Pocketknife');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pocketknife', 'n.', 'A knife with one or more blades, which fold into the\n   handle so as to admit of being carried in the pocket.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pock-fretten', 'a.', 'See Pockmarked.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pockiness', 'n.', 'The state of being pocky.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pockmark', 'n.', 'A mark or pit made by smallpox.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pockmarked', 'a.', 'Marked by smallpox; pitted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pock-pitted', 'a.', 'Pockmarked; pitted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pock-pudding', 'n.', 'A bag pudding; a name of reproach or ridicule\n   formerly applied by the Scotch to the English.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pockwood', 'n.', 'Lignum-vitae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pocky', 'superl.', 'Full of pocks; affected with smallpox or other eruptive\n   disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poco', 'adv.', 'A little; -- used chiefly in phrases indicating the time or\n   movement; as, poco piu allegro, a little faster; poco largo, rather\n   slow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pocock', 'n.', 'Peacock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pococurante', 'n.', 'A careless person; a trifler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pococurantism', 'n.', 'Carelessness; apathy; indifference.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pocoson', 'n.', 'Low, wooded grounds or swamps in Eastern Maryland and\n   Virginia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poculent', 'a.', 'Fit for drink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poculiform', 'a.', 'Having the shape of a goblet or drinking cup.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-pod', '', 'A combining form or suffix from Gr. poy`s, podo`s, foot; as,\n   decapod, an animal having ten feet; phyllopod, an animal having\n   leaflike feet; myriapod, hexapod.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pod', 'n.', 'A bag; a pouch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pod', 'n.', 'A capsule of plant, especially a legume; a dry dehiscent\n   fruit. See Illust. of Angiospermous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pod', 'n.', 'A considerable number of animals closely clustered together;\n   -- said of seals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Podded', 'imp. & p. p.', 'of Pod');
  INSERT INTO words (word, word_type, description) VALUES  (    'Podding', 'p. pr. & vb. n.', 'of Pod');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pod', 'v. i.', 'To swell; to fill; also, to produce pods.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-poda', '', 'A New Latin plural combining form or suffix from Gr. /, /,\n   foot; as, hexapoda, myriapoda. See -pod.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Podagra', 'n.', 'Gout in the joints of the foot; -- applied also to gout in\n   other parts of body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Podagric', 'a.', 'Alt. of Podagrical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Podagrical', 'a.', 'Pertaining to the gout; gouty; caused by gout.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Podagrical', 'a.', 'Afflicted with gout.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Podagrous', 'a.', 'Gouty; podagric.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Podalgia', 'n.', 'pain in the foot, due to gout, rheumatism, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Podarthra', 'pl. ', 'of Podarthrum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Podarthrum', 'n.', 'The foot joint; in birds, the joint between the\n   metatarsus and the toes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Podded', 'a.', 'Having pods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Podder', 'n.', 'One who collects pods or pulse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Podesta', 'n.', 'One of the chief magistrates of the Italian republics in\n   the Middle Ages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Podesta', 'n.', 'A mayor, alderman, or other magistrate, in some towns of\n   Italy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Podetia', 'pl. ', 'of Podetium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Podetiums', 'pl. ', 'of Podetium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Podetium', 'n.', 'A stalk which bears the fructification in some lichens,\n   as in the so-called reindeer moss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Podge', 'n.', 'A puddle; a plash.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Podge', 'n.', 'Porridge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Podgy', 'a.', 'Fat and short; pudgy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Podical', 'a.', 'Anal; -- applied to certain organs of insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Podiceps', 'n.', 'See Grebe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Podia', 'pl. ', 'of Podium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Podium', 'n.', 'A low wall, serving as a foundation, a substructure, or a\n   terrace wall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Podium', 'n.', 'The dwarf wall surrounding the arena of an amphitheater,\n   from the top of which the seats began.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Podium', 'n.', 'The masonry under the stylobate of a temple, sometimes a\n   mere foundation, sometimes containing chambers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Podium', 'n.', 'The foot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Podley', 'n.', 'A young coalfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Podo-', '', 'A combining form or prefix from Gr. poy`s, podo`s, foot; as,\n   podocarp, podocephalous, podology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Podobranch', 'n.', 'One of the branchiae attached to the bases of the legs\n   in Crustacea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Podobranchiae', 'pl. ', 'of Podobranchia');
  INSERT INTO words (word, word_type, description) VALUES  (    'Podobranchia', 'n.', 'Same as Podobranch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Podocarp', 'n.', 'A stem, or footstalk, supporting the fruit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Podocephalous', 'a.', 'Having a head of flowers on a long peduncle, or\n   footstalk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Podogynium', 'n.', 'Same as Basigynium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Podophthalmia', 'n. pl.', 'The stalk-eyed Crustacea, -- an order of\n   Crustacea having the eyes supported on movable stalks. It includes the\n   crabs, lobsters, and prawns. Called also Podophthalmata, and Decapoda.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Podophthalmic', 'a.', 'Alt. of Podophthalmous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Podophthalmous', 'a.', 'Having the eyes on movable footstalks, or pedicels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Podophthalmous', 'a.', 'Of or pertaining to the Podophthalmia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Podophthalmite', 'n.', 'The eyestalk of a crustacean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Podophyllin', 'n.', 'A brown bitter gum extracted from the rootstalk of the\n   May apple (Podophyllum peltatum). It is a complex mixture of several\n   substances.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Podophyllous', 'a.', 'Having thin, flat, leaflike locomotive organs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Podophyllous', 'a.', 'Pertaining to, or composing, the layer of tissue,\n   made up of laminae, beneath a horses hoof.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Podophyllum', 'n.', 'A genus of herbs of the Barberry family, having large\n   palmately lobed peltate leaves and solitary flower. There are two\n   species, the American Podohyllum peltatum, or May apple, the Himalayan\n   P. Emodi.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Podophyllum', 'n.', 'The rhizome and rootlet of the May apple (Podophyllum\n   peltatum), -- used as a cathartic drug.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Podoscaph', 'n.', 'A canoe-shaped float attached to the foot, for walking\n   on water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Podosperm', 'n.', 'The stalk of a seed or ovule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Podostomata', 'n. pl.', 'An order of Bryozoa of which Rhabdopleura is the\n   type. See Rhabdopleura.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Podothecae', 'pl. ', 'of Podotheca');
