  INSERT INTO words (word, word_type, description) VALUES  (    'Podrida', 'n.', 'A miscellaneous dish of meats. See Olla-podrida.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Podurae', 'pl. ', 'of Podura');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poduras', 'pl. ', 'of Podura');
