  INSERT INTO words (word, word_type, description) VALUES  (    'Podurid', 'n.', 'Any species of Podura or allied genera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Podurid', 'a.', 'Pertaining to the poduras.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poe', 'n.', 'Same as Poi.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poebird', 'n.', 'The parson bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poecile', 'n.', 'Same as Poicile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poecilitic', 'a.', 'Mottled with various colors; variegated; spotted; --\n   said of certain rocks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poecilitic', 'a.', 'Specifically: Of or pertaining to, or characterizing,\n   Triassic and Permian sandstones of red and other colors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poecilopod', 'n.', 'One of the Poecilopoda. Also used adjectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poecilopoda', 'n. pl.', 'Originally, an artificial group including many\n   parasitic Entomostraca, together with the horseshoe crabs (Limuloidea).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poecilopoda', 'n. pl.', 'By some recent writers applied to the Merostomata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poem', 'n.', 'A metrical composition; a composition in verse written in\n   certain measures, whether in blank verse or in rhyme, and characterized\n   by imagination and poetic diction; -- contradistinguished from prose;\n   as, the poems of Homer or of Milton.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poem', 'n.', 'A composition, not in verse, of which the language is highly\n   imaginative or impassioned; as, a prose poem; the poems of Ossian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poematic', 'a.', 'Pertaining to a poem, or to poetry; poetical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poenamu', 'n.', 'A variety of jade or nephrite, -- used in New Zealand for\n   the manufacture of axes and weapons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poenology', 'n.', 'See Penology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poephaga', 'n. pl.', 'A group of herbivorous marsupials including the\n   kangaroos and their allies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poesy', 'n.', 'The art of composing poems; poetical skill or faculty; as,\n   the heavenly gift of poesy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poesy', 'n.', 'Poetry; metrical composition; poems.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poesy', 'n.', 'A short conceit or motto engraved on a ring or other thing;\n   a posy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poet', 'n.', 'One skilled in making poetry; one who has a particular genius\n   for metrical composition; the author of a poem; an imaginative thinker\n   or writer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poetaster', 'n.', 'An inferior rhymer, or writer of verses; a dabbler in\n   poetic art.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poetastry', 'n.', 'The works of a poetaster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poetess', 'n.', 'A female poet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poetic', 'a.', 'Alt. of Poetical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poetical', 'a.', 'Of or pertaining to poetry; suitable for poetry, or for\n   writing poetry; as, poetic talent, theme, work, sentiments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poetical', 'a.', 'Expressed in metrical form; exhibiting the imaginative or\n   the rhythmical quality of poetry; as, a poetical composition; poetical\n   prose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poetically', 'adv.', 'In a poetic manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poetics', 'n.', 'The principles and rules of the art of poetry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poeticule', 'n.', 'A poetaster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poetized', 'imp. & p. p.', 'of Poetize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poetizing', 'p. pr. & vb. n.', 'of Poetize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poetize', 'v. i.', 'To write as a poet; to compose verse; to idealize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poetry', 'n.', 'The art of apprehending and interpreting ideas by the\n   faculty of imagination; the art of idealizing in thought and in\n   expression.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poetry', 'n.', 'Imaginative language or composition, whether expressed\n   rhythmically or in prose. Specifically: Metrical composition; verse;\n   rhyme; poems collectively; as, heroic poetry; dramatic poetry; lyric or\n   Pindaric poetry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poetship', 'n.', 'The state or personality of a poet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poggy', 'n.', 'See Porgy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poggy', 'n.', 'A small whale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pogy', 'n.', 'The menhaden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poh', 'interj.', 'An exclamation expressing contempt or disgust; bah !');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pohagen', 'n.', 'See Pauhaugen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poi', 'n.', 'A national food of the Hawaiians, made by baking and pounding\n   the kalo (or taro) root, and reducing it to a thin paste, which is\n   allowed to ferment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poicile', 'n.', 'Alt. of Poecile');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poecile', 'n.', 'The frescoed porch or gallery in Athens where Zeno taught.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poignancy', 'n.', 'The quality or state of being poignant; as, the\n   poignancy of satire; the poignancy of grief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poignant', 'a.', 'Pricking; piercing; sharp; pungent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poignant', 'a.', 'Fig.: Pointed; keen; satirical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poignantly', 'adv.', 'In a poignant manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poikilitic', 'a.', 'See Poecilitic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poikilocyte', 'n.', 'An irregular form of corpuscle found in the blood in\n   cases of profound anaemia, probably a degenerated red blood corpuscle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poikilothermal', 'a.', 'Alt. of Poikilothermic');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poikilothermic', 'a.', 'Having a varying body temperature. See\n   Homoiothermal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poikilothermous', 'a.', 'Poikilothermal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poinciana', 'n.', 'A prickly tropical shrub (Caesalpinia, formerly\n   Poinciana, pulcherrima), with bipinnate leaves, and racemes of showy\n   orange-red flowers with long crimson filaments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poind', 'v. t.', 'To impound, as cattle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poind', 'v. t.', 'To distrain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poinder', 'n.', 'The keeper of a cattle pound; a pinder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poinder', 'n.', 'One who distrains property.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poinsettia', 'n.', 'A Mexican shrub (Euphorbia pulcherrima) with very large\n   and conspicuous vermilion bracts below the yellowish flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Point', 'v. t. & i.', 'To appoint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Point', 'n.', 'That which pricks or pierces; the sharp end of anything,\n   esp. the sharp end of a piercing instrument, as a needle or a pin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Point', 'n.', 'An instrument which pricks or pierces, as a sort of needle\n   used by engravers, etchers, lace workers, and others; also, a pointed\n   cutting tool, as a stone cutters point; -- called also pointer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Point', 'n.', 'Anything which tapers to a sharp, well-defined termination.\n   Specifically: A small promontory or cape; a tract of land extending\n   into the water beyond the common shore line.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Point', 'n.', 'The mark made by the end of a sharp, piercing instrument, as\n   a needle; a prick.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Point', 'n.', 'An indefinitely small space; a mere spot indicated or\n   supposed. Specifically: (Geom.) That which has neither parts nor\n   magnitude; that which has position, but has neither length, breadth,\n   nor thickness, -- sometimes conceived of as the limit of a line; that\n   by the motion of which a line is conceived to be produced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Point', 'n.', 'An indivisible portion of time; a moment; an instant; hence,\n   the verge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Point', 'n.', 'A mark of punctuation; a character used to mark the\n   divisions of a composition, or the pauses to be observed in reading, or\n   to point off groups of figures, etc.; a stop, as a comma, a semicolon,\n   and esp. a period; hence, figuratively, an end, or conclusion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Point', 'n.', 'Whatever serves to mark progress, rank, or relative\n   position, or to indicate a transition from one state or position to\n   another, degree; step; stage; hence, position or condition attained;\n   as, a point of elevation, or of depression; the stock fell off five\n   points; he won by tenpoints.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Point', 'n.', 'That which arrests attention, or indicates qualities or\n   character; a salient feature; a characteristic; a peculiarity; hence, a\n   particular; an item; a detail; as, the good or bad points of a man, a\n   horse, a book, a story, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Point', 'n.', 'Hence, the most prominent or important feature, as of an\n   argument, discourse, etc.; the essential matter; esp., the proposition\n   to be established; as, the point of an anecdote.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Point', 'n.', 'A small matter; a trifle; a least consideration; a\n   punctilio.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Point', 'n.', 'A dot or mark used to designate certain tones or time');
  INSERT INTO words (word, word_type, description) VALUES  (    'Point', 'n.', 'A dot or mark distinguishing or characterizing certain tones\n   or styles; as, points of perfection, of augmentation, etc.; hence, a\n   note; a tune.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Point', 'n.', 'A dot placed at the right hand of a note, to raise its\n   value, or prolong its time, by one half, as to make a whole note equal\n   to three half notes, a half note equal to three quarter notes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Point', 'n.', 'A fixed conventional place for reference, or zero of\n   reckoning, in the heavens, usually the intersection of two or more\n   great circles of the sphere, and named specifically in each case\n   according to the position intended; as, the equinoctial points; the\n   solstitial points; the nodal points; vertical points, etc. See\n   Equinoctial Nodal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Point', 'n.', 'One of the several different parts of the escutcheon. See\n   Escutcheon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Point', 'n.', 'One of the points of the compass (see Points of the compass,\n   below); also, the difference between two points of the compass; as, to\n   fall off a point.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Point', 'n.', 'A short piece of cordage used in reefing sails. See Reef\n   point, under Reef.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Point', 'n.', 'A a string or lace used to tie together certain parts of the\n   dress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Point', 'n.', 'Lace wrought the needle; as, point de Venise; Brussels\n   point. See Point lace, below.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Point', 'n.', 'A switch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Point', 'n.', 'An item of private information; a hint; a tip; a pointer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Point', 'n.', 'A fielder who is stationed on the off side, about twelve or\n   fifteen yards from, and a little in advance of, the batsman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Point', 'n.', 'The attitude assumed by a pointer dog when he finds game;\n   as, the dog came to a point. See Pointer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Point', 'n.', 'A standard unit of measure for the size of type bodies,\n   being one twelfth of the thickness of pica type. See Point system of\n   type, under Type.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Point', 'n.', 'A tyne or snag of an antler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Point', 'n.', 'One of the spaces on a backgammon board.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Point', 'n.', 'A movement executed with the saber or foil; as, tierce\n   point.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pointed', 'imp. & p. p.', 'of Point');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pointing', 'p. pr. & vb. n.', 'of Point');
  INSERT INTO words (word, word_type, description) VALUES  (    'Point', 'n.', 'To give a point to; to sharpen; to cut, forge, grind, or\n   file to an acute end; as, to point a dart, or a pencil. Used also\n   figuratively; as, to point a moral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Point', 'n.', 'To direct toward an abject; to aim; as, to point a gun at a\n   wolf, or a cannon at a fort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Point', 'n.', 'Hence, to direct the attention or notice of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Point', 'n.', 'To supply with punctuation marks; to punctuate; as, to point\n   a composition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Point', 'n.', 'To mark (as Hebrew) with vowel points.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Point', 'n.', 'To give particular prominence to; to designate in a special\n   manner; to indicate, as if by pointing; as, the error was pointed out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Point', 'n.', 'To indicate or discover by a fixed look, as game.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Point', 'n.', 'To fill up and finish the joints of (a wall), by introducing\n   additional cement or mortar, and bringing it to a smooth surface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Point', 'n.', 'To cut, as a surface, with a pointed tool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Point', 'v. i.', 'To direct the point of something, as of a finger, for the\n   purpose of designating an object, and attracting attention to it; --\n   with at.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Point', 'v. i.', 'To indicate the presence of game by fixed and steady\n   look, as certain hunting dogs do.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Point', 'v. i.', 'To approximate to the surface; to head; -- said of an\n   abscess.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pointal', 'n.', 'The pistil of a plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pointal', 'n.', 'A kind of pencil or style used with the tablets of the\n   Middle Ages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pointal', 'n.', 'See Poyntel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Point-blank', 'n.', 'The white spot on a target, at which an arrow or other\n   missile is aimed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Point-blank', 'n.', 'With all small arms, the second point in which the\n   natural line of sight, when horizontal, cuts the trajectory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Point-blank', 'n.', 'With artillery, the point where the projectile first\n   strikes the horizontal plane on which the gun stands, the axis of the\n   piece being horizontal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Point-blank', 'a.', 'Directed in a line toward the object aimed at; aimed\n   directly toward the mark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Point-blank', 'a.', 'Hence, direct; plain; unqualified; -- said of\n   language; as, a point-blank assertion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Point-blank', 'adv.', 'In a point-blank manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Point dappui', '', 'See under Appui.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Point-device', 'a.', 'Alt. of Point-devise');
  INSERT INTO words (word, word_type, description) VALUES  (    'Point-devise', 'a.', 'Uncommonly nice and exact; precise; particular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Point-device', 'adv.', 'Alt. of Point-devise');
  INSERT INTO words (word, word_type, description) VALUES  (    'Point-devise', 'adv.', 'Exactly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pointed', 'a.', 'Sharp; having a sharp point; as, a pointed rock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pointed', 'a.', 'Characterized by sharpness, directness, or pithiness of\n   expression; terse; epigrammatic; especially, directed to a particular\n   person or thing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pointel', 'n.', 'See Pointal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pointer', 'n.', 'One who, or that which, points.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pointer', 'n.', 'The hand of a timepiece.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pointer', 'n.', 'One of a breed of dogs trained to stop at scent of game,\n   and with the nose point it out to sportsmen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pointer', 'n.', 'The two stars (Merak and Dubhe) in the Great Bear, the\n   line between which points nearly in the direction of the north star.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pointer', 'n.', 'Diagonal braces sometimes fixed across the hold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pointing', 'n.', 'The act of sharpening.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pointing', 'n.', 'The act of designating, as a position or direction, by\n   means of something pointed, as a finger or a rod.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pointing', 'n.', 'The act or art of punctuating; punctuation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pointing', 'n.', 'The act of filling and finishing the joints in masonry\n   with mortar, cement, etc.; also, the material so used.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pointing', 'n.', 'The rubbing off of the point of the wheat grain in the\n   first process of high milling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pointing', 'n.', 'The act or process of measuring, at the various distances\n   from the surface of a block of marble, the surface of a future piece of\n   statuary; also, a process used in cutting the statue from the artists\n   model.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pointingstock', 'n.', 'An object of ridicule or scorn; a laughingstock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pointless', 'a.', 'Having no point; blunt; wanting keenness; obtuse; as, a\n   pointless sword; a pointless remark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pointlessly', 'adv.', 'Without point.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pointleted', 'a.', 'Having a small, distinct point; apiculate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pointrel', 'n.', 'A graving tool.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-men', 'pl. ', 'of Pointsman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pointsman', 'n.', 'A man who has charge of railroad points or switches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poise', 'v.', 'Weight; gravity; that which causes a body to descend;\n   heaviness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poise', 'v.', 'The weight, or mass of metal, used in weighing, to balance\n   the substance weighed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poise', 'v.', 'The state of being balanced by equal weight or power;\n   equipoise; balance; equilibrium; rest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poise', 'v.', 'That which causes a balance; a counterweight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poised', 'imp. & p. p.', 'of Poise');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poising', 'p. pr. & vb. n.', 'of Poise');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poise', 'n.', 'To balance; to make of equal weight; as, to poise the scales\n   of a balance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poise', 'n.', 'To hold or place in equilibrium or equiponderance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poise', 'n.', 'To counterpoise; to counterbalance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poise', 'n.', 'To ascertain, as by the balance; to weigh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poise', 'n.', 'To weigh (down); to oppress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poise', 'v. i.', 'To hang in equilibrium; to be balanced or suspended;\n   hence, to be in suspense or doubt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poiser', 'n.', 'The balancer of dipterous insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poison', 'n.', 'Any agent which, when introduced into the animal organism,\n   is capable of producing a morbid, noxious, or deadly effect upon it;\n   as, morphine is a deadly poison; the poison of pestilential diseases.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poison', 'n.', 'That which taints or destroys moral purity or health; as,\n   the poison of evil example; the poison of sin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poisoned', 'imp. & p. p.', 'of Poison');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poisoning', 'p. pr. & vb. n.', 'of Poison');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poison', 'n.', 'To put poison upon or into; to infect with poison; as, to\n   poison an arrow; to poison food or drink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poison', 'n.', 'To injure or kill by poison; to administer poison to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poison', 'n.', 'To taint; to corrupt; to vitiate; as, vice poisons\n   happiness; slander poisoned his mind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poison', 'v. i.', 'To act as, or convey, a poison.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poisonable', 'a.', 'Capable of poisoning; poisonous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poisonable', 'a.', 'Capable of being poisoned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poisoner', 'n.', 'One who poisons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poisonous', 'a.', 'Having the qualities or effects of poison; venomous;\n   baneful; corrupting; noxious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poisonsome', 'a.', 'Poisonous.[Obs.] Holland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poisure', 'n.', 'Weight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poitrel', 'a.', 'The breastplate of the armor of a horse. See Peytrel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poize', 'n.', 'See Poise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pokal', 'n.', 'A tall drinking cup.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poke', 'n.', 'A large North American herb of the genus Phytolacca (P.\n   decandra), bearing dark purple juicy berries; -- called also garget,\n   pigeon berry, pocan, and pokeweed. The root and berries have emetic and\n   purgative properties, and are used in medicine. The young shoots are\n   sometimes eaten as a substitute for asparagus, and the berries are said\n   to be used in Europe to color wine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poke', 'n.', 'A bag; a sack; a pocket.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poke', 'n.', 'A long, wide sleeve; -- called also poke sleeve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poked', 'imp. & p. p.', 'of Poke');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poking', 'p. pr. & vb. n.', 'of Poke');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poke', 'v. t.', 'To thrust or push against or into with anything pointed;\n   hence, to stir up; to excite; as, to poke a fire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poke', 'v. t.', 'To thrust with the horns; to gore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poke', 'v. t.', 'To put a poke on; as, to poke an ox.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poke', 'v. i.', 'To search; to feel ones way, as in the dark; to grope;\n   as, to poke about.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poke', 'n.', 'The act of poking; a thrust; a jog; as, a poke in the ribs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poke', 'n.', 'A lazy person; a dawdler; also, a stupid or uninteresting\n   person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poke', 'n.', 'A contrivance to prevent an animal from leaping or breaking\n   through fences. It consists of a yoke with a pole inserted, pointed\n   forward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pokebag', 'n.', 'The European long-tailed titmouse; -- called also\n   poke-pudding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poker', 'n.', 'One who pokes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poker', 'n.', 'That which pokes or is used in poking, especially a metal\n   bar or rod used in stirring a fire of coals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poker', 'n.', 'A poking-stick.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poker', 'n.', 'The poachard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poker', 'n.', 'A game at cards derived from brag, and first played about\n   1835 in the Southwestern United States.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poker', 'n.', 'Any imagined frightful object, especially one supposed to\n   haunt the darkness; a bugbear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pokerish', 'a.', 'Infested by pokers; adapted to excite fear; as, a\n   pokerish place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pokerish', 'a.', 'Stiff like a poker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poket', 'n.', 'A pocket.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pokeweed', 'n.', 'See Poke, the plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pokey', 'a.', 'See Poky.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poking', 'a.', 'Drudging; servile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poking-stick', 'n.', 'A small stick or rod of steel, formerly used in\n   adjusting the plaits of ruffs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poky', 'a.', 'Confined; cramped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poky', 'a.', 'Dull; tedious; uninteresting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polacca', 'n.', 'A vessel with two or three masts, used in the\n   Mediterranean. The masts are usually of one piece, and without tops,\n   caps, or crosstrees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polacca', 'n.', 'See Polonaise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polack', 'n.', 'A Polander.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polacre', 'n.', 'Same as Polacca, 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polander', 'n.', 'A native or inhabitant of Poland; a Pole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polar', 'a.', 'Of or pertaining to one of the poles of the earth, or of a\n   sphere; situated near, or proceeding from, one of the poles; as, polar\n   regions; polar seas; polar winds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polar', 'a.', 'Of or pertaining to the magnetic pole, or to the point to\n   which the magnetic needle is directed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polar', 'a.', 'Pertaining to, reckoned from, or having a common radiating\n   point; as, polar coordinates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polar', 'n.', 'The right line drawn through the two points of contact of\n   the two tangents drawn from a given point to a given conic section. The\n   given point is called the pole of the line. If the given point lies\n   within the curve so that the two tangents become imaginary, there is\n   still a real polar line which does not meet the curve, but which\n   possesses other properties of the polar. Thus the focus and directrix\n   are pole and polar. There are also poles and polar curves to curves of\n   higher degree than the second, and poles and polar planes to surfaces\n   of the second degree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polarchy', 'n.', 'See Polyarchy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polaric', 'a.', 'See Polar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polarily', 'adv.', 'In a polary manner; with polarity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polarimeter', 'n.', 'An instrument for determining the amount of\n   polarization of light, or the proportion of polarized light, in a\n   partially polarized ray.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polarimetry', 'n.', 'The art or process of measuring the polarization of\n   light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polaris', 'n.', 'The polestar. See North star, under North.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polariscope', 'n.', 'An instrument consisting essentially of a polarizer\n   and an analyzer, used for polarizing light, and analyzing its\n   properties.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polariscopic', 'a.', 'Of or pertaining to the polariscope; obtained by the\n   use of a polariscope; as, polariscopic observations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polariscopy', 'n.', 'The art or rocess of making observations with the\n   polariscope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polaristic', 'a.', 'Pertaining to, or exhibiting, poles; having a polar\n   arrangement or disposition; arising from, or dependent upon, the\n   possession of poles or polar characteristics; as, polaristic\n   antagonism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polarity', 'n.', 'That quality or condition of a body in virtue of which it\n   exhibits opposite, or contrasted, properties or powers, in opposite, or\n   contrasted, parts or directions; or a condition giving rise to a\n   contrast of properties corresponding to a contrast of positions, as,\n   for example, attraction and repulsion in the opposite parts of a\n   magnet, the dissimilar phenomena corresponding to the different sides\n   of a polarized ray of light, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polarity', 'n.', 'A property of the conic sections by virtue of which a\n   given point determines a corresponding right line and a given right\n   line determines a corresponding point. See Polar, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polarizable', 'a.', 'Susceptible of polarization.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polarization', 'n.', 'The act of polarizing; the state of being polarized,\n   or of having polarity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polarization', 'n.', 'A peculiar affection or condition of the rays of\n   light or heat, in consequence of which they exhibit different\n   properties in different directions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polarization', 'n.', 'An effect produced upon the plates of a voltaic\n   battery, or the electrodes in an electrolytic cell, by the deposition\n   upon them of the gases liberated by the action of the current. It is\n   chiefly due to the hydrogen, and results in an increase of the\n   resistance, and the setting up of an opposing electro-motive force,\n   both of which tend materially to weaken the current of the battery, or\n   that passing through the cell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polarized', 'imp. & p. p.', 'of Polarize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polarizing', 'p. pr. & vb. n.', 'of Polarize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polarize', 'v. t.', 'To communicate polarity to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polarizer', 'n.', 'That which polarizes; especially, the part of a\n   polariscope which receives and polarizes the light. It is usually a\n   reflecting plate, or a plate of some crystal, as tourmaline, or a\n   doubly refracting crystal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polary', 'a.', 'Tending to a pole; having a direction toward a pole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polatouche', 'n.', 'A flying squirrel (Sciuropterus volans) native of\n   Northern Europe and Siberia; -- called also minene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polder', 'n.', 'A tract of low land reclaimed from the sea by of high\n   embankments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poldway', 'n.', 'A kind of coarse bagging, -- used for coal sacks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pole', 'n.', 'A native or inhabitant of Poland; a Polander.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pole', 'n.', 'A long, slender piece of wood; a tall, slender piece of\n   timber; the stem of a small tree whose branches have been removed; as,\n   specifically: (a) A carriage pole, a wooden bar extending from the\n   front axle of a carriage between the wheel horses, by which the\n   carriage is guided and held back. (b) A flag pole, a pole on which a\n   flag is supported. (c) A Maypole. See Maypole. (d) A barbers pole, a\n   pole painted in stripes, used as a sign by barbers and hairdressers.\n   (e) A pole on which climbing beans, hops, or other vines, are trained.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pole', 'n.', 'A measuring stick; also, a measure of length equal to 5/\n   yards, or a square measure equal to 30/ square yards; a rod; a perch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poled', 'imp. & p. p.', 'of Pole');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poling', 'p. pr. & vb. n.', 'of Pole');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pole', 'v. t.', 'To furnish with poles for support; as, to pole beans or\n   hops.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pole', 'v. t.', 'To convey on poles; as, to pole hay into a barn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pole', 'v. t.', 'To impel by a pole or poles, as a boat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pole', 'v. t.', 'To stir, as molten glass, with a pole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pole', 'n.', 'Either extremity of an axis of a sphere; especially, one of\n   the extremities of the earths axis; as, the north pole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pole', 'n.', 'A point upon the surface of a sphere equally distant from\n   every part of the circumference of a great circle; or the point in\n   which a diameter of the sphere perpendicular to the plane of such\n   circle meets the surface. Such a point is called the pole of that\n   circle; as, the pole of the horizon; the pole of the ecliptic; the pole\n   of a given meridian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pole', 'n.', 'One of the opposite or contrasted parts or directions in\n   which a polar force is manifested; a point of maximum intensity of a\n   force which has two such points, or which has polarity; as, the poles\n   of a magnet; the north pole of a needle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pole', 'n.', 'The firmament; the sky.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pole', 'n.', 'See Polarity, and Polar, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poleax', 'n.', 'Alt. of Poleaxe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poleaxe', 'n.', 'Anciently, a kind of battle-ax with a long handle; later,\n   an ax or hatchet with a short handle, and a head variously patterned;\n   -- used by soldiers, and also by sailors in boarding a vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polecat', 'n.', 'A small European carnivore of the Weasel family (Putorius\n   foetidus). Its scent glands secrete a substance of an exceedingly\n   disagreeable odor. Called also fitchet, foulmart, and European ferret.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polecat', 'n.', 'The zorilla. The name is also applied to other allied\n   species.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poledavy', 'n.', 'A sort of coarse canvas; poldway.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poleless', 'a.', 'Without a pole; as, a poleless chariot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polemarch', 'n.', 'In Athens, originally, the military commanderin-chief;\n   but, afterward, a civil magistrate who had jurisdiction in respect of\n   strangers and sojourners. In other Grecian cities, a high military and\n   civil officer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polemic', 'a.', 'Of or pertaining to controversy; maintaining, or\n   involving, controversy; controversial; disputative; as, a polemic\n   discourse or essay; polemic theology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polemic', 'a.', 'Engaged in, or addicted to, polemics, or to controversy;\n   disputations; as, a polemic writer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polemic', 'n.', 'One who writes in support of one opinion, doctrine, or\n   system, in opposition to another; one skilled in polemics; a\n   controversialist; a disputant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polemic', 'n.', 'A polemic argument or controversy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polemical', 'a.', 'Polemic; controversial; disputatious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polemicist', 'n.', 'A polemic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polemics', 'n.', 'The art or practice of disputation or controversy,\n   especially on religious subjects; that branch of theological science\n   which pertains to the history or conduct of ecclesiastical controversy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polemist', 'n.', 'A polemic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polemoniaceous', 'a.', 'Of or pertaining to a natural order of plants\n   (Polemoniaceae), which includes Polemonium, Phlox, Gilia, and a few\n   other genera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polemonium', 'n.', 'A genus of gamopetalous perennial herbs, including the\n   Jacobs ladder and the Greek valerian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polemoscope', 'n.', 'An opera glass or field glass with an oblique mirror\n   arranged for seeing objects do not lie directly before the eye; --\n   called also diagonal, / side, opera glass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polemy', 'n.', 'Warfare; war; hence, contention; opposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polenta', 'n.', 'Pudding made of Indian meal; also, porridge made of\n   chestnut meal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poler', 'n.', 'One who poles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poler', 'n.', 'An extortioner. See Poller.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polestar', 'n.', 'Polaris, or the north star. See North star, under North.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polestar', 'n.', 'A guide or director.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polewards', 'adv.', 'Toward a pole of the earth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polewig', 'n.', 'The European spotted goby (Gobius minutus); -- called also\n   pollybait.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poley', 'n.', 'See Poly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poley', 'a.', 'Without horns; polled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polianite', 'n.', 'Manganese dioxide, occurring in tetragonal crystals\n   nearly as hard as quartz.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Policate', 'a.', 'Same as Pollicate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Police', 'n.', 'A judicial and executive system, for the government of a\n   city, town, or district, for the preservation of rights, order,\n   cleanliness, health, etc., and for the enforcement of the laws and\n   prevention of crime; the administration of the laws and regulations of\n   a city, incorporated town, or borough.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Police', 'n.', 'That which concerns the order of the community; the\n   internal regulation of a state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Police', 'n.', 'The organized body of civil officers in a city, town, or\n   district, whose particular duties are the preservation of good order,\n   the prevention and detection of crime, and the enforcement of the laws.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Police', 'n.', 'Military police, the body of soldiers detailed to preserve\n   civil order and attend to sanitary arrangements in a camp or garrison.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Police', 'n.', 'The cleaning of a camp or garrison, or the state / a camp\n   as to cleanliness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Policed', 'imp. & p. p.', 'of Police');
  INSERT INTO words (word, word_type, description) VALUES  (    'Policing', 'p. pr. & vb. n.', 'of Police');
  INSERT INTO words (word, word_type, description) VALUES  (    'Police', 'v. t.', 'To keep in order by police.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Police', 'v. t.', 'To make clean; as, to police a camp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Policed', 'a.', 'Regulated by laws for the maintenance of peace and order,\n   enforced by organized administration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Policemen', 'pl. ', 'of Policeman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Policeman', 'n.', 'A member of a body of police; a constable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Policial', 'a.', 'Relating to the police.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Policied', 'a.', 'Policed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Policies', 'pl. ', 'of Policy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Policy', 'n.', 'Civil polity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Policy', 'n.', 'The settled method by which the government and affairs of a\n   nation are, or may be, administered; a system of public or official\n   administration, as designed to promote the external or internal\n   prosperity of a state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Policy', 'n.', 'The method by which any institution is administered; system\n   of management; course.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Policy', 'n.', 'Management or administration based on temporal or material\n   interest, rather than on principles of equity or honor; hence, worldly\n   wisdom; dexterity of management; cunning; stratagem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Policy', 'n.', 'Prudence or wisdom in the management of public and private\n   affairs; wisdom; sagacity; wit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Policy', 'n.', 'Motive; object; inducement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Policied', 'imp. & p. p.', 'of Policy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Policying', 'p. pr. & vb. n.', 'of Policy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Policy', 'v. t.', 'To regulate by laws; to reduce to order.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Policy', 'n.', 'A ticket or warrant for money in the public funds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Policy', 'n.', 'The writing or instrument in which a contract of insurance\n   is embodied; an instrument in writing containing the terms and\n   conditions on which one party engages to indemnify another against loss\n   arising from certain hazards, perils, or risks to which his person or\n   property may be exposed. See Insurance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Policy', 'n.', 'A method of gambling by betting as to what numbers will be\n   drawn in a lottery; as, to play policy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poling', 'n.', 'The act of supporting or of propelling by means of a pole\n   or poles; as, the poling of beans; the poling of a boat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poling', 'n.', 'The operation of dispersing worm casts over the walks with\n   poles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poling', 'n.', 'One of the poles or planks used in upholding the side earth\n   in excavating a tunnel, ditch, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polish', 'a.', 'Of or pertaining to Poland or its inhabitants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polish', 'n.', 'The language of the Poles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polished', 'imp. & p. p.', 'of Polish');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polishing', 'p. pr. & vb. n.', 'of Polish');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polish', 'v. t.', 'To make smooth and glossy, usually by friction; to\n   burnish; to overspread with luster; as, to polish glass, marble,\n   metals, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polish', 'v. t.', 'Hence, to refine; to wear off the rudeness, coarseness,\n   or rusticity of; to make elegant and polite; as, to polish life or\n   manners.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polish', 'v. i.', 'To become smooth, as from friction; to receive a gloss;\n   to take a smooth and glossy surface; as, steel polishes well.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polish', 'n.', 'A smooth, glossy surface, usually produced by friction; a\n   gloss or luster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polish', 'n.', 'Anything used to produce a gloss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polish', 'n.', 'Fig.: Refinement; elegance of manners.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polishable', 'a.', 'Capable of being polished.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polished', 'a.', 'Made smooth and glossy, as by friction; hence, highly\n   finished; refined; polite; as, polished plate; polished manners;\n   polished verse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polishedness', 'n.', 'The quality of being polished.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polisher', 'n.', 'One who, or that which, polishes; also, that which is\n   used in polishing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polishing', '', 'a. & n. from Polish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polishment', 'n.', 'The act of polishing, or the state of being polished.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polite', 'v.', 'Smooth; polished.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polite', 'v.', 'Smooth and refined in behavior or manners; well bred;\n   courteous; complaisant; obliging; civil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polite', 'v.', 'Characterized by refinement, or a high degree of finish;\n   as, polite literature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polite', 'v. t.', 'To polish; to refine; to render polite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Politely', 'adv.', 'In a polished manner; so as to be smooth or glossy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Politely', 'adv.', 'In a polite manner; with politeness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Politeness', 'n.', 'High finish; smoothness; burnished elegance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Politeness', 'n.', 'The quality or state of being polite; refinement of\n   manners; urbanity; courteous behavior; complaisance; obliging\n   attentions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Politesse', 'n.', 'Politeness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Politic', 'a.', 'Of or pertaining to polity, or civil government;\n   political; as, the body politic. See under Body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Politic', 'a.', 'Pertaining to, or promoting, a policy, especially a\n   national policy; well-devised; adapted to its end, whether right or\n   wrong; -- said of things; as, a politic treaty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Politic', 'a.', 'Sagacious in promoting a policy; ingenious in devising and\n   advancing a system of management; devoted to a scheme or system rather\n   than to a principle; hence, in a good sense, wise; prudent; sagacious;\n   and in a bad sense, artful; unscrupulous; cunning; -- said of persons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Politic', 'n.', 'A politician.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Political', 'a.', 'Having, or conforming to, a settled system of\n   administration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Political', 'a.', 'Of or pertaining to public policy, or to politics;\n   relating to affairs of state or administration; as, a political writer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Political', 'a.', 'Of or pertaining to a party, or to parties, in the\n   state; as, his political relations were with the Whigs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Political', 'a.', 'Politic; wise; also, artful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Politicalism', 'n.', 'Zeal or party spirit in politics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Politically', 'adv.', 'In a political manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Politically', 'adv.', 'Politicly; artfully.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Politicaster', 'n.', 'A petty politician; a pretender in politics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Politician', 'n.', 'One versed or experienced in the science of government;\n   one devoted to politics; a statesman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Politician', 'n.', 'One primarily devoted to his own advancement in public\n   office, or to the success of a political party; -- used in a\n   depreciatory sense; one addicted or attached to politics as managed by\n   parties (see Politics, 2); a schemer; an intriguer; as, a mere\n   politician.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Politician', 'a.', 'Cunning; using artifice; politic; artful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Politicist', 'n.', 'A political writer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Politicly', 'adv.', 'In a politic manner; sagaciously; shrewdly; artfully.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Politics', 'n.', 'The science of government; that part of ethics which has\n   to do with the regulation and government of a nation or state, the\n   preservation of its safety, peace, and prosperity, the defense of its\n   existence and rights against foreign control or conquest, the\n   augmentation of its strength and resources, and the protection of its\n   citizens in their rights, with the preservation and improvement of\n   their morals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Politics', 'n.', 'The management of a political party; the conduct and\n   contests of parties with reference to political measures or the\n   administration of public affairs; the advancement of candidates to\n   office; in a bad sense, artful or dishonest management to secure the\n   success of political candidates or parties; political trickery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Politize', 'v. i.', 'To play the politician; to dispute as politicians do.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Politure', 'v.', 'Polish; gloss. [Obs.] Donne.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polities', 'pl. ', 'of Polity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polity', 'n.', 'The form or constitution of the civil government of a\n   nation or state; the framework or organization by which the various\n   departments of government are combined into a systematic whole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polity', 'n.', 'Hence: The form or constitution by which any institution is\n   organized; the recognized principles which lie at the foundation of any\n   human institution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polity', 'n.', 'Policy; art; management.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Politzerization', 'n.', 'The act of inflating the middle ear by blowing air\n   up the nose during the act of swallowing; -- so called from Prof.\n   Politzer of Vienna, who first practiced it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polive', 'n.', 'A pulley.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polka', 'n.', 'A dance of Polish origin, but now common everywhere. It is\n   performed by two persons in common time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polka', 'n.', 'A lively Bohemian or Polish dance tune in 2-4 measure, with\n   the third quaver accented.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poll', 'n.', 'A parrot; -- familiarly so called.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poll', 'n.', 'One who does not try for honors, but is content to take a\n   degree merely; a passman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poll', 'n.', 'The head; the back part of the head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poll', 'n.', 'A number or aggregate of heads; a list or register of heads\n   or individuals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poll', 'n.', 'Specifically, the register of the names of electors who may\n   vote in an election.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poll', 'n.', 'The casting or recording of the votes of registered electors;\n   as, the close of the poll.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poll', 'n.', 'The place where the votes are cast or recorded; as, to go to\n   the polls.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poll', 'n.', 'The broad end of a hammer; the but of an ax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poll', 'n.', 'The European chub. See Pollard, 3 (a).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polled', 'imp. & p. p.', 'of Poll');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polling', 'p. pr. & vb. n.', 'of Poll');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poll', 'v. t.', 'To remove the poll or head of; hence, to remove the top or\n   end of; to clip; to lop; to shear; as, to poll the head; to poll a\n   tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poll', 'v. t.', 'To cut off; to remove by clipping, shearing, etc.; to mow\n   or crop; -- sometimes with off; as, to poll the hair; to poll wool; to\n   poll grass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poll', 'v. t.', 'To extort from; to plunder; to strip.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poll', 'v. t.', 'To impose a tax upon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poll', 'v. t.', 'To pay as ones personal tax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poll', 'v. t.', 'To enter, as polls or persons, in a list or register; to\n   enroll, esp. for purposes of taxation; to enumerate one by one.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poll', 'v. t.', 'To register or deposit, as a vote; to elicit or call\n   forth, as votes or voters; as, he polled a hundred votes more than his\n   opponent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poll', 'v. t.', 'To cut or shave smooth or even; to cut in a straight line\n   without indentation; as, a polled deed. See Dee/ poll.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poll', 'v. i.', 'To vote at an election.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pollack', 'n.', 'A marine gadoid food fish of Europe (Pollachius virens).\n   Called also greenfish, greenling, lait, leet, lob, lythe, and whiting\n   pollack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pollack', 'n.', 'The American pollock; the coalfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pollage', 'n.', 'A head or poll tax; hence, extortion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pollan', 'n.', 'A lake whitefish (Coregonus pollan), native of Ireland. In\n   appearance it resembles a herring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pollard', 'n.', 'A tree having its top cut off at some height above the\n   ground, that may throw out branches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pollard', 'n.', 'A clipped coin; also, a counterfeit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pollard', 'n.', 'A fish, the chub.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pollard', 'n.', 'A stag that has cast its antlers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pollard', 'n.', 'A hornless animal (cow or sheep).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pollarded', 'imp. & p. p.', 'of Pollard');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pollarding', 'p. pr. & vb. n.', 'of Pollard');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pollard', 'v. t.', 'To lop the tops of, as trees; to poll; as, to pollard\n   willows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pollax', 'n.', 'A poleax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polled', 'a.', 'Deprived of a poll, or of something belonging to the poll.\n   Specifically: (a) Lopped; -- said of trees having their tops cut off.\n   (b) Cropped; hence, bald; -- said of a person. \"The polled bachelor.\"\n   Beau. & Fl. (c) Having cast the antlers; -- said of a stag. (d) Without\n   horns; as, polled cattle; polled sheep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pollen', 'n.', 'Fine bran or flour.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pollen', 'n.', 'The fecundating dustlike cells of the anthers of flowers.\n   See Flower, and Illust. of Filament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pollenarious', 'a.', 'Consisting of meal or pollen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pollened', 'a.', 'Covered with pollen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polleniferous', 'a.', 'Producing pollen; polliniferous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pollenin', 'n.', 'A substance found in the pollen of certain plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pollenized', 'imp. & p. p.', 'of Pollenize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pollenizing', 'p. pr. & vb. n.', 'of Pollenize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pollenize', 'v. t.', 'To supply with pollen; to impregnate with pollen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poller', 'n.', 'One who polls; specifically: (a) One who polls or lops\n   trees. (b) One who polls or cuts hair; a barber. [R.] (c) One who\n   extorts or plunders. [Obs.] Baex. (d) One who registplws votplws, or\n   one who enters his name as a voter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pollices', 'pl. ', 'of Pollex');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pollex', 'n.', 'The first, or preaxial, digit of the fore limb,\n   corresponding to the hallux in the hind limb; the thumb. In birds, the\n   pollex is the joint which bears the bastard wing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pollicate', 'a.', 'Having a curved projection or spine on the inner side of\n   a leg joint; -- said of insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pollicitation', 'n.', 'A voluntary engagement, or a paper containing it; a\n   promise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pollicitation', 'n.', 'A promise without mutuality; a promise which has not\n   been accepted by the person to whom it is made.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pollinate', 'a.', 'Pollinose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pollinate', 'v. t.', 'To apply pollen to (a stigma).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pollinctor', 'n.', 'One who prepared corpses for the funeral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polling', 'n.', 'The act of topping, lopping, or cropping, as trees or\n   hedges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polling', 'n.', 'Plunder, or extortion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polling', 'n.', 'The act of voting, or of registering a vote.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polliniferous', 'a.', 'Producing pollen; polleniferous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pollinia', 'pl. ', 'of Pollinium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pollinium', 'n.', 'A coherent mass of pollen, as in the milkweed and most\n   orchids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pollinose', 'a.', 'Having the surface covered with a fine yellow dust, like\n   pollen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polliwig', 'n.', 'Alt. of Polliwog');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polliwog', 'n.', 'A tadpole; -- called also purwiggy and porwigle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pollock', 'n.', 'A marine gadoid fish (Pollachius carbonarius), native both\n   of the European and American coasts. It is allied to the cod, and like\n   it is salted and dried. In England it is called coalfish, lob, podley,\n   podling, pollack, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pollucite', 'n.', 'A colorless transparent mineral, resembling quartz,\n   occurring with castor or castorite on the island of Elba. It is a\n   silicate of alumina and caesia. Called also pollux.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polluted', 'imp. & p. p.', 'of Pollute');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polluting', 'p. pr. & vb. n.', 'of Pollute');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pollute', 'v. t.', 'To make foul, impure, or unclean; to defile; to taint;\n   to soil; to desecrate; -- used of physical or moral defilement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pollute', 'v. t.', 'To violate sexually; to debauch; to dishonor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pollute', 'v. t.', 'To render ceremonially unclean; to disqualify or unfit\n   for sacred use or service, or for social intercourse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pollute', 'a.', 'Polluted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polluted', 'a.', 'Defiled; made unclean or impure; debauched.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polluter', 'n.', 'One who pollutes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polluting', 'a.', 'Adapted or tending to pollute; causing defilement or\n   pollution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pollution', 'n.', 'The act of polluting, or the state of being polluted (in\n   any sense of the verb); defilement; uncleanness; impurity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pollution', 'n.', 'The emission of semen, or sperm, at other times than in\n   sexual intercourse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pollux', 'n.', 'A fixed star of the second magnitude, in the constellation\n   Gemini. Cf. 3d Castor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pollux', 'n.', 'Same as Pollucite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polly', 'n.', 'A womans name; also, a popular name for a parrot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pollywog', 'n.', 'A polliwig.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polo', 'n.', 'A game of ball of Eastern origin, resembling hockey, with the\n   players on horseback.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polo', 'n.', 'A similar game played on the ice, or on a prepared floor, by\n   players wearing skates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polonaise', 'a.', 'Of or pertaining to the Poles, or to Poland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polonaise', 'n.', 'The Polish language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polonaise', 'n.', 'An article of dress for women, consisting of a body and\n   an outer skirt in one piece.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polonaise', 'n.', 'A stately Polish dance tune, in 3-4 measure, beginning\n   always on the beat with a quaver followed by a crotchet, and closing on\n   the beat after a strong accent on the second beat; also, a dance\n   adapted to such music; a polacca.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polonese', 'a. & n.', 'See Polonaise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polony', 'n.', 'A kind of sausage made of meat partly cooked.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polron', 'n.', 'See Pauldron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polt', 'n.', 'A blow or thump.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polt', 'a.', 'Distorted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polt-foot', 'a.', 'Alt. of Polt-footed');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polt-footed', 'a.', 'Having a distorted foot, or a clubfoot or clubfeet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poltroon', 'n.', 'An arrant coward; a dastard; a craven; a mean-spirited\n   wretch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poltroon', 'a.', 'Base; vile; contemptible; cowardly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poltroonery', 'n.', 'Cowardice; want of spirit; pusillanimity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poltroonish', 'a.', 'Resembling a poltroon; cowardly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polverine', 'n.', 'Glassmakers ashes; a kind of potash or pearlash,\n   brought from the Levant and Syria, -- used in the manufacture of fine\n   glass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polwig', 'n.', 'A polliwig. Holland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poly-', 'a.', 'A combining form or prefix from Gr. poly`s, many; as,\n   polygon, a figure of many angles; polyatomic, having many atoms;\n   polychord, polyconic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poly', 'n.', 'A whitish woolly plant (Teucrium Polium) of the order\n   Labiatae, found throughout the Mediterranean region. The name, with\n   sundry prefixes, is sometimes given to other related species of the\n   same genus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyacid', 'a.', 'Capable of neutralizing, or of combining with, several\n   molecules of a monobasic acid; having more than one hydrogen atom\n   capable of being replaced by acid radicals; -- said of certain bases;\n   as, calcium hydrate and glycerin are polyacid bases.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyacoustic', 'a.', 'Multiplying or magnifying sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyacoustic', 'n.', 'A polyacoustic instrument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyacoustics', 'n.', 'The art of multiplying or magnifying sounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyacra', 'pl. ', 'of Polyacron');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyacrons', 'pl. ', 'of Polyacron');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyacron', 'n.', 'A solid having many summits or angular points; a\n   polyhedron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyactinia', 'n. pl.', 'An old name for those Anthozoa which, like the\n   actinias, have numerous simple tentacles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyadelphia', 'n. pl.', 'A Linnaean class of plants having stamens united\n   in three or more bodies or bundles by the filaments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyadelphian', 'a.', 'Alt. of Polyadelphous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyadelphous', 'a.', 'Belonging to the class Polyadelphia; having stamens\n   united in three or more bundles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyandria', 'n. pl.', 'A Linnaean class of monoclinous or hermaphrodite\n   plants, having many stamens, or any number above twenty, inserted in\n   the receptacle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyandrian', 'a.', 'Polyandrous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyandric', 'a.', 'Pertaining to, or characterized by, polyandry; mating\n   with several males.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyandrous', 'a.', 'Belonging to the class Polyandria; having many\n   stamens, or any number above twenty, inserted in the receptacle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyandry', 'n.', 'The possession by a woman of more than one husband at\n   the same time; -- contrasted with monandry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyanthuses', 'pl. ', 'of Polyanthus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyanthus', 'n.', 'The oxlip. So called because the peduncle bears a\n   many-flowered umbel. See Oxlip. (b) A bulbous flowering plant of the\n   genus Narcissus (N. Tazetta, or N. polyanthus of some authors). See\n   Illust. of Narcissus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyarchist', 'n.', 'One who advocates polyarchy; -- opposed to monarchist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyarchy', 'n.', 'A government by many persons, of whatever order or\n   class.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyatomic', 'a.', 'Having more than one atom in the molecule; consisting\n   of several atoms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyatomic', 'a.', 'Having a valence greater than one.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyautography', 'n.', 'The act or practice of multiplying copies of ones\n   own handwriting, or of manuscripts, by printing from stone, -- a\n   species of lithography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polybasic', 'a.', 'Capable of neutralizing, or of combining with, several\n   molecules of a monacid base; having several hydrogen atoms capable of\n   being replaced by basic radicals; -- said of certain acids; as,\n   sulphuric acid is polybasic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polybasite', 'n.', 'An iron-black ore of silver, consisting of silver,\n   sulphur, and antimony, with some copper and arsenic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polybranchia', 'n. pl.', 'A division of Nudibranchiata including those\n   which have numerous branchiae on the back.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polybromide', 'n.', 'A bromide containing more than one atom of bromine in\n   the molecule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polycarpellary', 'a.', 'Composed of several or numerous carpels; -- said of\n   such fruits as the orange.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polycarpic', 'a.', 'Alt. of Polycarpous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polycarpous', 'a.', 'Bearing fruit repeatedly, or year after year.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polycarpous', 'a.', 'Having several pistils in one flower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polychaeta', 'n. pl.', 'One of the two principal groups of Chaetopoda. It\n   includes those that have prominent parapodia and fascicles of setae.\n   See Illust. under Parapodia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polychloride', 'n.', 'A chloride containing more than one atom of chlorine\n   in the molecule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polychoerany', 'n.', 'A government by many chiefs, princes, or rules.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polychord', 'a.', 'Having many strings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polychord', 'n.', 'A musical instrument of ten strings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polychord', 'n.', 'An apparatus for coupling two octave notes, capable of\n   being attached to a keyed instrument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polychrest', 'n.', 'A medicine that serves for many uses, or that cures\n   many diseases.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polychroism', 'n.', 'Same as Pleochroism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polychroite', 'n.', 'The coloring matter of saffron; -- formerly so called\n   because of the change of color on treatment with certain acids; --\n   called also crocin, and safranin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polychromate', 'n.', 'A salt of a polychromic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polychromate', 'n.', 'A compound which exhibits, or from which may be\n   prepared, a variety of colors, as certain solutions derived from\n   vegetables, which display colors by fluorescence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polychromatic', 'a.', 'Showing a variety, or a change, of colors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polychrome', 'n.', 'Esculin; -- so called in allusion to its fluorescent\n   solutions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polychrome', 'a.', 'Executed in the manner of polychromy; as, polychrome\n   printing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polychromic', 'a.', 'Polychromatic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polychromic', 'a.', 'Pertaining to, or designating, any one of several\n   acids (known only in their salts) which contain more than one atom of\n   chromium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polychromous', 'a.', 'Of or pertaining to polychromy; many-colored;\n   polychromatic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polychromy', 'n.', 'The art or practice of combining different colors,\n   especially brilliant ones, in an artistic way.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polychronious', 'a.', 'Enduring through a long time; chronic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyclinic', 'n.', 'A clinic in which diseases of many sorts are treated;\n   especially, an institution in which clinical instruction is given in\n   all kinds of disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyconic', 'a.', 'Pertaining to, or based upon, many cones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polycotyledon', 'n.', 'A plant that has many, or more than two, cotyledons\n   in the seed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polycotyledonary', 'a.', 'Having the villi of the placenta collected into\n   definite patches, or cotyledons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polycracy', 'n.', 'Government by many rulers; polyarchy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polycrotic', 'a.', 'Of or pertaining to polycrotism; manifesting\n   polycrotism; as, a polycrotic pulse; a polycrotic pulse curve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polycrotism', 'n.', 'That state or condition of the pulse in which the\n   pulse curve, or sphygmogram, shows several secondary crests or\n   elevations; -- contrasted with monocrotism and dicrotism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polycystid', 'n.', 'One of the Polycystidea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polycystid', 'n.', 'One of the Polycystina.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polycystid', 'a.', 'Pertaining to the Polycystidea, or the Polycystina.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polycystidea', 'n. pl.', 'A division of Gregarinae including those that\n   have two or more internal divisions of the body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polycystina', 'n. pl.', 'A division of Radiolaria including numerous minute\n   marine species. The skeleton is composed of silica, and is often very\n   elegant in form and sculpture. Many have been found in the fossil\n   state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polycystine', 'a.', 'Pertaining to the Polycystina.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polycystine', 'n.', 'One of the Polycystina.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polycyttaria', 'n. pl.', 'A division of Radiolaria. It includes those\n   having one more central capsules.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polydactylism', 'n.', 'The possession of more that the normal number of\n   digits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polydipsia', 'n.', 'Excessive and constant thirst occasioned by disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyedron', 'n.', 'See Polyhedron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyedrous', 'a.', 'See Polyhedral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyeidic', 'a.', 'Passing through several distinct larval forms; -- having\n   several distinct kinds of young.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyeidism', 'n.', 'The quality or state of being polyeidic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyembryonate', 'a.', 'Consisting of, or having, several embryos;\n   polyembryonic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyembryonic', 'a.', 'Polyembryonate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyembryony', 'n.', 'The production of two or more embryos in one seed,\n   due either to the existence and fertilization of more than one\n   embryonic sac or to the origination of embryos outside of the embryonic\n   sac.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyfoil', 'n.', 'Same as Multifoil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polygala', 'n.', 'A genus of bitter herbs or shrubs having eight stamens\n   and a two-celled ovary (as the Seneca snakeroot, the flowering\n   wintergreen, etc.); milkwort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polygalaceous', 'a.', 'Of or pertaining to a natural order of plants\n   (Polygalaceae) of which Polygala is the type.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polygalic', 'a.', 'Of, pertaining to, or obtained from, Polygala;\n   specifically, designating an acrid glucoside (called polygalic acid,\n   senegin, etc.), resembling, or possibly identical with, saponin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polygamia', 'n. pl.', 'A Linnaean class of plants, characterized by having\n   both hermaphrodite and unisexual flowers on the same plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polygamia', 'n. pl.', 'A name given by Linnaeus to file orders of plants\n   having syngenesious flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polygamian', 'a.', 'Polygamous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polygamist', 'a.', 'One who practices polygamy, or maintains its\n   lawfulness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polygamize', 'v. i.', 'To practice polygamy; to marry several wives.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polygamous', 'a.', 'Of or pertaining to polygamy; characterized by, or\n   involving, polygamy; having a plurality of wives; as, polygamous\n   marriages; -- opposed to monogamous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polygamous', 'a.', 'Pairing with more than one female.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polygamous', 'a.', 'Belonging to the Polygamia; bearing both hermaphrodite\n   and unisexual flowers on the same plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polygamy', 'n.', 'The having of a plurality of wives or husbands at the\n   same time; usually, the marriage of a man to more than one woman, or\n   the practice of having several wives, at the same time; -- opposed to\n   monogamy; as, the nations of the East practiced polygamy. See the Note\n   under Bigamy, and cf. Polyandry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polygamy', 'n.', 'The state or habit of having more than one mate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polygamy', 'n.', 'The condition or state of a plant which bears both\n   perfect and unisexual flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polygastrian', 'n.', 'One of the Polygastrica.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polygastric', 'a.', 'Having several bellies; -- applied to muscles which\n   are made up of several bellies separated by short tendons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polygastric', 'a.', 'Pertaining to the Polygastrica.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polygastric', 'n.', 'One of the Polygastrica.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polygastrica', 'n. pl.', 'The Infusoria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polygenesis', 'n.', 'Alt. of Polygeny');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polygeny', 'n.', 'The theory that living organisms originate in cells or\n   embryos of different kinds, instead of coming from a single cell; --\n   opposed to monogenesis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polygenetic', 'a.', 'Having many distinct sources; originating at various\n   places or times.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polygenetic', 'a.', 'Of or pertaining to polygenesis; polyphyletic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polygenic', 'a.', 'Of or relating to polygeny; polygenetic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polygenism', 'n.', 'The doctrine that animals of the same species have\n   sprung from more than one original pair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polygenist', 'n.', 'One who maintains that animals of the same species have\n   sprung from more than one original pair; -- opposed to monogenist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polygenous', 'a.', 'Consisting of, or containing, many kinds; as, a\n   polygenous mountain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyglot', 'a.', 'Containing, or made up, of, several languages; as, a\n   polyglot lexicon, Bible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyglot', 'a.', 'Versed in, or speaking, many languages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyglot', 'n.', 'One who speaks several languages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyglot', 'n.', 'A book containing several versions of the same text, or\n   containing the same subject matter in several languages; esp., the\n   Scriptures in several languages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyglottous', 'a.', 'Speaking many languages; polyglot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polygon', 'n.', 'A plane figure having many angles, and consequently many\n   sides; esp., one whose perimeter consists of more than four sides; any\n   figure having many angles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polygonaceous', 'a.', 'Of or pertaining to a natural order of apetalous\n   plants (Polygonaceae), of which the knotweeds (species of Polygonum)\n   are the type, and which includes also the docks (Rumex), the buckwheat,\n   rhubarb, sea grape (Coccoloba), and several other genera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polygonal', 'a.', 'Having many angles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polygoneutic', 'a.', 'Having two or more broods in a season.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polygonometry', 'n.', 'The doctrine of polygons; an extension of some of\n   the principles of trigonometry to the case of polygons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polygonous', 'a.', 'Polygonal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polygonum', 'n.', 'A genus of plants embracing a large number of species,\n   including bistort, knotweed, smartweed, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polygony', 'n.', 'Any plant of the genus Polygonum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polygordius', 'n.', 'A genus of marine annelids, believed to be an ancient\n   or ancestral type. It is remarkable for its simplicity of structure and\n   want of parapodia. It is the type of the order Archiannelida, or\n   Gymnotoma. See Loevens larva.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polygram', 'n.', 'A figure consisting of many lines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polygraph', 'n.', 'An instrument for multiplying copies of a writing; a\n   manifold writer; a copying machine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polygraph', 'n.', 'In bibliography, a collection of different works, either\n   by one or several authors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polygraph', 'n.', 'An instrument for detecting deceptive statements by a\n   subject, by measuring several physiological states of the subject, such\n   as pulse, heartbeat, and sweating. The instrument records these\n   parameters on a strip of paper while the subject is asked questions\n   designed to elicit emotional responses when the subject tries to\n   deceive the interrogator. Also called lie detector');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polygraphic', 'a.', 'Alt. of Polygraphical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polygraphical', 'a.', 'Pertaining to, or employed in, polygraphy; as, a\n   polygraphic instrument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polygraphical', 'a.', 'Done with a polygraph; as, a polygraphic copy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polygraphy', 'n.', 'Much writing; writing of many books.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polygraphy', 'n.', 'The art of writing in various ciphers, and of\n   deciphering the same.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polygraphy', 'n.', 'The art or practice of using a polygraph.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polygrooved', 'a.', 'Having many grooves; as, a polygrooved rifle or gun\n   (referring to the rifling).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polygyn', 'n.', 'A plant of the order Polygynia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polygynia', 'n. pl.', 'A Linnaean order of plants having many styles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polygynian', 'a.', 'Alt. of Polygynous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polygynous', 'a.', 'Having many styles; belonging to the order Polygynia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polygynist', 'n.', 'One who practices or advocates polygyny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polygyny', 'n.', 'The state or practice of having several wives at the same\n   time; marriage to several wives.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyhalite', 'n.', 'A mineral usually occurring in fibrous masses, of a\n   brick-red color, being tinged with iron, and consisting chiefly of the\n   sulphates of lime, magnesia, and soda.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyhedral', 'a.', 'Alt. of Polyhedrical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyhedrical', 'a.', 'Having many sides, as a solid body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyhedrons', 'pl. ', 'of Polyhedron');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyhedra', 'pl. ', 'of Polyhedron');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyhedron', 'n.', 'A body or solid contained by many sides or planes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyhedron', 'n.', 'A polyscope, or multiplying glass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyhedrous', 'a.', 'Polyhedral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyhistor', 'n.', 'One versed in various learning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyhymnia', 'n.', 'The Muse of lyric poetry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyiodide', 'n.', 'A iodide having more than one atom of iodine in the\n   molecule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polylogy', 'n.', 'Talkativeness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyloquent', 'a.', 'Garrulous; loquacious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polymastism', 'n.', 'The condition of having more than two mammae, or\n   breasts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polymathic', 'a.', 'Pertaining to polymathy; acquainted with many branches\n   of learning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polymathist', 'n.', 'One versed in many sciences; a person of various\n   learning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polymathy', 'n.', 'The knowledge of many arts and sciences; variety of\n   learning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polymeniscous', 'a.', 'Having numerous facets; -- said of the compound eyes\n   of insects and crustaceans.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polymer', 'n.', 'Any one of two or more substances related to each other by\n   polymerism; specifically, a substance produced from another substance\n   by chemical polymerization.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polymeric', 'a.', 'Having the same percentage composition (that is, having\n   the same elements united in the same proportion by weight), but\n   different molecular weights; -- often used with with; thus, cyanic acid\n   (CNOH), fulminic acid (C2N2O2H2), and cyanuric acid (C3N3O3H3), are\n   polymeric with each other.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polymerism', 'n.', 'The state, quality, or relation of two or more\n   polymeric substances.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polymerism', 'n.', 'The act or process of forming polymers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polymerization', 'n.', 'The act or process of changing to a polymeric form;\n   the condition resulting from such change.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polymerize', 'v. t.', 'To cause polymerization of; to produce polymers\n   from; to increase the molecular weight of, without changing the atomic\n   proportions; thus, certain acids polymerize aldehyde.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polymerize', 'v. i.', 'To change into another substance having the same\n   atomic proportions, but a higher molecular weight; to undergo\n   polymerization; thus, aldehyde polymerizes in forming paraldehyde.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polymerous', 'a.', 'Having many parts or members in each set.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polymerous', 'a.', 'Polymeric.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polymnia', 'n.', 'See Polyhymnia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polymnite', 'n.', 'A stone marked with dendrites and black lines, and so\n   disposed as to represent rivers, marshes, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polymorph', 'n.', 'A substance capable of crystallizing in several distinct\n   forms; also, any one of these forms. Cf. Allomorph.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polymorphic', 'a.', 'Polymorphous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polymorphism', 'n.', 'Same as Pleomorphism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polymorphism', 'n.', 'The capability of assuming different forms; the\n   capability of widely varying in form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polymorphism', 'n.', 'Existence in many forms; the coexistence, in the same\n   locality, of two or more distinct forms independent of sex, not\n   connected by intermediate gradations, but produced from common parents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polymorphosis', 'n.', 'The assumption of several structural forms without a\n   corresponding difference in function; -- said of sponges, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polymorphous', 'a.', 'Having, or assuming, a variety of forms, characters,\n   or styles; as, a polymorphous author.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polymorphous', 'a.', 'Having, or occurring in, several distinct forms; --\n   opposed to monomorphic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polymorphy', 'n.', 'Existence in many forms; polymorphism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poly-mountain', 'n.', 'Same as Poly, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poly-mountain', 'n.', 'The closely related Teucrium montanum, formerly\n   called Polium montanum, a plant of Southern Europe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poly-mountain', 'n.', 'The Bartsia alpina, a low purple-flowered herb of\n   Europe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polymyodae', 'n. pl.', 'Same as Oscines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polymyodous', 'a.', 'Polymyoid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polymyoid', 'a.', 'Having numerous vocal muscles; of or pertaining to the\n   Polymyodae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyneme', 'n.', 'Any one of numerous species of tropical food fishes of\n   the family Polynemidae. They have several slender filaments, often very\n   long, below the pectoral fin. Some of them yield isinglass of good\n   quality. Called also threadfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polynemoid', 'a.', 'Of or pertaining to the polynemes, or the family\n   Polynemidae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polynesian', 'a.', 'Of or pertaining to Polynesia (the islands of the\n   eastern and central Pacific), or to the Polynesians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polynesians', 'n. pl.', 'The race of men native in Polynesia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polynia', 'n.', 'The open sea supposed to surround the north pole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polynomial', 'n.', 'An expression composed of two or more terms, connected\n   by the signs plus or minus; as, a2 - 2ab + b2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polynomial', 'a.', 'Containing many names or terms; multinominal; as, the\n   polynomial theorem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polynomial', 'a.', 'Consisting of two or more words; having names\n   consisting of two or more words; as, a polynomial name; polynomial\n   nomenclature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polynuclear', 'a.', 'Containing many nuclei.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polynucleolar', 'a.', 'Having more than one nucleolus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyommatous', 'a.', 'Having many eyes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyonomous', 'a.', 'Having many names or titles; polyonymous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyonomy', 'n.', 'The use of a variety of names for the same object.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyonym', 'n.', 'An object which has a variety of names.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyonym', 'n.', 'A polynomial name or term.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyonymous', 'a.', 'Polyonomous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyoptron', 'n.', 'Alt. of Polyoptrum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyoptrum', 'n.', 'A glass through which objects appear multiplied, but\n   diminished in size.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyorama', 'n.', 'A view of many objects; also, a sort of panorama with\n   dissolving views.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyp', 'n.', 'One of the feeding or nutritive zooids of a hydroid or\n   coral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyp', 'n.', 'One of the Anthozoa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyp', 'n.', 'Same as Anthozoa. See Anthozoa, Madreporaria, Hydroid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyparous', 'a.', 'Producing or bearing a great number; bringing forth\n   many.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyparies', 'pl. ', 'of Polypary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polypary', 'n.', 'Same as Polypidom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polype', 'n.', 'See Polyp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polypean', 'a.', 'Of or pertaining to a polyp, or polyps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyperythrin', 'n.', 'A coloring matter found in many simple Anthozoa and\n   some hydroids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polypetalous', 'a.', 'Consisting of, or having, several or many separate\n   petals; as, a polypetalous corolla, flower, or plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyphagous', 'a.', 'Eating, or subsisting on, many kinds of food; as,\n   polyphagous animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyphagy', 'n.', 'The practice or faculty of subsisting on many kinds of\n   food.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polypharmacy', 'n.', 'The act or practice of prescribing too many\n   medicines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polypharmacy', 'n.', 'A prescription made up of many medicines or\n   ingredients.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyphemus', 'n.', 'A very large American moth (Telea polyphemus) belonging\n   to the Silkworm family (Bombycidae). Its larva, which is very large,\n   bright green, with silvery tubercles, and with oblique white stripes on\n   the sides, feeds on the oak, chestnut, willow, cherry, apple, and other\n   trees. It produces a large amount of strong silk. Called also American\n   silkworm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyphone', 'n.', 'A character or vocal sign representing more than one\n   sound, as read, which is pronounced red.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyphonic', 'a.', 'Having a multiplicity of sounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyphonic', 'a.', 'Characterized by polyphony; as, Assyrian polyphonic\n   characters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyphonic', 'a.', 'Consisting of several tone series, or melodic parts,\n   progressing simultaneously according to the laws of counterpoint;\n   contrapuntal; as, a polyphonic composition; -- opposed to homophonic,\n   or monodic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyphonism', 'n.', 'Polyphony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyphonist', 'n.', 'A proficient in the art of multiplying sounds; a\n   ventriloquist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyphonist', 'n.', 'A master of polyphony; a contrapuntist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyphonous', 'a.', 'Same as Polyphonic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyphony', 'n.', 'Multiplicity of sounds, as in the reverberations of an\n   echo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyphony', 'n.', 'Plurality of sounds and articulations expressed by the\n   same vocal sign.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyphony', 'n.', 'Composition in mutually related, equally important parts\n   which share the melody among them; contrapuntal composition; -- opposed\n   to homophony, in which the melody is given to one part only, the others\n   filling out the harmony. See Counterpoint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyphore', 'n.', 'A receptacle which bears many ovaries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyphyletic', 'a.', 'Pertaining to, or characterized by, descent from more\n   than one root form, or from many different root forms; polygenetic; --\n   opposed to monophyletic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyphyllous', 'a.', 'Many-leaved; as, a polyphyllous calyx or perianth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polypi', 'n. pl.', 'The Anthozoa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polypide', 'n.', 'One of the ordinary zooids of the Bryozoa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polypidom', 'n.', 'A coral, or corallum; also, one of the coral-like\n   structure made by bryozoans and hydroids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polypier', 'n.', 'A polypidom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polypifera', 'n. pl.', 'The Anthozoa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polypiferous', 'a.', 'Bearing polyps, or polypites.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polypiparous', 'a.', 'Producing polyps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polypite', 'n.', 'One of the feeding zooids, or polyps, of a coral,\n   hydroid, or siphonophore; a hydranth. See Illust. of Campanularian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polypite', 'n.', 'Sometimes, the manubrium of a hydroid medusa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polypite', 'n.', 'A fossil coral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyplacophora', 'n. pl.', 'See Placophora.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyplastic', 'a.', 'Assuming, or having the power of assuming, many forms;\n   as, a polyplastic element which does not preserve its original shape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polypode', 'n.', 'A plant of the genus Polypodium; polypody.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polypode', 'n.', 'An animal having many feet; a myriapod.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polypodium', 'n.', 'A genus of plants of the order Filices or ferns. The\n   fructifications are in uncovered roundish points, called sori,\n   scattered over the inferior surface of the frond or leaf. There are\n   numerous species.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polypody', 'n.', 'Any plant of the genus Polypodium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polypoid', 'a.', 'Like a polyp; having the nature of a polyp, but lacking\n   the tentacles or other parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polypoid', 'a.', 'Resembling a polypus in appearance; having a character\n   like that of a polypus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polypomedusae', 'n. pl.', 'Same as Hydrozoa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyporous', 'a.', 'Having many pores.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polypori', 'pl. ', 'of Polyporus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyporus', 'n.', 'A genus of fungi having the under surface full of minute\n   pores; also, any fungus of this genus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polypous', 'a.', 'Of the nature of a polypus; having many feet or roots,\n   like the polypus; affected with polypus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polypragmatic', 'a.', 'Alt. of Polypragmatical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polypragmatical', 'a.', 'Overbusy; officious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polypragmaty', 'n.', 'The state of being overbusy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyprotodonta', 'n. pl.', 'A division of marsupials in which there are\n   more fore incisor teeth in each jaw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polypteroidei', 'n. pl.', 'A suborder of existing ganoid fishes having\n   numerous fins along the back. The bichir, or Polypterus, is the type.\n   See Illust. under Crossopterygian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polypterus', 'n.', 'An African genus of ganoid fishes including the bichir.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyptoton', 'n.', 'A figure by which a word is repeated in different\n   forms, cases, numbers, genders, etc., as in Tennysons line, -- \"My own\n   hearts heart, and ownest own, farewell.\"');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polypuses', 'pl. ', 'of Polypus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polypi', 'pl. ', 'of Polypus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polypus', 'n.', 'Same as Polyp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polypus', 'n.', 'A tumor, usually with a narrow base, somewhat resembling a\n   pear, -- found in the nose, uterus, etc., and produced by hypertrophy\n   of some portion of the mucous membrane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyrhizous', 'a.', 'Having numerous roots, or rootlets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyschematist', 'a.', 'Having, or existing in, many different forms or\n   fashions; multiform.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyscope', 'n.', 'A glass which makes a single object appear as many; a\n   multiplying glass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyscope', 'n.', 'An apparatus for affording a view of the different\n   cavities of the body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polysepalous', 'a.', 'Having the sepals separate from each other.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polysilicic', 'a.', 'Of or pertaining to compounds formed by the\n   condensation of two or more molecules of silicic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyspast', 'n.', 'A machine consisting of many pulleys; specifically, an\n   apparatus formerly used for reducing luxations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyspermous', 'a.', 'Containing many seeds; as, a polyspermous capsule or\n   berry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyspermy', 'n.', 'Fullness of sperm, or seed; the passage of more than\n   one spermatozoon into the vitellus in the impregnation of the ovum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polysporous', 'a.', 'Containing many spores.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polystomata', 'n. pl.', 'A division of trematode worms having more two\n   suckers. Called also Polystomea and Polystoma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polystome', 'a.', 'Having many mouths.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polystome', 'n.', 'An animal having many mouths; -- applied to Protozoa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polystyle', 'a.', 'Having many columns; -- said of a building, especially\n   of an interior part or court; as, a polystyle hall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polystyle', 'n.', 'A polystyle hall or edifice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polysulphide', 'n.', 'A sulphide having more than one atom of sulphur in\n   the molecule; -- contrasted with monosulphide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polysulphuret', 'n.', 'A polysulphide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polysyllabic', 'a.', 'Alt. of Polysyllabical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polysyllabical', 'a.', 'Pertaining to a polysyllable; containing, or\n   characterized by, polysyllables; consisting of more than three\n   syllables.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polysyllabicism', 'n.', 'Polysyllabism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polysyllabicity', 'n.', 'Polysyllabism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polysyllabism', 'n.', 'The quality or state of being polysyllabic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polysyllable', 'n.', 'A word of many syllables, or consisting of more\n   syllables than three; -- words of less than four syllables being called\n   monosyllables, dissyllables, and trisyllables.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polysyndetic', 'a.', 'Characterized by polysyndeton, or the multiplication\n   of conjunctions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polysyndeton', 'n.', 'A figure by which the conjunction is often repeated,\n   as in the sentence, \"We have ships and men and money and stores.\"\n   Opposed to asyndeton.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polysynthesis', 'n.', 'The act or process of combining many separate\n   elements into a whole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polysynthesis', 'n.', 'The formation of a word by the combination of\n   several simple words, as in the aboriginal languages of America;\n   agglutination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polysynthetic', 'a.', 'Characterized by polysynthesis; agglutinative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polsyntheticism', 'n.', 'Polysynthesis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polytechnic', 'a.', 'Comprehending, or relating to, many arts and sciences;\n   -- applied particularly to schools in which many branches of art and\n   science are taught with especial reference to their practical\n   application; also to exhibitions of machinery and industrial products.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polytechnical', 'a.', 'Polytechnic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polytechnics', 'n.', 'The science of the mechanic arts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polythalamia', 'n. pl.', 'A division of Foraminifera including those having\n   a manychambered shell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polythalamous', 'a.', 'Many-chambered; -- applied to shells of Foraminifera\n   and cephalopods. See Illust. of Nautilus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polytheism', 'n.', 'The doctrine of, or belief in, a plurality of gods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polytheist', 'n.', 'One who believes in, or maintains the doctrine of, a\n   plurality of gods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polytheistic', 'a.', 'Alt. of Polytheistical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polytheistical', 'a.', 'Of or pertaining to polytheism; characterized by\n   polytheism; professing or advocating polytheism; as, polytheistic\n   worship; a polytheistic author, or nation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polytheize', 'v. i.', 'To adhere to, advocate, or inculcate, the doctrine\n   of polytheism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polythelism', 'n.', 'The condition of having more than two teats, or\n   nipples.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polytocous', 'a.', 'Bearing fruit repeatedly, as most perennial plants;\n   polycarpic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polytocous', 'a.', 'Producing many or young.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polytomous', 'a.', 'Subdivided into many distinct subordinate parts, which,\n   however, not being jointed to the petiole, are not true leaflets; --\n   said of leaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polytomy', 'n.', 'A division into many members.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polytungstate', 'n.', 'A salt of polytungstic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polytungstic', 'a.', 'Containing several tungsten atoms or radicals; as,\n   polytungstic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polytype', 'n.', 'A cast, or facsimile copy, of an engraved block, matter\n   in type, etc. (see citation); as, a polytype in relief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polytype', 'a.', 'Of or pertaining to polytypes; obtained by polytyping;\n   as, a polytype plate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polytyped', 'imp. & p. p.', 'of Polytype');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polytyping', 'p. pr. & vb. n.', 'of Polytype');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polytype', 'v. t.', 'To produce a polytype of; as, to polytype an\n   engraving.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poluria', 'n.', 'A persistently excessive flow of watery urine, with low\n   specific gravity and without the presence of either albumin or sugar.\n   It is generally accompanied with more or less thirst.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyvalent', 'a.', 'Multivalent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyve', 'n.', 'A pulley.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyzoa', 'n. pl.', 'Same as Bryozoa. See Illust. under Bryozoa, and\n   Phylactolaemata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyzoan', 'n.', 'Any species of Polyzoa; one of the Polyzoa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyzoan', 'n.', 'A polyzoon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyzoaria', 'pl. ', 'of Polyzoarium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyzoarium', 'n.', 'Same as Polyzoary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyzoary', 'n.', 'The compound organism of a polyzoan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyzonal', 'a.', 'Consisting of many zones or rings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyzoa', 'pl. ', 'of Polyzoon');
  INSERT INTO words (word, word_type, description) VALUES  (    'Polyzoon', 'n.', 'One of the individual zooids forming the compound\n   organism of a polyzoan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pomace', 'n.', 'The substance of apples, or of similar fruit, crushed by\n   grinding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pomacentroid', 'a.', 'Pertaining to the Pomacentridae, a family of\n   bright-colored tropical fishes having spiny opercula; -- often called\n   coral fishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pomaceous', 'a.', 'Like an apple or pear; producing pomes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pomaceous', 'a.', 'Of or pertaining to a suborder (Pomeae) of rosaceous\n   plants, which includes the true thorn trees, the quinces, service\n   berries, medlars, and loquats, as well as the apples, pears, crabs,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pomaceous', 'a.', 'Like pomace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pomade', 'n.', 'Cider.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pomade', 'n.', 'Perfumed ointment; esp., a fragrant unguent for the hair;\n   pomatum; -- originally made from apples.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pomander', 'n.', 'A perfume to be carried with one, often in the form of a\n   ball.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pomander', 'n.', 'A box to contain such perfume, formerly carried by\n   ladies, as at the end of a chain; -- more properly pomander box.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pomarine', 'a.', 'Having the nostril covered with a scale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pomatum', 'n.', 'A perfumed unguent or composition, chiefly used in\n   dressing the hair; pomade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pomatum', 'v. t.', 'To dress with pomatum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pome', 'n.', 'A fruit composed of several cartilaginous or bony carpels\n   inclosed in an adherent fleshy mass, which is partly receptacle and\n   partly calyx, as an apple, quince, or pear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pome', 'n.', 'A ball of silver or other metal, which is filled with hot\n   water, and used by the priest in cold weather to warm his hands during\n   the service.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pome', 'n.', 'To grow to a head, or form a head in growing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pomegranate', 'n.', 'The fruit of the tree Punica Granatum; also, the tree\n   itself (see Balaustine), which is native in the Orient, but is\n   successfully cultivated in many warm countries, and as a house plant in\n   colder climates. The fruit is as large as an orange, and has a hard\n   rind containing many rather large seeds, each one separately covered\n   with crimson, acid pulp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pomegranate', 'n.', 'A carved or embroidered ornament resembling a\n   pomegranate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pomel', 'n.', 'A pommel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pomelo', 'n.', 'A variety of shaddock, called also grape fruit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pomely', 'a.', 'Dappled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pomeranian', 'a.', 'Of or pertaining to Pomerania, a province of Prussia on\n   the Baltic Sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pomeranian', 'n.', 'A native or inhabitant of Pomerania.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pomewater', 'n.', 'A kind of sweet, juicy apple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pomeys', 'pl. ', 'of Pomey');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pomey', 'n.', 'A figure supposed to resemble an apple; a roundel, -- always\n   of a green color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pomfret', 'n.', 'One of two or more species of marine food fishes of the\n   genus Stromateus (S. niger, S. argenteus) native of Southern Europe and\n   Asia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pomfret', 'n.', 'A marine food fish of Bermuda (Brama Raji).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pomiferous', 'a.', 'Bearing pomes, or applelike fruits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pomiferous', 'a.', 'Bearing fruits, or excrescences, more or less\n   resembling an apple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pommage', 'n.', 'See Pomage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pomme', 'a.', 'Having the ends terminating in rounded protuberances or\n   single balls; -- said of a cross.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pomme blanche', '', 'The prairie turnip. See under Prairie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pommel', 'n.', 'A knob or ball; an object resembling a ball in form');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pommel', 'n.', 'The knob on the hilt of a sword.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pommel', 'n.', 'The knob or protuberant part of a saddlebow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pommel', 'n.', 'The top (of the head).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pommel', 'n.', 'A knob forming the finial of a turret or pavilion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pommeled', 'imp. & p. p.', 'of Pommel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pommelled', '', 'of Pommel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pommeling', 'p. pr. & vb. n.', 'of Pommel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pommelling', '', 'of Pommel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pommel', 'v. t.', 'To beat soundly, as with the pommel of a sword, or with\n   something knoblike; hence, to beat with the fists.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pommelion', 'n.', 'The cascabel, or hindmost knob, of a cannon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pommette', 'a.', 'Having two balls or protuberances at each end; -- said of\n   a cross.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pomological', 'a.', 'Of or pertaining to pomology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pomologist', 'n.', 'One versed in pomology; one who culticvates fruit\n   trees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pomology', 'n.', 'The science of fruits; a treatise on fruits; the\n   cultivation of fruits and fruit trees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pomona', 'n.', 'The goddess of fruits and fruit trees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pomp', 'n.', 'A procession distinguished by ostentation and splendor; a\n   pageant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pomp', 'n.', 'Show of magnificence; parade; display; power.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pomp', 'v. i.', 'To make a pompons display; to conduct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pompadour', 'n.', 'A crimson or pink color; also, a style of dress cut low\n   and square in the neck; also, a mode of dressing the hair by drawing it\n   straight back from the forehead over a roll; -- so called after the\n   Marchioness de Pompadour of France. Also much used adjectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pompano', 'n.', 'Any one of several species of marine fishes of the genus\n   Trachynotus, of which four species are found on the Atlantic coast of\n   the United States; -- called also palometa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pompano', 'n.', 'A California harvest fish (Stromateus simillimus), highly\n   valued as a food fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pompatic', 'a.', 'Pompous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pompelmouses', 'pl. ', 'of Pompelmous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pompelmous', 'n.', 'A shaddock, esp. one of large size.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pompet', 'n.', 'The ball formerly used to ink the type.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pompholyx', 'n.', 'Impure zinc oxide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pompholyx', 'n.', 'A skin disease in which there is an eruption of bullae,\n   without inflammation or fever.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pompillion', 'n.', 'An ointment or pomatum made of black poplar buds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pompion', 'n.', 'See Pumpion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pompire', 'n.', 'A pearmain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pompoleon', 'n.', 'See Pompelmous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pompon', 'n.', 'Any trifling ornament for a womans dress or bonnet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pompon', 'n.', 'A tuft or ball of wool, or the like, sometimes worn by\n   soldiers on the front of the hat, instead of a feather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pomposities', 'pl. ', 'of Pomposity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pomposity', 'n.', 'The quality or state of being pompous; pompousness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pomposo', 'a. & adv.', 'Grand and dignified; in grand style.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pompous', 'a.', 'Displaying pomp; stately; showy with grandeur;\n   magnificent; as, a pompous procession.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pompous', 'a.', 'Ostentatious; pretentious; boastful; vainlorious; as,\n   pompous manners; a pompous style.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pomptine', 'a.', 'See Pontine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pomwater', 'n.', 'Same as Pomewater.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ponchos', 'pl. ', 'of Poncho');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poncho', 'n.', 'A kind of cloak worn by the Spanish Americans, having the\n   form of a blanket, with a slit in the middle for the head to pass\n   through. A kind of poncho made of rubber or painted cloth is used by\n   the mounted troops in the United States service.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poncho', 'n.', 'A trade name for camlets, or stout worsteds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pond', 'n.', 'A body of water, naturally or artificially confined, and\n   usually of less extent than a lake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pond', 'v. t.', 'To make into a pond; to collect, as water, in a pond by\n   damming.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pond', 'v. t.', 'To ponder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pondered', 'imp. & p. p.', 'of Ponder');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pondering', 'p. pr. & vb. n.', 'of Ponder');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ponder', 'v. t.', 'To weigh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ponder', 'v. t.', 'To weigh in the mind; to view with deliberation; to\n   examine carefully; to consider attentively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ponder', 'v. i.', 'To think; to deliberate; to muse; -- usually followed by\n   on or over.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ponderability', 'n.', 'The quality or state of being ponderable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ponderable', 'a.', 'Capable of being weighed; having appreciable weight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ponderal', 'a.', 'Estimated or ascertained by weight; -- distinguished from\n   numeral; as, a ponderal drachma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ponderance', 'n.', 'Weight; gravity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ponderary', 'a.', 'Of or pertaining to weight; as, a ponderary system.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ponderate', 'v. t.', 'To consider; to ponder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ponderate', 'v. i.', 'To have weight or influence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ponderation', 'n.', 'The act of weighing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ponderer', 'n.', 'One who ponders.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pondering', 'a.', 'Deliberating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ponderosities', 'pl. ', 'of Ponderosity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ponderosity', 'n.', 'The quality or state of being ponderous; weight;\n   gravity; heaviness, ponderousness; as, the ponderosity of gold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ponderous', 'a.', 'Very heavy; weighty; as, a ponderous shield; a ponderous\n   load; the ponderous elephant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ponderous', 'a.', 'Important; momentous; forcible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ponderous', 'a.', 'Heavy; dull; wanting; lightless or spirit; as, a\n   ponderous style; a ponderous joke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ponderously', 'adv.', 'In a ponderous manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ponderousness', 'n.', 'The quality or state of being ponderous;\n   ponderosity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pondfish', 'n.', 'Any one of numerous species of American fresh-water\n   fishes belonging to the family Centrarchidae; -- called also pond\n   perch, and sunfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pondweed', 'n.', 'Any aquatic plant of the genus Potamogeton, of which many\n   species are found in ponds or slow-moving rivers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pone', 'n.', 'A kind of johnnycake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ponent', 'a.', 'Western; occidental.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pongee', 'n.', 'A fabric of undyed silk from India and China.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ponghee', 'n.', 'A Buddhist priest of the higher orders in Burmah.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pongo', 'n.', 'Any large ape; especially, the chimpanzee and the\n   orang-outang.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poniard', 'n.', 'A kind of dagger, -- usually a slender one with a\n   triangular or square blade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poniarded', 'imp. & p. p.', 'of Poniard');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poniarding', 'p. pr. & vb. n.', 'of Poniard');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poniard', 'v. t.', 'To pierce with a poniard; to stab.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ponibility', 'n.', 'The capability of being placed or located.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pontes', 'pl. ', 'of Pons');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pons', 'n.', 'A bridge; -- applied to several parts which connect others,\n   but especially to the pons Varolii, a prominent band of nervous tissue\n   situated on the ventral side of the medulla oblongata and connected at\n   each side with the hemispheres of the cerebellum; the mesocephalon. See\n   Brain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pontage', 'n.', 'A duty or tax paid for repairing bridges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pontee', 'n.', 'An iron rod used by glass makers for manipulating the hot\n   glass; -- called also, puntil, puntel, punty, and ponty. See Fascet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pontic', 'a.', 'Of or pertaining to the Pontus, Euxine, or Black Sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pontifices', 'pl. ', 'of Pontifex');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pontifex', 'n.', 'A high priest; a pontiff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pontiff', 'n.', 'A high priest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pontiff', 'n.', 'One of the sacred college, in ancient Rome, which had the\n   supreme jurisdiction over all matters of religion, at the head of which\n   was the Pontifex Maximus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pontiff', 'n.', 'The chief priest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pontiff', 'n.', 'The pope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pontific', 'a.', 'Relating to, or consisting of, pontiffs or priests.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pontific', 'a.', 'Of or pertaining to the pope; papal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pontifical', 'a.', 'Of or pertaining to a pontiff, or high priest; as,\n   pontifical authority; hence, belonging to the pope; papal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pontifical', 'a.', 'Of or pertaining to the building of bridges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pontifical', 'n.', 'A book containing the offices, or formulas, used by a\n   pontiff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pontifical', 'n.', 'The dress and ornaments of a pontiff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pontificality', 'n.', 'The state and government of the pope; the papacy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pontifically', 'adv.', 'In a pontifical manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pontificate', 'n.', 'The state or dignity of a high priest; specifically,\n   the office of the pope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pontificate', 'n.', 'The term of office of a pontiff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pontificate', 'v. i.', 'To perform the duty of a pontiff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pontifice', 'n.', 'Bridgework; structure or edifice of a bridge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pontificial', 'a.', 'Papal; pontifical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pontifician', 'a.', 'Of or pertaining to the pontiff or pope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pontifician', 'n.', 'One who adheres to the pope or papacy; a papist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pontil', 'n.', 'Same as Pontee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pontile', 'a.', 'Of or pertaining to the pons Varolii. See Pons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pontine', 'a.', 'Of or pertaining to an extensive marshy district between\n   Rome and Naples.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pontlevis', 'n.', 'The action of a horse in rearing repeatedly and\n   dangerously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ponton', 'n.', 'See Pontoon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pontoon', 'n.', 'A wooden flat-bottomed boat, a metallic cylinder, or a\n   frame covered with canvas, India rubber, etc., forming a portable\n   float, used in building bridges quickly for the passage of troops.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pontoon', 'n.', 'A low, flat vessel, resembling a barge, furnished with\n   cranes, capstans, and other machinery, used in careening ships, raising\n   weights, drawing piles, etc., chiefly in the Mediterranean; a lighter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pontooning', 'n.', 'The act, art, or process of constructing pontoon\n   bridges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ponvolant', 'n.', 'A kind of light bridge, used in sieges, for surprising a\n   post or outwork which has but a narrow moat; a flying bridge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ponty', 'n.', 'See Pontee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ponies', 'pl. ', 'of Pony');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pony', 'n.', 'A small horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pony', 'n.', 'Twenty-five pounds sterling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pony', 'n.', 'A translation or a key used to avoid study in getting\n   lessons; a crib.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pony', 'n.', 'A small glass of beer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pood', 'n.', 'A Russian weight, equal to forty Russian pounds or about\n   thirty-six English pounds avoirdupois.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poodle', 'n.', 'A breed of dogs having curly hair, and often showing\n   remarkable intelligence in the performance of tricks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pooh', 'interj.', 'Pshaw! pish! nonsense! -- an expression of scorn,\n   dislike, or contempt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pooh-pooh', 'v. t.', 'To make light of; to treat with derision or contempt,\n   as if by saying pooh! pooh!');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pookoo', 'n.', 'A red African antelope (Kobus Vardoni) allied to the water\n   buck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pool', 'n.', 'A small and rather deep collection of (usually) fresh water,\n   as one supplied by a spring, or occurring in the course of a stream; a\n   reservoir for water; as, the pools of Solomon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pool', 'n.', 'A small body of standing or stagnant water; a puddle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pool', 'n.', 'The stake played for in certain games of cards, billiards,\n   etc.; an aggregated stake to which each player has contributed a snare;\n   also, the receptacle for the stakes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pool', 'n.', 'A game at billiards, in which each of the players stakes a\n   certain sum, the winner taking the whole; also, in public billiard\n   rooms, a game in which the loser pays the entrance fee for all who\n   engage in the game; a game of skill in pocketing the balls on a pool\n   table.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pool', 'n.', 'In rifle shooting, a contest in which each competitor pays a\n   certain sum for every shot he makes, the net proceeds being divided\n   among the winners.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pool', 'n.', 'Any gambling or commercial venture in which several persons\n   join.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pool', 'n.', 'A combination of persons contributing money to be used for\n   the purpose of increasing or depressing the market price of stocks,\n   grain, or other commodities; also, the aggregate of the sums so\n   contributed; as, the pool took all the wheat offered below the limit;\n   he put $10,000 into the pool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pool', 'n.', 'A mutual arrangement between competing lines, by which the\n   receipts of all are aggregated, and then distributed pro rata according\n   to agreement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pool', 'n.', 'An aggregation of properties or rights, belonging to\n   different people in a community, in a common fund, to be charged with\n   common liabilities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pooled', 'imp. & p. p.', 'of Pool');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pooling', 'p. pr. & vb. n.', 'of Pool');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pool', 'v. t.', 'To put together; to contribute to a common fund, on the\n   basis of a mutual division of profits or losses; to make a common\n   interest of; as, the companies pooled their traffic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pool', 'v. i.', 'To combine or contribute with others, as for a commercial,\n   speculative, or gambling transaction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pooler', 'n.', 'A stick for stirring a tan vat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pooling', 'n.', 'The act of uniting, or an agreement to unite, an\n   aggregation of properties belonging to different persons, with a view\n   to common liabilities or profits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poon', 'n.', 'A name for several East Indian, or their wood, used for the\n   masts and spars of vessels, as Calophyllum angustifolium, C.\n   inophullum, and Sterculia foetida; -- called also peon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poonac', 'n.', 'A kind of oil cake prepared from the cocoanut. See Oil\n   cake, under Cake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poonga oil', '', 'A kind of oil used in India for lamps, and for boiling\n   with dammar for pitching vessels. It is pressed from the seeds of a\n   leguminous tree (Pongamia glabra).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poop', 'n.', 'See 2d Poppy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pooped', 'imp. & p. p.', 'of Poop');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pooping', 'p. pr. & vb. n.', 'of Poop');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poop', 'v. i.', 'To make a noise; to pop; also, to break wind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poop', 'n.', 'A deck raised above the after part of a vessel; the hindmost\n   or after part of a vessels hull; also, a cabin covered by such a deck.\n   See Poop deck, under Deck. See also Roundhouse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poop', 'v. t.', 'To break over the poop or stern, as a wave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poop', 'v. t.', 'To strike in the stern, as by collision.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pooped', 'p. p. & a.', 'Having a poop; furnished with a poop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pooped', 'p. p. & a.', 'Struck on the poop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pooping', 'n.', 'The act or shock of striking a vessels stern by a\n   following wave or vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poor', 'superl.', 'Destitute of property; wanting in material riches or\n   goods; needy; indigent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poor', 'superl.', 'So completely destitute of property as to be entitled to\n   maintenance from the public.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poor', 'superl.', 'Destitute of such qualities as are desirable, or might\n   naturally be expected');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poor', 'superl.', 'Wanting in fat, plumpness, or fleshiness; lean;\n   emaciated; meager; as, a poor horse, ox, dog, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poor', 'superl.', 'Wanting in strength or vigor; feeble; dejected; as, poor\n   health; poor spirits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poor', 'superl.', 'Of little value or worth; not good; inferior; shabby;\n   mean; as, poor clothes; poor lodgings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poor', 'superl.', 'Destitute of fertility; exhausted; barren; sterile; --\n   said of land; as, poor soil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poor', 'superl.', 'Destitute of beauty, fitness, or merit; as, a poor\n   discourse; a poor picture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poor', 'superl.', 'Without prosperous conditions or good results;\n   unfavorable; unfortunate; unconformable; as, a poor business; the sick\n   man had a poor night.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poor', 'superl.', 'Inadequate; insufficient; insignificant; as, a poor\n   excuse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poor', 'superl.', 'Worthy of pity or sympathy; -- used also sometimes as a\n   term of endearment, or as an expression of modesty, and sometimes as a\n   word of contempt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poor', 'superl.', 'Free from self-assertion; not proud or arrogant; meek.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poor', 'n.', 'A small European codfish (Gadus minutus); -- called also\n   power cod.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poorbox', 'n.', 'A receptacle in which money given for the poor is placed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poorhouse', 'n.', 'A dwelling for a number of paupers maintained at public\n   expense; an almshouse; a workhouse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poor-john', 'n.', 'A small European fish, similar to the cod, but of\n   inferior quality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poorliness', 'n.', 'The quality or state of being poorly; ill health.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poorly', 'adv.', 'In a poor manner or condition; without plenty, or\n   sufficiency, or suitable provision for comfort; as, to live poorly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poorly', 'adv.', 'With little or no success; indifferently; with little\n   profit or advantage; as, to do poorly in business.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poorly', 'adv.', 'Meanly; without spirit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poorly', 'adv.', 'Without skill or merit; as, he performs poorly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poorly', 'a.', 'Somewhat ill; indisposed; not in health.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poorness', 'n.', 'The quality or state of being poor (in any of the senses\n   of the adjective).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poor-spirited', 'a.', 'Of a mean spirit; cowardly; base.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poor-will', 'n.', 'A bird of the Western United States (Phalaenoptilus\n   Nutalli) allied to the whip-poor-will.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poor-willie', 'n.', 'The bar-tailed godwit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pop', 'n.', 'A small, sharp, quick explosive sound or report; as, to go off\n   with a pop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pop', 'n.', 'An unintoxicating beverage which expels the cork with a pop\n   from the bottle containing it; as, ginger pop; lemon pop, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pop', 'n.', 'The European redwing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Popped', 'imp. & p. p.', 'of Pop');
  INSERT INTO words (word, word_type, description) VALUES  (    'Popping', 'p. pr. & vb. n.', 'of Pop');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pop', 'v. i.', 'To make a pop, or sharp, quick sound; as, the muskets\n   popped away on all sides.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pop', 'v. i.', 'To enter, or issue forth, with a quick, sudden movement; to\n   move from place to place suddenly; to dart; -- with in, out, upon, off,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pop', 'v. i.', 'To burst open with a pop, when heated over a fire; as, this\n   corn pops well.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pop', 'v. t.', 'To thrust or push suddenly; to offer suddenly; to bring\n   suddenly and unexpectedly to notice; as, to pop ones head in at the\n   door.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pop', 'v. t.', 'To cause to pop; to cause to burst open by heat, as grains\n   of Indian corn; as, to pop corn or chestnuts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pop', 'adv.', 'Like a pop; suddenly; unexpectedly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pope', 'n.', 'Any ecclesiastic, esp. a bishop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pope', 'n.', 'The bishop of Rome, the head of the Roman Catholic Church.\n   See Note under Cardinal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pope', 'n.', 'A parish priest, or a chaplain, of the Greek Church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pope', 'n.', 'A fish; the ruff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Popedom', 'n.', 'The place, office, or dignity of the pope; papal dignity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Popedom', 'n.', 'The jurisdiction of the pope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Popeling', 'n.', 'A petty or deputy pope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Popeling', 'n.', 'An adherent of the pope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Popelote', 'n.', 'A word variously explained as \"a little puppet,\" \"a\n   little doll,\" or \"a young butterfly.\" Cf. Popet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Popery', 'n.', 'The religion of the Roman Catholic Church, comprehending\n   doctrines and practices; -- generally used in an opprobrious sense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Popet', 'n.', 'A puppet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Popgun', 'n.', 'A childs gun; a tube and rammer for shooting pellets, with\n   a popping noise, by compression of air.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Popinjay', 'n.', 'The green woodpecker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Popinjay', 'n.', 'A parrot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Popinjay', 'n.', 'A target in the form of a parrot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Popinjay', 'n.', 'A trifling, chattering, fop or coxcomb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Popish', 'a.', 'Of or pertaining to the pope; taught or ordained by the\n   pope; hence, of or pertaining to the Roman Catholic Church; -- often\n   used opprobriously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poplar', 'n.', 'Any tree of the genus Populus; also, the timber, which is\n   soft, and capable of many uses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poplar', 'n.', 'The timber of the tulip tree; -- called also white poplar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poplexy', 'n.', 'Apoplexy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poplin', 'n.', 'A fabric of many varieties, usually made of silk and\n   worsted, -- used especially for womens dresses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Popliteal', 'a.', 'Of or pertaining to the ham; in the region of the ham,\n   or behind the knee joint; as, the popliteal space.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poplitic', 'a.', 'Popliteal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Popper', 'n.', 'A utensil for popping corn, usually a wire basket with a\n   long handle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Popper', 'n.', 'A dagger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poppet', 'n.', 'See Puppet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poppet', 'n.', 'One of certain upright timbers on the bilge ways, used to\n   support a vessel in launching.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poppet', 'n.', 'An upright support or guide fastened at the bottom only.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poppied', 'a.', 'Mingled or interspersed with poppies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poppied', 'a.', 'Affected with poppy juice; hence, figuratively, drugged;\n   drowsy; listless; inactive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Popping', '', 'a. & n. from Pop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Popple', 'v. i.', 'To move quickly up and down; to bob up and down, as a\n   cork on rough water; also, to bubble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Popple', 'n.', 'The poplar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Popple', 'n.', 'Tares.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poppies', 'pl. ', 'of Poppy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poppy', 'n.', 'Any plant or species of the genus Papaver, herbs with showy\n   polypetalous flowers and a milky juice. From one species (Papaver\n   somniferum) opium is obtained, though all the species contain it to\n   some extent; also, a flower of the plant. See Illust. of Capsule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poppy', 'n.', 'Alt. of Poppyhead');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poppyhead', 'n.', 'A raised ornament frequently having the form of a final.\n   It is generally used on the tops of the upright ends or elbows which\n   terminate seats, etc., in Gothic churches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Populace', 'n.', 'The common people; the vulgar; the multitude, --\n   comprehending all persons not distinguished by rank, office, education,\n   or profession.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Populacy', 'n.', 'Populace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Popular', 'a.', 'Of or pertaining to the common people, or to the whole\n   body of the people, as distinguished from a select portion; as, the\n   popular voice; popular elections.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Popular', 'a.', 'Suitable to common people; easy to be comprehended; not\n   abstruse; familiar; plain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Popular', 'a.', 'Adapted to the means of the common people; possessed or\n   obtainable by the many; hence, cheap; common; ordinary; inferior; as,\n   popular prices; popular amusements.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Popular', 'a.', 'Beloved or approved by the people; pleasing to people in\n   general, or to many people; as, a popular preacher; a popular law; a\n   popular administration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Popular', 'a.', 'Devoted to the common people; studious of the favor of the\n   populace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Popular', 'a.', 'Prevailing among the people; epidemic; as, a popular\n   disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Populares', 'n. pl.', 'The people or the peoples party, in ancient Rome,\n   as opposed to the optimates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Popularities', 'pl. ', 'of Popularity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Popularity', 'n.', 'The quality or state of being popular; especially, the\n   state of being esteemed by, or of being in favor with, the people at\n   large; good will or favor proceeding from the people; as, the\n   popularity of a law, statesman, or a book.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Popularity', 'n.', 'The quality or state of being adapted or pleasing to\n   common, poor, or vulgar people; hence, cheapness; inferiority;\n   vulgarity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Popularity', 'n.', 'Something which obtains, or is intended to obtain, the\n   favor of the vulgar; claptrap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Popularity', 'n.', 'The act of courting the favor of the people.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Popularity', 'n.', 'Public sentiment; general passion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Popularization', 'n.', 'The act of making popular, or of introducing among\n   the people.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Popularized', 'imp. & p. p.', 'of Popularize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Popularizing', 'p. pr. & vb. n.', 'of Popularize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Popularize', 'v. t.', 'To make popular; to make suitable or acceptable to\n   the common people; to make generally known; as, to popularize\n   philosophy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Popularizer', 'n.', 'One who popularizes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Popularly', 'adv.', 'In a popular manner; so as to be generally favored or\n   accepted by the people; commonly; currently; as, the story was\n   popularity reported.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Popularness', 'n.', 'The quality or state of being popular; popularity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Populate', 'a.', 'Populous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Populated', 'imp. & p. p.', 'of Populate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Populating', 'p. pr. & vb. n.', 'of Populate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Populate', 'v. t.', 'To furnish with inhabitants, either by natural\n   increase or by immigration or colonization; to cause to be inhabited;\n   to people.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Populate', 'v. i.', 'To propagate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Population', 'n.', 'The act or process of populating; multiplication of\n   inhabitants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Population', 'n.', 'The whole number of people, or inhabitants, in a\n   country, or portion of a country; as, a population of ten millions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Populator', 'n.', 'One who populates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Populicide', 'n.', 'Slaughter of the people.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Populin', 'n.', 'A glycoside, related to salicin, found in the bark of\n   certain species of the poplar (Populus), and extracted as a sweet white\n   crystalline substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Populosity', 'n.', 'Populousness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Populous', 'a.', 'Abounding in people; full of inhabitants; containing many\n   inhabitants in proportion to the extent of the country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Populous', 'a.', 'Popular; famous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Populous', 'a.', 'Common; vulgar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Populous', 'a.', 'Numerous; in large number.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poraille', 'n.', 'Poor people; the poor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Porbeagle', 'n.', 'A species of shark (Lamna cornubica), about eight feet\n   long, having a pointed nose and a crescent-shaped tail; -- called also\n   mackerel shark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Porcate', 'a.', 'Having grooves or furrows broader than the intervening\n   ridges; furrowed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Porcelain', 'n.', 'Purslain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Porcelain', 'n.', 'A fine translucent or semitransculent kind of\n   earthenware, made first in China and Japan, but now also in Europe and\n   America; -- called also China, or China ware.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Porcelainized', 'a.', 'Baked like potters lay; -- applied to clay shales\n   that have been converted by heat into a substance resembling porcelain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Porcelaneous', 'a.', 'Alt. of Porcellaneous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Porcellaneous', 'a.', 'Of or pertaining to porcelain; resembling porcelain;\n   as, porcelaneous shells.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Porcellaneous', 'a.', 'Having a smooth, compact shell without pores; --\n   said of certain Foraminifera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Porcelanite', 'n.', 'A semivitrified clay or shale, somewhat resembling\n   jasper; -- called also porcelain jasper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Porcelanous', 'a.', 'Alt. of Porcellanous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Porcellanous', 'a.', 'Porcelaneous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Porch', 'n.', 'A covered and inclosed entrance to a building, whether taken\n   from the interior, and forming a sort of vestibule within the main\n   wall, or projecting without and with a separate roof. Sometimes the\n   porch is large enough to serve as a covered walk. See also Carriage\n   porch, under Carriage, and Loggia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Porch', 'n.', 'A portico; a covered walk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Porcine', 'a.', 'Of or pertaining to swine; characteristic of the hog.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Porcupine', 'n.', 'Any Old Word rodent of the genus Hystrix, having the\n   back covered with long, sharp, erectile spines or quills, sometimes a\n   foot long. The common species of Europe and Asia (Hystrix cristata) is\n   the best known.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Porcupine', 'n.', 'Any species of Erethizon and related genera, native of\n   America. They are related to the true porcupines, but have shorter\n   spines, and are arboreal in their habits. The Canada porcupine\n   (Erethizon dorsatus) is a well known species.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pore', 'v.', 'One of the minute orifices in an animal or vegetable\n   membrane, for transpiration, absorption, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pore', 'v.', 'A minute opening or passageway; an interstice between the\n   constituent particles or molecules of a body; as, the pores of stones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pored', 'imp. & p. p.', 'of Pore');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poring', 'p. pr. & vb. n.', 'of Pore');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pore', 'v. i.', 'To look or gaze steadily in reading or studying; to fix\n   the attention; to be absorbed; -- often with on or upon, and now\n   usually with over.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poreblind', 'a.', 'Nearsighted; shortsighted; purblind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Porer', 'n.', 'One who pores.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Porgies', 'pl. ', 'of Porgy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Porgy', 'n.', 'The scup.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Porgy', 'n.', 'The sailors choice, or pinfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Porgy', 'n.', 'The margate fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Porgy', 'n.', 'The spadefish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Porgy', 'n.', 'Any one of several species of embiotocoids, or surf fishes,\n   of the Pacific coast. The name is also given locally to several other\n   fishes, as the bur fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Porifera', 'n. pl.', 'A grand division of the Invertebrata, including the\n   sponges; -- called also Spongiae, Spongida, and Spongiozoa. The\n   principal divisions are Calcispongiae, Keratosa or Fibrospongiae, and\n   Silicea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poriferan', 'n.', 'One of the Polifera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poriferata', 'n. pl.', 'The Polifera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poriform', 'a.', 'Resembling a pore, or small puncture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Porime', 'n.', 'A theorem or proposition so easy of demonstration as to be\n   almost self-evident.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poriness', 'n.', 'Porosity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Porism', 'n.', 'A proposition affirming the possibility of finding such\n   conditions as will render a certain determinate problem indeterminate\n   or capable of innumerable solutions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Porism', 'n.', 'A corollary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Porismatic', 'a.', 'Alt. of Porismatical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Porismatical', 'a.', 'Of or pertaining to a porism; poristic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poristic', 'a.', 'Alt. of Poristical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poristical', 'a.', 'Of or pertaining to a porism; of the nature of a\n   porism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Porite', 'n.', 'Any coral of the genus Porites, or family Poritidae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Porites', 'n.', 'An important genus of reef-building corals having small\n   twelve-rayed calicles, and a very porous coral. Some species are\n   branched, others grow in large massive or globular forms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pork', 'n.', 'The flesh of swine, fresh or salted, used for food.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Porker', 'n.', 'A hog.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Porket', 'n.', 'A young hog; a pig.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Porkling', 'n.', 'A pig; a porket.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Porkwood', 'n.', 'The coarse-grained brownish yellow wood of a small tree\n   (Pisonia obtusata) of Florida and the West Indies. Also called pigeon\n   wood, beefwood, and corkwood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pornerastic', 'a.', 'Lascivious; licentious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pornographic', 'a.', 'Of or pertaining to pornography; lascivious;\n   licentious; as, pornographic writing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pornography', 'n.', 'Licentious painting or literature; especially, the\n   painting anciently employed to decorate the walls of rooms devoted to\n   bacchanalian orgies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pornography', 'n.', 'A treatise on prostitutes, or prostitution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Porosity', 'n.', 'The quality or state of being porous; -- opposed to\n   density.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Porotic', 'n.', 'A medicine supposed to promote the formation of callus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Porous', 'n.', 'Full of pores; having interstices in the skin or in the\n   substance of the body; having spiracles or passages for fluids;\n   permeable by liquids; as, a porous skin; porous wood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Porously', 'adv.', 'In a porous manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Porousness', 'n.', 'The quality of being porous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Porousness', 'n.', 'The open parts; the interstices of anything.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Porpentine', 'n.', 'Porcupine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Porpesse', 'n.', 'A porpoise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Porphyraceous', 'a.', 'Porphyritic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Porphyre', 'n.', 'Porphyry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Porphyrite', 'n.', 'A rock with a porphyritic structure; as, augite\n   porphyrite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Porphyritic', 'a.', 'Relating to, or resembling, porphyry, that is,\n   characterized by the presence of distinct crystals, as of feldspar,\n   quartz, or augite, in a relatively fine-grained base, often aphanitic\n   or cryptocrystalline.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Porphyrization', 'n.', 'The act of porphyrizing, or the state of being\n   porphyrized.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Porphyrize', 'v. t.', 'To cause to resemble porphyry; to make spotted in\n   composition, like porphyry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Porphyrogenitism', 'n.', 'The principle of succession in royal families,\n   especially among the Eastern Roman emperors, by which a younger son, if\n   born after the accession of his father to the throne, was preferred to\n   an elder son who was not so born.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Porphyries', 'pl. ', 'of Porphyry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Porphyry', 'n.', 'A term used somewhat loosely to designate a rock\n   consisting of a fine-grained base (usually feldspathic) through which\n   crystals, as of feldspar or quartz, are disseminated. There are red,\n   purple, and green varieties, which are highly esteemed as marbles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Porpita', 'n.', 'A genus of bright-colored Siphonophora found floating in\n   the warmer parts of the ocean. The individuals are round and\n   disk-shaped, with a large zooid in the center of the under side,\n   surrounded by smaller nutritive and reproductive zooids, and by slender\n   dactylozooids near the margin. The disk contains a central float, or\n   pneumatocyst.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Porpoise', 'n.', 'Any small cetacean of the genus Phocaena, especially P.\n   communis, or P. phocaena, of Europe, and the closely allied American\n   species (P. Americana). The color is dusky or blackish above, paler\n   beneath. They are closely allied to the dolphins, but have a shorter\n   snout. Called also harbor porpoise, herring hag, puffing pig, and\n   snuffer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Porpoise', 'n.', 'A true dolphin (Delphinus); -- often so called by\n   sailors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Porporino', 'n.', 'A composition of quicksilver, tin, and sulphur, forming\n   a yellow powder, sometimes used by mediaeval artists, for the sake of\n   economy, instead of gold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Porpus', 'n.', 'A porpoise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Porraceous', 'a.', 'Resembling the leek in color; greenish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Porrect', 'a.', 'Extended horizontally; stretched out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Porrection', 'n.', 'The act of stretching forth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Porret', 'n.', 'A scallion; a leek or small onion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Porridge', 'n.', 'A food made by boiling some leguminous or farinaceous\n   substance, or the meal of it, in water or in milk, making of broth or\n   thin pudding; as, barley porridge, milk porridge, bean porridge, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Porringer', 'n.', 'A porridge dish; esp., a bowl or cup from which children\n   eat or are fed; as, a silver porringer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Port', 'n.', 'A dark red or purple astringent wine made in Portugal. It\n   contains a large percentage of alcohol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Port', 'v.', 'A place where ships may ride secure from storms; a sheltered\n   inlet, bay, or cove; a harbor; a haven. Used also figuratively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Port', 'v.', 'In law and commercial usage, a harbor where vessels are\n   admitted to discharge and receive cargoes, from whence they depart and\n   where they finish their voyages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Port', 'n.', 'A passageway; an opening or entrance to an inclosed place; a\n   gate; a door; a portal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Port', 'n.', 'An opening in the side of a vessel; an embrasure through\n   which cannon may be discharged; a porthole; also, the shutters which\n   close such an opening.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Port', 'n.', 'A passageway in a machine, through which a fluid, as steam,\n   water, etc., may pass, as from a valve to the interior of the cylinder\n   of a steam engine; an opening in a valve seat, or valve face.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ported', 'imp. & p. p.', 'of Port');
  INSERT INTO words (word, word_type, description) VALUES  (    'Porting', 'p. pr. & vb. n.', 'of Port');
  INSERT INTO words (word, word_type, description) VALUES  (    'Port', 'v. t.', 'To carry; to bear; to transport.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Port', 'v. t.', 'To throw, as a musket, diagonally across the body, with\n   the lock in front, the right hand grasping the small of the stock, and\n   the barrel sloping upward and crossing the point of the left shoulder;\n   as, to port arms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Port', 'n.', 'The manner in which a person bears himself; deportment;\n   carriage; bearing; demeanor; hence, manner or style of living; as, a\n   proud port.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Port', 'n.', 'The larboard or left side of a ship (looking from the stern\n   toward the bow); as, a vessel heels to port. See Note under Larboard.\n   Also used adjectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Port', 'v. t.', 'To turn or put to the left or larboard side of a ship; --\n   said of the helm, and used chiefly in the imperative, as a command; as,\n   port your helm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portae', 'pl. ', 'of Porta');
  INSERT INTO words (word, word_type, description) VALUES  (    'Porta', 'n.', 'The part of the liver or other organ where its vessels and\n   nerves enter; the hilus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Porta', 'n.', 'The foramen of Monro.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portability', 'n.', 'The quality or state of being portable; fitness to be\n   carried.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portable', 'a.', 'Capable of being borne or carried; easily transported;\n   conveyed without difficulty; as, a portable bed, desk, engine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portable', 'a.', 'Possible to be endured; supportable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portableness', 'n.', 'The quality or state of being portable; portability.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portace', 'n.', 'See Portass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portage', 'n.', 'A sailors wages when in port.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portage', 'n.', 'The amount of a sailors wages for a voyage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portage', 'n.', 'A porthole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portage', 'n.', 'The act of carrying or transporting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portage', 'n.', 'The price of carriage; porterage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portage', 'n.', 'Capacity for carrying; tonnage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portage', 'n.', 'A carry between navigable waters. See 3d Carry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portage', 'v. t. & i.', 'To carry (goods, boats, etc.) overland between\n   navigable waters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portage group', '', 'A subdivision of the Chemung period in American\n   geology. See Chart of Geology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portague', 'n.', 'A Portuguese gold coin formerly current, and variously\n   estimated to be worth from three and one half to four and one half\n   pounds sterling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portal', 'n.', 'A door or gate; hence, a way of entrance or exit,\n   especially one that is grand and imposing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portal', 'n.', 'The lesser gate, where there are two of different\n   dimensions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portal', 'n.', 'Formerly, a small square corner in a room separated from\n   the rest of the apartment by wainscoting, forming a short passage to\n   another apartment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portal', 'n.', 'By analogy with the French portail, used by recent writers\n   for the whole architectural composition which surrounds and includes\n   the doorways and porches of a church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portal', 'n.', 'The space, at one end, between opposite trusses when these\n   are terminated by inclined braces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portal', 'n.', 'A prayer book or breviary; a portass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portal', 'a.', 'Of or pertaining to a porta, especially the porta of the\n   liver; as, the portal vein, which enters the liver at the porta, and\n   divides into capillaries after the manner of an artery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portamento', 'n.', 'In singing, or in the use of the bow, a gradual\n   carrying or lifting of the voice or sound very smoothly from one note\n   to another; a gliding from tone to tone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portance', 'n.', 'See Port, carriage, demeanor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portass', 'n.', 'A breviary; a prayer book.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portate', 'a.', 'Borne not erect, but diagonally athwart an escutcheon; as,\n   a cross portate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portative', 'a.', 'Portable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portative', 'a.', 'Capable of holding up or carrying; as, the portative\n   force of a magnet, of atmospheric pressure, or of capillarity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portcluse', 'n.', 'A portcullis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portcrayon', 'n.', 'A metallic handle with a clasp for holding a crayon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portcullis', 'n.', 'A grating of iron or of timbers pointed with iron, hung\n   over the gateway of a fortress, to be let down to prevent the entrance\n   of an enemy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portcullis', 'n.', 'An English coin of the reign of Elizabeth, struck for\n   the use of the East India Company; -- so called from its bearing the\n   figure of a portcullis on the reverse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portcullised', 'imp. & p. p.', 'of Portcullis');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portcullising', 'p. pr. & vb. n.', 'of Portcullis');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portcullis', 'v. t.', 'To obstruct with, or as with, a portcullis; to shut;\n   to bar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Porte', 'n.', 'The Ottoman court; the government of the Turkish empire,\n   officially called the Sublime Porte, from the gate (port) of the\n   sultans palace at which justice was administered.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Porte-cochere', 'n.', 'A large doorway allowing vehicles to drive into or\n   through a building. It is common to have the entrance door open upon\n   the passage of the porte-cochere. Also, a porch over a driveway before\n   an entrance door.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ported', 'a.', 'Having gates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portegue', 'n.', 'See Portague.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portemonnaie', 'n.', 'A small pocketbook or wallet for carrying money.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portended', 'imp. & p. p.', 'of Portend');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portending', 'p. pr. & vb. n.', 'of Portend');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portend', 'v. t.', 'To indicate (events, misfortunes, etc.) as in future;\n   to foreshow; to foretoken; to bode; -- now used esp. of unpropitious\n   signs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portend', 'v. t.', 'To stretch out before.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portension', 'n.', 'The act of foreshowing; foreboding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portent', 'n.', 'That which portends, or foretoken; esp., that which\n   portends evil; a sign of coming calamity; an omen; a sign.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portentive', 'a.', 'Presaging; foreshadowing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portentous', 'a.', 'Of the nature of a portent; containing portents;\n   foreshadowing, esp. foreshadowing ill; ominous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portentous', 'a.', 'Hence: Monstrous; prodigious; wonderful; dreadful; as,\n   a beast of portentous size.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Porter', 'n.', 'A man who has charge of a door or gate; a doorkeeper; one\n   who waits at the door to receive messages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Porter', 'n.', 'A carrier; one who carries or conveys burdens, luggage,\n   etc.; for hire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Porter', 'n.', 'A bar of iron or steel at the end of which a forging is\n   made; esp., a long, large bar, to the end of which a heavy forging is\n   attached, and by means of which the forging is lifted and handled in\n   hammering and heating; -- called also porter bar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Porter', 'n.', 'A malt liquor, of a dark color and moderately bitter taste,\n   possessing tonic and intoxicating qualities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Porterage', 'n.', 'The work of a porter; the occupation of a carrier or of\n   a doorkeeper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Porterage', 'n.', 'Money charged or paid for the carriage of burdens or\n   parcels by a porter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Porteress', 'n.', 'See Portress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Porterhouse', 'n.', 'A house where porter is sold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portesse', 'n.', 'See Porteass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portfire', 'n.', 'A case of strong paper filled with a composition of\n   niter, sulphur, and mealed powder, -- used principally to ignite the\n   priming in proving guns, and as an incendiary material in shells.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portfolio', 'n.', 'A portable case for holding loose papers, prints,\n   drawings, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portfolio', 'n.', 'Hence: The office and functions of a minister of state\n   or member of the cabinet; as, to receive the portfolio of war; to\n   resign the portfolio.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portglave', 'n.', 'A sword bearer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portgreve', '', 'Alt. of Portgrave');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portgrave', '', 'In old English law, the chief magistrate of a port or\n   maritime town.; a portreeve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Porthole', 'n.', 'An embrasure in a ships side. See 3d Port.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Porthook', 'n.', 'One of the iron hooks to which the port hinges are\n   attached.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Porthors', 'n.', 'See Portass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Porticoes', 'pl. ', 'of Portico');
  INSERT INTO words (word, word_type, description) VALUES  (    'Porticos', 'pl. ', 'of Portico');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portico', 'n.', 'A colonnade or covered ambulatory, especially in classical\n   styles of architecture; usually, a colonnade at the entrance of a\n   building.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Porticoed', 'a.', 'Furnished with a portico.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portiere', 'n.', 'A curtain hanging across a doorway.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portigue', 'n.', 'See Portague.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portingal', 'a.', 'Of or pertaining to Portugal; Portuguese.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portingal', 'n.', 'A Portuguese.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portion', 'n.', 'That which is divided off or separated, as a part from a\n   whole; a separated part of anything.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portion', 'n.', 'A part considered by itself, though not actually cut off\n   or separated from the whole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portion', 'n.', 'A part assigned; allotment; share; fate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portion', 'n.', 'The part of an estate given to a child or heir, or\n   descending to him by law, and distributed to him in the settlement of\n   the estate; an inheritance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portion', 'n.', 'A wifes fortune; a dowry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portioned', 'imp. & p. p.', 'of Portion');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portioning', 'p. pr. & vb. n.', 'of Portion');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portion', 'v. t.', 'To separate or divide into portions or shares; to\n   parcel; to distribute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portion', 'v. t.', 'To endow with a portion or inheritance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portioner', 'n.', 'One who portions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portioner', 'n.', 'See Portionist, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portionist', 'n.', 'A scholar at Merton College, Oxford, who has a certain\n   academical allowance or portion; -- corrupted into postmaster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portionist', 'n.', 'One of the incumbents of a benefice which has two or\n   more rectors or vicars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portionless', 'a.', 'Having no portion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portise', 'n.', 'See Portass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portland cement', '', 'A cement having the color of the Portland stone of\n   England, made by calcining an artificial mixture of carbonate of lime\n   and clay, or sometimes certain natural limestones or chalky clays. It\n   contains a large proportion of clay, and hardens under water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portland stone', '', 'A yellowish-white calcareous freestone from the Isle\n   of Portland in England, much used in building.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portland vase', '', 'A celebrated cinerary urn or vase found in the tomb of\n   the Emperor Alexander Severus. It is owned by the Duke of Portland, and\n   kept in the British Museum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portlast', 'n.', 'The portoise. See Portoise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portliness', 'n.', 'The quality or state of being portly; dignity of mien\n   or of personal appearance; stateliness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portliness', 'n.', 'Bulkiness; corpulence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portly', 'a.', 'Having a dignified port or mien; of a noble appearance;\n   imposing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portly', 'a.', 'Bulky; corpulent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portmen', 'pl. ', 'of Portman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portman', 'n.', 'An inhabitant or burgess of a port, esp. of one of the\n   Cinque Ports.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portmanteaus', 'pl. ', 'of Portmanteau');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portmanteau', 'n.', 'A bag or case, usually of leather, for carrying\n   wearing apparel, etc., on journeys.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portmantle', 'n.', 'A portmanteau.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portmote', 'n.', 'In old English law, a court, or mote, held in a port\n   town.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portoir', 'n.', 'One who, or that which, bears; hence, one who, or that\n   which, produces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portoise', 'n.', 'The gunwale of a ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portos', 'n.', 'See Portass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portpane', 'n.', 'A cloth for carrying bread, so as not to touch it with\n   the hands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portrait', 'n.', 'The likeness of a person, painted, drawn, or engraved;\n   commonly, a representation of the human face painted from real life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portrait', 'n.', 'Hence, any graphic or vivid delineation or description of\n   a person; as, a portrait in words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portrait', 'v. t.', 'To portray; to draw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portraitist', 'n.', 'A portrait painter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portraiture', 'n.', 'A portrait; a likeness; a painted resemblance; hence,\n   that which is copied from some example or model.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portraiture', 'n.', 'Pictures, collectively; painting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portraiture', 'n.', 'The art or practice of making portraits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portraiture', 'v. t.', 'To represent by a portrait, or as by a portrait; to\n   portray.');
  INSERT INTO words (word, word_type, description) VALUES  (    'portrayed', 'imp. & p. p.', 'of Portray');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portraying', 'p. pr. & vb. n.', 'of Portray');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portray', 'v. t.', 'To paint or draw the likeness of; as, to portray a king\n   on horseback.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portray', 'v. t.', 'Hence, figuratively, to describe in words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portray', 'v. t.', 'To adorn with pictures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portrayal', 'n.', 'The act or process of portraying; description;\n   delineation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portrayer', 'n.', 'One who portrays.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portreeve', 'n.', 'A port warden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portress', 'n.', 'A female porter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Port-royalist', 'n.', 'One of the dwellers in the Cistercian convent of\n   Port Royal des Champs, near Paris, when it was the home of the\n   Jansenists in the 17th century, among them being Arnauld, Pascal, and\n   other famous scholars. Cf. Jansenist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portsale', 'n.', 'Public or open sale; auction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portuary', 'n.', 'A breviary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portuguese', 'a.', 'Of or pertaining to Portugal, or its inhabitants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portuguese', 'n. sing. & pl.', 'A native or inhabitant of Portugal; people\n   of Portugal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portulaca', 'n.', 'A genus of polypetalous plants; also, any plant of the\n   genus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Portulacaceous', 'a.', 'Of or pertaining to a natural order of plants\n   (Portulacaceae), of which Portulaca is the type, and which includes\n   also the spring beauty (Claytonia) and other genera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Porwigle', 'n.', 'See Polliwig.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pory', 'a.', 'Porous; as, pory stone. [R.] Dryden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pose', 'a.', 'Standing still, with all the feet on the ground; -- said of\n   the attitude of a lion, horse, or other beast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pose', 'n.', 'A cold in the head; catarrh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pose', 'v. t.', 'The attitude or position of a person; the position of the\n   body or of any member of the body; especially, a position formally\n   assumed for the sake of effect; an artificial position; as, the pose of\n   an actor; the pose of an artists model or of a statue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Posed', 'imp. & p. p.', 'of Pose');
  INSERT INTO words (word, word_type, description) VALUES  (    'Posing', 'p. pr. & vb. n.', 'of Pose');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pose', 'v. t.', 'To place in an attitude or fixed position, for the sake of\n   effect; to arrange the posture and drapery of (a person) in a studied\n   manner; as, to pose a model for a picture; to pose a sitter for a\n   portrait.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pose', 'v. i.', 'To assume and maintain a studied attitude, with studied\n   arrangement of drapery; to strike an attitude; to attitudinize;\n   figuratively, to assume or affect a certain character; as, she poses as\n   a prude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pose', 'v. t.', 'To interrogate; to question.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pose', 'v. t.', 'To question with a view to puzzling; to embarrass by\n   questioning or scrutiny; to bring to a stand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Posed', 'a.', 'Firm; determined; fixed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poser', 'n.', 'One who, or that which, puzzles; a difficult or inexplicable\n   question or fact.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Posied', 'a.', 'Inscribed with a posy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Posingly', 'adv.', 'So as to pose or puzzle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Posited', 'imp. & p. p.', 'of Posit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Positing', 'p. pr. & vb. n.', 'of Posit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Posit', 'v. t.', 'To dispose or set firmly or fixedly; to place or dispose\n   in relation to other objects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Posit', 'v. t.', 'To assume as real or conceded; as, to posit a principle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Position', 'n.', 'The state of being posited, or placed; the manner in\n   which anything is placed; attitude; condition; as, a firm, an inclined,\n   or an upright position.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Position', 'n.', 'The spot where a person or thing is placed or takes a\n   place; site; place; station; situation; as, the position of man in\n   creation; the fleet changed its position.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Position', 'n.', 'Hence: The ground which any one takes in an argument or\n   controversy; the point of view from which any one proceeds to a\n   discussion; also, a principle laid down as the basis of reasoning; a\n   proposition; a thesis; as, to define ones position; to appear in a\n   false position.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Position', 'n.', 'Relative place or standing; social or official rank; as,\n   a person of position; hence, office; post; as, to lose ones position.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Position', 'n.', 'A method of solving a problem by one or two suppositions;\n   -- called also the rule of trial and error.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Position', 'v. t.', 'To indicate the position of; to place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Positional', 'a.', 'Of or pertaining to position.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Positive', 'a.', 'Having a real position, existence, or energy; existing in\n   fact; real; actual; -- opposed to negative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Positive', 'a.', 'Derived from an object by itself; not dependent on\n   changing circumstances or relations; absolute; -- opposed to relative;\n   as, the idea of beauty is not positive, but depends on the different\n   tastes individuals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Positive', 'a.', 'Definitely laid down; explicitly stated; clearly\n   expressed; -- opposed to implied; as, a positive declaration or\n   promise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Positive', 'a.', 'Hence: Not admitting of any doubt, condition,\n   qualification, or discretion; not dependent on circumstances or\n   probabilities; not speculative; compelling assent or obedience;\n   peremptory; indisputable; decisive; as, positive instructions; positive\n   truth; positive proof.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Positive', 'a.', 'Prescribed by express enactment or institution; settled\n   by arbitrary appointment; said of laws.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Positive', 'a.', 'Fully assured; confident; certain; sometimes,\n   overconfident; dogmatic; overbearing; -- said of persons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Positive', 'a.', 'Having the power of direct action or influence; as, a\n   positive voice in legislation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Positive', 'a.', 'Corresponding with the original in respect to the\n   position of lights and shades, instead of having the lights and shades\n   reversed; as, a positive picture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Positive', 'a.', 'Electro-positive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Positive', 'a.', 'Hence, basic; metallic; not acid; -- opposed to negative,\n   and said of metals, bases, and basic radicals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Positive', 'n.', 'That which is capable of being affirmed; reality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Positive', 'n.', 'That which settles by absolute appointment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Positive', 'n.', 'The positive degree or form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Positive', 'n.', 'A picture in which the lights and shades correspond in\n   position with those of the original, instead of being reversed, as in a\n   negative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Positive', 'n.', 'The positive plate of a voltaic or electrolytic cell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Positively', 'adv.', 'In a positive manner; absolutely; really; expressly;\n   with certainty; indubitably; peremptorily; dogmatically; -- opposed to\n   negatively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Positiveness', 'n.', 'The quality or state of being positive; reality;\n   actualness; certainty; confidence; peremptoriness; dogmatism. See\n   Positive, a.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Positivism', 'n.', 'A system of philosophy originated by M. Auguste Comte,\n   which deals only with positives. It excludes from philosophy everything\n   but the natural phenomena or properties of knowable things, together\n   with their invariable relations of coexistence and succession, as\n   occurring in time and space. Such relations are denominated laws, which\n   are to be discovered by observation, experiment, and comparison. This\n   philosophy holds all inquiry into causes, both efficient and final, to\n   be useless and unprofitable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Positivist', 'n.', 'A believer in positivism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Positivist', 'a.', 'Relating to positivism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Positivity', 'n.', 'Positiveness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Positure', 'n.', 'See Posture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Posnet', 'n.', 'A little basin; a porringer; a skillet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Posologic', 'a.', 'Alt. of Posological');
  INSERT INTO words (word, word_type, description) VALUES  (    'Posological', 'a.', 'Pertaining to posology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Posology', 'n.', 'The science or doctrine of doses; dosology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pospolite', 'n.', 'A kind of militia in Poland, consisting of the gentry,\n   which, in case of invasion, was summoned to the defense of the country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poss', 'v. t.', 'To push; to dash; to throw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Posse', 'n.', 'See Posse comitatus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Posse comitatus', '', 'The power of the county, or the citizens who may be\n   summoned by the sheriff to assist the authorities in suppressing a\n   riot, or executing any legal precept which is forcibly opposed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Posse comitatus', '', 'A collection of people; a throng; a rabble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Possessed', 'imp. & p. p.', 'of Possess');
  INSERT INTO words (word, word_type, description) VALUES  (    'Possessing', 'p. pr. & vb. n.', 'of Possess');
  INSERT INTO words (word, word_type, description) VALUES  (    'Possess', 'v. t.', 'To occupy in person; to hold or actually have in ones\n   own keeping; to have and to hold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Possess', 'v. t.', 'To have the legal title to; to have a just right to; to\n   be master of; to own; to have; as, to possess property, an estate, a\n   book.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Possess', 'v. t.', 'To obtain occupation or possession of; to accomplish;\n   to gain; to seize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Possess', 'v. t.', 'To enter into and influence; to control the will of; to\n   fill; to affect; -- said especially of evil spirits, passions, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Possess', 'v. t.', 'To put in possession; to make the owner or holder of\n   property, power, knowledge, etc.; to acquaint; to inform; -- followed\n   by of or with before the thing possessed, and now commonly used\n   reflexively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Possession', 'n.', 'The act or state of possessing, or holding as ones\n   own.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Possession', 'n.', 'The having, holding, or detention of property in ones\n   power or command; actual seizin or occupancy; ownership, whether\n   rightful or wrongful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Possession', 'n.', 'The thing possessed; that which any one occupies, owns,\n   or controls; in the plural, property in the aggregate; wealth;\n   dominion; as, foreign possessions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Possession', 'n.', 'The state of being possessed or controlled, as by an\n   evil spirit, or violent passions; madness; frenzy; as, demoniacal\n   possession.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Possession', 'v. t.', 'To invest with property.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Possessionary', 'a.', 'Of or pertaining to possession; arising from\n   possession.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Possessioner', 'n.', 'A possessor; a property holder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Possessioner', 'n.', 'An invidious name for a member of any religious\n   community endowed with property in lands, buildings, etc., as\n   contrasted with mendicant friars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Possessival', 'a.', 'Of or pertaining to the possessive case; as, a\n   possessival termination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Possessive', 'a.', 'Of or pertaining to possession; having or indicating\n   possession.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Possessive', 'n.', 'The possessive case.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Possessive', 'n.', 'A possessive pronoun, or a word in the possessive case.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Possessively', 'adv.', 'In a possessive manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Possessor', 'n.', 'One who possesses; one who occupies, holds, owns, or\n   controls; one who has actual participation or enjoyment, generally of\n   that which is desirable; a proprietor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Possessory', 'a.', 'Of or pertaining to possession, either as a fact or a\n   right; of the nature of possession; as, a possessory interest; a\n   possessory lord.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Posset', 'n.', 'A beverage composed of hot milk curdled by some strong\n   infusion, as by wine, etc., -- much in favor formerly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Posseted', 'imp. & p. p.', 'of Posset');
  INSERT INTO words (word, word_type, description) VALUES  (    'Posseting', 'p. pr. & vb. n.', 'of Posset');
  INSERT INTO words (word, word_type, description) VALUES  (    'Posset', 'v. t.', 'To curdle; to turn, as milk; to coagulate; as, to posset\n   the blood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Posset', 'v. t.', 'To treat with possets; to pamper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Possibilities', 'pl. ', 'of Possibility');
  INSERT INTO words (word, word_type, description) VALUES  (    'Possibility', 'n.', 'The quality or state of being possible; the power of\n   happening, being, or existing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Possibility', 'n.', 'That which is possible; a contingency; a thing or\n   event that may not happen; a contingent interest, as in real or\n   personal estate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Possible', 'a.', 'Capable of existing or occurring, or of being conceived\n   or thought of; able to happen; capable of being done; not contrary to\n   the nature of things; -- sometimes used to express extreme\n   improbability; barely able to be, or to come to pass; as, possibly he\n   is honest, as it is possible that Judas meant no wrong.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Possibly', 'adv.', 'In a possible manner; by possible means; especially, by\n   extreme, remote, or improbable intervention, change, or exercise of\n   power; by a chance; perhaps; as, possibly he may recover.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Possum', 'n.', 'An opossum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Post-', '', 'A prefix signifying behind, back, after; as, postcommissure,\n   postdot, postscript.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Post', 'a.', 'Hired to do what is wrong; suborned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Post', 'n.', 'A piece of timber, metal, or other solid substance, fixed, or\n   to be fixed, firmly in an upright position, especially when intended as\n   a stay or support to something else; a pillar; as, a hitching post; a\n   fence post; the posts of a house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Post', 'n.', 'The doorpost of a victualers shop or inn, on which were\n   chalked the scores of customers; hence, a score; a debt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Post', 'n.', 'The place at which anything is stopped, placed, or fixed; a\n   station.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Post', 'n.', 'A station, or one of a series of stations, established for\n   the refreshment and accommodation of travelers on some recognized\n   route; as, a stage or railway post.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Post', 'n.', 'A military station; the place at which a soldier or a body of\n   troops is stationed; also, the troops at such a station.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Post', 'n.', 'The piece of ground to which a sentinels walk is limited.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Post', 'n.', 'A messenger who goes from station; an express; especially,\n   one who is employed by the government to carry letters and parcels\n   regularly from one place to another; a letter carrier; a postman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Post', 'n.', 'An established conveyance for letters from one place or\n   station to another; especially, the governmental system in any country\n   for carrying and distributing letters and parcels; the post office; the\n   mail; hence, the carriage by which the mail is transported.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Post', 'n.', 'Haste or speed, like that of a messenger or mail carrier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Post', 'n.', 'One who has charge of a station, especially of a postal\n   station.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Post', 'n.', 'A station, office, or position of service, trust, or\n   emolument; as, the post of duty; the post of danger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Post', 'n.', 'A size of printing and writing paper. See the Table under\n   Paper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Posted', 'imp. & p. p.', 'of Post');
  INSERT INTO words (word, word_type, description) VALUES  (    'Posting', 'p. pr. & vb. n.', 'of Post');
  INSERT INTO words (word, word_type, description) VALUES  (    'Post', 'v. t.', 'To attach to a post, a wall, or other usual place of\n   affixing public notices; to placard; as, to post a notice; to post\n   playbills.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Post', 'v. t.', 'To hold up to public blame or reproach; to advertise\n   opprobriously; to denounce by public proclamation; as, to post one for\n   cowardice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Post', 'v. t.', 'To enter (a name) on a list, as for service, promotion, or\n   the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Post', 'v. t.', 'To assign to a station; to set; to place; as, to post a\n   sentinel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Post', 'v. t.', 'To carry, as an account, from the journal to the ledger;\n   as, to post an account; to transfer, as accounts, to the ledger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Post', 'v. t.', 'To place in the care of the post; to mail; as, to post a\n   letter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Post', 'v. t.', 'To inform; to give the news to; to make (one) acquainted\n   with the details of a subject; -- often with up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Post', 'v. i.', 'To travel with post horses; figuratively, to travel in\n   haste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Post', 'v. i.', 'To rise and sink in the saddle, in accordance with the\n   motion of the horse, esp. in trotting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Post', 'adv.', 'With post horses; hence, in haste; as, to travel post.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Post-abdomen', 'n.', 'That part of a crustacean behind the cephalothorax;\n   -- more commonly called abdomen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postable', 'a.', 'Capable of being carried by, or as by, post.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postact', 'n.', 'An act done afterward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postage', 'n.', 'The price established by law to be paid for the conveyance\n   of a letter or other mailable matter by a public post.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postal', 'a.', 'Belonging to the post office or mail service; as, postal\n   arrangements; postal authorities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postanal', 'a.', 'Situated behind, or posterior to, the anus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postaxial', 'a.', 'Situated behind any transverse axis in the body of an\n   animal; caudal; posterior; especially, behind, or on the caudal or\n   posterior (that is, ulnar or fibular) side of, the axis of a vertebrate\n   limb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postboy', 'n.', 'One who rides post horses; a position; a courier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postboy', 'n.', 'A boy who carries letters from the post.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Post-captain', 'n.', 'A captain of a war vessel whose name appeared, or was\n   \"posted,\" in the seniority list of the British navy, as distinguished\n   from a commander whose name was not so posted. The term was also used\n   in the United States navy; but no such commission as post-captain was\n   ever recognized in either service, and the term has fallen into disuse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postcavae', 'pl. ', 'of Postcava');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postcava', 'n.', 'The inferior vena cava.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postclavicle', 'n.', 'A bone in the pectoral girdle of many fishes\n   projecting backward from the clavicle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postcommissure', 'n.', 'A transverse commisure in the posterior part of the\n   roof of the third ventricle of the brain; the posterior cerebral\n   commisure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postcomminion', 'n.', 'The concluding portion of the communion service.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postcomminion', 'n.', 'A prayer or prayers which the priest says at Mass,\n   after the ablutions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postcornua', 'pl. ', 'of Postcornu');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postcornu', 'n.', 'The posterior horn of each lateral ventricle of the\n   brain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postdated', 'imp. & p. p.', 'of Postdate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postdating', 'p. pr. & vb. n.', 'of Postdate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postdate', 'v. t.', 'To date after the real time; as, to postdate a\n   contract, that is, to date it later than the time when it was in fact\n   made.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postdate', 'v. t.', 'To affix a date to after the event.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postdate', 'a.', 'Made or done after the date assigned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postdate', 'n.', 'A date put to a bill of exchange or other paper, later\n   than that when it was actually made.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postdiluvial', 'a.', 'Alt. of Postdiluvian');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postdiluvian', 'a.', 'Being or happening after the flood in Noahs days.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postdiluvian', 'n.', 'One who lived after the flood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Post-disseizin', 'n.', 'A subsequent disseizin committed by one of lands\n   which the disseizee had before recovered of the same disseizor; a writ\n   founded on such subsequent disseizin, now abolished.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Post-disseizor', 'n.', 'A person who disseizes another of lands which the\n   disseizee had before recovered of the same disseizor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postea', 'n.', 'The return of the judge before whom a cause was tried,\n   after a verdict, of what was done in the cause, which is indorsed on\n   the nisi prius record.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postel', 'n.', 'Apostle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postencephalon', 'n.', 'The metencephalon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postentry', 'n.', 'A second or subsequent, at the customhouse, of goods\n   which had been omitted by mistake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postentry', 'n.', 'An additional or subsequent entry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poster', 'n.', 'A large bill or placard intended to be posted in public\n   places.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poster', 'n.', 'One who posts bills; a billposter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poster', 'n.', 'One who posts, or travels expeditiously; a courier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poster', 'n.', 'A post horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Posterior', 'a.', 'Later in time; hence, later in the order of proceeding\n   or moving; coming after; -- opposed to prior.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Posterior', 'a.', 'Situated behind; hinder; -- opposed to anterior.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Posterior', 'a.', 'At or toward the caudal extremity; caudal; -- in human\n   anatomy often used for dorsal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Posterior', 'a.', 'On the side next the axis of inflorescence; -- said of\n   an axillary flower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Posteriority', 'n.', 'The state of being later or subsequent; as,\n   posteriority of time, or of an event; -- opposed to priority.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Posteriorly', 'adv.', 'Subsequently in time; also, behind in position.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Posteriors', 'n. pl.', 'The hinder parts, as of an animals body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Posterity', 'n.', 'The race that proceeds from a progenitor; offspring to\n   the furthest generation; the aggregate number of persons who are\n   descended from an ancestor of a generation; descendants; -- contrasted\n   with ancestry; as, the posterity of Abraham.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Posterity', 'n.', 'Succeeding generations; future times.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postern', 'n.', 'Originally, a back door or gate; a private entrance;\n   hence, any small door or gate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postern', 'n.', 'A subterraneous passage communicating between the parade\n   and the main ditch, or between the ditches and the interior of the\n   outworks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postern', 'a.', 'Back; being behind; private.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postero', '', '- (/). A combining form meaning posterior, back; as,\n   postero-inferior, situated back and below; postero-lateral, situated\n   back and at the side.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postexist', 'v. i.', 'To exist after; to live subsequently.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postexistence', 'n.', 'Subsequent existence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postexistent', 'a.', 'Existing or living after.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postfact', 'a.', 'Relating to a fact that occurs after another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postfact', 'n.', 'A fact that occurs after another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postfactum', 'n.', 'Same as Postfact.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Post-fine', 'n.', 'A duty paid to the king by the cognizee in a fine of\n   lands, when the same was fully passed; -- called also the kings\n   silver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postfixes', 'pl. ', 'of Postfix');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postfix', 'n.', 'A letter, syllable, or word, added to the end of another\n   word; a suffix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postfix', 'v. t.', 'To annex; specifically (Gram.), to add or annex, as a\n   letter, syllable, or word, to the end of another or principal word; to\n   suffix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postfrontal', 'a.', 'Situated behind the frontal bone or the frontal region\n   of the skull; -- applied especially to a bone back of and below the\n   frontal in many animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postfrontal', 'n.', 'A postfrontal bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postfurcae', 'pl. ', 'of Postfurca');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postfurca', 'n.', 'One of the internal thoracic processes of the sternum of\n   an insect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postgeniture', 'n.', 'The condition of being born after another in the same\n   family; -- distinguished from primogeniture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postglenoid', 'a.', 'Situated behind the glenoid fossa of the temporal\n   bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Posthaste', 'n.', 'Haste or speed in traveling, like that of a post or\n   courier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Posthaste', 'adv.', 'With speed or expedition; as, he traveled posthaste;\n   to send posthaste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Posthetomy', 'n.', 'Circumcision.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Posthouse', 'n.', 'A house established for the convenience of the post,\n   where relays of horses can be obtained.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Posthouse', 'n.', 'A house for distributing the malls; a post office.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Posthume', 'a.', 'Alt. of Posthumed');
  INSERT INTO words (word, word_type, description) VALUES  (    'Posthumed', 'a.', 'Posthumous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Posthumous', 'a.', 'Born after the death of the father, or taken from the\n   dead body of the mother; as, a posthumous son or daughter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Posthumous', 'a.', 'Published after the death of the author; as, posthumous\n   works; a posthumous edition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Posthumous', 'a.', 'Being or continuing after ones death; as, a posthumous\n   reputation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Posthumously', 'adv.', 'In a posthumous manner; after ones decease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postic', 'a.', 'Backward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postticous', 'a.', 'Posterior.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postticous', 'a.', 'Situated on the outer side of a filament; -- said of an\n   extrorse anther.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postil', 'n.', 'Originally, an explanatory note in the margin of the Bible,\n   so called because written after the text; hence, a marginal note; a\n   comment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postil', 'n.', 'A short homily or commentary on a passage of Scripture; as,\n   the first postils were composed by order of Charlemagne.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postil', 'v. t.', 'To write marginal or explanatory notes on; to gloss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postiled', 'imp. & p. p.', 'of Postil');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postilled', '', 'of Postil');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postiling', 'p. pr. & vb. n.', 'of Postil');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postilling', '', 'of Postil');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postil', 'v. i.', 'To write postils, or marginal notes; to comment; to\n   postillate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postiler', 'n.', 'One who writers marginal notes; one who illustrates the\n   text of a book by notes in the margin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postilion', 'n.', 'One who rides and guides the first pair of horses of a\n   coach or post chaise; also, one who rides one of the horses when one\n   pair only is used.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postillate', 'v. t.', 'To explain by marginal notes; to postil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postillate', 'v. i.', 'To write postils; to comment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postillate', 'v. i.', 'To preach by expounding Scripture verse by verse, in\n   regular order.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postillation', 'n.', 'The act of postillating; exposition of Scripture in\n   preaching.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postillator', 'n.', 'One who postillates; one who expounds the Scriptures\n   verse by verse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Posttiller', 'n.', 'See Postiler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Posting', 'n.', 'The act of traveling post.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Posting', 'n.', 'The act of transferring an account, as from the journal to\n   the ledger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postliminiar', 'a.', 'Contrived, done, or existing subsequently.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postliminiary', 'a.', 'Pertaining to, or involving, the right of\n   postliminium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postliminium', 'n.', 'Alt. of Postliminy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postliminy', 'n.', 'The return to his own country, and his former\n   privileges, of a person who had gone to sojourn in a foreign country,\n   or had been banished, or taken by an enemy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postliminy', 'n.', 'The right by virtue of which persons and things taken\n   by an enemy in war are restored to their former state when coming again\n   under the power of the nation to which they belonged.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postlude', 'n.', 'A voluntary at the end of a service.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postmen', 'pl. ', 'of Postman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postman', 'n.', 'A post or courier; a letter carrier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postman', 'n.', 'One of the two most experienced barristers in the Court of\n   Exchequer, who have precedence in motions; -- so called from the place\n   where he sits. The other of the two is called the tubman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postmark', 'n.', 'The mark, or stamp, of a post office on a letter, giving\n   the place and date of mailing or of arrival.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postmarked', 'imp. & p. p.', 'of Postmark');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postmarking', 'p. pr. & vb. n.', 'of Postmark');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postmark', 'v. t.', 'To mark with a post-office stamp; as, to postmark a\n   letter or parcel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postmaster', 'n.', 'One who has charge of a station for the accommodation\n   of travelers; one who supplies post horses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postmaster', 'n.', 'One who has charge of a post office, and the\n   distribution and forwarding of mails.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postmasters-general', 'pl. ', 'of Postmaster-general');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postmaster-general', 'n.', 'The chief officer of the post-office department\n   of a government. In the United States the postmaster-general is a\n   member of the cabinet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postmastership', 'n.', 'The office of postmaster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postmeridian', 'a.', 'Coming after the sun has passed the meridian; being\n   in, or belonging to, the afternoon. (Abbrev. P. M.)');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postmeridian', 'a.', 'Fig., belonging to the after portion of life; late.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Post-mortem', 'a.', 'After death; as, post-mortem rigidity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postnares', 'n. pl.', 'The posterior nares. See Nares.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postnatal', 'a.', 'After birth; subsequent to birth; as, postnatal\n   infanticide; postnatal diseases.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postnate', 'a.', 'Subsequent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Post note', '', 'A note issued by a bank, payable at some future specified\n   time, as distinguished from a note payable on demand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postnuptial', 'a.', 'Being or happening after marriage; as, a postnuptial\n   settlement on a wife.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Post-obit', '', 'Alt. of Post-obit bond');
  INSERT INTO words (word, word_type, description) VALUES  (    'Post-obit bond', '', 'A bond in which the obligor, in consideration of\n   having received a certain sum of money, binds himself to pay a larger\n   sum, on unusual interest, on the death of some specified individual\n   from whom he has expectations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postoblongata', 'n.', 'The posterior part of the medulla oblongata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postocular', 'a. & n.', 'Same as Postorbital.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Post office', 'n.', 'See under 4th Post.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postoral', 'a.', 'Situated behind, or posterior to, the mouth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postorbital', 'a.', 'Situated behind the orbit; as, the postorbital scales\n   of some fishes and reptiles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postorbital', 'n.', 'A postorbital bone or scale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postpaid', 'a.', 'Having the postage prepaid, as a letter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postpalatine', 'a.', 'Situated behind the palate, or behind the palatine\n   bones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postpliocene', 'a.', 'Of or pertaining to the period immediately following\n   the Pliocene; Pleistocene. Also used as a noun. See Quaternary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postponed', 'imp. & p. p.', 'of Postpone');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postponing', 'p. pr. & vb. n.', 'of Postpone');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postpone', 'v. t.', 'To defer to a future or later time; to put off; also,\n   to cause to be deferred or put off; to delay; to adjourn; as, to\n   postpone the consideration of a bill to the following day, or\n   indefinitely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postpone', 'v. t.', 'To place after, behind, or below something, in respect\n   to precedence, preference, value, or importance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postponement', 'n.', 'The act of postponing; a deferring, or putting off,\n   to a future time; a temporary delay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postponence', 'n.', 'The act of postponing, in sense 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postponer', 'n.', 'One who postpones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postposed', 'imp. & p. p.', 'of Postpose');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postposing', 'p. pr. & vb. n.', 'of Postpose');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postpose', 'v. t.', 'To postpone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postposit', 'v. t.', 'To postpone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postposition', 'n.', 'The act of placing after, or the state of being\n   placed after.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postposition', 'n.', 'A word or particle placed after, or at the end of,\n   another word; -- distinguished from preposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postpositional', 'a.', 'Of or pertaining to postposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postpositive', 'a.', 'Placed after another word; as, a postpositive\n   conjunction; a postpositive letter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postprandial', 'a.', 'Happening, or done, after dinner; after-dinner; as,\n   postprandial speeches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postremogeniture', 'n.', 'The right of the youngest born.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postremote', 'a.', 'More remote in subsequent time or order.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postrider', 'n.', 'One who rides over a post road to carry the mails.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postscapula', 'n.', 'The part of the scapula behind or below the spine, or\n   mesoscapula.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postscapular', 'a.', 'Of or pertaining to the postscapula; infraspinous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postscenium', 'n.', 'The part of a theater behind the scenes; the back part\n   of the stage of a theater.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postscribe', 'v. t.', 'To make a postscript.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postscript', 'n.', 'A paragraph added to a letter after it is concluded and\n   signed by the writer; an addition made to a book or composition after\n   the main body of the work has been finished, containing something\n   omitted, or something new occurring to the writer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postscripted', 'a.', 'Having a postscript; added in a postscript.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postscutellum', 'n.', 'The hindermost dorsal piece of a thoracic somite of\n   an insect; the plate behind the scutellum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postsphenoid', 'a.', 'Of or pertaining to the posterior part of the\n   sphenoid bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Post-temporal', 'a.', 'Situated back of the temporal bone or the temporal\n   region of the skull; -- applied especially to a bone which usually\n   connects the supraclavicle with the skull in the pectoral arch of\n   fishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Post-temporal', 'n.', 'A post-temporal bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Posttertiary', 'a.', 'Following, or more recent than, the Tertiary;\n   Quaternary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Post-tragus', 'n.', 'A ridge within and behind the tragus in the ear of\n   some animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Post-tympanic', 'a.', 'Situated behind the tympanum, or in the skull,\n   behind the auditory meatus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postulant', 'n.', 'One who makes a request or demand; hence, a candidate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postulate', 'n.', 'Something demanded or asserted; especially, a position\n   or supposition assumed without proof, or one which is considered as\n   self-evident; a truth to which assent may be demanded or challenged,\n   without argument or evidence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postulate', 'n.', 'The enunciation of a self-evident problem, in\n   distinction from an axiom, which is the enunciation of a self-evident\n   theorem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postulate', 'a.', 'Postulated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postulated', 'imp. & p. p.', 'of Postulate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postulating', 'p. pr. & vb. n.', 'of Postulate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postulate', 'v. t.', 'To beg, or assume without proof; as, to postulate\n   conclusions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postulate', 'v. t.', 'To take without express consent; to assume.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postulate', 'v. t.', 'To invite earnestly; to solicit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postulated', 'a.', 'Assumed without proof; as, a postulated inference.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postulation', 'n.', 'The act of postulating, or that which is postulated;\n   assumption; solicitation; suit; cause.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postulatory', 'a.', 'Of the nature of a postulate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postulata', 'pl. ', 'of Postulatum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postulatum', 'n.', 'A postulate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postumous', 'a.', 'See Posthumous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postural', 'a.', 'Of or pertaining to posture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Posture', 'n.', 'The position of the body; the situation or disposition of\n   the several parts of the body with respect to each other, or for a\n   particular purpose; especially (Fine Arts), the position of a figure\n   with regard to the several principal members by which action is\n   expressed; attitude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Posture', 'n.', 'Place; position; situation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Posture', 'n.', 'State or condition, whether of external circumstances, or\n   of internal feeling and will; disposition; mood; as, a posture of\n   defense; the posture of affairs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postured', 'imp. & p. p.', 'of Posture');
  INSERT INTO words (word, word_type, description) VALUES  (    'Posturing', 'p. pr. & vb. n.', 'of Posture');
  INSERT INTO words (word, word_type, description) VALUES  (    'Posture', 'v. t.', 'To place in a particular position or attitude; to\n   dispose the parts of, with reference to a particular purpose; as, to\n   posture ones self; to posture a model.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Posture', 'v. i.', 'To assume a particular posture or attitude; to contort\n   the body into artificial attitudes, as an acrobat or contortionist;\n   also, to pose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Posture', 'v. i.', 'Fig.: To assume a character; as, to posture as a saint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Posturer', 'n.', 'One who postures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postzygapophyses', 'pl. ', 'of Postzygapophysis');
  INSERT INTO words (word, word_type, description) VALUES  (    'Postzygapophysis', 'n.', 'A posterior zygapophysis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Posies', 'pl. ', 'of Posy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Posy', 'n.', 'A brief poetical sentiment; hence, any brief sentiment,\n   motto, or legend; especially, one inscribed on a ring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Posy', 'n.', 'A flower; a bouquet; a nosegay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pot', 'n.', 'A metallic or earthen vessel, appropriated to any of a great\n   variety of uses, as for boiling meat or vegetables, for holding\n   liquids, for plants, etc.; as, a quart pot; a flower pot; a bean pot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pot', 'n.', 'An earthen or pewter cup for liquors; a mug.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pot', 'n.', 'The quantity contained in a pot; a potful; as, a pot of ale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pot', 'n.', 'A metal or earthenware extension of a flue above the top of a\n   chimney; a chimney pot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pot', 'n.', 'A crucible; as, a graphite pot; a melting pot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pot', 'n.', 'A wicker vessel for catching fish, eels, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pot', 'n.', 'A perforated cask for draining sugar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pot', 'n.', 'A size of paper. See Pott.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potted', 'imp. & p. p.', 'of Pot');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potting', 'p. pr. & vb. n.', 'of Pot');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pot', 'v. t.', 'To place or inclose in pots');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pot', 'v. t.', 'To preserve seasoned in pots.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pot', 'v. t.', 'To set out or cover in pots; as, potted plants or bulbs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pot', 'v. t.', 'To drain; as, to pot sugar, by taking it from the cooler,\n   and placing it in hogsheads, etc., having perforated heads, through\n   which the molasses drains off.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pot', 'v. t.', 'To pocket.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pot', 'v. i.', 'To tipple; to drink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potable', 'a.', 'Fit to be drunk; drinkable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potable', 'n.', 'A potable liquid; a beverage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potableness', 'n.', 'The quality of being drinkable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potage', 'n.', 'See Pottage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potager', 'n.', 'A porringer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potagro', 'n.', 'See Potargo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potale', 'n.', 'The refuse from a grain distillery, used to fatten swine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potamian', 'n.', 'A river tortoise; one of a group of tortoises (Potamites,\n   or Trionychoidea) having a soft shell, webbed feet, and a sharp beak.\n   See Trionyx.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potamography', 'n.', 'An account or description of rivers; potamology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potamology', 'n.', 'A scientific account or discussion of rivers; a\n   treatise on rivers; potamography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potamospongiae', 'n. pl.', 'The fresh-water sponges. See Spongilla.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potance', 'n.', 'The stud in which the bearing for the lower pivot of the\n   verge is made.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potargo', 'n.', 'A kind of sauce or pickle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potash', 'n.', 'The hydroxide of potassium hydrate, a hard white brittle\n   substance, KOH, having strong caustic and alkaline properties; -- hence\n   called also caustic potash.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potash', 'n.', 'The impure potassium carbonate obtained by leaching wood\n   ashes, either as a strong solution (lye), or as a white crystalline\n   (pearlash).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potashes', 'n. pl.', 'Potash.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potassa', 'n.', 'Potassium oxide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potassa', 'n.', 'Potassium hydroxide, commonly called caustic potash.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potassamide', 'n.', 'A yellowish brown substance obtained by heating\n   potassium in ammonia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pottassic', 'a.', 'Pertaining to, or containing, potassium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potassium', 'n.', 'An Alkali element, occurring abundantly but always\n   combined, as in the chloride, sulphate, carbonate, or silicate, in the\n   minerals sylvite, kainite, orthoclase, muscovite, etc. Atomic weight\n   39.0. Symbol K (Kalium).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potassoxyl', 'n.', 'The radical KO, derived from, and supposed to exist in,\n   potassium hydroxide and other compounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potation', 'n.', 'The act of drinking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potation', 'n.', 'A draught.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potation', 'n.', 'Drink; beverage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potatoes', 'pl. ', 'of Potato');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potato', 'n.', 'A plant (Solanum tuberosum) of the Nightshade family, and\n   its esculent farinaceous tuber, of which there are numerous varieties\n   used for food. It is native of South America, but a form of the species\n   is found native as far north as New Mexico.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potato', 'n.', 'The sweet potato (see below).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potator', 'n.', 'A drinker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potatory', 'a.', 'Of or pertaining to drinking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pot-bellied', 'a.', 'Having a protuberant belly, like the bottom of a pot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pot-belly', 'n.', 'A protuberant belly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potboiler', 'n.', 'A term applied derisively to any literary or artistic\n   work, and esp. a painting, done simply for money and the means of\n   living.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potboy', 'n.', 'A boy who carries pots of ale, beer, etc.; a menial in a\n   public house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potch', 'v. i.', 'To thrust; to push.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potch', 'v. t.', 'See Poach, to cook.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potcher', 'n.', 'One who, or that which, potches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potecary', 'n.', 'An apothecary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poteen', 'n.', 'Whisky; especially, whisky illicitly distilled by the Irish\n   peasantry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potelot', 'n.', 'Molybdenum sulphide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potence', 'n.', 'Potency; capacity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potency', 'n.', 'The quality or state of being potent; physical or moral\n   power; inherent strength; energy; ability to effect a purpose;\n   capability; efficacy; influence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potent', 'a.', 'Producing great physical effects; forcible; powerful\n   efficacious; as, a potent medicine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potent', 'a.', 'Having great authority, control, or dominion; puissant;\n   mighty; influential; as, a potent prince.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potent', 'a.', 'Powerful, in an intellectual or moral sense; having great\n   influence; as, potent interest; a potent argument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potent', 'n.', 'A prince; a potentate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potent', 'n.', 'A staff or crutch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potent', 'n.', 'One of the furs; a surface composed of patches which are\n   supposed to represent crutch heads; they are always alternately argent\n   and azure, unless otherwise specially mentioned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potentacy', 'n.', 'Sovereignty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potentate', 'a.', 'One who is potent; one who possesses great power or\n   sway; a prince, sovereign, or monarch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potential', 'a.', 'Being potent; endowed with energy adequate to a result;\n   efficacious; influential.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potential', 'a.', 'Existing in possibility, not in actuality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potential', 'n.', 'Anything that may be possible; a possibility;\n   potentially.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potential', 'n.', 'In the theory of gravitation, or of other forces acting\n   in space, a function of the rectangular coordinates which determine the\n   position of a point, such that its differential coefficients with\n   respect to the coordinates are equal to the components of the force at\n   the point considered; -- also called potential function, or force\n   function. It is called also Newtonian potential when the force is\n   directed to a fixed center and is inversely as the square of the\n   distance from the center.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potential', 'n.', 'The energy of an electrical charge measured by its power\n   to do work; hence, the degree of electrification as referred to some\n   standard, as that of the earth; electro-motive force.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potentiality', 'n.', 'The quality or state of being potential; possibility,\n   not actuality; inherent capability or disposition, not actually\n   exhibited.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potentially', 'adv.', 'With power; potently.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potentially', 'adv.', 'In a potential manner; possibly, not positively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potentiated', 'imp. & p. p.', 'of Potentiate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potentiating', 'p. pr. & vb. n.', 'of Potentiate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potentiate', 'v. t.', 'To render active or potent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potentiometer', 'n.', 'An instrument for measuring or comparing electrial\n   potentials or electro-motive forces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potentized', 'imp. & p. p.', 'of Potentize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potentizing', 'p. pr. & vb. n.', 'of Potentize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potentize', 'v. t.', 'To render the latent power of (anything) available.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potently', 'adv.', 'With great force or energy; powerfully; efficaciously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potentness', 'n.', 'The quality or state of being potent; powerfulness;\n   potency; efficacy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potestate', 'n.', 'A chief ruler; a potentate. [Obs.] Wyclif.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potestative', 'a.', 'Authoritative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potgun', 'n.', 'A pot-shaped cannon; a mortar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potgun', 'n.', 'A popgun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pothecary', 'n.', 'An apothecary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potheen', 'n.', 'See Poteen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pother', 'n.', 'Bustle; confusion; tumult; flutter; bother.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pother', 'v. i.', 'To make a bustle or stir; to be fussy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pothered', 'imp. & p. p.', 'of Pother');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pothering', 'p. pr. & vb. n.', 'of Pother');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pother', 'v. t.', 'To harass and perplex; to worry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pothole', 'n.', 'A circular hole formed in the rocky beds of rivers by the\n   grinding action of stones or gravel whirled round by the water in what\n   was at first a natural depression of the rock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pothook', 'n.', 'An S-shaped hook on which pots and kettles are hung over\n   an open fire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pothook', 'n.', 'A written character curved like a pothook; (pl.) a\n   scrawled writing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pothouse', 'n.', 'An alehouse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potichomania', 'n.', 'Alt. of Potichomanie');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potichomanie', 'n.', 'The art or process of coating the inside of glass\n   vessels with engravings or paintings, so as to give them the appearance\n   of painted ware.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potion', 'n.', 'A draught; a dose; usually, a draught or dose of a liquid\n   medicine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potion', 'v. t.', 'To drug.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potlid', 'n.', 'The lid or cover of a pot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potluck', 'n.', 'Whatever may chance to be in the pot, or may be provided\n   for a meal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potmen', 'pl. ', 'of Potman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potman', 'n.', 'A pot companion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potman', 'n.', 'A servant in a public house; a potboy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potoo', 'n.', 'A large South American goatsucker (Nyctibius grandis).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potoroo', 'n.', 'Any small kangaroo belonging to Hypsiprymnus, Bettongia,\n   and allied genera, native of Australia and Tasmania. Called also\n   kangaroo rat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potpie', 'n.', 'A meat pie which is boiled instead of being baked.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potpourri', 'n.', 'A medley or mixture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potpourri', 'n.', 'A ragout composed of different sorts of meats,\n   vegetables, etc., cooked together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potpourri', 'n.', 'A jar or packet of flower leaves, perfumes, and spices,\n   used to scent a room.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potpourri', 'n.', 'A piece of music made up of different airs strung\n   together; a medley.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potpourri', 'n.', 'A literary production composed of parts brought together\n   without order or bond of connection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potsdam group', '', 'A subdivision of the Primordial or Cambrian period in\n   American geology; -- so named from the sandstone of Potsdam, New York.\n   See Chart of Geology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potshard', 'n.', 'Alt. of Potshare');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potshare', 'n.', 'A potsherd.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potsherd', 'n.', 'A piece or fragment of a broken pot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potstone', 'n.', 'A variety of steatite sometimes manufactured into\n   culinary vessels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pot-sure', 'a.', 'Made confident by drink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pott', 'n.', 'A size of paper. See under Paper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pottage', 'n.', 'A kind of food made by boiling vegetables or meat, or both\n   together, in water, until soft; a thick soup or porridge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pottain', 'n.', 'Old pot metal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potteen', 'n.', 'See Poteen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potter', 'n.', 'One whose occupation is to make earthen vessels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potter', 'n.', 'One who hawks crockery or earthenware.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potter', 'n.', 'One who pots meats or other eatables.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potter', 'n.', 'The red-bellied terrapin. See Terrapin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pottered', 'imp. & p. p.', 'of Potter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pottering', 'p. pr. & vb. n.', 'of Potter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potter', 'v. i.', 'To busy ones self with trifles; to labor with little\n   purpose, energy, of effect; to trifle; to pother.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potter', 'v. i.', 'To walk lazily or idly; to saunter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potter', 'v. t.', 'To poke; to push; also, to disturb; to confuse; to\n   bother.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pottern', 'a.', 'Of or pertaining to potters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potteries', 'pl. ', 'of Pottery');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pottery', 'n.', 'The vessels or ware made by potters; earthenware, glazed\n   and baked.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pottery', 'n.', 'The place where earthen vessels are made.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potting', 'n.', 'Tippling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potting', 'n.', 'The act of placing in a pot; as, the potting of plants;\n   the potting of meats for preservation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potting', 'n.', 'The process of putting sugar in casks for cleansing and\n   draining.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pottle', 'n.', 'A liquid measure of four pints.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pottle', 'n.', 'A pot or tankard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pottle', 'n.', 'A vessel or small basket for holding fruit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potto', 'n.', 'A nocturnal mammal (Perodictius potto) of the Lemur family,\n   found in West Africa. It has rudimentary forefingers. Called also\n   aposoro, and bush dog.');
