  INSERT INTO words (word, word_type, description) VALUES  (    'Potts disease', '', 'Caries of the vertebrae, frequently resulting in\n   curvature of the spine and paralysis of the lower extremities; -- so\n   named from Percival Pott, an English surgeon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potulent', 'a.', 'Fit to drink; potable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Potulent', 'a.', 'Nearly drunk; tipsy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pot-valiant', 'a.', 'Having the courage given by drink.');
