  INSERT INTO words (word, word_type, description) VALUES  (    'Pot-walloper', 'n.', 'One who cleans pots; a scullion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pouch', 'n.', 'A small bag; usually, a leathern bag; as, a pouch for money;\n   a shot pouch; a mail pouch, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pouch', 'n.', 'That which is shaped like, or used as, a pouch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pouch', 'n.', 'A protuberant belly; a paunch; -- so called in ridicule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pouch', 'n.', 'A sac or bag for carrying food or young; as, the cheek\n   pouches of certain rodents, and the pouch of marsupials.');
