  INSERT INTO words (word, word_type, description) VALUES  (    'Pouching', 'p. pr. & vb. n.', 'of Pouch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pouch', 'v. t.', 'To put or take into a pouch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pouch', 'v. t.', 'To swallow; -- said of fowls.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pouch', 'v. t.', 'To pout.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pouch', 'v. t.', 'To pocket; to put up with.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pouched', 'a.', 'Having a marsupial pouch; as, the pouched badger, or the\n   wombat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pouched', 'a.', 'Having external cheek pouches; as, the pouched gopher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pouched', 'a.', 'Having internal cheek pouches; as, the pouched squirrels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pouchet box', '', 'See Pouncet box.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pouch-mouthed', 'a.', 'Having a pouch mouth; blobber-lipped.');
