  INSERT INTO words (word, word_type, description) VALUES  (    'Poudre', 'n.', 'Dust; powder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poudrette', 'n.', 'A manure made from night soil, dried and mixed with\n   charcoal, gypsum, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poulaine', 'n.', 'A long pointed shoe. See Cracowes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pouldavis', 'n.', 'Same as Poledavy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poulder', 'n. & v.', 'Powder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pouldron', 'n.', 'See Pauldron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poulp', 'n.', 'Alt. of Poulpe');
