  INSERT INTO words (word, word_type, description) VALUES  (    'Pounce', 'v. t.', 'To punch; to perforate; to stamp holes in, or dots on,\n   by way of ornament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pounce', 'v. i.', 'To fall suddenly and seize with the claws; -- with on or\n   upon; as, a hawk pounces upon a chicken. Also used figuratively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pounced', 'a.', 'Furnished with claws or talons; as, the pounced young of\n   the eagle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pounced', 'a.', 'Ornamented with perforations or dots.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pouncet box', '', 'A box with a perforated lid, for sprinkling pounce, or\n   for holding perfumes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pouncing', 'n.', 'The art or practice of transferring a design by means of\n   pounce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pouncing', 'n.', 'Decorative perforation of cloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pounded', 'imp. & p. p.', 'of Pound');
