  INSERT INTO words (word, word_type, description) VALUES  (    'Pound', 'v. t.', 'To strike repeatedly with some heavy instrument; to beat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pound', 'v. t.', 'To comminute and pulverize by beating; to bruise or break\n   into fine particles with a pestle or other heavy instrument; as, to\n   pound spice or salt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pound', 'v. i.', 'To strike heavy blows; to beat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pound', 'v. i.', 'To make a jarring noise, as in running; as, the engine\n   pounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pound', 'n.', 'An inclosure, maintained by public authority, in which\n   cattle or other animals are confined when taken in trespassing, or when\n   going at large in violation of law; a pinfold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pound', 'n.', 'A level stretch in a canal between locks.');
