  INSERT INTO words (word, word_type, description) VALUES  (    'Pound', 'pl. ', 'of Pound');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pounds', 'pl. ', 'of Pound');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pound', 'n.', 'A certain specified weight; especially, a legal standard\n   consisting of an established number of ounces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pound', 'n.', 'A British denomination of money of account, equivalent to\n   twenty shillings sterling, and equal in value to about $4.86. There is\n   no coin known by this name, but the gold sovereign is of the same\n   value.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poundage', 'n.', 'A sum deducted from a pound, or a certain sum paid for\n   each pound; a commission.');
