  INSERT INTO words (word, word_type, description) VALUES  (    'Poundage', 'n.', 'The sum allowed to a sheriff or other officer upon the\n   amount realized by an execution; -- estimated in England, and formerly\n   in the United States, at so much of the pound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poundage', 'v. t.', 'To collect, as poundage; to assess, or rate, by\n   poundage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poundage', 'n.', 'Confinement of cattle, or other animals, in a public\n   pound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poundage', 'n.', 'A charge paid for the release of impounded cattle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poundal', 'n.', 'A unit of force based upon the pound, foot, and second,\n   being the force which, acting on a pound avoirdupois for one second,\n   causes it to acquire by the of that time a velocity of one foot per\n   second. It is about equal to the weight of half an ounce, and is 13,825\n   dynes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pound-breach', 'n.', 'The breaking of a public pound for releasing\n   impounded animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poundcake', 'n.', 'A kind of rich, sweet cake; -- so called from the\n   ingredients being used by pounds, or in equal quantities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pounder', 'n.', 'One who, or that which, pounds, as a stamp in an ore mill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pounder', 'n.', 'An instrument used for pounding; a pestle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pounder', 'n.', 'A person or thing, so called with reference to a certain\n   number of pounds in value, weight, capacity, etc.; as, a cannon\n   carrying a twelve-pound ball is called a twelve pounder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pounding', 'n.', 'The act of beating, bruising, or breaking up; a beating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pounding', 'n.', 'A pounded or pulverized substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pound/keeper', 'n.', 'The keeper of a pound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poundrate', 'n.', 'A rate or proportion estimated at a certain amount for\n   each pound; poundage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poup', 'v. i.', 'See Powp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pouparts ligament', '', 'A ligament, of fascia, extending, in most\n   mammals, from the ventral side of the ilium to near the symphysis of\n   the pubic bones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poupeton', 'n.', 'A puppet, or little baby.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pour', 'a.', 'Poor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pour', 'v. i.', 'To pore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poured', 'imp. & p. p.', 'of Pour');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pouring', 'p. pr. & vb. n.', 'of Pour');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pour', 'v. t.', 'To cause to flow in a stream, as a liquid or anything\n   flowing like a liquid, either out of a vessel or into it; as, to pour\n   water from a pail; to pour wine into a decanter; to pour oil upon the\n   waters; to pour out sand or dust.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pour', 'v. t.', 'To send forth as in a stream or a flood; to emit; to let\n   escape freely or wholly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pour', 'v. t.', 'To send forth from, as in a stream; to discharge\n   uninterruptedly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pour', 'v. i.', 'To flow, pass, or issue in a stream, or as a stream; to\n   fall continuously and abundantly; as, the rain pours; the people poured\n   out of the theater.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pour', 'n.', 'A stream, or something like a stream; a flood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poureliche', 'adv.', 'Poorly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pourer', 'n.', 'One who pours.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pourlieu', 'n.', 'See Purlieu.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pourparler', 'n.', 'A consultation preliminary to a treaty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pourparties', 'pl. ', 'of Pourparty');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pourparty', 'n.', 'A division; a divided share.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pourpoint', 'n.', 'A quilted military doublet or gambeson worn in the 14th\n   and 15th centuries; also, a name for the doublet of the 16th and 17th\n   centuries worn by civilians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pourpresture', 'n.', 'See Purpresture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poursuivant', 'n.', 'See Pursuivant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pourtray', 'v. t.', 'See Portray.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pourveyance', 'n.', 'See Purveyance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pousse', 'n.', 'Pulse; pease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poussette', 'n.', 'A movement, or part of a figure, in the contradance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poussette', 'v. i.', 'To perform a certain movement in a dance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pout', 'n.', 'The young of some birds, as grouse; a young fowl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pout', 'v. i.', 'To shoot pouts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pouted', 'imp. & p. p.', 'of Pout');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pouting', 'p. pr. & vb. n.', 'of Pout');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pout', 'v. i.', 'To thrust out the lips, as in sullenness or displeasure;\n   hence, to look sullen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pout', 'v. i.', 'To protrude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pout', 'n.', 'A sullen protrusion of the lips; a fit of sullenness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pout', 'n.', 'The European whiting pout or bib.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pouter', 'n.', 'One who, or that which, pouts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pouter', 'n.', 'A variety of the domestic pigeon remarkable for the extent\n   to which it is able to dilate its throat and breast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pouting', 'n.', 'Childish sullenness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poutingly', 'adv.', 'In a pouting, or a sullen, manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Povert', 'n.', 'Poverty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poverty', 'n.', 'The quality or state of being poor or indigent; want or\n   scarcity of means of subsistence; indigence; need.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poverty', 'n.', 'Any deficiency of elements or resources that are needed or\n   desired, or that constitute richness; as, poverty of soil; poverty of\n   the blood; poverty of ideas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Powan', 'n.', 'Alt. of Powen');
  INSERT INTO words (word, word_type, description) VALUES  (    'Powen', 'n.', 'A small British lake whitefish (Coregonus clupeoides, or C.\n   ferus); -- called also gwyniad and lake herring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Powder', 'n.', 'The fine particles to which any dry substance is reduced by\n   pounding, grinding, or triturating, or into which it falls by decay;\n   dust.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Powder', 'n.', 'An explosive mixture used in gunnery, blasting, etc.;\n   gunpowder. See Gunpowder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Powdered', 'imp. & p. p.', 'of Powder');
  INSERT INTO words (word, word_type, description) VALUES  (    'Powdering', 'p. pr. & vb. n.', 'of Powder');
  INSERT INTO words (word, word_type, description) VALUES  (    'Powder', 'v. t.', 'To reduce to fine particles; to pound, grind, or rub\n   into a powder; to comminute; to pulverize; to triturate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Powder', 'v. t.', 'To sprinkle with powder, or as with powder; to be\n   sprinkle; as, to powder the hair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Powder', 'v. t.', 'To sprinkle with salt; to corn, as meat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Powder', 'v. i.', 'To be reduced to powder; to become like powder; as, some\n   salts powder easily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Powder', 'v. i.', 'To use powder on the hair or skin; as, she paints and\n   powders.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Powdered', 'a.', 'Reduced to a powder; sprinkled with, or as with, powder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Powdered', 'a.', 'Sprinkled with salt; salted; corned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Powdered', 'a.', 'Same as Seme.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Powderflask', 'n.', 'A flask in which gunpowder is carried, having a\n   charging tube at the end.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Powderhorn', 'n.', 'A horn in which gunpowder is carried.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Powdering', '', 'a. & n. from Powder, v. t.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Powdermill', 'n.', 'A mill in which gunpowder is made.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Powder-posted', 'a.', 'Affected with dry rot; reduced to dust by rot. See\n   Dry rot, under Dry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Powdery', 'a.', 'Easily crumbling to pieces; friable; loose; as, a powdery\n   spar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Powdery', 'a.', 'Sprinkled or covered with powder; dusty; as, the powdery\n   bloom on plums.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Powdery', 'a.', 'Resembling powder; consisting of powder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Powdike', 'n.', 'A dike a marsh or fen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Powdry', 'a.', 'See Powdery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Power', 'n.', 'Same as Poor, the fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Power', 'n.', 'Ability to act, regarded as latent or inherent; the faculty\n   of doing or performing something; capacity for action or performance;\n   capability of producing an effect, whether physical or moral: potency;\n   might; as, a man of great power; the power of capillary attraction;\n   money gives power.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Power', 'n.', 'Ability, regarded as put forth or exerted; strength, force,\n   or energy in action; as, the power of steam in moving an engine; the\n   power of truth, or of argument, in producing conviction; the power of\n   enthusiasm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Power', 'n.', 'Capacity of undergoing or suffering; fitness to be acted\n   upon; susceptibility; -- called also passive power; as, great power of\n   endurance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Power', 'n.', 'The exercise of a faculty; the employment of strength; the\n   exercise of any kind of control; influence; dominion; sway; command;\n   government.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Power', 'n.', 'The agent exercising an ability to act; an individual\n   invested with authority; an institution, or government, which exercises\n   control; as, the great powers of Europe; hence, often, a superhuman\n   agent; a spirit; a divinity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Power', 'n.', 'A military or naval force; an army or navy; a great host.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Power', 'n.', 'A large quantity; a great number; as, a power o/ good\n   things.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Power', 'n.', 'The rate at which mechanical energy is exerted or mechanical\n   work performed, as by an engine or other machine, or an animal, working\n   continuously; as, an engine of twenty horse power.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Power', 'n.', 'A mechanical agent; that from which useful mechanical energy\n   is derived; as, water power; steam power; hand power, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Power', 'n.', 'Applied force; force producing motion or pressure; as, the\n   power applied at one and of a lever to lift a weight at the other end.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Power', 'n.', 'A machine acted upon by an animal, and serving as a motor to\n   drive other machinery; as, a dog power.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Power', 'n.', 'The product arising from the multiplication of a number into\n   itself; as, a square is the second power, and a cube is third power, of\n   a number.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Power', 'n.', 'Mental or moral ability to act; one of the faculties which\n   are possessed by the mind or soul; as, the power of thinking,\n   reasoning, judging, willing, fearing, hoping, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Power', 'n.', 'The degree to which a lens, mirror, or any optical\n   instrument, magnifies; in the telescope, and usually in the microscope,\n   the number of times it multiplies, or augments, the apparent diameter\n   of an object; sometimes, in microscopes, the number of times it\n   multiplies the apparent surface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Power', 'n.', 'An authority enabling a person to dispose of an interest\n   vested either in himself or in another person; ownership by\n   appointment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Power', 'n.', 'Hence, vested authority to act in a given case; as, the\n   business was referred to a committee with power.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Powerable', 'a.', 'Capable of being effected or accomplished by the\n   application of power; possible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Powerable', 'a.', 'Capable of exerting power; powerful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Powerful', 'a.', 'Full of power; capable of producing great effects of any\n   kind; potent; mighty; efficacious; intense; as, a powerful man or\n   beast; a powerful engine; a powerful argument; a powerful light; a\n   powerful vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Powerful', 'a.', 'Large; capacious; -- said of veins of ore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Powerless', 'a.', 'Destitute of power, force, or energy; weak; impotent;\n   not able to produce any effect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Powldron', 'n.', 'Same as Pauldron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Powp', 'v. i.', 'See Poop, v. i.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Powter', 'n.', 'See Pouter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Powpow', 'n.', 'A priest, or conjurer, among the North American Indians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Powpow', 'n.', 'Conjuration attended with great noise and confusion, and\n   often with feasting, dancing, etc., performed by Indians for the cure\n   of diseases, to procure success in hunting or in war, and for other\n   purposes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Powpow', 'n.', 'Hence: Any assembly characterized by noise and confusion; a\n   noisy frolic or gathering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Powwow', 'v. i.', 'To use conjuration, with noise and confusion, for the\n   cure of disease, etc., as among the North American Indians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Powwow', 'v. i.', 'Hence: To hold a noisy, disorderly meeting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pox', 'n.', 'Strictly, a disease by pustules or eruptions of any kind, but\n   chiefly or wholly restricted to three or four diseases, -- the\n   smallpox, the chicken pox, and the vaccine and the venereal diseases.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poxed', 'imp. & p. p.', 'of Pox');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poxing', 'p. pr. & vb. n.', 'of Pox');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pox', 'v. t.', 'To infect with the pox, or syphilis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poy', 'n.', 'A support; -- used in composition; as, teapoy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poy', 'n.', 'A ropedancers balancing pole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poy', 'n.', 'A long boat hook by which barges are propelled against the\n   stream.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poynado', 'n.', 'A poniard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poynd', 'n.', 'Alt. of Poynder');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poynder', 'n.', 'See Poind, Poinder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poy nette', 'n.', 'A bodkin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poyntel', 'n.', 'Paving or flooring made of small squares or lozenges set\n   diagonally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poyou', 'n.', 'A South American armadillo (Dasypus sexcinctus). Called also\n   sixbanded armadillo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Poze', 'v. t.', 'See 5th Pose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pozzuolana', 'n.', 'Alt. of Pozzolana');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pozzolana', 'n.', 'Volcanic ashes from Pozzuoli, in Italy, used in the\n   manufacture of a kind of mortar which hardens under water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Praam', 'n.', 'A flat-bottomed boat or lighter, -- used in Holland and the\n   Baltic, and sometimes armed in case of war.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Practic', 'a.', 'Practical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Practic', 'a.', 'Artful; deceitful; skillful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Practicability', 'n.', 'The quality or state of being practicable;\n   practicableness; feasibility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Practicable', 'a.', 'That may be practiced or performed; capable of being\n   done or accomplished with available means or resources; feasible; as, a\n   practicable method; a practicable aim; a practicable good.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Practicable', 'a.', 'Capable of being used; passable; as, a practicable\n   weapon; a practicable road.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Practical', 'a.', 'Of or pertaining to practice or action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Practical', 'a.', 'Capable of being turned to use or account; useful, in\n   distinction from ideal or theoretical; as, practical chemistry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Practical', 'a.', 'Evincing practice or skill; capable of applying\n   knowledge to some useful end; as, a practical man; a practical mind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Practical', 'a.', 'Derived from practice; as, practical skill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Practicality', 'n.', 'The quality or state of being practical;\n   practicalness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Practically', 'adv.', 'In a practical way; not theoretically; really; as,\n   to look at things practically; practically worthless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Practically', 'adv.', 'By means of practice or use; by experience or\n   experiment; as, practically wise or skillful; practically acquainted\n   with a subject.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Practically', 'adv.', 'In practice or use; as, a medicine practically safe;\n   theoretically wrong, but practically right.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Practically', 'adv.', 'Almost.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Practicalness', 'n.', 'Same as Practicality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Practicalize', 'v. t.', 'To render practical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Practice', 'n.', 'Frequently repeated or customary action; habitual\n   performance; a succession of acts of a similar kind; usage; habit;\n   custom; as, the practice of rising early; the practice of making\n   regular entries of accounts; the practice of daily exercise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Practice', 'n.', 'Customary or constant use; state of being used.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Practice', 'n.', 'Skill or dexterity acquired by use; expertness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Practice', 'n.', 'Actual performance; application of knowledge; -- opposed\n   to theory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Practice', 'n.', 'Systematic exercise for instruction or discipline; as,\n   the troops are called out for practice; she neglected practice in\n   music.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Practice', 'n.', 'Application of science to the wants of men; the exercise\n   of any profession; professional business; as, the practice of medicine\n   or law; a large or lucrative practice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Practice', 'n.', 'Skillful or artful management; dexterity in contrivance\n   or the use of means; art; stratagem; artifice; plot; -- usually in a\n   bad sense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Practice', 'n.', 'A easy and concise method of applying the rules of\n   arithmetic to questions which occur in trade and business.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Practice', 'n.', 'The form, manner, and order of conducting and carrying on\n   suits and prosecutions through their various stages, according to the\n   principles of law and the rules laid down by the courts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Practiced', 'imp. & p. p.', 'of Practice');
  INSERT INTO words (word, word_type, description) VALUES  (    'Practicing', 'p. pr. & vb. n.', 'of Practice');
  INSERT INTO words (word, word_type, description) VALUES  (    'Practice', 'v. t.', 'To do or perform frequently, customarily, or\n   habitually; to make a practice of; as, to practice gaming.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Practice', 'v. t.', 'To exercise, or follow, as a profession, trade, art,\n   etc., as, to practice law or medicine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Practice', 'v. t.', 'To exercise ones self in, for instruction or\n   improvement, or to acquire discipline or dexterity; as, to practice\n   gunnery; to practice music.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Practice', 'v. t.', 'To put into practice; to carry out; to act upon; to\n   commit; to execute; to do.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Practice', 'v. t.', 'To make use of; to employ.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Practice', 'v. t.', 'To teach or accustom by practice; to train.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Practice', 'v. i.', 'To perform certain acts frequently or customarily,\n   either for instruction, profit, or amusement; as, to practice with the\n   broadsword or with the rifle; to practice on the piano.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Practice', 'v. i.', 'To learn by practice; to form a habit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Practice', 'v. i.', 'To try artifices or stratagems.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Practice', 'v. i.', 'To apply theoretical science or knowledge, esp. by way\n   of experiment; to exercise or pursue an employment or profession, esp.\n   that of medicine or of law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Practiced', 'a.', 'Experienced; expert; skilled; as, a practiced marksman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Practiced', 'a.', 'Used habitually; learned by practice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Practicer', 'n.', 'One who practices, or puts in practice; one who\n   customarily performs certain acts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Practicer', 'n.', 'One who exercises a profession; a practitioner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Practicer', 'n.', 'One who uses art or stratagem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Practician', 'n.', 'One who is acquainted with, or skilled in, anything by\n   practice; a practitioner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Practick', 'n.', 'Practice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Practisant', 'n.', 'An agent or confederate in treachery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Practise', 'v. t. & i.', 'See Practice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Practisour', 'n.', 'A practitioner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Practitioner', 'n.', 'One who is engaged in the actual use or exercise of\n   any art or profession, particularly that of law or medicine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Practitioner', 'n.', 'One who does anything customarily or habitually.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Practitioner', 'n.', 'A sly or artful person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Practive', 'a.', 'Doing; active.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prad', 'n.', 'A horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prae-', '', 'A prefix. See Pre-.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Praecava', 'n.', 'The superior vena cava.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Praecipe', 'n.', 'A writ commanding something to be done, or requiring a\n   reason for neglecting it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Praecipe', 'n.', 'A paper containing the particulars of a writ, lodged in\n   the office out of which the writ is to be issued.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Praecoces', 'n. pl.', 'A division of birds including those whose young are\n   able to run about when first hatched.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Praecocial', 'a.', 'Of or pertaining to the Praecoces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Praecognita', 'n. pl.', 'This previously known, or which should be known in\n   order to understand something else.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Praecommissure', 'n.', 'A transverse commissure in the anterior part of the\n   third ventricle of the brain; the anterior cerebral commissure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Praecoracoid', 'n.', 'See Precoracoid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Praecordia', 'n.', 'The front part of the thoracic region; the epigastrium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Praecordial', 'a.', 'Same as Precordial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Praecornua', 'pl. ', 'of Praecornu');
  INSERT INTO words (word, word_type, description) VALUES  (    'Praecornu', 'n.', 'The anterior horn of each lateral ventricle of the\n   brain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Praedial', 'a.', 'See Predial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Praefloration', 'n.', 'Same as Prefloration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Praefoliation', 'n.', 'Same as Prefoliation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Praemaxilla', 'n.', 'See Premaxilla.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Praemolar', 'a.', 'See Premolar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Praemorse', 'a.', 'Same as Premorse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Praemunire', 'n.', 'The offense of introducing foreign authority into\n   England, the penalties for which were originally intended to depress\n   the civil power of the pope in the kingdom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Praemunire', 'n.', 'The writ grounded on that offense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Praemunire', 'n.', 'The penalty ascribed for the offense of praemunire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Praemnire', 'v. t.', 'The subject to the penalties of praemunire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Praemunitory', 'a.', 'See Premunitory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Praenares', 'n. pl.', 'The anterior nares. See Nares.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Praenasal', 'a.', 'Same as Prenasal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Praenomina', 'pl. ', 'of Praenomen');
  INSERT INTO words (word, word_type, description) VALUES  (    'Praenomen', 'n.', 'The first name of a person, by which individuals of the\n   same family were distinguished, answering to our Christian name, as\n   Caius, Lucius, Marcus, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Praenominical', 'a.', 'Of or pertaining to a praenomen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Praeoperculum', 'n.', 'Same as Preoperculum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Praesternum', 'n.', 'Same as Preoral, Prepubis, Prescapula, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Praeter-', '', 'A prefix. See Preter-.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Praeterist', 'n.', 'See Preterist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Praetermit', 'v. t.', 'See Pretermit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Praetextae', 'pl. ', 'of Praetexta');
  INSERT INTO words (word, word_type, description) VALUES  (    'Praetextas', 'pl. ', 'of Praetexta');
  INSERT INTO words (word, word_type, description) VALUES  (    'Praetexta', 'n.', 'A white robe with a purple border, worn by a Roman boy\n   before he was entitled to wear the toga virilis, or until about the\n   completion of his fourteenth year, and by girls until their marriage.\n   It was also worn by magistrates and priests.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Praetor', 'n.', 'See Pretor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Praetores', 'n. pl.', 'A division of butterflies including the satyrs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Praetorian', 'a.', 'See Pretorian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Praetorium', 'n.', 'See Pretorium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Praezygapophysis', 'n.', 'Same as Prezygapophysis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pragmatic', 'a.', 'Alt. of Pragmatical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pragmatical', 'a.', 'Of or pertaining to business or to affairs; of the\n   nature of business; practical; material; businesslike in habit or\n   manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pragmatical', 'a.', 'Busy; specifically, busy in an objectionable way;\n   officious; fussy and positive; meddlesome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pragmatical', 'a.', 'Philosophical; dealing with causes, reasons, and\n   effects, rather than with details and circumstances; -- said of\n   literature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pragmatic', 'n.', 'One skilled in affairs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pragmatic', 'n.', 'A solemn public ordinance or decree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pragmatically', 'adv.', 'In a pragmatical manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pragmaticalness', 'n.', 'The quality or state of being pragmatical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pragmatism', 'n.', 'The quality or state of being pragmatic; in literature,\n   the pragmatic, or philosophical, method.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pragmatist', 'n.', 'One who is pragmatic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pragmatize', 'v. t.', 'To consider, represent, or embody (something unreal)\n   as fact; to materialize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prairial', 'n.', 'The ninth month of the French Republican calendar, which\n   dated from September 22, 1792. It began May, 20, and ended June 18. See\n   Vendemiaire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prairie', 'n.', 'An extensive tract of level or rolling land, destitute of\n   trees, covered with coarse grass, and usually characterized by a deep,\n   fertile soil. They abound throughout the Mississippi valley, between\n   the Alleghanies and the Rocky mountains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prairie', 'n.', 'A meadow or tract of grass; especially, a so called\n   natural meadow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Praisable', 'a.', 'Fit to be praised; praise-worthy; laudable; commendable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Praisably', 'adv.', 'In a praisable manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Praised', 'imp. & p. p.', 'of Praise');
  INSERT INTO words (word, word_type, description) VALUES  (    'Praising', 'p. pr. & vb. n.', 'of Praise');
  INSERT INTO words (word, word_type, description) VALUES  (    'Praise', 'v.', 'To commend; to applaud; to express approbation of; to laud;\n   -- applied to a person or his acts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Praise', 'v.', 'To extol in words or song; to magnify; to glorify on\n   account of perfections or excellent works; to do honor to; to display\n   the excellence of; -- applied especially to the Divine Being.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Praise', 'v.', 'To value; to appraise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Praise', 'v.', 'Commendation for worth; approval expressed; honor rendered\n   because of excellence or worth; laudation; approbation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Praise', 'v.', 'Especially, the joyful tribute of gratitude or homage\n   rendered to the Divine Being; the act of glorifying or extolling the\n   Creator; worship, particularly worship by song, distinction from prayer\n   and other acts of worship; as, a service of praise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Praise', 'v.', 'The object, ground, or reason of praise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Praiseful', 'a.', 'Praiseworthy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Praiseful', 'a.', 'Praiseworthy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Praiseless', 'a.', 'Without praise or approbation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Praise-meeting', 'n.', 'A religious service mainly in song.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Praisement', 'n.', 'Appraisement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Praiseer', 'n.', 'One who praises.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Praiseer', 'n.', 'An appraiser; a valuator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Praiseworthily', 'adv.', 'In a praiseworthy manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Praiseworthiness', 'n.', 'The quality or state of being praiseworthy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Praiseworthy', 'a.', 'Worthy of praise or applause; commendable; as,\n   praiseworthy action; he was praiseworthy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prakrit', 'n.', 'Any one of the popular dialects descended from, or akin\n   to, Sanskrit; -- in distinction from the Sanskrit, which was used as a\n   literary and learned language when no longer spoken by the people. Pali\n   is one of the Prakrit dialects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prakritic', 'a.', 'Pertaining to Prakrit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pram', 'n.', 'Alt. of Prame');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prame', 'n.', 'See Praam.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pranced', 'imp. & p. p.', 'of Prance');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prancing', 'p. pr. & vb. n.', 'of Prance');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prance', 'v. i.', 'To spring or bound, as a horse in high mettle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prance', 'v. i.', 'To ride on a prancing horse; to ride in an ostentatious\n   manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prance', 'v. i.', 'To walk or strut about in a pompous, showy manner, or\n   with warlike parade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prancer', 'n.', 'A horse which prances.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prandial', 'a.', 'Of or pertaining to a repast, especially to dinner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prangos', 'n.', 'A genus of umbelliferous plants, one species of which (P.\n   pabularia), found in Thibet, Cashmere, Afghanistan, etc., has been used\n   as fodder for cattle. It has decompound leaves with very long narrow\n   divisions, and a highly fragrant smell resembling that of new clover\n   hay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pranked', 'imp. & p. p.', 'of Prank');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pranking', 'p. pr. & vb. n.', 'of Prank');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prank', 'v. t.', 'To adorn in a showy manner; to dress or equip\n   ostentatiously; -- often followed by up; as, to prank up the body. See\n   Prink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prank', 'v. i.', 'To make ostentatious show.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prank', 'n.', 'A gay or sportive action; a ludicrous, merry, or mischievous\n   trick; a caper; a frolic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prank', 'a.', 'Full of gambols or tricks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pranker', 'n.', 'One who dresses showily; a prinker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prankish', 'a.', 'Full of pranks; frolicsome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prase', 'n.', 'A variety of cryptocrystalline of a leek-green color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Praseo-', '', 'A combining form signifying green; as, praseocobalt, a green\n   variety of cobalt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Praseodymium', 'n.', 'An elementary substance, one of the constituents of\n   didymium; -- so called from the green color of its salts. Symbol Ps.\n   Atomic weight 143.6.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Praseolite', 'n.', 'A variety of altered iolite of a green color and greasy\n   luster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prasinous', 'a.', 'Grass-green; clear, lively green, without any mixture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prasoid', 'a.', 'Resembling prase.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prated', 'imp. & p. p.', 'of Prate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prating', 'p. pr. & vb. n.', 'of Prate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prate', 'v. i.', 'To talk much and to little purpose; to be loquacious; to\n   speak foolishly; to babble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prate', 'v. t.', 'To utter foolishly; to speak without reason or purpose;\n   to chatter, or babble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prate', 'n.', 'Talk to little purpose; trifling talk; unmeaning loquacity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prateful', 'a.', 'Talkative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prater', 'n.', 'One who prates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pratic', 'n.', 'See Pratique.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pratincole', 'n.', 'Any bird of the Old World genus Glareola, or family\n   Glareolidae, allied to the plovers. They have long, pointed wings and a\n   forked tail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pratingly', 'adv.', 'With idle talk; with loquacity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pratique', 'n.', 'Primarily, liberty of converse; intercourse; hence, a\n   certificate, given after compliance with quarantine regulations,\n   permitting a ship to land passengers and crew; -- a term used\n   particularly in the south of Europe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pratique', 'n.', 'Practice; habits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prattled', 'imp. & p. p.', 'of Prattle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prattling', 'p. pr. & vb. n.', 'of Prattle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prattle', 'v. i.', 'To talk much and idly; to prate; hence, to talk lightly\n   and artlessly, like a child; to utter childs talk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prattle', 'v. t.', 'To utter as prattle; to babble; as, to prattle treason.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prattle', 'n.', 'Trifling or childish tattle; empty talk; loquacity on\n   trivial subjects; prate; babble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prattlement', 'n.', 'Prattle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prattler', 'n.', 'One who prattles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pravity', 'n.', 'Deterioration; degeneracy; corruption; especially, moral\n   crookedness; moral perversion; perverseness; depravity; as, the pravity\n   of human nature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prawn', 'n.', 'Any one of numerous species of large shrimplike Crustacea\n   having slender legs and long antennae. They mostly belong to the genera\n   Pandalus, Palaemon, Palaemonetes, and Peneus, and are much used as\n   food. The common English prawn is Palaemon serratus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Praxinoscope', 'n.', 'An instrument, similar to the phenakistoscope, for\n   presenting to view, or projecting upon a screen, images the natural\n   motions of real objects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Praxis', 'n.', 'Use; practice; especially, exercise or discipline for a\n   specific purpose or object.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Praxis', 'n.', 'An example or form of exercise, or a collection of such\n   examples, for practice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pray', 'n. & v.', 'See Pry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prayed', 'imp. & p. p.', 'of Pray');
  INSERT INTO words (word, word_type, description) VALUES  (    'Praying', 'p. pr. & vb. n.', 'of Pray');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pray', 'v. i.', 'To make request with earnestness or zeal, as for something\n   desired; to make entreaty or supplication; to offer prayer to a deity\n   or divine being as a religious act; specifically, to address the\n   Supreme Being with adoration, confession, supplication, and\n   thanksgiving.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pray', 'v. t.', 'To address earnest request to; to supplicate; to entreat;\n   to implore; to beseech.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pray', 'v. t.', 'To ask earnestly for; to seek to obtain by supplication;\n   to entreat for.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pray', 'v. t.', 'To effect or accomplish by praying; as, to pray a soul out\n   of purgatory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prayer', 'n.', 'One who prays; a supplicant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prayer', 'v. i.', 'The act of praying, or of asking a favor; earnest\n   request or entreaty; hence, a petition or memorial addressed to a court\n   or a legislative body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prayer', 'v. i.', 'The act of addressing supplication to a divinity,\n   especially to the true God; the offering of adoration, confession,\n   supplication, and thanksgiving to the Supreme Being; as, public prayer;\n   secret prayer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prayer', 'v. i.', 'The form of words used in praying; a formula of\n   supplication; an expressed petition; especially, a supplication\n   addressed to God; as, a written or extemporaneous prayer; to repeat\n   ones prayers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prayerful', 'a.', 'Given to prayer; praying much or often; devotional.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prayerless', 'a.', 'Not using prayer; habitually neglecting prayer to God;\n   without prayer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Praying', '', 'a. & n. from Pray, v.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prayingly', 'adv.', 'With supplication to God.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pre-', '', 'A prefix denoting priority (of time, place, or rank); as,\n   precede, to go before; precursor, a forerunner; prefix, to fix or place\n   before; preeminent eminent before or above others. Pre- is sometimes\n   used intensively, as in prepotent, very potent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preaccusation', 'n.', 'Previous accusation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preace', 'v. & n.', 'Press.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preached', 'imp. & p. p.', 'of Preach');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preaching', 'p. pr. & vb. n.', 'of Preach');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preach', 'v. i.', 'To proclaim or publish tidings; specifically, to\n   proclaim the gospel; to discourse publicly on a religious subject, or\n   from a text of Scripture; to deliver a sermon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preach', 'v. i.', 'To give serious advice on morals or religion; to\n   discourse in the manner of a preacher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preach', 'v. t.', 'To proclaim by public discourse; to utter in a sermon or\n   a formal religious harangue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preach', 'v. t.', 'To inculcate in public discourse; to urge with\n   earnestness by public teaching.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preach', 'v. t.', 'To deliver or pronounce; as, to preach a sermon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preach', 'v. t.', 'To teach or instruct by preaching; to inform by\n   preaching.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preach', 'v. t.', 'To advise or recommend earnestly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preach', 'v.', 'A religious discourse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preacher', 'n.', 'One who preaches; one who discourses publicly on\n   religious subjects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preacher', 'n.', 'One who inculcates anything with earnestness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preachership', 'n.', 'The office of a preacher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preachify', 'v. i.', 'To discourse in the manner of a preacher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preaching', 'n.', 'The act of delivering a religious discourse; the art of\n   sermonizing; also, a sermon; a public religious discourse; serious,\n   earnest advice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preachmen', 'pl. ', 'of Preachman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preachman', 'n.', 'A preacher; -- so called in contempt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preachment', 'n.', 'A religious harangue; a sermon; -- used derogatively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preacquaint', 'v. t.', 'To acquaint previously or beforehand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preacquaintance', 'n.', 'Previous acquaintance or knowledge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preact', 'v. t.', 'To act beforehand; to perform previously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preaction', 'n.', 'Previous action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preadamic', 'a.', 'Prior to Adam.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preadamite', 'n.', 'An inhabitant of the earth before Adam.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preadamite', 'n.', 'One who holds that men existed before Adam.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preadamitic', 'a.', 'Existing or occurring before Adam; preadamic; as,\n   preadamitic periods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preadjustment', 'n.', 'Previous adjustment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preadministration', 'n.', 'Previous administration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preadmonish', 'v. t.', 'To admonish previously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preadmonition', 'n.', 'Previous warning or admonition; forewarning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preadvertise', 'v. t.', 'To advertise beforehand; to preannounce publicly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preamble', 'n.', 'A introductory portion; an introduction or preface, as to\n   a book, document, etc.; specifically, the introductory part of a\n   statute, which states the reasons and intent of the law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preamble', 'v. t. & i.', 'To make a preamble to; to preface; to serve as a\n   preamble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preambulary', 'a.', 'Of or pertaining to a preamble; introductory;\n   contained or provided for in a preamble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preambulate', 'v. i.', 'To walk before.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preambulation', 'n.', 'A walking or going before; precedence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preambulation', 'n.', 'A preamble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preambulatory', 'a.', 'Preceding; going before; introductory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preambulous', 'n.', 'See Perambulatory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preannounce', 'v. t.', 'To announce beforehand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preantenultimate', 'a.', 'Being or indicating the fourth syllable from the\n   end of a word, or that before the antepenult.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preaortic', 'a.', 'In front, or on the ventral side, of the aorta.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preappoint', 'v. t.', 'To appoint previously, or beforehand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preappointment', 'n.', 'Previous appointment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preapprehension', 'n.', 'An apprehension or opinion formed before\n   examination or knowledge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prearm', 'v. t.', 'To forearm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prearrange', 'v. t.', 'To arrange beforehand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prease', 'v. t. & i.', 'To press; to crowd.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prease', 'n.', 'A press; a crowd.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preassurance', 'n.', 'Previous assurance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preataxic', 'a.', 'Occurring before the symptom ataxia has developed; --\n   applied to the early symptoms of locomotor ataxia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preaudience', 'n.', 'Precedence of rank at the bar among lawyers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preaxial', 'a.', 'Situated in front of any transverse axis in the body of\n   an animal; anterior; cephalic; esp., in front, or on the anterior, or\n   cephalic (that is, radial or tibial) side of the axis of a limb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prebend', 'n.', 'A payment or stipend; esp., the stipend or maintenance\n   granted to a prebendary out of the estate of a cathedral or collegiate\n   church with which he is connected. See Note under Benefice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prebend', 'n.', 'A prebendary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prebendal', 'a.', 'Of or pertaining to a prebend; holding a prebend; as, a\n   prebendal priest or stall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prebendary', 'n.', 'A clergyman attached to a collegiate or cathedral\n   church who enjoys a prebend in consideration of his officiating at\n   stated times in the church. See Note under Benefice, n., 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prebendary', 'n.', 'A prebendaryship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prebendaryship', 'n.', 'The office of a prebendary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prebendate', 'v. t.', 'To invest with the office of prebendary; to present\n   to a prebend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prebendship', 'n.', 'A prebendaryship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prebronchial', 'a.', 'Situated in front of the bronchus; -- applied\n   especially to an air sac on either side of the esophagus of birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precalculate', 'v. t.', 'To calculate or determine beforehand; to\n   prearrange.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precant', 'n.', 'One who prays.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precarious', 'a.', 'Depending on the will or pleasure of another; held by\n   courtesy; liable to be changed or lost at the pleasure of another; as,\n   precarious privileges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precarious', 'a.', 'Held by a doubtful tenure; depending on unknown causes\n   or events; exposed to constant risk; not to be depended on for\n   certainty or stability; uncertain; as, a precarious state of health;\n   precarious fortunes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precation', 'n.', 'The act of praying; supplication; entreaty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preative', 'a.', 'Alt. of Preatory');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preatory', 'a.', 'Suppliant; beseeching.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precaution', 'n.', 'Previous caution or care; caution previously employed\n   to prevent mischief or secure good; as, his life was saved by\n   precaution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precaution', 'n.', 'A measure taken beforehand to ward off evil or secure\n   good or success; a precautionary act; as, to take precautions against\n   accident.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precaution', 'v. t.', 'To warn or caution beforehand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precaution', 'v. t.', 'To take precaution against.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precautional', 'a.', 'Precautionary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precautionary', 'a.', 'Of or pertaining to precaution, or precautions; as,\n   precautionary signals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precautious', 'a.', 'Taking or using precaution; precautionary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precedaneous', 'a.', 'Preceding; antecedent; previous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preceded', 'imp. & p. p.', 'of Precede');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preceding', 'p. pr. & vb. n.', 'of Precede');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precede', 'v. t.', 'To go before in order of time; to occur first with\n   relation to anything.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precede', 'v. t.', 'To go before in place, rank, or importance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precede', 'v. t.', 'To cause to be preceded; to preface; to introduce; --\n   used with by or with before the instrumental object.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precedence', 'n.', 'Alt. of Precedency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precedency', 'n.', 'The act or state of preceding or going before in order\n   of time; priority; as, one event has precedence of another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precedency', 'n.', 'The act or state of going or being before in rank or\n   dignity, or the place of honor; right to a more honorable place;\n   superior rank; as, barons have precedence of commoners.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precedent', 'a.', 'Going before; anterior; preceding; antecedent; as,\n   precedent services.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precedent', 'n.', 'Something done or said that may serve as an example to\n   authorize a subsequent act of the same kind; an authoritative example.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precedent', 'n.', 'A preceding circumstance or condition; an antecedent;\n   hence, a prognostic; a token; a sign.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precedent', 'n.', 'A rough draught of a writing which precedes a finished\n   copy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precedent', 'n.', 'A judicial decision which serves as a rule for future\n   determinations in similar or analogous cases; an authority to be\n   followed in courts of justice; forms of proceeding to be followed in\n   similar cases.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precedented', 'a.', 'Having a precedent; authorized or sanctioned by an\n   example of a like kind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precedential', 'a.', 'Of the nature of a precedent; having force as an\n   example for imitation; as, precedential transactions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precedently', 'adv.', 'Beforehand; antecedently.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preceding', 'a.', 'Going before; -- opposed to following.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preceding', 'a.', 'In the direction toward which stars appear to move. See\n   Following, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precel', 'v. t. & i.', 'To surpass; to excel; to exceed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precellence', 'n.', 'Alt. of Precellency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precellency', 'n.', 'Excellence; superiority.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precellent', 'a.', 'Excellent; surpassing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precentor', 'n.', 'A leader of a choir; a directing singer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precentor', 'n.', 'The leader of the choir in a cathedral; -- called also\n   the chanter or master of the choir.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precentor', 'n.', 'The leader of the congregational singing in Scottish and\n   other churches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precentorship', 'n.', 'The office of a precentor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precent', 'n.', 'Any commandment, instruction, or order intended as an\n   authoritative rule of action; esp., a command respecting moral conduct;\n   an injunction; a rule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precent', 'n.', 'A command in writing; a species of writ or process.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precept', 'v. t.', 'To teach by precepts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preceptial', 'a.', 'Preceptive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preception', 'n.', 'A precept.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preceptive', 'a.', 'Containing or giving precepts; of the nature of\n   precepts; didactic; as, the preceptive parts of the Scriptures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preceptor', 'n.', 'One who gives commands, or makes rules; specifically,\n   the master or principal of a school; a teacher; an instructor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preceptor', 'n.', 'The head of a preceptory among the Knights Templars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preceptorial', 'a.', 'Of or pertaining to a preceptor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preceptory', 'a.', 'Preceptive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preceptories', 'pl. ', 'of Preceptory');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preceptory', 'n.', 'A religious house of the Knights Templars, subordinate\n   to the temple or principal house of the order in London. See\n   Commandery, n., 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preceptress', 'n.', 'A woman who is the principal of a school; a female\n   teacher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precession', 'n.', 'The act of going before, or forward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precessional', 'a.', 'Of or pertaining to pression; as, the precessional\n   movement of the equinoxes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precessor', 'n.', 'A predecessor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precinct', 'n.', 'The limit or exterior line encompassing a place; a\n   boundary; a confine; limit of jurisdiction or authority; -- often in\n   the plural; as, the precincts of a state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precinct', 'n.', 'A district within certain boundaries; a minor territorial\n   or jurisdictional division; as, an election precinct; a school\n   precinct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precinct', 'n.', 'A parish or prescribed territory attached to a church,\n   and taxed for its support.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preciosity', 'n.', 'Preciousness; something precious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precious', 'a.', 'Of great price; costly; as, a precious stone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precious', 'a.', 'Of great value or worth; very valuable; highly esteemed;\n   dear; beloved; as, precious recollections.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precious', 'a.', 'Particular; fastidious; overnice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preciously', 'adv.', 'In a precious manner; expensively; extremely; dearly.\n   Also used ironically.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preciousness', 'n.', 'The quality or state of being precious; costliness;\n   dearness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precipe', 'n.', 'See Praecipe, and Precept.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precipice', 'n.', 'A sudden or headlong fall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precipice', 'n.', 'A headlong steep; a very steep, perpendicular, or\n   overhanging place; an abrupt declivity; a cliff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precipient', 'a.', 'Commanding; directing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precipitability', 'n.', 'The quality or state of being precipitable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precipitable', 'a.', 'Capable of being precipitated, or cast to the bottom,\n   as a substance in solution. See Precipitate, n. (Chem.)');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precipitance', 'n.', 'Alt. of Precipitancy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precipitancy', 'n.', 'The quality or state of being precipitant, or\n   precipitate; headlong hurry; excessive or rash haste in resolving,\n   forming an opinion, or executing a purpose; precipitation; as, the\n   precipitancy of youth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precipitant', 'a.', 'Falling or rushing headlong; rushing swiftly,\n   violently, or recklessly; moving precipitately.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precipitant', 'a.', 'Unexpectedly or foolishly brought on or hastened;\n   rashly hurried; hasty; sudden; reckless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precipitant', 'n.', 'Any force or reagent which causes the formation of a\n   precipitate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precipitantly', 'adv.', 'With rash or foolish haste; in headlong manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precipitantness', 'n.', 'The quality or state of being precipitant;\n   precipitation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precipitate', 'a.', 'Overhasty; rash; as, the king was too precipitate in\n   declaring war.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precipitate', 'a.', 'Lacking due deliberation or care; hurried; said or\n   done before the time; as, a precipitate measure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precipitate', 'a.', 'Falling, flowing, or rushing, with steep descent;\n   headlong.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precipitate', 'a.', 'Ending quickly in death; brief and fatal; as, a\n   precipitate case of disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precipitate', 'n.', 'An insoluble substance separated from a solution in a\n   concrete state by the action of some reagent added to the solution, or\n   of some force, such as heat or cold. The precipitate may fall to the\n   bottom (whence the name), may be diffused through the solution, or may\n   float at or near the surface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precipitated', 'imp. & p. p.', 'of Precipitate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precipitating', 'p. pr. & vb. n.', 'of Precipitate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precipitate', 'v. t.', 'To throw headlong; to cast down from a precipice or\n   height.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precipitate', 'v. t.', 'To urge or press on with eager haste or violence;\n   to cause to happen, or come to a crisis, suddenly or too soon; as,\n   precipitate a journey, or a conflict.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precipitate', 'v. t.', 'To separate from a solution, or other medium, in\n   the form of a precipitate; as, water precipitates camphor when in\n   solution with alcohol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precipitate', 'v. i.', 'To dash or fall headlong.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precipitate', 'v. i.', 'To hasten without preparation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precipitate', 'v. i.', 'To separate from a solution as a precipitate. See\n   Precipitate, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precipitately', 'adv.', 'In a precipitate manner; headlong; hastily;\n   rashly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precipitation', 'n.', 'The act of precipitating, or the state of being\n   precipitated, or thrown headlong.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precipitation', 'n.', 'A falling, flowing, or rushing downward with\n   violence and rapidity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precipitation', 'n.', 'Great hurry; rash, tumultuous haste; impetuosity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precipitation', 'n.', 'The act or process of precipitating from a solution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precipitator', 'n.', 'One who precipitates, or urges on with vehemence or\n   rashness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precipitious', 'a.', 'Precipitous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precipitous', 'a.', 'Steep, like a precipice; as, a precipitous cliff or\n   mountain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precipitous', 'a.', 'Headlong; as, precipitous fall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precipitous', 'a.', 'Hasty; rash; quick; sudden; precipitate; as,\n   precipitous attempts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precis', 'n.', 'A concise or abridged statement or view; an abstract; a\n   summary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precise', 'a.', 'Having determinate limitations; exactly or sharply defined\n   or stated; definite; exact; nice; not vague or equivocal; as, precise\n   rules of morality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precise', 'a.', 'Strictly adhering or conforming to rule; very nice or\n   exact; punctilious in conduct or ceremony; formal; ceremonious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precisian', 'n.', 'One who limits, or restrains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precisian', 'n.', 'An overprecise person; one rigidly or ceremoniously\n   exact in the observance of rules; a formalist; -- formerly applied to\n   the English Puritans.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precisianism', 'n.', 'The quality or state of being a precisian; the\n   practice of a precisian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precisianist', 'n.', 'A precisian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precision', 'n.', 'The quality or state of being precise; exact limitation;\n   exactness; accuracy; strict conformity to a rule or a standard;\n   definiteness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precisive', 'a.', 'Cutting off; (Logic) exactly limiting by cutting off all\n   that is not absolutely relative to the purpose; as, precisive censure;\n   precisive abstraction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precluded', 'imp. & p. p.', 'of Preclude');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precluding', 'p. pr. & vb. n.', 'of Preclude');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preclude', 'v.', 'To put a barrier before; hence, to shut out; to hinder;\n   to stop; to impede.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preclude', 'v.', 'To shut out by anticipative action; to prevent or hinder\n   by necessary consequence or implication; to deter action of, access to,\n   employment of, etc.; to render ineffectual; to obviate by anticipation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preclusion', 'n.', 'The act of precluding, or the state of being precluded;\n   a shutting out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preclusive', 'a.', 'Shutting out; precluding, or tending to preclude;\n   hindering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precoce', 'a.', 'Precocious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precoces', 'n. pl.', 'Same as Praecoces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precocious', 'a.', 'Ripe or mature before the proper or natural time; early\n   or prematurely ripe or developed; as, precocious trees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precocious', 'a.', 'Developed more than is natural or usual at a given age;\n   exceeding what is to be expected of ones years; too forward; -- used\n   especially of mental forwardness; as, a precocious child; precocious\n   talents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precociously', 'adv.', 'In a precocious manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precociousness', 'n.', 'Alt. of Precocity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precocity', 'n.', 'The quality or state of being precocious; untimely\n   ripeness; premature development, especially of the mental powers;\n   forwardness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precoetanean', 'n.', 'One contemporary with, but older than, another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precogitate', 'v. t.', 'To cogitate beforehand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precogita/tion', 'n.', 'Previous cogitation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precognition', 'n.', 'Previous cognition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precognition', 'n.', 'A preliminary examination of a criminal case with\n   reference to a prosecution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precognizable', 'a.', 'Cognizable beforehand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precognosce', 'v. t.', 'To examine beforehand, as witnesses or evidence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precollection', 'n.', 'A collection previously made.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precomposed', 'imp. & p. p.', 'of Precompose');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precomposing', 'p. pr. & vb. n.', 'of Precompose');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precompose', 'v. t.', 'To compose beforehand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preconceit', 'n.', 'An opinion or notion formed beforehand; a\n   preconception.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preconceived', 'imp. & p. p.', 'of Preconceive');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preconceiving', 'p. pr. & vb. n.', 'of Preconceive');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preconceive', 'v. t.', 'To conceive, or form an opinion of, beforehand; to\n   form a previous notion or idea of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preconception', 'n.', 'The act of preconceiving; conception or opinion\n   previously formed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preconcerted', 'imp. & p. p.', 'of Preconcert');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preconcerting', 'p. pr. & vb. n.', 'of Preconcert');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preconcert', 'v. t.', 'To concert or arrange beforehand; to settle by\n   previous agreement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preconcert', 'n.', 'Something concerted or arranged beforehand; a previous\n   agreement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preconcerted', 'a.', 'Previously arranged; agreed upon beforehand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preconcertion', 'n.', 'The act of preconcerting; preconcert.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precondemn', 'v. t.', 'To condemn beforehand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precondition', 'n.', 'A previous or antecedent condition; a preliminary\n   condition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preconform', 'v. t. & i.', 'To conform by way anticipation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preconformity', 'n.', 'Anticipative or antecedent conformity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preconizate', 'v. t.', 'To proclaim; to publish; also, to summon; to call.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preconization', 'n.', 'A publishing by proclamation; a public proclamation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preconization', 'n.', 'A formal approbation by the pope of a person\n   nominated to an ecclesiastical dignity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preconize', 'v. t.', 'To approve by preconization.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preconquer', 'v. t.', 'To conquer in anticipation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preconscious', 'a.', 'Of or pertaining to a state before consciousness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preconsent', 'n.', 'A previous consent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preconsigned', 'imp. & p. p.', 'of Preconsign');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preconsigning', 'p. pr. & vb. n.', 'of Preconsign');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preconsign', 'v. t.', 'To consign beforehand; to make a previous\n   consignment of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preconsolidated', 'a.', 'Consolidated beforehand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preconstitute', 'v. t.', 'To constitute or establish beforehand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precontracted', 'imp. & p. p.', 'of Precontract');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precontracting', 'p. pr. & vb. n.', 'of Precontract');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precontract', 'v. t.', 'To contract, engage, or stipulate previously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precontract', 'v. i.', 'To make a previous contract or agreement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precontract', 'n.', 'A contract preceding another');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precontract', 'n.', 'a contract of marriage which, according to the ancient\n   law, rendered void a subsequent marriage solemnized in violation of it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precontrive', 'v. t. & i.', 'To contrive or plan beforehand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precoracoid', 'n.', 'The anterior part of the coracoid (often closely\n   united with the clavicle) in the shoulder girdle of many reptiles and\n   amphibians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precordial', 'a.', 'Situated in front of the heart; of or pertaining to the\n   praecordia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precrural', 'a.', 'Situated in front of the leg or thigh; as, the precrural\n   glands of the horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precurrer', 'n.', 'A precursor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precurse', 'n.', 'A forerunning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precursive', 'a.', 'Preceding; introductory; precursory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precursor', 'n.', 'One who, or that which, precedes an event, and indicates\n   its approach; a forerunner; a harbinger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precursorship', 'n.', 'The position or condition of a precursor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precursory', 'a.', 'Preceding as a precursor or harbinger; indicating\n   something to follow; as, precursory symptoms of a fever.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Precursory', 'n.', 'An introduction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predacean', 'n.', 'A carnivorous animal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predaceous', 'a.', 'Living by prey; predatory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predal', 'a.', 'Of or pertaining to prey; plundering; predatory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predate', 'v. t.', 'To date anticipation; to affix to (a document) an\n   earlier than the actual date; to antedate; as, a predated deed or\n   letter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predation', 'n.', 'The act of pillaging.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predatorily', 'adv.', 'In a predatory manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predatory', 'a.', 'Characterized by plundering; practicing rapine;\n   plundering; pillaging; as, a predatory excursion; a predatory party.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predatory', 'a.', 'Hungry; ravenous; as, predatory spirits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predatory', 'a.', 'Living by preying upon other animals; carnivorous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prede', 'v. i.', 'To prey; to plunder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prede', 'n.', 'Prey; plunder; booty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predecay', 'n.', 'Premature decay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predecease', 'v. t.', 'To die sooner than.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predecease', 'n.', 'The death of one person or thing before another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predecessive', 'a.', 'Going before; preceding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predecessor', 'n.', 'One who precedes; one who has preceded another in any\n   state, position, office, etc.; one whom another follows or comes after,\n   in any office or position.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predeclare', 'v. t.', 'To declare or announce beforehand; to preannounce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prededication', 'n.', 'A dedication made previously or beforehand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predefine', 'v. t.', 'To define beforehand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predeliberation', 'n.', 'Previous deliberation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predelineation', 'n.', 'Previous delineation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predella', 'n.', 'The step, or raised secondary part, of an altar; a\n   superaltar; hence, in Italian painting, a band or frieze of several\n   pictures running along the front of a superaltar, or forming a border\n   or frame at the foot of an altarpiece.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predesign', 'v. t.', 'To design or purpose beforehand; to predetermine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predesignate', 'a.', 'A term used by Sir William Hamilton to define\n   propositions having their quantity indicated by a verbal sign; as, all,\n   none, etc.; -- contrasted with preindesignate, defining propositions of\n   which the quantity is not so indicated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predestinarian', 'a.', 'Of or pertaining to predestination; as, the\n   predestinarian controversy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predestinarian', 'n.', 'One who believes in or supports the doctrine of\n   predestination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predestinarianism', 'n.', 'The system or doctrine of the predestinarians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predestinary', 'a.', 'Predestinarian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predestinate', 'a.', 'Predestinated; foreordained; fated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predestinated', 'imp. & p. p.', 'of Predestinate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predestinating', 'p. pr. & vb. n.', 'of Predestinate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predestinate', 'v. t.', 'To predetermine or foreordain; to appoint or\n   ordain beforehand by an unchangeable purpose or decree; to preelect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predestination', 'n.', 'The act of predestinating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predestination', 'n.', 'The purpose of Good from eternity respecting all\n   events; especially, the preordination of men to everlasting happiness\n   or misery. See Calvinism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predestinative', 'a.', 'Determining beforehand; predestinating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predestinator', 'n.', 'One who predestinates, or foreordains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predestinator', 'n.', 'One who holds to the doctrine of predestination; a\n   predestinarian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predestined', 'imp. & p. p.', 'of Predestine');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predestining', 'p. pr. & vb. n.', 'of Predestine');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predestine', 'v. t.', 'To decree beforehand; to foreordain; to\n   predestinate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predestiny', 'n.', 'Predestination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predeterminable', 'a.', 'Capable of being determined beforehand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predeterminate', 'a.', 'Determined beforehand; as, the predeterminate\n   counsel of God.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predetermination', 'n.', 'The act of previous determination; a purpose\n   formed beforehand; as, the predetermination of Gods will.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predetermined', 'imp. & p. p.', 'of Predetermine');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predermining', 'p. pr. & vb. n.', 'of Predetermine');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predetermine', 'v. t.', 'To determine (something) beforehand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predetermine', 'v. t.', 'To doom by previous decree; to foredoom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predetermine', 'v. i.', 'To determine beforehand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predial', 'a.', 'Consisting of land or farms; landed; as, predial estate;\n   that is, real estate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predial', 'a.', 'Attached to land or farms; as, predial slaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predial', 'a.', 'Issuing or derived from land; as, predial tithes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prediastolic', 'a.', 'Preceding the diastole of the heart; as, a\n   prediastolic friction sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predicability', 'n.', 'The quality or state of being predicable, or\n   affirmable of something, or attributed to something.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predicable', 'a.', 'Capable of being predicated or affirmed of something;\n   affirmable; attributable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predicable', 'n.', 'Anything affirmable of another; especially, a general\n   attribute or notion as affirmable of, or applicable to, many\n   individuals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predicable', 'n.', 'One of the five most general relations of attributes\n   involved in logical arrangements, namely, genus, species, difference,\n   property, and accident.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predicament', 'n.', 'A class or kind described by any definite marks;\n   hence, condition; particular situation or state; especially, an\n   unfortunate or trying position or condition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predicament', 'n.', 'See Category.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predicamental', 'a.', 'Of or pertaining to a predicament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predicant', 'a.', 'Predicating; affirming; declaring; proclaiming; hence;\n   preaching.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predicant', 'n.', 'One who predicates, affirms, or proclaims; specifically,\n   a preaching friar; a Dominican.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predicated', 'imp. & p. p.', 'of Predicate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predicating', 'p. pr. & vb. n.', 'of Predicate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predicate', 'v. t.', 'To assert to belong to something; to affirm (one\n   thing of another); as, to predicate whiteness of snow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predicate', 'v. t.', 'To found; to base.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predicate', 'v. i.', 'To affirm something of another thing; to make an\n   affirmation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predicate', 'v. t.', 'That which is affirmed or denied of the subject. In\n   these propositions, \"Paper is white,\" \"Ink is not white,\" whiteness is\n   the predicate affirmed of paper and denied of ink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predicate', 'v. t.', 'The word or words in a proposition which express what\n   is affirmed of the subject.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predicate', 'a.', 'Predicated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predication', 'n.', 'The act of predicating, or of affirming one thing of\n   another; affirmation; assertion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predication', 'n.', 'Preaching.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predicative', 'a.', 'Expressing affirmation or predication; affirming;\n   predicating, as, a predicative term.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predicatory', 'a.', 'Affirmative; positive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predicrotic', 'a.', 'A term applied to the pulse wave sometimes seen in a\n   pulse curve or sphygmogram, between the apex of the curve and the\n   dicrotic wave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predicted', 'imp. & p. p.', 'of Predict');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predicting', 'p. pr. & vb. n.', 'of Predict');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predict', 'v. t.', 'To tell or declare beforehand; to foretell; to\n   prophesy; to presage; as, to predict misfortune; to predict the return\n   of a comet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predict', 'n.', 'A prediction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predictable', 'a.', 'That may be predicted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prediction', 'n.', 'The act of foretelling; also, that which is foretold;\n   prophecy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predictional', 'a.', 'Prophetic; prognostic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predictive', 'a.', 'Foretelling; prophetic; foreboding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predictor', 'n.', 'One who predicts; a foreteller.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predictory', 'a.', 'Predictive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predigest', 'v. t.', 'To subject (food) to predigestion or artificial\n   digestion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predigestion', 'n.', 'Digestion too soon performed; hasty digestion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predigestion', 'n.', 'Artificial digestion of food for use in illness or\n   impaired digestion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predilect', 'v. t.', 'To elect or choose beforehand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predilection', 'n.', 'A previous liking; a prepossession of mind in favor\n   of something; predisposition to choose or like; partiality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prediscover', 'v. t.', 'To discover beforehand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prediscovery', 'n.', 'A previous discovery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predisponency', 'n.', 'The state of being predisposed; predisposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predisponent', 'a.', 'Disposing beforehand; predisposing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predisponent', 'n.', 'That which predisposes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predisposed', 'imp. & p. p.', 'of Predispose');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predisposing', 'p. pr. & vb. n.', 'of Predispose');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predispose', 'v. t.', 'To dispose or incline beforehand; to give a\n   predisposition or bias to; as, to predispose the mind to friendship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predispose', 'v. t.', 'To make fit or susceptible beforehand; to give a\n   tendency to; as, debility predisposes the body to disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predisposition', 'n.', 'The act of predisposing, or the state of being\n   predisposed; previous inclination, tendency, or propensity;\n   predilection; -- applied to the mind; as, a predisposition to anger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predisposition', 'n.', 'Previous fitness or adaptation to any change,\n   impression, or purpose; susceptibility; -- applied to material things;\n   as, the predisposition of the body to disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predominance', 'n.', 'The quality or state of being predominant;\n   superiority; ascendency; prevalence; predomination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predominance', 'n.', 'The superior influence of a planet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predominancy', 'n.', 'Predominance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predominant', 'a.', 'Having the ascendency over others; superior in\n   strength, influence, or authority; prevailing; as, a predominant color;\n   predominant excellence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predominantly', 'adv.', 'In a predominant manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predominated', 'imp. & p. p.', 'of Predominate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predominating', 'p. pr. & vb. n.', 'of Predominate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predominate', 'v. i.', 'To be superior in number, strength, influence, or\n   authority; to have controlling power or influence; to prevail; to rule;\n   to have the mastery; as, love predominated in her heart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predominate', 'v. t.', 'To rule over; to overpower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predomination', 'n.', 'The act or state of predominating; ascendency;\n   predominance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predoom', 'v. t.', 'To foredoom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predorsal', 'a.', 'Situated in front of the back; immediately in front, or\n   on the ventral side the dorsal part of the vertebral column.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Predy', 'a.', 'Cleared and ready for engagement, as a ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preedy', 'adv.', 'With ease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preef', 'n.', 'Proof.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preelect', 'v. t.', 'To elect beforehand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preelection', 'n.', 'Election beforehand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preeminence', 'n.', 'The quality or state of being preeminent; superiority\n   in prominence or in excellence; distinction above others in quality,\n   rank, etc.; rarely, in a bad sense, superiority or notoriety in evil;\n   as, preeminence in honor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preeminent', 'a.', 'Eminent above others; prominent among those who are\n   eminent; superior in excellence; surpassing, or taking precedence of,\n   others; rarely, surpassing others in evil, or in bad qualities; as,\n   preeminent in guilt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preeminently', 'adv.', 'In a preeminent degree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preemploy', 'v. t.', 'To employ beforehand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preempted', 'imp. & p. p.', 'of Preempt');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preempting', 'p. pr. & vb. n.', 'of Preempt');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preempt', 'v. t. & i.', 'To settle upon (public land) with a right of\n   preemption, as under the laws of the United States; to take by\n   preemption.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preemption', 'n.', 'The act or right of purchasing before others.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preemption', 'n.', 'The privilege or prerogative formerly enjoyed by the\n   king of buying provisions for his household in preference to others.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preemption', 'n.', 'The right of an actual settler upon public lands\n   (particularly those of the United States) to purchase a certain portion\n   at a fixed price in preference to all other applicants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preemptioner', 'n.', 'One who holds a prior to purchase certain public\n   land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preemptive', 'a.', 'Of or pertaining to preemption; having power to\n   preempt; preempting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preemtor', 'n.', 'One who preempts; esp., one who preempts public land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preemptory', 'a.', 'Pertaining to preemption.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preen', 'n.', 'A forked tool used by clothiers in dressing cloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preened', 'imp. & p. p.', 'of Preen');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preening', 'p. pr. & vb. n.', 'of Preen');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preen', 'n.', 'To dress with, or as with, a preen; to trim or dress with\n   the beak, as the feathers; -- said of birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preen', 'n.', 'To trim up, as trees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preengaged', 'imp. & p. p.', 'of Preengage');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preengaging', 'p. pr. & vb. n.', 'of Preengage');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preengage', 'v. t.', 'To engage by previous contract; to bind or attach\n   previously; to preoccupy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preengagement', 'n.', 'Prior engagement, obligation, or attachment, as by\n   contract, promise, or affection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preerect', 'v. t.', 'To erect beforehand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prees', 'n.', 'Press; throng.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preestablish', 'v. t.', 'To establish beforehand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preestablishment', 'n.', 'Settlement beforehand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preeternity', 'n.', 'Infinite previous duration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preexamination', 'n.', 'Previous examination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preexamined', 'imp. & p. p.', 'of Preexamine');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preexamining', 'p. pr. & vb. n.', 'of Preexamine');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preexamine', 'v. t.', 'To examine beforehand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preexisted', 'imp. & p. p.', 'of Preexist');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preexisting', 'p. pr. & vb. n.', 'of Preexist');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preexist', 'v. i.', 'To exist previously; to exist before something else.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preexistence', 'n.', 'Existence in a former state, or previous to something\n   else.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preexistence', 'n.', 'Existence of the soul before its union with the body;\n   -- a doctrine held by certain philosophers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preexistency', 'n.', 'Preexistence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preexistent', 'a.', 'Existing previously; preceding existence; as, a\n   preexistent state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preexistentism', 'n.', 'The theory of a preexistence of souls before their\n   association with human bodies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preexistimation', 'n.', 'Previous esteem or estimation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preexpectation', 'n.', 'Previous expectation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preface', 'n.', 'Something spoken as introductory to a discourse, or\n   written as introductory to a book or essay; a proem; an introduction,\n   or series of preliminary remarks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preface', 'n.', 'The prelude or introduction to the canon of the Mass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prefaced', 'imp. & p. p.', 'of Preface');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prefacing', 'p. pr. & vb. n.', 'of Preface');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preface', 'v. t.', 'To introduce by a preface; to give a preface to; as, to\n   preface a book discourse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preface', 'v. i.', 'To make a preface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prefacer', 'n.', 'The writer of a preface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prefatorial', 'a.', 'Prefatory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prefatorily', 'adv.', 'In a prefatory manner; by way of preface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prefatory', 'a.', 'Pertaining to, or of the nature of, a preface;\n   introductory to a book, essay, or discourse; as, prefatory remarks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prefect', 'n.', 'A Roman officer who controlled or superintended a\n   particular command, charge, department, etc.; as, the prefect of the\n   aqueducts; the prefect of a camp, of a fleet, of the city guard, of\n   provisions; the pretorian prefect, who was commander of the troops\n   guarding the emperors person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prefect', 'n.', 'A superintendent of a department who has control of its\n   police establishment, together with extensive powers of municipal\n   regulation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prefect', 'n.', 'In the Greek and Roman Catholic churches, a title of\n   certain dignitaries below the rank of bishop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prefectorial', 'a.', 'Of or pertaining to a prefect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prefectship', 'n.', 'The office or jurisdiction of a prefect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prefecture', 'n.', 'The office, position, or jurisdiction of a prefect;\n   also, his official residence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prefecundation', 'n.', 'A term collectively applied to the changes or\n   conditions preceding fecundation, especially to the changes which the\n   ovum undergoes before fecundation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prefecundatory', 'a.', 'Of or pertaining to prefecundation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preferred', 'imp. & p. p.', 'of Prefer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preferring', 'p. pr. & vb. n.', 'of Prefer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prefer', 'v. t.', 'To carry or bring (something) forward, or before one;\n   hence, to bring for consideration, acceptance, judgment, etc.; to\n   offer; to present; to proffer; to address; -- said especially of a\n   request, prayer, petition, claim, charge, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prefer', 'v. t.', 'To go before, or be before, in estimation; to outrank;\n   to surpass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prefer', 'v. t.', 'To cause to go before; hence, to advance before others,\n   as to an office or dignity; to raise; to exalt; to promote; as, to\n   prefer an officer to the rank of general.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prefer', 'v. t.', 'To set above or before something else in estimation,\n   favor, or liking; to regard or honor before another; to hold in greater\n   favor; to choose rather; -- often followed by to, before, or above.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preferability', 'n.', 'The quality or state of being preferable;\n   preferableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preferable', 'a.', 'Worthy to be preferred or chosen before something else;\n   more desirable; as, a preferable scheme.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preferableness', 'n.', 'The quality or state of being preferable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preferably', 'adv.', 'In preference; by choice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preference', 'n.', 'The act of Preferring, or the state of being preferred;\n   the setting of one thing before another; precedence; higher estimation;\n   predilection; choice; also, the power or opportunity of choosing; as,\n   to give him his preference.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preference', 'n.', 'That which is preferred; the object of choice or\n   superior favor; as, which is your preference?');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preferential', 'a.', 'Giving, indicating, or having a preference or\n   precedence; as, a preferential claim; preferential shares.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preferment', 'n.', 'The act of choosing, or the state of being chosen;\n   preference.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preferment', 'n.', 'The act of preferring, or advancing in dignity or\n   office; the state of being advanced; promotion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preferment', 'n.', 'A position or office of honor or profit; as, the\n   preferments of the church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preferrer', 'n.', 'One who prefers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prefidence', 'n.', 'The quality or state of being prefident.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prefident', 'a.', 'Trusting beforehand; hence, overconfident.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prefigurate', 'v. t.', 'To prefigure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prefiguration', 'n.', 'The act of prefiguring, or the state of being\n   prefigured.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prefigurative', 'a.', 'Showing by prefiguration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prefigured', 'imp. & p. p.', 'of Prefigure');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prefiguring', 'p. pr. & vb. n.', 'of Prefigure');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prefigure', 'v. t.', 'To show, suggest, or announce, by antecedent types\n   and similitudes; to foreshadow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prefigurement', 'n.', 'The act of prefiguring; prefiguration; also, that\n   which is prefigured.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prefine', 'v. t.', 'To limit beforehand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prefinite', 'a.', 'Prearranged.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prefinition', 'n.', 'Previous limitation.');
