  INSERT INTO words (word, word_type, description) VALUES  (    'Prefixing', 'p. pr. & vb. n.', 'of Prefix');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prefix', 'v. t.', 'To put or fix before, or at the beginning of, another\n   thing; as, to prefix a syllable to a word, or a condition to an\n   agreement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prefix', 'v. t.', 'To set or appoint beforehand; to settle or establish\n   antecedently.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prefix', 'n.', 'That which is prefixed; esp., one or more letters or\n   syllables combined or united with the beginning of a word to modify its\n   signification; as, pre- in prefix, con- in conjure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prefixion', 'n.', 'The act of prefixing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prefloration', 'n.', 'Aestivation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prefoliation', 'n.', 'Vernation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preform', 'v. t.', 'To form beforehand, or for special ends.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preformation', 'n.', 'An old theory of the preexistence of germs. Cf.\n   Embo/tement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preformative', 'n.', 'A formative letter at the beginning of a word.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prefrontal', 'a.', 'Situated in front of the frontal bone, or the frontal\n   region of the skull; ectethmoid, as a certain bone in the nasal capsule\n   of many animals, and certain scales of reptiles and fishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prefrontal', 'n.', 'A prefrontal bone or scale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prefulgency', 'n.', 'Superior brightness or effulgency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pregage', 'v. t.', 'To preengage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preglacial', 'a.', 'Prior to the glacial or drift period.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pregnable', 'a.', 'Capable of being entered, taken, or captured;\n   expugnable; as, a pregnable fort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pregnance', 'n.', 'Pregnancy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pregnancy', 'n.', 'The condition of being pregnant; the state of being with\n   young.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pregnancy', 'n.', 'Figuratively: The quality of being heavy with important\n   contents, issue, significance, etc.; unusual consequence or capacity;\n   fertility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pregnant', 'a.', 'Being with young, as a female; having conceived; great\n   with young; breeding; teeming; gravid; preparing to bring forth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pregnant', 'a.', 'Heavy with important contents, significance, or issue;\n   full of consequence or results; weighty; as, pregnant replies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pregnant', 'a.', 'Full of promise; abounding in ability, resources, etc.;\n   as, a pregnant youth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pregnant', 'n.', 'A pregnant woman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pregnant', 'a.', 'Affording entrance; receptive; yielding; willing; open;\n   prompt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pregnantly', 'adv.', 'In a pregnant manner; fruitfully; significantly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pregnantly', 'adv.', 'Unresistingly; openly; hence, clearly; evidently.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pregravate', 'v. t.', 'To bear down; to depress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pregravitate', 'v. i.', 'To descend by gravity; to sink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pregustant', 'a.', 'Tasting beforehand; having a foretaste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pregustation', 'n.', 'The act of tasting beforehand; foretaste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prehallux', 'n.', 'An extra first toe, or rudiment of a toe, on the\n   preaxial side of the hallux.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prehend', 'v. t.', 'To lay hold of; to seize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prehensi-ble', 'a.', 'Capable of being seized.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prehensile', 'n.', 'Adapted to seize or grasp; seizing; grasping; as, the\n   prehensile tail of a monkey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prehension', 'n.', 'The act of taking hold, seizing, or grasping, as with\n   the hand or other member.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prehensory', 'a.', 'Adapted to seize or grasp; prehensile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prehistoric', 'a.', 'Of or pertaining to a period before written history\n   begins; as, the prehistoric ages; prehistoric man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prehnite', 'n.', 'A pale green mineral occurring in crystalline aggregates\n   having a botryoidal or mammillary structure, and rarely in distinct\n   crystals. It is a hydrous silicate of alumina and lime.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prehnitic', 'a.', 'Pertaining to, or designating, a tetrabasic acid of\n   benzene obtained as a white crystalline substance; -- probably so\n   called from the resemblance of the wartlike crystals to the mammillae\n   on the surface of prehnite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preindesignate', 'a.', 'Having no sign expressive of quantity; indefinite.\n   See Predesignate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preindispose', 'v. t.', 'To render indisposed beforehand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preinstructed', 'imp. & p. p.', 'of Preinstruct');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preinstructing', 'p. pr. & vb. n.', 'of Preinstruct');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preinstruct', 'v. t.', 'To instruct previously or beforehand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preintimation', 'n.', 'Previous intimation; a suggestion beforehand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prejudged', 'imp. & p. p.', 'of Prejudge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prejudging', 'p. pr. & vb. n.', 'of Prejudge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prejudge', 'v. t.', 'To judge before hearing, or before full and sufficient\n   examination; to decide or sentence by anticipation; to condemn\n   beforehand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prejudgment', 'n.', 'The act of prejudging; decision before sufficient\n   examination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prejudicacy', 'n.', 'Prejudice; prepossession.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prejudical', 'a.', 'Of or pertaining to the determination of some matter\n   not previously decided; as, a prejudical inquiry or action at law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prejudicant', 'a.', 'Influenced by prejudice; biased.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prejudicate', 'a.', 'Formed before due examination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prejudicate', 'a.', 'Biased by opinions formed prematurely; prejudiced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prejudicated', 'imp. & p. p.', 'of Prejudicate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prejudicating', 'p. pr. & vb. n.', 'of Prejudicate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prejudicate', 'v. t.', 'To determine beforehand, especially to\n   disadvantage; to prejudge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prejudicate', 'v. i.', 'To prejudge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prejudicately', 'adv.', 'With prejudice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prejudication', 'n.', 'The act of prejudicating, or of judging without due\n   examination of facts and evidence; prejudgment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prejudication', 'n.', 'A preliminary inquiry and determination about\n   something which belongs to a matter in dispute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prejudication', 'n.', 'A previous treatment and decision of a point; a\n   precedent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prejudicative', 'a.', 'Forming a judgment without due examination;\n   prejudging.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prejudice', 'n.', 'Foresight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prejudice', 'n.', 'An opinion or judgment formed without due examination;\n   prejudgment; a leaning toward one side of a question from other\n   considerations than those belonging to it; an unreasonable predilection\n   for, or objection against, anything; especially, an opinion or leaning\n   adverse to anything, without just grounds, or before sufficient\n   knowledge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prejudice', 'n.', 'A bias on the part of judge, juror, or witness which\n   interferes with fairness of judgment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prejudice', 'n.', 'Mischief; hurt; damage; injury; detriment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prejudiced', 'imp. & p. p.', 'of Prejudice');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prejudicing', 'p. pr. & vb. n.', 'of Prejudice');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prejudice', 'n.', 'To cause to have prejudice; to prepossess with opinions\n   formed without due knowledge or examination; to bias the mind of, by\n   hasty and incorrect notions; to give an unreasonable bent to, as to one\n   side or the other of a cause; as, to prejudice a critic or a juryman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prejudice', 'n.', 'To obstruct or injure by prejudices, or by previous bias\n   of the mind; hence, generally, to hurt; to damage; to injure; to\n   impair; as, to prejudice a good cause.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prejudicial', 'a.', 'Biased, possessed, or blinded by prejudices; as, to\n   look with a prejudicial eye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prejudicial', 'a.', 'Tending to obstruct or impair; hurtful; injurious;\n   disadvantageous; detrimental.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preknowledge', 'n.', 'Prior knowledge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prelacies', 'pl. ', 'of Prelacy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prelacy', 'n.', 'The office or dignity of a prelate; church government by\n   prelates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prelacy', 'n.', 'The order of prelates, taken collectively; the body of\n   ecclesiastical dignitaries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prelal', 'a.', 'Of or pertaining to printing; typographical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prelate', 'n.', 'A clergyman of a superior order, as an archbishop or a\n   bishop, having authority over the lower clergy; a dignitary of the\n   church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prelate', 'v. i.', 'To act as a prelate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prelateity', 'n.', 'Prelacy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prelateship', 'n.', 'The office of a prelate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prelatess', 'n.', 'A woman who is a prelate; the wife of a prelate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prelatial', 'a.', 'Prelatical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prelatic', 'a.', 'Alt. of Prelatical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prelatical', 'a.', 'Of or pertaining to prelates or prelacy; as, prelatical\n   authority.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prelatically', 'adv.', 'In a prelatical manner; with reference to prelates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prelation', 'n.', 'The setting of one above another; preference.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prelatism', 'n.', 'Prelacy; episcopacy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prelatist', 'n.', 'One who supports of advocates prelacy, or the government\n   of the church by prelates; hence, a high-churchman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prelatized', 'imp. & p. p.', 'of Prelatize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prelatizing', 'p. pr. & vb. n.', 'of Prelatize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prelatize', 'v. t.', 'To bring under the influence of prelacy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prelatize', 'v. i.', 'To uphold or encourage prelacy; to exercise\n   prelatical functions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prelatry', 'n.', 'Prelaty; prelacy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prelature', 'n.', 'Alt. of Prelatureship');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prelatureship', 'n.', 'The state or dignity of a prelate; prelacy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prelaty', 'n.', 'Prelacy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prelected', 'imp. & p. p.', 'of Prelect');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prelecting', 'p. pr. & vb. n.', 'of Prelect');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prelect', 'v. t.', 'To read publicly, as a lecture or discourse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prelect', 'v. i.', 'To discourse publicly; to lecture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prelection', 'n.', 'A lecture or discourse read in public or to a select\n   company.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prelector', 'n.', 'A reader of lectures or discourses; a lecturer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prelibation', 'n.', 'A tasting beforehand, or by anticipation; a foretaste;\n   as, a prelibation of heavenly bliss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prelibation', 'n.', 'A pouring out, or libation, before tasting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preliminarily', 'adv.', 'In a preliminary manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preliminary', 'a.', 'Introductory; previous; preceding the main discourse\n   or business; prefatory; as, preliminary observations to a discourse or\n   book; preliminary articles to a treaty; preliminary measures;\n   preliminary examinations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preliminaries', 'pl. ', 'of Preliminary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preliminary', 'n.', 'That which precedes the main discourse, work, design,\n   or business; something introductory or preparatory; as, the\n   preliminaries to a negotiation or duel; to take ones preliminaries the\n   year before entering college.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prelimit', 'v. t.', 'To limit previously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prelook', 'v. i.', 'To look forward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prelude', 'v. t.', 'An introductory performance, preceding and preparing\n   for the principal matter; a preliminary part, movement, strain, etc.;\n   especially (Mus.), a strain introducing the theme or chief subject; a\n   movement introductory to a fugue, yet independent; -- with recent\n   composers often synonymous with overture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preluded', 'imp. & p. p.', 'of Prelude');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preluding', 'p. pr. & vb. n.', 'of Prelude');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prelude', 'v. i.', 'To play an introduction or prelude; to give a prefatory\n   performance; to serve as prelude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prelude', 'v. t.', 'To introduce with a previous performance; to play or\n   perform a prelude to; as, to prelude a concert with a lively air.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prelude', 'v. t.', 'To serve as prelude to; to precede as introductory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preluder', 'n.', 'One who, or that which, preludes; one who plays a\n   prelude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preludial', 'a.', 'Of or pertaining to a prelude; of the nature of a\n   prelude; introductory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preludious', 'a.', 'Preludial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prelumbar', 'a.', 'Situated immediately in front of the loins; -- applied\n   to the dorsal part of the abdomen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prelusive', 'a.', 'Of the nature of a prelude; introductory; indicating\n   that something of a like kind is to follow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prelusorily', 'adv.', 'In a prelusory way.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prelusory', 'a.', 'Introductory; prelusive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Premature', 'a.', 'Mature or ripe before the proper time; as, the premature\n   fruits of a hotbed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Premature', 'a.', 'Happening, arriving, existing, or performed before the\n   proper or usual time; adopted too soon; too early; untimely; as, a\n   premature fall of snow; a premature birth; a premature opinion;\n   premature decay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Premature', 'a.', 'Arriving or received without due authentication or\n   evidence; as, a premature report.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prematurity', 'n.', 'The quality or state of being premature; early, or\n   untimely, ripeness; as, the prematurity of genius.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Premaxillae', 'pl. ', 'of Premaxilla');
  INSERT INTO words (word, word_type, description) VALUES  (    'Premaxilla', 'n.', 'A bone on either side of the middle line between the\n   nose and mouth, forming the anterior part of each half of the upper\n   jawbone; the intermaxilla. In man the premaxillae become united and\n   form the incisor part of the maxillary bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Premaxillary', 'a.', 'Situated in front of the maxillary bones; pertaining\n   to the premaxillae; intermaxillary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Premaxillary', 'n.', 'A premaxilla.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Premediate', 'v. t.', 'To advocate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Premeditated', 'imp. & p. p.', 'of Premeditate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Premeditating', 'p. pr. & vb. n.', 'of Premeditate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Premeditate', 'v. t.', 'To think on, and revolve in the mind, beforehand;\n   to contrive and design previously; as, to premeditate robbery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Premeditate', 'v. i.', 'To think, consider, deliberate, or revolve in the\n   mind, beforehand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Premeditate', 'a.', 'Premeditated; deliberate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Premeditately', 'adv.', 'With premeditation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Premeditation', 'n.', 'The act of meditating or contriving beforehand;\n   previous deliberation; forethought.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Premerit', 'v. t.', 'To merit or deserve beforehand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Premial', 'a.', 'Alt. of Premiant');
  INSERT INTO words (word, word_type, description) VALUES  (    'Premiant', 'a.', 'Serving to reward; rewarding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Premices', 'n. pl.', 'First fruits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Premier', 'a.', 'First; chief; principal; as, the premier place; premier\n   minister.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Premier', 'a.', 'Most ancient; -- said of the peer bearing the oldest title\n   of his degree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Premier', 'n.', 'The first minister of state; the prime minister.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Premiership', 'n.', 'The office of the premier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Premillennial', 'a.', 'Previous to the millennium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Premious', 'a.', 'Rich in gifts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Premises', 'pl. ', 'of Premise');
  INSERT INTO words (word, word_type, description) VALUES  (    'Premise', 'n.', 'A proposition antecedently supposed or proved; something\n   previously stated or assumed as the basis of further argument; a\n   condition; a supposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Premise', 'n.', 'Either of the first two propositions of a syllogism, from\n   which the conclusion is drawn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Premise', 'n.', 'Matters previously stated or set forth; esp., that part in\n   the beginning of a deed, the office of which is to express the grantor\n   and grantee, and the land or thing granted or conveyed, and all that\n   precedes the habendum; the thing demised or granted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Premise', 'n.', 'A piece of real estate; a building and its adjuncts; as,\n   to lease premises; to trespass on anothers premises.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Premised', 'imp. & p. p.', 'of Premise');
  INSERT INTO words (word, word_type, description) VALUES  (    'Premising', 'p. pr. & vb. n.', 'of Premise');
  INSERT INTO words (word, word_type, description) VALUES  (    'Premise', 'n.', 'To send before the time, or beforehand; hence, to cause to\n   be before something else; to employ previously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Premise', 'n.', 'To set forth beforehand, or as introductory to the main\n   subject; to offer previously, as something to explain or aid in\n   understanding what follows; especially, to lay down premises or first\n   propositions, on which rest the subsequent reasonings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Premise', 'v. i.', 'To make a premise; to set forth something as a premise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Premiss', 'n.', 'Premise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Premit', 'v. t.', 'To premise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Premiums', 'pl. ', 'of Premium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Premium', 'n.', 'A reward or recompense; a prize to be won by being before\n   another, or others, in a competition; reward or prize to be adjudged; a\n   bounty; as, a premium for good behavior or scholarship, for\n   discoveries, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Premium', 'n.', 'Something offered or given for the loan of money; bonus;\n   -- sometimes synonymous with interest, but generally signifying a sum\n   in addition to the capital.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Premium', 'n.', 'A sum of money paid to underwriters for insurance, or for\n   undertaking to indemnify for losses of any kind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Premium', 'n.', 'A sum in advance of, or in addition to, the nominal or par\n   value of anything; as, gold was at a premium; he sold his stock at a\n   premium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Premolar', 'a.', 'Situated in front of the molar teeth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Premolar', 'n.', 'An anterior molar tooth which has replaced a deciduous\n   molar. See Tooth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Premonished', 'imp. & p. p.', 'of Premonish');
  INSERT INTO words (word, word_type, description) VALUES  (    'Premonishing', 'p. pr. & vb. n.', 'of Premonish');
  INSERT INTO words (word, word_type, description) VALUES  (    'Premonish', 'v. t.', 'To forewarn; to admonish beforehand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Premonishment', 'n.', 'Previous warning or admonition; forewarning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Premonition', 'n.', 'Previous warning, notice, or information; forewarning;\n   as, a premonition of danger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Premonitor', 'n.', 'One who, or that which, gives premonition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Premonitory', 'a.', 'Giving previous warning or notice; as, premonitory\n   symptoms of disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Premonstrant', 'n.', 'A Premonstratensian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Premonstrate', 'v. t.', 'To show beforehand; to foreshow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Premonstratensian', 'n.', 'One of a religious order of regular canons\n   founded by St. Norbert at Premontre, in France, in 1119. The members of\n   the order are called also White Canons, Norbertines, and Premonstrants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Premonstration', 'n.', 'A showing beforehand; foreshowing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Premonstrator', 'n.', 'One who, or that which, premonstrates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Premorse', 'a.', 'Terminated abruptly, or as it bitten off.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Premosaic', 'a.', 'Relating to the time before Moses; as, premosaic\n   history.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Premotion', 'n.', 'Previous motion or excitement to action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Premunire', 'n.', 'See Praemunire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Premunite', 'v. t.', 'To fortify beforehand; to guard against objection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Premunition', 'n.', 'The act of fortifying or guarding against objections.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Premunitory', 'a.', 'Of or pertaining to a premunire; as, a premunitory\n   process.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prenasal', 'a.', 'Situated in front of the nose, or in front of the nasal\n   chambers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prenatal', 'a.', 'Being or happening before birth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prender', 'n.', 'The power or right of taking a thing before it is offered.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prenomen', 'n.', 'See Praenomen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prenominal', 'a.', 'Serving as a prefix in a compound name.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prenominate', 'a.', 'Forenamed; named beforehand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prenominate', 'v. t.', 'To forename; to name beforehand; to tell by name\n   beforehand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prenomination', 'n.', 'The act of prenominating; privilege of being named\n   first.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prenostic', 'n.', 'A prognostic; an omen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prenote', 'v. t.', 'To note or designate beforehand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prenotion', 'n.', 'A notice or notion which precedes something else in\n   time; previous notion or thought; foreknowledge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prensation', 'n.', 'The act of seizing with violence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prentice', 'n.', 'An apprentice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prenticehood', 'n.', 'Apprenticehood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prenticeship', 'n.', 'Apprenticeship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prenunciation', 'n.', 'The act of announcing or proclaiming beforehand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prenuncious', 'a.', 'Announcing beforehand; presaging.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preoblongata', 'n.', 'The anterior part of the medulla oblongata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preobtain', 'v. t.', 'To obtain beforehand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preoccupancy', 'n.', 'The act or right of taking possession before another;\n   as, the preoccupancy of wild land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preoccupate', 'v. t.', 'To anticipate; to take before.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preoccupate', 'v. t.', 'To prepossess; to prejudice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preoccupation', 'n.', 'The act of preoccupying, or taking possession of\n   beforehand; the state of being preoccupied; prepossession.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preoccupation', 'n.', 'Anticipation of objections.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preoccupied', 'imp. & p. p.', 'of Preoccupy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preoccupying', 'p. pr. & vb. n.', 'of Preoccupy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preoccupy', 'v. t.', 'To take possession of before another; as, to\n   preoccupy a country not before held.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preoccupy', 'v. t.', 'To prepossess; to engage, occupy, or engross the\n   attention of, beforehand; hence, to prejudice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preocular', 'a.', 'Placed just in front of the eyes, as the antennae of\n   certain insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preocular', 'n.', 'One of the scales just in front of the eye of a reptile\n   or fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preominate', 'v. t.', 'To ominate beforehand; to portend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preopercular', 'a.', 'Situated in front of the operculum; pertaining to the\n   preoperculum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preopercular', 'n.', 'The preoperculum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preoperculum', 'n.', 'The anterior opercular bone in fishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preopinion', 'n.', 'Opinion previously formed; prepossession; prejudice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preoption', 'n.', 'Right of first choice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preoral', 'a.', 'Situated in front of, or anterior to, the mouth; as,\n   preoral bands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preorbital', 'a.', 'Situated in front or the orbit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preordain', 'v. t.', 'To ordain or appoint beforehand: to predetermine: to\n   foreordain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preorder', 'v. t.', 'To order to arrange beforehand; to foreordain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preordinance', 'n.', 'Antecedent decree or determination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preordinate', 'a.', 'Preordained.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preordination', 'n.', 'The act of foreordaining: previous determination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preparable', 'a.', 'Capable of being prepared.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preparation', 'n.', 'The act of preparing or fitting beforehand for a\n   particular purpose, use, service, or condition; previous arrangement or\n   adaptation; a making ready; as, the preparation of land for a crop of\n   wheat; the preparation of troops for a campaign.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preparation', 'n.', 'The state of being prepared or made ready;\n   preparedness; readiness; fitness; as, a nation in good preparation for\n   war.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preparation', 'n.', 'That which makes ready, prepares the way, or\n   introduces; a preparatory act or measure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preparation', 'n.', 'That which is prepared, made, or compounded by a\n   certain process or for a particular purpose; a combination.\n   Specifically: (a) Any medicinal substance fitted for use. (b) Anything\n   treated for preservation or examination as a specimen. (c) Something\n   prepared for use in cookery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preparation', 'n.', 'An army or fleet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preparation', 'n.', 'The holding over of a note from one chord into the\n   next chord, where it forms a temporary discord, until resolved in the\n   chord that follows; the anticipation of a discordant note in the\n   preceding concord, so that the ear is prepared for the shock. See\n   Suspension.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preparation', 'n.', 'Accomplishment; qualification.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preparative', 'a.', 'Tending to prepare or make ready; having the power of\n   preparing, qualifying, or fitting; preparatory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preparative', 'n.', 'That which has the power of preparing, or previously\n   fitting for a purpose; that which prepares.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preparative', 'n.', 'That which is done in the way of preparation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preparatively', 'adv.', 'By way of preparation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preparator', 'n.', 'One who prepares beforehand, as subjects for\n   dissection, specimens for preservation in collections, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preparatory', 'a.', 'Preparing the way for anything by previous measures of\n   adaptation; antecedent and adapted to what follows; introductory;\n   preparative; as, a preparatory school; a preparatory condition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prepare/', 'imp. & p. p.', 'of Prepare');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preparing', 'p. pr. & vb. n.', 'of Prepare');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prepare', 'v. t.', 'To fit, adapt, or qualify for a particular purpose or\n   condition; to make ready; to put into a state for use or application;\n   as, to prepare ground for seed; to prepare a lesson.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prepare', 'v. t.', 'To procure as suitable or necessary; to get ready; to\n   provide; as, to prepare ammunition and provisions for troops; to\n   prepare ships for defence; to prepare an entertainment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prepare', 'v. i.', 'To make all things ready; to put things in order; as,\n   to prepare for a hostile invasion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prepare', 'v. i.', 'To make ones self ready; to get ready; to take the\n   necessary previous measures; as, to prepare for death.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prepare', 'n.', 'Preparation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prepared', 'a.', 'Made fit or suitable; adapted; ready; as, prepared food;\n   prepared questions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preparer', 'n.', 'One who, or that which, prepares, fits, or makes ready.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prepaid', 'imp. & p. p.', 'of Prepay');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prepaying', 'p. pr. & vb. n.', 'of Prepay');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prepay', 'v. t.', 'To pay in advance, or beforehand; as, to prepay postage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prepayment', 'n.', 'Payment in advance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prepenial', 'a.', 'Situated in front of, or anterior to, the penis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prepense', 'v. t.', 'To weigh or consider beforehand; to premeditate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prepense', 'v. i.', 'To deliberate beforehand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prepense', 'v. t.', 'Devised, contrived, or planned beforehand;\n   preconceived; premeditated; aforethought; -- usually placed after the\n   word it qualifies; as, malice prepense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prepensely', 'adv.', 'In a premeditated manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prepollence', 'n.', 'Alt. of Prepollency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prepollency', 'n.', 'The quality or state of being prepollent; superiority\n   of power; predominance; prevalence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prepollent', 'a.', 'Having superior influence or power; prevailing;\n   predominant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prepollices', 'pl. ', 'of Prepollent');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prepollent', 'n.', 'An extra first digit, or rudiment of a digit, on the\n   preaxial side of the pollex.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preponder', 'v. t.', 'To preponderate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preponderance', 'n.', 'Alt. of Preponderancy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preponderancy', 'n.', 'The quality or state of being preponderant;\n   superiority or excess of weight, influence, or power, etc.; an\n   outweighing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preponderancy', 'n.', 'The excess of weight of that part of a canon behind\n   the trunnions over that in front of them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preponderant', 'a.', 'Preponderating; outweighing; overbalancing; -- used\n   literally and figuratively; as, a preponderant weight; of preponderant\n   importance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preponderated', 'imp. & p. p.', 'of Preponderate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preponderating', 'p. pr. & vb. n.', 'of Preponderate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preponderate', 'v. t.', 'To outweigh; to overpower by weight; to exceed in\n   weight; to overbalance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preponderate', 'v. t.', 'To overpower by stronger or moral power.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preponderate', 'v. t.', 'To cause to prefer; to incline; to decide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preponderate', 'v. i.', 'To exceed in weight; hence, to incline or descend,\n   as the scale of a balance; figuratively, to exceed in influence, power,\n   etc.; hence; to incline to one side; as, the affirmative side\n   preponderated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preponderatingly', 'adv.', 'In a preponderating manner; preponderantly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preponderation', 'n.', 'The act or state of preponderating; preponderance;\n   as, a preponderation of reasons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prepose', 'v. t.', 'To place or set before; to prefix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preposition', 'n.', 'A word employed to connect a noun or a pronoun, in an\n   adjectival or adverbial sense, with some other word; a particle used\n   with a noun or pronoun (in English always in the objective case) to\n   make a phrase limiting some other word; -- so called because usually\n   placed before the word with which it is phrased; as, a bridge of iron;\n   he comes from town; it is good for food; he escaped by running.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preposition', 'n.', 'A proposition; an exposition; a discourse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prepositional', 'a.', 'Of or pertaining to a preposition; of the nature of\n   a preposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prepositive', 'a.', 'Put before; prefixed; as, a prepositive particle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prepositive', 'n.', 'A prepositive word.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prepositor', 'n.', 'A scholar appointed to inspect other scholars; a\n   monitor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prepositure', 'n.', 'The office or dignity of a provost; a provostship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prepossessed', 'imp. & p. p.', 'of Prepossess');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prepossessing', 'p. pr. & vb. n.', 'of Prepossess');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prepossess', 'v. t.', 'To preoccupy, as ground or land; to take previous\n   possession of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prepossess', 'v. t.', 'To preoccupy, as the mind or heart, so as to\n   preclude other things; hence, to bias or prejudice; to give a previous\n   inclination to, for or against anything; esp., to induce a favorable\n   opinion beforehand, or at the outset.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prepossessing', 'a.', 'Tending to invite favor; attracting confidence,\n   favor, esteem, or love; attractive; as, a prepossessing manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prepossession', 'n.', 'Preoccupation; prior possession.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prepossession', 'n.', 'Preoccupation of the mind by an opinion, or\n   impression, already formed; preconceived opinion; previous impression;\n   bias; -- generally, but not always, used in a favorable sense; as, the\n   prepossessions of childhood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prepossessor', 'n.', 'One who possesses, or occupies, previously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preposterous', 'a.', 'Having that first which ought to be last; inverted in\n   order.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preposterous', 'a.', 'Contrary to nature or reason; not adapted to the end;\n   utterly and glaringly foolish; unreasonably absurd; perverted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prepostor', 'n.', 'See Prepositor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prepotency', 'n.', 'The quality or condition of being prepotent;\n   predominance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prepotency', 'n.', 'The capacity, on the part of one of the parents, as\n   compared with the other, to transmit more than his or her own share of\n   characteristics to their offspring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prepotent', 'a.', 'Very powerful; superior in force, influence, or\n   authority; predominant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prepotent', 'a.', 'Characterized by prepotency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preprovide', 'v. t.', 'To provide beforehand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prepubic', 'a.', 'Situated in front of, or anterior to, the pubis;\n   pertaining to the prepubis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prepubis', 'n.', 'A bone or cartilage, of some animals, situated in the\n   middle line in front of the pubic bones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prepuce', 'n.', 'The foreskin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preputial', 'a.', 'Of or pertaining to the prepuce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preraphaelism', 'n.', 'Alt. of Preraphaelitism');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preraphaelitism', 'n.', 'The doctrine or practice of a school of modern\n   painters who profess to be followers of the painters before Raphael.\n   Its adherents advocate careful study from nature, delicacy and\n   minuteness of workmanship, and an exalted and delicate conception of\n   the subject.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preraphaelite', 'a.', 'Of or pertaining to the style called\n   preraphaelitism; as, a preraphaelite figure; a preraphaelite landscape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preraphaelite', 'n.', 'One who favors or practices art as it was before\n   Raphael; one who favors or advocates preraphaelitism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preregnant', 'n.', 'One who reigns before another; a sovereign predecessor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preremote', 'a.', 'More remote in previous time or prior order.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prerequire', 'v. t.', 'To require beforehand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prerequisite', 'a.', 'Previously required; necessary as a preliminary to\n   any proposed effect or end; as, prerequisite conditions of success.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prerequisite', 'n.', 'Something previously required, or necessary to an end\n   or effect proposed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preresolved', 'imp. & p. p.', 'of Preresolve');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preresolving', 'p. pr. & vb. n.', 'of Preresolve');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preresolve', 'v. t. & i.', 'To resolve beforehand; to predetermine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prerogative', 'n.', 'An exclusive or peculiar privilege; prior and\n   indefeasible right; fundamental and essential possession; -- used\n   generally of an official and hereditary right which may be asserted\n   without question, and for the exercise of which there is no\n   responsibility or accountability as to the fact and the manner of its\n   exercise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prerogative', 'n.', 'Precedence; preeminence; first rank.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prerogatived', 'a.', 'Endowed with a prerogative, or exclusive privilege.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prerogatively', 'adv.', 'By prerogative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presage', 'v. t.', 'Something which foreshows or portends a future event; a\n   prognostic; an omen; an augury.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presage', 'v. t.', 'Power to look the future, or the exercise of that\n   power; foreknowledge; presentiment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presaged', 'imp. & p. p.', 'of Presage');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presaging', 'p. pr. & vb. n.', 'of Presage');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presage', 'v. t.', 'To have a presentiment of; to feel beforehand; to\n   foreknow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presage', 'v. t.', 'To foretell; to predict; to foreshow; to indicate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presage', 'v. i.', 'To form or utter a prediction; -- sometimes used with\n   of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presageful', 'a.', 'Full of presages; ominous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presagement', 'n.', 'The act or art of presaging; a foreboding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presagement', 'n.', 'That which is presaged, or foretold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presager', 'n.', 'One who, or that which, presages; a foreteller; a\n   foreboder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presagious', 'a.', 'Foreboding; ominous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presbyope', 'n.', 'One who has presbyopia; a farsighted person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presbyopia', '', 'A defect of vision consequent upon advancing age. It is\n   due to rigidity of the crystalline lens, which produces difficulty of\n   accommodation and recession of the near point of vision, so that\n   objects very near the eyes can not be seen distinctly without the use\n   of convex glasses. Called also presbytia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presbyopic', 'a.', 'Affected by presbyopia; also, remedying presbyopia;\n   farsighted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presbyopy', 'n.', 'See Presbyopia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presbyte', 'n.', 'Same as Presbyope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presbyter', 'n.', 'An elder in the early Christian church. See 2d Citation\n   under Bishop, n., 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presbyter', 'n.', 'One ordained to the second order in the ministry; --\n   called also priest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presbyter', 'n.', 'A member of a presbytery whether lay or clerical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presbyter', 'n.', 'A Presbyterian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presbyteral', 'a.', 'Of or pertaining to a presbyter or presbytery;\n   presbyterial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presbyterate', 'n.', 'A presbytery; also, presbytership.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presbyteress', 'n.', 'A female presbyter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presbyterial', 'a.', 'Presbyterian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presbyterian', 'a.', 'Of or pertaining to a presbyter, or to ecclesiastical\n   government by presbyters; relating to those who uphold church\n   government by presbyters; also, to the doctrine, discipline, and\n   worship of a communion so governed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presbyterian', 'n.', 'One who maintains the validity of ordination and\n   government by presbyters; a member of the Presbyterian church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presbyterianism', 'n.', 'That form of church government which invests\n   presbyters with all spiritual power, and admits no prelates over them;\n   also, the faith and polity of the Presbyterian churches, taken\n   collectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presbyterium', 'n.', 'Same as Presbytery, 4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presbytership', 'n.', 'The office or station of a presbyter; presbyterate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presbyteries', 'pl. ', 'of Presbytery');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presbytery', 'n.', 'A body of elders in the early Christian church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presbytery', 'n.', 'A judicatory consisting of all the ministers within a\n   certain district, and one layman, who is a ruling elder, from each\n   parish or church, commissioned to represent the church in conjunction\n   with the pastor. This body has a general jurisdiction over the churches\n   under its care, and next below the provincial synod in authority.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presbytery', 'n.', 'The Presbyterian religion of polity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presbytery', 'n.', 'That part of the church reserved for the officiating\n   priest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presbytery', 'n.', 'The residence of a priest or clergyman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presbytia', 'n.', 'Presbyopia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presbytic', 'a.', 'Same as Presbyopic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presbytism', 'n.', 'Presbyopia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presscapula', 'n.', 'The part of the scapula in front of, or above, the\n   spine, or mesoscapula.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prescapular', 'a.', 'Of or pertaining to the prescapula; supraspinous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prescience', 'n.', 'Knowledge of events before they take place; foresight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prescient', 'a.', 'Having knowledge of coming events; foreseeing; conscious\n   beforehand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presciently', 'adv.', 'With prescience or foresight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prescind', 'v. t.', 'To cut off; to abstract.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prescind', 'v. t.', 'To consider by a separate act of attention or\n   analysis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presciendent', 'a.', 'Foreknowing; having foreknowledge; as, prescious of\n   ills.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prescribed', 'imp. & p. p.', 'of Prescribe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prescribing', 'p. pr & vb. n.', 'of Prescribe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prescribe', 'v. t.', 'To lay down authoritatively as a guide, direction, or\n   rule of action; to impose as a peremptory order; to dictate; to\n   appoint; to direct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prescribe', 'v. t.', 'To direct, as a remedy to be used by a patient; as,\n   the doctor prescribed quinine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prescribe', 'v. i.', 'To give directions; to dictate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prescribe', 'v. i.', 'To influence by long use');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prescribe', 'v. i.', 'To write or to give medical directions; to indicate\n   remedies; as, to prescribe for a patient in a fever.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prescribe', 'v. i.', 'To claim by prescription; to claim a title to a thing\n   on the ground of immemorial use and enjoyment, that is, by a custom\n   having the force of law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prescriber', 'n.', 'One who prescribes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prescript', 'a.', 'Directed; prescribed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prescript', 'n.', 'Direction; precept; model prescribed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prescript', 'n.', 'A medical prescription.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prescriptibility', 'n.', 'The quality or state of being prescriptible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prescriptible', 'a.', 'Depending on, or derived from, prescription; proper\n   to be prescribed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prescription', 'n.', 'The act of prescribing, directing, or dictating;\n   direction; precept; also, that which is prescribed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prescription', 'n.', 'A direction of a remedy or of remedies for a disease,\n   and the manner of using them; a medical recipe; also, a prescribed\n   remedy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prescription', 'n.', 'A prescribing for title; the claim of title to a\n   thing by virtue immemorial use and enjoyment; the right or title\n   acquired by possession had during the time and in the manner fixed by\n   law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prescriptive', 'a.', 'Consisting in, or acquired by, immemorial or\n   long-continued use and enjoyment; as, a prescriptive right of title;\n   pleading the continuance and authority of long custom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prescriptively', 'adv.', 'By prescription.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prescuta', 'pl. ', 'of Prescutum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prescutum', 'n.', 'The first of the four pieces composing the dorsal part,\n   or tergum, of a thoracic segment of an insect. It is usually small and\n   inconspicuous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preseance', 'n.', 'Priority of place in sitting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preselect', 'v. t.', 'To select beforehand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presence', 'n.', 'The state of being present, or of being within sight or\n   call, or at hand; -- opposed to absence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presence', 'n.', 'The place in which one is present; the part of space\n   within ones ken, call, influence, etc.; neighborhood without the\n   intervention of anything that forbids intercourse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presence', 'n.', 'Specifically, neighborhood to the person of one of\n   superior of exalted rank; also, presence chamber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presence', 'n.', 'The whole of the personal qualities of an individual;\n   person; personality; especially, the person of a superior, as a\n   sovereign.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presence', 'n.', 'An assembly, especially of person of rank or nobility;\n   noble company.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presence', 'n.', 'Port, mien; air; personal appearence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presensation', 'n.', 'Previous sensation, notion, or idea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presension', 'n.', 'Previous perception.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Present', 'a.', 'Being at hand, within reach or call, within certain\n   contemplated limits; -- opposed to absent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Present', 'a.', 'Now existing, or in process; begun but not ended; now in\n   view, or under consideration; being at this time; not past or future;\n   as, the present session of Congress; the present state of affairs; the\n   present instance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Present', 'a.', 'Not delayed; immediate; instant; coincident.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Present', 'a.', 'Ready; quick in emergency; as a present wit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Present', 'a.', 'Favorably attentive; propitious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Present', 'a.', 'Present time; the time being; time in progress now, or at\n   the moment contemplated; as, at this present.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Present', 'a.', 'Present letters or instrument, as a deed of conveyance, a\n   lease, letter of attorney, or other writing; as in the phrase, \" Know\n   all men by these presents,\" that is, by the writing itself, \" per has\n   literas praesentes; \" -- in this sense, rarely used in the singular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Present', 'a.', 'A present tense, or the form of the verb denoting the\n   present tense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presented', 'imp. & p. p.', 'of Present');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presenting', 'p. pr. & vb. n.', 'of Present');
  INSERT INTO words (word, word_type, description) VALUES  (    'Present', 'a.', 'To bring or introduce into the presence of some one,\n   especially of a superior; to introduce formally; to offer for\n   acquaintance; as, to present an envoy to the king; (with the reciprocal\n   pronoun) to come into the presence of a superior.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Present', 'a.', 'To exhibit or offer to view or notice; to lay before ones\n   perception or cognizance; to set forth; to present a fine appearance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Present', 'a.', 'To pass over, esp. in a ceremonious manner; to give in\n   charge or possession; to deliver; to make over.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Present', 'a.', 'To make a gift of; to bestow; to give, generally in a\n   formal or ceremonious manner; to grant; to confer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Present', 'a.', 'Hence: To endow; to bestow a gift upon; to favor, as with\n   a donation; also, to court by gifts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Present', 'a.', 'To present; to personate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Present', 'a.', 'To nominate to an ecclesiastical benefice; to offer to the\n   bishop or ordinary as a candidate for institution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Present', 'a.', 'To nominate for support at a public school or other\n   institution .');
  INSERT INTO words (word, word_type, description) VALUES  (    'Present', 'a.', 'To lay before a public body, or an official, for\n   consideration, as before a legislature, a court of judicature, a\n   corporation, etc.; as, to present a memorial, petition, remonstrance,\n   or indictment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Present', 'a.', 'To lay before a court as an object of inquiry; to give\n   notice officially of, as a crime of offence; to find or represent\n   judicially; as, a grand jury present certain offenses or nuisances, or\n   whatever they think to be public injuries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Present', 'a.', 'To bring an indictment against .');
  INSERT INTO words (word, word_type, description) VALUES  (    'Present', 'a.', 'To aim, point, or direct, as a weapon; as, to present a\n   pistol or the point of a sword to the breast of another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Present', 'v. i.', 'To appear at the mouth of the uterus so as to be\n   perceptible to the finger in vaginal examination; -- said of a part of\n   an infant during labor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Present', 'n.', 'Anything presented or given; a gift; a donative; as, a\n   Christmas present.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Present', 'n.', 'The position of a soldier in presenting arms; as, to stand\n   at present.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presentable', 'a.', 'Capable or admitting of being presented; suitable to\n   be exhibited, represented, or offered; fit to be brought forward or set\n   forth; hence, fitted to be introduced to another, or to go into\n   society; as, ideas that are presentable in simple language; she is not\n   presentable in such a gown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presentable', 'a.', 'Admitting of the presentation of a clergiman; as, a\n   church presentable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presentaneous', 'a.', 'Ready; quick; immediate in effect; as, presentaneous\n   poison.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presentation', 'n.', 'The act of presenting, or the state of being\n   presented; a setting forth; an offering; bestowal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presentation', 'n.', 'exhibition; representation; display; appearance;\n   semblance; show.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presentation', 'n.', 'That which is presented or given; a present; a gift,\n   as, the picture was a presentation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presentation', 'n.', 'The act of offering a clergyman to the bishop or\n   ordinary for institution in a benefice; the right of presenting a\n   clergyman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presentation', 'n.', 'The particular position of the child during labor\n   relatively to the passage though which it is to be brought forth; --\n   specifically designated by the part which first appears at the mouth of\n   the uterus; as, a breech presentation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presentative', 'a.', 'Having the right of presentation, or offering a\n   clergyman to the bishop for institution; as, advowsons are\n   presentative, collative, or donative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presentative', 'a.', 'Admitting the presentation of a clergyman; as, a\n   presentative parsonage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presentative', 'a.', 'Capable of being directly known by, or presented to,\n   the mind; intuitive; directly apprehensible, as objects; capable of\n   apprehending, as faculties.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presentee', 'v. t.', 'One to whom something is presented; also, one who is\n   presented; specifically (Eccl.), one presented to benefice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presenter', 'n.', 'One who presents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presential', 'a.', 'Implying actual presence; present, immediate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presentiality', 'n.', 'State of being actually present.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presentiate', 'v. t.', 'To make present.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presentient', 'a.', 'Feeling or perceiving beforehand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presentific', 'a.', 'Making present.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presentifical', 'a.', 'Presentific.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presentiment', 'n.', 'Previous sentiment, conception, or opinion; previous\n   apprehension; especially, an antecedent impression or conviction of\n   something unpleasant, distressing, or calamitous, about to happen;\n   anticipation of evil; foreboding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presentimental', 'a.', 'Of nature of a presentiment; foreboding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presention', 'n.', 'See Presension.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presentive', 'a.', 'Bringing a conception or notion directly before the\n   mind; presenting an object to the memory of imagination; --\n   distinguished from symbolic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presently', 'adv.', 'At present; at this time; now.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presently', 'adv.', 'At once; without delay; forthwith; also, less\n   definitely, soon; shortly; before long; after a little while; by and\n   by.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presently', 'adv.', 'With actual presence; actually .');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presentment', 'n.', 'The act of presenting, or the state of being\n   presented; presentation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presentment', 'n.', 'Setting forth to view; delineation; appearance;\n   representation; exhibition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presentment', 'n.', 'The notice taken by a grand jury of any offence from\n   their own knowledge or observation, without any bill of indictment laid\n   before them, as, the presentment of a nuisance, a libel, or the like;\n   also, an inquisition of office and indictment by a grand jury; an\n   official accusation presented to a tribunal by the grand jury in an\n   indictment, or the act of offering an indictment; also, the indictment\n   itself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presentment', 'n.', 'The official notice (formerly required to be given in\n   court) of the surrender of a copyhold estate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presentness', 'n.', 'The quality or state of being present; presence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presentoir', 'n.', 'An ornamental tray, dish, or the like, used as a\n   salver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preservable', 'a.', 'Capable of being preserved; admitting of preservation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preservation', 'n.', 'The act or process of preserving, or keeping safe;\n   the state of being preserved, or kept from injury, destruction, or\n   decay; security; safety; as, preservation of life, fruit, game, etc.; a\n   picture in good preservation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preservative', 'a.', 'Having the power or quality of preserving; tending to\n   preserve, or to keep from injury, decay, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preservative', 'n.', 'That which preserves, or has the power of preserving;\n   a presevative agent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preservatory', 'a.', 'Preservative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preservatories', 'pl. ', 'of Preservatory');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preservatory', 'n.', 'A preservative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preservatory', 'n.', 'A room, or apparatus, in which perishable things, as\n   fruit, vegetables, etc., can be preserved without decay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preserved', 'imp. & p. p.', 'of Preserve');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preserving', 'p. pr. & vb. n.', 'of Preserve');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preserve', 'v. t.', 'To keep or save from injury or destruction; to guard\n   or defend from evil, harm, danger, etc.; to protect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preserve', 'v. t.', 'To save from decay by the use of some preservative\n   substance, as sugar, salt, etc.; to season and prepare for remaining in\n   a good state, as fruits, meat, etc.; as, to preserve peaches or grapes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preserve', 'v. t.', 'To maintain throughout; to keep intact; as, to\n   preserve appearances; to preserve silence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preserve', 'v. i.', 'To make preserves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preserve', 'v. i.', 'To protect game for purposes of sport.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preserve', 'n.', 'That which is preserved; fruit, etc., seasoned and kept\n   by suitable preparation; esp., fruit cooked with sugar; -- commonly in\n   the plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preserve', 'n.', 'A place in which game, fish, etc., are preserved for\n   purposes of sport, or for food.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preserver', 'n.', 'One who, or that which, preserves, saves, or defends,\n   from destruction, injury, or decay; esp., one who saves the life or\n   character of another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preserver', 'n.', 'One who makes preserves of fruit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preshow', 'v. t.', 'To foreshow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presided', 'imp. & p. p.', 'of Preside');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presiding', 'p. pr. & vb. n.', 'of Preside');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preside', 'v. i.', 'To be set, or to sit, in the place of authority; to\n   occupy the place of president, chairman, moderator, director, etc.; to\n   direct, control, and regulate, as chief officer; as, to preside at a\n   public meeting; to preside over the senate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preside', 'v. i.', 'To exercise superintendence; to watch over.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presidence', 'n.', 'See Presidency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presidencies', 'pl. ', 'of Presidency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presidency', 'n.', 'The function or condition of one who presides;\n   superintendence; control and care.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presidency', 'n.', 'The office of president; as, Washington was elected to\n   the presidency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presidency', 'n.', 'The term during which a president holds his office; as,\n   during the presidency of Madison.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presidency', 'n.', 'One of the three great divisions of British India, the\n   Bengal, Madras, and Bombay Presidencies, each of which had a council of\n   which its governor was president.');
  INSERT INTO words (word, word_type, description) VALUES  (    'President', 'n.', 'Precedent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'President', 'a.', 'Occupying the first rank or chief place; having the\n   highest authority; presiding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'President', 'n.', 'One who is elected or appointed to preside; a presiding\n   officer, as of a legislative body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'President', 'n.', 'The chief officer of a corporation, company,\n   institution, society, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'President', 'n.', 'The chief executive officer of the government in certain\n   republics; as, the president of the United States.');
  INSERT INTO words (word, word_type, description) VALUES  (    'President', 'n.', 'A protector; a guardian; a presiding genius.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presidential', 'a.', 'Presiding or watching over.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presidential', 'a.', 'Of or pertaining to a president; as, the presidential\n   chair; a presidential election.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presidentship', 'n.', 'The office and dignity of president; presidency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presider', 'n.', 'One who presides.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presidial', 'a.', 'Alt. of Presidiary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presidiary', 'a.', 'Of or pertaining to a garrison; having a garrison.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presidary', 'n.', 'A guard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presiding', '', 'a. & n. from Preside.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presidio', 'n.', 'A place of defense; a fortress; a garrison; a fortress; a\n   garrison or guardhouse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presignification', 'n.', 'The act of signifying or showing beforehand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presignified', 'imp. & p. p.', 'of Presignify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presignifying', 'imp. & p. p.', 'of Presignify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presignify', 'v. t.', 'To intimate or signify beforehand; to presage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presphenoid', 'a.', 'Situated in front of the sphenoid bone; of or\n   pertaining to the anterior part of the sphenoid bone (i. e., the\n   presphenoid bone).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presphenoid', 'n.', 'The presphenoid bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presphenoidal', 'a.', 'Of or pertaining to the presphenoid bone;\n   presphenoid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prespinal', 'a.', 'Prevertebral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Press', 'n.', 'An East Indian insectivore (Tupaia ferruginea). It is\n   arboreal in its habits, and has a bushy tail. The fur is soft, and\n   varies from rusty red to maroon and to brownish black.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Press', 'n.', 'To force into service, particularly into naval service; to\n   impress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Press', 'n.', 'A commission to force men into public service, particularly\n   into the navy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pressed', 'imp. & p. p.', 'of Press');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pressing', 'p. pr. & vb. n.', 'of Press');
  INSERT INTO words (word, word_type, description) VALUES  (    'Press', 'v.', 'To urge, or act upon, with force, as weight; to act upon by\n   pushing or thrusting, in distinction from pulling; to crowd or compel\n   by a gradual and continued exertion; to bear upon; to squeeze; to\n   compress; as, we press the ground with the feet when we walk; we press\n   the couch on which we repose; we press substances with the hands,\n   fingers, or arms; we are pressed in a crowd.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Press', 'v.', 'To squeeze, in order to extract the juice or contents of; to\n   squeeze out, or express, from something.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Press', 'v.', 'To squeeze in or with suitable instruments or apparatus, in\n   order to compact, make dense, or smooth; as, to press cotton bales,\n   paper, etc.; to smooth by ironing; as, to press clothes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Press', 'v.', 'To embrace closely; to hug.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Press', 'v.', 'To oppress; to bear hard upon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Press', 'v.', 'To straiten; to distress; as, to be pressed with want or\n   hunger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Press', 'v.', 'To exercise very powerful or irresistible influence upon or\n   over; to constrain; to force; to compel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Press', 'v.', 'To try to force (something upon some one); to urge or\n   inculcate with earnestness or importunity; to enforce; as, to press\n   divine truth on an audience.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Press', 'v.', 'To drive with violence; to hurry; to urge on; to ply hard;\n   as, to press a horse in a race.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Press', 'v. i.', 'To exert pressure; to bear heavily; to push, crowd, or\n   urge with steady force.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Press', 'v. i.', 'To move on with urging and crowding; to make ones way\n   with violence or effort; to bear onward forcibly; to crowd; to throng;\n   to encroach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Press', 'v. i.', 'To urge with vehemence or importunity; to exert a strong\n   or compelling influence; as, an argument presses upon the judgment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Press', 'n.', 'An apparatus or machine by which any substance or body is\n   pressed, squeezed, stamped, or shaped, or by which an impression of a\n   body is taken; sometimes, the place or building containing a press or\n   presses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Press', 'n.', 'Specifically, a printing press.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Press', 'n.', 'The art or business of printing and publishing; hence,\n   printed publications, taken collectively, more especially newspapers or\n   the persons employed in writing for them; as, a free press is a\n   blessing, a licentious press is a curse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Press', 'n.', 'An upright case or closet for the safe keeping of articles;\n   as, a clothes press.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Press', 'n.', 'The act of pressing or thronging forward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Press', 'n.', 'Urgent demands of business or affairs; urgency; as, a press\n   of engagements.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Press', 'n.', 'A multitude of individuals crowded together; / crowd of\n   single things; a throng.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presser', 'n.', 'One who, or that which, presses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pressgang', 'n.', 'See Press gang, under Press.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pressing', 'a.', 'Urgent; exacting; importunate; as, a pressing necessity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pression', 'n.', 'The act of pressing; pressure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pression', 'n.', 'An endeavor to move.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pressiroster', 'n.', 'One of a tribe of wading birds (Pressirostres)\n   including those which have a compressed beak, as the plovers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pressirostral', 'a.', 'Of or pertaining to the pressirosters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pressitant', 'a.', 'Gravitating; heavy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pressive', 'a.', 'Pressing; urgent; also, oppressive; as, pressive\n   taxation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pressly', 'adv.', 'Closely; concisely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pressmen', 'pl. ', 'of Pressman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pressman', 'n.', 'One who manages, or attends to, a press, esp. a printing\n   press.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pressman', 'n.', 'One who presses clothes; as, a tailors pressman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pressman', 'n.', 'One of a press gang, who aids in forcing men into the\n   naval service; also, one forced into the service.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pressor', 'a.', 'Causing, or giving rise to, pressure or to an increase of\n   pressure; as, pressor nerve fibers, stimulation of which excites the\n   vasomotor center, thus causing a stronger contraction of the arteries\n   and consequently an increase of the arterial blood pressure; -- opposed\n   to depressor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presspack', 'v. t.', 'To pack, or prepare for packing, by means of a press.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pressurage', 'n.', 'Pressure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pressurage', 'n.', 'The juice of the grape extracted by the press; also, a\n   fee paid for the use of a wine press.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pressure', 'n.', 'The act of pressing, or the condition of being pressed;\n   compression; a squeezing; a crushing; as, a pressure of the hand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pressure', 'n.', 'A contrasting force or impulse of any kind; as, the\n   pressure of poverty; the pressure of taxes; the pressure of motives on\n   the mind; the pressure of civilization.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pressure', 'n.', 'Affliction; distress; grievance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pressure', 'n.', 'Urgency; as, the pressure of business.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pressure', 'n.', 'Impression; stamp; character impressed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pressure', 'n.', 'The action of a force against some obstacle or opposing\n   force; a force in the nature of a thrust, distributed over a surface,\n   often estimated with reference to the upon a units area.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presswork', 'n.', 'The art of printing from the surface of type, plates, or\n   engravings in relief, by means of a press; the work so done.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prest', '', 'imp. & p. p. of Press.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prest', 'a.', 'Ready; prompt; prepared.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prest', 'a.', 'Neat; tidy; proper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prest', 'n.', 'Ready money; a loan of money.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prest', 'n.', 'A duty in money formerly paid by the sheriff on his account\n   in the exchequer, or for money left or remaining in his hands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prest', 'v. t.', 'To give as a loan; to lend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prestable', 'a.', 'Payable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prestation', 'n.', 'A payment of money; a toll or duty; also, the rendering\n   of a service.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prester', 'n.', 'A meteor or exhalation formerly supposed to be thrown from\n   the clouds with such violence that by collision it is set on fire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prester', 'n.', 'One of the veins of the neck when swollen with anger or\n   other excitement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prester', 'n.', 'A priest or presbyter; as, Prester John.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presternum', 'n.', 'The anterior segment of the sternum; the manubrium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prestidigital', 'a.', 'Nimble-fingered; having fingers fit for\n   prestidigitation, or juggling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prestidigitation', 'n.', 'Legerdemain; sleight of hand; juggling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prestidigitator', 'n.', 'One skilled in legerdemain or sleight of hand; a\n   juggler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prestige', 'v.', 'Delusion; illusion; trick.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prestige', 'v.', 'Weight or influence derived from past success;\n   expectation of future achievements founded on those already\n   accomplished; force or charm derived from acknowledged character or\n   reputation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prestigiation', 'n.', 'Legerdemain; prestidigitation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prestigiator', 'n.', 'A juggler; prestidigitator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prestigiatory', 'a.', 'Consisting of impostures; juggling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prestigious', 'a.', 'Practicing tricks; juggling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prestimony', 'n.', 'A fund for the support of a priest, without the title\n   of a benefice. The patron in the collator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prestissimo', 'adv.', 'Very quickly; with great rapidity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presto', 'a.', 'Quickly; immediately; in haste; suddenly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presto', 'a.', 'Quickly; rapidly; -- a direction for a quick, lively\n   movement or performance; quicker than allegro, or any rate of time\n   except prestissimo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presstriction', 'n.', 'Obstruction, dimness, or defect of sight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presultor', 'n.', 'A leader in the dance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presumable', 'a.', 'Such as may be presumed or supposed to be true; that\n   seems entitled to belief without direct evidence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presumably', 'adv.', 'In a presumable manner; by, or according to,\n   presumption.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presumed', 'imp. & p. p.', 'of Presume');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presuming', 'p. pr. & vb. n.', 'of Presume');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presume', 'v. t.', 'To assume or take beforehand; esp., to do or undertake\n   without leave or authority previously obtained.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presume', 'v. t.', 'To take or suppose to be true, or entitled to belief,\n   without examination or proof, or on the strength of probability; to\n   take for granted; to infer; to suppose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presume', 'v. i.', 'To suppose or assume something to be, or to be true, on\n   grounds deemed valid, though not amounting to proof; to believe by\n   anticipation; to infer; as, we may presume too far.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presume', 'v. i.', 'To venture, go, or act, by an assumption of leave or\n   authority not granted; to go beyond what is warranted by the\n   circumstances of the case; to venture beyond license; to take\n   liberties; -- often with on or upon before the ground of confidence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presumedly', 'adv.', 'By presumption.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presumer', 'n.', 'One who presumes; also, an arrogant person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presumingly', 'adv.', 'Confidently; arrogantly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presumption', 'n.', 'The act of presuming, or believing upon probable\n   evidence; the act of assuming or taking for granted; belief upon\n   incomplete proof.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presumption', 'n.', 'Ground for presuming; evidence probable, but not\n   conclusive; strong probability; reasonable supposition; as, the\n   presumption is that an event has taken place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presumption', 'n.', 'That which is presumed or assumed; that which is\n   supposed or believed to be real or true, on evidence that is probable\n   but not conclusive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presumption', 'n.', 'The act of venturing beyond due beyond due bounds; an\n   overstepping of the bounds of reverence, respect, or courtesy; forward,\n   overconfident, or arrogant opinion or conduct; presumptuousness;\n   arrogance; effrontery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presumptive', 'a.', 'Based on presumption or probability; grounded on\n   probable evidence; probable; as, presumptive proof.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presumptive', 'a.', 'Presumptuous; arrogant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presumptively', 'adv.', 'By presumption, or supposition grounded or\n   probability; presumably.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presumptuous', 'a.', 'Full of presumption; presuming; overconfident or\n   venturesome; audacious; rash; taking liberties unduly; arrogant;\n   insolent; as, a presumptuous commander; presumptuous conduct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presumptuous', 'a.', 'Founded on presumption; as, a presumptuous idea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presumptuous', 'a.', 'Done with hold design, rash confidence, or in\n   violation of known duty; willful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presumptuously', 'adv.', 'In a presumptuous manner; arrogantly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presumptuousness', 'n.', 'The quality or state of being presumptuous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presupposal', 'n.', 'Presupposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presupposed', 'imp. & p. p.', 'of Presuppose');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presupposing', 'p. pr. & vb. n.', 'of Presuppose');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presuppose', 'v. t.', 'To suppose beforehand; to imply as antecedent; to\n   take for granted; to assume; as, creation presupposes a creator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presupposition', 'n.', 'The act of presupposing; an antecedent implication;\n   presumption.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presupposition', 'n.', 'That which is presupposed; a previous supposition\n   or surmise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presurmise', 'n.', 'A surmise previously formed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Presystolic', 'a.', 'Preceding the systole or contraction of the heart; as,\n   the presystolic friction sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pretemporal', 'a.', 'Situated in front of the temporal bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pretence', 'a.', 'Alt. of Pretenceless');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pretenceful', 'a.', 'Alt. of Pretenceless');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pretenceless', 'a.', 'See Pretense, Pretenseful, Pretenseless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pretended', 'imp. & p. p.', 'of Pretend');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pretending', 'p. pr. & vb. n.', 'of Pretend');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pretend', 'v. t.', 'To lay a claim to; to allege a title to; to claim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pretend', 'v. t.', 'To hold before, or put forward, as a cloak or disguise\n   for something else; to exhibit as a veil for something hidden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pretend', 'v. t.', 'To hold out, or represent, falsely; to put forward, or\n   offer, as true or real (something untrue or unreal); to show\n   hypocritically, or for the purpose of deceiving; to simulate; to feign;\n   as, to pretend friendship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pretend', 'v. t.', 'To intend; to design; to plot; to attempt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pretend', 'v. t.', 'To hold before one; to extend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pretend', 'v. i.', 'To put in, or make, a claim, truly or falsely; to\n   allege a title; to lay claim to, or strive after, something; -- usually\n   with to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pretend', 'v. i.', 'To hold out the appearance of being, possessing, or\n   performing; to profess; to make believe; to feign; to sham; as, to\n   pretend to be asleep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pretendant', 'n.', 'A pretender; a claimant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pretended', 'a.', 'Making a false appearance; unreal; false; as, pretended\n   friend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pretendence', 'n.', 'The act of pretending; pretense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pretender', 'n.', 'One who lays claim, or asserts a title (to something); a\n   claimant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pretender', 'n.', 'The pretender (Eng. Hist.), the son or the grandson of\n   James II., the heir of the royal family of Stuart, who laid claim to\n   the throne of Great Britain, from which the house was excluded by law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pretender', 'n.', 'One who pretends, simulates, or feigns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pretendership', 'n.', 'The character, right, or claim of a pretender.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pretendingly', 'adv.', 'As by right or title; arrogantly; presumptuously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pretense', 'n.', 'Alt. of Pretence');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pretence', 'n.', 'The act of laying claim; the claim laid; assumption;\n   pretension.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pretence', 'n.', 'The act of holding out, or offering, to others something\n   false or feigned; presentation of what is deceptive or hypocritical;\n   deception by showing what is unreal and concealing what is real; false\n   show; simulation; as, pretense of illness; under pretense of\n   patriotism; on pretense of revenging Caesars death.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pretence', 'n.', 'That which is pretended; false, deceptive, or\n   hypocritical show, argument, or reason; pretext; feint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pretence', 'n.', 'Intention; design.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pretensed', 'a.', 'Pretended; feigned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pretenseful', 'a.', 'Abounding in pretenses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pretenseless', 'a.', 'Not having or making pretenses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pretension', 'n.', 'The act of pretending, or laying claim; the act of\n   asserting right or title.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pretension', 'n.', 'A claim made, whether true or false; a right alleged or\n   assumed; a holding out the appearance of possessing a certain\n   character; as, pretensions to scholarship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pretentative', 'a.', 'Fitted for trial beforehand; experimental.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pretentious', 'a.', 'Full of pretension; disposed to lay claim to more than\n   is ones; presuming; assuming.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preter-', '', 'A prefix signifying past, by, beyond, more than; as, preter-\n   mission, a permitting to go by; preternatural, beyond or more than is\n   natural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preterhuman', 'a.', 'More than human.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preterient', 'a.', 'Passed through; antecedent; previous; as, preterient\n   states.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preterimperfect', 'a. & n.', 'Old name of the tense also called imperfect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preterist', 'n.', 'One whose chief interest is in the past; one who regards\n   the past with most pleasure or favor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preterist', 'n.', 'One who believes the prophecies of the Apocalypse to\n   have been already fulfilled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preterit', 'a.', 'Past; -- applied to a tense which expresses an action or\n   state as past.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preterit', 'a.', 'Belonging wholly to the past; passed by.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preterit', 'n.', 'The preterit; also, a word in the preterit tense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preterite', 'a. & n.', 'Same as Preterit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preteriteness', 'n.', 'Same as Preteritness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preterition', 'n.', 'The act of passing, or going past; the state of being\n   past.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preterition', 'n.', 'A figure by which, in pretending to pass over\n   anything, a summary mention of it is made; as, \"I will not say, he is\n   valiant, he is learned, he is just.\" Called also paraleipsis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preterition', 'n.', 'The omission by a testator of some one of his heirs\n   who is entitled to a portion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preteritive', 'a.', 'Used only or chiefly in the preterit or past tenses,\n   as certain verbs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preteritness', 'n.', 'The quality or state of being past.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preterlapsed', 'a.', 'Past; as, preterlapsed ages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preterlegal', 'a.', 'Exceeding the limits of law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pretermission', 'n.', 'The act of passing by or omitting; omission.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pretermission', 'n.', 'See Preterition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pretermitted', 'imp. & p. p.', 'of Pretermit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pretermitting', 'p. pr. & vb. n.', 'of Pretermit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pretermit', 'v. t.', 'To pass by; to omit; to disregard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preternatural', 'a.', 'Beyond of different from what is natural, or\n   according to the regular course of things, but not clearly supernatural\n   or miraculous; strange; inexplicable; extraordinary; uncommon;\n   irregular; abnormal; as, a preternatural appearance; a preternatural\n   stillness; a preternatural presentation (in childbirth) or labor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preternaturalism', 'n.', 'The state of being preternatural; a preternatural\n   condition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preternaturality', 'n.', 'Preternaturalness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preternaturally', 'adv.', 'In a preternatural manner or degree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preternaturalness', 'n.', 'The quality or state of being preternatural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preterperfect', 'a. & n.', 'Old name of the tense also called preterit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preterpluperfect', 'a. & n.', 'Old name of the tense also called\n   pluperfect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pretertiary', 'a.', 'Earlier than Tertiary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pretervection', 'n.', 'The act of carrying past or beyond.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pretex', 'v. t.', 'To frame; to devise; to disguise or excuse; hence, to\n   pretend; to declare falsely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pretext', 'n.', 'Ostensible reason or motive assigned or assumed as a color\n   or cover for the real reason or motive; pretense; disguise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pretexture', 'n.', 'A pretext.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pretibial', 'a.', 'Situated in front of the tibia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pretor', 'n.', 'A civil officer or magistrate among the ancient Romans.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pretor', 'n.', 'Hence, a mayor or magistrate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pretorial', 'a.', 'Pretorian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pretorian', 'a.', 'Of or pertaining to a pretor or magistrate; judicial;\n   exercised by, or belonging to, a pretor; as, pretorian power or\n   authority.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pretorian', 'n.', 'A soldier of the pretorian guard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pretorium', 'n.', 'The generals tent in a Roman camp; hence, a council of\n   war, because held in the generals tent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pretorium', 'n.', 'The official residence of a governor of a province;\n   hence, a place; a splendid country seat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pretorship', 'n.', 'The office or dignity of a pretor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pretorture', 'v. t.', 'To torture beforehand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prettily', 'adv.', 'In a pretty manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prettiness', 'n.', 'The quality or state of being pretty; -- used sometimes\n   in a disparaging sense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pretty', 'superl.', 'Pleasing by delicacy or grace; attracting, but not\n   striking or impressing; of a pleasing and attractive form a color;\n   having slight or diminutive beauty; neat or elegant without elevation\n   or grandeur; pleasingly, but not grandly, conceived or expressed; as, a\n   pretty face; a pretty flower; a pretty poem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pretty', 'superl.', 'Moderately large; considerable; as, he had saved a\n   pretty fortune.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pretty', 'superl.', 'Affectedly nice; foppish; -- used in an ill sense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pretty', 'superl.', 'Mean; despicable; contemptible; -- used ironically;\n   as, a pretty trick; a pretty fellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pretty', 'superl.', 'Stout; strong and brave; intrepid; valiant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pretty', 'adv.', 'In some degree; moderately; considerably; rather; almost;\n   -- less emphatic than very; as, I am pretty sure of the fact; pretty\n   cold weather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prettyish', 'a.', 'Somewhat pretty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prettyism', 'n.', 'Affectation of a pretty style, manner, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pretty-spoken', 'a.', 'Spoken or speaking prettily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pretypified', 'imp. & p. p.', 'of Pretypify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pretypifying', 'p. pr. & vb. n.', 'of Pretypify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pretypify', 'v. t.', 'To prefigure; to exhibit previously in a type.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pretzel', 'n.', 'A kind of German biscuit or cake in the form of a twisted\n   ring, salted on the outside.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prevailed', 'imp. & p. p.', 'of Prevail');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prevailing', 'p. pr. & vb. n.', 'of Prevail');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prevail', 'v. i.', 'To overcome; to gain the victory or superiority; to\n   gain the advantage; to have the upper hand, or the mastery; to succeed;\n   -- sometimes with over or against.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prevail', 'v. i.', 'To be in force; to have effect, power, or influence; to\n   be predominant; to have currency or prevalence; to obtain; as, the\n   practice prevails this day.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prevail', 'v. i.', 'To persuade or induce; -- with on, upon, or with; as, I\n   prevailedon him to wait.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prevailing', 'a.', 'Having superior force or influence; efficacious;\n   persuasive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prevailing', 'a.', 'Predominant; prevalent; most general; as, the\n   prevailing disease of a climate; a prevailing opinion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prevailingly', 'adv.', 'So as to prevail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prevailment', 'n.', 'Prevalence; superior influence; efficacy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prevalence', 'n.', 'The quality or condition of being prevalent; superior\n   strength, force, or influence; general existence, reception, or\n   practice; wide extension; as, the prevalence of virtue, of a fashion,\n   or of a disease; the prevalence of a rumor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prevalency', 'n.', 'See Prevalence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prevalent', 'a.', 'Gaining advantage or superiority; having superior force,\n   influence, or efficacy; prevailing; predominant; successful;\n   victorious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prevalent', 'a.', 'Most generally received or current; most widely adopted\n   or practiced; also, generally or extensively existing; widespread;\n   prevailing; as, a prevalent observance; prevalent disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prevalently', 'adv.', 'In a prevalent manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prevaricated', 'imp. & p. p.', 'of Prevaricate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prevaricating', 'p. pr. & vb. n.', 'of Prevaricate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prevaricate', 'v. i.', 'To shift or turn from one side to the other, from\n   the direct course, or from truth; to speak with equivocation; to\n   shuffle; to quibble; as, he prevaricates in his statement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prevaricate', 'v. i.', 'To collude, as where an informer colludes with the\n   defendant, and makes a sham prosecution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prevaricate', 'v. i.', 'To undertake a thing falsely and deceitfully, with\n   the purpose of defeating or destroying it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prevaricate', 'v. t.', 'To evade by a quibble; to transgress; to pervert.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prevarication', 'n.', 'The act of prevaricating, shuffling, or quibbling,\n   to evade the truth or the disclosure of truth; a deviation from the\n   truth and fair dealing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prevarication', 'n.', 'A secret abuse in the exercise of a public office.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prevarication', 'n.', 'The collusion of an informer with the defendant, for\n   the purpose of making a sham prosecution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prevarication', 'n.', 'A false or deceitful seeming to undertake a thing\n   for the purpose of defeating or destroying it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prevaricator', 'n.', 'One who prevaricates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prevaricator', 'n.', 'A sham dealer; one who colludes with a defendant in a\n   sham prosecution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prevaricator', 'n.', 'One who betrays or abuses a trust.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preve', 'v. i. & i.', 'To prove.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preve', 'n.', 'Proof.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prevenance', 'n.', 'A going before; anticipation in sequence or order.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prevenancy', 'n.', 'The act of anticipating anothers wishes, desires,\n   etc., in the way of favor or courtesy; hence, civility; obligingness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prevene', 'v. t. & i.', 'To come before; to anticipate; hence, to hinder;\n   to prevent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prevenience', 'n.', 'The act of going before; anticipation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prevenient', 'a.', 'Going before; preceding; hence, preventive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prevented', 'imp. & p. p.', 'of Prevent');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preventing', 'p. pr. & vb. n.', 'of Prevent');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prevent', 'v. t.', 'To go before; to precede; hence, to go before as a\n   guide; to direct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prevent', 'v. t.', 'To be beforehand with; to anticipate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prevent', 'v. t.', 'To intercept; to hinder; to frustrate; to stop; to\n   thwart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prevent', 'v. i.', 'To come before the usual time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preventability', 'n.', 'The quality or state of being preventable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preventable', 'a.', 'Capable of being prevented or hindered; as,\n   preventable diseases.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preventative', 'n.', 'That which prevents; -- incorrectly used instead of\n   preventive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preventer', 'n.', 'One who goes before; one who forestalls or anticipates\n   another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preventer', 'n.', 'One who prevents or obstructs; a hinderer; that which\n   hinders; as, a preventer of evils or of disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preventer', 'n.', 'An auxiliary rope to strengthen a mast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preventingly', 'adv.', 'So as to prevent or hinder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prevention', 'n.', 'The act of going, or state of being, before.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prevention', 'n.', 'Anticipation; esp., anticipation of needs or wishes;\n   hence, precaution; forethought.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prevention', 'n.', 'The act of preventing or hindering; obstruction of\n   action, access, or approach; thwarting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prevention', 'n.', 'Prejudice; prepossession.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preventional', 'a.', 'Tending to prevent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preventive', 'a.', 'Going before; preceding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preventive', 'a.', 'Tending to defeat or hinder; obviating; preventing the\n   access of; as, a medicine preventive of disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preventive', 'n.', 'That which prevents, hinders, or obstructs; that which\n   intercepts access; in medicine, something to prevent disease; a\n   prophylactic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preventively', 'adv.', 'In a preventive manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prevertebral', 'a.', 'Situated immediately in front, or on the ventral\n   side, of the vertebral column; prespinal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Previous', 'a.', 'Going before in time; being or happening before something\n   else; antecedent; prior; as, previous arrangements; a previous illness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Previously', 'adv.', 'Beforehand; antecedently; as, a plan previously\n   formed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Previousness', 'n.', 'The quality or state of being previous; priority or\n   antecedence in time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Previse', 'v. t.', 'To foresee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Previse', 'v. t.', 'To inform beforehand; to warn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prevision', 'n.', 'Foresight; foreknowledge; prescience.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prevoyant', 'a.', 'Foreseeing; prescient.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prewarned', 'imp. & p. p.', 'of Prewarn');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prewarning', 'p. pr. & vb. n.', 'of Prewarn');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prewarn', 'v. t. & i.', 'To warn beforehand; to forewarn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prey', 'n.', 'Anything, as goods, etc., taken or got by violence; anything\n   taken by force from an enemy in war; spoil; booty; plunder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prey', 'n.', 'That which is or may be seized by animals or birds to be\n   devoured; hence, a person given up as a victim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prey', 'n.', 'The act of devouring other creatures; ravage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preyed', 'imp. & p. p.', 'of Prey');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preying', 'p. pr. & vb. n.', 'of Prey');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prey', 'n.', 'To take booty; to gather spoil; to ravage; to take food by\n   violence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preyer', 'n.', 'One who, or that which, preys; a plunderer; a waster; a\n   devourer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preyful', 'a.', 'Disposed to take prey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Preyful', 'a.', 'Rich in prey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prezygapophyses', 'pl. ', 'of Prezygapophysis');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prezygapophysis', 'n.', 'An anterior zygapophysis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prial', 'n.', 'A corruption of pair royal. See under Pair, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prian', 'n.', 'A fine, white, somewhat friable clay; also, the ore\n   contained in a mixture of clay and pebbles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Priapean', 'n.', 'A species of hexameter verse so constructed as to be\n   divisible into two portions of three feet each, having generally a\n   trochee in the first and the fourth foot, and an amphimacer in the\n   third; -- applied also to a regular hexameter verse when so constructed\n   as to be divisible into two portions of three feet each.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Priapism', 'n.', 'More or less permanent erection and rigidity of the\n   penis, with or without sexual desire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Priapulacea', 'n. pl.', 'A suborder of Gephyraea, having a cylindrical body\n   with a terminal anal opening, and usually with one or two caudal gills.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pricasour', 'n.', 'A hard rider.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Price', 'n. & v.', 'The sum or amount of money at which a thing is valued,\n   or the value which a seller sets on his goods in market; that for which\n   something is bought or sold, or offered for sale; equivalent in money\n   or other means of exchange; current value or rate paid or demanded in\n   market or in barter; cost.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Price', 'n. & v.', 'Value; estimation; excellence; worth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Price', 'n. & v.', 'Reward; recompense; as, the price of industry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Priced', 'imp. & p. p.', 'of Price');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pricing', 'p. pr. & vb. n.', 'of Price');
  INSERT INTO words (word, word_type, description) VALUES  (    'Price', 'v. t.', 'To pay the price of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Price', 'v. t.', 'To set a price on; to value. See Prize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Price', 'v. t.', 'To ask the price of; as, to price eggs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Priced', 'a.', 'Rated in price; valued; as, high-priced goods; low-priced\n   labor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Priceite', 'n.', 'A hydrous borate of lime, from Oregon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Priceless', 'a.', 'Too valuable to admit of being appraised; of inestimable\n   worth; invaluable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Priceless', 'a.', 'Of no value; worthless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prick', 'v.', 'That which pricks, penetrates, or punctures; a sharp and\n   slender thing; a pointed instrument; a goad; a spur, etc.; a point; a\n   skewer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prick', 'v.', 'The act of pricking, or the sensation of being pricked; a\n   sharp, stinging pain; figuratively, remorse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prick', 'v.', 'A mark made by a pointed instrument; a puncture; a point.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prick', 'v.', 'A point or mark on the dial, noting the hour.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prick', 'v.', 'The point on a target at which an archer aims; the mark; the\n   pin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prick', 'v.', 'A mark denoting degree; degree; pitch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prick', 'v.', 'A mathematical point; -- regularly used in old English\n   translations of Euclid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prick', 'v.', 'The footprint of a hare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prick', 'v.', 'A small roll; as, a prick of spun yarn; a prick of tobacco.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pricked', 'imp. & p. p.', 'of Prick');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pricking', 'p. pr. & vb. n.', 'of Prick');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prick', 'n.', 'To pierce slightly with a sharp-pointed instrument or\n   substance; to make a puncture in, or to make by puncturing; to drive a\n   fine point into; as, to prick one with a pin, needle, etc.; to prick a\n   card; to prick holes in paper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prick', 'n.', 'To fix by the point; to attach or hang by puncturing; as, to\n   prick a knife into a board.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prick', 'n.', 'To mark or denote by a puncture; to designate by pricking;\n   to choose; to mark; -- sometimes with off.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prick', 'n.', 'To mark the outline of by puncturing; to trace or form by\n   pricking; to mark by punctured dots; as, to prick a pattern for\n   embroidery; to prick the notes of a musical composition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prick', 'n.', 'To ride or guide with spurs; to spur; to goad; to incite; to\n   urge on; -- sometimes with on, or off.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prick', 'n.', 'To affect with sharp pain; to sting, as with remorse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prick', 'n.', 'To make sharp; to erect into a point; to raise, as something\n   pointed; -- said especially of the ears of an animal, as a horse or\n   dog; and usually followed by up; -- hence, to prick up the ears, to\n   listen sharply; to have the attention and interest strongly engaged.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prick', 'n.', 'To render acid or pungent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prick', 'n.', 'To dress; to prink; -- usually with up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prick', 'n.', 'To run a middle seam through, as the cloth of a sail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prick', 'n.', 'To trace on a chart, as a ships course.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prick', 'n.', 'To drive a nail into (a horses foot), so as to cause\n   lameness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prick', 'n.', 'To nick.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prick', 'v. i.', 'To be punctured; to suffer or feel a sharp pain, as by\n   puncture; as, a sore finger pricks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prick', 'v. i.', 'To spur onward; to ride on horseback.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prick', 'v. i.', 'To become sharp or acid; to turn sour, as wine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prick', 'v. i.', 'To aim at a point or mark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prick-eared', 'a.', 'Having erect, pointed ears; -- said of certain dogs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pricker', 'n.', 'One who, or that which, pricks; a pointed instrument; a\n   sharp point; a prickle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pricker', 'n.', 'One who spurs forward; a light horseman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pricker', 'n.', 'A priming wire; a priming needle, -- used in blasting and\n   gunnery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pricker', 'n.', 'A small marline spike having generally a wooden handle, --\n   used in sailmaking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pricket', 'n.', 'A buck in his second year. See Note under 3d Buck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pricking', 'n.', 'The act of piercing or puncturing with a sharp point.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pricking', 'n.', 'The driving of a nail into a horses foot so as to\n   produce lameness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pricking', 'n.', 'Same as Nicking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pricking', 'n.', 'A sensation of being pricked.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pricking', 'n.', 'The mark or trace left by a hares foot; a prick; also,\n   the act of tracing a hare by its footmarks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pricking', 'n.', 'Dressing ones self for show; prinking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pricking-up', 'n.', 'The first coating of plaster in work of three coats\n   upon laths. Its surface is scratched once to form a better key for the\n   next coat. In the United States called scratch coat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prickle', 'n.', 'A little prick; a small, sharp point; a fine, sharp\n   process or projection, as from the skin of an animal, the bark of a\n   plant, etc.; a spine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prickle', 'n.', 'A kind of willow basket; -- a term still used in some\n   branches of trade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prickle', 'n.', 'A sieve of filberts, -- about fifty pounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prickle', 'v. t.', 'To prick slightly, as with prickles, or fine, sharp\n   points.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prickleback', 'n.', 'Alt. of Pricklefish');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pricklefish', 'n.', 'The stickleback.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prickliness', 'n.', 'The quality of being prickly, or of having many\n   prickles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prickling', 'a.', 'Prickly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pricklouse', 'n.', 'A tailor; -- so called in contempt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prickly', 'a.', 'Full of sharp points or prickles; armed or covered with\n   prickles; as, a prickly shrub.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prickmadam', 'n.', 'A name given to several species of stonecrop, used as\n   ingredients of vermifuge medicines. See Stonecrop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prickpunch', 'n.', 'A pointed steel punch, to prick a mark on metal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prickshaft', 'n.', 'An arrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pricksong', 'v. t.', 'Music written, or noted, with dots or points; -- so\n   called from the points or dots with which it is noted down.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prickwood', 'n.', 'A shrub (Euonymus Europaeus); -- so named from the use\n   of its wood for goads, skewers, and shoe pegs. Called also spindle\n   tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pricky', 'a.', 'Stiff and sharp; prickly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pride', 'n.', 'A small European lamprey (Petromyzon branchialis); -- called\n   also prid, and sandpiper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pride', 'n.', 'The quality or state of being proud; inordinate self-esteem;\n   an unreasonable conceit of ones own superiority in talents, beauty,\n   wealth, rank, etc., which manifests itself in lofty airs, distance,\n   reserve, and often in contempt of others.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pride', 'n.', 'A sense of ones own worth, and abhorrence of what is\n   beneath or unworthy of one; lofty self-respect; noble self-esteem;\n   elevation of character; dignified bearing; proud delight; -- in a good\n   sense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pride', 'n.', 'Proud or disdainful behavior or treatment; insolence or\n   arrogance of demeanor; haughty bearing and conduct; insolent\n   exultation; disdain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pride', 'n.', 'That of which one is proud; that which excites boasting or\n   self-gratulation; the occasion or ground of self-esteem, or of arrogant\n   and presumptuous confidence, as beauty, ornament, noble character,\n   children, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pride', 'n.', 'Show; ostentation; glory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pride', 'n.', 'Highest pitch; elevation reached; loftiness; prime; glory;\n   as, to be in the pride of ones life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pride', 'n.', 'Consciousness of power; fullness of animal spirits; mettle;\n   wantonness; hence, lust; sexual desire; esp., an excitement of sexual\n   appetite in a female beast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prided', 'imp. & p. p.', 'of Pride');
  INSERT INTO words (word, word_type, description) VALUES  (    'Priding', 'p. pr. & vb. n.', 'of Pride');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pride', 'v. t.', 'To indulge in pride, or self-esteem; to rate highly; to\n   plume; -- used reflexively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pride', 'v. i.', 'To be proud; to glory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prideful', 'a.', 'Full of pride; haughty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prideless', 'a.', 'Without pride.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pridian', 'a.', 'Of or pertaining to the day before, or yesterday.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pridingly', 'adv.', 'Proudly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prie', 'n.', 'The plant privet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prie', 'v. i.', 'To pry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pried', '', 'imp. & p. p. of Pry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Priedieu', 'n.', 'A kneeling desk for prayers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prief', 'n.', 'Proof.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prier', 'n.', 'One who pries; one who inquires narrowly and searches, or is\n   inquisitive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Priest', 'n.', 'A presbyter elder; a minister');
  INSERT INTO words (word, word_type, description) VALUES  (    'Priest', 'n.', 'One who is authorized to consecrate the host and to say\n   Mass; but especially, one of the lowest order possessing this power.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Priest', 'n.', 'A presbyter; one who belongs to the intermediate order\n   between bishop and deacon. He is authorized to perform all ministerial\n   services except those of ordination and confirmation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Priest', 'n.', 'One who officiates at the altar, or performs the rites of\n   sacrifice; one who acts as a mediator between men and the divinity or\n   the gods in any form of religion; as, Buddhist priests.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Priest', 'v. t.', 'To ordain as priest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Priestcap', 'n.', 'A form of redan, so named from its shape; -- called also\n   swallowtail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Priestcraft', 'n.', 'Priestly policy; the policy of a priesthood; esp., in\n   an ill sense, fraud or imposition in religious concerns; management by\n   priests to gain wealth and power by working upon the religious motives\n   or credulity of others.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Priestery', 'n.', 'Priests, collectively; the priesthood; -- so called in\n   contempt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Priestess', 'n.', 'A woman who officiated in sacred rites among pagans.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Priesthood', 'n.', 'The office or character of a priest; the priestly\n   function.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Priesthood', 'n.', 'Priests, taken collectively; the order of men set apart\n   for sacred offices; the order of priests.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Priesting', 'n.', 'The office of a priest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Priestism', 'n.', 'The influence, doctrines, principles, etc., of priests\n   or the priesthood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Priestless', 'a.', 'Without a priest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Priestlike', 'a.', 'Priestly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Priestliness', 'n.', 'The quality or state of being priestly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Priestly', 'a.', 'Of or pertaining to a priest or the priesthood;\n   sacerdotal; befitting or becoming a priest; as, the priestly office; a\n   priestly farewell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Priest-ridden', 'a.', 'Controlled or oppressed by priests; as, a\n   priest-ridden people.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prieve', 'v. t.', 'To prove.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prigged', 'imp. & p. p.', 'of Prig');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prigging', 'p. pr. & vb. n.', 'of Prig');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prig', 'v. i.', 'To haggle about the price of a commodity; to bargain hard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prig', 'v. t.', 'To cheapen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prig', 'v. t.', 'To filch or steal; as, to prig a handkerchief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prig', 'n.', 'A pert, conceited, pragmatical fellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prig', 'n.', 'A thief; a filcher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Priggery', 'n.', 'Priggism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Priggish', 'a.', 'Like a prig; conceited; pragmatical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Priggism', 'n.', 'The quality or state of being priggish; the manners of a\n   prig.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Priggism', 'n.', 'Roguery; thievery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prighte', '', 'imp. of Prick.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prill', 'n.', 'The brill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prill', 'v. i.', 'To flow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prill', 'n.', 'A stream.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prill', 'n.', 'A nugget of virgin metal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prill', 'n.', 'Ore selected for excellence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prill', 'n.', 'The button of metal from an assay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prillion', 'n.', 'Tin extracted from the slag.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prim', 'n.', 'The privet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prim', 'a.', 'Formal; precise; affectedly neat or nice; as, prim\n   regularity; a prim person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Primmed', 'imp. & p. p.', 'of Prim');
  INSERT INTO words (word, word_type, description) VALUES  (    'Primming', 'p. pr. & vb. n.', 'of Prim');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prim', 'v. t.', 'To deck with great nicety; to arrange with affected\n   preciseness; to prink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prim', 'v. i.', 'To dress or act smartly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Primacy', 'a.', 'The state or condition of being prime or first, as in\n   time, place, rank, etc., hence, excellency; supremacy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Primacy', 'a.', 'The office, rank, or character of a primate; the chief\n   ecclesiastical station or dignity in a national church; the office or\n   dignity of an archbishop; as, the primacy of England.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prima donnas', 'pl. ', 'of Prima donna');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prime', '#', 'Donne (#) (pl. ) of Prima donna');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prima donna', 'a.', 'The first or chief female singer in an opera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prima facie', '', 'At first view; on the first appearance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Primage', 'n.', 'A charge in addition to the freight; originally, a\n   gratuity to the captain for his particular care of the goods (sometimes\n   called hat money), but now belonging to the owners or freighters of the\n   vessel, unless by special agreement the whole or part is assigned to\n   the captain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Primal', 'a.', 'First; primary; original; chief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Primality', 'n.', 'The quality or state of being primal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Primarily', 'adv.', 'In a primary manner; in the first place; in the first\n   place; in the first intention; originally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Primariness', 'n.', 'The quality or state of being primary, or first in\n   time, in act, or in intention.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Primary', 'a.', 'First in order of time or development or in intention;\n   primitive; fundamental; original.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Primary', 'a.', 'First in order, as being preparatory to something higher;\n   as, primary assemblies; primary schools.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Primary', 'a.', 'First in dignity or importance; chief; principal; as,\n   primary planets; a matter of primary importance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Primary', 'a.', 'Earliest formed; fundamental.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Primary', 'a.', 'Illustrating, possessing, or characterized by, some\n   quality or property in the first degree; having undergone the first\n   stage of substitution or replacement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Primaries', 'pl. ', 'of Primary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Primary', 'n.', 'That which stands first in order, rank, or importance; a\n   chief matter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Primary', 'n.', 'A primary meeting; a caucus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Primary', 'n.', 'One of the large feathers on the distal joint of a birds\n   wing. See Plumage, and Illust. of Bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Primary', 'n.', 'A primary planet; the brighter component of a double star.\n   See under Planet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Primate', 'a.', 'The chief ecclesiastic in a national church; one who\n   presides over other bishops in a province; an archbishop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Primate', 'a.', 'One of the Primates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Primates', 'n. pl.', 'The highest order of mammals. It includes man,\n   together with the apes and monkeys. Cf. Pitheci.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Primateship', 'n.', 'The office, dignity, or position of a primate;\n   primacy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Primatial', 'a.', 'Primatical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Primatical', 'a.', 'Of or pertaining to a primate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prime', 'a.', 'First in order of time; original; primeval; primitive;\n   primary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prime', 'a.', 'First in rank, degree, dignity, authority, or importance;\n   as, prime minister.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prime', 'a.', 'First in excellence; of highest quality; as, prime wheat; a\n   prime quality of cloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prime', 'a.', 'Early; blooming; being in the first stage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prime', 'a.', 'Lecherous; lustful; lewd.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prime', 'a.', 'Marked or distinguished by a mark () called a prime mark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prime', 'n.', 'The first part; the earliest stage; the beginning or\n   opening, as of the day, the year, etc.; hence, the dawn; the spring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prime', 'n.', 'The spring of life; youth; hence, full health, strength, or\n   beauty; perfection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prime', 'n.', 'That which is first in quantity; the most excellent portion;\n   the best part.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prime', 'a.', 'The morning; specifically (R. C. Ch.), the first canonical\n   hour, succeeding to lauds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prime', 'a.', 'The first of the chief guards.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prime', 'a.', 'Any number expressing the combining weight or equivalent of\n   any particular element; -- so called because these numbers were\n   respectively reduced to their lowest relative terms on the fixed\n   standard of hydrogen as 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prime', 'a.', 'A prime number. See under Prime, a.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prime', 'a.', 'An inch, as composed of twelve seconds in the duodecimal\n   system; -- denoted by []. See 2d Inch, n., 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Primed', 'imp. & p. p.', 'of Prime');
  INSERT INTO words (word, word_type, description) VALUES  (    'Priming', 'p. pr. & vb. n.', 'of Prime');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prime', 'a.', 'To apply priming to, as a musket or a cannon; to apply a\n   primer to, as a metallic cartridge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prime', 'a.', 'To lay the first color, coating, or preparation upon (a\n   surface), as in painting; as, to prime a canvas, a wall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prime', 'a.', 'To prepare; to make ready; to instruct beforehand; to post;\n   to coach; as, to prime a witness; the boys are primed for mischief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prime', 'a.', 'To trim or prune, as trees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prime', 'a.', 'To mark with a prime mark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prime', 'v. i.', 'To be renewed, or as at first.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prime', 'v. i.', 'To serve as priming for the charge of a gun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prime', 'v. i.', 'To work so that foaming occurs from too violent\n   ebullition, which causes water to become mixed with, and be carried\n   along with, the steam that is formed; -- said of a steam boiler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Primely', 'adv.', 'At first; primarily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Primely', 'adv.', 'In a prime manner; excellently.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Primeness', 'n.', 'The quality or state of being first.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Primeness', 'n.', 'The quality or state of being prime, or excellent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Primer', 'n.', 'One who, or that which, primes');
  INSERT INTO words (word, word_type, description) VALUES  (    'Primer', 'n.', 'an instrument or device for priming; esp., a cap, tube, or\n   water containing percussion powder or other compound for igniting a\n   charge of gunpowder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Primer', 'a.', 'First; original; primary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Primer', 'n.', 'Originally, a small prayer book for church service,\n   containing the little office of the Virgin Mary; also, a work of\n   elementary religious instruction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Primer', 'n.', 'A small elementary book for teaching children to read; a\n   reading or spelling book for a beginner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Primer', 'n.', 'A kind of type, of which there are two species; one, called\n   long primer, intermediate in size between bourgeois and small pica [see\n   Long primer]; the other, called great primer, larger than pica.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Primero', 'n.', 'A game at cards, now unknown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Primerole', 'n.', 'See Primrose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Primeval', 'a.', 'Belonging to the first ages; pristine; original;\n   primitive; primary; as, the primeval innocence of man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Primevally', 'adv.', 'In a primeval manner; in or from the earliest times;\n   originally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Primevous', 'a.', 'Primeval.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Primigenial', 'a.', 'First born, or first of all; original; primary. See\n   Primogenial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Primigenious', 'a.', 'Alt. of Primigenous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Primigenous', 'a.', 'First formed or generated; original; primigenial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Primine', 'n.', 'The outermost of the two integuments of an ovule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Priming', 'n.', 'The powder or other combustible used to communicate fire\n   to a charge of gunpowder, as in a firearm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Priming', 'n.', 'The first coating of color, size, or the like, laid on\n   canvas, or on a building, or other surface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Priming', 'n.', 'The carrying over of water, with the steam, from the\n   boiler, as into the cylinder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Primipara', 'n.', 'A woman who bears a child for the first time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Primiparous', 'a.', 'Belonging to a first birth; bearing young for the\n   first time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Primipilar', 'a.', 'Of or pertaining to the captain of the vanguard of a\n   Roman army.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Primitiae', 'pl. ', 'of Primitia');
  INSERT INTO words (word, word_type, description) VALUES  (    'Primitias', 'pl. ', 'of Primitia');
  INSERT INTO words (word, word_type, description) VALUES  (    'Primitia', 'n.', 'The first fruit; the first years whole profit of an\n   ecclesiastical preferment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Primitial', 'a.', 'Being of the first production; primitive; original.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Primitive', 'a.', 'Of or pertaining to the beginning or origin, or to early\n   times; original; primordial; primeval; first; as, primitive innocence;\n   the primitive church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Primitive', 'a.', 'Of or pertaining to a former time; old-fashioned;\n   characterized by simplicity; as, a primitive style of dress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Primitive', 'a.', 'Original; primary; radical; not derived; as, primitive\n   verb in grammar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Primitive', 'n.', 'An original or primary word; a word not derived from\n   another; -- opposed to derivative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Primitively', 'adv.', 'Originally; at first.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Primitively', 'adv.', 'Primarily; not derivatively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Primitively', 'adv.', 'According to the original rule or ancient practice;\n   in the ancient style.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Primitiveness', 'n.', 'The quality or state of being primitive; conformity\n   to primitive style or practice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Primity', 'n.', 'Quality of being first; primitiveness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Primly', 'adv.', 'In a prim or precise manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Primness', 'n.', 'The quality or state of being prim; affected formality or\n   niceness; preciseness; stiffness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Primo', 'a.', 'First; chief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Primogenial', 'a.', 'First born, made, or generated; original; primary;\n   elemental; as, primogenial light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Primogenitive', 'a.', 'Of or pertaining to primogeniture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Primogenitive', 'n.', 'Primogeniture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Primogenitor', 'n.', 'The first ancestor; a forefather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Primogeniture', 'a.', 'The state of being the firstborn of the same\n   parents; seniority by birth among children of the same family.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Primogeniture', 'a.', 'The exclusive right of inheritance which belongs to\n   the eldest son. Thus in England the right of inheriting the estate of\n   the father belongs to the eldest son, and in the royal family the\n   eldest son of the sovereign is entitled to the throne by primogeniture.\n   In exceptional cases, among the female children, the crown descends by\n   right of primogeniture to the eldest daughter only and her issue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Primogenitureship', 'n.', 'The state or privileges of the firstborn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Primordial', 'a.', 'First in order; primary; original; of earliest origin;\n   as, primordial condition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Primordial', 'a.', 'Of or pertaining to the lowest beds of the Silurian\n   age, corresponding to the Acadian and Potsdam periods in American\n   geology. It is called also Cambrian, and by many geologists is\n   separated from the Silurian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Primordial', 'a.', 'Originally or earliest formed in the growth of an\n   individual or organ; as, a primordial leaf; a primordial cell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Primordial', 'n.', 'A first principle or element.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Primordialism', 'n.', 'Devotion to, or persistence in, conditions of the\n   primordial state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Primordially', 'adv.', 'At the beginning; under the first order of things;\n   originally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Primordian', 'n.', 'A name given to several kinds of plums; as, red\n   primordian, amber primordian, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Primordiate', 'a.', 'Primordial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Primp', 'a.', 'To be formal or affected in dress or manners; -- often with\n   up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Primrose', 'a.', 'An early flowering plant of the genus Primula (P.\n   vulgaris) closely allied to the cowslip. There are several varieties,\n   as the white-, the red-, the yellow-flowered, etc. Formerly called also\n   primerole, primerolles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Primrose', 'a.', 'Any plant of the genus Primula.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Primrose', 'a.', 'Of or pertaining to the primrose; of the color of a\n   primrose; -- hence, flowery; gay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Primula', 'n.', 'The genus of plants including the primrose (Primula vera).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Primulaceous', 'a.', 'Of or pertaining to an order of herbaceous plants\n   (Primulaceae), of which the primrose is the type, and the pimpernel,\n   the cyclamen, and the water violet are other examples.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Primum mobile', '', 'In the Ptolemaic system, the outermost of the\n   revolving concentric spheres constituting the universe, the motion of\n   which was supposed to carry with it all the inclosed spheres with their\n   planets in a daily revolution from east to west. See Crystalline\n   heavens, under Crystalline.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Primus', 'n.', 'One of the bishops of the Episcopal Church of Scotland, who\n   presides at the meetings of the bishops, and has certain privileges but\n   no metropolitan authority.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Primy', 'a.', 'Being in its prime.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prince', 'a.', 'The one of highest rank; one holding the highest place and\n   authority; a sovereign; a monarch; -- originally applied to either sex,\n   but now rarely applied to a female.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prince', 'a.', 'The son of a king or emperor, or the issue of a royal\n   family; as, princes of the blood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prince', 'a.', 'A title belonging to persons of high rank, differing in\n   different countries. In England it belongs to dukes, marquises, and\n   earls, but is given to members of the royal family only. In Italy a\n   prince is inferior to a duke as a member of a particular order of\n   nobility; in Spain he is always one of the royal family.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prince', 'a.', 'The chief of any body of men; one at the head of a class or\n   profession; one who is preeminent; as, a merchant prince; a prince of\n   players.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prince', 'v. i.', 'To play the prince.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Princedom', 'n.', 'The jurisdiction, sovereignty, rank, or estate of a\n   prince.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Princehood', 'n.', 'Princeliness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Princekin', 'n.', 'A petty prince; a princeling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Princeless', 'a.', 'Without a prince.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Princelet', 'n.', 'A petty prince.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Princelike', 'a.', 'Princely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Princeliness', 'n.', 'The quality of being princely; the state, manner, or\n   dignity of a prince.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Princeling', 'n.', 'A petty prince; a young prince.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Princely', 'a.', 'Of or relating to a prince; regal; royal; of highest rank\n   or authority; as, princely birth, character, fortune, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Princely', 'a.', 'Suitable for, or becoming to, a prince; grand; august;\n   munificent; magnificent; as, princely virtues; a princely fortune.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Princely', 'adv.', 'In a princely manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Princess', 'n.', 'A female prince; a woman having sovereign power, or the\n   rank of a prince.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Princess', 'n.', 'The daughter of a sovereign; a female member of a royal\n   family.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Princess', 'n.', 'The consort of a prince; as, the princess of Wales.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Princesse', 'a.', 'A term applied to a ladys long, close-fitting dress\n   made with waist and skirt in one.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Princesslike', 'a.', 'Like a princess.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Princewood', 'n.', 'The wood of two small tropical American trees (Hamelia\n   ventricosa, and Cordia gerascanthoides). It is brownish, veined with\n   lighter color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Princified', 'a.', 'Imitative of a prince.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Principal', 'a.', 'Highest in rank, authority, character, importance, or\n   degree; most considerable or important; chief; main; as, the principal\n   officers of a Government; the principal men of a state; the principal\n   productions of a country; the principal arguments in a case.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Principal', 'a.', 'Of or pertaining to a prince; princely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Principal', 'n.', 'A leader, chief, or head; one who takes the lead; one\n   who acts independently, or who has controlling authority or influence;\n   as, the principal of a faction, a school, a firm, etc.; --\n   distinguished from a subordinate, abettor, auxiliary, or assistant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Principal', 'n.', 'The chief actor in a crime, or an abettor who is present\n   at it, -- as distinguished from an accessory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Principal', 'n.', 'A chief obligor, promisor, or debtor, -- as\n   distinguished from a surety.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Principal', 'n.', 'One who employs another to act for him, -- as\n   distinguished from an agent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Principal', 'n.', 'A thing of chief or prime importance; something\n   fundamental or especially conspicuous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Principal', 'n.', 'A capital sum of money, placed out at interest, due as a\n   debt or used as a fund; -- so called in distinction from interest or\n   profit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Principal', 'n.', 'The construction which gives shape and strength to a\n   roof, -- generally a truss of timber or iron, but there are roofs with\n   stone principals. Also, loosely, the most important member of a piece\n   of framing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Principal', 'n.', 'In English organs the chief open metallic stop, an\n   octave above the open diapason. On the manual it is four feet long, on\n   the pedal eight feet. In Germany this term corresponds to the English\n   open diapason.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Principal', 'n.', 'A heirloom; a mortuary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Principal', 'n.', 'The first two long feathers of a hawks wing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Principal', 'n.', 'One of turrets or pinnacles of waxwork and tapers with\n   which the posts and center of a funeral hearse were formerly crowned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Principal', 'n.', 'A principal or essential point or rule; a principle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Principalities', 'pl. ', 'of Principality');
  INSERT INTO words (word, word_type, description) VALUES  (    'Principality', 'n.', 'Sovereignty; supreme power; hence, superiority;\n   predominance; high, or the highest, station.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Principality', 'n.', 'A prince; one invested with sovereignty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Principality', 'n.', 'The territory or jurisdiction of a prince; or the\n   country which gives title to a prince; as, the principality of Wales.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Principally', 'adv.', 'In a principal manner; primarily; above all;\n   chiefly; mainly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Principalness', 'n.', 'The quality of being principal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Principate', 'n.', 'Principality; supreme rule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Principia', 'n. pl.', 'First principles; fundamental beginnings; elements;\n   as. Newtons Principia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Principial', 'a.', 'Elementary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Principiant', 'a.', 'Relating to principles or beginnings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Principiate', 'v. t.', 'To begin; to initiate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Principiation', 'n.', 'Analysis into primary or elemental parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Principle', 'n.', 'Beginning; commencement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Principle', 'n.', 'A source, or origin; that from which anything proceeds;\n   fundamental substance or energy; primordial substance; ultimate\n   element, or cause.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Principle', 'n.', 'An original faculty or endowment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Principle', 'n.', 'A fundamental truth; a comprehensive law or doctrine,\n   from which others are derived, or on which others are founded; a\n   general truth; an elementary proposition; a maxim; an axiom; a\n   postulate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Principle', 'n.', 'A settled rule of action; a governing law of conduct; an\n   opinion or belief which exercises a directing influence on the life and\n   behavior; a rule (usually, a right rule) of conduct consistently\n   directing ones actions; as, a person of no principle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Principle', 'n.', 'Any original inherent constituent which characterizes a\n   substance, or gives it its essential properties, and which can usually\n   be separated by analysis; -- applied especially to drugs, plant\n   extracts, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Principled', 'imp. & p. p.', 'of Principle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Principling', 'p. pr. & vb. n.', 'of Principle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Principle', 'v. t.', 'To equip with principles; to establish, or fix, in\n   certain principles; to impress with any tenet, or rule of conduct, good\n   or ill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Princock', 'n.', 'Alt. of Princox');
  INSERT INTO words (word, word_type, description) VALUES  (    'Princox', 'n.', 'A coxcomb; a pert boy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prinked', 'imp. & p. p.', 'of Prink');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prinking', 'p. pr. & vb. n.', 'of Prink');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prink', 'v. t.', 'To dress or adjust ones self for show; to prank.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prink', 'v. t.', 'To prank or dress up; to deck fantastically.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prinker', 'n.', 'One who prinks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prinpriddle', 'n.', 'The long-tailed titmouse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Printed', 'imp. & p. p.', 'of Print');
  INSERT INTO words (word, word_type, description) VALUES  (    'Printing', 'p. pr. & vb. n.', 'of Print');
  INSERT INTO words (word, word_type, description) VALUES  (    'Print', 'v. t.', 'To fix or impress, as a stamp, mark, character, idea,\n   etc., into or upon something.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Print', 'v. t.', 'To stamp something in or upon; to make an impression or\n   mark upon by pressure, or as by pressure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Print', 'v. t.', 'To strike off an impression or impressions of, from type,\n   or from stereotype, electrotype, or engraved plates, or the like; in a\n   wider sense, to do the typesetting, presswork, etc., of (a book or\n   other publication); as, to print books, newspapers, pictures; to print\n   an edition of a book.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Print', 'v. t.', 'To stamp or impress with colored figures or patterns; as,\n   to print calico.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Print', 'v. t.', 'To take (a copy, a positive picture, etc.), from a\n   negative, a transparent drawing, or the like, by the action of light\n   upon a sensitized surface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Print', 'v. i.', 'To use or practice the art of typography; to take\n   impressions of letters, figures, or electrotypes, engraved plates, or\n   the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Print', 'v. i.', 'To publish a book or an article.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Print', 'n.', 'A mark made by impression; a line, character, figure, or\n   indentation, made by the pressure of one thing on another; as, the\n   print of teeth or nails in flesh; the print of the foot in sand or\n   snow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Print', 'n.', 'A stamp or die for molding or impressing an ornamental\n   design upon an object; as, a butter print.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Print', 'n.', 'That which receives an impression, as from a stamp or mold;\n   as, a print of butter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Print', 'n.', 'Printed letters; the impression taken from type, as to\n   excellence, form, size, etc.; as, small print; large print; this line\n   is in print.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Print', 'n.', 'That which is produced by printing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Print', 'n.', 'An impression taken from anything, as from an engraved\n   plate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Print', 'n.', 'A printed publication, more especially a newspaper or other\n   periodical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Print', 'n.', 'A printed cloth; a fabric figured by stamping, especially\n   calico or cotton cloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Print', 'n.', 'A photographic copy, or positive picture, on prepared paper,\n   as from a negative, or from a drawing on transparent paper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Print', 'n.', 'A core print. See under Core.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Printa-ble', 'a.', 'Worthy to be published.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Printer', 'n.', 'One who prints; especially, one who prints books,\n   newspapers, engravings, etc., a compositor; a typesetter; a pressman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Printery', 'n.', 'A place where cloth is printed; print works; also, a\n   printing office.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Printing', 'n.', 'The act, art, or practice of impressing letters,\n   characters, or figures on paper, cloth, or other material; the business\n   of a printer, including typesetting and presswork, with their adjuncts;\n   typography; also, the act of producing photographic prints.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Printless', 'a.', 'Making no imprint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Printless', 'a.', 'Making no imprint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Printshop', 'n.', 'A shop where prints are sold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prior', 'a.', 'Preceding in the order of time; former; antecedent;\n   anterior; previous; as, a prior discovery; prior obligation; -- used\n   elliptically in cases like the following: he lived alone [in the time]\n   prior to his marriage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prior', 'a.', 'The superior of a priory, and next below an abbot in\n   dignity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Priorate', 'n.', 'The dignity, office, or government, of a prior.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prioress', 'n.', 'A lady superior of a priory of nuns, and next in dignity\n   to an abbess.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Priority', 'a.', 'The quality or state of being prior or antecedent in\n   time, or of preceding something else; as, priority of application.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Priority', 'a.', 'Precedence; superior rank.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Priorly', 'adv.', 'Previously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Priorship', 'n.', 'The state or office of prior; priorate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Priories', 'pl. ', 'of Priory');
  INSERT INTO words (word, word_type, description) VALUES  (    'Priory', 'n.', 'A religious house presided over by a prior or prioress; --\n   sometimes an offshoot of, an subordinate to, an abbey, and called also\n   cell, and obedience. See Cell, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pris', 'n.', 'See Price, and 1st Prize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prisage', 'n.', 'A right belonging to the crown of England, of taking two\n   tuns of wine from every ship importing twenty tuns or more, -- one\n   before and one behind the mast. By charter of Edward I. butlerage was\n   substituted for this.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prisage', 'n.', 'The share of merchandise taken as lawful prize at sea\n   which belongs to the king or admiral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Priscillianist', 'n.', 'A follower of Priscillian, bishop of Avila in\n   Spain, in the fourth century, who mixed various elements of Gnosticism\n   and Manicheism with Christianity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prise', 'n.', 'An enterprise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prise', 'n. & v.', 'See Prize, n., 5. Also Prize, v. t.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Priser', 'n.', 'See 1st Prizer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prism', 'n.', 'A solid whose bases or ends are any similar, equal, and\n   parallel plane figures, and whose sides are parallelograms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prism', 'n.', 'A transparent body, with usually three rectangular plane\n   faces or sides, and two equal and parallel triangular ends or bases; --\n   used in experiments on refraction, dispersion, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prism', 'n.', 'A form the planes of which are parallel to the vertical\n   axis. See Form, n., 13.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prismatic', 'a.', 'Alt. of Prismatical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prismatical', 'a.', 'Resembling, or pertaining to, a prism; as, a prismatic\n   form or cleavage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prismatical', 'a.', 'Separated or distributed by a prism; formed by a\n   prism; as, prismatic colors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prismatical', 'a.', 'Same as Orthorhombic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prismatically', 'adv.', 'In the form or manner of a prism; by means of a\n   prism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prismatoidal', 'a.', 'Having a prismlike form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prismoid', 'n.', 'A body that approaches to the form of a prism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prismoidal', 'a.', 'Having the form of a prismoid; as, prismoidal solids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prismy', 'a.', 'Pertaining to a prism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prison', 'n.', 'A place where persons are confined, or restrained of\n   personal liberty; hence, a place or state o/ confinement, restraint, or\n   safe custody.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prison', 'n.', 'Specifically, a building for the safe custody or\n   confinement of criminals and others committed by lawful authority.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prisoned', 'imp. & p. p.', 'of Prison');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prisoning', 'p. pr. & vb. n.', 'of Prison');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prison', 'v. t.', 'To imprison; to shut up in, or as in, a prison; to\n   confine; to restrain from liberty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prison', 'v. t.', 'To bind (together); to enchain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prisoner', 'n.', 'One who is confined in a prison.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prisoner', 'n.', 'A person under arrest, or in custody, whether in prison\n   or not; a person held in involuntary restraint; a captive; as, a\n   prisoner at the bar of a court.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prisonment', 'n.', 'Imprisonment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pristinate', 'a.', 'Pristine; primitive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pristine', 'a.', 'Belonging to the earliest period or state; original;\n   primitive; primeval; as, the pristine state of innocence; the pristine\n   manners of a people; pristine vigor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pritch', 'n.', 'A sharp-pointed instrument; also, an eelspear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pritch', 'n.', 'Pique; offense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pritchel', 'n.', 'A tool employed by blacksmiths for punching or enlarging\n   the nail holes in a horseshoe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prithee', 'interj.', 'A corruption of pray thee; as, I prithee; generally\n   used without I.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prittle-prattle', 'n.', 'Empty talk; trifling loquacity; prattle; -- used\n   in contempt or ridicule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Privacies', 'pl. ', 'of Privacy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Privacy', 'n.', 'The state of being in retirement from the company or\n   observation of others; seclusion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Privacy', 'n.', 'A place of seclusion from company or observation; retreat;\n   solitude; retirement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Privacy', 'n.', 'Concealment of what is said or done.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Privacy', 'n.', 'A private matter; a secret.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Privacy', 'n.', 'See Privity, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Privado', 'n.', 'A private friend; a confidential friend; a confidant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Private', 'a.', 'Belonging to, or concerning, an individual person,\n   company, or interest; peculiar to ones self; unconnected with others;\n   personal; ones own; not public; not general; separate; as, a mans\n   private opinion; private property; a private purse; private expenses or\n   interests; a private secretary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Private', 'a.', 'Sequestered from company or observation; appropriated to\n   an individual; secret; secluded; lonely; solitary; as, a private room\n   or apartment; private prayer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Private', 'a.', 'Not invested with, or engaged in, public office or\n   employment; as, a private citizen; private life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Private', 'a.', 'Not publicly known; not open; secret; as, a private\n   negotiation; a private understanding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Private', 'a.', 'Having secret or private knowledge; privy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Private', 'n.', 'A secret message; a personal unofficial communication.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Private', 'n.', 'Personal interest; particular business.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Private', 'n.', 'Privacy; retirement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Private', 'n.', 'One not invested with a public office.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Private', 'n.', 'A common soldier; a soldier below the grade of a\n   noncommissioned officer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Private', 'n.', 'The private parts; the genitals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Privateer', 'n.', 'An armed private vessel which bears the commission of\n   the sovereign power to cruise against the enemy. See Letters of marque,\n   under Marque.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Privateer', 'n.', 'The commander of a privateer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Privateered', 'imp. & p. p.', 'of Privateer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Privateering', 'p. pr. & vb. n.', 'of Privateer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Privateer', 'v. i.', 'To cruise in a privateer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Privateering', 'n.', 'Cruising in a privateer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Privateersmen', 'pl. ', 'of Privateersman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Privateersman', 'n.', 'An officer or seaman of a privateer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Privately', 'adv.', 'In a private manner; not openly; without the presence\n   of others.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Privately', 'adv.', 'In a manner affecting an individual; personally not\n   officially; as, he is not privately benefited.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Privateness', 'n.', 'Seclusion from company or society; retirement;\n   privacy; secrecy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Privateness', 'n.', 'The state of one not invested with public office.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Privation', 'n.', 'The act of depriving, or taking away; hence, the\n   depriving of rank or office; degradation in rank; deprivation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Privation', 'n.', 'The state of being deprived or destitute of something,\n   especially of something required or desired; destitution; need; as, to\n   undergo severe privations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Privation', 'n.', 'The condition of being absent; absence; negation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Privative', 'a.', 'Causing privation; depriving.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Privative', 'a.', 'Consisting in the absence of something; not positive;\n   negative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Privative', 'a.', 'Implying privation or negation; giving a negative force\n   to a word; as, alpha privative; privative particles; -- applied to such\n   prefixes and suffixes as a- (Gr. /), un-, non-, -less.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Privative', 'n.', 'That of which the essence is the absence of something.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Privative', 'n.', 'A term indicating the absence of any quality which might\n   be naturally or rationally expected; -- called also privative term.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Privative', 'n.', 'A privative prefix or suffix. See Privative, a., 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Privatively', 'adv.', 'In a privative manner; by the absence of something;\n   negatively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Privativeness', 'n.', 'The state of being privative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Privet', 'n.', 'An ornamental European shrub (Ligustrum vulgare), much used\n   in hedges; -- called also prim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Privilege', 'n.', 'A peculiar benefit, advantage, or favor; a right or\n   immunity not enjoyed by others or by all; special enjoyment of a good,\n   or exemption from an evil or burden; a prerogative; advantage;\n   franchise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Privilege', 'n.', 'See Call, Put, Spread, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Privileged', 'imp. & p. p.', 'of Privilege');
  INSERT INTO words (word, word_type, description) VALUES  (    'Privileging', 'p. pr. & vb. n.', 'of Privilege');
  INSERT INTO words (word, word_type, description) VALUES  (    'Privilege', 'v. t.', 'To grant some particular right or exemption to; to\n   invest with a peculiar right or immunity; to authorize; as, to\n   privilege representatives from arrest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Privilege', 'v. t.', 'To bring or put into a condition of privilege or\n   exemption from evil or danger; to exempt; to deliver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Privileged', 'a.', 'Invested with a privilege; enjoying a peculiar right,\n   advantage, or immunity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Privily', 'adv.', 'In a privy manner; privately; secretly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Privities', 'pl. ', 'of Privity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Privity', 'a.', 'Privacy; secrecy; confidence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Privity', 'a.', 'Private knowledge; joint knowledge with another of a\n   private concern; cognizance implying consent or concurrence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Privity', 'a.', 'A private matter or business; a secret.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Privity', 'a.', 'The genitals; the privates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Privity', 'a.', 'A connection, or bond of union, between parties, as to\n   some particular transaction; mutual or successive relationship to the\n   same rights of property.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Privy', 'a.', 'Of or pertaining to some person exclusively; assigned to\n   private uses; not public; private; as, the privy purse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Privy', 'a.', 'Secret; clandestine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Privy', 'a.', 'Appropriated to retirement; private; not open to the public.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Privy', 'a.', 'Admitted to knowledge of a secret transaction; secretly\n   cognizant; privately knowing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Privies', 'pl. ', 'of Privy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Privy', 'n.', 'A partaker; a person having an interest in any action or\n   thing; one who has an interest in an estate created by another; a\n   person having an interest derived from a contract or conveyance to\n   which he is not himself a party. The term, in its proper sense, is\n   distinguished from party.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Privy', 'n.', 'A necessary house or place; a backhouse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prizable', 'a.', 'Valuable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prize', 'n.', 'That which is taken from another; something captured; a\n   thing seized by force, stratagem, or superior power.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prize', 'n.', 'Anything captured by a belligerent using the rights of war;\n   esp., property captured at sea in virtue of the rights of war, as a\n   vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prize', 'n.', 'An honor or reward striven for in a competitive contest;\n   anything offered to be competed for, or as an inducement to, or reward\n   of, effort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prize', 'n.', 'That which may be won by chance, as in a lottery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prize', 'n.', 'Anything worth striving for; a valuable possession held or\n   in prospect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prize', 'n.', 'A contest for a reward; competition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prize', 'n.', 'A lever; a pry; also, the hold of a lever.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prize', 'v. t.', 'To move with a lever; to force up or open; to pry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prized', 'imp. & p. p.', 'of Prize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prizing', 'p. pr. & vb. n.', 'of Prize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prize', 'v. t.', 'To set or estimate the value of; to appraise; to price;\n   to rate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prize', 'v. t.', 'To value highly; to estimate to be of great worth; to\n   esteem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prize', 'n.', 'Estimation; valuation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prizemen', 'pl. ', 'of Prizeman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prizeman', 'n.', 'The winner of a prize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prizer', 'n.', 'One who estimates or sets the value of a thing; an\n   appraiser.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prizer', 'n.', 'One who contends for a prize; a prize fighter; a\n   challenger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prizing', 'n.', 'The application of a lever to move any weighty body, as a\n   cask, anchor, cannon, car, etc. See Prize, n., 5.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pro-', '', 'A prefix signifying before, in front, forth, for, in behalf of,\n   in place of, according to; as, propose, to place before; proceed, to go\n   before or forward; project, to throw forward; prologue, part spoken\n   before (the main piece); propel, prognathous; provide, to look out for;\n   pronoun, a word instead of a noun; proconsul, a person acting in place\n   of a consul; proportion, arrangement according to parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pro', 'a.', 'A Latin preposition signifying for, before, forth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pro', 'adv.', 'For, on, or in behalf of, the affirmative side; -- in\n   contrast with con.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proa', 'n.', 'A sailing canoe of the Ladrone Islands and Malay Archipelago,\n   having its lee side flat and its weather side like that of an ordinary\n   boat. The ends are alike. The canoe is long and narrow, and is kept\n   from overturning by a cigar-shaped log attached to a frame extending\n   several feet to windward. It has been called the flying proa, and is\n   the swiftest sailing craft known.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proach', 'v. i.', 'See Approach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proatlas', 'n.', 'A vertebral rudiment in front of the atlas in some\n   reptiles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Probabiliorism', 'n.', 'The doctrine of the probabiliorists.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Probabiliorist', 'n.', 'One who holds, in opposition to the probabilists,\n   that a man is bound to do that which is most probably right.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Probabilism', 'n.', 'The doctrine of the probabilists.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Probabilist', 'n.', 'One who maintains that certainty is impossible, and\n   that probability alone is to govern our faith and actions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Probabilist', 'n.', 'One who maintains that a man may do that which has a\n   probability of being right, or which is inculcated by teachers of\n   authority, although other opinions may seem to him still more probable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Probabilities', 'pl. ', 'of Probability');
  INSERT INTO words (word, word_type, description) VALUES  (    'Probability', 'n.', 'The quality or state of being probable; appearance of\n   reality or truth; reasonable ground of presumption; likelihood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Probability', 'n.', 'That which is or appears probable; anything that has\n   the appearance of reality or truth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Probability', 'n.', 'Likelihood of the occurrence of any event in the\n   doctrine of chances, or the ratio of the number of favorable chances to\n   the whole number of chances, favorable and unfavorable. See 1st Chance,\n   n., 5.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Probable', 'a.', 'Capable of being proved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Probable', 'a.', 'Having more evidence for than against; supported by\n   evidence which inclines the mind to believe, but leaves some room for\n   doubt; likely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Probable', 'a.', 'Rendering probable; supporting, or giving ground for,\n   belief, but not demonstrating; as, probable evidence; probable\n   presumption.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Probably', 'adv.', 'In a probable manner; in likelihood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Probacy', 'n.', 'Proof; trial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Probal', 'a.', 'Approved; probable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Probality', 'n.', 'Probability.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Probang', 'n.', 'A slender elastic rod, as of whalebone, with a sponge on\n   the end, for removing obstructions from the esophagus, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Probate', 'n.', 'Proof.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Probate', 'n.', 'Official proof; especially, the proof before a competent\n   officer or tribunal that an instrument offered, purporting to be the\n   last will and testament of a person deceased, is indeed his lawful act;\n   the copy of a will proved, under the seal of the Court of Probate,\n   delivered to the executors with a certificate of its having been\n   proved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Probate', 'n.', 'The right or jurisdiction of proving wills.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Probate', 'a.', 'Of or belonging to a probate, or court of probate; as, a\n   probate record.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Probate', 'v. t.', 'To obtain the official approval of, as of an instrument\n   purporting to be the last will and testament; as, the executor has\n   probated the will.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Probation', 'n.', 'The act of proving; also, that which proves anything;\n   proof.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Probation', 'n.', 'Any proceeding designed to ascertain truth, to determine\n   character, qualification, etc.; examination; trial; as, to engage a\n   person on probation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Probation', 'n.', 'The novitiate which a person must pass in a convent, to\n   probe his or her virtue and ability to bear the severities of the rule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Probation', 'n.', 'The trial of a ministerial candidates qualifications\n   prior to his ordination, or to his settlement as a pastor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Probation', 'n.', 'Moral trial; the state of man in the present life, in\n   which he has the opportunity of proving his character, and becoming\n   qualified for a happier state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Probational', 'a.', 'Probationary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Probationary', 'a.', 'Of or pertaining to probation; serving for trial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Probationer', 'n.', 'One who is undergoing probation; one who is on trial;\n   a novice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Probationer', 'n.', 'A student in divinity, who, having received\n   certificates of good morals and qualifications from his university, is\n   admitted to several trials by a presbytery, and, on acquitting himself\n   well, is licensed to preach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Probationership', 'n.', 'The state of being a probationer; novitiate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Probationship', 'n.', 'A state of probation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Probative', 'a.', 'Serving for trial or proof; probationary; as, probative\n   judgments; probative evidence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Probator', 'n.', 'An examiner; an approver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Probator', 'n.', 'One who, when indicted for crime, confessed it, and\n   accused others, his accomplices, in order to obtain pardon; a states\n   evidence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Probatory', 'a.', 'Serving for trial; probationary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Probatory', 'a.', 'Pertaining to, or serving for, proof.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Probed', 'imp. & p. p.', 'of Probe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Probing', 'p. pr. & vb. n.', 'of Probe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Probe', 'v. t.', 'To examine, as a wound, an ulcer, or some cavity of the\n   body, with a probe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Probe', 'v. t.', 'Fig.: to search to the bottom; to scrutinize or examine\n   thoroughly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Probe', 'n.', 'An instrument for examining the depth or other circumstances\n   of a wound, ulcer, or cavity, or the direction of a sinus, of for\n   exploring for bullets, for stones in the bladder, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Probeagle', 'n.', 'See Porbeagle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Probe-pointed', 'a.', 'Having a blunt or button-shaped extremity; -- said\n   of cutting instruments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Probity', 'n.', 'Tried virtue or integrity; approved moral excellence;\n   honesty; rectitude; uprightness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Problem', 'n.', 'A question proposed for solution; a matter stated for\n   examination or proof; hence, a matter difficult of solution or\n   settlement; a doubtful case; a question involving doubt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Problem', 'n.', 'Anything which is required to be done; as, in geometry, to\n   bisect a line, to draw a perpendicular; or, in algebra, to find an\n   unknown quantity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Problematic', 'a.', 'Alt. of Problematical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Problematical', 'a.', 'Having the nature of a problem; not shown in fact;\n   questionable; uncertain; unsettled; doubtful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Problematist', 'n.', 'One who proposes problems.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Problematize', 'v. t.', 'To propose problems.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proboscidate', 'a.', 'Having a proboscis; proboscidial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proboscidea', 'n. pl.', 'An order of large mammals including the elephants\n   and mastodons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proboscidean', 'a.', 'Proboscidian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proboscidial', 'a.', 'Proboscidate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proboscidian', 'a.', 'Pertaining to the Proboscidea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proboscidian', 'n.', 'One of the Proboscidea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proboscidifera', 'n. pl.', 'An extensive division of pectinibranchiate\n   gastropods, including those that have a long retractile proboscis, with\n   the mouth at the end, as the cones, whelks, tritons, and cowries. See\n   Illust. of Gastropoda, and of Winkle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proboscidifera', 'n. pl.', 'A subdivision of the taenioglossate gastropods,\n   including the fig-shells (Pyrula), the helmet shells (Cassis), the\n   tritons, and allied genera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proboscidiform', 'a.', 'Having the form or uses of a proboscis; as, a\n   proboscidiform mouth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proboscides', 'pl. ', 'of Proboscis');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proboscis', 'n.', 'A hollow organ or tube attached to the head, or\n   connected with the mouth, of various animals, and generally used in\n   taking food or drink; a snout; a trunk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proboscis', 'n.', 'By extension, applied to various tubelike mouth organs\n   of the lower animals that can be everted or protruded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proboscis', 'n.', 'The nose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Procacious', 'a.', 'Pert; petulant; forward; saucy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Procacity', 'n.', 'Forwardness; pertness; petulance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Procambium', 'n.', 'The young tissue of a fibrovascular bundle before its\n   component cells have begun to be differentiated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Procatarctic', 'a.', 'Beginning; predisposing; exciting; initial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Procatarxis', 'n.', 'The kindling of a disease into action; also, the\n   procatarctic cause.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Procedendo', 'n.', 'A writ by which a cause which has been removed on\n   insufficient grounds from an inferior to a superior court by\n   certiorari, or otherwise, is sent down again to the same court, to be\n   proceeded in there.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Procedendo', 'n.', 'In English practice, a writ issuing out of chancery in\n   cases where the judges of subordinate courts delay giving judgment,\n   commanding them to proceed to judgment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Procedendo', 'n.', 'A writ by which the commission of the justice of the\n   peace is revived, after having been suspended.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Procedure', 'n.', 'The act or manner of proceeding or moving forward;\n   progress; process; operation; conduct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Procedure', 'n.', 'A step taken; an act performed; a proceeding; the steps\n   taken in an action or other legal proceeding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Procedure', 'n.', 'That which results; issue; product.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proceeded', 'imp. & p. p.', 'of Proceed');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proceeding', 'p. pr. & vb. n.', 'of Proceed');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proceed', 'v. i.', 'To move, pass, or go forward or onward; to advance; to\n   continue or renew motion begun; as, to proceed on a journey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proceed', 'v. i.', 'To pass from one point, topic, or stage, to another;\n   as, to proceed with a story or argument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proceed', 'v. i.', 'To issue or come forth as from a source or origin; to\n   come from; as, light proceeds from the sun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proceed', 'v. i.', 'To go on in an orderly or regulated manner; to begin\n   and carry on a series of acts or measures; to act by method; to\n   prosecute a design.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proceed', 'v. i.', 'To be transacted; to take place; to occur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proceed', 'v. i.', 'To have application or effect; to operate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proceed', 'v. i.', 'To begin and carry on a legal process.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proceed', 'n.', 'See Proceeds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proceeder', 'n.', 'One who proceeds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proceeding', 'n.', 'The act of one who proceeds, or who prosecutes a design\n   or transaction; progress or movement from one thing to another; a\n   measure or step taken in a course of business; a transaction; as, an\n   illegal proceeding; a cautious or a violent proceeding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proceeding', 'n.', 'The course of procedure in the prosecution of an action\n   at law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proceeds', 'n. pl.', 'That which comes forth or results; effect; yield;\n   issue; product; sum accruing from a sale, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proceleusmatic', 'a.', 'Inciting; animating; encouraging.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proceleusmatic', 'a.', 'Consisting of four short syllables; composed of\n   feet of four short syllables each.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proceleusmatic', 'n.', 'A foot consisting of four short syllables.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Procellarian', 'n.', 'One of a family of oceanic birds (Procellaridae)\n   including the petrels, fulmars, and shearwaters. They are often seen in\n   great abundance in stormy weather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Procellous', 'a.', 'Stormy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Procephalic', 'a.', 'Pertaining to, or forming, the front of the head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proception', 'n.', 'Preoccupation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Procere', 'a.', 'Of high stature; tall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Procerebrum', 'n.', 'The prosencephalon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proceres', 'n. pl.', 'An order of large birds; the Ratitae; -- called also\n   Proceri.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Procerite', 'n.', 'The segment next to the flagellum of the antennae of\n   Crustacea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Procerity', 'n.', 'Height of stature; tallness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Process', 'n.', 'The act of proceeding; continued forward movement;\n   procedure; progress; advance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Process', 'n.', 'A series of actions, motions, or occurrences; progressive\n   act or transaction; continuous operation; normal or actual course or\n   procedure; regular proceeding; as, the process of vegetation or\n   decomposition; a chemical process; processes of nature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Process', 'n.', 'A statement of events; a narrative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Process', 'n.', 'Any marked prominence or projecting part, especially of a\n   bone; anapophysis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Process', 'n.', 'The whole course of proceedings in a cause real or\n   personal, civil or criminal, from the beginning to the end of the suit;\n   strictly, the means used for bringing the defendant into court to\n   answer to the action; -- a generic term for writs of the class called\n   judicial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Procession', 'n.', 'The act of proceeding, moving on, advancing, or\n   issuing; regular, orderly, or ceremonious progress; continuous course.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Procession', 'n.', 'That which is moving onward in an orderly, stately, or\n   solemn manner; a train of persons advancing in order; a ceremonious\n   train; a retinue; as, a procession of mourners; the Lord Mayors\n   procession.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Procession', 'n.', 'An orderly and ceremonial progress of persons, either\n   from the sacristy to the choir, or from the choir around the church,\n   within or without.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Procession', 'n.', 'An old term for litanies which were said in procession\n   and not kneeling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Procession', 'v. t.', 'To ascertain, mark, and establish the boundary lines\n   of, as lands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Procession', 'v. i.', 'To march in procession.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Procession', 'v. i.', 'To honor with a procession.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Processional', 'a.', 'Of or pertaining to a procession; consisting in a\n   procession.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Processional', 'n.', 'A service book relating to ecclesiastical\n   processions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Processional', 'n.', 'A hymn, or other selection, sung during a church\n   procession; as, the processional was the 202d hymn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Processionalist', 'n.', 'One who goes or marches in a procession.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Processionary', 'a.', 'Pertaining to a procession; consisting in\n   processions; as, processionary service.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Processioner', 'n.', 'One who takes part in a procession.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Processioner', 'n.', 'A manual of processions; a processional.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Processioner', 'n.', 'An officer appointed to procession lands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Processioning', 'n.', 'A proceeding prescribed by statute for ascertaining\n   and fixing the boundaries of land. See 2d Procession.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Processive', 'a.', 'Proceeding; advancing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proces verbal', '', 'An authentic minute of an official act, or statement\n   of facts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prochein', 'a.', 'Next; nearest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prochordal', 'a.', 'Situated in front of the notochord; -- applied\n   especially to parts of the cartilaginous rudiments in the base of the\n   skull.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prochronism', 'n.', 'The dating of an event before the time it happened; an\n   antedating; -- opposed to metachronism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prochronize', 'v. t.', 'To antedate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Procidence', 'n.', 'Alt. of Procidentia');
  INSERT INTO words (word, word_type, description) VALUES  (    'Procidentia', 'n.', 'A falling down; a prolapsus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prociduous', 'a.', 'Falling from its proper place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Procinct', 'n.', 'A state of complete readiness for action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proclaimed', 'imp. & p. p.', 'of Proclaim');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proclaiming', 'p. pr. & vb. n.', 'of Proclaim');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proclaim', 'v. t.', 'To make known by public announcement; to give wide\n   publicity to; to publish abroad; to promulgate; to declare; as, to\n   proclaim war or peace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proclaim', 'v. t.', 'To outlaw by public proclamation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proclaimer', 'n.', 'One who proclaims.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proclamation', 'n.', 'The act of proclaiming; official or general notice;\n   publication.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proclamation', 'n.', 'That which is proclaimed, publicly announced, or\n   officially declared; a published ordinance; as, the proclamation of a\n   king; a Thanksgiving proclamation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proclitic', 'a.', 'Leaning forward; -- said of certain monosyllabic words\n   which are so closely attached to the following word as not to have a\n   separate accent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proclive', 'a.', 'Having a tendency by nature; prone; proclivous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proclivity', 'n.', 'Inclination; propensity; proneness; tendency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proclivity', 'n.', 'Readiness; facility; aptitude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proclivous', 'a.', 'Inclined; tending by nature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proclivous', 'a.', 'Having the incisor teeth directed forward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Procoele', 'n.', 'A lateral cavity of the prosencephalon; a lateral\n   ventricle of the brain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Procoeliae', 'pl. ', 'of Procoelia');
  INSERT INTO words (word, word_type, description) VALUES  (    'Procoelia', 'n.', 'Same as Procoele.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Procoelia', 'n. pl.', 'A division of Crocodilia, including the true\n   crocodiles and alligators, in which the dorsal vertebrae are concave in\n   front.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Procoelian', 'a.', 'Concave in front; as, procoelian vertebrae, which have\n   the anterior end of the centra concave and the posterior convex.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Procoelian', 'n.', 'A reptile having procoelian vertebrae; one of the\n   Procoelia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Procoelous', 'a.', 'Same as Procoelian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proconsul', 'n.', 'An officer who discharged the duties of a consul without\n   being himself consul; a governor of, or a military commander in, a\n   province. He was usually one who had previously been consul.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proconsular', 'a.', 'Alt. of Proconsulary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proconsulary', 'a.', 'Of or pertaining of a proconsul; as, proconsular\n   powers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proconsulary', 'a.', 'Under the government of a proconsul; as, a\n   proconsular province.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proconsulate', 'n.', 'The office jurisdiction of a proconsul, or the term\n   of his office.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proconsulship', 'n.', 'Proconsulate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Procrastinated', 'imp. & p. p.', 'of Procrastinate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Procrastinating', 'p. pr. & vb. n.', 'of Procrastinate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Procrastinate', 'v. t.', 'To put off till to-morrow, or from day to day; to\n   defer; to postpone; to delay; as, to procrastinate repentance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Procrastinate', 'v. i.', 'To delay; to be dilatory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Procrastination', 'n.', 'The act or habit of procrastinating, or putting\n   off to a future time; delay; dilatoriness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Procrastinator', 'n.', 'One who procrastinates, or defers the performance\n   of anything.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Procrastinatory', 'a.', 'Of or pertaining to procrastination; dilatory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Procrastine', 'v. t.', 'To procrastinate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Procreant', 'a.', 'Generating; producing; productive; fruitful; assisting\n   in procreation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Procreant', 'n.', 'One who, or that which, procreates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Procreated', 'imp. & p. p.', 'of Procreate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Procreating', 'p. pr. & vb. n.', 'of Procreate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Procreate', 'v. t.', 'To generate and produce; to beget; to engender.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Procreation', 'n.', 'The act of begetting; generation and production of\n   young.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Procreative', 'a.', 'Having the power to beget; generative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Procreativeness', 'n.', 'The power of generating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Procreator', 'n.', 'One who begets; a father or sire; a generator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Procris', 'n.', 'Any species of small moths of the genus Procris. The\n   larvae of some species injure the grapevine by feeding in groups upon\n   the leaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Procrustean', 'a.', 'Of or pertaining to Procrustes, or the mode of torture\n   practiced by him; producing conformity by violent means; as, the\n   Procrustean treatment; a Procrustean limit. See Procrustes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Procrusteanized', 'imp. & p. p.', 'of Procrusteanize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Procrusteanizing', 'p. pr. & vb. n.', 'of Procrusteanize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Procrusteanize', 'v. t.', 'To stretch or contract according to some rule or\n   standard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Procrustes', 'n.', 'A celebrated legendary highwayman of Attica, who tied\n   his victims upon an iron bed, and, as the case required, either\n   stretched or cut of their legs to adapt them to its length; -- whence\n   the metaphorical phrase, the bed of Procrustes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Procrustesian', 'a.', 'See Procrustean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proctitis', 'n.', 'Inflammation of the rectum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proctocele', 'n.', 'Inversion and prolapse of the mucous coat of the\n   rectum, from relaxation of the sphincter, with more or less swelling;\n   prolapsus ani.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proctodaeum', 'n.', 'See Mesenteron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proctor', 'n.', 'One who is employed to manage to affairs of another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proctor', 'n.', 'A person appointed to collect alms for those who could not\n   go out to beg for themselves, as lepers, the bedridden, etc.; hence a\n   beggar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proctor', 'n.', 'An officer employed in admiralty and ecclesiastical\n   causes. He answers to an attorney at common law, or to a solicitor in\n   equity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proctor', 'n.', 'A representative of the clergy in convocation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proctor', 'n.', 'An officer in a university or college whose duty it is to\n   enforce obedience to the laws of the institution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proctor', 'v. t.', 'To act as a proctor toward; to manage as an attorney or\n   agent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proctorage', 'n.', 'Management by a proctor, or as by a proctor; hence,\n   control; superintendence; -- in contempt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proctorial', 'a.', 'Of or pertaining to a proctor, esp. an academic\n   proctor; magisterial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proctorical', 'a.', 'Proctorial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proctorship', 'n.', 'The office or dignity of a proctor; also, the term of\n   his office.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proctotomy', 'n.', 'An incision into the rectum, as for the division of a\n   stricture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proctucha', 'n. pl.', 'A division of Turbellaria including those that have\n   an intestine terminating posteriorly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proctucha', 'n. pl.', 'The Nemertina.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Procumbent', 'a.', 'Lying down, or on the face; prone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Procumbent', 'a.', 'Lying on the ground, but without putting forth roots;\n   trailing; prostrate; as, a procumbent stem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Procurable', 'a.', 'Capable of being procured; obtainable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Procuracies', 'pl. ', 'of Procuracy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Procuracy', 'n.', 'The office or act of a proctor or procurator; management\n   for another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Procuracy', 'n.', 'Authority to act for another; a proxy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Procuration', 'n.', 'The act of procuring; procurement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Procuration', 'n.', 'The management of anothers affairs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Procuration', 'n.', 'The instrument by which a person is empowered to\n   transact the affairs of another; a proxy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Procuration', 'n.', 'A sum of money paid formerly to the bishop or\n   archdeacon, now to the ecclesiastical commissioners, by an incumbent,\n   as a commutation for entertainment at the time of visitation; -- called\n   also proxy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Procurator', 'n.', 'One who manages anothers affairs, either generally or\n   in a special matter; an agent; a proctor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Procurator', 'n.', 'A governor of a province under the emperors; also, one\n   who had charge of the imperial revenues in a province; as, the\n   procurator of Judea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Procuratorial', 'a.', 'Of or pertaining to a procurator, or proctor; made\n   by a proctor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Procuratorship', 'n.', 'The office or term of a procurator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Procuratory', 'a.', 'Tending to, or authorizing, procuration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Procured', 'imp. & p. p.', 'of Procure');
  INSERT INTO words (word, word_type, description) VALUES  (    'Procuring', 'p. pr. & vb. n.', 'of Procure');
  INSERT INTO words (word, word_type, description) VALUES  (    'Procure', 'v. t.', 'To bring into possession; to cause to accrue to, or to\n   come into possession of; to acquire or provide for ones self or for\n   another; to gain; to get; to obtain by any means, as by purchase or\n   loan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Procure', 'v. t.', 'To contrive; to bring about; to effect; to cause.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Procure', 'v. t.', 'To solicit; to entreat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Procure', 'v. t.', 'To cause to come; to bring; to attract.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Procure', 'v. t.', 'To obtain for illicit intercourse or prostitution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Procure', 'v. i.', 'To pimp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Procure', 'v. i.', 'To manage business for another in court.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Procurement', 'n.', 'The act of procuring or obtaining; obtainment;\n   attainment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Procurement', 'n.', 'Efficient contrivance; management; agency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Procurer', 'n.', 'One who procures, or obtains; one who, or that which,\n   brings on, or causes to be done, esp. by corrupt means.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Procurer', 'n.', 'One who procures the gratification of lust for another; a\n   pimp; a pander.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Procuress', 'n.', 'A female procurer, or pander.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Procyon', 'n.', 'A star of the first magnitude in the constellation Canis\n   Minor, or the Little Dog.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Procyon', 'n.', 'A genus of mammals including the raccoon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prod', 'n.', 'A pointed instrument for pricking or puncturing, as a goad,\n   an awl, a skewer, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prod', 'n.', 'A prick or stab which a pointed instrument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prod', 'n.', 'A light kind of crossbow; -- in the sense, often spelled\n   prodd.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prodded', 'imp. & p. p.', 'of Prod');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prodding', 'p. pr. & vb. n.', 'of Prod');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prod', 'v. t.', 'To thrust some pointed instrument into; to prick with\n   something sharp; as, to prod a soldier with a bayonet; to prod oxen;\n   hence, to goad, to incite, to worry; as, to prod a student.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prodd', 'n.', 'A crossbow. See Prod, 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prodigal', 'a.', 'Given to extravagant expenditure; expending money or\n   other things without necessity; recklessly or viciously profuse;\n   lavish; wasteful; not frugal or economical; as, a prodigal man; the\n   prodigal son; prodigal giving; prodigal expenses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prodigal', 'n.', 'One who expends money extravagantly, viciously, or\n   without necessity; one that is profuse or lavish in any expenditure; a\n   waster; a spendthrift.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prodigality', 'n.', 'Extravagance in expenditure, particularly of money;\n   excessive liberality; profusion; waste; -- opposed to frugality,\n   economy, and parsimony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prodigalize', 'v. i.', 'To act as a prodigal; to spend liberally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prodigalize', 'v. t.', 'To expend lavishly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prodigally', 'adv.', 'In a prodigal manner; with profusion of expense;\n   extravagantly; wasteful; profusely; lavishly; as, an estate prodigally\n   dissipated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prodigate', 'v. t.', 'To squander.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prodigence', 'n.', 'Waste; profusion; prodigality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prodigious', 'a.', 'Of the nature of a prodigy; marvelous; wonderful;\n   portentous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prodigious', 'a.', 'Extraordinary in bulk, extent, quantity, or degree;\n   very great; vast; huge; immense; as, a prodigious mountain; a\n   prodigious creature; a prodigious blunder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prodigiously', 'adv.', 'Enormously; wonderfully; astonishingly; as,\n   prodigiously great.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prodigiously', 'adv.', 'Very much; extremely; as, he was prodigiously\n   pleased.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prodigiousness', 'n.', 'The quality or state of being prodigious; the state\n   of having qualities that excite wonder or astonishment; enormousness;\n   vastness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prodigies', 'pl. ', 'of Prodigy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prodigy', 'n.', 'Something extraordinary, or out of the usual course of\n   nature, from which omens are drawn; a portent; as, eclipses and meteors\n   were anciently deemed prodigies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prodigy', 'n.', 'Anything so extraordinary as to excite wonder or\n   astonishment; a marvel; as, a prodigy of learning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prodigy', 'n.', 'A production out of ordinary course of nature; an abnormal\n   development; a monster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prodition', 'n.', 'Disclosure; treachery; treason.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proitor', 'n.', 'A traitor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proditorious', 'a.', 'Treacherous; perfidious; traitorous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proditorious', 'a.', 'Apt to make unexpected revelations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proditory', 'a.', 'Treacherous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prodromal', 'a.', 'Of or pertaining to prodromes; as, the prodromal stage\n   of a disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prodrome', 'n.', 'A forerunner; a precursor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prodromous', 'a.', 'Precursory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prodromus', 'n.', 'A prodrome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prodromus', 'n.', 'A preliminary course or publication; -- used esp. in the\n   titles of elementary works.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Produced', 'imp. & p. p.', 'of Produce');
  INSERT INTO words (word, word_type, description) VALUES  (    'Producing', 'p. pr. & vb. n.', 'of Produce');
  INSERT INTO words (word, word_type, description) VALUES  (    'Produce', 'v. t.', 'To bring forward; to lead forth; to offer to view or\n   notice; to exhibit; to show; as, to produce a witness or evidence in\n   court.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Produce', 'v. t.', 'To bring forth, as young, or as a natural product or\n   growth; to give birth to; to bear; to generate; to propagate; to yield;\n   to furnish; as, the earth produces grass; trees produce fruit; the\n   clouds produce rain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Produce', 'v. t.', 'To cause to be or to happen; to originate, as an effect\n   or result; to bring about; as, disease produces pain; vice produces\n   misery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Produce', 'v. t.', 'To give being or form to; to manufacture; to make; as,\n   a manufacturer produces excellent wares.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Produce', 'v. t.', 'To yield or furnish; to gain; as, money at interest\n   produces an income; capital produces profit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Produce', 'v. t.', 'To draw out; to extend; to lengthen; to prolong; as, to\n   produce a mans life to threescore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Produce', 'v. t.', 'To extend; -- applied to a line, surface, or solid; as,\n   to produce a side of a triangle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Produce', 'v. i.', 'To yield or furnish appropriate offspring, crops,\n   effects, consequences, or results.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Produce', 'n.', 'That which is produced, brought forth, or yielded;\n   product; yield; proceeds; result of labor, especially of agricultural\n   labors');
  INSERT INTO words (word, word_type, description) VALUES  (    'Produce', 'n.', 'agricultural products.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Producement', 'n.', 'Production.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Producent', 'n.', 'One who produces, or offers to notice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Producer', 'n.', 'One who produces, brings forth, or generates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Producer', 'n.', 'One who grows agricultural products, or manufactures\n   crude materials into articles of use.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Producer', 'n.', 'A furnace for producing combustible gas which is used for\n   fuel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Producibility', 'n.', 'The quality or state of being producible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Producible', 'a.', 'Capable of being produced, brought forward, brought\n   forth, generated, made, or extended.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Product', 'n.', 'Anything that is produced, whether as the result of\n   generation, growth, labor, or thought, or by the operation of\n   involuntary causes; as, the products of the season, or of the farm; the\n   products of manufactures; the products of the brain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Product', 'n.', 'The number or sum obtained by adding one number or\n   quantity to itself as many times as there are units in another number;\n   the number resulting from the multiplication of two or more numbers;\n   as, the product of the multiplication of 7 by 5 is 35. In general, the\n   result of any kind of multiplication. See the Note under\n   Multiplication.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Product', 'v. t.', 'To produce; to bring forward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Product', 'v. t.', 'To lengthen out; to extend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Product', 'v. t.', 'To produce; to make.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Productibility', 'n.', 'The state of being productible; producibility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Productible', 'a.', 'Capable of being produced; producible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Productile', 'a.', 'Capable of being extended or prolonged; extensible;\n   ductile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Production', 'n.', 'The act or process or producing, bringing forth, or\n   exhibiting to view; as, the production of commodities, of a witness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Production', 'n.', 'That which is produced, yielded, or made, whether\n   naturally, or by the application of intelligence and labor; as, the\n   productions of the earth; the productions of handicraft; the\n   productions of intellect or genius.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Production', 'n.', 'The act of lengthening out or prolonging.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Productive', 'a.', 'Having the quality or power of producing; yielding or\n   furnishing results; as, productive soil; productive enterprises;\n   productive labor, that which increases the number or amount of\n   products.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Productive', 'a.', 'Bringing into being; causing to exist; producing;\n   originative; as, an age productive of great men; a spirit productive of\n   heroic achievements.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Productive', 'a.', 'Producing, or able to produce, in large measure;\n   fertile; profitable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Productivity', 'n.', 'The quality or state of being productive;\n   productiveness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Productress', 'n.', 'A female producer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Productus', 'n.', 'An extinct genus of brachiopods, very characteristic of\n   the Carboniferous rocks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proeguminal', 'a.', 'Serving to predispose; predisposing; as, a proeguminal\n   cause of disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proem', 'n.', 'Preface; introduction; preliminary observations; prelude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proem', 'v. t.', 'To preface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proembryo', 'n.', 'The series of cells formed in the ovule of a flowering\n   plant after fertilization, but before the formation of the embryo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proembryo', 'n.', 'The primary growth from the spore in certain\n   cryptogamous plants; as, the proembryo, or protonema, of mosses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proemial', 'a.', 'Introductory; prefatory; preliminary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proemptosis', 'n.', 'The addition of a day to the lunar calendar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proface', 'interj.', 'Much good may it do you! -- a familiar salutation or\n   welcome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Profanate', 'v. t.', 'To profane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Profanation', 'v. t.', 'The act of violating sacred things, or of treating\n   them with contempt or irreverence; irreverent or too familiar treatment\n   or use of what is sacred; desecration; as, the profanation of the\n   Sabbath; the profanation of a sanctuary; the profanation of the name of\n   God.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Profanation', 'v. t.', 'The act of treating with abuse or disrespect, or\n   with undue publicity, or lack of delicacy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Profane', 'a.', 'Not sacred or holy; not possessing peculiar sanctity;\n   unconsecrated; hence, relating to matters other than sacred; secular;\n   -- opposed to sacred, religious, or inspired; as, a profane place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Profane', 'a.', 'Unclean; impure; polluted; unholy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Profane', 'a.', 'Treating sacred things with contempt, disrespect,\n   irreverence, or undue familiarity; irreverent; impious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Profane', 'a.', 'Irreverent in language; taking the name of God in vain;\n   given to swearing; blasphemous; as, a profane person, word, oath, or\n   tongue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Profaned', 'imp. & p. p.', 'of Profane');
  INSERT INTO words (word, word_type, description) VALUES  (    'Profaning', 'p. pr. & vb. n.', 'of Profane');
  INSERT INTO words (word, word_type, description) VALUES  (    'Profane', 'a.', 'To violate, as anything sacred; to treat with abuse,\n   irreverence, obloquy, or contempt; to desecrate; to pollute; as, to\n   profane the name of God; to profane the Scriptures, or the ordinance of\n   God.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Profane', 'a.', 'To put to a wrong or unworthy use; to make a base\n   employment of; to debase; to abuse; to defile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Profanely', 'adv.', 'In a profane manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Profaneness', 'n.', 'The quality or state of being profane; especially, the\n   use of profane language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Profaner', 'n.', 'One who treats sacred things with irreverence, or defiles\n   what is holy; one who uses profane language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Profanity', 'n.', 'The quality or state of being profane; profaneness;\n   irreverence; esp., the use of profane language; blasphemy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Profanity', 'n.', 'That which is profane; profane language or acts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Profection', 'n.', 'A setting out; a going forward; advance; progression.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Profectitious', 'a.', 'Proceeding from, as from a parent; derived, as from\n   an ancestor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Profert', 'n.', 'The exhibition or production of a record or paper in open\n   court, or an allegation that it is in court.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Professed', 'imp. & p. p.', 'of Profess');
  INSERT INTO words (word, word_type, description) VALUES  (    'Professing', 'p. pr. & vb. n.', 'of Profess');
  INSERT INTO words (word, word_type, description) VALUES  (    'Profess', 'v. t.', 'To make open declaration of, as of ones knowledge,\n   belief, action, etc.; to avow or acknowledge; to confess publicly; to\n   own or admit freely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Profess', 'v. t.', 'To set up a claim to; to make presence to; hence, to\n   put on or present an appearance of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Profess', 'v. t.', 'To present to knowledge of, to proclaim ones self\n   versed in; to make ones self a teacher or practitioner of, to set up\n   as an authority respecting; to declare (ones self to be such); as, he\n   professes surgery; to profess ones self a physician.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Profess', 'v. i.', 'To take a profession upon ones self by a public\n   declaration; to confess.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Profess', 'v. i.', 'To declare friendship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Provessel', 'a.', 'Openly declared, avowed, acknowledged, or claimed; as, a\n   professed foe; a professed tyrant; a professed Christian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Professedly', 'adv.', 'By profession.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Profession', 'v.', 'The act of professing or claiming; open declaration;\n   public avowal or acknowledgment; as, professions of friendship; a\n   profession of faith.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Profession', 'v.', 'That which one professed; a declaration; an avowal; a\n   claim; as, his professions are insincere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Profession', 'v.', 'That of which one professed knowledge; the occupation,\n   if not mechanical, agricultural, or the like, to which one devotes\n   ones self; the business which one professes to understand, and to\n   follow for subsistence; calling; vocation; employment; as, the\n   profession of arms; the profession of a clergyman, lawyer, or\n   physician; the profession of lecturer on chemistry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Profession', 'v.', 'The collective body of persons engaged in a calling;\n   as, the profession distrust him.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Profession', 'v.', 'The act of entering, or becoming a member of, a\n   religious order.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Professional', 'a.', 'Of or pertaining to a profession, or calling;\n   conforming to the rules or standards of a profession; following a\n   profession; as, professional knowledge; professional conduct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Professional', 'a.', 'Engaged in by professionals; as, a professional race;\n   -- opposed to amateur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Professional', 'n.', 'A person who prosecutes anything professionally, or\n   for a livelihood, and not in the character of an amateur; a\n   professional worker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Professionalism', 'n.', 'The following of a profession, sport, etc., as an\n   occupation; -- opposed to amateurism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Professionalist', 'n.', 'professional person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Professionally', 'adv.', 'In a professional manner or capacity; by\n   profession or calling; in the exercise of ones profession; one\n   employed professionally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Professor', 'n.', 'One who professed, or makes open declaration of, his\n   sentiments or opinions; especially, one who makes a public avowal of\n   his belief in the Scriptures and his faith in Christ, and thus unites\n   himself to the visible church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Professor', 'n.', 'One who professed, or publicly teaches, any science or\n   branch of learning; especially, an officer in a university, college, or\n   other seminary, whose business it is to read lectures, or instruct\n   students, in a particular branch of learning; as a professor of\n   theology, of botany, of mathematics, or of political economy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Professorial', 'a.', 'Of or pertaining to a professor; as, the professional\n   chair; professional interest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Professorialism', 'n.', 'The character, manners, or habits of a professor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Professoriat', 'n.', 'See Professoriate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Professoriate', 'n.', 'The body of professors, or the professorial staff,\n   in a university or college.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Professoriate', 'n.', 'A professorship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Professorship', 'n.', 'The office or position of a professor, or public\n   teacher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Professory', 'a.', 'Of or pertaining to a professor; professorial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proffered', 'imp. & p. p.', 'of Proffer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proffering', 'p. pr. & vb. n.', 'of Proffer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proffer', 'v. t.', 'To offer for acceptance; to propose to give; to make a\n   tender of; as, to proffer a gift; to proffer services; to proffer\n   friendship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proffer', 'v. t.', 'To essay or attempt of ones own accord; to undertake,\n   or propose to undertake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proffer', 'n.', 'An offer made; something proposed for acceptance by\n   another; a tender; as, proffers of peace or friendship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proffer', 'n.', 'Essay; attempt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Profferer', 'n.', 'One who proffers something.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proficience', 'n.', 'Alt. of Proficiency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proficiency', 'n.', 'The quality of state of being proficient; advance in\n   the acquisition of any art, science, or knowledge; progression in\n   knowledge; improvement; adeptness; as, to acquire proficiency in music.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proficient', 'n.', 'One who has made considerable advances in any business,\n   art, science, or branch of learning; an expert; an adept; as,\n   proficient in a trade; a proficient in mathematics, music, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proficient', 'a.', 'Well advanced in any branch of knowledge or skill;\n   possessed of considerable acquirements; well-skilled; versed; adept,');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proficiently', 'adv.', 'In a proficient manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proficuous', 'a.', 'Profitable; advantageous; useful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Profile', 'n.', 'An outline, or contour; as, the profile of an apple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Profile', 'n.', 'A human head represented sidewise, or in a side view; the\n   side face or half face.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Profile', 'n.', 'A section of any member, made at right angles with its\n   main lines, showing the exact shape of moldings and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Profile', 'n.', 'A drawing exhibiting a vertical section of the ground\n   along a surveyed line, or graded work, as of a railway, showing\n   elevations, depressions, grades, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Profiled', 'imp. & p. p.', 'of Profile');
  INSERT INTO words (word, word_type, description) VALUES  (    'Profiling', 'p. pr. & vb. n.', 'of Profile');
  INSERT INTO words (word, word_type, description) VALUES  (    'Profile', 'n.', 'to draw the outline of; to draw in profile, as an\n   architectural member.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Profile', 'n.', 'To shape the outline of an object by passing a cutter\n   around it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Profiling', 'n.', 'In the construction of fieldworks, the erection at\n   proper intervals of wooden profiles, to show to the workmen the\n   sectional form of the parapets at those points.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Profilist', 'n.', 'One who takes profiles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Profit', 'n.', 'Acquisition beyond expenditure; excess of value received\n   for producing, keeping, or selling, over cost; hence, pecuniary gain in\n   any transaction or occupation; emolument; as, a profit on the sale of\n   goods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Profit', 'n.', 'Accession of good; valuable results; useful consequences;\n   benefit; avail; gain; as, an office of profit,');
  INSERT INTO words (word, word_type, description) VALUES  (    'Profited', 'imp. & p. p.', 'of Profit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Profiting', 'p. pr. & vb. n.', 'of Profit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Profit', 'n.', 'To be of service to; to be good to; to help on; to benefit;\n   to advantage; to avail; to aid; as, truth profits all men.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Profit', 'v. i.', 'To gain advantage; to make improvement; to improve; to\n   gain; to advance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Profit', 'v. i.', 'To be of use or advantage; to do or bring good.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Profitable', 'a.', 'Yielding or bringing profit or gain; gainful;\n   lucrative; useful; helpful; advantageous; beneficial; as, a profitable\n   trade; profitable business; a profitable study or profession.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Profiting', 'n.', 'Gain; advantage; profit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Profitless', 'a.', 'Without profit; unprofitable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Profligacy', 'a.', 'The quality of state of being profligate; a profligate\n   or very vicious course of life; a state of being abandoned in moral\n   principle and in vice; dissoluteness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Profligate', 'a.', 'Overthrown; beaten; conquered.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Profligate', 'a.', 'Broken down in respect of rectitude, principle, virtue,\n   or decency; openly and shamelessly immoral or vicious; dissolute; as,\n   profligate man or wretch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Profligate', 'n.', 'An abandoned person; one openly and shamelessly\n   vicious; a dissolute person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Profligate', 'v. t.', 'To drive away; to overcome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Profligately', 'adv.', 'In a profligate manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Profligateness', 'n.', 'The quality of being profligate; an abandoned\n   course of life; profligacy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Profligation', 'n.', 'Defeat; rout; overthrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Profluence', 'n.', 'Quality of being profluent; course.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Profluent', 'a.', 'Flowing forward,');
  INSERT INTO words (word, word_type, description) VALUES  (    'Profound', 'a.', 'Descending far below the surface; opening or reaching to\n   a great depth; deep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Profound', 'a.', 'Intellectually deep; entering far into subjects; reaching\n   to the bottom of a matter, or of a branch of learning; thorough; as, a\n   profound investigation or treatise; a profound scholar; profound\n   wisdom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Profound', 'a.', 'Characterized by intensity; deeply felt; pervading;\n   overmastering; far-reaching; strongly impressed; as, a profound sleep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Profound', 'a.', 'Bending low, exhibiting or expressing deep humility;\n   lowly; submissive; as, a profound bow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Profound', 'n.', 'The deep; the sea; the ocean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Profound', 'n.', 'An abyss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Profound', 'v. t.', 'To cause to sink deeply; to cause to dive or penetrate\n   far down.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Profound', 'v. i.', 'To dive deeply; to penetrate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Profoundly', 'adv.', 'In a profound manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Profoundness', 'n.', 'The quality or state of being profound; profundity;\n   depth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Profulgent', 'a.', 'Shining forth; brilliant; effulgent.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-ties', 'pl. ', 'of Profundity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Profundity', 'n.', 'The quality or state of being profound; depth of place,\n   knowledge, feeling, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Profuse', 'a.', 'Pouring forth with fullness or exuberance; bountiful;\n   exceedingly liberal; giving without stint; as, a profuse government;\n   profuse hospitality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Profuse', 'a.', 'Superabundant; excessive; prodigal; lavish; as, profuse\n   expenditure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Profuse', 'v. t.', 'To pour out; to give or spend liberally; to lavish; to\n   squander.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Profusely', 'adv.', 'In a profuse manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Profuseness', 'n.', 'Extravagance; profusion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Profusion', 'n.', 'The act of one who is profuse; a lavishing or pouring\n   out without sting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Profusion', 'n.', 'Abundance; exuberant plenty; lavish supply; as, a\n   profusion of commodities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Profusive', 'a.', 'Profuse; lavish; prodigal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Progged', 'imp. & p. p.', 'of Prog');
  INSERT INTO words (word, word_type, description) VALUES  (    'Progging', 'p. pr. & vb. n.', 'of Prog');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prog', 'v. i.', 'To wander about and beg; to seek food or other supplies by\n   low arts; to seek for advantage by mean shift or tricks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prog', 'v. i.', 'To steal; to rob; to filch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prog', 'v. i.', 'To prick; to goad; to progue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prog', 'n.', 'Victuals got by begging, or vagrancy; victuals of any kind;\n   food; supplies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prog', 'n.', 'A vagrant beggar; a tramp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prog', 'n.', 'A goal; progue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Progenerate', 'v. t.', 'To beget; to generate; to produce; to procreate;\n   as, to progenerate a race.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Progeneration', 'n.', 'The act of begetting; propagation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Progenitor', 'n.', 'An ancestor in the direct line; a forefather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Progenitorship', 'n.', 'The state of being a progenitor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Progenitress', 'n.', 'A female progenitor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Progeniture', 'n.', 'A begetting, or birth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Progeny', 'n.', 'Descendants of the human kind, or offspring of other\n   animals; children; offspring; race, lineage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proglottid', 'n.', 'Proglottis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proglottides', 'pl. ', 'of Proglottis');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proglottis', 'n.', 'One of the free, or nearly free, segments of a\n   tapeworm. It contains both male and female reproductive organs, and is\n   capable of a brief independent existence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prognathi', 'n. pl.', 'A comprehensive group of mankind, including those\n   that have prognathous jaws.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prognathic', 'a.', 'Prognathous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prognathism', 'n.', 'Projection of the jaws.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prognathous', 'a.', 'Having the jaws projecting beyond the upper part of\n   the face; -- opposed to orthognathous. See Gnathic index, under\n   Gnathic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Progne', 'n.', 'A swallow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Progne', 'n.', 'A genus of swallows including the purple martin. See\n   Martin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Progne', 'n.', 'An American butterfly (Polygonia, / Vanessa, Progne). It is\n   orange and black above, grayish beneath, with an L-shaped silver mark\n   on the hind wings. Called also gray comma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prognosis', 'n.', 'The act or art of foretelling the course and termination\n   of a disease; also, the outlook afforded by this act of judgment; as,\n   the prognosis of hydrophobia is bad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prognostic', 'a.', 'Indicating something future by signs or symptoms;\n   foreshowing; aiding in prognosis; as, the prognostic symptoms of a\n   disease; prognostic signs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prognostic', 'a.', 'That which prognosticates; a sign by which a future\n   event may be known or foretold; an indication; a sign or omen; hence, a\n   foretelling; a prediction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prognostic', 'a.', 'A sign or symptom indicating the course and termination\n   of a disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prognostic', 'v. t.', 'To prognosticate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prognosticable', 'a.', 'Capable of being prognosticated or foretold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prognosticated', 'imp. & p. p.', 'of Prognosticate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prognosticating', 'p. pr. & vb. n.', 'of Prognosticate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prognosticate', 'v. t.', 'To indicate as future; to foretell from signs or\n   symptoms; to prophesy; to foreshow; to predict; as, to prognosticate\n   evil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prognostication', 'n.', 'The act of foreshowing or foretelling something\n   future by present signs; prediction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prognostication', 'n.', 'That which foreshows; a foretoken.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prognosticator', 'n.', 'One who prognosticates; a foreknower or foreteller\n   of a future course or event by present signs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Program', 'n.', 'Same as Programme.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Programmata', 'pl. ', 'of Programma');
  INSERT INTO words (word, word_type, description) VALUES  (    'Programma', 'n.', 'Any law, which, after it had passed the Athenian senate,\n   was fixed on a tablet for public inspection previously to its being\n   proposed to the general assembly of the people.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Programma', 'n.', 'An edict published for public information; an official\n   bulletin; a public proclamation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Programma', 'n.', 'See Programme.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Programma', 'n.', 'A preface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Programme', 'n.', 'That which is written or printed as a public notice or\n   advertisement; a scheme; a prospectus; especially, a brief outline or\n   explanation of the order to be pursued, or the subjects embraced, in\n   any public exercise, performance, or entertainment; a preliminary\n   sketch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Progress', 'n.', 'A moving or going forward; a proceeding onward; an\n   advance');
  INSERT INTO words (word, word_type, description) VALUES  (    'Progress', 'n.', 'In actual space, as the progress of a ship, carriage,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Progress', 'n.', 'In the growth of an animal or plant; increase.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Progress', 'n.', 'In business of any kind; as, the progress of a\n   negotiation; the progress of art.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Progress', 'n.', 'In knowledge; in proficiency; as, the progress of a child\n   at school.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Progress', 'n.', 'Toward ideal completeness or perfection in respect of\n   quality or condition; -- applied to individuals, communities, or the\n   race; as, social, moral, religious, or political progress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Progress', 'n.', 'A journey of state; a circuit; especially, one made by a\n   sovereign through parts of his own dominions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Progressed', 'imp. & p. p.', 'of Progress');
  INSERT INTO words (word, word_type, description) VALUES  (    'Progressing', 'p. pr. & vb. n.', 'of Progress');
  INSERT INTO words (word, word_type, description) VALUES  (    'Progress', 'v. i.', 'To make progress; to move forward in space; to\n   continue onward in course; to proceed; to advance; to go on; as,\n   railroads are progressing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Progress', 'v. i.', 'To make improvement; to advance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Progress', 'v. t.', 'To make progress in; to pass through.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Progression', 'n.', 'The act of moving forward; a proceeding in a course;\n   motion onward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Progression', 'n.', 'Course; passage; lapse or process of time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Progression', 'n.', 'Regular or proportional advance in increase or\n   decrease of numbers; continued proportion, arithmetical, geometrical,\n   or harmonic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Progression', 'n.', 'A regular succession of tones or chords; the movement\n   of the parts in harmony; the order of the modulations in a piece from\n   key to key.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Progressional', 'a.', 'Of or pertaining to progression; tending to, or\n   capable of, progress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Progressionist', 'n.', 'One who holds to a belief in the progression of\n   society toward perfection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Progressionist', 'n.', 'One who maintains the doctrine of progression in\n   organic forms; -- opposed to uniformitarian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Progressist', 'n.', 'One who makes, or holds to, progress; a\n   progressionist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Progressive', 'a.', 'Moving forward; proceeding onward; advancing; evincing\n   progress; increasing; as, progressive motion or course; -- opposed to\n   retrograde.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Progressive', 'a.', 'Improving; as, art is in a progressive state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Progue', 'v. i.', 'To prog.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Progue', 'n.', 'A sharp point; a goad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Progue', 'v. t. ', 'To prick; to goad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proheme', 'n.', 'Proem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prohibited', 'imp. & p. p.', 'of Prohibit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prohibiting', 'p. pr. & vb. n.', 'of Prohibit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prohibit', 'v. t.', 'To forbid by authority; to interdict; as, God\n   prohibited Adam from eating of the fruit of a certain tree; we prohibit\n   a person from doing a thing, and also the doing of the thing; as, the\n   law prohibits men from stealing, or it prohibits stealing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prohibit', 'v. t.', 'To hinder; to debar; to prevent; to preclude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prohibiter', 'n.', 'One who prohibits or forbids; a forbidder; an\n   interdicter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prohibition', 'n.', 'The act of prohibiting; a declaration or injunction\n   forbidding some action; interdict.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prohibition', 'n.', 'Specifically, the forbidding by law of the sale of\n   alcoholic liquors as beverages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prohibitionist', 'n.', 'One who favors prohibitory duties on foreign goods\n   in commerce; a protectionist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prohibitionist', 'n.', 'One who favors the prohibition of the sale (or of\n   the sale and manufacture) of alcoholic liquors as beverages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prohibitive', 'a.', 'That prohibits; prohibitory; as, a tax whose effect is\n   prohibitive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prohibitory', 'a.', 'Tending to prohibit, forbid, or exclude; implying\n   prohibition; forbidding; as, a prohibitory law; a prohibitory price.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proin', 'v. t.', 'To lop; to trim; to prune; to adorn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proin', 'v. i.', 'To employed in pruning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Project', 'n.', 'The place from which a thing projects, or starts forth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Project', 'n.', 'That which is projected or designed; something intended or\n   devised; a scheme; a design; a plan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Project', 'n.', 'An idle scheme; an impracticable design; as, a man given\n   to projects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Projected', 'imp. & p. p.', 'of Project');
  INSERT INTO words (word, word_type, description) VALUES  (    'Projecting', 'p. pr. & vb. n.', 'of Project');
  INSERT INTO words (word, word_type, description) VALUES  (    'Project', 'v. t.', 'To throw or cast forward; to shoot forth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Project', 'v. t.', 'To cast forward or revolve in the mind; to contrive; to\n   devise; to scheme; as, to project a plan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Project', 'v. t.', 'To draw or exhibit, as the form of anything; to\n   delineate; as, to project a sphere, a map, an ellipse, and the like; --\n   sometimes with on, upon, into, etc.; as, to project a line or point\n   upon a plane. See Projection, 4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Project', 'v. i.', 'To shoot forward; to extend beyond something else; to\n   be prominent; to jut; as, the cornice projects; branches project from\n   the tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Project', 'v. i.', 'To form a project; to scheme.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Projectile', 'a.', 'Projecting or impelling forward; as, a projectile\n   force.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Projectile', 'a.', 'Caused or imparted by impulse or projection; impelled\n   forward; as, projectile motion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Projectile', 'n.', 'A body projected, or impelled forward, by force;\n   especially, a missile adapted to be shot from a firearm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Projectile', 'n.', 'A part of mechanics which treats of the motion, range,\n   time of flight, etc., of bodies thrown or driven through the air by an\n   impelling force.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Projection', 'n.', 'The act of throwing or shooting forward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Projection', 'n.', 'A jutting out; also, a part jutting out, as of a\n   building; an extension beyond something else.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Projection', 'n.', 'The act of scheming or planning; also, that which is\n   planned; contrivance; design; plan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Projection', 'n.', 'The representation of something; delineation; plan;\n   especially, the representation of any object on a perspective plane, or\n   such a delineation as would result were the chief points of the object\n   thrown forward upon the plane, each in the direction of a line drawn\n   through it from a given point of sight, or central point; as, the\n   projection of a sphere. The several kinds of projection differ\n   according to the assumed point of sight and plane of projection in\n   each.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Projection', 'n.', 'Any method of representing the surface of the earth\n   upon a plane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Projectment', 'n.', 'Design; contrivance; projection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Projector', 'n.', 'One who projects a scheme or design; hence, one who\n   forms fanciful or chimerical schemes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Projecture', 'n.', 'A jutting out beyond a surface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Projet', 'n.', 'A plan proposed; a draft of a proposed measure; a project.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proke', 'v. i.', 'To poke; to thrust.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prolapse', 'n.', 'The falling down of a part through the orifice with which\n   it is naturally connected, especially of the uterus or the rectum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prolapse', 'v. i.', 'To fall down or out; to protrude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prolapsion', 'n.', 'Prolapse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prolapsus', 'n.', 'Prolapse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prolate', 'a.', 'Stretched out; extended; especially, elongated in the\n   direction of a line joining the poles; as, a prolate spheroid; --\n   opposed to oblate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prolate', 'v. t.', 'To utter; to pronounce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prolation', 'n.', 'The act of prolating or pronouncing; utterance;\n   pronunciation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prolation', 'n.', 'The act of deferring; delay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prolation', 'n.', 'A mediaeval method of determining of the proportionate\n   duration of semibreves and minims.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prolata', 'pl. ', 'of Prolatum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prolatum', 'n.', 'A prolate spheroid. See Ellipsoid of revolution, under\n   Ellipsoid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proleg', 'n.', 'One of the fleshy legs found on the abdominal segments of\n   the larvae of Lepidoptera, sawflies, and some other insects. Those of\n   Lepidoptera have a circle of hooks. Called also proped, propleg, and\n   falseleg.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prolegate', 'n.', 'The deputy or substitute for a legate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prolegomenary', 'a.', 'Of the nature of a prolegomenon; preliminary;\n   introductory; prefatory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prolegomena', 'pl. ', 'of Prolegomenon');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prolegomenon', 'n.', 'A preliminary remark or observation; an introductory\n   discourse prefixed to a book or treatise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prolepsis', 'n.', 'A figure by which objections are anticipated or\n   prevented.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prolepsis', 'n.', 'A necessary truth or assumption; a first or assumed\n   principle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prolepsis', 'n.', 'An error in chronology, consisting in an event being\n   dated before the actual time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prolepsis', 'n.', 'The application of an adjective to a noun in\n   anticipation, or to denote the result, of the action of the verb; as,\n   to strike one dumb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proleptic', 'a.', 'Alt. of Proleptical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proleptical', 'a.', 'Of or pertaining to prolepsis; anticipative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proleptical', 'a.', 'Previous; antecedent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proleptical', 'a.', 'Anticipating the usual time; -- applied to a\n   periodical disease whose paroxysms return at an earlier hour at every\n   repetition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proleptically', 'adv.', 'In a proleptical manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proleptics', 'n.', 'The art and science of predicting in medicine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proletaire', 'n.', 'One of the common people; a low person; also, the\n   common people as a class or estate in a country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proletaneous', 'a.', 'Having a numerous offspring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proletarian', 'a.', 'Of or pertaining to the proletaries; belonging to the\n   commonalty; hence, mean; vile; vulgar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proletarian', 'n.', 'A proletary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proletariat', 'n.', 'The indigent class in the State; the body of\n   proletarians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proletariate', 'n.', 'The lower classes; beggars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proletaries', 'pl. ', 'of Proletary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proletary', 'n.', 'A citizen of the lowest class, who served the state, not\n   with property, but only by having children; hence, a common person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prolicide', 'n.', 'The crime of destroying ones offspring, either in the\n   womb or after birth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proliferate', 'v. t.', 'To produce or form cells; especially, to produce\n   cells rapidly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proliferate', 'v. t.', 'To produce zooids by budding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proliferation', 'n.', 'The continuous development of cells in tissue\n   formation; cell formation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proliferation', 'n.', 'The production of numerous zooids by budding,\n   especially when buds arise from other buds in succession.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proliferous', 'a.', 'Bearing offspring; -- applied to a flower from within\n   which another is produced, or to a branch or frond from which another\n   rises, or to a plant which is reproduced by buds or gemmae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proliferous', 'a.', 'Producing young by budding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proliferous', 'a.', 'Producing sexual zooids by budding; -- said of the\n   blastostyle of a hydroid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proliferous', 'a.', 'Producing a cluster of branchlets from a larger\n   branch; -- said of corals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prolific', 'a.', 'Having the quality of generating; producing young or\n   fruit; generative; fruitful; productive; -- applied to plants producing\n   fruit, animals producing young, etc.; -- usually with the implied idea\n   of frequent or numerous production; as, a prolific tree, female, and\n   the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prolific', 'a.', 'Serving to produce; fruitful of results; active; as, a\n   prolific brain; a controversy prolific of evil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prolific', 'a.', 'Proliferous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prolificacy', 'n.', 'Prolificness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prolifical', 'a.', 'Producing young or fruit abundantly; fruitful;\n   prolific.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prolificate', 'v. t.', 'To make prolific; to fertilize; to impregnate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prolification', 'n.', 'The generation of young.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prolification', 'n.', 'Reproduction by the growth of a plant, or part of a\n   plant, directly from an older one, or by gemmae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prolificness', 'n.', 'The quality or state of being prolific; fruitfulness;\n   prolificacy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prolix', 'a.', 'Extending to a great length; unnecessarily long; minute in\n   narration or argument; excessively particular in detail; -- rarely used\n   except with reference to discourse written or spoken; as, a prolix\n   oration; a prolix poem; a prolix sermon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prolix', 'a.', 'Indulging in protracted discourse; tedious; wearisome; --\n   applied to a speaker or writer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prolixious', 'a.', 'Dilatory; tedious; superfluous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prolixity', 'n.', 'The quality or state of being prolix; great length;\n   minute detail; as, prolixity in discourses and writings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prolixly', 'adv.', 'In a prolix manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prolixness', 'n.', 'Prolixity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prolled', 'imp. & p. p.', 'of Proll');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prolling', 'p. pr. & vb. n.', 'of Proll');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proll', 'v. t.', 'To search or prowl after; to rob; to plunder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proll', 'v. i.', 'To prowl about; to rob.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proller', 'n.', 'Prowler; thief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prolocutor', 'n.', 'One who speaks for another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prolocutor', 'n.', 'The presiding officer of a convocation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proocutorship', 'n.', 'The office of a prolocutor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prolog', 'n. & v.', 'Prologue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prologize', 'v. i.', 'To deliver a Prologue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prologizer', 'n.', 'One who prologizes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prologue', 'n.', 'The preface or introduction to a discourse, poem, or\n   performance; as, the prologue of Chaucers \"Canterbury Tales;\" esp., a\n   discourse or poem spoken before a dramatic performance');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prologue', 'n.', 'One who delivers a prologue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prologued', 'imp. & p. p.', 'of Prologue');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prologuing', 'p. pr. & vb. n.', 'of Prologue');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prologue', 'v. t.', 'To introduce with a formal preface, or prologue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prolonged', 'imp. & p. p.', 'of Prolong');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prolonging', 'p. pr. & vb. n.', 'of Prolong');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prolong', 'a.', 'To extend in space or length; as, to prolong a line.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prolong', 'a.', 'To lengthen in time; to extend the duration of; to draw\n   out; to continue; as, to prolong ones days.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prolong', 'a.', 'To put off to a distant time; to postpone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prolongable', 'a.', 'Capable of being prolonged; as, life is prolongable by\n   care.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prolongated', 'imp. & p. p.', 'of Prolongate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prolongating', 'p. pr. & vb. n.', 'of Prolongate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prolongate', 'v. t.', 'To prolong; to extend in space or in time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prolongation', 'n.', 'The act of lengthening in space or in time;\n   extension; protraction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prolongation', 'n.', 'That which forms an additional length.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prolonge', 'n.', 'A rope with a hook and a toggle, sometimes used to drag a\n   gun carriage or to lash it to the limber, and for various other\n   purposes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prolonger', 'n.', 'One who, or that which, causes an extension in time or\n   space.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prolongment', 'n.', 'Prolongation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prolusion', 'n.', 'A trial before the principal performance; a prelude;\n   hence, an introductory essay or exercise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Promanation', 'n.', 'The act of flowing forth; emanation; efflux.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Promenade', 'n.', 'A walk for pleasure, display, or exercise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Promenade', 'n.', 'A place for walking; a public walk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Promenaded', 'imp. & p. p.', 'of Promenade');
  INSERT INTO words (word, word_type, description) VALUES  (    'Promenading', 'p. pr. & vb. n.', 'of Promenade');
  INSERT INTO words (word, word_type, description) VALUES  (    'Promenade', 'v. i.', 'To walk for pleasure, display, or exercise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Promenader', 'n.', 'One who promenades.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Promerit', 'v. t.', 'To oblige; to confer a favor on.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Promerit', 'v. t.', 'To deserve; to procure by merit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Promerops', 'n.', 'Any one of several species of very brilliant birds\n   belonging to Promerops, Epimarchus, and allied genera, closely related\n   to the paradise birds, and mostly native of New Guinea. They have a\n   long curved beak and a long graduated tail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Promethea', 'n.', 'A large American bombycid moth (Callosamia promethea).\n   Its larva feeds on the sassafras, wild cherry, and other trees, and\n   suspends its cocoon from a branch by a silken band.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Promethean', 'a.', 'Of or pertaining to Prometheus. See Prometheus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Promethean', 'a.', 'Having a life-giving quality; inspiring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Promethean', 'n.', 'An apparatus for automatic ignition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Promethean', 'n.', 'A kind of lucifer match.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prometheus', 'n.', 'The son of Iapetus (one of the Titans) and Clymene,\n   fabled by the poets to have surpassed all mankind in knowledge, and to\n   have formed men of clay to whom he gave life by means of fire stolen\n   from heaven. Jupiter, being angry at this, sent Mercury to bind\n   Prometheus to Mount Caucasus, where a vulture preyed upon his liver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prominence', 'n.', 'Alt. of Prominency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prominency', 'n.', 'The quality or state of being prominent; a standing out\n   from something; conspicuousness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prominency', 'n.', 'That which is prominent; a protuberance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prominent', 'a.', 'Standing out, or projecting, beyond the line surface of\n   something; jutting; protuberant; in high relief; as, a prominent figure\n   on a vase.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prominent', 'a.', 'Hence; Distinctly manifest; likely to attract attention\n   from its size or position; conspicuous; as, a prominent feature of the\n   face; a prominent building.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prominent', 'a.', 'Eminent; distinguished above others; as, a prominent\n   character.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prominently', 'adv.', 'In a prominent manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Promiscuity', 'n.', 'Promiscuousness; confusion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Promiscuous', 'a.', 'Consisting of individuals united in a body or mass\n   without order; mingled; confused; undistinguished; as, a promiscuous\n   crowd or mass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Promiscuous', 'a.', 'Distributed or applied without order or\n   discrimination; not restricted to an individual; common;\n   indiscriminate; as, promiscuous love or intercourse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Promiscuously', 'adv.', 'In a promiscuous manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Promiscuousness', 'n.', 'The quality or state of being promiscuous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Promise', 'a.', 'In general, a declaration, written or verbal, made by one\n   person to another, which binds the person who makes it to do, or to\n   forbear to do, a specified act; a declaration which gives to the person\n   to whom it is made a right to expect or to claim the performance or\n   forbearance of a specified act.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Promise', 'a.', 'An engagement by one person to another, either in words or\n   in writing, but properly not under seal, for the performance or\n   nonperformance of some particular thing. The word promise is used to\n   denote the mere engagement of a person, without regard to the\n   consideration for it, or the corresponding duty of the party to whom it\n   is made.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Promise', 'a.', 'That which causes hope, expectation, or assurance;\n   especially, that which affords expectation of future distinction; as, a\n   youth of great promise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Promise', 'a.', 'Bestowal, fulfillment, or grant of what is promised.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Promised', 'imp. & p. p.', 'of Promise');
  INSERT INTO words (word, word_type, description) VALUES  (    'Promising', 'p. pr. & vb. n.', 'of Promise');
  INSERT INTO words (word, word_type, description) VALUES  (    'Promise', 'v. t.', 'To engage to do, give, make, or to refrain from doing,\n   giving, or making, or the like; to covenant; to engage; as, to promise\n   a visit; to promise a cessation of hostilities; to promise the payment\n   of money.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Promise', 'v. t.', 'To afford reason to expect; to cause hope or assurance\n   of; as, the clouds promise rain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Promise', 'v. t.', 'To make declaration of or give assurance of, as some\n   benefit to be conferred; to pledge or engage to bestow; as, the\n   proprietors promised large tracts of land; the city promised a reward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Promise', 'v. i.', 'To give assurance by a promise, or binding declaration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Promise', 'v. i.', 'To afford hopes or expectation; to give ground to\n   expect good; rarely, to give reason to expect evil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Promisee', 'n.', 'The person to whom a promise is made.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Promiser', 'n.', 'One who promises.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Promising', 'a.', 'Making a promise or promises; affording hope or\n   assurance; as, promising person; a promising day.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Promisor', 'n.', 'One who engages or undertakes; a promiser.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Promissive', 'a.', 'Making a promise; implying a promise; promising.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Promissorily', 'adv.', 'In a promissory manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Promissory', 'a.', 'Containing a promise or binding declaration of\n   something to be done or forborne.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Promont', 'n.', 'Promontory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Promontories', 'pl. ', 'of Promontory');
  INSERT INTO words (word, word_type, description) VALUES  (    'Promontory', 'n.', 'A high point of land or rock projecting into the sea\n   beyond the line of coast; a headland; a high cape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Promontory', 'n.', 'A projecting part. Especially: (a) The projecting angle\n   of the ventral side of the sacrum where it joins the last lumbar\n   vertebra. (b) A prominence on the inner wall of the tympanum of the\n   ear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Promerphological', 'a.', 'Relating to promorphology; as, a promorphological\n   conception.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Promorphologist', 'n.', 'One versed in the science of promorphology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Promorphology', 'n.', 'Crystallography of organic forms; -- a division of\n   morphology created by Haeckel. It is essentially stereometric, and\n   relates to a mathematical conception of organic forms. See Tectology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Promoted', 'imp. & p. p.', 'of Promote');
  INSERT INTO words (word, word_type, description) VALUES  (    'Promoting', 'p. pr. & vb. n.', 'of Promote');
  INSERT INTO words (word, word_type, description) VALUES  (    'Promote', 'v. t.', 'To contribute to the growth, enlargement, or prosperity\n   of (any process or thing that is in course); to forward; to further; to\n   encourage; to advance; to excite; as, to promote learning; to promote\n   disorder; to promote a business venture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Promote', 'v. t.', 'To exalt in station, rank, or honor; to elevate; to\n   raise; to prefer; to advance; as, to promote an officer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Promote', 'v. i.', 'To urge on or incite another, as to strife; also, to\n   inform against a person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Promoter', 'n.', 'One who, or that which, forwards, advances, or promotes;\n   an encourager; as, a promoter of charity or philosophy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Promoter', 'n.', 'Specifically, one who sets on foot, and takes the\n   preliminary steps in, a scheme for the organization of a corporation, a\n   joint-stock company, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Promoter', 'n.', 'One who excites; as, a promoter of sedition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Promoter', 'n.', 'An informer; a makebate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Promottion', 'n.', 'The act of promoting, advancing, or encouraging; the\n   act of exalting in rank or honor; also, the condition of being\n   advanced, encouraged, or exalted in honor; preferment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Promotive', 'a.', 'Tending to advance, promote, or encourage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Promove', 'v. t.', 'To move forward; to advance; to promote.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Promover', 'n.', 'A promoter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Promt', 'superl.', 'Ready and quick to act as occasion demands; meeting\n   requirements readily; not slow, dilatory, or hesitating in decision or\n   action; responding on the instant; immediate; as, prompt in obedience\n   or compliance; -- said of persons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Promt', 'superl.', 'Done or rendered quickly, readily, or immediately;\n   given without delay or hesitation; -- said of conduct; as, prompt\n   assistance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Promt', 'superl.', 'Easy; unobstructed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prompt', 'n.', 'A limit of time given for payment of an account for produce\n   purchased, this limit varying with different goods. See Prompt-note.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prompted', 'imp. & p. p.', 'of Prompt');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prompting', 'p. pr. & vb. n.', 'of Prompt');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prompt', 'v. t.', 'To assist or induce the action of; to move to action; to\n   instigate; to incite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prompt', 'v. t.', 'To suggest; to dictate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prompt', 'v. t.', 'To remind, as an actor or an orator, of words or topics\n   forgotten.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prompt-book', 'n.', 'The book used by a prompter of a theater.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prompter', 'n.', 'One who, or that which, prompts; one who admonishes or\n   incites to action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prompter', 'n.', 'One who reminds another, as an actor or an orator, of the\n   words to be spoken next; specifically, one employed for this purpose in\n   a theater.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Promptitude', 'a.', 'The quality of being prompt; quickness of decision and\n   action when occasion demands; alacrity; as, promptitude in obedience.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Promptly', 'adv.', 'In a prompt manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Promptness', 'n.', 'Promptitude; readiness; quickness of decision or\n   action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Promptness', 'n.', 'Cheerful willingness; alacrity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prompt-note', 'n.', 'A memorandum of a sale, and time when payment is due,\n   given to the purchaser at a sale of goods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Promptuary', 'a.', 'Of or pertaining to preparation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Promptuary', 'a.', 'That from which supplies are drawn; a storehouse; a\n   magazine; a repository.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prompture', 'a.', 'Suggestion; incitement; prompting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Promulgated', 'imp. & p. p.', 'of Promulgate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Promulgating', 'p. pr. & vb. n.', 'of Promulgate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Promulgate', 'v. t.', 'To make known by open declaration, as laws, decrees,\n   or tidings; to publish; as, to promulgate the secrets of a council.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Promulgation', 'n.', 'The act of promulgating; publication; open\n   declaration; as, the promulgation of the gospel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Promulgator', 'n.', 'One who promulgates or publishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Promulged', 'imp. & p. p.', 'of Promulge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Promulging', 'p. pr. & vb. n.', 'of Promulge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Promulge', 'v. t.', 'To promulgate; to publish or teach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Promulger', 'n.', 'One who promulges or publishes what was before unknown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Promuscis', 'n.', 'The proboscis of hemipterous insects. See Illust. under\n   Hemiptera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pronaos', 'n.', 'The porch or vestibule of a temple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pronate', 'a.', 'Somewhat prone; inclined; as, pronate trees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pronation', 'n.', 'The act of turning the palm or palmar surface of the\n   forefoot downward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pronation', 'n.', 'That motion of the forearm whereby the palm or palmar,\n   surface is turned downward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pronation', 'n.', 'The position of the limb resulting from the act of\n   pronation. Opposed to supination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pronator', 'n.', 'A muscle which produces pronation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prone', 'a.', 'Bending forward; inclined; not erect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prone', 'a.', 'Prostrate; flat; esp., lying with the face down; -- opposed\n   to supine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prone', 'a.', 'Headlong; running downward or headlong.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prone', 'a.', 'Sloping, with reference to a line or surface; declivous;\n   inclined; not level.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prone', 'a.', 'Inclined; propense; disposed; -- applied to the mind or\n   affections, usually in an ill sense. Followed by to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pronely', 'adv.', 'In a prone manner or position.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proneness', 'n.', 'The quality or state of being prone, or of bending\n   downward; as, the proneness of beasts is opposed to the erectness of\n   man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proneness', 'n.', 'The state of lying with the face down; -- opposed to\n   supineness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proneness', 'n.', 'Descent; declivity; as, the proneness of a hill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proneness', 'n.', 'Inclination of mind, heart, or temper; propension;\n   disposition; as, proneness to self-gratification.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pronephric', 'a.', 'Of or pertaining to the pronephros.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pronephros', 'n.', 'Alt. of Pronephron');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pronephron', 'n.', 'The head kidney. See under Head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prong', 'n.', 'A sharp-pointed instrument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prong', 'n.', 'The tine of a fork, or of a similar instrument; as, a fork\n   of two or three prongs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prong', 'n.', 'A sharp projection, as of an antler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prong', 'n.', 'The fang of a tooth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prongbuck', 'n.', 'The springbuck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prongbuck', 'n.', 'The pronghorn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pronged', 'a.', 'Having prongs or projections like the tines of a fork; as,\n   a three-pronged fork.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prong-hoe', 'n.', 'A hoe with prongs to break the earth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pronghorn', 'n.', 'An American antelope (Antilocapra Americana), native of\n   the plain near the Rocky Mountains. The upper parts are mostly\n   yellowish brown; the under parts, the sides of the head and throat, and\n   the buttocks, are white. The horny sheath of the horns is shed\n   annually. Called also cabree, cabut, prongbuck, and pronghorned\n   antelope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pronity', 'n.', 'Proneness; propensity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pronominal', 'a.', 'Belonging to, or partaking of the nature of, a pronoun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pronominalize', 'v. t.', 'To give the effect of a pronoun to; as, to\n   pronominalize the substantives person, people, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pronominally', 'adv.', 'In a pronominal manner/ with the nature or office\n   of a pronoun; as a pronoun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prononce', 'a.', 'Strongly marked; decided, as in manners, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pronotary', 'n.', 'See Prothonotary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pronota', 'pl. ', 'of Pronotum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pronotum', 'n.', 'The dorsal plate of the prothorax in insects. See Illust.\n   of Coleoptera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pronoun', 'n.', 'A word used instead of a noun or name, to avoid the\n   repetition of it. The personal pronouns in English are I, thou or you,\n   he, she, it, we, ye, and they.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pronounced', 'imp. & p. p.', 'of Pronounce');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pronounging', 'p. pr. & vb. n.', 'of Pronounce');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pronounce', 'v. t.', 'To utter articulately; to speak out or distinctly; to\n   utter, as words or syllables; to speak with the proper sound and accent\n   as, adults rarely learn to pronounce a foreign language correctly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pronounce', 'v. t.', 'To utter officially or solemnly; to deliver, as a\n   decree or sentence; as, to pronounce sentence of death.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pronounce', 'v. t.', 'To speak or utter rhetorically; to deliver; to\n   recite; as, to pronounce an oration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pronounce', 'v. t.', 'To declare or affirm; as, he pronounced the book to\n   be a libel; he pronounced the act to be a fraud.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pronounce', 'v. i.', 'To give a pronunciation; to articulate; as, to\n   pronounce faultlessly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pronounce', 'v. i.', 'To make declaration; to utter on opinion; to speak\n   with confidence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pronounce', 'n.', 'Pronouncement; declaration; pronunciation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pronounceable', 'a.', 'Capable of being pronounced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pronounced', 'a.', 'Strongly marked; unequivocal; decided. [A Gallicism]');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pronouncement', 'n.', 'The act of pronouncing; a declaration; a formal\n   announcement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pronouncer', 'n.', 'One who pronounces, utters, or declares; also, a\n   pronouncing book.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pronouncing', 'a.', 'Pertaining to, or indicating, pronunciation; as, a\n   pronouncing dictionary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pronubial', 'a.', 'Presiding over marriage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pronuclei', 'pl. ', 'of Pronucleus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pronucleus', 'n.', 'One of the two bodies or nuclei (called male and female\n   pronuclei) which unite to form the first segmentation nucleus of an\n   impregnated ovum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pronuncial', 'a.', 'Of or pertaining to pronunciation; pronunciative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pronunciamento', 'n.', 'A proclamation or manifesto; a formal announcement\n   or declaration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pronunciamiento', 'n.', 'See Pronunciamento.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pronunciation', 'n.', 'The act of uttering with articulation; the act of\n   giving the proper sound and accent; utterance; as, the pronunciation of\n   syllables of words; distinct or indistinct pronunciation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pronunciation', 'n.', 'The mode of uttering words or sentences.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pronunciation', 'n.', 'The art of manner of uttering a discourse publicly\n   with propriety and gracefulness; -- now called delivery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pronunciative', 'a.', 'Of or pertaining to pronunciation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pronunciative', 'a.', 'Uttering confidently; dogmatical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pronunciator', 'n.', 'One who pronounces; a pronouncer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pronunciatory', 'a.', 'Of or pertaining to pronunciation; that pronounces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proof', 'n.', 'Any effort, process, or operation designed to establish or\n   discover a fact or truth; an act of testing; a test; a trial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proof', 'n.', 'That degree of evidence which convinces the mind of any\n   truth or fact, and produces belief; a test by facts or arguments that\n   induce, or tend to induce, certainty of the judgment; conclusive\n   evidence; demonstration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proof', 'n.', 'The quality or state of having been proved or tried;\n   firmness or hardness that resists impression, or does not yield to\n   force; impenetrability of physical bodies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proof', 'n.', 'Firmness of mind; stability not to be shaken.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proof', 'n.', 'A trial impression, as from type, taken for correction or\n   examination; -- called also proof sheet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proof', 'n.', 'A process for testing the accuracy of an operation\n   performed. Cf. Prove, v. t., 5.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proof', 'v. t.', 'Armor of excellent or tried quality, and deemed\n   impenetrable; properly, armor of proof.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proof', 'a.', 'Used in proving or testing; as, a proof load, or proof\n   charge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proof', 'a.', 'Firm or successful in resisting; as, proof against harm;\n   waterproof; bombproof.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proof', 'a.', 'Being of a certain standard as to strength; -- said of\n   alcoholic liquors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proof-arm', 'v. t.', 'To arm with proof armor; to arm securely; as, to\n   proof-arm herself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proofless', 'a.', 'Wanting sufficient evidence to induce belief; not\n   proved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proof-proof', 'a.', 'Proof against proofs; obstinate in the wrong.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proostraca', 'pl. ', 'of Proostracum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proostracum', 'n.', 'The anterior prolongation of the guard of the\n   phragmocone of belemnites and allied fossil cephalopods, whether horny\n   or calcareous. See Illust. of Phragmocone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prootic', 'a.', 'In front of the auditory capsule; -- applied especially to\n   a bone, or center of ossification, in the periotic capsule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prootic', 'n.', 'A prootic bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prop', 'n.', 'A shell, used as a die. See Props.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propped', 'imp. & p. p.', 'of Prop');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propping', 'p. pr. & vb. n.', 'of Prop');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prop', 'v. t.', 'To support, or prevent from falling, by placing something\n   under or against; as, to prop up a fence or an old building; (Fig.) to\n   sustain; to maintain; as, to prop a declining state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prop', 'v.', 'That which sustains an incumbent weight; that on which\n   anything rests or leans for support; a support; a stay; as, a prop for\n   a building.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propaedeutic', 'a.', 'Alt. of Propaedeutical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propaedeutical', 'a.', 'Of, pertaining to, or conveying, preliminary\n   instruction; introductory to any art or science; instructing\n   beforehand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propaedeutics', 'n.', 'The preliminary learning connected with any art or\n   science; preparatory instruction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propagable', 'a.', 'Capable of being propagated, or of being continued or\n   multiplied by natural generation or production.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propagable', 'a.', 'Capable of being spread or extended by any means; --\n   said of tenets, doctrines, or principles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propaganda', 'n.', 'A congregation of cardinals, established in 1622,\n   charged with the management of missions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propaganda', 'n.', 'The college of the Propaganda, instituted by Urban\n   VIII. (1623-1644) to educate priests for missions in all parts of the\n   world.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propaganda', 'n.', 'Hence, any organization or plan for spreading a\n   particular doctrine or a system of principles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propagandism', 'n.', 'The art or practice of propagating tenets or\n   principles; zeal in propagating ones opinions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propagandist', 'n.', 'A person who devotes himself to the spread of any\n   system of principles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propagated', 'imp. & p. p.', 'of Propagate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propagating', 'p. pr. & vb. n.', 'of Propagate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propagate', 'v. t.', 'To cause to continue or multiply by generation, or\n   successive production; -- applied to animals and plants; as, to\n   propagate a breed of horses or sheep; to propagate a species of fruit\n   tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propagate', 'v. t.', 'To cause to spread to extend; to impel or continue\n   forward in space; as, to propagate sound or light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propagate', 'v. t.', 'To spread from person to person; to extend the\n   knowledge of; to originate and spread; to carry from place to place; to\n   disseminate; as, to propagate a story or report; to propagate the\n   Christian religion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propagate', 'v. t.', 'To multiply; to increase.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propagate', 'v. t.', 'To generate; to produce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propagate', 'v. i.', 'To have young or issue; to be produced or multiplied\n   by generation, or by new shoots or plants; as, rabbits propagate\n   rapidly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propagation', 'n.', 'The act of propagating; continuance or multiplication\n   of the kind by generation or successive production; as, the propagation\n   of animals or plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propagation', 'n.', 'The spreading abroad, or extension, of anything;\n   diffusion; dissemination; as, the propagation of sound; the propagation\n   of the gospel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propagative', 'a.', 'Producing by propagation, or by a process of growth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propagator', 'n.', 'One who propagates; one who continues or multiplies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propagula', 'pl. ', 'of Propagulum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propagulum', 'n.', 'A runner terminated by a germinating bud.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propane', 'n.', 'A heavy gaseous hydrocarbon, C3H8, of the paraffin series,\n   occurring naturally dissolved in crude petroleum, and also made\n   artificially; -- called also propyl hydride.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propargyl', 'n.', 'Same as Propinyl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proparoxytone', 'n.', 'A word which has the acute accent on the antepenult.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proped', 'n.', 'Same as Proleg.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propelled', 'imp. & p. p.', 'of Propel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propelling', 'p. pr. & vb. n.', 'of Propel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propel', 'v. t.', 'To drive forward; to urge or press onward by force; to\n   move, or cause to move; as, the wind or steam propels ships; balls are\n   propelled by gunpowder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propeller', 'n.', 'One who, or that which, propels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propeller', 'n.', 'A contrivance for propelling a steam vessel, usually\n   consisting of a screw placed in the stern under water, and made to\n   revolve by an engine; a propeller wheel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propeller', 'n.', 'A steamboat thus propelled; a screw steamer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propend', 'v. i.', 'To lean toward a thing; to be favorably inclined or\n   disposed; to incline; to tend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propendency', 'n.', 'Propensity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propendency', 'n.', 'Attentive deliberation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propendent', 'a.', 'Inclining forward or toward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propene', 'n.', 'Same as Propylene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propense', 'a.', 'Leaning toward, in a moral sense; inclined; disposed;\n   prone; as, women propense to holiness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propension', 'n.', 'The quality or state of being propense; propensity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propensities', 'pl. ', 'of Propensity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propensity', 'n.', 'The quality or state of being propense; natural\n   inclination; disposition to do good or evil; bias; bent; tendency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propenyl', 'n.', 'A hypothetical hydrocarbon radical, C3H5, isomeric with\n   allyl and glyceryl, and regarded as the essential residue of glycerin.\n   Cf. Allyl, and Glyceryl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propepsin', 'n.', 'See Persinogen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propeptone', 'n.', 'A product of gastric digestion intermediate between\n   albumin and peptone, identical with hemialbumose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proper', 'a.', 'Belonging to one; ones own; individual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proper', 'a.', 'Belonging to the natural or essential constitution;\n   peculiar; not common; particular; as, every animal has his proper\n   instincts and appetites.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proper', 'a.', 'Befitting ones nature, qualities, etc.; suitable in all\n   respect; appropriate; right; fit; decent; as, water is the proper\n   element for fish; a proper dress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proper', 'a.', 'Becoming in appearance; well formed; handsome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proper', 'a.', 'Pertaining to one of a species, but not common to the\n   whole; not appellative; -- opposed to common; as, a proper name; Dublin\n   is the proper name of a city.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proper', 'a.', 'Rightly so called; strictly considered; as, Greece proper;\n   the garden proper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proper', 'a.', 'Represented in its natural color; -- said of any object\n   used as a charge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proper', 'adv.', 'Properly; hence, to a great degree; very; as, proper\n   good.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Properate', 'v. t. & i.', 'To hasten, or press forward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Properation', 'n.', 'The act of hastening; haste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Properispome', 'n.', 'Properispomenon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Properispomena', 'pl. ', 'of Properispomenon');
  INSERT INTO words (word, word_type, description) VALUES  (    'Properispomenon', 'n.', 'A word which has the circumflex accent on the\n   penult.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Properly', 'adv.', 'In a proper manner; suitably; fitly; strictly; rightly;\n   as, a word properly applied; a dress properly adjusted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Properly', 'adv.', 'Individually; after ones own manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Properness', 'n.', 'The quality of being proper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Properness', 'n.', 'Tallness; comeliness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propertied', 'a.', 'Possessing property; holding real estate, or other\n   investments of money.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Properties', 'pl. ', 'of Property');
  INSERT INTO words (word, word_type, description) VALUES  (    'Property', 'a.', 'That which is proper to anything; a peculiar quality of a\n   thing; that which is inherent in a subject, or naturally essential to\n   it; an attribute; as, sweetness is a property of sugar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Property', 'a.', 'An acquired or artificial quality; that which is given by\n   art, or bestowed by man; as, the poem has the properties which\n   constitute excellence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Property', 'a.', 'The exclusive right of possessing, enjoying, and\n   disposing of a thing; ownership; title.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Property', 'a.', 'That to which a person has a legal title, whether in his\n   possession or not; thing owned; an estate, whether in lands, goods, or\n   money; as, a man of large property, or small property.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Property', 'a.', 'All the adjuncts of a play except the scenery and the\n   dresses of the actors; stage requisites.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Property', 'a.', 'Propriety; correctness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Property', 'v. t.', 'To invest which properties, or qualities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Property', 'v. t.', 'To make a property of; to appropriate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prophane', 'a. & v. t.', 'See Profane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prophasis', 'n.', 'Foreknowledge of a disease; prognosis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prophecies', 'pl. ', 'of Prophecy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prophecy', 'n.', 'A declaration of something to come; a foretelling; a\n   prediction; esp., an inspired foretelling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prophecy', 'n.', 'A book of prophecies; a history; as, the prophecy of\n   Ahijah.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prophecy', 'n.', 'Public interpretation of Scripture; preaching;\n   exhortation or instruction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prophesier', 'n.', 'A prophet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prophesied', 'imp. & p. p.', 'of Prophesy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prophesying', 'p. pr. & vb. n.', 'of Prophesy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prophesy', 'v. t.', 'To foretell; to predict; to prognosticate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prophesy', 'v. t.', 'To foreshow; to herald; to prefigure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prophesy', 'v. i.', 'To utter predictions; to make declaration of events to\n   come.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prophesy', 'v. i.', 'To give instruction in religious matters; to interpret\n   or explain Scripture or religious subjects; to preach; to exhort; to\n   expound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prophet', 'n.', 'One who prophesies, or foretells events; a predicter; a\n   foreteller.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prophet', 'n.', 'One inspired or instructed by God to speak in his name, or\n   announce future events, as, Moses, Elijah, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prophet', 'n.', 'An interpreter; a spokesman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prophet', 'n.', 'A mantis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prophetess', 'n.', 'A female prophet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prophetic', 'a.', 'Alt. of Prophetical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prophetical', 'a.', 'Containing, or pertaining to, prophecy; foretelling\n   events; as, prophetic writings; prophetic dreams; -- used with of\n   before the thing foretold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propheticality', 'n.', 'Propheticalness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prophetically', 'adv.', 'In a prophetical manner; by way of prediction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propheticalness', 'n.', 'The quality or state of being prophetical; power\n   or capacity to foretell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prophetize', 'v. i.', 'To give predictions; to foreshow events; to\n   prophesy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prophoric', 'a.', 'Enunciative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prophragmata', 'pl. ', 'of Prophragma');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prophragma', 'n.', 'An internal dorsal chitinous process between the first\n   two divisions of the thorax of insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prophylactic', 'n.', 'A medicine which preserves or defends against\n   disease; a preventive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prophylactic', 'a.', 'Alt. of Prophylactical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prophylactical', 'a.', 'Defending or preserving from disease; preventive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prophylaxis', 'n.', 'The art of preserving from, or of preventing, disease;\n   the observance of the rules necessary for the preservation of health;\n   preservative or preventive treatment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propice', 'a.', 'Fit; propitious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propidene', 'n.', 'The unsymmetrical hypothetical hydrocarbon radical,\n   CH3.CH2.CH, analogous to ethylidene, and regarded as the type of\n   certain derivatives of propane; -- called also propylidene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propination', 'n.', 'The act of pledging, or drinking first, and then\n   offering the cup to another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propine', 'v. t.', 'To pledge; to offer as a toast or a health in the\n   manner of drinking, that is, by drinking first and passing the cup.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propine', 'v. t.', 'Hence, to give in token of friendship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propine', 'v. t.', 'To give, or deliver; to subject.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propine', 'n.', 'A pledge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propine', 'n.', 'A gift; esp., drink money.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propine', 'n.', 'Same as Allylene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propinquity', 'n.', 'Nearness in place; neighborhood; proximity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propinquity', 'n.', 'Nearness in time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propinquity', 'n.', 'Nearness of blood; kindred; affinity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propinyl', 'n.', 'A hydrocarbon radical regarded as an essential residue of\n   propine and allied compounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propiolate', 'n.', 'A salt of propiolic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propiolic', 'a.', 'Pertaining to, or designating, an organic acid (called\n   also propargylic acid) of the acetylene or tetrolic series, analogous\n   to propionic acid, and obtained as a white crystalline substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propionate', 'n.', 'A salt of propionic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propione', 'n.', 'The ketone of propionic acid, obtained as a colorless\n   fragrant liquid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propionic', 'a.', 'Pertaining to, derived from, or designating, an organic\n   acid which is produced in the distillation of wood, in the fermentation\n   of various organic substances, as glycerin, calcium lactate, etc., and\n   is obtained as a colorless liquid having a sharp, pungent odor.\n   Propionic acid is so called because it is the first or lowest member of\n   the fatty acid series whose salts have a fatty feel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propionyl', 'n.', 'The hypothetical radical C3H5O, regarded as the\n   essential residue of propionic acid and certain related compounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propithecus', 'n.', 'A genus including the long-tailed, or diadem, indris.\n   See Indris.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propitiable', 'a.', 'Capable of being propitiated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propitiated', 'imp. & p. p.', 'of Propitiate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propitiating', 'p. pr. & vb. n.', 'of Propitiate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propitiate', 'v. t.', 'To appease to render favorable; to make propitious;\n   to conciliate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propitiate', 'v. i.', 'To make propitiation; to atone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propitiation', 'n.', 'The act of appeasing the wrath and conciliating the\n   favor of an offended person; the act of making propitious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propitiation', 'n.', 'That which propitiates; atonement or atoning\n   sacrifice; specifically, the influence or effects of the death of\n   Christ in appeasing the divine justice, and conciliating the divine\n   favor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propitiator', 'n.', 'One who propitiates or appeases.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propitiatorily', 'adv.', 'By way of propitiation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propitiatory', 'a.', 'Having the power to make propitious; pertaining to,\n   or employed in, propitiation; expiatory; as, a propitiatory sacrifice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propitiatory', 'n.', 'The mercy seat; -- so called because a symbol of the\n   propitiated Jehovah.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propitious', 'a.', 'Convenient; auspicious; favorable; kind; as, a\n   propitious season; a propitious breeze.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propitious', 'a.', 'Hence, kind; gracious; merciful; helpful; -- said of a\n   person or a divinity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proplasm', 'n.', 'A mold; a matrix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proplastic', 'a.', 'Forming a mold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proplastics', 'n.', 'The art of making molds for castings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propleg', 'n.', 'Same as Proleg.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propodial', 'a.', 'Of or pertaining to the propodialia, or the parts of the\n   limbs to which they belong.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propodialia', 'pl. ', 'of Propodiale');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propodiale', 'n.', 'The bone of either the upper arm or the thing, the\n   propodialia being the humerus and femur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propodite', 'n.', 'The sixth joint of a typical leg of a crustacean;\n   usually, the penultimate joint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propodia', 'pl. ', 'of Propodium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propodium', 'n.', 'The anterior portion of the foot of a mollusk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propodium', 'n.', 'The segment which forms the posterior part of the thorax\n   of a hymenopterous insect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propolis', 'n.', 'Same as Bee glue, under Bee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propone', 'v. t.', 'To propose; to bring forward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proponent', 'a.', 'Making proposals; proposing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proponent', 'n.', 'One who makes a proposal, or lays down a proposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proponent', 'n.', 'The propounder of a thing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proportion', 'n.', 'The relation or adaptation of one portion to another,\n   or to the whole, as respect magnitude, quantity, or degree; comparative\n   relation; ratio; as, the proportion of the parts of a building, or of\n   the body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proportion', 'n.', 'Harmonic relation between parts, or between different\n   things of the same kind; symmetrical arrangement or adjustment;\n   symmetry; as, to be out of proportion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proportion', 'n.', 'The portion one receives when a whole is distributed by\n   a rule or principle; equal or proper share; lot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proportion', 'n.', 'A part considered comparatively; a share.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proportion', 'n.', 'The equality or similarity of ratios, especially of\n   geometrical ratios; or a relation among quantities such that the\n   quotient of the first divided by the second is equal to that of the\n   third divided by the fourth; -- called also geometrical proportion, in\n   distinction from arithmetical proportion, or that in which the\n   difference of the first and second is equal to the difference of the\n   third and fourth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proportion', 'n.', 'The rule of three, in arithmetic, in which the three\n   given terms, together with the one sought, are proportional.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proportioned', 'imp. & p. p.', 'of Proportion');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proportioning', 'p. pr. & vb. n.', 'of Proportion');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proportion', 'v.', 'To adjust in a suitable proportion, as one thing or one\n   part to another; as, to proportion the size of a building to its\n   height; to proportion our expenditures to our income.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proportion', 'v.', 'To form with symmetry or suitableness, as the parts of\n   the body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proportion', 'v.', 'To divide into equal or just shares; to apportion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proportionable', 'a.', 'Capable of being proportioned, or made\n   proportional; also, proportional; proportionate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proportionably', 'adv.', 'Proportionally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proportional', 'a.', 'Having a due proportion, or comparative relation;\n   being in suitable proportion or degree; as, the parts of an edifice are\n   proportional.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proportional', 'a.', 'Relating to, or securing, proportion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proportional', 'a.', 'Constituting a proportion; having the same, or a\n   constant, ratio; as, proportional quantities; momentum is proportional\n   to quantity of matter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proportional', 'n.', 'Any number or quantity in a proportion; as, a mean\n   proportional.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proportional', 'n.', 'The combining weight or equivalent of an element.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proportionality', 'n.', 'The state of being in proportion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proportionally', 'adv.', 'In proportion; in due degree; adapted relatively;\n   as, all parts of the building are proportionally large.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proportionate', 'a.', 'Adjusted to something else according to a\n   proportion; proportional.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proportionated', 'imp. & p. p.', 'of Proportionate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proportionating', 'p. pr. & vb. n.', 'of Proportionate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proportionate', 'v.', 'To make proportional; to adjust according to a\n   settled rate, or to due comparative relation; to proportion; as, to\n   proportionate punishment to crimes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proportionately', 'adv.', 'In a proportionate manner; with due proportion;\n   proportionally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proportionateness', 'n.', 'The quality or state of being proportionate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proportionless', 'a.', 'Without proportion; unsymmetrical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proportionment', 'n.', 'The act or process of dividing out proportionally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proposal', 'n.', 'That which is proposed, or propounded for consideration\n   or acceptance; a scheme or design; terms or conditions proposed; offer;\n   as, to make proposals for a treaty of peace; to offer proposals for\n   erecting a building; to make proposals of marriage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proposal', 'n.', 'The offer by a party of what he has in view as to an\n   intended business transaction, which, with acceptance, constitutes a\n   contract.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proposed', 'imp. & p. p.', 'of Propose');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proposing', 'p. pr. & vb. n.', 'of Propose');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propose', 'v.', 'To set forth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propose', 'v.', 'To offer for consideration, discussion, acceptance, or\n   adoption; as, to propose terms of peace; to propose a question for\n   discussion; to propose an alliance; to propose a person for office.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propose', 'v.', 'To set before ones self or others as a purpose formed;\n   hence, to purpose; to intend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propose', 'v. i.', 'To speak; to converse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propose', 'v. i.', 'To form or declare a purpose or intention; to lay a\n   scheme; to design; as, man proposes, but God disposes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propose', 'v. i.', 'To offer ones self in marriage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propose', 'n.', 'Talk; discourse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proposer', 'n.', 'One who proposes or offers anything for consideration or\n   adoption.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proposer', 'n.', 'A speaker; an orator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proposition', 'n.', 'The act of setting or placing before; the act of\n   offering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proposition', 'n.', 'That which is proposed; that which is offered, as for\n   consideration, acceptance, or adoption; a proposal; as, the enemy made\n   propositions of peace; his proposition was not accepted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proposition', 'n.', 'A statement of religious doctrine; an article of\n   faith; creed; as, the propositions of Wyclif and Huss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proposition', 'n.', 'A complete sentence, or part of a sentence consisting\n   of a subject and predicate united by a copula; a thought expressed or\n   propounded in language; a from of speech in which a predicate is\n   affirmed or denied of a subject; as, snow is white.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proposition', 'n.', 'A statement in terms of a truth to be demonstrated, or\n   of an operation to be performed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proposition', 'n.', 'That which is offered or affirmed as the subject of\n   the discourse; anything stated or affirmed for discussion or\n   illustration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proposition', 'n.', 'The part of a poem in which the author states the\n   subject or matter of it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propositional', 'a.', 'Pertaining to, or in the nature of, a proposition;\n   considered as a proposition; as, a propositional sense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propounded', 'imp. & p. p.', 'of Propound');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propounding', 'p. pr. & vb. n.', 'of Propound');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propound', 'v. t.', 'To offer for consideration; to exhibit; to propose;\n   as, to propound a question; to propound an argument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propound', 'v. t.', 'To propose or name as a candidate for admission to\n   communion with a church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propounder', 'n.', 'One who propounds, proposes, or offers for\n   consideration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propretor', 'n.', 'A magistrate who, having been pretor at home, was\n   appointed to the government of a province.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proprietaries', 'pl. ', 'of Proprietary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proprietary', 'n.', 'A proprietor or owner; one who has exclusive title to\n   a thing; one who possesses, or holds the title to, a thing in his own\n   right.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proprietary', 'n.', 'A body proprietors, taken collectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proprietary', 'n.', 'A monk who had reserved goods and effects to himself,\n   notwithstanding his renunciation of all at the time of profession.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proprietary', 'a.', 'Belonging, or pertaining, to a proprietor; considered\n   as property; owned; as, proprietary medicine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proprietor', 'n.', 'One who has the legal right or exclusive title to\n   anything, whether in possession or not; an owner; as, the proprietor of\n   farm or of a mill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proprietorial', 'a.', 'Of or pertaining to ownership; proprietary; as,\n   proprietorial rights.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proprietorship', 'n.', 'The state of being proprietor; ownership.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proprietress', 'n.', 'A female proprietor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proprieties', 'pl. ', 'of Propriety');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propriety', 'n.', 'Individual right to hold property; ownership by personal\n   title; property.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propriety', 'n.', 'That which is proper or peculiar; an inherent property\n   or quality; peculiarity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propriety', 'n.', 'The quality or state of being proper; suitableness to an\n   acknowledged or correct standard or rule; consonance with established\n   principles, rules, or customs; fitness; appropriateness; as, propriety\n   of behavior, language, manners, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proproctor', 'n.', 'A assistant proctor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Props', 'n. pl.', 'A game of chance, in which four sea shells, each called\n   a prop, are used instead of dice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propterygia', 'pl. ', 'of Propterygium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propterygium', 'n.', 'The anterior of three principal cartilages in the\n   fins of some fishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propugn', 'v. t.', 'To contend for; to defend; to vindicate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propugnacle', 'n.', 'A fortress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propugnation', 'n.', 'Means of defense; defense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propugner', 'n.', 'A defender; a vindicator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propulsation', 'n.', 'The act of driving away or repelling; a keeping at a\n   distance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propulse', 'v. t.', 'To repel; to drive off or away.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propulsion', 'n.', 'The act driving forward or away; the act or process of\n   propelling; as, steam propulsion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propulsion', 'n.', 'An impelling act or movement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propulsive', 'a.', 'Tending, or having power, to propel; driving on;\n   urging.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propulsory', 'a.', 'Propulsive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propyl', 'n.', 'The hypothetical radical C3H7, regarded as the essential\n   residue of propane and related compounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propylaea', 'pl. ', 'of Propylaeum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propylaeum', 'n.', 'Any court or vestibule before a building or leading\n   into any inclosure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propylene', 'n.', 'A colorless gaseous hydrocarbon (C3H6) of the ethylene\n   series, having a garlic odor. It occurs in coal gas, and is produced\n   artificially in various ways. Called also propene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propylic', 'a.', 'Pertaining to, derived from, or containing, propyl; as,\n   propylic alcohol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propylidene', 'n.', 'See Propidene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propyla', 'pl. ', 'of Propylon');
  INSERT INTO words (word, word_type, description) VALUES  (    'Propylon', 'n.', 'The porch, vestibule, or entrance of an edifice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pro rata', '', 'In proportion; proportionately; according to the share,\n   interest, or liability of each.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proratable', 'a.', 'Capable of being prorated, or divided proportionately.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prorated', 'imp. & p. p.', 'of Prorate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prorating', 'p. pr. & vb. n.', 'of Prorate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prorate', 'v. t.', 'To divide or distribute proportionally; to assess pro\n   rata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prore', 'n.', 'The prow or fore part of a ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prorector', 'n.', 'An officer who presides over the academic senate of a\n   German university.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prorectorate', 'n.', 'The office of prorector.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prorenal', 'a.', 'Pronephric.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proreption', 'n.', 'A creeping on.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prorhinal', 'a.', 'Situated in front of the nasal chambers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prorogate', 'v. t.', 'To prorogue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prorogation', 'n.', 'The act of counting in duration; prolongation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prorogation', 'n.', 'The act of proroguing; the ending of the session of\n   Parliament, and postponing of its business, by the command of the\n   sovereign.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prorogued', 'imp. & p. p.', 'of Prorogue');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proroguing', 'p. pr. & vb. n.', 'of Prorogue');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prorogue', 'v. t.', 'To protract; to prolong; to extend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prorogue', 'v. t.', 'To defer; to delay; to postpone; as, to proroguedeath;\n   to prorogue a marriage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prorogue', 'v. t.', 'To end the session of a parliament by an order of the\n   sovereign, thus deferring its business.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proruption', 'n.', 'The act or state of bursting forth; a bursting out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prosaic', 'a.', 'Alt. of Prosaical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prosaical', 'a.', 'Of or pertaining to prose; resembling prose; in the form\n   of prose; unpoetical; writing or using prose; as, a prosaic\n   composition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prosaical', 'a.', 'Dull; uninteresting; commonplace; unimaginative; prosy;\n   as, a prosaic person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prosaicism', 'n.', 'The quality or state of being prosaic; a prosaic manner\n   or style.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prosaism', 'n.', 'That which is in the form of prose writing; a prosaic\n   manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prosaist', 'n.', 'A writer of prose; an unpoetical writer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prosal', 'a.', 'Of or pertaining to prose; prosaic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proscenia', 'pl. ', 'of Proscenium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proscenium', 'n.', 'The part where the actors performed; the stage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proscenium', 'n.', 'The part of the stage in front of the curtain;\n   sometimes, the curtain and its framework.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proscolices', 'pl. ', 'of Proscolex');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proscolex', 'n.', 'An early larval form of a trematode worm; a redia. See\n   Redia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proscribed', 'imp. & p. p.', 'of Proscribe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proscribing', 'p. pr. & vb. n.', 'of Proscribe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proscribe', 'v. t.', 'To doom to destruction; to put out of the protection\n   of law; to outlaw; to exile; as, Sylla and Marius proscribed each\n   others adherents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proscribe', 'v. t.', 'To denounce and condemn; to interdict; to prohibit;\n   as, the Puritans proscribed theaters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proscriber', 'n.', 'One who, or that which, proscribes, denounces, or\n   prohibits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proscript', 'n.', 'A proscription; a prohibition; an interdict.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proscript', 'n.', 'One who is proscribed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proscription', 'n.', 'The act of proscribing; a dooming to death or exile;\n   outlawry; specifically, among the ancient Romans, the public offer of a\n   reward for the head of a political enemy; as, under the triumvirate,\n   many of the best Roman citizens fell by proscription.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proscription', 'n.', 'The state of being proscribed; denunciation;\n   interdiction; prohibition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proscriptional', 'a.', 'Proscriptive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proscriptionist', 'n.', 'One who proscribes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proscriptive', 'a.', 'Of or pertaining to proscription; consisting in, or\n   of the nature of, proscription; proscribing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prose', 'n.', 'The ordinary language of men in speaking or writing;\n   language not cast in poetical measure or rhythm; -- contradistinguished\n   from verse, or metrical composition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prose', 'n.', 'Hence, language which evinces little imagination or\n   animation; dull and commonplace discourse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prose', 'n.', 'A hymn with no regular meter, sometimes introduced into the\n   Mass. See Sequence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prose', 'a.', 'Pertaining to, or composed of, prose; not in verse; as,\n   prose composition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prose', 'a.', 'Possessing or exhibiting unpoetical characteristics; plain;\n   dull; prosaic; as, the prose duties of life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prosed', 'imp. & p. p.', 'of Prose');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prosing', 'p. pr. & vb. n.', 'of Prose');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prose', 'v. t.', 'To write in prose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prose', 'v. t.', 'To write or repeat in a dull, tedious, or prosy way.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prose', 'v. i.', 'To write prose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prosector', 'n.', 'One who makes dissections for anatomical illustration;\n   usually, the assistant of a professional anatomist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prosecutable', 'a.', 'Capable of being prosecuted; liable to prosecution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prosecuted', 'imp. & p. p.', 'of Prosecute');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prosecuting', 'p. pr. & vb. n.', 'of Prosecute');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prosecute', 'v. t.', 'To follow or pursue with a view to reach, execute, or\n   accomplish; to endeavor to obtain or complete; to carry on; to\n   continue; as, to prosecute a scheme, hope, or claim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prosecute', 'v. t.', 'To seek to obtain by legal process; as, to prosecute\n   a right or a claim in a court of law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prosecute', 'v. t.', 'To pursue with the intention of punishing; to accuse\n   of some crime or breach of law, or to pursue for redress or punishment,\n   before a legal tribunal; to proceed against judicially; as, to\n   prosecute a man for trespass, or for a riot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prosecute', 'v. i.', 'To follow after.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prosecute', 'v. i.', 'To institute and carry on a legal prosecution; as, to\n   prosecute for public offenses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prosecution', 'n.', 'The act or process of prosecuting, or of endeavoring\n   to gain or accomplish something; pursuit by efforts of body or mind;\n   as, the prosecution of a scheme, plan, design, or undertaking; the\n   prosecution of war.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prosecution', 'n.', 'The institution and carrying on of a suit in a court\n   of law or equity, to obtain some right, or to redress and punish some\n   wrong; the carrying on of a judicial proceeding in behalf of a\n   complaining party, as distinguished from defense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prosecution', 'n.', 'The institution, or commencement, and continuance of a\n   criminal suit; the process of exhibiting formal charges against an\n   offender before a legal tribunal, and pursuing them to final judgment\n   on behalf of the state or government, as by indictment or information.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prosecution', 'n.', 'The party by whom criminal proceedings are instituted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prosecutor', 'n.', 'One who prosecutes or carries on any purpose, plan, or\n   business.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prosecutor', 'n.', 'The person who institutes and carries on a criminal\n   suit against another in the name of the government.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prosecutrix', 'n.', 'A female prosecutor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proselyte', 'n.', 'A new convert especially a convert to some religion or\n   religious sect, or to some particular opinion, system, or party; thus,\n   a Gentile converted to Judaism, or a pagan converted to Christianity,\n   is a proselyte.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proselyted', 'imp. & p. p.', 'of Proselyte');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proselyting', 'p. pr. & vb. n.', 'of Proselyte');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proselyte', 'v. t.', 'To convert to some religion, opinion, or system; to\n   bring over.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proselytism', 'n.', 'The act or practice of proselyting; the making of\n   converts to a religion or a religious sect, or to any opinion, system,\n   or party.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proselytism', 'n.', 'Conversion to a religion, system, or party.');
  INSERT INTO words (word, word_type, description) VALUES  (    'proselytized', 'imp. & p. p.', 'of Proselytize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proselytizing', 'p. pr. & vb. n.', 'of Proselytize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proselytize', 'v. t.', 'To convert to some religion, system, opinion, or\n   the like; to bring, or cause to come, over; to proselyte.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proselytize', 'v. i.', 'To make converts or proselytes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proselytizer', 'n.', 'One who proselytes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proseman', 'n.', 'A writer of prose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proseminary', 'n.', 'A seminary which prepares pupils for a higher\n   institution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prosemination', 'n.', 'Propagation by seed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prosencephalic', 'a.', 'Of or pertaining to the prosencephalon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prosencephalon', 'n.', 'The anterior segment of the brain, including the\n   cerebrum and olfactory lobes; the forebrain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prosencephalon', 'n.', 'The cerebrum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prosenchyma', 'n.', 'A general term applied to the tissues formed of\n   elongated cells, especially those with pointed or oblique extremities,\n   as the principal cells of ordinary wood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proser', 'n.', 'A writer of prose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proser', 'n.', 'One who talks or writes tediously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prosiliency', 'n.', 'The act of leaping forth or forward; projection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prosily', 'adv.', 'In a prosy manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prosimetrical', 'a.', 'Consisting both of prose and verse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prosimiae', 'n. pl.', 'Same as Lemuroidea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prosiness', 'n.', 'The quality or state of being prosy; tediousness;\n   tiresomeness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prosing', 'n.', 'Writing prose; speaking or writing in a tedious or prosy\n   manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prosingly', 'adv.', 'Prosily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prosiphon', 'n.', 'A minute tube found in the protoconch of ammonites, and\n   not connected with the true siphon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proslavery', 'a.', 'Favoring slavery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proslavery', 'n.', 'Advocacy of slavery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prosobranch', 'n.', 'One of the Prosobranchiata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prosobranchiata', 'n. pl.', 'The highest division, or subclass, of\n   gastropod mollusks, including those that have the gills situated\n   anteriorly, or forward of the heart, and the sexes separate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prosocoele', 'n.', 'The entire cavity of the prosencephalon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prosocoelle', 'pl. ', 'of Prosocoelia');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prosocoelia', 'n.', 'Same as Prosocoele.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prosodiacal', 'a.', 'Prosodical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prosodiacally', 'adv.', 'Prosodically.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prosodial', 'a.', 'Prosodical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prosodian', 'n.', 'A prosodist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prosodical', 'a.', 'Of or pertaining to prosody; according to the rules of\n   prosody.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prosdist', 'n.', 'One skilled in prosody.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prosody', 'n.', 'That part of grammar which treats of the quantity of\n   syllables, of accent, and of the laws of versification or metrical\n   composition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prosomata', 'pl. ', 'of Prosoma');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prosoma', 'n.', 'The anterior of the body of an animal, as of a cephalopod;\n   the thorax of an arthropod.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prosopalgia', 'n.', 'Facial neuralgia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prosopocephala', 'n. pl.', 'Same as Scaphopoda.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prosopolepsy', 'n.', 'Respect of persons; especially, a premature opinion\n   or prejudice against a person, formed from his external appearance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prosopop/ia', 'n.', 'A figure by which things are represented as persons,\n   or by which things inanimate are spoken of as animated beings; also, a\n   figure by which an absent person is introduced as speaking, or a\n   deceased person is represented as alive and present. It includes\n   personification, but is more extensive in its signification.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prosopulmonata', 'n. pl.', 'A division of pulmonate mollusks having the\n   breathing organ situated on the neck, as in the common snail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prospect', 'v.', 'That which is embraced by eye in vision; the region which\n   the eye overlooks at one time; view; scene; outlook.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prospect', 'v.', 'Especially, a picturesque or widely extended view; a\n   landscape; hence, a sketch of a landscape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prospect', 'v.', 'A position affording a fine view; a lookout.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prospect', 'v.', 'Relative position of the front of a building or other\n   structure; face; relative aspect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prospect', 'v.', 'The act of looking forward; foresight; anticipation; as,\n   a prospect of the future state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prospect', 'v.', 'That which is hoped for; ground for hope or expectation;\n   expectation; probable result; as, the prospect of success.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prospected', 'imp. & p. p.', 'of Prospect');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prospecting', 'p. pr. & vb. n.', 'of Prospect');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prospect', 'v. t.', 'To look over; to explore or examine for something; as,\n   to prospect a district for gold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prospect', 'v. i.', 'To make a search; to seek; to explore, as for mines or\n   the like; as, to prospect for gold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prospection', 'n.', 'The act of looking forward, or of providing for future\n   wants; foresight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prospective', 'n.', 'Of or pertaining to a prospect; furnishing a prospect;\n   perspective.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prospective', 'n.', 'Looking forward in time; acting with foresight; --\n   opposed to retrospective.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prospective', 'n.', 'Being within view or consideration, as a future event\n   or contingency; relating to the future: expected; as, a prospective\n   benefit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prospective', 'n.', 'The scene before or around, in time or in space; view;\n   prospect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prospective', 'n.', 'A perspective glass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prospectively', 'adv.', 'In a prospective manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prospectiveness', 'n.', 'Quality of being prospective.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prospectless', 'a.', 'Having no prospect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prospector', 'n.', 'One who prospects; especially, one who explores a\n   region for minerals and precious metals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prospectus', 'n.', 'A summary, plan, or scheme of something proposed,\n   affording a prospect of its nature; especially, an exposition of the\n   scheme of an unpublished literary work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prospered', 'imp. & p. p.', 'of Prosper');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prospering', 'p. pr. & vb. n.', 'of Prosper');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prosper', 'v. t.', 'To favor; to render successful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prosper', 'v. i.', 'To be successful; to succeed; to be fortunate or\n   prosperous; to thrive; to make gain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prosper', 'v. i.', 'To grow; to increase.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prosperity', 'n.', 'The state of being prosperous; advance or gain in\n   anything good or desirable; successful progress in any business or\n   enterprise; attainment of the object desired; good fortune; success;\n   as, commercial prosperity; national prosperity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prosperous', 'a.', 'Tending to prosperity; favoring; favorable; helpful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prosperous', 'a.', 'Being prospered; advancing in the pursuit of anything\n   desirable; making gain, or increase; thriving; successful; as, a\n   prosperous voyage; a prosperous undertaking; a prosperous man or\n   nation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prosphysis', 'n.', 'A growing together of parts; specifically, a morbid\n   adhesion of the eyelids to each other or to the eyeball.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prospicience', 'n.', 'The act of looking forward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prostate', 'a.', 'Standing before; -- applied to a gland which is found in\n   the males of most mammals, and is situated at the neck of the bladder\n   where this joins the urethra.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prostate', 'n.', 'The prostate gland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prostatic', 'a.', 'Of or pertaining to the prostate gland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prostatitis', 'n.', 'Inflammation of the prostate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prosternation', 'n.', 'Dejection; depression.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prosternum', 'n.', 'The ventral plate of the prothorax of an insect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prosthesis', 'n.', 'The addition to the human body of some artificial part,\n   to replace one that is wanting, as a log or an eye; -- called also\n   prothesis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prosthesis', 'n.', 'The prefixing of one or more letters to the beginning\n   of a word, as in beloved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prosthetic', 'a.', 'Of or pertaining to prosthesis; prefixed, as a letter\n   or letters to a word.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prostibulous', 'a.', 'Of or pertaining to prostitutes or prostitution;\n   meretricious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prostituted', 'imp. & p. p.', 'of Prostitute');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prostituting', 'p. pr. & vb. n.', 'of Prostitute');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prostitute', 'v. t.', 'To offer, as a woman, to a lewd use; to give up to\n   lewdness for hire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prostitute', 'v. t.', 'To devote to base or unworthy purposes; to give up\n   to low or indiscriminate use; as, to prostitute talents; to prostitute\n   official powers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prostitute', 'a.', 'Openly given up to lewdness; devoted to base or\n   infamous purposes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prostitute', 'n.', 'A woman giver to indiscriminate lewdness; a strumpet; a\n   harlot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prostitute', 'n.', 'A base hireling; a mercenary; one who offers himself to\n   infamous employments for hire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prostitution', 'n.', 'The act or practice of prostituting or offering the\n   body to an indiscriminate intercourse with men; common lewdness of a\n   woman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prostitution', 'n.', 'The act of setting ones self to sale, or of devoting\n   to infamous purposes what is in ones power; as, the prostitution of\n   abilities; the prostitution of the press.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prostitutor', 'n.', 'One who prostitutes; one who submits himself, of or\n   offers another, to vile purposes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prostomia', 'pl. ', 'of Prostomium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prostomium', 'n.', 'That portion of the head of an annelid situated in\n   front of the mouth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prostrate', 'a.', 'Lying at length, or with the body extended on the ground\n   or other surface; stretched out; as, to sleep prostrate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prostrate', 'a.', 'Lying at mercy, as a supplicant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prostrate', 'a.', 'Lying in a humble, lowly, or suppliant posture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prostrate', 'a.', 'Trailing on the ground; procumbent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prostrated', 'imp. & p. p.', 'of Prostrate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prostrating', 'p. pr. & vb. n.', 'of Prostrate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prostrate', 'v. t.', 'To lay fiat; to throw down; to level; to fell; as, to\n   prostrate the body; to prostrate trees or plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prostrate', 'v. t.', 'to overthrow; to demolish; to destroy; to deprive of\n   efficiency; to ruin; as, to prostrate a village; to prostrate a\n   government; to prostrate law or justice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prostrate', 'v. t.', 'To throw down, or cause to fall in humility or\n   adoration; to cause to bow in humble reverence; used reflexively; as,\n   he prostrated himself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prostrate', 'v. t.', 'To cause to sink totally; to deprive of strength; to\n   reduce; as, a person prostrated by fever.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prostration', 'n.', 'The act of prostrating, throwing down, or laying fiat;\n   as, the prostration of the body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prostration', 'n.', 'The act of falling down, or of bowing in humility or\n   adoration; primarily, the act of falling on the face, but usually\n   applied to kneeling or bowing in reverence and worship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prostration', 'n.', 'The condition of being prostrate; great depression;\n   lowness; dejection; as, a postration of spirits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prostration', 'n.', 'A latent, not an exhausted, state of the vital\n   energies; great oppression of natural strength and vigor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prostyle', 'a.', 'Having columns in front.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prostyle', 'n.', 'A prostyle portico or building.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prosy', 'superl.', 'Of or pertaining to prose; like prose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prosy', 'superl.', 'Dull and tedious in discourse or writing; prosaic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prosylogism', 'n.', 'A syllogism preliminary or logically essential to\n   another syllogism; the conclusion of such a syllogism, which becomes a\n   premise of the following syllogism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protactic', 'a.', 'Giving a previous narrative or explanation, as of the\n   plot or personages of a play; introductory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protagon', 'n.', 'A nitrogenous phosphorized principle found in brain\n   tissue. By decomposition it yields neurine, fatty acids, and other\n   bodies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protagonist', 'n.', 'One who takes the leading part in a drama; hence, one\n   who takes lead in some great scene, enterprise, conflict, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protamin', 'n.', 'An amorphous nitrogenous substance found in the spermatic\n   fluid of salmon. It is soluble in water, which an alkaline reaction,\n   and unites with acids and metallic bases.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protandric', 'a.', 'Having male sexual organs while young, and female\n   organs later in life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protandrous', 'a.', 'Proterandrous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protasis', 'n.', 'A proposition; a maxim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protasis', 'n.', 'The introductory or subordinate member of a sentence,\n   generally of a conditional sentence; -- opposed to apodosis. See\n   Apodosis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protasis', 'n.', 'The first part of a drama, of a poem, or the like; the\n   introduction; opposed to epitasis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protatic', 'a.', 'Of or pertaining to the protasis of an ancient play;\n   introductory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proteaceous', 'a.', 'Of or pertaining to the Proteaceae, an order of\n   apetalous evergreen shrubs, mostly natives of the Cape of Good Hope or\n   of Australia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protean', 'a.', 'Of or pertaining to Proteus; characteristic of Proteus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protean', 'a.', 'Exceedingly variable; readily assuming different shapes or\n   forms; as, an amoeba is a protean animalcule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proteanly', 'adv.', 'In a protean manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protected', 'imp. & p. p.', 'of Protect');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protecting', 'p. pr. & vb. n.', 'of Protect');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protect', 'v. t.', 'To cover or shield from danger or injury; to defend; to\n   guard; to preserve in safety; as, a father protects his children.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protectingly', 'adv.', 'By way of protection; in a protective manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protection', 'n.', 'The act of protecting, or the state of being protected;\n   preservation from loss, injury, or annoyance; defense; shelter; as, the\n   weak need protection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protection', 'n.', 'That which protects or preserves from injury; a\n   defense; a shield; a refuge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protection', 'n.', 'A writing that protects or secures from molestation or\n   arrest; a pass; a safe-conduct; a passport.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protection', 'n.', 'A theory, or a policy, of protecting the producers in a\n   country from foreign competition in the home market by the imposition\n   of such discriminating duties on goods of foreign production as will\n   restrict or prevent their importation; -- opposed to free trade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protectionism', 'n.', 'The doctrine or policy of protectionists. See\n   Protection, 4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protectionist', 'n.', 'One who favors protection. See Protection, 4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protective', 'a.', 'Affording protection; sheltering; defensive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protectiveness', 'n.', 'The quality or state of being protective.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protector', 'n.', 'One who, or that which, defends or shields from injury,\n   evil, oppression, etc.; a defender; a guardian; a patron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protector', 'n.', 'One having the care of the kingdom during the kings\n   minority; a regent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protector', 'n.', 'A cardinal, from one of the more considerable Roman\n   Catholic nations, who looks after the interests of his people at Rome;\n   also, a cardinal who has the same relation to a college, religious\n   order, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protectoral', 'a.', 'Of or pertaining to a protector; protectorial; as,\n   protectoral power.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protectorate', 'n.', 'Government by a protector; -- applied especially to\n   the government of England by Oliver Cromwell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protectorate', 'n.', 'The authority assumed by a superior power over an\n   inferior or a dependent one, whereby the former protects the latter\n   from invasion and shares in the management of its affairs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protectorial', 'a.', 'Same as Protectoral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protectorless', 'a.', 'Having no protector; unprotected.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protectorship', 'n.', 'The office of a protector or regent; protectorate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protectress', 'n.', 'Alt. of Protectrix');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protectrix', 'n.', 'A woman who protects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protege', 'n. f.', 'Alt. of Protegee');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protegee', 'n. f.', 'One under the care and protection of another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proteid', 'n.', 'One of a class of amorphous nitrogenous principles,\n   containing, as a rule, a small amount of sulphur; an albuminoid, as\n   blood fibrin, casein of milk, etc. Proteids are present in nearly all\n   animal fluids and make up the greater part of animal tissues and\n   organs. They are also important constituents of vegetable tissues. See\n   2d Note under Food.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proteidea', 'n. pl.', 'An order of aquatic amphibians having prominent\n   external gills and four legs. It includes Proteus and Menobranchus\n   (Necturus). Called also Proteoidea, and Proteida.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proteiform', 'a.', 'Changeable in form; resembling a Proteus, or an amoeba.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protein', 'n.', 'A body now known as alkali albumin, but originally\n   considered to be the basis of all albuminous substances, whence its\n   name.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proteinaceous', 'a.', 'Of or related to protein; albuminous; proteid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proteinous', 'a.', 'Proteinaceuos.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proteles', 'n.', 'A South Africa genus of Carnivora, allied to the hyenas,\n   but smaller and having weaker jaws and teeth. It includes the\n   aard-wolf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protended', 'imp. & p. p.', 'of Protend');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protending', 'p. pr. & vb. n.', 'of Protend');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protend', 'v. t.', 'To hold out; to stretch forth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protense', 'n.', 'Extension.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protension', 'n.', 'A drawing out; extension.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protensive', 'a.', 'Drawn out; extended.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proteolysis', 'n.', 'The digestion or dissolving of proteid matter by\n   proteolytic ferments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proteolytic', 'a.', 'Converting proteid or albuminous matter into soluble\n   and diffusible products, as peptones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proterandrous', 'a.', 'Having the stamens come to maturity before the\n   pistil; -- opposed to proterogynous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proterandry', 'n.', 'The condition of being proterandrous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proteranthous', 'a.', 'Having flowers appearing before the leaves; -- said\n   of certain plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proteroglypha', 'n. pl.', 'A suborder of serpents including those that have\n   permanently erect grooved poison fangs, with ordinary teeth behind them\n   in the jaws. It includes the cobras, the asps, and the sea snakes.\n   Called also Proteroglyphia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proterogynous', 'a.', 'Having the pistil come to maturity before the\n   stamens; protogynous; -- opposed to proterandrous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proterosaurus', 'n.', 'An extinct genus of reptiles of the Permian period.\n   Called also Protosaurus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protervity', 'n.', 'Peevishness; petulance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protested', 'imp. & p. p.', 'of Protest');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protesting', 'p. pr. & vb. n.', 'of Protest');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protest', 'v. i.', 'To affirm in a public or formal manner; to bear\n   witness; to declare solemnly; to avow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protest', 'v. i.', 'To make a solemn declaration (often a written one)\n   expressive of opposition; -- with against; as, he protest against your\n   votes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protest', 'v. t.', 'To make a solemn declaration or affirmation of; to\n   proclaim; to display; as, to protest ones loyalty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protest', 'v. t.', 'To call as a witness in affirming or denying, or to\n   prove an affirmation; to appeal to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protest', 'v.', 'A solemn declaration of opinion, commonly a formal\n   objection against some act; especially, a formal and solemn\n   declaration, in writing, of dissent from the proceedings of a\n   legislative body; as, the protest of lords in Parliament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protest', 'v.', 'A solemn declaration in writing, in due form, made by a\n   notary public, usually under his notarial seal, on behalf of the holder\n   of a bill or note, protesting against all parties liable for any loss\n   or damage by the nonacceptance or nonpayment of the bill, or by the\n   nonpayment of the note, as the case may be.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protest', 'v.', 'A declaration made by the master of a vessel before a\n   notary, consul, or other authorized officer, upon his arrival in port\n   after a disaster, stating the particulars of it, and showing that any\n   damage or loss sustained was not owing to the fault of the vessel, her\n   officers or crew, but to the perils of the sea, etc., ads the case may\n   be, and protesting against them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protest', 'v.', 'A declaration made by a party, before or while paying a\n   tax, duty, or the like, demanded of him, which he deems illegal,\n   denying the justice of the demand, and asserting his rights and claims,\n   in order to show that the payment was not voluntary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protestancy', 'n.', 'Protestantism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protestant', 'v.', 'One who protests; -- originally applied to those who\n   adhered to Luther, and protested against, or made a solemn declaration\n   of dissent from, a decree of the Emperor Charles V. and the Diet of\n   Spires, in 1529, against the Reformers, and appealed to a general\n   council; -- now used in a popular sense to designate any Christian who\n   does not belong to the Roman Catholic or the Greek Church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protestant', 'a.', 'Making a protest; protesting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protestant', 'a.', 'Of or pertaining to the faith and practice of those\n   Christians who reject the authority of the Roman Catholic Church; as,\n   Protestant writers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protestantical', 'a.', 'Protestant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protestantism', 'n.', 'The quality or state of being protestant, especially\n   against the Roman Catholic Church; the principles or religion of the\n   Protestants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protestantly', 'adv.', 'Like a Protestant; in conformity with\n   Protestantism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protestation', 'n.', 'The act of making a protest; a public avowal; a\n   solemn declaration, especially of dissent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protestation', 'n.', 'Formerly, a declaration in common-law pleading, by\n   which the party interposes an oblique allegation or denial of some\n   fact, protesting that it does or does not exist, and at the same time\n   avoiding a direct affirmation or denial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protestator', 'n.', 'One who makes protestation; a protester.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protester', 'n.', 'One who protests; one who utters a solemn declaration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protester', 'n.', 'One who protests a bill of exchange, or note.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protestingly', 'adv.', 'By way of protesting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proteus', 'n.', 'A sea god in the service of Neptune who assumed different\n   shapes at will. Hence, one who easily changes his appearance or\n   principles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proteus', 'n.', 'A genus of aquatic eel-shaped amphibians found in caves in\n   Austria. They have permanent external gills as well as lungs. The eyes\n   are small and the legs are weak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proteus', 'n.', 'A changeable protozoan; an amoeba.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prothalamion', 'n.', 'Alt. of Prothalamium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prothalamium', 'n.', 'A song in celebration of a marriage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prothallia', 'pl. ', 'of Prothallium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prothallium', 'n.', 'Same as Prothallus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prothalli', 'pl. ', 'of Prothallus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prothallus', 'n.', 'The minute primary growth from the spore of ferns and\n   other Pteridophyta, which bears the true sexual organs; the oophoric\n   generation of ferns, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prothesis', 'n.', 'A credence table; -- so called by the Eastern or Greek\n   Church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prothesis', 'n.', 'See Prosthesis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prothetic', 'a.', 'Of or pertaining to prothesis; as, a prothetic\n   apparatus.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-ries', 'pl. ', 'of Protonotary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prothonotary', 'n.', 'Alt. of Protonotary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protonotary', 'n.', 'A chief notary or clerk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protonotary', 'n.', 'Formerly, a chief clerk in the Court of Kings Bench\n   and in the Court of Common Pleas, now superseded by the master.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protonotary', 'n.', 'A register or chief clerk of a court in certain States\n   of the United States.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protonotary', 'n.', 'Formerly, one who had the charge of writing the acts\n   of the martyrs, and the circumstances of their death; now, one of\n   twelve persons, constituting a college in the Roman Curia, whose office\n   is to register pontifical acts and to make and preserve the official\n   record of beatifications.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protonotary', 'n.', 'The chief secretary of the patriarch of\n   Constantinople.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prothonotaryship', 'n.', 'Office of a prothonotary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prothoracic', 'a.', 'Of or pertaining to the prothorax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prothorax', 'n.', 'The first or anterior segment of the thorax in insects.\n   See Illusts. of Butterfly and Coleoptera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prothyalosomata', 'pl. ', 'of Pro thyalosoma');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pro thyalosoma', 'n.', 'The investing portion, or spherical envelope,\n   surrounding the eccentric germinal spot of the germinal vesicle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prothyalosome', 'n.', 'Same as Prothyalosoma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protist', 'n.', 'One of the Protista.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protista', 'n. pl.', 'A provisional group in which are placed a number of\n   low microscopic organisms of doubtful nature. Some are probably plants,\n   others animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protista', 'pl. ', 'of Protiston');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protiston', 'n.', 'One of the Protista.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proto-', '', 'A combining form prefix signifying first, primary,\n   primordial; as, protomartyr, the first martyr; protomorphic, primitive\n   in form; protoplast, a primordial organism; prototype, protozoan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proto-', '', 'Denoting the first or lowest of a series, or the one having\n   the smallest amount of the element to the name of which it is prefixed;\n   as protoxide, protochloride, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proto-', '', 'Sometimes used as equivalent to mono-, as indicating that the\n   compound has but one atom of the element to the name of which it is\n   prefixed. Also used adjectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protocanonical', 'a.', 'Of or pertaining to the first canon, or that which\n   contains the authorized collection of the books of Scripture; --\n   opposed to deutero-canonical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protocatechuic', 'a.', 'Pertaining to, derived from, or designating, an\n   organic acid which is obtained as a white crystalline substance from\n   catechin, asafetida, oil of cloves, etc., and by distillation itself\n   yields pyrocatechin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protocercal', 'a.', 'Having a caudal fin extending around the end of the\n   vertebral column, like that which is first formed in the embryo of\n   fishes; diphycercal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protococcus', 'n.', 'A genus of minute unicellular algae including the red\n   snow plant (Protococcus nivalis).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protocol', 'n.', 'The original copy of any writing, as of a deed, treaty,\n   dispatch, or other instrument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protocol', 'n.', 'The minutes, or rough draught, of an instrument or\n   transaction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protocol', 'n.', 'A preliminary document upon the basis of which\n   negotiations are carried on.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protocol', 'n.', 'A convention not formally ratified.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protocol', 'n.', 'An agreement of diplomatists indicating the results\n   reached by them at a particular stage of a negotiation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protocol', 'v. t.', 'To make a protocol of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protocol', 'v. i.', 'To make or write protocols, or first draughts; to\n   issue protocols.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protocolist', 'n.', 'One who draughts protocols.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protoconch', 'n.', 'The embryonic shell, or first chamber, of ammonites and\n   other cephalopods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proto-Doric', 'a.', 'Pertaining to, or designating, architecture, in which\n   the beginnings of the Doric style are supposed to be found.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protogine', 'n.', 'A kind of granite or gneiss containing a silvery talcose\n   mineral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protogynous', 'a.', 'Same as Proterogynous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protohippus', 'n.', 'A genus of fossil horses from the Lower Pliocene. They\n   had three toes on each foot, the lateral ones being small.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protomartyr', 'n.', 'The first martyr; the first who suffers, or is\n   sacrificed, in any cause; -- applied esp. to Stephen, the first\n   Christian martyr.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protomerite', 'n.', 'The second segment of one of the Gregarinae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protomorphic', 'a.', 'Having the most primitive character; in the earliest\n   form; as, a protomorphic layer of tissue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protonemata', 'pl. ', 'of Protonema');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protonema', 'n.', 'The primary growth from the spore of a moss, usually\n   consisting of branching confervoid filaments, on any part of which stem\n   and leaf buds may be developed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protonotary', 'n.', 'Same as Prothonotary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protoorganism', 'n.', 'An organism whose nature is so difficult to\n   determine that it might be referred to either the animal or the\n   vegetable kingdom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protopapas', 'n.', 'A protopope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protophyte', 'n.', 'Any unicellular plant, or plant forming only a\n   plasmodium, having reproduction only by fission, gemmation, or cell\n   division.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protophytology', 'n.', 'Paleobotany.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protopine', 'n.', 'An alkaloid found in opium in small quantities, and\n   extracted as a white crystalline substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protoplasm', 'n.', 'The viscid and more or less granular material of\n   vegetable and animal cells, possessed of vital properties by which the\n   processes of nutrition, secretion, and growth go forward; the so-called\n   \" physical basis of life;\" the original cell substance, cytoplasm,\n   cytoblastema, bioplasm sarcode, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protoplasmatic', 'a.', 'Protoplasmic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protoplasmic', 'a.', 'Of or pertaining to the first formation of living\n   bodies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protoplasmic', 'a.', 'Of or pertaining to protoplasm; consisting of, or\n   resembling, protoplasm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protoplast', 'n.', 'The thing first formed; that of which there are\n   subsequent copies or reproductions; the original.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protoplast', 'n.', 'A first-formed organized body; the first individual, or\n   pair of individuals, of a species.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protoplasta', 'n. pl.', 'A division of fresh-water rhizopods including\n   those that have a soft body and delicate branched pseudopodia. The\n   genus Gromia is one of the best-known.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protoplastic', 'a.', 'First-formed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protopodite', 'n.', 'The basal portion, or two proximal and more or less\n   consolidated segments, of an appendage of a crustacean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protopope', 'n.', 'One of the clergy of first rank in the lower order of\n   secular clergy; an archpriest; -- called also protopapas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protopterus', 'n.', 'See Komtok.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protosalt', 'n.', 'A salt derived from a protoxide base.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protosilicate', 'n.', 'A silicate formed with the lowest proportion of\n   silicic acid, or having but one atom of silicon in the molecule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protosomite', 'n.', 'One of the primitive segments, or metameres, of an\n   animal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protosulphide', 'n.', 'That one of a series of sulphides of any element\n   which has the lowest proportion of sulphur; a sulphide with but one\n   atom of sulphur in the molecule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protosulphuret', 'n.', 'A protosulphide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prototheria', 'n. pl.', 'Same as Monotremata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prototracheata', 'n. pl.', 'Same as Malacopoda.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prototype', 'n.', 'An original or model after which anything is copied; the\n   pattern of anything to be engraved, or otherwise copied, cast, or the\n   like; a primary form; exemplar; archetype.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protovertebrae', 'pl. ', 'of Protovertebra');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protovertebra', 'n.', 'One of the primitive masses, or segments, into which\n   the mesoblast of the vertebrate embryo breaks up on either side of the\n   anterior part of the notochord; a mesoblastic, or protovertebral,\n   somite. See Illust. of Ectoderm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protovertebral', 'a.', 'Of or pertaining to the protovertebrae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protoxide', 'n.', 'That one of a series of oxides having the lowest\n   proportion of oxygen. See Proto-, 2 (b).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protoxidize', 'v. t.', 'To combine with oxygen, as any elementary\n   substance, in such proportion as to form a protoxide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protozoa', 'n. pl.', 'The lowest of the grand divisions of the animal\n   kingdom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protozoan', 'a.', 'Of or pertaining to the Protozoa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protozoan', 'n.', 'One of the Protozoa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protozoic', 'a.', 'Of or pertaining to the Protozoa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protozoic', 'a.', 'Containing remains of the earliest discovered life of\n   the globe, which included mollusks, radiates and protozoans.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protozoa', 'pl. ', 'of Protozoon');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protozoon', 'n.', 'One of the Protozoa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protozoon', 'n.', 'A single zooid of a compound protozoan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protozoonite', 'n.', 'One of the primary, or first-formed, segments of an\n   embryonic arthropod.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protracheata', 'n. pl.', 'Same as Malacopoda.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protracted', 'imp. & p. p.', 'of Protract');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protracting', 'p. pr. vb. n.', 'of Protract');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protract', 'v. t.', 'To draw out or lengthen in time or (rarely) in space;\n   to continue; to prolong; as, to protract an argument; to protract a\n   war.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protract', 'v. t.', 'To put off to a distant time; to delay; to defer; as,\n   to protract a decision or duty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protract', 'v. t.', 'To draw to a scale; to lay down the lines and angles\n   of, with scale and protractor; to plot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protract', 'v. t.', 'To extend; to protrude; as, the cat can protract its\n   claws; -- opposed to retract.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protract', 'n.', 'Tedious continuance or delay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protracted', 'a.', 'Prolonged; continued.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protracter', 'n.', 'A protractor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protractile', 'a.', 'Capable of being protracted, or protruded; protrusile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protraction', 'n.', 'A drawing out, or continuing; the act of delaying the\n   termination of a thing; prolongation; continuance; delay; as, the\n   protraction of a debate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protraction', 'n.', 'The act or process of making a plot on paper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protraction', 'n.', 'A plot on paper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protractive', 'a.', 'Drawing out or lengthening in time; prolonging;\n   continuing; delaying.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protractor', 'n.', 'One who, or that which, protracts, or causes\n   protraction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protractor', 'n.', 'A mathematical instrument for laying down and measuring\n   angles on paper, used in drawing or in plotting. It is of various\n   forms, semicircular, rectangular, or circular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protractor', 'n.', 'An instrument formerly used in extracting foreign or\n   offensive matter from a wound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protractor', 'n.', 'A muscle which extends an organ or part; -- opposed to\n   retractor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protractor', 'n.', 'An adjustable pattern used by tailors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protreptical', 'a.', 'Adapted to persuade; hortatory; persuasive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protrudable', 'a.', 'That may be protruded; protrusile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protruded', 'imp. & p. p.', 'of Protrude');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protruding', 'p. pr. & vb. n.', 'of Protrude');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protrude', 'v. t.', 'To thrust forward; to drive or force along.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protrude', 'v. t.', 'To thrust out, as through a narrow orifice or from\n   confinement; to cause to come forth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protrude', 'v. i.', 'To shoot out or forth; to be thrust forward; to extend\n   beyond a limit; to project.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protrusile', 'a.', 'Capable of being protruded or thrust out; protractile;\n   protrusive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protrusion', 'n.', 'The act of protruding or thrusting forward, or beyond\n   the usual limit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protrusion', 'n.', 'The state of being protruded, or thrust forward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protrusive', 'a.', 'Thrusting or impelling forward; as, protrusive motion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protrusive', 'a.', 'Capable of being protruded; protrusile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protrusively', 'adv.', 'In a protrusive manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protuberance', 'n.', 'That which is protuberant swelled or pushed beyond\n   the surrounding or adjacent surface; a swelling or tumor on the body; a\n   prominence; a bunch or knob; an elevation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protuberancy', 'n.', 'The quality or state of being protuberant;\n   protuberance; prominence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protuberant', 'a.', 'Prominent, or excessively prominent; bulging beyond\n   the surrounding or adjacent surface; swelling; as, a protuberant joint;\n   a protuberant eye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protuberate', 'v. i.', 'To swell, or be prominent, beyond the adjacent\n   surface; to bulge out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protuberation', 'n.', 'The act of swelling beyond the surrounding surface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protuberous', 'a.', 'Protuberant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protureter', 'n.', 'The duct of a pronephros.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Protyle', 'n.', 'The hypothetical homogeneous cosmic material of the\n   original universe, supposed to have been differentiated into what are\n   recognized as distinct chemical elements.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proud', 'superl.', 'Feeling or manifesting pride, in a good or bad sense');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proud', 'superl.', 'Possessing or showing too great self-esteem; overrating\n   ones excellences; hence, arrogant; haughty; lordly; presumptuous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proud', 'superl.', 'Having a feeling of high self-respect or self-esteem;\n   exulting (in); elated; -- often with of; as, proud of ones country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proud', 'superl.', 'Giving reason or occasion for pride or\n   self-gratulation; worthy of admiration; grand; splendid; magnificent;\n   admirable; ostentatious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proud', 'superl.', 'Excited by sexual desire; -- applied particularly to\n   the females of some animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proudish', 'a.', 'Somewhat proud.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proudling', 'n.', 'A proud or haughty person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proudly', 'adv.', 'In a proud manner; with lofty airs or mien; haughtily;\n   arrogantly; boastfully.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proudness', 'n.', 'The quality of being proud; pride.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proustite', 'n.', 'A sulphide of arsenic and silver of a beautiful\n   cochineal-red color, occurring in rhombohedral crystals, and also\n   massive; ruby silver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Provable', 'a.', 'Capable of being proved; demonstrable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Provand', 'n.', 'Alt. of Proant');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proant', 'n.', 'Provender or food.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Provant', 'v. t.', 'To supply with provender or provisions; to provide for.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Provant', 'a.', 'Provided for common or general use, as in an army; hence,\n   common in quality; inferior.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proved', 'imp. & p. p.', 'of Prove');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proving', 'p. pr. & vb. n.', 'of Prove');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prove', 'v. t.', 'To try or to ascertain by an experiment, or by a test or\n   standard; to test; as, to prove the strength of gunpowder or of\n   ordnance; to prove the contents of a vessel by a standard measure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prove', 'v. t.', 'To evince, establish, or ascertain, as truth, reality, or\n   fact, by argument, testimony, or other evidence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prove', 'v. t.', 'To ascertain or establish the genuineness or validity of;\n   to verify; as, to prove a will.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prove', 'v. t.', 'To gain experience of the good or evil of; to know by\n   trial; to experience; to suffer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prove', 'v. t.', 'To test, evince, ascertain, or verify, as the correctness\n   of any operation or result; thus, in subtraction, if the difference\n   between two numbers, added to the lesser number, makes a sum equal to\n   the greater, the correctness of the subtraction is proved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prove', 'v. t.', 'To take a trial impression of; to take a proof of; as, to\n   prove a page.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prove', 'v. i.', 'To make trial; to essay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prove', 'v. i.', 'To be found by experience, trial, or result; to turn out\n   to be; as, a medicine proves salutary; the report proves false.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prove', 'v. i.', 'To succeed; to turn out as expected.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Provect', 'a.', 'Carried forward; advanced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Provection', 'n.', 'A carrying forward, as of a final letter, to a\n   following word; as, for example, a nickname for an ekename.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proveditor', 'n.', 'One employed to procure supplies, as for an army, a\n   steamer, etc.; a purveyor; one who provides for another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Provedore', 'n.', 'A proveditor; a purveyor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proven', 'p. p. / a.', 'Proved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proven/al', 'a.', 'Of or pertaining to Provence or its inhabitants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proven/al', 'n.', 'A native or inhabitant of Provence in France.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proven/al', 'n.', 'The Provencal language. See Langue doc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Provence rose', '', 'The cabbage rose (Rosa centifolia).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Provence rose', '', 'A name of many kinds of roses which are hybrids of\n   Rosa centifolia and R. Gallica.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Provencial', 'a.', 'Of or pertaining to Provence in France.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Provend', 'n.', 'See Provand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Provender', 'n.', 'Dry food for domestic animals, as hay, straw, corn,\n   oats, or a mixture of ground grain; feed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Provender', 'n.', 'Food or provisions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Provent', 'n.', 'See Provand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proventricle', 'n.', 'Proventriculus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proventriulus', 'n.', 'The glandular stomach of birds, situated just above\n   the crop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prover', 'n.', 'One who, or that which, proves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proverb', 'n.', 'An old and common saying; a phrase which is often\n   repeated; especially, a sentence which briefly and forcibly expresses\n   some practical truth, or the result of experience and observation; a\n   maxim; a saw; an adage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proverb', 'n.', 'A striking or paradoxical assertion; an obscure saying; an\n   enigma; a parable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proverb', 'n.', 'A familiar illustration; a subject of contemptuous\n   reference.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proverb', 'n.', 'A drama exemplifying a proverb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proverb', 'v. t.', 'To name in, or as, a proverb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proverb', 'v. t.', 'To provide with a proverb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proverb', 'v. i.', 'To write or utter proverbs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proverbial', 'a.', 'Mentioned or comprised in a proverb; used as a proverb;\n   hence, commonly known; as, a proverbial expression; his meanness was\n   proverbial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proverbial', 'a.', 'Of or pertaining to proverbs; resembling a proverb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proverbialism', 'n.', 'A proverbial phrase.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proverbialist', 'n.', 'One who makes much use of proverbs in speech or\n   writing; one who composes, collects, or studies proverbs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proverbialize', 'v. t. & i.', 'To turn into a proverb; to speak in\n   proverbs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proverbially', 'adv.', 'In a proverbial manner; by way of proverb; hence,\n   commonly; universally; as, it is proverbially said; the bee is\n   proverbially busy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Provexity', 'n.', 'Great advance in age.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Provided', 'imp. & p. p.', 'of Provide');
  INSERT INTO words (word, word_type, description) VALUES  (    'Providing', 'p. pr. & vb. n.', 'of Provide');
  INSERT INTO words (word, word_type, description) VALUES  (    'Provide', 'v. t.', 'To look out for in advance; to procure beforehand; to\n   get, collect, or make ready for future use; to prepare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Provide', 'v. t.', 'To supply; to afford; to contribute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Provide', 'v. t.', 'To furnish; to supply; -- formerly followed by of, now\n   by with.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Provide', 'v. t.', 'To establish as a previous condition; to stipulate; as,\n   the contract provides that the work be well done.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Provide', 'v. t.', 'To foresee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Provide', 'v. t.', 'To appoint to an ecclesiastical benefice before it is\n   vacant. See Provisor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Provide', 'v. i.', 'To procure supplies or means in advance; to take\n   measures beforehand in view of an expected or a possible future need,\n   especially a danger or an evil; -- followed by against or for; as, to\n   provide against the inclemency of the weather; to provide for the\n   education of a child.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Provide', 'v. i.', 'To stipulate previously; to condition; as, the\n   agreement provides for an early completion of the work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Provided', 'conj.', 'On condition; by stipulation; with the understanding;\n   if; -- usually followed by that; as, provided that nothing in this act\n   shall prejudice the rights of any person whatever.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Providence', 'n.', 'The act of providing or preparing for future use or\n   application; a making ready; preparation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Providence', 'n.', 'Foresight; care; especially, the foresight and care\n   which God manifests for his creatures; hence, God himself, regarded as\n   exercising a constant wise prescience.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Providence', 'n.', 'A manifestation of the care and superintendence which\n   God exercises over his creatures; an event ordained by divine\n   direction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Providence', 'n.', 'Prudence in the management of ones concerns; economy;\n   frugality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Provident', 'a.', 'Foreseeing wants and making provision to supply them;\n   prudent in preparing for future exigencies; cautious; economical; --\n   sometimes followed by of; as, aprovident man; an animal provident of\n   the future.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Providential', 'a.', 'Effected by, or referable to, divine direction or\n   superintendence; as, the providential contrivance of thing; a\n   providential escape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Providently', 'adv.', 'In a provident manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Providentness', 'n.', 'The quality or state of being provident;\n   carefulness; prudence; economy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Provider', 'n.', 'One who provides, furnishes, or supplies; one who\n   procures what is wanted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Providore', 'n.', 'One who makes provision; a purveyor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Province', 'n.', 'A country or region, more or less remote from the city of\n   Rome, brought under the Roman government; a conquered country beyond\n   the limits of Italy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Province', 'n.', 'A country or region dependent on a distant authority; a\n   portion of an empire or state, esp. one remote from the capital.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Province', 'n.', 'A region of country; a tract; a district.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Province', 'n.', 'A region under the supervision or direction of any\n   special person; the district or division of a country, especially an\n   ecclesiastical division, over which one has jurisdiction; as, the\n   province of Canterbury, or that in which the archbishop of Canterbury\n   exercises ecclesiastical authority.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Province', 'n.', 'The proper or appropriate business or duty of a person or\n   body; office; charge; jurisdiction; sphere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Province', 'n.', 'Specif.: Any political division of the Dominion of\n   Canada, having a governor, a local legislature, and representation in\n   the Dominion parliament. Hence, colloquially, The Provinces, the\n   Dominion of Canada.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Provincial', 'a.', 'Of or pertaining to province; constituting a province;\n   as, a provincial government; a provincial dialect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Provincial', 'a.', 'Exhibiting the ways or manners of a province;\n   characteristic of the inhabitants of a province; not cosmopolitan;\n   countrified; not polished; rude; hence, narrow; illiberal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Provincial', 'a.', 'Of or pertaining to an ecclesiastical province, or to\n   the jurisdiction of an archbishop; not ecumenical; as, a provincial\n   synod.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Provincial', 'a.', 'Of or pertaining to Provence; Provencal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Provincial', 'n.', 'A person belonging to a province; one who is\n   provincial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Provincial', 'n.', 'A monastic superior, who, under the general of his\n   order, has the direction of all the religious houses of the same\n   fraternity in a given district, called a province of the order.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Provincialism', 'n.', 'A word, or a manner of speaking, peculiar to a\n   province or a district remote from the mother country or from the\n   metropolis; a provincial characteristic; hence, narrowness;\n   illiberality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Provincialist', 'n.', 'One who lives in a province; a provincial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Provinciality', 'n.', 'The quality or state of being provincial;\n   peculiarity of language characteristic of a province.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Provincialized', 'imp. & p. p.', 'of Provincialize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Provincializing', 'p. pr. & vb. n.', 'of Provincialize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Provincialize', 'v. t.', 'To render provincial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Provincially', 'adv.', 'In a provincial manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Provinciate', 'v. t.', 'To convert into a province or provinces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Provine', 'v. t.', 'To lay a stock or branch of a vine in the ground for\n   propagation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Provision', 'n.', 'The act of providing, or making previous preparation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Provision', 'n.', 'That which is provided or prepared; that which is\n   brought together or arranged in advance; measures taken beforehand;\n   preparation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Provision', 'n.', 'Especially, a stock of food; any kind of eatables\n   collected or stored; -- often in the plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Provision', 'n.', 'That which is stipulated in advance; a condition; a\n   previous agreement; a proviso; as, the provisions of a contract; the\n   statute has many provisions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Provision', 'n.', 'A canonical term for regular induction into a benefice,\n   comprehending nomination, collation, and installation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Provision', 'n.', 'A nomination by the pope to a benefice before it became\n   vacant, depriving the patron of his right of presentation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Provisioned', 'imp. & p. p.', 'of Provision');
  INSERT INTO words (word, word_type, description) VALUES  (    'Provisioning', 'p. pr. & vb. n.', 'of Provision');
  INSERT INTO words (word, word_type, description) VALUES  (    'Provision', 'v. t.', 'To supply with food; to victual; as, to provision a\n   garrison.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Provisional', 'a.', 'Of the nature of a provision; serving as a provision\n   for the time being; -- used of partial or temporary arrangements; as, a\n   provisional government; a provisional treaty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Provisionally', 'adv.', 'By way of provision for the time being;\n   temporarily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Provisionary', 'a.', 'Provisional.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Provisos', 'pl. ', 'of Proviso');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proviso', 'n.', 'An article or clause in any statute, agreement, contract,\n   grant, or other writing, by which a condition is introduced, usually\n   beginning with the word provided; a conditional stipulation that\n   affects an agreement, contract, law, grant, or the like; as, the\n   contract was impaired by its proviso.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Provisor', 'n.', 'One who provides; a purveyor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Provisor', 'n.', 'The purveyor, steward, or treasurer of a religious house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Provisor', 'n.', 'One who is regularly inducted into a benefice. See\n   Provision, 5.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Provisor', 'n.', 'One who procures or receives a papal provision. See\n   Provision, 6.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Provisorily', 'adv.', 'In a provisory manner; conditionally; subject to a\n   proviso; as, to admit a doctrine provisorily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Provisorship', 'n.', 'The office or position of a provisor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Provisory', 'a.', 'Of the nature of a proviso; containing a proviso or\n   condition; conditional; as, a provisory clause.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Provisory', 'a.', 'Making temporary provision; provisional.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Provocation', 'n.', 'The act of provoking, or causing vexation or, anger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Provocation', 'n.', 'That which provokes, or excites anger; the cause of\n   resentment; as, to give provocation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Provocation', 'n.', 'Incitement; stimulus; as, provocation to mirth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Provocation', 'n.', 'Such prior insult or injury as may be supposed, under\n   the circumstances, to create hot blood, and to excuse an assault made\n   in retort or redress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Provocation', 'n.', 'An appeal to a court. [A Latinism]');
  INSERT INTO words (word, word_type, description) VALUES  (    'Provocative', 'a.', 'Serving or tending to provoke, excite, or stimulate;\n   exciting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Provocative', 'n.', 'Anything that is provocative; a stimulant; as, a\n   provocative of appetite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Provocativeness', 'n.', 'Quality of being provocative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Provocatory', 'a.', 'Provocative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Provokable', 'a.', 'That may be provoked.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Provoked', 'imp. & p. p.', 'of Provoke');
  INSERT INTO words (word, word_type, description) VALUES  (    'Provoking', 'p. pr. & vb. n.', 'of Provoke');
  INSERT INTO words (word, word_type, description) VALUES  (    'Provoke', 'v. t.', 'To call forth; to call into being or action; esp., to\n   incense to action, a faculty or passion, as love, hate, or ambition;\n   hence, commonly, to incite, as a person, to action by a challenge, by\n   taunts, or by defiance; to exasperate; to irritate; to offend\n   intolerably; to cause to retaliate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Provoke', 'v. i.', 'To cause provocation or anger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Provoke', 'v. i.', 'To appeal. [A Latinism]');
  INSERT INTO words (word, word_type, description) VALUES  (    'Provokement', 'n.', 'The act that which, provokes; one who excites anger or\n   other passion, or incites to action; as, a provoker of sedition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Provoking', 'a.', 'Having the power or quality of exciting resentment;\n   tending to awaken passion or vexation; as, provoking words or\n   treatment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Provost', 'n.', 'A person who is appointed to superintend, or preside over,\n   something; the chief magistrate in some cities and towns; as, the\n   provost of Edinburgh or of Glasgow, answering to the mayor of other\n   cities; the provost of a college, answering to president; the provost\n   or head of certain collegiate churches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Provost', 'n.', 'The keeper of a prison.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Provostship', 'n.', 'The office of a provost.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prow', 'n.', 'The fore part of a vessel; the bow; the stem; hence, the\n   vessel itself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prow', 'n.', 'See Proa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prow', 'superl.', 'Valiant; brave; gallant; courageous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prow', 'a.', 'Benefit; profit; good; advantage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prowess', 'a.', 'Distinguished bravery; valor; especially, military bravery\n   and skill; gallantry; intrepidity; fearlessness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prowled', 'imp. & p. p.', 'of Prowl');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prowling', 'p. pr. & vb. n.', 'of Prowl');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prowl', 'v. t.', 'To rove over, through, or about in a stealthy manner;\n   esp., to search in, as for prey or booty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prowl', 'v. t.', 'To collect by plunder; as, to prowl money.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prowl', 'v. i.', 'To rove or wander stealthily, esp. for prey, as a wild\n   beast; hence, to prey; to plunder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prowl', 'n.', 'The act of prowling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prowler', 'n.', 'One that prowls.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prowling', 'a.', 'Accustomed to prowl, or engaged in roving stealthily, as\n   for prey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prox', 'n.', '\"The ticket or list of candidates at elections, presented to\n   the people for their votes.\"');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proxene', 'n.', 'An officer who had the charge of showing hospitality to\n   those who came from a friendly city or state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proxenet', 'n.', 'A negotiator; a factor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proximad', 'adv.', 'Toward a proximal part; on the proximal side of;\n   proximally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proximal', 'a.', 'Toward or nearest, as to a body, or center of motion of\n   dependence; proximate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proximal', 'a.', 'Situated near the point of attachment or origin; as, the\n   proximal part of a limb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proximal', 'a.', 'Of or pertaining to that which is proximal; as, the\n   proximal bones of a limb. Opposed to distal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proximally', 'adv.', 'On or toward a proximal part; proximad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proximate', 'a.', 'Nearest; next immediately preceding or following.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proximately', 'adv.', 'In a proximate manner, position, or degree;\n   immediately.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proxime', 'a.', 'Next; immediately preceding or following.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proximious', 'a.', 'Proximate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proximity', 'n.', 'The quality or state of being next in time, place,\n   causation, influence, etc.; immediate nearness, either in place, blood,\n   or alliance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proximo', '', 'In the next month after the present; -- often contracted to\n   prox.; as, on the 3d proximo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proxies', 'pl. ', 'of Proxy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proxy', 'n.', 'The agency for another who acts through the agent; authority\n   to act for another, esp. to vote in a legislative or corporate\n   capacity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proxy', 'n.', 'The person who is substituted or deputed to act or vote for\n   another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proxy', 'n.', 'A writing by which one person authorizes another to vote in\n   his stead, as in a corporation meeting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proxy', 'n.', 'The written appointment of a proctor in suits in the\n   ecclesiastical courts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proxy', 'n.', 'See Procuration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proxy', 'v. i.', 'To act or vote by proxy; to do anything by the agency of\n   another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Proxyship', 'n.', 'The office or agency of a proxy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pruce', 'n.', 'Prussian leather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prude', 'a.', 'A woman of affected modesty, reserve, or coyness; one who is\n   overscrupulous or sensitive; one who affects extraordinary prudence in\n   conduct and speech.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prudence', 'n.', 'The quality or state of being prudent; wisdom in the way\n   of caution and provision; discretion; carefulness; hence, also,\n   economy; frugality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prudency', 'n.', 'Prudence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prudent', 'a.', 'Sagacious in adapting means to ends; circumspect in\n   action, or in determining any line of conduct; practically wise;\n   judicious; careful; discreet; sensible; -- opposed to rash; as, a\n   prudent man; dictated or directed by prudence or wise forethought;\n   evincing prudence; as, prudent behavior.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prudent', 'a.', 'Frugal; economical; not extravagant; as, a prudent woman;\n   prudent expenditure of money.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prudential', 'a.', 'Proceeding from, or dictated or characterized by,\n   prudence; prudent; discreet; sometimes, selfish or pecuniary as\n   distinguished from higher motives or influences; as, prudential\n   motives.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prudential', 'a.', 'Exercising prudence; discretionary; advisory;\n   superintending or executive; as, a prudential committee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prudential', 'n.', 'That which relates to or demands the exercise of,\n   discretion or prudence; -- usually in the pl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prudentialist', 'n.', 'One who is governed by, or acts from, prudential\n   motives.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prudentiality', 'n.', 'The quality or state of being prudential.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prudentially', 'adv.', 'In a prudential manner; prudently.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prudently', 'adv.', 'In a prudent manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pruderies', 'pl. ', 'of Prudery');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prudery', 'n.', 'The quality or state of being prudish; excessive or\n   affected scrupulousness in speech or conduct; stiffness; coyness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prudhomme', 'n.', 'A trustworthy citizen; a skilled workman. See Citation\n   under 3d Commune, 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prudish', 'a.', 'Like a prude; very formal, precise, or reserved;\n   affectedly severe in virtue; as, a prudish woman; prudish manners.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prudishly', 'adv.', 'In a prudish manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pruinate', 'a.', 'Same as Pruinose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pruinose', 'a.', 'Frosty; covered with fine scales, hairs, dust, bloom, or\n   the like, so as to give the appearance of frost.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pruinous', 'a.', 'Frosty; pruinose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pruned', 'imp. & p. p.', 'of Prune');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pruning', 'p. pr. & vb. n.', 'of Prune');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prune', 'v. t.', 'To lop or cut off the superfluous parts, branches, or\n   shoots of; to clear of useless material; to shape or smooth by\n   trimming; to trim: as, to prune trees; to prune an essay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prune', 'v. t.', 'To cut off or cut out, as useless parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prune', 'v. t.', 'To preen; to prepare; to dress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prune', 'v. i.', 'To dress; to prink; -used humorously or in contempt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prune', 'n.', 'A plum; esp., a dried plum, used in cookery; as, French or\n   Turkish prunes; California prunes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prunella', 'n.', 'Angina, or angina pectoris.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prunella', 'n.', 'Thrush.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prunella', 'n.', 'Alt. of Prunello');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prunello', 'n.', 'A smooth woolen stuff, generally black, used for making\n   shoes; a kind of lasting; -- formerly used also for clergymens gowns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prunelle', 'n.', 'A kind of small and very acid French plum; -- applied\n   especially to the stoned and dried fruit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prunello', 'n.', 'A species of dried plum; prunelle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pruner', 'n.', 'One who prunes, or removes, what is superfluous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pruner', 'n.', 'Any one of several species of beetles whose larvae gnaw the\n   branches of trees so as to cause them to fall, especially the American\n   oak pruner (Asemum moestum), whose larva eats the pith of oak branches,\n   and when mature gnaws a circular furrow on the inside nearly to the\n   bark. When the branches fall each contains a pupa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pruniferous', 'a.', 'Bearing plums.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pruning', 'n.', 'The act of trimming, or removing what is superfluous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pruning', 'n.', 'That which is cast off by bird in pruning her feathers;\n   leavings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prunus', 'n.', 'A genus of trees with perigynous rosaceous flowers, and a\n   single two-ovuled carpel which usually becomes a drupe in ripening.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prurience', 'n.', 'Alt. of Pruriency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pruriency', 'n.', 'The quality or state of being prurient.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prurient', 'a.', 'Uneasy with desire; itching; especially, having a\n   lascivious curiosity or propensity; lustful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pruriginous', 'a.', 'Tending to, or caused by, prurigo; affected by, or of\n   the nature of, prurigo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prurigo', 'n.', 'A papular disease of the skin, of which intense itching is\n   the chief symptom, the eruption scarcely differing from the healthy\n   cuticle in color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pruritus', 'n.', 'Itching.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prussian', 'a.', 'Of or pertaining to Prussia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prussian', 'n.', 'A native or inhabitant of Prussia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prussiate', 'n.', 'A salt of prussic acid; a cyanide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prussic', 'a.', 'designating the acid now called hydrocyanic acid, but\n   formerly called prussic acid, because Prussian blue is derived from it\n   or its compounds. See Hydrocyanic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prutenic', 'a.', 'Prussian; -- applied to certain astronomical tables\n   published in the sixteenth century, founded on the principles of\n   Copernicus, a Prussian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pry', 'n.', 'A lever; also, leverage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pried', 'imp. & p. p.', 'of Pry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prying', 'p. pr. & vb. n.', 'of Pry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pry', 'v. t.', 'To raise or move, or attempt to raise or move, with a pry\n   or lever; to prize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pry', 'v. i.', 'To peep narrowly; to gaze; to inspect closely; to attempt\n   to discover something by a scrutinizing curiosity; -- often implying\n   reproach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pry', 'n.', 'Curious inspection; impertinent peeping.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pryan', 'n.', 'See Prian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prying', 'a.', 'Inspecting closely or impertinently.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pryingly', 'adv.', 'In a prying manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prytaneum', 'n.', 'A public building in certain Greek cities; especially, a\n   public hall in Athens regarded as the home of the community, in which\n   official hospitality was extended to distinguished citizens and\n   strangers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prytanes', 'pl. ', 'of Prytanis');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prytanis', 'n.', 'A member of one of the ten sections into which the\n   Athenian senate of five hundred was divided, and to each of which\n   belonged the presidency of the senate for about one tenth of the year.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prytany', 'n.', 'The period during which the presidency of the senate\n   belonged to the prytanes of the section.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prythee', 'interj.', 'See Prithee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Psalm', 'n.', 'A sacred song; a poetical composition for use in the praise\n   or worship of God.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Psalm', 'n.', 'Especially, one of the hymns by David and others, collected\n   into one book of the Old Testament, or a modern metrical version of\n   such a hymn for public worship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Psalm', 'v. t.', 'To extol in psalms; to sing; as, psalming his praises.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Psalmist', 'n.', 'A writer or composer of sacred songs; -- a title\n   particularly applied to David and the other authors of the Scriptural\n   psalms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Psalmist', 'n.', 'A clerk, precentor, singer, or leader of music, in the\n   church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Psalmistry', 'n.', 'The use of psalms in devotion; psalmody.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Psalmodic', 'a.', 'Alt. of Psalmodical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Psalmodical', 'a.', 'Relating to psalmody.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Psalmodist', 'n.', 'One who sings sacred songs; a psalmist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Psalmodize', 'v. i.', 'To practice psalmody.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Psalmody', 'n.', 'The act, practice, or art of singing psalms or sacred\n   songs; also, psalms collectively, or a collection of psalms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Psalmograph', 'n.', 'A writer of psalms; a psalmographer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Psalmographer', 'n.', 'Alt. of Psalmographist');
  INSERT INTO words (word, word_type, description) VALUES  (    'Psalmographist', 'n.', 'A writer of psalms, or sacred songs and hymns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Psalmography', 'n.', 'The act or practice of writing psalms, or sacred\n   songs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Psalter', 'n.', 'The Book of Psalms; -- often applied to a book containing\n   the Psalms separately printed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Psalter', 'n.', 'Specifically, the Book of Psalms as printed in the Book of\n   Common Prayer; among the Roman Catholics, the part of the Breviary\n   which contains the Psalms arranged for each day of the week.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Psalter', 'n.', 'A rosary, consisting of a hundred and fifty beads,\n   corresponding to the number of the psalms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Psalterial', 'a.', 'Of or pertaining to the psalterium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Psalteria', 'pl. ', 'of Psalterium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Psalterium', 'n.', 'The third stomach of ruminants. See Manyplies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Psalterium', 'n.', 'The lyra of the brain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Psalteries', 'pl. ', 'of Psaltery');
  INSERT INTO words (word, word_type, description) VALUES  (    'Psaltery', 'n.', 'A stringed instrument of music used by the Hebrews, the\n   form of which is not known.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Psammite', 'n.', 'A species of micaceous sandstone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Psarolite', 'n.', 'A silicified stem of tree fern, found in abundance in\n   the Triassic sandstone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Psellism', 'n.', 'Indistinct pronunciation; stammering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Psephism', 'n.', 'A proposition adopted by a majority of votes; especially,\n   one adopted by vote of the Athenian people; a statute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pseudaesthesia', 'n.', 'False or imaginary feeling or sense perception such\n   as occurs in hypochondriasis, or such as is referred to an organ that\n   has been removed, as an amputated foot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pseudembryo', 'n.', 'A false embryo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pseudembryo', 'n.', 'An asexual form from which the true embryo is produced\n   by budding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pseudepigraphic', 'a.', 'Alt. of Pseudepigraphic');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pseudepigraphic', 'a.', 'Of or pertaining to pseudepigraphy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pseudepigraphous', 'a.', 'Inscribed with a false name.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pseudepigraphy', 'n.', 'The ascription of false names of authors to works.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pseudhaemal', 'a.', 'Pertaining to the vascular system of annelids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pseudo-', '', 'A combining form or prefix signifying false, counterfeit,\n   pretended, spurious; as, pseudo-apostle, a false apostle;\n   pseudo-clergy, false or spurious clergy; pseudo-episcopacy,\n   pseudo-form, pseudo-martyr, pseudo-philosopher. Also used adjectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pseudobacteria', 'n. pl.', 'Microscopic organic particles, molecular\n   granules, powdered inorganic substances, etc., which in form, size, and\n   grouping resemble bacteria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pseudoblepsis', 'n.', 'False or depraved sight; imaginary vision of\n   objects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pseudobranch', 'n.', 'Same as Pseudobranchia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pseudobranchiae', 'pl. ', 'of Pseudobranchia');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pseudobranchia', 'n.', 'A rudimentary branchia, or gill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pseudo-bulb', 'n.', 'An aerial corm, or thickened stem, as of some\n   epiphytic orchidaceous plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pseudocarp', 'n.', 'That portion of an anthocarpous fruit which is not\n   derived from the ovary, as the soft part of a strawberry or of a fig.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pseudo-china', 'n.', 'The false china root, a plant of the genus Smilax (S.\n   Pseudo-china), found in America.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pseudocoele', 'n.', 'Same as Pseudocoelia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pseudocoelia', 'n.', 'The fifth ventricle in the mammalian brain. See\n   Ventricle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pseudo-cone', 'n.', 'One of the soft gelatinous cones found in the compound\n   eyes of certain insects, taking the place of the crystalline cones of\n   others.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pseudo-cumene', 'n.', 'A hydrocarbon of the aromatic series, metameric with\n   mesitylene and cumene, found in coal tar, and obtained as a colorless\n   liquid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pseudo-dipteral', 'a.', 'Falsely or imperfectly dipteral, as a temple with\n   the inner range of columns surrounding the cella omitted, so that the\n   space between the cella wall and the columns is very great, being equal\n   to two intercolumns and one column.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pseudo-dipteral', 'n.', 'A pseudo-dipteral temple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pseudodox', 'a.', 'Not true in opinion or doctrine; false.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pseudodox', 'n.', 'A false opinion or doctrine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pseudofilari/', 'pl. ', 'of Pseudofilaria');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pseudofilaria', 'n.', 'One of the two elongated vibratile young formed by\n   fission of the embryo during the development of certain Gregarinae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pseudo-galena', 'n.', 'False galena, or blende. See Blende (a).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pseudograph', 'n.', 'A false writing; a spurious document; a forgery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pseudography', 'n.', 'False writing; forgery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pseudohalteres', 'pl. ', 'of Pseudohalter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pseudohalter', 'n.', 'One of the rudimentary front wings of certain insects\n   (Stylops). They resemble the halteres, or rudimentary hind wings, of\n   Diptera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pseudo-heart', 'n.', 'Any contractile vessel of invertebrates which is not\n   of the nature of a real heart, especially one of those pertaining to\n   the excretory system.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pseudo-hyperthophic', 'a.', 'Falsely hypertrophic; as, pseudo-hypertrophic\n   paralysis, a variety of paralysis in which the muscles are apparently\n   enlarged, but are really degenerated and replaced by fat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pseudologist', 'n.', 'One who utters falsehoods; a liar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pseudology', 'n.', 'Falsehood of speech.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pseudo-metallic', 'a.', 'Falsely or imperfectly metallic; -- said of a kind\n   of luster, as in minerals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pseudo-monocotyledonous', 'a.', 'Having two coalescent cotyledons, as the\n   live oak and the horse-chestnut.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pseudomorph', 'n.', 'An irregular or deceptive form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pseudomorph', 'n.', 'A pseudomorphous crystal, as a crystal consisting of\n   quartz, but having the cubic form of fluor spar, the fluor crystal\n   having been changed to quartz by a process of substitution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pseudomorphism', 'n.', 'The state of having, or the property of taking, a\n   crystalline form unlike that which belongs to the species.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pseudomorphous', 'a.', 'Not having the true form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pseudonavicullae', 'pl. ', 'of Pseudonavicella');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pseudonavicella', 'n.', 'Same as Pseudonavicula.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pseudonaviculae', 'pl. ', 'of Pseudonavicula');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pseudonavicula', 'n.', 'One of the minute spindle-shaped embryos of\n   Gregarinae and some other Protozoa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pseudoneuroptera', 'n. pl.', 'division of insects (Zool.) reticulated\n   wings, as in the Neuroptera, but having an active pupa state. It\n   includes the dragon flies, May flies, white ants, etc. By some\n   zoologists they are classed with the Orthoptera; by others, with the\n   Neuroptera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pseudoneuropterous', 'a.', 'Of or pertaining to the Pseudoneuroptera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pseudonym', 'n.', 'A fictitious name assumed for the time, as by an author;\n   a pen name.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pseudonumity', 'n.', 'The using of fictitious names, as by authors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pseudonymous', 'a.', 'Bearing a false or fictitious name; as, a\n   pseudonymous work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pseuso-peripteral', 'a.', 'Falsely or imperfectly peripteral, as a temple\n   having the columns at the sides attached to the walls, and an\n   ambulatory only at the ends or only at one end.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pseuso-peripteral', 'n.', 'A pseudo-peripteral temple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pseudopod', 'n.', 'Any protoplasmic filament or irregular process\n   projecting from any unicellular organism, or from any animal or plant\n   call.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pseudopod', 'n.', 'A rhizopod.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pseudopodial', 'a.', 'Of or pertaining to a pseudopod, or to pseudopodia.\n   See Illust. of Heliozoa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pseudopodia', 'pl. ', 'of Pseudopodium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pseudopodium', 'n.', 'Same as Pseudopod.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pseudopupae', 'pl. ', 'of Pseudopupa');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pseudopupas', 'pl. ', 'of Pseudopupa');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pseudopupa', 'n.', 'A stage intermediate between the larva and pupa of bees\n   and certain other hymenopterous insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pseudorhabdite', 'n.', 'One of the peculiar rodlike corpuscles found in the\n   integument of certain Turbellaria. They are filled with a soft granular\n   substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pseudo-romantic', 'a.', 'Falsely romantic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pseudoscope', 'n.', 'An instrument which exhibits objects with their proper\n   relief reversed; -- an effect opposite to that produced by the\n   stereoscope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pseudoscopic', 'a.', 'Of, pertaining to, or formed by, a pseudoscope;\n   having its parts appearing with the relief reversed; as, a pseudoscopic\n   image.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pseudoscorpiones', 'n. pl.', 'An order of Arachnoidea having the palpi\n   terminated by large claws, as in the scorpions, but destitute of a\n   caudal sting; the false scorpions. Called also Pseudoscorpii, and\n   Pseudoscorpionina. See Illust. of Book scorpion, under Book.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pseudosphere', 'n.', 'The surface of constant negative curvature generated\n   by the revolution of a tractrix. This surface corresponds in\n   non-Euclidian space to the sphere in ordinary space. An important\n   property of the surface is that any figure drawn upon it can be\n   displaced in any way without tearing it or altering in size any of its\n   elements.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pseudospore', 'n.', 'A peculiar reproductive cell found in some fungi.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-lae', 'pl. ', 'of Pseudostella');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pseudostella', 'n.', 'Any starlike meteor or phenomenon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pseudostomata', 'pl. ', 'of Pseudostoma');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pseudostoma', 'n.', 'A group of cells resembling a stoma, but without any\n   true aperture among them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pseudo-symmetric', 'a.', 'Exhibiting pseudo-symmetry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pseudo-symmetry', 'n.', 'A kind of symmetry characteristic of certain\n   crystals which from twinning, or other causes, come to resemble forms\n   of a system other than that to which they belong, as the apparently\n   hexagonal prisms of aragonite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pseudotetramera', 'n. pl.', 'A division of beetles having the fifth tarsal\n   joint minute and obscure, so that there appear to be but four joints.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pseudotineae', 'pl. ', 'of Pseudotinea');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pseudotinea', 'n.', 'The bee moth, or wax moth (Galleria).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pseudoturbinal', 'a.', 'See under Turbinal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pseudovaries', 'pl. ', 'of Pseudovary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pseudovary', 'n.', 'The organ in which pseudova are produced; -- called\n   also pseudovarium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pseudova', 'pl. ', 'of Pseudovum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pseudovum', 'n.', 'An egglike germ produced by the agamic females of some\n   insects and other animals, and by the larvae of certain insects. It is\n   capable of development without fertilization. See Illust. of\n   Paedogenesis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pshaw', 'interj.', 'Pish! pooch! -- an exclamation used as an expression of\n   contempt, disdain, dislike, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pshaw', 'v. i.', 'To express disgust or contemptuous disapprobation, as by\n   the exclamation \" Pshaw!\"');
  INSERT INTO words (word, word_type, description) VALUES  (    'Psilanthropic', 'a.', 'Pertaining to, or embodying, psilanthropy. \"A\n   psilanthropic explanation.\"');
  INSERT INTO words (word, word_type, description) VALUES  (    'Psilanthropism', 'n.', 'Psilanthropy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Psilanthropist', 'n.', 'One who believes that Christ was a mere man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Psilanthropy', 'n.', 'The doctrine of the merely human existence of Christ.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Psilology', 'n.', 'Love of empty of empty talk or noise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Psilomelane', 'n.', 'A hydrous oxide of manganese, occurring in smooth,\n   botryoidal forms, and massive, and having an iron-black or steel-gray\n   color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Psilopaedes', 'n. pl.', 'birds whose young at first have down on the\n   pterylae only; -- called also Gymnopaedes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Psilopaedic', 'a.', 'Having down upon the pterylae only; -- said of the\n   young of certain birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Psilosopher', 'n.', 'A superficial or narrow pretender to philosophy; a\n   sham philosopher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Psittaceous', 'a.', 'Alt. of Psittacid');
  INSERT INTO words (word, word_type, description) VALUES  (    'Psittacid', 'a.', 'Of or pertaining to the parrots, or the Psittaci.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Psittacid', 'n.', 'One of the Psittaci.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Psittaci', 'n. pl.', 'The order of birds which comprises the parrots.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Psitta-co-fulvine', 'n.', 'A yellow pigment found in the feathers of\n   certain parrots.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Psoas', 'n.', 'An internal muscle arising from the lumbar vertebrae and\n   inserted into the femur. In man there are usually two on each side, and\n   the larger one, or great psoas, forms a part of the iliopsoas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Psora', 'n.', 'A cutaneous disease; especially, the itch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Psoriasis', 'n.', 'The state of being affected with psora.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Psoriasis', 'n.', 'A cutaneous disease, characterized by imbricated silvery\n   scales, affecting only the superficial layers of the skin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Psoric', 'a.', 'Of or pertaining to psora.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Psorosperm', 'n.', 'A minute parasite, usually the young of Gregarinae, in\n   the pseudonavicula stage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Psychagogic', 'a.', 'Attractive; persuasive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Psychagogue', 'n.', 'A necromancer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Psychal', 'a.', 'Of or pertaining to the soul; psychical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Psyche', 'n.', 'A lovely maiden, daughter of a king and mistress of Eros,\n   or Cupid. She is regarded as the personification of the soul.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Psyche', 'n.', 'The soul; the vital principle; the mind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Psyche', 'n.', 'A cheval glass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Psychian', 'n.', 'Any small moth of the genus Psyche and allied genera\n   (family Psychidae). The larvae are called basket worms. See Basket\n   worm, under Basket.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Psychiatria', 'n.', 'Alt. of Psychiatry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Psychiatry', 'n.', 'The application of the healing art to mental diseases.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Psychiatric', 'a.', 'Of or pertaining to psychiatria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Psychic', 'a.', 'Alt. of Psychical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Psychical', 'a.', 'Of or pertaining to the human soul, or to the living\n   principle in man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Psychical', 'a.', 'Of or pertaining to the mind, or its functions and\n   diseases; mental; -- contrasted with physical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Psychics', 'n.', 'Psychology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Psychism', 'n.', 'The doctrine of Quesne, that there is a fluid universally\n   diffused, end equally animating all living beings, the difference in\n   their actions being due to the difference of the individual\n   organizations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Psycho-', '', 'A combining form from Gr. psychh` the soul, the mind, the\n   understanding; as, psychology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Psychogenesis', 'n.', 'Genesis through an internal force, as opposed to\n   natural selection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Psychography', 'n.', 'A description of the phenomena of mind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Psychography', 'n.', 'Spirit writing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Psychologic', 'a.', 'Alt. of Psychological');
  INSERT INTO words (word, word_type, description) VALUES  (    'Psychological', 'a.', 'Of or pertaining to psychology. See Note under\n   Psychic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Psychologist', 'n.', 'One who is versed in, devoted to, psychology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Psychologue', 'n.', 'A psychologist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Psychologies', 'pl. ', 'of Psychology');
  INSERT INTO words (word, word_type, description) VALUES  (    'Psychology', 'n.', 'The science of the human soul; specifically, the\n   systematic or scientific knowledge of the powers and functions of the\n   human soul, so far as they are known by consciousness; a treatise on\n   the human soul.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Psychomachy', 'n.', 'A conflict of the soul with the body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Psychomancy', 'n.', 'Necromancy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Psychometry', 'n.', 'The art of measuring the duration of mental processes,\n   or of determining the time relations of mental phenomena.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Psycho-motor', 'a.', 'Of or pertaining to movement produced by action of\n   the mind or will.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Psychopannychism', 'n.', 'The doctrine that the soul falls asleep at death,\n   and does not wake until the resurrection of the body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Psychopathy', 'n.', 'Mental disease. See Psychosis, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Psychophysical', 'a.', 'Of or pertaining to psychophysics; involving the\n   action or mutual relations of the psychical and physical in man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Psychophysics', 'n.', 'The science of the connection between nerve action\n   and consciousness; the science which treats of the relations of the\n   psychical and physical in their conjoint operation in man; the doctrine\n   of the relation of function or dependence between body and soul.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Psychopomp', 'n.', 'A leader or guide of souls .');
  INSERT INTO words (word, word_type, description) VALUES  (    'Psychosis', 'n.', 'Any vital action or activity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Psychosis', 'n.', 'A disease of the mind; especially, a functional mental\n   disorder, that is, one unattended with evident organic changes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Psychozoic', 'a.', 'Designating, or applied to the Era of man; as, the\n   psychozoic era.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Psychrometer', 'n.', 'An instrument for measuring the tension of the\n   aqueous vapor in the atmosphere, being essentially a wet and dry bulb\n   hygrometer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Psychrometrical', 'a.', 'Of or pertaining to the psychrometer or\n   psychrometry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Psychrometry', 'n.', 'Hygrometry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Psyllae', 'pl. ', 'of Psylla');
  INSERT INTO words (word, word_type, description) VALUES  (    'Psylla', 'n.', 'Any leaping plant louse of the genus Psylla, or family\n   Psyllidae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ptarmigan', 'n.', 'Any grouse of the genus Lagopus, of which numerous\n   species are known. The feet are completely feathered. Most of the\n   species are brown in summer, but turn white, or nearly white, in\n   winter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ptenoglossa', 'n. pl.', 'A division of gastropod mollusks having the teeth\n   of the radula arranged in long transverse rows, somewhat like the barbs\n   of a feather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ptenoglossate', 'a.', 'Of or pertaining to the Ptenoglossa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pteranodon', 'n.', 'A genus of American Cretaceous pterodactyls destitute\n   of teeth. Several species are known, some of which had an expanse of\n   wings of twenty feet or more.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pteranodontia', 'n. pl.', 'A group of pterodactyls destitute of teeth, as\n   in the genus Pteranodon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pterichthys', 'n.', 'A genus of Devonian fossil fishes with winglike\n   appendages. The head and most of the body were covered with large bony\n   plates. See Placodermi.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pteridologist', 'n.', 'One who is versed in pteridology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pteridology', 'n.', 'That department of botany which treats of ferns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pteridomania', 'n.', 'A madness, craze, or strong fancy, for ferns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pteridophyta', 'n. pl.', 'A class of flowerless plants, embracing ferns,\n   horsetails, club mosses, quillworts, and other like plants. See the\n   Note under Cryptogamia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pterobranchia', 'n. pl.', 'An order of marine Bryozoa, having a bilobed\n   lophophore and an axial cord. The genus Rhabdopleura is the type.\n   Called also Podostomata. See Rhabdopleura.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pteroceras', 'n.', 'A genus of large marine gastropods having the outer\n   border of the lip divided into lobes; -- called also scorpion shell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pterocletes', 'n. pl.', 'A division of birds including the sand grouse.\n   They are in some respects intermediate between the pigeons and true\n   grouse. Called also Pteroclomorphae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pterodactyl', 'n.', 'An extinct flying reptile; one of the Pterosauria. See\n   Illustration in Appendix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pterodactyli', 'n. pl.', 'Same as Pterosauria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pteroglossal', 'a.', 'Having the tongue finely notched along the sides, so\n   as to have a featherlike appearance, as the toucans.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pteron', 'n.', 'The region of the skull, in the temporal fossa back of the\n   orbit, where the great wing of the sphenoid, the temporal, the\n   parietal, and the frontal hones approach each other.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pteropappi', 'n. pl.', 'Same as Odontotormae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pterophore', 'n.', 'Any moth of the genus Pterophorus and allied genera; a\n   plume moth. See Plume moth, under Plume.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pteropod', 'n.', 'One of the Pteropoda.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pteropoda', 'n. pl.', 'A class of Mollusca in which the anterior lobes of\n   the foot are developed in the form of broad, thin, winglike organs,\n   with which they swim at near the surface of the sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pteropodous', 'a.', 'Of or pertaining to the Pteropoda.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pterosaur', 'n.', 'A pterodactyl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pterosauria', 'n. pl.', 'An extinct order of flying reptiles of the\n   Mesozoic age; the pterodactyls; -- called also Pterodactyli, and\n   Ornithosauria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pterosaurian', 'a.', 'Of or pertaining to the Pterosauria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pterostigmata', 'pl. ', 'of Pterostigma');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pterostigma', 'n.', 'A thickened opaque spot on the wings of certain\n   insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pterotic', 'a.', 'Of or pertaining to, or designating, a bone between the\n   prootic and epiotic in the dorsal and outer part of the periotic\n   capsule of many fishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pterotic', 'n.', 'The pterotic bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pterygiums', 'pl. ', 'of Pterygium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pterygia', 'pl. ', 'of Pterygium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pterygium', 'n.', 'A superficial growth of vascular tissue radiating in a\n   fanlike manner from the cornea over the surface of the eye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pterygoid', 'a.', 'Like a birds wing in form; as, a pterygoid bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pterygoid', 'a.', 'Of, pertaining to, or in the region of, the pterygoid\n   bones, pterygoid processes, or the whole sphenoid bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pterygoid', 'n.', 'A pterygoid bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pterygomaxillary', 'a.', 'Of or pertaining to the inner pterygoid plate, or\n   pterygoid bone, and the lower jaw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pterygopalatine', 'a.', 'Of or pertaining to the pterygoid processes and\n   the palatine bones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pterygopodia', 'pl. ', 'of Pterygopodium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pterygopodium', 'n.', 'A specially modified part of the ventral fin in male\n   elasmobranchs, which serves as a copulatory organ, or clasper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pterygoquadrate', 'a.', 'Of, pertaining to, or representing the pterygoid\n   and quadrate bones or cartilages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pterylae', 'pl. ', 'of Pteryla');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pteryla', 'n.', 'One of the definite areas of the skin of a bird on which\n   feathers grow; -- contrasted with apteria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pterylography', 'n.', 'The study or description of the arrangement of\n   feathers, or of the pterylae, of birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pterylosis', 'n.', 'The arrangement of feathers in definite areas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ptilocerque', 'n.', 'The pentail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ptilopaedes', 'n. pl.', 'Same as Dasypaedes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ptilopaedic', 'a.', 'Having nearly the whole surface of the skin covered\n   with down; dasypaedic; -- said of the young of certain birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ptilopteri', 'n. pl.', 'An order of birds including only the penguins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ptilosis', 'n.', 'Same as Pterylosis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ptisan', 'n.', 'A decoction of barley with other ingredients; a farinaceous\n   drink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ptisan', 'n.', 'An aqueous medicine, containing little, if any, medicinal\n   agent; a tea or tisane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ptolemaic', 'a.', 'Of or pertaining to Ptolemy, the geographer and\n   astronomer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ptolemaist', 'n.', 'One who accepts the astronomical system of Ptolemy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ptomaine', 'n.', 'One of a class of animal bases or alkaloids formed in the\n   putrefaction of various kinds of albuminous matter, and closely related\n   to the vegetable alkaloids; a cadaveric poison. The ptomaines, as a\n   class, have their origin in dead matter, by which they are to be\n   distinguished from the leucomaines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ptosis', 'n.', 'Drooping of the upper eyelid, produced by paralysis of its\n   levator muscle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ptyalin', 'n.', 'An unorganized amylolytic ferment, on enzyme, present in\n   human mixed saliva and in the saliva of some animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ptyalism', 'n.', 'Salivation, or an excessive flow of saliva.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ptyalogogue', 'n.', 'A ptysmagogue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ptysmagogue', 'n.', 'A medicine that promotes the discharge of saliva.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ptyxis', 'n.', 'The way in which a leaf is sometimes folded in the bud.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pubble', 'a.', 'Puffed out, pursy; pudgy; fat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puberal', 'a.', 'Of or pertaining to puberty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puberty', 'n.', 'The earliest age at which persons are capable of begetting\n   or bearing children, usually considered, in temperate climates, to be\n   about fourteen years in males and twelve in females.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puberty', 'n.', 'The period when a plant first bears flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puberulent', 'a.', 'Very minutely downy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pubes', 'n.', 'The hair which appears upon the lower part of the\n   hypogastric region at the age of puberty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pubes', 'n.', 'Hence (as more commonly used), the lower part of the\n   hypogastric region; the pubic region.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pubes', 'n.', 'The down of plants; a downy or villous substance which grows\n   on plants; pubescence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pubescence', 'n.', 'The quality or state of being pubescent, or of having\n   arrived at puberty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pubescence', 'n.', 'A covering of soft short hairs, or down, as one some\n   plants and insects; also, the state of being so covered.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pubescency', 'n.', 'Pubescence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pubescent', 'a.', 'Arrived at puberty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pubescent', 'a.', 'Covered with pubescence, or fine short hairs, as certain\n   insects, and the leaves of some plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pubic', 'a.', 'Of or pertaining to the pubes; in the region of the pubes;\n   as, the pubic bone; the pubic region, or the lower part of the\n   hypogastric region. See Pubes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pubic', 'a.', 'Of or pertaining to the pubis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pubis', 'n.', 'The ventral and anterior of the three principal bones\n   composing either half of the pelvis; sharebone; pubic bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Public', 'a.', 'Of or pertaining to the people; belonging to the people;\n   relating to, or affecting, a nation, state, or community; -- opposed to\n   private; as, the public treasury.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Public', 'a.', 'Open to the knowledge or view of all; general; common;\n   notorious; as, public report; public scandal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Public', 'a.', 'Open to common or general use; as, a public road; a public\n   house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Public', 'n.', 'The general body of mankind, or of a nation, state, or\n   community; the people, indefinitely; as, the American public; also, a\n   particular body or aggregation of people; as, an authors public.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Public', 'n.', 'A public house; an inn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Publican', 'n.', 'A farmer of the taxes and public revenues; hence, a\n   collector of toll or tribute. The inferior officers of this class were\n   often oppressive in their exactions, and were regarded with great\n   detestation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Publican', 'n.', 'The keeper of an inn or public house; one licensed to\n   retail beer, spirits, or wine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Publication', 'n.', 'The act of publishing or making known; notification to\n   the people at large, either by words, writing, or printing;\n   proclamation; divulgation; promulgation; as, the publication of the law\n   at Mount Sinai; the publication of the gospel; the publication of\n   statutes or edicts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Publication', 'n.', 'The act of offering a book, pamphlet, engraving, etc.,\n   to the public by sale or by gratuitous distribution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Publication', 'n.', 'That which is published or made known; especially, any\n   book, pamphlet, etc., offered for sale or to public notice; as, a daily\n   or monthly publication.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Publication', 'n.', 'An act done in public.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Public-hearted', 'a.', 'Public-spirited.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Publicist', 'n.', 'A writer on the laws of nature and nations; one who is\n   versed in the science of public right, the principles of government,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Publicity', 'n.', 'The quality or state of being public, or open to the\n   knowledge of a community; notoriety; publicness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Publicly', 'adv.', 'With exposure to popular view or notice; without\n   concealment; openly; as, property publicly offered for sale; an opinion\n   publicly avowed; a declaration publicly made.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Publicly', 'adv.', 'In the name of the community.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Public-minded', 'a.', 'Public-spirited.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Publicness', 'n.', 'The quality or state of being public, or open to the\n   view or notice of people at large; publicity; notoriety; as, the\n   publicness of a sale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Publicness', 'n.', 'The quality or state of belonging to the community; as,\n   the publicness of property.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Public-spirited', 'a.', 'Having, or exercising, a disposition to advance\n   the interest of the community or public; as, public-spirited men.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Public-spirited', 'a.', 'Dictated by a regard to public good; as, a\n   public-spirited project or measure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Published', 'imp. & p. p.', 'of Publish');
  INSERT INTO words (word, word_type, description) VALUES  (    'Publishing', 'p. pr. & vb. n.', 'of Publish');
  INSERT INTO words (word, word_type, description) VALUES  (    'Publish', 'v. t.', 'To make public; to make known to mankind, or to people\n   in general; to divulge, as a private transaction; to promulgate or\n   proclaim, as a law or an edict.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Publish', 'v. t.', 'To make known by posting, or by reading in a church;\n   as, to publish banns of marriage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Publish', 'v. t.', 'To send forth, as a book, newspaper, musical piece, or\n   other printed work, either for sale or for general distribution; to\n   print, and issue from the press.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Publish', 'v. t.', 'To utter, or put into circulation; as, to publish\n   counterfeit paper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Publishable', 'a.', 'Capable of being published; suitable for publication.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Publisher', 'n.', 'One who publishes; as, a publisher of a book or\n   magazine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Publishment', 'n.', 'The act or process of making publicly known;\n   publication.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Publishment', 'n.', 'A public notice of intended marriage, required by the\n   laws of some States.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puccoon', 'n.', 'Any one of several plants yielding a red pigment which is\n   used by the North American Indians, as the bloodroot and two species of\n   Lithospermum (L. hirtum, and L. canescens); also, the pigment itself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puce', 'a.', 'Of a dark brown or brownish purple color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pucel', 'n.', 'See Pucelle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pucelage', 'n.', 'Virginity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pucelle', 'n.', 'A maid; a virgin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puceron', 'n.', 'Any plant louse, or aphis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pucherite', 'n.', 'Vanadate of bismuth, occurring in minute reddish brown\n   crystals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puck', 'n.', 'A celebrated fairy, \"the merry wanderer of the night;\" --\n   called also Robin Goodfellow, Friar Rush, Pug, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puck', 'n.', 'The goatsucker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puckball', 'n.', 'A puffball.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puckered', 'imp. & p. p.', 'of Pucker');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puckering', 'p. pr. & vb. n.', 'of Pucker');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pucker', 'v. t. & i.', 'To gather into small folds or wrinkles; to contract\n   into ridges and furrows; to corrugate; -- often with up; as, to pucker\n   up the mouth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pucker', 'n.', 'A fold; a wrinkle; a collection of folds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pucker', 'n.', 'A state of perplexity or anxiety; confusion; bother;\n   agitation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puckerer', 'n.', 'One who, or that which, puckers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puckery', 'a.', 'Producing, or tending to produce, a pucker; as, a puckery\n   taste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puckery', 'a.', 'Inclined to become puckered or wrinkled; full of puckers\n   or wrinkles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puckfist', 'n.', 'A puffball.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puckish', 'a.', 'Resembling Puck; merry; mischievous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pucras', 'n.', 'See Koklass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pud', 'n.', 'Same as Pood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pud', 'n.', 'The hand; the first.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puddening', 'n.', 'A quantity of rope-yarn, or the like, placed, as a\n   fender, on the bow of a boat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puddening', 'n.', 'A bunch of soft material to prevent chafing between\n   spars, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puddered', 'imp. & p. p.', 'of Pudder');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puddering', 'p. pr. & vb. n.', 'of Pudder');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pudder', 'v. i.', 'To make a tumult or bustle; to splash; to make a pother\n   or fuss; to potter; to meddle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pudder', 'v. t.', 'To perplex; to embarrass; to confuse; to bother; as, to\n   pudder a man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pudder', 'n.', 'A pother; a tumult; a confused noise; turmoil; bustle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pudding', 'n.', 'A species of food of a soft or moderately hard\n   consistence, variously made, but often a compound of flour or meal,\n   with milk and eggs, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pudding', 'n.', 'Anything resembling, or of the softness and consistency\n   of, pudding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pudding', 'n.', 'An intestine; especially, an intestine stuffed with meat,\n   etc.; a sausage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pudding', 'n.', 'Any food or victuals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pudding', 'n.', 'Same as Puddening.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pudding-headed', 'a.', 'Stupid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puddle', 'n.', 'A small quantity of dirty standing water; a muddy plash; a\n   small pool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puddle', 'n.', 'Clay, or a mixture of clay and sand, kneaded or worked,\n   when wet, to render it impervious to water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puddled', 'imp. & p. p.', 'of Puddle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puddling', 'p. pr. & vb. n.', 'of Puddle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puddle', 'v. t.', 'To make foul or muddy; to pollute with dirt; to mix dirt\n   with (water).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puddle', 'v. t.', 'To make dense or close, as clay or loam, by working when\n   wet, so as to render impervious to water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puddle', 'v. t.', 'To make impervious to liquids by means of puddle; to\n   apply puddle to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puddle', 'v. t.', 'To subject to the process of puddling, as iron, so as to\n   convert it from the condition of cast iron to that of wrought iron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puddle', 'v. i.', 'To make a dirty stir.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puddle-ball', 'n.', 'The lump of pasty wrought iron as taken from the\n   puddling furnace to be hammered or rolled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puddle-bar', 'n.', 'An iron bar made at a single heat from a puddle-ball\n   hammering and rolling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puddler', 'n.', 'One who converts cast iron into wrought iron by the\n   process of puddling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puddling', 'n.', 'The process of working clay, loam, pulverized ore, etc.,\n   with water, to render it compact, or impervious to liquids; also, the\n   process of rendering anything impervious to liquids by means of puddled\n   material.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puddling', 'n.', 'Puddle. See Puddle, n., 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puddling', 'n.', 'The art or process of converting cast iron into wrought\n   iron or steel by subjecting it to intense heat and frequent stirring in\n   a reverberatory furnace in the presence of oxidizing substances, by\n   which it is freed from a portion of its carbon and other impurities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puddly', 'a.', 'Consisting of, or resembling, puddles; muddy; foul.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puddock', 'n.', 'A small inclosure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pudency', 'n.', 'Modesty; shamefacedness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pudenda', 'n. pl.', 'The external organs of generation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pudendal', 'a.', 'Of or pertaining to the pudenda, or pudendum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pudendum', 'n.', 'The external organs of generation, especially of the\n   female; the vulva.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pudgy', 'a.', 'Short and fat or sturdy; dumpy; podgy; as, a short, pudgy\n   little man; a pudgy little hand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pudic', 'a.', 'Of or pertaining to the external organs of generation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pudical', 'a.', 'Pudic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pudicity', 'n.', 'Modesty; chastity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pudu', 'n.', 'A very small deer (Pudua humilis), native of the Chilian\n   Andes. It has simple spikelike antlers, only two or three inches long.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pued', 'imp. & p. p.', 'of Pue');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puing', 'p. pr. & vb. n.', 'of Pue');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pue', 'v. i.', 'To make a low whistling sound; to chirp, as birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pueblo', 'n.', 'A communistic building erected by certain Indian tribes of\n   Arizona and New Mexico. It is often of large size and several stories\n   high, and is usually built either of stone or adobe. The term is also\n   applied to any Indian village in the same region.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puefellow', 'n.', 'A pewfellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puer', 'n.', 'The dung of dogs, used as an alkaline steep in tanning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puerco', 'n.', 'A hog.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puerile', 'a.', 'Boyish; childish; trifling; silly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puerilely', 'adv.', 'In a puerile manner; childishly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puerileness', 'n.', 'The quality of being puerile; puerility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puerilities', 'pl. ', 'of Puerility');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puerility', 'n.', 'The quality of being puerile; childishness; puerileness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puerility', 'n.', 'That which is puerile or childish; especially, an\n   expression which is flat, insipid, or silly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puerperal', 'a.', 'Of or pertaining to childbirth; as, a puerperal fever.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puerperous', 'a.', 'Bearing children.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puet', 'n.', 'The pewit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puff', 'n.', 'A sudden and single emission of breath from the mouth; hence,\n   any sudden or short blast of wind; a slight gust; a whiff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puff', 'n.', 'Anything light and filled with air.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puff', 'n.', 'A puffball.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puff', 'n.', 'a kind of light pastry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puff', 'n.', 'A utensil of the toilet for dusting the skin or hair with\n   powder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puff', 'n.', 'An exaggerated or empty expression of praise, especially one\n   in a public journal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puffed', 'imp. & p. p.', 'of Puff');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puffing', 'p. pr. & vb. n.', 'of Puff');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puff', 'n.', 'To blow in puffs, or with short and sudden whiffs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puff', 'n.', 'To blow, as an expression of scorn; -- with at.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puff', 'n.', 'To breathe quick and hard, or with puffs, as after violent\n   exertion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puff', 'n.', 'To swell with air; to be dilated or inflated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puff', 'n.', 'To breathe in a swelling, inflated, or pompous manner; hence,\n   to assume importance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puff', 'v. t.', 'To drive with a puff, or with puffs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puff', 'v. t.', 'To repel with words; to blow at contemptuously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puff', 'v. t.', 'To cause to swell or dilate; to inflate; to ruffle with\n   puffs; -- often with up; as, a bladder puffed with air.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puff', 'v. t.', 'To inflate with pride, flattery, self-esteem, or the like;\n   -- often with up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puff', 'v. t.', 'To praise with exaggeration; to flatter; to call public\n   attention to by praises; to praise unduly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puff', 'a.', 'Puffed up; vain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puffball', 'n.', 'A kind of ball-shaped fungus (Lycoperdon giganteum, and\n   other species of the same genus) full of dustlike spores when ripe; --\n   called also bullfist, bullfice, puckfist, puff, and puffin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puffer', 'n.', 'One who puffs; one who praises with noisy or extravagant\n   commendation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puffer', 'n.', 'One who is employed by the owner or seller of goods sold at\n   suction to bid up the price; a by-bidder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puffer', 'n.', 'Any plectognath fish which inflates its body, as the\n   species of Tetrodon and Diodon; -- called also blower, puff-fish,\n   swellfish, and globefish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puffer', 'n.', 'The common, or harbor, porpoise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puffer', 'n.', 'A kier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puffery', 'n.', 'The act of puffing; bestowment of extravagant\n   commendation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puffin', 'n.', 'An arctic sea bird Fratercula arctica) allied to the auks,\n   and having a short, thick, swollen beak, whence the name; -- called\n   also bottle nose, cockandy, coulterneb, marrot, mormon, pope, and sea\n   parrot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puffin', 'n.', 'The puffball.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puffin', 'n.', 'A sort of apple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puffiness', 'n.', 'The quality or state of being puffy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puffing', '', 'a. & n. from Puff, v. i. & t.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puffingly', 'adv.', 'In a puffing manner; with vehement breathing or\n   shortness of breath; with exaggerated praise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puff-leg', 'n.', 'Any one of numerous species of beautiful humming birds of\n   the genus Eriocnemis having large tufts of downy feathers on the legs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puff-legged', 'a.', 'Having a conspicuous tuft of feathers on the legs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puffy', 'a.', 'Swelled with air, or any soft matter; tumid with a soft\n   substance; bloated; fleshy; as, a puffy tumor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puffy', 'a.', 'Hence, inflated; bombastic; as, a puffy style.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pugged', 'imp. & p. p.', 'of Pug');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pugging', 'p. pr. & vb. n.', 'of Pug');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pug', 'v. t.', 'To mix and stir when wet, as clay for bricks, pottery, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pug', 'v. t.', 'To fill or stop with clay by tamping; to fill in or spread\n   with mortar, as a floor or partition, for the purpose of deadening\n   sound. See Pugging, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pug', 'n.', 'Tempered clay; clay moistened and worked so as to be plastic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pug', 'n.', 'A pug mill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pug', 'n.', 'An elf, or a hobgoblin; also same as Puck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pug', 'n.', 'A name for a monkey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pug', 'n.', 'A name for a fox.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pug', 'n.', 'An intimate; a crony; a dear one.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pug', 'n.', 'Chaff; the refuse of grain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pug', 'n.', 'A prostitute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pug', 'n.', 'One of a small breed of pet dogs having a short nose and head;\n   a pug dog.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pug', 'n.', 'Any geometrid moth of the genus Eupithecia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pug-faced', 'a.', 'Having a face like a monkey or a pug; monkey-faced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pugger', 'v. t.', 'To pucker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puggered', 'a.', 'Puckered.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pugging', 'v. t.', 'The act or process of working and tempering clay to\n   make it plastic and of uniform consistency, as for bricks, for pottery,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pugging', 'v. t.', 'Mortar or the like, laid between the joists under the\n   boards of a floor, or within a partition, to deaden sound; -- in the\n   United States usually called deafening.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pugging', 'a.', 'Thieving.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pugh', 'interj.', 'Pshaw! pish! -- a word used in contempt or disdain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pugil', 'n.', 'As much as is taken up between the thumb and two first\n   fingers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pugilism', 'n.', 'The practice of boxing, or fighting with the fist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pugilist', 'n.', 'One who fights with his fists; esp., a professional prize\n   fighter; a boxer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pugilistic', 'a.', 'Of or pertaining to pugillism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pugnacious', 'a.', 'Disposed to fight; inclined to fighting; quarrelsome;\n   fighting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pugnacity', 'n.', 'Inclination or readiness to fight; quarrelsomeness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pug nose', '', 'A short, thick nose; a snubnose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puh', 'interj.', 'The same as Pugh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puisne', 'a.', 'Later in age, time, etc.; subsequent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puisne', 'a.', 'Puny; petty; unskilled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puisne', 'a.', 'Younger or inferior in rank; junior; associate; as, a chief\n   justice and three puisne justices of the Court of Common Pleas; the\n   puisne barons of the Court of Exchequer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puisne', 'n.', 'One who is younger, or of inferior rank; a junior; esp., a\n   judge of inferior rank.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puisny', 'a.', 'Puisne; younger; inferior; petty; unskilled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puissance', 'n.', 'Power; strength; might; force; potency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puissant', 'a.', 'Powerful; strong; mighty; forcible; as, a puissant prince\n   or empire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puissantly', 'adv.', 'In a puissant manner; powerfully; with great\n   strength.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puissantness', 'n.', 'The state or quality of being puissant; puissance;\n   power.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puit', 'n.', 'A well; a small stream; a fountain; a spring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puked', 'imp. & p. p.', 'of Puke');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puking', 'p. pr. & vb. n.', 'of Puke');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puke', 'v. i.', 'To eject the contests of the stomach; to vomit; to spew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puke', 'v. t.', 'To eject from the stomach; to vomit up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puke', 'n.', 'A medicine that causes vomiting; an emetic; a vomit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puke', 'a.', 'Of a color supposed to be between black and russet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puker', 'n.', 'One who pukes, vomits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puker', 'n.', 'That which causes vomiting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulas', 'n.', 'The East Indian leguminous tree Butea frondosa. See Gum\n   Butea, under Gum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulchritude', 'n.', 'That quality of appearance which pleases the eye;\n   beauty; comeliness; grace; loveliness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulchritude', 'n.', 'Attractive moral excellence; moral beauty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puled', 'imp. & p. p.', 'of Pule');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puling', 'p. pr. & vb. n.', 'of Pule');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pule', 'v. i.', 'To cry like a chicken.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pule', 'v. i.', 'To whimper; to whine, as a complaining child.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puler', 'n.', 'One who pules; one who whines or complains; a weak person.');
