  INSERT INTO words (word, word_type, description) VALUES  (    'Pulicene', 'a.', 'Pertaining to, or abounding in, fleas; pulicose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulicose', 'a.', 'Alt. of Pulicous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulicous', 'a.', 'Abounding with fleas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puling', 'n.', 'A cry, as of a chicken,; a whining or whimpering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puling', 'a.', 'Whimpering; whining; childish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulingly', 'adv.', 'With whining or complaint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulkha', 'n.', 'A Laplanders traveling sledge. See Sledge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulled', 'imp. & p. p.', 'of Pull');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulling', 'p. pr. & vb. n.', 'of Pull');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pull', 'v. t.', 'To draw, or attempt to draw, toward one; to draw forcibly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pull', 'v. t.', 'To draw apart; to tear; to rend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pull', 'v. t.', 'To gather with the hand, or by drawing toward one; to\n   pluck; as, to pull fruit; to pull flax; to pull a finch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pull', 'v. t.', 'To move or operate by the motion of drawing towards one;\n   as, to pull a bell; to pull an oar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pull', 'v. t.', 'To hold back, and so prevent from winning; as, the\n   favorite was pulled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pull', 'v. t.', 'To take or make, as a proof or impression; -- hand presses\n   being worked by pulling a lever.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pull', 'v. t.', 'To strike the ball in a particular manner. See Pull, n.,\n   8.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pull', 'v. i.', 'To exert ones self in an act or motion of drawing or\n   hauling; to tug; as, to pull at a rope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pull', 'n.', 'The act of pulling or drawing with force; an effort to move\n   something by drawing toward one.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pull', 'n.', 'A contest; a struggle; as, a wrestling pull.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pull', 'n.', 'A pluck; loss or violence suffered.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pull', 'n.', 'A knob, handle, or lever, etc., by which anything is pulled;\n   as, a drawer pull; a bell pull.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pull', 'n.', 'The act of rowing; as, a pull on the river.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pull', 'n.', 'The act of drinking; as, to take a pull at the beer, or the\n   mug.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pull', 'n.', 'Something in ones favor in a comparison or a contest; an\n   advantage; means of influencing; as, in weights the favorite had the\n   pull.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pull', 'n.', 'A kind of stroke by which a leg ball is sent to the off side,\n   or an off ball to the side.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pullail', 'n.', 'Poultry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pullback', 'n.', 'That which holds back, or causes to recede; a drawback; a\n   hindrance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pullback', 'n.', 'The iron hook fixed to a casement to pull it shut, or to\n   hold it party open at a fixed point.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulled', 'a.', 'Plucked; pilled; moulting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pullen', 'n.', 'Poultry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puller', 'n.', 'One who, or that which, pulls.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pullet', 'n.', 'A young hen, or female of the domestic fowl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulleys', 'pl. ', 'of Pulley');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulley', 'v. t.', 'A wheel with a broad rim, or grooved rim, for\n   transmitting power from, or imparting power to, the different parts of\n   machinery, or for changing the direction of motion, by means of a belt,\n   cord, rope, or chain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulley', 'b. t.', 'To raise or lift by means of a pulley.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pullicate', 'n.', 'A kind of checked cotton or silk handkerchief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pullman car', '', 'A kind of sleeping car; also, a palace car; -- often\n   shortened to Pullman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pullulate', 'v. i.', 'To germinate; to bud; to multiply abundantly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pullulation', 'n.', 'A germinating, or budding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulli', 'pl. ', 'of Pullus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pullus', 'n.', 'A chick; a young bird in the downy stage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulmobranchiata', 'a. & n.', 'Alt. of Pulmobranchiate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulmobranchiate', 'a. & n.', 'Same as Pulmonibranchiata, -ate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulmocutaneous', 'a.', 'Of or pertaining to the lungs and the akin; as, the\n   pulmocutaneous arteries of the frog.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulmogasteropoda', 'n. pl.', 'Same as Pulmonata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulmograde', 'a.', 'Swimming by the expansion and contraction, or lunglike\n   movement, of the body, or of the disk, as do the medusae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulmometer', 'n.', 'A spirometer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulmonarian', 'n.', 'Any arachnid that breathes by lunglike organs, as the\n   spiders and scorpions. Also used adjectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulmonary', 'a.', 'Of or pertaining to the lungs; affecting the lungs;\n   pulmonic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulmonary', 'a.', 'Lungwort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulmonata', 'n. pl.', 'An extensive division, or sub-class, of\n   hermaphrodite gastropods, in which the mantle cavity is modified into\n   an air-breathing organ, as in Helix, or land snails, Limax, or garden\n   slugs, and many pond snails, as Limnaea and Planorbis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulmonate', 'a.', 'Having breathing organs that act as lungs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulmonate', 'a.', 'Pertaining to the Pulmonata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulmonate', 'n.', 'One of the Pulmonata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulmonated', 'a.', 'same as Pulmonate (a).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulmonibranchiata', 'n. pl.', 'Same as Pulmonata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulmonibranchiate', 'a. & n.', 'Same as Pulmonate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulmonic', 'a.', 'Relating to, or affecting the lungs; pulmonary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulmonic', 'n.', 'A pulmonic medicine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulmonifera', 'n. pl.', 'Same as Pulmonata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulmoniferous', 'a.', 'Having lungs; pulmonate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulp', 'n.', 'A moist, slightly cohering mass, consisting of soft,\n   undissolved animal or vegetable matter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulp', 'n.', 'A tissue or part resembling pulp; especially, the soft,\n   highly vascular and sensitive tissue which fills the central cavity,\n   called the pulp cavity, of teeth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulp', 'n.', 'The soft, succulent part of fruit; as, the pulp of a grape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulp', 'n.', 'The exterior part of a coffee berry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulp', 'n.', 'The material of which paper is made when ground up and\n   suspended in water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulped', 'imp. & p. p.', 'of Pulp');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulping', 'p. pr. & vb. n.', 'of Pulp');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulp', 'v. t.', 'To reduce to pulp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulp', 'v. t.', 'To deprive of the pulp, or integument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulpatoon', 'n.', 'A kind of delicate confectionery or cake, perhaps made\n   from the pulp of fruit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulpiness', 'n.', 'the quality or state of being pulpy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulpit', 'n.', 'An elevated place, or inclosed stage, in a church, in which\n   the clergyman stands while preaching.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulpit', 'n.', 'The whole body of the clergy; preachers as a class; also,\n   preaching.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulpit', 'n.', 'A desk, or platform, for an orator or public speaker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulpit', 'a.', 'Of or pertaining to the pulpit, or preaching; as, a pulpit\n   orator; pulpit eloquence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulpited', 'a.', 'Placed in a pulpit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulpiteer', 'n.', 'One who speaks in a pulpit; a preacher; -- so called in\n   contempt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulpiter', 'n.', 'A preacher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulpitical', 'a.', 'Of or pertaining to the pulpit; suited to the pulpit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulpitish', 'a.', 'Of or pertaining to the pulpit; like preaching.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulpitry', 'n.', 'The teaching of the pulpit; preaching.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulpous', 'a.', 'Containing pulp; pulpy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulpy', 'n.', 'Like pulp; consisting of pulp; soft; fleshy; succulent; as,\n   the pulpy covering of a nut; the pulpy substance of a peach or a\n   cherry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulque', 'n.', 'An intoxicating Mexican drink. See Agave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulsated', 'imp. & p. p.', 'of Pulsate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulsating', 'p. pr. & vb. n.', 'of Pulsate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulsate', 'v.', 'To throb, as a pulse; to beat, as the heart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulsatile', 'a.', 'Capable of being struck or beaten; played by beating or\n   by percussion; as, a tambourine is a pulsatile musical instrument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulsatile', 'a.', 'Pulsating; throbbing, as a tumor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulsatilla', 'n.', 'A genus of ranunculaceous herbs including the pasque\n   flower. This genus is now merged in Anemone. Some species, as Anemone\n   Pulsatilla, Anemone pratensis, and Anemone patens, are used\n   medicinally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulsation', 'n.', 'A beating or throbbing, especially of the heart or of an\n   artery, or in an inflamed part; a beat of the pulse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulsation', 'n.', 'A single beat or throb of a series.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulsation', 'n.', 'A stroke or impulse by which some medium is affected, as\n   in the propagation of sounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulsation', 'n.', 'Any touching of anothers body willfully or in anger.\n   This constitutes battery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulsative', 'a.', 'Beating; throbbing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulsator', 'n.', 'A beater; a striker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulsator', 'n.', 'That which beats or throbs in working.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulsatory', 'a.', 'Capable of pulsating; throbbing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulse', 'n.', 'Leguminous plants, or their seeds, as beans, pease, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulse', 'n.', 'The beating or throbbing of the heart or blood vessels,\n   especially of the arteries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulse', 'n.', 'Any measured or regular beat; any short, quick motion,\n   regularly repeated, as of a medium in the transmission of light, sound,\n   etc.; oscillation; vibration; pulsation; impulse; beat; movement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulse', 'v. i.', 'To beat, as the arteries; to move in pulses or beats; to\n   pulsate; to throb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulse', 'v. t.', 'To drive by a pulsation; to cause to pulsate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulseless', 'a.', 'Having no pulsation; lifeless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulselessness', 'n.', 'The state of being pulseless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulsific', 'a.', 'Exciting the pulse; causing pulsation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulsimeter', 'n.', 'A sphygmograph.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulsion', 'n.', 'The act of driving forward; propulsion; -- opposed to\n   suction or traction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulsive', 'a.', 'Tending to compel; compulsory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulsometer', 'n.', 'A device, with valves, for raising water by steam,\n   partly by atmospheric pressure, and partly by the direct action of the\n   steam on the water, without the intervention of a piston; -- also\n   called vacuum pump.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulsometer', 'n.', 'A pulsimeter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pult', 'v. t.', 'To put.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pultaceous', 'a.', 'Macerated; softened; nearly fluid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pultesse', 'n.', 'Alt. of Pultise');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pultise', 'n.', 'Poultry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulu', 'n.', 'A vegetable substance consisting of soft, elastic, yellowish\n   brown chaff, gathered in the Hawaiian Islands from the young fronds of\n   free ferns of the genus Cibotium, chiefly C. Menziesii; -- used for\n   stuffing mattresses, cushions, etc., and as an absorbent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purverable', 'a.', 'Capable of being reduced to fine powder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulveraceous', 'a.', 'Having a finely powdered surface; pulverulent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulverate', 'v. t.', 'To beat or reduce to powder or dust; to pulverize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulverine', 'n.', 'Ashes of barilla.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulverizable', 'a.', 'Admitting of being pulverized; pulverable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulverization', 'n.', 'The action of reducing to dust or powder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulverized', 'imp. & p. p.', 'of Pulverize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulverizing', 'p. pr. & vb. n.', 'of Pulverize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulverize', 'v. t.', 'To reduce of fine powder or dust, as by beating,\n   grinding, or the like; as, friable substances may be pulverized by\n   grinding or beating, but to pulverize malleable bodies other methods\n   must be pursued.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulverize', 'v. i.', 'To become reduced to powder; to fall to dust; as, the\n   stone pulverizes easily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulverizer', 'n.', 'One who, or that which, pulverizes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulverous', 'a.', 'Consisting of dust or powder; like powder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulverulence', 'n.', 'The state of being pulverulent; abundance of dust or\n   powder; dustiness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulverulent', 'a.', 'Consisting of, or reducible to, fine powder; covered\n   with dust or powder; powdery; dusty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulvil', 'n.', 'A sweet-scented powder; pulvillio.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulvil', 'v. t.', 'To apply pulvil to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulvillio', 'n.', 'Alt. of Pulvillo');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulvillo', 'n.', 'A kind of perfume in the form of a powder, formerly much\n   used, -- often in little bags.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulvilli', 'pl. ', 'of Pulvillus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulvillus', 'n.', 'One of the minute cushions on the feet of certain\n   insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulvinar', 'n.', 'A prominence on the posterior part of the thalamus of the\n   human brain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulvinate', 'a.', 'Alt. of Pulvinated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulvinated', 'a.', 'Curved convexly or swelled; as, a pulvinated frieze.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulvinated', 'a.', 'Having the form of a cushion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulvinic', 'a.', 'Pertaining to, or designating, an acid obtained by the\n   decomposition of vulpinic acid, as a white crystalline substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulvinuli', 'pl. ', 'of Pulvinulus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pulvinulus', 'n.', 'Same as Pulvillus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puma', 'n.', 'A large American carnivore (Felis concolor), found from\n   Canada to Patagonia, especially among the mountains. Its color is\n   tawny, or brownish yellow, without spots or stripes. Called also\n   catamount, cougar, American lion, mountain lion, and panther or\n   painter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pume', 'n.', 'A stint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pumicated', 'imp. & p. p.', 'of Pumicate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pumicating', 'p. pr. & vb. n.', 'of Pumicate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pumicate', 'v. t.', 'To make smooth with pumice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pumice', 'n.', 'A very light porous volcanic scoria, usually of a gray\n   color, the pores of which are capillary and parallel, giving it a\n   fibrous structure. It is supposed to be produced by the disengagement\n   of watery vapor without liquid or plastic lava. It is much used, esp.\n   in the form of powder, for smoothing and polishing. Called also pumice\n   stone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pumiced', 'a.', 'Affected with a kind of chronic laminitis in which there\n   is a growth of soft spongy horn between the coffin bone and the hoof\n   wall. The disease is called pumiced foot, or pumice foot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pumiceous', 'a.', 'Of or pertaining to pumice; resembling pumice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pumice stone', '', 'Same as Pumice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pumiciform', 'a.', 'Resembling, or having the structure of, pumice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pummace', 'n.', 'Same as Pomace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pummel', 'n. & v. t.', 'Same as Pommel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pump', 'n.', 'A low shoe with a thin sole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pump', 'n.', 'An hydraulic machine, variously constructed, for raising or\n   transferring fluids, consisting essentially of a moving piece or piston\n   working in a hollow cylinder or other cavity, with valves properly\n   placed for admitting or retaining the fluid as it is drawn or driven\n   through them by the action of the piston.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pumped', 'imp. & p. p.', 'of Pump');
  INSERT INTO words (word, word_type, description) VALUES  (    'pumping', 'p. pr. & vb. n.', 'of Pump');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pump', 'v. t.', 'To raise with a pump, as water or other liquid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pump', 'v. t.', 'To draw water, or the like, from; to from water by means\n   of a pump; as, they pumped the well dry; to pump a ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pump', 'v. t.', 'Figuratively, to draw out or obtain, as secrets or money,\n   by persistent questioning or plying; to question or ply persistently in\n   order to elicit something, as information, money, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pump', 'v. i.', 'To work, or raise water, a pump.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pumpage', 'n.', 'That which is raised by pumps, or the work done by pumps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pumper', 'n.', 'One who pumps; the instrument or machine used in pumping.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pumpernickel', 'n.', 'A sort of bread, made of unbolted rye, which forms\n   the chief food of the Westphalian peasants. It is acid but nourishing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pumpet', 'n.', 'A pompet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pumping', '', 'a. & n. from pump.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pumpion', 'n.', 'See Pumpkin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pumpkin', 'n.', 'A well-known trailing plant (Cucurbita pepo) and its\n   fruit, -- used for cooking and for feeding stock; a pompion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pumy', 'a.', 'Large and rounded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pun', 'v. t.', 'To pound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pun', 'n.', 'A play on words which have the same sound but different\n   meanings; an expression in which two different applications of a word\n   present an odd or ludicrous idea; a kind of quibble or equivocation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Punned', 'imp. & p. p.', 'of Pun');
  INSERT INTO words (word, word_type, description) VALUES  (    'Punning', 'p. pr. & vb. n.', 'of Pun');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pun', 'v. i.', 'To make puns, or a pun; to use a word in a double sense,\n   especially when the contrast of ideas is ludicrous; to play upon words;\n   to quibble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pun', 'v. t.', 'To persuade or affect by a pun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Punch', 'n.', 'A beverage composed of wine or distilled liquor, water (or\n   milk), sugar, and the juice of lemon, with spice or mint; --\n   specifically named from the kind of spirit used; as rum punch, claret\n   punch, champagne punch, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Punch', 'n.', 'The buffoon or harlequin of a puppet show.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Punch', 'n.', 'A short, fat fellow; anything short and thick.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Punch', 'n.', 'One of a breed of large, heavy draught horses; as, the\n   Suffolk punch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Punch', 'v. t.', 'To thrust against; to poke; as, to punch one with the end\n   of a stick or the elbow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Punch', 'n.', 'A thrust or blow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Punch', 'n.', 'A tool, usually of steel, variously shaped at one end for\n   different uses, and either solid, for stamping or for perforating holes\n   in metallic plates and other substances, or hollow and sharpedged, for\n   cutting out blanks, as for buttons, steel pens, jewelry, and the like;\n   a die.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Punch', 'n.', 'An extension piece applied to the top of a pile; a dolly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Punch', 'n.', 'A prop, as for the roof of a mine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Punched', 'imp. & p. p.', 'of Punch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Punching', 'p. pr. & vb. n.', 'of Punch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Punch', 'n.', 'To perforate or stamp with an instrument by pressure, or a\n   blow; as, to punch a hole; to punch ticket.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puncheon', 'n.', 'A figured stamp, die, or punch, used by goldsmiths,\n   cutlers, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puncheon', 'n.', 'A short, upright piece of timber in framing; a short\n   post; an intermediate stud.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puncheon', 'n.', 'A split log or heavy slab with the face smoothed; as, a\n   floor made of puncheons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puncheon', 'n.', 'A cask containing, sometimes 84, sometimes 120, gallons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puncher', 'n.', 'One who, or that which, punches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Punchin', 'n.', 'See Puncheon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Punchinello', 'n.', 'A punch; a buffoon; originally, in a puppet show, a\n   character represented as fat, short, and humpbacked.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Punchy', 'a.', 'Short and thick, or fat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Punctated', 'a.', 'Alt. of Punctated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Punctated', 'a.', 'Pointed; ending in a point or points.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Punctated', 'a.', 'Dotted with small spots of color, or with minute\n   depressions or pits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Punctator', 'n.', 'One who marks with points. specifically, one who writes\n   Hebrew with points; -- applied to a Masorite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puncticular', 'a.', 'Comprised in, or like, a point; exact.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Punctiform', 'a.', 'Having the form of a point.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Punctilios', 'pl. ', 'of Punctilio');
  INSERT INTO words (word, word_type, description) VALUES  (    'Punctilio', 'n.', 'A nice point of exactness in conduct, ceremony, or\n   proceeding; particularity or exactness in forms; as, the punctilios of\n   a public ceremony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Punctilious', 'a.', 'Attentive to punctilio; very nice or exact in the\n   forms of behavior, etiquette, or mutual intercourse; precise; exact in\n   the smallest particulars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Punction', 'n.', 'A puncturing, or pricking; a puncture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Punctist', 'n.', 'A punctator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puncto', 'n.', 'A nice point of form or ceremony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puncto', 'n.', 'A term applied to the point in fencing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Punctual', 'a.', 'Consisting in a point; limited to a point; unextended.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Punctual', 'a.', 'Observant of nice points; punctilious; precise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Punctual', 'a.', 'Appearing or done at, or adhering exactly to, a regular\n   or an appointed time; precise; prompt; as, a punctual man; a punctual\n   payment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Punctualist', 'n.', 'One who is very exact in observing forms and\n   ceremonies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Punctuality', 'n.', 'The quality or state of being punctual; especially,\n   adherence to the exact time of an engagement; exactness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Punctually', 'adv.', 'In a punctual manner; promptly; exactly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Punctualness', 'n.', 'Punctuality; exactness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Punctuated', 'imp. & p. p.', 'of Punctuate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Punctuating', 'p. pr. & vb. n.', 'of Punctuate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Punctuate', 'v. t.', 'To mark with points; to separate into sentences,\n   clauses, etc., by points or stops which mark the proper pauses in\n   expressing the meaning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Punctuation', 'n.', 'The act or art of punctuating or pointing a writing or\n   discourse; the art or mode of dividing literary composition into\n   sentences, and members of a sentence, by means of points, so as to\n   elucidate the authors meaning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Punctuative', 'a.', 'Of or belonging to points of division; relating to\n   punctuation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Punctuator', 'n.', 'One who punctuates, as in writing; specifically, a\n   punctator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Punctuist', 'n.', 'A punctator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Punctulate', 'a.', 'Alt. of Punctulated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Punctulated', 'a.', 'Marked with small spots.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Punctum', 'n.', 'A point.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puncturation', 'n.', 'The act or process of puncturing. See Acupuncture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puncture', 'n.', 'The act of puncturing; perforating with something\n   pointed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puncture', 'n.', 'A small hole made by a point; a slight wound, bite, or\n   sting; as, the puncture of a nail, needle, or pin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Punctured', 'imp. & p. p.', 'of Puncture');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puncturing', 'p. pr. & vb. n.', 'of Puncture');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puncture', 'v. t.', 'To pierce with a small, pointed instrument, or the\n   like; to prick; to make a puncture in; as, to puncture the skin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Punctured', 'a.', 'Having the surface covered with minute indentations or\n   dots.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Punctured', 'a.', 'Produced by puncture; having the characteristics of a\n   puncture; as, a punctured wound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pundit', 'n.', 'A learned man; a teacher; esp., a Brahman versed in the\n   Sanskrit language, and in the science, laws, and religion of the\n   Hindoos; in Cashmere, any clerk or native official.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pundle', 'n.', 'A short and fat woman; a squab.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Punese', 'n.', 'A bedbug.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pung', 'n.', 'A kind of plain sleigh drawn by one horse; originally, a rude\n   oblong box on runners.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pungence', 'n.', 'Pungency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pungency', 'n.', 'The quality or state of being pungent or piercing;\n   keenness; sharpness; piquancy; as, the pungency of ammonia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pungent', 'v. t.', 'Causing a sharp sensation, as of the taste, smell, or\n   feelings; pricking; biting; acrid; as, a pungent spice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pungent', 'v. t.', 'Sharply painful; penetrating; poignant; severe;\n   caustic; stinging.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pungent', 'v. t.', 'Prickly-pointed; hard and sharp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pungently', 'adv.', 'In a pungent manner; sharply.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pungled', 'a.', 'Shriveled or shrunken; -- said especially of grain which\n   has lost its juices from the ravages of insects, such as the wheat\n   midge, or Trips (Thrips cerealium).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pungy', 'n.', 'A small sloop or shallop, or a large boat with sails.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Punic', 'a.', 'Of or pertaining to the ancient Carthaginians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Punic', 'a.', 'Characteristic of the ancient Carthaginians; faithless;\n   treacherous; as, Punic faith.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Punice', 'n.', 'See Punese.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Punice', 'v. t.', 'To punish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puniceous', 'a.', 'Alt. of Punicial');
  INSERT INTO words (word, word_type, description) VALUES  (    'Punicial', 'a.', 'Of a bright red or purple color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puniness', 'n.', 'The quality or state of being puny; littleness;\n   pettiness; feebleness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Punished', 'imp. & p. p.', 'of Punish');
  INSERT INTO words (word, word_type, description) VALUES  (    'Punishing', 'p. pr. & vb. n.', 'of Punish');
  INSERT INTO words (word, word_type, description) VALUES  (    'Punish', 'v. t.', 'To impose a penalty upon; to afflict with pain, loss, or\n   suffering for a crime or fault, either with or without a view to the\n   offenders amendment; to cause to suffer in retribution; to chasten;\n   as, to punish traitors with death; a father punishes his child for\n   willful disobedience.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Punish', 'v. t.', 'To inflict a penalty for (an offense) upon the offender;\n   to repay, as a fault, crime, etc., with pain or loss; as, to punish\n   murder or treason with death.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Punish', 'v. t.', 'To injure, as by beating; to pommel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Punishable', 'a.', 'Deserving of, or liable to, punishment; capable of\n   being punished by law or right; -- said of person or offenses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Punisher', 'n.', 'One who inflicts punishment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Punishment', 'n.', 'The act of punishing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Punishment', 'n.', 'Any pain, suffering, or loss inflicted on a person\n   because of a crime or offense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Punishment', 'n.', 'A penalty inflicted by a court of justice on a\n   convicted offender as a just retribution, and incidentally for the\n   purposes of reformation and prevention.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Punition', 'n.', 'Punishment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Punitive', 'a.', 'Of or pertaining to punishment; involving, awarding, or\n   inflicting punishment; as, punitive law or justice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Punitory', 'a.', 'Punishing; tending to punishment; punitive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Punk', 'n.', 'Wood so decayed as to be dry, crumbly, and useful for tinder;\n   touchwood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Punk', 'n.', 'A fungus (Polyporus fomentarius, etc.) sometimes dried for\n   tinder; agaric.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Punk', 'n.', 'An artificial tinder. See Amadou, and Spunk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Punk', 'n.', 'A prostitute; a strumpet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Punka', 'n.', 'A machine for fanning a room, usually a movable fanlike\n   frame covered with canvas, and suspended from the ceiling. It is kept\n   in motion by pulling a cord.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Punkin', 'n.', 'A pumpkin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Punkling', 'n.', 'A young strumpet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Punner', 'n.', 'A punster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Punnet', 'n.', 'A broad, shallow basket, for displaying fruit or flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Punnology', 'n.', 'The art or practice of punning; paronomasia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Punster', 'n.', 'One who puns, or is skilled in, or given to, punning; a\n   quibbler; a low wit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Punt', 'v. i.', 'To play at basset, baccara, faro. or omber; to gamble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Punt', 'n.', 'Act of playing at basset, baccara, faro, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Punt', 'n.', 'A flat-bottomed boat with square ends. It is adapted for use\n   in shallow waters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Punt', 'v. t.', 'To propel, as a boat in shallow water, by pushing with a\n   pole against the bottom; to push or propel (anything) with exertion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Punt', 'v. t.', 'To kick (the ball) before it touches the ground, when let\n   fall from the hands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Punt', 'n.', 'The act of punting the ball.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Punter', 'v. t.', 'One who punts; specifically, one who plays against the\n   banker or dealer, as in baccara and faro.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Punter', 'n.', 'One who punts a football; also, one who propels a punt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puntil', 'n.', 'Alt. of Puntel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puntel', 'n.', 'See Pontee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Punto', 'n.', 'A point or hit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Punty', 'n.', 'See Pontee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puny', 'superl.', 'Imperfectly developed in size or vigor; small and\n   feeble; inferior; petty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puny', 'n.', 'A youth; a novice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puoy', 'n.', 'Same as Poy, n., 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pup', 'n.', 'A young dog; a puppy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pup', 'n.', 'a young seal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pupped', 'imp. & p. p.', 'of Pup');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pupping', 'p. pr. & vb. n.', 'of Pup');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pup', 'v. i.', 'To bring forth whelps or young, as the female of the canine\n   species.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pup/', 'pl. ', 'of Pupa');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pupas', 'pl. ', 'of Pupa');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pupa', 'n.', 'Any insect in that stage of its metamorphosis which usually\n   immediately precedes the adult, or imago, stage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pupa', 'n.', 'A genus of air-breathing land snails having an elongated\n   spiral shell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pupal', 'a.', 'Of or pertaining to a pupa, or the condition of a pupa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pupate', 'v. i.', 'To become a pupa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pupation', 'n.', 'the act of becoming a pupa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pupe', 'n.', 'A pupa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pupelo', 'n.', 'Cider brandy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pupigerous', 'a.', 'Bearing or containing a pupa; -- said of dipterous\n   larvae which do not molt when the pupa is formed within them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pupil', 'n.', 'The aperture in the iris; the sight, apple, or black of the\n   eye. See the Note under Eye, and Iris.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pupil', 'n.', 'A youth or scholar of either sex under the care of an\n   instructor or tutor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pupil', 'n.', 'A person under a guardian; a ward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pupil', 'n.', 'A boy or a girl under the age of puberty, that is, under\n   fourteen if a male, and under twelve if a female.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pupilage', 'n.', 'The state of being a pupil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pupillarity', 'n.', 'The period before puberty, or from birth to fourteen\n   in males, and twelve in females.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pupillary', 'a.', 'Of or pertaining to a pupil or ward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pupillary', 'a.', 'Of or pertaining to the pupil of the eye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pupillometer', 'n.', 'An instrument for measuring the size of the pupil of\n   the pupil of the eye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pupipara', 'n. pl.', 'A division of Diptera in which the young are born in\n   a stage like the pupa. It includes the sheep tick, horse tick, and\n   other parasites. Called also Homaloptera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pupiparous', 'a.', 'Bearing, or containing, a pupa; -- said of the matured\n   larvae, or larval skins, of certain Diptera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pupiparous', 'a.', 'Of or pertaining to the Pupipara.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pupivora', 'n. pl.', 'A group of parasitic Hymenoptera, including the\n   ichneumon flies, which destroy the larvae and pupae of insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pupivorous', 'a.', 'Feeding on the pupae of insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puplican', 'n.', 'Publican.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puppet', 'n.', 'A small image in the human form; a doll.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puppet', 'n.', 'A similar figure moved by the hand or by a wire in a mock\n   drama; a marionette; a wooden actor in a play.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puppet', 'n.', 'One controlled in his action by the will of another; a\n   tool; -- so used in contempt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puppet', 'n.', 'The upright support for the bearing of the spindle in a\n   lathe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puppetish', 'a.', 'Resembling a puppet in appearance or action; of the\n   nature of a puppet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puppetman', 'n.', 'A master of a puppet show.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puppetry', 'n.', 'Action or appearance resembling that of a puppet, or\n   puppet show; hence, mere form or show; affectation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puppies', 'pl. ', 'of Puppy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puppy', 'n.', 'The young of a canine animal, esp. of the common dog; a\n   whelp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puppy', 'n.', 'A name of contemptuous reproach for a conceited and\n   impertinent person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puppied', 'imp. & p. p.', 'of Puppy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puppying', 'p. pr. & vb. n.', 'of Puppy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puppy', 'v. i.', 'To bring forth whelps; to pup.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puppyhood', 'n.', 'The time or state of being a puppy; the time of being\n   young and undisciplined.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puppyish', 'a.', 'Like a puppy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puppyism', 'n.', 'Extreme meanness, affectation, conceit, or impudence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purred', 'imp. & p. p.', 'of Pur');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purring', 'p. pr. & vb. n.', 'of Pur');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pur', 'v. i.', 'To utter a low, murmuring, continued sound, as a cat does\n   when pleased.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pur', 'v. t.', 'To signify or express by purring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pur', 'n.', 'The low, murmuring sound made by a cat to express contentment\n   or pleasure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purana', 'n.', 'One of a class of sacred Hindoo poetical works in the\n   Sanskrit language which treat of the creation, destruction, and\n   renovation of worlds, the genealogy and achievements of gods and\n   heroes, the reigns of the Manus, and the transactions of their\n   descendants. The principal Puranas are eighteen in number, and there\n   are the same number of supplementary books called Upa Puranas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puranic', 'a.', 'Pertaining to the Puranas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purbeck beds', '', 'The strata of the Purbeck stone, or Purbeck limestone,\n   belonging to the Oolitic group. See the Chart of Geology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purbeck stone', '', 'A limestone from the Isle of Purbeck in England.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purblind', 'a.', 'Wholly blind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purblind', 'a.', 'Nearsighted, or dim-sighted; seeing obscurely; as, a\n   purblind eye; a purblind mole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purcelane', 'n.', 'Purslane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purchasable', 'a.', 'Capable of being bought, purchased, or obtained for a\n   consideration; hence, venal; corrupt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purchased', 'imp. & p. p.', 'of Purchase');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purchasing', 'p. pr. & vb. n.', 'of Purchase');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purchase', 'v. t.', 'To pursue and obtain; to acquire by seeking; to gain,\n   obtain, or acquire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purchase', 'v. t.', 'To obtain by paying money or its equivalent; to buy\n   for a price; as, to purchase land, or a house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purchase', 'v. t.', 'To obtain by any outlay, as of labor, danger, or\n   sacrifice, etc.; as, to purchase favor with flattery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purchase', 'v. t.', 'To expiate by a fine or forfeit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purchase', 'v. t.', 'To acquire by any means except descent or inheritance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purchase', 'v. t.', 'To buy for a price.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purchase', 'v. t.', 'To apply to (anything) a device for obtaining a\n   mechanical advantage; to get a purchase upon, or apply a purchase to;\n   as, to purchase a cannon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purchase', 'v. i.', 'To put forth effort to obtain anything; to strive; to\n   exert ones self.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purchase', 'v. i.', 'To acquire wealth or property.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purchase', 'v. t.', 'The act of seeking, getting, or obtaining anything.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purchase', 'v. t.', 'The act of seeking and acquiring property.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purchase', 'v. t.', 'The acquisition of title to, or properly in, anything\n   for a price; buying for money or its equivalent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purchase', 'v. t.', 'That which is obtained, got, or acquired, in any\n   manner, honestly or dishonestly; property; possession; acquisition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purchase', 'v. t.', 'That which is obtained for a price in money or its\n   equivalent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purchase', 'v. t.', 'Any mechanical hold, or advantage, applied to the\n   raising or removing of heavy bodies, as by a lever, a tackle, capstan,\n   and the like; also, the apparatus, tackle, or device by which the\n   advantage is gained.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purchase', 'v. t.', 'Acquisition of lands or tenements by other means than\n   descent or inheritance, namely, by ones own act or agreement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purchaser', 'n.', 'One who purchases; one who acquires property for a\n   consideration, generally of money; a buyer; a vendee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purchaser', 'n.', 'One who acquires an estate in lands by his own act or\n   agreement, or who takes or obtains an estate by any means other than by\n   descent or inheritance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purdah', 'n.', 'A curtain or screen; also, a cotton fabric in blue and\n   white stripes, used for curtains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pure', 'superl.', 'Separate from all heterogeneous or extraneous matter;\n   free from mixture or combination; clean; mere; simple; unmixed; as,\n   pure water; pure clay; pure air; pure compassion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pure', 'superl.', 'Free from moral defilement or quilt; hence, innocent;\n   guileless; chaste; -- applied to persons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pure', 'superl.', 'Free from that which harms, vitiates, weakens, or\n   pollutes; genuine; real; perfect; -- applied to things and actions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pure', 'superl.', 'Ritually clean; fitted for holy services.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pure', 'superl.', 'Of a single, simple sound or tone; -- said of some\n   vowels and the unaspirated consonants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pured', 'a.', 'Purified; refined.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puree', 'n.', 'A dish made by boiling any article of food to a pulp and\n   rubbing it through a sieve; as, a puree of fish, or of potatoes;\n   especially, a soup the thickening of which is so treated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purely', 'adv.', 'In a pure manner (in any sense of the adjective).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purely', 'adv.', 'Nicely; prettily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pureness', 'n.', 'The state of being pure (in any sense of the adjective).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purfile', 'n.', 'A sort of ancient trimming of tinsel and thread for\n   womens gowns; -- called also bobbinwork.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purfle', 'v. t.', 'To decorate with a wrought or flowered border; to\n   embroider; to ornament with metallic threads; as, to purfle with blue\n   and white.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purfle', 'v. t.', 'To ornament with a bordure of emines, furs, and the\n   like; also, with gold studs or mountings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purfle', 'n.', 'Alt. of Purflew');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purflew', 'n.', 'A hem, border., or trimming, as of embroidered work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purflew', 'n.', 'A border of any heraldic fur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purfled', 'a.', 'Ornamented; decorated; esp., embroidered on the edges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purfling', 'n.', 'Ornamentation on the border of a thing; specifically, the\n   inlaid border of a musical instrument, as a violin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purgament', 'n.', 'That which is excreted; excretion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purgament', 'n.', 'A cathartic; a purgative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purgation', 'n.', 'The act of purging; the act of clearing, cleansing, or\n   putifying, by separating and carrying off impurities, or whatever is\n   superfluous; the evacuation of the bowels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purgation', 'n.', 'The clearing of ones self from a crime of which one was\n   publicly suspected and accused. It was either canonical, which was\n   prescribed by the canon law, the form whereof used in the spiritual\n   court was, that the person suspected take his oath that he was clear of\n   the matter objected against him, and bring his honest neighbors with\n   him to make oath that they believes he swore truly; or vulgar, which\n   was by fire or water ordeal, or by combat. See Ordeal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purgative', 'a.', 'Having the power or quality of purging; cathartic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purgative', 'n.', 'A purging medicine; a cathartic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purgatively', 'adv.', 'In a purgative manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purgatorial', 'a.', 'Alt. of Purgatorian');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purgatorian', 'a.', 'Of or pertaining to purgatory; expiatory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purgatorian', 'n.', 'One who holds to the doctrine of purgatory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purgatory', 'a.', 'Tending to cleanse; cleansing; expiatory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purgatory', 'n.', 'A state or place of purification after death; according\n   to the Roman Catholic creed, a place, or a state believed to exist\n   after death, in which the souls of persons are purified by expiating\n   such offenses committed in this life as do not merit eternal damnation,\n   or in which they fully satisfy the justice of God for sins that have\n   been forgiven. After this purgation from the impurities of sin, the\n   souls are believed to be received into heaven.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purged', 'imp. & p. p.', 'of Purge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purging', 'p. pr. & vb. n.', 'of Purge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purge', 'v. t.', 'To cleanse, clear, or purify by separating and carrying\n   off whatever is impure, heterogeneous, foreign, or superfluous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purge', 'v. t.', 'To operate on as, or by means of, a cathartic medicine,\n   or in a similar manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purge', 'v. t.', 'To clarify; to defecate, as liquors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purge', 'v. t.', 'To clear of sediment, as a boiler, or of air, as a steam\n   pipe, by driving off or permitting escape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purge', 'v. t.', 'To clear from guilt, or from moral or ceremonial\n   defilement; as, to purge one of guilt or crime.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purge', 'v. t.', 'To clear from accusation, or the charge of a crime or\n   misdemeanor, as by oath or in ordeal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purge', 'v. t.', 'To remove in cleansing; to deterge; to wash away; --\n   often followed by away.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purge', 'v. i.', 'To become pure, as by clarification.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purge', 'v. i.', 'To have or produce frequent evacuations from the\n   intestines, as by means of a cathartic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purge', 'v. t.', 'The act of purging.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purge', 'v. t.', 'That which purges; especially, a medicine that evacuates\n   the intestines; a cathartic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purger', 'n.', 'One who, or that which, purges or cleanses; especially, a\n   cathartic medicine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purgery', 'n.', 'The part of a sugarhouse where the molasses is drained off\n   from the sugar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purging', 'a.', 'That purges; cleansing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purging', 'n.', 'The act of cleansing; excessive evacuations; especially,\n   diarrhea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puri', 'n.', 'See Euxanthin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purification', 'n.', 'The act of purifying; the act or operation of\n   separating and removing from anything that which is impure or noxious,\n   or heterogeneous or foreign to it; as, the purification of liquors, or\n   of metals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purification', 'n.', 'The act or operation of cleansing ceremonially, by\n   removing any pollution or defilement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purification', 'n.', 'A cleansing from guilt or the pollution of sin; the\n   extinction of sinful desires, appetites, and inclinations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purificative', 'a.', 'Having power to purify; tending to cleanse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purificator', 'n.', 'One who, or that which, purifies; a purifier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purrificatory', 'a.', 'Serving or tending to purify; purificative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purifier', 'n.', 'One who, or that which, purifies or cleanses; a cleanser;\n   a refiner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puriform', 'a.', 'In the form of pus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purified', 'imp. & p. p.', 'of Purify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purifying', 'p. pr. & vb. n.', 'of Purify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purify', 'v. t.', 'To make pure or clear from material defilement,\n   admixture, or imperfection; to free from extraneous or noxious matter;\n   as, to purify liquors or metals; to purify the blood; to purify the\n   air.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purify', 'v. t.', 'Hence, in figurative uses: (a) To free from guilt or\n   moral defilement; as, to purify the heart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purify', 'v. t.', 'To free from ceremonial or legal defilement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purify', 'v. t.', 'To free from improprieties or barbarisms; as, to purify\n   a language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purify', 'v. i.', 'To grow or become pure or clear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purim', 'n.', 'A Jewish festival, called also the Feast of Lots, instituted\n   to commemorate the deliverance of the Jews from the machinations of\n   Haman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purism', 'n.', 'Rigid purity; the quality of being affectedly pure or nice,\n   especially in the choice of language; over-solicitude as to purity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purist', 'n.', 'One who aims at excessive purity or nicety, esp. in the\n   choice of language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purist', 'n.', 'One who maintains that the New Testament was written in\n   pure Greek.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puristic', 'a.', 'Alt. of Puristical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puristical', 'a.', 'Of or pertaining to purists or purism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puritan', 'n.', 'One who, in the time of Queen Elizabeth and the first two\n   Stuarts, opposed traditional and formal usages, and advocated simpler\n   forms of faith and worship than those established by law; --\n   originally, a term of reproach. The Puritans formed the bulk of the\n   early population of New England.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puritan', 'n.', 'One who is scrupulous and strict in his religious life; --\n   often used reproachfully or in contempt; one who has overstrict\n   notions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puritan', 'a.', 'Of or pertaining to the Puritans; resembling, or\n   characteristic of, the Puritans.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puritanic', 'a.', 'Alt. of Puritanical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puritanical', 'a.', 'Of or pertaining to the Puritans, or to their\n   doctrines and practice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puritanical', 'a.', 'Precise in observance of legal or religious\n   requirements; strict; overscrupulous; rigid; -- often used by way of\n   reproach or contempt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puritanically', 'adv.', 'In a puritanical manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puritanism', 'n.', 'The doctrines, notions, or practice of Puritans.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puritanized', 'imp. & p. p.', 'of Puritanize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puritanizing', 'p. pr. & vb. n.', 'of Puritanize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puritanize', 'v. i.', 'To agree with, or teach, the doctrines of Puritans;\n   to conform to the practice of Puritans.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purity', 'n.', 'The condition of being pure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purity', 'n.', 'freedom from foreign admixture or deleterious matter; as,\n   the purity of water, of wine, of drugs, of metals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purity', 'n.', 'Cleanness; freedom from foulness or dirt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purity', 'n.', 'Freedom from guilt or the defilement of sin; innocence;\n   chastity; as, purity of heart or of life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purity', 'n.', 'Freedom from any sinister or improper motives or views.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purity', 'n.', 'Freedom from foreign idioms, or from barbarous or improper\n   words or phrases; as, purity of style.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purkinjes cells', '', 'Large ganglion cells forming a layer near the\n   surface of the cerebellum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purl', 'v. t.', 'To decorate with fringe or embroidery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purl', 'n.', 'An embroidered and puckered border; a hem or fringe, often of\n   gold or silver twist; also, a pleat or fold, as of a band.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purl', 'n.', 'An inversion of stitches in knitting, which gives to the work\n   a ribbed or waved appearance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purled', 'imp. & p. p.', 'of Purl');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purling', 'p. pr. & vb. n.', 'of Purl');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purl', 'v. i.', 'To run swiftly round, as a small stream flowing among\n   stones or other obstructions; to eddy; also, to make a murmuring sound,\n   as water does in running over or through obstructions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purl', 'v. & n.', 'To rise in circles, ripples, or undulations; to curl; to\n   mantle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purl', 'n.', 'A circle made by the notion of a fluid; an eddy; a ripple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purl', 'n.', 'A gentle murmur, as that produced by the running of a liquid\n   among obstructions; as, the purl of a brook.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purl', 'n.', 'Malt liquor, medicated or spiced; formerly, ale or beer in\n   which wormwood or other bitter herbs had been infused, and which was\n   regarded as tonic; at present, hot beer mixed with gin, sugar, and\n   spices.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purl', 'n.', 'A tern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purlieu', 'n.', 'Originally, the ground near a royal forest, which, having\n   been unlawfully added to the forest, was afterwards severed from it,\n   and disafforested so as to remit to the former owners their rights.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purlieu', 'n.', 'Hence, the outer portion of any place; an adjacent\n   district; environs; neighborhood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purlin', 'n.', 'Alt. of Purline');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purline', 'n.', 'In root construction, a horizontal member supported on the\n   principals and supporting the common rafters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purling', 'n.', 'The motion of a small stream running among obstructions;\n   also, the murmur it makes in so doing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purloined', 'imp. & p. p.', 'of Purloin');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purloining', 'p. pr. & vb. n.', 'of Purloin');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purloin', 'v. t.', 'To take or carry away for ones self; hence, to steal;\n   to take by theft; to filch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purloin', 'v. i.', 'To practice theft; to steal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purloiner', 'n.', 'One who purloins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purparty', 'n.', 'A share, part, or portion of an estate allotted to a\n   coparcener.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purples', 'pl. ', 'of Purple');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purple', 'n.', 'A color formed by, or resembling that formed by, a\n   combination of the primary colors red and blue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purple', 'n.', 'Cloth dyed a purple color, or a garment of such color;\n   especially, a purple robe, worn as an emblem of rank or authority;\n   specifically, the purple rode or mantle worn by Roman emperors as the\n   emblem of imperial dignity; as, to put on the imperial purple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purple', 'n.', 'Hence: Imperial sovereignty; royal rank, dignity, or favor;\n   loosely and colloquially, any exalted station; great wealth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purple', 'n.', 'A cardinalate. See Cardinal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purple', 'n.', 'Any species of large butterflies, usually marked with\n   purple or blue, of the genus Basilarchia (formerly Limenitis) as, the\n   banded purple (B. arthemis). See Illust. under Ursula.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purple', 'n.', 'Any shell of the genus Purpura.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purple', 'n.', 'See Purpura.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purple', 'n.', 'A disease of wheat. Same as Earcockle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purple', 'a.', 'Exhibiting or possessing the color called purple, much\n   esteemed for its richness and beauty; of a deep red, or red and blue\n   color; as, a purple robe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purple', 'a.', 'Imperial; regal; -- so called from the color having been an\n   emblem of imperial authority.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purple', 'a.', 'Blood-red; bloody.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purpled', 'imp. & p. p.', 'of Purple');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purpling', 'p. pr. & vb. n.', 'of Purple');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purple', 'v. t.', 'To make purple; to dye of purple or deep red color; as,\n   hands purpled with blood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purpleheart', 'n.', 'A strong, durable, and elastic wood of a purplish\n   color, obtained from several tropical American leguminous trees of the\n   genus Copaifera (C. pubiflora, bracteata, and officinalis). Used for\n   decorative veneering. See Copaiba.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purplewood', 'n.', 'Same as Purpleheart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purplish', 'a.', 'Somewhat purple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purport', 'n.', 'Design or tendency; meaning; import; tenor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purport', 'n.', 'Disguise; covering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purported', 'imp. & p. p.', 'of Purport');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purporting', 'p. pr. & vb. n.', 'of Purport');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purport', 'n.', 'To intend to show; to intend; to mean; to signify; to\n   import; -- often with an object clause or infinitive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purportless', 'a.', 'Without purport or meaning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purpose', 'n.', 'That which a person sets before himself as an object to be\n   reached or accomplished; the end or aim to which the view is directed\n   in any plan, measure, or exertion; view; aim; design; intention; plan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purpose', 'n.', 'Proposal to another; discourse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purpose', 'n.', 'Instance; example.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purposed', 'imp. & p. p.', 'of Purpose');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purposing', 'p. pr. & vb. n.', 'of Purpose');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purpose', 'v. t.', 'To set forth; to bring forward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purpose', 'v. t.', 'To propose, as an aim, to ones self; to determine\n   upon, as some end or object to be accomplished; to intend; to design;\n   to resolve; -- often followed by an infinitive or dependent clause.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purpose', 'v. i.', 'To have a purpose or intention; to discourse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purposedly', 'adv.', 'In a purposed manner; according to purpose or design;\n   purposely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purposeful', 'a.', 'Important; material.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purposeless', 'a.', 'Having no purpose or result; objectless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purposely', 'adv.', 'With purpose or design; intentionally; with\n   predetermination; designedly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purposer', 'n.', 'One who brings forward or proposes anything; a proposer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purposer', 'n.', 'One who forms a purpose; one who intends.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purposive', 'a.', 'Having or indicating purpose or design.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purpre', 'n. & a.', 'Purple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purpresture', 'n.', 'Wrongful encroachment upon anothers property; esp.,\n   any encroachment upon, or inclosure of, that which should be common or\n   public, as highways, rivers, harbors, forts, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purprise', 'n.', 'A close or inclosure; the compass of a manor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purpura', 'n.', 'A disease characterized by livid spots on the skin from\n   extravasated blood, with loss of muscular strength, pain in the limbs,\n   and mental dejection; the purples.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purpura', 'n.', 'A genus of marine gastropods, usually having a rough and\n   thick shell. Some species yield a purple dye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purpurate', 'a.', 'Of or pertaining to purpura.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purpurate', 'n.', 'A salt of purpuric acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purpure', 'n.', 'Purple, -- represented in engraving by diagonal lines\n   declining from the right top to the left base of the escutcheon (or\n   from sinister chief to dexter base).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purpureal', 'a.', 'Of a purple color; purple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purpureo-', '', 'A combining form signifying of a purple or purple-red\n   color. Specif. (Chem.), used in designating certain brilliant\n   purple-red compounds of cobaltic chloride and ammonia, similar to the\n   roseocobaltic compounds. See Cobaltic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purpuric', 'a.', 'Of or pertaining to purpura.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purpuric', 'a.', 'Pertaining to or designating, a nitrogenous acid\n   contained in uric acid. It is not known in the pure state, but forms\n   well-known purple-red compounds (as murexide), whence its name.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purpurin', 'n.', 'A dyestuff resembling alizarin, found in madder root, and\n   extracted as an orange or red crystalline substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purpuriparous', 'a.', 'Producing, or connected with, a purple-colored\n   secretion; as, the purpuriparous gland of certain gastropods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purpurogenous', 'a.', 'Having the power to produce a purple color; as, the\n   purpurogenous membrane, or choroidal epithelium, of the eye. See Visual\n   purple, under Visual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purr', 'v. i. & t.', 'To murmur as a cat. See Pur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purr', 'n.', 'The low murmuring sound made by a cat; pur. See Pur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purre', 'n.', 'The dunlin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purree', 'n.', 'A yellow coloring matter. See Euxanthin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purrock', 'n.', 'See Puddock, and Parrock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purse', 'n.', 'A small bag or pouch, the opening of which is made to draw\n   together closely, used to carry money in; by extension, any receptacle\n   for money carried on the person; a wallet; a pocketbook; a\n   portemonnaie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purse', 'n.', 'Hence, a treasury; finances; as, the public purse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purse', 'n.', 'A sum of money offered as a prize, or collected as a\n   present; as, to win the purse; to make up a purse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purse', 'n.', 'A specific sum of money');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purse', 'n.', 'In Turkey, the sum of 500 piasters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purse', 'n.', 'In Persia, the sum of 50 tomans.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pursed', 'imp. & p. p.', 'of Purse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pursing', 'p. pr. & vb. n.', 'of Purse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purse', 'v. t.', 'To put into a purse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purse', 'v. t.', 'To draw up or contract into folds or wrinkles, like the\n   mouth of a purse; to pucker; to knit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purse', 'v. i.', 'To steal purses; to rob.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pursefuls', 'pl. ', 'of Purseful');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purseful', 'n.', 'All that is, or can be, contained in a purse; enough to\n   fill a purse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purse-proud', 'a.', 'Affected with purse pride; puffed up with the\n   possession of riches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purser', 'n.', 'A commissioned officer in the navy who had charge of the\n   provisions, clothing, and public moneys on shipboard; -- now called\n   paymaster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purser', 'n.', 'A clerk on steam passenger vessels whose duty it is to keep\n   the accounts of the vessels, such as the receipt of freight, tickets,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purser', 'n.', 'Colloquially, any paymaster or cashier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pursership', 'n.', 'The office of purser.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purset', 'n.', 'A purse or purse net.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pursiness', 'n.', 'State of being pursy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pursive', 'a.', 'Pursy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pursiveness', 'n.', 'Pursiness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purslain', 'n.', 'Same as Purslane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purslane', 'n.', 'An annual plant (Portulaca oleracea), with fleshy,\n   succulent, obovate leaves, sometimes used as a pot herb and for salads,\n   garnishing, and pickling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pursuable', 'a.', 'Capable of being, or fit to be, pursued, followed, or\n   prosecuted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pursual', 'n.', 'The act of pursuit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pursuance', 'n.', 'The act of pursuing or prosecuting; a following out or\n   after.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pursuance', 'n.', 'The state of being pursuant; consequence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pursuant', 'a.', 'Acting in consequence or in prosecution (of anything);\n   hence, agreeable; conformable; following; according; -- with to or of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pursuant', 'adv.', 'Alt. of Pursuantly');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pursuantly', 'adv.', 'Agreeably; conformably.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pursued', 'imp. & p. p.', 'of Pursue');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pursuing', 'p. pr. & vb. n.', 'of Pursue');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pursue', 'v. t.', 'To follow with a view to overtake; to follow eagerly, or\n   with haste; to chase; as, to pursue a hare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pursue', 'v. t.', 'To seek; to use or adopt measures to obtain; as, to\n   pursue a remedy at law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pursue', 'v. t.', 'To proceed along, with a view to some and or object; to\n   follow; to go in; as, Captain Cook pursued a new route; the\n   administration pursued a wise course.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pursue', 'v. t.', 'To prosecute; to be engaged in; to continue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pursue', 'v. t.', 'To follow as an example; to imitate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pursue', 'v. t.', 'To follow with enmity; to persecute; to call to account.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pursue', 'v. i.', 'To go in pursuit; to follow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pursue', 'v. i.', 'To go on; to proceed, especially in argument or\n   discourse; to continue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pursue', 'v. i.', 'To follow a matter judicially, as a complaining party;\n   to act as a prosecutor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pursuer', 'n.', 'One who pursues or chases; one who follows in haste, with\n   a view to overtake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pursuer', 'n.', 'A plaintiff; a prosecutor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pursuit', 'v. t.', 'The act of following or going after; esp., a following\n   with haste, either for sport or in hostility; chase; prosecution; as,\n   the pursuit of game; the pursuit of an enemy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pursuit', 'v. t.', 'A following with a view to reach, accomplish, or\n   obtain; endeavor to attain to or gain; as, the pursuit of knowledge;\n   the pursuit of happiness or pleasure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pursuit', 'v. t.', 'Course of business or occupation; continued employment\n   with a view to same end; as, mercantile pursuits; a literary pursuit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pursuit', 'v. t.', 'Prosecution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pursuivant', 'n.', 'A functionary of lower rank than a herald, but\n   discharging similar duties; -- called also pursuivant at arms; an\n   attendant of the heralds. Also used figuratively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pursuivant', 'n.', 'The kings messenger; a state messenger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pursuivant', 'v. t.', 'To pursue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pursy', 'a.', 'Fat and short-breathed; fat, short, and thick; swelled with\n   pampering; as, pursy insolence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purtenance', 'n.', 'That which pertains or belongs to something; esp., the\n   heard, liver, and lungs of an animal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purrulence', 'n.', 'Alt. of Purulency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purulency', 'n.', 'The quality or state of being purulent; the generation\n   of pus; also, the pus itself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purulent', 'a.', 'Consisting of pus, or matter; partaking of the nature of\n   pus; attended with suppuration; as, purulent inflammation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purulently', 'v.', 'In a purulent manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purveance', 'n.', 'Alt. of Purveiaunce');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purveiaunce', 'n.', 'Purveyance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purveyed', 'imp. & p. p.', 'of Purvey');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purveying', 'p. pr. & vb. n.', 'of Purvey');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purvey', 'v. t.', 'To furnish or provide, as with a convenience,\n   provisions, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purvey', 'v. t.', 'To procure; to get.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purvey', 'v. i.', 'To purchase provisions; to provide; to make provision.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purvey', 'v. i.', 'To pander; -- with to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purveyance', 'n.', 'The act or process of providing or procuring;\n   providence; foresight; preparation; management.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purveyance', 'n.', 'That which is provided; provisions; food.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purveyance', 'n.', 'A providing necessaries for the sovereign by buying\n   them at an appraised value in preference to all others, and oven\n   without the owners consent. This was formerly a royal prerogative, but\n   has long been abolished.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purveyor', 'n.', 'One who provides victuals, or whose business is to make\n   provision for the table; a victualer; a caterer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purveyor', 'n.', 'An officer who formerly provided, or exacted provision,\n   for the kings household.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purveyor', 'n.', 'a procurer; a pimp; a bawd.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purview', 'n.', 'The body of a statute, or that part which begins with \" Be\n   it enacted, \" as distinguished from the preamble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purview', 'n.', 'The limit or scope of a statute; the whole extent of its\n   intention or provisions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purview', 'n.', 'Limit or sphere of authority; scope; extent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pus', 'a.', 'The yellowish white opaque creamy matter produced by the\n   process of suppuration. It consists of innumerable white nucleated\n   cells floating in a clear liquid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pusane', 'n.', 'A piece of armor for the breast; often, an addition to, or\n   reenforcement of. the breastplate; -- called also pesane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puseyism', 'n.', 'The principles of Dr. Pusey and others at Oxford,\n   England, as exhibited in various publications, esp. in a series which\n   appeared from 1833 to 1841, designated \" Tracts for the Times;\"\n   tractarianism. See Tractarianism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puseyistic', 'a.', 'Alt. of Puseyite');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puseyite', 'a.', 'Of or pertaining to Puseyism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puseyite', 'n.', 'One who holds the principles of Puseyism; -- often used\n   opprobriously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Push', 'n.', 'A pustule; a pimple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pushed', 'imp. & p. p.', 'of Push');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pushing', 'p. pr. & vb. n.', 'of Push');
  INSERT INTO words (word, word_type, description) VALUES  (    'Push', 'v. t.', 'To press against with force; to drive or impel by\n   pressure; to endeavor to drive by steady pressure, without striking; --\n   opposed to draw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Push', 'v. t.', 'To thrust the points of the horns against; to gore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Push', 'v. t.', 'To press or urge forward; to drive; to push an objection\n   too far.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Push', 'v. t.', 'To bear hard upon; to perplex; to embarrass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Push', 'v. t.', 'To importune; to press with solicitation; to tease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Push', 'v. i.', 'To make a thrust; to shove; as, to push with the horns or\n   with a sword.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Push', 'v. i.', 'To make an advance, attack, or effort; to be energetic;\n   as, a man must push in order to succeed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Push', 'v. i.', 'To burst pot, as a bud or shoot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Push', 'n.', 'A thrust with a pointed instrument, or with the end of a\n   thing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Push', 'n.', 'Any thrust. pressure, impulse, or force, or force applied; a\n   shove; as, to give the ball the first push.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Push', 'n.', 'An assault or attack; an effort; an attempt; hence, the time\n   or occasion for action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Push', 'n.', 'The faculty of overcoming obstacles; aggressive energy; as,\n   he has push, or he has no push.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pusher', 'n.', 'One who, or that which, pushes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pushing', 'a.', 'Pressing forward in business; enterprising; driving;\n   energetic; also, forward; officious, intrusive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pushpin', 'n.', 'A childs game played with pins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pusil', 'a.', 'Very small; little; petty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pusillanimity', 'n.', 'The quality of being pusillanimous; weakness of\n   spirit; cowardliness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pusillanimous', 'a.', 'Destitute of a manly or courageous strength and\n   firmness of mind; of weak spirit; mean-spirited; spiritless; cowardly;\n   -- said of persons, as, a pussillanimous prince.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pusillanimous', 'a.', 'Evincing, or characterized by, weakness of mind, and\n   want of courage; feeble; as, pusillanimous counsels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pusillanimously', 'adv.', 'With pusillanimity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pusley', 'n.', 'Purslane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puss', 'n.', 'A cat; -- a fondling appellation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puss', 'n.', 'A hare; -- so called by sportsmen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pussy', 'n.', 'A pet name for a cat; also, an endearing name for a girl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pussy', 'n.', 'A catkin of the pussy willow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pussy', 'n.', 'The game of tipcat; -- also called pussy cat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pussy', 'a.', 'See Pursy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pustulant', 'v. t.', 'Producing pustules.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pustulant', 'n.', 'A medicine that produces pustules, as croton oil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pustular', 'a.', 'Of or pertaining to pustules; as, pustular prominences;\n   pustular eruptions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pustular', 'a.', 'Covered with pustulelike prominences; pustulate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pustulate', 'v. t.', 'To form into pustules, or blisters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pustulate', 'a.', 'Alt. of Pustulated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pustulated', 'a.', 'Covered with pustulelike prominences; pustular;\n   pustulous; as, a pustulate leaf; a pustulate shell or coral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pustulation', 'n.', 'The act of producing pustules; the state of being\n   pustulated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pustule', 'n.', 'A vesicle or an elevation of the cuticle with an inflamed\n   base, containing pus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pustulous', 'a.', 'Resembling, or covered with, pustules; pustulate;\n   pustular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Put', 'n.', 'A pit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Put', '', '3d pers. sing. pres. of Put, contracted from putteth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Put', 'n.', 'A rustic; a clown; an awkward or uncouth person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Put', 'imp. & p. p.', 'of Put');
  INSERT INTO words (word, word_type, description) VALUES  (    'Putting', 'p. pr. & vb. n.', 'of Put');
  INSERT INTO words (word, word_type, description) VALUES  (    'Put', 'v. t.', 'To move in any direction; to impel; to thrust; to push; --\n   nearly obsolete, except with adverbs, as with by (to put by = to thrust\n   aside; to divert); or with forth (to put forth = to thrust out).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Put', 'v. t.', 'To bring to a position or place; to place; to lay; to set;\n   figuratively, to cause to be or exist in a specified relation,\n   condition, or the like; to bring to a stated mental or moral condition;\n   as, to put one in fear; to put a theory in practice; to put an enemy to\n   fight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Put', 'v. t.', 'To attach or attribute; to assign; as, to put a wrong\n   construction on an act or expression.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Put', 'v. t.', 'To lay down; to give up; to surrender.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Put', 'v. t.', 'To set before one for judgment, acceptance, or rejection;\n   to bring to the attention; to offer; to state; to express;\n   figuratively, to assume; to suppose; -- formerly sometimes followed by\n   that introducing a proposition; as, to put a question; to put a case.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Put', 'v. t.', 'To incite; to entice; to urge; to constrain; to oblige.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Put', 'v. t.', 'To throw or cast with a pushing motion \"overhand,\" the hand\n   being raised from the shoulder; a practice in athletics; as, to put the\n   shot or weight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Put', 'v. t.', 'To convey coal in the mine, as from the working to the\n   tramway.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Put', 'v. i.', 'To go or move; as, when the air first puts up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Put', 'v. i.', 'To steer; to direct ones course; to go.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Put', 'v. i.', 'To play a card or a hand in the game called put.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Put', 'n.', 'The act of putting; an action; a movement; a thrust; a push;\n   as, the put of a ball.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Put', 'n.', 'A certain game at cards.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Put', 'n.', 'A privilege which one party buys of another to \"put\" (deliver)\n   to him a certain amount of stock, grain, etc., at a certain price and\n   date.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Put', 'n.', 'A prostitute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Putage', 'n.', 'Prostitution or fornication on the part of a woman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Putamen', 'n.', 'The shell of a nut; the stone of a drupe fruit. See\n   Endocarp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Putanism', 'n.', 'Habitual lewdness or prostitution of a woman; harlotry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Putative', 'a.', 'Commonly thought or deemed; supposed; reputed; as, the\n   putative father of a child.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Putchuck', 'n.', 'Same as Pachak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puteal', 'n.', 'An inclosure surrounding a well to prevent persons from\n   falling into it; a well curb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puteli', 'n.', 'Same as Patela.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Putery', 'n.', 'Putage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Putid', 'a.', 'Rotten; fetid; stinking; base; worthless. Jer. Taylor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Putidity', 'n.', 'Alt. of Putidness');
  INSERT INTO words (word, word_type, description) VALUES  (    'Putidness', 'n.', 'The quality or state of being putrid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Putlog', 'n.', 'One of the short pieces of timber on which the planks\n   forming the floor of a scaffold are laid, -- one end resting on the\n   ledger of the scaffold, and the other in a hole left in the wall\n   temporarily for the purpose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Put-off', 'n.', 'A shift for evasion or delay; an evasion; an excuse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Putour', 'n.', 'A keeper of a brothel; a procurer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Putredinous', 'a.', 'Proceeding from putrefaction, or partaking of the\n   putrefactive process; having an offensive smell; stinking; rotten.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Putrefaction', 'n.', 'The act or the process of putrefying; the offensive\n   decay of albuminous or other matter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Putrefaction', 'n.', 'The condition of being putrefied; also, that which\n   putrefied.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Putrefactive', 'a.', 'Of or pertaining to putrefaction; as, the\n   putrefactive smell or process.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Putrefactive', 'a.', 'Causing, or tending to promote, putrefaction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Putrefied', 'imp. & p. p.', 'of Putrefy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Putrefying', 'p. pr. & vb. n.', 'of Putrefy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Putrefy', 'v. t.', 'To render putrid; to cause to decay offensively; to\n   cause to be decomposed; to cause to rot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Putrefy', 'v. t.', 'To corrupt; to make foul.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Putrefy', 'v. t.', 'To make morbid, carious, or gangrenous; as, to putrefy\n   an ulcer or wound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Putrefy', 'v. i.', 'To become putrid; to decay offensively; to rot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Putrescence', 'n.', 'The state of being putrescent; putrescent matter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Putrescent', 'a.', 'Becoming putrid or rotten.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Putrescent', 'a.', 'Of or pertaining to the process of putrefaction; as, a\n   putrescent smell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Putrescible', 'a.', 'Capable of putrefaction; liable to become putrid; as,\n   putrescible substances.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Putrescible', 'n.', 'A substance, usually nitrogenous, which is liable to\n   undergo decomposition when in contact with air and moisture at ordinary\n   temperatures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Putrescin', 'n.', 'A nontoxic diamine, C4H12N2, formed in the putrefaction\n   of the flesh of mammals and some other animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Putrid', 'a.', 'Tending to decomposition or decay; decomposed; rotten; --\n   said of animal or vegetable matter; as, putrid flesh. See Putrefaction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Putrid', 'a.', 'Indicating or proceeding from a decayed state of animal or\n   vegetable matter; as, a putrid smell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Putridity', 'n.', 'The quality of being putrid; putrefaction; rottenness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Putridness', 'n.', 'Putridity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Putrifacted', 'a.', 'Putrefied.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Putrification', 'n.', 'Putrefaction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Putrify', 'v. t. & i.', 'To putrefy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Putrilage', 'n.', 'That which is undergoing putrefaction; the products of\n   putrefaction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Putry', 'a.', 'Putrid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Putry', 'n.', 'Putage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Putter', 'n.', 'One who puts or plates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Putter', 'n.', 'Specifically, one who pushes the small wagons in a coal\n   mine, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puttered', 'imp. & p. p.', 'of Putter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puttering', 'p. pr. & vb. n.', 'of Putter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Putter', 'v. i.', 'To act inefficiently or idly; to trifle; to potter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Putter-on', 'n.', 'An instigator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puttier', 'n.', 'One who putties; a glazier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Putting', 'n.', 'The throwing of a heavy stone, shot, etc., with the hand\n   raised or extended from the shoulder; -- originally, a Scottish game.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puttock', 'n.', 'The European kite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puttock', 'n.', 'The buzzard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puttock', 'n.', 'The marsh harrier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puttock', 'n.', 'See Futtock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Putty', 'n.', 'A kind of thick paste or cement compounded of whiting, or\n   soft carbonate of lime, and linseed oil, when applied beaten or kneaded\n   to the consistence of dough, -- used in fastening glass in sashes,\n   stopping crevices, and for similar purposes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puttied', 'imp. & p. p.', 'of Putty');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puttying', 'p. pr. & vb. n.', 'of Putty');
  INSERT INTO words (word, word_type, description) VALUES  (    'Putty', 'v. t.', 'To cement, or stop, with putty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Putty-faced', 'a.', 'White-faced; -- used contemptuously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puttyroot', 'n.', 'An American orchidaceous plant (Aplectrum hyemale) which\n   flowers in early summer. Its slender naked rootstock produces each year\n   a solid corm, filled with exceedingly glutinous matter, which sends up\n   later a single large oval evergreen plaited leaf. Called also\n   Adam-and-Eve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Put-up', 'a.', 'Arranged; plotted; -- in a bad sense; as, a put-up job.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puy', 'n.', 'See Poy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puzzel', 'n.', 'A harlot; a drab; a hussy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puzzle', 'v.', 'Something which perplexes or embarrasses; especially, a toy\n   or a problem contrived for testing ingenuity; also, something\n   exhibiting marvelous skill in making.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puzzle', 'v.', 'The state of being puzzled; perplexity; as, to be in a\n   puzzle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puzzled', 'imp. & p. p.', 'of Puzzle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puzzling', 'p. pr. & vb. n.', 'of Puzzle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puzzle', 'v. t.', 'To perplex; to confuse; to embarrass; to put to a stand;\n   to nonplus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puzzle', 'v. t.', 'To make intricate; to entangle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puzzle', 'v. t.', 'To solve by ingenuity, as a puzzle; -- followed by out;\n   as, to puzzle out a mystery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puzzle', 'v. i.', 'To be bewildered, or perplexed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puzzle', 'v. i.', 'To work, as at a puzzle; as, to puzzle over a problem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puzzledom', 'n.', 'The domain of puzzles; puzzles, collectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puzzle-headed', 'a.', 'Having the head full of confused notions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puzzlement', 'n.', 'The state of being puzzled; perplexity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puzzier', 'n.', 'One who, or that which, puzzles or perplexes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puzzlingly', 'adv.', 'In a puzzling manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puzzolan', 'n.', 'Alt. of Puzzolana');
  INSERT INTO words (word, word_type, description) VALUES  (    'Puzzolana', 'n.', 'See Pozzuolana.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyaemia', 'n.', 'A form of blood poisoning produced by the absorption into\n   the blood of morbid matters usually originating in a wound or local\n   inflammation. It is characterized by the development of multiple\n   abscesses throughout the body, and is attended with irregularly\n   recurring chills, fever, profuse sweating, and exhaustion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyaemic', 'a.', 'Of or pertaining to pyaemia; of the nature of pyaemia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pycnaspidean', 'a.', 'Having the posterior side of the tarsus covered with\n   small irregular scales; -- said of certain birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pycnidia', 'pl. ', 'of Pycnidium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pycnidium', 'n.', 'In certain fungi, a flask-shaped cavity from the surface\n   of the inner walls of which spores are produced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pycnite', 'n.', 'A massive subcolumnar variety of topaz.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pycnodont', 'n.', 'Any fossil fish belonging to the Pycnodontini. They have\n   numerous round, flat teeth, adapted for crushing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pycnodontini', 'n. pl.', 'An extinct order of ganoid fishes. They had a\n   compressed body, covered with dermal ribs (pleurolepida) and with\n   enameled rhomboidal scales.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pycnogonid', 'n.', 'One of the Pycnogonida.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pycnogonida', 'n. pl.', 'A class of marine arthropods in which the body is\n   small and thin, and the eight legs usually very long; -- called also\n   Pantopoda.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pycnometer', 'n.', 'A specific gravity bottle; a standard flask for\n   measuring and comparing the densities of liquids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pycnostyle', 'a.', 'See under Intercolumniation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pycnostyle', 'n.', 'A pycnostyle colonnade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pye', 'n.', 'See 2d Pie (b).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyebald', 'a.', 'See Piebald.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyelitis', 'n.', 'Inflammation of the pelvis of the kidney.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyemia', 'n.', 'See PyAemia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyet', 'n.', 'A magpie; a piet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pygal', 'a.', 'Situated in the region of the rump, or posterior end of the\n   backbone; -- applied especially to the posterior median plates in the\n   carapace of chelonians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pygarg', '', 'Alt. of Pygargus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pygargus', '', 'A quadruped, probably the addax, an antelope having a white\n   rump.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pygargus', '', 'The female of the hen harrier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pygargus', '', 'The sea eagle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pygidia', 'pl. ', 'of Pygidium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pygidium', 'n.', 'The caudal plate of trilobites, crustacean, and certain\n   insects. See Illust. of Limulus and Trilobite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pygmy', 'a.', 'Alt. of Pygmean');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pygmean', 'a.', 'Of or pertaining to a pygmy; resembling a pygmy or dwarf;\n   dwarfish; very small.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pygmies', 'pl. ', 'of Pygmy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pygmy', 'n.', 'One of a fabulous race of dwarfs who waged war with the\n   cranes, and were destroyed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pygmy', 'n.', 'Hence, a short, insignificant person; a dwarf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pygobranchia', 'n. pl.', 'A division of opisthobranchiate mollusks having\n   the branchiae in a wreath or group around the anal opening, as in the\n   genus Doris.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pygopod', 'n.', 'One of the Pygopodes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pygopod', 'n.', 'Any species of serpentiform lizards of the family\n   Pygopodidae, which have rudimentary hind legs near the anal cleft, but\n   lack fore legs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pygropodes', 'n. pl.', 'A division of swimming birds which includes the\n   grebes, divers, auks, etc., in which the legs are placed far back.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pygopodous', 'a.', 'Of or pertaining to the Pygopodes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pygostyle', 'n.', 'The plate of bone which forms the posterior end of the\n   vertebral column in most birds; the plowshare bone; the vomer. It is\n   formed by the union of a number of the last caudal vertebrae, and\n   supports the uropigium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyin', 'n.', 'An albuminoid constituent of pus, related to mucin, possibly\n   a mixture of substances rather than a single body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyjama', 'n.', 'In India and Persia, thin loose trowsers or drawers; in\n   Europe and America, drawers worn at night, or a kind of nightdress with\n   legs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pykar', 'n.', 'An ancient English fishing boat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pylae', 'pl. ', 'of Pyla');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pylas', 'pl. ', 'of Pyla');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyla', 'n.', 'The passage between the iter and optocoele in the brain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pylagore', 'n.', 'a deputy of a State at the Amphictyonic council.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pylangia', 'pl. ', 'of Pylangium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pylangium', 'n.', 'The first and undivided part of the aortic trunk in the\n   amphibian heart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pylon', 'n.', 'A low tower, having a truncated pyramidal form, and flanking\n   an ancient Egyptian gateway.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pylon', 'n.', 'An Egyptian gateway to a large building (with or without\n   flanking towers).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyloric', 'a.', 'Of, pertaining to, or in the region of, the pylorus; as,\n   the pyloric end of the stomach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pylori', 'pl. ', 'of Pylorus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pylorus', 'n.', 'The opening from the stomach into the intestine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pylorus', 'n.', 'A posterior division of the stomach in some invertebrates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyne', 'n. & v.', 'See Pine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pynoun', 'n.', 'A pennant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyocyanin', 'n.', 'A blue coloring matter found in the pus from old sores,\n   supposed to be formed through the agency of a species of bacterium\n   (Bacillus pyocyaneus).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyogenic', 'a.', 'Producing or generating pus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyoid', 'a.', 'Of or pertaining to pus; of the nature of, or like, pus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyopneumothorax', 'n.', 'Accumulation of air, or other gas, and of pus, in\n   the pleural cavity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyot', 'n.', 'The magpie. See Piet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyoxanthose', 'n.', 'A greenish yellow crystalline coloring matter found\n   with pyocyanin in pus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyracanth', 'n.', 'The evergreen thorn (Crataegus Pyracantha), a shrub\n   native of Europe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyral', 'a.', 'Of or pertaining to a pyre.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyralid', 'n.', 'Any moth of the family Pyralidae. The species are numerous\n   and mostly small, but some of them are very injurious, as the bee moth,\n   meal moth, hop moth, and clover moth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyramid', 'n.', 'A solid body standing on a triangular, square, or\n   polygonal base, and terminating in a point at the top; especially, a\n   structure or edifice of this shape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyramid', 'n.', 'A solid figure contained by a plane rectilineal figure as\n   base and several triangles which have a common vertex and whose bases\n   are sides of the base.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyramid', 'n.', 'The game of pool in which the balls are placed in the form\n   of a triangle at spot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyramidal', 'a.', 'Of or pertaining to a pyramid; in the form of a a\n   pyramid; pyramidical; as, pyramidal cleavage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyramidal', 'a.', 'Same as Tetragonal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyramidal', 'n.', 'One of the carpal bones. See Cuneiform, n., 2 (b).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyramidally', 'adv.', 'Like a pyramid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyramidic', 'a.', 'Alt. of Pyramidical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyramidical', 'a.', 'Of or pertaining to a pyramid; having the form of a\n   pyramid; pyramidal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyramidia', 'pl. ', 'of Pyramidion');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyramidion', 'n.', 'The small pyramid which crowns or completes an obelisk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyramidoid', 'n.', 'A solid resembling a pyramid; -- called also pyramoid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyramides', 'pl. ', 'of Pyramis');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyramis', 'n.', 'A pyramid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyramoid', 'n.', 'See Pyramidoid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyrargyrite', 'n.', 'Ruby silver; dark red silver ore. It is a sulphide of\n   antimony and silver, occurring in rhombohedral crystals or massive, and\n   is of a dark red or black color with a metallic adamantine luster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyre', 'n.', 'A funeral pile; a combustible heap on which the dead are\n   burned; hence, any pile to be burnt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyrenae', 'pl. ', 'of Pyrena');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyrena', 'n.', 'A nutlet resembling a seed, or the kernel of a drupe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyrene', 'n.', 'One of the less volatile hydrocarbons of coal tar, obtained\n   as a white crystalline substance, C16H10.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyrene', 'n.', 'Same as Pyrena.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyrenean', 'a.', 'Of or pertaining to the Pyrenees, a range of mountains\n   separating France and Spain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyrenean', 'n.', 'The Pyrenees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyrenoid', 'n.', 'A transparent body found in the chromatophores of certain\n   Infusoria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyrethrin', 'n.', 'A substance resembling, and isomeric with, ordinary\n   camphor, and extracted from the essential oil of feverfew; -- called\n   also Pyrethrum camphor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyrethrine', 'n.', 'An alkaloid extracted from the root of the pellitory of\n   Spain (Anacyclus pyrethrum).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyretic', 'a.', 'Of or pertaining to fever; febrile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyretology', 'n.', 'A discourse or treatise on fevers; the doctrine of\n   fevers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyrexiae', 'pl. ', 'of Pyrexia');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyrexia', 'n.', 'The febrile condition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyrexial', 'a.', 'Alt. of Pyrexical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyrexical', 'a.', 'Of or pertaining to fever; feverish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyrgom', 'n.', 'A variety of pyroxene; -- called also fassaite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyrheliometer', 'n.', 'An instrument for measuring the direct heating\n   effect of the suns rays.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyridic', 'a.', 'Related to, or formed from, pyridin or its homologues; as,\n   the pyridic bases.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyridine', 'n.', 'A nitrogenous base, C5H5N, obtained from the distillation\n   of bone oil or coal tar, and by the decomposition of certain alkaloids,\n   as a colorless liquid with a peculiar pungent odor. It is the nucleus\n   of a large number of organic substances, among which several vegetable\n   alkaloids, as nicotine and certain of the ptomaines, may be mentioned.\n   See Lutidine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyridyl', 'n.', 'A hypothetical radical, C5H4N, regarded as the essential\n   residue of pyridine, and analogous to phenyl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyriform', 'a.', 'Having the form of a pear; pear-shaped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyritaceous', 'a.', 'Of or pertaining to pyrites. See Pyritic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyrites', 'pl. ', 'of Pyrite');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyrite', 'n.', 'A common mineral of a pale brass-yellow color and brilliant\n   metallic luster, crystallizing in the isometric system; iron pyrites;\n   iron disulphide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyrites', 'n.', 'A name given to a number of metallic minerals, sulphides\n   of iron, copper, cobalt, nickel, and tin, of a white or yellowish\n   color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyritic', 'a.', 'Alt. of Pyritical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyritical', 'a.', 'Of or pertaining to pyrites; consisting of, or\n   resembling, pyrites.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyritiferous', 'a.', 'Containing or producing pyrites.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyritize', 'v. t.', 'To convert into pyrites.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyritohedral', 'a.', 'Like pyrites in hemihedral form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyritohedron', 'n.', 'The pentagonal dodecahedron, a common form of pyrite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyritoid', 'n.', 'Pyritohedron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyritology', 'n.', 'The science of blowpipe analysis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyritous', 'a.', 'Pyritic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyro-', '', 'Alt. of Pyr-');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyr-', '', 'Combining forms designating fire or heat; specifically (Chem.),\n   used to imply an actual or theoretical derivative by the action of\n   heat; as in pyrophosphoric, pyrosulphuric, pyrotartaric, pyrotungstic,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyro', 'n.', 'Abbreviation of pyrogallic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyroacetic', 'a.', 'Pertaining to, and designating, a substance (acetone)\n   obtained by the distillation of the acetates. It is now called also\n   pyroacetic ether, and formerly was called pyroacetic spirit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyroacid', 'n.', 'An acid obtained by sybjecting another acid to the action\n   of heat. Cf. Pyro-.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyroantimonate', 'n.', 'A salt of pyroantimonic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyroantimonic', 'a.', 'Pertaining to, or designating, an acid of antimony\n   analogous to pyrophosphoric acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyroarsenate', 'n.', 'A salt of pyroarsenic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyroarsenic', 'a.', 'Pertaining to or designating, an acid of arsenic\n   analogous to pyrophosphoric acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyroborate', 'n.', 'A salt of pyroboric acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyroboric', 'a.', 'Pertaining to derived from, or designating, an acid,\n   H2B4O7 (called also tetraboric acid), which is the acid ingredient of\n   ordinary borax, and is obtained by heating boric acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyrocatechin', 'n.', 'A white crystalline substance, C6H4(OH)2, of the\n   phenol series, found in various plants; -- so called because first\n   obtained by distillation of gum catechu. Called also catechol,\n   oxyphenol. etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyrochlore', 'n.', 'A niobate of calcium, cerium, and other bases,\n   occurring usually in octahedrons of a yellowish or brownish color and\n   resinous luster; -- so called from its becoming grass-green on being\n   subjected to heat under the blowpipe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyrocitric', 'a.', 'Pertaining to, or designating, any one of three acids\n   obtained by the distillation of citric acid, and called respectively\n   citraconic, itaconic, and mesaconic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Purocoll', 'n.', 'A yellow crystalline substance allied to pyrrol, obtained\n   by the distillation of gelatin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyroelectric', 'a.', 'Pertaining to, or dependent on, pyroelectricity;\n   receiving electric polarity when heated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyroelectric', 'n.', 'A substance which becomes electrically polar when\n   heated, exhibiting opposite charges of statical electricity at two\n   separate parts, especially the two extremities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyroelectricity', 'n.', 'Electricity developed by means of heat; the\n   science which treats of electricity thus developed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyrogallate', 'n.', 'A salt of pyrogallic acid; an ether of pyrogallol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyrogallic', 'a.', 'Pertaining to, derived from, or designating, an acid\n   called pyrogallol. See Pyrogallol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyrogallol', 'n.', 'A phenol metameric with phloroglucin, obtained by the\n   distillation of gallic acid as a poisonous white crystalline substance\n   having acid properties, and hence called also pyrogallic acid. It is a\n   strong reducer, and is used as a developer in photography and in the\n   production of certain dyes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyrogen', 'n.', 'Electricity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyrogen', 'n.', 'A poison separable from decomposed meat infusions, and\n   supposed to be formed from albuminous matter through the agency of\n   bacteria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyrogenic', 'a.', 'Producing heat; -- said of substances, as septic\n   poisons, which elevate the temperature of the body and cause fever.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyrogenous', 'a.', 'Produced by fire; igneous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyrognostic', 'a.', 'Of or pertaining to characters developed by the use of\n   heat; pertaining to the characters of minerals when examined before the\n   blowpipe; as, the pyrognostic characters of galena.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyrognostics', 'n. pl.', 'The characters of a mineral observed by the use\n   of the blowpipe, as the degree of fusibility, flame coloration, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyrography', 'n.', 'A process of printing, ornamenting, or carving, by\n   burning with heated instruments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyrolator', 'n.', 'A fire worshiper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyrolatry', 'n.', 'The worship of fire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyroligneous', 'a.', 'Alt. of Pyrolignic');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyrolignic', 'a.', 'Pertaining to, or designating, the acid liquid obtained\n   in the distillation of wood, consisting essentially of impure acetic\n   acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyrolignous', 'a.', 'Same as Pyroligneous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyrolithic', 'a.', 'Same as Pyrouric, or Cyanuric.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyrologist', 'n.', 'One who is versed in, or makes a study of, pyrology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyrology', 'n.', 'That branch of physical science which treats of the\n   properties, phenomena, or effects of heat; also, a treatise on heat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyrolusite', 'n.', 'Manganese dioxide, a mineral of an iron-black or dark\n   steel-gray color and metallic luster, usually soft. Pyrolusite parts\n   with its oxygen at a red heat, and is extensively used in discharging\n   the brown and green tints of glass (whence its name).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyromagnetic', 'a.', 'Acting by the agency of heat and magnetism; as, a\n   pyromagnetic machine for producing electric currents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyromalate', 'n.', 'A salt of pyromalic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyromalic', 'a.', 'Pertaining to, or designating, an acid now called maleic\n   acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyromancy', 'n.', 'Divination by means of fire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyromania', 'n.', 'An insane disposition to incendiarism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyromantic', 'a.', 'Of or pertaining to pyromancy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyromantic', 'n.', 'One who pretends to divine by fire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyrometer', 'n.', 'An instrument used for measuring the expansion of solid\n   bodies by heat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyrometer', 'n.', 'An instrument for measuring degrees of heat above those\n   indicated by the mercurial thermometer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyrometric', 'a.', 'Alt. of Pyrometrical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyrometrical', 'a.', 'Pertaining to, or obtained by, the pyrometer; as,\n   pyrometrical instruments; pyrometrical measurements.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyrometry', 'n.', 'The art of measuring degrees of heat, or the expansion\n   of bodies by heat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyromorphite', 'n.', 'Native lead phosphate with lead chloride, occurring\n   in bright green and brown hexagonal crystals and also massive; -- so\n   called because a fused globule crystallizes in cooling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyromorphous', 'a.', 'Having the property of crystallizing by the agency of\n   fire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyromucate', 'n.', 'A salt of pyromucic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyromucic', 'a.', 'Pertaining to, derived from, or designating, an acid\n   obtained as a white crystalline substance by the distillation of mucic\n   acid, or by the oxidation of furfurol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyronomics', 'n.', 'The science of heat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyrope', 'n.', 'A variety of garnet, of a poppy or blood-red color,\n   frequently with a tinge of orange. It is used as a gem. See the Note\n   under Garnet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyrophane', 'n.', 'A mineral which is opaque in its natural state, but is\n   said to change its color and become transparent by heat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyrophanous', 'a.', 'Rendered transparent by heat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyrophone', 'n.', 'A musical instrument in which the tones are produced by\n   flames of hydrogen, or illuminating gas, burning in tubes of different\n   sizes and lengths.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyrophoric', 'a.', 'Alt. of Pyrophorous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyrophorous', 'a.', 'Light-producing; of or pertaining to pyrophorus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyrophorus', 'n.', 'Any one of several substances or mixtures which\n   phosphoresce or ignite spontaneously on exposure to air, as a heated\n   mixture of alum, potash, and charcoal, or a mixture of charcoal and\n   finely divided lead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyrophosphate', 'n.', 'A salt of pyrophosphoric acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyrophosphoric', 'a.', 'Pertaining to, or designating, an acid, H4P2O7,\n   which is obtained as a white crystalline substance. Its salts are\n   obtained by heating the phosphates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyrophyllite', 'n.', 'A mineral, usually of a white or greenish color and\n   pearly luster, consisting chiefly of the hydrous silicate of alumina.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyroscope', 'n.', 'An instrument for measuring the intensity of heat\n   radiating from a fire, or the cooling influence of bodies. It is a\n   differential thermometer, having one bulb coated with gold or silver\n   leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyrosis', 'n.', 'See Water brash, under Brash.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyrosmalite', 'n.', 'A mineral, usually of a pale brown or of a gray or\n   grayish green color, consisting chiefly of the hydrous silicate of iron\n   and manganese; -- so called from the odor given off before the\n   blowpipe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyrosome', 'n.', 'Any compound ascidian of the genus Pyrosoma. The\n   pyrosomes form large hollow cylinders, sometimes two or three feet\n   long, which swim at the surface of the sea and are very phosphorescent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyrosulphate', 'n.', 'A salt of pyrosulphuric acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyrosulphuric', 'a.', 'Pertaining to, or designating, an acid called also\n   disulphuric acid) obtained by distillation of certain sulphates, as a\n   colorless, thick, oily liquid, H2S2O7 resembling sulphuric acid. It is\n   used in the solution of indigo, in the manufacture of alizarin, and in\n   dehydration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyrotartaric', 'a.', 'Pertaining to, or designating, an acid obtained as a\n   white crystalline substance by the distillation of tartaric acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyrotartrate', 'n.', 'A salt of pyrotartaric acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyrotechnian', 'n.', 'A pyrotechnist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyrotechnic', 'a.', 'Alt. of Pyrotechnical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyrotechnical', 'a.', 'Of or pertaining to fireworks, or the art of forming\n   them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyrotechnician', 'n.', 'A pyrotechnist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyrotechnics', 'n.', 'The art of making fireworks; the manufacture and use\n   of fireworks; pyrotechny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyrotechnist', 'n.', 'One skilled in pyrotechny; one who manufactures\n   fireworks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyrotechny', 'n.', 'The use and application of fire in science and the\n   arts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyrotechny', 'n.', 'Same as Pyrotechnics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyrothonide', 'n.', 'A kind of empyreumatic oil produced by the combustion\n   of textures of hemp, linen, or cotton in a copper vessel, -- formerly\n   used as a remedial agent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyrotic', 'a.', 'Caustic. See Caustic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyrotic', 'n.', 'A caustic medicine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyrotritartaric', 'a.', 'Designating an acid which is more commonly called\n   uric acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyrotungstic', 'a.', 'Polytungstic. See Metatungstic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyrouric', 'a.', 'Pertaining to, or designating, an acid now called\n   cyanuric acid. See Cyanuric.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyrovanadic', 'a.', 'Pertaining to, or designating, an acid of vanadium,\n   analogous to pyrophosphoric acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyroxanthin', 'n.', 'A yellow crystalline hydrocardon extracted from crude\n   wood spirit; -- called also eblanin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyroxene', 'n.', 'A common mineral occurring in monoclinic crystals, with a\n   prismatic angle of nearly 90, and also in massive forms which are\n   often laminated. It varies in color from white to dark green and black,\n   and includes many varieties differing in color and composition, as\n   diopside, malacolite, salite, coccolite, augite, etc. They are all\n   silicates of lime and magnesia with sometimes alumina and iron.\n   Pyroxene is an essential constituent of many rocks, especially basic\n   igneous rocks, as basalt, gabbro, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyroxenic', 'a.', 'Containing pyroxene; composed chiefly of pyroxene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyroxenite', 'n.', 'A rock consisting essentially of pyroxene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyroxylic', 'a.', 'Derived from wood by distillation; -- formerly used in\n   designating crude wood spirit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyroxylin', 'n.', 'A substance resembling gun cotton in composition and\n   properties, but distinct in that it is more highly nitrified and is\n   soluble in alcohol, ether, etc.; -- called also pyroxyle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyrrhic', 'a.', 'Of or pertaining to an ancient Greek martial dance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyrrhic', 'a.', 'Of or pertaining to a pyrrhic, or to pyrrhics; containing\n   pyrrhic; as, a pyrrhic verse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyrrhic', 'n.', 'An ancient Greek martial dance, to the accompaniment of\n   the flute, its time being very quick.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyrrhic', 'n.', 'A foot consisting of two short syllables.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyrrhicist', 'n.', 'One two danced the pyrrhic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyrrhonean', 'a.', 'Alt. of Pyrrhonic');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyrrhonic', 'a.', 'Of or pertaining to pyrrhonism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyrrhonism', 'n.', 'Skepticism; universal doubt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyrrhonist', 'n.', 'A follower of Pyrrho; a skeptic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyrrhotine', 'n.', 'Alt. of Pyrrhotite');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyrrhotite', 'n.', 'A bronze-colored mineral, of metallic luster. It is a\n   sulphide of iron, and is remarkable for being attracted by the magnet.\n   Called also magnetic pyrites.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyrrol', 'n.', 'A nitrogenous base found in coal tar, bone oil, and other\n   distillates of organic substances, and also produced synthetically as a\n   colorless liquid, C4H5N, having on odor like that of chloroform. It is\n   the nucleus and origin of a large number of derivatives. So called\n   because it colors a splinter of wood moistened with hydrochloric acid a\n   deep red.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyrroline', 'n.', 'A nitrogenous base, C4H7N, obtained as a colorless\n   liquid by the reduction of pyrrol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyrula', 'n.', 'A genus of large marine gastropods. having a pear-shaped\n   shell. It includes the fig-shells. See Illust. in Appendix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyruric', 'a.', 'Same as Pyrouric.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyrus', 'n.', 'A genus of rosaceous trees and shrubs having pomes for\n   fruit. It includes the apple, crab apple, pear, chokeberry, sorb, and\n   mountain ash.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyruvic', 'a.', 'Pertaining to, or designating, an acid (called also\n   pyroracemic acid) obtained, as a liquid having a pungent odor, by the\n   distillation of racemic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyruvil', 'n.', 'A complex nitrogenous compound obtained by heating\n   together pyruvic acid and urea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pythagorean', 'a.', 'Of or pertaining to Pythagoras (a Greek philosopher,\n   born about 582 b. c.), or his philosophy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pythagorean', 'n.', 'A follower of Pythagoras; one of the school of\n   philosophers founded by Pythagoras.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pythagoreanism', 'n.', 'The doctrines of Pythagoras or the Pythagoreans.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pythagoric', 'a.', 'Alt. of Pythagorical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pythagorical', 'a.', 'See Pythagorean, a.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pythagorism', 'n.', 'The doctrines taught by Pythagoras.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pythagorized', 'imp. & p. p.', 'of Pythagorize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pythagorizing', 'p. pr. & vb. n.', 'of Pythagorize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pythagorize', 'v. i.', 'To speculate after the manner of Pythagoras.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pythiad', 'n.', 'The period intervening between one celebration of the\n   Pythian games and the next.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pythian', 'a.', 'Of or pertaining to Delphi, to the temple of Apollo, or to\n   the priestess of Apollo, who delivered oracles at Delphi.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pythocenic', 'a.', 'Producing decomposition, as diseases which are supposed\n   to be accompanied or caused by decomposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Python', 'n.', 'Any species of very large snakes of the genus Python, and\n   allied genera, of the family Pythonidae. They are nearly allied to the\n   boas. Called also rock snake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Python', 'n.', 'A diviner by spirits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pythoness', 'n.', 'The priestess who gave oracular answers at Delphi in\n   Greece.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pythoness', 'n.', 'Any woman supposed to have a spirit of divination; a\n   sort of witch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pythonic', 'a.', 'Prophetic; oracular; pretending to foretell events.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pythonism', 'n.', 'The art of predicting events after the manner of the\n   priestess of Apollo at Delphi; equivocal prophesying.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pythonist', 'n.', 'A conjurer; a diviner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pythonomorpha', 'n. pl.', 'Same as Mosasauria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyuria', 'n.', 'A morbid condition in which pus is discharged in the urine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyx', 'n.', 'The box, case, vase, or tabernacle, in which the host is\n   reserved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyx', 'n.', 'A box used in the British mint as a place of deposit for\n   certain sample coins taken for a trial of the weight and fineness of\n   metal before it is sent from the mint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyx', 'n.', 'The box in which the compass is suspended; the binnacle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyx', 'n.', 'Same as Pyxis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyx', 'v. t.', 'To test as to weight and fineness, as the coins deposited\n   in the pyx.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyxidate', 'a.', 'Having a pyxidium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyxidia', 'pl. ', 'of Pyxidium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyxidium', 'n.', 'A pod which divides circularly into an upper and lower\n   half, of which the former acts as a kind of lid, as in the pimpernel\n   and purslane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyxidium', 'n.', 'The theca of mosses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyxle', 'n.', 'Same as Pixy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyxis', 'n.', 'A box; a pyx.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyxis', 'n.', 'A pyxidium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Pyxis', 'n.', 'The acetabulum. See Acetabulum, 2.\n   Q () the seventeenth letter of the English alphabet, has but one sound\n   (that of k), and is always followed by u, the two letters together\n   being sounded like kw, except in some words in which the u is silent.\n   See Guide to Pronunciation, / 249. Q is not found in Anglo-Saxon, cw\n   being used instead of qu; as in cwic, quick; cwen, queen. The name (k/)\n   is from the French ku, which is from the Latin name of the same letter;\n   its form is from the Latin, which derived it, through a Greek alphabet,\n   from the Ph/nician, the ultimate origin being Egyptian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Qua', 'conj.', 'In so far as; in the capacity or character of; as.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quab', 'n.', 'An unfledged bird; hence, something immature or unfinished.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quab', 'v. i.', 'See Quob, v. i.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Qua-bird', 'n.', 'The American night heron. See under Night.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quacha', 'n.', 'The quagga.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Qvacked', 'imp. & p. p.', 'of Quack');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quacking', 'p. pr. & vb. n.', 'of Quack');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quack', 'v. i.', 'To utter a sound like the cry of a duck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quack', 'v. i.', 'To make vain and loud pretensions; to boast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quack', 'v. i.', 'To act the part of a quack, or pretender.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quack', 'n.', 'The cry of the duck, or a sound in imitation of it; a\n   hoarse, quacking noise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quack', 'n.', 'A boastful pretender to medical skill; an empiric; an\n   ignorant practitioner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quack', 'n.', 'Hence, one who boastfully pretends to skill or knowledge of\n   any kind not possessed; a charlatan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quack', 'a.', 'Pertaining to or characterized by, boasting and pretension;\n   used by quacks; pretending to cure diseases; as, a quack medicine; a\n   quack doctor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quackeries', 'pl. ', 'of Quackery');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quackery', 'n.', 'The acts, arts, or boastful pretensions of a quack; false\n   pretensions to any art; empiricism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quack grass', '', 'See Quitch grass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quackish', 'a.', 'Like a quack; boasting; characterized by quackery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quackism', 'n.', 'Quackery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quackled', 'imp. & p. p.', 'of Quackle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quackling', 'p. pr. & vb. n.', 'of Quackle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quackle', 'v. i. & t.', 'To suffocate; to choke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quacksalver', 'n.', 'One who boasts of his skill in medicines and salves,\n   or of the efficacy of his prescriptions; a charlatan; a quack; a\n   mountebank.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quad', 'a.', 'Alt. of Quade');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quade', 'a.', 'Evil; bad; baffling; as, a quade wind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quad', 'n.', 'A quadrat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quad', 'n.', 'A quadrangle; hence, a prison.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadrae', 'pl. ', 'of Quadra');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadra', 'n.', 'The plinth, or lowest member, of any pedestal, podium,\n   water table, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadra', 'n.', 'A fillet, or listel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadrable', 'a.', 'That may be sqyared, or reduced to an equivalent square;\n   -- said of a surface when the area limited by a curve can be exactly\n   found, and expressed in a finite number of algebraic terms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadragenarious', 'a.', 'Consisting of forty; forty years old.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadragene', 'n.', 'An indulgence of forty days, corresponding to the forty\n   days of ancient canonical penance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadragesima', 'n.', 'The forty days of fast preceding Easter; Lent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadragesimal', 'a.', 'Belonging to Lent; used in Lent; Lenten.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadragesimals', 'n. pl.', 'Offerings formerly made to the mother church of\n   a diocese on Mid-Lent Sunday.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadrangle', 'n.', 'A plane figure having four angles, and consequently\n   four sides; any figure having four angles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadrangle', 'n.', 'A square or quadrangular space or inclosure, such a\n   space or court surrounded by buildings, esp. such a court in a college\n   or public school in England.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadrangular', 'a.', 'Having four angles, and consequently four sides;\n   tetragonal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadrantes', 'pl. ', 'of Quadrans');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadrans', 'n.', 'A fourth part of the coin called an as. See 3d As, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadrans', 'n.', 'The fourth of a penny; a farthing. See Cur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadrant', 'n.', 'The fourth part; the quarter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadrant', 'n.', 'The quarter of a circle, or of the circumference of a\n   circle, an arc of 90, or one subtending a right angle at the center.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadrant', 'n.', 'One of the four parts into which a plane is divided by\n   the coordinate axes. The upper right-hand part is the first quadrant;\n   the upper left-hand part the second; the lower left-hand part the\n   third; and the lower right-hand part the fourth quadrant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadrant', 'n.', 'An instrument for measuring altitudes, variously\n   constructed and mounted for different specific uses in astronomy,\n   surveying, gunnery, etc., consisting commonly of a graduated arc of\n   90, with an index or vernier, and either plain or telescopic sights,\n   and usually having a plumb line or spirit level for fixing the vertical\n   or horizontal direction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadrantal', 'a.', 'Of or pertaining to a quadrant; also, included in the\n   fourth part of a circle; as, quadrantal space.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadrantal', 'n.', 'A cubical vessel containing a Roman cubic foot, each\n   side being a Roman square foot; -- used as a measure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadrantal', 'n.', 'A cube.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadrat', 'n.', 'A block of type metal lower than the letters, -- used in\n   spacing and in blank lines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadrat', 'n.', 'An old instrument used for taking altitudes; -- called\n   also geometrical square, and line of shadows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadrate', 'a.', 'Having four equal sides, the opposite sides parallel, and\n   four right angles; square.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadrate', 'a.', 'Produced by multiplying a number by itself; square.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadrate', 'a.', 'Square; even; balanced; equal; exact.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadrate', 'a.', 'Squared; suited; correspondent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadrate', 'a.', 'A plane surface with four equal sides and four right\n   angles; a square; hence, figuratively, anything having the outline of a\n   square.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadrate', 'a.', 'An aspect of the heavenly bodies in which they are\n   distant from each other 90, or the quarter of a circle; quartile. See\n   the Note under Aspect, 6.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadrate', 'a.', 'The quadrate bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadrated', 'imp. & p. p.', 'of Quadrate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadrating', 'p. pr. & vb. n.', 'of Quadrate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadrate', 'a.', 'To square; to agree; to suit; to correspond; -- followed\n   by with.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadrate', 'v. t.', 'To adjust (a gun) on its carriage; also, to train (a\n   gun) for horizontal firing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadratic', 'a.', 'Of or pertaining to a square, or to squares; resembling\n   a quadrate, or square; square.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadratic', 'a.', 'Tetragonal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadratic', 'a.', 'Pertaining to terms of the second degree; as, a\n   quadratic equation, in which the highest power of the unknown quantity\n   is a square.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadratics', 'n.', 'That branch of algebra which treats of quadratic\n   equations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadratojugal', 'a.', 'Of or pertaining to the quadrate and jugal bones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadratojugal', 'a.', 'Of or pertaining to the quadratojugal bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadratojugal', 'n.', 'The quadratojugal bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-trixes', 'pl. ', 'of Quadratrix');
  INSERT INTO words (word, word_type, description) VALUES  (    '-trices', 'pl. ', 'of Quadratrix');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadratrix', 'n.', 'A curve made use of in the quadrature of other curves;\n   as the quadratrix, of Dinostratus, or of Tschirnhausen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadrature', 'a.', 'The act of squaring; the finding of a square having the\n   same area as some given curvilinear figure; as, the quadrature of a\n   circle; the operation of finding an expression for the area of a figure\n   bounded wholly or in part by a curved line, as by a curve, two\n   ordinates, and the axis of abscissas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadrature', 'a.', 'A quadrate; a square.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadrature', 'a.', 'The integral used in obtaining the area bounded by a\n   curve; hence, the definite integral of the product of any function of\n   one variable into the differential of that variable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadrature', 'a.', 'The position of one heavenly body in respect to another\n   when distant from it 90, or a quarter of a circle, as the moon when at\n   an equal distance from the points of conjunction and opposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadrel', 'n.', 'A square piece of turf or peat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadrel', 'n.', 'A square brick, tile, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadrennial', 'a.', 'Comprising four years; as, a quadrennial period.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadrennial', 'a.', 'Occurring once in four years, or at the end of every\n   four years; as, quadrennial games.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadrennially', 'adv.', 'Once in four years.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadrennium', 'n.', 'A space or period of four years.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadri-', '', 'A combining form meaning four, four times, fourfold; as,\n   quadricapsular, having four capsules.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadribasic', 'a.', 'Same as Tetrabasic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadrible', 'a.', 'Quadrable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadric', 'a.', 'Of or pertaining to the second degree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadric', 'n.', 'A quantic of the second degree. See Quantic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadric', 'n.', 'A surface whose equation in three variables is of the\n   second degree. Spheres, spheroids, ellipsoids, paraboloids,\n   hyperboloids, also cones and cylinders with circular bases, are\n   quadrics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadricapsular', 'a.', 'Having four capsules.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadriceps', 'n.', 'The great extensor muscle of the knee, divided above\n   into four parts which unite in a single tendon at the knee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadricipital', 'n.', 'Of or pertaining to the quadriceps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadricorn', 'n.', 'Any quadricornous animal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadricornous', 'a.', 'Having four horns, or hornlike organs; as, a\n   quadricornous beetle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadricostate', 'a.', 'Having four ribs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadridentate', 'a.', 'Having four teeth; as, a quadridentate leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadriennial', 'a.', 'Same as Quadrennial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadrifarious', 'a.', 'Arranged in four rows or ranks; as, quadrifarious\n   leaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadrifid', 'a.', 'Divided, or deeply cleft, into four parts; as, a\n   quadrifid perianth; a quadrifid leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadrifoil', 'a.', 'Alt. of Quadrifoliate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadrifoliate', 'a.', 'Four-leaved; having the leaves in whorls of four.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadrifurcated', 'a.', 'Having four forks, or branches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadrigae', 'pl. ', 'of Quadriga');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadriga', 'n.', 'A car or chariot drawn by four horses abreast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadrigeminal', 'a.', 'Alt. of Quadrigeminous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadrigeminous', 'a.', 'Fourfold; having four similar parts, or two pairs\n   of similar parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadrigenarious', 'a.', 'Consisting of four hundred.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadrijugate', 'a.', 'Same as Quadrijugous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadrijugous', 'a.', 'Pinnate, with four pairs of leaflets; as, a\n   quadrijugous leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadrilateral', 'a.', 'Having four sides, and consequently four angles;\n   quadrangular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadrilateral', 'n.', 'A plane figure having four sides, and consequently\n   four angles; a quadrangular figure; any figure formed by four lines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadrilateral', 'n.', 'An area defended by four fortresses supporting each\n   other; as, the Venetian quadrilateral, comprising Mantua, Peschiera,\n   Verona, and Legnano.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadrilateralness', 'n.', 'The property of being quadrilateral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadriliteral', 'a.', 'Consisting of four letters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadrille', 'n.', 'A dance having five figures, in common time, four\n   couples of dancers being in each set.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadrille', 'n.', 'The appropriate music for a quadrille.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadrille', 'n.', 'A game played by four persons with forty cards, being\n   the remainder of an ordinary pack after the tens, nines, and eights are\n   discarded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadrillion', 'n.', 'According to the French notation, which is followed\n   also upon the Continent and in the United States, a unit with fifteen\n   ciphers annexed; according to the English notation, the number produced\n   by involving a million to the fourth power, or the number represented\n   by a unit with twenty-four ciphers annexed. See the Note under\n   Numeration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadrilobate', 'a.', 'Alt. of Quadrilobed');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadrilobed', 'a.', 'Having four lobes; as, a quadrilobate leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadrilocular', 'a.', 'Having four cells, or cavities; as, a quadrilocular\n   heart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadrin', 'n.', 'A small piece of money, in value about a farthing, or a\n   half cent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadrinodal', 'a.', 'Possessing four nodes; as, quadrinodal curves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadrinomial', 'n.', 'A polynomial of four terms connected by the signs\n   plus or minus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadrinomical', 'a.', 'Quadrinomial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadrinominal', 'a.', 'Quadrinomial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadripartite', 'a.', 'Divided into four parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadripartitely', 'adv.', 'In four parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadripartition', 'n.', 'A division or distribution by four, or into four\n   parts; also, a taking the fourth part of any quantity or number.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadripennate', 'a.', 'Having four wings; -- said of insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadriphyllous', 'a.', 'Having four leaves; quadrifoliate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadrireme', 'n.', 'A galley with four banks of oars or rowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadrisection', 'n.', 'A subdivision into four parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadrisulcate', 'a.', 'Having four hoofs; as, a quadrisulcate foot; a\n   quadrisulcate animal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadrisyllabic', '', 'Alt. of Quadri-syllabical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadri-syllabical', '', 'Having four syllables; of or pertaining to\n   quadrisyllables; as, a quadrisyllabic word.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadrisyllable', 'n.', 'A word consisting of four syllables.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadrivalence', 'n.', 'The quality or state of being quadrivalent;\n   tetravalence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadrivalent', 'a.', 'Having a valence of four; capable of combining with,\n   being replaced by, or compared with, four monad atoms; tetravalent; --\n   said of certain atoms and radicals; thus, carbon and silicon are\n   quadrivalent elements.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadrivalve', 'a.', 'Dehiscent into four similar parts; four-valved; as, a\n   quadrivalve pericarp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadrivalve', 'n.', 'A door, shutter, or the like, having four folds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadrivalvular', 'a.', 'Having four valves; quadrivalve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadrivial', 'a.', 'Having four ways meeting in a point.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadrivial', 'n.', 'One of the four \"liberal arts\" making up the\n   quadrivium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadrivium', 'n.', 'The four \"liberal arts,\" arithmetic, music, geometry,\n   and astronomy; -- so called by the schoolmen. See Trivium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadroon', 'n.', 'The offspring of a mulatto and a white person; a person\n   quarter-blooded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadroxide', 'n.', 'A tetroxide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadrumana', 'n. pl.', 'A division of the Primates comprising the apes and\n   monkeys; -- so called because the hind foot is usually prehensile, and\n   the great toe opposable somewhat like a thumb. Formerly the Quadrumana\n   were considered an order distinct from the Bimana, which last included\n   man alone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadrumane', 'n.', 'One of the Quadrumana.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadrumanous', 'a.', 'Having four hands; of or pertaining to the\n   Quadrumana.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadruped', 'a.', 'Having four feet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadruped', 'n.', 'An animal having four feet, as most mammals and\n   reptiles; -- often restricted to the mammals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadrupedal', 'a.', 'Having four feet; of or pertaining to a quadruped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadruple', 'a.', 'Fourfold; as, to make quadruple restitution; a quadruple\n   alliance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadruple', 'n.', 'four times the sum or number; a fourfold amount; as, to\n   receive to quadruple of the amount in damages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadrupled', 'imp. & p. p.', 'of Quadruple');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadrupling', 'p. pr. & vb. n.', 'of Quadruple');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadruple', 'v. t.', 'To multiply by four; to increase fourfold; to double;\n   to double twice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadruple', 'v. i.', 'To be multiplied by four; to increase fourfold; to\n   become four times as much.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadruplex', 'a.', 'Fourfold; folded or doubled twice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadruplicated', 'imp. & p. p.', 'of Quadruplicate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadruplicating', 'p. pr. & vb. n.', 'of Quadruplicate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadruplicate', 'v. t.', 'To make fourfold; to double twice; to quadruple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadruplicate', 'a.', 'Fourfold; doubled twice; four times repeated; as, a\n   quadruplicate ratio, or a quadruplicate proportion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadruplicate', 'a.', 'Raised to the fourth power.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadruplication', 'n.', 'The act of making fourfold; a taking four times\n   the simple sum or amount.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quadruply', 'adv.', 'To a fourfold quantity; so as to be, or cause to be,\n   quadruple; as, to be quadruply recompensed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quaere', 'v. imperative.', 'Inquire; question; see; -- used to signify\n   doubt or to suggest investigation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quaestor', 'n.', 'Same as Questor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quaffed', 'imp. & p. p.', 'of Quaff');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quaffing', 'p. pr. & vb. n.', 'of Quaff');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quaff', 'v. t.', 'To drink with relish; to drink copiously of; to swallow\n   in large draughts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quaff', 'v. i.', 'To drink largely or luxuriously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quaffer', 'n.', 'One who quaffs, or drinks largely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quag', 'n.', 'A quagmire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quagga', 'n.', 'A South African wild ass (Equus, / Hippotigris, quagga).\n   The upper parts are reddish brown, becoming paler behind and behind and\n   beneath, with dark stripes on the face, neck, and fore part of the\n   body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quaggy', 'a.', 'Of the nature of a quagmire; yielding or trembling under\n   the foot, as soft, wet earth; spongy; boggy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quagmire', 'n.', 'Soft, wet, miry land, which shakes or yields under the\n   feet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quahog', 'n.', 'Alt. of Quahaug');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quahaug', 'n.', 'An American market clam (Venus mercenaria). It is sold in\n   large quantities, and is highly valued as food. Called also round clam,\n   and hard clam.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quaigh', 'n.', 'Alt. of Quaich');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quaich', 'n.', 'A small shallow cup or drinking vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Qualled', 'imp. & p. p.', 'of Quail');
  INSERT INTO words (word, word_type, description) VALUES  (    'Qualling', 'p. pr. & vb. n.', 'of Quail');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quail', 'v. i.', 'To die; to perish; hence, to wither; to fade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quail', 'v. i.', 'To become quelled; to become cast down; to sink under\n   trial or apprehension of danger; to lose the spirit and power of\n   resistance; to lose heart; to give way; to shrink; to cower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quail', 'v. t.', 'To cause to fail in spirit or power; to quell; to crush;\n   to subdue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quail', 'v. i.', 'To curdle; to coagulate, as milk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quail', 'n.', 'Any gallinaceous bird belonging to Coturnix and several\n   allied genera of the Old World, especially the common European quail\n   (C. communis), the rain quail (C. Coromandelica) of India, the stubble\n   quail (C. pectoralis), and the Australian swamp quail (Synoicus\n   australis).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quail', 'n.', 'Any one of several American partridges belonging to Colinus,\n   Callipepla, and allied genera, especially the bobwhite (called Virginia\n   quail, and Maryland quail), and the California quail (Calipepla\n   Californica).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quail', 'n.', 'Any one of numerous species of Turnix and allied genera,\n   native of the Old World, as the Australian painted quail (Turnix\n   varius). See Turnix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quail', 'n.', 'A prostitute; -- so called because the quail was thought to\n   be a very amorous bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quaily', 'n.', 'The upland plover.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quaint', 'a.', 'Prudent; wise; hence, crafty; artful; wily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quaint', 'a.', 'Characterized by ingenuity or art; finely fashioned;\n   skillfully wrought; elegant; graceful; nice; neat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quaint', 'a.', 'Curious and fanciful; affected; odd; whimsical; antique;\n   archaic; singular; unusual; as, quaint architecture; a quaint\n   expression.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quaintise', 'n.', 'Craft; subtlety; cunning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quaintise', 'n.', 'Elegance; beauty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quaintly', 'adv.', 'In a quaint manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quaintness', 'n.', 'The quality of being quaint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quair', 'n.', 'A quire; a book.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quaked', 'imp. & p. p.', 'of Quake');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quaking', 'p. pr. & vb. n.', 'of Quake');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quake', 'v. i.', 'To be agitated with quick, short motions continually\n   repeated; to shake with fear, cold, etc.; to shudder; to tremble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quake', 'v. i.', 'To shake, vibrate, or quiver, either from not being\n   solid, as soft, wet land, or from violent convulsion of any kind; as,\n   the earth quakes; the mountains quake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quake', 'v. t.', 'To cause to quake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quake', 'n.', 'A tremulous agitation; a quick vibratory movement; a\n   shudder; a quivering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quaker', 'n.', 'One who quakes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quaker', 'n.', 'One of a religious sect founded by George Fox, of\n   Leicestershire, England, about 1650, -- the members of which call\n   themselves Friends. They were called Quakers, originally, in derision.\n   See Friend, n., 4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quaker', 'n.', 'The nankeen bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quaker', 'n.', 'The sooty albatross.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quaker', 'n.', 'Any grasshopper or locust of the genus (Edipoda; -- so\n   called from the quaking noise made during flight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quakeress', 'n.', 'A woman who is a member of the Society of Friends.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quakerish', 'a.', 'Like or pertaining to a Quaker; Quakerlike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quakerism', 'n.', 'The peculiar character, manners, tenets, etc., of the\n   Quakers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quakerlike', 'a.', 'Like a Quaker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quakerly', 'a.', 'Resembling Quakers; Quakerlike; Quakerish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quakery', 'n.', 'Quakerism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quaketail', 'n.', 'A wagtail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quakness', 'n.', 'The state of being quaky; liability to quake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quaking', '', 'a. & n. from Quake, v.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quakingly', 'adv.', 'In a quaking manner; fearfully.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quaky', 'a.', 'Shaky, or tremulous; quaking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Qualifiable', 'a.', 'Capable of being qualified; abatable; modifiable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Qualification', 'n.', 'The act of qualifying, or the condition of being\n   qualified.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Qualification', 'n.', 'That which qualifies; any natural endowment, or any\n   acquirement, which fits a person for a place, office, or employment, or\n   which enables him to sustian any character with success; an enabling\n   quality or circumstance; requisite capacity or possession.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Qualification', 'n.', 'The act of limiting, or the state of being limited;\n   that which qualifies by limiting; modification; restriction; hence,\n   abatement; diminution; as, to use words without any qualification.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Qualificative', 'n.', 'That which qualifies, modifies, or restricts; a\n   qualifying term or statement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Qualificator', 'n.', 'An officer whose business it is to examine and\n   prepare causes for trial in the ecclesiastical courts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Qualified', 'a.', 'Fitted by accomplishments or endowments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Qualified', 'a.', 'Modified; limited; as, a qualified statement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Qualifiedly', 'adv.', 'In the way of qualification; with modification or\n   qualification.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Qualifiedness', 'n.', 'The state of being qualified.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Qualifier', 'n.', 'One who, or that which, qualifies; that which modifies,\n   reduces, tempers or restrains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Qualified', 'imp. & p. p.', 'of Qualify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Qualifying', 'p. pr. & vb. n.', 'of Qualify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Qualify', 'v. t.', 'To make such as is required; to give added or requisite\n   qualities to; to fit, as for a place, office, occupation, or character;\n   to furnish with the knowledge, skill, or other accomplishment necessary\n   for a purpose; to make capable, as of an employment or privilege; to\n   supply with legal power or capacity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Qualify', 'v. t.', 'To give individual quality to; to modulate; to vary; to\n   regulate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Qualify', 'v. t.', 'To reduce from a general, undefined, or comprehensive\n   form, to particular or restricted form; to modify; to limit; to\n   restrict; to restrain; as, to qualify a statement, claim, or\n   proposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Qualify', 'v. t.', 'Hence, to soften; to abate; to diminish; to assuage; to\n   reduce the strength of, as liquors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Qualify', 'v. t.', 'To soothe; to cure; -- said of persons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Qualify', 'v. i.', 'To be or become qualified; to be fit, as for an office\n   or employment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Qualify', 'v. i.', 'To obtain legal power or capacity by taking the oath,\n   or complying with the forms required, on assuming an office.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Qualitative', 'a.', 'Relating to quality; having the character of quality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Qualitied', 'a.', 'Furnished with qualities; endowed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Qualities', 'pl. ', 'of Quality');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quality', 'n.', 'The condition of being of such and such a sort as\n   distinguished from others; nature or character relatively considered,\n   as of goods; character; sort; rank.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quality', 'n.', 'Special or temporary character; profession; occupation;\n   assumed or asserted rank, part, or position.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quality', 'n.', 'That which makes, or helps to make, anything such as it\n   is; anything belonging to a subject, or predicable of it;\n   distinguishing property, characteristic, or attribute; peculiar power,\n   capacity, or virtue; distinctive trait; as, the tones of a flute differ\n   from those of a violin in quality; the great quality of a statesman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quality', 'n.', 'An acquired trait; accomplishment; acquisition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quality', 'n.', 'Superior birth or station; high rank; elevated character.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Qualm', 'n.', 'Sickness; disease; pestilence; death.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Qualm', 'n.', 'A sudden attack of illness, faintness, or pain; an agony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Qualm', 'n.', 'Especially, a sudden sensation of nausea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Qualm', 'n.', 'A prick or scruple of conscience; uneasiness of conscience;\n   compunction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Qualmish', 'a.', 'Sick at the stomach; affected with nausea or sickly\n   languor; inclined to vomit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quamash', 'n.', 'See Camass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quamoclit', 'n.', 'Formerly, a genus of plants including the cypress vine\n   (Quamoclit vulgaris, now called Ipomoea Quamoclit). The genus is now\n   merged in Ipomoea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quandaries', 'pl. ', 'of Quandary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quandary', 'n.', 'A state of difficulty or perplexity; doubt; uncertainty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quandary', 'v. t.', 'To bring into a state of uncertainty, perplexity, or\n   difficulty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quandong', 'n.', 'The edible drupaceous fruit of an Australian tree\n   (Fusanus acuminatus) of the Sandalwood family; -- called also quandang.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quandy', 'n.', 'The old squaw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quannet', 'n.', 'A flat file having the handle at one side, so as to be\n   used like a plane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quant', 'n.', 'A punting pole with a broad flange near the end to prevent\n   it from sinking into the mud; a setting pole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quantic', 'n.', 'A homogeneous algebraic function of two or more variables,\n   in general containing only positive integral powers of the variables,\n   and called quadric, cubic, quartic, etc., according as it is of the\n   second, third, fourth, fifth, or a higher degree. These are further\n   called binary, ternary, quaternary, etc., according as they contain\n   two, three, four, or more variables; thus, the quantic / is a binary\n   cubic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quantification', 'n.', 'Modification by a reference to quantity; the\n   introduction of the element of quantity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quantity', 'v. t.', 'To modify or qualify with respect to quantity; to fix\n   or express the quantity of; to rate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quantitative', 'a.', 'Relating to quantity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quantitive', 'a.', 'Estimable according to quantity; quantitative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quantitively', 'adv.', 'So as to be measurable by quantity; quantitatively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quantities', 'pl. ', 'of Quantity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quantity', 'n.', 'The attribute of being so much, and not more or less; the\n   property of being measurable, or capable of increase and decrease,\n   multiplication and division; greatness; and more concretely, that which\n   answers the question \"How much?\"; measure in regard to bulk or amount;\n   determinate or comparative dimensions; measure; amount; bulk; extent;\n   size.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quantity', 'n.', 'The extent or extension of a general conception, that is,\n   the number of species or individuals to which it may be applied; also,\n   its content or comprehension, that is, the number of its constituent\n   qualities, attributes, or relations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quantity', 'n.', 'The measure of a syllable; that which determines the time\n   in which it is pronounced; as, the long or short quantity of a vowel or\n   syllable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quantity', 'n.', 'The relative duration of a tone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quantity', 'n.', 'That which can be increased, diminished, or measured;\n   especially (Math.), anything to which mathematical processes are\n   applicable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quantity', 'n.', 'A determinate or estimated amount; a sum or bulk; a\n   certain portion or part; sometimes, a considerable amount; a large\n   portion, bulk, or sum; as, a medicine taken in quantities, that is, in\n   large quantities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quantivalence', 'n.', 'Valence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quantivalent', 'a.', 'Of or pertaining to quantivalence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quanta', 'pl. ', 'of Quantum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quantum', 'n.', 'Quantity; amount.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quantum', 'n.', 'A definite portion of a manifoldness, limited by a mark or\n   by a boundary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quap', 'v. i.', 'To quaver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quaquaversal', 'a.', 'Turning or dipping in any or every direction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quaquaversal', 'a.', 'Dipping toward all points of the compass round a\n   center, as beds of lava round a crater.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quar', 'n.', 'A quarry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quarantine', 'n.', 'A space of forty days; -- used of Lent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quarantine', 'n.', 'Specifically, the term, originally of forty days,\n   during which a ship arriving in port, and suspected of being infected a\n   malignant contagious disease, is obliged to forbear all intercourse\n   with the shore; hence, such restraint or inhibition of intercourse;\n   also, the place where infected or prohibited vessels are stationed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quarantine', 'n.', 'The period of forty days during which the widow had the\n   privilege of remaining in the mansion house of which her husband died\n   seized.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quarantined', 'imp. & p. p.', 'of Quarantine');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quarantining', 'p. pr. & vb. n.', 'of Quarantine');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quarantine', 'v. t.', 'To compel to remain at a distance, or in a given\n   place, without intercourse, when suspected of having contagious\n   disease; to put under, or in, quarantine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quarl', 'n.', 'A medusa, or jellyfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quarrel', 'n.', 'An arrow for a crossbow; -- so named because it commonly\n   had a square head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quarrel', 'n.', 'Any small square or quadrangular member');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quarrel', 'n.', 'A square of glass, esp. when set diagonally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quarrel', 'n.', 'A small opening in window tracery, of which the cusps,\n   etc., make the form nearly square.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quarrel', 'n.', 'A square or lozenge-shaped paving tile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quarrel', 'n.', 'A glaziers diamond.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quarrel', 'n.', 'A four-sided cutting tool or chisel having a\n   diamond-shaped end.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quarrel', 'n.', 'A breach of concord, amity, or obligation; a falling out;\n   a difference; a disagreement; an antagonism in opinion, feeling, or\n   conduct; esp., an angry dispute, contest, or strife; a brawl; an\n   altercation; as, he had a quarrel with his father about expenses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quarrel', 'n.', 'Ground of objection, dislike, difference, or hostility;\n   cause of dispute or contest; occasion of altercation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quarrel', 'n.', 'Earnest desire or longing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quarreled', 'imp. & p. p.', 'of Quarrel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quarrelled', '', 'of Quarrel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quarreling', 'p. pr. & vb. n.', 'of Quarrel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quarrelling', '', 'of Quarrel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quarrel', 'v. i.', 'To violate concord or agreement; to have a difference;\n   to fall out; to be or become antagonistic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quarrel', 'v. i.', 'To dispute angrily, or violently; to wrangle; to scold;\n   to altercate; to contend; to fight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quarrel', 'v. i.', 'To find fault; to cavil; as, to quarrel with ones lot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quarrel', 'v. t.', 'To quarrel with.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quarrel', 'v. t.', 'To compel by a quarrel; as, to quarrel a man out of his\n   estate or rights.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quarrel', 'n.', 'One who quarrels or wrangles; one who is quarrelsome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quarrelet', 'n.', 'A little quarrel. See 1st Quarrel, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quarreling', 'a.', 'Engaged in a quarrel; apt or disposed to quarrel; as,\n   quarreling factions; a quarreling mood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quarrellous', 'a.', 'Quarrelsome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quarrelsome', 'a.', 'Apt or disposed to quarrel; given to brawls and\n   contention; easily irritated or provoked to contest; irascible;\n   choleric.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quarried', 'a.', 'Provided with prey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quarrier', 'n.', 'A worker in a stone quarry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quarry', 'n.', 'Same as 1st Quarrel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quarry', 'a.', 'Quadrate; square.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quarries', 'pl. ', 'of Quarry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quarry', 'n.', 'A part of the entrails of the beast taken, given to the\n   hounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quarry', 'n.', 'A heap of game killed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quarry', 'n.', 'The object of the chase; the animal hunted for; game;\n   especially, the game hunted with hawks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quarry', 'v. i.', 'To secure prey; to prey, as a vulture or harpy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quarry', 'n.', 'A place, cavern, or pit where stone is taken from the rock\n   or ledge, or dug from the earth, for building or other purposes; a\n   stone pit. See 5th Mine (a).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quarried', 'imp. & p. p.', 'of Quarry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quarrying', 'p. pr. & vb. n.', 'of Quarry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quarry', 'v. t.', 'To dig or take from a quarry; as, to quarry marble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quarry-faced', 'a.', 'Having a face left as it comes from the quarry and\n   not smoothed with the chisel or point; -- said of stones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quarrymen', 'pl. ', 'of Quarry-man');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quarry-man', 'n.', 'A man who is engaged in quarrying stones; a quarrier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quart', 'n.', 'The fourth part; a quarter; hence, a region of the earth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quart', 'n.', 'A measure of capacity, both in dry and in liquid measure;\n   the fourth part of a gallon; the eighth part of a peck; two pints.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quart', 'n.', 'A vessel or measure containing a quart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quart', 'n.', 'In cards, four successive cards of the same suit. Cf.\n   Tierce, 4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quartan', 'a.', 'Of or pertaining to the fourth; occurring every fourth\n   day, reckoning inclusively; as, a quartan ague, or fever.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quartan', 'n.', 'An intermittent fever which returns every fourth day,\n   reckoning inclusively, that is, one in which the interval between\n   paroxysms is two days.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quartan', 'n.', 'A measure, the fourth part of some other measure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quartane', 'n.', 'Butane, each molecule of which has four carbon atoms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quartation', 'n.', 'The act, process, or result (in the process of parting)\n   of alloying a button of nearly pure gold with enough silver to reduce\n   the fineness so as to allow acids to attack and remove all metals\n   except the gold; -- called also inquartation. Compare Parting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quarte', 'n.', 'Same as 2d Carte.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quartene', 'n.', 'Same as Butylene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quartenylic', 'a.', 'Pertaining to, or designating, an acid of the acrylic\n   acid series, metameric with crotonic acid, and obtained as a colorless\n   liquid; -- so called from having four carbon atoms in the molecule.\n   Called also isocrotonic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quarter', 'n.', 'One of four equal parts into which anything is divided, or\n   is regarded as divided; a fourth part or portion; as, a quarter of a\n   dollar, of a pound, of a yard, of an hour, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quarter', 'n.', 'The fourth of a hundred-weight, being 25 or 28 pounds,\n   according as the hundredweight is reckoned at 100 or 112 pounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quarter', 'n.', 'The fourth of a ton in weight, or eight bushels of grain;\n   as, a quarter of wheat; also, the fourth part of a chaldron of coal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quarter', 'n.', 'The fourth part of the moons period, or monthly\n   revolution; as, the first quarter after the change or full.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quarter', 'n.', 'One limb of a quadruped with the adjacent parts; one\n   fourth part of the carcass of a slaughtered animal, including a leg;\n   as, the fore quarters; the hind quarters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quarter', 'n.', 'That part of a boot or shoe which forms the side, from the\n   heel to the vamp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quarter', 'n.', 'That part on either side of a horses hoof between the toe\n   and heel, being the side of the coffin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quarter', 'n.', 'A term of study in a seminary, college, etc, etc.;\n   properly, a fourth part of the year, but often longer or shorter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quarter', 'n.', 'The encampment on one of the principal passages round a\n   place besieged, to prevent relief and intercept convoys.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quarter', 'n.', 'The after-part of a vessels side, generally corresponding\n   in extent with the quarter-deck; also, the part of the yardarm outside\n   of the slings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quarter', 'n.', 'One of the divisions of an escutcheon when it is divided\n   into four portions by a horizontal and a perpendicular line meeting in\n   the fess point.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quarter', 'v. t.', 'A division of a town, city, or county; a particular\n   district; a locality; as, the Latin quarter in Paris.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quarter', 'v. t.', 'A small upright timber post, used in partitions; -- in\n   the United States more commonly called stud.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quarter', 'v. t.', 'The fourth part of the distance from one point of the\n   compass to another, being the fourth part of 11 15, that is, about 2\n   49; -- called also quarter point.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quarter', 'v. t.', 'Proper station; specific place; assigned position;\n   special location.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quarter', 'v. t.', 'A station at which officers and men are posted in\n   battle; -- usually in the plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quarter', 'v. t.', 'Place of lodging or temporary residence; shelter;\n   entertainment; -- usually in the plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quarter', 'v. t.', 'A station or encampment occupied by troops; a place of\n   lodging for soldiers or officers; as, winter quarters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quarter', 'v. t.', 'Treatment shown by an enemy; mercy; especially, the act\n   of sparing the life a conquered enemy; a refraining from pushing ones\n   advantage to extremes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quarter', 'v. t.', 'Friendship; amity; concord.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quartered', 'imp. & p. p.', 'of Quartter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quartering', 'p. pr. & vb. n.', 'of Quartter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quartter', 'v. t.', 'To divide into four equal parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quartter', 'v. t.', 'To divide; to separate into parts or regions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quartter', 'v. t.', 'To furnish with shelter or entertainment; to supply\n   with the means of living for a time; especially, to furnish shelter to;\n   as, to quarter soldiers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quartter', 'v. t.', 'To furnish as a portion; to allot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quartter', 'v. t.', 'To arrange (different coats of arms) upon one\n   escutcheon, as when a man inherits from both father and mother the\n   right to bear arms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quarter', 'v. i.', 'To lodge; to have a temporary residence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quarter', 'v. i.', 'To drive a carriage so as to prevent the wheels from\n   going into the ruts, or so that a rut shall be between the wheels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quarterage', 'n.', 'A quarterly allowance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quarter-deck', 'n.', 'That part of the upper deck abaft the mainmast,\n   including the poop deck when there is one.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quarterfoil', 'n.', 'An ornamental foliation having four lobes, or foils.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quarterhung', 'a.', 'Having trunnions the axes of which lie below the bore;\n   -- said of a cannon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quartering', 'a.', 'Coming from a point well abaft the beam, but not\n   directly astern; -- said of waves or any moving object.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quartering', 'a.', 'At right angles, as the cranks of a locomotive, which\n   are in planes forming a right angle with each other.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quartering', 'n.', 'A station.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quartering', 'n.', 'Assignment of quarters for soldiers; quarters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quartering', 'n.', 'The division of a shield containing different coats of\n   arms into four or more compartments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quartering', 'n.', 'One of the different coats of arms arranged upon an\n   escutcheon, denoting the descent of the bearer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quartering', 'n.', 'A series of quarters, or small upright posts. See\n   Quarter, n., 1 (m) (Arch.)');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quarterly', 'a.', 'Containing, or consisting of, a fourth part; as,\n   quarterly seasons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quarterly', 'a.', 'Recurring during, or at the end of, each quarter; as,\n   quarterly payments of rent; a quarterly meeting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quarterlies', 'pl. ', 'of Quarterly');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quarterly', 'n.', 'A periodical work published once a quarter, or four\n   times in a year.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quarterly', 'adv.', 'By quarters; once in a quarter of a year; as, the\n   returns are made quarterly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quarterly', 'adv.', 'In quarters, or quarterings; as, to bear arms\n   quarterly; in four or more parts; -- said of a shield thus divided by\n   lines drawn through it at right angles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quartermaster', 'n.', 'An officer whose duty is to provide quarters,\n   provisions, storage, clothing, fuel, stationery, and transportation for\n   a regiment or other body of troops, and superintend the supplies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quartermaster', 'n.', 'A petty officer who attends to the helm, binnacle,\n   signals, and the like, under the direction of the master.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quartern', 'n.', 'A quarter. Specifically: (a) The fourth part of a pint; a\n   gill. (b) The fourth part of a peck, or of a stone (14 ibs.).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quartern', 'n.', 'A loaf of bread weighing about four pounds; -- called\n   also quartern loaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quarteron', 'n.', 'A quarter; esp., a quarter of a pound, or a quarter of a\n   hundred.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quarteron', 'n.', 'Alt. of Quarteroon');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quarteroon', 'n.', 'A quadroon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quarterpace', 'n.', 'A platform of a staircase where the stair turns at a\n   right angle only. See Halfpace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quarter round', '', 'An ovolo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quarterstaves', 'pl. ', 'of Quarterstaff');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quarterstaff', 'n.', 'A long and stout staff formerly used as a weapon of\n   defense and offense; -- so called because in holding it one hand was\n   placed in the middle, and the other between the middle and the end.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quartet', 'n.', 'Alt. of Quartette');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quartette', 'n.', 'A composition in four parts, each performed by a single\n   voice or instrument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quartette', 'n.', 'The set of four person who perform a piece of music in\n   four parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quartette', 'n.', 'A stanza of four lines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quartic', 'a.', 'Of the fourth degree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quartic', 'n.', 'A quantic of the fourth degree. See Quantic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quartic', 'n.', 'A curve or surface whose equation is of the fourth degree\n   in the variables.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quartile', 'n.', 'Same as Quadrate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quartine', 'n.', 'A supposed fourth integument of an ovule, counting from\n   the outside.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quarto', 'a.', 'Having four leaves to the sheet; of the form or size of a\n   quarto.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quartos', 'pl. ', 'of Quarto');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quarto', 'n.', 'Originally, a book of the size of the fourth of sheet of\n   printing paper; a size leaves; in present usage, a book of a square or\n   nearly square form, and usually of large size.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quartridge', 'n.', 'Quarterage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quartz', 'n.', 'A form of silica, or silicon dioxide (SiO2), occurring in\n   hexagonal crystals, which are commonly colorless and transparent, but\n   sometimes also yellow, brown, purple, green, and of other colors; also\n   in cryptocrystalline massive forms varying in color and degree of\n   transparency, being sometimes opaque.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quartziferous', 'a.', 'Consisting chiefly of quartz; containing quartz.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quartzite', 'n.', 'Massive quartz occurring as a rock; a metamorphosed\n   sandstone; -- called also quartz rock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quartzoid', 'n.', 'A form of crystal common with quartz, consisting of two\n   six-sided pyramids, base to base.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quartzose', 'a.', 'Containing, or resembling, quartz; partaking of the\n   nature or qualities of quartz.');
  INSERT INTO words (word, word_type, description) VALUES  (    'quartzous', 'a.', 'Quarzose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quartzy', 'a.', 'Quartzose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quas', 'n.', 'A kind of beer. Same as Quass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quaschi', 'n.', 'Alt. of Quasje');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quasje', 'n.', 'The brown coati. See Coati.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quash', 'n.', 'Same as Squash.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quashed', 'imp. & p. p.', 'of Quash');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quashing', 'p. pr. & vb. n.', 'of Quash');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quash', 'v. t.', 'To abate, annul, overthrow, or make void; as, to quash an\n   indictment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quash', 'v. t.', 'To beat down, or beat in pieces; to dash forcibly; to\n   crush.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quash', 'v. t.', 'To crush; to subdue; to suppress or extinguish summarily\n   and completely; as, to quash a rebellion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quash', 'v. i.', 'To be shaken, or dashed about, with noise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quashee', 'n.', 'A negro of the West Indies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quasi', '', 'As if; as though; as it were; in a manner sense or degree;\n   having some resemblance to; qualified; -- used as an adjective, or a\n   prefix with a noun or an adjective; as, a quasi contract, an implied\n   contract, an obligation which has arisen from some act, as if from a\n   contract; a quasi corporation, a body that has some, but not all, of\n   the peculiar attributes of a corporation; a quasi argument, that which\n   resembles, or is used as, an argument; quasi historical, apparently\n   historical, seeming to be historical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quasimodo', 'n.', 'The first Sunday after Easter; Low Sunday.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quass', 'n.', 'A thin, sour beer, made by pouring warm water on rye or\n   barley meal and letting it ferment, -- much used by the Russians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quassation', 'n.', 'The act of shaking, or the state of being shaken.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quassia', 'n.', 'The wood of several tropical American trees of the order\n   Simarubeae, as Quassia amara, Picraena excelsa, and Simaruba amara. It\n   is intensely bitter, and is used in medicine and sometimes as a\n   substitute for hops in making beer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quassin', 'n.', 'The bitter principle of quassia, extracted as a white\n   crystalline substance; -- formerly called quassite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quat', 'n.', 'A pustule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quat', 'n.', 'An annoying, worthless person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quat', 'v. t.', 'To satiate; to satisfy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quata', 'n.', 'The coaita.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quatch', 'a.', 'Squat; flat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quater-cousin', 'n.', 'A cousin within the first four degrees of kindred.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quaternary', 'a.', 'Consisting of four; by fours, or in sets of four.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quaternary', 'a.', 'Later than, or subsequent to, the Tertiary;\n   Post-tertiary; as, the Quaternary age, or Age of man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quaternary', 'n.', 'The number four.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quaternary', 'n.', 'The Quaternary age, era, or formation. See the Chart of\n   Geology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quaternate', 'a.', 'Composed of, or arranged in, sets of four; quaternary;\n   as, quaternate leaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quaternion', 'n.', 'The number four.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quaternion', 'n.', 'A set of four parts, things, or person; four things\n   taken collectively; a group of four words, phrases, circumstances,\n   facts, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quaternion', 'n.', 'A word of four syllables; a quadrisyllable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quaternion', 'n.', 'The quotient of two vectors, or of two directed right\n   lines in space, considered as depending on four geometrical elements,\n   and as expressible by an algebraic symbol of quadrinomial form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quaternion', 'v. t.', 'To divide into quaternions, files, or companies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quaternity', 'n.', 'The number four.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quaternity', 'n.', 'The union of four in one, as of four persons; --\n   analogous to the theological term trinity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quateron', 'n.', 'See 2d Quarteron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quatorzain', 'n.', 'A poem of fourteen lines; a sonnet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quatorze', 'n.', 'The four aces, kings, queens, knaves, or tens, in the\n   game of piquet; -- so called because quatorze counts as fourteen\n   points.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quatrain', 'n.', 'A stanza of four lines rhyming alternately.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quatre', 'n.', 'A card, die. or domino, having four spots, or pips');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quatrefeuille', 'n.', 'Alt. of Quatrefoil');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quatrefoil', 'n.', 'Same as Quarterfoil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quatuor', 'n.', 'A quartet; -- applied chiefly to instrumental\n   compositions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quave', 'n.', 'See Quaver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quave', 'v. i.', 'To quaver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quavemire', 'n.', 'See Quagmire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quavered', 'imp. & p. p.', 'of Quaver');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quavering', 'p. pr. & vb. n.', 'of Quaver');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quaver', 'v. i.', 'To tremble; to vibrate; to shake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quaver', 'v. i.', 'Especially, to shake the voice; to utter or form sound\n   with rapid or tremulous vibrations, as in singing; also, to trill on a\n   musical instrument');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quaver', 'v. t.', 'To utter with quavers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quaver', 'n.', 'A shake, or rapid and tremulous vibration, of the voice, or\n   of an instrument of music.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quaver', 'n.', 'An eighth note. See Eighth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quaverer', 'n.', 'One who quavers; a warbler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quay', 'n.', 'A mole, bank, or wharf, formed toward the sea, or at the side\n   of a harbor, river, or other navigable water, for convenience in\n   loading and unloading vessels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quay', 'v. t.', 'To furnish with quays.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quayage', 'n.', 'Wharfage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quayd', '', 'p. p. of Quail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Que', 'n.', 'A half farthing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Queach', 'n.', 'A thick, bushy plot; a thicket.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Queach', 'v. i.', 'To stir; to move. See Quick, v. i.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Queachy', 'a.', 'Yielding or trembling under the feet, as moist or boggy\n   ground; shaking; moving.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Queachy', 'a.', 'Like a queach; thick; bushy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quean', 'n.', 'A woman; a young or unmarried woman; a girl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quean', 'n.', 'A low woman; a wench; a slut.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Queasily', 'adv.', 'In a queasy manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Queasiness', 'n.', 'The state of being queasy; nausea; qualmishness;\n   squeamishness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Queasy', 'a.', 'Sick at the stomach; affected with nausea; inclined to\n   vomit; qualmish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Queasy', 'a.', 'Fastidious; squeamish; delicate; easily disturbed;\n   unsettled; ticklish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quebec group', '', 'The middle of the three groups into which the rocks of\n   the Canadian period have been divided in the American Lower Silurian\n   system. See the Chart of Geology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quebracho', 'n.', 'A Chilian apocynaceous tree (Aspidosperma Quebracho);\n   also, its bark, which is used as a febrifuge, and for dyspn/a of the\n   lung, or bronchial diseases; -- called also white quebracho, to\n   distinguish it from the red quebracho, a Mexican anacardiaceous tree\n   (Loxopterygium Lorentzii) whose bark is said to have similar\n   properties.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quebrith', 'n.', 'Sulphur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quech', 'v. i.', 'Alt. of Queck');
  INSERT INTO words (word, word_type, description) VALUES  (    'Queck', 'v. i.', 'A word occurring in a corrupt passage of Bacons Essays,\n   and probably meaning, to stir, to move.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Queen', 'n.', 'The wife of a king.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Queen', 'n.', 'A woman who is the sovereign of a kingdom; a female monarch;\n   as, Elizabeth, queen of England; Mary, queen of Scots.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Queen', 'n.', 'A woman eminent in power or attractions; the highest of her\n   kind; as, a queen in society; -- also used figuratively of cities,\n   countries, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Queen', 'n.', 'The fertile, or fully developed, female of social bees,\n   ants, and termites.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Queen', 'n.', 'The most powerful, and except the king the most important,\n   piece in a set of chessmen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Queen', 'n.', 'A playing card bearing the picture of a queen; as, the queen\n   of spades.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Queen', 'n.', 'A male homosexual, esp. one who is effeminate or dresses in\n   womens clothing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Queen', 'v. i.', 'To act the part of a queen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Queened', 'imp. & p. p.', 'of Queen');
  INSERT INTO words (word, word_type, description) VALUES  (    'Queening', 'p. pr. & vb. n.', 'of Queen');
  INSERT INTO words (word, word_type, description) VALUES  (    'Queen', 'v. i.', 'To make a queen (or other piece, at the players\n   discretion) of by moving it to the eighth row; as, to queen a pawn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Queencraft', 'n.', 'Craft or skill in policy on the part of a queen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Queendom', 'n.', 'The dominion, condition, or character of a queen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Queenfish', 'n.', 'A California sciaenoid food fish (Seriphys politus). The\n   back is bluish, and the sides and belly bright silvery. Called also\n   kingfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Queenhood', 'n.', 'The state, personality, or character of a queen;\n   queenliness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Queening', 'n.', 'Any one of several kinds of apples, as summer queening,\n   scarlet queening, and early queening. An apple called the queening was\n   cultivated in England two hundred years ago.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Queenliness', 'n.', 'The quality of being queenly; the; characteristic of a\n   queen; stateliness; eminence among women in attractions or power.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Queenly', 'a.', 'Like, becoming, or suitable to, a queen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Oueen-post', 'n.', 'One of two suspending posts in a roof truss, or other\n   framed truss of similar form. See King-post.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Queenship', 'n.', 'The state, rank, or dignity of a queen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Queensland nut', '', 'The nut of an Australian tree (Macadamia ternifolia).\n   It is about an inch in diameter, and contains a single round edible\n   seed, or sometimes two hemispherical seeds. So called from Queensland\n   in Australia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Queen truss', '', 'A truss framed with queen-posts; a queen-post truss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Queer', 'a.', 'At variance with what is usual or normal; differing in some\n   odd way from what is ordinary; odd; singular; strange; whimsical; as, a\n   queer story or act.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Queer', 'a.', 'Mysterious; suspicious; questionable; as, a queer\n   transaction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Queer', 'n.', 'Counterfeit money.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Queerish', 'a.', 'Rather queer; somewhat singular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Queerly', 'adv.', 'In a queer or odd manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Queerness', 'n.', 'The quality or state of being queer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Queest', 'n.', 'The European ringdove (Columba palumbus); the cushat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quegh', 'n.', 'A drinking vessel. See Quaich.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Queint', 'a.', 'See Quaint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Queint', '', 'imp. & p. p. of Quench.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Queintise', 'n.', 'See Quaintise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quelled', 'imp. & p. p.', 'of Quell');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quelling', 'p. pr. & vb. n.', 'of Quell');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quell', 'v. i.', 'To die.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quell', 'v. i.', 'To be subdued or abated; to yield; to abate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quell', 'v. t.', 'To take the life of; to kill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quell', 'v. t.', 'To overpower; to subdue; to put down.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quell', 'v. t.', 'To quiet; to allay; to pacify; to cause to yield or\n   cease; as, to quell grief; to quell the tumult of the soul.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quell', 'n.', 'Murder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Queller', 'n.', 'A killer; as, Jack the Giant Queller.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Queller', 'n.', 'One who quells; one who overpowers or subdues.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quellio', 'n.', 'A ruff for the neck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quelquechose', 'n.', 'A trifle; a kickshaw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Queme', 'v. t. & i.', 'To please.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quemeful', 'a.', 'Kindly; merciful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quenched', 'imp. & p. p.', 'of Quench');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quenching', 'p. pr. & vb. n.', 'of Quench');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quench', 'v. t.', 'To extinguish; to overwhelm; to make an end of; -- said\n   of flame and fire, of things burning, and figuratively of sensations\n   and emotions; as, to quench flame; to quench a candle; to quench\n   thirst, love, hate, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quench', 'v. t.', 'To cool suddenly, as heated steel, in tempering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quench', 'v. i.', 'To become extinguished; to go out; to become calm or\n   cool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quenchable', 'a.', 'Capable of being quenched.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quencher', 'n.', 'One who, or that which, quenches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quenchless', 'a.', 'Incapable of being quenched; inextinguishable; as,\n   quenchless fire or fury.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quenelle', 'n.', 'A kind of delicate forcemeat, commonly poached and used\n   as a dish by itself or for garnishing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quenouille training', '', 'A method of training trees or shrubs in the\n   shape of a cone or distaff by tying down the branches and pruning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quercitannic', 'a.', 'Pertaining to, or designating, a tannic acid found in\n   oak bark and extracted as a yellowish brown amorphous substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quercite', 'n.', 'A white crystalline substance, C6H7(OH)5, found in\n   acorns, the fruit of the oak (Quercus). It has a sweet taste, and is\n   regarded as a pentacid alcohol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quercitin', 'n.', 'A yellow crystalline substance, occurring quite widely\n   distributed in the vegetable kingdom, as is apple-tree bark,\n   horse-chestnut leaves, etc., but originally obtained by the\n   decomposition of quercitrin. Called also meletin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quercitrin', 'n.', 'A glucoside extracted from the bark of the oak\n   (Quercus) as a bitter citron-yellow crystalline substance, used as a\n   pigment and called quercitron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quercitron', 'n.', 'The yellow inner bark of the Quercus tinctoria, the\n   American black oak, yellow oak, dyers oak, or quercitron oak, a large\n   forest tree growing from Maine to eastern Texas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quercitron', 'n.', 'Quercitrin, used as a pigment. See Quercitrin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quercus', 'n.', 'A genus of trees constituted by the oak. See Oak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Querele', 'n.', 'A complaint to a court. See Audita Querela.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Querent', 'n.', 'A complainant; a plaintiff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Querent', 'n.', 'An inquirer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quermonious', 'a.', 'Complaining; querulous; apt to complain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Querimony', 'n.', 'A complaint or complaining.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Querist', 'n.', 'One who inquires, or asks questions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Querken', 'v. t.', 'To stifle or choke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Querl', 'v. t.', 'To twirl; to turn or wind round; to coil; as, to querl a\n   cord, thread, or rope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Querl', 'n.', 'A coil; a twirl; as, the qwerl of hair on the fore leg of a\n   blooded horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quern', 'n.', 'A mill for grinding grain, the upper stone of which was\n   turned by hand; -- used before the invention of windmills and\n   watermills.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Querpo', 'n.', 'The inner or body garments taken together. See Cuerpo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Querquedule', 'n.', 'A teal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Querquedule', 'n.', 'The pintail duck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Querry', 'n.', 'A groom; an equerry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Querulential', 'a.', 'Querulous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Querulous', 'v.', 'Given to quarreling; quarrelsome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Querulous', 'v.', 'Apt to find fault; habitually complaining; disposed to\n   murmur; as, a querulous man or people.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Querulous', 'v.', 'Expressing complaint; fretful; whining; as, a querulous\n   tone of voice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Queries', 'pl. ', 'of Query');
  INSERT INTO words (word, word_type, description) VALUES  (    'Query', 'n.', 'A question; an inquiry to be answered or solved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Query', 'n.', 'A question in the mind; a doubt; as, I have a query about\n   his sincerity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Query', 'n.', 'An interrogation point [?] as the sign of a question or a\n   doubt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Query', 'v. i.', 'To ask questions; to make inquiry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Query', 'v. i.', 'To have a doubt; as, I query if he is right.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Queried', 'imp. & p. p.', 'of Query');
  INSERT INTO words (word, word_type, description) VALUES  (    'Querying', 'p. pr. & vb. n.', 'of Query');
  INSERT INTO words (word, word_type, description) VALUES  (    'Query', 'v. t.', 'To put questions about; to elicit by questioning; to\n   inquire into; as, to query the items or the amount; to query the motive\n   or the fact.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Query', 'v. t.', 'To address questions to; to examine by questions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Query', 'v. t.', 'To doubt of; to regard with incredulity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Query', 'v. t.', 'To write \" query\" (qu., qy., or ?) against, as a doubtful\n   spelling, or sense, in a proof. See Quaere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quesal', 'n.', 'The long-tailed, or resplendent, trogon (Pharomachus\n   mocinno, formerly Trogon resplendens), native of Southern Mexico and\n   Central America. Called also quetzal, and golden trogon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quest', 'n.', 'The act of seeking, or looking after anything; attempt to\n   find or obtain; search; pursuit; as, to rove in quest of game, of a\n   lost child, of property, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quest', 'n.', 'Request; desire; solicitation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quest', 'n.', 'Those who make search or inquiry, taken collectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quest', 'n.', 'Inquest; jury of inquest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quest', 'n.', 'To search for; to examine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quest', 'v. i.', 'To go on a quest; to make a search; to go in pursuit; to\n   beg.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Questant', 'n.', 'One who undertakes a quest; a seeker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quester', 'n.', 'One who seeks; a seeker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Question', 'n.', 'The act of asking; interrogation; inquiry; as, to examine\n   by question and answer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Question', 'n.', 'Discussion; debate; hence, objection; dispute; doubt; as,\n   the story is true beyond question; he obeyed without question.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Question', 'n.', 'Examination with reference to a decisive result;\n   investigation; specifically, a judicial or official investigation;\n   also, examination under torture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Question', 'n.', 'That which is asked; inquiry; interrogatory; query.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Question', 'n.', 'Hence, a subject of investigation, examination, or\n   debate; theme of inquiry; matter to be inquired into; as, a delicate or\n   doubtful question.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Question', 'n.', 'Talk; conversation; speech; speech.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Questioned', 'imp. & p. p.', 'of Question');
  INSERT INTO words (word, word_type, description) VALUES  (    'Questioning', 'p. pr. & vb. n.', 'of Question');
  INSERT INTO words (word, word_type, description) VALUES  (    'Question', 'n.', 'To ask questions; to inquire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Question', 'n.', 'To argue; to converse; to dispute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Question', 'v. t.', 'To inquire of by asking questions; to examine by\n   interrogatories; as, to question a witness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Question', 'v. t.', 'To doubt of; to be uncertain of; to query.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Question', 'v. t.', 'To raise a question about; to call in question; to\n   make objection to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Question', 'v. t.', 'To talk to; to converse with.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Questionability', 'n.', 'The state or condition of being questionable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Questionable', 'a.', 'Admitting of being questioned; inviting, or seeming\n   to invite, inquiry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Questionable', 'a.', 'Liable to question; subject to be doubted or called\n   in question; problematical; doubtful; suspicious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Questionableness', 'n.', 'The quality or state of being questionable,\n   doubtful, or suspicious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Questionably', 'adv.', 'In a questionable manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Questionary', 'a.', 'Inquiring; asking questions; testing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Questionary', 'n.', 'One who makes it his business to seek after relics and\n   carry them about for sale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Questioner', 'n.', 'One who asks questions; an inquirer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Questionist', 'n.', 'A questioner; an inquirer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Questionist', 'n.', 'A candidate for honors or degrees who is near the time\n   of his examination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Questionless', 'a.', 'Unquestioning; incurious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Questionless', 'adv.', 'Beyond a question or doubt; doubtless; certainly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Questmen', 'pl. ', 'of Questman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Questman', 'n.', 'One legally empowered to make quest of certain matters,\n   esp. of abuses of weights and measures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Questman', 'n.', 'A churchwardens assistant; a sidesman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Questman', 'n.', 'A collector of parish rents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Questmonger', 'n.', 'One who lays informations, and encourages petty\n   lawsuits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Questor', 'n.', 'An officer who had the management of the public treasure;\n   a receiver of taxes, tribute, etc.; treasurer of state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Questorship', 'n.', 'The office, or the term of office, of a questor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Questrist', 'n.', 'A seeker; a pursuer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Questuary', 'a.', 'Studious of profit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Questuary', 'n.', 'One employed to collect profits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quet', 'n.', 'The common guillemot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Queue', 'n.', 'A tail-like appendage of hair; a pigtail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Queue', 'n.', 'A line of persons waiting anywhere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Queue', 'v. t.', 'To fasten, as hair, in a queue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quey', 'n.', 'A heifer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quib', 'n.', 'A quip; a gibe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quibble', 'n.', 'A shift or turn from the point in question; a trifling or\n   evasive distinction; an evasion; a cavil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quibble', 'n.', 'A pun; a low conceit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quibbled', 'imp. & p. p.', 'of Quibble');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quibbling', 'p. pr. & vb. n.', 'of Quibble');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quibble', 'v. i.', 'To evade the point in question by artifice, play upon\n   words, caviling, or by raising any insignificant or impertinent\n   question or point; to trifle in argument or discourse; to equivocate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quibble', 'v. i.', 'To pun; to practice punning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quibbler', 'n.', 'One who quibbles; a caviler; also, a punster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quibblingly', 'adv.', 'Triflingly; evasively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quica', 'n.', 'A small South American opossum (Didelphys quica), native of\n   Guiana and Brazil. It feeds upon insects, small birds, and fruit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quice', 'n.', 'See Queest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quich', 'v. i.', 'To stir.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quick', 'superl.', 'Alive; living; animate; -- opposed to dead or\n   inanimate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quick', 'superl.', 'Characterized by life or liveliness; animated;\n   sprightly; agile; brisk; ready.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quick', 'superl.', 'Speedy; hasty; swift; not slow; as, be quick.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quick', 'superl.', 'Impatient; passionate; hasty; eager; eager; sharp;\n   unceremonious; as, a quick temper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quick', 'superl.', 'Fresh; bracing; sharp; keen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quick', 'superl.', 'Sensitive; perceptive in a high degree; ready; as, a\n   quick ear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quick', 'superl.', 'Pregnant; with child.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quick', 'adv.', 'In a quick manner; quickly; promptly; rapidly; with haste;\n   speedily; without delay; as, run quick; get back quick.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quick', 'n.', 'That which is quick, or alive; a living animal or plant;\n   especially, the hawthorn, or other plants used in making a living\n   hedge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quick', 'n.', 'The life; the mortal point; a vital part; a part susceptible\n   of serious injury or keen feeling; the sensitive living flesh; the part\n   of a finger or toe to which the nail is attached; the tender emotions;\n   as, to cut a finger nail to the quick; to thrust a sword to the quick,\n   to taunt one to the quick; -- used figuratively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quick', 'n.', 'Quitch grass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quick', 'v. t. & i.', 'To revive; to quicken; to be or become alive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quickbeam', 'n.', 'See Quicken tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'quickened', 'imp. & p. p.', 'of Quicken');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quickening', 'p. pr. & vb. n.', 'of Quicken');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quicken', 'a.', 'To make alive; to vivify; to revive or resuscitate, as\n   from death or an inanimate state; hence, to excite; to, stimulate; to\n   incite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quicken', 'a.', 'To make lively, active, or sprightly; to impart additional\n   energy to; to stimulate; to make quick or rapid; to hasten; to\n   accelerate; as, to quicken ones steps or thoughts; to quicken ones\n   departure or speed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quicken', 'a.', 'To shorten the radius of (a curve); to make (a curve)\n   sharper; as, to quicken the sheer, that is, to make its curve more\n   pronounced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quicken', 'v. i.', 'To come to life; to become alive; to become vivified or\n   enlivened; hence, to exhibit signs of life; to move, as the fetus in\n   the womb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quicken', 'v. i.', 'To move with rapidity or activity; to become\n   accelerated; as, his pulse quickened.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quickener', 'n.', 'One who, or that which, quickens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quickening', 'n.', 'The act or process of making or of becoming quick.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quickening', 'n.', 'The first motion of the fetus in the womb felt by the\n   mother, occurring usually about the middle of the term of pregnancy. It\n   has been popularly supposed to be due to the fetus becoming possessed\n   of independent life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quickens', 'n.', 'Quitch grass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quicken tree', '', 'The European rowan tree; -- called also quickbeam, and\n   quickenbeam. See Rowan tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quickhatch', 'n.', 'The wolverine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quicklime', 'a.', 'Calcium oxide; unslacked lime; -- so called because when\n   wet it develops great heat. See 4th Lime, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quickly', 'adv.', 'Speedily; with haste or celerity; soon; without delay;\n   quick.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quickness', 'n.', 'The condition or quality of being quick or living; life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quickness', 'n.', 'Activity; briskness; especially, rapidity of motion;\n   speed; celerity; as, quickness of wit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quickness', 'n.', 'Acuteness of perception; keen sensibility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quickness', 'n.', 'Sharpness; pungency of taste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quicksand', 'n.', 'Sand easily moved or readily yielding to pressure;\n   especially, a deep mass of loose or moving sand mixed with water,\n   sometimes found at the mouth of a river or along some coasts, and very\n   dangerous, from the difficulty of extricating a person who begins\n   sinking into it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quick-scented', 'a.', 'Acute of smell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quickset', 'n.', 'A living plant set to grow, esp. when set for a hedge;\n   specifically, the hawthorn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quickset', 'a.', 'Made of quickset.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quickset', 'v. t.', 'To plant with living shrubs or trees for a hedge; as,\n   to quickset a ditch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quick-sighted', 'a.', 'Having quick sight or acute discernment; quick to\n   see or to discern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quicksilver', 'a.', 'The metal mercury; -- so called from its resemblance\n   to liquid silver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quicksilvered', 'a.', 'Overlaid with quicksilver, or with an amalgam of\n   quicksilver and tinfoil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quicksilvering', 'n.', 'The mercury and foil on the back of a\n   looking-glass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quickstep', 'n.', 'A lively, spirited march; also, a lively style of\n   dancing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quick-witted', 'a.', 'Having ready wit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quick-wittedness', 'n.', 'Readiness of wit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quickwork', 'n.', 'All the submerged section of a vessels planking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quickwork', 'n.', 'The planking between the spirketing and the clamps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quickwork', 'n.', 'The short planks between the portholes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quid', 'n.', 'A portion suitable to be chewed; a cud; as, a quid of\n   tobacco.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quid', 'v. t.', 'To drop from the mouth, as food when partially chewed; --\n   said of horses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quidam', 'n.', 'Somebody; one unknown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quiddany', 'n.', 'A confection of quinces, in consistency between a sirup\n   and marmalade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quiddative', 'a.', 'Constituting, or containing, the essence of a thing;\n   quidditative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quiddit', 'n.', 'A subtilty; an equivocation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quidditative', 'a.', 'Quiddative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quiddities', 'pl. ', 'of Quiddity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quiddity', 'n.', 'The essence, nature, or distinctive peculiarity, of a\n   thing; that which answers the question, Quid est? or, What is it?');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quiddity', 'n.', 'A trifling nicety; a cavil; a quibble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quiddled', 'imp. & p. p.', 'of Quiddle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quiddling', 'p. pr. & vb. n.', 'of Quiddle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quiddle', 'v. i.', 'To spend time in trifling employments, or to attend to\n   useful subjects in an indifferent or superficial manner; to dawdle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quiddle', 'n.', 'Alt. of Quiddler');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quiddler', 'n.', 'One who wastes his energy about trifles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quidnunc', 'n.', 'One who is curious to know everything that passes; one\n   who knows, or pretends to know, all that is going on.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quiesced', 'imp. & p. p.', 'of Quiesce');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quiescing', 'p. pr. & vb. n.', 'of Quiesce');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quiesce', 'a. & n.', 'To be silent, as a letter; to have no sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quiescence', 'n.', 'Alt. of Quiescency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quiescency', 'n.', 'The state or quality of being quiescent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quiescent', 'a.', 'Being in a state of repose; at rest; still; not moving;\n   as, a quiescent body or fluid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quiescent', 'a.', 'Not ruffed with passion; unagitated; not in action; not\n   excited; quiet; dormant; resting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quiescent', 'a.', 'Not sounded; silent; as, y is quiescent in \"day\" and\n   \"say.\"');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quiescent', 'n.', 'A silent letter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quiescently', 'adv.', 'In a quiescent manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quiet', 'a.', 'In a state of rest or calm; without stir, motion, or\n   agitation; still; as, a quiet sea; quiet air.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quiet', 'a.', 'Free from noise or disturbance; hushed; still.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quiet', 'a.', 'Not excited or anxious; calm; peaceful; placid; settled; as,\n   a quiet life; a quiet conscience.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quiet', 'a.', 'Not giving offense; not exciting disorder or trouble; not\n   turbulent; gentle; mild; meek; contented.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quiet', 'a.', 'Not showy; not such as to attract attention;\n   undemonstrative; as, a quiet dress; quiet colors; a quiet movement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quiet', 'a.', 'The quality or state of being quiet, or in repose; as an\n   hour or a time of quiet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quiet', 'a.', 'Freedom from disturbance, noise, or alarm; stillness;\n   tranquillity; peace; security.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quieted', 'imp. & p. p.', 'of Quiet');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quieting', 'p. pr. & vb. n.', 'of Quiet');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quiet', 'v. t.', 'To stop motion in; to still; to reduce to a state of\n   rest, or of silence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quiet', 'v. t.', 'To calm; to appease; to pacify; to lull; to allay; to\n   tranquillize; as, to quiet the passions; to quiet clamors or disorders;\n   to quiet pain or grief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quiet', 'v. i.', 'To become still, silent, or calm; -- often with down; as,\n   be soon quieted down.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quietage', 'n.', 'Quietness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quieter', 'n.', 'One who, or that which, quiets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quietism', 'n.', 'Peace or tranquillity of mind; calmness; indifference;\n   apathy; dispassion; indisturbance; inaction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quietism', 'n.', 'The system of the Quietists, who maintained that religion\n   consists in the withdrawal of the mind from worldly interests and\n   anxieties and its constant employment in the passive contemplation of\n   God and his attributes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quietist', 'n.', 'One of a sect of mystics originated in the seventeenth\n   century by Molinos, a Spanish priest living in Rome. See Quietism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quietistic', 'a.', 'Of or pertaining to the Quietists, or to Quietism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quietly', 'adv.', 'In a quiet state or manner; without motion; in a state\n   of rest; as, to lie or sit quietly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quietly', 'adv.', 'Without tumult, alarm, dispute, or disturbance;\n   peaceably; as, to live quietly; to sleep quietly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quietly', 'adv.', 'Calmly, without agitation or violent emotion; patiently;\n   as, to submit quietly to unavoidable evils.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quietly', 'adv.', 'Noiselessly; silently; without remark or violent\n   movement; in a manner to attract little or no observation; as, he\n   quietly left the room.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quietness', 'n.', 'The quality or state of being quiet; freedom from noise,\n   agitation, disturbance, or excitement; stillness; tranquillity;\n   calmness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quietsome', 'a.', 'Calm; still.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quietude', 'n.', 'Rest; repose; quiet; tranquillity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quietus', 'a.', 'Final discharge or acquittance, as from debt or\n   obligation; that which silences claims; (Fig.) rest; death.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quill', 'n.', 'One of the large feathers of a birds wing, or one of the\n   rectrices of the tail; also, the stock of such a feather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quill', 'n.', 'A pen for writing made by sharpening and splitting the point\n   or nib of the stock of a feather; as, history is the proper subject of\n   his quill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quill', 'n.', 'A spine of the hedgehog or porcupine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quill', 'n.', 'The pen of a squid. See Pen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quill', 'n.', 'The plectrum with which musicians strike the strings of\n   certain instruments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quill', 'n.', 'The tube of a musical instrument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quill', 'n.', 'Something having the form of a quill');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quill', 'n.', 'The fold or plain of a ruff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quill', 'n.', 'A spindle, or spool, as of reed or wood, upon which the\n   thread for the woof is wound in a shuttle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quill', 'n.', 'A hollow spindle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quilled', 'imp. & p. p.', 'of Quill');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quilling', 'p. pr. & vb. n.', 'of Quill');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quill', 'v. t.', 'To plaint in small cylindrical ridges, called quillings;\n   as, to quill a ruffle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quill', 'v. t.', 'To wind on a quill, as thread or yarn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quillaia bark', '', 'The bark of a rosaceous tree (Quillaja Saponaria),\n   native of Chili. The bark is finely laminated, and very heavy with\n   alkaline substances, and is used commonly by the Chilians instead of\n   soap. Also called soap bark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quillback', 'n.', 'An American fresh-water fish (Ictiobus, / Carpiodes,\n   cyprinus); -- called also carp sucker, sailfish, spearfish, and\n   skimback.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quilled', 'a.', 'Furnished with quills; also, shaped like quills.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quillet', 'n.', 'Subtilty; nicety; quibble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quilling', 'n.', 'A band of linen, muslin, or the like, fluted, folded, or\n   plaited so as somewhat to resemble a row of quills.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quilling', 'n.', 'One of the rounded plaits or flutings of such a band.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quillwort', 'n.', 'Any plant or species of the genus Isoetes, cryptogamous\n   plants with a cluster of elongated four-tubed rushlike leaves, rising\n   from a corm, and containing spores in their enlarged and excavated\n   bases. There are about seventeen American species, usually growing in\n   the mud under still, shallow water. So called from the shape of the\n   shape of the leaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quilt', 'n.', 'Anything that is quilted; esp., a quilted bed cover, or a\n   skirt worn by women; any cover or garment made by putting wool, cotton,\n   etc., between two cloths and stitching them together; also, any outer\n   bed cover.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quilted', 'imp. & p. p.', 'of Quilt');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quilting', 'p. pr. & vb. n.', 'of Quilt');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quilt', 'v. t.', 'To stitch or sew together at frequent intervals, in order\n   to confine in place the several layers of cloth and wadding of which a\n   garment, comforter, etc., may be made; as, to quilt a coat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quilt', 'v. t.', 'To wad, as a garment, with warm soft material.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quilt', 'v. t.', 'To stitch or sew in lines or patterns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quilter', 'n.', 'One who, or that which, quilts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quilting', 'n.', 'The act of stitching or running in patterns, as in making\n   a quilt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quilting', 'n.', 'A quilting bee. See Bee, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quilting', 'n.', 'The material used for making quilts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quilting', 'n.', 'A coating of strands of rope for a water vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quin', 'n.', 'A European scallop (Pecten opercularis), used as food.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quinaldine', 'n.', 'A colorless liquid of a slightly pungent odor,\n   C9H6N.CH3, first obtained as a condensation product of aldehyde and\n   aniline, and regarded as a derivative of quinoline; -- called also\n   methyl quinoline.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quinary', 'a.', 'Consisting of five; arranged by fives.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quiname', 'a.', 'Growing in sets of five; -- said especially of leaves\n   composed of five leaflets set at the end of a common petiole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quinate', 'n.', 'A salt of quinic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quinazol', 'n.', 'A complex nitrogenous base related to cinnoline.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quince', 'n.', 'The fruit of a shrub (Cydonia vulgaris) belonging to the\n   same tribe as the apple. It somewhat resembles an apple, but differs in\n   having many seeds in each carpel. It has hard flesh of high flavor, but\n   very acid, and is largely used for marmalade, jelly, and preserves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quince', 'n.', 'a quince tree or shrub.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quincewort', 'n.', 'The squinancy. Called also quinsywort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quinch', 'v. i.', 'To stir; to wince.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quincuncial', '', 'Having the form of a quincunx.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quincuncial', '', 'Having the leaves of a pentamerous calyx or corolla so\n   imbricated that two are exterior, two are interior, and the other has\n   one edge exterior and one interior; as, quincuncial aestivation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quincuncially', 'adv.', 'In the manner or order of a quincunx.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quincunx', 'n.', 'An arrangement of things by fives in a square or a\n   rectangle, one being placed at each corner and one in the middle;\n   especially, such an arrangement of trees repeated indefinitely, so as\n   to form a regular group with rows running in various directions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quincunx', 'n.', 'The position of planets when distant from each other five\n   signs, or 150.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quincunx', 'n.', 'A quincuncial arrangement, as of the parts of a flower in\n   aestivation. See Quincuncial, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quindecagon', 'n.', 'A plane figure with fifteen angles, and consequently\n   fifteen sides.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quindecemvirs', 'pl. ', 'of Quindecemvir');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quindecemviri', 'pl. ', 'of Quindecemvir');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quindecemvir', 'n.', 'One of a sacerdotal college of fifteen men whose\n   chief duty was to take care of the Sibylline books.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quindecemvirate', 'n.', 'The body or office of the quindecemviri.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quindecone', 'n.', 'An unsaturated hydrocarbon, C15H26, of the valylene\n   series, produced artificially as an oily liquid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quindecylic', 'n.', 'Pertaining to, or designating, an acid of the fatty\n   acid series, containing fifteen atoms of carbon; called also\n   pentadecylic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quindem', 'n.', 'A fifteenth part.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quindism', 'n.', 'A fifteenth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quinhydrone', 'n.', 'A green crystalline substance formed by the union of\n   quinone with hydroquinone, or as an intermediate product in the\n   oxidation of hydroquinone or the reduction of quinone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quinia', 'n.', 'Quinine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quinible', 'n.', 'An interval of a fifth; also, a part sung with such\n   intervals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quinic', 'a.', 'Pertaining to, derived from, or connected with, quinine and\n   related compounds; specifically, designating a nonnitrogenous acid\n   obtained from cinchona bark, coffee, beans, etc., as a white\n   crystalline substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quinicine', 'n.', 'An uncrystallizable alkaloid obtained by the action of\n   heat from quinine, with which it is isomeric.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quinidine', 'n.', 'An alkaloid isomeric with, and resembling, quinine,\n   found in certain species of cinchona, from which it is extracted as a\n   bitter white crystalline substance; conchinine. It is used somewhat as\n   a febrifuge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quinine', 'n.', 'An alkaloid extracted from the bark of several species of\n   cinchona (esp. Cinchona Calisaya) as a bitter white crystalline\n   substance, C20H24N2O2. Hence, by extension (Med.), any of the salts of\n   this alkaloid, as the acetate, chloride, sulphate, etc., employed as a\n   febrifuge or antiperiodic. Called also quinia, quinina, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quininic', 'a.', 'Pertaining to, or designating, a nitrogenous acid\n   obtained as a yellow crystalline substance by the oxidation of quinine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quininism', 'n.', 'Alt. of Quinism');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quinism', 'n.', 'See Cinchonism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quinizarin', '', 'A yellow crystalline substance produced artificially. It\n   is isomeric with alizarin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quinizine', 'n.', 'any one of a series of nitrogenous bases, certain of\n   which are used as antipyretics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quinnat', 'n.', 'The California salmon (Oncorhynchus choicha); -- called\n   also chouicha, king salmon, chinnook salmon, and Sacramento salmon. It\n   is of great commercial importance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quinoa', 'n.', 'The seeds of a kind of goosewort (Chenopodium Quinoa), used\n   in Chili and Peru for making porridge or cakes; also, food thus made.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quinogen', 'n.', 'A hypothetical radical of quinine and related alkaloids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quinoidine', 'n.', 'A brownish resinous substance obtained as a by-product\n   in the treatment of cinchona bark. It consists of a mixture of several\n   alkaloids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quinoline', 'n.', 'A nitrogenous base, C9H7N obtained as a pungent\n   colorless liquid by the distillation of alkaloids, bones, coal tar,\n   etc. It the nucleus of many organic bodies, especially of certain\n   alkaloids and related substances; hence, by extension, any one of the\n   series of alkaloidal bases of which quinoline proper is the type.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quinologist', 'n.', 'One who is versed in quinology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quinology', 'n.', 'The science which treats of the cultivation of the\n   cinchona, and of its use in medicine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quinone', 'n.', 'A crystalline substance, C6H4O2 (called also benzoketone),\n   first obtained by the oxidation of quinic acid and regarded as a double\n   ketone; also, by extension, any one of the series of which quinone\n   proper is the type.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quinovic', 'a.', 'Pertaining to, or designating, a crystalline acid\n   obtained from some varieties of cinchona bark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quinovin', 'n.', 'An amorphous bitter glucoside derived from cinchona and\n   other barks. Called also quinova bitter, and quinova.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quinoxaline', 'n.', 'Any one of a series of complex nitrogenous bases\n   obtained by the union of certain aniline derivatives with glyoxal or\n   with certain ketones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quinoxyl', 'n.', 'The hypothetical radical of certain quinone derivatives\n   related to rhodizonic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quinoyl', 'n.', 'A radical of which quinone is the hydride, analogous to\n   phenyl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quinquagesima', 'a.', 'Fiftieth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quinquangular', 'a.', 'Having five angles or corners.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quinquarticular', 'a.', 'Relating to the five articles or points; as, the\n   quinquarticular controversy between Arminians and Calvinists.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quinque-', '', 'A combining form meaning five, five times, fivefold; as,\n   quinquefid, five-cleft; quinquedentate, five-toothed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quinqueangled', 'a.', 'Having five angles; quinquangular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quinquedentate', 'a.', 'Alt. of Quinquedentated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quinquedentated', 'a.', 'Five-toothed; as, a quinquedentate leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quinquefarious', 'a.', 'Arranged in five vertical rows; pentastichous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quinquefid', 'a.', 'Sharply cut about halfway to the middle or base into\n   five segments; as, a quinquefid leaf or corolla.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quinquefoliate', 'a.', 'Alt. of Quinquefoliated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quinquefoliated', 'a.', 'Having five leaves or leaflets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quinque foliolate', 'a.', 'Having five leaflets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quinqueliteral', 'a.', 'Consisting of five letters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quinquelobate', 'a.', 'Alt. of Quinquelobared');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quinquelobared', 'a.', 'Cut less than halfway into portions, usually\n   somewhat rounded; five-lobed; as, a quinquelobate leaf or corolla.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quinquelobed', 'a.', 'Same as Quinquelobate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quinquelocular', 'a.', 'Having five cells or loculi; five-celled; as, a\n   quinquelocular pericarp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quinquenerved', 'a.', 'Having five nerves; -- said of a leaf with five\n   nearly equal nerves or ribs rising from the end of the petiole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quinquennalia', 'n. pl.', 'Public games celebrated every five years.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quinquennial', 'a.', 'Occurring once in five years, or at the end of every\n   five years; also, lasting five years. A quinquennial event.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quinquennium', 'n.', 'Space of five years.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quinquepartite', 'a.', 'Consisting of five parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quinquepartite', 'a.', 'Divided into five parts almost to the base.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quinquereme', 'n.', 'A galley having five benches or banks of oars; as, an\n   Athenian quinquereme.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quinquesyllable', 'n.', 'A word of five syllables.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quinquevalve', 'a.', 'Alt. of Quinquevalvular');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quinquevalvular', 'a.', 'Having five valves, as a pericarp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quinquevirs', 'pl. ', 'of Quinquevir');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quinqueviri', 'pl. ', 'of Quinquevir');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quinquevir', 'n.', 'One of five commissioners appointed for some special\n   object.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quinquina', 'n.', 'Peruvian bark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quinquivalent', 'a.', 'Same as Pentavalent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quinsy', 'n.', 'An inflammation of the throat, or parts adjacent,\n   especially of the fauces or tonsils, attended by considerable swelling,\n   painful and impeded deglutition, and accompanied by inflammatory fever.\n   It sometimes creates danger of suffocation; -- called also squinancy,\n   and squinzey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quint', 'n.', 'A set or sequence of five, as in piquet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quint', 'n.', 'The interval of a fifth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quintain', 'n.', 'An object to be tilted at; -- called also quintel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quintal', 'n.', 'A hundredweight, either 112 or 100 pounds, according to\n   the scale used. Cf. Cental.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quintal', 'n.', 'A metric measure of weight, being 100,000 grams, or 100\n   kilograms, equal to 220.46 pounds avoirdupois.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quintan', 'a.', 'Occurring as the fifth, after four others also, occurring\n   every fifth day, reckoning inclusively; as, a quintan fever.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quintan', 'n.', 'An intermittent fever which returns every fifth day,\n   reckoning inclusively, or in which the intermission lasts three days.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quintel', 'n.', 'See Quintain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quintessence', 'n.', 'The fifth or last and highest essence or power in a\n   natural body. See Ferment oils, under Ferment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quintessence', 'n.', 'Hence: An extract from anything, containing its\n   rarest virtue, or most subtle and essential constituent in a small\n   quantity; pure or concentrated essence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quintessence', 'v. t.', 'To distil or extract as a quintessence; to reduce\n   to a quintessence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quintessential', 'a.', 'Of the nature of a quintessence; purest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quintet', 'n.', 'Alt. of Quintette');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quintette', 'n.', 'A composition for five voices or instruments; also, the\n   set of five persons who sing or play five-part music.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quintic', 'a.', 'Of the fifth degree or order.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quintic', 'n.', 'A quantic of the fifth degree. See Quantic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quintile', 'n.', 'The aspect of planets when separated the fifth part of\n   the zodiac, or 72.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quintilllion', 'n.', 'According to the French notation, which is used on\n   the Continent and in America, the cube of a million, or a unit with\n   eighteen ciphers annexed; according to the English notation, a number\n   produced by involving a million to the fifth power, or a unit with\n   thirty ciphers annexed. See the Note under Numeration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quintin', 'n.', 'See Quintain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quintine', 'n.', 'The embryonic sac of an ovule, sometimes regarded as an\n   innermost fifth integument. Cf. Quartine, and Tercine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quintole', 'n.', 'A group of five notes to be played or sung in the time of\n   four of the same species.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quintuple', 'a.', 'Multiplied by five; increased to five times the amount;\n   fivefold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quintupled', 'imp. & p. p.', 'of Quintuple');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quintupling', 'p. pr. & vb. n.', 'of Quintuple');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quintuple', 'v. t.', 'To make fivefold, or five times as much or many.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quittuple-nerved', 'a.', 'Alt. of Quintuple-ribbed');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quintuple-ribbed', 'a.', 'The same as Quinquenerved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quinzaine', 'n.', 'The fifteenth day after a feast day, including both in\n   the reckoning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quinze', 'n.', 'A game at cards in which the object is to make fifteen\n   points.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quip', 'n.', 'A smart, sarcastic turn or jest; a taunt; a severe retort; a\n   gibe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quipped', 'imp. & p. p.', 'of Quip');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quipping', 'p. pr. & vb. n.', 'of Quip');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quip', 'v. t.', 'To taunt; to treat with quips.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quip', 'v. i.', 'To scoff; to use taunts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quipo', 'n.', 'Same as Quipu.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quipus', 'pl. ', 'of Quipu');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quipu', 'n.', 'A contrivance employed by the ancient Peruvians, Mexicans,\n   etc., as a substitute for writing and figures, consisting of a main\n   cord, from which hung at certain distances smaller cords of various\n   colors, each having a special meaning, as silver, gold, corn, soldiers.\n   etc. Single, double, and triple knots were tied in the smaller cords,\n   representing definite numbers. It was chiefly used for arithmetical\n   purposes, and to register important facts and events.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quirboilly', 'n.', 'Leather softened by boiling so as to take any required\n   shape. Upon drying, it becomes exceedingly hard, and hence was formerly\n   used for armor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quire', 'n.', 'See Choir.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quire', 'v. i.', 'To sing in concert.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quire', 'n.', 'A collection of twenty-four sheets of paper of the same size\n   and quality, unfolded or having a single fold; one twentieth of a ream.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quirister', 'n.', 'A chorister. See Chorister.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quiritation', 'n.', 'A crying for help.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quirite', 'n.', 'One of the Quirites.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quirites', 'n. pl.', 'Roman citizens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quirk', 'n.', 'A sudden turn; a starting from the point or line; hence, an\n   artful evasion or subterfuge; a shift; a quibble; as, the quirks of a\n   pettifogger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quirk', 'n.', 'A fit or turn; a short paroxysm; a caprice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quirk', 'n.', 'A smart retort; a quibble; a shallow conceit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quirk', 'n.', 'An irregular air; as, light quirks of music.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quirk', 'n.', 'A piece of ground taken out of any regular ground plot or\n   floor, so as to make a court, yard, etc.; -- sometimes written quink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quirk', 'n.', 'A small channel, deeply recessed in proportion to its width,\n   used to insulate and give relief to a convex rounded molding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quirked', 'a.', 'Having, or formed with, a quirk or quirks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quirkish', 'a.', 'Consisting of quirks; resembling a quirk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quirky', 'a.', 'Full of quirks; tricky; as, a quirky lawyer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quirl', 'n. & v.', 'See Querl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quirpele', 'n.', 'The Indian ferret.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quirt', 'n.', 'A rawhide whip plaited with two thongs of buffalo hide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quish', 'n.', 'See Cuish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quit', 'n.', 'Any one of numerous species of small passerine birds native\n   of tropical America. See Banana quit, under Banana, and Guitguit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quit', 'v.', 'Released from obligation, charge, penalty, etc.; free; clear;\n   absolved; acquitted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quit', 'imp. & p. p.', 'of Quit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quitted', '', 'of Quit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quitting', 'p. pr. & vb. n.', 'of Quit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quit', 'a.', 'To set at rest; to free, as from anything harmful or\n   oppressive; to relieve; to clear; to liberate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quit', 'a.', 'To release from obligation, accusation, penalty, or the like;\n   to absolve; to acquit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quit', 'a.', 'To discharge, as an obligation or duty; to meet and satisfy,\n   as a claim or debt; to make payment for or of; to requite; to repay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quit', 'a.', 'To meet the claims upon, or expectations entertained of; to\n   conduct; to acquit; -- used reflexively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quit', 'a.', 'To carry through; to go through to the end.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quit', 'a.', 'To have done with; to cease from; to stop; hence, to depart\n   from; to leave; to forsake; as, to quit work; to quit the place; to\n   quit jesting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quit', 'v. i.', 'To away; to depart; to stop doing a thing; to cease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quitch', 'n.', 'Same as Quitch grass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quitch', 'n.', 'Figuratively: A vice; a taint; an evil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quitch grass', '', 'A perennial grass (Agropyrum repens) having long\n   running rootstalks, by which it spreads rapidly and pertinaciously, and\n   so becomes a troublesome weed. Also called couch grass, quick grass,\n   quick grass, twitch grass. See Illustration in Appendix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quitclaim', 'n.', 'A release or relinquishment of a claim; a deed of\n   release; an instrument by which some right, title, interest, or claim,\n   which one person has, or is supposed to have, in or to an estate held\n   by himself or another, is released or relinquished, the grantor\n   generally covenanting only against persons who claim under himself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quitclaimed', 'imp. & p. p.', 'of Quitclaim');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quitclaiming', 'p. pr. & vb. n.', 'of Quitclaim');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quitclaim', 'v. t.', 'To release or relinquish a claim to; to release a\n   claim to by deed, without covenants of warranty against adverse and\n   paramount titles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quite', 'v. t. & i.', 'See Quit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quite', 'a.', 'Completely; wholly; entirely; totally; perfectly; as, the\n   work is not quite done; the object is quite accomplished; to be quite\n   mistaken.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quite', 'a.', 'To a great extent or degree; very; very much; considerably.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quitly', 'adv.', 'Quite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quitrent', 'n.', 'A rent reserved in grants of land, by the payment of\n   which the tenant is quit from other service.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quits', 'interj.', 'See the Note under Quit, a.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quittable', 'a.', 'Capable of being quitted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quittal', 'n.', 'Return; requital; quittance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quittance', 'v. t.', 'Discharge from a debt or an obligation; acquittance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quittance', 'v. t.', 'Recompense; return; repayment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quittance', 'v. t.', 'To repay; to requite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quitter', 'n.', 'One who quits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quitter', 'n.', 'A deliverer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quittor', 'n.', 'A chronic abscess, or fistula of the coronet, in a horses\n   foot, resulting from inflammation of the tissues investing the coffin\n   bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quitture', 'n.', 'A discharge; an issue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quiver', 'a.', 'Nimble; active.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quivered', 'imp. & p. p.', 'of Quiver');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quivering', 'p. pr. & vb. n.', 'of Quiver');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quiver', 'v. i.', 'To shake or move with slight and tremulous motion; to\n   tremble; to quake; to shudder; to shiver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quiver', 'n.', 'The act or state of quivering; a tremor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quiver', 'n.', 'A case or sheath for arrows to be carried on the person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quivered', 'a.', 'Furnished with, or carrying, a quiver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quivered', 'a.', 'Sheathed, as in a quiver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quiveringly', 'adv.', 'With quivering motion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Qui vive', '', 'The challenge of a French sentinel, or patrol; -- used like\n   the English challenge: \"Who comes there?\"');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quixotic', 'a.', 'Like Don Quixote; romantic to extravagance; absurdly\n   chivalric; apt to be deluded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quixotically', 'adv.', 'In a quixotic way.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quixotism', 'n.', 'That form of delusion which leads to extravagant and\n   absurd undertakings or sacrifices in obedience to a morbidly romantic\n   ideal of duty or honor, as illustrated by the exploits of Don Quixote\n   in knight-errantry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quixotry', 'n.', 'Quixotism; visionary schemes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quiz', 'n.', 'A riddle or obscure question; an enigma; a ridiculous hoax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quiz', 'n.', 'One who quizzes others; as, he is a great quiz.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quiz', 'n.', 'An odd or absurd fellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quiz', 'n.', 'An exercise, or a course of exercises, conducted as a\n   coaching or as an examination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quizzed', 'imp. & p. p.', 'of Quiz');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quizzing', 'p. pr. & vb. n.', 'of Quiz');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quiz', 'v. t.', 'To puzzle; to banter; to chaff or mock with pretended\n   seriousness of discourse; to make sport of, as by obscure questions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quiz', 'v. t.', 'To peer at; to eye suspiciously or mockingly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quiz', 'v. t.', 'To instruct in or by a quiz. See Quiz, n., 4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quiz', 'v. i.', 'To conduct a quiz. See Quiz, n., 4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quizzer', 'n.', 'One who quizzes; a quiz.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quizzical', 'a.', 'Relating to quizzing: given to quizzing; of the nature\n   of a quiz; farcical; sportive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quizzism', 'n.', 'The act or habit of quizzing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quob', 'v. i.', 'To throb; to quiver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quod', 'n.', 'A quadrangle or court, as of a prison; hence, a prison.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quod', 'v.', 'Quoth; said. See Quoth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quoddies', 'n. pl.', 'Herring taken and cured or smoked near Quoddy Head,\n   Maine, or near the entrance of Passamaquoddy Ray.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quodlibet', 'n.', 'A nice point; a subtilty; a debatable point.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quodlibet', 'n.', 'A medley improvised by several performers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quodlibetarian', 'n.', 'One who discusses any subject at pleasure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quodlibetical', 'a.', 'Not restricted to a particular subject; discussed\n   for curiosity or entertainment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quoif', 'n. & v. t.', 'See Coif.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quoifffure', 'n.', 'See Coiffure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quoil', 'n.', 'See Coil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quoin', 'n.', 'Originally, a solid exterior angle, as of a building; now,\n   commonly, one of the selected pieces of material by which the corner is\n   marked.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quoin', 'n.', 'A wedgelike piece of stone, wood metal, or other material,\n   used for various purposes');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quoin', 'n.', 'to support and steady a stone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quoin', 'n.', 'To support the breech of a cannon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quoin', 'n.', 'To wedge or lock up a form within a chase.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quoin', 'n.', 'To prevent casks from rolling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quoit', 'n.', 'A flattened ring-shaped piece of iron, to be pitched at a\n   fixed object in play; hence, any heavy flat missile used for the same\n   purpose, as a stone, piece of iron, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quoit', 'n.', 'A game played with quoits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quoit', 'n.', 'The discus of the ancients. See Discus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quoit', 'n.', 'A cromlech.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quoit', 'v. i.', 'To throw quoits; to play at quoits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quoit', 'v. t.', 'To throw; to pitch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quoke', '', 'imp. of Quake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quoll', 'n.', 'A marsupial of Australia (Dasyurus macrurus), about the size\n   of a cat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quondam', 'a.', 'Having been formerly; former; sometime.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quondam', 'n.', 'A person dismissed or ejected from a position.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quook', '', 'imp. of Quake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quop', 'v. i.', 'See Quob.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quorum', 'n.', 'Such a number of the officers or members of any body as is\n   competent by law or constitution to transact business; as, a quorum of\n   the House of Representatives; a constitutional quorum was not present.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quota', 'n.', 'A proportional part or share; the share or proportion\n   assigned to each in a division.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quotable', 'a.', 'Capable or worthy of being quoted; as, a quotable writer;\n   a quotable sentence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quotation', 'n.', 'The act of quoting or citing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quotation', 'n.', 'That which is quoted or cited; a part of a book or\n   writing named, repeated, or adduced as evidence or illustration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quotation', 'n.', 'The naming or publishing of the current price of stocks,\n   bonds, or any commodity; also the price named.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quotation', 'n.', 'Quota; share.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quotation', 'n.', 'A piece of hollow type metal, lower than type, and\n   measuring two or more pica ems in length and breadth, used in the blank\n   spaces at the beginning and end of chapters, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quotationist', 'n.', 'One who makes, or is given to making, quotations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quoted', 'imp. & p. p.', 'of Quote');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quoting', 'p. pr. & vb. n.', 'of Quote');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quote', 'v. t.', 'To cite, as a passage from some author; to name, repeat,\n   or adduce, as a passage from an author or speaker, by way of authority\n   or illustration; as, to quote a passage from Homer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quote', 'v. t.', 'To cite a passage from; to name as the authority for a\n   statement or an opinion; as, to quote Shakespeare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quote', 'v. t.', 'To name the current price of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quote', 'v. t.', 'To notice; to observe; to examine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quote', 'v. t.', 'To set down, as in writing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quote', 'n.', 'A note upon an author.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quoter', 'n.', 'One who quotes the words of another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quoth', 'v. t.', 'Said; spoke; uttered; -- used only in the first and third\n   persons in the past tenses, and always followed by its nominative, the\n   word or words said being the object; as, quoth I. quoth he.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quotha', 'interj.', 'Indeed; forsooth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quotidian', 'a.', 'Occurring or returning daily; as, a quotidian fever.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quotidian', 'n.', 'Anything returning daily; especially (Med.), an\n   intermittent fever or ague which returns every day.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quotient', 'n.', 'The number resulting from the division of one number by\n   another, and showing how often a less number is contained in a greater;\n   thus, the quotient of twelve divided by four is three.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quotient', 'n.', 'The result of any process inverse to multiplication. See\n   the Note under Multiplication.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quotiety', 'n.', 'The relation of an object to number.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quotum', 'n.', 'Part or proportion; quota.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quo warranto', '', 'A writ brought before a proper tribunal, to inquire by\n   what warrant a person or a corporation acts, or exercises certain\n   powers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quran', 'n.', 'See Koran.\n   R () R, the eighteenth letter of the English alphabet, is a vocal\n   consonant. It is sometimes called a semivowel, and a liquid. See Guide\n   to Pronunciation, // 178, 179, and 250-254.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ra', 'n.', 'A roe; a deer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ra-', '', 'A prefix, from the Latin re and ad combined, coming to us\n   through the French and Italian. See Re-, and Ad-.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raash', 'n.', 'The electric catfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rab', 'n.', 'A rod or stick used by masons in mixing hair with mortar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rabat', 'n.', 'A polishing material made of potters clay that has failed\n   in baking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rabate', 'v.', 'To recover to the fist, as a hawk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rabatine', 'n.', 'A collar or cape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rabato', 'n.', 'A kind of ruff for the neck; a turned-down collar; a\n   rebato.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rabbate', 'v. t.', 'To abate or diminish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rabbate', 'n.', 'Abatement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rabbeted', 'imp. & p. p.', 'of Rabbet');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rabbeting', 'p. pr. & vb. n.', 'of Rabbet');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rabbet', 'v. t.', 'To cut a rabbet in; to furnish with a rabbet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rabbet', 'v. t.', 'To unite the edges of, as boards, etc., in a rabbet\n   joint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rabbet', 'n.', 'A longitudinal channel, groove, or recess cut out of the\n   edge or face of any body; especially, one intended to receive another\n   member, so as to break or cover the joint, or more easily to hold the\n   members in place; thus, the groove cut for a panel, for a pane of\n   glass, or for a door, is a rabbet, or rebate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rabbet', 'n.', 'Same as Rabbet joint, below.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rabbis', 'pl. ', 'of Rabbi');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rabbies', 'pl. ', 'of Rabbi');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rabbi', 'n.', 'Master; lord; teacher; -- a Jewish title of respect or honor\n   for a teacher or doctor of the law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rabbin', 'n.', 'Same as Rabbi.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rabbinic', 'a.', 'Alt. of Rabbinical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rabbinical', 'a.', 'Of or pertaining to the rabbins or rabbis, or\n   pertaining to the opinions, learning, or language of the rabbins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rabbinic', 'n.', 'The language or dialect of the rabbins; the later Hebrew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rabbinically', 'adv.', 'In a rabbinical manner; after the manner of the\n   rabbins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rabbinism', 'n.', 'A rabbinic expression or phraseology; a peculiarity of\n   the language of the rabbins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rabbinism', 'n.', 'The teachings and traditions of the rabbins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rabbinist', 'n.', 'One among the Jews who adhered to the Talmud and the\n   traditions of the rabbins, in opposition to the Karaites, who rejected\n   the traditions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rabbinite', 'n.', 'Same as Rabbinist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rabbit', 'n.', 'Any of the smaller species of the genus Lepus, especially\n   the common European species (Lepus cuniculus), which is often kept as a\n   pet, and has been introduced into many countries. It is remarkably\n   prolific, and has become a pest in some parts of Australia and New\n   Zealand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rabbiting', 'n.', 'The hunting of rabbits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rabbitry', 'n.', 'A place where rabbits are kept; especially, a collection\n   of hutches for tame rabbits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rabble', 'n.', 'An iron bar, with the end bent, used in stirring or\n   skimming molten iron in the process of puddling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rabble', 'v. t.', 'To stir or skim with a rabble, as molten iron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rabble', 'v. i.', 'To speak in a confused manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rabble', 'v. i.', 'A tumultuous crowd of vulgar, noisy people; a mob; a\n   confused, disorderly throng.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rabble', 'v. i.', 'A confused, incoherent discourse; a medley of voices; a\n   chatter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rabble', 'a.', 'Of or pertaining to a rabble; like, or suited to, a rabble;\n   disorderly; vulgar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rabbled', 'imp. & p. p.', 'of Rabble');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rabbling', 'p. pr. & vb. n.', 'of Rabble');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rabble', 'v. t.', 'To insult, or assault, by a mob; to mob; as, to rabble a\n   curate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rabble', 'v. t.', 'To utter glibly and incoherently; to mouth without\n   intelligence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rabble', 'v. t.', 'To rumple; to crumple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rabblement', 'n.', 'A tumultuous crowd of low people; a rabble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rabbler', 'n.', 'A scraping tool for smoothing metal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rabble-rout', 'n.', 'A tumultuous crowd; a rabble; a noisy throng.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rabdoidal', 'a.', 'See Sagittal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rabdology', 'n.', 'The method or art of performing arithmetical operations\n   by means of Napiers bones. See Napiers bones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rabdomancy', 'n.', 'Divination by means of rods or wands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rabid', 'n.', 'Furious; raging; extremely violent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rabid', 'n.', 'Extreme, unreasonable, or fanatical in opinion; excessively\n   zealous; as, a rabid socialist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rabid', 'n.', 'Affected with the distemper called rabies; mad; as, a rabid\n   dog or fox.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rabid', 'n.', 'Of or pertaining to rabies, or hydrophobia; as, rabid virus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rabidity', 'n.', 'Rabidness; furiousness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rabidly', 'adv.', 'In a rabid manner; with extreme violence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rabidness', 'n.', 'The quality or state of being rabid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rabies', 'n.', 'Same as Hydrophobia (b); canine madness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rabinet', 'n.', 'A kind of small ordnance formerly in use.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rabious', 'a.', 'Fierce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rabot', 'n.', 'A rubber of hard wood used in smoothing marble to be\n   polished.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raca', 'a.', 'A term of reproach used by the Jews of our Saviors time,\n   meaning \"worthless.\"');
  INSERT INTO words (word, word_type, description) VALUES  (    'Racahout', 'n.', 'A preparation from acorns used by the Arabs as a\n   substitute for chocolate, and also as a beverage for invalids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raccoon', 'n.', 'A North American nocturnal carnivore (Procyon lotor)\n   allied to the bears, but much smaller, and having a long, full tail,\n   banded with black and gray. Its body is gray, varied with black and\n   white. Called also coon, and mapach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Race', 'v. t.', 'To raze.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Race', 'n.', 'A root.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Race', 'n.', 'The descendants of a common ancestor; a family, tribe,\n   people, or nation, believed or presumed to belong to the same stock; a\n   lineage; a breed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Race', 'n.', 'Company; herd; breed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Race', 'n.', 'A variety of such fixed character that it may be propagated\n   by seed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Race', 'n.', 'Peculiar flavor, taste, or strength, as of wine; that\n   quality, or assemblage of qualities, which indicates origin or kind, as\n   in wine; hence, characteristic flavor; smack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Race', 'n.', 'Hence, characteristic quality or disposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Race', 'n.', 'A progress; a course; a movement or progression.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Race', 'n.', 'Esp., swift progress; rapid course; a running.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Race', 'n.', 'Hence: The act or process of running in competition; a\n   contest of speed in any way, as in running, riding, driving, skating,\n   rowing, sailing; in the plural, usually, a meeting for contests in the\n   running of horses; as, he attended the races.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Race', 'n.', 'Competitive action of any kind, especially when prolonged;\n   hence, career; course of life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Race', 'n.', 'A strong or rapid current of water, or the channel or passage\n   for such a current; a powerful current or heavy sea, sometimes produced\n   by the meeting of two tides; as, the Portland Race; the Race of\n   Alderney.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Race', 'n.', 'The current of water that turns a water wheel, or the channel\n   in which it flows; a mill race.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Race', 'n.', 'A channel or guide along which a shuttle is driven back and\n   forth, as in a loom, sewing machine, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raced', 'imp. & p. p.', 'of Race');
  INSERT INTO words (word, word_type, description) VALUES  (    'Racing', 'p. pr. & vb. n.', 'of Race');
  INSERT INTO words (word, word_type, description) VALUES  (    'Race', 'v. i.', 'To run swiftly; to contend in a race; as, the animals\n   raced over the ground; the ships raced from port to port.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Race', 'v. i.', 'To run too fast at times, as a marine engine or screw,\n   when the screw is lifted out of water by the action of a heavy sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Race', 'v. t.', 'To cause to contend in a race; to drive at high speed; as,\n   to race horses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Race', 'v. t.', 'To run a race with.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Racemate', 'n.', 'A salt of racemic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Racemation', 'n.', 'A cluster or bunch, as of grapes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Racemation', 'n.', 'Cultivation or gathering of clusters of grapes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raceme', 'n.', 'A flower cluster with an elongated axis and many\n   one-flowered lateral pedicels, as in the currant and chokecherry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Racemed', 'a.', 'Arranged in a raceme, or in racemes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Racemic', 'a.', 'Pertaining to, or designating, an acid found in many kinds\n   of grapes. It is also obtained from tartaric acid, with which it is\n   isomeric, and from sugar, gum, etc., by oxidation. It is a sour white\n   crystalline substance, consisting of a combination of dextrorotatory\n   and levorotatory tartaric acids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Racemiferous', 'a.', 'Bearing racemes, as the currant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Racemiform', 'a.', 'Having the form of a raceme.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Racemose', 'a.', 'Resembling a raceme; growing in the form of a raceme; as,\n   (Bot.) racemose berries or flowers; (Anat.) the racemose glands, in\n   which the ducts are branched and clustered like a raceme.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Racemous', 'a.', 'See Racemose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Racemule', 'n.', 'A little raceme.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Racemulose', 'a.', 'Growing in very small racemes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Racer', 'n.', 'One who, or that which, races, or contends in a race; esp.,\n   a race horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Racer', 'n.', 'The common American black snake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Racer', 'n.', 'One of the circular iron or steel rails on which the chassis\n   of a heavy gun is turned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rach', 'n.', 'Alt. of Rache');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rache', 'n.', 'A dog that pursued his prey by scent, as distinguished from\n   the greyhound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rachialgia', 'n.', 'A painful affection of the spine; especially, Potts\n   disease; also, formerly, lead colic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rachidian', 'a.', 'Of or pertaining to the rachis; spinal; vertebral. Same\n   as Rhachidian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rachilla', 'n.', 'Same as Rhachilla.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rachiodont', 'a.', 'Same as Rhachiodont.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rachises', 'pl. ', 'of Rachis');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rachides', 'pl. ', 'of Rachis');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rachis', 'n.', 'The spine; the vertebral column.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rachis', 'n.', 'Same as Rhachis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rachitic', 'a.', 'Of or pertaining to rachitis; affected by rachitis;\n   rickety.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rachitis', 'n.', 'Literally, inflammation of the spine, but commonly\n   applied to the rickets. See Rickets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rachitis', 'n.', 'A disease which produces abortion in the fruit or seeds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rachitome', 'n.', 'A dissecting instrument for opening the spinal canal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Racial', 'a.', 'Of or pertaining to a race or family of men; as, the racial\n   complexion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Racily', 'adv.', 'In a racy manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raciness', 'n.', 'The quality of being racy; peculiar and piquant flavor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Racing', '', 'a. & n. from Race, v. t. & i.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rack', 'n.', 'Same as Arrack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rack', 'n.', 'The neck and spine of a fore quarter of veal or mutton.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rack', 'n.', 'A wreck; destruction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rack', 'n.', 'Thin, flying, broken clouds, or any portion of floating vapor\n   in the sky.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rack', 'v. i.', 'To fly, as vapor or broken clouds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Racked', 'imp. & p. p.', 'of Rack');
  INSERT INTO words (word, word_type, description) VALUES  (    'Racking', 'p. pr. & vb. n.', 'of Rack');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rack', 'v.', 'To amble fast, causing a rocking or swaying motion of the\n   body; to pace; -- said of a horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rack', 'n.', 'A fast amble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rack', 'v. t.', 'To draw off from the lees or sediment, as wine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rack', 'a.', 'An instrument or frame used for stretching, extending,\n   retaining, or displaying, something.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rack', 'a.', 'An engine of torture, consisting of a large frame, upon which\n   the body was gradually stretched until, sometimes, the joints were\n   dislocated; -- formerly used judicially for extorting confessions from\n   criminals or suspected persons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rack', 'a.', 'An instrument for bending a bow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rack', 'a.', 'A grate on which bacon is laid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rack', 'a.', 'A frame or device of various construction for holding, and\n   preventing the waste of, hay, grain, etc., supplied to beasts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rack', 'a.', 'A frame on which articles are deposited for keeping or\n   arranged for display; as, a clothes rack; a bottle rack, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rack', 'a.', 'A piece or frame of wood, having several sheaves, through\n   which the running rigging passes; -- called also rack block. Also, a\n   frame to hold shot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rack', 'a.', 'A frame or table on which ores are separated or washed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rack', 'a.', 'A frame fitted to a wagon for carrying hay, straw, or grain\n   on the stalk, or other bulky loads.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rack', 'a.', 'A distaff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rack', 'a.', 'A bar with teeth on its face, or edge, to work with those of\n   a wheel, pinion, or worm, which is to drive it or be driven by it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rack', 'a.', 'That which is extorted; exaction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rack', 'v. t.', 'To extend by the application of force; to stretch or\n   strain; specifically, to stretch on the rack or wheel; to torture by an\n   engine which strains the limbs and pulls the joints.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rack', 'v. t.', 'To torment; to torture; to affect with extreme pain or\n   anguish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rack', 'v. t.', 'To stretch or strain, in a figurative sense; hence, to\n   harass, or oppress by extortion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rack', 'v. t.', 'To wash on a rack, as metals or ore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rack', 'v. t.', 'To bind together, as two ropes, with cross turns of yarn,\n   marline, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rackabones', 'n.', 'A very lean animal, esp. a horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Racker', 'n.', 'One who racks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Racker', 'n.', 'A horse that has a racking gait.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Racket', 'n.', 'A thin strip of wood, having the ends brought together,\n   forming a somewhat elliptical hoop, across which a network of catgut or\n   cord is stretched. It is furnished with a handle, and is used for\n   catching or striking a ball in tennis and similar games.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Racket', 'n.', 'A variety of the game of tennis played with peculiar\n   long-handled rackets; -- chiefly in the plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Racket', 'n.', 'A snowshoe formed of cords stretched across a long and\n   narrow frame of light wood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Racket', 'n.', 'A broad wooden shoe or patten for a man or horse, to enable\n   him to step on marshy or soft ground.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Racket', 'v. t.', 'To strike with, or as with, a racket.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Racket', 'n.', 'Confused, clattering noise; din; noisy talk or sport.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Racket', 'n.', 'A carouse; any reckless dissipation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Racketed', 'imp. & p. p.', 'of Racket');
  INSERT INTO words (word, word_type, description) VALUES  (    'Racketing', 'p. pr. & vb. n.', 'of Racket');
  INSERT INTO words (word, word_type, description) VALUES  (    'Racket', 'v. i.', 'To make a confused noise or racket.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Racket', 'v. i.', 'To engage in noisy sport; to frolic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Racket', 'v. i.', 'To carouse or engage in dissipation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Racketer', 'n.', 'One who makes, or engages in, a racket.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rackett', 'n.', 'An old wind instrument of the double bassoon kind, having\n   ventages but not keys.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Racket-tail', 'n.', 'Any one of several species of humming birds of the\n   genus Steganura, having two of the tail feathers very long and\n   racket-shaped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Racket-tailed', 'a.', 'Having long and spatulate, or racket-shaped, tail\n   feathers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rackety', 'a.', 'Making a tumultuous noise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Racking', 'n.', 'Spun yarn used in racking ropes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rack-rent', 'n.', 'A rent of the full annual value of the tenement, or near\n   it; an excessive or unreasonably high rent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rack-rent', 'v. t.', 'To subject to rack-rent, as a farm or tenant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rack-renter', 'n.', 'One who is subjected to paying rack-rent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rack-renter', 'n.', 'One who exacts rack-rent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Racktail', 'n.', 'An arm attached to a swinging notched arc or rack, to let\n   off the striking mechanism of a repeating clock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rackwork', 'n.', 'Any mechanism having a rack, as a rack and pinion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Racle', 'a.', 'See Rakel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Racleness', 'n.', 'See Rakelness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raconteur', 'n.', 'A relater; a storyteller.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Racoonda', 'n.', 'The coypu.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Racovian', 'n.', 'One of a sect of Socinians or Unitarians in Poland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Racquet', 'n.', 'See Racket.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Racy', 'superl.', 'Having a strong flavor indicating origin; of distinct\n   characteristic taste; tasting of the soil; hence, fresh; rich.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Racy', 'superl.', 'Hence: Exciting to the mental taste by a strong or\n   distinctive character of thought or language; peculiar and piquant;\n   fresh and lively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rad', '', 'imp. & p. p. of Read, Rede.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radde', '', 'imp. of Read, Rede.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raddle', 'n.', 'A long, flexible stick, rod, or branch, which is interwoven\n   with others, between upright posts or stakes, in making a kind of hedge\n   or fence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raddle', 'n.', 'A hedge or fence made with raddles; -- called also raddle\n   hedge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raddle', 'n.', 'An instrument consisting of a wooden bar, with a row of\n   upright pegs set in it, used by domestic weavers to keep the warp of a\n   proper width, and prevent tangling when it is wound upon the beam of\n   the loom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raddle', 'v. t.', 'To interweave or twist together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raddle', 'n.', 'A red pigment used in marking sheep, and in some mechanical\n   processes; ruddle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raddle', 'v. t.', 'To mark or paint with, or as with, raddle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raddock', 'n.', 'The ruddock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rade', 'n.', 'A raid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radeau', 'n.', 'A float; a raft.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radial', 'a.', 'Of or pertaining to a radius or ray; consisting of, or\n   like, radii or rays; radiated; as, (Bot.) radial projections; (Zool.)\n   radial vessels or canals; (Anat.) the radial artery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radialia', 'pl. ', 'of Radiale');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radiale', 'n.', 'The bone or cartilage of the carpus which articulates with\n   the radius and corresponds to the scaphoid bone in man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radiale', 'n.', 'Radial plates in the calyx of a crinoid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radially', 'adv.', 'In a radial manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radian', 'n.', 'An arc of a circle which is equal to the radius, or the\n   angle measured by such an arc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radiance', 'n.', 'Alt. of Radiancy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radiancy', 'n.', 'The quality of being radiant; brilliancy; effulgence;\n   vivid brightness; as, the radiance of the sun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radiant', 'a.', 'Emitting or proceeding as from a center; resembling rays;\n   radiating; radiate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radiant', 'a.', 'Especially, emitting or darting rays of light or heat;\n   issuing in beams or rays; beaming with brightness; emitting a vivid\n   light or splendor; as, the radiant sun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radiant', 'a.', 'Beaming with vivacity and happiness; as, a radiant face.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radiant', 'a.', 'Giving off rays; -- said of a bearing; as, the sun\n   radiant; a crown radiant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radiant', 'a.', 'Having a raylike appearance, as the large marginal flowers\n   of certain umbelliferous plants; -- said also of the cluster which has\n   such marginal flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radiant', 'n.', 'The luminous point or object from which light emanates;\n   also, a body radiating light brightly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radiant', 'n.', 'A straight line proceeding from a given point, or fixed\n   pole, about which it is conceived to revolve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radiant', 'n.', 'The point in the heavens at which the apparent paths of\n   shooting stars meet, when traced backward, or whence they appear to\n   radiate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radiantly', 'adv.', 'In a radiant manner; with glittering splendor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radiary', 'n.', 'A radiate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radiata', 'n. pl.', 'An extensive artificial group of invertebrates, having\n   all the parts arranged radially around the vertical axis of the body,\n   and the various organs repeated symmetrically in each ray or\n   spheromere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radiated', 'imp. & p. p.', 'of Radiate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radiating', 'p. pr. & vb. n.', 'of Radiate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radiate', 'v. i.', 'To emit rays; to be radiant; to shine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radiate', 'v. i.', 'To proceed in direct lines from a point or surface; to\n   issue in rays, as light or heat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radiate', 'v. t.', 'To emit or send out in direct lines from a point or\n   points; as, to radiate heat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radiate', 'v. t.', 'To enlighten; to illuminate; to shed light or\n   brightness on; to irradiate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radiate', 'a.', 'Having rays or parts diverging from a center; radiated;\n   as, a radiate crystal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radiate', 'a.', 'Having in a capitulum large ray florets which are unlike\n   the disk florets, as in the aster, daisy, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radiate', 'a.', 'Belonging to the Radiata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radiate', 'n.', 'One of the Radiata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radiated', 'a.', 'Emitted, or sent forth, in rays or direct lines; as,\n   radiated heat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radiated', 'a.', 'Formed of, or arranged like, rays or radii; having parts\n   or markings diverging, like radii, from a common center or axis; as, a\n   radiated structure; a radiated group of crystals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radiated', 'a.', 'Belonging to the Radiata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radiately', 'adv.', 'In a radiate manner; with radiation or divergence from\n   a center.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radiate-veined', 'a.', 'Having the principal veins radiating, or diverging,\n   from the apex of the petiole; -- said of such leaves as those of the\n   grapevine, most maples, and the castor-oil plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radiatiform', 'a.', 'Having the marginal florets enlarged and radiating but\n   not ligulate, as in the capitula or heads of the cornflower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radiation', 'n.', 'The act of radiating, or the state of being radiated;\n   emission and diffusion of rays of light; beamy brightness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radiation', 'n.', 'The shooting forth of anything from a point or surface,\n   like the diverging rays of light; as, the radiation of heat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radiative', 'a.', 'Capable of radiating; acting by radiation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radiator', 'n.', 'That which radiates or emits rays, whether of light or\n   heat; especially, that part of a heating apparatus from which the heat\n   is radiated or diffused; as, a steam radiator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radical', 'a.', 'Of or pertaining to the root; proceeding directly from the\n   root.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radical', 'a.', 'Hence: Of or pertaining to the root or origin; reaching to\n   the center, to the foundation, to the ultimate sources, to the\n   principles, or the like; original; fundamental; thorough-going;\n   unsparing; extreme; as, radical evils; radical reform; a radical party.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radical', 'a.', 'Belonging to, or proceeding from, the root of a plant; as,\n   radical tubers or hairs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radical', 'a.', 'Proceeding from a rootlike stem, or one which does not\n   rise above the ground; as, the radical leaves of the dandelion and the\n   sidesaddle flower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radical', 'a.', 'Relating, or belonging, to the root, or ultimate source of\n   derivation; as, a radical verbal form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radical', 'a.', 'Of or pertaining to a radix or root; as, a radical\n   quantity; a radical sign. See below.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radical', 'n.', 'A primitive word; a radix, root, or simple, underived,\n   uncompounded word; an etymon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radical', 'n.', 'A primitive letter; a letter that belongs to the radix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radical', 'n.', 'One who advocates radical changes in government or social\n   institutions, especially such changes as are intended to level class\n   inequalities; -- opposed to conservative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radical', 'n.', 'A characteristic, essential, and fundamental constituent\n   of any compound; hence, sometimes, an atom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radical', 'n.', 'Specifically, a group of two or more atoms, not completely\n   saturated, which are so linked that their union implies certain\n   properties, and are conveniently regarded as playing the part of a\n   single atom; a residue; -- called also a compound radical. Cf. Residue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radical', 'n.', 'A radical quantity. See under Radical, a.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radical', 'a.', 'A radical vessel. See under Radical, a.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radicalism', 'n.', 'The quality or state of being radical; specifically,\n   the doctrines or principles of radicals in politics or social reform.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radicality', 'n.', 'Germinal principle; source; origination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radicality', 'n.', 'Radicalness; relation to a root in essential nature or\n   principle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radically', 'adv.', 'In a radical manner; at, or from, the origin or root;\n   fundamentally; as, a scheme or system radically wrong or defective.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radically', 'adv.', 'Without derivation; primitively; essentially.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radicalness', 'n.', 'Quality or state of being radical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radicant', 'a.', 'Taking root on, or above, the ground; rooting from the\n   stem, as the trumpet creeper and the ivy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radicate', 'a.', 'Radicated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radicate', 'v. i.', 'To take root; to become rooted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radicated', 'imp. & p. p.', 'of Radicate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radicating', 'p. pr. & vb. n.', 'of Radicate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radicate', 'v. t.', 'To cause to take root; to plant deeply and firmly; to\n   root.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radicated', 'a.', 'Rooted');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radicated', 'a.', 'Having roots, or possessing a well-developed root.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radicated', 'a.', 'Having rootlike organs for attachment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radication', 'n.', 'The process of taking root, or state of being rooted;\n   as, the radication of habits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radication', 'n.', 'The disposition of the roots of a plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radicel', 'n.', 'A small branch of a root; a rootlet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radiciflorous', 'a.', 'Rhizanthous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radiciform', 'a.', 'Having the nature or appearance of a radix or root.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radicle', 'n.', 'The rudimentary stem of a plant which supports the\n   cotyledons in the seed, and from which the root is developed downward;\n   the stem of the embryo; the caulicle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radicle', 'n.', 'A rootlet; a radicel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radicular', 'a.', 'Of or pertaining to roots, or the root of a plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radicule', 'n.', 'A radicle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radiculose', 'a.', 'Producing numerous radicles, or rootlets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radii', 'n.', 'pl. of Radius.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radio-', '', 'A combining form indicating connection with, or relation to,\n   a radius or ray; specifically (Anat.), with the radius of the forearm;\n   as, radio-ulnar, radio-muscular, radio-carpal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radio-flagellata', 'n. pl.', 'A group of Protozoa having both flagella and\n   pseudopodia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radiograph', 'n.', 'A picture produced by the Rontgen rays upon a sensitive\n   surface, photographic or fluorescent, especially a picture of opaque\n   objects traversed by the rays.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radiolaria', 'n. pl.', 'Order of rhizopods, usually having a siliceous\n   skeleton, or shell, and sometimes radiating spicules. The pseudopodia\n   project from the body like rays. It includes the polycystines. See\n   Polycystina.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radiolarian', 'a.', 'Of or pertaining to the Radiolaria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radiolarian', 'n.', 'One of the Radiolaria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radioli', 'n. pl.', 'The barbs of the radii of a feather; barbules.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radiolite', 'n.', 'A hippurite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radiometer', 'n.', 'A forestaff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radiometer', 'n.', 'An instrument designed for measuring the mechanical\n   effect of radiant energy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radiomicrometer', 'n.', 'A very sensitive modification or application of\n   the thermopile, used for indicating minute changes of radiant heat, or\n   temperature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radiophone', 'n.', 'An apparatus for the production of sound by the action\n   of luminous or thermal rays. It is essentially the same as the\n   photophone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radiophony', 'n.', 'The art or practice of using the radiophone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radious', 'a.', 'Consisting of rays, as light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radious', 'a.', 'Radiating; radiant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radish', 'n.', 'The pungent fleshy root of a well-known cruciferous plant\n   (Raphanus sativus); also, the whole plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radii', 'pl. ', 'of Radius');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radiuses', 'pl. ', 'of Radius');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radius', 'n.', 'A right line drawn or extending from the center of a circle\n   to the periphery; the semidiameter of a circle or sphere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radius', 'n.', 'The preaxial bone of the forearm, or brachium,\n   corresponding to the tibia of the hind limb. See Illust. of\n   Artiodactyla.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radius', 'n.', 'A ray, or outer floret, of the capitulum of such plants as\n   the sunflower and the daisy. See Ray, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radius', 'n.', 'The barbs of a perfect feather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radius', 'n.', 'Radiating organs, or color-markings, of the radiates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radius', 'n.', 'The movable limb of a sextant or other angular instrument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radius vector', '', 'A straight line (or the length of such line)\n   connecting any point, as of a curve, with a fixed point, or pole, round\n   which the straight line turns, and to which it serves to refer the\n   successive points of a curve, in a system of polar coordinates. See\n   Coordinate, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radius vector', 'n.', 'An ideal straight line joining the center of an\n   attracting body with that of a body describing an orbit around it, as a\n   line joining the sun and a planet or comet, or a planet and its\n   satellite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radices', 'pl. ', 'of Radix');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radixes', 'pl. ', 'of Radix');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radix', 'n.', 'A primitive word, from which spring other words; a radical;\n   a root; an etymon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radix', 'n.', 'A number or quantity which is arbitrarily made the\n   fundamental number of any system; a base. Thus, 10 is the radix, or\n   base, of the common system of logarithms, and also of the decimal\n   system of numeration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radix', 'n.', 'A finite expression, from which a series is derived.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radix', 'n.', 'The root of a plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radulae', 'pl. ', 'of Radula');
  INSERT INTO words (word, word_type, description) VALUES  (    'Radula', 'n.', 'The chitinous ribbon bearing the teeth of mollusks; --\n   called also lingual ribbon, and tongue. See Odontophore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raduliform', 'a.', 'Rasplike; as, raduliform teeth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raffed', 'imp. & p. p.', 'of Raff');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raffing', 'p. pr. & vb. n.', 'of Raff');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raff', 'v. t.', 'To sweep, snatch, draw, or huddle together; to take by a\n   promiscuous sweep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raff', 'n.', 'A promiscuous heap; a jumble; a large quantity; lumber;\n   refuse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raff', 'n.', 'The sweepings of society; the rabble; the mob; -- chiefly\n   used in the compound or duplicate, riffraff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raff', 'n.', 'A low fellow; a churl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raffaelesque', 'a.', 'Raphaelesque.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raffia', 'n.', 'A fibrous material used for tying plants, said to come from\n   the leaves of a palm tree of the genus Raphia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raffinose', 'n.', 'A colorless crystalline slightly sweet substance\n   obtained from the molasses of the sugar beet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raffish', 'a.', 'Resembling, or having the character of, raff, or a raff;\n   worthless; low.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raffle', 'v.', 'A kind of lottery, in which several persons pay, in shares,\n   the value of something put up as a stake, and then determine by chance\n   (as by casting dice) which one of them shall become the sole possessor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raffle', 'v.', 'A game of dice in which he who threw three alike won all\n   the stakes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raffled', 'imp. & p. p.', 'of Raffle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raffling', 'p. pr. & vb. n.', 'of Raffle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raffle', 'v. i.', 'To engage in a raffle; as, to raffle for a watch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raffle', 'v. t.', 'To dispose of by means of a raffle; -- often followed by\n   off; as, to raffle off a horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raffler', 'n.', 'One who raffles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rafflesia', 'n.', 'A genus of stemless, leafless plants, living\n   parasitically upon the roots and stems of grapevines in Malaysia. The\n   flowers have a carrionlike odor, and are very large, in one species\n   (Rafflesia Arnoldi) having a diameter of two or three feet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raft', '', 'imp. & p. p. of Reave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raft', 'n.', 'A collection of logs, boards, pieces of timber, or the like,\n   fastened together, either for their own collective conveyance on the\n   water, or to serve as a support in conveying other things; a float.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raft', 'n.', 'A collection of logs, fallen trees, etc. (such as is formed\n   in some Western rivers of the United States), which obstructs\n   navigation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raft', 'n.', 'A large collection of people or things taken\n   indiscriminately.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rafted', 'imp. & p. p.', 'of Raft');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rafting', 'p. pr. & vb. n.', 'of Raft');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raft', 'v. t.', 'To transport on a raft, or in the form of a raft; to make\n   into a raft; as, to raft timber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rafte', '', 'imp. of Reave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rafter', 'n.', 'A raftsman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rafter', 'n.', 'Originally, any rough and somewhat heavy piece of timber.\n   Now, commonly, one of the timbers of a roof which are put on sloping,\n   according to the inclination of the roof. See Illust. of Queen-post.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rafter', 'v. t.', 'To make into rafters, as timber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rafter', 'v. t.', 'To furnish with rafters, as a house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rafter', 'v. t.', 'To plow so as to turn the grass side of each furrow upon\n   an unplowed ridge; to ridge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rafting', 'n.', 'The business of making or managing rafts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raftsmen', 'pl. ', 'of Raftsman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raftsman', 'n.', 'A man engaged in rafting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rafty', 'a.', 'Damp; musty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rag', 'v. t.', 'To scold or rail at; to rate; to tease; to torment; to\n   banter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rag', 'n.', 'A piece of cloth torn off; a tattered piece of cloth; a shred;\n   a tatter; a fragment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rag', 'n.', 'Hence, mean or tattered attire; worn-out dress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rag', 'n.', 'A shabby, beggarly fellow; a ragamuffin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rag', 'n.', 'A coarse kind of rock, somewhat cellular in texture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rag', 'n.', 'A ragged edge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rag', 'n.', 'A sail, or any piece of canvas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ragged', 'imp. & p. p.', 'of Rag');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ragging', 'p. pr. & vb. n.', 'of Rag');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rag', 'v. i.', 'To become tattered.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rag', 'v. t.', 'To break (ore) into lumps for sorting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rag', 'v. t.', 'To cut or dress roughly, as a grindstone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ragabash', 'n.', 'Alt. of Ragabrash');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ragabrash', 'n.', 'An idle, ragged person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ragamuffin', 'n.', 'A paltry or disreputable fellow; a mean wretch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ragamuffin', 'n.', 'A person who wears ragged clothing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ragamuffin', 'n.', 'The long-tailed titmouse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rage', 'n.', 'Violent excitement; eager passion; extreme vehemence of\n   desire, emotion, or suffering, mastering the will.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rage', 'n.', 'Especially, anger accompanied with raving; overmastering\n   wrath; violent anger; fury.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rage', 'n.', 'A violent or raging wind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rage', 'n.', 'The subject of eager desire; that which is sought after, or\n   prosecuted, with unreasonable or excessive passion; as, to be all the\n   rage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raged', 'imp. & p. p.', 'of Rage');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raging', 'p. pr. & vb. n.', 'of Rage');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rage', 'n.', 'To be furious with anger; to be exasperated to fury; to be\n   violently agitated with passion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rage', 'n.', 'To be violent and tumultuous; to be violently driven or\n   agitated; to act or move furiously; as, the raging sea or winds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rage', 'n.', 'To ravage; to prevail without restraint, or with destruction\n   or fatal effect; as, the plague raged in Cairo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rage', 'n.', 'To toy or act wantonly; to sport.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rage', 'v. t.', 'To enrage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rageful', 'a.', 'Full of rage; expressing rage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ragery', 'n.', 'Wantonness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ragged', 'n.', 'Rent or worn into tatters, or till the texture is broken;\n   as, a ragged coat; a ragged sail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ragged', 'n.', 'Broken with rough edges; having jags; uneven; rough;\n   jagged; as, ragged rocks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ragged', 'n.', 'Hence, harsh and disagreeable to the ear; dissonant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ragged', 'n.', 'Wearing tattered clothes; as, a ragged fellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ragged', 'n.', 'Rough; shaggy; rugged.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raggie', 'a.', 'Alt. of Raggy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raggy', 'a.', 'Ragged; rough.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raghuvansa', 'n.', 'A celebrated Sanskrit poem having for its subject the\n   Raghu dynasty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raging', '', 'a. & n. from Rage, v. i.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ragious', 'a.', 'Raging; furious; rageful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raglan', 'n.', 'A loose overcoat with large sleeves; -- named from Lord\n   Raglan, an English general.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ragmen', 'pl. ', 'of Ragman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ragman', 'n.', 'A man who collects, or deals in, rags.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ragman', 'n.', 'A document having many names or numerous seals, as a papal\n   bull.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ragmans roll', '', 'The rolls of deeds on parchment in which the Scottish\n   nobility and gentry subscribed allegiance to Edward I. of England, A.\n   D. 1296.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ragout', 'n.', 'A dish made of pieces of meat, stewed, and highly seasoned;\n   as, a ragout of mutton.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ragpicker', 'n.', 'One who gets a living by picking up rags and refuse\n   things in the streets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raguled', 'a.', 'Alt. of Ragguled');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ragguled', 'a.', 'Notched in regular diagonal breaks; -- said of a line, or\n   a bearing having such an edge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ragweed', 'n.', 'A common American composite weed (Ambrosia\n   artemisiaefolia) with finely divided leaves; hogweed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ragwork', 'n.', 'A kind of rubblework. In the United States, any rubblework\n   of thin and small stones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ragwort', 'n.', 'A name given to several species of the composite genus\n   Senecio.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raia', 'n.', 'A genus of rays which includes the skates. See Skate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raiae', 'n. pl.', 'The order of elasmobranch fishes which includes the\n   sawfishes, skates, and rays; -- called also Rajae, and Rajii.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raid', 'n.', 'A hostile or predatory incursion; an inroad or incursion of\n   mounted men; a sudden and rapid invasion by a cavalry force; a foray.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raid', 'n.', 'An attack or invasion for the purpose of making arrests,\n   seizing property, or plundering; as, a raid of the police upon a\n   gambling house; a raid of contractors on the public treasury.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raided', 'imp. & p. p.', 'of Raid');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raiding', 'p. pr. & vb. n.', 'of Raid');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raid', 'v. t.', 'To make a raid upon or into; as, two regiments raided the\n   border counties.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raider', 'n.', 'One who engages in a raid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rail', 'n.', 'An outer cloak or covering; a neckerchief for women.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rail', 'v. i.', 'To flow forth; to roll out; to course.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rail', 'n.', 'A bar of timber or metal, usually horizontal or nearly so,\n   extending from one post or support to another, as in fences,\n   balustrades, staircases, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rail', 'n.', 'A horizontal piece in a frame or paneling. See Illust. of\n   Style.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rail', 'n.', 'A bar of steel or iron, forming part of the track on which\n   the wheels roll. It is usually shaped with reference to vertical\n   strength, and is held in place by chairs, splices, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rail', 'n.', 'The stout, narrow plank that forms the top of the bulwarks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rail', 'n.', 'The light, fencelike structures of wood or metal at the break\n   of the deck, and elsewhere where such protection is needed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Railed', 'imp. & p. p.', 'of Rail');
  INSERT INTO words (word, word_type, description) VALUES  (    'Railing', 'p. pr. & vb. n.', 'of Rail');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rail', 'v. t.', 'To inclose with rails or a railing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rail', 'v. t.', 'To range in a line.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rail', 'v.', 'Any one of numerous species of limicoline birds of the family\n   Rallidae, especially those of the genus Rallus, and of closely allied\n   genera. They are prized as game birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rail', 'v. i.', 'To use insolent and reproachful language; to utter\n   reproaches; to scoff; -- followed by at or against, formerly by on.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rail', 'v. t.', 'To rail at.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rail', 'v. t.', 'To move or influence by railing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Railer', 'n.', 'One who rails; one who scoffs, insults, censures, or\n   reproaches with opprobrious language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Railing', 'a.', 'Expressing reproach; insulting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Railing', 'n.', 'A barrier made of a rail or of rails.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Railing', 'n.', 'Rails in general; also, material for making rails.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Railingly', 'adv.', 'With scoffing or insulting language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raillery', 'n.', 'Pleasantry or slight satire; banter; jesting language;\n   satirical merriment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Railleur', 'n.', 'A banterer; a jester; a mocker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Railroad', 'n.', 'Alt. of Railway');
  INSERT INTO words (word, word_type, description) VALUES  (    'Railway', 'n.', 'A road or way consisting of one or more parallel series of\n   iron or steel rails, patterned and adjusted to be tracks for the wheels\n   of vehicles, and suitably supported on a bed or substructure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Railway', 'n.', 'The road, track, etc., with all the lands, buildings,\n   rolling stock, franchises, etc., pertaining to them and constituting\n   one property; as, a certain railroad has been put into the hands of a\n   receiver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Railroading', 'n.', 'The construction of a railroad; the business of\n   managing or operating a railroad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raiment', 'n.', 'Clothing in general; vesture; garments; -- usually\n   singular in form, with a collective sense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raiment', 'n.', 'An article of dress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rain', 'n. & v.', 'Reign.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rain', 'n.', 'Water falling in drops from the clouds; the descent of water\n   from the clouds in drops.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rained', 'imp. & p. p.', 'of Rain');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raining', 'p. pr. & vb. n.', 'of Rain');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rain', 'n.', 'To fall in drops from the clouds, as water; -- used mostly\n   with it for a nominative; as, it rains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rain', 'n.', 'To fall or drop like water from the clouds; as, tears rained\n   from their eyes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rain', 'v. t.', 'To pour or shower down from above, like rain from the\n   clouds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rain', 'v. t.', 'To bestow in a profuse or abundant manner; as, to rain\n   favors upon a person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rainbow', 'n.', 'A bow or arch exhibiting, in concentric bands, the several\n   colors of the spectrum, and formed in the part of the hemisphere\n   opposite to the sun by the refraction and reflection of the suns rays\n   in drops of falling rain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rainbowed', 'a.', 'Formed with or like a rainbow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raindeer', 'n.', 'See Reindeer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raindrop', 'n.', 'A drop of rain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rainfall', 'n.', 'A fall or descent of rain; the water, or amount of water,\n   that falls in rain; as, the average annual rainfall of a region.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raininess', 'n.', 'The state of being rainy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rainless', 'a.', 'Destitute of rain; as, a rainless region.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rain-tight', 'a.', 'So tight as to exclude rain; as, a rain-tight roof.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rainy', 'a.', 'Abounding with rain; wet; showery; as, rainy weather; a\n   rainy day or season.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raip', 'n.', 'A rope; also, a measure equal to a rod.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rais', 'n.', 'Same as 2d Reis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raisable', 'a.', 'Capable of being raised.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raised', 'imp. & p. p.', 'of Raise');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raising', 'p. pr. & vb. n.', 'of Raise');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raise', 'v. t.', 'To cause to rise; to bring from a lower to a higher\n   place; to lift upward; to elevate; to heave; as, to raise a stone or\n   weight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raise', 'v. t.', 'To bring to a higher condition or situation; to elevate\n   in rank, dignity, and the like; to increase the value or estimation of;\n   to promote; to exalt; to advance; to enhance; as, to raise from a low\n   estate; to raise to office; to raise the price, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raise', 'v. t.', 'To increase the strength, vigor, or vehemence of; to\n   excite; to intensify; to invigorate; to heighten; as, to raise the\n   pulse; to raise the voice; to raise the spirits or the courage; to\n   raise the heat of a furnace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raise', 'v. t.', 'To elevate in degree according to some scale; as, to\n   raise the pitch of the voice; to raise the temperature of a room.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raise', 'v. t.', 'To cause to rise up, or assume an erect position or\n   posture; to set up; to make upright; as, to raise a mast or flagstaff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raise', 'v. t.', 'To cause to spring up from a recumbent position, from a\n   state of quiet, or the like; to awaken; to arouse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raise', 'v. t.', 'To rouse to action; to stir up; to incite to tumult,\n   struggle, or war; to excite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raise', 'v. t.', 'To bring up from the lower world; to call up, as a spirit\n   from the world of spirits; to recall from death; to give life to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raise', 'v. t.', 'To cause to arise, grow up, or come into being or to\n   appear; to give rise to; to originate, produce, cause, effect, or the\n   like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raise', 'v. t.', 'To form by the accumulation of materials or constituent\n   parts; to build up; to erect; as, to raise a lofty structure, a wall, a\n   heap of stones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raise', 'v. t.', 'To bring together; to collect; to levy; to get together\n   or obtain for use or service; as, to raise money, troops, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raise', 'v. t.', 'To cause to grow; to procure to be produced, bred, or\n   propagated; to grow; as, to raise corn, barley, hops, etc.; toraise\n   cattle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raise', 'v. t.', 'To bring into being; to produce; to cause to arise, come\n   forth, or appear; -- often with up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raise', 'v. t.', 'To give rise to; to set agoing; to occasion; to start; to\n   originate; as, to raise a smile or a blush.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raise', 'v. t.', 'To give vent or utterance to; to utter; to strike up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raise', 'v. t.', 'To bring to notice; to submit for consideration; as, to\n   raise a point of order; to raise an objection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raise', 'v. t.', 'To cause to rise, as by the effect of leaven; to make\n   light and spongy, as bread.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raise', 'v. t.', 'To cause (the land or any other object) to seem higher by\n   drawing nearer to it; as, to raise Sandy Hook light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raise', 'v. t.', 'To let go; as in the command, Raise tacks and sheets, i.\n   e., Let go tacks and sheets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raise', 'v. t.', 'To create or constitute; as, to raise a use, that is, to\n   create it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raised', 'a.', 'Lifted up; showing above the surroundings; as, raised or\n   embossed metal work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raised', 'a.', 'Leavened; made with leaven, or yeast; -- used of bread,\n   cake, etc., as distinguished from that made with cream of tartar, soda,\n   etc. See Raise, v. t., 4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raiser', 'n.', 'One who, or that which, raises (in various senses of the\n   verb).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raisin', 'n.', 'A grape, or a bunch of grapes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raisin', 'n.', 'A grape dried in the sun or by artificial heat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raising', 'n.', 'The act of lifting, setting up, elevating, exalting,\n   producing, or restoring to life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raising', 'n.', 'Specifically, the operation or work of setting up the\n   frame of a building; as, to help at a raising.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raising', 'n.', 'The operation of embossing sheet metal, or of forming it\n   into cup-shaped or hollow articles, by hammering, stamping, or\n   spinning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raisonne', 'a.', 'Arranged systematically, or according to classes or\n   subjects; as, a catalogue raisonne. See under Catalogue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raivel', 'n.', 'A separator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raj', 'n.', 'Reign; rule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raja', 'n.', 'Same as Rajah.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rajah', 'a.', 'A native prince or king; also, a landholder or person of\n   importance in the agricultural districts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rajahship', 'n.', 'The office or dignity of a rajah.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rajpoot', 'n.', 'Alt. of Rajput');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rajput', 'n.', 'A Hindoo of the second, or royal and military, caste; a\n   Kshatriya; especially, an inhabitant of the country of Rajpootana, in\n   northern central India.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rake', 'n.', 'An implement consisting of a headpiece having teeth, and a\n   long handle at right angles to it, -- used for collecting hay, or other\n   light things which are spread over a large surface, or for breaking and\n   smoothing the earth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rake', 'n.', 'A toothed machine drawn by a horse, -- used for collecting\n   hay or grain; a horserake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rake', 'n.', 'A fissure or mineral vein traversing the strata vertically,\n   or nearly so; -- called also rake-vein.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raked', 'imp. & p. p.', 'of Rake');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raking', 'p. pr. & vb. n.', 'of Rake');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rake', 'v. t.', 'To collect with a rake; as, to rake hay; -- often with up;\n   as, he raked up the fallen leaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rake', 'v. t.', 'To collect or draw together with laborious industry; to\n   gather from a wide space; to scrape together; as, to rake together\n   wealth; to rake together slanderous tales; to rake together the rabble\n   of a town.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rake', 'v. t.', 'To pass a rake over; to scrape or scratch with a rake for\n   the purpose of collecting and clearing off something, or for stirring\n   up the soil; as, to rake a lawn; to rake a flower bed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rake', 'v. t.', 'To search through; to scour; to ransack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rake', 'v. t.', 'To scrape or scratch across; to pass over quickly and\n   lightly, as a rake does.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rake', 'v. t.', 'To enfilade; to fire in a direction with the length of; in\n   naval engagements, to cannonade, as a ship, on the stern or head so\n   that the balls range the whole length of the deck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rake', 'v. i.', 'To use a rake, as for searching or for collecting; to\n   scrape; to search minutely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rake', 'v. i.', 'To pass with violence or rapidity; to scrape along.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rake', 'n.', 'The inclination of anything from a perpendicular direction;\n   as, the rake of a roof, a staircase, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rake', 'n.', 'the inclination of a mast or funnel, or, in general, of any\n   part of a vessel not perpendicular to the keel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rake', 'v. i.', 'To incline from a perpendicular direction; as, a mast\n   rakes aft.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rake', 'n.', 'A loose, disorderly, vicious man; a person addicted to\n   lewdness and other scandalous vices; a debauchee; a roue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rake', 'v. i.', 'To walk about; to gad or ramble idly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rake', 'v. i.', 'To act the rake; to lead a dissolute, debauched life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rakehell', 'n.', 'A lewd, dissolute fellow; a debauchee; a rake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rakehell', 'a.', 'Alt. of Rakehelly');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rakehelly', 'a.', 'Dissolute; wild; lewd; rakish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rakel', 'a.', 'Hasty; reckless; rash.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raker', 'n.', 'One who, or that which, rakes');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raker', 'n.', 'A person who uses a rake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raker', 'n.', 'A machine for raking grain or hay by horse or other power.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raker', 'n.', 'A gun so placed as to rake an enemys ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raker', 'n.', 'See Gill rakers, under 1st Gill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rakery', 'n.', 'Debauchery; lewdness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rakeshame', 'n.', 'A vile, dissolute wretch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rakestale', 'n.', 'The handle of a rake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rake-vein', 'n.', 'See Rake, a mineral vein.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raking', 'n.', 'The act or process of using a rake; the going over a space\n   with a rake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raking', 'n.', 'A space gone over with a rake; also, the work done, or the\n   quantity of hay, grain, etc., collected, by going once over a space\n   with a rake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rakish', 'a.', 'Dissolute; lewd; debauched.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rakish', 'a.', 'Having a saucy appearance indicative of speed and dash.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rakishly', 'adv.', 'In a rakish manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rakishness', 'n.', 'The quality or state of being rakish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raku ware', '', 'A kind of earthenware made in Japan, resembling Satsuma\n   ware, but having a paler color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rale', 'n.', 'An adventitious sound, usually of morbid origin, accompanying\n   the normal respiratory sounds. See Rhonchus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rallentando', 'a.', 'Slackening; -- a direction to perform a passage with a\n   gradual decrease in time and force; ritardando.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ralliance', 'n.', 'The act of rallying.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rallier', 'n.', 'One who rallies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ralline', 'a.', 'Pertaining to the rails.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rallied', 'imp. & p. p.', 'of Rally');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rallying', 'p. pr. & vb. n.', 'of Rally');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rally', 'v. t.', 'To collect, and reduce to order, as troops dispersed or\n   thrown into confusion; to gather again; to reunite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rally', 'v. i.', 'To come into orderly arrangement; to renew order, or\n   united effort, as troops scattered or put to flight; to assemble; to\n   unite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rally', 'v. i.', 'To collect ones vital powers or forces; to regain health\n   or consciousness; to recuperate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rally', 'v. i.', 'To recover strength after a decline in prices; -- said of\n   the market, stocks, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rallies', 'pl. ', 'of Rally');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rally', 'n.', 'The act or process of rallying (in any of the senses of that\n   word).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rally', 'n.', 'A political mass meeting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rally', 'v. t.', 'To attack with raillery, either in good humor and\n   pleasantry, or with slight contempt or satire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rally', 'v. i.', 'To use pleasantry, or satirical merriment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rally', 'n.', 'Good-humored raillery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ralph', 'n.', 'A name sometimes given to the raven.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ralstonite', 'n.', 'A fluoride of alumina and soda occurring with the\n   Greenland cryolite in octahedral crystals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ram', 'n.', 'The male of the sheep and allied animals. In some parts of\n   England a ram is called a tup.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ram', 'n.', 'Aries, the sign of the zodiac which the sun enters about the\n   21st of March.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ram', 'n.', 'The constellation Aries, which does not now, as formerly,\n   occupy the sign of the same name.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ram', 'n.', 'An engine of war used for butting or battering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ram', 'n.', 'In ancient warfare, a long beam suspended by slings in a\n   framework, and used for battering the walls of cities; a battering-ram.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ram', 'n.', 'A heavy steel or iron beak attached to the prow of a steam war\n   vessel for piercing or cutting down the vessel of an enemy; also, a\n   vessel carrying such a beak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ram', 'n.', 'A hydraulic ram. See under Hydraulic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ram', 'n.', 'The weight which strikes the blow, in a pile driver, steam\n   hammer, stamp mill, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ram', 'n.', 'The plunger of a hydraulic press.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rammed', 'imp. & p. p.', 'of Ram');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ramming', 'p. pr. & vb. n.', 'of Ram');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ram', 'v. t.', 'To butt or strike against; to drive a ram against or\n   through; to thrust or drive with violence; to force in; to drive\n   together; to cram; as, to ram an enemys vessel; to ram piles,\n   cartridges, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ram', 'v. t.', 'To fill or compact by pounding or driving.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ramadan', 'n.', 'The ninth Mohammedan month.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ramadan', 'n.', 'The great annual fast of the Mohammedans, kept during\n   daylight through the ninth month.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ramage', 'n.', 'Boughs or branches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ramage', 'n.', 'Warbling of birds in trees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ramage', 'a.', 'Wild; untamed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ramagious', 'a.', 'Wild; not tame.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ramal', 'a.', 'Of or pertaining to a ramus, or branch; rameal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ramayana', 'n.', 'The more ancient of the two great epic poems in Sanskrit.\n   The hero and heroine are Rama and his wife Sita.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ramberge', 'n.', 'Formerly, a kind of large war galley.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rambled', 'imp. & p. p.', 'of Ramble');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rambling', 'p. pr. & vb. n.', 'of Ramble');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ramble', 'v. i.', 'To walk, ride, or sail, from place to place, without any\n   determinate object in view; to roam carelessly or irregularly; to rove;\n   to wander; as, to ramble about the city; to ramble over the world.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ramble', 'v. i.', 'To talk or write in a discursive, aimless way.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ramble', 'v. i.', 'To extend or grow at random.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ramble', 'n.', 'A going or moving from place to place without any\n   determinate business or object; an excursion or stroll merely for\n   recreation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ramble', 'n.', 'A bed of shale over the seam.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rambler', 'n.', 'One who rambles; a rover; a wanderer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rambling', 'a.', 'Roving; wandering; discursive; as, a rambling fellow,\n   talk, or building.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ramblingly', 'adv.', 'In a rambling manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rambooze', 'n.', 'A beverage made of wine, ale (or milk), sugar, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rambutan', 'n.', 'A Malayan fruit produced by the tree Nephelium lappaceum,\n   and closely related to the litchi nut. It is bright red, oval in shape,\n   covered with coarse hairs (whence the name), and contains a pleasant\n   acid pulp. Called also ramboostan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rameal', 'a.', 'Same as Ramal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ramean', 'n.', 'A Ramist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ramed', 'a.', 'Having the frames, stem, and sternpost adjusted; -- said of\n   a ship on the stocks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ramee', 'n.', 'See Ramie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ramekin', 'n.', 'See Ramequin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rament', 'n.', 'A scraping; a shaving.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rament', 'n.', 'Ramenta.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ramenta', 'n. pl.', 'Thin brownish chaffy scales upon the leaves or young\n   shoots of some plants, especially upon the petioles and leaves of\n   ferns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ramentaceous', 'a.', 'Covered with ramenta.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rameous', 'a.', 'Ramal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ramequin', 'n.', 'A mixture of cheese, eggs, etc., formed in a mold, or\n   served on bread.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ramie', 'n.', 'The grass-cloth plant (B/hmeria nivea); also, its fiber,\n   which is very fine and exceedingly strong; -- called also China grass,\n   and rhea. See Grass-cloth plant, under Grass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ramification', 'n.', 'The process of branching, or the development of\n   branches or offshoots from a stem; also, the mode of their arrangement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ramification', 'n.', 'A small branch or offshoot proceeding from a main\n   stock or channel; as, the ramifications of an artery, vein, or nerve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ramification', 'n.', 'A division into principal and subordinate classes,\n   heads, or departments; also, one of the subordinate parts; as, the\n   ramifications of a subject or scheme.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ramification', 'n.', 'The production of branchlike figures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ramiflorous', 'a.', 'Flowering on the branches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ramiform', 'a.', 'Having the form of a branch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ramified', 'imp. & p. p.', 'of Ramify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ramifying', 'p. pr. & vb. n.', 'of Ramify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ramify', 'v. t.', 'To divide into branches or subdivisions; as, to ramify\n   an art, subject, scheme.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ramify', 'v. i.', 'To shoot, or divide, into branches or subdivisions, as\n   the stem of a plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ramify', 'v. i.', 'To be divided or subdivided, as a main subject.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ramigerous', 'a.', 'Bearing branches; branched.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ramiparous', 'a.', 'Producing branches; ramigerous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ramist', 'n.', 'A follower of Pierre Rame, better known as Ramus, a\n   celebrated French scholar, who was professor of rhetoric and philosophy\n   at Paris in the reign of Henry II., and opposed the Aristotelians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ramline', 'n.', 'A line used to get a straight middle line, as on a spar,\n   or from stem to stern in building a vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rammel', 'n.', 'Refuse matter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rammer', 'n.', 'One who, or that which, rams or drives.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rammer', 'n.', 'An instrument for driving anything with force; as, a rammer\n   for driving stones or piles, or for beating the earth to more solidity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rammer', 'n.', 'A rod for forcing down the charge of a gun; a ramrod');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rammer', 'n.', 'An implement for pounding the sand of a mold to render it\n   compact.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rammish', 'a.', 'Like a ram; hence, rank; lascivious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rammishness', 'n.', 'The quality of being rammish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rammy', 'a.', 'Like a ram; rammish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ramollescence', 'n.', 'A softening or mollifying.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ramoon', 'n.', 'A small West Indian tree (Trophis Americana) of the\n   Mulberry family, whose leaves and twigs are used as fodder for cattle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ramose', 'a.', 'Branched, as the stem or root of a plant; having lateral\n   divisions; consisting of, or having, branches; full of branches;\n   ramifying; branching; branchy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ramous', 'a.', 'Ramose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ramped', 'imp. & p. p.', 'of Ramp');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ramping', 'p. pr. & vb. n.', 'of Ramp');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ramp', 'v. i.', 'To spring; to leap; to bound; to rear; to prance; to\n   become rampant; hence, to frolic; to romp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ramp', 'v. i.', 'To move by leaps, or as by leaps; hence, to move swiftly\n   or with violence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ramp', 'v. i.', 'To climb, as a plant; to creep up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ramp', 'n.', 'A leap; a spring; a hostile advance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ramp', 'n.', 'A highwayman; a robber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ramp', 'n.', 'A romping woman; a prostitute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ramp', 'n.', 'Any sloping member, other than a purely constructional one,\n   such as a continuous parapet to a staircase.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ramp', 'n.', 'A short bend, slope, or curve, where a hand rail or cap\n   changes its direction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ramp', 'n.', 'An inclined plane serving as a communication between\n   different interior levels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rampacious', 'a.', 'High-spirited; rampageous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rampage', 'v.', 'Violent or riotous behavior; a state of excitement,\n   passion, or debauchery; as, to be on the rampage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rampage', 'v. i.', 'To leap or prance about, as an animal; to be violent;\n   to rage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rampageous', 'a.', 'Characterized by violence and passion; unruly; rampant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rampallian', 'n.', 'A mean wretch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rampancy', 'n.', 'The quality or state of being rampant; excessive action\n   or development; exuberance; extravagance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rampant', 'v.', 'Ramping; leaping; springing; rearing upon the hind legs;\n   hence, raging; furious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rampant', 'v.', 'Ascending; climbing; rank in growth; exuberant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rampant', 'v.', 'Rising with fore paws in the air as if attacking; -- said\n   of a beast of prey, especially a lion. The right fore leg and right\n   hind leg should be raised higher than the left.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rampantly', 'adv.', 'In a rampant manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rampart', 'n.', 'That which fortifies and defends from assault; that which\n   secures safety; a defense or bulwark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rampart', 'n.', 'A broad embankment of earth round a place, upon which the\n   parapet is raised. It forms the substratum of every permanent\n   fortification.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ramparted', 'imp. & p. p.', 'of Rampart');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ramparting', 'p. pr. & vb. n.', 'of Rampart');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rampart', 'v. t.', 'To surround or protect with, or as with, a rampart or\n   ramparts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rampe', 'n.', 'The cuckoopint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rampier', 'n.', 'See Rampart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rampion', 'n.', 'A plant (Campanula Rapunculus) of the Bellflower family,\n   with a tuberous esculent root; -- also called ramps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rampire', 'n.', 'A rampart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rampire', 'v. t.', 'To fortify with a rampire; to form into a rampire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rampler', 'n.', 'A rambler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rampler', 'a.', 'Roving; rambling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ramrod', 'n.', 'The rod used in ramming home the charge in a muzzle-loading\n   firearm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ramshackle', 'a.', 'Loose; disjointed; falling to pieces; out of repair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ramshackle', 'v. t.', 'To search or ransack; to rummage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ramson', 'n.', 'A broad-leaved species of garlic (Allium ursinum), common\n   in European gardens; -- called also buckram.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ramsted', 'n.', 'A yellow-flowered weed; -- so named from a Mr. Ramsted who\n   introduced it into Pennsylvania. See Toad flax. Called also Ramsted\n   weed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ramulose', 'a.', 'Having many small branches, or ramuli.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ramulous', 'a.', 'Ramulose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ramuli', 'pl. ', 'of Ramulus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ramulus', 'n.', 'A small branch, or branchlet, of corals, hydroids, and\n   similar organisms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rami', 'pl. ', 'of Ramus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ramus', 'n.', 'A branch; a projecting part or prominent process; a\n   ramification.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ramuscule', 'n.', 'A small ramus, or branch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ran', '', 'imp. of Run.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ran', 'n.', 'Open robbery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ran', 'n.', 'Yarns coiled on a spun-yarn winch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rana', 'n.', 'A genus of anurous batrachians, including the common frogs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ranal', 'a.', 'Having a general affinity to ranunculaceous plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rance', 'n.', 'A prop or shore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rance', 'n.', 'A round between the legs of a chair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rancescent', 'a.', 'Becoming rancid or sour.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ranch', 'v. t.', 'To wrench; to tear; to sprain; to injure by violent\n   straining or contortion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ranch', 'n.', 'A tract of land used for grazing and the rearing of horses,\n   cattle, or sheep. See Rancho, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rancheros', 'pl. ', 'of Ranchero');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ranchero', 'n.', 'A herdsman; a peasant employed on a ranch or rancho.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ranchero', 'n.', 'The owner and occupant of a ranch or rancho.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ranchmen', 'pl. ', 'of Ranchman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ranchman', 'n.', 'An owner or occupant of, or laborer on, a ranch; a\n   herdsman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ranchos', 'pl. ', 'of Rancho');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rancho', 'n.', 'A rude hut, as of posts, covered with branches or thatch,\n   where herdsmen or farm laborers may live or lodge at night.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rancho', 'n.', 'A large grazing farm where horses and cattle are raised; --\n   distinguished from hacienda, a cultivated farm or plantation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rancid', 'a.', 'Having a rank smell or taste, from chemical change or\n   decomposition; musty; as, rancid oil or butter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rancidity', 'n.', 'The quality or state of being rancid; a rancid scent or\n   flavor, as of old oil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rancidly', 'adv.', 'In a rancid manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rancidness', 'n.', 'The quality of being rancid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rancor', 'n.', 'The deepest malignity or spite; deep-seated enmity or\n   malice; inveterate hatred.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rancorous', 'a.', 'Full of rancor; evincing, or caused by, rancor; deeply\n   malignant; implacably spiteful or malicious; intensely virulent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rancorously', 'adv.', 'In a rancorous manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rand', 'n.', 'A border; edge; margin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rand', 'n.', 'A long, fleshy piece, as of beef, cut from the flank or leg;\n   a sort of steak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rand', 'n.', 'A thin inner sole for a shoe; also, a leveling slip of\n   leather applied to the sole before attaching the heel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rand', 'v. i.', 'To rant; to storm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Randall grass', '', 'The meadow fescue (Festuca elatior). See under Grass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Randan', 'n.', 'The product of a second sifting of meal; the finest part of\n   the bran.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Randan', 'n.', 'A boat propelled by three rowers with four oars, the middle\n   rower pulling two.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Randing', 'n.', 'The act or process of making and applying rands for shoes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Randing', 'n.', 'A kind of basket work used in gabions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Random', 'n.', 'Force; violence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Random', 'n.', 'A roving motion; course without definite direction; want of\n   direction, rule, or method; hazard; chance; -- commonly used in the\n   phrase at random, that is, without a settled point of direction; at\n   hazard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Random', 'n.', 'Distance to which a missile is cast; range; reach; as, the\n   random of a rifle ball.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Random', 'n.', 'The direction of a rake-vein.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Random', 'a.', 'Going at random or by chance; done or made at hazard, or\n   without settled direction, aim, or purpose; hazarded without previous\n   calculation; left to chance; haphazard; as, a random guess.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Randomly', 'adv.', 'In a random manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Randon', 'n.', 'Random.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Randon', 'v. i.', 'To go or stray at random.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ranedeer', 'n.', 'See Reindeer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ranee', 'n.', 'Same as Rani.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ranforce', 'n.', 'See Re/nforce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rang', '', 'imp. of Ring, v. t. & i.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ranged', 'imp. & p. p.', 'of Range');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ranging', 'p. pr. & vb. n.', 'of Range');
  INSERT INTO words (word, word_type, description) VALUES  (    'Range', 'n.', 'To set in a row, or in rows; to place in a regular line or\n   lines, or in ranks; to dispose in the proper order; to rank; as, to\n   range soldiers in line.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Range', 'n.', 'To place (as a single individual) among others in a line,\n   row, or order, as in the ranks of an army; -- usually, reflexively and\n   figuratively, (in the sense) to espouse a cause, to join a party, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Range', 'n.', 'To separate into parts; to sift.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Range', 'n.', 'To dispose in a classified or in systematic order; to\n   arrange regularly; as, to range plants and animals in genera and\n   species.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Range', 'n.', 'To rove over or through; as, to range the fields.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Range', 'n.', 'To sail or pass in a direction parallel to or near; as, to\n   range the coast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Range', 'n.', 'To be native to, or to live in; to frequent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Range', 'v. i.', 'To rove at large; to wander without restraint or\n   direction; to roam.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Range', 'v. i.', 'To have range; to change or differ within limits; to be\n   capable of projecting, or to admit of being projected, especially as to\n   horizontal distance; as, the temperature ranged through seventy degrees\n   Fahrenheit; the gun ranges three miles; the shot ranged four miles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Range', 'v. i.', 'To be placed in order; to be ranked; to admit of\n   arrangement or classification; to rank.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Range', 'v. i.', 'To have a certain direction; to correspond in direction;\n   to be or keep in a corresponding line; to trend or run; -- often\n   followed by with; as, the front of a house ranges with the street; to\n   range along the coast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Range', 'v. i.', 'To be native to, or live in, a certain district or\n   region; as, the peba ranges from Texas to Paraguay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Range', 'v.', 'A series of things in a line; a row; a rank; as, a range of\n   buildings; a range of mountains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Range', 'v.', 'An aggregate of individuals in one rank or degree; an order;\n   a class.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Range', 'v.', 'The step of a ladder; a rung.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Range', 'v.', 'A kitchen grate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Range', 'v.', 'An extended cooking apparatus of cast iron, set in\n   brickwork, and affording conveniences for various ways of cooking;\n   also, a kind of cooking stove.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Range', 'v.', 'A bolting sieve to sift meal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Range', 'v.', 'A wandering or roving; a going to and fro; an excursion; a\n   ramble; an expedition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Range', 'v.', 'That which may be ranged over; place or room for excursion;\n   especially, a region of country in which cattle or sheep may wander and\n   pasture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Range', 'v.', 'Extent or space taken in by anything excursive; compass or\n   extent of excursion; reach; scope; discursive power; as, the range of\n   ones voice, or authority.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Range', 'v.', 'The region within which a plant or animal naturally lives.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Range', 'v.', 'The horizontal distance to which a shot or other projectile\n   is carried.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Range', 'v.', 'Sometimes, less properly, the trajectory of a shot or\n   projectile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Range', 'v.', 'A place where shooting, as with cannons or rifles, is\n   practiced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Range', 'v.', 'In the public land system of the United States, a row or\n   line of townships lying between two successive meridian lines six miles\n   apart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Range', 'v.', 'See Range of cable, below.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rangement', 'n.', 'Arrangement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ranger', 'n.', 'One who ranges; a rover; sometimes, one who ranges for\n   plunder; a roving robber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ranger', 'n.', 'That which separates or arranges; specifically, a sieve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ranger', 'n.', 'A dog that beats the ground in search of game.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ranger', 'n.', 'One of a body of mounted troops, formerly armed with short\n   muskets, who range over the country, and often fight on foot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ranger', 'n.', 'The keeper of a public park or forest; formerly, a sworn\n   officer of a forest, appointed by the kings letters patent, whose\n   business was to walk through the forest, recover beasts that had\n   strayed beyond its limits, watch the deer, present trespasses to the\n   next court held for the forest, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rangership', 'n.', 'The office of the keeper of a forest or park.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rangle', 'v. i.', 'To range about in an irregular manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rani', 'n.', 'A queen or princess; the wife of a rajah.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ranine', 'a.', 'Of or pertaining to the frogs and toads.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ranine', 'a.', 'Pertaining to, or designating, a swelling under the tongue;\n   also, pertaining to the region where the swelling occurs; -- applied\n   especially to branches of the lingual artery and lingual vein.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rank', 'superl.', 'Luxuriant in growth; of vigorous growth; exuberant;\n   grown to immoderate height; as, rank grass; rank weeds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rank', 'superl.', 'Raised to a high degree; violent; extreme; gross; utter;\n   as, rank heresy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rank', 'superl.', 'Causing vigorous growth; producing luxuriantly; very\n   rich and fertile; as, rank land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rank', 'superl.', 'Strong-scented; rancid; musty; as, oil of a rank smell;\n   rank-smelling rue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rank', 'superl.', 'Strong to the taste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rank', 'superl.', 'Inflamed with venereal appetite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rank', 'adv.', 'Rankly; stoutly; violently.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rank', 'n. & v.', 'A row or line; a range; an order; a tier; as, a rank of\n   osiers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rank', 'n. & v.', 'A line of soldiers ranged side by side; -- opposed to\n   file. See 1st File, 1 (a).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rank', 'n. & v.', 'Grade of official standing, as in the army, navy, or\n   nobility; as, the rank of general; the rank of admiral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rank', 'n. & v.', 'An aggregate of individuals classed together; a\n   permanent social class; an order; a division; as, ranks and orders of\n   men; the highest and the lowest ranks of men, or of other intelligent\n   beings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rank', 'n. & v.', 'Degree of dignity, eminence, or excellence; position in\n   civil or social life; station; degree; grade; as, a writer of the first\n   rank; a lawyer of high rank.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rank', 'n. & v.', 'Elevated grade or standing; high degree; high social\n   position; distinction; eminence; as, a man of rank.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ranked', 'imp. & p. p.', 'of Rank');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ranking', 'p. pr. & vb. n.', 'of Rank');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rank', 'v. t.', 'To place abreast, or in a line.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rank', 'v. t.', 'To range in a particular class, order, or division; to\n   class; also, to dispose methodically; to place in suitable classes or\n   order; to classify.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rank', 'v. t.', 'To take rank of; to outrank.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rank', 'v. i.', 'To be ranged; to be set or disposed, as in a particular\n   degree, class, order, or division.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rank', 'v. i.', 'To have a certain grade or degree of elevation in the\n   orders of civil or military life; to have a certain degree of esteem or\n   consideration; as, he ranks with the first class of poets; he ranks\n   high in public estimation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ranker', 'n.', 'One who ranks, or disposes in ranks; one who arranges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rankled', 'imp. & p. p.', 'of Rankle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rankling', 'p. pr. & vb. n.', 'of Rankle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rankle', 'a.', 'To become, or be, rank; to grow rank or strong; to be\n   inflamed; to fester; -- used literally and figuratively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rankle', 'a.', 'To produce a festering or inflamed effect; to cause a sore;\n   -- used literally and figuratively; as, a splinter rankles in the\n   flesh; the words rankled in his bosom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rankle', 'v. t.', 'To cause to fester; to make sore; to inflame.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rankly', 'adv.', 'With rank or vigorous growth; luxuriantly; hence,\n   coarsely; grossly; as, weeds grow rankly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rankness', 'n.', 'The condition or quality of being rank.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rannel', 'n.', 'A prostitute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ranny', 'n.', 'The erd shrew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ransacked', 'imp. & p. p.', 'of Ransack');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ransacking', 'p. pr. & vb. n.', 'of Ransack');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ransack', 'v. t.', 'To search thoroughly; to search every place or part of;\n   as, to ransack a house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ransack', 'v. t.', 'To plunder; to pillage completely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ransack', 'v. t.', 'To violate; to ravish; to defiour.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ransack', 'v. i.', 'To make a thorough search.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ransack', 'n.', 'The act of ransacking, or state of being ransacked;\n   pillage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ransom', 'n.', 'The release of a captive, or of captured property, by\n   payment of a consideration; redemption; as, prisoners hopeless of\n   ransom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ransom', 'n.', 'The money or price paid for the redemption of a prisoner,\n   or for goods captured by an enemy; payment for freedom from restraint,\n   penalty, or forfeit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ransom', 'n.', 'A sum paid for the pardon of some great offense and the\n   discharge of the offender; also, a fine paid in lieu of corporal\n   punishment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ransomed', 'imp. & p. p.', 'of Ransom');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ransoming', 'p. pr. & vb. n.', 'of Ransom');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ransom', 'n.', 'To redeem from captivity, servitude, punishment, or\n   forfeit, by paying a price; to buy out of servitude or penalty; to\n   rescue; to deliver; as, to ransom prisoners from an enemy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ransom', 'n.', 'To exact a ransom for, or a payment on.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ransomable', 'a.', 'Such as can be ransomed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ransomer', 'n.', 'One who ransoms or redeems.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ransomless', 'a.', 'Incapable of being ransomed; without ransom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ranted', 'imp. & p. p.', 'of Rant');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ranting', 'p. pr. & vb. n.', 'of Rant');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rant', 'v. i.', 'To rave in violent, high-sounding, or extravagant\n   language, without dignity of thought; to be noisy, boisterous, and\n   bombastic in talk or declamation; as, a ranting preacher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rant', 'n.', 'High-sounding language, without importance or dignity of\n   thought; boisterous, empty declamation; bombast; as, the rant of\n   fanatics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ranter', 'n.', 'A noisy talker; a raving declaimer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ranter', 'n.', 'One of a religious sect which sprung up in 1645; -- called\n   also Seekers. See Seeker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ranter', 'n.', 'One of the Primitive Methodists, who seceded from the\n   Wesleyan Methodists on the ground of their deficiency in fervor and\n   zeal; -- so called in contempt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ranterism', 'n.', 'The practice or tenets of the Ranters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rantingly', 'adv.', 'In a ranting manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rantipole', 'n.', 'A wild, romping young person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rantipole', 'a.', 'Wild; roving; rakish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rantipole', 'v. i.', 'To act like a rantipole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rantism', 'n.', 'Ranterism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ranty', 'a.', 'Wild; noisy; boisterous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ranula', 'n.', 'A cyst formed under the tongue by obstruction of the duct\n   of the submaxillary gland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ranunculaceous', 'a.', 'Of or pertaining to a natural order of plants\n   (Ranunculaceae), of which the buttercup is the type, and which includes\n   also the virgins bower, the monkshood, larkspur, anemone, meadow rue,\n   and peony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ranunculuses', 'pl. ', 'of Ranunculus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ranunculi', 'pl. ', 'of Ranunculus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ranunculus', 'n.', 'A genus of herbs, mostly with yellow flowers, including\n   crowfoot, buttercups, and the cultivated ranunculi (R. Asiaticus, R.\n   aconitifolius, etc.) in which the flowers are double and of various\n   colors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ranz des vaches', '', 'The name for numerous simple, but very irregular,\n   melodies of the Swiss mountaineers, blown on a long tube called the\n   Alpine horn, and sometimes sung.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rap', 'n.', 'A lay or skein containing 120 yards of yarn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rapped', 'imp. & p. p.', 'of Rap');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rapping', 'p. pr. & vb. n.', 'of Rap');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rap', 'v. i.', 'To strike with a quick, sharp blow; to knock; as, to rap on\n   the door.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rap', 'v. t.', 'To strike with a quick blow; to knock on.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rap', 'v. t.', 'To free (a pattern) in a mold by light blows on the\n   pattern, so as to facilitate its removal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rap', 'n.', 'A quick, smart blow; a knock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rapped', 'imp. & p. p.', 'of Rap');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rapt', '', 'of Rap');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rapping', 'p. pr. & vb. n.', 'of Rap');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rap', 'v.', 'To snatch away; to seize and hurry off.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rap', 'v.', 'To hasten.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rap', 'v.', 'To seize and bear away, as the mind or thoughts; to transport\n   out of ones self; to affect with ecstasy or rapture; as, rapt into\n   admiration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rap', 'v.', 'To exchange; to truck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rap', 'n.', 'A popular name for any of the tokens that passed current for a\n   half-penny in Ireland in the early part of the eighteenth century; any\n   coin of trifling value.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rapaces', 'n. pl.', 'Same as Accipitres.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rapacious', 'a.', 'Given to plunder; disposed or accustomed to seize by\n   violence; seizing by force.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rapacious', 'a.', 'Accustomed to seize food; subsisting on prey, or animals\n   seized by violence; as, a tiger is a rapacious animal; a rapacious\n   bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rapacious', 'a.', 'Avaricious; grasping; extortionate; also, greedy;\n   ravenous; voracious; as, rapacious usurers; a rapacious appetite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rapacity', 'n.', 'The quality of being rapacious; rapaciousness;\n   ravenousness; as, the rapacity of pirates; the rapacity of wolves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rapacity', 'n.', 'The act or practice of extorting or exacting by\n   oppressive injustice; exorbitant greediness of gain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raparee', 'n.', 'See Rapparee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rape', 'n.', 'Fruit, as grapes, plucked from the cluster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rape', 'n.', 'The refuse stems and skins of grapes or raisins from which\n   the must has been expressed in wine making.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rape', 'n.', 'A filter containing the above refuse, used in clarifying and\n   perfecting malt, vinegar, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rape', 'n.', 'The act of seizing and carrying away by force; violent\n   seizure; robbery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rape', 'n.', 'Sexual connection with a woman without her consent. See Age\n   of consent, under Consent, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rape', 'n.', 'That which is snatched away.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rape', 'n.', 'Movement, as in snatching; haste; hurry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rape', 'v. t.', 'To commit rape upon; to ravish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rape', 'v. i.', 'To rob; to pillage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rape', 'n.', 'One of six divisions of the county of Sussex, England,\n   intermediate between a hundred and a shire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rape', 'n.', 'A name given to a variety or to varieties of a plant of the\n   turnip kind, grown for seeds and herbage. The seeds are used for the\n   production of rape oil, and to a limited extent for the food of cage\n   birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rapeful', 'a.', 'Violent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rapeful', 'a.', 'Given to the commission of rape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rapfully', 'adv.', 'Violently.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raphaelesque', 'a.', 'Like Raphaels works; in Raphaels manner of\n   painting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raphaelism', 'n.', 'The principles of painting introduced by Raphael, the\n   Italian painter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raphaelite', 'n.', 'One who advocates or adopts the principles of\n   Raphaelism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raphany', 'n.', 'A convulsive disease, attended with ravenous hunger, not\n   uncommon in Sweden and Germany. It was so called because supposed to be\n   caused by eating corn with which seeds of jointed charlock (Raphanus\n   raphanistrum) had been mixed, but the condition is now known to be a\n   form of ergotism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raphe', 'n.', 'A line, ridge, furrow, or band of fibers, especially in the\n   median line; as, the raphe of the tongue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raphe', 'n.', 'Same as Rhaphe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raphides', 'n. pl.', 'See Rhaphides.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rapid', 'a.', 'Very swift or quick; moving with celerity; fast; as, a rapid\n   stream; a rapid flight; a rapid motion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rapid', 'a.', 'Advancing with haste or speed; speedy in progression; in\n   quick sequence; as, rapid growth; rapid improvement; rapid recurrence;\n   rapid succession.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rapid', 'a.', 'Quick in execution; as, a rapid penman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rapid', 'a.', 'The part of a river where the current moves with great\n   swiftness, but without actual waterfall or cascade; -- usually in the\n   plural; as, the Lachine rapids in the St. Lawrence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rapidity', 'n.', 'The quality or state of being rapid; swiftness; celerity;\n   velocity; as, the rapidity of a current; rapidity of speech; rapidity\n   of growth or improvement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rapidly', 'adv.', 'In a rapid manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rapidness', 'n.', 'Quality of being rapid; rapidity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rapier', 'n.', 'A straight sword, with a narrow and finely pointed blade,\n   used only for thrusting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rapiered', 'a.', 'Wearing a rapier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rapilli', 'n. pl.', 'Lapilli.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rapine', 'n.', 'The act of plundering; the seizing and carrying away of\n   things by force; spoliation; pillage; plunder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rapine', 'n.', 'Ravishment; rape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rapine', 'v. t.', 'To plunder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rapinous', 'a.', 'Given to rapine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rappage', 'n.', 'The enlargement of a mold caused by rapping the pattern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rapparee', 'n.', 'A wild Irish plunderer, esp. one of the 17th century; --\n   so called from his carrying a half-pike, called a rapary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rapped', '', 'imp. & p. p. of Rap, to strike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rapped', '', 'imp. & p. p. of Rap, to snatch away.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rappee', 'v.', 'A pungent kind of snuff made from the darker and ranker\n   kinds of tobacco leaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rappel', 'n.', 'The beat of the drum to call soldiers to arms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rapper', 'n.', 'One who, or that which, raps or knocks; specifically, the\n   knocker of a door.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rapper', 'n.', 'A forcible oath or lie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rapport', 'n.', 'Relation; proportion; conformity; correspondence; accord.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rapscallion', 'n.', 'A rascal; a good-for-nothing fellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rapt', '', 'imp. & p. p. of Rap, to snatch away.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rapt', 'a.', 'Snatched away; hurried away or along.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rapt', 'a.', 'Transported with love, admiration, delight, etc.; enraptured.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rapt', 'a.', 'Wholly absorbed or engrossed, as in work or meditation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rapt', 'a.', 'An ecstasy; a trance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rapt', 'a.', 'Rapidity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rapt', 'v. t.', 'To transport or ravish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rapt', 'v. t.', 'To carry away by force.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rapter', 'n.', 'A raptor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raptor', 'n.', 'A ravisher; a plunderer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raptores', 'n. pl.', 'Same as Accipitres. Called also Raptatores.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raptorial', 'a.', 'Rapacious; living upon prey; -- said especially of\n   certain birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raptorial', 'a.', 'Adapted for seizing prey; -- said of the legs, claws,\n   etc., of insects, birds, and other animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raptorial', 'a.', 'Of or pertaining to the Raptores. See Illust. (f) of\n   Aves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raptorious', 'a.', 'Raptorial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rapture', 'n.', 'A seizing by violence; a hurrying along; rapidity with\n   violence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rapture', 'n.', 'The state or condition of being rapt, or carried away from\n   ones self by agreeable excitement; violence of a pleasing passion;\n   extreme joy or pleasure; ecstasy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rapture', 'n.', 'A spasm; a fit; a syncope; delirium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raptured', 'imp. & p. p.', 'of Rapture');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rapturing', 'p. pr. & vb. n.', 'of Rapture');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rapture', 'v. t.', 'To transport with excitement; to enrapture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rapturist', 'n.', 'An enthusiast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rapturize', 'v. t. & i.', 'To put, or be put, in a state of rapture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rapturous', 'a.', 'Ecstatic; transporting; ravishing; feeling, expressing,\n   or manifesting rapture; as, rapturous joy, pleasure, or delight;\n   rapturous applause.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rapturously', 'adv.', 'In a rapturous manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rare', 'a.', 'Early.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rare', 'superl.', 'Nearly raw; partially cooked; not thoroughly cooked;\n   underdone; as, rare beef or mutton.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rare', 'superl.', 'Not frequent; seldom met with or occurring; unusual; as,\n   a rare event.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rare', 'superl.', 'Of an uncommon nature; unusually excellent; valuable to\n   a degree seldom found.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rare', 'superl.', 'Thinly scattered; dispersed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rare', 'superl.', 'Characterized by wide separation of parts; of loose\n   texture; not thick or dense; thin; as, a rare atmosphere at high\n   elevations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rarebit', 'n.', 'A dainty morsel; a Welsh rabbit. See Welsh rabbit, under\n   Rabbit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raree-show', 'n.', 'A show carried about in a box; a peep show.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rarefaction', 'n.', 'The act or process of rarefying; the state of being\n   rarefied; -- opposed to condensation; as, the rarefaction of air.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rarefiable', 'a.', 'Capable of being rarefied.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rarefied', 'imp. & p. p.', 'of Rarefy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rarefying', 'p. pr. & vb. n.', 'of Rarefy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rarefy', 'v. t.', 'To make rare, thin, porous, or less dense; to expand or\n   enlarge without adding any new portion of matter to; -- opposed to\n   condense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rarefy', 'v. i.', 'To become less dense; to become thin and porous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rarely', 'adv.', 'In a rare manner or degree; seldom; not often; as, things\n   rarely seen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rarely', 'adv.', 'Finely; excellently; with rare skill. See 3d Rare, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rareness', 'n.', 'The state or quality of being rare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rareripe', 'a.', 'Early ripe; ripe before others, or before the usual\n   season.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rareripe', 'n.', 'An early ripening fruit, especially a kind of freestone\n   peach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rarification', 'n.', 'See Rarefaction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rarities', 'pl. ', 'of Rarity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rarity', 'n.', 'The quality or state of being rare; rareness; thinness; as,\n   the rarity (contrasted with the density) of gases.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rarity', 'n.', 'That which is rare; an uncommon thing; a thing valued for\n   its scarcity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ras', 'n.', 'See 2d Reis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rasante', 'a.', 'Sweeping; grazing; -- applied to a style of fortification\n   in which the command of the works over each other, and over the\n   country, is kept very low, in order that the shot may more effectually\n   sweep or graze the ground before them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rascal', 'v.', 'One of the rabble; a low, common sort of person or\n   creature; collectively, the rabble; the common herd; also, a lean,\n   ill-conditioned beast, esp. a deer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rascal', 'v.', 'A mean, trickish fellow; a base, dishonest person; a rogue;\n   a scoundrel; a trickster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rascal', 'a.', 'Of or pertaining to the common herd or common people; low;\n   mean; base.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rascaldom', 'n.', 'State of being a rascal; rascality; domain of rascals;\n   rascals, collectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rascaless', 'n.', 'A female rascal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rascalities', 'pl. ', 'of Rascality');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rascality', 'n.', 'The quality or state of being rascally, or a rascal;\n   mean trickishness or dishonesty; base fraud.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rascality', 'n.', 'The poorer and lower classes of people.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rascallion', 'n.', 'A low, mean wretch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rascally', 'a.', 'Like a rascal; trickish or dishonest; base; worthless; --\n   often in humorous disparagement, without implication of dishonesty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rased', 'imp. & p. p.', 'of Rase');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rasing', 'p. pr. & vb. n.', 'of Rase');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rase', 'v. t.', 'To rub along the surface of; to graze.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rase', 'v. t.', 'To rub or scratch out; to erase.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rase', 'v. t.', 'To level with the ground; to overthrow; to destroy; to\n   raze.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rase', 'v. i.', 'To be leveled with the ground; to fall; to suffer\n   overthrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rase', 'n.', 'A scratching out, or erasure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rase', 'n.', 'A slight wound; a scratch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rase', 'n.', 'A way of measuring in which the commodity measured was made\n   even with the top of the measuring vessel by rasing, or striking off,\n   all that was above it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rash', 'v. t.', 'To pull off or pluck violently.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rash', 'v. t.', 'To slash; to hack; to cut; to slice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rash', 'n.', 'A fine eruption or efflorescence on the body, with little or\n   no elevation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rash', 'n.', 'An inferior kind of silk, or mixture of silk and worsted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rash', 'superl.', 'Sudden in action; quick; hasty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rash', 'superl.', 'Requiring sudden action; pressing; urgent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rash', 'superl.', 'Esp., overhasty in counsel or action; precipitate;\n   resolving or entering on a project or measure without due deliberation\n   and caution; opposed to prudent; said of persons; as, a rash statesman\n   or commander.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rash', 'superl.', 'Uttered or undertaken with too much haste or too little\n   reflection; as, rash words; rash measures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rash', 'superl.', 'So dry as to fall out of the ear with handling, as corn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rash', 'v. t.', 'To prepare with haste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rasher', 'n.', 'A thin slice of bacon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rasher', 'n.', 'A California rockfish (Sebastichthys miniatus).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rashful', 'a.', 'Rash; hasty; precipitate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rashling', 'n.', 'A rash person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rashly', 'adv.', 'In a rash manner; with precipitation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rashness', 'n.', 'The quality or state of being rash.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raskolnik', 'n.', 'One of the separatists or dissenters from the\n   established or Greek church in Russia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rasores', 'v. t.', 'An order of birds; the Gallinae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rasorial', 'a.', 'Of or pertaining to the Rasores, or gallinaceous birds,\n   as the peacock, domestic fowl, partridge, quail, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rasour', 'n.', 'Razor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rasped', 'imp. & p. p.', 'of Rasp');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rasping', 'p. pr. & vb. n.', 'of Rasp');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rasp', 'v. t.', 'To rub or file with a rasp; to rub or grate with a rough\n   file; as, to rasp wood to make it smooth; to rasp bones to powder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rasp', 'v. t.', 'Hence, figuratively: To grate harshly upon; to offend by\n   coarse or rough treatment or language; as, some sounds rasp the ear;\n   his insults rasped my temper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rasp', 'v.', 'A coarse file, on which the cutting prominences are distinct\n   points raised by the oblique stroke of a sharp punch, instead of lines\n   raised by a chisel, as on the true file.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rasp', 'v.', 'The raspberry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raspatorium', 'n.', 'See Raspatory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raspatory', 'v.', 'A surgeons rasp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raspberry', 'n.', 'The thimble-shaped fruit of the Rubus Idaeus and other\n   similar brambles; as, the black, the red, and the white raspberry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raspberry', 'n.', 'The shrub bearing this fruit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rasper', 'n.', 'One who, or that which, rasps; a scraper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raspis', 'n.', 'The raspberry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raspy', 'a.', 'Like a rasp, or the sound made by a rasp; grating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rasse', 'n.', 'A carnivore (Viverricula Mallaccensis) allied to the civet\n   but smaller, native of China and the East Indies. It furnishes a\n   perfume resembling that of the civet, which is highly prized by the\n   Javanese. Called also Malacca weasel, and lesser civet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rasure', 'v.', 'The act of rasing, scraping, or erasing; erasure;\n   obliteration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rasure', 'v.', 'A mark by which a letter, word, or any part of a writing or\n   print, is erased, effaced, or obliterated; an erasure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rat', 'n.', 'One of several species of small rodents of the genus Mus and\n   allied genera, larger than mice, that infest houses, stores, and ships,\n   especially the Norway, or brown, rat (M. decumanus), the black rat (M.\n   rattus), and the roof rat (M. Alexandrinus). These were introduced into\n   America from the Old World.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rat', 'n.', 'A round and tapering mass of hair, or similar material, used\n   by women to support the puffs and rolls of their natural hair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rat', 'n.', 'One who deserts his party or associates; hence, in the trades,\n   one who works for lower wages than those prescribed by a trades union.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ratted', 'imp. & p. p.', 'of Rat');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ratting', 'p. pr. & vb. n.', 'of Rat');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rat', 'v. i.', 'In English politics, to desert ones party from interested\n   motives; to forsake ones associates for ones own advantage; in the\n   trades, to work for less wages, or on other conditions, than those\n   established by a trades union.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rat', 'v. i.', 'To catch or kill rats.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rata', 'n.', 'A New Zealand forest tree (Metrosideros robusta), also, its\n   hard dark red wood, used by the Maoris for paddles and war clubs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ratability', 'n.', 'The quality or state of being ratable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ratable', 'a.', 'Capable of being rated, or set at a certain value.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ratable', 'a.', 'Liable to, or subjected by law to, taxation; as, ratable\n   estate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ratable', 'a.', 'Made at a proportionate rate; as, ratable payments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ratafia', 'n.', 'A spirituous liquor flavored with the kernels of cherries,\n   apricots, peaches, or other fruit, spiced, and sweetened with sugar; --\n   a term applied to the liqueurs called noyau, cura/ao, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ratan', 'n.', 'See Rattan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ratany', 'n.', 'Same as Rhatany.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rataplan', 'n.', 'The iterative sound of beating a drum, or of a galloping\n   horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ratch', 'n.', 'Same as Rotche.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ratch', 'n.', 'A ratchet wheel, or notched bar, with which a pawl or click\n   works.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ratchel', 'n.', 'Gravelly stone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ratchet', 'n.', 'A pawl, click, or detent, for holding or propelling a\n   ratchet wheel, or ratch, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ratchet', 'n.', 'A mechanism composed of a ratchet wheel, or ratch, and\n   pawl. See Ratchet wheel, below, and 2d Ratch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rate', 'v. t. & i.', 'To chide with vehemence; to scold; to censure\n   violently.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rate', 'n.', 'Established portion or measure; fixed allowance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rate', 'n.', 'That which is established as a measure or criterion; degree;\n   standard; rank; proportion; ratio; as, a slow rate of movement; rate of\n   interest is the ratio of the interest to the principal, per annum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rate', 'n.', 'Valuation; price fixed with relation to a standard; cost;\n   charge; as, high or low rates of transportation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rate', 'n.', 'A tax or sum assessed by authority on property for public\n   use, according to its income or value; esp., in England, a local tax;\n   as, parish rates; town rates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rate', 'n.', 'Order; arrangement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rate', 'n.', 'Ratification; approval.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rate', 'n.', 'The gain or loss of a timepiece in a unit of time; as, daily\n   rate; hourly rate; etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rate', 'n.', 'The order or class to which a war vessel belongs, determined\n   according to its size, armament, etc.; as, first rate, second rate,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rate', 'n.', 'The class of a merchant vessel for marine insurance,\n   determined by its relative safety as a risk, as A1, A2, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rated', 'imp. & p. p.', 'of Rate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rating', 'p. pr. & vb. n.', 'of Rate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rate', 'v. t.', 'To set a certain estimate on; to value at a certain price\n   or degree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rate', 'v. t.', 'To assess for the payment of a rate or tax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rate', 'v. t.', 'To settle the relative scale, rank, position, amount,\n   value, or quality of; as, to rate a ship; to rate a seaman; to rate a\n   pension.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rate', 'v. t.', 'To ratify.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rate', 'v. i.', 'To be set or considered in a class; to have rank; as, the\n   ship rates as a ship of the line.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rate', 'v. i.', 'To make an estimate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rateable', 'a.', 'See Ratable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ratel', 'n.', 'Any carnivore of the genus Mellivora, allied to the weasels\n   and the skunks; -- called also honey badger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ratepayer', 'n.', 'One who pays rates or taxes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rater', 'n.', 'One who rates or estimates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rater', 'n.', 'One who rates or scolds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ratfish', 'n.', 'Same as Rat-tail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rath', 'n.', 'A hill or mound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rath', 'n.', 'A kind of ancient fortification found in Ireland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rath', 'a.', 'Alt. of Rathe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rathe', 'a.', 'Coming before others, or before the usual time; early.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rath', 'adv.', 'Alt. of Rathe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rathe', 'adv.', 'Early; soon; betimes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rather', 'a.', 'Prior; earlier; former.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rather', 'a.', 'Earlier; sooner; before.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rather', 'a.', 'More readily or willingly; preferably.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rather', 'a.', 'On the other hand; to the contrary of what was said or\n   suggested; instead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rather', 'a.', 'Of two alternatives conceived of, this by preference to, or\n   as more likely than, the other; somewhat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rather', 'a.', 'More properly; more correctly speaking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rather', 'a.', 'In some degree; somewhat; as, the day is rather warm; the\n   house is rather damp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rathripe', 'a.', 'Rareripe, or early ripe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rathripe', 'n.', 'A rareripe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ratification', 'n.', 'The act of ratifying; the state of being ratified;\n   confirmation; sanction; as, the ratification of a treaty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ratifier', 'n.', 'One who, or that which, ratifies; a confirmer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ratified', 'imp. & p. p.', 'of Ratify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ratifying', 'p. pr. & vb. n.', 'of Ratify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ratify', 'n.', 'To approve and sanction; to make valid; to confirm; to\n   establish; to settle; especially, to give sanction to, as something\n   done by an agent or servant; as, to ratify an agreement, treaty, or\n   contract; to ratify a nomination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ratihabition', 'n.', 'Confirmation or approbation, as of an act or\n   contract.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ratio', 'n.', 'The relation which one quantity or magnitude has to another\n   of the same kind. It is expressed by the quotient of the division of\n   the first by the second; thus, the ratio of 3 to 6 is expressed by / or\n   /; of a to b by a/b; or (less commonly) the second term is made the\n   dividend; as, a:b = b/a.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ratio', 'n.', 'Hence, fixed relation of number, quantity, or degree; rate;\n   proportion; as, the ratio of representation in Congress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ratiocinate', 'v. i.', 'To reason, esp. deductively; to offer reason or\n   argument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ratiocination', 'n.', 'The process of reasoning, or deducing conclusions\n   from premises; deductive reasoning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ratiocinative', 'a.', 'Characterized by, or addicted to, ratiocination;\n   consisting in the comparison of propositions or facts, and the\n   deduction of inferences from the comparison; argumentative; as, a\n   ratiocinative process.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ratiocinatory', 'a.', 'Ratiocinative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ration', 'n.', 'A fixed daily allowance of provisions assigned to a soldier\n   in the army, or a sailor in the navy, for his subsistence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ration', 'n.', 'Hence, a certain portion or fixed amount dealt out; an\n   allowance; an allotment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ration', 'v. t.', 'To supply with rations, as a regiment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rational', 'a.', 'Relating to the reason; not physical; mental.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rational', 'a.', 'Having reason, or the faculty of reasoning; endowed with\n   reason or understanding; reasoning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rational', 'a.', 'Agreeable to reason; not absurd, preposterous,\n   extravagant, foolish, fanciful, or the like; wise; judicious; as,\n   rational conduct; a rational man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rational', 'a.', 'Expressing the type, structure, relations, and reactions\n   of a compound; graphic; -- said of formulae. See under Formula.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rational', 'n.', 'A rational being.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rationale', 'a.', 'An explanation or exposition of the principles of some\n   opinion, action, hypothesis, phenomenon, or the like; also, the\n   principles themselves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rationalism', 'n.', 'The doctrine or system of those who deduce their\n   religious opinions from reason or the understanding, as distinct from,\n   or opposed to, revelation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rationalism', 'n.', 'The system that makes rational power the ultimate test\n   of truth; -- opposed to sensualism, or sensationalism, and empiricism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rationalist', 'n.', 'One who accepts rationalism as a theory or system;\n   also, disparagingly, a false reasoner. See Citation under Reasonist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rationalistic', 'a.', 'Alt. of Rationalistical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rationalistical', 'a.', 'Belonging to, or in accordance with, the\n   principles of rationalism.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-ties', 'pl. ', 'of Rationality');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rationality', 'n.', 'The quality or state of being rational; agreement with\n   reason; possession of reason; due exercise of reason; reasonableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rationalization', 'n.', 'The act or process of rationalizing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rationalize', 'v. t.', 'To make rational; also, to convert to rationalism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rationalize', 'v. t.', 'To interpret in the manner of a rationalist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rationalize', 'v. t.', 'To form a rational conception of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rationalize', 'v. t.', 'To render rational; to free from radical signs or\n   quantities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rationalize', 'v. i.', 'To use, and rely on, reason in forming a theory,\n   belief, etc., especially in matters of religion: to accord with the\n   principles of rationalism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rationally', 'adv.', 'In a rational manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rationalness', 'n.', 'The quality or state of being rational; rationality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ratitae', 'n. pl.', 'An order of birds in which the wings are small,\n   rudimentary, or absent, and the breastbone is destitute of a keel. The\n   ostrich, emu, moa, and apteryx are examples.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ratitate', 'a.', 'Of or pertaining to the Ratitae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ratite', 'a.', 'Of or pertaining to the Ratitae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ratite', 'n.', 'One of the Ratitae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ratlines', 'n. pl.', 'Alt. of Ratlins');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ratlins', 'n. pl.', 'The small transverse ropes attached to the shrouds and\n   forming the steps of a rope ladder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raton', 'n.', 'A small rat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ratoon', 'n.', 'Same as Rattoon, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ratoon', 'n.', 'A rattan cane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ratoon', 'v. i.', 'Same as Rattoon, v. i.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ratsbane', 'n.', 'Rat poison; white arsenic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ratsbaned', 'a.', 'Poisoned by ratsbane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rat-tail', 'a.', 'Like a rats tail in form; as, a rat-tail file, which is\n   round, slender, and tapering. See Illust. of File.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rat-tail', 'n.', 'An excrescence growing from the pastern to the middle of\n   the shank of a horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rat-tail', 'n.', 'The California chimaera. See Chimaera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rat-tail', 'n.', 'Any fish of the genus Macrurus. See Grenadier, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rat-tailed', 'a.', 'Having a long, tapering tail like that of a rat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rattan', 'n.', 'One of the long slender flexible stems of several species\n   of palms of the genus Calamus, mostly East Indian, though some are\n   African and Australian. They are exceedingly tough, and are used for\n   walking sticks, wickerwork, chairs and seats of chairs, cords and\n   cordage, and many other purposes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ratteen', 'n.', 'A thick woolen stuff quilled or twilled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ratten', 'v. t.', 'To deprive feloniously of the tools used in ones\n   employment (as by breaking or stealing them), for the purpose of\n   annoying; as, to ratten a mechanic who works during a strike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ratter', 'n.', 'One who, or that which, rats, as one who deserts his party.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ratter', 'n.', 'Anything which catches rats; esp., a dog trained to catch\n   rats; a rat terrier. See Terrier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rattinet', 'n.', 'A woolen stuff thinner than ratteen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ratting', 'n.', 'The conduct or practices of one who rats. See Rat, v. i.,\n   1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ratting', 'v. i.', 'The low sport of setting a dog upon rats confined in a\n   pit to see how many he will kill in a given time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rattled', 'imp. & p. p.', 'of Rattle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rattling', 'p. pr. & vb. n.', 'of Rattle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rattle', 'v. i.', 'To make a quick succession of sharp, inharmonious\n   noises, as by the collision of hard and not very sonorous bodies shaken\n   together; to clatter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rattle', 'v. i.', 'To drive or ride briskly, so as to make a clattering;\n   as, we rattled along for a couple of miles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rattle', 'v. i.', 'To make a clatter with the voice; to talk rapidly and\n   idly; to clatter; -- with on or away; as, she rattled on for an hour.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rattle', 'v. t.', 'To cause to make a rattling or clattering sound; as, to\n   rattle a chain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rattle', 'v. t.', 'To assail, annoy, or stun with a rattling noise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rattle', 'v. t.', 'Hence, to disconcert; to confuse; as, to rattle ones\n   judgment; to rattle a player in a game.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rattle', 'v. t.', 'To scold; to rail at.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rattle', 'n.', 'A rapid succession of sharp, clattering sounds; as, the\n   rattle of a drum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rattle', 'n.', 'Noisy, rapid talk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rattle', 'n.', 'An instrument with which a rattling sound is made;\n   especially, a childs toy that rattles when shaken.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rattle', 'n.', 'A noisy, senseless talker; a jabberer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rattle', 'n.', 'A scolding; a sharp rebuke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rattle', 'n.', 'Any organ of an animal having a structure adapted to\n   produce a rattling sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rattle', 'n.', 'The noise in the throat produced by the air in passing\n   through mucus which the lungs are unable to expel; -- chiefly\n   observable at the approach of death, when it is called the death\n   rattle. See R/le.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rattlebox', 'n.', 'A toy that makes a rattling sound; a rattle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rattlebox', 'n.', 'An American herb (Crotalaria sagittalis), the seeds of\n   which, when ripe, rattle in the inflated pod.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rattlebox', 'n.', 'Any species of Crotalaria, a genus of yellow-flowered\n   herbs, with inflated, many-seeded pods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rattle-brained', 'a.', 'Giddy; rattle-headed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rattlehead', 'n.', 'An empty, noisy talker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rattle-headed', 'a.', 'Noisy; giddy; unsteady.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rattlemouse', 'n.', 'A bat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rattlepate', 'n.', 'A rattlehead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rattle-pated', 'a.', 'Rattle-headed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rattler', 'n.', 'One who, or that which, rattles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rattlesnake', 'n.', 'Any one of several species of venomous American snakes\n   belonging to the genera Crotalus and Caudisona, or Sistrurus. They have\n   a series of horny interlocking joints at the end of the tail which make\n   a sharp rattling sound when shaken. The common rattlesnake of the\n   Northern United States (Crotalus horridus), and the diamond rattlesnake\n   of the South (C. adamanteus), are the best known. See Illust. of Fang.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rattletrap', 'n.', 'Any machine or vehicle that does not run smoothly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rattleweed', 'n.', 'Any plant of the genus Astragalus. See Milk vetch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rattlewings', 'n.', 'The golden-eye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rattlewort', 'n.', 'Same as Rattlebox.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rattlings', 'n. pl.', 'Ratlines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rattoon', 'n.', 'One of the stems or shoots of sugar cane of the second\n   years growth from the root, or later. See Plant-cane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rattooned', 'imp. & p. p.', 'of Rattoon');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rattooning', 'p. pr. & vb. n.', 'of Rattoon');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rattoon', 'v. i.', 'To sprout or spring up from the root, as sugar cane\n   from the root of the previous years planting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raucid', 'a.', 'Hoarse; raucous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raucity', 'n.', 'Harshness of sound; rough utterance; hoarseness; as, the\n   raucity of a trumpet, or of the human voice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raucous', 'a.', 'Hoarse; harsh; rough; as, a raucous, thick tone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raught', '', 'imp. & p. p. of Reach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raught', '', 'imp. & p. p. of Reck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raunch', 'v. t.', 'See Ranch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raunsoun', 'n.', 'Ransom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ravage', 'n.', 'Desolation by violence; violent ruin or destruction;\n   devastation; havoc; waste; as, the ravage of a lion; the ravages of\n   fire or tempest; the ravages of an army, or of time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ravaged', 'imp. & p. p.', 'of Ravage');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ravaging', 'p. pr. & vb. n.', 'of Ravage');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ravage', 'n.', 'To lay waste by force; to desolate by violence; to commit\n   havoc or devastation upon; to spoil; to plunder; to consume.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ravager', 'n.', 'One who, or that which, ravages or lays waste; spoiler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rave', '', 'imp. of Rive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rave', 'n.', 'One of the upper side pieces of the frame of a wagon body or\n   a sleigh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raved', 'imp. & p. p.', 'of Rave');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raving', 'p. pr. & vb. n.', 'of Rave');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rave', 'v. i.', 'To wander in mind or intellect; to be delirious; to talk\n   or act irrationally; to be wild, furious, or raging, as a madman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rave', 'v. i.', 'To rush wildly or furiously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rave', 'v. i.', 'To talk with unreasonable enthusiasm or excessive passion\n   or excitement; -- followed by about, of, or on; as, he raved about her\n   beauty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rave', 'v. t.', 'To utter in madness or frenzy; to say wildly; as, to rave\n   nonsense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ravehook', 'n.', 'A tool, hooked at the end, for enlarging or clearing\n   seams for the reception of oakum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raveled', 'imp. & p. p.', 'of Ravel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ravelled', '', 'of Ravel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raveling', 'p. pr. & vb. n.', 'of Ravel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ravelling', '', 'of Ravel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ravel', 'v. t.', 'To separate or undo the texture of; to take apart; to\n   untwist; to unweave or unknit; -- often followed by out; as, to ravel a\n   twist; to ravel out a stocking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ravel', 'v. t.', 'To undo the intricacies of; to disentangle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ravel', 'v. t.', 'To pull apart, as the threads of a texture, and let them\n   fall into a tangled mass; hence, to entangle; to make intricate; to\n   involve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ravel', 'v. i.', 'To become untwisted or unwoven; to be disentangled; to be\n   relieved of intricacy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ravel', 'v. i.', 'To fall into perplexity and confusion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ravel', 'v. i.', 'To make investigation or search, as by picking out the\n   threads of a woven pattern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raveler', 'n.', 'One who ravels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ravelin', 'n.', 'A detached work with two embankments which make a salient\n   angle. It is raised before the curtain on the counterscarp of the\n   place. Formerly called demilune, and half-moon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raveling', 'n.', 'The act of untwisting or of disentangling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raveling', 'n.', 'That which is raveled out; esp., a thread detached from a\n   texture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raven', 'n.', 'A large black passerine bird (Corvus corax), similar to the\n   crow, but larger. It is native of the northern parts of Europe, Asia,\n   and America, and is noted for its sagacity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raven', 'a.', 'Of the color of the raven; jet black; as, raven curls; raven\n   darkness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raven', 'n.', 'Rapine; rapacity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raven', 'n.', 'Prey; plunder; food obtained by violence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ravened', 'imp. & p. p.', 'of Raven');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ravening', 'p. pr. & vb. n.', 'of Raven');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raven', 'v. t.', 'To obtain or seize by violence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raven', 'v. t.', 'To devour with great eagerness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raven', 'v. i.', 'To prey with rapacity; to be greedy; to show rapacity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ravenala', 'n.', 'A genus of plants related to the banana.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ravener', 'n.', 'One who, or that which, ravens or plunders.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ravener', 'n.', 'A bird of prey, as the owl or vulture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ravening', 'n.', 'Eagerness for plunder; rapacity; extortion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ravening', 'a.', 'Greedily devouring; rapacious; as, ravening wolves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ravenous', 'a.', 'Devouring with rapacious eagerness; furiously voracious;\n   hungry even to rage; as, a ravenous wolf or vulture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ravenous', 'a.', 'Eager for prey or gratification; as, a ravenous appetite\n   or desire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ravens-duck', 'n.', 'A fine quality of sailcloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raver', 'n.', 'One who raves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ravin', 'a.', 'Ravenous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ravin', 'n.', 'Alt. of Ravine');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ravine', 'n.', 'Food obtained by violence; plunder; prey; raven.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ravin', 'v. t. & i.', 'Alt. of Ravine');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ravine', 'v. t. & i.', 'See Raven, v. t. & i.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ravine', 'n.', 'A torrent of water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ravine', 'n.', 'A deep and narrow hollow, usually worn by a stream or\n   torrent of water; a gorge; a mountain cleft.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raving', 'a.', 'Talking irrationally and wildly; as, a raving lunatic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ravished', 'imp. & p. p.', 'of Ravish');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ravishing', 'p. pr. & vb. n.', 'of Ravish');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ravish', 'v. t.', 'To seize and carry away by violence; to snatch by force.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ravish', 'v. t.', 'To transport with joy or delight; to delight to ecstasy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ravish', 'v. t.', 'To have carnal knowledge of (a woman) by force, and\n   against her consent; to rape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ravisher', 'n.', 'One who ravishes (in any sense).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ravishing', 'a.', 'Rapturous; transporting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ravishingly', 'adv.', 'In a ravishing manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ravishment', 'n.', 'The act of carrying away by force or against consent;\n   abduction; as, the ravishment of children from their parents, of a ward\n   from his guardian, or of a wife from her husband.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ravishment', 'n.', 'The state of being ravished; rapture; transport of\n   delight; ecstasy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ravishment', 'n.', 'The act of ravishing a woman; rape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ravissant', 'a.', 'In a half-raised position, as if about to spring on\n   prey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raw', 'superl.', 'Not altered from its natural state; not prepared by the\n   action of heat; as, raw sienna; specifically, not cooked; not changed\n   by heat to a state suitable for eating; not done; as, raw meat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raw', 'superl.', 'Hence: Unprepared for use or enjoyment; immature; unripe;\n   unseasoned; inexperienced; unpracticed; untried; as, raw soldiers; a\n   raw recruit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raw', 'superl.', 'Not worked in due form; in the natural state; untouched\n   by art; unwrought.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raw', 'superl.', 'Not distilled; as, raw water');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raw', 'superl.', 'Not spun or twisted; as, raw silk or cotton');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raw', 'superl.', 'Not mixed or diluted; as, raw spirits');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raw', 'superl.', 'Not tried; not melted and strained; as, raw tallow');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raw', 'superl.', 'Not tanned; as, raw hides');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raw', 'superl.', 'Not trimmed, covered, or folded under; as, the raw edge\n   of a piece of metal or of cloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raw', 'superl.', 'Not covered; bare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raw', 'superl.', 'Bald.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raw', 'superl.', 'Deprived of skin; galled; as, a raw sore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raw', 'superl.', 'Sore, as if by being galled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raw', 'superl.', 'Disagreeably damp or cold; chilly; bleak; as, a raw wind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raw', 'n.', 'A raw, sore, or galled place; a sensitive spot; as, to touch\n   one on the raw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rawbone', 'a.', 'Rawboned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rawboned', 'a.', 'Having little flesh on the bones; gaunt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rawhead', 'n.', 'A specter mentioned to frighten children; as, rawhead and\n   bloodybones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rawhide', 'n.', 'A cowhide, or coarse riding whip, made of untanned (or\n   raw) hide twisted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rawish', 'a.', 'Somewhat raw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rawly', 'adv.', 'In a raw manner; unskillfully; without experience.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rawly', 'adv.', 'Without proper preparation or provision.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rawness', 'n.', 'The quality or state of being raw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ray', 'v. t.', 'To array.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ray', 'v. t.', 'To mark, stain, or soil; to streak; to defile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ray', 'n.', 'Array; order; arrangement; dress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ray', 'n.', 'One of a number of lines or parts diverging from a common\n   point or center, like the radii of a circle; as, a star of six rays.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ray', 'n.', 'A radiating part of a flower or plant; the marginal florets of\n   a compound flower, as an aster or a sunflower; one of the pedicels of\n   an umbel or other circular flower cluster; radius. See Radius.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ray', 'n.', 'One of the radiating spines, or cartilages, supporting the\n   fins of fishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ray', 'n.', 'One of the spheromeres of a radiate, especially one of the\n   arms of a starfish or an ophiuran.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ray', 'n.', 'A line of light or heat proceeding from a radiant or\n   reflecting point; a single element of light or heat propagated\n   continuously; as, a solar ray; a polarized ray.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ray', 'n.', 'One of the component elements of the total radiation from a\n   body; any definite or limited portion of the spectrum; as, the red ray;\n   the violet ray. See Illust. under Light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ray', 'n.', 'Sight; perception; vision; -- from an old theory of vision,\n   that sight was something which proceeded from the eye to the object\n   seen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ray', 'n.', 'One of a system of diverging lines passing through a point,\n   and regarded as extending indefinitely in both directions. See\n   Half-ray.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rayed', 'imp. & p. p.', 'of Ray');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raying', 'p. pr. & vb. n.', 'of Ray');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ray', 'n.', 'To mark with long lines; to streak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ray', 'n.', 'To send forth or shoot out; to cause to shine out; as, to ray\n   smiles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ray', 'v. i.', 'To shine, as with rays.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ray', 'n.', 'Any one of numerous elasmobranch fishes of the order Raiae,\n   including the skates, torpedoes, sawfishes, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ray', 'n.', 'In a restricted sense, any of the broad, flat, narrow-tailed\n   species, as the skates and sting rays. See Skate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rayah', 'n.', 'A person not a Mohammedan, who pays the capitation tax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ray grass', '', 'A perennial European grass (Lolium perenne); -- called\n   also rye grass, and red darnel. See Darnel, and Grass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rayless', 'a.', 'Destitute of rays; hence, dark; not illuminated; blind;\n   as, a rayless sky; rayless eyes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rayon', 'n.', 'Ray; beam.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rayonnant', 'a.', 'Darting forth rays, as the sun when it shines out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raze', 'n.', 'A Shakespearean word (used once) supposed to mean the same as\n   race, a root.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Razed', 'imp. & p. p.', 'of Raze');
  INSERT INTO words (word, word_type, description) VALUES  (    'Razing', 'p. pr. & vb. n.', 'of Raze');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raze', 'v. t.', 'To erase; to efface; to obliterate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raze', 'v. t.', 'To subvert from the foundation; to lay level with the\n   ground; to overthrow; to destroy; to demolish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Razed', 'a.', 'Slashed or striped in patterns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Razee', 'v. t.', 'An armed ship having her upper deck cut away, and thus\n   reduced to the next inferior rate, as a seventy-four cut down to a\n   frigate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Razeed', 'imp. & p. p.', 'of Razee');
  INSERT INTO words (word, word_type, description) VALUES  (    'Razeeing', 'p. pr. & vb. n.', 'of Razee');
  INSERT INTO words (word, word_type, description) VALUES  (    'Razee', 'v. t.', 'To cut down to a less number of decks, and thus to an\n   inferior rate or class, as a ship; hence, to prune or abridge by\n   cutting off or retrenching parts; as, to razee a book, or an article.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Razor', 'v. t.', 'A keen-edged knife of peculiar shape, used in shaving the\n   hair from the face or the head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Razor', 'v. t.', 'A tusk of a wild boar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Razorable', 'a.', 'Ready for the razor; fit to be shaved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Razorback', 'n.', 'The rorqual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Razor-backed', 'a.', 'Having a sharp, lean, or thin back; as, a\n   razor-backed hog, perch, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Razorbill', 'n.', 'A species of auk (Alca torda) common in the Arctic seas.\n   See Auk, and Illust. in Appendix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Razorbill', 'n.', 'See Cutwater, 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Razure', 'n.', 'The act of erasing or effacing, or the state of being\n   effaced; obliteration. See Rasure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Razure', 'n.', 'An erasure; a change made by erasing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Razzia', 'n.', 'A plundering and destructive incursion; a foray; a raid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Re-', '', 'A prefix signifying back, against, again, anew; as, recline, to\n   lean back; recall, to call back; recede; remove; reclaim, to call out\n   against; repugn, to fight against; recognition, a knowing again;\n   rejoin, to join again; reiterate; reassure. Combinations containing the\n   prefix re- are readily formed, and are for the most part of obvious\n   signification.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Re', '', 'A syllable applied in solmization to the second tone of the\n   diatonic scale of C; in the American system, to the second tone of any\n   diatonic scale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reabsorb', 'v. t.', 'To absorb again; to draw in, or imbibe, again what has\n   been effused, extravasated, or thrown off; to swallow up again; as, to\n   reabsorb chyle, lymph, etc.; -- used esp. of fluids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reabsorption', 'n.', 'The act or process of reabsorbing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reaccess', 'n.', 'A second access or approach; a return.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reaccuse', 'v. t.', 'To accuse again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reach', 'v. i.', 'To retch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reach', 'n.', 'An effort to vomit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reached', 'imp. & p. p.', 'of Reach');
  INSERT INTO words (word, word_type, description) VALUES  (    'Raught', '', 'of Reach');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reaching', 'p. pr. & vb. n.', 'of Reach');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reach', 'v. t.', 'To extend; to stretch; to thrust out; to put forth, as a\n   limb, a member, something held, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reach', 'v. t.', 'Hence, to deliver by stretching out a member, especially\n   the hand; to give with the hand; to pass to another; to hand over; as,\n   to reach one a book.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reach', 'v. t.', 'To attain or obtain by stretching forth the hand; to\n   extend some part of the body, or something held by one, so as to touch,\n   strike, grasp, or the like; as, to reach an object with the hand, or\n   with a spear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reach', 'v. t.', 'To strike, hit, or touch with a missile; as, to reach an\n   object with an arrow, a bullet, or a shell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reach', 'v. t.', 'Hence, to extend an action, effort, or influence to; to\n   penetrate to; to pierce, or cut, as far as.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reach', 'v. t.', 'To extend to; to stretch out as far as; to touch by\n   virtue of extent; as, his land reaches the river.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reach', 'v. t.', 'To arrive at; to come to; to get as far as.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reach', 'v. t.', 'To arrive at by effort of any kind; to attain to; to\n   gain; to be advanced to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reach', 'v. t.', 'To understand; to comprehend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reach', 'v. t.', 'To overreach; to deceive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reach', 'v. i.', 'To stretch out the hand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reach', 'v. i.', 'To strain after something; to make efforts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reach', 'v. i.', 'To extend in dimension, time, amount, action, influence,\n   etc., so as to touch, attain to, or be equal to, something.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reach', 'v. i.', 'To sail on the wind, as from one point of tacking to\n   another, or with the wind nearly abeam.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reach', 'n.', 'The act of stretching or extending; extension; power of\n   reaching or touching with the person, or a limb, or something held or\n   thrown; as, the fruit is beyond my reach; to be within reach of cannon\n   shot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reach', 'n.', 'The power of stretching out or extending action, influence,\n   or the like; power of attainment or management; extent of force or\n   capacity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reach', 'n.', 'Extent; stretch; expanse; hence, application; influence;\n   result; scope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reach', 'n.', 'An extended portion of land or water; a stretch; a straight\n   portion of a stream or river, as from one turn to another; a level\n   stretch, as between locks in a canal; an arm of the sea extending up\n   into the land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reach', 'n.', 'An artifice to obtain an advantage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reach', 'n.', 'The pole or rod which connects the hind axle with the\n   forward bolster of a wagon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reachable', 'a.', 'Being within reach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reacher', 'n.', 'One who reaches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reacher', 'n.', 'An exaggeration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reachless', 'a.', 'Being beyond reach; lofty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'React', 'v. t.', 'To act or perform a second time; to do over again; as, to\n   react a play; the same scenes were reacted at Rome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'React', 'v. i.', 'To return an impulse or impression; to resist the action\n   of another body by an opposite force; as, every body reacts on the body\n   that impels it from its natural state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'React', 'v. i.', 'To act upon each other; to exercise a reciprocal or a\n   reverse effect, as two or more chemical agents; to act in opposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reaction', 'n.', 'Any action in resisting other action or force; counter\n   tendency; movement in a contrary direction; reverse action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reaction', 'n.', 'The mutual or reciprocal action of chemical agents upon\n   each other, or the action upon such chemical agents of some form of\n   energy, as heat, light, or electricity, resulting in a chemical change\n   in one or more of these agents, with the production of new compounds or\n   the manifestation of distinctive characters. See Blowpipe reaction,\n   Flame reaction, under Blowpipe, and Flame.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reaction', 'n.', 'An action induced by vital resistance to some other\n   action; depression or exhaustion of vital force consequent on\n   overexertion or overstimulation; heightened activity and overaction\n   succeeding depression or shock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reaction', 'n.', 'The force which a body subjected to the action of a force\n   from another body exerts upon the latter body in the opposite\n   direction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reaction', 'n.', 'Backward tendency or movement after revolution, reform,\n   or great progress in any direction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reactionary', 'a.', 'Being, causing, or favoring reaction; as, reactionary\n   movements.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reactionaries', 'pl. ', 'of Reactionary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reactionary', 'n.', 'One who favors reaction, or seeks to undo political\n   progress or revolution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reactionist', 'n.', 'A reactionary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reactive', 'a.', 'Having power to react; tending to reaction; of the nature\n   of reaction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Read', 'n.', 'Rennet. See 3d Reed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Read', 'imp. & p. p.', 'of Read');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reading', 'p. pr. & vb. n.', 'of Read');
  INSERT INTO words (word, word_type, description) VALUES  (    'Read', 'v. t.', 'To advise; to counsel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Read', 'v. t.', 'To interpret; to explain; as, to read a riddle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Read', 'v. t.', 'To tell; to declare; to recite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Read', 'v. t.', 'To go over, as characters or words, and utter aloud, or\n   recite to ones self inaudibly; to take in the sense of, as of\n   language, by interpreting the characters with which it is expressed; to\n   peruse; as, to read a discourse; to read the letters of an alphabet; to\n   read figures; to read the notes of music, or to read music; to read a\n   book.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Read', 'v. t.', 'Hence, to know fully; to comprehend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Read', 'v. t.', 'To discover or understand by characters, marks, features,\n   etc.; to learn by observation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Read', 'v. t.', 'To make a special study of, as by perusing textbooks; as,\n   to read theology or law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Read', 'v. i.', 'To give advice or counsel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Read', 'v. i.', 'To tell; to declare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Read', 'v. i.', 'To perform the act of reading; to peruse, or to go over\n   and utter aloud, the words of a book or other like document.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Read', 'v. i.', 'To study by reading; as, he read for the bar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Read', 'v. i.', 'To learn by reading.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Read', 'v. i.', 'To appear in writing or print; to be expressed by, or\n   consist of, certain words or characters; as, the passage reads thus in\n   the early manuscripts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Read', 'v. i.', 'To produce a certain effect when read; as, that sentence\n   reads queerly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Read', 'v. t.', 'Saying; sentence; maxim; hence, word; advice; counsel. See\n   Rede.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Read', 'v.', 'Reading.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Read', '', 'imp. & p. p. of Read, v. t. & i.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Read', 'a.', 'Instructed or knowing by reading; versed in books; learned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Readability', 'n.', 'The state of being readable; readableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Readable', 'a.', 'Such as can be read; legible; fit or suitable to be read;\n   worth reading; interesting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Readdress', 'v. t.', 'To address a second time; -- often used reflexively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Readept', 'v. t.', 'To regain; to recover.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Readeption', 'n.', 'A regaining; recovery of something lost.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reader', 'n.', 'One who reads.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reader', 'n.', 'One whose distinctive office is to read prayers in a\n   church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reader', 'n.', 'One who reads lectures on scientific subjects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reader', 'n.', 'A proof reader.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reader', 'n.', 'One who reads manuscripts offered for publication and\n   advises regarding their merit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reader', 'n.', 'One who reads much; one who is studious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reader', 'n.', 'A book containing a selection of extracts for exercises in\n   reading; an elementary book for practice in a language; a reading book.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Readership', 'n.', 'The office of reader.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Readily', 'adv.', 'In a ready manner; quickly; promptly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Readily', 'adv.', 'Without delay or objection; without reluctance;\n   willingly; cheerfully.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Readiness', 'n.', 'The state or quality of being ready; preparation;\n   promptness; aptitude; willingness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reading', 'n.', 'The act of one who reads; perusal; also, printed or\n   written matter to be read.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reading', 'n.', 'Study of books; literary scholarship; as, a man of\n   extensive reading.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reading', 'n.', 'A lecture or prelection; public recital.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reading', 'n.', 'The way in which anything reads; force of a word or\n   passage presented by a documentary authority; lection; version.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reading', 'n.', 'Manner of reciting, or acting a part, on the stage; way of\n   rendering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reading', 'n.', 'An observation read from the scale of a graduated\n   instrument; as, the reading of a barometer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reading', 'a.', 'Of or pertaining to the act of reading; used in reading.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reading', 'a.', 'Addicted to reading; as, a reading community.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Readjourn', 'v. t.', 'To adjourn a second time; to adjourn again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Readjournment', 'n.', 'The act of readjourning; a second or repeated\n   adjournment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Readjust', 'v. t.', 'To adjust or settle again; to put in a different order\n   or relation; to rearrange.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Readjuster', 'n.', 'One who, or that which, readjusts; in some of the\n   States of the United States, one who advocates a refunding, and\n   sometimes a partial repudiation, of the State debt without the consent\n   of the States creditors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Readjustment', 'n.', 'A second adjustment; a new or different adjustment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Readmission', 'n.', 'The act of admitting again, or the state of being\n   readmitted; as, the readmission of fresh air into an exhausted\n   receiver; the readmission of a student into a seminary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Readmit', 'v. t.', 'To admit again; to give entrance or access to again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Readmittance', 'n.', 'Allowance to enter again; a second admission.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Readopt', 'v. t.', 'To adopt again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Readorn', 'v. t.', 'To adorn again or anew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Readvance', 'v. i.', 'To advance again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Readvertency', 'n.', 'The act of adverting to again, or of reviewing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ready', 'superl.', 'Prepared for what one is about to do or experience;\n   equipped or supplied with what is needed for some act or event;\n   prepared for immediate movement or action; as, the troops are ready to\n   march; ready for the journey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ready', 'superl.', 'Fitted or arranged for immediate use; causing no delay\n   for lack of being prepared or furnished.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ready', 'superl.', 'Prepared in mind or disposition; not reluctant;\n   willing; free; inclined; disposed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ready', 'superl.', 'Not slow or hesitating; quick in action or perception\n   of any kind; dexterous; prompt; easy; expert; as, a ready apprehension;\n   ready wit; a ready writer or workman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ready', 'superl.', 'Offering itself at once; at hand; opportune;\n   convenient; near; easy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ready', 'superl.', 'On the point; about; on the brink; near; -- with a\n   following infinitive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ready', 'superl.', 'A word of command, or a position, in the manual of\n   arms, at which the piece is cocked and held in position to execute\n   promptly the next command, which is, aim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ready', 'adv.', 'In a state of preparation for immediate action; so as to\n   need no delay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ready', 'n.', 'Ready money; cash; -- commonly with the; as, he was well\n   supplied with the ready.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ready', 'v. t.', 'To dispose in order.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ready-made', 'a.', 'Made already, or beforehand, in anticipation of need;\n   not made to order; as, ready-made clothing; ready-made jokes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ready-witted', 'a.', 'Having ready wit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reaffirm', 'v. t.', 'To affirm again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reaffirmance', 'n.', 'Alt. of Reaffirmation');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reaffirmation', 'n.', 'A second affirmation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reafforest', 'v. t.', 'To convert again into a forest, as a region of\n   country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reafforestation', 'n.', 'The act or process of converting again into a\n   forest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reagent', 'n.', 'A substance capable of producing with another a reaction,\n   especially when employed to detect the presence of other bodies; a\n   test.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reaggravation', 'n.', 'The last monitory, published after three admonitions\n   and before the last excommunication.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reagree', 'v. i.', 'To agree again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reak', 'n.', 'A rush.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reak', 'n.', 'A prank.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Real', 'n.', 'A small Spanish silver coin; also, a denomination of money of\n   account, formerly the unit of the Spanish monetary system.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Real', 'a.', 'Royal; regal; kingly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Real', 'a.', 'Actually being or existing; not fictitious or imaginary; as,\n   a description of real life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Real', 'a.', 'True; genuine; not artificial, counterfeit, or factitious;\n   often opposed to ostensible; as, the real reason; real Madeira wine;\n   real ginger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Real', 'a.', 'Relating to things, not to persons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Real', 'a.', 'Having an assignable arithmetical or numerical value or\n   meaning; not imaginary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Real', 'a.', 'Pertaining to things fixed, permanent, or immovable, as to\n   lands and tenements; as, real property, in distinction from personal or\n   movable property.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Real', 'n.', 'A realist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Realgar', 'n.', 'Arsenic sulphide, a mineral of a brilliant red color; red\n   orpiment. It is also an artificial product.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Realism', 'n.', 'As opposed to nominalism, the doctrine that genera and\n   species are real things or entities, existing independently of our\n   conceptions. According to realism the Universal exists ante rem\n   (Plato), or in re (Aristotle).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Realism', 'n.', 'As opposed to idealism, the doctrine that in sense\n   perception there is an immediate cognition of the external object, and\n   our knowledge of it is not mediate and representative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Realism', 'n.', 'Fidelity to nature or to real life; representation without\n   idealization, and making no appeal to the imagination; adherence to the\n   actual fact.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Realist', 'n.', 'One who believes in realism; esp., one who maintains that\n   generals, or the terms used to denote the genera and species of things,\n   represent real existences, and are not mere names, as maintained by the\n   nominalists.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Realist', 'n.', 'An artist or writer who aims at realism in his work. See\n   Realism, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Realistic', 'a.', 'Of or pertaining to the realists; in the manner of the\n   realists; characterized by realism rather than by imagination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Realistically', 'adv.', 'In a realistic manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Realities', 'pl. ', 'of Reality');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reality', 'n.', 'The state or quality of being real; actual being or\n   existence of anything, in distinction from mere appearance; fact.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reality', 'n.', 'That which is real; an actual existence; that which is not\n   imagination, fiction, or pretense; that which has objective existence,\n   and is not merely an idea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reality', 'n.', 'Loyalty; devotion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reality', 'n.', 'See 2d Realty, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Realizable', 'a.', 'Capable of being realized.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Realization', 'n.', 'The act of realizing, or the state of being realized.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Realized', 'imp. & p. p.', 'of Realize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Realizing', 'p. pr. & vb. n.', 'of Realize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Realize', 'v. t.', 'To make real; to convert from the imaginary or\n   fictitious into the actual; to bring into concrete existence; to\n   effectuate; to accomplish; as, to realize a scheme or project.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Realize', 'v. t.', 'To cause to seem real; to impress upon the mind as\n   actual; to feel vividly or strongly; to make ones own in apprehension\n   or experience.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Realize', 'v. t.', 'To convert into real property; to make real estate of;\n   as, to realize his fortune.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Realize', 'v. t.', 'To acquire as an actual possession; to obtain as the\n   result of plans and efforts; to gain; to get; as, to realize large\n   profits from a speculation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Realize', 'v. t.', 'To convert into actual money; as, to realize assets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Realize', 'v. i.', 'To convert any kind of property into money, especially\n   property representing investments, as shares in stock companies, bonds,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Realizer', 'n.', 'One who realizes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Realizing', 'a.', 'Serving to make real, or to impress on the mind as a\n   reality; as, a realizing view of the danger incurred.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reallege', 'v. t.', 'To allege again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Realliance', 'n.', 'A renewed alliance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Re-ally', 'v. t.', 'To bring together again; to compose or form anew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Really', 'adv.', 'Royally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Really', 'adv.', 'In a real manner; with or in reality; actually; in truth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Realm', 'n.', 'A royal jurisdiction or domain; a region which is under the\n   dominion of a king; a kingdom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Realm', 'n.', 'Hence, in general, province; region; country; domain;\n   department; division; as, the realm of fancy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Realmless', 'a.', 'Destitute of a realm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Realness', 'n.', 'The quality or condition of being real; reality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Realty', 'n.', 'Royalty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Realty', 'n.', 'Loyalty; faithfulness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Realty', 'n.', 'Reality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Realty', 'n.', 'Immobility, or the fixed, permanent nature of real\n   property; as, chattels which savor of the realty; -- so written in\n   legal language for reality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Realty', 'n.', 'Real estate; a piece of real property.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ream', 'n.', 'Cream; also, the cream or froth on ale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ream', 'v. i.', 'To cream; to mantle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ream', 'v. t.', 'To stretch out; to draw out into thongs, threads, or\n   filaments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ream', 'n.', 'A bundle, package, or quantity of paper, usually consisting\n   of twenty quires or 480 sheets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reamed', 'imp. & p. p.', 'of Ream');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reaming', 'p. pr. & vb. n.', 'of Ream');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ream', 'v. t.', 'To bevel out, as the mouth of a hole in wood or metal; in\n   modern usage, to enlarge or dress out, as a hole, with a reamer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reame', 'n.', 'Realm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reamer', 'n.', 'One who, or that which, reams; specifically, an instrument\n   with cutting or scraping edges, used, with a twisting motion, for\n   enlarging a round hole, as the bore of a cannon, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reamputation', 'n.', 'The second of two amputations performed upon the same\n   member.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reanimate', 'v. t.', 'To animate anew; to restore to animation or life; to\n   infuse new life, vigor, spirit, or courage into; to revive; to\n   reinvigorate; as, to reanimate a drowned person; to reanimate\n   disheartened troops; to reanimate languid spirits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reanimation', 'n.', 'The act or operation of reanimating, or the state of\n   being reanimated; reinvigoration; revival.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reannex', 'v. t.', 'To annex again or anew; to reunite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reannexation', 'n.', 'Act of reannexing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reanswer', 'v. t. & i.', 'To answer in return; to repay; to compensate; to\n   make amends for.');
