  INSERT INTO words (word, word_type, description) VALUES  (    'Rear', 'a.', 'Being behind, or in the hindmost part; hindmost; as, the rear\n   rank of a company.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rear', 'v. t.', 'To place in the rear; to secure the rear of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reared', 'imp. & p. p.', 'of Rear');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rearing', 'p. pr. & vb. n.', 'of Rear');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rear', 'v. t.', 'To raise; to lift up; to cause to rise, become erect,\n   etc.; to elevate; as, to rear a monolith.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rear', 'v. t.', 'To erect by building; to set up; to construct; as, to rear\n   defenses or houses; to rear one government on the ruins of another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rear', 'v. t.', 'To lift and take up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rear', 'v. t.', 'To bring up to maturity, as young; to educate; to\n   instruct; to foster; as, to rear offspring.');
