  INSERT INTO words (word, word_type, description) VALUES  (    'Rear', 'v. t.', 'To rouse; to stir up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rear', 'v. i.', 'To rise up on the hind legs, as a horse; to become erect.');
