  INSERT INTO words (word, word_type, description) VALUES  (    'Rearer', 'n.', 'One who, or that which, rears.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reargue', 'v. t.', 'To argue anew or again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reargument', 'n.', 'An arguing over again, as of a motion made in court.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rear-horse', 'n.', 'A mantis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rearly', 'adv.', 'Early.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rearmost', 'a.', 'Farthest in the rear; last.');
