  INSERT INTO words (word, word_type, description) VALUES  (    'Reassemble', 'v. t. & i.', 'To assemble again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reassert', 'v. t.', 'To assert again or anew; to maintain after an omission\n   to do so.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reassertion', 'n.', 'A second or renewed assertion of the same thing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reassessment', 'n.', 'A renewed or second assessment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reassign', 'v. t.', 'To assign back or again; to transfer back what has\n   been assigned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reassignment', 'n.', 'The act of reassigning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reassimilate', 'v. t. & i.', 'To assimilate again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reassociate', 'v. t. & i.', 'To associate again; to bring again into close\n   relations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reassume', 'v. t.', 'To assume again or anew; to resume.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reassurance', 'n.', 'Assurance or confirmation renewed or repeated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reassurance', 'n.', 'Same as Reinsurance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reassure', 'v. t.', 'To assure anew; to restore confidence to; to free from\n   fear or terror.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reassure', 'v. t.', 'To reinsure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reassurer', 'n.', 'One who reassures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reasty', 'a.', 'Rusty and rancid; -- applied to salt meat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reata', 'n.', 'A lariat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reattach', 'v. t.', 'To attach again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reattachment', 'n.', 'The act of reattaching; a second attachment.');
