  INSERT INTO words (word, word_type, description) VALUES  (    'Rebate', 'v. t.', 'To beat to obtuseness; to deprive of keenness; to blunt;\n   to turn back the point of, as a lance used for exercise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rebate', 'v. t.', 'To deduct from; to make a discount from, as interest\n   due, or customs duties.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rebate', 'v. i.', 'To abate; to withdraw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rebate', 'n.', 'Diminution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rebate', 'n.', 'Deduction; abatement; as, a rebate of interest for\n   immediate payment; a rebate of importation duties.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rebate', 'n.', 'A rectangular longitudinal recess or groove, cut in the\n   corner or edge of any body; a rabbet. See Rabbet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rebate', 'n.', 'A piece of wood hafted into a long stick, and serving to\n   beat out mortar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rebate', 'n.', 'An iron tool sharpened something like a chisel, and used\n   for dressing and polishing wood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rebate', 'n.', 'A kind of hard freestone used in making pavements.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rebate', 'v. t.', 'To cut a rebate in. See Rabbet, v.');
