  INSERT INTO words (word, word_type, description) VALUES  (    'Rebec', 'n.', 'A contemptuous term applied to an old woman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rebel', 'v. i.', 'Pertaining to rebels or rebellion; acting in revolt;\n   rebellious; as, rebel troops.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rebel', 'n.', 'One who rebels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rebelled', 'imp. & p. p.', 'of Rebel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rebelling', 'p. pr. & vb. n.', 'of Rebel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rebel', 'v. i.', 'To renounce, and resist by force, the authority of the\n   ruler or government to which one owes obedience. See Rebellion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rebel', 'v. i.', 'To be disobedient to authority; to assume a hostile or\n   insubordinate attitude; to revolt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rebeldom', 'n.', 'A region infested by rebels; rebels, considered\n   collectively; also, conduct or quality characteristic of rebels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rebeller', 'n.', 'One who rebels; a rebel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rebellion', 'v. i.', 'The act of rebelling; open and avowed renunciation of\n   the authority of the government to which one owes obedience, and\n   resistance to its officers and laws, either by levying war, or by\n   aiding others to do so; an organized uprising of subjects for the\n   purpose of coercing or overthrowing their lawful ruler or government by\n   force; revolt; insurrection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rebellion', 'v. i.', 'Open resistance to, or defiance of, lawful authority.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rebellious', 'a.', 'Engaged in rebellion; disposed to rebel; of the nature\n   of rebels or of rebellion; resisting government or lawful authority by\n   force.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rebellow', 'v. i.', 'To bellow again; to repeat or echo a bellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rebiting', 'n.', 'The act or process of deepening worn lines in an etched\n   plate by submitting it again to the action of acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rebloom', 'v. i.', 'To bloom again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reblossom', 'v. i.', 'To blossom again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reboant', 'a.', 'Rebellowing; resounding loudly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reboation', 'n.', 'Repetition of a bellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reboil', 'v. t. & i.', 'To boil, or to cause to boil, again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reboil', 'v. t. & i.', 'Fig.: To make or to become hot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reborn', 'p. p.', 'Born again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rebound', 'v. i.', 'To spring back; to start back; to be sent back or\n   reverberated by elastic force on collision with another body; as, a\n   rebounding echo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rebound', 'v. i.', 'To give back an echo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rebound', 'v. i.', 'To bound again or repeatedly, as a horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rebound', 'v. t.', 'To send back; to reverberate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rebound', 'n.', 'The act of rebounding; resilience.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rebrace', 'v. t.', 'To brace again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rebreathe', 'v. t.', 'To breathe again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rebucous', 'a.', 'Rebuking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rebuff', 'n.', 'Repercussion, or beating back; a quick and sudden\n   resistance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rebuff', 'n.', 'Sudden check; unexpected repulse; defeat; refusal;\n   repellence; rejection of solicitation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rebuffed', 'imp. & p. p.', 'of Rebuff');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rebuffing', 'p. pr. & vb. n.', 'of Rebuff');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rebuff', 'v. t.', 'To beat back; to offer sudden resistance to; to check;\n   to repel or repulse violently, harshly, or uncourteously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rebuild', 'v. t.', 'To build again, as something which has been demolished;\n   to construct anew; as, to rebuild a house, a wall, a wharf, or a city.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rebuilder', 'n.', 'One who rebuilds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rebukable', 'a.', 'Worthy of rebuke or reprehension; reprehensible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rebuked', 'imp. & p. p.', 'of Rebuke');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rebuking', 'p. pr. & vb. n.', 'of Rebuke');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rebuke', 'v. t.', 'To check, silence, or put down, with reproof; to\n   restrain by expression of disapprobation; to reprehend sharply and\n   summarily; to chide; to reprove; to admonish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rebuke', 'n.', 'A direct and pointed reproof; a reprimand; also,\n   chastisement; punishment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rebuke', 'n.', 'Check; rebuff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rebukeful', 'a.', 'Containing rebuke; of the nature of rebuke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rebuker', 'n.', 'One who rebukes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rebukingly', 'adv.', 'By way of rebuke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rebullition', 'n.', 'The act of boiling up or effervescing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rebury', 'v. t.', 'To bury again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rebuses', 'pl. ', 'of Rebus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rebus', 'n.', 'A mode of expressing words and phrases by pictures of\n   objects whose names resemble those words, or the syllables of which\n   they are composed; enigmatical representation of words by figures;\n   hence, a peculiar form of riddle made up of such representations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rebus', 'n.', 'A pictorial suggestion on a coat of arms of the name of the\n   person to whom it belongs. See Canting arms, under Canting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rebus', 'v. t.', 'To mark or indicate by a rebus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rebutted', 'imp. & p. p.', 'of Rebut');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rebutting', 'p. pr. & vb. n.', 'of Rebut');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rebut', 'v. t.', 'To drive or beat back; to repulse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rebut', 'v. t.', 'To contradict, meet, or oppose by argument, plea, or\n   countervailing proof.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rebut', 'v. i.', 'To retire; to recoil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rebut', 'v. i.', 'To make, or put in, an answer, as to a plaintiffs\n   surrejoinder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rebuttable', 'a.', 'Capable of being rebutted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rebuttal', 'n.', 'The giving of evidence on the part of a plaintiff to\n   destroy the effect of evidence introduced by the defendant in the same\n   suit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rebutter', 'n.', 'The answer of a defendant in matter of fact to a\n   plaintiffs surrejoinder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recadency', 'n.', 'A falling back or descending a second time; a relapse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recalcitrant', 'a.', 'Kicking back; recalcitrating; hence, showing\n   repugnance or opposition; refractory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recalcitrate', 'v. t.', 'To kick against; to show repugnance to; to rebuff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recalcitrate', 'v. i.', 'To kick back; to kick against anything; hence, to\n   express repugnance or opposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recalcitration', 'n.', 'A kicking back again; opposition; repugnance;\n   refractoriness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recall', 'v. t.', 'To call back; to summon to return; as, to recall troops;\n   to recall an ambassador.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recall', 'v. t.', 'To revoke; to annul by a subsequent act; to take back;\n   to withdraw; as, to recall words, or a decree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recall', 'v. t.', 'To call back to mind; to revive in memory; to recollect;\n   to remember; as, to recall bygone days.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recall', 'n.', 'A calling back; a revocation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recall', 'n.', 'A call on the trumpet, bugle, or drum, by which soldiers\n   are recalled from duty, labor, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recallable', 'a.', 'Capable of being recalled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recallment', 'n.', 'Recall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recanted', 'imp. & p. p.', 'of Recant');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recanting', 'p. pr. & vb. n.', 'of Recant');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recant', 'v. t.', 'To withdraw or repudiate formally and publicly (opinions\n   formerly expressed); to contradict, as a former declaration; to take\n   back openly; to retract; to recall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recant', 'v. i.', 'To revoke a declaration or proposition; to unsay what\n   has been said; to retract; as, convince me that I am wrong, and I will\n   recant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recantation', 'n.', 'The act of recanting; a declaration that contradicts a\n   former one; that which is thus asserted in contradiction; retraction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recanter', 'n.', 'One who recants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recapacitate', 'v. t.', 'To qualify again; to confer capacity on again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recapitulate', 'v. t.', 'To repeat, as the principal points in a discourse,\n   argument, or essay; to give a summary of the principal facts, points,\n   or arguments of; to relate in brief; to summarize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recapitulate', 'v. i.', 'To sum up, or enumerate by heads or topics, what\n   has been previously said; to repeat briefly the substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recapitulation', 'n.', 'The act of recapitulating; a summary, or concise\n   statement or enumeration, of the principal points, facts, or\n   statements, in a preceding discourse, argument, or essay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recapitulator', 'n.', 'One who recapitulates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recapitulatory', 'a.', 'Of the nature of a recapitulation; containing\n   recapitulation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recapper', 'n.', 'A tool used for applying a fresh percussion cap or primer\n   to a cartridge shell in reloading it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recaption', 'n.', 'The act of retaking, as of one who has escaped after\n   arrest; reprisal; the retaking of ones own goods, chattels, wife, or\n   children, without force or violence, from one who has taken them and\n   who wrongfully detains them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recaptor', 'n.', 'One who recaptures; one who takes a prize which had been\n   previously taken.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recapture', 'n.', 'The act of retaking or recovering by capture;\n   especially, the retaking of a prize or goods from a captor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recapture', 'n.', 'That which is captured back; a prize retaken.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recapture', 'v. t.', 'To capture again; to retake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recarbonize', 'v. t.', 'To restore carbon to; as, to recarbonize iron in\n   converting it into steel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recarnify', 'v. t.', 'To convert again into flesh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recarriage', 'n.', 'Act of carrying back.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recarry', 'v. t.', 'To carry back.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recast', 'v. t.', 'To throw again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recast', 'v. t.', 'To mold anew; to cast anew; to throw into a new form or\n   shape; to reconstruct; as, to recast cannon; to recast an argument or a\n   play.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recast', 'v. t.', 'To compute, or cast up, a second time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recche', 'v. i.', 'To reck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reccheles', 'a.', 'Reckless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Receded', 'imp. & p. p.', 'of Recede');
  INSERT INTO words (word, word_type, description) VALUES  (    'Receding', 'p. pr. & vb. n.', 'of Recede');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recede', 'v. i.', 'To move back; to retreat; to withdraw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recede', 'v. i.', 'To withdraw a claim or pretension; to desist; to\n   relinquish what had been proposed or asserted; as, to recede from a\n   demand or proposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recede', 'v. i.', 'To cede back; to grant or yield again to a former\n   possessor; as, to recede conquered territory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Receipt', 'n.', 'The act of receiving; reception.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Receipt', 'n.', 'Reception, as an act of hospitality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Receipt', 'n.', 'Capability of receiving; capacity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Receipt', 'n.', 'Place of receiving.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Receipt', 'n.', 'Hence, a recess; a retired place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Receipt', 'n.', 'A formulary according to the directions of which things\n   are to be taken or combined; a recipe; as, a receipt for making sponge\n   cake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Receipt', 'n.', 'A writing acknowledging the taking or receiving of goods\n   delivered; an acknowledgment of money paid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Receipt', 'n.', 'That which is received; that which comes in, in\n   distinction from what is expended, paid out, sent away, and the like;\n   -- usually in the plural; as, the receipts amounted to a thousand\n   dollars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Receipted', 'imp. & p. p.', 'of Receipt');
  INSERT INTO words (word, word_type, description) VALUES  (    'Receipting', 'p. pr. & vb. n.', 'of Receipt');
  INSERT INTO words (word, word_type, description) VALUES  (    'Receipt', 'v. t.', 'To give a receipt for; as, to receipt goods delivered\n   by a sheriff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Receipt', 'v. t.', 'To put a receipt on, as by writing or stamping; as, to\n   receipt a bill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Receipt', 'v. i.', 'To give a receipt, as for money paid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Receiptment', 'n.', 'The receiving or harboring a felon knowingly, after\n   the commission of a felony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Receiptor', 'n.', 'One who receipts; specifically (Law), one who receipts\n   for property which has been taken by the sheriff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Receit', 'n.', 'Receipt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Receivability', 'n.', 'The quality of being receivable; receivableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Receivable', 'a.', 'Capable of being received.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Received', 'imp. & p. p.', 'of Receive');
  INSERT INTO words (word, word_type, description) VALUES  (    'Receiving', 'p. pr. & vb. n.', 'of Receive');
  INSERT INTO words (word, word_type, description) VALUES  (    'Receive', 'v. t.', 'To take, as something that is offered, given,\n   committed, sent, paid, or the like; to accept; as, to receive money\n   offered in payment of a debt; to receive a gift, a message, or a\n   letter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Receive', 'v. t.', 'Hence: To gain the knowledge of; to take into the mind\n   by assent to; to give admission to; to accept, as an opinion, notion,\n   etc.; to embrace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Receive', 'v. t.', 'To allow, as a custom, tradition, or the like; to give\n   credence or acceptance to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Receive', 'v. t.', 'To give admittance to; to permit to enter, as into\n   ones house, presence, company, and the like; as, to receive a lodger,\n   visitor, ambassador, messenger, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Receive', 'v. t.', 'To admit; to take in; to hold; to contain; to have\n   capacity for; to be able to take in.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Receive', 'v. t.', 'To be affected by something; to suffer; to be subjected\n   to; as, to receive pleasure or pain; to receive a wound or a blow; to\n   receive damage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Receive', 'v. t.', 'To take from a thief, as goods known to be stolen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Receive', 'v. t.', 'To bat back (the ball) when served.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Receive', 'v. i.', 'To receive visitors; to be at home to receive calls;\n   as, she receives on Tuesdays.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Receive', 'v. i.', 'To return, or bat back, the ball when served; as, it is\n   your turn to receive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Receivedness', 'n.', 'The state or quality of being received, accepted, or\n   current; as, the receivedness of an opinion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Receiver', 'n.', 'One who takes or receives in any manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Receiver', 'n.', 'A person appointed, ordinarily by a court, to receive,\n   and hold in trust, money or other property which is the subject of\n   litigation, pending the suit; a person appointed to take charge of the\n   estate and effects of a corporation, and to do other acts necessary to\n   winding up its affairs, in certain cases.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Receiver', 'n.', 'One who takes or buys stolen goods from a thief, knowing\n   them to be stolen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Receiver', 'n.', 'A vessel connected with an alembic, a retort, or the\n   like, for receiving and condensing the product of distillation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Receiver', 'n.', 'A vessel for receiving and containing gases.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Receiver', 'n.', 'The glass vessel in which the vacuum is produced, and the\n   objects of experiment are put, in experiments with an air pump. Cf.\n   Bell jar, and see Illust. of Air pump.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Receiver', 'n.', 'A vessel for receiving the exhaust steam from the\n   high-pressure cylinder before it enters the low-pressure cylinder, in a\n   compound engine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Receiver', 'n.', 'A capacious vessel for receiving steam from a distant\n   boiler, and supplying it dry to an engine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Receiver', 'n.', 'That portion of a telephonic apparatus, or similar\n   system, at which the message is received and made audible; -- opposed\n   to transmitter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Receivership', 'n.', 'The state or office of a receiver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recelebrate', 'v. t.', 'To celebrate again, or anew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recency', 'n.', 'The state or quality of being recent; newness; new state;\n   late origin; lateness in time; freshness; as, the recency of a\n   transaction, of a wound, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recense', 'v. t.', 'To review; to revise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recension', 'n.', 'The act of reviewing or revising; review; examination;\n   enumeration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recension', 'n.', 'Specifically, the review of a text (as of an ancient\n   author) by an editor; critical revisal and establishment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recension', 'n.', 'The result of such a work; a text established by\n   critical revision; an edited version.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recensionist', 'n.', 'One who makes recensions; specifically, a critical\n   editor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recent', 'a.', 'Of late origin, existence, or occurrence; lately come; not\n   of remote date, antiquated style, or the like; not already known,\n   familiar, worn out, trite, etc.; fresh; novel; new; modern; as, recent\n   news.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recent', 'a.', 'Of or pertaining to the present or existing epoch; as,\n   recent shells.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recenter', 'v. t.', 'To center again; to restore to the center.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recently', 'adv.', 'Newly; lately; freshly; not long since; as, advices\n   recently received.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recentness', 'n.', 'Quality or state of being recent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Receptacle', 'n.', 'That which serves, or is used, for receiving and\n   containing something, as a basket, a vase, a bag, a reservoir; a\n   repository.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Receptacle', 'n.', 'The apex of the flower stalk, from which the organs of\n   the flower grow, or into which they are inserted. See Illust. of\n   Flower, and Ovary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Receptacle', 'n.', 'The dilated apex of a pedicel which serves as a common\n   support to a head of flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Receptacle', 'n.', 'An intercellular cavity containing oil or resin or\n   other matters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Receptacle', 'n.', 'A special branch which bears the fructification in many\n   cryptogamous plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Receptacular', 'a.', 'Pertaining to the receptacle, or growing on it; as,\n   the receptacular chaff or scales in the sunflower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Receptacula', 'pl. ', 'of Receptaculum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Receptaculum', 'n.', 'A receptacle; as, the receptaculum of the chyle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Receptary', 'a.', 'Generally or popularly admitted or received.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Receptary', 'n.', 'That which is received.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Receptibility', 'n.', 'The quality or state of being receptible;\n   receivableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Receptibility', 'n.', 'A receptible thing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Receptible', 'a.', 'Such as may be received; receivable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reception', 'n.', 'The act of receiving; receipt; admission; as, the\n   reception of food into the stomach; the reception of a letter; the\n   reception of sensation or ideas; reception of evidence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reception', 'n.', 'The state of being received.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reception', 'n.', 'The act or manner of receiving, esp. of receiving\n   visitors; entertainment; hence, an occasion or ceremony of receiving\n   guests; as, a hearty reception; an elaborate reception.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reception', 'n.', 'Acceptance, as of an opinion or doctrine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reception', 'n.', 'A retaking; a recovery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Receptive', 'a.', 'Having the quality of receiving; able or inclined to\n   take in, absorb, hold, or contain; receiving or containing; as, a\n   receptive mind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Receptiveness', 'n.', 'The quality of being receptive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Receptivity', 'n.', 'The state or quality of being receptive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Receptivity', 'n.', 'The power or capacity of receiving impressions, as\n   those of the external senses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Receptory', 'n.', 'Receptacle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recess', 'n.', 'A withdrawing or retiring; a moving back; retreat; as, the\n   recess of the tides.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recess', 'n.', 'The state of being withdrawn; seclusion; privacy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recess', 'n.', 'Remission or suspension of business or procedure;\n   intermission, as of a legislative body, court, or school.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recess', 'n.', 'Part of a room formed by the receding of the wall, as an\n   alcove, niche, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recess', 'n.', 'A place of retirement, retreat, secrecy, or seclusion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recess', 'n.', 'Secret or abstruse part; as, the difficulties and recesses\n   of science.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recess', 'n.', 'A sinus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recessed', 'imp. & p. p.', 'of Recess');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recessing', 'p. pr. & vb. n.', 'of Recess');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recess', 'v. t.', 'To make a recess in; as, to recess a wall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recess', 'n.', 'A decree of the imperial diet of the old German empire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recessed', 'a.', 'Having a recess or recesses; as, a recessed arch or wall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recessed', 'a.', 'Withdrawn; secluded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recession', 'n.', 'The act of receding or withdrawing, as from a place, a\n   claim, or a demand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recession', 'n.', 'The act of ceding back; restoration; repeated cession;\n   as, the recession of conquered territory to its former sovereign.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recessional', 'a.', 'Of or pertaining to recession or withdrawal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recessive', 'a.', 'Going back; receding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rechabite', 'n.', 'One of the descendants of Jonadab, the son of Rechab,\n   all of whom by his injunction abstained from the use of intoxicating\n   drinks and even from planting the vine. Jer. xxxv. 2-19. Also, in\n   modern times, a member of a certain society of abstainers from\n   alcoholic liquors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rechange', 'v. t. & i.', 'To change again, or change back.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recharge', 'v. t. & i.', 'To charge or accuse in return.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recharge', 'v. t. & i.', 'To attack again; to attack anew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recharter', 'n.', 'A second charter; a renewal of a charter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recharter', 'v. t.', 'To charter again or anew; to grant a second or\n   another charter to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rechase', 'v. t.', 'To chase again; to chase or drive back.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recheat', 'n.', 'A strain given on the horn to call back the hounds when\n   they have lost track of the game.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recheat', 'v. i.', 'To blow the recheat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recherche', 'a.', 'Sought out with care; choice. Hence: of rare quality,\n   elegance, or attractiveness; peculiar and refined in kind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rechless', 'a.', 'Reckless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rechoose', 'v. t.', 'To choose again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recidivate', 'v. i.', 'To backslide; to fall again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recidivation', 'n.', 'A falling back; a backsliding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recidivous', 'a.', 'Tending or liable to backslide or relapse to a former\n   condition or habit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recipes', 'pl. ', 'of Recipe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recipe', 'n.', 'A formulary or prescription for making some combination,\n   mixture, or preparation of materials; a receipt; especially, a\n   prescription for medicine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recipiangle', 'n.', 'An instrument with two arms that are pivoted together\n   at one end, and a graduated arc, -- used by military engineers for\n   measuring and laying off angles of fortifications.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recipience', 'n.', 'Alt. of Recipiency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recipiency', 'n.', 'The quality or state of being recipient; a receiving;\n   reception; receptiveness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recipient', 'n.', 'A receiver; the person or thing that receives; one to\n   whom, or that to which, anything is given or communicated;\n   specifically, the receiver of a still.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recipient', 'a.', 'Receiving; receptive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reciprocal', 'a.', 'Recurring in vicissitude; alternate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reciprocal', 'a.', 'Done by each to the other; interchanging or\n   interchanged; given and received; due from each to each; mutual; as,\n   reciprocal love; reciprocal duties.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reciprocal', 'a.', 'Mutually interchangeable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reciprocal', 'a.', 'Reflexive; -- applied to pronouns and verbs, but\n   sometimes limited to such pronouns as express mutual action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reciprocal', 'a.', 'Used to denote different kinds of mutual relation;\n   often with reference to the substitution of reciprocals for given\n   quantities. See the Phrases below.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reciprocal', 'n.', 'That which is reciprocal to another thing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reciprocal', 'n.', 'The quotient arising from dividing unity by any\n   quantity; thus, / is the reciprocal of 4; 1/(a +b) is the reciprocal of\n   a + b. The reciprocal of a fraction is the fraction inverted, or the\n   denominator divided by the numerator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reciprocality', 'n.', 'The quality or condition of being reciprocal;\n   reciprocalness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reciprocally', 'adv.', 'In a reciprocal manner; so that each affects the\n   other, and is equally affected by it; interchangeably; mutually.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reciprocally', 'adv.', 'In the manner of reciprocals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reciprocalness', 'n.', 'The quality or condition of being reciprocal;\n   mutual return; alternateness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reciprocated', 'imp. & p. p.', 'of Reciprocate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reciprocating', 'p. pr. & vb. n.', 'of Reciprocate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reciprocate', 'v. i.', 'To move forward and backward alternately; to recur\n   in vicissitude; to act interchangeably; to alternate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reciprocate', 'v. t.', 'To give and return mutually; to make return for; to\n   give in return; to interchange; to alternate; as, to reciprocate\n   favors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reciprocation', 'n.', 'The act of reciprocating; interchange of acts; a\n   mutual giving and returning; as, the reciprocation of kindnesses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reciprocation', 'n.', 'Alternate recurrence or action; as, the\n   reciprocation of the sea in the flow and ebb of tides.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reciprocity', 'n.', 'Mutual action and reaction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reciprocity', 'n.', 'Reciprocal advantages, obligations, or rights;\n   reciprocation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reciprocornous', 'a.', 'Having horns turning backward and then forward,\n   like those of a ram.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reciprocous', 'a.', 'Reciprocal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reciprok', 'a.', 'Reciprocal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reciproque', 'a. & n.', 'Reciprocal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recision', 'n.', 'The act of cutting off.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recital', 'n.', 'The act of reciting; the repetition of the words of\n   another, or of a document; rehearsal; as, the recital of testimony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recital', 'n.', 'A telling in detail and due order of the particulars of\n   anything, as of a law, an adventure, or a series of events; narration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recital', 'n.', 'That which is recited; a story; a narration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recital', 'n.', 'A vocal or instrumental performance by one person; --\n   distinguished from concert; as, a song recital; an organ, piano, or\n   violin recital.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recital', 'n.', 'The formal statement, or setting forth, of some matter of\n   fact in any deed or writing in order to explain the reasons on which\n   the transaction is founded; the statement of matter in pleading\n   introductory to some positive allegation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recitation', 'n.', 'The act of reciting; rehearsal; repetition of words or\n   sentences.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recitation', 'n.', 'The delivery before an audience of something committed\n   to memory, especially as an elocutionary exhibition; also, that which\n   is so delivered.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recitation', 'n.', 'The rehearsal of a lesson by pupils before their\n   instructor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recitative', 'n.', 'A species of musical recitation in which the words are\n   delivered in a manner resembling that of ordinary declamation; also, a\n   piece of music intended for such recitation; -- opposed to melisma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recitative', 'a.', 'Of or pertaining to recitation; intended for musical\n   recitation or declamation; in the style or manner of recitative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recitativo', 'n.', 'Recitative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recited', 'imp. & p. p.', 'of Recite');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reciting', 'p. pr. & vb. n.', 'of Recite');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recite', 'v. t.', 'To repeat, as something already prepared, written down,\n   committed to memory, or the like; to deliver from a written or printed\n   document, or from recollection; to rehearse; as, to recite the words of\n   an author, or of a deed or covenant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recite', 'v. t.', 'To tell over; to go over in particulars; to relate; to\n   narrate; as, to recite past events; to recite the particulars of a\n   voyage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recite', 'v. t.', 'To rehearse, as a lesson to an instructor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recite', 'v. t.', 'To state in or as a recital. See Recital, 5.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recite', 'v. i.', 'To repeat, pronounce, or rehearse, as before an\n   audience, something prepared or committed to memory; to rehearse a\n   lesson learned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recite', 'n.', 'A recital.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reciter', 'n.', 'One who recites; also, a book of extracts for recitation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recked', 'imp. & p. p.', 'of Reck');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roughte', '', 'of Reck');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recking', 'p. pr. & vb. n.', 'of Reck');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reck', 'v. t.', 'To make account of; to care for; to heed; to regard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reck', 'v. t.', 'To concern; -- used impersonally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reck', 'v. i.', 'To make account; to take heed; to care; to mind; -- often\n   followed by of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reckless', 'a.', 'Inattentive to duty; careless; neglectful; indifferent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reckless', 'a.', 'Rashly negligent; utterly careless or heedless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reckling', 'a.', 'Needing care; weak; feeble; as, a reckling child.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reckling', 'n.', 'A weak child or animal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reckoned',    'imp. & p. p.',    'of Reckon');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reckoning',    'p. pr. & vb. n.',    'of Reckon');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reckon',    'v. t.',    'To count; to enumerate; to number; also, to compute; to\n   calculate.');
