INSERT INTO words (word, word_type, description)VALUES  (    'Reckon',    'v. t.',    'To count as in a number, rank, or series; to estimate by\n   rank or quality; to place by estimation; to account; to esteem; to\n   repute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reckon', 'v. t.', 'To charge, attribute, or adjudge to one, as having a\n   certain quality or value.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reckon', 'v. t.', 'To conclude, as by an enumeration and balancing of\n   chances; hence, to think; to suppose; -- followed by an objective\n   clause; as, I reckon he wont try that again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reckon', 'v. i.', 'To make an enumeration or computation; to engage in\n   numbering or computing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reckon', 'v. i.', 'To come to an accounting; to make up accounts; to\n   settle; to examine and strike the balance of debt and credit; to adjust\n   relations of desert or penalty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reckoner', 'n.', 'One who reckons or computes; also, a book of\n   calculations, tables, etc., to assist in reckoning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reckoning', 'n.', 'The act of one who reckons, counts, or computes; the\n   result of reckoning or counting; calculation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reckoning', 'n.', 'An account of time');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reckoning', 'n.', 'Adjustment of claims and accounts; settlement of\n   obligations, liabilities, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reckoning', 'n.', 'The charge or account made by a host at an inn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reckoning', 'n.', 'Esteem; account; estimation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reckoning', 'n.', 'The calculation of a ships position, either from\n   astronomical observations, or from the record of the courses steered\n   and distances sailed as shown by compass and log, -- in the latter case\n   called dead reckoning (see under Dead); -- also used for dead reckoning\n   in contradistinction to observation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reckoning', 'n.', 'The position of a ship as determined by calculation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reclaim', 'v. t.', 'To claim back; to demand the return of as a right; to\n   attempt to recover possession of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reclaimed', 'imp. & p. p.', 'of Reclaim');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reclaiming', 'p. pr. & vb. n.', 'of Reclaim');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reclaim', 'v. t.', 'To call back, as a hawk to the wrist in falconry, by a\n   certain customary call.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reclaim', 'v. t.', 'To call back from flight or disorderly action; to call\n   to, for the purpose of subduing or quieting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reclaim', 'v. t.', 'To reduce from a wild to a tamed state; to bring under\n   discipline; -- said especially of birds trained for the chase, but also\n   of other animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reclaim', 'v. t.', 'Hence: To reduce to a desired state by discipline,\n   labor, cultivation, or the like; to rescue from being wild, desert,\n   waste, submerged, or the like; as, to reclaim wild land, overflowed\n   land, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reclaim', 'v. t.', 'To call back to rectitude from moral wandering or\n   transgression; to draw back to correct deportment or course of life; to\n   reform.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reclaim', 'v. t.', 'To correct; to reform; -- said of things.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reclaim', 'v. t.', 'To exclaim against; to gainsay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reclaim', 'v. i.', 'To cry out in opposition or contradiction; to exclaim\n   against anything; to contradict; to take exceptions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reclaim', 'v. i.', 'To bring anyone back from evil courses; to reform.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reclaim', 'v. i.', 'To draw back; to give way.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reclaim', 'n.', 'The act of reclaiming, or the state of being reclaimed;\n   reclamation; recovery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reclaimable', 'a.', 'That may be reclaimed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reclaimant', 'n.', 'One who reclaims; one who cries out against or\n   contradicts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reclaimer', 'n.', 'One who reclaims.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reclaimless', 'a.', 'That can not be reclaimed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reclamation', 'n.', 'The act or process of reclaiming.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reclamation', 'n.', 'Representation made in opposition; remonstrance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reclasp', 'v. i.', 'To clasp or unite again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reclinant', 'a.', 'Bending or leaning backward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reclinate', 'a.', 'Reclined, as a leaf; bent downward, so that the point,\n   as of a stem or leaf, is lower than the base.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reclination', 'n.', 'The act of leaning or reclining, or the state of being\n   reclined.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reclination', 'n.', 'The angle which the plane of the dial makes with a\n   vertical plane which it intersects in a horizontal line.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reclination', 'n.', 'The act or process of removing a cataract, by applying\n   the needle to its anterior surface, and depressing it into the vitreous\n   humor in such a way that the front surface of the cataract becomes the\n   upper one and its back surface the lower one.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reclined', 'imp. & p. p.', 'of Recline');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reclining', 'p. pr. & vb. n.', 'of Recline');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recline', 'v. t.', 'To cause or permit to lean, incline, rest, etc.; to\n   place in a recumbent position; as, to recline the head on the hand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recline', 'v. i.', 'To lean or incline; as, to recline against a wall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recline', 'v. i.', 'To assume, or to be in, a recumbent position; as, to\n   recline on a couch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recline', 'v. t.', 'Having a reclining posture; leaning; reclining.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reclined', 'a.', 'Falling or turned downward; reclinate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recliner', 'n.', 'One who, or that which, reclines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reclining', 'a.', 'Bending or curving gradually back from the\n   perpendicular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reclining', 'a.', 'Recumbent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reclose', 'v. t.', 'To close again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reclothe', 'v. t.', 'To clothe again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reclude', 'v. t.', 'To open; to unclose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recluse', 'a.', 'Shut up; sequestered; retired from the world or from\n   public notice; solitary; living apart; as, a recluse monk or hermit; a\n   recluse life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recluse', 'a.', 'A person who lives in seclusion from intercourse with the\n   world, as a hermit or monk; specifically, one of a class of secluded\n   devotees who live in single cells, usually attached to monasteries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recluse', 'a.', 'The place where a recluse dwells.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recluse', 'v. t.', 'To shut up; to seclude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reclusely', 'adv.', 'In a recluse or solitary manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recluseness', 'n.', 'Quality or state of being recluse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reclusion', 'n.', 'A state of retirement from the world; seclusion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reclusive', 'a.', 'Affording retirement from society.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reclusory', 'n.', 'The habitation of a recluse; a hermitage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recoct', 'v. t.', 'To boil or cook again; hence, to make over; to vamp up;\n   to reconstruct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recoction', 'n.', 'A second coction or preparation; a vamping up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recognition', 'n.', 'The act of recognizing, or the state of being\n   recognized; acknowledgment; formal avowal; knowledge confessed or\n   avowed; notice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recognitor', 'n.', 'One of a jury impaneled on an assize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recognitory', 'a.', 'Pertaining to, or connected with, recognition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recognizability', 'n.', 'The quality or condition of being recognizable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recognizable', 'a.', 'Capable of being recognized.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recognizance', 'n.', 'An obligation of record entered into before some\n   court of record or magistrate duly authorized, with condition to do\n   some particular act, as to appear at the same or some other court, to\n   keep the peace, or pay a debt. A recognizance differs from a bond,\n   being witnessed by the record only, and not by the partys seal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recognizance', 'n.', 'The verdict of a jury impaneled upon assize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recognizance', 'n.', 'A token; a symbol; a pledge; a badge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recognizance', 'n.', 'Acknowledgment of a person or thing; avowal;\n   profession; recognition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recognization', 'n.', 'Recognition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recognized', 'imp. & p. p.', 'of Recognize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recognizing', 'p. pr. & vb. n.', 'of Recognize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recognize', 'v. t.', 'To know again; to perceive the identity of, with a\n   person or thing previously known; to recover or recall knowledge of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recognize', 'v. t.', 'To avow knowledge of; to allow that one knows; to\n   consent to admit, hold, or the like; to admit with a formal\n   acknowledgment; as, to recognize an obligation; to recognize a consul.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recognize', 'v. t.', 'To acknowledge acquaintance with, as by salutation,\n   bowing, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recognize', 'v. t.', 'To show appreciation of; as, to recognize services by\n   a testimonial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recognize', 'v. t.', 'To review; to reexamine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recognize', 'v. t.', 'To reconnoiter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recognize', 'v. i.', 'To enter an obligation of record before a proper\n   tribunal; as, A B recognized in the sum of twenty dollars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recognizee', 'n.', 'The person in whose favor a recognizance is made.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recognizer', 'n.', 'One who recognizes; a recognizor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recognizor', 'n.', 'One who enters into a recognizance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recognosce', 'v. t.', 'To recognize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recoiled', 'imp. & p. p.', 'of Recoil');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recoiling', 'p. pr. & vb. n.', 'of Recoil');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recoil', 'v. i.', 'To start, roll, bound, spring, or fall back; to take a\n   reverse motion; to be driven or forced backward; to return.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recoil', 'v. i.', 'To draw back, as from anything repugnant, distressing,\n   alarming, or the like; to shrink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recoil', 'v. i.', 'To turn or go back; to withdraw ones self; to retire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recoil', 'v. t.', 'To draw or go back.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recoil', 'n.', 'A starting or falling back; a rebound; a shrinking; as, the\n   recoil of nature, or of the blood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recoil', 'n.', 'The state or condition of having recoiled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recoil', 'n.', 'Specifically, the reaction or rebounding of a firearm when\n   discharged.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recoiler', 'n.', 'One who, or that which, recoils.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recoilingly', 'adv.', 'In the manner of a recoil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recoilment', 'n.', 'Recoil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recoin', 'v. t.', 'To coin anew or again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recoinage', 'n.', 'The act of coining anew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recoinage', 'n.', 'That which is coined anew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Re-collect', 'v. t.', 'To collect again; to gather what has been scattered;\n   as, to re-collect routed troops.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recollected', 'imp. & p. p.', 'of Recollect');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recollecting', 'p. pr. & vb. n.', 'of Recollect');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recollect', 'v. t.', 'To recover or recall the knowledge of; to bring back\n   to the mind or memory; to remember.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recollect', 'v. t.', 'Reflexively, to compose ones self; to recover\n   self-command; as, to recollect ones self after a burst of anger; --\n   sometimes, formerly, in the perfect participle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recollect', 'n.', 'A friar of the Strict Observance, -- an order of\n   Franciscans.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recollection', 'n.', 'The act of recollecting, or recalling to the memory;\n   the operation by which objects are recalled to the memory, or ideas\n   revived in the mind; reminiscence; remembrance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recollection', 'n.', 'The power of recalling ideas to the mind, or the\n   period within which things can be recollected; remembrance; memory; as,\n   an event within my recollection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recollection', 'n.', 'That which is recollected; something called to mind;\n   reminiscence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recollection', 'n.', 'The act or practice of collecting or concentrating\n   the mind; concentration; self-control.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recollective', 'a.', 'Having the power of recollecting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recollet', 'n.', 'Same as Recollect, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recolonization', 'n.', 'A second or renewed colonization.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recolonize', 'v. t.', 'To colonize again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recombination', 'n.', 'Combination a second or additional time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recombine', 'v. t.', 'To combine again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recomfort', 'v. t.', 'To comfort again; to console anew; to give new\n   strength to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recomfortless', 'a.', 'Without comfort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recomforture', 'n.', 'The act of recomforting; restoration of comfort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recommence', 'v. i.', 'To commence or begin again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recommence', 'v. i.', 'To begin anew to be; to act again as.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recommence', 'v. t.', 'To commence again or anew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recommencement', 'n.', 'A commencement made anew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recommended', 'imp. & p. p.', 'of Recommend');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recommending', 'p. pr. & vb. n.', 'of Recommend');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recommend', 'v. t.', 'To commend to the favorable notice of another; to\n   commit to anothers care, confidence, or acceptance, with favoring\n   representations; to put in a favorable light before any one; to bestow\n   commendation on; as, he recommended resting the mind and exercising the\n   body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recommend', 'v. t.', 'To make acceptable; to attract favor to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recommend', 'v. t.', 'To commit; to give in charge; to commend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recommendable', 'a.', 'Suitable to be recommended; worthy of praise;\n   commendable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recommendation', 'n.', 'The act of recommending.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recommendation', 'n.', 'That which recommends, or commends to favor;\n   anything procuring, or tending to procure, a favorable reception, or to\n   secure acceptance and adoption; as, he brought excellent\n   recommendations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recommendation', 'n.', 'The state of being recommended; esteem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recommendative', 'n.', 'That which recommends; a recommendation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recommendatory', 'a.', 'Serving to recommend; recommending; commendatory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recommender', 'n.', 'One who recommends.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recommission', 'v. t.', 'To commission again; to give a new commission to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recommit', 'v. t.', 'To commit again; to give back into keeping;\n   specifically, to refer again to a committee; as, to recommit a bill to\n   the same committee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recommitment', 'n.', 'Alt. of Recommittal');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recommittal', 'n.', 'A second or renewed commitment; a renewed reference to\n   a committee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recompact', 'v. t.', 'To compact or join anew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recompensation', 'n.', 'Recompense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recompensation', 'n.', 'Used to denote a case where a set-off pleaded by\n   the defendant is met by a set-off pleaded by the plaintiff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recompensed', 'imp. & p. p.', 'of Recompense');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recompensing', 'p. pr. & vb. n.', 'of Recompense');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recompense', 'v. t.', 'To render an equivalent to, for service, loss, etc.;\n   to requite; to remunerate; to compensate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recompense', 'v. t.', 'To return an equivalent for; to give compensation\n   for; to atone for; to pay for.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recompense', 'v. t.', 'To give in return; to pay back; to pay, as something\n   earned or deserved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recompense', 'v. i.', 'To give recompense; to make amends or requital.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recompense', 'n.', 'An equivalent returned for anything done, suffered, or\n   given; compensation; requital; suitable return.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recompensement', 'n.', 'Recompense; requital.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recompenser', 'n.', 'One who recompenses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recompensive', 'a.', 'Of the nature of recompense; serving to recompense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recompilation', 'n.', 'A new compilation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recompile', 'v. t.', 'To compile anew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recompilement', 'n.', 'The act of recompiling; new compilation or digest;\n   as, a recompilement of the laws.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recomposed', 'imp. & p. p.', 'of Recompose');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recomposing', 'p. pr. & vb. n.', 'of Recompose');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recompose', 'v. t.', 'To compose again; to form anew; to put together again\n   or repeatedly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recompose', 'v. t.', 'To restore to composure; to quiet anew; to\n   tranquilize; as, to recompose the mind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recomposer', 'n.', 'One who recomposes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recomposition', 'n.', 'The act of recomposing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reconcilable', 'a.', 'Capable of being reconciled; as, reconcilable\n   adversaries; an act reconciable with previous acts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reconciled', 'imp. & p. p.', 'of Reconcile');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reconciling', 'p. pr. & vb. n.', 'of Reconcile');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reconcile', 'v. t.', 'To cause to be friendly again; to conciliate anew; to\n   restore to friendship; to bring back to harmony; to cause to be no\n   longer at variance; as, to reconcile persons who have quarreled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reconcile', 'v. t.', 'To bring to acquiescence, content, or quiet\n   submission; as, to reconcile ones self to affictions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reconcile', 'v. t.', 'To make consistent or congruous; to bring to\n   agreement or suitableness; -- followed by with or to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reconcile', 'v. t.', 'To adjust; to settle; as, to reconcile differences.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reconcile', 'v. i.', 'To become reconciled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reconcilement', 'n.', 'Reconciliation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reconciler', 'n.', 'One who reconciles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reconciliation', 'n.', 'The act of reconciling, or the state of being\n   reconciled; reconcilenment; restoration to harmony; renewal of\n   friendship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reconciliation', 'n.', 'Reduction to congruence or consistency; removal of\n   inconsistency; harmony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reconciliatory', 'a.', 'Serving or tending to reconcile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recondensation', 'n.', 'The act or process of recondensing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recondense', 'v. t.', 'To condense again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recondite', 'a.', 'Hidden from the mental or intellectual view; secret;\n   abstruse; as, recondite causes of things.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recondite', 'a.', 'Dealing in things abstruse; profound; searching; as,\n   recondite studies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reconditory', 'n.', 'A repository; a storehouse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reconduct', 'v. t.', 'To conduct back or again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reconfirm', 'v. t.', 'To confirm anew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reconfort', 'v. t.', 'To recomfort; to comfort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reconjoin', 'v. t.', 'To join or conjoin anew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reconnoissance', 'n.', 'Alt. of Reconnaissance');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reconnaissance', 'n.', 'The act of reconnoitering; preliminary examination\n   or survey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reconnaissance', 'n.', 'An examination or survey of a region in reference\n   to its general geological character.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reconnaissance', 'n.', 'An examination of a region as to its general\n   natural features, preparatory to a more particular survey for the\n   purposes of triangulation, or of determining the location of a public\n   work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reconnaissance', 'n.', 'An examination of a territory, or of an enemys\n   position, for the purpose of obtaining information necessary for\n   directing military operations; a preparatory expedition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reconnoiter', 'v. t.', 'Alt. of Reconnoitre');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reconnoitre', 'v. t.', 'To examine with the eye to make a preliminary\n   examination or survey of; esp., to survey with a view to military or\n   engineering operations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reconnoitre', 'v. t.', 'To recognize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reconquer', 'v. t.', 'To conquer again; to recover by conquest; as, to\n   reconquer a revolted province.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reconquest', 'n.', 'A second conquest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reconsecrate', 'v. t.', 'To consecrate anew or again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reconsecration', 'n.', 'Renewed consecration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reconsider', 'v. t.', 'To consider again; as, to reconsider a subject.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reconsider', 'v. t.', 'To take up for renewed consideration, as a motion or\n   a vote which has been previously acted upon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reconsideration', 'n.', 'The act of reconsidering, or the state of being\n   reconsidered; as, the reconsideration of a vote in a legislative body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reconsolate', 'v. t.', 'To console or comfort again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reconsolidate', 'v. t.', 'To consolidate anew or again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reconsolidation', 'n.', 'The act or process of reconsolidating; the state\n   of being reconsolidated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reconstruct', 'v. t.', 'To construct again; to rebuild; to remodel; to form\n   again or anew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reconstruction', 'n.', 'The act of constructing again; the state of being\n   reconstructed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reconstruction', 'n.', 'The act or process of reorganizing the governments\n   of the States which had passed ordinances of secession, and of\n   reestablishing their constitutional relations to the national\n   government, after the close of the Civil War.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reconstructive', 'a.', 'Reconstructing; tending to reconstruct; as, a\n   reconstructive policy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recontinuance', 'n.', 'The act or state of recontinuing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recontinue', 'v. t. & i.', 'To continue anew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reconvene', 'v. t. & i.', 'To convene or assemble again; to call or come\n   together again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reconvention', 'n.', 'A cross demand; an action brought by the defendant\n   against the plaintiff before the same judge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reconversion', 'n.', 'A second conversion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reconvert', 'v. t.', 'To convert again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reconvert', 'n.', 'A person who has been reconverted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reconvertible', 'a.', 'Capable of being reconverted; convertible again to\n   the original form or condition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reconvey', 'v. t.', 'To convey back or to the former place; as, to reconvey\n   goods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reconvey', 'v. t.', 'To transfer back to a former owner; as, to reconvey an\n   estate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reconveyance', 'n.', 'Act of reconveying.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recopy', 'v. t.', 'To copy again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recorded', 'imp. & p. p.', 'of Record');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recording', 'p. pr. & vb. n.', 'of Record');
  INSERT INTO words (word, word_type, description) VALUES  (    'Record', 'v. t.', 'To recall to mind; to recollect; to remember; to\n   meditate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Record', 'v. t.', 'To repeat; to recite; to sing or play.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Record', 'v. t.', 'To preserve the memory of, by committing to writing, to\n   printing, to inscription, or the like; to make note of; to write or\n   enter in a book or on parchment, for the purpose of preserving\n   authentic evidence of; to register; to enroll; as, to record the\n   proceedings of a court; to record historical events.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Record', 'v. i.', 'To reflect; to ponder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Record', 'v. i.', 'To sing or repeat a tune.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Record', 'v. t.', 'A writing by which some act or event, or a number of\n   acts or events, is recorded; a register; as, a record of the acts of\n   the Hebrew kings; a record of the variations of temperature during a\n   certain time; a family record.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Record', 'v. t.', 'An official contemporaneous writing by which the acts of\n   some public body, or public officer, are recorded; as, a record of city\n   ordinances; the records of the receiver of taxes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Record', 'v. t.', 'An authentic official copy of a document which has been\n   entered in a book, or deposited in the keeping of some officer\n   designated by law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Record', 'v. t.', 'An official contemporaneous memorandum stating the\n   proceedings of a court of justice; a judicial record.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Record', 'v. t.', 'The various legal papers used in a case, together with\n   memoranda of the proceedings of the court; as, it is not permissible to\n   allege facts not in the record.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Record', 'v. t.', 'Testimony; witness; attestation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Record', 'v. t.', 'That which serves to perpetuate a knowledge of acts or\n   events; a monument; a memorial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Record', 'v. t.', 'That which has been, or might be, recorded; the known\n   facts in the course, progress, or duration of anything, as in the life\n   of a public man; as, a politician with a good or a bad record.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Record', 'v. t.', 'That which has been publicly achieved in any kind of\n   competitive sport as recorded in some authoritative manner, as the time\n   made by a winning horse in a race.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recordance', 'n.', 'Remembrance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recordation', 'v. t.', 'Remembrance; recollection; also, a record.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recorder', 'n.', 'One who records; specifically, a person whose official\n   duty it is to make a record of writings or transactions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recorder', 'n.', 'The title of the chief judical officer of some cities and\n   boroughs; also, of the chief justice of an East Indian settlement. The\n   Recorder of London is judge of the Lord Mayors Court, and one of the\n   commissioners of the Central Criminal Court.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recorder', 'n.', 'A kind of wind instrument resembling the flageolet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recordership', 'n.', 'The office of a recorder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recording', 'a.', 'Keeping a record or a register; as, a recording\n   secretary; -- applied to numerous instruments with an automatic\n   appliance which makes a record of their action; as, a recording gauge\n   or telegraph.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recorporification', 'n.', 'The act of investing again with a body; the\n   state of being furnished anew with a body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recouch', 'v. i.', 'To retire again to a couch; to lie down again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recount', 'v. t.', 'To count or reckon again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recount', 'n.', 'A counting again, as of votes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recount', 'v.', 'To tell over; to relate in detail; to recite; to tell or\n   narrate the particulars of; to rehearse; to enumerate; as, to recount\n   ones blessings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recountment', 'n.', 'Recital.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recoup', 'v. t.', 'Alt. of Recoupe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recoupe', 'v. t.', 'To keep back rightfully (a part), as if by cutting off,\n   so as to diminish a sum due; to take off (a part) from damages; to\n   deduct; as, where a landlord recouped the rent of premises from damages\n   awarded to the plaintiff for eviction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recoupe', 'v. t.', 'To get an equivalent or compensation for; as, to recoup\n   money lost at the gaming table; to recoup ones losses in the share\n   market.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recoupe', 'v. t.', 'To reimburse; to indemnify; -- often used reflexively\n   and in the passive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recouper', 'n.', 'One who recoups.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recoupment', 'n.', 'The act of recouping.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recourse', 'n.', 'A coursing back, or coursing again, along the line of a\n   previous coursing; renewed course; return; retreat; recurence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recourse', 'n.', 'Recurrence in difficulty, perplexity, need, or the like;\n   access or application for aid; resort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recourse', 'n.', 'Access; admittance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recourse', 'v. i.', 'To return; to recur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recourse', 'v. i.', 'To have recourse; to resort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recourseful', 'a.', 'Having recurring flow and ebb; moving alternately.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recover', 'v. t.', 'To cover again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recovered', 'imp. & p. p.', 'of Recover');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recovering', 'p. pr. & vb. n.', 'of Recover');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recover', 'v. t.', 'To get or obtain again; to get renewed possession of;\n   to win back; to regain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recover', 'v. t.', 'To make good by reparation; to make up for; to\n   retrieve; to repair the loss or injury of; as, to recover lost time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recover', 'v. t.', 'To restore from sickness, faintness, or the like; to\n   bring back to life or health; to cure; to heal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recover', 'v. t.', 'To overcome; to get the better of, -- as a state of\n   mind or body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recover', 'v. t.', 'To rescue; to deliver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recover', 'v. t.', 'To gain by motion or effort; to obtain; to reach; to\n   come to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recover', 'v. t.', 'To gain as a compensation; to obtain in return for\n   injury or debt; as, to recover damages in trespass; to recover debt and\n   costs in a suit at law; to obtain title to by judgement in a court of\n   law; as, to recover lands in ejectment or common recovery; to gain by\n   legal process; as, to recover judgement against a defendant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recover', 'v. i.', 'To regain health after sickness; to grow well; to be\n   restored or cured; hence, to regain a former state or condition after\n   misfortune, alarm, etc.; -- often followed by of or from; as, to\n   recover from a state of poverty; to recover from fright.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recover', 'v. i.', 'To make ones way; to come; to arrive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recover', 'v. i.', 'To obtain a judgement; to succeed in a lawsuit; as, the\n   plaintiff has recovered in his suit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recover', 'n.', 'Recovery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recoverable', 'a.', 'Capable of being recovered or regained; capable of\n   being brought back to a former condition, as from sickness, misfortune,\n   etc.; obtainable from a debtor or possessor; as, the debt is\n   recoverable; goods lost or sunk in the ocean are not recoverable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Re coverance', 'n.', 'Recovery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recoveree', 'n.', 'The person against whom a judgment is obtained in common\n   recovery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recoverer', 'n.', 'One who recovers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recoveror', 'n.', 'The demandant in a common recovery after judgment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recovery', 'n.', 'The act of recovering, regaining, or retaking possession.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recovery', 'n.', 'Restoration from sickness, weakness, faintness, or the\n   like; restoration from a condition of mistortune, of fright, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recovery', 'n.', 'The obtaining in a suit at law of a right to something by\n   a verdict and judgment of court.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recovery', 'n.', 'The getting, or gaining, of something not previously had.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recovery', 'n.', 'In rowing, the act of regaining the proper position for\n   making a new stroke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recreance', 'n.', 'Recreancy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recreancy', 'n.', 'The quality or state of being recreant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recreant', 'a.', 'Crying for mercy, as a combatant in the trial by battle;\n   yielding; cowardly; mean-spirited; craven.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recreant', 'a.', 'Apostate; false; unfaithful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recreant', 'n.', 'One who yields in combat, and begs for mercy; a\n   mean-spirited, cowardly wretch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Re-create', 'v. t.', 'To create or form anew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recreated', 'imp. & p. p.', 'of Recreate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recreating', 'p. pr. & vb. n.', 'of Recreate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recreate', 'v. t.', 'To give fresh life to; to reanimate; to revive;\n   especially, to refresh after wearying toil or anxiety; to relieve; to\n   cheer; to divert; to amuse; to gratify.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recreate', 'v. i.', 'To take recreation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recreation', 'n.', 'The act of recreating, or the state of being recreated;\n   refreshment of the strength and spirits after toil; amusement;\n   diversion; sport; pastime.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Re-creation', 'n.', 'A forming anew; a new creation or formation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Re-creative', 'a.', 'Creating anew; as, re-creative power.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recreative', 'a.', 'Tending to recreate or refresh; recreating; giving new\n   vigor or animation; reinvigorating; giving relief after labor or pain;\n   amusing; diverting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recrement', 'n.', 'Superfluous matter separated from that which is useful;\n   dross; scoria; as, the recrement of ore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recrement', 'n.', 'Excrement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recrement', 'n.', 'A substance secreted from the blood and again absorbed\n   by it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recremental', 'a.', 'Recrementitious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recrementitial', 'a.', 'Of the nature of a recrement. See Recrement, 2 (b).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recrementitious', 'a.', 'Of or pertaining to recrement; consisting of\n   recrement or dross.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recriminate', 'v. i.', 'To return one charge or accusation with another; to\n   charge back fault or crime upon an accuser.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recriminate', 'v. t.', 'To accuse in return.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recrimination', 'n.', 'The act of recriminating; an accusation brought by\n   the accused against the accuser; a counter accusation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recriminative', 'a.', 'Recriminatory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recriminator', 'n.', 'One who recriminates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recriminatory', 'a.', 'Having the quality of recrimination; retorting\n   accusation; recriminating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recross', 'v. t.', 'To cross a second time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recrudency', 'n.', 'Recrudescence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recrudescence', 'n.', 'Alt. of Recrudescency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recrudescency', 'n.', 'The state or condition of being recrudescent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recrudescency', 'n.', 'Increased severity of a disease after temporary\n   remission.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recrudescent', 'a.', 'Growing raw, sore, or painful again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recrudescent', 'a.', 'Breaking out again after temporary abatement or\n   supression; as, a recrudescent epidemic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recruited', 'imp. & p. p.', 'of Recruit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recruiting', 'p. pr. & vb. n.', 'of Recruit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recruit', 'v. t.', 'To repair by fresh supplies, as anything wasted; to\n   remedy lack or deficiency in; as, food recruits the flesh; fresh air\n   and exercise recruit the spirits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recruit', 'v. t.', 'Hence, to restore the wasted vigor of; to renew in\n   strength or health; to reinvigorate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recruit', 'v. t.', 'To supply with new men, as an army; to fill up or make\n   up by enlistment; as, he recruited two regiments; the army was\n   recruited for a campaign; also, to muster; to enlist; as, he recruited\n   fifty men.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recruit', 'v. i.', 'To gain new supplies of anything wasted; to gain\n   health, flesh, spirits, or the like; to recuperate; as, lean cattle\n   recruit in fresh pastures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recruit', 'v. i.', 'To gain new supplies of men for military or other\n   service; to raise or enlist new soldiers; to enlist troops.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recruit', 'n.', 'A supply of anything wasted or exhausted; a reenforcement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recruit', 'n.', 'Specifically, a man enlisted for service in the army; a\n   newly enlisted soldier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recruiter', 'n.', 'One who, or that which, recruits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recruitment', 'n.', 'The act or process of recruiting; especially, the\n   enlistment of men for an army.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recrystallization', 'n.', 'The process or recrystallizing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recrystallize', 'v. i. & t.', 'To crystallize again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rectal', 'a.', 'Of or pertaining to the rectum; in the region of the\n   rectum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rectangle', 'n.', 'A four-sided figure having only right angles; a\n   right-angled parallelogram.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rectangle', 'a.', 'Rectangular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rectangled', 'a.', 'Rectangular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rectangular', 'a.', 'Right-angled; having one or more angles of ninety\n   degrees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rectangularity', 'n.', 'The quality or condition of being rectangular, or\n   right-angled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recti-', '', 'A combining form signifying straight; as, rectilineal, having\n   straight lines; rectinerved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rectifiable', 'a.', 'Capable of being rectified; as, a rectifiable mistake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rectifiable', 'a.', 'Admitting, as a curve, of the construction of a\n   straight l//e equal in length to any definite portion of the curve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rectification', 'n.', 'The act or operation of rectifying; as, the\n   rectification of an error; the rectification of spirits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rectification', 'n.', 'The determination of a straight line whose length is\n   equal a portion of a curve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rectificator', 'n.', 'That which rectifies or refines; esp., a part of a\n   distilling apparatus in which the more volatile portions are separated\n   from the less volatile by the process of evaporation and condensation;\n   a rectifier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rectifier', 'n.', 'One who, or that which, rectifies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rectifier', 'n.', 'Specifically: (a) (Naut.) An instrument used for\n   determining and rectifying the variations of the compass on board ship.\n   (b) (Chem.) A rectificator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rectified', 'imp. & p. p.', 'of Rectify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rectifying', 'p. pr. & vb. n.', 'of Rectify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rectify', 'v. t.', 'To make or set right; to correct from a wrong,\n   erroneous, or false state; to amend; as, to rectify errors, mistakes,\n   or abuses; to rectify the will, the judgment, opinions; to rectify\n   disorders.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rectify', 'v. t.', 'To refine or purify by repeated distillation or\n   sublimation, by which the fine parts of a substance are separated from\n   the grosser; as, to rectify spirit of wine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rectify', 'v. t.', 'To produce ( as factitious gin or brandy) by\n   redistilling low wines or ardent spirits (whisky, rum, etc.), flavoring\n   substances, etc., being added.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rectilineal', 'a.', 'Alt. of Rectilinear');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rectilinear', 'a.', 'Straight; consisting of a straight line or lines;\n   bounded by straight lines; as, a rectineal angle; a rectilinear figure\n   or course.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rectilinearity', 'n.', 'The quality or state of being rectilinear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rectilineous', 'a.', 'Rectilinear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rectinerved', 'a.', 'Having the veins or nerves straight; -- said of\n   leaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rection', 'n.', 'See Government, n., 7.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rectirostral', 'a.', 'Having a straight beak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rectiserial', 'a.', 'Arranged in exactly vertical ranks, as the leaves on\n   stems of many kinds; -- opposed to curviserial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rectitis', 'n.', 'Proctitis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rectitude', 'n.', 'Straightness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rectitude', 'n.', 'Rightness of principle or practice; exact conformity to\n   truth, or to the rules prescribed for moral conduct, either by divine\n   or human laws; uprightness of mind; uprightness; integrity; honesty;\n   justice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rectitude', 'n.', 'Right judgment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recto-', '', 'A combining form indicating connection with, or relation to,\n   the rectum; as, recto-vesical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recto', 'n.', 'A writ of right.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recto', 'n.', 'The right-hand page; -- opposed to verso.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rector', 'n.', 'A ruler or governor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rector', 'n.', 'A clergyman who has the charge and cure of a parish, and\n   has the tithes, etc.; the clergyman of a parish where the tithes are\n   not impropriate. See the Note under Vicar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rector', 'n.', 'A clergyman in charge of a parish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rector', 'n.', 'The head master of a public school.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rector', 'n.', 'The chief elective officer of some universities, as in\n   France and Scotland; sometimes, the head of a college; as, the Rector\n   of Exeter College, or of Lincoln College, at Oxford.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rector', 'n.', 'The superior officer or chief of a convent or religious\n   house; and among the Jesuits the superior of a house that is a seminary\n   or college.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rectoral', 'a.', 'Pertaining to a rector or governor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rectorate', 'n.', 'The office, rank, or station of a rector; rectorship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rectoress', 'n.', 'A governess; a rectrix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rectoress', 'n.', 'The wife of a rector.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rectorial', 'a.', 'Pertaining to a rector or a rectory; rectoral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rectorship', 'n.', 'Government; guidance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rectorship', 'n.', 'The office or rank of a rector; rectorate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rectories', 'pl. ', 'of Rectory');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rectory', 'n.', 'The province of a rector; a parish church, parsonage, or\n   spiritual living, with all its rights, tithes, and glebes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rectory', 'n.', 'A rectors mansion; a parsonage house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recto-uterine', 'a.', 'Of or pertaining to both the rectum and the uterus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rectovaginal', 'a.', 'Of or pertaining to both the rectum and the vagina.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recto-vesical', 'a.', 'Of or pertaining to both the rectum and the bladder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rectress', 'n.', 'A rectoress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rectrices', 'pl. ', 'of Rectrix');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rectrix', 'n.', 'A governess; a rectoress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rectrix', 'n.', 'One of the quill feathers of the tail of a bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rectum', 'n.', 'The terminal part of the large intestine; -- so named\n   because supposed by the old anatomists to be straight. See Illust.\n   under Digestive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recti', 'pl. ', 'of Rectus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rectus', 'n.', 'A straight muscle; as, the recti of the eye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recubation', 'n.', 'Recumbence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recule', 'v. i.', 'To recoil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recule', 'n.', 'Alt. of Reculement');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reculement', 'n.', 'Recoil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recumb', 'v. i.', 'To lean; to recline; to repose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recumbence', 'n.', 'The act of leaning, resting, or reclining; the state of\n   being recumbent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recumbency', 'n.', 'Recumbence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recumbent', 'a.', 'Leaning; reclining; lying; as, the recumbent posture of\n   the Romans at their meals. Hence, figuratively; Resting; inactive;\n   idle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recuperable', 'a.', 'Recoverable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recuperated', 'imp. &. p. p.', 'of Recuperate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recuperating', 'p. pr. & vb. n.', 'of Recuperate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recuperate', 'v. i.', 'To recover health; to regain strength; to\n   convalesce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recuperate', 'v. t.', 'To recover; to regain; as, to recuperate the health\n   or strength.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recuperation', 'n..', 'Recovery, as of anything lost, especially of the\n   health or strength.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recuperative', 'a.', 'Alt. of Recuperatory');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recuperatory', 'a.', 'Of or pertaining to recuperation; tending to\n   recovery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recuperator', 'n.', 'Same as Regenerator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recurred', 'imp. & p. p.', 'of Recur');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recurring', 'p. pr. & vb. n.', 'of Recur');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recur', 'v. i.', 'To come back; to return again or repeatedly; to come\n   again to mind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recur', 'v. i.', 'To occur at a stated interval, or according to some\n   regular rule; as, the fever will recur to-night.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recur', 'v. i.', 'To resort; to have recourse; to go for help.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recure', 'v. t.', 'To arrive at; to reach; to attain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recure', 'v. t.', 'To recover; to regain; to repossess.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recure', 'v. t.', 'To restore, as from weariness, sickness; or the like; to\n   repair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recure', 'v. t.', 'To be a cure for; to remedy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recure', 'n.', 'Cure; remedy; recovery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recureless', 'a.', 'Incapable of cure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recurrence', 'n.', 'Alt. of Recurrency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recurrency', 'n.', 'The act of recurring, or state of being recurrent;\n   return; resort; recourse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recurrent', 'a.', 'Returning from time to time; recurring; as, recurrent\n   pains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recurrent', 'a.', 'Running back toward its origin; as, a recurrent nerve or\n   artery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recursant', 'a.', 'Displayed with the back toward the spectator; -- said\n   especially of an eagle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recursion', 'n.', 'The act of recurring; return.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recurvate', 'a.', 'Recurved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recurvate', 'v. t.', 'To bend or curve back; to recurve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recurvation', 'n.', 'The act of recurving, or the state of being recurved;\n   a bending or flexure backward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recurve', 'v. t.', 'To curve in an opposite or unusual direction; to bend\n   back or down.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recurved', 'a.', 'Curved in an opposite or uncommon direction; bent back;\n   as, a bird with a recurved bill; flowers with recurved petals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recurviroster', 'n.', 'A bird whose beak bends upward, as the avocet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recurvirostral', 'a.', 'Having the beak bent upwards.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recurvity', 'n.', 'Recurvation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recurvous', 'a.', 'Recurved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recusancy', 'n.', 'The state of being recusant; nonconformity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recusant', 'a.', 'Obstinate in refusal; specifically, in English history,\n   refusing to acknowledge the supremacy of the king in the churc, or to\n   conform to the established rites of the church; as, a recusant lord.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recusant', 'n.', 'One who is obstinate in refusal; one standing out\n   stubbornly against general practice or opinion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recusant', 'n.', 'A person who refuses to acknowledge the supremacy of the\n   king in matters of religion; as, a Roman Catholic recusant, who\n   acknowledges the supremacy of the pope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recusant', 'n.', 'One who refuses communion with the Church of England; a\n   nonconformist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recusation', 'n.', 'Refusal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recusation', 'n.', 'The act of refusing a judge or challenging that he\n   shall not try the cause, on account of his supposed partiality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recusative', 'a.', 'Refusing; denying; negative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recuse', 'v. t.', 'To refuse or reject, as a judge; to challenge that the\n   judge shall not try the cause.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Recussion', 'n.', 'The act of beating or striking back.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Red', '', '. imp. & p. p. of Read.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Red', 'v. t.', 'To put on order; to make tidy; also, to free from\n   entanglement or embarrassement; -- generally with up; as, to red up a\n   house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Red', 'superl.', 'Of the color of blood, or of a tint resembling that\n   color; of the hue of that part of the rainbow, or of the solar\n   spectrum, which is furthest from the violet part.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Red', 'n.', 'The color of blood, or of that part of the spectrum farthest\n   from violet, or a tint resembling these.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Red', 'n.', 'A red pigment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Red', 'n.', 'An abbreviation for Red Republican. See under Red, a.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Red', 'a.', 'The menses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redact', 'v. t.', 'To reduce to form, as literary matter; to digest and put\n   in shape (matter for publication); to edit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redacteur', 'n.', 'See Redactor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redaction', 'n.', 'The act of redacting; work produced by redacting; a\n   digest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redactor', 'n.', 'One who redacts; one who prepares matter for publication;\n   an editor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redan', 'n.', 'A work having two parapets whose faces unite so as to form a\n   salient angle toward the enemy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redan', 'n.', 'A step or vertical offset in a wall on uneven ground, to\n   keep the parts level.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redargued', 'imp. & p. p.', 'of Redargue');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redarguing', 'p. pr. & vb. n.', 'of Redargue');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redargue', 'v. t.', 'To disprove; to refute; toconfute; to reprove; to\n   convict.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redargution', 'n.', 'The act of redarguing; refutation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redargutory', 'a.', 'Pertaining to, or containing, redargution; refutatory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redback', 'n.', 'The dunlin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redbelly', 'n.', 'The char.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redbird', 'n.', 'The cardinal bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redbird', 'n.', 'The summer redbird (Piranga rubra).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redbird', 'n.', 'The scarlet tanager. See Tanager.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redbreast', 'n.', 'The European robin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redbreast', 'n.', 'The American robin. See Robin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redbreast', 'n.', 'The knot, or red-breasted snipe; -- called also robin\n   breast, and robin snipe. See Knot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redbreast', 'n.', 'The long-eared pondfish. See Pondfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redbud', 'n.', 'A small ornamental leguminous tree of the American species\n   of the genus Cercis. See Judas tree, under Judas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redcap', 'n.', 'The European goldfinch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redcap', 'n.', 'A specter having long teeth, popularly supposed to haunt\n   old castles in Scotland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redcoat', 'n.', 'One who wears a red coat; specifically, a red-coated\n   British soldier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redde', '', 'obs. imp. of Read, or Rede.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reddened', 'imp. & p. p.', 'of Redden');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reddening', 'p. pr. & vb. n.', 'of Redden');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redden', 'a.', 'To make red or somewhat red; to give a red color to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redden', 'v. i.', 'To grow or become red; to blush.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reddendum', 'n.', 'A clause in a deed by which some new thing is reserved\n   out of what had been granted before; the clause by which rent is\n   reserved in a lease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reddish', 'a.', 'Somewhat red; moderately red.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reddition', 'n.', 'Restoration: restitution: surrender.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reddition', 'n.', 'Explanation; representation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redditive', 'a.', 'Answering to an interrogative or inquiry; conveying a\n   reply; as, redditive words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reddle', 'n.', 'Red chalk. See under Chalk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reddour', 'n.', 'Rigor; violence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rede', 'v. t.', 'To advise or counsel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rede', 'v. t.', 'To interpret; to explain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rede', 'n.', 'Advice; counsel; suggestion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rede', 'n.', 'A word or phrase; a motto; a proverb; a wise saw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redeemed', 'imp. & p. p.', 'of Redeem');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redeeming', 'p. pr. & vb. n.', 'of Redeem');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redeem', 'v. t.', 'To purchase back; to regain possession of by payment of\n   a stipulated price; to repurchase.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redeem', 'v. t.', 'To recall, as an estate, or to regain, as mortgaged\n   property, by paying what may be due by force of the mortgage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redeem', 'v. t.', 'To regain by performing the obligation or condition\n   stated; to discharge the obligation mentioned in, as a promissory note,\n   bond, or other evidence of debt; as, to redeem bank notes with coin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redeem', 'v. t.', 'To ransom, liberate, or rescue from captivity or\n   bondage, or from any obligation or liability to suffer or to be\n   forfeited, by paying a price or ransom; to ransom; to rescue; to\n   recover; as, to redeem a captive, a pledge, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redeem', 'v. t.', 'Hence, to rescue and deliver from the bondage of sin and\n   the penalties of Gods violated law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redeem', 'v. t.', 'To make good by performing fully; to fulfill; as, to\n   redeem ones promises.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redeem', 'v. t.', 'To pay the penalty of; to make amends for; to serve as\n   an equivalent or offset for; to atone for; to compensate; as, to redeem\n   an error.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redeemability', 'n.', 'Redeemableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redeemable', 'a.', 'Capable of being redeemed; subject to repurchase; held\n   under conditions permitting redemption; as, a pledge securing the\n   payment of money is redeemable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redeemable', 'a.', 'Subject to an obligation of redemtion; conditioned upon\n   a promise of redemtion; payable; due; as, bonds, promissory notes, etc.\n   , redeemabble in gold, or in current money, or four months after date.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redeemableness', 'n.', 'The quality or state of being redeemable;\n   redeemability.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redeemer', 'n.', 'One who redeems.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redeemer', 'n.', 'Specifically, the Savior of the world, Jesus Christ.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redeless', 'a.', 'Without rede or counsel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redeliberate', 'v. t. & i.', 'To deliberate again; to reconsider.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redeliver', 'v. t.', 'To deliver or give back; to return.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redeliver', 'v. t.', 'To deliver or liberate a second time or again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redeliver', 'v. t.', 'To report; to deliver the answer of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redeliverance', 'n.', 'A second deliverance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redelivery', 'n.', 'Act of delivering back.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redelivery', 'n.', 'A second or new delivery or liberation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redemand', 'v. t.', 'To demand back; to demand again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redemand', 'n.', 'A demanding back; a second or renewed demand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redemise', 'v. t.', 'To demise back; to convey or transfer back, as an\n   estate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redemise', 'n.', 'The transfer of an estate back to the person who demised\n   it; reconveyance; as, the demise and redemise of an estate. See under\n   Demise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redemonstrate', 'v. t.', 'To demonstrate again, or anew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redemptible', 'a.', 'Redeemable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Re-demption', 'n.', 'The act of redeeming, or the state of being redeemed;\n   repurchase; ransom; release; rescue; deliverance; as, the redemption of\n   prisoners taken in war; the redemption of a ship and cargo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Re-demption', 'n.', 'The liberation of an estate from a mortgage, or the\n   taking back of property mortgaged, upon performance of the terms or\n   conditions on which it was conveyed; also, the right of redeeming and\n   reentering upon an estate mortgaged. See Equity of redemption, under\n   Equity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Re-demption', 'n.', 'Performance of the obligation stated in a note, bill,\n   bond, or other evidence of debt, by making payment to the holder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Re-demption', 'n.', 'The procuring of Gods favor by the sufferings and\n   death of Christ; the ransom or deliverance of sinners from the bondage\n   of sin and the penalties of Gods violated law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redemptionary', 'n.', 'One who is, or may be, redeemed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redemptioner', 'n.', 'One who redeems himself, as from debt or servitude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redemptioner', 'n.', 'Formerly, one who, wishing to emigrate from Europe to\n   America, sold his services for a stipulated time to pay the expenses of\n   his passage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redemptionist', 'n.', 'A monk of an order founded in 1197; -- so called\n   because the order was especially devoted to the redemption of\n   Christians held in captivity by the Mohammedans. Called also\n   Trinitarian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redemptive', 'a.', 'Serving or tending to redeem; redeeming; as, the\n   redemptive work of Christ.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redemptorist', 'n.', 'One of the Congregation of the Most Holy Redeemer,\n   founded in Naples in 1732 by St. Alphonsus Maria de Liquori. It was\n   introduced onto the United States in 1832 at Detroit. The Fathers of\n   the Congregation devote themselves to preaching to the neglected, esp.\n   in missions and retreats, and are forbidden by their rule to engage in\n   the instruction of youth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redemptory', 'a.', 'Paid for ransom; serving to redeem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redempture', 'n.', 'Redemption.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redented', 'a.', 'Formed like the teeth of a saw; indented.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redeposit', 'v. t.', 'To deposit again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redescend', 'v. i.', 'To descend again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redeye', 'n.', 'The rudd.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redeye', 'n.', 'Same as Redfish (d).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redeye', 'n.', 'The goggle-eye, or fresh-water rock bass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redfin', 'n.', 'A small North American dace (Minnilus cornutus, or Notropis\n   megalops). The male, in the breeding season, has bright red fins.\n   Called also red dace, and shiner. Applied also to Notropis ardens, of\n   the Mississippi valley.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redfinch', 'n.', 'The European linnet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redfish', 'n.', 'The blueback salmon of the North Pacific; -- called also\n   nerka. See Blueback (b).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redfish', 'n.', 'The rosefish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redfish', 'n.', 'A large California labroid food fish (Trochocopus\n   pulcher); -- called also fathead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redfish', 'n.', 'The red bass, red drum, or drumfish. See the Note under\n   Drumfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Red-gum', 'n.', 'An eruption of red pimples upon the face, neck, and arms,\n   in early infancy; tooth rash; strophulus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Red-gum', 'n.', 'A name of rust on grain. See Rust.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Red-hand', 'a. / adv.', 'Alt. of Red-handed');
  INSERT INTO words (word, word_type, description) VALUES  (    'Red-handed', 'a. / adv.', 'Having hands red with blood; in the very act, as\n   if with red or bloody hands; -- said of a person taken in the act of\n   homicide; hence, fresh from the commission of crime; as, he was taken\n   red-hand or red-handed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redhead', 'n.', 'A person having red hair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redhead', 'n.', 'An American duck (Aythya Americana) highly esteemed as a\n   game bird. It is closely allied to the canvasback, but is smaller and\n   its head brighter red. Called also red-headed duck. American poachard,\n   grayback, and fall duck. See Illust. under Poachard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redhead', 'n.', 'The red-headed woodpecker. See Woodpecker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redhead', 'n.', 'A kind of milkweed (Asclepias Curassavica) with red\n   flowers. It is used in medicine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redhibition', 'n.', 'The annulling of a sale, and the return by the buyer\n   of the article sold, on account of some defect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redhibitory', 'a.', 'Of or pertaining to redhibition; as, a redhibitory\n   action or fault.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redhoop', 'n.', 'The male of the European bullfinch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redhorn', 'n.', 'Any species of a tribe of butterflies (Fugacia) including\n   the common yellow species and the cabbage butterflies. The antennae are\n   usually red.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Red-hot', 'a.', 'Red with heat; heated to redness; as, red-hot iron;\n   red-hot balls. Hence, figuratively, excited; violent; as, a red-hot\n   radical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rediae', 'pl. ', 'of Redia');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redias', 'pl. ', 'of Redia');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redia', 'n.', 'A kind of larva, or nurse, which is prroduced within the\n   sporocyst of certain trematodes by asexual generation. It in turn\n   produces, in the same way, either another generation of rediae, or else\n   cercariae within its own body. Called also proscolex, and nurse. See\n   Illustration in Appendix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redient', 'a.', 'Returning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redigest', 'v. t.', 'To digest, or reduce to form, a second time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rediminish', 'v. t.', 'To diminish again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redingote', 'n.', 'A long plain double-breasted outside coat for women.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redintegrate', 'a.', 'Restored to wholeness or a perfect state; renewed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redintegrate', 'v. t.', 'To make whole again; a renew; to restore to\n   integrity or soundness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redintegration', 'n.', 'Restoration to a whole or sound state; renewal;\n   renovation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redintegration', 'n.', 'Restoration of a mixed body or matter to its former\n   nature and state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redintegration', 'n.', 'The law that objects which have been previously\n   combined as part of a single mental state tend to recall or suggest one\n   another; -- adopted by many philosophers to explain the phenomena of\n   the association of ideas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redirect', 'a.', 'Applied to the examination of a witness, by the party\n   calling him, after the cross-examination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redisburse', 'v. t.', 'To disburse anew; to give, or pay, back.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rediscover', 'v. t.', 'To discover again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redispose', 'v. t.', 'To dispose anew or again; to readjust; to rearrange.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redisseize', 'v. t.', 'To disseize anew, or a second time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redisseizin', 'n.', 'A disseizin by one who once before was adjudged to\n   have dassezed the same person of the same lands, etc.; also, a writ\n   which lay in such a case.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redisseizor', 'n.', 'One who redisseizes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redissolve', 'v. t.', 'To dissolve again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redistill', 'v. t.', 'To distill again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redistrainer', 'n.', 'One who distrains again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redistribute', 'v. t.', 'To distribute again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redistrict', 'v. t.', 'To divide into new districts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redition', 'n.', 'Act of returning; return.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redivide', 'v. t.', 'To divide anew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redleg', 'n.', 'Alt. of Redlegs');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redlegs', 'n.', 'The redshank.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redlegs', 'n.', 'The turnstone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Red-letter', 'a.', 'Of or pertaining to a red letter; marked by red\n   letters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redly', 'adv.', 'In a red manner; with redness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redmouth', 'n.', 'Any one of several species of marine food fishes of the\n   genus Diabasis, or Haemulon, of the Southern United States, having the\n   inside of the mouth bright red. Called also flannelmouth, and grunt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redness', 'n.', 'The quality or state of being red; red color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redolence', 'n.', 'Alt. of Redolency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redolency', 'n.', 'The quality of being redolent; sweetness of scent;\n   pleasant odor; fragrance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redolent', 'a.', 'Diffusing odor or fragrance; spreading sweet scent;\n   scented; odorous; smelling; -- usually followed by of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redouble', 'v. t.', 'To double again or repeatedly; to increase by\n   continued or repeated additions; to augment greatly; to multiply.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redouble', 'v. i.', 'To become greatly or repeatedly increased; to be\n   multiplied; to be greatly augmented; as, the noise redoubles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redoubt', 'n.', 'A small, and usually a roughly constructed, fort or\n   outwork of varying shape, commonly erected for a temporary purpose, and\n   without flanking defenses, -- used esp. in fortifying tops of hills and\n   passes, and positions in hostile territory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redoubt', 'n.', 'In permanent works, an outwork placed within another\n   outwork. See F and i in Illust. of Ravelin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redoubt', 'v. t.', 'To stand in dread of; to regard with fear; to dread.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redoubtable', 'a.', 'Formidable; dread; terrible to foes; as, a redoubtable\n   hero; hence, valiant; -- often in contempt or burlesque.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redoubted', 'a.', 'Formidable; dread.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redoubting', 'n.', 'Reverence; honor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redounded', 'imp. & p. p.', 'of Redound');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redounding', 'p. pr. & vb. n.', 'of Redound');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redound', 'v. i.', 'To roll back, as a wave or flood; to be sent or driven\n   back; to flow back, as a consequence or effect; to conduce; to\n   contribute; to result.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redound', 'v. i.', 'To be in excess; to remain over and above; to be\n   redundant; to overflow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redound', 'n.', 'The coming back, as of consequence or effect; result;\n   return; requital.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redound', 'n.', 'Rebound; reverberation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redowa', 'n.', 'A Bohemian dance of two kinds, one in triple time, like a\n   waltz, the other in two-four time, like a polka. The former is most in\n   use.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redpole', 'n.', 'Same as Redpoll.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redpoll', 'n.', 'Any one of several species of small northern finches of\n   the genus Acanthis (formerly Aegiothus), native of Europe and America.\n   The adults have the crown red or rosy. The male of the most common\n   species (A. linarius) has also the breast and rump rosy. Called also\n   redpoll linnet. See Illust. under Linnet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redpoll', 'n.', 'The common European linnet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redpoll', 'n.', 'The American redpoll warbler (Dendroica palmarum).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redraft', 'v. t.', 'To draft or draw anew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redraft', 'n.', 'A second draft or copy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redraft', 'n.', 'A new bill of exchange which the holder of a protected\n   bill draws on the drawer or indorsers, in order to recover the amount\n   of the protested bill with costs and charges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redrew', 'imp.', 'of Redraw');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redrawn', 'p. p.', 'of Redraw');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redrawing', 'p. pr. & vb. n.', 'of Redraw');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redraw', 'v. t.', 'To draw again; to make a second draft or copy of; to\n   redraft.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redraw', 'v. i.', 'To draw a new bill of exchange, as the holder of a\n   protested bill, on the drawer or indorsers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redress', 'v. t.', 'To dress again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redress', 'v. t.', 'To put in order again; to set right; to emend; to\n   revise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redress', 'v. t.', 'To set right, as a wrong; to repair, as an injury; to\n   make amends for; to remedy; to relieve from.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redress', 'v. t.', 'To make amends or compensation to; to relieve of\n   anything unjust or oppressive; to bestow relief upon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redress', 'n.', 'The act of redressing; a making right; reformation;\n   correction; amendment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redress', 'n.', 'A setting right, as of wrong, injury, or opression; as,\n   the redress of grievances; hence, relief; remedy; reparation;\n   indemnification.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redress', 'n.', 'One who, or that which, gives relief; a redresser.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redressal', 'n.', 'Redress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redresser', 'n.', 'One who redresses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redressible', 'a.', 'Such as may be redressed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redressive', 'a.', 'Tending to redress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redressless', 'a.', 'Not having redress; such as can not be redressed;\n   irremediable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redressment', 'n.', 'The act of redressing; redress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Red-riband', 'n.', 'The European red band fish, or fireflame. See Rend\n   fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redroot', 'n.', 'A name of several plants having red roots, as the New\n   Jersey tea (see under Tea), the gromwell, the bloodroot, and the\n   Lachnanthes tinctoria, an endogenous plant found in sandy swamps from\n   Rhode Island to Florida.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redsear', 'v. i.', 'To be brittle when red-hot; to be red-short.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redshank', 'n.', 'A common Old World limicoline bird (Totanus calidris),\n   having the legs and feet pale red. The spotted redshank (T. fuscus) is\n   larger, and has orange-red legs. Called also redshanks, redleg, and\n   clee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redshank', 'n.', 'The fieldfare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redshank', 'n.', 'A bare-legged person; -- a contemptuous appellation\n   formerly given to the Scotch Highlanders, in allusion to their bare\n   legs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Red-short', 'a.', 'Hot-short; brittle when red-hot; -- said of certain\n   kinds of iron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redskin', 'n.', 'A common appellation for a North American Indian; -- so\n   called from the color of the skin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redstart', 'n.', 'A small, handsome European singing bird (Ruticilla\n   phoenicurus), allied to the nightingale; -- called also redtail,\n   brantail, fireflirt, firetail. The black redstart is P.tithys. The name\n   is also applied to several other species of Ruticilla amnd allied\n   genera, native of India.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redstart', 'n.', 'An American fly-catching warbler (Setophaga ruticilla).\n   The male is black, with large patches of orange-red on the sides,\n   wings, and tail. The female is olive, with yellow patches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redstreak', 'n.', 'A kind of apple having the skin streaked with red and\n   yellow, -- a favorite English cider apple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redstreak', 'n.', 'Cider pressed from redstreak apples.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redtail', 'n.', 'The red-tailed hawk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redtail', 'n.', 'The European redstart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Red-tailed', 'a.', 'Having a red tail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Red-tape', 'a.', 'Pertaining to, or characterized by, official formality.\n   See Red tape, under Red, a.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Red-tapism', 'n.', 'Strict adherence to official formalities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Red-tapist', 'n.', 'One who is tenacious of a strict adherence to official\n   formalities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redthroat', 'n.', 'A small Australian singing bird (Phyrrholaemus\n   brunneus). The upper parts are brown, the center of the throat red.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redtop', 'n.', 'A kind of grass (Agrostis vulgaris) highly valued in the\n   United States for pasturage and hay for cattle; -- called also English\n   grass, and in some localities herds grass. See Illustration in\n   Appendix. The tall redtop is Triodia seslerioides.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redub', 'v. t.', 'To refit; to repair, or make reparation for; hence, to\n   repay or requite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reduced', 'imp. & p. p.', 'of Reduce');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reducing', 'p. pr. & vb. n.', 'of Reduce');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reduce', 'n.', 'To bring or lead back to any former place or condition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reduce', 'n.', 'To bring to any inferior state, with respect to rank, size,\n   quantity, quality, value, etc.; to diminish; to lower; to degrade; to\n   impair; as, to reduce a sergeant to the ranks; to reduce a drawing; to\n   reduce expenses; to reduce the intensity of heat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reduce', 'n.', 'To bring to terms; to humble; to conquer; to subdue; to\n   capture; as, to reduce a province or a fort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reduce', 'n.', 'To bring to a certain state or condition by grinding,\n   pounding, kneading, rubbing, etc.; as, to reduce a substance to powder,\n   or to a pasty mass; to reduce fruit, wood, or paper rags, to pulp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reduce', 'n.', 'To bring into a certain order, arrangement, classification,\n   etc.; to bring under rules or within certain limits of descriptions and\n   terms adapted to use in computation; as, to reduce animals or\n   vegetables to a class or classes; to reduce a series of observations in\n   astronomy; to reduce language to rules.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reduce', 'n.', 'To change, as numbers, from one denomination into another\n   without altering their value, or from one denomination into others of\n   the same value; as, to reduce pounds, shillings, and pence to pence, or\n   to reduce pence to pounds; to reduce days and hours to minutes, or\n   minutes to days and hours.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reduce', 'n.', 'To change the form of a quantity or expression without\n   altering its value; as, to reduce fractions to their lowest terms, to a\n   common denominator, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reduce', 'n.', 'To bring to the metallic state by separating from\n   impurities; hence, in general, to remove oxygen from; to deoxidize; to\n   combine with, or to subject to the action of, hydrogen; as, ferric iron\n   is reduced to ferrous iron; or metals are reduced from their ores; --\n   opposed to oxidize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reduce', 'n.', 'To restore to its proper place or condition, as a displaced\n   organ or part; as, to reduce a dislocation, a fracture, or a hernia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reducement', 'n.', 'Reduction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reducent', 'a.', 'Tending to reduce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reducent', 'n.', 'A reducent agent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reducer', 'n.', 'One who, or that which, reduces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reducible', 'a.', 'Capable of being reduced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reducibleness', 'n.', 'Quality of being reducible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reducing', '', 'a & n. from Reduce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reduct', 'v. t..', 'To reduce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reductibility', 'n.', 'The quality of being reducible; reducibleness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reduction', 'n.', 'The act of reducing, or state of being reduced;\n   conversion to a given state or condition; diminution; conquest; as, the\n   reduction of a body to powder; the reduction of things to order; the\n   reduction of the expenses of government; the reduction of a rebellious\n   province.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reduction', 'n.', 'The act or process of reducing. See Reduce, v. t., 6.\n   and To reduce an equation, To reduce an expression, under Reduce, v. t.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reduction', 'v. t.', 'The correction of observations for known errors of\n   instruments, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reduction', 'v. t.', 'The preparation of the facts and measurements of\n   observations in order to deduce a general result.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reduction', 'v. t.', 'The process of making a copy of something, as a\n   figure, design, or draught, on a smaller scale, preserving the proper\n   proportions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reduction', 'v. t.', 'The bringing of a syllogism in one of the so-called\n   imperfect modes into a mode in the first figure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reduction', 'v. t.', 'The act, process, or result of reducing; as, the\n   reduction of iron from its ores; the reduction of aldehyde from\n   alcohol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reduction', 'v. t.', 'The operation of restoring a dislocated or fractured\n   part to its former place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reductive', 'a.', 'Tending to reduce; having the power or effect of\n   reducing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reductive', 'n.', 'A reductive agent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reductively', 'adv.', 'By reduction; by consequence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reduit', 'n.', 'A central or retired work within any other work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redundance', 'n.', 'Alt. of Redundancy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redundancy', 'n.', 'The quality or state of being redundant; superfluity;\n   superabundance; excess.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redundancy', 'n.', 'That which is redundant or in excess; anything\n   superfluous or superabundant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redundancy', 'n.', 'Surplusage inserted in a pleading which may be rejected\n   by the court without impairing the validity of what remains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redundant', 'a.', 'Exceeding what is natural or necessary; superabundant;\n   exuberant; as, a redundant quantity of bile or food.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redundant', 'a.', 'Using more worrds or images than are necessary or\n   useful; pleonastic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redundantly', 'adv.', 'In a refundant manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reduplicate', 'a.', 'Double; doubled; reduplicative; repeated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reduplicate', 'a.', 'Valvate with the margins curved outwardly; -- said of\n   the /stivation of certain flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reduplicate', 'v. t.', 'To redouble; to multiply; to repeat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reduplicate', 'v. t.', 'To repeat the first letter or letters of (a word).\n   See Reduplication, 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reduplication', 'n.', 'The act of doubling, or the state of being doubled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reduplication', 'n.', 'A figure in which the first word of a verse is the\n   same as the last word of the preceding verse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reduplication', 'n.', 'The doubling of a stem or syllable (more or less\n   modified), with the effect of changing the time expressed, intensifying\n   the meaning, or making the word more imitative; also, the syllable thus\n   added; as, L. tetuli; poposci.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reduplicative', 'a.', 'Double; formed by reduplication; reduplicate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reduvid', 'n.', 'Any hemipterous insect of the genus Redivius, or family\n   Reduvidae. They live by sucking the blood of other insects, and some\n   species also attack man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redweed', 'n.', 'The red poppy (Papaver Rhoeas).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redwing', 'n.', 'A European thrush (Turdus iliacus). Its under wing coverts\n   are orange red. Called also redwinged thrush. (b) A North American\n   passerine bird (Agelarius ph/niceus) of the family Icteridae. The male\n   is black, with a conspicuous patch of bright red, bordered with orange,\n   on each wing. Called also redwinged blackbird, red-winged troupial,\n   marsh blackbird, and swamp blackbird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redwithe', 'n.', 'A west Indian climbing shrub (Combretum Jacquini) with\n   slender reddish branchlets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redwood', 'n.', 'A gigantic coniferous tree (Sequoia sempervirens) of\n   California, and its light and durable reddish timber. See Sequoia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Redwood', 'n.', 'An East Indian dyewood, obtained from Pterocarpus\n   santalinus, Caesalpinia Sappan, and several other trees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ree', 'n.', 'See Rei.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ree', 'v. t.', 'To riddle; to sift; to separate or throw off.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reebok', 'n.', 'The peele.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reecho', 'v. t.', 'To echo back; to reverberate again; as, the hills reecho\n   the roar of cannon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reecho', 'v. i.', 'To give echoes; to return back, or be reverberated, as\n   an echo; to resound; to be resonant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reecho', 'n.', 'The echo of an echo; a repeated or second echo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reechy', 'a.', 'Smoky; reeky; hence, begrimed with dirt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reed', 'a.', 'Red.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reed', 'v. & n.', 'Same as Rede.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reed', 'n.', 'The fourth stomach of a ruminant; rennet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reed', 'n.', 'A name given to many tall and coarse grasses or grasslike\n   plants, and their slender, often jointed, stems, such as the various\n   kinds of bamboo, and especially the common reed of Europe and North\n   America (Phragmites communis).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reed', 'n.', 'A musical instrument made of the hollow joint of some plant;\n   a rustic or pastoral pipe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reed', 'n.', 'An arrow, as made of a reed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reed', 'n.', 'Straw prepared for thatching a roof.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reed', 'n.', 'A small piece of cane or wood attached to the mouthpiece of\n   certain instruments, and set in vibration by the breath. In the\n   clarinet it is a single fiat reed; in the oboe and bassoon it is\n   double, forming a compressed tube.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reed', 'n.', 'One of the thin pieces of metal, the vibration of which\n   produce the tones of a melodeon, accordeon, harmonium, or seraphine;\n   also attached to certain sets or registers of pipes in an organ.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reed', 'n.', 'A frame having parallel flat stripe of metal or reed, between\n   which the warp threads pass, set in the swinging lathe or batten of a\n   loom for beating up the weft; a sley. See Batten.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reed', 'n.', 'A tube containing the train of powder for igniting the charge\n   in blasting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reed', 'n.', 'Same as Reeding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reedbird', 'n.', 'The bobolink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reedbird', 'n.', 'One of several small Asiatic singing birds of the genera\n   Sch/nicola and Eurycercus; -- called also reed babbler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reedbuck', 'n.', 'See Rietboc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reeded', 'a.', 'Civered with reeds; reedy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reeded', 'a.', 'Formed with channels and ridges like reeds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reeden', 'a.', 'Consisting of a reed or reeds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reedification', 'n.', 'The act reedifying; the state of being reedified.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reedify', 'v. t.', 'To edify anew; to build again after destruction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reeding', 'n.', 'A small convex molding; a reed (see Illust. (i) of\n   Molding); one of several set close together to decorate a surface;\n   also, decoration by means of reedings; -- the reverse of fluting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reeding', 'n.', 'The nurling on the edge of a coin; -- commonly called\n   milling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reedless', 'a.', 'Destitute of reeds; as, reedless banks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reedling', 'n.', 'The European bearded titmouse (Panurus biarmicus); --\n   called also reed bunting, bearded pinnock, and lesser butcher bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reed-mace', 'n.', 'The cat-tail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reedwork', 'n.', 'A collective name for the reed stops of an organ.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reedy', 'a.', 'Abounding with reeds; covered with reeds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reedy', 'a.', 'Having the quality of reed in tone, that is, ///// and thin^\n   as some voices.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reef', 'n.', 'A chain or range of rocks lying at or near the surface of the\n   water. See Coral reefs, under Coral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reef', 'n.', 'A large vein of auriferous quartz; -- so called in Australia.\n   Hence, any body of rock yielding valuable ore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reef', 'v. t.', 'That part of a sail which is taken in or let out by means\n   of the reef points, in order to adapt the size of the sail to the force\n   of the wind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reefed', 'imp. & p. p.', 'of Reef');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reefing', 'p. pr. & vb. n.', 'of Reef');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reef', 'v. t.', 'To reduce the extent of (as a sail) by roiling or folding\n   a certain portion of it and making it fast to the yard or spar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reef-band', 'n.', 'A piece of canvas sewed across a sail to strengthen it\n   in the part where the eyelet holes for reefing are made.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reefer', 'n.', 'One who reefs; -- a name often given to midshipmen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reefer', 'n.', 'A close-fitting lacket or short coat of thick cloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reefing', 'n.', 'The process of taking in a reef.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reefy', 'a.', 'Full of reefs or rocks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reek', 'n.', 'A rick.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reek', 'n.', 'Vapor; steam; smoke; fume.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reeked', 'imp. & p. p.', 'of Reek');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reeking', 'p. pr. & vb. n.', 'of Reek');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reek', 'v. i.', 'To emit vapor, usually that which is warm and moist; to be\n   full of fumes; to steam; to smoke; to exhale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reeky', 'a.', 'Soiled with smoke or steam; smoky; foul.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reeky', 'a.', 'Emitting reek.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reel', 'n.', 'A lively dance of the Highlanders of Scotland; also, the\n   music to the dance; -- often called Scotch reel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reel', 'n.', 'A frame with radial arms, or a kind of spool, turning on an\n   axis, on which yarn, threads, lines, or the like, are wound; as, a log\n   reel, used by seamen; an anglers reel; a garden reel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reel', 'n.', 'A machine on which yarn is wound and measured into lays and\n   hanks, -- for cotton or linen it is fifty-four inches in circuit; for\n   worsted, thirty inches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reel', 'n.', 'A device consisting of radial arms with horizontal stats,\n   connected with a harvesting machine, for holding the stalks of grain in\n   position to be cut by the knives.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reeled', 'imp. & p. p.', 'of Reel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reeling', 'p. pr. & vb. n.', 'of Reel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reel', 'v. t.', 'To roll.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reel', 'v. t.', 'To wind upon a reel, as yarn or thread.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reel', 'v. i.', 'To incline, in walking, from one side to the other; to\n   stagger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reel', 'v. i.', 'To have a whirling sensation; to be giddy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reel', 'n.', 'The act or motion of reeling or staggering; as, a drunken\n   reel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reelect', 'v. t.', 'To elect again; as, to reelect the former governor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reelection', 'n.', 'Election a second time, or anew; as, the reelection of\n   a former chief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reeler', 'n.', 'One who reels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reeler', 'n.', 'The grasshopper warbler; -- so called from its note.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reeligible', 'a.', 'Eligible again; capable of reelection; as, reeligible\n   to the same office.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reem', 'n.', 'The Hebrew name of a horned wild animal, probably the Urus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reem', 'v. t.', 'To open (the seams of a vessels planking) for the purpose\n   of calking them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reembark', 'v. t. & i.', 'To put, or go, on board a vessel again; to embark\n   again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reembarkation', 'n.', 'A putting, or going, on board a vessel again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reembody', 'v. t.', 'To embody again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reembrace', 'v. i.', 'To embrace again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reemerge', 'v. i.', 'To emerge again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reemergence', 'n.', 'Act of reemerging.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reenact', 'v. t.', 'To enact again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reenaction', 'n.', 'The act of reenacting; the state of being reenacted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reenactment', 'n.', 'The enacting or passing of a law a second time; the\n   renewal of a law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reencourage', 'v. t.', 'To encourage again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reendow', 'v. t.', 'To endow again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reenforce', 'v. t.', 'To strengthen with new force, assistance, material,\n   or support; as, to reenforce an argument; to reenforce a garment;\n   especially, to strengthen with additional troops, as an army or a fort,\n   or with additional ships, as a fleet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reenforce', 'v.', 'Something which reenforces or strengthens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reenforce', 'v.', 'That part of a cannon near the breech which is thicker\n   than the rest of the piece, so as better to resist the force of the\n   exploding powder. See Illust. of Cannon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reenforce', 'v.', '(b)');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reenforce', 'v.', 'An additional thickness of canvas, cloth, or the like,\n   around an eyelet, buttonhole, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reenforcement', 'n.', 'The act of reenforcing, or the state of being\n   reenforced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reenforcement', 'n.', 'That which reenforces; additional force; especially,\n   additional troops or force to augment the strength of any army, or\n   ships to strengthen a navy or fleet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reengage', 'v. t. & i.', 'To engage a second time or again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reengagement', 'n.', 'A renewed or repeated engagement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reengrave', 'v. t.', 'To engrave anew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reenjoy', 'v. i.', 'To enjoy anew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reenjoyment', 'n.', 'Renewed enjoyment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reenkindle', 'v. t.', 'To enkindle again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reenlist', 'v. t. & i.', 'To enlist again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reenlistment', 'n.', 'A renewed enlistment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reenslave', 'v. t.', 'To enslave again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reenter', 'v. t.', 'To enter again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reenter', 'v. t.', 'To cut deeper, as engraved lines on a plate of metal,\n   when the engraving has not been deep enough, or the plate has become\n   worn in printing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reenter', 'v. i.', 'To enter anew or again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reentering', 'n.', 'The process of applying additional colors, by\n   applications of printing blocks, to patterns already partly colored.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reenthrone', 'v. t.', 'To enthrone again; to replace on a throne.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reenthronement', 'n.', 'A second enthroning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reentrance', 'n.', 'The act entereing again; re/ntry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reentrant', 'a.', 'Reentering; pointing or directed inwardds; as, a\n   re/ntrant angle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reentry', 'n.', 'A second or new entry; as, a reentry into public life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reentry', 'n.', 'A resuming or retaking possession of what one has lately\n   foregone; -- applied especially to land; the entry by a lessor upon the\n   premises leased, on failure of the tenant to pay rent or perform the\n   covenants in the lease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reerect', 'v. t.', 'To erect again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reermouse', 'n.', 'See Rearmouse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reestablish', 'v. t.', 'To establish anew; to fix or confirm again; to\n   restore; as, to reestablish a covenant; to reestablish health.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reestablisher', 'n.', 'One who establishes again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reestablishment', 'n.', 'The act reestablishing; the state of being\n   reestablished.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reestate', 'v. t.', 'To reestablish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reeve', 'n.', 'The female of the ruff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rove', 'imp. & p. p.', 'of Reeve');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reeving', 'p. pr. & vb. n.', 'of Reeve');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reeve', 'v. t.', 'To pass, as the end of a pope, through any hole in a\n   block, thimble, cleat, ringbolt, cringle, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reeve', 'n.', 'an officer, steward, bailiff, or governor; -- used chiefly\n   in compounds; as, shirereeve, now written sheriff; portreeve, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reexaminable', 'a.', 'Admitting of being reexamined or reconsidered.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reexamination', 'n.', 'A repeated examination. See under Examination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reexamine', 'v. t.', 'To examine anew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reexchange', 'v. t.', 'To exchange anew; to reverse (a previous exchange).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reexchange', 'n.', 'A renewed exchange; a reversal of an exchange.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reexchange', 'n.', 'The expense chargeable on a bill of exchange or draft\n   which has been dishonored in a foreign country, and returned to the\n   country in which it was made or indorsed, and then taken up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reexhibit', 'v. t.', 'To exhibit again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reexpel', 'v. t.', 'To expel again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reexperience', 'n.', 'A renewed or repeated experience.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reexport', 'v. t.', 'To export again, as what has been imported.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reexport', 'n.', 'Any commodity reexported; -- chiefly in the plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reexportation', 'n.', 'The act of reexporting, or of exporting an import.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reexpulsion', 'n.', 'Renewed or repeated expulsion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reezed', 'a.', 'Grown rank; rancid; rusty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refaction', 'n.', 'Recompense; atonement; retribution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refar', 'v. t.', 'To go over again; to repeat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refashion', 'v. t.', 'To fashion anew; to form or mold into shape a second\n   time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refashionment', 'n.', 'The act of refashioning, or the state of being\n   refashioned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refasten', 'v. t.', 'To fasten again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refect', 'v. t.', 'To restore after hunger or fatigue; to refresh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refection', 'n.', 'Refreshment after hunger or fatigue; a repast; a lunch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refective', 'a.', 'Refreshing; restoring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refective', 'n.', 'That which refreshes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refectories', 'pl. ', 'of Refectory');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refectory', 'n.', 'A room for refreshment; originally, a dining hall in\n   monasteries or convents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refel', 'v. t.', 'To refute; to disprove; as, to refel the tricks of a\n   sophister.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Referred', 'imp. & p. p.', 'of Refer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Referring', 'p. pr. & vb. n.', 'of Refer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refer', 'v. t.', 'To carry or send back.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refer', 'v. t.', 'Hence: To send or direct away; to send or direct\n   elsewhere, as for treatment, aid, information, decision, etc.; to make\n   over, or pass over, to another; as, to refer a student to an author; to\n   refer a beggar to an officer; to refer a bill to a committee; a court\n   refers a matter of fact to a commissioner for investigation, or refers\n   a question of law to a superior tribunal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refer', 'v. t.', 'To place in or under by a mental or rational process; to\n   assign to, as a class, a cause, source, a motive, reason, or ground of\n   explanation; as, he referred the phenomena to electrical disturbances.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refer', 'v. i.', 'To have recourse; to apply; to appeal; to betake ones\n   self; as, to refer to a dictionary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refer', 'v. i.', 'To have relation or reference; to relate; to point; as,\n   the figure refers to a footnote.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refer', 'v. i.', 'To carry the mind or thought; to direct attention; as,\n   the preacher referred to the late election.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refer', 'v. i.', 'To direct inquiry for information or a guarantee of any\n   kind, as in respect to ones integrity, capacity, pecuniary ability,\n   and the like; as, I referred to his employer for the truth of his\n   story.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Referable', 'a.', 'Capable of being referred, or considered in relation to\n   something else; assignable; ascribable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Referee', 'n.', 'One to whom a thing is referred; a person to whom a matter\n   in dispute has been referred, in order that he may settle it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reference', 'n.', 'The act of referring, or the state of being referred;\n   as, reference to a chart for guidance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reference', 'n.', 'That which refers to something; a specific direction of\n   the attention; as, a reference in a text-book.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reference', 'n.', 'Relation; regard; respect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reference', 'n.', 'One who, or that which, is referred to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reference', 'n.', 'One of whom inquires can be made as to the integrity,\n   capacity, and the like, of another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reference', 'n.', 'A work, or a passage in a work, to which one is\n   referred.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reference', 'n.', 'The act of submitting a matter in dispute to the\n   judgment of one or more persons for decision.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reference', 'n.', 'The process of sending any matter, for inquiry in a\n   cause, to a master or other officer, in order that he may ascertain\n   facts and report to the court.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reference', 'n.', 'Appeal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Referendary', 'n.', 'One to whose decision a cause is referred; a referee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Referendary', 'n.', 'An officer who delivered the royal answer to\n   petitions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Referendary', 'n.', 'Formerly, an officer of state charged with the duty of\n   procuring and dispatching diplomas and decrees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Referendum', 'n.', 'A diplomatic agents note asking for instructions from\n   his government concerning a particular matter or point.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Referendum', 'n.', 'The right to approve or reject by popular vote a\n   meassure passed upon by a legislature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Referential', 'a.', 'Containing a reference; pointing to something out of\n   itself; as, notes for referential use.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Referment', 'n.', 'The act of referring; reference.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Re-ferment', 'v. t. & i.', 'To ferment, or cause to ferment, again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Referrer', 'n.', 'One who refers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Referrible', 'a.', 'Referable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refigure', 'v. t.', 'To figure again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refill', 'v. t. & i.', 'To fill, or become full, again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refind', 'v. t.', 'To find again; to get or experience again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refined', 'imp. & p. p.', 'of Refine');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refining', 'p. pr. & vb. n.', 'of Refine');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refine', 'v. t.', 'To reduce to a fine, unmixed, or pure state; to free\n   from impurities; to free from dross or alloy; to separate from\n   extraneous matter; to purify; to defecate; as, to refine gold or\n   silver; to refine iron; to refine wine or sugar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refine', 'v. t.', 'To purify from what is gross, coarse, vulgar, inelegant,\n   low, and the like; to make elegant or exellent; to polish; as, to\n   refine the manners, the language, the style, the taste, the intellect,\n   or the moral feelings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refine', 'v. i.', 'To become pure; to be cleared of feculent matter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refine', 'v. i.', 'To improve in accuracy, delicacy, or excellence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refine', 'v. i.', 'To affect nicety or subtilty in thought or language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refined', 'a.', 'Freed from impurities or alloy; purifed; polished;\n   cultured; delicate; as; refined gold; refined language; refined\n   sentiments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refinement', 'n.', 'The act of refining, or the state of being refined; as,\n   the refinement or metals; refinement of ideas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refinement', 'n.', 'That which is refined, elaborated, or polished to\n   excess; an affected subtilty; as, refinements of logic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refiner', 'n.', 'One who, or that which, refines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refineries', 'pl. ', 'of Refinery');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refinery', 'n.', 'The building and apparatus for refining or purifying,\n   esp. metals and sugar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refinery', 'n.', 'A furnace in which cast iron is refined by the action of\n   a blast on the molten metal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refit', 'v. t.', 'To fit or prepare for use again; to repair; to restore\n   after damage or decay; as, to refit a garment; to refit ships of war.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refit', 'v. t.', 'To fit out or supply a second time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refit', 'v. i.', 'To obtain repairs or supplies; as, the fleet returned to\n   refit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refitment', 'n.', 'The act of refitting, or the state of being refitted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refix', 'v. t.', 'To fix again or anew; to establish anew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reflame', 'v. i.', 'To kindle again into flame.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reflected', 'imp. & p. p.', 'of Reflect');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reflecting', 'p. pr. & vb. n.', 'of Reflect');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reflect', 'v.', 'To bend back; to give a backwa/d turn to; to throw back;\n   especially, to cause to return after striking upon any surface; as, a\n   mirror reflects rays of light; polished metals reflect heat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reflect', 'v.', 'To give back an image or likeness of; to mirror.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reflect', 'v. i.', 'To throw back light, heat, or the like; to return rays\n   or beams.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reflect', 'v. i.', 'To be sent back; to rebound as from a surface; to\n   revert; to return.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reflect', 'v. i.', 'To throw or turn back the thoughts upon anything; to\n   contemplate. Specifically: To attend earnestly to what passes within\n   the mind; to attend to the facts or phenomena of consciousness; to use\n   attention or earnest thought; to meditate; especially, to think in\n   relation to moral truth or rules.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reflect', 'v. i.', 'To cast reproach; to cause censure or dishonor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reflected', 'a.', 'Thrown back after striking a surface; as, reflected\n   light, heat, sound, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reflected', 'a.', 'Hence: Not ones own; received from another; as, his\n   glory was reflected glory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reflected', 'a.', 'Bent backward or outward; reflexed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reflectent', 'a.', 'Bending or flying back; reflected.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reflectent', 'a.', 'Reflecting; as, a reflectent body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reflectible', 'a.', 'Capable of being reflected, or thrown back;\n   reflexible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reflecting', 'a.', 'Throwing back light, heat, etc., as a mirror or other\n   surface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reflecting', 'a.', 'Given to reflection or serious consideration;\n   reflective; contemplative; as, a reflecting mind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reflectingly', 'adv.', 'With reflection; also, with censure; reproachfully.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reflection', 'n.', 'The act of reflecting, or turning or sending back, or\n   the state of being reflected.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reflection', 'n.', 'The return of rays, beams, sound, or the like, from a\n   surface. See Angle of reflection, below.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reflection', 'n.', 'The reverting of the mind to that which has already\n   occupied it; continued consideration; meditation; contemplation; hence,\n   also, that operation or power of the mind by which it is conscious of\n   its own acts or states; the capacity for judging rationally, especially\n   in view of a moral rule or standard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reflection', 'n.', 'Shining; brightness, as of the sun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reflection', 'n.', 'That which is produced by reflection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reflection', 'n.', 'An image given back from a reflecting surface; a\n   reflected counterpart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reflection', 'n.', 'A part reflected, or turned back, at an angle; as, the\n   reflection of a membrane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reflection', 'n.', 'Result of meditation; thought or opinion after\n   attentive consideration or contemplation; especially, thoughts\n   suggested by truth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reflection', 'n.', 'Censure; reproach cast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reflection', 'n.', 'The transference of an excitement from one nerve fiber\n   to another by means of the nerve cells, as in reflex action. See Reflex\n   action, under Reflex.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reflective', 'a.', 'Throwing back images; as, a reflective mirror.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reflective', 'a.', 'Capable of exercising thought or judgment; as,\n   reflective reason.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reflective', 'a.', 'Addicted to introspective or meditative habits; as, a\n   reflective person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reflective', 'a.', 'Reflexive; reciprocal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reflector', 'n.', 'One who, or that which, reflects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reflector', 'n.', 'Something having a polished surface for reflecting light\n   or heat, as a mirror, a speculum, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reflector', 'n.', 'A reflecting telescope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reflector', 'n.', 'A device for reflecting sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reflex', 'a.', 'Directed back; attended by reflection; retroactive;\n   introspective.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reflex', 'a.', 'Produced in reaction, in resistance, or in return.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reflex', 'a.', 'Of, pertaining to, or produced by, stimulus or excitation\n   without the necessary intervention of consciousness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reflex', 'n.', 'Reflection; the light reflected from an illuminated surface\n   to one in shade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reflex', 'n.', 'An involuntary movement produced by reflex action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reflex', 'v. t.', 'To reflect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reflex', 'v. t.', 'To bend back; to turn back.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reflexed', 'a.', 'Bent backward or outward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reflexibility', 'n.', 'The quality or capability of being reflexible; as,\n   the reflexibility of the rays of light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reflexible', 'a.', 'Capable of being reflected, or thrown back.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reflexion', 'n.', 'See Reflection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reflexity', 'n.', 'The state or condition of being reflected.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reflexive', 'a.', 'Bending or turned backward; reflective; having respect\n   to something past.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reflexive', 'a.', 'Implying censure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reflexive', 'a.', 'Having for its direct object a pronoun which refers to\n   the agent or subject as its antecedent; -- said of certain verbs; as,\n   the witness perjured himself; I bethought myself. Applied also to\n   pronouns of this class; reciprocal; reflective.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reflexly', 'adv.', 'In a reflex manner; reflectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refloat', 'n.', 'Reflux; ebb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reflorescence', 'n.', 'A blossoming anew of a plant after it has apparently\n   ceased blossoming for the season.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reflourish', 'v. t. & i.', 'To flourish again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reflow', 'v. i.', 'To flow back; to ebb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reflower', 'v. i. & t.', 'To flower, or cause to flower, again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refluctuation', 'n.', 'A flowing back; refluence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refluence', 'n.', 'Alt. of Refluency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refluency', 'n.', 'The quality of being refluent; a flowing back.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refluent', 'a.', 'Flowing back; returning; ebbing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reflueus', 'a.', 'Refluent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reflux', 'a.', 'Returning, or flowing back; reflex; as, reflux action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reflux', 'n.', 'A flowing back, as the return of a fluid; ebb; reaction;\n   as, the flux and reflux of the tides.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refocillate', 'v. t.', 'To refresh; to revive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refocillation', 'n.', 'Restoration of strength by refreshment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refold', 'v. t.', 'To fold again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refoment', 'v. t.', 'To foment anew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reforestization', 'n.', 'The act or process of reforestizing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reforestize', 'v. t.', 'To convert again into a forest; to plant again with\n   trees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reforge', 'v. t.', 'To forge again or anew; hence, to fashion or fabricate\n   anew; to make over.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reforger', 'n.', 'One who reforges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reform', 'v. t.', 'To put into a new and improved form or condition; to\n   restore to a former good state, or bring from bad to good; to change\n   from worse to better; to amend; to correct; as, to reform a profligate\n   man; to reform corrupt manners or morals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reform', 'v. i.', 'To return to a good state; to amend or correct ones own\n   character or habits; as, a man of settled habits of vice will seldom\n   reform.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reform', 'n.', 'Amendment of what is defective, vicious, corrupt, or\n   depraved; reformation; as, reform of elections; reform of government.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Re-formed', 'imp. & p. p.', 'of Re-form');
  INSERT INTO words (word, word_type, description) VALUES  (    'Re-forming', 'p. pr. & vb. n.', 'of Re-form');
  INSERT INTO words (word, word_type, description) VALUES  (    'Re-form', 'v. t. & i.', 'To give a new form to; to form anew; to take form\n   again, or to take a new form; as, to re-form the line after a charge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reformable', 'a.', 'Capable of being reformed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reformade', 'n.', 'A reformado.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reformado', 'v. t.', 'A monk of a reformed order.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reformado', 'v. t.', 'An officer who, in disgrace, is deprived of his\n   command, but retains his rank, and sometimes his pay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reformalize', 'v. i.', 'To affect reformation; to pretend to correctness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reformation', 'n.', 'The act of reforming, or the state of being reformed;\n   change from worse to better; correction or amendment of life, manners,\n   or of anything vicious or corrupt; as, the reformation of manners;\n   reformation of the age; reformation of abuses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reformation', 'n.', 'Specifically (Eccl. Hist.), the important religious\n   movement commenced by Luther early in the sixteenth century, which\n   resulted in the formation of the various Protestant churches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Re-formation', 'n.', 'The act of forming anew; a second forming in order;\n   as, the reformation of a column of troops into a hollow square.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reformative', 'a.', 'Forming again; having the quality of renewing form;\n   reformatory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reformatory', 'a.', 'Tending to produce reformation; reformative.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-ries', 'pl. ', 'of Reformatory');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reformatory', 'n.', 'An institution for promoting the reformation of\n   offenders.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reformed', 'a.', 'Corrected; amended; restored to purity or excellence;\n   said, specifically, of the whole body of Protestant churches\n   originating in the Reformation. Also, in a more restricted sense, of\n   those who separated from Luther on the doctrine of consubstantiation,\n   etc., and carried the Reformation, as they claimed, to a higher point.\n   The Protestant churches founded by them in Switzerland, France,\n   Holland, and part of Germany, were called the Reformed churches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reformed', 'a.', 'Amended in character and life; as, a reformed gambler or\n   drunkard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reformed', 'a.', 'Retained in service on half or full pay after the\n   disbandment of the company or troop; -- said of an officer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reformer', 'n.', 'One who effects a reformation or amendment; one who\n   labors for, or urges, reform; as, a reformer of manners, or of abuses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reformer', 'n.', 'One of those who commenced the reformation of religion in\n   the sixteenth century, as Luther, Melanchthon, Zwingli, and Calvin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reformist', 'n.', 'A reformer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reformly', 'adv.', 'In the manner of a reform; for the purpose of reform.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refortification', 'n.', 'A fortifying anew, or a second time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refortify', 'v. t.', 'To fortify anew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refossion', 'n.', 'The act of digging up again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refound', 'v. t.', 'To found or cast anew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refound', 'v. t.', 'To found or establish again; to re/stablish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refound', '', 'imp. & p. p. of Refind, v. t.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refounder', 'n.', 'One who refounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refracted', 'imp. & p. p.', 'of Refract');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refracting', 'p. pr. & vb. n.', 'of Refract');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refract', 'n.', 'To bend sharply and abruptly back; to break off.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refract', 'n.', 'To break the natural course of, as rays of light orr heat,\n   when passing from one transparent medium to another of different\n   density; to cause to deviate from a direct course by an action distinct\n   from reflection; as, a dense medium refrcts the rays of light as they\n   pass into it from a rare medium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refractable', 'a.', 'Capable of being refracted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refracted', 'a.', 'Bent backward angularly, as if half-broken; as, a\n   refracted stem or leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refracted', 'a.', 'Turned from a direct course by refraction; as, refracted\n   rays of light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refracting', 'a.', 'Serving or tending to refract; as, a refracting medium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refraction', 'n.', 'The act of refracting, or the state of being refracted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refraction', 'n.', 'The change in the direction of ray of light, heat, or\n   the like, when it enters obliquely a medium of a different density from\n   that through which it has previously moved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refraction', 'n.', 'The change in the direction of a ray of light, and,\n   consequently, in the apparent position of a heavenly body from which it\n   emanates, arising from its passage through the earths atmosphere; --\n   hence distinguished as atmospheric refraction, or astronomical\n   refraction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refraction', 'n.', 'The correction which is to be deducted from the\n   apparent altitude of a heavenly body on account of atmospheric\n   refraction, in order to obtain the true altitude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refractive', 'a.', 'Serving or having power to refract, or turn from a\n   direct course; pertaining to refraction; as, refractive surfaces;\n   refractive powers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refractiveness', 'n.', 'The quality or condition of being refractive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refractometer', 'n.', 'A contrivance for exhibiting and measuring the\n   refraction of light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refractor', 'n.', 'Anything that refracts');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refractor', 'n.', 'A refracting telescope, in which the image to be viewed\n   is formed by the refraction of light in passing through a convex lens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refractorily', 'adv.', 'In a refractory manner; perversely; obstinately.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refractoriness', 'n.', 'The quality or condition of being refractory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refractory', 'a.', 'Obstinate in disobedience; contumacious; stubborn;\n   unmanageable; as, a refractory child; a refractory beast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refractory', 'a.', 'Resisting ordinary treatment; difficult of fusion,\n   reduction, or the like; -- said especially of metals and the like,\n   which do not readily yield to heat, or to the hammer; as, a refractory\n   ore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refractory', 'n.', 'A refractory person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refractory', 'n.', 'Refractoriness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refractory', 'n.', 'OPottery) A piece of ware covered with a vaporable flux\n   and placed in a kiln, to communicate a glaze to the other articles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refracture', 'n.', 'A second breaking (as of a badly set bone) by the\n   surgeon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refracture', 'v. t.', 'To break again, as a bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refragable', 'a.', 'Capable of being refuted; refutable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refragate', 'v. i.', 'To oppose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refrained', 'imp. & p. p.', 'of Refrain');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refraining', 'p. pr. & vb/ n.', 'of Refrain');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refrain', 'v. t.', 'To hold back; to restrain; to keep within prescribed\n   bounds; to curb; to govern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refrain', 'v. t.', 'To abstain from');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refrain', 'v. i.', 'To keep ones self from action or interference; to hold\n   aloof; to forbear; to abstain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refrain', 'v.', 'The burden of a song; a phrase or verse which recurs at\n   the end of each of the separate stanzas or divisions of a poetic\n   composition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refrainer', 'n.', 'One who refrains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refrainment', 'n.', 'Act of refraining.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reframe', 'v. t.', 'To frame again or anew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refrangibility', 'n.', 'The quality of being refrangible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refrangible', 'a.', 'Capable of being refracted, or turned out of a direct\n   course, in passing from one medium to another, as rays of light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refrenation', 'v. t.', 'The act of refraining.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refreshed', 'imp. & p. p.', 'of Refresh');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refreshing', 'p. pr. & vb. n.', 'of Refresh');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refresh', 'a.', 'To make fresh again; to restore strength, spirit,\n   animation, or the like, to; to relieve from fatigue or depression; to\n   reinvigorate; to enliven anew; to reanimate; as, sleep refreshes the\n   body and the mind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refresh', 'a.', 'To make as if new; to repair; to restore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refresh', 'n.', 'The act of refreshing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refresher', 'n.', 'One who, or that which, refreshes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refresher', 'n.', 'An extra fee paid to counsel in a case that has been\n   adjourned from one term to another, or that is unusually protracted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refreshful', 'a.', 'Full of power to refresh; refreshing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refreshing', 'a.', 'Reviving; reanimating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refreshment', 'n.', 'The act of refreshing, or the state of being\n   refreshed; restoration of strength, spirit, vigor, or liveliness;\n   relief after suffering; new life or animation after depression.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refreshment', 'n.', 'That which refreshes; means of restoration or\n   reanimation; especially, an article of food or drink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refret', 'n.', 'Refrain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refreyd', 'v. t.', 'To chill; to cool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refrication', 'n.', 'A rubbing up afresh; a brightening.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refrigerant', 'a.', 'Cooling; allaying heat or fever.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refrigerant', 'n.', 'That which makes to be cool or cold; specifically, a\n   medicine or an application for allaying fever, or the symptoms of\n   fever; -- used also figuratively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refrigerated', 'imp. & p. p.', 'of Refrigerate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refrigerating', 'p. pr. & vb. n.', 'of Refrigerate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refrigerate', 'v. t.', 'To cause to become cool; to make or keep cold or\n   cool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refrigeration', 'n.', 'The act or process of refrigerating or cooling, or\n   the state of being cooled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refrigerative', 'a.', 'Cooling; allaying heat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refrigerative', 'n.', 'A refrigerant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refrigerator', 'n.', 'That which refrigerates or makes cold; that which\n   keeps cool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refrigerator', 'n.', 'A box or room for keeping food or other articles\n   cool, usually by means of ice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refrigerator', 'n.', 'An apparatus for rapidly cooling heated liquids or\n   vapors, connected with a still, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refrigeratory', 'a.', 'Mitigating heat; cooling.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-ries', 'pl. ', 'of Refrigeratory');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refrigeratory', 'n.', 'That which refrigerates or cools.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refrigeratory', 'n.', 'In distillation, a vessel filled with cold water,\n   surrounding the worm, the vapor in which is thereby condensed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refrigeratory', 'n.', 'The chamber, or tank, in which ice is formed, in an\n   ice machine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refrigerium', 'n.', 'Cooling refreshment; refrigeration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refringency', 'n.', 'The power possessed by a substance to refract a ray;\n   as, different substances have different refringencies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refringent', 'a.', 'Pertaining to, or possessing, refringency; refractive;\n   refracting; as, a refringent prism of spar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reft', 'imp. & p. p.', 'Bereft.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reft', 'n.', 'A chink; a rift. See Rift.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refuge', 'n.', 'Shelter or protection from danger or distress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refuge', 'n.', 'That which shelters or protects from danger, or from\n   distress or calamity; a stronghold which protects by its strength, or a\n   sanctuary which secures safety by its sacredness; a place inaccessible\n   to an enemy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refuge', 'n.', 'An expedient to secure protection or defense; a device or\n   contrivance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refuge', 'v. t.', 'To shelter; to protect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refugee', 'n.', 'One who flees to a shelter, or place of safety.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refugee', 'n.', 'Especially, one who, in times of persecution or political\n   commotion, flees to a foreign power or country for safety; as, the\n   French refugees who left France after the revocation of the edict of\n   Nantes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refulgence', 'n.', 'Alt. of Refulgency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refulgency', 'n.', 'The quality of being refulgent; brilliancy; splender;\n   radiance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refulgent', 'a.', 'Casting a bright light; radiant; brilliant; resplendent;\n   shining; splendid; as, refulgent beams.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refund', 'v. t.', 'To fund again or anew; to replace (a fund or loan) by a\n   new fund; as, to refund a railroad loan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refund', 'v. t.', 'To pour back.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refund', 'v. t.', 'To give back; to repay; to restore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refund', 'v. t.', 'To supply again with funds; to reimburse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refunder', 'n.', 'One who refunds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refundment', 'n.', 'The act of refunding; also, that which is refunded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refurbish', 'v. t.', 'To furbish anew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refurnish', 'v. t.', 'To furnish again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refurnishment', 'n.', 'The act of refurnishing, or state of being\n   refurnished.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refusable', 'a.', 'Capable of being refused; admitting of refusal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refusal', 'n.', 'The act of refusing; denial of anything demanded,\n   solicited, or offered for acceptance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refusal', 'n.', 'The right of taking in preference to others; the choice of\n   taking or refusing; option; as, to give one the refusal of a farm; to\n   have the refusal of an employment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refused', 'imp. & p. p.', 'of Refuse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refusing', 'p. pr. & vb. n.', 'of Refuse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refuse', 'v. t.', 'To deny, as a request, demand, invitation, or command;\n   to decline to do or grant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refuse', 'v. t.', 'To throw back, or cause to keep back (as the center, a\n   wing, or a flank), out of the regular aligment when troops ar/ about to\n   engage the enemy; as, to refuse the right wing while the left wing\n   attacks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refuse', 'v. t.', 'To decline to accept; to reject; to deny the request or\n   petition of; as, to refuse a suitor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refuse', 'v. t.', 'To disown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refuse', 'v. i.', 'To deny compliance; not to comply.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refuse', 'n.', 'Refusal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refuse', 'n.', 'That which is refused or rejected as useless; waste or\n   worthless matter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refuse', 'a.', 'Refused; rejected; hence; left as unworthy of acceptance;\n   of no value; worthless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refuser', 'n.', 'One who refuses or rejects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refusion', 'n.', 'New or repeated melting, as of metals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refusion', 'n.', 'Restoration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refut', 'n.', 'Refuge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refutability', 'n.', 'The quality of being refutable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refutable', 'a.', 'Admitting of being refuted or disproved; capable of\n   being proved false or erroneous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refutal', 'n.', 'Act of refuting; refutation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refutation', 'n.', 'The act or process of refuting or disproving, or the\n   state of being refuted; proof of falsehood or error; the overthrowing\n   of an argument, opinion, testimony, doctrine, or theory, by argument or\n   countervailing proof.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refutatory', 'a.', 'Tending tu refute; refuting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refuted', 'imp. & p. p.', 'of Refute');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refuting', 'p. pr. & vb. n.', 'of Refute');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refute', 'v. t.', 'To disprove and overthrow by argument, evidence, or\n   countervailing proof; to prove to be false or erroneous; to confute;\n   as, to refute arguments; to refute testimony; to refute opinions or\n   theories; to refute a disputant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Refuter', 'n.', 'One who, or that which, refutes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regain', 'v. t.', 'To gain anew; to get again; to recover, as what has\n   escaped or been lost; to reach again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regal', 'a.', 'Of or pertaining to a king; kingly; royal; as, regal\n   authority, pomp, or sway.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regal', 'n.', 'A small portable organ, played with one hand, the bellows\n   being worked with the other, -- used in the sixteenth and seventeenth\n   centuries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regale', 'n.', 'A prerogative of royalty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regaled', 'imp. & p. p.', 'of Regale');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regaling', 'p. pr. & vb. n.', 'of Regale');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regale', 'v. t.', 'To enerta/n in a regal or sumptuous manner; to\n   enrtertain with something that delights; to gratify; to refresh; as, to\n   regale the taste, the eye, or the ear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regale', 'v. i.', 'To feast; t/ fare sumtuously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regale', 'v. t.', 'A sumptuous repast; a banquet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regalement', 'n.', 'The act of regaling; anything which regales;\n   refreshment; entertainment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regaler', 'n.', 'One who regales.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regalia', 'n. pl.', 'That which belongs to royalty. Specifically: (a) The\n   rights and prerogatives of a king. (b) Royal estates and revenues. (c)\n   Ensings, symbols, or paraphernalia of royalty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regalia', 'n. pl.', 'Hence, decorations or insignia of an office or order,\n   as of Freemasons, Odd Fellows,etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regalia', 'n. pl.', 'Sumptuous food; delicacies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regalia', 'n.', 'A kind of cigar of large size and superior quality; also,\n   the size in which such cigars are classed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regalian', 'a.', 'Pertaining to regalia; pertaining to the royal insignia\n   or prerogatives.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regalism', 'n.', 'The doctrine of royal prerogative or supremacy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regality', 'n.', 'Royalty; sovereignty; sovereign jurisdiction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regality', 'n.', 'An ensign or badge of royalty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regally', 'adv.', 'In a regal or royal manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regarded', 'imp. & p. p.', 'of Regard');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regarding', 'p. pr. & vb. n.', 'of Regard');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regard', 'v. t.', 'To keep in view; to behold; to look at; to view; to gaze\n   upon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regard', 'v. t.', 'Hence, to look or front toward; to face.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regard', 'v. t.', 'To look closely at; to observe attentively; to pay\n   attention to; to notice or remark particularly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regard', 'v. t.', 'To look upon, as in a certain relation; to hold as an\n   popinion; to consider; as, to regard abstinence from wine as a duty; to\n   regard another as a friend or enemy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regard', 'v. t.', 'To consider and treat; to have a certain feeling toward;\n   as, to regard one with favor or dislike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regard', 'v. t.', 'To pay respect to; to treat as something of peculiar\n   value, sanctity, or the like; to care for; to esteem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regard', 'v. t.', 'To take into consideration; to take account of, as a\n   fact or condition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regard', 'v. t.', 'To have relation to, as bearing upon; to respect; to\n   relate to; to touch; as, an argument does not regard the question; --\n   often used impersonally; as, I agree with you as regards this or that.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regard', 'v. i.', 'To look attentively; to consider; to notice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regard', 'v. t.', 'A look; aspect directed to another; view; gaze.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regard', 'v. t.', 'Attention of the mind with a feeling of interest;\n   observation; heed; notice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regard', 'v. t.', 'That view of the mind which springs from perception of\n   value, estimable qualities, or anything that excites admiration;\n   respect; esteem; reverence; affection; as, to have a high regard for a\n   person; -- often in the plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regard', 'v. t.', 'State of being regarded, whether favorably or otherwise;\n   estimation; repute; note; account.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regard', 'v. t.', 'Consideration; thought; reflection; heed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regard', 'v. t.', 'Matter for consideration; account; condition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regard', 'v. t.', 'Respect; relation; reference.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regard', 'v. t.', 'Object of sight; scene; view; aspect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regard', 'v. t.', 'Supervision; inspection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regardable', 'a.', 'Worthy of regard or notice; to be regarded; observable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regardant', 'v. t.', 'Looking behind; looking backward watchfully.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regardant', 'v. t.', 'Looking behind or backward; as, a lion regardant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regardant', 'v. t.', 'Annexed to the land or manor; as, a villain\n   regardant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regarder', 'n.', 'One who regards.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regarder', 'n.', 'An officer appointed to supervise the forest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regardful', 'a.', 'Heedful; attentive; observant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regarding', 'prep.', 'Concerning; respecting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regardless', 'a.', 'Having no regard; heedless; careless; as, regardless of\n   life, consequences, dignity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regardless', 'a.', 'Not regarded; slighted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regather', 'v. t.', 'To gather again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regattas', 'pl. ', 'of Regatta');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regatta', 'n.', 'Originally, a gondola race in Venice; now, a rowing or\n   sailing race, or a series of such races.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regel', 'n.', 'See Rigel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regelate', 'v. i.', 'To freeze together again; to undergo regelation, as\n   ice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regelation', 'n.', 'The act or process of freezing anew, or together,as two\n   pieces of ice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regence', 'n.', 'Rule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regencies', 'pl. ', 'of Regency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regency', 'a.', 'The office of ruler; rule; authority; government.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regency', 'a.', 'Especially, the office, jurisdiction, or dominion of a\n   regent or vicarious ruler, or of a body of regents; deputed or\n   vicarious government.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regency', 'a.', 'A body of men intrusted with vicarious government; as, a\n   regency constituted during a kings minority, absence from the kingdom,\n   or other disability.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regeneracy', 'n.', 'The state of being regenerated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regenerate', 'a.', 'Reproduced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regenerate', 'a.', 'Born anew; become Christian; renovated in heart;\n   changed from a natural to a spiritual state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regenerate', 'v. t.', 'To generate or produce anew; to reproduce; to give\n   new life, strength, or vigor to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regenerate', 'v. t.', 'To cause to be spiritually born anew; to cause to\n   become a Christian; to convert from sin to holiness; to implant holy\n   affections in the heart of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regenerate', 'v. t.', 'Hence, to make a radical change for the better in\n   the character or condition of; as, to regenerate society.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regenerateness', 'n.', 'The quality or state of being rgenerate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regeneration', 'n.', 'The act of regenerating, or the state of being\n   regenerated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regeneration', 'n.', 'The entering into a new spiritual life; the act of\n   becoming, or of being made, Christian; that change by which holy\n   affectations and purposes are substituted for the opposite motives in\n   the heart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regeneration', 'n.', 'The reproduction of a part which has been removed or\n   destroyed; re-formation; -- a process especially characteristic of a\n   many of the lower animals; as, the regeneration of lost feelers, limbs,\n   and claws by spiders and crabs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regeneration', 'n.', 'The reproduction or renewal of tissues, cells, etc.,\n   which have been used up and destroyed by the ordinary processes of\n   life; as, the continual regeneration of the epithelial cells of the\n   body, or the regeneration of the contractile substance of muscle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regeneration', 'n.', 'The union of parts which have been severed, so that\n   they become anatomically perfect; as, the regeneration of a nerve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regenerative', 'a.', 'Of or pertaining to regeneration; tending to\n   regenerate; as, regenerative influences.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regeneratively', 'adv.', 'So as to regenerate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regenerator', 'n.', 'One who, or that which, regenerates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regenerator', 'n.', 'A device used in connection with hot-air engines,\n   gas-burning furnaces, etc., in which the incoming air or gas is heated\n   by being brought into contact with masses of iron, brick, etc., which\n   have been previously heated by the outgoing, or escaping, hot air or\n   gas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regeneratory', 'a.', 'Having power to renew; tending to reproduce;\n   regenerating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regenesis', 'n.', 'New birth; renewal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regent', 'a.', 'Ruling; governing; regnant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regent', 'a.', 'Exercising vicarious authority.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regent', 'a.', 'One who rules or reigns; a governor; a ruler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regent', 'a.', 'Especially, one invested with vicarious authority; one who\n   governs a kingdom in the minority, absence, or disability of the\n   sovereign.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regent', 'a.', 'One of a governing board; a trustee or overseer; a\n   superintendent; a curator; as, the regents of the Smithsonian\n   Institution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regent', 'a.', 'A resident master of arts of less than five years\n   standing, or a doctor of less than twwo. They were formerly privileged\n   to lecture in the schools.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regentess', 'n.', 'A female regent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regentship', 'n.', 'The office of a regent; regency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regerminate', 'v. i.', 'To germinate again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regermination', 'n.', 'A germinating again or anew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regest', 'n.', 'A register.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reget', 'v. t.', 'To get again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regian', 'n.', 'An upholder of kingly authority; a royalist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regible', 'a.', 'Governable; tractable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regicidal', 'a.', 'Pertaining to regicide, or to one committing it; having\n   the nature of, or resembling, regicide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regicide', 'n.', 'One who kills or who murders a king; specifically\n   (Eng.Hist.), one of the judges who condemned Charles I. to death.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regicide', 'n.', 'The killing or the murder of a king.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regild', 'v. t.', 'To gild anew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regime', 'n.', 'Mode or system of rule or management; character of\n   government, or of the prevailing social system.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regime', 'n.', 'The condition of a river with respect to the rate of its\n   flow, as measured by the volume of water passing different cross\n   sections in a given time, uniform regime being the condition when the\n   flow is equal and uniform at all the cross sections.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regimen', 'n.', 'Orderly government; system of order; adminisration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regimen', 'n.', 'Any regulation or remedy which is intended to produce\n   beneficial effects by gradual operation');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regimen', 'n.', 'a systematic course of diet, etc., pursed with a view to\n   improving or preserving the health, or for the purpose of attaining\n   some particular effect, as a reduction of flesh; -- sometimes used\n   synonymously with hygiene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regimen', 'n.', 'A syntactical relation between words, as when one depends\n   on another and is regulated by it in respect to case or mood;\n   government.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regimen', 'n.', 'The word or words governed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regiment', 'n.', 'Government; mode of ruling; rule; authority; regimen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regiment', 'n.', 'A region or district governed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regiment', 'n.', 'A body of men, either horse, foot, or artillery,\n   commanded by a colonel, and consisting of a number of companies,\n   usually ten.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regimented', 'imp. & p. p.', 'of Regiment');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regimenting', 'p. pr. & vb. n.', 'of Regiment');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regiment', 'v. t.', 'To form into a regiment or into regiments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regimental', 'a.', 'Belonging to, or concerning, a regiment; as, regimental\n   officers, clothing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regimentally', 'adv.', 'In or by a regiment or regiments; as, troops\n   classified regimentally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regimentals', 'n. pl.', 'The uniform worn by the officers and soldiers of a\n   regiment; military dress; -- formerly used in the singular in the same\n   sense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regiminal', 'a.', 'Of or relating to regimen; as, regiminal rules.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Region', 'n.', 'One of the grand districts or quarters into which any space\n   or surface, as of the earth or the heavens, is conceived of as divided;\n   hence, in general, a portion of space or territory of indefinite\n   extent; country; province; district; tract.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Region', 'n.', 'Tract, part, or space, lying about and including anything;\n   neighborhood; vicinity; sphere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Region', 'n.', 'The upper air; the sky; the heavens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Region', 'n.', 'The inhabitants of a district.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Region', 'n.', 'Place; rank; station.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regional', 'a.', 'Of or pertaining to a particular region; sectional.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regious', 'a.', 'Regal; royal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Register', 'n.', 'A written account or entry; an official or formal\n   enumeration, description, or record; a memorial record; a list or roll;\n   a schedule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Register', 'n.', 'A record containing a list and description of the\n   merchant vessels belonging to a port or customs district.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Register', 'n.', 'A certificate issued by the collector of customs of a\n   port or district to the owner of a vessel, containing the description\n   of a vessel, its name, ownership, and other material facts. It is kept\n   on board the vessel, to be used as an evidence of nationality or as a\n   muniment of title.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Register', 'n.', 'One who registers or records; a registrar; a recorder;\n   especially, a public officer charged with the duty of recording certain\n   transactions or events; as, a register of deeds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Register', 'n.', 'That which registers or records.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Register', 'n.', 'A contrivance for automatically noting the performance of\n   a machine or the rapidity of a process.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Register', 'n.', 'The part of a telegraphic apparatus which records\n   automatically the message received.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Register', 'n.', 'A machine for registering automatically the number of\n   persons passing through a gateway, fares taken, etc.; a telltale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Register', 'n.', 'A lid, stopper, or sliding plate, in a furnace, stove,\n   etc., for regulating the admission of air to the fuel; also, an\n   arrangement containing dampers or shutters, as in the floor or wall of\n   a room or passage, or in a chimney, for admitting or excluding heated\n   air, or for regulating ventilation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Register', 'n.', 'The inner part of the mold in which types are cast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Register', 'n.', 'The correspondence of pages, columns, or lines on the\n   opposite or reverse sides of the sheet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Register', 'n.', 'The correspondence or adjustment of the several\n   impressions in a design which is printed in parts, as in\n   chromolithographic printing, or in the manufacture of paper hangings.\n   See Register, v. i. 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Register', 'v. i.', 'The compass of a voice or instrument; a specified\n   portion of the compass of a voice, or a series of vocal tones of a\n   given compass; as, the upper, middle, or lower register; the soprano\n   register; the tenor register.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Register', 'v. i.', 'A stop or set of pipes in an organ.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Registered', 'imp. & p. p.', 'of Register');
  INSERT INTO words (word, word_type, description) VALUES  (    'Registering', 'p. pr. & vb. n.', 'of Register');
  INSERT INTO words (word, word_type, description) VALUES  (    'Register', 'n.', 'To enter in a register; to record formally and\n   distinctly, as for future use or service.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Register', 'n.', 'To enroll; to enter in a list.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Register', 'v. i.', 'To enroll ones name in a register.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Register', 'v. i.', 'To correspond in relative position; as, two pages,\n   columns, etc. , register when the corresponding parts fall in the same\n   line, or when line falls exactly upon line in reverse pages, or (as in\n   chromatic printing) where the various colors of the design are printed\n   consecutively, and perfect adjustment of parts is necessary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Registering', 'a.', 'Recording; -- applied to instruments; having an\n   apparatus which registers; as, a registering thermometer. See\n   Recording.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Registership', 'n.', 'The office of a register.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Registrant', 'n.', 'One who registers; esp., one who , by virtue of\n   securing an official registration, obtains a certain right or title of\n   possession, as to a trade-mark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Registrar', 'n.', 'One who registers; a recorder; a keeper of records; as,\n   a registrar of births, deaths, and marriages. See Register, n., 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Registrarship', 'n.', 'The office of a registrar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Registrary', 'n.', 'A registrar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Registrate', 'v. t.', 'To register.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Registration', 'v.', 'The act of registering; registry; enrollment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Registration', 'v.', 'The art of selecting and combining the stops or\n   registers of an organ.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Registry', 'n.', 'The act of recording or writing in a register;\n   enrollment; registration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Registry', 'n.', 'The place where a register is kept.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Registry', 'n.', 'A record; an account; a register.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regius', 'a.', 'Of or pertaining to a king; royal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regive', 'v. t.', 'To give again; to give back.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regle', 'v. t.', 'To rule; to govern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reglement', 'n.', 'Regulation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reglementary', 'a.', 'Regulative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reglet', 'n.', 'A flat, narrow molding, used chiefly to separate the parts\n   or members of compartments or panels from one another, or doubled,\n   turned, and interlaced so as to form knots, frets, or other ornaments.\n   See Illust. (12) of Column.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reglet', 'n.', 'A strip of wood or metal of the height of a quadrat, used\n   for regulating the space between pages in a chase, and also for spacing\n   out title-pages and other open matter. It is graded to different sizes,\n   and designated by the name of the type that it matches; as, nonpareil\n   reglet, pica reglet, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regma', 'n.', 'A kind of dry fruit, consisting of three or more cells, each\n   which at length breaks open at the inner angle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regmacarp', 'n.', 'Any dry dehiscent fruit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regnal', 'a.', 'Of or pertaining to the reign of a monarch; as, regnal\n   years.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regnancy', 'n.', 'The condition or quality of being regnant; sovereignty;\n   rule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regnant', 'a.', 'Exercising regal authority; reigning; as, a queen regnant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regnant', 'a.', 'Having the chief power; ruling; predominant; prevalent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regnative', 'a.', 'Ruling; governing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regne', 'n. & v.', 'See Reign.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regorge', 'v. t.', 'To vomit up; to eject from the stomach; to throw back.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regorge', 'v. t.', 'To swallow again; to swallow back.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regrade', 'v. i.', 'To retire; to go back.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regraft', 'v. t.', 'To graft again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regrant', 'v. t.', 'To grant back; to grant again or anew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regrant', 'n.', 'The act of granting back to a former proprietor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regrant', 'n.', 'A renewed of a grant; as, the regrant of a monopoly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regrated', 'imp. & p. p.', 'of Regrate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regrating', 'p. pr. & vb. n.', 'of Regrate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regrate', 'v. t.', 'To remove the outer surface of, as of an old hewn\n   stone, so as to give it a fresh appearance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regrate', 'v. t.', 'To offend; to shock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regrate', 'v. t.', 'To buy in large quantities, as corn, provisions, etc.,\n   at a market or fair, with the intention of selling the same again, in\n   or near the same place, at a higher price, -- a practice which was\n   formerly treated as a public offense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regrater', 'n.', 'One who regrates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regratery', 'n.', 'The act or practice of regrating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regratiatory', 'n.', 'A returning or giving of thanks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regrator', 'n.', 'One guilty of regrating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regrede', 'v. i.', 'To go back; to retrograde, as the apsis of a planets\n   orbit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regredience', 'n.', 'A going back; a retrogression; a return.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regreet', 'v. t.', 'To greet again; to resalute; to return a salutation to;\n   to greet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regreet', 'n.', 'A return or exchange of salutation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regress', 'n.', 'The act of passing back; passage back; return;\n   retrogression. \"The progress or regress of man\".');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regress', 'n.', 'The power or liberty of passing back.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regressed', 'imp. & p. p.', 'of Regress');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regressing', 'p. pr. & vb. n.', 'of Regress');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regress', 'v. i.', 'To go back; to return to a former place or state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regression', 'n.', 'The act of passing back or returning; retrogression;\n   retrogradation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regressive', 'a.', 'Passing back; returning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regressive', 'a.', 'Characterized by retrogression; retrogressive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regressively', 'adv.', 'In a regressive manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regret', 'v.', 'Pain of mind on account of something done or experienced in\n   the past, with a wish that it had been different; a looking back with\n   dissatisfaction or with longing; grief; sorrow; especially, a mourning\n   on account of the loss of some joy, advantage, or satisfaction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regret', 'v.', 'Dislike; aversion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regretted', 'imp. & p. p.', 'of Regret');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regretting', 'p. pr. & vb. n.', 'of Regret');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regret', 'v. t.', 'To experience regret on account of; to lose or miss with\n   a sense of regret; to feel sorrow or dissatisfaction on account of (the\n   happening or the loss of something); as, to regret an error; to regret\n   lost opportunities or friends.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regretful', 'a.', 'Full of regret; indulging in regrets; repining.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regrow', 'v. i. & t.', 'To grow again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regrowth', 'n.', 'The act of regrowing; a second or new growth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reguardant', 'a.', 'Same as Regardant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reguerdon', 'v. t.', 'To reward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regulable', 'a.', 'Capable of being regulated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regular', 'a.', 'Conformed to a rule; agreeable to an established rule,\n   law, principle, or type, or to established customary forms; normal;\n   symmetrical; as, a regular verse in poetry; a regular piece of music; a\n   regular verb; regular practice of law or medicine; a regular building.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regular', 'a.', 'Governed by rule or rules; steady or uniform in course,\n   practice, or occurence; not subject to unexplained or irrational\n   variation; returning at stated intervals; steadily pursued; orderlly;\n   methodical; as, the regular succession of day and night; regular\n   habits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regular', 'a.', 'Constituted, selected, or conducted in conformity with\n   established usages, rules, or discipline; duly authorized; permanently\n   organized; as, a regular meeting; a regular physican; a regular\n   nomination; regular troops.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regular', 'a.', 'Belonging to a monastic order or community; as, regular\n   clergy, in distinction dfrom the secular clergy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regular', 'a.', 'Thorough; complete; unmitigated; as, a regular humbug.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regular', 'a.', 'Having all the parts of the same kind alike in size and\n   shape; as, a regular flower; a regular sea urchin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regular', 'a.', 'Same as Isometric.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regular', 'a.', 'A member of any religious order or community who has taken\n   the vows of poverty, chastity, and obedience, and who has been solemnly\n   recognized by the church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regular', 'a.', 'A soldier belonging to a permanent or standing army; --\n   chiefly used in the plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regularia', 'n. pl.', 'A division of Echini which includes the circular, or\n   regular, sea urchins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regularity', 'n.', 'The condition or quality of being regular; as,\n   regularity of outline; the regularity of motion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regularize', 'v. t.', 'To cause to become regular; to regulate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regularly', 'adv.', 'In a regular manner; in uniform order; methodically;\n   in due order or time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regularness', 'n.', 'Regularity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regulated', 'imp. & p. p.', 'of Regulate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regulating', 'p. pr. & vb. n.', 'of Regulate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regulate', 'v. t.', 'To adjust by rule, method, or established mode; to\n   direct by rule or restriction; to subject to governing principles or\n   laws.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regulate', 'v. t.', 'To put in good order; as, to regulate the disordered\n   state of a nation or its finances.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regulate', 'v. t.', 'To adjust, or maintain, with respect to a desired\n   rate, degree, or condition; as, to regulate the temperature of a room,\n   the pressure of steam, the speed of a machine, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regulation', 'n.', 'The act of regulating, or the state of being regulated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regulation', 'n.', 'A rule or order prescribed for management or\n   government; prescription; a regulating principle; a governing\n   direction; precept; law; as, the regulations of a society or a school.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regulative', 'a.', 'Tending to regulate; regulating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regulative', 'a.', 'Necessarily assumed by the mind as fundamental to all\n   other knowledge; furnishing fundamental principles; as, the regulative\n   principles, or principles a priori; the regulative faculty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regulator', 'n.', 'One who, or that which, regulates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regulator', 'n.', 'A contrivance for regulating and controlling motion, as:\n   (a) The lever or index in a watch, which controls the effective length\n   of the hairspring, and thus regulates the vibrations of the balance.\n   (b) The governor of a steam engine. (c) A valve for controlling the\n   admission of steam to the steam chest, in a locomotive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regulator', 'n.', 'A clock, or other timepiece, used as a standard of\n   correct time. See Astronomical clock (a), under Clock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regulator', 'n.', 'A member of a volunteer committee which, in default of\n   the lawful authority, undertakes to preserve order and prevent crimes;\n   also, sometimes, one of a band organized for the comission of violent\n   crimes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reguline', 'a.', 'Of or pertaining to regulus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regulize', 'v. t.', 'To reduce to regulus; to separate, as a metal from\n   extraneous matter; as, to regulize antimony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reguluses', 'pl. ', 'of Regulus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reguli', 'pl. ', 'of Regulus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regulus', 'n.', 'A petty king; a ruler of little power or consequence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regulus', 'n.', 'The button, globule, or mass of metal, in a more or less\n   impure state, which forms in the bottom of the crucible in smelting and\n   reduction of ores.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regulus', 'n.', 'A star of the first magnitude in the constellation Leo; --\n   called also the Lions Heart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regurgitate', 'v. t.', 'To throw or pour back, as from a deep or hollow\n   place; to pour or throw back in great quantity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regurgitate', 'v. i.', 'To be thrown or poured back; to rush or surge back.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regurgitation', 'n.', 'The act of flowing or pouring back by the orifice of\n   entrance');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regurgitation', 'n.', 'the reversal of the natural direction in which the\n   current or contents flow through a tube or cavity of the body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Regurgitation', 'n.', 'The act of swallowing again; reabsorption.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rehabilitated', 'imp. & p. p.', 'of Rehabilitate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rehabilitating', 'p. pr. & vb. n.', 'of Rehabilitate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rehabilitate', 'v. t.', 'To invest or clothe again with some right,\n   authority, or dignity; to restore to a former capacity; to reinstate;\n   to qualify again; to restore, as a delinquent, to a former right, rank,\n   or privilege lost or forfeited; -- a term of civil and canon law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rehabilitation', 'n.', 'The act of rehabilitating, or the state of being\n   rehabilitated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rehash', 'v. t.', 'To hash over again; to prepare or use again; as, to\n   rehash old arguments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rehash', 'n.', 'Something hashed over, or made up from old materials.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rehear', 'v. t.', 'To hear again; to try a second time; as, to rehear a\n   cause in Chancery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rehearsal', 'n.', 'The act of rehearsing; recital; narration; repetition;\n   specifically, a private recital, performance, or season of practice, in\n   preparation for a public exhibition or exercise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rehearsed', 'imp. & p. p.', 'of Rehearse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rehearsing', 'p. pr. & vb. n.', 'of Rehearse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rehearse', 'v. t.', 'To repeat, as what has been already said; to tell over\n   again; to recite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rehearse', 'v. t.', 'To narrate; to relate; to tell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rehearse', 'v. t.', 'To recite or repeat in private for experiment and\n   improvement, before a public representation; as, to rehearse a tragedy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rehearse', 'v. t.', 'To cause to rehearse; to instruct by rehearsal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rehearse', 'v. i.', 'To recite or repeat something for practice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rehearser', 'n.', 'One who rehearses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reheat', 'v. t.', 'To heat again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reheat', 'v. t.', 'To revive; to cheer; to cherish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rehibition', 'n.', 'The returning of a thing purchased to the seller, on\n   the ground of defect or frand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rehibitory', 'a.', 'Of or relating to rehibition; as, a rehibitory action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rehire', 'v. t.', 'To hire again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rehypothecate', 'v. t.', 'To hypothecate again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reis', 'pl. ', 'of Rei');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rei', 'n.', 'A portuguese money of account, in value about one tenth of a\n   cent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reichsrath', 'n.', 'The parliament of Austria (exclusive of Hungary, which\n   has its own diet, or parliament). It consists of an Upper and a Lower\n   House, or a House of Lords and a House of Representatives.');
