  INSERT INTO words (word, word_type, description) VALUES  (    'Reimbursing', 'p. pr. & vb. n.', 'of Reimburse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reimburse', 'v. t.', 'To replace in a treasury or purse, as an equivalent\n   for what has been taken, lost, or expended; to refund; to pay back; to\n   restore; as, to reimburse the expenses of a war.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reimburse', 'v. t.', 'To make restoration or payment of an equivalent to (a\n   person); to pay back to; to indemnify; -- often reflexive; as, to\n   reimburse ones self by successful speculation.');
