  INSERT INTO words (word, word_type, description) VALUES  (    'Rejoice', 'v. t.', 'To enjoy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rejoice', 'v. t.', 'To give joy to; to make joyful; to gladden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rejoice', 'n.', 'The act of rejoicing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rejoicement', 'n.', 'Rejoicing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rejoicer', 'n.', 'One who rejoices.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rejoicing', 'n.', 'Joy; gladness; delight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rejoicing', 'n.', 'The expression of joy or gladness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rejoicing', 'n.', 'That which causes to rejoice; occasion of joy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rejoicingly', 'adv.', 'With joi or exultation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rejoined', 'imp. & p. p.', 'of Rejoin');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rejoining', 'p. pr. & vb. n.', 'of Rejoin');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rejoin', 'v. t.', 'To join again; to unite after separation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rejoin', 'v. t.', 'To come, or go, again into the presence of; to join the\n   company of again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rejoin', 'v. t.', 'To state in reply; -- followed by an object clause.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rejoin', 'v. i.', 'To answer to a reply.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rejoin', 'v. i.', 'To answer, as the defendant to the plaintiffs\n   replication.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rejoinder', 'n.', 'An answer to a reply; or, in general, an answer or\n   reply.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rejoinder', 'n.', 'The defendants answer to the plaintiffs replication.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rejoinder', 'v. i.', 'To make a rejoinder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rejoindure', 'n.', 'Act of joining again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rejoint', 'v. t.', 'To reunite the joints of; to joint anew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rejoint', 'v. t.', 'Specifically (Arch.), to fill up the joints of, as\n   stones in buildings when the mortar has been dislodged by age and the\n   action of the weather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rejolt', 'n.', 'A reacting jolt or shock; a rebound or recoil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rejolt', 'v. t.', 'To jolt or shake again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rejourn', 'v. t.', 'To adjourn; to put off.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rejournment', 'n.', 'Adjournment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rejudge', 'v. t.', 'To judge again; to reexamine; to review; to call to a\n   new trial and decision.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rejuvenate', 'v. t.', 'To render young again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rejuvenation', 'n.', 'Rejuvenescence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rejuvenescence', 'n.', 'A renewing of youth; the state of being or growing\n   young again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rejuvenescence', 'n.', 'A method of cell formation in which the entire\n   protoplasm of an old cell escapes by rupture of the cell wall, and then\n   develops a new cell wall. It is seen sometimes in the formation of\n   zoospores, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rejuvenescency', 'n.', 'Rejuvenescence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rejuvenescent', 'a.', 'Becoming, or causing to become, rejuvenated;\n   rejuvenating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rejuvenize', 'v. t.', 'To rejuvenate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rekindle', 'v. t. & i.', 'To kindle again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rekne', 'v. t.', 'To reckon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relade', 'v. t.', 'To lade or load again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relaid', '', 'imp. & p. p. of Relay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relais', 'n.', 'A narrow space between the foot of the rampart and the\n   scarp of the ditch, serving to receive the earth that may crumble off\n   or be washed down, and prevent its falling into the ditch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reland', 'v. t.', 'To land again; to put on land, as that which had been\n   shipped or embarked.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reland', 'v. i.', 'To go on shore after having embarked; to land again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relapsed', 'imp. & p. p.', 'of Relapse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relapsing', 'p. pr. & vb. n.', 'of Relapse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relapse', 'v. i.', 'To slip or slide back, in a literal sense; to turn\n   back.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relapse', 'v. i.', 'To slide or turn back into a former state or practice;\n   to fall back from some condition attained; -- generally in a bad sense,\n   as from a state of convalescence or amended condition; as, to relapse\n   into a stupor, into vice, or into barbarism; -- sometimes in a good\n   sense; as, to relapse into slumber after being disturbed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relapse', 'v. i.', 'To fall from Christian faith into paganism, heresy, or\n   unbelief; to backslide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relapse', 'v.', 'A sliding or falling back, especially into a former bad\n   state, either of body or morals; backsliding; the state of having\n   fallen back.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relapse', 'v.', 'One who has relapsed, or fallen back, into error; a\n   backslider; specifically, one who, after recanting error, returns to it\n   again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relapser', 'n.', 'One who relapses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relapsing', 'a.', 'Marked by a relapse; falling back; tending to return to\n   a former worse state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Related', 'imp. & p. p.', 'of Relate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relating', 'p. pr. & vb. n.', 'of Relate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relate', 'v. t.', 'To bring back; to restore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relate', 'v. t.', 'To refer; to ascribe, as to a source.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relate', 'v. t.', 'To recount; to narrate; to tell over.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relate', 'v. t.', 'To ally by connection or kindred.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relate', 'v. i.', 'To stand in some relation; to have bearing or concern;\n   to pertain; to refer; -- with to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relate', 'v. i.', 'To make reference; to take account.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Related', 'p. p. & a.', 'Allied by kindred; connected by blood or alliance,\n   particularly by consanguinity; as, persons related in the first or\n   second degree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Related', 'p. p. & a.', 'Standing in relation or connection; as, the\n   electric and magnetic forcec are closely related.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Related', 'p. p. & a.', 'Narrated; told.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Related', 'p. p. & a.', 'Same as Relative, 4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relatedness', 'n.', 'The state or condition of being related; relationship;\n   affinity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relater', 'n.', 'One who relates or narrates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relation', 'n.', 'The act of relating or telling; also, that which is\n   related; recital; account; narration; narrative; as, the relation of\n   historical events.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relation', 'n.', 'The state of being related or of referring; what is\n   apprehended as appertaining to a being or quality, by considering it in\n   its bearing upon something else; relative quality or condition; the\n   being such and such with regard or respect to some other thing;\n   connection; as, the relation of experience to knowledge; the relation\n   of master to servant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relation', 'n.', 'Reference; respect; regard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relation', 'n.', 'Connection by consanguinity or affinity; kinship;\n   relationship; as, the relation of parents and children.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relation', 'n.', 'A person connected by cosanguinity or affinity; a\n   relative; a kinsman or kinswoman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relation', 'n.', 'The carrying back, and giving effect or operation to, an\n   act or proceeding frrom some previous date or time, by a sort of\n   fiction, as if it had happened or begun at that time. In such case the\n   act is said to take effect by relation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relation', 'n.', 'The act of a relator at whose instance a suit is begun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relational', 'a.', 'Having relation or kindred; related.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relational', 'a.', 'Indicating or specifying some relation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relationist', 'n.', 'A relative; a relation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relationship', 'n.', 'The state of being related by kindred, affinity, or\n   other alliance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relative', 'a.', 'Having relation or reference; referring; respecting;\n   standing in connection; pertaining; as, arguments not relative to the\n   subject.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relative', 'a.', 'Arising from relation; resulting from connection with, or\n   reference to, something else; not absolute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relative', 'a.', 'Indicating or expressing relation; refering to an\n   antecedent; as, a relative pronoun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relative', 'a.', 'Characterizing or pertaining to chords and keys, which,\n   by reason of the identify of some of their tones, admit of a natural\n   transition from one to the other.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relative', 'n.', 'One who, or that which, relates to, or is considered in\n   its relation to, something else; a relative object or term; one of two\n   object or term; one of two objects directly connected by any relation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relative', 'n.', 'A person connected by blood or affinity; strictly, one\n   allied by blood; a relation; a kinsman or kinswoman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relative', 'n.', 'A relative pronoun; a word which relates to, or\n   represents, another word or phrase, called its antecedent; as, the\n   relatives \"who\", \"which\", \"that\".');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relatively', 'adv.', 'In a relative manner; in relation or respect to\n   something else; not absolutely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relativeness', 'n.', 'The state of being relative, or having relation;\n   relativity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relativity', 'n.', 'The state of being relative; as, the relativity of a\n   subject.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relator', 'n.', 'One who relates; a relater.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relator', 'n.', 'A private person at whose relation, or in whose behalf,\n   the attorney-general allows an information in the nature of a quo\n   warranto to be filed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relatrix', 'n.', 'A female relator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relaxed', 'imp. & p. p.', 'of Relax');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relaxing', 'p. pr. & vb. n.', 'of Relax');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relax', 'n.', 'To make lax or loose; to make less close, firm, rigid,\n   tense, or the like; to slacken; to loosen; to open; as, to relax a rope\n   or cord; to relax the muscles or sinews.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relax', 'n.', 'To make less severe or rigorous; to abate the stringency of;\n   to remit in respect to strenuousness, earnestness, or effort; as, to\n   relax discipline; to relax ones attention or endeavors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relax', 'n.', 'Hence, to relieve from attention or effort; to ease; to\n   recreate; to divert; as, amusement relaxes the mind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relax', 'n.', 'To relieve from constipation; to loosen; to open; as, an\n   aperient relaxes the bowels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relax', 'v. i.', 'To become lax, weak, or loose; as, to let ones grasp\n   relax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relax', 'v. i.', 'To abate in severity; to become less rigorous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relax', 'v. i.', 'To remit attention or effort; to become less diligent; to\n   unbend; as, to relax in study.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relax', 'n.', 'Relaxation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relax', 'a.', 'Relaxed; lax; hence, remiss; careless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relaxable', 'a.', 'Capable of being relaxed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relaxant', 'n.', 'A medicine that relaxes; a laxative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relaxation', 'n.', 'The act or process of relaxing, or the state of being\n   relaxed; as, relaxation of the muscles; relaxation of a law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relaxation', 'n.', 'Remission from attention and effort; indulgence in\n   recreation, diversion, or amusement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relaxative', 'a.', 'Having the quality of relaxing; laxative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relaxative', 'n.', 'A relaxant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relaid', 'imp. & p. p.', 'of Relay');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relaying', 'p. pr. & vb. n.', 'of Relay');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relay', 'v. t.', 'To lay again; to lay a second time; as, to relay a\n   pavement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relay', 'n.', 'A supply of anything arranged beforehand for affording\n   relief from time to time, or at successive stages; provision for\n   successive relief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relay', 'n.', 'A supply of horses placced at stations to be in readiness to\n   relieve others, so that a trveler may proceed without delay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relay', 'n.', 'A supply of hunting dogs or horses kept in readiness at\n   certain places to relive the tired dogs or horses, and to continue the\n   pursuit of the game if it comes that way.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relay', 'n.', 'A number of men who relieve others in carrying on some work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relay', 'n.', 'In various forms of telegraphic apparatus, a magnet which\n   receives the circuit current, and is caused by it to bring into into\n   action the power of a local battery for performing the work of making\n   the record; also, a similar device by which the current in one circuit\n   is made to open or close another circuit in which a current is passing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relbun', 'n.', 'The roots of the Chilian plant Calceolaria arachnoidea, --\n   used for dyeing crimson.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Releasable', 'a.', 'That may be released.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Release', 'v. t.', 'To lease again; to grant a new lease of; to let back.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Released', 'imp. & p. p.', 'of Release');
  INSERT INTO words (word, word_type, description) VALUES  (    'Releasing', 'p. pr. & vb. n.', 'of Release');
  INSERT INTO words (word, word_type, description) VALUES  (    'Release', 'n.', 'To let loose again; to set free from restraint,\n   confinement, or servitude; to give liberty to, or to set at liberty; to\n   let go.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Release', 'n.', 'To relieve from something that confines, burdens, or\n   oppresses, as from pain, trouble, obligation, penalty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Release', 'n.', 'To let go, as a legal claim; to discharge or relinquish a\n   right to, as lands or tenements, by conveying to another who has some\n   right or estate in possession, as when the person in remainder releases\n   his right to the tenant in possession; to quit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Release', 'n.', 'To loosen; to relax; to remove the obligation of; as, to\n   release an ordinance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Release', 'n.', 'The act of letting loose or freeing, or the state of being\n   let loose or freed; liberation or discharge from restraint of any kind,\n   as from confinement or bondage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Release', 'n.', 'Relief from care, pain, or any burden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Release', 'n.', 'Discharge from obligation or responsibility, as from debt,\n   penalty, or claim of any kind; acquittance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Release', 'n.', 'A giving up or relinquishment of some right or claim; a\n   conveyance of a mans right in lands or tenements to another who has\n   some estate in possession; a quitclaim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Release', 'n.', 'The act of opening the exhaust port to allow the steam to\n   escape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Releasee', 'n.', 'One to whom a release is given.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Releasement', 'n.', 'The act of releasing, as from confinement or\n   obligation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Releaser', 'n.', 'One who releases, or sets free.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Releasor', 'n.', 'One by whom a release is given.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relegated', 'imp. & p. p.', 'of Relegate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relegating', 'p. pr. & vb. n.', 'of Relegate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relegate', 'v. t.', 'To remove, usually to an inferior position; to\n   consign; to transfer; specifically, to send into exile; to banish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relegation', 'n.', 'The act of relegating, or the state of being relegated;\n   removal; banishment; exile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relented', 'imp. & p. p.', 'of Relent');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relenting', 'p. pr. & vb. n.', 'of Relent');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relent', 'v. i.', 'To become less rigid or hard; to yield; to dissolve; to\n   melt; to deliquesce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relent', 'v. i.', 'To become less severe or intense; to become less hard,\n   harsh, cruel, or the like; to soften in temper; to become more mild and\n   tender; to feel compassion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relent', 'v. t.', 'To slacken; to abate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relent', 'v. t.', 'To soften; to dissolve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relent', 'v. t.', 'To mollify ; to cause to be less harsh or severe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relent', 'n.', 'Stay; stop; delay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relentless', 'a.', 'Unmoved by appeals for sympathy or forgiveness;\n   insensible to the distresses of others; destitute of tenderness;\n   unrelenting; unyielding; unpitying; as, a prey to relentless despotism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relentment', 'n.', 'The act or process of relenting; the state of having\n   relented.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relesse', 'v. t.', 'To release.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relessee', 'n.', 'See Releasee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relessor', 'n.', 'See Releasor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Re-let', 'v. t.', 'To let anew, as a house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relevance', 'n.', 'Alt. of Relevancy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relevancy', 'n.', 'The quality or state of being relevant; pertinency;\n   applicability.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relevancy', 'n.', 'Sufficiency to infer the conclusion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relevant', 'a.', 'Relieving; lending aid or support.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relevant', 'a.', 'Bearing upon, or properly applying to, the case in hand;\n   pertinent; applicable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relevant', 'a.', 'Sufficient to support the cause.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relevantly', 'adv.', 'In a relevant manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relevation', 'n.', 'A raising or lifting up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reliability', 'n.', 'The state or quality of being reliable; reliableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reliable', 'a.', 'Suitable or fit to be relied on; worthy of dependance or\n   reliance; trustworthy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reliance', 'n.', 'The act of relying, or the condition or quality of being\n   reliant; dependence; confidence; trust; repose of mind upon what is\n   deemed sufficient support or authority.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reliance', 'n.', 'Anything on which to rely; dependence; ground of trust;\n   as, the boat was a poor reliance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reliant', 'a.', 'Having, or characterized by, reliance; confident;\n   trusting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relic', 'n.', 'That which remains; that which is left after loss or decay;\n   a remaining portion; a remnant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relic', 'n.', 'The body from which the soul has departed; a corpse;\n   especially, the body, or some part of the body, of a deceased saint or\n   martyr; -- usually in the plural when referring to the whole body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relic', 'n.', 'Hence, a memorial; anything preserved in remembrance; as,\n   relics of youthful days or friendships.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relicly', 'adv.', 'In the manner of relics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relict', 'n.', 'A woman whose husband is dead; a widow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relicted', 'a.', 'Left uncovered, as land by recession of water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reliction', 'n.', 'A leaving dry; a recession of the sea or other water,\n   leaving dry land; land left uncovered by such recession.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relief', 'n.', 'The act of relieving, or the state of being relieved; the\n   removal, or partial removal, of any evil, or of anything oppressive or\n   burdensome, by which some ease is obtained; succor; alleviation;\n   comfort; ease; redress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relief', 'n.', 'Release from a post, or from the performance of duty, by\n   the intervention of others, by discharge, or by relay; as, a relief of\n   a sentry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relief', 'n.', 'That which removes or lessens evil, pain, discomfort,\n   uneasiness, etc.; that which gives succor, aid, or comfort; also, the\n   person who relieves from performance of duty by taking the place of\n   another; a relay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relief', 'n.', 'A fine or composition which the heir of a deceased tenant\n   paid to the lord for the privilege of taking up the estate, which, on\n   strict feudal principles, had lapsed or fallen to the lord on the death\n   of the tenant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relief', 'n.', 'The projection of a figure above the ground or plane on\n   which it is formed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relief', 'n.', 'The appearance of projection given by shading, shadow,\n   etc., to any figure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relief', 'n.', 'The height to which works are raised above the bottom of\n   the ditch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relief', 'n.', 'The elevations and surface undulations of a country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reliefful', 'a.', 'Giving relief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reliefless', 'a.', 'Destitute of relief; also, remediless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relier', 'n.', 'One who relies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relievable', 'a.', 'Capable of being relieved; fitted to recieve relief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relieved', 'imp. & p. p.', 'of Relieve');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relieving', 'p. pr. & vb. n.', 'of Relieve');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relieve', 'v. t.', 'To lift up; to raise again, as one who has fallen; to\n   cause to rise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relieve', 'v. t.', 'To cause to seem to rise; to put in relief; to give\n   prominence or conspicuousness to; to set off by contrast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relieve', 'v. t.', 'To raise up something in; to introduce a contrast or\n   variety into; to remove the monotony or sameness of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relieve', 'v. t.', 'To raise or remove, as anything which depresses, weighs\n   down, or crushes; to render less burdensome or afflicting; to\n   alleviate; to abate; to mitigate; to lessen; as, to relieve pain; to\n   relieve the wants of the poor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relieve', 'v. t.', 'To free, wholly or partly, from any burden, trial,\n   evil, distress, or the like; to give ease, comfort, or consolation to;\n   to give aid, help, or succor to; to support, strengthen, or deliver;\n   as, to relieve a besieged town.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relieve', 'v. t.', 'To release from a post, station, or duty; to put\n   another in place of, or to take the place of, in the bearing of any\n   burden, or discharge of any duty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relieve', 'v. t.', 'To ease of any imposition, burden, wrong, or\n   oppression, by judicial or legislative interposition, as by the removal\n   of a grievance, by indemnification for losses, or the like; to right.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relievement', 'n.', 'The act of relieving, or the state of being relieved;\n   relief; release.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reliever', 'n.', 'One who, or that which, relieves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relieving', 'a.', 'Serving or tending to relieve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relievo', 'n.', 'See Relief, n., 5.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relight', 'v. t.', 'To light or kindle anew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Religieuse', 'n. m.', 'Alt. of Religieux');
  INSERT INTO words (word, word_type, description) VALUES  (    'Religieux', 'n. m.', 'A person bound by monastic vows; a nun; a monk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Religion', 'n.', 'The outward act or form by which men indicate their\n   recognition of the existence of a god or of gods having power over\n   their destiny, to whom obedience, service, and honor are due; the\n   feeling or expression of human love, fear, or awe of some superhuman\n   and overruling power, whether by profession of belief, by observance of\n   rites and ceremonies, or by the conduct of life; a system of faith and\n   worship; a manifestation of piety; as, ethical religions; monotheistic\n   religions; natural religion; revealed religion; the religion of the\n   Jews; the religion of idol worshipers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Religion', 'n.', 'Specifically, conformity in faith and life to the\n   precepts inculcated in the Bible, respecting the conduct of life and\n   duty toward God and man; the Christian faith and practice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Religion', 'n.', 'A monastic or religious order subject to a regulated mode\n   of life; the religious state; as, to enter religion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Religion', 'n.', 'Strictness of fidelity in conforming to any practice, as\n   if it were an enjoined rule of conduct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Religionary', 'a.', 'Relating to religion; pious; as, religionary\n   professions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Religionary', 'n.', 'Alt. of Religioner');
  INSERT INTO words (word, word_type, description) VALUES  (    'Religioner', 'n.', 'A religionist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Religionism', 'n.', 'The practice of, or devotion to, religion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Religionism', 'n.', 'Affectation or pretense of religion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Religionist', 'n.', 'One earnestly devoted or attached to a religion; a\n   religious zealot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Religionize', 'v. t.', 'To bring under the influence of religion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Religionless', 'a.', 'Destitute of religion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Religiosity', 'n.', 'The quality of being religious; religious feeling or\n   sentiment; religiousness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Religious', 'a.', 'Of or pertaining to religion; concerned with religion;\n   teaching, or setting forth, religion; set apart to religion; as, a\n   religious society; a religious sect; a religious place; religious\n   subjects, books, teachers, houses, wars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Religious', 'a.', 'Possessing, or conforming to, religion; pious; godly;\n   as, a religious man, life, behavior, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Religious', 'a.', 'Scrupulously faithful or exact; strict.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Religious', 'a.', 'Belonging to a religious order; bound by vows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Religious', 'n.', 'A person bound by monastic vows, or sequestered from\n   secular concern, and devoted to a life of piety and religion; a monk or\n   friar; a nun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Religiously', 'adv.', 'In a religious manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Religiousness', 'n.', 'The quality of being religious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relik', 'n.', 'Relic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relinquent', 'a.', 'Relinquishing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relinquent', 'n.', 'One who relinquishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relinquished', 'imp. & p. p.', 'of Relinquish');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relinquishing', 'p. pr. & vb. n.', 'of Relinquish');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relinquish', 'v. t.', 'To withdraw from; to leave behind; to desist from;\n   to abandon; to quit; as, to relinquish a pursuit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relinquish', 'v. t.', 'To give up; to renounce a claim to; resign; as, to\n   relinquish a debt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relinquisher', 'n.', 'One who relinquishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relinquishment', 'n.', 'The act of relinquishing.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-ries', 'pl. ', 'of Reliquary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reliquary', 'n.', 'A depositary, often a small box or casket, in which\n   relics are kept.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relique', 'n.', 'See Relic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reliquiae', 'n. pl.', 'Remains of the dead; organic remains; relics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reliquiae', 'n. pl.', 'Same as Induviae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reliquian', 'a.', 'Of or pertaining to a relic or relics; of the nature of\n   a relic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reliquidate', 'v. t.', 'To liquidate anew; to adjust a second time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reliquidation', 'n.', 'A second or renewed liquidation; a renewed\n   adjustment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relished', 'imp. & p. p.', 'of Relish');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relishing', 'p. pr. & vb. n.', 'of Relish');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relish', 'v. t.', 'To taste or eat with pleasure; to like the flavor of; to\n   partake of with gratification; hence, to enjoy; to be pleased with or\n   gratified by; to experience pleasure from; as, to relish food.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relish', 'v. t.', 'To give a relish to; to cause to taste agreeably.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relish', 'v. i.', 'To have a pleasing or appetizing taste; to give\n   gratification; to have a flavor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relish', 'n.', 'A pleasing taste; flavor that gratifies the palate; hence,\n   enjoyable quality; power of pleasing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relish', 'n.', 'Savor; quality; characteristic tinge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relish', 'n.', 'A taste for; liking; appetite; fondness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relish', 'n.', 'That which is used to impart a flavor; specifically,\n   something taken with food to render it more palatable or to stimulate\n   the appetite; a condiment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relish', 'n.', 'The projection or shoulder at the side of, or around, a\n   tenon, on a tenoned piece.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relishable', 'a.', 'Capable of being relished; agreeable to the taste;\n   gratifying.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relive', 'v. i.', 'To live again; to revive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relive', 'v. t.', 'To recall to life; to revive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reload', 'v. t.', 'To load again, as a gun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reloan', 'n.', 'A second lending of the same thing; a renewal of a loan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relocate', 'v. t.', 'To locate again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relocation', 'n.', 'A second location.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relocation', 'n.', 'Renewal of a lease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relodge', 'v. t.', 'To lodge again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relove', 'v. t.', 'To love in return.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relucent', 'a.', 'Reflecting light; shining; glittering; glistening;\n   bright; luminous; splendid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reluct', 'v. i.', 'To strive or struggle against anything; to make\n   resistance; to draw back; to feel or show repugnance or reluctance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reluctance', 'n.', 'Alt. of Reluctancy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reluctancy', 'n.', 'The state or quality of being reluctant; repugnance;\n   aversion of mind; unwillingness; -- often followed by an infinitive, or\n   by to and a noun, formerly sometimes by against.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reluctant', 'a.', 'Striving against; opposed in desire; unwilling;\n   disinclined; loth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reluctant', 'a.', 'Proceeding from an unwilling mind; granted with\n   reluctance; as, reluctant obedience.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reluctantly', 'adv.', 'In a reluctant manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reluctate', 'v. i.', 'To struggle against anything; to resist; to oppose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reluctation', 'n.', 'Repugnance; resistance; reluctance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relumed', 'imp. & p. p.', 'of Relume');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reluming', 'p. pr. & vb. n.', 'of Relume');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relume', 'v. t.', 'To rekindle; to light again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relumined', 'imp. & p. p.', 'of Relumine');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relumining', 'p. pr. & vb. n.', 'of Relumine');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relumine', 'v. t.', 'To light anew; to rekindle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relumine', 'v. t.', 'To illuminate again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relied', 'imp. & p. p.', 'of Rely');
  INSERT INTO words (word, word_type, description) VALUES  (    'Relying', 'p. pr. & vb. n.', 'of Rely');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rely', 'v. i.', 'To rest with confidence, as when fully satisfied of the\n   veracity, integrity, or ability of persons, or of the certainty of\n   facts or of evidence; to have confidence; to trust; to depend; -- with\n   on, formerly also with in.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remade', '', 'imp. & p. p. of Remake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remained', 'imp. & p. p.', 'of Remain');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remaining', 'p. pr. & vb. n.', 'of Remain');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remain', 'v. i.', 'To stay behind while others withdraw; to be left after\n   others have been removed or destroyed; to be left after a number or\n   quantity has been subtracted or cut off; to be left as not included or\n   comprised.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remain', 'v. i.', 'To continue unchanged in place, form, or condition, or\n   undiminished in quantity; to abide; to stay; to endure; to last.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remain', 'v. t.', 'To await; to be left to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remain', 'n.', 'State of remaining; stay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remain', 'n.', 'That which is left; relic; remainder; -- chiefly in the\n   plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remain', 'n.', 'That which is left of a human being after the life is gone;\n   relics; a dead body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remain', 'n.', 'The posthumous works or productions, esp. literary works,\n   of one who is dead; as, Cecils');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remainder', 'n.', 'Anything that remains, or is left, after the separation\n   and removal of a part; residue; remnant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remainder', 'n.', 'The quantity or sum that is left after subtraction, or\n   after any deduction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remainder', 'n.', 'An estate in expectancy, generally in land, which\n   becomes an estate in possession upon the determination of a particular\n   prior estate, created at the same time, and by the same instrument; for\n   example, if land be conveyed to A for life, and on his death to B, As\n   life interest is a particuar estate, and Bs interest is a remainder,\n   or estate in remainder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remainder', 'a.', 'Remaining; left; left over; refuse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remainder-men', 'pl. ', 'of Remainder-man');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remainder-man', 'n.', 'One who has an estate after a particular estate is\n   determined. See Remainder, n., 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remake', 'v. t.', 'To make anew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remanded', 'imp. & p. p.', 'of Remand');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remanding', 'p. pr. & vb. n.', 'of Remand');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remand', 'v. t.', 'To recommit; to send back.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remand', 'n.', 'The act of remanding; the order for recommitment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remandment', 'n.', 'A remand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remanence', 'a.', 'Alt. of Remanency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remanency', 'a.', 'The state of being remanent; continuance; permanence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remanent', 'a.', 'That which remains; a remnant; a residue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remanent', 'a.', 'Remaining; residual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remanet', 'n.', 'A case for trial which can not be tried during the term; a\n   postponed case.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Re-mark', 'v. t.', 'To mark again, or a second time; to mark anew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remarked', 'imp. & p. p.', 'of Remark');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remarking', 'p. pr. & vb. n.', 'of Remark');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remark', 'n.', 'To mark in a notable manner; to distinquish clearly; to\n   make noticeable or conspicuous; to piont out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remark', 'n.', 'To take notice of, or to observe, mentally; as, to remark\n   the manner of a speaker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remark', 'n.', 'To express in words or writing, as observed or noticed; to\n   state; to say; -- often with a substantive clause; as, he remarked that\n   it was time to go.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remark', 'v. i.', 'To make a remark or remarks; to comment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remark', 'n.', 'Act of remarking or attentively noticing; notice or\n   observation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remark', 'n.', 'The expression, in speech or writing, of something remarked\n   or noticed; the mention of that which is worthy of attention or notice;\n   hence, also, a casual observation, comment, or statement; as, a\n   pertinent remark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remarkable', 'a.', 'Worthy of being remarked or noticed; noticeable;\n   conspicuous; hence, uncommon; extraordinary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remarker', 'n.', 'One who remarks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remarriage', 'n.', 'A second or repeated marriage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remarry', 'v. t. & i.', 'To marry again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remast', 'v. t.', 'To furnish with a new mast or set of masts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remasticate', 'v. t.', 'To chew or masticate again; to chew over and over,\n   as the cud.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remastication', 'n.', 'The act of masticating or chewing again or\n   repeatedly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remberge', 'n.', 'See Ramberge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remblai', 'n.', 'Earth or materials made into a bank after having been\n   excavated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remble', 'v. t.', 'To remove.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reme', 'n.', 'Realm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remean', 'v. t.', 'To give meaning to; to explain the meaning of; to\n   interpret.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remeant', 'a.', 'Coming back; returning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remeasure', 'v. t.', 'To measure again; to retrace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remede', 'n.', 'Remedy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remediable', 'a.', 'Capable of being remedied or cured.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remedial', 'a.', 'Affording a remedy; intended for a remedy, or for the\n   removal or abatement of an evil; as, remedial treatment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remedially', 'adv.', 'In a remedial manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remediate', 'a.', 'Remedial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remediless', 'a.', 'Not admitting of a remedy; incapable of being restored\n   or corrected; incurable; irreparable; as, a remediless mistake or loss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remediless', 'a.', 'Not answering as a remedy; ineffectual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remedies', 'pl. ', 'of Remedy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remedy', 'n.', 'That which relieves or cures a disease; any medicine or\n   application which puts an end to disease and restores health; -- with\n   for; as, a remedy for the gout.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remedy', 'n.', 'That which corrects or counteracts an evil of any kind; a\n   corrective; a counteractive; reparation; cure; -- followed by for or\n   against, formerly by to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remedy', 'n.', 'The legal means to recover a right, or to obtain redress\n   for a wrong.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remedied', 'imp. & p. p.', 'of Remedy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remedying', 'p. pr. & vb. n.', 'of Remedy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remedy', 'n.', 'To apply a remedy to; to relieve; to cure; to heal; to\n   repair; to redress; to correct; to counteract.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remelt', 'v. t.', 'To melt again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remembered', 'imp. & p. p.', 'of Remember');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remembering', 'p. pr. & vb. n.', 'of Remember');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remember', 'v. t.', 'To have ( a notion or idea) come into the mind again,\n   as previously perceived, known, or felt; to have a renewed apprehension\n   of; to bring to mind again; to think of again; to recollect; as, I\n   remember the fact; he remembers the events of his childhood; I cannot\n   remember dates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remember', 'v. t.', 'To be capable of recalling when required; to keep in\n   mind; to be continually aware or thoughtful of; to preserve fresh in\n   the memory; to attend to; to think of with gratitude, affection,\n   respect, or any other emotion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remember', 'v. t.', 'To put in mind; to remind; -- also used reflexively\n   and impersonally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remember', 'v. t.', 'To mention.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remember', 'v. t.', 'To recall to the mind of another, as in the friendly\n   messages, remember me to him, he wishes to be remembered to you, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remember', 'v. i.', 'To execise or have the power of memory; as, some\n   remember better than others.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rememberable', 'a.', 'Capable or worthy of being remembered.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rememberer', 'n.', 'One who remembers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remembrance', 'n.', 'The act of remembering; a holding in mind, or bringing\n   to mind; recollection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remembrance', 'n.', 'The state of being remembered, or held in mind;\n   memory; recollection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remembrance', 'n.', 'Something remembered; a person or thing kept in\n   memory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remembrance', 'n.', 'That which serves to keep in or bring to mind; a\n   memorial; a token; a memento; a souvenir; a memorandum or note of\n   something to be remembered.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remembrance', 'n.', 'Something to be remembered; counsel; admoni//on;\n   instruction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remembrance', 'n.', 'Power of remembering; reach of personal knowledge;\n   period over which ones memory extends.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remembrancer', 'n.', 'One who, or that which, serves to bring to, or keep\n   in, mind; a memento; a memorial; a reminder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remembrancer', 'n.', 'A term applied in England to several officers, having\n   various functions, their duty originally being to bring certain matters\n   to the attention of the proper persons at the proper time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rememorate', 'v. i.', 'To recall something by means of memory; to remember.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rememoration', 'n.', 'A recalling by the faculty of memory; remembrance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rememorative', 'a.', 'Tending or serving to remind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remenant', 'n.', 'A remnant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remercie', 'v. t.', 'Alt. of Remercy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remercy', 'v. t.', 'To thank.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remerge', 'v. i.', 'To merge again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remeve', 'v. t. & i.', 'Alt. of Remewe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remewe', 'v. t. & i.', 'To remove.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remiform', 'a.', 'Shaped like an oar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remiges', 'n. pl.', 'The quill feathers of the wings of a bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remigrate', 'v. i.', 'To migrate again; to go back; to return.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remigration', 'n.', 'Migration back to the place from which one came.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remind', 'v. t.', 'To put (one) in mind of something; to bring to the\n   remembrance of; to bring to the notice or consideration of (a person).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reminder', 'n.', 'One who, or that which, reminds; that which serves to\n   awaken remembrance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remindful', 'a.', 'Tending or adapted to remind; careful to remind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reminiscence', 'n.', 'The act or power of recalling past experience; the\n   state of being reminiscent; remembrance; memory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reminiscence', 'n.', 'That which is remembered, or recalled to mind; a\n   statement or narration of remembered experience; a recollection; as,\n   pleasing or painful reminiscences.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reminiscency', 'n.', 'Reminiscence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reminiscent', 'a.', 'Recalling to mind, or capable of recalling to mind;\n   having remembrance; reminding one of something.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reminiscent', 'n.', 'One who is addicted to indulging, narrating, or\n   recording reminiscences.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reminiscential', 'a.', 'Of or pertaining to reminiscence, or remembrance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remiped', 'a.', 'Having feet or legs that are used as oars; -- said of\n   certain crustaceans and insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remiped', 'n.', 'An animal having limbs like oars, especially one of\n   certain crustaceans.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remiped', 'n.', 'One of a group of aquatic beetles having tarsi adapted for\n   swimming. See Water beetle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remised', 'imp. & p. p.', 'of Remise');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remising', 'p. pr. & vb. n.', 'of Remise');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remise', 'v. t.', 'To send, give, or grant back; to release a claim to; to\n   resign or surrender by deed; to return.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remise', 'n.', 'A giving or granting back; surrender; return; release, as\n   of a claim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remiss', 'a.', 'Not energetic or exact in duty or business; not careful or\n   prompt in fulfilling engagements; negligent; careless; tardy;\n   behindhand; lagging; slack; hence, lacking earnestness or activity;\n   languid; slow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remiss', 'n.', 'The act of being remiss; inefficiency; failure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remissful', 'a.', 'Inclined to remit punishment; lenient; clement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remissibility', 'n.', 'The state or quality of being remissible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remissible', 'a.', 'Capable of being remitted or forgiven.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remission', 'n.', 'The act of remitting, surrendering, resigning, or giving\n   up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remission', 'n.', 'Discharge from that which is due; relinquishment of a\n   claim, right, or obligation; pardon of transgression; release from\n   forfeiture, penalty, debt, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remission', 'n.', 'Diminution of intensity; abatement; relaxation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remission', 'n.', 'A temporary and incomplete subsidence of the force or\n   violence of a disease or of pain, as destinguished from intermission,\n   in which the disease completely leaves the patient for a time;\n   abatement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remission', 'n.', 'The act of sending back.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remission', 'n.', 'Act of sending in payment, as money; remittance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remissive', 'a.', 'Remitting; forgiving; abating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remissly', 'adv.', 'In a remiss or negligent manner; carelessly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remissness', 'n.', 'Quality or state of being remiss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remissory', 'a.', 'Serving or tending to remit, or to secure remission;\n   remissive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remitted', 'imp. & p. p.', 'of Remit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remitting', 'p. pr. & vb. n.', 'of Remit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remit', 'v. t.', 'To send back; to give up; to surrender; to resign.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remit', 'v. t.', 'To restore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remit', 'v. t.', 'To transmit or send, esp. to a distance, as money in\n   payment of a demand, account, draft, etc.; as, he remitted the amount\n   by mail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remit', 'v. t.', 'To send off or away; hence: (a) To refer or direct (one)\n   for information, guidance, help, etc. \"Remitting them . . . to the\n   works of Galen.\" Sir T. Elyot. (b) To submit, refer, or leave\n   (something) for judgment or decision.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remit', 'v. t.', 'To relax in intensity; to make less violent; to abate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remit', 'v. t.', 'To forgive; to pardon; to remove.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remit', 'v. t.', 'To refrain from exacting or enforcing; as, to remit the\n   performance of an obligation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remit', 'v. i.', 'To abate in force or in violence; to grow less intense;\n   to become moderated; to abate; to relax; as, a fever remits; the\n   severity of the weather remits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remit', 'v. i.', 'To send money, as in payment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remitment', 'n.', 'The act of remitting, or the state of being remitted;\n   remission.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remittal', 'n.', 'A remitting; a giving up; surrender; as, the remittal of\n   the first fruits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remittance', 'n.', 'The act of transmitting money, bills, or the like, esp.\n   to a distant place, as in satisfaction of a demand, or in discharge of\n   an obligation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remittance', 'n.', 'The sum or thing remitted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remittee', 'n.', 'One to whom a remittance is sent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remittent', 'a.', 'Remitting; characterized by remission; having\n   remissions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remitter', 'n.', 'One who remits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remitter', 'n.', 'One who pardons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remitter', 'n.', 'One who makes remittance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remitter', 'n.', 'The sending or placing back of a person to a title or\n   right he had before; the restitution of one who obtains possession of\n   property under a defective title, to his rights under some valid title\n   by virtue of which he might legally have entered into possession only\n   by suit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remittitur', 'n.', 'A remission or surrender, -- remittitur damnut being a\n   remission of excess of damages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remittitur', 'n.', 'A sending back, as when a record is remitted by a\n   superior to an inferior court.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remittor', 'n.', 'One who makes a remittance; a remitter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remix', 'v. t.', 'To mix again or repeatedly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remnant', 'a.', 'Remaining; yet left.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remnant', 'a.', 'That which remains after a part is removed, destroyed,\n   used up, performed, etc.; residue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remnant', 'a.', 'A small portion; a slight trace; a fragment; a little bit;\n   a scrap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remnant', 'a.', 'An unsold end of piece goods, as cloth, ribbons, carpets,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remodel', 'v. t.', 'To model or fashion anew; to change the form of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remodification', 'n.', 'The act of remodifying; the state of being\n   remodified.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remodify', 'v. t.', 'To modify again or anew; to reshape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remolade', 'n.', 'Alt. of Remoulad');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remoulad', 'n.', 'A kind of piquant sauce or salad dressing resembling\n   mayonnaise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remold', 'v. t.', 'Alt. of Remould');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remould', 'v. t.', 'To mold or shape anew or again; to reshape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remollient', 'a.', 'Mollifying; softening.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remonetization', 'n.', 'The act of remonetizing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remonetize', 'v. t.', 'To restore to use as money; as, to remonetize\n   silver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remonstrance', 'n.', 'The act of remonstrating');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remonstrance', 'n.', 'A pointing out; manifestation; proof; demonstration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remonstrance', 'n.', 'Earnest presentation of reason in opposition to\n   something; protest; expostulation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remonstrance', 'n.', 'Same as Monstrance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remonstrant', 'a.', 'Inclined or tending to remonstrate; expostulatory;\n   urging reasons in opposition to something.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remonstrant', 'n.', 'One who remonstrates');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remonstrant', 'n.', 'one of the Arminians who remonstrated against the\n   attacks of the Calvinists in 1610, but were subsequently condemned by\n   the decisions of the Synod of Dort in 1618. See Arminian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remonstrantly', 'adv.', 'In a remonstrant manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remonstrated', 'imp. & p. p.', 'of Remonstrate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remonstrating', 'p. pr. & vb. n.', 'of Remonstrate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remonstrate', 'v. t.', 'To point out; to show clearly; to make plain or\n   manifest; hence, to prove; to demonstrate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remonstrate', 'v. i.', 'To present and urge reasons in opposition to an\n   act, measure, or any course of proceedings; to expostulate; as, to\n   remonstrate with a person regarding his habits; to remonstrate against\n   proposed taxation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remonstration', 'n.', 'The act of remonstrating; remonstrance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remonstrative', 'a.', 'Having the character of a remonstrance; expressing\n   remonstrance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remonstrator', 'n.', 'One who remonstrates; a remonsrant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remontant', 'a.', 'Rising again; -- applied to a class of roses which bloom\n   more than once in a season; the hybrid perpetual roses, of which the\n   Jacqueminot is a well-known example.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remontoir', 'n.', 'See under Escapement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remora', 'n.', 'Delay; obstacle; hindrance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remora', 'n.', 'Any one of several species of fishes belonging to Echeneis,\n   Remora, and allied genera. Called also sucking fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remora', 'n.', 'An instrument formerly in use, intended to retain parts in\n   their places.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remorate', 'v. t.', 'To hinder; to delay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remord', 'v. t.', 'To excite to remorse; to rebuke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remord', 'v. i.', 'To feel remorse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remordency', 'n.', 'Remorse; compunction; compassion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remorse', 'n.', 'The anguish, like gnawing pain, excited by a sense of\n   guilt; compunction of conscience for a crime committed, or for the sins\n   of ones past life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remorse', 'n.', 'Sympathetic sorrow; pity; compassion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remorsed', 'a.', 'Feeling remorse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remorseful', 'a.', 'Full of remorse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remorseful', 'a.', 'Compassionate; feeling tenderly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remorseful', 'a.', 'Exciting pity; pitiable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remorseless', 'a.', 'Being without remorse; having no pity; hence,\n   destitute of sensibility; cruel; insensible to distress; merciless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remote', 'superl.', 'Removed to a distance; not near; far away; distant; --\n   said in respect to time or to place; as, remote ages; remote lands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remote', 'superl.', 'Hence, removed; not agreeing, according, or being\n   related; -- in various figurative uses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remote', 'superl.', 'Not agreeing; alien; foreign.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remote', 'superl.', 'Not nearly related; not close; as, a remote connection\n   or consanguinity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remote', 'superl.', 'Separate; abstracted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remote', 'superl.', 'Not proximate or acting directly; primary; distant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remote', 'superl.', 'Not obvious or sriking; as, a remote resemblance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remote', 'superl.', 'Separated by intervals greater than usual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remotion', 'n.', 'The act of removing; removal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remotion', 'n.', 'The state of being remote; remoteness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remould', 'v. t.', 'See Remold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remount', 'v. t. & i.', 'To mount again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remount', 'n.', 'The opportunity of, or things necessary for, remounting;\n   specifically, a fresh horse, with his equipments; as, to give one a\n   remount.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Removable', 'a.', 'Admitting of being removed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Removal', 'n.', 'The act of removing, or the state of being removed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Removed', 'imp. & p. p.', 'of Remove');
  INSERT INTO words (word, word_type, description) VALUES  (    'Removing', 'p. pr. & vb. n.', 'of Remove');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remove', 'v. t.', 'To move away from the position occupied; to cause to\n   change place; to displace; as, to remove a building.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remove', 'v. t.', 'To cause to leave a person or thing; to cause to cease\n   to be; to take away; hence, to banish; to destroy; to put an end to; to\n   kill; as, to remove a disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remove', 'v. t.', 'To dismiss or discharge from office; as, the President\n   removed many postmasters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remove', 'v. i.', 'To change place in any manner, or to make a change in\n   place; to move or go from one residence, position, or place to another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remove', 'n.', 'The act of removing; a removal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remove', 'n.', 'The transfer of ones business, or of ones domestic\n   belongings, from one location or dwelling house to another; -- in the\n   United States usually called a move.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remove', 'n.', 'The state of being removed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remove', 'n.', 'That which is removed, as a dish removed from table to make\n   room for something else.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remove', 'n.', 'The distance or space through which anything is removed;\n   interval; distance; stage; hence, a step or degree in any scale of\n   gradation; specifically, a division in an English public school; as,\n   the boy went up two removes last year.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remove', 'n.', 'The act of resetting a horses shoe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Removed', 'a.', 'Changed in place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Removed', 'a.', 'Dismissed from office.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Removed', 'a.', 'Distant in location; remote.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Removed', 'a.', 'Distant by degrees in relationship; as, a cousin once\n   removed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remover', 'n.', 'One who removes; as, a remover of landmarks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remuable', 'a.', 'That may be removed; removable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remue', 'v. t.', 'To remove.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remugient', 'a.', 'Rebellowing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remunerable', 'a.', 'Admitting, or worthy, of remuneration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remunerated', 'imp. & p. p.', 'of Remunerate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remunerating', 'p. pr. & vb. n.', 'of Remunerate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remunerate', 'v. t.', 'To pay an equivalent to for any service, loss,\n   expense, or other sacrifice; to recompense; to requite; as, to\n   remunerate men for labor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remuneration', 'n.', 'The act of remunerating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remuneration', 'n.', 'That which is given to remunerate; an equivalent\n   given, as for services, loss, or sufferings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remunerative', 'a.', 'Affording remuneration; as, a remunerative payment\n   for services; a remunerative business.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remuneratory', 'a.', 'Remunerative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Remurmur', 'v. t. & i.', 'To murmur again; to utter back, or reply, in\n   murmurs.');
