  INSERT INTO words (word, word_type, description) VALUES  (    'Repleader', 'n.', 'A second pleading, or course of pleadings; also, the\n   right of pleading again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Replenished', 'imp. & p. p.', 'of Replenish');
  INSERT INTO words (word, word_type, description) VALUES  (    'Replenishing', 'p. pr. & vb. n.', 'of Replenish');
  INSERT INTO words (word, word_type, description) VALUES  (    'Replenish', 'v. t.', 'To fill again after having been diminished or\n   emptied; to stock anew; hence, to fill completely; to cause to abound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Replenish', 'v. t.', 'To finish; to complete; to perfect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Replenish', 'v. i.', 'To recover former fullness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Replenisher', 'n.', 'One who replenishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Replenishment', 'n.', 'The act of replenishing, or the state of being\n   replenished.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Replenishment', 'n.', 'That which replenishes; supply.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Replete', 'a.', 'Filled again; completely filled; full; charged; abounding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Replete', 'v. t.', 'To fill completely, or to satiety.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Repleteness', 'n.', 'The state of being replete.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Repletion', 'n.', 'The state of being replete; superabundant fullness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Repletion', 'n.', 'Fullness of blood; plethora.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Repletive', 'a.', 'Tending to make replete; filling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Repletory', 'a.', 'Repletive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Repleviable', 'a.', 'Capable of being replevied.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Replevin', 'n.', 'A personal action which lies to recover possession of\n   goods and chattle wrongfully taken or detained. Originally, it was a\n   remedy peculiar to cases for wrongful distress, but it may generally\n   now be brought in all cases of wrongful taking or detention.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Replevin', 'n.', 'The writ by which goods and chattels are replevied.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Replevin', 'v. t.', 'To replevy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Replevisable', 'a.', 'Repleviable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Replevied', 'imp. & p. p.', 'of Replevy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Replevying', 'p. pr. & vb. n.', 'of Replevy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Replevy', 'v. t.', 'To take or get back, by a writ for that purpose (goods\n   and chattels wrongfully taken or detained), upon giving security to try\n   the right to them in a suit at law, and, if that should be determined\n   against the plaintiff, to return the property replevied.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Replevy', 'v. t.', 'To bail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Replevy', 'n.', 'Replevin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Replica', 'v. & n.', 'A copy of a work of art, as of a picture or statue,\n   made by the maker of the original.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Replica', 'v. & n.', 'Repetition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Replicant', 'n.', 'One who replies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Replicate', 'v. t.', 'To reply.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Replicate', 'a.', 'Alt. of Replicated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Replicated', 'a.', 'Folded over or backward; folded back upon itself; as, a\n   replicate leaf or petal; a replicate margin of a shell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Replication', 'n.', 'An answer; a reply.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Replication', 'n.', 'The reply of the plaintiff, in matters of fact, to the\n   defendants plea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Replication', 'n.', 'Return or repercussion, as of sound; echo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Replication', 'n.', 'A repetition; a copy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Replier', 'n.', 'One who replies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Replum', 'n.', 'The framework of some pods, as the cress, which remains\n   after the valves drop off.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Replied', 'imp. & p. p.', 'of Reply');
  INSERT INTO words (word, word_type, description) VALUES  (    'Replying', 'p. pr. & vb. n.', 'of Reply');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reply', 'v. i.', 'To make a return in words or writing; to respond; to\n   answer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reply', 'v. i.', 'To answer a defendants plea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reply', 'v. i.', 'Figuratively, to do something in return for something\n   done; as, to reply to a signal; to reply to the fire of a battery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reply', 'v. t.', 'To return for an answer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Replies', 'pl. ', 'of Reply');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reply', 'v. i.', 'That which is said, written, or done in answer to what is\n   said, written, or done by another; an answer; a response.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Replyer', 'n.', 'See Replier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Repolish', 'v. t.', 'To polish again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Repone', 'v. t.', 'To replace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Repopulation', 'n.', 'The act of repeopling; act of furnishing with a\n   population anew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reported', 'imp. & p. p.', 'of Report');
  INSERT INTO words (word, word_type, description) VALUES  (    'Report', 'v. t.', 'To refer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Report', 'v. t.', 'To bring back, as an answer; to announce in return; to\n   relate, as what has been discovered by a person sent to examine,\n   explore, or investigate; as, a messenger reports to his employer what\n   he has seen or ascertained; the committee reported progress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Report', 'v. t.', 'To give an account of; to relate; to tell; to circulate\n   publicly, as a story; as, in the common phrase, it is reported.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Report', 'v. t.', 'To give an official account or statement of; as, a\n   treasurer reports the receipts and expenditures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Report', 'v. t.', 'To return or repeat, as sound; to echo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Report', 'v. t.', 'To return or present as the result of an examination or\n   consideration of any matter officially referred; as, the committee\n   reported the bill witth amendments, or reported a new bill, or reported\n   the results of an inquiry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Report', 'v. t.', 'To make minutes of, as a speech, or the doings of a\n   public body; to write down from the lips of a speaker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Report', 'v. t.', 'To write an account of for publication, as in a\n   newspaper; as, to report a public celebration or a horse race.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Report', 'v. t.', 'To make a statement of the conduct of, especially in an\n   unfavorable sense; as, to report a servant to his employer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Report', 'v. i.', 'To make a report, or response, in respect of a matter\n   inquired of, a duty enjoined, or information expected; as, the\n   committee will report at twelve oclock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Report', 'v. i.', 'To furnish in writing an account of a speech, the\n   proceedings at a meeting, the particulars of an occurrence, etc., for\n   publication.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Report', 'v. i.', 'To present ones self, as to a superior officer, or to\n   one to whom service is due, and to be in readiness for orders or to do\n   service; also, to give information, as of ones address, condition,\n   etc.; as, the officer reported to the general for duty; to report\n   weekly by letter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Report', 'v. t.', 'That which is reported.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Report', 'v. t.', 'An account or statement of the results of examination or\n   inquiry made by request or direction; relation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Report', 'v. t.', 'A story or statement circulating by common talk; a\n   rumor; hence, fame; repute; reputation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Report', 'v. t.', 'Sound; noise; as, the report of a pistol or cannon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Report', 'v. t.', 'An official statement of facts, verbal or written;\n   especially, a statement in writing of proceedings and facts exhibited\n   by an officer to his superiors; as, the reports of the heads af\n   departments to Congress, of a master in chancery to the court, of\n   committees to a legislative body, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Report', 'v. t.', 'An account or statement of a judicial opinion or\n   decision, or of case argued and determined in a court of law, chancery,\n   etc.; also, in the plural, the volumes containing such reports; as,\n   Cokes Reports.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Report', 'v. t.', 'A sketch, or a fully written account, of a speech,\n   debate, or the proceedings of a public meeting, legislative body, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Report', 'v. t.', 'Rapport; relation; connection; reference.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reportable', 'a.', 'Capable or admitting of being reported.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reportage', 'n.', 'SAme as Report.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reporter', 'n.', 'One who reports.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reporter', 'n.', 'An officer or person who makes authorized statements of\n   law proceedings and decisions, or of legislative debates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reporter', 'n.', 'One who reports speeches, the proceedings of public\n   meetings, news, etc., for the newspapers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reportingly', 'adv.', 'By report or common fame.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reportorial', 'a.', 'Of or pertaining to a reporter or reporters; as, the\n   reportorial staff of a newspaper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reposal', 'n.', 'The act or state of reposing; as, the reposal of a trust.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reposal', 'n.', 'That on which one reposes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reposance', 'n.', 'Reliance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reposed', 'imp. & p. p.', 'of Repose');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reposing', 'p. pr. & vb. n.', 'of Repose');
  INSERT INTO words (word, word_type, description) VALUES  (    'Repose', 'v.', 'To cause to stop or to rest after motion; hence, to\n   deposit; to lay down; to lodge; to reposit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Repose', 'v.', 'To lay at rest; to cause to be calm or quiet; to compose;\n   to rest, -- often reflexive; as, to repose ones self on a couch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Repose', 'v.', 'To place, have, or rest; to set; to intrust.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Repose', 'v. i.', 'To lie at rest; to rest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Repose', 'v. i.', 'Figuratively, to remain or abide restfully without\n   anxiety or alarms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Repose', 'v. i.', 'To lie; to be supported; as, trap reposing on sand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Repose', 'v.', 'A lying at rest; sleep; rest; quiet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Repose', 'v.', 'Rest of mind; tranquillity; freedom from uneasiness; also,\n   a composed manner or deportment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Repose', 'v.', 'A rest; a pause.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Repose', 'v.', 'That harmony or moderation which affords rest for the eye;\n   -- opposed to the scattering and division of a subject into too many\n   unconnected parts, and also to anything which is overstrained; as, a\n   painting may want repose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reposed', 'a.', 'Composed; calm; tranquil; at rest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reposeful', 'a.', 'Full of repose; quiet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reposer', 'n.', 'One who reposes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reposited', 'imp. & p. p.', 'of Reposit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Repositing', 'p. pr. & vb. n.', 'of Reposit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reposit', 'v. t.', 'To cause to rest or stay; to lay away; to lodge, as for\n   safety or preservation; to place; to store.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reposition', 'n.', 'The act of repositing; a laying up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Repositor', 'n.', 'An instrument employed for replacing a displaced organ\n   or part.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Repository', 'n.', 'A place where things are or may be reposited, or laid\n   up, for safety or preservation; a depository.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Repossess', 'v. t.', 'To possess again; as, to repossess the land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Repossession', 'n.', 'The act or the state of possessing again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reposure', 'n.', 'Rest; quiet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Repour', 'v. t.', 'To pour again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Repousse', 'a.', 'Formed in relief, as a pattern on metal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Repousse', 'a.', 'Ornamented with patterns in relief made by pressing or\n   hammering on the reverse side; -- said of thin metal, or of a vessel\n   made of thin metal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Repousse', 'n.', 'Repousse work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reprefe', 'n.', 'Reproof.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reprehended', 'imp. & p. p.', 'of Reprehend');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reprehending', 'p. pr. & vb. n.', 'of Reprehend');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reprehend', 'v. t.', 'To reprove or reprimand with a view of restraining,\n   checking, or preventing; to make charge of fault against; to disapprove\n   of; to chide; to blame; to censure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reprehender', 'n.', 'One who reprehends.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reprehensible', 'a.', 'Worthy of reprehension; culpable; censurable;\n   blamable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reprehension', 'n.', 'Reproof; censure; blame; disapproval.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reprehensive', 'a.', 'Containing reprehension; conveying reproof.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reprehensory', 'a.', 'Containing reproof; reprehensive; as, reprehensory\n   complaint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Re-present', 'v. t.', 'To present again; as, to re-present the points of an\n   argument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Represent', 'v. t.', 'To present again or anew; to present by means of\n   something standing in the place of; to exhibit the counterpart or image\n   of; to typify.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Represent', 'v. t.', 'To portray by pictoral or plastic art; to delineate;\n   as, to represent a landscape in a picture, a horse in bronze, and the\n   like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Represent', 'v. t.', 'To portray by mimicry or action of any kind; to act\n   the part or character of; to personate; as, to represent Hamlet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Represent', 'v. t.', 'To stand in the place of; to supply the place,\n   perform the duties, exercise the rights, or receive the share, of; to\n   speak and act with authority in behalf of; to act the part of\n   (another); as, an heir represents his ancestor; an attorney represents\n   his client in court; a member of Congress represents his district in\n   Congress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Represent', 'v. t.', 'To exhibit to another mind in language; to show; to\n   give ones own impressions and judgement of; to bring before the mind;\n   to set forth; sometimes, to give an account of; to describe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Represent', 'v. t.', 'To serve as a sign or symbol of; as, mathematical\n   symbols represent quantities or relations; words represent ideas or\n   things.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Represent', 'v. t.', 'To bring a sensation of into the mind or sensorium;\n   to cause to be known, felt, or apprehended; to present.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Represent', 'v. t.', 'To form or image again in consciousness, as an object\n   of cognition or apprehension (something which was originally\n   apprehended by direct presentation). See Presentative, 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Representable', 'a.', 'Capable of being represented.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Representance', 'n.', 'Representation; likeness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Representant', 'a.', 'Appearing or acting for another; representing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Representant', 'n.', 'A representative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Representation', 'n.', 'The act of representing, in any sense of the verb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Representation', 'n.', 'That which represents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Representation', 'n.', 'A likeness, a picture, or a model; as, a\n   representation of the human face, or figure, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Representation', 'n.', 'A dramatic performance; as, a theatrical\n   representation; a representation of Hamlet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Representation', 'n.', 'A description or statement; as, the representation\n   of an historian, of a witness, or an advocate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Representation', 'n.', 'The body of those who act as representatives of a\n   community or society; as, the representation of a State in Congress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Representation', 'n.', 'Any collateral statement of fact, made orally or in\n   writing, by which an estimate of the risk is affected, or either party\n   is influenced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Representation', 'n.', 'The state of being represented.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Re-presentation', 'n.', 'The act of re-presenting, or the state of being\n   presented again; a new presentation; as, re-presentation of facts\n   previously stated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Representationary', 'a.', 'Implying representation; representative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Representative', 'a.', 'Fitted to represent; exhibiting a similitude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Representative', 'a.', 'Bearing the character or power of another; acting\n   for another or others; as, a council representative of the people.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Representative', 'a.', 'Conducted by persons chosen to represent, or act as\n   deputies for, the people; as, a representative government.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Representative', 'a.', 'Serving or fitted to present the full characters of\n   the type of a group; typical; as, a representative genus in a family.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Representative', 'a.', 'Similar in general appearance, structure, and\n   habits, but living in different regions; -- said of certain species and\n   varieties.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Representative', 'a.', 'Giving, or existing as, a transcript of what was\n   originally presentative knowledge; as, representative faculties;\n   representative knowledge. See Presentative, 3 and Represent, 8.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Representative', 'n.', 'One who, or that which, represents (anything); that\n   which exhibits a likeness or similitude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Representative', 'n.', 'An agent, deputy, or substitute, who supplies the\n   place of another, or others, being invested with his or their\n   authority.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Representative', 'n.', 'One who represents, or stands in the place of,\n   another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Representative', 'n.', 'A member of the lower or popular house in a State\n   legislature, or in the national Congress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Representative', 'n.', 'That which presents the full character of the type\n   of a group.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Representative', 'n.', 'A species or variety which, in any region, takes\n   the place of a similar one in another region.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Representatively', 'adv.', 'In a representative manner; vicariously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Representativeness', 'n.', 'The quality or state of being representative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Representer', 'n.', 'One who shows, exhibits, or describes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Representer', 'n.', 'A representative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Representment', 'n.', 'Representation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Repress', 'v. t.', 'To press again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Repress', 'v. t.', 'To press back or down effectually; to crush down or\n   out; to quell; to subdue; to supress; as, to repress sedition or\n   rebellion; to repress the first risings of discontent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Repress', 'v. t.', 'Hence, to check; to restrain; to keep back.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Repress', 'n.', 'The act of repressing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Represser', 'n.', 'One who, or that which, represses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Repressible', 'a.', 'Capable of being repressed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Repression', 'n.', 'The act of repressing, or state of being repressed; as,\n   the repression of evil and evil doers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Repression', 'n.', 'That which represses; check; restraint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Repressive', 'a.', 'Having power, or tending, to repress; as, repressive\n   acts or measures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reprevable', 'a.', 'Reprovable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Repreve', 'v. t.', 'To reprove.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Repreve', 'n.', 'Reproof.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Repriefe', 'n.', 'Repreve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reprieval', 'n.', 'Reprieve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reprieved', 'imp. & p. p.', 'of Reprieve');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reprieving', 'p. pr. & vb. n.', 'of Reprieve');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reprieve', 'v. t.', 'To delay the punishment of; to suspend the execution\n   of sentence on; to give a respite to; to respite; as, to reprieve a\n   criminal for thirty days.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reprieve', 'v. t.', 'To relieve for a time, or temporarily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reprieve', 'n.', 'A temporary suspension of the execution of a sentence,\n   especially of a sentence of death.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reprieve', 'n.', 'Interval of ease or relief; respite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reprimand', 'n.', 'Severe or formal reproof; reprehension, private or\n   public.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reprimanded', 'imp. & p. p.', 'of Reprimand');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reprimanding', 'p. pr. & vb. n.', 'of Reprimand');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reprimand', 'n.', 'To reprove severely; to reprehend; to chide for a fault;\n   to consure formally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reprimand', 'n.', 'To reprove publicly and officially, in execution of a\n   sentence; as, the court ordered him to be reprimanded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reprimander', 'n.', 'One who reprimands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reprimer', 'n.', 'A machine or implement for applying fresh primers to\n   spent cartridge shells, so that the shells be used again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reprint', 'v. t.', 'To print again; to print a second or a new edition of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reprint', 'v. t.', 'To renew the impression of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reprint', 'n.', 'A second or a new impression or edition of any printed\n   work; specifically, the publication in one country of a work previously\n   published in another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reprinter', 'n.', 'One who reprints.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reprisal', 'n.', 'The act of taking from an enemy by way of reteliation or\n   indemnity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reprisal', 'n.', 'Anything taken from an enemy in retaliation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reprisal', 'n.', 'The act of retorting on an enemy by inflicting suffering\n   or death on a prisoner taken from him, in retaliation for an act of\n   inhumanity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reprisal', 'n.', 'Any act of retaliation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reprise', 'n.', 'A taking by way of retaliation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reprise', 'n.', 'Deductions and duties paid yearly out of a manor and\n   lands, as rent charge, rent seck, pensions, annuities, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reprise', 'n.', 'A ship recaptured from an enemy or from a pirate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reprise', 'v. t.', 'To take again; to retake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reprise', 'v. t.', 'To recompense; to pay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Repristinate', 'v. t.', 'To restore to an original state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Repristination', 'n.', 'Restoration to an original state; renewal of\n   purity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reprive', 'v. t.', 'To take back or away.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reprive', 'v. t.', 'To reprieve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reprize', 'v. t.', 'See Reprise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reprizes', 'n. pl.', 'See Reprise, n., 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reproached', 'imp. & p. p.', 'of Reproach');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reproaching', 'p. pr. & vb. n.', 'of Reproach');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reproach', 'v. t.', 'To come back to, or come home to, as a matter of\n   blame; to bring shame or disgrace upon; to disgrace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reproach', 'v. t.', 'To attribute blame to; to allege something disgraceful\n   against; to charge with a fault; to censure severely or contemptuously;\n   to upbraid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reproach', 'v.', 'The act of reproaching; censure mingled with contempt;\n   contumelious or opprobrious language toward any person; abusive\n   reflections; as, severe reproach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reproach', 'v.', 'A cause of blame or censure; shame; disgrace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reproach', 'v.', 'An object of blame, censure, scorn, or derision.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reproachablr', 'a.', 'Deserving reproach; censurable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reproachablr', 'a.', 'Opprobrius; scurrilous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reproacher', 'n.', 'One who reproaches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reproachful', 'a.', 'Expressing or containing reproach; upbraiding;\n   opprobrious; abusive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reproachful', 'a.', 'Occasioning or deserving reproach; shameful; base; as,\n   a reproachful life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reproachless', 'a.', 'Being without reproach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reprobacy', 'n.', 'Reprobation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reprobance', 'n.', 'Reprobation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reprobate', 'a.', 'Not enduring proof or trial; not of standard purity or\n   fineness; disallowed; rejected.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reprobate', 'a.', 'Abandoned to punishment; hence, morally abandoned and\n   lost; given up to vice; depraved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reprobate', 'a.', 'Of or pertaining to one who is given up to wickedness;\n   as, reprobate conduct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reprobate', 'n.', 'One morally abandoned and lost.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reprobated', 'imp. & p. p.', 'of Reprobate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reprobating', 'p. pr. & vb. n.', 'of Reprobate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reprobate', 'v. t.', 'To disapprove with detestation or marks of extreme\n   dislike; to condemn as unworthy; to disallow; to reject.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reprobate', 'v. t.', 'To abandon to punishment without hope of pardon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reprobateness', 'n.', 'The state of being reprobate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reprobater', 'n.', 'One who reprobates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reprobation', 'n.', 'The act of reprobating; the state of being reprobated;\n   strong disapproval or censure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reprobation', 'n.', 'The predestination of a certain number of the human\n   race as reprobates, or objects of condemnation and punishment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reprobationer', 'n.', 'One who believes in reprobation. See Reprobation, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reprobative', 'a.', 'Of or pertaining to reprobation; expressing\n   reprobation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reprobatory', 'a.', 'Reprobative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reproduce', 'v. t.', 'To produce again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reproduce', 'v. t.', 'To bring forward again; as, to reproduce a witness;\n   to reproduce charges; to reproduce a play.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reproduce', 'v. t.', 'To cause to exist again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reproduce', 'v. t.', 'To produce again, by generation or the like; to cause\n   the existence of (something of the same class, kind, or nature as\n   another thing); to generate or beget, as offspring; as, to reproduce a\n   rose; some animals are reproduced by gemmation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reproduce', 'v. t.', 'To make an image or other representation of; to\n   portray; to cause to exist in the memory or imagination; to make a copy\n   of; as, to reproduce a persons features in marble, or on canvas; to\n   reproduce a design.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reproducer', 'n.', 'One who, or that which, reproduces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reproduction', 'n.', 'The act or process of reproducing; the state of being\n   reproduced');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reproduction', 'n.', 'the process by which plants and animals give rise to\n   offspring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reproduction', 'n.', 'That which is reproduced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reproductive', 'a.', 'Tending, or pertaining, to reproduction; employed in\n   reproduction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reproductory', 'a.', 'Reproductive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reproof', 'n.', 'Refutation; confutation; contradiction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reproof', 'n.', 'An expression of blame or censure; especially, blame\n   expressed to the face; censure for a fault; chiding; reproach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reprovable', 'a.', 'Worthy of reproof or censure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Re proval', 'n.', 'Reproof.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reproved', 'imp. & p. p.', 'of Reprove');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reproving', 'p. pr. & vb. n.', 'of Reprove');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reprove', 'v. t.', 'To convince.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reprove', 'v. t.', 'To disprove; to refute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reprove', 'v. t.', 'To chide to the face as blameworthy; to accuse as\n   guilty; to censure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reprove', 'v. t.', 'To express disapprobation of; as, to reprove faults.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reprover', 'n.', 'One who, or that which, reproves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reprovingly', 'adv.', 'In a reproving manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reprune', 'v. t.', 'To prune again or anew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rep-silver', 'n.', 'Money anciently paid by servile tenants to their lord,\n   in lieu of the customary service of reaping his corn or grain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reptant', 'a.', 'Same as Repent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reptant', 'a.', 'Creeping; crawling; -- said of reptiles, worms, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reptantia', 'n. pl.', 'A division of gastropods; the Pectinibranchiata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reptation', 'n.', 'The act of creeping.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reptatory', 'a.', 'Creeping.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reptile', 'a.', 'Creeping; moving on the belly, or by means of small and\n   short legs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reptile', 'a.', 'Hence: Groveling; low; vulgar; as, a reptile race or crew;\n   reptile vices.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reptile', 'n.', 'An animal that crawls, or moves on its belly, as snakes,,\n   or by means of small, short legs, as lizards, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reptile', 'n.', 'One of the Reptilia, or one of the Amphibia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reptile', 'n.', 'A groveling or very mean person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reptilia', 'n. pl.', 'A class of air-breathing oviparous vertebrates,\n   usually covered with scales or bony plates. The heart generally has two\n   auricles and one ventricle. The development of the young is the same as\n   that of birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reptilian', 'a.', 'Belonging to the reptiles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reptilian', 'n.', 'One of the Reptilia; a reptile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Republic', 'a.', 'Common weal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Republic', 'a.', 'A state in which the sovereign power resides in the whole\n   body of the people, and is exercised by representatives elected by\n   them; a commonwealth. Cf. Democracy, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Republican', 'a.', 'Of or pertaining to a republic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Republican', 'a.', 'Consonant with the principles of a republic; as,\n   republican sentiments or opinions; republican manners.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Republican', 'n.', 'One who favors or prefers a republican form of\n   government.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Republican', 'n.', 'A member of the Republican party.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Republican', 'n.', 'The American cliff swallow. The cliff swallows build\n   their nests side by side, many together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Republican', 'n.', 'A South African weaver bird (Philetaerus socius). These\n   weaver birds build many nests together, under a large rooflike shelter,\n   which they make of straw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Republicanism', 'n.', 'A republican form or system of government; the\n   principles or theory of republican government.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Republicanism', 'n.', 'Attachment to, or political sympathy for, a\n   republican form of government.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Republicanism', 'n.', 'The principles and policy of the Republican party,\n   so called');
  INSERT INTO words (word, word_type, description) VALUES  (    'Republicanized', 'imp. & p. p.', 'of Republicanize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Republicanizing', 'p. pr. & vb. n.', 'of Republicanize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Republicanize', 'v. t.', 'To change, as a state, into a republic; to\n   republican principles; as, France was republicanized; to republicanize\n   the rising generation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Republicate', 'v. t.', 'To make public again; to republish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Republication', 'n.', 'A second publication, or a new publication of\n   something before published, as of a former will, of a volume already\n   published, or the like; specifically, the publication in one country of\n   a work first issued in another; a reprint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Republished', 'imp. & p. p.', 'of Republish');
  INSERT INTO words (word, word_type, description) VALUES  (    'Republishing', 'p. pr. & vb. n.', 'of Republish');
  INSERT INTO words (word, word_type, description) VALUES  (    'Republish', 'v. t.', 'To publish anew; specifically, to publish in one\n   country (a work first published in another); also, to revive (a will)\n   by re/xecution or codicil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Republisher', 'n.', 'One who republishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Repudiable', 'a.', 'Admitting of repudiation; fit or proper to be put away.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Repudiated', 'imp. & p. p.', 'of Repudiate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Repudiating', 'p. pr. & vb. n.', 'of Repudiate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Repudiate', 'v. t.', 'To cast off; to disavow; to have nothing to do with;\n   to renounce; to reject.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Repudiate', 'v. t.', 'To divorce, put away, or discard, as a wife, or a\n   woman one has promised to marry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Repudiate', 'v. t.', 'To refuse to acknowledge or to pay; to disclaim; as,\n   the State has repudiated its debts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Repudiation', 'n.', 'The act of repudiating, or the state of being\n   repuddiated; as, the repudiation of a doctrine, a wife, a debt, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Repudiation', 'n.', 'One who favors repudiation, especially of a public\n   debt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Repudiator', 'n.', 'One who repudiates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Repugn', 'v. t.', 'To fight against; to oppose; to resist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Repugnable', 'a.', 'Capable of being repugned or resisted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Repugnance', 'n.', 'Alt. of Repugnancy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Repugnancy', 'n.', 'The state or condition of being repugnant; opposition;\n   contrariety; especially, a strong instinctive antagonism; aversion;\n   reluctance; unwillingness, as of mind, passions, principles, qualities,\n   and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Repugnant', 'a.', 'Disposed to fight against; hostile; at war with; being\n   at variance; contrary; inconsistent; refractory; disobedient; also,\n   distasteful in a high degree; offensive; -- usually followed by to,\n   rarely and less properly by with; as, all rudeness was repugnant to her\n   nature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Repugnantly', 'adv.', 'In a repugnant manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Repugnate', 'v. t.', 'To oppose; to fight against.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Repugner', 'n.', 'One who repugns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Repullulate', 'v. i.', 'To bud again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Repullulation', 'n.', 'The act of budding again; the state of having budded\n   again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Repulsed', 'imp. & p. p.', 'of Repulse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Repulsing', 'p. pr. & vb. n.', 'of Repulse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Repulse', 'v. t.', 'To repel; to beat or drive back; as, to repulse an\n   assault; to repulse the enemy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Repulse', 'v. t.', 'To repel by discourtesy, coldness, or denial; to\n   reject; to send away; as, to repulse a suitor or a proffer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Repulse', 'n.', 'The act of repelling or driving back; also, the state of\n   being repelled or driven back.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Repulse', 'n.', 'Figuratively: Refusal; denial; rejection; failure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Repulseless', 'a.', 'Not capable of being repulsed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Repulser', 'n.', 'One who repulses, or drives back.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Repulsion', 'n.', 'The act of repulsing or repelling, or the state of being\n   repulsed or repelled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Repulsion', 'n.', 'A feeling of violent offence or disgust; repugnance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Repulsion', 'n.', 'The power, either inherent or due to some physical\n   action, by which bodies, or the particles of bodies, are made to recede\n   from each other, or to resist each others nearer approach; as,\n   molecular repulsion; electrical repulsion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Repulsive', 'a.', 'Serving, or able, to repulse; repellent; as, a repulsive\n   force.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Repulsive', 'a.', 'Cold; forbidding; offensive; as, repulsive manners.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Repulsory', 'a.', 'Repulsive; driving back.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Repurchase', 'v. t.', 'To buy back or again; to regain by purchase.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Repurchase', 'n.', 'The act of repurchasing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Repurify', 'v. t.', 'To purify again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reputable', 'a.', 'Having, or worthy of, good repute; held in esteem;\n   honorable; praiseworthy; as, a reputable man or character; reputable\n   conduct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reputation', 'v. t.', 'The estimation in which one is held; character in\n   public opinion; the character attributed to a person, thing, or action;\n   repute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reputation', 'v. t.', 'The character imputed to a person in the community\n   in which he lives. It is admissible in evidence when he puts his\n   character in issue, or when such reputation is otherwise part of the\n   issue of a case.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reputation', 'v. t.', 'Specifically: Good reputation; favorable regard;\n   public esteem; general credit; good name.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reputation', 'v. t.', 'Account; value.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reputatively', 'adv.', 'By repute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reputed', 'imp. & p. p.', 'of Repute');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reputing', 'p. pr. & vb. n.', 'of Repute');
  INSERT INTO words (word, word_type, description) VALUES  (    'Repute', 'v. t.', 'To hold in thought; to account; to estimate; to hold; to\n   think; to reckon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Repute', 'n.', 'Character reputed or attributed; reputation, whether good\n   or bad; established opinion; public estimate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Repute', 'n.', 'Specifically: Good character or reputation; credit or honor\n   derived from common or public opinion; -- opposed to disrepute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reputedly', 'adv.', 'In common opinion or estimation; by repute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reputeless', 'a.', 'Not having good repute; disreputable; disgraceful;\n   inglorius.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Requere', 'v. t.', 'To require.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Request', 'n.', 'The act of asking for anything desired; expression of\n   desire or demand; solicitation; prayer; petition; entreaty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Request', 'n.', 'That which is asked for or requested.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Request', 'n.', 'A state of being desired or held in such estimation as to\n   be sought after or asked for; demand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Requested', 'imp. & p. p.', 'of Request');
  INSERT INTO words (word, word_type, description) VALUES  (    'Requesting', 'p. pr. & vb. n.', 'of Request');
  INSERT INTO words (word, word_type, description) VALUES  (    'Request', 'v. t.', 'To ask for (something); to express desire ffor; to\n   solicit; as, to request his presence, or a favor.');
