  INSERT INTO words (word, word_type, description) VALUES  (    'Reserver', 'n.', 'One who reserves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reservist', 'n.', 'A member of a reserve force of soldiers or militia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reservoir', 'n.', 'A place where anything is kept in store; especially, a\n   place where water is collected and kept for use when wanted, as to\n   supply a fountain, a canal, or a city by means of aqueducts, or to\n   drive a mill wheel, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reservoir', 'n.', 'A small intercellular space, often containing resin,\n   essential oil, or some other secreted matter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reservor', 'n.', 'One who reserves; a reserver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reset', 'v. t.', 'To set again; as, to reset type; to reset copy; to reset\n   a diamond.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reset', 'n.', 'The act of resetting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reset', 'n.', 'That which is reset; matter set up again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reset', 'n.', 'The receiving of stolen goods, or harboring an outlaw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reset', 'v. t.', 'To harbor or secrete; to hide, as stolen goods or a\n   criminal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resetter', 'n.', 'One who receives or conceals, as stolen goods or\n   criminal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resetter', 'n.', 'One who resets, or sets again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resettle', 'v. t.', 'To settle again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resettle', 'v. i.', 'To settle again, or a second time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resettlement', 'n.', 'Act of settling again, or state of being settled\n   again; as, the resettlement of lees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reshape', 'v. t.', 'To shape again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reship', 'v. t.', 'To ship again; to put on board of a vessel a second\n   time; to send on a second voyage; as, to reship bonded merchandise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reship', 'v. i.', 'To engage ones self again for service on board of a\n   vessel after having been discharged.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reshipment', 'n.', 'The act of reshipping; also, that which is reshippped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reshipper', 'n.', 'One who reships.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resiance', 'n.', 'Residence; abode.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resiant', 'a.', 'Resident; present in a place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resiant', 'n.', 'A resident.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resided', 'imp. & p. p.', 'of Reside');
  INSERT INTO words (word, word_type, description) VALUES  (    'Residing', 'p. pr. & vb. n.', 'of Reside');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reside', 'v. i.', 'To dwell permanently or for a considerable time; to have\n   a settled abode for a time; to abide continuosly; to have ones\n   domicile of home; to remain for a long time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reside', 'v. i.', 'To have a seat or fixed position; to inhere; to lie or\n   be as in attribute or element.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reside', 'v. i.', 'To sink; to settle, as sediment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Residence', 'n.', 'The act or fact of residing, abiding, or dwelling in a\n   place for some continuance of time; as, the residence of an American in\n   France or Italy for a year.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Residence', 'n.', 'The place where one resides; an abode; a dwelling or\n   habitation; esp., a settled or permanent home or domicile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Residence', 'n.', 'The residing of an incumbent on his benefice; -- opposed\n   to nonresidence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Residence', 'n.', 'The place where anything rests permanently.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Residence', 'n.', 'Subsidence, as of a sediment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Residence', 'n.', 'That which falls to the bottom of liquors; sediment;\n   also, refuse; residuum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Residency', 'n.', 'Residence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Residency', 'n.', 'A political agency at a native court in British India,\n   held by an officer styled the Resident; also, a Dutch commercial colony\n   or province in the East Indies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resident', 'a.', 'Dwelling, or having an abode, in a place for a continued\n   length of time; residing on ones own estate; -- opposed to\n   nonresident; as, resident in the city or in the country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resident', 'a.', 'Fixed; stable; certain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resident', 'n.', 'One who resides or dwells in a place for some time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resident', 'n.', 'A diplomatic representative who resides at a foreign\n   court; -- a term usualy applied to ministers of a rank inferior to that\n   of ambassadors. See the Note under Minister, 4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Residenter', 'n.', 'A resident.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Residential', 'a.', 'Of or pertaining to a residence or residents; as,\n   residential trade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Residential', 'a.', 'Residing; residentiary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Residentiary', 'a.', 'Having residence; as, a canon residentary; a\n   residentiary guardian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Residentiary', 'n.', 'One who is resident.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Residentiary', 'n.', 'An ecclesiastic who keeps a certain residence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Residentiaryship', 'n.', 'The office or condition of a residentiary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Residentship', 'n.', 'The office or condition of a resident.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resider', 'n.', 'One who resides in a place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Residual', 'a.', 'Pertaining to a residue; remaining after a part is taken.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Residual', 'n.', 'The difference of the results obtained by observation,\n   and by computation from a formula.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Residual', 'n.', 'The difference between the mean of several observations\n   and any one of them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Residuary', 'a.', 'Consisting of residue; as, residuary matter; pertaining\n   to the residue, or part remaining; as, the residuary advantage of an\n   estate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Residue', 'n.', 'That which remains after a part is taken, separated,\n   removed, or designated; remnant; remainder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Residue', 'n.', 'That part of a testeators estate wwhich is not disposed\n   of in his will by particular and special legacies and devises, and\n   which remains after payment of debts and legacies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Residue', 'n.', 'That which remains of a molecule after the removal of a\n   portion of its constituents; hence, an atom or group regarded as a\n   portion of a molecule; -- used as nearly equivalent to radical, but in\n   a more general sense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Residue', 'n.', 'Any positive or negative number that differs from a given\n   number by a multiple of a given modulus; thus, if 7 is the modulus, and\n   9 the given number, the numbers -5, 2, 16, 23, etc., are residues.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Residuous', 'a.', 'Remaining; residual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Residuum', 'n.', 'That which is left after any process of separation or\n   purification; that which remains after certain specified deductions are\n   made; residue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resiege', 'v. t.', 'To seat again; to reinstate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Re-sign', 'v. t.', 'To affix ones signature to, a second time; to sign\n   again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resigned', 'imp. & p. p.', 'of Resign');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resigning', 'p. pr. & vb. n.', 'of Resign');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resign', 'v. t.', 'To sign back; to return by a formal act; to yield to\n   another; to surrender; -- said especially of office or emolument.\n   Hence, to give up; to yield; to submit; -- said of the wishes or will,\n   or of something valued; -- also often used reflexively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resign', 'v. t.', 'To relinquish; to abandon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resign', 'v. t.', 'To commit to the care of; to consign.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Re sign', 'n.', 'Resignation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resignation', 'n.', 'The act of resigning or giving up, as a claim,\n   possession, office, or the like; surrender; as, the resignation of a\n   crown or comission.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resignation', 'n.', 'The state of being resigned or submissive; quiet or\n   patient submission; unresisting acquiescence; as, resignation to the\n   will and providence of God.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resigned', 'a.', 'Submissive; yielding; not disposed to resist or murmur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resignedly', 'adv.', 'With submission.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resignee', 'n.', 'One to whom anything is resigned, or in whose favor a\n   resignation is made.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resigner', 'n.', 'One who resigns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resignment', 'n.', 'The act of resigning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resiled', 'imp. & p. p.', 'of Resile');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resiling', 'p. pr. & vb. n.', 'of Resile');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resile', 'v. i.', 'To start back; to recoil; to recede from a purpose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resilience', 'n.', 'Alt. of Resiliency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resiliency', 'n.', 'The act of resiling, springing back, or rebounding; as,\n   the resilience of a ball or of sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resiliency', 'n.', 'The mechanical work required to strain an elastic body,\n   as a deflected beam, stretched spring, etc., to the elastic limit;\n   also, the work performed by the body in recovering from such strain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resilient', 'a.', 'Leaping back; rebounding; recoiling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resilition', 'n.', 'Resilience.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resin', 'n.', 'Any one of a class of yellowish brown solid inflammable\n   substances, of vegetable origin, which are nonconductors of\n   electricity, have a vitreous fracture, and are soluble in ether,\n   alcohol, and essential oils, but not in water; specif., pine resin (see\n   Rosin).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resinaceous', 'a.', 'Having the quality of resin; resinous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resinate', 'n.', 'Any one of the salts the resinic acids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resinic', 'a.', 'Pertaining to, or obtained from, resin; as, the resinic\n   acids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resiniferous', 'a.', 'Yielding resin; as, a resiniferous tree or vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resiniform', 'a.', 'Having the form of resin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resino-electric', 'a.', 'Containing or exhibiting resinous electricity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resinoid', 'a.', 'Somewhat like resin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resinous', 'a.', 'Of or pertaining to resin; of the nature of resin;\n   resembling or obtained from resin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resinously', 'adv.', 'By means, or in the manner, of resin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resinousness', 'n.', 'The quality of being resinous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resiny', 'a.', 'Like resin; resinous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resipiscence', 'n.', 'Wisdom derived from severe experience; hence,\n   repentance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resisted', 'imp. & p. p.', 'of Resist');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resisting', 'p. pr. & vb. n.', 'of Resist');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resist', 'v. t.', 'To stand against; to withstand; to obstruct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resist', 'v. t.', 'To strive against; to endeavor to counteract, defeat, or\n   frustrate; to act in opposition to; to oppose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resist', 'v. t.', 'To counteract, as a force, by inertia or reaction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resist', 'v. t.', 'To be distasteful to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resist', 'v. i.', 'To make opposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resist', 'n.', 'A substance used to prevent a color or mordant from fixing\n   on those parts to which it has been applied, either by acting\n   machanically in preventing the color, etc., from reaching the cloth, or\n   chemically in changing the color so as to render it incapable of fixing\n   itself in the fibers.. The pastes prepared for this purpose are called\n   resist pastes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resistance', 'n.', 'The act of resisting; opposition, passive or active.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resistance', 'n.', 'The quality of not yielding to force or external\n   pressure; that power of a body which acts in opposition to the impulse\n   or pressure of another, or which prevents the effect of another power;\n   as, the resistance of the air to a body passing through it; the\n   resistance of a target to projectiles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resistance', 'n.', 'A means or method of resisting; that which resists.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resistance', 'n.', 'A certain hindrance or opposition to the passage of an\n   electrical current or discharge offered by conducting bodies. It bears\n   an inverse relation to the conductivity, -- good conductors having a\n   small resistance, while poor conductors or insulators have a very high\n   resistance. The unit of resistance is the ohm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resistant', 'a.', 'Making resistance; resisting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resistant', 'n.', 'One who, or that which, resists.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resister', 'n.', 'One who resists.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resistful', 'a.', 'Making much resistance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resistibility', 'n..', 'The quality of being resistible; resistibleness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resistibility', 'n..', 'The quality of being resistant; resitstance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resistible', 'a.', 'Capable of being resisted; as, a resistible force.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resisting', 'a.', 'Making resistance; opposing; as, a resisting medium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resistive', 'a.', 'Serving to resist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resistless', 'a.', 'Having no power to resist; making no opposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resistless', 'a.', 'Incapable of being resisted; irresistible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resoluble', 'a.', 'Admitting of being resolved; resolvable; as, bodies\n   resoluble by fire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resolute', 'v. t. & i.', 'Having a decided purpose; determined; resolved;\n   fixed in a determination; hence, bold; firm; steady.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resolute', 'v. t. & i.', 'Convinced; satisfied; sure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resolute', 'v. t. & i.', 'Resolving, or explaining; as, the Resolute Doctor\n   Durand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resolute', 'n.', 'One who is resolute; hence, a desperado.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resolute', 'n.', 'Redelivery; repayment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resolutely', 'adv.', 'In a resolute manner; with fixed purpose; boldly;\n   firmly; steadily; with perseverance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resoluteness', 'n.', 'The quality of being resolute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resolution', 'n.', 'The act, operation, or process of resolving.\n   Specifically: (a) The act of separating a compound into its elements or\n   component parts. (b) The act of analyzing a complex notion, or solving\n   a vexed question or difficult problem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resolution', 'n.', 'The state of being relaxed; relaxation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resolution', 'n.', 'The state of being resolved, settled, or determined;\n   firmness; steadiness; constancy; determination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resolution', 'n.', 'That which is resolved or determined; a settled\n   purpose; determination. Specifically: A formal expression of the\n   opinion or will of an official body or a public assembly, adopted by\n   vote; as, a legislative resolution; the resolutions of a public\n   meeting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resolution', 'n.', 'The state of being resolved or firm in opinion or\n   thought; conviction; assurance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resolution', 'n.', 'The act or process of solving; solution; as, the\n   resolution of an equation or problem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resolution', 'n.', 'A breaking up, disappearance; or termination, as of a\n   fever, a tumor, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resolution', 'n.', 'The passing of a dissonant into a consonant chord by\n   the rising or falling of the note which makes the discord.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resolutioner', 'n.', 'One who makes a resolution; one who joins with others\n   in a declaration or resolution; specifically, one of a party in the\n   Scottish Church in the 17th century.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resolutionist', 'n.', 'One who makes a resolution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resolutive', 'a.', 'Serving to dissolve or relax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resolutory', 'a.', 'Resolutive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resolvability', 'n.', 'The quality or condition of being resolvable;\n   resolvableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resolvable', 'a.', 'Admitting of being resolved; admitting separation into\n   constituent parts, or reduction to first principles; admitting solution\n   or explanation; as, resolvable compounds; resolvable ideas or\n   difficulties.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resolvableness', 'n.', 'The quality of being resolvable; resolvability.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resolved', 'imp. & p. p.', 'of Resolve');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resolving', 'p. pr. & vb. n.', 'of Resolve');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resolve', 'v. i.', 'To separate the component parts of; to reduce to the\n   constituent elements; -- said of compound substances; hence, sometimes,\n   to melt, or dissolve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resolve', 'v. i.', 'To reduce to simple or intelligible notions; -- said of\n   complex ideas or obscure questions; to make clear or certain; to free\n   from doubt; to disentangle; to unravel; to explain; hence, to clear up,\n   or dispel, as doubt; as, to resolve a riddle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resolve', 'v. i.', 'To cause to perceive or understand; to acquaint; to\n   inform; to convince; to assure; to make certain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resolve', 'v. i.', 'To determine or decide in purpose; to make ready in\n   mind; to fix; to settle; as, he was resolved by an unexpected event.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resolve', 'v. i.', 'To express, as an opinion or determination, by\n   resolution and vote; to declare or decide by a formal vote; -- followed\n   by a clause; as, the house resolved (or, it was resolved by the house)\n   that no money should be apropriated (or, to appropriate no money).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resolve', 'v. i.', 'To change or convert by resolution or formal vote; --\n   used only reflexively; as, the house resolved itself into a committee\n   of the whole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resolve', 'v. i.', 'To solve, as a problem, by enumerating the several\n   things to be done, in order to obtain what is required; to find the\n   answer to, or the result of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resolve', 'v. i.', 'To dispere or scatter; to discuss, as an inflammation\n   or a tumor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resolve', 'v. i.', 'To let the tones (as of a discord) follow their several\n   tendencies, resulting in a concord.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resolve', 'v. i.', 'To relax; to lay at ease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resolve', 'v. i.', 'To be separated into its component parts or distinct\n   principles; to undergo resolution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resolve', 'v. i.', 'To melt; to dissolve; to become fluid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resolve', 'v. i.', 'To be settled in opinion; to be convinced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resolve', 'v. i.', 'To form a purpose; to make a decision; especially, to\n   determine after reflection; as, to resolve on a better course of life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resolve', 'n.', 'The act of resolving or making clear; resolution;\n   solution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resolve', 'n.', 'That which has been resolved on or determined; decisive\n   conclusion; fixed purpose; determination; also, legal or official\n   determination; a legislative declaration; a resolution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resolved', 'p. p. & a.', 'Having a fixed purpose; determined; resolute; --\n   usually placed after its noun; as, a man resolved to be rich.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resolvedly', 'adv.', 'So as to resolve or clear up difficulties; clearly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resolvedly', 'adv.', 'Resolutely; decidedly; firmly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resolvedness', 'n.', 'Fixedness of purpose; firmness; resolution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resolvent', 'a.', 'Having power to resolve; causing solution; solvent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resolvent', 'n.', 'That which has the power of resolving, or causing\n   solution; a solvent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resolvent', 'n.', 'That which has power to disperse inflammatory or other\n   tumors; a discutient; anything which aids the absorption of effused\n   products.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resolvent', 'n.', 'An equation upon whose solution the solution of a given\n   pproblem depends.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Resolver', 'n.', 'That which decomposes, or dissolves.');
