  INSERT INTO words (word, word_type, description) VALUES  (    'Retain', 'v. i.', 'To keep; to continue; to remain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retainable', 'a.', 'Capable of being retained.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retainal', 'n.', 'The act of retaining; retention.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retainer', 'n.', 'One who, or that which, retains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retainer', 'n.', 'One who is retained or kept in service; an attendant; an\n   adherent; a hanger-on.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retainer', 'n.', 'Hence, a servant, not a domestic, but occasionally\n   attending and wearing his masters livery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retainer', 'n.', 'The act of a client by which he engages a lawyer or\n   counselor to manage his cause.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retainer', 'n.', 'The act of withholding what one has in his hands by\n   virtue of some right.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retainer', 'n.', 'A fee paid to engage a lawyer or counselor to maintain a\n   cause, or to prevent his being employed by the opposing party in the\n   case; -- called also retaining fee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retainer', 'n.', 'The act of keeping dependents, or the state of being in\n   dependence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retainment', 'n.', 'The act of retaining; retention.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retake', 'v. t.', 'To take or receive again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retake', 'v. t.', 'To take from a captor; to recapture; as, to retake a\n   ship or prisoners.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retaker', 'n.', 'One who takes again what has been taken; a recaptor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retaliated', 'imp. & p. p.', 'of Retaliate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retaliating', 'p. pr. & vb. n.', 'of Retaliate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retaliate', 'v. t.', 'To return the like for; to repay or requite by an act\n   of the same kind; to return evil for (evil). [Now seldom used except in\n   a bad sense.]');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retaliate', 'v. i.', 'To return like for like; specifically, to return evil\n   for evil; as, to retaliate upon an enemy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retaliation', 'n.', 'The act of retaliating, or of returning like for like;\n   retribution; now, specifically, the return of evil for evil; e.g., an\n   eye for an eye, a tooth for a tooth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retaliative', 'a.', 'Same as Retaliatory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retaliatory', 'a.', 'Tending to, or involving, retaliation; retaliative; as\n   retaliatory measures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retarded', 'imp. & p. p.', 'of Retard');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retarding', 'p. pr. & vb. n.', 'of Retard');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retard', 'v. t.', 'To keep delaying; to continue to hinder; to prevent from\n   progress; to render more slow in progress; to impede; to hinder; as, to\n   retard the march of an army; to retard the motion of a ship; -- opposed\n   to accelerate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retard', 'v. t.', 'To put off; to postpone; as, to retard the attacks of\n   old age; to retard a rupture between nations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retard', 'v. i.', 'To stay back.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retard', 'n.', 'Retardation; delay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retardation', 'n.', 'The act of retarding; hindrance; the act of delaying;\n   as, the retardation of the motion of a ship; -- opposed to\n   acceleration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retardation', 'n.', 'That which retards; an obstacle; an obstruction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retardation', 'n.', 'The keeping back of an approaching consonant chord by\n   prolonging one or more tones of a previous chord into the intermediate\n   chord which follows; -- differing from suspension by resolving upwards\n   instead of downwards.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retardation', 'n.', 'The extent to which anything is retarded; the amount\n   of retarding or delay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retardative', 'a.', 'Tending, or serving, to retard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retarder', 'n.', 'One who, or that which, retards.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retardment', 'n.', 'The act of retarding; retardation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retched', 'imp. & p. p.', 'of Retch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retching', 'p. pr. & vb. n.', 'of Retch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retch', 'v. i.', 'To make an effort to vomit; to strain, as in vomiting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retch', 'v. t. & i.', 'To care for; to heed; to reck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retchless', 'a.', 'Careless; reckless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rete', 'n.', 'A net or network; a plexus; particularly, a network of blood\n   vessels or nerves, or a part resembling a network.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retecious', 'a.', 'Resembling network; retiform.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retection', 'n.', 'Act of disclosing or uncovering something concealed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retell', 'v. t.', 'To tell again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retene', 'n.', 'A white crystalline hydrocarbon, polymeric with benzene. It\n   is extracted from pine tar, and is also found in certain fossil resins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retent', 'n.', 'That which is retained.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retention', 'n.', 'The act of retaining, or the state of being ratined.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retention', 'n.', 'The power of retaining; retentiveness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retention', 'n.', 'That which contains something, as a tablet; a //// of\n   preserving impressions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retention', 'n.', 'The act of withholding; retraint; reserve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retention', 'n.', 'Place of custody or confinement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retention', 'n.', 'The right of withholding a debt, or of retaining\n   property until a debt due to the person claiming the right be duly\n   paid; a lien.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retentive', 'a.', 'Having power to retain; as, a retentive memory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retentive', 'n.', 'That which retains or confines; a restraint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retentively', 'adv.', 'In a retentive manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retentiveness', 'n.', 'The quality of being retentive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retentivity', 'n.', 'The power of retaining; retentive force; as, the\n   retentivity of a magnet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retentor', 'n.', 'A muscle which serves to retain an organ or part in\n   place, esp. when retracted. See Illust. of Phylactolemata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retepore', 'n.', 'Any one of several species of bryozoans of the genus\n   Retepora. They form delicate calcareous corals, usually composed of\n   thin fenestrated fronds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retex', 'v. t.', 'To annual, as orders.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retexture', 'n.', 'The act of weaving or forming again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rethor', 'n.', 'A rhetorician; a careful writer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rethoryke', 'n.', 'Rhetoric.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retiarius', 'n.', 'A gladiator armed with a net for entangling his\n   adversary and a trident for despatching him.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retiary', 'n.', 'Any spider which spins webs to catch its prey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retiary', 'n.', 'A retiarius.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retiary', 'a.', 'Netlike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retiary', 'a.', 'Constructing or using a web, or net, to catch prey; --\n   said of certain spiders.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retiary', 'a.', 'Armed with a net; hence, skillful to entangle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reticence', 'n.', 'The quality or state of being reticent, or keeping\n   silence; the state of holding ones tonque; refraining to speak of that\n   which is suggested; uncommunicativeness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reticence', 'n.', 'A figure by which a person really speaks of a thing\n   while he makes a show as if he would say nothingon the subject.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reticency', 'n.', 'Reticence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reticent', 'a.', 'Inclined to keep silent; reserved; uncommunicative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reticle', 'n.', 'A small net.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reticle', 'n.', 'A reticule. See Reticule, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reticular', 'a.', 'Having the form of a net, or of network; formed with\n   interstices; retiform; as, reticular cartilage; a reticular leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reticular', 'a.', 'Of or pertaining to a reticulum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reticularia', 'n. pl.', 'An extensive division of rhizopods in which the\n   pseudopodia are more or less slender and coalesce at certain points,\n   forming irregular meshes. It includes the shelled Foraminifera,\n   together with some groups which lack a true shell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reticularian', 'n.', 'One of the Reticularia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reticularly', 'adv.', 'In a reticular manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reticulate', 'a.', 'Alt. of Reticulated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reticulated', 'a.', 'Resembling network; having the form or appearance of a\n   net; netted; as, a reticulated structure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reticulated', 'a.', 'Having veins, fibers, or lines crossing like the\n   threads or fibers of a network; as, a reticulate leaf; a reticulated\n   surface; a reticulated wing of an insect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reticulation', 'n.', 'The quality or state of being reticulated, or\n   netlike; that which is reticulated; network; an organization resembling\n   a net.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reticule', 'n..', 'A little bag, originally of network; a womans workbag,\n   or a little bag to be carried in the hand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reticule', 'n..', 'A system of wires or lines in the focus of a telescope\n   or other instrument; a reticle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reticulosa', 'n. pl.', 'Same as Reticularia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reticulose', 'a.', 'Forming a network; characterized by a reticulated\n   sructure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reticula', 'pl. ', 'of Reticulum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reticulum', 'n.', 'The second stomach of ruminants, in which folds of the\n   mucous membrane form hexagonal cells; -- also called the honeycomb\n   stomach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reticulum', 'n.', 'The neuroglia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retiform', 'a.', 'Composed of crossing lines and interstices; reticular;\n   netlike; as, the retiform coat of the eye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retina', 'n.', 'The delicate membrane by which the back part of the globe\n   of the eye is lined, and in which the fibers of the optic nerve\n   terminate. See Eye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retinacula', 'pl. ', 'of Retinaculum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retinaculum', 'n.', 'A connecting band; a fraenum; as, the retinacula of\n   the ileocaecal and ileocolic valves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retinaculum', 'n.', 'One of the annular ligaments which hold the tendons\n   close to the bones at the larger joints, as at the wrist and ankle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retinaculum', 'n.', 'One of the retractor muscles of the proboscis of\n   certain worms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retinaculum', 'n.', 'A small gland or process to which bodies are attached;\n   as, the glandular retinacula to which the pollinia of orchids are\n   attached, or the hooks which support the seeds in many acanthaceous\n   plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retinal', 'a.', 'Of or pertaining to the retina.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retinalite', 'n.', 'A translucent variety of serpentine, of a honey yellow\n   or greenish yellow color, having a waxy resinlike luster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retinasphalt', 'n.', 'Alt. of Retinasphaltum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retinasphaltum', 'n.', 'Retinite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retinerved', 'a.', 'Having reticulated veins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retinea', 'pl. ', 'of Retineum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retineum', 'n.', 'That part of the eye of an invertebrate which corresponds\n   in function with the retina of a vertebrate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retinic', 'a.', 'Of or pertaining to resin; derived from resin;\n   specifically, designating an acid found in certain fossil resins and\n   hydrocarbons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retinite', 'n.', 'An inflammable mineral resin, usually of a yellowish\n   brown color, found in roundish masses, sometimes with coal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retinitis', 'n.', 'Inflammation of the retina.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retinoid', 'a.', 'Resinlike, or resinform; resembling a resin without being\n   such.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retinol', 'n.', 'A hydrocarbon oil obtained by the distillation of resin,\n   -- used in printers ink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retiniphorae', 'pl. ', 'of Retinophora');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retinophora', 'n.', 'One of group of two to four united cells which occupy\n   the axial part of the ocelli, or ommatidia, of the eyes of\n   invertebrates, and contain the terminal nerve fibrillae. See Illust.\n   under Ommatidium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retinophoral', 'a.', 'Of or pertaining to retinophorae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retinoscopy', 'n.', 'The study of the retina of the eye by means of the\n   ophthalmoscope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retinue', 'n.', 'The body of retainers who follow a prince or other\n   distinguished person; a train of attendants; a suite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retinulae', 'pl. ', 'of Retinula');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retinula', 'n.', 'One of the group of pigmented cells which surround the\n   retinophorae of invertebrates. See Illust. under Ommatidium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retinulate', 'a.', 'Having, or characterized by, retinul/.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retiped', 'n.', 'A bird having small polygonal scales covering the tarsi.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retiracy', 'n.', 'Retirement; -- mostly used in a jocose or burlesque way.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retirade', 'n.', 'A kind of retrenchment, as in the body of a bastion,\n   which may be disputed inch by inch after the defenses are dismantled.\n   It usually consists of two faces which make a reentering angle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retired', 'imp. & p. p.', 'of Retire');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retiring', 'p. pr. & vb. n.', 'of Retire');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retire', 'v. t.', 'To withdraw; to take away; -- sometimes used\n   reflexively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retire', 'v. t.', 'To withdraw from circulation, or from the market; to\n   take up and pay; as, to retire bonds; to retire a note.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retire', 'v. t.', 'To cause to retire; specifically, to designate as no\n   longer qualified for active service; to place on the retired list; as,\n   to retire a military or naval officer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retire', 'v. i.', 'To go back or return; to draw back or away; to keep\n   aloof; to withdraw or retreat, as from observation; to go into privacy;\n   as, to retire to his home; to retire from the world, or from notice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retire', 'v. i.', 'To retreat from action or danger; to withdraw for safety\n   or pleasure; as, to retire from battle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retire', 'v. i.', 'To withdraw from a public station, or from business; as,\n   having made a large fortune, he retired.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retire', 'v. i.', 'To recede; to fall or bend back; as, the shore of the\n   sea retires in bays and gulfs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retire', 'v. i.', 'To go to bed; as, he usually retires early.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retire', 'n.', 'The act of retiring, or the state of being retired; also, a\n   place to which one retires.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retire', 'n.', 'A call sounded on a bugle, announcing to skirmishers that\n   they are to retire, or fall back.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retired', 'a.', 'Private; secluded; quiet; as, a retired life; a person of\n   retired habits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retired', 'a.', 'Withdrawn from active duty or business; as, a retired\n   officer; a retired physician.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retirement', 'n.', 'The act of retiring, or the state of being retired;\n   withdrawal; seclusion; as, the retirement of an officer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retirement', 'n.', 'A place of seclusion or privacy; a place to which one\n   withdraws or retreats; a private abode.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retirer', 'n.', 'One who retires.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retiring', 'a.', 'Reserved; shy; not forward or obtrusive; as, retiring\n   modesty; retiring manners.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retiring', 'a.', 'Of or pertaining to retirement; causing retirement;\n   suited to, or belonging to, retirement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retistene', 'n.', 'A white crystalline hydrocarbon produced indirectly from\n   retene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retitelae', 'n. pl.', 'A group of spiders which spin irregular webs; --\n   called also Retitelariae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retold', '', 'imp. & p. p. of Retell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retorsion', 'n.', 'Same as Retortion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retorted', 'imp. & p. p.', 'of Retort');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retorting', 'p. pr. & vb. n.', 'of Retort');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retort', 'n.', 'To bend or curve back; as, a retorted line.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retort', 'n.', 'To throw back; to reverberate; to reflect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retort', 'n.', 'To return, as an argument, accusation, censure, or\n   incivility; as, to retort the charge of vanity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retort', 'v. i.', 'To return an argument or a charge; to make a severe\n   reply.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retort', 'v. t.', 'The return of, or reply to, an argument, charge,\n   censure, incivility, taunt, or witticism; a quick and witty or severe\n   response.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retort', 'v. t.', 'A vessel in which substances are subjected to\n   distillation or decomposition by heat. It is made of different forms\n   and materials for different uses, as a bulb of glass with a curved beak\n   to enter a receiver for general chemical operations, or a cylinder or\n   semicylinder of cast iron for the manufacture of gas in gas works.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retorter', 'n.', 'One who retorts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retortion', 'v. t.', 'Act of retorting or throwing back; reflection or\n   turning back.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retortion', 'v. t.', 'Retaliation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retortive', 'a.', 'Containing retort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retoss', 'v. t.', 'To toss back or again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retouch', 'v. t.', 'To touch again, or rework, in order to improve; to\n   revise; as, to retouch a picture or an essay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retouch', 'v. t.', 'To correct or change, as a negative, by handwork.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retouch', 'n.', 'A partial reworking,as of a painting, a sculptors clay\n   model, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retoucher', 'n.', 'One who retouches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retrace', 'v. t.', 'To trace back, as a line.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retrace', 'v. t.', 'To go back, in or over (a previous course); to go over\n   again in a reverse direction; as, to retrace ones steps; to retrace\n   ones proceedings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retrace', 'v. t.', 'To trace over again, or renew the outline of, as a\n   drawing; to draw again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retracted', 'imp. & p. p.', 'of Retract');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retracting', 'p. pr. & vb. n.', 'of Retract');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retract', 'v. t.', 'To draw back; to draw up or shorten; as, the cat can\n   retract its claws; to retract a muscle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retract', 'v. t.', 'To withdraw; to recall; to disavow; to recant; to take\n   back; as, to retract an accusation or an assertion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retract', 'v. t.', 'To take back,, as a grant or favor previously bestowed;\n   to revoke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retract', 'v. i.', 'To draw back; to draw up; as, muscles retract after\n   amputation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retract', 'v. i.', 'To take back what has been said; to withdraw a\n   concession or a declaration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retract', 'n.', 'The pricking of a horses foot in nailing on a shoe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retractable', 'a.', 'Capable of being retracted; retractile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retractate', 'v. t.', 'To retract; to recant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retractation', 'n.', 'The act of retracting what has been said;\n   recantation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retractible', 'a.', 'Retractable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retractile', 'a.', 'Capable of retraction; capable of being drawn back or\n   up; as, the claws of a cat are retractile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retraction', 'n.', 'The act of retracting, or drawing back; the state of\n   being retracted; as, the retraction of a cats claws.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retraction', 'n.', 'The act of withdrawing something advanced, stated,\n   claimed, or done; declaration of change of opinion; recantation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retraction', 'n.', 'The act of retracting or shortening; as, the retraction\n   of a severed muscle; the retraction of a sinew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retraction', 'n.', 'The state or condition of a part when drawn back, or\n   towards the center of the body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retractive', 'a.', 'Serving to retract; of the nature of a retraction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retractive', 'n.', 'That which retracts, or withdraws.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retractor', 'n.', 'One who, or that which, retracts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retractor', 'n.', 'In breech-loading firearms, a device for withdrawing a\n   cartridge shell from the barrel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retractor', 'n.', 'An instrument for holding apart the edges of a wound\n   during amputation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retractor', 'n.', 'A bandage to protect the soft parts from injury by the\n   saw during amputation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retractor', 'n.', 'A muscle serving to draw in any organ or part. See\n   Illust. under Phylactolaemata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retraict', 'n.', 'Retreat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retrait', 'n.', 'A portrait; a likeness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retransform', 'v. t.', 'To transform anew or back.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retranslate', 'v. t.', 'To translate anew; especially, to translate back\n   into the original language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retraxit', 'n.', 'The withdrawing, or open renunciation, of a suit in court\n   by the plaintiff, by which he forever lost his right of action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retread', 'v. t. & i.', 'To tread again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retreat', 'n.', 'The act of retiring or withdrawing ones self, especially\n   from what is dangerous or disagreeable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retreat', 'n.', 'The place to which anyone retires; a place or privacy or\n   safety; a refuge; an asylum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retreat', 'n.', 'The retiring of an army or body of men from the face of an\n   enemy, or from any ground occupied to a greater distance from the\n   enemy, or from an advanced position.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retreat', 'n.', 'The withdrawing of a ship or fleet from an enemy for the\n   purpose of avoiding an engagement or escaping after defeat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retreat', 'n.', 'A signal given in the army or navy, by the beat of a drum\n   or the sounding of trumpet or bugle, at sunset (when the roll is\n   called), or for retiring from action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retreat', 'n.', 'A special season of solitude and silence to engage in\n   religious exercises.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retreat', 'n.', 'A period of several days of withdrawal from society to a\n   religious house for exclusive occupation in the duties of devotion; as,\n   to appoint or observe a retreat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retreated', 'imp. & p. p.', 'of Retreat');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retreating', 'p. pr. & vb. n.', 'of Retreat');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retreat', 'v. i.', 'To make a retreat; to retire from any position or\n   place; to withdraw; as, the defeated army retreated from the field.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retreatful', 'a.', 'Furnishing or serving as a retreat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retreatment', 'n.', 'The act of retreating; specifically, the Hegira.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retrenched', 'imp. & p. p.', 'of Retrench');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retrenching', 'p. pr. & vb. n.', 'of Retrench');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retrench', 'v. t.', 'To cut off; to pare away.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retrench', 'v. t.', 'To lessen; to abridge; to curtail; as, to retrench\n   superfluities or expenses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retrench', 'v. t.', 'To confine; to limit; to restrict.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retrench', 'v. t.', 'To furnish with a retrenchment; as, to retrench\n   bastions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retrench', 'v. i.', 'To cause or suffer retrenchment; specifically, to cut\n   down living expenses; as, it is more reputable to retrench than to live\n   embarrassed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retrenchment', 'n.', 'The act or process of retrenching; as, the\n   retrenchment of words in a writing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retrenchment', 'n.', 'A work constructed within another, to prolong the\n   defense of the position when the enemy has gained possession of the\n   outer work; or to protect the defenders till they can retreat or obtain\n   terms for a capitulation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retrial', 'n.', 'A secdond trial, experiment, or test; a second judicial\n   trial, as of an accused person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retribute', 'v. t.', 'To pay back; to give in return, as payment, reward,\n   or punishment; to requite; as, to retribute one for his kindness; to\n   retribute just punishment to a criminal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retributer', 'n.', 'One who makes retribution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retribution', 'n.', 'The act of retributing; repayment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retribution', 'n.', 'That which is given in repayment or compensation;\n   return suitable to the merits or deserts of, as an action; commonly,\n   condign punishment for evil or wrong.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retribution', 'n.', 'Specifically, reward and punishment, as distributed at\n   the general judgment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retributive', 'a.', 'Alt. of Retributory');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retributory', 'a.', 'Of or pertaining to retribution; of the nature of\n   retribution; involving retribution or repayment; as, retributive\n   justice; retributory comforts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retrievable', 'a.', 'That may be retrieved or recovered; admitting of\n   retrieval.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retrieval', 'n.', 'The act retrieving.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retrieved', 'imp. & p. p.', 'of Retrieve');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retrieving', 'p. pr. & vb. n.', 'of Retrieve');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retrieve', 'v. t.', 'To find again; to recover; to regain; to restore from\n   loss or injury; as, to retrieve ones character; to retrieve\n   independence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retrieve', 'v. t.', 'To recall; to bring back.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retrieve', 'v. t.', 'To remedy the evil consequence of, to repair, as a\n   loss or damadge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retrieve', 'v. i.', 'To discover and bring in game that has been killed or\n   wounded; as, a dog naturally inclined to retrieve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retrieve', 'n.', 'A seeking again; a discovery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retrieve', 'n.', 'The recovery of game once sprung; -- an old sporting\n   term.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retrievement', 'n.', 'Retrieval.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retriever', 'n.', 'One who retrieves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retriever', 'n.', 'A dor, or a breed of dogs, chiefly employed to retrieve,\n   or to find and recover game birds that have been killed or wounded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retrim', 'v. t.', 'To trim again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retriment', 'n.', 'Refuse; dregs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retro-', '', 'A prefix or combining form signifying backward, back; as,\n   retroact, to act backward; retrospect, a looking back.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retroact', 'v. i.', 'To act backward, or in return; to act in opposition;\n   to be retrospective.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retroaction', 'n.', 'Action returned, or action backward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retroaction', 'n.', 'Operation on something past or preceding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retroactive', 'a.', 'Fitted or designed to retroact; operating by returned\n   action; affecting what is past; retrospective.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retroactively', 'adv.', 'In a retroactive manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retrocede', 'v. t.', 'To cede or grant back; as, to retrocede a territory\n   to a former proprietor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retrocede', 'v. i.', 'To go back.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retrocedent', 'a.', 'Disposed or likely to retrocede; -- said of diseases\n   which go from one part of the body to another, as the gout.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retrocession', 'n.', 'The act of retroceding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retrocession', 'n.', 'The state of being retroceded, or granted back.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retrocession', 'n.', 'Metastasis of an eruption or a tumor from the surface\n   to the interior of the body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retrochoir', 'n.', 'Any extension of a church behind the high altar, as a\n   chapel; also, in an apsidal church, all the space beyond the line of\n   the back or eastern face of the altar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retrocopulant', 'a.', 'Copulating backward, or from behind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retrocopulation', 'n.', 'Copulation from behind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retroduction', 'n.', 'A leading or bringing back.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retroflex', 'a.', 'Alt. of Retroflexed');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retroflexed', 'a.', 'Reflexed; bent or turned abruptly backward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retroflexion', 'n.', 'The act of reflexing; the state of being retroflexed.\n   Cf. Retroversion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retrofract', 'a.', 'Alt. of Retrofracted');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retrofracted', 'a.', 'Refracted; as, a retrofract stem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retrogenerative', 'a.', 'Begetting young by retrocopulation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retrogradation', 'n.', 'The act of retrograding, or moving backward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retrogradation', 'n.', 'The state of being retrograde; decline.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retrograde', 'a.', 'Apparently moving backward, and contrary to the\n   succession of the signs, that is, from east to west, as a planet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retrograde', 'a.', 'Tending or moving backward; having a backward course;\n   contrary; as, a retrograde motion; -- opposed to progressive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retrograde', 'a.', 'Declining from a better to a worse state; as, a\n   retrograde people; retrograde ideas, morals, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retrograded', 'imp. & p. p.', 'of Retrograde');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retrograding', 'p. pr. & vb. n.', 'of Retrograde');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retrograde', 'v. i.', 'To go in a retrograde direction; to move, or appear\n   to move, backward, as a planet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retrograde', 'v. i.', 'Hence, to decline from a better to a worse\n   condition, as in morals or intelligence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retrogradingly', 'adv.', 'By retrograding; so as to retrograde.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retrogress', 'n.', 'Retrogression.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retrogression', 'n.', 'The act of retrograding, or going backward;\n   retrogradation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retrogression', 'n.', 'Backward development; a passing from a higher to a\n   lower state of organization or structure, as when an animal,\n   approaching maturity, becomes less highly organized than would be\n   expected from its earlier stages or known relationship. Called also\n   retrograde development, and regressive metamorphism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retrogressive', 'a.', 'Tending to retrograde; going or moving backward;\n   declining from a better to a worse state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retrogressive', 'a.', 'Passing from a higher to a lower condition;\n   declining from a more perfect state of organization; regressive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retrogressively', 'adv.', 'In a retrogressive manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retromingency', 'n.', 'The quality or state of being retromingent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retromingent', 'a.', 'Organized so as to discharge the urine backward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retromingent', 'n.', 'An animal that discharges its urine backward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retropulsive', 'a.', 'Driving back; repelling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retrorse', 'a.', 'Bent backward or downward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retrospect', 'v. i.', 'To look backward; hence, to affect or concern what\n   is past.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retrospect', 'n.', 'A looking back on things past; view or contemplation of\n   the past.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retrospection', 'n.', 'The act, or the faculty, of looking back on things\n   past.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retrospective', 'a.', 'Looking backward; contemplating things past; --\n   opposed to prospective; as, a retrospective view.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retrospective', 'a.', 'Having reference to what is past; affecting things\n   past; retroactive; as, a retrospective law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retrospectively', 'adv.', 'By way of retrospect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retrousse', 'a.', 'Turned up; -- said of a pug nose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retrovaccination', 'n.', 'The inoculation of a cow with human vaccine\n   virus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retroversion', 'n.', 'A turning or bending backward; also, the state of\n   being turned or bent backward; displacement backwards; as, retroversion\n   of the uterus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retroverted', 'imp. & p. p.', 'of Retrovert');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retroverting', 'p. pr. & vb. n.', 'of Retrovert');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retrovert', 'v. t.', 'To turn back.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retroverted', 'a.', 'In a state of retroversion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retruded', 'imp. & p. p.', 'of Retrude');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retruding', 'p. pr. & vb. n.', 'of Retrude');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retrude', 'v. t.', 'To thrust back.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retruse', 'a.', 'Abstruse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retrusion', 'n.', 'The act of retruding, or the state of being retruded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retry', 'v. t.', 'To try (esp. judicially) a second time; as, to retry a\n   case; to retry an accused person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rette', 'v. t.', 'See Aret.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rettery', 'n.', 'A place or establishment where flax is retted. See Ret.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retting', 'n.', 'The act or process of preparing flax for use by soaking,\n   maceration, and kindred processes; -- also called rotting. See Ret.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retting', 'n.', 'A place where flax is retted; a rettery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retund', 'v. t.', 'To blunt; to turn, as an edge; figuratively, to cause to\n   be obtuse or dull; as, to retund confidence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Re-turn', 'v. t. & i.', 'To turn again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Returned', 'imp. & p. p.', 'of Return');
  INSERT INTO words (word, word_type, description) VALUES  (    'Returning', 'p. pr. & vb. n.', 'of Return');
  INSERT INTO words (word, word_type, description) VALUES  (    'Return', 'v. i.', 'To turn back; to go or come again to the same place or\n   condition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Return', 'v. i.', 'To come back, or begin again, after an interval, regular\n   or irregular; to appear again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Return', 'v. i.', 'To speak in answer; to reply; to respond.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Return', 'v. i.', 'To revert; to pass back into possession.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Return', 'v. i.', 'To go back in thought, narration, or argument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Return', 'v. t.', 'To bring, carry, send, or turn, back; as, to return a\n   borrowed book, or a hired horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Return', 'v. t.', 'To repay; as, to return borrowed money.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Return', 'v. t.', 'To give in requital or recompense; to requite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Return', 'v. t.', 'To give back in reply; as, to return an answer; to\n   return thanks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Return', 'v. t.', 'To retort; to throw back; as, to return the lie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Return', 'v. t.', 'To report, or bring back and make known.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Return', 'v. t.', 'To render, as an account, usually an official account,\n   to a superior; to report officially by a list or statement; as, to\n   return a list of stores, of killed or wounded; to return the result of\n   an election.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Return', 'v. t.', 'Hence, to elect according to the official report of the\n   election officers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Return', 'v. t.', 'To bring or send back to a tribunal, or to an office,\n   with a certificate of what has been done; as, to return a writ.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Return', 'v. t.', 'To convey into official custody, or to a general\n   depository.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Return', 'v. t.', 'To bat (the ball) back over the net.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Return', 'v. t.', 'To lead in response to the lead of ones partner; as, to\n   return a trump; to return a diamond for a club.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Return', 'n.', 'The act of returning (intransitive), or coming back to the\n   same place or condition; as, the return of one long absent; the return\n   of health; the return of the seasons, or of an anniversary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Return', 'n.', 'The act of returning (transitive), or sending back to the\n   same place or condition; restitution; repayment; requital; retribution;\n   as, the return of anything borrowed, as a book or money; a good return\n   in tennis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Return', 'n.', 'That which is returned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Return', 'n.', 'A payment; a remittance; a requital.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Return', 'n.', 'An answer; as, a return to ones question.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Return', 'n.', 'An account, or formal report, of an action performed, of a\n   duty discharged, of facts or statistics, and the like; as, election\n   returns; a return of the amount of goods produced or sold; especially,\n   in the plural, a set of tabulated statistics prepared for general\n   information.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Return', 'n.', 'The profit on, or advantage received from, labor, or an\n   investment, undertaking, adventure, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Return', 'n.', 'The continuation in a different direction, most often at a\n   right angle, of a building, face of a building, or any member, as a\n   molding or mold; -- applied to the shorter in contradistinction to the\n   longer; thus, a facade of sixty feet east and west has a return of\n   twenty feet north and south.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Return', 'n.', 'The rendering back or delivery of writ, precept, or\n   execution, to the proper officer or court.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Return', 'n.', 'The certificate of an officer stating what he has done in\n   execution of a writ, precept, etc., indorsed on the document.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Return', 'n.', 'The sending back of a commission with the certificate of\n   the commissioners.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Return', 'n.', 'A day in bank. See Return day, below.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Return', 'n.', 'An official account, report, or statement, rendered to the\n   commander or other superior officer; as, the return of men fit for\n   duty; the return of the number of the sick; the return of provisions,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Return', 'n.', 'The turnings and windings of a trench or mine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Returnable', 'a.', 'Capable of, or admitting of, being returned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Returnable', 'a.', 'Legally required to be returned, delivered, given, or\n   rendered; as, a writ or precept returnable at a certain day; a verdict\n   returnable to the court.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Returner', 'n.', 'One who returns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Returnless', 'a.', 'Admitting no return.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Retuse', 'a.', 'Having the end rounded and slightly indented; as, a retuse\n   leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reule', 'n.& v.', 'Rule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reume', 'n.', 'Realm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reunion', 'n.', 'A second union; union formed anew after separation,\n   secession, or discord; as, a reunion of parts or particles of matter; a\n   reunion of parties or sects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reunion', 'n.', 'An assembling of persons who have been separated, as of a\n   family, or the members of a disbanded regiment; an assembly so\n   composed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reunite', 'v. t. & i.', 'To unite again; to join after separation or\n   variance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reunitedly', 'adv.', 'In a reunited manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reunition', 'n.', 'A second uniting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reurge', 'v. t.', 'To urge again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revaccinate', 'v. t.', 'To vaccinate a second time or again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revalescence', 'n.', 'The act of growing well; the state of being\n   revalescent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revalescent', 'a.', 'Growing well; recovering strength.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revaluation', 'n.', 'A second or new valuation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revamp', 'v. t.', 'To vamp again; hence, to patch up; to reconstruct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reve', 'v. t.', 'To reave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reve', 'n.', 'An officer, steward, or governor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revealed', 'imp. & p. p.', 'of Reveal');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revealing', 'p. pr. & vb. n.', 'of Reveal');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reveal', 'v. t.', 'To make known (that which has been concealed or kept\n   secret); to unveil; to disclose; to show.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reveal', 'v. t.', 'Specifically, to communicate (that which could not be\n   known or discovered without divine or supernatural instruction or\n   agency).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reveal', 'n.', 'A revealing; a disclosure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reveal', 'n.', 'The side of an opening for a window, doorway, or the like,\n   between the door frame or window frame and the outer surface of the\n   wall; or, where the opening is not filled with a door, etc., the whole\n   thickness of the wall; the jamb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revealability', 'n.', 'The quality or state of being revealable;\n   revealableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revealable', 'a.', 'Capable of being revealed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revealer', 'n.', 'One who, or that which, reveals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revealment', 'n.', 'Act of revealing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revegetate', 'v. i.', 'To vegetate anew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reveille', 'n.', 'The beat of drum, or bugle blast, about break of day, to\n   give notice that it is time for the soldiers to rise, and for the\n   sentinels to forbear challenging.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revel', 'n.', 'See Reveal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revel', 'v. i.', 'A feast with loose and noisy jollity; riotous festivity\n   or merrymaking; a carousal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reveled', 'imp. & p. p.', 'of Revel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revelled', '', 'of Revel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reveling', 'p. pr. & vb. n.', 'of Revel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revelling', '', 'of Revel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revel', 'v. i.', 'To feast in a riotous manner; to carouse; to act the\n   bacchanalian; to make merry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revel', 'v. i.', 'To move playfully; to indulge without restraint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revel', 'v. t.', 'To draw back; to retract.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revelate', 'v. t.', 'To reveal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revelation', 'n.', 'The act of revealing, disclosing, or discovering to\n   others what was before unknown to them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revelation', 'n.', 'That which is revealed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revelation', 'n.', 'The act of revealing divine truth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revelation', 'n.', 'That which is revealed by God to man; esp., the Bible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revelation', 'n.', 'Specifically, the last book of the sacred canon,\n   containing the prophecies of St. John; the Apocalypse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revelator', 'n.', 'One who makes a revelation; a revealer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reveler', 'n.', 'One who revels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revellent', 'v. t.', 'Causing revulsion; revulsive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revellent', 'n.', 'A revulsive medicine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revelment', 'n.', 'The act of reveling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revelous', 'a.', 'Fond of festivity; given to merrymaking or reveling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revel-rout', 'n.', 'Tumultuous festivity; revelry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revel-rout', 'n.', 'A rabble; a riotous assembly; a mob.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revelry', 'n.', 'The act of engaging in a revel; noisy festivity; reveling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revendicated', 'imp. & p. p.', 'of Revendicate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revendicating', 'p. pr. & vb. n.', 'of Revendicate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revendicate', 'v. t.', 'To reclaim; to demand the restoration of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revendication', 'n.', 'The act of revendicating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revenged', 'imp. & p. p.', 'of Revenge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revenging', 'p. pr. & vb. n.', 'of Revenge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revenge', 'v. t.', 'To inflict harm in return for, as an injury, insult,\n   etc.; to exact satisfaction for, under a sense of injury; to avenge; --\n   followed either by the wrong received, or by the person or thing\n   wronged, as the object, or by the reciprocal pronoun as direct object,\n   and a preposition before the wrong done or the wrongdoer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revenge', 'v. t.', 'To inflict injury for, in a spiteful, wrong, or\n   malignant spirit; to wreak vengeance for maliciously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revenge', 'v. i.', 'To take vengeance; -- with');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revenge', 'n.', 'The act of revenging; vengeance; retaliation; a returning\n   of evil for evil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revenge', 'n.', 'The disposition to revenge; a malignant wishing of evil to\n   one who has done us an injury.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revengeable', 'a.', 'Capable of being revenged; as, revengeable wrong.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revengeance', 'n.', 'Vengeance; revenge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revengeful', 'a.', 'Full of, or prone to, revenge; vindictive; malicious;\n   revenging; wreaking revenge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revengeless', 'a.', 'Unrevenged.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revengement', 'n.', 'Revenge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revenger', 'n.', 'One who revenges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revenging', 'a.', 'Executing revenge; revengeful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revenue', 'n.', 'That which returns, or comes back, from an investment; the\n   annual rents, profits, interest, or issues of any species of property,\n   real or personal; income.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revenue', 'n.', 'Hence, return; reward; as, a revenue of praise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revenue', 'n.', 'The annual yield of taxes, excise, customs, duties, rents,\n   etc., which a nation, state, or municipality collects and receives into\n   the treasury for public use.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reverb', 'v. t.', 'To echo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reverberant', 'a.', 'Having the quality of reverberation; reverberating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reverberate', 'a.', 'Reverberant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reverberate', 'a.', 'Driven back, as sound; reflected.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reverberated', 'imp. & p. p.', 'of Reverberate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reverberating', 'p. pr. & vb. n.', 'of Reverberate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reverberate', 'v. t.', 'To return or send back; to repel or drive back; to\n   echo, as sound; to reflect, as light, as light or heat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reverberate', 'v. t.', 'To send or force back; to repel from side to side;\n   as, flame is reverberated in a furnace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reverberate', 'v. t.', 'Hence, to fuse by reverberated heat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reverberate', 'v. i.', 'To resound; to echo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reverberate', 'v. i.', 'To be driven back; to be reflected or repelled, as\n   rays of light; to be echoed, as sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reverberation', 'n.', 'The act of reverberating; especially, the act of\n   reflecting light or heat, or reechoing sound; as, the reverberation of\n   rays from a mirror; the reverberation of rays from a mirror; the\n   reverberation of voices; the reverberation of heat or flame in a\n   furnace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reverberative', 'a.', 'Of the nature of reverberation; tending to\n   reverberate; reflective.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reverberator', 'n.', 'One who, or that which, produces reverberation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reverberatory', 'a.', 'Producing reverberation; acting by reverberation;\n   reverberative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reverberatory', 'n.', 'A reverberatory furnace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reverdure', 'v. t.', 'To cover again with verdure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revered', 'imp. & p. p.', 'of Revere');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revering', 'p. pr. & vb. n.', 'of Revere');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revere', 'v. t.', 'To regard with reverence, or profound respect and\n   affection, mingled with awe or fear; to venerate; to reverence; to\n   honor in estimation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reverence', 'n.', 'Profound respect and esteem mingled with fear and\n   affection, as for a holy being or place; the disposition to revere;\n   veneration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reverence', 'n.', 'The act of revering; a token of respect or veneration;\n   an obeisance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reverence', 'n.', 'That which deserves or exacts manifestations of\n   reverence; reverend character; dignity; state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reverence', 'n.', 'A person entitled to be revered; -- a title applied to\n   priests or other ministers with the pronouns his or your; sometimes\n   poetically to a father.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reverenced', 'imp. & p. p.', 'of Reverence');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reverencing', 'p. pr. & vb. n.', 'of Reverence');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reverence', 'v. t.', 'To regard or treat with reverence; to regard with\n   respect and affection mingled with fear; to venerate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reverencer', 'n.', 'One who regards with reverence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reverend', 'a.', 'Worthy of reverence; entitled to respect mingled with\n   fear and affection; venerable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reverendly', 'adv.', 'Reverently.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reverent', 'a.', 'Disposed to revere; impressed with reverence; submissive;\n   humble; respectful; as, reverent disciples.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reverent', 'a.', 'Expressing reverence, veneration, devotion, or\n   submission; as, reverent words; reverent behavior.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reverential', 'a.', 'Proceeding from, or expressing, reverence; having a\n   reverent quality; reverent; as, reverential fear or awe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reverentially', 'adv.', 'In a reverential manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reverently', 'adv.', 'In a reverent manner; in respectful regard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reverer', 'n.', 'One who reveres.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reveries', 'pl. ', 'of Revery');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reverie', 'n.', 'Alt. of Revery');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revery', 'n.', 'A loose or irregular train of thought occurring in musing\n   or mediation; deep musing; daydream.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revery', 'n.', 'An extravagant conceit of the fancy; a vision.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reversal', 'a.', 'Intended to reverse; implying reversal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reversal', 'n.', 'The act of reversing; the causing to move or face in an\n   opposite direction, or to stand or lie in an inverted position; as, the\n   reversal of a rotating wheel; the reversal of objects by a convex lens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reversal', 'n.', 'A change or overthrowing; as, the reversal of a judgment,\n   which amounts to an official declaration that it is false; the reversal\n   of an attainder, or of an outlawry, by which the sentence is rendered\n   void.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reverse', 'a.', 'Turned backward; having a contrary or opposite direction;\n   hence; opposite or contrary in kind; as, the reverse order or method.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reverse', 'a.', 'Turned upside down; greatly disturbed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reverse', 'a.', 'Reversed; as, a reverse shell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reverse', 'a.', 'That which appears or is presented when anything, as a\n   lance, a line, a course of conduct, etc., is reverted or turned\n   contrary to its natural direction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reverse', 'a.', 'That which is directly opposite or contrary to something\n   else; a contrary; an opposite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reverse', 'a.', 'The act of reversing; complete change; reversal; hence,\n   total change in circumstances or character; especially, a change from\n   better to worse; misfortune; a check or defeat; as, the enemy met with\n   a reverse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reverse', 'a.', 'The back side; as, the reverse of a drum or trench; the\n   reverse of a medal or coin, that is, the side opposite to the obverse.\n   See Obverse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reverse', 'a.', 'A thrust in fencing made with a backward turn of the hand;\n   a backhanded stroke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reverse', 'a.', 'A turn or fold made in bandaging, by which the direction\n   of the bandage is changed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reversed', 'imp. & p. p.', 'of Reverse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reversing', 'p. pr. & vb. n.', 'of Reverse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reverse', 'a.', 'To turn back; to cause to face in a contrary direction; to\n   cause to depart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reverse', 'a.', 'To cause to return; to recall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reverse', 'a.', 'To change totally; to alter to the opposite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reverse', 'a.', 'To turn upside down; to invert.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reverse', 'a.', 'Hence, to overthrow; to subvert.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reverse', 'a.', 'To overthrow by a contrary decision; to make void; to\n   under or annual for error; as, to reverse a judgment, sentence, or\n   decree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reverse', 'v. i.', 'To return; to revert.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reverse', 'v. i.', 'To become or be reversed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reversed', 'a.', 'Turned side for side, or end for end; changed to the\n   contrary; specifically (Bot. & Zool.), sinistrorse or sinistral; as, a\n   reversed, or sinistral, spiral or shell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reversed', 'a.', 'Annulled and the contrary substituted; as, a reversed\n   judgment or decree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reversedly', 'adv.', 'In a reversed way.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reverseless', 'a.', 'Irreversible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reversely', 'adv.', 'In a reverse manner; on the other hand; on the\n   opposite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reverser', 'n.', 'One who reverses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reversibility', 'n.', 'The quality of being reversible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reversible', 'a.', 'Capable of being reversed; as, a chair or seat having a\n   reversible back; a reversible judgment or sentence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reversible', 'a.', 'Hence, having a pattern or finished surface on both\n   sides, so that either may be used; -- said of fabrics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reversibly', 'adv.', 'In a reversible manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reversing', 'a.', 'Serving to effect reversal, as of motion; capable of\n   being reversed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reversion', 'n.', 'The act of returning, or coming back; return.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reversion', 'n.', 'That which reverts or returns; residue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reversion', 'n.', 'The returning of an esttate to the grantor or his heirs,\n   by operation of law, after the grant has terminated; hence, the residue\n   of an estate left in the proprietor or owner thereof, to take effect in\n   possession, by operation of law, after the termination of a limited or\n   less estate carved out of it and conveyed by him.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reversion', 'n.', 'Hence, a right to future possession or enjoiment;\n   succession.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reversion', 'n.', 'A payment which is not to be received, or a benefit\n   which does not begin, until the happening of some event, as the death\n   of a living person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reversion', 'n.', 'A return towards some ancestral type or character;\n   atavism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reversionary', 'a.', 'Of or pertaining to a reversion; involving a\n   reversion; to be enjoyed in succession, or after the termination of a\n   particular estate; as, a reversionary interest or right.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reversionary', 'n.', 'That which is to be received in reversion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reversioner', 'n.', 'One who has a reversion, or who is entitled to lands\n   or tenements, after a particular estate granted is terminated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reversis', 'n.', 'A certain game at cards.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reverted', 'imp. & p. p.', 'of Revert');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reverting', 'p. pr. & vb. n.', 'of Revert');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revert', 'v. t.', 'To turn back, or to the contrary; to reverse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revert', 'v. t.', 'To throw back; to reflect; to reverberate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revert', 'v. t.', 'To change back. See Revert, v. i.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revert', 'v. i.', 'To return; to come back.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revert', 'v. i.', 'To return to the proprietor after the termination of a\n   particular estate granted by him.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revert', 'v. i.', 'To return, wholly or in part, towards some preexistent\n   form; to take on the traits or characters of an ancestral type.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revert', 'v. i.', 'To change back, as from a soluble to an insoluble state\n   or the reverse; thus, phosphoric acid in certain fertilizers reverts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revert', 'n.', 'One who, or that which, reverts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reverted', 'a.', 'Turned back; reversed. Specifically: (Her.) Bent or\n   curved twice, in opposite directions, or in the form of an S.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revertent', 'n.', 'A remedy which restores the natural order of the\n   inverted irritative motions in the animal system.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reverter', 'n.', 'One who, or that which, reverts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reverter', 'n.', 'Reversion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revertible', 'a.', 'Capable of, or admitting of, reverting or being\n   reverted; as, a revertible estate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revertive', 'a.', 'Reverting, or tending to revert; returning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revery', 'n.', 'Same as Reverie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revest', 'v. t.', 'To clothe again; to cover, as with a robe; to robe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revest', 'v. t.', 'To vest again with possession or office; as, to revest a\n   magistrate with authority.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revest', 'v. i.', 'To take effect or vest again, as a title; to revert to\n   former owner; as, the title or right revests in A after alienation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revestiary', 'n.', 'The apartment, in a church or temple, where the\n   vestments, etc., are kept; -- now contracted into vestry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revestry', 'n.', 'Same as Revestiary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revestture', 'n.', 'Vesture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revetted', 'imp. & p. p.', 'of Revet');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revetting', 'p. pr. & vb. n.', 'of Revet');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revet', 'v. t.', 'To face, as an embankment, with masonry, wood, or other\n   material.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revetment', 'v. t.', 'A facing of wood, stone, or any other material, to\n   sustain an embankment when it receives a slope steeper than the natural\n   slope; also, a retaining wall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revibrate', 'v. i.', 'To vibrate back or in return.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revict', 'v. t.', 'To reconquer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reviction', 'n.', 'Return to life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revictual', 'v. t.', 'To victual again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revie', 'v. t.', 'To vie with, or rival, in return.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revie', 'v. t.', 'To meet a wager on, as on the taking of a trick, with a\n   higher wager.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revie', 'v. i.', 'To exceed an adversarys wager in card playing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revie', 'v. i.', 'To make a retort; to bandy words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Review/d', 'imp. & p. p.', 'of Review');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reveiwing', 'p. pr. & vb. n.', 'of Review');
  INSERT INTO words (word, word_type, description) VALUES  (    'Review', 'n.', 'To view or see again; to look back on.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Review', 'n.', 'To go over and examine critically or deliberately.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Review', 'n.', 'To reconsider; to revise, as a manuscript before printing\n   it, or a book for a new edition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Review', 'n.', 'To go over with critical examination, in order to discover\n   exellences or defects; hence, to write a critical notice of; as, to\n   review a new novel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Review', 'n.', 'To make a formal or official examination of the state of,\n   as troops, and the like; as, to review a regiment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Review', 'n.', 'To reexamine judically; as, a higher court may review the\n   proceedings and judgments of a lower one.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Review', 'n.', 'To retrace; to go over again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Review', 'v. i.', 'To look back; to make a review.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Review', 'n.', 'A second or repeated view; a reexamination; a retrospective\n   survey; a looking over again; as, a review of ones studies; a review\n   of life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Review', 'n.', 'An examination with a view to amendment or improvement;\n   revision; as, an authors review of his works.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Review', 'n.', 'A critical examination of a publication, with remarks; a\n   criticism; a critique.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Review', 'n.', 'A periodical containing critical essays upon matters of\n   interest, as new productions in literature, art, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Review', 'n.', 'An inspection, as of troops under arms or of a naval force,\n   by a high officer, for the purpose of ascertaining the state of\n   discipline, equipments, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Review', 'n.', 'The judicial examination of the proceedings of a lower\n   court by a higher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Review', 'n.', 'A lesson studied or recited for a second time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reviewable', 'a.', 'Capable of being reviewed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reviewal', 'n.', 'A review.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reviewer', 'n.', 'One who reviews or reexamines; an inspector; one who\n   examines publications critically, and publishes his opinion upon their\n   merits; a professional critic of books.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revigorate', 'a.', 'Having new vigor or strength; invigorated anew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revigorate', 'v. t.', 'To give new vigor to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reviled', 'imp. & p. p.', 'of Revile');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reviling', 'p. pr. & vb. n.', 'of Revile');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revile', 'v. t. & i.', 'To address or abuse with opprobrious and\n   contemptuous language; to reproach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revile', 'n.', 'Reproach; reviling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revilement', 'n.', 'The act of reviling; also, contemptuous language;\n   reproach; abuse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reviler', 'n.', 'One who reviles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reviling', 'n.', 'Reproach; abuse; vilification.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reviling', 'a.', 'Uttering reproaches; containing reproaches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revince', 'v. t.', 'To overcome; to refute, as error.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revindicate', 'v. t.', 'To vindicate again; to reclaim; to demand and take\n   back.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revirescence', 'n.', 'A growing green or fresh again; renewal of youth or\n   vigor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revisable', 'a.', 'That may be revised.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revisal', 'n.', 'The act of revising, or reviewing and reexamining for\n   correction and improvement; revision; as, the revisal of a manuscript;\n   the revisal of a proof sheet; the revisal of a treaty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revised', 'imp. & p. p.', 'of Revise');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revising', 'p. pr. & vb. n.', 'of Revise');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revise', 'v. t.', 'To look at again for the detection of errors; to\n   reexamine; to review; to look over with care for correction; as, to\n   revise a writing; to revise a translation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revise', 'v. t.', 'To compare (a proof) with a previous proof of the same\n   matter, and mark again such errors as have not been corrected in the\n   type.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revise', 'v. t.', 'To review, alter, and amend; as, to revise statutes; to\n   revise an agreement; to revise a dictionary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revise', 'n.', 'A review; a revision.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revise', 'n.', 'A second proof sheet; a proof sheet taken after the first\n   or a subsequent correction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reviser', 'n.', 'One who revises.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revision', 'n.', 'The act of revising; reexamination for correction;\n   review; as, the revision of a book or writing, or of a proof sheet; a\n   revision of statutes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revision', 'n.', 'That which is made by revising.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revisional', 'a.', 'Alt. of Revisionary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revisionary', 'a.', 'Of or pertaining to revision; revisory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revisit', 'v. t.', 'To visit again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revisit', 'v. t.', 'To revise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revisitation', 'n.', 'The act of revisiting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revisory', 'a.', 'Having the power or purpose to revise; revising.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revitalize', 'v. t.', 'To restore vitality to; to bring back to life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revivable', 'a.', 'That may be revived.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revival', 'n.', 'The act of reviving, or the state of being revived.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revival', 'n.', 'Renewed attention to something, as to letters or\n   literature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revival', 'n.', 'Renewed performance of, or interest in, something, as the\n   drama and literature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revival', 'n.', 'Renewed interest in religion, after indifference and\n   decline; a period of religious awakening; special religious interest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revival', 'n.', 'Reanimation from a state of langour or depression; --\n   applied to the health, spirits, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revival', 'n.', 'Renewed pursuit, or cultivation, or flourishing state of\n   something, as of commerce, arts, agriculture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revival', 'n.', 'Renewed prevalence of something, as a practice or a\n   fashion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revival', 'n.', 'Restoration of force, validity, or effect; renewal; as,\n   the revival of a debt barred by limitation; the revival of a revoked\n   will, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revival', 'n.', 'Revivification, as of a metal. See Revivification, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revivalism', 'n.', 'The spirit of religious revivals; the methods of\n   revivalists.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revivalist', 'n.', 'A clergyman or layman who promotes revivals of\n   religion; an advocate for religious revivals; sometimes, specifically,\n   a clergyman, without a particular charge, who goes about to promote\n   revivals. Also used adjectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revivalistic', 'a.', 'Pertaining to revivals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revived', 'imp. & p. p.', 'of Revive');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reviving', 'p. pr. & vb. n.', 'of Revive');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revive', 'v. i.', 'To return to life; to recover life or strength; to live\n   anew; to become reanimated or reinvigorated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revive', 'v. i.', 'Hence, to recover from a state of oblivion, obscurity,\n   neglect, or depression; as, classical learning revived in the fifteenth\n   century.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revive', 'v. i.', 'To recover its natural or metallic state, as a metal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revive', 'v. i.', 'To restore, or bring again to life; to reanimate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revive', 'v. i.', 'To raise from coma, languor, depression, or\n   discouragement; to bring into action after a suspension.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revive', 'v. i.', 'Hence, to recover from a state of neglect or disuse; as,\n   to revive letters or learning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revive', 'v. i.', 'To renew in the mind or memory; to bring to\n   recollection; to recall attention to; to reawaken.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revive', 'v. i.', 'To restore or reduce to its natural or metallic state;\n   as, to revive a metal after calcination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revivement', 'n.', 'Revival.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reviver', 'n.', 'One who, or that which, revives.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revivificate', 'v. t.', 'To revive; to recall or restore to life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revivification', 'n.', 'Renewal of life; restoration of life; the act of\n   recalling, or the state of being recalled, to life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revivification', 'n.', 'The reduction of a metal from a state of\n   combination to its metallic state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revivify', 'v. t.', 'To cause to revive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reviving', 'a. & n.', 'Returning or restoring to life or vigor;\n   reanimating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reviviscence', 'n.', 'Alt. of Reviviscency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reviviscency', 'n.', 'The act of reviving, or the state of being revived;\n   renewal of life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reviviscent', 'a.', 'Able or disposed to revive; reviving.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revivor', 'n.', 'Revival of a suit which is abated by the death or marriage\n   of any of the parties, -- done by a bill of revivor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revocability', 'n.', 'The quality of being revocable; as, the revocability\n   of a law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revocable', 'a.', 'Capable of being revoked; as, a revocable edict or\n   grant; a revocable covenant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revocate', 'v. t.', 'To recall; to call back.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revocation', 'n.', 'The act of calling back, or the state of being\n   recalled; recall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revocation', 'n.', 'The act by which one, having the right, annuls an act\n   done, a power or authority given, or a license, gift, or benefit\n   conferred; repeal; reversal; as, the revocation of an edict, a power, a\n   will, or a license.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revocatory', 'a.', 'Of or pertaining to revocation; tending to, or\n   involving, a revocation; revoking; recalling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revoice', 'v. t.', 'To refurnish with a voice; to refit, as an organ pipe,\n   so as to restore its tone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revoked', 'imp. & p. p.', 'of Revoke');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revoking', 'p. pr. & vb. n.', 'of Revoke');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revoke', 'v. t.', 'To call or bring back; to recall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revoke', 'v. t.', 'Hence, to annul, by recalling or taking back; to repeal;\n   to rescind; to cancel; to reverse, as anything granted by a special\n   act; as, , to revoke a will, a license, a grant, a permission, a law,\n   or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revoke', 'v. t.', 'To hold back; to repress; to restrain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revoke', 'v. t.', 'To draw back; to withdraw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revoke', 'v. t.', 'To call back to mind; to recollect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revoke', 'v. i.', 'To fail to follow suit when holding a card of the suit\n   led, in violation of the rule of the game; to renege.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revoke', 'n.', 'The act of revoking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revokement', 'n.', 'Revocation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revoker', 'n.', 'One who revokes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revokingly', 'adv.', 'By way of revocation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revolted', 'imp. & p. p.', 'of Revolt');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revolting', 'p. pr. & vb. n.', 'of Revolt');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revolt', 'n.', 'To turn away; to abandon or reject something; specifically,\n   to turn away, or shrink, with abhorrence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revolt', 'n.', 'Hence, to be faithless; to desert one party or leader for\n   another; especially, to renounce allegiance or subjection; to rise\n   against a government; to rebel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revolt', 'n.', 'To be disgusted, shocked, or grossly offended; hence, to\n   feel nausea; -- with at; as, the stomach revolts at such food; his\n   nature revolts at cruelty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revolt', 'v. t.', 'To cause to turn back; to roll or drive back; to put to\n   flight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revolt', 'v. t.', 'To do violence to; to cause to turn away or shrink with\n   abhorrence; to shock; as, to revolt the feelings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revolt', 'n.', 'The act of revolting; an uprising against legitimate\n   authority; especially, a renunciation of allegiance and subjection to a\n   government; rebellion; as, the revolt of a province of the Roman\n   empire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revolt', 'n.', 'A revolter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revolter', 'n.', 'One who revolts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revolting', 'a.', 'Causing abhorrence mixed with disgust; exciting extreme\n   repugnance; loathsome; as, revolting cruelty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revoluble', 'a.', 'Capable of revolving; rotatory; revolving.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revolute', 'a.', 'Rolled backward or downward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revolution', 'n.', 'The act of revolving, or turning round on an axis or a\n   center; the motion of a body round a fixed point or line; rotation; as,\n   the revolution of a wheel, of a top, of the earth on its axis, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revolution', 'n.', 'Return to a point before occupied, or to a point\n   relatively the same; a rolling back; return; as, revolution in an\n   ellipse or spiral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revolution', 'n.', 'The space measured by the regular return of a revolving\n   body; the period made by the regular recurrence of a measure of time,\n   or by a succession of similar events.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revolution', 'n.', 'The motion of any body, as a planet or satellite, in a\n   curved line or orbit, until it returns to the same point again, or to a\n   point relatively the same; -- designated as the annual, anomalistic,\n   nodical, sidereal, or tropical revolution, according as the point of\n   return or completion has a fixed relation to the year, the anomaly, the\n   nodes, the stars, or the tropics; as, the revolution of the earth about\n   the sun; the revolution of the moon about the earth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revolution', 'n.', 'The motion of a point, line, or surface about a point\n   or line as its center or axis, in such a manner that a moving point\n   generates a curve, a moving line a surface (called a surface of\n   revolution), and a moving surface a solid (called a solid of\n   revolution); as, the revolution of a right-angled triangle about one of\n   its sides generates a cone; the revolution of a semicircle about the\n   diameter generates a sphere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revolution', 'n.', 'A total or radical change; as, a revolution in ones\n   circumstances or way of living.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revolution', 'n.', 'A fundamental change in political organization, or in a\n   government or constitution; the overthrow or renunciation of one\n   government, and the substitution of another, by the governed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revolutionary', 'a.', 'Of or pertaining to a revolution in government;\n   tending to, or promoting, revolution; as, revolutionary war;\n   revolutionary measures; revolutionary agitators.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revolutionary', 'n.', 'A revolutionist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revolutioner', 'n.', 'One who is engaged in effecting a revolution; a\n   revolutionist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revolutionism', 'n.', 'The state of being in revolution; revolutionary\n   doctrines or principles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revolutionist', 'n.', 'One engaged in effecting a change of government; a\n   favorer of revolution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revolutioniezed', 'imp. & p. p.', 'of Revolutionize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revolutionizing', 'p. pr. & vb. n.', 'of Revolutionize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revolutionize', 'v. t.', 'To change completely, as by a revolution; as, to\n   revolutionize a government.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revolutive', 'a.', 'Inclined to revolve things in the mind; meditative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revolvable', 'a.', 'That may be revolved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revolved', 'imp. & p. p.', 'of Revolve');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revolving', 'p. pr. & vb. n.', 'of Revolve');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revolve', 'v. i.', 'To turn or roll round on, or as on, an axis, like a\n   wheel; to rotate, -- which is the more specific word in this sense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revolve', 'v. i.', 'To move in a curved path round a center; as, the\n   planets revolve round the sun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revolve', 'v. i.', 'To pass in cycles; as, the centuries revolve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revolve', 'v. i.', 'To return; to pass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revolve', 'v. t.', 'To cause to turn, as on an axis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revolve', 'v. t.', 'Hence, to turn over and over in the mind; to reflect\n   repeatedly upon; to consider all aspects of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revolvement', 'n.', 'Act of revolving.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revolvency', 'n.', 'The act or state of revolving; revolution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revolver', 'n.', 'One who, or that which, revolves; specifically, a firearm\n   ( commonly a pistol) with several chambers or barrels so arranged as to\n   revolve on an axis, and be discharged in succession by the same lock; a\n   repeater.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revolving', 'a.', 'Making a revolution or revolutions; rotating; -- used\n   also figuratively of time, seasons, etc., depending on the revolution\n   of the earth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revulse', 'v. t.', 'To pull back with force.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revulsion', 'n.', 'A strong pulling or drawing back; withdrawal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revulsion', 'n.', 'A sudden reaction; a sudden and complete change; --\n   applied to the feelings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revulsion', 'n.', 'The act of turning or diverting any disease from one\n   part of the body to another. It resembles derivation, but is usually\n   applied to a more active form of counter irritation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revulsive', 'a.', 'Causing, or tending to, revulsion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Revulsive', 'n.', 'That which causes revulsion; specifically (Med.), a\n   revulsive remedy or agent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rew', 'n.', 'A row.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rewake', 'v. t. & i.', 'To wake again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rewarded', 'imp. & p. p.', 'of Reward');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rewarding', 'p. pr. & vb. n.', 'of Reward');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reward', 'v. t.', 'To give in return, whether good or evil; -- commonly in\n   a good sense; to requite; to recompense; to repay; to compensate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reward', 'n.', 'Regard; respect; consideration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reward', 'n.', 'That which is given in return for good or evil done or\n   received; esp., that which is offered or given in return for some\n   service or attainment, as for excellence in studies, for the return of\n   something lost, etc.; recompense; requital.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reward', 'n.', 'Hence, the fruit of ones labor or works.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reward', 'n.', 'Compensation or remuneration for services; a sum of money\n   paid or taken for doing, or forbearing to do, some act.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rewardable', 'a.', 'Worthy of reward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rewarder', 'n.', 'One who rewards.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rewardful', 'a.', 'Yielding reward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rewardless', 'a.', 'Having, or affording, no reward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rewe', 'v. t. & i.', 'To rue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rewel bone', '', 'An obsolete phrase of disputed meaning, -- perhaps,\n   smooth or polished bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rewet', 'n.', 'A gunlock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rewful', 'a.', 'Rueful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rewin', 'v. t.', 'To win again, or win back.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rewle', 'n. & v.', 'Rule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rewme', 'n.', 'Realm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reword', 'v. t.', 'To repeat in the same words; to reecho.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reword', 'v. t.', 'To alter the wording of; to restate in other words; as,\n   to reword an idea or a passage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rewrite', 'v. t.', 'To write again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rewth', 'n.', 'Ruth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reges', 'pl. ', 'of Rex');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rex', 'n.', 'A king.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reyn', 'n.', 'Rain or rein.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reynard', 'n.', 'An appelation applied after the manner of a proper name to\n   the fox. Same as Renard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reyse', 'v. t.', 'To raise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Reyse', 'v. i.', 'To go on a military expedition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhabarbarate', 'a.', 'Impregnated or tinctured with rhubarb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhabarbarin', 'n.', 'Alt. of Rhabarbarine');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhabarbarine', 'n.', 'Chrysophanic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhabdite', 'n.', 'A minute smooth rodlike or fusiform structure found in\n   the tissues of many Turbellaria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhabdite', 'n.', 'One of the hard parts forming the ovipositor of insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhabdocoela', 'n. pl.', 'A suborder of Turbellaria including those that\n   have a simple cylindrical, or saclike, stomach, without an intestine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhabdocoelous', 'a.', 'Of or pertaining to the Rhabdocoela.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhabdoidal', 'a.', 'See Sagittal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhabdolith', 'n.', 'A minute calcareous rodlike structure found both at the\n   surface and the bottom of the ocean; -- supposed by some to be a\n   calcareous alga.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhabdology', 'n.', 'Same as Rabdology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhabdom', 'n.', 'One of numerous minute rodlike structures formed of two or\n   more cells situated behind the retinulae in the compound eyes of\n   insects, etc. See Illust. under Ommatidium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhabdomancy', 'n.', 'Same as Rabdomancy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhabdomere', 'n.', 'One of the several parts composing a rhabdom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhabdophora', 'n. pl.', 'An extinct division of Hydrozoa which includes the\n   graptolities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhabdopleura', 'n.', 'A genus of marine Bryozoa in which the tubular cells\n   have a centralchitinous axis and the tentacles are borne on a bilobed\n   lophophore. It is the type of the order Pterobranchia, or Podostomata');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhabdosphere', 'n.', 'A minute sphere composed of rhabdoliths.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhachialgia', 'n.', 'See Rachialgia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhachidian', 'a.', 'Of or pertaining to the rhachis; as, the rhachidian\n   teeth of a mollusk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhachiglossa', 'n. pl.', 'A division of marine gastropods having a\n   retractile proboscis and three longitudinal rows of teeth on the\n   radula. It includes many of the large ornamental shells, as the miters,\n   murices, olives, purpuras, volutes, and whelks. See Illust. in Append.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhachilla', 'n.', 'A branch of inflorescence; the zigzag axis on which the\n   florets are arranged in the spikelets of grasses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhachiodont', 'a.', 'Having gular teeth formed by a peculiar modification\n   of the inferior spines of some of the vertebrae, as certain South\n   African snakes (Dasypeltis) which swallow birds eggs and use these\n   gular teeth to crush them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhachises', 'pl. ', 'of Rhachis');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhachides', 'pl. ', 'of Rhachis');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhachis', 'n.', 'The spine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhachis', 'n.', 'The continued stem or midrib of a pinnately compound leaf,\n   as in a rose leaf or a fern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhachis', 'n.', 'The principal axis in a raceme, spike, panicle, or corymb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhachis', 'n.', 'The shaft of a feather. The rhachis of the after-shaft, or\n   plumule, is called the hyporhachis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhachis', 'n.', 'The central cord in the stem of a crinoid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhachis', 'n.', 'The median part of the radula of a mollusk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhachis', 'n.', 'A central cord of the ovary of nematodes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhachitis', 'n.', 'See Rachitis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhadamanthine', 'a.', 'Of or pertaining to Rhadamanthus; rigorously just;\n   as, a Rhadamanthine judgment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhadamanthus', 'n.', 'One of the three judges of the infernal regions;\n   figuratively, a strictly just judge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rh/tian', 'a & n.', 'Rhetain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rh/tic', 'a.', 'Pertaining to, or of the same horizon as, certain Mesozoic\n   strata of the Rhetian Alps. These strata are regarded as closing the\n   Triassic period. See the Chart of Geology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rh/tizite', 'n.', 'A variety of the mineral cyanite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhamadan', 'n.', 'See Ramadan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhamnaceous', 'a.', 'Of or pertaining to a natural order of shrubs and\n   trees (Rhamnaceae, or Rhamneae) of which the buckthorn (Rhamnus) is the\n   type. It includes also the New Jersey tea, the supple-jack, and one of\n   the plants called lotus (Zizyphus).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhamnus', 'n.', 'A genus of shrubs and small trees; buckthorn. The\n   California Rhamnus Purshianus and the European R. catharticus are used\n   in medicine. The latter is used for hedges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhamphorhynchus', 'n.', 'A genus of pterodactyls in which the elongated\n   tail supported a leathery expansion at the tip.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhamphothecae', 'pl. ', 'of Rhamphotheca');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhamphotheca', 'n.', 'The horny covering of the bill of birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhaphe', 'n.', 'The continuation of the seed stalk along the side of an\n   anatropous ovule or seed, forming a ridge or seam.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhaphides', 'n. pl.', 'Minute transparent, often needle-shaped, crystals\n   found in the tissues of plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhaponticine', 'n.', 'Chrysophanic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhapsode', 'n.', 'A rhapsodist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhapsoder', 'n.', 'A rhapsodist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhapsodic', 'a.', 'Alt. of Rhapsodic');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhapsodic', 'a.', 'Of or pertaining to rhapsody; consisting of rhapsody;\n   hence, confused; unconnected.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhapsodist', 'n.', 'Anciently, one who recited or composed a rhapsody;\n   especially, one whose profession was to recite the verses of Hormer and\n   other epic poets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhapsodist', 'n.', 'Hence, one who recites or sings poems for a livelihood;\n   one who makes and repeats verses extempore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhapsodist', 'n.', 'One who writes or speaks disconnectedly and with great\n   excitement or affectation of feeling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhapsodized', 'imp. & p. p.', 'of Rhapsodize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhapsodizing', 'p. pr. & vb. n.', 'of Rhapsodize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhapsodize', 'v. t.', 'To utter as a rhapsody, or in the manner of a\n   rhapsody');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhapsodize', 'v. i.', 'To utter rhapsodies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhapsodomancy', 'n.', 'Divination by means of verses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhapsodies', 'pl. ', 'of Rhapsody');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhapsody', 'n.', 'A recitation or song of a rhapsodist; a portion of an\n   epic poem adapted for recitation, or usually recited, at one time;\n   hence, a division of the Iliad or the Odyssey; -- called also a book.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhapsody', 'n.', 'A disconnected series of sentences or statements composed\n   under excitement, and without dependence or natural connection;\n   rambling composition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhapsody', 'n.', 'A composition irregular in form, like an improvisation;\n   as, Liszts \"Hungarian Rhapsodies.\"');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhatany', 'n.', 'Alt. of Rhatanhy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhatanhy', 'n.', 'The powerfully astringent root of a half-shrubby Peruvian\n   plant (Krameria triandra). It is used in medicine and to color port\n   wine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhea', 'n.', 'The ramie or grass-cloth plant. See Grass-cloth plant, under\n   Grass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhea', 'n.', 'Any one of three species of large South American ostrichlike\n   birds of the genera Rhea and Pterocnemia. Called also the American\n   ostrich.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rheae', 'n. pl.', 'A suborder of struthious birds including the rheas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rheeboc', 'n.', 'The peele.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rheic', 'a.', 'Pertaining to, or designating, an acid (commonly called\n   chrysophanic acid) found in rhubarb (Rheum).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhein', 'n.', 'Chrysophanic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rheinberry', 'n.', 'One of the berries or drupes of the European buckthorn;\n   also, the buckthorn itself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhematic', 'a.', 'Having a verb for its base; derived from a verb; as,\n   rhematic adjectives.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhematic', 'n.', 'The doctrine of propositions or sentences.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhemish', 'a.', 'Of or pertaining to Rheimis, or Reima, in France.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhenish', 'a.', 'Of or pertaining to the river Rhine; as, Rhenish wine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhenish', 'n.', 'Rhine wine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rheochord', 'n.', 'A metallic wire used for regulating the resistance of a\n   circuit, or varying the strength of an electric current, by inserting a\n   greater or less length of it in the circuit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rheometer', 'n.', 'An instrument for measuring currents, especially the\n   force or intensity of electrical currents; a galvanometer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rheometer', 'n.', 'An instrument for measuring the velocity of the blood\n   current in the arteries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rheometric', 'a.', 'Of or pertaining to a rheometer or rheometry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rheometry', 'n.', 'The measurement of the force or intensity of currents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rheometry', 'n.', 'The calculus; fluxions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rheomotor', 'n.', 'Any apparatus by which an electrical current is\n   originated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rheophore', 'n.', 'A connecting wire of an electric or voltaic apparatus,\n   traversed by a current.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rheophore', 'n.', 'One of the poles of a voltaic battery; an electrode.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rheoscope', 'n.', 'An instrument for detecting the presence or movement of\n   currents, as of electricity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rheostat', 'n.', 'A contrivance for adjusting or regulating the strength of\n   electrical currents, operating usually by the intercalation of\n   resistance which can be varied at will.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rheotome', 'n.', 'An instrument which periodically or otherwise interrupts\n   an electric current.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rheotrope', 'n.', 'An instrument for reversing the direction of an electric\n   current.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhesus', 'n.', 'A monkey; the bhunder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhetian', 'a.', 'Pertaining to the ancient Rhaeti, or Rhaetians, or to\n   Rhaetia, their country; as, the Rhetian Alps, now the country of Tyrol\n   and the Grisons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhetic', 'a.', 'Same as Rhaetic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhetizite', 'n.', 'Same as Rhaetizite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhetor', 'n.', 'A rhetorician.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhetoric', 'n.', 'The art of composition; especially, elegant composition\n   in prose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhetoric', 'n.', 'Oratory; the art of speaking with propriety, elegance,\n   and force.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhetoric', 'n.', 'Hence, artificial eloquence; fine language or declamation\n   without conviction or earnest feeling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhetoric', 'n.', 'Fig. : The power of persuasion or attraction; that which\n   allures or charms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhetorical', 'a.', 'Of or pertaining to rhetoric; according to, or\n   exhibiting, rhetoric; oratorical; as, the rhetorical art; a rhetorical\n   treatise; a rhetorical flourish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhetoricate', 'v. i.', 'To play the orator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhetorication', 'n.', 'Rhetorical amplification.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhetorician', 'n.', 'One well versed in the rules and principles of\n   rhetoric.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhetorician', 'n.', 'A teacher of rhetoric.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhetorician', 'n.', 'An orator; specifically, an artificial orator without\n   genuine eloquence; a declaimer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhetorician', 'a.', 'Suitable to a master of rhetoric.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhetorized', 'imp. & p. p.', 'of Rhetorize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhetorizing', 'p. pr. & vb. n.', 'of Rhetorize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhetorize', 'v. i.', 'To play the orator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhetorize', 'v. t.', 'To represent by a figure of rhetoric, or by\n   personification.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rheum', 'n.', 'A genus of plants. See Rhubarb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rheum', 'n.', 'A serous or mucous discharge, especially one from the eves\n   or nose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rheumatic', 'a.', 'Derived from, or having the character of, rheum;\n   rheumic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rheumatic', 'a.', 'Of or pertaining to rheumatism; as, rheumatic pains or\n   affections; affected with rheumatism; as, a rheumatic old man; causing\n   rheumatism; as, a rheumatic day.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rheumatic', 'n.', 'One affected with rheumatism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rheumatism', 'n.', 'A general disease characterized by painful, often\n   multiple, local inflammations, usually affecting the joints and\n   muscles, but also extending sometimes to the deeper organs, as the\n   heart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rheumatismal', 'a.', 'Of or pertaining to rheumatism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rheumatismoid', 'a.', 'Of or resembling rheum or rheumatism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rheumic', 'a.', 'Pertaining to, or characterized by, rheum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rheumides', 'n. pl.', 'The class of skin disease developed by the dartrous\n   diathesis. See under Dartrous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rheumy', 'a.', 'Of or pertaining to rheum; abounding in, or causing, rheum;\n   affected with rheum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhigolene', 'n.', 'A mixture of volatile hydrocarbons intermediate between\n   gsolene and cymogene. It is obtained in the purification of crude\n   petroleum, and is used as a refregerant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhime', 'n.', 'See Rhyme.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhinal', 'a.', 'Og or pertaining to the nose or olfactory organs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhinaster', 'n.', 'The borele.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhine', 'n.', 'A water course; a ditch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhinencephalic', 'a.', 'Of or pertaining to the rhinencephalon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhinencephala', 'pl. ', 'of Rhinencephalon');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhinencephalon', 'n.', 'The division of the brain in front of the\n   prosencephalon, consisting of the two olfactory lobes from which the\n   olfactory nerves arise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhinestone', 'n.', 'A colorless stone of high luster, made of paste. It is\n   much used as an inexpensive ornament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhinitis', 'n.', 'Infllammation of the nose; esp., inflammation of the\n   mucous membrane of the nostrils.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhino', 'n.', 'Gold and silver, or money.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhino-', '', 'A combining form from Greek //, ///, the nose, as in\n   rhinolith, rhinology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhinocerial', 'a.', 'Alt. of Rhinocerical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhinocerical', 'a.', 'Of or pertaining to the rhinoceros; resembling the\n   rhinoceros, or his horn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhinoceros', 'n.', 'Any pachyderm belonging to the genera Rhinoceros,\n   Atelodus, and several allied genera of the family Rhinocerotidae, of\n   which several living, and many extinct, species are known. They are\n   large and powerful, and usually have either one or two stout conical\n   median horns on the snout.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhinocerote', 'n.', 'A rhinoceros.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhinocerotic', 'a.', 'Of or pertaining to the rhinoceros.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhinolite', 'n.', 'Alt. of Rhinolith');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhinolith', 'n.', 'A concretion formed within the cavities of the nose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhinological', 'a.', 'Of or pertaining to rhinology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhinologist', 'n.', 'One skilled in rhinology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhinology', 'n.', 'The science which treats of the nose, and its diseases.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhinolophid', 'n.', 'Any species of the genus Rhinilophus, or family\n   Rhinolophidae, having a horseshoe-shaped nasal crest; a horseshoe bat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhinolophine', 'a.', 'Like or pertaining to the rhinolophids, or horseshoe\n   bats.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhinophore', 'n.', 'One of the two tentacle-like organs on the back of the\n   head or neck of a nudibranch or tectibranch mollusk. They are usually\n   retractile, and often transversely furrowed or plicate, and are\n   regarded as olfactory organs. Called also dorsal tentacles. See Illust.\n   under Pygobranchia, and Opisthobranchia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhinoplastic', 'a.', 'Of or pertaining to rhinoplasty; as, a rhinoplastic\n   operation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhinoplasty', 'n.', 'Plastic surgery of the nose to correct deformity or to\n   replace lost tissue. Tissue may be transplanted from the patients\n   cheek, forehead, arm, etc., or even from another person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhinopome', 'n.', 'Any old-world bat of the genus Rhinopoma. The rhinopomes\n   have a long tail extending beyond the web, and inhabit caves and tombs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhinoscleroma', 'n.', 'A rare disease of the skin, characterized by the\n   development of very hard, more or less flattened, prominences,\n   appearing first upon the nose and subsequently upon the neighboring\n   parts, esp. the lips, palate, and throat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhinoscope', 'n.', 'A small mirror for use in rhinoscopy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhinoscopic', 'a.', 'Of or pertaining to rhinoscopy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhinoscopy', 'n.', 'The examination or study of the soft palate, posterior\n   nares, etc., by means of a laryngoscopic mirror introduced into the\n   pharynx.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhinothecae', 'pl. ', 'of Rhinotheca');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhinotheca', 'n.', 'The sheath of the upper mandible of a bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhipidoglossa', 'n. pl.', 'A division of gastropod mollusks having a large\n   number of long, divergent, hooklike, lingual teeth in each transverse\n   row. It includes the scutibranchs. See Illustration in Appendix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhipipter', 'n.', 'One of the Rhipiptera, a group of insects having wings\n   which fold like a fan; a strepsipter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhipipteran', 'n.', 'Same as Rhipipter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhizanthous', 'a.', 'Producing flowers from a rootstock, or apparently from\n   a root.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhizine', 'n.', 'A rootlike filament or hair growing from the stems of\n   mosses or on lichens; a rhizoid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhizocarpous', 'a.', 'Having perennial rootstocks or bulbs, but annual\n   flowering stems; -- said of all perennial herbs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhizocephala', 'n. pl.', 'A division of Pectostraca including saclike\n   parasites of Crustacea. They adhere by rootlike extensions of the head.\n   See Illusration in Appendix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhizodont', 'n.', 'A reptile whose teeth are rooted in sockets, as the\n   crocodile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhizogan', 'a.', 'Prodicing roots.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhizogen', 'n.', 'One of a proposed class of flowering plants growning on\n   the roots of other plants and destitute of green foliage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhizoid', 'n.', 'A rootlike appendage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhizomata', 'pl. ', 'of Rhizoma');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhizoma', 'n.', 'SAme as Rhizome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhizomatous', 'a.', 'Having the nature or habit of a rhizome or rootstock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhizome', 'n.', 'A rootstock. See Rootstock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhizophaga', 'n. pl.', 'A division of marsupials. The wombat is the type.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhizophagous', 'a.', 'Feeding on roots; root-eating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhizophora', 'n.', 'A genus of trees including the mangrove. See Mangrove.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhizophorous', 'a.', 'Bearing roots.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhizopod', 'n.', 'One of the Rhizopoda.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhizopoda', 'n. pl.', 'An extensive class of Protozoa, including those\n   which have pseudopodia, by means of which they move about and take\n   their food. The principal groups are Lobosa (or Am/bea), Helizoa,\n   Radiolaria, and Foraminifera (or Reticularia). See Protozoa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhizopodous', 'a.', 'Of or pertaining to the rhizopods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhizostomata', 'n. pl.', 'A suborder of Medusae which includes very large\n   species without marginal tentacles, but having large mouth lobes\n   closely united at the edges. See Illust. in Appendix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhizostome', 'n.', 'One of the Rhizostomata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhizotaxis', 'n.', 'The arrangement of the roots of plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhob', 'n.', 'See 1st Rob.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhodammonium', 'a.', 'Pertaining to, derived from, or containing, rhodium\n   and ammonia; -- said of certain complex compounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhodanate', 'n.', 'A salt of rhodanic acid; a sulphocyanate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhodanic', 'a.', 'Pertaining to, or designating, an acid (commonly called\n   sulphocyanic acid) which frms a red color with ferric salts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhodeoretin', 'n.', 'Same as Convolvuln.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhodian', 'a.', 'Of or pertaining to Rhodes, an island of the\n   Mediterranean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhodian', 'n.', 'A native or inhabitant of Rhodes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhodic', 'a.', 'Of or pertaining to rhodium; containing rhodium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhodium', 'n.', 'A rare element of the light platinum group. It is found in\n   platinum ores, and obtained free as a white inert metal which it is\n   very difficult to fuse. Symbol Rh. Atomic weight 104.1. Specific\n   gravity 12.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhodizonic', 'a.', 'Pertaining to, or designating, a colorless crystalline\n   substance (called rhodizonic acid, and carboxylic acid) obtained from\n   potassium carboxide and from certain quinones. It forms brilliant red,\n   yellow, and purple salts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhodochrosite', 'n.', 'Manganese carbonate, a rose-red mineral sometimes\n   occuring crystallized, but generally massive with rhombohedral cleavage\n   like calcite; -- called also dialogite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhodocrinite', 'n.', 'A rose encrinite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhododendron', 'n.', 'A genus of shrubs or small trees, often having\n   handsome evergreen leaves, and remarkable for the beauty of their\n   flowers; rosebay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhodomontade', 'n.', 'See Rodomontade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhodomontader', 'n.', 'See Rodomontador.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhodonite', 'n.', 'Manganese spar, or silicate of manganese, a mineral\n   occuring crystallised and in rose-red masses. It is often used as an\n   ornamental stone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhodophane', 'n.', 'The red pigment contained in the inner segments of the\n   cones of the retina in animals. See Chromophane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhodopsin', 'n.', 'The visual purple. See under Visual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhodosperm', 'n.', 'Any seaweed with red spores.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhomb', 'n.', 'An equilateral parallelogram, or quadrilateral figure whose\n   sides are equal and the opposite sides parallel. The angles may be\n   unequal, two being obtuse and two acute, as in the cut, or the angles\n   may be equal, in which case it is usually called a square.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhomb', 'n.', 'A rhombohedron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhombic', 'a.', 'Shaped like a rhomb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhombic', 'a.', 'Same as Orthorhombic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhomboganoid', 'n.', 'A ganoid fish having rhombic enameled scales; one of\n   the Rhomboganoidei.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhomboganoidei', 'n. pl.', 'Same as Ginglymodi.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhombogene', 'n.', 'A dicyemid which produces infusorialike embryos; --\n   opposed to nematogene. See Dicyemata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhombohedral', 'a.', 'Related to the rhombohedron; presenting the form of a\n   rhombohedron, or a form derivable from a rhombohedron; relating to a\n   system of forms including the rhombohedron and scalenohedron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhombohedric', 'a.', 'Rhombohedral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhombohedron', 'n.', 'A solid contained by six rhomboids; a parallelopiped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhomboid', 'n.', 'An oblique-angled parallelogram like a rhomb, but having\n   only the opposite sides equal, the length and with being different.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhomboid', 'a.', 'Same as Rhomboidal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhomboidal', 'a.', 'Having, or approaching, the shape of a rhomboid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhomboides', 'n.', 'A rhomboid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhomboid-ovate', 'a.', 'Between rhomboid and ovate, or oval, in shape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhomb spar', '', 'A variety of dolomite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhombus', 'n.', 'Same as Rhomb, 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhonchal', 'a.', 'Rhonchial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhonchial', 'a.', 'Of or pertaining to a rhonchus; produced by rhonchi.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhonchisonant', 'a.', 'Making a snorting noise; snorting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhonchi', 'pl. ', 'of Rhonchus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhonchus', 'n.', 'An adventitious whistling or snoring sound heard on\n   auscultation of the chest when the air channels are partially\n   obstructed. By some writers the term rhonchus is used as equivalent to\n   rale in its widest sense. See Rale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhopalic', 'a.', 'Applied to a line or verse in which each successive word\n   has one more syllable than the preceding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhopalia', 'pl. ', 'of Rhopalium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhopalium', 'n.', 'One of the marginal sensory bodies of medusae belonging\n   to the Discophora.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhopalocera', 'n. pl.', 'A division of Lepidoptera including all the\n   butterflies. They differ from other Lepidoptera in having club-shaped\n   antennae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhotacism', 'n.', 'An oversounding, or a misuse, of the letter r;\n   specifically (Phylol.), the tendency, exhibited in the Indo-European\n   languages, to change s to r, as wese to were.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhubarb', 'n.', 'The name of several large perennial herbs of the genus\n   Rheum and order Polygonaceae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhubarb', 'n.', 'The large and fleshy leafstalks of Rheum Rhaponticum and\n   other species of the same genus. They are pleasantly acid, and are used\n   in cookery. Called also pieplant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhubarb', 'n.', 'The root of several species of Rheum, used much as a\n   cathartic medicine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhubarby', 'a.', 'Like rhubarb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhumb', 'n.', 'A line which crosses successive meridians at a constant\n   angle; -- called also rhumb line, and loxodromic curve. See Loxodromic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhus', 'n.', 'A genus of shrubs and small treets. See Sumac.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhusma', 'n.', 'A mixtire of caustic lime and orpiment, or tersulphide of\n   arsenic, -- used in the depilation of hides.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhyme', 'n.', 'An expression of thought in numbers, measure, or verse; a\n   composition in verse; a rhymed tale; poetry; harmony of language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhyme', 'n.', 'Correspondence of sound in the terminating words or\n   syllables of two or more verses, one succeeding another immediately or\n   at no great distance. The words or syllables so used must not begin\n   with the same consonant, or if one begins with a vowel the other must\n   begin with a consonant. The vowel sounds and accents must be the same,\n   as also the sounds of the final consonants if there be any.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhyme', 'n.', 'Verses, usually two, having this correspondence with each\n   other; a couplet; a poem containing rhymes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhyme', 'n.', 'A word answering in sound to another word.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhymed', 'imp. & p. p.', 'of Rhyme');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhyming', 'p. pr. & vb. n.', 'of Rhyme');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhyme', 'n.', 'To make rhymes, or verses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhyme', 'n.', 'To accord in rhyme or sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhyme', 'v. t.', 'To put into rhyme.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhyme', 'v. t.', 'To influence by rhyme.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhymeless', 'a.', 'Destitute of rhyme.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhymer', 'n.', 'One who makes rhymes; a versifier; -- generally in\n   contempt; a poor poet; a poetaster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhymery', 'n.', 'The art or habit of making rhymes; rhyming; -- in\n   contempt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhymester', 'n.', 'A rhymer; a maker of poor poetry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhymic', 'a.', 'Pertaining to rhyme.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhymist', 'n.', 'A rhymer; a rhymester.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhynchobdellea', 'n. pl.', 'A suborder of leeches including those that have\n   a protractile proboscis, without jaws. Clepsine is the type.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhynchocephala', 'n. pl.', 'An order of reptiles having biconcave\n   vertebrae, immovable quadrate bones, and many other peculiar\n   osteological characters. Hatteria is the only living genus, but\n   numerous fossil genera are known, some of which are among the earliest\n   of reptiles. See Hatteria. Called also Rhynchocephalia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhynchocoela', 'n. pl.', 'Same as Nemertina.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhyncholite', 'n.', 'A fossil cephalopod beak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhynchonella', 'n.', 'A genus of brachiopods of which some species are\n   still living, while many are found fossil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhynchophora', 'n. pl.', 'A group of Coleoptera having a snoutlike head;\n   the snout beetles, curculios, or weevils.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhynchophore', 'n.', 'One of the Rhynchophora.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhynchota', 'n. pl.', 'Same as Hemiptera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhyolite', 'n.', 'A quartzose trachyte, an igneous rock often showing a\n   fluidal structure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhyparography', 'n.', 'In ancient art, the painting of genre or still-life\n   pictures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhysimeter', 'n.', 'An instrument, acting on the principle of Pitots tube,\n   for measuring the velocity of a fluid current, the speed of a ship,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhythm', 'n.', 'In the widest sense, a dividing into short portions by a\n   regular succession of motions, impulses, sounds, accents, etc.,\n   producing an agreeable effect, as in music poetry, the dance, or the\n   like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhythm', 'n.', 'Movement in musical time, with periodical recurrence of\n   accent; the measured beat or pulse which marks the character and\n   expression of the music; symmetry of movement and accent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhythm', 'n.', 'A division of lines into short portions by a regular\n   succession of arses and theses, or percussions and remissions of voice\n   on words or syllables.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhythm', 'n.', 'The harmonious flow of vocal sounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhythmer', 'n.', 'One who writes in rhythm, esp. in poetic rhythm or meter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhythmic', 'a.', 'Alt. of Rhythmical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhythmical', 'a.', 'Pertaining to, or of the nature of, rhythm');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhythmically', 'adv.', 'In a rhythmical manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhythmics', 'n.', 'The department of musical science which treats of the\n   length of sounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhythming', 'a.', 'Writing rhythm; verse making.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhythmless', 'a.', 'Being without rhythm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhythmometer', 'n.', 'An instrument for marking time in musical movements.\n   See Metronome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhythmus', 'n.', 'Rhythm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rhytina', 'n.', 'See Rytina.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rial', 'n.', 'A Spanish coin. See Real.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rial', 'a.', 'Royal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rial', 'n.', 'A gold coin formerly current in England, of the value of ten\n   shillings sterling in the reign of Henry VI., and of fifteen shillings\n   in the reign of Elizabeth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Riant', 'a.', 'Laughing; laughable; exciting gayety; gay; merry; delightful\n   to the view, as a landscape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rib', 'n.', 'One of the curved bones attached to the vertebral column and\n   supporting the lateral walls of the thorax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rib', 'n.', 'That which resembles a rib in form or use.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rib', 'n.', 'One of the timbers, or bars of iron or steel, that branch\n   outward and upward from the keel, to support the skin or planking, and\n   give shape and strength to the vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rib', 'n.', 'A ridge, fin, or wing, as on a plate, cylinder, beam, etc., to\n   strengthen or stiffen it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rib', 'n.', 'One of the rods on which the cover of an umbrella is extended.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rib', 'n.', 'A prominent line or ridge, as in cloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rib', 'n.', 'A longitudinal strip of metal uniting the barrels of a\n   double-barreled gun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rib', 'n.', 'The chief nerve, or one of the chief nerves, of a leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rib', 'n.', 'Any longitudinal ridge in a plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rib', 'n.', 'In Gothic vaulting, one of the primary members of the vault.\n   These are strong arches, meeting and crossing one another, dividing the\n   whole space into triangles, which are then filled by vaulted\n   construction of lighter material. Hence, an imitation of one of these\n   in wood, plaster, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rib', 'n.', 'A projecting mold, or group of moldings, forming with others a\n   pattern, as on a ceiling, ornamental door, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rib', 'n.', 'Solid coal on the side of a gallery; solid ore in a vein.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rib', 'n.', 'An elongated pillar of ore or coal left as a support.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rib', 'n.', 'A wife; -- in allusion to Eve, as made out of Adams rib.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ribbed', 'imp. & p. p.', 'of Rib');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ribbing', 'p. pr. & vb. n.', 'of Rib');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rib', 'v. t.', 'To furnish with ribs; to form with rising lines and\n   channels; as, to rib cloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rib', 'v. t.', 'To inclose, as with ribs, and protect; to shut in.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ribald', 'n./', 'A low, vulgar, brutal, foul-mouthed wretch; a lewd fellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ribald', 'a.', 'Low; base; mean; filthy; obscene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ribaldish', 'a.', 'Like a ribald.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ribaldrous', 'a.', 'Of a ribald quality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ribaldry', 'n.', 'The talk of a ribald; low, vulgar language; indecency;\n   obscenity; lewdness; -- now chiefly applied to indecent language, but\n   formerly, as by Chaucer, also to indecent acts or conduct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Riban', 'n.', 'See Ribbon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Riband', 'n.', 'See Ribbon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Riband', 'n.', 'See Rib-band.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ribanded', 'a.', 'Ribboned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ribaud', 'n.', 'A ribald.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ribaudequin', 'n.', 'An engine of war used in the Middle Ages, consisting\n   of a protected elevated staging on wheels, and armed in front with\n   pikes. It was (after the 14th century) furnished with small cannon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ribaudequin', 'n.', 'A huge bow fixed on the wall of a fortified town for\n   casting javelins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ribaudred', 'a.', 'Alt. of Ribaudrous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ribaudrous', 'a.', 'Filthy; obscene; ribald.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ribaudry', 'n.', 'Ribaldry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ribaudy', 'n.', 'Ribaldry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ribauld', 'n.', 'A ribald.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ribband', 'n.', 'A ribbon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ribband', 'n.', 'A long, narrow strip of timber bent and bolted\n   longitudinally to the ribs of a vessel, to hold them in position, and\n   give rigidity to the framework.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ribbed', 'a.', 'Furnished or formed with ribs; as, a ribbed cylinder;\n   ribbed cloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ribbed', 'a.', 'Intercalated with slate; -- said of a seam of coal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ribbing', 'n.', 'An assemblage or arrangement of ribs, as the timberwork\n   for the support of an arch or coved ceiling, the veins in the leaves of\n   some plants, ridges in the fabric of cloth, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ribbon', 'n.', 'A fillet or narrow woven fabric, commonly of silk, used for\n   trimming some part of a womans attire, for badges, and other\n   decorative purposes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ribbon', 'n.', 'A narrow strip or shred; as, a steel or magnesium ribbon;\n   sails torn to ribbons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ribbon', 'n.', 'Same as Rib-band.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ribbon', 'n.', 'Driving reins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ribbon', 'n.', 'A bearing similar to the bend, but only one eighth as wide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ribbon', 'n.', 'A silver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ribboned', 'imp. & p. p.', 'of Ribbon');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ribboning', 'p. pr. & vb. n.', 'of Ribbon');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ribbon', 'v. t.', 'To adorn with, or as with, ribbons; to mark with stripes\n   resembling ribbons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ribbonism', 'n.', 'The principles and practices of the Ribbonmen. See\n   Ribbon Society, under Ribbon.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-men', 'pl. ', 'of Ribbonman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ribbonman', 'n.', 'A member of the Ribbon Society. See Ribbon Society,\n   under Ribbon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ribbonwood', 'n.', 'A malvaceous tree (Hoheria populnea) of New Zealand,\n   the bark of which is used for cordage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ribes', 'n.', 'A genus of shrubs including gooseberries and currants of\n   many kinds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ribibe', 'n.', 'A sort of stringed instrument; a rebec.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ribibe', 'n.', 'An old woman; -- in contempt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ribibe', 'n.', 'A bawd; a prostitute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ribible', 'n.', 'A small threestringed viol; a rebec.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ribless', 'a.', 'Having no ribs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ribroast', 'v. t.', 'To beat soundly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ribwort', 'n.', 'A species of plantain (Plantago lanceolata) with long,\n   narrow, ribbed leaves; -- called also rib grass, ripple grass, ribwort\n   plantain.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-ric', '', 'A suffix signifying dominion, jurisdiction; as, bishopric, the\n   district over which a bishop exercises authority.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rice', 'n.', 'A well-known cereal grass (Oryza sativa) and its seed. This\n   plant is extensively cultivated in warm climates, and the grain forms a\n   large portion of the food of the inhabitants. In America it grows\n   chiefly on low, moist land, which can be overflowed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ricebird', 'n.', 'The Java sparrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ricebird', 'n.', 'The bobolink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rice-shell', 'n.', 'Any one of numerous species of small white polished\n   marine shells of the genus Olivella.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rich', 'superl.', 'Having an abundance of material possessions; possessed\n   of a large amount of property; well supplied with land, goods, or\n   money; wealthy; opulent; affluent; -- opposed to poor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rich', 'superl.', 'Hence, in general, well supplied; abounding; abundant;\n   copious; bountiful; as, a rich treasury; a rich entertainment; a rich\n   crop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rich', 'superl.', 'Yielding large returns; productive or fertile; fruitful;\n   as, rich soil or land; a rich mine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rich', 'superl.', 'Composed of valuable or costly materials or ingredients;\n   procured at great outlay; highly valued; precious; sumptuous; costly;\n   as, a rich dress; rich silk or fur; rich presents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rich', 'superl.', 'Abounding in agreeable or nutritive qualities; --\n   especially applied to articles of food or drink which are high-seasoned\n   or abound in oleaginous ingredients, or are sweet, luscious, and\n   high-flavored; as, a rich dish; rich cream or soup; rich pastry; rich\n   wine or fruit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rich', 'superl.', 'Not faint or delicate; vivid; as, a rich color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rich', 'superl.', 'Full of sweet and harmonius sounds; as, a rich voice;\n   rich music.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rich', 'superl.', 'Abounding in beauty; gorgeous; as, a rich landscape;\n   rich scenery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rich', 'superl.', 'Abounding in humor; exciting amusement; entertaining;\n   as, the scene was a rich one; a rich incident or character.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rich', 'v. t.', 'To enrich.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Riches', 'a.', 'That which makes one rich; an abundance of land, goods,\n   money, or other property; wealth; opulence; affluence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Riches', 'a.', 'That which appears rich, sumptuous, precious, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Richesse', 'n.', 'Wealth; riches. See the Note under Riches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Richly', 'adv.', 'In a rich manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Richness', 'n.', 'The quality or state of being rich (in any sense of the\n   adjective).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Richweed', 'n.', 'An herb (Pilea pumila) of the Nettle family, having a\n   smooth, juicy, pellucid stem; -- called also clearweed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ricinelaidic', 'a.', 'Pertaining to, or designating, an isomeric\n   modification of ricinoleic acid obtained as a white crystalline solid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ricinelaidin', 'n.', 'The glycerin salt of ricinelaidic acid, obtained as a\n   white crystalline waxy substance by treating castor oil with nitrous\n   acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ricinic', 'a.', 'Pertaining to, or derived from, castor oil; formerly,\n   designating an acid now called ricinoleic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ricinine', 'n.', 'A bitter white crystalline alkaloid extracted from the\n   seeds of the castor-oil plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ricinoleate', 'n.', 'A salt of ricinoleic acid; -- formerly called palmate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ricinoleic', 'a.', 'Pertaining to, or designating, a fatty acid analogous\n   to oleic acid, obtained from castor oil as an oily substance, C/H/O/\n   with a harsh taste. Formerly written ricinolic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ricinolein', 'n.', 'The glycerin salt of ricinoleic acid, occuring as a\n   characteristic constituent of castor oil; -- formerly called palmin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ricinolic', 'a.', 'Ricinoleic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ricinus', 'n.', 'A genus of plants of the Spurge family, containing but one\n   species (R. communis), the castor-oil plant. The fruit is three-celled,\n   and contains three large seeds from which castor oil iss expressed. See\n   Palma Christi.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rick', 'n.', 'A stack or pile, as of grain, straw, or hay, in the open air,\n   usually protected from wet with thatching.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rick', 'v. t.', 'To heap up in ricks, as hay, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ricker', 'n.', 'A stout pole for use in making a rick, or for a spar to a\n   boat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ricketish', 'a.', 'Rickety.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rickets', 'n. pl.', 'A disease which affects children, and which is\n   characterized by a bulky head, crooked spine and limbs, depressed ribs,\n   enlarged and spongy articular epiphyses, tumid abdomen, and short\n   stature, together with clear and often premature mental faculties. The\n   essential cause of the disease appears to be the nondeposition of\n   earthy salts in the osteoid tissues. Children afflicted with this\n   malady stand and walk unsteadily. Called also rachitis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rickety', 'a.', 'Affected with rickets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rickety', 'a.', 'Feeble in the joints; imperfect; weak; shaky.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rickrack', 'n.', 'A kind of openwork edging made of serpentine braid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rickstand', 'n.', 'A flooring or framework on which a rick is made.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ricochet', 'n.', 'A rebound or skipping, as of a ball along the ground when\n   a gun is fired at a low angle of elevation, or of a fiat stone thrown\n   along the surface of water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ricochetted', 'imp. & p. p.', 'of Ricochet');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ricochetting', 'p. pr. & vb. n.', 'of Ricochet');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ricochet', 'v. t.', 'To operate upon by ricochet firing. See Ricochet, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ricochet', 'v. i.', 'To skip with a rebound or rebounds, as a flat stone on\n   the surface of water, or a cannon ball on the ground. See Ricochet, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rictal', 'a.', 'Of or pertaining to the rictus; as, rictal bristles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ricture', 'n.', 'A gaping.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rictus', 'n.', 'The gape of the mouth, as of birds; -- often resricted to\n   the corners of the mouth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rid', '', 'imp. & p. p. of Ride, v. i.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rid', 'imp. & p. p.', 'of Rid');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ridded', '', 'of Rid');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ridding', 'p. pr. & vb. n.', 'of Rid');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rid', 'v. t.', 'To save; to rescue; to deliver; -- with out of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rid', 'v. t.', 'To free; to clear; to disencumber; -- followed by of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rid', 'v. t.', 'To drive away; to remove by effort or violence; to make\n   away with; to destroy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rid', 'v. t.', 'To get over; to dispose of; to dispatch; to finish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ridable', 'a.', 'Suitable for riding; as, a ridable horse; a ridable road.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Riddance', 'n.', 'The act of ridding or freeing; deliverance; a cleaning up\n   or out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Riddance', 'n.', 'The state of being rid or free; freedom; escape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ridden', '', 'p. p. of Ride.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ridder', 'n.', 'One who, or that which, rids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Riddle', 'n.', 'A sieve with coarse meshes, usually of wire, for separating\n   coarser materials from finer, as chaff from grain, cinders from ashes,\n   or gravel from sand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Riddle', 'n.', 'A board having a row of pins, set zigzag, between which\n   wire is drawn to straighten it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Riddled', 'imp. & p. p.', 'of Riddle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Riddling', 'p. pr. & vb. n.', 'of Riddle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Riddle', 'v. t.', 'To separate, as grain from the chaff, with a riddle; to\n   pass through a riddle; as, riddle wheat; to riddle coal or gravel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Riddle', 'v. t.', 'To perforate so as to make like a riddle; to make many\n   holes in; as, a house riddled with shot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Riddle', 'n.', 'Something proposed to be solved by guessing or conjecture;\n   a puzzling question; an ambiguous proposition; an enigma; hence,\n   anything ambiguous or puzzling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Riddle', 'v. t.', 'To explain; to solve; to unriddle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Riddle', 'v. i.', 'To speak ambiguously or enigmatically.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Riddler', 'n.', 'One who riddles (grain, sand, etc.).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Riddler', 'n.', 'One who speaks in, or propounds, riddles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Riddling', 'a.', 'Speaking in a riddle or riddles; containing a riddle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rode', 'imp.', 'of Ride');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rid', '', 'of Ride');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ridden', 'p. p.', 'of Ride');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rid', '', 'of Ride');
  INSERT INTO words (word, word_type, description) VALUES  (    'Riding', 'p. pr. & vb. n.', 'of Ride');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ride', 'v. i.', 'To be carried on the back of an animal, as a horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ride', 'v. i.', 'To be borne in a carriage; as, to ride in a coach, in a\n   car, and the like. See Synonym, below.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ride', 'v. i.', 'To be borne or in a fluid; to float; to lie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ride', 'v. i.', 'To be supported in motion; to rest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ride', 'v. i.', 'To manage a horse, as an equestrian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ride', 'v. i.', 'To support a rider, as a horse; to move under the saddle;\n   as, a horse rides easy or hard, slow or fast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ride', 'v. t.', 'To sit on, so as to be carried; as, to ride a horse; to\n   ride a bicycle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ride', 'v. t.', 'To manage insolently at will; to domineer over.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ride', 'v. t.', 'To convey, as by riding; to make or do by riding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ride', 'v. t.', 'To overlap (each other); -- said of bones or fractured\n   fragments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ride', 'n.', 'The act of riding; an excursion on horseback or in a vehicle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ride', 'n.', 'A saddle horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ride', 'n.', 'A road or avenue cut in a wood, or through grounds, to be\n   used as a place for riding; a riding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rideau', 'n.', 'A small mound of earth; ground slightly elevated; a small\n   ridge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Riden', '', 'imp. pl. & p. p. of Ride.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rident', 'a.', 'Laughing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rider', 'n.', 'One who, or that which, rides.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rider', 'n.', 'Formerly, an agent who went out with samples of goods to\n   obtain orders; a commercial traveler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rider', 'n.', 'One who breaks or manages a horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rider', 'n.', 'An addition or amendment to a manuscript or other document,\n   which is attached on a separate piece of paper; in legislative\n   practice, an additional clause annexed to a bill while in course of\n   passage; something extra or burdensome that is imposed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rider', 'n.', 'A problem of more than usual difficulty added to another on\n   an examination paper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rider', 'n.', 'A Dutch gold coin having the figure of a man on horseback\n   stamped upon it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rider', 'n.', 'Rock material in a vein of ore, dividing it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rider', 'n.', 'An interior rib occasionally fixed in a ships hold,\n   reaching from the keelson to the beams of the lower deck, to strengthen\n   her frame.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rider', 'n.', 'The second tier of casks in a vessels hold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rider', 'n.', 'A small forked weight which straddles the beam of a balance,\n   along which it can be moved in the manner of the weight on a steelyard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rider', 'n.', 'A robber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Riderless', 'a.', 'Having no rider; as, a riderless horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ridge', 'n.', 'The back, or top of the back; a crest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ridge', 'n.', 'A range of hills or mountains, or the upper part of such a\n   range; any extended elevation between valleys.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ridge', 'n.', 'A raised line or strip, as of ground thrown up by a plow or\n   left between furrows or ditches, or as on the surface of metal, cloth,\n   or bone, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ridge', 'n.', 'The intersection of two surface forming a salient angle,\n   especially the angle at the top between the opposite slopes or sides of\n   a roof or a vault.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ridge', 'n.', 'The highest portion of the glacis proceeding from the\n   salient angle of the covered way.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ridged', 'imp. & p. p.', 'of Ridge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ridging', 'p. pr. & vb. n.', 'of Ridge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ridge', 'v. t.', 'To form a ridge of; to furnish with a ridge or ridges; to\n   make into a ridge or ridges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ridge', 'v. t.', 'To form into ridges with the plow, as land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ridge', 'v. t.', 'To wrinkle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ridgeband', 'n.', 'The part of a harness which passes over the saddle, and\n   supports the shafts of a cart; -- called also ridgerope, and ridger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ridgebone', 'n.', 'The backbone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ridgel', 'n.', 'Same as Ridgelling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ridgelet', 'n.', 'A little ridge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ridgeling', 'n.', 'A half-castrated male animal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ridgepiece', 'n.', 'Alt. of Ridgeplate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ridgeplate', 'n.', 'See Ridgepole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ridgepole', 'n.', 'The timber forming the ridge of a roof, into which the\n   rafters are secured.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ridgerope', 'n.', 'See Life line (a), under Life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ridgingly', 'adv.', 'So as to form ridges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ridgy', 'a.', 'Having a ridge or ridges; rising in a ridge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ridicle', 'n.', 'Ridicule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ridicule', 'n.', 'An object of sport or laughter; a laughingstock; a\n   laughing matter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ridicule', 'n.', 'Remarks concerning a subject or a person designed to\n   excite laughter with a degree of contempt; wit of that species which\n   provokes contemptuous laughter; disparagement by making a person an\n   object of laughter; banter; -- a term lighter than derision.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ridicule', 'n.', 'Quality of being ridiculous; ridiculousness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ridiculed', 'imp. & p. p.', 'of Ridicule');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ridiculing', 'p. pr. & vb. n.', 'of Ridicule');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ridicule', 'v. t.', 'To laugh at mockingly or disparagingly; to awaken\n   ridicule toward or respecting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ridicule', 'a.', 'Ridiculous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ridiculer', 'n.', 'One who ridicules.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ridiculize', 'v. t.', 'To make ridiculous; to ridicule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ridiculosity', 'n.', 'The quality or state of being ridiculous;\n   ridiculousness; also, something ridiculous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ridiculous', 'a.', 'Fitted to excite ridicule; absurd and laughable;\n   unworthy of serious consideration; as, a ridiculous dress or behavior.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ridiculous', 'a.', 'Involving or expressing ridicule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Riding', 'n.', 'One of the three jurisdictions into which the county of\n   York, in England, is divided; -- formerly under the government of a\n   reeve. They are called the North, the East, and the West, Riding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Riding', 'a.', 'Employed to travel; traveling; as, a riding clerk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Riding', 'a.', 'Used for riding on; as, a riding horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Riding', 'a.', 'Used for riding, or when riding; devoted to riding; as, a\n   riding whip; a riding habit; a riding day.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Riding', 'n.', 'The act or state of one who rides.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Riding', 'n.', 'A festival procession.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Riding', 'n.', 'Same as Ride, n., 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Riding', 'n.', 'A district in charge of an excise officer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ridotto', 'n.', 'A favorite Italian public entertainment, consisting of\n   music and dancing, -- held generally on fast eves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ridotto', 'v. i.', 'To hold ridottos.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rie', 'n.', 'See Rye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rief', 'n.', 'Robbery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rietboc', 'n.', 'The reedbuck, a South African antelope (Cervicapra\n   arundinacea); -- so called from its frequenting dry places covered with\n   high grass or reeds. Its color is yellowish brown. Called also\n   inghalla, and rietbok.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rife', 'a.', 'Prevailing; prevalent; abounding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rife', 'a.', 'Having power; active; nimble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Riffle', 'n.', 'A trough or sluice having cleats, grooves, or steps across\n   the bottom for holding quicksilver and catching particles of gold when\n   auriferous earth is washed; also, one of the cleats, grooves, or steps\n   in such a trough. Also called ripple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Riffler', 'n.', 'A curved file used in carving wool and marble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Riffraff', 'n.', 'Sweepings; refuse; the lowest order of society.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rifled', 'imp. & p. p.', 'of Rifle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rifling', 'p. pr. & vb. n.', 'of Rifle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rifle', 'v. t.', 'To seize and bear away by force; to snatch away; to carry\n   off.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rifle', 'v. t.', 'To strip; to rob; to pillage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rifle', 'v. t.', 'To raffle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rifle', 'v. i.', 'To raffle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rifle', 'v. i.', 'To commit robbery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rifle', 'n.', 'A gun, the inside of whose barrel is grooved with spiral\n   channels, thus giving the ball a rotary motion and insuring greater\n   accuracy of fire. As a military firearm it has superseded the musket.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rifle', 'n.', 'A body of soldiers armed with rifles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rifle', 'n.', 'A strip of wood covered with emery or a similar material,\n   used for sharpening scythes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rifle', 'v. t.', 'To grove; to channel; especially, to groove internally\n   with spiral channels; as, to rifle a gun barrel or a cannon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rifle', 'v. t.', 'To whet with a rifle. See Rifle, n., 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Riflebird', 'n.', 'Any one of several species of beautiful birds of\n   Australia and New Guinea, of the genera Ptiloris and Craspidophora,\n   allied to the paradise birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rifleman', 'pl. ', 'of Rifleman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rifleman', 'n.', 'A soldier armed with a rifle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rifler', 'n.', 'One who rifles; a robber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rifling', 'n.', 'The act or process of making the grooves in a rifled\n   cannon or gun barrel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rifling', 'n.', 'The system of grooves in a rifled gun barrel or cannon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rift', '', 'p. p. of Rive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rift', 'n.', 'An opening made by riving or splitting; a cleft; a fissure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rift', 'n.', 'A shallow place in a stream; a ford.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rifted', 'imp. & p. p.', 'of Rift');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rifting', 'p. pr. & vb. n.', 'of Rift');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rift', 'v. t.', 'To cleave; to rive; to split; as, to rift an oak or a\n   rock; to rift the clouds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rift', 'v. i.', 'To burst open; to split.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rift', 'v. i.', 'To belch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rifter', 'n.', 'A rafter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rig', 'n.', 'A ridge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rigged', 'imp. & p. p.', 'of Rig');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rigging', 'p. pr. & vb. n.', 'of Rig');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rig', 'v. t.', 'To furnish with apparatus or gear; to fit with tackling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rig', 'v. t.', 'To dress; to equip; to clothe, especially in an odd or\n   fanciful manner; -- commonly followed by out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rig', 'n.', 'The peculiar fitting in shape, number, and arrangement of\n   sails and masts, by which different types of vessels are distinguished;\n   as, schooner rig, ship rig, etc. See Illustration in Appendix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rig', 'n.', 'Dress; esp., odd or fanciful clothing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rig', 'n.', 'A romp; a wanton; one given to unbecoming conduct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rig', 'n.', 'A sportive or unbecoming trick; a frolic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rig', 'n.', 'A blast of wind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rig', 'v. i.', 'To play the wanton; to act in an unbecoming manner; to play\n   tricks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rig', 'v. t.', 'To make free with; hence, to steal; to pilfer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rigadoon', 'n.', 'A gay, lively dance for one couple, -- said to have been\n   borrowed from Provence in France.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Riga fir', '', 'A species of pine (Pinus sylvestris), and its wood, which\n   affords a valuable timber; -- called also Scotch pine, and red / yellow\n   deal. It grows in all parts of Europe, in the Caucasus, and in Siberia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rigarion', 'n.', 'See Irrigation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rigel', 'n.', 'A fixed star of the first magnitude in the left foot of the\n   constellation Orion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rigescent', 'a.', 'Growing stiff or numb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rigger', 'n.', 'One who rigs or dresses; one whose occupation is to fit the\n   rigging of a ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rigger', 'n.', 'A cylindrical pulley or drum in machinery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rigging', 'n.', 'DRess; tackle; especially (Naut.), the ropes, chains,\n   etc., that support the masts and spars of a vessel, and serve as\n   purchases for adjusting the sails, etc. See Illustr. of Ship and Sails.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Riggish', 'a.', 'Like a rig or wanton.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Riggle', 'v. i.', 'See Wriggle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Riggle', 'n.', 'The European lance fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Right', 'a.', 'Straight; direct; not crooked; as, a right line.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Right', 'a.', 'Upright; erect from a base; having an upright axis; not\n   oblique; as, right ascension; a right pyramid or cone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Right', 'a.', 'Conformed to the constitution of man and the will of God, or\n   to justice and equity; not deviating from the true and just; according\n   with truth and duty; just; true.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Right', 'a.', 'Fit; suitable; proper; correct; becoming; as, the right man\n   in the right place; the right way from London to Oxford.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Right', 'a.', 'Characterized by reality or genuineness; real; actual; not\n   spurious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Right', 'a.', 'According with truth; passing a true judgment; conforming to\n   fact or intent; not mistaken or wrong; not erroneous; correct; as, this\n   is the right faith.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Right', 'a.', 'Most favorable or convenient; fortunate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Right', 'a.', 'Of or pertaining to that side of the body in man on which\n   the muscular action is usually stronger than on the other side; --\n   opposed to left when used in reference to a part of the body; as, the\n   right side, hand, arm. Also applied to the corresponding side of the\n   lower animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Right', 'a.', 'Well placed, disposed, or adjusted; orderly; well regulated;\n   correctly done.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Right', 'a.', 'Designed to be placed or worn outward; as, the right side of\n   a piece of cloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Right', 'adv.', 'In a right manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Right', 'adv.', 'In a right or straight line; directly; hence; straightway;\n   immediately; next; as, he stood right before me; it went right to the\n   mark; he came right out; he followed right after the guide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Right', 'adv.', 'Exactly; just.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Right', 'adv.', 'According to the law or will of God; conforming to the\n   standard of truth and justice; righteously; as, to live right; to judge\n   right.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Right', 'adv.', 'According to any rule of art; correctly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Right', 'adv.', 'According to fact or truth; actually; truly; really;\n   correctly; exactly; as, to tell a story right.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Right', 'adv.', 'In a great degree; very; wholly; unqualifiedly; extremely;\n   highly; as, right humble; right noble; right valiant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Right', 'a.', 'That which is right or correct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Right', 'a.', 'The straight course; adherence to duty; obedience to lawful\n   authority, divine or human; freedom from guilt, -- the opposite of\n   moral wrong.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Right', 'a.', 'A true statement; freedom from error of falsehood; adherence\n   to truth or fact.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Right', 'a.', 'A just judgment or action; that which is true or proper;\n   justice; uprightness; integrity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Right', 'a.', 'That to which one has a just claim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Right', 'a.', 'That which one has a natural claim to exact.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Right', 'a.', 'That which one has a legal or social claim to do or to\n   exact; legal power; authority; as, a sheriff has a right to arrest a\n   criminal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Right', 'a.', 'That which justly belongs to one; that which one has a claim\n   to possess or own; the interest or share which anyone has in a piece of\n   property; title; claim; interest; ownership.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Right', 'a.', 'Privilege or immunity granted by authority.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Right', 'a.', 'The right side; the side opposite to the left.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Right', 'a.', 'In some legislative bodies of Europe (as in France), those\n   members collectively who are conservatives or monarchists. See Center,\n   5.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Right', 'a.', 'The outward or most finished surface, as of a piece of\n   cloth, a carpet, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Righted', 'imp. & p. p.', 'of Right');
  INSERT INTO words (word, word_type, description) VALUES  (    'Righting', 'p. pr. & vb. n.', 'of Right');
  INSERT INTO words (word, word_type, description) VALUES  (    'Right', 'a.', 'To bring or restore to the proper or natural position; to\n   set upright; to make right or straight (that which has been wrong or\n   crooked); to correct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Right', 'a.', 'To do justice to; to relieve from wrong; to restore rights\n   to; to assert or regain the rights of; as, to right the oppressed; to\n   right ones self; also, to vindicate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Right', 'v. i.', 'To recover the proper or natural condition or position;\n   to become upright.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Right', 'v. i.', 'Hence, to regain an upright position, as a ship or boat,\n   after careening.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Right-about', 'n.', 'A turning directly about by the right, so as to face\n   in the opposite direction; also, the quarter directly opposite; as, to\n   turn to the right-about.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Right-angled', 'a.', 'Containing a right angle or right angles; as, a\n   right-angled triangle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Righten', 'v. t.', 'To do justice to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Righteous', 'a.', 'Doing, or according with, that which is right; yielding\n   to all their due; just; equitable; especially, free from wrong, guilt,\n   or sin; holy; as, a righteous man or act; a righteous retribution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Righteoused', 'a.', 'Made righteous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Righteously', 'adv.', 'In a righteous manner; as, to judge righteously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Righteousness', 'n.', 'The quality or state of being righteous; holiness;\n   purity; uprightness; rectitude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Righteousness', 'n.', 'A righteous act, or righteous quality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Righteousness', 'n.', 'The act or conduct of one who is righteous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Righteousness', 'n.', 'The state of being right with God; justification;\n   the work of Christ, which is the ground of justification.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Righter', 'n.', 'One who sets right; one who does justice or redresses\n   wrong.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rightful', 'a.', 'Righteous; upright; just; good; -- said of persons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rightful', 'a.', 'Consonant to justice; just; as, a rightful cause.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rightful', 'a.', 'Having the right or just claim according to established\n   laws; being or holding by right; as, the rightful heir to a throne or\n   an estate; a rightful king.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rightful', 'a.', 'Belonging, held, or possessed by right, or by just claim;\n   as, a rightful inheritance; rightful authority.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rightfully', 'adv.', 'According to right or justice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rightfulness', 'n.', 'The quality or state of being rightful; accordance\n   with right and justice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rightfulness', 'n.', 'Moral rectitude; righteousness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Right-hand', 'a.', 'Situated or being on the right; nearer the right hand\n   than the left; as, the right-hand side, room, or road.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Right-hand', 'a.', 'Chiefly relied on; almost indispensable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Right-handed', 'a.', 'Using the right hand habitually, or more easily than\n   the left.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Right-handed', 'a.', 'Having the same direction or course as the movement\n   of the hands of a watch seen in front; -- said of the motion of a\n   revolving object looked at from a given direction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Right-handed', 'a.', 'Having the whorls rising from left to right; dextral;\n   -- said of spiral shells. See Illust. of Scalaria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Right-handedness', 'n.', 'The state or quality of being right-handed;\n   hence, skill; dexterity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Right-hearted', 'a.', 'Having a right heart or disposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rightless', 'a.', 'Destitute of right.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Right-lined', 'a.', 'Formed by right lines; rectilineal; as, a right-lined\n   angle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rightly', 'adv.', 'Straightly; directly; in front.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rightly', 'adv.', 'According to justice; according to the divine will or\n   moral rectitude; uprightly; as, duty rightly performed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rightly', 'adv.', 'Properly; fitly; suitably; appropriately.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rightly', 'adv.', 'According to truth or fact; correctly; not erroneously;\n   exactly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Right-minded', 'a.', 'Having a right or honest mind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rightness', 'n.', 'Straightness; as, the rightness of a line.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rightness', 'n.', 'The quality or state of being right; right relation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Right-running', 'a.', 'Straight; direct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rightward', 'adv.', 'Toward the right.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Right whale', '', 'The bowhead, Arctic, or Greenland whale (Balaena\n   mysticetus), from whose mouth the best whalebone is obtained.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Right whale', '', 'Any other whale that produces valuable whalebone, as the\n   Atlantic, or Biscay, right whale (Balaena cisarctica), and the Pacific\n   right whale (B. Sieboldii); a bone whale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rightwise', 'a.', 'Righteous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rightwise', 'v. t.', 'To make righteous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rightwisely', 'adv.', 'Righteously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rightwiseness', 'n.', 'Righteousness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rigid', 'a.', 'Firm; stiff; unyielding; not pliant; not flexible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rigid', 'a.', 'Hence, not lax or indulgent; severe; inflexible; strict; as,\n   a rigid father or master; rigid discipline; rigid criticism; a rigid\n   sentence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rigidity', 'n.', 'The quality or state of being rigid; want of pliability;\n   the quality of resisting change of form; the amount of resistance with\n   which a body opposes change of form; -- opposed to flexibility,\n   ductility, malleability, and softness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rigidity', 'n.', 'Stiffness of appearance or manner; want of ease or\n   elegance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rigidity', 'n.', 'Severity; rigor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rigidly', 'v.', 'In a rigid manner; stiffly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rigidness', 'n.', 'The quality or state of being rigid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rigidulous', 'a.', 'Somewhat rigid or stiff; as, a rigidulous bristle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Riglet', 'n.', 'See Reglet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rigmarole', 'n.', 'A succession of confused or nonsensical statements;\n   foolish talk; nonsense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rigmarole', 'a.', 'Consisting of rigmarole; frovolous; nonsensical;\n   foolish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rigol', 'n.', 'A circle; hence, a diadem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rigoll', 'n.', 'A musical instrument formerly in use, consisting of several\n   sticks bound together, but separated by beads, and played with a stick\n   with a ball at its end.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rigor', 'n.', 'Rigidity; stiffness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rigor', 'n.', 'A sense of chilliness, with contraction of the skin; a\n   convulsive shuddering or tremor, as in the chill preceding a fever.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rigor', 'n.', 'The becoming stiff or rigid; the state of being rigid;\n   rigidity; stiffness; hardness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rigor', 'n.', 'See 1st Rigor, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rigor', 'n.', 'Severity of climate or season; inclemency; as, the rigor of\n   the storm; the rigors of winter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rigor', 'n.', 'Stiffness of opinion or temper; rugged sternness; hardness;\n   relentless severity; hard-heartedness; cruelty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rigor', 'n.', 'Exactness without allowance, deviation, or indulgence;\n   strictness; as, the rigor of criticism; to execute a law with rigor; to\n   enforce moral duties with rigor; -- opposed to lenity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rigor', 'n.', 'Severity of life; austerity; voluntary submission to pain,\n   abstinence, or mortification.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rigor', 'n.', 'Violence; force; fury.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rigorism', 'n.', 'Rigidity in principle or practice; strictness; -- opposed\n   to laxity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rigorism', 'n.', 'Severity, as of style, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rigorist', 'n.', 'One who is rigorous; -- sometimes applied to an extreme\n   Jansenist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rigorous', 'a.', 'Manifesting, exercising, or favoring rigor; allowing no\n   abatement or mitigation; scrupulously accurate; exact; strict; severe;\n   relentless; as, a rigorous officer of justice; a rigorous execution of\n   law; a rigorous definition or demonstration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rigorous', 'a.', 'Severe; intense; inclement; as, a rigorous winter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rigorous', 'a.', 'Violent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rigsdaler', 'n.', 'A Danish coin worth about fifty-four cents. It was the\n   former unit of value in Denmark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rig-Veda', '', 'See Veda.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Riksdaler', 'n.', 'A Swedish coin worth about twenty-seven cents. It was\n   formerly the unit of value in Sweden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Riled', 'imp. & p. p.', 'of Rile');
  INSERT INTO words (word, word_type, description) VALUES  (    'Riling', 'p. pr. & vb. n.', 'of Rile');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rile', 'v. t.', 'To render turbid or muddy; to stir up; to roil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rile', 'v. t.', 'To stir up in feelings; to make angry; to vex.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rilievo', 'n.', 'Same as Relief, n., 5.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rill', 'n.', 'A very small brook; a streamlet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rill', 'n.', 'See Rille.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rill', 'v. i.', 'To run a small stream.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rille', 'n.', 'One of certain narrow, crooked valleys seen, by aid of the\n   telescope, on the surface of the moon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rillet', 'n.', 'A little rill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rily', 'a.', 'Roily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rim', 'n.', 'The border, edge, or margin of a thing, usually of something\n   circular or curving; as, the rim of a kettle or basin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rim', 'n.', 'The lower part of the abdomen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rimmed', 'imp. & p. p.', 'of Rim');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rimming', 'p. pr. & vb. n.', 'of Rim');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rim', 'v. t.', 'To furnish with a rim; to border.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rimae', 'pl. ', 'of Rima');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rima', 'n.', 'A narrow and elongated aperture; a cleft; a fissure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rimau dahan', '', 'The clouded tiger cat (Felis marmorata) of Southern Asia\n   and the East Indies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rimbase', 'n.', 'A short cylinder connecting a trunnion with the body of a\n   cannon. See Illust. of Cannon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rime', 'n.', 'A rent or long aperture; a chink; a fissure; a crack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rime', 'n.', 'White frost; hoarfrost; congealed dew or vapor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rimed', 'imp. & p. p.', 'of Rime');
  INSERT INTO words (word, word_type, description) VALUES  (    'Riming', 'p. pr. & vb. n.', 'of Rime');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rime', 'v. i.', 'To freeze or congeal into hoarfrost.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rime', 'n.', 'A step or round of a ladder; a rung.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rime', 'n.', 'Rhyme. See Rhyme.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rime', 'v. i. & t.', 'To rhyme. See Rhyme.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rimer', 'n.', 'A rhymer; a versifier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rimer', 'n.', 'A tool for shaping the rimes of a ladder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rimey', 'v. t.', 'To compose in rhyme; to versify.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rimmer', 'n.', 'An implement for cutting, trimming, or ornamenting the rim\n   of anything, as the edges of pies, etc.; also, a reamer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rimose', 'a.', 'Full of rimes, fissures, or chinks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rimose', 'a.', 'Having long and nearly parallel clefts or chinks, like\n   those in the bark of trees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rimosely', 'adv.', 'In a rimose manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rimosity', 'n.', 'State of being rimose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rimous', 'a.', 'Rimose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rimple', 'n.', 'A fold or wrinkle. See Rumple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rimpled', 'imp. & p. p.', 'of Rimple');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rimpling', 'p. pr. & vb. n.', 'of Rimple');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rimple', 'v. t. & i.', 'To rumple; to wrinkle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rimy', 'a.', 'Abounding with rime; frosty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rind', 'n.', 'The external covering or coat, as of flesh, fruit, trees,\n   etc.; skin; hide; bark; peel; shell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rind', 'v. t.', 'To remove the rind of; to bark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rinderpest', 'n.', 'A highly contagious distemper or murrain, affecting\n   neat cattle, and less commonly sheep and goats; -- called also cattle\n   plague, Russian cattle plague, and steppe murrain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rindle', 'n.', 'A small water course or gutter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rindless', 'a.', 'Destitute of a rind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rindy', 'a.', 'Having a rind or skin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rine', 'n.', 'See Rind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rined', 'a.', 'Having a rind');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rinforzando', 'a.', 'Increasing; strengthening; -- a direction indicating a\n   sudden increase of force (abbreviated rf., rfz.) Cf. Forzando, and\n   Sforzando.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rang', 'imp.', 'of Ring');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rung', '', 'of Ring');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rung', 'p. p.', 'of Ring');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ringing', 'p. pr. & vb. n.', 'of Ring');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ring', 'v. t.', 'To cause to sound, especially by striking, as a metallic\n   body; as, to ring a bell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ring', 'v. t.', 'To make (a sound), as by ringing a bell; to sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ring', 'v. t.', 'To repeat often, loudly, or earnestly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ring', 'v. i.', 'To sound, as a bell or other sonorous body, particularly a\n   metallic one.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ring', 'v. i.', 'To practice making music with bells.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ring', 'v. i.', 'To sound loud; to resound; to be filled with a ringing or\n   reverberating sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ring', 'v. i.', 'To continue to sound or vibrate; to resound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ring', 'v. i.', 'To be filled with report or talk; as, the whole town rings\n   with his fame.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ring', 'n.', 'A sound; especially, the sound of vibrating metals; as, the\n   ring of a bell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ring', 'n.', 'Any loud sound; the sound of numerous voices; a sound\n   continued, repeated, or reverberated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ring', 'n.', 'A chime, or set of bells harmonically tuned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ring', 'n.', 'A circle, or a circular line, or anything in the form of a\n   circular line or hoop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ring', 'n.', 'Specifically, a circular ornament of gold or other precious\n   material worn on the finger, or attached to the ear, the nose, or some\n   other part of the person; as, a wedding ring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ring', 'n.', 'A circular area in which races are or run or other sports are\n   performed; an arena.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ring', 'n.', 'An inclosed space in which pugilists fight; hence,\n   figuratively, prize fighting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ring', 'n.', 'A circular group of persons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ring', 'n.', 'The plane figure included between the circumferences of two\n   concentric circles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ring', 'n.', 'The solid generated by the revolution of a circle, or other\n   figure, about an exterior straight line (as an axis) lying in the same\n   plane as the circle or other figure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ring', 'n.', 'An instrument, formerly used for taking the suns altitude,\n   consisting of a brass ring suspended by a swivel, with a hole at one\n   side through which a solar ray entering indicated the altitude on the\n   graduated inner surface opposite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ring', 'n.', 'An elastic band partly or wholly encircling the spore cases\n   of ferns. See Illust. of Sporangium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ring', 'n.', 'A clique; an exclusive combination of persons for a selfish\n   purpose, as to control the market, distribute offices, obtain\n   contracts, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ringed', 'imp. & p. p.', 'of Ring');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ringing', 'p. pr. & vb. n.', 'of Ring');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ring', 'v. t.', 'To surround with a ring, or as with a ring; to encircle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ring', 'v. t.', 'To make a ring around by cutting away the bark; to girdle;\n   as, to ring branches or roots.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ring', 'v. t.', 'To fit with a ring or with rings, as the fingers, or a\n   swines snout.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ring', 'v. i.', 'To rise in the air spirally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ringbill', 'n.', 'The ring-necked scaup duck; -- called also ring-billed\n   blackhead. See Scaup.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ringbird', 'n.', 'The reed bunting. It has a collar of white feathers.\n   Called also ring bunting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ringbolt', 'n.', 'An eyebolt having a ring through the eye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ringbone', 'n.', 'A morbid growth or deposit of bony matter between or on\n   the small pastern and the great pastern bones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ringdove', 'n.', 'A European wild pigeon (Columba palumbus) having a white\n   crescent on each side of the neck, whence the name. Called also wood\n   pigeon, and cushat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ringed', 'a.', 'Encircled or marked with, or as with, a ring or rings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ringed', 'a.', 'Wearning a wedding ring; hence, lawfully wedded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ringent', 'a.', 'Having the lips widely separated and gaping like an open\n   mouth; as a ringent bilabiate corolla.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ringer', 'n.', 'One who, or that which, rings; especially, one who rings\n   chimes on bells.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ringer', 'n.', 'A crowbar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ringer', 'n.', 'A horse that is not entitled to take part in a race, but is\n   fraudulently got into it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ringhead', 'n.', 'An instrument used for stretching woolen cloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ringing', '', 'a & n. from Ring, v.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ringingly', 'adv.', 'In a ringing manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ringleader', 'n.', 'The leader of a circle of dancers; hence, the leader of\n   a number of persons acting together; the leader of a herd of animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ringleader', 'n.', 'Opprobriously, a leader of a body of men engaged in the\n   violation of law or in an illegal enterprise, as rioters, mutineers, or\n   the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ringlestone', 'n.', 'The ringed dotterel, or ring plover.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ringlet', 'n.', 'A small ring; a small circle; specifically, a fairy ring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ringlet', 'n.', 'A curl; especially, a curl of hair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ringmen', 'pl. ', 'of Ringman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ringman', 'n.', 'The ring finger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ringmaster', 'n.', 'One in charge of the performances (as of horses) within\n   the ring in a circus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ringneck', 'n.', 'Any one of several species of small plovers of the genus\n   Aegialitis, having a ring around the neck. The ring is black in summer,\n   but becomes brown or gray in winter. The semipalmated plover (Ae.\n   semipalmata) and the piping plover (Ae. meloda) are common North\n   American species. Called also ring plover, and ring-necked plover.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ringneck', 'n.', 'The ring-necked duck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ring-necked', 'a.', 'Having a well defined ring of color around the neck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ringsail', 'n.', 'See Ringtail, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ringstraked', 'a.', 'Ring-streaked.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ring-streaked', 'a.', 'Having circular streaks or lines on the body; as,\n   ring-streaked goats.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ringtail', 'n.', 'A bird having a distinct band of color across the tail,\n   as the hen harrier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ringtail', 'n.', 'A light sail set abaft and beyong the leech of a\n   boom-and-gaff sail; -- called also ringsail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ring-tailed', 'a.', 'Having the tail crossed by conspicuous bands of color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ringtoss', 'n.', 'A game in which the object is to toss a ring so that it\n   will catch upon an upright stick.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ringworm', 'n.', 'A contagious affection of the skin due to the presence of\n   a vegetable parasite, and forming ring-shaped discolored patches\n   covered with vesicles or powdery scales. It occurs either on the body,\n   the face, or the scalp. Different varieties are distinguished as Tinea\n   circinata, Tinea tonsurans, etc., but all are caused by the same\n   parasite (a species of Trichophyton).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rink', 'n.', 'The smooth and level extent of ice marked off for the game of\n   curling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rink', 'n.', 'An artificial sheet of ice, generally under cover, used for\n   skating; also, a floor prepared for skating on with roller skates, or a\n   building with such a floor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rinker', 'n.', 'One who skates at a rink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rinking', 'n.', 'Skating in a rink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rinsed', 'imp. & p. p.', 'of Rinse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rinsing', 'p. pr. & vb. n.', 'of Rinse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rinse', 'v. t.', 'To wash lightly; to cleanse with a second or repeated\n   application of water after washing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rinse', 'v. t.', 'To cleancse by the introduction of water; -- applied\n   especially to hollow vessels; as, to rinse a bottle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rinse', 'n.', 'The act of rinsing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rinser', 'n.', 'One who, or that which, rinses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Riot', 'n.', 'Wanton or unrestrained behavior; uproar; tumult.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Riot', 'n.', 'Excessive and exxpensive feasting; wild and loose festivity;\n   revelry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Riot', 'n.', 'The tumultuous disturbance of the public peace by an unlawful\n   assembly of three or more persons in the execution of some private\n   object.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rioted', 'imp. & p. p.', 'of Riot');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rioting', 'p. pr. & vb. n.', 'of Riot');
  INSERT INTO words (word, word_type, description) VALUES  (    'Riot', 'v. i.', 'To engage in riot; to act in an unrestrained or wanton\n   manner; to indulge in excess of luxury, feasting, or the like; to\n   revel; to run riot; to go to excess.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Riot', 'v. i.', 'To disturb the peace; to raise an uproar or sedition. See\n   Riot, n., 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Riot', 'v. t.', 'To spend or pass in riot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rioter', 'n.', 'One who riots; a reveler; a roisterer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rioter', 'n.', 'One who engages in a riot. See Riot, n., 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Riotise', 'n.', 'Excess; tumult; revelry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Riotour', 'n.', 'A rioter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Riotous', 'a.', 'Involving, or engaging in, riot; wanton; unrestrained;\n   luxurious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Riotous', 'a.', 'Partaking of the nature of an unlawful assembly or its\n   acts; seditious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Riotry', 'n.', 'The act or practice of rioting; riot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rip', 'n.', 'A wicker fish basket.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ripped', 'imp. & p. p.', 'of Rip');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ripping', 'p. pr. & vb. n.', 'of Rip');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rip', 'v. t.', 'To divide or separate the parts of, by cutting or tearing;\n   to tear or cut open or off; to tear off or out by violence; as, to rip\n   a garment by cutting the stitches; to rip off the skin of a beast; to\n   rip up a floor; -- commonly used with up, open, off.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rip', 'v. t.', 'To get by, or as by, cutting or tearing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rip', 'v. t.', 'To tear up for search or disclosure, or for alteration; to\n   search to the bottom; to discover; to disclose; -- usually with up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rip', 'v. t.', 'To saw (wood) lengthwise of the grain or fiber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rip', 'n.', 'A rent made by ripping, esp. by a seam giving way; a tear; a\n   place torn; laceration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rip', 'n.', 'A term applied to a mean, worthless thing or person, as to a\n   scamp, a debauchee, or a prostitute, or a worn-out horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rip', 'n.', 'A body of water made rough by the meeting of opposing tides or\n   currents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Riparian', 'a.', 'Of or pertaining to the bank of a river; as, riparian\n   rights.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Riparious', 'a.', 'Growing along the banks of rivers; riparian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ripe', 'n.', 'The bank of a river.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ripe', 'superl.', 'Ready for reaping or gathering; having attained\n   perfection; mature; -- said of fruits, seeds, etc.; as, ripe grain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ripe', 'superl.', 'Advanced to the state of fitness for use; mellow; as,\n   ripe cheese; ripe wine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ripe', 'superl.', 'Having attained its full development; mature; perfected;\n   consummate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ripe', 'superl.', 'Maturated or suppurated; ready to discharge; -- said of\n   sores, tumors, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ripe', 'superl.', 'Ready for action or effect; prepared.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ripe', 'superl.', 'Like ripened fruit in ruddiness and plumpness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ripe', 'superl.', 'Intoxicated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ripe', 'v. i.', 'To ripen; to grow ripe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ripe', 'v. t.', 'To mature; to ripen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ripely', 'adv.', 'Maturely; at the fit time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ripened', 'imp. & p. p.', 'of Ripen');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ripening', 'p. pr. & vb. n.', 'of Ripen');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ripen', 'v. i.', 'To grow ripe; to become mature, as grain, fruit, flowers,\n   and the like; as, grapes ripen in the sun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ripen', 'v. i.', 'To approach or come to perfection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ripen', 'v. t.', 'To cause to mature; to make ripe; as, the warm days\n   ripened the corn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ripen', 'v. t.', 'To mature; to fit or prepare; to bring to perfection; as,\n   to ripen the judgment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ripeness', 'n.', 'The state or quality of being ripe; maturity;;\n   completeness; perfection; as, the ripeness of grain; ripeness of\n   manhood; ripeness of judgment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ripidolite', 'n.', 'A translucent mineral of a green color and micaceous\n   structure, belonging to the chlorite group; a hydrous silicate of\n   alumina, magnesia, and iron; -- called also clinochlore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ripienist', 'n.', 'A player in the ripieno portion of an orchestra. See\n   Ripieno.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ripieno', 'a.', 'Filling up; supplementary; supernumerary; -- a term\n   applied to those instruments which only swell the mass or tutti of an\n   orchestra, but are not obbligato.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ripler', 'n.', 'Alt. of Ripper');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ripper', 'n.', 'One who brings fish from the seacoast to markets in inland\n   towns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ripost', 'n.', 'In fencing, a return thrust after a parry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ripost', 'n.', 'A quick and sharp refort; a repartee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ripper', 'n.', 'One who, or that which, rips; a ripping tool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ripper', 'n.', 'A tool for trimming the edges of roofing slates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ripper', 'n.', 'Anything huge, extreme, startling, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ripple', 'v.', 'An implement, with teeth like those of a comb, for removing\n   the seeds and seed vessels from flax, broom corn, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ripple', 'v. t.', 'To remove the seeds from (the stalks of flax, etc.), by\n   means of a ripple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ripple', 'v. t.', 'Hence, to scratch or tear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rippled', 'imp. & p. p.', 'of Ripple');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rippling', 'p. pr. & vb. n.', 'of Ripple');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ripple', 'v. i.', 'To become fretted or dimpled on the surface, as water\n   when agitated or running over a rough bottom; to be covered with small\n   waves or undulations, as a field of grain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ripple', 'v. i.', 'To make a sound as of water running gently over a rough\n   bottom, or the breaking of ripples on the shore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ripple', 'v. t.', 'To fret or dimple, as the surface of running water; to\n   cover with small waves or undulations; as, the breeze rippled the lake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ripple', 'n.', 'The fretting or dimpling of the surface, as of running\n   water; little curling waves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ripple', 'n.', 'A little wave or undulation; a sound such as is made by\n   little waves; as, a ripple of laughter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ripple', 'n.', 'a small wave on the surface of water or other liquids for\n   which the driving force is not gravity, but surface tension.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ripple', 'n.', 'the residual AC component in the DC current output from a\n   rectifier, expressed as a percentage of the steady component of the\n   current.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ripple-marked', 'a.', 'Having ripple marks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ripplet', 'n.', 'A small ripple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ripplingly', 'adv.', 'In a rippling manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ripply', 'a.', 'Having ripples; as, ripply water; hence, resembling the\n   sound of rippling water; as, ripply laughter; a ripply cove.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Riprap', 'n.', 'A foundation or sustaining wall of stones thrown together\n   without order, as in deep water or on a soft bottom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Riprapped', 'imp. & p. p.', 'of Riprap');
  INSERT INTO words (word, word_type, description) VALUES  (    'Riprapping', 'p. pr. & vb. n.', 'of Riprap');
  INSERT INTO words (word, word_type, description) VALUES  (    'Riprap', 'v. t.', 'To form a riprap in or upon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ripsaw', 'v. t.', 'A handsaw with coarse teeth which have but a slight set,\n   used for cutting wood in the direction of the fiber; -- called also\n   ripping saw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Riptowel', 'n.', 'A gratuity given to tenants after they had reaped their\n   lords corn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ris', 'n.', 'A bough or branch; a twig.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rose', 'imp.', 'of Rise');
  INSERT INTO words (word, word_type, description) VALUES  (    'Risen', 'p. p.', 'of Rise');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rising', 'p. pr. & vb. n.', 'of Rise');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rise', 'v.', 'To move from a lower position to a higher; to ascend; to\n   mount up. Specifically: -- (a) To go upward by walking, climbing,\n   flying, or any other voluntary motion; as, a bird rises in the air; a\n   fish rises to the bait.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rise', 'v.', 'To ascend or float in a fluid, as gases or vapors in air,\n   cork in water, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rise', 'v.', 'To move upward under the influence of a projecting force; as,\n   a bullet rises in the air.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rise', 'v.', 'To grow upward; to attain a certain height; as, this elm\n   rises to the height of seventy feet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rise', 'v.', 'To reach a higher level by increase of quantity or bulk; to\n   swell; as, a river rises in its bed; the mercury rises in the\n   thermometer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rise', 'v.', 'To become erect; to assume an upright position; as, to rise\n   from a chair or from a fall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rise', 'v.', 'To leave ones bed; to arise; as, to rise early.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rise', 'v.', 'To tower up; to be heaved up; as, the Alps rise far above the\n   sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rise', 'v.', 'To slope upward; as, a path, a line, or surface rises in this\n   direction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rise', 'v.', 'To retire; to give up a siege.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rise', 'v.', 'To swell or puff up in the process of fermentation; to become\n   light, as dough, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rise', 'v.', 'To have the aspect or the effect of rising.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rise', 'v.', 'To appear above the horizont, as the sun, moon, stars, and\n   the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rise', 'v.', 'To become apparent; to emerge into sight; to come forth; to\n   appear; as, an eruption rises on the skin; the land rises to view to\n   one sailing toward the shore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rise', 'v.', 'To become perceptible to other senses than sight; as, a noise\n   rose on the air; odor rises from the flower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rise', 'v.', 'To have a beginning; to proceed; to originate; as, rivers\n   rise in lakes or springs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rise', 'v.', 'To increase in size, force, or value; to proceed toward a\n   climax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rise', 'v.', 'To increase in power or fury; -- said of wind or a storm, and\n   hence, of passion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rise', 'v.', 'To become of higher value; to increase in price.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rise', 'v.', 'To become larger; to swell; -- said of a boil, tumor, and the\n   like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rise', 'v.', 'To increase in intensity; -- said of heat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rise', 'v.', 'To become louder, or higher in pitch, as the voice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rise', 'v.', 'To increase in amount; to enlarge; as, his expenses rose\n   beyond his expectations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rise', 'v.', 'In various figurative senses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rise', 'v.', 'To become excited, opposed, or hostile; to go to war; to take\n   up arms; to rebel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rise', 'v.', 'To attain to a better social position; to be promoted; to\n   excel; to succeed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rise', 'v.', 'To become more and more dignified or forcible; to increase in\n   interest or power; -- said of style, thought, or discourse; as, to rise\n   in force of expression; to rise in eloquence; a story rises in\n   interest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rise', 'v.', 'To come to mind; to be suggested; to occur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rise', 'v.', 'To come; to offer itself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rise', 'v.', 'To ascend from the grave; to come to life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rise', 'v.', 'To terminate an official sitting; to adjourn; as, the\n   committee rose after agreeing to the report.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rise', 'v.', 'To ascend on a musical scale; to take a higher pith; as, to\n   rise a tone or semitone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rise', 'v.', 'To be lifted, or to admit of being lifted, from the imposing\n   stone without dropping any of the type; -- said of a form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rise', 'n.', 'The act of rising, or the state of being risen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rise', 'n.', 'The distance through which anything rises; as, the rise of\n   the thermometer was ten degrees; the rise of the river was six feet;\n   the rise of an arch or of a step.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rise', 'n.', 'Land which is somewhat higher than the rest; as, the house\n   stood on a rise of land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rise', 'n.', 'Spring; source; origin; as, the rise of a stream.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rise', 'n.', 'Appearance above the horizon; as, the rise of the sun or of a\n   planet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rise', 'n.', 'Increase; advance; augmentation, as of price, value, rank,\n   property, fame, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rise', 'n.', 'Increase of sound; a swelling of the voice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rise', 'n.', 'Elevation or ascent of the voice; upward change of key; as, a\n   rise of a tone or semitone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rise', 'n.', 'The spring of a fish to seize food (as a fly) near the\n   surface of the water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Risen', '', 'p. p. & a. from Rise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Risen', 'p. p. & a.', 'Obs. imp. pl. of Rise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Riser', 'n.', 'One who rises; as, an early riser.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Riser', 'n.', 'The upright piece of a step, from tread to tread.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Riser', 'n.', 'Any small upright face, as of a seat, platform, veranda, or\n   the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Riser', 'n.', 'A shaft excavated from below upward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Riser', 'n.', 'A feed head. See under Feed, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rish', 'n.', 'A rush (the plant).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Risibility', 'n.', 'The quality of being risible; as, risibility is\n   peculiar to the human species.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Risible', 'a.', 'Having the faculty or power of laughing; disposed to\n   laugh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Risible', 'a.', 'Exciting laughter; worthy to be laughed at; amusing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Risible', 'a.', 'Used in, or expressing, laughter; as, risible muscles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rising', 'a.', 'Attaining a higher place; taking, or moving in, an upward\n   direction; appearing above the horizon; ascending; as, the rising moon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rising', 'a.', 'Increasing in wealth, power, or distinction; as, a rising\n   state; a rising character.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rising', 'a.', 'Growing; advancing to adult years and to the state of\n   active life; as, the rising generation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rising', 'prep.', 'More than; exceeding; upwards of; as, a horse rising six\n   years of age.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rising', 'n.', 'The act of one who, or that which, rises (in any sense).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rising', 'n.', 'That which rises; a tumor; a boil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Risk', 'n.', 'Hazard; danger; peril; exposure to loss, injury, or\n   destruction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Risk', 'n.', 'Hazard of loss; liabillity to loss in property.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Risked', 'imp. & p. p.', 'of Risk');
  INSERT INTO words (word, word_type, description) VALUES  (    'Risking', 'p. pr. & vb. n.', 'of Risk');
  INSERT INTO words (word, word_type, description) VALUES  (    'Risk', 'n.', 'To expose to risk, hazard, or peril; to venture; as, to risk\n   goods on board of a ship; to risk ones person in battle; to risk ones\n   fame by a publication.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Risk', 'n.', 'To incur the risk or danger of; as, to risk a battle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Risker', 'n.', 'One who risks or hazards.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Riskful', 'a.', 'Risky.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Risky', 'a.', 'Attended with risk or danger; hazardous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Risorial', 'a.', 'Pertaining to, or producing, laughter; as, the risorial\n   muscles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Risotto', 'n.', 'A kind of pottage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Risse', '', 'imp. of Rise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rissoid', 'n.', 'Any one of very numerous species of small spiral\n   gastropods of the genus Rissoa, or family Rissoidae, found both in\n   fresh and salt water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rissole', 'n.', 'A small ball of rich minced meat or fish, covered with\n   pastry and fried.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rist', '', '3d pers. sing. pres. of Rise, contracted from riseth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rit', '', '3d pers. sing. pres. of Ride, contracted from rideth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ritardando', 'a.', 'Retarding; -- a direction for slower time; rallentado.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rite', 'n.', 'The act of performing divine or solemn service, as\n   established by law, precept, or custom; a formal act of religion or\n   other solemn duty; a solemn observance; a ceremony; as, the rites of\n   freemasonry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ritenuto', 'a.', 'Held back; holding back; ritardando.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ritornelle', 'n.', 'Alt. of Ritornello');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ritornello', 'n.', 'A short return or repetition; a concluding symphony to\n   an air, often consisting of the burden of the song.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ritornello', 'n.', 'A short intermediate symphony, or instrumental passage,\n   in the course of a vocal piece; an interlude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ritratto', 'n.', 'A picture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ritual', 'a.', 'Of or pertaining to rites or ritual; as, ritual service or\n   sacrifices; the ritual law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ritual', 'n.', 'A prescribed form of performing divine service in a\n   particular church or communion; as, the Jewish ritual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ritual', 'n.', 'Hence, the code of ceremonies observed by an organization;\n   as, the ritual of the freemasons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ritual', 'n.', 'A book containing the rites to be observed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ritualism', 'n.', 'A system founded upon a ritual or prescribed form of\n   religious worship; adherence to, or observance of, a ritual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ritualism', 'n.', 'Specifically :(a) The principles and practices of those\n   in the Church of England, who in the development of the Oxford\n   movement, so-called, have insisted upon a return to the use in church\n   services of the symbolic ornaments (altar cloths, encharistic\n   vestments, candles, etc.) that were sanctioned in the second year of\n   Edward VI., and never, as they maintain, forbidden by competennt\n   authority, although generally disused. Schaff-Herzog Encyc. (b) Also,\n   the principles and practices of those in the Protestant Episcopal\n   Church who sympathize with this party in the Church of England.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ritualist', 'n.', 'One skilled un, or attached to, a ritual; one who\n   advocates or practices ritualism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ritualistic', 'a.', 'Pertaining to, or in accordance with, a ritual;\n   adhering to ritualism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ritually', 'adv.', 'By rites, or by a particular rite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rivage', 'n.', 'A bank, shore, or coast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rivage', 'n.', 'A duty paid to the crown for the passage of vessels on\n   certain rivers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rival', 'n.', 'A person having a common right or privilege with another; a\n   partner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rival', 'n.', 'One who is in pursuit of the same object as another; one\n   striving to reach or obtain something which another is attempting to\n   obtain, and which one only can posses; a competitor; as, rivals in\n   love; rivals for a crown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rival', 'a.', 'Having the same pretensions or claims; standing in\n   competition for superiority; as, rival lovers; rival claims or\n   pretensions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rivaled', 'imp. & p. p.', 'of Rival');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rivalled', '', 'of Rival');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rivaling', 'p. pr. & vb. n.', 'of Rival');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rivalling', '', 'of Rival');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rival', 'v. t.', 'To stand in competition with; to strive to gain some\n   object in opposition to; as, to rival one in love.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rival', 'v. t.', 'To strive to equal or exel; to emulate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rival', 'v. i.', 'To be in rivalry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rivaless', 'n.', 'A female rival.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rivality', 'n.', 'Rivalry; competition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rivality', 'n.', 'Equality, as of right or rank.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rivalries', 'pl. ', 'of Rivalry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rivalry', 'n.', 'The act of rivaling, or the state of being a rival; a\n   competition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rivalship', 'n.', 'Rivalry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rived', 'imp.', 'of Rive');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rived', 'p. p.', 'of Rive');
  INSERT INTO words (word, word_type, description) VALUES  (    'Riven', '', 'of Rive');
  INSERT INTO words (word, word_type, description) VALUES  (    'Riving', 'p. pr. & vb. n.', 'of Rive');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rive', 'v. t.', 'To rend asunder by force; to split; to cleave; as, to rive\n   timber for rails or shingles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rive', 'v. i.', 'To be split or rent asunder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rive', 'n.', 'A place torn; a rent; a rift.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Riveled', 'imp. & p. p.', 'of Rivel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Riveling', 'p. pr. & vb. n.', 'of Rivel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rivel', 'v. t.', 'To contract into wrinkles; to shrivel; to shrink; as,\n   riveled fruit; riveled flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rivel', 'n.', 'A wrinkle; a rimple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Riven', '', 'p. p. & a. from Rive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'River', 'n.', 'One who rives or splits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'River', 'n.', 'A large stream of water flowing in a bed or channel and\n   emptying into the ocean, a sea, a lake, or another stream; a stream\n   larger than a rivulet or brook.');
  INSERT INTO words (word, word_type, description) VALUES  (    'River', 'n.', 'Fig.: A large stream; copious flow; abundance; as, rivers of\n   blood; rivers of oil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'River', 'v. i.', 'To hawk by the side of a river; to fly hawks at river\n   fowl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rivered', 'a.', 'Supplied with rivers; as, a well rivered country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Riveret', 'n.', 'A rivulet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Riverhood', 'n.', 'The quality or state of being a river.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Riverling', 'n.', 'A rivulet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Riverside', 'n.', 'The side or bank of a river.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rivery', 'a.', 'Having rivers; as, a rivery country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rivet', 'n.', 'A metallic pin with a head, used for uniting two plates or\n   pieces of material together, by passing it through them and then\n   beating or pressing down the point so that it shall spread out and form\n   a second head; a pin or bolt headed or clinched at both ends.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Riveted', 'imp. & p. p.', 'of Rivet');
  INSERT INTO words (word, word_type, description) VALUES  (    'Riveting', 'p. pr. & vb. n.', 'of Rivet');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rivet', 'v. t.', 'To fasten with a rivet, or with rivets; as, to rivet two\n   pieces of iron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rivet', 'v. t.', 'To spread out the end or point of, as of a metallic pin,\n   rod, or bolt, by beating or pressing, so as to form a sort of head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rivet', 'v. t.', 'Hence, to fasten firmly; to make firm, strong, or\n   immovable; as, to rivet friendship or affection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Riveter', 'n.', 'One who rivets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Riveting', 'n.', 'The act of joining with rivets; the act of spreading out\n   and clinching the end, as of a rivet, by beating or pressing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Riveting', 'n.', 'The whole set of rivets, collectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rivose', 'a.', 'Marked with sinuate and irregular furrows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rivulet', 'n.', 'A small stream or brook; a streamlet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rixation', 'n.', 'A brawl or quarrel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rixatrix', 'n.', 'A scolding or quarrelsome woman; a scold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rixdaler', 'n.', 'A Dutch silver coin, worth about $1.00.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rix-dollar', 'n.', 'A name given to several different silver coins of\n   Denmark, Holland, Sweden,, NOrway, etc., varying in value from about 30\n   cents to $1.10; also, a British coin worth about 36 cents, used in\n   Ceylon and at the Cape of Good Hope. See Rigsdaler, Riksdaler, and\n   Rixdaler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rizzar', 'v. t.', 'To dry in the sun; as, rizzared haddock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roach', 'n.', 'A cockroach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roach', 'n.', 'A European fresh-water fish of the Carp family (Leuciscus\n   rutilus). It is silver-white, with a greenish back.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roach', 'n.', 'An American chub (Semotilus bullaris); the fallfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roach', 'n.', 'The redfin, or shiner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roach', 'n.', 'A convex curve or arch cut in the edge of a sail to prevent\n   chafing, or to secure a better fit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roach', 'v. t.', 'To cause to arch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roach', 'v. t.', 'To cut off, as a horses mane, so that the part left\n   shall stand upright.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roach-backed', 'a.', 'Having a back like that of roach; -- said of a horse\n   whose back a convex instead of a concave curve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Road', 'n.', 'A journey, or stage of a journey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Road', 'n.', 'An inroad; an invasion; a raid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Road', 'n.', 'A place where one may ride; an open way or public passage for\n   vehicles, persons, and animals; a track for travel, forming a means of\n   communication between one city, town, or place, and another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Road', 'n.', 'A place where ships may ride at anchor at some distance from\n   the shore; a roadstead; -- often in the plural; as, Hampton Roads.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roadbed', 'n.', 'In railroads, the bed or foundation on which the\n   superstructure (ties, rails, etc.) rests; in common roads, the whole\n   material laid in place and ready for travel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roadless', 'a.', 'Destitute of roads.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roadmaker', 'n.', 'One who makes roads.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roadside', 'n.', 'Land adjoining a road or highway; the part of a road or\n   highway that borders the traveled part. Also used ajectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roadstead', 'n.', 'An anchorage off shore. Same as Road, 4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roadster', 'n.', 'A clumsy vessel that works its way from one anchorage to\n   another by means of the tides.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roadster', 'n.', 'A horse that is accustomed to traveling on the high road,\n   or is suitable for use on ordinary roads.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roadster', 'n.', 'A bicycle or tricycle adapted for common roads rather\n   than for the racing track.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roadster', 'n.', 'One who drives much; a coach driver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roadster', 'n.', 'A hunter who keeps to the roads instead of following the\n   hounds across country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roadway', 'n.', 'A road; especially, the part traveled by carriages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roamed', 'imp. & p. p.', 'of Roam');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roaming', 'p. pr. & vb. n.', 'of Roam');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roam', 'v. i.', 'To go from place to place without any certain purpose or\n   direction; to rove; to wander.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roam', 'v. t.', 'To range or wander over.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roam', 'n.', 'The act of roaming; a wandering; a ramble; as, he began his\n   roam oer hill amd dale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roamer', 'n.', 'One who roams; a wanderer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roan', 'a.', 'Having a bay, chestnut, brown, or black color, with gray or\n   white thickly interspersed; -- said of a horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roan', 'a.', 'Made of the leather called roan; as, roan binding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roan', 'n.', 'The color of a roan horse; a roan color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roan', 'n.', 'A roan horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roan', 'n.', 'A kind of leather used for slippers, bookbinding, etc., made\n   from sheepskin, tanned with sumac and colored to imitate ungrained\n   morocco.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roared', 'imp. & p. p.', 'of Roar');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roaring', 'p. pr. & vvb. n.', 'of Roar');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roar', 'v. i.', 'To cry with a full, loud, continued sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roar', 'v. i.', 'To bellow, or utter a deep, loud cry, as a lion or other\n   beast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roar', 'v. i.', 'To cry loudly, as in pain, distress, or anger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roar', 'v. i.', 'To make a loud, confused sound, as winds, waves, passing\n   vehicles, a crowd of persons when shouting together, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roar', 'v. i.', 'To be boisterous; to be disorderly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roar', 'v. i.', 'To laugh out loudly and continuously; as, the hearers\n   roared at his jokes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roar', 'v. i.', 'To make a loud noise in breathing, as horses having a\n   certain disease. See Roaring, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roar', 'v. t.', 'To cry aloud; to proclaim loudly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roar', 'n.', 'The sound of roaring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roar', 'n.', 'The deep, loud cry of a wild beast; as, the roar of a lion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roar', 'n.', 'The cry of one in pain, distress, anger, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roar', 'n.', 'A loud, continuous, and confused sound; as, the roar of a\n   cannon, of the wind, or the waves; the roar of ocean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roar', 'n.', 'A boisterous outcry or shouting, as in mirth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roarer', 'n.', 'One who, or that which, roars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roarer', 'n.', 'A riotous fellow; a roaring boy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roarer', 'n.', 'A horse subject to roaring. See Roaring, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roarer', 'n.', 'The barn owl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roaring', 'n.', 'A loud, deep, prolonged sound, as of a large beast, or of\n   a person in distress, anger, mirth, etc., or of a noisy congregation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roaring', 'n.', 'An affection of the windpipe of a horse, causing a loud,\n   peculiar noise in breathing under exertion; the making of the noise so\n   caused. See Roar, v. i., 5.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roaringly', 'adv.', 'In a roaring manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roasted', 'imp. & p. p.', 'of Roast');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roasting', 'p. pr. & vb. n.', 'of Roast');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roast', 'v. t.', 'To cook by exposure to radiant heat before a fire; as, to\n   roast meat on a spit, or in an oven open toward the fire and having\n   reflecting surfaces within; also, to cook in a close oven.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roast', 'v. t.', 'To cook by surrounding with hot embers, ashes, sand,\n   etc.; as, to roast a potato in ashes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roast', 'v. t.', 'To dry and parch by exposure to heat; as, to roast\n   coffee; to roast chestnuts, or peanuts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roast', 'v. t.', 'Hence, to heat to excess; to heat violently; to burn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roast', 'v. t.', 'To dissipate by heat the volatile parts of, as ores.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roast', 'v. t.', 'To banter severely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roast', 'v. i.', 'To cook meat, fish, etc., by heat, as before the fire or\n   in an oven.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roast', 'v. i.', 'To undergo the process of being roasted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roast', 'n.', 'That which is roasted; a piece of meat which has been\n   roasted, or is suitable for being roasted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roast', 'a.', 'Roasted; as, roast beef.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roaster', 'n.', 'One who roasts meat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roaster', 'n.', 'A contrivance for roasting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roaster', 'n.', 'A pig, or other article of food fit for roasting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roasting', '', 'a. & n., from Roast, v.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rob', 'n.', 'The inspissated juice of ripe fruit, obtained by evaporation\n   of the juice over a fire till it acquires the consistence of a sirup.\n   It is sometimes mixed with honey or sugar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Robbed', 'imp. & p. p.', 'of Rob');
  INSERT INTO words (word, word_type, description) VALUES  (    'Robbing', 'p. pr. & vb. n.', 'of Rob');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rob', 'v. t.', 'To take (something) away from by force; to strip by\n   stealing; to plunder; to pillage; to steal from.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rob', 'v. t.', 'To take the property of (any one) from his person, or in\n   his presence, feloniously, and against his will, by violence or by\n   putting him in fear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rob', 'v. t.', 'To deprive of, or withhold from, unjustly or injuriously;\n   to defraud; as, to rob one of his rest, or of his good name; a tree\n   robs the plants near it of sunlight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rob', 'v. i.', 'To take that which belongs to another, without right or\n   permission, esp. by violence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roband', 'n.', 'See Roperand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Robber', 'n.', 'One who robs; in law, one who feloniously takes goods or\n   money from the person of another by violence or by putting him in fear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Robberies', 'pl. ', 'of Robbery');
  INSERT INTO words (word, word_type, description) VALUES  (    'Robbery', 'n.', 'The act or practice of robbing; theft.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Robbery', 'n.', 'The crime of robbing. See Rob, v. t., 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Robbin', 'n.', 'A kind of package in which pepper and other dry commodities\n   are sometimes exported from the East Indies. The robbin of rice in\n   Malabar weighs about 84 pounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Robbin', 'n.', 'See Ropeband.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Robe', 'v. t.', 'An outer garment; a dress of a rich, flowing, and elegant\n   style or make; hence, a dress of state, rank, office, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Robe', 'v. t.', 'A skin of an animal, especially, a skin of the bison,\n   dressed with the fur on, and used as a wrap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Robed', 'imp. & p. p.', 'of Robe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Robing', 'p. pr. & vb. n.', 'of Robe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Robe', 'v. t.', 'To invest with a robe or robes; to dress; to array; as,\n   fields robed with green.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Robe-de-chambre', 'n.', 'A dressing gown, or morning gown.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-men', 'pl. ', 'of Robertsman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roberdsman', 'n.', 'Alt. of Robertsman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Robertsman', 'n.', 'A bold, stout robber, or night thief; -- said to be so\n   called from Robin Hood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Robert', 'n.', 'See Herb Robert, under Herb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Robin', 'n.', 'A small European singing bird (Erythacus rubecula), having a\n   reddish breast; -- called also robin redbreast, robinet, and ruddock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Robin', 'n.', 'An American singing bird (Merula migratoria), having the\n   breast chestnut, or dull red. The upper parts are olive-gray, the head\n   and tail blackish. Called also robin redbreast, and migratory thrush.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Robin', 'n.', 'Any one of several species of Australian warblers of the\n   genera Petroica, Melanadrays, and allied genera; as, the\n   scarlet-breasted robin (Petroica mullticolor).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Robin', 'n.', 'Any one of several Asiatic birds; as, the Indian robins. See\n   Indian robin, below.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Robinet', 'n.', 'The chaffinch; -- called also roberd.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Robinet', 'n.', 'The European robin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Robinet', 'n.', 'A military engine formerly used for throwing darts and\n   stones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Robing', 'n.', 'The act of putting on a robe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Robin Goodfellow', '', 'A celebrated fairy; Puck. See Puck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Robinia', 'n.', 'A genus of leguminous trees including the common locust of\n   North America (Robinia Pseudocacia).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roborant', 'a.', 'Strengthening.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roborant', 'n.', 'A strengthening medicine; a tonic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roborate', 'v. t.', 'To give strength or support to; to confirm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roboration', 'n.', 'The act of strengthening.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roborean', 'a.', 'Alt. of Roboreous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roboreous', 'a.', 'Made of oak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Robust', 'a.', 'Evincing strength; indicating vigorous health; strong;\n   sinewy; muscular; vigorous; sound; as, a robust body; robust youth;\n   robust health.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Robust', 'a.', 'Violent; rough; rude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Robust', 'a.', 'Requiring strength or vigor; as, robust employment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Robustious', 'a.', 'Robust.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Robustly', 'adv.', 'In a robust manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Robustness', 'n.', 'The quality or state of being robust.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roc', 'n.', 'A monstrous bird of Arabian mythology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rocambole', 'n.', 'A name of Allium Scorodoprasum and A. Ascalonium, two\n   kinds of garlic, the latter of which is also called shallot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roccellic', 'a.', 'Pertaining to, or designating, a dibasic acid of the\n   oxalic series found in archil (Roccella tinctoria, etc.), and other\n   lichens, and extracted as a white crystalline substance C17H32O4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roccellin', 'n.', 'A red dyestuff, used as a substitute for cochineal,\n   archil, etc. It consists of the sodium salt of a complex azo derivative\n   of naphtol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roche', 'n.', 'Rock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roche alum', '', 'A kind of alum occuring in small fragments; -- so called\n   from Rocca, in Syria, whence alum is said to have been obtained; --\n   also called rock alum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rochelime', 'n.', 'Lime in the lump after it is burned; quicklime.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rochelle', 'n.', 'A seaport town in France.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roche moutonnee', '', 'See Sheepback.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rochet', 'n.', 'A linen garment resembling the surplise, but with narrower\n   sleeves, also without sleeves, worn by bishops, and by some other\n   ecclesiastical dignitaries, in certain religious ceremonies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rochet', 'n.', 'A frock or outer garment worn in the thirteenth and\n   fourteenth centuries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rochet', 'n.', 'The red gurnard, or gurnet. See Gurnard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roching cask', '', 'A tank in which alum is crystallized from a solution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rock', 'n.', 'See Roc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rock', 'n.', 'A distaff used in spinning; the staff or frame about which\n   flax is arranged, and from which the thread is drawn in spinning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rock', 'n.', 'A large concreted mass of stony material; a large fixed stone\n   or crag. See Stone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rock', 'n.', 'Any natural deposit forming a part of the earths crust,\n   whether consolidated or not, including sand, earth, clay, etc., when in\n   natural beds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rock', 'n.', 'That which resembles a rock in firmness; a defense; a\n   support; a refuge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rock', 'n.', 'Fig.: Anything which causes a disaster or wreck resembling\n   the wreck of a vessel upon a rock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rock', 'n.', 'The striped bass. See under Bass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rocked', 'imp. & p. p.', 'of Rock');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rocking', 'p. pr. & vb. n.', 'of Rock');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rock', 'v. t.', 'To cause to sway backward and forward, as a body resting\n   on a support beneath; as, to rock a cradle or chair; to cause to\n   vibrate; to cause to reel or totter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rock', 'v. t.', 'To move as in a cradle; hence, to put to sleep by rocking;\n   to still; to quiet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rock', 'v. i.', 'To move or be moved backward and forward; to be violently\n   agitated; to reel; to totter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rock', 'v. i.', 'To roll or saway backward and forward upon a support; as,\n   to rock in a rocking-chair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rockaway', '', 'Formerly, a light, low, four-wheeled carriage, with\n   standing top, open at the sides, but having waterproof curtains which\n   could be let down when occasion required; now, a somewhat similar, but\n   heavier, carriage, inclosed, except in front, and having a door at each\n   side.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rockelay', 'n.', 'Alt. of Rocklay');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rocklay', 'n.', 'See Rokelay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rocker', 'n.', 'One who rocks; specifically, one who rocks a cradle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rocker', 'n.', 'One of the curving pieces of wood or metal on which a\n   cradle, chair, etc., rocks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rocker', 'n.', 'Any implement or machine working with a rocking motion, as\n   a trough mounted on rockers for separating gold dust from gravel, etc.,\n   by agitation in water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rocker', 'n.', 'A play horse on rockers; a rocking-horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rocker', 'n.', 'A chair mounted on rockers; a rocking-chair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rocker', 'n.', 'A skate with a curved blade, somewhat resembling in shape\n   the rocker of a cradle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rocker', 'n.', 'Same as Rock shaft.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rockered', 'a.', 'Shaped like a rocker; curved; as, a rockered keel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rockery', 'n.', 'A mound formed of fragments of rock, earth, etc., and set\n   with plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rocket', 'n.', 'A cruciferous plant (Eruca sativa) sometimes eaten in\n   Europe as a salad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rocket', 'n.', 'Damewort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rocket', 'n.', 'Rocket larkspur. See below.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rocket', 'n.', 'An artificial firework consisting of a cylindrical case of\n   paper or metal filled with a composition of combustible ingredients, as\n   niter, charcoal, and sulphur, and fastened to a guiding stick. The\n   rocket is projected through the air by the force arising from the\n   expansion of the gases liberated by combustion of the composition.\n   Rockets are used as projectiles for various purposes, for signals, and\n   also for pyrotechnic display.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rocket', 'n.', 'A blunt lance head used in the joust.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rocketed', 'imp. & p. p.', 'of Rocket');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rocketing', 'p. pr. & vb. n.', 'of Rocket');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rocket', 'v. i.', 'To rise straight up; said of birds; usually in the\n   present participle or as an adjective.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rocketer', 'n.', 'A bird, especially a pheasant, which, being flushed,\n   rises straight in the air like a rocket.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rockfish', 'n.', 'Any one of several California scorpaenoid food fishes of\n   the genus Sebastichthys, as the red rockfish (S. ruber). They are among\n   the most important of California market fishes. Called also rock cod,\n   and garrupa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rockfish', 'n.', 'The striped bass. See Bass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rockfish', 'n.', 'Any one of several species of Florida and Bermuda\n   groupers of the genus Epinephelus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rockfish', 'n.', 'An American fresh-water darter; the log perch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rockiness', 'n.', 'The state or quality of being rocky.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rocking', 'a.', 'Having a swaying, rolling, or back-and-forth movement;\n   used for rocking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rocking-chair', 'n.', 'A chair mounted on rockers, in which one may rock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rocking-horse', 'n.', 'The figure of a horse, mounted upon rockers, for\n   children to ride.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rocking-stone', 'n.', 'A stone, often of great size and weight, resting\n   upon another stone, and so exactly poised that it can be rocked, or\n   slightly moved, with but little force.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rockless', 'a.', 'Being without rocks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rockling', 'n.', 'Any species of small marine fishes of the genera Onos and\n   Rhinonemus (formerly Motella), allied to the cod. They have three or\n   four barbels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rockrose', 'n.', 'A name given to any species of the genus Helianthemum,\n   low shrubs or herbs with yellow flowers, especially the European H.\n   vulgare and the American frostweed, H. Canadense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rock shaft', '', 'A shaft that oscillates on its journals, instead of\n   revolving, -- usually carrying levers by means of which it receives and\n   communicates reciprocating motion, as in the valve gear of some steam\n   engines; -- called also rocker, rocking shaft, and way shaft.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rock staff', 'v. i.', 'An oscillating bar in a machine, as the lever of the\n   bellows of a forge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rocksucker', 'n.', 'A lamprey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rockweed', 'n.', 'Any coarse seaweed growing on sea-washed rocks,\n   especially Fucus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rockwood', 'n.', 'Ligniform asbestus; also, fossil wood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rockwork', 'n.', 'Stonework in which the surface is left broken and rough.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rockwork', 'n.', 'A rockery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rocky', 'a.', 'Full of, or abounding in, rocks; consisting of rocks; as, a\n   rocky mountain; a rocky shore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rocky', 'a.', 'Like a rock; as, the rocky orb of a shield.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rocky', 'a.', 'Fig.: Not easily impressed or affected; hard; unfeeling;\n   obdurate; as, a rocky bosom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rocoa', 'n.', 'The orange-colored pulp covering the seeds of the tropical\n   plant Bixa Orellana, from which annotto is prepared. See Annoto.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rococo', 'n.', 'A florid style of ornamentation which prevailed in Europe\n   in the latter part of the eighteenth century.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rococo', 'a.', 'Of or pertaining to the style called rococo; like rococo;\n   florid; fantastic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rod', 'n.', 'A straight and slender stick; a wand; hence, any slender bar,\n   as of wood or metal (applied to various purposes).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rod', 'n.', 'An instrument of punishment or correction; figuratively,\n   chastisement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rod', 'n.', 'A kind of sceptor, or badge of office; hence, figuratively,\n   power; authority; tyranny; oppression.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rod', 'n.', 'A support for a fishing line; a fish pole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rod', 'n.', 'A member used in tension, as for sustaining a suspended\n   weight, or in tension and compression, as for transmitting\n   reciprocating motion, etc.; a connecting bar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rod', 'n.', 'An instrument for measuring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rod', 'n.', 'A measure of length containing sixteen and a half feet; --\n   called also perch, and pole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roddy', 'a.', 'Full of rods or twigs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roddy', 'a.', 'Ruddy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rode', 'n.', 'Redness; complexion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rode', '', 'imp. of Ride.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rode', 'n.', 'See Rood, the cross.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rodent', 'v. t.', 'Gnawing; biting; corroding; (Med.) applied to a\n   destructive variety of cancer or ulcer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rodent', 'v. t.', 'Gnawing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rodent', 'v. t.', 'Of or pertaining to the Rodentia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rodent', 'n.', 'One of the Rodentia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rodentia', 'a.', 'An order of mammals having two (rarely four) large\n   incisor teeth in each jaw, distant from the molar teeth. The rats,\n   squirrels, rabbits, marmots, and beavers belong to this order.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rodeo', 'n.', 'A round-up. See Round-up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rodge', 'n.', 'The gadwall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rodomel', 'n.', 'Juice of roses mixed with honey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rodomont', 'n.', 'A vain or blustering boaster; a braggart; a braggadocio.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rodomont', 'a.', 'Bragging; vainly boasting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rodomontade', 'n.', 'Vain boasting; empty bluster or vaunting; rant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rodomontade', 'v. i.', 'To boast; to brag; to bluster; to rant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rodomontadist', 'n.', 'One who boasts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rodomontado', 'n.', 'Rodomontade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rodomontador', 'n.', 'A rodomontadist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rodsmen', 'pl. ', 'of Rodsman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rodsman', 'n.', 'One who carries and holds a leveling staff, or rod, in a\n   surveying party.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rody', 'a.', 'Ruddy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roe', 'n.', 'A roebuck. See Roebuck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roe', 'n.', 'The female of any species of deer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roe', 'n.', 'The ova or spawn of fishes and amphibians, especially when\n   still inclosed in the ovarian membranes. Sometimes applied, loosely, to\n   the sperm and the testes of the male.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roe', 'n.', 'A mottled appearance of light and shade in wood, especially in\n   mahogany.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roebuck', 'n.', 'A small European and Asiatic deer (Capreolus capraea)\n   having erect, cylindrical, branched antlers, forked at the summit.\n   This, the smallest European deer, is very nimble and graceful. It\n   always prefers a mountainous country, or high grounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roed', 'a.', 'Filled with roe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roedeer', 'n.', 'The roebuck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roestone', 'n.', 'Same as Oolite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rogation', 'n.', 'The demand, by the consuls or tribunes, of a law to be\n   passed by the people; a proposed law or decree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rogation', 'n.', 'Litany; supplication.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rogatory', 'a.', 'Seeking information; authorized to examine witnesses or\n   ascertain facts; as, a rogatory commission.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rogue', 'n.', 'A vagrant; an idle, sturdy beggar; a vagabond; a tramp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rogue', 'n.', 'A deliberately dishonest person; a knave; a cheat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rogue', 'n.', 'One who is pleasantly mischievous or frolicsome; hence,\n   often used as a term of endearment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rogue', 'n.', 'An elephant that has separated from a herd and roams about\n   alone, in which state it is very savage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rogue', 'n.', 'A worthless plant occuring among seedlings of some choice\n   variety.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rogue', 'v. i.', 'To wander; to play the vagabond; to play knavish tricks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rogue', 'v. t.', 'To give the name or designation of rogue to; to decry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rogue', 'v. t.', 'To destroy (plants that do not come up to a required\n   standard).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roguery', 'n.', 'The life of a vargant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roguery', 'n.', 'The practices of a rogue; knavish tricks; cheating; fraud;\n   dishonest practices.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roguery', 'n.', 'Arch tricks; mischievousness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rogueship', 'n.', 'The quality or state of being a rogue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roguish', 'a.', 'Vagrant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roguish', 'a.', 'Resembling, or characteristic of, a rogue; knavish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roguish', 'a.', 'Pleasantly mischievous; waggish; arch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roguy', 'a.', 'Roguish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rohob', 'n.', 'An inspissated juice. See Rob.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roial', 'a.', 'Royal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roiled', 'imp. & p. p.', 'of Roil');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roiling', 'p. pr. & vb. n.', 'of Roil');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roil', 'v.', 'To render turbid by stirring up the dregs or sediment of; as,\n   to roil wine, cider, etc. , in casks or bottles; to roil a spring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roil', 'v.', 'To disturb, as the temper; to ruffle the temper of; to rouse\n   the passion of resentment in; to perplex.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roil', 'v. i.', 'To wander; to roam.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roil', 'v. i.', 'To romp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roily', 'a.', 'Turbid; as, roily water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roin', 'v. t.', 'See Royne.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roin', 'n.', 'A scab; a scurf, or scurfy spot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roinish', 'a.', 'See Roynish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roint', 'interj.', 'See Aroint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roist', 'v. i.', 'See Roister.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roister', 'v. i.', 'To bluster; to swagger; to bully; to be bold, noisy,\n   vaunting, or turbulent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roister', 'n.', 'See Roisterer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roisterer', 'n.', 'A blustering, turbulent fellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roisterly', 'a.', 'Blustering; violent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roisterly', 'adv.', 'In a roistering manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rokambole', 'n.', 'See Rocambole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roke', 'n.', 'Mist; smoke; damp');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roke', 'n.', 'A vein of ore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rokeage', 'n.', 'Alt. of Rokee');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rokee', 'n.', 'Parched Indian corn, pounded up and mixed with sugar; --\n   called also yokeage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rokelay', 'n.', 'A short cloak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roky', 'a.', 'Misty; foggy; cloudy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Role', 'n.', 'A part, or character, performed by an actor in a drama;\n   hence, a part of function taken or assumed by any one; as, he has now\n   taken the role of philanthropist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rolled', 'imp. & p. p.', 'of Roll');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rolling', 'p. pr. & vb. n.', 'of Roll');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roll', 'n.', 'To cause to revolve by turning over and over; to move by\n   turning on an axis; to impel forward by causing to turn over and over\n   on a supporting surface; as, to roll a wheel, a ball, or a barrel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roll', 'n.', 'To wrap round on itself; to form into a spherical or\n   cylindrical body by causing to turn over and over; as, to roll a sheet\n   of paper; to roll parchment; to roll clay or putty into a ball.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roll', 'n.', 'To bind or involve by winding, as in a bandage; to inwrap; --\n   often with up; as, to roll up a parcel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roll', 'n.', 'To drive or impel forward with an easy motion, as of rolling;\n   as, a river rolls its waters to the ocean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roll', 'n.', 'To utter copiously, esp. with sounding words; to utter with a\n   deep sound; -- often with forth, or out; as, to roll forth some ones\n   praises; to roll out sentences.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roll', 'n.', 'To press or level with a roller; to spread or form with a\n   roll, roller, or rollers; as, to roll a field; to roll paste; to roll\n   steel rails, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roll', 'n.', 'To move, or cause to be moved, upon, or by means of, rollers\n   or small wheels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roll', 'n.', 'To beat with rapid, continuous strokes, as a drum; to sound a\n   roll upon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roll', 'n.', 'To apply (one line or surface) to another without slipping;\n   to bring all the parts of (one line or surface) into successive contact\n   with another, in suck manner that at every instant the parts that have\n   been in contact are equal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roll', 'n.', 'To turn over in ones mind; to revolve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roll', 'v. i.', 'To move, as a curved object may, along a surface by\n   rotation without sliding; to revolve upon an axis; to turn over and\n   over; as, a ball or wheel rolls on the earth; a body rolls on an\n   inclined plane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roll', 'v. i.', 'To move on wheels; as, the carriage rolls along the\n   street.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roll', 'v. i.', 'To be wound or formed into a cylinder or ball; as, the\n   cloth rolls unevenly; the snow rolls well.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roll', 'v. i.', 'To fall or tumble; -- with over; as, a stream rolls over a\n   precipice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roll', 'v. i.', 'To perform a periodical revolution; to move onward as with\n   a revolution; as, the rolling year; ages roll away.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roll', 'v. i.', 'To turn; to move circularly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roll', 'v. i.', 'To move, as waves or billows, with alternate swell and\n   depression.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roll', 'v. i.', 'To incline first to one side, then to the other; to rock;\n   as, there is a great difference in ships about rolling; in a general\n   semse, to be tossed about.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roll', 'v. i.', 'To turn over, or from side to side, while lying down; to\n   wallow; as, a horse rolls.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roll', 'v. i.', 'To spread under a roller or rolling-pin; as, the paste\n   rolls well.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roll', 'v. i.', 'To beat a drum with strokes so rapid that they can\n   scarcely be distinguished by the ear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roll', 'v. i.', 'To make a loud or heavy rumbling noise; as, the thunder\n   rolls.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roll', 'v.', 'The act of rolling, or state of being rolled; as, the roll of\n   a ball; the roll of waves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roll', 'v.', 'That which rolls; a roller.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roll', 'v.', 'A heavy cylinder used to break clods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roll', 'v.', 'One of a set of revolving cylinders, or rollers, between\n   which metal is pressed, formed, or smoothed, as in a rolling mill; as,\n   to pass rails through the rolls.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roll', 'v.', 'That which is rolled up; as, a roll of fat, of wool, paper,\n   cloth, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roll', 'v.', 'A document written on a piece of parchment, paper, or other\n   materials which may be rolled up; a scroll.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roll', 'v.', 'Hence, an official or public document; a register; a record;\n   also, a catalogue; a list.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roll', 'v.', 'A quantity of cloth wound into a cylindrical form; as, a roll\n   of carpeting; a roll of ribbon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roll', 'v.', 'A cylindrical twist of tobacco.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roll', 'v.', 'A kind of shortened raised biscuit or bread, often rolled or\n   doubled upon itself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roll', 'v.', 'The oscillating movement of a vessel from side to side, in\n   sea way, as distinguished from the alternate rise and fall of bow and\n   stern called pitching.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roll', 'v.', 'A heavy, reverberatory sound; as, the roll of cannon, or of\n   thunder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roll', 'v.', 'The uniform beating of a drum with strokes so rapid as\n   scarcely to be distinguished by the ear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roll', 'v.', 'Part; office; duty; role.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rollable', 'a.', 'Capable of being rolled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roller', 'n.', 'One who, or that which, rolls; especially, a cylinder,\n   sometimes grooved, of wood, stone, metal, etc., used in husbandry and\n   the arts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roller', 'n.', 'A bandage; a fillet; properly, a long and broad bandage\n   used in surgery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roller', 'n.', 'One of series of long, heavy waves which roll in upon a\n   coast, sometimes in calm weather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roller', 'n.', 'A long, belt-formed towel, to be suspended on a rolling\n   cylinder; -- called also roller towel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roller', 'n.', 'A cylinder coated with a composition made principally of\n   glue and molassess, with which forms of type are inked previously to\n   taking an impression from them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roller', 'n.', 'A long cylinder on which something is rolled up; as, the\n   roller of a man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roller', 'n.', 'A small wheel, as of a caster, a roller skate, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roller', 'n.', 'ANy insect whose larva rolls up leaves; a leaf roller. see\n   Tortrix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roller', 'n.', 'Any one of numerous species of Old World picarian birds of\n   the family Coraciadae. The name alludes to their habit of suddenly\n   turning over or \"tumbling\" in flight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roller', 'n.', 'Any species of small ground snakes of the family\n   Tortricidae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rolley', 'n.', 'A small wagon used for the underground work of a mine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rollicked', 'imp. & p. p.', 'of Rollic');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rollicking', 'p. pr. & vb. n.', 'of Rollic');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rollic', 'v. i.', 'To move or play in a careless, swaggering manner, with a\n   frolicsome air; to frolic; to sport; commonly in the form rollicking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rolling', 'a.', 'Rotating on an axis, or moving along a surface by\n   rotation; turning over and over as if on an axis or a pivot; as, a\n   rolling wheel or ball.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rolling', 'a.', 'Moving on wheels or rollers, or as if on wheels or\n   rollers; as, a rolling chair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rolling', 'a.', 'Having gradual, rounded undulations of surface; as, a\n   rolling country; rolling land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rolling-pin', 'n.', 'A cylindrical piece of wood or other material, with\n   which paste or dough may be rolled out and reduced to a proper\n   thickness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rollway', 'n.', 'A place prepared for rolling logs into a stream.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rolly-poly', 'n.', 'A kind of pudding made of paste spread with fruit,\n   rolled into a cylindrical form, and boiled or steamed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rolly-poly', 'a.', 'Shaped like a rolly-poly; short and stout.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rolly-pooly', 'n.', 'A game in which a ball, rolling into a certain place,\n   wins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roly-poly', 'n. & a.', 'Rolly-poly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Romage', 'n. & v.', 'See Rummage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Romaic', 'a.', 'Of or relating to modern Greece, and especially to its\n   language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Romaic', 'n.', 'The modern Greek language, now usually called by the Greeks\n   Hellenic or Neo-Hellenic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roman', 'a.', 'Of or pertaining to Rome, or the Roman people; like or\n   characteristic of Rome, the Roman people, or things done by Romans; as,\n   Roman fortitude; a Roman aqueduct; Roman art.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roman', 'a.', 'Of or pertaining to the Roman Catholic religion; professing\n   that religion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roman', 'a.', 'Upright; erect; -- said of the letters or kind of type\n   ordinarily used, as distinguished from Italic characters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roman', 'a.', 'Expressed in letters, not in figures, as I., IV., i., iv.,\n   etc.; -- said of numerals, as distinguished from the Arabic numerals,\n   1, 4, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roman', 'n.', 'A native, or permanent resident, of Rome; a citizen of Rome,\n   or one upon whom certain rights and privileges of a Roman citizen were\n   conferred.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roman', 'n.', 'Roman type, letters, or print, collectively; -- in\n   distinction from Italics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Romance', 'n.', 'A species of fictitious writing, originally composed in\n   meter in the Romance dialects, and afterward in prose, such as the\n   tales of the court of Arthur, and of Amadis of Gaul; hence, any\n   fictitious and wonderful tale; a sort of novel, especially one which\n   treats of surprising adventures usually befalling a hero or a heroine;\n   a tale of extravagant adventures, of love, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Romance', 'n.', 'An adventure, or series of extraordinary events,\n   resembling those narrated in romances; as, his courtship, or his life,\n   was a romance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Romance', 'n.', 'A dreamy, imaginative habit of mind; a disposition to\n   ignore what is real; as, a girl full of romance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Romance', 'n.', 'The languages, or rather the several dialects, which were\n   originally forms of popular or vulgar Latin, and have now developed\n   into Italian. Spanish, French, etc. (called the Romanic languages).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Romance', 'n.', 'A short lyric tale set to music; a song or short\n   instrumental piece in ballad style; a romanza.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Romance', 'a.', 'Of or pertaining to the language or dialects known as\n   Romance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Romanced', 'imp. & p. p.', 'of Romance');
  INSERT INTO words (word, word_type, description) VALUES  (    'Romancing', 'p. pr. & vb. n.', 'of Romance');
  INSERT INTO words (word, word_type, description) VALUES  (    'Romance', 'v. i.', 'To write or tell romances; to indulge in extravagant\n   stories.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Romancer', 'n.', 'One who romances.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Romancist', 'n.', 'A romancer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Romancy', 'a.', 'Romantic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Romanesque', 'a.', 'Somewhat resembling the Roman; -- applied sometimes to\n   the debased style of the later Roman empire, but esp. to the more\n   developed architecture prevailing from the 8th century to the 12th.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Romanesque', 'a.', 'Of or pertaining to romance or fable; fanciful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Romanesque', 'n.', 'Romanesque style.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Romanic', 'n.', 'Of or pertaining to Rome or its people.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Romanic', 'n.', 'Of or pertaining to any or all of the various languages\n   which, during the Middle Ages, sprung out of the old Roman, or popular\n   form of Latin, as the Italian, Spanish, Portuguese, French, Provencal,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Romanic', 'n.', 'Related to the Roman people by descent; -- said especially\n   of races and nations speaking any of the Romanic tongues.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Romanish', 'a.', 'Pertaining to Romanism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Romanism', 'n.', 'The tenets of the Church of Rome; the Roman Catholic\n   religion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Romanist', 'n.', 'One who adheres to Romanism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Romanized', 'imp. & p. p.', 'of Romanize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Romanizing', 'p. pr. & vb. n.', 'of Romanize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Romanize', 'v. t.', 'To Latinize; to fill with Latin words or idioms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Romanize', 'v. t.', 'To convert to the Roman Catholic religion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Romanize', 'v. i.', 'To use Latin words and idioms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Romanize', 'v. i.', 'To conform to Roman Catholic opinions, customs, or\n   modes of speech.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Romanizer', 'n.', 'One who Romanizes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Romansch', 'n.', 'The language of the Grisons in Switzerland, a corruption\n   of the Latin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Romant', 'n.', 'A romaunt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Romantic', 'a.', 'Of or pertaining to romance; involving or resembling\n   romance; hence, fanciful; marvelous; extravagant; unreal; as, a\n   romantic tale; a romantic notion; a romantic undertaking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Romantic', 'a.', 'Entertaining ideas and expectations suited to a romance;\n   as, a romantic person; a romantic mind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Romantic', 'a.', 'Of or pertaining to the style of the Christian and\n   popular literature of the Middle Ages, as opposed to the classical\n   antique; of the nature of, or appropriate to, that style; as, the\n   romantic school of poets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Romantic', 'a.', 'Characterized by strangeness or variety; suggestive of\n   adventure; suited to romance; wild; picturesque; -- applied to scenery;\n   as, a romantic landscape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Romantical', 'a.', 'Romantic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Romanticaly', 'adv.', 'In a romantic manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Romanticism', 'n.', 'A fondness for romantic characteristics or\n   peculiarities; specifically, in modern literature, an aiming at\n   romantic effects; -- applied to the productions of a school of writers\n   who sought to revive certain medi/val forms and methods in opposition\n   to the so-called classical style.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Romanticist', 'n.', 'One who advocates romanticism in modern literature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Romanticly', 'adv.', 'Romantically.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Romanticness', 'n.', 'The state or quality of being romantic; widness;\n   fancifulness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Romany', 'n.', 'A gypsy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Romany', 'n.', 'The language spoken among themselves by the gypsies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Romanza', 'n.', 'See Romance, 5.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Romaunt', 'n.', 'A romantic story in verse; as, the \"Romaunt of the Rose.\"');
  INSERT INTO words (word, word_type, description) VALUES  (    'Romble', 'v.& n.', 'Rumble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rombowline', 'n.', 'Old, condemned canvas, rope, etc., unfit for use except\n   in chafing gear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Romeine', 'n.', 'Alt. of Romeite');
  INSERT INTO words (word, word_type, description) VALUES  (    'Romeite', 'n.', 'A mineral of a hyacinth or honey-yellow color, occuring in\n   square octahedrons. It is an antimonate of calcium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Romekin', 'n.', 'A drinking cup.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rome penny', '', 'Alt. of Rome scot');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rome scot', '', 'See Peter pence, under Peter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Romeward', 'adv.', 'Toward Rome, or toward the Roman Catholic Church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Romeward', 'a.', 'Tending or directed toward Rome, or toward the Roman\n   Catholic Church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Romic', 'n.', 'A method of notation for all spoken sounds, proposed by Mr.\n   Sweet; -- so called because it is based on the common Roman-letter\n   alphabet. It is like the palaeotype of Mr. Ellis in the general plan,\n   but simpler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Romish', 'a.', 'Belonging or relating to Rome, or to the Roman Catholic\n   Church; -- frequently used in a disparaging sense; as, the Romish\n   church; the Romish religion, ritual, or ceremonies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Romist', 'n.', 'A Roman Catholic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Romped', 'imp. & p. p.', 'of Romp');
  INSERT INTO words (word, word_type, description) VALUES  (    'Romping', 'p. pr. & vb. n.', 'of Romp');
  INSERT INTO words (word, word_type, description) VALUES  (    'Romp', 'v. i.', 'To play rudely and boisterously; to leap and frisk about\n   in play.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Romp', 'n.', 'A girl who indulges in boisterous play.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Romp', 'n.', 'Rude, boisterous play or frolic; rough sport.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Romping', 'a.', 'Inclined to romp; indulging in romps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rompingly', 'adv.', 'In a romping manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rompish', 'a.', 'Given to rude play; inclined to romp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rompu', 'a.', 'Broken, as an ordinary; cut off, or broken at the top, as a\n   chevron, a bend, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roncador', 'n.', 'Any one of several species of California sciaenoid food\n   fishes, especially Roncador Stearnsi, which is an excellent market\n   fish, and the red roncador (Corvina, / Johnius, saturna).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ronchil', 'n.', 'An American marine food fish (Bathymaster signatus) of the\n   North Pacific coast, allied to the tilefish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ronco', 'n.', 'See Croaker, n., 2. (a).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rondache', 'n.', 'A circular shield carried by foot soldiers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ronde', 'n.', 'A kind of script in which the heavy strokes are nearly\n   upright, giving the characters when taken together a round look.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rondeau', 'n.', 'A species of lyric poetry so composed as to contain a\n   refrain or repetition which recurs according to a fixed law, and a\n   limited number of rhymes recurring also by rule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rondeau', 'n.', 'See Rondo, 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rondel', 'n.', 'A small round tower erected at the foot of a bastion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rondel', 'n.', 'Same as Rondeau.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rondel', 'n.', 'Specifically, a particular form of rondeau containing\n   fourteen lines in two rhymes, the refrain being a repetition of the\n   first and second lines as the seventh and eighth, and again as the\n   thirteenth and fourteenth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rondeletia', 'n.', 'A tropical genus of rubiaceous shrubs which often have\n   brilliant flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rondle', 'n.', 'A rondeau.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rondle', 'n.', 'A round mass, plate, or disk; especially (Metal.), the\n   crust or scale which forms upon the surface of molten metal in the\n   crucible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rondo', 'n.', 'A composition, vocal or instrumental, commonly of a lively,\n   cheerful character, in which the first strain recurs after each of the\n   other strains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rondo', 'n.', 'See Rondeau, 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rondure', 'n.', 'A round; a circle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rondure', 'n.', 'Roundness; plumpness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rong', '', 'imp. & p. p. of Ring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rong', 'n.', 'Rung (of a ladder).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rongeur', 'n.', 'An instrument for removing small rough portions of bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ronion', 'n.', 'Alt. of Ronyon');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ronyon', 'n.', 'A mangy or scabby creature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ronne', '', 'obs. imp. pl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ronnen', '', 'obs. p. p. of Renne, to run.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ront', 'n.', 'A runt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rood', 'n.', 'A representation in sculpture or in painting of the cross\n   with Christ hanging on it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rood', 'n.', 'A measure of five and a half yards in length; a rod; a perch;\n   a pole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rood', 'n.', 'The fourth part of an acre, or forty square rods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roodebok', 'n.', 'The pallah.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roody', 'a.', 'Rank in growth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roof', 'n.', 'The cover of any building, including the roofing (see\n   Roofing) and all the materials and construction necessary to carry and\n   maintain the same upon the walls or other uprights. In the case of a\n   building with vaulted ceilings protected by an outer roof, some writers\n   call the vault the roof, and the outer protection the roof mask. It is\n   better, however, to consider the vault as the ceiling only, in cases\n   where it has farther covering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roof', 'n.', 'That which resembles, or corresponds to, the covering or the\n   ceiling of a house; as, the roof of a cavern; the roof of the mouth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roof', 'n.', 'The surface or bed of rock immediately overlying a bed of\n   coal or a flat vein.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roofed', 'imp. & p. p.', 'of Roof');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roofing', 'p. pr. & vb. n.', 'of Roof');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roof', 'v. t.', 'To cover with a roof.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roof', 'v. t.', 'To inclose in a house; figuratively, to shelter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roofer', 'n.', 'One who puts on roofs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roofing', 'n.', 'The act of covering with a roof.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roofing', 'n.', 'The materials of which a roof is composed; materials for a\n   roof.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roofing', 'n.', 'Hence, the roof itself; figuratively, shelter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roofing', 'n.', 'The wedging, as of a horse or car, against the top of an\n   underground passage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roofless', 'a.', 'Having no roof; as, a roofless house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roofless', 'a.', 'Having no house or home; shelterless; homeless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rooflet', 'n.', 'A small roof, covering, or shelter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rooftree', 'n.', 'The beam in the angle of a roof; hence, the roof itself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roofy', 'a.', 'Having roofs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rook', 'n.', 'Mist; fog. See Roke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rook', 'v. i.', 'To squat; to ruck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rook', 'n.', 'One of the four pieces placed on the corner squares of the\n   board; a castle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rook', 'n.', 'A European bird (Corvus frugilegus) resembling the crow, but\n   smaller. It is black, with purple and violet reflections. The base of\n   the beak and the region around it are covered with a rough, scabrous\n   skin, which in old birds is whitish. It is gregarious in its habits.\n   The name is also applied to related Asiatic species.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rook', 'n.', 'A trickish, rapacious fellow; a cheat; a sharper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rooked', 'imp. & p. p.', 'of Rook');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rooking', 'p. pr. & vb. n.', 'of Rook');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rook', 'v. t. & i.', 'To cheat; to defraud by cheating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rookeries', 'pl. ', 'of Rookery');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rookery', 'n.', 'The breeding place of a colony of rooks; also, the birds\n   themselves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rookery', 'n.', 'A breeding place of other gregarious birds, as of herons,\n   penguins, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rookery', 'n.', 'The breeding ground of seals, esp. of the fur seals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rookery', 'n.', 'A dilapidated building with many rooms and occupants; a\n   cluster of dilapidated or mean buildings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rookery', 'n.', 'A brothel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rooky', 'a.', 'Misty; gloomy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Room', 'n.', 'Unobstructed spase; space which may be occupied by or devoted\n   to any object; compass; extent of place, great or small; as, there is\n   not room for a house; the table takes up too much room.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Room', 'n.', 'A particular portion of space appropriated for occupancy; a\n   place to sit, stand, or lie; a seat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Room', 'n.', 'Especially, space in a building or ship inclosed or set apart\n   by a partition; an apartment or chamber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Room', 'n.', 'Place or position in society; office; rank; post; station;\n   also, a place or station once belonging to, or occupied by, another,\n   and vacated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Room', 'n.', 'Possibility of admission; ability to admit; opportunity to\n   act; fit occasion; as, to leave room for hope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roomed', 'imp. & p. p.', 'of Room');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rooming', 'p. pr. & vb. n.', 'of Room');
  INSERT INTO words (word, word_type, description) VALUES  (    'Room', 'v. i.', 'To occupy a room or rooms; to lodge; as, they arranged to\n   room together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Room', 'a.', 'Spacious; roomy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roomage', 'n.', 'Space; place; room.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roomer', 'n.', 'A lodger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roomer', 'a.', 'At a greater distance; farther off.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roomful', 'a.', 'Abounding with room or rooms; roomy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roomfuls', 'pl. ', 'of Roomful');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roomful', 'n.', 'As much or many as a room will hold; as, a roomful of men.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roomily', 'adv.', 'Spaciously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roominess', 'n.', 'The quality or state of being roomy; spaciousness; as,\n   the roominess of a hall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roomless', 'a.', 'Being without room or rooms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roommate', 'n.', 'One of twe or more occupying the same room or rooms; one\n   who shares the occupancy of a room or rooms; a chum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roomsome', 'a.', 'Roomy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roomth', 'n.', 'Room; space.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roomthy', 'a.', 'Roomy; spacious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roomy', 'a.', 'Having ample room; spacious; large; as, a roomy mansion; a\n   roomy deck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roon', 'a. & n.', 'Vermilion red; red.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roop', 'n.', 'See Roup.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roorback', 'n.', 'Alt. of Roorbach');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roorbach', 'n.', 'A defamatory forgery or falsehood published for purposes\n   of political intrigue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roosa oil', '', 'The East Indian name for grass oil. See under Grass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roost', 'n.', 'Roast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roost', 'v. t.', 'See Roust, v. t.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roost', 'n.', 'The pole or other support on which fowls rest at night; a\n   perch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roost', 'n.', 'A collection of fowls roosting together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roosted', 'imp. & p. p.', 'of Roost');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roosting', 'p. pr. & vb. n.', 'of Roost');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roost', 'v. i.', 'To sit, rest, or sleep, as fowls on a pole, limb of a\n   tree, etc.; to perch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roost', 'v. i.', 'Fig.; To lodge; to rest; to sleep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roostcock', 'n.', 'The male of the domestic fowl; a cock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rooster', 'n.', 'The male of the domestic fowl; a cock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Root', 'v. i.', 'To turn up the earth with the snout, as swine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Root', 'v. i.', 'Hence, to seek for favor or advancement by low arts or\n   groveling servility; to fawn servilely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Root', 'v. t.', 'To turn up or to dig out with the snout; as, the swine\n   roots the earth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Root', 'n.', 'The underground portion of a plant, whether a true root or a\n   tuber, a bulb or rootstock, as in the potato, the onion, or the sweet\n   flag.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Root', 'n.', 'The descending, and commonly branching, axis of a plant,\n   increasing in length by growth at its extremity only, not divided into\n   joints, leafless and without buds, and having for its offices to fix\n   the plant in the earth, to supply it with moisture and soluble matters,\n   and sometimes to serve as a reservoir of nutriment for future growth. A\n   true root, however, may never reach the ground, but may be attached to\n   a wall, etc., as in the ivy, or may hang loosely in the air, as in some\n   epiphytic orchids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Root', 'n.', 'An edible or esculent root, especially of such plants as\n   produce a single root, as the beet, carrot, etc.; as, the root crop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Root', 'n.', 'That which resembles a root in position or function, esp. as\n   a source of nourishment or support; that from which anything proceeds\n   as if by growth or development; as, the root of a tooth, a nail, a\n   cancer, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Root', 'n.', 'An ancestor or progenitor; and hence, an early race; a stem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Root', 'n.', 'A primitive form of speech; one of the earliest terms\n   employed in language; a word from which other words are formed; a\n   radix, or radical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Root', 'n.', 'The cause or occasion by which anything is brought about; the\n   source.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Root', 'n.', 'That factor of a quantity which when multiplied into itself\n   will produce that quantity; thus, 3 is a root of 9, because 3\n   multiplied into itself produces 9; 3 is the cube root of 27.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Root', 'n.', 'The fundamental tone of any chord; the tone from whose\n   harmonics, or overtones, a chord is composed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Root', 'n.', 'The lowest place, position, or part.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Root', 'n.', 'The time which to reckon in making calculations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rooted', 'imp. & p. p.', 'of Root');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rooting', 'p. pr. & vb. n.', 'of Root');
  INSERT INTO words (word, word_type, description) VALUES  (    'Root', 'v. i.', 'To fix the root; to enter the earth, as roots; to take\n   root and begin to grow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Root', 'v. i.', 'To be firmly fixed; to be established.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Root', 'v. t.', 'To plant and fix deeply in the earth, or as in the earth;\n   to implant firmly; hence, to make deep or radical; to establish; --\n   used chiefly in the participle; as, rooted trees or forests; rooted\n   dislike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Root', 'v. t.', 'To tear up by the root; to eradicate; to extirpate; --\n   with up, out, or away.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rootcap', 'n.', 'A mass of parenchymatous cells which covers and protects\n   the growing cells at the end of a root; a pileorhiza.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rooted', 'a.', 'Having taken root; firmly implanted; fixed in the heart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rooter', 'n.', 'One who, or that which, roots; one that tears up by the\n   roots.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rootery', 'n.', 'A pile of roots, set with plants, mosses, etc., and used\n   as an ornamental object in gardening.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rootless', 'a.', 'Destitute of roots.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rootlet', 'n.', 'A radicle; a little root.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rootstock', 'n.', 'A perennial underground stem, producing leafly s/ems or\n   flower stems from year to year; a rhizome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rooty', 'a.', 'Full of roots; as, rooty ground.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ropalic', 'a.', 'See Rhopalic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rope', 'n.', 'A large, stout cord, usually one not less than an inch in\n   circumference, made of strands twisted or braided together. It differs\n   from cord, line, and string, only in its size. See Cordage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rope', 'n.', 'A row or string consisting of a number of things united, as\n   by braiding, twining, etc.; as, a rope of onions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rope', 'n.', 'The small intestines; as, the ropes of birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roped', 'imp. & p. p.', 'of Rope');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roping', 'p. pr. & vb. n.', 'of Rope');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rope', 'v. i.', 'To be formed into rope; to draw out or extend into a\n   filament or thread, as by means of any glutinous or adhesive quality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rope', 'v. t.', 'To bind, fasten, or tie with a rope or cord; as, to rope a\n   bale of goods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rope', 'v. t.', 'To connect or fasten together, as a party of mountain\n   climbers, with a rope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rope', 'v. t.', 'To partition, separate, or divide off, by means of a rope,\n   so as to include or exclude something; as, to rope in, or rope off, a\n   plot of ground; to rope out a crowd.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rope', 'v. t.', 'To lasso (a steer, horse).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rope', 'v. t.', 'To draw, as with a rope; to entice; to inveigle; to decoy;\n   as, to rope in customers or voters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rope', 'v. t.', 'To prevent from winning (as a horse), by pulling or\n   curbing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ropeband', 'n.', 'A small piece of spun yarn or marline, used to fasten the\n   head of the sail to the spar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ropedancer', 'n.', 'One who dances, walks, or performs acrobatic feats, on\n   a rope extended through the air at some height.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roper', 'n.', 'A maker of ropes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roper', 'n.', 'One who ropes goods; a packer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roper', 'n.', 'One fit to be hanged.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ropery', 'n.', 'A place where ropes are made.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ropery', 'n.', 'Tricks deserving the halter; roguery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ropes-end', 'v. t.', 'To punish with a ropes end.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ropewalk', 'a.', 'A long, covered walk, or a low, level building, where\n   ropes are manufactured.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ropewalker', 'n.', 'A ropedancer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rope-yarn', 'n.', 'the yarn or thread of any stuff of which the strands of\n   a rope are made.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ropily', 'adv.', 'In a ropy manner; in a viscous or glutinous manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ropiness', 'n.', 'Quality of being ropy; viscosity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ropish', 'a.', 'Somewhat ropy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ropy', 'a.', 'capable of being drawn into a thread, as a glutinous\n   substance; stringy; viscous; tenacious; glutinous; as ropy sirup; ropy\n   lees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roquelaure', 'n.', 'A cloak reaching about to, or just below, the knees,\n   worn in the 18th century.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roquet', 'v. t.', 'To hit, as anothers ball, with ones own ball.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roquet', 'v. i.', 'To hit anothers ball with ones own.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roral', 'a.', 'Of or pertaining to dew; consisting of dew; dewy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roration', 'n.', 'A falling of dew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roric', 'a.', 'Of or pertaining to dew; resembling dew; dewy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rorid', 'a.', 'Dewy; bedewed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roriferous', 'a.', 'generating or producing dew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rorifluent', 'a.', 'Flowing with dew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rorqual', 'n.', 'A very large North Atlantic whalebone whale (Physalus\n   antiquorum, or Balaenoptera physalus). It has a dorsal fin, and strong\n   longitudinal folds on the throat and belly. Called also razorback.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rorulent', 'a.', 'Full of, or abounding in, dew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rorulent', 'a.', 'Having the surface appearing as if dusty, or covered with\n   fine dew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rory', 'a.', 'Dewy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rosaceous', 'a.', 'Of or pertaining to a natural order of plants (Rosaceae)\n   of which the rose is the type. It includes also the plums and cherries,\n   meadowsweet, brambles, the strawberry, the hawthorn, applies, pears,\n   service trees, and quinces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rosaceous', 'a.', 'Like a rose in shape or appearance; as, a rosaceous\n   corolla.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rosaceous', 'a.', 'Of a pure purpish pink color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rosacic', 'a.', 'Pertaining to, or designating, an acid (called also lithic\n   acid) found in certain red precipitates of urine. See Uric.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rosalgar', 'n.', 'realgar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rosalia', 'n.', 'A form of melody in which a phrase or passage is\n   successively repeated, each time a step or half step higher; a melodic\n   sequence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rosaniline', 'n.', 'A complex nitrogenous base, C20H21N3O, obtained by\n   oxidizing a mixture of aniline and toluidine, as a colorless\n   crystalline substance which forms red salts. These salts are essential\n   components of many of the socalled aniline dyes, as fuchsine, aniline\n   red, etc. By extension, any one of the series of substances derived\n   from, or related to, rosaniline proper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rosarian', 'n.', 'A cultivator of roses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rosaries', 'pl. ', 'of Rosary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rosary', 'n.', 'A bed of roses, or place where roses grow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rosary', 'n.', 'A series of prayers (see Note below) arranged to be recited\n   in order, on beads; also, a string of beads by which the prayers are\n   counted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rosary', 'n.', 'A chapelet; a garland; a series or collection, as of\n   beautiful thoughts or of literary selections.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rosary', 'n.', 'A coin bearing the figure of a rose, fraudulently\n   circulated in Ireland in the 13th century for a penny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roscid', 'a.', 'Containing, or consisting of, dew; dewy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roscoelite', 'n.', 'A green micaceous mineral occurring in minute scales.\n   It is essentially a silicate of aluminia and potash containing\n   vanadium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rose', '', 'imp. of Rise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rose', 'n.', 'A flower and shrub of any species of the genus Rosa, of which\n   there are many species, mostly found in the morthern hemispere');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rose', 'n.', 'A knot of ribbon formed like a rose; a rose knot; a rosette,\n   esp. one worn on a shoe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rose', 'n.', 'A rose window. See Rose window, below.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rose', 'n.', 'A perforated nozzle, as of a pipe, spout, etc., for\n   delivering water in fine jets; a rosehead; also, a strainer at the foot\n   of a pump.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rose', 'n.', 'The erysipelas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rose', 'n.', 'The card of the mariners compass; also, a circular card with\n   radiating lines, used in other instruments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rose', 'n.', 'The color of a rose; rose-red; pink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rose', 'n.', 'A diamond. See Rose diamond, below.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rose', 'v. t.', 'To render rose-colored; to redden; to flush.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rose', 'v. t.', 'To perfume, as with roses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roseal', 'a.', 'resembling a rose in smell or color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roseate', 'a.', 'Full of roses; rosy; as, roseate bowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roseate', 'a.', 'resembling a rose in color or fragrance; esp., tinged with\n   rose color; blooming; as, roseate beauty; her roseate lips.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rosebay', 'n.', 'the oleander.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rosebay', 'n.', 'Any shrub of the genus Rhododendron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rosebay', 'n.', 'An herb (Epilobium spicatum) with showy purple flowers,\n   common in Europe and North America; -- called also great willow herb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rosebud', 'n.', 'The flower of a rose before it opens, or when but\n   partially open.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rosebush', 'n.', 'The bush or shrub which bears roses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rose-colored', 'a.', 'Having the color of a pink rose; rose-pink; of a\n   delicate pink color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rose-colored', 'a.', 'Uncommonly beautiful; hence, extravagantly fine or\n   pleasing; alluring; as, rose-colored anticipations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rose-cut', 'a.', 'Cut flat on the reverse, and with a convex face formed of\n   triangular facets in rows; -- said of diamonds and other precious\n   stones. See Rose diamond, under Rose. Cf. Brilliant, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rosedrop', 'n.', 'A lozenge having a rose flavor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rosedrop', 'n.', 'A kind of earring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rosedrop', 'n.', 'A ruddy eruption upon the nose caused by drinking ardent\n   spirits; a grog blossom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rosefinch', 'n.', 'Any one of numerous species of Asiatic finches of the\n   genera Carpodacus, and Propasser, and allied genera, in which the male\n   is more or less colored with rose red.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rosefish', 'n.', 'A large marine scorpaenoid food fish (Sebastes marinus)\n   found on the northern coasts of Europe and America. called also red\n   perch, hemdurgan, Norway haddok, and also, erroneously, snapper, bream,\n   and bergylt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rosehead', 'n.', 'See Rose, n., 4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rosehead', 'n.', 'A many-sided pyramidal head upon a nail; also a nail with\n   such a head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roseine', 'n.', 'See Magenta.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roselite', 'n.', 'A hydrous arsenite of cobalt, occuring in small red\n   crystals, allied to erythrite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rosella', 'n.', 'A beautiful Australian parrakeet (Platycercus eximius)\n   often kept as a cage bird. The head and back of the neck are scarlet,\n   the throat is white, the back dark green varied with lighter green, and\n   the breast yellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roselle', 'n.', 'a malvaceous plant (Hibiscus Sabdariffa) cultivated in the\n   east and West Indies for its fleshy calyxes, which are used for making\n   tarts and jelly and an acid drink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rosemaloes', 'n.', 'The liquid storax of the East Indian Liquidambar\n   orientalis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rosemary', 'n.', 'A labiate shrub (Rosmarinus officinalis) with narrow\n   grayish leaves, growing native in the southern part of France, Spain,\n   and Italy, also in Asia Minor and in China. It has a fragrant smell,\n   and a warm, pungent, bitterish taste. It is used in cookery, perfumery,\n   etc., and is an emblem of fidelity or constancy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rosen', 'a.', 'Consisting of roses; rosy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rosenmullers organ', '', 'The parovarium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roseo-', '', 'A prefix (also used adjectively) signifying rose-red;\n   specifically used to designate certain rose-red compounds (called\n   roseo-cobaltic compounds) of cobalt with ammonia. Cf. Luteo-.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roseola', 'n.', 'A rose-colored efflorescence upon the skin, occurring in\n   circumscribed patches of little or no elevation and often alternately\n   fading and reviving; also, an acute specific disease which is\n   characterized by an eruption of this character; -- called also rose\n   rash.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rose-pink', 'a.', 'Having a pink color like that of the rose, or like the\n   pigment called rose pink. See Rose pink, under Rose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rose-pink', 'a.', 'Disposed to clothe everything with roseate hues; hence,\n   sentimental.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roser', 'n.', 'A rosier; a rosebush.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rose-red', 'a.', 'Red as a rose; specifically (Zool.), of a pure purplish\n   red color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rose-rial', 'n.', 'A name of several English gold coins struck in different\n   reigns and having having different values; a rose noble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roseroot', 'n.', 'A fleshy-leaved herb (Rhodiola rosea); rosewort; -- so\n   called because the roots have the odor of roses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rosery', 'n.', 'A place where roses are cultivated; a nursery of roses. See\n   Rosary, 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roset', 'n.', 'A red color used by painters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ro-setta stone', '', 'A stone found at Rosetta, in Egypt, bearing a\n   trilingual inscription, by aid of which, with other inscriptions, a key\n   was obtained to the hieroglyphics of ancient Egypt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rosetta wood', '', 'An east Indian wood of a reddish orange color,\n   handsomely veined with darker marks. It is occasionally used for\n   cabinetwork.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rosette', 'n.', 'An imitation of a rose by means of ribbon or other\n   material, -- used as an ornament or a badge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rosette', 'n.', 'An ornament in the form of a rose or roundel, -much used\n   in decoration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rosette', 'n.', 'A red color. See Roset.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rosette', 'n.', 'A rose burner. See under Rose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rosette', 'n.', 'Any structure having a flowerlike form; especially, the\n   group of five broad ambulacra on the upper side of the spatangoid and\n   clypeastroid sea urchins. See Illust. of Spicule, and Sand dollar,\n   under Sand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rosette', 'n.', 'A flowerlike color marking; as, the rosettes on the\n   leopard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rose water', '', 'Water tinctured with roses by distillation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rose-water', 'a.', 'Having the odor of rose water; hence, affectedly nice\n   or delicate; sentimental.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rosewood', 'n.', 'A valuable cabinet wood of a dark red color, streaked and\n   variegated with black, obtained from several tropical leguminous trees\n   of the genera Dalbergia and Machaerium. The finest kind is from Brazil,\n   and is said to be from the Dalbergia nigra.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roseworm', 'n.', 'The larva of any one of several species of lepidopterous\n   insects which feed upon the leaves, buds, or blossoms of the rose,\n   especially Cacaecia rosaceana, which rolls up the leaves for a nest,\n   and devours both the leaves and buds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rosewort', 'n.', 'Roseroot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rosewort', 'n.', 'Any plant nearly related to the rose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rosicrucian', 'n.', 'One who, in the 17th century and the early part of the\n   18th, claimed to belong to a secret society of philosophers deeply\n   versed in the secrets of nature, -- the alleged society having existed,\n   it was stated, several hundred years.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rosicrucian', 'a.', 'Of or pertaining to the Rosicrucians, or their arts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rosied', 'a.', 'Decorated with roses, or with the color of roses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rosier', 'n.', 'A rosebush; roses, collectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rosily', 'adv.', 'In a rosy manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rosin', 'n.', 'The hard, amber-colored resin left after distilling off the\n   volatile oil of turpentine; colophony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rosin', 'v. t.', 'To rub with rosin, as musicians rub the bow of a violin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rosiness', 'n.', 'The quality of being rosy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rosinweed', 'n.', 'The compass plant. See under Compass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rosinweed', 'n.', 'A name given in California to various composite plants\n   which secrete resins or have a resinous smell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rosiny', 'a.', 'like rosin, or having its qualities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rosland', 'n.', 'heathy land; land full of heather; moorish or watery land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rosmarine', 'n.', 'Dew from the sea; sea dew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rosmarine', 'n.', 'Rosemary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rosmarine', 'n.', 'A fabulous sea animal which was reported to climb by\n   means of its teeth to the tops of rocks to feed upon the dew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rosolic', 'a.', 'Pertaining to, or designating, a complex red dyestuff\n   (called rosolic acid) which is analogous to rosaniline and aurin. It is\n   produced by oxidizing a mixture of phenol and cresol, as a dark red\n   amorphous mass, C20H16O3, which forms weak salts with bases, and stable\n   ones with acids. Called also methyl aurin, and, formerly, corallin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ross', 'n.', 'The rough, scaly matter on the surface of the bark of trees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ross', 'v. t.', 'To divest of the ross, or rough, scaly surface; as, to\n   ross bark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rossel', 'n.', 'Light land; rosland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rossel current', '', 'A portion of the southern equatorial current flowing\n   westward from the Fiji Islands to New Guinea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rosselly', 'a.', 'Loose; light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rost', 'n.', 'See Roust.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rostel', 'n.', 'same as Rostellum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rostellar', 'a.', 'Pertaining to a rostellum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rostellate', 'a.', 'Having a rostellum, or small beak; terminating in a\n   beak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rostelliform', 'a.', 'Having the form of a rostellum, or small beak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rostella', 'pl. ', 'of Rostellum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rostellum', 'n.', 'A small beaklike process or extension of some part; a\n   small rostrum; as, the rostellum of the stigma of violets, or of the\n   operculum of many mosses; the rostellum on the head of a tapeworm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roster', 'n.', 'A register or roll showing the order in which officers,\n   enlisted men, companies, or regiments are called on to serve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rostra', 'n. pl.', 'See Rostrum, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rostral', 'a.', 'Of or pertaining to the beak or snout of an animal, or the\n   beak of a ship; resembling a rostrum, esp., the rostra at Rome, or\n   their decorations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rostrate', 'a.', 'Alt. of Rostrated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rostrated', 'a.', 'Having a process resembling the beak of a bird; beaked;\n   rostellate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rostrated', 'a.', 'Furnished or adorned with beaks; as, rostrated galleys.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rostrifera', 'n. pl.', 'A division of pectinibranchiate gastropods, having\n   the head prolonged into a snout which is not retractile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rostriform', 'a.', 'Having the form of a beak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rostrula', 'pl. ', 'of Rostrulum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rostrulum', 'n.', 'A little rostrum, or beak, as of an insect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rostra', 'pl. ', 'of Rostrum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rostrums', 'pl. ', 'of Rostrum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rostrum', 'n.', 'The beak or head of a ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rostrum', 'n.', 'The Beaks; the stage or platform in the forum where\n   orations, pleadings, funeral harangues, etc., were delivered; -- so\n   called because after the Latin war, it was adorned with the beaks of\n   captured vessels; later, applied also to other platforms erected in\n   Rome for the use of public orators.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rostrum', 'n.', 'Hence, a stage for public speaking; the pulpit or platform\n   occupied by an orator or public speaker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rostrum', 'n.', 'Any beaklike prolongation, esp. of the head of an animal,\n   as the beak of birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rostrum', 'n.', 'The beak, or sucking mouth parts, of Hemiptera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rostrum', 'n.', 'The snout of a gastropod mollusk. See Illust. of\n   Littorina.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rostrum', 'n.', 'The anterior, often spinelike, prolongation of the\n   carapace of a crustacean, as in the lobster and the prawn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rostrum', 'n.', 'Same as Rostellum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rostrum', 'n.', 'The pipe to convey the distilling liquor into its receiver\n   in the common alembic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rostrum', 'n.', 'A pair of forceps of various kinds, having a beaklike\n   form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rosulate', 'a.', 'Arranged in little roselike clusters; -- said of leaves\n   and bracts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rosy', 'superl.', 'Resembling a rose in color, form, or qualities;\n   blooming; red; blushing; also, adorned with roses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rotted', 'imp. & p. p.', 'of Rot');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rotting', 'p. pr. & vb. n.', 'of Rot');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rot', 'v. i.', 'To undergo a process common to organic substances by which\n   they lose the cohesion of their parts and pass through certain chemical\n   changes, giving off usually in some stages of the process more or less\n   offensive odors; to become decomposed by a natural process; to putrefy;\n   to decay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rot', 'v. i.', 'Figuratively: To perish slowly; to decay; to die; to become\n   corrupt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rot', 'v. t.', 'To make putrid; to cause to be wholly or partially\n   decomposed by natural processes; as, to rot vegetable fiber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rot', 'v. t.', 'To expose, as flax, to a process of maceration, etc., for\n   the purpose of separating the fiber; to ret.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rot', 'n.', 'Process of rotting; decay; putrefaction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rot', 'n.', 'A disease or decay in fruits, leaves, or wood, supposed to be\n   caused by minute fungi. See Bitter rot, Black rot, etc., below.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rot', 'n.', 'A fatal distemper which attacks sheep and sometimes other\n   animals. It is due to the presence of a parasitic worm in the liver or\n   gall bladder. See 1st Fluke, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rota', 'n.', 'An ecclesiastical court of Rome, called also Rota Romana,\n   that takes cognizance of suits by appeal. It consists of twelve\n   members.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rota', 'n.', 'A short-lived political club established in 1659 by\n   J.Harrington to inculcate the democratic doctrine of election of the\n   principal officers of the state by ballot, and the annual retirement of\n   a portion of Parliament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rota', 'n.', 'A species of zither, played like a guitar, used in the Middle\n   Ages in church music; -- written also rotta.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rotacism', 'n.', 'See Rhotacism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rotal', 'a.', 'Relating to wheels or to rotary motion; rotary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rotalite', 'n.', 'Any fossil foraminifer of the genus Rotalia, abundant in\n   the chalk formation. See Illust. under Rhizopod.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rotary', 'a.', 'Turning, as a wheel on its axis; pertaining to, or\n   resembling, the motion of a wheel on its axis; rotatory; as, rotary\n   motion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rotascope', 'n.', 'Same as Gyroscope, 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rotate', 'a.', 'Having the parts spreading out like a wheel; wheel-shaped;\n   as, a rotate spicule or scale; a rotate corolla, i.e., a monopetalous\n   corolla with a flattish border, and no tube or a very short one.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rotated', 'imp. & p. p.', 'of Rotate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rotating', 'p. pr. & vb. n.', 'of Rotate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rotate', 'v. i.', 'To turn, as a wheel, round an axis; to revolve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rotate', 'v. i.', 'To perform any act, function, or operation in turn, to\n   hold office in turn; as, to rotate in office.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rotate', 'v. i.', 'To cause to turn round or revolve, as a wheel around an\n   axle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rotate', 'v. i.', 'To cause to succeed in turn; esp., to cause to succeed\n   some one, or to be succeeded by some one, in office.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rotated', 'a.', 'Turned round, as a wheel; also, wheel-shaped; rotate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rotation', 'n.', 'The act of turning, as a wheel or a solid body on its\n   axis, as distinguished from the progressive motion of a revolving round\n   another body or a distant point; thus, the daily turning of the earth\n   on its axis is a rotation; its annual motion round the sun is a\n   revolution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rotation', 'n.', 'Any return or succesion in a series.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rotation', 'a.', 'Pertaining to, or resulting from, rotation; of the nature\n   of, or characterized by, rotation; as, rotational velocity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rotative', 'a.', 'turning, as a wheel; rotary; rotational.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rotator', 'n.', 'that which gives a rotary or rolling motion, as a muscle\n   which partially rotates or turns some part on its axis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rotator', 'n.', 'A revolving reverberatory furnace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rotatoria', 'n. pl.', 'Same as Rotifera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rotatory', 'a.', 'Turning as on an axis; rotary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rotatory', 'a.', 'Going in a circle; following in rotation or succession;\n   as, rotatory assembles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rotatory', 'a.', 'Producing rotation of the plane of polarization; as, the\n   rotatory power of bodies on light. See the Note under polarization.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rotatory', 'n.', 'A rotifer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rotche', 'n.', 'A very small arctic sea bird (Mergulus alle, or Alle alle)\n   common on both coasts of the Atlantic in winter; -- called also little\n   auk, dovekie, rotch, rotchie, and sea dove.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rotchet', 'n.', 'The European red gurnard (Trigla pini).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rote', 'n.', 'A root.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rote', 'n.', 'A kind of guitar, the notes of which were produced by a small\n   wheel or wheel-like arrangement; an instrument similar to the\n   hurdy-gurdy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rote', 'n.', 'The noise produced by the surf of the sea dashing upon the\n   shore. See Rut.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rote', 'n.', 'A frequent repetition of forms of speech without attention to\n   the meaning; mere repetition; as, to learn rules by rote.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roted', 'imp. & p. p.', 'of Rote');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roting', 'p. pr. & vb. n.', 'of Rote');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rote', 'v. t.', 'To learn or repeat by rote.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rote', 'v. i.', 'To go out by rotation or succession; to rotate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rotella', 'n.', 'Any one of numerous species of small, polished,\n   brightcolored gastropods of the genus Rotella, native of tropical seas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rotgut', 'n.', 'Bad small beer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rotgut', 'n.', 'Any bad spirituous liquor, especially when adulterated so\n   as to be very deleterious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rother', 'a.', 'Bovine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rother', 'n.', 'A bovine beast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rother', 'n.', 'A rudder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rotifer', 'n.', 'One of the Rotifera. See Illust. in Appendix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rotifera', 'n.', 'An order of minute worms which usually have one or two\n   groups of vibrating cilia on the head, which, when in motion, often\n   give an appearance of rapidly revolving wheels. The species are very\n   numerous in fresh waters, and are very diversified in form and habits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rotiform', 'a.', 'Wheel-shaped; as, rotiform appendages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rotiform', 'a.', 'Same as Rotate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rotta', 'n.', 'See Rota.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rotten', 'a.', 'Having rotted; putrid; decayed; as, a rotten apple; rotten\n   meat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rotten', 'a.', 'Offensive to the smell; fetid; disgusting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rotten', 'a.', 'Not firm or trusty; unsound; defective; treacherous;\n   unsafe; as, a rotten plank, bone, stone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rotula', 'n.', 'The patella, or kneepan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rotular', 'a.', 'Of or pertaining to the rotula, or kneepan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rotund', 'a.', 'Round; circular; spherical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rotund', 'a.', 'Hence, complete; entire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rotund', 'a.', 'Orbicular, or nearly so.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rotund', 'n.', 'A rotunda.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rotunda', 'a.', 'A round building; especially, one that is round both on\n   the outside and inside, like the Pantheon at Rome. Less properly, but\n   very commonly, used for a large round room; as, the rotunda of the\n   Capitol at Washington.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rotundate', 'a.', 'Rounded; especially, rounded at the end or ends, or at\n   the corners.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rotundifolious', 'a.', 'Having round leaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rotundity', 'n.', 'The state or quality of being rotu/; roundness;\n   sphericity; circularity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rotundity', 'n.', 'Hence, completeness; entirety; roundness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rotundness', 'n.', 'Roundness; rotundity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rotundo', 'n.', 'See Rotunda.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roturer', 'n.', 'A roturier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roturier', 'n.', 'A person who is not of noble birth; specif., a freeman\n   who during the prevalence of feudalism held allodial land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roty', 'v. t.', 'To make rotten.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rouble', 'n.', 'A coin. See Ruble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rouche', 'n.', 'See Ruche.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roue', 'n.', 'One devoted to a life of sensual pleasure; a debauchee; a\n   rake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rouet', 'n.', 'A small wheel formerly fixed to the pan of firelocks for\n   discharging them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rouge', 'a.', 'red.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rouge', 'n.', 'A red amorphous powder consisting of ferric oxide. It is\n   used in polishing glass, metal, or gems, and as a cosmetic, etc. Called\n   also crocus, jewelers rouge, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rouge', 'n.', 'A cosmetic used for giving a red color to the cheeks or\n   lips. The best is prepared from the dried flowers of the safflower, but\n   it is often made from carmine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rouged', 'imp. & p. p.', 'of Rouge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rouging', 'p. pr. & vb. n.', 'of Rouge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rouge', 'v. i.', 'To paint the face or cheeks with rouge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rouge', 'v. t.', 'To tint with rouge; as, to rouge the face or the cheeks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rougecroix', 'n.', 'One of the four pursuivants of the English college of\n   arms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rouge dragon', 'n.', 'One of the four pursuivants of the English college of\n   arms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rough', 'n.', 'Having inequalities, small ridges, or points, on the\n   surface; not smooth or plain; as, a rough board; a rough stone; rough\n   cloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rough', 'n.', 'Not level; having a broken surface; uneven; -- said of a\n   piece of land, or of a road.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rough', 'n.', 'Not polished; uncut; -- said of a gem; as, a rough diamond.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rough', 'n.', 'Tossed in waves; boisterous; high; -- said of a sea or other\n   piece of water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rough', 'n.', 'Marked by coarseness; shaggy; ragged; disordered; -- said of\n   dress, appearance, or the like; as, a rough coat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rough', 'n.', 'Hence, figuratively, lacking refinement, gentleness, or\n   polish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rough', 'n.', 'Not courteous or kind; harsh; rude; uncivil; as, a rough\n   temper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rough', 'n.', 'Marked by severity or violence; harsh; hard; as, rough\n   measures or actions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rough', 'n.', 'Loud and hoarse; offensive to the ear; harsh; grating; --\n   said of sound, voice, and the like; as, a rough tone; rough numbers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rough', 'n.', 'Austere; harsh to the taste; as, rough wine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rough', 'n.', 'Tempestuous; boisterous; stormy; as, rough weather; a rough\n   day.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rough', 'n.', 'Hastily or carelessly done; wanting finish; incomplete; as,\n   a rough estimate; a rough draught.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rough', 'n.', 'Produced offhand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rough', 'n.', 'Boisterous weather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rough', 'n.', 'A rude fellow; a coarse bully; a rowdy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rough', 'adv.', 'In a rough manner; rudely; roughly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rough', 'v. t.', 'To render rough; to roughen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rough', 'v. t.', 'To break in, as a horse, especially for military\n   purposes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rough', 'v. t.', 'To cut or make in a hasty, rough manner; -- with out; as,\n   to rough out a carving, a sketch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roughcast', 'v. t.', 'To form in its first rudiments, without revision,\n   correction, or polish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roughcast', 'v. t.', 'To mold without nicety or elegance; to form with\n   asperities and inequalities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roughcast', 'v. t.', 'To plaster with a mixture of lime and shells or\n   pebbles; as, to roughcast a building.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roughcast', 'n.', 'A rude model; the rudimentary, unfinished form of a\n   thing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roughcast', 'n.', 'A kind of plastering made of lime, with a mixture of\n   shells or pebbles, used for covering buildings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roughcaster', 'n.', 'One who roughcasts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roughdraw', 'v. t.', 'To draw or delineate rapidly and by way of a first\n   sketch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roughdry', 'v. t.', 'in laundry work, to dry without smoothing or ironing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roughened', 'imp. & p. p.', 'of Roughen');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roughening', 'p. pr. & vb. n.', 'of Roughen');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roughen', 'v. t.', 'To make rough.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roughen', 'v. i.', 'To grow or become rough.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rough-footed', 'a.', 'Feather-footed; as, a rough-footed dove.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rough-grained', 'a.', 'Having a rough grain or fiber; hence, figuratively,\n   having coarse traits of character; not polished; brisque.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roughhead', 'n.', 'The redfin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roughhew', 'v. t.', 'To hew coarsely, without smoothing; as, to roughhew\n   timber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roughhew', 'v. t.', 'To give the first form or shape to; to form rudely; to\n   shape approximately and rudely; to roughcast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roughhewer', 'n.', 'One who roughhews.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roughhewn', 'a.', 'Hewn coarsely without smoothing; unfinished; not\n   polished.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roughhewn', 'a.', 'Of coarse manners; rude; uncultivated; rough-grained.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roughing-in', 'n.', 'The first coat of plaster laid on brick; also, the\n   process of applying it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roughings', 'n. pl.', 'Rowen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roughish', 'a.', 'Somewhat rough.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roughleg', 'n.', 'Any one of several species of large hawks of the genus\n   Archibuteo, having the legs feathered to the toes. Called also\n   rough-legged hawk, and rough-legged buzzard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rough-legged', 'a.', 'Having the legs covered with feathers; -- said of a\n   bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roughly', 'adv.', 'In a rough manner; unevenly; harshly; rudely; severely;\n   austerely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roughness', 'n.', 'The quality or state of being rough.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roughrider', 'n.', 'One who breaks horses; especially (Mil.), a\n   noncommissioned officer in the British cavalry, whose duty is to assist\n   the riding master.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roughscuff', 'n.', 'A rough, coarse fellow; collectively, the lowest class\n   of the people; the rabble; the riffraff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roughsetter', 'n.', 'A mason who builds rough stonework.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roughshod', 'a.', 'Shod with shoes armed with points or calks; as, a\n   roughshod horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roughstrings', 'n. pl.', 'Pieces of undressed timber put under the steps of\n   a wooden stair for their support.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rought', '', 'imp. of Reach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rought', '', 'imp. of Reck, to care.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roughtail', 'n.', 'Any species of small ground snakes of the family\n   Uropeltidae; -- so called from their rough tails.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roughwork', 'v. t.', 'To work over coarsely, without regard to nicety,\n   smoothness, or finish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roughwrought', 'a.', 'Wrought in a rough, unfinished way; worked over\n   coarsely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rouk', 'v. i.', 'See 5th Ruck, and Roke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roulade', 'n.', 'A smoothly running passage of short notes (as semiquavers,\n   or sixteenths) uniformly grouped, sung upon one long syllable, as in\n   Handels oratorios.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rouleaux', 'pl. ', 'of Rouleau');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rouleaus', 'pl. ', 'of Rouleau');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rouleau', 'n.', 'A little roll; a roll of coins put up in paper, or\n   something resembling such a roll.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roulette', 'n.', 'A game of chance, in which a small ball is made to move\n   round rapidly on a circle divided off into numbered red and black\n   spaces, the one on which it stops indicating the result of a variety of\n   wagers permitted by the game.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roulette', 'n.', 'A small toothed wheel used by engravers to roll over a\n   plate in order to order to produce rows of dots.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roulette', 'n.', 'A similar wheel used to roughen the surface of a plate,\n   as in making alterations in a mezzotint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roulette', 'n.', 'the curve traced by any point in the plane of a given\n   curve when the latter rolls, without sliding, over another fixed curve.\n   See Cycloid, and Epycycloid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rouly-pouly', 'n.', 'See Rolly-pooly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roun', 'v. i. & t.', 'Alt. of Rown');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rown', 'v. i. & t.', 'To whisper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rounce', 'n.', 'The handle by which the bed of a hand press, holding the\n   form of type, etc., is run in under the platen and out again; --\n   sometimes applied to the whole apparatus by which the form is moved\n   under the platen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rounceval', 'a.', 'Large; strong; -- from the gigantic bones shown at\n   Roncesvalles, and alleged to be those of old heroes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rounceval', 'n.', 'A giant; anything large; a kind of pea called also\n   marrowfat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rouncy', 'n.', 'A common hackney horse; a nag.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Round', 'v. i. & t.', 'To whisper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Round', 'a.', 'Having every portion of the surface or of the circumference\n   equally distant from the center; spherical; circular; having a form\n   approaching a spherical or a circular shape; orbicular; globular; as, a\n   round ball.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Round', 'a.', 'Having the form of a cylinder; cylindrical; as, the barrel\n   of a musket is round.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Round', 'a.', 'Having a curved outline or form; especially, one like the\n   arc of a circle or an ellipse, or a portion of the surface of a sphere;\n   rotund; bulging; protuberant; not angular or pointed; as, a round arch;\n   round hills.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Round', 'a.', 'Full; complete; not broken; not fractional; approximately in\n   even units, tens, hundreds, thousands, etc.; -- said of numbers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Round', 'a.', 'Not inconsiderable; large; hence, generous; free; as, a\n   round price.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Round', 'a.', 'Uttered or emitted with a full tone; as, a round voice; a\n   round note.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Round', 'a.', 'Modified, as a vowel, by contraction of the lip opening,\n   making the opening more or less round in shape; rounded; labialized;\n   labial. See Guide to Pronunciation, / 11.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Round', 'a.', 'Outspoken; plain and direct; unreserved; unqualified; not\n   mincing; as, a round answer; a round oath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Round', 'a.', 'Full and smoothly expanded; not defective or abrupt;\n   finished; polished; -- said of style, or of authors with reference to\n   their style.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Round', 'a.', 'Complete and consistent; fair; just; -- applied to conduct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Round', 'n.', 'Anything round, as a circle, a globe, a ring. \"The golden\n   round\" [the crown].');
  INSERT INTO words (word, word_type, description) VALUES  (    'Round', 'n.', 'A series of changes or events ending where it began; a\n   series of like events recurring in continuance; a cycle; a periodical\n   revolution; as, the round of the seasons; a round of pleasures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Round', 'n.', 'A course of action or conduct performed by a number of\n   persons in turn, or one after another, as if seated in a circle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Round', 'n.', 'A series of duties or tasks which must be performed in turn,\n   and then repeated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Round', 'n.', 'A circular dance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Round', 'n.', 'That which goes round a whole circle or company; as, a round\n   of applause.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Round', 'n.', 'Rotation, as in office; succession.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Round', 'n.', 'The step of a ladder; a rundle or rung; also, a crosspiece\n   which joins and braces the legs of a chair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Round', 'n.', 'A course ending where it began; a circuit; a beat;\n   especially, one freguently or regulary traversed; also, the act of\n   traversing a circuit; as, a watchmans round; the rounds of the\n   postman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Round', 'n.', 'A walk performed by a guard or an officer round the rampart\n   of a garrison, or among sentinels, to see that the sentinels are\n   faithful and all things safe; also, the guard or officer, with his\n   attendants, who performs this duty; -- usually in the plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Round', 'n.', 'A general discharge of firearms by a body of troops in which\n   each soldier fires once.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Round', 'n.', 'Ammunition for discharging a piece or pieces once; as,\n   twenty rounds of ammunition were given out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Round', 'n.', 'A short vocal piece, resembling a catch in which three or\n   four voices follow each other round in a species of canon in the\n   unison.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Round', 'n.', 'The time during which prize fighters or boxers are in actual\n   contest without an intermission, as prescribed by their rules; a bout.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Round', 'n.', 'A brewers vessel in which the fermentation is concluded,\n   the yeast escaping through the bunghole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Round', 'n.', 'A vessel filled, as for drinking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Round', 'n.', 'An assembly; a group; a circle; as, a round of politicians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Round', 'n.', 'See Roundtop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Round', 'n.', 'Same as Round of beef, below.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Round', 'adv.', 'On all sides; around.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Round', 'adv.', 'Circularly; in a circular form or manner; by revolving or\n   reversing ones position; as, to turn ones head round; a wheel turns\n   round.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Round', 'adv.', 'In circumference; as, a ball is ten inches round.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Round', 'adv.', 'From one side or party to another; as to come or turn\n   round, -- that is, to change sides or opinions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Round', 'adv.', 'By or in a circuit; by a course longer than the direct\n   course; back to the starting point.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Round', 'adv.', 'Through a circle, as of friends or houses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Round', 'adv.', 'Roundly; fully; vigorously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Round', 'prep.', 'On every side of, so as to encompass or encircle; around;\n   about; as, the people atood round him; to go round the city; to wind a\n   cable round a windlass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rounded', 'imp. & p. p.', 'of Round');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rounding', 'p. pr. & vb. n.', 'of Round');
  INSERT INTO words (word, word_type, description) VALUES  (    'Round', 'v. t.', 'To make circular, spherical, or cylindrical; to give a\n   round or convex figure to; as, to round a silver coin; to round the\n   edges of anything.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Round', 'v. t.', 'To surround; to encircle; to encompass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Round', 'v. t.', 'To bring to fullness or completeness; to complete; hence,\n   to bring to a fit conclusion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Round', 'v. t.', 'To go round wholly or in part; to go about (a corner or\n   point); as, to round a corner; to round Cape Horn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Round', 'v. t.', 'To make full, smooth, and flowing; as, to round periods\n   in writing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Round', 'v. i.', 'To grow round or full; hence, to attain to fullness,\n   completeness, or perfection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Round', 'v. i.', 'To go round, as a guard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Round', 'v. i.', 'To go or turn round; to wheel about.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roundabout', 'a.', 'Circuitous; going round; indirect; as, roundabout\n   speech.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roundabout', 'a.', 'Encircling; enveloping; comprehensive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roundabout', 'n.', 'A horizontal wheel or frame, commonly with wooden\n   horses, etc., on which children ride; a merry-go-round.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roundabout', 'n.', 'A dance performed in a circle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roundabout', 'n.', 'A short, close jacket worn by boys, sailors, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roundabout', 'n.', 'A state or scene of constant change, or of recurring\n   labor and vicissitude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roundaboutness', 'n.', 'The quality of being roundabout; circuitousness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Round-arm', 'a.', 'Applied to the method delivering the ball in bowling, by\n   swinging the arm horizontally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Round-backed', 'a.', 'Having a round back or shoulders; round-shouldered.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rounded', 'a.', 'Modified by contraction of the lip opening; labialized;\n   labial. See Guide to Pronunciation, / 11.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roundel', 'a.', 'A rondelay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roundel', 'a.', 'Anything having a round form; a round figure; a circle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roundel', 'a.', 'A small circular shield, sometimes not more than a foot in\n   diameter, used by soldiers in the fourteenth and fifteenth centuries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roundel', 'a.', 'A circular spot; a sharge in the form of a small circle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roundel', 'a.', 'A bastion of a circular form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roundelay', 'n.', 'See Rondeau, and Rondel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roundelay', 'n.', 'A tune in which a simple strain is often repeated; a\n   simple rural strain which is short and lively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roundelay', 'n.', 'A dance in a circle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roundelay', 'n.', 'Anything having a round form; a roundel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rounder', 'n.', 'One who rounds; one who comes about frequently or\n   regularly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rounder', 'n.', 'A tool for making an edge or surface round.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rounder', 'n.', 'An English game somewhat resembling baseball; also,\n   another English game resembling the game of fives, but played with a\n   football.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roundfish', 'n.', 'Any ordinary market fish, exclusive of flounders, sole,\n   halibut, and other flatfishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roundfish', 'n.', 'A lake whitefish (Coregonus quadrilateralis), less\n   compressed than the common species. It is very abundant in British\n   America and Alaska.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roundhead', 'n.', 'A nickname for a Puritan. See Roundheads, the, in the\n   Dictionary of Noted Names in Fiction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roundheaded', 'a.', 'Having a round head or top.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roundhouse', 'n.', 'A constables prison; a lockup, watch-house, or station\n   house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roundhouse', 'n.', 'A cabin or apartament on the after part of the\n   quarter-deck, having the poop for its roof; -- sometimes called the\n   coach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roundhouse', 'n.', 'A privy near the bow of the vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roundhouse', 'n.', 'A house for locomotive engines, built circularly around\n   a turntable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rounding', 'a.', 'Round or nearly round; becoming round; roundish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rounding', 'n.', 'Small rope, or strands of rope, or spun yarn, wound round\n   a rope to keep it from chafing; -- called also service.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rounding', 'n.', 'Modifying a speech sound by contraction of the lip\n   opening; labializing; labialization. See Guide to Pronunciation, / 11.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roundish', 'a.', 'Somewhat round; as, a roundish seed; a roundish figure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roundlet', 'n.', 'A little circle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roundly', 'adv.', 'In a round form or manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roundly', 'adv.', 'Openly; boldly; peremptorily; plumply.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roundly', 'adv.', 'Briskly; with speed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roundly', 'adv.', 'Completely; vigorously; in earnest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roundly', 'adv.', 'Without regard to detail; in gross; comprehensively;\n   generally; as, to give numbers roundly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roundness', 'n.', 'The quality or state of being round in shape; as, the\n   roundness of the globe, of the orb of the sun, of a ball, of a bowl, a\n   column, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roundness', 'n.', 'Fullness; smoothness of flow; as, the roundness of a\n   period; the roundness of a note; roundness of tone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roundness', 'n.', 'Openess; plainess; boldness; positiveness; as, the\n   roundness of an assertion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roundridge', 'v. t.', 'To form into round ridges by plowing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Round-shouldered', 'a.', 'Having the shoulders stooping or projecting;\n   round-backed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roundsmen', 'pl. ', 'of Roundsman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roundsman', 'n.', 'A patrolman; also, a policeman who acts as an inspector\n   over the rounds of the patrolmen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roundtop', 'n.', 'A top; a platform at a masthead; -- so called because\n   formerly round in shape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Round-up', 'n.', 'The act of collecting or gathering together scattered\n   cattle by riding around them and driving them in.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roundure', 'n.', 'Roundness; a round or circle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roundworm', 'n.', 'A nematoid worm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roundy', 'a.', 'Round.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roup', 'v. i. & t.', 'To cry or shout; hence, to sell by auction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roup', 'n.', 'An outcry; hence, a sale of gods by auction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roup', 'n.', 'A disease in poultry. See Pip.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rousant', 'a.', 'Rising; -- applied to a bird in the attitude of rising;\n   also, sometmes, to a bird in profile with wings addorsed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rouse', 'v. i. & t.', 'To pull or haul strongly and all together, as upon a\n   rope, without the assistance of mechanical appliances.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rouse', 'n.', 'A bumper in honor of a toast or health.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rouse', 'n.', 'A carousal; a festival; a drinking frolic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roused', 'imp. & p. p.', 'of Rouse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rousing', 'p. pr. & vb. n.', 'of Rouse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rouse', 'v.', 'To cause to start from a covert or lurking place; as, to\n   rouse a deer or other animal of the chase.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rouse', 'v.', 'To wake from sleep or repose; as, to rouse one early or\n   suddenly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rouse', 'v.', 'To excite to lively thought or action from a state of\n   idleness, languor, stupidity, or indifference; as, to rouse the\n   faculties, passions, or emotions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rouse', 'v.', 'To put in motion; to stir up; to agitate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rouse', 'v.', 'To raise; to make erect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rouse', 'v. i.', 'To get or start up; to rise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rouse', 'v. i.', 'To awake from sleep or repose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rouse', 'v. i.', 'To be exited to thought or action from a state of\n   indolence or inattention.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rouser', 'n.', 'One who, or that which, rouses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rouser', 'n.', 'Something very exciting or great.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rouser', 'n.', 'A stirrer in a copper for boiling wort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rousing', 'a.', 'Having power to awaken or excite; exciting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rousing', 'a.', 'Very great; violent; astounding; as, a rousing fire; a\n   rousing lie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rousingly', 'adv.', 'In a rousing manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roussette', 'n.', 'A fruit bat, especially the large species (Pieropus\n   vulgaris) inhabiting the islands of the Indian ocean. It measures about\n   a yard across the expanded wings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roussette', 'n.', 'Any small shark of the genus Scyllium; -- called also\n   dogfish. See Dogfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roust', 'v. t.', 'To rouse; to disturb; as, to roust one out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roust', 'n.', 'A strong tide or current, especially in a narrow channel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roustabout', 'n.', 'A laborer, especially a deck hand, on a river\n   steamboat, who moves the cargo, loads and unloads wood, and the like;\n   in an opprobrious sense, a shiftless vagrant who lives by chance jobs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rout', 'v. i.', 'To roar; to bellow; to snort; to snore loudly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rout', 'n.', 'A bellowing; a shouting; noise; clamor; uproar; disturbance;\n   tumult.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rout', 'v. t.', 'To scoop out with a gouge or other tool; to furrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rout', 'v. i.', 'To search or root in the ground, as a swine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rout', 'n.', 'A troop; a throng; a company; an assembly; especially, a\n   traveling company or throng.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rout', 'n.', 'A disorderly and tumultuous crowd; a mob; hence, the rabble;\n   the herd of common people.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rout', 'n.', 'The state of being disorganized and thrown into confusion; --\n   said especially of an army defeated, broken in pieces, and put to\n   flight in disorder or panic; also, the act of defeating and breaking up\n   an army; as, the rout of the enemy was complete.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rout', 'n.', 'A disturbance of the peace by persons assembled together with\n   intent to do a thing which, if executed, would make them rioters, and\n   actually making a motion toward the executing thereof.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rout', 'n.', 'A fashionable assembly, or large evening party.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Routed', 'imp. & p. p.', 'of Rout');
  INSERT INTO words (word, word_type, description) VALUES  (    'Routing', 'p. pr. & vb. n.', 'of Rout');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rout', 'v. t.', 'To break the ranks of, as troops, and put them to flight\n   in disorder; to put to rout.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rout', 'v. i.', 'To assemble in a crowd, whether orderly or disorderly; to\n   collect in company.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Route', 'n.', 'The course or way which is traveled or passed, or is to be\n   passed; a passing; a course; a road or path; a march.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Router', 'n.', 'A plane made like a spokeshave, for working the inside\n   edges of circular sashes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Router', 'n.', 'A plane with a hooked tool protruding far below the sole,\n   for smoothing the bottom of a cavity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Routhe', 'n.', 'Ruth; sorrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Routinary', 'a.', 'Involving, or pertaining to, routine; ordinary;\n   customary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Routine', 'n.', 'A round of business, amusement, or pleasure, daily or\n   frequently pursued; especially, a course of business or offical duties\n   regularly or frequently returning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Routine', 'n.', 'Any regular course of action or procedure rigidly adhered\n   to by the mere force of habit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Routinism', 'n.', 'the practice of doing things with undiscriminating,\n   mechanical regularity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Routinist', 'n.', 'One who habituated to a routine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Routish', 'a.', 'Uproarious; riotous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Routously', 'adv.', 'With that violation of law called a rout. See 5th\n   Rout, 4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roux', 'n.', 'A thickening, made of flour, for soups and gravies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rove', 'v. t.', 'To draw through an eye or aperture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rove', 'v. t.', 'To draw out into flakes; to card, as wool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rove', 'v. t.', 'To twist slightly; to bring together, as slivers of wool\n   or cotton, and twist slightly before spinning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rove', 'n.', 'A copper washer upon which the end of a nail is clinched in\n   boat building.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rove', 'n.', 'A roll or sliver of wool or cotton drawn out and slighty\n   twisted, preparatory to further process; a roving.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roved', 'imp. & p. p.', 'of Rove');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roving', 'p. pr. & vb. n.', 'of Rove');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rove', 'v. i.', 'To practice robbery on the seas; to wander about on the\n   seas in piracy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rove', 'v. i.', 'Hence, to wander; to ramble; to rauge; to go, move, or\n   pass without certain direction in any manner, by sailing, walking,\n   riding, flying, or otherwise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rove', 'v. i.', 'To shoot at rovers; hence, to shoot at an angle of\n   elevation, not at point-blank (rovers usually being beyond the\n   point-blank range).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rove', 'v. t.', 'To wander over or through.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rove', 'v. t.', 'To plow into ridges by turning the earth of two furrows\n   together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rove', 'n.', 'The act of wandering; a ramble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rover', 'v. i.', 'One who practices robbery on the seas; a pirate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rover', 'v. i.', 'One who wanders about by sea or land; a wanderer; a\n   rambler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rover', 'v. i.', 'Hence, a fickle, inconstant person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rover', 'v. i.', 'A ball which has passed through all the hoops and would\n   go out if it hit the stake but is continued in play; also, the player\n   of such a ball.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rover', 'v. i.', 'Casual marks at uncertain distances.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rover', 'v. i.', 'A sort of arrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roving', 'n.', 'The operatin of forming the rove, or slightly twisted\n   sliver or roll of wool or cotton, by means of a machine for the\n   purpose, called a roving frame, or roving machine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roving', 'n.', 'A roll or sliver of wool or cotton drawn out and slightly\n   twisted; a rove. See 2d Rove, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roving', 'n.', 'The act of one who roves or wanders.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rovingly', 'adv.', 'In a wandering manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rovingness', 'n.', 'The state of roving.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Row', 'a. & adv.', 'Rough; stern; angry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Row', 'n.', 'A noisy, turbulent quarrel or disturbance; a brawl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Row', 'n.', 'A series of persons or things arranged in a continued line; a\n   line; a rank; a file; as, a row of trees; a row of houses or columns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rowed', 'imp. & p. p.', 'of Row');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rowing', 'p. pr. & vb. n.', 'of Row');
  INSERT INTO words (word, word_type, description) VALUES  (    'Row', 'v. t.', 'To propel with oars, as a boat or vessel, along the surface\n   of water; as, to row a boat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Row', 'v. t.', 'To transport in a boat propelled with oars; as, to row the\n   captain ashore in his barge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Row', 'v. i.', 'To use the oar; as, to row well.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Row', 'v. i.', 'To be moved by oars; as, the boat rows easily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Row', 'n.', 'The act of rowing; excursion in a rowboat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rowable', 'a.', 'That may be rowed, or rowed upon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rowan', 'n.', 'Rowan tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rowan tree', '', 'A european tree (Pyrus aucuparia) related to the apple,\n   but with pinnate leaves and flat corymbs of small white flowers\n   followed by little bright red berries. Called also roan tree, and\n   mountain ash. The name is also applied to two American trees of similar\n   habit (Pyrus Americana, and P. sambucifolia).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rowboat', 'n.', 'A boat designed to be propelled by oars instead of sails.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rowdies', 'pl. ', 'of Rowdy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rowdy', 'n.', 'One who engages in rows, or noisy quarrels; a ruffianly\n   fellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rowdydow', 'n.', 'Hubbub; uproar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rowdydowdy', 'a.', 'Uproarious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rowdyish', 'a.', 'Resembling a rowdy in temper or conduct; characteristic\n   of a rowdy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rowdyism', 'n.', 'the conduct of a rowdy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rowed', 'a.', 'Formed into a row, or rows; having a row, or rows; as, a\n   twelve-rowed ear of corn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rowel', 'n.', 'The little wheel of a spur, with sharp points.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rowel', 'n.', 'A little flat ring or wheel on horses bits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rowel', 'n.', 'A roll of hair, silk, etc., passed through the flesh of\n   horses, answering to a seton in human surgery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roweled', 'imp. & p. p.', 'of Rowel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rowelled', '', 'of Rowel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roweling', 'p. pr. & vb. n.', 'of Rowel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rowelling', '', 'of Rowel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rowel', 'v. t.', 'To insert a rowel, or roll of hair or silk, into (as the\n   flesh of a horse).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rowel bone', '', 'See rewel bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rowen', 'n.', 'A stubble field left unplowed till late in the autumn, that\n   it may be cropped by cattle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rowen', 'n.', 'The second growth of grass in a season; aftermath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rower', 'n.', 'One who rows with an oar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rowett', 'n.', 'See Rowen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rowlock', 'n.', 'A contrivance or arrangement serving as a fulcrum for an\n   oar in rowing. It consists sometimes of a notch in the gunwale of a\n   boat, sometimes of a pair of pins between which the oar rests on the\n   edge of the gunwale, sometimes of a single pin passing through the oar,\n   or of a metal fork or stirrup pivoted in the gunwale and suporting the\n   oar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rown', 'v. i. & t.', 'see Roun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rowport', 'n.', 'An opening in the side of small vessels of war, near the\n   surface of the water, to facilitate rowing in calm weather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roxburgh', 'n.', 'A style of bookbinding in which the back is plain\n   leather, the sides paper or cloth, the top gilt-edged, but the front\n   and bottom left uncut.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roy', 'n.', 'A king.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roy', 'a.', 'Royal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Royal', 'a.', 'Kingly; pertaining to the crown or the sovereign; suitable\n   for a king or queen; regal; as, royal power or prerogative; royal\n   domains; the royal family; royal state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Royal', 'a.', 'Noble; generous; magnificent; princely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Royal', 'a.', 'Under the patronage of royality; holding a charter granted\n   by the sovereign; as, the Royal Academy of Arts; the Royal Society.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Royal', 'n.', 'Printing and writing papers of particular sizes. See under\n   paper, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Royal', 'n.', 'A small sail immediately above the topgallant sail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Royal', 'n.', 'One of the upper or distal branches of an antler, as the\n   third and fourth tynes of the antlers of a stag.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Royal', 'n.', 'A small mortar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Royal', 'n.', 'One of the soldiers of the first regiment of foot of the\n   British army, formerly called the Royals, and supposed to be the oldest\n   regular corps in Europe; -- now called the Royal Scots.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Royal', 'n.', 'An old English coin. See Rial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Royalet', 'n.', 'A petty or powerless king.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Royalism', 'n.', 'the principles or conduct of royalists.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Royalist', 'n.', 'An adherent of a king (as of Charles I. in England, or of\n   the Bourbons in france); one attached to monarchical government.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Royalization', 'n.', 'The act of making loyal to a king.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Royalize', 'v. t.', 'to make royal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Royally', 'adv.', 'In a royal or kingly manner; like a king; as becomes a\n   king.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Royalties', 'pl. ', 'of Royalty');
  INSERT INTO words (word, word_type, description) VALUES  (    'Royalty', 'n.', 'The state of being royal; the condition or quality of a\n   royal person; kingship; kingly office; sovereignty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Royalty', 'n.', 'The person of a king or sovereign; majesty; as, in the\n   presence of royalty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Royalty', 'n.', 'An emblem of royalty; -- usually in the plural, meaning\n   regalia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Royalty', 'n.', 'Kingliness; spirit of regal authority.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Royalty', 'n.', 'Domain; province; sphere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Royalty', 'n.', 'That which is due to a sovereign, as a seigniorage on gold\n   and silver coined at the mint, metals taken from mines, etc.; the tax\n   exacted in lieu of such share; imperiality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Royalty', 'n.', 'A share of the product or profit (as of a mine, forest,\n   etc.), reserved by the owner for permitting another to use the\n   property.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Royalty', 'n.', 'Hence (Com.), a duty paid by a manufacturer to the owner\n   of a patent or a copyright at a certain rate for each article\n   manufactured; or, a percentage paid to the owner of an article by one\n   who hires the use of it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Royne', 'v. t.', 'To bite; to gnaw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roynish', 'a.', 'Mangy; scabby; hence, mean; paltry; troublesome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Royster', 'n.', 'Alt. of Roysterer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roysterer', 'n.', 'same as Roister, Roisterer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Royston crow', '', 'See Hooded crow, under Hooded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roytelet', 'n.', 'A little king.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Roytish', 'a.', 'Wild; irregular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rubbed', 'imp. & p. p.', 'of Rub');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rubbing', 'p. pr. & vb. n.', 'of Rub');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rub', 'v. t.', 'To subject (a body) to the action of something moving over\n   its surface with pressure and friction, especially to the action of\n   something moving back and forth; as, to rub the flesh with the hand; to\n   rub wood with sandpaper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rub', 'v. t.', 'To move over the surface of (a body) with pressure and\n   friction; to graze; to chafe; as, the boat rubs the ground.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rub', 'v. t.', 'To cause (a body) to move with pressure and friction along\n   a surface; as, to rub the hand over the body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rub', 'v. t.', 'To spread a substance thinly over; to smear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rub', 'v. t.', 'To scour; to burnish; to polish; to brighten; to cleanse;\n   -- often with up or over; as, to rub up silver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rub', 'v. t.', 'To hinder; to cross; to thwart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rub', 'v. i.', 'To move along the surface of a body with pressure; to\n   grate; as, a wheel rubs against the gatepost.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rub', 'v. i.', 'To fret; to chafe; as, to rub upon a sore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rub', 'v. i.', 'To move or pass with difficulty; as, to rub through woods,\n   as huntsmen; to rub through the world.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rub', 'n.', 'The act of rubbing; friction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rub', 'n.', 'That which rubs; that which tends to hinder or obstruct motion\n   or progress; hindrance; obstruction, an impediment; especially, a\n   difficulty or obstruction hard to overcome; a pinch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rub', 'n.', 'Inequality of surface, as of the ground in the game of bowls;\n   unevenness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rub', 'n.', 'Something grating to the feelings; sarcasm; joke; as, a hard\n   rub.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rub', 'n.', 'Imperfection; failing; fault.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rub', 'n.', 'A chance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rub', 'n.', 'A stone, commonly flat, used to sharpen cutting tools; a\n   whetstone; -- called also rubstone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruba-dub', 'n.', 'The sound of a drum when continuously beaten; hence, a\n   clamorous, repeated sound; a clatter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rubato', 'a.', 'Robbed; borrowed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rubbage', 'n.', 'Rubbish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rubber', 'n.', 'One who, or that which, rubs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rubber', 'n.', 'An instrument or thing used in rubbing, polishing, or\n   cleaning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rubber', 'n.', 'A coarse file, or the rough part of a file.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rubber', 'n.', 'A whetstone; a rubstone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rubber', 'n.', 'An eraser, usually made of caoutchouc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rubber', 'n.', 'The cushion of an electrical machine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rubber', 'n.', 'One who performs massage, especially in a Turkish bath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rubber', 'n.', 'Something that chafes or annoys; hence, something that\n   grates on the feelings; a sarcasm; a rub.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rubber', 'n.', 'In some games, as whist, the odd game, as the third or the\n   fifth, when there is a tie between the players; as, to play the rubber;\n   also, a contest determined by the winning of two out of three games;\n   as, to play a rubber of whist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rubber', 'n.', 'India rubber; caoutchouc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rubber', 'n.', 'An overshoe made of India rubber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rubbidge', 'n.', 'Rubbish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rubbing', '', 'a. & n. from Rub, v.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rubbish', 'n.', 'Waste or rejected matter; anything worthless; valueless\n   stuff; trash; especially, fragments of building materials or fallen\n   buildings; ruins; debris.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rubbish', 'a.', 'Of or pertaining to rubbish; of the quality of rubbish;\n   trashy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rubble', 'n.', 'Water-worn or rough broken stones; broken bricks, etc.,\n   used in coarse masonry, or to fill up between the facing courses of\n   walls.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rubble', 'n.', 'Rough stone as it comes from the quarry; also, a\n   quarrymans term for the upper fragmentary and decomposed portion of a\n   mass of stone; brash.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rubble', 'n.', 'A mass or stratum of fragments or rock lying under the\n   alluvium, and derived from the neighboring rock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rubble', 'n.', 'The whole of the bran of wheat before it is sorted into\n   pollard, bran, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rubblestone', 'n.', 'See Rubble, 1 and 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rubblework', 'n.', 'Masonry constructed of unsquared stones that are\n   irregular in size and shape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rubbly', 'a.', 'Relating to, or containing, rubble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rubedinous', 'a.', 'Reddish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rubefacient', 'a.', 'Making red.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rubefacient', 'n.', 'An external application which produces redness of the\n   skin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rubefaction', 'n.', 'The act or process of making red.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rubelet', 'n.', 'A little ruby.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rubella', 'n.', 'An acute specific disease with a dusky red cutaneous\n   eruption resembling that of measles, but unattended by catarrhal\n   symptoms; -- called also German measles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rubelle', 'n.', 'A red color used in enameling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rubellite', 'n.', 'A variety of tourmaline varying in color from a pale\n   rose to a deep ruby, and containing lithium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rubeola', 'n.', 'the measles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rubeola', 'n.', 'Rubella.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruberythrinic', 'a.', 'Pertaining to, or designating, an acid extracted\n   from madder root. It is a yellow crystalline substance from which\n   alizarin is obtained.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rubescence', 'n.', 'The quality or state of being rubescent; a reddening; a\n   flush.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rubescent', 'a.', 'Growing or becoming red; tending to redness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rubiaceous', 'a.', 'Of or pertaining to a very large natural order of\n   plants (Rubiaceae) named after the madder (Rubia tinctoria), and\n   including about three hundred and seventy genera and over four thousand\n   species. Among them are the coffee tree, the trees yielding peruvian\n   bark and quinine, the madder, the quaker ladies, and the trees bearing\n   the edible fruits called genipap and Sierre Leone peach, besides many\n   plants noted for the beauty or the fragrance of their blossoms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rubiacin', 'n.', 'A substance found in madder root, and probably identical\n   with ruberythrinic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rubian', 'n.', 'One of several color-producing glycosides found in madder\n   root.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rubianic', 'a.', 'pertaining to, or derived from, rubian; specifically,\n   designating an acid called also ruberythrinic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ru bible', 'n.', 'A ribble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rubican', 'a.', 'Colored a prevailing red, bay, or black, with flecks of\n   white or gray especially on the flanks; -- said of horses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rubicelle', 'n.', 'A variety of ruby of a yellowish red color, from Brazil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rubicon', 'n.', 'A small river which separated Italy from Cisalpine Gaul,\n   the province alloted to Julius Caesar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rubicund', 'a.', 'Inclining to redness; ruddy; red.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rubicundity', 'n.', 'The quality or state of being rubicund; ruddiness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rubidic', 'a.', 'Of or pertaining to rubidium; containing rubidium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rubidine', 'n.', 'A nitrogenous base homologous with pyridine, obtained\n   from coal tar as an oily liquid, C11H17N; also, any one of the group od\n   metameric compounds of which rubidine is the type.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rubidium', 'n.', 'A rare metallic element. It occurs quite widely, but in\n   small quantities, and always combined. It is isolated as a soft\n   yellowish white metal, analogous to potassium in most of its\n   properties. Symbol Rb. Atomic weight, 85.2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rubific', 'a.', 'Making red; as, rubific rays.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rubification', 'n.', 'The act of making red.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rubiform', 'a.', 'Having the nature or quality of red; as, the rubiform\n   rays of the sun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rubify', 'v. t.', 'To redden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rubiginose', 'a.', 'Alt. of Rubiginous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rubiginous', 'a.', 'Having the appearance or color of iron rust;\n   rusty-looking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rubigo', 'n.', 'same as Rust, n., 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rubin', 'n.', 'A ruby.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rubious', 'a.', 'Red; ruddy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rubiretin', 'n.', 'One of the red dye products extracted from madder root,\n   and probably identical with ruberythrinic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruble', 'n.', 'The unit of monetary value in Russia. It is divided into 100\n   copecks, and in the gold coin of the realm (as in the five and ten\n   ruble pieces) is worth about 77 cents. The silver ruble is a coin worth\n   about 60 cents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rubric', 'n.', 'That part of any work in the early manuscripts and\n   typography which was colored red, to distinguish it from other\n   portions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rubric', 'n.', 'A titlepage, or part of it, especially that giving the date\n   and place of printing; also, the initial letters, etc., when printed in\n   red.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rubric', 'n.', 'The title of a statute; -- so called as being anciently\n   written in red letters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rubric', 'n.', 'The directions and rules for the conduct of service,\n   formerly written or printed in red; hence, also, an ecclesiastical or\n   episcopal injunction; -- usually in the plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rubric', 'n.', 'Hence, that which is established or settled, as by\n   authority; a thing definitely settled or fixed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rubric', 'v. t.', 'To adorn ith red; to redden; to rubricate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rubric', 'a.', 'Alt. of Rubrical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rubrical', 'a.', 'Colored in, or marked with, red; placed in rubrics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rubrical', 'a.', 'Of or pertaining to the rubric or rubrics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rubricate', 'n.', 'Marked with red.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rubricate', 'v. t.', 'To mark or distinguished with red; to arrange as in a\n   rubric; to establish in a settled and unchangeable form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rubrician', 'n.', 'Alt. of Rubricist');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rubricist', 'n.', 'One skilled in, or tenaciously adhering to, the rubric\n   or rubrics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rubricity', 'n.', 'Redness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rubstone', 'n.', 'A stone for scouring or rubbing; a whetstone; a rub.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rubus', 'n.', 'A genus of rosaceous plants, including the raspberry and\n   blackberry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rubies', 'pl. ', 'of Ruby');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruby', 'n.', 'A precious stone of a carmine red color, sometimes verging to\n   violet, or intermediate between carmine and hyacinth red. It is a red\n   crystallized variety of corundum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruby', 'n.', 'The color of a ruby; carmine red; a red tint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruby', 'n.', 'That which has the color of the ruby, as red wine. Hence, a\n   red blain or carbuncle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruby', 'n.', 'See Agate, n., 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruby', 'n.', 'Any species of South American humming birds of the genus\n   Clytolaema. The males have a ruby-colored throat or breast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruby', 'a.', 'Ruby-colored; red; as, ruby lips.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rubied', 'imp. & p. p.', 'of Ruby');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rubying', 'p. pr. & vb. n.', 'of Ruby');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruby', 'v. t.', 'To make red; to redden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rubytail', 'n.', 'A European gold wasp (Chrysis ignita) which has the under\n   side of the abdomen bright red, and the other parts deep bluish green\n   with a metallic luster. The larva is parasitic in the nests of other\n   wasps and of bees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruby-tailed', 'a.', 'Having the tail, or lower part of the body, bright\n   red.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rubythroat', 'n.', 'Any one of numerous species of humming birds belonging\n   to Trochilus, Calypte, Stellula, and allies, in which the male has on\n   the throat a brilliant patch of red feathers having metallic\n   reflections; esp., the common humming bird of the Eastern United States\n   (Trochilus colubris).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rubywood', 'n.', 'red sandalwood. See under Sandalwood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rucervine', 'a.', 'Of, like, or pertaining to, a deer of the genus\n   Rucervus, which includes the swamp deer of India.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruche', 'n.', 'A plaited, quilled, or goffered strip of lace, net, ribbon,\n   or other material, -- used in place of collars or cuffs, and as a\n   trimming for womens dresses and bonnets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruche', 'n.', 'A pile of arched tiles, used to catch and retain oyster\n   spawn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruching', 'n.', 'A ruche, or ruches collectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruck', 'n.', 'A roc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rucked', 'imp. & p. p.', 'of Ruck');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rucking', 'p. pr. & vb. n.', 'of Ruck');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruck', 'v. t. & i.', 'To draw into wrinkles or unsightly folds; to crease;\n   as, to ruck up a carpet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruck', 'v. t.', 'A wrinkle or crease in a piece of cloth, or in needlework.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruck', 'v. i.', 'To cower; to huddle together; to squat; to sit, as a hen\n   on eggs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruck', 'n.', 'A heap; a rick.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruck', 'n.', 'The common sort, whether persons or things; as, the ruck in a\n   horse race.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ructation', 'n.', 'The act of belching wind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruction', 'n.', 'An uproar; a quarrel; a noisy outbreak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rud', 'n.', 'Redness; blush.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rud', 'n.', 'Ruddle; red ocher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rud', 'n.', 'The rudd.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rud', 'v. t.', 'To make red.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rudd', 'n.', 'A fresh-water European fish of the Carp family (Leuciscus\n   erythrophthalmus). It is about the size and shape of the roach, but it\n   has the dorsal fin farther back, a stouter body, and red irises. Called\n   also redeye, roud, finscale, and shallow. A blue variety is called\n   azurine, or blue roach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rudder', 'n.', 'A riddle or sieve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rudder', 'n.', 'The mechanical appliance by means of which a vessel is\n   guided or steered when in motion. It is a broad and flat blade made of\n   wood or iron, with a long shank, and is fastened in an upright\n   position, usually by one edge, to the sternpost of the vessel in such a\n   way that it can be turned from side to side in the water by means of a\n   tiller, wheel, or other attachment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rudder', 'n.', 'Fig.: That which resembles a rudder as a guide or governor;\n   that which guides or governs the course.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rudderhead', 'n.', 'The upper end of the rudderpost, to which the tiller is\n   attached.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rudderhole', 'n.', 'The hole in the deck through which the rudderpost\n   passes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rudderless', 'a.', 'Without a rudder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rudderpost', 'n.', 'The shank of a rudder, having the blade at one end and\n   the attachments for operating it at the other.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rudderstock', 'n.', 'The main part or blade of the rudder, which is\n   connected by hinges, or the like, with the sternpost of a vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruddied', 'a.', 'Made ruddy or red.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruddily', 'adv.', 'In a ruddy manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruddiness', 'n.', 'The quality or state of being ruddy; as, the ruddiness\n   of the cheeks or the sky.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruddle', 'v. t.', 'To raddle or twist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruddle', 'n.', 'A riddle or sieve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruddle', 'n.', 'A species of red earth colored by iron sesquioxide; red\n   ocher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruddle', 'v. t.', 'To mark with ruddle; to raddle; to rouge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruddock', 'n.', 'The European robin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruddock', 'n.', 'A piece of gold money; -- probably because the gold of\n   coins was often reddened by copper alloy. Called also red ruddock, and\n   golden ruddock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruddy', 'n.', 'Of a red color; red, or reddish; as, a ruddy sky; a ruddy\n   flame.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruddy', 'n.', 'Of a lively flesh color, or the color of the human skin in\n   high health; as, ruddy cheeks or lips.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruddy', 'v. t.', 'To make ruddy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rude', 'superl.', 'Characterized by roughness; umpolished; raw; lacking\n   delicacy or refinement; coarse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rude', 'superl.', 'Unformed by taste or skill; not nicely finished; not\n   smoothed or polished; -- said especially of material things; as, rude\n   workmanship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rude', 'superl.', 'Of untaught manners; unpolished; of low rank; uncivil;\n   clownish; ignorant; raw; unskillful; -- said of persons, or of conduct,\n   skill, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rude', 'superl.', 'Violent; tumultuous; boisterous; inclement; harsh;\n   severe; -- said of the weather, of storms, and the like; as, the rude\n   winter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rude', 'superl.', 'Barbarous; fierce; bloody; impetuous; -- said of war,\n   conflict, and the like; as, the rude shock of armies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rude', 'superl.', 'Not finished or complete; inelegant; lacking chasteness\n   or elegance; not in good taste; unsatisfactory in mode of treatment; --\n   said of literature, language, style, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rudenture', 'n.', 'Cabling. See Cabling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruderary', 'a.', 'Of or pertaining to rubbish..');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rudesby', 'n.', 'An uncivil, turbulent fellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rudesheimer', 'n.', 'A German wine made near Rudesheim, on the Rhine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rudiment', 'n.', 'That which is unformed or undeveloped; the principle\n   which lies at the bottom of any development; an unfinished beginning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rudiment', 'n.', 'Hence, an element or first principle of any art or\n   science; a beginning of any knowledge; a first step.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rudiment', 'n.', 'An imperfect organ or part, or one which is never\n   developed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rudiment', 'v. t.', 'To furnish with first principles or rules; to insrtuct\n   in the rudiments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rudimental', 'a.', 'Rudimentary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rudimentary', 'a.', 'Of or pertaining to rudiments; consisting in first\n   principles; elementary; initial; as, rudimental essays.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rudimentary', 'a.', 'Very imperfectly developed; in an early stage of\n   development; embryonic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rudish', 'a.', 'Somewhat rude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rudistes', 'n. pl.', 'An extinct order or suborder of bivalve mollusks\n   characteristic of the Cretaceous period; -- called also Rudista. See\n   Illust. under Hippurite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rudity', 'n.', 'Rudeness; ignorance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rudmasday', 'n.', 'Either of the feasts of the Holy Cross, occuring on May\n   3 and September 14, annually.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rudolphine', 'a.', 'Pertaining to, or designating, a set of astronomical\n   tables computed by Kepler, and founded on the observations of Tycho\n   Brahe; -- so named from Rudolph II., emperor of Germany.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rue', 'n.', 'A perennial suffrutescent plant (Ruta graveolens), having a\n   strong, heavy odor and a bitter taste; herb of grace. It is used in\n   medicine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rue', 'n.', 'Fig.: Bitterness; disappointment; grief; regret.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rued', 'imp. & p. p.', 'of Rue');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruing', 'p. pr. & vb. n.', 'of Rue');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rue', 'v. t.', 'To lament; to regret extremely; to grieve for or over.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rue', 'v. t.', 'To cause to grieve; to afflict.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rue', 'v. t.', 'To repent of, and withdraw from, as a bargain; to get\n   released from.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rue', 'v. i.', 'To have compassion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rue', 'v. i.', 'To feel sorrow and regret; to repent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rue', 'v. t.', 'Sorrow; repetance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rueful', 'a.', 'Causing one to rue or lament; woeful; mournful; sorrowful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rueful', 'a.', 'Expressing sorrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruell bone', '', 'See rewel bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruelle', 'n.', 'A private circle or assembly at a private house; a circle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rufescent', 'a.', 'Reddish; tinged with red.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruff', 'n.', 'A game similar to whist, and the predecessor of it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruff', 'n.', 'The act of trumping, especially when one has no card of the\n   suit led.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruff', 'v. i. & t.', 'To trump.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruff', 'n.', 'A muslin or linen collar plaited, crimped, or fluted, worn\n   formerly by both sexes, now only by women and children.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruff', 'n.', 'Something formed with plaits or flutings, like the collar of\n   this name.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruff', 'n.', 'An exhibition of pride or haughtiness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruff', 'n.', 'Wanton or tumultuous procedure or conduct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruff', 'n.', 'A low, vibrating beat of a drum, not so loud as a roll; a\n   ruffle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruff', 'n.', 'A collar on a shaft ot other piece to prevent endwise motion.\n   See Illust. of Collar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruff', 'n.', 'A set of lengthened or otherwise modified feathers round, or\n   on, the neck of a bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruff', 'n.', 'A limicoline bird of Europe and Asia (Pavoncella, /\n   Philommachus, pugnax) allied to the sandpipers. The males during the\n   breeding season have a large ruff of erectile feathers, variable in\n   their colors, on the neck, and yellowish naked tubercles on the face.\n   They are polygamous, and are noted for their pugnacity in the breeding\n   season. The female is called reeve, or rheeve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruff', 'n.', 'A variety of the domestic pigeon, having a ruff of its neck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruffed', 'imp. & p. p.', 'of Ruff');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruffing', 'p. pr. & vb. n.', 'of Ruff');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruff', 'v. t.', 'To ruffle; to disorder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruff', 'v. t.', 'To beat with the ruff or ruffle, as a drum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruff', 'v. t.', 'To hit, as the prey, without fixing it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruff', 'n.', 'Alt. of Ruffe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruffe', 'n.', 'A small freshwater European perch (Acerina vulgaris); --\n   called also pope, blacktail, and stone, / striped, perch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruffed', 'a.', 'Furnished with a ruff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruffian', 'n.', 'A pimp; a pander; also, a paramour.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruffian', 'n.', 'A boisterous, cruel, brutal fellow; a desperate fellow\n   ready for murderous or cruel deeds; a cutthroat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruffian', 'a.', 'brutal; cruel; savagely boisterous; murderous; as, ruffian\n   rage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruffian', 'v. i.', 'To play the ruffian; to rage; to raise tumult.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruffianage', 'n.', 'Ruffians, collectively; a body of ruffians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruffianish', 'a.', 'Having the qualities or manners of a ruffian;\n   ruffianly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruffianlike', 'a.', 'Ruffianly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruffianly', 'a.', 'Like a ruffian; bold in crimes; characteristic of a\n   ruffian; violent; brutal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruffianous', 'a.', 'Ruffianly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruffin', 'a.', 'Disordered.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruffled', 'imp. & p. p.', 'of Ruffle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruffling', 'p. pr. & vb. n.', 'of Ruffle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruffle', 'v. t.', 'To make into a ruff; to draw or contract into puckers,\n   plaits, or folds; to wrinkle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruffle', 'v. t.', 'To furnish with ruffles; as, to ruffle a shirt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruffle', 'v. t.', 'To oughen or disturb the surface of; to make uneven by\n   agitation or commotion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruffle', 'v. t.', 'To erect in a ruff, as feathers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruffle', 'v. t.', 'To beat with the ruff or ruffle, as a drum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruffle', 'v. t.', 'To discompose; to agitate; to disturb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruffle', 'v. t.', 'To throw into disorder or confusion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruffle', 'v. t.', 'To throw together in a disorderly manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruffle', 'v. i.', 'To grow rough, boisterous, or turbulent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruffle', 'v. i.', 'To become disordered; to play loosely; to flutter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruffle', 'v. i.', 'To be rough; to jar; to be in contention; hence, to put\n   on airs; to swagger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruffle', 'v. t. & i.', 'That which is ruffled; specifically, a strip of\n   lace, cambric, or other fine cloth, plaited or gathered on one edge or\n   in the middle, and used as a trimming; a frill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruffle', 'v. t. & i.', 'A state of being ruffled or disturbed; disturbance;\n   agitation; commotion; as, to put the mind in a ruffle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruffle', 'v. t. & i.', 'A low, vibrating beat of a drum, not so loud as a\n   roll; -- called also ruff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruffle', 'v. t. & i.', 'The connected series of large egg capsules, or\n   oothecae, of any one of several species of American marine gastropods\n   of the genus Fulgur. See Ootheca.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruffleless', 'a.', 'Having no ruffle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rufflement', 'n.', 'The act of ruffling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruffler', 'n.', 'One who ruffles; a swaggerer; a bully; a ruffian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruffler', 'n.', 'That which ruffles; specifically, a sewing machine\n   attachment for making ruffles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rufigallic', 'a.', 'Pertaining to, or designating, an acid which is\n   obtained from gallic acid as a brown or red crystalline substance, and\n   is related to rufiopin and anthracene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rufiopin', 'n.', 'A yellowish red crystalline substance related to\n   anthracene, and obtained from opianic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rufol', 'n.', 'A phenol derivative of anthracene obtained as a white\n   crystalline substance, which on oxidation produces a red dyestuff\n   related to anthraquinone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rufous', 'a.', 'Reddish; of a yellowish red or brownish red color; tawny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruft', 'n.', 'Eructation; belching.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rufterhood', 'n.', 'A kind of hood for a hawk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rug', 'a.', 'A kind of coarse, heavy frieze, formerly used for garments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rug', 'a.', 'A piece of thick, nappy fabric, commonly made of wool, -- used\n   for various purposes, as for covering and ornamenting part of a bare\n   floor, for hanging in a doorway as a potiere, for protecting a portion\n   of carpet, for a wrap to protect the legs from cold, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rug', 'a.', 'A rough, woolly, or shaggy dog.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rug', 'v. t.', 'To pull roughly or hastily; to plunder; to spoil; to tear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rugae', 'pl. ', 'of Ruga');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruga', 'n.', 'A wrinkle; a fold; as, the rugae of the stomach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rugate', 'a.', 'Having alternate ridges and depressions; wrinkled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rugged', 'n.', 'Full of asperities on the surface; broken into sharp or\n   irregular points, or otherwise uneven; not smooth; rough; as, a rugged\n   mountain; a rugged road.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rugged', 'n.', 'Not neat or regular; uneven.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rugged', 'n.', 'Rough with bristles or hair; shaggy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rugged', 'n.', 'Harsh; hard; crabbed; austere; -- said of temper,\n   character, and the like, or of persons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rugged', 'n.', 'Stormy; turbulent; tempestuous; rude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rugged', 'n.', 'Rough to the ear; harsh; grating; -- said of sound, style,\n   and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rugged', 'n.', 'Sour; surly; frowning; wrinkled; -- said of looks, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rugged', 'n.', 'Violent; rude; boisterrous; -- said of conduct, manners,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rugged', 'n.', 'Vigorous; robust; hardy; -- said of health, physique, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rugging', 'n.', 'A coarse kind of woolen cloth, used for wrapping,\n   blanketing, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rug-gowned', 'a.', 'Wearing a coarse gown or shaggy garment made of rug.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruggy', 'a.', 'Rugged; rough.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rug-headed', 'a.', 'Having shaggy hair; shock-headed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rugin', 'n.', 'A nappy cloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rugine', 'n.', 'An instrument for scraping the periosteum from bones; a\n   raspatory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rugine', 'v. t.', 'To scrape or rasp, as a bone; to scale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rugosa', 'n. pl.', 'An extinct tribe of fossil corals, including numerous\n   species, many of them of large size. They are characteristic of the\n   Paleozoic formations. The radiating septs, when present, are usually in\n   multiples of four. See Cyathophylloid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rugose', 'a.', 'Wrinkled; full of wrinkles; specifically (Bot.), having the\n   veinlets sunken and the spaces between them elevated, as the leaves of\n   the sage and horehound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rugosity', 'n.', 'The quality or state of being rugose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rugous', 'a.', 'Wrinkled; rugose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rugulose', 'a.', 'Somewhat rugose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruhmkorffs coil', '', 'See Induction coil, under Induction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruin', 'n.', 'The act of falling or tumbling down; fall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruin', 'n.', 'Such a change of anything as destroys it, or entirely defeats\n   its object, or unfits it for use; destruction; overthrow; as, the ruin\n   of a ship or an army; the ruin of a constitution or a government; the\n   ruin of health or hopes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruin', 'n.', 'That which is fallen down and become worthless from injury or\n   decay; as, his mind is a ruin; especially, in the plural, the remains\n   of a destroyed, dilapidated, or desolate house, fortress, city, or the\n   like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruin', 'n.', 'The state of being dcayed, or of having become ruined or\n   worthless; as, to be in ruins; to go to ruin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruin', 'n.', 'That which promotes injury, decay, or destruction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruined', 'imp. & p. p.', 'of Ruin');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruining', 'p. pr. & vb. n.', 'of Ruin');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruin', 'n.', 'To bring to ruin; to cause to fall to pieces and decay; to\n   make to perish; to bring to destruction; to bring to poverty or\n   bankruptcy; to impair seriously; to damage essentially; to overthrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruin', 'v. i.', 'To fall to ruins; to go to ruin; to become decayed or\n   dilapidated; to perish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruinable', 'a.', 'Capable of being ruined.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruinate', 'v. t.', 'To demolish; to subvert; to destroy; to reduce to\n   poverty; to ruin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruinate', 'v. t.', 'To cause to fall; to cast down.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruinate', 'v. i.', 'To fall; to tumble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruinate', 'a.', 'Involved in ruin; ruined.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruination', 'n.', 'The act of ruining, or the state of being ruined.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruiner', 'n.', 'One who, or that which, ruins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruiniform', 'a.', 'Having the appearance of ruins, or of the ruins of\n   houses; -- said of certain minerals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruinous', 'a.', 'Causing, or tending to cause, ruin; destructive; baneful;\n   pernicious; as, a ruinous project.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruinous', 'a.', 'Characterized by ruin; ruined; dilapidated; as, an\n   edifice, bridge, or wall in a ruinous state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruinous', 'a.', 'Composed of, or consisting in, ruins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rukh', 'n.', 'The roc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rukh', 'n.', 'A large bird, supposed by some to be the same as the extinct\n   Epiornis of Madagascar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rulable', 'a.', 'That may be ruled; subject to rule; accordant or\n   conformable to rule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rule', 'a.', 'That which is prescribed or laid down as a guide for conduct\n   or action; a governing direction for a specific purpose; an\n   authoritative enactment; a regulation; a prescription; a precept; as,\n   the rules of various societies; the rules governing a school; a rule of\n   etiquette or propriety; the rules of cricket.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rule', 'a.', 'Uniform or established course of things.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rule', 'a.', 'Systematic method or practice; as, my ule is to rise at six\n   oclock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rule', 'a.', 'Ordibary course of procedure; usual way; comon state or\n   condition of things; as, it is a rule to which there are many\n   exeptions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rule', 'a.', 'Conduct in general; behavior.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rule', 'a.', 'The act of ruling; administration of law; government; empire;\n   authority; control.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rule', 'a.', 'An order regulating the practice of the courts, or an order\n   made between parties to an action or a suit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rule', 'a.', 'A determinate method prescribed for performing any operation\n   and producing a certain result; as, a rule for extracting the cube\n   root.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rule', 'a.', 'A general principle concerning the formation or use of words,\n   or a concise statement thereof; thus, it is a rule in England, that s\n   or es , added to a noun in the singular number, forms the plural of\n   that noun; but \"man\" forms its plural \"men\", and is an exception to the\n   rule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rule', 'a.', 'A straight strip of wood, metal, or the like, which serves as\n   a guide in drawing a straight line; a ruler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rule', 'a.', 'A measuring instrument consisting of a graduated bar of wood,\n   ivory, metal, or the like, which is usually marked so as to show inches\n   and fractions of an inch, and jointed so that it may be folded\n   compactly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rule', 'a.', 'A thin plate of metal (usually brass) of the same height as\n   the type, and used for printing lines, as between columns on the same\n   page, or in tabular work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rule', 'a.', 'A composing rule. See under Conposing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruled', 'imp. & p. p.', 'of Rule');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruling', 'p. pr. & vb. n.', 'of Rule');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rule', 'n.', 'To control the will and actions of; to exercise authority or\n   dominion over; to govern; to manage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rule', 'n.', 'To control or direct by influence, counsel, or persuasion; to\n   guide; -- used chiefly in the passive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rule', 'n.', 'To establish or settle by, or as by, a rule; to fix by\n   universal or general consent, or by common practice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rule', 'n.', 'To require or command by rule; to give as a direction or\n   order of court.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rule', 'n.', 'To mark with lines made with a pen, pencil, etc., guided by a\n   rule or ruler; to print or mark with lines by means of a rule or other\n   contrivance effecting a similar result; as, to rule a sheet of paper of\n   a blank book.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rule', 'v. i.', 'To have power or command; to exercise supreme authority;\n   -- often followed by over.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rule', 'v. i.', 'To lay down and settle a rule or order of court; to decide\n   an incidental point; to enter a rule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rule', 'v. i.', 'To keep within a (certain) range for a time; to be in\n   general, or as a rule; as, prices ruled lower yesterday than the day\n   before.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruleless', 'a.', 'Destitute of rule; lawless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rule-monger', 'n.', 'A stickler for rules; a slave of rules');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruler', 'n.', 'One who rules; one who exercises sway or authority; a\n   governor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruler', 'n.', 'A straight or curved strip of wood, metal, etc., with a\n   smooth edge, used for guiding a pen or pencil in drawing lines. Cf.\n   Rule, n., 7 (a).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruling', 'a.', 'Predominant; chief; reigning; controlling; as, a ruling\n   passion; a ruling sovereign.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruling', 'a.', 'Used in marking or engraving lines; as, a ruling machine or\n   pen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruling', 'n.', 'The act of one who rules; ruled lines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruling', 'n.', 'A decision or rule of a judge or a court, especially an\n   oral decision, as in excluding evidence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rulingly', 'adv.', 'In a ruling manner; so as to rule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rullichies', 'n. pl.', 'Chopped meat stuffed into small bags of tripe. They\n   are cut in slices and fried.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruly', 'a.', 'Orderly; easily restrained; -- opposed to unruly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rum', 'n.', 'A kind of intoxicating liquor distilled from cane juice, or\n   from the scummings of the boiled juice, or from treacle or molasses, or\n   from the lees of former distillations. Also, sometimes used\n   colloquially as a generic or a collective name for intoxicating liquor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rum', 'a.', 'Old-fashioned; queer; odd; as, a rum idea; a rum fellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rum', 'n.', 'A queer or odd person or thing; a country parson.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rumble', 'v. i.', 'To make a low, heavy, continued sound; as, the thunder\n   rumbles at a distance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rumble', 'v. i.', 'To murmur; to ripple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rumble', 'n.', 'A noisy report; rumor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rumble', 'n.', 'A low, heavy, continuous sound like that made by heavy\n   wagons or the reverberation of thunder; a confused noise; as, the\n   rumble of a railroad train.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rumble', 'n.', 'A seat for servants, behind the body of a carriage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rumble', 'n.', 'A rotating cask or box in which small articles are smoothed\n   or polished by friction against each other.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rumble', 'v. t.', 'To cause to pass through a rumble, or shaking machine.\n   See Rumble, n., 4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rumbler', 'n.', 'One who, or that which, rumbles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rumbling', '', 'a. & n. from Rumble, v. i.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rumblingly', 'adv.', 'In a rumbling manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rumbo', 'n.', 'Grog.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rumbowline', 'n.', 'Same as Rombowline.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rumen', 'n.', 'The first stomach of ruminants; the paunch; the fardingbag.\n   See Illust. below.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rumen', 'n.', 'The cud of a ruminant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rumicin', 'n.', 'A yellow crystalline substance found in the root of yellow\n   dock (Rumex crispus) and identical with chrysophanic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruminal', 'a.', 'Ruminant; ruminating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruminant', 'a.', 'Chewing the cud; characterized by chewing again what has\n   been swallowed; of or pertaining to the Ruminantia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruminant', 'n.', 'A ruminant animal; one of the Ruminantia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruminantia', 'n. pl.', 'A division of Artiodactyla having four stomachs.\n   This division includes the camels, deer, antelopes, goats, sheep, neat\n   cattle, and allies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruminantly', 'adv.', 'In a ruminant manner; by ruminating, or chewing the\n   cud.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruminated', 'imp. & p. p.', 'of Ruminate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruminating', 'p. pr. & vb. n.', 'of Ruminate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruminate', 'v. i.', 'To chew the cud; to chew again what has been slightly\n   chewed and swallowed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruminate', 'v. i.', 'To think again and again; to muse; to meditate; to\n   ponder; to reflect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruminate', 'v. t.', 'To chew over again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruminate', 'v. t.', 'To meditate or ponder over; to muse on.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruminate', 'a.', 'Alt. of Ruminated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruminated', 'a.', 'Having a hard albumen penetrated by irregular channels\n   filled with softer matter, as the nutmeg and the seeds of the North\n   American papaw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rumination', 'n.', 'The act or process of ruminating, or chewing the cud;\n   the habit of chewing the cud.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rumination', 'n.', 'The state of being disposed to ruminate or ponder;\n   deliberate meditation or reflection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rumination', 'n.', 'The regurgitation of food from the stomach after it has\n   been swallowed, -- occasionally observed as a morbid phenomenon in man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruminative', 'a.', 'Inclined to, or engaged in, rumination or meditation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruminator', 'n.', 'One who ruminates or muses; a meditator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rumkin', 'n.', 'A popular or jocular name for a drinking vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rummage', 'n.', 'A place or room for the stowage of cargo in a ship; also,\n   the act of stowing cargo; the pulling and moving about of packages\n   incident to close stowage; -- formerly written romage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rummage', 'n.', 'A searching carefully by looking into every corner, and by\n   turning things over.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rummaged', 'imp. & p. p.', 'of Rummage');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rummaging', 'p. pr. & vb. n.', 'of Rummage');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rummage', 'v. t.', 'To make room in, as a ship, for the cargo; to move\n   about, as packages, ballast, so as to permit close stowage; to stow\n   closely; to pack; -- formerly written roomage, and romage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rummage', 'v. t.', 'To search or examine thoroughly by looking into every\n   corner, and turning over or removing goods or other things; to examine,\n   as a book, carefully, turning over leaf after leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rummage', 'v. i.', 'To search a place narrowly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rummager', 'n.', 'One who rummages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rummager', 'n.', 'A person on shipboard whose business was to take charge\n   of stowing the cargo; -- formerly written roomager, and romager.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rummer', 'n.', 'A large and tall glass, or drinking cup.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rummy', 'a.', 'Of or pertaining to rum; characteristic of rum; as a rummy\n   flavor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rummies', 'pl. ', 'of Rummy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rummy', 'n.', 'One who drinks rum; an habitually intemperate person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rummy', 'a.', 'Strange; odd.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rumney', 'n.', 'A sort of Spanish wine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rumor', 'n.', 'A flying or popular report; the common talk; hence, public\n   fame; notoriety.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rumor', 'n.', 'A current story passing from one person to another, without\n   any known authority for its truth; -- in this sense often personified.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rumor', 'n.', 'A prolonged, indistinct noise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rumored', 'imp. & p. p.', 'of Rumor');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rumoring', 'p. pr. & vb. n.', 'of Rumor');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rumor', 'v. t.', 'To report by rumor; to tell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rumorer', 'n.', 'A teller of news; especially, one who spreads false\n   reports.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rumorous', 'a.', 'Of or pertaining to a rumor; of the nature of rumors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rumorous', 'a.', 'Famous; notorious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rumorous', 'a.', 'Murmuring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rump', 'n.', 'The end of the backbone of an animal, with the parts\n   adjacent; the buttock or buttocks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rump', 'n.', 'Among butchers, the piece of beef between the sirloin and the\n   aitchbone piece. See Illust. of Beef.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rump', 'n.', 'The hind or tail end; a fag-end; a remnant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rumper', 'n.', 'A member or a supporter of the Rump Parliament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rump-fed', 'a.', 'A Shakespearean word of uncertain meaning. Perhaps\n   \"fattened in the rump, pampered.\"');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rumpled', 'imp. & p. p.', 'of Rumple');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rumpling', 'p. pr. & vb. n.', 'of Rumple');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rumple', 'v. t. & i.', 'To make uneven; to form into irregular\n   inequalities; to wrinkle; to crumple; as, to rumple an apron or a\n   cravat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rumple', 'n.', 'A fold or plait; a wrinkle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rumpled', 'a.', 'Wrinkled; crumpled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rumpless', 'a.', 'Destitute of a rump.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rumply', 'a.', 'Rumpled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rumpus', 'n.', 'A disturbance; noise and confusion; a quarrel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rumseller', 'n.', 'One who sells rum; one who deals in intoxicating\n   liquors; especially, one who sells spirituous beverages at retail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ran', 'imp.', 'of Run');
  INSERT INTO words (word, word_type, description) VALUES  (    'Run', '', 'of Run');
  INSERT INTO words (word, word_type, description) VALUES  (    'Run', 'p. p.', 'of Run');
  INSERT INTO words (word, word_type, description) VALUES  (    'Running', 'p. pr. & vb. n.', 'of Run');
  INSERT INTO words (word, word_type, description) VALUES  (    'Run', 'a.', 'To move, proceed, advance, pass, go, come, etc., swiftly,\n   smoothly, or with quick action; -- said of things animate or inanimate.\n   Hence, to flow, glide, or roll onward, as a stream, a snake, a wagon,\n   etc.; to move by quicker action than in walking, as a person, a horse,\n   a dog.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Run', 'a.', 'To go swiftly; to pass at a swift pace; to hasten.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Run', 'a.', 'To flee, as from fear or danger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Run', 'a.', 'To steal off; to depart secretly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Run', 'a.', 'To contend in a race; hence, to enter into a contest; to\n   become a candidate; as, to run for Congress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Run', 'a.', 'To pass from one state or condition to another; to come into a\n   certain condition; -- often with in or into; as, to run into evil\n   practices; to run in debt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Run', 'a.', 'To exert continuous activity; to proceed; as, to run through\n   life; to run in a circle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Run', 'a.', 'To pass or go quickly in thought or conversation; as, to run\n   from one subject to another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Run', 'a.', 'To discuss; to continue to think or speak about something; --\n   with on.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Run', 'a.', 'To make numerous drafts or demands for payment, as upon a\n   bank; -- with on.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Run', 'a.', 'To creep, as serpents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Run', 'a.', 'To flow, as a liquid; to ascend or descend; to course; as,\n   rivers run to the sea; sap runs up in the spring; her blood ran cold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Run', 'a.', 'To proceed along a surface; to extend; to spread.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Run', 'a.', 'To become fluid; to melt; to fuse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Run', 'a.', 'To turn, as a wheel; to revolve on an axis or pivot; as, a\n   wheel runs swiftly round.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Run', 'a.', 'To travel; to make progress; to be moved by mechanical means;\n   to go; as, the steamboat runs regularly to Albany; the train runs to\n   Chicago.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Run', 'a.', 'To extend; to reach; as, the road runs from Philadelphia to\n   New York; the memory of man runneth not to the contrary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Run', 'a.', 'To go back and forth from place to place; to ply; as, the\n   stage runs between the hotel and the station.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Run', 'a.', 'To make progress; to proceed; to pass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Run', 'a.', 'To continue in operation; to be kept in action or motion; as,\n   this engine runs night and day; the mill runs six days in the week.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Run', 'a.', 'To have a course or direction; as, a line runs east and west.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Run', 'a.', 'To be in form thus, as a combination of words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Run', 'a.', 'To be popularly known; to be generally received.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Run', 'a.', 'To have growth or development; as, boys and girls run up\n   rapidly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Run', 'a.', 'To tend, as to an effect or consequence; to incline.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Run', 'a.', 'To spread and blend together; to unite; as, colors run in\n   washing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Run', 'a.', 'To have a legal course; to be attached; to continue in force,\n   effect, or operation; to follow; to go in company; as, certain\n   covenants run with the land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Run', 'a.', 'To continue without falling due; to hold good; as, a note has\n   thirty days to run.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Run', 'a.', 'To discharge pus or other matter; as, an ulcer runs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Run', 'a.', 'To be played on the stage a number of successive days or\n   nights; as, the piece ran for six months.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Run', 'a.', 'To sail before the wind, in distinction from reaching or\n   sailing closehauled; -- said of vessels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Run', 'a.', 'Specifically, of a horse: To move rapidly in a gait in which\n   each leg acts in turn as a propeller and a supporter, and in which for\n   an instant all the limbs are gathered in the air under the body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Run', 'a.', 'To move rapidly by springing steps so that there is an instant\n   in each step when neither foot touches the ground; -- so distinguished\n   from walking in athletic competition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Run', 'v. t.', 'To cause to run (in the various senses of Run, v. i.); as,\n   to run a horse; to run a stage; to run a machine; to run a rope through\n   a block.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Run', 'v. i.', 'To pursue in thought; to carry in contemplation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Run', 'v. i.', 'To cause to enter; to thrust; as, to run a sword into or\n   through the body; to run a nail into the foot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Run', 'v. i.', 'To drive or force; to cause, or permit, to be driven.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Run', 'v. i.', 'To fuse; to shape; to mold; to cast; as, to run bullets,\n   and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Run', 'v. i.', 'To cause to be drawn; to mark out; to indicate; to\n   determine; as, to run a line.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Run', 'v. i.', 'To cause to pass, or evade, offical restrictions; to\n   smuggle; -- said of contraband or dutiable goods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Run', 'v. i.', 'To go through or accomplish by running; as, to run a race;\n   to run a certain career.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Run', 'v. i.', 'To cause to stand as a candidate for office; to support for\n   office; as, to run some one for Congress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Run', 'v. i.', 'To encounter or incur, as a danger or risk; as, to run the\n   risk of losing ones life. See To run the chances, below.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Run', 'v. i.', 'To put at hazard; to venture; to risk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Run', 'v. i.', 'To discharge; to emit; to give forth copiously; to be\n   bathed with; as, the pipe or faucet runs hot water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Run', 'v. i.', 'To be charged with, or to contain much of, while flowing;\n   as, the rivers ran blood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Run', 'v. i.', 'To conduct; to manage; to carry on; as, to run a factory or\n   a hotel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Run', 'v. i.', 'To tease with sarcasms and ridicule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Run', 'v. i.', 'To sew, as a seam, by passing the needle through material\n   in a continuous line, generally taking a series of stitches on the\n   needle at the same time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Run', 'v. i.', 'To migrate or move in schools; -- said of fish; esp., to\n   ascend a river in order to spawn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Run', 'n.', 'The act of running; as, a long run; a good run; a quick run;\n   to go on the run.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Run', 'n.', 'A small stream; a brook; a creek.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Run', 'n.', 'That which runs or flows in the course of a certain operation,\n   or during a certain time; as, a run of must in wine making; the first\n   run of sap in a maple orchard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Run', 'n.', 'A course; a series; that which continues in a certain course\n   or series; as, a run of good or bad luck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Run', 'n.', 'State of being current; currency; popularity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Run', 'n.', 'Continued repetition on the stage; -- said of a play; as, to\n   have a run of a hundred successive nights.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Run', 'n.', 'A continuing urgent demand; especially, a pressure on a bank\n   or treasury for payment of its notes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Run', 'n.', 'A range or extent of ground for feeding stock; as, a sheep\n   run.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Run', 'n.', 'The aftermost part of a vessels hull where it narrows toward\n   the stern, under the quarter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Run', 'n.', 'The distance sailed by a ship; as, a good run; a run of fifty\n   miles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Run', 'n.', 'A voyage; as, a run to China.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Run', 'n.', 'A pleasure excursion; a trip.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Run', 'n.', 'The horizontal distance to which a drift may be carried,\n   either by license of the proprietor of a mine or by the nature of the\n   formation; also, the direction which a vein of ore or other substance\n   takes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Run', 'n.', 'A roulade, or series of running tones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Run', 'n.', 'The greatest degree of swiftness in marching. It is executed\n   upon the same principles as the double-quick, but with greater speed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Run', 'n.', 'The act of migrating, or ascending a river to spawn; -- said\n   of fish; also, an assemblage or school of fishes which migrate, or\n   ascend a river for the purpose of spawning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Run', 'n.', 'In baseball, a complete circuit of the bases made by a player,\n   which enables him to score one; in cricket, a passing from one wicket\n   to the other, by which one point is scored; as, a player made three\n   runs; the side went out with two hundred runs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Run', 'n.', 'A pair or set of millstones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Run', 'a.', 'Melted, or made from molten material; cast in a mold; as, run\n   butter; run iron or lead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Run', 'a.', 'Smuggled; as, run goods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Runagate', 'n.', 'A fugitive; a vagabond; an apostate; a renegade. See\n   Renegade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Runaway', 'n.', 'One who, or that which, flees from danger, duty,\n   restraint, etc.; a fugitive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Runaway', 'n.', 'The act of running away, esp. of a horse or teams; as,\n   there was a runaway yesterday.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Runaway', 'a.', 'Running away; fleeing from danger, duty, restraint, etc.;\n   as, runaway soldiers; a runaway horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Runaway', 'a.', 'Accomplished by running away or elopement, or during\n   flight; as, a runaway marriage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Runaway', 'a.', 'Won by a long lead; as, a runaway victory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Runaway', 'a.', 'Very successful; accomplishing success quickly; as, a\n   runaway bestseller.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Runcation', 'n.', 'A weeding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Runch', 'n.', 'The wild radish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Runcinate', 'a.', 'Pinnately cut with the lobes pointing downwards, as the\n   leaf of the dandelion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rundel', 'n.', 'A moat with water in it; also, a small stream; a runlet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rundel', 'n.', 'A circle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rundle', 'n.', 'A round; a step of a ladder; a rung.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rundle', 'n.', 'A ball.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rundle', 'n.', 'Something which rotates about an axis, as a wheel, or the\n   drum of a capstan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rundle', 'n.', 'One of the pins or trundles of a lantern wheel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rundlet', 'n.', 'A small barrel of no certain dimensions. It may contain\n   from 3 to 20 gallons, but it usually holds about 14/ gallons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rune', 'n.', 'A letter, or character, belonging to the written language of\n   the ancient Norsemen, or Scandinavians; in a wider sense, applied to\n   the letters of the ancient nations of Northern Europe in general.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rune', 'n.', 'Old Norse poetry expressed in runes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Runer', 'n.', 'A bard, or learned man, among the ancient Goths.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rung', '', 'imp. & p. p. of Ring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rung', 'n.', 'A floor timber in a ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rung', 'n.', 'One of the rounds of a ladder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rung', 'n.', 'One of the stakes of a cart; a spar; a heavy staff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rung', 'n.', 'One of the radial handles projecting from the rim of a\n   steering wheel; also, one of the pins or trundles of a lantern wheel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Runghead', 'n.', 'The upper end of a floor timber in a ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Runic', 'a.', 'Of or pertaining to a rune, to runes, or to the Norsemen;\n   as, runic verses; runic letters; runic names; runic rhyme.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Runlet', 'n.', 'A little run or stream; a streamlet; a brook.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Runlet', 'n.', 'Same as Rundlet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Runnel', 'n.', 'A rivulet or small brook.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Runner', 'n.', 'One who, or that which, runs; a racer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Runner', 'n.', 'A detective.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Runner', 'n.', 'A messenger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Runner', 'n.', 'A smuggler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Runner', 'n.', 'One employed to solicit patronage, as for a steamboat,\n   hotel, shop, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Runner', 'n.', 'A slender trailing branch which takes root at the joints or\n   end and there forms new plants, as in the strawberry and the common\n   cinquefoil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Runner', 'n.', 'The rotating stone of a set of millstones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Runner', 'n.', 'A rope rove through a block and used to increase the\n   mechanical power of a tackle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Runner', 'n.', 'One of the pieces on which a sled or sleigh slides; also\n   the part or blade of a skate which slides on the ice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Runner', 'n.', 'A horizontal channel in a mold, through which the metal\n   flows to the cavity formed by the pattern; also, the waste metal left\n   in such a channel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Runner', 'n.', 'A trough or channel for leading molten metal from a furnace\n   to a ladle, mold, or pig bed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Runner', 'n.', 'The movable piece to which the ribs of an umbrella are\n   attached.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Runner', 'n.', 'A food fish (Elagatis pinnulatus) of Florida and the West\n   Indies; -- called also skipjack, shoemaker, and yellowtail. The name\n   alludes to its rapid successive leaps from the water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Runner', 'n.', 'Any cursorial bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Runner', 'n.', 'A movable slab or rubber used in grinding or polishing a\n   surface of stone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Runner', 'n.', 'A tool on which lenses are fastened in a group, for\n   polishing or grinding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Runnet', 'n.', 'See Rennet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Running', 'a.', 'Moving or advancing by running.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Running', 'a.', 'Having a running gait; not a trotter or pacer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Running', 'a.', 'trained and kept for running races; as, a running horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Running', 'a.', 'Successive; one following the other without break or\n   intervention; -- said of periods of time; as, to be away two days\n   running; to sow land two years running.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Running', 'a.', 'Flowing; easy; cursive; as, a running hand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Running', 'a.', 'Continuous; keeping along step by step; as, he stated the\n   facts with a running explanation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Running', 'a.', 'Extending by a slender climbing or trailing stem; as, a\n   running vine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Running', 'a.', 'Discharging pus; as, a running sore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Running', 'n.', 'The act of one who, or of that which runs; as, the running\n   was slow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Running', 'n.', 'That which runs or flows; the quantity of a liquid which\n   flows in a certain time or during a certain operation; as, the first\n   running of a still.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Running', 'n.', 'The discharge from an ulcer or other sore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Runningly', 'adv.', 'In a running manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Runnion', 'n.', 'See Ronion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Runology', 'n.', 'The science of runes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Runround', 'n.', 'A felon or whitlow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Runt', 'a.', 'Any animal which is unusually small, as compared with others\n   of its kind; -- applied particularly to domestic animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Runt', 'a.', 'A variety of domestic pigeon, related to the barb and\n   carrier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Runt', 'a.', 'A dwarf; also, a mean, despicable, boorish person; -- used\n   opprobriously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Runt', 'a.', 'The dead stump of a tree; also, the stem of a plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Runty', 'a.', 'Like a runt; diminutive; mean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Runway', 'n.', 'The channel of a stream.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Runway', 'n.', 'The beaten path made by deer or other animals in passing to\n   and from their feeding grounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rupee', 'n.', 'A silver coin, and money of account, in the East Indies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rupellary', 'n.', 'Rocky.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruperts drop', '', 'A kind of glass drop with a long tail, made by\n   dropping melted glass into water. It is remarkable for bursting into\n   fragments when the surface is scratched or the tail broken; -- so\n   called from Prince Rupert, nephew of Charles I., by whom they were\n   first brought to England. Called also Ruperts ball, and glass tear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rupia', 'n.', 'An eruption upon the skin, consisting of vesicles with\n   inflamed base and filled with serous, purulent, or bloody fluid, which\n   dries up, forming a blackish crust.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rupial', 'a.', 'Of or pertaining to rupia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rupicola', 'n.', 'A genus of beautiful South American passerine birds,\n   including the cock of the rock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rupicoline', 'a.', 'Rock-inhabiting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruption', 'n.', 'A breaking or bursting open; breach; rupture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruptuary', 'n.', 'One not of noble blood; a plebeian; a roturier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rupture', 'n.', 'The act of breaking apart, or separating; the state of\n   being broken asunder; as, the rupture of the skin; the rupture of a\n   vessel or fiber; the rupture of a lutestring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rupture', 'n.', 'Breach of peace or concord between individuals; open\n   hostility or war between nations; interruption of friendly relations;\n   as, the parties came to a rupture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rupture', 'n.', 'Hernia. See Hernia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rupture', 'n.', 'A bursting open, as of a steam boiler, in a less sudden\n   manner than by explosion. See Explosion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruptured', 'imp. & p. p.', 'of Rupture');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rupturing', 'p. pr. & vb. n.', 'of Rupture');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rupture', 'v. t.', 'To part by violence; to break; to burst; as, to rupture\n   a blood vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rupture', 'v. t.', 'To produce a hernia in.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rupture', 'v. i.', 'To suffer a breach or disruption.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruptured', 'a.', 'Having a rupture, or hernia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rupturewort', 'n.', 'Same as Burstwort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rupturewort', 'n.', 'A West Indian plant (Alternanthera polygonoides)\n   somewhat resembling burstwort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rural', 'a.', 'Of or pertaining to the country, as distinguished from a\n   city or town; living in the country; suitable for, or resembling, the\n   country; rustic; as, rural scenes; a rural prospect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rural', 'a.', 'Of or pertaining to agriculture; as, rural economy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rurales', 'n. pl.', 'The gossamer-winged butterflies; a family of small\n   butterflies, including the hairstreaks, violets, and theclas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruralism', 'n.', 'The quality or state of being rural; ruralness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruralism', 'n.', 'A rural idiom or expression.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruralist', 'n.', 'One who leads a rural life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'ties', 'pl. ', 'of Rurality');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rurality', 'n.', 'The quality or state of being rural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rurality', 'n.', 'A rural place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruralized', 'imp. & p. p.', 'of Ruralize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruralizing', 'p. pr. & vb. n.', 'of Ruralize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruralize', 'v. t.', 'To render rural; to give a rural appearance to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruralize', 'v. i.', 'To become rural; to go into the country; to rusticate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rurally', 'adv.', 'In a rural manner; as in the country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruralness', 'n.', 'The quality or state of being rural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruricolist', 'n.', 'An inhabitant of the country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruridecanal', 'a.', 'Of or pertaining to a rural dean; as, a ruridecanal\n   district; the ruridecanal intellect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rurigenous', 'a.', 'Born in the country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruse', 'n.', 'An artifice; trick; stratagem; wile; fraud; deceit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rush', 'n.', 'A name given to many aquatic or marsh-growing endogenous\n   plants with soft, slender stems, as the species of Juncus and Scirpus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rush', 'n.', 'The merest trifle; a straw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rushed', 'imp. & p. p.', 'of Rush');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rushing', 'p. pr. & vb. n.', 'of Rush');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rush', 'v. i.', 'To move forward with impetuosity, violence, and tumultuous\n   rapidity or haste; as, armies rush to battle; waters rush down a\n   precipice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rush', 'v. i.', 'To enter into something with undue haste and eagerness, or\n   without due deliberation and preparation; as, to rush business or\n   speculation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rush', 'v. t.', 'To push or urge forward with impetuosity or violence; to\n   hurry forward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rush', 'v. t.', 'To recite (a lesson) or pass (an examination) without an\n   error.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rush', 'n.', 'A moving forward with rapidity and force or eagerness; a\n   violent motion or course; as, a rush of troops; a rush of winds; a rush\n   of water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rush', 'n.', 'Great activity with pressure; as, a rush of business.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rush', 'n.', 'A perfect recitation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rush', 'n.', 'A rusher; as, the center rush, whose place is in the center\n   of the rush line; the end rush.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rush', 'n.', 'The act of running with the ball.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rush-bearing', 'n.', 'A kind of rural festival at the dedication of a\n   church, when the parishioners brought rushes to strew the church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rushbuckler', 'n.', 'A bullying and violent person; a braggart; a\n   swashbuckler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rushed', 'a.', 'Abounding or covered with rushes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rusher', 'n.', 'One who rushes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rusher', 'n.', 'One who strewed rushes on the floor at dances.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rushiness', 'n.', 'The quality or state of abounding with rushes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rushingly', 'adv.', 'In a rushing manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rushlight', 'n.', 'A rush candle, or its light; hence, a small, feeble\n   light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rushlike', 'a.', 'Resembling a rush; weak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rushy', 'a.', 'Abounding with rushes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rushy', 'a.', 'Made of rushes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rusine', 'a.', 'Of, like, or pertaining to, a deer of the genus Rusa, which\n   includes the sambur deer (Rusa Aristotelis) of India.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rusk', 'n.', 'A kind of light, soft bread made with yeast and eggs, often\n   toasted or crisped in an oven; or, a kind of sweetened biscuit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rusk', 'n.', 'A kind of light, hard cake or bread, as for stores.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rusk', 'n.', 'Bread or cake which has been made brown and crisp, and\n   afterwards grated, or pulverized in a mortar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rusma', 'n.', 'A depilatory made of orpiment and quicklime, and used by the\n   Turks. See Rhusma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Russ', 'n. sing. & pl.', 'A Russian, or the Russians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Russ', 'n. sing. & pl.', 'The language of the Russians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Russ', 'a.', 'Of or pertaining to the Russians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Russet', 'a.', 'Of a reddish brown color, or (by some called) a red gray;\n   of the color composed of blue, red, and yellow in equal strength, but\n   unequal proportions, namely, two parts of red to one each of blue and\n   yellow; also, of a yellowish brown color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Russet', 'a.', 'Coarse; homespun; rustic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Russet', 'n.', 'A russet color; a pigment of a russet color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Russet', 'n.', 'Cloth or clothing of a russet color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Russet', 'n.', 'A country dress; -- so called because often of a russet\n   color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Russet', 'n.', 'An apple, or a pear, of a russet color; as, the English\n   russet, and the Roxbury russet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Russeting', 'n.', 'See Russet, n., 2 and 4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Russety', 'a.', 'Of a russet color; russet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Russia', 'n.', 'A country of Europe and Asia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Russian', 'a.', 'Of or pertaining to Russia, its inhabitants, or language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Russian', 'n.', 'A native or inhabitant of Russia; the language of Russia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Russianize', 'v. t.', 'To make Russian, or more or less like the Russians;\n   as, to Russianize the Poles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Russification', 'n.', 'The act or process of Russifying, or the state of\n   being Russified.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Russify', 'v. t.', 'To Russianize; as, to Russify conquered tribes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Russophile', 'n.', 'Alt. of Russophilist');
  INSERT INTO words (word, word_type, description) VALUES  (    'Russophilist', 'n.', 'One who, not being a Russian, favors Russian policy\n   and aggrandizement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Russophobe', '', 'Alt. of Russophobist');
  INSERT INTO words (word, word_type, description) VALUES  (    'Russophobist', '', 'One who dreads Russia or Russian influence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Russophobia', 'n.', 'Morbid dread of Russia or of Russian influence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rust', 'n.', 'The reddish yellow coating formed on iron when exposed to\n   moist air, consisting of ferric oxide or hydroxide; hence, by\n   extension, any metallic film of corrosion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rust', 'n.', 'A minute mold or fungus forming reddish or rusty spots on the\n   leaves and stems of cereal and other grasses (Trichobasis Rubigo-vera),\n   now usually believed to be a form or condition of the corn mildew\n   (Puccinia graminis). As rust, it has solitary reddish spores; as corn\n   mildew, the spores are double and blackish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rust', 'n.', 'That which resembles rust in appearance or effects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rust', 'n.', 'A composition used in making a rust joint. See Rust joint,\n   below.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rust', 'n.', 'Foul matter arising from degeneration; as, rust on salted\n   meat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rust', 'n.', 'Corrosive or injurious accretion or influence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rusted', 'imp. & p. p.', 'of Rust');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rusting', 'p. pr. & vb. n.', 'of Rust');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rust', 'v. i.', 'To contract rust; to be or become oxidized.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rust', 'v. i.', 'To be affected with the parasitic fungus called rust;\n   also, to acquire a rusty appearance, as plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rust', 'v. i.', 'To degenerate in idleness; to become dull or impaired by\n   inaction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rust', 'v. t.', 'To cause to contract rust; to corrode with rust; to affect\n   with rust of any kind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rust', 'v. t.', 'To impair by time and inactivity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rustful', 'a.', 'Full of rust; resembling rust; causing rust; rusty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rustic', 'a.', 'Of or pertaining to the country; rural; as, the rustic gods\n   of antiquity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rustic', 'a.', 'Rude; awkward; rough; unpolished; as, rustic manners.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rustic', 'a.', 'Coarse; plain; simple; as, a rustic entertainment; rustic\n   dress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rustic', 'a.', 'Simple; artless; unadorned; unaffected.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rustic', 'n.', 'An inhabitant of the country, especially one who is rude,\n   coarse, or dull; a clown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rustic', 'n.', 'A rural person having a natural simplicity of character or\n   manners; an artless, unaffected person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rustical', 'a.', 'Rustic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rusticated', 'imp. & p. p.', 'of Rusticate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rusticating', 'p. pr. & vb. n.', 'of Rusticate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rusticate', 'v. i.', 'To go into or reside in the country; to ruralize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rusticate', 'v. t.', 'To require or compel to reside in the country; to\n   banish or send away temporarily; to impose rustication on.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rusticated', 'a.', 'Resembling rustic work. See Rustic work (a), under\n   Rustic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rustication', 'n.', 'The act of rusticating, or the state of being\n   rusticated; specifically, the punishment of a student for some offense,\n   by compelling him to leave the institution for a time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rustication', 'n.', 'Rustic work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rusticity', 'n.', 'The quality or state of being rustic; rustic manners;\n   rudeness; simplicity; artlessness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rusticly', 'adv.', 'In a rustic manner; rustically.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rustily', 'adv.', 'In a rusty state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rustiness', 'n.', 'The quality or state of being rusty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rustled', 'imp. & p. p.', 'of Rustle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rustling', 'p. pr. & vb. n.', 'of Rustle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rustle', 'v. i.', 'To make a quick succession of small sounds, like the\n   rubbing or moving of silk cloth or dry leaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rustle', 'v. i.', 'To stir about energetically; to strive to succeed; to\n   bustle about.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rustle', 'v. t.', 'To cause to rustle; as, the wind rustles the leaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rustle', 'n.', 'A quick succession or confusion of small sounds, like those\n   made by shaking leaves or straw, by rubbing silk, or the like; a\n   rustling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rustler', 'n.', 'One who, or that which, rustles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rustler', 'n.', 'A bovine animal that can care for itself in any\n   circumstances; also, an alert, energetic, driving person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rustless', 'a.', 'Free from rust.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rusty', 'superl.', 'Covered or affected with rust; as, a rusty knife or\n   sword; rusty wheat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rusty', 'superl.', 'Impaired by inaction, disuse, or neglect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rusty', 'superl.', 'Discolored and rancid; reasty; as, rusty bacon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rusty', 'superl.', 'Surly; morose; crusty; sullen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rusty', 'superl.', 'Rust-colored; dark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rusty', 'superl.', 'Discolored; stained; not cleanly kept; filthy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rusty', 'superl.', 'Resembling, or covered with a substance resembling,\n   rust; affected with rust; rubiginous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rut', 'n.', 'Sexual desire or oestrus of deer, cattle, and various other\n   mammals; heat; also, the period during which the oestrus exists.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rut', 'n.', 'Roaring, as of waves breaking upon the shore; rote. See Rote.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rutted', 'imp. & p. p.', 'of Rut');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rutting', 'p. pr. & vb. n.', 'of Rut');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rut', 'v. i.', 'To have a strong sexual impulse at the reproductive period;\n   -- said of deer, cattle, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rut', 'v. t.', 'To cover in copulation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rut', 'n.', 'A track worn by a wheel or by habitual passage of anything; a\n   groove in which anything runs. Also used figuratively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rut', 'v. t.', 'To make a rut or ruts in; -- chiefly used as a past\n   participle or a participial adj.; as, a rutted road.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruta-baga', 'n.', 'A kind of turnip commonly with a large and long or ovoid\n   yellowish root; a Swedish turnip. See Turnip.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rutaceous', 'a.', 'Of or pertaining to plants of a natural order (Rutaceae)\n   of which the rue is the type, and which includes also the orange,\n   lemon, dittany, and buchu.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rutate', 'n.', 'A salt of rutic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruth', 'v.', 'Sorrow for the misery of another; pity; tenderness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruth', 'v.', 'That which causes pity or compassion; misery; distress; a\n   pitiful sight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruthenic', 'a.', 'Pertaining to, or containing, ruthenium; specifically,\n   designating those compounds in which it has a higher valence as\n   contrasted with ruthenious compounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruthenious', 'a.', 'Pertaining to, or containing, ruthenium; specifically,\n   designating those compounds in which it has a lower valence as\n   contrasted with ruthenic compounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruthenium', 'n.', 'A rare element of the light platinum group, found\n   associated with platinum ores, and isolated as a hard, brittle\n   steel-gray metal which is very infusible. Symbol Ru. Atomic weight\n   103.5. Specific gravity 12.26. See Platinum metals, under Platinum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruthful', 'a.', 'Full of ruth');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruthful', 'a.', 'Pitiful; tender.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruthful', 'a.', 'Full of sorrow; woeful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruthful', 'a.', 'Causing sorrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruthless', 'a.', 'Having no ruth; cruel; pitiless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rutic', 'a.', 'Pertaining to, or obtained from, rue (Ruta); as, rutic acid,\n   now commonly called capric acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rutilant', 'a.', 'Having a reddish glow; shining.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rutilate', 'v. i.', 'To shine; to emit rays of light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rutile', 'n.', 'A mineral usually of a reddish brown color, and brilliant\n   metallic adamantine luster, occurring in tetragonal crystals. In\n   composition it is titanium dioxide, like octahedrite and brookite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rutilian', 'n.', 'Any species of lamellicorn beetles belonging to Rutila\n   and allied genera, as the spotted grapevine beetle (Pelidnota\n   punctata).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rutin', 'n.', 'A glucoside resembling, but distinct from, quercitrin. Rutin\n   is found in the leaves of the rue (Ruta graveolens) and other plants,\n   and obtained as a bitter yellow crystalline substance which yields\n   quercitin on decomposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rutinose.', '', 'A disaccharide present in glycosides.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rutter', 'n.', 'A horseman or trooper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rutter', 'n.', 'That which ruts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rutterkin', 'n.', 'An old crafty fox or beguiler -- a word of contempt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruttier', 'n.', 'A chart of a course, esp. at sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruttish', 'a.', 'Inclined to rut; lustful; libidinous; salacious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ruttle', 'n.', 'A rattling sound in the throat arising from difficulty of\n   breathing; a rattle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rutty', 'a.', 'Ruttish; lustful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rutty', 'a.', 'Full of ruts; as, a rutty road.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rutty', 'a.', 'Rooty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rutylene', 'n.', 'A liquid hydrocarbon, C10H18, of the acetylene series. It\n   is produced artificially.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ryal', 'a.', 'Royal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ryal', 'n.', 'See Rial, an old English coin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ryder', 'n.', 'A clause added to a document; a rider. See Rider.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ryder', 'n.', 'A gold coin of Zealand [Netherlands] equal to 14 florins,\n   about $ 5.60.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rye', 'n.', 'A grain yielded by a hardy cereal grass (Secale cereale),\n   closely allied to wheat; also, the plant itself. Rye constitutes a\n   large portion of the breadstuff used by man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rye', 'n.', 'A disease in a hawk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rynd', 'n.', 'A piece of iron crossing the hole in the upper millstone by\n   which the stone is supported on the spindle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ryot', 'n.', 'A peasant or cultivator of the soil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rypophagous', 'a.', 'Eating, or subsisting on, filth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rys', 'n.', 'A branch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rysh', 'n.', 'Rush, a plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rysimeter', 'n.', 'See Rhysimeter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ryth', 'n.', 'A ford.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Rytina', 'n.', 'A genus of large edentulous sirenians, allied to the dugong\n   and manatee, including but one species (R. Stelleri); -- called also\n   Stellers sea cow.\n   S () the nineteenth letter of the English alphabet, is a consonant, and\n   is often called a sibilant, in allusion to its hissing sound. It has\n   two principal sounds; one a mere hissing, as in sack, this; the other a\n   vocal hissing (the same as that of z), as in is, wise. Besides these it\n   sometimes has the sounds of sh and zh, as in sure, measure. It\n   generally has its hissing sound at the beginning of words, but in the\n   middle and at the end of words its sound is determined by usage. In a\n   few words it is silent, as in isle, debris. With the letter h it forms\n   the digraph sh. See Guide to pronunciation, // 255-261.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-s', '', 'The suffix used to form the plural of most words; as in roads,\n   elfs, sides, accounts.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-s', '', 'The suffix used to form the third person singular indicative of\n   English verbs; as in falls, tells, sends.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-s', '', 'An adverbial suffix; as in towards, needs, always, -- originally\n   the genitive, possesive, ending. See -s.');
  INSERT INTO words (word, word_type, description) VALUES  (    's', '', 'A contraction for is or (colloquially) for has.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saadh', 'n.', 'See Sadh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saan', 'n. pl.', 'Same as Bushmen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sabadilla', 'n.', 'A Mexican liliaceous plant (Schoenocaulon officinale);\n   also, its seeds, which contain the alkaloid veratrine. It was formerly\n   used in medicine as an emetic and purgative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sabaean', 'a. & n.', 'Same as Sabian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sabaeanism', 'n.', 'Same as Sabianism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sabaeism', 'n.', 'Alt. of Sabaism');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sabaism', 'n.', 'See Sabianism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sabal', 'n.', 'A genus of palm trees including the palmetto of the Southern\n   United States.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sabaoth', 'n. pl.', 'Armies; hosts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sabaoth', 'n. pl.', 'Incorrectly, the Sabbath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sabbat', 'n.', 'In mediaeval demonology, the nocturnal assembly in which\n   demons and sorcerers were thought to celebrate their orgies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sabbatarian', 'n.', 'One who regards and keeps the seventh day of the week\n   as holy, agreeably to the letter of the fourth commandment in the\n   Decalogue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sabbatarian', 'n.', 'A strict observer of the Sabbath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sabbatarian', 'a.', 'Of or pertaining to the Sabbath, or the tenets of\n   Sabbatarians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sabbatarianism', 'n.', 'The tenets of Sabbatarians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sabbath', 'n.', 'A season or day of rest; one day in seven appointed for\n   rest or worship, the observance of which was enjoined upon the Jews in\n   the Decalogue, and has been continued by the Christian church with a\n   transference of the day observed from the last to the first day of the\n   week, which is called also Lords Day.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sabbath', 'n.', 'The seventh year, observed among the Israelites as one of\n   rest and festival.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sabbath', 'n.', 'Fig.: A time of rest or repose; intermission of pain,\n   effort, sorrow, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sabbathless', 'a.', 'Without Sabbath, or intermission of labor; hence,\n   without respite or rest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sabbatic', 'a.', 'Alt. of Sabbatical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sabbatical', 'a.', 'Of or pertaining to the Sabbath; resembling the\n   Sabbath; enjoying or bringing an intermission of labor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sabbatism', 'n.', 'Intermission of labor, as upon the Sabbath; rest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sabbaton', 'n.', 'A round-toed, armed covering for the feet, worn during a\n   part of the sixteenth century in both military and civil dress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sabean', 'a. & n.', 'Same as Sabian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sabeism', 'n.', 'Same as Sabianism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sabella', 'n.', 'A genus of tubicolous annelids having a circle of plumose\n   gills around the head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sabellian', 'a.', 'Pertaining to the doctrines or tenets of Sabellius. See\n   Sabellian, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sabellian', 'n.', 'A follower of Sabellius, a presbyter of Ptolemais in the\n   third century, who maintained that there is but one person in the\n   Godhead, and that the Son and Holy Spirit are only different powers,\n   operations, or offices of the one God the Father.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sabellianism', 'n.', 'The doctrines or tenets of Sabellius. See Sabellian,\n   n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sabelloid', 'a.', 'Like, or related to, the genus Sabella.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saber', 'n.', 'Alt. of Sabre');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sabre', 'n.', 'A sword with a broad and heavy blade, thick at the back, and\n   usually more or less curved like a scimiter; a cavalry sword.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sabered', 'imp. & p. p.', 'of Sabre');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sabred', '', 'of Sabre');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sabering', 'p. pr. & vb. n.', 'of Sabre');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sabring', '', 'of Sabre');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saber', 'v. t.', 'Alt. of Sabre');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sabre', 'v. t.', 'To strike, cut, or kill with a saber; to cut down, as\n   with a saber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saberbill', 'n.', 'Alt. of Sabrebill');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sabrebill', 'n.', 'The curlew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sabian', 'a.', 'Of or pertaining to Saba in Arabia, celebrated for\n   producing aromatic plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sabian', 'a.', 'Relating to the religion of Saba, or to the worship of the\n   heavenly bodies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sabian', 'n.', 'An adherent of the Sabian religion; a worshiper of the\n   heavenly bodies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sabianism', 'n.', 'The doctrine of the Sabians; the Sabian religion; that\n   species of idolatry which consists in worshiping the sun, moon, and\n   stars; heliolatry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sabicu', 'n.', 'The very hard wood of a leguminous West Indian tree\n   (Lysiloma Sabicu), valued for shipbuilding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sabine', 'a.', 'Of or pertaining to the ancient Sabines, a people of Italy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sabine', 'n.', 'One of the Sabine people.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sabine', 'n.', 'See Savin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sable', 'n.', 'A carnivorous animal of the Weasel family (Mustela\n   zibellina) native of the northern latitudes of Europe, Asia, and\n   America, -- noted for its fine, soft, and valuable fur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sable', 'n.', 'The fur of the sable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sable', 'n.', 'A mourning garment; a funeral robe; -- generally in the\n   plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sable', 'n.', 'The tincture black; -- represented by vertical and\n   horizontal lines crossing each other.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sable', 'a.', 'Of the color of the sables fur; dark; black; -- used\n   chiefly in poetry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sabled', 'imp. & p. p.', 'of Sable');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sabling', 'p. pr. & vb. n.', 'of Sable');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sable', 'v. t.', 'To render sable or dark; to drape darkly or in black.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sabot', 'n.', 'A kind of wooden shoe worn by the peasantry in France,\n   Belgium, Sweden, and some other European countries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sabot', 'n.', 'A thick, circular disk of wood, to which the cartridge bag\n   and projectile are attached, in fixed ammunition for cannon; also, a\n   piece of soft metal attached to a projectile to take the groove of the\n   rifling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sabotiere', 'n.', 'A kind of freezer for ices.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sabre', 'n. & v.', 'See Saber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sabretasche', 'n.', 'A leather case or pocket worn by cavalry at the left\n   side, suspended from the sword belt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sabrina work', '', 'A variety of applique work for quilts, table covers,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sabulose', 'a.', 'Growing in sandy places.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sabulosity', 'n.', 'The quality of being sabulous; sandiness; grittiness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sabulous', 'a.', 'Sandy; gritty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sac', 'n.', 'See Sacs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sac', 'n.', 'The privilege formerly enjoyed by the lord of a manor, of\n   holding courts, trying causes, and imposing fines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sac', 'n.', 'See 2d Sack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sac', 'n.', 'A cavity, bag, or receptacle, usually containing fluid, and\n   either closed, or opening into another cavity to the exterior; a sack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sacalait', 'n.', 'A kind of fresh-water bass; the crappie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sacar', 'n.', 'See Saker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saccade', 'n.', 'A sudden, violent check of a horse by drawing or twitching\n   the reins on a sudden and with one pull.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saccate', 'a.', 'Having the form of a sack or pouch; furnished with a sack\n   or pouch, as a petal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saccate', 'a.', 'Of or pertaining to the Saccata, a suborder of ctenophores\n   having two pouches into which the long tentacles can be retracted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saccharate', 'n.', 'A salt of saccharic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saccharate', 'n.', 'In a wider sense, a compound of saccharose, or any\n   similar carbohydrate, with such bases as the oxides of calcium, barium,\n   or lead; a sucrate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saccharic', 'a.', 'Of, pertaining to, or obtained from, saccharine\n   substances; specifically, designating an acid obtained, as a white\n   amorphous gummy mass, by the oxidation of mannite, glucose, sucrose,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sacchariferous', 'a.', 'Producing sugar; as, sacchariferous canes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saccharified', 'imp. & p. p.', 'of Saccharify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saccharifying', 'p. pr. & vb. n.', 'of Saccharify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saccharify', 'v. t.', 'To convert into, or to impregnate with, sugar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saccharilla', 'n.', 'A kind of muslin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saccharimeter', 'n.', 'An instrument for ascertaining the quantity of\n   saccharine matter in any solution, as the juice of a plant, or brewers\n   and distillers worts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saccharimetrical', 'a.', 'Of or pertaining to saccharimetry; obtained by\n   saccharimetry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saccharimetry', 'n.', 'The act, process or method of determining the amount\n   and kind of sugar present in sirup, molasses, and the like, especially\n   by the employment of polarizing apparatus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saccharin', 'n.', 'A bitter white crystalline substance obtained from the\n   saccharinates and regarded as the lactone of saccharinic acid; -- so\n   called because formerly supposed to be isomeric with cane sugar\n   (saccharose).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saccharinate', 'n.', 'A salt of saccharinic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saccharinate', 'n.', 'A salt of saccharine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saccharine', 'a.', 'Of or pertaining to sugar; having the qualities of\n   sugar; producing sugar; sweet; as, a saccharine taste; saccharine\n   matter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saccharine', 'n.', 'A trade name for benzoic sulphinide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saccharinic', 'a.', 'Of, pertaining to, or derived from, saccharin;\n   specifically, designating a complex acid not known in the free state\n   but well known in its salts, which are obtained by boiling dextrose and\n   levulose (invert sugar) with milk of lime.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saccharized', 'imp. & p. p.', 'of Saccharize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saccharizing', 'p. pr. & vb. n.', 'of Saccharize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saccharize', 'v. t.', 'To convert into, or to impregnate with, sugar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saccharoid', 'a.', 'Alt. of Saccharoidal');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saccharoidal', 'a.', 'Resembling sugar, as in taste, appearance,\n   consistency, or composition; as, saccharoidal limestone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saccharometer', 'n.', 'A saccharimeter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saccharomyces', 'n.', 'A genus of budding fungi, the various species of\n   which have the power, to a greater or less extent, or splitting up\n   sugar into alcohol and carbonic acid. They are the active agents in\n   producing fermentation of wine, beer, etc. Saccharomyces cerevisiae is\n   the yeast of sedimentary beer. Also called Torula.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saccharomycetes', 'n. pl.', 'A family of fungi consisting of the one genus\n   Saccharomyces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saccharonate', 'n.', 'A salt of saccharonic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saccharone', 'n.', 'A white crystalline substance, C6H8O6, obtained by the\n   oxidation of saccharin, and regarded as the lactone of saccharonic\n   acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saccharone', 'n.', 'An oily liquid, C6H10O2, obtained by the reduction of\n   saccharin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saccharonic', 'a.', 'Of, pertaining to, or derived from, saccharone;\n   specifically, designating an unstable acid which is obtained from\n   saccharone (a) by hydration, and forms a well-known series of salts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saccharose', 'n.', 'Cane sugar; sucrose; also, in general, any one of the\n   group of which saccharose, or sucrose proper, is the type. See Sucrose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saccharous', 'a.', 'Saccharine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saccharum', 'n.', 'A genus of tall tropical grasses including the sugar\n   cane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saccholactate', 'n.', 'A salt of saccholactic acid; -- formerly called also\n   saccholate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saccholactic', 'a.', 'Of, pertaining to, or designating, an acid now called\n   mucic acid; saccholic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saccholic', 'a.', 'Saccholactic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sacchulmate', 'n.', 'A salt of sacchulmic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sacchulmic', 'a.', 'Of, pertaining to, or designating, an acid obtained as\n   a dark amorphous substance by the long-continued boiling of sucrose\n   with very dilute sulphuric acid. It resembles humic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sacchulmin', 'n.', 'An amorphous huminlike substance resembling sacchulmic\n   acid, and produced together with it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sacciferous', 'a.', 'Bearing a sac.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sacciform', 'a.', 'Having the general form of a sac.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saccoglossa', 'n. pl.', 'Same as Pellibranchiata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saccular', 'a.', 'Like a sac; sacciform.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sacculated', 'a.', 'Furnished with little sacs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saccule', 'n.', 'A little sac; specifically, the sacculus of the ear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sacculo-cochlear', 'a.', 'Pertaining to the sacculus and cochlea of the\n   ear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sacculo-utricular', 'a.', 'Pertaining to the sacculus and utriculus of the\n   ear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sacculi', 'pl. ', 'of Sacculus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sacculus', 'n.', 'A little sac; esp., a part of the membranous labyrinth of\n   the ear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sacci', 'pl. ', 'of Saccus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saccus', 'n.', 'A sac.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sacella', 'pl. ', 'of Sacellum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sacellum', 'n.', 'An unroofed space consecrated to a divinity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sacellum', 'n.', 'A small monumental chapel in a church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sacerdotal', 'a.', 'Of or pertaining to priests, or to the order of\n   priests; relating to the priesthood; priesty; as, sacerdotal dignity;\n   sacerdotal functions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sacerdotalism', 'm.', 'The system, style, spirit, or character, of a\n   priesthood, or sacerdotal order; devotion to the interests of the\n   sacerdotal order.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sacerdotally', 'adv.', 'In a sacerdotal manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sachel', 'n.', 'A small bag.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sachem', 'n.', 'A chief of a tribe of the American Indians; a sagamore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sachemdom', 'n.', 'The government or jurisdiction of a sachem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sachemship', 'n.', 'Office or condition of a sachem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sachet', 'n.', 'A scent bag, or perfume cushion, to be laid among\n   handkerchiefs, garments, etc., to perfume them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saciety', 'n.', 'Satiety.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sack', 'n.', 'A name formerly given to various dry Spanish wines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sack', 'n.', 'A bag for holding and carrying goods of any kind; a\n   receptacle made of some kind of pliable material, as cloth, leather,\n   and the like; a large pouch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sack', 'n.', 'A measure of varying capacity, according to local usage and\n   the substance. The American sack of salt is 215 pounds; the sack of\n   wheat, two bushels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sack', 'n.', 'Originally, a loosely hanging garment for women, worn like a\n   cloak about the shoulders, and serving as a decorative appendage to the\n   gown; now, an outer garment with sleeves, worn by women; as, a dressing\n   sack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sack', 'n.', 'A sack coat; a kind of coat worn by men, and extending from\n   top to bottom without a cross seam.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sack', 'n.', 'See 2d Sac, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sack', 'n.', 'Bed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sack', 'v. t.', 'To put in a sack; to bag; as, to sack corn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sack', 'v. t.', 'To bear or carry in a sack upon the back or the shoulders.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sack', 'n.', 'The pillage or plunder, as of a town or city; the storm and\n   plunder of a town; devastation; ravage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sacked', 'imp. & p. p.', 'of Sack');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sacking', 'p. pr. & vb. n.', 'of Sack');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sack', 'v. t.', 'To plunder or pillage, as a town or city; to devastate; to\n   ravage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sackage', 'n.', 'The act of taking by storm and pillaging; sack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sackbut', 'n.', 'A brass wind instrument, like a bass trumpet, so contrived\n   that it can be lengthened or shortened according to the tone required;\n   -- said to be the same as the trombone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sackcloth', 'n.', 'Linen or cotton cloth such as sacks are made of; coarse\n   cloth; anciently, a cloth or garment worn in mourning, distress,\n   mortification, or penitence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sackclothed', 'a.', 'Clothed in sackcloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sacker', 'n.', 'One who sacks; one who takes part in the storm and pillage\n   of a town.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sackfuls', 'pl. ', 'of Sackful');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sackful', 'n.', 'As much as a sack will hold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sackful', 'a.', 'Bent on plunder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sacking', 'n.', 'Stout, coarse cloth of which sacks, bags, etc., are made.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sackless', 'a.', 'Quiet; peaceable; harmless; innocent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sack-winged', 'a.', 'Having a peculiar pouch developed near the front edge\n   of the wing; -- said of certain bats of the genus Saccopteryx.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sacque', 'n.', 'Same as 2d Sack, 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sacral', 'a.', 'Of or pertaining to the sacrum; in the region of the\n   sacrum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sacrament', 'n.', 'The oath of allegiance taken by Roman soldiers; hence, a\n   sacred ceremony used to impress an obligation; a solemn oath-taking; an\n   oath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sacrament', 'n.', 'The pledge or token of an oath or solemn covenant; a\n   sacred thing; a mystery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sacrament', 'n.', 'One of the solemn religious ordinances enjoined by\n   Christ, the head of the Christian church, to be observed by his\n   followers; hence, specifically, the eucharist; the Lords Supper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sacrament', 'v. t.', 'To bind by an oath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sacramental', 'a.', 'Of or pertaining to a sacrament or the sacraments; of\n   the nature of a sacrament; sacredly or solemnly binding; as,\n   sacramental rites or elements.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sacramental', 'a.', 'Bound by a sacrament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sacramental', 'n.', 'That which relates to a sacrament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sacramentalism', 'n.', 'The doctrine and use of sacraments; attachment of\n   excessive importance to sacraments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sacramentalist', 'n.', 'One who holds the doctrine of the real objective\n   presence of Christs body and blood in the holy eucharist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sacramentally', 'adv.', 'In a sacramental manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sacramentarian', 'n.', 'A name given in the sixteenth century to those\n   German reformers who rejected both the Roman and the Lutheran doctrine\n   of the holy eucharist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sacramentarian', 'n.', 'One who holds extreme opinions regarding the\n   efficacy of sacraments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sacramentarian', 'a.', 'Of or pertaining a sacrament, or to the\n   sacramentals; sacramental.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sacramentarian', 'a.', 'Of or pertaining to the Sacramentarians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sacramentary', 'a.', 'Of or pertaining to a sacrament or the sacraments;\n   sacramental.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sacramentary', 'a.', 'Of or pertaining to the Sacramentarians.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-ries', 'pl. ', 'of Sacramentary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sacramentary', 'n.', 'An ancient book of the Roman Catholic Church, written\n   by Pope Gelasius, and revised, corrected, and abridged by St. Gregory,\n   in which were contained the rites for Mass, the sacraments, the\n   dedication of churches, and other ceremonies. There are several ancient\n   books of the same kind in France and Germany.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sacramentary', 'n.', 'Same as Sacramentarian, n., 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sacramentize', 'v. i.', 'To administer the sacraments.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-ria', 'pl. ', 'of Sacrarium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sacrarium', 'n.', 'A sort of family chapel in the houses of the Romans,\n   devoted to a special divinity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sacrarium', 'n.', 'The adytum of a temple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sacrarium', 'n.', 'In a Christian church, the sanctuary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sacrate', 'v. t.', 'To consecrate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sacration', 'n.', 'Consecration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sacre', 'n.', 'See Saker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sacre', 'v. t.', 'To consecrate; to make sacred.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sacred', 'a.', 'Set apart by solemn religious ceremony; especially, in a\n   good sense, made holy; set apart to religious use; consecrated; not\n   profane or common; as, a sacred place; a sacred day; sacred service.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sacred', 'a.', 'Relating to religion, or to the services of religion; not\n   secular; religious; as, sacred history.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sacred', 'a.', 'Designated or exalted by a divine sanction; possessing the\n   highest title to obedience, honor, reverence, or veneration; entitled\n   to extreme reverence; venerable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sacred', 'a.', 'Hence, not to be profaned or violated; inviolable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sacred', 'a.', 'Consecrated; dedicated; devoted; -- with to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sacred', 'a.', 'Solemnly devoted, in a bad sense, as to evil, vengeance,\n   curse, or the like; accursed; baleful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sacrific', 'a.', 'Alt. of Sacrifical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sacrifical', 'a.', 'Employed in sacrifice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sacrificable', 'a.', 'Capable of being offered in sacrifice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sacrificant', 'n.', 'One who offers a sacrifice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sacrificator', 'n.', 'A sacrificer; one who offers a sacrifice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sacrificatory', 'n.', 'Offering sacrifice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sacrifice', 'n.', 'The offering of anything to God, or to a god;\n   consecratory rite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sacrifice', 'n.', 'Anything consecrated and offered to God, or to a\n   divinity; an immolated victim, or an offering of any kind, laid upon an\n   altar, or otherwise presented in the way of religious thanksgiving,\n   atonement, or conciliation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sacrifice', 'n.', 'Destruction or surrender of anything for the sake of\n   something else; devotion of some desirable object in behalf of a higher\n   object, or to a claim deemed more pressing; hence, also, the thing so\n   devoted or given up; as, the sacrifice of interest to pleasure, or of\n   pleasure to interest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sacrifice', 'n.', 'A sale at a price less than the cost or the actual\n   value.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sacrificed', 'imp. & p. p.', 'of Sacrifice');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sacrificing', 'p. pr. & vb. n.', 'of Sacrifice');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sacrifice', 'n.', 'To make an offering of; to consecrate or present to a\n   divinity by way of expiation or propitiation, or as a token\n   acknowledgment or thanksgiving; to immolate on the altar of God, in\n   order to atone for sin, to procure favor, or to express thankfulness;\n   as, to sacrifice an ox or a sheep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sacrifice', 'n.', 'Hence, to destroy, surrender, or suffer to be lost, for\n   the sake of obtaining something; to give up in favor of a higher or\n   more imperative object or duty; to devote, with loss or suffering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sacrifice', 'n.', 'To destroy; to kill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sacrifice', 'n.', 'To sell at a price less than the cost or the actual\n   value.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sacrifice', 'v. i.', 'To make offerings to God, or to a deity, of things\n   consumed on the altar; to offer sacrifice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sacrificer', 'n.', 'One who sacrifices.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sacrificial', 'a.', 'Of or pertaining to sacrifice or sacrifices;\n   consisting in sacrifice; performing sacrifice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sacrilege', 'n.', 'The sin or crime of violating or profaning sacred\n   things; the alienating to laymen, or to common purposes, what has been\n   appropriated or consecrated to religious persons or uses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sacrilegious', 'a.', 'Violating sacred things; polluted with sacrilege;\n   involving sacrilege; profane; impious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sacrilegist', 'n.', 'One guilty of sacrilege.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sacring', '', 'a. & n. from Sacre.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sacrist', 'n.', 'A sacristan; also, a person retained in a cathedral to\n   copy out music for the choir, and take care of the books.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sacristan', 'n.', 'An officer of the church who has the care of the\n   utensils or movables, and of the church in general; a sexton.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sacristies', 'pl. ', 'of Sacristy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sacristy', 'n.', 'An apartment in a church where the sacred utensils,\n   vestments, etc., are kept; a vestry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sacro-', '', 'A combining form denoting connection with, or relation to,\n   the sacrum, as in sacro-coccygeal, sacro-iliac, sacrosciatic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sacrosanct', 'a.', 'Sacred; inviolable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sacrosciatic', 'a.', 'Of or pertaining to both the sacrum and the hip; as,\n   the sacrosciatic foramina formed by the sacrosciatic ligaments which\n   connect the sacrum and the hip bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sacrovertebral', 'a.', 'Of or pertaining to the sacrum and that part of the\n   vertebral column immediately anterior to it; as, the sacrovertebral\n   angle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'sacra', 'pl. ', 'of Sacrum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sacrum', 'n.', 'That part of the vertebral column which is directly\n   connected with, or forms a part of, the pelvis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sacs', 'n. pl.', 'A tribe of Indians, which, together with the Foxes,\n   formerly occupied the region about Green Bay, Wisconsin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sad', 'supperl.', 'Sated; satisfied; weary; tired.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sad', 'supperl.', 'Heavy; weighty; ponderous; close; hard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sad', 'supperl.', 'Dull; grave; dark; somber; -- said of colors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sad', 'supperl.', 'Serious; grave; sober; steadfast; not light or\n   frivolous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sad', 'supperl.', 'Affected with grief or unhappiness; cast down with\n   affliction; downcast; gloomy; mournful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sad', 'supperl.', 'Afflictive; calamitous; causing sorrow; as, a sad\n   accident; a sad misfortune.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sad', 'supperl.', 'Hence, bad; naughty; troublesome; wicked.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sad', 'v. t.', 'To make sorrowful; to sadden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sadda', 'n.', 'A work in the Persian tongue, being a summary of the\n   Zend-Avesta, or sacred books.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saddened', 'imp. & p. p.', 'of Sadden');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saddening', 'p. pr. & vb. n.', 'of Sadden');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sadden', 'v. t.', 'To make sad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sadden', 'v. t.', 'To render heavy or cohesive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sadden', 'v. t.', 'To make dull- or sad-colored, as cloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sadden', 'v. t.', 'To make grave or serious; to make melancholy or\n   sorrowful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sadden', 'v. i.', 'To become, or be made, sad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sadder', 'n.', 'Same as Sadda.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saddle', 'n.', 'A seat for a rider, -- usually made of leather, padded to\n   span comfortably a horses back, furnished with stirrups for the\n   riders feet to rest in, and fastened in place with a girth; also, a\n   seat for the rider on a bicycle or tricycle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saddle', 'n.', 'A padded part of a harness which is worn on a horses back,\n   being fastened in place with a girth. It serves various purposes, as to\n   keep the breeching in place, carry guides for the reins, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saddle', 'n.', 'A piece of meat containing a part of the backbone of an\n   animal with the ribs on each side; as, a saddle of mutton, of venison,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saddle', 'n.', 'A block of wood, usually fastened to some spar, and shaped\n   to receive the end of another spar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saddle', 'n.', 'A part, as a flange, which is hollowed out to fit upon a\n   convex surface and serve as a means of attachment or support.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saddle', 'n.', 'The clitellus of an earthworm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saddle', 'n.', 'The threshold of a door, when a separate piece from the\n   floor or landing; -- so called because it spans and covers the joint\n   between two floors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saddled', 'imp. & p. p.', 'of Saddle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saddling', 'p. pr. & vb. n.', 'of Saddle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saddle', 'v. t.', 'To put a saddle upon; to equip (a beast) for riding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saddle', 'v. t.', 'Hence: To fix as a charge or burden upon; to load; to\n   encumber; as, to saddle a town with the expense of bridges and\n   highways.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saddleback', 'a.', 'Same as Saddle-backed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saddleback', 'n.', 'Anything saddle-backed; esp., a hill or ridge having a\n   concave outline at the top.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saddleback', 'n.', 'The harp seal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saddleback', 'n.', 'The great blackbacked gull (Larus marinus).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saddleback', 'n.', 'The larva of a bombycid moth (Empretia stimulea) which\n   has a large, bright green, saddle-shaped patch of color on the back.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saddle-backed', 'a.', 'Having the outline of the upper part concave like\n   the seat of a saddle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saddle-backed', 'a.', 'Having a low back and high neck, as a horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saddlebags', 'n. pl.', 'Bags, usually of leather, united by straps or a\n   band, formerly much used by horseback riders to carry small articles,\n   one bag hanging on each side.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saddlebow', 'n.', 'The bow or arch in the front part of a saddle, or the\n   pieces which form the front.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saddlecloth', 'n.', 'A cloth under a saddle, and extending out behind; a\n   housing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saddled', 'a.', 'Having a broad patch of color across the back, like a\n   saddle; saddle-backed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saddler', 'n.', 'One who makes saddles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saddler', 'n.', 'A harp seal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saddlery', 'n.', 'The materials for making saddles and harnesses; the\n   articles usually offered for sale in a saddlers shop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saddlery', 'n.', 'The trade or employment of a saddler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saddle-shaped', 'a.', 'Shaped like a saddle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saddle-shaped', 'a.', 'Bent down at the sides so as to give the upper part\n   a rounded form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saddle-shaped', 'a.', 'Bent on each side of a mountain or ridge, without\n   being broken at top; -- said of strata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saddletree', 'n.', 'The frame of a saddle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sadducaic', 'a.', 'Pertaining to, or like, the Sadducees; as, Sadducaic\n   reasonings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sadducee', 'n.', 'One of a sect among the ancient Jews, who denied the\n   resurrection, a future state, and the existence of angels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sadduceeism', 'n.', 'Alt. of Sadducism');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sadducism', 'n.', 'The tenets of the Sadducees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sadducized', 'imp. & p. p.', 'of Sadducize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sadducizing', 'p. pr. & vb. n.', 'of Sadducize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sadducize', 'v. i.', 'To adopt the principles of the Sadducees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sadh', 'n.', 'A member of a monotheistic sect of Hindoos. Sadhs resemble\n   the Quakers in many respects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sadiron', 'n.', 'An iron for smoothing clothes; a flatiron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sadly', 'adv.', 'Wearily; heavily; firmly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sadly', 'adv.', 'Seriously; soberly; gravely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sadly', 'adv.', 'Grievously; deeply; sorrowfully; miserably.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sadness', 'n.', 'Heaviness; firmness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sadness', 'n.', 'Seriousness; gravity; discretion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sadness', 'n.', 'Quality of being sad, or unhappy; gloominess;\n   sorrowfulness; dejection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sadr', 'n.', 'A plant of the genus Ziziphus (Z. lotus); -- so called by the\n   Arabs of Barbary, who use its berries for food. See Lotus (b).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saengerfest', 'n.', 'A festival of singers; a German singing festival.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Safe', 'superl.', 'Free from harm, injury, or risk; untouched or\n   unthreatened by danger or injury; unharmed; unhurt; secure; whole; as,\n   safe from disease; safe from storms; safe from foes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Safe', 'superl.', 'Conferring safety; securing from harm; not exposing to\n   danger; confining securely; to be relied upon; not dangerous; as, a\n   safe harbor; a safe bridge, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Safe', 'superl.', 'Incapable of doing harm; no longer dangerous; in secure\n   care or custody; as, the prisoner is safe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Safe', 'n.', 'A place for keeping things in safety.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Safe', 'n.', 'A strong and fireproof receptacle (as a movable chest of\n   steel, etc., or a closet or vault of brickwork) for containing money,\n   valuable papers, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Safe', 'n.', 'A ventilated or refrigerated chest or closet for securing\n   provisions from noxious animals or insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Safe', 'v. t.', 'To render safe; to make right.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Safe-conduct', 'n.', 'That which gives a safe passage');
  INSERT INTO words (word, word_type, description) VALUES  (    'Safe-conduct', 'n.', 'a convoy or guard to protect a person in an enemys\n   country or a foreign country');
  INSERT INTO words (word, word_type, description) VALUES  (    'Safe-conduct', 'n.', 'a writing, pass, or warrant of security, given to a\n   person to enable him to travel with safety.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Safe-conduct', 'v. t.', 'To conduct safely; to give safe-conduct to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Safeguard', 'n.', 'One who, or that which, defends or protects; defense;\n   protection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Safeguard', 'n.', 'A convoy or guard to protect a traveler or property.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Safeguard', 'n.', 'A pass; a passport; a safe-conduct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Safeguard', 'v. t.', 'To guard; to protect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Safe-keeping', 'n.', 'The act of keeping or preserving in safety from\n   injury or from escape; care; custody.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Safely', 'adv.', 'In a safe manner; danger, injury, loss, or evil\n   consequences.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Safeness', 'n.', 'The quality or state of being safe; freedom from hazard,\n   danger, harm, or loss; safety; security; as the safeness of an\n   experiment, of a journey, or of a possession.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Safe-pledge', 'n.', 'A surety for the appearance of a person at a given\n   time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Safety', 'n.', 'The condition or state of being safe; freedom from danger\n   or hazard; exemption from hurt, injury, or loss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Safety', 'n.', 'Freedom from whatever exposes one to danger or from\n   liability to cause danger or harm; safeness; hence, the quality of\n   making safe or secure, or of giving confidence, justifying trust,\n   insuring against harm or loss, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Safety', 'n.', 'Preservation from escape; close custody.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Safety', 'n.', 'Same as Safety touchdown, below.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Safflow', 'n.', 'The safflower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Safflower', 'n.', 'An annual composite plant (Carthamus tinctorius), the\n   flowers of which are used as a dyestuff and in making rouge; bastard,\n   or false, saffron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Safflower', 'n.', 'The dried flowers of the Carthamus tinctorius.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Safflower', 'n.', 'A dyestuff from these flowers. See Safranin (b).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saffron', 'n.', 'A bulbous iridaceous plant (Crocus sativus) having blue\n   flowers with large yellow stigmas. See Crocus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saffron', 'n.', 'The aromatic, pungent, dried stigmas, usually with part of\n   the stile, of the Crocus sativus. Saffron is used in cookery, and in\n   coloring confectionery, liquors, varnishes, etc., and was formerly much\n   used in medicine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saffron', 'n.', 'An orange or deep yellow color, like that of the stigmas\n   of the Crocus sativus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saffron', 'a.', 'Having the color of the stigmas of saffron flowers; deep\n   orange-yellow; as, a saffron face; a saffron streamer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saffron', 'v. t.', 'To give color and flavor to, as by means of saffron; to\n   spice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saffrony', 'a.', 'Having a color somewhat like saffron; yellowish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Safranin', 'n.', 'An orange-red dyestuff extracted from the saffron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Safranin', 'n.', 'A red dyestuff extracted from the safflower, and formerly\n   used in dyeing wool, silk, and cotton pink and scarlet; -- called also\n   Spanish red, China lake, and carthamin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Safranin', 'n.', 'An orange-red dyestuff prepared from certain nitro\n   compounds of creosol, and used as a substitute for the safflower dye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Safranine', 'n.', 'An orange-red nitrogenous dyestuff produced artificially\n   by oxidizing certain aniline derivatives, and used in dyeing silk and\n   wool; also, any one of the series of which safranine proper is the\n   type.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sagged', 'imp. & p. p.', 'of Sag');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sagging', 'p. pr. & vb. n.', 'of Sag');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sag', 'v. i.', 'To sink, in the middle, by its weight or under applied\n   pressure, below a horizontal line or plane; as, a line or cable\n   supported by its ends sags, though tightly drawn; the floor of a room\n   sags; hence, to lean, give way, or settle from a vertical position; as,\n   a building may sag one way or another; a door sags on its hinges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sag', 'v. i.', 'Fig.: To lose firmness or elasticity; to sink; to droop; to\n   flag; to bend; to yield, as the mind or spirits, under the pressure of\n   care, trouble, doubt, or the like; to be unsettled or unbalanced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sag', 'v. i.', 'To loiter in walking; to idle along; to drag or droop\n   heavily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sag', 'v. t.', 'To cause to bend or give way; to load.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sag', 'n.', 'State of sinking or bending; sagging.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sagas', 'pl. ', 'of Saga');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saga', 'n.', 'A Scandinavian legend, or heroic or mythic tradition, among\n   the Norsemen and kindred people; a northern European popular historical\n   or religious tale of olden time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sagacious', 'a.', 'Of quick sense perceptions; keen-scented; skilled in\n   following a trail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sagacious', 'a.', 'Hence, of quick intellectual perceptions; of keen\n   penetration and judgment; discerning and judicious; knowing;\n   far-sighted; shrewd; sage; wise; as, a sagacious man; a sagacious\n   remark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sagacity', 'n.', 'The quality of being sagacious; quickness or acuteness of\n   sense perceptions; keenness of discernment or penetration with\n   soundness of judgment; shrewdness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sagamore', 'n.', 'The head of a tribe among the American Indians; a chief;\n   -- generally used as synonymous with sachem, but some writters\n   distinguished between them, making the sachem a chief of the first\n   rank, and a sagamore one of the second rank.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sagamore', 'n.', 'A juice used in medicine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sagapen', 'n.', 'Sagapenum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sagapenum', 'n.', 'A fetid gum resin obtained from a species of Ferula. It\n   has been used in hysteria, etc., but is now seldom met with.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sagathy', 'n.', 'A mixed woven fabric of silk and cotton, or silk and wool;\n   sayette; also, a light woolen fabric.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sage', 'n.', 'A suffruticose labiate plant (Salvia officinalis) with\n   grayish green foliage, much used in flavoring meats, etc. The name is\n   often extended to the whole genus, of which many species are cultivated\n   for ornament, as the scarlet sage, and Mexican red and blue sage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sage', 'n.', 'The sagebrush.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sage', 'superl.', 'Having nice discernment and powers of judging; prudent;\n   grave; sagacious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sage', 'superl.', 'Proceeding from wisdom; well judged; shrewd; well\n   adapted to the purpose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sage', 'superl.', 'Grave; serious; solemn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sage', 'n.', 'A wise man; a man of gravity and wisdom; especially, a man\n   venerable for years, and of sound judgment and prudence; a grave\n   philosopher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sagebrush', 'n.', 'A low irregular shrub (Artemisia tridentata), of the\n   order Compositae, covering vast tracts of the dry alkaline regions of\n   the American plains; -- called also sagebush, and wild sage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sagely', 'adv.', 'In a sage manner; wisely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sagene', 'n.', 'A Russian measure of length equal to about seven English\n   feet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sageness', 'n.', 'The quality or state of being sage; wisdom; sagacity;\n   prudence; gravity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sagenite', 'n.', 'Acicular rutile occurring in reticulated forms imbedded\n   in quartz.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sagenitic', 'a.', 'Resembling sagenite; -- applied to quartz when\n   containing acicular crystals of other minerals, most commonly rutile,\n   also tourmaline, actinolite, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sagger', 'n.', 'A pot or case of fire clay, in which fine stoneware is\n   inclosed while baking in the kiln; a seggar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sagger', 'n.', 'The clay of which such pots or cases are made.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sagging', 'n.', 'A bending or sinking between the ends of a thing, in\n   consequence of its own, or an imposed, weight; an arching downward in\n   the middle, as of a ship after straining. Cf. Hogging.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saginate', 'v. t.', 'To make fat; to pamper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sagination', 'n.', 'The act of fattening or pampering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sagitta', 'n.', 'A small constellation north of Aquila; the Arrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sagitta', 'n.', 'The keystone of an arch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sagitta', 'n.', 'The distance from a point in a curve to the chord; also,\n   the versed sine of an arc; -- so called from its resemblance to an\n   arrow resting on the bow and string.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sagitta', 'n.', 'The larger of the two otoliths, or ear bones, found in\n   most fishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sagitta', 'n.', 'A genus of transparent, free-swimming marine worms having\n   lateral and caudal fins, and capable of swimming rapidly. It is the\n   type of the class Chaetognatha.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sagittal', 'a.', 'Of or pertaining to an arrow; resembling an arrow;\n   furnished with an arrowlike appendage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sagittal', 'a.', 'Of or pertaining to the sagittal suture; in the region of\n   the sagittal suture; rabdoidal; as, the sagittal furrow, or groove, on\n   the inner surface of the roof of the skull.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sagittal', 'a.', 'In the mesial plane; mesial; as, a sagittal section of an\n   animal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sagittarius', 'n.', 'The ninth of the twelve signs of the zodiac, which the\n   sun enters about November 22, marked thus [/] in almanacs; the Archer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sagittarius', 'n.', 'A zodiacal constellation, represented on maps and\n   globes as a centaur shooting an arrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sagittary', 'n.', 'A centaur; a fabulous being, half man, half horse, armed\n   with a bow and quiver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sagittary', 'n.', 'The Arsenal in Venice; -- so called from having a figure\n   of an archer over the door.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sagittary', 'a.', 'Pertaining to, or resembling, an arrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sagittate', 'a.', 'Shaped like an arrowhead; triangular, with the two basal\n   angles prolonged downward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sagittated', 'a.', 'Sagittal; sagittate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sagittocyst', 'n.', 'A defensive cell containing a minute rodlike structure\n   which may be expelled. Such cells are found in certain Turbellaria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sago', 'n.', 'A dry granulated starch imported from the East Indies, much\n   used for making puddings and as an article of diet for the sick; also,\n   as starch, for stiffening textile fabrics. It is prepared from the\n   stems of several East Indian and Malayan palm trees, but chiefly from\n   the Metroxylon Sagu; also from several cycadaceous plants (Cycas\n   revoluta, Zamia integrifolia, etc.).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sagoin', 'n.', 'A marmoset; -- called also sagouin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saga', 'pl. ', 'of Sagum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sagum', 'n.', 'The military cloak of the Roman soldiers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sagus', 'n.', 'A genus of palms from which sago is obtained.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sagy', 'a.', 'Full of sage; seasoned with sage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sahib', 'n.', 'Alt. of Saheb');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saheb', 'n.', 'A respectful title or appellation given to Europeans of\n   rank.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sahibah', 'n.', 'A lady; mistress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sahidic', 'a.', 'Same as Thebaic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sahlite', 'n.', 'See Salite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sahui', 'n.', 'A marmoset.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sai', 'n.', 'See Capuchin, 3 (a).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saibling', 'n.', 'A European mountain trout (Salvelinus alpinus); -- called\n   also Bavarian charr.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saic', 'n.', 'A kind of ketch very common in the Levant, which has neither\n   topgallant sail nor mizzen topsail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Said', '', 'imp. & p. p. of Say.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Said', 'a.', 'Before-mentioned; already spoken of or specified; aforesaid;\n   -- used chiefly in legal style.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saiga', 'n.', 'An antelope (Saiga Tartarica) native of the plains of\n   Siberia and Eastern Russia. The male has erect annulated horns, and\n   tufts of long hair beneath the eyes and ears.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saikyr', 'n.', 'Same as Saker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sail', 'n.', 'An extent of canvas or other fabric by means of which the\n   wind is made serviceable as a power for propelling vessels through the\n   water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sail', 'n.', 'Anything resembling a sail, or regarded as a sail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sail', 'n.', 'A wing; a van.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sail', 'n.', 'The extended surface of the arm of a windmill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sail', 'n.', 'A sailing vessel; a vessel of any kind; a craft.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sail', 'n.', 'A passage by a sailing vessel; a journey or excursion upon\n   the water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sailed', 'imp. & p. p.', 'of Sail');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sailing', 'p. pr. & vb. n.', 'of Sail');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sail', 'n.', 'To be impelled or driven forward by the action of wind upon\n   sails, as a ship on water; to be impelled on a body of water by the\n   action of steam or other power.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sail', 'n.', 'To move through or on the water; to swim, as a fish or a\n   water fowl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sail', 'n.', 'To be conveyed in a vessel on water; to pass by water; as,\n   they sailed from London to Canton.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sail', 'n.', 'To set sail; to begin a voyage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sail', 'n.', 'To move smoothly through the air; to glide through the air\n   without apparent exertion, as a bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sail', 'v. t.', 'To pass or move upon, as in a ship, by means of sails;\n   hence, to move or journey upon (the water) by means of steam or other\n   force.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sail', 'v. t.', 'To fly through; to glide or move smoothly through.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sail', 'v. t.', 'To direct or manage the motion of, as a vessel; as, to\n   sail ones own ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sailable', 'a.', 'Capable of being sailed over; navigable; as, a sailable\n   river.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sailboat', 'n.', 'A boat propelled by a sail or sails.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sailcloth', 'n.', 'Duck or canvas used in making sails.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sailer', 'n.', 'A sailor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sailer', 'n.', 'A ship or other vessel; -- with qualifying words\n   descriptive of speed or manner of sailing; as, a heavy sailer; a fast\n   sailer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sailfish', 'n.', 'The banner fish, or spikefish (Histiophorus.)');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sailfish', 'n.', 'The basking, or liver, shark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sailfish', 'n.', 'The quillback.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sailing', 'n.', 'The act of one who, or that which, sails; the motion of a\n   vessel on water, impelled by wind or steam; the act of starting on a\n   voyage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sailing', 'n.', 'The art of managing a vessel; seamanship; navigation; as,\n   globular sailing; oblique sailing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sailless', 'a.', 'Destitute of sails.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sailmaker', 'n.', 'One whose occupation is to make or repair sails.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sailor', 'n.', 'One who follows the business of navigating ships or other\n   vessels; one who understands the practical management of ships; one of\n   the crew of a vessel; a mariner; a common seaman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saily', 'a.', 'Like a sail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saim', 'n.', 'Lard; grease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saimir', 'n.', 'The squirrel monkey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sain', 'p. p.', 'Said.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sain', 'v. t.', 'To sanctify; to bless so as to protect from evil\n   influence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sainfoin', 'n.', 'A leguminous plant (Onobrychis sativa) cultivated for\n   fodder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sainfoin', 'n.', 'A kind of tick trefoil (Desmodium Canadense).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saint', 'n.', 'A person sanctified; a holy or godly person; one eminent for\n   piety and virtue; any true Christian, as being redeemed and consecrated\n   to God.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saint', 'n.', 'One of the blessed in heaven.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saint', 'n.', 'One canonized by the church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sainted', 'imp. & p. p.', 'of Saint');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sainting', 'p. pr. & vb. n.', 'of Saint');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saint', 'v. t.', 'To make a saint of; to enroll among the saints by an\n   offical act, as of the pope; to canonize; to give the title or\n   reputation of a saint to (some one).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saint', 'v. i.', 'To act or live as a saint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saintdom', 'n.', 'The state or character of a saint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sainted', 'a.', 'Consecrated; sacred; holy; pious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sainted', 'a.', 'Entered into heaven; -- a euphemism for dead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saintess', 'n.', 'A female saint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sainthood', 'n.', 'The state of being a saint; the condition of a saint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sainthood', 'n.', 'The order, or united body, of saints; saints, considered\n   collectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saintish', 'a.', 'Somewhat saintlike; -- used ironically.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saintism', 'n.', 'The character or quality of saints; also, hypocritical\n   pretense of holiness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saintlike', 'a.', 'Resembling a saint; suiting a saint; becoming a saint;\n   saintly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saintliness', 'n.', 'Quality of being saintly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saintly', 'superl.', 'Like a saint; becoming a holy person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saintologist', 'n.', 'One who writes the lives of saints.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saintship', 'n.', 'The character or qualities of a saint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saint-Simonian', 'n.', 'A follower of the Count de St. Simon, who died in\n   1825, and who maintained that the principle of property held in common,\n   and the just division of the fruits of common labor among the members\n   of society, are the true remedy for the social evils which exist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saint-Simonianism', 'n.', 'The principles, doctrines, or practice of the\n   Saint-Simonians; -- called also Saint- Simonism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saith', '', '3d pers. sing. pres. of Say.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saithe', 'n.', 'The pollock, or coalfish; -- called also sillock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saiva', 'n.', 'One of an important religious sect in India which regards\n   Siva with peculiar veneration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saivism', 'n.', 'The worship of Siva.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sajene', 'n.', 'Same as Sagene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sajou', 'n.', 'Same as Sapajou.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sake', 'n.', 'Final cause; end; purpose of obtaining; cause; motive;\n   reason; interest; concern; account; regard or respect; -- used chiefly\n   in such phrases as, for the sake of, for his sake, for mans sake, for\n   mercys sake, and the like; as, to commit crime for the sake of gain;\n   to go abroad for the sake of ones health.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saker', 'n.', 'A falcon (Falco sacer) native of Southern Europe and Asia,\n   closely resembling the lanner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saker', 'n.', 'The peregrine falcon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saker', 'n.', 'A small piece of artillery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sakeret', 'n.', 'The male of the saker (a).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saki', 'n.', 'Any one of several species of South American monkeys of the\n   genus Pithecia. They have large ears, and a long hairy tail which is\n   not prehensile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saki', 'n.', 'The alcoholic drink of Japan. It is made from rice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sakti', 'n.', 'The divine energy, personified as the wife of a deity\n   (Brahma, Vishnu, Siva, etc.); the female principle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sal', 'n.', 'An East Indian timber tree (Shorea robusta), much used for\n   building purposes. It is of a light brown color, close-grained, heavy,\n   and durable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sal', 'n.', 'Salt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salaam', 'n.', 'Same as Salam.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salaam', 'v. i.', 'To make or perform a salam.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salability', 'n.', 'The quality or condition of being salable; salableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salable', 'a.', 'Capable of being sold; fit to be sold; finding a ready\n   market.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salacious', 'n.', 'Having a propensity to venery; lustful; lecherous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salacity', 'n.', 'Strong propensity to venery; lust; lecherousness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salad', 'n.', 'A preparation of vegetables, as lettuce, celery, water\n   cress, onions, etc., usually dressed with salt, vinegar, oil, and\n   spice, and eaten for giving a relish to other food; as, lettuce salad;\n   tomato salad, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salad', 'n.', 'A dish composed of chopped meat or fish, esp. chicken or\n   lobster, mixed with lettuce or other vegetables, and seasoned with oil,\n   vinegar, mustard, and other condiments; as, chicken salad; lobster\n   salad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salade', 'n.', 'A helmet. See Sallet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salading', 'n.', 'Vegetables for salad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salaeratus', 'n.', 'See Saleratus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salagane', 'n.', 'The esculent swallow. See under Esculent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salal-berry', 'n.', 'The edible fruit of the Gaultheria Shallon, an\n   ericaceous shrub found from California northwards. The berries are\n   about the size of a common grape and of a dark purple color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salam', 'n.', 'A salutation or compliment of ceremony in the east by word\n   or act; an obeisance, performed by bowing very low and placing the\n   right palm on the forehead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salamander', 'n.', 'Any one of numerous species of Urodela, belonging to\n   Salamandra, Amblystoma, Plethodon, and various allied genera,\n   especially those that are more or less terrestrial in their habits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salamander', 'n.', 'The pouched gopher (Geomys tuza) of the Southern United\n   States.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salamander', 'n.', 'A culinary utensil of metal with a plate or disk which\n   is heated, and held over pastry, etc., to brown it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salamander', 'n.', 'A large poker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salamander', 'n.', 'Solidified material in a furnace hearth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salamandrina', 'n.', 'A suborder of Urodela, comprising salamanders.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salamandrine', 'a.', 'Of, pertaining to, or resembling, a salamander;\n   enduring fire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salamandroid', 'a.', 'Like or pertaining to the salamanders.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salamandroidea', 'n. pl.', 'A division of Amphibia including the\n   Salamanders and allied groups; the Urodela.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salamstone', 'n.', 'A kind of blue sapphire brought from Ceylon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salangana', 'n.', 'The salagane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salaried', 'a.', 'Receiving a salary; paid by a salary; having a salary\n   attached; as, a salaried officer; a salaried office.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salary', 'a.', 'Saline');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salaries', 'pl. ', 'of Salary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salary', 'n.', 'The recompense or consideration paid, or stipulated to be\n   paid, to a person at regular intervals for services; fixed wages, as by\n   the year, quarter, or month; stipend; hire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salaried', 'imp. & p. p.', 'of Salary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salarying', 'p. pr. & vb. n.', 'of Salary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salary', 'v. t.', 'To pay, or agree to pay, a salary to; to attach salary\n   to; as, to salary a clerk; to salary a position.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sale', 'n.', 'See 1st Sallow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sale', 'v. t.', 'The act of selling; the transfer of property, or a\n   contract to transfer the ownership of property, from one person to\n   another for a valuable consideration, or for a price in money.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sale', 'v. t.', 'Opportunity of selling; demand; market.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sale', 'v. t.', 'Public disposal to the highest bidder, or exposure of\n   goods in market; auction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saleable', 'adv.', 'Alt. of Saleably');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saleably', 'adv.', 'See Salable, Salably, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saleb', 'n.', 'See Salep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salebrosity', 'n.', 'Roughness or ruggedness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salebrous', 'a.', 'Rough; rugged.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salep', 'n.', 'The dried tubers of various species of Orchis, and Eulophia.\n   It is used to make a nutritious beverage by treating the powdered\n   preparation with hot water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saleratus', 'n.', 'Aerated salt; a white crystalline substance having an\n   alkaline taste and reaction, consisting of sodium bicarbonate (see\n   under Sodium.) It is largely used in cooking, with sour milk (lactic\n   acid) or cream of tartar as a substitute for yeast. It is also an\n   ingredient of most baking powders, and is used in the preparation of\n   effervescing drinks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salesmen', 'pl. ', 'of Salesman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salesman', 'n.', 'One who sells anything; one whose occupation is to sell\n   goods or merchandise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saleswomen', 'pl. ', 'of Saleswoman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saleswoman', 'n.', 'A woman whose occupation is to sell goods or\n   merchandise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salework', 'n.', 'Work or things made for sale; hence, work done carelessly\n   or slightingly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salian', 'a.', 'Denoting a tribe of Franks who established themselves early\n   in the fourth century on the river Sala [now Yssel]; Salic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salian', 'n.', 'A Salian Frank.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saliant', 'a.', 'Same as Salient.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saliaunce', 'a.', 'Salience; onslaught.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salic', 'a.', 'Of or pertaining to the Salian Franks, or to the Salic law\n   so called.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salicaceous', 'a.', 'Belonging or relating to the willow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salicin', 'n.', 'A glucoside found in the bark and leaves of several\n   species of willow (Salix) and poplar, and extracted as a bitter white\n   crystalline substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salicyl', 'n.', 'The hypothetical radical of salicylic acid and of certain\n   related compounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salicylal', 'n.', 'A thin, fragrant, colorless oil, HO.C6H4.CHO, found in\n   the flowers of meadow sweet (Spiraea), and also obtained by oxidation\n   of salicin, saligenin, etc. It reddens on exposure. Called also\n   salicylol, salicylic aldehyde, and formerly salicylous, / spiroylous,\n   acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salicylate', 'n.', 'A salt of salicylic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salicylic', 'a.', 'Pertaining to, derived from, or designating, an acid\n   formerly obtained by fusing salicin with potassium hydroxide, and now\n   made in large quantities from phenol (carbolic acid) by the action of\n   carbon dioxide on heated sodium phenolate. It is a white crystalline\n   substance. It is used as an antiseptic, and in its salts in the\n   treatment of rheumatism. Called also hydroxybenzoic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salicylide', 'n.', 'A white crystalline substance obtained by dehydration\n   of salicylic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salicylite', 'n.', 'A compound of salicylal; -- named after the analogy of\n   a salt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salicylol', 'n.', 'Same as Salicylal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salicylous', 'a.', 'Pertaining to, or designating, a substance formerly\n   called salicylous acid, and now salicylal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salience', 'n.', 'The quality or condition of being salient; a leaping; a\n   springing forward; an assaulting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salience', 'n.', 'The quality or state of projecting, or being projected;\n   projection; protrusion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saliency', 'n.', 'Quality of being salient; hence, vigor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salient', 'v. i.', 'Moving by leaps or springs; leaping; bounding; jumping.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salient', 'v. i.', 'Shooting out or up; springing; projecting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salient', 'v. i.', 'Hence, figuratively, forcing itself on the attention;\n   prominent; conspicuous; noticeable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salient', 'v. i.', 'Projecting outwardly; as, a salient angle; -- opposed\n   to reentering. See Illust. of Bastion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salient', 'v. i.', 'Represented in a leaping position; as, a lion salient.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salient', 'a.', 'A salient angle or part; a projection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saliently', 'adv.', 'In a salient manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saliferous', 'a.', 'Producing, or impregnated with, salt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salifiable', 'a.', 'Capable of neutralizing an acid to form a salt; -- said\n   of bases; thus, ammonia is salifiable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salification', 'n.', 'The act, process, or result of salifying; the state\n   of being salified.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salified', 'imp. & p. p.', 'of Salify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salifying', 'p. pr. & vb. n.', 'of Salify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salify', 'v. t.', 'To combine or impregnate with a salt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salify', 'v. t.', 'To form a salt with; to convert into a salt; as, to\n   salify a base or an acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saligenin', 'n.', 'A phenol alcohol obtained, by the decomposition of\n   salicin, as a white crystalline substance; -- called also\n   hydroxy-benzyl alcohol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saligot', 'n.', 'The water chestnut (Trapa natans).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salimeter', 'n.', 'An instrument for measuring the amount of salt present\n   in any given solution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salimetry', 'n.', 'The art or process of measuring the amount of salt in a\n   substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salina', 'a.', 'A salt marsh, or salt pond, inclosed from the sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salina', 'a.', 'Salt works.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salina period', '', 'The period in which the American Upper Silurian\n   system, containing the brine-producing rocks of central New York, was\n   formed. See the Chart of Geology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salination', 'n.', 'The act of washing with salt water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saline', 'a.', 'Consisting of salt, or containing salt; as, saline\n   particles; saline substances; a saline cathartic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saline', 'a.', 'Of the quality of salt; salty; as, a saline taste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saline', 'a.', 'A salt spring; a place where salt water is collected in the\n   earth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saline', 'n.', 'A crude potash obtained from beet-root residues and other\n   similar sources.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saline', 'n.', 'A metallic salt; esp., a salt of potassium, sodium,\n   lithium, or magnesium, used in medicine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salineness', 'n.', 'The quality or state of being salt; saltness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saliniferous', 'a.', 'Same as Saliferous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saliniform', 'a.', 'Having the form or the qualities of a salt, especially\n   of common salt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salinity', 'n.', 'Salineness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salinometer', 'n.', 'A salimeter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salinous', 'a.', 'Saline.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salique', 'a.', 'Salic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saliretin', 'n.', 'A yellow amorphous resinoid substance obtained by the\n   action of dilute acids on saligenin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salisburia', 'n.', 'The ginkgo tree (Ginkgo biloba, or Salisburia\n   adiantifolia).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salite', 'v. t.', 'To season with salt; to salt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salite', 'n.', 'A massive lamellar variety of pyroxene, of a dingy green\n   color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saliva', 'n.', 'The secretion from the salivary glands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salival', 'a.', 'Salivary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salivant', 'a.', 'Producing salivation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salivant', 'n.', 'That which produces salivation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salivary', 'a.', 'Of or pertaining to saliva; producing or carrying saliva;\n   as, the salivary ferment; the salivary glands; the salivary ducts, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salivated', 'imp. & p. p.', 'of Salivate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salivating', 'p. pr. & vb. n.', 'of Salivate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salivate', 'v. t.', 'To produce an abnormal flow of saliva in; to produce\n   salivation or ptyalism in, as by the use of mercury.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salivate', 'v. i.', 'To produce saliva, esp. in excess.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salivation', 'n.', 'The act or process of salivating; an excessive\n   secretion of saliva, often accompanied with soreness of the mouth and\n   gums; ptyalism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salivous', 'a.', 'Pertaining to saliva; of the nature of saliva.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salices', 'pl. ', 'of Salix');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salix', 'n.', 'A genus of trees or shrubs including the willow, osier, and\n   the like, growing usually in wet grounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salix', 'n.', 'A tree or shrub of any kind of willow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sallenders', 'n. pl.', 'An eruption on the hind leg of a horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sallet', 'n.', 'A light kind of helmet, with or without a visor, introduced\n   during the 15th century.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sallet', 'n.', 'Alt. of Salleting');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salleting', 'n.', 'Salad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salliance', 'n.', 'Salience.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sallow', 'n.', 'The willow; willow twigs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sallow', 'n.', 'A name given to certain species of willow, especially those\n   which do not have flexible shoots, as Salix caprea, S. cinerea, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sallow', 'superl.', 'Having a yellowish color; of a pale, sickly color,\n   tinged with yellow; as, a sallow skin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sallow', 'v. t.', 'To tinge with sallowness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sallowish', 'a.', 'Somewhat sallow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sallowness', 'n.', 'The quality or condition of being sallow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sallied', 'imp. & p. p.', 'of Sally');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sallying', 'p. pr. & vb. n.', 'of Sally');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sally', 'v. i.', 'To leap or rush out; to burst forth; to issue suddenly;\n   as a body of troops from a fortified place to attack besiegers; to make\n   a sally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sallies', 'pl. ', 'of Sally');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sally', 'v.', 'A leaping forth; a darting; a spring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sally', 'v.', 'A rushing or bursting forth; a quick issue; a sudden\n   eruption; specifically, an issuing of troops from a place besieged to\n   attack the besiegers; a sortie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sally', 'v.', 'An excursion from the usual track; range; digression;\n   deviation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sally', 'v.', 'A flight of fancy, liveliness, wit, or the like; a flashing\n   forth of a quick and active mind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sally', 'v.', 'Transgression of the limits of soberness or steadiness; act\n   of levity; wild gayety; frolic; escapade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sally Lunn', '', 'A tea cake slighty sweetened, and raised with yeast,\n   baked in the form of biscuits or in a thin loaf, and eaten hot with\n   butter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sallyman', 'n.', 'The velella; -- called also saleeman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salm', 'n.', 'Psalm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salmagundi', 'n.', 'A mixture of chopped meat and pickled herring, with\n   oil, vinegar, pepper, and onions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salmagundi', 'n.', 'Hence, a mixture of various ingredients; an olio or\n   medley; a potpourri; a miscellany.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salmi', 'n.', 'Same as Salmis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salmiac', 'n.', 'Sal ammoniac. See under Sal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salmis', 'n.', 'A ragout of partly roasted game stewed with sauce, wine,\n   bread, and condiments suited to provoke appetite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salmons', 'pl. ', 'of Salmon');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salmon', 'pl. ', 'of Salmon');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salmon', 'v.', 'Any one of several species of fishes of the genus Salmo and\n   allied genera. The common salmon (Salmo salar) of Northern Europe and\n   Eastern North America, and the California salmon, or quinnat, are the\n   most important species. They are extensively preserved for food. See\n   Quinnat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salmon', 'v.', 'A reddish yellow or orange color, like the flesh of the\n   salmon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salmon', 'a.', 'Of a reddish yellow or orange color, like that of the flesh\n   of the salmon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salmonet', 'n.', 'A salmon of small size; a samlet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salmonoid', 'a.', 'Like, or pertaining to, the Salmonidae, a family of\n   fishes including the trout and salmon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salmonoid', 'n.', 'Any fish of the family Salmonidae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salogen', 'n.', 'A halogen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salol', 'n.', 'A white crystalline substance consisting of phenol\n   salicylate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'salometer', 'n.', 'See Salimeter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salomtry', 'n.', 'Salimetry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salon', 'n.', 'An apartment for the reception of company; hence, in the\n   plural, fashionable parties; circles of fashionable society.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saloon', 'n.', 'A spacious and elegant apartment for the reception of\n   company or for works of art; a hall of reception, esp. a hall for\n   public entertainments or amusements; a large room or parlor; as, the\n   saloon of a steamboat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saloon', 'n.', 'Popularly, a public room for specific uses; esp., a barroom\n   or grogshop; as, a drinking saloon; an eating saloon; a dancing saloon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saloop', 'n.', 'An aromatic drink prepared from sassafras bark and other\n   ingredients, at one time much used in London.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salp', 'n.', 'Any species of Salpa, or of the family Salpidae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salpae', 'pl. ', 'of Salpa');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salpas', 'pl. ', 'of Salpa');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salpa', 'n.', 'A genus of transparent, tubular, free-swimming oceanic\n   tunicates found abundantly in all the warmer latitudes. See\n   Illustration in Appendix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salpian', 'n.', 'Alt. of Salpid');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salpid', 'n.', 'A salpa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salpicon', 'n.', 'Chopped meat, bread, etc., used to stuff legs of veal or\n   other joints; stuffing; farce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salpingitis', 'n.', 'Inflammation of the salpinx.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salpinx', 'n.', 'The Eustachian tube, or the Fallopian tube.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salsafy', 'n.', 'See Salsify.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salsamentarious', 'a.', 'Salt; salted; saline.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salse', 'n.', 'A mud volcano, the water of which is often impregnated with\n   salts, whence the name.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salsify', 'n.', 'See Oyster plant (a), under Oyster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salso-acid', 'a.', 'Having a taste compounded of saltness and acidity; both\n   salt and acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salsoda', 'n.', 'See Sal soda, under Sal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salsola', 'n.', 'A genus of plants including the glasswort. See Glasswort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'salsuginous', 'a.', 'Growing in brackish places or in salt marshes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salt', 'n.', 'The chloride of sodium, a substance used for seasoning food,\n   for the preservation of meat, etc. It is found native in the earth, and\n   is also produced, by evaporation and crystallization, from sea water\n   and other water impregnated with saline particles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salt', 'n.', 'Hence, flavor; taste; savor; smack; seasoning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salt', 'n.', 'Hence, also, piquancy; wit; sense; as, Attic salt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salt', 'n.', 'A dish for salt at table; a saltcellar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salt', 'n.', 'A sailor; -- usually qualified by old.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salt', 'n.', 'The neutral compound formed by the union of an acid and a\n   base; thus, sulphuric acid and iron form the salt sulphate of iron or\n   green vitriol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salt', 'n.', 'Fig.: That which preserves from corruption or error; that\n   which purifies; a corrective; an antiseptic; also, an allowance or\n   deduction; as, his statements must be taken with a grain of salt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salt', 'n.', 'Any mineral salt used as an aperient or cathartic, especially\n   Epsom salts, Rochelle salt, or Glaubers salt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salt', 'n.', 'Marshes flooded by the tide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salt', 'n.', 'Of or relating to salt; abounding in, or containing, salt;\n   prepared or preserved with, or tasting of, salt; salted; as, salt beef;\n   salt water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salt', 'n.', 'Overflowed with, or growing in, salt water; as, a salt marsh;\n   salt grass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salt', 'n.', 'Fig.: Bitter; sharp; pungent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salt', 'n.', 'Fig.: Salacious; lecherous; lustful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salted', 'imp. & p. p.', 'of Salt');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salting', 'p. pr. & vb. n.', 'of Salt');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salt', 'v. t.', 'To sprinkle, impregnate, or season with salt; to preserve\n   with salt or in brine; to supply with salt; as, to salt fish, beef, or\n   pork; to salt cattle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salt', 'v. t.', 'To fill with salt between the timbers and planks, as a\n   ship, for the preservation of the timber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salt', 'v. i.', 'To deposit salt as a saline solution; as, the brine begins\n   to salt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salt', 'n.', 'The act of leaping or jumping; a leap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saltant', 'v.', 'Leaping; jumping; dancing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saltant', 'v.', 'In a leaping position; springing forward; -- applied\n   especially to the squirrel, weasel, and rat, also to the cat,\n   greyhound, monkey, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saltarella', 'n.', 'See Saltarello.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saltarello', 'n.', 'A popular Italian dance in quick 3-4 or 6-8 time,\n   running mostly in triplets, but with a hop step at the beginning of\n   each measure. See Tarantella.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saltate', 'v. i.', 'To leap or dance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saltation', 'n.', 'A leaping or jumping.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saltation', 'n.', 'Beating or palpitation; as, the saltation of the great\n   artery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saltation', 'n.', 'An abrupt and marked variation in the condition or\n   appearance of a species; a sudden modification which may give rise to\n   new races.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saltatoria', 'n. pl.', 'A division of Orthoptera including grasshoppers,\n   locusts, and crickets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saltatorial', 'a.', 'Relating to leaping; saltatory; as, saltatorial\n   exercises.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saltatorial', 'a.', 'Same as Saltatorious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saltatorial', 'a.', 'Of or pertaining to the Saltatoria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saltatorious', 'a.', 'Capable of leaping; formed for leaping; saltatory;\n   as, a saltatorious insect or leg.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saltatory', 'a.', 'Leaping or dancing; having the power of, or used in,\n   leaping or dancing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saltbush', 'n.', 'An Australian plant (Atriplex nummularia) of the\n   Goosefoot family.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saltcat', 'n.', 'A mixture of salt, coarse meal, lime, etc., attractive to\n   pigeons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saltcellar', 'n.', 'Formerly a large vessel, now a small vessel of glass or\n   other material, used for holding salt on the table.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salter', 'n.', 'One who makes, sells, or applies salt; one who salts meat\n   or fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saltern', 'n.', 'A building or place where salt is made by boiling or by\n   evaporation; salt works.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saltfoot', 'n.', 'A large saltcellar formerly placed near the center of the\n   table. The superior guests were seated above the saltfoot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salt-green', 'a.', 'Sea-green in color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saltle', 'n.', 'The European dab.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saltier', 'n.', 'See Saltire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saltigradae', 'n. pl.', 'A tribe of spiders including those which lie in\n   wait and leap upon their prey; the leaping spiders.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saltigrade', 'a.', 'Having feet or legs formed for leaping.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saltigrade', 'n.', 'One of the Saltigradae, a tribe of spiders which leap\n   to seize their prey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saltimbanco', 'n.', 'A mountebank; a quack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salting', 'n.', 'The act of sprinkling, impregnating, or furnishing, with\n   salt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salting', 'n.', 'A salt marsh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saltire', 'v.', 'A St. Andrews cross, or cross in the form of an X, -- one\n   of the honorable ordinaries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saltirewise', 'adv.', 'In the manner of a saltire; -- said especially of\n   the blazoning of a shield divided by two lines drawn in the direction\n   of a bend and a bend sinister, and crossing at the center.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saltish', 'a.', 'Somewhat salt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saltless', 'a.', 'Destitute of salt; insipid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saltly', 'adv.', 'With taste of salt; in a salt manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saltmouth', 'n.', 'A wide-mouthed bottle with glass stopper for holding\n   chemicals, especially crystallized salts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saltness', 'n.', 'The quality or state of being salt, or state of being\n   salt, or impregnated with salt; salt taste; as, the saltness of sea\n   water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saltpeter', 'n.', 'Alt. of Saltpetre');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saltpetre', 'n.', 'Potassium nitrate; niter; a white crystalline substance,\n   KNO3, having a cooling saline taste, obtained by leaching from certain\n   soils in which it is produced by the process of nitrification (see\n   Nitrification, 2). It is a strong oxidizer, is the chief constituent of\n   gunpowder, and is also used as an antiseptic in curing meat, and in\n   medicine as a diuretic, diaphoretic, and refrigerant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saltpetrous', 'a.', 'Pertaining to saltpeter, or partaking of its\n   qualities; impregnated with saltpeter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salt rheum', '', 'A popular name, esp. in the United States, for various\n   cutaneous eruptions, particularly for those of eczema. See Eczema.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saltwort', 'n.', 'A name given to several plants which grow on the\n   seashore, as the Batis maritima, and the glasswort. See Glasswort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salty', 'a.', 'Somewhat salt; saltish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salubrious', 'a.', 'Favorable to health; healthful; promoting health; as,\n   salubrious air, water, or climate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salubrity', 'n.', 'The quality of being salubrious; favorableness to the\n   preservation of health; salubriousness; wholesomeness; healthfulness;\n   as, the salubrity of the air, of a country, or a climate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salue', 'v. t.', 'To salute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salutary', 'a.', 'Wholesome; healthful; promoting health; as, salutary\n   exercise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salutary', 'a.', 'Promotive of, or contributing to, some beneficial\n   purpose; beneficial; advantageous; as, a salutary design.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salutation', 'n.', 'The act of saluting, or paying respect or reverence, by\n   the customary words or actions; the act of greeting, or expressing good\n   will or courtesy; also, that which is uttered or done in saluting or\n   greeting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salutatorian', 'n.', 'The student who pronounces the salutatory oration at\n   the annual Commencement or like exercises of a college, -- an honor\n   commonly assigned to that member of the graduating class who ranks\n   second in scholarship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salutatorily', 'adv.', 'By way of salutation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salutatory', 'a.', 'Containing or expressing salutations; speaking a\n   welcome; greeting; -- applied especially to the oration which\n   introduces the exercises of the Commencements, or similar public\n   exhibitions, in American colleges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salutatory', 'n.', 'A place for saluting or greeting; a vestibule; a porch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salutatory', 'n.', 'The salutatory oration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saluted', 'imp. & p. p.', 'of Salute');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saluting', 'p. pr. & vb. n.', 'of Salute');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salute', 'v. t.', 'To address, as with expressions of kind wishes and\n   courtesy; to greet; to hail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salute', 'v. t.', 'Hence, to give a sign of good will; to compliment by an\n   act or ceremony, as a kiss, a bow, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salute', 'v. t.', 'To honor, as some day, person, or nation, by a discharge\n   of cannon or small arms, by dipping colors, by cheers, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salute', 'v. t.', 'To promote the welfare and safety of; to benefit; to\n   gratify.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salute', 'v.', 'The act of saluting, or expressing kind wishes or respect;\n   salutation; greeting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salute', 'v.', 'A sign, token, or ceremony, expressing good will,\n   compliment, or respect, as a kiss, a bow, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salute', 'v.', 'A token of respect or honor for some distinguished or\n   official personage, for a foreign vessel or flag, or for some festival\n   or event, as by presenting arms, by a discharge of cannon, volleys of\n   small arms, dipping the colors or the topsails, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saluter', 'n.', 'One who salutes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salutiferous', 'a.', 'Bringing health; healthy; salutary; beneficial; as,\n   salutiferous air.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salutiferously', 'adv.', 'Salutarily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salvability', 'n.', 'The quality or condition of being salvable;\n   salvableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salvable', 'a.', 'Capable of being saved; admitting of salvation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salvage', 'n.', 'The act of saving a vessel, goods, or life, from perils of\n   the sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salvage', 'n.', 'The compensation allowed to persons who voluntarily assist\n   in saving a ship or her cargo from peril.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salvage', 'n.', 'That part of the property that survives the peril and is\n   saved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salvage', 'a. & n.', 'Savage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salvation', 'n.', 'The act of saving; preservation or deliverance from\n   destruction, danger, or great calamity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salvation', 'n.', 'The redemption of man from the bondage of sin and\n   liability to eternal death, and the conferring on him of everlasting\n   happiness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salvation', 'n.', 'Saving power; that which saves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salvationist', 'n.', 'An evangelist, a member, or a recruit, of the\n   Salvation Army.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salvatory', 'n.', 'A place where things are preserved; a repository.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salve', 'interj.', 'Hail!');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salve', 'v. t.', 'To say \"Salve\" to; to greet; to salute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salve', 'n.', 'An adhesive composition or substance to be applied to wounds\n   or sores; a healing ointment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salve', 'n.', 'A soothing remedy or antidote.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salved', 'imp. & p. p.', 'of Salve');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salving', 'p. pr. & vb. n.', 'of Salve');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salve', 'n.', 'To heal by applications or medicaments; to cure by remedial\n   treatment; to apply salve to; as, to salve a wound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salve', 'n.', 'To heal; to remedy; to cure; to make good; to soothe, as\n   with an ointment, especially by some device, trick, or quibble; to\n   gloss over.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salve', 'v. t. & i.', 'To save, as a ship or goods, from the perils of the\n   sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salver', 'n.', 'One who salves, or uses salve as a remedy; hence, a\n   quacksalver, or quack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salver', 'n.', 'A salvor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salver', 'n.', 'A tray or waiter on which anything is presented.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salver-shaped', 'a.', 'Tubular, with a spreading border. See\n   Hypocraterimorphous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salvia', 'n.', 'A genus of plants including the sage. See Sage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salvific', 'a.', 'Tending to save or secure safety.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salvos', 'pl. ', 'of Salvo');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salvo', 'n.', 'An exception; a reservation; an excuse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salvo', 'n.', 'A concentrated fire from pieces of artillery, as in\n   endeavoring to make a break in a fortification; a volley.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salvo', 'n.', 'A salute paid by a simultaneous, or nearly simultaneous,\n   firing of a number of cannon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Salvor', 'n.', 'One who assists in saving a ship or goods at sea, without\n   being under special obligation to do so.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sam', 'a.', 'Together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Samara', 'n.', 'A dry, indehiscent, usually one-seeded, winged fruit, as\n   that of the ash, maple, and elm; a key or key fruit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Samare', 'n.', 'See Simar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Samaritan', 'a.', 'Of or pertaining to Samaria, in Palestine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Samaritan', 'n.', 'A native or inhabitant of Samaria; also, the language of\n   Samaria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Samarium', 'n.', 'A rare metallic element of doubtful identity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Samaroid', 'a.', 'Resembling a samara, or winged seed vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Samarra', 'n.', 'See Simar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Samarskite', 'a.', 'A rare mineral having a velvet-black color and\n   submetallic luster. It is a niobate of uranium, iron, and the yttrium\n   and cerium metals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sambo', 'n.', 'A colloquial or humorous appellation for a negro; sometimes,\n   the offspring of a black person and a mulatto; a zambo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Samboo', 'n.', 'Same as Sambur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sambucus', 'n.', 'A genus of shrubs and trees; the elder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sambuke', 'n.', 'An ancient stringed instrument used by the Greeks, the\n   particular construction of which is unknown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sambur', 'n.', 'An East Indian deer (Rusa Aristotelis) having a mane on its\n   neck. Its antlers have but three prongs. Called also gerow. The name is\n   applied to other species of the genus Rusa, as the Bornean sambur (R.\n   equina).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Same', 'v. i.', 'Not different or other; not another or others; identical;\n   unchanged.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Same', 'v. i.', 'Of like kind, species, sort, dimensions, or the like; not\n   differing in character or in the quality or qualities compared;\n   corresponding; not discordant; similar; like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Same', 'v. i.', 'Just mentioned, or just about to be mentioned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sameliness', 'n.', 'Sameness, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sameness', 'n.', 'The state of being the same; identity; absence of\n   difference; near resemblance; correspondence; similarity; as, a\n   sameness of person, of manner, of sound, of appearance, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sameness', 'n.', 'Hence, want of variety; tedious monotony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Samette', 'n.', 'See Samite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Samian', 'a.', 'Of or pertaining to the island of Samos.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Samian', 'n.', 'A native or inhabitant of Samos.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Samiel', 'n.', 'A hot and destructive wind that sometimes blows, in Turkey,\n   from the desert. It is identical with the simoom of Arabia and the\n   kamsin of Syria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Samiot', 'a. & n.', 'Samian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Samite', 'a.', 'A species of silk stuff, or taffeta, generally interwoven\n   with gold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Samlet', 'n.', 'The parr.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sammier', 'n.', 'A machine for pressing the water from skins in tanning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Samoan', 'a.', 'Of or pertaining to the Samoan Islands (formerly called\n   Navigators Islands) in the South Pacific Ocean, or their inhabitants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Samoan', 'n.', 'An inhabitant of the Samoan Islands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Samovar', 'n.', 'A metal urn used in Russia for making tea. It is filled\n   with water, which is heated by charcoal placed in a pipe, with chimney\n   attached, which passes through the urn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Samoyedes', 'n. pl.', 'An ignorant and degraded Turanian tribe which\n   occupies a portion of Northern Russia and a part of Siberia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Samp', 'n.', 'An article of food consisting of maize broken or bruised,\n   which is cooked by boiling, and usually eaten with milk; coarse hominy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sampan', 'n.', 'A Chinese boat from twelve to fifteen feet long, covered\n   with a house, and sometimes used as a permanent habitation on the\n   inland waters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Samphire', 'n.', 'A fleshy, suffrutescent, umbelliferous European plant\n   (Crithmum maritimum). It grows among rocks and on cliffs along the\n   seacoast, and is used for pickles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Samphire', 'n.', 'The species of glasswort (Salicornia herbacea); -- called\n   in England marsh samphire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Samphire', 'n.', 'A seashore shrub (Borrichia arborescens) of the West\n   Indies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sample', 'n.', 'Example; pattern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sample', 'n.', 'A part of anything presented for inspection, or shown as\n   evidence of the quality of the whole; a specimen; as, goods are often\n   purchased by samples.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sample', 'v. t.', 'To make or show something similar to; to match.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sample', 'v. t.', 'To take or to test a sample or samples of; as, to sample\n   sugar, teas, wools, cloths.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sampler', 'n.', 'One who makes up samples for inspection; one who examines\n   samples, or by samples; as, a wool sampler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sampler', 'n.', 'A pattern; a specimen; especially, a collection of\n   needlework patterns, as letters, borders, etc., to be used as samples,\n   or to display the skill of the worker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Samshoo', 'n.', 'Alt. of Samshu');
  INSERT INTO words (word, word_type, description) VALUES  (    'Samshu', 'n.', 'A spirituous liquor distilled by the Chinese from the\n   yeasty liquor in which boiled rice has fermented under pressure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Samson', 'n.', 'An Israelite of Bible record (see Judges xiii.),\n   distinguished for his great strength; hence, a man of extraordinary\n   physical strength.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanability', 'n.', 'The quality or state of being sanable; sanableness;\n   curableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanable', 'a.', 'Capable of being healed or cured; susceptible of remedy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanableness', 'n.', 'The quality of being sanable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanation', 'n.', 'The act of healing or curing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanative', 'a.', 'Having the power to cure or heal; healing; tending to\n   heal; sanatory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanatorium', 'n.', 'An establishment for the treatment of the sick; a\n   resort for invalids. See Sanitarium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanatory', 'a.', 'Conducive to health; tending to cure; healing; curative;\n   sanative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanbenito', 'n.', 'Anciently, a sackcloth coat worn by penitents on being\n   reconciled to the church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanbenito', 'n.', 'A garnment or cap, or sometimes both, painted with\n   flames, figures, etc., and worn by persons who had been examined by the\n   Inquisition and were brought forth for punishment at the auto-da-fe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sance-bell', 'n.', 'Alt. of Sancte bell');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sancte bell', 'n.', 'See Sanctus bell, under Sanctus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanctificate', 'v. t.', 'To sanctify.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanctification', 'n.', 'The act of sanctifying or making holy; the state of\n   being sanctified or made holy;');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanctification', 'n.', 'the act of Gods grace by which the affections of\n   men are purified, or alienated from sin and the world, and exalted to a\n   supreme love to God; also, the state of being thus purified or\n   sanctified.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanctification', 'n.', 'The act of consecrating, or of setting apart for a\n   sacred purpose; consecration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanctified', 'a.', 'Made holy; also, made to have the air of sanctity;\n   sanctimonious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanctifier', 'n.', 'One who sanctifies, or makes holy; specifically, the\n   Holy Spirit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanctified', 'imp. & p. p.', 'of Sanctify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanctifying', 'p. pr. & vb. n.', 'of Sanctify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanctify', 'v. t.', 'To make sacred or holy; to set apart to a holy or\n   religious use; to consecrate by appropriate rites; to hallow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanctify', 'v. t.', 'To make free from sin; to cleanse from moral\n   corruption and pollution; to purify.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanctify', 'v. t.', 'To make efficient as the means of holiness; to render\n   productive of holiness or piety.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanctify', 'v. t.', 'To impart or impute sacredness, venerableness,\n   inviolability, title to reverence and respect, or the like, to; to\n   secure from violation; to give sanction to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanctifyingly', 'adv.', 'In a manner or degree tending to sanctify or make\n   holy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanctiloquent', 'a.', 'Discoursing on heavenly or holy things, or in a holy\n   manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanctimonial', 'a.', 'Sanctimonious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanctimonious', 'a.', 'Possessing sanctimony; holy; sacred; saintly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanctimonious', 'a.', 'Making a show of sanctity; affecting saintliness;\n   hypocritically devout or pious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanctimony', 'n.', 'Holiness; devoutness; scrupulous austerity; sanctity;\n   especially, outward or artificial saintliness; assumed or pretended\n   holiness; hypocritical devoutness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanction', 'n.', 'Solemn or ceremonious ratification; an official act of a\n   superior by which he ratifies and gives validity to the act of some\n   other person or body; establishment or furtherance of anything by\n   giving authority to it; confirmation; approbation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanction', 'n.', 'Anything done or said to enforce the will, law, or\n   authority of another; as, legal sanctions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanctioned', 'imp. & p. p.', 'of Sanction');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanctioning', 'p. pr. & vb. n.', 'of Sanction');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanction', 'v. t.', 'To give sanction to; to ratify; to confirm; to\n   approve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanctionary', 'a.', 'Of, pertaining to, or giving, sanction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanctitude', 'n.', 'Holiness; sacredness; sanctity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanctities', 'pl. ', 'of Sanctity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanctity', 'n.', 'The state or quality of being sacred or holy; holiness;\n   saintliness; moral purity; godliness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanctity', 'n.', 'Sacredness; solemnity; inviolability; religious binding\n   force; as, the sanctity of an oath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanctity', 'n.', 'A saint or holy being.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanctuarize', 'v. t.', 'To shelter by means of a sanctuary or sacred\n   privileges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanctuaries', 'pl. ', 'of Sanctuary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanctuary', 'n.', 'A sacred place; a consecrated spot; a holy and\n   inviolable site.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanctuary', 'n.', 'The most retired part of the temple at Jerusalem, called\n   the Holy of Holies, in which was kept the ark of the covenant, and into\n   which no person was permitted to enter except the high priest, and he\n   only once a year, to intercede for the people; also, the most sacred\n   part of the tabernacle; also, the temple at Jerusalem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanctuary', 'n.', 'The most sacred part of any religious building, esp.\n   that part of a Christian church in which the altar is placed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanctuary', 'n.', 'A house consecrated to the worship of God; a place where\n   divine service is performed; a church, temple, or other place of\n   worship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanctuary', 'n.', 'A sacred and inviolable asylum; a place of refuge and\n   protection; shelter; refuge; protection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanctum', 'n.', 'A sacred place; hence, a place of retreat; a room reserved\n   for personal use; as, an editors sanctum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanctus', 'n.', 'A part of the Mass, or, in Protestant churches, a part of\n   the communion service, of which the first words in Latin are Sanctus,\n   sanctus, sanctus [Holy, holy, holy]; -- called also Tersanctus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanctus', 'n.', 'An anthem composed for these words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sand', 'n.', 'Fine particles of stone, esp. of siliceous stone, but not\n   reduced to dust; comminuted stone in the form of loose grains, which\n   are not coherent when wet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sand', 'n.', 'A single particle of such stone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sand', 'n.', 'The sand in the hourglass; hence, a moment or interval of\n   time; the term or extent of ones life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sand', 'n.', 'Tracts of land consisting of sand, like the deserts of Arabia\n   and Africa; also, extensive tracts of sand exposed by the ebb of the\n   tide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sand', 'n.', 'Courage; pluck; grit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanded', 'imp. & p. p.', 'of Sand');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanding', 'p. pr. & vb. n.', 'of Sand');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sand', 'v. t.', 'To sprinkle or cover with sand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sand', 'v. t.', 'To drive upon the sand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sand', 'v. t.', 'To bury (oysters) beneath drifting sand or mud.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sand', 'v. t.', 'To mix with sand for purposes of fraud; as, to sand sugar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sandal', 'n.', 'Same as Sendal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sandal', 'n.', 'Sandalwood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sandal', 'n.', 'A kind of shoe consisting of a sole strapped to the foot; a\n   protection for the foot, covering its lower surface, but not its upper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sandal', 'n.', 'A kind of slipper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sandal', 'n.', 'An overshoe with parallel openings across the instep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sandaled', 'a.', 'Wearing sandals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sandaled', 'a.', 'Made like a sandal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sandaliform', 'a.', 'Shaped like a sandal or slipper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sandalwood', 'n.', 'The highly perfumed yellowish heartwood of an East\n   Indian and Polynesian tree (Santalum album), and of several other trees\n   of the same genus, as the Hawaiian Santalum Freycinetianum and S.\n   pyrularium, the Australian S. latifolium, etc. The name is extended to\n   several other kinds of fragrant wood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sandalwood', 'n.', 'Any tree of the genus Santalum, or a tree which yields\n   sandalwood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sandalwood', 'n.', 'The red wood of a kind of buckthorn, used in Russia for\n   dyeing leather (Rhamnus Dahuricus).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sandarach', 'n.', 'Alt. of Sandarac');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sandarac', 'n.', 'Realgar; red sulphide of arsenic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sandarac', 'n.', 'A white or yellow resin obtained from a Barbary tree\n   (Callitris quadrivalvis or Thuya articulata), and pulverized for\n   pounce; -- probably so called from a resemblance to the mineral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sandbagger', 'n.', 'An assaulter whose weapon is a sand bag. See Sand bag,\n   under Sand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sand-blind', 'a.', 'Having defective sight; dim-sighted; purblind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanded', 'a.', 'Covered or sprinkled with sand; sandy; barren.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanded', 'a.', 'Marked with small spots; variegated with spots; speckled;\n   of a sandy color, as a hound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanded', 'a.', 'Short-sighted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sandemanian', 'n.', 'A follower of Robert Sandeman, a Scotch sectary of the\n   eighteenth century. See Glassite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sandemanianism', 'n.', 'The faith or system of the Sandemanians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanderling', 'n.', 'A small gray and brown sandpiper (Calidris arenaria)\n   very common on sandy beaches in America, Europe, and Asia. Called also\n   curwillet, sand lark, stint, and ruddy plover.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanders', 'n.', 'An old name of sandalwood, now applied only to the red\n   sandalwood. See under Sandalwood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanders-blue', 'n.', 'See Saunders-blue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sandever', 'n.', 'See Sandiver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sandfish', 'n.', 'A small marine fish of the Pacific coast of North America\n   (Trichodon trichodon) which buries itself in the sand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sandglass', 'n.', 'An instrument for measuring time by the running of sand.\n   See Hourglass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sandhiller', 'n.', 'A nickname given to any \"poor white\" living in the pine\n   woods which cover the sandy hills in Georgia and South Carolina.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sandiness', 'n.', 'The quality or state of being sandy, or of being of a\n   sandy color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sandish', 'a.', 'Approaching the nature of sand; loose; not compact.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sandiver', 'n.', 'A whitish substance which is cast up, as a scum, from the\n   materials of glass in fusion, and, floating on the top, is skimmed off;\n   -- called also glass gall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sandix', 'n.', 'A kind of minium, or red lead, made by calcining carbonate\n   of lead, but inferior to true minium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sandman', 'n.', 'A mythical person who makes children sleepy, so that they\n   rub their eyes as if there were sand in them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sandnecker', 'n.', 'A European flounder (Hippoglossoides limandoides); --\n   called also rough dab, long fluke, sand fluke, and sand sucker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sandpaper', 'n.', 'Paper covered on one side with sand glued fast, -- used\n   for smoothing and polishing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sandpaper', 'v. t.', 'To smooth or polish with sandpaper; as, to sandpaper\n   a door.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sandpiper', 'n.', 'Any one of numerous species of small limicoline game\n   birds belonging to Tringa, Actodromas, Ereunetes, and various allied\n   genera of the family Tringidae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sandpiper', 'n.', 'A small lamprey eel; the pride.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sandpit', 'n.', 'A pit or excavation from which sand is or has been taken.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sandre', 'n.', 'A Russian fish (Lucioperca sandre) which yields a valuable\n   oil, called sandre oil, used in the preparation of caviare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sandstone', 'n.', 'A rock made of sand more or less firmly united. Common\n   or siliceous sandstone consists mainly of quartz sand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sandwich', 'n.', 'Two pieces of bread and butter with a thin slice of meat,\n   cheese, or the like, between them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sandwiched', 'imp. & p. p.', 'of Sandwich');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sandwiching', 'p. pr. & vb. n.', 'of Sandwich');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sandwich', 'v. t.', 'To make into a sandwich; also, figuratively, to insert\n   between portions of something dissimilar; to form of alternate parts or\n   things, or alternating layers of a different nature; to interlard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sandworm', 'n.', 'Any one of numerous species of annelids which burrow in\n   the sand of the seashore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sandworm', 'n.', 'Any species of annelids of the genus Sabellaria. They\n   construct firm tubes of agglutinated sand on rocks and shells, and are\n   sometimes destructive to oysters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sandworm', 'n.', 'The chigoe, a species of flea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sandwort', 'n.', 'Any plant of the genus Arenaria, low, tufted herbs (order\n   Caryophyllaceae.)');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sandy', 'superl.', 'Consisting of, abounding with, or resembling, sand;\n   full of sand; covered or sprinkled with sand; as, a sandy desert, road,\n   or soil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sandy', 'superl.', 'Of the color of sand; of a light yellowish red color;\n   as, sandy hair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sandyx', 'n.', 'See Sandix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sane', 'a.', 'Being in a healthy condition; not deranged; acting\n   rationally; -- said of the mind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sane', 'a.', 'Mentally sound; possessing a rational mind; having the mental\n   faculties in such condition as to be able to anticipate and judge of\n   the effect of ones actions in an ordinary maner; -- said of persons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saneness', 'n.', 'The state of being sane; sanity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sang', '', 'imp. of Sing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanga', 'n.', 'Alt. of Sangu');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sangu', 'n.', 'The Abyssinian ox (Bos / Bibos, Africanus), noted for the\n   great length of its horns. It has a hump on its back.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sangaree', 'n.', 'Wine and water sweetened and spiced, -- a favorite West\n   Indian drink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sang-froid', 'n.', 'Freedom from agitation or excitement of mind; coolness\n   in trying circumstances; indifference; calmness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sangiac', 'n.', 'See Sanjak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sangraal', 'n.', 'Alt. of Sangreal');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sangreal', 'n.', 'See Holy Grail, under Grail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanguiferous', 'a.', 'Conveying blood; as, sanguiferous vessels, i. e., the\n   arteries, veins, capillaries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanguification', 'n.', 'The production of blood; the conversion of the\n   products of digestion into blood; hematosis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanguifier', 'n.', 'A producer of blood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanguifluous', 'a.', 'Flowing or running with blood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanguify', 'v. t.', 'To produce blood from.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanguigenous', 'a.', 'Producing blood; as, sanguigenous food.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanguinaceous', 'n.', 'Of a blood-red color; sanguine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanguinaria', 'n.', 'A genus of plants of the Poppy family.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanguinaria', 'n.', 'The rootstock of the bloodroot, used in medicine as an\n   emetic, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanguinarily', 'adv.', 'In a sanguinary manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanguinariness', 'n.', 'The quality or state of being sanguinary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanguinary', 'a.', 'Attended with much bloodshed; bloody; murderous; as, a\n   sanguinary war, contest, or battle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanguinary', 'a.', 'Bloodthirsty; cruel; eager to shed blood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanguinary', 'a.', 'The yarrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanguinary', 'a.', 'The Sanguinaria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanguine', 'a.', 'Having the color of blood; red.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanguine', 'a.', 'Characterized by abundance and active circulation of\n   blood; as, a sanguine bodily temperament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanguine', 'a.', 'Warm; ardent; as, a sanguine temper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanguine', 'a.', 'Anticipating the best; not desponding; confident; full of\n   hope; as, sanguine of success.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanguine', 'n.', 'Blood color; red.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanguine', 'n.', 'Anything of a blood-red color, as cloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanguine', 'n.', 'Bloodstone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanguine', 'n.', 'Red crayon. See the Note under Crayon, 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanguine', 'v. t.', 'To stain with blood; to impart the color of blood to;\n   to ensanguine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanguineless', 'a.', 'Destitute of blood; pale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanguinely', 'adv.', 'In a sanguine manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanguineness', 'n.', 'The quality of being sanguine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanguineous', 'a.', 'Abounding with blood; sanguine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanguineous', 'a.', 'Of or pertaining to blood; bloody; constituting blood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanguineous', 'a.', 'Blood-red; crimson.');
  INSERT INTO words (word, word_type, description) VALUES  (    'sanguinity', 'n.', 'The quality of being sanguine; sanguineness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanguinivorous', 'a.', 'Subsisting on blood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanguinolency', 'n.', 'The state of being sanguinolent, or bloody.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanguinolent', 'a.', 'Tinged or mingled with blood; bloody; as,\n   sanguinolent sputa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanguisuge', 'n.', 'A bloodsucker, or leech.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanguivorous', 'a.', 'Subsisting upon blood; -- said of certain\n   blood-sucking bats and other animals. See Vampire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanhedrin', 'n.', 'Alt. of Sanhedrim');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanhedrim', 'n.', 'the great council of the Jews, which consisted of\n   seventy members, to whom the high priest was added. It had jurisdiction\n   of religious matters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanhedrist', 'n.', 'A member of the sanhedrin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanhita', 'n.', 'A collection of vedic hymns, songs, or verses, forming the\n   first part of each Veda.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanicle', 'n.', 'Any plant of the umbelliferous genus Sanicula, reputed to\n   have healing powers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanidine', 'n.', 'A variety of orthoclase feldspar common in certain\n   eruptive rocks, as trachyte; -- called also glassy feldspar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanies', 'n.', 'A thin, serous fluid commonly discharged from ulcers or\n   foul wounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanious', 'a.', 'Pertaining to sanies, or partaking of its nature and\n   appearance; thin and serous, with a slight bloody tinge; as, the\n   sanious matter of an ulcer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanious', 'a.', 'Discharging sanies; as, a sanious ulcer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanitarian', 'a.', 'Of or pertaining to health, or the laws of health;\n   sanitary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanitarian', 'n.', 'An advocate of sanitary measures; one especially\n   interested or versed in sanitary measures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanitarist', 'n.', 'A sanitarian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanitarium', 'n.', 'A health station or retreat; a sanatorium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanitary', 'a.', 'Of or pertaining to health; designed to secure or\n   preserve health; relating to the preservation or restoration of health;\n   hygienic; as, sanitary regulations. See the Note under Sanatory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanitation', 'n.', 'The act of rendering sanitary; the science of sanitary\n   conditions; the preservation of health; the use of sanitary measures;\n   hygiene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanity', 'n.', 'The condition or quality of being sane; soundness of health\n   of body or mind, especially of the mind; saneness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanjak', 'n.', 'A district or a subvision of a vilayet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sank', '', 'imp. of Sink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sankha', 'n.', 'A chank shell (Turbinella pyrum); also, a shell bracelet or\n   necklace made in India from the chank shell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sankhya', 'n.', 'A Hindoo system of philosophy which refers all things to\n   soul and a rootless germ called prakriti, consisting of three elements,\n   goodness, passion, and darkness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sannop', 'n.', 'Same as Sannup.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sannup', 'n.', 'A male Indian; a brave; -- correlative of squaw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanny', 'n.', 'The sandpiper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sans', 'prep.', 'Without; deprived or destitute of. Rarely used as an\n   English word.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanscrit', 'n.', 'See Sanskrit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sans-culotte', 'n.', 'A fellow without breeches; a ragged fellow; -- a name\n   of reproach given in the first French revolution to the extreme\n   republican party, who rejected breeches as an emblem peculiar to the\n   upper classes or aristocracy, and adopted pantaloons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sans-culotte', 'n.', 'Hence, an extreme or radical republican; a violent\n   revolutionist; a Jacobin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sans-culottic', 'a.', 'Pertaining to, or involving, sans-culottism;\n   radical; revolutionary; Jacobinical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sans-culottism', 'n.', 'Extreme republican principles; the principles or\n   practice of the sans-culottes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanskrit', 'n.', 'The ancient language of the Hindoos, long since obsolete\n   in vernacular use, but preserved to the present day as the literary and\n   sacred dialect of India. It is nearly allied to the Persian, and to the\n   principal languages of Europe, classical and modern, and by its more\n   perfect preservation of the roots and forms of the primitive language\n   from which they are all descended, is a most important assistance in\n   determining their history and relations. Cf. Prakrit, and Veda.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanskrit', 'a.', 'Of or pertaining to Sanskrit; written in Sanskrit; as, a\n   Sanskrit dictionary or inscription.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanskritic', 'a.', 'Sanskrit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sanskritist', 'n.', 'One versed in Sanskrit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sans-souci', 'adv.', 'Without care; free and easy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Santal', 'n.', 'A colorless crystalline substance, isomeric with piperonal,\n   but having weak acid properties. It is extracted from sandalwood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Santalaceous', 'a.', 'Of or pertaining to a natural order of plants\n   (Santalaceae), of which the genus Santalum is the type, and which\n   includes the buffalo nut and a few other North American plants, and\n   many peculiar plants of the southern hemisphere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Santalic', 'a.', 'Of, pertaining to, or obtained from, sandalwood\n   (Santalum); -- used specifically to designate an acid obtained as a\n   resinous or red crystalline dyestuff, which is called also santalin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Santalin', 'n.', 'Santalic acid. See Santalic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Santalum', 'n.', 'A genus of trees with entire opposite leaves and small\n   apetalous flowers. There are less than a dozen species, occurring from\n   India to Australia and the Pacific Islands. See Sandalwood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Santees', 'n. pl.', 'One of the seven confederated tribes of Indians\n   belonging to the Sioux, or Dakotas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Santer', 'v. i.', 'See Saunter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Santon', 'n.', 'A Turkish saint; a kind of dervish, regarded by the people\n   as a saint: also, a hermit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Santonate', 'n.', 'A salt of santonic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Santonic', 'a.', 'Of, pertaining to, or designating, an acid (distinct from\n   santoninic acid) obtained from santonin as a white crystalline\n   substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Santonin', 'n.', 'A white crystalline substance having a bitter taste,\n   extracted from the buds of levant wormseed and used as an anthelmintic.\n   It occassions a peculiar temporary color blindness, causing objects to\n   appear as if seen through a yellow glass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Santoninate', 'n.', 'A salt of santoninic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Santoninic', 'a.', 'Of or pertaining to santonin; -- used specifically to\n   designate an acid not known in the free state, but obtained in its\n   salts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sao', 'n.', 'Any marine annelid of the genus Hyalinaecia, especially H.\n   tubicola of Europe, which inhabits a transparent movable tube\n   resembling a quill in color and texture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sap', 'n.', 'The juice of plants of any kind, especially the ascending and\n   descending juices or circulating fluid essential to nutrition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sap', 'n.', 'The sapwood, or alburnum, of a tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sap', 'n.', 'A simpleton; a saphead; a milksop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sapped', 'imp. & p. p.', 'of Sap');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sapping', 'p. pr. & vb. n.', 'of Sap');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sap', 'v. t.', 'To subvert by digging or wearing away; to mine; to\n   undermine; to destroy the foundation of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sap', 'v. t.', 'To pierce with saps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sap', 'v. t.', 'To make unstable or infirm; to unsettle; to weaken.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sap', 'v. i.', 'To proceed by mining, or by secretly undermining; to\n   execute saps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sap', 'n.', 'A narrow ditch or trench made from the foremost parallel\n   toward the glacis or covert way of a besieged place by digging under\n   cover of gabions, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sapadillo', 'n.', 'See Sapodila.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sapajo', 'n.', 'The sapajou.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sapajou', 'n.', 'Any one of several species of South American monkeys of\n   the genus Cebus, having long and prehensile tails. Some of the species\n   are called also capuchins. The bonnet sapajou (C. subcristatus), the\n   golden-handed sapajou (C. chrysopus), and the white-throated sapajou\n   (C. hypoleucus) are well known species. See Capuchin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sapan wood', '', 'A dyewood yielded by Caesalpinia Sappan, a thorny\n   leguminous tree of Southern Asia and the neighboring islands. It is the\n   original Brazil wood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sapful', 'a.', 'Abounding in sap; sappy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saphead', 'n.', 'A weak-minded, stupid fellow; a milksop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saphenous', 'a.', 'Manifest; -- applied to the two principal superficial\n   veins of the lower limb of man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saphenous', 'a.', 'Of, pertaining to, or in the region of, the saphenous\n   veins; as, the saphenous nerves; the saphenous opening, an opening in\n   the broad fascia of the thigh through which the internal saphenous vein\n   passes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sapid', 'a.', 'Having the power of affecting the organs of taste;\n   possessing savor, or flavor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sapidity', 'n.', 'The quality or state of being sapid; taste; savor;\n   savoriness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sapidness', 'n.', 'Quality of being sapid; sapidity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sapience', 'n.', 'The quality of being sapient; wisdom; sageness;\n   knowledge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sapient', 'a.', 'Wise; sage; discerning; -- often in irony or contempt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sapiential', 'a.', 'Having or affording wisdom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sapientious', 'a.', 'Sapiential.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sapientize', 'v. t.', 'To make sapient.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sapiently', 'adv.', 'In a sapient manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sapindaceous', 'a.', 'Of or pertaining to an order of trees and shrubs\n   (Sapindaceae), including the (typical) genus Sapindus, the maples, the\n   margosa, and about seventy other genera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sapindus', 'n.', 'A genus of tropical and subtropical trees with pinnate\n   leaves and panicled flowers. The fruits of some species are used\n   instead of soap, and their round black seeds are made into necklaces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sapless', 'a.', 'Destitute of sap; not juicy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sapless', 'a.', 'Fig.: Dry; old; husky; withered; spiritless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'sapling', 'n.', 'A young tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sapodilla', 'n.', 'A tall, evergeen, tropical American tree (Achras\n   Sapota); also, its edible fruit, the sapodilla plum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sapogenin', 'n.', 'A white crystalline substance obtained by the\n   decomposition of saponin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saponaceous', 'a.', 'Resembling soap; having the qualities of soap; soapy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saponacity', 'n.', 'The quality or state of being saponaceous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saponary', 'a.', 'Saponaceous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saponifiable', 'a.', 'Capable of conversion into soap; as, a saponifiable\n   substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saponification', 'n.', 'The act, process, or result, of soap making;\n   conversion into soap; specifically (Chem.), the decomposition of fats\n   and other ethereal salts by alkalies; as, the saponification of ethyl\n   acetate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saponifier', 'n.', 'That which saponifies; any reagent used to cause\n   saponification.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saponified', 'imp. & p. p.', 'of Saponify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saponifying', 'p. pr. & vb. n.', 'of Saponify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saponify', 'v. t.', 'To convert into soap, as tallow or any fat; hence\n   (Chem.), to subject to any similar process, as that which ethereal\n   salts undergo in decomposition; as, to saponify ethyl acetate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saponin', 'n.', 'A poisonous glucoside found in many plants, as in the root\n   of soapwort (Saponaria), in the bark of soap bark (Quillaia), etc. It\n   is extracted as a white amorphous powder, which occasions a soapy\n   lather in solution, and produces a local anaesthesia. Formerly called\n   also struthiin, quillaiin, senegin, polygalic acid, etc. By extension,\n   any one of a group of related bodies of which saponin proper is the\n   type.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saponite', 'n.', 'A hydrous silicate of magnesia and alumina. It occurs in\n   soft, soapy, amorphous masses, filling veins in serpentine and cavities\n   in trap rock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saponul', 'n.', 'A soapy mixture obtained by treating an essential oil with\n   an alkali; hence, any similar compound of an essential oil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sapor', 'n.', 'Power of affecting the organs of taste; savor; flavor;\n   taste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saporific', 'a.', 'Having the power to produce the sensation of taste;\n   producing taste, flavor, or relish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saporosity', 'n.', 'The quality of a body by which it excites the sensation\n   of taste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saporous', 'a.', 'Having flavor or taste; yielding a taste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sapota', 'n.', 'The sapodilla.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sapotaceous', 'a.', 'Of or pertaining to a natural order (Sapotaceae) of\n   (mostly tropical) trees and shrubs, including the star apple, the\n   Lucuma, or natural marmalade tree, the gutta-percha tree (Isonandra),\n   and the India mahwa, as well as the sapodilla, or sapota, after which\n   the order is named.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sappan wood', '', 'Sapan wood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sappare', 'n.', 'Kyanite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sapper', 'n.', 'One who saps; specifically (Mil.), one who is employed in\n   working at saps, building and repairing fortifications, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sapphic', 'a.', 'Of or pertaining to Sappho, the Grecian poetess; as,\n   Sapphic odes; Sapphic verse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sapphic', 'a.', 'Belonging to, or in the manner of, Sappho; -- said of a\n   certain kind of verse reputed to have been invented by Sappho,\n   consisting of five feet, of which the first, fourth, and fifth are\n   trochees, the second is a spondee, and the third a dactyl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sapphic', 'n.', 'A Sapphic verse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sapphire', 'n.', 'Native alumina or aluminium sesquioxide, Al2O3; corundum;\n   esp., the blue transparent variety of corundum, highly prized as a gem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sapphire', 'n.', 'The color of the gem; bright blue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sapphire', 'n.', 'Any humming bird of the genus Hylocharis, native of South\n   America. The throat and breast are usually bright blue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sapphire', 'a.', 'Of or resembling sapphire; sapphirine; blue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sapphirine', 'n.', 'Resembling sapphire; made of sapphire; having the\n   color, or any quality of sapphire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sappho', 'n.', 'Any one of several species of brilliant South American\n   humming birds of the genus Sappho, having very bright-colored and\n   deeply forked tails; -- called also firetail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sappiness', 'n.', 'The quality of being sappy; juiciness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sappodilla', 'n.', 'See Sapodilla.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sappy', 'superl.', 'Abounding with sap; full of sap; juicy; succulent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sappy', 'superl.', 'Hence, young, not firm; weak, feeble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sappy', 'superl.', 'Weak in intellect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sappy', 'superl.', 'Abounding in sap; resembling, or consisting largely of,\n   sapwood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sappy', 'a.', 'Musty; tainted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saprophagan', 'n.', 'One of a tribe of beetles which feed upon decaying\n   animal and vegetable substances; a carrion beetle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saprophagous', 'a.', 'Feeding on carrion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saprophyte', 'n.', 'Any plant growing on decayed animal or vegetable\n   matter, as most fungi and some flowering plants with no green color, as\n   the Indian pipe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saprophytic', 'a.', 'Feeding or growing upon decaying animal or vegetable\n   matter; pertaining to a saprophyte or the saprophytes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sapsago', 'n.', 'A kind of Swiss cheese, of a greenish color, flavored with\n   melilot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sapskull', 'n.', 'A saphead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sapucaia', 'n.', 'A Brazilian tree. See Lecythis, and Monkey-pot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sapwood', 'n.', 'The alburnum, or part of the wood of any exogenous tree\n   next to the bark, being that portion of the tree through which the sap\n   flows most freely; -- distinguished from heartwood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sarabaite', 'n.', 'One of certain vagrant or heretical Oriental monks in\n   the early church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saraband', 'n.', 'A slow Spanish dance of Saracenic origin, to an air in\n   triple time; also, the air itself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saracen', 'n.', 'Anciently, an Arab; later, a Mussulman; in the Middle\n   Ages, the common term among Christians in Europe for a Mohammedan\n   hostile to the crusaders.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saracenic', 'a.', 'Alt. of Saracenical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saracenical', 'a.', 'Of or pertaining to the Saracens; as, Saracenic\n   architecture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sarasin', 'n.', 'See Sarrasin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saraswati', 'n.', 'The sakti or wife of Brahma; the Hindoo goddess of\n   learning, music, and poetry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sarcasm', 'n.', 'A keen, reproachful expression; a satirical remark uttered\n   with some degree of scorn or contempt; a taunt; a gibe; a cutting jest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sarcasmous', 'a.', 'Sarcastic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sarcastic', 'a.', 'Alt. of Sarcastical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sarcastical', 'a.', 'Expressing, or expressed by, sarcasm; characterized\n   by, or of the nature of, sarcasm; given to the use of sarcasm; bitterly\n   satirical; scornfully severe; taunting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sarcastically', 'adv.', 'In a sarcastic manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sarcel', 'n.', 'One of the outer pinions or feathers of the wing of a bird,\n   esp. of a hawk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sarceled', 'a.', 'Cut through the middle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sarcelle', 'n.', 'The old squaw, or long-tailed duck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sarcenet', 'n.', 'A species of fine thin silk fabric, used for linings,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sarcin', 'n.', 'Same as Hypoxanthin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sarcina', 'n.', 'A genus of bacteria found in various organic fluids,\n   especially in those those of the stomach, associated with certain\n   diseases. The individual organisms undergo division along two\n   perpendicular partitions, so that multiplication takes place in two\n   directions, giving groups of four cubical cells. Also used adjectively;\n   as, a sarcina micrococcus; a sarcina group.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sarcle', 'v. t.', 'To weed, or clear of weeds, with a hoe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sarco-', '', 'A combining form from Gr. sa`rx, sa`rkos, flesh; as,\n   sarcophagous, flesh-eating; sarcology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sarcobases', 'pl. ', 'of Sarcobasis');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sarcobasis', 'n.', 'A fruit consisting of many dry indehiscent cells, which\n   contain but few seeds and cohere about a common style, as in the\n   mallows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sarcoblast', 'n.', 'A minute yellowish body present in the interior of\n   certain rhizopods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sarcocarp', 'n.', 'The fleshy part of a stone fruit, situated between the\n   skin, or epicarp, and the stone, or endocarp, as in a peach. See\n   Illust. of Endocarp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sarcocele', 'n.', 'Any solid tumor of the testicle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sarcocol', 'n.', 'Alt. of Sarcocolla');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sarcocolla', 'n.', 'A gum resin obtained from certain shrubs of Africa\n   (Penaea), -- formerly thought to cause healing of wounds and ulcers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sarcode', 'n.', 'A name applied by Dujardin in 1835 to the gelatinous\n   material forming the bodies of the lowest animals; protoplasm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sarcoderm', 'n.', 'Alt. of sarcoderma');
  INSERT INTO words (word, word_type, description) VALUES  (    'sarcoderma', 'n.', 'A fleshy covering of a seed, lying between the external\n   and internal integuments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'sarcoderma', 'n.', 'A sarcocarp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sarcodic', 'a.', 'Of or pertaining to sarcode.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sarcoid', 'a.', 'Resembling flesh, or muscle; composed of sarcode.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sarcolactic', 'a.', 'Relating to muscle and milk; as, sarcolactic acid. See\n   Lactic acid, under Lactic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sarcolemma', 'n.', 'The very thin transparent and apparently homogeneous\n   sheath which incloses a striated muscular fiber; the myolemma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sarcoline', 'a.', 'Flesh-colored.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sarcologic', 'a.', 'Alt. of Sarcological');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sarcological', 'a.', 'Of or pertaining to sarcology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sarcology', 'n.', 'That part of anatomy which treats of the soft parts. It\n   includes myology, angiology, neurology, and splanchnology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sarcomata', 'pl. ', 'of Sarcoma');
  INSERT INTO words (word, word_type, description) VALUES  (    'sarcomas', 'pl. ', 'of Sarcoma');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sarcoma', 'n.', 'A tumor of fleshy consistence; -- formerly applied to many\n   varieties of tumor, now restricted to a variety of malignant growth\n   made up of cells resembling those of fetal development without any\n   proper intercellular substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sarcomatous', 'a.', 'Of or pertaining to sarcoma; resembling sarcoma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sarcophaga', 'n. pl.', 'A suborder of carnivorous and insectivorous\n   marsupials including the dasyures and the opossums.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sarcophaga', 'n.', 'A genus of Diptera, including the flesh flies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sarcophagan', 'n.', 'Any animal which eats flesh, especially any\n   carnivorous marsupial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sarcophagan', 'n.', 'Any fly of the genus Sarcophaga.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sarcophagous', 'a.', 'Feeding on flesh; flesh-eating; carnivorous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sarcophagi', 'pl. ', 'of Sarcophagus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sarcophaguses', 'pl. ', 'of Sarcophagus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sarcophagus', 'n.', 'A species of limestone used among the Greeks for\n   making coffins, which was so called because it consumed within a few\n   weeks the flesh of bodies deposited in it. It is otherwise called lapis\n   Assius, or Assian stone, and is said to have been found at Assos, a\n   city of Lycia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sarcophagus', 'n.', 'A coffin or chest-shaped tomb of the kind of stone\n   described above; hence, any stone coffin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sarcophagus', 'n.', 'A stone shaped like a sarcophagus and placed by a\n   grave as a memorial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sarcophagy', 'n.', 'The practice of eating flesh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sarcophile', 'n.', 'A flesh-eating animal, especially any one of the\n   carnivorous marsupials.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sarcoptes', 'n.', 'A genus of parasitic mites including the itch mites.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sarcoptid', 'n.', 'Any species of the genus Sarcoptes and related genera of\n   mites, comprising the itch mites and mange mites.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sarcoptid', 'a.', 'Of or pertaining to the itch mites.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sarcorhamphi', 'n. pl.', 'A division of raptorial birds comprising the\n   vultures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sarcosepta', 'pl. ', 'of Sarcoseptum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sarcoseptum', 'n.', 'One of the mesenteries of an anthozoan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sarcosin', 'n.', 'A crystalline nitrogenous substance, formed in the\n   decomposition of creatin (one of the constituents of muscle tissue).\n   Chemically, it is methyl glycocoll.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sarcosis', 'n.', 'Abnormal formation of flesh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sarcosis', 'n.', 'Sarcoma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sarcotic', 'a.', 'Producing or promoting the growth of flesh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sarcotic', 'n.', 'A sarcotic medicine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sarcous', 'a.', 'Fleshy; -- applied to the minute structural elements,\n   called sarcous elements, or sarcous disks, of which striated muscular\n   fiber is composed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sarculation', 'n.', 'A weeding, as with a hoe or a rake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sard', 'n.', 'A variety of carnelian, of a rich reddish yellow or brownish\n   red color. See the Note under Chalcedony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sardachate', 'n.', 'A variety of agate containing sard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sardan', 'n.', 'Alt. of Sardel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sardel', 'n.', 'A sardine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sardel', 'n.', 'A precious stone. See Sardius.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sardine', 'n.', 'Any one of several small species of herring which are\n   commonly preserved in olive oil for food, especially the pilchard, or\n   European sardine (Clupea pilchardus). The California sardine (Clupea\n   sagax) is similar. The American sardines of the Atlantic coast are\n   mostly the young of the common herring and of the menhaden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sardine', 'n.', 'See Sardius.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sardinian', 'a.', 'Of or pertaining to the island, kingdom, or people of\n   Sardinia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sardinian', 'n.', 'A native or inhabitant of Sardinia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sardius', 'n.', 'A precious stone, probably a carnelian, one of which was\n   set in Aarons breastplate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sardoin', 'n.', 'Sard; carnelian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sardonian', 'a.', 'Sardonic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sardonic', 'a.', 'Forced; unnatural; insincere; hence, derisive, mocking,\n   malignant, or bitterly sarcastic; -- applied only to a laugh, smile, or\n   some facial semblance of gayety.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sardonic', 'a.', 'Of, pertaining to, or resembling, a kind of linen made at\n   Colchis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sardonyx', 'n.', 'A variety of onyx consisting of sard and white chalcedony\n   in alternate layers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saree', 'n.', 'The principal garment of a Hindoo woman. It consists of a\n   long piece of cloth, which is wrapped round the middle of the body, a\n   portion being arranged to hang down in front, and the remainder passed\n   across the bosom over the left shoulder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sargasso', 'n.', 'The gulf weed. See under Gulf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sargassum', 'n.', 'A genus of algae including the gulf weed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sargo', 'n.', 'Any one of several species of sparoid fishes belonging to\n   Sargus, Pomadasys, and related genera; -- called also sar, and saragu.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sari', 'n.', 'Same as Saree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sarigue', 'n.', 'A small South American opossum (Didelphys opossum), having\n   four white spots on the face.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sark', 'n.', 'A shirt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sark', 'v. t.', 'To cover with sarking, or thin boards.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sarkin', 'n.', 'Same as Hypoxanthin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sarking', 'n.', 'Thin boards for sheathing, as above the rafters, and under\n   the shingles or slates, and for similar purposes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sarlac', 'n.', 'Alt. of Sarlyk');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sarlyk', 'n.', 'The yak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sarmatian', 'a.', 'Alt. of Sarmatic');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sarmatic', 'a.', 'Of or pertaining to Sarmatia, or its inhabitants, the\n   ancestors of the Russians and the Poles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sarment', 'n.', 'A prostrate filiform stem or runner, as of the strawberry.\n   See Runner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sarmentaceous', 'a.', 'Bearing sarments, or runners, as the strawberry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sarmentose', 'a.', 'Long and filiform, and almost naked, or having only\n   leaves at the joints where it strikes root; as, a sarmentose stem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sarmentose', 'a.', 'Bearing sarments; sarmentaceous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sarmentous', 'a.', 'Sarmentose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sarn', 'n.', 'A pavement or stepping-stone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sarong', 'n.', 'A sort of petticoat worn by both sexes in Java and the\n   Malay Archipelago.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saros', 'n.', 'A Chaldean astronomical period or cycle, the length of which\n   has been variously estimated from 3,600 years to 3,600 days, or a\n   little short of 10 years.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sarplar', 'n.', 'A large bale or package of wool, containing eighty tods,\n   or 2,240 pounds, in weight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sarplier', 'n.', 'A coarse cloth made of hemp, and used for packing goods,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sarpo', 'n.', 'A large toadfish of the Southern United States and the Gulf\n   of Mexico (Batrachus tau, var. pardus).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sarracenia', 'n.', 'A genus of American perennial herbs growing in bogs;\n   the American pitcher plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sarrasin', 'n.', 'Alt. of Sarrasine');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sarrasine', 'n.', 'A portcullis, or herse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sarsa', 'n.', 'Sarsaparilla.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sarsaparilla', 'n.', 'Any plant of several tropical American species of\n   Smilax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sarsaparilla', 'n.', 'The bitter mucilaginous roots of such plants, used in\n   medicine and in sirups for soda, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sarsaparillin', 'n.', 'See Parillin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sarse', 'n.', 'A fine sieve; a searce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sarse', 'v. t.', 'To sift through a sarse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sarsen', 'n.', 'One of the large sandstone blocks scattered over the\n   English chalk downs; -- called also sarsen stone, and Druid stone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sarsenet', 'n.', 'See Sarcenet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sart', 'n.', 'An assart, or clearing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sartorial', 'a.', 'Of or pertaining to a tailor or his work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sartorial', 'a.', 'Of or pertaining to the sartorius muscle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sartorius', 'n.', 'A muscle of the thigh, called the tailors muscle, which\n   arises from the hip bone and is inserted just below the knee. So named\n   because its contraction was supposed to produce the position of the\n   legs assumed by the tailor in sitting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sarum use', '', 'A liturgy, or use, put forth about 1087 by St. Osmund,\n   bishop of Sarum, based on Anglo-Saxon and Norman customs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sash', 'n.', 'A scarf or band worn about the waist, over the shoulder, or\n   otherwise; a belt; a girdle, -- worn by women and children as an\n   ornament; also worn as a badge of distinction by military officers,\n   members of societies, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sash', 'v. t.', 'To adorn with a sash or scarf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sash', 'n.', 'The framing in which the panes of glass are set in a glazed\n   window or door, including the narrow bars between the panes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sash', 'n.', 'In a sawmill, the rectangular frame in which the saw is\n   strained and by which it is carried up and down with a reciprocating\n   motion; -- also called gate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sashed', 'imp. & p. p.', 'of Sash');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sashing', 'p. pr. & vb. n.', 'of Sash');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sash', 'v. t.', 'To furnish with a sash or sashes; as, to sash a door or a\n   window.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sashery', 'n.', 'A collection of sashes; ornamentation by means of sashes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sashoon', 'n.', 'A kind of pad worn on the leg under the boot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sasin', 'n.', 'The Indian antelope (Antilope bezoartica, / cervicapra),\n   noted for its beauty and swiftness. It has long, spiral, divergent\n   horns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sassaby', 'n.', 'Alt. of Sassabye');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sassabye', 'n.', 'A large African antelope (Alcelaphus lunata), similar to\n   the hartbeest, but having its horns regularly curved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sassafras', 'n.', 'An American tree of the Laurel family (Sassafras\n   officinale); also, the bark of the roots, which has an aromatic smell\n   and taste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sassanage', 'n.', 'Stones left after sifting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sassarara', 'n.', 'A word used to emphasize a statement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sasse', 'n.', 'A sluice or lock, as in a river, to make it more navigable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sassenach', 'n.', 'A Saxon; an Englishman; a Lowlander.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sassolin', 'n.', 'Alt. of Sassoline');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sassoline', 'n.', 'Native boric acid, found in saline incrustations on the\n   borders of hot springs near Sasso, in the territory of Florence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sassorol', 'n.', 'Alt. of Sassorolla');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sassorolla', 'n.', 'The rock pigeon. See under Pigeon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sassy bark', '', 'The bark of a West African leguminous tree\n   (Erythrophlaeum Guineense, used by the natives as an ordeal poison, and\n   also medicinally; -- called also mancona bark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sastra', 'n.', 'Same as Shaster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sat', '', 'imp. of Sit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Satan', 'n.', 'The grand adversary of man; the Devil, or Prince of\n   darkness; the chief of the fallen angels; the archfiend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Satanic', 'a.', 'Alt. of Satanical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Satanical', 'a.', 'Of or pertaining to Satan; having the qualities of\n   Satan; resembling Satan; extremely malicious or wicked; devilish;\n   infernal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Satanism', 'n.', 'The evil and malicious disposition of Satan; a diabolical\n   spirit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Satanist', 'n.', 'A very wicked person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Satanophany', 'n.', 'An incarnation of Satan; a being possessed by a demon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Satchel', 'n.', 'A little sack or bag for carrying papers, books, or small\n   articles of wearing apparel; a hand bag.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sated', 'imp. & p. p.', 'of Sate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sating', 'p. pr. & vb. n.', 'of Sate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sate', 'v. t.', 'To satisfy the desire or appetite of; to satiate; to glut;\n   to surfeit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sate', '', 'imp. of Sit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sateen', 'n.', 'A kind of dress goods made of cotton or woolen, with a\n   glossy surface resembling satin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sateless', 'a.', 'Insatiable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Satellite', 'n.', 'An attendant attached to a prince or other powerful\n   person; hence, an obsequious dependent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Satellite', 'n.', 'A secondary planet which revolves about another planet;\n   as, the moon is a satellite of the earth. See Solar system, under\n   Solar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Satellite', 'a.', 'Situated near; accompanying; as, the satellite veins,\n   those which accompany the arteries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Satellitious', 'a.', 'Pertaining to, or consisting of, satellites.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sathanas', 'n.', 'Satan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Satiate', 'a.', 'Filled to satiety; glutted; sated; -- followed by with or\n   of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Satiated', 'imp. & p. p.', 'of Satiate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Satiating', 'p. pr. & vb. n.', 'of Satiate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Satiate', 'v. t.', 'To satisfy the appetite or desire of; to feed to the\n   full; to furnish enjoyment to, to the extent of desire; to sate; as, to\n   satiate appetite or sense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Satiate', 'v. t.', 'To full beyond natural desire; to gratify to repletion\n   or loathing; to surfeit; to glut.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Satiate', 'v. t.', 'To saturate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Satiation', 'n.', 'Satiety.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Satiety', 'n.', 'The state of being satiated or glutted; fullness of\n   gratification, either of the appetite or of any sensual desire;\n   fullness beyond desire; an excess of gratification which excites\n   wearisomeness or loathing; repletion; satiation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Satin', 'n.', 'A silk cloth, of a thick, close texture, and overshot woof,\n   which has a glossy surface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Satinet', 'n.', 'A thin kind of satin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Satinet', 'n.', 'A kind of cloth made of cotton warp and woolen filling,\n   used chiefly for trousers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Satinwood', 'n.', 'The hard, lemon-colored, fragrant wood of an East Indian\n   tree (Chloroxylon Swietenia). It takes a lustrous finish, and is used\n   in cabinetwork. The name is also given to the wood of a species of\n   prickly ash (Xanthoxylum Caribaeum) growing in Florida and the West\n   Indies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Satiny', 'a.', 'Like or composed of satin; glossy; as, to have a satiny\n   appearance; a satiny texture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sation', 'n.', 'A sowing or planting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Satire', 'a.', 'A composition, generally poetical, holding up vice or folly\n   to reprobation; a keen or severe exposure of what in public or private\n   morals deserves rebuke; an invective poem; as, the Satires of Juvenal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Satire', 'a.', 'Keeness and severity of remark; caustic exposure to\n   reprobation; trenchant wit; sarcasm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Satiric', 'a.', 'Alt. of Satirical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Satirical', 'a.', 'Of or pertaining to satire; of the nature of satire; as,\n   a satiric style.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Satirical', 'a.', 'Censorious; severe in language; sarcastic; insulting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Satirist', 'n.', 'One who satirizes; especially, one who writes satire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Satirized', 'imp. & p. p.', 'of Satirize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Satirizing', 'p. pr. & vb. n.', 'of Satirize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Satirize', 'v. t.', 'To make the object of satire; to attack with satire;\n   to censure with keenness or severe sarcasm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Satisfaction', 'n.', 'The act of satisfying, or the state of being\n   satisfied; gratification of desire; contentment in possession and\n   enjoyment; repose of mind resulting from compliance with its desires or\n   demands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Satisfaction', 'n.', 'Settlement of a claim, due, or demand; payment;\n   indemnification; adequate compensation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Satisfaction', 'n.', 'That which satisfies or gratifies; atonement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Satisfactive', 'a.', 'Satisfactory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Satisfactory', 'a.', 'Giving or producing satisfaction; yielding content;\n   especially, relieving the mind from doubt or uncertainty, and enabling\n   it to rest with confidence; sufficient; as, a satisfactory account or\n   explanation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Satisfactory', 'a.', 'Making amends, indemnification, or recompense;\n   causing to cease from claims and to rest content; compensating;\n   atoning; as, to make satisfactory compensation, or a satisfactory\n   apology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Satisfiable', 'a.', 'That may be satisfied.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Satisfier', 'n.', 'One who satisfies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Satisfied', 'imp. & p. p.', 'of Satisfy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Satisfying', 'p. pr. & vb. n.', 'of Satisfy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Satisfy', 'a.', 'In general, to fill up the measure of a want of (a person\n   or a thing); hence, to grafity fully the desire of; to make content; to\n   supply to the full, or so far as to give contentment with what is\n   wished for.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Satisfy', 'a.', 'To pay to the extent of claims or deserts; to give what is\n   due to; as, to satisfy a creditor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Satisfy', 'a.', 'To answer or discharge, as a claim, debt, legal demand, or\n   the like; to give compensation for; to pay off; to requite; as, to\n   satisfy a claim or an execution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Satisfy', 'a.', 'To free from doubt, suspense, or uncertainty; to give\n   assurance to; to set at rest the mind of; to convince; as, to satisfy\n   ones self by inquiry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Satisfy', 'v. i.', 'To give satisfaction; to afford gratification; to leave\n   nothing to be desired.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Satisfy', 'v. i.', 'To make payment or atonement; to atone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Satisfyingly', 'adv.', 'So as to satisfy; satisfactorily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sative', 'a.', 'Sown; propagated by seed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Satle', 'v. t. & i.', 'To settle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Satrap', 'n.', 'The governor of a province in ancient Persia; hence, a\n   petty autocrat despot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Satrapal', 'a.', 'Of or pertaining to a satrap, or a satrapy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Satrapess', 'n.', 'A female satrap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Satrapical', 'a.', 'Satrapal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Satrapies', 'pl. ', 'of Satrapy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Satrapy', 'n.', 'The government or jurisdiction of a satrap; a\n   principality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Satsuma ware', '', 'A kind of ornamental hard-glazed pottery made at\n   Satsuma in Kiushu, one of the Japanese islands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saturable', 'a.', 'Capable of being saturated; admitting of saturation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saturant', 'a.', 'Impregnating to the full; saturating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saturant', 'n.', 'A substance used to neutralize or saturate the affinity\n   of another substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saturant', 'n.', 'An antacid, as magnesia, used to correct acidity of the\n   stomach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saturated', 'imp. & p. p.', 'of Saturate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saturating', 'p. pr. & vb. n.', 'of Saturate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saturate', 'v. t.', 'To cause to become completely penetrated, impregnated,\n   or soaked; to fill fully; to sate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saturate', 'v. t.', 'To satisfy the affinity of; to cause to become inert\n   by chemical combination with all that it can hold; as, to saturate\n   phosphorus with chlorine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saturate', 'p. a.', 'Filled to repletion; saturated; soaked.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saturated', 'a.', 'Filled to repletion; holding by absorption, or in\n   solution, all that is possible; as, saturated garments; a saturated\n   solution of salt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saturated', 'a.', 'Having its affinity satisfied; combined with all it can\n   hold; -- said of certain atoms, radicals, or compounds; thus, methane\n   is a saturated compound. Contrasted with unsaturated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saturation', 'n.', 'The act of saturating, or the state of being\n   saturating; complete penetration or impregnation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saturation', 'n.', 'The act, process, or result of saturating a substance,\n   or of combining it to its fullest extent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saturation', 'n.', 'Freedom from mixture or dilution with white; purity; --\n   said of colors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saturator', 'n.', 'One who, or that which, saturates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saturday', 'n.', 'The seventh or last day of the week; the day following\n   Friday and preceding Sunday.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saturity', 'n.', 'The state of being saturated; fullness of supply.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saturn', 'n.', 'One of the elder and principal deities, the son of Coelus\n   and Terra (Heaven and Earth), and the father of Jupiter. The\n   corresponding Greek divinity was Kro`nos, later CHro`nos, Time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saturn', 'n.', 'One of the planets of the solar system, next in magnitude\n   to Jupiter, but more remote from the sun. Its diameter is seventy\n   thousand miles, its mean distance from the sun nearly eight hundred and\n   eighty millions of miles, and its year, or periodical revolution round\n   the sun, nearly twenty-nine years and a half. It is surrounded by a\n   remarkable system of rings, and has eight satellites.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saturn', 'n.', 'The metal lead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saturnalia', 'n. pl.', 'The festival of Saturn, celebrated in December,\n   originally during one day, but afterward during seven days, as a period\n   of unrestrained license and merriment for all classes, extending even\n   to the slaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saturnalia', 'n. pl.', 'Hence: A period or occasion of general license, in\n   which the passions or vices have riotous indulgence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saturnalian', 'a.', 'Of or pertaining to the Saturnalia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saturnalian', 'a.', 'Of unrestrained and intemperate jollity; riotously\n   merry; dissolute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saturnian', 'a.', 'Of or pertaining to Saturn, whose age or reign, from the\n   mildness and wisdom of his government, is called the golden age.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saturnian', 'a.', 'Hence: Resembling the golden age; distinguished for\n   peacefulness, happiness, contentment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saturnian', 'a.', 'Of or pertaining to the planet Saturn; as, the Saturnian\n   year.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saturnian', 'n.', 'Any one of numerous species of large handsome moths\n   belonging to Saturnia and allied genera. The luna moth, polyphemus, and\n   promethea, are examples. They belong to the Silkworn family, and some\n   are raised for their silk. See Polyphemus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saturnicentric', 'a.', 'Appearing as if seen from the center of the planet\n   Saturn; relating or referred to Saturn as a center.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saturnine', 'a.', 'Born under, or influenced by, the planet Saturn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saturnine', 'a.', 'Heavy; grave; gloomy; dull; -- the opposite of\n   mercurial; as, a saturnine person or temper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saturnine', 'a.', 'Of or pertaining to lead; characterized by, or\n   resembling, lead, which was formerly called Saturn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saturnism', 'n.', 'Plumbism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saturnist', 'n.', 'A person of a dull, grave, gloomy temperament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Satyr', 'n.', 'A sylvan deity or demigod, represented as part man and part\n   goat, and characterized by riotous merriment and lasciviousness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Satyr', 'n.', 'Any one of many species of butterflies belonging to the\n   family Nymphalidae. Their colors are commonly brown and gray, often\n   with ocelli on the wings. Called also meadow browns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Satyr', 'n.', 'The orang-outang.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Satyriasis', 'n.', 'Immoderate venereal appetite in the male.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Satyric', 'a.', 'Alt. of Satyrical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Satyrical', 'a.', 'Of or pertaining to satyrs; burlesque; as, satyric\n   tragedy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Satyrion', 'n.', 'Any one of several kinds of orchids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sauba ant', '', 'A South American ant (Oecodoma cephalotes) remarkable for\n   having two large kinds of workers besides the ordinary ones, and for\n   the immense size of its formicaries. The sauba ant cuts off leaves of\n   plants and carries them into its subterranean nests, and thus often\n   does great damage by defoliating trees and cultivated plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sauce', 'n.', 'A composition of condiments and appetizing ingredients eaten\n   with food as a relish; especially, a dressing for meat or fish or for\n   puddings; as, mint sauce; sweet sauce, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sauce', 'n.', 'Any garden vegetables eaten with meat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sauce', 'n.', 'Stewed or preserved fruit eaten with other food as a relish;\n   as, apple sauce, cranberry sauce, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sauce', 'n.', 'Sauciness; impertinence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sauced', 'imp. & p. p.', 'of Sauce');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saucing', 'p. pr. & vb. n.', 'of Sauce');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sauce', 'v. t.', 'To accompany with something intended to give a higher\n   relish; to supply with appetizing condiments; to season; to flavor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sauce', 'v. t.', 'To cause to relish anything, as if with a sauce; to\n   tickle or gratify, as the palate; to please; to stimulate; hence, to\n   cover, mingle, or dress, as if with sauce; to make an application to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sauce', 'v. t.', 'To make poignant; to give zest, flavor or interest to; to\n   set off; to vary and render attractive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sauce', 'v. t.', 'To treat with bitter, pert, or tart language; to be\n   impudent or saucy to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sauce', 'n.', 'A soft crayon for use in stump drawing or in shading with\n   the stump.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sauce-alone', 'n.', 'Jack-by-the-hedge. See under Jack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saucebox', 'n.', 'A saucy, impudent person; especially, a pert child.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saucepan', 'n.', 'A small pan with a handle, in which sauce is prepared\n   over a fire; a stewpan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saucer', 'n.', 'A small pan or vessel in which sauce was set on a table.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saucer', 'n.', 'A small dish, commonly deeper than a plate, in which a cup\n   is set at table.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saucer', 'n.', 'Something resembling a saucer in shape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saucer', 'n.', 'A flat, shallow caisson for raising sunken ships.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saucer', 'n.', 'A shallow socket for the pivot of a capstan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saucily', 'adv.', 'In a saucy manner; impudently; with impertinent\n   boldness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sauciness', 'n.', 'The quality or state of being saucy; that which is\n   saucy; impertinent boldness; contempt of superiors; impudence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saucisson', 'n.', 'Alt. of Saucisse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saucisse', 'n.', 'A long and slender pipe or bag, made of cloth well\n   pitched, or of leather, filled with powder, and used to communicate\n   fire to mines, caissons, bomb chests, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saucisse', 'n.', 'A fascine of more than ordinary length.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saucy', 'superl.', 'Showing impertinent boldness or pertness; transgressing\n   the rules of decorum; treating superiors with contempt; impudent;\n   insolent; as, a saucy fellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saucy', 'superl.', 'Expressive of, or characterized by, impudence;\n   impertinent; as, a saucy eye; saucy looks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sauerkraut', 'n.', 'Cabbage cut fine and allowed to ferment in a brine made\n   of its own juice with salt, -- a German dish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sauf', 'a.', 'Safe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sauf', 'conj. & prep.', 'Save; except.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saufly', 'adv.', 'Safely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sauger', 'n.', 'An American fresh-water food fish (Stizostedion Canadense);\n   -- called also gray pike, blue pike, hornfish, land pike, sand pike,\n   pickering, and pickerel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saugh', '', 'Alt. of Sauh');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sauh', '', 'imp. sing. of See.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sauks', 'n. pl.', 'Same as Sacs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saul', 'n.', 'Soul.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saul', 'n.', 'Same as Sal, the tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saulie', 'n.', 'A hired mourner at a funeral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sault', 'n.', 'A rapid in some rivers; as, the Sault Ste. Marie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saunders', 'n.', 'See Sandress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saunders-blue', 'n.', 'A kind of color prepared from calcined lapis lazuli;\n   ultramarine; also, a blue prepared from carbonate of copper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sauntered', 'imp. & p. p.', 'of Saunter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sauntering', 'p. pr. & vb. n.', 'of Saunter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saunter', 'n. & v.', 'To wander or walk about idly and in a leisurely or\n   lazy manner; to lounge; to stroll; to loiter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saunter', 'n.', 'A sauntering, or a sauntering place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saunterer', 'n.', 'One who saunters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saur', 'n.', 'Soil; dirt; dirty water; urine from a cowhouse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saurel', 'n.', 'Any carangoid fish of the genus Trachurus, especially T.\n   trachurus, or T. saurus, of Europe and America, and T. picturatus of\n   California. Called also skipjack, and horse mackerel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sauria', 'n. pl.', 'A division of Reptilia formerly established to include\n   the Lacertilia, Crocodilia, Dinosauria, and other groups. By some\n   writers the name is restricted to the Lacertilia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saurian', 'a.', 'Of or pertaining to, or of the nature of, the Sauria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saurian', 'n.', 'One of the Sauria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saurioid', 'a.', 'Same as Sauroid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saurobatrachia', 'n. pl.', 'The Urodela.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saurognathous', 'a.', 'Having the bones of the palate arranged as in\n   saurians, the vomer consisting of two lateral halves, as in the\n   woodpeckers (Pici).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sauroid', 'a.', 'Like or pertaining to the saurians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sauroid', 'a.', 'Resembling a saurian superficially; as, a sauroid fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sauroidichnite', 'n.', 'The fossil track of a saurian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sauropoda', 'n. pl.', 'An extinct order of herbivorous dinosaurs having the\n   feet of a saurian type, instead of birdlike, as they are in many\n   dinosaurs. It includes the largest known land animals, belonging to\n   Brontosaurus, Camarasaurus, and allied genera. See Illustration in\n   Appendix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sauropsida', 'n. pl.', 'A comprehensive group of vertebrates, comprising\n   the reptiles and birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sauropterygia', 'n. pl.', 'Same as Plesiosauria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saururae', 'n. pl.', 'An extinct order of birds having a long vertebrated\n   tail with quills along each side of it. Archaeopteryx is the type. See\n   Archaeopteryx, and Odontornithes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sauries', 'pl. ', 'of Saury');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saury', 'n.', 'A slender marine fish (Scomberesox saurus) of Europe and\n   America. It has long, thin, beaklike jaws. Called also billfish,\n   gowdnook, gawnook, skipper, skipjack, skopster, lizard fish, and Egypt\n   herring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sausage', 'n.', 'An article of food consisting of meat (esp. pork) minced\n   and highly seasoned, and inclosed in a cylindrical case or skin usually\n   made of the prepared intestine of some animal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sausage', 'n.', 'A saucisson. See Saucisson.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sauseflem', 'a.', 'Having a red, pimpled face.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saussurite', 'n.', 'A tough, compact mineral, of a white, greenish, or\n   grayish color. It is near zoisite in composition, and in part, at\n   least, has been produced by the alteration of feldspar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saut', 'n.', 'Alt. of Saute');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saute', 'n.', 'An assault.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saute', '', 'p. p. of Sauter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sauter', 'v. t.', 'To fry lightly and quickly, as meat, by turning or\n   tossing it over frequently in a hot pan greased with a little fat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sauter', 'n.', 'Psalter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sauterelle', 'n.', 'An instrument used by masons and others to trace and\n   form angles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sauterne', 'n.', 'A white wine made in the district of Sauterne, France.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sautrie', 'n.', 'Psaltery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sauvegarde', 'n.', 'The monitor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Savable', 'a.', 'Capable of, or admitting of, being saved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Savableness', 'n.', 'Capability of being saved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Savacioun', 'n.', 'Salvation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Savage', 'a.', 'Of or pertaining to the forest; remote from human abodes\n   and cultivation; in a state of nature; wild; as, a savage wilderness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Savage', 'a.', 'Wild; untamed; uncultivated; as, savage beasts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Savage', 'a.', 'Uncivilized; untaught; unpolished; rude; as, savage life;\n   savage manners.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Savage', 'a.', 'Characterized by cruelty; barbarous; fierce; ferocious;\n   inhuman; brutal; as, a savage spirit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Savage', 'n.', 'A human being in his native state of rudeness; one who is\n   untaught, uncivilized, or without cultivation of mind or manners.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Savage', 'n.', 'A man of extreme, unfeeling, brutal cruelty; a barbarian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Savage', 'v. t.', 'To make savage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Savagely', 'adv.', 'In a savage manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Savageness', 'n.', 'The state or quality of being savage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Savagery', 'n.', 'The state of being savage; savageness; savagism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Savagery', 'n.', 'An act of cruelty; barbarity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Savagery', 'n.', 'Wild growth, as of plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Savagism', 'n.', 'The state of being savage; the state of rude, uncivilized\n   men, or of men in their native wildness and rudeness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Savanilla', 'n.', 'The tarpum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Savanna', 'n.', 'A tract of level land covered with the vegetable growth\n   usually found in a damp soil and warm climate, -- as grass or reeds, --\n   but destitute of trees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Savants', 'pl. ', 'of Savant');
  INSERT INTO words (word, word_type, description) VALUES  (    'Savant', 'a.', 'A man of learning; one versed in literature or science; a\n   person eminent for acquirements.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Save', 'n.', 'The herb sage, or salvia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saved', 'imp. & p. p.', 'of Save');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saving', 'p. pr. & vb. n.', 'of Save');
  INSERT INTO words (word, word_type, description) VALUES  (    'Save', 'a.', 'To make safe; to procure the safety of; to preserve from\n   injury, destruction, or evil of any kind; to rescue from impending\n   danger; as, to save a house from the flames.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Save', 'a.', 'Specifically, to deliver from sin and its penalty; to rescue\n   from a state of condemnation and spiritual death, and bring into a\n   state of spiritual life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Save', 'a.', 'To keep from being spent or lost; to secure from waste or\n   expenditure; to lay up; to reserve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Save', 'a.', 'To rescue from something undesirable or hurtful; to prevent\n   from doing something; to spare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Save', 'a.', 'To hinder from doing, suffering, or happening; to obviate the\n   necessity of; to prevent; to spare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Save', 'a.', 'To hold possession or use of; to escape loss of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Save', 'v. i.', 'To avoid unnecessary expense or expenditure; to prevent\n   waste; to be economical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Save', 'a.', 'Except; excepting; not including; leaving out; deducting;\n   reserving; saving.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Save', 'conj.', 'Except; unless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saveable', 'a.', 'See Savable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Save-all', 'n.', 'Anything which saves fragments, or prevents waste or\n   loss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Save-all', 'n.', 'A device in a candlestick to hold the ends of candles, so\n   that they be burned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Save-all', 'n.', 'A small sail sometimes set under the foot of another\n   sail, to catch the wind that would pass under it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saveloy', 'n.', 'A kind of dried sausage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Savely', 'adv.', 'Safely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Savement', 'n.', 'The act of saving.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saver', 'n.', 'One who saves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Savin', 'n.', 'Alt. of Savine');
  INSERT INTO words (word, word_type, description) VALUES  (    'Savine', 'n.', 'A coniferous shrub (Juniperus Sabina) of Western Asia,\n   occasionally found also in the northern parts of the United States and\n   in British America. It is a compact bush, with dark-colored foliage,\n   and produces small berries having a glaucous bloom. Its bitter, acrid\n   tops are sometimes used in medicine for gout, amenorrhoea, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Savine', 'n.', 'The North American red cedar (Juniperus Virginiana.)');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saving', 'a.', 'Preserving; rescuing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saving', 'a.', 'Avoiding unnecessary expense or waste; frugal; not lavish\n   or wasteful; economical; as, a saving cook.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saving', 'a.', 'Bringing back in returns or in receipts the sum expended;\n   incurring no loss, though not gainful; as, a saving bargain; the ship\n   has made a saving voyage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saving', 'a.', 'Making reservation or exception; as, a saving clause.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saving', 'participle', 'With the exception of; except; excepting; also,\n   without disrespect to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saving', 'n.', 'Something kept from being expended or lost; that which is\n   saved or laid up; as, the savings of years of economy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saving', 'n.', 'Exception; reservation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Savingly', 'adv.', 'In a saving manner; with frugality or parsimony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Savingly', 'adv.', 'So as to be finally saved from eternal death.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Savingness', 'n.', 'The quality of being saving; carefulness not to expend\n   money uselessly; frugality; parsimony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Savingness', 'n.', 'Tendency to promote salvation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Savior', 'v.', 'One who saves, preserves, or delivers from destruction or\n   danger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Savior', 'v.', 'Specifically: The (or our, your, etc.) Savior, he who\n   brings salvation to men; Jesus Christ, the Redeemer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Savioress', 'n.', 'A female savior.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Savor', 'a.', 'That property of a thing which affects the organs of taste\n   or smell; taste and odor; flavor; relish; scent; as, the savor of an\n   orange or a rose; an ill savor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Savor', 'a.', 'Hence, specific flavor or quality; characteristic property;\n   distinctive temper, tinge, taint, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Savor', 'a.', 'Sense of smell; power to scent, or trace by scent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Savor', 'a.', 'Pleasure; delight; attractiveness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Savored', 'imp. & p. p.', 'of Savor');
  INSERT INTO words (word, word_type, description) VALUES  (    'Savoring', 'p. pr. & vb. n.', 'of Savor');
  INSERT INTO words (word, word_type, description) VALUES  (    'Savor', 'n.', 'To have a particular smell or taste; -- with of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Savor', 'n.', 'To partake of the quality or nature; to indicate the\n   presence or influence; to smack; -- with of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Savor', 'n.', 'To use the sense of taste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Savor', 'v. t.', 'To perceive by the smell or the taste; hence, to\n   perceive; to note.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Savor', 'v. t.', 'To have the flavor or quality of; to indicate the\n   presence of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Savor', 'v. t.', 'To taste or smell with pleasure; to delight in; to\n   relish; to like; to favor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Savorily', 'adv.', 'In a savory manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Savoriness', 'n.', 'The quality of being savory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Savorless', 'a.', 'Having no savor; destitute of smell or of taste;\n   insipid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Savorly', 'a.', 'Savory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Savorly', 'adv.', 'In a savory manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Savorous', 'n.', 'Having a savor; savory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Savory', 'a.', 'Pleasing to the organs of taste or smell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Savory', 'n.', 'An aromatic labiate plant (Satureia hortensis), much used\n   in cooking; -- also called summer savory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Savoy', 'n.', 'A variety of the common cabbage (Brassica oleracea major),\n   having curled leaves, -- much cultivated for winter use.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Savoyard', 'n.', 'A native or inhabitant of Savoy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saw', '', 'imp. of See.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saw', 'v. t.', 'Something said; speech; discourse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saw', 'v. t.', 'A saying; a proverb; a maxim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saw', 'v. t.', 'Dictate; command; decree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saw', 'n.', 'An instrument for cutting or dividing substances, as wood,\n   iron, etc., consisting of a thin blade, or plate, of steel, with a\n   series of sharp teeth on the edge, which remove successive portions of\n   the material by cutting and tearing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sawed', 'imp.', 'of Saw');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sawed', 'p. p.', 'of Saw');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sawn', '', 'of Saw');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sawing', 'p. pr. & vb. n.', 'of Saw');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saw', 'v. t.', 'To cut with a saw; to separate with a saw; as, to saw\n   timber or marble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saw', 'v. t.', 'To form by cutting with a saw; as, to saw boards or planks,\n   that is, to saw logs or timber into boards or planks; to saw shingles;\n   to saw out a panel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saw', 'v. t.', 'Also used figuratively; as, to saw the air.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saw', 'v. i.', 'To use a saw; to practice sawing; as, a man saws well.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saw', 'v. i.', 'To cut, as a saw; as, the saw or mill saws fast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saw', 'v. i.', 'To be cut with a saw; as, the timber saws smoothly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sawarra nut', '', 'See Souari nut.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sawbelly', 'n.', 'The alewife.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sawbill', 'n.', 'The merganser.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sawbones', 'n.', 'A nickname for a surgeon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sawbuck', 'n.', 'A sawhorse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sawceflem', 'a.', 'See Sauseflem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sawder', 'n.', 'A corrupt spelling and pronunciation of solder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sawdust', 'n.', 'Dust or small fragments of wood (or of stone, etc.) made\n   by the cutting of a saw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sawer', 'n.', 'One who saws; a sawyer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sawfish', 'n.', 'Any one of several species of elasmobranch fishes of the\n   genus Pristis. They have a sharklike form, but are more nearly allied\n   to the rays. The flattened and much elongated snout has a row of stout\n   toothlike structures inserted along each edge, forming a sawlike organ\n   with which it mutilates or kills its prey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sawfly', 'n.', 'Any one of numerous species of hymenopterous insects\n   belonging to the family Tenthredinidae. The female usually has an\n   ovipositor containing a pair of sawlike organs with which she makes\n   incisions in the leaves or stems of plants in which to lay the eggs.\n   The larvae resemble those of Lepidoptera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sawhorse', 'n.', 'A kind of rack, shaped like a double St. Andrews cross,\n   on which sticks of wood are laid for sawing by hand; -- called also\n   buck, and sawbuck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sawmill', 'n.', 'A mill for sawing, especially one for sawing timber or\n   lumber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sawneb', 'n.', 'A merganser.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saw palmetto', '', 'See under Palmetto.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saw-set', 'n.', 'An instrument used to set or turn the teeth of a saw a\n   little sidewise, that they may make a kerf somewhat wider than the\n   thickness of the blade, to prevent friction; -- called also saw-wrest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sawtooth', 'n.', 'An arctic seal (Lobodon carcinophaga), having the molars\n   serrated; -- called also crab-eating seal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saw-toothed', 'a.', 'Having a tooth or teeth like those of a saw; serrate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sawtry', 'n.', 'A psaltery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saw-whet', 'n.', 'A small North American owl (Nyctale Acadica), destitute\n   of ear tufts and having feathered toes; -- called also Acadian owl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saw-wort', 'n.', 'Any plant of the composite genus Serratula; -- so named\n   from the serrated leaves of most of the species.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saw-wrest', 'n.', 'See Saw-set.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sawyer', 'n.', 'One whose occupation is to saw timber into planks or\n   boards, or to saw wood for fuel; a sawer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sawyer', 'n.', 'A tree which has fallen into a stream so that its branches\n   project above the surface, rising and falling with a rocking or swaying\n   motion in the current.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sawyer', 'n.', 'The bowfin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sax', 'n.', 'A kind of chopping instrument for trimming the edges of\n   roofing slates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saxatile', 'a.', 'Of or pertaining to rocks; living among rocks; as, a\n   saxatile plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saxhorn', 'n.', 'A name given to a numerous family of brass wind\n   instruments with valves, invented by Antoine Joseph Adolphe Sax (known\n   as Adolphe Sax), of Belgium and Paris, and much used in military bands\n   and in orchestras.');
  INSERT INTO words (word, word_type, description) VALUES  (    'saxicavas', 'pl. ', 'of Saxicava');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saxicavae', 'pl. ', 'of Saxicava');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saxicava', 'n.', 'Any species of marine bivalve shells of the genus\n   Saxicava. Some of the species are noted for their power of boring holes\n   in limestone and similar rocks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saxicavid', 'a.', 'Of or pertaining to the saxicavas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saxicavid', 'n.', 'A saxicava.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saxicavous', 'a.', 'Boring, or hollowing out, rocks; -- said of certain\n   mollusks which live in holes which they burrow in rocks. See Illust. of\n   Lithodomus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saxicoline', 'a.', 'Stone-inhabiting; pertaining to, or having the\n   characteristics of, the stonechats.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saxicolous', 'a.', 'Growing on rocks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saxifraga', 'n.', 'A genus of exogenous polypetalous plants, embracing\n   about one hundred and eighty species. See Saxifrage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saxifragaceous', 'a.', 'Of or pertaining to a natural order of plants\n   (Saxifragaceae) of which saxifrage is the type. The order includes also\n   the alum root, the hydrangeas, the mock orange, currants and\n   gooseberries, and many other plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saxifragant', 'a.', 'Breaking or destroying stones; saxifragous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saxifragant', 'n.', 'That which breaks or destroys stones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saxifrage', 'n.', 'Any plant of the genus Saxifraga, mostly perennial herbs\n   growing in crevices of rocks in mountainous regions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saxifragous', 'a.', 'Dissolving stone, especially dissolving stone in the\n   bladder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saxon', 'n.', 'One of a nation or people who formerly dwelt in the northern\n   part of Germany, and who, with other Teutonic tribes, invaded and\n   conquered England in the fifth and sixth centuries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saxon', 'n.', 'Also used in the sense of Anglo-Saxon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saxon', 'n.', 'A native or inhabitant of modern Saxony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saxon', 'n.', 'The language of the Saxons; Anglo-Saxon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saxon', 'a.', 'Of or pertaining to the Saxons, their country, or their\n   language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saxon', 'a.', 'Anglo-Saxon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saxon', 'a.', 'Of or pertaining to Saxony or its inhabitants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saxonic', 'a.', 'Relating to the Saxons or Anglo- Saxons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saxonism', 'n.', 'An idiom of the Saxon or Anglo-Saxon language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saxonist', 'n.', 'One versed in the Saxon language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saxonite', 'n.', 'See Mountain soap, under Mountain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saxophone', 'n.', 'A wind instrument of brass, containing a reed, and\n   partaking of the qualities both of a brass instrument and of a\n   clarinet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sax-tuba', 'n.', 'A powerful instrument of brass, curved somewhat like the\n   Roman buccina, or tuba.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Say', 'imp.', 'Saw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Say', 'n.', 'Trial by sample; assay; sample; specimen; smack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Say', 'n.', 'Tried quality; temper; proof.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Say', 'n.', 'Essay; trial; attempt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Say', 'v. t.', 'To try; to assay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Say', 'n.', 'A kind of silk or satin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Say', 'n.', 'A delicate kind of serge, or woolen cloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Said', 'imp. & p. p.', 'of Say');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saying', 'p. pr. & vb. n.', 'of Say');
  INSERT INTO words (word, word_type, description) VALUES  (    'Say', 'v. t.', 'To utter or express in words; to tell; to speak; to\n   declare; as, he said many wise things.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Say', 'v. t.', 'To repeat; to rehearse; to recite; to pronounce; as, to say\n   a lesson.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Say', 'v. t.', 'To announce as a decision or opinion; to state positively;\n   to assert; hence, to form an opinion upon; to be sure about; to be\n   determined in mind as to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Say', 'v. t.', 'To mention or suggest as an estimate, hypothesis, or\n   approximation; hence, to suppose; -- in the imperative, followed\n   sometimes by the subjunctive; as, he had, say fifty thousand dollars;\n   the fox had run, say ten miles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Say', 'v. i.', 'To speak; to express an opinion; to make answer; to reply.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Say', 'v. t.', 'A speech; something said; an expression of opinion; a\n   current story; a maxim or proverb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sayer', 'n.', 'One who says; an utterer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sayette', 'n.', 'A mixed stuff, called also sagathy. See Sagathy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saying', 'n.', 'That which is said; a declaration; a statement, especially\n   a proverbial one; an aphorism; a proverb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sayman', 'n.', 'One who assays.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saymaster', 'n.', 'A master of assay; one who tries or proves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saynd', '', 'p. p. of Senge, to singe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sblood', 'interj.', 'An abbreviation of Gods blood; -- used as an oath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scab', 'n.', 'An incrustation over a sore, wound, vesicle, or pustule,\n   formed by the drying up of the discharge from the diseased part.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scab', 'n.', 'The itch in man; also, the scurvy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scab', 'n.', 'The mange, esp. when it appears on sheep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scab', 'n.', 'A disease of potatoes producing pits in their surface, caused\n   by a minute fungus (Tiburcinia Scabies).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scab', 'n.', 'A slight irregular protuberance which defaces the surface of\n   a casting, caused by the breaking away of a part of the mold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scab', 'n.', 'A mean, dirty, paltry fellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scab', 'n.', 'A nickname for a workman who engages for lower wages than are\n   fixed by the trades unions; also, for one who takes the place of a\n   workman on a strike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scabbed', 'imp. & p. p.', 'of Scab');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scabbing', 'p. pr. & vb. n.', 'of Scab');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scab', 'v. i.', 'To become covered with a scab; as, the wound scabbed over.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scabbard', 'n.', 'The case in which the blade of a sword, dagger, etc., is\n   kept; a sheath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scabbard', 'v. t.', 'To put in a scabbard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scabbard plane', '', 'See Scaleboard plane, under Scaleboard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scabbed', 'a.', 'Abounding with scabs; diseased with scabs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scabbed', 'a.', 'Fig.: Mean; paltry; vile; worthless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scabbedness', 'n.', 'Scabbiness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scabbily', 'adv.', 'In a scabby manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scabbiness', 'n.', 'The quality or state of being scabby.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scabble', 'v. t.', 'See Scapple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scabby', 'superl.', 'Affected with scabs; full of scabs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scabby', 'superl.', 'Diseased with the scab, or mange; mangy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scabies', 'n.', 'The itch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scabious', 'a.', 'Consisting of scabs; rough; itchy; leprous; as, scabious\n   eruptions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scabious', 'a.', 'Any plant of the genus Scabiosa, several of the species\n   of which are common in Europe. They resemble the Compositae, and have\n   similar heads of flowers, but the anthers are not connected.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scabling', 'n.', 'A fragment or chip of stone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scabredity', 'n.', 'Roughness; ruggedness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scabrous', 'a.', 'Rough to the touch, like a file; having small raised\n   dots, scales, or points; scabby; scurfy; scaly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scabrous', 'a.', 'Fig.: Harsh; unmusical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scabrousness', 'n.', 'The quality of being scabrous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scabwort', 'n.', 'Elecampane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scad', 'n.', 'A small carangoid fish (Trachurus saurus) abundant on the\n   European coast, and less common on the American. The name is applied\n   also to several allied species.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scad', 'n.', 'The goggler; -- called also big-eyed scad. See Goggler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scad', 'n.', 'The friar skate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scad', 'n.', 'The cigar fish, or round robin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scaffold', 'n.', 'A temporary structure of timber, boards, etc., for\n   various purposes, as for supporting workmen and materials in building,\n   for exhibiting a spectacle upon, for holding the spectators at a show,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scaffold', 'n.', 'Specifically, a stage or elevated platform for the\n   execution of a criminal; as, to die on the scaffold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scaffold', 'n.', 'An accumulation of adherent, partly fused material\n   forming a shelf, or dome-shaped obstruction, above the tuyeres in a\n   blast furnace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scaffold', 'v. t.', 'To furnish or uphold with a scaffold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scaffoldage', 'n.', 'A scaffold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scaffolding', 'n.', 'A scaffold; a supporting framework; as, the\n   scaffolding of the body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scaffolding', 'n.', 'Materials for building scaffolds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scaglia', 'n.', 'A reddish variety of limestone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scagliola', 'n.', 'An imitation of any veined and ornamental stone, as\n   marble, formed by a substratum of finely ground gypsum mixed with glue,\n   the surface of which, while soft, is variegated with splinters of\n   marble, spar, granite, etc., and subsequently colored and polished.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scalae', 'pl. ', 'of Scala');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scala', 'n.', 'A machine formerly employed for reducing dislocations of the\n   humerus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scala', 'n.', 'A term applied to any one of the three canals of the\n   cochlea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scalable', 'a.', 'Capable of being scaled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scalade', 'n.', 'Alt. of Scalado');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scalado', 'n.', 'See Escalade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scalar', 'n.', 'In the quaternion analysis, a quantity that has magnitude,\n   but not direction; -- distinguished from a vector, which has both\n   magnitude and direction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scalaria', 'n.', 'Any one of numerous species of marine gastropods of the\n   genus Scalaria, or family Scalaridae, having elongated spiral turreted\n   shells, with rounded whorls, usually crossed by ribs or varices. The\n   color is generally white or pale. Called also ladder shell, and\n   wentletrap. See Ptenoglossa, and Wentletrap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scalariform', 'a.', 'Resembling a ladder in form or appearance; having\n   transverse bars or markings like the rounds of a ladder; as, the\n   scalariform cells and scalariform pits in some plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scalariform', 'a.', 'Like or pertaining to a scalaria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scalary', 'a.', 'Resembling a ladder; formed with steps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scalawag', 'n.', 'A scamp; a scapegrace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scalded', 'imp. & p. p.', 'of Scald');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scalding', 'p. pr. & vb. n.', 'of Scald');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scald', 'v. t.', 'To burn with hot liquid or steam; to pain or injure by\n   contact with, or immersion in, any hot fluid; as, to scald the hand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scald', 'v. t.', 'To expose to a boiling or violent heat over a fire, or in\n   hot water or other liquor; as, to scald milk or meat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scald', 'n.', 'A burn, or injury to the skin or flesh, by some hot liquid,\n   or by steam.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scald', 'a.', 'Affected with the scab; scabby.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scald', 'a.', 'Scurvy; paltry; as, scald rhymers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scald', 'n.', 'Scurf on the head. See Scall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scald', 'n.', 'One of the ancient Scandinavian poets and historiographers;\n   a reciter and singer of heroic poems, eulogies, etc., among the\n   Norsemen; more rarely, a bard of any of the ancient Teutonic tribes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scalder', 'n.', 'A Scandinavian poet; a scald.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scaldfish', 'n.', 'A European flounder (Arnoglossus laterna, or Psetta\n   arnoglossa); -- called also megrim, and smooth sole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scaldic', 'a.', 'Of or pertaining to the scalds of the Norsemen; as,\n   scaldic poetry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scale', 'n.', 'The dish of a balance; hence, the balance itself; an\n   instrument or machine for weighing; as, to turn the scale; -- chiefly\n   used in the plural when applied to the whole instrument or apparatus\n   for weighing. Also used figuratively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scale', 'n.', 'The sign or constellation Libra.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scaled', 'imp. & p. p.', 'of Scale');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scaling', 'p. pr. & vb. n.', 'of Scale');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scale', 'v. t.', 'To weigh or measure according to a scale; to measure;\n   also, to grade or vary according to a scale or system.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scale', 'n.', 'One of the small, thin, membranous, bony or horny pieces\n   which form the covering of many fishes and reptiles, and some mammals,\n   belonging to the dermal part of the skeleton, or dermoskeleton. See\n   Cycloid, Ctenoid, and Ganoid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scale', 'n.', 'Hence, any layer or leaf of metal or other material,\n   resembling in size and thinness the scale of a fish; as, a scale of\n   iron, of bone, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scale', 'n.', 'One of the small scalelike structures covering parts of some\n   invertebrates, as those on the wings of Lepidoptera and on the body of\n   Thysanura; the elytra of certain annelids. See Lepidoptera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scale', 'n.', 'A scale insect. (See below.)');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scale', 'n.', 'A small appendage like a rudimentary leaf, resembling the\n   scales of a fish in form, and often in arrangement; as, the scale of a\n   bud, of a pine cone, and the like. The name is also given to the chaff\n   on the stems of ferns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scale', 'n.', 'The thin metallic side plate of the handle of a pocketknife.\n   See Illust. of Pocketknife.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scale', 'n.', 'An incrustation deposit on the inside of a vessel in which\n   water is heated, as a steam boiler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scale', 'n.', 'The thin oxide which forms on the surface of iron forgings.\n   It consists essentially of the magnetic oxide, Fe3O4. Also, a similar\n   coating upon other metals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scale', 'v. t.', 'To strip or clear of scale or scales; as, to scale a\n   fish; to scale the inside of a boiler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scale', 'v. t.', 'To take off in thin layers or scales, as tartar from the\n   teeth; to pare off, as a surface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scale', 'v. t.', 'To scatter; to spread.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scale', 'v. t.', 'To clean, as the inside of a cannon, by the explosion of\n   a small quantity of powder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scale', 'v. i.', 'To separate and come off in thin layers or laminae; as,\n   some sandstone scales by exposure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scale', 'v. i.', 'To separate; to scatter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scale', 'n.', 'A ladder; a series of steps; a means of ascending.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scale', 'n.', 'Hence, anything graduated, especially when employed as a\n   measure or rule, or marked by lines at regular intervals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scale', 'n.', 'A mathematical instrument, consisting of a slip of wood,\n   ivory, or metal, with one or more sets of spaces graduated and numbered\n   on its surface, for measuring or laying off distances, etc., as in\n   drawing, plotting, and the like. See Gunters scale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scale', 'n.', 'A series of spaces marked by lines, and representing\n   proportionately larger distances; as, a scale of miles, yards, feet,\n   etc., for a map or plan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scale', 'n.', 'A basis for a numeral system; as, the decimal scale; the\n   binary scale, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scale', 'n.', 'The graduated series of all the tones, ascending or\n   descending, from the keynote to its octave; -- called also the gamut.\n   It may be repeated through any number of octaves. See Chromatic scale,\n   Diatonic scale, Major scale, and Minor scale, under Chromatic,\n   Diatonic, Major, and Minor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scale', 'n.', 'Gradation; succession of ascending and descending steps and\n   degrees; progressive series; scheme of comparative rank or order; as, a\n   scale of being.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scale', 'n.', 'Relative dimensions, without difference in proportion of\n   parts; size or degree of the parts or components in any complex thing,\n   compared with other like things; especially, the relative proportion of\n   the linear dimensions of the parts of a drawing, map, model, etc., to\n   the dimensions of the corresponding parts of the object that is\n   represented; as, a map on a scale of an inch to a mile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scale', 'v. t.', 'To climb by a ladder, or as if by a ladder; to ascend by\n   steps or by climbing; to clamber up; as, to scale the wall of a fort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scale', 'v. i.', 'To lead up by steps; to ascend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scaleback', 'n.', 'Any one of numerous species of marine annelids of the\n   family Polynoidae, and allies, which have two rows of scales, or\n   elytra, along the back. See Illust. under Chaetopoda.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scalebeam', 'n.', 'The lever or beam of a balance; the lever of a platform\n   scale, to which the poise for weighing is applied.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scalebeam', 'n.', 'A weighing apparatus with a sliding weight, resembling a\n   steelyard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scaleboard', 'n.', 'A thin slip of wood used to justify a page.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scaleboard', 'n.', 'A thin veneer of leaf of wood used for covering the\n   surface of articles of furniture, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scaled', 'a.', 'Covered with scales, or scalelike structures; -- said of a\n   fish, a reptile, a moth, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scaled', 'a.', 'Without scales, or with the scales removed; as, scaled\n   herring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scaled', 'a.', 'Having feathers which in form, color, or arrangement\n   somewhat resemble scales; as, the scaled dove.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scaleless', 'a.', 'Destitute of scales.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scalene', 'a.', 'Having the sides and angles unequal; -- said of a\n   triangle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scalene', 'a.', 'Having the axis inclined to the base, as a cone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scalene', 'a.', 'Designating several triangular muscles called scalene\n   muscles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scalene', 'a.', 'Of or pertaining to the scalene muscles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scalene', 'n.', 'A triangle having its sides and angles unequal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scalenohedral', 'a.', 'Of or pertaining to a scalenohedron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scalenohedron', 'n.', 'A pyramidal form under the rhombohedral system,\n   inclosed by twelve faces, each a scalene triangle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scaler', 'n.', 'One who, or that which, scales; specifically, a dentists\n   instrument for removing tartar from the teeth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scale-winged', 'a.', 'Having the wings covered with small scalelike\n   structures, as the Lepidoptera; scaly-winged.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scaliness', 'n.', 'The state of being scaly; roughness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scaling', 'a.', 'Adapted for removing scales, as from a fish; as, a scaling\n   knife; adapted for removing scale, as from the interior of a steam\n   boiler; as, a scaling hammer, bar, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scaling', 'a.', 'Serving as an aid in clambering; as, a scaling ladder,\n   used in assaulting a fortified place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scaliola', 'n.', 'Same as Scagliola.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scall', 'a.', 'A scurf or scabby disease, especially of the scalp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scall', 'a.', 'Scabby; scurfy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scalled', 'a.', 'Scabby; scurfy; scall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scallion', 'n.', 'A kind of small onion (Allium Ascalonicum), native of\n   Palestine; the eschalot, or shallot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scallion', 'n.', 'Any onion which does not \"bottom out,\" but remains with a\n   thick stem like a leek.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scallop', 'n.', 'Any one of numerous species of marine bivalve mollusks of\n   the genus Pecten and allied genera of the family Pectinidae. The shell\n   is usually radially ribbed, and the edge is therefore often undulated\n   in a characteristic manner. The large adductor muscle of some the\n   species is much used as food. One species (Vola Jacobaeus) occurs on\n   the coast of Palestine, and its shell was formerly worn by pilgrims as\n   a mark that they had been to the Holy Land. Called also fan shell. See\n   Pecten, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scallop', 'n.', 'One of series of segments of circles joined at their\n   extremities, forming a border like the edge or surface of a scallop\n   shell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scallop', 'n.', 'One of the shells of a scallop; also, a dish resembling a\n   scallop shell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scalloped', 'imp. & p. p.', 'of Scallop');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scalloping', 'p. pr. & vb. n.', 'of Scallop');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scallop', 'v. t.', 'To mark or cut the edge or border of into segments of\n   circles, like the edge or surface of a scallop shell. See Scallop, n.,\n   2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scallop', 'n.', 'To bake in scallop shells or dishes; to prepare with\n   crumbs of bread or cracker, and bake. See Scalloped oysters, below.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scalloped', 'a.', 'Furnished with a scallop; made or done with or in a\n   scallop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scalloped', 'a.', 'Having the edge or border cut or marked with segments of\n   circles. See Scallop, n., 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scalloped', 'n.', 'Baked in a scallop; cooked with crumbs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scalloper', 'n.', 'One who fishes for scallops.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scalloping', 'n.', 'Fishing for scallops.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scalp', 'n.', 'A bed of oysters or mussels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scalp', 'n.', 'That part of the integument of the head which is usually\n   covered with hair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scalp', 'n.', 'A part of the skin of the head, with the hair attached, cut\n   or torn off from an enemy by the Indian warriors of North America, as a\n   token of victory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scalp', 'n.', 'Fig.: The top; the summit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scalped', 'imp. & p. p.', 'of Scalp');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scalping', 'p. pr. & vb. n.', 'of Scalp');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scalp', 'v. t.', 'To deprive of the scalp; to cut or tear the scalp from\n   the head of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scalp', 'v. t.', 'To remove the skin of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scalp', 'v. t.', 'To brush the hairs or fuzz from, as wheat grains, in the\n   process of high milling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scalp', 'v. i.', 'To make a small, quick profit by slight fluctuations of\n   the market; -- said of brokers who operate in this way on their own\n   account.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scalpel', 'n.', 'A small knife with a thin, keen blade, -- used by\n   surgeons, and in dissecting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scalper', 'n.', 'One who, or that which, scalps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scalper', 'n.', 'Same as Scalping iron, under Scalping.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scalper', 'n.', 'A broker who, dealing on his own account, tries to get a\n   small and quick profit from slight fluctuations of the market.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scalper', 'n.', 'A person who buys and sells the unused parts of railroad\n   tickets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scalper', 'n.', 'A person who buys tickets for entertainment or sports\n   events and sells them at a profit, often at a much higher price. Also,\n   ticket scalper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scalping', '', 'a. & n. from Scalp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scalpriform', 'a.', 'Shaped like a chisel; as, the scalpriform incisors of\n   rodents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scaly', 'a.', 'Covered or abounding with scales; as, a scaly fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scaly', 'a.', 'Resembling scales, laminae, or layers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scaly', 'a.', 'Mean; low; as, a scaly fellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scaly', 'a.', 'Composed of scales lying over each other; as, a scaly bulb;\n   covered with scales; as, a scaly stem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scaly-winged', 'a.', 'Scale-winged.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scambled', 'imp. & p. p.', 'of Scamble');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scambling', 'p. pr. & vb. n.', 'of Scamble');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scamble', 'v. i.', 'To move awkwardly; to be shuffling, irregular, or\n   unsteady; to sprawl; to shamble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scamble', 'v. i.', 'To move about pushing and jostling; to be rude and\n   turbulent; to scramble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scamble', 'v. t.', 'To mangle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scambler', 'n.', '1. One who scambles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scambler', 'n.', 'A bold intruder upon the hospitality of others; a\n   mealtime visitor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scamblingly', 'adv.', 'In a scambling manner; with turbulence and noise;\n   with bold intrusiveness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scamell', 'n.', 'Alt. of Scammel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scammel', 'n.', 'The female bar-tailed godwit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scamilli', 'pl. ', 'of Scamillus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scamillus', 'n.', 'A sort of second plinth or block, below the bases of\n   Ionic and Corinthian columns, generally without moldings, and of\n   smaller size horizontally than the pedestal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scammoniate', 'a.', 'Made from scammony; as, a scammoniate aperient.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scammony', 'n.', 'A species of bindweed or Convolvulus (C. Scammonia).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scammony', 'n.', 'An inspissated sap obtained from the root of the\n   Convolvulus Scammonia, of a blackish gray color, a nauseous smell like\n   that of old cheese, and a somewhat acrid taste. It is used in medicine\n   as a cathartic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scamp', 'n.', 'A rascal; a swindler; a rogue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scamp', 'a.', 'To perform in a hasty, neglectful, or imperfect manner; to\n   do superficially.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scampavia', 'n.', 'A long, low war galley used by the Neapolitans and\n   Sicilians in the early part of the nineteenth century.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scampered', 'imp. & p. p.', 'of Scamper');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scampering', 'p. pr. & vb. n.', 'of Scamper');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scamper', 'v. t.', 'To run with speed; to run or move in a quick, hurried\n   manner; to hasten away.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scamper', 'n.', 'A scampering; a hasty flight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scamperer', 'n.', 'One who scampers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scampish', 'a.', 'Of or like a scamp; knavish; as, scampish conduct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scanned', 'imp. & p. p.', 'of Scan');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scanning', 'p. pr. & vb. n.', 'of Scan');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scan', 'v. t.', 'To mount by steps; to go through with step by step.');
  INSERT INTO words (word, word_type, description) VALUES  (    ', a , or an . PCP. It is ', '', '() Specifically (Pros.), to go through with, as a verse, marking and\n   distinguishing the feet of which it is composed; to show, in reading,\n   the metrical structure of; to recite metrically.');
  INSERT INTO words (word, word_type, description) VALUES  (    ', a , or an . PCP. It is ', '', 'Specifically (Pros.), to go through with, as a verse, marking and\n   distinguishing the feet of which it is composed; to show, in reading,\n   the metrical structure of; to recite metrically () To go over and\n   examine point by point; to examine with care; to look closely at or\n   into; to scrutinize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scandal', 'n.', 'Offense caused or experienced; reproach or reprobation\n   called forth by what is regarded as wrong, criminal, heinous, or\n   flagrant: opprobrium or disgrace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scandal', 'n.', 'Reproachful aspersion; opprobrious censure; defamatory\n   talk, uttered heedlessly or maliciously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scandal', 'n.', 'Anything alleged in pleading which is impertinent, and is\n   reproachful to any person, or which derogates from the dignity of the\n   court, or is contrary to good manners.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scandal', 'v. t.', 'To treat opprobriously; to defame; to asperse; to\n   traduce; to slander.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scandal', 'v. t.', 'To scandalize; to offend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scandalized', 'imp. & p. p.', 'of Scandalize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scandalizing', 'p. pr. & vb. n.', 'of Scandalize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scandalize', 'v. t.', 'To offend the feelings or the conscience of (a\n   person) by some action which is considered immoral or criminal; to\n   bring shame, disgrace, or reproach upon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scandalize', 'v. t.', 'To reproach; to libel; to defame; to slander.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scandalous', 'a.', 'Giving offense to the conscience or moral feelings;\n   exciting reprobation; calling out condemnation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scandalous', 'a.', 'Disgraceful to reputation; bringing shame or infamy;\n   opprobrious; as, a scandalous crime or vice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scandalous', 'a.', 'Defamatory; libelous; as, a scandalous story.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scandalously', 'adv.', 'In a manner to give offense; shamefully.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scandalously', 'adv.', 'With a disposition to impute immorality or wrong.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scandalousness', 'n.', 'Quality of being scandalous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scandalum magnatum', '', 'A defamatory speech or writing published to the\n   injury of a person of dignity; -- usually abbreviated scan. mag.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scandent', 'a.', 'Climbing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scandia', 'n.', 'A chemical earth, the oxide of scandium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scandic', 'a.', 'Of or pertaining to scandium; derived from, or containing,\n   scandium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scandinavian', 'a.', 'Of or pertaining to Scandinavia, that is, Sweden,\n   Norway, and Denmark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scandinavian', 'n.', 'A native or inhabitant of Scandinavia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scandium', 'n.', 'A rare metallic element of the boron group, whose\n   existence was predicted under the provisional name ekaboron by means of\n   the periodic law, and subsequently discovered by spectrum analysis in\n   certain rare Scandinavian minerals (euxenite and gadolinite). It has\n   not yet been isolated. Symbol Sc. Atomic weight 44.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scansion', 'n.', 'The act of scanning; distinguishing the metrical feet of\n   a verse by emphasis, pauses, or otherwise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scansores', 'n. pl.', 'An artifical group of birds formerly regarded as an\n   order. They are distributed among several orders by modern\n   ornithologists.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scansorial', 'a.', 'Capable of climbing; as, the woodpecker is a scansorial\n   bird; adapted for climbing; as, a scansorial foot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scansorial', 'a.', 'Of or pertaining to the Scansores. See Illust.. under\n   Aves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scant', 'superl.', 'Not full, large, or plentiful; scarcely sufficient;\n   less than is wanted for the purpose; scanty; meager; not enough; as, a\n   scant allowance of provisions or water; a scant pattern of cloth for a\n   garment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scant', 'superl.', 'Sparing; parsimonious; chary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scanted', 'imp. & p. p.', 'of Scant');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scanting', 'p. pr. & vb. n.', 'of Scant');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scant', 'v. t.', 'To limit; to straiten; to treat illiberally; to stint;\n   as, to scant one in provisions; to scant ourselves in the use of\n   necessaries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scant', 'v. t.', 'To cut short; to make small, narrow, or scanty; to\n   curtail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scant', 'v. i.', 'To fail, or become less; to scantle; as, the wind scants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scant', 'adv.', 'In a scant manner; with difficulty; scarcely; hardly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scant', 'n.', 'Scantness; scarcity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scantily', 'adv.', 'In a scanty manner; not fully; not plentifully;\n   sparingly; parsimoniously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scantiness', 'n.', 'Quality or condition of being scanty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scantle', 'v. i.', 'To be deficient; to fail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scantle', 'v. t.', 'To scant; to be niggard of; to divide into small\n   pieces; to cut short or down.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scantlet', 'n.', 'A small pattern; a small quantity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scantling', 'a.', 'Not plentiful; small; scanty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scantling', 'v. t.', 'A fragment; a bit; a little piece.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scantling', 'v. t.', 'A piece or quantity cut for a special purpose; a\n   sample.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scantling', 'v. t.', 'A small quantity; a little bit; not much.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scantling', 'v. t.', 'A piece of timber sawed or cut of a small size, as\n   for studs, rails, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scantling', 'v. t.', 'The dimensions of a piece of timber with regard to\n   its breadth and thickness; hence, the measure or dimensions of\n   anything.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scantling', 'v. t.', 'A rough draught; a rude sketch or outline.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scantling', 'v. t.', 'A frame for casks to lie upon; a trestle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scantly', 'adv.', 'In a scant manner; not fully or sufficiently; narrowly;\n   penuriously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scantly', 'adv.', 'Scarcely; hardly; barely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scantness', 'n.', 'The quality or condition of being scant; narrowness;\n   smallness; insufficiency; scantiness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scanty', 'a.', 'Wanting amplitude or extent; narrow; small; not abundant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scanty', 'a.', 'Somewhat less than is needed; insufficient; scant; as, a\n   scanty supply of words; a scanty supply of bread.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scanty', 'a.', 'Sparing; niggardly; parsimonious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scape', 'n.', 'A peduncle rising from the ground or from a subterranean\n   stem, as in the stemless violets, the bloodroot, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scape', 'n.', 'The long basal joint of the antennae of an insect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scape', 'n.', 'The shaft of a column.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scape', 'n.', 'The apophyge of a shaft.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scaped', 'imp. & p. p.', 'of Scape');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scaping', 'p. pr. & vb. n.', 'of Scape');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scape', 'v. t. & i.', 'To escape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scape', 'n.', 'An escape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scape', 'n.', 'Means of escape; evasion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scape', 'n.', 'A freak; a slip; a fault; an escapade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scape', 'n.', 'Loose act of vice or lewdness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scapegallows', 'n.', 'One who has narrowly escaped the gallows for his\n   crimes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scapegoat', 'n.', 'A goat upon whose head were symbolically placed the sins\n   of the people, after which he was suffered to escape into the\n   wilderness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scapegoat', 'n.', 'Hence, a person or thing that is made to bear blame for\n   others.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scapegrace', 'n.', 'A graceless, unprincipled person; one who is wild and\n   reckless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scapeless', 'a.', 'Destitute of a scape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scapement', 'v.', 'Same as Escapement, 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scape-wheel', 'n.', 'The wheel in an escapement (as of a clock or a watch)\n   into the teeth of which the pallets play.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scaphander', 'n.', 'The case, or impermeable apparel, in which a diver can\n   work while under water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scaphism', 'n.', 'An ancient mode of punishing criminals among the\n   Persians, by confining the victim in a trough, with his head and limbs\n   smeared with honey or the like, and exposed to the sun and to insects\n   until he died.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scaphite', 'n.', 'Any fossil cephalopod shell of the genus Scaphites,\n   belonging to the Ammonite family and having a chambered boat-shaped\n   shell. Scaphites are found in the Cretaceous formation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scaphocephalic', 'a.', 'Of, pertaining to, or affected with, scaphocephaly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scaphocephaly', 'n.', 'A deformed condition of the skull, in which the\n   vault is narrow, elongated, and more or less boat-shaped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scaphocerite', 'n.', 'A flattened plate or scale attached to the second\n   joint of the antennae of many Crustacea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scaphognathite', 'n.', 'A thin leafike appendage (the exopodite) of the\n   second maxilla of decapod crustaceans. It serves as a pumping organ to\n   draw the water through the gill cavity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scaphoid', 'a.', 'Resembling a boat in form; boat-shaped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scaphoid', 'n.', 'The scaphoid bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scapholunar', 'a.', 'Of or pertaining to the scaphoid and lunar bones of\n   the carpus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scapholunar', 'n.', 'The scapholunar bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scaphopda', 'n. pl.', 'A class of marine cephalate Mollusca having a\n   tubular shell open at both ends, a pointed or spadelike foot for\n   burrowing, and many long, slender, prehensile oral tentacles. It\n   includes Dentalium, or the tooth shells, and other similar shells.\n   Called also Prosopocephala, and Solenoconcha.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scapiform', 'a.', 'Resembling a scape, or flower stem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scapolite', 'n.', 'A grayish white mineral occuring in tetragonal crystals\n   and in cleavable masses. It is essentially a silicate of alumina and\n   soda.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scapple', 'v. t.', 'To work roughly, or shape without finishing, as stone\n   before leaving the quarry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scapple', 'v. t.', 'To dress in any way short of fine tooling or rubbing,\n   as stone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scapulae', 'pl. ', 'of Scapula');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scapulas', 'pl. ', 'of Scapula');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scapula', 'n.', 'The principal bone of the shoulder girdle in mammals; the\n   shoulder blade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scapula', 'n.', 'One of the plates from which the arms of a crinoid arise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scapular', 'a.', 'Of or pertaining to the scapula or the shoulder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scapular', 'n.', 'One of a special group of feathers which arise from each\n   of the scapular regions and lie along the sides of the back.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scapular', 'n.', 'Alt. of Scapulary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scapulary', 'n.', 'A loose sleeveless vestment falling in front and behind,\n   worn by certain religious orders and devout persons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scapulary', 'n.', 'The name given to two pieces of cloth worn under the\n   ordinary garb and over the shoulders as an act of devotion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scapulary', 'n.', 'A bandage passing over the shoulder to support it, or to\n   retain another bandage in place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scapulary', 'a.', 'Same as Scapular, a.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scapulary', 'n.', 'Same as 2d and 3d Scapular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scapulet', 'n.', 'A secondary mouth fold developed at the base of each of\n   the armlike lobes of the manubrium of many rhizostome medusae. See\n   Illustration in Appendix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scapulo-', '', 'A combining form used in anatomy to indicate connection\n   with, or relation to, the scapula or the shoulder; as, the\n   scapulo-clavicular articulation, the articulation between the scapula\n   and clavicle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scapus', 'n.', 'See 1st Scape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scar', 'n.', 'A mark in the skin or flesh of an animal, made by a wound or\n   ulcer, and remaining after the wound or ulcer is healed; a cicatrix; a\n   mark left by a previous injury; a blemish; a disfigurement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scar', 'n.', 'A mark left upon a stem or branch by the fall of a leaf,\n   leaflet, or frond, or upon a seed by the separation of its support. See\n   Illust.. under Axillary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scarred', 'imp. & p. p.', 'of Scar');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scarring', 'p. pr. & vb. n.', 'of Scar');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scar', 'v. t.', 'To mark with a scar or scars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scar', 'v. i.', 'To form a scar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scar', 'n.', 'An isolated or protruding rock; a steep, rocky eminence; a\n   bare place on the side of a mountain or steep bank of earth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scar', 'n.', 'A marine food fish, the scarus, or parrot fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scarab', 'n.', 'Alt. of Scarabee');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scarabee', 'n.', 'Any one of numerous species of lamellicorn beetles of the\n   genus Scarabaeus, or family Scarabaeidae, especially the sacred, or\n   Egyptian, species (Scarabaeus sacer, and S. Egyptiorum).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scarabee', 'n.', 'A stylized representation of a scarab beetle in stone or\n   faience; -- a symbol of resurrection, used by the ancient Egyptians as\n   an ornament or a talisman, and in modern times used in jewelry, usually\n   by engraving designs on cabuchon stones. Also used attributively; as, a\n   scarab bracelet [a bracelet containing scarabs]; a scarab [the carved\n   stone itelf].');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scarabaeus', 'n.', 'Same as Scarab.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scaraboid', 'a.', 'Of or pertaining to the family Scarabaeidae, an\n   extensive group which includes the Egyptian scarab, the tumbledung, and\n   many similar lamellicorn beetles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scaraboid', 'n.', 'A scaraboid beetle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scaramouch', 'n.', 'A personage in the old Italian comedy (derived from\n   Spain) characterized by great boastfulness and poltroonery; hence, a\n   person of like characteristics; a buffoon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scarce', 'superl.', 'Not plentiful or abundant; in small quantity in\n   proportion to the demand; not easily to be procured; rare; uncommon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scarce', 'superl.', 'Scantily supplied (with); deficient (in); -- with of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scarce', 'superl.', 'Sparing; frugal; parsimonious; stingy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scarce', 'adv.', 'Alt. of Scarcely');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scarcely', 'adv.', 'With difficulty; hardly; scantly; barely; but just.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scarcely', 'adv.', 'Frugally; penuriously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scarcement', 'n.', 'An offset where a wall or bank of earth, etc.,\n   retreats, leaving a shelf or footing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scarceness', 'n.', 'Alt. of Scarcity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scarcity', 'n.', 'The quality or condition of being scarce; smallness of\n   quantity in proportion to the wants or demands; deficiency; lack of\n   plenty; short supply; penury; as, a scarcity of grain; a great scarcity\n   of beauties.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scard', 'n.', 'A shard or fragment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scared', 'imp. & p. p.', 'of Scare');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scaring', 'p. pr. & vb. n.', 'of Scare');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scare', 'v. t.', 'To frighten; to strike with sudden fear; to alarm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scare', 'n.', 'Fright; esp., sudden fright produced by a trifling cause, or\n   originating in mistake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scarecrow', 'n.', 'Anything set up to frighten crows or other birds from\n   cornfields; hence, anything terifying without danger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scarecrow', 'n.', 'A person clad in rags and tatters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scarecrow', 'n.', 'The black tern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scarefire', 'n.', 'An alarm of fire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scarefire', 'n.', 'A fire causing alarm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scarf', 'n.', 'A cormorant.');
